INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1,1,1,'পরম করুণাময়, অসীম দয়ালু আল্লাহর নামে (শুরু করছি)।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2,1,2,'আল্লাহরই জন্য সমস্ত প্রশংসা, যিনি বিশ্বজগতের রাব্ব।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3,1,3,'যিনি পরম দয়ালু, অতিশয় করুণাময়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4,1,4,' যিনি বিচার দিনের মালিক।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5,1,5,' আমরা আপনারই ইবাদাত করছি এবং আপনারই নিকট সাহায্য চাচ্ছি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6,1,6,' আমাদেরকে সরল সঠিক পথ প্রদর্শন করুন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (7,1,7,'তাদের পথ, যাদের প্রতি আপনি অনুগ্রহ করেছেন। তাদের পথে নয়, যাদের প্রতি আপনার গযব বর্ষিত হয়েছে, তাদের পথও নয় যারা পথভ্রষ্ট হয়েছে। আমীন!!');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (8,2,1,' আলিফ-লাম-মীম। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (9,2,2,'ইহা ঐ গ্রন্থ যার মধ্যে কোন সন্দেহ-সংশয়ের অবকাশ নেই; ধর্ম-ভীরুদের জন্য এ গ্রন্থ পথনির্দেশ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (10,2,3,' যারা অদৃশ্য বিষয়গুলিতে বিশ্বাস স্থাপন করে এবং সালাত প্রতিষ্ঠা করে এবং আমি তাদেরকে যে উপজীবিকা প্রদান করেছি তা হতে দান করে থাকে ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (11,2,4,' এবং তোমার প্রতি যা অবতীর্ণ হয়েছে ও তোমার পূর্বে যা অবতীর্ণ হয়েছিল, যারা তদ্বিষয়ে বিশ্বাস স্থাপন করে এবং আখিরাতের প্রতি যারা দৃঢ় বিশ্বাস রাখে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (12,2,5,' এরাই তাদের রবের পক্ষ হতে প্রাপ্ত হিদায়াতের উপর প্রতিষ্ঠিত রয়েছে এবং এরাই পূর্ণ সফলকাম। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (13,2,6,' নিশ্চয়ই যারা অবিশ্বাস করছে তাদের জন্য উভয়ই সমান; তুমি তাদেরকে ভয় প্রদর্শন কর বা না কর, তারা ঈমান আনবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (14,2,7,' আল্লাহ তাদের অন্তরসমূহের উপর ও তাদের কর্ণসমূহের উপর মোহরাংকিত করে দিয়েছেন এবং তাদের চক্ষুসমূহের উপর আবরণ পড়ে আছে এবং তাদের জন্য রয়েছে ভয়ানক শাস্তি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (15,2,8,'আর মানুষের মধ্যে এমন লোক আছে যারা বলে, আমরা আল্লাহর উপর এবং শেষ দিনের উপর ঈমান এনেছি, অথচ তারা মোটেই ঈমানদার নয়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (16,2,9,' তারা আল্লাহ ও মু’মিনদের সঙ্গে ধোঁকাবাজী করে। প্রকৃত পক্ষে তারা নিজেদের ব্যতীত আর কারও সাথে ধোঁকাবাজী করেনা, অথচ তারা এ সম্বন্ধে অনুভব করতে পারেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (17,2,10,' তাদের অন্তরে পীড়া রয়েছে, পরন্ত আল্লাহ তাদের পীড়া আরও বাড়িয়ে দিয়েছেন এবং তাদের জন্য গুরুতর শাস্তি রয়েছে যেহেতু তারা অসত্য বলত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (18,2,11,' এবং যখন তাদেরকে বলা হয়ঃ তোমরা পৃথিবীতে অশান্তি সৃষ্টি করনা তখন তারা বলেঃ আমরাতো শুধুই শান্তি স্থাপনকারী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (19,2,12,' সাবধান! নিশ্চয়ই তারাই অশান্তি সৃষ্টিকারী, কিন্তু তারা বুঝেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (20,2,13,' এবং যখন তাদেরকে বলা হয়ঃ লোকে যেরূপ বিশ্বাস করেছে তোমরাও তদ্রুপ বিশ্বাস স্থাপন কর, তখন তারা বলেঃ নির্বোধেরা যেরূপ বিশ্বাস করেছে আমরাও কি সেইরূপ বিশ্বাস করব? সাবধান! নিশ্চয়ই তারাই নির্বোধ, কিন্তু তা তারা অবগত নয়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (21,2,14,' এবং যখন তারা মু’মিনদের সাথে মিলিত হয় তখন তারা বলেঃ আমরা বিশ্বাস স্থাপন করেছি; এবং যখন তারা নিজেদের দলপতি ও দুষ্ট নেতাদের সাথে গোপনে মিলিত হয় তখন বলেঃ আমরা তোমাদের সঙ্গেই আছি, আমরাতো শুধু ঠাট্টা-বিদ্রুপ ও প্রহসন করে থাকি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (22,2,15,' আল্লাহ তাদের সঙ্গে ঠাট্টা-বিদ্রুপ করছেন এবং তাদেরকে অবকাশ দিচ্ছেন। ফলে তারা নিজেদের অবাধ্যতার মধ্যে উদ্ভ্রান্ত হয়ে ফিরছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (23,2,16,' এরা তারাই যারা সু-পথের পরিবর্তে কু-পথকে ক্রয় করেছে, সুতরাং তাদের বাণিজ্য লাভজনক হয়নি এবং তারা সরল সঠিক পথে চলেনি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (24,2,17,'এদের অবস্থা ঐ ব্যক্তির ন্যায় যে আগুন প্রজ্জ্বলিত করল, অতঃপর যখন তার পার্শ্ববর্তী সমস্ত স্থান আলোকিত হল, তখন আল্লাহ তাদের আলো ছিনিয়ে নিলেন এবং তাদেরকে অন্ধকারের মধ্যে ছেড়ে দিলেন, সুতরাং তারা কিছুই দেখতে পায়না।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (25,2,18,' তারা বধির, মূক, অন্ধ। অতএব তারা প্রত্যাবৃত্ত হবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (26,2,19,' অথবা আকাশ হতে বারি বর্ষণের ন্যায় যাতে অন্ধকার, গর্জন ও বিদ্যুৎ রয়েছে, তারা বজ্রধ্বনি বশতঃ মৃত্যুভয়ে তাদের কর্ণসমূহে স্ব স্ব অঙ্গুলী গুজে দেয়, এবং আল্লাহ অবিশ্বাসীদের পরিবেষ্টনকারী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (27,2,20,' অচিরে বিদ্যুৎ তাদের দৃষ্টি হরণ করবে, যখন তাদের প্রতি আলোক প্রদীপ্ত হয় তখন তারা চলতে থাকে এবং যখন তাদের উপর অন্ধকার আচ্ছন্ন হয় তখন তারা ঠায় দাঁড়িয়ে থাকে এবং যদি আল্লাহ ইচ্ছা করেন - নিশ্চয়ই তাদের শ্রবণশক্তি ও তাদের দৃষ্টিশক্তি হরণ করতে পারেন, নিশ্চয়ই আল্লাহ সর্ব বিষয়ে শক্তিমান।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (28,2,21,'হে মানববৃন্দ! তোমরা তোমাদের রবের ইবাদাত কর যিনি তোমাদেরকে এবং তোমাদের পূর্ববর্তীদেরকে সৃষ্টি করেছেন, যেন তোমরা ধর্মভীরু হও।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (29,2,22,' যিনি তোমাদের জন্য ভূতলকে শয্যা ও আকাশকে ছাদ স্বরূপ সৃষ্টি করেছেন এবং আকাশ হতে বারি বর্ষণ করেন, অতঃপর তদ্বারা তোমাদের জন্য উপজীবিকা স্বরূপ ফলপুঞ্জ উৎপাদন করেন, অতএব তোমরা আল্লাহর সমকক্ষ স্থির করনা এবং তোমরা এটা অবগত আছ। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (30,2,23,' এবং আমি আমার বান্দার প্রতি যা অবতীর্ণ করেছি, যদি তোমরা তাতে সন্দিহান হও তাহলে তৎসদৃশ একটি ‘‘সূরা’’ আনয়ন কর এবং তোমাদের সেই সাহায্যকারীদেরকে ডেকে নাও যারা আল্লাহ হতে পৃথক, যদি তোমরা সত্যবাদী হও!');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (31,2,24,'অতঃপর যদি তোমরা তা করতে না পার এবং তোমরা তা কখনও করতে পারবেনা, তাহলে তোমরা সেই জাহান্নামের ভয় কর যার খোরাক মনুষ্য ও প্রস্তর খন্ড - যা অবিশ্বাসীদের জন্য প্রস্তুত করা হয়েছে।  ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (32,2,25,'এবং যারা বিশ্বাস স্থাপন করেছে ও সৎ কার্যাবলী সম্পাদন করে থাকে তাদেরকে সুসংবাদ প্রদান কর যে, তাদের জন্য এমন জান্নাত রয়েছে যার তলদেশ দিয়ে নদীসমূহ প্রবাহিত হচ্ছে; যখনই সেখানে তাদেরকে খাবার হিসাবে ফলপুঞ্জ প্রদান করা হবে তখনই তারা বলবেঃ আমাদেরতো এটা পূর্বেই প্রদত্ত হয়েছিল, বস্তুতঃ তাদেরকে একই সদৃশ ফল প্রদান করা হবে, এবং তাদের জন্য তন্মধ্যে শুদ্ধা সহধর্মিনীগণ রয়েছে, এবং তন্মধ্যে তারা চিরকাল অবস্থান করবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (33,2,26,'নিশ্চয়ই আল্লাহ মশা অথবা তদপেক্ষা ক্ষুদ্রতর দৃষ্টান্ত বর্ণনা করতে লজ্জাবোধ করেননা। সুতরাং যারা ঈমান এনেছে তারাতো বিশ্বাস করবে যে, এ উপমা তাদের রবের পক্ষ হতে খুবই স্থানোপযোগী হয়েছে, আর যারা কাফির সর্বাবস্থায় তারা এটাই বলবে যে, এ সব নগণ্য বস্ত্তর উপমা দ্বারা আল্লাহর উদ্দেশ্যই বা কি? তিনি এর দ্বারা অনেককে বিপথগামী করেন এবং এর দ্বারা অনেককে সঠিক পথ প্রদর্শন করেন, আর এর দ্বারা তিনি শুধু ফাসিকদেরকেই বিপথগামী করে থাকেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (34,2,27,' যারা আল্লাহর সঙ্গে দৃঢ়ভাবে প্রতিজ্ঞাবদ্ধ হওয়ার পর তা ভঙ্গ করে এবং ঐ সব সম্বন্ধ ছিন্ন করে যা অবিচ্ছিন্ন রাখতে আল্লাহ নির্দেশ দিয়েছেন এবং যারা ভূপৃষ্ঠে বিবাদ সৃষ্টি করে তারাই পূর্ণ ক্ষতিগ্রস্ত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (35,2,28,'কিরূপে তোমরা আল্লাহকে অবিশ্বাস করছ? অথচ তোমরা ছিলে প্রাণহীন? অতঃপর তিনিই তোমাদেরকে সঞ্জীবিত করেছেন, পুনরায় তিনি তোমাদেরকে নির্জীব করবেন এবং পুনরায় তোমাদেরকে জীবিত করা হবে। অবশেষে তোমাদেরকে তাঁরই দিকে প্রত্যাগমন করতে হবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (36,2,29,' পৃথিবীতে যা কিছু আছে সমস্তই তিনি তোমাদের জন্য সৃষ্টি করেছেন; অতঃপর তিনি আকাশের প্রতি মনঃসংযোগ করেন, অতঃপর সপ্ত আকাশ সুবিন্যস্ত করেন এবং তিনি সর্ব বিষয়ে মহাজ্ঞানী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (37,2,30,' এবং যখন তোমার রাব্ব মালাইকা/ফেরেশতাদের বললেনঃ নিশ্চয়ই আমি পৃথিবীতে প্রতিনিধি সৃষ্টি করব; তারা বললঃ আপনি কি যমীনে এমন কেহকে সৃষ্টি করবেন যারা তন্মধ্যে অশান্তি সৃষ্টি করবে এবং রক্তপাত করবে? এবং আমরাইতো আপনার গুণগান করছি এবং আপনারই পবিত্রতা বর্ণনা করে থাকি। তিনি বললেনঃ তোমরা যা অবগত নও নিশ্চয়ই আমি তা জ্ঞাত আছি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (38,2,31,'এবং তিনি আদমকে সমস্ত নাম শিক্ষা দিলেন, অনন্তর তৎসমূদয় মালাইকা/ফেরেশতাদের সামনে উপস্থাপিত করলেন, অতঃপর বললেনঃ যদি তোমরা সত্যবাদী হও তাহলে আমাকে এ সব বস্তুর নামসমূহ বর্ণনা কর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (39,2,32,'তারা বলেছিলঃ আপনি পরম পবিত্র! আপনি আমাদেরকে যা শিক্ষা দিয়েছেন তদ্ব্যতীত আমাদের কোনই জ্ঞান নেই, নিশ্চয়ই আপনি মহাজ্ঞানী, বিজ্ঞানময়। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (40,2,33,' তিনি বলেছিলেনঃ হে আদম! তুমি তাদেরকে ঐ সকলের নামসমূহ বর্ণনা কর; অতঃপর যখন সে তাদেরকে ঐগুলির নামসমূহ বলেছিল তখন তিনি বলেছিলেনঃ আমি কি তোমাদেরকে বলিনি যে, নিশ্চয়ই আমি আসমান ও যমীনের অদৃশ্য বিষয় অবগত আছি এবং তোমরা যা প্রকাশ কর ও যা গোপন কর আমি তাও পরিজ্ঞাত আছি?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (41,2,34,' এবং যখন আমি মালাইকা/ফেরেশতাদেরকে বলেছিলাম যে, তোমরা আদমকে সাজদাহ কর, তখন ইবলীস ব্যতীত সকলে সাজদাহ করেছিল; সে অগ্রাহ্য করল ও অহংকার করল এবং কাফিরদের অন্তর্ভুক্ত হয়ে গেল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (42,2,35,' এবং আমি বললামঃ হে আদম! তুমি ও তোমার স্ত্রী জান্নাতে অবস্থান কর এবং তা হতে যা ইচ্ছা স্বচ্ছন্দে আহার কর; কিন্তু ঐ বৃক্ষের নিকটবর্তী হয়োনা, তাহলে তোমরা অত্যাচারীদের অন্তর্ভুক্ত হয়ে যাবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (43,2,36,' অনন্তর শাইতান তাদের উভয়কে  সেখান হতে বিচ্যুত করল, অতঃপর তারা উভয়ে যেখানে ছিল  সেখান হতে তাদেরকে বর্হিগত করল; এবং আমি বললামঃ তোমরা নীচে নেমে যাও, তোমরা পরস্পর পরস্পরের শত্রু; এবং পৃথিবীতেই রয়েছে তোমাদের জন্য এক নির্দিষ্ট কালের অবস্থিতি ও ভোগ সম্পদ। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (44,2,37,' অতঃপর আদম স্বীয় রাব্ব হতে কতিপয় বাণী শিক্ষা লাভ করল, আল্লাহ তখন তার প্রতি কৃপা দৃষ্টি দিলেন; নিশ্চয়ই তিনি ক্ষমাশীল, করুণাময়!');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (45,2,38,'আমি বললামঃ তোমরা সবাই এখান থেকে নীচে নেমে যাও; অনন্তর আমার পক্ষ হতে তোমাদের নিকট যে উপদেশ উপস্থিত হবে-যারা আমার সেই উপদেশ অনুসরণ করবে বস্তুতঃ তাদের কোনই ভয় নেই এবং তারা চিন্তিতও হবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (46,2,39,' আর যারা অবিশ্বাস করবে এবং আমার নিদর্শনসমূহে অসত্যারোপ করবে, তারাই জাহান্নামের অধিবাসী - তন্মধ্যে তারা সদা অবস্থান করবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (47,2,40,' হে ইসরাঈলী বংশধর! আমি তোমাদেরকে যে সুখ সম্পদ দান করেছি তা স্মরণ কর এবং আমার অঙ্গীকার পূর্ণ কর - আমিও তোমাদের প্রতি কৃত অঙ্গীকার পূর্ণ করব এবং তোমরা শুধু আমাকেই ভয় কর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (48,2,41,' এবং আমি যা অবতীর্ণ করেছি তৎপ্রতি বিশ্বাস স্থাপন কর, ইহা (কুরআন) তোমাদের কাছে যা আছে উহারই সত্যতা প্রনয়নকারী এবং এতে তোমরাই প্রথম অবিশ্বাসী হয়োনা এবং আমার নিদর্শনাবলীর পরিবর্তে সামান্য মূল্য গ্রহণ করনা এবং তোমরা বরং আমাকেই ভয় কর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (49,2,42,' এবং তোমরা সত্যকে মিথ্যার সাথে মিশ্রিত করনা এবং জেনে শুনে সত্য গোপন করনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (50,2,43,' আর তোমরা সালাত প্রতিষ্ঠিত কর ও যাকাত প্রদান কর এবং রুকুকারীদের সাথে রুকু কর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (51,2,44,' তোমরা কি লোকদেরকে সৎ কাজে আদেশ করছ এবং তোমাদের নিজেদের সম্বন্ধে বিস্মৃত হচ্ছ? অথচ তোমরা গ্রন্থ পাঠ কর; তাহলে কি তোমরা হৃদয়ঙ্গম করছনা?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (52,2,45,'এবং তোমরা ধৈর্য ও সালাতের মাধ্যমে সাহায্য চাও; অবশ্যই ওটা কঠিন, কিন্তু বিনীতদের পক্ষে নয়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (53,2,46,' যারা ধারণা করে যে, নিশ্চয়ই তারা তাদের রবের সাথে সম্মিলিত হবে এবং তারা তাঁরই দিকে প্রতিগমন করবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (54,2,47,' হে বানী ইসরাঈল! আমি তোমাদেরকে যে সুখ সম্পদ দান করেছি তা স্মরণ কর এবং নিশ্চয়ই আমি তোমাদেরকে সমগ্র পৃথিবীর উপর শ্রেষ্ঠত্ব দান করেছি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (55,2,48,' এবং তোমরা সেই দিনের ভয় কর, যে দিন এক ব্যক্তি অন্য ব্যক্তি হতে কিছুমাত্র উপকৃত হবেনা এবং কোন ব্যক্তি হতে কোন সুপারিশও গৃহীত হবেনা, কোন ব্যক্তি হতে কোন বিনিময়ও গ্রহণ করা হবেনা এবং তাদেরকে সাহায্য করাও হবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (56,2,49,' এবং যখন আমি তোমাদেরকে ফির‘আউনের সম্প্রদায় হতে বিমুক্ত করেছিলাম - তারা তোমাদেরকে কঠোর শাস্তি প্রদান করত, তোমাদের পুত্রদেরকে হত্যা করত এবং তোমাদের কন্যাদেরকে জীবিত রাখত এবং এতে তোমাদের রাব্ব হতে তোমাদের জন্য মহা পরীক্ষা ছিল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (57,2,50,' এবং যখন আমি তোমাদের জন্য সমুদ্রকে বিভক্ত করেছিলাম, অতঃপর তোমাদেরকে উদ্ধার করেছিলাম ও ফির‘আউনের স্বজনদেরকে নিমজ্জিত করেছিলাম এবং তোমরা তা প্রত্যক্ষ করেছিলে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (58,2,51,' এবং যখন আমি মূসার সঙ্গে চল্লিশ রজনীর অঙ্গীকার করেছিলাম, অনন্তর তোমরা গো-বৎসকে উপাস্যরূপে গ্রহণ করেছিলে এবং তোমরা ছিলে অত্যাচারী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (59,2,52,' এর পরেও আমি তোমাদেরকে মার্জনা করেছিলাম - যেন তোমরা কৃতজ্ঞতা প্রকাশ কর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (60,2,53,'আর যখন আমি মূসাকে গ্রন্থ এবং সত্য ও মিথ্যার প্রভেদকারী নির্দেশ দিয়েছিলাম, যেন তোমরা সুপথ প্রাপ্ত হও।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (61,2,54,'আর যখন মূসা স্বীয় সম্প্রদায়কে বললঃ হে আমার সম্প্রদায়! নিশ্চয়ই তোমরা গো-বৎসকে উপাস্য রূপে গ্রহণ করে তোমাদের নিজেদের প্রতি অত্যাচার করেছ। অতএব তোমরা তোমাদের রবের নিকট ক্ষমা প্রার্থনা কর, অতঃপর তোমরা তোমাদের নিজ নিজ প্রাণ বিসর্জন দাও, তোমাদের রবের নিকট এটাই তোমাদের জন্য কল্যাণকর; অনন্তর তিনি তোমাদের প্রতি ক্ষমা প্রদর্শন করলেন, নিশ্চয়ই তিনি ক্ষমাশীল, করুণাময়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (62,2,55,' এবং যখন তোমরা বলেছিলেঃ হে মূসা! আমরা আল্লাহকে প্রকাশ্যভাবে দর্শন না করা পর্যন্ত তোমাকে বিশ্বাস করবনা - তখন বজ্রপাত তোমাদেরকে আক্রমণ করেছিল এবং তোমরা তা প্রত্যক্ষ করেছিলে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (63,2,56,' অতঃপর তোমাদের মৃত্যুর পর আমি তোমাদেরকে পুনরুজ্জীবিত করেছিলাম, যেন তোমরা কৃতজ্ঞতা প্রকাশ কর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (64,2,57,'এবং আমি তোমাদের উপর মেঘমালার ছায়া দান করেছিলাম এবং তোমাদের প্রতি ‘মান্না’ ও ‘সালওয়া’ অবতীর্ণ করেছিলাম; আমি তোমাদেরকে যে উপজীবিকা দান করেছি সেই পবিত্র জিনিস হতে আহার কর; এবং তারা আমার কোন অনিষ্ট করেনি, বরং তারা নিজেদের অনিষ্ট করেছিল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (65,2,58,'এবং যখন আমি বললামঃ তোমরা এই নগরে প্রবেশ কর, অতঃপর যা ইচ্ছা স্বচ্ছন্দে আহার কর এবং সাজদাহবনতভাবে দ্বারে প্রবেশ কর এবং তোমরা বলঃ আমরা ক্ষমা প্রার্থনা করছি, তাহলে আমি তোমাদের অপরাধ ক্ষমা করব এবং অচিরেই সৎকর্মশীলগণকে অধিকতর দান করব।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (66,2,59,'অনন্তর যারা অত্যাচার করেছিল তাদেরকে যা বলা হয়েছিল, তৎপরিবর্তে তারা সেই কথার পরিবর্তন করল, পরে অত্যাচারীরা যে দুস্কর্ম করেছিল তজ্জন্য আমি তাদের উপর আকাশ হতে শাস্তি অবতীর্ণ করেছিলাম। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (67,2,60,'এবং যখন মূসা স্বীয় সম্প্রদায়ের জন্য পানি প্রার্থনা করেছিল তখন আমি বলেছিলামঃ তুমি স্বীয় লাঠি দ্বারা প্রস্তরে আঘাত কর, অনন্তর তা হতে দ্বাদশ প্রস্রবন বিনিঃসৃত হল, প্রত্যেকেই স্ব স্ব স্থান জেনে নিল, তোমরা আল্লাহর উপজীবিকা হতে আহার কর ও পান কর এবং পৃথিবীতে শান্তি ভঙ্গকারী রূপে বিচরণ করনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (68,2,61,'এবং যখন তোমরা বলেছিলে - হে মূসা! আমরা একইরূপ খাদ্যে ধৈর্য ধারণ করতে পারছিনা, অতএব তুমি আমাদের জন্য তোমার রবের নিকট প্রার্থনা কর যেন তিনি আমাদের জন্মভূমিতে যা উৎপন্ন হয় তা হতে ওর শাক-শব্জি, ওর কাঁকুড়, ওর গম, ওর মসুর এবং ওর পিয়াজ উৎপাদন করেন। সে বলেছিলঃ যা উৎকৃষ্ট তোমরা কি তার সঙ্গে যা নিকৃষ্ট তার বিনিময় করতে চাও? কোন নগরে উপনীত হও, তোমাদের প্রার্থিত দ্রব্যগুলি অবশ্যই প্রাপ্ত হবে। তাদের উপর লাঞ্ছনা ও দারিদ্রতা নিপতিত হল এবং তারা আল্লাহর কোপে পতিত হল এই হেতু যে, নিশ্চয়ই তারা আল্লাহর নিদর্শনসমূহে অবিশ্বাস করত এবং অন্যায়ভাবে নাবীগণকে হত্যা করত; এবং এই হেতু যে, তারা অবাধ্যাচরণ করেছিল ও তারা সীমা অতিক্রম করেছিল। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (69,2,62,'নিশ্চয়ই মুসলিম, ইয়াহুদী, খৃষ্টান এবং সাবেঈন সম্প্রদায়, (এদের মধ্যে) যারা আল্লাহর প্রতি ও কিয়ামাতের প্রতি বিশ্বাস রাখে এবং ভাল কাজ করে, তাদের জন্য তাদের রবের নিকট পুরস্কার রয়েছে, তাদের কোন প্রকার ভয় নেই এবং তারা চিন্তিতও হবেনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (70,2,63,' এবং যখন আমি তোমাদের অঙ্গীকার গ্রহণ করেছিলাম এবং তোমাদের উপর তূর পর্বত সমুচ্চ করেছিলাম যে, আমি তোমাদেরকে যা দিয়েছি তা দৃঢ় রূপে ধারণ কর এবং এতে যা আছে তা স্মরণ কর - সম্ভবতঃ তোমরা নিস্কৃতি পাবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (71,2,64,' এরপর পুনরায় তোমরা ফিরে গেলে, অতএব যদি তোমাদের প্রতি আল্লাহর অনুগ্রহ এবং তাঁর করুণা না থাকত তাহলে অবশ্যই তোমরা বিনাশ প্রাপ্ত হতে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (72,2,65,' এবং অবশ্যই তোমরা অবগত আছ যে, তোমাদের মধ্যে যারা শনিবারের ব্যাপারে সীমা লংঘন করেছিল। আমি তাদেরকে বলেছিলাম যে, তোমরা অধম বানর হয়ে যাও।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (73,2,66,' অনন্তর আমি এটা তাদের সমসাময়িক ও তাদের পরবর্তীদের জন্য দৃষ্টান্ত এবং ধর্মভীরুগণের জন্য উপদেশ স্বরূপ করেছিলাম।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (74,2,67,'এবং যখন মূসা নিজ সম্প্রদায়কে বলেছিলঃ নিশ্চয়ই আল্লাহ তোমাদেরকে আদেশ করেছেন যে, তোমরা একটি গরু যবাহ কর। তারা বলেছিলঃ তুমি কি আমাদেরকে উপহাস করছ? সে বলেছিলঃ আমি আল্লাহর নিকট আশ্রয় প্রার্থনা করছি, যেন আমি মূর্খদের অন্তর্ভুক্ত না হই।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (75,2,68,' তারা বলেছিলঃ তুমি আমাদের জন্য তোমার রবের নিকট প্রার্থনা কর যে, তিনি আমাদেরকে যেন ওটা কি কি গুণ বিশিষ্ট হওয়া দরকার তা বলে দেন। সে বলেছিলঃ তিনি বলেছেন যে, নিশ্চয়ই সেই গরু বয়োঃবৃদ্ধ নয় এবং শাবকও নয়, এ দু’য়ের মধ্যবর্তী; অতএব তোমরা যেরূপ আদিষ্ট হয়েছ তা করে ফেল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (76,2,69,'তারা বলেছিলঃ তুমি আমাদের জন্য তোমার রবের নিকট প্রার্থনা কর যে, তিনি যেন ওর বর্ণ কিরূপ তা আমাদেরকে বলে দেন। সে বলেছিলঃ তিনি বলেছেন যে, নিশ্চয়ই সেই গরুর বর্ণ গাঢ় পীত, ওটা দর্শকদেরকে আনন্দ দান করে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (77,2,70,' তারা বলেছিলঃ তুমি আমাদের জন্য তোমার রবের নিকট প্রার্থনা কর যে, তিনি যেন ওটা কিরূপ তা আমাদের জন্য বর্ণনা করেন, নিশ্চয়ই আমাদের নিকট সকল গরুই সমতুল্য এবং আল্লাহ ইচ্ছা করলে আমরা সুপথগামী হব।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (78,2,71,' সে বলেছিলঃ নিশ্চয়ই তিনি বলেছেন যে, অবশ্যই সেই গরু সুস্থকায়, নিখুঁত, ওটা চাষাবাদের কাজে লাগানো হয়নি এবং ক্ষেতে পানি সেচনেও নিযুক্ত হয়নি। তারা বলেছিলঃ এক্ষণে তুমি সত্য এনেছ, অতঃপর তারা ওটা যবাহ করল যা তাদের করার ইচ্ছা ছিলনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (79,2,72,' এবং যখন তোমরা এক ব্যক্তিকে হত্যা করার পর তদ্বিষয়ে বিরোধ করছিলে এবং তোমরা যা গোপন করছিলে আল্লাহ তার প্রকাশক হলেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (80,2,73,' অতঃপর আমি বলেছিলামঃ ওর এক খন্ড দ্বারা তাকে আঘাত কর, এই রূপে আল্লাহ মৃতকে জীবিত করেন এবং স্বীয় নিদর্শনসমূহ প্রদর্শন করেন যাতে তোমরা হৃদয়ঙ্গম কর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (81,2,74,' অতঃপর তোমাদের হৃদয় প্রস্তরের ন্যায় কঠিন, বরং ওর চেয়েও কঠিনতর হল এবং নিশ্চয়ই প্রস্তর হতেও প্রস্রবন নির্গত হয় এবং নিশ্চয়ই ওগুলির মধ্যে কোন কোনটি বিদীর্ণ হয়, অতঃপর তা হতে পানি নির্গত হয় এবং নিশ্চয়ই ঐগুলির মধ্যে কোনটি আল্লাহর ভয়ে পতিত হয় এবং তোমরা যা করছ তৎপ্রতি আল্লাহ অমনোযোগী নন। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (82,2,75,'তোমরা কি আশা কর যে, তোমাদের কথায় তারা ঈমান আনবে? অথচ তাদের মধ্যে এমন কতক লোক গত হয়েছে যারা আল্লাহর কালাম শুনত, অতঃপর উহা বুঝার পর উহাকে বিকৃত করত, অথচ তারা জানত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (83,2,76,' যখন তারা মু’মিনদের সাথে মিলিত হয় তখন বলেঃ আমরা ঈমান এনেছি; আর যখন তাদের কেহ কারও (ইয়াহুদীর) নিকট গোপনে যায় তখন তারা বলেঃ তোমরা কি মুসলিমদেরকে এমন কথা বলে দাও যা আল্লাহ তোমাদের নিকট প্রকাশ করেছেন? পরিণামে তারা তোমাদেরকে তর্কে পরাজিত করবে (এই বলে) যে, এ বিষয়টি রবের নিকট হতে তোমাদের কিতাবে রয়েছে, তোমরা কি বুঝনা? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (84,2,77,' তারা কি জানেনা যে, তারা যা গোপন রাখে এবং যা প্রকাশ করে, আল্লাহ সবই জানেন?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (85,2,78,'এবং তাদের মধ্যে অনেক অশিক্ষিত লোক আছে যারা প্রবৃত্তি ব্যতীত কোন গ্রন্থ অবগত নয় এবং তারা শুধু কল্পনাসমূহ রচনা করে থাকে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (86,2,79,'তাদের জন্য আক্ষেপ যারা স্বহস্তে পুস্তক রচনা করে, যারা বলে যে, এটা আল্লাহর নিকট হতে সমাগত! এর দ্বারা তারা সামান্য মূল্য অর্জন করছে, তাদের হস্ত যা লিপিবদ্ধ করেছে তজ্জন্য তাদের প্রতি আক্ষেপ! এবং তারা যা উপার্জন করছে তজ্জন্যও তাদের প্রতি আক্ষেপ!');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (87,2,80,' এবং তারা বলেঃ নির্ধারিত দিনসমূহ ব্যতীত (জাহন্নামের) আগুন আমাদেরকে স্পর্শ করবেনা। তুমি বলঃ তোমরা কি আল্লাহর নিকট হতে অঙ্গীকার নিয়েছ যে, পরে আল্লাহ কখনই স্বীয় অঙ্গীকারের অন্যথা করবেননা? অথবা আল্লাহ সম্বন্ধে যা জানোনা তোমরা তা’ই বলছ?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (88,2,81,' হ্যাঁ, যারা অনিষ্ট অর্জন করেছে এবং স্বীয় পাপের দ্বারা পরিবেষ্টিত হয়েছে তারাই অগ্নিবাসী, সেখানে তারা সদা অবস্থান করবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (89,2,82,' এবং যারা বিশ্বাস স্থাপন করেছে ও সৎ কাজ করছে তারাই জান্নাতবাসী, তন্মধ্যে তারা চিরকাল অবস্থান করবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (90,2,83,' আর যখন আমি বানী ইসরাঈল হতে অঙ্গীকার নিয়েছিলাম যে, তোমরা আল্লাহ ব্যতীত আর কারও ইবাদাত করবেনা এবং মাতাপিতার সঙ্গে সদ্ব্যবহার করবে ও আত্মীয়দের, পিতৃহীনদের ও মিসকীনদের সঙ্গেও (সদ্ব্যবহার করবে), আর তোমরা লোকের সাথে উত্তমভাবে কথা বলবে এবং সালাত প্রতিষ্ঠিত করবে ও যাকাত প্রদান করবে; অতঃপর তোমাদের মধ্য হতে অল্প সংখ্যক ব্যতীত তোমরা সকলেই বিমুখ হয়েছিলে, যেহেতু তোমরা ছিলে অগ্রাহ্যকারী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (91,2,84,' এবং আমি যখন তোমাদের অঙ্গীকার গ্রহণ করেছিলাম যে, পরস্পর শোণিতপাত করবেনা এবং স্বীয় বাসস্থান হতে আপন ব্যক্তিদেরকে বহিস্কার করবেনা; অতঃপর তোমরা স্বীকৃত হয়েছিলে এবং তোমরাই ওর সাক্ষী ছিলে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (92,2,85,'অতঃপর সেই তোমরাই পরস্পর খুনাখুনি করছ এবং তোমরা তোমাদের মধ্য হতে এক দলকে তাদের গৃহ হতে বহিস্কার করে দিচ্ছ, তাদের বিরুদ্ধে (শত্রুতা বশতঃ) পাপ ও অন্যায় কাজে সাহায্য করছ এবং তারা বন্দী হয়ে তোমাদের নিকট আনীত হলে তোমরা তাদেরকে বিনিময় প্রদান কর, অথচ তাদেরকে বহিস্কার করা তোমাদের জন্য অবৈধ ছিল, তাহলে কি তোমরা গ্রন্থের কিছু অংশ বিশ্বাস কর এবং কিছু অংশ অবিশ্বাস কর? তোমাদের মধ্যে যারা এরূপ করে তাদের পার্থিব জীবনে দুর্গতি ব্যতীত কিছুই নেই এবং উত্থান দিনে তারা কঠোর শাস্তির দিকে নিক্ষিপ্ত হবে এবং তোমরা যা করছ, তদ্বিষয়ে আল্লাহ অমনোযোগী নন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (93,2,86,' এরাই আখিরাতের বিনিময়ে পার্থিব জীবন ক্রয় করেছে, অতএব তাদের দন্ড লঘু করা হবেনা এবং তারা সাহায্য প্রাপ্তও হবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (94,2,87,'এবং অবশ্যই আমি মূসাকে কিতাব প্রদান করেছি ও তৎপরে ক্রমান্বয়ে রাসূলগণকে প্রেরণ করেছি, এবং আমি মারইয়াম নন্দন ঈসাকে নিদর্শনসমূহ প্রদান করেছিলাম এবং পবিত্র আত্মাযোগে তাকে শক্তি সম্পন্ন করেছিলাম; কিন্তু পরে যখন তোমাদের নিকট কোন রাসূল - তোমাদের প্রবৃত্তি যা ইচ্ছা করতনা তা নিয়ে উপস্থিত হল তখন তোমরা অহংকার করলে, অবশেষে এক দলকে মিথ্যাবাদী বললে এবং একদলকে হত্যা করলে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (95,2,88,' এবং তারা বলে, আমাদের হৃদয় আবৃত; এবং তাদের অবিশ্বাসের জন্য আল্লাহ তাদেরকে অভিসম্পাত করেছেন - যেহেতু তারা অতি অল্পই বিশ্বাস করে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (96,2,89,' এবং যখন আল্লাহর সন্নিধান হতে তাদের নিকট যা আছে তার সত্যতা প্রতিপাদক গ্রন্থ উপস্থিত হল এবং যা পূর্ব হতেই তারা কাফিরদের উপর বিজয় প্রার্থনা করত; অতঃপর যখন তাদের নিকট সেই পরিচিত কিতাব এল তখন তারা তাকে অস্বীকার করল। সুতরাং এরূপ কাফিরদের উপর আল্লাহর লা’নত বর্ষিত হোক।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (97,2,90,'যার বিনিময়ে তারা নিজেদের বিক্রি করেছে তা খুবই মন্দ, যেহেতু তারা আল্লাহ যা নাযিল করেছেন তা অস্বীকার করেছে - এই হঠকারিতার জন্য যে, আল্লাহ স্বীয় বান্দাদের মধ্যে যার প্রতি ইচ্ছা অনুগ্রহ অবতারণ করেন। অতএব তারা ক্রোধের উপর ক্রোধ অর্জন করেছে। এবং কাফিরদের জন্য লাঞ্ছনাদায়ক শাস্তি রয়েছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (98,2,91,'এবং যখন তাদেরকে বলা হয় - আল্লাহ যা অবতীর্ণ করেছেন তা বিশ্বাস কর তখন তারা বলেঃ যা আমাদের প্রতি অবতীর্ণ হয়েছে আমরা তা’ই বিশ্বাস করি এবং তা ছাড়া যা রয়েছে তা অবিশ্বাস করি; অথচ এ গ্রন্থটি সত্য, তাদের কাছে যা আছে এটা তারই সত্যতার প্রতিপাদক। তুমি বলঃ যদি তোমরা বিশ্বাসীই ছিলে তাহলে ইতোপূর্বে কেন আল্লাহর নাবীগণকে হত্যা করেছিলে?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (99,2,92,'এবং নিশ্চয়ই মূসা উজ্জ্বল নিদর্শনাবলীসহ তোমাদের নিকট উপস্থিত হয়েছিল; অনন্তর তোমরা গো-বৎসকে উপাস্য রূপে গ্রহণ করেছিলে, যেহেতু তোমরা অত্যাচারী ছিলে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (100,2,93,' এবং যখন আমি তোমাদের অঙ্গীকার গ্রহণ করেছিলাম এবং তোমাদের উপর তূর পর্বত সমুচ্চ করেছিলাম এবং বলেছিলাম যে, আমি যা প্রদান করলাম তা দৃঢ়রূপে ধারণ কর এবং শ্রবণ কর। তারা বলেছিল, আমরা শুনলাম ও অগ্রাহ্য করলাম, এবং তাদের অবিশ্বাসের নিমিত্ত তাদের অন্তরসমূহে গো-বৎস প্রিয়তা সিঞ্চিত হয়েছিল। তুমি বলঃ যদি তোমরা বিশ্বাসী হও তাহলে তোমাদের বিশ্বাস যা কিছু আদেশ করছে তা অত্যন্ত নিন্দনীয়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (101,2,94,' তুমি বলঃ যদি অপর ব্যক্তিগণ অপেক্ষা তোমাদের জন্য আল্লাহর নিকট বিশেষ পারলৌকিক আলয় থাকে তাহলে তোমরা মৃত্যু কামনা কর, যদি তোমরা সত্যবাদী হও।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (102,2,95,' এবং তাদের হস্তসমূহ পূর্বে যা প্রেরণ করেছে তজ্জন্য তারা কখনই তা কামনা করবেনা এবং আল্লাহ অত্যাচারীদের সম্বন্ধে সবিশেষ অবগত আছেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (103,2,96,'এবং নিশ্চয়ই তুমি তাদেরকে অন্যান্য লোক এবং অংশীবাদীদের অপেক্ষাও অধিকতর আয়ু-আকাংক্ষী পাবে; তাদের মধ্যে প্রত্যেকে কামনা করে যেন তাকে হাজার বছর আয়ু দেয়া হয়, এবং ঐরূপ আয়ু প্রাপ্তিও তাদেরকে শাস্তি হতে মুক্ত করতে পারবেনা এবং তারা যা করছে আল্লাহ তার পরিদর্শক।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (104,2,97,' তুমি বলঃ যে ব্যক্তি জিবরাঈলের সাথে শত্রুতা রাখে এ জন্য যে, সে আল্লাহর হুকুমে এই কুরআনকে তোমার অন্তঃকরণ পর্যন্ত পৌঁছিয়েছে, যা পূর্ববতী কিতাবসমূহের সত্যতা প্রমাণ করছে এবং মু’মিনদের সুসংবাদ দিচ্ছে ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (105,2,98,'যে ব্যক্তি আল্লাহর, তাঁর মালাইকা/ফেরেশতাগণের, তাঁর রাসূলগণের, জিবরাঈলের এবং মিকাঈলের শত্রু, নিশ্চয়ই আল্লাহ এরূপ কাফিরদের শত্রু।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (106,2,99,' এবং নিশ্চয়ই আমি তোমার প্রতি স্পষ্ট নিদর্শনসমূহ অবতরণ করেছি এবং দুস্কর্মকারী ব্যতীত কেহই তা অবিশ্বাস করবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (107,2,100,' কি আশ্চর্য! যখন তারা কোন অঙ্গীকারে আবদ্ধ হল তখনই তাদের একদল তা ভঙ্গ করল। বরং তাদের অধিকাংশই বিশ্বাস করেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (108,2,101,' এবং যখন আল্লাহর নিকট হতে, তাদের নিকট যা আছে তার সত্য প্রত্যয়নকারী রাসূল তাদের নিকট আগমন করল, তখন যাদেরকে গ্রন্থ প্রদান করা হয়েছে তাদের একদল আল্লাহর গ্রন্থকে নিজেদের পশ্চাদভাগে নিক্ষেপ করল, যেন তারা কিছুই জানেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (109,2,102,'এবং সুলাইমানের রাজত্বকালে শাইতানরা যা আবৃত্তি করত, তারা ওরই অনুসরণ করছে, এবং সুলাইমান অবিশ্বাসী হয়নি - কিন্তু শাইতানরাই অবিশ্বাস করেছিল। তারা লোকদেরকে যাদু বিদ্যা এবং যা বাবেল শহরে হারূত-মারূত মালাক/ফেরেশতাদ্বয়ের প্রতি অবতীর্ণ হয়েছিল তা শিক্ষা দিত, এবং তারা উভয়ে কেহকেও ওটা এ কথা না বলে শিক্ষা দিতনা যে, ‘আমরা পরীক্ষাধীন ছাড়া কিছুই নই, অতএব তোমরা কুফরী করনা’। অনন্তর তারা যাতে স্বামী ও স্ত্রীর মধ্যে বিচ্ছেদ সংঘটিত হয় তা তাদের নিকট থেকে শিক্ষা গ্রহণ করত। কিন্তু তারা আল্লাহর আদেশ ব্যতীত তদ্বারা কারও অনিষ্ট করতে পারতনা, কিন্তু তারা ওটাই শিক্ষা করছে যাতে তাদের ক্ষতি হয় এবং তাদের কোন উপকার সাধিত না হয়; এবং নিশ্চয়ই তারা জ্ঞাত আছে - যে কেহ ওটা ক্রয় করেছে তার জন্য আখিরাতে সুখ লাভ নেই এবং তার বিনিময়ে তারা যে আত্মবিক্রয় করেছে তা নিকৃষ্ট - যদি তারা তা জানত!');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (110,2,103,' এবং যদি তারা সত্য সত্যই বিশ্বাস করত ও ধর্মভীরু হত তাহলে আল্লাহর নিকট হতে কল্যাণ লাভ করত - যদি তারা এটা বুঝতো। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (111,2,104,'হে মু’মিনগণ! তোমরা ‘রা‘য়েনা’ বলনা, বরং ‘উনযুরনা’ বল এবং শুনে নাও - অবিশ্বাসীদের জন্য রয়েছে যন্ত্রনাময় শাস্তি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (112,2,105,' আহলে কিতাবদের মধ্যে যারা কাফির (অর্থাৎ ইয়াহুদী ও নাসারা) এবং মুশরিকরা মোটেই পছন্দ করেনা যে, তোমাদের রবের পক্ষ হতে তোমাদের উপর কোন কল্যাণ বর্ষিত হোক, আর আল্লাহ যাকে ইচ্ছা করেন তাঁর করুণার জন্য নির্দিষ্ট করে নেন এবং আল্লাহ মহা করুণাময়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (113,2,106,' আমি কোন আয়াতের হুকুম রহিত করলে কিংবা আয়াতটিকে বিস্মৃত করিয়ে দিলে তদপেক্ষা উত্তম অথবা তদনুরূপ আয়াত আনয়ন করি। তুমি কি জাননা যে, আল্লাহ সর্ব বিষয়ের উপরই ক্ষমতাবান?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (114,2,107,' তুমি কি জাননা যে, আকাশ ও পৃথিবীর আধিপত্য আল্লাহরই এবং আল্লাহ ব্যতীত তোমাদের কোন বন্ধুও নেই এবং কোন সাহায্যকারীও নেই?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (115,2,108,'তোমরা কি চাও যে, তোমাদের রাসূলের নিকট আবেদন করবে যেমন ইতোপূর্বে মূসার নিকট (হঠকারিতা বশতঃ এইরূপ বহু নিরর্থক) আবেদন করা হয়েছিল? আর যে ব্যক্তি ঈমানের পরিবর্তে কুফরী অবলম্বন করে, নিশ্চয়ই সে সঠিক পথ হতে দূরে সরে পড়ে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (116,2,109,' কিতাবীদের অনেকে তাদের প্রতি সত্য প্রকাশিত হওয়ার পর তারা তাদের অন্তর্হিত বিদ্বেষ বশতঃ তোমাদেরকে বিশ্বাস স্থাপনের পরে অবিশ্বাসী করতে ইচ্ছা করে; কিন্তু যে পর্যন্ত আল্লাহ স্বয়ং আদেশ আনয়ন না করেন সে পর্যন্ত তোমরা ক্ষমা কর ও উপেক্ষা করতে থাক; নিশ্চয়ই আল্লাহ সর্ব বিষয়ের উপর ক্ষমতাবান।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (117,2,110,' আর তোমরা সালাত প্রতিষ্ঠিত কর ও যাকাত প্রদান কর; এবং তোমরা স্ব স্ব জীবনের জন্য যে সৎকাজ অগ্রে প্রেরণ করেছ তা আল্লাহর নিকট প্রাপ্ত হবে; তোমরা যা করছো নিশ্চয়ই আল্লাহ তার পরিদর্শক। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (118,2,111,' এবং তারা বলেঃ ইয়াহুদী বা খৃষ্টান ছাড়া আর কেহই জান্নাতে প্রবেশ করবেনা; এটা তাদের মিথ্যা আশা। তুমি বলঃ যদি তোমরা সত্যবাদী হও তাহলে তোমাদের প্রমাণ উপস্থিত কর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (119,2,112,' অবশ্য যে ব্যক্তি নিজেকে আল্লাহর নিকট সর্মপন করেছে এবং সৎকাজ করেছে, ফলতঃ তার জন্য তার রবের নিকট প্রতিদান রয়েছে এবং তাদের জন্য কোন আশংকা নেই ও তারা চিন্তিত হবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (120,2,113,' আর ইয়াহুদীরা বলেঃ খৃষ্টানরা কোন বিষয়ের উপর নেই; এবং খৃষ্টানরা বলে, ইয়াহুদীরাও কোন বিষয়ের উপর নেই - অথচ তারা গ্রন্থ পাঠ করে। এরূপ যারা জানেনা, তারাও ওদের কথার অনুরূপ কথা বলে থাকে; অতএব যে বিষয়ে তারা মতবিরোধ করেছিল, উত্থান দিনে আল্লাহ তাদের মধ্যে তদ্বিষয়ে ফায়সালা করে দিবেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (121,2,114,' এবং যে আল্লাহর মাসজিদসমূহের মধ্যে তাঁর নাম উচ্চারণ করতে নিষেধ করছে এবং তা উজাড় করতে চেষ্টা করছে সে অপেক্ষা কে অধিক অত্যাচারী? তাদের পক্ষে উপযুক্ত নয় যে, তারা শংকিত হওয়া ব্যতীত তন্মধ্যে প্রবেশ করে; তাদের জন্য ইহলোকে দুর্গতি এবং পরলোকে কঠোর শাস্তি রয়েছে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (122,2,115,' আল্লাহরই জন্য পূর্ব ও পশ্চিম; অতএব তোমরা যে দিকেই মুখ ফিরাও সেই দিকেই আল্লাহর দিক; কেননা আল্লাহ (সর্বদিক) পরিবেষ্টনকারী, পূর্ণ জ্ঞানবান।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (123,2,116,' এবং তারা বলে, আল্লাহ সন্তান গ্রহণ করেছেন। তিনি পরম পবিত্র! বরং যা কিছু আসমানে ও ভূমন্ডলে রয়েছে তা তাঁরই জন্য; সবই তাঁর আজ্ঞাধীন। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (124,2,117,'তিনি আকাশ ও পৃথিবীর সৃষ্টিকর্তা এবং যখন তিনি কোন কাজ সম্পাদন করতে ইচ্ছা করেন তখন তার জন্য শুধুমাত্র ‘হও’ বলেন, আর তাতেই তা হয়ে যায়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (125,2,118,'এবং মূর্খেরা বলেঃ আল্লাহ আমাদের সাথে কেন কথা বলেননা, অথবা কেন আমাদের জন্য কোন নিদর্শন উপস্থিত হয়না? এদের পূর্বে যারা ছিল তারাও এদের অনুরূপ কথা বলত; তাদের সবারই অন্তর পরস্পর সাদৃশ্যপূর্ণ; নিশ্চয়ই আমি বিশ্বাসী সম্প্রদায়ের জন্য উজ্জ্বল নিদর্শনাবলী বর্ণনা করি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (126,2,119,' নিশ্চয়ই আমি তোমাকে সত্যসহ সুসংবাদদাতা ও ভয় প্রদর্শক রূপে প্রেরণ করেছি এবং তুমি জাহান্নামবাসীদের সম্বন্ধে জিজ্ঞাসিত হবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (127,2,120,' এবং ইয়াহুদী ও খৃষ্টানরা - তুমি তাদের ধর্ম অনুসরণ না করা পর্যন্ত তোমার প্রতি সন্তুষ্ট হবেনা; তুমি বলঃ আল্লাহর প্রদর্শিত পথই সুপথ; এবং জ্ঞান প্রাপ্তির পর তুমি যদি তাদের খেয়াল-খুশির অনুসরণ কর তাহলে আল্লাহ হতে তোমার জন্য কোনই অভিভাবক ও সাহায্যকারী নেই।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (128,2,121,' আমি যাদেরকে যে ধর্মগ্রন্থ দান করেছি তা যারা সঠিকভাবে সত্য বুঝে পাঠ করে তারাই এর প্রতি বিশ্বাস স্থাপনকারী, এবং যে কেহ এটা অবিশ্বাস করে ফলতঃ তারাই ক্ষতিগ্রস্ত হবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (129,2,122,' হে বানী ইসরাঈল! আমি তোমাদেরকে যে সুখ সম্পদ দান করেছি এবং আমি পৃথিবীর উপর তোমাদেরকে যে শ্রেষ্ঠত্ব দান করেছি - তোমরা তা স্মরণ কর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (130,2,123,' আর তোমরা ঐ দিনের ভয় কর যেদিন একজন অন্যজন হতে কিছুমাত্র উপকৃত হবেনা এবং কারও নিকট হতে বিনিময় গৃহীত হবেনা, কারও সুপারিশ ফলপ্রদ হবেনা এবং তারা সাহায্য প্রাপ্ত হবেনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (131,2,124,'এবং যখন তোমার রাব্ব ইবরাহীমকে কতিপয় বাক্য দ্বারা পরীক্ষা করেছিলেন পরে সে তা পূর্ণ করেছিল; তিনি বলেছিলেনঃ নিশ্চয়ই আমি তোমাকে মানবমন্ডলীর নেতা করব। সে বলেছিলঃ আমার বংশধরগণ হতেও। তিনি বলেছিলেনঃ আমার অঙ্গীকার অত্যাচারীদের প্রতি প্রযোজ্য হবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (132,2,125,'এবং যখন আমি কা‘বা গৃহকে মানব জাতির জন্য সুরক্ষিত স্থান ও পুণ্যধাম করেছিলাম, এবং মাকামে ইবরাহীমকে সালাতের জায়গা নির্ধারণ করেছিলাম; এবং আমি ইবরাহীম ও ইসমাঈলের নিকট অঙ্গীকার নিয়েছিলাম যে, তোমরা আমার গৃহকে তাওয়াফকারী ও ই’তিকাফকারী এবং রুকু ও সাজদাহকারীদের জন্য পবিত্র রেখ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (133,2,126,'যখন ইবরাহীম বললঃ হে আমার রাব্ব! এ স্থানকে আপনি নিরাপত্তাময় শহরে পরিণত করুন এবং এর অধিবাসীদের মধ্যে যারা আল্লাহ ও আখিরাতে বিশ্বাস স্থাপন করেছে তাদেরকে উপজীবিকার জন্য ফল-শষ্য প্রদান করুন। (আল্লাহ) বলেনঃ যারা কুফরী করে তাদেরকে আমি অল্প কিছু দিন জীবনোপভোগ করতে দিব, অতঃপর তাদেরকে অগ্নির শাস্তি ভোগ করতে বাধ্য করব, ঐ গন্তব্য স্থান নিকৃষ্টতম।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (134,2,127,'যখন ইবরাহীম ও ইসমাঈল কা‘বার ভিত্তি স্থাপন করল (তখন বলল) হে আমাদের রাব্ব! আমাদের পক্ষ হতে এটি গ্রহণ করুন, নিশ্চয়ই আপনি শ্রবণকারী, মহাজ্ঞানী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (135,2,128,' হে আমাদের রাব্ব! আমাদের উভয়কে আপনার অনুগত করুন, এবং আমাদের বংশধরদের মধ্য হতেও আপনার অনুগত এক দল লোক সৃষ্টি করুন, আর আমাদেরকে হাজ্জের আহকাম বলে দিন এবং আমাদের প্রতি দয়া করুন, নিশ্চয়ই আপনি ক্ষমাশীল, করুণাময়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (136,2,129,' হে আমাদের রাব্ব! তাদেরই মধ্য হতে এমন একজন রাসূল প্রেরণ করুন যিনি তাদেরকে আপনার নিদর্শনাবলী পাঠ করে শুনাবেন এবং তাদেরকে গ্রন্থ ও বিজ্ঞান শিক্ষা দান করবেন ও তাদেরকে পবিত্র করবেন। নিশ্চয়ই আপনি পরাক্রান্ত, বিজ্ঞানময়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (137,2,130,' এবং যে নিজকে নির্বোধ করে তুলেছে সে ব্যতীত কে ইবরাহীমের ধর্ম হতে বিমুখ হবে? এবং নিশ্চয়ই আমি তাকে এই পৃথিবীতে মনোনীত করেছিলাম, নিশ্চয়ই সে সৎ কর্মশীলগণের অন্তর্ভুক্ত ছিল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (138,2,131,'যখন তার রাব্ব তাকে বলেছিলেনঃ তুমি আনুগত্য স্বীকার কর; সে বলেছিল: আমি বিশ্বজগতের রবের নিকট আত্মসমর্পণ করলাম।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (139,2,132,'আর ইবরাহীম ও ইয়াকূব স্বীয় সন্তানদেরকে সদুপদেশ প্রদান করেছিলঃ হে আমার বংশধরগণ! নিশ্চয়ই আল্লাহ তোমাদের জন্য এই ধর্ম মনোনীত করেছেন, অতএব তোমরা মুসলিম না হয়ে মৃত্যু বরণ করনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (140,2,133,' যখন ইয়াকূবের মৃত্যু উপস্থিত হল তখন কি তোমরা উপস্থিত ছিলে, যখন সে নিজ পুত্রদেরকে বলেছিলঃ আমার পরে তোমরা কোন্ জিনিসের ইবাদাত করবে? তারা বলেছিলঃ আমরা তোমার উপাস্যের এবং তোমার পিতৃপুরুষ ইবরাহীম, ইসমাঈল ও ইসহাকের উপাস্য - সেই অদ্বিতীয় উপাস্যের ইবাদাত করব এবং আমরা তাঁরই অনুগত থাকব।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (141,2,134,' ওটি একটি দল ছিল, যা অতীত হয়ে গেছে; তারা যা অর্জন করেছিল তা তাদের জন্য এবং তোমরা যা অর্জন করেছ তা তোমাদের জন্য এবং তারা যা করে গেছে তজ্জন্য তোমরা জিজ্ঞাসিত হবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (142,2,135,'এবং তারা বলে যে, তোমরা ইয়াহুদী অথবা খৃষ্টান হও, তাহলেই সুপথ প্রাপ্ত হবে; তুমি বলঃ বরং আমরা ইবরাহীমের সুদৃঢ় ধর্মে আছি এবং সে অংশীবাদীদের অন্তর্ভুক্ত ছিলনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (143,2,136,'তোমরা বলঃ আমরা আল্লাহর প্রতি এবং যা আমাদের প্রতি অবতীর্ণ হয়েছে, আর যা ইবরাহীম, ইসমাঈল, ইসহাক, ইয়াকূব ও তদীয় বংশধরগণের প্রতি অবতীর্ণ হয়েছিল এবং যা মূসা ও ঈসাকে প্রদান করা হয়েছিল এবং অন্যান্য নাবীগণকে তাদের রাব্ব হতে যা প্রদত্ত হয়েছিল, তদসমুদয়ের উপর বিশ্বাস স্থাপন করছি, তাদের মধ্যে কেহকেও আমরা প্রভেদ করিনা, এবং আমরা তাঁরই প্রতি আত্মসমর্পণকারী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (144,2,137,' অনন্তর তোমরা যেরূপ বিশ্বাস স্থাপন করেছ, তারাও যদি তদ্রুপ বিশ্বাস স্থাপন করে তাহলে নিশ্চয়ই তারা সুপথ প্রাপ্ত হবে; এবং যদি তারা ফিরে যায় তাহলে তারা শুধু বিরুদ্ধাচরণেই ফিরে যাবে; অতএব অচিরেই আল্লাহ তাদের প্রতিকূলে তোমাকেই যথেষ্ট করবেন এবং তিনিই শ্রবণকারী, মহাজ্ঞানী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (145,2,138,' আমরা আল্লাহরই রংয়ে রঞ্জিত, আল্লাহ অপেক্ষা কে শ্রেষ্ঠতম রঞ্জনকারী? এবং আমরা তাঁরই বান্দা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (146,2,139,' তুমি বলঃ তোমরা কি আল্লাহ সম্বন্ধে আমাদের সঙ্গে বিরোধ করছ? অথচ তিনিই আমাদের রাব্ব ও তোমাদের রাব্ব, এবং আমাদের জন্য আমাদের কার্যসমূহ এবং তোমাদের জন্য তোমাদের কার্যসমূহ এবং আমরা তাঁরই প্রতি বিশ্বস্ত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (147,2,140,'তোমরা কি বলছ - ইবরাহীম, ইসমাঈল, ইসহাক, ইয়াকূব ও তদীয় বংশধরগণ ইয়াহুদী ও খৃষ্টান ছিল? তুমি বলঃ তোমরাই সঠিক জ্ঞানী না আল্লাহ এবং আল্লাহর নিকট হতে প্রাপ্ত সাক্ষ্য যে ব্যক্তি গোপন করছে সে অপেক্ষা কে বেশি অত্যাচারী? এবং তোমরা যা করছ তা হতে আল্লাহ অমনোযোগী নন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (148,2,141,' ওটা একটি জামা‘আত ছিল যা বিগত হয়েছে; তারা যা অর্জন করেছে তা তাদের জন্য এবং তোমরা যা অর্জন করেছ তা তোমাদের জন্য, এবং তারা যা করে গেছে তদ্বিষয়ে তোমরা জিজ্ঞাসিত হবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (149,2,142,' মানবমন্ডলীর মধ্যস্থিত নির্বোধেরা অচিরেই বলবে, কিসে তাদেরকে সেই কিবলা হতে প্রত্যাবৃত্ত করল যার দিকে তারা ছিল? তুমি বলে দাওঃ পূর্ব ও পশ্চিম আল্লাহরই জন্য, তিনি যাকে ইচ্ছা সরল পথ প্রদর্শন করেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (150,2,143,' এভাবে আমি তোমাদেরকে আদর্শ জাতি করেছি, যেন তোমরা মানবগণের জন্য সাক্ষী হও এবং রাসূলও তোমাদের জন্য সাক্ষী হয়; এবং তুমি যে কিবলার দিকে ছিলে তা আমি এ জন্য প্রতিষ্ঠিত করেছিলাম যে, কে রাসূলের অনুসরণ করে, আর কে তা হতে স্বীয় পদদ্বয়ে পশ্চাতে ফিরে যায় আমি তা জেনে নিব এবং আল্লাহ যাদেরকে পথ প্রদর্শন করেছেন তারা ছাড়া অপরের জন্য এটি অবশ্যই কঠোরতর; এবং আল্লাহ এরূপ নন যে, তোমাদের বিশ্বাস বিনষ্ট করেন; নিশ্চয়ই আল্লাহ মানুষের প্রতি স্নেহশীল, করুণাময়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (151,2,144,' নিশ্চয়ই আমি আকাশের দিকে তোমার মুখমন্ডল উত্তোলন অবলোকন করেছি। তাই আমি তোমাকে ঐ কিবলাহমুখীই করাচ্ছি যা তুমি কামনা করছো। অতএব তুমি মাসজিদুল হারামের দিকে তোমার মুখমন্ডল ফিরিয়ে দাও এবং তোমরা যেখানেই থাক তোমাদের আনন সে দিকেই প্রত্যাবর্তিত কর; এবং নিশ্চয়ই যাদেরকে কিতাব দেয়া হয়েছে তারা অবশ্যই অবগত আছে যে, নিশ্চয়ই এটি তাদের রবের নিকট হতে প্রেরিত সত্য; এবং তারা যা করছে তদ্বিষয়ে আল্লাহ অমনোযোগী নন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (152,2,145,' এবং যাদেরকে গ্রন্থ প্রদত্ত হয়েছে তাদের নিকট যদি তুমি সমুদয় নিদর্শন আনয়ন কর তবুও তারা তোমার কিবলাহকে গ্রহণ করবেনা; এবং তুমিও তাদের কিবলাহ গ্রহণ করতে পারনা, আর তাদের কোন দলও অন্য দলের কিবলাহকে স্বীকার করেনি, এবং তোমার নিকট যে জ্ঞান এসেছে এর পরেও যদি তুমি তাদের প্রবৃত্তির অনুসরণ কর তাহলে নিশ্চয়ই তুমি অত্যাচারীদের অন্তর্ভুক্ত হয়ে যাবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (153,2,146,' যাদেরকে আমি কিতাব প্রদান করেছি তারা তাকে এরূপভাবে চিনে, যেমন চিনে তারা আপন সন্তানদেরকে এবং নিশ্চয়ই তাদের এক দল জ্ঞাতসারে সত্যকে গোপন করছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (154,2,147,' এই বাস্তব সত্য তোমার রবের পক্ষ হতে; সুতরাং তুমি সংশয়ীদের অন্তর্ভুক্ত হয়োনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (155,2,148,'প্রত্যেকের জন্য এক একটি লক্ষ্যস্থল রয়েছে, ঐ দিকেই সে মুখমন্ডল প্রত্যাবর্তিত করে, অতএব তোমরা কল্যাণের দিকে ধাবিত হও; তোমরা যেখানেই থাকনা কেন, আল্লাহ তোমাদের সকলকেই একত্রিত করবেন; নিশ্চয়ই আল্লাহ সর্ব বিষয়ে পূর্ণ ক্ষমতাবান।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (156,2,149,'এবং তুমি যেখান হতেই বের হবে- তোমার মুখ পবিত্রতম মাসজিদের দিকে প্রত্যাবর্তিত কর এবং নিশ্চয়ই এটাই তোমার রবের নিকট হতে প্রেরিত সত্য এবং তোমরা যা করছ তদ্বিষয়ে আল্লাহ অমনোযোগী নন। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (157,2,150,' আর তুমি যেখান হতেই নিস্ক্রান্ত হও - তোমার মুখ পবিত্রতম মাসজিদের দিকে ফিরাও এবং যে যেখানে আছ তোমাদের মুখমন্ডল তদ্দিকেই প্রত্যাবর্তিত কর যেন অত্যাচারীরা ব্যতীত অপরে তোমাদের সাথে বির্তক করতে না পারে। অতএব তোমরা তাদেরকে ভয় করনা, বরং আমাকেই ভয় কর যেন আমি তোমাদের উপর আমার অনুগ্রহ পূর্ণ করি, এবং যেন তোমরা সুপথ প্রাপ্ত হও।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (158,2,151,' আমি তোমাদের মধ্য হতে এরূপ রাসূল প্রেরণ করেছি যে তোমাদের নিকট আমার নিদর্শনাবলী পাঠ করে এবং তোমাদেরকে পবিত্র করে, তোমাদেরকে গ্রন্থ ও বিজ্ঞান শিক্ষা দেয় এবং তোমরা যা অবগত ছিলেনা তা শিক্ষা দান করে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (159,2,152,' অতএব তোমরা আমাকেই স্মরণ কর, আমিও তোমাদেরকেই স্মরণ করব এবং তোমরা আমারই নিকট কৃতজ্ঞ হও এবং অবিশ্বাসী হয়োনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (160,2,153,'হে বিশ্বাস স্থাপনকারীগণ! তোমরা ধৈর্য ও সালাতের মাধ্যমে সাহায্য প্রার্থনা কর; নিশ্চয়ই আল্লাহ ধৈর্যশীলগণের সাথে আছেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (161,2,154,' আর যারা আল্লাহর পথে নিহত হয়েছে তাদেরকে মৃত বলনা, বরং তারা জীবিত; কিন্তু তোমরা তা অবগত নও।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (162,2,155,' এবং নিশ্চয়ই আমি তোমাদেরকে ভয়, ক্ষুধা, ধন, প্রাণ এবং ফল-ফসলের দ্বারা পরীক্ষা করব; এবং ঐ সব ধৈর্যশীলদের সুসংবাদ প্রদান কর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (163,2,156,' যাদের উপর কোন বিপদ নিপতিত হলে তারা বলে, নিশ্চয়ই আমরা আল্লাহরই জন্য এবং নিশ্চয়ই আমরা তাঁরই দিকে প্রত্যাবর্তনকারী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (164,2,157,' এদের উপর তাদের রবের পক্ষ হতে শান্তি ও করুনা বর্ষিত হবে এবং এরাই সুপথগামী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (165,2,158,'নিশ্চয়ই ‘সাফা’ ও ‘মারওয়া’ আল্লাহর নিদর্শনসমূহের অন্তর্গত। অতএব যে ব্যক্তি এই গৃহে ‘হাজ্জ’ অথবা ‘উমরাহ’ পালন’ করে তার জন্য এতদুভয়ের প্রদক্ষিণ করা দোষণীয় নয়, এবং কোন ব্যক্তি স্বেচ্ছায় সৎ কাজ করলে আল্লাহ গুণগ্রাহী, সর্বজ্ঞাত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (166,2,159,' আমি যে সব উজ্জ্বল নিদর্শন ও পথ-নির্দেশ অবতীর্ণ করেছি ঐগুলিকে সর্ব সাধারণের নিকট প্রকাশ করার পরও যারা ঐসব বিষয়কে গোপন করে, আল্লাহ তাদেরকে অভিসম্পাত করেন এবং অভিসম্পাতকারীরাও তাদেরকে অভিসম্পাত করে থাকে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (167,2,160,' কিন্তু যারা তাওবাহ করে ও সংশোধিত হয় এবং সত্য প্রকাশ করে, বস্ত্ততঃ আমি তাদের প্রতি ক্ষমা প্রদানকারী, করুণাময়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (168,2,161,'যারা অবিশ্বাস করেছে ও অবিশ্বাসী অবস্থায় মৃত্যু বরণ করেছে, নিশ্চয়ই তাদের উপর আল্লাহর মালাক/ফেরেশতা এবং মানবকূলের সবারই অভিসম্পাত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (169,2,162,'তন্মধ্যে তারা সর্বদা অবস্থান করবে, তাদের শাস্তি প্রশমিত হবেনা এবং তাদেরকে অবকাশ দেয়া হবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (170,2,163,' এবং তোমাদের ইলাহ একমাত্র আল্লাহ; সেই সর্বদাতা করুণাময় ব্যতীত অন্য কেহ উপাস্য নেই।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (171,2,164,'নিশ্চয়ই নভোমন্ডল ও ভূমন্ডল সৃষ্টিতে, দিন ও রাতের পরিবর্তনে, নৌ-পথে জাহাজসমূহের চলাচলে - যাতে রয়েছে মানুষের জন্য কল্যাণ। মৃত পৃথিবীকে সঞ্জীবিত করণে, তাতে নানাবিধ জীবজন্তু সঞ্চারিত করার জন্য আল্লাহ আকাশ হতে বৃষ্টি বর্ষণ করেন। বায়ুরাশির গতি পরিবর্তনে এবং আকাশ ও পৃথিবীর মধ্যস্থ সঞ্চিত মেঘের সঞ্চারণে সত্যি সত্যিই জ্ঞানবান সম্প্রদায়ের জন্য নিদর্শন রয়েছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (172,2,165,'এবং মানবমন্ডলীর মধ্যে এরূপ আছে - যারা আল্লাহ ব্যতীত অপরকে সদৃশ স্থির করে, আল্লাহকে ভালবাসার ন্যায় তারা তাদেরকে ভালবাসে। এবং যারা বিশ্বাস স্থাপন করেছে আল্লাহর প্রতি - তাদের প্রেম দৃঢ়তর এবং যারা অত্যাচার করেছে তারা যদি শাস্তি অবলোকন করত তাহলে দেখত, সমুদয় শক্তিই আল্লাহর এবং নিশ্চয়ই আল্লাহ শাস্তি দানে কঠোর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (173,2,166,' যাদের অনুসরণ করা হয়েছে তারা যখন অনুসারীদেরকে প্রত্যাখ্যান করবে তখন তারা শাস্তি প্রত্যক্ষ করবে এবং তাদের সম্পর্ক বিচ্ছিন্ন হয়ে যাবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (174,2,167,'অনুসরণকারীরা বলবেঃ যদি আমরা ফিরে যেতে পারতাম তাহলে তারা যেরূপ আমাদেরকে প্রত্যাখ্যান করেছে আমরাও তদ্রুপ তাদেরকে প্রত্যাখ্যান করতাম। এভাবে আল্লাহ তাদের কৃতকর্মের পরিণাম দুঃখজনকভাবে প্রদর্শন করবেন এবং তারা অগ্নি হতে উদ্ধার পাবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (175,2,168,' হে মানবমন্ডলী! পৃথিবীর মধ্যে যা বৈধ ও পবিত্র, তা হতে আহার কর এবং শাইতানের পদাঙ্ক অনুসরণ করনা, নিশ্চয়ই সে তোমাদের প্রকাশ্য শত্রু।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (176,2,169,' সে এতদ্ব্যতীত তোমাদেরকে আদেশ করে শাইতানী ও অশ্লীল কাজ করতে এবং আল্লাহ সম্বন্ধে তোমরা যা জাননা তা বলতে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (177,2,170,'এবং যখন তাদেরকে বলা হয় যে, আল্লাহ যা অবতীর্ণ করেছেন তা অনুসরণ কর তখন তারা বলেঃ বরং আমরা ওরই অনুসরণ করব যা আমাদের পিতৃ-পুরুষগণ হতে প্রাপ্ত হয়েছি; যদিও তাদের পিতৃ-পুরুষদের কোনই জ্ঞান ছিলনা এবং তারা সুপথগামীও ছিলনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (178,2,171,' আর যারা অবিশ্বাস করেছে তাদের দৃষ্টান্ত ওদের ন্যায় - যেমন কেহ আহবান করলে শুধু চীৎকার ও ধ্বনি ব্যতীত আর কিছুই শোনেনা, তারা বধির, মুক, অন্ধ; কাজেই তারা বুঝতে পারেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (179,2,172,'হে বিশ্বাস স্থাপনকারীগণ! আমি তোমাদেরকে যা উপজীবিকা স্বরূপ দান করেছি সেই পবিত্র বস্তুসমূহ আহার কর এবং আল্লাহর নিকট কৃতজ্ঞতা প্রকাশ কর, যদি তোমরা তাঁরই ইবাদাত করে থাক।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (180,2,173,' তিনি শুধু তোমাদের জন্য মৃত জীব, রক্ত, শুকরের মাংস এবং যা আল্লাহ ব্যতীত অপরের উদ্দেশে নিবেদিত - তদ্ব্যতীত অবৈধ করেননি; বস্তুতঃ যে ব্যক্তি নিরূপায়, কিন্তু সীমা লংঘনকারী নয়, তার জন্য পাপ নেই; এবং নিশ্চয়ই আল্লাহ ক্ষমাশীল, করুণাময়। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (181,2,174,' আল্লাহ যা গ্রন্থে অবতীর্ণ করেছেন তা যারা গোপন করে ও তৎপরিবর্তে নগণ্য মূল্য গ্রহণ করে, নিশ্চয়ই তারা অগ্নি ছাড়া অন্য কিছু ভক্ষন করেনা; এবং উত্থান দিনে আল্লাহ তাদের সাথে কথা বলবেননা, তাদেরকে পবিত্র করবেননা এবং তাদের জন্য রয়েছে যন্ত্রনাদায়ক শাস্তি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (182,2,175,'ওরাই সুপথের বিনিময়ে কুপথ এবং ক্ষমার পরিবর্তে শাস্তি ক্রয় করেছে, অতঃপর জাহান্নামের আগুন কিরূপে সহ্য করবে?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (183,2,176,' এ জন্যই আল্লাহ সত্যসহ গ্রন্থ অবতীর্ণ করেছেন এবং যারা গ্রন্থ সম্বন্ধে বিরোধ করে বাস্তবিকই তারা বিরুদ্ধাচরণে সুদূরগামী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (184,2,177,' তোমরা তোমাদের মুখমন্ডল পূর্ব বা পশ্চিম দিকে প্রত্যাবর্তিত করলেই তাতে পুণ্য নেই, বরং পুণ্য তার যে ব্যক্তি আল্লাহ, আখিরাত, মালাইকা/ফেরেশতা, কিতাব ও নাবীগণের প্রতি বিশ্বাস স্থাপন করে এবং তাঁরই প্রেমে ধন-সম্পদের প্রতি আকর্ষণ থাকা সত্ত্বেও সে তা আত্মীয়-স্বজন, পিতৃহীন, দরিদ্র, পথিক ও ভিক্ষুকদেরকে এবং দাসত্ব মোচনের জন্য ব্যয় করে, আর সালাত প্রতিষ্ঠিত করে ও যাকাত প্রদান করে এবং অঙ্গীকার করলে তা পূরণ করে এবং যারা অভাবে ও ক্লেশে এবং যুদ্ধকালে ধৈর্যশীল তারাই সত্য পরায়ণ এবং তারাই ধর্মভীরু।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (185,2,178,' হে বিশ্বাস স্থাপনকারীগণ! নিহতদের সম্বন্ধে তোমাদের জন্য প্রতিশোধ গ্রহণ বিধিবদ্ধ করা হল; স্বাধীনের পরিবর্তে স্বাধীন, দাসের পরিবর্তে দাস এবং নারীর পরিবর্তে নারী। কিন্তু যদি কেহ তার ভাই কর্তৃক কোন বিষয়ে ক্ষমা প্রাপ্ত হয় তাহলে যেন ন্যায় সঙ্গতভাবে পাওনা সাব্যস্ত করা হয় এবং সদ্ভাবে তা পরিশোধ করে। এটা তোমাদের রবের পক্ষ হতে লঘু বিধান ও করুণা; অতঃপর যে কেহ সীমা লংঘন করবে তার জন্য যন্ত্রনাদায়ক শাস্তি রয়েছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (186,2,179,' হে জ্ঞানবান লোকেরা! কিসাসের মধ্যে তোমাদের জন্য জীবন রয়েছে, যেন তোমরা তাকওয়া অবলম্বন করতে পার।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (187,2,180,'যখন তোমাদের কারও মৃত্যু নিকটবর্তী বলে মনে হয়, তখন সে যদি ধন সম্পত্তি রেখে যায় তাহলে মাতা-পিতা ও আত্মীয়-স্বজনের জন্য বৈধভাবে অসীয়াত করা তোমাদের জন্য বিধিবদ্ধ হল, ধর্ম-ভীরুদের এটা অবশ্য করণীয়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (188,2,181,' অতঃপর যে ব্যক্তি শোনার পর তা পরিবর্তন করে, তাহলে এর পাপ তাদেরই হবে যারা একে পরিবর্তন করবে; নিশ্চয়ই আল্লাহ শ্রবণকারী, মহাজ্ঞানী। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (189,2,182,'অনন্তর যদি কেহ অসীয়াতকারীর পক্ষে পক্ষপাতিত্ব অথবা পাপের আশঙ্কা করে তাদের মধ্যে মীমাংসা করে দেয়, তাতে তার পাপ নেই; নিশ্চয়ই আল্লাহ ক্ষমাশীল, করুণাময়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (190,2,183,' হে বিশ্বাস স্থাপনকারীগণ! তোমাদের পূর্ববতী লোকদের ন্যায় তোমাদের উপরও সিয়ামকে অপরিহার্য কর্তব্য রূপে নির্ধারণ করা হল যেন তোমরা সংযমশীল হতে পারো।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (191,2,184,'ওটা নির্দিষ্ট কয়েক দিন। কিন্তু তোমাদের মধ্যে যে কেহ পীড়িত কিংবা প্রবাসী হয় তার জন্য অপর কোন দিন হতে গণনা করবে, আর যারা ওতে অক্ষম তারা তৎপরিবর্তে একজন দরিদ্রকে আহার্য দান করবে। অতএব যে ব্যক্তি স্বেচ্ছায় সৎ কাজ করে তার জন্য কল্যাণ এবং তোমরা যদি বুঝে থাক তাহলে সিয়াম পালনই তোমাদের জন্য কল্যাণকর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (192,2,185,' রামাযান মাস, যে মাসে বিশ্বমানবের জন্য পথ প্রদর্শন এবং সু-পথের উজ্জ্বল নিদর্শন এবং হক ও বাতিলের প্রভেদকারী কুরআন অবতীর্ণ হয়েছে। অতএব তোমাদের মধ্যে যে ব্যক্তি সেই মাসে (নিজ আবাসে) উপস্থিত থাকে সে যেন সিয়াম পালন করে এবং যে ব্যক্তি পীড়িত অথবা প্রবাসী, তার জন্য অপর কোন দিন হতে গণনা করবে; তোমাদের পক্ষে যা সহজসাধ্য আল্লাহ তা’ই ইচ্ছা করেন ও তোমাদের পক্ষে যা দুঃসাধ্য তা ইচ্ছা করেননা এবং যেন তোমরা নির্ধারিত সংখ্যা পূরণ করে নিতে পার এবং তোমাদেরকে যে সুপথ দেখিয়েছেন তজ্জন্য তোমরা আল্লাহকে মহিমান্বিত কর এবং যেন তোমরা কৃতজ্ঞতা প্রকাশ কর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (193,2,186,' এবং যখন আমার সেবকবৃন্দ (বান্দা) আমার সম্বন্ধে তোমাকে জিজ্ঞেস করে তখন তাদেরকে বলে দাওঃ নিশ্চয়ই আমি সন্নিকটবর্তী। কোন আহবানকারী যখনই আমাকে আহবান করে তখনই আমি তার আহবানে সাড়া দিই; সুতরাং তারাও যেন আমার ডাকে সাড়া দেয় এবং আমাকে বিশ্বাস করে - তাহলেই তারা সঠিক পথপ্রাপ্ত হতে পারবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (194,2,187,' রামাযানের রাতে আপন স্ত্রীদের সাথে মেলামেশা করা তোমাদের জন্য বৈধ করা হয়েছে, তারা তোমাদের জন্য এবং তোমরা তাদের জন্য আবরণ, তোমরা যে নিজেদের ক্ষতি করছিলে আল্লাহ তা জ্ঞাত আছেন, এ জন্য তিনি তোমাদের প্রতি প্রত্যাবৃত্ত হলেন এবং তোমাদের (ভার) লাঘব করে দিলেন; অতএব এক্ষণে তোমরা (রামাযানের রাতেও) তাদের সাথে মিলিত হও এবং আল্লাহ তোমাদের জন্য যা লিপিবদ্ধ করেছেন তা অনুসন্ধান কর এবং প্রত্যুষে কালো সূতা হতে সাদা সূতা প্রকাশিত না হওয়া পর্যন্ত তোমরা আহার ও পান কর, অতঃপর রাত সমাগম পর্যন্ত তোমরা সিয়াম পূর্ণ কর; তোমরা মাসজিদে ই‘তিকাফ করার সময় (তোমাদের স্ত্রীদের সাথে) মিলিত হবেনা; এটিই আল্লাহর সীমা। অতএব তোমরা উহার নিকটেও যাবেনা; এভাবে আল্লাহ মানবমন্ডলীর জন্য তাঁর নিদর্শনসমূহ বিবৃত করেন, যেন তারা সংযত হয়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (195,2,188,' এবং তোমরা নিজেদের মধ্যে পরস্পরের ধন সম্পত্তি অন্যায়ভাবে গ্রাস করনা এবং তা বিচারকের নিকট টোপ হিসাবে উপস্থাপন করনা যাতে তোমরা জ্ঞাতসারে লোকের সম্পদের অংশ অন্যায়ভাবে উদরস্থ করতে পার।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (196,2,189,' তারা তোমাকে নতুন চাঁদসমূহ সম্বন্ধে জিজ্ঞেস করছে। তুমি বলঃ এগুলি হচ্ছে সমগ্র মানব জাতির জন্য সময়সমূহ (মাসসমূহ) নির্ধারণ (গণনা বা হিসাব) করার মাধ্যম এবং হাজ্জের জন্য; আর (ঐ হাজ্জের চাঁদে) তোমরা যে পশ্চাৎ দিক দিয়ে গৃহে প্রবেশ কর এটি পুণ্যের কাজ নয়, বরং পুণ্যের কাজ হল যে ব্যক্তি সংযমশীলতা অবলম্বন করল। এবং তোমরা গৃহসমূহে ওগুলির দরযা দিয়ে প্রবেশ কর এবং আল্লাহকে ভয় কর, যাতে তোমরা সুফল প্রাপ্ত হও।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (197,2,190,' এবং যারা তোমাদের সাথে যুদ্ধ করে, তোমরাও তাদের সাথে আল্লাহর পথে যুদ্ধ কর এবং সীমা অতিক্রম করনা; নিশ্চয়ই আল্লাহ সীমা লংঘনকারীদেরকে ভালবাসেননা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (198,2,191,' তাদেরকে যেখানেই পাও,  হত্যা কর এবং তারা তোমাদেরকে যেখান হতে বহিস্কার করেছে তোমরাও তাদেরকে সেখান হতে বহিস্কার কর এবং হত্যা অপেক্ষা অশান্তি (ফিতনা) গুরুতর এবং তোমরা তাদের সাথে পবিত্রতম মাসজিদের নিকট যুদ্ধ করনা, যে পর্যন্ত না তারা তোমাদের সাথে তন্মধ্যে যুদ্ধ করে; কিন্তু যদি তারা তোমাদের সাথে যুদ্ধ করে তাহলে তোমরাও তাদেরকে হত্যা কর; অবিশ্বাসীদের জন্য এটাই প্রতিফল। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (199,2,192,' অতঃপর যদি নিবৃত্ত হয় তাহলে নিশ্চয়ই আল্লাহ ক্ষমাশীল, করুণাময়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (200,2,193,'ফিতনা দূর হয়ে আল্লাহর দীন প্রতিষ্ঠিত না হওয়া পর্যন্ত তোমরা তাদের সাথে যুদ্ধ কর; অতঃপর যদি তারা নিবৃত্ত হয় তাহলে অত্যাচারীদের উপর ব্যতীত শত্রুতা নেই। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (201,2,194,' নিষিদ্ধ মাসের পরিবর্তে নিষিদ্ধ মাস ও সমস্ত নিষিদ্ধ বিষয় পরস্পর সমান; অতঃপর যে কেহ তোমাদের প্রতি অত্যাচার করে, তাহলে সে তোমাদের প্রতি যেরূপ অত্যাচার করবে তোমরাও তার প্রতি সেরূপ অত্যাচার কর এবং আল্লাহকে ভয় কর ও জেনে রেখ যে, আল্লাহ সংযমশীলদের সঙ্গী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (202,2,195,' এবং তোমরা আল্লাহর পথে ব্যয় কর এবং স্বীয় হস্ত ধ্বংসের দিকে প্রসারিত করনা এবং কল্যাণ সাধন করতে থাকো, নিশ্চয়ই আল্লাহ কল্যাণ সাধনকারীদের ভালবাসেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (203,2,196,'তোমরা আল্লাহর উদ্দেশে হাজ্জ ও ‘ওমরাহ সম্পূর্ণ কর; কিন্তু তোমরা যদি বাধাপ্রাপ্ত হও তাহলে যা সহজ প্রাপ্র তাই উৎসর্গ কর এবং কুরবানীর জন্তুগুলি স্বস্থানে না পৌঁছা পর্যন্ত তোমাদের মস্তক মুন্ডন করনা। কিন্তু কেহ যদি তোমাদের মধ্যে পীড়িত হয়, অথবা তার মস্তিস্ক যন্ত্রনাগ্রস্ত হয় তাহলে সে সিয়াম কিংবা সাদাকাহ অথবা কুরবানী দ্বারা ওর বিনিময় করবে, অতঃপর যখন তোমরা শান্তিতে থাকো তখন যে ব্যক্তি ওমরাহ্ ও হাজ্জ একত্রে কামনা করে তাহলে যা সহজ প্রাপ্য তা’ই উৎসর্গ করবে। কিন্তু কেহ যদি তা প্রাপ্ত না হয় তাহলে হাজ্জের সময় তিন দিন এবং যখন তোমরা প্রত্যাবর্তিত হও তখন সাত দিন - এই পূর্ণ দশ দিন সিয়াম পালন করবে; এটা তারই জন্য - যার পরিজন পবিত্রতম মাসজিদে উপস্থিত না থাকে এবং আল্লাহকে ভয় কর ও জেনে রেখ যে, আল্লাহ কঠিন শাস্তি দাতা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (204,2,197,' হাজ্জের মাসগুলি সুবিদিত। কেহ যদি ঐ মাসগুলির মধ্যে হাজ্জের সংকল্প করে তাহলে সে হাজ্জের সময়ে সহবাস, দুস্কার্য ও কলহ করতে পারবেনা এবং তোমরা যে কোন সৎ কাজ করনা কেন আল্লাহ তা জ্ঞাত আছেন। আর তোমরা তোমাদের সাথে পাথেয় নিয়ে নাও। বস্ত্ততঃ উৎকৃষ্ট পাথেয় হচ্ছে তাকওয়া বা আত্মসংযম। সুতরাং হে জ্ঞানবানগণ! আমাকে ভয় কর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (205,2,198,'তোমরা স্বীয় রবের অনুগ্রহ লাভের চেষ্টা করলে তাতে তোমাদের পক্ষে কোন অপরাধ নেই; অতঃপর যখন তোমরা আরাফাত হতে প্রত্যাবর্তিত হও তখন পবিত্র স্মৃতি-স্থানের নিকট আল্লাহকে স্মরণ কর; এবং তিনি তোমাদেরকে যেরূপ নির্দেশ দিয়েছেন তদ্রুপ তাঁকে স্মরণ কর; এবং নিশ্চয়ই তোমরা এর পূর্বে বিভ্রান্তদের অন্তর্ভুক্ত ছিলে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (206,2,199,' অতঃপর অন্যান্যরা যেখান হতে প্রত্যাবর্তন করে তোমরাও প্রত্যাবর্তন কর এবং আল্লাহর নিকট ক্ষমা প্রার্থনা কর; নিশ্চয়ই আল্লাহ ক্ষমাশীল, করুণাময়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (207,2,200,'অনন্তর যখন তোমরা তোমাদের (হাজ্জের) অনুষ্ঠানগুলি সম্পন্ন করে ফেল তখন যেরূপ তোমাদের পিতৃ-পুরুষদেরকে স্মরণ করতে তদ্রুপ আল্লাহকে স্মরণ কর, বরং তদপেক্ষা দৃঢ়তরভাবে স্মরণ কর; কিন্তু মানবমন্ডলীর মধ্যে কেহ কেহ এরূপ আছে যারা বলেঃ হে আমাদের রাব্ব! আমাদেরকে ইহকালেই দান কর এবং তাদের জন্য আখিরাতে কোনই অংশ নেই।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (208,2,201,' আর তাদের মধ্যে কেহ কেহ বলে - হে আমাদের রাব্ব! আমাদেরকে ইহকালে কল্যাণ দান করুন ও আখিরাতে কল্যাণ দান করুন এবং জাহান্নামের শাস্তি হতে আমাদেরকে রক্ষা করুন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (209,2,202,' তারা যা অর্জন করেছে, তাদের জন্য তারই অংশ রয়েছে এবং নিশ্চয়ই আল্লাহ সত্ত্বর হিসাব গ্রহণকারী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (210,2,203,' এবং নির্ধারিত দিনসমূহে আল্লাহকে স্মরণ কর; অতঃপর কেহ যদি দু’ দিনের মধ্যে (মাক্কায় ফিরে যেতে) তাড়াহুড়া করে তাহলে তার জন্য কোন পাপ নেই। পক্ষান্তরে কেহ যদি দু‘ দিন বিলম্ব করে তাহলে তার জন্যও পাপ নেই এবং তোমরা আল্লাহকে ভয় কর ও জেনে রেখ যে, তোমাদের সকলকে তাঁরই সন্নিধানে সমবেত করা হবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (211,2,204,'এবং মানুষের মধ্যে এমনও আছে যার পার্থিব জীবন সংক্রান্ত কথা তোমাকে চমৎকৃত করে, আর সে নিজের (অন্তরস্থ সততা) সম্বন্ধে আল্লাহকে সাক্ষী করে থাকে, কিন্তু বস্তুতঃ সে হচ্ছে কঠোর কলহপরায়ণ ব্যক্তি। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (212,2,205,'যখন সে প্রত্যাবর্তন করে তখন তার উদ্দেশ্য থাকে দুনিয়ায় অশান্তি সৃষ্টি করা এবং শস্য-ক্ষেত্র ও জীব-জন্তু বিনাশ করা; এবং আল্লাহ অশান্তি ভালবাসেন না।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (213,2,206,'যখন তাকে বলা হয়, তুমি আল্লাহকে ভয় কর, তখন প্রতিপত্তির অহমিকা তাকে অধিকতর অনাচারে লিপ্ত করে। অতএব জাহান্নামই তার জন্য যথেষ্ট এবং নিশ্চয়ই ওটা নিকৃষ্ট আশ্রয় স্থল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (214,2,207,' পক্ষান্তরে কোন কোন লোক এরূপ আছে যে আল্লাহর সন্তুষ্টি সাধনের জন্য আত্মবির্সজন করে, এবং আল্লাহ হচ্ছেন সমস্ত বান্দার প্রতি স্নেহ-পরায়ণ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (215,2,208,' হে মু’মিনগণ! তোমরা পূর্ণ রূপে ইসলামে প্রবিষ্ট হও এবং শাইতানের পদাংক অনুসরণ করনা, নিশ্চয়ই সে তোমাদের জন্য প্রকাশ্য শত্রু।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (216,2,209,' অতঃপর স্পষ্ট দলীল প্রমাণাদী তোমাদের নিকট সমাগত হওয়ার পরেও যদি তোমরা পদস্খলিত হয়ে যাও তাহলে জেনে রেখ, আল্লাহ হচ্ছেন মহাপরাক্রান্ত, বিজ্ঞানময়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (217,2,210,' তারা শুধু এই অপেক্ষাই করছে যে, আল্লাহ শুভ্র মেঘমালার ছায়াতলে মালাইকা/ফেরেশতাদেরকে সঙ্গে নিয়ে তাদের নিকট সমাগত হবেন ও সমস্ত কাজের নিস্পত্তি করবেন। এবং আল্লাহরই নিকট সমস্ত কার্য প্রত্যাবর্তিত হয়ে থাকে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (218,2,211,' ইসরাঈল বংশীয়দেরকে জিজ্ঞেস কর, আমি কত স্পষ্ট প্রমাণ তাদেরকে প্রদান করেছি। এবং যে কেহ তার নিকট আল্লাহর অনুগ্রহ সম্পদ আসার পর তা পরিবর্তন করে তাহলে জেনে রেখ, নিশ্চয়ই আল্লাহ কঠোর শাস্তিদাতা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (219,2,212,' যারা অবিশ্বাস করেছে তাদের পার্থিব জীবন সুশোভিত করা হয়েছে এবং তারা বিশ্বাস স্থাপনকারীদেরকে উপহাস করে থাকে, এবং যারা ধর্মভীরু তাদেরকে উত্থান দিনে সমুন্নত করা হবে; এবং আল্লাহ যাকে ইচ্ছা করেন অপরিমিত জীবিকা দান করে থাকেন। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (220,2,213,'মানব জাতি একই সম্প্রদায়ভুক্ত ছিল; অতঃপর আল্লাহ সুসংবাদ বাহক ও ভয় প্রদর্শক রূপে নাবীগণকে প্রেরণ করলেন এবং তিনি তাদের সাথে সত্যসহ গ্রন্থ অবতীর্ণ করলেন যেন (ঐ কিতাব) তাদের মতভেদের বিষয়গুলি সম্বন্ধে মীমাংসা করে দেয়, অথচ যারা কিতাব প্রাপ্ত হয়েছিল, স্পষ্ট নিদর্শনসমূহ তাদের নিকট সমাগত হওয়ার পর, পরস্পরের প্রতি হিংসা বিদ্বেষ বশতঃ তারা সেই কিতাবকে নিয়ে মতভেদ ঘটিয়ে বসলো, বিশ্বাস স্থাপনকারীরা যে বিষয়ে ভিন্ন মত পোষণ করত আল্লাহ নিজ অনুগ্রহে সেই বিষয়ে তাদেরকে সত্য পথে পরিচালিত করেন। আল্লাহর যাকে ইচ্ছা সঠিক পথে পরিচালিত করেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (221,2,214,' তোমরা কি মনে করেছ যে, তোমরাই জান্নাতে প্রবেশ করবে? অথচ তোমরা এখনও তাদের অবস্থা প্রাপ্ত হওনি যারা তোমাদের পূর্বে বিগত হয়েছে; তাদেরকে বিপদ ও দুঃখ স্পর্শ করেছিল এবং তাদেরকে প্রকম্পিত করা হয়েছিল; এমন কি রাসূল ও তৎসহ বিশ্বাস স্থাপনকারীগণ বলেছিলঃ কখন আল্লাহর সাহায্য আসবে? সতর্ক হও, নিশ্চয়ই আল্লাহর সাহায্য নিকটবর্তী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (222,2,215,' তারা তোমাকে জিজ্ঞেস করছে, তারা কিরূপে ব্যয় করবে? তুমি বলঃ তোমরা ধন সম্পত্তি হতে যা ব্যয় করবে তা মাতা-পিতার, আত্মীয়-স্বজনের, পিতৃহীনদের, দরিদ্রদের ও পথিকবৃন্দের জন্য কর; এবং তোমরা যে সব সৎকাজ কর নিশ্চয়ই আল্লাহ তা সম্যক রূপে অবগত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (223,2,216,' জিহাদকে তোমাদের জন্য অপরিহার্য কর্তব্য রূপে অবধারিত করা হয়েছে এবং এটি তোমাদের নিকট অপ্রীতিকর; বস্ত্ততঃ তোমরা এমন বিষয়কে অপছন্দ করছ যা তোমাদের পক্ষে বাস্তবিকই মঙ্গলজনক, পক্ষান্তরে তোমরা এমন বিষয়কে পছন্দ করছ যা তোমাদের জন্য বাস্তবিকই অনিষ্টকর এবং আল্লাহই (তোমাদের ভাল-মন্দ) অবগত আছেন এবং তোমরা অবগত নও।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (224,2,217,'তারা তোমাকে নিষিদ্ধ মাসে যুদ্ধ করা সম্বন্ধে জিজ্ঞেস করছে। তুমি বলঃ ওর মধ্যে যুদ্ধ করা অতীব অন্যায়। আর আল্লাহর পথে প্রতিরোধ করা এবং তাঁকে অবিশ্বাস করা ও পবিত্র মাসজিদ হতে তার অধিবাসীদেরকে বহিস্কার করা আল্লাহর নিকট তদপেক্ষা গুরুতর অপরাধ; এবং অশান্তি সৃষ্টি হত্যা অপেক্ষা গুরুতর এবং যদি তারা সক্ষম হয় তাহলে তারা তোমাদেরকে তোমাদের ধর্ম হতে ফিরাতে না পারা পর্যন্ত নিবৃত্ত হবেনা; আর তোমাদের মধ্যকার কেহ যদি স্বধর্ম হতে ফিরে যায় এবং ঐ কাফির অবস্থায়ই তার মৃত্যু ঘটে তাহলে তার ইহকাল ও পরকাল সংক্রান্ত সমস্ত সাধনাই ব্যর্থ হয়ে যাবে; তারাই অগ্নির অধিবাসী এবং তারই মধ্যে তারা চিরকাল অবস্থান করবে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (225,2,218,' নিশ্চয়ই যারা বিশ্বাস স্থাপন করেছে এবং আল্লাহর পথে দেশ ত্যাগ করেছে ও ধর্ম-যুদ্ধ করেছে, তারাই আল্লাহর অনুগ্রহ প্রত্যাশী এবং আল্লাহ ক্ষমাশীল, করুণাময়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (226,2,219,' মাদক দ্রব্য ও জুয়া খেলা সম্বন্ধে তারা তোমাকে জিজ্ঞেস করছে। তুমি বলঃ এ দু’টোর মধ্যে গুরুতর পাপ রয়েছে এবং কোন কোন লোকের (কিছু) উপকার আছে, কিন্তু ও দু’টোর লাভ অপেক্ষা পাপই গুরুতর; তারা তোমাকে (আরও) জিজ্ঞেস করছে, তারা কি (পরিমান) ব্যয় করবে? তুমি বলঃ যা তোমাদের উদ্ধৃত্ত; এভাবে আল্লাহ তোমাদের জন্য নিদর্শনাবলী ব্যক্ত করেন যেন তোমরা চিন্তা-ভাবনা কর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (227,2,220,' পার্থিব ও পারলৌকিক বিষয়ে। তারা তোমাকে ইয়াতীমদের সম্বন্ধে জিজ্ঞেস করছে; তুমি বলঃ তাদের হিত সাধন করাই উত্তম; এবং যদি তোমরা তাদেরকে সম্মিলিত করে নাও তাহলে তারা তোমাদের ভাই; আর কে অনিষ্টকারী, কে হিতাকাংখী আল্লাহ তা অবগত আছেন এবং যদি আল্লাহ ইচ্ছা করতেন তাহলে তিনি তোমাদেরকে বিপদে ফেলতেন, নিশ্চয়ই আল্লাহ পরাক্রান্ত, বিজ্ঞানময়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (228,2,221,' এবং মুশরিক নারীকে ঈমান না আনা পর্যন্ত তোমরা বিয়ে করনা এবং নিশ্চয়ই মু’মিন কৃতদাসী মুশরিক মহিলা অপেক্ষা উত্তম যদিও সে তোমাদেরকে মোহিত করে; এবং অংশীবাদীরা বিশ্বাস স্থাপন না করা পর্যন্ত তাদের সাথে (মুসলিম নারীদের) বিয়ে দিওনা এবং নিশ্চয়ই অংশীবাদী তোমাদের মনঃপুত হলেও বিশ্বাসী দাস তদপেক্ষা শ্রেয়তর; এরাই জাহান্নামের দিকে আহবান করে এবং আল্লাহ স্বীয় ইচ্ছায় জান্নাত ও ক্ষমার দিকে আহবান করেন ও মানবমন্ডলীর জন্য স্বীয় নিদর্শনাবলী বিবৃত করেন যেন তারা শিক্ষা গ্রহণ করে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (229,2,222,' এবং তারা তোমাকে (স্ত্রীলোকদের) ঋতু সম্বন্ধে জিজ্ঞেস করছে; তুমি বলঃ ওটা হচ্ছে অশুচি। অতএব ঋতুকালে স্ত্রীলোকদেরকে অন্তরাল কর এবং উত্তম রূপে শুদ্ধ না হওয়া পর্যন্ত তাদের নিকটে যেওনা; অতঃপর যখন তারা পবিত্র হবে তখন আল্লাহর নির্দেশ মত তোমরা তাদের নিকট গমন কর, নিশ্চয়ই আল্লাহ তাওবাহকারীদেরকে ভালবাসেন এবং যারা পবিত্র থাকে তাদেরকেও ভালবাসেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (230,2,223,' তোমাদের স্ত্রীগণ তোমাদের জন্য ক্ষেত্র স্বরূপ; অতএব তোমরা যখন যেভাবে ইচ্ছা স্বীয় জীবনের জন্য ব্যবহার কর এবং নিজেদের আগামী দিনের জন্য ব্যবস্থা কর এবং আল্লাহকে ভয় কর ও জেনে রেখ, একদিন আল্লাহর সাথে সাক্ষাত হবে। আর বিশ্বাসীদেরকে সুসংবাদ জানিয়ে দাও।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (231,2,224,'এবং মানবমন্ডলীর মধ্যে হিত সাধন, পরহেযগারী ও মীমাংসা করে দেয়ার ক্ষেত্রে তোমরা স্বীয় শপথসমূহের জন্য আল্লাহর নামকে লক্ষ্যবস্তু বানিও না। বস্তুতঃ আল্লাহ সর্বশ্রোতা, সর্বজ্ঞাতা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (232,2,225,' তোমাদের নিরর্থক শপথসমূহের জন্য তোমাদেরকে আল্লাহ পাকড়াও করবেননা, কিন্তু তিনি তোমাদেরকে ঐ সব শপথ সম্বন্ধে ধরবেন যেগুলি তোমাদের মনের সংকল্প অনুসারে সাধিত হয়েছে; এবং আল্লাহ ক্ষমাশীল, ধৈর্যশীল। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (233,2,226,' যারা স্বীয় স্ত্রীগণ হতে পৃথক থাকার শপথ করে তারা চার মাস প্রতীক্ষা করবে, অতঃপর যদি তারা প্রত্যাবর্তিত হয় তাহলে নিশ্চয়ই আল্লাহ ক্ষমাশীল, করুণাময়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (234,2,227,' পক্ষান্তরে যদি তারা তালাক দিতেই দৃঢ় প্রতিজ্ঞ হয় তাহলে নিশ্চয়ই আল্লাহ শ্রবণকারী, মহাজ্ঞানী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (235,2,228,'এবং তালাক প্রাপ্তাগণ তিন ঋতু পর্যন্ত অপেক্ষা করবে; এবং যদি তারা আল্লাহ ও পরকালে বিশ্বাস করে তাহলে আল্লাহ তাদের গর্ভে যা সৃষ্টি করেছেন তা গোপন করা তাদের পক্ষে বৈধ হবেনা; এবং এর মধ্যে যদি তারা সন্ধি কামনা করে তাহলে তাদের স্বামীই তাদেরকে প্রতিগ্রহণ করতে সমধিক স্বত্ববান; আর নারীদের উপর তাদের স্বামীদের যেরূপ স্বত্ব আছে, স্ত্রীদেরও তাদের পুরুষদের (স্বামীর) উপর তদনুরূপ ন্যায়সঙ্গত অধিকার আছে; এবং তাদের উপর পুরুষদের শ্রেষ্ঠত্ব রয়েছে; আল্লাহ হচ্ছেন মহা পরাক্রান্ত, বিজ্ঞানময়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (236,2,229,'তালাক দুইবার; অতঃপর (স্ত্রীকে) হয় বিহিতভাবে রাখতে হবে অথবা সদ্ভাবে পরিত্যাগ করতে হবে; আর নিজেদের দেয় সম্পদ থেকে কিছু ফিরিয়ে নেয়া তোমাদের জন্য জায়েয নয় স্ত্রীদের কাছ থেকে, যদি উভয়ে আশংকা করে যে, তারা আল্লাহর সীমা স্থির রাখতে পারবেনা। অনন্তর তোমরা যদি আশংকা কর যে, আল্লাহর নির্দেশ ঠিক রাখতে পারবেনা, সেই অবস্থায় স্ত্রী নিজের মুক্তি লাভের জন্য কিছু বিনিময় দিলে তাতে উভয়ের কোন দোষ নেই; এগুলি হচ্ছে আল্লাহর সীমাসমূহ। অতএব তা অতিক্রম করনা এবং যারা আল্লাহর সীমা অতিক্রম করে বস্তুতঃ তারাই অত্যাচারী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (237,2,230,'অতঃপর যদি সে তালাক প্রদান করে তাহলে এরপরে অন্য স্বামীর সাথে বিবাহিতা না হওয়া পর্যন্ত সে তার জন্য বৈধ হবেনা, অতঃপর সে তাকে তালাক প্রদান করলে যদি উভয়ে পরস্পর প্রত্যাবর্তিত হয় তাতে উভয়ের পক্ষে কোনই দোষ নেই, যদি আল্লাহর সীমারেখা বজায় রাখার ইচ্ছা থাকে। এবং এগুলিই আল্লাহর সীমাসমূহ, তিনি জ্ঞানী সম্প্রদায়ের জন্য এগুলি ব্যক্ত করে থাকেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (238,2,231,'এবং তোমরা যখন স্ত্রীদেরকে তালাক দাও, আর তারা তাদের নির্ধারিত সময়ে পৌঁছে যায়, তখন তাদেরকে নিয়মিতভাবে রাখতে পার অথবা নিয়মিতভাবে পরিত্যাগ করতে পার; এবং তাদেরকে যন্ত্রণা দেয়ার জন্য আবদ্ধ করে রেখনা, তাহলে সীমা লংঘন করবে; আর যে ব্যক্তি এরূপ করে সে নিশ্চয়ই নিজের প্রতি অবিচার করে থাকে। এবং আল্লাহর নিদর্শনাবলীকে বিদ্রুপাচ্ছলে গ্রহণ করনা, তোমাদের প্রতি আল্লাহর অনুগ্রহ এবং তোমাদেরকে উপদেশ দানের জন্য গ্রন্থ ও বিজ্ঞান হতে যা অবতীর্ণ করেছেন তা স্মরণ কর, আর আল্লাহকে ভয় কর এবং জেনে রেখ যে, আল্লাহ সর্ব বিষয়ে মহাজ্ঞানী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (239,2,232,' এবং যখন তোমরা স্ত্রীলোকদেরকে তালাক দাও, অতঃপর তারা তাদের নির্ধারিত সময়ে পৌছে যায়, তখন তারা উভয়েই যদি পরস্পরের প্রতি বিহিতভাবে সম্মত হয়ে থাকে, সেই অবস্থায় স্ত্রীরা নিজ স্বামীদেরকে বিয়ে করতে গেলে তোমরা তাদেরকে বাঁধা প্রদান করনা; তোমাদের মধ্যে যে আল্লাহ ও আখিরাতের প্রতি বিশ্বাস স্থাপন করেছে এর দ্বারা তাদেরকেই উপদেশ দেয়া হচ্ছে; তোমাদের জন্য এটি শুদ্ধতম ও পবিত্রতম (ব্যবস্থা) এবং আল্লাহ অবগত আছেন ও তোমরা অবগত নও।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (240,2,233,'এবং যদি কেহ স্তন্য পানের কাল পূর্ণ করতে ইচ্ছা করে তার জন্য জননীগণ পূর্ণ দুই বছর স্বীয় সন্তানদেরকে স্তন্য দান করবে; আর সন্তানের জনকগণ বিহিতভাবে প্রসূতিদের খোরাক ও তাদের পোশাক দিতে বাধ্য; কেহকেও তার সাধ্যের অতীত কষ্ট দেয়া যাবেনা, নিজ সন্তানের কারণে জননীকে ক্ষতিগ্রস্ত করা চলবেনা, এবং পিতার জন্যও একই বিধান, কিন্তু যদি তারা পরস্পর পরামর্শ ও সম্মতি অনুসারে স্তন্য ত্যাগ করাতে ইচ্ছা করে তাতে উভয়ের কোন দোষ নেই; আর তোমরা যদি নিজ সন্তানদেরকে স্তন্য পানের জন্য সমর্পণ করে বিহিতভাবে কিছু প্রদান কর তাহলেও তোমাদের কোন দোষ নেই; এবং আল্লাহকে ভয় কর ও জেনে রেখ যে, তোমরা যা করছ আল্লাহ তা প্রত্যক্ষকারী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (241,2,234,' এবং তোমাদের মধ্যে যারা স্ত্রীদেরকে রেখে মৃত্যুমুখে পতিত হয়, তারা (বিধবাগণ) চার মাস ও দশ দিন প্রতীক্ষা করবে; অতঃপর যখন তারা স্বীয় নির্ধারিত সময়ে উপনীত হয় তখন তারা নিজেদের সম্বন্ধে বিহিতভাবে যা করবে তাতে তোমাদের কোন দোষ নেই; এবং তোমরা যা করছো তদ্বিষয়ে আল্লাহ সম্যক খবর রাখেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (242,2,235,' এবং তোমরা স্ত্রীলোকদের প্রস্তাব সম্বন্ধে পরোক্ষভাবে যা ব্যক্ত কর অথবা নিজেদের মনে গোপনে যা পোষণ কর তাতে তোমাদের কোন দোষ নেই; আল্লাহ অবগত আছেন যে, তোমরা তাদের বিষয় আলোচনা করবে, কিন্তু গোপনভাবে তাদেরকে প্রতিশ্রুতি দান করনা, বরং বিহিতভাবে তাদের সাথে কথা বল; এবং নির্ধারিত সময় পূর্ণ না হওয়া পর্যন্ত বিবাহ বন্ধনে আবদ্ধ হওয়ার সংকল্প করনা; এবং এটিও জেনে রেখ যে, তোমাদের অন্তরে যা আছে আল্লাহ তা অবগত। অতএব তোমরা তাঁকে ভয় কর এবং জেনে রেখ যে, আল্লাহ ক্ষমাশীল, সহিষ্ণু।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (243,2,236,' যদি তোমরা স্ত্রীদেরকে স্পর্শ না করে অথবা তাদের প্রাপ্য নির্ধারণ করার পূর্বে তালাক প্রদান কর তাহলে তাতে তোমাদের কোন দোষ নেই, এবং তোমরা তাদেরকে কিছু সংস্থান করে দিবে, অবস্থাপন্ন লোক নিজের অবস্থানুসারে এবং অভাবগ্রস্ত লোক তার সাধ্যানুসারে বিহিত সংস্থান (করে দিবে); সৎ কর্মশীল লোকদের উপর ইহাই কর্তব্য।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (244,2,237,' আর তোমরা যদি তাদেরকে স্পর্শ করার পূর্বেই তালাক প্রদান কর এবং তাদের মোহর নির্ধারণ করে থাক তাহলে যা নির্ধারণ করেছিলে তার অর্ধেক দিয়ে দাও; কিন্তু যদি তারা ক্ষমা করে কিংবা যার হাতে বিবাহ বন্ধন সে ক্ষমা করে তাহলে তা স্বতন্ত্র কথা; অথবা তোমরা পুরুষরা যদি ক্ষমা কর তাহলে এটা পরহেজগারীর অতি নিকটবর্তী; এবং পরস্পরের উপকারকে যেন ভুলে যেওনা; তোমরা যা কর নিশ্চয়ই আল্লাহ তা প্রত্যক্ষকারী। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (245,2,238,' তোমরা সালাতের প্রতি যত্নবান হবে বিশেষতঃ মধ্যবর্তী সালাতের এবং আল্লাহর উদ্দেশে তোমরা বিনীতভাবে দন্ডায়মান হও।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (246,2,239,' তবে তোমরা যদি আশংকা কর, সেই অবস্থায় পদব্রজে বা যানবাহনের উপর সালাত সমাপন করে নিবে, পরে যখন নিরাপদ হও তখন তোমাদেরকে যেভাবে শিখিয়ে দেয়া হয়েছে সেইভাবে আল্লাহর প্রশংসা কর যা তোমরা ইতোপূর্বে জানতেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (247,2,240,' এবং তোমাদের মধ্যে যারা মৃত্যুমুখে পতিত হয় ও স্ত্রীগণকে ছেড়ে যায় তারা যেন স্বীয় স্ত্রীগণকে বহিস্কৃত না করে এক বছর পর্যন্ত তাদেরকে ভরণ-পোষণ প্রদান করার জন্য অসীয়াত করে যায়, কিন্তু যদি তারা (স্বেচ্ছায়) বের হয়ে যায় তাহলে নিজেদের সম্বন্ধে বিহিতভাবে তারা যে ব্যবস্থা করে তজ্জন্য তোমাদের কোন দোষ নেই, এবং আল্লাহ মহাপরাক্রান্ত, বিজ্ঞানময়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (248,2,241,' আর তালাক প্রাপ্তদের জন্য বিহিতভাবে ভরণ পোষণের ব্যবস্থা করা ধর্মভীরুগণের কর্তব্য। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (249,2,242,'এভাবে আল্লাহ স্বীয় নিদর্শনাবলী বিবৃত করেন, যেন তোমরা হৃদয়ঙ্গম কর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (250,2,243,' তুমি কি তাদের প্রতি লক্ষ্য করনি, মৃত্যুর বিভীষিকাকে এড়ানোর জন্য যারা নিজেদের গৃহ হতে বহির্গত হয়েছিল? অথচ তারা ছিল বহু সহস্র; তখন আল্লাহ তাদেরকে বললেনঃ তোমরা মর; পুনরায় তিনি তাদেরকে জীবন দান করলেন; নিশ্চয়ই মানবগণের প্রতি আল্লাহ অনুগ্রহশীল, কিন্তু অধিকাংশ লোক কৃতজ্ঞতা স্বীকার করেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (251,2,244,' তোমরা আল্লাহর পথে সংগ্রাম কর এবং জেনে রেখ যে, নিশ্চয়ই আল্লাহ হচ্ছেন সর্বশ্রোতা, সর্বজ্ঞাতা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (252,2,245,' কে সে, যে আল্লাহকে উত্তম ঋণদান করে? অনন্তর তিনি তাকে দ্বিগুণ, বহুগুণ বর্ধিত করেন এবং আল্লাহই (মানুষের আর্থিক অবস্থাকে) কৃচ্ছ বা স্বচ্ছল করে থাকেন এবং তাঁরই দিকে প্রত্যাবর্তন করতে হবে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (253,2,246,' তুমি কি মূসার পরে ইসরাঈল বংশীয় প্রধানদের প্রতি লক্ষ্য করনি? নিজেদের এক নাবীকে যখন তারা বলেছিলঃ আমাদের জন্য একজন রাজা নিযুক্ত করে দাও (যেন) আমরা আল্লাহর পথে যুদ্ধ করতে পারি। সে বলেছিলঃ এটা কি সম্ভবপর নয় যে, যখন তোমাদের উপর যুদ্ধ বিধিবদ্ধ হয়ে যাবে তখন তোমরা যুদ্ধ করবেনা? তারা বলেছিলঃ আমরা যুদ্ধ করবনা এটা কিরূপে (সম্ভব), অথচ নিজেদের আবাস হতে ও স্বজনদের নিকট হতে আমরা বহিস্কৃত হয়েছি? অনন্তর যখন তাদের উপর যুদ্ধ বিধিবদ্ধ হল তখন তাদের অল্প সংখ্যক ব্যতীত সবাই পশ্চাৎপদ হয়ে পড়ল এবং অত্যাচারীদেরকে আল্লাহ সম্যক রূপে অবগত আছেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (254,2,247,' এবং তাদের নাবী তাদেরকে বলেছিলঃ নিশ্চয়ই আল্লাহ তালূতকে তোমাদের জন্য রাজা রূপে নির্বাচিত করেছেন; তারা বললঃ আমাদের উপর তালূতের রাজত্ব কি রূপে (সঙ্গত) হতে পারে? রাজত্বে তার অপেক্ষা আমাদেরই স্বত্ব অধিক, পক্ষান্তরে যথেষ্ট আর্থিক স্বচ্ছলতাও তার নেই; তিনি বললেনঃ নিশ্চয়ই আল্লাহ তোমাদের জন্য তাকেই মনোনীত করেছেন এবং তাকে প্রচুর জ্ঞান ও দৈহিক শক্তি দান করেছেন, আল্লাহ তাঁর রাজত্ব যাকে ইচ্ছা প্রদান করেন এবং আল্লাহ হচ্ছেন দানশীল, সর্বজ্ঞাতা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (255,2,248,'এবং তাদের নাবী তাদের বলেছিলঃ তার রাজত্বের নিদর্শন এই যে, তোমাদের নিকট সিন্দুক সমাগত হবে, যাতে থাকবে তোমাদের রবের নিকট হতে শান্তি এবং মূসা ও হারুনের পরিবারের (পরিত্যক্ত) কিছু সামগ্রী, মালাইকা/ফেরেশতা ওটা বহন করে আনবে, তোমরা যদি বিশ্বাস স্থাপনকারী হও তাহলে ওর মধ্যে নিশ্চয়ই তোমাদের জন্য নিদর্শন রয়েছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (256,2,249,' অনন্তর যখন তালূত সৈন্যদলসহ বহির্গত হয়েছিল তখন সে বলেছিল, নিশ্চয়ই আল্লাহ একটি নদী দ্বারা তোমাদেরকে পরীক্ষা করবেন, ওটা হতে যে পান করবে সে আমার দলভুক্ত নয় এবং যে স্বীয় হস্ত দ্বারা অঞ্জলি পূর্ণ করে নিবে এবং তদ্ব্যতীত সে আর আস্বাদন করবেনা সে নিশ্চয়ই আমার লোক; কিন্তু তাদের মধ্যে অল্প সংখ্যক ব্যতীত অন্য সবাই সেই নদীর পানি পান করল, অতঃপর যখন সে ও তার সঙ্গী বিশ্বাস স্থাপনকারীগণ নদী অতিক্রম করে গেল তখন তারা বললঃ জালুত ও তার সেনাবাহিনীর মুকাবিলা করার শক্তি আজ আমাদের নেই; পক্ষান্তরে যারা বিশ্বাস করত যে, তাদেরকে আল্লাহর সাথে সাক্ষাৎ করতে হবে তারা বললঃ আল্লাহর হুকুমে কত ক্ষুদ্র দল কত বৃহৎ দলকে পরাজিত করেছে; বস্তুতঃ ধৈর্যশীলদের সঙ্গী হচ্ছেন আল্লাহ!');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (257,2,250,' এবং যখন তারা জালুত ও তার সেনাবাহিনীর সম্মুখীন হল, বলতে লাগলঃ হে আমাদের রাব্ব! আমাদেরকে পূর্ণ সহিষ্ণুতা দান করুন, আর আমাদের চরণগুলি অটল রাখুন এবং কাফির জাতির উপর আমাদেরকে সাহায্য করুন!');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (258,2,251,' তখন তারা আল্লাহর হুকুমে জালুতের সৈন্যদেরকে পরাজিত করল এবং দাঊদ জালুতকে হত্যা করল। এবং আল্লাহ দাউদকে রাজ্য ও প্রজ্ঞা দান করলেন এবং তাকে ইচ্ছানুযায়ী শিক্ষা দান করলেন; আর যদি আল্লাহ এক দলকে অপর দলের দ্বারা প্রদমিত না করতেন তাহলে নিশ্চয়ই পৃথিবী অশান্তিপূর্ণ হত, কিন্তু আল্লাহ বিশ্ব জগতের প্রতি অনুগ্রহকারী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (259,2,252,' এগুলো আল্লাহর নিদর্শন - তোমার নিকট এগুলি সত্যরূপে উপস্থাপন করেছি এবং নিশ্চয়ই তুমি রাসূলগণের অন্তর্ভুক্ত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (260,2,253,'এই সকল রাসূল, আমি যাদের কারও উপর কেহকে মর্যাদা প্রদান করেছি, তাদের মধ্যে কারও সাথে আল্লাহ কথা বলেছেন এবং কেহকে পদমর্যাদায় সমুন্নত করেছেন, আর মারইয়াম নন্দন ঈসাকে প্রকাশ্য নিদর্শনাবলী দান করেছি এবং তাকে পবিত্র আত্মাযোগে সাহায্য করেছি, আর আল্লাহ ইচ্ছা করলে নাবীগণের পরবর্তী লোকেরা, তাদের নিকট স্পষ্ট প্রমাণপুঞ্জ সমাগত হওয়ার পর পরস্পরের সাথে যুদ্ধ বিগ্রহে লিপ্ত হতনা। কিন্তু তারা পরস্পর মতবিরোধ করেছিল; ফলে তাদের কতক হল মু’মিন, আর কতক হল কাফির। বস্ত্ততঃ আল্লাহ ইচ্ছা করলে তারা পরস্পর যুদ্ধ বিগ্রহে লিপ্ত হতনা, কিন্তু আল্লাহ যা ইচ্ছা করেন তা’ই সম্পন্ন করে থাকেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (261,2,254,' হে বিশ্বাসীগণ! আমি তোমাদেরকে যে জীবনোপকরণ দান করেছি তা হতে সেদিন সমাগত হওয়ার পূর্বে ব্যয় কর যেদিন ক্রয়-বিক্রয়, বন্ধুত্ব ও সুপারিশ নেই, আর অবিশ্বাসীরাই অত্যাচারী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (262,2,255,'আল্লাহ, তিনি ছাড়া কোন (সত্য) ইলাহ নেই, তিনি চিরঞ্জীব, সুপ্রতিষ্ঠিত ধারক। তাঁকে তন্দ্রা ও নিদ্রা স্পর্শ করে না। তাঁর জন্যই আসমানসমূহে যা রয়েছে তা এবং যমীনে যা আছে তা। কে সে, যে তাঁর নিকট সুপারিশ করবে তাঁর অনুমতি ছাড়া? তিনি জানেন যা আছে তাদের সামনে এবং যা আছে তাদের পেছনে। আর তারা তাঁর জ্ঞানের সামান্য পরিমাণও আয়ত্ব করতে পারে না, তবে তিনি যা চান তা ছাড়া। তাঁর কুরসী আসমানসমূহ ও যমীন পরিব্যাপ্ত করে আছে এবং এ দু’টোর সংরক্ষণ তাঁর জন্য বোঝা হয় না। আর তিনি সুউচ্চ, মহান। (আয়াতুল কুরসী)');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (263,2,256,'দীনের ব্যাপারে কোন জবরদস্তি কিংবা বাধ্যবাধকতা নেই। নিশ্চয়ই ভ্রান্তি হতে সুপথ প্রকাশিত হয়েছে। অতএব যে তাগুতকে অবিশ্বাস করে এবং আল্লাহর প্রতি বিশ্বাস স্থাপন করে সে দৃঢ়তর রজ্জুকে আঁকড়ে ধরলো যা কখনও ছিন্ন হবার নয় এবং আল্লাহ শ্রবণকারী, মহাজ্ঞানী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (264,2,257,' আল্লাহই হচ্ছেন মু’মিনদের অভিভাবক। তিনি তাদেরকে অন্ধকার হতে আলোর দিকে নিয়ে যান; আর যারা অবিশ্বাস করেছে তাগুত তাদের পৃষ্ঠপোষক, সে তাদেরকে আলো হতে অন্ধকারের দিকে নিয়ে যায়, তারাই জাহান্নামের অধিবাসী, ওখানে তারা চিরকাল অবস্থান করবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (265,2,258,'তুমি কি তার প্রতি লক্ষ্য করনি যে ইবরাহীমের সাথে তার রাব্ব সম্বন্ধে বিতর্ক করেছিল, যেহেতু আল্লাহ তাকে রাজত্ব প্রদান করেছিলেন। ইবরাহীম বলেছিলঃ আমার রাব্ব তিনিই যিনি জীবিত করেন ও মৃত্যু দান করেন। সে বলেছিলঃ আমিই জীবন ও মৃত্যু দান করি। ইবরাহীম বলেছিলঃ নিশ্চয়ই আল্লাহ সূর্যকে পূর্ব দিক হতে আনয়ন করেন, কিন্তু তুমি ওকে পশ্চিম দিক হতে আনয়ন কর; এতে সেই অবিশ্বাসকারী হতবুদ্ধি হয়েছিল; এবং আল্লাহ অত্যাচারী সম্প্রদায়কে পথ প্রদর্শন করেননা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (266,2,259,' অথবা ঐ ব্যক্তির অনুরূপ যে কোন এক জনপদ অতিক্রম করছিল এবং তা ছিল শূণ্য - নিজ ভিত্তির উপর পতিত। সে বললঃ এই নগরের মৃত্যুর পর আল্লাহ আবার তাকে জীবন দান করবেন কিরূপে? অনন্তর আল্লাহ তাকে একশ বছরের জন্য মৃত্যু দান করলেন, অতঃপর তাকে পুনর্জীবিত করলেন এবং বললেনঃ এ অবস্থায় তুমি কতক্ষণ অতিবাহিত করেছ? সে বললঃ একদিন অথবা একদিনের কিয়দংশ। তিনি বললেনঃ তা নয়, বরং তুমিতো এভাবে একশ বছর ছিলে। তোমার খাদ্য ও পানীয়ের প্রতি লক্ষ্য কর - ওটা বিকৃত হয়নি এবং তোমার গর্দভের প্রতি দৃষ্টিপাত কর; এবং যেহেতু আমি তোমাকে মানবের জন্য নিদর্শন বানাতে চাই; আরও লক্ষ্য কর অস্থিপুঞ্জের দিকে, ওকে কিরূপে আমি সংযুক্ত করি; অতঃপর ওকে মাংসাবৃত করি। অনন্তর যখন ওটা তার নিকট স্পষ্ট হয়ে গেল তখন সে বললঃ আমি জানি, আল্লাহ সকল বিষয়ে সর্বশক্তিমান। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (267,2,260,'এবং যখন ইবরাহীম বলেছিলঃ হে আমার রাব্ব! আপনি কিরূপে মৃতকে জীবিত করেন তা আমাকে প্রদর্শন করুন। তিনি বললেনঃ তাহলে কি তুমি বিশ্বাস করনা? সে বললঃ হ্যাঁ অবশ্যই, কিন্তু তাতে আমার অন্তর পরিতৃপ্ত হবে। তিনি বললেনঃ তাহলে চারটি পাখী গ্রহণ কর, তারপর ওদেরকে টুকরা টুকরা করে মিশ্রিত কর, অনন্তর প্রত্যেক পাহাড়ের উপর ওদের এক এক খন্ড রেখে দাও, অতঃপর ওদেরকে আহবান কর, ওরা তোমার নিকট দৌড়ে আসবে; এবং জেনে রেখ যে, নিশ্চয়ই আল্লাহ পরাক্রান্ত, বিজ্ঞানময়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (268,2,261,'যারা আল্লাহর পথে স্বীয় ধন-সম্পদ ব্যয় করে তাদের উপমা যেমন একটি শস্যবীজ, তা হতে উৎপন্ন হল সাতটি শীষ, প্রত্যেক শীষে (উৎপন্ন হল) এক শত শস্য, এবং আল্লাহ যার জন্য ইচ্ছা করেন বর্ধিত করে দেন; বস্তুতঃ আল্লাহ হচ্ছেন অতি দানশীল, সর্বজ্ঞ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (269,2,262,'যারা আল্লাহর পথে নিজেদের ধন সম্পদ ব্যয় করে, এবং ব্যয় করার পর অনুগ্রহের কথা প্রকাশ করেনা, কষ্টও দেয়না, তাদের জন্য তাদের রবের নিকট রয়েছে পুরস্কার; বস্তুতঃ তাদের কোন ভয় নেই এবং তারা দুশ্চিন্তাগ্রস্তও হবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (270,2,263,'যে দানের পশ্চাতে থাকে ক্লেশ, সেই দান অপেক্ষা উত্তম বাক্য ও ক্ষমাই উৎকৃষ্ট এবং আল্লাহ মহা সম্পদশালী, সহিষ্ণু।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (271,2,264,' হে মু’মিনগণ! কৃপা প্রকাশ ও ক্লেশ দান করে নিজেদের দানগুলি ব্যর্থ করে ফেলনা সেই ব্যক্তির ন্যায় যে নিজের ধন ব্যয় করে লোক দেখানোর জন্য, অথচ আল্লাহ ও আখিরাতে সে বিশ্বাস করেনা। ফলতঃ তার উপমা, যেমন এক বৃহৎ মসৃন প্রস্তর খন্ড যার উপর কিছু মাটি (জমে) আছে, এ অবস্থায় তাতে বর্ষিত হল প্রবল বর্ষা, অতঃপর তা পরিষ্কার হয়ে গেল। তারা যা অর্জন করেছে তন্মধ্য হতে কোন বিষয়েই তারা সুফল পাবেনা এবং আল্লাহ অবিশ্বাসী সম্প্রদায়কে পথ প্রদর্শন করেননা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (272,2,265,' এবং যারা আল্লাহর সন্তুষ্টি সাধন ও স্বীয় জীবনের প্রতিষ্ঠার জন্য ধন সম্পদ ব্যয় করে তাদের উপমা - যেমন উর্বর ভূভাগে অবস্থিত একটি উদ্যান, তাতে প্রবল বৃষ্টিধারা পতিত হয়, ফলে সেই উদ্যান দ্বিগুণ খাদ্যশস্য দান করে; কিন্তু যদি তাতে বৃষ্টিপাত না হয় তাহলে শিশিরই যথেষ্ট এবং তোমরা যা করছ আল্লাহ তা প্রত্যক্ষকারী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (273,2,266,' তোমাদের কারও যদি এমন একটি খেজুর ও আঙ্গুর উদ্যান থাকে যার তলদেশ দিয়ে নদী-নালা প্রবাহিত, সেখানে সর্ব প্রকার ফলের সংস্থান তার রয়েছে, আর সে বার্ধক্যে উপনীত হল, অথচ তার কতকগুলি দুর্বল (অপ্রাপ্ত বয়স্ক) সন্তান-সন্ততি রয়েছে, এ অবস্থায় সেই বাগানে উপস্থিত হল অগ্নিসহ এক বাত্যাবর্ত, আর তা পুড়ে (ভস্মীভূত হয়ে) গেল; তোমরা কেহ এটা পছন্দ করবে কি? এ রূপে আল্লাহ তোমাদের জন্য নিদর্শনাবলী ব্যক্ত করেন, যেন তোমরা চিন্তা-ভাবনা কর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (274,2,267,' হে মু’মিনগণ! তোমরা যা উপার্জন করেছ এবং আমি যা তোমাদের জন্য ভূমি হতে উৎপন্ন করেছি, তা হতে উৎকৃষ্ট বস্ত্ত খরচ কর এবং তা হতে এরূপ নিকৃষ্ট বস্ত্ত ব্যয় করতে মনস্থ করনা যা তোমরা মুদিত চক্ষু ব্যতীত গ্রহণ করনা; এবং তোমরা জেনে রেখ, আল্লাহ মহা সম্পদশালী, প্রশংসিত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (275,2,268,' শাইতান তোমাদেরকে অভাবের ভীতি প্রদর্শন করে এবং তোমাদেরকে অশ্লীলতার আদেশ করে এবং আল্লাহ তোমাদেরকে তাঁর নিকট হতে ক্ষমা ও দয়ার অংগীকার করেন। আল্লাহ হচ্ছেন বিপুল দাতা, সর্বজ্ঞ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (276,2,269,'তিনি যাকে ইচ্ছা প্রজ্ঞা দান করেন এবং যাকে প্রজ্ঞা দান করা হয় সে নিশ্চয়ই প্রচুর কল্যাণ লাভ করে; বস্ত্ততঃ জ্ঞানবান ব্যক্তিগণ ব্যতীত কেহই উপলদ্ধি করতে পারেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (277,2,270,'এবং যে কোন বস্তু তোমরা ব্যয় করনা কেন, অথবা যে কোন প্রতিজ্ঞা (নযর) তোমরা গ্রহণ করনা কেন, আল্লাহ নিশ্চয়ই তা অবগত হন; আর অত্যাচারীদের কোনই সাহায্যকারী নেই।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (278,2,271,'যদি তোমরা প্রকাশ্যভাবে দান কর তাহলে তা উৎকৃষ্ট এবং যদি তোমরা তা গোপন কর ও দরিদ্রদেরকে প্রদান কর তাহলে ওটাও তোমাদের জন্য উত্তম, এবং এর দ্বারা তোমাদের কিছু পাপ (এর কালিমা) বিদূরিত হবে; বস্ত্ততঃ তোমাদের কার্যকলাপ সম্বন্ধে আল্লাহ বিশেষ রূপে খবর রাখেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (279,2,272,'তাদেরকে সুপথে আনার দায়িত্ব তোমার নয়, বরং যাকে ইচ্ছা আল্লাহ তাকে সৎ পথে পরিচালিত করেন এবং তোমরা ধনসম্পদ হতে যা ব্যয় কর বস্তুতঃ তা তোমাদের নিজেদের জন্য, আর একমাত্র আল্লাহর সন্তোষ লাভের চেষ্টা ব্যতীত (অন্য কোন উদ্দেশে) অর্থ ব্যয় করনা; এবং তোমরা হালাল সম্পদ হতে যা ব্যয় করবে তা সম্পূর্ণভাবে পুনঃপ্রাপ্ত হবে, আর তোমাদের প্রতি অন্যায় করা হবেনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (280,2,273,'দান খয়রাত ঐ সব লোকদের জন্য যারা আল্লাহর কাছে আবদ্ধ হয়ে গেছে, জীবিকার সন্ধানে অন্যত্র ঘোরাফিরা করতে সক্ষম নয়। তাদের সাবলিল চলাচলের জন্য অজ্ঞ লোকেরা তাদেরকে অভাবহীন মনে করে, তুমি তাদেরকে তাদের লক্ষণ দ্বারা চিনবে। তারা মানুষের কাছে যাঞ্চা করেনা। এবং তোমরা বৈধ সম্পদ থেকে যা ব্যয় কর সে বিষয়ে আল্লাহ সম্যকরূপে অবগত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (281,2,274,' যারা রাতে ও দিনে, গোপনে ও প্রকাশ্যে নিজেদের ধন-সম্পদ ব্যয় করে তাদের রবের নিকট তাদের পুরস্কার রয়েছে, তাদের কোন ভয় নেই এবং তারা দুঃখিত হবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (282,2,275,'যারা সুদ ভক্ষণ করে তারা শাইতানের স্পর্শে মোহাভিভূত ব্যক্তির অনুরূপ কিয়ামাত দিবসে দন্ডায়মান হবে; এর কারণ এই যে, তারা বলে, ব্যবসা সুদের অনুরূপ বৈ তো নয়; অথচ আল্লাহ তা‘আলা ব্যবসাকে হালাল করেছেন এবং সুদকে হারাম করেছেন; অতঃপর যার নিকট তার রবের পক্ষ হতে উপদেশ সমাগত হয়, ফলে সে নিবৃত্ত হয়; সুতরাং যা অতীত হয়েছে তার কৃতকর্ম আল্লাহর উপর নির্ভর; এবং যারা পুনরায় সুদ গ্রহণ করবে তারাই হচ্ছে জাহান্নামের অধিবাসী, সেখানেই চিরকাল অবস্থান করবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (283,2,276,'আল্লাহ সুদকে ক্ষয় করেন এবং দানকে বর্ধিত করেন, বস্তুতঃ আল্লাহ অতি কৃতঘ্ন পাপাচারীদেরকে ভালবাসেননা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (284,2,277,' নিশ্চয়ই যারা ঈমান আনে এবং সৎ কার্যাবলী সম্পাদন করে, সালাতকে প্রতিষ্ঠিত রাখে ও যাকাত প্রদান করে, তাদের জন্য তাদের রবের নিকট পুরষ্কার রয়েছে; এবং তাদের কোনো আশংকা নেই এবং তারা দুঃখিত হবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (285,2,278,' হে বিশ্বাস স্থাপনকারীগণ! আল্লাহকে ভয় কর এবং যদি তোমরা মু’মিন হও তাহলে সুদের মধ্যে যা অবশিষ্ট রয়েছে তা বর্জন কর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (286,2,279,' কিন্তু যদি না কর তাহলে আল্লাহ ও তাঁর রাসূলের সাথে যুদ্ধ করতে প্রস্ত্তত হয়ে যাও। আর যদি তোমরা ক্ষমা প্রার্থনা কর তাহলে তোমাদের মূলধন পেয়ে যাবে, তোমরা অত্যাচার করবেনা এবং তোমরাও অত্যাচারিত হবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (287,2,280,' আর যদি সে অভাবগ্রস্ত হয় তাহলে স্বচ্ছলতার প্রতীক্ষা করবে এবং যদি তোমরা বুঝে থাক তাহলে তোমাদের জন্য দান করাই উত্তম।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (288,2,281,' আর তোমরা সেই দিনের ভয় কর যেদিন তোমরা আল্লাহর কাছে  প্রত্যাবর্তিত হবে, তখন যে যা অর্জন করেছে তা সম্পূর্ণ রূপে প্রদত্ত হবে এবং তারা অত্যাচারিত হবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (289,2,282,' হে বিশ্বাস স্থাপনকারীগণ! যখন তোমরা কোন নির্দিষ্ট সময়ের জন্য ঋণের আদান প্রদান করবে তখন তা লিখে নিবে; আর কোন একজন লেখক যেন ন্যায্যভাবে তোমাদের মধ্যে (ঐ আদান প্রদানের দলীল) লিখে দেয়, আর কোন লেখক যেন (দলীল) লিখে দিতে অস্বীকার না করে, আল্লাহ তাকে যেরূপ শিক্ষা দিয়েছেন, তার উচিত তা লিখে দেয়া, এবং ঋণ গ্রহিতা যেন লেখার বিষয় বলে দেয় এবং তার উচিত স্বীয় রাব্ব আল্লাহকে ভয় করা এবং ওর মধ্যে কিছুমাত্র ব্যতিক্রম না করা; অতঃপর ঋণ গ্রহীতা যদি নির্বোধ বা অযোগ্য অথবা লিখিয়ে নিতে অসমর্থ হয় তাহলে তার অভিভাবকরা ন্যায় সঙ্গতভাবে লিখিয়ে নিবে এবং তোমাদের মধ্যে দু’জন পুরুষ সাক্ষীকে সাক্ষী করবে; কিন্তু যদি দু’জন পুরুষ না পাওয়া যায় তাহলে সাক্ষীগণের মধ্যে তোমরা একজন পুরুষ ও দু’জন নারী মনোনীত করবে, যদি নারীদ্বয়ের একজন ভুলে যায় তাহলে একজন অপর জনকে স্মরণ করিয়ে দিবে; এবং যখন আহবান করা হয় তখন সাক্ষীগণের অস্বীকার না করা উচিত, এবং ক্ষুদ্র অথবা বৃহৎ বিষয়ের নির্দিষ্ট সময় লিখে দিতে তোমরা অবহেলা করনা, এটা আল্লাহর নিকট অতি সঙ্গত এবং সাক্ষ্যের জন্য এটাই দৃঢ়তর ও সন্দেহে পতিত না হওয়ার নিকটতর; কিন্তু যদি তোমরা কারবারে পরস্পর হাতে হাতে আদান প্রদান কর তাহলে তা লিপিবদ্ধ না করলে তোমাদের পক্ষে দোষ নেই; কিন্তু বাণিজ্যিক লেনদেনের জন্য সাক্ষী রেখ, যেন লেখক কিংবা সাক্ষী ক্ষতিগ্রস্ত না হয়। যদি এরূপ কর (ক্ষতিগ্রস্ত) তাহলে তা তোমাদের পক্ষে পাপের বিষয়। আল্লাহকে ভয় কর। আল্লাহই তোমাদেরকে শিক্ষা প্রদান করেন এবং আল্লাহ সর্ব বিষয়ে মহাজ্ঞানী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (290,2,283,' এবং যদি তোমরা প্রবাসে থাক ও লেখক না পাও তাহলে বন্ধকী বস্ত্ত নিজের দখলে রাখ। অতঃপর কেহ যদি কেহকে বিশ্বাস করে তাহলে যাকে বিশ্বাস করা হয়েছে তার পক্ষে গচ্ছিত দ্রব্য প্রত্যর্পণ করা উচিত এবং স্বীয় রাব্ব আল্লাহকে ভয় করা উচিৎ। সাক্ষ্য গোপন করনা; এবং যে কেহ তা গোপন করবে, নিশ্চয়ই তার মন পাপাচারী; বস্তুতঃ আল্লাহ তোমাদের কার্যকলাপ সম্বন্ধে সম্যক জ্ঞাত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (291,2,284,' নভোমন্ডল ও ভূমন্ডলে যা কিছু রয়েছে সবই আল্লাহর এবং তোমাদের অন্তরে যা রয়েছে তা প্রকাশ কর অথবা গোপন রাখ, আল্লাহ তার হিসাব তোমাদের নিকট হতে গ্রহণ করবেন; অতঃপর তিনি যাকে ইচ্ছা ক্ষমা করবেন, যাকে ইচ্ছা শাস্তি দিবেন; এবং আল্লাহ সর্ব বিষয়োপরি শক্তিমান।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (292,2,285,' রাসূল তার রাব্ব হতে তার প্রতি যা অবতীর্ণ হয়েছে তা বিশ্বাস করে এবং মু’মিনগণও (বিশ্বাস করে); তারা সবাই আল্লাহকে, তাঁর মালাইকাকে, তাঁর গ্রন্থসমূহকে এবং তাঁর রাসূলগণকে বিশ্বাস করে; (তারা বলে) আমরা তাঁর রাসূলগণের মধ্যে কেহকেও পার্থক্য করিনা। এবং তারা বলে, আমরা শুনলাম এবং স্বীকার করলাম; হে আমাদের রাব্ব! আমরা আপনারই নিকট ক্ষমা প্রার্থনা করছি এবং আপনারই দিকে শেষ প্রত্যাবর্তন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (293,2,286,' কোন ব্যক্তিকেই আল্লাহ তার সাধ্যের অতিরিক্ত কর্তব্য পালনে বাধ্য করেননা; সে যা উপার্জন করেছে তা তারই জন্য এবং যা সে অর্জন করেছে তা তারই উপর বর্তাবে। হে আমাদের রাব্ব! আমরা যদি ভুলে যাই অথবা ভুল করি সেজন্য আমাদেরকে অপরাধী করবেননা। হে আমাদের রাব্ব! আমাদের পূর্ববর্তীগণের উপর যেরূপ গুরুভার অর্পণ করেছিলেন আমাদের উপর তদ্রুপ ভার অর্পণ করবেননা। হে আমাদের রাব্ব! যা আমাদের শক্তির বাইরে ঐরূপ ভার বহনে আমাদেরকে বাধ্য করবেননা, এবং আমাদের পাপ মোচন করুন ও আমাদেরকে ক্ষমা করুন, আমাদেরকে দয়া করুন, আপনিই আমাদের আশ্রয়দাতা! অতএব কাফির সম্প্রদায়ের বিরুদ্ধে আমাদেরকে জয়যুক্ত করুন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (294,3,1,' আলিফ, লাম, মীম।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (295,3,2,'আল্লাহ ছাড়া কোনই ইলাহ (উপাস্য) নেই, তিনি চিরঞ্জীব ও নিত্য বিরাজমান।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (296,3,3,'তিনি সত্যসহ তোমার প্রতি গ্রন্থ অবতীর্ণ করেছেন, যা পূর্ববর্তী বিষয়ের সত্যতা প্রতিপাদনকারী। এবং এর পূর্বে তাওরাত ও ইঞ্জীল অবতীর্ণ করেছিলেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (297,3,4,' মানবমন্ডলীর জন্য সৎপথ প্রদর্শনের জন্য এবং তিনিই ফুরকান অবতীর্ণ করেছেন। যারা আল্লাহর নিদর্শনাবলীর প্রতি অবিশ্বাস করে নিশ্চয়ই তাদের জন্য কঠোর শাস্তি রয়েছে এবং আল্লাহ পরাক্রান্ত, প্রতিশোধ গ্রহণকারী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (298,3,5,' নিশ্চয়ই আল্লাহর নিকট ভূমন্ডল ও নভোমন্ডলের কোন বিষয়ই লুকায়িত নেই।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (299,3,6,' তিনিই স্বীয় ইচ্ছানুযায়ী জরায়ুর মধ্যে তোমাদের আকৃতি গঠন করেছেন। তিনি ব্যতীত অন্য কোনই উপাস্য নেই, তিনি পরাক্রান্তশালী, বিজ্ঞানময় ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (300,3,7,' তিনিই তোমার প্রতি গ্রন্থ অবতীর্ণ করেছেন যাতে সুস্পষ্ট আয়াতসমূহ রয়েছে - ওগুলি গ্রন্থের জননী স্বরূপ এবং অন্যান্য আয়াতসমূহ অস্পষ্ট; অতএব যাদের অন্তরে বক্রতা রয়েছে, ফলতঃ তারাই অশান্তি সৃষ্টি ও ব্যাখ্যা বিশ্লেষণের উদ্দেশে অস্পষ্টের অনুসরণ করে; এবং আল্লাহ ব্যতীত ওর অর্থ কেহই অবগত নয়, আর যারা জ্ঞানে সুপ্রতিষ্ঠিত তারা বলেঃ আমরা ওতে বিশ্বাস করি, সমস্তই আমাদের রবের নিকট হতে সমাগত এবং জ্ঞানবান ব্যতীত কেহই উপদেশ গ্রহণ করেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (301,3,8,'হে আমাদের রাব্ব! আমাদেরকে পথ প্রদর্শনের পর আমাদের অন্তরসমূহ বক্র করবেন না এবং আমাদেরকে আপনার নিকট হতে করুণা প্রদান করুন, নিশ্চয়ই আপনি প্রভূত প্রদানকারী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (302,3,9,' হে আমাদের রাব্ব! নিশ্চয়ই আপনি সকল মানুষকে সমবেতকারী, ঐ দিনে মোটেই সন্দেহ নেই, নিশ্চয়ই আল্লাহ প্রতিজ্ঞা ভঙ্গকারী নন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (303,3,10,' নিশ্চয়ই যারা অবিশ্বাস করেছে, তাদের ধন-সম্পদ ও তাদের সন্তান-সন্ততি আল্লাহর নিকট কোন বিষয়েই ফলপ্রদ হবেনা এবং তারাই জাহান্নামের ইন্ধন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (304,3,11,' ফির‘আউন সম্প্রদায় এবং তাদের পূর্ববর্তীদের প্রকৃতির ন্যায় তারা আমার আয়াতসমূহের প্রতি অসত্যারোপ করেছে, এই হেতু আল্লাহ তাদের ধৃত করেছেন এবং আল্লাহ কঠোর শাস্তিদাতা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (305,3,12,' যারা অবিশ্বাস করেছে, তুমি তাদেরকে বলঃ অচিরেই তোমরা পরাভূত হবে এবং তোমরা জাহান্নামের দিকে একত্রিত হবে এবং ওটা নিকৃষ্টতর স্থান।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (306,3,13,' নিশ্চয়ই তোমাদের জন্য দু’টি দলের পরস্পর সম্মুখীন হওয়ার মধ্যে মহান নমুনা রয়েছে, তাদের একদল আল্লাহর পথে সংগ্রাম করছিল এবং অপর দল অবিশ্বাসী ছিল; তারা প্রত্যক্ষ দৃষ্টিতে মুসলিমদেরকে দ্বিগুণ দেখেছিল এবং আল্লাহ যাকে ইচ্ছা তদ্বীয় সাহায্য দানে শক্তিশালী করেন, নিশ্চয়ই এর মধ্যে চক্ষুস্মানদের জন্য উপদেশ রয়েছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (307,3,14,' মানবমন্ডলীকে রমণী, সন্তান-সন্ততি, পুঞ্জীকৃত স্বর্ণ ও রৌপ্যভান্ডার, সুশিক্ষিত অশ্ব ও পালিত পশু এবং শস্য ক্ষেত্রের আকর্ষণীয় বস্ত্ত দ্বারা সুশোভিত করা হয়েছে, এটা পার্থিব জীবনের সম্পদ এবং আল্লাহর নিকট রয়েছে শ্রেষ্ঠতম অবস্থান।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (308,3,15,' তুমি বলঃ আমি কি তোমাদেরকে এটা অপেক্ষাও উত্তম বিষয়ের সংবাদ দিব? যারা ধর্মভীরু তাদের জন্য তাদের রবের নিকট জান্নাত রয়েছে যার তলদেশে স্রোতস্বিনীসমূহ প্রবাহিতা, তন্মধ্যে তারা সদা অবস্থান করবে এবং সেখানে শুদ্ধা সহধর্মিনীগণ ও আল্লাহর প্রসন্নতা রয়েছে এবং আল্লাহ বান্দাদের প্রতি লক্ষ্যকারী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (309,3,16,'যারা বলেঃ হে আমাদের রাব্ব! নিশ্চয়ই আমরা বিশ্বাস স্থাপন করেছি, অতএব আমাদের অপরাধ ক্ষমা করুন এবং জাহান্নামের আগুনের শাস্তি হতে আমাদেরকে বাঁচান।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (310,3,17,'যারা সহিষ্ণু, সত্যপরায়ণ, সেবানুগত, দানশীল এবং ঊষাকালে ক্ষমাপ্রার্থী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (311,3,18,' আল্লাহ সাক্ষ্য প্রদান করেন যে, নিশ্চয়ই তিনি ব্যতীত কেহ ইলাহ (উপাস্য) নেই এবং মালাইকা/ফেরেশতা, জ্ঞানবানগণ ও সুবিচারে আস্থা স্থাপনকারীগণও সাক্ষ্য দেয় যে, এই মহা পরাক্রান্ত বিজ্ঞানময় ব্যতীত আর কোনই উপাস্য নেই।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (312,3,19,'নিশ্চয়ই ইসলামই আল্লাহর নিকট একমাত্র ধর্ম। এবং যাদেরকে গ্রন্থ প্রদত্ত হয়েছে তাদের জ্ঞান আসার পরও তারা মতবিরোধে লিপ্ত রয়েছে শুধুমাত্র পরস্পর বিদ্বেষ বশতঃ; এবং যে আল্লাহর নিদর্শনসমূহ অস্বীকার করে নিশ্চয়ই আল্লাহ সত্ত্বর হিসাব গ্রহণকারী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (313,3,20,'অতঃপর যদি তারা তোমার সাথে কলহ করে তাহলে তুমি বলঃ আমি ও আমার অনুসারীগণ আল্লাহর উদ্দেশ্যে আত্মসমর্পণ করেছি এবং যাদেরকে গ্রন্থ প্রদত্ত হয়েছে ও যারা নিরক্ষর তাদেরকে বলঃ তোমরাও কি আত্মসমর্পণ করেছ? অতঃপর যদি তারা ‌আত্মসমর্পণ করে তাহলে নিশ্চয়ই তারা সুপথ পেয়ে যাবে, আর যদি ফিরে যায় তাহলে তোমার উপর দায়িত্ব হচ্ছে প্রচার করা মাত্র এবং আল্লাহ বান্দাদের প্রতি লক্ষ্যকারী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (314,3,21,' নিশ্চয়ই যারা আল্লাহর নিদর্শনসমূহ অবিশ্বাস করে ও অন্যায়ভাবে নাবীদেরকে হত্যা করে এবং হত্যা করে তাদের যারা মানবমন্ডলীর মধ্যে ন্যায়ের আদেশকারী, তাদেরকে যন্ত্রণাদায়ক শাস্তির সুসংবাদ দাও।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (315,3,22,' এদের কৃতকর্মসমূহ ইহকাল ও আখিরাতে ব্যর্থ হবে এবং তাদের জন্য কেহ সাহায্যকারী নেই।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (316,3,23,' তুমি কি তাদের প্রতি লক্ষ্য করনি যাদেরকে গ্রন্থের একাংশ প্রদত্ত হয়েছে? তাদেরকে গ্রন্থের দিকে আহবান করা হয়েছে যেন এটা তাদের মধ্যে মীমাংসা করে, অতঃপর তাদের একদল প্রতিগমন করল এবং তারা প্রতিগমনকারী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (317,3,24,' এটা এ জন্য যে, তারা বলেঃ নির্দিষ্ট সংখ্যক দিন ব্যতীত জাহান্নামের আগুন আমাদেরকে স্পর্শ করবেনা; এবং তারা যা স্থির করেছে, তাদের ধর্ম বিষয়ে ওটা তাদেরকে প্রতারিত করেছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (318,3,25,' অতঃপর যে দিন আমি তাদেরকে একত্রিত করব, যাতে কোন সন্দেহ নেই, তখন তাদের কি দশা হবে? এবং প্রত্যেক ব্যক্তি যা অর্জন করেছে তা সম্যক রূপে প্রদত্ত হবে এবং তারা অত্যাচারিত হবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (319,3,26,' তুমি বলঃ হে রাজ্যাধিপতি আল্লাহ! আপনি যাকে ইচ্ছা রাজত্ব দান করেন এবং যার নিকট হতে ইচ্ছা রাজত্ব ছিনিয়ে নেন; যাকে ইচ্ছা সম্মান দান করেন এবং যাকে ইচ্ছা লাঞ্ছিত করেন; আপনারই হাতে রয়েছে কল্যাণ। নিশ্চয়ই আপনিই সর্ব বিষয়ে ক্ষমতাবান। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (320,3,27,' আপনি রাতকে দিনে পরিবর্তিত করেন এবং দিনকে রাতে পরিণত করেন, এবং জীবিতকে মৃত হতে নির্গত করেন ও মৃতকে জীবিত হতে বহির্গত করেন এবং আপনি যাকে ইচ্ছা অপরিমিত জীবিকা দান করেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (321,3,28,'মু’মিনগণ যেন মু’মিনগণকে ছেড়ে কাফিরদেরকে বন্ধু রূপে গ্রহণ না করে, এবং তাদের আশংকা হতে আত্মরক্ষা ব্যতীত যে এরূপ করে সে আল্লাহর নিকট সম্পর্কহীন; আর আল্লাহ তোমাদেরকে স্বীয় পবিত্র অস্তিত্বের ভয় প্রদর্শন করছেন এবং আল্লাহরই দিকে ফিরে যেতে হবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (322,3,29,'তুমি বলঃ তোমাদের অন্তরসমূহে যা রয়েছে তা তোমরা গোপন কর অথবা প্রকাশ কর আল্লাহ তা অবগত আছেন এবং নভোমন্ডলে ও ভূমন্ডলে যা কিছু রয়েছে আল্লাহ তা জ্ঞাত আছেন; এবং আল্লাহ সর্ব বিষয়ে শক্তিমান।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (323,3,30,' সেদিন প্রত্যেক ব্যক্তি সৎকাজ হতে যা করেছে ও দুস্কর্ম হতে যা করেছে তা দেখতে পাবে; তখন সে ইচ্ছা করবে - যদি তার মধ্যে এবং ঐ দুস্কর্মের মধ্যে সুদূর ব্যবধান হত! আল্লাহ তোমাদেরকে স্বীয় পবিত্র অস্তিত্বের ভয় প্রদর্শন করছেন এবং আল্লাহ স্বীয় বান্দাদের প্রতি স্নেহশীল। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (324,3,31,' তুমি বলঃ যদি তোমরা আল্লাহকে ভালবাস তাহলে আমার অনুসরণ কর, যাতে আল্লাহ তোমাদেরকে ভালবাসেন ও তোমাদের অপরাধসমূহ ক্ষমা করেন; এবং আল্লাহ ক্ষমাশীল, করুণাময়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (325,3,32,'তুমি বলঃ তোমরা আল্লাহ ও রাসূলের অনুসরণ কর; কিন্তু যদি তারা ফিরে যায় তাহলে নিশ্চয়ই আল্লাহ অবিশ্বাসীদেরকে ভালবাসেন না। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (326,3,33,'নিশ্চয়ই আল্লাহ আদম ও নূহকে এবং ইবরাহীম ও ইমরানের সন্তানগণকে বিশ্ব জগতের উপর মনোনীত করেছেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (327,3,34,' তারা একে অপরের বংশধর এবং আল্লাহ শ্রবণকারী, মহাজ্ঞানী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (328,3,35,' যখন ইমরান-পত্নী নিবেদন করল, হে আমার রাব্ব! নিশ্চয়ই আমার গর্ভে যা রয়েছে তা আমি মুক্ত করে আপনার উদ্দেশে উৎসর্গ করলাম, সুতরাং আপনি আমা হতে তা গ্রহণ করুন, নিশ্চয়ই আপনি শ্রবণকারী, মহাজ্ঞানী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (329,3,36,' অনন্তর যখন সে তা প্রসব করল তখন বললঃ হে আমার রাব্ব! আমিতো কন্যা প্রসব করেছি; এবং সে যা প্রসব করেছে তা আল্লাহ সম্যক অবগত আছেন, এবং কোনো পুত্র এই কন্যার সমকক্ষ নয়! আর আমি কন্যার নাম রাখলাম ‘মারইয়াম’ এবং আমি তাকে ও তার সন্তানগণকে বিতাড়িত শাইতান হতে আপনার আশ্রয়ে সমর্পন করলাম।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (330,3,37,' অনন্তর তার রাব্ব তাকে উত্তম রূপে গ্রহণ করলেন এবং তাকে উত্তম প্রবৃদ্ধি দান করলেন এবং যাকারিয়াকে তার ভারার্পন করলেন; যখনই যাকারিয়া তার নিকট উক্ত প্রকোষ্ঠে প্রবেশ করত তখন তার নিকট খাদ্য সম্ভার দেখতে পেত; সে বলতঃ হে মারইয়াম! এটা কোথা হতে প্রাপ্ত হলে? সে বলতঃ এটা আল্লাহর নিকট হতে, নিশ্চয়ই আল্লাহ যাকে ইচ্ছা অপরিমিত জীবিকা দান করেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (331,3,38,' তখন যাকারিয়া তার রবের নিকট প্রার্থনা করেছিল, সে বলেছিলঃ হে আমার রাব্ব! আমাকে আপনার নিকট হতে পবিত্র সন্তান প্রদান করুন, নিশ্চয়ই আপনি প্রার্থনা শ্রবণকারী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (332,3,39,' অতঃপর যখন সে মেহরাবের মধ্যে দাঁড়িয়ে প্রার্থনা করছিল তখন মালাইকা/ফেরেশতাগণ তাকে সম্বোধন করে বলেছিলঃ নিশ্চয়ই আল্লাহ তোমাকে ইয়াহইয়া সম্বন্ধে সুসংবাদ দিচ্ছেন - তার অবস্থা এই হবে যে, সে আল্লাহর একটি বাক্যের সত্যতা প্রকাশকারী হবে, নেতা হবে, স্বীয় প্রবৃত্তিকে দমনকারী হবে এবং সৎ কর্মশীলগণের মধ্যে নাবী হবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (333,3,40,' সে বলেছিলঃ হে আমার রাব্ব! কিরূপে আমার পুত্র হবে? নিশ্চয়ই আমার বার্ধক্য উপস্থিত হয়েছে এবং আমার স্ত্রী বন্ধ্যা; তিনি বললেনঃ এইরূপে আল্লাহ যা ইচ্ছা করেন তা’ই করে থাকেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (334,3,41,' সে বলেছিলঃ হে আমার রাব্ব! আমার জন্য কোন নিদর্শন নির্দিষ্ট করুন; তিনি বললেনঃ তোমার নিদর্শন এই যে, তুমি তিন দিন ইঙ্গিত ব্যতীত লোকের সাথে কথা বলতে পারবেনা; এবং স্বীয় রাব্বকে বেশী বেশী স্মরণ কর এবং সন্ধ্যায় ও প্রভাতে তাঁর পবিত্রতা ও মহিমা ঘোষণা কর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (335,3,42,' এবং যখন মালাক/ফেরেশতা বলেছিলঃ ওহে মারইয়াম! নিশ্চয়ই আল্লাহ তোমাকে মনোনীত করেছেন ও তোমাকে পবিত্র করেছেন এবং বিশ্ব জগতের নারীগণের উপর তোমাকে মনোনীত করেছেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (336,3,43,' হে মারইয়াম! তোমার রবের ইবাদাত কর এবং সাজদাহ কর ও রুকুকারীগণের সাথে রুকু কর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (337,3,44,' এটা সেই অদৃশ্য বিষয়ক সংবাদ যা আমি তোমার প্রতি প্রত্যাদেশ করছি; এবং যখন তারা স্বীয় লেখনীসমূহ নিক্ষেপ করছিল যে, তাদের মধ্যে কে মারইয়ামের প্রতিপালন করবে তখন তুমি তাদের নিকট ছিলেনা; এবং যখন তারা কলহ করছিল তখনও তুমি তাদের নিকট ছিলেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (338,3,45,' যখন মালাইকা/ফেরেশতারা বলেছিলঃ হে মারইয়াম! নিশ্চয়ই আল্লাহ তাঁর নিকট হতে একটি বাক্য দ্বারা তোমাকে সুসংবাদ দিচ্ছেন নন্দন ঈসা মসীহ - সে ইহলোক ও পরলোকে সম্মানিত এবং সান্নিধ্য প্রাপ্তগণের অন্তর্ভুক্ত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (339,3,46,' আর সে দোলনা হতে ও পরিণত বয়সে লোকের সাথে কথা বলবে এবং সে পুণ্যবানগণের অন্তর্ভুক্ত হবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (340,3,47,'সে বলেছিলঃ হে আমার রাব্ব! কিরূপে আমার পুত্র হবে? কোন পুরুষ মানুষতো আমাকে স্পর্শ করেনি; তিনি বললেনঃ এরূপে আল্লাহ যা ইচ্ছা করেন, সৃষ্টি করে থাকেন, যখন তিনি কোন কাজের মনস্থ করেন তখন তিনি ওকে ‘হও’ বলেন, ফলতঃ তাতেই হয়ে যায়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (341,3,48,' তিনি তাকে গ্রন্থ, বিজ্ঞান এবং তাওরাত ও ইঞ্জীল শিক্ষা দিবেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (342,3,49,' আর তাকে ইসরাঈল বংশীয়গণের জন্য রাসূল করবেন। নিশ্চয়ই আমি (ঈসা) তোমাদের রবের নিকট হতে নিদর্শনসহ তোমাদের নিকট আগমন করেছি; নিশ্চয়ই আমি তোমাদের জন্য মাটি হতে পাখির আকার গঠন করব, অতঃপর ওর মধ্যে ফুৎকার দিব, অনন্তর আল্লাহর আদেশে ওটা পাখি হয়ে যাবে, এবং জন্মান্ধকে ও কুষ্ঠ রোগীকে নিরাময় করি এবং আল্লাহর আদেশে মৃতকে জীবিত করি এবং তোমরা যা আহার কর ও তোমরা যা স্বীয় গৃহের মধ্যে সংগ্রহ করে রাখ তদ্বিষয়ে সংবাদ দিচ্ছি - যদি তোমরা বিশ্বাসী হও তাহলে নিশ্চয়ই এতে তোমাদের জন্য নিদর্শন রয়েছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (343,3,50,' এবং আমি এসেছি আমার পূর্বে তাওরাতে যা ছিল তার সত্যতা প্রদান করতে এবং তোমাদের জন্য যা অবৈধ হয়েছে তার কতিপয় তোমাদের জন্য বৈধ করতে, আর তোমাদের রবের নিকট হতে তোমাদের জন্য নিদর্শন নিয়ে; অতএব আল্লাহকে ভয় কর ও আমার অনুগত হও।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (344,3,51,' নিশ্চয়ই আল্লাহ আমার রাব্ব এবং তোমাদের রাব্ব; অতএব তাঁর ইবাদাত কর - এটাই সরল পথ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (345,3,52,' অনন্তর যখন ঈসা তাদের মধ্যে প্রত্যাখ্যান প্রত্যক্ষ করল তখন সে বললঃ আল্লাহর উদ্দেশে কে আমার সাহায্যকারী হবে? হাওয়ারিগণ বললঃ আমরাই আল্লাহর সাহায্যকারী, আমরা আল্লাহর প্রতি বিশ্বাস স্থাপন করেছি, এবং সাক্ষী থেকো যে, আমরা আত্মসমর্পণকারী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (346,3,53,' হে আমাদের রাব্ব! আপনি যা অবতীর্ণ করেছেন, আমরা তা বিশ্বাস করি এবং আমরা রাসূলের অনুসরণ করছি, অতএব সাক্ষীগণের সাথে আমাদেরকে লিপিবদ্ধ করুন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (347,3,54,' আর তারা (কাফিরেরা) ষড়যন্ত্র করেছিল ও আল্লাহও কৌশল করলেন এবং আল্লাহ শ্রেষ্ঠতম কৌশলী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (348,3,55,' যখন আল্লাহ বললেনঃ হে ঈসা! নিশ্চয়ই আমি তোমাকে আমার দিকে প্রতিগ্রহণ করব এবং তোমাকে উত্তোলন করব এবং অবিশ্বাসকারীদের হতে তোমাকে পবিত্র করব, আর যারা অবিশ্বাস করেছে তাদের উপর তোমার অনুসারীগণকে উত্থান দিন পর্যন্ত সমুন্নত করব; অনন্তর আমারই দিকে তোমাদের প্রত্যাবর্তন; অতঃপর তোমাদের মধ্যে যে বিষয়ে মতভেদ ছিল তার মীমাংসা করব।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (349,3,56,' অনন্তর যারা কুফরী করেছে (অবিশ্বাসী হয়েছে), বস্তুতঃ তাদেরকে ইহকাল ও আখিরাতে কঠোর শাস্তি প্রদান করব এবং তাদের জন্য কেহ সাহায্যকারী নেই।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (350,3,57,' আর যারা বিশ্বাস স্থাপন করেছে ও সৎকাজ করেছে ফলতঃ তিনি তাদেরকে পূর্ণ প্রতিদান প্রদান করবেন এবং আল্লাহ অত্যাচারীদেরকে ভালবাসেননা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (351,3,58,' আমি তোমার প্রতি বিজ্ঞানময় বর্ণনা ও নিদর্শনাবলী হতে এটা আবৃত্তি করছি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (352,3,59,' নিশ্চয়ই আল্লাহর নিকট ঈসার দৃষ্টান্ত আদমের অনুরূপ; তিনি তাকে মাটি দ্বারা সৃষ্টি করলেন, অতঃপর বললেন হও, ফলতঃ তাতেই হয়ে গেল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (353,3,60,' এই বাস্তব ঘটনা তোমার রবের পক্ষ হতে; সুতরাং তুমি সংশয়ীদের অন্তর্ভুক্ত হয়োনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (354,3,61,' অতঃপর তোমার নিকট যে জ্ঞান এসেছে, তারপরে তদ্বিষয়ে যে তোমার সাথে কলহ করে, তুমি বলঃ এসো, আমরা আমাদের সন্তানগণ ও তোমাদের সন্তানগণকে, আমাদের নারীগণ ও তোমাদের নারীগণকে এবং স্বয়ং আমাদের ও তোমাদেরকে আহবান করি। অতঃপর প্রার্থনা করি যে, অসত্যবাদীদের উপর আল্লাহর অভিসম্পাত বর্ষিত হোক।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (355,3,62,' নিশ্চয়ই এটাই সত্য বিবরণ এবং আল্লাহ ব্যতীত অন্য কোন ইলাহ (উপাস্য) নেই, নিশ্চয়ই সেই আল্লাহ বিজ্ঞানময়, মহাপরাক্রান্ত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (356,3,63,' অতঃপর যদি তারা মুখ ফিরিয়ে নেয় তাহলে নিশ্চয়ই আল্লাহ দুস্কার্যকারীদের জ্ঞাত আছেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (357,3,64,'তুমি বলঃ হে আহলে কিতাব! আমাদের মধ্যে ও তোমাদের মধ্যে যে বাক্যে সুসাদৃশ্য রয়েছে তার দিকে এসো, যেন আমরা আল্লাহ ব্যতীত কারও ইবাদাত না করি এবং তাঁর সাথে কোন অংশী স্থির না করি এবং আল্লাহকে পরিত্যাগ করে আমরা পরস্পর কেহকে রাব্ব রূপে গ্রহণ না করি। অতঃপর যদি তারা ফিরে যায় তাহলে বলঃ সাক্ষী থেকো যে, আমরা মুসলিম (আল্লাহর নিকট আত্মসমপর্নকারী)।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (358,3,65,' হে আহলে কিতাব! তোমরা ইবরাহীম সম্বন্ধে কেন বির্তক করছ? অথচ তার পরে ব্যতীত তাওরাত ও ইঞ্জীল অবতীর্ণ হয়নি - তবুও কি তোমরা বুঝছনা?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (359,3,66,' হ্যাঁ, তোমরা এমন যে, যে বিষয়ে তোমাদের জ্ঞান ছিল তা নিয়েও তোমরা কলহ করেছিলে, কিন্তু যদ্বিষয়ে তোমাদের কোনই জ্ঞান নেই তা নিয়ে তোমরা কেন কলহ করছ এবং আল্লাহ জ্ঞাত আছেন ও তোমরা অবগত নও।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (360,3,67,'ইবরাহীম ইয়াহুদী ছিলনা এবং খৃষ্টানও ছিলনা, বরং সে সুদৃঢ় মুসলিম ছিল এবং সে মুশরিকদের (অংশীবাদী) অন্তর্ভুক্ত ছিলনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (361,3,68,' নিঃসন্দেহে ঐ সব লোক ইবরাহীমের নিকটতম যারা তার অনুসরণ করেছে এবং এই নাবী এবং (তাঁর সাথের) মু’মিনগণ; এবং আল্লাহ বিশ্বাসীগণের অভিভাবক।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (362,3,69,' এবং কিতাবীদের মধ্যে এক দলের বাসনা যে, তোমাদেরকে পথভ্রান্ত করে; কিন্তু তারা নিজেদের ব্যতীত অন্য কেহকে বিপথগামী করেনা এবং তারা তা বুঝছেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (363,3,70,' হে আহলে কিতাব! তোমরা কেন আল্লাহর নিদর্শনসমূহের প্রতি অবিশ্বাস করছো? অথচ তোমরাই ওর সাক্ষী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (364,3,71,' হে কিতাবীরা! তোমরা কেন সত্যের সঙ্গে মিথ্যাকে মিলিয়ে নিচ্ছ এবং সত্যকে গোপন করছ? অথচ তোমরাতো অবগত আছ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (365,3,72,' আর আহলে কিতাবের মধ্যে একদল এটাই বলে যে, বিশ্বাস স্থাপনকারীদের প্রতি যা অবতীর্ণ হয়েছে তৎপ্রতি পূর্বাহ্নে বিশ্বাস স্থাপন কর এবং অপরাহ্নে তা অস্বীকার কর, তাহলে তারা ফিরে যাবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (366,3,73,' আর যারা তোমাদের ধর্মের অনুসরণ করে তারা ব্যতীত অন্যদের বিশ্বাস করনা। তুমি তাদেরকে বলঃ আল্লাহর পথই একমাত্র সুপথ। এই আহলে কিতাবীরা বিশ্বাস করেনা যে, তাদের পরিবর্তে অন্য কারও উপর অহী অবতীর্ণ হতে পারে। অথবা তারা তোমার রবের কাছে পৌঁছা পর্যন্ত তর্ক/বিতর্ক করতেই থাকবে। তুমি বলঃ অনুগ্রহ আল্লাহরই হাতে, তিনি যাকে ইচ্ছা ইহা দান করেন এবং আল্লাহই রক্ষাকারী, মহাজ্ঞানী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (367,3,74,' তিনি যার প্রতি ইচ্ছা করেন স্বীয় করুনা নির্দিষ্ট করেন এবং আল্লাহ মহান, অনুগ্রহশীল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (368,3,75,' কিতাবীদের মধ্যে এরূপ আছে যে, যদি তুমি তার নিকট পুঞ্জীভূত ধনরাশিও গচ্ছিত রেখে দাও তবুও সে তা তোমার নিকট প্রত্যর্পণ করবে এবং তাদের মধ্যে এরূপও আছে যে, যদি তুমি তার নিকট একটি দীনারও গচ্ছিত রাখ তাহলে সে তাও তোমাকে প্রত্যর্পণ করবেনা যে পর্যন্ত তুমি তার শিরোপরি দন্ডায়মান থাক। কারণ তারা বলে যে, আমাদের উপর ঐ অশিক্ষিতদের কোন দায় দায়িত্ব নেই এবং তারা আল্লাহর প্রতি মিথ্যা আরোপ করে, যা তারাও জানে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (369,3,76,' হ্যাঁ, যারা স্বীয় অঙ্গীকার পূর্ণ করে ও সংযত হয়, তাহলে নিশ্চয়ই আল্লাহ ধর্মভীরুগণকে ভালবাসেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (370,3,77,' নিশ্চয়ই যারা আল্লাহর প্রতি অঙ্গীকার ও স্বীয় শপথ সামান্য মূল্যে বিক্রি করে, পরলোকে তাদের কোনই অংশ নেই এবং উত্থান দিনে আল্লাহ তাদের সাথে কথা বলবেননা এবং তাদের প্রতি দৃষ্টিপাতও করবেননা এবং পরিশুদ্ধও করবেননা। তাদের জন্য যন্ত্রণাদায়ক শাস্তি রয়েছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (371,3,78,' আর তাদের মধ্যে নিশ্চয়ই এরূপ একদল আছে যারা কুঞ্চিত ভাষায় গ্রন্থ আবৃত্তি করে, যেন তোমরা ওটাকে গ্রন্থের অংশ মনে কর, অথচ ওটা গ্রন্থের অংশ নয়; এবং তারা বলে যে, এটা আল্লাহর নিকট হতে সমাগত, অথচ ওটা আল্লাহর নিকট হতে নয়, এবং তারা আল্লাহ সম্বন্ধে মিথ্যা বলে এবং তারাও তা অবগত আছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (372,3,79,'এটা কোন মানুষের পক্ষে উপযোগী নয় যে, আল্লাহ যাকে গ্রন্থ, বিজ্ঞান ও নবুওয়াত দান করেন, অতঃপর সে মানবমন্ডলীর মধ্যে বলে - তোমরা আল্লাহকে পরিত্যাগ করে আমার উপাসক হও; বরং বলবেঃ রবের ইবাদাতকারী হও - কারণ তোমরাই কুরআন শিক্ষা দান কর এবং ওটা পাঠ করে থাক।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (373,3,80,' আর তিনি আদেশ করেননা যে, তোমরা মালাক/ফেরেশতা ও নাবীগণকে রাব্ব রূপে গ্রহণ কর; তোমরা আত্মসমর্পণকারী হবার পর তিনি কি তোমাদেরকে পুনরায় কুফরীতে ফিরে যাবার আদেশ করবেন?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (374,3,81,'এবং আল্লাহ যখন নাবীগণের অঙ্গীকার গ্রহণ করেছিলেন, আমি তোমাদেরকে গ্রন্থ ও বিজ্ঞান হতে দান করার পর তোমাদের সঙ্গে যা আছে তার সত্যতা প্রত্যায়নকারী একজন রাসূল আগমন করবে, তখন তোমরা অবশ্যই তার প্রতি বিশ্বাস স্থাপন করবে এবং তার সাহায্যকারী হবে; তিনি আরও বলেছিলেনঃ তোমরা কি এতে স্বীকৃত হলে এবং প্রতিশ্রুতি গ্রহণ করলে? তারা বলেছিল - আমরা স্বীকার করলাম। তিনি বললেনঃ তাহলে তোমরা সাক্ষী থেক এবং আমিও তোমাদের সাথে সাক্ষীগণের অন্তর্ভুক্ত রইলাম।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (375,3,82,' অতঃপর এর পরে যারা ফিরে যাবে তারাই দুস্কার্যকারী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (376,3,83,' তাহলে কি তারা আল্লাহর ধর্ম ব্যতীত অন্য কিছু কামনা করে? অথচ নভোমন্ডল ও ভূমন্ডলে যা কিছু আছে, ইচ্ছা ও অনিচ্ছাক্রমে সবাই তাঁর উদ্দেশে আত্মসমর্পণ করেছে এবং তাঁরই দিকে প্রত্যাবর্তিত হবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (377,3,84,'তুমি বলঃ আমরা আল্লাহর প্রতি এবং যা আমাদের প্রতি অবতীর্ণ হয়েছে এবং ইবরাহীম, ইসমাঈল, ইসহাক, ইয়াকূব ও তাদের বংশধরগণের প্রতি অবতীর্ণ হয়েছে এবং যা মূসা, ঈসা ও অন্যান্য নাবীগণকে তাদের রাব্ব কর্তৃক প্রদত্ত হয়েছে তৎপ্রতি বিশ্বাস স্থাপন করলাম, আমরা তাদের মধ্যে কেহকেও পার্থক্য জ্ঞান করিনা এবং আমরা তাঁরই প্রতি আত্মসমর্পণকারী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (378,3,85,' আর যে কেহ ইসলাম ব্যতীত অন্য ধর্ম অন্বেষণ করে তা কখনই তার নিকট হতে গৃহীত হবেনা এবং পরলোকে সে ক্ষতিগ্রস্তদের অন্তর্ভুক্ত হবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (379,3,86,' কিরূপে আল্লাহ সেই সম্প্রদায়কে পথ প্রদর্শন করবেন যারা বিশ্বাস স্থাপনের পর, রাসূলের সত্যতা বিষয়ে সাক্ষ্যদানের পর এবং তাদের নিকট প্রকাশ্য নিদর্শনসমূহ আসার পরও অবিশ্বাসী হয়েছে? আর আল্লাহ অত্যাচারী সম্প্রদায়কে পথ প্রদর্শন করেননা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (380,3,87,' ওরাই - যাদের প্রতিফল এই যে, নিশ্চয়ই তাদের উপর আল্লাহর, তাঁর মালাইকা/ফেরেশতার এবং সমস্ত মানবের অভিসম্পাত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (381,3,88,' তারা তন্মধ্যে সদা অবস্থান করবে - তাদের উপর হতে শাস্তি প্রশমিত হবেনা এবং তাদেরকে বিরাম দেয়া হবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (382,3,89,' কিন্তু যারা এরপর ক্ষমা প্রার্থনা করে ও সংশোধিত হয়, তাহলে নিশ্চয়ই আল্লাহ ক্ষমাশীল, করুণাময়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (383,3,90,' নিশ্চয়ই যারা বিশ্বাস স্থাপনের পর অবিশ্বাসী হয়েছে, অতঃপর অবিশ্বাস পরিবর্ধিত করেছে - তাদের ক্ষমা প্রার্থনা কখনই গৃহীত হবেনা এবং তারাই পথভ্রান্ত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (384,3,91,' নিশ্চয়ই যারা অবিশ্বাস করেছে ও অবিশ্বাসী অবস্থায় মৃত্যু বরণ করেছে তাদের মুক্তির বিনিময়ে যদি পৃথিবীপূর্ণ স্বর্ণ দেয়া হয় তবুও কক্ষনো তা গ্রহণ করা হবেনা। ওদেরই জন্য যন্ত্রণাদায়ক শাস্তি রয়েছে এবং ওদের জন্য কোনই সাহায্যকারী নেই।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (385,3,92,' তোমরা যা ভালবাস তা হতে ব্যয় না করা পর্যন্ত তোমরা কখনই কল্যাণ লাভ করতে পারবেনা; এবং তোমরা যা কিছুই ব্যয় কর, আল্লাহ তা জ্ঞাত আছেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (386,3,93,' প্রত্যেক খাদ্যই বানী ইসরাঈলের জন্য হালাল ছিল। একমাত্র তাওরাত অবতীর্ণ হওয়ার পূর্বে বানী ইসরাঈল নিজের উপর যা হারাম করে নিয়েছিল তাই তাদের উপর হারাম করা হয়েছিল। তুমি বলঃ যদি তোমরা সত্যবাদী হও তাহলে তাওরাত আনয়ন কর, অতঃপর ওটা পাঠ কর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (387,3,94,' সুতরাং যদি কেহ এরপর আল্লাহর প্রতি অসত্যারোপ করে তাহলে তারাই অত্যাচারী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (388,3,95,'তুমি বলঃ আল্লাহ সত্য বলেছেন; অতএব তোমরা ইবরাহীমের সুদৃঢ় ধর্মের অনুসরণ কর এবং সে অংশীবাদীদের অন্তর্গত ছিলনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (389,3,96,' নিশ্চয়ই সর্ব প্রথম গৃহ, যা মানবমন্ডলীর জন্য নির্দিষ্ট করা হয়েছে তা ঐ ঘর যা বাক্কায় (মাক্কায়) অবিস্থত; ওটি সৌভাগ্যযুক্ত এবং সমস্ত বিশ্ববাসীর জন্য পথ প্রদর্শক।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (390,3,97,'ওর মধ্যে প্রকাশ্য নিদর্শনসমূহ বিদ্যমান রয়েছে, মাকামে ইবরাহীম উক্ত নিদর্শনসমূহের অন্যতম। আর যে ওর মধ্যে প্রবেশ করে সে নিরাপত্তা প্রাপ্ত হয় এবং আল্লাহর উদ্দেশে এই গৃহের হাজ্জ করা সেই সব মানুষের কর্তব্য যারা সফর করার আর্থিক সামর্থ্য রাখে এবং যদি কেহ অস্বীকার করে তাহলে নিশ্চয়ই আল্লাহ সমগ্র বিশ্ববাসী হতে প্রত্যাশামুক্ত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (391,3,98,' তুমি বলঃ হে কিতাবধারীরা! তোমরা কেন আল্লাহর নিদর্শনাবলীর প্রতি অবিশ্বাস করছো? তোমরা যা করছো আল্লাহ তদ্বিষয়ে সাক্ষী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (392,3,99,' হে গ্রন্থপ্রাপ্তরা! যে ব্যক্তি বিশ্বাস স্থাপন করেছে তার মধ্যে কুটিলতার কামনায় কেন তোমরা তাকে আল্লাহর পথে রোধ করছো, অথচ তোমরা এ পথের সত্যতা প্রত্যক্ষ করছ এবং তোমরাই সাক্ষী রয়েছ? আর তোমরা যা করছো তদ্বিষয়ে আল্লাহ অমনোযোগী নন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (393,3,100,' হে মু’মিনগণ! যাদেরকে গ্রন্থ প্রদত্ত হয়েছে, যদি তোমরা তাদের এক দলের অনুসরণ কর তাহলে তারা তোমাদেরকে তোমাদের ঈমান আনয়নের পর কাফির বানিয়ে দিবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (394,3,101,' আর কিরূপে তোমরা অবিশ্বাস করতে পার - যখন আল্লাহর নিদর্শনাবলী তোমাদের সামনে পঠিত হয় এবং তোমাদের মধ্যে তাঁর রাসূল বিদ্যমান রয়েছে? আর যে আল্লাহর পথ দৃঢ় রূপে ধারণ করে, অবশ্যই সে সরল পথ প্রদর্শিত হবে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (395,3,102,' হে বিশ্বাস স্থাপনকারীগণ! তোমরা আল্লাহকে ভয় কর যেমনভাবে করা উচিৎ এবং তোমরা মুসলিম না হয়ে মৃত্যু বরণ করনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (396,3,103,'আর তোমরা একযোগে আল্লাহর রজ্জু সুদৃঢ় রূপে ধারণ কর ও বিভক্ত হয়ে যেওনা, এবং তোমাদের প্রতি আল্লাহর যে দান রয়েছে তা স্মরণ কর। যখন তোমরা পরস্পর শত্রু ছিলে তখন তিনিই তোমাদের অন্তঃকরণে প্রীতি স্থাপন করেছিলেন, অতঃপর তোমরা তাঁর অনুগ্রহে ভ্রাতৃত্বে আবদ্ধ হলে এবং তোমরা অনল-কুন্ডের ধারে ছিলে, অনন্তর তিনিই তোমাদেরকে ওটা হতে উদ্ধার করেছেন; এরূপে আল্লাহ তোমাদের জন্য স্বীয় নিদর্শনাবলী ব্যক্ত করেন, যেন তোমরা সুপথ প্রাপ্ত হও।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (397,3,104,' এবং তোমাদের মধ্যে এরূপ এক সম্প্রদায় হওয়া উচিত যারা কল্যাণের দিকে আহবান করে এবং সৎ কাজে আদেশ করে ও অসৎ কাজে নিষেধ করে, আর তারাই সুফল প্রাপ্ত হবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (398,3,105,' এবং তাদের মতো হয়োনা যাদের নিকট স্পষ্ট প্রমাণ আসার পরও বিভিন্ন দলে বিভক্ত হয়েছে ও পরস্পর মতভেদে লিপ্ত রয়েছে; তাদের জন্য রয়েছে কঠোর শাস্তি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (399,3,106,' সেদিন কতগুলি মুখমন্ডল হবে শ্বেতবর্ণ এবং কতক মুখমন্ডল হবে কৃষ্ণবর্ণ; অতঃপর যাদের মুখমন্ডল কৃষ্ণবর্ণ হবে, (তাদেরকে বলা হবে) তাহলে কি তোমরা বিশ্বাস স্থাপনের পর অবিশ্বাসী হয়েছ? অতএব তোমরা শাস্তির আস্বাদ গ্রহণ কর, যেহেতু তোমরা অবিশ্বাস করেছিলে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (400,3,107,' আর যাদের মুখমন্ডল শুভ্র হবে, তারা আল্লাহর করুণার অন্তর্ভুক্ত, তারা তন্মধ্যে সদা অবস্থান করবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (401,3,108,' এগুলি হচ্ছে আল্লাহর নিদর্শন - যা আমি তোমার প্রতি সত্যসহ আবৃত্তি করছি এবং আল্লাহ বিশ্ব জগতের প্রতি অত্যাচারের ইচ্ছা করেননা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (402,3,109,' আর যা নভোমন্ডলের মধ্যে আছে ও যা ভূমন্ডলের মধ্যে রয়েছে তা আল্লাহর এবং আল্লাহর কাছেই সব কিছু প্রত্যাবর্তনশীল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (403,3,110,'তোমরাই সর্বোত্তম উম্মাত, মানব জাতির কল্যাণের জন্য তোমাদের উদ্ভব ঘটানো হয়েছে, তোমরা সৎ কাজে আদেশ করবে ও অসৎ কাজে নিষেধ করবে এবং আল্লাহর প্রতি বিশ্বাস স্থাপন করবে; আর যদি গ্রন্থ প্রাপ্তরা বিশ্বাস স্থাপন করত তাহলে অবশ্যই তাদের জন্য মঙ্গল হত; তাদের মধ্যে কেহ কেহ মু’মিন এবং তাদের অধিকাংশই দুস্কার্যকারী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (404,3,111,' সামান্য দুঃখ প্রদান ব্যতীত তারা তোমাদের কোন অনিষ্ট করতে পারবেনা; আর যদি তারা তোমাদের সাথে লড়াই করে তাহলে তারা তোমাদেরকে পৃষ্ঠ প্রদর্শন করবে; অতঃপর তাদেরকে সাহায্য করা হবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (405,3,112,'আল্লাহর নিকট আশ্রয় গ্রহণ এবং মনুষ্যগণের নিকট অত্মসমর্পণ ব্যতীত তারা যেখানেই অবস্থান করুক না কেন, লাঞ্ছনায় আক্রান্ত হয়েছে, আল্লাহর কোপে নিপতিত হয়েছে এবং গলগ্রহতা ওদের উপর চাপিয়ে দেয়া হয়েছে। এটা এ কারণে যে, তারা আল্লাহর নিদর্শনাবলীর প্রতি অবিশ্বাস করেছিল এবং নাবীগণকে অন্যায়ভাবে হত্যা করেছিল; যেহেতু তারা বিরুদ্ধাচরণ করেছিল ও সীমা অতিক্রম করেছিল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (406,3,113,' তারা সকলে সমান নয়; আহলে কিতাবের মধ্যে কিছু লোক রয়েছে যারা গভীর রাত পর্যন্ত আল্লাহর আয়াতসমূহ পাঠ করে এবং সাজদাহ করে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (407,3,114,' তারা আল্লাহ ও আখিরাত বিশ্বাস করে এবং সৎ কাজে আদেশ ও অসৎ কাজে নিষেধ করে এবং সৎ কাজসমূহে তৎপর থাকে, আর তারাই সৎ কর্মশীলগণের অন্তর্ভুক্ত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (408,3,115,' আর তারা যে সৎ কাজ করবে তা কখনও অবজ্ঞা করা হবেনা; এবং আল্লাহ ধর্মভীরুগণকে অবগত আছেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (409,3,116,' নিশ্চয়ই যারা অবিশ্বাস করেছে তাদের ধনরাশি ও সন্তান-সন্ততি আল্লাহর নিকট কিছুমাত্র কাজে আসবেনা; এবং তারাই জাহান্নামের অধিবাসী, তন্মধ্যে তারা চিরকাল অবস্থান করবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (410,3,117,' তারা পার্থিব জীবনে যা ব্যয় করে তার দৃষ্টান্ত শৈত্যপূর্ণ ঝঞ্ঝা-বায়ুর অনুরূপ - যারা স্বীয় জীবনের প্রতি অত্যাচার করেছে - ওটা সেই সকল সম্প্রদায়ের শস্যক্ষেতে নিপতিত হয় এবং তা বিধ্বস্ত করে। এবং আল্লাহ তাদের প্রতি অত্যাচার করেননি, বরং তারাই স্বীয় জীবনের প্রতি অত্যাচার করেছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (411,3,118,' হে বিশ্বাস স্থাপনকারীগণ! তোমরা নিজেদের সম্প্রদায় ব্যতিরেকে অন্য কেহকে বন্ধু রূপে গ্রহণ করনা, তারা তোমাদেরকে ক্ষতিগ্রস্ত করতে সংকুচিত হবেনা; এবং তোমরা যাতে বিপন্ন হও তারা তা’ই কামনা করে; বস্তুতঃ তাদের মুখ হতেই শত্রুতা প্রকাশিত হয়, এবং তাদের অন্তর যা গোপন করে তা আরও গুরুতর; নিশ্চয়ই আমি তোমাদের জন্য নিদর্শনাবলী ব্যক্ত করছি, যেন তোমরা বুঝতে পার।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (412,3,119,' সাবধান হও - তোমরাই তাদেরকে ভালবাস, অথচ তারা তোমাদেরকে ভালবাসেনা; এবং তোমরা সমস্ত গ্রন্থই বিশ্বাস কর; আর তারা যখন তোমাদের সাথে মিলিত হয় তখন বলেঃ আমরা বিশ্বাস স্থাপন করেছি এবং যখন তোমাদের হতে পৃথক হয়ে যায় তখন তোমাদের প্রতি আক্রোশে আঙ্গুলের অগ্রভাগসমূহ দংশন করে। তুমি বলঃ তোমরা নিজেদের আক্রোশে মরে যাও! নিশ্চয়ই আল্লাহ অন্তরের কথা জ্ঞাত আছেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (413,3,120,' যদি তোমাদেরকে কল্যাণ স্পর্শ করে তাহলে তারা অসন্তষ্ট হয়; আর যদি অমঙ্গল উপস্থিত হয় তখন তারা আনন্দিত হয়ে থাকে, এবং যদি তোমরা ধৈর্য ধারণ কর ও সংযমী হও তাহলে তাদের চক্রান্ত তোমাদের কোনই ক্ষতি করতে পারবেনা। তারা যা করে - নিশ্চয়ই আল্লাহ তার পরিবেষ্টনকারী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (414,3,121,'(স্মরণ কর) যখন তুমি বিশ্বাসীদেরকে যুদ্ধার্থে যথাস্থানে সংস্থাপিত করার জন্য প্রভাতে স্বীয় পরিজন হতে বের হয়েছিলে এবং আল্লাহ সব কিছু শ্রবণ করেন ও জানেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (415,3,122,' যখন তোমাদের দুই দল ভীরুতা প্রকাশের সংকল্প করেছিল এবং আল্লাহ সেই দলদ্বয়ের পৃষ্ঠপোষক ছিলেন; এবং মু’মিনগণই আল্লাহর উপর নির্ভর করে থাকে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (416,3,123,'আর নিশ্চয়ই আল্লাহ তোমাদেরকে বদরে সাহায্য করেছিলেন এবং তোমরা দুর্বল ছিলে; অতএব তোমরা আল্লাহকে ভয় কর, যেন তোমরা কৃতজ্ঞ হও।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (417,3,124,' যখন মু’মিনদেরকে বলেছিলেনঃ এটা কি তোমাদের পক্ষে যথেষ্ট নয় যে, তোমাদের রাব্ব তিন সহস্র মালাইকা/ফেরেশতা প্রেরণ করে তোমাদের সাহায্য করবেন?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (418,3,125,'বরং যদি তোমরা ধৈর্য ধারণ কর ও সংযমী হও এবং তারা যদি স্বেচ্ছায় তোমাদের উপর নিপতিত হয় তাহলে তোমাদের রাব্ব পাঁচ সহস্র বিশিষ্ট মালাইকা দ্বারা তোমাদেরকে সাহায্য করবেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (419,3,126,' আর আল্লাহ এই সাহায্য শুধু এ জন্যই করেছেন যেন তোমাদের জন্য সুসংবাদ হয় এবং তোমাদের অন্তরে শান্তি আসে। আর সাহায্য শুধু আল্লাহর পক্ষ হতেই হয়ে থাকে, যিনি পরাক্রান্ত, বিজ্ঞানময়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (420,3,127,' যারা অবিশ্বাসী হয়েছে - তিনি এরূপে তাদের একাংশকে কর্তিত করেন অথবা তাদেরকে দুর্বল করেন, যাতে তারা অকৃতকার্য সহকারে ফিরে যায়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (421,3,128,' এ ব্যাপারে তোমার কোনই করণীয় নেই, তিনি তাদেরকে ক্ষমা করেন অথবা শাস্তি প্রদান করেন; নিশ্চয়ই তারা অত্যাচারী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (422,3,129,' আর নভোঃমন্ডলে যা রয়েছে ও ভূমন্ডলে যা আছে তা আল্লাহরই; তিনি যাকে ইচ্ছা ক্ষমা করেন এবং যাকে ইচ্ছা শাস্তি প্রদান করেন এবং আল্লাহ ক্ষমাশীল, পরম করুণাময়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (423,3,130,' হে ঈমানদারগণ! তোমরা দ্বিগুণের উপর দ্বিগুণ সুদ ভক্ষণ করনা এবং আল্লাহকে ভয় কর যেন তোমরা সুফল প্রাপ্ত হও।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (424,3,131,'আর তোমরা সেই জাহান্নামের ভয় কর, যা অবিশ্বাসীদের জন্য প্রস্তুত রাখা হয়েছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (425,3,132,' আর আল্লাহ ও রাসূলের আনুগত্য স্বীকার কর যেন তোমরা করুণা প্রাপ্ত হও।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (426,3,133,' তোমরা স্বীয় রবের ক্ষমা ও জান্নাতের দিকে ধাবিত হও, যার প্রসারতা নভোমন্ডল ও ভূমন্ডল সদৃশ, ওটা ধর্মভীরুদের জন্য নির্মিত হয়েছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (427,3,134,'যারা স্বচ্ছলতা ও অভাবের মধ্যে ব্যয় করে এবং ক্রোধ সংবরণ করে ও মানবদেরকে ক্ষমা করে; এবং আল্লাহ সৎকর্মশীলদের ভালবাসেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (428,3,135,' এবং যখন কেহ অশ্লীল কাজ করে কিংবা স্বীয় জীবনের প্রতি অত্যাচার করে অতঃপর আল্লাহকে স্মরণ করে এবং অপরাধসমূহের জন্য ক্ষমা প্রার্থনা করে, এবং আল্লাহ ব্যতীত কে অপরাধসমূহ ক্ষমা করতে পারে? এবং তারা যা করছে সেই ব্যাপারে জেনে শুনে হঠকারিতা করেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (429,3,136,' তাদের পুরস্কার হবে তাদের রবের নিকট হতে মার্জনা এবং এমন উদ্যানসমূহ যেগুলির তলদেশ দিয়ে স্রোতস্বিনীসমূহ প্রবাহিত থাকবে, তন্মধ্যে তারা সদা অবস্থান করবে, এবং কর্মীদের জন্য কি সুন্দর প্রতিদান!');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (430,3,137,' নিশ্চয়ই তোমাদের পূর্বে বহু জীবনাচরণ অতিক্রান্ত হয়েছে, পৃথিবীতে বিচরণ কর, এবং লক্ষ্য কর যে, অবিশ্বাসীদের পরিণাম কিরূপ হয়েছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (431,3,138,' এটা মানবমন্ডলীর জন্য বিবরণ এবং আল্লাহভীরুগণের জন্য পথ প্রদর্শন ও উপদেশ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (432,3,139,' আর তোমরা নিরাশ হয়োনা ও বিষন্ন হয়োনা এবং যদি তোমরা বিশ্বাসী হও তাহলে তোমরাই বিজয়ী হবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (433,3,140,' যদি তোমাদের আঘাত লেগে থাকে তাহলে নিশ্চয়ই সেই সম্প্রদায়েরও তদ্রুপ আঘাত লেগেছে, এবং এই দিনসমূহকে আমি মানবগণের মধ্যে পরিক্রমণ করাই; এবং যারা বিশ্বাস স্থাপন করেছে তাদেরকে আল্লাহ এই রূপে প্রকাশ করেন; এবং তোমাদের মধ্য হতে কতককে শহীদ রূপে গ্রহণ করবেন, আর আল্লাহ অত্যাচারীদেরকে ভালবাসেননা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (434,3,141,' আর যারা বিশ্বাস স্থাপন করেছে তাদেরকে আল্লাহ এইরূপে পবিত্র করেন এবং অবিশ্বাসীদেরকে ধ্বংস করেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (435,3,142,' তোমরা কি ধারণা করছ যে, তোমরাই জান্নাতে প্রবেশ করবে? অথচ কারা জিহাদ করে ও কারা ধৈর্যশীল আল্লাহ তোমাদের মধ্য হতে তাদেরকে এখনও পরীক্ষা করেননি?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (436,3,143,' এবং নিশ্চয়ই তোমরা মৃত্যুর পূর্বেই ওর সাক্ষাৎ কামনা করছিলে, অনন্তর নিশ্চয়ই তোমরা এখন তা প্রত্যক্ষ করছ তোমাদের নিজেদের চোখে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (437,3,144,' এবং মুহাম্মাদ রাসূল ব্যতীত কিছুই নয়, নিশ্চয়ই তার পূর্বে রাসূলগণ বিগত হয়েছে, অনন্তর যদি তার মৃত্যু হয় অথবা সে নিহত হয় তাহলে কি তোমরা পশ্চাদপদে ফিরে যাবে? এবং যে কেহ পশ্চাদপদে ফিরে যায় তাতে সে আল্লাহর কোনই অনিষ্ট করবেনা এবং আল্লাহ কৃতজ্ঞগণকে পুরস্কার প্রদান করেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (438,3,145,' আর আল্লাহর আদেশে লিপিবদ্ধ নির্দিষ্ট সময় ব্যতীত কেহই মৃত্যুমুখে পতিত হয়না; এবং যে কেহ ইহলোকের প্রতিদান কামনা করে, আমি তাকে তা দুনিয়ায় প্রদান করে থাকি; পক্ষান্তরে যে লোক আখিরাতে বিনিময় কামনা করে আমি তাকে তা প্রদান করব এবং আমি কৃতজ্ঞগণকে অচিরেই পুরস্কার প্রদান করব।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (439,3,146,' আর এমন অনেক নাবী ছিল যারা সঙ্গীসহযোগে অনুবর্তী হয়ে যুদ্ধ করেছিল; পরন্ত আল্লাহর পথে যা সংঘটিত হয়েছিল তাতে তারা নিরুৎসাহ হয়নি ও শক্তিহীন হয়নি এবং বিচলিত হয়নি। এবং আল্লাহ ধৈর্যশীলগণকে ভালবাসেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (440,3,147,' আর এতদ্ব্যতীত তাদের কথা ছিলনা যে, তারা বলতঃ হে আমাদের রাব্ব! আমাদের অপরাধ ও আমাদের কাজের বাড়াবাড়ি হেতু কৃত অন্যায়সমূহ ক্ষমা করুন, আমাদেরকে দৃঢ় রাখুন এবং অবিশ্বাসীদের বিরুদ্ধে আমাদেরকে সাহায্য করুন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (441,3,148,'অনন্তর আল্লাহ তাদেরকে পার্থিব পুরস্কার প্রদান করলেন এবং পরলোকের পুরস্কার শ্রেষ্ঠতর; এবং আল্লাহ সৎকর্মশীলগণকে ভালবাসেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (442,3,149,' হে মু’মিনগণ! যারা অবিশ্বাস করেছে, যদি তোমরা তাদের আজ্ঞাবহ হও তাহলে তারা তোমাদেরকে পশ্চাদপদে ফিরিয়ে নিবে, তাতে তোমরা ক্ষতিগ্রস্ত হবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (443,3,150,' বরং আল্লাহ তোমাদের অভিভাবক এবং তিনিই শ্রেষ্ঠতর সাহায্যকারী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (444,3,151,' যারা অবিশ্বাস করেছে, আমি সত্ত্বর তাদের অন্তরে ভীতি সঞ্চার করব, যেহেতু তারা আল্লাহর সাথে সেই বিষয়ে অংশী স্থাপন করেছে যদ্বিষয়ে তিনি কোন প্রমাণ অবতীর্ণ করেননি এবং জাহান্নাম তাদের অবস্থান স্থল এবং ওটা অত্যাচারীদের জন্য নিকৃষ্ট বাসস্থান!');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (445,3,152,' আর নিশ্চয়ই আল্লাহ তোমাদের প্রতি দেয়া তাঁর অঙ্গীকার পূর্ণ করেছেন যখন তোমরা তাঁর অনুমতিক্রমে তোমাদের শত্রুদের বিনাশ করছিলে তোমরা সাহসহারা হয়ে যাওয়া পর্যন্ত এবং নির্দেশ সম্পর্কে বিবাদ করছিলে ও অবাধ্য হয়েছিলে। অতঃপর তোমরা যা পছন্দ কর (বিজয়) তা তোমাদের প্রত্যক্ষ করালেন। তোমাদের মধ্যে এমন কিছু লোক আছে যারা পার্থিব বস্ত্ত কামনা করে এবং কিছু লোক পরকাল পছন্দ করে। অতঃপর তিনি তোমাদের পরীক্ষা করার জন্য শত্রুদের থেকে ফিরিয়ে দিলেন এবং নিশ্চয়ই তিনি তোমাদেরকে ক্ষমা করলেন। আল্লাহ বিশ্বাসীদের প্রতি অনুগ্রহশীল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (446,3,153,' আর যখন তোমরা আরোহণ করে যাচ্ছিলে এবং কারও দিকে ফিরেও দেখছিলেনা এবং রাসূল তোমাদেরকে পশ্চাদ হতে আহবান করছিল; অনন্তর তিনি তোমাদেরকে দুঃখের উপর দুঃখ প্রদান করলেন। কিন্তু যা অতিক্রান্ত হয়েছে এবং তোমাদের উপর যা উপনীত হয়নি, তোমরা তজ্জন্য দুঃখ করনা; এবং তোমরা যা করছ আল্লাহ তদ্বিষয়ে অভিজ্ঞ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (447,3,154,'অনন্তর তিনি দুঃখের পরে তোমাদের উপর শান্তি অবতরণ করলেন, তা ছিল তন্দ্রা, যা তোমাদের এক দলকে তন্দ্রাচ্ছন্ন করেছিল, আর একদল নিজেদের জীবনের জন্য চিন্তা করছিল; তারা আল্লাহ সম্বন্ধে অজ্ঞতার অনুরূপ ধারণা পোষণ করছিল। তারা বলেছিলঃ এ বিষয়ে কি আমাদের কোন অধিকার নেই? তুমি বলঃ সকল বিষয়ে আল্লাহর অধিকার। তারা নিজেদের অন্তরে যা গোপন রাখে তা তোমার নিকট প্রকাশ করেনা; তারা বলেঃ যদি এ বিষয়ে আমাদের কোন অধিকার থাকতো তাহলে এখানে আমরা নিহত হতামনা। তুমি বলঃ যদি তোমরা তোমাদের গৃহের মধ্যেও থাকতে তবুও যাদের প্রতি মৃত্যু বিধিবদ্ধ হয়েছে তারা নিশ্চয়ই স্বীয় মৃত্যু স্থানে এসে উপস্থিত হত; তোমাদের অন্তরের মধ্যে যা আছে, আল্লাহ তা পরীক্ষা করে থাকেন; এবং আল্লাহ মনের অন্তর্নিহিত ভাব জ্ঞাত আছেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (448,3,155,'নিশ্চয়ই তোমাদের মধ্যে যারা দু’দলের সম্মুখীন হওয়ার দিন পশ্চাদবর্তীত হয়েছিল তার কারণ শাইতান তাদেরকে প্রতারিত করেছিল তাদেরই কোনো পাপের কারণে। কিন্তু আল্লাহ তাদেরকে ক্ষমা করেছেন; নিশ্চয়ই আল্লাহ ক্ষমাশীল, সহিষ্ণু।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (449,3,156,'হে মু’মিনগণ! যারা অবিশ্বাস করেছে তোমরা তাদের মত হয়োনা; এবং যখন তাদের ভ্রাতৃগণ পৃথিবীতে কোন অভিযানে বের হয় অথবা যুদ্ধে নিহত হয় তখন তারা বলেঃ যদি ওরা আমাদের নিকট থাকতো তাহলে মৃত্যুমুখে পতিত হতনা অথবা নিহত হতনা; আল্লাহ এরূপে তাদের অন্তরে দুঃখ ও অনুতাপ সঞ্চার করেন, আল্লাহই জীবন দান করেন ও মৃত্যু দেন এবং তোমরা যা করছ তৎপ্রতি আল্লাহ লক্ষ্যকারী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (450,3,157,'আর যদি তোমরা আল্লাহর পথে নিহত অথবা মৃত্যুমুখে পতিত হও তাহলে আল্লাহর নিকট হতেই ক্ষমা রয়েছে এবং তারা যা সঞ্চয় করেছে তদপেক্ষা তাঁর করুণা শ্রেষ্ঠতর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (451,3,158,'আর যদি তোমরা মৃত্যুবরণ কর কিংবা নিহত হও তাহলে তোমাদেরকে অবশ্যই আল্লাহর দিকে একত্রিত করা হবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (452,3,159,'অতএব আল্লাহর অনুগ্রহ এই যে, তুমি তাদের প্রতি কোমল চিত্ত; এবং তুমি যদি কর্কশভাষী, কঠোর হৃদয় হতে তাহলে নিশ্চয়ই তারা তোমার সংসর্গ হতে অন্তর্হিত হত। অতএব তুমি তাদেরকে ক্ষমা কর ও তাদের জন্য ক্ষমা প্রার্থনা কর এবং কার্য সম্বন্ধে তাদের সাথে পরামর্শ কর; অতঃপর তুমি যখন সংকল্প কর তখন আল্লাহর প্রতি নির্ভর কর; এবং নিশ্চয়ই আল্লাহ তাঁর উপর ভরসাকারীগণকে ভালবাসেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (453,3,160,' যদি আল্লাহ তোমাদেরকে সাহায্য করেন তাহলে কেহই তোমাদের উপর জয়যুক্ত হবেনা; এবং যদি তিনি তোমাদেরকে পরিত্যাগ করেন তাহলে তাঁর পরে আর কে আছে যে তোমাদেরকে সাহায্য করতে পারে? এবং বিশ্বাসীগণ আল্লাহর উপরেই নির্ভর করে থাকে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (454,3,161,' আর কোন নাবীর পক্ষে কোন বিষয় গোপন করা শোভনীয় নয়; এবং যে কেহ গোপন করবে তাহলে সে যা গোপন করেছে তা উত্থান দিনে আনয়ন করা হবে; অনন্তর প্রত্যেক ব্যক্তি যা অর্জন করেছে তা পূর্ণরূপে প্রদত্ত হবে এবং তারা নির্যাতিত হবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (455,3,162,'যে আল্লাহর সন্তুষ্টির অনুসরণ করেছে সে কি তার মত হতে পারে, যে আল্লাহর আক্রোশে পতিত হয়েছে? এবং তার বাসস্থান জাহান্নাম এবং ওটা নিকৃষ্ট গন্তব্য স্থান।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (456,3,163,' আল্লাহর নিকট মানুষের বিভিন্ন পদমর্যাদা রয়েছে এবং তোমরা যা করছ তদ্বিষয়ে আল্লাহ লক্ষ্যকারী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (457,3,164,' নিশ্চয়ই আল্লাহ মু’মিনদের প্রতি অনুগ্রহ করেছেন, তিনি তাদের নিজেদেরই মধ্য হতে রাসূল প্রেরণ করেছেন, যে তাদের নিকট তাঁর নিদর্শনাবলী পাঠ করে ও তাদেরকে পবিত্র করে এবং তাদেরকে গ্রন্থ ও বিজ্ঞান শিক্ষা দান করে এবং নিশ্চয়ই তারা এর পূর্বে প্রকাশ্য ভ্রান্তির মধ্যে ছিল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (458,3,165,'হ্যাঁ, যখন তোমাদের উপর বিপদ উপস্থিত হল, বস্তুতঃ তোমরাও তাদের প্রতি তদনুরূপ দু’বার বিপদ উপস্থিত করেছিলে, তোমরা বলেছিলেঃ এটা কোথা হতে হল? তুমি বলঃ ওটা তোমাদের নিজেদেরই নিকট হতে; নিশ্চয়ই আল্লাহ সর্ব বিষয়োপরি শক্তিমান।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (459,3,166,'এই দুই দলের সম্মুখীন হওয়ার দিনে তোমাদের উপর যা আপতিত হয়েছিল তা আল্লাহরই ইচ্ছাক্রমে; এবং তদ্দারা আল্লাহ বিশ্বাসীদেরকে পরীক্ষা করেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (460,3,167,'আর তদ্দারা তিনি মুনাফিকদেরকে পরিচিত করেন; এবং তাদেরকে বলা হয়েছিলঃ এসো, আল্লাহর পথে সংগ্রাম কর অথবা শত্রুদেরকে প্রতিহত কর; তারা বলেছিলঃ যদি আমরা জানতাম যে, লড়াই হবে তাহলে অবশ্যই আমরা তোমাদের অনুগমন করতাম। তারা সেদিন বিশ্বাস অপেক্ষা অবিশ্বাসের নিকটবর্তী ছিল; তাদের অন্তরে যা নেই তা’ই তারা মুখে বলে থাকে; তারা যে বিষয় গোপন করে আল্লাহ তা জ্ঞাত আছেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (461,3,168,' ওরা তারা, যারা গৃহে বসে স্বীয় নিহত ভাইদের সম্বন্ধে বলেঃ যদি তারা আমাদের কথা মান্য করত তাহলে নিহত হতনা; তুমি বলঃ যদি তোমরা সত্যবাদী হও তাহলে নিজেদেরকে মৃত্যু হতে রক্ষা কর। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (462,3,169,' যারা আল্লাহর পথে নিহত হয়েছে তাদেরকে কখনও মৃত মনে করনা; বরং তারা জীবিত, তারা তাদের রাব্ব হতে জীবিকা প্রাপ্ত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (463,3,170,' আল্লাহ তাদেরকে স্বীয় অনুগ্রহ হতে যা দান করেছেন তাতেই তারা পরিতুষ্ট; এবং তাদের ভাইয়েরা যারা এখনো তাদের সাথে সম্মিলিত হয়নি তাদের এই অবস্থার প্রতিও তারা সন্তুষ্ট হয় যে, তাদের কোন ভয় নেই এবং তারা দুঃখিত হবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (464,3,171,' তারা আল্লাহর নিকট হতে অনুগ্রহ ও নি‘আমাত লাভ করার কারণে আনন্দিত হয়; আর এ জন্য যে, নিশ্চয়ই আল্লাহ বিশ্বাসীগণের প্রতিদান বিনষ্ট করেননা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (465,3,172,' যারা আঘাত পাওয়ার পরেও আল্লাহ ও রাসূলের নির্দেশকে মান্য করেছে তাদের মধ্যে যারা সৎ কাজ করেছে ও সংযত হয়েছে তাদের জন্য রয়েছে মহান প্রতিদান।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (466,3,173,' যাদেরকে লোকেরা বলেছিলঃ নিশ্চয়ই তোমাদের বিরুদ্ধে সেই সব লোক সমবেত হয়েছে; অতএব তোমরা তাদেরকে ভয় কর; কিন্তু এতে তাদের বিশ্বাস পরিবর্ধিত হয়েছিল এবং তারা বলেছিলঃ আল্লাহই আমাদের জন্য যথেষ্ট এবং তিনি মঙ্গলময়, কর্মবিধায়ক।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (467,3,174,' অনন্তর তারা আল্লাহর অনুগ্রহ সম্পদসহ প্রত্যাবর্তীত হয়েছিল, তাদেরকে অমঙ্গল স্পর্শ করেনি এবং তারা আল্লাহর সন্তুষ্টির অনুসরণ করেছিল; আর আল্লাহর অনুগ্রহ অতি ব্যাপক।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (468,3,175,' নিশ্চয়ই শাইতান শুধুমাত্র তার অলী হতে তোমাদেরকে ভয় প্রদর্শন করে; কিন্তু যদি তোমরা বিশ্বাসী হও তাহলে তাদেরকে ভয় করনা; এবং আমাকেই ভয় কর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (469,3,176,' আর যারা অবিশ্বাসে তৎপর, তুমি তাদের জন্য বিষন্ন হয়োনা; বস্তুতঃ তারা আল্লাহর কোন অনিষ্ট করতে পারবেনা; আল্লাহ তাদের জন্য আখিরাতে কোন কল্যাণ ইচ্ছা করেননা এবং তাদেরই জন্য কঠোর শাস্তি রয়েছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (470,3,177,' নিশ্চয়ই যারা বিশ্বাসের পরিবর্তে অবিশ্বাস ক্রয় করেছে তারা আল্লাহর কোনই অনিষ্ট করতে পারবেনা এবং তাদের জন্য রয়েছে যন্ত্রনাদায়ক শাস্তি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (471,3,178,' অবিশ্বাসীরা যেন এ ধারণা না করে যে, আমি তাদেরকে যে অবকাশ দিয়েছি তা তাদের জীবনের জন্য কল্যাণকর; তারা স্বীয় পাপ বর্ধিত করবে এ জন্যই আমি তাদেরকে অবকাশ প্রদান করি; এবং তাদের জন্য অপমানকর শাস্তি রয়েছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (472,3,179,' সৎকে অসৎ (মুনাফিক) হতে পৃথক না করা পর্যন্ত আল্লাহ মু’মিনদেরকে, তারা যে অবস্থায় আছে ঐ অবস্থায় রেখে দিতে পারেননা, আর গাইবের খবরও তাদেরকে অবহিত করবেননা, তবে তাঁর রাসূলদের মধ্য থেকে যাকে ইচ্ছা গ্রহণ করেন। সুতরাং তোমরা আল্লাহকে ও তাঁর রাসূলকে বিশ্বাস কর। এবং যদি বিশ্বাস কর ও ভাল ‘আমল কর তাহলে তোমাদের জন্য রয়েছে মহা পুরস্কার।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (473,3,180,' আর আল্লাহ যাদেরকে স্বীয় অনুগ্রহে কিছু দান করেছেন তদ্বিষয়ে যারা কার্পণ্য করে তারা যেন এরূপ ধারণা না করে যে, ওটা তাদের জন্য কল্যাণকর; বরং ওটা তাদের জন্য ক্ষতিকর; তারা যে বিষয়ে কৃপণতা করেছে উত্থান দিনে ওটাই তাদের কন্ঠ-নিগড় হবে; এবং আল্লাহ নভোমন্ডল ও ভূমন্ডলের স্বত্বাধিকারী এবং যা তোমরা করছ আল্লাহ তদ্বিষয়ে পূর্ণ খবর রাখেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (474,3,181,' অবশ্যই আল্লাহ তাদের কথা শ্রবণ করেছেন যারা বলে থাকে যে, আল্লাহ দরিদ্র ও তারা ধনবান; তারা যা বলছে এবং তাদের অন্যায়ভাবে নাবীগণকে হত্যা করা আমি লিপিবদ্ধ করব; এবং তাদেরকে বলবঃ তোমরা জ্বলন্ত আগুনের শাস্তির আস্বাদ গ্রহণ কর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (475,3,182,' এটা তা’ই যা তোমাদের হস্তসমূহ পূর্বে প্রেরণ করেছে এবং নিশ্চয়ই আল্লাহ অত্যাচারী নন, যারা তাঁকে সেবা করে তাদের প্রতি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (476,3,183,' যারা বলে থাকে, অবশ্যই আল্লাহ আমাদের থেকে অঙ্গীকার নিয়েছেন যে, অগ্নি যা গ্রাস করে, আমাদের জন্য এমন কুরবানী আনয়ন না করা পর্যন্ত আমরা যেন কোন নাবীর প্রতি বিশ্বাস স্থাপন না করি; তুমি বলঃ নিশ্চয়ই আমার পূর্বে সমুজ্জ্বল নিদর্শনাবলী এবং তোমরা যা বল তৎসহ রাসূলগণ আগমন করেছিল; যদি তোমরা সত্যবাদী হও তাহলে কেন তোমরা তাদেরকে হত্যা করেছিলে?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (477,3,184,'অতঃপর যদি তারা তোমার প্রতি অসত্যারোপ করে তাহলে তোমার পূর্বেও রাসূলগণকে অবিশ্বাস করা হয়েছিল, যারা প্রকাশ্য নিদর্শনাবলী ও ক্ষুদ্র পুস্তিকা এবং উজ্জ্বল গ্রন্থসহ আগমন করেছিল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (478,3,185,' সমস্ত জীবই মৃত্যুর আস্বাদ গ্রহণ করবে; এবং নিশ্চয়ই উত্থান দিনে তোমাদেরকে পূর্ণ প্রতিদান দেয়া হবে; অতএব যে কেহ জাহান্নাম হতে বিমুক্ত হয় এবং জান্নাতে প্রবিষ্ট হয় - ফলতঃ নিশ্চয়ই সে সফলকাম; আর পার্থিব জীবন ছলনাময় ভোগ ব্যতীত আর কিছুই নয়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (479,3,186,' অবশ্যই তোমরা তোমাদের ধন-সম্পদ ও জীবন সম্পর্কে পরীক্ষিত হবে। তোমাদের পূর্বে যাদেরকে গ্রন্থ প্রদত্ত হয়েছে ও যারা অংশী স্থাপন করেছে তাদের নিকট হতে তোমাদেরকে বহু দুঃখজনক বাক্য শুনতে হবে; এবং যদি তোমরা ধৈর্য ধারণ কর ও সংযমী হও তাহলে অবশ্যই এটা সুদৃঢ় কার্যাবলীর অন্তর্গত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (480,3,187,' আর আল্লাহ যখন যাদেরকে গ্রন্থ প্রদান করা হয়েছে তাদের কাছ থেকে অঙ্গীকার গ্রহণ করেছিলেন যে, তারা নিশ্চয়ই এটি লোকদের মধ্যে ব্যক্ত করবে এবং তা গোপন করবেনা; কিন্তু তারা ওটা তাদের পশ্চাতে নিক্ষেপ করল এবং ওটা অল্প মূল্যে বিক্রি করল। অতএব তারা যা ক্রয় করেছিল তা নিকৃষ্টতর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (481,3,188,' যারা স্বীয় কৃতকর্মে সন্তষ্ট এবং তারা যা করেনি তজ্জন্য প্রশংসা প্রার্থী এরূপ লোকদের সম্বন্ধে ধারণা করনা যে, তারা শাস্তি হতে বিমুক্ত, তাদের জন্য রয়েছে যন্ত্রণাদায়ক শাস্তি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (482,3,189,' আল্লাহরই জন্য নভোমন্ডল ও ভূমন্ডলের আধিপত্য এবং আল্লাহ সর্ব বিষয়োপরি শক্তিমান।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (483,3,190,' নিশ্চয়ই নভোমন্ডল ও ভূমন্ডল সৃষ্টিতে এবং দিন ও রাতের পরিবর্তনে জ্ঞানবানদের জন্য স্পষ্ট নিদর্শনাবলী রয়েছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (484,3,191,' যারা দন্ডায়মান, উপবেশন ও এলায়িত অবস্থায় আল্লাহকে স্মরণ করে এবং নভোমন্ডল ও ভূমন্ডলের সৃষ্টি বিষয়ে চিন্তা-গবেষনা করে এবং বলেঃ হে আমাদের রাব্ব! আপনি এসব বৃথা সৃষ্টি করেননি; আপনিই পবিত্রতম! অতএব আমাদেরকে জাহান্নাম হতে রক্ষা করুন!');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (485,3,192,' হে আমাদের রাব্ব! আপনি যাকে জাহান্নামে প্রবিষ্ট করান, ফলতঃ নিশ্চয়ই তাকে লাঞ্ছিত করা হয়েছে; এবং অত্যাচারীদের জন্য কেহই সাহায্যকারী নেই।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (486,3,193,' হে আমাদের রাব্ব! নিশ্চয়ই আমরা এক আহবানকারীকে ঈমানের প্রতি আহবান করতে শুনেছিলাম যে, তোমরা স্বীয় রবের প্রতি বিশ্বাস স্থাপন কর; আমরা বিশ্বাস স্থাপন করেছি; হে আমাদের রাব্ব! অতএব আমাদের অপরাধসমূহ ক্ষমা করুন ও আমাদের সকল দোষক্রটি দূর করুন এবং পুন্যবানদের সাথে আমাদের মৃত্যু দান করুন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (487,3,194,' হে আমাদের রাব্ব! আপনি স্বীয় রাসূলগণের মাধ্যমে আমাদের সাথে যে অঙ্গীকার করেছিলেন তা দান করুন এবং উত্থান দিবসে আমাদেরকে লাঞ্ছিত করবেননা। নিশ্চয়ই আপনি অঙ্গীকার ভঙ্গ করেননা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (488,3,195,' অনন্তর তাদের রাব্ব তাদের জন্য ওটা স্বীকার করলেন এবং বললেনঃ আমি তোমাদের পুরুষ অথবা নারীর মধ্য হতে কোন কর্মীর কৃতকর্ম ব্যর্থ করবনা, তোমরা পরস্পর এক, অতএব যারা দেশ ত্যাগ করেছে অথবা স্বীয় গৃহসমূহ হতে বিতাড়িত হয়েছে ও আমার পথে নির্যাতিত হয়েছে এবং সংগ্রাম করেছে ও নিহত হয়েছে - নিশ্চয়ই তাদের জন্য আমি তাদের অমঙ্গলসমূহ অপসারণ করাব এবং নিশ্চয়ই আমি তাদেরকে জান্নাতে প্রবেশ করাব, যার নিম্নে স্রোতস্বিনী নদীসমূহ প্রবাহিত; এটা আল্লাহর নিকট হতে প্রতিদান এবং আল্লাহর নিকটই উত্তম প্রতিদান রয়েছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (489,3,196,' যারা অবিশ্বাসী হয়েছে তাদের নগরসমূহে প্রত্যাগমন যেন তোমাদেরকে প্রতারিত না করে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (490,3,197,' এটা মাত্র কয়েকদিনের সম্ভোগ; অনন্তর তাদের অবস্থান জাহান্নাম এবং ওটা নিকৃষ্ট স্থান।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (491,3,198,'কিন্তু যারা স্বীয় রাব্বকে ভয় করে তাদের জন্য রয়েছে জান্নাত-যার নিম্নে স্রোতস্বিনীসমূহ প্রবাহিত, তম্মধ্যে তারা সদা অবস্থান করবে, এটা আল্লাহর নিকট রয়েছে, তা সৎকর্মশীলদের জন্য বহুগুণে উত্তম।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (492,3,199,' এবং নিশ্চয়ই আহলে কিতাবের মধ্যে এরূপ লোকও রয়েছে যারা আল্লাহর প্রতি ঈমান আনে এবং তোমাদের প্রতি যা অবতীর্ণ হয়েছে এবং যা তাদের প্রতি অবতীর্ণ হয়েছিল তদ্বিষয়ে বিশ্বাস স্থাপন করে এবং আল্লাহর সম্মুখে বিনয়াবনত থাকে। যারা অল্প মূল্যে আল্লাহর নিদর্শনাবলী বিক্রি করেনা তাদেরই জন্য তাদের রবের নিকট প্রতিদান রয়েছে; নিশ্চয়ই আল্লাহ সত্ত্বর হিসাব গ্রহণকারী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (493,3,200,' হে বিশ্বাস স্থাপনকারীগণ! তোমরা ধৈর্য অবলম্বন কর এবং সহিষ্ণু ও সুপ্রতিষ্ঠিত হও; এবং আল্লাহকে ভয় কর যেন তোমরা সুফল প্রাপ্ত হও।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (494,4,1,' হে মানবমন্ডলী! তোমরা তোমাদের রাব্বকে ভয় কর, যিনি তোমাদেরকে একই ব্যক্তি হতে সৃষ্টি করেছেন এবং তা হতে তদীয় সহধর্মিনী সৃষ্টি করেছেন এবং তাদের উভয় হতে বহু নর ও নারী ছড়িয়ে দিয়েছেন এবং সেই আল্লাহকে ভয় কর যাঁর নামের দোহাই দিয়ে তোমরা একে অপরকে যাঞ্চা কর, এবং আত্মীয়-জ্ঞাতিদের ব্যাপারে সতর্কতা অবলম্বন কর। নিশ্চয়ই আল্লাহই তত্ত্বাবধানকারী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (495,4,2,' আর ইয়াতীমদেরকে তাদের ধন সম্পত্তি বুঝিয়ে দাও এবং পবিত্রতার সাথে অপবিত্রতার বিনিময় করনা ও তোমাদের ধন সম্পত্তির সাথে তাদের ধন সম্পত্তি মিশ্রিত করে ভোগ করনা; নিশ্চয়ই এটা গুরুতর অপরাধ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (496,4,3,' আর যদি তোমরা আশংকা কর যে, ইয়াতীমদের প্রতি সুবিচার করতে পারবেনা তাহলে নারীদের মধ্য হতে তোমাদের পছন্দ মত দু’টি, তিনটি কিংবা চারটিকে বিয়ে করে নাও; কিন্তু যদি তোমরা আশংকা কর যে, তাদের সাথে ন্যায় সঙ্গত আচরণ করতে পারবেনা তাহলে মাত্র একটি অথবা তোমাদের ডান হাত যার অধিকারী (ক্রীতদাসী); এটা আরও উত্তম; এটা অবিচার না করার নিকটবর্তী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (497,4,4,'আর সন্তুষ্ট চিত্তে নারীদেরকে তাদের দেয় মোহর প্রদান কর, কিন্তু যদি তারা স্বেচ্ছায় কিয়দংশ প্রদান করে তাহলে সঠিক বিবেচনা মত তৃপ্তির সাথে ভোগ কর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (498,4,5,' আল্লাহ তোমাদের জন্য যে ধন-সম্পত্তি নির্ধারণ করেছেন তা অবোধদেরকে প্রদান করনা; বরং তা হতে তাদেরকে ভক্ষণ করাতে থাক, পরিধান করাতে থাক এবং তাদের সাথে সদ্ভাবে কথা বল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (499,4,6,'আর ইয়াতীমরা বিয়ের যোগ্য না হওয়া পর্যন্ত তাদেরকে পরীক্ষা করে নাও; অতঃপর যদি তাদের মধ্যে বিবেক-বুদ্ধি পরিদৃষ্ট হয় তাহলে তাদের ধন-সম্পত্তি তাদেরকে সমর্পণ কর; ইয়াতীমের ধন-সম্পদ অপব্যয় করনা অথবা তারা বয়ঃপ্রাপ্ত হবে বলে ওটা সত্ত্বরতা সহকারে আত্মসাৎ করনা; এবং দেখাশোনাকারী যদি অভাবমুক্ত হয় তাহলে ইয়াতীমের মাল খরচ করা হতে সে নিজকে সম্পূর্ণ বিরত রাখবে, আর যে ব্যক্তি অভাবগ্রস্ত সে সঙ্গত পরিমাণ ভোগ করবে, অনন্তর যখন তাদের সম্পত্তি তাদেরকে সমর্পণ করতে চাও তখন তাদের জন্য সাক্ষী রেখ এবং আল্লাহই হিসাব গ্রহণে যথেষ্ট।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (500,4,7,' পুরুষদের জন্য মাতা-পিতা ও আত্মীয় স্বজনের পরিত্যক্ত বিষয়ে অংশ রয়েছে- অল্প বা অধিক, তা নির্দিষ্ট পরিমাণ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (501,4,8,' বন্টনের সময়ে যখন স্বজনগণ, ইয়াতীমগণ এবং দরিদ্রগণ উপস্থিত হয় তখন তা হতে তাদেরকেও জীবিকা দান কর এবং তাদের সাথে সদ্ভাবে কথা বল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (502,4,9,'তাদের মনে এই ভেবে শংকা থাকা উচিৎ যে, যদি তারা মৃত্যুকালে তাদের পশ্চাতে অসহায় পরিবার রেখে যায় তাহলে তাদেরও একই অবস্থা ঘটতে পারে। সুতরাং তারা যেন আল্লাহকে ভয় করে এবং সঙ্গত কথা বলে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (503,4,10,' যারা অন্যায়ভাবে পিতৃহীনদের ধন সম্পত্তি গ্রাস করে, নিশ্চয়ই তা স্বীয় উদরে অগ্নি ব্যতীত কিছুই ভক্ষণ করেনা এবং সত্ত্বরই তারা অগ্নি শিখায় প্রবেশ করবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (504,4,11,' আল্লাহ তোমাদের সন্তানদের সম্বন্ধে তোমাদেরকে নির্দেশ দিচ্ছেনঃ এক পুত্রের জন্য দুই কন্যার অংশের তুল্য; আর যদি শুধু কন্যাগণ দুই জনের অধিক হয় তাহলে তারা মৃত ব্যক্তির পরিত্যক্ত সম্পত্তি হতে দুই তৃতীয়াংশ প্রাপ্ত হবে। আর যদি একটি মাত্র কন্যা হয় তাহলে সে অর্ধেকাংশ প্রাপ্ত হবে; এবং যদি মৃত ব্যক্তির কোন সন্তান থাকে তাহলে মাতা-পিতার জন্য অর্থাৎ উভয়ের প্রত্যেকেরই জন্য তার পরিত্যক্ত সম্পত্তি হতে এক ষষ্ঠাংশ রয়েছে, আর যদি তার কোন সন্তান না থাকে এবং শুধু মাতা-পিতাই তার উত্তরাধিকারী হয় তাহলে তার মাতার জন্য রয়েছে এক তৃতীয়াংশ এবং যদি তার ভাই থাকে তাহলে সে যা নির্দেশ করে গেছে সেই নির্দেশ ও ঋণ অন্তে তার জননীর জন্য এক ষষ্ঠাংশ; তোমাদের পিতা ও তোমাদের পুত্রের মধ্যে কে তোমাদের অধিকতর উপকারী তা তোমরা অবগত নও, এটাই আল্লাহর নির্দেশ। নিশ্চয়ই আল্লাহ মহাজ্ঞানী ও বিজ্ঞানময়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (505,4,12,' আর তোমাদের পত্নীগণের যদি সন্তান-সন্ততি না থাকে তাহলে তারা যা পরিত্যাগ করে যায় তা থেকে তোমাদের জন্য উহার অর্ধাংশ; কিন্তু যদি তাদের সন্তান-সন্ততি থাকে তাহলে তারা যা অসীয়াত করেছে সেই অসীয়াত ও ঋণ পরিশোধের পর তাদের পরিত্যক্ত সম্পত্তি হতে তোমাদের জন্য এক চতুর্থাংশ; এবং যদি তোমাদের স্ত্রীদের কোন সন্তান-সন্ততি না থাকে তাহলে তোমরা যা পরিত্যাগ করে যাবে, তাদের জন্য তার এক চতুর্থাংশ; কিন্তু যদি তোমাদের সন্তুান-সন্ততি থাকে তাহলে তোমরা যা অসীয়াত করবে সেই অসীয়াত ও ঋণ পরিশোধের পর তোমাদের পরিত্যক্ত সম্পত্তি হতে তাদের জন্য এক অষ্টমাংশ; যদি কোন মূল ও শাখাবিহীন পুরুষ কিংবা স্ত্রী মারা যায় এবং তার এক ভাই অথবা এক ভগ্নী থাকে তাহলে এতদুভয়ের মধ্য হতে প্রত্যেকেই এক ষষ্ঠাংশ পাবে, আর যদি তারা তদপেক্ষা অধিক হয় তাহলে কৃত অসীয়াত পূরণ করার পর অথবা ঋণ শোধের পর, কারও অনিষ্ট না করে তারা উহার এক তৃতীয়াংশ প্রাপ্ত হবে; এটাই আল্লাহর নির্দেশ এবং আল্লাহ মহাজ্ঞানী, সহিষ্ণু।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (506,4,13,' এটাই আল্লাহর নির্দিষ্ট সীমাসমূহ; এবং যে কেহ আল্লাহ ও তদীয় রাসূলের অনুগত হয় তিনি তাকে এরূপ জান্নাতে প্রবিষ্ট করাবেন যার নিম্নে স্রোতস্বিনীসমূহ প্রবাহিতা, তম্মধ্যে তারা সদা অবস্থান করবে; এবং এটাই বড় সফলতা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (507,4,14,' আর যে কেহ আল্লাহ ও তদীয় রাসূলকে অমান্য করে এবং তাঁর নির্দিষ্ট সীমাসমূহ অতিক্রম করে তিনি তাকে জাহান্নামে প্রবেশ করাবেন। তন্মধ্যে সে সদা অবস্থান করবে এবং তার জন্য লাঞ্ছনাদায়ক শাস্তি রয়েছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (508,4,15,' আর তোমাদের নারীদের মধ্যে যারা নির্লজ্জ কাজ করে, তোমরা তাদের বিরুদ্ধে তোমাদের মধ্য হতে চার জন সাক্ষী উপস্থিত কর; অনন্তর যদি তারা সাক্ষ্য প্রদান করে তাহলে তাদেরকে তোমরা গৃহসমূহের মধ্যে আবদ্ধ করে রাখ যে পর্যন্ত মৃত্যু তাদেরকে উঠিয়ে না নেয় কিংবা আল্লাহ তাদের জন্য কোন পথ নির্দেশ না করেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (509,4,16,'আর তোমাদের মধ্য হতে যে কোন দুইজন এই নির্লজ্জতার কাজ করবে, তাদের উভয়কেই শাস্তি প্রদান কর; কিন্তু তারা যদি তাওবাহ করে এবং সদাচারী হয় তাহলে এতদুভয় হতে প্রত্যাবর্তিত হও। নিশ্চয়ই আল্লাহ তাওবাহ গ্রহণকারী, করুণাময়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (510,4,17,' তাওবাহ কবূল করার দায়িত্ব যে আল্লাহর উপর রয়েছে তাতো শুধু তাদেরই জন্য যারা অজ্ঞতা বশতঃ পাপ করে থাকে, অতঃপর অবিলম্বে ক্ষমা প্রার্থনা করে; সুতরাং আল্লাহ তাদেরকেই ক্ষমা করবেন; আল্লাহ মহাজ্ঞানী, বিজ্ঞানময়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (511,4,18,'আর তাদের জন্য কোন ক্ষমা নেই যারা ঐ পর্যন্ত পাপ করতে থাকে যখন তাদের কারও নিকট মৃত্যু উপস্থিত হয় তখন বলে, নিশ্চয়ই আমি এখন ক্ষমা প্রার্থনা করছি এবং তাদের জন্যও ক্ষমা নয় যারা অবিশ্বাসী অবস্থায় মৃত্যুমুখে পতিত হয়েছে; তাদেরই জন্য আমি বেদনাদায়ক শাস্তি প্রস্তুত করে রেখেছি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (512,4,19,' হে মু’মিনগণ! এটা তোমাদের জন্য বৈধ নয় যে, তোমরা বলপূর্বক নারীদের উত্তরাধিকারী হও; এবং প্রকাশ্য অশ্লীলতা ব্যতীত তোমরা তাদেরকে যা প্রদান করেছ উহার কিয়দংশ গ্রহণের জন্য তাদেরকে বাধ্য করনা এবং তাদের সাথে সদ্ভাবে সম মর্যাদায় অবস্থান কর; কিন্তু যদি অপছন্দ কর তাহলে তোমরা যে বিষয় অপছন্দ কর আল্লাহ সেটাকে প্রচুর কল্যাণকর করতে পারেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (513,4,20,' আর যদি তোমরা এক স্ত্রীর স্থলে অন্য স্ত্রী পরিবর্তন করতে ইচ্ছা কর এবং তাদের একজনকে প্রচুর ধন-সম্পদ প্রদান করে থাক তাহলে তোমরা তন্মধ্য হতে কিছুই প্রতিগ্রহণ করনা; তাহলে কি তোমরা অপবাদ প্রয়োগ এবং প্রকাশ্য পাপ করে সেটা গ্রহণ করবে?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (514,4,21,' এবং কিরূপে তোমরা তা গ্রহণ করবে? বস্তুতঃ তোমরা পরস্পর একে অন্যের নিকট গমন করেছিলে এবং তারা তোমাদের নিকট হতে সুদৃঢ় অঙ্গীকার গ্রহণ করেছিল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (515,4,22,' তোমাদের পিতৃগণ নারীকূলের মধ্যে যাদেরকে বিয়ে করেছে তোমরা তাদেরকে বিয়ে করনা, কিন্তু যা বিগত হয়েছে। নিশ্চয়ই এটি অশ্লীল, অরুচিকর এবং নিকৃষ্ট আচরণ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (516,4,23,' তোমাদের জন্য অবৈধ করা হয়েছে - তোমাদের মাতৃগণ, কন্যাগণ, ভগ্নিগণ, ফুফুগণ, খালাগণ, ভ্রাতৃকন্যাগণ, ভগ্নির কন্যাগণ, তোমাদের সেই মাতৃগণ যারা তোমাদেরকে স্তন্য দান করেছে, তোমাদের দুগ্ধ ভগ্নিগণ, তোমাদের স্ত্রীদের মাতৃগণ, তোমরা যাদের অভ্যন্তরে উপনীত হয়েছ সেই স্ত্রীদের যে সকল কন্যা তোমাদের ক্রোড়ে অবস্থিত; কিন্তু যদি তোমরা তাদের মধ্যে উপনীত না হয়ে থাক তাহলে তোমাদের জন্য কোন অপরাধ নেই; এবং ঔরসজাত পুত্রদের পত্নীগণ; এবং যা অতীত হয়ে গেছে, তদ্ব্যতীত দুই ভগ্নিকে একত্রে বিয়ে করা; নিশ্চয়ই আল্লাহ ক্ষমাশীল, করুণাময়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (517,4,24,'এবং নারীদের মধ্যে বিবাহিতগণ তোমাদের জন্য নিষিদ্ধ করা হয়েছে; কিন্তু তোমাদের ডান হাত যাদের অধিকারী - আল্লাহ তোমাদের জন্য তাদেরকে বিধিবদ্ধ করেছেন, এতদ্ব্যতীত তোমাদের জন্য বৈধ করা হয়েছে অন্যান্য নারীদের; তোমরা স্বীয় ধনের দ্বারা ব্যভিচারের উদ্দেশ্য ব্যতীত বিবাহ করার জন্য তাদের অনুসন্ধান কর; অনন্তর তাদের দ্বারা যে ফল ভোগ করবে তজ্জন্য তাদেরকে তাদের নির্ধারিত দেয় প্রদান কর এবং কোন অপরাধ হবেনা যদি নির্ধারণের পর তোমরা পরস্পর সম্মত হও, নিশ্চয়ই আল্লাহ মহাজ্ঞানী, বিজ্ঞানময়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (518,4,25,'আর তোমাদের মধ্যে যে ব্যক্তি স্বাধীনা ও মুসলিম নারীকে বিয়ে করার সামর্থ্য রাখেনা তাহলে তোমাদের ডান হাত অধিকারী সেই বিশ্বাসিনী দাসীকে বিয়ে করে। আল্লাহ তোমাদের বিশ্বাস বিষয়ে জ্ঞাত আছেন, তোমরা একে অপর হতে সমুদ্ভুত। অতএব তাদের মনিবদের অনুমতিক্রমে তাদেরকে বিয়ে কর এবং নিয়ম অনুযায়ী তাদেরকে তাদের প্রাপ্য (মোহরানা) প্রদান কর এমতাবস্থায় যে, তারা ব্যভিচারিণী কিংবা উপ-পতি গ্রহণকারিণী হবেনা। অতঃপর যখন তারা বিবাহবদ্ধ হয়, তৎপর যদি তারা ব্যভিচার করে তাহলে তাদের প্রতি স্বাধীনা নারীদের শাস্তির অর্ধেক, এটা তাদেরই জন্য তোমাদের মধ্যে যারা দুস্কার্যকে ভয় করে। এবং যদি বিরত থাক তাহলে এটা তোমাদের জন্য কল্যাণকর এবং আল্লাহ ক্ষমাশীল, করুণাময়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (519,4,26,' আল্লাহ তোমাদের জন্য সবকিছু পরিস্কার বর্ণনা করতে এবং তোমাদেরকে তোমাদের পূর্ববর্তীগণের আদর্শসমূহ প্রদর্শন করতে ও তোমাদেরকে ক্ষমা করতে ইচ্ছা করেন এবং আল্লাহ মহাজ্ঞানী, বিজ্ঞানময়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (520,4,27,' আর আল্লাহ তোমাদেরকে ক্ষমা করতে ইচ্ছা করেন এবং যারা প্রবৃত্তির পূজারী তারা ইচ্ছা করে যে, তোমরা ঘোর অধঃপতনে পতিত হও।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (521,4,28,' আল্লাহ তোমাদের বোঝা লঘু করতে চান যেহেতু মানুষ দুর্বল (রূপে) সৃষ্ট হয়েছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (522,4,29,'হে মু’মিনগণ! তোমরা অন্যায়ভাবে পরস্পরের ধন-সম্পদ গ্রাস করনা; কেবলমাত্র পরস্পর সম্মতিক্রমে যে ব্যবসা কর তা বৈধ এবং তোমরা নিজেদের হত্যা করনা; নিশ্চয়ই আল্লাহ তোমাদের প্রতি ক্ষমাশীল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (523,4,30,'আর যে কেহ সীমা অতিক্রম করে অথবা যলমের বশবর্তী হয়ে এ কাজ করে, ফলতঃ নিশ্চয়ই আমি তাকে জাহান্নামে নিক্ষেপ করব এবং আল্লাহর পক্ষে এটা খুবই সহজসাধ্য।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (524,4,31,' তোমরা যদি সেই বড় বড় পাপসমূহ হতে বিরত হও যা তোমাদেরকে নিষেধ করা হয়েছে, তাহলেই আমি তোমাদের ক্রটি বিচ্যুতিগুলি ক্ষমা করে দিব এবং তোমাদেরকে সম্মানজনক স্থানে প্রবিষ্ট করাব।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (525,4,32,'এবং তোমরা ওর আকাংখা করনা যদ্বারা আল্লাহ একের উপর অপরকে শ্রেষ্ঠত্ব দান করেছেন; পুরুষেরা যা উপার্জন করেছে তাতে তাদের অংশ রয়েছে এবং নারীরা যা উপার্জন করেছে তাতে তাদের অংশ রয়েছে এবং তোমরা আল্লাহরই নিকট অনুগ্রহ প্রার্থনা কর, নিশ্চয়ই আল্লাহ সর্ব বিষয়ে মহাজ্ঞানী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (526,4,33,'আমি সবাইকেই উত্তরাধিকারী করেছি যা তাদের মাতা-পিতা ও আত্মীয়-স্বজন পরিত্যাগ করে যায় এবং দক্ষিণ হস্ত যাদের সাথে প্রতিজ্ঞাবদ্ধ; অতএব তোমরা তাদেরকে তাদের অংশ প্রদান কর; নিশ্চয়ই আল্লাহ সব বিষয়ে সাক্ষী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (527,4,34,' পুরুষেরা নারীদের উপর তত্ত্বাবধানকারী ও ভরণপোষণকারী, যেহেতু আল্লাহ তাদের মধ্যে একের উপর অপরকে বৈশিষ্ট্য দান করেছেন এবং এই হেতু যে, তারা স্বীয় ধন সম্পদ হতে তাদের জন্য ব্যয় করে থাকে; সুতরাং যে সমস্ত নারী পুণ্যবতী তারা আনুগত্য করে, আল্লাহর সংরক্ষিত প্রচ্ছন্ন বিষয় সংরক্ষণ করে; যদি নারীদের অবাধ্যতার আশংকা হয় তাহলে তাদেরকে সদুপদেশ প্রদান কর, তাদেরকে শয্যা হতে পৃথক কর এবং তাদেরকে প্রহার কর; অনন্তর যদি তারা তোমাদের অনুগত হয় তাহলে তাদের জন্য অন্য পন্থা অবলম্বন করনা; নিশ্চয়ই আল্লাহ সমুন্নত, মহা মহীয়ান।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (528,4,35,'আর যদি তোমরা উভয়ের মধ্যে বিচ্ছেদের আশংকা কর তাহলে তোমাদের বংশ হতে একজন বিচারক এবং তোমাদের স্ত্রীদের বংশ হতে একজন বিচারক নির্দিষ্ট কর; যদি তারা মীমাংসার আকাংখা করে তাহলে আল্লাহ তাদের মধ্যে সম্প্রীতি সঞ্চার করবেন; নিশ্চয়ই আল্লাহ মহাজ্ঞানী, অভিজ্ঞ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (529,4,36,' এবং তোমরা আল্লাহরই ইবাদাত কর এবং তাঁর সাথে কোন বিষয়ে অংশী স্থাপন করনা; এবং মাতা-পিতার সাথে সদ্ব্যবহার কর এবং আত্মীয়-স্বজন, পিতৃহীন, দরিদ্র, সম্পর্কবিহীন প্রতিবেশী, পার্শ্ববতী সহচর ও পথিক এবং তোমাদের দাস-দাসীদের সাথেও সদ্ব্যবহার কর; নিশ্চয়ই আল্লাহ অহংকারী আত্মাভিমানীকে ভালবাসেননা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (530,4,37,'যারা কৃপণতা করে ও লোকদের কার্পণ্য শিক্ষা দেয় এবং আল্লাহ স্বীয় অনুগ্রহে যা দান করেছেন তা গোপন করে, বস্তুতঃ আমি সেই অবিশ্বাসীদের জন্য অপমানজনক শাস্তি প্রস্তুত করে রেখেছি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (531,4,38,' এবং যারা লোকদের দেখানোর জন্য স্বীয় ধন সম্পদ ব্যয় করে এবং আল্লাহর প্রতি ও আখিরাতের প্রতি বিশ্বাস স্থাপন করেনা, আর যাদের সহচর শাইতান - সে নিকৃষ্ট সঙ্গীই বটে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (532,4,39,' আর এতে তাদের কি ক্ষতি হত - যদি তারা আল্লাহ ও আখিরাতের প্রতি বিশ্বাস স্থাপন করত এবং আল্লাহ তাদেরকে যা দান করেছেন তা হতে ব্যয় করত? এবং আল্লাহ তাদের বিষয়ে যথার্থই অবগত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (533,4,40,' নিশ্চয়ই আল্লাহ অণু পরিমাণও অত্যাচার করেননা এবং যদি কেহ কোন সৎ কাজ করে তাহলে তিনি ওটা দ্বিগুণ করে দেন এবং স্বীয় পক্ষ হতে ওর মহান প্রতিদান প্রদান করেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (534,4,41,' অনন্তর তখন কি দশা হবে যখন আমি প্রত্যেক ধর্ম/সম্প্রদায় হতে সাক্ষী আনয়ন করব এবং তোমাকেই তাদের প্রতি সাক্ষী করব?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (535,4,42,' যারা অবিশ্বাসী হয়েছে ও রাসূলের বিরুদ্ধাচরণ করেছে তারা সেদিন কামনা করবে - যেন ভূমন্ডলের সাথে তারা মিশে যায় এবং আল্লাহর নিকট তারা কোন কথাই গোপন করতে পারবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (536,4,43,'হে মু’মিনগণ! নেশাগ্রস্ত অবস্থায় সালাতের কাছেও যেওনা, যখন নিজের উচ্চারিত বাক্যের অর্থ নিজেই বুঝতে সক্ষম নও - এ অবস্থায় অথবা গোসল যরুরী হলে তা সমাপ্ত না করে সালাতের জন্য দান্ডায়মান হয়োনা। কিন্তু মুসাফির অবস্থার কথা স্বতন্ত্র। যদি তোমরা পীড়িত হও, কিংবা প্রবাসে অবস্থান কর অথবা তোমাদের মধ্যে কেহ পায়খানা হতে প্রত্যাগত হয় কিংবা রমণী স্পর্শ করে এবং পানি না পাওয়া যায় তাহলে বিশুদ্ধ মাটির অম্বেষণ কর, তদ্বারা তোমাদের মুখমন্ডল ও হস্তসমূহ মুছে ফেল; নিশ্চয়ই আল্লাহ মার্জনাকারী, ক্ষমাশীল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (537,4,44,'তোমরা কি তাদের প্রতি লক্ষ্য করনি যাদেরকে গ্রন্থের এক অংশ প্রদত্ত হয়েছে? তারা পথভ্রষ্টতা ক্রয় করেছে এবং ইচ্ছা করে যে, তোমরাও সঠিক পথ হতে দূরে সরে যাও।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (538,4,45,' এবং আল্লাহ তোমাদের শত্রুদেরকে সম্যক অবগত আছেন; পৃষ্ঠপোষক এবং সাহায্যকারী হিসাবে আল্লাহই যথেষ্ট।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (539,4,46,'ইয়াহুদীদের মধ্যে কেহ কেহ যথাস্থান হতে বাক্যাবলী পরিবতির্ত করে এবং বলেঃ আমরা শ্রবণ করলাম ও আগ্রহ্য করলাম; এবং বলে, শোন - না শোনার মত; এবং তারা স্বীয় জিহবা বিকৃত করে ও ধর্মের প্রতি দোষারোপ করে বলে ‘রাইনা’; এবং যদি তারা বলত,  ‘আমরা শুনলাম ও মেনে নিলাম; এবং আমাদেরকে বুঝার শক্তি দাও’ তাহলে এটা তাদের পক্ষে উত্তম ও সঠিক হত; কিন্তু আল্লাহ তাদের অবিশ্বাস হেতু তাদেরকে অভিসম্পাত করেছেন; অতএব অল্প সংখ্যক ব্যতীত তারা বিশ্বাস করেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (540,4,47,'হে গ্রন্থ প্রাপ্তরা! তোমাদের সঙ্গে যা আছে তার সত্যতা প্রতিপাদনকারী যা অবতীর্ণ করেছি তৎপ্রতি বিশ্বাস স্থাপন কর ঐ সময় আসার পূর্বে, যখন আমি অনেক মুখমন্ডল বিকৃত করে দিব। অতঃপর তাদেরকে পৃষ্ঠের দিকে উল্টিয়ে দিব অথবা আসহাবে সাবতের প্রতি যেরূপ অভিসম্পাত করেছিলাম তদ্রুপ তাদের প্রতিও অভিসম্পাত করি; এবং আল্লাহর আদেশ কার্যকরী হয়েই থাকে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (541,4,48,' নিশ্চয়ই আল্লাহ তাঁর সাথে অংশী স্থাপনকারীকে ক্ষমা করবেননা এবং তদ্ব্যতীত যাকে ইচ্ছা ক্ষমা করবেন এবং যে কেহ আল্লাহর অংশী স্থির করে সে মহাপাপে আবদ্ধ হল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (542,4,49,'তুমি কি তাদের প্রতি লক্ষ্য করনি যারা স্বীয় পবিত্রতা প্রকাশ করে? বরং আল্লাহ যাকে ইচ্ছা পবিত্র করেন এবং তারা এক সূতা পরিমাণও অত্যাচারিত হবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (543,4,50,' লক্ষ্য করঃ তারা কিরূপে আল্লাহর প্রতি মিথ্যা অপবাদ দিচ্ছে! স্পষ্ট অপরাধী হওয়ার জন্য এটাই যথেষ্ট।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (544,4,51,'তুমি কি তাদের প্রতি লক্ষ্য করনি যাদেরকে গ্রন্থের একাংশ প্রদত্ত হয়েছে? তারা মূর্তি ও শাইতানের প্রতি বিশ্বাস স্থাপন করে এবং কাফিরদেরকে বলে, মুসলিমদের অপেক্ষা তারাই অধিকতর সুপথগামী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (545,4,52,'এদেরই প্রতি আল্লাহ অভিসম্পাত করেছেন; এবং আল্লাহ যাকে অভিসম্পাত করেন, তুমি তার জন্য কোনই সাহায্যকারী খুঁজে পাবেনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (546,4,53,' তাহলে কি রাজত্বে তাদের কোন অংশ রয়েছে? বস্তুতঃ তখন তারা লোকদেরকে কণা পরিমাণও প্রদান করবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (547,4,54,'তাহলে কি তারা লোকদের প্রতি এ জন্য হিংসা করে যে, আল্লাহ তাদেরকে স্বীয় সম্পদ হতে কিছু দান করেছেন? ফলতঃ নিশ্চয়ই আমি ইবরাহীম বংশীয়গণকে গ্রন্থ ও বিজ্ঞান দান করেছি এবং তাদেরকে বিশাল সাম্রাজ্য প্রদান করেছি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (548,4,55,' অনন্তর তাদের মধ্যে অনেকে ওর প্রতি বিশ্বাস স্থাপন করেছে এবং অনেকেই ওটা হতে বিরত রয়েছে; এবং (তাদের জন্য) শিখা বিশিষ্ট জাহান্নামই যথেষ্ট।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (549,4,56,' যারা আমার নিদর্শনসমূহের প্রতি অবিশ্বাসী হয়েছে তাদেরকে অবশ্যই আমি জাহান্নামের আগুনে প্রবেশ করাব। যখন তার চর্ম বিদগ্ধ হবে, আমি তৎপরিবর্তে তাদের চর্ম পরিবর্তন করে দিব, যেন তারা শাস্তির আস্বাদ গ্রহণ করে, নিশ্চয়ই আল্লাহ পরাক্রান্ত, হিকমাতের অধিকারী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (550,4,57,' আর যারা বিশ্বাস স্থাপন করে সৎ কার্যাবলী সম্পাদন করেছে, নিশ্চয়ই আমি তাদেরকে জান্নাতে প্রবেশ করাব, যার নিম্নে স্রোতস্বিনীসমূহ প্রবাহিতা, তন্মধ্যে তারা চিরকাল অবস্থান করবে; সেখানে তাদের জন্য পবিত্রা সহধর্মিণীগণ রয়েছে এবং আমি তাদেরকে সুবিস্তৃত ছায়া শীতল স্থানে প্রবেশ করাব।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (551,4,58,' নিশ্চয়ই আল্লাহ তোমাদেরকে আদেশ করছেন, গচ্ছিত বিষয় ওর অধিকারীকে অর্পণ কর; এবং যখন তোমরা লোকদের মধ্যে বিচার মীমাংসা কর তখন ন্যায় বিচার কর; অবশ্যই আল্লাহ তোমাদেরকে উত্তম উপদেশ দান করছেন; নিশ্চয়ই আল্লাহ শ্রবণকারী, পরিদর্শক।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (552,4,59,'হে মু’মিনগণ! তোমরা আল্লাহর ও রাসূলের অনুগত হও এবং তোমাদের জন্য যারা বিচারক তাদের; অতঃপর যদি তোমাদের মধ্যে কোন বিষয়ে কোন মতবিরোধ হয় তাহলে আল্লাহ ও রাসূলের দিকে প্রত্যাবর্তিত হও, যদি তোমরা আল্লাহ ও পরকালের প্রতি বিশ্বাস করে থাক; এটাই কল্যাণকর ও শ্রেষ্ঠতর পরিসমাপ্তি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (553,4,60,' তুমি কি তাদের প্রতি লক্ষ্য করনি যারা দাবী করে যে, তোমার প্রতি যা অবতীর্ণ হয়েছে এবং তোমার পূর্বে যা অবতীর্ণ হয়েছিল তৎপ্রতি তারা বিশ্বাস করে? অথচ তারা নিজেদের মুকাদ্দামা তাগুতের নিকট নিয়ে যেতে চায়, যদিও তাদেরকে আদেশ করা হয়েছিল যেন তাকে অবিশ্বাস করে; পক্ষান্তরে শাইতান তাদেরকে প্রতারিত করে পথভ্রষ্ট করতে চায়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (554,4,61,' আর যখন তাদেরকে বলা হয় যে, আল্লাহ যা অবতীর্ণ করেছেন সেই দিকে এবং রাসূলের দিকে এসো তখন তুমি মুনাফিকদেরকে দেখবে, তারা তোমা হতে মুখ ফিরিয়ে বিচ্ছিন্ন হচ্ছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (555,4,62,' অনন্তর তখন কিরূপ হবে, যখন তাদের হস্তসমূহ যা পূর্বে প্রেরণ করেছে, তজ্জন্য তাদের উপর বিপদ উপনীত হয়? তখন তারা আল্লাহর শপথ করতে করতে তোমার কাছে এসে বলবে - কল্যাণ ও সম্প্রীতি ব্যতীত আমরা কিছুই কামনা করিনি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (556,4,63,'তাদের অন্তরসমূহে যা আছে আল্লাহ তা জ্ঞাত আছেন। অতএব তুমি তাদের দিক হতে নির্লিপ্ত হও এবং তাদেরকে সদুপদেশ প্রদান কর এবং তাদেরকে তাদের সম্বন্ধে হৃদয়স্পর্শী কথা বল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (557,4,64,' আমি এতদ্ব্যতীত কোনই রাসূল প্রেরণ করিনি যে, আল্লাহর আদেশে তাঁর আনুগত্য স্বীকার করবে, এবং যদি তারা স্বীয় জীবনের উপর অত্যাচার করার পর তোমার নিকট আগমন করত, অতঃপর আল্লাহর নিকট ক্ষমা প্রার্থনা করত আর রাসূলও তাদের জন্য আল্লাহর নিকট ক্ষমা চাইত তাহলে নিশ্চয়ই তারা আল্লাহকে তাওবাহ কবূলকারী, করুণাময় দেখতে পেত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (558,4,65,'অতএব তোমার রবের শপথ! তারা কখনই বিশ্বাস স্থাপনকারী হতে পারবেনা, যে পর্যন্ত তোমাকে তাদের সৃষ্ট বিরোধের বিচারক না করে, অতঃপর তুমি যে বিচার করবে তা দ্বিধাহীন অন্তরে গ্রহণ না করে এবং ওটা সন্তষ্ট চিত্তে কবূল না করে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (559,4,66,' আর আমি যদি তাদের উপর বিধিবদ্ধ করতাম যে, তোমরা নিজেদেরকে হত্যা কর অথবা স্বীয় গৃহ প্রাচীর হতে নিস্ক্রান্ত হও তাহলে তাদের অল্প সংখ্যক ব্যতীত ওটা করতনা এবং যদ্বিষয়ে তাদেরকে উপদেশ দেয়া হয়েছিল তা যদি তারা করত তাহলে নিশ্চয়ই ওটা হত তাদের জন্য কল্যাণকর এবং ধর্মের উপর সুদৃঢ় থাকার জন্যও।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (560,4,67,'এবং তখন আমি তাদেরকে অবশ্যই স্বীয় সন্নিধান হতে বৃহত্তর প্রতিদান প্রদান করতাম।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (561,4,68,'এবং নিশ্চয়ই তাদেরকে সরল পথ প্রদর্শন করতাম।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (562,4,69,' আর যে কেহ আল্লাহ ও রাসূলের অনুগত হয়, তারা ঐ ব্যক্তিদের সঙ্গী হবে যাদের প্রতি আল্লাহ অনুগ্রহ করেছেন; অর্থাৎ নাবীগণ, সত্য সাধকগণ, শহীদগণ ও সৎ কর্মশীলগণ; এবং এরাই সর্বোত্তম সঙ্গী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (563,4,70,' এটাই আল্লাহর অনুগ্রহ এবং আল্লাহর জ্ঞানই যথেষ্ট।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (564,4,71,' হে মু’মিনগণ! তোমরা স্বীয় আত্মরক্ষার সরঞ্জাম তুলে নাও, অতঃপর পৃথকভাবে বহির্গত হও অথবা সম্মিলিতভাবে অভিযান কর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (565,4,72,' আর তোমাদের মধ্যে এরূপ লোকও রয়েছে যে শৈথিল্য করে; অনন্তর যদি তোমাদের উপর বিপদ নিপতিত হয় তাহলে বলেঃ আমার প্রতি আল্লাহর অনুগ্রহ ছিল যে, আমি তখন তাদের সাথে উপস্থিত ছিলামনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (566,4,73,' পক্ষান্তরে যদি আল্লাহর সন্নিধান হতে তোমাদের উপর অনুগ্রহ, সম্পদ অবতীর্ণ হয় তাহলে এরূপভাবে বলে যেন তোমাদের ও তাদের মধ্যে কোনই সম্বন্ধ ছিলনা - হায়! যদি আমিও তাদের সঙ্গী হতাম তাহলে মহান ফলপ্রদ সুফল লাভ করতাম!');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (567,4,74,' অতএব যারা দুনিয়ার বিনিময়ে আখিরাত ক্রয় করে তারা যেন আল্লাহর পথে সংগ্রাম করে; এবং যে আল্লাহর পথে যুদ্ধ করে, অতঃপর নিহত অথবা বিজয়ী হয়, তাহলে আমি তাকে মহান প্রতিদান প্রদান করব।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (568,4,75,' তোমাদের কি হয়েছে যে, তোমরা আল্লাহর পথে যুদ্ধ করছনা? অথচ নারী, পুরুষ এবং শিশুদের মধ্যে যারা দুর্বল তারা বলেঃ হে আমাদের রাব্ব! আমাদেরকে অত্যাচারী এই নগর হতে নিস্কৃতি দিন এবং স্বীয় সন্নিধান হতে আমাদের পৃষ্ঠপোষক ও নিজের নিকট হতে আমাদের জন্য সাহায্যকারী প্রেরণ করুন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (569,4,76,' যারা বিশ্বাস স্থাপন করেছে তারা আল্লাহর পথে সংগ্রাম করে এবং যারা কাফির তারা শাইতানের পক্ষে যুদ্ধ করে; সুতরাং তোমরা শাইতানের বন্ধুদের বিরুদ্ধে যুদ্ধ কর; নিশ্চয়ই শাইতানের কৌশল দুর্বল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (570,4,77,'তুমি কি তাদের প্রতি লক্ষ্য করনি যাদেরকে বলা হয়েছিল যে, তোমাদের হস্তসমূহ সংযত রাখ এবং সালাত প্রতিষ্ঠিত কর ও যাকাত প্রদান কর। অনন্তর যখন তাদের প্রতি জিহাদ ফরয করে দেয়া হল তখন তাদের একদল আল্লাহকে যেরূপ ভয় করবে তদ্রুপ মানুষকে ভয় করতে লাগল, বরং তদপেক্ষাও অধিক; এবং তারা বললঃ হে আমাদের রাব্ব! আপনি কেন আমাদের উপর যুদ্ধ ফরয করলেন? কেন আমাদেরকে আর কিছুকালের জন্য অবসর দিলেননা? তুমি বলঃ পার্থিব আনন্দ খুবই সীমিত এবং ধর্মভীরুগণের জন্য পরকালই কল্যাণকর; এবং তোমরা খেজুর বীজ পরিমাণও অত্যাচারিত হবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (571,4,78,' তোমরা যেখানেই থাক না কেন, মৃত্যু তোমাদেরকে পেয়ে যাবে, যদিও তোমরা সুদৃঢ় দুর্গে অবস্থান কর; এবং যদি তাদের উপর কোন কল্যাণ অবতীর্ণ হয় তাহলে তারা বলেঃ এটা আল্লাহর নিকট হতে এবং যদি তাদের প্রতি অমঙ্গল নিপতিত হয় তাহলে বলে যে, এটা তোমার নিকট হতে হয়েছে। তুমি বলঃ সমস্তই আল্লাহর নিকট হতে হয়; অতএব ঐ সম্প্রদায়ের কি হয়েছে যে, তারা কোন কথা বুঝতে চেষ্টা করেনা!');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (572,4,79,' তোমার নিকট যে কল্যাণ উপস্থিত হয় তা আল্লাহর সন্নিধান হতে এবং তোমার উপর যে অকল্যাণ নিপতিত হয় তা তোমার নিজ হতে হয়ে থাকে, এবং আমি তোমাকে মানবমন্ডলীর জন্য রাসূল রূপে প্রেরণ করেছি; এবং আল্লাহর সাক্ষীই যথেষ্ট।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (573,4,80,' যে কেহ রাসূলের অনুগত হয় নিশ্চয়ই সে আল্লাহরই অনুগত হয়ে থাকে, এবং যে ফিরে যায় আমি তার জন্য তোমাকে রক্ষক রূপে প্রেরণ করিনি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (574,4,81,' আর তারা বলেঃ আমরা অনুগত। কিন্তু যখন তারা তোমার নিকট হতে বের হয়ে যায় তখন তাদের একদল, তুমি যা বল তার বিরুদ্ধে পরামর্শ করে; এবং তারা যা পরামর্শ করে আল্লাহ তা লিপিবদ্ধ করেন; অতএব তাদের প্রতি নিস্পৃহ হও এবং আল্লাহর উপর নির্ভর কর; এবং আল্লাহই কার্য সম্পাদনে যথেষ্ট।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (575,4,82,' তারা কেন কুরআন সম্বন্ধে গবেষনা করেনা? আর যদি ওটা আল্লাহ ব্যতীত অন্য কারও নিকট হতে হত তাহলে তারা ওতে বহু বৈপরিত্য দেখতে পেত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (576,4,83,'আর যখন তাদের নিকট কোন শাস্তি অথবা ভীতিজনক বিষয় উপস্থিত হয় তখন তারা ওটা রটনা করতে থাকে এবং যদি তারা ওটা রাসূলের কিংবা তাদের আদেশ দাতাদের প্রতি সমর্পন করত তাহলে তাদের মধ্যে সঠিক তথ্য পেয়ে যেত এবং যদি তোমাদের প্রতি আল্লাহর অনুগ্রহ ও করুণা না হত তাহলে অল্প সংখ্যক ব্যতীত তোমরা শাইতানের অনুসরণ করতে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (577,4,84,' অতএব আল্লাহর পথে যুদ্ধ কর; তোমার নিজের ছাড়া তোমার উপর অন্য কারও ভার অর্পণ করা হয়নি এবং বিশ্বাসীদেরকে উদ্ধুদ্ধ কর; অচিরেই আল্লাহ অবিশ্বাসীদের সংগ্রাম প্রতিরোধ করবেন; এবং আল্লাহ সংগ্রামে সুদৃঢ় ও শাস্তি দানে কঠোর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (578,4,85,'যে কেহ সৎ সুপারিশ করবে সে ওর অংশ পাবে এবং যে কেহ অসৎ সুপারিশ করবে সেও ওর অংশ প্রাপ্ত হবে; এবং আল্লাহ সর্বশক্তিমান।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (579,4,86,' আর যখন তোমরা শুভাশীষে সম্ভাষিত হও তখন তোমরাও তা হতে শ্রেষ্ঠতর শুভ সম্ভাষণ কর অথবা ওটাই প্রত্যর্পণ কর; নিশ্চয়ই আল্লাহ সর্ব বিষয়ে হিসাব গ্রহণকারী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (580,4,87,'আল্লাহ, তিনি ব্যতীত কেহ উপাস্য নেই; নিশ্চয়ই এতে সন্দেহ নেই যে, তিনি তোমাদেরকে একত্রিত করবেন; এবং বাক্যে আল্লাহ অপেক্ষা কে বেশি সত্যপরায়ন?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (581,4,88,'অনন্তর তোমাদের কি হয়েছে যে, তোমরা মুনাফিকদের সম্বন্ধে দুই দল হলে? এবং তারা যা অর্জন করেছে তজ্জন্য আল্লাহ তাদেরকে প্রত্যাখ্যান করেছেন; আল্লাহ যাকে পথভ্রষ্ট করেন তুমি কি তাকে পথ প্রদর্শন করতে চাও? এবং আল্লাহ যাকে পথভ্রষ্ট করেছেন বস্তুতঃ তুমি তার জন্য কোনই পথ পাবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (582,4,89,' তারা ইচ্ছা করে যে, তারা যেরূপ কাফির তোমরাও যেন তদ্রুপ কাফির হয়ে যাও, যাতে তোমরাও তাদের সদৃশ হও। অতএব তাদের মধ্য হতে বন্ধু গ্রহণ করনা, যে পর্যন্ত না তারা আল্লাহর পথে দেশ ত্যাগ করে; অতঃপর যদি তারা প্রতিগমন করে তাহলে তাদেরকে ধর এবং যেখানে পাও তাদেরকে সংহার কর; এবং তাদের মধ্য হতে বন্ধু অথবা সাহায্যকারী গ্রহণ করনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (583,4,90,' কিন্তু তোমাদের মধ্যে ও তাদের মধ্যে সন্ধিবদ্ধ সম্প্রদায়ের সাথে যারা সম্মিলিত হয়, অথবা তোমাদের সাথে যুদ্ধ করতে সংকুচিত চিত্ত হয়ে যারা তোমাদের নিকট উপস্থিত হয়। যদি আল্লাহ ইচ্ছা করতেন তাহলে তোমাদের উপর তাদেরকে শক্তিশালী করতেন। তাহলে নিশ্চয়ই তারা তোমাদের সাথে যুদ্ধ করত। অতঃপর যদি তারা তোমাদের দিক হতে সন্ধি প্রার্থনা করে তাহলে আল্লাহ তাদের প্রতিকূলে তোমাদের জন্য কোন পন্থা রাখেননি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (584,4,91,' অচিরেই তুমি এরূপও প্রাপ্ত হবে, যারা তোমাদের দিক হতে ও স্বীয় সম্প্রদায় হতে শান্তির সাথে থাকতে ইচ্ছা করে, যখন তাদের বিরোধের প্রতি প্রলুব্ধ করানো হয় তখন তাতেই নিপতিত হয়; অনন্তর যদি তোমাদের দিক হতে নিবৃত্ত না হয় ও সন্ধি প্রার্থনা না করে এবং তাদের হস্তসমূহ সংযত না করে তাহলে তাদেরকে পাকড়াও কর এবং যেখানে পাও তাদেরকে সংহার কর; এদেরই জন্য আল্লাহ তোমাদেরকে প্রকাশ্য যুক্তি প্রমাণ দান করেছেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (585,4,92,' কোন মু’মিনের উচিত নয় যে, ভ্রম ব্যতীত কোন মু’মিনকে হত্যা করে; যে কেহ ভ্রম বশতঃ কোন মু’মিনকে হত্যা করে তাহলে সে জনৈক মু’মিন দাসকে মুক্ত করে দিবে এবং তার আত্মীয় স্বজনকে হত্যার বিনিময় সমর্পণ করবে; কিন্তু যদি তারা ক্ষমা করে দেয় এবং যদি সে তোমাদের শত্রু সম্প্রদায়ের অন্তর্গত ও মু’মিন হয় তাহলে জনৈক মু’মিন দাসকে মুক্তি দান করবে; এবং যদি সে তোমাদের মধ্যে ও তাদের মধ্যে সন্ধিবদ্ধ সম্প্রদায়ের অন্তর্গত হয় তাহলে তার স্বজনদেরকে হত্যার বিনিময় অর্পণ করবে এবং জনৈক মু’মিন দাসকে মুক্ত করবে; কিন্তু যদি সে ওটায় অক্ষম হয় তাহলে আল্লাহর নিকট হতে ক্ষমা প্রাপ্তির জন্য একাদিক্রমে দুই মাস সিয়াম পালন করবে। আল্লাহ মহাজ্ঞানী, বিজ্ঞানময়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (586,4,93,'আর যে কেহ ইচ্ছা করে কোন মু’মিনকে হত্যা করে তাহলে তার শাস্তি জাহান্নাম, তন্মধ্যে সে সদা অবস্থান করবে এবং আল্লাহ তার প্রতি ক্রুদ্ধ হয়েছেন ও তাকে অভিশপ্ত করেছেন এবং তার জন্য ভীষণ শাস্তি প্রস্তুত রেখেছেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (587,4,94,' হে মু’মিনগণ! যখন তোমরা আল্লাহর পথে বহির্গত হও তখন প্রত্যেক কাজ যাচাই করে নাও, এবং কেহ তোমাদেরকে ‘সালাম’ করলে তাকে বলনা যে, ‘তুমি মু’মিন নও’; তোমরা কি পার্থিব জীবনের সম্পদ অনুসন্ধান করছো? তাহলে আল্লাহর নিকট প্রচুর সম্পদ রয়েছে; প্রথমে তোমরা ঐরূপই ছিলে, অতঃপর আল্লাহ তোমাদের উপর অনুগ্রহ করেছেন। অতএব তোমরা স্থির করে নাও যে, তোমরা যা করছ নিশ্চয়ই আল্লাহ তদ্বিষয়ে অভিজ্ঞ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (588,4,95,'মু’মিনদের মধ্যে যারা কোন দুঃখ পীড়া ব্যতীতই গৃহে বসে থাকে, আর যারা স্বীয় ধন ও প্রাণ দ্বারা আল্লাহর পথে জিহাদ করে তারা সমান নয়; যারা ধন-প্রাণ দ্বারা জিহাদ করে আল্লাহ তাদেরকে গৃহে অবস্থানকারীদের উপর উচ্চ মর্যাদা দিয়েছেন; এবং সকলকেই আল্লাহ কল্যাণপ্রদ প্রতিশ্রুতি দান করেছেন। এবং উপবিষ্টদের উপর ধর্মযোদ্ধাগণকে মহান প্রতিদানে গৌরবান্বিত করেছেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (589,4,96,' স্বীয় সন্নিধান হতে পদমর্যাদা, ক্ষমা ও করুনা (দ্বারা গৌরবান্বিত করেছেন) এবং আল্লাহ ক্ষমাশীল, করুণাময়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (590,4,97,' যারা স্বীয় জীবনের প্রতি অত্যাচার করেছিল, মালাক/ফেরেশতা তাদের প্রাণ হরণ করে বলে, তোমরা কি অবস্থায় ছিলে? তারা বলে, আমরা দুনিয়ায় অসহায় ছিলাম; তারা বলে, আল্লাহর পৃথিবী কি প্রশস্ত ছিলনা যে, তন্মধ্যে তোমরা হিজরাত করতে? অতএব ওদেরই বাসস্থান জাহান্নাম এবং কত নিকৃষ্ট ঐ জায়গা!');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (591,4,98,' কিন্তু পুরুষ, নারী এবং শিশুদের মধ্যে অসহায়তা বশতঃ যারা কোন উপায় করতে পারেনা অথবা কোন পথ প্রাপ্ত হয়না।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (592,4,99,' ফলতঃ তাদেরই আশা আছে যে, আল্লাহ তাদেরকে ক্ষমা করবেন এবং আল্লাহ মার্জনাকারী, ক্ষমাশীল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (593,4,100,' আর যে কেহ আল্লাহর পথে দেশ ত্যাগ করে সে পৃথিবীতে বহু প্রশস্ত স্থান ও স্বচ্ছলতা প্রাপ্ত হবে। এবং যে কেহ গৃহ হতে বহির্গত হয়ে আল্লাহ ও রাসূলের উদ্দেশে দেশ ত্যাগ করে, অতঃপর সে মৃত্যুমুখে পতিত হয়, তাহলে নিশ্চয়ই এর প্রতিদান আল্লাহর উপর ন্যস্ত রয়েছে; এবং আল্লাহ ক্ষমাশীল, করুণাময়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (594,4,101,' আর যখন তোমরা ভূপৃষ্ঠে ভ্রমণ কর তখন সালাত সংক্ষেপ করলে তোমাদের কোন অপরাধ নেই, যদি তোমরা আশংকা কর যে, যারা অবিশ্বাসী তারা তোমাদেরকে বিব্রত করবে; নিশ্চয়ই কাফিরেরা তোমাদের প্রকাশ্য শত্রু।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (595,4,102,' এবং যখন তুমি তাদের (সৈন্যদের) মধ্যে থাক, অতঃপর সালাতে দন্ডায়মান হও তখন যেন তাদের একদল তোমার সাথে দন্ডায়মান হয় এবং স্ব স্ব অস্ত্র গ্রহণ করে; অতঃপর যখন সাজদাহ্ সম্পন্ন করে তখন যেন তারা তোমার পশ্চাদ্বর্তী হয়; এবং অন্য দল যারা সালাত আদায় করেনি তারা যেন অগ্রসর হয়ে তোমার সাথে সালাত আদায় করে এবং স্ব স্ব আত্মরক্ষিকা ও অস্ত্র গ্রহণ করে। অবিশ্বাসীরা ইচ্ছা করে যে, তোমরা স্বীয় অস্ত্রশস্ত্র ও দ্রব্য সম্ভার সম্বন্ধে অসতর্ক হলেই তারা একযোগে তোমাদের উপর নিপতিত হয়; এবং এতে তোমাদের অপরাধ নেই যদি তোমরা বৃষ্টিপাতে বিব্রত হয়ে অথবা পীড়িত অবস্থায় স্ব স্ব অস্ত্র পরিত্যাগ কর এবং স্বীয় আত্মরক্ষিকা সঙ্গে গ্রহণ কর; নিশ্চয়ই আল্লাহ অবিশ্বাসীদের জন্য অবমাননাকর শাস্তি প্রস্তুত করে রেখেছেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (596,4,103,' অতঃপর যখন তোমরা সালাত সম্পন্ন কর তখন দন্ডায়মান, উপবিষ্ট এবং শায়িত অবস্থায় আল্লাহকে স্মরণ কর; অতঃপর যখন তোমরা নিরাপদ হও তখন সালাত প্রতিষ্ঠিত কর; নিশ্চয়ই সালাত বিশ্বাসীগণের উপর নির্দিষ্ট সময়ের জন্য নির্ধারিত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (597,4,104,' এবং সেই সম্প্রদায়ের পশ্চাদ্ধাবনে শৈথিল্য করনা; যদি তোমরা কষ্ট পেয়ে থাক তাহলে তারাও তোমাদের অনুরূপ কষ্ট ভোগ করেছে; এবং তৎসহ আল্লাহ হতে তোমাদের যে ভরসা আছে তাদের সেই ভরসা নেই; এবং আল্লাহ মহাজ্ঞানী, প্রজ্ঞাময়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (598,4,105,' নিশ্চয়ই আমি তোমার প্রতি সত্যসহ গ্রন্থ অবতীর্ণ করেছি - যেন তুমি তদনুযায়ী মানবদেরকে আদেশ প্রদান কর, যা আল্লাহ তোমাকে শিক্ষা দান করেছেন এবং তুমি বিশ্বাসঘাতকদের পক্ষে বিতর্ককারী হয়োনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (599,4,106,' এবং আল্লাহর নিকট ক্ষমা প্রার্থনা কর; নিশ্চয়ই আল্লাহ ক্ষমাশীল, করুণাময়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (600,4,107,'এবং যারা স্বীয় জীবনের প্রতি বিশ্বাসঘাতকতা করে, তুমি তাদের পক্ষে বির্তক করনা; নিশ্চয়ই আল্লাহ বিশ্বাসঘাতক পাপীকে ভালবাসেননা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (601,4,108,' তারা মানব হতে গোপন করতে চায়, কিন্তু আল্লাহ হতে গোপন করতে পারেনা; এবং তিনি তাদের সঙ্গে রয়েছেন যখন তারা রজনীতে এমন বিষয়ে পরামর্শ করে যাতে তিনি সম্মত নন; এবং তারা যা করছে আল্লাহ তার পরিবেষ্টনকারী। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (602,4,109,' সাবধান! তোমরাই ঐ লোক যারা ওদের পক্ষ হতে পার্থিব জীবন সম্বন্ধে বিতর্ক করছ; কিন্তু কিয়ামাত দিবসে তাদের পক্ষ হতে কে আল্লাহর সাথে বিতর্ক করবে এবং কে তাদের কার্য সম্পাদনকারী হবে?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (603,4,110,' এবং যে কেহ দুস্কার্য করে অথবা স্বীয় জীবনের প্রতি অত্যাচার করে, অতঃপর আল্লাহর নিকট ক্ষমা প্রার্থী হয়, সে আল্লাহকে ক্ষমাশীল, করুণাময় দেখতে পাবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (604,4,111,' এবং যে কেহ পাপ অর্জন করে, বস্তুতঃ সে স্বীয় আত্মার প্রতিই এর প্রতিক্রিয়া পৌঁছিয়ে থাকে এবং আল্লাহ মহাজ্ঞানী, বিজ্ঞানময়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (605,4,112,' আর যে কেহ অপরাধ অথবা পাপ অর্জন করে, অতঃপর ওটা নিরপরাধীর প্রতি আরোপ করে, তাহলে সে নিজেই সেই অপরাধ ও প্রকাশ্য পাপ বহন করবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (606,4,113,' আর যদি তোমার প্রতি আল্লাহর অনুগ্রহ ও করুণা না হত তাহলে তাদের একদল তোমাকে পথভ্রষ্ট করতে ইচ্ছুক হয়েছিল, এবং তারা নিজেদেরকে ছাড়া বিপদগামী করেনি ও তারা তোমাকে কোন বিষয়ে ক্লেশ দিতে পারবেনা; এবং আল্লাহ তোমার প্রতি গ্রন্থ ও প্রজ্ঞা অবতীর্ণ করেছেন এবং তুমি যা জানতেনা তিনি তা তোমাকে শিক্ষা দিয়েছেন; এবং তোমার প্রতি আল্লাহর অসীম করুণা রয়েছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (607,4,114,' সাধারণ লোকের অধিকাংশ গুপ্ত পরামর্শে কোন মঙ্গল নিহিত থাকেনা, তবে হ্যাঁ যে ব্যক্তি দান খয়রাত করে অথবা কোন সৎ কাজ করে কিংবা লোকের মধ্যে পরস্পর সন্ধি স্থাপন করে দেয়ার উদ্দেশে তা করে তাহলে তা স্বতন্ত্র এবং যে আল্লাহর প্রসন্নতা সন্ধানের জন্য ঐরূপ করে, আমি তাকে মহান বিনিময় প্রদান করব।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (608,4,115,'আর সুপথ প্রকাশিত হওয়ার পর যে রাসূলের বিরুদ্ধাচরণ করে এবং বিশ্বাসীগণের বিপরীত পথের অনুগামী হয়, তাহলে সে যাতে অভিনিবিষ্ট আমি তাকে তাতেই প্রত্যাবর্তিত করাব এবং তাকে জাহান্নামে নিক্ষেপ করব; এবং ওটা নিকৃষ্টতর প্রত্যাবর্তন স্থল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (609,4,116,' নিশ্চয়ই আল্লাহ তাঁর সাথে অংশী স্থাপনকারীকে ক্ষমা করেননা এবং এতদ্ব্যতীত তিনি যাকে ইচ্ছা ক্ষমা করে থাকেন; এবং যে আল্লাহর সাথে অংশী স্থাপন করে সে নিশ্চয়ই সুদূর বিপথে বিভ্রান্ত হয়েছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (610,4,117,'তারা তাঁকে পরিত্যাগ করে নারী মূর্তিদেরকেই আহবান করে এবং তারা বিদ্রোহী শাইতানকে ব্যতীত আহবান করেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (611,4,118,' আল্লাহ তাকে অভিসম্পাত করেছেন; এবং শাইতান বলেছিল, আমি অবশ্যই তোমার সেবকবৃন্দ হতে এক নির্দিষ্ট অংশ গ্রহণ করব ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (612,4,119,' এবং নিশ্চয়ই আমি তাদেরকে পথভ্রান্ত করব, তাদেরকে কু-মন্ত্রনা দিব এবং তাদেরকে আদেশ করব যেন তারা পশুর কর্ণ ছেদন করে এবং তাদেরকে আদেশ করব আল্লাহর সৃষ্ট আকৃতি পরিবর্তন করতে। যে আল্লাহকে পরিত্যাগ করে শাইতানকে বন্ধু রূপে গ্রহণ করে, নিশ্চয়ই সে প্রকাশ্য ক্ষতিতে ক্ষতিগ্রস্ত হবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (613,4,120,' শাইতান তাদেরকে প্রতিশ্রুতি দেয় ও আশ্বাস দান করে, কিন্তু শাইতান প্রতারণা ব্যতীত তাদেরকে প্রতিশ্রুতি প্রদান করেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (614,4,121,' তাদেরই বাসস্থান জাহান্নাম এবং  সেখান হতে তারা পালাবার কোন জায়গা পাবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (615,4,122,' এবং যারা বিশ্বাস স্থাপন করেছে ও সৎ কাজ করে, আমি তাদেরকে জান্নাতে প্রবিষ্ট করাব যার নিম্নে স্রোতস্বিনীসমূহ প্রবাহিতা, তন্মধ্যে তারা চিরকাল অবস্থান করবে, আল্লাহর প্রতিশ্রুতি সত্য; এবং কে আল্লাহ অপেক্ষা বাক্যে অধিকতর সত্যপরায়ণ?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (616,4,123,' না তোমাদের বৃথা আশায় কাজ হবে, আর না আহলে কিতাবের বৃথা আশায়; যে অসৎ কাজ করবে সে তার প্রতিফল পাবে এবং সে আল্লাহর পরিবর্তে কেহকে বন্ধু অথবা সাহায্যকারী প্রাপ্ত হবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (617,4,124,'পুরুষ অথবা নারীর মধ্যে যারা সৎ কাজ করে এবং সে বিশ্বাসীও হয়, তাহলে তারাই জান্নাতে প্রবেশ করবে এবং তারা খর্জুর দানার কণা পরিমাণও অত্যাচারিত হবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (618,4,125,'আর যে আল্লাহর উদ্দেশে স্বীয় আনন সমর্পণ করেছে ও সৎ কাজ করে এবং ইবরাহীমের সুদৃঢ় ধর্মের অনুসরণ করে, তার অপেক্ষা কার ধর্ম উৎকৃষ্ট? এবং আল্লাহ ইবরাহীমকে স্বীয় বন্ধু রূপে গ্রহণ করেছিলেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (619,4,126,' এবং নভোমন্ডল ও ভূ-মন্ডলে যা কিছু আছে সবই আল্লাহরই জন্য; আল্লাহ সর্ব বিষয়ে পরিবেষ্টনকারী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (620,4,127,'এবং তারা তোমার নিকট নারীদের সম্বন্ধে ব্যবস্থা জিজ্ঞেস করছে; তুমি বল, আল্লাহ তোমাদেরকে তাদের সম্বন্ধে ব্যবস্থা দান করেছেন এবং পিতৃহীনা নারীদের সম্বন্ধে তোমাদের প্রতি গ্রন্থ হতে পাঠ করা হয়েছে যে, তাদের জন্য যা বিধিবদ্ধ হয়েছে তা তোমরা প্রদান করছনা, অথচ তাদেরকে বিয়ে করতে বাসনা কর এবং শিশুদের মধ্যে দুর্বলদের ও পিতৃহীনদের প্রতি সুবিচার প্রতিষ্ঠা কর এবং তোমরা যে সৎ কাজ কর, নিশ্চয়ই আল্লাহ তদ্বিষয়ে খুব ভাল জানেন ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (621,4,128,' কোন নারী যদি তার স্বামীর পক্ষ থেকে অসদাচরণ অথবা উপেক্ষার আশংকা করে তাহলে তারা উভয়ে আপোষ মীমাংসা করে নিলে তাতে তাদের উভয়ের কোন অপরাধ নেই। বস্তুতঃ আপোষ মীমাংসাই উত্তম। এবং লোভের কারণে স্বভাবতঃই মানুষের হৃদয় কৃপণ; এবং যদি তোমরা সৎ ব্যবহার কর ও সংযমী হও তাহলে তোমরা যা করছো তদ্বিষয়ে আল্লাহ অভিজ্ঞ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (622,4,129,'তোমরা কখনও স্ত্রীগণের মধ্যে সুবিচার করতে পারবেনা যদিও তোমরা তা কামনা কর, সুতরাং তোমরা কোন একজনের প্রতি সম্পূর্ণরূপে ঝুকে পড়োনা ও অপরজনকে ঝুলন্ত অবস্থায় রেখোনা এবং যদি তোমরা পরস্পর সমঝতায় আসো ও সংযমী হও তাহলে নিশ্চয়ই আল্লাহ ক্ষমাশীল, করুণাময়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (623,4,130,' এবং যদি তারা উভয়ে বিচ্ছিন্ন হয় তাহলে আল্লাহ স্বীয় প্রাচুর্য হতে তাদের প্রত্যেককে সম্পদশালী করবেন এবং আল্লাহ সুপ্রশস্ত মহাজ্ঞানী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (624,4,131,'নভোমন্ডল ও ভূ-মন্ডলে যা কিছু রয়েছে তা আল্লাহরই জন্য; এবং নিশ্চয়ই তোমাদের পূর্বে যাদেরকে গ্রন্থ প্রদত্ত হয়েছিল - আমি তাদেরকে ও তোমাদেরকে আদেশ করেছিলাম যে, তোমরা আল্লাহকে ভয় কর, এবং যদি অবিশ্বাস কর তাহলে নিশ্চয়ই নভোমন্ডল ও ভূ-মন্ডলে যা কিছু আছে তা আল্লাহরই এবং আল্লাহ মহাসম্পদশালী, প্রশংসিত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (625,4,132,' এবং আকাশসমূহে যা কিছু রয়েছে ও পৃথিবীতে যা কিছু আছে তা আল্লাহরই; এবং কর্মবিধানে (ওয়াকিল) আল্লাহই যথেষ্ট।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (626,4,133,' হে লোক সকল! যদি তিনি ইচ্ছা করেন তাহলে তোমাদেরকে বিলুপ্ত করে অন্যদেরকে আনয়ন করতে পারেন এবং আল্লাহ এ ব্যাপারে শক্তিমান।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (627,4,134,'যে ইহলোকের প্রতিদান আকাংখা করে, আল্লাহর নিকট ইহলোক ও পরলোকের প্রতিদান রয়েছে; এবং আল্লাহ শ্রবণকারী, পরিদর্শক।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (628,4,135,' হে মু’মিনগণ! তোমরা আল্লাহর উদ্দেশে সাক্ষ্য দানকারী, সুবিচার প্রতিষ্ঠাতা হও এবং যদিও এটা তোমাদের নিজের অথবা মাতা-পিতা ও আত্মীয়-স্বজনের প্রতিকূল হয়, যদিও সে সম্পদশালী কিংবা দরিদ্র হয় তাহলে আল্লাহই তাদের জন্য যথেষ্ট; অতএব সুবিচারে স্বীয় প্রবৃত্তির অনুসরণ করনা, আর তোমরা যদি ঘুরিয়ে পেচিয়ে কথা বল কিংবা পাশ কাটিয়ে যাও তাহলে নিশ্চয়ই আল্লাহ তোমাদের সমস্ত কর্মের পূর্ণ সংবাদ রাখেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (629,4,136,' হে মু’মিনগণ! তোমরা বিশ্বাস স্থাপন কর আল্লাহর প্রতি ও তাঁর রাসূলের প্রতি এবং এই কিতাবের প্রতি যা তিনি তাঁর রাসূলের উপর অবতীর্ণ করেছেন এবং ঐ কিতাবের প্রতি যা পূর্বে অবতীর্ণ হয়েছিল, এবং যে কেহ আল্লাহ, তদ্বীয় ফেরেশ্তা, তাঁর কিতাবসমূহ, তাঁর রাসূলগণ এবং পরকাল সম্বন্ধে অবিশ্বাস করে, নিশ্চয়ই সে সুদূর বিপথে বিভ্রান্ত হয়েছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (630,4,137,'নিশ্চয়ই যারা বিশ্বাস স্থাপন করে, অতঃপর অবিশ্বাসী হয়, পুনরায় বিশ্বাস স্থা্পন করে এবং আবার অবিশ্বাসী হয়, অনন্তর অবিশ্বাসে পরিবর্ধিত হয়, তাহলে আল্লাহ কখনই তাদেরকে ক্ষমা করবেননা এবং তাদেরকে পথ প্রদর্শন করবেননা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (631,4,138,' মুনাফিকদের সুসংবাদ দাও যে, তাদের জন্য যন্ত্রণাদায়ক শাস্তি রয়েছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (632,4,139,'যারা মু’মিনদেরকে পরিত্যাগ করে কাফিরদের বন্ধু রূপে গ্রহণ করে, তারা কি তাদের নিকট সম্মান অনুসন্ধান করে? কিন্তু যাবতীয় সম্মানই আল্লাহর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (633,4,140,' এবং নিশ্চয়ই তিনি গ্রন্থের মাধ্যমে তোমাদের আদেশ করছেন যে, তোমরা যখন আল্লাহর নিদর্শনসমূহের প্রতি অবিশ্বাস করা এবং তাঁর প্রতি উপহাস করা হচ্ছে শ্রবণ কর তখন তাদের সাথে (বৈঠকে) উপবেশন করনা, যে পর্যন্ত না তারা অন্য কথার আলোচনা করে; অন্যথায় তোমরাও তাদের সদৃশ হয়ে যাবে, নিশ্চয়ই আল্লাহ সেই সমস্ত মুনাফিক ও কাফিরদেরকে জাহান্নামে একত্রিত করবেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (634,4,141,' ওরা এমন যারা তোমাদের সম্বন্ধে প্রতীক্ষা করছে; এবং যদি তোমরা আল্লাহর পক্ষ হতে জয়লাভ কর তাহলে তারা বলেঃ আমরা কি তোমাদের সঙ্গে ছিলামনা? এবং যদি ওটা অবিশ্বাসীদের ভাগ্যে ঘটে তাহলে বলে- আমরা কি তোমাদের নেতৃত্ব করিনি এবং বিশ্বাসীগণ হতে তোমাদেরকে রক্ষা করিনি? অতঃপর আল্লাহ উত্থান দিবসে তোমাদের মধ্যে বিচার করবেন; এবং আল্লাহ কখনও মু’মিনদের বিরুদ্ধে কাফিরদেরকে বিজয়ী করবেননা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (635,4,142,' নিশ্চয়ই মুনাফিকরা আল্লাহর সাথে প্রতারণা করে এবং তিনিও তাদেরকে ঐ প্রতারণা প্রত্যার্পণ করছেন; এবং যখন তারা সালাতের জন্য দাঁড়ায় তখন লোকদেরকে দেখানোর জন্য আলস্যভরে দাঁড়ায় এবং আল্লাহকে খুব কমই স্মরণ করে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (636,4,143,' এরা সন্দেহের দোলায় দোদুল্যমান রয়েছে। তারা এ দিকেও নয় ওদিকেও নয়; এবং আল্লাহ যাকে পথভ্রান্ত করেছেন, বস্তুতঃ তুমি তার জন্য কোনই পথ পাবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (637,4,144,' হে মু’মিনগণ! তোমরা মু’মিনদেরকে ছেড়ে কাফিরদেরকে বন্ধু রূপে গ্রহণ করনা, তোমরা কি আল্লাহর জন্য তোমাদের বিরুদ্ধে স্পষ্ট প্রমাণ দিতে চাও?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (638,4,145,' নিশ্চয়ই মুনাফিকরা জাহান্নামের নিম্নতম স্তরে অবস্থান করবে এবং তুমি কখনও তাদের জন্য সাহায্যকারী পাবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (639,4,146,' কিন্তু যারা ক্ষমা প্রার্থনা করে ও সংশোধিত হয় এবং আল্লাহর পথকে সুদৃঢ়ভাবে আঁকড়ে ধরে ও আল্লাহর ধর্মে বিশুদ্ধ হয়, ফলতঃ তারাই মু’মিনদের সঙ্গী এবং অচিরেই আল্লাহ মু’মিনদেরকে উত্তম প্রতিদান দিবেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (640,4,147,' আল্লাহ কেনইবা তোমাদেরকে শাস্তি প্রদান করবেন যদি তোমরা কৃতজ্ঞ হও ও বিশ্বাস স্থাপন কর? এবং আল্লাহতো অতিশয় গুণগ্রাহী, মহাজ্ঞানী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (641,4,148,'আল্লাহ কোন মন্দ কথার প্রচারণা ভালবাসেন না, তবে কেহ অত্যাচারিত হয়ে থাকলে তার কথা স্বতন্ত্র; এবং আল্লাহ শ্রবণকারী মহাজ্ঞানী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (642,4,149,' যদি তোমরা সৎ কাজ প্রকাশ্যে কর অথবা গোপনে কর অথবা যদি তোমরা অপরাধ ক্ষমা করে দাও তাহলে জেনে রেখ যে, আল্লাহ নিজেও ক্ষমাকারী, সর্বশক্তিমান।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (643,4,150,' নিশ্চয়ই যারা আল্লাহ ও তাঁর রাসূলগণের প্রতি অবিশ্বাস করে এবং আল্লাহ ও তাঁর রাসূলগণের মধ্যে পার্থক্য করতে ইচ্ছা করে এবং বলে, আমরা কতিপয়কে বিশ্বাস করি ও কতিপয়কে অবিশ্বাস করি এবং তারা এর মধ্যবর্তী পথ অবলম্বন করতে ইচ্ছা করে ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (644,4,151,' ওরাই প্রকৃত অবিশ্বাসী, এবং আমি অবিশ্বাসীদের জন্য অবমাননাকর শাস্তি প্রস্তুত করে রেখেছি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (645,4,152,'এবং যারা আল্লাহ ও তাঁর রাসূলগণের প্রতি বিশ্বাস স্থাপন করে এবং তাঁর রাসূলগণের মধ্যে কোন পার্থক্য করেনা - আল্লাহ শীঘ্রই তাদের প্রতিদান প্রদান করবেন এবং আল্লাহ ক্ষমাশীল, করুণাময়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (646,4,153,' আহলে কিতাব তোমার নিকট আবেদন জানায় যে, তুমি তাদের প্রতি আকাশ হতে কোন গ্রন্থ নাযিল কর - পরন্ত তারা মূসার নিকট এটা অপেক্ষাও বৃহত্তর দাবী করেছিল। তারা বলেছিল, আল্লাহকে প্রকাশ্যভাবে প্রদর্শন কর; অতঃপর তাদের অবাধ্যতার জন্য বজ্রপাত তাদেরকে আক্রমণ করেছিল, অতঃপর তাদের নিকট নিদর্শনাবলী আসার পরেও তারা গো-বৎসকে উপাস্য হিসাবে গ্রহণ করেছিল। কিন্তু ওটাও আমি ক্ষমা করেছিলাম, এবং মূসাকে প্রকাশ্য প্রভাব প্রদান করেছিলাম।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (647,4,154,' এবং আমি তাদের প্রতিশ্রুতির জন্য তাদের উপর তূর পর্বত সমুচ্চ করেছিলাম এবং তাদেরকে বলেছিলাম অবনত শিরে দ্বারে প্রবেশ কর। এবং তাদেরকে আরও বলেছিলাম, শনিবারের সীমা অতিক্রম করনা। এভাবে তাদের নিকট হতে কঠোর প্রতিশ্রুতি গ্রহণ করেছিলাম।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (648,4,155,' কিন্তু তারা লা‘নতগ্রস্ত হয়েছিল তাদের প্রতিশ্রুতি ভঙ্গ এবং আল্লাহর নিদর্শনাবলীর প্রতি তাদের অবিশ্বাস ও অন্যায়ভাবে তাদের নাবীদের হত্যা এবং ‘‘তাদের স্ব স্ব অন্তরসমূহ আচ্ছাদিত’’ এই উক্তি করার জন্য; হ্যাঁ - তাদের অবিশ্বাস হেতু আল্লাহ ওদের অন্তরে মোহর এঁটে দিয়েছেন, এ কারণে তারা অল্প সংখ্যক ব্যতীত বিশ্বাস করেনা ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (649,4,156,' এবং তাদের কুফরী ও মারইয়ামের প্রতি তাদের ভয়ানক অপবাদের জন্য।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (650,4,157,'এবং ‘‘আল্লাহর রাসূল ও মারইয়াম নন্দন ঈসাকে আমরা হত্যা করেছি’’ বলার জন্য। অথচ তারা না তাকে হত্যা করেছে আর না শুলে চড়িয়েছে; বরং তারা ধাঁধাঁয় পতিত হয়েছিল। তারা তদ্বিষয়ে সন্দেহাচ্ছন্ন ছিল, কল্পনার অনুসরণ ব্যতীত এ বিষয়ে তাদের কোন জ্ঞান ছিলনা। প্রকৃত পক্ষে তারা তাকে হত্যা করেনি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (651,4,158,'পরন্ত আল্লাহ তাকে নিজের দিকে উঠিয়ে নিয়েছেন, এবং আল্লাহ পরাক্রান্ত মহাজ্ঞানী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (652,4,159,' এবং আহলে কিতাবের মধ্যে এমন কেহ নেই যে, তার মৃত্যুর পূর্বে ব্যতীত এটা বিশ্বাস করবে; এবং উত্থান দিনে সে (ঈসা) তাদের উপর সাক্ষ্য দান করবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (653,4,160,' আমি ইয়াহুদীদের অবাধ্যতা হেতু তাদের জন্য যে সমস্ত বস্ত্ত বৈধ ছিল তা তাদের প্রতি অবৈধ করেছি; এবং যেহেতু তারা অনেককে আল্লাহর পথ হতে প্রতিরোধ করত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (654,4,161,' এবং তারা নিষিদ্ধ হওয়া সত্ত্বেও সুদ গ্রহণ করত এবং তারা অন্যায়ভাবে লোকদের ধন সম্পদ গ্রাস করত এবং আমি তাদের মধ্যস্থ অবিশ্বাসীদের জন্য যন্ত্রণাদায়ক শাস্তি প্রস্তুত করে রেখেছি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (655,4,162,' কিন্তু তাদের মধ্যে যারা জ্ঞানে সুদৃঢ় এবং বিশ্বাসী তারা তোমার প্রতি যা অবতীর্ণ হয়েছে এবং তোমার পূর্বে যা অবতীর্ণ হয়েছিল তৎপ্রতি বিশ্বাস স্থাপন করে। এবং যারা সালাত আদায়কারী ও যাকাত প্রদানকারী এবং আল্লাহ ও আখিরাতের প্রতি বিশ্বাস স্থাপনকারী তাদেরকেই আমি প্রচুর প্রতিদান প্রদান করব।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (656,4,163,'নিশ্চয়ই আমি তোমার প্রতি প্রত্যাদেশ করেছি, যেরূপ আমি নূহ ও তৎপরবর্তী নাবীগণের প্রতি প্রত্যাদেশ করেছিলাম এবং ইবরাহীম, ইসমাঈল, ইসহাক, ইয়াকূব ও তদ্বংশীয়গণের প্রতি এবং ঈসা, আইযুব, ইউনুস, হারূণ, সুলাইমানের প্রতি প্রত্যাদেশ করেছিলাম এবং আমি দাঊদকে যাবুর প্রদান করেছিলাম।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (657,4,164,' আর নিশ্চয়ই আমি তোমার পূর্বের বহু রাসূলের প্রসঙ্গ তোমাকে বর্ণনা করেছি এবং অনেক রাসূল যাদের কথা তোমাকে বলিনি; আল্লাহ মূসার সাথে প্রত্যক্ষ কথা বলেছেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (658,4,165,' আমি সুসংবাদদাতা ও ভয় প্রদর্শক রূপে রাসূলগণকে প্রেরণ করেছি যাতে রাসূলগণের পরে লোকদের মধ্যে আল্লাহ সম্বন্ধে কোন অপবাদ দেয়ার অবকাশ না থাকে এবং আল্লাহ পরাক্রান্ত, মহাজ্ঞানী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (659,4,166,' কিন্তু আল্লাহ তোমার প্রতি যা অবতীর্ণ করেছেন, তৎসম্বন্ধে তিনি সজ্ঞানে অবতারণের সাক্ষ্য প্রদান করেছেন; এবং সাক্ষ্য দানে আল্লাহই যথেষ্ট।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (660,4,167,'নিশ্চয়ই যারা অবিশ্বাস করেছে এবং অপরকে আল্লাহর পথ হতে প্রতিরোধ করেছে, অবশ্যই তারা সুদূর বিপথে বিভ্রান্ত হয়েছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (661,4,168,'নিশ্চয়ই যারা অবিশ্বাসী হয়েছে ও অত্যাচার করেছে, আল্লাহ তাদেরকে ক্ষমা করবেননা এবং তাদেরকে সুপথ প্রদর্শন করবেন না ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (662,4,169,'জাহান্নামের পথ ব্যতীত, তন্মধ্যে তারা চিরকাল অবস্থান করবে এবং এটা আল্লাহর পক্ষে সহজসাধ্য। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (663,4,170,' জাহান্নামের পথ ব্যতীত, তন্মধ্যে তারা চিরকাল অবস্থান করবে এবং এটা আল্লাহর পক্ষে সহজসাধ্য।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (664,4,171,'হে আহলে কিতাব! তোমরা স্বীয় ধর্মে সীমা অতিক্রম করনা এবং আল্লাহর বিরুদ্ধে সত্য ব্যতীত বলনা; নিশ্চয়ই মারইয়াম নন্দন ঈসা মাসীহ্ আল্লাহর রাসূল ও তাঁর বাণী - যা তিনি মারইয়ামের প্রতি সঞ্চারিত করেছিলেন এবং তাঁর আদিষ্ট আত্মা; অতএব আল্লাহ ও তাঁর রাসূলের উপর বিশ্বাস স্থাপন কর। আর ‘‘আল্লাহ তিন জনের একজন’’- এ কথা বলা পরিহার কর। তোমাদের কল্যাণ হবে; নিশ্চয়ই আল্লাহই একমাত্র ইলাহ; তিনি কোন সন্তান হওয়া হতে পুতঃ, মুক্ত। নভোমন্ডল ও ভূ-মন্ডলে যা আছে তা তাঁরই এবং আল্লাহই কার্য সম্পাদনে যথেষ্ট। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (665,4,172,'আল্লাহর বান্দা হওয়ার ব্যাপারে মাসীহ্ এবং সান্নিধ্য প্রাপ্ত মালাইকাদের কোনই সংকোচ নেই; এবং যারা তাঁর সেবায় সংকুচিত হয় ও অহংকার করে তিনি তাদের সকলকে নিজের দিকে একত্রিত করবেন। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (666,4,173,'অনন্তর যারা বিশ্বাস স্থাপন করেছে ও সৎ কাজ করে থাকে তাদেরকে তিনি সম্যক প্রতিদান প্রদান করবেন এবং স্বীয় সম্পদ হতে অধিকতর দান করবেন এবং যারা সংকুচিত হয় অহংকার করে, তাদেরকে তিনি যন্ত্রণাদায়ক শাস্তি প্রদান করবেন। এবং আল্লাহ ব্যতীত তারা নিজেদের জন্য কোন অভিভাবক বা সাহায্যকারী পাবেনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (667,4,174,'হে লোক সকল! তোমাদের রবের সন্নিধান হতে তোমাদের নিকট প্রত্যক্ষ প্রমাণ এসেছে এবং আমি তোমাদের প্রতি সমুজ্জ্বল জ্যোতি অবতীর্ণ করেছি। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (668,4,175,'অতঃপর যারা আল্লাহর প্রতি বিশ্বাস স্থাপন করেছে এবং তাঁকে সুদৃঢ় রূপে ধারণ করেছে, ফলতঃ তিনি তাদেরকে স্বীয় করুণা ও কল্যাণের দিকে প্রবিষ্ট করাবেন এবং স্বীয় সরল পথ প্রদর্শন করবেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (669,4,176,'তারা তোমাদের নিকট ব্যবস্থা প্রার্থনা করছে, তুমি বলঃ আল্লাহ তোমাদেরকে পিতা-পুত্রহীন সম্বন্ধে ব্যবস্থা দান করেছেন। যদি কোন ব্যক্তি নিঃসন্তান অবস্থায় মারা যায় এবং তার ভগ্নী থাকে তাহলে সে তার পরিত্যক্ত সম্পত্তি হতে অর্ধাংশ পাবে; এবং যদি কোন নারীর সন্তান না থাকে তাহলে তার ভাইই তদীয় উত্তরাধিকারী হবে; কিন্তু যদি দুই ভগ্নী থাকে তাহলে তাদের উভয়ের জন্য পরিত্যক্ত বিষয়ের দুই তৃতীয়াংশ এবং যদি তার ভাই ভগ্নী-পুরুষ ও নারীগণ থাকে তাহলে পুরুষ দুই নারীর তুল্য অংশ পাবে; আল্লাহ তোমাদের জন্য বর্ণনা করছেন যেন তোমরা বিভ্রান্ত না হও, এবং আল্লাহ সর্ব বিষয়ে মহাজ্ঞানী। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (670,5,1,'হে মু’মিনগণ! তোমরা তোমাদের ও‘য়াদাগুলি পূর্ণ কর। তোমাদের জন্য চতুস্পদ জন্তু হালাল করা হয়েছে। তবে যেগুলি হারাম হওয়া সম্পর্কে তোমাদের উপর পবিত্র কুরআনের আয়াতগুলি অবতীর্ণ হয়েছে সেগুলি এবং ইহরাম বাঁধা অবস্থায় তোমাদের শিকার করা জন্তুগুলি হালাল নয়। নিশ্চয়ই আল্লাহ তাঁর ইচ্ছা অনুযায়ী হুকুম করে থাকেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (671,5,2,'হে ঈমানদারগণ! তোমরা আল্লাহর নিদর্শনাবলী, নিষিদ্ধ মাসগুলি, কুরবানীর পশুগুলির গলায় ব্যবহৃত চিহ্নগুলি এবং যারা তাদের রবের সন্তুষ্টি ও অনুগ্রহ পাবার জন্য সম্মানিত ঘরে যাবার ইচ্ছা করে তাদের অবমাননা করা বৈধ মনে করনা। আর তোমরা যখন ইহরাম থেকে মুক্ত হও তখন শিকার কর। যারা তোমাদেরকে পবিত্র মাসজিদে যেতে বাঁধা দিয়েছে সেই সম্প্রদায়ের দুশমনি যেন তোমাদেরকে সীমা ছাড়িয়ে যেতে উৎসাহিত না করে। সৎ কাজ করতে ও সংযমী হতে তোমরা পরস্পরকে সাহায্য কর। তবে পাপ ও শক্রতার ব্যাপারে তোমরা একে অপরকে সাহায্য করনা। আর তোমরা আল্লাহকে ভয় কর। নিশ্চয়ই আল্লাহ কঠিন শাস্তিদাতা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (672,5,3,' তোমাদের জন্য মৃত জীব, রক্ত, শুকরের মাংস, আল্লাহ ছাড়া অপরের নামে উৎসর্গীকৃত পশু, কন্ঠরোধে মারা পশু, আঘাত লেগে মরে যাওয়া পশু, পতনের ফলে মৃত পশু, শৃংগাঘাতে মৃত পশু এবং হিংস্র জন্তুতে খাওয়া পশু হারাম করা হয়েছে; তবে যা তোমরা যবাহ দ্বারা পবিত্র করেছ তা হালাল। আর যে সমস্ত পশুকে পূজার বেদীর উপর বলি দেয়া হয়েছে তা এবং জুয়ার তীর দ্বারা ভাগ্য নির্ণয় করাও তোমাদের জন্য হারাম করা হয়েছে। এসব কাজ পাপ। আজ কাফিরেরা তোমাদের দীনের বিরুদ্ধাচরণের ব্যাপারে হতাশ হয়ে পড়েছে। সুতরাং তোমরা তাদেরকে ভয় করনা; শুধু আমাকেই ভয় কর। আজ তোমাদের জন্য তোমাদের দীন পূর্ণাঙ্গ করলাম, তোমাদের প্রতি আমার অনুগ্রহ সম্পূর্ণ করলাম এবং ইসলামকে তোমাদের জন্য দীন হিসাবে মনোনীত করলাম। তবে কেহ পাপের দিকে না ঝুঁকে ক্ষুধার তাড়নায় আহার করতে বাধ্য হলে সেগুলি খাওয়া তার জন্য হারাম হবেনা। কারণ নিশ্চয়ই আল্লাহ ক্ষমাশীল ও দয়ালু।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (673,5,4,' তারা তোমাকে জিজ্ঞেস করে - তাদের জন্য কি কি হালাল করা হয়েছে? তুমি বলঃ পবিত্র জিনিসগুলি তোমাদের জন্য হালাল করা হয়েছে। আল্লাহর নির্দেশিত নিয়মানুযায়ী তোমরা যে সমস্ত পশু-পক্ষীকে শিকার করা শিক্ষা দিয়েছ; তারা যা শিকার করে আনে তা তোমরা খাও এবং ওগুলিকে শিকারের জন্য পাঠানোর সময় আল্লাহর নাম স্মরণ কর। তোমরা আল্লাহকে ভয় কর, নিশ্চয়ই আল্লাহ হিসাব গ্রহণে তৎপর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (674,5,5,'আজ তোমাদের জন্য পবিত্র বস্তুগুলি হালাল করা হল। আহলে কিতাবের যবাহকৃত জীবও তোমাদের জন্য হালাল এবং তোমাদের যবাহকৃত জীবও তাদের জন্য হালাল। আর সতী সাধ্বী মুসলিম নারীরাও এবং তোমাদের পূর্ববর্তী আহলে কিতাবের মধ্যকার সতী-সাধ্বী নারীরাও (তোমাদের জন্য হালাল), যখন তোমরা তাদেরকে তাদের বিনিময় (মোহর) প্রদান কর, এ রূপে যে, তোমরা (তাদেরকে) পত্নী রূপে গ্রহণ করে নাও, না প্রকাশ্যে ব্যভিচার কর, আর না গোপন প্রণয় কর; আর যে ব্যক্তি ঈমানের সাথে কুফরী মিশ্রিত করবে তার ‘আমল নিস্ফল হয়ে যাবে এবং সে আখিরাতে সম্পূর্ণ রূপে ক্ষতিগ্রস্ত হবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (675,5,6,'হে মু’মিনগণ! যখন তোমরা সালাতের উদ্দেশে দন্ডায়মান হও তখন (সালাতের পূর্বে) তোমাদের মুখমন্ডল ধৌত কর এবং হাতগুলিকে কনুই পর্যন্ত ধুয়ে নাও, আর মাথা মাসাহ কর এবং পা’গুলিকে টাখনু পর্যন্ত ধুয়ে ফেল। যদি তোমরা অপবিত্র হও তাহলে গোসল করে সমস্ত শরীর পবিত্র করে নাও। কিন্তু যদি রোগগ্রস্ত হও কিংবা সফরে থাক অথবা তোমাদের কেহ পায়খানা হতে ফিরে আস কিংবা তোমরা স্ত্রীদেরকে স্পর্শ কর (স্ত্রী-সহবাস কর), অতঃপর পানি না পাও তাহলে পবিত্র মাটি দ্বারা তায়াম্মুম করে নাও, তখন তোমরা তা দ্বারা তোমাদের মুখমন্ডল ও হাত মাসাহ কর, আল্লাহ তোমাদের উপর কোন সংকীর্ণতা আনয়ন করতে চাননা, বরং তিনি তোমাদেরকে পবিত্র করতে ও তোমাদের উপর স্বীয় নি‘আমাত পূর্ণ করতে চান, যেন তোমরা কৃতজ্ঞতা প্রকাশ কর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (676,5,7,' আর তোমরা তোমাদের প্রতি বর্ষিত আল্লাহর অনুগ্রহকে স্মরণ কর এবং তাঁর ঐ অঙ্গীকারকেও স্মরণ কর, যে অঙ্গীকার তিনি তোমাদের নিকট থেকে গ্রহণ করেছিলেন। তোমরা বলেছিলে, আমরা শুনলাম ও মেনে নিলাম। আর তোমরা আল্লাহকে ভয় কর, নিশ্চয়ই তিনি অন্তরের কথাগুলিরও পূর্ণ খবর রাখেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (677,5,8,'হে মু’মিনগণ! তোমরা আল্লাহর উদ্দেশে বিধানসমূহ পূর্ণ রূপে প্রতিষ্ঠাকারী ও ন্যায়ের সাথে সাক্ষ্যদানকারী হয়ে যাও, কোন বিশেষ সম্প্রদায়ের শক্রতা যেন তোমাদেরকে এর প্রতি প্ররোচিত না করে যে, তোমরা ন্যায়বিচার করবেনা। তোমরা ন্যায়বিচার কর, এটা আল্লাহ-ভীতির অধিকতর নিকটবর্তী। আল্লাহকে ভয় কর, নিশ্চয়ই আল্লাহ তোমাদের কৃতকর্ম সম্বন্ধে পূর্ণ ওয়াকিফহাল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (678,5,9,'যারা ঈমান এনেছে ও ভাল কাজ করেছে তাদেরকে আল্লাহ প্রতিশ্রুতি দিয়েছেন যে, তাদের জন্য ক্ষমা ও মহান পুরস্কার রয়েছে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (679,5,10,'পক্ষান্তরে যারা কুফরী করেছে এবং আমার বিধানসমূহকে মিথ্যা জেনেছে তারাই হচ্ছে জাহান্নামের অধিবাসী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (680,5,11,'হে মু’মিনগণ! তোমাদের প্রতি যে আল্লাহর অনুগ্রহ রয়েছে তা স্মরণ কর, যখন এক সম্প্রদায় এই চিন্তায় ছিল যে, তোমাদের দিকে তাদের হস্ত প্রসারিত করবে, কিন্তু আল্লাহ তাদের হাতকে তোমাদের দিক থেকে থামিয়ে দিয়েছেন, তোমরা আল্লাহকে ভয় কর, এবং মু’মিনদের আল্লাহর উপরই ভরসা করা উচিত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (681,5,12,'আর আল্লাহ বানী ইসরাঈলের নিকট থেকে অঙ্গীকার নিয়েছিলেন, আমি তাদের মধ্য হতে বারো জন দলপতি নিযুক্ত করেছিলাম; এবং আল্লাহ বলেছিলেনঃ আমি তোমাদের সাথে রয়েছি, যদি তোমরা সালাত সুপ্রতিষ্ঠিত কর ও যাকাত দিতে থাক এবং আমার রাসূলদের উপর ঈমান আন ও তাদেরকে সাহায্য কর এবং আল্লাহকে উত্তমরূপে কর্জ দিতে থাক; তাহলে আমি অবশ্যই তোমাদের পাপগুলি তোমাদের থেকে মুছে দিব এবং অবশ্যই তোমাদেরকে এমন উদ্যানসমূহে দাখিল করব যার তলদেশে নহরসমূহ বইতে থাকবে, অতঃপর যে ব্যক্তি এরপরও কুফরী করবে, নিশ্চয়ই সে সোজা পথ থেকে দূরে সরে পড়ল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (682,5,13,'বস্তুতঃ শুধু তাদের প্রতিশ্রুতি ভঙ্গের কারণেই আমি তাদেরকে স্বীয় অভিশপ্ত করলাম এবং অন্তরকে কঠোর করে দিলাম। তারা কালামকে (তাওরাত) ওর স্থানসমূহ হতে পরিবর্তন করে দেয় এবং তাদেরকে যা কিছু উপদেশ দেয়া হয়েছিল তারা তার মধ্য হতে এক বড় অংশকে বিস্মৃত হতে বসেছে, আর আগামীতেও (অবিরত) তাদের কোন না কোন খিয়ানতের সংবাদ তোমার নিকট আসতে থাকবে, তাদের অল্প কয়েকজন ব্যতীত। অতএব তুমি তাদেরকে ক্ষমা করতে থাক এবং তাদেরকে মার্জনা করতে থাক; নিশ্চয়ই আল্লাহ সদাচারী লোকদেরকে ভালবাসেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (683,5,14,'আর যারা বলেঃ ‘আমরা নাসারাহ,’ আমি তাদের নিকট থেকেও ও‘য়াদা নিয়েছিলাম, অনন্তর তাদেরকেও যা কিছু উপদেশ দেয়া হয়েছিল তার মধ্য হতে তারা নিজেদের এক বড় অংশ বিস্মৃত হয়েছে। সুতরাং আমি তাদের পরস্পরের মধ্যে হিংসা ও শক্রতা সঞ্চার করে দিলাম কিয়ামাতের দিন পর্যন্ত এবং অচিরেই আল্লাহ তাদেরকে তাদের কৃতকর্ম সম্বন্ধে সংবাদ দিবেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (684,5,15,'হে আহলে কিতাব! তোমাদের কাছে আমার রাসূল এসেছে, তোমরা কিতাবের যে সব বিষয় গোপন কর তন্মধ্য হতে বহু বিষয় সে তোমাদের সামনে পরিস্কারভাবে ব্যক্ত করে, আর বহু বিষয় (প্রকাশ করা) বর্জন করে, তোমাদের কাছে আল্লাহর নিকট থেকে এক আলোকময় বস্ত্ত এসেছে এবং তা একটি স্পষ্ট কিতাব (কুরআন)।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (685,5,16,'তা দ্বারা আল্লাহ এরূপ লোকদেরকে শান্তির পন্থাসমূহ বলে দেন যারা তাঁর সন্তুষ্টি অম্বেষণ করে এবং তিনি তাদেরকে নিজ তাওফীকে (ও করুণায়) কুফরীর অন্ধকার থেকে বের করে (ঈমানের) আলোর দিকে আনয়ন করেন এবং তাদেরকে সরল (সঠিক) পথে প্রতিষ্ঠিত রাখেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (686,5,17,'নিশ্চয়ই তারা কাফির যারা বলেঃ নিশ্চয়ই আল্লাহ স্বয়ং হচ্ছেন মসীহ্ (ঈসা) ইবনে মারইয়াম! তুমি বলঃ যদি আল্লাহ মসীহ্ (ঈসা) ইবনু মারইয়ামকে ও তার মাতাকে এবং ভূ-পৃষ্ঠে যারা আছে তাদের সবাইকে ধ্বংস করার ইচ্ছা করেন তাহলে এরূপ কে আছে যে তাদেরকে আল্লাহ হতে এতটুকু রক্ষা করতে পারে? আল্লাহরই কর্তৃত্ব নির্দিষ্ট রয়েছে আকাশসমূহে ও যমীনে এবং এতদুভয়ের মধ্যস্থিত যাবতীয় কিছুর উপর; তিনি যা ইচ্ছা করেন তাই সৃষ্টি করেন, আর আল্লাহ সকল বস্তুর উপর পূর্ণ ক্ষমতাবান।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (687,5,18,'ইয়াহুদী ও নাসারাহ্ বলেঃ আমরা আল্লাহর পুত্র ও তাঁর প্রিয়পাত্র। তুমি তাদের বলে দাও, আচ্ছা তাহলে তিনি তোমাদেরকে তোমাদের পাপের দরুণ কেন শাস্তি প্রদান করবেন? বরং তোমরাও অন্যান্য সৃষ্টির ন্যায় সাধারণ মানুষ মাত্র, তিনি যাকে ইচ্ছা মার্জনা করবেন এবং যাকে ইচ্ছা শাস্তি দিবেন, আর আল্লাহর কর্তৃত্ব রয়েছে আকাশসমূহে ও যমীনে এবং এতদুভয়ের মধ্যস্থিত সবকিছুতেও; আর সবাইকে আল্লাহর দিকেই প্রত্যাবর্তন করতে হবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (688,5,19,'হে আহলে কিতাব! রাসূলদের আগমন দীর্ঘকাল বন্ধ থাকার পর তোমাদের নিকট আমার রাসূল এসে পৌঁছেছে, যে তোমাদেরকে স্পষ্টভাবে (আল্লাহর হুকুম) বলে দিচ্ছে, যেন তোমরা (কিয়ামাত দিনে) বলতে না পার যে, তোমাদের নিকট কোন সুসংবাদদাতা ও ভয় প্রর্দশনকারী আগমন করেনি। (এখন তো) তোমাদের নিকট সুসংবাদদাতা ও ভয় প্রর্দশনকারী এসে গেছে, আর আল্লাহ সকল বস্তুর উপর পূর্ণ ক্ষমতাবান।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (689,5,20,'আর যখন মূসা স্বীয় সম্প্রদায়কে বললঃ হে আমার সম্প্রদায়! তোমাদের প্রতি আল্লাহর নি’আমতকে স্মরণ কর, যখন তিনি তোমাদের মধ্যে বহু নাবী সৃষ্টি করলেন, রাজ্যাধিপতি করলেন এবং তোমাদেরকে এমন বস্তুসমূহ দান করলেন যা বিশ্ববাসীদের মধ্যে কেহকেও দান করেননি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (690,5,21,'হে আমার সম্প্রদায়! এই পুণ্য ভূমিতে প্রবেশ কর যা আল্লাহ তোমাদের জন্য লিখে দিয়েছেন, আর পিছনের দিকে ফিরে যেওনা, তাহলে তোমরা সম্পূর্ণ রূপে ক্ষতিগ্রস্ত হবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (691,5,22,'তারা বললঃ হে মূসা! সেখানেতো পরাক্রমশালী লোক রয়েছে। অতএব তারা যে পর্যন্ত সেখান হতে বের হয়ে না যায় সে পর্যন্ত আমরা সেখানে কখনও প্রবেশ করবনা। হ্যাঁ, যদি তারা সেখান হতে বেরিয়ে যায় তাহলে নিশ্চয়ই আমরা যেতে প্রস্তুত আছি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (692,5,23,'সেই দুই ব্যক্তি (যারা আল্লাহকে ভয়কারীদের অন্তর্ভুক্ত ছিল এবং যাদের প্রতি আল্লাহ অনুগ্রহ করেছিলেন) বললঃ তোমরা তাদের উপর আক্রমণ চালিয়ে (নগরের) দ্বারদেশ পর্যন্ত যাও, অনন্তর যখনই তোমরা দ্বারদেশে পা রাখবে তখনই জয় লাভ করবে; এবং তোমরা আল্লাহর উপরই নির্ভর কর, যদি তোমরা মু’মিন হও।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (693,5,24,'তারা বললঃ হে মূসা! নিশ্চয়ই আমরা কখনও সেখানে পা রাখবনা যে পর্যন্ত তারা সেখানে বিদ্যমান থাকে। অতএব আপনি ও আপনার রাব্ব (আল্লাহ) চলে যান এবং উভয়ে যুদ্ধ করুন, আমরা এখানেই বসে থাকব।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (694,5,25,'মূসা বলল- হে আমার রাব্ব! আমি শুধু নিজের উপর ও নিজের ভাইয়ের উপর অধিকার রাখি, সুতরাং আপনি আমাদের উভয়ের এবং এই অবাধ্য সম্প্রদায়ের মধ্যে মীমাংসা করে দিন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (695,5,26,'তিনি (আল্লাহ) বললেনঃ (তাহলে মীমাংসা এই যে) এই দেশ চল্লিশ বছর পর্যন্ত এদের হস্তগত হবেনা, এ রূপেই তারা ভূ-পৃষ্ঠে উদভ্রান্ত হয়ে ফিরবে, সুতরাং তুমি অবাধ্য সম্প্রদায়ের জন্য (একটুও) বিষন্ন হয়োনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (696,5,27,'তুমি তাদেরকে (আহলে কিতাবদেরকে) আদমের পুত্রদ্বয়ের (হাবীল ও কাবীলের) ঘটনা সঠিকভাবে পাঠ করে শুনিয়ে দাও; যখন তারা উভয়েই এক একটি কুরবানী উপস্থিত করল এবং তন্মধ্য হতে একজনের (হাবীলের) কুরবানী কবূল হল এবং অপরজনের কবূল হলনা। অপরজন বলতে লাগলঃ আমি তোমাকে নিশ্চয়ই হত্যা করব; প্রথমজন বললঃ আল্লাহ আল্লাহভীরুদের ‘আমলই কবূল করে থাকেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (697,5,28,'তুমি যদি আমাকে হত্যা করার জন্য হাত প্রসারিত কর, তথাপি আমি তোমাকে হত্যা করার জন্য তোমার দিকে কখনও আমার হাত বাড়াবনা; আমিতো বিশ্ব জাহানের রাব্ব আল্লাহকে ভয় করি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (698,5,29,'আমি চাই যে, (আমার দ্বারা কোন পাপ না হোক) তুমি আমার পাপ এবং তোমার পাপ সমস্তই নিজের মাথায় তুলে নাও; অনন্তর তুমি জাহান্নামীদের অন্তর্ভুক্ত হয়ে যাও, অত্যাচারীদের শাস্তি এরূপই হয়ে থাকে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (699,5,30,'অতঃপর তার প্রবৃত্তি তাকে স্বীয় ভ্রাতৃ হত্যার প্রতি প্রলুব্ধ করে তুলল। সুতরাং সে তাকে হত্যা করেই ফেলল, ফলে সে ক্ষতিগ্রস্তদের অন্তর্ভুক্ত হয়ে পড়ল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (700,5,31,'অতঃপর আল্লাহ একটি কাক প্রেরণ করলেন, সে মাটি খুঁড়তে লাগল, যেন সে তাকে (কাবীলকে) শিখিয়ে দেয় যে, স্বীয় ভাইয়ের মৃতদেহ কিভাবে ঢাকবে, সে বলতে লাগলঃ আমার অবস্থার প্রতি আফসোস! আমি ঐ কাকের সমতুল্য হতে পারলামনা এবং নিজ ভাইয়ের মৃতদেহ আবৃত করতে অক্ষম হয়ে গেলাম। ফলে সে অত্যন্ত লজ্জিত হল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (701,5,32,'এ কারণেই আমি বানী ইসরাঈলের প্রতি এই নির্দেশ দিয়েছি যে, যে ব্যক্তি কোন ব্যক্তিকে হত্যা করে অন্য প্রাণের বিনিময় ব্যতীত, কিংবা তার দ্বারা ভূ-পৃষ্ঠে কোন ফিতনা-ফাসাদ বিস্তার ব্যতীত, তাহলে সে যেন সমস্ত মানুষকে হত্যা করে ফেলল; আর যে ব্যক্তি কোন ব্যক্তিকে রক্ষা করল, তাহলে সে যেন সমস্ত মানুষকে রক্ষা করল; আর তাদের (বানী ইসরাঈলের) কাছে আমার বহু রাসূলও স্পষ্ট প্রমাণসমূহ নিয়ে আগমন করেছিল, তবু এর পরেও তন্মধ্য হতে অনেকেই ভূ-পৃষ্ঠে সীমা লংঘনকারী হয়ে গেছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (702,5,33,'যারা আল্লাহর বিরুদ্ধে ও তাঁর রাসূলের বিরুদ্ধে সংগ্রাম করে, আর ভূ-পৃষ্ঠে অশান্তি সৃষ্টি করে বেড়ায়, তাদের শাস্তি এটাই যে, তাদেরকে হত্যা করা হবে অথবা শুলে চড়ান হবে, অথবা এক দিকের হাত ও অপর দিকের পা কেটে ফেলা হবে, অথবা তাদের দেশ থেকে নির্বাসিত করা হবে; এটাতো দুনিয়ায় তাদের জন্য ভীষণ অপমান, আর আখিরাতেও তাদের জন্য ভীষণ শাস্তি রয়েছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (703,5,34,'কিন্তু হ্যাঁ, তোমরা তাদেরকে গ্রেফতার করার পূর্বে যারা তাওবাহ করে, তাহলে জেনে রেখ যে, নিশ্চয়ই আল্লাহ ক্ষমাশীল, দয়ালু।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (704,5,35,'হে মু’মিনগণ! আল্লাহকে ভয় কর এবং তাঁর সান্নিধ্য অম্বেষণ কর ও আল্লাহর পথে জিহাদ করতে থাক। আশা করা যায় যে, তোমরা সফলকাম হবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (705,5,36,'নিশ্চয়ই যারা কাফির, যদি তাদের কাছে বিশ্বের সমস্ত সম্পদও থাকে এবং ওর সাথে তৎপরিমান আরও থাকে, এবং এগুলোর বিনিময়ে কিয়ামাতের শাস্তি থেকে মুক্তি পেতে চায়, তবুও এই সম্পদ তাদের থেকে কবূল করা হবেনা, আর তাদের জন্য রয়েছে যন্ত্রণাদায়ক শাস্তি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (706,5,37,'নিশ্চয়ই তারা এটা কামনা করবে যে, জাহান্নাম থেকে বের হয়ে যায়, অথচ তারা তা থেকে কখনও বের হতে পারবেনা, বস্তুতঃ তাদের জন্য রয়েছে চিরস্থায়ী শাস্তি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (707,5,38,'আর যে পুরুষ চুরি করে এবং যে নারী চুরি করে, তোমরা তাদের কৃতকর্মের সাজা হিসাবে তাদের (ডান হাত) কেটে ফেল, এটা আল্লাহর পক্ষ থেকে শাস্তি, আর আল্লাহ অতিশয় ক্ষমতাবান, মহা প্রজ্ঞাময়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (708,5,39,'অনন্তর যে ব্যক্তি সীমা লংঘন করার পর (চুরি করার পর) তাওবাহ করে এবং ‘আমলকে সংশোধন করে, তাহলে আল্লাহ তার প্রতি (রাহমাতের) দৃষ্টি বর্ষণ করবেন, নিশ্চয়ই আল্লাহ পরম ক্ষমাশীল, অতি দয়ালু।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (709,5,40,'তুমি কি জাননা যে, আল্লাহরই জন্য রয়েছে আধিপত্য আসমানসমূহে এবং যমীনে, তিনি যাকে ইচ্ছা শাস্তি দেন এবং যাকে ইচ্ছা ক্ষমা করেন! আর আল্লাহ সর্ব বিষয়ের উপর পূর্ণ ক্ষমতাবান।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (710,5,41,'হে রাসূল! যারা দৌড়ে দৌড়ে কুফরীতে পতিত হয় তাদের এই কাজ যেন তোমাকে চিন্তিত না করে, তারা ঐ সব লোকের মধ্য থেকেই হোক যারা নিজেদের মুখেতো (মিছামিছি) বলেঃ আমরা ঈমান এনেছি; অথচ তাদের অন্তর বিশ্বাস করেনি, অথবা তারা সেই সব ইয়াহুদী যারা মিথ্যা কথা শুনতে অভ্যস্ত, তারা তোমার কথাগুলি অন্য সম্প্রদায়ের জন্য কান পেতে শোনে; সেই সম্প্রদায়ের অবস্থা এরূপ যে, তারা তোমার নিকট আসেনি (বরং অন্যকে পাঠিয়েছে); তারা কালামকে ওর স্বস্থান থেকে পরিবর্তন করে থাকে। তারা বলেঃ যদি তোমরা (সেখানে গিয়ে) এই (বিকৃত) বিধান পাও তাহলে তা কবূল করবে, আর যদি এই (বিকৃত) বিধান না পাও তাহলে বিরত থাকবে। আল্লাহ যাকে পরীক্ষায় ফেলার ইচ্ছা করেন তুমি তার জন্য আল্লাহর সাথে কোন কিছুই করার অধিকারী নও; তারা এরূপ যে, তাদের অন্তরগুলি পবিত্র করা আল্লাহর অভিপ্রায় নয়; তাদের জন্য দুনিয়ায় রয়েছে অপমান এবং আখিরাতেও তাদের জন্য রয়েছে ভীষণ শাস্তি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (711,5,42,'তারা মিথ্যা কথা শুনতে অভ্যস্ত, হারাম বস্তু খেতে অভ্যস্ত। অতএব তারা যদি তোমার কাছে আসে তাহলে তুমি তাদের মধ্যে মীমাংসা করে দাও, কিংবা তাদের ব্যাপারে নিলিপ্ত থাক, আর যদি তুমি তাদের থেকে নিলিপ্তই থাক তাহলে তাদের সাধ্য নেই যে, তোমার বিন্দুমাত্রও ক্ষতি করে। আর যদি তুমি বিচার-মীমাংসা কর তাহলে তাদের মধ্যে ন্যায়সঙ্গত বিচার করবে, নিশ্চয়ই আল্লাহ ন্যায়বিচারকদেরকে ভালবাসেন। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (712,5,43,'আর তারা কিরূপে তোমাকে মীমাংসাকারী বানিয়ে নিচ্ছে? অথচ তাদের কাছে তাওরাত রয়েছে, যাতে আল্লাহর বিধান বিদ্যমান! অতঃপর তারা (তোমার মীমাংসা হতে) ফিরে যায়, আর তারা কখনও বিশ্বাসী নয়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (713,5,44,'আমি তাওরাত নাযিল করেছিলাম, যাতে হিদায়াত ছিল এবং (আনুষ্ঠানিক বিধানাবলীর) আলো ছিল, আল্লাহর অনুগত নাবীগণ তদনুযায়ী ইয়াহুদীদেরকে আদেশ করত আর আল্লাহওয়ালাগণ এবং আলিমগণও। কারণ এই যে, তাদেরকে এই কিতাবুল্লাহর সংরক্ষণের আদেশ দেয়া হয়েছিল এবং তারা এর সাক্ষী। অতএব (হে ইয়াহুদী আলিমগণ!) তোমরা মানুষকে ভয় করনা, বরং আমাকে ভয় কর; আর আমার বিধানসমূহের বিনিময়ে (পার্থিব) সামান্য বস্তু গ্রহণ করনা; আর যে ব্যক্তি আল্লাহর অবতারিত (বিধান) অনুযায়ী বিচার করেনা, এমন লোকতো পূর্ণ কাফির।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (714,5,45,'আর আমি তাদের প্রতি তাতে (তাওরাতে) এটা ফরয করেছিলাম যে, প্রাণের বিনিময়ে প্রাণ, চক্ষুর বিনিময়ে চক্ষু, নাকের বিনিময়ে নাক, কানের বিনিময়ে কান, দাঁতের বিনিময়ে দাঁত এবং (তদ্রুপ অন্যান্য) যখমেরও বিনিময়ে যখম রয়েছে; কিন্তু যে ব্যক্তি তাকে ক্ষমা করে দেয়, তাহলে এটা তার জন্য (পাপের) কাফ্ফারা হয়ে যাবে; আর যে ব্যক্তি আল্লাহর অবতারিত বিধান অনুযায়ী ফাইসালা করেনা, তাহলেতো এমন ব্যক্তি পূর্ণ যালিম।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (715,5,46,'আর আমি তাদের পর ঈসা ইবনে মারইয়ামকে এই অবস্থায় প্রেরণ করেছিলাম, সে তার পূর্ববর্তী কিতাবের অর্থাৎ তাওরাতের সত্যায়নকারী ছিল এবং আমি তাকে ইঞ্জীল প্রদান করেছি, যাতে হিদায়াত এবং আলো ছিল, আর ওটা তার পূর্ববর্তী কিতাব অর্থাৎ তাওরাতের সত্যতা সমর্থন করত এবং এটা সম্পূর্ণ রূপে মুত্তাকীদের জন্য হিদায়াত ও নসীহত ছিল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (716,5,47,'আহলে ইঞ্জীলের উচিত-আল্লাহ তাতে যা কিছু অবতীর্ণ করেছেন, তদনুযায়ী হুকুম প্রদান করা, আর যে ব্যক্তি আল্লাহর অবতারিত (বিধান) অনুযায়ী হুকুম প্রদান করেনা, তাহলে তো এ রূপ লোকই পাপাচারী ফাসিক।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (717,5,48,'আর আমি এই কিতাবকে (কুরআন) তোমার প্রতি নাযিল করেছি যা নিজেও সত্যতা গুণে বিভূষিত, (এবং) ওর পূর্ববর্তী কিতাবসমূহেরও সত্যায়নকারী এবং ঐ সব কিতাবের বিষয় বস্তুর সংরক্ষকও। অতএব তুমি তাদের পারস্পরিক বিষয়ে আল্লাহর অবতারিত এই কিতাব অনুযায়ী মীমাংসা কর, যা তুমি প্রাপ্ত হয়েছ, তা থেকে বিরত হয়ে তাদের প্রবৃত্তি অনুযায়ী কাজ করনা, তোমাদের প্রত্যেকের (সম্প্রদায়) জন্য আমি নির্দিষ্ট শারীয়াত এবং নির্দিষ্ট পন্থা নির্ধারণ করেছিলাম; আর যদি আল্লাহ ইচ্ছা করতেন তাহলে তোমাদের সকলকে একই উম্মাত করে দিতেন। কিন্তু তিনি তা করেননি এ কারণে যে, যে ধর্ম তিনি তোমাদেরকে প্রদান করেছেন তাতে তোমাদের সকলকে পরীক্ষা করবেন, সুতরাং তোমরা কল্যাণকর বিষয়সমূহের দিকে ধাবিত হও; তোমাদের সকলকে আল্লাহরই সমীপে প্রত্যাবর্তন করতে হবে, তখন তিনি তোমাদেরকে জানিয়ে দিবেন যে বিষয়ে তোমরা মতবিরোধ করছিলে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (718,5,49,'আর আমি নির্দেশ দিচ্ছি যে, তুমি তাদের পারস্পরিক ব্যাপারে এই প্রেরিত কিতাব অনুযায়ী মীমাংসা করবে এবং তাদের প্রবৃত্তি অনুযায়ী কাজ করবেনা, এবং তাদের দিক থেকে সতর্ক থাকবে যেন তারা তোমাকে আল্লাহ প্রেরিত কোন নির্দেশ হতে বিভ্রান্ত করতে না পারে; অনন্তর তারা যদি মুখ ফিরিয়ে নেয় তাহলে দৃঢ় বিশ্বাস রেখ, আল্লাহর ইচ্ছা এটাই যে, তাদেরকে কোন কোন পাপের কারণে শাস্তি প্রদান করবেন; আর বহু লোকতো নাফরমানই হয়ে থাকে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (719,5,50,'তাহলে কি তারা অজ্ঞতা যুগের মীমাংসা কামনা করে? আর দৃঢ় বিশ্বাসীদের কাছে মীমাংসা কার্যে আল্লাহর চেয়ে কে উত্তম ফাইসালাকারী?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (720,5,51,'হে মু’মিনগণ! তোমরা ইয়াহুদী ও খৃষ্টানদেরকে বন্ধু রূপে গ্রহণ করনা, তারা পরস্পর বন্ধু; আর তোমাদের মধ্যে যে ব্যক্তি তাদের সাথে বন্ধুত্ব করবে নিশ্চয়ই সে তাদেরই মধ্যে গণ্য হবে; নিশ্চয়ই আল্লাহ অত্যাচারী সম্প্রদায়কে সুপথ প্রদর্শন করেননা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (721,5,52,'এ কারণেই যাদের অন্তরে পীড়া রয়েছে তাদেরকে তোমরা দেখেছ যে, তারা দৌড়ে দৌড়ে তাদের (কাফিরদের) মধ্যে প্রবেশ করছে, এবং তারা বলেঃ আমাদের ভয় হচ্ছে যে, আমাদের উপর কোন বিপদ এসে পড়ে না কি! অতএব আশা করা যায় যে, অচিরেই আল্লাহ (মুসলিমদের) পূর্ণ বিজয় দান করবেন অথবা অন্য কোন বিষয় বিশেষভাবে নিজ পক্ষ হতে (প্রকাশ করবেন), অনন্তর তারা নিজেদের অন্তরে লুকায়িত মনোভাবের কারণে লজ্জিত হবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (722,5,53,'আর মুসলিমরা বলবেঃ আরে! এরাই নাকি তারা, যারা অতি দৃঢ়তার সাথে আল্লাহর নামে শপথ করত, আমরা তোমাদের সাথেই আছি? এদের সমস্ত কাজই ব্যর্থ হয়ে গেছে, ফলে তারা অকৃতকার্য হয়ে রইল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (723,5,54,'হে মু’মিনগণ! তোমাদের মধ্য হতে যে ব্যক্তি স্বীয় ধর্ম হতে বিচ্যুত হবে, (এতে ইসলামের কোন ক্ষতি নেই, কেননা) আল্লাহ সত্ত্বরই (তাদের স্থলে) এমন এক সম্প্রদায় সৃষ্টি করবেন যাদেরকে আল্লাহ ভালবাসবেন এবং তারাও আল্লাহকে ভালবাসবে, তারা মুসলিমদের প্রতি মেহেরবান থাকবে, কাফিরদের প্রতি কঠোর হবে, তারা আল্লাহর পথে জিহাদ করবে আর তারা কোন নিন্দুকের নিন্দার পরওয়া করবেনা; এটা আল্লাহর অনুগ্রহ, তা তিনি যাকে ইচ্ছা প্রদান করেন; বস্তুতঃ আল্লাহ প্রাচুর্য দানকারী, মহাজ্ঞানী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (724,5,55,'তোমাদের বন্ধুতো আল্লাহ ও তাঁর রাসূল এবং মু’মিনগণ - যারা সালাত সুপ্রতিষ্ঠিত করে, যাকাত প্রদান করে এবং বিনম্র।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (725,5,56,'আর যারা বন্ধুত্ব রাখবে আল্লাহর সাথে, তাঁর রাসূলের সাথে এবং মু’মিনদের সাথে, তাহলে তারা আল্লাহর দলভুক্ত হল এবং নিশ্চয়ই আল্লাহর দলই বিজয়ী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (726,5,57,'হে মু’মিনগণ! যারা তোমাদের পূর্বে কিতাব প্রাপ্ত হয়েছে, তাদের মধ্যে যারা তোমাদের ধর্মকে হাসি-তামাশার বস্তু মনে করে তাদেরকে এবং অন্যান্য কাফিরদেরকে বন্ধু রূপে গ্রহণ করনা, এবং আল্লাহকে ভয় কর, যদি তোমরা ঈমানদার হয়ে থাক।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (727,5,58,'আর যখন তোমরা সালাতের (আযান দ্বারা) আহবান কর তখন তারা ওর সাথে উপহাস করে; এর কারণ এই যে, তারা এরূপ লোক যারা মোটেই জ্ঞান রাখেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (728,5,59,'তুমি বলে দাও, হে আহলে কিতাব! তোমরা আমাদের মধ্যে কোন্ কাজটি দুষণীয় পাচ্ছ এটা ব্যতীত যে, আমরা ঈমান এনেছি আল্লাহর প্রতি এবং ঐ কিতাবের প্রতি যা আমাদের নিকট প্রেরিত হয়েছে এবং ঐ কিতাবের প্রতিও যা অতীতে প্রেরিত হয়েছে, অথচ তোমাদের অধিকাংশ লোক (উল্লিখিত কিতাবসমূহের) প্রতি ঈমান (এর গন্ডি) হতে বহির্ভূত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (729,5,60,'তুমি বলে দাওঃ আমি কি তোমাদেরকে এরূপ পন্থা হিসাবে ওটা হতেও (যাকে তোমরা মন্দ বলে জান) এরূপ সংবাদ দিব যা আল্লাহর কাছে অধিক নিকৃষ্ট? ওটা ঐ সব লোকের পন্থা, যাদেরকে আল্লাহ অভিসম্পাত করেছেন এবং যাদের প্রতি গযব নাযিল করেছেন ও যাদেরকে বানর ও শুকরে রূপান্তরিত করেছেন এবং যারা তাগুতের ইবাদাত করছে তারা মর্যাদার দিক দিয়ে নিকৃষ্টতর এবং সরল পথ হতে সর্বাধিক বিচ্যুত ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (730,5,61,'আর যখন তারা তোমাদের নিকট আসে তখন বলে, আমরা ঈমান এনেছি; অথচ তারা কুফরী নিয়েই এসেছিল এবং তারা কুফরী নিয়েই চলে গেছে; এবং আল্লাহতো খুব ভাল জানেন যা তারা গোপন রাখে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (731,5,62,'আর তুমি তাদের মধ্যে অনেককে দেখবে, দৌড়ে দৌড়ে পাপ, যুলম ও হারাম ভক্ষণে নিপতিত হচ্ছে; বাস্তবিকই তারা খুব মন্দ কাজ করছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (732,5,63,'তাদেরকে আল্লাহওয়ালা এবং আলিমগণ পাপের বাক্য হতে এবং হারাম মাল ভক্ষণ করা হতে কেন নিষেধ করছেনা? তাদের এ অভ্যাস নিন্দনীয়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (733,5,64,'আর ইয়াহুদীরা বলেঃ আল্লাহর হাত বন্ধ হয়ে গেছে। তাদেরই হাত বন্ধ, এবং তাদের এই উক্তির দরুণ তারা অভিশপ্ত। বরং তাঁর (আল্লাহর) তো উভয় হাত উম্মুক্ত, যেরূপ ইচ্ছা তিনি ব্যয় করেন; আর যে বিষয় তোমার নিকট তোমার রবের পক্ষ হতে প্রেরিত হয় তা তাদের মধ্যে অনেকের নাফরমানী ও কুফর বৃদ্ধির কারণ হয়, এবং তাদের পরস্পরের মধ্যে শক্রতা ও হিংসা নিক্ষেপ করেছি কিয়ামাত পর্যন্ত; যখনই (মুসলিমদের বিরুদ্ধে) যুদ্ধাগ্নি প্রজ্জ্বলিত করতে চায়, আল্লাহ তা নির্বাপিত করে দেন; তারা ভূ-পৃষ্ঠে অশান্তি ছড়িয়ে বেড়ায়; আর আল্লাহ অশান্তি বিস্তারকারীদেরকে ভালবাসেননা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (734,5,65,'আর এই আহলে কিতাব (ইয়াহুদী ও নাসারাহ্) যদি ঈমান আনত এবং তাকওয়া অবলম্বন করত, আমি অবশ্যই তাদের সমস্ত অন্যায় ক্ষমা করে দিতাম এবং অবশ্যই তাদেরকে নি‘আমাতের উদ্যানসমূহে দাখিল করতাম।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (735,5,66,'আর যদি তারা তাওরাত ও ইঞ্জীলের এবং যে কিতাব (অর্থাৎ কুরআন) তাদের রবের পক্ষ হতে তাদের প্রতি অবতীর্ণ হয়েছে, ওর থেকে যথারীতি ‘আমলকারী হত তাহলে তারা উপর (অর্থাৎ আকাশ) হতে এবং নিম্ন (অর্থাৎ যমীন) হতে প্রাচুর্যের সাথে আহার পেত; তাদের একদলতো সরল পথের অনুগামী; আর তাদের অধিকাংশই এরূপ যে, তাদের কার্যকলাপ অতি জঘন্য।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (736,5,67,'হে রাসূল! যা কিছু তোমার রবের পক্ষ থেকে তোমার উপর অবতীর্ণ করা হয়েছে, তুমি (মানুষকে) সব কিছুই পৌঁছে দাও। আর যদি এরূপ না কর তাহলে তোমাকে অর্পিত দায়িত্ব পালন করলেনা; আল্লাহ তোমাকে মানুষ (অর্থাৎ কাফির) হতে রক্ষা করবেন। নিশ্চয়ই আল্লাহ কাফির সম্প্রদায়কে সুপথ প্রদর্শন করেননা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (737,5,68,'তুমি বলে দাওঃ হে আহলে কিতাব! তোমরা কোনো পথেই প্রতিষ্ঠিত নও যে পর্যন্ত না তাওরাত, ইঞ্জীল এবং যে কিতাব (অর্থাৎ কুরআন) তোমাদের নিকট তোমাদের রবের পক্ষ থেকে পাঠানো হয়েছে তার উপর আমল কর; আর অবশ্যই যা তোমার প্রতি তোমার রবের পক্ষ থেকে প্রেরণ করা হয়, তা তাদের মধ্যে অনেকেরই নাফরমানী ও কুফর বৃদ্ধির কারণ হয়ে যায়। অতএব তুমি এই কাফিরদের জন্য মনঃক্ষুন্ন হয়োনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (738,5,69,'এটা সুনিশ্চিত যে, মুসলিম, ইয়াহুদী, সাবেঈ এবং খৃষ্টানদের মধ্যে যে ব্যক্তি আল্লাহর প্রতি ও কিয়ামাতের প্রতি বিশ্বাস রাখে এবং সৎ কাজ করে, এইরূপ লোকদের জন্য শেষ দিনে না কোন প্রকার ভয় থাকবে আর না তারা চিন্তান্বিত হবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (739,5,70,'আমি বানী ইসরাঈল হতে অঙ্গীকার নিয়েছি এবং তাদের কাছে বহু রাসূল প্রেরণ করেছি; যখনই তাদের কাছে কোন নাবী আগমন করত এমন কোন বিধান নিয়ে যা তাদের মনঃপুত হতনা, তখনই তারা কতিপয়কে মিথ্যাবাদী সাব্যস্ত করত এবং কতিপয়কে হত্যাই করে ফেলত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (740,5,71,'আর তারা এই ধারণাই করেছিল যে, কোন শাস্তিই হবেনা, ফলে তারা আরও অন্ধ ও বধির হয়ে গেল, অতঃপর দীর্ঘকাল পর আল্লাহ তাদের প্রতি করুণা করলেন; তবুও তাদের অধিকাংশই অন্ধ ও বধিরই রইল। বস্তুতঃ আল্লাহ তাদের কার্যকলাপসমূহ প্রত্যক্ষ করেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (741,5,72,'নিশ্চয়ই তারা কাফির হয়েছে যারা বলে - মসীহ ইবনে মারইয়ামই আল্লাহ। অথচ মসীহ্ নিজেই বলেছিলঃ হে বানী ইসরাঈল! তোমরা আল্লাহর ইবাদাত কর! যিনি আমার রাব্ব এবং তোমাদেরও রাব্ব; নিশ্চয়ই যে ব্যক্তি আল্লাহর অংশী স্থাপন করবে, আল্লাহ তার জন্য জান্নাত হারাম করে দিবেন এবং তার বাসস্থান হবে জাহান্নাম; এবং এরূপ অত্যাচারীদের জন্য কোন সাহায্যকারী থাকবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (742,5,73,'নিঃসন্দেহে তারাও কাফির যারা বলেঃ ‘আল্লাহ তিনের (অর্থাৎ তিন মা‘বূদের) এক’, অথচ ইবাদাত পাবার যোগ্য এক আল্লাহ ছাড়া অন্য কেহই নেই; আর যদি তারা স্বীয় উক্তিসমূহ হতে নিবৃত্ত না হয় তাহলে তাদের মধ্যে যারা কুফরীতে অটল থাকবে তাদের উপর যন্ত্রণাদায়ক শাস্তি পতিত হবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (743,5,74,'তারা আল্লাহর সমীপে কেন তাওবাহ করেনা এবং তাঁর কাছে ক্ষমা প্রার্থনা করেনা? অথচ আল্লাহ অত্যন্ত ক্ষমাশীল, পরম করুণাময়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (744,5,75,'মারইয়াম পুত্র মাসীহ কেবল একজন রাসূল। তার পূর্বে অনেক রাসূল বিগত হয়েছে এবং তার মা ছিল অতি সত্যবাদী। তারা উভয়ে খাবার খেত। দেখ, কীভাবে আমি তাদের জন্য আয়াতসমূহ বর্ণনা করছি। অতঃপর দেখ, কীভাবে তাদেরকে সত্যবিমুখ করা হচ্ছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (745,5,76,'তুমি বলে দাওঃ তোমরা কি আল্লাহ ছাড়া এমন বস্তুর ইবাদাত কর যা তোমাদের না কোন অপকার করার ক্ষমতা রাখে, আর না কোন উপকার করার? অথচ আল্লাহই সব শুনেন, জানেন। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (746,5,77,'তুমি বলঃ হে আহলে কিতাব! তোমরা নিজেদের ধর্মে অন্যায়ভাবে সীমা লংঘন করনা, এবং ঐ সম্প্রদায়ের (ভিত্তিহীন) কল্পনার উপর চলোনা যারা অতীতে নিজেরাও ভ্রান্তিতে পতিত হয়েছে এবং অন্যান্যদেরকে ভ্রান্তিতে নিক্ষেপ করেছে। বস্তুতঃ তারা সরল পথ থেকে দূরে সরে পড়েছিল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (747,5,78,'বানী ইসরাঈলের মধ্যে যারা কাফির ছিল, তাদের উপর অভিসম্পাত করা হয়েছিল দাঊদ ও ঈসা ইবনে মারইয়ামের মুখে; এই লা’নত এ কারণে করা হয়েছিল যে, তারা অবাধ্য ছিল এবং সীমালংঘন করত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (748,5,79,'তারা যে অন্যায় কাজ করেছিল তা হতে একে অপরকে নিষেধ করতনা; বাস্তবিকই তাদের কাজ ছিল অত্যন্ত গর্হিত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (749,5,80,'তুমি তাদের (ইয়াহুদীদের) অনেককে দেখবে, তারা কাফিরদের সাথে বন্ধুত্ব করছে; যে কাজ তারা ভবিষ্যতের জন্য করেছে তা নিঃসন্দেহে মন্দ, আল্লাহ তাদের প্রতি অসন্তুষ্ট হয়েছেন। ফলতঃ তারা আযাবে চিরকাল থাকবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (750,5,81,'আর যদি তারা আল্লাহর প্রতি ঈমান আনতো এবং নাবীর প্রতি এবং ঐ কিতাবের (তাওরাতের) প্রতি যা তাঁর নাবীর নিকট প্রেরিত হয়েছিল, তাহলে তাদেরকে (মুশরিকদেরকে) কখনও বন্ধু রূপে গ্রহণ করতনা, কিন্তু তাদের অধিকাংশ লোকই ফাসিক।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (751,5,82,'তুমি মানবমন্ডলীর মধ্যে ইয়াহুদী ও মুশরিকদেরকে মুসলিমদের সাথে অধিক শক্রতা পোষণকারী পাবে, আর তন্মধ্যে মুসলিমদের সাথে বন্ধুত্ব রাখার অধিকতর নিকটবর্তী ঐ সব লোককে পাবে যারা নিজেদেরকে নাসারাহ্ (খৃষ্টান) বলে; এটা এ কারণে যে, তাদের মধ্যে বহু আলিম এবং বহু দরবেশ রয়েছে; আর এ কারণে যে, তারা অহংকারী নয়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (752,5,83,'আর যখন রাসূলের কাছে যা অবতীর্ণ হয়েছে তা শ্রবণ করে তখন তুমি দেখতে পাও যে, তাদের অশ্রু বইছে; এ কারণে যে, তারা সত্যকে উপলদ্ধি করতে পেরেছে। তারা এরূপ বলেঃ হে আমাদের রাব্ব! আমরা মু’মিন হয়ে গেলাম, সুতরাং আমাদেরকেও ঐ সব লোকের সাথে লিপিবদ্ধ করে নিন, যারা (মুহাম্মাদ ও কুরআনকে সত্য বলে) স্বীকার করে ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (753,5,84,'আর আমাদের কি এমন ওযর আছে, যে জন্য আমরা ঈমান আনবনা আল্লাহর প্রতি এবং সেই সত্যের প্রতি যা আমাদের কাছে পৌঁছেছে; অথচ এ আশা রাখবো যে, আমাদের রাব্ব নেককারদের সাথে আমাদেরকে শামিল করবেন?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (754,5,85,'ফলতঃ তাদের এই উক্তির বিনিময়ে আল্লাহ তাদেরকে এমন উদ্যানসমূহ প্রদান করবেন, যার তলদেশে নহর বইতে থাকবে, তারা তাতে অনন্তকাল অবস্থান করবে। সৎ কর্মশীলদের জন্য এটাই প্রতিদান!');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (755,5,86,'আর যারা কাফির হয়েছে এবং আমার আয়াতসমূহকে মিথ্যা বলেছে তারা হবে জাহান্নামের অধিবাসী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (756,5,87,'হে মু’মিনগণ! আল্লাহ যে সব বস্তু তোমাদের জন্য হালাল করেছেন তন্মধ্যে সুস্বাদু বস্তুগুলিকে হারাম করনা এবং সীমা লংঘন করনা; নিশ্চয়ই আল্লাহ সীমা লংঘনকারীদেরকে পছন্দ করেননা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (757,5,88,'আর আল্লাহ তোমাদেরকে যা দান করেছেন তন্মধ্য হতে হালাল ও পবিত্র বস্তুগুলি আহার কর, এবং আল্লাহকে ভয় কর - যাঁর প্রতি তোমরা বিশ্বাসী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (758,5,89,'আল্লাহ তোমাদের অর্থহীন কসমের জন্য তোমাদের পাকড়াও করবেননা, কিন্তু তিনি তোমাদেরকে ঐ কসমসমূহের জন্য পাকড়া করবেন যেগুলিকে তোমরা (ভবিষ্যত বিষয়ের প্রতি) দৃঢ় কর, সুতরাং ওর কাফফারা হচ্ছে দশজন অভাবগ্রস্তকে মধ্যম ধরণের খাদ্য প্রদান করা, যা তোমরা নিজ পরিবারের লোকদেরকে খাইয়ে থাক, কিংবা তাদেরকে পরিধেয় বস্ত্র দান করা (মধ্যম ধরণের), কিংবা একজন গোলাম কিংবা বাঁদী মুক্ত করা। আর যে ব্যক্তি সামর্থ্য রাখেনা সে (একাধারে) তিন দিন সিয়াম পালন করবে; এটা তোমাদের কসমসমূহের কাফ্ফারা যখন তোমরা কসম কর এবং অতঃপর ভঙ্গ কর এবং নিজেদের কসমসমূহকে রক্ষা করবে। এই রূপেই আল্লাহ তোমাদের জন্য স্বীয় বিধানসমূহ বর্ণনা করেন যেন তোমরা কৃতজ্ঞতা প্রকাশ কর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (759,5,90,'হে মু’মিনগণ! নিশ্চয়ই মদ, জুয়া, মূর্তি ইত্যাদি এবং লটারীর তীর, এ সব গর্হিত বিষয়, শাইতানী কাজ ছাড়া আর কিছুই নয়। সুতরাং এ থেকে সম্পূর্ণ রূপে দূরে থাক, যেন তোমাদের কল্যাণ হয়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (760,5,91,'শাইতানতো এটাই চায় যে, মদ ও জুয়া দ্বারা তোমাদের পরস্পরের মধ্যে শক্রতা ও হিংসা সৃষ্টি হোক এবং আল্লাহর স্মরণ হতে ও সালাত হতে তোমাদেরকে বিরত রাখে। সুতরাং এখনো কি তোমরা নিবৃত্ত হবেনা?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (761,5,92,'আর তোমরা আল্লাহর আনুগত্য করতে থাক ও রাসূলের অনুগত হও এবং সতর্ক থাকো, আর যদি বিমুখ থাকো তাহলে জেনে রেখ যে, আমার রাসূলের দায়িত্ব ছিল শুধু স্পষ্টভাবে (আদেশ) পৌঁছে দেয়া।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (762,5,93,'যারা ঈমান এনেছে ও ভাল কাজ করে, এরূপ লোকদের উপর কোন পাপ নেই যা তারা পূর্বে আহার করেছে যখন তারা ভবিষ্যতের জন্য পরহেয করে, ঈমান রাখে ও ভাল কাজ করে, পুনরায় সংযত থাকে এবং বিশ্বাস স্থাপন করে। পুনরায় সংযত থাকে ও ভাল কাজ করতে থাকে; বস্তুত আল্লাহ এরূপ সৎকর্মশীলদের ভালবাসেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (763,5,94,'হে মু’মিনগণ! আল্লাহ তোমাদেরকে কতক শিকারের দ্বারা পরীক্ষা করবেন, যে শিকার পর্যন্ত তোমাদের হাত ও তোমাদের বল্লম পৌঁছতে পারবে, এই উদ্দেশে যে, আল্লাহ জেনে নিবেন কে তাঁকে না দেখে ভয় করে। সুতরাং যে ব্যক্তি এরপরও সীমা লংঘন করবে, তার জন্য যন্ত্রণাদায়ক শাস্তি রয়েছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (764,5,95,'হে মু’মিনগণ! তোমরা ইহ্রামের অবস্থায় নিষিদ্ধ এলাকায় বণ্য শিকারকে হত্যা করনা; আর তোমাদের মধ্যে যে ব্যক্তি ইচ্ছা পূর্বক শিকার বধ করবে তার উপর তখন বিনিময় ওয়াজিব হবে, যা (মূল্যের দিক দিয়ে) সেই জানোয়ারের সমতুল্য হয় যাকে সে হত্যা করেছে, যার (আনুমানিক মূল্যের) মীমাংসা তোমাদের মধ্য হতে দু‘জন নির্ভরযোগ্য ব্যক্তি করে দিবে। (অতঃপর নিরূপিত মূল্য দ্বারা) সেই বিনিময় গৃহপালিত চতুস্পদ জন্তু নেয়ায্ স্বরূপ কা‘বা ঘর পর্যন্ত পৌঁছে দিবে, না হয় কাফফারা স্বরূপ (নিরূপিত মূল্যের খাদ্যদ্রব্য) মিসকীনদের মধ্যে বিতরণ করবে, অথবা এর সম পরিমাণ সিয়াম পালন করবে, যেন নিজের কৃতকর্মের পরিণামের স্বাদ গ্রহণ করে; অতীত (ক্রটি) আল্লাহ ক্ষমা করে দিয়েছেন; আর পুনরায় যে ব্যক্তি এরূপ কাজই করবে, আল্লাহ সেই ব্যক্তি হতে (এর) প্রতিশোধ গ্রহণ করবেন; আল্লাহ পরাক্রান্ত, প্রতিশোধ গ্রহণে সক্ষম।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (765,5,96,'তোমাদের জন্য সামুদ্রিক শিকার ধরা ও খাওয়া হালাল করা হয়েছে তোমাদের ও মুসাফিরদের উপকারের জন্য, আর স্থলচর শিকার ধরা তোমাদের জন্য হারাম করা হয়েছে যতক্ষণ তোমরা ইহ্রাম অবস্থায় থাক; আর আল্লাহকে ভয় কর, যাঁর সমীপে তোমাদেরকে একত্রিত করা হবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (766,5,97,'সম্মানিত গৃহ কা‘বাকে আল্লাহ মানুষের স্থিতিশীলতার কারণ রূপে তৈরী করেছেন এবং সম্মানিত মাসসমূহকে, হারামে কুরবানীর জন্তুকে এবং সেই পশুকেও যার গলায় বিশেষ ধরণের বেড়ী পড়ানো হয়েছে। এটা এ জন্য যেন তোমরা দৃঢ় বিশ্বাস রাখ যে, নিশ্চয়ই আল্লাহ আকাশসমূহ ও যমীনস্থিত সব বস্তুরই খবর রাখেন, আর নিশ্চয়ই আল্লাহ সর্ব বিষয়ে মহাজ্ঞানী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (767,5,98,'তোমরা জেনে রেখ যে, নিশ্চয়ই আল্লাহ কঠিন শাস্তিদাতা এবং নিশ্চয়ই অতি ক্ষমাশীল ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (768,5,99,'রাসূলের দায়িত্ব শুধু পৌঁছে দেয়া মাত্র; আর তোমরা যা কিছু প্রকাশ কর এবং যা কিছু গোপন কর তা সবকিছুই আল্লাহ জানেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (769,5,100,'তুমি বলে দাওঃ পবিত্র ও অপবিত্র সমান নয়, যদিও অপবিত্রের আধিক্য তোমাকে চমৎকৃত করে। অতএব হে জ্ঞানীগণ! আল্লাহকে ভয় করতে থাক যেন তোমরা (পূর্ণ) সফলকাম হতে পার।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (770,5,101,'হে মু’মিনগণ! তোমরা এমন সব বিষয় জিজ্ঞেস করনা, যদি তা তোমাদের নিকট প্রকাশ করে দেয়া হয় তাহলে তোমাদের খারাপ লাগবে, আর যদি তোমরা কুরআন অবতীর্ণ হওয়ার সময় উক্ত বিষয়সমূহ সম্বন্ধে জিজ্ঞেস কর তাহলে তোমাদের জন্য প্রকাশ করে দেয়া হবে, অতীতের জিজ্ঞাসাবাদ আল্লাহ ক্ষমা করে দিয়েছেন। বস্তুতঃ আল্লাহ মহাক্ষমাশীল, অতিশয় সহিষ্ণু।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (771,5,102,'এরূপ বিষয় তোমাদের পূর্বে অন্যান্য লোকেরাও জিজ্ঞেস করেছিল, অতঃপর ঐ সব বিষয়ের হক তারা আদায় করেনি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (772,5,103,'আল্লাহ না বাহীরাহর প্রচলন করেছেন, না সায়েবাহর; না ওয়াছীলার আর না হা‘মীর; কিন্তু যারা কাফির তারা আল্লাহর প্রতি মিথ্যারোপ করে, আর অধিকাংশই (ধর্ম) জ্ঞান রাখে না।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (773,5,104,'আর যখন তাদেরকে বলা হয়, ‘আল্লাহর অবতীর্ণ বিধানসমূহের দিকে এসো এবং রাসূলের দিকে’ তখন তারা বলেঃ আমাদের জন্য ওটাই যথেষ্ট যার উপর আমরা আমাদের পূর্ব-পুরুষদেরকে পেয়েছি; যদিও তাদের পূর্ব-পুরুষরা না কোন জ্ঞান রাখতো, আর না হিদায়াত প্রাপ্ত ছিল। তবুও কি তারা তাই করবে?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (774,5,105,'হে মু’মিনগণ! তোমাদের (সংশোধন করার) দায়িত্ব তোমাদের, যারা পথভ্রষ্ট হয়েছে তারা তোমাদের কোন ক্ষতি করতে পারবেনা যদি তোমরা দীনের পথে চল; তোমরা সবাই আল্লাহরই সমীপে প্রত্যাবর্তিত হবে, অতঃপর তোমরা যা কিছু করছিলে সে সম্পর্কে তিনি তোমাদেরকে অবহিত করবেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (775,5,106,'হে মু’মিনগণ! যখন তোমাদের মধ্যে কারও মৃত্যু আসন্ন হয় তখন অসীয়াত করার সময় তোমাদের মধ্য হতে দু’জন লোক সাক্ষী থাকা সঙ্গত। এই দু’ব্যক্তি হবে দীনদার এবং তোমাদের মধ্য হতে; অথবা ভিন্ন সম্প্রদায় হতে দু’জন হবে, যদি তোমরা সফরে থাক এবং ঐ অবস্থায় মৃত্যু তোমাদের উপর উপস্থিত হয়; যদি তোমাদের সন্দেহ হয় তাহলে ওসীদ্বয়কে সালাতের (জামা‘আতের) পর রুখে নাও, অতঃপর তারা আল্লাহর নামে শপথ করে বলবে, আমরা এই শপথের বিনিময়ে কোন স্বার্থ ভোগ করবনা যদি আত্মীয়ও হয়; আর আল্লাহর সাক্ষ্য প্রমাণকে আমরা গোপন করবনা, (যদি এরূপ করি) এমতাবস্থায় আমরা ভীষণ পাপী হব।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (776,5,107,'অতঃপর যদি জানা যায় যে, ওসীদ্বয় কোন পাপে জড়িত হয়ে পড়েছিল তাদের মধ্য হতে (মৃতের) সর্বাপেক্ষা নিকটতম অপর দু’ব্যক্তি তাদের স্থলাভিষিক্ত হবে, অতঃপর উভয়ে (এরূপে) আল্লাহর নামে শপথ করে বলবেঃ নিশ্চয়ই আমাদের এই শপথ তাদের শপথ অপেক্ষা অধিক সত্য এবং আমরা বিন্দুমাত্র ব্যতিক্রম করিনি, (যদি করি তাহলে) এমতাবস্থায় যালিমদের অন্তর্ভুক্ত হব।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (777,5,108,'এটাই এ বিষয়ে অতীব সহজ পন্থা যে, তারা ঘটনা যথাযথভাবে প্রকাশ করে দিবে, অথবা এই ভয় করবে যে, তাদের শপথ গ্রহণ করার পর (পুনঃ) শপথ করানো হবে; আল্লাহকে ভয় কর এবং (বিধানসমূহ) শ্রবণ কর, আর আল্লাহ ফাসিকদেরকে পথ দেখাবেননা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (778,5,109,'যে দিন আল্লাহ সমস্ত রাসূলদেরকে সমবেত করবেন, অতঃপর বলবেনঃ তোমরা (উম্মাতদের নিকট থেকে) কি উত্তর পেয়েছিলে? তারা বলবেঃ (তাদের অন্তরের কথা) আমাদের কিছুই জানা নেই; নিশ্চয়ই আপনি সমস্ত গোপনীয় বিষয়ে সম্পূর্ণ জ্ঞাত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (779,5,110,'যখন আল্লাহ বলবেনঃ হে ঈসা ইবনে মারইয়াম! আমার অনুগ্রহ স্মরণ কর যা তোমার উপর ও তোমার মাতার উপর (প্রদত্ত) হয়েছে। যখন আমি তোমাকে রূহুল কুদুস দ্বারা সাহায্য করেছি, (এবং) তুমি মানুষের সাথে (মাতার) ক্রোধের কথা বলেছ এবং পৌঁঢ় বয়সেও; আর যখন আমি তোমাকে কিতাবসমূহ, প্রগাঢ় জ্ঞান এবং তাওরাত ও ইঞ্জীল শিক্ষা দিয়েছি এবং যখন তুমি আমার আদেশে মাটি দ্বারা পাখীর আকৃতি সদৃশ এক আকৃতি প্রস্তুত করেছিলে, অতঃপর তুমি ওতে ফুৎকার দিলে, যার ফলে ওটা আমার আদেশে পাখী হয়ে যেত, আর তুমি আমার আদেশে জন্মান্ধ ও কুষ্ঠ রোগী নিরাময় করে দিতে আর যখন তুমি আমার আদেশে মৃতদেরকে বের করে দাঁড় করাতে, আর যখন আমি বানী ইসরাঈলকে (তোমাকে হত্যা করা হতে) নিবৃত্ত রেখেছি যখন তুমি তাদের কাছে (স্বীয় নবুওয়াতের) প্রমাণাদী নিয়ে হাজির হয়েছিলে, অতঃপর তাদের মধ্যে যারা কাফির ছিল তারা বলেছিলঃ এটা (মু’জিযাসমূহ) স্পষ্ট যাদু ছাড়া আর কিছুই নয়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (780,5,111,'আর যখন আমি হাওয়ারীদেরকে আদেশ করলামঃ আমার প্রতি এবং আমার রাসূলের প্রতি ঈমান আন তখন তারা বললঃ আমরা ঈমান আনলাম এবং আপনি সাক্ষী থাকুন যে, আমরা মুসলিম।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (781,5,112,'যখন হাওয়ারীরা বললঃ হে ঈসা ইবনে মারইয়াম! আপনার রাব্ব কি এরূপ করতে পারেন যে, আমাদের জন্য আকাশ হতে কিছু খাদ্য প্রেরণ করেন? ঈসা বললঃ আল্লাহকে ভয় কর যদি তোমরা ঈমানদার হয়ে থাক।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (782,5,113,'তারা বললঃ আমাদের উদ্দেশ্য এই যে, আমরা তা থেকে আহার করি এবং আমাদের অন্তর সম্পূর্ণ প্রশান্ত হয়ে যায়, আর আমাদের এই বিশ্বাস আরও সুদৃঢ় হয় যে, আপনি আমাদের নিকট সত্য বলেছেন এবং আমরা এর প্রতি সাক্ষ্যদানকারীদের অন্তর্ভুক্ত হই।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (783,5,114,'ঈসা ইবনে মারইয়াম দু‘আ করলঃ হে আল্লাহ! আমাদের প্রতি আকাশ হতে খাদ্য অবতীর্ণ করুন যেন ওটা আমাদের, অগ্র ও পশ্চাতবর্তীদের জন্য একটি আনন্দের বিষয় হয় এবং আপনার পক্ষ হতে এক নিদর্শন হয়ে থাকে। আর আমাদেরকে রিয্ক প্রদান করুন, বস্তুতঃ আপনিতো সর্বোত্তম জীবিকা প্রদানকারী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (784,5,115,'আল্লাহ বললেনঃ আমি এই খাদ্য তোমাদের প্রতি অবতীর্ণ করব, অনন্তর তোমাদের মধ্য হতে যে এরপর অকৃতজ্ঞ হবে, আমি তাকে এমন শাস্তি দিব যে, বিশ্ববাসীদের মধ্যে ঐ শাস্তি আর কেহকেও দিবনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (785,5,116,'আর যখন আল্লাহ বলবেনঃ হে ঈসা ইবনে মারইয়াম! তুমি কি লোকদেরকে বলেছিলেঃ তোমরা আল্লাহর সাথে আমার ও আমার মায়েরও ইবাদাত কর? ঈসা নিবেদন করবেঃ আপনি পবিত্র! আমার পক্ষে কোনক্রমেই শোভনীয় ছিলনা যে, আমি এমন কথা বলি যা বলার আমার কোন অধিকার নেই; যদি আমি বলে থাকি তাহলে অবশ্যই আপনার জানা থাকবে; আপনিতো আমার অন্তরে যা আছে তাও জানেন, পক্ষান্তরে আপনার জ্ঞানে যা কিছু রয়েছে আমি তা জানিনা; সমস্ত গাইবের বিষয় আপনিই জ্ঞাত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (786,5,117,'আমি তাদেরকে উহা ব্যতীত কিছুই বলিনি যা আপনি আমাকে আদেশ করেছেন যে, তোমরা আল্লাহর ইবাদাত কর, যিনি আমার রাব্ব এবং তোমাদেরও রাব্ব। আমি যতদিন তাদের মধ্যে ছিলাম ততদিন তাদের সম্পর্কে অবগত ছিলাম, অতঃপর আপনি যখন আমাকে তুলে নিলেন তখন আপনিই ছিলেন তাদের রক্ষক, বস্তুতঃ আপনিই সর্ব বিষয়ে পূর্ণ খবর রাখেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (787,5,118,'আপনি যদি তাদেরকে শাস্তি প্রদান করেন তাহলে ওরাতো আপনার বান্দা; আর যদি তাদেরকে ক্ষমা করেন তাহলেতো আপনি পরাক্রমশালী, প্রজ্ঞাময়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (788,5,119,'আল্লাহ বলবেনঃ এটি সেই দিন যেদিন সত্যবাদীদের সত্যবাদীতা কাজে আসবে, তারা উদ্যান প্রাপ্ত হবে, যার তলদেশে নহরসমূহ বইতে থাকবে, আল্লাহ তাদের প্রতি সন্তুষ্ট হয়েছেন এবং তারাও আল্লাহর প্রতি সন্তুষ্ট; এটাই হচ্ছে মহাসফলতা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (789,5,120,'আল্লাহরই জন্য অধিপত্য রয়েছে নভোমন্ডল ও ভূ-মন্ডল এবং ঐ সমস্ত কিছুর যা এতদুভয়ের মাঝে বিদ্যমান; আর তিনি সকল বিষয়ে পূর্ণ ক্ষমতাবান।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (790,6,1,'সমস্ত প্রশংসা আল্লাহরই জন্য যিনি আকাশসমূহ ও পৃথিবী সৃষ্টি করেছেন এবং সৃষ্টি করেছেন আলো ও অন্ধকার; এ সত্ত্বেও যারা কাফির হয়েছে তারা অপর কিছুকে তাদের রবের সমকক্ষ নিরূপণ করেছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (791,6,2,'অথচ তিনি তোমাদের মাটি হতে সৃষ্টি করেছেন, অতঃপর তোমাদের জীবনের জন্য একটি নির্দিষ্ট মেয়াদ নির্ধারণ করেছেন, এছাড়া আরও একটি নির্দিষ্ট মেয়াদ তাঁর নিকট নির্ধারিত রয়েছে, কিন্তু এরপরেও তোমরা সন্দেহ করে থাক।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (792,6,3,'আকাশসমূহ ও পৃথিবীতে ঐ এক আল্লাহই রয়েছেন, তোমাদের অপ্রকাশ্য ও প্রকাশ্য সব অবস্থাই তিনি জানেন, আর তোমরা যা কিছু কর তাও তিনি পূর্ণরূপে অবগত আছেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (793,6,4,'আর তাদের অবস্থা হচ্ছে এই যে, তাদের নিকট তাদের রবের নিদর্শনসমূহ হতে যে কোন নিদর্শনই আসুক না কেন, তা হতেই তারা মুখ ফিরিয়ে নেয়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (794,6,5,'সুতরাং তাদের নিকট যখন সত্য বাণী এসেছে, ওটাও তারা মিথ্যা জেনেছে। অতএব অতি সত্ত্বরই তাদের নিকট সেই বিষয়ের সংবাদ এসে পৌঁছবে, যে ব্যাপারে তারা ঠাট্টা-বিদ্রুপ করত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (795,6,6,'তারা কি ভেবে দেখেনি যে, আমি তাদের পূর্বে বহু দল ও সম্প্রদায়কে ধ্বংস করেছি, যাদেরকে দুনিয়ায় এমন শক্তি সামর্থ্য ও প্রতিপত্তি দিয়েছিলাম যা তোমাদেরকে দিইনি, আর আমি তাদের প্রতি আকাশ হতে প্রচুর বৃষ্টি বর্ষণ করেছি এবং তাদের নিম্নভূমি হতে ঝর্ণাধারা প্রবাহিত করেছি, কিন্তু আমার নি‘আমাতের শোকর না করার পাপের কারণে আমি তাদেরকে ধ্বংস করেছি, এবং তাদের পর অন্য নতুন নতুন জাতি ও সম্প্রদায়সমূহ সৃষ্টি করেছি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (796,6,7,'যদি আমি তোমার প্রতি কাগজে লিখিত কোন কিতাব অবতীর্ণ করতাম, অতঃপর তারা তা নিজেদের হাত দ্বারা স্পর্শও করত; তবুও কাফির ও অবিশ্বাসী লোকেরা বলতঃ এটা প্রকাশ্য যাদু ছাড়া আর কিছুই নয়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (797,6,8,'আর তারা বলে থাকে, তাদের কাছে কোন মালাক/ফেরেশতা কেন পাঠানো হয়না? আমি যদি প্রকৃতই কোন মালাক/ফেরেশতা অবতীর্ণ করতাম তাহলে যাবতীয় বিষয়েরই চূড়ান্ত সমাধান হয়ে যেত, অতঃপর তাদেরকে কিছুমাত্রই অবকাশ দেয়া হতনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (798,6,9,'আর যদি কোন মালাককেও/ফেরেশতাকেও রাসূল করে পাঠাতাম তাহলে তাকে মানুষ রূপেই পাঠাতাম; এতেও তারা ঐ সন্দেহই করত, যে সন্দেহ ও প্রশ্ন এখন তারা করছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (799,6,10,'তোমার পূর্বে যে সব নাবী রাসূল এসেছিল, তাদের সাথেও ঠাট্টা বিদ্রুপ করা হয়েছে, ফলতঃ এই সব ব্যঙ্গ বিদ্রুপের পরিণাম ফল বিদ্রুপকারীদেরকেই পরিবেষ্টন করে ফেলেছিল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (800,6,11,'তুমি বলঃ তোমরা ভূ-পৃষ্ঠ পরিভ্রমণ কর, অতঃপর সত্যকে মিথ্যা প্রতিপন্নকারীদের পরিণাম কি হয়েছে তা গভীর অভিনিবেশ সহকারে লক্ষ্য কর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (801,6,12,'তুমি জিজ্ঞেস করঃ আকাশমন্ডলী ও ধরাধামে অবস্থিত যা কিছু রয়েছে তার মালিক কে? তুমি বলঃ তা সবই আল্লাহর মালিকানায়, অনুগ্রহ করা তিনি তাঁর নীতি বলে গ্রহণ করেছেন, তিনি তোমাদের সকলকে কিয়ামাত দিবসে অবশ্যই সমবেত করবেন যে দিন সম্পর্কে কোন সন্দেহই নেই; যারা নিজেরাই নিজেদের ক্ষতি ও ধবংসের মুখে ফেলেছে তারাই বিশ্বাস করেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (802,6,13,'রাতের অন্ধকারে এবং দিনের আলোয় যা কিছু বসবাস করে ও বর্তমান রয়েছে তা সব কিছুই আল্লাহর। তিনি সব কিছুই শোনেন ও জানেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (803,6,14,'বলঃ আমি কি আল্লাহকে বাদ দিয়ে অন্য কেহকেও আমার অভিভাবক রূপে গ্রহণ করব, যিনি হলেন আকাশ ও পৃথিবীর সৃষ্টিকর্তা? তিনি রিয্ক দান করেন, কিন্তু কারও রিয্ক গ্রহণ করেননা। তুমি বলঃ আমাকে এই আদেশই করা হয়েছে যে, আমি সকলের আগেই ইসলাম গ্রহণ করে তাঁর সামনে মাথা নত করে দিব। আর তুমি মুশরিকদের মধ্যে শামিল হয়োনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (804,6,15,'তুমি বলঃ আমি আমার রবের অবাধ্য হলে, আমি মহাবিচারের দিনের শাস্তির ভয় করছি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (805,6,16,'সেদিন যার উপর হতে শাস্তি প্রত্যাহার করা হবে তার প্রতি আল্লাহ বড়ই অনুগ্রহ করবেন, আর এটাই হচ্ছে প্রকাশ্য মহাসাফল্য।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (806,6,17,'আল্লাহ যদি কারও ক্ষতি সাধন করেন তাহলে তিনি ছাড়া সেই ক্ষতি দূর করার আর কেহ নেই, আর যদি তিনি কারও কল্যাণ করেন, (তাহলে আল্লাহ সেটাও করতে পারেন, কেননা) তিনি সমস্ত কিছুর উপর ক্ষমতাবান।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (807,6,18,'তিনিই তাঁর বান্দাদের উপর একচ্ছত্র ক্ষমতার অধিকারী, তিনিই মহাজ্ঞানী ও সর্ব বিষয়ে ওয়াকিফহাল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (808,6,19,'তুমি তাদেরকে জিজ্ঞেস করঃ কার সাক্ষ্য সবচেয়ে বেশি গণ্য? তুমি বলে দাওঃ আমার ও তোমাদের মধ্যে আল্লাহই হচ্ছেন সাক্ষী, আর এই কুরআন আমার নিকট অহীর মাধ্যমে পাঠানো হয়েছে, যেন আমি তোমাদেরকে এবং যাদের নিকট এটি পৌঁছবে তাদের সকলকে এর দ্বারা সতর্ক করি। বাস্তবিকই তোমরা কি এই সাক্ষ্য দিতে পার যে, আল্লাহর সাথে অন্য কোন মা‘বূদ রয়েছে? তুমি বলঃ আমি এই সাক্ষ্য দিতে পারিনা। তুমি ঘোষণা করঃ তিনিই একমাত্র ইলাহ, আর তোমরা যে শিরকে লিপ্ত রয়েছ, আমার সাথে ওর কোনই সম্পর্ক নেই।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (809,6,20,'যাদেরকে আমি কিতাব দান করেছি, তারা রাসূলকে এমনভাবে জানে ও চিনে, যেরূপ তারা নিজেদের সন্তান-সন্ততিদেরকে জানে ও চিনে, কিন্তু যারা নিজেদেরকে ধ্বংসের মুখে ফেলে দিয়েছে তারা ঈমান আনবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (810,6,21,'যে ব্যক্তি আল্লাহর প্রতি মিথ্যা দোষারোপ করে, কিংবা আল্লাহর আয়াতসমূহকে মিথ্যা প্রতিপন্ন করে, তার চেয়ে বড় যালিম আর কে হতে পারে? এরূপ যালিম লোক কক্ষনোই সাফল্য লাভ করতে পারবে না।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (811,6,22,'সেই দিনটিও স্মরণযোগ্য যেদিন আমি সকলকে একত্রিত করব, অতঃপর যারা আমার সাথে শির্‌ক করেছে তাদেরকে আমি বলবঃ যাদেরকে তোমরা মা‘বূদ বলে ধারণা করতে তারা এখন কোথায়? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (812,6,23,'তখন তাদের এ কথা বলা ব্যতীত আর কোন কথা বলার থাকবেনা, তারা বলবেঃ আল্লাহর শপথ, হে আমাদের রাব্ব! আমরা মুশরিক ছিলামনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (813,6,24,'লক্ষ্য কর, তারা নিজেদের সম্পর্কে কিরূপ মিথ্যা বলছে! তখন যাদেরকে তারা মিথ্যা মা‘বূদ মনোনীত করেছিল তারা সবাই নিরুদ্দেশ হয়ে যাবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (814,6,25,'তাদের মধ্যে এমন লোকও রয়েছে যারা মনোযোগ সহকারে কান লাগিয়ে তোমার কথা শুনে থাকে, (অথচ গ্রহণ করেনা) তোমার কথা যাতে তারা ভাল রূপে বুঝতে না পারে সেজন্য আমি তাদের অন্তরের উপর আবরণ রেখে দিয়েছি এবং তাদের কর্ণে কঠিন ভার (বধিরতা) অর্পণ করেছি। তারা যদি সমস্ত নিদর্শনও অবলোকন করে তবুও তারা ঈমান আনবেনা, এমনকি যখন তারা তোমার কাছে আসে তখন তোমার সাথে অর্থহীন বির্তক জুড়ে দেয়, আর তাদের কাফির লোকেরা (সব কথা শোনার পর) বলেঃ এটা প্রাচীন কালের লোকদের কিস্সা কাহিনী ব্যতীত আর কিছুই নয়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (815,6,26,'তারা নিজেরাতো তা থেকে বিরত থাকে, অধিকন্ত লোকদেরকেও তারা তা থেকে বিরত রাখতে চায়; বস্তুতঃ তারা ধ্বংস করছে শুধুমাত্র নিজেদেরকেই অথচ তারা অনুভব করছেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (816,6,27,'তুমি যদি তাদের সেই সময়ের অবস্থাটি অবলোকন করতে যখন তাদেরকে জাহান্নামের কিনারায় দাঁড় করানো হবে। তখন তারা বলবেঃ হায়! কতই না ভাল হত আমরা যদি আবার দুনিয়ায় ফিরে যেতে পারতাম, আমরা সেখানে আমাদের রবের নিদর্শনসমূহকে অবিশ্বাস করতামনা এবং আমরা ঈমানদার হয়ে যেতাম!');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (817,6,28,'যে সত্য তারা পূর্বে গোপন করেছিল তা তখন তাদের নিকট সুস্পষ্ট রূপে প্রতিভাত হয়ে পড়বে, আর যদি তাদেরকে সাবেক পার্থিব জীবনে ফিরে যেতেও দেয়া হয়, তবুও যা করতে তাদেরকে নিষেধ করা হয়েছিল তারা তা’ই করবে, নিঃসন্দেহে তারা মিথ্যাবাদী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (818,6,29,'তারা বলেঃ এই পার্থিব জীবনই প্রকৃত জীবন, এরপর আর কোন জীবন নেই, আর আমাদেরকে পুনরুত্থিত করা হবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (819,6,30,'হায়! তুমি যদি সেই দৃশ্যটি দেখতে, যখন তাদেরকে তাদের রবের সম্মুখে দন্ডায়মান করা হবে, তখন আল্লাহ তা‘আলা জিজ্ঞেস করবেনঃ এটা (কিয়ামাত) কি সত্য নয়? তখন তারা উত্তরে বলবেঃ হ্যাঁ, আমরা আমাদের রবের (আল্লাহর) শপথ করে বলছি! এটা বাস্তব ও সত্য বিষয়। তখন আল্লাহ বলবেনঃ তাহলে তোমরা এটাকে অস্বীকার ও অমান্য করার ফল স্বরূপ শাস্তির স্বাদ গ্রহণ কর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (820,6,31,'ঐ সব লোকই ক্ষতিগ্রস্ত যারা আল্লাহর সাথে সাক্ষাত হওয়াকে মিথ্যা ভেবেছে। যখন সেই নির্দিষ্ট সময়টি হঠাৎ তাদের কাছে এসে পড়বে তখন তারা বলবেঃ হায়! পিছনে আমরা কতই না দোষ ক্রটি করেছি! তারা নিজেরাই নিজেদের বোঝা পিঠে বহন করবে। শুনে রেখ! তারা যা কিছু বহন করেছে তা কতই না নিকৃষ্টতর বোঝা!');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (821,6,32,'এই পার্থিব জীবন খেল-তামাশা ও আমোদ প্রমোদের ব্যাপার ছাড়া আর কিছুই নয়, যারা তাকওয়া অবলম্বন করে, পরকালের জীবনই হবে তাদের জন্য উৎকৃষ্টতর। তোমরা কি চিন্তা ভাবনা করবেনা?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (822,6,33,'তাদের কথাবার্তায় তোমার যে দুঃখ ও মনঃকষ্ট হয় তা আমি খুব ভালভাবেই জানি, তারা শুধুমাত্র তোমাকেই মিথ্যা প্রতিপন্ন করছেনা, বরং এই পাপিষ্ঠ যালিমরা আল্লাহর আয়াতসমূহকেও অস্বীকার ও অমান্য করছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (823,6,34,'তোমার পূর্বে বহু নাবী ও রাসূলকেও মিথ্যা প্রতিপন্ন করেছে, অতঃপর তারা এই মিথ্যা প্রতিপন্নকে এবং তাদের প্রতি কৃত নির্যাতন ও উৎপীড়নকে অম্লান বদনে সহ্য করেছে, যে পর্যন্ত না তাদের কাছে আমার সাহায্য এসে পৌঁছেছে। আল্লাহর আদেশকে পরিবর্তন করার কেহ নেই। তোমার কাছে পূর্ববর্তী কোন কোন নাবীর কিছু কিছু সংবাদ ও কাহিনীতো পৌঁছে গেছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (824,6,35,'আর যদি তাদের অনাগ্রহ ও উপেক্ষা সহ্য করা তোমার কাছে কঠিন হয়ে পড়ে তাহলে ক্ষমতা থাকলে মাটির কোন সুড়ঙ্গ পথ অনুসন্ধান কর অথবা আকাশে সিঁড়ি লাগিয়ে দাও; অতঃপর তাদের কাছে কোন নিদর্শন নিয়ে এসো, আল্লাহ ইচ্ছা করলে তাদের সকলকে হিদায়াতের উপর সমবেত করতেন। সুতরাং তুমি অবুঝদের মত হয়োনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (825,6,36,'যারা শুনে তারাই সত্যের ডাকে সাড়া দেয়। আল্লাহ মৃতদেরকে জীবিত করে উঠাবেন, অতঃপর তারা তাঁরই কাছে প্রত্যাবর্তিত হবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (826,6,37,'তারা বলেঃ রবের পক্ষ হতে তার প্রতি কোন নিদর্শন কেন অবতীর্ণ করা হলনা? তুমি বলে দাওঃ নিদর্শন অবতীর্ণ করায় আল্লাহ নিঃসন্দেহে পূর্ণ ক্ষমতাবান, কিন্তু অধিকাংশ লোকই তা জ্ঞাত নয়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (827,6,38,'ভূ-পৃষ্ঠে চলাচলকারী প্রতিটি জীব এবং বায়ুমন্ডলে ডানার সাহায্যে উড়ন্ত প্রতিটি পাখীই তোমাদের ন্যায় এক একটি জাতি, আমি কিতাবে কোন বিষয়ই লিপিবদ্ধ করতে বাদ রাখিনি। অতঃপর তাদের সকলকে তাদের রবের কাছে সমবেত করা হবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (828,6,39,'আর যারা আমার নিদর্শনসমূহকে মিথ্যা মনে করে তারা অন্ধকারে নিমজ্জিত মূক ও বধির, আল্লাহ যাকে ইচ্ছা পথভ্রষ্ট করেন এবং যাকে ইচ্ছা হিদায়াতের সরল সহজ পথের সন্ধান দেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (829,6,40,'তুমি তাদেরকে বলঃ তোমরা যদি নিজেদের আদর্শে সত্যবাদী হও তাহলে চিন্তা করে দেখ, যদি তোমাদের প্রতি আল্লাহর শাস্তি এসে পড়ে অথবা তোমাদের নিকট কিয়ামাত দিবস এসে উপস্থিত হয় তখনও কি তোমরা আল্লাহ ব্যতীত অন্য কেহকে ডাকবে?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (830,6,41,'বরং তাঁকেই তোমরা ডাকতে থাকবে। অতএব যে বিপদের জন্য তোমরা তাঁকে ডাকছো, ইচ্ছা করলে তিনি তা তোমাদের থেকে দূর করে দিবেন, আর যাদেরকে তোমরা অংশী করেছিলে তাদের কথা ভুলে যাবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (831,6,42,'আর অবশ্যই আমি তোমার পূর্বে বিভিন্ন কওমের কাছে রাসূল প্রেরণ করেছি। অতঃপর আমি তাদেরকে দারিদ্র্য ও দুঃখ দ্বারা পাকড়াও করেছি, যাতে তারা অনুনয় বিনয় করে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (832,6,43,'সুতরাং তাদের প্রতি যখন আমার শাস্তি এসে পৌঁছল তখন তারা কেন নম্রতা ও বিনয় প্রকাশ করলনা? বরং তাদের অন্তর আরও কঠিন হয়ে পড়ল, আর শাইতান তাদের কাজকে তাদের চোখের সামনে সুশোভিত করে দেখাল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (833,6,44,'অতঃপর তাদেরকে যা কিছু উপদেশ ও নাসীহাত করা হয়েছিল তা যখন তারা ভুলে গেল তখন আমি সুখ শান্তির জন্য প্রতিটি বস্তুর দরজা উন্মুক্ত করে দিলাম। যখন তারা তাদেরকে দানকৃত বস্তু লাভ করে খুব আনন্দিত ও উল্লসিত হল তখন হঠাৎ একদিন আমি তাদেরকে পাকড়াও করলাম, আর তারা সেই অবস্থায় নিরাশ হয়ে পড়ল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (834,6,45,'অতঃপর অত্যাচারী সম্প্রদায়ের মূল শিকড় কেটে ফেলা হল, আর সমস্ত প্রশংসা বিশ্বের রাব্ব আল্লাহরই জন্য।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (835,6,46,'তুমি জিজ্ঞেস করঃ আল্লাহ যদি তোমাদের শ্রবণশক্তি ও দৃষ্টিশক্তি কেড়ে নেন এবং তোমাদের মনের কপাটে তালা লাগিয়ে মোহর এটে দেন তাহলে এই শক্তি তোমাদেরকে আবার দান করতে পারে এমন কোন সত্তা আল্লাহ ব্যতীত আছে কি? লক্ষ্য করতো! আমি আমার নিদর্শনসমূহ ও দলীল প্রমাণাদী কিভাবে ঘুরিয়ে ফিরিয়ে বর্ণনা করছি। এর পরেও তারা তা থেকে মুখ ফিরিয়ে নেয়!');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (836,6,47,'তুমি আরও জিজ্ঞেস করঃ আল্লাহর শাস্তি যদি হঠাৎ করে অথবা প্রকাশ্যে তোমাদের উপর এসে পড়ে তাহলে কি অত্যাচারীরা ছাড়া আর কেহ ধ্বংস হবে?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (837,6,48,'আমি রাসূলদেরকে শুধু এ উদ্দেশে পাঠিয়ে থাকি যে, তারা (সৎ লোকদেরকে) সুসংবাদ দিবে এবং (অসৎ লোকদেরকে) ভয় দেখাবে। সুতরাং যারা ঈমান এনেছে ও চরিত্র সংশোধন করেছে তাদের জন্য কোন ভয়-ভীতি থাকবেনা এবং তারা চিন্তিতও হবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (838,6,49,'আর যারা আমার আয়াত ও নিদর্শনসমূহকে মিথ্যা প্রতিপন্ন করবে তারা তাদের নিজেদের ফাসেকীর কারণে শাস্তি ভোগ করবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (839,6,50,'তুমি বলঃ আমি তোমাদেরকে এ কথা বলিনা যে, আমার কাছে আল্লাহর ধন ভান্ডার রয়েছে, আর আমি অদৃশ্য জগতেরও কোন জ্ঞান রাখিনা, এবং আমি তোমাদেরকে এ কথাও বলিনা যে, আমি একজন মালাক/ফেরেশতা। আমার কাছে যা কিছু অহী রূপে পাঠানো হয়, আমি শুধুমাত্র তারই অনুসরণ করে থাকি। তুমি (তাদেরকে) জিজ্ঞেস করঃ অন্ধ ও চক্ষুস্মান কি সমান হতে পারে? সুতরাং তোমরা কেন চিন্তা ভাবনা করনা?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (840,6,51,'তুমি এর (কুরআন) সাহায্যে ঐ সব লোককে ভীতি প্রদর্শন কর যারা ভয় করে যে, তাদেরকে তাদের রবের কাছে এমন অবস্থায় সমবেত করা হবে যেখানে তিনি ছাড়া তাদের না কোন সাহায্যকারী থাকবে, আর না থাকবে কোন সুপারিশকারী, হয়ত তারা সাবধান হবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (841,6,52,'আর যে সব লোক সকাল সন্ধ্যায় তাদের রবের ইবাদাত করে এবং এর মাধ্যমে তাঁর সন্তুষ্টিই কামনা করে, তাদেরকে তুমি দূরে সরিয়ে দিবেনা, তাদের হিসাব-নিকাশের কোন কিছুর দায়িত্ব তোমার উপর নয় এবং তোমার হিসাব-নিকাশের কোন কিছুর দায়িত্বও তাদের উপর নয়। এর পরও যদি তুমি তাদেরকে দূরে সরিয়ে দাও তাহলে তুমি যালিমদের মধ্যে শামিল হয়ে যাবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (842,6,53,'এভাবেই আমি কিছু লোককে কিছু লোক দ্বারা পরীক্ষায় নিপতিত করি। তারা বলতে থাকেঃ এরাই কি ঐ সব লোক, আমাদের মধ্য থেকে যাদের প্রতি আল্লাহ অনুগ্রহ ও মেহেরবাণী করেছেন? আল্লাহ কি কৃতজ্ঞদের সম্পর্কে অবগত নন?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (843,6,54,'আমার আয়াতসমূহের উপর বিশ্বাস স্থাপনকারীরা যখন তোমার নিকট আসে তখন তাদেরকে বলঃ তোমাদের প্রতি শান্তি বর্ষিত হোক, তোমাদের রাব্ব নিজের উপর দয়া ও অনুগ্রহ করার নীতি বাধ্যতামূলক করে নিয়েছেন। তোমাদের মধ্যে যে ব্যক্তি অজ্ঞতা ও মূর্খতাবশতঃ কোন খারাপ কাজ করে, অতঃপর সে যদি তাওবাহ করে এবং নিজেকে সংশোধন করে তাহলে জানবে যে, তিনি হচ্ছেন ক্ষমাপরায়ণ, করুণাময়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (844,6,55,'এমনিভাবে আমি আমার আয়াত ও নিদর্শনসমূহ সবিস্তার বর্ণনা করে থাকি যেন, অপরাধী লোকদের পথটি সুস্পষ্ট হয়ে পড়ে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (845,6,56,'তুমি কাফিরদেরকে বলে দাওঃ তোমরা আল্লাহকে ছেড়ে যার ইবাদাত কর, আমাকে তার ইবাদাত করতে নিষেধ করা হয়েছে। বলঃ আমি তোমাদের খেয়াল খুশির অনুসরণ করবনা, তাহলে আমি পথভ্রান্ত হয়ে যাব এবং আমি আর পথ প্রাপ্তদের মধ্যে শামিল থাকবনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (846,6,57,'তুমি বলঃ আমি আমার রবের প্রদত্ত একটি সুস্পষ্ট উজ্জ্বল যুক্তি প্রমাণের উপর প্রতিষ্ঠিত, অথচ তোমরা সেই দলীলকে মিথ্যারোপ করছো। যে বিষয়টি তোমরা খুব তাড়াতাড়ি পেতে চাও তার এখতিয়ার আমার হাতে নেই। হুকুমের মালিক আল্লাহ ছাড়া আর কেহ নয়, তিনি সত্য ও বাস্তবানুগ কথা বর্ণনা করেন, তিনিই হচ্ছেন সর্বোত্তম ফাইসালাকারী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (847,6,58,'তুমি বলঃ তোমরা যে বস্তুটি তাড়াতাড়ি পেতে চাও তা যদি আমার এখতিয়ারভুক্ত থাকতো তাহলেতো আমার ও তোমাদের মধ্যে চূড়ান্ত ফাইসালা অনেক আগেই হয়ে যেত, যালিমদেরকে আল্লাহ খুব ভাল করেই জানেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (848,6,59,'অদৃশ্য জগতের চাবিকাঠি তাঁরই নিকট রয়েছে; তিনি ছাড়া আর কেহই তা জ্ঞাত নয়। পৃথিবীতে ও সমুদ্রের সব কিছুই তিনি অবগত আছেন, তাঁর অবগতি ব্যতীত বৃক্ষ হতে একটি পাতাও ঝরে পড়েনা এবং ভূ-পৃষ্ঠের অন্ধকারের মধ্যে একটি দানাও পতিত হয়না, এমনিভাবে কোন সরস ও নিরস বস্তুও পতিত হয়না; সমস্ত কিছুই সুস্পষ্ট কিতাবে লিপিবদ্ধ রয়েছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (849,6,60,'আর সেই মহান সত্তা রাতে নিদ্রারূপে তোমাদের এক প্রকার মৃত্যু ঘটিয়ে থাকেন, আর দিনের বেলা তোমরা যে পরিশ্রম কর তিনি সেটাও সম্যক পরিজ্ঞাত; অতঃপর তিনি নির্দিষ্ট সময়কাল পূরণের নিমিত্ত তোমাদেরকে নিদ্রা থেকে জাগিয়ে থাকেন, পরিশেষে তাঁর কাছেই তোমাদেরকে ফিরে যেতে হবে, তখন তিনি তোমাদেরকে তোমাদের কৃতকর্ম সম্পর্কে অবহিত করবেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (850,6,61,'আর আল্লাহই স্বীয় বান্দাদের উপর প্রতাপশালী, তিনি তোমাদের উপর রক্ষণাবেক্ষণকারী নিযুক্ত করে পাঠিয়ে থাকেন, এমনকি যখন তোমাদের কারও মৃত্যু সময় সমুপস্থিত হয় তখন আমার প্রেরিত দূতগণ তার প্রাণ হরণ করে নেয়, এ ব্যাপারে তারা বিন্দুমাত্র ক্রটি করেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (851,6,62,'তারপর সকলকে তাদের সত্যিকার অভিভাবক আল্লাহর কাছে প্রত্যাবর্তিত করানো হয়। তোমরা জেনে রেখ যে, ঐ দিন একমাত্র আল্লাহই রায় প্রদানকারী হবেন, আর তিনি খুবই ত্বরিৎ হিসাব গ্রহণকারী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (852,6,63,'তুমি তাদেরকে জিজ্ঞেস করঃ স্থলভাগ ও পানিস্থিত অন্ধকার (বিপদ) থেকে তোমাদেরকে কে পরিত্রাণ দিয়ে থাকেন, যখন কাতর কন্ঠে বিনীতভাবে এবং চুপে চুপে তাঁর কাছে প্রার্থনা করে থাক, আর বলতে থাকঃ তিনি যদি আমাদেরকে এই বিপদ থেকে মুক্তি দেন তাহলে আমরা অবশ্যই কৃতজ্ঞদের অন্তর্ভুক্ত হব।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (853,6,64,'তুমি বলে দাওঃ আল্লাহই তোমাদেরকে ঐ বিপদ এবং অন্যান্য প্রতিটি বিপদ আপদ থেকে রক্ষা করেন, কিন্তু এরপরও তোমরা শিরক কর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (854,6,65,'তুমি বলে দাওঃ আল্লাহ তোমাদের ঊর্ধ্বলোক হতে এবং তোমাদের পায়ের তলদেশ হতে শাস্তি প্রেরণ করতে যথেষ্ট ক্ষমতাবান, অথবা তোমাদেরকে দলে দলে বিচ্ছিন্ন করে এক দলের দ্বারা অপর দলের শক্তির স্বাদ গ্রহণ করাবেন; লক্ষ্য কর! আমি বার বার বিভিন্ন উপায়ে আমার নিদর্শন ও যুক্তি প্রমাণ বর্ণনা করছি - উদ্দেশ্য হল, যেন বিষয়টিকে তারা পূর্ণ রূপে জ্ঞানায়ত্ব ও হৃদয়ঙ্গম করে নিতে পারে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (855,6,66,'তোমার সম্প্রদায়ের লোকেরা ওকে মিথ্যা প্রতিপন্ন করছে, অথচ ওটা প্রমানিত সত্য। তুমি বলে দাওঃ আমি তোমাদের উকিল হয়ে আসিনি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (856,6,67,'প্রত্যেকটি সংবাদ প্রকাশের একটি নির্দিষ্ট সময় রয়েছে এবং অচিরেই তোমরা তা জানতে পারবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (857,6,68,'যখন তুমি দেখবে যে, লোকেরা আমার আয়াতসমূহে দোষ-ক্রটি অনুসন্ধান করছে তখন তুমি তাদের নিকট হতে দূরে সরে যাবে, যতক্ষণ না তারা অন্য কোন প্রসঙ্গে নিমগ্ন হয়। শাইতান যদি তোমাকে এটা বিস্মৃত করে তাহলে স্মরণ হওয়ার পর আর ঐ যালিম লোকদের সাথে তুমি বসবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (858,6,69,'ওদের যখন বিচার হবে তখন মুত্তাকীদের উপর এর কোন প্রভাব পরবেনা, কিন্তু ওদেরকে উপদেশ প্রদানের জন্য তাদের উপর দায়িত্ব রয়েছে, হয়তো বা উপদেশের ফলে ওরা পাপাচার হতে বেঁচে থাকতে পারবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (859,6,70,'যারা নিজেদের দীনকে খেল-তামাশার বস্তুতে পরিণত করেছে তুমি তাদেরকে বর্জন করে চলবে, পার্থিব জীবন যাদেরকে সম্মোহিত করে ধোঁকায় নিপতিত করেছে, কুরআন দ্বারা তাদেরকে উপদেশ দিতে থাক, যাতে কোন ব্যক্তি স্বীয় কাজ দোষে ধ্বংস হয়ে না যায়। আল্লাহ ছাড়া তার কোন বন্ধু, সাহায্যকারী ও সুপারিশকারী থাকবে না, আর যেন এই অবস্থার সম্মুখীন না হয় যে, দুনিয়ার সমস্ত কিছুর বিনিময় দিয়েও মুক্তি পেতে চাইলে সেই বিনিময় গ্রহণ করা হবেনা। তারা এমনই লোক যারা নিজেদের কর্মদোষে আটকা পড়ে গেছে; তাদের কুফরী করার কারণে তাদের জন্য ফুটন্ত গরম পানীয় এবং যন্ত্রণাদায়ক শাস্তি রয়েছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (860,6,71,'তুমি বলে দাওঃ আমরা কি আল্লাহ ছাড়া এমন কিছুর ইবাদাত করব, যারা আমাদের কোন উপকার করতে পারবেনা এবং আমাদের কোন ক্ষতিও করতে পারবেনা? অধিকন্তু আমাদেরকে সুপথ প্রদর্শনের পর আমরা কি উল্টা পথে ফিরে যাব? আমরা কি ঐ ব্যক্তির ন্যায় হব যাকে শাইতান মরুভূমির মধ্যে বিভ্রান্ত করে ফেলেছে এবং যে দিশেহারা-লক্ষ্যহারা হয়ে ঘুরে মরছে? তার সহচরেরা তাকে হিদায়াতের দিকে ডেকে বলছে - তুমি আমাদের সঙ্গে এসো। তুমি বলঃ আল্লাহর হিদায়াতই হচ্ছে সত্যিকারের সঠিক হিদায়াত, আর আমাকে সারা জাহানের রবের সামনে মাথা নত করার নির্দেশ দেয়া হয়েছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (861,6,72,'আর তুমি নিয়মিতভাবে সালাত কায়েম কর এবং সেই রাব্বকে ভয় করে চল যাঁর নিকট তোমাদের সকলকে সমবেত করা হবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (862,6,73,'সেই সত্তা আকাশমন্ডল ও ভূ-মন্ডলকে যথাযথভাবে সৃষ্টি করেছেন। যেদিন তিনি বলবেনঃ ‘হাশর হও‘ সেদিন হাশর হয়ে যাবে। তাঁর কথা খুবই যথার্থ বাস্তবানুগ। যেদিন শিঙ্গায় ফুৎকার দেয়া হবে সেদিন একমাত্র তাঁরই হবে বাদশাহী ও রাজত্ব। গোপন ও প্রকাশ্য সব কিছু তাঁর জ্ঞানায়ত্বে। তিনি হচ্ছেন প্রজ্ঞাময়, সর্ববিদিত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (863,6,74,'স্মরণ কর, যখন ইবরাহীম তার পিতা আযারকে বললঃ আপনি মূর্তিগুলোকে মা‘বূদ মনোনীত করেছেন? নিঃসন্দেহে আমি আপনাকে ও আপনার সম্প্রদায়কে প্রকাশ্য ভ্রান্তির মধ্যে নিপতিত দেখছি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (864,6,75,'এমনিভাবেই আমি ইবরাহীমকে আসমান ও যমীনের সৃষ্টি অবলোকন করিয়েছি, যাতে সে বিশ্বাসীদের অন্তর্ভুক্ত হয়ে যায়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (865,6,76,'যখন রাতের অন্ধকার তাকে আবৃত করল তখন সে আকাশের একটি নক্ষত্র দেখতে পেল, আর বললঃ এটাই আমার রাব্ব! কিন্তু যখন ওটা অস্তমিত হল তখন সে বললঃ আমি অস্তমিত বস্তুকে ভালবাসিনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (866,6,77,'আর যখন সে আকাশে চাঁদকে উজ্জ্বল আভায় দেখতে পেল তখন বললঃ এটাই আমার রাব্ব! কিন্তু ওটাও যখন অস্তমিত হল তখন বললঃ আমার রাব্ব যদি আমাকে পথ প্রদর্শন না করেন তাহলে আমি পথভ্রষ্ট সম্প্রদায়ের অন্তর্ভুক্ত হয়ে যাব।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (867,6,78,'অতঃপর যখন সে সূর্যকে উদ্ভাসিত দেখতে পেল তখন সে বললঃ এটিই আমার রাব্ব! এটি বৃহত্তর। অতঃপর যখন ওটা ডুবে গেল তখন বললঃ হে আমার সম্প্রদায়! তোমাদের শিরকের সাথে আমার আদৌ কোন সম্পর্ক নেই, আমি মুক্ত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (868,6,79,'আমার মুখমন্ডলকে আমি একনিষ্ঠভাবে সেই মহান সত্তার দিকে ফিরাচ্ছি যিনি নভোমন্ডল ও ভূ-মন্ডল সৃষ্টি করেছেন, আর আমি মুশরিকদের অন্তর্ভুক্ত নই।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (869,6,80,'আর তার জাতির লোকেরা তার সাথে ঝগড়া করতে থাকলে সে তাদেরকে বললঃ তোমরা কি আল্লাহর ব্যাপারে আমার সাথে ঝগড়া করছো? অথচ তিনি আমাকে সঠিক পথের সন্ধান দিয়েছেন! তোমরা আল্লাহর সাথে যা কিছু শরীক করছো আমি ওদের ভয় করিনা, তবে যদি আমার রাব্ব কিছু চান। প্রতিটি বস্তু সম্পর্কে আমার রবের জ্ঞান খুবই ব্যাপক, এর পরেও কি তোমরা উপদেশ গ্রহণ করবেনা?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (870,6,81,'তোমাদের মনগড়া ও বানানো শরীকদেরকে আমি কিরূপে ভয় করতে পারি? অথচ তোমরা এই ভয় করছনা যে, আল্লাহর সাথে যাদেরকে তোমরা শরীক করছ তাদের ব্যাপারে আল্লাহ তোমাদের কাছে কোন দলীল প্রমাণ অবতীর্ণ করেননি, আমাদের দুই দলের মধ্যে কারা অধিকতর শান্তি ও নিরাপত্তা লাভের অধিকারী যদি তোমাদের জানা থাকে তাহলে বলত?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (871,6,82,'প্রকৃত পক্ষে তারাই শান্তি ও নিরাপত্তার অধিকারী এবং তারাই সঠিক পথে পরিচালিত, যারা নিজেদের ঈমানকে যুলমের সাথে (শির্‌কের সাথে) মিশ্রিত করেনি। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (872,6,83,'আর এটাই ছিল আমার যুক্তি প্রমাণ, যা আমি ইবরাহীমকে তার স্বজাতির মুকাবিলায় দান করেছিলাম। আমি যাকে ইচ্ছা করি, সম্মান-মর্তবা ও মহত্ত্ব বাড়িয়ে দেই, নিঃসন্দেহে তোমার রাব্ব প্রজ্ঞাময় ও বিজ্ঞ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (873,6,84,'আমি তাকে ইসহাক ও ইয়াকূবকে দান করেছি এবং উভয়কেই সঠিক পথের সন্ধান দিয়েছি, আর তার পূর্বে নূহকেও সঠিক পথের হিদায়াত দিয়েছি; আর তার (ইবরাহীমের) বংশের মধ্যে দাঊদ, সুলাইমান, আইউব, ইউসুফ, মূসা ও হারূনকে এমনিভাবেই সঠিক পথের সন্ধান দিয়েছি। এমনিভাবেই আমি সৎ ও পুণ্যশীল লোকদেরকে প্রতিদান দিয়ে থাকি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (874,6,85,'আর যাকারিয়া, ইয়াহইয়া, ঈসা ও ইলিয়াস - তারা প্রত্যেকেই সৎ লোকদের অন্তর্ভুক্ত ছিল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (875,6,86,'আর ইসমাঈল, ঈসা, ইউনুস ও লূত - এদের প্রত্যেককেই আমি নবুওয়াত দান করে সমগ্র বিশ্বের উপর মহত্ত্ব ও শ্রেষ্ঠত্ব দান করেছি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (876,6,87,'আর এদের বাপ-দাদা, সন্তান, সন্ততি ও ভাইদের মধ্যে অনেককে আমি মনোনীত করে সঠিক ও সোজা পথে পরিচালিত করেছি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (877,6,88,'এটাই আল্লাহর হিদায়াত; তিনি তাঁর বান্দার মধ্যে যাকে চান এই পথে পরিচালিত করেন। কিন্তু তারা যদি শির্‌ক করত তাহলে তারা যা কিছুই করত, সবই ব্যর্থ হয়ে যেত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (878,6,89,'এরা ছিল সেই লোক, যাদেরকে আমি কিতাব, শাসনভার ও নবুওয়াত দান করেছি। সুতরাং যদি এরা তোমার নবুওয়াতকে অস্বীকার করে তাহলে তাদের স্থলে আমি এমন এক জাতিকে নিয়োগ করব, যারা ওটা অস্বীকার করেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (879,6,90,'এরা হচ্ছে ওরাই, যাদেরকে আল্লাহ সুপথ প্রদর্শন করেছিলেন। সুতরাং তুমি তাদের পথ অনুসরণ করে চল। তুমি বলে দাওঃ আমি কুরআন ও দীনের তাবলীগের বিনিময়ে তোমাদের কাছে কোন প্রতিদান চাইনা। এই কুরআন সমগ্র জগতবাসীর জন্য উপদেশের ভান্ডার ছাড়া কিছুই নয়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (880,6,91,'এই লোকেরা আল্লাহর যথাযথ মর্যাদা উপলব্ধি করেনি। কেননা তারা বললঃ আল্লাহ কোন মানুষের উপর কোন কিছু অবতীর্ণ করেননি; তুমি তাদেরকে জিজ্ঞেস করঃ মানুষের হিদায়াত ও আলোকবর্তিকা রূপে যে কিতাব মূসা এনেছিল তা কে অবতীর্ণ করেছেন? তোমরা সেই কিতাব খন্ড খন্ড করে বিভিন্ন পত্রে রেখেছ, ওর কিয়দংশ তোমরা প্রকাশ করছ এবং বহুলাংশ গোপন করছ। (ঐ কিতাব দ্বারা) তোমাদেরকে বহু বিষয়ে অবহিত করা হয়েছে, যা তোমরা ও তোমাদের পূর্ব-পুরুষরা জানতেনা; তুমি বলে দাওঃ তা আল্লাহই অবতীর্ণ করেছেন। সুতরাং তুমি তাদেরকে তাদের বাতিল ধারণার উপর ছেড়ে দাও, তারা খেলা করতে থাকুক।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (881,6,92,'আর এই কিতাবও (কুরআন) আমিই অবতীর্ণ করেছি; যা খুবই বারাকাতময় কিতাব এবং পূর্বের সকল কিতাবকে সত্যায়িত করে থাকে, যেন তুমি কেন্দ্রীয় মাক্কা নগরী এবং ওর চতুস্পার্শ্বস্থ জনপদের লোকদেরকে ওর দ্বারা ভীতি প্রদর্শন কর। যারা পরকালে বিশ্বাস রাখে তারা এই কিতাবকেও বিশ্বাস করবে এবং ওর প্রতি ঈমান আনবে, আর তারা নিয়মিতভাবে সালাতও আদায় করে থাকে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (882,6,93,'আর ঐ ব্যক্তি অপেক্ষা অধিক অত্যাচারী কে হতে পারে যে আল্লাহর প্রতি মিথ্যা আরোপ করেছে? অথবা এরূপ বলেঃ আমার উপর অহী নাযিল করা হয়েছে, অথচ তার উপর প্রকৃত পক্ষে কোন অহী নাযিল করা হয়নি এবং যে ব্যক্তি এও বলেঃ যেরূপ কালাম আল্লাহ নাযিল করেছেন, তদ্রুপ আমিও আনয়ন করছি। আর তুমি যদি দেখতে পেতে (ঐ সময়ের অবস্থা) যে সময় যালিমরা হবে মৃত্যু সংকটে (পরিবেষ্টিত); আর মালাইকা/ফেরেশতারা হাত বাড়িয়ে বলবেঃ নিজেদের প্রাণগুলি বের কর, আজ তোমাদেরকে সেই সব অপরাধের শাস্তি হিসাবে লাঞ্ছনাদায়ক শাস্তি দেয়া হবে যা তোমরা আল্লাহর উপর মিথ্যা দোষারোপ করে অকারণ প্রলাপ বকেছিলে এবং তাঁর আয়াতসমূহ কবূল করা হতে অহংকার করেছিলে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (883,6,94,'আর তোমরা আমার কাছে নিঃসঙ্গ এসেছ, যেভাবে প্রথমবার আমি তোমাদেরকে সৃষ্টি করেছিলাম, আর যা কিছু আমি তোমাদেরকে দিয়েছিলাম তা তোমরা নিজেদের পশ্চাতেই ছেড়ে এসেছ, আর আমিতো তোমাদের সাথে তোমাদের সেই সুপারিশকারীদেরকে দেখছিনা যাদের সম্বন্ধে তোমরা দাবী করতে যে, তারা তোমাদের কাজকর্মে (আমার) শরীক; বাস্তবিকই তোমাদের পরস্পরের সম্পর্কতো বিচ্ছিন্ন হয়ে গেছে, আর তোমরা যা কিছু ধারণা করতে তা সবই আজ তোমাদের নিকট থেকে উধাও হয়ে গেছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (884,6,95,'নিশ্চয়ই দানা ও বীজ দানকারী হচ্ছেন আল্লাহ। তিনিই জীবন্তকে প্রাণহীন থেকে বের করেন এবং তিনিই প্রাণহীনকে জীবন্ত হতে নিগর্তকারী; তিনিইতো আল্লাহ, তাহলে তোমরা কোথায় যাচ্ছো?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (885,6,96,'তিনিই রাতের আবরণ বিদীর্ণ করে রঙ্গিন প্রভাতের উন্মেষকারী, তিনিই রাতকে বিশ্রামকাল এবং সূর্য ও চাঁদকে সময়ের নিরূপক করে দিয়েছেন; এটা হচ্ছে সেই পরম পরাক্রান্ত ও সর্ব পরিজ্ঞাতার (আল্লাহর) নির্ধারণ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (886,6,97,'আর তিনিই তোমাদের জন্য নক্ষত্ররাজিকে সৃষ্টি করেছেন, যেন তোমরা ওগুলির সাহায্যে অন্ধকারে পথের সন্ধান পেতে পার স্থল ভাগে এবং সমুদ্রে। নিশ্চয়ই আমি প্রমাণসমূহ বিশদভাবে বর্ণনা করে দিয়েছি ঐ সমস্ত লোকের জন্য যারা জ্ঞান রাখে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (887,6,98,'তিনিই তোমাদেরকে এক ব্যক্তি হতে সৃষ্টি করেছেন, অতঃপর (প্রত্যেকের জন্য) একটি স্থান অধিক দিন থাকার জন্য এবং একটি স্থান অল্প দিন থাকার জন্য রয়েছে, এই নিদর্শনসমূহ আমি তাদের জন্য সুস্পষ্টভাবে বর্ণনা করলাম যাদের বুদ্ধি বিবেচনা আছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (888,6,99,'আর তিনি আকাশ হতে পানি বর্ষণ করেন, ওর সাহায্যে সব রকমের উদ্ভিদ আমি (আল্লাহ) উৎপন্ন করি; অতঃপর তা থেকে সবুজ শাখা বের করি, ফলতঃ তা থেকে আমি উপর্যুপরি উত্থিত বীজ উৎপন্ন করি। এবং খেজুর বৃক্ষ থেকে অর্থাৎ ওর পুস্পকণিকা থেকে ছড়া হয় যা নিম্ন দিকে ঝুঁকে পড়ে, আর আঙ্গুরসমূহের উদ্যান এবং যাইতূন ও আনার যা পরস্পর সাদৃশ্যযুক্ত। প্রত্যেক ফলের প্রতি লক্ষ্য কর যখন ওটা ফলে এবং ওর পরিপক্ক হওয়ার প্রতি লক্ষ্য কর। এই সমুদয়ের মধ্যে নিদর্শনসমূহ রয়েছে তাদেরই জন্য যারা ঈমান রাখে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (889,6,100,'আর এই (অজ্ঞ) লোকেরা জিনদেরকে আল্লাহর শরীক বানিয়ে নিয়েছে, অথচ আল্লাহই ঐগুলিকে সৃষ্টি করেছেন, আর না জেনে না বুঝে তারা তাঁর জন্য পুত্র কন্যা রচনা করে; তিনি মহিমান্বিত (পবিত্র), এদের আরোপিত বিশেষণগুলি হতে বহু উর্ধ্বে তিনি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (890,6,101,'তিনি আসমান ও যমীনের স্রষ্টা; তাঁর সন্তান হবে কি করে? অথচ তাঁর জীবন সঙ্গিনীই কেহ নেই। তিনিই প্রত্যেকটি জিনিস সৃষ্টি করেছেন, প্রত্যেকটি জিনিস সম্পর্কে তাঁর ভাল রুপে জ্ঞান রয়েছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (891,6,102,'তিনি আল্লাহ, তোমাদের পরওয়ারদিগার। তিনি ছাড়া অন্য কেহই মা‘বূদ নেই, প্রত্যেক বস্তুরই স্রষ্টা তিনি, অতএব তোমরা তাঁরই ইবাদাত কর, তিনিই সব জিনিসের উপর দায়িত্বশীল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (892,6,103,'কোন মানব-দৃষ্টি তাঁকে দেখতে পারেনা, অথচ তিনি সকল কিছুই দেখতে পান এবং তিনি অতীব সূক্ষ্মদর্শী এবং সর্ব বিষয়ে ওয়াকিফহাল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (893,6,104,'এখন নিশ্চয়ই তোমাদের কাছে তোমাদের রবের পক্ষ হতে সত্য দর্শনের উপায়সমূহ পৌঁছেছে, অতএব যে ব্যক্তি নিজের গভীর দৃষ্টিতে অবলোকন করবে সে নিজেরই কল্যাণ সাধন করবে, আর যে অন্ধ থাকবে সে নিজেই ক্ষতিগ্রস্ত হবে, আর আমিতো তোমাদের প্রহরী নই।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (894,6,105,'এ রূপেই আমি নিদর্শনসমূহ প্রকাশ করি, যেন লোকেরা না বলে - তুমি কারও নিকট থেকে পাঠ করে নিয়েছ, আর যেন আমি একে বুদ্ধিমান লোকদের জন্য প্রকাশ করে দিই।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (895,6,106,'তোমার প্রতি তোমার রবের পক্ষ থেকে যে অহী নাযিল হয়েছে, তুমি তারই অনুসরণ করে চল, তিনি ছাড়া অন্য কেহই মা‘বূদ নেই, আর অংশীবাদীদের থেকে মুখ ফিরিয়ে নাও।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (896,6,107,'আর আল্লাহর যদি অভিপ্রায় হত তাহলে এরা শির্‌ক করতনা; আর আমি তোমাকে এদের রক্ষক নিযুক্ত করিনি এবং তুমি তাদের উপর ক্ষমতাপ্রাপ্ত নও।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (897,6,108,'এরা আল্লাহকে বাদ দিয়ে যাদের ইবাদাত (পূজা-অর্চনা) করে তোমরা তাদেরকে গালাগালি করনা, তাহলে তারা অজ্ঞতা বশতঃ বৈরীভাবে আল্লাহকেই গালাগালি দিতে শুরু করবে। আমিতো এ রূপেই প্রতিটি জনগোষ্ঠীর জন্য তাদের ‘আমলকে চাকচিক্যময় করে দিয়েছি। শেষ পর্যন্ত তাদেরকে তাদের রবের কাছে ফিরে যেতে হবে, তখন তারা কি কি কাজ করেছিল তা তিনি তাদেরকে জানিয়ে দিবেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (898,6,109,'আর কঠিন অঙ্গীকার সহকারে আল্লাহর নামে কসম করে তারা বলেঃ কোন নিদর্শন (মু’জিযা) তাদের কাছে এলে তারা ঈমান আনবে; তুমি বলে দাওঃ নিদর্শনগুলি সমস্তই আল্লাহর অধিকারে, আর (হে মুসলিমরা!) কি করে তোমাদেরকে বুঝানো যাবে যে, নিদর্শন এলেও তারা ঈমান আনবেনা!');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (899,6,110,'আর যেহেতু তারা প্রথমবার ঈমান আনেনি, এর ফলে তাদের মনোভাবের ও দৃষ্টিভঙ্গির পরিবর্তন করে দিব এবং তাদেরকে তাদের অবাধ্যতার মধ্যেই বিভ্রান্তের ন্যায় ঘুরে বেড়াতে ছেড়ে দিব।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (900,6,111,'আমি যদি তাদের কাছে মালাক অবতীর্ণ করতাম, আর মৃতরাও যদি তাদের সাথে কথা বলত এবং দুনিয়ার সমস্তই যদি আমি তাদের চোখের সামনে সমবেত করতাম, তবুও তারা ঈমান আনতনা আল্লাহর ইচ্ছা ব্যতীত। কিন্তু তাদের অধিকাংশই মূর্খ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (901,6,112,'আর এমনিভাবেই আমি প্রত্যেক নাবীর জন্য বহু শাইতানকে শত্রুরূপে সৃষ্টি করেছি; তাদের কতক মানুষ শাইতানের মধ্য হতে এবং কতক জিন শাইতানের মধ্য হতে হয়ে থাকে, এরা একে অন্যকে কতকগুলি মনোমুগ্ধকর ধোকাপূর্ণ ও প্রতারণাময় কথা দ্বারা প্ররোচিত করে থাকে। তোমার রবের ইচ্ছা হলে, তারা এমন কাজ করতে পারতনা। সুতরাং তুমি তাদেরকে এবং তাদের মিথ্যা রচনাগুলিকে বর্জন করে চলবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (902,6,113,'যারা পরকালের প্রতি ঈমান রাখেনা তাদের অন্তরকে ঐ দিকে অনুরক্ত হতে দাও; এবং তারা যেন তাতে সন্তুষ্ট থাকে, আর তারা যেসব কাজ করে তা যেন তারা আরও করতে থাকে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (903,6,114,'(তুমি তাদেরকে জিজ্ঞেস কর) তাহলে কি আমি আল্লাহকে বর্জন করে অন্য কেহকে মীমাংসাকারী ও বিচারক রূপে অনুসন্ধান করব? অথচ তিনিই তোমাদের কাছে এই কিতাবকে বিস্তারিতভাবে অবতীর্ণ করেছেন! আমি যাদেরকে কিতাব দান করেছি তারা জানে যে, এই কিতাব তোমার রবের পক্ষ হতেই যথার্থ ও সঠিকভাবে অবতীর্ণ করা হয়েছে, সুতরাং তুমি সন্দেহ পোষণকারীদের মধ্যে শামিল হয়োনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (904,6,115,'তোমার রবের বাণী সত্যতা ও ইনসাফের দিক দিয়ে পরিপূর্ণ, তাঁর বাণী পরিবর্তনকারী কেহই নেই, তিনি সবকিছু শোনেন ও সবকিছু জানেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (905,6,116,'তুমি যদি দুনিয়াবাসী অধিকাংশ লোকের কথামত চল তাহলে তারা তোমাকে আল্লাহর পথ হতে বিচ্যূত করে ফেলবে, তারাতো শুধু অনুমানের অনুসরণ করে, আর তারা শুধু অনুমানভিত্তিক কথা বলে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (906,6,117,'কোন্ ব্যক্তি আল্লাহর পথ হতে বিভ্রান্ত হয়েছে তা তোমার রাব্ব নিশ্চিতভাবে অবগত আছেন, আর তিনি তাঁর পথের পথিকগণ সম্পর্কেও খুব ভালভাবে জ্ঞাত রয়েছেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (907,6,118,'অতএব যে জীবকে আল্লাহর নাম নিয়ে যবাহ করা হয়েছে তা তোমরা আহার কর, যদি তোমরা আল্লাহর বিধানের প্রতি ঈমান রাখ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (908,6,119,'যে জন্তুর উপর যবাহ করার সময় আল্লাহর নাম উচ্চারণ করা হয়েছে, তা আহার না করার তোমাদের কাছে কি কারণ থাকতে পারে? অথচ আল্লাহ তোমাদের উপর যা কিছু হারাম করেছেন তা তিনি সবিস্তারে বর্ণনা করেছেন, তবে নিরূপায় অবস্থায় তোমরা উক্ত হারাম বস্তুও আহার করতে পার, নিঃসন্দেহে কোন দীনী জ্ঞান না থাকা সত্ত্বেও নিজেদের ইচ্ছা, বাসনা ও ভ্রান্ত ধারণার ভিত্তিতে অনেকে বহু লোককে পথভ্রষ্ট করেছে, নিশ্চয়ই তোমার রাব্ব সীমা লংঘনকারীদের সম্পর্কে ভালভাবেই ওয়াকিফহাল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (909,6,120,'তোমরা প্রকাশ্য পাপ কাজ পরিত্যাগ কর এবং পরিত্যাগ কর গোপনীয় পাপ কাজও। যারা পাপ কাজ করে তাদেরকে অতি সত্ত্বরই তাদের মন্দ কাজের প্রতিফল দেয়া হবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (910,6,121,'আর যে জন্তু যবাহ করার সময় আল্লাহর নাম উচ্চারণ করা হয়না তা তোমরা আহার করনা। কেননা এটা গর্হিত বস্তু, শাইতানরা নিজেদের সঙ্গী সাথীদের মনে নানা প্রকার সন্দেহ ও প্রশ্ন সৃষ্টি করে, যেন তারা তোমাদের সাথে ঝগড়া ও বিতর্ক করতে পারে। যদি তোমরা তাদের ‘আকীদাহ্ বিশ্বাস ও কাজ কর্মে আনুগত্য কর তাহলে নিঃসন্দেহে তোমরা মুশরিক হয়ে যাবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (911,6,122,'এমন ব্যক্তি-যে ছিল প্রাণহীন, অতঃপর তাকে আমি জীবন দান করি এবং তার জন্য আমি এমন আলোকের ব্যবস্থা করে দিই, যার সাহায্যে সে জনগণের মধ্যে চলাফিরা করতে থাকে, সে কি এমন কোন লোকের মত হতে পারে যে ডুবে আছে অন্ধকারে এবং তা হতে বের হওয়ার পথ পাচ্ছেনা? এ রূপেই কাফিরদের জন্য তাদের কার্যকলাপ মনোমুগ্ধকর করে দেয়া হয়েছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (912,6,123,'আর এমনিভাবেই আমি প্রত্যেক জনপদে অপরাধীদের জন্য কিছু সর্দার নিয়োগ করেছি যেন তারা সেখানে চক্রান্ত করে। তাদের সে চক্রান্ত নিজেদের বিরুদ্ধেই। কিন্তু তারা তা উপলব্ধি করতে পারেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (913,6,124,'তাদের সামনে যখন কোন নিদর্শন আসে তখন তারা বলেঃ আল্লাহর রাসূলদেরকে যা কিছু দেয়া হয়েছিল, আমাদের অনুরূপ জিনিস না দেয়া পর্যন্ত আমরা ঈমান আনবনা। রিসালাতের দায়িত্ব কার উপর অর্পণ করতে হবে তা আল্লাহ ভালভাবেই জানেন। এই অপরাধী লোকেরা অতি সত্ত্বরই তাদের ষড়যন্ত্র ও প্রতারণার জন্য আল্লাহর নিকট লাঞ্ছনা ও কঠিন শাস্তি প্রাপ্ত হবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (914,6,125,'অতএব আল্লাহ যাকে হিদায়াত করতে চান, ইসলামের জন্য তার অন্তঃকরণ উন্মুক্ত করে দেন, আর যাকে পথভ্রষ্ট করার ইচ্ছা করেন, তিনি তার অন্তঃকরণ সংকুচিত করে দেন - খুবই সংকুচিত করে দেন, এমনভাবে সংকুচিত করেন যেন মনে হয় সে আকাশে আরোহণ করছে। এমনিভাবেই যারা ঈমান আনেনা তাদেরকে আল্লাহ কলুষময় করে থাকেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (915,6,126,'আর এটাই হচ্ছে তোমার রবের সহজ সরল পথ, আমি উপদেশ গ্রহণকারী লোকদের জন্য আয়াতসমূহ বিশদভাবে বর্ণনা করে দিয়েছি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (916,6,127,'তাদের জন্য তাদের রবের নিকট রয়েছে এক শান্তির আবাস তাদের কৃতকর্মের কারণে, তিনিই হচ্ছেন তাদের অভিভাবক।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (917,6,128,'আর যেদিন আল্লাহ তাদের সকলকে একত্রিত করবেন, সেদিন তিনি বলবেনঃ হে জিন সম্প্রদায়! তোমরা মানুষের মধ্যে অনেককে বিভ্রান্ত করে অনুগামী করেছ, আর ওদের মধ্যে যাদের মানুষের সাথে বন্ধুৃত্বের সম্পর্ক ছিল তারা স্বীকারোক্তিতে বলবেঃ হে আমাদের রাব্ব! আমরা একে অপরের দ্বারা উপকৃত হয়েছি। হায়! আপনি আমাদের জন্য যে নির্দিষ্ট সময় নির্ধারণ করেছেন তা এসে গেছে! তখন (কিয়ামাত দিবসে) আল্লাহ (সমস্ত কাফির জিন ও মানুষকে) বলবেনঃ জাহান্নামই হচ্ছে তোমাদের বাসস্থান, তাতে তোমরা চিরস্থায়ীভাবে থাকবে, তবে আল্লাহ যাদেরকে চাইবেন (তারাই তা থেকে মুক্তি পেতে পারে) তোমাদের রাব্ব অতিশয় সম্মানিত এবং অত্যন্ত জ্ঞানবান।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (918,6,129,'এমনিভাবেই আমি যালিমদেরকে (কাফিরদেরকে) তাদের কৃতকর্মের ফলে পরস্পরকে পরস্পরের উপর প্রভাবশালী ও কর্তৃত্বশালী বানিয়ে দিব।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (919,6,130,'(কিয়ামাত দিবসে আল্লাহ জিজ্ঞেস করবেন) হে জিন ও মানব জাতি! তোমাদের কাছে কি তোমাদের মধ্য হতেই নাবী রাসূল আসেনি যারা তোমাদের কাছে আমার আয়াতসমূহ বর্ণনা করত এবং আজকের এ দিনের ব্যাপারে তোমাদের সাক্ষাত হওয়ার ভীতি প্রদর্শন করেনি? তারা জবাব দিবেঃ হ্যাঁ, আমরাই আমাদের বিরুদ্ধে সাক্ষ্য দিচ্ছি (অর্থাৎ আমরা অপরাধ করেছি), পার্থিব জীবনই তাদেরকে ধোঁকায় নিপতিত করেছিল। আর তারাই নিজেদের বিরুদ্ধে সাক্ষ্য দিবে যে, তারা কাফির ছিল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (920,6,131,'এই রাসূল প্রেরণ এ জন্য যে, তোমার রাব্ব কোন জনপদকে উহার অধিবাসীরা সত্য সম্পর্কে অজ্ঞাত থাকা অবস্থায় অন্যায়ভাবে ধ্বংস করেন না।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (921,6,132,'আর প্রত্যেক লোকই নিজ নিজ ‘আমলের কারণে মর্যাদা লাভ করবে, তারা কি ‘আমল করত সে বিষয়ে তোমার রাব্ব উদাসীন নন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (922,6,133,'তোমার রাব্ব অমুখাপেক্ষী ও দয়াশীল, তাঁর ইচ্ছা হলে তোমাদেরকে অপসারিত করবেন এবং তোমাদের পরে তোমাদের স্থানে যাকে ইচ্ছা স্থলাভিষিক্ত করবেন, যেমন তিনি তোমাদেরকে অন্য এক জাতির বংশধর হতে সৃষ্টি করেছেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (923,6,134,'তোমাদের নিকট যে বিষয় সম্পর্কে প্রতিশ্রুতি দেয়া হয়েছে তা অবশ্যম্ভাবী, তোমরা আল্লাহকে অক্ষম ও দুর্বল করতে পারবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (924,6,135,'তুমি বলে দাওঃ হে আমার সম্প্রদায়! তোমরা নিজ নিজ অবস্থায় ‘আমল করতে থাক, আমিও ‘আমল করছি, অতঃপর শীঘ্রই তোমরা জানতে পারবে যে, কার পরিণাম কল্যাণকর। নিঃসন্দেহে অত্যাচারীরা কখনও মুক্তি ও সাফল্য লাভ করতে পারবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (925,6,136,'আর আল্লাহ যে সব শস্য ও পশু সৃষ্টি করেছেন, তারা (মুশরিকরা) ওর একটি অংশ আল্লাহর জন্য নির্ধারণ করে থাকে। অতঃপর নিজেদের ধারণা মতে তারা বলে যে, এই অংশ আল্লাহর জন্য এবং এই অংশ আমাদের শরীকদের জন্য। কিন্তু যা তাদের শরীকদের জন্য নির্ধারিত হয়ে থাকে, তাতো আল্লাহর দিকে পৌঁছেনা, পক্ষান্তরে যা আল্লাহর জন্য নির্ধারণ করা হয়েছিল তা তাদের শরীকদের কাছে পৌঁছে থাকে। এই লোকদের ফাইসালা ও বন্টন নীতি কতই না নিকৃষ্ট!');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (926,6,137,'আর এমনিভাবে অনেক মুশরিকের দৃষ্টিতে তাদের উপাস্যরা তাদের সন্তান হত্যা করাকে শোভণীয় করে দিয়েছে, যেন তারা তাদের সর্বনাশ করতে পারে এবং তাদের কাছে তাদের ধর্মকে বিভ্রান্ত করে দিতে পারে। আল্লাহ চাইলে তারা এসব কাজ করতে পারতনা। সুতরাং তুমি তাদেরকে এবং তাদের ভ্রান্ত উক্তিগুলিকে ছেড়ে দাও।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (927,6,138,'আর তারা বলে থাকেঃ এই সব নির্দিষ্ট পশু ও ক্ষেতের ফসল সুরক্ষিত, কেহই তা আহার করতে পারবেনা, তবে যাদেরকে আমরা অনুমতি দিব (তারাই আহার করতে পারবে), আর (তারা বলে) এই বিশেষ পশুগুলির উপর আরোহণ করা ও ভার বহন নিষেধ করে দেয়া হয়েছে। আর কতকগুলি বিশেষ পশু রয়েছে যেগুলিকে যবাহ করার সময় তারা আল্লাহর নাম উচ্চারণ করেনা, শুধু আল্লাহর প্রতি মিথ্যা আরোপ করার উদ্দেশে। আল্লাহ এসব মিথ্যা আরোপের প্রতিফল অতি সত্ত্বরই দান করবেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (928,6,139,'আর তারা এ কথাও বলে থাকে যে, এই সব বিশেষ পশুগুলির গর্ভে যা কিছু রয়েছে তা বিশেষভাবে তাদের পুরুষদের জন্য রক্ষিত, আর তাদের নারীদের জন্য ওটা হারাম; কিন্তু গর্ভ হতে প্রসূত বাচ্চা যদি মৃত হয় তাহলে নারী-পুরুষ সবাই তা ভক্ষণে অংশী হতে পারবে। তাদের কৃত এই সব বর্ণনার প্রতিদান অতি সত্ত্বরই আল্লাহ তাদেরকে দিবেন, নিঃসন্দেহে তিনি হচ্ছেন প্রজ্ঞাময়, সর্ববিদিত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (929,6,140,'যারা নিজেদের সন্তানদেরকে মূর্খতা ও অজ্ঞানতার কারণে হত্যা করেছে আর আল্লাহ সম্পর্কে মিথ্যা রচনা করে তাঁর প্রদত্ত রিয্ককে হারাম করে নিয়েছে, তারা নিশ্চিত রূপে পথভ্রষ্ট হয়েছে; বস্তুতঃ তারা হিদায়াত গ্রহণ করার পাত্রও ছিলনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (930,6,141,'আর সেই আল্লাহই নানা প্রকার বাগান ও গুল্মলতা সৃষ্টি করেছেন যার কতক স্বীয় কান্ডের উপর সন্নিবিষ্ট হয়, আর কতক কান্ডের উপর সন্নিবিষ্ট হয়না; আর খেজুর বৃক্ষ ও শস্যক্ষেত্র যাতে বিভিন্ন প্রকারের খাদ্য উৎপন্ন হয়ে থাকে, আর তিনি যাইতূন (জলপাই) ও আনারের (ডালিমের) বৃক্ষও সৃষ্টি করেছেন যা দৃশ্যতঃ অভিন্ন হলেও স্বাদে বিভিন্ন, এই সব ফল তোমরা আহার কর যখন ওতে ফল ধরে, আর তা হতে শারীয়াতের নির্ধারিত যে অংশ রয়েছে তা ফসল কাটার দিন আদায় করে নাও, অপব্যয় করে সীমা লংঘন করনা। নিশ্চয়ই তিনি (আল্লাহ) অপব্যয়কারী ও সীমালংঘনকারীকে ভালবাসেন না।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (931,6,142,'আর চতুস্পদ জন্তুগুলির মধ্যে কতকগুলি (উঁচু আকৃতির) ভারবাহী রয়েছে; আর কতকগুলি রয়েছে ছোট আকৃতির, গোশত খাওয়ার ও চামড়া দ্বারা বিছানা বানানোর যোগ্য। আল্লাহ যা কিছু দান করেছেন তোমরা তা আহার কর, আর শাইতানের পদাঙ্ক অনুসরণ করনা, নিঃসন্দেহে সে তোমাদের প্রকাশ্য শত্রু।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (932,6,143,'এই পশুগুলি আট প্রকার রয়েছে, ভেড়ার এক জোড়া স্ত্রী-পুরুষ এবং বকরীর এক জোড়া স্ত্রী-পুরুষ। তুমি জিজ্ঞেস করঃ আল্লাহ কি উভয় পুরুষ পশুগুলিকে হারাম করেছেন, নাকি উভয় স্ত্রী পশুগুলিকে, অথবা স্ত্রী দু‘টির গর্ভে যা আছে তা হারাম করেছেন? তোমরা জ্ঞানের সাথে আমাকে উত্তর দাও যদি তোমরা সত্যবাদী হয়ে থাক।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (933,6,144,'আর উটের স্ত্রী-পুরুষ দু‘টি এবং গরুর স্ত্রী-পুরুষ দু‘টি পশু, তুমি তাদেরকে জিজ্ঞেস করঃ আল্লাহ কি এ দু‘টি পুরুষ পশুকে বা এ দু‘টি স্ত্রী পশুকে হারাম করেছেন, অথবা উভয় স্ত্রী গরু ও উটের গর্ভে যা রয়েছে তা হারাম করেছেন? আল্লাহ যখন এসব পশু হালাল-হারাম হওয়ার বিধান জারি করেন তখন কি তোমরা হাযির ছিলে? যে ব্যক্তি বিনা প্রমাণে না জেনে (অজ্ঞতাবশতঃ) মানুষকে বিভ্রান্ত করার উদ্দেশে আল্লাহর নামে এরূপ মিথ্যা আরোপ করে তার চেয়ে বড় যালিম আর কে হতে পারে? আল্লাহ যালিমদেরকে সুপথ প্রদর্শন করেননা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (934,6,145,'তুমি বলঃ অহীর মাধ্যমে আমার কাছে যে বিধান পাঠানো হয়েছে তাতে কোন আহারকারীর জন্য কোন বস্তু হারাম করা হয়েছে এমন কিছু আমি পাইনি; তবে মৃত জন্তু, প্রবাহিত রক্ত, শুকরের মাংস এবং যা আল্লাহর নামে যবাহ করা হয়নি তা হারাম করা হয়েছে। কেননা ওটা নাপাক ও শারীয়াত বিগর্হিত বস্তু। কিন্তু যদি কোন লোক স্বাদ আস্বাদন ও সীমা লংঘনের উদ্দেশ্য ব্যতীত নিরূপায় হয়ে পড়ে (তার পক্ষে ওটাও খাওয়া বৈধ)। কেননা আল্লাহ ক্ষমাশীল ও অনুগ্রহশীল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (935,6,146,'ইয়াহুদীদের প্রতি আমি সর্ব প্রকার অবিভক্ত নখ বিশিষ্ট জীব হারাম করেছিলাম। আর গরু ও ভেড়া হতে উৎপন্ন উভয়ের চর্বি তাদের জন্য আমি হারাম করেছিলাম, কিন্তু পৃষ্ঠদেশের চর্বি, নাড়ি ভুড়ির চর্বি ও হাড়ের সাথে মিশ্রিত চর্বি এই হারামের অন্তর্ভুক্ত ছিলনা। তাদের বিদ্রোহমূলক আচরণের জন্য আমি তাদেরকে এই শাস্তি দিয়েছিলাম, আর আমি নিঃসন্দেহে সত্যবাদী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (936,6,147,'সুতরাং এ সব বিষয়ে যদি তারা তোমাকে মিথ্যাবাদী মনে করে তাহলে তুমি বলে দাওঃ তোমাদের রাব্ব খুবই করুণাময়, আর অপরাধী সম্প্রদায় হতে তাঁর শাস্তির বিধান কখনই প্রত্যাহার করা হবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (937,6,148,'মুশরিকরা (তোমার কথার উত্তরে) অবশ্যই বলবেঃ আল্লাহ যদি চাইতেন তাহলে আমরা শির্‌ক করতামনা, আর না আমাদের বাপ-দাদারা করত, আর কোনো জিনিসও আমরা হারাম করতামনা। বস্তুতঃ এভাবেই তাদের পূর্ব যুগের কাফিরেরা(রাসূলদেরকে) মিথ্যা প্রতিপন্ন করেছিল। শেষ পর্যন্ত তারা আমার শাস্তির স্বাদ গ্রহণ করতে বাধ্য হয়েছিল। তুমি জিজ্ঞেস করঃ তোমাদের কাছে কি কোন দলীল প্রমাণ আছে? থাকলে আমার সামনে পেশ কর। তোমরা ধারণা ও অনুমান ব্যতীত আর কিছুরই অনুসরণ করনা, তোমরা সম্পূর্ণ আনুমানিক কথা ছাড়া আর কিছুই বলছনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (938,6,149,'তুমি বলে দাওঃ সত্য ভিত্তিক পূর্ণাঙ্গ দলীল প্রমাণতো একমাত্র আল্লাহরই রয়েছে, তিনি চাইলে তোমাদের সবাইকে হিদায়াত দান করতেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (939,6,150,'তুমি আরও বলঃ আল্লাহ এসব পশু হারাম করেছেন, এর সাক্ষ্য যারা দিবে সেই সাক্ষীদেরকে তোমরা নিয়ে এসো। তারা যদি সাক্ষ্য দেয় তাহলে তুমি তাদের সাথে সাক্ষ্য দিবেনা, তুমি এমন লোকদের খেয়াল খুশির (বাতিল ধ্যান ধারণার) অনুসরণ করনা যারা আমার আয়াতসমূহকে মিথ্যা প্রতিপন্ন করে, পরকালের প্রতি ঈমান আনেনা এবং তারা অন্যান্যদেরকে নিজেদের রবের সমমর্যাদা দান করে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (940,6,151,'লোকদেরকে বলঃ তোমরা এসো! তোমাদের রাব্ব তোমাদের প্রতি কি কি বিধি-নিষেধ আরোপ করেছেন তা আমি তোমাদেরকে পাঠ করে শোনাই; তা এই যে, তোমরা তাঁর সাথে কেহকেই শরীক করবেনা, মাতা-পিতার সাথে সদ্ব্যবহার করবে, দারিদ্রতার ভয়ে নিজেদের সন্তানদেরকে হত্যা করবেনা। কেননা আমিই তোমাদেরকে ও তাদেরকে জীবিকা দিই; আর অশ্লীল কাজ ও কথার নিকটেও যেওনা, তা প্রকাশ্যই হোক কিংবা গোপনীয়ই হোক, আর আল্লাহ যার হত্যা নিষিদ্ধ করেছেন - যথার্থ কারণ ছাড়া তাকে হত্যা করনা। এসব বিষয় আল্লাহ তোমাদেরকে নির্দেশ দিয়েছেন, যেন তোমরা অনুধাবন করতে পার।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (941,6,152,'আর ইয়াতীমরা বয়ঃপ্রাপ্ত না হওয়া পর্যন্ত সদুদ্দেশ্য ব্যতীত তাদের বিষয় সম্পত্তির কাছেও যেওনা, আর আদান-প্রদান, পরিমান-ওজন সঠিকভাবে করবে, আমি কারও উপর তার সাধ্যাতীত ভার (দায়িত্ব/কর্তব্য) অর্পন করিনা, আর তোমরা যখন কথা বলবে তখন স্বজনের বিরুদ্ধে হলেও ন্যায়ানুগ কথা বলবে, আর আল্লাহর সাথে কৃত অঙ্গীকার পূরণ করবে। আল্লাহ তোমাদেরকে এসব বিষয় নির্দেশ দিয়েছেন, যেন তোমরা তাঁর এ নির্দেশ ও উপদেশ গ্রহণ কর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (942,6,153,'আর নিশ্চয়ই এই পথই আমার সরল পথ; এই পথই তোমরা অনুসরণ করে চলবে, এই পথ ছাড়া অন্য কোন পথের অনুসরণ করবেনা, তাহলে তোমাদেরকে তাঁর পথ থেকে বিচ্ছিন্ন করে দূরে সরিয়ে নিবে। আল্লাহ তোমাদেরকে এই নির্দেশ দিচ্ছেন, যেন তোমরা সতর্ক হও।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (943,6,154,'অতঃপর মূসাকে আমি এমন কিতাব প্রদান করেছিলাম, যা ছিল সৎ কর্মপরায়ণদের জন্য পূর্ণাঙ্গ কিতাব। আর তাতে ছিল প্রত্যেকটি বস্তুর বিশদ বিবরণ, পথ নির্দেশ সম্বলিত আল্লাহর রাহমাতের প্রতীক স্বরূপ, যাতে তারা তাদের রবের সাথে সাক্ষাৎ হওয়া সম্বন্ধে পূর্ণ বিশ্বাস লাভ করতে পারে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (944,6,155,'আর আমি এই কিতাব অবতীর্ণ করেছি যা বারাকাতময় ও কল্যাণময়। সুতরাং তোমরা এর অনুসরণ কর এবং এর বিরোধিতা হতে বেঁচে থাক, হয়ত তোমাদের প্রতি দয়া ও অনুগ্রহ প্রদর্শন করা হবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (945,6,156,'যেন তোমরা না বলতে পার : ঐ কিতাবতো আমাদের পূর্ববর্তী দুই সম্প্রদায়ের প্রতি অবতীর্ণ করা হয়েছিল এবং আমরা তাদের পঠন পাঠনে সম্পূর্ণ অজ্ঞ ছিলাম। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (946,6,157,'অথবা তোমরা যেন এ কথাও বলতে না পার, আমাদের প্রতি কিতাব নাযিল করা হলে আমরা তাদের তুলনায় বেশি হিদায়াত লাভ করতাম। এখন তোমাদের কাছে তোমাদের রবের পক্ষ থেকে এক সুস্পষ্ট দলীল এবং পথ নির্দেশ ও রাহমাত সমাগত হয়েছে। এরপর আল্লাহর আয়াতকে যে মিথ্যা প্রতিপন্ন করবে এবং তা থেকে এড়িয়ে চলবে তার চেয়ে বড় যালিম আর কে হতে পারে? যারা আমার আয়াতসমূহ এড়িয়ে চলে, অতি সত্ত্বর তাদেরকে আমি কঠিন শাস্তি দিব, জঘণ্য শাস্তি-তাদের এড়িয়ে চলার জন্য!');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (947,6,158,'তারা কি শুধু এ প্রতীক্ষায় রয়েছে যে, তাদের কাছে মালাইকা/ফেরেশতা আসবে? কিংবা স্বয়ং তোমার রাব্ব আসবেন? অথবা তোমার রবের কোন কোন নিদর্শন প্রকাশ হয়ে পড়বে? যেদিন তোমার রবের কতক নিদর্শন প্রকাশ হয়ে পড়বে সেই দিনের পূর্বে যারা ঈমান আনেনি অথবা যারা নিজেদের ঈমান দ্বারা কোন নেক কাজ করেনি তখন তাদের ঈমান আনয়নে কোন উপকার হবেনা, তুমি এসব পাপিষ্ঠকে জানিয়ে দাওঃ তোমরা (এরূপ আশা নিয়ে) প্রতীক্ষা করতে থাক, আমিও প্রতীক্ষা করছি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (948,6,159,'নিশ্চয়ই যারা নিজেদের দীনের মধ্যে নানা মতবাদ সৃষ্টি করে ওকে খন্ড বিখন্ড করেছে এবং বিভিন্ন দলে উপদলে বিভক্ত হয়ে পড়েছে, তাদের সাথে কোন ব্যাপারে তোমার কোন দায়িত্ব নেই, তাদের বিষয়টি নিশ্চয়ই আল্লাহর নিকট সমর্পিত, পরিশেষে তিনিই তাদেরকে তাদের কার্যকলাপ সম্পর্কে অবহিত করবেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (949,6,160,'কেহ কোন ভাল কাজ করলে সে তার দশ গুণ প্রতিদান পাবে, আর কেহ পাপ ও অসৎ কাজ করলে তাকে শুধু ততটুকুই প্রতিফল দেয়া হবে যতটুকু সে করেছে, আর তারা অত্যাচারিত হবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (950,6,161,'তুমি বলঃ নিঃসন্দেহে আমার রাব্ব আমাকে সঠিক ও নির্ভুল পথে পরিচালিত করেছেন। ওটাই সুপ্রতিষ্ঠিত দীন এবং ইবরাহীমের অবলম্বিত আদর্শ যা সে ঐকান্তিক নিষ্ঠার সাথে গ্রহণ করেছিল। আর সে মুশরিকদের অন্তর্ভুক্ত ছিলনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (951,6,162,'তুমি বলে দাওঃ আমার সালাত, আমার কুরবানী, আমার জীবন ও আমার মরণ সব কিছু সারা জাহানের রাব্ব আল্লাহর জন্য।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (952,6,163,'তাঁর কোন শরীক নেই, আমি এর জন্য আদিষ্ট হয়েছি, আর মুসলিমদের মধ্যে আমিই হলাম প্রথম।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (953,6,164,'তুমি জিজ্ঞেস করঃ আমি কি আল্লাহকে বাদ দিয়ে অন্য রবের সন্ধান করব? অথচ তিনিই হচ্ছেন প্রতিটি বস্তুর রাব্ব! প্রত্যেক ব্যক্তিই স্বীয় কৃতকর্মের জন্য দায়ী হবে, কেহ কারও কোন বোঝা বহন করবেনা, পরিশেষে তোমাদের রবের নিকট তোমাদের প্রত্যাবর্তন করতে হবে, অতঃপর তিনি তোমরা যে বিষয়ে মতবিরোধ করেছিলে সে বিষয়ের মূল তত্ত্ব তোমাদেরকে অবহিত করবেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (954,6,165,'আর তিনি এমন, যিনি তোমাদেরকে দুনিয়ার প্রতিনিধি করেছেন এবং তোমাদের কতককে কতকের উপর মর্যাদায় উন্নীত করেছেন, উদ্দেশ্য হল তোমাদেরকে তিনি যা কিছু দিয়েছেন তাতে তোমাদেরকে পরীক্ষা করা। নিঃসন্দেহে তোমার রাব্ব ত্বরিত শাস্তিদাতা, আর নিঃসন্দেহে তিনি ক্ষমাশীল ও কৃপানিধান।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (955,7,1,'আলিফ লাম-মিম-সাদ। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (956,7,2,'এ একটি কিতাব যা তোমার উপর অবতীর্ণ করা হয়েছে, সুতরাং তোমার অন্তরে যেন মোটেই সংকীর্ণতা না আসে। আর মু’মিনদের জন্য এটা উপদেশ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (957,7,3,'তোমার রবের পক্ষ থেকে যা তোমার প্রতি নাযিল করা হয়েছে তুমি তা অনুসরণ কর এবং আল্লাহকে বাদ দিয়ে অন্যদেরকে অভিভাবক অথবা সাহায্যকারী হিসাবে গ্রহণ করনা। তোমরা খুব অল্পই উপদেশ গ্রহণ করে থাকো।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (958,7,4,'কত জনপদকেই না আমি ধ্বংস করেছি! আমার শাস্তি তাদের উপর রাতে ঘুমন্ত অবস্থায় অথবা দ্বিপ্রহরে যখন তারা বিশ্রামরত ছিল তখনই আপতিত হয়েছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (959,7,5,'আমার শাস্তি যখন তাদের কাছে এসে পড়েছিল তখন তাদের মুখে ‘‘বাস্তবিকই আমরা অত্যাচারী ছিলাম’’ এ কথা ছাড়া আর কিছুই ছিলনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (960,7,6,'অতঃপর আমি (কিয়ামাত দিবসে) যাদের কাছে রাসূল প্রেরণ করা হয়েছিল তাদেরকে এবং রাসূলদেরকেও অবশ্যই জিজ্ঞাসাবাদ করব।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (961,7,7,'তখন আমি তাদের সমস্ত বিবরণ অকপটে প্রকাশ করে দিব, যেহেতু আমি পূর্ণরূপে জ্ঞাত আছি, আর আমিতো কোন কালে বেখবর ছিলামনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (962,7,8,'আর সেদিন (কিয়ামাতের দিন) ন্যায় ও সঠিকভাবে (প্রত্যেকের ‘আমল) ওযন করা হবে, সুতরাং যাদের (পুণ্যের) পাল্লা ভারী হবে তারাই হবে কৃতকার্য ও সফলকাম।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (963,7,9,'আর যাদের পাল্লা হালকা হবে, তারা হবে সেই সব লোক যারা নিজেদের ধ্বংস ও ক্ষতি নিজেরাই করেছে। কেননা তারা আমার নিদর্শনসমূহকে (আয়াত) প্রত্যাখ্যান করত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (964,7,10,'আর নিশ্চয়ই আমি তোমাদেরকে ভূ-পৃষ্ঠে প্রতিষ্ঠিত করেছি এবং আমি তোমাদের জন্য ওতে জীবিকা নির্বাহের উপকরণসমূহ সৃষ্টি করেছি, তোমরা খুব কমই কৃতজ্ঞতা প্রকাশ করে থাক।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (965,7,11,'আমিই তোমাদেরকে সৃষ্টি করেছি, অতঃপর তোমাদেরকে রূপ দান করেছি, তারপর আমি মালাইকাদেরকে/ফেরেশতাদেরকে নির্দেশ দিয়েছি - তোমরা আদমকে সাজদাহ কর। তখন ইবলীস ছাড়া সবাই সাজদাহ করল, যারা সাজদাহ করল সে তাদের অন্তর্ভুক্ত হলনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (966,7,12,'তিনি (আল্লাহ) তাকে (ইবলীসকে) জিজ্ঞেস করলেনঃ আমি যখন তোকে সাজদাহ (আদমকে) করতে আদেশ করলাম তখন কোন বস্তু তোকে নত শির হতে নিবৃত্ত করল? সে উত্তরে বললঃ আমি তার চেয়ে শ্রেষ্ঠ, আপনি আমাকে আগুন দ্বারা সৃষ্টি করেছেন, আর তাকে সৃষ্টি করেছেন কাদামাটি দ্বারা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (967,7,13,'আল্লাহ বললেনঃ এই স্থান থেকে নেমে যা, এখানে থেকে অহংকার করা যেতে পারেনা; সুতরাং বের হয়ে যা, নিশ্চয়ই তুই ইতরদের অন্তর্ভুক্ত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (968,7,14,'সে বললঃ আমাকে পুনরুত্থান দিন পর্যন্ত অবকাশ দিন!');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (969,7,15,'আল্লাহ বললেনঃ তোকে অবকাশ দেয়া হল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (970,7,16,'(ইবলীস) বললঃ আপনি যে আমাকে পথভ্রষ্ট করলেন এ কারণে আমিও শপথ করে বলছি - আমি আপনার সরল পথে অবশ্যই ওৎ পেতে বসে থাকব।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (971,7,17,'অতঃপর আমি তাদের সম্মুখ দিয়ে, পিছন দিয়ে, ডান দিক দিয়ে এবং বাম দিক দিয়ে তাদের কাছে আসব, আপনি তাদের অধিকাংশকেই কৃতজ্ঞ পাবেননা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (972,7,18,'তিনি (আল্লাহ) বললেনঃ তুই এখান থেকে অপমানিত ও লাঞ্ছিত অবস্থায় বের হয়ে যা, তাদের (বানী আদমের) মধ্যে যারা তোর অনুসরণ করবে, নিশ্চয়ই আমি তোদের সকলের দ্বারা জাহান্নাম পূর্ণ করব।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (973,7,19,'আর হে আদম! তুমি এবং তোমার স্ত্রী জান্নাতে বসবাস কর এবং এখানে তোমাদের মনে যা চায় তাই খাও, কিন্তু এই বৃক্ষের নিকটবর্তী হয়োনা, তাহলে অত্যাচারীদের মধ্যে গণ্য হবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (974,7,20,'অতঃপর তাদের লজ্জাস্থান যা পরস্পরের কাছে গোপন রাখা হয়েছিল তা প্রকাশ করার জন্য শাইতান তাদেরকে কুমন্ত্রণা দিল, সে বললঃ তোমাদের রাব্ব এই বৃক্ষের কাছে যেতে নিষেধ করেছেন, এর কারণ এ ছাড়া কিছুই নয় যে, তোমরা যেন মালাইকা/ফেরেশতা হয়ে না যাও, অথবা এখানে (এই জান্নাতে) চিরন্তন জীবন লাভ করতে না পার।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (975,7,21,'সে তাদের উভয়ের নিকট শপথ করে বললঃ আমি তোমাদের হিতাকাংখীদের অন্যতম।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (976,7,22,'অতঃপর সে (শাইতান) তাদের উভয়কে বিভ্রান্ত করল। যখন তারা সেই নিষিদ্ধ গাছের ফলের স্বাদ গ্রহণ করল তখন তাদের লজ্জাস্থান তাদের কাছে প্রকাশ হয়ে পড়ল এবং তারা বাগানের বৃক্ষপত্র দ্বারা নিজেদেরকে আবৃত করতে লাগল। তাদের রাব্ব তাদেরকে সম্বোধন করে বললেনঃ আমি কি এই বৃক্ষ সম্পর্কে তোমাদেরকে নিষেধ করিনি এবং বলিনি যে, শাইতান তোমাদের প্রকাশ্য শক্র?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (977,7,23,'তারা বললঃ হে আমাদের রাব্ব! আমরা নিজেদের প্রতি অন্যায় করেছি, আপনি যদি আমাদেরকে ক্ষমা না করেন তাহলে আমরা ক্ষতিগ্রস্তদের অন্তর্ভুক্ত হয়ে পড়ব।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (978,7,24,'তিনি (আল্লাহ) বললেনঃ তোমরা একে অন্যের শক্র রূপে এখান থেকে নেমে যাও, তোমাদের জন্য পৃথিবীতে বাসস্থান রয়েছে, একটি নির্দিষ্ট মেয়াদ পর্যন্ত সেখানে জীবন ধারণের উপযোগী সামগ্রীর ব্যবস্থা রাখা হয়েছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (979,7,25,'তিনি বললেনঃ সেই পৃথিবীতেই তোমরা জীবন যাপন করবে, সেখানেই তোমাদের মৃত্যু সংঘটিত হবে এবং সেখান হতেই তোমাদেরকে পুনরুত্থিত করা হবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (980,7,26,'হে বানী আদম! আমি তোমাদের লজ্জাস্থান আবৃত করার ও বেশভূষার জন্য তোমাদের পোশাক পরিচ্ছদের উপকরণ অবতীর্ণ করেছি। (বেশ-ভূষার তুলনায়) আল্লাহভীতির পরিচ্ছদই হচ্ছে সর্বোত্তম পরিচ্ছদ। এটা আল্লাহর নিদর্শনসমূহের অন্যতম নিদর্শন, সম্ভবতঃ মানুষ এটা হতে উপদেশ গ্রহণ করবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (981,7,27,'হে আদম সন্তান! শাইতান যেন তোমাদেরকে সেরূপ প্রলুব্ধ করতে না পারে যেরূপ তোমাদের মাতা-পিতাকে (প্রলুব্ধ করে) জান্নাত হতে বহিস্কার করেছিল এবং তাদেরকে তাদের লজ্জাস্থান দেখানোর জন্য বিবস্ত্র করেছিল। সে (শাইতান) নিজে এবং তার দল তোমাদেরকে দেখতে পায়, অথচ তোমরা তাদেরকে দেখতে পাওনা। নিঃসন্দেহে আমি অবিশ্বাসীদের জন্য শাইতানকে বন্ধু ও অভিভাবক বানিয়ে দিয়েছি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (982,7,28,'যখন তারা কোন লজ্জাস্কর ও অশ্লীল আচরণ করে তখন তারা বলেঃ আমরা আমাদের পূর্ব-পুরুষদেরকে এসব কাজ করতে দেখেছি এবং আল্লাহও আমাদেরকে এটা করতে নির্দেশ দিয়েছেন। তুমি বলঃ না আল্লাহ কখনও অশ্লীল ও লজ্জাস্কর আচরণের নির্দেশ দেননা, তোমরা কি আল্লাহ সম্পর্কে এমন সব কথা বলছ যে বিষয়ে তোমাদের কোন জ্ঞান নেই?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (983,7,29,'তুমি বলঃ আমার রাব্ব ন্যায় বিচারের আদেশ দিয়েছেন এবং তোমরা প্রত্যেক সালাতে তোমাদের মনযোগ স্থির রেখ এবং তাঁর আনুগত্যে বিশুদ্ধ মনে একনিষ্ঠভাবে তাঁকেই ডাক; তোমাদেরকে প্রথম যেভাবে সৃষ্টি করা হয়েছে, তোমরা তেমনিভাবে ফিরে আসবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (984,7,30,'আল্লাহ এক দলকে সৎ পথে পরিচালিত করেছেন এবং অপর দলের জন্য সংগত কারণেই ভ্রান্তি নির্ধারিত হয়েছে,তারা আল্লাহকে ছেড়ে শাইতানকে অভিভাবক ও বন্ধু বানিয়েছিল এবং নিজেদেরকে সৎ পথগামী মনে করত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (985,7,31,'হে আদম সন্তান! প্রত্যেক সালাতের সময় সুন্দর পোশাক পরিচ্ছদ গ্রহণ কর, আর খাও এবং পান কর। তবে অপব্যয় ও অমিতাচার করবেনা, নিশ্চয়ই আল্লাহ অপব্যয়কারীদের ভালবাসেননা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (986,7,32,'তুমি জিজ্ঞেস করঃ আল্লাহ তাঁর বান্দাদের জন্য যে সব শোভনীয় বস্তু ও পবিত্র জীবিকা সৃষ্টি করেছেন তা কে নিষিদ্ধ করেছে? তুমি ঘোষণা করে দাও - এই সমস্ততো তাদের জন্যই যারা পার্থিব জীবনে এবং বিশেষ করে কিয়ামাত দিবসে বিশ্বাস করে। এমনিভাবে আমি জ্ঞানী সম্প্রদায়ের জন্য নিদর্শনসমূহ বিশদভাবে বিবৃত করে থাকি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (987,7,33,'তুমি বলঃ আমার রাব্ব প্রকাশ্য, অপ্রকাশ্য অশ্লীলতা, পাপ কাজ, অন্যায় ও অসংগত বিদ্রোহ ও বিরোধিতা এবং আল্লাহর সাথে কোন কিছুকে শরীক করা যার পক্ষে আল্লাহ কোন দলীল প্রমাণ অবতীর্ণ করেননি, আর আল্লাহ সম্বন্ধে এমন কিছু বলা যে সম্বন্ধে তোমাদের কোন জ্ঞান নেই, (ইত্যাদি কাজ ও বিষয়সমূহ) নিষিদ্ধ করেছেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (988,7,34,'প্রত্যেক জাতির জন্য একটি নির্দিষ্ট সময় রয়েছে, সুতরাং যখন সেই নির্দিষ্ট সময় সমুপস্থিত হবে তখন তা এক মুহুর্তকালও আগে কিংবা পরে হবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (989,7,35,'হে আদম সন্তান! তোমাদের মধ্য হতে যদি কোন রাসূল তোমাদের নিকট আগমন করে এবং আমার বাণী ও নিদর্শন তোমাদের কাছে বিবৃত করে; তখন যারা সতর্ক হবে এবং নিজেদেরকে সংশোধন করে নিবে এবং সৎ কাজ করবে, তাদের কোন ভয়-ভীতি থাকবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (990,7,36,'আর যারা আমার নিদর্শন ও বিধানকে মিথ্যা প্রতিপন্ন করে এবং অহংকার করে ওটা হতে দূরে সরে থাকে তারাই হবে জাহান্নামী, সেখানে তারা চিরকাল অবস্থান করবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (991,7,37,'যে ব্যক্তি আল্লাহর উপর মিথ্যা আরোপ করে এবং তাঁর নিদর্শনসমূহকে মিথ্যা প্রতিপন্ন করে সে অপেক্ষা বড় যালিম আর কে হতে পারে? তাদের ‘আমলনামায় লিখিত নির্ধারিত অংশ তাদের নিকট পৌঁছবেই, পরিশেষে যখন আমার প্রেরিত মালাক/ফেরেশতা তাদের প্রাণ হরণের জন্য তাদের নিকট পৌঁছবে, তখন তারা (ফেরেশতারা) জিজ্ঞেস করবেঃ আল্লাহকে বাদ দিয়ে যাদেরকে তোমরা ডাকতে তারা কোথায়? তখন তারা উত্তরে বলবেঃ আমাদের হতে তারা উধাও হয়ে গেছে। আর নিজেরাই স্বীকারোক্তি করবে যে, তারা কাফির বা সত্য প্রত্যাখ্যানকারী ছিল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (992,7,38,'আল্লাহ বলেনঃ তোমাদের পূর্বে মানব ও জিন হতে যে সব সম্প্রদায় গত হয়েছে, তাদের সাথে তোমরাও জাহান্নামে প্রবেশ কর। যখন কোন দল তাতে প্রবেশ করবে তখনই অপর দলকে তারা অভিসস্পাত করবে, পরিশেষে যখন তাতে সকলে জমায়েত হবে তখন পরবর্তীরা পূর্ববর্তীদের সম্পর্কে বলবেঃ হে আমাদের রাব্ব! এরাই আমাদেরকে বিভ্রান্ত করেছে, সুতরাং আপনি এদের দ্বিগুণ শাস্তি দিন! তখন আল্লাহ বলবেনঃ তাদের প্রত্যেকের জন্যই রয়েছে দ্বিগুণ শাস্তি, কিন্তু তোমরা জাননা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (993,7,39,'অতঃপর পূর্ববর্তী লোকেরা পরবর্তী লোকদেরকে বলবেঃ আমাদের উপর তোমাদের কোন শ্রেষ্ঠত্ব নেই, তোমরা তোমাদের কৃতকর্মের ফল স্বরূপ শাস্তি ভোগ করতে থাক।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (994,7,40,'নিশ্চয়ই যারা আমার আয়াতকে মিথ্যা প্রতিপন্ন করে এবং অহংকার বশতঃ তা থেকে ফিরে থাকে, তাদের জন্য আকাশের দ্বার উন্মুক্ত করা হবেনা এবং তারা জান্নাতেও প্রবেশ করবেনা, যতক্ষণ না সুচের ছিদ্র পথে উট প্রবেশ করে, এমনিভাবেই আমি অপরাধীদেরকে প্রতিফল দিয়ে থাকি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (995,7,41,'তাদের জন্য হবে জাহান্নামের (আগুনের) শয্যা এবং তাদের উপরের আচ্ছাদনও হবে (আগুনের তৈরী) চাদর, এমনিভাবেই আমি যালিমদেরকে প্রতিফল দিয়ে থাকি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (996,7,42,'যারা ঈমান এনেছে ও ভাল কাজ করেছে এমন কোন ব্যক্তিকে আমি তার সাধ্যাতীত দায়িত্ব অর্পণ করিনা - তারাই হবে জান্নাতবাসী, সেখানে তারা চিরকাল অবস্থান করবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (997,7,43,'আর তাদের অন্তরে যা কিছু ঈর্ষা ও বিদ্বেষ রয়েছে তা আমি দূর করে দিব, তাদের নিম্নদেশ দিয়ে নদী প্রবাহিত হবে; তখন তারা বলবেঃ সমস্ত প্রশংসা সেই আল্লাহর জন্য যিনি আমাদেরকে পথ প্রদর্শন করেছেন, আল্লাহ আমাদেরকে পথ প্রদর্শন না করলে আমরা পথ পেতামনা, আমাদের রবের প্রেরিত রাসূলগণ সত্য বাণী নিয়ে এসেছিলেন। আর তাদেরকে সম্বোধন করে বলা হবেঃ তোমরা যে (ভাল) ‘আমল করতে তারই জন্য তোমাদেরকে এই জান্নাতের উত্তরাধিকারী বানানো হয়েছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (998,7,44,'আর জান্নাতের অধিবাসীগণ আগুনের অধিবাসীদেরকে ডাকবে যে, ‘আমাদের রব আমাদেরকে যে ওয়াদা দিয়েছেন তা আমরা সত্য পেয়েছি। সুতরাং তোমাদের রব তোমাদেরকে যে ওয়াদা দিয়েছেন, তা কি তোমরা সত্যই পেয়েছ’? তারা বলবে, ‘হ্যাঁ’। অতঃপর এক ঘোষক তাদের মধ্যে ঘোষণা দেবে যে, আল্লাহর লা’নত যালিমদের উপর’।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (999,7,45,'যারা আল্লাহর পথে চলতে (মানুষকে) বাধা দিত এবং তাতে বক্রতা অনুসন্ধান করত তারা পরকালকেও অস্বীকার করত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1000,7,46,'এই উভয় শ্রেণীর লোকদের মাঝে পার্থক্যকারী একটি পর্দা রয়েছে। এবং আ‘রাফে (জান্নাত ও জাহান্নামের ঊর্ধ্বস্থানে) কিছু লোক থাকবে, তারা প্রত্যেককে লক্ষণ ও চিহ্ন দ্বারা চিনতে পারবে। তারা জান্নাতবাসীকে ডেকে বলবেঃ তোমাদের প্রতি শান্তি বর্ষিত হোক; তখনো তারা জান্নাতে প্রবেশ করেনি বটে, কিন্তু তারা প্রবেশ করার আকাংখা করবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1001,7,47,'পরন্ত জাহান্নামীদের প্রতি তাদের দৃষ্টি ফিরিয়ে দেয়া হবে, তখন তারা (আ‘রাফবাসীরা) বলবেঃ হে আমাদের রাব্ব! আপনি আমাদেরকে যালিম সম্প্রদায়ের সঙ্গী করবেননা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1002,7,48,'আ‘রাফবাসীদের কয়েকজন জাহান্নামী লোককে তাদের লক্ষণ দ্বারা চিনতে পেরে ডাক দিয়ে বলবেঃ তোমাদের দলবল ও পার্থিব জীবনের ধন-সম্পদ এবং তোমাদের গর্ব, অহংকার তোমাদের কোনই উপকারে এলোনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1003,7,49,'এই জান্নাতবাসীরা কি তারা নয় যাদের সম্পর্কে তোমরা কসম করে বলতে যে, এদের প্রতি আল্লাহ দয়া প্রদর্শন করবেননা? তোমরা জান্নাতে প্রবেশ কর, তোমাদের কোন ভয় নেই এবং তোমরা চিন্তিত ও দুঃখিত হবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1004,7,50,'জাহান্নামীরা জান্নাতীদেরকে সম্বোধন করে বলবেঃ আমাদের উপর কিছু পানি ঢেলে দাও অথবা আল্লাহ প্রদত্ত তোমাদের জীবিকা হতে কিছু প্রদান কর। তারা বলবেঃ আল্লাহ এ দু’টি জিনিস কাফিরদের জন্য হারাম করে দিয়েছেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1005,7,51,'তারা নিজেদের দীনকে ক্রীড়া-কৌতুকের বস্তুতে পরিণত করেছিল এবং পার্থিব জীবন তাদেরকে ধোকায় ফেলে রেখেছিল। সুতরাং আজ আমি তাদেরকে তেমনিভাবে ভুলে থাকব যেমনিভাবে তারা এই দিনের সাক্ষাতের কথা ভুলে গিয়েছিল এবং যেমনভাবে তারা আমার নিদর্শন ও আয়াতসমূহকে অস্বীকার করেছিল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1006,7,52,'আর আমি তাদের নিকট এমন একটি কিতাব পৌঁছিয়েছিলাম যাকে আমি স্বীয় জ্ঞান দ্বারা বিস্তারিত বর্ণনা করেছিলাম এবং যা ছিল মু’মিনদের জন্য পথ নির্দেশ ও রাহমাতের প্রতীক।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1007,7,53,'তারা কি এই অপেক্ষায়ই আছে যে, এর বিষয় বস্তু প্রকাশ করা হোক? যেদিন এর বিষয় বস্তু প্রকাশিত হবে সেদিন যারা এর আগমনের কথা ভুলে গিয়েছিল তারা বলবেঃ বাস্তবিকই আমাদের রবের রাসূলগণ সত্য বাণী নিয়ে এসেছিলেন, এমন কোন সুপারিশকারী আছে কি যারা আমাদের জন্য সুপারিশ করবে? অথবা আমাদেরকে কি পুনরায় দুনিয়ায় পাঠানো যেতে পারে যাতে আমরা পূর্বের কৃতকর্মের তুলনায় ভিন্ন কিছু করতে পারি? নিঃসন্দেহে তারা নিজেরাই নিজেদের ক্ষতি করেছে, আর তারা যেসব মিথ্যা (মা‘বূদ ও রসম রেওয়াজ) রচনা করেছিল, তাও তাদের হতে অন্তর্হিত হয়ে যাবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1008,7,54,'নিশ্চয়ই তোমাদের রাব্ব হচ্ছেন সেই আল্লাহ যিনি আসমান ও যমীনকে ছয় দিনে সৃষ্টি করেছেন। অতঃপর তিনি স্বীয় আরশের উপর সমাসীন হন। তিনি দিনকে রাত দ্বারা আচ্ছাদিত করেন যাতে ওরা একে অন্যকে অনুসরণ করে চলে ত্বরিত গতিতে; সূর্য, চাঁদ ও নক্ষত্ররাজী সবই তাঁর হুকুমের অনুগত। জেনে রেখ, সৃষ্টির একমাত্র কর্তা তিনিই, আর হুকুমের একমাত্র মালিকও তিনি, সারা জাহানের রাব্ব আল্লাহ হলেন বারাকাতময়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1009,7,55,'তোমরা বিনীতভাবে ও সংগোপনে তোমাদের রাব্বকে ডাকবে, তিনি সীমা লংঘনকারীদেরকে ভালবাসেননা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1010,7,56,'দুনিয়ায় শান্তি শৃংখলা স্থাপনের পর বিপর্যয় ও বিশৃংখলা সৃষ্টি করনা, আল্লাহকে ভয়-ভীতি ও আশা আকাংখার সাথে ডাক, নিঃসন্দেহে আল্লাহর রাহমাত সৎকর্মশীলদের অতি সন্নিকটে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1011,7,57,'সেই আল্লাহই স্বীয় রাহমাতের (বৃষ্টির) আগে বাতাসকে সুসংবাদ বহনকারী রূপে প্রেরণ করেন। যখন ঐ বাতাস ভারী মেঘমালাকে বহন করে নিয়ে আসে তখন আমি ঐ মেঘমালাকে কোন নির্জীব ভূ-খন্ডের দিকে প্রেরণ করি। অতঃপর ওটা হতে বারিধারা বর্ষণ করি, তারপর সেই পানির সাহায্যে সেখানে সর্ব প্রকার ফল ফলাদি উৎপাদন করি। এমনিভাবেই আমি মৃতকে জীবিত করি, যাতে তোমরা এটা থেকে শিক্ষা গ্রহণ করতে পার।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1012,7,58,'আর উৎকৃষ্ট ভূমি ওর রবের নির্দেশক্রমে খুব উৎকৃষ্ট ফসল ফলায়, আর যা নিকৃষ্ট ভূমি - তাতে খুব কমই ফসল ফলে থাকে। এমনিভাবেই আমি কৃতজ্ঞ পরায়ণদের জন্য আমার আয়াতসমূহ বিভিন্নভাবে বর্ণনা করে থাকি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1013,7,59,'আমি নূহকে তার সম্প্রদায়ের নিকট প্রেরণ করেছিলাম। সে তাদেরকে বলেছিলঃ হে আমার সম্প্রদায়! তোমরা শুধু আল্লাহর ইবাদাত কর, তিনি ছাড়া তোমাদের আর কোন ইলাহ নেই, আমি তোমাদের প্রতি এক গুরুতর দিনের শাস্তির আশংকা করছি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1014,7,60,'তার সম্প্রদায়ের প্রধান ও নেতারা বললঃ আমরা তোমাকে স্পষ্ট বিভ্রান্তির মধ্যে দেখছি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1015,7,61,'সে বললঃ হে আমার সম্প্রদায়! আমি কোন ভুল ভ্রান্তি ও গুমরাহীর মধ্যে লিপ্ত নই, বরং আমি সারা জাহানের রবের (প্রেরিত) একজন রাসূল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1016,7,62,'আমি আমার রবের বার্তা তোমাদের কাছে পৌঁছে দিচ্ছি, আর আমি তোমাদেরকে হিতোপদেশ দিচ্ছি। তোমরা যা জাননা আমি তা আল্লাহর নিকট থেকে জেনে থাকি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1017,7,63,'তোমাদের মধ্য থেকে একজন লোকের মাধ্যমে তোমাদের রবের পক্ষ হতে উপদেশ বাণী আসায় কি তোমরা বিস্মিত হয়েছ, যাতে সে তোমাদেরকে সতর্ক ও হুশিয়ার করতে পারে, যেন তোমরা সাবধান হও এবং যেন আল্লাহভীতি অবলম্বন করতে পার, হয়ত তোমাদের প্রতি অনুগ্রহ প্রদর্শন করা হবে?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1018,7,64,'কিন্তু তারা মিথ্যা প্রতিপন্ন করল, ফলে তাকে এবং তার সাথে নৌকায় যারা ছিল তাদেরকে (আযাব হতে) রক্ষা করলাম, আর যারা আমার আয়াতসমূহকে মিথ্যা প্রতিপন্ন করে অমান্য করেছিল, তাদেরকে ডুবিয়ে মারলাম। বস্তুতঃ নিঃসন্দেহে তারা ছিল এক অন্ধ সম্প্রদায়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1019,7,65,'‘আদ জাতির নিকট তাদের ভাই হুদকে (নাবী রূপে) পাঠিয়েছিলাম। সে বললঃ হে আমার জাতি! তোমরা আল্লাহর ইবাদাত কর, তিনি ছাড়া তোমাদের আর কোন মা‘বূদ নেই, তোমরা কি সাবধান হবেনা?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1020,7,66,'তার জাতির নেতারা বললঃ আমরা তোমাকে নির্বোধ দেখছি এবং আমরাতো তোমাকে নিশ্চিত রূপে মিথ্যাবাদী মনে করি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1021,7,67,'সে বললঃ হে আমার সম্প্রদায়! আমি নির্বোধ নই, বরং আমি হলাম সারা জাহানের রবের মনোনীত রাসূল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1022,7,68,'আমি আমার রবের বার্তা তোমাদের নিকট পৌঁছে দিচ্ছি, আর আমি তোমাদের একজন বিশ্বস্ত হিতাকাংখী ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1023,7,69,'তোমরা কি এতে বিস্মিত হচ্ছ যে, তোমাদের জাতিরই একটি লোকের মাধ্যমে তোমাদের রবের পক্ষ হতে তাঁর বিধান ও উপদেশসহ তোমাদেরকে সতর্ক করার উদ্দেশে তোমাদের কাছে এসেছে? তোমরা সেই অবস্থার কথা স্মরণ কর যখন নূহের সম্প্রদায়ের পর আল্লাহ তোমাদেরকে তাদের স্থলাভিষিক্ত করেছেন এবং তোমাদেরকে অন্যদের অপেক্ষা শক্তিতে অধিকতর বৈশিষ্ট্যমন্ডিত করেছেন। তোমরা আল্লাহর অনুগ্রহ স্মরণ কর, হয়তো তোমরা সফলকাম হবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1024,7,70,'তারা বললঃ তুমি কি আমাদের নিকট শুধু এই উদ্দেশে এসেছো যে, আমরা যেন একমাত্র আল্লাহরই ইবাদাত করি এবং আমাদের পূর্ব-পুরুষরা যাদের পূজা করত তাদেরকে বর্জন করি? তুমি তোমার কথা ও দাবীতে সত্যবাদী হলে আমাদেরকে যে শাস্তির ভয় দেখাচ্ছ তা আনয়ন কর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1025,7,71,'সে বললঃ তোমাদের রবের শাস্তি ও ক্রোধ তোমাদের উপর অবধারিত হয়ে আছে। তোমরা কি আমার সাথে এমন কতকগুলি নাম সম্বন্ধে বিতর্ক করছ যার নামকরণ করেছ তোমরা এবং তোমাদের বাপ দাদারা, আর যে বিষয়ে আল্লাহ কোন দলীল প্রমাণ অবতীর্ণ করেননি? সুতরাং তোমরা (শাস্তির জন্য) অপেক্ষা করতে থাক, আমিও তোমাদের সাথে অপেক্ষা করছি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1026,7,72,'অতঃপর আমি তাকে (হুদকে) এবং তার সঙ্গী-সাথীদেরকে আমার অনুগ্রহে রক্ষা করলাম, আর যারা আমার নিদর্শনকে মিথ্যা প্রতিপন্ন করেছিল এবং যারা ঈমানদার ছিলনা তাদের মূলোৎপাটন করে ছাড়লাম।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1027,7,73,'আর আমি ছামূদ জাতির নিকট তাদের ভাই সালিহকে প্রেরণ করেছিলাম। সে বলেছিলঃ হে আমার সম্প্রদায়! তোমরা আল্লাহর ইবাদাত কর, আল্লাহ ছাড়া তোমাদের আর কোন মা‘বূদ নেই, তোমাদের রবের পক্ষ হতে এক স্পষ্ট নিদর্শন তোমাদের নিকট এসেছে, এটি আল্লাহর উষ্ট্রী - তোমাদের জন্য একটি নিদর্শন স্বরূপ। তোমরা একে ছেড়ে দাও - আল্লাহর যমীনে চরে খাবে, ওকে খারাপ উদ্দেশে স্পর্শ করনা, (কেহ কোন কষ্ট দিলে) এক যন্ত্রণাদায়ক শাস্তি তোমাদেরকে গ্রাস করে ফেলবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1028,7,74,'তোমরা স্মরণ কর সেই বিষয়টি যখন তিনি ‘আদ জাতির পর তোমাদেরকে তাদের স্থলাভিষিক্ত করেছেন, আর তিনি তোমাদেরকে পৃথিবীতে এমনভাবে প্রতিষ্ঠিত করেছেন যে, তোমরা সমতল ভূমিতে প্রাসাদ ও পাহাড় কেটে আবাস গৃহ নির্মাণ করেছ। সুতরাং তোমরা আল্লাহর অনুগ্রহের কথা স্মরণ কর এবং পৃথিবীতে বিপর্যয় ছড়িয়ে দিওনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1029,7,75,'তার সম্প্রদায়ের দাম্ভিক প্রধানরা তাদের মধ্যকার দুর্বল ও উৎপীড়িত মু’মিনদেরকে বললঃ তোমরা কি বিশ্বাস কর যে, সালিহ তার রাব্ব কর্তৃক প্রেরিত হয়েছে? তারা উত্তরে বললঃ নিশ্চয়ই যে বার্তাসহ সে প্রেরিত হয়েছে, আমরা তা বিশ্বাস করি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1030,7,76,'দাম্ভিকরা বললঃ তোমরা যা বিশ্বাস কর আমরা তা বিশ্বাস করিনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1031,7,77,'অতঃপর তারা সেই উষ্ট্রীটিকে মেরে ফেললো এবং গর্ব ও দাম্ভিকতার সাথে তাদের রবের নির্দেশের বিরোদ্ধাচরণ করল এবং বললঃ হে সালিহ! তুমি সত্য রাসূল হয়ে থাকলে আমাদেরকে যে শাস্তির ভয় দেখাচ্ছ তা আনয়ন কর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1032,7,78,'সুতরাং ভূমিকম্প তাদেরকে গ্রাস করে নিলো, ফলে তারা নিজেদের গৃহের মধ্যেই নতজানু হয়ে পড়ে রইল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1033,7,79,'সালিহ এ কথা বলে তাদের জনপদ হতে বের হয়ে গেলঃ হে আমার সম্প্রদায়! আমি আমার রবের বার্তা তোমাদের কাছে পৌঁছে দিয়েছি, আর আমি তোমাদেরকে উপদেশ দিয়েছিলাম, কিন্তু তোমরাতো হিতৈষী বন্ধুদেরকে পছন্দ করনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1034,7,80,'আর আমি লূতকে পাঠিয়েছিলাম। সে তার কাওমকে বলেছিলঃ তোমরা এমন অশ্লীল ও কু-কর্ম করছো যা তোমাদের পূর্বে বিশ্বে আর কেহই করেনি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1035,7,81,'তোমরা স্ত্রীলোকদের বাদ দিয়ে পুরুষদের দ্বারা নিজেদের যৌন ইচ্ছা নিবারণ করে নিচ্ছ। প্রকৃত পক্ষে, তোমরা হচ্ছ সীমালংঘনকারী সম্প্রদায়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1036,7,82,'তার জাতির লোকদের এটা ছাড়া আর কোন জবাবই ছিলনা যে, এদের তোমাদের জনপদ থেকে বের করে দাও, এরা নিজেদেরকে বড় পবিত্র রাখতে চায়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1037,7,83,'পরিশেষে, আমি তাকে এবং তার পরিবারের লোকদেরকে, তার স্ত্রী ছাড়া, শাস্তি হতে রক্ষা করেছিলাম, তার স্ত্রী তাদের সাথে পিছনেই রয়ে গিয়েছিল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1038,7,84,'অতঃপর আমি তাদের উপর মুষলধারে বারিপাত ঘটালাম, অতঃপর লক্ষ্য কর, অপরাধী লোকদের পরিণাম কি হয়েছিল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1039,7,85,'আর আমি মাদইয়ানবাসীদের কাছে তাদেরই ভাই শু‘আইবকে পাঠিয়েছিলাম। সে বললঃ হে আমার সম্প্রদায়! তোমরা একমাত্র আল্লাহর ইবাদাত কর, তিনি ছাড়া তোমাদের আর কোন মা‘বূদ নেই। তোমাদের রবের পক্ষ হতে তোমাদের কাছে সুস্পষ্ট দলীল এসে গেছে। সুতরাং তোমরা ওযন ও পরিমাণ পূর্ণ মাত্রায় দিবে, মানুষকে তাদের প্রাপ্য বস্তু কম দিয়ে ক্ষতিগ্রস্ত করবেনা। আর দুনিয়ায় শান্তি শৃংখলা স্থাপনের পর ঝগড়া ফাসাদ ও বিপর্যয় ঘটাবেনা, তোমরা বাস্তবিক পক্ষে ঈমানদার হলে এই পথই হল তোমাদের জন্য কল্যাণকর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1040,7,86,'আর (জীবনের) প্রতিটি পথে এমনিভাবে দস্যি হয়ে যেওনা যে, ঈমানদার লোকদেরকে ভয়ভীতি প্রদর্শন ও আল্লাহর পথ হতে বিরত রাখতে থাকবে এবং সহজ সরল পথকে বক্র করায় ব্যস্ত থাকবে। ঐ অবস্থানটির কথা স্মরণ কর, যখন তোমরা সংখ্যায় স্বল্প ছিলে, অতঃপর তিনি (আল্লাহ) তোমাদের সংখ্যা বৃদ্ধি করে দিলেন, আর এই জগতে বিপর্যয় সৃষ্টিকারীদের পরিণতি কি হয়েছে তা জ্ঞানচক্ষু খুলে লক্ষ্য কর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1041,7,87,'আমার নিকট যা (আল্লাহর পক্ষ হতে) প্রেরিত হয়েছে তা যদি তোমাদের কোন দল বিশ্বাস করে এবং কোন দল অবিশ্বাস করে তাহলে ধৈর্য ধারণ কর যতক্ষণ না আল্লাহ আমাদের মধ্যে চুড়ান্ত ফাইসালা করে দেন। তিনিই হলেন উত্তম ফাইসালাকারী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1042,7,88,'আর তার সম্প্রদায়ের দাম্ভিক ও অহংকারী প্রধানরা বলেছিলঃ হে শু‘আইব! আমরা অবশ্যই তোমাকে, তোমার সংগী সাথী মু’মিনদেরকে আমাদের জনপদ হতে বহিস্কার করব অথবা তোমরা আমাদের ধর্মাদর্শে ফিরে আসবে। সে বললঃ আমরা যদি তাতে রাযী না হই?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1043,7,89,'তোমাদের ধর্মাদর্শ হতে আল্লাহ আমাদেরকে মুক্তি দেয়ার পর আমরা যদি তাতে আবার ফিরে যাই তাহলে নিশ্চিতভাবে আল্লাহর প্রতি মিথ্যা আরোপকারী হব! আমাদের রাব্ব আল্লাহ না চাইলে ওতে আবার ফিরে যাওয়া আমাদের পক্ষে কোন ক্রমেই সম্ভব নয়। সবকিছুই আমাদের রবের জ্ঞানায়ত্ত, আমরা আল্লাহর উপরই নির্ভর করছি। হে আমাদের রাব্ব! আমাদের ও আমাদের সম্প্রদায়ের মধ্যে সঠিকভাবে ফাইসালা করে দিন, আপনিইতো সর্বোত্তম ফাইসালাকারী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1044,7,90,'আর তাদের সম্প্রদায়ের কাফির সর্দাররা বললঃ তোমরা যদি শু‘আইবকে অনুসরণ কর তাহলে তোমরা অবশ্যই ক্ষতিগ্রস্ত হবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1045,7,91,'অতঃপর ভূ-কম্পন তাদেরকে গ্রাস করল, ফলে তারা নিজেদের গৃহেই উপুড় হয়ে পড়ে রইল। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1046,7,92,'অবস্থা দেখে মনে হল, যারা শু‘আইবকে মিথ্যা প্রতিপন্ন করেছিল তারা যেন কখনও সেখানে বসবাস করেনি, শু‘আইবকে মিথ্যা প্রতিপন্নকারী লোকেরাই শেষ পর্যন্ত ধ্বংস হয়ে গিয়েছিল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1047,7,93,'সে তাদের নিকট হতে এ কথা বলে বেরিয়ে এলোঃ হে আমার জাতি! আমি আমার রবের বার্তা তোমাদের নিকট পৌঁছিয়েছি এবং সৎ উপদেশ দিয়েছি। সুতরাং আমি কাফির সম্প্রদায়ের জন্য কি করে আক্ষেপ করতে পারি?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1048,7,94,'আমি কোন জনপদে নাবী রাসূল পাঠালে, ওর অধিবাসীদেরকে দুঃখ-দারিদ্র ও রোগ ব্যাধিতে আক্রান্ত করে থাকি, উদ্দেশ্য হল, তারা যেন নম্র ও বিনয়ী হয়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1049,7,95,'অতঃপর আমি তাদের দুরাবস্থাকে সুখ স্বাচ্ছন্দ্য দ্বারা পরিবর্তন করে দিয়েছি। অবশেষে তারা খুব প্রাচুর্যের অধিকারী হয়, আর তারা (অকৃতজ্ঞ স্বরে) বলেঃ আমাদের পূর্ব-পুরুষরাও এভাবে দুঃখ ভোগ করেছে। অতঃপর অকস্মাৎ আমি তাদেরকে পাকড়াও করলাম, কিন্তু তারা কিছুই বুঝতে পারলনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1050,7,96,'জনপদের অধিবাসীরা যদি ঈমান আনত এবং আল্লাহভীতি অবলম্বন করত তাহলে আমি তাদের জন্য আকাশ ও পৃথিবীর বরকতের দ্বারসমূহ খুলে দিতাম, কিন্তু তারা নাবী রাসূলদেরকে মিথ্যা প্রতিপন্ন করেছে, ফলে তাদের কৃতকর্মের জন্য আমি তাদেরকে পাকড়াও করলাম।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1051,7,97,'রাতে যখন তারা ঘুমিয়ে থাকে তখন আমার শাস্তি এসে তাদেরকে গ্রাস করে ফেলবে - এটা হতে কি জনপদের অধিবাসীরা নির্ভয় হয়ে পড়েছে ?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1052,7,98,'অথবা জনপদের লোকেরা কি এই ভয় রাখেনা যে, আমার শাস্তি তাদের উপর তখন আপতিত হবে যখন তারা পূর্বাহ্নে আমোদ প্রমোদে রত থাকবে ?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1053,7,99,'তারা কি আল্লাহর পাকড়াও থেকে নিরাপদ হয়ে গেছে ? সর্বনাশগ্রস্থ সম্প্রদায় ছাড়া আল্লাহর পাকড়াও থেকে কেহই নি:শঙ্ক হতে পারেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1054,7,100,'কোন এলাকার অধিবাসী ধ্বংস হওয়ার পর সেই এলাকার যারা উত্তরাধিকারী হয়, তাদের কাছে কি এটা প্রতীয়মান হয়নি যে, আমি ইচ্ছা করলে তাদের পাপের কারণে তাদেরকে শাস্তি দিতে পারি, আর তাদের অন্তঃকরণের উপর মোহর এটে দিতে পারি যাতে তারা কিছুই শুনতে পারেনা?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1055,7,101,'ঐ জনপদগুলির কিছু ঘটনা আমি তোমার নিকট বর্ণনা করছি, তাদের কাছে রাসূলগণ সুস্পষ্ট দলীল প্রমাণসহ এসেছিল, কিন্তু পূর্বে তারা যা প্রত্যাখ্যান করেছিল তার প্রতি তারা ঈমান আনার ছিলনা, এমনিভাবেই আল্লাহ অবিশ্বাসীদের অন্তঃকরণের উপর মোহর মেরে দিয়েছেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1056,7,102,'আমি তাদের অধিকাংশকে অঙ্গীকার ও প্রতিশ্রুতি রক্ষাকারীরূপে পাইনি, তবে তাদের অধিকাংশকে পাপাচারী রূপে পেয়েছি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1057,7,103,'অতঃপর আমি মূসাকে আমার আয়াত ও নিদর্শনসহ ফির‘আউন ও তার পরিষদবর্গের নিকট পাঠালাম, কিন্তু তারা যুলম করল। সুতরাং এই বিপর্যয় সৃষ্টিকারীদের পরিণাম কি হয়েছিল তা তুমি লক্ষ্য কর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1058,7,104,'মূসা বললঃ হে ফির‘আউন! আমি বিশ্বের রবের একজন রাসূল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1059,7,105,'আমি আল্লাহ সম্বন্ধে সত্য ছাড়া আর কিছুই বলবনা, আমি তোমাদের রবের পক্ষ হতে তোমাদের কাছে সুস্পষ্ট প্রমাণ নিয়ে এসেছি। সুতরাং বানী ইসরাঈলকে আমার সাথে যেতে দাও।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1060,7,106,'ফির‘আউন বললঃ তুমি যদি বাস্তবিকই স্পষ্ট দলীল ও কোন নিদর্শন এনে থাক তাহলে উপস্থিত কর, যদি তুমি সত্যবাদী হও।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1061,7,107,'তখন মূসা তার লাঠি নিক্ষেপ করল এবং সহসাই ওটা এক জীবিত অজগরে পরিণত হল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1062,7,108,'আর সে তার হাত বের করল, তৎক্ষণাৎই ওটা দর্শকদের দৃষ্টিতে শুভ্র ও উজ্জ্বল আলোকময় প্রতিভাত হল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1063,7,109,'ফির‘আউন সম্প্রদায়ের প্রধানরা বললঃ নিঃসন্দেহে এ ব্যক্তি বড় সুদক্ষ যাদুকর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1064,7,110,'সে তোমাদেরকে তোমাদের দেশ থেকে বহিস্কার করতে চায়, এখন তোমাদের পরামর্শ কি?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1065,7,111,'তারা বললঃ তাকে এবং তার ভাইকে (হারুন) কিছু দিনের জন্য অবকাশ দিন, আর শহরে শহরে সংগ্রাহক পাঠিয়ে দিন,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1066,7,112,'যেন তারা আপনার (ফির‘আউন) নিকট প্রত্যেক সুদক্ষ যাদুকরকে উপস্থিত করে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1067,7,113,'যাদুকরেরা ফির‘আউনের কাছে এসে বললঃ আমরা যদি বিজয় লাভ করতে পারি তাহলে আমাদের জন্য পুরস্কার থাকবে তো?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1068,7,114,'সে বললঃ হ্যাঁ, তোমরাই হবে আমার দরবারের নিকটতম ব্যক্তি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1069,7,115,'অতঃপর যাদুকরেরা বললঃ হে মূসা! তুমি কি তোমার লাঠি নিক্ষেপ করবে, নাকি আমরাই (প্রথমে) নিক্ষেপ করব?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1070,7,116,'বললঃ তোমরাই নিক্ষেপ কর। সুতরাং যখন যাদুকরেরা নিক্ষেপ করল তখন লোকের চোখে ধাঁধাঁর সৃষ্টি করল এবং তাদেরকে ভীত ও আতংকিত করল, তারা এক বড় রকমের যাদু দেখাল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1071,7,117,'আমি মূসার নিকট এই প্রত্যাদেশ পাঠালাম, তুমি তোমার লাঠিখানা নিক্ষেপ কর। মূসা তা নিক্ষেপ করলে ওটা (এক বিরাট অজগর হয়ে) সহসা ওদের অলীক সৃষ্টিগুলিকে গ্রাস করতে লাগল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1072,7,118,'পরিশেষে যা হক ছিল তা সত্য প্রমাণিত হল, আর তারা যা কিছু করেছিল তা বাতিল প্রতিপন্ন হল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1073,7,119,'আর ফির‘আউন ও তার দলবলের লোকেরা মুকাবিলার মাইদানে পরাজিত হল এবং লাঞ্ছিত ও অপমানিত হল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1074,7,120,'যাদুকরেরা তখন সাজদাহবনত হল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1075,7,121,'তারা বললঃ আমরা বিশ্বের রবের প্রতি ঈমান আনলাম,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1076,7,122,'মূসা ও হারূনের রবের প্রতি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1077,7,123,'ফির‘আউন বললঃ আমি অনুমতি দেয়ার আগেই তোমরা তার উপর ঈমান আনলে? নিশ্চয়ই তোমরা এক চক্রান্ত পাকিয়েছ শহরবাসীদের সেখান থেকে তাড়িয়ে দেয়ার জন্য। কিন্তু সত্ত্বরই তোমরা এর পরিণাম জ্ঞাত হবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1078,7,124,'অবশ্যই আমি তোমাদের বিপরীত হস্ত-পদ কর্তন করব, তারপর তোমাদের সবাইকে আমি শুলে চড়াব।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1079,7,125,'তারা (যাদুকরেরা) বললঃ নিশ্চয়ই আমরা আমাদের রবের নিকট ফিরে যাব।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1080,7,126,'তুমি আমাদের মধ্যে এছাড়া কোনই দোষ পাচ্ছনা যে, আমাদের কাছে যখন আমাদের রবের নিদর্শনাবলী এসে গেল তখন আমরা ঈমান এনেছি। হে আমাদের রাব্ব! আমাদেরকে ধৈর্য দান করুন এবং মুসলিম রূপে আমাদের মৃত্যু দান করুন!');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1081,7,127,'ফির‘আউন সম্প্রদায়ের সর্দাররা তাকে বললঃ তুমি কি মূসা ও তার সম্প্রদায়কে রাজ্যে অশান্তি ও বিপর্যয় সৃষ্টির জন্য মুক্ত ছেড়ে দিবে এবং তোমাকে ও তোমার দেবতাদেরকে বর্জন করে চলার সুযোগ দিবে? সে বললঃ আমি তাদের সন্তানদের হত্যা করব এবং তাদের নারীদেরকে জীবিত রাখব, তাদের উপর আমাদের শক্তি ও ক্ষমতা প্রবল ও সুপ্রতিষ্ঠিত রয়েছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1082,7,128,'মূসা তার সম্প্রদায়কে বললঃ তোমরা আল্লাহর নিকট সাহায্য প্রার্থনা কর এবং ধৈর্য ও সহিষ্ণুতা অবলম্বন কর। এই পৃথিবীর সার্বভৌম মালিক আল্লাহ, তিনি তাঁর বান্দাদের মধ্যে যাকে ইচ্ছা উহার উত্তরাধিকারী করেন, শুভ পরিণাম ও শেষ সাফল্য লাভ হয় আল্লাহভীরুদের জন্য।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1083,7,129,'তারা বললঃ আপনি আমাদের নিকট (নাবী রূপে) আগমনের পূর্বেও আমরা (ফির‘আউন কর্তৃক) নির্যাতিত হয়েছি এবং আপনার আগমনের পরও নির্যাতিত হচ্ছি। সে (মূসা) বললঃ সম্ভবতঃ শীঘ্রই তোমাদের রাব্ব তোমাদের শক্রকে ধ্বংস করবেন এবং তোমাদেরকে তাদের রাজ্যে স্থলাভিষিক্ত করবেন, অতঃপর তোমরা কিরূপ কাজ কর তা তিনি লক্ষ্য করবেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1084,7,130,'আমি ফির‘আউনের অনুসারীদেরকে বছরের পর বছর দুর্ভিক্ষ, অজন্ম ও ফসলহানির মধ্যে বিপন্ন রেখেছিলাম, যাতে তারা ঈমান আনে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1085,7,131,'যখন তাদের সুখ, শান্তি ও কল্যাণ হত তখন তারা বলতঃ এটা আমাদের প্রাপ্য, আর যদি তাদের দুঃখ দৈন্য ও বিপদ আপদ হত তখন তারা ওটাকে মূসা ও তার সঙ্গী সাথীদের মন্দ ভাগ্যের কারণ রূপে নিরূপণ করত। তোমরা জেনে রেখ যে, তাদের অকল্যাণ আল্লাহরই নিয়ন্ত্রণাধীন, কিন্তু তাদের অধিকাংশ সে সম্পর্কে কোন জ্ঞান রাখেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1086,7,132,'তারা বললঃ আমাদেরকে যাদু করার জন্য যে কোন নিদর্শনই পেশ করনা কেন আমরা তাতে ঈমান আনবনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1087,7,133,'অতঃপর আমি তাদের উপর প্লাবণ, পঙ্গপাল, উকুন, ব্যাঙ ও রক্ত ধারার শাস্তি পাঠিয়ে ক্লিষ্ট করি, ওগুলি ছিল আমার সুস্পষ্ট নিদর্শন, কিন্তু তারা শেষ পর্যন্ত দাম্ভিকতা ও অহংকারেই মেতে রইল, তারা ছিল একটি অপরাধী জাতি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1088,7,134,'তাদের উপর কোন বালা মুসীবত ও বিপদ-আপদ আপতিত হলে তারা বলতঃ হে মূসা! আমাদের পক্ষ থেকে তোমার রবের নিকট দু‘আ কর। তার সাথে তোমার যে অঙ্গীকার রয়েছে তদনুযায়ী যদি আমাদের উপর থেকে প্লেগ দূর করে দিতে পার তাহলে আমরা তোমার প্রতি ঈমান আনবো এবং তোমার সাথে বানী ইসরাঈলদেরকে পাঠিয়ে দিব।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1089,7,135,'কিন্তু যখনই আমি তাদের উপর হতে প্লেগের শাস্তির সেই সময়টি অপসারিত করতাম যা তাদের জন্য নির্ধারিত ছিল, তখনই আবার তারা প্রতিশ্রুতি ভঙ্গ করত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1090,7,136,'সুতরাং আমি তাদের হতে প্রতিশোধ নিলাম এবং তাদেরকে অতল সমুদ্রে ডুবিয়ে মারলাম, কেননা তারা আমার নিদর্শনসমূহকে মিথ্যা প্রতিপন্ন করেছে, আর এই ব্যাপারে তারা ছিল সম্পূর্ণ গাফিল বা উদাসীন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1091,7,137,'যে জাতিকে দুর্বল ও দীনহীন ভাবা হত আমি তাদেরকে আমার কল্যাণ প্রাপ্ত রাজ্যের পূর্ব ও পশ্চিমের উত্তরাধিকারী বানাই, আর বানী ইসরাঈল জাতি সম্পর্কে তোমার রবের শুভ ও কল্যাণময় বাণী (প্রতিশ্রুতি) পূর্ণ হল যেহেতু তারা ধৈর্য ধারণ করেছিল। আর ফির‘আউন ও তার সম্প্রদায়ের কীর্তিকলাপ ও উচ্চ প্রাসাদসমূহকে আমি ধ্বংসস্তুপে পরিণত করেছি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1092,7,138,'আমি বানী ইসরাঈলকে সমুদ্র পার করিয়ে দিলাম, অতঃপর তারা মূর্তি পূজারত এক জাতির সংস্পর্শে এল। তারা বললঃ হে মূসা! তাদের যেরূপ মা‘বূদ রয়েছে, আমাদের জন্যও ঐরূপ মা‘বূদ বানিয়ে দাও। সে বললঃ তোমরা একটি মূর্খ সম্প্রদায়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1093,7,139,'এ সব লোক যে কাজে লিপ্ত রয়েছে তাতো ধ্বংস করা হবে, আর তারা যা করছে তা অমূলক ও বাতিল বিষয়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1094,7,140,'সে বললঃ আমি কি আল্লাহকে ছেড়ে তোমাদের জন্য অন্য মা‘বূদের সন্ধান করব? অথচ তিনিই হলেন একমাত্র আল্লাহ যিনি তোমাদেরকে বিশ্ব জগতে শ্রেষ্ঠত্ব দান করেছেন!');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1095,7,141,'স্মরণ কর সেই সময়টির কথা, যখন আমি তোমাদেরকে ফির‘আউনের অনুসারীদের দাসত্ব হতে মুক্তি দিয়েছি, যারা তোমাদেরকে অতিশয় মর্মান্তিক, কষ্টদায়ক ও ন্যাক্কারজনক শাস্তি দিত, তোমাদের পুত্রদেরকে হত্যা করত এবং নারীদেরকে জীবিত রাখত। এটা ছিল তোমাদের জন্য তোমাদের রবের পক্ষ থেকে বিরাট পরীক্ষা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1096,7,142,'আমি মূসাকে ও‘য়াদা দিয়েছিলাম ত্রিশ রাতের জন্য এবং আরো দশ দ্বারা ওটা পূর্ণ করেছিলাম। এভাবে তার রবের নির্ধারিত সময়টি চল্লিশ রাত দ্বারা পূর্ণতা লাভ করে। মূসা তার ভাই হারুনকে বলেছিলঃ তুমি আমার কাওমের মধ্যে আমার স্থলাভিষিক্তরূপে কাজ করবে এবং তাদেরকে সংশোধন করার কাজ করতে থাকবে, এবং বিপর্যয় ও ফাসাদ সৃষ্টিকারীদের অনুসরণ করবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1097,7,143,'মূসা যখন নির্ধারিত স্থানে উপস্থিত হল, তখন তার রাব্ব তার সাথে কথা বললেন। সে তখন নিবেদন করলঃ হে আমার রাব্ব! আপনি আমাকে দর্শন দিন। আল্লাহ বললেনঃ তুমি আমাকে আদৌ দেখতে পারবেনা, তবে তুমি ঐ পাহাড়ের দিকে তাকাও। যদি ঐ পাহাড় স্বস্থানে স্থির থাকে তাহলে তুমি আমাকে দেখতে পারবে। অতঃপর তার রাব্ব যখন পাহাড়ে জ্যোতিস্মান হলেন তখন তা পাহাড়কে চূর্ণ বিচূর্ণ করে দিল, আর মূসা সংজ্ঞাহীন হয়ে পড়ে গেল। যখন চেতনা ফিরে এলো তখন সে বললঃ আপনি মহিমাময়, আপনার পবিত্র সত্তার কাছে আমি তাওবাহ করছি এবং আমিই সর্বপ্রথম ঈমান আনলাম।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1098,7,144,'আল্লাহ বললেনঃ হে মূসা! আমি তোমাকেই আমার রিসালাত ও আমার সাথে বাক্যালাপের জন্য লোকদের মধ্য হতে মনোনীত করেছি। অতএব আমি তোমাকে যা কিছু দিই তা তুমি গ্রহণ কর এবং কৃতজ্ঞতা প্রকাশকারীদের অন্তর্ভুক্ত হও।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1099,7,145,'অতঃপর আমি তার জন্য ফলকের উপর সর্ব বিষয়ের উপদেশ এবং সর্ব বিষয়ের সুস্পষ্ট ব্যাখ্যা লিখে দিয়েছি, (অতঃপর তাকে বললাম) এই হিদায়াতকে দৃঢ় হস্তে শক্তভাবে গ্রহণ কর এবং তোমার সম্প্রদায়কে এর সুন্দর সুন্দর বিধানগুলি মেনে চলতে আদেশ কর। আমি ফাসেক বা সত্যত্যাগীদের আবাসস্থান শীঘ্রই তোমাদেরকে প্রদর্শন করাব।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1100,7,146,'পৃথিবীতে যারা অন্যায়ভাবে অহংকার করে বেড়ায় আমি তাদেরকে আমার নিদর্শনসমূহ হতে ফিরিয়ে রাখব, প্রত্যেকটি নিদর্শন দেখার পরেও তারা তাতে ঈমান আনবে না, তারা যদি সৎ পথ দেখতে পায় তবুও সেই পথ সৎ পথ বলে গ্রহণ করবে না। কিন্তু তারা ভ্রান্ত ও গুমরাহীর পথ দেখলে তাকেই তারা গ্রহণ করবে। এর কারণ হল-তারা আমার নিদর্শনসমূহকে মিথ্যা প্রতিপন্ন করেছে এবং তারা তা থেকে সম্পূর্ণরূপে অমনোযোগী ছিল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1101,7,147,'যারা আমার নিদর্শনসমূহ ও আখিরাতের সাক্ষাত মিথ্যা প্রতিপন্ন করে তাদের সমূদয় ‘আমল বিনষ্ট হয়ে যায়, তারা যা করে তদনুযায়ী তাদেরকে প্রতিফল দেয়া হবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1102,7,148,'আর মূসার চলে যাবার পর অলংকার দ্বারা একটি বাছুরের (মত) পুতুল তৈরী করল, ওটা হতে গরুর মত শব্দ বের হত। তারা কি দেখেনি যে, ওটা তাদের সাথে কথা বলেনা এবং তাদেরকে কোন পথও দেখিয়ে দেয়না? তবুও তারা ওটাকে মা‘বূদ রূপে গ্রহণ করল। বস্তুতঃ তারা ছিল বড় অত্যাচারী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1103,7,149,'আর যখন তারা লজ্জিত হল এবং দেখল যে, (প্রকৃত পক্ষে) তারা বিভ্রান্ত হয়েছে, তখন তারা বললঃ আমাদের প্রভু যদি আমাদের প্রতি অনুগ্রহ না করেন তাহলে আমরা ক্ষতিগ্রস্ত হয়ে যাব।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1104,7,150,'মূসা রাগাম্বিত বিক্ষুদ্ধ অবস্থায় নিজ জাতির নিকট ফিরে এসে বললঃ আমার চলে যাওয়ার পর তোমরা কত নিকৃষ্ট প্রতিনিধিত্ব করেছ, তোমরা তোমাদের প্রভুর নির্দেশের পূর্বেই কেন তাড়াহুড়া করতে গেলে? অতঃপর সে ফলকগুলি ফেলে দিল এবং স্বীয় ভাইয়ের মস্তক (চুল) ধরে নিজের দিকে টানতে লাগল। সে (হারূন) বললঃ হে আমার মাতার পুত্র! এই লোকগুলি আমাকে পরাভূত করে ফেলেছিল এবং আমাকে মেরে ফেলতে উদ্যত হয়েছিল। অতএব তুমি আমাকে শক্র সমক্ষে হাস্যস্পদ করনা, আর এই যালিম লোকদের মধ্যে আমাকে গণ্য করনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1105,7,151,'তখন মূসা বললঃ হে আমার রাব্ব! আমাকে ও আমার ভাইকে ক্ষমা করুন! আর আমাদেরকে আপনার রাহমাতের মধ্যে দাখিল করুন! আপনি সব চেয়ে দয়াবান।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1106,7,152,'যারা গো-বৎসকে উপাস্য রূপে গ্রহণ করেছে, অবশ্যই তারা এই পার্থিব জীবনে তাদের রবের গযব ও লাঞ্ছনায় নিপতিত হবে, মিথ্যা রচনাকারীদেরকে আমি এভাবেই প্রতিফল দিয়ে থাকি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1107,7,153,'যারা খারাপ কাজ করে, এরপর তাওবাহ করলে ও ঈমান আনলে, তোমার আল্লাহতো ক্ষমাশীল ও পরম দয়ালু।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1108,7,154,'মূসার ক্রোধ যখন প্রশমিত হল তখন সে প্রস্তর ফলকগুলি তুলে নিল, তাতে লেখা ছিলঃ যারা তাদের রাব্বকে ভয় করে তাদের জন্য রয়েছে হিদায়াত ও রাহমাত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1109,7,155,'মূসা তার সম্প্রদায় হতে সত্তর জন নেতৃস্থানীয় লোক আমার নির্ধারিত স্থানে সমবেত হওয়ার জন্য নির্বাচন করল, যখন ঐ লোকগুলি একটি কঠিন ভূ-কম্পনে আক্রান্ত হল তখন মূসা বললঃ হে আমার রাব্ব! আপনি ইচ্ছা করলে এর পূর্বেও ওদেরকে এবং আমাকে ধ্বংস করতে পারতেন, আমাদের মধ্যকার কতক নির্বোধ লোকের অন্যায়ের কারণে কি আপনি আমাদেরকে ধ্বংস করবেন? এটাতো আপনার পরীক্ষা, আপনি যাকে ইচ্ছা বিভ্রান্ত করেন এবং যাকে ইচ্ছা সৎ পথে পরিচালিত করেন, আপনিইতো আমাদের অভিভাবক। সুতরাং আপনি আমাদেরকে ক্ষমা করুন, এবং আমাদের প্রতি অনুগ্রহ করুন, ক্ষমাকারীদের মধ্যে আপনিইতো উত্তম ক্ষমাকারী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1110,7,156,'অতএব আমাদের জন্য এই দুনিয়ায় ও পরকালে কল্যাণ নির্ধারিত করে দিন, আমরা আপনার নিকটই প্রত্যাবর্তন করেছি। তিনি (আল্লাহ) বললেনঃ যাকে ইচ্ছা আমি আমার শাস্তি দিয়ে থাকি, আর আমার করুণা ও দয়া প্রতিটি জিনিসকেই পরিব্যপ্ত করে রয়েছে, সুতরাং আমি তাদের জন্যই কল্যাণ অবধারিত করব যারা পাপাচার হতে বিরত থাকে, যাকাত দেয় এবং আমার নিদর্শনসমূহের প্রতি ঈমান আনে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1111,7,157,'যারা সেই নিরক্ষর রাসূলের অনুসরণ করে চলে যার কথা তারা তাদের নিকট রক্ষিত তাওরাত ও ইঞ্জীল কিতাবে লিখিত পায়, যে মানুষকে সৎ কাজের নির্দেশ দেয় ও অন্যায় কাজ করতে নিষেধ করে, আর সে তাদের জন্য পবিত্র বস্তুসমূহ বৈধ করে এবং অপবিত্র ও খারাপ বস্তুকে তাদের প্রতি অবৈধ করে, আর তাদের উপর চাপানো বোঝা ও বন্ধন হতে তাদেরকে মুক্ত করে। সুতরাং তার প্রতি যারা ঈমান রাখে, তাকে সম্মান করে এবং সাহায্য করে ও সহানুভূতি প্রকাশ করে, আর সেই আলোকের অনুসরণ করে চলে যা তার সাথে অবতীর্ণ করা হয়েছে, তারাই (ইহকালে ও পরকালে) সাফল্য লাভ করবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1112,7,158,'বলঃ হে মানবমন্ডলী! আমি তোমাদের সকলের জন্য সেই আল্লাহর রাসূল রূপে প্রেরিত হয়েছি, যিনি আকাশ ও ভূ-মন্ডলের সার্বভৌম একচ্ছত্র মালিক, তিনি ছাড়া আর কোন উপাস্য নেই, তিনিই জীবিত করেন ও মৃত্যু ঘটান। সুতরাং আল্লাহর প্রতি এবং তাঁর সেই বার্তাবাহক নিরক্ষর নাবীর প্রতি ঈমান আন। যে আল্লাহ ও তাঁর কালামে বিশ্বাস স্থাপন করে, তোমরা তারই অনুসরণ কর। আশা করা যায়, তোমরা সরল সঠিক পথের সন্ধান পাবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1113,7,159,'মূসার সম্প্রদায়ের মধ্যে এমন একদল লোক রয়েছে যারা সঠিক ও নির্ভুল পথ প্রদর্শন করে এবং ন্যায় বিচার করে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1114,7,160,'আমি বানী ইসরাঈলকে দ্বাদশ গোত্রে বিভক্ত করেছি। মূসার সম্প্রদায়ের লোকেরা যখন তার কাছে পানির দাবী জানাল, তখন আমি মূসার কাছে প্রত্যাদেশ পাঠালাম - তোমার লাঠি দ্বারা পাথরে আঘাত কর, ফলে ওটা হতে দ্বাদশ প্রস্রবণ উৎসারিত হল, প্রত্যেক গোত্র নিজ নিজ পানস্থান জেনে নিল। আর আমি তাদের উপর মেঘ দ্বারা ছায়া বিস্তার করলাম এবং তাদের জন্য আকাশ হতে ‘মান্না’ ও ‘সালওয়া’ খাদ্যরূপী নি‘আমাত অবতীর্ণ করলাম। সুতরাং (আমি বললাম) তোমাদেরকে যা কিছু পবিত্র জীবিকা দান করা হয়েছে তা আহার কর। (কিন্তু ওরা আমার শর্ত উপেক্ষা করে যুলম করল) তারা আমার উপর কোন যুলম করেনি, বরং তারা নিজেদের উপরই যুলম করেছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1115,7,161,'যখন আমি তাদেরকে বলেছিলামঃ এই (বাইতুল মুকাদ্দাস ও তৎসংশ্লিষ্ট) জনপদে বসবাস কর এবং যা ইচ্ছা আহার কর, আর তোমরা বলঃ (হে রাব্ব!) ক্ষমা চাই, আর দ্বারদেশ দিয়ে নত শিরে প্রবেশ কর; আমি তোমাদের অপরাধ ক্ষমা করব এবং সৎ কর্মশীল লোকদের জন্য আমার দান বৃদ্ধি করব।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1116,7,162,'কিন্তু তাদের মধ্যে যারা যালিম ও সীমা লংঘনকারী ছিল, তারা সেই কথা পরিবর্তন করে ফেললো যা তাদেরকে বলতে বলা হয়েছিল, সুতরাং তাদের সীমা লংঘনের কারণে আমি আসমান হতে তাদের উপর শাস্তি প্রেরণ করলাম।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1117,7,163,'আর তাদেরকে সেই জনপদের অবস্থাও জিজ্ঞেস কর যা সমুদ্রের তীরে অবস্থিত ছিল। যখন তারা শনিবারের আদেশ লংঘন করেছিল। শনিবার উদযাপনের দিন মাছ পানিতে ভেসে তাদের নিকট আসত, কিন্তু যেদিন তারা শনিবার উদযাপন করতনা, সেদিন ওগুলি তাদের কাছে আসতনা, এভাবে আমি তাদের নাফরমানীর কারণে তাদেরকে পরীক্ষা করেছিলাম।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1118,7,164,'যখন তাদের একদল লোক অপর দলের নিকট বলেছিলঃ ঐ জাতিকে তোমরা কেন উপদেশ দিচ্ছ যাদেরকে আল্লাহ ধ্বংস করবেন অথবা কঠিন শাস্তি দিবেন? তারা উত্তরে বললঃ তোমাদের রবের নিকট দোষমুক্তির জন্য এবং এই আশা করছি যে, হয়তো তারা তাঁকে ভয় করবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1119,7,165,'তাদেরকে যে উপদেশ দেয়া হয় তা যখন তারা বিস্মৃত হয় তখন যারা অসৎ কাজ থেকে নিষেধ করত তাদেরকে আমি উদ্ধার করি, আর যালিমদেরকে তাদের অসৎ কর্মের কারণে কঠোর শাস্তি দ্বারা পাকড়াও করলাম।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1120,7,166,'অতঃপর যখন তারা বেপরোয়াভাবে নিষিদ্ধ কাজগুলি করতে থাকল তখন আমি বললামঃ তোমরা ঘৃণিত ও লাঞ্ছিত বানর হয়ে যাও।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1121,7,167,'তোমার রাব্ব ঘোষণা করলেন যে, তিনি তাদের (ইয়াহুদীদের) উপর কিয়ামাত পর্যন্ত এমন সব লোককে শক্তিশালী করে প্রেরণ করতে থাকবেন যারা তাদেরকে কঠিনতর শাস্তি দিতে থাকবে। নিঃসন্দেহে তোমার রাব্ব শাস্তি দানে ক্ষিপ্র হস্ত, আর নিশ্চয়ই তিনি ক্ষমাশীল ও অনুগ্রহশীল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1122,7,168,'আমি তাদেরকে বিভিন্ন দলে দুনিয়ায় বিস্তৃত করেছি, তাদের কতক লোক সদাচারী, আর কিছু লোক ভিন্নতর। আর আমি ভাল ও মন্দের মধ্যে নিপতিত করে তাদেরকে পরীক্ষা করে থাকি যাতে তারা আমার পথে ফিরে আসে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1123,7,169,'অতঃপর তাদের অযোগ্য উত্তরসুরীরা একের পর এক তাদের স্থলাভিষিক্ত হয় এবং তারা কিতাবেরও উত্তরাধিকারী হয়। কিন্তু তারা এই নিকৃষ্ট দুনিয়ার স্বার্থাবলী করায়ত্ত্ব করে আর বলেঃ আমাদেরকে ক্ষমা করা হবে। বস্তুতঃ ওর অনুরূপ সামগ্রী আবার তাদের নিকট এলে ওটাও তারা গ্রহণ করে। তাদের নিকট হতে কি কিতাবের প্রতিশ্রুতি নেয়া হয়নি যে, আল্লাহর নামে সত্য ছাড়া কিছুই বলবেনা? আর কিতাবে যা রয়েছে তাতো তারা অধ্যয়নও করে। মুত্তাকী ও আল্লাহভীরু লোকদের জন্য পরকালের সামগ্রী, তোমরা কি এতটুকু কথাও অনুধাবণ করতে পারনা?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1124,7,170,'যারা আল্লাহর কিতাবকে দৃঢ়ভাবে ধারণ করে এবং সালাত কায়েম করে; আমিতো সৎ কর্মশীলদের কর্মফল নষ্ট করিনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1125,7,171,'যখন আমি বানী ইসরাঈলের উপর পাহাড়কে স্থাপন করি, ওটা ছিল কোন একটি ছায়ার ন্যায়, তারা তখন মনে করেছিল যে, ওটা তাদের উপর পড়ে যাবে। তোমাদেরকে যা দিয়েছি তা দৃঢ়ভাবে শক্ত হাতে ধারণ কর এবং ওতে যা রয়েছে তা স্মরণ রেখ। আশা করা যায় যে, তোমরা তাকওয়ার অধিকারী হবে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1126,7,172,'যখন তোমার রাব্ব বানী আদমের পৃষ্ঠদেশ হতে তাদের বংশধরদেরকে বের করলেন এবং তাদেরকেই তাদের উপর সাক্ষী বানিয়ে জিজ্ঞেস করলেনঃ আমি কি তোমাদের রাব্ব (প্রভু) নই? তারা সমস্বরে উত্তর দিলঃ ‘হ্যাঁ! আমরা সাক্ষী থাকলাম।’ (এটা এ জন্য যে) যাতে তোমরা কিয়ামাত দিবসে বলতে না পার, ‘‘আমরা এ বিষয়ে সম্পূর্ণ অনবহিত ছিলাম।’’');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1127,7,173,'অথবা তোমরা যেন কিয়ামাত দিবসে এ কথা বলতে না পার - আমাদের পূর্ব-পুরুষরাইতো আমাদের পূর্বে শির্‌ক করেছিল, আমরা ছিলাম (শুধুমাত্র) তাদের পরবর্তী বংশধর। সুতরাং আপনি কি আমাদেরকে সেই ভ্রান্ত ও বাতিলদের কৃতকর্মের দরুণ ধ্বংস করবেন?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1128,7,174,'এভাবেই আমি নিদর্শনাবলী বিশদভাবে বিবৃত করি যাতে তারা ফিরে আসে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1129,7,175,'তুমি এদেরকে সেই ব্যক্তির বৃত্তান্ত শুনিয়ে দাও, যাকে আমি নিদর্শন দান করেছিলাম, কিন্তু সে উহা বর্জন করে। ফলে শাইতান তার পিছনে লেগে যায়, আর সে পথভ্রষ্টদের মধ্যে শামিল হয়ে যায়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1130,7,176,'আর আমি ইচ্ছা করলে তাকে এই আয়াতসমূহের সাহায্যে উন্নত করতাম, কিন্তু সে দুনিয়ার প্রতি অধিক ঝুঁকে পড়ে এবং স্বীয় কামনা বাসনার (প্রবৃত্তির) অনুসরণ করতে থাকে। তার উদাহরণ একটি কুকুরের ন্যায়, ওকে যদি তুমি কষ্ট দাও তাহলে জিহবা বের করে হাঁপায়, আবার কষ্ট না দিলেও জিহবা বের করে হাঁপাতে থাকে। যারা আমার আয়াতসমূহকে মিথ্যা প্রতিপন্ন করে, এই উদাহরণ হল সেই সম্প্রদায়ের জন্য। তুমি কাহিনী বর্ণনা করে শোনাতে থাক, হয়তো তারা এটা নিয়ে চিন্তা-ভাবনা করবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1131,7,177,'কতই না মন্দ উদাহরণ সেই সম্প্রদায়ের জন্য যারা আমার আয়াতসমূহকে মিথ্যা প্রতিপন্ন করে এবং তারা নিজেরাই নিজেদের উপর অত্যাচার করতে থাকে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1132,7,178,'আল্লাহ যাকে পথ দেখান সে’ই পথ প্রাপ্ত হয়, আর যাকে তিনি পথ প্রদর্শন হতে বঞ্চিত করেন সে ব্যর্থ ও ক্ষতিগ্রস্ত হয়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1133,7,179,'আমি বহু জিন ও মানুষকে জাহান্নামের জন্য সৃষ্টি করেছি। তাদের হৃদয় রয়েছে, কিন্তু তারা তদ্বারা উপলব্ধি করেনা; তাদের চক্ষু রয়েছে, কিন্তু তারা তদ্বারা দেখেনা। তাদের কর্ণ রয়েছে, কিন্তু তদ্বারা তারা শোনেনা। তারাই হল পশুর ন্যায়, বরং তা অপেক্ষাও অধিক বিভ্রান্ত। তারাই হল গাফিল বা উদাসীন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1134,7,180,'আর আল্লাহর জন্য সুন্দর সুন্দর নাম রয়েছে, সুতরাং তোমরা তাঁকে সেই সব নামেই ডাকবে, আর তাদেরকে বর্জন কর যারা তাঁর নাম বিকৃত করে, সত্ত্বরই তাদেরকে তাদের কৃতকর্মের প্রতিফল দেয়া হবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1135,7,181,'আর আমি যাদেরকে সৃষ্টি করেছি তাদের মধ্যে এমন একটি দলও রয়েছে যারা সত্য পথের দা‘ওয়াত দেয় এবং ন্যায় বিচার করে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1136,7,182,'যারা আমার আয়াতসমূহকে মিথ্যা প্রতিপন্ন করে, আমি তাদের অজ্ঞাতে তাদেরকে ধীরে ধীরে ধ্বংসের পথে নিয়ে যাব।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1137,7,183,'আমি তাদেরকে অবকাশ দিচ্ছি, নিশ্চয়ই আমার কৌশল অতি শক্ত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1138,7,184,'তারা কি এটা চিন্তা করেনা যে, তাদের সঙ্গী পাগল নয়? সে নিছক একজন সুস্পষ্ট ভয় প্রদর্শনকারী!');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1139,7,185,'তারা কি আল্লাহর সৃষ্টি আকাশসমূহ ও পৃথিবীর ব্যবস্থাপনা সম্পর্কে কোন গভীর চিন্তা করেনা? এবং তাদের জীবনে নির্দিষ্ট মেয়াদটি পূর্ণ হওয়ার সময়টি হয়তো বা নিকটে এসে পড়েছে, তারা কি এটাও চিন্তা করেনা? এরপর তারা আর কোন কথায় ঈমান আনবে?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1140,7,186,'যাদেরকে আল্লাহ বিপথগামী করেন, তাদের কোন পথ প্রদর্শক নেই, আর আল্লাহ তাদেরকে তাদেরই বিভ্রান্তির মধ্যে উদভ্রান্তের ন্যায় ঘুরে বেড়াতে ছেড়ে দেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1141,7,187,'তারা তোমাকে জিজ্ঞেস করছে, কিয়ামাত কখন সংঘটিত হবে? তুমি বলে দাওঃ এ বিষয়ে আমার রাব্বই একমাত্র জ্ঞানের অধিকারী, শুধু তিনিই ওটা ওর নির্ধারিত সময়ে প্রকাশ করবেন, তা হবে আকাশসমূহ ও পৃথিবীতে এক ভয়ংকর ঘটনা। তোমাদের উপর ওটা আকস্মিকভাবেই আসবে। তুমি যেন এ বিষয় সবিশেষ অবগত, এটা ভেবে তারা তোমাকে এ সম্পর্কে জিজ্ঞেস করছে। তুমি বলে দাওঃ এ সম্পর্কীয় জ্ঞান একমাত্র আমার রবেরই রয়েছে। কিন্তু অধিকাংশ লোকই এ কথা বুঝেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1142,7,188,'তুমি বলঃ আল্লাহ যা ইচ্ছা করেন তা ছাড়া আমার নিজের ভাল-মন্দ বিষয়ে আমার কোন অধিকার নেই। আমি যদি অদৃশ্য তত্ত্ব ও খবর জানতাম তাহলে আমি রবের কল্যাণ লাভ করতে পারতাম, আর কোন অকল্যাণই আমাকে স্পর্শ করতে পারতনা, আমিতো শুধু মু’মিন সম্প্রদায়ের জন্য একজন ভয় প্রদর্শনকারী ও সুসংবাদবাহী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1143,7,189,'তিনিই আল্লাহ যিনি তোমাদেরকে এক ব্যক্তি হতে সৃষ্টি করেছেন এবং সেই ব্যক্তি হতেই তার সঙ্গিনী সৃষ্টি করেছেন, যেন সে তার নিকট থেকে প্রশান্তি লাভ করতে পারে। অতঃপর যখন সে তার সাথে মিলনে প্রবৃত্ত হয় তখন সেই মহিলাটি এক গোপন ও লঘু গর্ভ ধারণ করে, আর ওটা নিয়ে চলাফেরা করতে থাকে। যখন তার গর্ভ গুরুভার হয় তখন তারা উভয়েই তাদের রবের কাছে প্রার্থনা করেঃ আপনি যদি আমাদেরকে সৎ সন্তান দান করেন তাহলে আমরা আপনার কৃতজ্ঞ বান্দা হব।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1144,7,190,'অতঃপর তিনি যখন তাদেরকে সৎ ও সুস্থ সন্তান দান করেন তখন তারা আল্লাহর দেয়া এই দানে অংশী স্থাপন করে, কিন্তু তারা যাকে অংশী করে আল্লাহ তার অনেক উর্ধ্বে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1145,7,191,'তারা কি এমন বস্তুকে (আল্লাহর সাথে) অংশী করে যারা কোন বস্তুই সৃষ্টি করেনা, বরং তারা নিজেরাই (আল্লাহর দ্বারা) সৃষ্ট?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1146,7,192,'তারা যেমন তাদের কোন সাহায্য করার ক্ষমতা রাখেনা, তেমনি নিজেরাও কোন সাহায্য করতে পারেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1147,7,193,'তোমরা যদি ওদেরকে সৎ পথে ডাক তাহলে তারা তোমাদের অনুসরণ করবেনা, তাদেরকে ডাকা অথবা চুপ করে থাকা উভয়ই তোমাদের পক্ষে সমান।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1148,7,194,'আল্লাহ ছাড়া তোমরা যাদেরকেই ডাক, তারাতো তোমাদেরই মত বান্দা। সুতরাং যদি তোমরা সত্যবাদী হও তাহলে তাদেরকে উপাস্য হিসাবে ডাকতে থাক, দেখ তোমাদের ডাকে সাড়া দেয় কি না!');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1149,7,195,'তাদের কি পা আছে যা দ্বারা চলছে? তাদের কি হাত আছে যদ্বারা কোন কিছু ধরে থাকে অথবা চক্ষু আছে যা দ্বারা দেখতে পায়? তাদের কি কর্ণ আছে যা দ্বারা শুনে থাকে? তুমি বলঃ আল্লাহর সাথে তোমরা যাদেরকে অংশী করছ তাদেরকে ডাক, তারপর (সকলে একত্রিত হয়ে) আমার বিরুদ্ধে চক্রান্ত করতে থাক, আমাকে আদৌ কোন অবকাশ দিওনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1150,7,196,'আমার অভিভাবক হলেন সেই আল্লাহ যিনি কিতাব অবতীর্ণ করেছেন, আর তিনিই সৎ কর্মশীলদের অভিভাবকত্ব করে থাকেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1151,7,197,'আল্লাহ ছাড়া তোমরা যাদেরকে ডাক তারা তোমাদের সাহায্য করার কোন ক্ষমতা রাখেনা এবং নিজেদেরকেও সাহায্য করতে পারেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1152,7,198,'যদি তুমি তাদেরকে (মূর্তি) হিদায়াতের পথে ডাক তাহলে সে ডাক তারা শুনবেনা। আর তুমি দেখবে যে, তারা তোমার দিকে তাকিয়ে রয়েছে, আসলে তারা কিছুই দেখছেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1153,7,199,'তুমি বিনয় ও ক্ষমা পরায়ণতার নীতি গ্রহণ কর, এবং লোকদেরকে সৎ কাজের নির্দেশ দাও, আর মূর্খদেরকে এড়িয়ে চল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1154,7,200,'শাইতানের কু-মন্ত্রণা যদি তোমাকে প্ররোচিত করে তাহলে তুমি আল্লাহর আশ্রয় প্রার্থনা কর, তিনি সর্ব শ্রোতা ও সর্বজ্ঞ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1155,7,201,'যারা আল্লাহভীরু, শাইতান যখন তাদেরকে কু-মন্ত্রণা দেয়, সাথে সাথে তারা আত্মসচেতন হয়ে আল্লাহকে স্মরণ করে এবং তাদের জ্ঞান চক্ষু ফিরে পায়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1156,7,202,'শাইতান যাদের অনুগত সাথী, তারা আরও বিভ্রান্তি ও গুমরাহীর মধ্যে প্রবেশ করে, এ প্রচেষ্টায় তারা আদৌ থেমে থাকেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1157,7,203,'তুমি যখন কোন নিদর্শন ও মু’জিযা তাদের কাছে পেশ করনা, তখন তারা বলেঃ আপনি এ সব মু’জিযা কেন পেশ করেননা? তুমি বলঃ আমার রবের নিকট থেকে আমার কাছে যা কিছু প্রত্যাদেশ পাঠানো হয়, আমি শুধুমাত্র তারই অনুসরণ করি। এই কুরআন তোমার রবের নিকট থেকে বিশেষ নিদর্শন, আর এটা ঈমানদার সম্প্রদায়ের জন্য হিদায়াত ও রাহমাত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1158,7,204,'যখন কুরআন পাঠ করা হয় তখন তোমরা মনোযোগের সাথে তা শ্রবণ করবে এবং নীরব নিশ্চুপ হয়ে থাকবে, হয়তো তোমাদের প্রতি দয়া ও অনুগ্রহ প্রদর্শন করা হবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1159,7,205,'তোমার রাব্বকে মনে মনে সবিনয় ও সশংক চিত্তে অনুচ্চস্বরে প্রত্যুষে ও সন্ধ্যায় স্মরণ করবে, আর (হে নাবী!) তুমি এ ব্যাপারে গাফিল ও উদাসীন হয়ো না ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1160,7,206,'যারা (অর্থাৎ ফেরেশতা) তোমাদের রবের সান্নিধ্যে থাকে তারা তাঁরই গুণগান ও মহিমা প্রকাশ করে এবং তাঁরই সম্মুখে সাজদাহবনত হয়। [সাজদাহ]');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1161,8,1,'হে নাবী! লোকেরা তোমাকে যুদ্ধলব্ধ সম্পদ সম্পর্কে জিজ্ঞেস করছে। তুমি বলঃ যুদ্ধলব্ধ সম্পদ আল্লাহ ও তাঁর রাসূলের জন্য। অতএব তোমরা এ ব্যাপারে আল্লাহকে ভয় কর এবং তোমাদের নিজেদের পারস্পরিক সম্পর্ক সঠিক রূপে গড়ে নাও, আর যদি তোমরা মু’মিন হয়ে থাক তাহলে আল্লাহ এবং তাঁর রাসূলের আনুগত্য কর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1162,8,2,'নিশ্চয়ই মু’মিনরা এরূপই হয় যে, যখন (তাদের সামনে) আল্লাহর নাম উচ্চারণ করা হয় তখন তাদের অন্তরসমূহ ভীত হয়ে পড়ে, আর যখন তাদের সামনে তাঁর আয়াতসমূহ পাঠ করা হয় তখন তাদের ঈমান আরও বৃদ্ধি পায়, আর তারা নিজেদের রবের উপর নির্ভর করে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1163,8,3,'যারা সালাত সুপ্রতিষ্ঠিত করে এবং আমি যা কিছু তাদেরকে দিয়েছি তা থেকে তারা খরচ করে,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1164,8,4,'এরাই সত্যিকারের ঈমানদার, এদের জন্য রয়েছে তাদের রবের সন্নিধানে উচ্চ পদসমূহ, আরও রয়েছে ক্ষমা ও সম্মানজনক জীবিকা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1165,8,5,'যেরূপ তোমার রাব্ব তোমাকে তোমার গৃহ হতে (বদরের দিকে) বের করলেন, আর মুসলিমদের একটি দল তা পছন্দ করেনি ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1166,8,6,'সেই যথার্থ বিষয় প্রকাশ হওয়ার পরও ওতে তারা তোমার সাথে এরূপ বিবাদ করছিল যেন কেহ তাদেরকে মৃত্যুর দিকে হাঁকিয়ে নিয়ে যাচ্ছে, আর তারা তা প্রত্যক্ষ করছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1167,8,7,'স্মরণ কর, যখন আল্লাহ তোমাদেরকে দু’টি দলের মধ্য হতে একটি সম্বন্ধে প্রতিশ্রুতি দিয়েছিলেন যে, ওটা তোমাদের করতলগত হবে। তোমরা এই আশা করেছিলে যেন নিরস্ত্র দলটি তোমাদের আয়ত্তে এসে পড়ে, আর আল্লাহর ইচ্ছা ছিল এই যে, তিনি স্বীয় নির্দেশাবলী দ্বারা সত্যকে সত্যরূপে প্রতিষ্ঠা করেন এবং কাফিরদেরকে নির্মূল করেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1168,8,8,'ইহা এ জন্য যাতে সত্য সত্যরূপে এবং অসত্য অসত্যরূপে প্রতিভাত হয়ে যায়, যদিও এটা অপরাধীরা অপ্রীতিকরই মনে করে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1169,8,9,'স্মরণ কর সেই সংকট মুহুর্তের কথা, যখন তোমরা তোমাদের রবের নিকট কাতর কন্ঠে প্রার্থনা করেছিলে, আর তিনি সেই প্রার্থনা কবূল করে বলেছিলেনঃ নিশ্চয়ই আমি তোমাদেরকে এক হাজার মালাইকা/ফেরেশতা দ্বারা সাহায্য করব, যারা একের পর এক আসবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1170,8,10,'আল্লাহ এটা করেছিলেন শুভ সংবাদ হিসাবে এবং যাতে তোমাদের চিত্ত আশ্বস্ত হয়। বস্তুতঃ সাহায্যতো শুধু আল্লাহর তরফ থেকেই হয়ে থাকে। আল্লাহ মহা পরাক্রমশালী, প্রজ্ঞাময়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1171,8,11,'যখন তিনি (আল্লাহ) তাঁর পক্ষ থেকে প্রশান্তির জন্য তোমাদেরকে তন্দ্রায় আচ্ছন্ন করেন, আকাশ হতে বারি বর্ষণ করেন, (উদ্দেশ্য ছিল) তোমাদেরকে এর দ্বারা পবিত্র করবেন এবং তোমাদের হতে শাইতানের কু-মন্ত্রনা দূর করবেন, আর তোমাদের হৃদয়কে সুদৃঢ় করবেন এবং তোমাদের পা স্থির ও প্রতিষ্ঠিত রাখবেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1172,8,12,'স্মরণ কর, যখন তোমার রাব্ব মালাক/ফেরেশতার নিকট প্রত্যাদেশ করলেনঃ আমি তোমাদের সাথে আছি। সুতরাং তোমরা ঈমানদারদের সুপ্রতিষ্ঠিত ও অবিচল রাখ, আর যারা কাফির, আমি তাদের হৃদয়ে ভীতি সৃষ্টি করে দিব। অতএব তোমরা তাদের স্কন্ধে আঘাত হান, আর আঘাত হান তাদের অঙ্গুলিসমূহের প্রতিটি জোড়ায়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1173,8,13,'এটা এ কারণে যে, তারা আল্লাহ ও তাঁর রাসূলের বিরোধিতা করে। যে ব্যক্তি আল্লাহ ও তাঁর রাসূলের বিরোধিতা করে তার জেনে রাখা উচিত যে, আল্লাহ শাস্তি দানে খুবই কঠোর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1174,8,14,'সুতরাং তোমরা এর স্বাদ গ্রহণ কর, সত্য অস্বীকারকারীদের জন্য রয়েছে জাহান্নামের লেলিহান আগুনের শাস্তি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1175,8,15,'হে ঈমানদারগণ! তোমরা যখন কাফির বাহিনীর সম্মুখীন হবে তখন তাদের মুকাবিলা করা হতে কখনোই পৃষ্ঠ প্রদর্শন করবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1176,8,16,'আর সেদিন যুদ্ধ কৌশল বা স্বীয় বাহিনীর কেন্দ্রস্থলে স্থান নেয়া ব্যতীত কেহ তাদের থেকে পৃষ্ঠ প্রদর্শন করলে অর্থাৎ পালিয়ে গেলে সে আল্লাহর গযবে পরিবেষ্টিত হবে, তার আশ্রয়স্থল হবে জাহান্নাম, আর জাহান্নাম কতই না নিকৃষ্ট স্থান।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1177,8,17,'তোমরা তাদেরকে হত্যা করনি, বরং আল্লাহই তাদেরকে হত্যা করেছেন। আর (হে নাবী!) যখন তুমি (ধূলাবালি) নিক্ষেপ করেছিলে তখন তা মূলতঃ তুমি নিক্ষেপ করনি, বরং আল্লাহই তা নিক্ষেপ করেছিলেন। এটা করা হয়েছিল মু’মিনদেরকে উত্তম পুরস্কার দান করার জন্য, নিঃসন্দেহে আল্লাহ সব কিছু শোনেন ও জানেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1178,8,18,'আর এমনিভাবেই আল্লাহ কাফিরদের চক্রান্ত দুর্বল ও নস্যাৎ করে থাকেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1179,8,19,'(হে কাফিরেরা!) তোমরাতো ফাইসালা চাচ্ছ, ফাইসালাতো তোমাদের সামনেই এসে গেছে। তোমরা যদি এখনও (মুসলিমদের অনিষ্ট করা হতে) বিরত থাক তাহলে তা তোমাদের পক্ষেই কল্যাণকর, আর যদি পুনরায় তোমরা এ কাজ কর তাহলে আমিও তোমাদেরকে পুনরায় শাস্তি দিব, আর তোমাদের বিরাট বাহিনী তোমাদের কোনই উপকারে আসবেনা। নিঃসন্দেহে আল্লাহ মু’মিনদের সাথে রয়েছেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1180,8,20,'হে মু’মিনগণ! আল্লাহ ও তাঁর রাসূলের আনুগত্য কর, তোমরা যখন তার কথা শুনছ তখন তোমরা তার আনুগত্য হতে মুখ ফিরিয়ে নিওনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1181,8,21,'তোমরা ঐ সব লোকের মত হয়োনা, যারা বলে - আমরা আপনাদের কথা শুনলাম, কার্যতঃ তারা কিছুই শোনেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1182,8,22,'আল্লাহর কাছে নিকৃষ্টতম জীব হচ্ছে ঐ সব মূক ও বধির লোক, যারা কিছুই বুঝেনা (অর্থাৎ বিবেক বুদ্ধিকে কাজে লাগায়না)।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1183,8,23,'আল্লাহ যদি জানতেন যে, তাদের মধ্যে কল্যাণকর কিছু নিহিত রয়েছে তাহলে অবশ্যই তিনি তাদেরকে শোনার তাওফীক দিতেন, তিনি যদি তাদেরকে শোনাতেন তাহলেও তারা উপেক্ষা করে মুখ ফিরিয়ে অন্য দিকে চলে যেত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1184,8,24,'হে মু’মিনগণ! তোমরা আল্লাহ ও তাঁর রাসূলের ডাকে সাড়া দাও যখন তিনি তোমাদেরকে তোমাদের জীবন সঞ্চারক বস্তুর দিকে আহবান করেন। আর জেনে রেখ, আল্লাহ মানুষ ও তার অন্তরের অন্তরালে থাকেন, পরিশেষে তাঁর কাছেই তোমাদেরকে সমবেত করা হবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1185,8,25,'তোমরা সেই ফিতনাকে ভয় কর যা তোমাদের মধ্যকার শুধুমাত্র যালিম ও পাপিষ্ঠদেরকেই বিশেষভাবে ক্লিষ্ট করবেনা। তোমরা জেনে রেখ, নিশ্চয়ই আল্লাহ শাস্তি দানে খুবই কঠোর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1186,8,26,'স্মরণ কর, যখন তোমরা ভূ-পৃষ্ঠে খুব দুর্বলরূপে পরিগণিত হতে, আর তোমরা এই শংকায় নিপতিত থাকতে যে, লোকেরা অকস্মাৎ তোমাদেরকে ধরে নিয়ে যাবে। (এই অবস্থায়) আল্লাহই তোমাদেরকে (মাদীনায়) আশ্রয় দেন এবং স্বীয় সাহায্য দ্বারা তোমাদেরকে শক্তিশালী করেন, আর পবিত্র বস্তু দ্বারা তোমাদের জীবিকা দান করেন যেন তোমরা কৃতজ্ঞতা প্রকাশ কর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1187,8,27,'হে মু’মিনগণ! তোমরা জেনে শুনে আল্লাহ ও তাঁর রাসূলের সাথে বিশ্বাসঘাতকতা করনা এবং তোমাদের পরস্পরের আমানত সম্পর্কেও বিশ্বাস ভঙ্গ করনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1188,8,28,'আর তোমরা জেনে রেখ যে, তোমাদের ধন সম্পদ ও সন্তান সন্ততি প্রকৃত পক্ষে পরীক্ষার সামগ্রী মাত্র, আর আল্লাহর নিকট মহাপুরস্কার রয়েছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1189,8,29,'হে মু’মিনগণ! তোমরা যদি আল্লাহকে ভয় কর তাহলে তিনি তোমাদেরকে ন্যায়-অন্যায় পার্থক্য করার একটি মান নির্ণয়ক শক্তি দান করবেন, আর তোমাদের দোষক্রটি তোমাদের হতে দূর করবেন এবং তোমাদেরকে ক্ষমা করবেন, আল্লাহ বড়ই অনুগ্রহশীল ও মঙ্গলময়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1190,8,30,'আর স্মরণ কর, যখন কাফিরেরা তোমার বিরুদ্ধে ষড়যন্ত্র করেছিল, তোমাকে বন্দী করবে অথবা হত্যা করবে কিংবা নির্বাসিত করবে। তারাও ষড়যন্ত্র করতে থাকুক এবং আল্লাহও (স্বীয় নাবীকে বাঁচানোর) কৌশল করতে থাকেন, আল্লাহ হচ্ছেন শ্রেষ্ঠ কৌশলী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1191,8,31,'তাদেরকে যখন আমার আয়াতসমূহ পাঠ করে শোনানো হয় তখন তারা বলেঃ আমরা শুনেছি, ইচ্ছা করলে আমরাও এর অনুরূপ বলতে পারি, নিঃসন্দেহে এটা পুরাকালের উপাখ্যান ছাড়া কিছু নয়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1192,8,32,'আর স্মরণ কর, যখন তারা বলেছিলঃ হে আল্লাহ! ইহা (কুরআন) যদি আপনার পক্ষ হতে সত্য হয় তাহলে আকাশ থেকে আমাদের উপর প্রস্তর বর্ষণ করুন অথবা আমাদের উপর কঠিন পীড়াদায়ক শাস্তি এনে দিন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1193,8,33,'(হে নাবী!) তুমি তাদের মধ্যে থাকা অবস্থায় তাদেরকে শাস্তি দেয়া আল্লাহর অভিপ্রায় নয়, আর আল্লাহ এটাও চাননা যে, তারা ক্ষমা প্রার্থনা করতে থাকবে অথচ তিনি তাদেরকে শাস্তি দিবেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1194,8,34,'কিন্তু তাদের কি বলার আছে যে জন্য আল্লাহ তাদের শাস্তি দিবেননা, যখন তারা মাসজিদুল হারামের পথ রোধ করছে, অথচ তারা মাসজিদুল হারামের তত্ত্বাবধায়ক নয়? আল্লাহভীরু লোকেরাই উহার তত্ত্বাবধায়ক, কিন্তু তাদের অধিকাংশ লোক এটা অবগত নয়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1195,8,35,'কা’বা ঘরের কাছে তাদের সালাত হল শিস দেয়া ও করতালি প্রদান ছাড়া অন্য কিছুই নয়, সুতরাং তোমরা কুফরী করার কারণে এখন শাস্তির স্বাদ গ্রহণ কর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1196,8,36,'নিশ্চয়ই কাফিরেরা মানুষকে আল্লাহর পথ থেকে নিবৃত্ত করার উদ্দেশে তাদের ধন সম্পদ ব্যয় করে, তারা তাদের ধন-সম্পদ ব্যয় করতেই থাকবে, অতঃপর ওটাই শেষ পর্যন্ত তাদের জন্য দুঃখ ও আফসোসের কারণ হবে এবং তারা পরাভূতও হবে। আর যারা কুফরী করে তাদেরকে জাহান্নামে একত্রিত করা হবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1197,8,37,'এটা এ কারণে যে, আল্লাহ ভাল থেকে মন্দকে পৃথক করবেন, আর কু-জনদের সকলকে একজনের উপর অপর জনকে স্তপীকৃত করবেন এবং অতঃপর জাহান্নামে নিক্ষেপ করবেন। এরাই চরম ক্ষতিগ্রস্ত লোক।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1198,8,38,'তুমি কাফিরদেরকে বলঃ তারা যদি অনাচার থেকে বিরত থাকে তাহলে তাদের পূর্বের অপরাধ যা হয়েছে তা আল্লাহ ক্ষমা করবেন। কিন্তু তারা যদি অন্যায়ের পুনরাবৃত্তি করে তাহলে পূর্ববর্তীদের দৃষ্টান্ততো রয়েছেই।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1199,8,39,'তোমরা সদা তাদের বিরুদ্ধে লড়াই করতে থাকবে যতক্ষণ না ফিতনার অবসান হয় এবং দীন সম্পূর্ণ রূপে আল্লাহর জন্য হয়ে যায়। আর তারা যদি ফিতনা ও বিপর্যয় সৃষ্টি হতে বিরত থাকে তাহলে তারা কি করেছে তা আল্লাহই দেখবেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1200,8,40,'আর যদি তোমাকে না’ই মানে এবং দীন থেকে মুখ ফিরিয়ে নেয় তাহলে জেনে রেখ যে, আল্লাহই তোমাদের (মুসলিমদের) অভিভাবক। তিনি কতইনা উত্তম অভিভাবক ও সাহায্যকারী!');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1201,8,41,'আর তোমরা জেনে রেখ যে, যুদ্ধে তোমরা যা কিছু গাণীমাতের মাল লাভ করেছ ওর এক পঞ্চমাংশ আল্লাহ, তাঁর রাসূল, (রাসূলের) নিকটাত্মীয়, ইয়াতীম, মিসকীন এবং মূসাফিরের জন্য - যদি তোমরা ঈমান এনে থাক আল্লাহর প্রতি এবং যা আমি অবতীর্ণ করেছি আমার বান্দার উপর সেই চুড়ান্ত ফাইসালার দিন, যেদিন দু‘দল পরস্পরের সম্মুখীন হয়েছিল। আর নিশ্চয়ই আল্লাহ সকল বিষয়ে সর্বশক্তিমান।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1202,8,42,'আর স্মরণ কর, যখন তোমরা প্রান্তরের এই দিকে ছিলে, আর তারা প্রান্তরের অপর দিকে শিবির রচনা করেছিল, আর উষ্ট্রারোহী কাফেলা তোমাদের অপেক্ষা নিম্নভূমিতে ছিল, যদি পূর্ব হতেই তোমাদের ও তাদের মধ্যে যুদ্ধ সম্পর্কে কোন সিদ্ধান্ত গ্রহণ করতে চাইতে তাহলে তোমাদের মধ্যে মতানৈক্য সৃষ্টি হত, কিন্তু যা ঘটানোর ছিল তা আল্লাহ সম্পন্ন করার জন্য উভয় দলকে যুদ্ধক্ষেত্রে সমবেত করলেন, তাতে যে ধ্বংস হবে সে যেন সত্য সুস্পষ্ট রূপে প্রতিভাত হবার পর ধ্বংস হয়, আর যে জীবিত থাকবে সে যেন সত্য সুস্পষ্ট রূপে প্রতিভাত হবার পর জীবিত থাকে। আল্লাহ সর্বশ্রোতা ও মহাজ্ঞানী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1203,8,43,'আর স্মরণ কর, যখন আল্লাহ তোমাকে স্বপ্নযোগে ওদের সংখ্যা অল্প দেখিয়েছিলেন, যদি তোমাকে তাদের সংখ্যা অধিক দেখাতেন তাহলে তোমরা সাহস হারিয়ে ফেলতে এবং যুদ্ধ সম্পর্কে তোমাদের মধ্যে মতানৈক্য সৃষ্টি হত, কিন্তু আল্লাহ তোমাদেরকে রক্ষা করেছেন। অন্তরে যা কিছু আছে সে সম্পর্কে তিনি সবিশেষ অবহিত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1204,8,44,'আরও স্মরণ কর, যা ঘটানোর ছিল, চূড়ান্তভাবে সম্পন্ন করার জন্য যখন দু’দল মুখোমুখী দন্ডায়মান হয়েছিল তখন তোমাদের দৃষ্টিতে ওদের সংখ্যা খুব অল্প দেখাচ্ছিল, আর ওদের চোখেও তোমাদেরকে খুব স্বল্প সংখ্যক পরিদৃষ্ট হচ্ছিল, সমস্ত বিষয় ও সমস্যাই আল্লাহর দিকে প্রত্যাবর্তন করে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1205,8,45,'হে মু’মিনগণ! তোমরা আল্লাহকে অধিক স্মরণ কর এবং অবিচল থাকবে যখন কোন দলের সম্মুখীন হও, আশা করা যায় তোমরা সফলকাম হবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1206,8,46,'তোমরা আল্লাহ ও তাঁর রাসূলের অনুগত হও। তোমরা সাহস ও ক্ষমতাহারা হয়ে যাবে যদি নিজেদের মধ্যে বিবাদ কর। তোমরা ধৈর্য ধারণ কর। আল্লাহ ধৈর্যশীলদের সাথে রয়েছেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1207,8,47,'তোমরা তাদের মতো আচরণ করনা যারা নিজেদের গৃহ হতে সদর্পে এবং লোকদেরকে (নিজেদের শক্তি) প্রদর্শন করে বের হয় ও মানুষকে আল্লাহর পথ হতে নিবৃত্ত রাখে, তারা যা করে আল্লাহ তা পরিবেষ্টন করে রয়েছেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1208,8,48,'স্মরণ কর, যখন শাইতান তাদের কার্যাবলীকে তাদের দৃষ্টিতে খুব চাকচিক্যময় ও শোভনীয় করে দেখাচ্ছিল, সে গর্বভরে বলেছিলঃ কোন মানুষই আজ তোমাদের উপর বিজয় লাভ করতে পারবেনা, আমি সাহায্যার্থে তোমাদের নিকটই থাকব। কিন্তু উভয় বাহিনীর মধ্যে যখন প্রত্যক্ষ যুদ্ধ শুরু হল তখন সে পৃষ্ঠ প্রদর্শন করে সরে পড়ল এবং বললঃ আমি তোমাদের বিষয়ে দায়িত্ব মুক্ত, আমি যা দেখেছি তোমরা তা দেখনা, আমি আল্লাহকে ভয় করি, আর আল্লাহ শাস্তি দানে খুবই কঠোর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1209,8,49,'যারা মুনাফিক, অন্তরে যাদের ব্যাধি রয়েছে তারা বলে, তাদের ধর্ম তাদেরকে বিভ্রান্ত করেছে। যে কেহ আল্লাহর উপর বিশ্বাস স্থাপন করে, নিশ্চয়ই আল্লাহ মহা পরাক্রান্ত, প্রজ্ঞাময়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1210,8,50,'তুমি যদি ঐ অবস্থা দেখতে যখন মালাইকা/ফেরেশতারা কাফিরদের মুখমন্ডল ও পৃষ্ঠদেশে আঘাত হেনে তাদের মৃত্যু ঘটিয়েছে, (আর বলছে) তোমরা জাহান্নামের যন্ত্রণাদায়ক শাস্তির স্বাদ গ্রহণ কর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1211,8,51,'এই শাস্তি হল তোমাদের সেই কাজেরই পরিণাম ফল যা তোমাদের দু’হাত পূর্বাহ্নেই আয়োজন করেছিল, আল্লাহ তাঁর বান্দাদের উপর কখনও অত্যাচারী নন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1212,8,52,'এটা ফির‘আউনের বংশ ও তাদের পূর্ববর্তী লোকদের অবস্থার ন্যায়; তারা আল্লাহর নিদর্শনকে প্রত্যাখ্যান করেছিল। ফলে আল্লাহ তাদের পাপের কারণে তাদেরকে পাকড়াও করলেন, নিঃসন্দেহে আল্লাহ মহাশক্তিমান ও কঠিন শাস্তিদাতা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1213,8,53,' এই শাস্তির কারণ এই যে, আল্লাহ যদি কোন জাতির উপর নি‘আমাত দান করেন সেই নি‘আমাত ততক্ষণ পর্যন্ত পরিবর্তন করেননা, যতক্ষণ পর্যন্ত সেই জাতি নিজেদের অবস্থা পরিবর্তন না করে, নিঃসন্দেহে আল্লাহ মহাশ্রোতা ও মহাজ্ঞানী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1214,8,54,'ফির‘আউনের বংশধর ও তৎপূর্বের জাতিসমূহের ন্যায় তারা তাদের রবের নিদর্শনসমূহ মিথ্যা প্রতিপন্ন করেছে। ফলে আমি তাদের পাপের কারণে তাদেরকে ধ্বংস করেছি এবং ফির‘আউনের বংশধরকে (সমুদ্রে) নিমজ্জিত করেছি, তারা প্রত্যেকেই ছিল যুলমকারী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1215,8,55,'নিশ্চয়ই আল্লাহর নিকট নিকৃষ্ট জীব তারাই যারা কুফরী করে এবং যারা ঈমান আনেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1216,8,56,'ওদের মধ্যে যাদের সাথে তুমি চুক্তিবদ্ধ হয়েছ তারাও নিকৃষ্ট, তারা প্রতিবারেই কৃত চুক্তি ভঙ্গ করেছে, (চুক্তি ভঙ্গের ব্যাপারে আল্লাহকে কিছুমাত্র) তারা ভয় করেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1217,8,57,'অতএব তোমরা যদি তাদেরকে যুদ্ধের মাইদানে আয়ত্তে আনতে পার তাহলে তাদেরকে তাদের পিছনে যারা রয়েছে তাদের হতে বিচ্ছিন্ন করে এমনভাবে শায়েস্তা কর যাতে তারা শিক্ষা পায়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1218,8,58,'(হে নাবী!) তুমি যদি কোন সম্প্রদায়ের বিশ্বাস ভঙ্গের আশংকা কর তাহলে তোমার চুক্তিকেও প্রকাশ্যভাবে তাদের সামনে চুক্তি ভঙ্গের খবর দিয়ে দাও, নিশ্চয়ই আল্লাহ বিশ্বাস ভঙ্গকারীদেরকে পছন্দ করেননা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1219,8,59,'যারা কাফির তারা (বদর প্রান্তরে প্রাণ বাঁচাতে পেরে) যেন মনে না করে যে, তারা পরিত্রাণ পেয়েছে, তারা মু’মিনগণকে হতবল করতে পারবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1220,8,60,'তোমরা কাফিরদের মুকাবিলা করার জন্য যথাসাধ্য শক্তি ও সদাসজ্জিত অশ্ববাহিনী প্রস্তুত রাখবে যদ্দবারা আল্লাহর শক্র ও তোমাদের শক্রদেরকে ভীত সন্ত্রস্ত করবে, এছাড়া অন্যান্যদেরকেও যাদেরকে তোমরা জাননা, কিন্তু আল্লাহ জানেন। আর তোমরা আল্লাহর পথে যা কিছু ব্যয় কর, তার প্রতিদান তোমাদেরকে পুরোপুরি প্রদান করা হবে, তোমাদের প্রতি (কম দিয়ে) অত্যাচার করা হবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1221,8,61,'যদি তারা (কাফিরেরা) সন্ধির দিকে ঝুঁকে পড়ে তাহলে তুমিও সন্ধি করতে আগ্রহী হও, আর আল্লাহর উপর ভরসা কর, নিঃসন্দেহে তিনি সর্বশ্রোতা ও সর্বজ্ঞাত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1222,8,62,'আর তারা যদি তোমাকে প্রতারিত করার ইচ্ছা করে তাহলে তোমার জন্য আল্লাহই যথেষ্ট, তিনি এমন (মহাশক্তিশালী) যে, (গাইবি) সাহায্য (ফেরেশতা) দ্বারা এবং মু’মিনগণ দ্বারা তোমাকে শক্তিশালী করেছেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1223,8,63,'আর তিনি মু’মিনদের অন্তরে প্রীতি ও ঐক্য স্থাপন করেছেন, তুমি যদি পৃথিবীর সমুদয় সম্পদও ব্যয় করতে তবুও তাদের অন্তরে প্রীতি, সদ্ভাব ও ঐক্য স্থাপন করতে পারতেনা, কিন্তু আল্লাহই ওদের পরস্পরের মধ্যে প্রীতি ও সদ্ভাব স্থাপন করে দিয়েছেন, নিঃসন্দেহে তিনি মহাশক্তিমান ও মহাকৌশলী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1224,8,64,'হে নাবী! তোমার জন্য ও তোমার অনুসারী মু’মিনদের জন্য (সর্বক্ষেত্রে) আল্লাহই যথেষ্ট।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1225,8,65,'হে নাবী! মু’মিনদেরকে জিহাদের জন্য উদ্ধুদ্ধ কর, তোমাদের মধ্যে যদি বিশজন ধৈর্যশীল মুজাহিদ থাকে তাহলে তারা দু’শ জন কাফিরের উপর জয়যুক্ত হবে, আর তোমাদের মধ্যে এক‘শ জন থাকলে তারা এক হাজার কাফিরের উপর বিজয়ী হবে, কারণ তারা এমন এক সম্প্রদায় যাদের বোধশক্তি নেই, কিছুই বোঝেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1226,8,66,'আল্লাহ এক্ষণে তোমাদের গুরু দায়িত্ব লাঘব করে দিলেন, তোমাদের মধ্যে যে দৈহিক দুর্বলতা রয়েছে সে সম্পর্কে তিনি অবগত আছেন, এতদসত্ত্বেও তোমাদের মধ্যে একশ’ জন ধৈর্যশীল লোক থাকলে তারা দু’শ’ জন কাফিরের উপর জয়যুক্ত হবে, আর এক হাজার জন থাকলে তারা আল্লাহর হুকুমে দু’হাজার কাফিরের উপর বিজয় লাভ করবে, আল্লাহ ধৈর্যশীলদের সাথে রয়েছেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1227,8,67,'কোন নাবীর পক্ষে তখন পর্যন্ত বন্দী (জীবিত) রাখা শোভা পায়না, যতক্ষণ পর্যন্ত ভূ-পৃষ্ঠ (দেশ) হতে শক্র বাহিনী নির্মূল না হয়, তোমরা দুনিয়ার ক্ষণস্থায়ী সম্পদ কামনা করছ, অথচ আল্লাহ চান তোমাদের পরকালের কল্যাণ, আল্লাহ মহা পরাক্রমশালী, প্রজ্ঞাময়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1228,8,68,'আল্লাহর লিপি পূর্বেই লিখিত না হলে তোমরা যা কিছু গ্রহণ করেছ তজ্জন্য তোমাদের উপর কঠিন শাস্তি আপতিত হত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1229,8,69,'সুতরাং যুদ্ধে তোমরা যা কিছু গণীমাত রূপে লাভ করেছ তা হালাল ও পবিত্র রূপে ভোগ কর, আর আল্লাহকে ভয় কর, নিঃসন্দেহে আল্লাহ ক্ষমাশীল ও দয়ালু।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1230,8,70,'হে নাবী! তোমাদের হাতে যারা বন্দী হয়েছে তাদেরকে বল, আল্লাহ যদি তোমাদের অন্তরে কল্যাণকর কিছু রয়েছে বলে অবগত হন তাহলে তোমাদের হতে (মুক্তিপণ রূপে) যা কিছু নেয়া হয়েছে তা অপেক্ষা উত্তম কিছু দান করবেন এবং তোমাদেরকে ক্ষমা করে দিবেন, আল্লাহ ক্ষমাশীল ও দয়ালু।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1231,8,71,'আর তারা যদি তোমাদের সাথে বিশ্বাসঘাতকতা করার ইচ্ছা রাখে তাহলে এর পূর্বে আল্লাহর সাথেও তারা বিশ্বাসঘাতকতা করেছিল। সুতরাং তিনি তাদের উপর তোমাকে শক্তিশালী করেছেন; আল্লাহ সর্বজ্ঞ ও প্রজ্ঞাময়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1232,8,72,'যারা ঈমান এনেছে, দীনের জন্য হিজরাত করেছে, নিজেদের জানমাল দ্বারা আল্লাহর পথে জিহাদ করেছে এবং যারা আশ্রয় দান ও সাহায্য করেছে, তারা পরস্পরের বন্ধু। আর যারা ঈমান এনেছে কিন্তু হিজরাত করেনি, তারা হিজরাত না করা পর্যন্ত তাদের অভিভাবকত্বের কোন দায়িত্ব তোমাদের নেই, কিন্তু তারা যদি দীনের ব্যাপারে তোমাদের নিকট সাহায্য প্রার্থী হয় তাহলে তাদের সাহায্য করা তোমাদের কর্তব্য, তবে তোমাদের এবং যে জাতির মধ্যে চুক্তি রয়েছে তাদের বিরুদ্ধে নয়, তোমরা যা করছ আল্লাহ তা খুব ভাল রূপেই লক্ষ্য করেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1233,8,73,'যারা কুফরী করছে তারা পরস্পর পরস্পরের বন্ধু, তোমরা যদি (উপরোক্ত) বিধান কার্যকর না কর তাহলে ভূ-পৃষ্ঠে ফিতনা ও মহা বিপর্যয় দেখা দিবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1234,8,74,'যারা ঈমান এনেছে, (দীনের জন্য) হিজরাত করেছে এবং আল্লাহর পথে জিহাদ করেছে, আর যারা (মু’মিনদেরকে) আশ্রয় দিয়েছে এবং যাবতীয় সাহায্য সহানুভূতি প্রকাশ করেছে, তারাই হল প্রকৃত মু’মিন। তাদের জন্য রয়েছে ক্ষমা ও সম্মানজনক জীবিকা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1235,8,75,'আর যারা এর পরে ঈমান এনেছে ও হিজরাত করেছে এবং তোমাদের সাথে একত্রে জিহাদ করেছে, তারা তোমাদেরই অন্তর্ভুক্ত; আল্লাহর বিধানে আত্মীয়গণ একে অন্যের অপেক্ষা বেশি হকদার, নিঃসন্দেহে আল্লাহ প্রতিটি বস্তু সম্পর্কে ভাল রূপে অবহিত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1236,9,1,'আল্লাহ ও তাঁর রাসূলের পক্ষ হতে অব্যাহতি (ঘোষণা করা) হচ্ছে ঐ মুশরিকদের প্রতি যাদের সাথে তোমরা সন্ধি করেছিলে।*');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1237,9,2,'সুতরাং (হে মুশরিকরা!) তোমরা এই ভূ-মন্ডলে চার মাস বিচরণ করে নাও এবং জেনে রেখ যে, তোমরা আল্লাহকে অক্ষম করতে পারবেনা, আর নিশ্চয়ই আল্লাহ কাফিরদেরকে অপদস্থ করবেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1238,9,3,'আল্লাহ ও তাঁর রাসূলের পক্ষ থেকে বড় হাজ্জের তারিখসমূহে জনগণের সামনে ঘোষণা করা হচ্ছে যে, আল্লাহ ও তাঁর রাসূল উভয়ই এই মুশরিকদের (নিরাপত্তা প্রদান করা) হতে নিঃসম্পর্ক হচ্ছেন; তবে যদি তোমরা তাওবাহ কর তাহলে তা তোমাদের জন্য উত্তম, আর যদি তোমরা মুখ ফিরিয়ে নাও তাহলে জেনে রেখ যে, তোমরা আল্লাহকে অক্ষম করতে পারবেনা, আর (হে নাবী!) এই কাফিরদেরকে যন্ত্রণাদায়ক শাস্তির সুসংবাদ দাও।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1239,9,4,'কিন্তু হ্যাঁ ঐ সব মুশরিক হচ্ছে স্বতন্ত্র যাদের নিকট থেকে তোমরা চুক্তিতে আবদ্ধ হয়েছ, অতঃপর তারা তোমাদের বিরুদ্ধে কেহকেও সাহায্য করেনি। সুতরাং তাদের সন্ধি চুক্তিতে তাদের নির্ধারিত সময় পর্যন্ত পূর্ণ কর; নিশ্চয়ই আল্লাহ সৎকর্মশীলদের পছন্দ করেন ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1240,9,5,'অতঃপর যখন নিষিদ্ধ মাসগুলি অতীত হয়ে যায় তখন ঐ মুশরিকদেরকে যেখানে পাবে তাদের সাথে যুদ্ধ কর এবং হত্যা কর, তাদেরকে ধরে ফেল, তাদেরকে অবরোধ করে রাখো এবং তাদের সন্ধানে ঘাঁটিসমূহে অবস্থান কর। অতঃপর যদি তারা তাওবাহ করে, সালাত আদায় করে এবং যাকাত প্রদান করে তাহলে তাদের পথ ছেড়ে দাও। নিশ্চয়ই আল্লাহ অতিশয় ক্ষমাপরায়ণ, পরম করুণাময়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1241,9,6,'মুশরিকদের মধ্য হতে যদি কেহ তোমার কাছে আশ্রয় প্রার্থনা করে তাহলে তুমি তাকে আশ্রয় দান কর, যাতে সে আল্লাহর কালাম শুনতে পায়; অতঃপর তাকে তার নিরাপদ স্থানে পৌঁছে দাও, এই আদেশ এ জন্য যে, এরা এমন লোক যারা জ্ঞান রাখেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1242,9,7,'এই (কুরাইশ) মুশরিকদের অঙ্গীকার আল্লাহ ও তাঁর রাসূলের নিকট কি রূপে (বলবৎ) থাকবে যদি না তাদের সাথে তোমরা মাসজিদুল হারামের সন্নিকটে অঙ্গীকার নিয়ে থাক? অতএব যে পর্যন্ত তারা তোমাদের সাথে সরলভাবে থাকে, তোমরাও তাদের সাথে সরলভাবে থাকবে, নিঃসন্দেহে আল্লাহ সংযমশীলদের পছন্দ করেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1243,9,8,'কি করে চুক্তি রক্ষা হবে, যদি অবস্থা এই হয় যে, তারা যদি তোমাদের উপর প্রাধান্য লাভ করে তাহলে তোমাদের আত্মীয়তার মর্যাদাও রক্ষা করবেনা এবং অঙ্গীকারেরও না। তারা তোমাদেরকে নিজেদের মুখের কথায় সন্তুষ্ট রাখে, কিন্তু তাদের অন্তরসমূহ অস্বীকার করে, আর তাদের অধিকাংশ লোকই ফাসিক ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1244,9,9,'তারা আল্লাহর আয়াতসমূহকে নগণ্য মূল্যে বিক্রি করেছে এবং তারা আল্লাহর পথ থেকে (মু’মিনদেরকে) সরিয়ে রেখেছে। নিশ্চয়ই তাদের কাজ অতি মন্দ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1245,9,10,'তারা কোন মু’মিনের সাথে আত্মীয়তার মর্যাদা রক্ষা করেনা এবং না অঙ্গীকারের; আর তারাই সীমালংঘনকারী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1246,9,11,'অতঃপর যদি তারা তাওবাহ করে এবং সালাত আদায় করে ও যাকাত দেয় তাহলে তারা তোমাদের দীনী ভাই; আর আমি জ্ঞানী লোকদের জন্য বিধানাবলী বিস্তারিতভাবে বর্ণনা করে থাকি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1247,9,12,'আর যদি তারা অঙ্গীকার করার পর নিজেদের শপথগুলিকে ভঙ্গ করে এবং তোমাদের ধর্মের প্রতি দোষারোপ করে তাহলে তোমরা কুফরের অগ্রনায়কদের বিরুদ্ধে যুদ্ধ কর, (এই অবস্থায়) তাদের শপথ রইলনা, হয়তো তারা বিরত থাকবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1248,9,13,'তোমরা এমন লোকদের বিরুদ্ধে কেন যুদ্ধ করবেনা যারা নিজেদের শপথগুলিকে ভঙ্গ করেছে, আর রাসূলকে দেশান্তর করার সিদ্ধান্ত গ্রহণ করেছে এবং তারা তোমাদের বিরুদ্ধে নিজেরাই প্রথমে আক্রমণ করেছে? তোমরা কি তাদেরকে ভয় করছ? বস্তুতঃ আল্লাহকেই তোমাদের ভয় করা উচিত, যদি তোমরা মু’মিন হয়ে থাক।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1249,9,14,'তোমরা তাদের সাথে যুদ্ধ কর। আল্লাহ তোমাদের হাতে তাদেরকে শাস্তি প্রদান করবেন এবং তাদেরকে লাঞ্ছিত করবেন, আর তোমাদেরকে তাদের উপর বিজয়ী করবেন এবং মু’মিনের অন্তরসমূহকে প্রশান্ত ও ঠান্ডা করবেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1250,9,15,'আর তাদের অন্তরসমূহের ক্ষোভ দূর করে দিবেন এবং যার প্রতি ইচ্ছা, আল্লাহ করুণা প্রদর্শন করবেন, আল্লাহ মহাজ্ঞানী, প্রজ্ঞাময়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1251,9,16,'তোমরা কি ধারণা করেছ যে, তোমাদেরকে এভাবেই ছেড়ে দেয়া হবে? অথচ আল্লাহতো এখনও তোমাদেরকে পরীক্ষা করেননি যে, কারা তোমাদের মধ্য হতে জিহাদ করেছে এবং আল্লাহ, তাঁর রাসূল ও মু’মিনগণ ছাড়া অন্য কেহকেও অন্তরঙ্গ বন্ধু রূপে গ্রহণ করেনি? আর আল্লাহ তোমাদের সমস্ত কর্মের পূর্ণ খবর রাখেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1252,9,17,'মুশরিকরা যখন নিজেরাই নিজেদের কুফরী স্বীকার করে তখন তারা আল্লাহর মাসজিদের রক্ষণাবেক্ষণ করবে এমনতো হতে পারেনা। তারা এমন যাদের সমস্ত কাজ ব্যর্থ; এবং তারা জাহান্নামে স্থায়ীভাবে অবস্থান করবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1253,9,18,'আল্লাহর মাসজিদগুলি সংরক্ষণ করা তাদেরই কাজ, যারা আল্লাহর প্রতি ও কিয়ামাত দিবসের প্রতি ঈমান আনে এবং সালাত কায়েম করে ও যাকাত প্রদান করে এবং আল্লাহ ছাড়া কেহকেও ভয় করেনা। আশা করা যায় যে, এরাই সঠিক পথ প্রাপ্ত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1254,9,19,'তোমরা কি হাজীদের পানি পান করানোকে এবং মাসজিদুল হারামের রক্ষণাবেক্ষণ করাকে তাদের সমান সাব্যস্ত করে রেখেছ যারা আল্লাহ ও কিয়ামাত দিবসের প্রতি ঈমান আনে ও আল্লাহর পথে জিহাদ করে? তারা আল্লাহর সমীপে সমান নয়; যারা সীমা লংঘনকারী তাদেরকে আল্লাহ সুপথ প্রদর্শন করেননা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1255,9,20,'যারা ঈমান এনেছে ও হিজরাত করেছে, আর নিজেদের ধন ও প্রাণ দ্বারা আল্লাহর পথে জিহাদ করেছে তারা মর্যাদায় আল্লাহর কাছে অতি বড়, আর তারাই হচ্ছে পূর্ণ সফলকাম ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1256,9,21,'তাদের রাব্ব তাদেরকে নিজের পক্ষ থেকে সুসংবাদ দিচ্ছেন রাহমাতের ও অতি সন্তুষ্টির, আর এমন জান্নাতের যার মধ্যে তাদের জন্য চিরস্থায়ী নি’আমাত থাকবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1257,9,22,'ওর মধ্যে তারা অনন্তকাল থাকবে, নিঃসন্দেহে আল্লাহর নিকট রয়েছে শ্রেষ্ঠ পুরস্কার।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1258,9,23,'হে মু’মিনগণ! তোমরা নিজেদের পিতাদেরকে ও ভাইদেরকে বন্ধু রূপে গ্রহণ করনা যদি তারা ঈমানের মুকাবিলায় কুফরকে প্রিয় মনে করে; আর তোমাদের মধ্য হতে যারা তাদের সাথে বন্ধুত্ব রাখবে, বস্তুতঃ ঐ সব লোকই হচ্ছে বড় অত্যাচারী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1259,9,24,'(হে নাবী!) তুমি তাদেরকে বলে দাওঃ যদি তোমাদের পিতা, তোমাদের পুত্রগণ, তোমাদের ভাইগণ, তোমাদের স্ত্রীগণ, তোমাদের স্বগোত্র, আর ঐ সব ধন সম্পদ যা তোমরা অর্জন করেছ, আর ঐ ব্যবসায় যাতে তোমরা মন্দা পড়ার আশংকা করছ অথবা ঐ গৃহসমূহ যেখানে অতি আনন্দে বসবাস করছ, আল্লাহ এবং তাঁর রাসূলের চেয়ে এবং তাঁর পথে জিহাদ করার চেয়ে যদি (এই সব) তোমাদের নিকট অধিক প্রিয় হয় তাহলে তোমরা প্রতীক্ষা করতে থাক যে পর্যন্ত আল্লাহ নিজের নির্দেশ পাঠিয়ে দেন। আর আল্লাহ আদেশ অমান্যকারীদেরকে পথ প্রদশর্ন করেন না।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1260,9,25,'অবশ্যই আল্লাহ তোমাদেরকে (যুদ্ধে) বহু ক্ষেত্রে বিজয়ী করেছেন এবং হুনাইনের দিনেও। যখন তোমাদেরকে তোমাদের সংখ্যাধিক্য গর্বে উম্মত্ত করেছিল, অতঃপর সেই সংখ্যাধিক্য তোমাদের কোনই কাজে আসেনি, আর ভূ-পৃষ্ঠ প্রশস্ত থাকা সত্ত্বেও তা তোমাদের উপর সংকীর্ণ হয়ে গেল, অতঃপর তোমরা পৃষ্ঠ প্রদর্শন পূর্বক পলায়ন করলে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1261,9,26,'অতঃপর আল্লাহ নিজ রাসূলের প্রতি এবং অন্যান্য মু’মিনদের প্রতি তাঁর সাকিনা (প্রশান্তি) নাযিল করলেন এবং এমন সৈন্যদল (অর্থাৎ ফেরেশতা) নাযিল করলেন যাদেরকে তোমরা দেখনি, আর কাফিরদেরকে শাস্তি প্রদান করলেন; আর এটা হচ্ছে কাফিরদের কর্মফল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1262,9,27,'অতঃপর আল্লাহ (ঐ কাফিরদের মধ্য হতে) যাকে ইচ্ছা দয়া প্রদর্শন করেন, আর আল্লাহ হচ্ছেন অতি ক্ষমাশীল, পরম করুণাময়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1263,9,28,'হে মু’মিনগণ! মুশরিকরা হচ্ছে একেবারেই অপবিত্র, অতএব তারা যেন এ বছরের পর মাসজিদুল হারামের নিকটেও আসতে না পারে, আর যদি তোমরা দারিদ্রতার ভয় কর তাহলে আল্লাহ নিজ অনুগ্রহে তোমাদেরকে অভাবমুক্ত করবেন, যদি তিনি চান। নিশ্চয়ই আল্লাহ অতিশয় জ্ঞানী, বড়ই হিকমাতওয়ালা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1264,9,29,'যারা আল্লাহর প্রতি ঈমান রাখেনা এবং কিয়ামাত দিনের প্রতিও না, আর ঐ বস্তুগুলিকে হারাম মনে করেনা যেগুলিকে আল্লাহ ও তাঁর রাসূল হারাম বলেছেন, আর সত্য ধর্ম (অর্থাৎ ইসলাম) গ্রহণ করেনা, তাদের বিরুদ্ধে যুদ্ধ করতে থাক যে পর্যন্ত না তারা অধীনতা স্বীকার করে প্রজা রূপে জিযিয়া দিতে স্বীকার করে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1265,9,30,'ইয়াহুদীরা বলেঃ উযায়ের আল্লাহর পুত্র এবং নাসারারা বলেঃ মাসীহ্ আল্লাহর পুত্র। এটা তাদের মুখের কথা মাত্র (বাস্তবে তা কিছুই নয়), তারাতো তাদের মতই কথা বলছে যারা তাদের পূর্বে কাফির হয়েছে। আল্লাহ তাদেরকে ধ্বংস করুন! তারা উল্টা কোন দিকে যাচ্ছে!');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1266,9,31,'তারা আল্লাহকে ছেড়ে নিজেদের পন্ডিত ও ধর্ম যাজকদেরকে রাব্ব বানিয়ে নিয়েছে এবং মারইয়ামের পুত্র মসীহকেও। অথচ তাদের প্রতি শুধু এই আদেশ করা হয়েছে যে, তারা শুধুমাত্র এক মা‘বূদের ইবাদাত করবে যিনি ব্যতীত ইলাহ হওয়ার যোগ্য কেহই নয়। তিনি তাদের অংশী স্থির করা হতে পবিত্র।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1267,9,32,'তারা এরূপ চাচ্ছে যে, আল্লাহর নূরকে নিজেদের মুখের ফুৎকার দ্বারা নির্বাপিত করে দেয়, অথচ আল্লাহ স্বীয় নূরকে (দীন ইসলাম) পূর্ণত্বে পৌঁছানো ব্যতীত নিরস্ত হবেননা, যদিও কাফিরেরা অপ্রীতিকরই মনে করে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1268,9,33,'সেই আল্লাহ এমন যে, তিনি নিজ রাসূলকে হিদায়াত (কুরআন) এবং সত্য ধর্ম সহকারে প্রেরণ করেছেন, যেন ওকে সকল ধর্মের উপর প্রবল করে দেন, যদিও মুশরিকরা অপ্রীতিকর মনে করে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1269,9,34,'হে মু’মিনগণ! অধিকাংশ আহবার এবং রুহবান (ইয়াহুদ ও খৃষ্টানদের আলেম ও ধর্ম যাজক) মানুষের ধন-সম্পদ শারীয়াত বিরুদ্ধ উপায়ে ভক্ষণ করে এবং আল্লাহর পথ হতে বিরত রাখে, আর যারা স্বর্ণ ও রৌপ্য জমা করে রাখে এবং তা আল্লাহর পথে ব্যয় করেনা, তুমি তাদেরকে যন্ত্রণাদায়ক এক শাস্তির সুসংবাদ শুনিয়ে দাও।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1270,9,35,'সেদিন জাহান্নামের আগুনে ঐগুলিকে উত্তপ্ত করা হবে, অতঃপর ঐগুলি দ্বারা তাদের ললাটসমূহে, পার্শ্বদেশসমূহে এবং পৃষ্ঠদেশসমূহে দাগ দেয়া হবে, আর বলা হবেঃ এটা হচ্ছে ওটাই যা তোমরা নিজেদের জন্য সঞ্চয় করে রেখেছিলে, সুতরাং এখন নিজেদের সঞ্চয়ের স্বাদ গ্রহণ কর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1271,9,36,'নিশ্চয়ই আকাশমন্ডলী ও পৃথিবী সৃষ্টির দিন থেকে আল্লাহর বিধানে মাস গণনায় বারটি। এর মধ্যে বিশেষ রূপে চারটি মাস হচ্ছে সম্মানিত। এটাই হচ্ছে সুপ্রতিষ্ঠিত ধর্ম। অতএব তোমরা এ মাসগুলিতে (ধর্মের বিরুদ্ধাচরণ করে) নিজেদের ক্ষতি সাধন করনা, আর মুশরিকদের বিরুদ্ধে সকলে একযোগে যুদ্ধ কর, যেমন তারা তোমাদের বিরুদ্ধে সকলে একযোগে যুদ্ধ করে। আর জেনে রেখ যে, আল্লাহ মুত্তাকীদের সাথে রয়েছেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1272,9,37,'নিশ্চয়ই এই (মাসগুলির) স্থানান্তর কুফরের মধ্যে আরও কুফরী বৃদ্ধি করা, যদ্বারা কাফিরদেরকে পথভ্রষ্ট করা হয়। (তা এ রূপে যে) তারা সেই হারাম মাসকে কোন বছর হালাল করে নেয় এবং কোন বছর হারাম মনে করে, আল্লাহ যে মাসগুলিকে হারাম করেছেন, যেন তারা ওগুলির সংখ্যা পূর্ণ করে নিতে পারে, অতঃপর তারা আল্লাহর নিষিদ্ধ মাসগুলিকে হালাল করে নেয়, তাদের দুস্কর্মগুলি তাদের কাছে শোভনীয় মনে হয়, আর আল্লাহ এইরূপ কাফিরদেরকে হিদায়াত (এর তাওফীক দান) করেননা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1273,9,38,'হে মু’মিনগণ! তোমাদের কি হল যে, যখন তোমাদেরকে বলা হয়, বের হও আল্লাহর পথে, তখন তোমরা মাটিতে লেগে থাক (অলসভাবে বসে থাক)। তাহলে কি তোমরা পরকালের বিনিময়ে পার্থিব জীবনের উপর পরিতুষ্ট হয়ে গেলে? বস্তুতঃ পার্থিব জীবনের ভোগ বিলাসতো আখিরাতের তুলনায় কিছুই নয়, অতি সামান্য।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1274,9,39,'যদি তোমরা বের না হও তাহলে আল্লাহ তোমাদেরকে কঠোর শাস্তি প্রদান করবেন এবং তোমাদের পরিবর্তে অন্য এক জাতিকে স্থলাভিষিক্ত করবেন, আর তোমরা আল্লাহর (দীনের) কোনই ক্ষতি করতে পারবেনা। আল্লাহ সর্বময় ক্ষমতার অধিকারী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1275,9,40,'যদি তোমরা তাকে (রাসূলুল্লাহকে) সাহায্য না কর তাহলে আল্লাহই তাকে সাহায্য করবেন যেমন তিনি তাকে সাহায্য করেছিলেন সেই সময়ে যখন কাফিরেরা তাকে দেশান্তর করেছিল, যখন দু’জনের মধ্যে একজন ছিল সে, যে সময় উভয়ে গুহার মধ্যে ছিল, যখন সে স্বীয় সঙ্গীকে (আবূ বাকরকে) বলেছিলঃ তুমি বিষণ্ণ হয়োনা, নিশ্চয়ই আল্লাহ আমাদের সঙ্গে রয়েছেন। অতঃপর আল্লাহ তার প্রতি স্বীয় প্রশান্তি নাযিল করলেন এবং তাকে শক্তিশালী করলেন এমন সেনাদল দ্বারা যাদেরকে তোমরা দেখতে পাওনি এবং আল্লাহ কাফিরদের বাক্য নীচু করে দিলেন, আর আল্লাহর বাণী সমুচ্চ রইল, আর আল্লাহ হচ্ছেন প্রবল প্রজ্ঞাময়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1276,9,41,'অভিযানে বের হও স্বল্প সরঞ্জামের সাথেই হোক, অথবা প্রচুর সরঞ্জামের সাথেই হোক এবং আল্লাহর পথে নিজেদের ধন সম্পদ ও প্রাণ দ্বারা যুদ্ধ কর, এটাই তোমাদের জন্য অতি উত্তম, যদি তোমরা জানতে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1277,9,42,'যদি কিছু আশু লভ্য হত এবং সফরও সহজ হত তাহলে তারা অবশ্যই তোমার সহগামী হত; কিন্তু তাদেরতো পথের দূরত্বই দীর্ঘতর বোধ হতে লাগল; আর তারা অচিরেই আল্লাহর নামে শপথ করে বলবেঃ যদি আমাদের সাধ্য থাকত তাহলে আমরা নিশ্চয়ই তোমাদের সাথে বের হতাম; তারা (মিথ্যা বলে) নিজেরাই নিজেদেরকে ধ্বংস করছে; আর আল্লাহ জানেন যে, তারা মিথ্যাবাদী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1278,9,43,'আল্লাহ তোমাকে ক্ষমা করেছেন (কিন্তু) তুমি তাদেরকে কেন অনুমতি দিলে যে পর্যন্ত না সত্যবাদী লোকেরা তোমার কাছে প্রকাশ হয়ে যেত এবং তুমি মিথ্যাবাদীদেরকে জেনে নিতে?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1279,9,44,'যারা আল্লাহর প্রতি ও পরকালের প্রতি ঈমান রাখে তারা নিজেদের ধন-সম্পদ ও প্রাণ দ্বারা জিহাদ করার ব্যাপারে তোমার কাছে অব্যাহতি প্রার্থনা করবেনা, আর আল্লাহ এই পরহেজগার লোকদের সম্বন্ধে খুবই অবগত আছেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1280,9,45,'অবশ্যই ঐসব লোক তোমার কাছে অব্যাহতি চেয়ে থাকে যারা আল্লাহর প্রতি ও আখিরাতের প্রতি ঈমান রাখেনা, তারা তাদের অন্তরসমূহ সন্দেহে নিপতিত রয়েছে। অতএব তারা নিজেদের সন্দেহে হতবুদ্ধি হয়ে রয়েছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1281,9,46,'আর যদি তারা (যুদ্ধে) যাত্রা করার ইচ্ছা করত তাহলে সেজন্য কিছু সরঞ্জামতো প্রস্তুত করত, কিন্তু আল্লাহ তাদের যাত্রাকে অপছন্দ করেছেন, এ জন্য তাদেরকে তাওফীক দেননি এবং বলে দেয়া হল, তোমরাও এখানেই অক্ষম লোকদের সাথে বসে থাকো।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1282,9,47,'যদি তারা তোমাদের সাথে বের হত তাহলে দ্বিগুণ বিভ্রাট সৃষ্টি করা ব্যতীত আর কি হত? তারা তোমাদের মধ্যে ফাসাদ সৃষ্টি করার উদ্দেশে দৌড়াদৌড়ি করে ফিরত, আর তোমাদের মধ্যের কতিপয় উহা শ্রবণ করত; আল্লাহ এই যালিমদের সম্বন্ধে খুব অবগত আছেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1283,9,48,'পূর্বেও তারা ফিতনা ফাসাদ সৃষ্টি করেছিল এবং তোমার কার্যক্রম ব্যর্থ করার চেষ্টায় রত ছিল। শেষ পর্যন্ত হক ও আল্লাহর নির্দেশ প্রকাশমান হল, যদিও তা তাদের মনঃপুত ছিলনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1284,9,49,'আর তাদের মধ্যে কেহ কেহ এমনও আছে যে বলেঃ আমাকে (যুদ্ধে গমন না করার) অনুমতি দিন এবং আমাকে বিপদে ফেলবেননা। ভাল রূপে বুঝে নাও যে, তারাতো বিপদে পড়েই আছে। আর নিশ্চয়ই জাহান্নাম এই কাফিরদের বেষ্টন করবেই।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1285,9,50,'যদি তোমার প্রতি কোন মঙ্গল উপস্থিত হয় তাহলে তাদের জন্য তা চিন্তার কারণ হয়ে দাঁড়ায়, আর যদি তোমার উপর কোন বিপদ এসে পড়ে তখন তারা বলেঃ আমরাতো প্রথম থেকেই নিজেদের জন্য সাবধানতার পথ অবলম্বন করেছিলাম এবং তারা খুশী হয়ে চলে যায়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1286,9,51,'বলঃ আল্লাহ আমাদের জন্য যা নির্ধারণ করে দিয়েছেন তা ছাড়া অন্য কোন বিপদ আমাদের উপর আপতিত হবেনা, তিনিই আমাদের কর্ম বিধায়ক, আর সকল মু’মিনের কর্তব্য হল, তারা যেন নিজেদের যাবতীয় কাজে আল্লাহর উপরই নির্ভর করে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1287,9,52,'বলঃ তোমরাতো আমাদের জন্য দু’টি মঙ্গলের মধ্যে একটি মঙ্গলের প্রতীক্ষায় রয়েছ; আর আমরা তোমাদের জন্য এই প্রতীক্ষা করছি যে, আল্লাহ তোমাদের উপর কোন্ শাস্তি সংঘটন করবেন - নিজের পক্ষ হতে অথবা আমাদের দ্বারা; অতএব তোমরা অপেক্ষা করতে থাক, আমরাও তোমাদের সাথে অপেক্ষমান রইলাম।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1288,9,53,'তুমি বলঃ তোমরা সন্তুষ্টির সাথে ব্যয় কর কিংবা অসন্তুষ্টির সাথে, তোমাদের পক্ষ থেকে তা কক্ষণই গৃহীত হবেনা; নিঃসন্দেহে তোমরা হচ্ছ আদেশ লংঘনকারী সম্প্রদায়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1289,9,54,'আর তাদের দান খয়রাত গ্রহণ না হওয়ার কারণ এই যে, তারা আল্লাহর সাথে ও তাঁর রাসূলের সাথে কুফরী করেছে, আর তারা শৈথিল্যের সাথে ছাড়া সালাত আদায় করেনা, আর তারা দান করেনা। কিন্তু অনিচ্ছার সাথে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1290,9,55,'অতএব তাদের ধন-সম্পদ এবং সন্তান-সন্ততি যেন তোমাকে বিস্মিত না করে; আল্লাহর ইচ্ছা শুধু এটাই যে, এসব বস্তুর কারণে তাদেরকে পার্থিব জীবনে আযাবে আবদ্ধ রাখেন এবং তাদের প্রাণ কুফরী অবস্থায় বের হয়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1291,9,56,'আর তারা আল্লাহর কসম করে বলে যে, তারা (মুনাফিকরা) তোমাদেরই অন্তর্ভুক্ত; অথচ তারা তোমাদের অন্তর্ভুক্ত নয়, বরং তারা হচ্ছে কাপুরুষের দল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1292,9,57,'যদি তারা কোন আশ্রয়স্থল পেত, অথবা গুহা কিংবা লুকিয়ে থাকার একটু স্থান পেত তাহলে তারা অবশ্যই ক্ষিপ্র গতিতে সেদিকে ধাবিত হত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1293,9,58,'আর তাদের মধ্যে এমন কতক লোক রয়েছে যারা সাদাকাহর (বন্টন) ব্যাপারে তোমার প্রতি দোষারোপ করে, অতঃপর যদি তারা ঐ সমস্ত সাদাকাহ হতে (অংশ) প্রাপ্ত হয় তাহলে তারা সন্তুষ্ট হয়, আর যদি তারা তা থেকে বঞ্চিত হয় তাহলে তারা অসন্তুষ্ট হয়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1294,9,59,'তাদের জন্য উত্তম হত যদি তারা ওর প্রতি সন্তুষ্ট থাকতো যা কিছু তাদেরকে আল্লাহ ও তাঁর রাসূল দান করেছিলেন, আর বলতঃ আমাদের পক্ষে আল্লাহই যথেষ্ট, ভবিষ্যতে আল্লাহ স্বীয় অনুগ্রহে আমাদেরকে আরও দান করবেন এবং তাঁর রাসূলও, আমরা আল্লাহরই প্রতি আগ্রহান্বিত রইলাম।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1295,9,60,'সাদাকাহ হচ্ছে শুধুমাত্র গরীবদের এবং অভাবগ্রস্তদের, আর এই সাদাকাহর (আদায়ের) জন্য নিযুক্ত কর্মচারীদের এবং (দীনের ব্যাপারে) যাদের মন রক্ষা করতে (অভিপ্রায়) হয় (তাদের), আর গোলামদের আযাদ করার কাজে এবং কর্জদারদের কর্জে (কর্জ পরিশোধে), আর জিহাদে (অর্থাৎ যুদ্ধ সরঞ্জাম সংগ্রহের জন্য) আর মুসাফিরদের সাহায্যার্থে। এই হুকুম আল্লাহর পক্ষ হতে নির্ধারিত, আর আল্লাহ মহাজ্ঞানী, অতি প্রজ্ঞাময়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1296,9,61,'আর তাদের মধ্যে এমন কতিপয় লোক আছে, যারা নাবীকে যাতনা দেয় এবং বলেঃ তিনি প্রত্যেক কথায় কর্ণপাত করে থাকেন। বলে দাওঃ এই নাবীতো কর্ণপাত করে সেই কথায় যা তোমাদের জন্য কল্যাণকর, সে আল্লাহর প্রতি ঈমান আনে, আর মু’মিনদের বিশ্বাস করে, আর সে ঐ সব লোকের প্রতি রাহমাত স্বরূপ যারা মু’মিন। আর যারা আল্লাহর রাসূলকে যাতনা দেয় তাদের জন্য রয়েছে যন্ত্রণাদায়ক শাস্তি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1297,9,62,'তারা তোমাদের কাছে আল্লাহর নামে শপথ করে যেন তারা তোমাদেরকে সন্তুষ্ট করতে পারে। আল্লাহ ও তাঁর রাসূলকে সন্তুষ্ট করা তাদের জন্য বেশি যরুরী, যদি তারা সত্যিকারের মু’মিন হয়ে থাকে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1298,9,63,'তারা কি জানেনা যে, আল্লাহ ও তাঁর রাসূলের যারা বিরুদ্ধাচরণ করে, এমন লোকের ভাগ্যে রয়েছে জাহান্নামের আগুন? তারা তাতে অনন্তকাল থাকবে, এটা হচ্ছে চরম লাঞ্ছনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1299,9,64,'মুনাফিকরা আশংকা করে যে, তাদের (মুসলিমদের) প্রতি না জানি এমন কোন সূরা নাযিল হয় যা তাদের (মুনাফিকদের) অন্তরের কথা অবহিত করে দেয়। তুমি বলে দাওঃ হ্যাঁ, তোমরা বিদ্রুপ করতে থাক, নিশ্চয়ই আল্লাহ সেই বিষয়কে প্রকাশ করেই দিবেন যে সম্বন্ধে তোমরা আশংকা করছিলে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1300,9,65,'আর যদি তাদেরকে জিজ্ঞেস কর তাহলে তারা বলে দিবেঃ আমরাতো শুধু আলাপ আলোচনা ও হাসি তামাশা করছিলাম। তুমি বলঃ তাহলে কি তোমরা আল্লাহ, তাঁর আয়াতসমূহ এবং তাঁর রাসূলের প্রতি হাসি তামাসা করছিলে?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1301,9,66,'তোমরা এখন অজুহাত দেখিয়োনা, তোমরাতো ঈমান আনার পর কুফরী করেছ, যদিও আমি তোমাদের মধ্য হতে কতককে ক্ষমা করে দিই, তবুও কতককে শাস্তি দিবই। কারণ তারা অপরাধী ছিল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1302,9,67,'মুনাফিক পুরুষ এবং মুনাফিক নারীরা সবাই এক রকম, অসৎ কর্মের শিক্ষা দেয় এবং সৎ কাজ হতে বিরত রাখে, আর নিজেদের হাতসমূহকে (আল্লাহর পথে ব্যয় করা হতে) বন্ধ করে রাখে, তারা আল্লাহকে ভুলে গেছে। সুতরাং তিনিও তাদেরকে ভুলে গেছেন, নিঃসন্দেহে মুনাফিকরা হচ্ছে অতি অবাধ্য।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1303,9,68,'আল্লাহ মুনাফিক পুরুষ ও নারীদের এবং কাফিরদের সাথে জাহান্নামের আগুনের অঙ্গীকার করেছেন, তাতে তারা চিরকাল থাকবে, ওটাই তাদের জন্য যথেষ্ট। আর আল্লাহ তাদেরকে লা’নত করেছেন এবং তাদের জন্য রয়েছে চিরস্থায়ী শাস্তি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1304,9,69,'তোমাদের পূর্বে যারা গত হয়েছে, যারা ছিল তোমাদের চেয়ে অধিকতর শক্তিশালী এবং ধন-সম্পদ ও সন্তানাদীর প্রাচুর্য্যও ছিল তোমাদের চেয়ে অনেক বেশী; তারা তাদের (পার্থিব) অংশ দ্বারা যথেষ্ট উপকার লাভ করেছে। অতঃপর তোমরাও তোমাদের (পার্থিব) অংশ দ্বারা খুব উপকার লাভ করলে যেমন, তোমাদের পূর্ববর্তীরা নিজেদের অংশ দ্বারা ফল ভোগ করেছিল; আর তোমরাও ব্যাঙ্গাত্মক হাসি তামাসায় এরূপভাবে নিমগ্ন রয়েছ যেমন তারা নিমগ্ন হয়েছিল। তাদের কার্যসমূহ বিনষ্ট হয়ে গেছে দুনিয়ায় ও আখিরাতে, আর তারাই ক্ষতিগ্রস্ত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1305,9,70,'তাদের কাছে কি ঐ সব লোকের সংবাদ পৌঁছেনি যারা তাদের পূর্বে গত হয়েছে, নূহ সম্প্রদায় এবং আদ ও ছামূদ সম্প্রদায়, আর ইবরাহীমের সম্প্রদায় এবং মাদইয়ানের অধিবাসীরা এবং বিধ্বস্ত জনপদগুলির? তাদের কাছে তাদের রাসূলগণ স্পষ্ট নিদর্শনসমূহ নিয়ে এসেছিল। বস্তুতঃ আল্লাহ তাদের প্রতি অত্যাচার করেননি, বরং তারা নিজেরাই নিজেদের প্রতি অত্যাচার করেছিল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1306,9,71,'আর মু’মিন পুরুষরা ও মু’মিনা নারীরা হচ্ছে পরস্পর একে অন্যের বন্ধু। তারা সৎ বিষয়ের শিক্ষা দেয় এবং অসৎ বিষয় হতে নিষেধ করে, আর সালাতের পাবন্দী করে ও যাকাত প্রদান করে, আর আল্লাহ ও তাঁর রাসূলের আদেশ মেনে চলে, এসব লোকের প্রতি আল্লাহ অবশ্যই করুণা বর্ষণ করবেন, নিঃসন্দেহে আল্লাহ অতিশয় ক্ষমতাবান, হিকমাতওয়ালা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1307,9,72,'আর মু’মিন পুরুষ ও মু’মিনা নারীদেরকে আল্লাহ এমন উদ্যানসমূহের ও‘য়াদা দিয়ে রেখেছেন যেগুলির নিম্নদেশে বইতে থাকবে নহরসমূহ, যেখানে তারা অনন্ত কাল থাকবে, আরও (ও‘য়াদা দিয়েছেন) উত্তম বাসস্থানসমূহের, ঐ স্থায়ী জান্নাতে। আর আল্লাহর সন্তুষ্টি হচ্ছে সর্বাপেক্ষা বড় নি‘আমাত, এটা হচ্ছে অতি বড় সফলতা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1308,9,73,'হে নাবী! কাফির ও মুনাফিকদের বিরুদ্ধে জিহাদ কর এবং তাদের প্রতি কঠোরতা অবলম্বন কর। তাদের বাসস্থান হচ্ছে জাহান্নাম; এবং ওটা নিকৃষ্ট স্থান।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1309,9,74,'তারা আল্লাহর নামে শপথ করে বলছে যে, তারা কিছুই বলেনি; অথচ নিশ্চয়ই তারা কুফরী কথা বলেছিল এবং ইসলাম গ্রহণের পর কাফির হয়ে গেল, আর তারা এমন বিষয়ের সংকল্প করেছিল যা তারা কার্যকর করতে পারেনি; তারা শুধু এ কারণে প্রতিশোধ নিয়েছে যে, আল্লাহ এবং তাঁর রাসূল স্বীয় অনুগ্রহে তাদেরকে সম্পদশালী  করেছেন। যদি তারা তাওবাহ করে তাহলে তা তাদের জন্য উত্তম হবে; আর যদি তারা মুখ ফিরিয়ে নেয় তাহলে আল্লাহ তাদেরকে ইহকাল ও পরকালে যন্ত্রণাদায়ক শাস্তি প্রদান করবেন, আর ভূ-পৃষ্ঠে তাদের না কোন অলী থাকবে আর না কোন সাহায্যকারী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1310,9,75,'আর তাদের মধ্যে এমন কতিপয় লোক রয়েছে যারা আল্লাহর সাথে অঙ্গীকার করেঃ আল্লাহ যদি আমাদেরকে নিজ অনুগ্রহে (প্রচুর সম্পদ) দান করেন তাহলে আমরা অনেক দান খয়রাত করব এবং খুব ভাল কাজ করব।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1311,9,76,'কার্যতঃ যখন আল্লাহ তাদেরকে নিজ অনুগ্রহ প্রদান করলেন, তখন তারা তাতে কার্পণ্য করতে লাগল এবং (আনুগত্য করা হতে) মুখ ফিরিয়ে নিল, আর তারাতো মুখ ফিরিয়ে রাখতেই অভ্যস্ত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1312,9,77,'অতঃপর তাদের শাস্তি স্বরূপ আল্লাহ তাদের অন্তরসমূহে নিফাক (সৃষ্টি) করে দিলেন, যা আল্লাহর সামনে হাযির হওয়ার দিন পর্যন্ত স্থায়ী থাকবে এ কারণে যে, তারা আল্লাহর সাথে নিজেদের ও‘য়াদার খেলাফ করেছে; আর এ কারণে যে, তারা মিথ্যা বলেছিল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1313,9,78,'তাদের কি জানা নেই যে, আল্লাহ তাদের মনের গুপ্ত কথা এবং গোপন পরামর্শ সবই অবগত আছেন? আর তাদের কি এ খবর জানা নেই যে, আল্লাহ সমস্ত গাইবের কথা খুবই জ্ঞাত আছেন?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1314,9,79,'স্বতঃপ্রবৃদ্ধ হয়ে সাদাকাহ প্রদানকারী মু’মিন এবং যারা নিজ পরিশ্রম থেকে দেয়া ছাড়া অন্য কিছু দিতে পারেনা তাদের প্রতি যারা দোষারোপ করে/উপহাস করে, আল্লাহ তাদেরকে এই উপহাসের প্রতিফল দিবেন এবং তাদের জন্য রয়েছে যন্ত্রণাদায়ক শাস্তি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1315,9,80,'(হে মুহাম্মাদ!) তুমি তাদের জন্য ক্ষমা প্রার্থনা কর অথবা না কর (উভয়ই সমান), যদি তুমি তাদের জন্য সত্তর বারও ক্ষমা প্রার্থনা কর তবুও আল্লাহ তাদেরকে ক্ষমা করবেননা। কারণ তারা আল্লাহ ও তাঁর রাসূলের সাথে কুফরী করেছে। আর এরূপ সত্যত্যাগী সম্প্রদায়কে আল্লাহ পথ প্রদর্শন করেননা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1316,9,81,'রাসূলুল্লাহকে (যুদ্ধে গমনের পর) পিছনে পরে থাকা লোকেরা নিজেদের গৃহে বসে থেকে উল্লাস প্রকাশ করছিল এবং তারা আল্লাহর পথে নিজেদের ধন ও প্রাণ দিয়ে জিহাদ করাকে অপছন্দ করল। অধিকন্ত বলতে লাগলঃ তোমরা এই গরমের মধ্যে বের হয়োনা। তুমি বলে দাও -জাহান্নামের আগুন অধিকতর গরম, কত ভাল হত যদি তারা বুঝতে পারত!');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1317,9,82,'অতএব তারা অল্প কয়েকদিন (হেসে-খেলে) কাটিয়ে দিক, আর প্রচুর তারা কাঁদবে, ঐ সব কাজের বিনিময়ে যা তারা অর্জন করেছিল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1318,9,83,'আল্লাহ যদি তোমাকে (মাদীনায়) তাদের কোন সম্প্রদায়ের কাছে ফিরিয়ে আনেন, অতঃপর তারা কোন জিহাদে বের হতে অনুমতি চায়, তাহলে তুমি বলে দাওঃ তোমরা কখনও আমার সাথী হয়ে বের হবেনা এবং আমার সাথী হয়ে কোন শক্রর বিরুদ্ধে যুদ্ধও করবেনা; তোমরা পূর্বেও বসে থাকাকে পছন্দ করেছিলে। অতএব এখনো তোমরা ঐ সব লোকের সাথে বসে থাক যারা পশ্চাদবর্তী থাকার যোগ্য।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1319,9,84,'আর ভবিষ্যতে তাদের (মুনাফিকদের) কোন লোক মারা গেলে তার (জানাযার) সালাত তুমি কখনই আদায় করবেনা এবং তাদের কাবরের পাশে কখনও দাঁড়াবেনা। তারা আল্লাহ ও তাঁর রাসূলের সাথে কুফরী করেছে এবং তারা কুফরী অবস্থায়ই মৃত্যু বরণ করেছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1320,9,85,'আর তাদের ধন-সস্পদ ও সন্তান-সন্ততি তোমাকে যেন বিস্মিত না করে; আল্লাহ শুধু এটাই চাচ্ছেন যে, এ সমস্ত বস্তুর কারণে দুনিয়ায় তাদেরকে শাস্তিতে আবদ্ধ রাখেন এবং তাদের প্রাণবায়ু কুফরী অবস্থায়ই বের হয়ে যায়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1321,9,86,'আর যখনই কুরআনের কোন অংশ এ বিষয়ে অবতীর্ণ করা হয় যে, তোমরা আল্লাহর উপর ঈমান আন এবং তাঁর রাসূলের সঙ্গী হয়ে জিহাদ কর তখন তাদের মধ্যকার সম্পদশালী ক্ষমতাবান ব্যক্তিরা তোমার কাছে অব্যাহতি চায় ও বলেঃ আমাদেরকে অনুমতি দিন, আমরাও এখানে অবস্থানকারীদের সাথে থেকে যাই।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1322,9,87,'তারা অন্তঃপুরবাসিনী নারীদের সাথে থাকতে পছন্দ করল, তাদের অন্তরসমূহের উপর মোহর লাগিয়ে দেয়া হল, কাজেই তারা বুঝতে পারেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1323,9,88,'কিন্তু রাসূল ও তার সঙ্গীদের মধ্যে যারা মুসলিম ছিল তারা নিজেদের ধন সম্পদ ও প্রাণ দ্বারা জিহাদ করল; তাদেরই জন্য রয়েছে যাবতীয় কল্যাণ এবং তারাই হচ্ছে সফলকাম।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1324,9,89,'আল্লাহ তাদের জন্য এমন উদ্যানসমূহ প্রস্তুত করে রেখেছেন যেগুলির নিম্নদেশ দিয়ে নহরসমূহ বয়ে চলবে, আর তারা ওর মধ্যে অনন্তকাল অবস্থান করবে; এটাই হচ্ছে (তাদের) বিরাট সফলতা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1325,9,90,'আর গ্রামবাসীদের মধ্য হতে কতিপয় বাহানাকারী লোক এলো যেন তারা অনুমতি পায়। আর যারা আল্লাহ ও তাঁর রাসূলের সাথে সম্পূর্ণ রূপেই মিথ্যা বলেছিল তারা একেবারেই বসে রইল; তাদের মধ্যে যেসব লোক কুফরী করেছে তাদের জন্য রয়েছে যন্ত্রণাদায়ক শাস্তি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1326,9,91,'দুর্বল লোকদের উপর কোন পাপ নেই, আর না রুগ্নদের উপর, আর না ঐ সব লোকের উপর যাদের খরচ করার সামর্থ্য নেই। যদি এই সব লোক আল্লাহ ও তাঁর রাসূলের প্রতি নিষ্ঠা রাখে (এবং আন্তরিকতার সাথে আনুগত্য স্বীকার করে) তাহলে এ সব সৎ লোকের প্রতি কোন প্রকার অভিযোগ নেই। আর আল্লাহ অতিশয় ক্ষমাশীল, পরম করুণাময়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1327,9,92,'আর ঐ লোকদের উপরও নয়, যখন তারা তোমার নিকট এ উদ্দেশে আসে যে, তুমি তাদেরকে বাহন দান করবে, আর তুমি বলে দিয়েছ - আমার নিকটতো কোন বাহন নেই যার উপর আমি তোমাদেরকে উপবিষ্ট করাই, তখন তারা এমন অবস্থায় ফিরে যায় যে, তাদের চক্ষুসমূহ হতে অশ্রু বইতে থাকে এই অনুতাপে যে, তাদের ব্যয় করার মত কোন সম্বল নেই।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1328,9,93,'অভিযোগতো শুধুমাত্র ঐ লোকদেরই উপর যারা সামর্থ্যশালী হওয়া সত্ত্বেও (যুদ্ধে গমন না করার) অনুমতি প্রার্থনা করছে, তারা অন্তঃপুরবাসিনী মহিলাদের সাথে থাকাকে অগ্রাধিকার দিচ্ছে। আল্লাহ তাদের অন্তরসমূহের উপর মোহর মেরে দিলেন, কাজেই তারা (পাপ-পুণ্যকে) বুঝতে পারেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1329,9,94,'তারা তোমাদের কাছে ওযর পেশ করবে যখন তোমরা তাদের কাছে ফিরে যাবে; (হে নাবী) তুমি বলে দাওঃ তোমরা ওযর পেশ করনা, আমরা কখনও তোমাদেরকে সত্যবাদী বলে মনে করবনা, আল্লাহ আমাদেরকে তোমাদের (জিহাদে না যাওয়ার) বৃত্তান্ত জানিয়ে দিয়েছেন, আর ভবিষ্যতেও আল্লাহ এবং তাঁর রাসূল তোমাদের কার্যকলাপ পর্যবেক্ষণ করবেন, অতঃপর তোমরা প্রত্যাবর্তিত হবে এমন সত্তার কাছে যিনি অদৃশ্য এবং প্রকাশ্য সকল বিষয় অবগত আছেন, অতঃপর তিনি তোমাদেরকে জানিয়ে দিবেন যা কিছু তোমরা করেছিলে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1330,9,95,'হ্যাঁ, তারা তখন তোমাদের সামনে শপথ করে বলবে, যখন তোমরা তাদের কাছে ফিরে যাবে, যেন তোমরা তাদেরকে তাদের অবস্থার উপর ছেড়ে দাও; অতএব তোমরা তাদেরকে তাদের অবস্থার উপর ছেড়েই দাও; তারা হচ্ছে অতিশয় অপবিত্র, আর তাদের ঠিকানা হচ্ছে জাহান্নাম, ঐ সব কর্মের বিনিময়ে যা তারা করত ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1331,9,96,'তারা এ জন্য শপথ করবে যেন তোমরা তাদের প্রতি খুশী হয়ে যাও, অতঃপর যদি তোমরা তাদের প্রতি খুশী হও তাহলে আল্লাহতো এমন দুস্কর্মকারী লোকদের প্রতি খুশী হবেননা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1332,9,97,'পল্লীবাসী লোকেরা কুফরী ও কপটতায় অতি কঠোর, আর আল্লাহ তাঁর রাসূলের প্রতি যা অবতীর্ণ করেছেন তাদের ঐ সব আহকামের জ্ঞান না থাকায় তাদের এইরূপ হওয়াই উচিত; আর আল্লাহ হচ্ছেন মহাজ্ঞানী, অতি প্রজ্ঞাময়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1333,9,98,'আর এই গ্রামবাসীদের মধ্যে এমন লোক রয়েছে যে, তারা যা কিছু ব্যয় করে তা জরিমানা মনে করে এবং তোমাদের জন্য দুর্দিনের প্রতীক্ষায় থাকে; (বস্তুতঃ) অশুভ আবতর্ন তাদের উপরই পতিত প্রায়, আর আল্লাহ খুব শোনেন, খুব জানেন ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1334,9,99,'আর গ্রামবাসীদের মধ্যে কতিপয় লোক এমনও আছে যারা আল্লাহর প্রতি এবং কিয়ামাত দিনের প্রতি পূর্ণ ঈমান রাখে, আর যা কিছু ব্যয় করে ওকে আল্লাহর সান্নিধ্য লাভের উপকরণ ও রাসূলের দু‘আ লাভের উপকরণ রূপে গ্রহণ করে। স্মরণ রেখ, তাদের এই ব্যয় কার্য নিঃসন্দেহে তাদের জন্য (আল্লাহর) নৈকট্য লাভের কারণ; নিশ্চয়ই আল্লাহ তাদেরকে নিজের রাহ্মাতে দাখিল করে নিবেন; নিশ্চয়ই আল্লাহ হচ্ছেন অতি ক্ষমাশীল, পরম করুণাময়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1335,9,100,'আর যে সব মুহাজির ও আনসার (ঈমান আনায়) অগ্রবর্তী এবং প্রথম, আর যে সব লোক সরল অন্তরে তাদের অনুগামী, আল্লাহ তাদের প্রতি রাযী-খুশি হয়েছেন যেমনভাবে তারা তাঁর প্রতি রাযী হয়েছে, আর আল্লাহ তাদের জন্য এমন উদ্যানসমূহ প্রস্তুত করে রেখেছেন যার তলদেশে নহরসমূহ বইতে থাকবে, যার মধ্যে তারা চিরস্থায়ীভাবে অবস্থান করবে, তা হচ্ছে বিরাট কৃতকার্যতা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1336,9,101,'আর তোমাদের মরুবাসীদের মধ্য হতে কতিপয় লোক এবং মাদীনাবাসীদের মধ্য হতেও কতিপয় লোক এমন মুনাফিক রয়েছে যারা নিফাকের চরমে পৌঁছে গেছে। তুমি তাদেরকে জাননা, আমিই তাদেরকে জানি, আমি তাদেরকে দ্বিগুণ শাস্তি প্রদান করব, অতঃপর (পরকালেও) তারা মহা শাস্তির দিকে প্রত্যাবর্তিত হবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1337,9,102,'এবং আরও কতকগুলি লোক আছে যারা নিজেদের অপরাধসমূহ স্বীকার করেছে, যারা মিশ্রিত ‘আমল করেছিল, কিছু ভাল, আর কিছু মন্দ, আশা রয়েছে যে, আল্লাহ তাদের প্রতি করুণা দৃষ্টি করবেন, নিঃসন্দেহে আল্লাহ অতিশয় ক্ষমাশীল, পরম করুণাময়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1338,9,103,'(হে নাবী!) তুমি তাদের ধন-সম্পদ হতে সাদাকাহ গ্রহণ কর, যদ্বারা তুমি তাদেরকে পবিত্র করে দিবে, আর তাদের জন্য দু‘আ কর। নিঃসন্দেহে তোমার দু‘আ হচ্ছে তাদের জন্য শান্তির কারণ, আর আল্লাহ খুব শোনেন, খুব জানেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1339,9,104,'তারা কি এটা অবগত নয় যে, আল্লাহই নিজ বান্দাদের তাওবাহ কবূল করেন, আর তিনিই দান খয়রাত কবূল করে থাকেন, আর এটাও যে, আল্লাহ হচ্ছেন তাওবাহ কবূল করতে এবং অনুগ্রহ করতে পূর্ণ সামর্থ্যবান?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1340,9,105,'হে নাবী! তুমি বলে দাওঃ তোমরা কাজ করতে থাক, অতঃপর তোমাদের কার্যকে অচিরেই দেখে নিবেন আল্লাহ, তাঁর রাসূল ও ঈমানদারগণ। আর নিশ্চয়ই তোমাদেরকে প্রত্যাবর্তিত হতে হবে এমন এক সত্তার নিকট যিনি হচ্ছেন সকল অদৃশ্য ও প্রকাশ্য বিষয়ের জ্ঞাতা, অতঃপর তিনি তোমাদেরকে তোমাদের সকল কৃতকর্ম জানিয়ে দিবেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1341,9,106,'এবং আরও কতক লোক আছে যাদের ব্যাপার মুলতবী রয়েছে আল্লাহর আদেশ আসা পর্যন্ত, হয় তিনি তাদেরকে শাস্তি প্রদান করবেন, অথবা তাদের তাওবাহ কবূল করবেন, আর আল্লাহ মহাজ্ঞানী, প্রজ্ঞাময়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1342,9,107,'আর কেহ কেহ এমন আছে যারা এ উদ্দেশে মাসজিদ নির্মাণ করেছে যেন তারা (ইসলামের) ক্ষতি সাধন করে এবং কুফরী কথাবার্তা বলে, আর মু’মিনদের মধ্যে বিভেদ সৃষ্টি করে, আর ঐ ব্যক্তির অবস্থানের ব্যবস্থা করে যে এর পূর্ব হতেই আল্লাহ ও তাঁর রাসূলের বিরোধী, আর তারা শপথ করে বলবে, মঙ্গল ভিন্ন আমাদের আর কোন উদ্দেশ্য নেই; আর আল্লাহ সাক্ষী আছেন যে, তারা সম্পূর্ণ মিথ্যাবাদী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1343,9,108,'(হে মুহাম্মাদ!) তুমি কখনও ওতে (সালাতের জন্য) দাঁড়াবেনা; অবশ্য যে মাসজিদের ভিত্তি প্রথম দিন হতেই তাকওয়ার উপর স্থাপিত হয়েছে তা এর উপযোগী যে, তুমি তাতে (সালাতের জন্য) দাঁড়াবে; ওতে এমন সব লোক রয়েছে যারা উত্তম রূপে পবিত্র হওয়াকে পছন্দ করে, আর আল্লাহ উত্তম রূপে পবিত্রতা সম্পাদনকারীদেরকে পছন্দ করেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1344,9,109,'তাহলে কোন্ ব্যক্তি উত্তম, যে ব্যক্তি স্বীয় ইমারাতের ভিত্তি আল্লাহভীতির উপর এবং তাঁর সন্তুষ্টির উপর স্থাপন করেছে অথবা সেই ব্যক্তি যে স্বীয় ইমারাতের ভিত্তি স্থাপন করেছে কোন গহবরের কিনারায়, যা ধ্বসে পড়ার উপক্রম, অতঃপর তা তাকে নিয়ে জাহান্নামের আগুনে পতিত হয়? আর আল্লাহ এমন যালিমদেরকে (ধর্মের) জ্ঞান দান করেননা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1345,9,110,'তাদের এই ইমারাত যা তারা নির্মাণ করেছে, তা সদা তাদের মনে খট্কা সৃষ্টি করতে থাকবে, যে পর্যন্ত তাদের অন্তরই ছিন্নভিন্ন হয়ে যায়, আর আল্লাহ হচ্ছেন মহাজ্ঞানী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1346,9,111,'নিঃসন্দেহে আল্লাহ মু’মিনদের নিকট থেকে তাদের প্রাণ ও তাদের ধন সম্পদসমূহকে এর বিনিময়ে ক্রয় করে নিয়েছেন যে, তাদের জন্য জান্নাত রয়েছে, তারা আল্লাহর পথে যুদ্ধ করে, যাতে তারা (কখনও) হত্যা করে এবং (কখনও) নিহত হয়, এর কারণে (জান্নাত প্রদানের) সত্য অঙ্গীকার করা হয়েছে তাওরাতে, ইঞ্জীলে এবং কুরআনে। নিজের অঙ্গীকার পালনকারী আল্লাহ অপেক্ষা অধিক আর কে আছে? অতএব তোমরা আনন্দ করতে থাক তোমাদের এই ক্রয় বিক্রয়ের উপর, যা তোমরা সম্পাদন করেছ, আর এটা হচ্ছে বিরাট সফলতা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1347,9,112,'তারা হচ্ছে তাওবাহকারী, ইবাদাতকারী, আল্লাহর প্রশংসাকারী, সিয়াম পালনকারী, রুকু ও সাজদাহকারী, সৎ বিষয় শিক্ষা প্রদানকারী এবং মন্দ বিষয়ে বাধা প্রদানকারী, আল্লাহর সীমাসমূহের (অর্থাৎ আহকামের) সংরক্ষণকারী; আর তুমি এমন মু’মিনদেরকে সুসংবাদ শুনিয়ে দাও।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1348,9,113,'নাবী ও অন্যান্য মু’মিনদের জন্য জায়েয নয় যে, তারা মুশরিকদের জন্য ক্ষমা প্রার্থনা করে, যদিও তারা আত্মীয়ই হোক না কেন, এ কথা প্রকাশ হবার পর যে, তারা জাহান্নামের অধিবাসী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1349,9,114,'আর ইবরাহীমের নিজ পিতার জন্য ক্ষমা প্রার্থনা করা শুধু সেই ও‘য়াদার কারণে ছিল, যে ও‘য়াদা সে তার সাথে করেছিল। অতঃপর যখন তার নিকট এ বিষয় প্রকাশ পেল যে, সে (পিতা) আল্লাহর দুশমন, তখন সে তা হতে সম্পূর্ণ রূপে নির্লিপ্ত হয়ে গেল। বাস্তবিকই ইবরাহীম ছিল অতিশয় কোমল হৃদয়, সহনশীল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1350,9,115,'আর আল্লাহ এরূপ নন যে, কোন জাতিকে হিদায়াত করার পর পথভ্রষ্ট করেন, যে পর্যন্ত না তাদেরকে সেই সব বিষয় পরিস্কারভাবে বলে দেন, যে বিষয়ে তারা তাকওয়া অবলম্বন করবে; নিশ্চয়ই আল্লাহ হচ্ছেন সকল বিষয়ে সর্বজ্ঞ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1351,9,116,'নিশ্চয়ই আল্লাহরই রাজত্ব রয়েছে আসমানসমূহে ও যমীনে। তিনিই জীবন দান করেন এবং মৃত্যু ঘটান; আল্লাহ ছাড়া তোমাদের না কোন বন্ধু আছে, আর না কোন সাহায্যকারী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1352,9,117,'আল্লাহ অনুগ্রহের দৃষ্টি করলেন নাবীর অবস্থার প্রতি এবং মুহাজির ও আনসারদের অবস্থার প্রতিও, যারা নাবীর অনুগামী হয়েছিল এমন সংকট মুহুর্তে যখন তাদের মধ্যকার এক দলের অন্তর বিচলিত হওয়ার উপক্রম হয়েছিল। অতঃপর আল্লাহ তাদের প্রতি অনুগ্রহ দৃষ্টি করলেন; নিঃসন্দেহে আল্লাহ তাদের সকলের উপর স্নেহশীল, করুণাময়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1353,9,118,'আর ঐ তিন ব্যক্তির অবস্থার প্রতিও (অনুগ্রহ করলেন) যাদের ব্যাপার মুলতবী রাখা হয়েছিল এই পর্যন্ত যে, তখন ভূ-পৃষ্ঠ নিজ প্রশস্ততা সত্ত্বেও তাদের প্রতি সংকীর্ণ হতে লাগল এবং তারা নিজেরা নিজেদের জীবনের প্রতি বীতশ্রদ্ধ হয়ে পড়ল; আর তারা বুঝতে পারল যে, আল্লাহর পাকড়াও হতে কোথাও আশ্রয় পাওয়া যেতে পারেনা তাঁরই দিকে প্রত্যাবর্তন করা ব্যতীত; অতঃপর তাদের অবস্থার প্রতিও অনুগ্রহের দৃষ্টি করলেন, যাতে তারা তাওবাহ করে। নিশ্চয়ই আল্লাহ হচ্ছেন অতিশয় অনুগ্রহকারী, করুণাময়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1354,9,119,'হে মু’মিনগণ! আল্লাহকে ভয় কর এবং সত্যবাদীদের সঙ্গে থাক।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1355,9,120,'মাদীনার অধিবাসী এবং তাদের আশেপাশে যে সব পল্লী রয়েছে তাদের পক্ষে এটা উচিত ছিলনা যে, তারা আল্লাহর রাসূলের সঙ্গী না হয়; আর এটাও (উচিত ছিল) না যে, নিজেদের প্রাণ তার প্রাণ অপেক্ষা প্রিয় মনে করে। এর কারণ এই যে, আল্লাহর পথে তাদের যে পিপাসা, ক্লান্তি আর ক্ষুধা পায় এবং তাদের এমন পদক্ষেপ গ্রহণ করায় কাফিরদের যে ক্রোধের কারণ হয়ে থাকে, আর দুশমনদের হতে তারা যা কিছু প্রাপ্ত হয় - এর প্রত্যেকটি সৎ কাজ বলে গণ্য হবে। নিশ্চয়ই আল্লাহ সৎ কর্মশীল লোকদের শ্রমফল (সাওয়াব) বিনষ্ট করেননা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1356,9,121,'আর ছোট-বড় যা কিছু তারা ব্যয় করেছে, আর যত প্রান্তর তাদের অতিক্রম করতে হয়েছে, তৎসমুদয়ও তাদের নামে লিখিত হয়েছে, যেন আল্লাহ তাদের কৃতকর্মসমূহের অতি উত্তম বিনিময় প্রদান করেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1357,9,122,'আর মু’মিনদের এটাও সমীচীন নয় যে, (জিহাদের জন্য) সবাই একত্রে বের হয়ে পড়ে; সুতরাং এমন কেন করা হয়না যে, তাদের প্রত্যেকটি বড় দল হতে এক একটি ছোট দল (জিহাদে) বহির্গত হয়, যাতে অবশিষ্ট লোক ধর্মজ্ঞান অর্জন করতে থাকে, আর যাতে তারা নিজ কাওমকে (নাফরমানী হতে) ভয় প্রদর্শন করে যখন তারা তাদের নিকট প্রত্যাবর্তন করে, যেন তারা পরহেয করে চলতে পারে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1358,9,123,'হে মু’মিনগণ! ঐ কাফিরদের সাথে যুদ্ধ কর যারা তোমাদের আশেপাশে অবস্থান করছে, যেন তারা তোমাদের মধ্যে কঠোরতা খুঁজে পায়; আর জেনে রেখ যে, আল্লাহ পরহেযগারদের সাথে রয়েছেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1359,9,124,'আর যখন কোন সূরা অবতীর্ণ করা হয় তখন কেহ কেহ বলে, তোমাদের মধ্যে এই সূরা কার ঈমান বৃদ্ধি করল? অবশ্যই যে সব লোক ঈমান এনেছে, এই সূরা তাদের ঈমানকে বর্ধিত করেছে এবং তারাই আনন্দ লাভ করছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1360,9,125,'কিন্তু যাদের অন্তরসমূহে ব্যাধি রয়েছে, এই সূরা তাদের মধ্যে তাদের কলুষতার সাথে আরও কলুষতা বর্ধিত করেছে, আর তাদের কুফরী অবস্থায়ই মৃত্যু হয়েছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1361,9,126,'আর তারা কি লক্ষ্য করেনা যে, তারা প্রতি বছর একবার অথবা দু’বার কোন না কোন বিপদে পতিত হয়? তবুও তারা তাওবাহ করেনা, আর না তারা উপদেশ গ্রহণ করে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1362,9,127,'আর যখন কোন সূরা নাযিল করা হয় তখন তারা একে অপরের দিকে তাকাতে থাকে (এবং ইঙ্গিতে বলে) তোমাদেরকে কেহ দেখছেনা তো? অতঃপর তারা চলে যায়; আল্লাহ তাদের অন্তরগুলিকে (আলো থেকে) ফিরিয়ে দিয়েছেন, কারণ তারা হচ্ছে নির্বোধ সমাজ!');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1363,9,128,'তোমাদের নিকট আগমন করেছে তোমাদেরই মধ্যকার এমন একজন রাসূল যার কাছে তোমাদের ক্ষতিকর বিষয় অতি কষ্টদায়ক মনে হয়, যে হচ্ছে তোমাদের খুবই হিতাকাংখী, মু’মিনদের প্রতি বড়ই স্নেহশীল, করুণাপরায়ণ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1364,9,129,'অতঃপর যদি তারা মুখ ফিরিয়ে নেয় তাহলে তুমি বলে দাওঃ আমার জন্য আল্লাহই যথেষ্ট, তিনি ছাড়া অন্য কেহ মা‘বূদ নেই, আমি তাঁরই উপর নির্ভর করছি, আর তিনি হচ্ছেন অতি বড় আরশের মালিক।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1365,10,1,'আলিফ লাম রা, এটা হচ্ছে অতি সূক্ষ্ম তত্ত্বপূর্ণ কিতাবের আয়াত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1366,10,2,'লোকদের জন্য এটা কি বিস্ময়কর মনে হয়েছে যে, আমি তাদের মধ্য হতে একজনের নিকট অহী প্রেরণ করেছি এই মর্মে যে, তুমি সকলকে ভয় প্রদর্শন কর এবং যারা ঈমান এনেছে তাদেরকে এই সুসংবাদ দাও যে, তারা তাদের রবের নিকট পূর্ণ মর্যাদা লাভ করবে। কাফিরেরা বলতে লাগল যে, এ ব্যক্তিতো নিঃসন্দেহে প্রকাশ্য যাদুকর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1367,10,3,'নিশ্চয়ই আল্লাহই হচ্ছেন তোমাদের রাব্ব, যিনি আসমানসমূহকে এবং যমীনকে সৃষ্টি করেছেন ছয় দিনে, অতঃপর তিনি আরশে সমাসীন হলেন, তিনি প্রত্যেক কাজ পরিচালনা করে থাকেন। তাঁর অনুমতি ছাড়া সুপারিশ করার কেহ নেই; এমন আল্লাহ হচ্ছেন তোমাদের রাব্ব। অতএব তোমরা তাঁর ইবাদাত কর; তবুও কি তোমরা বুঝছনা?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1368,10,4,'তোমাদের সকলকে তাঁরই দিকে ফিরে যেতে হবে, আল্লাহর ও‘য়াদা সত্য; নিশ্চয়ই তিনিই প্রথমবার সৃষ্টি করেছেন, অতঃপর তিনিই পুনর্বার সৃষ্টি করবেন, যাতে এরূপ লোকদের যারা ঈমান এনেছে এবং ভাল কাজ করেছে তাদেরকে ইনসাফ মত প্রতিফল প্রদান করেন; আর যারা অবিশ্বাসী তারা পান করার জন্য পাবে উত্তপ্ত পানি এবং তাদের জন্য রয়েছে যন্ত্রনাদায়ক শাস্তি তাদের কুফরীর কারণে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1369,10,5,'আল্লাহ এমন, যিনি সূর্যকে দীপ্তিমান এবং চাঁদকে আলোকময় বানিয়েছেন এবং ওর (গতির) জন্য মানযিলসমূহ নির্ধারিত করেছেন যাতে তোমরা বছরসমূহের সংখ্যা ও হিসাব জানতে পার; আল্লাহ এসব বস্তু অযথা সৃষ্টি করেননি, তিনি এই প্রমাণসমূহ বিশদভাবে বর্ণনা করেছেন ঐসব লোকের জন্য যারা জ্ঞানবান।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1370,10,6,'নিঃসন্দেহে রাত ও দিনের পরিবর্তনের মধ্যে এবং আল্লাহ যা কিছু আসমানসমূহে ও যমীনে সৃষ্টি করেছেন তৎসমুদয়ের মধ্যে প্রমাণসমূহ রয়েছে ঐ লোকদের জন্য যারা আল্লাহর ভয় পোষণ করে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1371,10,7,'যারা আমার সাথে সাক্ষাতের আশা পোষণ করেনা এবং পার্থিব জীবনেই পরিতৃপ্ত এবং এতেই নিশ্চিন্ত থাকে এবং যারা আমার নিদর্শনাবলী সম্বন্ধে গাফিল ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1372,10,8,'এইরূপ লোকদের ঠিকানা হচ্ছে জাহান্নাম, তাদের কার্যকলাপের কারণে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1373,10,9,'নিশ্চয়ই যারা ঈমান এনেছে এবং ভাল কাজ করেছে, তাদের রাব্ব তাদেরকে লক্ষ্য স্থলে (জান্নাতে) পৌঁছে দিবেন তাদের ঈমানের কারণে, শান্তির উদ্যানসমূহে, তাদের (বাসস্থানের) তলদেশ দিয়ে নহরসমূহ বইতে থাকবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1374,10,10,'সেখানে তাদের বাক্য হবেঃ হে আল্লাহ! তুমি মহান, পবিত্র! এবং পরস্পরের অভিবাদন হবে সালাম (আসসালামু ‘আলাইকুম), আর তাদের দু‘আর শেষ বাক্য হবে ‘আলহামদুলিল্লাহি রাবিবল ‘আলামীন’ (সমস্ত প্রশংসা সারা জাহানের রাব্ব মহান আল্লাহর জন্য)।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1375,10,11,'আর আল্লাহ যদি মানুষের উপর ত্বরিত ক্ষতি সাধন করতেন, যেমন তারা ত্বরিত উপকার লাভ করতে আগ্রহ রাখে, তাহলে তাদের অঙ্গীকার কবেই পূর্ণ হয়ে যেত! অনন্তর আমি সেই লোকদেরকে, যারা আমার নিকট উপস্থিত হওয়ার চিন্তা করেনা, ছেড়ে দিই তাদের অবস্থার উপর, যেন তারা তাদের অবাধ্যতার মধ্যে ঘুরপাক খেতে থাকে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1376,10,12,'আর যখন মানুষকে কোন ক্লেশ স্পর্শ করে তখন আমাকে ডাকতে থাকে শুয়ে, বসে এবং দাঁড়িয়েও; অতঃপর যখন আমি তার সেই কষ্ট দূর করে দিই তখন সে নিজের পূর্বাবস্থায় ফিরে আসে। যে কষ্ট তাকে স্পর্শ করেছিল তা মোচন করার জন্য সে যেন আমাকে কখনও ডাকেইনি; এই সীমা লংঘনকারীদের কার্যকলাপ তাদের কাছে এইরূপই পছন্দনীয় মনে হয়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1377,10,13,'আমি তোমাদের পূর্বে বহু সম্প্রদায়কে ধ্বংস করেছি, যখন তারা যুলম করেছিল, অথচ তাদের নিকট তাদের রাসূলগণও প্রমাণাদীসহ আগমন করেছিল, কিন্তু কিছুতেই তারা ঈমান আনলনা। আর আমি অপরাধীদেরকে এই রূপেই শাস্তি দিয়ে থাকি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1378,10,14,'অতঃপর আমি তাদের স্থলে তোমাদেরকে তাদের পর ভূমন্ডলে আবাদ করলাম, যেন আমি প্রত্যক্ষ করি যে, তোমরা কি রূপ কাজ কর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1379,10,15,'আর যখন তাদের সামনে আমার আয়াতসমূহ পাঠ করা হয় - যা অতি স্পষ্ট, তখন ঐ সব লোক, যাদের আমার নিকট উপস্থিত হওয়ার চিন্তা নেই - এইরূপ বলেঃ এটা ছাড়া অন্য কোন কুরআন আনয়ন করুন অথবা এতেই কিছু পরিবর্তন করে দিন। বলঃ আমার দ্বারা ইহা পরিবর্তন করা সম্ভব নয়, আমিতো শুধুমাত্র উহারই অনুসরণ করব যা অহীযোগে আমার কাছে পৌঁছেছে। আমি যদি আমার রবের নাফরমানী করি তাহলে আমি এক অতি ভীষণ দিনের শাস্তির আশংকা রাখি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1380,10,16,'তুমি বলে দাওঃ যদি আল্লাহর ইচ্ছা হত তাহলে, না আমি তোমাদেরকে এটা পাঠ করে শোনাতাম আর না আল্লাহ তোমাদেরকে ওটা জানাতেন। কেননা আমি এর পূর্বেওতো জীবনের এক দীর্ঘ সময় তোমাদের মধ্যে অতিবাহিত করেছি; তাহলে কি তোমরা এতটুকু জ্ঞান রাখনা?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1381,10,17,'অতএব সেই ব্যক্তির চেয়ে অধিক অত্যাচারী (যালিম) কে যে ব্যক্তি আল্লাহর প্রতি মিথ্যা আরোপ করে অথবা তাঁর আয়াতসমূহকে মিথ্যা প্রতিপন্ন করে? নিঃসন্দেহে এমন পাপাচারীদের কিছুতেই মঙ্গল হবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1382,10,18,'আর তারা আল্লাহ ছাড়া এমন বস্তুসমূহের ইবাদাত করে যারা তাদের কোন অপকার করতে পারেনা এবং তাদের কোন উপকারও করতে পারেনা। আর তারা বলে, এরা হচ্ছে আল্লাহর নিকট আমাদের সুপারিশকারী। তুমি বলে দাওঃ তোমরা কি আল্লাহকে এমন বিষয়ের সংবাদ দিচ্ছ যা তিনি অবগত নন, না আসমানে, আর না যমীনে? তিনি পবিত্র এবং তাদের মুশরিকী কার্যকলাপ হতে তিনি অনেক উর্ধ্বে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1383,10,19,'আর সমস্ত মানুষ (প্রথম) এক উম্মাতই ছিল, অতঃপর তারা মতভেদ সৃষ্টি করল। আর যদি তোমার রবের পক্ষ হতে এক নির্দেশ বাণী প্রথমে সাব্যস্ত হয়ে না থাকত তাহলে যে বিষয়ে তারা মতভেদ করছে তার চূড়ান্ত মীমাংসা হয়ে যেত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1384,10,20,'আর তারা বলেঃ তার প্রতি তার রবের পক্ষ হতে কোন মু’জিযা কেন নাযিল হলনা? তুমি বলে দাওঃ গাইবের খবর শুধুমাত্র আল্লাহই জানেন। অতএব তোমরা প্রতীক্ষায় থাক, আমিও তোমাদের সাথে প্রতীক্ষায় থাকলাম।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1385,10,21,'আর যখন আমি মানুষকে কোন নি‘আমাতের স্বাদ উপভোগ করাই তাদের উপর কোন বিপদ পতিত হওয়ার পর, তখনই তারা আমার আয়াতসমূহ সম্বন্ধে দুরভিসন্ধি করতে থাকে। তুমি বলে দাওঃ আল্লাহ অতি দ্রুত কলাকৌশল তৈরী করতে পারেন। নিশ্চয়ই আমার মালাইকা/ফেরেশতাগণ তোমাদের সকল দুরভিসন্ধি লিপিবদ্ধ করছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1386,10,22,'তিনি এমন, যিনি তোমাদেরকে স্থলভাগে ও পানি পথে পরিভ্রমণ করান; যখন তোমরা নৌকায় অবস্থান কর, আর সেই নৌকাগুলি লোকদেরকে নিয়ে অনুকূল বায়ুর সাহায্যে চলতে থাকে, আর তারা তাতে আনন্দিত হয়, (হঠাৎ) তাদের উপর এক প্রচন্ড (প্রতিকূল) বায়ু এসে পড়ে এবং প্রত্যেক দিক হতে তাদের উপর তরঙ্গমালা ধেয়ে আসে, আর তারা মনে করে যে, তারা (বিপদে) বেষ্টিত হয়ে পড়েছে, (তখন) সকলে খাঁটি বিশ্বাসের সাথে আল্লাহকেই ডাকতে থাকেঃ (হে আল্লাহ!) আপনি যদি আমাদেরকে এটা হতে রক্ষা করেন তাহলে আমরা অবশ্যই কৃতজ্ঞ হয়ে যাব।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1387,10,23,'অতঃপর যখনই মা‘বূদ তাদেরকে উদ্ধার করেন তখনই তারা ভূপৃষ্ঠে অন্যায়ভাবে বিদ্রোহাচরণ করতে থাকে। হে লোক সকল! (শুনে রেখ), তোমাদের বিদ্রোহাচরণ তোমাদেরই (প্রাণের) জন্য বিপদ হবে, পার্থিব জীবনে (এটা দ্বারা কিছু ফল) ভোগ করে নাও, অতঃপর আমারই কাছে তোমাদেরকে ফিরে আসতে হবে, অতঃপর আমি তোমাদের যাবতীয় কৃতকর্ম তোমাদেরকে জানিয়ে দিব।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1388,10,24,'বস্তুতঃ পার্থিব জীবনের অবস্থাতো এরূপ, যেমন আমি আসমান হতে পানি বর্ষণ করলাম, অতঃপর তা দ্বারা উৎপন্ন হয় যমীনের উদ্ভিদগুলি অতিশয় ঘন হয়ে, যা মানুষ ও পশুরা আহার করে; এমন কি, যখন সেই যমীন নিজের সুদৃশ্যতার পূর্ণ রূপ ধারণ করল এবং তা শোভনীয় হয়ে উঠল, আর ওর মালিকরা মনে করল যে, তারা এখন ওর পূর্ণ অধিকারী হয়েছে, তখন দিনে অথবা রাতে ওর উপর আমার পক্ষ হতে কোন আপদ এসে পড়ল। সুতরাং আমি ওকে এমন নিশ্চিহ্ন করে দিলাম, যেন গতকাল ওর অস্তিত্বই ছিলনা। এরূপেই আয়াতগুলিতে আমি বিশদ রূপে বর্ণনা করি এমন লোকদের জন্য যারা চিন্তা ভাবনা করে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1389,10,25,'আর আল্লাহ তোমাদেরকে শান্তির নিবাসের দিকে আহবান করেন; এবং যাকে ইচ্ছা সরল পথে চলার ক্ষমতা দান করেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1390,10,26,'যারা সৎ কাজ করেছে তাদের জন্য উত্তম বস্তু (জান্নাত) রয়েছে; এবং অতিরিক্ত কিছুও বটে; আর না তাদের মুখমন্ডলকে মলিনতা আচ্ছন্ন করবে, আর না অপমান; তারাই হচ্ছে জান্নাতের অধিবাসী, তারা ওর মধ্যে অনন্তকাল থাকবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1391,10,27,'পক্ষান্তরে যারা মন্দ কাজ করেছে তারা তাদের মন্দ কাজের শাস্তি পাবে ওর অনুরূপ, এবং অপমান তাদেরকে আচ্ছাদিত করে নিবে; তাদেরকে আল্লাহ (এর শাস্তি) হতে কেহই রক্ষা করতে পারবেনা, যেন তাদের মুখমন্ডলকে আচ্ছাদিত করে দেয়া হয়েছে রাতের অন্ধকার স্তরসমূহ দ্বারা। এরা হচ্ছে জাহান্নামের অধিবাসী, তারা ওর মধ্যে অনন্ত কাল থাকবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1392,10,28,'আর সেই দিনটিও উল্লেখযোগ্য যেদিন আমি মুশরিকদেরকে একত্রিত করব, অতঃপর বলবঃ তোমরা ও তোমাদের নিরূপিত শরীকরা স্ব স্ব স্থানে অবস্থান কর, অতঃপর আমি তাদের মধ্যে পরস্পর বিচ্ছিন্ন করে দিব এবং তাদের সেই শরীকরা বলবেঃ তোমরাতো আমাদের ইবাদাত করতেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1393,10,29,'বস্তুতঃ আমাদের ও তোমাদের মধ্যে আল্লাহই হচ্ছেন যথোপযুক্ত সাক্ষী যে, আমরা তোমাদের ইবাদাত সম্বন্ধে অবগত ছিলামনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1394,10,30,'সেখানে প্রত্যেক ব্যক্তিই স্বীয় পূর্ব কৃতকর্মগুলি পরীক্ষা করে নিবে, এবং তাদেরকে আল্লাহর দিকে প্রত্যাবর্তিত করা হবে, যিনি তাদের প্রকৃত মালিক। আর যে সব মিথ্যা মা‘বূদ তারা বানিয়ে নিয়েছিল তারা সবাই তাদের থেকে দূরে সরে যাবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1395,10,31,'তুমি বলঃ তিনি কে, যিনি তোমাদেরকে আসমান ও যমীন হতে রিয্ক পৌঁছিয়ে থাকেন? অথবা কে তিনি, যিনি কর্ণ ও চক্ষুসমূহের উপর পূর্ণ অধিকার রাখেন? আর তিনি কে, যিনি জীবন্তকে প্রাণহীন হতে বের করেন, আর প্রাণহীনকে জীবন্ত হতে বের করেন? আর তিনি কে যিনি সমস্ত কাজ পরিচালনা করেন? তখন অবশ্যই তারা বলবেঃ আল্লাহ! অতএব তুমি বলঃ তাহলে কেন তোমরা (শির্‌ক হতে) নিবৃত্ত থাকছনা?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1396,10,32,'সুতরাং তিনিই হচ্ছেন আল্লাহ, যিনি তোমাদের প্রকৃত রাব্ব, অতএব সত্যের পর ভ্রষ্টতা ছাড়া আর কি রইল? তাহলে তোমরা (সত্যকে ছেড়ে) কোথায় ফিরে যাচ্ছ?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1397,10,33,'এভাবে সমস্ত অবাধ্য লোকদের সম্পর্কে তোমার রবের এই কথা সাব্যস্ত হয়ে গেল যে, তারা ঈমান আনবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1398,10,34,'(হে নাবী) তুমি বলঃ তোমাদের (নিরূপিত) শরীকদের মধ্যে এমন কেহ আছে কি যে প্রথমবারও সৃষ্টি করে এবং পুনরাবর্তন করতে পারে? তুমি বলে দাওঃ আল্লাহই প্রথমবারও সৃষ্টি করেন, অতঃপর তিনিই পুনর্বারও সৃষ্টি করবেন, অতএব তোমরা (সত্য হতে) কোথায় ফিরে যাচ্ছ?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1399,10,35,'তুমি বলঃ তোমাদের শরীকদের মধ্যে এমন কেহ আছে কি যে সত্য বিষয়ের সন্ধান দেয়? তুমি বলে দাও, আল্লাহই সত্য বিষয়ের পথ প্রদর্শন করেন; তাহলে কি যিনি সত্য বিষয়ের পথ প্রদর্শন করেন তিনি অনুসরণ করার সর্বাধিক যোগ্য, নাকি ঐ ব্যক্তি যে অন্যের পথ প্রদর্শন করা ছাড়া নিজেই পথ প্রাপ্ত হয়না? তাহলে তোমাদের কি হল? তোমরা কিরূপ সিদ্ধান্ত গ্রহণ করছ?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1400,10,36,'আর তাদের অধিকাংশ লোক শুধু অলীক কল্পনার পিছনে চলছে; নিশ্চয়ই অলীক কল্পনা বাস্তব ব্যাপারে মোটেই ফলপ্রসু নয়; নিশ্চয়ই আল্লাহ সবই জানেন, যা কিছু তারা করছে ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1401,10,37,'আর এই কুরআন কল্পনা প্রসূত নয় যে, আল্লাহ ছাড়া অন্য কারও দ্বারা প্রকাশিত হয়েছে, ইহাতো সেই কিতাবের সত্যতা প্রমাণকারী যা এর পূর্বে (নাযিল) হয়েছে, এবং আবশ্যকীয় বিধানসমূহের বিশদ বর্ণনাকারী, (এবং) এতে কোন সন্দেহ নেই (ইহা) বিশ্বের রবের পক্ষ হতে (নাযিল) হয়েছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1402,10,38,'তারা কি এরূপ বলে যে, এটা তার (নাবীর) স্বরচিত? তুমি বলে দাওঃ তাহলে তোমরা এর অনুরূপ একটি সূরা রচনা কর এবং গাইরুল্লাহ হতে যাকে ইচ্ছা ডেকে নাও, যদি তোমরা সত্যবাদী হও।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1403,10,39,'বরং তারা এমন বিষয়কে মিথ্যা সাব্যস্ত করছে, যাকে নিজ জ্ঞানের পরিধিতে আনয়ন করেনি, আর এখনো তাদের প্রতি ওর পরিণাম (আযাব) পৌঁছেনি; এরূপভাবে তারাও মিথ্যা সাব্যস্ত করেছিল, যারা তাদের পূর্বে গত হয়েছে। অতএব দেখ সেই অত্যাচারীদের পরিণাম কি হল?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1404,10,40,'আর তাদের মধ্যে এমন কতক লোক আছে, যারা এর প্রতি ঈমান আনবে এবং এমন কতক লোকও আছে যে, তারা এর প্রতি ঈমান আনবেনা, আর তোমার রাব্ব অত্যাচারীদেরকে ভাল রূপে জানেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1405,10,41,'আর (এতদসত্ত্বেও) যদি তারা তোমাকে মিথ্যা সাব্যস্ত করতে থাকে তাহলে তুমি বলে দাওঃ আমার কর্মফল আমি পাব, আর তোমাদের কর্মফল তোমরা পাবে। তোমরা আমার কৃতকর্মের জন্য দায়ী নও, আর আমিও তোমাদের কর্মের জন্য দায়ী নই।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1406,10,42,'আর তাদের কতক এমন আছে যারা তোমার (কথার) প্রতি কান পেতে রাখে। কিন্তু কি তুমি বধিরদেরকে শোনাবে, যদি তাদের বোধশক্তি না থাকে?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1407,10,43,'আর তাদের কতক এমনও আছে যারা তোমাকে দেখছে; তুমি কি অন্ধকে পথ দেখাতে পারবে, যদি তাদের অর্ন্তদৃষ্টি না থাকে?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1408,10,44,'নিশ্চয়ই আল্লাহ মানুষের প্রতি কোন যুলম করেননা, বরং মানুষ নিজেরাই নিজেদেরকে ধ্বংস করছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1409,10,45,'আর (ঐ দিনটি তাদেরকে স্মরণ করিয়ে দাও) যে দিন তিনি (আল্লাহ) তাদেরকে এইরূপ অবস্থায় একত্রিত করবেন যেন তারা পূর্ণ দিনের মুহুর্তকাল মাত্র অবস্থান করেছিল, এবং তারা পরস্পর পরস্পরকে চিনবে; বাস্তবিকই ক্ষতিগ্রস্ত হল ঐ সব লোক যারা আল্লাহর সাথে সাক্ষাত হওয়াকে মিথ্যা প্রতিপন্ন করেছে এবং তারা হিদায়াত প্রাপ্ত ছিলনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1410,10,46,'আর আমি তাদের সাথে যে শাস্তির অঙ্গীকার করছি, যদি ওর সামান্য অংশও তোমাকে দেখিয়ে দিই, অথবা তোমাকে মৃত্যু দান করি, সর্বাবস্থায় তাদেরকে আমারই পানে আসতে হবে, আর আল্লাহ তাদের সকল কৃতকর্মেরই খবর রাখেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1411,10,47,'প্রত্যেক উম্মাতের জন্য রাসূল রয়েছে, যখন তাদের সেই রাসূল (বিচার দিবসে) এসে পড়বে, (তখন) তাদের মীমাংসা করা হবে ন্যায়ভাবে, আর তাদের প্রতি কোন অবিচার করা হবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1412,10,48,'আর তারা বলেঃ (আমাদের) এই অঙ্গীকার কখন (সংঘটিত) হবে, যদি তোমরা সত্যবাদী হও?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1413,10,49,'তুমি বলে দাওঃ আল্লাহর ইচ্ছা ব্যতীত আমি আমার নিজের জন্যও কোন উপকার বা ক্ষতির অধিকারী নই, প্রত্যেক উম্মাতের (আযাবের) জন্য একটি নির্দিষ্ট সময় আছে; যখন তাদের সেই নির্দিষ্ট সময় এসে পৌঁছে তখন তারা মুহুর্তকাল না পশ্চাদপদ হতে পারবে, আর না অগ্রসর হতে পারবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1414,10,50,'তুমি বলে দাওঃ বল তো, যদি তোমাদের উপর আল্লাহর আযাব রাতে অথবা দিনে আসে তাহলে আযাবের মধ্যে এমন কোন্ জিনিস রয়েছে যা অপরাধীরা তাড়াতাড়ি চাচ্ছে?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1415,10,51,'তাহলে কি ওটা যখন এসেই পড়বে, তখন ওটা বিশ্বাস করবে? (বলা হবে) হ্যাঁ, এখন মেনে নিলে। অথচ তোমরা ওর জন্য তাড়াহুড়া করছিলে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1416,10,52,'অতঃপর যালিমদেরকে বলা হবেঃ চিরস্থায়ী শাস্তির স্বাদ গ্রহণ করতে থাক, তোমরাতো তোমাদেরই কৃতকর্মের ফল পাচ্ছ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1417,10,53,'তারা তোমাকে জিজ্ঞেস করছে, ওটা (শাস্তি) কি যথার্থ বিষয়? তুমি বলে দাওঃ হ্যাঁ, আমার রবের কসম! ওটা নিশ্চিত সত্য; আর তোমরা কিছুতেই আল্লাহকে অপারগ করতে পারবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1418,10,54,'আর যদি প্রত্যেক মুশরিকের কাছে এই পরিমাণ (মাল) থাকে যে, তা সমগ্র পৃথিবীর সম পরিমাণ হয় তাহলে সে তা দান করেও নিজের প্রাণ রক্ষা করতে চাবে; এবং যখন তারা আযাব দেখতে পাবে তখন (নিজেদের) অনুশোচনা প্রকাশ করতে সক্ষম হবেনা, আর তাদের ফাইসালা করা হবে ন্যায়ভাবে এবং তাদের প্রতি অবিচার করা হবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1419,10,55,'সাবধান! আসমানসমূহে এবং যমীনে যা কিছু আছে তা সবই আল্লাহর; সাবধান! আল্লাহর অঙ্গীকার সত্য, কিন্তু অধিকাংশ লোকই বিশ্বাস করেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1420,10,56,'তিনিই জীবন দান করেন এবং তিনিই মৃত্যু ঘটান, আর তোমরা সবাই তাঁরই কাছে প্রত্যাবর্তিত হবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1421,10,57,'(হে মানব জাতি!) তোমাদের কাছে তোমাদের রবের তরফ হতে এমন বিষয় সমাগত হয়েছে যা হচ্ছে নাসীহাত এবং অন্তরসমূহের সকল রোগের আরোগ্যকারী, আর মু’মিনদের জন্য ওটা পথ প্রদর্শক ও রাহমাত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1422,10,58,'তুমি বলে দাওঃ আল্লাহর এই দান ও রাহমাতের প্রতি সকলেরই আনন্দিত হওয়া উচিত; তা ওটা (পার্থিব সম্পদ) হতে বহু গুণে উত্তম যা তারা সঞ্চয় করছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1423,10,59,'তুমি বলঃ আল্লাহ তোমাদের জন্য যা কিছু রিয্ক পাঠিয়েছিলেন, অতঃপর তোমরা ওর কতক অংশ হারাম এবং কতক অংশ হালাল সাব্যস্ত করে নিলে; তুমি জিজ্ঞেস করঃ আল্লাহ কি তোমাদেরকে অনুমতি দিয়েছেন, না কি তোমরা আল্লাহর উপর মিথ্যা আরোপ করছ?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1424,10,60,'আর যারা আল্লাহর উপর মিথ্যা আরোপ করে তাদের কিয়ামাতের দিন সম্বন্ধে কি ধারণা? বাস্তবিক, মানুষের উপর আল্লাহর খুবই অনুগ্রহ রয়েছে, কিন্তু অধিকাংশ লোকই অকৃতজ্ঞ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1425,10,61,'আর তুমি যে অবস্থায়ই থাক না কেন, আর যে কোন অংশ হতে কুরআন পাঠ কর এবং তোমরা (অন্যান্য লোক) যে কাজই কর, আমার কাছে সব কিছুরই খবর থাকে, যখন তোমরা সেই কাজ করতে শুরু কর। কণা পরিমাণও কোন বস্তু তোমার রবের (জ্ঞানের) অগোচর নয় - না যমীনে, না আসমানে। আর তা হতে ক্ষুদ্রতর, কিংবা বৃহত্তর, সমস্তই সুস্পষ্ট কিতাবে (কুরআনে) লিপিবদ্ধ রয়েছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1426,10,62,'মনে রেখ, আল্লাহর বন্ধুদের না কোন আশংকা আছে, আর না তারা বিষন্ন হবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1427,10,63,'তারা হচ্ছে সেই লোক যারা ঈমান এনেছে এবং (পাপ হতে) পরহেয করে থাকে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1428,10,64,'তাদের জন্য সুসংবাদ রয়েছে পার্থিব জীবনে এবং পরকালেও। আল্লাহর বাক্যসমূহে কোন পরিবর্তন হয়না; এটা হচ্ছে বিরাট সফলতা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1429,10,65,'আর তোমাকে যেন তাদের উক্তিগুলি বিষণ্ণ না করে। সকল ক্ষমতা এবং ইয্যাত আল্লাহরই জন্য; তিনি শোনেন, জানেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1430,10,66,'মনে রেখ, যা কিছু আসমানসমূহে আছে এবং যা কিছু যমীনে আছে, এই সমস্তই আল্লাহর। আর যারা আল্লাহকে ছেড়ে অন্য শরীকদের ইবাদাত করে তারা কোন্ বস্তুর অনুসরণ করছে? তারা শুধু অবাস্তব খেয়ালের তাবেদারী করে চলছে এবং শুধু অনুমান প্রসূত কথা বলছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1431,10,67,'তিনি এমন যিনি তোমাদের জন্য রাত বানিয়েছেন, যেন তোমরা তাতে স্বস্তি লাভ কর। আর দিনকেও এভাবে সৃষ্টি করেছেন যে, তা হচ্ছে দেখাশোনার উপকরণ। ওতে (তাওহীদের) প্রমাণসমূহ রয়েছে তাদের জন্য যারা শোনে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1432,10,68,'তারা বলেঃ আল্লাহর সন্তান আছে, সুবহানাল্লাহ! তিনিতো কারও মুখাপেক্ষী নন। তাঁরই অধীনে রয়েছে যা কিছু আসমানসমূহে আছে এবং যা কিছু যমীনে আছে। তোমাদের কাছে এর (উক্ত দাবীর) কোন প্রমাণও নেই; আল্লাহ সম্বন্ধে কি তোমরা এমন কথা আরোপ করছো যা তোমাদের জানা নেই?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1433,10,69,'যারা আল্লাহর উপর মিথ্যা রচনা করে নিশ্চয়ই তারা সফলকাম হবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1434,10,70,'এটা দুনিয়ার সামান্য আরাম-আয়েশ মাত্র। অতঃপর আমারই দিকে তাদের ফিরে আসতে হবে, তখন আমি তাদেরকে তাদের কুফরীর কারণে কঠিন শাস্তির স্বাদ গ্রহণ করাব।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1435,10,71,'আর তুমি তাদেরকে নূহের ইতিবৃত্ত পড়ে শোনাও, যখন সে নিজের কাওমকে বললঃ হে আমার কাওম! যদি তোমাদের কাছে দুর্বহ মনে হয় আমার অবস্থান এবং আল্লাহর আদেশাবলীর নাসীহাত করা, তাহলে আমারতো আল্লাহরই উপর ভরসা। সুতরাং তোমরা তোমাদের (কল্পিত) শরীকদেরকে সঙ্গে নিয়ে নিজেদের ষড়যন্ত্র মাযবূত করে নাও, অতঃপর তোমাদের সেই তদবীর (গোপন ষড়যন্ত্র) যেন তোমাদের দুশ্চিন্তার কারণ না হয়, তারপর আমার সাথে (যা করতে চাও) করে ফেল, আর আমাকে মোটেই অবকাশ দিওনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1436,10,72,'অতঃপর যদি তোমরা পরোম্মুখই থাক তাহলে আমিতো তোমাদের কাছে কোন পারিশ্রমিক চাইনা, আমার পারিশ্রমিকতো শুধু আল্লাহরই যিম্মায় রয়েছে। আর আমাকে হুকুম করা হয়েছে, আমি যেন মুসলিমদের অন্তর্ভুক্ত থাকি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1437,10,73,'অতঃপর তারা তাকে মিথ্যা প্রতিপন্ন করতে থাকে; অতএব আমি তাকে এবং যারা তার সাথে নৌকায় ছিল তাদেরকে নাজাত দিলাম ও তাদেরকে আবাদ করলাম, আর যারা আমার আয়াতসমূহকে মিথ্যা সাব্যস্ত করেছিল তাদেরকে নিমজ্জিত করলাম। সুতরাং দেখ কি পরিণাম হয়েছিল তাদের যাদেরকে সাবধান করা হয়েছিল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1438,10,74,'আবার আমি তার পরে অপর রাসূলদেরকে তাদের সম্প্রদায়ের নিকট প্রেরণ করলাম। তারা তাদের নিকট মু’জিযা’সমূহ নিয়ে এলো। এতদসত্ত্বেও তারা পূর্বে যা মিথ্যা সাব্যস্ত করেছিল পরে তা মেনে নেয়ার ছিলনা; এভাবেই আল্লাহ কাফিরদের অন্তরসমূহে মোহর লাগিয়ে দেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1439,10,75,'অতঃপর আমি তাদের পর মূসা ও হারুনকে আমার মু’জিযা সহকারে ফির‘আউন ও তার প্রধানদের নিকট পাঠালাম, অতঃপর তারা অহংকার করল, আর সেই লোকগুলি ছিল পাপাচারীপরায়ণ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1440,10,76,'অতঃপর যখন তাদের প্রতি আমার সন্নিধান হতে প্রমাণ পৌঁছল তখন তারা বলতে লাগল, নিশ্চয়ই এটা সুস্পষ্ট যাদু।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1441,10,77,'মূসা বললঃ তোমরা কি এই যথার্থ প্রমাণ সম্পর্কে এমন কথা বলছ, যখন ওটা তোমাদের নিকট পৌঁছল? এটা কি যাদু? অথচ যাদুকররাতো সফলকাম হয়না!');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1442,10,78,'তারা বলতে লাগলঃ তুমি কি আমাদের নিকট এ জন্য এসেছ যে, আমাদেরকে সরিয়ে দাও সেই তরীকা হতে, যাতে আমরা আমাদের পূর্ব-পুরুষদেরকে পেয়েছি, আর পৃথিবীতে তোমাদের দু’জনের আধিপত্য স্থাপিত হয়ে যায়? আমরা তোমাদের দু’জনকে কখনও মানবনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1443,10,79,'এবং ফির‘আউন বললঃ আমার কাছে সমস্ত সুদক্ষ যাদুকরদের উপস্থিত কর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1444,10,80,'অতঃপর যখন যাদুকররা এলো, তখন মূসা তাদেরকে বললঃ নিক্ষেপ কর যা কিছু তোমরা নিক্ষেপ করতে চাও।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1445,10,81,'অতঃপর যখন তারা নিক্ষেপ করল তখন মূসা বললঃ যাদু এটাই, নিশ্চয়ই আল্লাহ এখনই এটাকে বানচাল করে দিবেন; (কেননা) আল্লাহ অশান্তি সৃষ্টিকারীদের কাজ সার্থক করেন না।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1446,10,82,'আর আল্লাহ তাঁর বাণী অনুযায়ী সত্যকে প্রতিষ্ঠিত করেন, যদিও পাপাচারীরা তা অপ্রীতিকর মনে করে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1447,10,83,'কিন্তু মূসার প্রতি তার কওমের ক্ষুদ্র একটি দল ছাড়া কেউ ঈমান আনল না, এ ভয়ে যে, ফিরআউন ও তাদের পারিষদবর্গ তাদেরকে ফিতনায় ফেলবে। আর নিশ্চয় ফির‘আউন ছিল যমীনে প্রতাপশালী এবং নিশ্চয় সে সীমালঙ্ঘনকারীদের অন্তর্ভুক্ত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1448,10,84,'আর মূসা বললঃ হে আমার সম্প্রদায়! যদি তোমরা আল্লাহর উপর ঈমান রাখ তাহলে তাঁরই উপর ভরসা কর, যদি তোমরা মুসলিম হও।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1449,10,85,'তারা বললঃ আমরা আল্লাহরই উপর ভরসা করলাম। হে আমাদের রাব্ব! আমাদেরকে এই যালিমদের লক্ষ্যস্থল বানাবেননা ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1450,10,86,'আর আমাদেরকে নিজ অনুগ্রহে এই কাফিরদের (কবল) হতে মুক্তি দিন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1451,10,87,'আর আমি মূসা ও তার ভাইয়ের প্রতি অহী পাঠালামঃ তোমরা উভয়ে তোমাদের এই লোকদের জন্য মিসরে বাসস্থান বহাল রাখ, আর (সালাতের সময়) তোমরা সবাই নিজেদের সেই গৃহগুলিকে সালাত আদায় করার স্থান রূপে গণ্য কর এবং সালাত কায়েম কর, আর মু’মিনদেরকে শুভ সংবাদ জানিয়ে দাও।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1452,10,88,'আর মূসা বললঃ হে আমাদের রাব্ব! আপনি ফির‘আউন ও তার প্রধানবর্গকে দান করেছেন জাঁকজমকের সামগ্রী এবং বিভিন্ন রকমের জীবনোপকরণ। হে আমাদের রাব্ব! এর ফলে তারা আপনার পথ হতে (মানব মন্ডলীকে) বিভ্রান্ত করছে। হে আমাদের রাব্ব! তাদের সম্পদগুলিকে নিশ্চিহ্ন করে দিন এবং তাদের অন্তরসমূহকে কঠিন করুন যাতে তারা ঈমান আনতে না পারে ঐ পর্যন্ত যতক্ষণ তারা যন্ত্রণাদায়ক আযাবকে প্রত্যক্ষ করে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1453,10,89,'তিনি (আল্লাহ) বললেনঃ তোমাদের উভয়ের দু‘আ কবূল করা হল। অতএব তোমরা দৃঢ় থেক এবং তাদের পথ অনুসরণ করনা যাদের জ্ঞান নেই।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1454,10,90,'আর আমি বানী ইসরাঈলকে সমুদ্র পার করে দিলাম, অতঃপর ফির‘আউন তার সৈন্যদলসহ তাদের পশ্চাদানুসরণ করল যুলম ও নির্যাতনের উদ্দেশে; এমনকি যখন সে নিমজ্জিত হতে লাগল তখন বলতে লাগলঃ আমি ঈমান এনেছি বানী ইসরাঈল যাঁর উপর ঈমান এনেছে, তিনি ছাড়া অন্য মা‘বূদ নেই এবং আমি মুসলিমদের অন্তর্ভুক্ত হচ্ছি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1455,10,91,'এখন ঈমান আনছ? অথচ পূর্ব (মুহুর্ত) পর্যন্ত তুমি নাফরমানী করছিলে এবং বিশৃংখলা সৃষ্টিকারীদের অন্তর্ভুক্ত ছিলে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1456,10,92,'অতএব আমি আজ তোমার লাশকে উদ্ধার করব, যেন তুমি তোমার পরবর্তী লোকদের জন্য উপদেশ গ্রহণের উপকরণ হয়ে থাক; আর প্রকৃত পক্ষে অনেক লোক আমার উপদেশাবলী হতে উদাসীন রয়েছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1457,10,93,'আর আমি বানী ইসরাঈলকে থাকার জন্য অতি উত্তম বাসস্থান প্রদান করলাম, আর আমি তাদেরকে আহার করার জন্য উৎকৃষ্ট বস্তুসমূহ দান করলাম, তারা মতভেদ করেনি ঐ পর্যন্ত যতক্ষণ তাদের নিকট (আহকামের) জ্ঞান পৌঁছল। নিঃসন্দেহে তোমার রাব্ব কিয়ামাত দিবসে তাদের মধ্যে সেই সব বিষয়ের মীমাংসা করবেন, যাতে তারা মতভেদ করছিল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1458,10,94,'অতঃপর (হে নাবী) যদি তুমি এ (কিতাব) সম্পর্কে সন্দিহান হও, যা আমি তোমার নিকট পাঠিয়েছি, তাহলে তুমি তাদেরকে জিজ্ঞেস করে দেখ, যারা তোমার পূর্বেকার কিতাবসমূহ পাঠ করে। নিঃসন্দেহে তোমার নিকট এসেছে তোমার রবের পক্ষ হতে সত্য কিতাব, সুতরাং তুমি কখনই সংশয়ীদের অন্তর্ভুক্ত হয়োনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1459,10,95,'আর ঐ সব লোকেরও অন্তর্ভুক্ত হয়োনা যারা আল্লাহর আয়াতগুলিকে মিথ্যা প্রতিপন্ন করেছে, যেন তুমি ধ্বংসপ্রাপ্তদের অন্তর্ভুক্ত না হও।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1460,10,96,'নিঃসন্দেহে যাদের সম্বন্ধে তোমার রবের বাক্য সাব্যস্ত হয়েছে, তারা কখনও ঈমান আনবেনা ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1461,10,97,'যদিও তাদের নিকট সমস্ত প্রমাণ পৌঁছে যায়, যে পর্যন্ত না তারা যন্ত্রনাদায়ক শাস্তি প্রত্যক্ষ করে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1462,10,98,'সুতরাং এমন কোন জনপদই ঈমান আনেনি যে, তাদের ঈমান আনা উপকারী হয়েছে, ইউনুসের কাওম ছাড়া। যখন তারা ঈমান আনল, তখন আমি তাদের থেকে পার্থিব জীবনের অপমানজনক শাস্তি বিদূরিত করলাম এবং তাদেরকে সুখ স্বাচ্ছন্দে থাকতে দিলাম এক নির্ধারিত কাল পর্যন্ত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1463,10,99,'আর যদি তোমার রাব্ব ইচ্ছা করতেন তাহলে বিশ্বের সকল লোকই ঈমান আনত। তাহলে তুমি কি মানুষের উপর যবরদস্তি করতে পার, যাতে তারা ঈমান আনেই?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1464,10,100,'অথচ আল্লাহর হুকুম ছাড়া কারও ঈমান আনা সম্ভব নয়; আর আল্লাহ নির্বোধ লোকদের উপর (কুফরীর) অপবিত্রতা স্থাপন করে দেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1465,10,101,'বলে দাওঃ তোমরা লক্ষ্য কর, যা কিছু রয়েছে আসমানসমূহে ও যমীনে; আর যারা ঈমান আনেনা, প্রমাণাদী ও ভয় প্রদর্শন তাদের কোন উপকার সাধন করতে পারেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1466,10,102,'অতএব তারা শুধু ঐ লোকদের অনুরূপ ঘটনাবলীর প্রতীক্ষা করছে যারা তাদের পূর্বে গত হয়েছে। তুমি বলে দাওঃ আচ্ছা তাহলে তোমরা ওর প্রতীক্ষায় থাক, আমিও তোমাদের সাথে প্রতীক্ষারতদের মধ্যে রইলাম।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1467,10,103,'শেষ পর্যন্ত আমি স্বীয় রাসূলদেরকে এবং মু’মিনদেরকে নাজাত দিলাম, এ রূপেই আমি মু’মিনদেরকে নাজাত দিয়ে থাকি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1468,10,104,'বলে দাওঃ হে লোকসকল! যদি তোমরা আমার দীন সম্বন্ধে সন্দিহান হও তাহলে আমি সেই মা‘বূদদের ইবাদাত করিনা, আল্লাহকে ছেড়ে তোমরা যাদের ইবাদাত কর; কিন্তু আমি সেই আল্লাহর ইবাদাত করি যিনি তোমাদের তোমাদের মৃত্যু ঘটান, আর আমাকে এই আদেশ করা হয়েছে যে, আমি যেন ঈমান আনয়নকারীদের দলভুক্ত থাকি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1469,10,105,'আর এটাও যে, নিজকে নিজে এই ধর্মের প্রতি এভাবে নিবিষ্ট করে রাখবে যে, অন্যান্য সকল তরীকা হতে পৃথক হয়ে যাও, আর কখনও মুশরিকদের অন্তর্ভুক্ত হয়োনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1470,10,106,'আর আল্লাহকে ছেড়ে এমন কিছুর ইবাদাত করনা যা না তোমার কোন উপকার করতে পারে, আর না কোন ক্ষতি করতে পারে। বস্তুতঃ যদি এরূপ কর তাহলে তুমি এমতাবস্থায় যালিমদের অন্তর্ভুক্ত হয়ে যাবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1471,10,107,'আল্লাহ যদি তোমাকে কোন কষ্টে নিপতিত করেন তাহলে তিনি ছাড়া কেহ তা মোচনকারী নেই, আর যদি তিনি তোমার প্রতি কোন কল্যাণ ও শান্তি পৌঁছাতে চান তাহলে তাঁর অনুগ্রহের কোন অপসারণকারী নেই; তিনি স্বীয় অনুগ্রহ নিজের বান্দাদের মধ্য হতে যাকে চান দান করেন; এবং তিনি অত্যন্ত ক্ষমাশীল, অতিশয় দয়ালু।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1472,10,108,'বলঃ হে লোক সকল! তোমাদের কাছে তোমাদের রবের পক্ষ হতে সত্য (ধর্ম) এসেছে, অতএব যে ব্যক্তি সঠিক পথে আসবে, বস্তুতঃ সে নিজের জন্যই পথে আসবে; আর যে ব্যক্তি পথভ্রষ্ট থাকবে তার পথভ্রষ্টতা তারই উপরে বর্তাবে, আর আমাকে (রাসূলকে) তোমাদের উপর দায়িত্বশীল করা হয়নি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1473,10,109,'আর তুমি তোমার প্রতি প্রেরিত অহীর অনুসরণ কর, আর ধৈর্য ধারণ কর এই পর্যন্ত যে, আল্লাহ মীমাংসা করে দেন, এবং তিনিই উত্তম মীমাংসাকারী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1474,11,1,'আলিফ লাম রা। এটি (কুরআন) এমন কিতাব যার আয়াতগুলি (প্রমাণাদী দ্বারা) মাযবূত করা হয়েছে। অতঃপর বিশদভাবে বর্ণনা করা হয়েছে; প্রজ্ঞাময়ের (আল্লাহর) পক্ষ হতে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1475,11,2,'এ (উদ্দেশে) যে, আল্লাহ ছাড়া কারও ইবাদাত করনা; আমি (নাবী) তাঁর (আল্লাহর) পক্ষ হতে তোমাদেরকে সতর্ককারী ও সুসংবাদদাতা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1476,11,3,'আর (এ উদ্দেশে) যে, তোমরা নিজেদের রবের নিকট ক্ষমা প্রার্থনা কর, অতঃপর তাঁর প্রতি নিবিষ্ট থাক। তিনি তোমাদেরকে সুখ-সম্ভোগ দান করবেন নির্দিষ্টকাল পর্যন্ত এবং প্রত্যেক অধিক ‘আমলকারীকে অধিক সাওয়াব দিবেন; আর যদি তোমরা মুখ ফিরাতেই থাক তাহলে আমি ভীষণ দিনের শাস্তির আশংকা করি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1477,11,4,'আল্লাহরই নিকট তোমাদেরকে ফিরে যেতে হবে এবং তিনি প্রত্যেক বস্তুর উপর পূর্ণ ক্ষমতা রাখেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1478,11,5,'জেনে রেখ, তারা কুঞ্চিত করে নিজেদের বক্ষকে, যেন নিজেদের কথাগুলি আল্লাহ হতে লুকাতে পারে। সাবধান, তারা যখন কাপড় (নিজেদের দেহে) জড়ায়, তিনি তখনও সব জানেন তারা যা কিছু গোপন করে অথবা প্রকাশ করে। নিশ্চয়ই তিনি অন্তরের কথাও জানেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1479,11,6,'আর ভূ-পৃষ্ঠে বিচরণকারী এমন কোন প্রাণী নেই যাদের রিয্ক আল্লাহর যিম্মায় না রয়েছে, আর তিনি প্রত্যেকের দীর্ঘ অবস্থানের স্থান এবং অল্প অবস্থানের স্থানকে জানেন, সবই কিতাবে মুবীনে (লাউহে মাহফুযে) রয়েছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1480,11,7,'আর তিনি এমন, যিনি সমস্ত আসমান ও যমীনকে সৃষ্টি করেছেন ছয় দিনে এবং সেই সময় তাঁর আরশ পানির উপর ছিল, যেন তোমাদেরকে পরীক্ষা করে নেন যে, তোমাদের মধ্যে উত্তম ‘আমলকারী কে? আর যদি তুমি বলঃ নিশ্চয়ই তোমাদেরকে মৃত্যুর পর জীবিত করা হবে, তখন যে সব লোক কাফির তারা বলেঃ এটাতো নিছক স্পষ্ট যাদু।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1481,11,8,'আর যদি আমি কিছু দিনের জন্য তাদের থেকে শাস্তিকে মুলতবী করে রাখি তাহলে তারা বলতে থাকেঃ সেই শাস্তিকে কিসে আটকে রেখেছে? স্মরণ রেখ, যেদিন ওটা তাদের উপর এসে পড়বে তখন তা কারও নিবারণে কিছুতেই নিবারিত হবেনা, আর যা নিয়ে তারা উপহাস করছিল তা এসে তাদেরকে ঘিরে নিবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1482,11,9,'আমি যদি মানুষকে আমার পক্ষ হতে করুণার স্বাদ গ্রহণ করাই, অতঃপর তার রাশ টেনে ধরি তাহলে সে নিরাশ ও অকৃতজ্ঞ হয়ে যায়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1483,11,10,'আর তাকে বিপদ-আপদ স্পর্শ করার পর আমি যদি তাকে নি‘আমাতের স্বাদ গ্রহণ করাই তখন সে বলতে শুরু করেঃ আমার সব দুঃখ কষ্ট দূর হয়ে গেল। (আর) সে গর্ব করতে থাকে, আত্মপ্রশংসা করতে থাকে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1484,11,11,'কিন্তু যারা ধৈর্য ধারণ করে ও ভাল কাজ করে এমন লোকদের জন্য রয়েছে ক্ষমা এবং বিরাট প্রতিদান।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1485,11,12,'তুমি কি অংশবিশেষ বর্জন করতে চাও ঐ নির্দেশাবলী হতে যা তোমার প্রতি অহী যোগে প্রেরিত হয়? আর তোমার মন সংকুচিত হয় এই কথায় যে, তারা বলেঃ তার প্রতি কোন ধন-ভান্ডার কেন নাযিল হলনা, অথবা কেন তার সাথে একজন (মালাইকা/ফেরেশতা) আসেনা? তুমিতো একজন সতর্ককারী মাত্র, আল্লাহই সবকিছু পরিবেষ্টনকারী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1486,11,13,'তাহলে কি তারা বলে যে, ওটা সে নিজেই রচনা করেছে? তুমি বলে দাওঃ তাহলে তোমরাও ওর অনুরূপ রচিত দশটি সূরা আনয়ন কর এবং (নিজ সাহায্যার্থে) যে সমস্ত গাইরুল্লাহকে ডাকতে পার ডেকে আন, যদি তোমরা সত্যবাদী হও।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1487,11,14,'অতঃপর যদি তারা তোমাদের ফরমায়েশ পূর্ণ করতে না পারে তাহলে তোমরা দৃঢ় বিশ্বাস রেখ যে, এই কুরআন অবতীর্ণ হয়েছে আল্লাহরই জ্ঞান (ও ক্ষমতা) দ্বারা; আর এটাও যে, আল্লাহ ছাড়া আর কোন ইলাহ নেই। তাহলে এখন তোমরা মুসলিম হবে কি?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1488,11,15,'যারা শুধু পার্থিব জীবন এবং ওর জাকজমকতা কামনা করে, আমি তাদের কৃতকর্মগুলির ফল দুনিয়ায়ই দিয়ে দিই, তাদের জন্য কিছুই কম করা হয়না।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1489,11,16,'এরা এমন লোক যে, তাদের জন্য আখিরাতে জাহান্নাম ছাড়া আর কিছুই নেই; আর তারা যা কিছু করছে তাও বিফল হবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1490,11,17,'তারা কি এমন ব্যক্তিদের সমান হতে পারে যারা কায়েম আছে তাদের রবের পক্ষ হতে প্রেরিত স্পষ্ট প্রমাণের উপর এবং যার কাছে তাঁর প্রেরিত এক সাক্ষী আবৃত্তি করে; এবং তাদের কাছে মূসার কিতাব রয়েছে, যা পথনির্দেশ ও রাহমাত স্বরূপ? এমন লোকেরাই এর প্রতি ঈমান রাখে। আর অন্যান্য সম্প্রদায়ের যে ইহা (কুরআন) অমান্য করবে, জাহান্নাম হবে তার প্রতিশ্রুত স্থান। অতএব তুমি কুরআন সম্পর্কে সন্দেহে পতিত হয়োনা, নিঃসন্দেহে এটি সত্য কিতাব তোমার রবের সন্নিধান হতে। কিন্তু অধিকাংশ লোক ঈমান আনেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1491,11,18,'আর ঐ ব্যক্তি অপেক্ষা অধিক অত্যাচারী (যালিম) কে হবে যে আল্লাহর প্রতি মিথ্যা আরোপ করে? এরূপ লোকদেরকে তাদের রবের সামনে পেশ করা হবে এবং সাক্ষী (মালাইকাগণ) বলবেঃ এরা ঐ লোক যারা নিজেদের রাব্ব সম্বন্ধে মিথ্যা আরোপ করেছিল। জেনে রেখ, এমন যালিমদের জন্য আল্লাহর লা’নত,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1492,11,19,'যারা অপরকে আল্লাহর পথ হতে নিবৃত্ত রাখত এবং ওতে বক্রতা বের করার চেষ্টায় লিপ্ত থাকত; আর তারাতো আখিরাতেও অমান্যকারী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1493,11,20,'তারা (সমগ্র) ভূ-পৃষ্ঠে আল্লাহকে অক্ষম করতে পারেনি, আর না তাদের জন্য আল্লাহ ছাড়া কেহ সহায়কও হল। এরূপ লোকদের জন্য দ্বিগুণ শাস্তি হবে; এরা (অবজ্ঞার কারণে আহকামসমূহ) না শুনতে সক্ষম হচ্ছিল, আর না তারা (সত্য পথ) দেখছিল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1494,11,21,'এরা সেই লোক যারা নিজেরা নিজেদের সর্বনাশ করে ফেলেছে, আর যে সব উপাস্য (দেবতা) তারা গড়ে রেখেছিল, তাদের দিক থেকে ওরা সবাই উধাও হয়ে গেছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1495,11,22,'এটা সুনিশ্চিত যে, আখিরাতে এরাই হবে সর্বাধিক ক্ষতিগ্রস্ত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1496,11,23,'নিশ্চয়ই যারা ঈমান এনেছে এবং সৎ কার্যাবলী সম্পন্ন করেছে, আর নিজেদের রবের প্রতি ঝুঁকে পড়েছে এরূপ লোকেরাই হচ্ছে জান্নাতবাসী, তাতে তারা অনন্তকাল থাকবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1497,11,24,'উভয় সম্প্রদায়ের দৃষ্টান্ত এরূপ যেমন এক ব্যক্তি, যে অন্ধ ও বধির, এবং আর এক ব্যক্তি যে দেখতেও পায় এবং শুনতেও পায়, এই দু’ ব্যক্তি কি তুলনায় সমান হবে? তবুও কি তোমরা বুঝনা?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1498,11,25,'আর আমি নূহকে তার কাওমের নিকট রাসূল রূপে প্রেরণ করেছি, (নূহ বলল) আমি তোমাদের জন্য স্পষ্ট ভয় প্রদর্শনকারী,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1499,11,26,'তোমরা আল্লাহ ছাড়া আর কারও ইবাদাত করনা; আমি তোমাদের উপর এক ভীষণ যন্ত্রণাদায়ক দিনের শাস্তির আশংকা করছি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1500,11,27,'অতঃপর তার সম্প্রদায়ের মধ্যে যে সব নেতৃস্থানীয় লোক কাফির ছিল তারা বলতে লাগলঃ আমরাতো তোমাকে আমাদেরই মত মানুষ দেখতে পাচ্ছি; আর আমরা দেখছি যে, শুধু ঐ লোকেরাই তোমার অনুসরণ করছে যারা আমাদের মধ্যে নিতান্তই হীন ও ইতর, কোন রকম চিন্তা-ভাবনা না করেই; আর আমাদের উপর তোমাদের কোন শ্রেষ্ঠত্বও আমরা দেখছিনা, বরং আমরা তোমাদেরকে মিথ্যাবাদী বলে মনে করছি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1501,11,28,'সে বললঃ হে আমার কাওম! আচ্ছা বলত, আমি যদি স্বীয় রবের পক্ষ হতে প্রমাণের উপর (প্রতিষ্ঠিত হয়ে) থাকি এবং তিনি আমাকে নিজ সন্নিধান হতে রাহমাত (নবুওয়াত) দান করেন, অতঃপর ওটা তোমাদের বোধগম্য না হয়, তাহলে কি ঐ বিষয়ে তোমাদের বাধ্য করতে পারি যখন তোমরা ওটা অবজ্ঞা করতে থাক?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1502,11,29,'আর হে আমার কাওম! আমি এতে তোমাদের কাছে কোন ধন সম্পদ চাচ্ছিনা; আমার বিনিময়তো শুধু আল্লাহর যিম্মায় রয়েছে, আর আমি এই মু’মিনদেরকে বের করে দিতে পারিনা; নিশ্চয়ই তারা নিজেদের রবের সমীপে গমনকারী, পরন্ত আমি তোমাদেরকে নির্বোধ কাওম রূপে দেখছি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1503,11,30,'হে আমার কাওম! আমি যদি তাদেরকে বের করেই দিই তাহলে আল্লাহর পাকড়াও হতে কে আমাকে রক্ষা করবে? তোমরা কি এতটুকু বুঝনা?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1504,11,31,'আর আমি তোমাদেরকে এ কথা বলছিনা যে, আমার নিকট আল্লাহর সকল ভান্ডার রয়েছে। এবং আমি অদৃশ্যের কথা জানিনা, আর আমি এটাও বলিনা যে, আমি মালাক/ফেরেশতা। আর যারা তোমাদের চোখে হীন, আমি তাদের সম্বন্ধে এটা বলতে পারিনা যে, আল্লাহ কখনও তাদেরকে কোন নি’আমাত দান করবেননা; তাদের অন্তরে যা কিছু আছে তা আল্লাহ উত্তম রূপে জানেন, আমি এরূপ বললে অন্যায়ই করে ফেলব।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1505,11,32,'তারা বললঃ হে নূহ! তুমি আমাদের সাথে বির্তক করেছ, অনন্তর সেই বির্তক অনেক বেশি করেছ। সুতরাং যে সম্বন্ধে তুমি আমাদেরকে ভয় দেখাচ্ছ তা আমাদের সামনে আনয়ন কর, যদি তুমি সত্যবাদী হও।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1506,11,33,'সে বললঃ ওটাতো আল্লাহ তোমাদের সামনে আনয়ন করবেন যদি তিনি ইচ্ছা করেন এবং তোমরা তাঁকে অক্ষম করতে পারবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1507,11,34,'আর আমার মঙ্গল কামনা (নাসীহত) করা তোমাদের উপকারে আসতে পারেনা, তা আমি তোমাদের যতই মঙ্গল কামনা করতে চাইনা কেন, যদি আল্লাহরই তোমাদেরকে পথভ্রষ্ট করার ইচ্ছা হয়। তিনিই তোমাদের রাব্ব, আর তাঁরই কাছে তোমাদেরকে ফিরে যেতে হবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1508,11,35,'তাহলে কি তারা (মাক্কার কাফিরেরা) বলে, সে (মুহাম্মাদ) এটা (কুরআন) নিজেই রচনা করেছে? তুমি বলে দাওঃ যদি আমি তা নিজে রচনা করে থাকি তাহলে আমার এই অপরাধ আমার উপর বর্তাবে, আর তোমরা যে অপরাধ করছ তা থেকে আমি সম্পূর্ণ মুক্ত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1509,11,36,'আর নূহের প্রতি অহী প্রেরিত হলঃ যারা ঈমান এনেছে তারা ছাড়া তোমার কাওম হতে আর কেহই ঈমান আনবেনা, অতএব যা তারা করছে তাতে তুমি মোটেই দুঃখ করনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1510,11,37,'আর তুমি আমার তত্ত্বাবধানে ও আমার নির্দেশক্রমে নৌকা নির্মাণ কর, আর আমার কাছে যালিমদের (কাফিরদের) সম্পর্কে কোন কথা বলনা, তাদের সকলকে নিমজ্জিত করা হবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1511,11,38,'সে নৌকা নির্মাণ করতে লাগল, আর যখনই তার কাওমের প্রধানদের কোন দল উহার নিকট দিয়ে গমন করত তখনই তার সাথে উপহাস করত। সে বলতঃ যদি তোমরা আমাদেরকে উপহাস কর তাহলে আমরাও (একদিন) তোমাদেরকে উপহাস করব, যেমন তোমরা আমাদেরকে উপহাস করছ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1512,11,39,'সুতরাং সত্ত্বরই তোমরা জানতে পারবে যে, কোন ব্যক্তির উপর এমন আযাব আসার উপক্রম হয়েছে যা তাকে লাঞ্ছিত করবে এবং তার উপর চিরস্থায়ী আযাব নাযিল হবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1513,11,40,'অবশেষে যখন আমার ফরমান এসে পৌঁছল এবং যমীন হতে পানি উথলে উঠতে লাগল, আমি বললামঃ প্রত্যেক শ্রেণীর প্রাণী হতে একটি নর এবং একটি মাদী অর্থাৎ দু’ দু’টি করে তাতে (নৌকায়) উঠিয়ে নাও এবং নিজ পরিবারবর্গকেও, তাকে ছাড়া যার সম্বন্ধে পূর্বে নির্দেশ হয়ে গেছে, এবং অন্যান্য মু’মিনদেরকেও। আর অল্প কয়েকজন ছাড়া কেহই তাঁর সাথে ঈমান আনেনি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1514,11,41,'আর সে বললঃ তোমরা এতে আরোহণ কর, এর গতি ও এর স্থিতি আল্লাহরই নামে; নিশ্চয়ই আমার রাব্ব ক্ষমাশীল, দয়াবান।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1515,11,42,'আর সেই নৌকাটি তাদেরকে নিয়ে পবর্ততুল্য তরঙ্গের মধ্যে চলতে লাগল, আর নূহ স্বীয় পুত্রকে ডাকতে লাগল এবং সে ছিল ভিন্ন স্থানে; হে আমার পুত্র! আমাদের সাথে সাওয়ার হয়ে যাও এবং কাফিরদের সাথে থেকনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1516,11,43,'সে বললঃ আমি এখনই কোন পাহাড়ে আশ্রয় গ্রহণ করব যা আমাকে পানি হতে রক্ষা করবে। সে (নূহ) বললঃ আজ আল্লাহর শাস্তি হতে কেহই রক্ষাকারী নেই, কিন্তু যার উপর তিনি দয়া করেন। ইতোমধ্যে তাদের উভয়ের মাঝে একটি তরঙ্গ অন্তরাল হয়ে পড়ল, অতঃপর সে নিমজ্জিতদের অন্তর্ভুক্ত হল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1517,11,44,'আর আদেশ হলঃ হে যমীন! স্বীয় পানি শুষে নাও, এবং হে আসমান! থেমে যাও। তখন পানি কমে গেল ও ঘটনার পরিসমাপ্তি ঘটল, আর নৌকা জূদী (পাহাড়) এর উপর এসে থামল। আর বলা হল, অন্যায়কারীরা আল্লাহর রাহমাত হতে দূরে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1518,11,45,'আর নূহ নিজ রাব্বকে ডাকল এবং বললঃ হে আমার রাব্ব! আমার এই পুত্রটি আমার পরিবারবর্গেরই অন্তর্ভুক্ত, আর আপনার ও‘য়াদাও সম্পূর্ণ সত্য এবং আপনি সমস্ত বিচারকের শ্রেষ্ঠ বিচারক।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1519,11,46,'তিনি (আল্লাহ) বললেনঃ হে নূহ! এই ব্যক্তি তোমার পরিবারের অন্তর্ভুক্ত নয়, সে অসৎ কর্মপরায়ণ। অতএব তুমি আমার কাছে এমন বিষয়ের আবেদন করনা যে সম্বন্ধে তোমার জ্ঞান নেই। আমি তোমাকে উপদেশ দিচ্ছি যে, তুমি অজ্ঞ লোকদের অন্তর্ভুক্ত হয়োনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1520,11,47,'সে বললঃ হে আমার রাব্ব! আমি আপনার নিকট এমন বিষয়ের আবেদন করা হতে আশ্রয় চাচ্ছি যে সম্বন্ধে আমার জ্ঞান নেই, আর আপনি যদি আমাকে ক্ষমা না করেন তাহলে আমি সম্পূর্ণ রূপে ধ্বংস হয়ে যাব।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1521,11,48,'বলা হলঃ হে নূহ! অবতরণ কর, আমার পক্ষ হতে সালাম ও বারাকাতসমূহ নিয়ে, যা তোমার উপর নাযিল করা হবে এবং সেই দলসমূহের উপর যারা তোমার সাথে রয়েছে; আর অনেক দল এরূপও হবে যাদেরকে আমি কিছুকাল (দুনিয়ার) সুখ স্বাচ্ছন্দ্য দান করব, অতঃপর তাদের উপর পতিত হবে আমার পক্ষ হতে কঠিন শাস্তি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1522,11,49,'এটা হচ্ছে গাইবি সংবাদসমূহের অন্তর্ভুক্ত, যা আমি তোমার কাছে অহী মারফত পৌঁছে দিচ্ছি। ইতোপূর্বে এটা না তুমি জানতে, আর না তোমার কাওম। অতএব তুমি ধৈর্য ধারণ কর; নিশ্চয়ই শুভ পরিণাম মুত্তাকীদের জন্যই।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1523,11,50,'আর ‘আদ (সম্প্রদায়) এর প্রতি তাদের ভাই হুদকে (রাসূল রূপে) প্রেরণ করলাম। সে বললঃ হে আমার কাওম! তোমরা আল্লাহর ইবাদাত কর, তিনি ছাড়া কেহ তোমাদের মা‘বূদ নেই; তোমরা শুধু মিথ্যা উদ্ভাবনকারী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1524,11,51,'হে আমার কাওম! আমি এর জন্য তোমাদের কাছে কোন বিনিময় চাইনা; আমার বিনিময় শুধু তাঁরই জিম্মায় রয়েছে যিনি আমাকে সৃষ্টি করেছেন। তবুও কি তোমরা বুঝনা?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1525,11,52,'আর হে আমার কাওম! তোমরা (তোমাদের পাপের জন্য) তোমাদের রবের নিকট ক্ষমা প্রার্থনা কর, অতঃপর তাঁরই প্রতি নিবিষ্ট হও। তিনি তোমাদের উপর প্রচুর বৃষ্টি বর্ষণ করবেন এবং তোমাদেরকে আরও শক্তি প্রদান করে তোমাদের শক্তিকে বর্ধিত করে দিবেন, আর তোমরা পাপে লিপ্ত হওয়ার জন্য মুখ ঘুরিয়ে নিওনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1526,11,53,'তারা বললঃ হে হুদ! তুমিতো আমাদের সামনে কোন প্রমাণ উপস্থাপন করনি এবং আমরা তোমার কথায় আমাদের উপাস্য দেবতাদেরকে বর্জন করতে পারিনা, আর আমরা কিছুতেই তোমার প্রতি বিশ্বাস স্থাপনকারী নই।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1527,11,54,'আমাদের কথা এই যে, আমাদের উপাস্য দেবতাদের মধ্য হতে কেহ তোমাকে দুর্দশায় ফেলে দিয়েছে। সে বললঃ আমি আল্লাহকে সাক্ষী রাখছি এবং তোমরাও সাক্ষী থেক যে, আমি তা থেকে মুক্ত, তোমরা যে ইবাদাতে শরীক সাব্যস্ত করছ,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1528,11,55,'তাঁর (আল্লাহর) সাথে। সুতরাং তোমরা সবাই মিলে আমার বিরুদ্ধে ষড়যন্ত্র করতে থাক, অতঃপর আমাকে সামান্য অবকাশ দিওনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1529,11,56,'আমি আল্লাহর উপর ভরসা করেছি, যিনি আমার রাব্ব এবং তোমাদেরও রাব্ব; ভূ-পৃষ্ঠে যত বিচরণকারী রয়েছে সবাই তাঁর মুষ্টিতে আবদ্ধ; নিশ্চয়ই আমার রাব্ব সরল পথে রয়েছেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1530,11,57,'অতঃপর যদি তোমরা ফিরে যাও তাহলে আমাকে যে বার্তা দিয়ে তোমাদের প্রতি পাঠানো হয়েছে, আমি ওটা তোমাদের কাছে পৌঁছিয়েছি; আর আমার রাব্ব ভূ-পৃষ্ঠে তোমাদের পরিবর্তে অন্য লোকদেরকে আবাদ করবেন এবং তোমরা তাঁর কোনই ক্ষতি করতে পারবেনা। নিশ্চয়ই আমার রাব্ব প্রত্যেক বস্তুর রক্ষণাবেক্ষণ করে থাকেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1531,11,58,'আর যখন আমার (শাস্তির) হুকুম এসে পৌঁছল তখন আমি হুদকে এবং যারা তার সাথে ঈমানদার ছিল তাদেরকে স্বীয় অনুগ্রহে রক্ষা করলাম, আর তাদেরকে বাঁচালাম অতি কঠিন শাস্তি হতে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1532,11,59,'আর তারা ছিল ‘আদ সম্প্রদায়. যারা নিজের রবের নিদর্শনগুলিকে অস্বীকার করল এবং রাসূলদেরকে অমান্য করল, পক্ষান্তরে তারা প্রত্যেক উদ্ধত স্বৈরাচারীর নির্দেশ অনুসরণ করত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1533,11,60,'আর এই দুনিয়ায়ও অভিসম্পাত তাদের সঙ্গে রইল এবং কিয়ামাত দিবসেও; ভাল রূপে জেনে রেখ! ‘আদ নিজ রবের সাথে কুফরী করল; আরও জেনে রেখ! দূরে পড়ে রইল ‘আদ, রাহমাত হতে, যারা হুদের কাওম ছিল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1534,11,61,'আর আমি সামূদ(সম্প্রদায়) এর নিকট তাদের ভাই সালিহকে নাবী রূপে প্রেরণ করলাম। সে বললঃ হে আমার কাওম! তোমরা আল্লাহর ইবাদাত কর, তিনি ছাড়া কেহ তোমাদের মা‘বূদ নেই, তিনি তোমাদেরকে যমীন হতে সৃষ্টি করেছেন এবং তোমাদেরকে তাতে আবাদ করেছেন। অতএব তোমরা তাঁর কাছে ক্ষমা প্রার্থনা কর, অতঃপর মনোনিবেশ কর তাঁরই দিকে; নিশ্চয়ই আমার রাব্ব নিকটে রয়েছেন এবং তিনি আবেদন গ্রহণকারী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1535,11,62,'তারা বললঃ হে সালিহ! তুমিতো ইতোপূর্বে আমাদের মধ্যে আশা-ভরসা স্থল ছিলে। তুমি কি আমাদেরকে ঐ বস্তুর ইবাদাত করতে নিষেধ করছ যাদের ইবাদাত আমাদের পিতৃ-পুরুষেরা করে এসেছে? আর যে ধর্মের দিকে তুমি আমাদের ডাকছ, বস্তুতঃ আমরা তৎসম্বন্ধে গভীর সন্দেহের মধ্যে রয়েছি, যা আমাদেরকে দ্বিধা-দ্বন্দ্বে ফেলে রেখেছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1536,11,63,'সে বললঃ হে আমার কাওম! আচ্ছা বলত, আমি যদি নিজ রবের পক্ষ হতে প্রমাণের উপর থাকি (এবং) তিনি আমার প্রতি নিজের রাহমাত (নবুওয়াত) দান করে থাকেন, আমি যদি আল্লাহর কথা না মানি তাহলে আমাকে আল্লাহ (শাস্তি) হতে কে রক্ষা করবে? তাহলেতো তোমরা শুধু আমার ক্ষতিই করছ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1537,11,64,'আর হে আমার কাওম! এটা হচ্ছে আল্লাহর উষ্ট্রী যা তোমাদের জন্য নিদর্শন। অতএব ওকে ছেড়ে দাও যেন আল্লাহর যমীনে চরে খায়, আর ওকে খারাপ উদ্দেশে স্পর্শ করনা, অন্যথায় তোমাদেরকে আকস্মিক শাস্তি এসে পাকড়াও করতে পারে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1538,11,65,'অনন্তর তারা ওকে মেরে ফেলল। তখন সে বললঃ তোমরা নিজেদের ঘরে আরও তিন দিন বাস করে নাও; এটা ও‘য়াদা, যাতে বিন্দুমাত্র মিথ্যা নেই।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1539,11,66,'অতঃপর যখন আমার হুকুম এসে পৌঁছল, আমি সালিহকে এবং যারা তার সাথে ঈমানদার ছিল তাদেরকে নিজ অনুগ্রহে রক্ষা করলাম, আর বাঁচালাম সেই দিনের বড় লাঞ্ছনা হতে; নিশ্চয়ই তোমার রাব্ব শক্তিমান, পরাক্রমশালী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1540,11,67,'আর সেই যালিমদেরকে এক প্রচন্ড ধ্বনি এসে আক্রমন করল যাতে তারা নিজ নিজ গৃহে উপুড় হয়ে পড়ে রইল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1541,11,68,'যেন তারা সেই গৃহগুলিতে কখনও বসবাস করেনি। ভাল রূপে জেনে রেখ! ছামূদ সম্প্রদায় নিজ রবের সাথে কুফরী করল। জেনে রেখ, ছামূদ সম্প্রদায় রাহমাত হতে দূরে ছিটকে পড়ল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1542,11,69,'আর আমার প্রেরিত মালাইকা/ফেরেশতা ইবরাহীমের নিকট সুসংবাদ নিয়ে আগমন করল। (এবং) তারা বললঃ সালাম! ইবরাহীম বললঃ সালাম! অতঃপর অনতি বিলম্বে একটা ভাজা গো-বৎস আনয়ন করল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1543,11,70,'কিন্তু যখন সে দেখল যে, তাদের হাত সেই খাদ্যের দিকে অগ্রসর হচ্ছেনা তখন তাদেরকে অদ্ভুত ভাবতে লাগল এবং মনে মনে তাদের থেকে শংকিত হল। (এ দেখে) তারা বললঃ ভয় করবেননা, আমরা লূত সম্প্রদায়ের প্রতি প্রেরিত হয়েছি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1544,11,71,'আর তার স্ত্রী দন্ডায়মান ছিল, সে হেসে উঠলো। তখন আমি তাকে (ইবরাহীমের স্ত্রীকে) সুসংবাদ দিলাম ইসহাকের এবং ইসহাকের পর ইয়াকূবের।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1545,11,72,'সে বললঃ হায় কপাল! এখন আমি সন্তান প্রসব করব বৃদ্ধা হয়ে! আর আমার এই স্বামী অতি বৃদ্ধ। বাস্তবিক এটাতো একটা বিস্ময়কর ব্যাপার!');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1546,11,73,'তারা (মালাক) বললঃ আপনি কি আল্লাহর কাজে বিস্ময় বোধ করছেন? (হে) এই পরিবারের লোকেরা! আপনাদের প্রতি রয়েছে আল্লাহর রাহমাত ও বারাকাত; নিশ্চয়ই তিনি সমস্ত প্রশংসার যোগ্য, মহিমান্বিত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1547,11,74,'অতঃপর যখন ইবরাহীমের সেই ভয় দূর হয়ে গেল এবং সে সুসংবাদ প্রাপ্ত হল তখন আমার প্রেরিত মালাইকার সাথে লূতের কাওম সম্বন্ধে তর্ক-বিতর্ক (জোর সুপারিশ) করতে শুরু করল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1548,11,75,'বাস্তবিক ইবরাহীম ছিল বড় সহিষ্ণু প্রকৃতির, দয়ালু স্বভাব, কোমল হৃদয়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1549,11,76,'হে ইবরাহীম! এ কথা ছেড়ে দাও, তোমার রবের আদেশ এসে গেছে এবং তাদের উপর এমন এক শাস্তি আসছে যা কিছুতেই প্রতিহত করার নয়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1550,11,77,'আর যখন আমার ঐ মালাইকাগণ লূতের নিকট উপস্থিত হল তখন সে তাদের জন্য চিন্তিত হয়ে পড়ল এবং সেই কারণে অন্তর সংকুচিত হল, আর বললঃ আজকের দিনটি অতি কঠিন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1551,11,78,'আর তার কাওম তার কাছে ছুটে এলো, এবং তারা পূর্ব হতে কু-কার্যসমূহ করেই আসছিল। লূত বললঃ হে আমার কাওম! (তোমাদের ঘরে) আমার এই কন্যারা রয়েছে, এরা তোমাদের জন্য অতি উত্তম, অতএব তোমরা আল্লাহকে ভয় কর এবং আমাকে আমার মেহমানদের সামনে অপমানিত করনা; তোমাদের মধ্যে কি সুবোধ লোক কেহ নেই?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1552,11,79,'তারা বললঃ তুমিতো অবগত আছ যে, তোমার এই কন্যাগুলির আমাদের কোন প্রয়োজন নেই, আর আমাদের অভিপ্রায় কি তাও তোমার জানা আছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1553,11,80,'সে বললঃ কি উত্তম হত যদি তোমাদের উপর আমার কিছু ক্ষমতা চলত, অথবা আমি কোন দৃঢ় স্তম্ভের আশ্রয় নিতাম!');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1554,11,81,'তারা (মালাইকা) বললঃ হে লূত! আমরাতো আপনার রবের প্রেরিত বার্তাবাহক, তারা কখনও আপনার নিকট পৌঁছতে পারবেনা, অতএব আপনি রাতের কোন এক ভাগে নিজের পরিবারবর্গকে নিয়ে চলে যান, আপনাদের কেহ যেন পিছনের দিকে ফিরেও না চায়; কিন্তু হ্যাঁ, আপনার স্ত্রী যাবেনা, তার উপরও ঐ আপদ আসবে যা অন্যান্যদের প্রতি আসবে, তাদের (শাস্তির) অঙ্গীকার কৃত সময় হচ্ছে প্রাতঃকাল, প্রভাত কি নিকটবর্তী নয়?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1555,11,82,'অতঃপর যখন আমার হুকুম এসে পৌঁছল, আমি ঐ ভূ-খন্ডের উপরি ভাগকে নীচে করে দিলাম এবং ওর উপর ঝামা পাথর বর্ষণ করতে লাগলাম, যা একাধারে ছিল (বর্ষিত হচ্ছিল),');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1556,11,83,'যা বিশেষ চিহ্নিত করা ছিল তোমার রবের নিকট; আর ঐ জনপদগুলি এই যালিমদের হতে বেশি দূরে নয়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1557,11,84,'আর আমি মাদইয়ানের (অধিবাসীদের) প্রতি তাদের ভাই শু‘আইবকে প্রেরণ করলাম। সে বললঃ হে আমার কাওম! তোমরা আল্লাহর ইবাদাত কর, তিনি ছাড়া আর কেহ তোমাদের ইলাহ নেই; আর তোমরা পরিমাপে ও ওযনে কম করনা। আমি তোমাদেরকে স্বচ্ছল দেখতে পাচ্ছি, আর আমি তোমাদের প্রতি এমন এক দিনের শাস্তির ভয় করছি যা নানাবিধ বিপদের সমষ্টি হবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1558,11,85,'আর হে আমার কাওম! তোমরা পরিমাপ ও ওযনকে পুরাপুরিভাবে সম্পন্ন কর এবং লোকদেরকে তাদের দ্রব্যাদিতে কম করনা, আর ভূ-পৃষ্ঠে ফাসাদ সৃষ্টি করে সীমা অতিক্রম করনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1559,11,86,'আল্লাহ প্রদত্ত যা অবশিষ্ট থাকে তা’ই তোমাদের জন্য অতি উত্তম যদি তোমাদের বিশ্বাস হয়, আর আমি তোমাদের পাহারাদার নই।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1560,11,87,'তারা বললঃ হে শু‘আইব! তোমার ধর্মনিষ্ঠা কি তোমাকে এই শিক্ষা দিচ্ছে যে, আমরা ঐ সব উপাস্য বর্জন করি যাদের উপাসনা আমাদের পিতৃ-পুরুষরা করে আসছে? অথবা এটা বর্জন করতে বল যে, আমরা নিজেদের মালে নিজেদের ইচ্ছানুসারে ব্যবস্থা অবলম্বন করি? বাস্তবিকই তুমি হচ্ছ বড় সহিষ্ণু, সদাচারী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1561,11,88,'সে বললঃ হে আমার কাওম! আচ্ছা বলত, যদি আমি আমার রবের পক্ষ হতে প্রমাণের উপর প্রতিষ্ঠিত থাকি এবং তিনি আমাকে নিজ সন্নিধান হতে একটি উত্তম সম্পদ (নবুওয়াত) দান করেন, তাহলে আমি কি রূপে প্রচার না করে পারি? আর আমি এটা চাইনা যে, আমি তোমাদের বিপরীত সেই সব কাজ করি যা হতে তোমাদেরকে নিষেধ করছি; আমিতো সংশোধন করে দিতে চাচ্ছি, যে পর্যন্ত আমার সাধ্যে হয়, আর আমার যা কিছু প্রচেষ্টা তা শুধু আল্লাহরই সাহায্যে হয়ে থাকে; আমি তাঁরই উপর ভরসা রাখি এবং তাঁরই দিকে প্রত্যাবর্তন করি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1562,11,89,'আর হে আমার কাওম! আমার প্রতি তোমাদের জন্য হটকারিতা যেন এই কারণ না হয়ে পড়ে যে, তোমাদের উপর সেই রূপ বিপদসমূহ এসে পড়ে যেমন নূহের কাওম অথবা হুদের কাওম অথবা সালিহর কাওমের উপর পতিত হয়েছিল; আর লূতের কাওমতো তোমাদের হতে দূরে (যুগে) নয়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1563,11,90,'আর তোমরা তোমাদের (পাপের জন্য) রবের নিকট ক্ষমা প্রার্থনা কর, অতঃপর তাঁর দিকে মনোনিবেশ কর; নিশ্চয়ই আমার রাব্ব পরম দয়ালু, অতি প্রেমময়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1564,11,91,'তারা বললঃ হে শু‘আইব! তোমার বর্ণিত অনেক কথা আমাদের বুঝে আসেনা এবং আমরা নিজেদের মধ্যে তোমাকে দুর্বল দেখছি, আর যদি তোমার প্রতি তোমার স্বজনবর্গের লক্ষ্য না থাকতো তাহলে আমরা তোমাকে প্রস্তরাঘাতে চূর্ণ করে ফেলতাম, আর আমাদের নিকট তোমার কোনই মর্যাদা নেই।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1565,11,92,'সে বললঃ হে আমার কাওম! আমার পরিজনবর্গ কি তোমাদের কাছে আল্লাহ অপেক্ষাও অধিক মর্যাদাবান? আর তোমরা তাঁকে পশ্চাতে ফেলে রেখেছ? নিশ্চয়ই আমার রাব্ব তোমাদের সমস্ত কার্যকলাপকে বেষ্টন করে আছেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1566,11,93,'আর হে আমার কাওম! তোমরা নিজেদের অবস্থায় কাজ করতে থাক, আমিও (আমার) কাজ করছি। সত্ত্বরই তোমরা জানতে পারবে যে, কে সেই ব্যক্তি যার উপর এমন শাস্তি আসন্ন যা তাকে অপমানিত করবে এবং কে সেই ব্যক্তি যে মিথ্যাবাদী ছিল; আর তোমরা প্রতীক্ষায় থাক, আমিও তোমাদের সাথে প্রতীক্ষায় রইলাম।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1567,11,94,'(আল্লাহ বলেন) আর যখন আমার হুকুম এসে পৌঁছল তখন আমি মুক্তি দিলাম শু‘আইবকে, আর যারা তার সাথে ঈমানদার ছিল তাদেরকে নিজ রাহমাতে এবং ঐ যালিমদেরকে আক্রমণ করল এক বিকট গর্জন। অতঃপর তারা নিজ গৃহে উপুড় হয়ে পড়ে রইল,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1568,11,95,'যেন তারা এই গৃহগুলিতে বাস করেনি। ভাল রূপে জেনে নাও, রাহমাত হতে দূরে সরে পড়ল মাদইয়ান, যেমন দূর হয়েছিল ছামূদ (সম্প্রদায়) রাহমাত হতে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1569,11,96,'এবং আমি মূসাকে প্রেরণ করলাম আমার মু’জিযাসমূহ ও সুস্পষ্ট প্রমাণ সহকারে –');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1570,11,97,'ফির‘আউন ও তার প্রধানদের নিকট। অতঃপর তারাও ফির‘আউনের মতানুসারে চলতে রইল এবং ফির‘আউনের কোন কথা মোটেই সঠিক ছিলনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1571,11,98,'কিয়ামাত দিবসে সে নিজ সম্প্রদায়ের আগে আগে থাকবে, অতঃপর তাদেরকে উপনীত করবেন জাহান্নামে, আর তা অতি নিকৃষ্ট স্থান, যাতে তারা উপনীত হবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1572,11,99,'এই দুনিয়ায় তাদেরকে করা হয়েছিল অভিশাপগ্রস্ত এবং অভিশাপগ্রস্ত হবে তারা কিয়ামাতের দিনেও। কত নিকৃষ্ট সেই পুরস্কার যা তারা লাভ করবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1573,11,100,'এটা ছিল এই যে, জনপদসমূহের কতিপয় অবস্থা যা আমি তোমার নিকট বর্ণনা করছি, ওগুলির মধ্যে কোন কোনটির সম্পূর্ণ বিলুপ্তি ঘটেছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1574,11,101,'আমি তাদের প্রতি অত্যাচার করিনি, কিন্তু তারা নিজেরাই নিজেদের উপর অত্যাচার করেছে। বস্তুতঃ তাদের কোনই উপকার করেনি তাদের সেই উপাস্যগুলি যাদের তারা ইবাদাত করত আল্লাহকে ছেড়ে, যখন এসে পৌঁছল তোমার রবের হুকুম; তাদের ক্ষতি সাধন ছাড়া তারা আর কোনো কিছুই বৃদ্ধি করলনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1575,11,102,'‘এরূপই তোমার রবের পাকড়াও। তিনি কোন জনপদের অধিবাসীদের পাকড়াও করেন যখন তারা অত্যাচার করে। নিঃসন্দেহে তাঁর পাকড়াও হচ্ছে অত্যন্ত যন্ত্রনাদায়ক, কঠোর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1576,11,103,'এসব ঘটনায় সেই ব্যক্তিদের জন্য বড় উপদেশ রয়েছে যারা পরকালের শাস্তিকে ভয় করে; ওটা এমন একটি দিন হবে যেদিন সমস্ত মানুষকে সমবেত করা হবে এবং ওটা হল সকলের উপস্থিতির দিন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1577,11,104,'আর আমি ওটা শুধু সামান্য কালের জন্য স্থগিত রেখেছি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1578,11,105,'যখন সেই দিন আসবে তখন কোন ব্যক্তি আমার অনুমতি ছাড়া কথাও বলতে পারবেনা। অতঃপর তাদের মধ্যে কতক দুর্ভাগা হবে এবং কতক হবে ভাগ্যবান।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1579,11,106,'অতএব যারা দুর্ভাগা হবে তারাতো জাহান্নামে এরূপ অবস্থায় থাকবে যে, তাতে তাদের চীৎকার ও আর্তনাদ হতে থাকবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1580,11,107,'তারা অনন্তকাল সেখানে থাকবে, যে পর্যন্ত আসমান ও যমীন স্থায়ী থাকে। তবে যদি আল্লাহর ইচ্ছা হয় তাহলে ভিন্ন কথা; নিশ্চয়ই তোমার রাব্ব যা কিছু চান তা তিনি পূর্ণরূপে সমাধা করতে পারেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1581,11,108,'পক্ষান্তরে যারা হয়েছে ভাগ্যবান, বস্তুতঃ তারা থাকবে জান্নাতে (এবং) তাতে তারা অনন্ত কাল থাকবে, যে পর্যন্ত আসমান ও যমীন স্থায়ী থাকে, কিন্তু যদি আল্লাহর ইচ্ছা হয় তাহলে ভিন্ন কথা; ওটা অফুরন্ত দান হবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1582,11,109,'সুতরাং এরা যার উপাসনা করে ওর সম্বন্ধে তুমি এতটুকুও সংশয় বোধ করনা; তারাও ঠিক সেই রূপেই ইবাদাত করছে যে রূপে তাদের পূর্ব-পুরুষরা ইবাদাত করত। এবং নিশ্চয়ই আমি তাদেরকে তাদের (শাস্তির) অংশ পূর্ণভাবে দিয়ে দিব, একটুও কম না করে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1583,11,110,'আর আমি মূসাকে কিতাব দিয়েছিলাম, অতঃপর ওতে মতভেদ করা হল। আর যদি একটি উক্তি তোমার রবের পক্ষ হতে পূর্বেই স্থিরীকৃত হয়ে না থাকত তাহলে ওদের চুড়ান্ত মীমাংসা হয়ে যেত। এবং এই লোকেরা এর সম্বন্ধে এমন সন্দেহে (পতিত) আছে যা তাদেরকে দ্বিধা দ্বন্দ্বে ফেলে রেখেছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1584,11,111,'আর নিশ্চিত এই যে, তোমার রাব্ব তাদেরকে তাদের কর্মের পূর্ণ অংশ প্রদান করবেন; নিশ্চয়ই তিনি তাদের কার্যকলাপের পূর্ণ খবর রাখেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1585,11,112,'অতএব তুমি যেভাবে আদিষ্ট হয়েছ, দৃঢ় থাক এবং সেই লোকেরাও যারা কুফরী হতে তাওবাহ করে তোমার সাথে রয়েছে, আর (ধর্মের) গন্ডি হতে একটুও বের হয়োনা; নিশ্চয়ই তিনি তোমাদের কার্যকলাপ সম্যকভাবে প্রত্যক্ষ করেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1586,11,113,'আর যালিমদের প্রতি ঝুকে পড়না, অন্যথায় তোমাদেরকে জাহান্নামের আগুন স্পর্শ করবে, আর আল্লাহ ছাড়া তোমরা কোন সাহায্যকারী পাবেনা, অতঃপর তোমাদেরকে কোন সাহায্যও করা হবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1587,11,114,'এবং সালাতের পাবন্দী হও দিনের দু’ প্রান্তে ও রাতের কিছু অংশে; নিঃসন্দেহে সৎ কার্যসমূহ অসৎ কার্যসমূহকে মিটিয়ে দেয়; এটা হচ্ছে একটি (ব্যাপক) নাসীহাত, নাসীহাত মান্যকারীদের জন্য।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1588,11,115,'আর ধৈর্য ধারণ কর। কেননা আল্লাহ সৎ কর্মশীলদের কর্মফলকে পন্ড করেননা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1589,11,116,'বস্তুতঃ যে সব সম্প্রদায় তোমাদের পূর্বে গত হয়েছে তাদের মধ্যে এমন বুদ্ধিমান লোক হয়নি, যারা দেশে ফাসাদ বিস্তারে বাধা প্রদান করত, সামান্য কয়েকজন ছাড়া, যাদেরকে আমি তাদের মধ্য হতে রক্ষা করেছিলাম। আর যারা অবাধ্য ছিল তারা যে আরাম আয়েশে ছিল, ওর পিছনেই পড়ে রইল এবং অপরাধ প্রবণ হয়ে পড়ল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1590,11,117,'আর তোমার রাব্ব এমন নন যে, জনপদসমূহকে অন্যায়ভাবে ধ্বংস করবেন, অথচ ওর অধিবাসীরা সৎ কাজে লিপ্ত রয়েছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1591,11,118,'এবং যদি তোমার রবের ইচ্ছা হত তাহলে তিনি সকল মানুষকে একই মতাবলম্বী করে দিতেন, কিন্তু তারা মতভেদ করতেই থাকবে,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1592,11,119,'কিন্তু যার প্রতি তোমার রবের অনুগ্রহ হয়। আর এ জন্যই তিনি তাদেরকে সৃষ্টি করেছেন; এবং তোমার রবের এই বাণীও পূর্ণ হবে যে, আমি জিন ও মানব সকলের দ্বারা জাহান্নামকে পূর্ণ করবই।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1593,11,120,'রাসূলদের ঐ সব বৃত্তান্ত আমি তোমার কাছে বর্ণনা করছি, যদ্দারা আমি তোমার চিত্তকে দৃঢ় করি। এর মাধ্যমে তোমার কাছে এসেছে সত্য এবং মু’মিনদের জন্য এসেছে উপদেশ ও সাবধানবাণী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1594,11,121,'যারা বিশ্বাস করেনা তাদেরকে বলঃ তোমরা যেমন করছ, করতে থাক এবং আমরাও আমাদের কাজ করছি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1595,11,122,'এবং তোমরা প্রতীক্ষা কর, আমরাও প্রতীক্ষা করছি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1596,11,123,'আকাশসমূহ ও পৃথিবীর অদৃশ্য বিষয়ের জ্ঞান আল্লাহরই এবং তাঁরই কাছে সবকিছু প্রত্যাবর্তিত হবে। সুতরাং তাঁর ইবাদাত কর এবং তাঁর উপর নির্ভর কর, আর তোমরা যা কর সে সম্বন্ধে তোমার রাব্ব অনবহিত নন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1597,12,1,'আলিফ-লাম-রা এগুলি সুস্পষ্ট কিতাবের আয়াত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1598,12,2,'আমি অবতীর্ণ করেছি আরাবী ভাষায় কুরআন যাতে তোমরা বুঝতে পার।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1599,12,3,'আমি তোমার কাছে উত্তম কাহিনী বর্ণনা করছি, অহীর মাধ্যমে তোমার কাছে এই কুরআন প্রেরণ করে, যদিও এর পূর্বে তুমি ছিলে অনবহিতদের অন্তর্ভুক্ত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1600,12,4,'যখন ইউসুফ তার পিতাকে বললঃ হে পিতা! আমি এগারটি নক্ষত্র, সূর্য এবং চাঁদকে দেখেছি - দেখেছি ওদেরকে আমার প্রতি সাজদাহবনত অবস্থায়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1601,12,5,'সে বললঃ হে আমার পুত্র! তোমার স্বপ্নের বৃত্তান্ত তোমার ভাইদের নিকট বর্ণনা করনা; করলে তারা তোমার বিরুদ্ধে ষড়যন্ত্র করবে; শাইতানতো মানুষের প্রকাশ্য শত্রু।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1602,12,6,'এভাবে তোমার রাব্ব তোমাকে মনোনীত করবেন এবং তোমাকে স্বপ্নের ব্যাখ্যা শিক্ষা দিবেন, আর তোমার প্রতি ও ইয়াকূবের পরিবার পরিজনের প্রতি অনুগ্রহ পূর্ণ করবেন যেভাবে তিনি এটা পূর্বে পূর্ণ করেছিলেন তোমার পিতৃপুরুষ ইবরাহীম ও ইসহাকের প্রতি। নিশ্চয়ই তোমার রাব্ব সর্বজ্ঞ, প্রজ্ঞাময়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1603,12,7,'ইউসুফ ও তার ভাইদের ঘটনায় জিজ্ঞাসুদের জন্য নিদর্শন রয়েছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1604,12,8,'যখন তারা (ভাইয়েরা) বলেছিলঃ আমাদের পিতার নিকট ইউসুফ এবং তার ভাইই (বিন ইয়ামীন) অধিক প্রিয়, অথচ আমরা একটি সংহত দল, আমাদের পিতাতো স্পষ্ট বিভ্রান্তিতেই রয়েছেন ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1605,12,9,'ইউসুফকে হত্যা কর অথবা তাকে কোন স্থানে ফেলে এসো। ফলে তোমাদের পিতার দৃষ্টি শুধু তোমাদের প্রতি নিবিষ্ট হবে এবং তারপর তোমরা ভাল লোক হয়ে যাবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1606,12,10,'তাদের মধ্যে একজন বললঃ ইউসুফকে হত্যা করনা, বরং যদি তোমরা কিছু করতেই চাও তাহলে তাকে কোন গভীর কুপে নিক্ষেপ কর, যাত্রী দলের কেহ তাকে তুলে নিয়ে যাবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1607,12,11,'তারা বললঃ হে আমাদের পিতা! ইউসুফের ব্যাপারে আপনি আমাদেরকে অবিশ্বাস করছেন কেন, যদিও আমরা তার হিতাকাংখী?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1608,12,12,'আপনি আগামীকাল তাকে আমাদের সাথে প্রেরণ করুন, সে ফলমূল খাবে ও খেলাধুলা করবে, আমরা অবশ্যই তার রক্ষণাবেক্ষণ করব।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1609,12,13,'সে বললঃ এটা আমাকে কষ্ট দিবে যে, তোমরা তাকে নিয়ে যাবে এবং আমি ভয় করি, তোমরা তার প্রতি অমনোযোগী হলে তাকে নেকড়ে বাঘ খেয়ে ফেলবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1610,12,14,'তারা বললঃ আমরা একটি সংহত দল হওয়া সত্ত্বেও যদি তাকে নেকড়ে বাঘ খেয়ে ফেলে তাহলেতো আমরা ক্ষতিগ্রস্তই হব।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1611,12,15,'অতঃপর যখন তারা তাকে নিয়ে গেল এবং তাকে গভীর কূপে নিক্ষেপ করতে একমত হল, এমতাবস্থায় আমি তাকে জানিয়ে দিলামঃ তুমি তাদেরকে তাদের এই কর্মের কথা অবশ্যই বলে দিবে যখন তারা তোমাকে চিনবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1612,12,16,'তারা রাতে কাঁদতে কাঁদতে তাদের পিতার নিকট এলো।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1613,12,17,'তারা বললঃ হে আমাদের পিতা! আমরা দৌড়ের প্রতিযোগিতা করেছিলাম এবং ইউসুফকে আমাদের মালপত্রের নিকট রেখে গিয়েছিলাম, অতঃপর তাকে নেকড়ে বাঘ খেয়ে ফেলেছে; কিন্তু আপনিতো আমাদের বিশ্বাস করবেননা, যদিও আমরা সত্যবাদী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1614,12,18,'আর তারা তার জামায় মিথ্যা রক্ত লেপন করে এনেছিল। সে বললঃ না, তোমাদের মন তোমাদের জন্য একটি কাহিনী সাজিয়ে দিয়েছে। সুতরাং পূর্ণ ধৈর্যই শ্রেয়, তোমরা যা বলছ সে বিষয়ে একমাত্র আল্লাহই আমার সাহায্যস্থল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1615,12,19,'এক যাত্রী দল এলো, তারা তাদের পানি সংগ্রাহককে প্রেরণ করল। সে তার পানির বালতি নামিয়ে দিল, সে বলে উঠলঃ কি সুখবর! এ যে এক কিশোর! অতঃপর তারা তাকে পণ্য রূপে লুকিয়ে রাখল, তারা যা করছিল সেই বিষয়ে আল্লাহ সবিশেষ অবগত ছিলেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1616,12,20,'আর তারা তাকে বিক্রি করল স্বল্প মূল্যে, মাত্র কয়েক দিরহামের বিনিময়ে, তারা এতে ছিল নির্লোভ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1617,12,21,'মিসরের যে ব্যক্তি তাকে ক্রয় করেছিল, সে তার স্ত্রীকে বললঃ সম্মানজনকভাবে এর থাকার ব্যবস্থা কর, সম্ভবতঃ সে আমাদের উপকারে আসবে অথবা আমরা একে পুত্র রূপেও গ্রহণ করতে পারি এবং এভাবে আমি ইউসুফকে সেই দেশে প্রতিষ্ঠিত করলাম তাকে স্বপ্নের ব্যাখ্যা শিক্ষা দেয়ার জন্য। আল্লাহ তাঁর কার্য সম্পাদনে অপ্রতিহত; কিন্তু অধিকাংশ মানুষ এটা অবগত নয়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1618,12,22,'সে যখন পূর্ণ যৌবনে উপনীত হল তখন আমি তাকে হিকমাত ও জ্ঞান দান করলাম, এবং এভাবেই আমি সৎকর্মপরায়ণদেরকে পুরস্কৃত করি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1619,12,23,'সে যে স্ত্রী লোকের গৃহে ছিল সে তার কাছ থেকে অসৎ কাজ কামনা করল এবং দরজাগুলি বন্ধ করে দিল ও বললঃ চলে এসো (আমরা কাম প্রবৃত্তি চরিতার্থ করি), সে বললঃ আমি আল্লাহর আশ্রয় প্রার্থনা করছি, তিনি (আযীয) আমার প্রভু! তিনি আমাকে সম্মানজনকভাবে থাকতে দিয়েছেন, সীমালংঘনকারীরা সফলকাম হয়না।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1620,12,24,'সেই রমণীতো তার প্রতি আসক্ত হয়ে ছিল এবং সেও তার প্রতি আসক্ত হয়ে পড়ত যদি না সে তার রবের নিদর্শন প্রত্যক্ষ করত। তাকে মন্দ কাজ ও অশ্লীলতা হতে বিরত রাখার জন্য এভাবে নিদর্শন দেখিয়েছিলাম। সে ছিল আমার বিশুদ্ধ চিত্ত বান্দাদের অন্তর্ভুক্ত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1621,12,25,'তারা উভয়ে দৌড়ে দরযার দিকে গেল এবং স্ত্রী লোকটি পিছন হতে তার জামা ছিড়ে ফেলল, তারা স্ত্রী লোকটির স্বামীকে দরযার কাছে দেখতে পেল। স্ত্রী লোকটি বললঃ যে তোমার পরিবারের সাথে কু-কাজ কামনা করে তার জন্য কারাগারে প্রেরণ অথবা অন্য কোন বেদনাদায়ক শাস্তি ব্যতীত আর কি দন্ড হতে পারে?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1622,12,26,'সে (ইউসুফ) বললঃ সে’ই আমা হতে অসৎ কাজ কামনা করেছিল। স্ত্রী লোকটির পরিবারের একজন সাক্ষী সাক্ষ্য দিলঃ যদি তার জামার সম্মুখ দিক ছিন্ন করা হয়ে থাকে তাহলে স্ত্রী লোকটি সত্য কথা বলেছে এবং পুরুষটি মিথ্যাবাদী ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1623,12,27,'আর যদি তার জামা পিছন দিক হতে ছিন্ন করা হয়ে থাকে তাহলে স্ত্রী লোকটি মিথ্যা কথা বলেছে এবং পুরুষটি সত্যবাদী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1624,12,28,'সুতরাং গৃহস্বামী যখন দেখল যে, তার জামা পিছন দিক হতে ছিন্ন করা হয়েছে তখন সে বললঃ ভীষণ তোমাদের ছলনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1625,12,29,'হে ইউসুফ! তুমি এটা উপেক্ষা কর এবং হে নারী! তুমি তোমার অপরাধের জন্য ক্ষমা প্রার্থনা কর, নিশ্চয়ই তুমিই অপরাধী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1626,12,30,'নগরে কতিপয় নারী বললঃ আযীযের স্ত্রী তার যুবক দাস হতে অসৎ কাজ কামনা করেছে; প্রেম তাকে উম্মত্ত করেছে, আমরাতো তাকে দেখছি স্পষ্ট বিভ্রান্তিতে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1627,12,31,'স্ত্রী লোকটি যখন তাদের ষড়যন্ত্রের কথা শুনল, তখন সে তাদেরকে ডেকে পাঠাল, তাদের প্রত্যেককে একটি করে চাকু দিল এবং যুবককে বললঃ তাদের সামনে বের হও। অতঃপর তারা যখন তাকে দেখল তখন তারা তার সৌন্দর্যে অভিভূত হল এবং নিজেদের হাত কেটে ফেলল। তারা বললঃ অদ্ভুত আল্লাহর মাহাত্য! এতো মানুষ নয়, এতো এক মহিমান্বিত মালাক/ফেরেশতা!');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1628,12,32,'সে বললঃ এই সে যার সম্বন্ধে তোমরা আমার নিন্দা করেছ, আমি তার হতে অসৎ কাজ কামনা করেছি; কিন্তু সে নিজকে পবিত্র রেখেছে; আমি তাকে যা আদেশ করেছি, সে যদি তা না করে তাহলে সে কারারুদ্ধ হবেই এবং হীনদের অন্তর্ভুক্ত হবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1629,12,33,'ইউসুফ বললঃ হে আমার রাব্ব! এই নারীরা আমাকে যার প্রতি আহবান করছে তা অপেক্ষা কারাগার আমার কাছে অধিক প্রিয়। আপনি যদি আমাকে ওদের ছলনা হতে রক্ষা না করেন তাহলে ওদের প্রতি আকৃষ্ট হয়ে পড়ব এবং অজ্ঞদের অন্তর্ভুক্ত হব।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1630,12,34,'অতঃপর তার রাব্ব তার আহবানে সাড়া দিলেন এবং তাদের ছলনা হতে রক্ষা করলেন, তিনিতো সর্বশ্রোতা, সর্বজ্ঞ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1631,12,35,'নিদর্শনাবলী দেখার পর তাদের মনে হল যে, তাকে কিছু কালের জন্য কারারুদ্ধ করতেই হবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1632,12,36,'তার সাথে দু’জন যুবক কারাগারে প্রবেশ করল, তাদের একজন বললঃ আমি স্বপ্নে দেখলাম, আমি আংগুর নিংড়িয়ে রস বের করছি এবং অপরজন বললঃ আমি স্বপ্নে দেখলাম, আমি আমার মাথায় রুটি বহন করছি এবং পাখী তা হতে খাচ্ছে, আমাদেরকে আপনি এর তাৎপর্য জানিয়ে দিন, আমরা আপনাকে সৎকর্মপরায়ণ দেখছি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1633,12,37,'ইউসুফ বললঃ তোমাদেরকে যে খাদ্য দেয়া হয় তা আসার পূর্বে আমি তোমাদেরকে স্বপ্নের ব্যাখ্যা জানিয়ে দিব, আমি যা তোমাদেরকে বলব তা আমার রাব্ব আমাকে যা শিক্ষা দিয়েছেন তা হতে বলব, যে সম্প্রদায় আল্লাহকে বিশ্বাস করেনা ও পরলোকে অবিশ্বাসী হয় আমি তাদের মতবাদ বর্জন করেছি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1634,12,38,'আমি আমার পিতৃ পুরুষ ইবরাহীম, ইসহাক এবং ইয়াকূবের মতবাদ অনুসরণ করি। আল্লাহর সাথে কোন বস্তুকে শরীক করা আমাদের কাজ নয়, এটা আমাদের ও সমস্ত মানুষের প্রতি আল্লাহর অনুগ্রহ; কিন্তু অধিকাংশ মানুষই কৃতজ্ঞতা প্রকাশ করেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1635,12,39,'হে আমার কারা সঙ্গীদ্বয়! ভিন্ন ভিন্ন বহু রাব্ব শ্রেয়, নাকি পরাক্রমশালী এক আল্লাহ?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1636,12,40,'তাঁকে ছেড়ে তোমরা শুধু কতকগুলি নামের ইবাদাত করছ, যে নাম তোমাদের পিতৃপুরুষ ও তোমরা রেখেছ, এইগুলির কোন প্রমাণ আল্লাহ পাঠাননি। হুকুম (বিধান) দেয়ার অধিকার শুধু আল্লাহরই। তিনি নির্দেশ দিয়েছেন যে, তোমরা শুধুমাত্র তাঁরই ইবাদাত করবে, আর কারও ইবাদাত করবেনা; এটাই সরল সঠিক দীন, কিন্তু অধিকাংশ মানুষ এটা অবগত নয়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1637,12,41,'হে আমার কারা সঙ্গীদ্বয়! তোমাদের একজনের ব্যাপার এই যে, সে তার প্রভুকে মদ পান করাবে এবং অপর সম্বন্ধে কথা এই যে, সে শুলবিদ্ধ হবে; অতঃপর তার মস্তক হতে পাখী আহার করবে, যে বিষয়ে তোমরা জানতে চেয়েছ তার সিদ্ধান্ত হয়ে গেছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1638,12,42,'ইউসুফ তাদের মধ্যে যে মুক্তি পাবে মনে করল, তাকে বললঃ তোমার প্রভুর কাছে আমার কথা বল; কিন্তু শাইতান তাকে তার প্রভুর কাছে তার বিষয়ে বলার কথা ভুলিয়ে দিল। সুতরাং ইউসুফ কয়েক বছর কারাগারে রইল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1639,12,43,'বাদশাহ বললঃ আমি স্বপ্নে দেখলাম, সাতটি স্থূলকায় গাভী, ওগুলিকে সাতটি শীর্ণকায় গাভী ভক্ষণ করছে এবং সাতটি সবুজ শীষ এবং অপর সাতটি শুস্ক। হে প্রধানগণ! যদি তোমরা স্বপ্নের ব্যাখ্যা করতে পার তাহলে আমার স্বপ্ন সম্বন্ধে অভিমত দাও।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1640,12,44,'তারা বললঃ এটা অর্থহীন স্বপ্ন এবং আমরা এরূপ স্বপ্ন ব্যাখ্যায় অভিজ্ঞ নই।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1641,12,45,'দু’জন কারারুদ্ধের মধ্যে যে মুক্তি পেয়েছিল এবং দীর্ঘকাল পরে তার স্মরণ হলে সে বললঃ আমি এর তাৎপর্য তোমাদেরকে জানিয়ে দিব, সুতরাং তোমরা আমাকে পাঠিয়ে দাও।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1642,12,46,'সে বললঃ হে ইউসুফ! হে সত্যবাদী! সাতটি স্থুলকায় গাভী, ওগুলিকে সাতটি শীর্ণকায় গাভী ভক্ষণ করছে এবং সাতটি সবুজ শীষ ও অপর সাতটি শুস্ক শীষ সম্বন্ধে আপনি আমাদেরকে ব্যাখ্যা দিন, যাতে আমি লোকদের কাছে ফিরে যেতে পারি এবং যাতে তারা অবগত হতে পারে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1643,12,47,'ইউসুফ বললঃ তোমরা সাত বছর একাদিক্রমে চাষ করবে, অতঃপর তোমরা শস্য সংগ্রহ করবে; তার মধ্যে যে সামান্য পরিমাণ তোমরা আহার করবে, তা ব্যতীত সমস্ত শস্য শীষ সমেত রেখে দিবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1644,12,48,'এরপর আসবে সাতটি কঠিন বছর; এই সাত বছর যা পূর্বে সঞ্চয় করে রাখবে - লোকে তা খাবে; শুধু সামান্য কিছু যা তোমরা সংরক্ষণ করবে তা ব্যতীত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1645,12,49,'এবং এরপর আসবে এক বছর, সেই বছর মানুষের জন্য প্রচুর বৃষ্টিপাত হবে এবং সেই বছর মানুষ প্রচুর ফলের রস নিংড়াবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1646,12,50,'বাদশাহ বললঃ তোমরা ইউসুফকে আমার কাছে নিয়ে এসো। যখন দূত তার কাছে উপস্থিত হল তখন সে বললঃ তুমি তোমার প্রভুর কাছে ফিরে যাও এবং তাকে জিজ্ঞেস করঃ যে নারীরা তাদের হাত কেটে ফেলেছিল তাদের অবস্থা কি? আমার রাব্ব তাদের ছলনা সম্যক অবগত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1647,12,51,'বাদশাহ নারীদেরকে বললঃ যখন তোমরা ইউসুফ হতে অসৎ কাজ কামনা করেছিলে তখন তোমাদের কি হয়েছিল? তারা বললঃ অদ্ভুত আল্লাহর মাহাত্ম্য! আমরা তার মধ্যে কোন দোষ দেখিনি। আযীযের স্ত্রী বললঃ এক্ষণে সত্য প্রকাশ হয়ে গেল, আমিই তার হতে অসৎ কাজ কামনা করেছিলাম, সেতো সত্যবাদী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1648,12,52,'সে বললঃ আমি এটা বলেছিলাম যাতে সে জানতে পারে যে, তার অনুপস্থিতিতে আমি তার প্রতি বিশ্বাসঘাতকতা করিনি এবং আল্লাহ বিশ্বাস ঘাতকদের ষড়যন্ত্র সফল করেননা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1649,12,53,'আমি নিজকে নির্দোষ মনে করিনা, মানুষের মন অবশ্যই মন্দ কর্ম-প্রবণ। কিন্তু সে নয় যার প্রতি আমার রাব্ব অনুগ্রহ করেন। নিশ্চয়ই আমার রাব্ব অতি ক্ষমাশীল ও দয়ালু।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1650,12,54,'বাদশাহ বললঃ ইউসুফকে আমার কাছে নিয়ে এসো, আমি তাকে একান্ত সহচর নিযুক্ত করব। অতঃপর বাদশাহ যখন তার সাথে কথা বলল তখন রাজা বললঃ আজ তুমি আমাদের কাছে মর্যাদাবান ও বিশ্বাস ভাজন হলে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1651,12,55,'সে বললঃ আমাকে কোষাগারের দায়িত্বে নিয়োজিত করুন। নিশ্চয়ই আমি উত্তম সংরক্ষণকারী, অতিশয় জ্ঞানবান।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1652,12,56,'এভাবে আমি ইউসুফকে সেই দেশে প্রতিষ্ঠিত করলাম। সে সেই দেশে যথা ইচ্ছা অবস্থান করতে পারত। আমি যাকে ইচ্ছা তার প্রতি দয়া করি, আর আমি সৎ কর্মপরায়ণদের শ্রমফল বিনষ্ট করিনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1653,12,57,'যারা মু’মিন ও মুত্তাকী তাদের পরকালের পুরস্কারই উত্তম।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1654,12,58,'ইউসুফের ভাইয়েরা এলো এবং তার নিকট উপস্থিত হল। সে তাদেরকে চিনল, কিন্তু তারা তাকে চিনতে পারলনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1655,12,59,'আর সে যখন তাদের সামগ্রীর ব্যবস্থা করে দিল তখন সে বললঃ তোমরা আমার নিকট তোমাদের বৈমাত্রেয় ভাইকে নিয়ে এসো; তোমরা কি দেখছনা যে, আমি মাপে পূর্ণ মাত্রায় দিই? এবং আমি উত্তম মেযবান?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1656,12,60,'কিন্তু তোমরা যদি তাকে আমার নিকট নিয়ে না আস তাহলে আমার নিকট তোমাদের জন্য কোন বরাদ্দ থাকবেনা এবং তোমরা আমার নিকটবর্তী হবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1657,12,61,'তারা বললঃ ওর বিষয়ে আমরা ওর পিতাকে সম্মত করার চেষ্টা করব এবং আমরা নিশ্চয়ই এটা করব।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1658,12,62,'ইউসুফ তার ভৃত্যদেরকে বললঃ তারা যে পণ্য মূল্য দিয়েছে তা তাদের মালপত্রের মধ্যে রেখে দাও, যাতে স্বজনদের কাছে প্রত্যাবর্তনের পর তারা বুঝতে পারে যে, ওটা প্রত্যর্পন করা হয়েছে, তা হলে তারা পুনরায় আসতে পারে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1659,12,63,'অতঃপর যখন তারা তাদের পিতার নিকট ফিরে এলো তখন তারা বললঃ হে আমাদের পিতা! আমাদের জন্য বরাদ্দ নিষিদ্ধ করা হয়েছে, সুতরাং আমাদের ভাইকে আমাদের সাথে পাঠিয়ে দিন যাতে আমরা রসদ পেতে পারি, আমরা অবশ্যই তার রক্ষণাবেক্ষণ করব।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1660,12,64,'সে বললঃ আমি কি তোমাদেরকে ওর সম্বন্ধে সেইরূপ বিশ্বাস করব, যেরূপ বিশ্বাস পূর্বে তোমাদেরকে করেছিলাম ওর ভাইয়ের ব্যাপারে? আল্লাহই রক্ষণাবেক্ষণে শ্রেষ্ঠ এবং তিনি শ্রেষ্ঠতম দয়ালু।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1661,12,65,'যখন তারা তাদের মালপত্র খুলল তখন তারা দেখতে পেল, তাদের পণ্যমূল্য তাদেরকে প্রত্যর্পন করা হয়েছে। তারা বললঃ হে আমাদের পিতা! আমরা আর কি প্রত্যাশা করতে পারি? এটা আমাদের প্রদত্ত পণ্যমূল্য, আমাদেরকে প্রত্যর্পন করা হয়েছে; পুনরায় আমরা আমাদের পরিবারবর্গকে খাদ্যসামগ্রী এনে দিব এবং আমরা আমাদের ভাইয়ের রক্ষণাবেক্ষণ করব এবং আমরা অতিরিক্ত আর এক উট বোঝাই পণ্য আনব, যা এনেছি তা পরিমাণে অল্প।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1662,12,66,'পিতা বললঃ আমি ওকে কখনও তোমাদের সাথে পাঠাবনা যতক্ষণ না তোমরা আল্লাহর নামে অঙ্গীকার কর যে, তোমরা তাকে আমার নিকট নিয়ে আসবেই, অবশ্য যদি তোমরা একান্ত অসহায় হয়ে না পড়। অতঃপর যখন তারা তার নিকট প্রতিজ্ঞা করল তখন সে বললঃ আমরা যে বিষয়ে কথা বলছি, আল্লাহ তার বিধায়ক।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1663,12,67,'সে বললঃ হে আমার পুত্রগণ! তোমরা এক দরযা দিয়ে প্রবেশ করনা, ভিন্ন ভিন্ন দরযা দিয়ে প্রবেশ করবে, আল্লাহর বিধানের বিরুদ্ধে আমি তোমাদের জন্য কিছুই করতে পারিনা। বিধান আল্লাহরই; আমি তাঁরই উপর নির্ভর করি এবং যারা নির্ভর করতে চায় তারা তাঁরই (আল্লাহরই) উপর নির্ভর করুক।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1664,12,68,'যখন তারা, তাদের পিতা তাদেরকে যেভাবে আদেশ করেছিল সেভাবেই প্রবেশ করল, তখন আল্লাহর বিধানের বিরুদ্ধে ওটা তাদের কোন কাজে এলোনা; ইয়াকূব শুধু তার মনের একটি অভিপ্রায় পূর্ণ করেছিল এবং সে অবশ্যই জ্ঞানী ছিল। কারণ আমি তাকে শিক্ষা দিয়েছিলাম, কিন্তু অধিকাংশ মানুষ এটা অবগত নয়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1665,12,69,'তারা যখন ইউসুফের সামনে হাযির হল তখন ইউসুফ তার (সহোদর) ভাইকে নিজের কাছে রাখল এবং বললঃ আমিই তোমার (সহোদর) ভাই। সুতরাং তারা যা করত সেজন্য দুঃখ করনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1666,12,70,'অতঃপর সে যখন তাদের সামগ্রীর ব্যবস্থা করে দিল তখন সে তার (সহোদর) ভাইয়ের মালপত্রের মধ্যে পানপাত্র রেখে দিল, অতঃপর এক ঘোষক উচ্চৈঃস্বরে বললঃ হে যাত্রীদল! তোমরা নিশ্চয়ই চোর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1667,12,71,'তারা তাদের দিকে ফিরে তাকাল এবং বললঃ তোমরা কি হারিয়েছ?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1668,12,72,'তারা বললঃ আমরা রাজার পানপাত্র হারিয়েছি; যে ওটা এনে দিবে সে এক উট বোঝাই মাল পাবে এবং আমি ওর যামীন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1669,12,73,'তারা বললঃ আল্লাহর শপথ! তোমরাতো জান যে, আমরা এই দেশে দুস্কৃতি করতে আসিনি এবং আমরা চোরও নই।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1670,12,74,'তারা বললঃ যদি তোমরা মিথ্যাবাদী হও তাহলে তার শাস্তি কি?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1671,12,75,'তারা বললঃ এর শাস্তি এই যে, যার মালপত্রের মধ্যে পাত্রটি পাওয়া যাবে, সে’ই তার বিনিময়, এভাবে আমরা সীমালংঘনকারীদের শাস্তি দিয়ে থাকি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1672,12,76,'অতঃপর সে তার (সহোদর) ভাইয়ের মালপত্র তল্লাশির পূর্বে তাদের মাল-পত্র তল্লাশি করতে লাগল, পরে তার সহোদরের মাল-পত্রের মধ্য হতে পাত্রটি বের করল। এভাবে আমি ইউসুফের জন্য কৌশল করেছিলাম, রাজার আইনে তার সহোদরকে সে আটক করতে পারতনা, আল্লাহ ইচ্ছা না করলে। আমি যাকে ইচ্ছা মর্যাদায় উন্নীত করি, প্রত্যেক জ্ঞানবান ব্যক্তির উপর আছেন সর্বজ্ঞানী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1673,12,77,'তারা বললঃ সে যদি চুরি করে থাকে তার (সহোদর) ভাইওতো ইতোপূর্বে চুরি করেছিল, এতে ইউসুফ প্রকৃত ব্যাপার নিজের মনে গোপন রাখল এবং তাদের কাছে প্রকাশ করলনা। সে মনে মনে বললঃ তোমাদের অবস্থাতো হীনতর এবং তোমরা যা বলছ সে সম্বন্ধে আল্লাহ সবিশেষ অবগত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1674,12,78,'তারা বললঃ হে আযীয! এর পিতা আছেন অতিশয় বৃদ্ধ, সুতরাং এর স্থলে আপনি আমাদের একজনকে রাখুন! আমরাতো আপনাকে দেখছি মহানুভব ব্যক্তিদের একজন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1675,12,79,'সে বললঃ যার নিকট আমরা আমাদের মাল পেয়েছি, তাকে ছাড়া অন্যকে রাখার অপরাধ হতে আমরা আল্লাহর কাছে আশ্রয় প্রার্থনা করছি। এরূপ করলে আমরা অবশ্যই সীমালংঘনকারী হব।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1676,12,80,'যখন তারা তার নিকট হতে সম্পূর্ণ নিরাশ হল তখন তারা নির্জনে গিয়ে পরামর্শ করতে লাগল, ওদের মধ্যে যে ছিল বয়োজ্যেষ্ঠ সে বললঃ তোমরা কি জাননা যে, তোমাদের পিতা তোমাদের নিকট হতে আল্লাহর নামে অঙ্গীকার নিয়েছেন এবং পূর্বেও তোমরা ইউসুফের ব্যাপারে ক্রটি করেছিলে; সুতরাং আমি কিছুতেই এই দেশ ত্যাগ করবনা যতক্ষণ না আমার পিতা আমাকে অনুমতি দেন অথবা আল্লাহ আমার জন্য কোন ব্যবস্থা করেন এবং তিনিই বিচারকদের মধ্যে শ্রেষ্ঠ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1677,12,81,'তোমরা তোমাদের পিতার নিকট ফিরে যাও এবং বলঃ হে আমাদের পিতা! আপনার পুত্র চুরি করেছে এবং আমরা যা জানি তারই প্রত্যক্ষ বিবরণ দিলাম, অদৃশ্যের ব্যাপারে আমরা অবহিত ছিলামনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1678,12,82,'যে জনপদে আমরা ছিলাম ওর অধিবাসীদেরকে জিজ্ঞেস করুন এবং যে যাত্রীদলের সাথে আমরা এসেছি তাদেরকেও, আমরা অবশ্যই সত্যবাদী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1679,12,83,'ইয়াকূব বললঃ না, তোমাদের মন তোমাদের জন্য একটি কাহিনী সাজিয়ে দিয়েছে; সুতরাং পূর্ণ ধৈর্যই শ্রেয়; হয়তো আল্লাহ ওদেরকে এক সাথে আমার কাছে এনে দিবেন, তিনি সর্বজ্ঞ, প্রজ্ঞাময়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1680,12,84,'সে ওদের দিক থেকে মুখ ফিরিয়ে নিল এবং বললঃ আফসোস ইউসুফের জন্য। শোকে তার চক্ষুদ্বয় সাদা হয়ে গিয়েছিল এবং সে ছিল অসহনীয় মনস্তাপে ক্লিষ্ট।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1681,12,85,'তারা বললঃ আল্লাহর শপথ! আপনিতো ইউসুফের কথা ভুলবেননা যতক্ষণ না আপনি শারীরিকভাবে বিধ্বস্ত হবেন, অথবা মৃত্যু বরণ করবেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1682,12,86,'সে বললঃ আমি আমার অসহনীয় বেদনা, আমার দুঃখ আল্লাহর নিকট নিবেদন করছি এবং আমি আল্লাহর নিকট হতে যা জানি তোমরা তা জাননা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1683,12,87,'হে আমার পুত্রগণ! তোমরা যাও, ইউসুফ ও তার সহোদরের অনুসন্ধান কর এবং আল্লাহর করুণা হতে তোমরা নিরাশ হয়োনা, কারণ কাফির ব্যতীত কেহই আল্লাহর করুণা হতে নিরাশ হয়না।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1684,12,88,'যখন তারা তার নিকট উপস্থিত হল তখন বললঃ হে আযীয! আমরা ও আমাদের পরিবার পরিজন বিপন্ন হয়ে পড়েছি এবং আমরা তুচ্ছ পণ্য নিয়ে এসেছি; আপনি আমাদের রসদ পূর্ণ মাত্রায় দিন এবং আমাদেরকে দান করুন; আল্লাহ দাতাদেরকে পুরস্কৃত করেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1685,12,89,'সে বললঃ তোমরা কি জান, তোমরা ইউসুফ ও তার সহোদরের প্রতি কিরূপ আচরণ করেছিলে, যখন তোমরা ছিলে অজ্ঞ?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1686,12,90,'তারা বললঃ তাহলে কি তুমিই ইউসুফ? সে বললঃ আমিই ইউসুফ এবং এই আমার সহোদর; আল্লাহ আমাদের প্রতি অনুগ্রহ করেছেন। যে ব্যক্তি মুত্তাকী ও ধৈর্যশীল, আল্লাহ সেইরূপ সৎকর্মপরায়ণদের শ্রমফল নষ্ট করেননা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1687,12,91,'তারা বললঃ আল্লাহর শপথ! আল্লাহ নিশ্চয়ই তোমাকে আমাদের উপর প্রাধান্য দিয়েছেন এবং আমরা নিশ্চয়ই অপরাধী ছিলাম।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1688,12,92,'সে বললঃ আজ তোমাদের বিরুদ্ধে কোন অভিযোগ নেই, আল্লাহ তোমাদেরকে ক্ষমা করুন, এবং তিনি দয়ালুদের মধ্যে শ্রেষ্ঠ দয়ালু।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1689,12,93,'তোমরা আমার এ জামাটি নিয়ে যাও এবং এটা আমার পিতার মুখমন্ডলের উপর রেখ, তিনি দৃষ্টিশক্তি ফিরে পাবেন, আর তোমরা তোমাদের পরিবারের সকলকেই আমার নিকট নিয়ে এসো।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1690,12,94,'অতঃপর যাত্রীদল যখন বের হয়ে পড়ল তখন তাদের পিতা বললঃ তোমরা যদি আমাকে অপ্রকৃতিস্থ মনে না কর তাহলে বলিঃ আমি ইউসুফের ঘ্রাণ পাচ্ছি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1691,12,95,'তারা বললঃ আল্লাহর শপথ! আপনিতো আপনার পূর্ব বিভ্রান্তিতেই রয়েছেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1692,12,96,'অতঃপর যখন সুসংবাদ বাহক উপস্থিত হল এবং তার মুখমন্ডলের উপর জামাটি রাখল তখন সে দৃষ্টিশক্তি ফিরে পেল। সে বললঃ আমি কি তোমাদেরকে বলিনি যে, আমি আল্লাহর নিকট হতে যা জানি তোমরা তা জাননা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1693,12,97,'তারা বললঃ হে আমাদের পিতা! আমাদের পাপের জন্য ক্ষমা প্রার্থনা করুন; আমরাতো অপরাধী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1694,12,98,'সে বললঃ আমি আমার রবের নিকট তোমাদের জন্য ক্ষমা প্রার্থনা করব, তিনিতো অতি ক্ষমাশীল, পরম দয়ালু।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1695,12,99,'অতঃপর তারা যখন ইউসুফের নিকট উপস্থিত হল তখন সে তার মাতা-পিতাকে আলিঙ্গন করল এবং বললঃ আপনারা আল্লাহর ইচ্ছায় নিরাপদে মিসরে প্রবেশ করুন!');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1696,12,100,'আর ইউসুফ তার মাতা-পিতাকে উচ্চাসনে বসাল এবং তারা সবাই তার সামনে সাজদাহয় লুটিয়ে পড়ল। সে বললঃ হে আমার পিতা! এটাই আমার পূর্বেকার স্বপ্নের ব্যাখ্যা; আমার রাব্ব ওটা সত্যে পরিণত করেছেন এবং তিনি আমাকে কারাগার হতে মুক্ত করেছেন এবং শাইতান আমার ও আমার ভাইদের সম্পর্ক নষ্ট করার পরও আপনাদেরকে মরু অঞ্চল হতে এখানে এনে দিয়ে আমার প্রতি অনুগ্রহ করেছেন, আমার রাব্ব যা ইচ্ছা তা নিপুণতার সাথে করে থাকেন, তিনিতো সর্বজ্ঞ, প্রজ্ঞাময়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1697,12,101,'হে আমার রাব্ব! আপনি আমাকে রাজ্য দান করেছেন এবং স্বপ্নের ব্যাখ্যা শিক্ষা দিয়েছেন। হে আকাশমন্ডলী ও পৃথিবীর সৃষ্টিকর্তা! আপনিই ইহলোক ও পরলোকে আমার অভিভাবক, আপনি আমাকে মুসলিম হিসাবে মৃত্যু দান করুন এবং আমাকে সৎকর্মপরায়ণদের অন্তর্ভুক্ত করুন!');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1698,12,102,'এটা অদৃশ্যলোকের সংবাদ যা তোমাকে আমি অহী দ্বারা অবহিত করছি, ষড়যন্ত্রকালে যখন তারা মতৈক্যে পৌঁছেছিল তখন তুমি তাদের সাথে ছিলেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1699,12,103,'তুমি যতই চাও না কেন, অধিকাংশ লোকই ঈমান আনার নয়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1700,12,104,'আর তুমিতো তাদের কাছে কোন পারিশ্রমিক দাবী করছোনা, এটাতো বিশ্বজগতের জন্য উপদেশ ছাড়া কিছু নয়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1701,12,105,'আকাশমন্ডলী ও পৃথিবীতে অনেক নিদর্শন রয়েছে, তারা এ সমস্ত প্রত্যক্ষ করে, কিন্তু তারা এ সবের প্রতি উদাসীন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1702,12,106,'তাদের অধিকাংশ আল্লাহকে বিশ্বাস করে, কিন্তু তাঁর সাথে শরীক করে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1703,12,107,'তাহলে কি তারা আল্লাহর সর্বগ্রাসী শাস্তি হতে অথবা তাদের অজ্ঞাতসারে কিয়ামাতের আকস্মিক উপস্থিতি হতে নিরাপদ?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1704,12,108,'তুমি বলঃ এটাই আমার (আল্লাহর) পথ; প্রতিটি মানুষকে আমি আহবান করি সজ্ঞানে, আমি এবং আমার অনুসারীগণও; আল্লাহ মহিমান্বিত এবং যারা আল্লাহর সাথে শরীক স্থাপন করে আমি তাদের অন্তভুর্ক্ত নই।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1705,12,109,'তোমার পূর্বেও জনপদবাসীদের মধ্য হতে পুরুষদেরকেই পাঠিয়েছিলাম, যাদের নিকট অহী পাঠাতাম; তারা কি পৃথিবীতে ভ্রমণ করেনি এবং তাদের পূর্ববর্তীদের কি পরিণাম হয়েছিল তা কি দেখেনি? যারা মুত্তাকী তাদের জন্য পরকালই শ্রেয়; তোমরা কি বুঝনা?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1706,12,110,'অবশেষে যখন রাসূলগণ নিরাশ হল এবং লোকে ভাবল যে, রাসূলদেরকে মিথ্যা আশ্বাস দেয়া হয়েছে তখন তাদের কাছে আমার সাহায্য এলো। এভাবে আমি যাকে ইচ্ছা করি সে উদ্ধার পায়, আর অপরাধী সম্প্রদায় হতে আমার শাস্তি রদ করা হয়না।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1707,12,111,'তাদের বৃত্তান্তে বোধশক্তি সম্পন্ন ব্যক্তিদের জন্য আছে শিক্ষা, ইহা এমন বাণী যা মিথ্যা প্রবন্ধ নয়, কিন্তু মু’মিনদের জন্য এটা পূর্ব গ্রন্থে যা আছে উহার সমর্থন এবং সমস্ত কিছুর বিশদ বিবরণ, হিদায়াত ও রাহমাত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1708,13,1,'আলিম লাম মীম রা, এগুলি কুরআনের আয়াত; যা তোমার রাব্ব হতে তোমার প্রতি অবতীর্ণ হয়েছে তা’ই সত্য; কিন্তু অধিকাংশ এতে বিশ্বাস করেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1709,13,2,'আল্লাহই উর্ধ্বদেশে আকাশমন্ডলী স্থাপন করেছেন স্তম্ভ ব্যতীত, তোমরা এটা দেখছ। অতঃপর তিনি আরশে সমাসীন হলেন এবং সূর্য ও চাঁদকে নিয়মাধীন করলেন; প্রত্যেকে নির্দিষ্টকাল পর্যন্ত আবর্তন করে, তিনি সকল বিষয় নিয়ন্ত্রণ করেন এবং নিদর্শনসমূহ বিশদভাবে বর্ণনা করেন যাতে তোমরা তোমাদের রবের সাথে সাক্ষাৎ সম্বন্ধে নিশ্চিত বিশ্বাস করতে পার।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1710,13,3,'তিনিই ভূতলকে বিস্তৃত করেছেন এবং ওতে পর্বত ও নদী সৃষ্টি করেছেন এবং প্রত্যেক প্রকারের ফল সৃষ্টি করেছেন জোড়ায় জোড়ায়; তিনি দিনকে রাত দ্বারা আচ্ছাদিত করেন; এতে অবশ্যই নিদর্শন রয়েছে চিন্তাশীল সম্প্রদায়ের জন্য।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1711,13,4,'পৃথিবীতে রয়েছে পরস্পর সংলগ্ন ভূখন্ড; ওতে আছে আঙ্গুর-কানন, শষ্যক্ষেত, একাধিক শির বিশিষ্ট অথবা এক শির বিশিষ্ট খেজুর-বৃক্ষ, সিঞ্চিত একই পানিতে; এবং ফল হিসাবে ওগুলির কতককে কতকের উপর আমি শ্রেষ্ঠত্ব দিয়ে থাকি, অবশ্যই বোধশক্তি সম্পন্ন সম্প্রদায়ের জন্য এতে রয়েছে নিদর্শন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1712,13,5,'যদি তুমি বিস্মিত হও তাহলে বিস্ময়ের বিষয় তাদের বক্তব্যঃ মাটিতে পরিণত হওয়ার পরও কি আমরা নতুন জীবন লাভ করব? ওরাই ওদের রাব্বকে অস্বীকার করে এবং ওদেরই গলদেশে থাকবে লৌহ-শৃংখল, ওরাই জাহান্নামী এবং সেখানে ওরা চিরস্থায়ীভাবে অবস্থানকারী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1713,13,6,'মঙ্গলের পূর্বে তারা তোমাকে শাস্তি ত্বরান্বিত করতে বলে, যদিও তাদের পূর্বে এর বহু দৃষ্টান্ত গত হয়েছে। বস্তুতঃ মানুষের সীমালংঘন সত্ত্বেও তোমার রাব্ব মানুষের প্রতি ক্ষমাশীল এবং তোমার রাব্ব শাস্তি দানেও কঠোর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1714,13,7,'যারা কুফরী করেছে তারা বলেঃ তার রবের নিকট হতে তার নিকট কোন নিদর্শন অবতীর্ণ হয়না কেন? হে নাবী! কথা এই যে, তুমিতো শুধুমাত্র ভয় প্রদর্শনকারী এবং প্রত্যেক সম্প্রদায়ের জন্য রয়েছে পথ প্রদর্শক।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1715,13,8,'প্রত্যেক নারী যা গর্ভে ধারণ করে এবং জরায়ুতে যা কিছু কমে ও বাড়ে আল্লাহ তা জানেন। এবং তাঁর বিধানে প্রত্যেক বস্তুরই এক নির্দিষ্ট পরিমাণ আছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1716,13,9,'যা অদৃশ্য ও যা দৃশ্যমান তিনি তা অবগত; তিনি মহান, সর্বোচ্চ মর্যাদাবান।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1717,13,10,'তোমাদের মধ্যে যে কথা গোপন রাখে এবং যে তা প্রকাশ করে, রাতে যে আত্মগোপন করে এবং দিনে যে প্রকাশ্যে বিচরণ করে, তারা সমভাবে তাঁর (আল্লাহর) জ্ঞানগোচর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1718,13,11,'মানুষের জন্য তার সামনে ও পিছনে একের পর এক প্রহরী থাকে; তারা আল্লাহর আদেশে তার রক্ষণাবেক্ষণ করে; নিশ্চয়ই আল্লাহ কোন সম্প্রদায়ের অবস্থা পরিবর্তন করেননা যতক্ষণ না তারা নিজেদের অবস্থা নিজেরা পরিবর্তন করে। কোন সম্প্রদায় সম্পর্কে যদি আল্লাহ অশুভ কিছু ইচ্ছা করেন তাহলে তা রদ করার কেহ নেই এবং তিনি ছাড়া তাদের কোন অভিভাবক (ওয়ালী) নেই।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1719,13,12,'তিনিই তোমাদেরকে দেখান বিজলী, যা ভয় ও ভরসা সঞ্চার করে এবং তিনিই সৃষ্টি করেন ঘন মেঘ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1720,13,13,'বজ্র ধ্বনি ও মালাইকা সভয়ে তাঁর সপ্রশংস মহিমা ও পবিত্রতা ঘোষণা করে। তিনি বজ্রপাত ঘটান এবং যাকে ইচ্ছা ওটা দ্বারা আঘাত করেন। তথাপি ওরা আল্লাহ সম্বন্ধে বিতন্ডা করে; যদিও তিনি মহা শক্তিশালী ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1721,13,14,'সত্যের আহবান তাঁরই। যারা তাঁকে ছাড়া আহবান করে অপরকে তারা (অপরেরা) তাদেরকে কোনই সাড়া দেয়না; তাদের দৃষ্টান্ত সেই ব্যক্তির মত যে তার মুখে পানি পৌঁছবে এই আশায় তার হস্তদ্বয় প্রসারিত করে এমন পানির দিকে যা তার মুখে পৌঁছার নয়, কাফিরদের আহবান নিষ্ফল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1722,13,15,'আল্লাহর প্রতি সাজদাহবনত হয় আকাশমন্ডলী ও পৃথিবীতে যা কিছু আছে, ইচ্ছায় অথবা অনিচ্ছায় এবং তাদের ছায়াগুলিও সকাল-সন্ধ্যায়। [সাজদাহ]');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1723,13,16,'বলঃ কে আকাশমন্ডলী ও পৃথিবীর রাব্ব? বলঃ তিনি আল্লাহ! বলঃ তাহলে কি তোমরা অভিভাবক রূপে গ্রহণ করেছ আল্লাহর পরিবর্তে অপরকে, যারা নিজেদের লাভ বা ক্ষতি সাধনে সক্ষম নয়? বলঃ অন্ধ ও চক্ষুস্মান কি সমান অথবা অন্ধকার ও আলো কি এক? তাহলে কি তারা আল্লাহর এমন শরীক স্থাপন করছে যারা (আল্লাহর সৃষ্টির মত) সৃষ্টি করেছে, যে কারণে সৃষ্টি তাদের মধ্যে বিভ্রাট ঘটিয়েছে? বলঃ আল্লাহ সকল কিছুর স্রষ্টা, তিনি এক, পরাক্রমশালী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1724,13,17,'তিনি আকাশ হতে বৃষ্টি বর্ষণ করেন, ফলে নদীসমূহ ওদের পরিমাণ অনুযায়ী প্লাবিত হয় এবং প্লাবন তার উপরিস্থিত আবর্জনা বহন করে। এভাবে আবর্জনা উপরি ভাগে আসে যখন অলংকার অথবা তৈজসপত্র নির্মানের উদ্দেশে কিছু অগ্নিতে উত্তপ্ত করা হয় তার। এভাবে আল্লাহ সত্য ও অসত্যের দৃষ্টান্ত দিয়ে থাকেন; যা আবর্জনা তা ফেলে দেয়া হয় এবং যা মানুষের উপকারে আসে তা যমীনে থেকে যায়, এভাবে আল্লাহ উপমা দিয়ে থাকেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1725,13,18,'মঙ্গল তাদের, যারা তাদের রবের আহবানে সাড়া দেয়। এবং যারা তাঁর ডাকে সাড়া দেয়না তাদের যদি পৃথিবীতে যা কিছু আছে তা সমস্তই থাকত এবং উহার সাথে সম পরিমাণ আরও থাকত তাহলে অবশ্যই তারা মুক্তিপণ স্বরূপ তা প্রদান করত; তাদের হিসাব হবে কঠোর এবং জাহান্নাম হবে তাদের আবাস; ওটা কত নিকৃষ্ট আশ্রয়স্থল!');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1726,13,19,'তোমার রাব্ব হতে তোমার প্রতি যা অবতীর্ণ হয়েছে তা যে ব্যক্তি সত্য বলে জানে সে, আর অন্ধ কি সমান? উপদেশ গ্রহণ করে শুধু বিবেকবুদ্ধি সম্পন্ন ব্যক্তিরাই।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1727,13,20,'যারা আল্লাহকে প্রদত্ত অঙ্গীকার রক্ষা করে এবং প্রতিজ্ঞা ভঙ্গ করেনা ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1728,13,21,'আর আল্লাহ যে সম্পর্ক অক্ষুণ্ণ রাখতে আদেশ করেছেন যারা তা অক্ষুণ্ণ রাখে, ভয় করে তাদের রাব্বকে এবং ভয় করে কঠোর হিসাবকে ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1729,13,22,'আর যারা তাদের রবের সন্তুষ্টি লাভের জন্য ধৈর্য ধারণ করে, সালাত সুপ্রতিষ্ঠিত করে, আমি তাদেরকে যে জীবনোপকরণ দিয়েছি তা হতে গোপনে ও প্রকাশ্যে ব্যয় করে এবং যারা ভাল দ্বারা মন্দ দূর করে তাদের জন্য শুভ পরিণাম ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1730,13,23,'স্থায়ী জান্নাত, তাতে তারা প্রবেশ করবে এবং তাদের মাতা-পিতা, পতি-পত্নী ও সন্তান-সন্ততিদের মধ্যে যারা সৎ কাজ করেছে তারাও এবং মালাইকা/ফেরেশতারা তাদের কাছে হাযির হবে প্রত্যেক দরজা দিয়ে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1731,13,24,'(তারা বলবেঃ) তোমরা ধৈর্য ধারণ করেছ বলে তোমাদের প্রতি শান্তি (সালাম)! কতই না ভাল এই পরিণাম!');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1732,13,25,'যারা আল্লাহর সাথে দৃঢ় অঙ্গীকারে আবদ্ধ হওয়ার পর তা ভঙ্গ করে, যে সম্পর্ক অক্ষুন্ন রাখতে আল্লাহ আদেশ করেছেন তা ছিন্ন করে এবং পৃথিবীতে অশান্তি সৃষ্টি করে বেড়ায়, তাদের জন্য আছে অভিসম্পাত এবং আছে মন্দ আবাস।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1733,13,26,'আল্লাহ যার জন্য ইচ্ছা করেন তার জীবনোপকরণ বর্ধিত করেন কিংবা সংকুচিত করেন; কিন্তু তারা পাথির্ব জীবন নিয়েই উল্লসিত, অথচ ইহজীবনতো পরজীবনের তুলনায় ক্ষণস্থায়ী ভোগ মাত্র।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1734,13,27,'যারা কুফরী করেছে তারা বলেঃ তার রবের নিকট হতে তার নিকট কোন নিদর্শন অবতীর্ণ হয়না কেন? তুমি বলঃ আল্লাহ যাকে ইচ্ছা বিভ্রান্ত করেন এবং তিনি তাদেরকে তাঁর পথ দেখান যারা তাঁর অভিমুখী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1735,13,28,'যারা ঈমান আনে এবং আল্লাহর স্মরণে যাদের চিত্ত প্রশান্ত হয়; জেনে রেখ, আল্লাহর স্মরণেই চিত্ত প্রশান্ত হয়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1736,13,29,'যারা ঈমান আনে ও সৎ কাজ করে, কল্যাণ ও শুভ পরিণাম তাদেরই।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1737,13,30,'এভাবে আমি তোমাকে পাঠিয়েছি এক জাতির প্রতি, যার পূর্বে বহু জাতি গত হয়েছে, তাদের নিকট আবৃত্তি করার জন্য, যা আমি তোমার প্রতি প্রত্যাদেশ করেছি; তথাপি তারা দয়াময়কে অস্বীকার করে। তুমি বলঃ তিনিই আমার রাব্ব! তিনি ছাড়া অন্য কোন মা‘বূদ নেই, তাঁরই উপর আমি নির্ভর করি এবং আমার প্রত্যাবর্তন তাঁরই কাছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1738,13,31,'যদি কোন কুরআন এমন হত যদ্বারা পর্বতকে গতিশীল করা যেত অথবা পৃথিবীকে বিদীর্ণ করা যেত অথবা মৃতের সাথে কথা বলা যেত, তবুও তারা তাতে বিশ্বাস করতনা; কিন্তু সমস্ত বিষয়ই আল্লাহর এখতিয়ারভুক্ত; তাহলে কি যারা ঈমান এনেছে তাদের প্রত্যয় হয়নি যে, আল্লাহ ইচ্ছা করলে নিশ্চয়ই সকলকে সৎ পথে পরিচালিত করতে পারতেন? যারা কুফরী করেছে তাদের কর্মফলের জন্য তাদের বিপর্যয় ঘটতেই থাকবে, অথবা বিপর্যয় তাদের আশেপাশে আপতিত হতেই থাকবে, যতক্ষণ পর্যন্ত না আল্লাহর প্রতিশ্রুতি ঘটবে, নিশ্চয়ই আল্লাহ প্রতিশ্রুতি পালনে অক্ষম নন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1739,13,32,'তোমার পূর্বেও অনেক রাসূলকে ঠাট্টা-বিদ্রুপ করা হয়েছে এবং যারা কুফরী করেছে তাদেরকে কিছু অবকাশ দিয়েছিলাম, অতঃপর তাদেরকে পাকড়াও করেছিলাম; কেমন ছিল আমার পাকড়াও!');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1740,13,33,'তাহলে কি প্রত্যেক মানুষ যা করে, তার যিনি পর্যবেক্ষক তিনি অক্ষম এদের উপাস্যগুলির মত? অথচ তারা আল্লাহর বহু শরীক করেছে। তুমি বলঃ তোমরা তাদের পরিচয় দাও; তোমরা কি পৃথিবীর এমন কিছুর সংবাদ তাঁকে দিতে চাও যা তিনি জানেননা? না বরং ওদের ছলনা ওদের নিকট শোভন প্রতীয়মান হয় এবং তারা সৎ পথ হতে নিবৃত্ত হয়; আল্লাহ যাকে বিভ্রান্ত করেন তার কোন পথ প্রদর্শক নেই।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1741,13,34,'তাদের জন্য পার্থিব জীবনে আছে শাস্তি এবং পরকালের শাস্তিতো আরও কঠোর! এবং আল্লাহর শাস্তি হতে রক্ষা করার তাদের কেহ নেই।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1742,13,35,'মুত্তাকীদেরকে যে জান্নাতের প্রতিশ্রুতি দেয়া হয়েছে তার উপমা এইরূপঃ ওর পাদদেশে নদী প্রবাহিত, ওর ফলসমূহ এবং ওর ছায়া চিরস্থায়ী; যারা মুত্তাকী এটা তাদের কর্মফল, এবং কাফিরদের কর্মফল আগুন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1743,13,36,'আমি যাদেরকে কিতাব দিয়েছি তারা যা তোমার প্রতি অবতীর্ণ হয়েছে তাতে আনন্দ পায়, কিন্তু কোন কোন দল ওর কতক অংশ অস্বীকার করে। তুমি বলঃ আমিতো আল্লাহরই ইবাদাত করতে ও তাঁর কোন শরীক না করতে আদিষ্ট হয়েছি; আমি তাঁরই প্রতি আহবান করি এবং তাঁরই নিকট আমার প্রত্যাবর্তন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1744,13,37,'আর এভাবে আমি ইহা (কুরআন) অবতীর্ণ করেছি এক বিধান, আরাবী ভাষায়। জ্ঞান প্রাপ্তির পর তুমি যদি তাদের খেয়াল খুশীর অনুসরণ কর তাহলে আল্লাহর বিরুদ্ধে তোমার কোন অভিভাবক ও রক্ষক থাকবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1745,13,38,'তোমার পূর্বেও আমি অনেক রাসূল প্রেরণ করেছিলাম এবং তাদেরকে স্ত্রী ও সন্তান-সন্ততি দিয়েছিলাম। আল্লাহর অনুমতি ছাড়া কোন নিদর্শন উপস্থিত করা কোন রাসূলের কাজ নয়; প্রত্যেক বিষয়ের নির্ধারিত কাল লিপিবদ্ধ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1746,13,39,'আল্লাহর যা ইচ্ছা তা নিশ্চিহ্ন করেন এবং যা ইচ্ছা তা প্রতিষ্ঠিত রাখেন এবং তাঁরই নিকট আছে কিতাবের মূল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1747,13,40,'আমি তাদেরকে যে শাস্তির কথা বলি, তার কিছু যদি তোমাকে দেখিয়েই দিই অথবা যদি এর পূর্বে তোমার মৃত্যু ঘটিয়েই দিই - তোমার কর্তব্যতো শুধু প্রচার করা; আর হিসাব গ্রহণের দায়িত্ব আমার।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1748,13,41,'তারা কি দেখেনা যে, আমি তাদের দেশকে চারদিক হতে সংকুচিত করে আনছি? আল্লাহ আদেশ করেন, তাঁর আদেশ রদ করার কেহ নেই এবং তিনি হিসাব গ্রহণে তৎপর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1749,13,42,'তাদের পূর্বে যারা ছিল তারাও চক্রান্ত করেছিল, কিন্তু সমস্ত চক্রান্ত আল্লাহর এখতিয়ারে; প্রত্যেক ব্যক্তি যা করে তা তিনি জানেন এবং কাফিরেরা শীঘ্রই জানবে শুভ পরিণাম কাদের জন্য।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1750,13,43,'যারা কুফরী করেছে তারা বলেঃ তুমি (আল্লাহর) প্রেরিত নও; তুমি বলঃ আল্লাহ এবং যাদের নিকট কিতাবের জ্ঞান আছে তারা আমার ও তোমাদের মধ্যে সাক্ষী হিসাবে যথেষ্ট।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1751,14,1,'আলিফ লাম রা। এই কিতাব আমি তোমার প্রতি অবতীর্ণ করেছি যাতে তুমি মানব জাতিকে বের করে আনতে পার অন্ধকার হতে আলোর দিকে; তাঁর পথে, যিনি পরাক্রমশালী, প্রশংসা ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1752,14,2,'আল্লাহ, আসমানসমূহ ও যমীনে যা কিছু আছে তা তাঁরই; কঠিন শাস্তির দুর্ভোগ কাফিরদের জন্য।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1753,14,3,'যারা পার্থিব জীবনকে পরকালিন জীবনের উপর প্রাধান্য দেয়, মানুষকে নিবৃত্ত করে আল্লাহর পথ হতে এবং তা (আল্লাহর পথ) বক্র করতে চায়; তারাইতো ঘোর বিভ্রান্তিতে রয়েছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1754,14,4,'আমি প্রত্যেক রাসূলকেই তার স্বজাতির ভাষাভাষী করে পাঠিয়েছি তাদের নিকট পরিস্কারভাবে ব্যাখ্যা করার জন্য, আল্লাহ যাকে ইচ্ছা বিভ্রান্ত করেন এবং যাকে ইচ্ছা সৎ পথে পরিচালিত করেন এবং তিনি পরাক্রমশালী, প্রজ্ঞাময়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1755,14,5,'মূসাকে আমি আমার নিদর্শনসহ প্রেরণ করেছিলাম এবং বলেছিলামঃ তোমার সম্প্রদায়কে অন্ধকার হতে আলোতে আনয়ন কর, এবং তাদেরকে আল্লাহর দিনগুলি দ্বারা উপদেশ দাও, এতেতো নিদর্শন রয়েছে পরম ধৈর্যশীল ও পরম কৃতজ্ঞ ব্যক্তির জন্য।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1756,14,6,'যখন মূসা তার সম্প্রদায়কে বলেছিলঃ তোমরা আল্লাহর অনুগ্রহ স্মরণ কর যখন তিনি তোমাদেরকে রক্ষা করেছিলেন ফির‘আউন সম্প্রদায়ের কবল হতে যারা তোমাদেরকে নিকৃষ্ট শাস্তি দিত, তোমাদের পুত্রদেরকে যবাহ করত ও তোমাদের নারীদেরকে জীবিত রাখত; এবং এতে ছিল তোমাদের রবের পক্ষ হতে এক মহা পরীক্ষা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1757,14,7,'যখন তোমাদের রাব্ব ঘোষণা করেনঃ তোমরা কৃতজ্ঞ হলে তোমাদেরকে অবশ্যই অধিক দিব, আর অকৃতজ্ঞ হলে অবশ্যই আমার শাস্তি হবে কঠোর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1758,14,8,'মূসা বলেছিলঃ তোমরা এবং পৃথিবীর সকলেও যদি অকৃতজ্ঞ হও তথাপি আল্লাহ অভাবমুক্ত এবং প্রশংসা ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1759,14,9,'তোমাদের কাছে কি সংবাদ আসেনি তোমাদের পূর্ববর্তীদের; নূহের সম্প্রদায়ের, ‘আদ ও ছামূদের এবং তাদের পূর্ববর্তীদের? তাদের বিষয় আল্লাহ ছাড়া অন্য কেহ জানেনা; তাদের কাছে স্পষ্ট নিদর্শনসহ তাদের রাসূল এসেছিল; তারা তাদের হাত তাদের মুখে স্থাপন করত এবং বলতঃ যা নিয়ে তোমরা প্রেরিত হয়েছ তা আমরা প্রত্যাখ্যান করি এবং আমরা অবশ্যই বিভ্রান্তিকর সন্দেহ পোষণ করি সেই বিষয়ে, যার প্রতি তোমরা আমাদেরকে আহবান করছ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1760,14,10,'তাদের রাসূলগণ বলেছিলঃ আল্লাহ সম্বন্ধে কি কোন সন্দেহ আছে, যিনি আকাশসমূহ ও পৃথিবীর সৃষ্টিকর্তা? তিনি তোমাদেরকে আহবান করেন তোমাদের পাপ ক্ষমা করার জন্য এবং নির্দিষ্টকাল পর্যন্ত তোমাদেরকে অবকাশ দেয়ার জন্য। তারা বলতঃ তোমরাতো আমাদের মতই মানুষ; আমাদের পিতৃ পুরুষগণ যাদের ইবাদাত করত তোমরা তাদের ইবাদাত হতে আমাদেরকে বিরত রাখতে চাও; অতএব তোমরা আমাদের কাছে কোন অকাট্য প্রমাণ উপস্থিত কর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1761,14,11,'তাদের রাসূলগণ তাদেরকে বলতঃ সত্য বটে আমরা তোমাদের মত মানুষ, কিন্তু আল্লাহ তাঁর বান্দাদের মধ্যে যাকে ইচ্ছা অনুগ্রহ করেন; আল্লাহর অনুমতি ছাড়া তোমাদের নিকট প্রমাণ উপস্থিত করা আমাদের কাজ নয়; মু’মিনদের আল্লাহর উপরই নির্ভর করা উচিত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1762,14,12,'আমরা আল্লাহর উপর নির্ভর করবনা কেন? তিনিইতো আমাদেরকে পথ প্রদর্শন করেছেন; তোমরা আমাদেরকে যে ক্লেশ দিচ্ছ, আমরা অবশ্যই তা ধৈর্যের সাথে সহ্য করব এবং নির্ভরকারীদের আল্লাহরই উপর নির্ভর করা উচিত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1763,14,13,'কাফিরেরা তাদের রাসূলদেরকে বলেছিলঃ আমরা তোমাদেরকে অবশ্যই আমাদের দেশ হতে বহিস্কার করব, অথবা তোমাদেরকে আমাদের ধর্মাদর্শে ফিরে আসতেই হবে। অতঃপর রাসূলদেরকে তাদের রাব্ব অহী প্রেরণ করলেন; যালিমদেরকে আমি অবশ্যই বিনাশ করব।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1764,14,14,'তাদের পরে আমি তোমাদেরকে দেশে প্রতিষ্ঠিত করবই; এটা তাদের জন্য যারা ভয় রাখে আমার সম্মুখে উপস্থিত হওয়ার এবং ভয় রাখে আমার শাস্তির।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1765,14,15,'তারা বিচার কামনা করল এবং প্রত্যেক উদ্ধত স্বৈরাচারী ব্যর্থ মনোরথ হল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1766,14,16,'তাদের প্রত্যেকের জন্য পরিণামে জাহান্নাম রয়েছে এবং প্রত্যেককে পান করানো হবে গলিত পুঁজ ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1767,14,17,'যা সে অতি কষ্টে গলধঃকরণ করবে এবং তা গলধঃকরণ করা প্রায় অসম্ভব হয়ে পড়বে। সর্ব দিক হতে তার নিকট আসবে মৃত্যু যন্ত্রণা, কিন্তু তার মৃত্যু ঘটবেনা এবং সে কঠোর শাস্তি ভোগ করতেই থাকবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1768,14,18,'যারা তাদের রাব্বকে অস্বীকার করে তাদের উপমা - তাদের কাজসমূহ ছাই সদৃশ যা ঝড়ের দিনে বাতাস প্রচন্ড বেগে উড়িয়ে নিয়ে যায়; যা তারা উপার্জন করে তার কিছুই তারা তাদের কাজে লাগাতে পারেনা; এটাতো ঘোর বিভ্রান্তি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1769,14,19,'তুমি কি লক্ষ্য করনা যে, আল্লাহ আকাশমন্ডলী ও পৃথিবী যথাবিধি সৃষ্টি করেছেন? তিনি ইচ্ছা করলে তোমাদের অস্তিত্ব বিলোপ করতে পারেন এবং এক নতুন সৃষ্টি অস্তিত্বে আনতে পারেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1770,14,20,'আর এটা আল্লাহর জন্য আদৌ কঠিন নয়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1771,14,21,'সবাই আল্লাহর নিকট উপস্থিত হবেই। যারা অহংকার করত, দুর্বলেরা তাদেরকে বলবেঃ আমরাতো তোমাদের অনুসারী ছিলাম; এখন তোমরা আল্লাহর শাস্তি হতে কি আমাদেরকে কিছু মাত্র রক্ষা করতে পারবে? তারা বলবেঃ আল্লাহ আমাদেরকে সৎ পথে পরিচালিত করলে আমরাও তোমাদেরকে সৎ পথে পরিচালিত করতাম; এখন আমাদের ধৈর্যচ্যূত হওয়া অথবা ধৈর্যশীল হওয়া একই কথা; আমাদের কোন নিস্কৃতি নেই।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1772,14,22,'যখন সব কিছুর মীমাংসা হয়ে যাবে তখন শাইতান বলবেঃ আল্লাহ তোমাদেরকে দিয়েছিলেন সত্য প্রতিশ্রুতি, আমিও তোমাদেরকে প্রতিশ্রুতি দিয়েছিলাম, কিন্তু আমি তোমাদেরকে প্রদত্ত প্রতিশ্রুতি রক্ষা করিনি; আমারতো তোমাদের উপর কোন আধিপত্য ছিলনা, আমি শুধু তোমাদেরকে আহবান করেছিলাম এবং তোমরা আমার আহবানে সাড়া দিয়েছিলে; সুতরাং তোমরা আমার প্রতি দোষারোপ করনা, তোমরা তোমাদের প্রতিই দোষারোপ কর; আমি তোমাদের উদ্ধারে সাহায্য করতে সক্ষম নই এবং তোমরাও আমার উদ্ধারে সাহায্য করতে সক্ষম নও; তোমরা যে পূর্বে আমাকে (আল্লাহর) শরীক করেছিলে তার সাথে আমার কোনই সম্পর্ক নেই; যালিমদের জন্যতো বেদনাদায়ক শাস্তি আছেই।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1773,14,23,'যারা ঈমান আনে ও সৎ কাজ করে তাদেরকে দাখিল করা হবে জান্নাতে যার পাদদেশে নদী প্রবাহিত। সেখানে তারা চিরস্থায়ীভাবে অবস্থানকারী হবে তাদের রবের অনুমতিক্রমে। সেখানে তাদের অভিবাদন হবে ‘সালাম’।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1774,14,24,'তুমি কি লক্ষ্য করনা আল্লাহ কিভাবে উপমা দিয়ে থাকেন? সৎ বাক্যের তুলনা উৎকৃষ্ট বৃক্ষ যার মূল সুদৃঢ় এবং যার প্রশাখা উর্ধ্বে বিস্তৃত ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1775,14,25,'যা প্রত্যেক মওসুমে ফল দান করে তার রবের অনুমতিক্রমে, এবং আল্লাহ মানুষের জন্য উপমা দিয়ে থাকেন যাতে তারা শিক্ষা গ্রহণ করে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1776,14,26,'কু-বাক্যের তুলনা এক মন্দ বৃক্ষ যার মূল ভূ-পৃষ্ঠ হতে বিচ্ছিন্ন, যার কোন স্থায়িত্ব নেই।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1777,14,27,'যারা শাশ্বত বাণীতে বিশ্বাসী তাদেরকে ইহজীবনে ও পরজীবনে আল্লাহ সুপ্রতিষ্ঠিত রাখবেন এবং যারা যালিম, আল্লাহ তাদেরকে বিভ্রান্তিতে রাখবেন; আল্লাহ যা ইচ্ছা তা করেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1778,14,28,'তুমি কি তাদের প্রতি লক্ষ্য করনা যারা আল্লাহর অনুগ্রহের পরিবর্তে অকৃতজ্ঞতা প্রকাশ করে এবং তাদের সম্প্রদায়কে নামিয়ে আনে ধ্বংসের আলয়ে ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1779,14,29,'জাহান্নাম, যার মধ্যে তারা প্রবেশ করবে, কত নিকৃষ্ট ঐ আবাসস্থল!');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1780,14,30,'আর তারা আল্লাহর সমকক্ষ উদ্ভাবন করে তাঁর পথ হতে বিভ্রান্ত করার জন্য; তুমি বলঃ ভোগ করে নাও, পরিণামে আগুনই তোমাদের প্রত্যাবর্তন স্থল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1781,14,31,'আমার বান্দাদের মধ্যে যারা মু’মিন তাদেরকে বল সালাত কায়েম করতে এবং আমি তাদেরকে যা দিয়েছি তা হতে গোপনে ও প্রকাশ্যে ব্যয় করতে, সেই দিন আসার পূর্বে যেদিন ক্রয়-বিক্রয় ও বন্ধুত্ব থাকবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1782,14,32,'তিনিই আল্লাহ যিনি আকাশমন্ডলী ও পৃথিবী সৃষ্টি করেছেন, যিনি আকাশ হতে পানি বর্ষণ করে তদ্বারা তোমাদের জীবিকার জন্য ফল-মূল উৎপাদন করেন, যিনি নৌযানকে তোমাদের অধীন করে দিয়েছেন যাতে তাঁর বিধানে ওটা সমুদ্রে বিচরণ করে এবং যিনি তোমাদের কল্যাণে নিয়োজিত করেছেন নদীসমূহকে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1783,14,33,'তিনি তোমাদের কল্যাণে নিয়োজিত করেছেন সূর্য ও চাঁদকে, যারা অবিরাম একই নিয়মের অনুবর্তী এবং তোমাদের কল্যাণে নিয়োজিত করেছেন রাত ও দিনকে ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1784,14,34,'আর তিনি তোমাদেরকে দিয়েছেন তোমরা তাঁর নিকট যা কিছু চেয়েছ; তোমরা আল্লাহর অনুগ্রহ গণনা করলে ওর সংখ্যা নির্ণয় করতে পারবেনা; মানুষ অবশ্যই অতিমাত্রায় যালিম, অকৃতজ্ঞ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1785,14,35,'স্মরণ কর, ইবরাহীম বলেছিলঃ হে আমার রাব্ব! এই শহরকে নিরাপদ করুন এবং আমাকে ও আমার পুত্রদেরকে মূর্তি পূজা হতে দূরে রাখুন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1786,14,36,'হে আমার রাব্ব! এই সব মূর্তি বহু মানুষকে বিভ্রান্ত করেছে; সুতরাং যে আমার অনুসরণ করবে সে’ই আমার দলভুক্ত, কিন্তু কেহ আমার অবাধ্য হলে আপনিতো ক্ষমাশীল, পরম দয়ালু।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1787,14,37,'হে আমাদের রাব্ব! আমি আমার বংশধরদের কতককে বসবাস করালাম অনুর্বর উপত্যকায় আপনার পবিত্র গৃহের নিকট। হে আমাদের রাব্ব! এ জন্য যে, তারা যেন সালাত কায়েম করে; সুতরাং আপনি কিছু লোকের অন্তর ওদের প্রতি অনুরাগী করে দিন এবং ফলফলাদি দ্বারা তাদের রিয্কের ব্যবস্থা করুন যাতে তারা কৃতজ্ঞতা প্রকাশ করে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1788,14,38,'হে আমাদের রাব্ব! আপনিতো জানেন যা আমরা গোপন করি এবং যা আমরা প্রকাশ করি; আকাশমন্ডলী ও পৃথিবীর কোন কিছুই আল্লাহর নিকট গোপন থাকেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1789,14,39,'সমস্ত প্রশংসা আল্লাহরই প্রাপ্য যিনি আমাকে আমার বার্ধক্যে ইসমাঈল ও ইসহাককে দান করেছেন; আমার রাব্ব অবশ্যই প্রার্থনা শুনে থাকেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1790,14,40,'হে আমার রাব্ব! আমাকে সালাত কায়েমকারী করুন এবং আমার বংশধরদের মধ্য হতেও; হে আমাদের রাব্ব! আমার প্রার্থনা কবূল করুন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1791,14,41,'হে আমার রাব্ব! যেদিন হিসাব হবে সেদিন আমাকে, আমার মাতাপিতাকে এবং মু’মিনদেরকে ক্ষমা করুন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1792,14,42,'তুমি কখনও মনে করনা যে, যালিমরা যা করে সে বিষয়ে আল্লাহ উদাসীন। তবে তিনি সেদিন পর্যন্ত তাদেরকে অবকাশ দেন যেদিন তাদের চক্ষু হবে স্থির।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1793,14,43,'ভীতি বিহবল চিত্তে (আকাশের দিকে চেয়ে) ছুটাছুটি করবে। নিজেদের প্রতি তাদের দৃষ্টি ফিরবেনা এবং তাদের অন্তর হবে শূন্য।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1794,14,44,'যেদিন তাদের শাস্তি আসবে সেদিন সম্পর্কে তুমি মানুষকে সতর্ক কর। তখন যালিমরা বলবেঃ হে আমাদের রাব্ব! আমাদের কিছুকালের জন্য অবকাশ দিন, আমরা আপনার আহবানে সাড়া দিব এবং রাসূলদের অনুসরণ করবই। তোমরা কি পূর্বে শপথ করে বলতে না যে, তোমাদের পতন নেই?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1795,14,45,'অথচ তোমরা বাস করতে তাদের বাসভূমিতে যারা নিজেদের প্রতি যুলম করেছিল এবং তাদের প্রতি আমি কি করেছিলাম তাও তোমাদের নিকট সুবিদিত ছিল এবং তোমাদের নিকট আমি তাদের দৃষ্টান্তও উপস্থিত করেছিলাম।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1796,14,46,'তারা ভীষণ চক্রান্ত করেছিল, কিন্তু আল্লাহর নিকট তাদের চক্রান্ত রক্ষিত রয়েছে। তাদের চক্রান্ত এমন ছিলনা যাতে পর্বত টলে যেত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1797,14,47,'তুমি কখনও মনে করনা যে, আল্লাহ তাঁর রাসূলদের প্রতি প্রদত্ত প্রতিশ্রুতি ভঙ্গ করেন; আল্লাহ পরাক্রমশালী, দন্ড বিধায়ক।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1798,14,48,'যেদিন এই পৃথিবী পরিবর্তিত হয়ে অন্য পৃথিবী হবে এবং আকাশমন্ডলীও এবং মানুষ উপস্থিত হবে আললাহর সামনে, যিনি এক, পরাক্রমশালী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1799,14,49,'সেদিন তুমি অপরাধীদেরকে দেখবে শৃংখলিত অবস্থায়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1800,14,50,'তাদের পোশাক হবে আলকাতরার এবং আগুন আচ্ছন্ন করবে তাদের মুখমন্ডল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1801,14,51,'যাতে আল্লাহ প্রতিদান দেন প্রত্যেক নাফসকে যা সে অর্জন করেছে। নিশ্চয় আল্লাহ দ্রুত হিসাব গ্রহণকারী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1802,14,52,'ইহা মানুষের জন্য এক বার্তা যাতে এর দ্বারা তারা সতর্ক হয় এবং জানতে পারে যে, তিনি একমাত্র উপাস্য এবং যাতে বোধশক্তি সম্পন্নরা উপদেশ গ্রহণ করে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1803,15,1,'আলিফ লাম রা। এগুলি আয়াত, মহাগ্রন্থের, সুস্পষ্ট কুরআনের।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1804,15,2,'যারা কুফরী করেছে, তারা একসময় কামনা করবে যদি তারা মুসলিম হত!');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1805,15,3,'তাদের ছেড়ে দাও, তারা খেতে থাকুক, ভোগ করতে থাকুক এবং মিথ্যা আশা ওদেরকে মোহাচ্ছন্ন করে রাখুক, পরিণামে তারা বুঝবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1806,15,4,'আমি কোন জনপদকে তার নির্দিষ্ট কাল পূর্ণ না হওয়া পর্যন্ত ধ্বংস করিনি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1807,15,5,'কোন জাতি তার নির্দিষ্ট কালকে ত্বরান্বিত করতে পারেনা এবং বিলম্বিতও করতে পারেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1808,15,6,'তারা বলেঃ ওহে, যার প্রতি কুরআন অবতীর্ণ হয়েছে! তুমিতো নিশ্চয়ই উম্মাদ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1809,15,7,'তুমি সত্যবাদী হলে আমাদের নিকট মালাইকা/ফেরেশতাদেরকে হাযির করছনা কেন?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1810,15,8,'আমি মালাইকা/ফেরেশতাদেরকে যথার্থ কারণ ব্যতীত প্রেরণ করিনা; মালাইকা/ফেরেশতারা হাযির হলে তারা অবকাশ পাবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1811,15,9,'আমিই জিকর (কুরআন) অবতীর্ণ করেছি এবং আমিই উহার সংরক্ষক।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1812,15,10,'তোমার পূর্বে আমি পূর্বের অনেক সম্প্রদায়ের নিকট রাসূল পাঠিয়েছি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1813,15,11,'তাদের নিকট এমন কোন রাসূল আসেনি যাকে তারা ঠাট্টা বিদ্রুপ করতনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1814,15,12,'এভাবে আমি অপরাধীদের অন্তরে তা সঞ্চার করি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1815,15,13,'তারা কুরআনে বিশ্বাস করবেনা এবং অতীতে পূর্ববর্তীদেরও এই আচরণ ছিল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1816,15,14,'যদি তাদের জন্য আমি আকাশের দরযা খুলে দিই এবং তারা সারাদিন তাতে আরোহণ করতে থাকে ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1817,15,15,'তবুও তারা বলবেঃ আমাদের দৃষ্টি সম্মোহিত করা হয়েছে; না, বরং আমরা এক যাদুগ্রস্ত সম্প্রদায়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1818,15,16,'আকাশে আমি গ্রহ নক্ষত্র সৃষ্টি করেছি এবং ওকে করেছি সুশোভিত, দর্শকদের জন্য।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1819,15,17,'প্রত্যেক অভিশপ্ত শাইতান হতে আমি ওকে রক্ষা করি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1820,15,18,'আর কেহ চুরি করে সংবাদ শুনতে চাইলে ওর পশ্চাদ্ধাবন করে প্রদীপ্ত শিখা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1821,15,19,'পৃথিবীকে আমি বিস্তৃত করেছি এবং ওতে পর্বতমালা স্থাপন করেছি; আমি ওতে প্রত্যেক বস্তু উৎপন্ন করেছি সুপরিমিতভাবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1822,15,20,'আর আমি ওতে জীবিকার ব্যবস্থা করেছি তোমাদের জন্য, আর তোমরা যাদের জীবিকাদাতা নও তাদের জন্যও।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1823,15,21,'আমারই কাছে আছে প্রত্যেক বস্তুর ভান্ডার এবং আমি তা সুসম পরিমাণেই সরবরাহ করে থাকি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1824,15,22,'আমি বৃষ্টিগর্ভ বায়ু প্রেরণ করি, অতঃপর আকাশ হতে বৃষ্টি বর্ষণ করি এবং তা তোমাদেরকে পান করতে দিই; ওর ভান্ডার তোমাদের কাছে নেই।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1825,15,23,'আমিই জীবন দান করি ও মৃত্যু ঘটাই এবং আমিই চূড়ান্ত মালিকানার অধিকারী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1826,15,24,'তোমাদের পূর্বে যারা গত হয়েছে আমি তাদেরকে জানি এবং পরে যারা আসবে তাদেরকেও জানি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1827,15,25,'তোমার রাব্বই তাদেরকে সমবেত করবেন; তিনি প্রজ্ঞাময়, সর্বজ্ঞ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1828,15,26,'আমিতো মানুষকে সৃষ্টি করেছি ছাঁচে ঢালা শুষ্ক ঠনঠনে মাটি হতে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1829,15,27,'এর পূর্বে সৃষ্টি করেছি জিনকে, প্রখর শিখাযুক্ত অগ্নি হতে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1830,15,28,'স্মরণ কর, যখন তোমার রাব্ব মালাইকা/ফেরেশতাদেরকে বললেনঃ আমি ছাঁচে ঢালা শুষ্ক ঠনঠনে মাটি হতে মানুষ সৃষ্টি করব।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1831,15,29,'যখন আমি তাকে সুঠাম করব এবং তাতে আমার রূহ সঞ্চার করব তখন তোমরা তার প্রতি সাজদাহবনত হও।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1832,15,30,'মালাইকা/ফেরেশতাগণ সবাই একত্রে সাজদাহ করল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1833,15,31,'কিন্তু ইবলীস করলনা, সে সাজদাহকারীদের অন্তর্ভুক্ত হতে অস্বীকার করল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1834,15,32,'(আল্লাহ) বললেনঃ হে ইবলীস! তোর কি হল, তুই কেন সাজদাহকারীদের অন্তর্ভুক্ত হলিনা?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1835,15,33,'সে উত্তরে বললঃ ছাঁচে ঢালা শুষ্ক ঠনঠনে মৃত্তিকা হতে যে মানুষ আপনি সৃষ্টি করেছেন, আমি তাকে সাজদাহ করার নই।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1836,15,34,'(আল্লাহ) বললেনঃ তাহলে তুই এখান হতে বের হয়ে যা, কারণ তুই অভিশপ্ত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1837,15,35,'কর্মফল দিন পর্যন্ত তোর প্রতি রইল লা’নত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1838,15,36,'ইবলীস বললঃ হে আমার রাব্ব! পুনরুত্থান দিন পর্যন্ত আমাকে অবকাশ দিন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1839,15,37,'আল্লাহ বললেনঃ তোকে অবকাশ দেয়া হল ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1840,15,38,'অবধারিত সময় উপস্থিত হওয়ার দিন পর্যন্ত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1841,15,39,'সে বললঃ হে আমার রাব্ব! আপনি যে আমাকে বিপথগামী করলেন তজ্জন্য আমি পৃথিবীতে মানুষের নিকট পাপ কাজকে শোভনীয় করে তুলব এবং আমি তাদের সকলকে বিপথগামী করব।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1842,15,40,'তবে তাদের মধ্য হতে আপনার নির্বাচিত বান্দাগণ ব্যতীত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1843,15,41,'তিনি বললেনঃ এটাই আমার নিকট পৌঁছার সরল পথ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1844,15,42,'বিভ্রান্তদের মধ্যে যারা তোর অনুসরণ করবে তারা ছাড়া আমার বান্দাদের উপর তোর কোন ক্ষমতা থাকবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1845,15,43,'অবশ্যই তোর অনুসারীদের সবারই নির্ধারিত স্থান হবে জাহান্নাম।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1846,15,44,'ওর সাতটি দরযা আছে, প্রত্যেক দরযার জন্য পৃথক পৃথক দল আছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1847,15,45,'নিশ্চয়ই মুত্তাকীরা থাকবে প্রস্রবন বহুল জান্নাতে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1848,15,46,'(তাদেরকে বলা হবে) তোমরা শান্তি ও নিরাপত্তার সাথে এখানে প্রবেশ কর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1849,15,47,'আমি তাদের অন্তর হতে ঈর্ষা দূর করব; তারা ভ্রাতৃভাবে পরস্পর মুখোমুখি হয়ে অবস্থান করবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1850,15,48,'সেখানে তাদেরকে অবসাদ স্পর্শ করবেনা এবং তারা সেখান হতে বহিস্কৃতও হবেনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1851,15,49,'আমার বান্দাদেরকে বলে দাওঃ নিশ্চয়ই আমি ক্ষমাশীল, পরম দয়ালু।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1852,15,50,'আর আমার শাস্তি! তা অতি মর্মন্তদ শাস্তি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1853,15,51,'আর তুমি তাদেরকে ইবরাহীমের মেহমানদের সংবাদ দাও।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1854,15,52,'যখন তারা তার নিকট উপস্থিত হয়ে বললঃ ‘সালাম’ তখন সে বলেছিলঃ আমরা তোমাদের আগমনে আতংকিত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1855,15,53,'তারা বললঃ ভয় করনা, আমরা তোমাকে এক জ্ঞানী পুত্রের সুসংবাদ দিচ্ছি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1856,15,54,'সে বললঃ তোমরা কি আমাকে সুসংবাদ দিচ্ছ আমি বার্ধক্যগ্রস্ত হওয়া সত্ত্বেও? তোমরা কি বিষয়ে সুসংবাদ দিচ্ছ?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1857,15,55,'তারা বললঃ আমরা সত্য সংবাদ দিচ্ছি; সুতরাং তুমি নিরাশ হয়োনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1858,15,56,'সে বললঃ যারা পথভ্রষ্ট তারা ব্যতীত আর কে তার রবের অনুগ্রহ হতে নিরাশ হয়?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1859,15,57,'সে বললঃ হে প্রেরিতগণ! অতঃপর তোমরা কি বিশেষ কাজ নিয়ে এসেছ?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1860,15,58,'তারা বললঃ আমাদেরকে এক অপরাধী সম্প্রদায়ের বিরুদ্ধে প্রেরণ করা হয়েছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1861,15,59,'তবে লূতের পরিবারবর্গের বিরুদ্ধে নয়, আমরা অবশ্যই তাদের সকলকে রক্ষা করব।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1862,15,60,'কিন্তু তার স্ত্রীকে নয়; আমরা স্থির করেছি যে, সে অবশ্যই পশ্চাতে অবস্থানকারীদের অন্তর্ভুক্ত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1863,15,61,'মালাইকা/ফেরেশতাগণ যখন লূত পরিবারের নিকট এলো – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1864,15,62,'তখন লূত বললঃ তোমরাতো অপরিচিত লোক।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1865,15,63,'তারা বললঃ না, তারা যে বিষয়ে সন্দিগ্ধ ছিল আমরা তোমার নিকট তা নিয়ে এসেছি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1866,15,64,'আমরা তোমার নিকট সত্য সংবাদ নিয়ে এসেছি এবং অবশ্যই আমরা সত্যবাদী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1867,15,65,'সুতরাং তুমি রাতের শেষ প্রহরে তোমার পরিবারবর্গসহ বের হয়ে পড় এবং তুমি তাদের পশ্চাদনুসরণ কর এবং তোমাদের মধ্যে কেহ যেন পিছন ফিরে না তাকায়, তোমাদেরকে যেখানে যেতে বলা হয়েছে সেখানে চলে যাও।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1868,15,66,'আমি তাকে এ বিষয়ে প্রত্যাদেশ দিলাম যে, প্রত্যুষে তাদেরকে সমূলে বিনাশ করা হবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1869,15,67,'নগরবাসীরা আনন্দোম্মাদ হয়ে উপস্থিত হল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1870,15,68,'সে বললঃ নিশ্চয়ই এরা আমার অতিথি; সুতরাং তোমরা আমাকে বেইয্যাত করনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1871,15,69,'তোমরা আল্লাহকে ভয় কর ও আমাকে লজ্জিত করনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1872,15,70,'তারা বললঃ আমরা কি দুনিয়াবাসী লোককে আশ্রয় দিতে আপনাকে নিষেধ করিনি?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1873,15,71,'লূত বললঃ একান্তই যদি তোমরা কিছু করতে চাও তাহলে আমার এই কন্যাগণ রয়েছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1874,15,72,'তোমার জীবনের শপথ! ওরাতো আপন নেশায় মত্ত ছিল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1875,15,73,'অতঃপর সূর্যোদয়ের সময়ে মহানাদ তাদেরকে আঘাত করল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1876,15,74,'সুতরাং আমি জনপদকে উল্টে দিলাম এবং তাদের উপর প্রস্তর কংকর নিক্ষেপ করলাম।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1877,15,75,'অবশ্যই এতে নিদর্শন রয়েছে বোধশক্তি সম্পন্ন ব্যক্তিদের জন্য।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1878,15,76,'ওটা লোক চলাচলের পথপার্শ্বে এখনও বিদ্যমান।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1879,15,77,'অবশ্যই এতে মু’মিনদের জন্য রয়েছে নিদর্শন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1880,15,78,'আর ‘আয়কা’বাসীরাওতো ছিল সীমা লংঘনকারী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1881,15,79,'সুতরাং আমি তাদেরকে শাস্তি দিয়েছি, ওদের উভয়ই প্রকাশ্য পথপার্শ্বে অবস্থিত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1882,15,80,'হিজরবাসীরা রাসূলদের প্রতি মিথ্যা আরোপ করেছিল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1883,15,81,'আমি তাদেরকে আমার নিদর্শন দেখিয়েছিলাম, কিন্তু তারা তা উপেক্ষা করেছিল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1884,15,82,'তারা পাহাড় কেটে গৃহ নির্মাণ করত নিরাপদ বসবাসের জন্য।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1885,15,83,'অতঃপর প্রভাতকালে মহানাদ তাদেরকে আঘাত করল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1886,15,84,'সুতরাং তারা যা অর্জন করেছিল তা তাদের কোন কাজে আসেনি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1887,15,85,'আকাশসমূহ ও পৃথিবী এবং এ দু’য়ের অন্তবর্তী কোন কিছুই আমি অযথা সৃষ্টি করিনি; এবং কিয়ামাত অবশ্যম্ভাবী; সুতরাং তুমি পরম সৌজন্যের সাথে তাদেরকে ক্ষমা কর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1888,15,86,'নিশ্চয়ই তোমার রাব্বই মহান স্রষ্টা, মহাজ্ঞানী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1889,15,87,'আমিতো তোমাকে দিয়েছি সাত আয়াত যা পুনঃ পুনঃ আবৃত্তি করা হয় এবং দিয়েছি মহান কুরআন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1890,15,88,'আমি তাদের বিভিন্ন শ্রেণীকে ভোগ বিলাসের যে উপকরণ দিয়েছি তার প্রতি তুমি কখনও তোমার চক্ষুদ্বয় প্রসারিত করনা; তাদেরকে যা দেয়া হয়েছে সেই জন্য তুমি ক্ষোভ করনা; তুমি মু’মিনদের জন্য তোমার বাহু অবনমিত কর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1891,15,89,'আর বলঃ আমিতো একজন ভয় প্রদর্শনকারী মাত্র।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1892,15,90,'যেভাবে আমি অবতীর্ণ করেছিলাম বিভক্তকারীদের উপর,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1893,15,91,'যারা কুরআনকে বিভিন্নভাবে বিভক্ত করেছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1894,15,92,'সুতরাং তোমার রবের শপথ! আমি তাদের সকলকে প্রশ্ন করবই,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1895,15,93,'সেই বিষয়ে, যা তারা করে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1896,15,94,'অতএব তুমি যে বিষয়ে আদিষ্ট হয়েছ তা প্রকাশ্যে প্রচার কর এবং মুশরিকদের উপেক্ষা কর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1897,15,95,'আমিই যথেষ্ট তোমার জন্য, বিদ্রুপকারীদের বিরুদ্ধে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1898,15,96,'যারা আল্লাহর সাথে অপর মা‘বূদ প্রতিষ্ঠা করেছে। এবং শীঘ্রই তারা জানতে পারবে!');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1899,15,97,'আমিতো জানি যে, তারা যা বলে তাতে তোমার অন্তর সংকুচিত হয়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1900,15,98,'সুতরাং তুমি তোমার রবের প্রশংসা দ্বারা তাঁর পবিত্রতা ও মহিমা ঘোষণা কর এবং সাজদাহকারীদের অন্তর্ভুক্ত হও।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1901,15,99,'আর তোমার মৃত্যু উপস্থিত না হওয়া পর্যন্ত তুমি তোমার রবের ইবাদাত কর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1902,16,1,'আল্লাহর আদেশ আসবেই; সুতরাং ওটা ত্বরান্বিত করতে চেওনা; তিনি মহিমান্বিত এবং তারা যাকে শরীক করে তিনি তার উর্ধ্বে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1903,16,2,'তিনি তাঁর বান্দাদের মধ্যে যার প্রতি ইচ্ছা নির্দেশ সম্বলিত ওহীসহ মালাক/ফেরেশতা প্রেরণ করেন, এই মর্মে সতর্ক করার জন্য, আমি ছাড়া কোন ইলাহ নেই; সুতরাং আমাকে ভয় কর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1904,16,3,'তিনি যথাযথভাবে আকাশমন্ডলী ও পৃথিবী সৃষ্টি করেছেন; তারা যাকে শরীক করে তিনি তার উর্ধ্বে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1905,16,4,'তিনি শুক্র হতে মানুষ সৃষ্টি করেছেন। অথচ দেখ, সে প্রকাশ্য বিতন্ডাকারী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1906,16,5,'তিনি চতুস্পদ জন্তু সৃষ্টি করেছেন; তোমাদের জন্য ওতে শীত নিবারক উপকরণ এবং আরও বহু উপকার রয়েছে; এবং ওটা হতে তোমরা আহার্য পেয়ে থাক।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1907,16,6,'আর যখন তোমরা গোধূলি লগ্নে ওদেরকে চারণভূমি হতে গৃহে নিয়ে আসো এবং প্রভাতে যখন ওদেরকে চারণভূমিতে নিয়ে যাও তখন তোমরা ওর সৌন্দর্য উপভোগ কর এবং গৌরব অনুভব কর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1908,16,7,'আর ওরা তোমাদের ভার বহন করে নিয়ে যায় দূরদেশে যেখানে প্রাণান্ত ক্লেশ ব্যতীত তোমরা পৌঁছতে পারতেনা; তোমাদের রাব্ব অবশ্যই দয়ার্দ্র, পরম দয়ালু।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1909,16,8,'তোমাদের আরোহনের জন্য ও শোভার জন্য তিনি সৃষ্টি করেছেন অশ্ব, খচ্চর, গর্দভ এবং তিনি সৃষ্টি করেন এমন অনেক কিছু যা তোমরা অবগত নও।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1910,16,9,'সরল পথ আল্লাহর কাছে পৌঁছায়, কিন্তু পথগুলির মধ্যে বক্র পথও রয়েছে। তিনি ইচ্ছা করলে তোমাদের সকলকেই সৎ পথে পরিচালিত করতেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1911,16,10,'তিনিই আকাশ হতে বারি বর্ষণ করেন। ওতে তোমাদের জন্য রয়েছে পানীয় এবং তা হতে জন্মায় উদ্ভিদ যাতে তোমরা পশু চারণ করে থাক।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1912,16,11,'তিনি তোমাদের জন্য ওর দ্বারা উৎপন্ন করেন শস্য, যাইতূন, খেজুর বৃক্ষ, আঙ্গুর এবং সর্বপ্রকার ফল; অবশ্যই এতে চিন্তাশীল সম্প্রদায়ের জন্য রয়েছে নিদর্শন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1913,16,12,'তিনিই তোমাদের কল্যাণে নিয়োজিত করেছেন রাত্রী, দিন, সূর্য এবং চাঁদকে। আর নক্ষত্ররাজিও অধীন হয়েছে তাঁরই আদেশে; অবশ্যই এতে বোধশক্তিসম্পন্ন সম্প্রদায়ের জন্য রয়েছে নিদর্শন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1914,16,13,'আর বিবিধ প্রকাশ্য বস্তুও, যা তোমাদের জন্য পৃথিবীতে সৃষ্টি করেছেন। এতে রয়েছে নিদর্শন সেই সম্প্রদায়ের জন্য যারা উপদেশ গ্রহণ করে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1915,16,14,'তিনিই সমুদ্রকে অধীন করেছেন যাতে তোমরা তা হতে তাজা গোশত আহার করতে পার এবং যাতে তা হতে আহরণ করতে পার রত্নাবলী যা তোমরা ভূষণ রূপে পরিধান কর; এবং তোমরা দেখতে পাও, ওর বুক চিরে নৌযান চলাচল করে এবং তা এ জন্য যে, তোমরা যেন তাঁর অনুগ্রহ সন্ধান করতে পার এবং তোমরা যেন কৃতজ্ঞতা প্রকাশ কর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1916,16,15,'আর তিনি পৃথিবীতে সুদৃঢ় পর্বত স্থাপন করেছেন, যাতে পৃথিবী তোমাদেরকে নিয়ে আন্দোলিত না হয় এবং স্থাপন করেছেন নদ-নদী ও পথ, যাতে তোমরা তোমাদের গন্তব্যস্থলে পৌঁছতে পার।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1917,16,16,'আর পথ নির্ণায়ক চিহ্নসমূহও; এবং নক্ষত্রের সাহায্যেও মানুষ পথের নির্দেশ পায়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1918,16,17,'তবে কি যিনি সৃষ্টি করেন তিনি তারই মত যে সৃষ্টি করেনা? তবুও কি তোমরা শিক্ষা গ্রহণ করবেনা?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1919,16,18,'তোমরা আল্লাহর অনুগ্রহ গণনা করলে ওর সংখ্যা নির্ণয় করতে পারবেনা; আল্লাহ অবশ্যই ক্ষমা পরায়ণ, পরম দয়ালু।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1920,16,19,'তোমরা যা গোপন রাখ এবং যা প্রকাশ কর, আল্লাহ তা জানেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1921,16,20,'তারা আল্লাহ ছাড়া অপর যাদেরকে আহবান করে তারা কিছুই সৃষ্টি করেনা, তাদেরকেই সৃষ্টি করা হয়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1922,16,21,'তারা নিস্প্রাণ নির্জীব এবং পুনরুত্থান কবে হবে সে বিষয়ে তাদের কোন জ্ঞান নেই।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1923,16,22,'তোমাদের মা‘বূদ একই মা‘বূদ। সুতরাং যারা আখিরাতে বিশ্বাস করেনা তাদের অন্তর সত্যবিমুখ এবং তারা অহংকারী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1924,16,23,'এটা নিঃসন্দেহ যে, আল্লাহ জানেন যা তারা গোপন করে এবং যা তারা প্রকাশ করে; তিনি অহংকারীকে পছন্দ করেননা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1925,16,24,'যখন তাদেরকে বলা হয়ঃ তোমাদের রাব্ব কি অবতীর্ণ করেছেন? উত্তরে তারা বলেঃ পূর্ববর্তীদের কিস্সা-কাহিনী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1926,16,25,'ফলে কিয়ামাত দিবসে তারা বহন করবে তাদের পাপভার পূর্ণমাত্রায় এবং পাপভার তাদেরও যাদেরকে তারা অজ্ঞতা বশতঃ বিভ্রান্ত করেছে; হায়! তারা যা বহন করবে তা কতই না নিকৃষ্ট!');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1927,16,26,'তাদের পূর্ববর্তীরাও চক্রান্ত করেছিল। আল্লাহ তাদের ইমারাতের ভিত্তিমূলে আঘাত করেছিলেন; ফলে ইমারাতের ছাদ তাদের উপর ধ্বসে পড়ল এবং তাদের প্রতি শাস্তি নেমে এলো এমন দিক হতে যা ছিল তাদের ধারনার বাহির।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1928,16,27,'পরে কিয়ামাত দিবসে তিনি তাদের লাঞ্ছিত করবেন এবং বলবেনঃ কোথায় আমার সেই সব শরীক যাদের সম্বন্ধে তোমরা বিতন্ডা করতে? যাদেরকে জ্ঞান দান করা হয়েছিল তারা বলবেঃ নিশ্চয়ই আজ লাঞ্ছনা ও অমঙ্গল কাফিরদের জন্য।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1929,16,28,'মালাইকা/ফেরেশতাগণ তাদের মৃত্যু ঘটায় তাদের নিজেদের প্রতি যুলম করতে থাকা অবস্থায়; অতঃপর তারা আত্মসমর্পণ করে বলবেঃ আমরা কোন মন্দ কাজ করতামনা। হ্যাঁ, তোমরা যা করতে সে বিষয়ে আল্লাহ সবিশেষ অবহিত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1930,16,29,'সুতরাং তোমরা দ্বারগুলি দিয়ে জাহান্নামে প্রবেশ কর, সেখানে স্থায়ী হওয়ার জন্য। দেখ, অহংকারীদের আবাসস্থল কত নিকৃষ্ট!');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1931,16,30,'আর যারা মুত্তাকী তাদেরকে বলা হবেঃ তোমাদের রাব্ব কি অবতীর্ণ করেছিলেন? তারা বলবেঃ মহা কল্যাণ। যারা সৎকাজ করে তাদের জন্য রয়েছে এই দুনিয়ার মঙ্গল এবং আখিরাতের আবাস আরও উৎকৃষ্ট; আর মুত্তাকীদের আবাসস্থল কত উত্তম!');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1932,16,31,'ওটা স্থায়ী জান্নাত, যাতে তারা প্রবেশ করবে; ওর পাদদেশে স্রোতস্বিনী নদী প্রবাহিত; তারা যা কিছু কামনা করবে তাতে তাদের জন্য তা’ই থাকবে; এভাবেই আল্লাহ পুরস্কৃত করেন মুত্তাকীদেরকে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1933,16,32,'মালাইকা/ফেরেশতাগণ যাদের মৃত্যু ঘটায় পবিত্র থাকা অবস্থায়, (তাদেরকে) বলবেঃ তোমাদের প্রতি শান্তি! তোমরা যা করতে তার প্রতিদানে জান্নাতে প্রবেশ কর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1934,16,33,'তারা শুধু প্রতীক্ষা করে তাদের কাছে মালাক/ফেরেশতা আগমনের অথবা তোমার রবের শাস্তি আগমনের; আল্লাহ তাদের প্রতি কোন যুলম করেননি, কিন্তু তারাই নিজেদের প্রতি যুলম করত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1935,16,34,'সুতরাং তাদের প্রতি আপতিত হয়েছিল তাদেরই মন্দ কর্মের শাস্তি এবং তাদেরকে পরিবেষ্টন করেছিল ওটাই যা নিয়ে তারা ঠাট্টা বিদ্রুপ করত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1936,16,35,'মুশরিকরা বলেঃ আল্লাহ ইচ্ছা করলে আমাদের পিতৃ-পুরুষরা ও আমরা তিনি ব্যতীত অপর কোন কিছুর ইবাদাত করতাম না এবং তাঁর অনুমতি ব্যতীত আমরা কোন কিছু নিষিদ্ধ করতাম না। তাদের পূর্ববর্তীরাও এই রূপই করত; রাসূলদের কর্তব্য তো শুধু সুস্পষ্ট বাণী প্রচার করা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1937,16,36,'আল্লাহর ইবাদাত করার ও তাগুতকে বর্জন করার নির্দেশ দেয়ার জন্য আমি প্রত্যেক জাতির মধ্যেই রাসূল পাঠিয়েছি; অতঃপর তাদের কতককে আল্লাহ সৎ পথে পরিচালিত করেন এবং তাদের কতকের উপর পথভ্রান্তি সাব্যস্ত হয়েছিল। সুতরাং পৃথিবীতে ভ্রমণ কর এবং দেখ, যারা সত্যকে মিথ্যা বলেছে তাদের পরিনাম কি হয়েছে!');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1938,16,37,'তুমি তাদেরকে পথ প্রদর্শন করতে আগ্রহী হলেও আল্লাহ যাকে বিভ্রান্ত করেছেন তাকে তিনি সৎ পথে পরিচালিত করবেননা এবং তাদের কোন সাহায্যকারীও নেই।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1939,16,38,'তারা দৃঢ়তার সাথে আল্লাহর শপথ করে বলেঃ যার মৃত্যু হয় আল্লাহ তাকে পুনরুজ্জীবিত করবেননা। কেন নয়? তিনি তাঁর প্রতিশ্রুতি পূর্ণ করবেনই; কিন্তু অধিকাংশ মানুষ এটা অবগত নয়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1940,16,39,'তিনি পুনরুত্থিত করবেন, যে বিষয়ে তাদের মতানৈক্য ছিল তা তাদেরকে স্পষ্টভাবে দেখানোর জন্য এবং যাতে কাফিরেরা জানতে পারে যে, তারাই ছিল মিথ্যাবাদী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1941,16,40,'আমি কোন কিছু ইচ্ছা করলে সেই বিষয়ে আমার কথা শুধু এই যে, আমি বলি ‘হও,’ ফলে তা হয়ে যায়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1942,16,41,'যারা অত্যাচারিত হওয়ার পর আল্লাহর পথে হিজরাত করেছে আমি অবশ্যই তাদেরকে দুনিয়ায় উত্তম আবাস প্রদান করব এবং আখিরাতের পুরষ্কারইতো শ্রেষ্ঠ। হায়! তারা যদি ওটা জানত!');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1943,16,42,'তারা ধৈর্য ধারণ করে এবং তাদের রবের উপর নির্ভর করে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1944,16,43,'তোমার পূর্বে আমি অহীসহ (পুরুষ) মানুষই প্রেরণ করেছিলাম, তোমরা যদি না জান তাহলে জ্ঞানীদেরকে জিজ্ঞেস কর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1945,16,44,'তাদের প্রেরণ করেছিলাম স্পষ্ট নিদর্শন ও গ্রন্থসহ এবং তোমার প্রতি কুরআন অবতীর্ণ করেছি মানুষকে স্পষ্টভাবে বুঝিয়ে দেয়ার জন্য, যা তাদের প্রতি অবতীর্ণ করা হয়েছিল, যাতে তারা চিন্তা ভাবনা করে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1946,16,45,'যারা দুস্কর্মের ষড়যন্ত্র করে তারা কি এ বিষয়ে নিশ্চিত যে, আল্লাহ তাদেরকে ভূ-গর্ভে বিলীন করবেন না, অথবা এমন দিক হতে শাস্তি আসবে না যা তাদের ধারণাতীত?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1947,16,46,'অথবা চলাফিরা করতে থাকাকালে তিনি তাদেরকে ধৃত করবেননা? তারাতো এটা ব্যর্থ করতে পারবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1948,16,47,'অথবা তাদেরকে তিনি ভীত সন্ত্রস্ত অবস্থায় পাকড়াও করবেননা? তোমাদের রাব্বতো অবশ্যই ক্ষমাকারী, পরম দয়ালু।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1949,16,48,'তারা কি লক্ষ্য করেনা আল্লাহর সৃষ্ট বস্তুর প্রতি, যার ছায়া ডানে ও বামে ঢলে পড়ে আল্লাহর প্রতি সাজদাহয় নত হয়?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1950,16,49,'আল্লাহকেই সাজদাহ করে যা কিছু সৃষ্টি রয়েছে আকাশমন্ডলীতে এবং পৃথিবীতে এবং মালাইকাগণও; তারা অহংকার করেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1951,16,50,'তারা ভয় করে, তাদের উপর পরাক্রমশালী তাদের রাব্বকে এবং তাদেরকে যা আদেশ করা হয় তারা তা পালন করে। [সাজদাহ]');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1952,16,51,'আল্লাহ বলেনঃ তোমরা দুই ইলাহ গ্রহণ করনা; তিনিই একমাত্র ইলাহ, সুতরাং তোমরা আমাকেই ভয় কর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1953,16,52,'আকাশমন্ডলী ও পৃথিবীতে যা কিছু আছে তা তাঁরই; এবং নিরবচ্ছিন্ন আনুগত্য তাঁরই প্রাপ্য; তোমরা কি আল্লাহ ব্যতীত অন্যকে ভয় করবে?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1954,16,53,'তোমরা যে সব অনুগ্রহ ভোগ কর তাতো আল্লাহরই নিকট হতে। অধিকন্ত যখন দুঃখ দৈন্য তোমাদেরকে স্পর্শ করে তখন তোমরা তাঁকেই ব্যাকুলভাবে আহবান কর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1955,16,54,'আবার যখন (আল্লাহ) তোমাদের দুঃখ-দৈন্য দূর করেন তখন তোমাদের এক দল তাদের রবের সাথে শরীক করে ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1956,16,55,'আমি তাদেরকে যা দান করেছি তা অস্বীকার করার জন্য, সুতরাং তোমরা ভোগ করে নাও, অচিরেই জানতে পারবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1957,16,56,'আমি তাদেরকে যে রিয্ক দান করি তারা তার এক অংশ নির্ধারিত করে তাদের জন্য যাদের সম্বন্ধে তারা কিছুই জানেনা। শপথ আল্লাহর! তোমরা যে মিথ্যা উদ্ভাবন কর সেই সম্বন্ধে তোমাদেরকে প্রশ্ন করা হবেই।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1958,16,57,'তারা নির্ধারণ করে আল্লাহর জন্য কন্যা সন্তান। তিনি পবিত্র মহিমান্বিত, এবং তাদের জন্য ওটাই যা তারা কামনা করে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1959,16,58,'তাদের কেহকে যখন কন্যা সন্তানের সুসংবাদ দেয়া হয় তখন তার মুখমন্ডল কালো হয়ে যায় এবং সে অসহনীয় মনস্তাপে ক্লিষ্ট হয়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1960,16,59,'তাকে যে সংবাদ দেয়া হয়, তার গ্লানি হেতু সে নিজ সম্প্রদায় হতে আত্মগোপন করে। সে চিন্তা করে যে, হীনতা সত্ত্বেও সে তাকে রেখে দিবে, নাকি মাটিতে পুঁতে ফেলবে। সাবধান! তারা যা সিদ্ধান্ত নেয় তা কতই না নিকৃষ্ট!');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1961,16,60,'যারা আখিরাতে বিশ্বাস করেনা তারা নিকৃষ্ট প্রকৃতির সদৃশ। আর আল্লাহতো মহত্তম প্রকৃতির অধিকারী; এবং তিনি পরাক্রমশালী, প্রজ্ঞাময়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1962,16,61,'আল্লাহ যদি মানুষকে তাদের সীমা লংঘনের জন্য শাস্তি দিতেন তাহলে ভূপৃষ্ঠে কোন জীব জন্তুকেই রেহাই দিতেননা; কিন্তু তিনি এক নির্দিষ্ট কাল পর্যন্ত তাদেরকে অবকাশ দিয়ে থাকেন। অতঃপর যখন তাদের সময় আসে তখন তারা মুহুর্তকাল বিলম্ব অথবা ত্বরা করতে পারবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1963,16,62,'যা তারা অপছন্দ করে তাই তারা আল্লাহর প্রতি আরোপ করে। তাদের জিহবা মিথ্যা বর্ণনা করে যে, মঙ্গল তাদেরই জন্য। নিশ্চয়ই তাদের জন্য রয়েছে জাহান্নাম এবং তাদেরকেই সর্বাগ্রে তাতে নিক্ষেপ করা হবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1964,16,63,'শপথ আল্লাহর! আমি তোমার পূর্বেও বহু জাতির নিকট রাসূল প্রেরণ করেছি; কিন্তু শাইতান ঐ সব জাতির কার্যকলাপ তাদের দৃষ্টিতে শোভন করেছিল; সুতরাং সে’ই আজ তাদের অভিভাবক এবং তাদেরই জন্য রয়েছে যন্ত্রণাদায়ক শাস্তি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1965,16,64,'আমিতো তোমার প্রতি এই কিতাব অবতীর্ণ করেছি যারা এ বিষয়ে মতভেদ করে তাদেরকে সুস্পষ্টভাবে বুঝিয়ে দেয়ার জন্য এবং মু’মিনদের জন্য পথ-নির্দেশ ও দয়া স্বরূপ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1966,16,65,'আর আল্লাহ আসমান থেকে বৃষ্টি বর্ষণ করেছেন। অতঃপর তা দিয়ে যমীনকে তার মৃত্যুর পর সজীব করেছেন। নিশ্চয় এতে নিদর্শন রয়েছে সেই কওমের জন্যা যারা শুনে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1967,16,66,'অবশ্যই (গৃহপালিত) চতুস্পদ জন্তুর মধ্যে তোমাদের জন্য শিক্ষা রয়েছে; ওগুলির উদরস্থিত গোবর ও রক্তের মধ্য হতে তোমাদেরকে আমি পান করাই বিশুদ্ধ দুগ্ধ, যা পানকারীদের জন্য সুস্বাদু।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1968,16,67,'আর খেজুর গাছের ফল ও আঙ্গুর হতে তোমরা মাদক ও উত্তম খাদ্য গ্রহণ করে থাক, এতে অবশ্যই বোধশক্তি সম্পন্ন সম্প্রদায়ের জন্য রয়েছে নিদর্শন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1969,16,68,'তোমার রাব্ব মৌমাছির অন্তরে ইঙ্গিত দ্বারা নির্দেশ দিয়েছেনঃ তুমি গৃহ নির্মাণ কর পাহাড়, বৃক্ষ এবং মানুষ যে গৃহ নির্মাণ করে তাতে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1970,16,69,'এর পর প্রত্যেক ফল হতে কিছু কিছু আহার কর, অতঃপর তোমার রবের সহজ পথ অনুসরণ কর। ওর উদর হতে নির্গত হয় বিবিধ বর্ণের পানীয়, যাতে মানুষের জন্য রয়েছে রোগের প্রতিষেধক। অবশ্যই এতে রয়েছে নিদর্শন চিন্তাশীল সম্প্রদায়ের জন্য।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1971,16,70,'আল্লাহই তোমাদেরকে সৃষ্টি করেছেন। অতঃপর তিনি তোমাদের মৃত্যু ঘটাবেন এবং তোমাদের মধ্যে কেহকে কেহকে উপনীত করা হয় জরাজীর্ণ বয়সে। ফলে তারা যা কিছু জানত সে সম্বন্ধে তারা সজ্ঞান থাকেনা; আল্লাহ সর্বজ্ঞ, সর্বশক্তিমান।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1972,16,71,'আল্লাহ জীবনোপকরণে তোমাদের একজনকে অন্যজনের উপর শ্রেষ্ঠত্ব দিয়েছেন। যাদেরকে শ্রেষ্ঠত্ব দেয়া হয়েছে তারা তাদের অধীনস্থ দাস দাসীদেরকে নিজেদের জীবনোপকরণ হতে এমন কিছু দেয়না যাতে তারা এ বিষয়ে সমান হয়ে যায়; তাহলে কি তারা আল্লাহর অনুগ্রহ অস্বীকার করে?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1973,16,72,'আর আল্লাহ তোমাদের হতেই তোমাদের জোড়া সৃষ্টি করেছেন এবং তোমাদের যুগল হতে তোমাদের জন্য পুত্র-পৌত্রাদি সৃষ্টি করেছেন এবং উত্তম জীবনোপকরণ দান করেছেন। তবুও কি তারা মিথ্যা বিষয়ে বিশ্বাস করবে এবং আল্লাহর অনুগ্রহ অস্বীকার করবে?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1974,16,73,'এবং তারা কি ইবাদাত করবে আল্লাহ ছাড়া অপরের যাদের আকাশমন্ডলী অথবা পৃথিবী হতে কোন জীবনোপকরণ সরবরাহ করার শক্তি নেই? এবং তারা কিছুই করতে সক্ষম নয় ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1975,16,74,'সুতরাং তোমরা আল্লাহর কোন সদৃশ স্থির করনা; নিশ্চয়ই আল্লাহ জানেন এবং তোমরা জাননা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1976,16,75,'আল্লাহ উপমা দিচ্ছেন অপরের অধিকারভুক্ত এক দাসের, যে কোন কিছুর উপর শক্তি রাখেনা। এবং অপর এক ব্যক্তি যাকে তিনি নিজ হতে উত্তম রিয্ক দান করেছেন এবং সে তা হতে গোপনে ও প্রকাশ্যে ব্যয় করে; তারা কি উভয়ে একে অপরের সমান? সকল প্রশংসা আল্লাহরই প্রাপ্য; অথচ তাদের অধিকাংশ এটা জানেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1977,16,76,'আল্লাহ আরও উপমা দিচ্ছেন দু’ ব্যক্তির। ওদের একজন মূক, কোন কিছুরই শক্তি রাখেনা এবং সে তার মালিকের জন্য বোঝা স্বরূপ। তাকে যেখানেই পাঠানো হোক না কেন সে ভাল কিছুই করে আসতে পারেনা; সে কি ঐ ব্যক্তির মত সমান হবে যে ন্যায়ের নির্দেশ দেয় এবং যে আছে সরল পথে?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1978,16,77,'আকাশমন্ডলী ও পৃথিবীর অদৃশ্য বিষয়ের জ্ঞান আল্লাহরই এবং কিয়ামাতের ব্যাপারতো চোখের পলকের ন্যায়, বরং ওর চেয়েও সত্ত্বর; আল্লাহ সর্ব বিষয়ে সর্বশক্তিমান।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1979,16,78,'আর আল্লাহ তোমাদেরকে নির্গত করেছেন তোমাদের মাতৃগর্ভ হতে এমন অবস্থায় যে, তোমরা কিছুই জানতেনা, এবং তিনি তোমাদেরকে দিয়েছেন শ্রবণশক্তি, দৃষ্টিশক্তি এবং হৃদয়, যাতে তোমরা কৃতজ্ঞতা প্রকাশ কর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1980,16,79,'তারা কি লক্ষ্য করেনা আকাশের শূন্য গর্ভে নিয়ন্ত্রণাধীন বিহংগের প্রতি? আল্লাহই ওদেরকে স্থির রাখেন; অবশ্যই এতে নিদর্শন রয়েছে মু’মিন সম্প্রদায়ের জন্য।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1981,16,80,'এবং আল্লাহ তোমাদের গৃহকে করেন তোমাদের আবাসস্থল, আর তিনি তোমাদের জন্য পশুচর্মের তাবুর ব্যবস্থা করেন; ওটা বহনকালে (তোমাদের ভ্রমণকালে) এবং ওতে অবস্থানকালে তোমরা তা সহজে বহন করতে পার। তিনি তোমাদের জন্য ব্যবস্থা করেন ওদের পশম, লোম ও কেশ হতে কিছু কালের জন্য গৃহ সামগ্রী ও ব্যবহার উপকরণ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1982,16,81,'আর আল্লাহ যা কিছু সৃষ্টি করেছেন তাতে তোমাদের জন্য ছায়ার ব্যবস্থা করেন এবং তিনি তোমাদের জন্য পাহাড়ে আশ্রয়ের ব্যবস্থা করেন এবং তোমাদের জন্য ব্যবস্থা করেন পরিধেয় বস্ত্রের; ওটা তোমাদেরকে তাপ হতে রক্ষা করে এবং তিনি ব্যবস্থা করেন তোমাদের জন্য বর্মের, ওটা তোমাদেরকে যুদ্ধে রক্ষা করে; এভাবে তিনি তোমাদের প্রতি তাঁর অনুগ্রহ পূর্ণ করেন যাতে তোমরা আত্মসমর্পণ কর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1983,16,82,'অতঃপর তারা যদি মুখ ফিরিয়ে নেয় তাহলে তোমার কর্তব্যতো শুধু স্পষ্টভাবে বাণী পৌঁছে দেয়া।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1984,16,83,'তারা আল্লাহর অনুগ্রহ জ্ঞাত আছে; কিন্তু সেগুলি তারা অস্বীকার করে এবং তাদের অধিকাংশই কাফির।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1985,16,84,'যেদিন আমি প্রত্যেক সম্প্রদায় হতে এক একজন সাক্ষী উত্থিত করব সেদিন কাফিরদেরকে অনুমতি দেয়া হবেনা এবং তাদেরকে (আল্লাহর) সন্তুষ্টি লাভের সুযোগ দেয়া হবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1986,16,85,'যখন যালিমরা শাস্তি প্রত্যক্ষ করবে তখন তাদের শাস্তি লঘু করা হবেনা এবং তাদেরকে কোন বিরাম দেয়া হবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1987,16,86,'মুশরিকরা যাদেরকে (আল্লাহর) শরীক করেছিল তাদেরকে দেখে বলবেঃ হে আমাদের রাব্ব! এরাই তারা যাদেরকে আমরা আপনার শরীক করেছিলাম, যাদেরকে আমরা আহবান করতাম আপনার পরিবর্তে; অতঃপর তদুত্তরে তারা বলবেঃ তোমরা অবশ্যই মিথ্যাবাদী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1988,16,87,'সেদিন তারা আল্লাহর কাছে আত্মসমর্পণ করবে এবং তারা যে মিথ্যা উদ্ভাবন করত তা তাদের জন্য নিষ্ফল হবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1989,16,88,'যারা কুফরী করেছে ও আল্লাহর পথ হতে বাধা দিয়েছে, আমি তাদের শাস্তির উপর শাস্তি বৃদ্ধি করব।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1990,16,89,'সেদিন আমি উত্থিত করব প্রত্যেক সম্প্রদায় হতে তাদের বিষয়ে এক একজন সাক্ষী এবং তোমাকে আমি আনব সাক্ষী রূপে এদের বিষয়ে; আমি আত্মসমর্পণকারীদের (মুসলিম) জন্য প্রত্যেক বিষয়ে স্পষ্ট ব্যাখ্যাস্বরূপ, পথ নির্দেশ, দয়া ও সুসংবাদ স্বরূপ তোমার প্রতি কিতাব অবতীর্ণ করেছি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1991,16,90,'নিশ্চয়ই আল্লাহ ন্যায় পরায়ণতা, সদাচরণ ও আত্মীয় স্বজনকে দানের নির্দেশ দেন এবং তিনি নিষেধ করেন অশ্লীলতা, অসৎ কাজ ও সীমা লংঘন করতে। তিনি তোমাদেরকে উপদেশ দেন যাতে তোমরা শিক্ষা গ্রহণ কর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1992,16,91,'তোমরা আল্লাহর অঙ্গীকার পূর্ণ কর যখন পরস্পর অঙ্গীকার কর এবং তোমরা শপথ দৃঢ় করার পর তা ভঙ্গ করনা; তোমরা যা কর আল্লাহ তা জানেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1993,16,92,'তোমরা সেই নারীর মত হয়োনা, যে তার সূতা মাযবূত করে পাকানোর পর ওর পাক খুলে নষ্ট করে দেয়। তোমাদের শপথ তোমরা পরস্পরকে প্রবঞ্চনা করার জন্য ব্যবহার করে থাকো, যাতে একদল অন্যদল অপেক্ষা অধিক লাভবান হও; আল্লাহতো এটা দ্বারা শুধু তোমাদেরকে পরীক্ষা করেন; তোমাদের যে বিষয়ে মতভেদ রয়েছে, কিয়ামাত দিবসে তিনি তা নিশ্চয়ই স্পষ্টভাবে প্রকাশ করে দিবেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1994,16,93,'যদি আল্লাহ ইচ্ছা করতেন তাহলে তোমাদেরকে এক জাতি করতে পারতেন, কিন্তু তিনি যাকে ইচ্ছা বিভ্রান্ত করেন এবং যাকে ইচ্ছা সৎ পথে পরিচালিত করেন; তোমরা যা কর সে বিষয়ে অবশ্যই তোমাদেরকে প্রশ্ন করা হবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1995,16,94,'পরস্পর প্রবঞ্চনা করার জন্য তোমরা তোমাদের শপথকে ব্যবহার করনা; তাহলে পা স্থির হওয়ার পর পিছলে যাবে এবং আল্লাহর পথে বাধা দেয়ার কারণে তোমরা শাস্তির আস্বাদ গ্রহণ করবে। তোমাদের জন্য রয়েছে কঠোর শাস্তি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1996,16,95,'তোমরা আল্লাহর সঙ্গে কৃত অংগীকার তুচ্ছ মূল্যে বিক্রি করনা; আল্লাহর কাছে যা আছে শুধু তা’ই তোমাদের জন্য উত্তম, যদি তোমরা জানতে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1997,16,96,'তোমাদের কাছে যা আছে তা নিঃশেষ হবে এবং আল্লাহর কাছে যা আছে তা স্থায়ী; যারা ধৈর্য ধারণ করে আমি নিশ্চয়ই তাদেরকে তারা যে উত্তম কাজ করে তা অপেক্ষা শ্রেষ্ঠ পুরস্কার দান করব।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1998,16,97,'মু’মিন পুরুষ ও নারীর মধ্যে যে কেহ সৎকাজ করবে তাকে আমি নিশ্চয়ই আনন্দময় জীবন দান করব এবং তাদেরকে তাদের কর্মের শ্রেষ্ঠ পুরস্কার প্রদান করব।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (1999,16,98,'যখন তুমি কুরআন পাঠ করবে তখন অভিশপ্ত শাইতান হতে আল্লাহর আশ্রয় গ্রহণ করবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2000,16,99,'তার কোন আধিপত্য নেই তাদের উপর যারা ঈমান আনে ও তাদের রবের উপরই নির্ভর করে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2001,16,100,'তার আধিপত্য শুধু তাদেরই উপর যারা তাকে অভিভাবক রূপে গ্রহণ করে এবং যারা (আল্লাহর) সাথে শরীক করে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2002,16,101,'আমি যখন এক আয়াতের পরিবর্তে অন্য এক আয়াত উপস্থিত করি, আর আল্লাহ যা অবতীর্ণ করেন তা তিনিই ভাল জানেন, তখন তারা বলেঃ তুমিতো শুধু মিথ্যা উদ্ভাবনকারী, কিন্তু তাদের অধিকাংশই জানেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2003,16,102,'তুমি বলঃ তোমার রবের নিকট হতে রূহুল কুদুস (জিবরাঈল) সত্যসহ কুরআন অবতীর্ণ করেছেন যারা মু’মিন তাদেরকে দৃঢ় প্রতিষ্ঠিত করার জন্য এবং হিদায়াত ও সুসংবাদ স্বরূপ আত্মসমর্পনকারীদের জন্য।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2004,16,103,'আমিতো জানিই তারা বলেঃ তাকে শিক্ষা দেয় জনৈক ব্যক্তি। তারা যার প্রতি এটা আরোপ করে তার ভাষাতো আরাবী নয়; কিন্তু কুরআনের ভাষা স্পষ্ট আরাবী ভাষা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2005,16,104,'যারা আল্লাহর আয়াত বিশ্বাস করেনা তাদেরকে আল্লাহ হিদায়াত করেননা এবং তাদের জন্য রয়েছে বেদনাদায়ক শাস্তি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2006,16,105,'যারা আল্লাহর নিদর্শনে বিশ্বাস করেনা তারাতো শুধু মিথ্যা উদ্ভাবক এবং তারাই মিথ্যাবাদী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2007,16,106,'কেহ ঈমান আনার পর আল্লাহকে অস্বীকার করলে এবং কুফরীর জন্য হৃদয় উন্মুক্ত রাখলে তার উপর আপতিত হবে আল্লাহর গযব এবং তার জন্য আছে মহা শাস্তি; তবে তার জন্য নয়, যাকে কুফরীর জন্য বাধ্য করা হয়, কিন্তু তার চিত্ত ঈমানে অবিচল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2008,16,107,'এটা এ জন্য যে, তারা দুনিয়ার জীবনকে আখিরাতের উপর প্রাধান্য দেয় এবং এ জন্য যে, আল্লাহ কাফির সম্প্রদায়কে হিদায়াত করেননা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2009,16,108,'ওরাই তারা, আল্লাহ যাদের অন্তর, কর্ণ ও চক্ষুর উপর মোহর করে দিয়েছেন এবং তারাই গাফিল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2010,16,109,'নিশ্চয়ই তারা আখিরাতে হবে ক্ষতিগ্রস্ত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2011,16,110,'(তোমার রবের পথে থেকে) যারা নির্যাতিত হবার পর হিজরাত করে এবং পরে জিহাদ করে এবং ধৈর্য ধারণ করে; তোমার রাব্ব এসব কিছুর পর, তাদের প্রতি অবশ্যই ক্ষমাশীল, পরম দয়ালু।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2012,16,111,'স্মরণ কর সেই দিনকে যেদিন আত্মপক্ষ সমর্থনে যুক্তি উপস্থিত করতে আসবে প্রত্যেক ব্যক্তি এবং প্রত্যেককে তার কৃতকর্মের পূর্ণ ফল দেয়া হবে এবং তাদের প্রতি যুলম করা হবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2013,16,112,'আল্লাহ দৃষ্টান্ত দিচ্ছেন এক জনপদের যা ছিল নিরাপদ ও নিশ্চিন্ত, যেখানে আসত সব দিক হতে প্রচুর জীবনোপকরণ; অতঃপর ওরা আল্লাহর অনুগ্রহ অস্বীকার করল। ফলে তাদের কৃতকর্মের কারণে আল্লাহ তাদেরকে আস্বাদ গ্রহণ করালেন ক্ষুধা ও ভীতির।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2014,16,113,'তাদের নিকট এসেছিল এক রাসূল তাদেরই মধ্য হতে, কিন্তু তারা তাকে অস্বীকার করেছিল; ফলে সীমা লংঘন করা অবস্থায় শাস্তি তাদেরকে গ্রাস করল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2015,16,114,'আল্লাহ তোমাদেরকে যা দিয়েছেন তম্মধ্যে যা বৈধ ও পবিত্র তা তোমরা আহার কর এবং তোমরা যদি শুধু আল্লাহরই ইবাদাত কর তাহলে তাঁর অনুগ্রহের জন্য কৃতজ্ঞতা প্রকাশ কর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2016,16,115,'তিনি (আল্লাহ) শুধুমাত্র মৃত, রক্ত, শূকরের মাংস এবং যা যবাহকালে আল্লাহর পরিবর্তে অন্যের নাম নেয়া হয়েছে তা’ই তোমাদের জন্য অবৈধ করেছেন, কিন্তু কেহ অনন্যোপায় কিংবা সীমা লংঘনকারী না হয়ে অনন্যোপায়ী হলে আল্লাহতো ক্ষমাশীল, পরম দয়ালু।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2017,16,116,'তোমাদের জিহবা থেকে সাধারণতঃ যে সব মিথ্যা কথা বের হয়ে আসে সেরূপ তোমরা আল্লাহর বিরুদ্ধে মিথ্যা অপবাদ আরোপ করে বলনা - এটা হালাল এবং ওটা হারাম। যারা আল্লাহ সম্বন্ধে মিথ্যা উদ্ভাবন করবে তারা সফলকাম হবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2018,16,117,'তাদের সুখ সম্ভোগ সামান্য এবং তাদের জন্য রয়েছে বেদনাদায়ক শাস্তি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2019,16,118,'ইয়াহুদীদের জন্য আমি শুধু তা’ই নির্ধারণ করেছিলাম যা তোমার নিকট আমি পূর্বে উল্লেখ করেছি এবং আমি তাদের উপর কোন যুলম করিনি, কিন্তু তারাই যুলম করত তাদের নিজেদের প্রতি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2020,16,119,'যারা অজ্ঞতা বশতঃ খারাপ কাজ করে তারা পরে তাওবাহ করলে এবং নিজেদেরকে সংশোধন করলে তাদের জন্য তোমার রাব্ব অবশ্যই অতি ক্ষমাশীল, পরম দয়ালু।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2021,16,120,'নিশ্চয়ই ইবরাহীম ছিল এক উম্মাত আল্লাহর অনুগত, একনিষ্ঠ এবং সে ছিলনা মুশরিকদের অন্তর্ভুক্ত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2022,16,121,'সে ছিল আল্লাহর অনুগ্রহের জন্য কৃতজ্ঞ; আল্লাহ তাকে মনোনীত করেছিলেন এবং তাকে পরিচালিত করেছিলেন সরল পথে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2023,16,122,'আমি তাকে দুনিয়ায় দিয়েছিলাম মঙ্গল এবং আখিরাতেও নিশ্চয়ই সে সৎকর্মপরায়ণদের অন্যতম।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2024,16,123,'এখন আমি তোমার প্রতি প্রত্যাদেশ করলাম, তুমি একনিষ্ঠ ইবরাহীমের ধর্মাদর্শ অনুসরণ কর; এবং সে মুশরিকদের অন্তর্ভুক্ত ছিলনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2025,16,124,'শনিবার পালনতো শুধু তাদের জন্য বাধ্যতামূলক করা হয়েছিল যারা এ সম্বন্ধে মতভেদ করত। যে বিষয়ে তারা মতভেদ করত তোমার রাব্ব অবশ্যই কিয়ামাত দিবসে সেই বিষয়ে তাদের মীমাংসা করে দিবেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2026,16,125,'তুমি মানুষকে তোমার রবের পথে আহবান কর হিকমাত ও সদুপদেশ দ্বারা এবং তাদের সাথে আলোচনা কর সুন্দরভাবে। তোমার রাব্ব ভাল করেই জানেন কে তাঁর পথ ছেড়ে বিপথগামী এবং কে সৎ পথে আছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2027,16,126,'যদি তোমরা প্রতিশোধ গ্রহণ কর তাহলে ঠিক ততখানি করবে যতখানি অন্যায় তোমাদের প্রতি করা হয়েছে; তবে তোমরা ধৈর্য ধারণ করলে ধৈর্যশীলদের জন্য ওটাই উত্তম।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2028,16,127,'তুমি ধৈর্য ধারণ কর; তোমার ধৈর্য হবে আল্লাহরই সাহায্যে; তাদের জন্য দুঃখ করনা এবং তাদের ষড়যন্ত্রে তুমি মনঃক্ষুন্ন হয়োনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2029,16,128,'নিশ্চয়ই আল্লাহ তাদেরই সঙ্গে আছেন যারা তাকওয়া অবলম্বন করে এবং যারা সৎ কর্মপরায়ণ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2030,17,1,'পবিত্র ও মহিমাময় তিনি যিনি তাঁর বান্দাকে রাতে ভ্রমণ করিয়েছিলেন মাসজিদুল হারাম হতে মাসজিদুল আকসায়, যার পরিবেশ আমি করেছিলাম বারাকাতময়, তাকে আমার নিদর্শন দেখানোর জন্য; তিনিই সর্বশ্রোতা, সর্বদ্রষ্টা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2031,17,2,'আমি মূসাকে কিতাব দিয়েছিলাম এবং তাকে করেছিলাম বানী ইসরাঈলের জন্য পথ নির্দেশক। আমি আদেশ করেছিলাম, তোমরা আমি ব্যতীত অপর কেহকেও কর্ম বিধায়করূপে গ্রহণ করনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2032,17,3,'তোমরাইতো তাদের বংশধর যাদেরকে আমি নূহের সাথে নৌকায় আরোহণ করিয়েছিলাম, সে ছিল পরম কৃতজ্ঞ দাস।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2033,17,4,'এবং আমি কিতাবে (তাওরাতে) প্রত্যাদেশ দ্বারা বানী ইসরাঈলকে জানিয়েছিলাম, নিশ্চয়ই তোমরা পৃথিবীতে দু’বার বিপর্যয় সৃষ্টি করবে এবং তোমরা অতিশয় উদ্ধত্যকারী হবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2034,17,5,'অতঃপর এই দু’এর প্রথমটির নির্ধারিত কাল যখন উপস্থিত হল তখন আমি তোমাদের বিরুদ্ধে প্রেরণ করেছিলাম আমার দাসদেরকে, যুদ্ধে অতিশয় শক্তিশালী; তারা ঘরে ঘরে প্রবেশ করে সমস্ত কিছু ধ্বংস করেছিল; শাস্তির প্রতিজ্ঞা কার্যকরী হয়েই থাকে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2035,17,6,'অতঃপর আমি তোমাদের পুনরায় তাদের উপর প্রতিষ্ঠিত করলাম, তোমাদেরকে ধন-সম্পদ ও সন্তান-সন্ততি দ্বারা সাহায্য করলাম ও সংখ্যাগরিষ্ঠ করলাম।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2036,17,7,'তোমরা সৎ কাজ করলে তা নিজেদেরই জন্য করবে এবং মন্দ কাজ করলে তাও করবে নিজেদের জন্য; অতঃপর পরবর্তী নির্ধারিত সময় উপস্থিত হলে আমি আমার দাসদেরকে প্রেরণ করলাম তোমাদের মুখমন্ডল কালিমাচ্ছন্ন করার জন্য, প্রথমবার তারা যেভাবে মাসজিদে প্রবেশ করেছিল পুনরায় সেভাবেই তাতে প্রবেশ করার জন্য এবং তারা যা অধিকার করেছিল তা সম্পূর্ণ রূপে ধ্বংস করার জন্য।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2037,17,8,'সম্ভবতঃ তোমাদের রাব্ব তোমাদের প্রতি দয়া করবেন। কিন্তু তোমরা যদি তোমাদের পূর্ব আচরণের পুনরাবৃত্তি কর তাহলে তিনিও তাঁর আচরণের পুনরাবৃত্তি করবেন; জাহান্নামকে আমি করেছি সত্য প্রত্যাখ্যানকারীদের জন্য কারাগার।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2038,17,9,'এই কুরআন সর্বশ্রেষ্ঠ পথ নির্দেশ করে এবং সৎ কর্মপরায়ণ বিশ্বাসীদেরকে সুসংবাদ দেয় যে, তাদের জন্য রয়েছে মহা পুরস্কার।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2039,17,10,'আর যারা পরকাল বিশ্বাস করেনা তাদের জন্য আমি প্রস্তুত করে রেখেছি মর্মন্তুদ শাস্তি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2040,17,11,'মানুষ যেভাবে কল্যাণ কামনা করে সেভাবেই অকল্যাণ কামনা করে। মানুষতো অতি ত্বরাপ্রবণ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2041,17,12,'আমি রাত ও দিনকে করেছি দু’টি নিদর্শন; রাতকে করেছি নিরালোক এবং দিনকে করেছি আলোকময়, যাতে তোমরা তোমাদের রবের অনুগ্রহ সন্ধান করতে পার এবং যাতে তোমরা বর্ষ সংখ্যা ও হিসাব স্থির করতে পার; এবং আমি সব কিছু বিশদভাবে বর্ণনা করেছি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2042,17,13,'প্রত্যেক মানুষের কৃতকর্ম আমি তার গ্রীবালগ্ন করেছি এবং কিয়ামাত দিবসে আমি তার জন্য বের করব এক কিতাব, যা সে পাবে উন্মুক্ত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2043,17,14,'(আমি বলব) তুমি তোমার কিতাব পাঠ কর; আজ তুমি নিজেই তোমার হিসাব নিকাশের জন্য যথেষ্ট।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2044,17,15,'যারা সৎ পথ অবলম্বন করবে তারাতো নিজেদেরই মঙ্গলের জন্য তা অবলম্বন করবে এবং যারা পথভ্রষ্ট হবে তারাতো পথভ্রষ্ট হবে নিজেদেরই ধ্বংসের জন্য এবং কেহ অন্য কারও ভার বহন করবেনা; আমি রাসূল না পাঠানো পর্যন্ত কেহকেও শাস্তি দিইনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2045,17,16,'যখন আমি কোন জনপদ ধ্বংস করার ইচ্ছা করি তখন ওর সমৃদ্ধশালী ব্যক্তিদেরকে সৎ কাজ করতে আদেশ করি, কিন্তু তারা সেখানে অসৎ কাজ করে। অতঃপর ওর প্রতি দন্ডাজ্ঞা ন্যায় সঙ্গত হয়ে যায় এবং আমি ওটাকে সম্পূর্ণ রূপে বিধ্বস্ত করি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2046,17,17,'নূহের পর আমি কত মানবগোষ্ঠী ধ্বংস করেছি। তোমার রাব্বই তাঁর দাসদের পাপাচারণের সংবাদ রাখা ও পর্যবেক্ষণের জন্য যথেষ্ট।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2047,17,18,'কেহ পার্থিব সুখ সম্ভোগ কামনা করলে আমি যাকে যা ইচ্ছা সত্ত্বর দিয়ে থাকি; পরে তার জন্য জাহান্নাম নির্ধারিত করি যেখানে সে প্রবেশ করবে নিন্দিত ও অনুগ্রহ হতে বঞ্চিত অবস্থায়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2048,17,19,'যারা বিশ্বাসী হয়ে পরকাল কামনা করে এবং ওর জন্য যথাযথ চেষ্টা করে তাদের প্রচেষ্টাসমূহ আল্লাহর কাছে গৃহীত হবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2049,17,20,'তোমার রাব্ব তাঁর দান দ্বারা এদেরকে এবং ওদেরকে সাহায্য করেন এবং তোমার রবের দান অবারিত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2050,17,21,'লক্ষ্য কর, আমি কিভাবে তাদের এক দলকে অপরের উপর শ্রেষ্ঠত্ব দিয়েছিলাম। পরকালতো নিশ্চয়ই মর্যাদায় শ্রেষ্ঠ ও শ্রেয়ত্বে শ্রেষ্ঠতর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2051,17,22,'আল্লাহর সাথে অপর কোন মা‘বূদ স্থির করনা; তাহলে নিন্দিত ও নিঃসহায় হয়ে পড়বে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2052,17,23,'তোমার রাব্ব নির্দেশ দিয়েছেন যে, তোমরা তিনি ছাড়া অন্য কারও ইবাদাত করবেনা এবং মাতা-পিতার প্রতি সদ্ব্যবহার করবে; তাদের একজন অথবা উভয়ে তোমার জীবদ্দশায় বার্ধক্যে উপনীত হলে তাদেরকে বিরক্তিসূচক কিছু বলনা এবং তাদেরকে ভৎর্সনা করনা; তাদের সাথে কথা বল সম্মানসূচক নম্রভাবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2053,17,24,'অনুকম্পায় তাদের প্রতি বিনয়াবনত থাক এবং বলঃ হে আমার রাব্ব! তাঁদের প্রতি দয়া করুন যেভাবে শৈশবে তাঁরা আমাকে লালন পালন করেছিলেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2054,17,25,'তোমাদের রাব্ব তোমাদের অন্তরে যা আছে তা ভাল জানেন; তোমরা যদি সৎ কর্মপরায়ণ হও তাহলে তিনি তাদের (আল্লাহ অভিমুখীদের) প্রতি ক্ষমাশীল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2055,17,26,'আত্মীয় স্বজনকে দিবে তার প্রাপ্য এবং অভাবগ্রস্ত ও পর্যটককেও (মুসাফিরকেও), এবং কিছুতেই অপব্যয় করনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2056,17,27,'নিশ্চয়ই যারা অপব্যয় করে তারা শাইতানের ভাই এবং শাইতান তার রবের প্রতি অতিশয় অকৃতজ্ঞ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2057,17,28,'আর তুমি যদি তাদের থেকে মুখ ফিরিয়ে নাও এবং তুমি তোমার রবের নিকট হতে অনুকম্পা লাভের প্রত্যাশায় ও সন্ধানে থাক তাহলে তাদের সাথে নম্রভাবে কথা বল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2058,17,29,'তুমি বদ্ধমুষ্টি হয়োনা এবং একেবারে মুক্ত হস্তও হয়োনা; তাহলে তুমি নিন্দিত ও নিঃস্ব হবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2059,17,30,'তোমার রাব্ব যার জন্য ইচ্ছা তার জীবনোপকরণ বর্ধিত করেন এবং যার জন্য ইচ্ছা তা হ্রাস করেন; তিনি তাঁর দাসদেরকে ভালভাবে জানেন ও দেখেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2060,17,31,'তোমাদের সন্তানদেরকে তোমরা দারিদ্রতার ভয়ে হত্যা করনা, তাদেরকে এবং তোমাদেরকে আমিই জীবনোপকরণ দিই; তাদেরকে হত্যা করা মহাপাপ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2061,17,32,'তোমরা অবৈধ যৌন সংযোগের নিকটবর্তী হয়োনা, ওটা অশ্লীল ও নিকৃষ্ট আচরণ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2062,17,33,'আল্লাহ যার হত্যা নিষেধ করেছেন যথার্থ কারণ ছাড়া তাকে হত্যা করনা; কেহ অন্যায়ভাবে নিহত হলে তার উত্তরাধিকারীকে আমি প্রতিশোধ গ্রহণের অধিকার দিয়েছি। কিন্তু হত্যার ব্যাপারে সে যেন বাড়াবাড়ি না করে; সেতো সাহায্য প্রাপ্ত হয়েছেই।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2063,17,34,'পিতৃহীন বয়ঃপ্রাপ্ত না হওয়া পর্যন্ত সদুদ্দেশ্য ছাড়া তার সম্পত্তির নিকটবর্তী হয়োনা এবং প্রতিশ্রুতি পালন কর; নিশ্চয়ই প্রতিশ্রুতি সম্পর্কে কৈফিয়ত তলব করা হবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2064,17,35,'মেপে দেয়ার সময় পূর্ণ মাপে দিবে এবং ওযন করবে সঠিক দাঁড়ি পাল্লায়, এটাই উত্তম ও পরিণামে উৎকৃষ্ট।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2065,17,36,'যে বিষয়ে তোমার কোন জ্ঞান নেই সেই বিষয়ে অনুমান দ্বারা পরিচালিত হয়োনা। কর্ণ, চক্ষু, হৃদয় - ওদের প্রত্যেকের নিকট কৈফিয়ত তলব করা হবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2066,17,37,'ভূপৃষ্ঠে দম্ভ ভরে বিচরণ করনা, তুমিতো কখনই পদভরে ভূ-পৃষ্ঠ বিদীর্ণ করতে পারবেনা এবং উচ্চতায় তুমি কখনই পর্বত সমান হতে পারবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2067,17,38,'এ সবের মধ্যে যেগুলি মন্দ সেগুলি তোমার রবের নিকট ঘৃণ্য।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2068,17,39,'তোমার রাব্ব অহীর দ্বারা তোমাকে যে হিকমাত দান করেছেন এগুলি উহার অন্তর্ভুক্ত; তুমি আল্লাহর সাথে কোন ইলাহ স্থির করনা, তাহলে তুমি নিন্দিত ও (আল্লাহর) অনুগ্রহ হতে দূরীকৃত অবস্থায় জাহান্নামে নিক্ষিপ্ত হবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2069,17,40,'তোমাদের রাব্ব কি তোমাদের জন্য পুত্র সন্তান নির্ধারণ করেছেন এবং তিনি নিজে (ফেরেশতাদের) কন্যা রূপে গ্রহণ করেছেন? তোমরাতো নিশ্চয়ই ভয়ানক কথা বলে থাক।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2070,17,41,'এই কুরআনে বহু নীতিবাক্য আমি বারবার বিবৃত করেছি যাতে তারা উপদেশ গ্রহণ করে; কিন্তু তাতে তাদের বিমুখতাই বৃদ্ধি পায়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2071,17,42,'বলঃ তাদের কথা মত যদি তাঁর সাথে আরও মা‘বূদ থাকত তাহলে তারা আরশ অধিপতির সাথে প্রতিদ্বন্দ্বিতা করার উপায় অন্বেষন করত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2072,17,43,'তিনি পবিত্র, মহিমান্বিত এবং তারা যা বলে তা হতে তিনি বহু উর্ধ্বে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2073,17,44,'সপ্ত আকাশ, পৃথিবী এবং ওদের অর্ন্তবর্তী সব কিছু তাঁরই পবিত্রতা ও মহিমা ঘোষণা করে এবং এমন কিছু নেই যা তাঁর সপ্রশংস পবিত্রতা ও মহিমা ঘোষণা করেনা। কিন্তু ওদের পবিত্রতা ও মহিমা ঘোষণা তোমরা অনুধাবন করতে পারনা; তিনি সহনশীল, ক্ষমাপরায়ণ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2074,17,45,'তুমি যখন কুরআন পাঠ কর তখন তোমার ও যারা পরলোকে বিশ্বাস করেনা তাদের মধ্যে এক প্রচ্ছন্ন পর্দা টেনে দিই।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2075,17,46,'আমি তাদের অন্তরের উপর আবরণ দিয়েছি যেন তারা উপলদ্ধি করতে না পারে এবং তাদেরকে বধির করেছি। তোমার রাব্ব এক, এটা যখন তুমি কুরআন হতে আবৃত্তি কর তখন তারা সরে পড়ে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2076,17,47,'যখন তারা কান পেতে তোমার কথা শুনে তখন তারা কেন তা শুনে আমি তা ভাল জানি, এবং এটাও জানি যে, গোপনে আলোচনা কালে সীমা লংঘনকারীরা বলেঃ তোমরাতো এক যাদুগ্রস্ত ব্যক্তির অনুসরণ করছ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2077,17,48,'দেখ, তারা তোমার কি উপমা দেয়! তারা পথভ্রষ্ট হয়েছে এবং তারা সৎ পথ খুঁজে পাবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2078,17,49,'তারা বলেঃ আমরা অস্থিতে পরিণত ও চূর্ণ-বিচূর্ণ হলেও কি নতুন সৃষ্টি রূপে পুনরুত্থিত হব?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2079,17,50,'বলঃ তোমরা হয়ে যাও পাথর অথবা লৌহ –');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2080,17,51,'অথবা এমন কিছু যা তোমাদের ধারণায় খুবই কঠিন। তারা বলবেঃ কে আমাদেরকে পুনরুত্থিত করবে? বলঃ তিনিই যিনি তোমাদেরকে প্রথম বার সৃষ্টি করেছেন; অতঃপর তারা তোমার সামনে মাথা নাড়াবে এবং বলবেঃ ওটা কবে হবে? বলঃ হবে সম্ভবতঃ শীঘ্রই –');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2081,17,52,'যেদিন তিনি তোমাদেরকে আহবান করবেন এবং তোমরা প্রশংসার সাথে তাঁর আহবানে সাড়া দিবে এবং তোমরা মনে করবে, তোমরা অল্পকালই অবস্থান করেছিলে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2082,17,53,'আমার বান্দাদেরকে যা উত্তম তা বলতে বল; শাইতান তাদের মধ্যে বিভেদ সৃষ্টির উস্কানি দেয়; শাইতান মানুষের প্রকাশ্য শত্রু।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2083,17,54,'তোমাদের রাব্ব তোমাদেরকে ভালভাবে জানেন; ইচ্ছা করলে তিনি তোমাদের প্রতি দয়া করেন এবং ইচ্ছা করলে তোমাদেরকে শাস্তি দেন; আমি তোমাকে তাদের অভিভাবক করে পাঠাইনি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2084,17,55,'যারা আকাশমন্ডলী ও পৃথিবীতে আছে তাদেরকে তোমার রাব্ব ভালভাবে জানেন; আমিতো নাবীদের কতককে কতকের উপর মর্যাদা দিয়েছি; দাউদকে আমি যাবুর দিয়েছি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2085,17,56,'বলঃ তোমরা আল্লাহ ছাড়া যাদেরকে মা‘বূদ মনে কর তাদেরকে আহবান কর; করলে দেখবে তোমাদের দুঃখ দৈন্য দূর করার অথবা পরিবর্তন করার শক্তি তাদের নেই।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2086,17,57,'তারা যাদেরকে আহবান করে তাদের মধ্যে যারা নিকটতর তারাইতো তাদের রবের নৈকট্য লাভের উপায় সন্ধান করে যে, কে কত নিকটতর হতে পারে, তাঁর দয়া প্রত্যাশা করে এবং তাঁর শাস্তিকে ভয় করে। নিশ্চয়ই তোমার রবের শাস্তি ভয়াবহ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2087,17,58,'এমন কোন জনপদ নেই যা আমি কিয়ামাত দিনের পূর্বে ধ্বংস করবনা অথবা কঠোর শাস্তি দিবনা; এটাতো কিতাবে লিপিবদ্ধ আছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2088,17,59,'পূর্ববর্তীগণ কর্তৃক নিদর্শন অস্বীকার করার কারণেই আমাকে নিদর্শন প্রেরণ করা হতে বিরত রাখে; আমি স্পষ্ট নিদর্শন স্বরূপ সামূদের নিকট উষ্ট্রী পাঠিয়েছিলাম, অতঃপর তারা ওর প্রতি যুলম করেছিল; আমি ভয় প্রদর্শনের জন্যই নিদর্শন প্রেরণ করি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2089,17,60,'স্মরণ কর, আমি তোমাকে বলেছিলাম, তোমার রাব্ব মানুষকে পরিবেষ্টন করে আছেন; আমি যে দৃশ্য তোমাকে দেখিয়েছি কিংবা কুরআনে উল্লিখিত অভিশপ্ত বৃক্ষ শুধু মানুষের পরীক্ষার জন্য আমি তাদেরকে ভীতি প্রদর্শন করি। কিন্তু এটা তাদের তীব্র অবাধ্যতাই বৃদ্ধি করে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2090,17,61,'স্মরণ কর, যখন আমি মালাইকা/ফেরেশতাদেরকে বললামঃ আদমের প্রতি সাজদাহবনত হও; তখন ইবলীস ছাড়া সবাই সাজদাহবনত হল; সে বললঃ আমি কি তাকে সাজদাহ করব যাকে আপনি মাটি হতে সৃষ্টি করেছেন?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2091,17,62,'সে আরও বললঃ লক্ষ্য করুন, তাকে যে আপনি আমার উপর মর্যাদা দান করলেন, কিয়ামাতের দিন পর্যন্ত যদি আমাকে অবকাশ দেন তাহলে আমি অল্প কয়েকজন ছাড়া তার বংশধরদেরকে সমূলে বিনষ্ট করব।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2092,17,63,'(আল্লাহ) বললেনঃ যা, জাহান্নামই তোর এবং তাদের সম্যক শাস্তি যারা তোর অনুসরণ করবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2093,17,64,'তোর আহবানে তাদের মধ্য থেকে যাকে পারিস সত্যচূত কর, তোর অশ্বারোহী ও পদাতিক বাহিনী দ্বারা তাদেরকে আক্রমণ কর এবং তাদের ধন-সম্পদে ও সন্তান-সন্ততিতে শরীক হয়ে যা, এবং তাদেরকে প্রতিশ্রুতি দে। শাইতান তাদেরকে যে প্রতিশ্রুতি দেয় তা ছলনা মাত্র।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2094,17,65,'নিশ্চয়ই আমার দাসদের উপর তোর কোন ক্ষমতা নেই; কর্ম বিধায়ক হিসাবে তোর রাব্বই যথেষ্ট।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2095,17,66,'তোমাদের রাব্ব তিনিই যিনি তোমাদের জন্য সমুদ্রে নৌযান পরিচালিত করেন যাতে তোমরা তাঁর অনুগ্রহ সন্ধান করতে পার; তিনি তোমাদের প্রতি পরম দয়ালু।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2096,17,67,'সমুদ্রে যখন তোমাদেরকে বিপদ স্পর্শ করে তখন শুধু তিনি ছাড়া অপর যাদেরকে তোমরা আহবান কর তারা তোমাদের মন হতে উধাও হয়ে যায়। অতঃপর তিনি যখন স্থলে ভিড়িয়ে তোমাদেরকে উদ্ধার করেন তখন তোমরা মুখ ফিরিয়ে নাও; বস্তুতঃ মানুষ বড়ই অকৃতজ্ঞ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2097,17,68,'তোমরা কি নিশ্চিত আছ যে, তিনি তোমাদেরকে স্থলে কোথাও ভূ-গর্ভস্থ করবেননা অথবা তোমাদের উপর কংকর বর্ষণ করবেননা? তখন তোমরা তোমাদের কোন কর্ম বিধায়ক পাবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2098,17,69,'অথবা তোমরা কি নিশ্চিন্ত আছ যে, তোমাদেরকে আর একবার সমুদ্রে নিয়ে যাবেননা এবং তোমাদের বিরুদ্ধে প্রচন্ড ঝটিকা পাঠাবেননা এবং তোমাদের সত্য প্রত্যাখ্যান করার জন্য তোমাদেরকে নিমজ্জিত করবেননা? তখন তোমরা এ বিষয়ে আমার বিরুদ্ধে কোন সাহায্যকারী পাবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2099,17,70,'আমিতো আদম-সন্তানকে মর্যাদা দান করেছি, স্থলে ও সমুদ্রে তাদের চলাচলের বাহন দিয়েছি; আর তাদেরকে উত্তম জীবনোপকরণ দান করেছি এবং আমি যাদেরকে সৃষ্টি করেছি তাদের অনেকের উপর তাদেরকে শ্রেষ্ঠত্ব দিয়েছি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2100,17,71,'স্মরণ কর সেই দিনকে যখন আমি প্রত্যেক সম্প্রদায়কে তাদের নেতাসহ আহবান করব; যাদেরকে ডান হাতে ‘আমলনামা দেয়া হবে তারা তাদের ‘আমলনামা পাঠ করবে (আনন্দের সাথে) এবং তাদের উপর সামান্য পরিমাণও যুলম করা হবে না।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2101,17,72,'যে ইহলোকে অন্ধ পরলোকেও সে অন্ধ এবং অধিকতর পথভ্রষ্ট।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2102,17,73,'আমি তোমার প্রতি যা প্রত্যাদেশ করেছি তা হতে তোমার পদস্খলন ঘটানোর জন্য তারা চূড়ান্ত চেষ্টা করেছে যাতে তুমি আমার সম্বন্ধে কিছু মিথ্যা উদ্ভাবন কর। সফলকাম হলে তারা অবশ্যই তোমাকে বন্ধু রূপে গ্রহণ করত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2103,17,74,'আমি তোমাকে অবিচলিত না রাখলে তুমি তাদের দিকে প্রায় কিছুটা ঝুঁকেই পড়তে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2104,17,75,'তুমি ঝুঁকে পড়লে অবশ্যই তোমাকে ইহজীবনে ও পরজীবনে দ্বিগুণ শাস্তি আস্বাদন করাতাম; তখন আমার বিরুদ্ধে তোমার জন্য কোন সাহায্যকারী পেতেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2105,17,76,'তারা তোমাকে দেশ হতে উৎখাত করার চূড়ান্ত চেষ্টা করেছিল তোমাকে সেখান হতে বহিস্কার করার জন্য। তাহলে তোমার পর তারাও সেখানে অল্পকালই টিকে থাকত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2106,17,77,'আমার রাসূলদের মধ্যে তোমার পূর্বে যাদেরকে আমি পাঠিয়েছিলাম তাদের ক্ষেত্রেও ছিল এরূপ নিয়ম এবং তুমি আমার নিয়মের কোন পরিবর্তন দেখতে পাবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2107,17,78,'সূর্য হেলে পড়ার পর হতে রাতের ঘন অন্ধকার পর্যন্ত সালাত কায়েম করবে এবং কায়েম করবে ফাজরের কুরআন পাঠও। কারণ ভোরের কুরআন পাঠ সাক্ষী স্বরূপ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2108,17,79,'আর রাতের কিছু অংশে তাহাজ্জুদ কায়েম করবে; এটা তোমার এক অতিরিক্ত কর্তব্য; আশা করা যায় তোমার রাব্ব তোমাকে প্রতিষ্ঠিত করবেন প্রশংসিত স্থানে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2109,17,80,'বলঃ হে আমার রাব্ব! যেখানে গমন শুভ ও সন্তোষজনক আপনি আমাকে সেখানে নিয়ে যান এবং যেখান হতে নির্গমন শুভ ও সন্তোষজনক সেখান হতে আমাকে বের করে নিন এবং আপনার নিকট হতে আমাকে দান করুন সাহায্যকারী শক্তি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2110,17,81,'আর বলঃ সত্য এসেছে এবং মিথ্যা বিলুপ্ত হয়েছে; মিথ্যাতো বিলুপ্ত হয়েই থাকে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2111,17,82,'আমি অবতীর্ণ করেছি কুরআন, যা বিশ্বাসীদের জন্য সুচিকিৎসা ও দয়া, কিন্তু তা সীমা লংঘনকারীদের ক্ষতিই বৃদ্ধি করে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2112,17,83,'যখন আমি মানুষের উপর অনুগ্রহ করি তখন সে মুখ ফিরিয়ে নেয় ও অহংকারে দূরে সরে যায় এবং তাকে অনিষ্ট স্পর্শ করলে সে একেবারে হতাশ হয়ে পড়ে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2113,17,84,'বলঃ প্রত্যেকে তার নিজ নিজ রীতি অনুসারে কাজ করে। কিন্তু তোমার রাব্ব ভাল করে জানেন, কে সর্বাপেক্ষা নির্ভুল পথে আছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2114,17,85,'তোমাকে তারা রূহ্ সম্পর্কে প্রশ্ন করে, তুমি বলঃ রূহ্ আমার রবের আদেশ ঘটিত; এ বিষয়ে তোমাদেরকে সামান্য জ্ঞানই দেয়া হয়েছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2115,17,86,'ইচ্ছা করলে আমি তোমার প্রতি যা প্রত্যাদেশ করেছি তা অবশ্যই প্রত্যাহার করতে পারতাম; তাহলে তুমি এ বিষয়ে আমার বিরুদ্ধে কোন কর্মবিধায়ক পেতেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2116,17,87,'এটা প্রত্যাহার না করা তোমার রবের দয়া; তোমার প্রতি আছে তাঁর মহা অনুগ্রহ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2117,17,88,'বলঃ যদি এই কুরআনের অনুরূপ কুরআন রচনা করার জন্য মানুষ ও জিন সমবেত হয় এবং তারা পরস্পরকে সাহায্য করে তবুও তারা এর অনুরূপ কুরআন রচনা করতে পারবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2118,17,89,'আমি এই কুরআনে মানুষের জন্য বিভিন্ন উপমা দ্বারা আমার বাণী বিশদভাবে বর্ণনা করেছি। কিন্তু অধিকাংশ মানুষ কুফরী করা ছাড়া ক্ষান্ত হয়না।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2119,17,90,'আর তারা বলেঃ কখনই আমরা তোমাতে বিশ্বাস স্থাপন করবনা, যতক্ষণ না তুমি আমাদের জন্য ভূমি হতে এক প্রস্রবণ উৎসারিত করবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2120,17,91,'অথবা তোমার খর্জুরের অথবা আঙ্গুরের এক বাগান হবে যার ফাঁকে ফাঁকে তুমি অজস্র ধারায় প্রবাহিত করে দিবে নদীনালা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2121,17,92,'অথবা তুমি যেমন বলে থাক, তদনুযায়ী আকাশকে খন্ড বিখন্ড করে আমাদের উপর ফেলবে, অথবা আল্লাহ ও মালাইকাদেরকে আমাদের সামনে উপস্থিত করবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2122,17,93,'অথবা তোমার একটি স্বর্ণ নির্মিত গৃহ হবে, অথবা তুমি আকাশে আরোহণ করবে, কিন্তু তোমার আকাশে আরোহণ আমরা তখনও বিশ্বাস করবনা যতক্ষণ না তুমি আমাদের প্রতি এক কিতাব অবতীর্ণ করবে যা আমরা পাঠ করব। বলঃ পবিত্র আমার মহান রাব্ব! আমিতো শুধু একজন মানুষ, একজন রাসূল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2123,17,94,'‘আল্লাহ কি মানুষকে রাসূল করে পাঠিয়েছেন?’ - তাদের এই উক্তিই বিশ্বাস স্থাপন হতে লোকদেরকে বিরত রাখে, যখন তাদের নিকট আসে পথ নির্দেশ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2124,17,95,'বলঃ মালাইকা/ফেরেশতারা যদি নিশ্চিত হয়ে পৃথিবীতে বিচরণ করত তাহলে আমি আকাশ হতে মালাক/ফেরেশতাকেই তাদের নিকট রাসূল করে পাঠাতাম।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2125,17,96,'বলঃ আমার ও তোমাদের মধ্যে স্বাক্ষী হিসাবে আল্লাহই যথেষ্ট; তিনি তাঁর দাসদেরকে সবিশেষ জানেন ও দেখেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2126,17,97,'আল্লাহ যাদের পথ প্রদর্শন করেন তারাইতো সঠিক পথপ্রাপ্ত এবং যাদেরকে পথভ্রষ্ট করেন তাদের জন্য তুমি আল্লাহ ছাড়া কোন সাহায্যকারী পাবেনা। কিয়ামাতের দিন আমি তাদেরকে সমবেত করব তাদের মুখে ভর দিয়ে চলা অবস্থায়, অন্ধ অবস্থায়, বোবা অবস্থায় এবং বধির অবস্থায়। তাদের আবাসস্থল জাহান্নাম! যখনই তা স্তিমিত হবে আমি তখন তাদের জন্য আগুন বৃদ্ধি করে দিব।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2127,17,98,'এটাই তাদের প্রতিফল, কারণ তারা আমার নিদর্শন অস্বীকার করেছিল ও বলেছিলঃ আমরা অস্থিতে পরিণত ও চূর্ণ বিচূর্ণ হলেও কি নতুন সৃষ্টি রূপে পুনরুত্থিত হব?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2128,17,99,'তারা কি লক্ষ্য করেনা যে, আল্লাহ! যিনি আকাশমন্ডলী ও পৃথিবী সৃষ্টি করেছেন তিনি ওগুলির অনুরূপ সৃষ্টি করতে ক্ষমতাবান? তিনি তাদের জন্য স্থির করেছেন এক নির্দিষ্ট কাল, যাতে কোন সন্দেহ নেই; তথাপি সীমা লংঘনকারীরা প্রত্যাখ্যান করা ছাড়া আর সবই অস্বীকার করে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2129,17,100,'বলঃ যদি তোমরা আমার রবের দয়ার ভান্ডারের অধিকারী হতে তবুও ‘ব্যয় হয়ে যাবে’ এই আশংকায় তোমরা ওটা ধরে রাখতে, মানুষতো অতিশয় কৃপণ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2130,17,101,'তুমি বানী ইসরাঈলকে জিজ্ঞেস করে দেখ, আমি মূসাকে নয়টি সুস্পষ্ট নিদর্শন দিয়েছিলাম; যখন সে তাদের নিকট এসেছিল তখন ফির‘আউন তাকে বলেছিলঃ হে মূসা! আমিতো মনে করি তুমি যাদুগ্রস্ত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2131,17,102,'মূসা বলেছিলঃ তুমি অবশ্যই অবগত আছ যে, এই সমস্ত স্পষ্ট নিদর্শন আকাশমন্ডলী ও পৃথিবীর রাব্বই অবতীর্ণ করেছেন প্রত্যক্ষ প্রমাণ স্বরূপ। হে ফির‘আউন! আমিতো দেখছি, তুমি ধ্বংস হয়ে গেছ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2132,17,103,'অতঃপর ফির‘আউন তাদেরকে দেশ হতে উচ্ছেদ করার সংকল্প করল; তখন ফির‘আউন ও তার সঙ্গীদের সকলকে আমি নিমজ্জিত করলাম।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2133,17,104,'এরপর আমি বানী ইসরাঈলকে বললামঃ তোমরা এই দেশে বসবাস কর এবং যখন কিয়ামাতের প্রতিশ্রুতি বাস্তবায়িত হবে তখন তোমাদের সকলকে আমি একত্রিত করে উপস্থিত করব।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2134,17,105,'আমি সত্য সত্যই কুরআন অবতীর্ণ করেছি এবং তা সত্যসহই অবতীর্ণ হয়েছে; আমিতো তোমাকে শুধু সুসংবাদদাতা ও সতর্ককারী রূপে প্রেরণ করেছি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2135,17,106,'আমি কুরআন অবতীর্ণ করেছি খন্ড খন্ডভাবে যাতে তুমি তা মানুষের কাছে পাঠ করতে পার ক্রমে ক্রমে; এবং আমি তা যথাযথভাবে অবতীর্ণ করেছি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2136,17,107,'বলঃ তোমরা বিশ্বাস কর অথবা না কর, যাদেরকে এর পূর্বে জ্ঞান দান করা হয়েছে তাদের সামনে যখন আবৃত্তি করা হয় তখন তারা বিনয়ের সাথে কাঁদতে কাঁদতে ভূমিতে লুটিয়ে পড়ে ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2137,17,108,'এবং বলেঃ আমাদের রাব্ব পবিত্র, মহান! আমাদের রবের প্রতিশ্রুতি কার্যকর হয়েই থাকে। [সাজদাহ]');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2138,17,109,'এবং কাঁদতে কাঁদতে তাদের মুখমন্ডল মাটিতে লুটিয়ে পড়ে এবং এতে তাদের বিনয়ই বৃদ্ধি পায়।[সাজদাহ]');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2139,17,110,'বলঃ তোমরা ‘আল্লাহ’ নামে আহবান কর অথবা ‘রাহমান’ নামে আহবান কর, তোমরা যে নামেই আহবান করনা কেন, সব সুন্দর নামইতো তাঁর! তোমরা সালাতে তোমাদের স্বর উচু করনা এবং অতিশয় ক্ষীণও করনা; এই দুই এর মধ্য পন্থা অবলম্বন কর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2140,17,111,'বলঃ প্রশংসা আল্লাহরই যিনি সন্তান গ্রহণ করেননি, তাঁর সার্বভৌমত্বে কোন অংশী নেই এবং তিনি দুর্দশাগ্রস্ত হননা যে কারণে তাঁর অভিভাবকের প্রয়োজন হতে পারে; সুতরাং স্বসম্ভ্রমে তাঁর মাহাত্ম্য ঘোষণা কর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2141,18,1,'সমস্ত প্রশংসা আল্লাহরই যিনি তাঁর দাসের প্রতি এই কিতাব অবতীর্ণ করেছেন এবং এতে তিনি অসঙ্গতি রাখেননি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2142,18,2,'একে করেছেন সুপ্রতিষ্ঠিত তাঁর কঠিন শাস্তি সম্পর্কে সতর্ক করার জন্য এবং বিশ্বাসীগণ, যারা সৎকাজ করে তাদেরকে এই সুসংবাদ দেয়ার জন্য যে, তাদের জন্য রয়েছে উত্তম পুরস্কার – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2143,18,3,'যেখানে তারা হবে চিরস্থায়ী – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2144,18,4,'এবং সতর্ক করার জন্য তাদেরকে যারা বলে যে, আল্লাহ সন্তান গ্রহণ করেছেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2145,18,5,'এ বিষয়ে তাদের কোনই জ্ঞান নেই এবং তাদের পিতৃপুরুষদেরও ছিলনা; তাদের মুখ-নিঃসৃত বাক্য কি উদ্ভট! তারাতো শুধু মিথ্যাই বলে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2146,18,6,'তারা এই বাণী বিশ্বাস না করলে তাদের পিছনে পিছনে ঘুরে সম্ভবতঃ তুমি দুঃখে আত্মবিনাশী হয়ে পড়বে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2147,18,7,'পৃথিবীর উপর যা কিছু আছে আমি সেগুলিকে ওর শোভনীয় করেছি মানুষকে এই পরীক্ষা করার জন্য যে, তাদের মধ্যে কর্মে কে শ্রেষ্ঠ?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2148,18,8,'ওর উপর যা কিছু আছে তা অবশ্যই আমি উদ্ভিদশূন্য মৃত্তিকায় পরিণত করব।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2149,18,9,'তুমি কি মনে কর যে, গুহা ও রাকীমের অধিবাসীরা আমার নিদর্শনাবলীর মধ্যে বিস্ময়কর?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2150,18,10,'যুবকরা যখন গুহায় আশ্রয় নিল তখন তারা বলেছিলঃ হে আমাদের রাব্ব! আপনি নিজ হতে আমাদেরকে অনুগ্রহ দান করুন এবং আমাদের জন্য আমাদের কাজকর্ম সঠিকভাবে পরিচালনার ব্যবস্থা করুন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2151,18,11,'অতঃপর আমি তাদেরকে গুহায় কয়েক বছর ঘুমন্ত অবস্থায় রেখে দিলাম।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2152,18,12,'পরে আমি তাদেরকে জাগরিত করলাম এটা জানার জন্য যে, দুই দলের মধ্যে কোন দল তাদের অবস্থিতি কাল সঠিকভাবে নির্ণয় করতে পারে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2153,18,13,'আমি তোমার কাছে তাদের সঠিক বৃত্তান্ত বর্ণনা করছিঃ তারা ছিল কয়েকজন যুবক, তারা তাদের রবের প্রতি বিশ্বাস স্থাপন করেছিল এবং আমি তাদের সৎ পথে চলার শক্তি বৃদ্ধি করেছিলাম।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2154,18,14,'আর আমি তাদের চিত্ত দৃঢ় করেছিলাম; তারা যখন উঠে দাঁড়াল তখন বললঃ আমাদের রাব্ব আকাশমন্ডলী ও পৃথিবীর রাব্ব; আমরা কখনই তাঁর পরিবর্তে অন্য কোন মা‘বূদকে আহবান করবনা; যদি করি তাহলে তা অতিশয় গর্হিত হবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2155,18,15,'আমাদেরই এই স্বজাতিরা তাঁর পরিবর্তে অনেক মা‘বূদ গ্রহণ করেছে, তারা এই সব মা‘বূদ সম্বন্ধে স্পষ্ট প্রমাণ উপস্থিত করে না কেন? যে আল্লাহ সম্বন্ধে মিথ্যা উদ্ভাবন করে তার চেয়ে অধিক সীমালংঘনকারী আর কে?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2156,18,16,'তোমরা যখন তাদের হতে বিচ্ছিন্ন হলে এবং তারা আল্লাহর পরিবর্তে যাদের ইবাদাত করে তাদের হতে, তখন তোমরা গুহায় আশ্রয় গ্রহণ কর; তোমাদের রাব্ব তোমাদের জন্য তাঁর দয়া বিস্তার করবেন এবং তিনি তোমাদের কর্মসমূহকে ফলপ্রসূ করার ব্যবস্থা করবেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2157,18,17,'তুমি সূর্যকে দেখবে যখন উদিত হয়, তাদের গুহা থেকে পাশ কেটে ডান দিকে চলে যায় এবং যখন অস্ত যায়, তাদের থেকে পাশ কেটে বাম দিকে চলে যায়, অথচ তারা গুহার প্রশস্ত চত্বরে শায়িত। এসবই আল্লাহর নিদর্শন। আল্লাহ যাকে সৎ পথে পরিচালিত করেন সে সৎ পথ প্রাপ্ত এবং তিনি যাকে পথভ্রষ্ট করেন, তুমি কখনও তার কোন পথ প্রদর্শনকারী অভিভাবক পাবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2158,18,18,'তুমি মনে করতে তারা জাগ্রত, কিন্তু তারা ছিল নিদ্রিত; আমি তাদেরকে পার্শ্ব পরিবর্তন করাতাম ডানে ও বামে এবং তাদের কুকুর ছিল সম্মুখের পা দু’টি গুহাদ্বারে প্রসারিত করে; তাকিয়ে তাদেরকে দেখলে তুমি পিছন ফিরে পালিয়ে যেতে এবং তাদের ভয়ে আতংকগ্রস্ত হয়ে পড়তে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2159,18,19,'এবং এভাবেই আমি তাদের জাগ্রত করলাম যাতে তারা পরস্পরের মধ্যে জিজ্ঞাসাবাদ করে; তাদের একজন বললঃ তোমরা কতকাল অবস্থান করেছ? কেহ কেহ বললঃ এক দিন অথবা এক দিনের কিছু অংশ; কেহ কেহ বললঃ তোমরা কতকাল অবস্থান করেছ তা তোমাদের রাব্বই ভাল জানেন; এখন তোমাদের একজনকে তোমাদের এই মুদ্রাসহ নগরে প্রেরণ কর; সে যেন দেখে কোন্ খাদ্য উত্তম এবং তা হতে যেন কিছু খাদ্য নিয়ে আসে তোমাদের জন্য; সে যেন বিচক্ষণতার সাথে কাজ করে এবং কিছুতেই যেন তোমাদের সম্বন্ধে কেহকেও কিছু জানতে না দেয়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2160,18,20,'তারা যদি তোমাদের বিষয় জানতে পারে তাহলে তোমাদেরকে প্রস্তরাঘাতে হত্যা করবে অথবা তোমাদেরকে তাদের ধর্মে ফিরিয়ে নিবে এবং সেক্ষেত্রে তোমরা কখনই সাফল্য লাভ করবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2161,18,21,'এবং এভাবে আমি মানুষকে তাদের বিষয় জানিয়ে দিলাম যাতে তারা জ্ঞাত হয় যে, আল্লাহর প্রতিশ্রুতি সত্য এবং কিয়ামাতে কোন সন্দেহ নেই; যখন তারা তাদের কর্তব্য বিষয়ে নিজেদের মধ্যে বিতর্ক করছিল তখন অনেকে বললঃ তাদের উপর সৌধ নির্মাণ কর; তাদের রাব্ব তাদের বিষয় ভাল জানেন; তাদের কর্তব্য বিষয়ে যাদের মত প্রবল হল তারা বললঃ আমরাতো নিশ্চয়ই তাদের উপর মাসজিদ নির্মাণ করব।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2162,18,22,'অজানা বিষয়ে অনুমানের উপর নির্ভর করে কেহ কেহ বলেঃ তারা ছিল তিন জন, তাদের চতুর্থটি ছিল তাদের কুকুর; এবং কেহ কেহ বলেঃ তারা ছিল পাঁচ জন, ষষ্ঠটি ছিল তাদের কুকুর; আবার কেহ কেহ বলেঃ তারা ছিল সাত জন, অষ্টমটি ছিল তাদের কুকুর; বলঃ আমার রাব্বই তাদের সংখ্যা ভাল জানেন; তাদের সংখ্যা অল্প কয়েকজনই জানে; সাধারণ আলোচনা ব্যতীত তুমি তাদের বিষয়ে বিতর্ক করনা এবং তাদের কেহকেও তাদের বিষয়ে জিজ্ঞাসাবাদ করনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2163,18,23,'কখনই তুমি কোন বিষয়ে বলনা - আমি ওটা আগামীকাল করব –');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2164,18,24,'‘আল্লাহ ইচ্ছা করলে’ - এই কথা না বলে; যদি ভুলে যাও তাহলে তোমার রাব্বকে স্মরণ কর ও বলঃ সম্ভবতঃ আমার রাব্ব আমাকে গুহাবাসীর বিবরণ অপেক্ষা সত্যের নিকটতর পথ নির্দেশ করবেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2165,18,25,'তারা তাদের গুহায় ছিল তিন শ’ বছর, আরও নয় বছর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2166,18,26,'তুমি বলঃ তারা কত কাল ছিল তা আল্লাহই ভাল জানেন, আকাশমন্ডলী ও পৃথিবীর অজ্ঞাত বিষয়ের জ্ঞান তাঁরই; তিনি কত সুন্দর দ্রষ্টা ও শ্রোতা! তিনি ছাড়া তাদের অন্য কোন অভিভাবক নেই; তিনি কেহকেও নিজ কর্তৃত্বে শরীক করেননা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2167,18,27,'তুমি তোমার প্রতি প্রত্যাদিষ্ট তোমার রবের কিতাব আবৃত্তি কর; তাঁর বাক্য পরিবর্তন করার কেহ নেই; তুমি কখনই তাঁকে ব্যতীত অন্য কোন আশ্রয় পাবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2168,18,28,'নিজকে তুমি রাখবে তাদেরই সংসর্গে যারা সকাল-সন্ধ্যায় আহবান করে তাদের রাব্বকে তাঁর সন্তুষ্টি লাভের উদ্দেশে এবং তুমি পার্থিব জীবনের শোভা কামনা করে তাদের দিক হতে তোমার দৃষ্টি ফিরিয়ে নিওনা; যার চিত্তকে আমি আমার স্মরণে অমনোযোগী করে দিয়েছি সে তার খেয়াল খুশীর অনুসরণ করে এবং যার কার্যকলাপ সীমা অতিক্রম করে তুমি তার আনুগত্য করনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2169,18,29,'বলঃ সত্য তোমাদের রবের নিকট হতে প্রেরিত; সুতরাং যার ইচ্ছা বিশ্বাস করুক এবং যার ইচ্ছা প্রত্যাখ্যান করুক; আমি সীমা লংঘনকারীদের জন্য প্রস্তুত রেখেছি অগ্নি, যার বেষ্টনী তাদেরকে পরিবেষ্টন করে থাকবে; তারা পানীয় চাইলে তাদেরকে দেয়া হবে গলিত ধাতুর ন্যায় পানীয়, যা তাদের মুখমন্ডল বিদগ্ধ করবে, কত নিকৃষ্ট পানীয় এটা এবং অগ্নি কত নিকৃষ্ট আশ্রয়!');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2170,18,30,'যারা বিশ্বাস করে এবং সৎ কাজ করে আমি তাদেরকে পুরস্কৃত করি, যে সৎ কাজ করে আমি তার কর্মফল নষ্ট করিনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2171,18,31,'তাদেরই জন্য আছে স্থায়ী জান্নাত যার পাদদেশে নদী প্রবাহিত, সেখানে তাদেরকে স্বর্ণ-কংকনে অলংকৃত করা হবে, তারা পরিধান করবে সূক্ষ্ম ও স্থূল রেশমের সবুজ বস্ত্র ও সমাসীন হবে সুসজ্জিত আসনে; কত সুন্দর পুরস্কার ও উত্তম আশ্রয়স্থল!');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2172,18,32,'তুমি তাদের কাছে পেশ কর দুই ব্যক্তির একটি উপমা; তাদের এক জনকে আমি দিয়েছিলাম দু’টি দ্রাক্ষা উদ্যান এবং এই দু’টিকে আমি খেজুর বৃক্ষ দ্বারা পরিবেষ্টিত করেছিলাম এবং এই দু’য়ের মধ্যবর্তী স্থানকে করেছিলাম শস্যক্ষেত্র।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2173,18,33,'উভয় উদ্যানই ফল দান করত এবং এতে কোন ক্রটি করতনা এবং উভয়ের ফাঁকে ফাঁকে প্রবাহিত করেছিলাম নহর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2174,18,34,'এবং তার প্রচুর ধন-সম্পদ ছিল; অতঃপর কথা প্রসঙ্গে সে তার বন্ধুকে বললঃ ধন-সম্পদে আমি তোমা অপেক্ষা শ্রেষ্ঠ এবং জনবলে তোমা অপেক্ষা শক্তিশালী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2175,18,35,'এভাবে নিজের প্রতি যুলম করে সে তার উদ্যানে প্রবেশ করল। সে বললঃ আমি মনে করিনা যে, এটা কখনও ধ্বংস হবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2176,18,36,'আমি মনে করিনা যে, কিয়ামাত হবে, আর আমি যদি আমার রবের নিকট প্রত্যাবৃত্ত হই-ই তাহলে আমিতো নিশ্চয়ই এটা অপেক্ষা উৎকৃষ্ট স্থান পাব।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2177,18,37,'তদুত্তরে তাকে তার বন্ধু বললঃ তুমি কি তাঁকে অস্বীকার করছ যিনি তোমাকে সৃষ্টি করেছেন মৃত্তিকা ও পরে শুক্র হতে এবং তারপর পূর্ণাঙ্গ করেছেন মানব আকৃতির?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2178,18,38,'কিন্তু আমি বলিঃ আল্লাহই আমার রাব্ব এবং আমি কেহকেও আমার রবের সাথে শরীক করিনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2179,18,39,'তুমি যখন ধনে ও সন্তানে আমাকে তোমা অপেক্ষা কম দেখলে তখন তোমার উদ্যানে প্রবেশ করে তুমি কেন বললেনাঃ আল্লাহ যা চেয়েছেন তা’ই হয়েছে, আল্লাহর সাহায্য ব্যতীত কোন শক্তি নেই।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2180,18,40,'সম্ভবতঃ আমার রাব্ব আমাকে তোমার উদ্যান অপেক্ষা উৎকৃষ্টতর কিছু দিবেন এবং তোমার উদ্যানে আকাশ হতে অগ্নি বর্ষণ করবেন যার ফলে তা উদ্ভিদশূণ্য মাইদানে পরিণত হবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2181,18,41,'অথবা ওর পানি ভূ-গর্ভে অন্তর্হিত হবে এবং তুমি কখনও ওকে ফিরিয়ে আনতে পারবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2182,18,42,'তার ধন-সম্পদ ধ্বংস হয়ে গেল এবং সে তাতে যা ব্যয় করেছিল তার জন্য হাতে হাত রেখে আক্ষেপ করতে লাগল, যখন তা ধ্বংস হয়ে গেল। সে বলতে লাগলঃ হায়! আমি যদি কেহকেও আমার রবের সাথে শরীক না করতাম!');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2183,18,43,'এবং আল্লাহ ব্যতীত তাকে সাহায্য করার কোন লোকজন ছিলনা এবং সে নিজেও প্রতিকারে সামর্থ্য হলনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2184,18,44,'এ ক্ষেত্রে সাহায্য করার অধিকার আল্লাহরই যিনি সত্য; পুরস্কার দানে ও পরিণাম নির্ধারণে তিনিই শ্রেষ্ঠ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2185,18,45,'তাদের কাছে পেশ কর পার্থিব জীবনের উপমা - এটা পানির ন্যায় যা আমি বর্ষণ করি আকাশ হতে, যদ্বারা ভূমির উদ্ভিদ ঘন সন্নিবিষ্ট হয়ে উদ্গত হয়, অতঃপর তা বিশুস্ক হয়ে এমন চূর্ণ বিচূর্ণ হয় যে, বাতাস ওকে উড়িয়ে নিয়ে যায়; আল্লাহ সর্ব বিষয়ে শক্তিমান।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2186,18,46,'ধনৈশ্বর্য ও সন্তান-সন্ততি পার্থিব জীবনের শোভা এবং সৎ কার্য - যার ফল স্থায়ী, ওটা তোমার রবের নিকট পুরস্কার প্রাপ্তির জন্য শ্রেষ্ঠ এবং বাঞ্ছা লাভের ব্যাপারেও উৎকৃষ্ট। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2187,18,47,'আর যেদিন আমি পাহাড়কে চলমান করব এবং তুমি যমীনকে দেখতে পাবে দৃশ্যমান, আর আমি তাদেরকে একত্র করব। অতঃপর তাদের কাউকেই ছাড়ব না।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2188,18,48,'আর তাদেরকে তোমার রবের নিকট উপস্থিত করা হবে সারিবদ্ধভাবে এবং বলা হবেঃ তোমাদেরকে প্রথমবার যেভাবে সৃষ্টি করেছিলাম সেভাবেই তোমরা আমার নিকট উপস্থিত হয়েছ; অথচ তোমরা মনে করতে যে, তোমাদের জন্য প্রতিশ্রুত ক্ষণ আমি উপস্থিত করবনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2189,18,49,'এবং সেদিন উপস্থিত করা হবে ‘আমলনামা এবং তাতে যা লিপিবদ্ধ আছে তার কারণে তুমি অপরাধীদেরকে দেখবে আতংকগ্রস্ত এবং তারা বলবেঃ হায়! দুর্ভোগ আমাদের! এটা কেমন গ্রন্থ! ওটাতো ছোট বড় কিছুই বাদ দেয়নি, বরং ওটা সমস্ত হিসাব রেখেছে। তারা তাদের কৃতকর্ম সম্মুখে উপস্থিত পাবে; তোমার রাব্ব কারও প্রতি যুলম করেন না।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2190,18,50,'এবং স্মরণ কর, আমি যখন মালাইকা/ফেরেশতাদেরকে বলেছিলামঃ তোমরা আদমের প্রতি নত হও। তখন সবাই নত হল ইবলীস ছাড়া; সে জিনদের একজন, সে তার রবের আদেশ অমান্য করল; তাহলে কি তোমরা আমার পরিবর্তে তাকে ও তার বংশধরকে অভিভাবকরূপে গ্রহণ করছ? তারা তো তোমাদের শত্রু; সীমালংঘনকারীদের জন্য রয়েছে কত নিকৃষ্ট বদলা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2191,18,51,'আকাশমন্ডলী ও পৃথিবীর সৃষ্টিকালে আমি তাদেরকে ডাকিনি এবং তাদের সৃজনকালেও নয়, এবং আমি বিভ্রান্তকারীদের সাহায্য গ্রহণ করার নই।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2192,18,52,'এবং সেদিনের কথা স্মরণ কর যেদিন তিনি বলবেনঃ তোমরা যাদেরকে আমার শরীক মনে করতে তাদেরকে আহবান কর; তারা তখন তাদেরকে আহবান করবে, কিন্তু তারা তাদের আহবানে সাড়া দিবেনা এবং আমি তাদের উভয়ের মধ্যস্থলে রেখে দিব এক ধ্বংস গহবর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2193,18,53,'পাপীরা আগুন অবলোকন করে আশংকা করবে যেন ওরা ওতেই পতিত হবে, বস্তুতঃ ওটা হতে ওরা পরিত্রাণ পাবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2194,18,54,'আমি মানুষের জন্য এই কুরআনে বিভিন্ন উপমার দ্বারা আমার বাণী বিশদভাবে বর্ণনা করেছি; মানুষ অধিকাংশ ব্যাপারেই বিতর্ক প্রিয়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2195,18,55,'হিদায়াত আসার পর এ প্রতীক্ষাই শুধু মানুষকে বিশ্বাস স্থাপন এবং তাদের রবের কাছে ক্ষমা প্রার্থনা করতে বিরত রাখে যে, কখন আসবে তাদের পূর্ব-পুরুষদের অনুরূপ আযাব অথবা কখনই বা তারা তা প্রত্যক্ষ করবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2196,18,56,'আমি শুধু সুসংবাদ দাতা ও সতর্ককারী রূপেই রাসূলদেরকে পাঠিয়ে থাকি, কিন্তু সত্য প্রত্যাখ্যানকারীরা মিথ্যা অবলম্বনে বিতন্ডা করে সত্যকে ব্যর্থ করে দেয়ার জন্য এবং আমার নিদর্শনাবলী ও যদ্বারা তাদেরকে সতর্ক করা হয়েছে সেই সবকে তারা বিদ্রুপের বিষয় রূপে গ্রহণ করে থাকে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2197,18,57,'কোন ব্যক্তিকে তার রবের নিদর্শনাবলী স্মরণ করিয়ে দেয়ার পর সে যদি তা হতে মুখ ফিরিয়ে নেয় এবং তার কৃতকর্মসমূহ ভুলে যায় তাহলে তার অপেক্ষা অধিক সীমা লংঘনকারী আর কে? আমি তাদের অন্তরের উপর আবরণ দিয়েছি যেন তারা কুরআন বুঝতে না পারে এবং তাদেরকে বধির করেছি; তুমি তাদেরকে সৎ পথে আহবান করলেও তারা কখনও সৎ পথে আসবেনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2198,18,58,'এবং তোমার রাব্ব ক্ষমাশীল, দয়াবান। তাদের কৃতকর্মের জন্য তাদেরকে শাস্তি দিতে চাইলে তিনি তাদের শাস্তি ত্বরান্বিত করতেন; কিন্তু তাদের জন্য রয়েছে এক প্রতিশ্রুত মুহুর্ত, যা হতে তাদের পরিত্রাণ নেই। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2199,18,59,'ঐ সব জনপদ - তাদের অধিবাসীবৃন্দকে আমি ধ্বংস করেছিলাম যখন তারা সীমা লংঘন করেছিল এবং তাদের ধ্বংসের জন্য আমি স্থির করেছিলাম এক নির্দিষ্ট ক্ষণ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2200,18,60,'স্মরণ কর সেই সময়ের কথা যখন মূসা তার সঙ্গীকে বলেছিলঃ দুই সমুদ্রের সংযোগস্থলে না পৌঁছে আমি থামবনা, আমি যুগ যুগ ধরে চলতে থাকব। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2201,18,61,'তারা যখন উভয়ের সংগমস্থলে পৌঁছল, তারা নিজেদের মাছের কথা ভুলে গেল; ওটা সুরঙ্গের মত পথ করে সমুদ্রে নেমে গেল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2202,18,62,'যখন তারা আরও অগ্রসর হল, মূসা তার সংগীকে বললঃ আমাদের প্রাতঃরাশ নিয়ে এসো, আমরাতো আমাদের এই সফরে ক্লান্ত হয়ে পড়েছি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2203,18,63,'সে বললঃ আপনি কি লক্ষ্য করেছেন, আমরা যখন শিলাখন্ডে বিশ্রাম করছিলাম তখন আমি মাছের কথা ভুলে গিয়েছিলাম? শাইতানই এ কথা বলতে আমাকে ভুলিয়ে দিয়েছিল; মাছটি আশ্চর্যজনকভাবে নিজের পথ করে নেমে গেল সমুদ্রে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2204,18,64,'মূসা বললঃ আমরাতো ঐ স্থানটিরই অনুসন্ধান করছিলাম; অতঃপর তারা নিজেদের পদচিহ্ন ধরে ফিরে চলল। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2205,18,65,'অতঃপর তারা সাক্ষাৎ পেল আমার দাসদের মধ্যে একজনের যাকে আমি আমার নিকট হতে দিয়েছিলাম এক বিশেষ জ্ঞান।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2206,18,66,'মূসা তাকে বললঃ সত্য পথের যে জ্ঞান আপনাকে দান করা হয়েছে তা হতে আমাকে শিক্ষা দিবেন - এই শর্তে আমি আপনার অনুসরণ করব কি?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2207,18,67,'সে বললঃ তুমি কিছুতেই আমার সঙ্গে ধৈর্য ধারণ করে থাকতে পারবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2208,18,68,'যে বিষয় তোমার জ্ঞানায়ত্ত নয় সে বিষয়ে তুমি ধৈর্য ধারণ করবে কেমন করে? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2209,18,69,'মূসা বললঃ আল্লাহ চাইলে আপনি আমাকে ধৈর্যশীল পাবেন এবং আপনার কোন আদেশ আমি অমান্য করবনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2210,18,70,'সে বললঃ আচ্ছা, তুমি যদি আমার অনুসরণ করই তাহলে কোনো বিষয়ে আমাকে প্রশ্ন করনা, যতক্ষণ না আমি সে সম্বন্ধে তোমাকে কিছু বলি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2211,18,71,'অতঃপর তারা যাত্রা শুরু করল। পরে যখন তারা নৌকায় আরোহণ করল তখন সে তাতে ছিদ্র করে দিল; মূসা বললঃ আপনি কি আরোহীদেরকে নিমজ্জিত করে দেয়ার জন্য তাতে ছিদ্র করলেন? আপনিতো এক গুরুতর অন্যায় কাজ করলেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2212,18,72,'সে বললঃ আমি কি বলিনি যে, তুমি আমার সঙ্গে কিছুতেই ধৈর্য ধারণ করতে পারবেনা?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2213,18,73,'মূসা বললঃ আমার ভুলের জন্য আমাকে অপরাধী করবেননা এবং আমার ব্যাপারে অত্যধিক কঠোরতা অবলম্বন করবেননা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2214,18,74,'অতঃপর তারা চলতে লাগল, চলতে চলতে তাদের সাথে এক বালকের সাক্ষাৎ হলে সে তাকে হত্যা করল; তখন মূসা বললঃ আপনি এক নিস্পাপ জীবন নাশ করলেন হত্যার অপরাধ ছাড়াই! আপনিতো এক গুরুতর অন্যায় কাজ করলেন। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2215,18,75,'সে বললঃ আমি কি বলিনি যে, তুমি আমার সাথে কিছুতেই ধৈর্য ধারণ করতে পারবেনা?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2216,18,76,'(মূসা) বললঃ এরপর যদি আমি আপনাকে কোন বিষয়ে জিজ্ঞেস করি তাহলে আপনি আমাকে সঙ্গে রাখবেননা; তখন আমার ওযর আপত্তি চূড়ান্ত সীমায় পৌঁছে যাবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2217,18,77,'অতঃপর উভয়ে চলতে লাগল; চলতে চলতে তারা এক জনপদের অধিবাসীদের নিকট পৌঁছল এবং তাদের নিকট খাদ্য চাইল। কিন্তু তারা তাদের মেহমানদারী করতে অস্বীকার করল; অতঃপর সেখানে তারা এক পতনোম্মুখ প্রাচীর দেখতে পেল এবং সে ওটাকে সুদৃঢ় করে দিল; (মূসা) বললঃ আপনিতো ইচ্ছা করলে এর জন্য পারিশ্রমিক গ্রহণ করতে পারতেন। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2218,18,78,'সে বললঃ এখানেই তোমার ও আমার মধ্যে সম্পর্কচ্ছেদ হল; যে বিষয়ে তুমি ধৈর্য ধারণ করতে পারনি আমি তার তাৎপর্য ব্যাখ্যা করছি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2219,18,79,'নৌকাটির ব্যাপারে - (কথা এই যে), ওটা ছিল কতিপয় দরিদ্র ব্যক্তির, তারা সমুদ্রে জীবিকা অন্বেষণ করত; আমি ইচ্ছা করলাম নৌকাটিকে ক্রটিযুক্ত করতে; কারণ তাদের সামনে ছিল এক রাজা, যে বল প্রয়োগে প্রত্যেক (নিখুত) নৌকা ছিনিয়ে নিত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2220,18,80,'আর কিশোরটি, তার মাতাপিতা ছিল মু’মিন - আমি আশংকা করলাম যে, সে বিদ্রোহাচরণ ও কুফরীর দ্বারা তাদের বিব্রত করবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2221,18,81,'অতঃপর আমি চাইলাম যে, তাদের রাব্ব যেন তাদেরকে তার পরিবর্তে এক সন্তান দান করেন যে হবে পবিত্রতায় মহত্তর ও ভক্তি ভালবাসায় ঘনিষ্টতর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2222,18,82,'আর ঐ প্রাচীরটি - ওটা ছিল নগরবাসী দুই পিতৃহীন কিশোরের, এর নিম্নদেশে আছে তাদের গুপ্তধন এবং তাদের পিতা ছিল সৎকর্ম পরায়ণ। সুতরাং তোমার রাব্ব দয়াপরবশ হয়ে ইচ্ছা করলেন যে, তারা বয়ঃপ্রাপ্ত হোক এবং তারা তোমার রবের দেয়া তাদের ধনভান্ডার উদ্ধার করুক; আমি নিজ হতে কিছু করিনি; তুমি যে বিষয়ে ধৈর্য ধারণে অপারগ হয়েছিলে এটাই তার ব্যাখ্যা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2223,18,83,'তারা তোমাকে যুলকারনাইন সম্বন্ধে জিজ্ঞেস করছে; তুমি বলে দাওঃ আমি তোমাদের নিকট তার বিষয় বর্ণনা করব।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2224,18,84,'আমি তাকে পৃথিবীতে কর্তৃত্ব দিয়েছিলাম এবং প্রত্যেক বিষয়ের উপায় ও পন্থা নির্দেশ করেছিলাম।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2225,18,85,'সে এক পথ অবলম্বন করল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2226,18,86,'চলতে চলতে যখন সে সূর্যের অস্তগমন স্থানে পৌঁছল তখন সে সূর্যকে এক পংকিল পানিতে অস্ত যেতে দেখল এবং সে সেখানে এক সম্প্রদায়কে দেখতে পেল; আমি বললামঃ হে যুলকারনাইন! তুমি তাদেরকে শাস্তি দিতে পার অথবা তাদেরকে সদয়ভাবে গ্রহণ করতে পার।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2227,18,87,'সে বললঃ যে কেহ সীমা লংঘন করবে আমি তাকে শাস্তি দিব। অতঃপর সে তার রবের নিকট প্রত্যাবর্তিত হবে এবং তিনি তাকে কঠিন শাস্তি দিবেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2228,18,88,'তবে যে বিশ্বাস করে এবং সৎ কাজ করে তার জন্য প্রতিদান স্বরূপ আছে কল্যাণ এবং আমার কাজে তাকে সহজ নির্দেশ দিব।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2229,18,89,'আবার সে এক পথ ধরল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2230,18,90,'চলতে চলতে যখন সে সূর্যোদয় স্থলে পৌঁছল তখন সে দেখলো - ওটা এমন এক সম্প্রদায়ের উপর উদিত হচ্ছে যাদের জন্য সূর্য তাপ হতে আত্মরক্ষার কোন অন্তরাল আমি সৃষ্টি করিনি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2231,18,91,'প্রকৃত ঘটনা এটাই যে, তার বৃত্তান্ত আমি সম্যক অবগত আছি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2232,18,92,'আবার সে এক পথ ধরল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2233,18,93,'চলতে চলতে সে যখন পর্বত প্রাচীরের মধ্যবর্তী স্থানে পৌঁছল তখন সেখানে সে এক সম্প্রদায়কে পেল যারা তার কথা মোটেই বুঝতে পারছিলনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2234,18,94,'তারা বললঃ হে যুলকারনাইন! ইয়াজুজ ও মা‘জুজ পৃথিবীতে অশান্তি সৃষ্টি করছে; আমরা কি তোমাকে রাজস্ব দিব এই শর্তে যে, তুমি আমাদের ও তাদের মধ্যে এক প্রাচীর গড়ে দিবে?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2235,18,95,'সে বললঃ আমার রাব্ব আমাকে যে ক্ষমতা দিয়েছেন তা’ই উৎকৃষ্ট; সুতরাং তোমরা আমাকে শ্রম দ্বারা সাহায্য কর, আমি তোমাদের ও তাদের মধ্যস্থলে এক মাযবূত প্রাচীর গড়ে দিব।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2236,18,96,'তোমরা আমার নিকট লৌহপিন্ডসমূহ আনয়ন কর; অতঃপর মধ্যবর্তী ফাঁকা স্থান পূর্ণ হয়ে যখন লৌহস্ত্তপ দুই পর্বতের সমান হল তখন সে বললঃ তোমরা হাঁপরে দম দিতে থাক। যখন ওটা অগ্নিবৎ উত্তপ্ত হল তখন সে বললঃ তোমরা গলিত তামা নিয়ে এসো, আমি ওটা ঢেলে দিই ওর উপর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2237,18,97,'এরপর ইয়াজুজ ও মা’জুজ তা অতিক্রম করতে পারলনা বা ভেদ করতে পারলনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2238,18,98,'যুলকারনাইন বললঃ এটা আমার রবের অনুগ্রহ; যখন আমার রবের প্রতিশ্রুতি পূর্ণ হবে তখন তিনি ওটাকে চূর্ন-বিচূর্ণ করে দিবেন এবং আমার রবের প্রতিশ্রুতি সত্য।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2239,18,99,'সেদিন আমি তাদেরকে ছেড়ে দিব একের পর এক তরঙ্গের আকারে এবং শিঙ্গায় ফুৎকার দেয়া হবে; অতঃপর আমি তাদের সবাইকে একত্রিত করব।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2240,18,100,'আর সেদিন আমি জাহান্নামকে প্রত্যক্ষভাবে উপস্থিত করব সত্য প্রত্যাখ্যানকারীদের নিকট –');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2241,18,101,'যাদের চক্ষু ছিল অন্ধ, আমার নিদর্শনের প্রতি এবং যারা শুনতেও ছিল অপারগ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2242,18,102,'যারা সত্য প্রত্যাখ্যান করেছে তারা কি মনে করে যে, তারা আমার পরিবর্তে আমার বান্দাদেরকে অভিভাবকরূপে গ্রহণ করবে? আমি সত্য প্রত্যাখ্যানকারীদের অভ্যর্থনার জন্য প্রস্তুত রেখেছি জাহান্নাম।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2243,18,103,'বলঃ আমি কি তোমাদেরকে সংবাদ দিব কর্মে বিশেষ ক্ষতিগ্রস্তদের ব্যাপারে?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2244,18,104,'ওরাই তারা, পার্থিব জীবনে যাদের প্রচেষ্টা পন্ড হয়, যদিও তারা মনে করে যে, তারা সৎ কাজ করছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2245,18,105,'ওরাই তারা, যারা অস্বীকার করে তাদের রবের নিদর্শনাবলী ও তাঁর সাথে তাদের সাক্ষাতের বিষয়। ফলে তাদের কাজ নিষ্ফল হয়ে যায়; সুতরাং কিয়ামাত দিবসে তাদের জন্য কোন ওজনের ব্যবস্থা রাখব না।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2246,18,106,'জাহান্নাম-এটাই তাদের প্রতিফল, যেহেতু তারা কুফরী করেছে এবং আমার নিদর্শনাবলী ও রাসূলগণকে গ্রহণ করেছে বিদ্রুপের বিষয়স্বরূপ। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2247,18,107,'যারা ঈমান আনে ও সৎ কাজ করে তাদের আপ্যায়নের জন্য আছে জান্নাতুল ফিরদাউস।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2248,18,108,'সেখানে তারা স্থায়ী হবে; তা হতে স্থানান্তর কামনা করবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2249,18,109,'বলঃ আমার রবের কথা লিপিবদ্ধ করার জন্য সমুদ্র যদি কালি হয় তাহলেও আমার রবের কথা শেষ হবার পূর্বেই সমুদ্র নিঃশেষ হয়ে যাবে-সাহায্যার্থে এর অনুরূপ আরও সমুদ্র নিয়ে এলেও।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2250,18,110,'বলঃ আমিতো তোমাদের মতই একজন মানুষ, আমার প্রতি প্রত্যাদেশ হয় যে, তোমাদের ইলাহ একমাত্র ইলাহ। সুতরাং যে তার রবের সাথে সাক্ষাত কামনা করে সে যেন সৎ কাজ করে এবং তার রবের ইবাদাতে কেহকেও শরীক না করে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2251,19,1,'কাফ্ হা ইয়া ‘আঈন সাদ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2252,19,2,'এটা তোমার রবের অনুগ্রহের বিবরণ, তাঁর দাস যাকারিয়ার প্রতি। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2253,19,3,'যখন সে তার রাব্বকে আহবান করেছিল নিভৃতে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2254,19,4,'সে বলেছিলঃ হে আমার রাব্ব! আমার অস্থি দুর্বল হয়েছে, বার্ধক্যে আমার মস্তক শুভ্রোজ্জ্বল হয়েছে; হে আমার রাব্ব! আপনাকে আহবান করে আমি কখনও ব্যর্থকাম হইনি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2255,19,5,'আমি আশংকা করি আমার পর আমার স্বগোত্ররা দীনকে ধ্বংস করে দিবে; আমার স্ত্রী বন্ধ্যা। সুতরাং আপনি আপনার তরফ হতে আমাকে দান করুন উত্তরাধিকারী – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2256,19,6,'যে আমার উত্তরাধিকারী হবে এবং উত্তরাধিকারীত্ব পাবে ইয়াকূবের বংশের এবং হে আমার রাব্ব! তাকে করুন সন্তোষভাজন। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2257,19,7,'তিনি বললেনঃ হে যাকারিয়া! আমি তোমাকে এক পুত্রের সুসংবাদ দিচ্ছি - তার নাম হবে ইয়াহইয়া। এই নামে আমি পূর্বে কারও নামকরণ করিনি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2258,19,8,'সে বললঃ হে আমার রাব্ব! কেমন করে আমার পুত্র হবে যখন আমার স্ত্রী বন্ধ্যা এবং আমি বার্ধক্যের শেষ সীমায় পৌঁছে গেছি!');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2259,19,9,'তিনি বললেনঃ এরূপই হবে। তোমার রাব্ব বললেনঃ এটা আমার জন্য সহজসাধ্য; আমিতো পূর্বে তোমাকে সৃষ্টি করেছি যখন তুমি কিছুই ছিলেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2260,19,10,'যাকারিয়া বললঃ হে আমার রাব্ব! আমাকে একটি নিদর্শন দিন। তিনি বললেনঃ তোমার নিদর্শন এই যে, তুমি সুস্থাবস্থায় কারও সাথে তিন দিন বাক্যালাপ করবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2261,19,11,'অতঃপর সে কক্ষ হতে বের হয়ে তার সম্প্রদায়ের নিকট এলো এবং ইঙ্গিতে তাদেরকে সকাল-সন্ধ্যায় (আল্লাহর) পবিত্রতা ও মহিমা ঘোষণা করতে বলল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2262,19,12,'আমি বললামঃ হে ইয়াহ্ইয়া! এই কিতাব দৃঢ়তার সাথে গ্রহণ কর; আমি তাকে শৈশবেই দান করেছিলাম জ্ঞান – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2263,19,13,'এবং আমার নিকট হতে হৃদয়ের কোমলতা ও পবিত্রতা; সে ছিল সাবধানী –');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2264,19,14,'মাতা-পিতার অনুগত এবং সে ছিলনা উদ্ধ্যত, অবাধ্য ছিলনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2265,19,15,'তার প্রতি ছিল শান্তি যেদিন সে জন্ম গ্রহণ করে এবং শান্তি যেদিন তার মৃত্যু হয় এবং যেদিন সে পুনরুজ্জীবিত হবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2266,19,16,'বর্ণনা কর এই কিতাবে উল্লেখিত মারইয়ামের কথা, যখন সে তার পরিবারবর্গ হতে পৃথক হয়ে নিরালায় পূর্ব দিকে এক স্থানে আশ্রয় নিল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2267,19,17,'অতঃপর তাদের হতে নিজেকে আড়াল করার জন্য সে পর্দা করল; অতঃপর আমি তার নিকট আমার রূহকে (জিবরাঈলকে) পাঠালাম, সে তার নিকট পূর্ণ মানবাকৃতিতে আত্মপ্রকাশ করল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2268,19,18,'মারইয়াম বললঃ তুমি যদি (আল্লাহকে) ভয় কর তাহলে আমি তোমা হতে দয়াময়ের কাছে আশ্রয় চাচ্ছি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2269,19,19,'সে বললঃ আমিতো শুধু তোমার রাব্ব হতে প্রেরিত, তোমাকে এক পবিত্র পুত্র দান করার (সুসংবাদ জানানোর) জন্য।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2270,19,20,'মারইয়াম বললঃ কেমন করে আমার পুত্র হবে যখন আমাকে কোন পুরুষ স্পর্শ করেনি এবং আমি ব্যভিচারিণীও নই।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2271,19,21,'সে বললঃ এরূপই হবে; তোমার রাব্ব বলেছেন - এটা আমার জন্য সহজ সাধ্য এবং তাকে আমি এ জন্য সৃষ্টি করব যেন সে হয় মানুষের জন্য এক নিদর্শন এবং আমার নিকট হতে এক অনুগ্রহ; এটাতো এক স্থিরীকৃত ব্যাপার।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2272,19,22,'অতঃপর সে গর্ভে সন্তান ধারণ করল এবং ঐ অবস্থায় এক দূরবর্তী স্থানে চলে গেল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2273,19,23,'প্রসব বেদনা তাকে এক খর্জুর বৃক্ষ তলে আশ্রয় নিতে বাধ্য করল; সে বললঃ হায়! এর পূর্বে আমি যদি মরে যেতাম এবং লোকের স্মৃতি হতে সম্পূর্ণ বিলুপ্ত হতাম!');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2274,19,24,'মালাক/ফেরেশতা তার নিম্ন পার্শ্ব হতে আহবান করে তাকে বললঃ তুমি দুঃখ করনা, তোমার পাদদেশে তোমার রাব্ব এক নহর সৃষ্টি করেছেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2275,19,25,'তুমি তোমার দিকে খেজুর বৃক্ষের কান্ডে নাড়া দাও, ওটা তোমাকে সুপক্ক তাজা খেজুর দান করবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2276,19,26,'সুতরাং আহার কর, পান কর ও চক্ষু জুড়িয়ে নাও; মানুষের মধ্যে কেহকে যদি তুমি দেখ তখন বলঃ আমি দয়াময়ের উদ্দেশে মৌনতা অবলম্বনের মানত করেছি; সুতরাং আজ আমি কিছুতেই কোন মানুষের সাথে বাক্যালাপ করবনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2277,19,27,'অতঃপর সে সন্তানকে নিয়ে তার সম্প্রদায়ের নিকট উপস্থিত হল; তারা বললঃ হে মারইয়াম! তুমিতো এক অদ্ভুত কান্ড করেছ!');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2278,19,28,'হে হারূন ভগ্নি! তোমার পিতা অসৎ ব্যক্তি ছিলনা এবং তোমার মাতাও ছিলনা ব্যভিচারিণী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2279,19,29,'অতঃপর মারইয়াম ইঙ্গিতে সন্তানকে দেখাল; তারা বললঃ যে কোলের শিশু তার সাথে আমরা কেমন করে কথা বলব?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2280,19,30,'সে (ঈসা) বললঃ আমিতো আল্লাহর দাস; তিনি আমাকে কিতাব দিয়েছেন, আমাকে নাবী করেছেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2281,19,31,'যেখানেই আমি থাকি না কেন তিনি আমাকে আশিষ ভাজন করেছেন, তিনি আমাকে নির্দেশ দিয়েছেন যত দিন জীবিত থাকি ততদিন সালাত ও যাকাত আদায় করতে –');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2282,19,32,'আর আমার মাতার প্রতি অনুগত থাকতে এবং তিনি আমাকে করেননি উদ্ধত ও হতভাগা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2283,19,33,'আমার প্রতি শান্তি যেদিন আমি জন্ম লাভ করেছি ও শান্তি থাকবে যেদিন আমার মৃত্যু হবে এবং যেদিন আমি জীবিত অবস্থায় পুনরুত্থিত হব।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2284,19,34,'এই মারইয়াম তনয় ঈসা! আমি বললাম সত্য কথা, যে বিষয়ে তারা বিতর্ক করে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2285,19,35,'সন্তান গ্রহণ করা আল্লাহর কাজ নয়, তিনি পবিত্র, মহিমাময়; তিনি যখন কিছু স্থির করেন তখন বলেনঃ ‘হও’ এবং তা হয়ে যায়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2286,19,36,'আল্লাহই আমার রাব্ব এবং তোমাদের রাব্ব। সুতরাং তাঁর ইবাদাত কর, এটাই সরল পথ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2287,19,37,'অতঃপর দলগুলি নিজেদের মধ্যে মতানৈক্য সৃষ্টি করল; সুতরাং এই কাফিরদের মহা দিনের আগমনে ভীষণ দুর্দশা রয়েছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2288,19,38,'তারা যেদিন আমার নিকট আসবে সেদিন তারা কত স্পষ্ট শুনবে ও দেখবে! কিন্তু সীমা লংঘনকারীরা আজ স্পষ্ট বিভ্রান্তিতে আছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2289,19,39,'তাদেরকে সতর্ক করে দাও পরিতাপের দিন সম্বন্ধে, যখন সকল সিদ্ধান্ত হয়ে যাবে; এখন তারা অনুধাবন এবং বিশ্বাস স্থাপন করবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2290,19,40,'চূড়ান্ত মালিকানার অধিকারী আমি, পৃথিবীর এবং ওর উপর যা আছে তাদেরও এবং তারা আমারই নিকট প্রত্যানীত হবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2291,19,41,'বর্ণনা কর এই কিতাবে উল্লিখিত ইবরাহীমের কথা; সে ছিল সত্যবাদী ও নাবী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2292,19,42,'যখন সে তার পিতাকে বললঃ হে আমার পিতা! যে শোনেনা, দেখেনা এবং তোমার কোন কাজে আসেনা তুমি তার ইবাদাত কর কেন?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2293,19,43,'হে আমার পিতা! আমার নিকটতো এসেছে জ্ঞান যা তোমার নিকট আসেনি। সুতরাং আমার অনুসরণ কর, আমি তোমায় সঠিক পথ দেখাব।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2294,19,44,'হে আমার পিতা! শাইতানের ইবাদাত করনা; শাইতান আল্লাহর অবাধ্য।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2295,19,45,'হে আমার পিতা! আমি আশংকা করি, তোমাকে আল্লাহর শাস্তি স্পর্শ করবে এবং তুমি শাইতানের সাথী হয়ে পড়বে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2296,19,46,'পিতা বললঃ হে ইবরাহীম! তুমি কি আমার দেব-দেবী হতে বিমুখ হচ্ছ? যদি নিবৃত্ত না হও তাহলে আমি প্রস্তরাঘাতে তোমার প্রাণ নাশ করবই; তুমি চিরদিনের জন্য আমার নিকট হতে দূর হয়ে যাও।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2297,19,47,'ইবরাহীম বললঃ তোমার নিকট হতে বিদায়; আমি আমার রবের নিকট তোমার জন্য ক্ষমা প্রার্থনা করব, তিনি আমার প্রতি অতিশয় অনুগ্রহশীল। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2298,19,48,'আমি তোমাদের দিক হতে এবং তোমরা আল্লাহ ব্যতীত যাদের ইবাদাত কর তাদের দিক হতে পৃথক হচ্ছি; আমি আমার রবের আহবান করি; আশা করি আমার রবের আহবান করে আমি ব্যর্থকাম হবনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2299,19,49,'অতঃপর সে যখন তাদের থেকে এবং তারা আল্লাহ ব্যতীত যাদের ইবাদাত করত সেই সব হতে পৃথক হয়ে গেল তখন আমি তাকে দান করলাম ইসহাক ও ইয়াকূব এবং প্রত্যেককে নাবী করলাম।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2300,19,50,'এবং তাদেরকে আমি দান করলাম আমার অনুগ্রহ ও তাদের দিলাম সমুচ্চ বংশ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2301,19,51,'এই কিতাবে উল্লেখিত মূসার কথা বর্ণনা কর, সে ছিল বিশুদ্ধ চিত্ত এবং সে ছিল রাসূল, নাবী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2302,19,52,'আমি তাকে আহবান করেছিলাম তূর পর্বতের দক্ষিণ দিক হতে এবং আমি গুঢ়তত্ত্ব আলোচনারত অবস্থায় তাকে নিকটবর্তী করেছিলাম। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2303,19,53,'আমি নিজ অনুগ্রহে তাকে দিলাম তার ভাই হারূনকে, নাবীরূপে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2304,19,54,'এই কিতাবে উল্লেখিত ইসমাঈলের কথা বর্ণনা কর, সে ছিল প্রতিশ্রুতি পালনে সত্যাশ্রয়ী এবং সে ছিল রাসূল, নাবী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2305,19,55,'সে তার পরিজনবর্গকে সালাত ও যাকাত আদায়ের নির্দেশ দিত এবং সে ছিল তার রবের সন্তোষভাজন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2306,19,56,'এই কিতাবে উল্লেখিত ইদরীসের কথা বর্ণনা কর, সে ছিল সত্যবাদী নাবী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2307,19,57,'এবং আমি তাকে দান করেছিলাম উচ্চ মর্যাদা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2308,19,58,'নাবীদের মধ্যে যাদেরকে আল্লাহ অনুগ্রহ করেছেন এরাই তারা, আদমের এবং যাদেরকে আমি নূহের সাথে নৌকায় আরোহণ করিয়েছিলাম তাদের বংশদ্ভুত, ইবরাহীম ও ইসরাঈলের বংশদ্ভুত ও যাদেরকে আমি পথ নির্দেশ করেছিলাম ও মনোনীত করেছিলাম তাদের অন্তর্ভুক্ত; তাদের নিকট দয়াময়ের আয়াত আবৃত্তি করা হলে তারা সাজদাহয় লুটিয়ে পড়ত ক্রন্দন করতে করতে। [সাজদাহ]');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2309,19,59,'তাদের পরে এলো অপদার্থ পরবর্তীরা; তারা সালাত নষ্ট করল ও লালসা পরবশ হল; সুতরাং তারা অচিরেই কুকর্মের শাস্তি প্রত্যক্ষ করবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2310,19,60,'কিন্তু তারা নয় - যারা তাওবাহ করেছে, ঈমান আনয়ন করেছে ও সৎ কাজ করেছে; তারাতো জান্নাতে প্রবেশ করবে, তাদের প্রতি কোন যুলম করা হবেনা –');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2311,19,61,'এটা স্থায়ী জান্নাত, যে অদৃশ্য বিষয়ের প্রতিশ্রুতি দয়াময় তাঁর বান্দাদেরকে দিয়েছেন, তাঁর প্রতিশ্রুত বিষয় অবশ্যম্ভাবী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2312,19,62,'সেখানে তারা শান্তি ছাড়া কোন অসার বাক্য শুনবেনা এবং সেখানে সকাল-সন্ধ্যা তাদের জন্য থাকবে জীবনোপকরণ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2313,19,63,'এই সেই জান্নাত, যার অধিকারী করব আমি আমার বান্দাদের মধ্যে মুত্তাকীদেরকে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2314,19,64,'আমরা আপনার রবের আদেশ ব্যতীত অবতরণ করিনা; যা আমাদের অগ্রে ও পশ্চাতে আছে এবং যা এই দু’এর অন্তবর্তী তা তাঁরই এবং তোমার রাব্ব কোনো কিছু ভুলেননা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2315,19,65,'তিনি আকাশমন্ডলী, পৃথিবী এবং এতদুভয়ের অন্তর্বতী যা কিছু আছে সবারই রাব্ব; সুতরাং তুমি তাঁরই ইবাদাতে ধৈর্যশীল থাক; তুমি কি তাঁর সমগুণ সম্পন্ন কেহকে জান?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2316,19,66,'মানুষ বলেঃ আমার মৃত্যু হলে আমি কি জীবিত অবস্থায় পুনরুত্থিত হব?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2317,19,67,'মানুষ কি স্মরণ করেনা যে, আমি তাকে পূর্বে সৃষ্টি করেছি যখন সে কিছুই ছিলনা?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2318,19,68,'সুতরাং শপথ তোমার রবের! আমিতো তাদেরকে শাইতানদেরসহ একত্রে সমবেত করবই এবং পরে আমি তাদেরকে নতজানু অবস্থায় জাহান্নামের চতুর্দিকে উপস্থিত করবই। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2319,19,69,'অতঃপর প্রত্যেক দলের মধ্যে যে দয়াময়ের প্রতি সর্বাধিক অবাধ্য আমি তাকে টেনে বের করবই।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2320,19,70,'তারপর আমিতো তাদের মধ্যে যারা জাহান্নামের আগুনে দগ্ধ হওয়ার অধিকতর যোগ্য তাদের বিষয় ভাল জানি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2321,19,71,'এবং তোমাদের প্রত্যেকেই ওটা অতিক্রম করবে; ওটা তোমার রবের অনিবার্য সিদ্ধান্ত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2322,19,72,'পরে আমি মুত্তাকীদেরকে উদ্ধার করব এবং যালিমদের সেখানে নতজানু অবস্থায় রেখে দিব।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2323,19,73,'তাদের নিকট আমার সুস্পষ্ট আয়াত আবৃত্তি করা হলে কাফিরেরা মু’মিনদেরকে বলেঃ দু’ দলের মধ্যে কোন্টি মর্যাদায় শ্রেয়তর ও মজলিস হিসাবে কোনটি উত্তম? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2324,19,74,'তাদের পূর্বে কত মানবগোষ্ঠীকে আমি ধ্বংস করেছি যারা তাদের অপেক্ষা সম্পদ ও বাহ্য দৃষ্টিতে শ্রেষ্ঠ ছিল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2325,19,75,'বলঃ যারা বিভ্রান্তিতে আছে, দয়াময় তাদেরকে প্রচুর অবকাশ দিবেন যতক্ষণ না তারা, যে বিষয়ে তাদেরকে সতর্ক করা হচ্ছে তা প্রত্যক্ষ করবে, তা শাস্তি হোক অথবা কিয়ামাতই হোক; অতঃপর তারা জানতে পারবে কে মর্যাদায় নিকৃষ্ট এবং কে দলবলে দুর্বল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2326,19,76,'এবং যারা সৎ পথে চলে আল্লাহ তাদেরকে অধিক হিদায়াত দান করেন এবং স্থায়ী সৎ কাজ তোমার রবের পুরস্কার প্রাপ্তির জন্য শ্রেষ্ঠ এবং প্রতিদান হিসাবেও শ্রেষ্ঠ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2327,19,77,'তুমি কি লক্ষ্য করেছ তাকে, যে আমার আয়াতসমূহ প্রত্যাখ্যান করে এবং বলেঃ আমাকে ধন সম্পদ ও সন্তান-সন্ততি দেয়া হবেই।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2328,19,78,'সে কি অদৃশ্য সম্বন্ধে অবহিত হয়েছে অথবা দয়াময়ের নিকট হতে প্রতিশ্রুতি লাভ করেছে?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2329,19,79,'কখনই নয়, তারা যা বলে আমি তা লিখে রাখবো এবং তাদের শাস্তি বৃদ্ধি করতে থাকব।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2330,19,80,'সে যে বিষয়ের কথা বলে তা থাকবে আমার অধিকারে এবং সে আমার নিকট আসবে একা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2331,19,81,'তারা আল্লাহ ছাড়া অন্য কেহকে মা‘বূদ রূপে গ্রহণ করে এ জন্য যে, যাতে তারা তাদের সহায় হয়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2332,19,82,'কখনই নয়; তারা তাদের ইবাদাত অস্বীকার করবে এবং তাদের বিরোধী হয়ে যাবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2333,19,83,'তুমি কি লক্ষ্য করনা যে, আমি কাফিরদের জন্য শাইতানদেরকে ছেড়ে রেখেছি তাদেরকে মন্দ কর্মে বিশেষভাবে প্রলুব্ধ করার জন্য। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2334,19,84,'সুতরাং তাদের বিষয়ে তাড়া করনা; আমিতো গণনা করছি তাদের নির্ধারিত কাল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2335,19,85,'যেদিন আমি দয়াময়ের নিকট মুত্তাকীদের সম্মানিত মেহমান রূপে সমবেত করব। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2336,19,86,'এবং অপরাধীদেরকে পিপাসার্ত অবস্থায় জাহান্নামের দিকে তাড়িয়ে নিয়ে যাব। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2337,19,87,'যে দয়াময়ের নিকট প্রতিশ্রুতি গ্রহণ করেছে সে ব্যতীত অন্য কারও সুপারিশ করার ক্ষমতা থাকবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2338,19,88,'তারা বলেঃ দয়াময় সন্তান গ্রহণ করেছেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2339,19,89,'তোমরাতো এক ভয়ংকর কথার অবতারণা করেছ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2340,19,90,'এতে যেন আকাশসমূহ বিদীর্ণ হয়ে যাবে, পৃথিবী খন্ড বিখন্ড হবে এবং পর্বতসমূহ চূর্ণ বিচূর্ণ হয়ে আপতিত হবে –');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2341,19,91,'যেহেতু তারা দয়াময়ের উপর সন্তান আরোপ করে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2342,19,92,'অথচ সন্তান গ্রহণ করা দয়াময়ের জন্য শোভন নয়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2343,19,93,'আকাশসমূহ ও পৃথিবীতে এমন কেহ নেই যে দয়াময়ের নিকট উপস্থিত হবেনা বান্দা রূপে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2344,19,94,'তিনি তাদেরকে পরিবেষ্টন করে রেখেছেন এবং তিনি তাদেরকে বিশেষভাবে গণনা করেছেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2345,19,95,'এবং কিয়ামাত দিবসে তাদের সকলেই তাঁর নিকট আসবে একাকী অবস্থায়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2346,19,96,'যারা ঈমান আনে ও সৎ কাজ করে দয়াময় তাদের জন্য সৃষ্টি করবেন ভালবাসা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2347,19,97,'আমিতো তোমার ভাষায় কুরআনকে সহজ করে দিয়েছি যাতে তুমি ওর দ্বারা মুত্তাকীদেরকে সুসংবাদ দিতে পার এবং বিতন্ডা প্রবণ সম্প্রদায়কে সতর্ক করতে পার।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2348,19,98,'তাদের পূর্বে আমি কত মানব গোষ্ঠীকে বিনাশ করেছি! তুমি কি তাদের কেহকেও দেখতে পাও অথবা ক্ষীণতম শব্দও শুনতে পাও?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2349,20,1,'ত্ব-হা');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2350,20,2,'তোমাকে ক্লেশ দেয়ার জন্য আমি তোমার প্রতি কুরআন অবতীর্ণ করিনি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2351,20,3,'বরং যারা ভয় করে তাদের উপদেশার্থে –');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2352,20,4,'যিনি সমুচ্চ আকাশমন্ডলী ও পৃথিবী সৃষ্টি করেছেন এটা তাঁর নিকট হতে অবতীর্ণ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2353,20,5,'দয়াময় আরশে সমাসীন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2354,20,6,'যা আছে আকাশমন্ডলীতে, পৃথিবীতে, এ দু’য়ের অন্তর্বতী স্থানে ও ভূগর্ভে তা তাঁরই।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2355,20,7,'তুমি যদি উচ্চ কন্ঠে বল, তিনি যা গুপ্ত ও অব্যক্ত সবই জানেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2356,20,8,'আল্লাহ! তিনি ছাড়া অন্য কোন মা‘বূদ নেই, সমস্ত উত্তম নাম তাঁরই।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2357,20,9,'মূসার বৃত্তান্ত তোমার কাছে পৌঁছেছে কি?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2358,20,10,'সে যখন আগুন দেখল তখন তার পরিবারবর্গকে বললঃ তোমরা এখানে থাক আমি আগুন দেখেছি; সম্ভবতঃ আমি তোমাদের জন্য তা হতে কিছু জ্বলন্ত অঙ্গার আনতে পারবো অথবা ওর নিকটে কোন পথ প্রদর্শক পাব।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2359,20,11,'অতঃপর যখন সে আগুনের নিকট এলো তখন আহবান করে বলা হলঃ হে মূসা!');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2360,20,12,'আমিই তোমার রাব্ব। অতএব তোমার পাদুকা খুলে ফেলো, কারণ তুমি পবিত্র তূওয়া উপত্যকায় রয়েছ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2361,20,13,'এবং আমি তোমাকে মনোনীত করেছি, অতএব যা অহী প্রেরণ করা হচ্ছে তুমি তা মনোযোগের সাথে শ্রবণ কর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2362,20,14,'আমি আল্লাহ! আমি ছাড়া কোন মা‘বূদ নেই; অতএব আমার ইবাদাত কর এবং আমার স্মরণার্থে সালাত কায়েম কর। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2363,20,15,'কিয়ামাত অবশ্যম্ভাবী, আমি এটা গোপন রাখতে চাই যাতে প্রত্যেকেই নিজ কর্মানুযায়ী ফল লাভ করতে পারে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2364,20,16,'সুতরাং যে ব্যক্তি কিয়ামাত বিশ্বাস করেনা এবং নিজ প্রবৃত্তির অনুসরণ করে সে যেন তোমাকে তাতে বিশ্বাস স্থাপনে প্রবৃত্ত না করে, তাহলে তুমি ধ্বংস হয়ে যাবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2365,20,17,'হে মূসা! তোমার ডান হাতে ওটা কি?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2366,20,18,'সে বললঃ এটা আমার লাঠি; আমি এতে ভর দিই এবং এটা দ্বারা আঘাত করে আমি আমার মেষপালের জন্য বৃক্ষ পত্র ফেলে থাকি এবং এটা আমার অন্যান্য কাজেও লাগে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2367,20,19,'(আল্লাহ) বললেনঃ হে মূসা! তুমি ওটা নিক্ষেপ কর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2368,20,20,'অতঃপর সে তা নিক্ষেপ করল, সাথে সাথে তা সাপ হয়ে ছুটতে লাগল। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2369,20,21,'তিনি বললেনঃ তুমি একে ধর। ভয় করনা, আমি একে এর পূর্বরূপে ফিরিয়ে দিব।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2370,20,22,'এবং তুমি হাত বগলে রাখ, ওটি বের হয়ে আসবে নির্মল উজ্জ্বল হয়ে অপর এক নিদর্শন স্বরূপ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2371,20,23,'এটা এ জন্য যে, আমি তোমাকে দেখাবো আমার মহা নিদর্শনগুলির কিছু।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2372,20,24,'ফির‘আউনের নিকট যাও, সে সীমালংঘন করেছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2373,20,25,'মূসা বললঃ হে আমার রাব্ব! আমার বক্ষ প্রশস্ত করে দিন,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2374,20,26,'আমার কাজকে সহজ করে দিন,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2375,20,27,' আমার জিহবার জড়তা দূর করে দিন, ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2376,20,28,'যাতে তারা আমার কথা বুঝতে পারে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2377,20,29,'আমার জন্য করে দিন একজন সাহায্যকারী আমার স্বজনবর্গের মধ্য হতে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2378,20,30,'আমার ভাই হারূণ –');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2379,20,31,'তার দ্বারা আমার শক্তি সুদৃঢ় করুন। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2380,20,32,'এবং তাকে আমার কাজে অংশী করুন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2381,20,33,'যাতে আমরা আপনার পবিত্রতা ও মহিমা ঘোষণা করতে পারি প্রচুর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2382,20,34,'এবং আপনাকে স্মরণ করতে পারি অধিক।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2383,20,35,'আপনিতো আমাদের সম্যক দ্রষ্টা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2384,20,36,'তিনি বললেনঃ হে মূসা, তুমি যা চেয়েছ তা তোমাকে দেয়া হল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2385,20,37,'এবং আমিতো তোমার প্রতি আর একবার অনুগ্রহ করেছিলাম।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2386,20,38,'যখন আমি তোমার মায়ের অন্তরে ইংগিত দ্বারা নির্দেশ দিয়েছিলাম যা এখানে বর্ণিত হচ্ছে –');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2387,20,39,'যে, তুমি তাকে সিন্দুকের মধ্যে রাখ, অতঃপর তা নদীতে ভাসিয়ে দাও যাতে নদী ওকে তীরে ঠেলে দেয়, ওকে আমার শত্রু ও তার শত্রু নিয়ে যাবে; আমি আমার নিকট হতে তোমার উপর ভালবাসা ঢেলে দিয়েছিলাম যাতে তুমি আমার দৃষ্টির সামনে প্রতিপালিত হও।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2388,20,40,'যখন তোমার বোন এসে বললঃ আমি কি তোমাদেরকে বলে দিব কে এই শিশুর দায়িত্ব নিবে? তখন আমি তোমাকে তোমার মায়ের নিকট ফিরিয়ে দিলাম যাতে তার চোখ জুড়ায় এবং সে দুঃখ না পায়; এবং তুমি এক ব্যক্তিকে হত্যা করেছিলে; অতঃপর আমি তোমাকে মনঃপীড়া হতে মুক্তি দিই, আমি তোমাকে বহু পরীক্ষা করেছি। অতঃপর তুমি কয়েক বছর মাদইয়ানবাসীদের মধ্যে ছিলে। হে মূসা! এরপরে তুমি নির্ধারিত সময়ে উপস্থিত হলে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2389,20,41,'এবং আমি তোমাকে আমার নিজের জন্য প্রস্তুত করে নিয়েছি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2390,20,42,'তুমি ও তোমার ভাই আমার নিদর্শনসমূহ সহ যাত্রা শুরু কর এবং আমার স্মরণে তোমরা উভয়ে শৈথিল্য করনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2391,20,43,'তোমরা উভয়ে ফির‘আউনের নিকট যাও, সেতো সীমালংঘন করেছে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2392,20,44,'তোমরা তার সাথে নম্র কথা বলবে, হয়তো সে উপদেশ গ্রহণ করবে, অথবা ভয় করবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2393,20,45,'তারা বললঃ হে আমাদের রাব্ব! আমরা আশংকা করি যে, সে আমাদেরকে ত্বরায় শাস্তি দিতে উদ্যত হবে অথবা অন্যায় আচরণে সীমালংঘন করবে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2394,20,46,'তিনি বললেনঃ তোমরা ভয় করনা, আমিতো তোমাদের সংগে আছি, আমি শুনি ও দেখি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2395,20,47,'সুতরাং তোমরা তার নিকট যাও এবং বলঃ অবশ্যই আমরা তোমার রবের পক্ষ থেকে প্রেরিত রাসূল। সুতরাং আমাদের সাথে বানী ইসরাঈলকে যেতে দাও এবং তাদেরকে কষ্ট দিওনা, আমরাতো তোমার নিকট এনেছি তোমার রবের নিকট হতে নিদর্শন। এবং শান্তি তাদের প্রতি যারা সৎ পথের অনুসরণ করে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2396,20,48,'আমাদের প্রতি অহী প্রেরণ করা হয়েছে যে, শাস্তি তার জন্য যে মিথ্যা আরোপ করে ও মুখ ফিরিয়ে নেয়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2397,20,49,'ফির‘আউন বললঃ হে মূসা! কে তোমাদের রাব্ব?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2398,20,50,'মূসা বললঃ আমার রাব্ব তিনি যিনি প্রত্যেক বস্তুকে তার যোগ্য আকৃতি দান করেছেন; অতঃপর পথ নির্দেশ করেছেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2399,20,51,'ফির‘আউন বললঃ তাহলে অতীত যুগের লোকদের অবস্থা কি?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2400,20,52,'মূসা বললঃ এর জ্ঞান আমার রবের নিকট লিপিবদ্ধ রয়েছে; আমার রাব্ব ভুল করেননা এবং বিস্মৃত হননা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2401,20,53,'যিনি তোমাদের জন্য পৃথিবীকে করেছেন বিছানা এবং তাতে করে দিয়েছেন তোমাদের চলার পথ, তিনি আকাশ হতে বারি বর্ষন করেন। আমি উহা দ্বারা বিভিন্ন প্রকারের উদ্ভিদ উৎপন্ন করি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2402,20,54,'তোমরা আহার কর ও তোমাদের গবাদি পশু চরাও; অবশ্যই এতে নিদর্শন রয়েছে বিবেক সম্পন্নদের জন্য।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2403,20,55,'আমি মাটি হতে তোমাদেরকে সৃষ্টি করেছি; তাতেই তোমাদেরকে ফিরিয়ে দিব এবং তা হতে পুনর্বার বের করব।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2404,20,56,'আমিতো তাকে আমার সমস্ত নিদর্শন দেখিয়েছিলাম; কিন্তু সে মিথ্যা আরোপ করেছে ও অমান্য করেছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2405,20,57,'সে বললঃ হে মূসা! তুমি কি আমাদের নিকট এসেছ তোমার যাদু দ্বারা আমাদেরকে আমাদের দেশ হতে বহিস্কার করে দেয়ার জন্য?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2406,20,58,'আমরাও অবশ্যই তোমার নিকট উপস্থিত করব এর অনুরূপ যাদু। সুতরাং আমাদের ও তোমার মাঝে নির্ধারণ কর এক নির্দিষ্ট সময় এবং এক মধ্যবর্তী স্থান, যার ব্যতিক্রম আমরাও করবনা এবং তুমিও করবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2407,20,59,'মূসা বললঃ তোমাদের নির্ধারিত সময় উৎসবের দিন এবং সেদিন পূর্বাহ্নে জনগণকে সমবেত করা হোক।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2408,20,60,'অতঃপর ফির‘আউন উঠে গেল এবং তার কৌশলসমূহ একত্রিত করল ও অতঃপর ফিরে এলো।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2409,20,61,'মূসা তাদেরকে বললঃ দুর্ভোগ তোমাদের! তোমরা আল্লাহর প্রতি মিথ্যা আরোপ করনা, তা করলে তিনি তোমাদেরকে শাস্তি দ্বারা সমূলে ধ্বংস করবেন; যে মিথ্যা উদ্ভাবন করেছে সে’ই ব্যর্থ হয়েছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2410,20,62,'তারা নিজেদের মধ্যে নিজেদের কাজ সম্বন্ধে বিতর্ক করল এবং তারা গোপনে পরামর্শ করল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2411,20,63,'তারা বললঃ এই দু’জন অবশ্যই যাদুকর, তারা চায় তাদের যাদু দ্বারা তোমাদেরকে তোমাদের দেশ হতে বহিস্কার করতে এবং তোমাদের উৎকৃষ্ট জীবন ব্যবস্থার অস্তিত্ব বিনাশ করতে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2412,20,64,'অতএব তোমরা তোমাদের যাদু ক্রিয়া সংহত কর, অতঃপর সারিবদ্ধ হয়ে উপস্থিত হও এবং যে আজ জয়ী হবে সেই সফল হবে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2413,20,65,'তারা বললঃ হে মূসা! হয় তুমি নিক্ষেপ কর অথবা প্রথমে আমরাই নিক্ষেপ করি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2414,20,66,'মূসা বললঃ বরং তোমরাই নিক্ষেপ কর। তাদের যাদুর প্রভাবে অকস্মাৎ মূসার মনে হল যে, তাদের দড়ি ও লাঠিগুলি ছুটাছুটি করছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2415,20,67,'মূসা তার অন্তরে কিছু ভীতি অনুভব করল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2416,20,68,'আমি বললামঃ ভয় করনা, তুমিই প্রবল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2417,20,69,'তোমার ডান হাতে যা আছে তা নিক্ষেপ কর, এটা তারা যা করেছে তা গ্রাস করে ফেলবে, তারা যা করেছে তাতো শুধু যাদুকরের কৌশল; যাদুকরেরা যা’ই করুক কখনও সফল হবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2418,20,70,'অতঃপর যাদুকরেরা সাজদাহবনত হল ও বললঃ আমরা হারূণ ও মূসার রবের প্রতি ঈমান আনলাম।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2419,20,71,'ফির‘আউন বললঃ কি, আমি তোমাদের অনুমতি দেয়ার পূর্বেই তোমরা মূসাতে বিশ্বাস স্থাপন করলে! সেতো দেখছি তোমাদের প্রধান, সে তোমাদেরকে যাদু শিক্ষা দিয়েছে; সুতরাং আমি তোমাদেরকে খেজুর বৃক্ষের কান্ডে শূলবিদ্ধ করবই এবং তোমরা অবশ্যই জানতে পারবে আমাদের মধ্যে কার শাস্তি কঠোর ও অধিক স্থায়ী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2420,20,72,'তারা বললঃ আমাদের নিকট যে স্পষ্ট নিদর্শন এসেছে তার উপর এবং যিনি আমাদেরকে সৃষ্টি করেছেন তাঁর উপর তোমাকে কিছুতেই আমরা প্রাধান্য দিবনা, সুতরাং তুমি কর যা তুমি করতে চাও, তুমিতো শুধু এই পার্থিব জীবনের উপর কর্তৃত্ব করতে পার।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2421,20,73,'আমরা আমাদের রবের প্রতি ঈমান এনেছি যাতে তিনি ক্ষমা করেন আমাদের অপরাধ এবং তুমি আমাদেরকে যাদু করতে বাধ্য করেছ তা; আর আল্লাহ শ্রেষ্ঠ ও স্থায়ী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2422,20,74,'যে তার রবের নিকট অপরাধী হয়ে উপস্থিত হবে তার জন্য রয়েছে জাহান্নাম, সেখানে সে মরবেও না, বাঁচবেও না।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2423,20,75,'আর যারা তাঁর নিকট উপস্থিত হবে মু’মিন অবস্থায়, সৎ কাজ করে, তাদের জন্য রয়েছে উচ্চ মর্যাদা –');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2424,20,76,'স্থায়ী জান্নাত, যার পাদদেশে নদী প্রবাহিত, সেখানে তারা স্থায়ী হবে এবং এই পুরস্কার তাদেরই জন্য যারা পবিত্র। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2425,20,77,'আমি অবশ্যই মূসার প্রতি প্রত্যাদেশ করেছিলাম এই মর্মে, আমার বান্দাদেরকে নিয়ে রজনীযোগে বহির্গত হও এবং তাদের জন্য সমুদ্রের মধ্য দিয়ে এক শুস্ক পথ নির্মাণ কর; পিছন হতে এসে তোমাকে ধরে ফেলা হবে এই আশংকা করনা এবং ভয়ও করনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2426,20,78,'অতঃপর ফির‘আউন তার সৈন্যবাহিনীসহ তাদের পশ্চাদ্ধাবন করল, অতঃপর সমুদ্র তাদেরকে সম্পূর্ণ রূপে নিমজ্জিত করল। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2427,20,79,'আর ফির‘আউন তার সম্প্রদায়কে পথভ্রষ্ট করেছিল এবং সৎ পথ দেখায়নি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2428,20,80,'হে বানী ইসরাঈল! আমিতো তোমাদেরকে তোমাদের শত্রু হতে উদ্ধার করেছিলাম, আমি তোমাদের প্রতিশ্রুতি দিয়েছিলাম তূর পর্বতের দক্ষিণ পার্শ্বে এবং তোমাদের নিকট মান্না ও সালওয়া প্রেরণ করেছিলাম।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2429,20,81,'তোমাদেরকে আমি যা দান করেছিলাম তা হতে ভাল ভাল বস্তু আহার কর এবং এই বিষয়ে সীমা লংঘন করনা, করলে তোমাদের উপর আবার ক্রোধ অবধারিত এবং যার উপর আমার ক্রোধ অবধারিত সেতো ধ্বংস হয়ে যায়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2430,20,82,'এবং আমি অবশ্যই ক্ষমাশীল তার প্রতি যে তাওবাহ’ করে, ঈমান আনে, সৎ কাজ করে ও সৎ পথে অবিচল থাকে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2431,20,83,'হে মূসা! তোমার সম্প্রদায়কে পশ্চাতে ফেলে তোমাকে ত্বরা করতে বাধ্য করল কিসে?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2432,20,84,'সে বললঃ এইতো তারা আমার পশ্চাতে এবং হে আমার রাব্ব! আমি ত্বরায় আপনার নিকট এলাম, আপনি সন্তষ্ট হবেন এ জন্য।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2433,20,85,'তিনি বললেনঃ আমি তোমার সম্প্রদায়কে পরীক্ষায় ফেলেছি তোমার চলে আসার পর এবং সামেরী তাদেরকে পথভ্রষ্ট করেছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2434,20,86,'অতঃপর মূসা তার সম্প্রদায়ের নিকট ফিরে গেল ক্রুদ্ধ হয়ে; সে বললঃ হে আমার সম্প্রদায়! তোমাদের রাব্ব কি তোমাদেরকে এক উত্তম প্রতিশ্রুতি দেননি? তাহলে কি প্রতিশ্রুত কাল তোমাদের নিকট সুদীর্ঘ হয়েছে, না তোমরা চেয়েছো তোমাদের প্রতি আপতিত হোক তোমাদের রবের ক্রোধ, যে কারণে তোমরা আমার প্রতি প্রদত্ত অঙ্গীকার ভঙ্গ করলে?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2435,20,87,'তারা বললঃ আমরা তোমার প্রতি প্রদত্ত অঙ্গীকার স্বেচ্ছায় ভঙ্গ করিনি; তবে আমাদের উপর চাপিয়ে দেয়া হয়েছিল লোকের অলংকারের বোঝা এবং আমরা তা অগ্নিকুন্ডে নিক্ষেপ করি, অনুরূপভাবে সামেরীও নিক্ষেপ করে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2436,20,88,'অতঃপর সে তাদের জন্য গড়লো এক গো-বৎস, এক অবয়ব, যা হাম্বা আওয়াজ করত; তারা বললঃ এটা তোমাদের মা‘বূদ এবং মূসারও মা‘বূদ, কিন্তু মূসা ভুলে গেছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2437,20,89,'তাহলে কি তারা দেখেনা যে, ওটা তাদের কথায় সাড়া দেয়না এবং তাদের কোন ক্ষতি অথবা উপকার করার ক্ষমতাও রাখেনা?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2438,20,90,'হারূন তাদেরকে পূর্বেই বলেছিলঃ হে আমার সম্প্রদায়! এটা দ্বারা তো শুধু তোমাদেরকে পরীক্ষায় ফেলা হয়েছে; তোমাদের রাব্ব দয়াময়, সুতরাং তোমরা আমার অনুসরণ কর এবং আমার আদেশ মেনে চল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2439,20,91,'তারা বলেছিলঃ আমাদের নিকট মূসা ফিরে না আসা পর্যন্ত আমরা এর পূজা হতে কিছুতেই বিরত হবনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2440,20,92,'মূসা বললঃ হে হারূণ! তুমি যখন দেখলে যে, তারা পথভ্রষ্ট হয়েছে তখন কিসে তোমাকে নিবৃত্ত করল –');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2441,20,93,'আমার অনুসরণ হতে? তাহলে কি তুমি আমার আদেশ অমান্য করলে?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2442,20,94,'হারূণ বললঃ হে আমার সহোদর! আমার শ্মশ্রু ও কেশ ধরে আকর্ষণ করনা; আমি আশংকা করেছিলাম যে, তুমি বলবেঃ তুমি বানী ইসরাঈলের মধ্যে বিভেদ সৃষ্টি করেছ ও আমার বাক্য পালনে যত্নবান হওনি। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2443,20,95,'মূসা বললঃ ওহে সামেরী! তোমার ব্যাপার কি?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2444,20,96,'সে বললঃ আমি যা দেখেছিলাম তারা তা দেখেনি; অতঃপর আমি সেই দূতের পদচিহ্ন হতে এক মুষ্টি নিয়েছিলাম এবং আমি তা নিক্ষেপ করেছিলাম, আর আমার মন আমার জন্য শোভন করেছিল এইরূপ করা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2445,20,97,'মূসা বললঃ দূর হও, তোমার জীবদ্দশায় তোমার জন্য এটাই রইল যে, তুমি বলবেঃ আমি অস্পৃশ্য এবং তোমার জন্য রইল এক নির্দিষ্ট কাল, তোমার বেলায় যার ব্যতিক্রম হবেনা এবং তুমি তোমার সেই মা‘বূদের প্রতি লক্ষ্য কর যার পূজায় তুমি রত ছিলে; আমরা ওকে জ্বালিয়ে দিবই, অতঃপর ওকে বিক্ষিপ্ত করে সাগরে নিক্ষেপ করবই।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2446,20,98,'তোমাদের ইলাহতো শুধুমাত্র আল্লাহই যিনি ছাড়া অন্য কোন ইলাহ নেই, তাঁর জ্ঞান সর্ব বিষয়ে ব্যাপ্ত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2447,20,99,'পূর্বে যা ঘটেছে উহার সংবাদ আমি এভাবে তোমার নিকট বিবৃত করি এবং আমি আমার নিকট হতে তোমাকে দান করেছি উপদেশ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2448,20,100,'এটা হতে যে বিমুখ হবে সে কিয়ামাত দিবসে মহাভার বহন করবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2449,20,101,'তাতে তারা স্থায়ী হবে এবং কিয়ামাত দিবসে এই বোঝা তাদের জন্য কত মন্দ!');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2450,20,102,'যেদিন শিঙ্গায় ফুৎকার দেয়া হবে সেই দিন আমি অপরাধীদেরকে দৃষ্টিহীন অবস্থায় সমবেত করব।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2451,20,103,'তারা নিজেদের মধ্যে চুপি চুপি বলাবলি করবেঃ তোমরা মাত্র দশ দিন অবস্থান করেছিলে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2452,20,104,'তারা কি বলবে তা আমি ভাল জানি। তাদের মধ্যে যে অপেক্ষাকৃত সৎ পথে ছিল সে বলবেঃ তোমরা এক দিনের বেশি অবস্থান করনি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2453,20,105,'তারা তোমাকে পর্বতসমূহ সম্পর্কে জিজ্ঞেস করছে, তুমি বলঃ আমার রাব্ব ওগুলিকে সমূলে উৎপাটন করে বিক্ষিপ্ত করে দিবেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2454,20,106,'অতঃপর তিনি ওকে পরিণত করবেন মসৃণ সমতল মাইদানে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2455,20,107,'যাতে তুমি বক্রতা ও উচ্চতা দেখবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2456,20,108,'সেদিন তারা আহবানকারীর অনুসরণ করবে, এ ব্যাপারে এদিক ওদিক করতে পারবেনা; দয়াময়ের সামনে সব শব্দ স্তদ্ধ হয়ে যাবে; সুতরাং মৃদু পদধ্বনি ছাড়া তুমি কিছুই শুনতে পাবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2457,20,109,'দয়াময় যাকে অনুমতি দিবেন ও যার কথা তিনি পছন্দ করবেন সে ব্যতীত কারও সুপারিশ সেদিন কোন কাজে আসবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2458,20,110,'তাদের সম্মুখে ও পশ্চাতে যা কিছু রয়েছে তা তিনি অবগত, কিন্তু তারা জ্ঞান দ্বারা তাঁকে আয়ত্ত করতে পারে না।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2459,20,111,'স্বাধীন, স্বধিষ্ঠ- পালনকর্তার নিকট সকলেই হবে অধোবদন এবং সে’ই ব্যর্থ হবে যে যুলমের ভার বহন করবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2460,20,112,'এবং যে সৎ কাজ করে মু’মিন হয়, তার আশংকা নেই অবিচারের এবং ক্ষতিরও।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2461,20,113,'এ রূপেই আমি কুরআনকে অবতীর্ণ করেছি আরাবী ভাষায় এবং তাতে বিশদভাবে বিবৃত করেছি সতর্কবাণী যাতে তারা ভয় করে অথবা এটা হয় তাদের জন্য উপদেশ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2462,20,114,'আল্লাহ অতি মহান, প্রকৃত অধিপতি। তোমার প্রতি কুরআনের আয়াত সম্পূর্ণ হবার পূর্বে তুমি ত্বরা করনা এবং বলঃ হে আমার রাব্ব! আমার জ্ঞানকে বৃদ্ধি করুন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2463,20,115,'আমিতো ইতোপূর্বে আদমের প্রতি নির্দেশ দান করেছিলাম, কিন্তু সে ভুলে গিয়েছিল; আমি তাকে সংকল্পে দৃঢ় পাইনি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2464,20,116,'স্মরণ কর, যখন আমি মালাইকাগণকে বললামঃ আদমের প্রতি সাজদাহবনত হও, তখন ইবলীস ছাড়া সবাই সাজদাহ করল; সে অমান্য করল। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2465,20,117,'অতঃপর আমি বললামঃ হে আদম! এ তোমার ও তোমার স্ত্রীর শত্রু; সুতরাং সে যেন কিছুতেই তোমাদেরকে জান্নাত হতে বের করে না দেয়, দিলে তোমরা দুঃখ পাবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2466,20,118,'তোমার জন্য এটাই রইল যে, তুমি জান্নাতে ক্ষুধার্ত হবেনা এবং নগ্নও হবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2467,20,119,'এবং তুমি সেখানে পিপাসার্ত হবেনা এবং রৌদ্র ক্লিষ্টও হবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2468,20,120,'অতঃপর শাইতান তাকে কুমন্ত্রণা দিল; সে বললঃ হে আদম! আমি কি তোমাকে বলে দিব অনন্ত জীবনপ্রদ বৃক্ষের কথা ও অক্ষয় রাজ্যের কথা?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2469,20,121,'অতঃপর তারা তা হতে আহার করল; তখন তাদের লজ্জাস্থান তাদের নিকট প্রকাশ হয়ে পড়ল এবং তারা জান্নাতের বৃক্ষপত্র দ্বারা নিজেদেরকে আবৃত করতে লাগল; আদম তার রবের হুকুম অমান্য করল, ফলে সে ভ্রমে পতিত হল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2470,20,122,'এরপর তার রাব্ব তাকে মনোনীত করলেন, তার প্রতি ক্ষমা পরায়ণ হলেন এবং তাকে পথ নির্দেশ করলেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2471,20,123,'তিনি বললেনঃ তোমরা উভয়ে একই সঙ্গে জান্নাত হতে নেমে যাও, তোমরা পরস্পর পরস্পরের শত্রু। পরে আমার পক্ষ হতে তোমাদের নিকট সৎ পথের নির্দেশ এলে, যে আমার পথ অনুসরণ করবে সে বিপথগামী হবেনা ও দুঃখ কষ্ট পাবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2472,20,124,'যে আমার স্মরণে বিমুখ তার জীবন যাপন হবে সংকুচিত এবং আমি তাকে কিয়ামাত দিবসে উত্থিত করব অন্ধ অবস্থায়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2473,20,125,'সে বলবেঃ হে আমার রাব্ব! কেন আমাকে অন্ধ অবস্থায় উত্থিত করলেন? আমিতো ছিলাম চক্ষুস্মান!');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2474,20,126,'তিনি বলবেনঃ এ রূপেই আমার নিদর্শনাবলী তোমার নিকট এসেছিল, কিন্তু তুমিতো ভুলে গিয়েছিলে এবং সেভাবে আজ তুমিও বিস্মৃত হলে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2475,20,127,'এবং এভাবেই আমি প্রতিফল দিই তাকে, যে বাড়াবাড়ি করে এবং তার রবের নিদর্শনে বিশ্বাস স্থাপন করেনা; পরকালের শাস্তিতো অবশ্যই কঠিনতর ও অধিক স্থায়ী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2476,20,128,'এটাও কি তাদেরকে সৎ পথ দেখালনা যে, আমি তাদের পূর্বে ধ্বংস করেছি কত মানবগোষ্ঠী যাদের বাসভূমিতে তারা বিচরণ করে? অবশ্যই এতে বিবেক সম্পন্নদের জন্য রয়েছে নিদর্শন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2477,20,129,'তোমার রবের পূর্ব সিদ্ধান্ত এবং এক নির্ধারিত সময় না থাকলে অবশ্যম্ভাবী হত ত্বরিত শাস্তি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2478,20,130,'সুতরাং তারা যা বলে সে বিষয়ে তুমি ধৈর্য ধারণ কর এবং সূর্যোদয়ের পূর্বে এবং সূর্যাস্তের পূর্বে তোমার রবের সপ্রশংস পবিত্রতা ও মহিমা ঘোষণা কর, এবং রাতে পবিত্রতা ও মহিমা ঘোষণা কর, আর দিনের প্রান্তসমূহে যাতে তুমি সন্তষ্ট হতে পার।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2479,20,131,'তুমি তোমার চক্ষুদ্বয় কখনও প্রসারিত করনা ওর প্রতি যা আমি তাদের বিভিন্ন শ্রেণীকে পার্থিব জীবনের সৌন্দর্য স্বরূপ উপভোগের উপকরণ হিসাবে দিয়েছি তদ্বারা তাদেরকে পরীক্ষা করার জন্য। তোমার রাব্ব প্রদত্ত জীবনোপকরণ উৎকৃষ্ট ও অধিক স্থায়ী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2480,20,132,'আর তোমার পরিবারবর্গকে সালাতের আদেশ দাও এবং তাতে অবিচল থাক। আমি তোমার নিকট কোন জীবনোপকরণ চাইনা, আমিই তোমাকে জীবনোপকরণ দিই এবং শুভ পরিণামতো মুত্তাকীদের জন্য।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2481,20,133,'তারা বলেঃ সে তার রবের নিকট হতে আমাদের জন্য কোন নিদর্শন কেন আনয়ন করেনা? তাদের নিকট কি আসেনি সুস্পষ্ট প্রমাণ যা আছে পূর্ববর্তী গ্রন্থসমূহে?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2482,20,134,'যদি আমি তাদেরকে ইতোপূর্বে শাস্তি দ্বারা ধ্বংস করতাম তাহলে তারা বলতঃ হে আমাদের রাব্ব! আপনি আমাদের নিকট একজন রাসূল প্রেরণ করলেন না কেন? তা হলে আমরা লাঞ্ছিত ও অপমানিত হবার পূর্বে আপনার নিদর্শন মেনে চলতাম।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2483,20,135,'বলঃ প্রত্যেকেই প্রতীক্ষা করছে, সুতরাং তোমরাও প্রতীক্ষা কর, অতঃপর তোমরা জানতে পারবে কারা রয়েছে সরল পথে এবং কারা সৎ পথ প্রাপ্ত হয়েছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2484,21,1,'মানুষের হিসাব নিকাশের সময় আসন্ন, কিন্তু তারা উদাসীনতায় মুখ ফিরিয়ে রয়েছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2485,21,2,'যখনই তাদের নিকট তাদের রবের কোন নতুন উপদেশ আসে তখন তারা তা শ্রবণ করে কৌতুকচ্ছলে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2486,21,3,'তাদের অন্তর থাকে অমনোযোগী, সীমা লংঘনকারীরা গোপনে পরামর্শ করেঃ এতো তোমাদের মতই একজন মানুষ, তবুও কি তোমরা দেখে শুনে যাদুর কবলে পড়বে?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2487,21,4,'বলঃ আকাশমন্ডলী ও পৃথিবীর সমস্ত কথাই আমার রাব্ব অবগত আছেন এবং তিনিই সর্বশ্রোতা, সর্বজ্ঞ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2488,21,5,'তারা এটাও বলেঃ এ সব অলীক কল্পনা হয় সে উদ্ভাবন করেছে, না হয় সে একজন কবি; অতএব সে আনয়ন করুক আমাদের নিকট এক নিদর্শন যেরূপ নিদর্শনসহ প্রেরিত হয়েছিল পূর্ববর্তীগণ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2489,21,6,'তাদের পূর্বে যে সব জনপদ আমি ধ্বংস করেছি ওর অধিবাসীরা ঈমান আনেনি; তাহলে কি তারা ঈমান আনবে?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2490,21,7,'তোমার পূর্বে আমি অহীসহ মানুষই পাঠিয়েছিলাম; তোমরা যদি না জান তাহলে জ্ঞানীদেরকে জিজ্ঞেস কর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2491,21,8,'আর আমি তাদেরকে এমন দেহ বিশিষ্ট করিনি যে, তারা আহার্য গ্রহণ করতনা। তারা চিরস্থায়ীও ছিলনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2492,21,9,'অতঃপর আমি তাদের প্রতি আমার প্রতিশ্রুতি পূর্ণ করলাম, আমি তাদেরকে ও যাদেরকে ইচ্ছা রক্ষা করেছিলাম এবং যালিমদেরকে করেছিলাম ধ্বংস।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2493,21,10,'আমিতো তোমাদের প্রতি অবতীর্ণ করেছি কিতাব যাতে আছে তোমাদের জন্য উপদেশ, তবুও কি তোমরা বুঝবেনা?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2494,21,11,'আমি ধ্বংস করেছি কত জনপদ যার অধিবাসীরা ছিল যালিম এবং তাদের পরে সৃষ্টি করেছি অপর জাতি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2495,21,12,'অতঃপর যখন তারা আমার শাস্তি প্রত্যক্ষ করল তখনই তারা জনপদ হতে পালাতে লাগল। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2496,21,13,'তাদেরকে বলা হলঃ পলায়ন করনা এবং ফিরে এসো তোমাদের ভোগ সম্ভারের নিকট এবং তোমাদের আবাসগৃহে, হয়তো এ বিষয়ে তোমাদেরকে জিজ্ঞেস করা হতে পারে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2497,21,14,'তারা বললঃ হায় দুর্ভোগ আমাদের! আমরাতো ছিলাম যালিম।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2498,21,15,'তাদের এই আর্তনাদ চলতে থাকে যতক্ষণ না আমি তাদেরকে কর্তিত শস্য ও নির্বাপিত আগুন সদৃশ করি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2499,21,16,'আকাশ ও পৃথিবী এবং যা এতদুভয়ের মধ্যে রয়েছে তা আমি ক্রীড়াচ্ছলে সৃষ্টি করিনি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2500,21,17,'আমি যদি ক্রীড়ার উপকরণ চাইতাম তাহলে আমি আমার নিকট যা আছে তা দিয়েই ওটা করতাম, আমি তা করিনি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2501,21,18,'কিন্তু আমি সত্য দ্বারা আঘাত হানি মিথ্যার উপর; ফলে ওটা মিথ্যাকে চূর্ণ-বিচূর্ণ করে দেয় এবং তৎক্ষণাৎ মিথ্যা নিশ্চিহ্ন হয়ে যায়। দুর্ভোগ তোমাদের! তোমরা যা বলছ তার জন্য।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2502,21,19,'আকাশমন্ডলী ও পৃথিবীতে যা আছে তা তাঁরই, তাঁর সান্নিধ্যে যারা আছে তারা অহংকার করে তাঁর ইবাদাত করা হতে বিমুখ হয়না এবং ক্লান্তিও বোধ করেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2503,21,20,'তারা দিন-রাত তাঁর পবিত্রতা ও মহিমা ঘোষণা করে, শৈথিল্য করেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2504,21,21,'তারা মাটি হতে তৈরী যে সব দেবতা গ্রহণ করেছে সেগুলি কি মৃতকে জীবিত করতে সক্ষম?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2505,21,22,'আকাশমন্ডলী ও পৃথিবীতে যদি আল্লাহ ছাড়া বহু মা‘বূদ থাকত তাহলে উভয়ই ধ্বংস হয়ে যেত। অতএব তারা যা বলে তা হতে আরশের রাব্ব (অধিপতি) আল্লাহ পবিত্র, মহান। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2506,21,23,'তিনি যা করেন সেই বিষয়ে তাঁকে প্রশ্ন করা যাবেনা; বরং তাদেরকেই প্রশ্ন করা হবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2507,21,24,'তারা তাঁকে ছাড়া বহু মা‘বূদ গ্রহণ করেছে? বলঃ তোমরা তোমাদের প্রমাণ উপস্থিত কর। এটাই আমার সাথে যারা আছে তাদের জন্য উপদেশ এবং এটাই উপদেশ ছিল আমার পূর্ববর্তীদের জন্য। কিন্তু তাদের অধিকাংশই প্রকৃত সত্য জানেনা, ফলে তারা মুখ ফিরিয়ে নেয়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2508,21,25,'আমি তোমার পূর্বে এমন কোন রাসূল প্রেরণ করিনি তার প্রতি ‘লা ইলাহা ইল্লাল্লাহ’ এই অহী ব্যতীত। সুতরাং তোমরা আমারই ইবাদাত কর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2509,21,26,'তারা বলেঃ দয়াময় আল্লাহ সন্তান গ্রহণ করেছেন। তিনি পবিত্র মহান! তারাতো তাঁর সম্মানিত বান্দা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2510,21,27,'তারা তাঁর আগে বেড়ে কথা বলে না; তারা তো তাঁর আদেশ অনুসারেই কাজ করে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2511,21,28,'তাদের সম্মুখে ও পশ্চাতে যা কিছু আছে তা তিনি অবগত। তারা সুপারিশ করে শুধু তাদের জন্য যাদের প্রতি তিনি সন্তষ্ট এবং তারা তাঁর ভয়ে ভীত সন্ত্রস্ত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2512,21,29,'তাদের মধ্যে যে বলবেঃ তিনি ব্যতীত আমিই মা‘বূদ, তাকে আমি প্রতিফল দিব জাহান্নাম, এভাবেই আমি যালিমদেরকে প্রতিফল দিয়ে থাকি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2513,21,30,'যারা কুফরী করে তারা কি ভেবে দেখেনা যে, আকাশমন্ডলী ও পৃথিবী মিশে ছিল ওতপ্রোতভাবে; অতঃপর আমি উভয়কে পৃথক করে দিলাম এবং প্রাণবান সমস্ত কিছু সৃষ্টি করলাম পানি হতে; তবুও কি তারা বিশ্বাস করবেনা?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2514,21,31,'এবং আমি পৃথিবীতে সৃষ্টি করেছি সুদৃঢ় পর্বত যাতে পৃথিবী তাদেরকে নিয়ে এদিক-ওদিক টলে না যায় এবং আমি তাতে করে দিয়েছি প্রশস্ত পথ যাতে তারা গন্তব্য স্থলে পৌঁছতে পারে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2515,21,32,'এবং আকাশকে করেছি সুরক্ষিত ছাদ। কিন্তু তারা আকাশস্থিত নিদর্শনাবলী হতে মুখ ফিরিয়ে নেয়। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2516,21,33,'(আল্লাহই) সৃষ্টি করেছেন রাত ও দিন এবং সূর্য ও চাঁদ; প্রত্যেকেই নিজ নিজ কক্ষপথে বিচরণ করে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2517,21,34,'আমি তোমার পূর্বেও কোন মানুষকে চিরস্থায়ী জীবন দান করিনি; সুতরাং তোমার মৃত্যু হলে তারা কি চিরঞ্জীব হয়ে থাকবে?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2518,21,35,'জীব মাত্রই মৃত্যুর স্বাদ গ্রহণ করবে; আমি তোমাদেরকে মন্দ ও ভাল দ্বারা বিশেষভাবে পরীক্ষা করি এবং আমারই নিকট তোমরা প্রত্যাবর্তিত হবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2519,21,36,'কাফিরেরা যখন তোমাকে দেখে তখন তারা তোমাকে শুধু বিদ্রুপের পাত্র রূপেই গ্রহণ করে। তারা বলেঃ এই কি সে যে তোমাদের দেবতাগুলির সমালোচনা করে? অথচ তারাইতো ‘রাহমান’ এর উল্লেখের বিরোধিতা করে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2520,21,37,'মানুষ সৃষ্টিগতভাবে ত্বরা প্রবণ, শীঘ্রই আমি তোমাদেরকে আমার নিদর্শনাবলী দেখাব; সুতরাং তোমরা আমাকে ত্বরা করতে বলনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2521,21,38,'আর তারা বলেঃ তোমরা যদি সত্যবাদী হও তাহলে বল, এই প্রতিশ্রুতি কখন পূর্ণ হবে?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2522,21,39,'হায়! যদি কাফিরেরা সেই সময়ের কথা জানতো যখন তারা তাদের সম্মুখ ও পশ্চাত হতে আগুন প্রতিরোধ করতে পারবেনা এবং তাদেরকে সাহায্য করাও হবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2523,21,40,'বস্তুতঃ ওটা তাদের উপর আসবে অতর্কিতে এবং তাদেরকে হতভম্ব করে দিবে; ফলে তারা ওটা রোধ করতে পারবেনা এবং তাদেরকে অবকাশ দেয়া হবেনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2524,21,41,'তোমার পূর্বেও অনেক রাসূলকেই ঠাট্টা বিদ্রুপ করা হয়েছিল; পরিণামে তারা যা নিয়ে ঠাট্টা বিদ্রুপ করত তা বিদ্রুপকারীদেরকে পরিবেষ্টন করেছিল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2525,21,42,'বলঃ ‘রাহমান’ এর পরিবর্তে কে তোমাদেরকে রক্ষা করছে রাতে ও দিনে? তবুও তারা তাদের রবের স্মরণ হতে মুখ ফিরিয়ে নেয়। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2526,21,43,'তাহলে কি আমি ব্যতীত তাদের এমন আরাধ্য আছে যারা তাদেরকে রক্ষা করতে পারে? তারাতো নিজেদেরকেই সাহায্য করতে পারেনা এবং আমার বিরুদ্ধে তাদের সাহায্যকারীও থাকবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2527,21,44,'বস্তুতঃ আমিই তাদেরকে এবং তাদের পিতৃ-পুরুষদেরকে ভোগ সম্ভার দিয়েছিলাম; অধিকন্ত তাদের আয়ুস্কালও হয়েছিল দীর্ঘ; তারা কি দেখছেনা যে, আমি তাদের দেশকে চতুর্দিক হতে সংকুচিত করে আনছি; তবুও কি তারা বিজয়ী হবে? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2528,21,45,'বলঃ আমিতো শুধু অহী দ্বারাই তোমাদেরকে সতর্ক করি, কিন্তু যারা বধির তাদেরকে যখন সতর্ক করা হয় তখন তারা সতর্কবাণী শোনেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2529,21,46,'তোমার রবের শাস্তির কিছুমাত্রও তাদেরকে স্পর্শ করলে তারা নিশ্চয়ই বলে উঠবেঃ হায়! দুর্ভোগ আমাদের, আমরাতো ছিলাম যালিম!');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2530,21,47,'এবং কিয়ামাত দিবসে আমি স্থাপন করব ন্যায়বিচারের মানদন্ড। সুতরাং কারও প্রতি কোন অবিচার করা হবেনা এবং কাজ যদি সরিষার দানা পরিমাণ ওজনেরও হয় তাও আমি উপস্থিত করব। হিসাব গ্রহণকারীরূপে আমিই যথেষ্ট।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2531,21,48,'আমিতো মূসা ও হারূনকে দিয়েছিলাম মীমাংসাকারী গ্রন্থ, জ্যোতি এবং মুত্তাকীদের জন্য উপদেশ –');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2532,21,49,'যারা না দেখেও তাদের রাব্বকে ভয় করে এবং কিয়ামাত সম্পর্কে থাকে ভীত-সন্ত্রস্ত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2533,21,50,'এটি কল্যাণময় উপদেশ; আমিই এটি অবতীর্ণ করেছি; তবুও কি তোমরা এটাকে অস্বীকার করবে?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2534,21,51,'আমি এর পূর্বে ইবরাহীমকে সৎ পথের জ্ঞান দিয়েছিলাম এবং আমি তার সম্বন্ধে ছিলাম সম্যক অবগত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2535,21,52,'যখন সে তার পিতা ও তার সম্প্রদায়কে বললঃ এই মূর্তিগুলি কি, যাদের পূজায় তোমরা রত রয়েছ?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2536,21,53,'তারা বললঃ আমরা আমাদের পিতৃপুরুষদেরকে এদের পূজা করতে দেখেছি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2537,21,54,'সে বললঃ তোমরা নিজেরা এবং তোমাদের পূর্ব-পুরুষরাও রয়েছ স্পষ্ট বিভ্রান্তিতে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2538,21,55,'তারা বললঃ তুমি কি আমাদের নিকট সত্য এনেছ, নাকি তুমি কৌতুক করছ?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2539,21,56,'সে বললঃ না, তোমাদের রাব্বতো আকাশমন্ডলী ও পৃথিবীর রাব্ব, যিনি ওগুলি সৃষ্টি করেছেন এবং এই বিষয়ে আমি অন্যতম সাক্ষী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2540,21,57,'শপথ আল্লাহর! তোমরা চলে গেলে আমি তোমাদের মূর্তিগুলি সম্বন্ধে অবশ্যই ব্যবস্থা অবলম্বন করব।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2541,21,58,'অতঃপর সে চূর্ণ-বিচূর্ণ করে দিল মূর্তিগুলিকে, তাদের বড় (প্রধান) মূর্তিটি ব্যতীত, যাতে তারা তার দিকে ফিরে আসে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2542,21,59,'তারা বললঃ আমাদের উপাস্যগুলির প্রতি এরূপ করল কে? সে নিশ্চয়ই সীমালংঘনকারী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2543,21,60,'কেহ কেহ বললঃ আমরা এক যুবককে ওদের সমালোচনা করতে শুনেছি, তাকে বলা হয় ইবরাহীম। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2544,21,61,'তারা বললঃ তাকে উপস্থিত কর লোক সম্মুখে, যাতে তারা সাক্ষ্য দিতে পারে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2545,21,62,'তারা বললঃ ইবরাহীম! তুমিই কি আমাদের উপাস্যগুলির উপর এরূপ করেছ?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2546,21,63,'সে বললঃ সে’ই তো এটা করেছে, এইতো এদের প্রধান। এদেরকে জিজ্ঞেস কর যদি এরা কথা বলতে পারে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2547,21,64,'তখন তারা মনে মনে চিন্তা করে দেখল এবং একে অপরকে বলতে লাগলঃ তোমরাইতো সীমালংঘনকারী। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2548,21,65,'অতঃপর তাদের মাথা নত হয়ে গেল এবং তারা বললঃ তুমিতো জানই যে, এরা কথা বলেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2549,21,66,'সে বললঃ তাহলে কি তোমরা আল্লাহর পরিবর্তে এমন কিছুর ইবাদাত কর যারা তোমাদের কোন উপকার করতে পারেনা, ক্ষতিও করতে পারেনা?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2550,21,67,'ধিক তোমাদেরকে এবং আল্লাহর পরিবর্তে তোমরা যাদের ইবাদাত কর তাদেরকে! তোমরা কি বুঝবেনা?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2551,21,68,'তারা বললঃ তাকে পুড়িয়ে দাও এবং সাহায্য কর তোমাদের দেবতাগুলিকে, যদি তোমরা কিছু করতে চাও।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2552,21,69,'আমি বললামঃ হে আগুন! তুমি ইবরাহীমের জন্য শীতল ও নিরাপদ হয়ে যাও।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2553,21,70,'তারা তার ক্ষতি সাধনের ইচ্ছা করেছিল। কিন্তু আমি তাদেরকে করে দিলাম সর্বাধিক ক্ষতিগ্রস্ত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2554,21,71,'আর আমি তাকে ও লূতকে উদ্ধার করে নিয়ে গেলাম সেই দেশে যেথায় আমি কল্যাণ রেখেছি বিশ্ববাসীর জন্য।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2555,21,72,'এবং আমি ইবরাহীমকে দান করেছিলাম ইসহাক এবং পুরস্কার স্বরূপ ইয়াকূব; এবং প্রত্যেককেই করেছিলাম সৎকর্মপরায়ণ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2556,21,73,'আর আমি তাদেরকে করেছিলাম নেতা; তারা আমার নির্দেশ অনুসারে মানুষকে পথ প্রদর্শন করত। তাদের কাছে আমি অহী প্রেরণ করেছিলাম সৎ কাজ করতে, সালাত কায়েম করতে এবং যাকাত প্রদান করতে; তারা আমারই ইবাদাত করত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2557,21,74,'এবং লূতকে দিয়েছিলাম প্রজ্ঞা ও জ্ঞান, আর তাকে উদ্ধার করেছিলাম এমন এক জনপদ হতে যার অধিবাসীরা লিপ্ত ছিল অশ্লীল কাজে। তারা ছিল এক মন্দ সম্প্রদায়; সত্যত্যাগী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2558,21,75,'এবং তাকে আমি আমার অনুগ্রহভাজন করেছিলাম; সে ছিল সৎকর্মপরায়ণদের অন্তর্ভুক্ত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2559,21,76,'স্মরণ কর নূহকে; পূর্বে সে যখন আহবান করেছিল তখন আমি সাড়া দিয়েছিলাম তার আহবানে এবং তাকে ও তার পরিজনবর্গকে মহাসংকট হতে উদ্ধার করেছিলাম। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2560,21,77,'এবং আমি তাকে সাহায্য করেছিলাম সেই সম্প্রদায়ের বিরুদ্ধে যারা আমার নিদর্শনাবলী অস্বীকার করেছিল, তারা ছিল এক মন্দ সম্প্রদায়; এ জন্য তাদের সবাইকে আমি নিমজ্জিত করেছিলাম।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2561,21,78,'এবং স্মরণ কর দাউদ ও সুলাইমানের কথা, যখন তারা বিচার করছিল শস্যক্ষেত্র সম্পর্কে; তাতে রাতে প্রবেশ করেছিল কোন সম্প্রদায়ের মেষ; আমি প্রত্যক্ষ করছিলাম তাদের বিচার।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2562,21,79,'এবং আমি সুলাইমানকে এ বিষয়ের মীমাংসা বুঝিয়ে দিয়েছিলাম এবং তাদের প্রত্যেককে আমি দিয়েছিলাম প্রজ্ঞা ও জ্ঞান। আমি পর্বত ও বিহঙ্গকূলের জন্য নিয়ম করে দিয়েছিলাম যেন ওরা দাউদের সাথে আমার পবিত্রতা ও মহিমা ঘোষণা করে; এ সমস্ত আমিই করেছিলাম।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2563,21,80,'আমি তাকে তোমাদের জন্য বর্ম নির্মাণ শিক্ষা দিয়েছিলাম যাতে ওটা তোমাদের যুদ্ধে তোমাদেরকে রক্ষা করে; সুতরাং তোমরা কি কৃতজ্ঞ হবেনা?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2564,21,81,'এবং সুলাইমানের বশীভূত করে দিয়েছিলাম উদ্দাম বায়ুকে; ওটা তার আদেশক্রমে প্রবাহিত হত সেই দেশের দিকে যেখানে আমি কল্যাণ রেখেছি। প্রত্যেক বিষয় সম্পর্কে আমিই সম্যক অবগত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2565,21,82,'এবং শাইতানদের মধ্যে কতক তার জন্য ডুবুরীর কাজ করত, এটা ছাড়া অন্য কাজও করত; আমি তাদের দিকে সতর্ক দৃষ্টি রাখতাম।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2566,21,83,'আর স্মরণ কর আইয়ুবের কথা, যখন সে তার রাব্বকে আহবান করে বলেছিলঃ আমি দুঃখ কষ্টে পতিত হয়েছি, আপনিতো দয়ালুদের মধ্যে সর্বশ্রেষ্ঠ দয়ালু।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2567,21,84,'তখন আমি তার ডাকে সাড়া দিলাম, তার দুঃখ কষ্ট দূরীভূত করে দিলাম, তাকে তার পরিবার পরিজন ফিরিয়ে দিয়েছিলাম, তাদের সাথে তাদের মত আরও দিয়েছিলাম আমার বিশেষ রাহমাত রূপে এবং ইবাদাতকারীদের জন্য উপদেশ স্বরূপ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2568,21,85,'আর স্মরণ কর ইসমাঈল, ইদরীস এবং যুলকিফল- এর কথা, তাদের প্রত্যেকেই ছিল ধৈর্যশীল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2569,21,86,'এবং তাদেরকে আমি আমার অনুগ্রহ ভাজন করেছিলাম, তারা ছিল সৎকর্মপরায়ণ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2570,21,87,'আর স্মরণ কর যুন-নূন এর কথা, যখন সে ক্রোধভরে বের হয়ে গিয়েছিল এবং মনে করেছিল আমি তার জন্য শাস্তি নির্ধারণ করবনা; অতঃপর সে অন্ধকার হতে আহবান করেছিলঃ আপনি ছাড়া কোন মা‘বূদ নেই; আপনি পবিত্র, মহান; আমিতো সীমালংঘনকারী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2571,21,88,'তখন আমি তার ডাকে সাড়া দিয়েছিলাম এবং তাকে উদ্ধার করেছিলাম দুশ্চিন্তা হতে এবং এভাবেই আমি মু''মিনদেরকে উদ্ধার করে থাকি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2572,21,89,'এবং স্মরণ কর যাকারিয়ার কথা, যখন সে তার রাব্বকে আহবান করে বলেছিলঃ হে আমার রাব্ব! আমাকে একা ছেড়ে দিবেননা, আপনি চূড়ান্ত মালিকানার অধিকারী');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2573,21,90,'অতঃপর আমি তার আহবানে সাড়া দিয়েছিলাম এবং তাকে দান করেছিলাম ইয়াহইয়াকে, আর তার জন্য তার স্ত্রীকে যোগ্যতা সম্পন্ন করেছিলাম; তারা সৎকর্মে প্রতিযোগিতা করত, তারা আমাকে ডাকত আশা ও ভীতির সাথে এবং তারা ছিল আমার নিকট বিনীত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2574,21,91,'আর স্মরণ কর সেই নারীকে যে নিজ সতীত্বকে রক্ষা করেছিল এবং আমি তার মধ্যে আত্মা ফুঁকে দিয়েছি, এবং তাকে ও তার পুত্রকে করেছিলাম বিশ্ববাসীর জন্য এক নিদর্শন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2575,21,92,'এই যে তোমাদের জাতি, এটাতো একই জাতি এবং আমিই তোমাদের রাব্ব, অতএব আমার ইবাদাত কর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2576,21,93,'কিন্তু মানুষ নিজেদের কার্যকলাপে পরস্পরের মধ্যে বিভেদ সৃষ্টি করেছে; প্রত্যেকেই প্রত্যাবর্তিত হবে আমার নিকট।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2577,21,94,'সুতরাং যদি কেহ মু’মিন হয়ে সৎ কাজ করে তাহলে তার কাজ প্রচেষ্টা অগ্রাহ্য হবেনা এবং আমিতো তা লিখে রাখি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2578,21,95,'যে সব জনপদকে আমি ধ্বংস করেছি তার অধিবাসীদের ফিরে না আসা অবধারিত – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2579,21,96,'যে পর্যন্ত না ইয়াজুজ ও মা’জুজকে বন্ধনমুক্ত করে দেয়া হবে এবং তারা প্রত্যক উঁচু ভূমি থেকে দ্রুত ছুটে আসবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2580,21,97,'অমোঘ প্রতিশ্রুত কাল আসন্ন হলে অকস্মাৎ কাফিরদের চক্ষু স্থির হয়ে যাবে। তারা বলবেঃ হায় দুর্ভোগ আমাদের! আমরাতো ছিলাম এ বিষয়ে উদাসীন; না, বরং আমরা ছিলাম সীমালংঘনকারী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2581,21,98,'তোমরা এবং আল্লাহর পরিবর্তে তোমরা যাদের ইবাদাত কর সেগুলিতো জাহান্নামের ইন্ধন; তোমরা সবাই তাতে প্রবেশ করবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2582,21,99,'যদি তারা উপাস্য হত তাহলে তারা জাহান্নামে প্রবেশ করত না; তাদের সবাই তাতে স্থায়ী হবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2583,21,100,'সেখানে থাকবে তাদের আর্তনাদ এবং সেখানে তারা কিছুই শুনতে পাবেনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2584,21,101,'যাদের জন্য আমার নিকট থেকে পূর্ব হতে কল্যাণ নির্ধারিত রয়েছে তাদেরকে তা হতে দূরে রাখা হবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2585,21,102,'তারা ওর ক্ষীণতম শব্দও শুনতে পাবেনা এবং সেখানে তারা তাদের মন যা চায় চিরকাল তা ভোগ করবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2586,21,103,'মহাভীতি তাদেরকে বিষাদ ক্লিষ্ট করবেনা এবং মালাইকারা তাদেরকে অভ্যর্থনা করবে এই বলেঃ এই তোমাদের সেই দিন যার প্রতিশ্রুতি তোমাদেরকে দেয়া হয়েছিল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2587,21,104,'সেদিন আমি আকাশকে গুটিয়ে ফেলবো, যেভাবে গুটানো হয় লিখিত দফতর। যেভাবে আমি সৃষ্টির সূচনা করেছিলাম সেভাবে পুনরায় সৃষ্টি করব; প্রতিশ্রুতি পালন আমার কর্তব্য, আমি এটা পালন করবই।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2588,21,105,'আমি উপদেশের পর কিতাবে লিখে দিয়েছি যে, আমার যোগ্যতাসম্পন্ন বান্দারা পৃথিবীর অধিকারী হবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2589,21,106,'এতে রয়েছে বাণী, সেই সম্প্রদায়ের জন্য, যারা ইবাদাত করে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2590,21,107,'আমিতো তোমাকে বিশ্ব জগতের প্রতি শুধু রাহমাত রূপেই প্রেরণ করেছি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2591,21,108,'বলঃ আমার প্রতি অহী হয় যে, তোমাদের মা‘বূদ একই মা‘বূদ; সুতরাং তোমরা হয়ে যাও আত্মসমর্পনকারী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2592,21,109,'যদি তারা মুখ ফিরিয়ে নেয় তাহলে তুমি বলঃ আমি তোমাদেরকে যথাযথভাবে জানিয়ে দিয়েছি এবং তোমাদেরকে যে বিষয়ে প্রতিশ্রুতি দেয়া হয়েছে, আমি জানিনা, তা আসন্ন, না দূরস্থিত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2593,21,110,'তিনি জানেন যা কথায় ব্যক্ত কর এবং যা তোমরা গোপন কর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2594,21,111,'আমি জানিনা, হয়তো এটা তোমাদের জন্য এক পরীক্ষা এবং জীবন উপভোগ কিছু কালের জন্য।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2595,21,112,'রাসূল বলেছিলঃ হে আমার রাব্ব! আপনি ন্যায়ের সাথে ফাইসালা করে দিন, আমাদের রাব্বতো দয়াময়, তোমরা যা বলেছ সে বিষয়ে একমাত্র সহায়স্থল তিনিই।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2596,22,1,'হে মানবমন্ডলী! তোমরা ভয় কর তোমাদের রাব্বকে; (জেনে রেখ) কিয়ামাতের প্রকম্পন এক ভয়ানক ব্যাপার। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2597,22,2,'যেদিন তোমরা তা প্রত্যক্ষ করবে সেদিন প্রত্যেক স্তন্যদাত্রী বিস্মৃত হবে তার দুগ্ধ পোষ্য শিশুকে এবং প্রত্যেক গর্ভবতী তার গর্ভপাত করে ফেলবে; মানুষকে দেখবে মাতাল সদৃশ, যদিও তারা নেশাগ্রস্ত নয়; বস্তুতঃ আল্লাহর শাস্তি কঠিন। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2598,22,3,'মানুষের কতক অজ্ঞতা বশতঃ আল্লাহ সম্বন্ধে বিতন্ডা করে এবং অনুসরণ করে প্রত্যেক বিদ্রোহী শাইতানের।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2599,22,4,'তার সম্বন্ধে এই নিয়ম করে দেয়া হয়েছে যে, যে কেহ তার সাথে বন্ধুত্ব করবে সে তাকে পথভ্রষ্ট করবে এবং তাকে পরিচালিত করবে প্রজ্জ্বলিত অগ্নির দিকে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2600,22,5,'হে মানুষ! পুনরুত্থান সম্বন্ধে যদি তোমরা সন্দিহান হও তাহলে  (জেনে রেখ),  আমি তোমাদেরকে সৃষ্টি করেছি মাটি হতে, তারপর শুক্র হতে, এরপর জমাট বাধা রক্ত থেকে, তারপর পূর্ণাকৃতি অথবা অপূর্ণাকৃতি মাংসপিন্ড হতে; তোমাদের নিকট ব্যক্ত করার জন্য। আমি যা ইচ্ছা করি তা এক নির্দিষ্ট কালের জন্য মাতৃগর্ভে স্থিতি রাখি, তারপর আমি তোমাদেরকে শিশু রূপে বের করি, পরে যাতে তোমরা পরিণত বয়সে উপনীত হও; তোমাদের মধ্যে কারও মৃত্যু ঘটানো হয় এবং তোমাদের মধ্যে কেহকে কেহকে প্রত্যাবৃত্ত করা হয় হীনতম বয়সে, যার ফলে তারা যা কিছু জানত সেআ সম্বন্ধে তারা সজ্ঞান থাকেনা। তুমি ভূমিকে দেখ শুস্ক, অতঃপর তাতে আমি বারি বর্ষণ করলে তা শস্য শ্যামল হয়ে আন্দোলিত ও স্ফীত হয় এবং উদগত করে সর্বপ্রকার নয়নাভিরাম উদ্ভিদ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2601,22,6,'এটা এ জন্য যে, আল্লাহ সত্য এবং তিনিই মৃত্যুকে জীবন দান করেন এবং তিনি সর্ব বিষয়ে শক্তিমান।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2602,22,7,'আর কিয়ামাত অবশ্যম্ভাবী, এতে কোন সন্দেহ নেই এবং কাবরে যারা আছে তাদেরকে আল্লাহ নিশ্চয়ই পুনরুত্থিত করবেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2603,22,8,'মানুষের মধ্যে কেহ কেহ আল্লাহ সম্বন্ধে বিতন্ডা করে, তাদের না আছে জ্ঞান, না আছে পথ নির্দেশক, আর না আছে কোন দীপ্তিমান কিতাব।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2604,22,9,'সে বিতন্ডা করে ঘাড় বাঁকিয়ে, লোকদেরকে আল্লাহর পথ হতে ভ্রষ্ট করার জন্য; তার জন্য রয়েছে লাঞ্ছনা। ইহলোকে এবং কিয়ামাত দিবসে আমি তাকে আস্বাদন করাব দহন যন্ত্রণা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2605,22,10,'(সেদিন তাকে বলা হবে) এটা তোমার কৃতকর্মের ফল। কারণ আল্লাহ বান্দাদের প্রতি অত্যাচার করেন না।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2606,22,11,'মানুষের মধ্যে কেহ কেহ আল্লাহর ইবাদাত করে দ্বিধার সাথে; তার মঙ্গল হলে তাতে তার চিত্ত প্রশস্ত হয় এবং কোন বিপর্যয় ঘটলে সে তার পূর্বাবস্থায় ফিরে যায়। সে ক্ষতিগ্রস্ত হয় দুনিয়ায় ও আখিরাতে; এটাইতো সুস্পষ্ট ক্ষতি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2607,22,12,'সে আল্লাহর পরিবর্তে এমন কিছুকে ডাকে যা তার কোন অপকার করতে পারেনা, উপকারও করতে পারেনা, এটাই চরম বিভ্রান্তি!');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2608,22,13,'সে ডাকে এমন কিছুকে যার ক্ষতিই তার উপকার অপেক্ষা নিকটতর। কত নিকৃষ্ট এই অভিভাবক এবং নিকৃষ্ট এই সহচর! ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2609,22,14,'যারা ঈমান আনে ও সৎ কাজ করে আল্লাহ তাদেরকে দাখিল করবেন জান্নাতে, যার নিম্নদেশে নদী প্রবাহিত; আল্লাহ যা ইচ্ছা তা-ই করেন। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2610,22,15,'যে মনে করে, আল্লাহ কখনই দুনিয়া ও আখিরাতে সাহায্য করবেননা, সে আকাশের দিকে রজ্জু প্রলম্বিত করুক এবং এরপর কেটে দিক; অতঃপর দেখুক তার প্রচেষ্টা তাঁর আক্রোশের হেতু দূর করে কি না!');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2611,22,16,'এভাবেই আমি সুস্পষ্ট নিদর্শন রূপে ওটা অবতীর্ণ করেছি; আর আল্লাহ যাকে ইচ্ছা সৎ পথ প্রদর্শন করেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2612,22,17,'যারা ঈমান এনেছে এবং যারা ইয়াহুদী হয়েছে, যারা সাবিয়ী, খৃষ্টান, অগ্নিপূজক এবং যারা মুশরিক - কিয়ামাত দিবসে আল্লাহ তাদের মধ্যে ফাইসালা করে দিবেন। নিশ্চয়ই আল্লাহ প্রত্যেক জিনিসের উপর সাক্ষী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2613,22,18,'তুমি কি দেখনা যে, আল্লাহকে সাজদাহ করে যা কিছু আছে আকাশমন্ডলীতে ও পৃথিবীতে - সূর্য, চাঁদ, নক্ষত্রমন্ডলী, পবর্তরাজি, বৃক্ষলতা, জীব-জন্তু এবং সাজদাহ করে মানুষের মধ্যে অনেকে? আবার অনেকের প্রতি অবধারিত হয়েছে শাস্তি। আল্লাহ যাকে হেয় করেন তার সম্মানদাতা কেহই নেই; আল্লাহ যা ইচ্ছা তা করেন। (সাজদাহ)।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2614,22,19,'এরা দু’টি বিবাদমান পক্ষ, তারা তাদের রাব্ব সম্বন্ধে বিতর্ক করে। যারা কুফরী করে তাদের জন্য প্রস্তুত করা হয়েছে আগুনের পোশাক; তাদের মাথার উপর ঢেলে দেয়া হবে ফুটন্ত পানি –');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2615,22,20,'যদ্বারা উদরে যা আছে তা এবং তাদের চর্ম বিগলিত করা হবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2616,22,21,'আর তাদের জন্য থাকবে লৌহ-মুগুর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2617,22,22,'যখনই তারা যন্ত্রণাকাতর হয়ে জাহান্নাম হতে বের হতে চাবে তখনই তাদেরকে ফিরিয়ে দেয়া হবে। তাদেরকে বলা হবেঃ স্বাদ গ্রহণ কর দহন যন্ত্রণার।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2618,22,23,'যারা ঈমান আনে ও সৎ কাজ করে, আল্লাহ তাদেরকে দাখিল করবেন জান্নাতে, যার পাদদেশে নদী প্রবাহিত, সেখানে তাদেরকে অলংকৃত করা হবে স্বর্ণ কংকন ও মুক্তা দ্বারা এবং সেখানে তাদের পোশাক পরিচ্ছদ হবে রেশমের। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2619,22,24,'তাদেরকে পবিত্র বাক্যের অনুগামী করা হয়েছিল এবং তারা পরিচালিত হয়েছিল পরম প্রশংসা ভাজন (আল্লাহর) পথে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2620,22,25,'যারা কুফরী করে এবং মানুষকে নিবৃত্ত করে আল্লাহর পথ হতে ও মাসজিদুল হারাম হতে, যা আমি করেছি স্থানীয় ও বহিরাগত সবারই জন্য সমান, আর যে ইচ্ছা করে ওতে পাপ কাজের সীমা লংঘন করে তাকে আমি আস্বাদন করাব মর্মন্তদ শাস্তি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2621,22,26,'আর স্মরণ কর, যখন আমি ইবরাহীমের জন্য নির্ধারণ করে দিয়েছিলাম সেই গৃহের স্থান তখন বলেছিলামঃ আমার সাথে কোন শরীক স্থির করনা এবং আমার গৃহকে পবিত্র রেখ তাদের জন্য যারা তাওয়াফ করে এবং যারা দন্ডায়মান থাকে, রুকু করে ও সাজদাহ করে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2622,22,27,'এবং মানুষের কাছে হাজ্জের ঘোষণা করে দাও, তারা তোমার কাছে আসবে পদব্রজে ও সর্ব প্রকার ক্ষীণকায় উষ্ট্রসমূহের পিঠে, তারা আসবে দূর-দূরান্তের পথ অতিক্রম করে –');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2623,22,28,'যাতে তারা তাদের কল্যাণময় স্থানগুলিতে উপস্থিত হতে পারে এবং তিনি তাদেরকে চতুস্পদ জন্তু হতে যা রিয্ক হিসাবে দান করেছেন ওর উপর নির্দিষ্ট দিনগুলিতে আল্লাহর নাম উচ্চারণ করতে পারে; অতঃপর তোমরা ওটা হতে আহার কর এবং দুস্থ, অভাবগ্রস্তকে আহার করাও। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2624,22,29,'অতঃপর তারা যেন তাদের অপরিচ্ছন্নতা দূর করে এবং তাদের মানত পূর্ণ করে ও তাওয়াফ করে প্রাচীন গৃহের।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2625,22,30,'এটাই বিধান এবং কেহ আল্লাহ কর্তৃক নির্ধারিত বিধানাবলীর প্রতি সম্মান প্রদর্শন করলে তার রবের নিকট তা উত্তম। তোমাদের জন্য হালাল করা হয়েছে চতুস্পদ পশু, ঐগুলি ব্যতীত যা তোমাদেরকে বলা হয়েছে। সুতরাং তোমরা বর্জন কর মূর্তি পূজার অপবিত্রতা এবং দূরে থাক মিথ্যা বলা হতে,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2626,22,31,'আল্লাহর প্রতি একনিষ্ঠ হয়ে এবং তাঁর কোন শরীক না করে। আর যে আল্লাহর শরীক করে সে যেন আকাশ হতে পড়ল, অতঃপর পাখী তাকে ছোঁ মেরে নিয়ে গেল, কিংবা বায়ু তাকে উড়িয়ে নিয়ে দূরবর্তী এক স্থানে নিক্ষেপ করল। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2627,22,32,'এটাই আল্লাহর বিধান এবং কেহ (আল্লাহর) নিদর্শনাবলীকে সম্মান করলে এটাতো তার হৃদয়ের তাকওয়ারই বহিঃপ্রকাশ। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2628,22,33,'এ সবগুলিতে তোমাদের জন্য নানাবিধ উপকার রয়েছে এক নির্দিষ্ট কালের জন্য; অতঃপর ওগুলির কুরবানীর স্থান প্রাচীন গৃহের নিকট।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2629,22,34,'আমি প্রত্যেক সম্প্রদায়ের জন্য কুরবানীর নিয়ম করে দিয়েছি যাতে আমি তাদেরকে জীবনোপকরণ স্বরূপ যে সব চতুস্পদ জন্তু দিয়েছি সেগুলির উপর আল্লাহর নাম উচ্চারণ করে। তোমাদের মা‘বূদ একই মা‘বূদ, সুতরাং তাঁরই নিকট আত্মসমর্পন কর এবং সুসংবাদ দাও বিনীতজনদেরকে,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2630,22,35,'যাদের হৃদয় ভয়-কম্পিত হয় আল্লাহর নাম স্মরণ করা হলে, যারা তাদের বিপদ আপদে ধৈর্য ধারণ করে এবং সালাত কায়েম করে এবং আমি তাদেরকে যে রিয্ক দিয়েছি তা হতে ব্যয় করে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2631,22,36,'এবং উৎসর্গীকৃত উষ্ট্রকে করেছি আল্লাহর নিদর্শনগুলির অন্যতম; তোমাদের জন্য তাতে মঙ্গল রয়েছে; সুতরাং সারিবদ্ধভাবে দন্ডায়মান অবস্থায় ওগুলির উপর তোমরা আল্লাহর নাম নাও। যখন ওরা কাত হয়ে পড়ে যায় তখন তোমরা তা হতে আহার কর এবং আহার করাও ধৈর্যশীল অভাবগ্রস্তকে ও যাঞ্চাকারী অভাবগ্রস্তকে। এভাবে আমি ওদেরকে তোমাদের অধীন করে দিয়েছি যাতে তোমরা কৃতজ্ঞতা প্রকাশ কর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2632,22,37,'আল্লাহর কাছে পৌঁছেনা ওগুলির গোশত এবং রক্ত, বরং পৌঁছে তোমাদের তাকওয়া। এভাবে তিনি ওগুলিকে তোমাদের অধীন করে দিয়েছেন যাতে তোমরা আল্লাহর শ্রেষ্ঠত্ব ঘোষণা কর এ জন্য যে, তিনি তোমাদেরকে পথ প্রদর্শন করেছেন। সুতরাং তুমি সুসংবাদ দাও সৎকর্মশীলদেরকে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2633,22,38,'আল্লাহ রক্ষা করেন মু’মিনদেরকে। নিশ্চয়ই আল্লাহ কোন বিশ্বাসঘাতক, অকৃতজ্ঞকে পছন্দ করেননা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2634,22,39,'যুদ্ধের অনুমতি দেয়া হল তাদেরকে যারা আক্রান্ত হয়েছে। কারণ তাদের প্রতি অত্যাচার করা হয়েছে। আল্লাহ নিশ্চয়ই তাদেরকে সাহায্য করতে সক্ষম।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2635,22,40,'তাদেরকে তাদের ঘর বাড়ী হতে অন্যায়ভাবে বহিস্কৃত করা হয়েছে শুধু এ কারণে যে, তারা বলেঃ আমাদের রাব্ব আল্লাহ! আল্লাহ যদি মানব জাতির এক দলকে অন্য দল দ্বারা প্রতিহত না করতেন তাহলে বিধ্বস্ত হয়ে যেত খৃষ্টান, সংসার বিরাগীদের উপাসনা স্থল, গীর্জা, ইয়াহুদীদের উপাসনালয় এবং মাসজিদসমূহ যাতে অধিক স্মরণ করা হয় আল্লাহর নাম; নিশ্চয়ই আল্লাহ তাকে সাহায্য করেন যে নিজকে সাহায্য করে; নিশ্চয়ই আল্লাহ শক্তিমান, পরাক্রমশালী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2636,22,41,'আমি তাদেরকে পৃথিবীতে প্রতিষ্ঠিত করলে তারা সালাত কায়েম করবে, যাকাত দিবে এবং সৎ কাজের আদেশ করবে ও অসৎ কার্য হতে নিষেধ করবে। সকল কাজের পরিণাম আল্লাহর ইখতিয়ারে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2637,22,42,'এবং লোকে যদি তোমাকে অস্বীকার করে তাহলে তাদের পূর্বেতো অস্বীকার করেছিল নূহের কাওম, ‘আদ ও ছামূদ –');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2638,22,43,'এবং ইবরাহীম ও লূতের কাওম।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2639,22,44,'এবং মাদইয়ানবাসী তাদের নাবীদেরকে অস্বীকার করেছিল; এবং অস্বীকার করা হয়েছিল মূসাকেও; আমি কাফিরদের অবকাশ দিয়েছিলাম এবং পরে তাদেরকে শাস্তি দিয়েছিলাম। অতঃপর কেমন ছিল আমার শাস্তি!');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2640,22,45,'আমি ধ্বংস করেছি কত জনপদ যেগুলির বাসিন্দা ছিল যালিম। এই সব জনপদ তাদের ঘরের ছাদসহ ধ্বংসস্তুপে পরিণত হয়েছিল এবং কত কূপ পরিত্যক্ত হয়েছিল ও কত সুদৃঢ় প্রাসাদও।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2641,22,46,'তারা কি দেশ ভ্রমণ করেনি? তাহলে তারা জ্ঞান বুদ্ধি সম্পন্ন হৃদয় ও শ্রুতিশক্তি সম্পন্ন কর্ণের অধিকারী হতে পারত। বস্তুতঃ চক্ষুতো অন্ধ নয়, বরং অন্ধ হচ্ছে বক্ষস্থিত হৃদয়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2642,22,47,'তারা তোমাকে শাস্তি ত্বরান্বিত করতে বলে, অথচ আল্লাহ তাঁর প্রতিশ্রুতি কখনও ভংগ করেননা। তোমার রবের একদিন তোমাদের গণনায় সহস্র বছরের সমান।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2643,22,48,'এবং আমি অবকাশ দিয়েছি কত জনপদকে যখন তারা ছিল অত্যাচারী। অতঃপর তাদেরকে শাস্তি দিয়েছি এবং প্রত্যাবর্তন আমারই নিকট। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2644,22,49,'বলঃ হে মানুষ! আমিতো তোমাদের জন্য এক স্পষ্ট সতর্ককারী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2645,22,50,'সুতরাং যারা ঈমান আনে এবং সৎ কাজ করে তাদের জন্য আছে ক্ষমা ও সম্মানজনক জীবিকা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2646,22,51,'আর যারা আমার আয়াত ব্যর্থ করার চেষ্টা করে তারাই হবে জাহান্নামের অধিবাসী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2647,22,52,'আমি তোমার পূর্বে যে সব রাসূল কিংবা নাবী প্রেরণ করেছি তাদের কেহ যখনই কিছু আকাংখা করেছে তখনই শাইতান তার আকাংখায় কিছু প্রক্ষিপ্ত করেছে। কিন্তু শাইতান যা প্রক্ষিপ্ত করে আল্লাহ তা বিদূরিত করেন; অতঃপর আল্লাহ তাঁর আয়াতসমূহকে সুপ্রতিষ্ঠিত করেন এবং আল্লাহ সর্বজ্ঞ, প্রজ্ঞাময়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2648,22,53,'এটা এ জন্য যে, শাইতান যা প্রক্ষিপ্ত করে তিনি ওকে পরীক্ষা স্বরূপ করেন তাদের জন্য যাদের অন্তরে ব্যাধি রয়েছে, যাদের হৃদয় পাষাণ। নিশ্চয়ই যালিমরা দুস্তর মতভেদে রয়েছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2649,22,54,'এবং এ জন্য যে, যাদেরকে জ্ঞান দেয়া হয়েছে তারা যেন জানতে পারে যে, এটা (কুরআন) তোমার রবের নিকট হতে প্রেরিত সত্য। অতঃপর তারা যেন তাতে বিশ্বাস স্থাপন করে এবং তাদের অন্তর যেন ওর প্রতি অনুগত হয়। যারা ঈমান এনেছে তাদেরকে আল্লাহ সরল পথে পরিচালিত করেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2650,22,55,'যারা কুফরী করেছে তারা ওতে সন্দেহ পোষণ করা হতে বিরত হবেনা, যতক্ষণ না তাদের নিকট কিয়ামাত এসে পড়বে আকস্মিকভাবে, অথবা এসে পড়বে ঐ দিনের শাস্তি যা থেকে রক্ষার উপায় নেই।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2651,22,56,'সেদিন আল্লাহরই আধিপত্য; তিনিই তাদের বিচার করবেন। সুতরাং যারা ঈমান আনে ও সৎ কাজ করে তারা অবস্থান করবে সুখময় জান্নাতে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2652,22,57,'আর যারা কুফরী করে এবং আমার আয়াতসমূহকে অস্বীকার করে তাদেরই জন্য রয়েছে লাঞ্ছনাদায়ক শাস্তি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2653,22,58,'আর যারা হিজরাত করেছে, আল্লাহর পথে নিহত হয়েছে অথবা মৃত্যু বরণ করেছে, তাদেরকে আল্লাহ অবশ্যই উৎকৃষ্ট জীবিকা দান করবেন; এবং আল্লাহ তিনিইতো সর্বোৎকৃষ্ট রিয্কদাতা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2654,22,59,'তিনি তাদেরকে অবশ্যই এমন স্থানে দাখিল করবেন যা তারা পছন্দ করবে এবং আল্লাহতো সম্যক প্রজ্ঞাময়, পরম সহনশীল। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2655,22,60,'এটাই হয়ে থাকে, কোন ব্যক্তি নিপীড়িত হয়ে সমপরিমান প্রতিশোধ গ্রহণ করলে এবং পুনরায় সে অত্যাচারিত হলে আল্লাহ অবশ্যই তাকে সাহায্য করবেন; আল্লাহ নিশ্চয়ই পাপ মোচনকারী, ক্ষমাশীল। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2656,22,61,'ওটা এ জন্য যে, আল্লাহ রাতকে প্রবিষ্ট করেন দিনের মধ্যে এবং দিনকে প্রবিষ্ট করেন রাতের মধ্যে এবং আল্লাহ সর্বশ্রোতা, সম্যক দ্রষ্টা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2657,22,62,'এ জন্যও যে, আল্লাহ সত্য এবং তারা তাঁর পরিবর্তে যাকে ডাকে ওটাতো অসত্য এবং আল্লাহ তো সমুচ্চ, মহান। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2658,22,63,'তুমি কি লক্ষ্য করনি যে, আল্লাহ বারি বর্ষণ করেন আকাশ হতে যাতে সবুজ শ্যামল হয়ে ওঠে ধরিত্রী? আল্লাহ সম্যক সূক্ষ্মদর্শী, পরিজ্ঞাত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2659,22,64,'আকাশমন্ডলী ও পৃথিবীতে যা কিছু আছে তা তাঁরই এবং আল্লাহই তো অভাবমুক্ত, প্রশংসা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2660,22,65,'তুমি কি লক্ষ্য করনি যে, আল্লাহ তোমাদের কল্যাণে নিয়োজিত করেছেন পৃথিবীতে যা কিছু আছে তৎসমূদয়কে এবং তাঁর নির্দেশে সমুদ্রে বিচরণশীল নৌযানসমূহকে এবং তিনিই আকাশকে স্থির রাখেন যাতে ওটা পতিত না হয় পৃথিবীর উপর তাঁর অনুমতি ছাড়া? আল্লাহ নিশ্চয়ই মানুষের প্রতি দয়ার্দ্র, পরম দয়ালু। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2661,22,66,'এবং তিনিই তোমাদেরকে জীবন দান করেছেন, অতঃপর তিনিই তোমাদের মৃত্যু ঘটাবেন, পুনরায় তোমাদেরকে জীবন দান করবেন। মানুষতো অতি মাত্রায় অকৃতজ্ঞ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2662,22,67,'আমি প্রত্যেক সম্প্রদায়ের জন্য নির্ধারণ করে দিয়েছি ইবাদাত-পদ্ধতি যা তারা অনুসরণ করবে। সুতরাং তারা যেন তোমার সাথে এ ব্যাপারে বিতর্ক না করে। তুমি তাদেরকে তোমার রবের দিকে আহবান কর, তুমিতো সরল পথেই প্রতিষ্ঠিত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2663,22,68,'তারা যদি তোমার সাথে বিতন্ডা করে তাহলে বলঃ তোমরা যা কর সেই সম্বন্ধে আল্লাহ সম্যক অবহিত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2664,22,69,'তোমরা যে বিষয়ে মতভেদ করছ আল্লাহ কিয়ামাত দিবসে সেই বিষয়ে তোমাদের মধ্যে বিচার মীমাংসা করে দিবেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2665,22,70,'তুমি কি জাননা যে, আকাশ ও পৃথিবীতে যা কিছু রয়েছে আল্লাহ তা অবগত আছেন? এ সবই লিপিবদ্ধ আছে এক কিতাবে; এটা আল্লাহর নিকট সহজ। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2666,22,71,'এবং তারা ইবাদাত করে আল্লাহর পরিবর্তে এমন কিছুর যে সম্পর্কে তিনি কোন দলীল প্রেরণ করেননি এবং যার সম্বন্ধে তাদের কোন জ্ঞান নেই। বস্তুতঃ যালিমদের কোন সাহায্যকারী নেই। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2667,22,72,'এবং তাদের নিকট আমার সুস্পষ্ট আয়াতসমূহ আবৃত্তি করা হলে তুমি কাফিরদের মুখমন্ডলে অসন্তোষের লক্ষণ দেখবে। যারা তাদের নিকট আমার আয়াত আবৃত্তি করে তাদেরকে তারা আক্রমণ করতে উদ্যত হয়। তুমি বলঃ তাহলে কি আমি তোমাদেরকে এটা অপেক্ষা অন্য কিছুর সংবাদ দিব? ওটা আগুন। এ বিষয়ে আল্লাহ প্রতিশ্রুতি দিয়েছেন কাফিরদেরকে এবং ওটা কত নিকৃষ্ট প্রত্যাবর্তনস্থল!');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2668,22,73,'হে লোকসকল! একটি উপমা দেয়া হচ্ছে, মনোযোগ সহকারে তা শ্রবণ কর। তোমরা আল্লাহর পরিবর্তে যাদেরকে ডাক তারাতো কখনও একটি মাছিও সৃষ্টি করতে পারবেনা, এ উদ্দেশে তারা সবাই একত্রিত হলেও; এবং মাছি যদি তাদের নিকট হতে কিছু ছিনিয়ে নিয়ে যায় ওটাও তারা ওর নিকট হতে উদ্ধার করতে পারবেনা। পূজারী ও পূজিত কতই না দুর্বল!');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2669,22,74,'তারা আল্লাহর যথোচিত মর্যাদা উপলব্ধি করেনা। আল্লাহ নিশ্চয়ই ক্ষমতাবান, পরাক্রমশালী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2670,22,75,'আল্লাহ মালাইকাদের মধ্য হতে মনোনীত করেন বাণীবাহক এবং মানুষের মধ্য হতেও; আল্লাহ সর্বশ্রোতা, সম্যক দ্রষ্টা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2671,22,76,'তাদের সম্মুখে ও পশ্চাতে যা কিছু আছে তিনি তা জানেন এবং সমস্ত বিষয় আল্লাহর নিকট প্রত্যাবর্তিত হবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2672,22,77,'হে মু’মিনগণ! তোমরা রুকু কর, সাজদাহ কর এবং তোমাদের রবের ইবাদাত কর ও সৎ কাজ কর যাতে সফলকাম হতে পার। [সাজদাহ]');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2673,22,78,'এবং জিহাদ কর আল্লাহর পথে যেভাবে জিহাদ করা উচিত; তিনি তোমাদেরকে মনোনীত করেছেন। তিনি দীনের ব্যাপারে তোমাদের উপর কঠোরতা আরোপ করেননি। এটা তোমাদের পিতা ইবরাহীমের মিল্লাত; তিনি পূর্বে তোমাদের নামকরণ করেছেন মুসলিম এবং এই কিতাবেও, যাতে রাসূল তোমাদের জন্য সাক্ষী স্বরূপ হয় এবং তোমরা স্বাক্ষী হও মানব জাতির জন্য। সুতরাং তোমরা সালাত কায়েম কর, যাকাত দাও এবং আল্লাহকে অবলম্বন কর; তিনিই তোমাদের অভিভাবক, কত উত্তম অভিভাবক এবং কত উত্তম সাহায্যকারী তিনি!');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2674,23,1,'অবশ্যই সফলকাম হয়েছে মু’মিনগণ-');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2675,23,2,'যারা নিজেদের সালাতে বিনয়, নম্র – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2676,23,3,'যারা অসার ক্রিয়া-কলাপ হতে বিরত থাকে – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2677,23,4,'যারা যাকাত প্রদানে সক্রিয় – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2678,23,5,'যারা নিজেদের যৌনাংগকে সংযত রাখে – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2679,23,6,'নিজেদের পত্নী অথবা অধিকারভুক্ত দাসীগণ ব্যতীত, এতে তারা নিন্দনীয় হবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2680,23,7,'সুতরাং কেহ এদেরকে ছাড়া অন্যকে কামনা করলে তারা হবে সীমালংঘনকারী,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2681,23,8,'এবং যারা আমানত ও প্রতিশ্রুতি রক্ষা করে –');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2682,23,9,'আর যারা নিজেদের সালাতে যত্নবান – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2683,23,10,'তারাই হবে উত্তরাধিকারী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2684,23,11,'অধিকারী হবে ফিরদাউসের, যাতে তারা বসবাস করবে চিরকাল। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2685,23,12,'আমিতো মানুষকে সৃষ্টি করেছি মাটির উপাদান হতে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2686,23,13,'অতঃপর আমি ওকে শুক্রবিন্দু রূপে স্থাপন করি এক নিরাপদ আধারে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2687,23,14,'পরে আমি শুক্রবিন্দুকে পরিণত করি রক্তপিন্ডে, অতঃপর রক্তপিন্ডকে পরিণত করি মাংসপিন্ডে এবং মাংসপিন্ডকে পরিণত করি অস্থিপঞ্জরে; অতঃপর অস্থিপঞ্জরকে ঢেকে দিই মাংস দ্বারা; অবশেষে ওকে গড়ে তুলি অন্য এক সৃষ্টি রূপে; অতএব নিপুণতম স্রষ্টা আল্লাহ কত কল্যাণময়!');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2688,23,15,'এরপর অবশ্যই তোমরা মৃত্যু বরণ করবে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2689,23,16,'অতঃপর কিয়ামাত দিবসে তোমাদের পুনরুত্থিত করা হবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2690,23,17,'আমিতো তোমাদের উর্ধ্বে সৃষ্টি করেছি সপ্ত স্তর এবং আমি সৃষ্টি বিষয়ে অসতর্ক নই।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2691,23,18,'এবং আমি আকাশ হতে বারি বর্ষণ করি পরিমিতভাবে, অতঃপর আমি তা মাটিতে সংরক্ষিত করি; আমি ওকে অপসারিত করতেও সক্ষম।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2692,23,19,'অতঃপর আমি ওটা দ্বারা তোমাদের জন্য খেজুর ও আঙ্গুরের বাগান সৃষ্টি করি; এতে তোমাদের জন্য আছে প্রচুর ফল; আর তা হতে তোমরা আহার কর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2693,23,20,'এবং সৃষ্টি করি এক বৃক্ষ যা জন্মে সিনাই পর্বতে, এতে উৎপন্ন হয় ভোজনকারীদের জন্য তেল ও ব্যঞ্জন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2694,23,21,'এবং তোমাদের জন্য অবশ্যই শিক্ষণীয় বিষয় রয়েছে চতুস্পদ জন্তুসমূহের মধ্যে। তোমাদেরকে আমি পান করাই ওদের যা আছে তা থেকে; এবং এতে তোমাদের জন্য রয়েছে প্রচুর উপকারিতা এবং তোমরা উহা হতে আহার কর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2695,23,22,'এবং তোমরা তাতে এবং নৌযানে আরোহণও করে থাক।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2696,23,23,'আমি নূহকে পাঠিয়েছিলাম তার সম্প্রদায়ের নিকট। সে বলেছিলঃ হে আমার সম্প্রদায়! আল্লাহর ইবাদাত কর, তিনি ছাড়া তোমাদের অন্য কোন মা‘বূদ নেই, তবুও কি তোমরা সাবধান হবেনা?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2697,23,24,'তার সম্প্রদায়ের কাফির প্রধানরা বলেছিলঃ এ লোকতো তোমাদের মতই একজন মানুষ, সে তোমাদের উপর শ্রেষ্ঠত্ব লাভ করতে চাচ্ছে, আল্লাহ ইচ্ছা করলে মালাক/ফেরেশতা পাঠাতেন; আমরাতো আমাদের পূর্ব-পুরুষদের যামানায় এরূপ ঘটেছে বলে শুনিনি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2698,23,25,'সেতো এক উম্মাদ ব্যক্তি বৈ নয়; সুতরাং এর সম্পর্কে তোমরা কিছুকাল অপেক্ষা কর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2699,23,26,'নূহ বলেছিলঃ হে আমার রাব্ব! আমাকে সাহায্য করুন। কারণ তারা আমাকে মিথ্যাবাদী বলেছে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2700,23,27,'অতঃপর আমি তার কাছে অহী করলামঃ তুমি আমার তত্ত্বাবধানে ও আমার অহী অনুযায়ী নৌযান নির্মাণ কর, অতঃপর যখন আমার আদেশ আসবে ও উনূন উথলে উঠবে তখন উঠিয়ে নিবে প্রত্যেক জীবের এক এক জোড়া স্ত্রী ও পুরুষ এবং তোমার পরিবার-পরিজনকে, তাদের মধ্যে যাদের বিরুদ্ধে পূর্বে সিদ্ধান্ত হয়েছে তাদের সম্পর্কে তুমি আমাকে কিছু বলনা, তারাতো নিমজ্জিত হবে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2701,23,28,'যখন তুমি ও তোমার সঙ্গীরা নৌযানে আরোহণ করবে তখন বলঃ সমস্ত প্রশংসা ঐ আল্লাহরই যিনি আমাদের উদ্ধার করেছেন যালিম সম্প্রদায় হতে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2702,23,29,'আর বলঃ হে আমার রাব্ব! আমাকে এমনভাবে অবতরণ করিয়ে নিন যা হবে কল্যাণকর; আর আপনিই শ্রেষ্ঠ অবতরণকারী। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2703,23,30,'এতে অবশ্যই নিদর্শন রয়েছে; আমিতো তাদেরকে পরীক্ষা করেছিলাম। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2704,23,31,'অতঃপর তাদের পর আমি অন্য এক সম্প্রদায় সৃষ্টি করেছিলাম। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2705,23,32,'এরপর তাদেরই একজনকে তাদের নিকট রাসূল করে পাঠিয়েছিলাম; সে বলেছিলঃ তোমরা আল্লাহর ইবাদাত কর, তিনি ছাড়া তোমাদের অন্য কোন মা‘বূদ নেই, তবুও কি তোমরা সাবধান হবেনা?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2706,23,33,'তার সম্প্রদায়ের প্রধানরা যারা কুফরী করেছিল ও আখিরাতের সাক্ষাতকে অস্বীকার করেছিল এবং যাদেরকে আমি দিয়েছিলাম পার্থিব জীবনে প্রচুর ভোগ সম্ভার, তারা বলেছিলঃ এতো তোমাদেরই মত একজন মানুষ; তোমরা যা আহার কর সেতো তা’ই আহার করে এবং তোমরা যা পান কর সেও তা’ই পান করে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2707,23,34,'যদি তোমরা তোমাদেরই মত একজন মানুষের আনুগত্য কর তাহলে তোমরা অবশ্যই ক্ষতিগ্রস্ত হবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2708,23,35,'সে কি তোমাদেরকে এই প্রতিশ্রুতিই দেয় যে, তোমাদের মৃত্যু হলে এবং তোমরা মাটি ও অস্থিতে পরিণত হলেও তোমাদেরকে পুনরুত্থিত করা হবে?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2709,23,36,'অসম্ভব, তোমাদেরকে যে বিষয়ে প্রতিশ্রুতি দেয়া হয়েছে তা অসম্ভব!');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2710,23,37,'একমাত্র পার্থিব জীবনই আমাদের জীবন, আমরা মরি বাঁচি এখানেই এবং আমরা পুনরুত্থিত হবনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2711,23,38,'সে তো এমন ব্যক্তি যে আল্লাহ সম্বন্ধে মিথ্যা উদ্ভাবন করেছে এবং আমরা তাকে বিশ্বাস করার নই। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2712,23,39,'সে বললঃ হে আমার রাব্ব! আমাকে সাহায্য করুন; কারণ তারা আমাকে মিথ্যাবাদী বলছে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2713,23,40,'(আল্লাহ) বললেনঃ অচিরেই তারা অনুতপ্ত হবেই। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2714,23,41,'অতঃপর সত্য সত্যই এক বিকট শব্দ তাদেরকে আঘাত করল এবং আমি তাদেরকে তরঙ্গ তাড়িত আবর্জনা সদৃশ করে দিলাম; সুতরাং ধ্বংস হয়ে গেল যালিম সম্প্রদায়। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2715,23,42,'অতঃপর তাদের পরে আমি বহু জাতি সৃষ্টি করেছি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2716,23,43,'কোন জাতিই তার নির্ধারিত কালকে ত্বরান্বিত করতে পারেনা, আর না পারে বিলম্বিত করতে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2717,23,44,'অতঃপর আমি একের পর এক আমার রাসূল প্রেরণ করেছি। যখনই কোন জাতির নিকট তাদের রাসূল এসেছে তখনই তারা তাকে মিথ্যাবাদী বলেছে। অতঃপর আমি তাদের একের পর এককে ধ্বংস করলাম; আমি তাদেরকে কাহিনীর বিষয় করেছি; সুতরাং ধ্বংস হোক অবিশ্বাসীরা! ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2718,23,45,'অতঃপর আমি আমার নিদর্শন ও সুস্পষ্ট প্রমাণসহ মূসা ও তার ভাই হারূনকে পাঠালাম – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2719,23,46,'ফির‘আউন ও তার পরিষদবর্গের নিকট; কিন্তু তারা অহঙ্কার করল; তারা ছিল উদ্ধত সম্প্রদায়। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2720,23,47,'তারা বললঃ আমরা কি এমন দু’ ব্যক্তিতে বিশ্বাস স্থাপন করব যারা আমাদেরই মত এবং যাদের সম্প্রদায় আমাদের দাসত্ব করে? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2721,23,48,'অতঃপর তারা তাদেরকে মিথ্যাবাদী বলল। ফলে তারা ধ্বংসপ্রাপ্তদের শামিল হল। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2722,23,49,'আমি মূসাকে কিতাব দিয়েছিলাম যাতে তারা সৎপথ প্রাপ্ত হয়। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2723,23,50,'এবং আমি মারইয়াম তনয় ও তার জননীকে করেছিলাম এক নিদর্শন, তাদেরকে আশ্রয় দিয়েছিলাম এক নিরাপদ ও প্রস্রবন বিশিষ্ট উচ্চ ভূমিতে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2724,23,51,'হে রাসূলগণ! তোমরা পবিত্র বস্তু হতে আহার কর ও সৎ কাজ কর; তোমরা যা কর সেই সম্বন্ধে আমি সবিশেষ অবগত।  ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2725,23,52,'এবং তোমাদের এই যে জাতি এটাতো একই জাতি এবং আমিই তোমাদের রাব্ব; অতএব আমাকে ভয় কর। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2726,23,53,'কিন্তু মানুষ নিজেদের মধ্যে তাদের দীনকে বহুধা বিভক্ত করেছে; প্রত্যেক দলই তাদের নিকট যা আছে তা নিয়েই সন্তুষ্ট।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2727,23,54,'সুতরাং কিছুকালের জন্য তাদেরকে স্বীয় বিভ্রান্তিতে থাকতে দাও।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2728,23,55,'তারা কি মনে করে যে, আমি তাদেরকে সাহায্য স্বরূপ যে ধনৈশ্বর্য ও সন্তান-সন্ততি দান করি তদ্দারা –');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2729,23,56,'তাদের জন্য সর্ব প্রকার মঙ্গল ত্বরান্বিত করছি? না, তারা বুঝেনা – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2730,23,57,'যারা তাদের রবের ভয়ে সন্ত্রস্ত – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2731,23,58,'যারা তাদের রবের নিদর্শনাবলীতে ঈমান আনে – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2732,23,59,'যারা তাদের রবের সাথে শরীক করেনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2733,23,60,'আর যারা তাদের রবের নিকট প্রত্যাবর্তন করবে, এই বিশ্বাসে তাদের যা দান করার তা দান করে ভীত-কম্পিত হৃদয়ে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2734,23,61,'তারাই দ্রুত সম্পাদন করে কল্যাণকর কাজ এবং তারা তাতে অগ্রগামী হয়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2735,23,62,'আমি কেহকেও তার সাধ্যাতীত দায়িত্ব অর্পন করিনা এবং আমার নিকট আছে এক কিতাব যা সত্য ব্যক্ত করে এবং তাদের প্রতি যুল্‌ম করা হবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2736,23,63,'বরং এ বিষয়ে তাদের অন্তর অজ্ঞানতায় আচ্ছন্ন, এতদ্ব্যতীত আরও কাজ আছে যা তারা করে থাকে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2737,23,64,'আর আমি যখন তাদের ঐশ্বর্যশালী ব্যক্তিদেরকে শাস্তি দ্বারা ধৃত করি তখনই তারা আর্তনাদ করে উঠে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2738,23,65,'তাদেরকে বলা হবেঃ আজ আর্তনাদ করনা, তোমরা আমার সাহায্য পাবেনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2739,23,66,'আমার আয়াত তোমাদের কাছে পাঠ করা হত, কিন্তু তোমরা পিছন ফিরে সরে পড়তে –');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2740,23,67,'দম্ভ ভরে, এ বিষয়ে অর্থহীন গল্প গুজব করতে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2741,23,68,'তাহলে কি তারা এই বাণী অনুধাবন করেনা? অথচ তাদের নিকট কি এমন কিছু এসেছে যা তাদের পূর্ব-পুরুষদের নিকট আসেনি?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2742,23,69,'অথচ তারা কি তাদের রাসূলকে চিনেনা বলে তাকে অস্বীকার করে? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2743,23,70,'অথবা তারা কি বলে যে, সে উন্মাদ? বস্তুতঃ সে তাদের নিকট সত্য এনেছে এবং তাদের অধিকাংশ সত্যকে অপছন্দ করে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2744,23,71,'সত্য যদি তাদের কামনার অনুগামী হত তাহলে বিশৃংখল হয়ে পড়ত আকাশমন্ডলী, পৃথিবী এবং ওদের মধ্যবর্তী সব কিছুই। পক্ষান্তরে আমি তাদেরকে দিয়েছি উপদেশ, কিন্তু তারা উপদেশ হতে মুখ ফিরিয়ে নেয়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2745,23,72,'অথবা তুমি কি তাদের কাছে কোন প্রতিদান চাও? তোমার রবের প্রতিদানই শ্রেষ্ঠ এবং তিনিই শ্রেষ্ঠ রিয্কদাতা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2746,23,73,'তুমিতো তাদেরকে সরল পথে আহবান করছ। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2747,23,74,'যারা আখিরাতে বিশ্বাস করেনা তারাতো সরল পথ হতে বিচ্যুত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2748,23,75,'আমি তাদের উপর দয়া করলেও এবং তাদের দুঃখ-দৈন্য দূর করলেও তারা অবাধ্যতায় বিভ্রান্তের ন্যায় ঘুরতে থাকবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2749,23,76,'আমি তাদেরকে শাস্তি দ্বারা ধৃত করলাম, কিন্তু তারা তাদের রবের প্রতি বিনত হলনা এবং কাতর প্রার্থনাও করলনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2750,23,77,'অবশেষে যখন আমি তাদের জন্য কঠিন শাস্তির দ্বার খুলে দিই তখনই তারা হতাশ হয়ে পড়ে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2751,23,78,'তিনিই তোমাদের জন্য কর্ণ, চক্ষু ও অন্তঃকরণ সৃষ্টি করেছেন; তোমরা অল্পই কৃতজ্ঞতা প্রকাশ করে থাক।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2752,23,79,'তিনিই তোমাদেরকে পৃথিবীতে বিস্তৃত করেছেন এবং তোমাদেরকে তাঁরই নিকট একত্রিত করা হবে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2753,23,80,'তিনিই জীবন দান করেন এবং মৃত্যু ঘটান, আর তাঁরই অধিকারে রাত ও দিনের পরিবর্তন, তবুও কি তোমরা বুঝবেনা?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2754,23,81,'এতদসত্ত্বেও তারা তা’ই বলে যেমন বলেছিল পূর্ববর্তীরা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2755,23,82,'তারা বলেঃ আমাদের মৃত্যু ঘটলে এবং আমরা মাটি ও অস্থিতে পরিণত হলেও কি আমরা পুনরুত্থিত হব? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2756,23,83,'আমাদেরকে তো এ বিষয়েই প্রতিশ্রুতি প্রদান করা হয়েছে এবং অতীতে আমাদের পূর্ব-পুরুষদেরকেও; এটাতো প্রাচীন কালের কল্পকথা ব্যতীত আর কিছুই নয়। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2757,23,84,'জিজ্ঞেস করঃ এই পৃথিবী এবং এতে যা আছে তা কার, যদি তোমরা জান তো বল?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2758,23,85,'তারা বলবেঃ আল্লাহর! বলঃ তবুও কি তোমরা শিক্ষা গ্রহণ করবেনা?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2759,23,86,'জিজ্ঞেস করঃ কে সপ্তাকাশের রাব্ব এবং কে’ইবা মহান আরশের রাব্ব?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2760,23,87,'তারা বলবেঃ আল্লাহ! বলঃ তবুও কি তোমরা আল্লাহভীরু হবেনা? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2761,23,88,'জিজ্ঞেস করঃ যদি তোমরা জান তাহলে বল সব কিছুর কর্তৃত্ব কার হাতে, কে আশ্রয় দান করেন, যাঁর উপর আশ্রয়দাতা নেই? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2762,23,89,'তারা বলবেঃ আল্লাহর! বলঃ তবুও তোমরা কেমন করে বিভ্রান্ত হচ্ছ? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2763,23,90,'বরং আমিতো তাদের নিকট সত্য পৌঁছিয়েছি; কিন্তু তারা মিথ্যাবাদী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2764,23,91,'আল্লাহ কোন সন্তান গ্রহণ করেননি এবং তাঁর সাথে অপর কোন মা‘বূদ নেই; যদি থাকত তাহলে প্রত্যেক মা‘বূদ স্বীয় সৃষ্টি নিয়ে পৃথক হয়ে যেত এবং একে অপরের উপর প্রাধান্য বিস্তার করত। তারা যা বলে তা হতে আল্লাহ অতি পবিত্র! ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2765,23,92,'তিনি দৃশ্য ও অদৃশ্যের পরিজ্ঞাতা, তারা যাকে শরীক করে তিনি তার উর্ধ্বে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2766,23,93,'বলঃ হে আমার রাব্ব! যে বিষয়ে তাদেরকে প্রতিশ্রুতি প্রদান করা হয়েছে, তা যদি আপনি আমাকে দেখাতেন – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2767,23,94,'তবে হে আমার রাব্ব! আপনি আমাকে যালিম সম্প্রদায়ের অন্তর্ভুক্ত করবেননা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2768,23,95,'আমি তাদেরকে যে বিষয়ে প্রতিশ্রুতি প্রদান করেছি তা আমি তোমাকে দেখাতে অবশ্যই সক্ষম।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2769,23,96,'মন্দের মুকাবিলা কর উত্তম দ্বারা, তারা যা বলে আমি সেই সম্বন্ধে সবিশেষ অবহিত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2770,23,97,'আর বলঃ হে আমার রাব্ব! আমি আপনার আশ্রয় প্রার্থনা করি শাইতানের প্ররোচনা হতে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2771,23,98,'হে আমার রাব্ব! আমি আপনার আশ্রয় প্রার্থনা করি আমার নিকট ওদের উপস্থিতি হতে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2772,23,99,'যখন তাদের কারও মৃত্যু উপস্থিত হয় তখন সে বলেঃ হে আমার রাব্ব! আমাকে পুনরায় প্রেরণ করুন – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2773,23,100,'যাতে আমি সৎ কাজ করতে পারি যা আমি পূর্বে করিনি। না এটা হবার নয়; এটাতো তার একটা উক্তি মাত্র; তাদের সামনে বারযাখ থাকবে পুনরুত্থান দিন পর্যন্ত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2774,23,101,'অতঃপর যে দিন শিঙ্গায় ফুৎকার দেয়া হবে সেদিন পরস্পরের মধ্যে আত্মীয়তার বন্ধন থাকবেনা, এবং একে অপরের খোঁজ খবর নিবেনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2775,23,102,'সুতরাং যাদের পাল্লা ভারী হবে তারাই হবে সফলকাম। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2776,23,103,'আর যাদের পাল্লা হালকা হবে তারাই নিজেদের ক্ষতি করেছে; তারা জাহান্নামে স্থায়ী হবে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2777,23,104,'আগুন তাদের মুখমন্ডল দগ্ধ করবে এবং তারা সেখানে থাকবে বীভৎস চেহারায়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2778,23,105,'তোমাদের নিকট কি আমার আয়াতসমূহ আবৃত্তি হতনা? অথচ তোমরা ওগুলি অস্বীকার করতে!');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2779,23,106,'তারা বলবেঃ হে আমাদের রাব্ব! দুর্ভাগ্য আমাদেরকে পেয়ে বসেছিল এবং আমরা ছিলাম এক বিভ্রান্ত সম্প্রদায়। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2780,23,107,'হে আমাদের রাব্ব! এই আগুন হতে আমাদেরকে উদ্ধার করুন; অতঃপর আমরা যদি পুনরায় কুফরী করি তাহলেতো আমরা অবশ্যই সীমালংঘনকারী হব। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2781,23,108,'আল্লাহ বলবেনঃ তোমরা হীন অবস্থায় এখানেই থাক এবং আমার সাথে কোন কথা বলনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2782,23,109,'আমার বান্দাদের মধ্যে একদল ছিল যারা বলতঃ হে আমাদের রাব্ব! আমরা ঈমান এনেছি, সুতরাং আপনি আমাদেরকে ক্ষমা করুন এবং আমাদের উপর দয়া করুন, আপনিতো দয়ালুগণের মধ্যে শ্রেষ্ঠ দয়ালু। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2783,23,110,'কিন্তু তাদেরকে নিয়ে তোমরা এতো ঠাট্টা বিদ্রুপ করতে যে, তা তোমাদেরকে আমার স্মরণ ভুলিয়ে দিয়েছিল; তোমরাতো তাদেরকে নিয়ে হাসি-ঠাট্টাই করতে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2784,23,111,'আমি আজ তাদেরকে তাদের ধৈর্যের কারণে এমনভাবে পুরস্কৃত করলাম যে, তারাই হল সফলকাম। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2785,23,112,'তিনি বলবেনঃ তোমরা পৃথিবীতে কত বছর অবস্থান করেছিলে? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2786,23,113,'তারা বলবেঃ আমরা অবস্থান করেছিলাম একদিন অথবা একদিনের কিছু অংশ, আপনি না হয় গণনাকারীদেরকে জিজ্ঞেস করুন। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2787,23,114,'তিনি বলবেনঃ তোমরা অল্পকালই অবস্থান করেছিলে, যদি তোমরা জানতে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2788,23,115,'তোমরা কি মনে করেছিলে যে, আমি তোমাদেরকে অনর্থক সৃষ্টি করেছি এবং তোমরা আমার নিকট প্রত্যাবর্তিত হবেনা? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2789,23,116,'মহিমান্বিত আল্লাহ যিনি প্রকৃত মালিক, তিনি ব্যতীত কোন মা‘বূদ নেই; সম্মানিত আরশের তিনিই রাব্ব। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2790,23,117,'যে ব্যক্তি আল্লাহর সাথে ডাকে অন্য ইলাহকে যার নিকট কোন সনদ নেই, তার হিসাব রয়েছে তার রবের নিকট, নিশ্চয়ই কাফিরেরা সফলকাম হবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2791,23,118,'বলঃ হে আমার রাব্ব! ক্ষমা করুন ও দয়া করুন, দয়ালুদের মধ্যে আপনিইতো শ্রেষ্ঠ দয়ালু।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2792,24,1,'এটি একটি সূরা, এটি আমি অবতীর্ণ করেছি এবং এর বিধানকে অবশ্য পালনীয় করেছি, এতে আমি অবতীর্ণ করেছি সুস্পষ্ট আয়াতসমূহ যাতে তোমরা উপদেশ গ্রহণ কর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2793,24,2,'ব্যভিচারিণী ও ব্যভিচারী - নারী ও পুরুষ প্রত্যেককে একশ’ কশাঘাত করবে, আল্লাহর বিধান কার্যকরী করতে তাদের প্রতি দয়া যেন তোমাদেরকে প্রভাবান্বিত না করে, যদি তোমরা আল্লাহ এবং পরকালে বিশ্বাসী হও; মু’মিনদের একটি দল যেন তাদের শাস্তি প্রত্যক্ষ করে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2794,24,3,'ব্যভিচারী ব্যভিচারিনী অথবা মুশরিক নারীকে ব্যতীত বিয়ে করেনা এবং ব্যভিচারিনীকে ব্যভিচারী অথবা মুশরিক ব্যতীত কেহ বিয়ে করেনা, মু’মিনদের জন্য এদেরকে নিষিদ্ধ করা হয়েছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2795,24,4,'যারা সতী-সাধ্বী রমনীর প্রতি অপবাদ আরোপ করে এবং চারজন সাক্ষী হাজির করেনা, তাদেরকে আশিটি কশাঘাত করবে এবং কখনও তাদের সাক্ষ্য গ্রহণ করবেনা; তারাই সত্যত্যাগী। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2796,24,5,'তবে যদি এরপর তারা তাওবাহ করে ও নিজেদেরকে সংশোধন করে, আল্লাহতো ক্ষমাশীল, পরম দয়ালু।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2797,24,6,'এবং যারা নিজেদের স্ত্রীর প্রতি অপবাদ আরোপ করে অথচ নিজেরা ব্যতীত তাদের কোন সাক্ষী নেই, তাদের প্রত্যেকের সাক্ষ্য এই হবে যে, সে আল্লাহর নামে চার বার শপথ করে বলবে যে, সে অবশ্যই সত্যবাদী। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2798,24,7,'আর পঞ্চম বারে বলবে যে, সে যদি মিথ্যাবাদী হয় তাহলে তার উপর নেমে আসবে আল্লাহর লা’নত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2799,24,8,'তবে স্ত্রীর শাস্তি রহিত হবে যদি সে চার বার আল্লাহর নামে শপথ করে সাক্ষ্য দেয় যে, তার স্বামীই মিথ্যাবাদী। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2800,24,9,'আর পঞ্চমবারে বলবে যে, তার স্বামী সত্যবাদী হলে তার নিজের উপর নেমে আসবে আল্লাহর গযব। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2801,24,10,'তোমাদের প্রতি আল্লাহর অনুগ্রহ ও দয়া না থাকলে তোমাদের কেহই অব্যাহতি পেতেনা; এবং আল্লাহ তাওবাহ গ্রহণকারী ও প্রজ্ঞাময়। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2802,24,11,'যারা এই অপবাদ রচনা করেছে তারাতো তোমাদেরই একটি দল; এটাকে তোমরা তোমাদের জন্য অনিষ্টকর মনে করনা; বরং এটাতো তোমাদের জন্য কল্যাণকর; তাদের প্রত্যেকের জন্য রয়েছে তাদের কৃত পাপ কাজের ফল, এবং তাদের মধ্যে যে এ ব্যাপারে প্রধান ভূমিকা পালন করেছে তার জন্য রয়েছে কঠিন শাস্তি। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2803,24,12,'এ কথা শোনার পর মু’মিন পুরুষ এবং নারীগণ কেন নিজেদের লোক সম্পর্কে উত্তম ধারনা করেনি এবং বলেনিঃ এটাতো সুস্পষ্ট অপবাদ? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2804,24,13,'তারা কেন এ ব্যাপারে চার জন সাক্ষী হাযির করেনি? যেহেতু তারা সাক্ষী হাযির করেনি, সেই কারণে তারা আল্লাহর বিধানে মিথ্যাবাদী। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2805,24,14,'দুনিয়া ও আখিরাতে তোমাদের প্রতি আল্লাহর অনুগ্রহ ও দয়া না থাকলে, তোমরা যাতে লিপ্ত ছিলে তজ্জন্য কঠিন শাস্তি তোমাদেরকে স্পর্শ করত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2806,24,15,'যখন তোমরা লোকমুখে এটা ছড়াচ্ছিলে এবং এমন বিষয় মুখে উচ্চারণ করছিলে যার কোন জ্ঞান তোমাদের ছিলনা এবং তোমরা একে তুচ্ছ গন্য করেছিলে, যদিও আল্লাহর নিকট ছিল এটা গুরুতর বিষয়। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2807,24,16,'এবং তোমরা যখন এটা শ্রবণ করলে তখন কেন বললেনাঃ এ বিষয়ে বলাবলি করা আমাদের উচিৎ নয়; আল্লাহ পবিত্র, মহান! এটাতো এক গুরুতর অপবাদ। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2808,24,17,'আল্লাহ তোমাদেরকে উপদেশ দিচ্ছেনঃ তোমরা যদি মু’মিন হও তাহলে কখনও অনুরূপ আচরণের পুনরাবৃত্তি করনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2809,24,18,'আল্লাহ তোমাদের জন্য তাঁর আয়াতসমূহ সুস্পষ্টভাবে বিবৃত করেন এবং আল্লাহ সর্বজ্ঞ, প্রজ্ঞাময়। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2810,24,19,'যারা মু’মিনদের মধ্যে অশ্লীলতার প্রসার কামনা করে তাদের জন্য রয়েছে দুনিয়া ও আখিরাতে মর্মন্তদ শাস্তি এবং আল্লাহ জানেন, তোমরা জাননা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2811,24,20,'তোমাদের প্রতি আল্লাহর অনুগ্রহ ও দয়া না থাকলে তোমাদের কেহই অব্যাহতি পেতেনা, এবং আল্লাহ দয়ার্দ্র ও পরম দয়ালু। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2812,24,21,'হে মু’মিনগণ! তোমরা শাইতানের পদাংক অনুসরণ করনা; কেহ শাইতানের পদাংক অনুসরণ করলে শাইতানতো অশ্লীলতা ও মন্দ কাজের নির্দেশ দেয়; আল্লাহর দয়া ও অনুগ্রহ না থাকলে তোমাদের কেহই কখনও পবিত্র হতে পারতেনা, তবে আল্লাহ যাকে ইচ্ছা পবিত্র করেন, এবং আল্লাহ সর্বশ্রোতা, সর্বজ্ঞ। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2813,24,22,'তোমাদের মধ্যে যারা ঐশ্বর্য ও প্রাচুর্যের অধিকারী তারা যেন শপথ গ্রহণ না করে যে, তারা আত্মীয়-স্বজন ও অভাবগ্রস্তদেরকে এবং আল্লাহর পথে যারা গৃহত্যাগ করেছে তাদেরকে কিছুই দিবেনা। তারা যেন তাদের দোষ-ক্রটি উপেক্ষা করে। তোমরা কি চাওনা যে, আল্লাহ তোমাদেরকে ক্ষমা করেন? এবং আল্লাহ ক্ষমাশীল, পরম দয়ালু। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2814,24,23,'যারা সাধ্বী, সরলমনা ও বিশ্বাসী নারীর প্রতি অপবাদ আরোপ করে তারা দুনিয়া ও আখিরাতে অভিশপ্ত এবং তাদের জন্য রয়েছে মহাশাস্তি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2815,24,24,'যেদিন তাদের বিরুদ্ধে সাক্ষ্য দিবে তাদের জিহবা, তাদের হাত এবং তাদের পা তাদের কৃতকর্ম সম্বন্ধে – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2816,24,25,'সেদিন আল্লাহ তাদের প্রাপ্ত প্রতিফল পুরোপুরি দিবেন এবং তারা জানবে যে, আল্লাহই সত্য, স্পষ্ট প্রকাশক। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2817,24,26,'দুশ্চরিত্রা নারী দুশ্চরিত্র পুরুষের জন্য; দুশ্চরিত্র পুরুষ দুশ্চরিত্রা নারীর জন্য; সচ্চরিত্রা নারী সচ্চরিত্র পুরুষের জন্য এবং সচ্চরিত্র পুরুষ সচ্চরিত্রা নারীর জন্য। লোকে যা বলে এরা তা থেকে পবিত্র; এদের জন্য রয়েছে ক্ষমা এবং সম্মানজনক জীবিকা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2818,24,27,'হে মু’মিনগণ! তোমরা নিজেদের গৃহ ব্যতীত অন্য কারও গৃহে গৃহবাসীদের অনুমতি না নিয়ে এবং তাদেরকে সালাম না করে প্রবেশ করনা; এটাই তোমাদের জন্য শ্রেয়, যাতে তোমরা উপদেশ গ্রহণ কর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2819,24,28,'যদি তোমরা গৃহে কেহকে না পাও তাহলে তাতে প্রবেশ করবে না যতক্ষণ না তোমাদেরকে অনুমতি দেয়া হয়। যদি তোমাদেরকে বলা হয় ফিরে যাও, তাহলে তোমরা ফিরে যাবে, এটাই তোমাদের জন্য উত্তম এবং তোমরা যা কর সেই সম্বন্ধে আল্লাহ সবিশেষ অবহিত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2820,24,29,'যে গৃহে কেহ বাস করেনা তাতে তোমাদের জন্য দ্রব্য সামগ্রী থাকলে সেখানে তোমাদের প্রবেশে কোনো পাপ নেই এবং আল্লাহ জানেন যা তোমরা প্রকাশ কর এবং যা তোমরা গোপন কর। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2821,24,30,'মু’মিনদেরকে বলঃ তারা যেন দৃষ্টিকে সংযত করে এবং তাদের লজ্জাস্থানের হিফাযাত করে। এতে তাদের জন্য উত্তম পবিত্রতা রয়েছে; তারা যা করে সেই বিষয়ে আল্লাহ অবহিত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2822,24,31,'ঈমান আনয়নকারিনী নারীদেরকে বলঃ তারা যেন তাদের দৃষ্টিকে এবং তাদের লজ্জাস্থানের হিফাযাত করে। তারা যেন যা সাধারণতঃ প্রকাশমান তা ব্যতীত তাদের সৌন্দর্য প্রদর্শন না করে। তাদের গ্রীবা ও বক্ষদেশ যেন মাথার কাপড় দ্বারা আবৃত করে। তারা যেন তাদের স্বামী, পিতা, শ্বশুর, পুত্র, স্বামীর পুত্র, ভাই, ভ্রাতুস্পুত্র, ভগ্নীপুত্র, আপন নারীগণ, তাদের মালিকানাধীন দাসী, পুরুষদের মধ্যে যৌন কামনা রহিত পুরুষ এবং নারীদের গোপন অঙ্গ সম্বন্ধে অজ্ঞ বালক ব্যতীত কারও নিকট তাদের আভরণ প্রকাশ না করে। তারা যেন তাদের গোপন আভরণ প্রকাশের উদ্দেশে সজোরে পদক্ষেপ না ফেলে। হে মু’মিনগণ! তোমরা সবাই আল্লাহর দিকে প্রত্যাবর্তন কর, যাতে তোমরা সফলকাম হতে পার। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2823,24,32,'তোমাদের মধ্যে যারা ‘‘আইয়িম’’ (বিপত্নীক পুরুষ বা বিধবা মহিলা) তাদের বিবাহ সম্পাদন কর এবং তোমাদের দাস-দাসীদের মধ্যে যারা সৎ তাদেরও। তারা অভাবগ্রস্ত হলে আল্লাহ নিজ অনুগ্রহে তাদেরকে অভাবমুক্ত করে দিবেন; আল্লাহতো প্রাচুর্যময়, সর্বজ্ঞ। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2824,24,33,'যাদের বিয়ের সামর্থ্য নেই আল্লাহ তাদেরকে নিজ অনুগ্রহে অভাবমুক্ত না করা পর্যন্ত তারা যেন সংযম অবলম্বন করে এবং তোমাদের মালিকানাধীন দাসদাসীদের মধ্যে কেহ তার মুক্তির জন্য লিখিত চুক্তি করতে চাইলে তাদের সাথে চুক্তিতে আবদ্ধ হও, যদি তোমরা তাদের মধ্যে মঙ্গলের সন্ধান পাও; আল্লাহ তোমাদের যে সম্পদ দিয়েছেন তা হতে তোমরা তাদেরকে দান করবে। তোমাদের দাসীরা সততা রক্ষা করতে চাইলে পার্থিব জীবনের ধন সম্পদের লালসায় তাদেরকে ব্যভিচারিণী হতে বাধ্য করনা, আর যে তাদেরকে বাধ্য করে, সেরূপ ক্ষেত্রে তাদের উপর যবরদস্তির পর আল্লাহ ক্ষমাশীল, দয়ালু। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2825,24,34,'আমি তোমাদের নিকট অবতীর্ণ করেছি সুস্পষ্ট আয়াত, তোমাদের পূর্ববর্তীদের দৃষ্টান্ত এবং মুত্তাকীদের জন্য উপদেশ। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2826,24,35,'আল্লাহ আকাশমন্ডলী ও পৃথিবীর জ্যোতি, তাঁর জ্যোতির উপমা যেন একটি দীপাধার, যার মধ্যে আছে একটি প্রদীপ; প্রদীপটি একটি কাঁচের আবরণের মধ্যে স্থাপিত, কাঁচের আবরণটি উজ্জ্বল নক্ষত্র সদৃশ; এটা প্রজ্জ্বলিত করা হয় পুতঃপবিত্র যাইতূন বৃক্ষের তেল দ্বারা যা প্রাচ্যের নয়, প্রতিচ্যেরও নয়, আগুন ওকে স্পর্শ না করলেও যেন ওর তেল উজ্জ্বল আলো দিচ্ছে; জ্যোতির উপর জ্যোতি! আল্লাহ যাকে ইচ্ছা পথ নির্দেশ করেন তাঁর জ্যোতির দিকে; আল্লাহ মানুষের জন্য উপমা দিয়ে থাকেন এবং আল্লাহ সর্ব বিষয়ে সর্বজ্ঞ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2827,24,36,'সেই সব গৃহ, যাকে মর্যাদায় সমুন্নত করতে এবং যাতে তাঁর নাম স্মরণ করতে আল্লাহ নির্দেশ দিয়েছেন, সকাল ও সন্ধ্যায় তাঁর পবিত্রতা ও মহিমা ঘোষণা করে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2828,24,37,'সেই সব লোক, যাদেরকে ব্যবসা-বাণিজ্য এবং ক্রয় বিক্রয় আল্লাহর স্মরণ হতে এবং সালাত কায়েম ও যাকাত প্রদান হতে বিরত রাখেনা, তারা ভয় করে সেই দিনকে যেদিন তাদের অন্তর ও দৃষ্টি বিপর্যস্ত হয়ে পড়বে –');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2829,24,38,'যাতে তারা যে কাজ করে তজ্জন্য আল্লাহ তাদেরকে উত্তম পুরস্কার দেন এবং নিজ অনুগ্রহে তাদের প্রাপ্যের অধিক দেন; আল্লাহ যাকে ইচ্ছা অপরিমিত জীবিকা দান করে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2830,24,39,'যারা কুফরী করে, তাদের আমলসমূহ মরুভূমির মরীচিকা সদৃশ। পিপাসার্ত যাকে পানি মনে করে থাকে, কিন্তু সে ওর নিকট উপস্থিত হলে দেখবে ওটা কিছু নয়, কিন্তু সে পাবে সেখানে আল্লাহকে। অতঃপর তিনি তার কর্মফল পূর্ণ মাত্রায় দিবেন; আল্লাহ হিসাব গ্রহণে তৎপর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2831,24,40,'অথবা (কাফিরদের কাজ) প্রমত্ত সমুদ্রের বুকে গভীর অন্ধকারের ন্যায়, যাকে উদ্বেলিত করে তরঙ্গের উপর তরঙ্গ, যার উপরে রয়েছে ঘন কালো মেঘ, একের উপর এক অন্ধকার। তার হাতকে বের করলে সে তা আদৌ দেখতে পায়না। আল্লাহ যাকে জ্যোতি দান করেননা তার জন্য কোন জ্যোতি নেই। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2832,24,41,'তুমি কি দেখনা যে, আকাশমন্ডলী ও পৃথিবীতে যারা আছে তারা এবং উড্ডীয়মান বিহংগকূল আল্লাহর পবিত্রতা ও মহিমা ঘোষণা করে? তারা প্রত্যেকেই জানে তাঁর যোগ্য প্রার্থনা এবং পবিত্রতা ও মহিমা ঘোষণার পদ্ধতি এবং তারা যা করে সেই বিষয়ে আল্লাহ সম্যক অবগত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2833,24,42,'আকাশমন্ডলী ও পৃথিবীর সার্বভৌমত্ব আল্লাহরই এবং তাঁরই দিকে প্রত্যাবর্তন। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2834,24,43,'তুমি কি দেখনা আল্লাহ সঞ্চালিত করেন মেঘমালাকে, অতঃপর তাদেরকে একত্রিত করেন এবং পরে পুঞ্জীভূত করেন? অতঃপর তুমি দেখতে পাও, ওর মধ্য হতে নির্গত হয় বারিধারা। আকাশস্থিত শিলাস্তুপ হতে তিনি শিলা বর্ষণ করেন এবং এর দ্বারা তিনি যাকে ইচ্ছা তার উপর হতে এটা অন্য দিকে ফিরিয়ে দেন; মেঘের বিদ্যুৎঝলক দৃষ্টিশক্তি প্রায় কেড়ে নেয়। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2835,24,44,'আল্লাহ দিন ও রাতের পরিবর্তন ঘটান, এতে শিক্ষা রয়েছে অন্তঃদৃষ্টি সম্পন্নদের জন্য। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2836,24,45,'আল্লাহ সমস্ত জীব সৃষ্টি করেছেন পানি হতে, ওদের কতক পেটে ভর দিয়ে চলে এবং কতক দু’ পায়ে ভর করে চলে এবং কতক চলে চার পায়ে, আল্লাহ যা ইচ্ছা সৃষ্টি করেন, আল্লাহ সর্ব বিষয়ে সর্বশক্তিমান। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2837,24,46,'আমিতো সুস্পষ্ট নিদর্শন অবতীর্ণ করেছি। আল্লাহ যাকে ইচ্ছা সরল সঠিক পথ প্রদর্শন করেন। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2838,24,47,'তারা বলেঃ আমরা আল্লাহ ও রাসূলের প্রতি ঈমান এনেছি এবং আমরা আনুগত্য স্বীকার করছি। কিন্তু এরপর তাদের একদল মুখ ফিরিয়ে নেয়; বস্তুতঃ তারা মু’মিন নয়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2839,24,48,'আর যখন তাদেরকে ফাইসালা করে দেয়ার জন্য আহবান করা হয় আল্লাহ ও তাঁর রাসূলের দিকে তখন তাদের একদল মুখ ফিরিয়ে নেয়। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2840,24,49,'আর সত্য তাদের স্বপক্ষে হলে তারা বিনীতভাবে রাসূলের নিকট ছুটে আসে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2841,24,50,'তাদের অন্তরে কি ব্যাধি আছে, না তারা সংশয় পোষণ করে, না কি তারা ভয় করে যে, আল্লাহ ও তাঁর রাসূল তাদের প্রতি যুল্‌ম করবেন? বরং তারাইতো যালিম।  ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2842,24,51,'মু’মিনদের উক্তিতো এই, যখন তাদের মধ্যে ফাইসালা করে দেয়ার জন্য আল্লাহ এবং তাঁর রাসূলের দিকে আহবান করা হয় তখন তারা বলেঃ আমরা শ্রবণ করলাম ও মান্য করলাম। আর তারাইতো সফলকাম।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2843,24,52,'যারা আল্লাহ ও তাঁর রাসূলের আনুগত্য করে, আল্লাহকে ভয় করে ও তাঁর অবাধ্যতা হতে সাবধান থাকে তারাই সফলকাম। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2844,24,53,'তারা দৃঢ়ভাবে আল্লাহর শপথ করে বলে, তুমি তাদেরকে আদেশ করলে তারা বের হবেই; তুমি বলঃ শপথ করনা, যথার্থ আনুগত্যই কাম্য। তোমরা যা কর আল্লাহ সে বিষয়ে সবিশেষ অবহিত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2845,24,54,'বলঃ তোমরা আল্লাহর আনুগত্য কর এবং রাসূলের আনুগত্য কর; অতঃপর যদি তোমরা মুখ ফিরিয়ে নাও তাহলে তার উপর অর্পিত দায়িত্বের জন্য সে দায়ী এবং তোমাদের উপর অর্পিত দায়িত্বের জন্য তোমরা দায়ী; এবং তোমরা তার আনুগত্য করলে সৎ পথ পাবে, রাসূলের দায়িত্বতো শুধু স্পষ্টভাবে পৌঁছে দেয়া। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2846,24,55,'তোমাদের মধ্যে যারা ঈমান আনে ও সৎ কাজ করে আল্লাহ তাদেরকে প্রতিশ্রুতি দিচ্ছেন যে, তিনি তাদেরকে পৃথিবীতে প্রতিনিধিত্ব দান করবেনই, যেমন তিনি প্রতিনিধিত্ব দান করেছিলেন তাদের পূর্ববর্তীদেরকে এবং তিনি অবশ্যই তাদের জন্য সুদৃঢ় করবেন তাদের দীনকে যা তিনি তাদের জন্য মনোনীত করেছেন এবং তাদের ভয় ভীতির পরিবর্তে তাদেরকে অবশ্যই নিরাপত্তা দান করবেন; তারা আমার ইবাদাত করবে, আমার সাথে কোন শরীক করবেনা, অতঃপর যারা অকৃতজ্ঞ হবে তারাতো সত্যত্যাগী। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2847,24,56,'তোমরা সালাত কায়েম কর, যাকাত প্রদান কর এবং রাসূলের আনুগত্য কর, যাতে তোমরা অনুগ্রহ প্রাপ্ত হতে পার। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2848,24,57,'তুমি কাফিরদেরকে পৃথিবীতে প্রবল মনে করনা; তাদের আশ্রয়স্থল আগুন; কত নিকৃষ্ট এই পরিণাম! ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2849,24,58,'হে মু’মিনগণ! তোমাদের মালিকানাধীন দাস-দাসীরা এবং তোমাদের মধ্যে যারা বয়ঃপ্রাপ্ত হয়নি তারা যেন তোমাদের কক্ষে প্রবেশ করতে তিন সময়ে অনুমতি গ্রহণ করে। ফাজরের সালাতের পূর্বে, দ্বিপ্রহরে যখন তোমরা তোমাদের পোশাক খুলে রাখ এবং ইশার সালাতের পর। এই তিন সময় তোমাদের দেহ খোলা রাখার সময়; এই তিন সময় ছাড়া অন্য সময়ে বিনা অনুমতিতে প্রবেশ করলে তোমাদের জন্য ও তাদের জন্য কোন দোষ নেই; তোমাদের এক জনকে অপর জনের নিকটতো যাতায়াত করতেই হয়। এভাবে আল্লাহ তোমাদের নিকট তাঁর নির্দেশ সুস্পষ্ট রূপে বিবৃত করেন; আল্লাহ সর্বজ্ঞ, প্রজ্ঞাময়। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2850,24,59,'এবং তোমাদের সন্তান-সন্ততি বয়ঃপ্রাপ্ত হলে তারাও যেন অনুমতি প্রার্থনা করে তাদের বয়ঃজ্যেষ্ঠদের ন্যায়। এভাবে আল্লাহ তোমাদের জন্য তাঁর নির্দেশ সুস্পষ্টভাবে বিবৃত করেন; আল্লাহ সর্বজ্ঞ, প্রজ্ঞাময়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2851,24,60,'আর বৃদ্ধা নারী, যারা বিয়ের আশা রাখেনা, তাদের জন্য অপরাধ নেই যদি তারা তাদের সৌন্দর্য প্রদর্শন না করে তাদের বহির্বাস খুলে রাখে; তবে এটা হতে বিরত থাকাই তাদের জন্য উত্তম। আল্লাহ সর্বশ্রোতা, সর্বজ্ঞ। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2852,24,61,'অন্ধের জন্য দোষ নেই, খোঁড়ার জন্য দোষ নেই, আর দোষ নেই রোগীর জন্য এবং তোমাদের নিজেদের জন্যও দোষ নেই আহার করায় নিজেদের গৃহে অথবা তোমাদের পিতাদের গৃহে অথবা তোমাদের মাতাদের গৃহে অথবা তোমাদের ভাইদের গৃহে অথবা তোমাদের বোনদের গৃহে অথবা তোমাদের চাচাদের গৃহে অথবা তোমাদের ফুফুদের গৃহে অথবা তোমাদের মামাদের গৃহে অথবা তোমাদের খালাদের গৃহে অথবা সেই গৃহে যার চাবি রয়েছে তোমাদের কাছে অথবা তোমাদের প্রকৃত বন্ধুদের গৃহে। তোমরা একত্রে আহার কর অথবা পৃথকভাবে আহার কর তাতে তোমাদের জন্য কোনো অপরাধ নেই। তবে যখন তোমরা গৃহে প্রবেশ করবে তখন তোমরা তোমাদের স্বজনদের প্রতি সালাম করবে অভিবাদন স্বরূপ, যা আল্লাহর নিকট হতে কল্যাণময় ও পবিত্র। এভাবে আল্লাহ তোমাদের জন্য তাঁর নির্দেশ বিশদভাবে বর্ণনা করেন যাতে তোমরা বুঝতে পার।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2853,24,62,'তারাই মু’মিন যারা আল্লাহ এবং তাঁর রাসূলের উপর ঈমান আনে এবং রাসূলের সঙ্গে সমষ্টিগত ব্যাপারে একত্র হলে তার অনুমতি ব্যতীত তারা সরে পড়েনা; যারা অনুমতি প্রার্থনা করে তারাই আল্লাহ এবং তাঁর রাসূলে বিশ্বাসী; অতএব তারা তাদের কোন কাজে বাইরে যাবার জন্য তোমার অনুমতি চাইলে তাদের মধ্যে যাদেরকে ইচ্ছা তুমি অনুমতি দিবে এবং তাদের জন্য আল্লাহর নিকট ক্ষমা প্রার্থনা করবে; আল্লাহ ক্ষমাশীল, পরম দয়ালু। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2854,24,63,'রাসূলের আহবানকে তোমরা একে অপরের প্রতি আহবানের মত গণ্য করনা; তোমাদের মধ্যে যারা চুপি চুপি সরে পড়ে আল্লাহ তাদেরকে জানেন। সুতরাং যারা তাঁর আদেশের বিরুদ্ধাচরণ করে তারা সতর্ক হোক যে, বিপর্যয় তাদের উপর আপতিত হবে অথবা আপতিত হবে তাদের উপর কঠিন শাস্তি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2855,24,64,'জেনে রেখ, আকাশমন্ডলী ও পৃথিবীতে যা কিছু রয়েছে তা আল্লাহরই, তোমরা যাতে ব্যাপৃত তিনি তা জানেন; যেদিন তারা তাঁর নিকট প্রত্যাবর্তিত হবে সেদিন তিনি তাদেরকে জানিয়ে দিবেন তারা যা করত; আল্লাহ সর্ব বিষয়ে সর্বজ্ঞ। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2856,25,1,'কত মহান তিনি যিনি তাঁর বান্দার প্রতি ‘ফুরকান’ অবতীর্ণ করেছেন যাতে সে বিশ্ব জগতের জন্য সতর্ককারী হতে পারে! ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2857,25,2,'যিনি আকাশমন্ডলী ও পৃথিবীর সার্বভৌমত্বের অধিকারী; তিনি কোন সন্তান গ্রহণ করেননি; সার্বভৌমত্বে তাঁর কোন অংশীদার নেই। তিনি সমস্ত কিছু সৃষ্টি করেছেন এবং প্রত্যেককে পরিমিত করেছেন যথাযথ অনুপাতে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2858,25,3,'আর তারা তাঁর পরিবর্তে মা‘বূদ রূপে গ্রহণ করেছে অপরকে যারা কিছুই সৃষ্টি করেনা, বরং তারা নিজেরাই সৃষ্ট এবং তারা নিজেদের অপকার অথবা উপকার করার ক্ষমতা রাখেনা এবং জীবন, মৃত্যু ও পুনরুত্থানের উপরও কোন ক্ষমতা রাখেনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2859,25,4,'কাফিরেরা বলেঃ এটা মিথ্যা ব্যতীত কিছু নয়, সে এটা উদ্ভাবন করেছে এবং ভিন্ন সম্প্রদায়ের লোক তাকে এ ব্যাপারে সাহায্য করেছে; অবশ্যই তারা যুল্‌ম ও মিথ্যাচারের আশ্রয় নিয়েছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2860,25,5,'এবং তারা বলেঃ এগুলিতো সেকালের উপকথা যা সে লিখিয়ে নিয়েছে; এগুলি সকাল-সন্ধ্যায় তার নিকট পাঠ করা হয়। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2861,25,6,'বলঃ এটা তিনিই অবতীর্ণ করেছেন যিনি আকাশমন্ডলী ও পৃথিবীর সমুদয় রহস্য অবগত আছেন; তিনি ক্ষমাশীল, পরম দয়ালু। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2862,25,7,'তারা বলেঃ এ কেমন রাসূল যে আহার করে এবং হাটে বাজারে চলাফিরা করে? তার কাছে কোন মালাক/ফেরেশতা কেন অবতীর্ণ করা হলনা যে তার সাথে থাকত সতর্ককারী রূপে? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2863,25,8,'তাকে ধন ভান্ডার দেয়া হয়নি কেন, অথবা কেন একটি বাগান দেয়া হলনা যা হতে সে খাদ্য সংগ্রহ করতে পারে? সীমালংঘনকারীরা আরও বলেঃ তোমরাতো এক যাদুগ্রস্ত ব্যক্তিরই অনুসরণ করছ। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2864,25,9,'দেখ, তারা তোমার কি উপমা দেয়। তারা পথভ্রষ্ট হয়েছে এবং তারা পথ পাবেনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2865,25,10,'কত মহান তিনি যিনি ইচ্ছা করলে তোমাকে দিতে পারেন এটা অপেক্ষা উৎকৃষ্টতর বস্তু - উদ্যানসমূহ, যার নিম্নদেশে নদ-নদী প্রবাহিত এবং দিতে পারেন প্রাসাদসমূহ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2866,25,11,'বরং তারা কিয়ামাতকে অস্বীকার করেছে এবং যারা কিয়ামাতকে অস্বীকার করে তাদের জন্য আমি প্রস্তুত রেখেছি জ্বলন্ত আগুন। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2867,25,12,'দূর হতে আগুন যখন তাদেরকে দেখবে তখন তারা শুনতে পাবে ওর ক্রুদ্ধ গর্জন ও হুঙ্কার। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2868,25,13,'এবং যখন তাদেরকে শৃংখলিত অবস্থায় ওর কোন সংকীর্ণ স্থানে নিক্ষেপ করা হবে তখন তারা সেখানে ধ্বংস কামনা করবে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2869,25,14,'তাদেরকে বলা হবেঃ আজ তোমরা একবারের জন্য ধ্বংস কামনা করনা, বরং বহুবার ধ্বংস হওয়ার কামনা করতে থাক। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2870,25,15,'তাদেরকে জিজ্ঞেস করঃ এটাই শ্রেয়, নাকি স্থায়ী জান্নাত, যার প্রতিশ্রুতি দেয়া হয়েছে মুত্তাকীদেরকে! এটাই তাদের পুরস্কার ও প্রত্যাবর্তন স্থল। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2871,25,16,'সেখানে তারা যা কামনা করবে তা’ই পাবে এবং তারা স্থায়ী হবে। এই প্রতিশ্রুতি পূরণ তোমার রবেরই দায়িত্ব। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2872,25,17,'এবং যেদিন তিনি একত্রিত করবেন তাদেরকে এবং তারা আল্লাহর পরিবর্তে যাদের ইবাদাত করত তাদেরকে, তিনি সেদিন জিজ্ঞেস করবেনঃ তোমরাই কি আমার এই বান্দাদেরকে বিভ্রান্ত করেছিলে, নাকি তারা নিজেরাই পথভ্রষ্ট হয়েছিল? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2873,25,18,'তারা বলবেঃ আপনি পবিত্র ও মহান! আপনার পরিবর্তে আমরা অন্যকে অভিভাবক রূপে গ্রহণ করতে পারিনা। আপনিইতো এদেরকে ও এদের পিতৃ-পুরুষদেরকে ভোগ-সম্ভার দিয়েছিলেন। পরিণামে তারা উপদেশ বিস্মৃত হয়েছিল এবং পরিণত হয়েছিল এক ধ্বংসপ্রাপ্ত জাতিতে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2874,25,19,'আল্লাহ (মুশরিকদেরকে) বলবেনঃ তোমরা যা বলতে তারা তা মিথ্যা সাব্যস্ত করেছে। সুতরাং তোমরা শাস্তি প্রতিরোধ করতে পারবে না, সাহায্যও পাবে না। তোমাদের মধ্যে যে সীমালংঘন করবে আমি তাকে মহাশাস্তি আস্বাদ করাব।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2875,25,20,'তোমার পূর্বে আমি যে সব রাসূল প্রেরণ করেছি তারা সবাই আহার করত ও হাটে-বাজারে চলাফিরা করত। হে লোক সকল! আমি তোমাদের মধ্যে এককে অপরের জন্য পরীক্ষা স্বরূপ করেছি। তোমরা ধৈর্য ধারণ করবে কি? তোমার রাব্ব সব কিছু দেখেন। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2876,25,21,'যারা আমার সাক্ষাৎ কামনা করেনা তারা বলেঃ আমাদের নিকট মালাক/ফেরেশতা অবতীর্ণ করা হয়না কেন? অথবা আমরা আমাদের রাব্বকে প্রত্যক্ষ করি না কেন? তারা তাদের অন্তরে অহংকার পোষণ করে এবং তারা সীমালংঘন করেছে গুরুতর রূপে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2877,25,22,'যেদিন তারা মালাইকাকে/ফেরেশতাদেরকে প্রত্যক্ষ করবে সেদিন অপরাধীদের জন্য সুসংবাদ থাকবে না এবং তারা বলবেঃ যদি কোনো বাধা থাকত! ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2878,25,23,'আমি তাদের কৃতকর্মগুলি বিবেচনা করব, অতঃপর সেগুলিকে বিক্ষিপ্ত ধূলিকণায় পরিণত করব। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2879,25,24,'সেদিন জান্নাতবাসীদের বাসস্থান হবে উৎকৃষ্ট এবং বিশ্রামস্থল হবে মনোরম। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2880,25,25,'যেদিন আকাশ মেঘপুঞ্জসহ বিদীর্ণ হবে এবং মালাইকাকে (ফেরেশতাদেরকে) নামিয়ে দেয়া হবে – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2881,25,26,'সেদিন প্রকৃত রাজত্ব হবে দয়াময় আল্লাহর এবং কাফিরদের জন্য সেদিন হবে কঠিন। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2882,25,27,'যালিম ব্যক্তি সেদিন নিজ হস্তদ্বয় দংশন করতে করতে বলবেঃ হায়! আমি যদি রাসূলের সাথে সৎ পথ অবলম্বন করতাম! ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2883,25,28,'হায় দুর্ভোগ আমার! আমি যদি অমুককে বন্ধু রূপে গ্রহণ না করতাম!');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2884,25,29,'আমাকেতো সে বিভ্রান্ত করেছিল আমার নিকট উপদেশ পৌঁছার পর; শাইতানতো মানুষের জন্য মহাপ্রতারক। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2885,25,30,'তখন রাসূল বলবেঃ হে আমার রাব্ব! আমার সম্প্রদায়তো এই কুরআনকে পরিত্যাজ্য মনে করেছিল। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2886,25,31,'এভাবেই প্রত্যেক নাবীর জন্য আমি অপরাধীদের মধ্য থেকে শত্রু করেছি। তোমার জন্য তোমার রাব্ব পথ প্রদর্শক ও সাহায্যকারী রূপে যথেষ্ট। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2887,25,32,'কাফিরেরা বলেঃ সমগ্র কুরআন তার নিকট একবারেই অবতীর্ণ হলনা কেন? এভাবেই অবতীর্ণ করেছি, যাতে তোমার হৃদয় ওর দ্বারা মযবূত হয় এবং তা সম্পূর্ণ রুপে আস্তে আস্তে আত্মস্থ করতে পার। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2888,25,33,'তারা তোমার নিকট এমন কোন সমস্যা উপস্থিত করেনি যার সঠিক সমাধান ও সুন্দর ব্যাখ্যা আমি তোমাকে দান করিনি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2889,25,34,'যাদেরকে মুখে ভর দিয়ে দিয়ে চলা অবস্থায় জাহান্নামের দিকে একত্র করা হবে, তাদেরই স্থান হবে অতি নিকৃষ্ট এবং তারাই পথভ্রষ্ট। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2890,25,35,'আমিতো মূসাকে কিতাব দিয়েছিলাম এবং তার ভাই হারূনকে তার সাহায্যকারী করেছিলাম। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2891,25,36,'এবং বলেছিলামঃ তোমরা সেই সম্প্রদায়ের নিকট যাও যারা আমার নিদর্শনাবলীকে অস্বীকার করেছে; অতঃপর আমি তাদেরকে সম্পূর্ণরূপে ধ্বংস করেছিলাম। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2892,25,37,'আর নূহের সম্প্রদায় যখন রাসূলদের প্রতি মিথ্যারোপ করল তখন আমি তাদেরকে নিমজ্জিত করলাম এবং তাদেরকে মানবজাতির জন্য নিদর্শন স্বরূপ করে রাখলাম; যালিমদের জন্য আমি মর্মন্তদ শাস্তি প্রস্তুত করে রেখেছি। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2893,25,38,'আমি ধ্বংস করেছিলাম ‘আদ, ছামূদ, রা’স্ এবং তাদের অর্ন্তবর্তী কালের বহু সম্প্রদায়কেও। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2894,25,39,'আমি তাদের প্রত্যেকের জন্য দৃষ্টান্ত বর্ণনা করেছিলাম, আর তাদের সকলকেই আমি সম্পূর্ণ রূপে ধ্বংস করেছিলাম।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2895,25,40,'তারাতো সেই জনপদ দিয়েই যাতায়াত করে যার উপর বর্ষিত হয়েছিল অকল্যাণের বৃষ্টি; তাহলে কি তারা এটা প্রত্যক্ষ করেনা? বস্তুতঃ তারা পুনরুত্থানের আশংকা করেনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2896,25,41,'তারা যখন তোমাকে দেখে তখন তারা তোমাকে শুধু ঠাট্টা-বিদ্রুপের পাত্র রূপে গণ্য করে এবং বলেঃ এ-ই কি সে, যাকে আল্লাহ রাসূল করে পাঠিয়েছেন!');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2897,25,42,'সেতো আমাদেরকে আমাদের দেবতাদের হতে দূরে সরিয়ে দিত যদি না আমরা তাদের আনুগত্যে দৃঢ় প্রতিষ্ঠিত থাকতাম। যখন তারা শাস্তি প্রত্যক্ষ করবে তখন তারা জানবে কে সর্বাধিক পথভ্রষ্ট।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2898,25,43,'তুমি কি দেখনা তাকে, যে তার কামনা বাসনাকে উপাস্য রূপে গ্রহণ করে? তবুও কি তুমি তার যিম্মাদার হবে?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2899,25,44,'তুমি কি মনে কর যে, তাদের অধিকাংশ শোনে ও বুঝে? তারাতো পশুরই মত; বরং তারা আরও অধম।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2900,25,45,'তুমি কি তোমার রবের প্রতি লক্ষ্য করনা, কিভাবে তিনি ছায়া সম্প্রসারিত করেন? তিনি ইচ্ছা করলে এটাকে তো স্থির রাখতে পারতেন। অনন্তর আমি সূর্যকে করেছি এর নির্দেশক। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2901,25,46,'অতঃপর আমি একে আমার দিকে ধীরে ধীরে গুটিয়ে আনি। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2902,25,47,'এবং তিনিই তোমাদের জন্য রাতকে করেছেন আবরণ স্বরূপ, বিশ্রামের জন্য তোমাদের দিয়েছেন নিদ্রা এবং সমুত্থানের জন্য দিয়েছেন দিন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2903,25,48,'তিনিই স্বীয় রাহমাতের প্রাক্কালে সুসংবাদবাহী রূপে বায়ু প্রেরণ করেন এবং আমি আকাশ হতে বিশুদ্ধ পানি বর্ষণ করি – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2904,25,49,'যদ্বারা আমি মৃত ভূ-খন্ডকে সঞ্জীবিত করি এবং আমার সৃষ্টির মধ্যের বহু জীব জন্তু ও মানুষকে তা পান করাই।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2905,25,50,'আর আমি এটা তাদের মধ্যে বিতরণ করি যাতে তারা স্মরণ করে; কিন্তু অধিকাংশ লোক শুধু অকৃতজ্ঞতাই প্রকাশ করে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2906,25,51,'আমি ইচ্ছা করলে প্রতিটি জনপদের জন্য একজন সতর্ককারী প্রেরণ করতে পারতাম। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2907,25,52,'সুতরাং তুমি কাফিরদের আনুগত্য করনা এবং তুমি কুরআনের সাহায্যে তাদের সাথে প্রবল সংগ্রাম চালিয়ে যাও। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2908,25,53,'তিনিই দুই সমুদ্রকে মিলিতভাবে প্রবাহিত করেছেন; একটি মিষ্টি, সুপেয় এবং অপরটি লবণাক্ত, খর; উভয়ের মধ্যে রেখে দিয়েছেন এক অন্তরায়, এক অনতিক্রম্য ব্যবধান। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2909,25,54,'এবং তিনিই মানুষকে সৃষ্টি করেছেন পানি হতে; অতঃপর তিনি তার বংশগত ও বৈবাহিক সম্বন্ধ স্থাপন করেছেন। তোমার রাব্ব সর্বশক্তিমান। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2910,25,55,'তারা আল্লাহর পরিবর্তে এমন কিছুর ইবাদাত করে যা তাদের উপকার করতে পারেনা, অপকারও করতে পারেনা; কাফিরতো স্বীয় রবের বিরোধী। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2911,25,56,'আমিতো তোমাকে শুধু সুসংবাদ দাতা ও সতর্ককারী রূপেই প্রেরণ করেছি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2912,25,57,'বলঃ আমি তোমাদের নিকট এ জন্য কোন প্রতিদান চাইনা, তবে যে ইচ্ছা করে সে তার রবের পথ অবলম্বন করুক। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2913,25,58,'তুমি নির্ভর কর তাঁর উপর যিনি চিরঞ্জীব, যাঁর মৃত্যু নেই এবং তাঁর সপ্রশংস পবিত্রতা ও মহিমা ঘোষণা কর। তিনি তাঁর বান্দাদের পাপ সম্পর্কে যথেষ্ট অবহিত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2914,25,59,'তিনি আকাশমন্ডলী, পৃথিবী এবং ওগুলির মধ্যবর্তী সমস্ত কিছু ছয় দিনে সৃষ্টি করেন; অতঃপর তিনি আরশে সমাসীন হন; তিনিই রাহমান। তাঁর সম্বন্ধে যে অবগত আছে তাকে জিজ্ঞেস করে দেখ। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2915,25,60,'যখন তাদেরকে বল হয়ঃ সাজদাহবনত হও ‘রাহমান’ এর প্রতি তখন তারা বলেঃ রাহমান আবার কে? তুমি কেহকে সাজদাহ করতে বললেই কি আমরা তাকে সাজদাহ করব? এতে তাদের বিমুখতাই বৃদ্ধি পায়। [সাজদাহ]');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2916,25,61,'কত মহান তিনি, যিনি নভোমন্ডলে সৃষ্টি করেছেন তারকারাজি এবং তাতে স্থাপন করেছেন প্রদীপ ও জ্যোতির্ময় চাঁদ! ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2917,25,62,'এবং যারা উপদেশ গ্রহণ করতে ও কৃতজ্ঞ হতে চায় তাদের জন্য তিনিই সৃষ্টি করেছেন রাত এবং দিনকে পরস্পরের অনুগামী রূপে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2918,25,63,'‘রাহমান’ এর বান্দা তারাই যারা নম্রভাবে চলাফিরা করে পৃথিবীতে এবং তাদেরকে যখন অজ্ঞ লোকেরা সম্বোধন করে তখন তারা বলেঃ সালাম। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2919,25,64,'এবং তারা রাত অতিবাহিত করে তাদের রবের উদ্দেশে সাজদাহবনত হয়ে ও দন্ডায়মান থেকে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2920,25,65,'এবং তারা বলেঃ হে আমাদের রাব্ব! আমাদের হতে জাহান্নামের শাস্তি বিদূরিত করুন; ওর শাস্তিতো নিশ্চিত বিনাশ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2921,25,66,'নিশ্চয়ই আশ্রয়স্থল ও বসতি হিসাবে ওটা কত নিকৃষ্ট! ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2922,25,67,'আর যখন তারা ব্যয় করে তখন তারা অপব্যয় করেনা এবং কার্পন্যও করেনা; বরং তারা আছে এতদুভয়ের মাঝে মধ্যম পন্থায়। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2923,25,68,'এবং তারা আল্লাহর সাথে কোন উপাস্যকে ডাকেনা; আল্লাহ যার হত্যা নিষিদ্ধ করেছেন, যথার্থ কারণ ছাড়া তাকে তারা হত্যা করেনা এবং ব্যভিচার করেনা; যে এগুলি করে সে শাস্তি ভোগ করবে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2924,25,69,'কিয়ামাত দিবসে তার শাস্তি দ্বিগুণ করা হবে এবং সেখানে সে স্থায়ী হবে হীন অবস্থায়। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2925,25,70,'তারা নয় - যারা তাওবাহ করে, ঈমান আনে ও সৎ কাজ করে; আল্লাহ তাদের পাপ পরিবর্তন করে দিবেন উত্তম আমলের দ্বারা; আল্লাহ ক্ষমাশীল, পরম দয়ালু। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2926,25,71,'যে ব্যক্তি তাওবাহ করে ও সৎ কাজ করে সে সম্পূর্ণ রূপে আল্লাহর অভিমুখী হয়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2927,25,72,'আর যারা মিথ্যা সাক্ষ্য দেয়না এবং যখন তারা অসার ক্রিয়া-কলাপের সম্মুখীন হয় তখন স্বীয় মর্যাদার সাথে তা পরিহার করে চলে – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2928,25,73,'এবং যারা তাদের রবের আয়াত স্মরণ করিয়ে দিলে ওর প্রতি অন্ধ এবং বধির সদৃশ আচরণ করেনা – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2929,25,74,'আর যারা প্রার্থনা করেঃ হে আমাদের রাব্ব! আমাদের জন্য এমন স্ত্রী ও সন্তান-সন্ততি দান করুন যারা আমাদের জন্য নয়ন প্রীতিকর এবং আমাদেরকে মুত্তাকীদের জন্য আদর্শ স্বরূপ করুন। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2930,25,75,'তাদেরকে প্রতিদান স্বরূপ দেয়া হবে জান্নাত, যেহেতু তারা ধৈর্যশীল। তাদেরকে সেখানে অভ্যর্থনা করা হবে অভিবাদন ও সালাম সহকারে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2931,25,76,'সেখানে তারা চিরকাল বসবাস করবে, কত উত্তম সেখানে অবস্থান ও আবাসস্থল!');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2932,25,77,'বলঃ তোমরা আমার রাব্বকে না ডাকলে তাঁর কিছুই আসে যায়না। তোমরা অস্বীকার করেছ, ফলে অচিরেই নেমে আসবে অপরিহার্য শাস্তি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2933,26,1,'তা’ সীন মীম। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2934,26,2,'এগুলি সুস্পষ্ট কিতাবের আয়াত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2935,26,3,'তারা মু’মিন হচ্ছেনা বলে তুমি হয়ত মনকষ্টে আত্মবিনাশী হয়ে পড়বে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2936,26,4,'আমি ইচ্ছা করলে আকাশ হতে তাদের নিকট এক নিদর্শন প্রেরণ করতাম, ফলে তাদের গ্রীবা বিনত হয়ে পড়ত ওর প্রতি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2937,26,5,'যখনই তাদের কাছে দয়াময়ের নিকট হতে কোন নতুন উপদেশ আসে তখনই তারা তা হতে মুখ ফিরিয়ে নেয়। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2938,26,6,'তারাতো মিথ্যা জেনেছে, সুতরাং তারা যা নিয়ে ঠাট্টা বিদ্রুপ করত তার প্রকৃত বার্তা তাদের নিকট শীঘ্রই এসে পড়বে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2939,26,7,'তারা কি পৃথিবীর দিকে দৃষ্টিপাত করেনা? আমি তাতে প্রত্যেক প্রকারের কত উৎকৃষ্ট জিনিস উদগত করেছি। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2940,26,8,'নিশ্চয়ই তাতে আছে নিদর্শন, কিন্তু তাদের অধিকাংশই মু’মিন নয়। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2941,26,9,'তোমার রাব্ব, তিনিতো পরাক্রমশালী, পরম দয়ালু। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2942,26,10,'স্মরণ কর, যখন তোমার রাব্ব মূসাকে ডেকে বললেনঃ তুমি যালিম সম্প্রদায়ের নিকট চলে যাও – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2943,26,11,'ফির‘আউনের সম্প্রদায়ের নিকট; তারা কি ভয় করেনা? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2944,26,12,'তখন সে বলেছিলঃ হে আমার রাব্ব! আমি আশংকা করি যে, তারা আমাকে অস্বীকার করবে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2945,26,13,'এবং আমার হৃদয় সংকুচিত হয়ে পড়ছে, আর আমার জিহবাতো সাবলীল নয়, সুতরাং হারুনের প্রতিও প্রত্যাদেশ পাঠান। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2946,26,14,'আমার বিরুদ্ধে তাদের এক অভিযোগ রয়েছে, আমি আশংকা করি যে, তারা আমাকে হত্যা করবে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2947,26,15,'আল্লাহ বললেনঃ না কখনই নয়, অতএব তোমরা উভয়ে আমার নিদর্শনসহ যাও, আমি তোমাদের সঙ্গে আছি শ্রবণকারী। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2948,26,16,'অতএব তোমরা উভয়ে ফির‘আউনের নিকট যাও এবং বলঃ আমরা জগতসমূহের রবের রাসূল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2949,26,17,'আমাদের সাথে যেতে দাও বানী ইসরাঈলকে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2950,26,18,'ফির‘আউন বললঃ আমরা কি তোমাকে শৈশবে আমাদের মধ্যে লালন পালন করিনি? এবং তুমিতো তোমার জীবনের বহু বছর আমাদের মধ্যে কাটিয়েছ। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2951,26,19,'তুমিতো তোমার কাজ যা করার তা করেছ; তুমি অকৃতজ্ঞ। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2952,26,20,'মূসা বললঃ আমিতো এটা করেছিলাম তখন যখন আমি অজ্ঞ ছিলাম।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2953,26,21,'অতঃপর আমি যখন তোমাদের ভয়ে ভীত হলাম তখন আমি তোমাদের নিকট হতে পালিয়ে গিয়েছিলাম; অতঃপর আমার রাব্ব আমাকে জ্ঞান দান করেছেন এবং আমাকে রাসূল করেছেন। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2954,26,22,'আর আমার প্রতি তোমার যে অনুগ্রহের কথা উল্লেখ করেছ তাতো এই যে, তুমি বানী ইসরাঈলকে দাসে পরিণত করেছ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2955,26,23,'ফির‘আউন বললঃ জগতসমূহের রাব্ব আবার কি? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2956,26,24,'মূসা বললঃ তিনি আকাশমন্ডলী ও পৃথিবী এবং এতদুভয়ের মধ্যবর্তী সব কিছুর রাব্ব, যদি তোমরা নিশ্চিত বিশ্বাসী হও। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2957,26,25,'ফির‘আউন তার পরিষদবর্গকে লক্ষ্য করে বললঃ তোমরা শুনেছ তো! ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2958,26,26,'মূসা বললঃ তিনি তোমাদের রাব্ব এবং তোমাদের পূর্ব-পুরুষদেরও রাব্ব। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2959,26,27,'ফির‘আউন বললঃ তোমাদের প্রতি প্রেরিত তোমাদের রাসূলটি নিশ্চয়ই পাগল। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2960,26,28,'মূসা বললঃ তিনি পূর্ব ও পশ্চিমের এবং এতদুভয়ের মধ্যবর্তী সব কিছুর রাব্ব, যদি তোমরা বুঝতে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2961,26,29,'ফির‘আউন বললঃ তুমি যদি আমার পরিবর্তে অন্যকে মা‘বূদ রূপে গ্রহণ কর তাহলে আমি তোমাকে অবশ্যই কারারুদ্ধ করব। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2962,26,30,'মূসা বললঃ আমি তোমার নিকট স্পষ্ট কোন নিদর্শন আনয়ন করলেও?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2963,26,31,'ফির‘আউন বললঃ তুমি যদি সত্যবাদী হও তাহলে তা উপস্থিত কর। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2964,26,32,'অতঃপর মূসা তার লাঠি নিক্ষেপ করলে তৎক্ষণাৎ তা এক সুস্পষ্ট অজগর হল। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2965,26,33,'আর মূসা হাত বের করল, তৎক্ষণাৎ তা দর্শকদের দৃষ্টিতে শুভ্র উজ্জ্বল প্রতিভাত হল। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2966,26,34,'ফির‘আউন তার পারিষদবর্গকে বললঃ এতো এক সুদক্ষ যাদুকর। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2967,26,35,'এ তোমাদেরকে তোমাদের দেশ হতে তার যাদুবলে বহিস্কার করতে চায়! এখন তোমরা কি করতে বল? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2968,26,36,'তারা বললঃ তাকে ও তার ভাইকে কিঞ্চিৎ অবকাশ দাও এবং নগরে সংগ্রাহকদেরকে পাঠাও – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2969,26,37,'যেন তারা তোমার নিকট প্রতিটি সুদক্ষ যাদুকর উপস্থিত করে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2970,26,38,'অতঃপর এক নির্ধারিত দিনে নির্দিষ্ট সময়ে যাদুকরদেরকে একত্রিত করা হল। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2971,26,39,'আর লোকদেরকে বলা হলঃ তোমরাও সমবেত হচ্ছ কি? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2972,26,40,'যেন আমরা যাদুকরদের অনুসরণ করতে পারি, যদি তারা বিজয়ী হয়। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2973,26,41,'অতঃপর যাদুকররা এসে ফির‘আউনকে বললঃ আমরা যদি বিজয়ী হই তাহলে আমাদের জন্য পুরস্কার থাকবে তো? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2974,26,42,'ফির‘আউন বললঃ হ্যাঁ, তখন তোমরা অবশ্যই আমার নিকটজনদের অন্তর্ভুক্ত হবে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2975,26,43,'মূসা তাদেরকে বললঃ তোমাদের যা নিক্ষেপ করার তা নিক্ষেপ কর। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2976,26,44,'অতঃপর তারা তাদের রজ্জু ও লাঠি নিক্ষেপ করল এবং তারা বললঃ ফির‘আউনের শপথ! আমরাই বিজয়ী হব। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2977,26,45,'অতঃপর মূসা তার লাঠি নিক্ষেপ করল; সহসা ওটা তাদের অলীক সৃষ্টিগুলিকে গ্রাস করতে লাগল। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2978,26,46,'তখন যাদুকরেরা সাজদাহবনত হয়ে পড়ল। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2979,26,47,'তারা বললঃ আমরা ঈমান আনলাম জগতসমূহের রবের প্রতি – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2980,26,48,'যিনি মূসা ও হারুনেরও রাব্ব। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2981,26,49,'ফির‘আউন বললঃ আমি তোমাদেরকে অনুমতি দেয়ার পূর্বেই তোমরা তাতে বিশ্বাস করলে? এইতো তোমাদের প্রধান যে তোমাদেরকে যাদু শিক্ষা দিয়েছে; শীঘ্রই তোমরা এর পরিণাম জানবে; আমি অবশ্যই তোমাদের হাত এবং তোমাদের পা বিপরীত দিক হতে কেটে ফেলব এবং তোমাদের সবাইকে শুলবিদ্ধ করবই। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2982,26,50,'তারা বললঃ কোন ক্ষতি নেই, আমরা আমাদের রবের নিকট প্রত্যাবর্তন করব।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2983,26,51,'আমরা আশা করি যে, আমাদের রাব্ব আমাদের অপরাধ ক্ষমা করবেন, কারণ আমরা মু’মিনদের মধ্যে অগ্রণী। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2984,26,52,'আমি মূসার প্রতি অহী করেছিলাম এই মর্মেঃ আমার বান্দাদেরকে নিয়ে রাতে বহির্গত হও; তোমাদেরতো পশ্চাদ্ধাবন করা হবে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2985,26,53,'অতঃপর ফির‘আউন শহরে শহরে লোক সংগ্রহকারী পাঠাল – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2986,26,54,'এই বলেঃ এরাতো ক্ষুদ্র একটি দল। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2987,26,55,'এবং তারা আমাদের ক্রোধের সৃষ্টি করেছে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2988,26,56,'এবং আমরাতো সদা সতর্ক একটি দল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2989,26,57,'পরিণামে আমি ফির‘আউন গোষ্ঠিকে বহিস্কৃত করলাম তাদের উদ্যানরাজি ও প্রস্রবণ হতে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2990,26,58,'এবং ধন ভান্ডার ও সুরম্য সৌধমালা হতে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2991,26,59,'এরূপই ঘটেছিল এবং বানী ইসরাঈলকে আমি করেছিলাম এ সমুদয়ের অধিকারী। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2992,26,60,'তারা সূর্যোদয় কালে তাদের পশ্চাতে এসে পড়ল। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2993,26,61,'অতঃপর যখন দু’ দল পরস্পরকে দেখল তখন মূসার সঙ্গীরা বললঃ আমরাতো ধরা পড়ে যাচ্ছি! ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2994,26,62,'মূসা বললঃ কক্ষণই নয়। আমার সঙ্গে আছেন আমার রাব্ব; সত্ত্বর তিনি আমাকে পথ নির্দেশ করবেন। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2995,26,63,'অতঃপর আমি মূসার প্রতি অহী করলামঃ তোমার লাঠি দ্বারা সমুদ্রে আঘাত কর, ফলে তা বিভক্ত হয়ে প্রত্যেক ভাগ বিশাল পর্বত সদৃশ হয়ে গেল। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2996,26,64,'আমি সেখানে উপনীত করলাম অপর দলটিকে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2997,26,65,'এবং আমি উদ্ধার করলাম মূসা ও তার সঙ্গী সকলকে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2998,26,66,'অতঃপর নিমজ্জিত করলাম অপর দলটিকে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (2999,26,67,'এতে অবশ্যই নিদর্শন রয়েছে, কিন্তু তাদের অধিকাংশই মু’মিন নয়। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3000,26,68,'এবং তোমার রাব্ব - তিনিতো পরাক্রমশালী, পরম দয়ালু। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3001,26,69,'তাদের নিকট ইবরাহীমের বৃত্তান্ত বর্ণনা কর। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3002,26,70,'সে যখন তার পিতা ও তার সম্প্রদায়কে বলেছিলঃ তোমরা কিসের ইবাদাত কর? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3003,26,71,'তারা বললঃ আমরা মূর্তি পূজা করি এবং আমরা নিষ্ঠার সাথে তাদের পূজায় রত থাকব। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3004,26,72,'সে বললঃ তোমরা প্রার্থনা করলে তারা কি শোনে? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3005,26,73,'অথবা তারা কি তোমাদের উপকার কিংবা অপকার করতে পারে? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3006,26,74,'তারা বললঃ না, তবে আমরা আমাদের পিতৃ-পুরুষদেরকে এরূপই করতে দেখেছি। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3007,26,75,'সে বললঃ তোমরা কি তার সম্বন্ধে ভেবে দেখেছ যার পূজা করছ – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3008,26,76,'তোমরা এবং তোমাদের পূর্ব-পুরুষরা? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3009,26,77,'তারা সবাই আমার শত্রু, জগতসমূহের রাব্ব ব্যতীত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3010,26,78,'তিনি আমাকে সৃষ্টি করেছেন, তিনিই আমাকে পথ প্রদর্শন করেন। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3011,26,79,'তিনিই আমাকে দান করেন আহার্য ও পানীয়। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3012,26,80,'এবং অসুস্থ হলে তিনিই আমাকে রোগমুক্ত করেন। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3013,26,81,'আর তিনিই আমার মৃত্যু ঘটাবেন, অতঃপর আমাকে পুনরুজ্জীবিত করবেন। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3014,26,82,'এবং আশা করি, তিনি কিয়ামাত দিবসে আমার অপরাধসমূহ ক্ষমা করে দিবেন। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3015,26,83,'হে আমার রাব্ব! আমাকে প্রজ্ঞা দান করুন এবং সৎ কর্মপরায়ণদের সাথে আমাকে মিলিত করুন। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3016,26,84,'আমাকে পরবর্তীদের মধ্যে সত্যভাষী করুন!');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3017,26,85,'এবং আমাকে সুখময় জান্নাতের অধিকারীদের অন্তর্ভুক্ত করুন! ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3018,26,86,'আর আমার পিতাকে ক্ষমা করুন, সেতো পথভ্রষ্টদের অন্তর্ভুক্ত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3019,26,87,'এবং আমাকে লাঞ্ছিত করবেননা পুনরুত্থান দিবসে – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3020,26,88,'যেদিন ধন-সম্পদ ও সন্তান-সন্ততি কোন কাজে আসবেনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3021,26,89,'সেদিন উপকৃত হবে শুধু সে, যে আল্লাহর নিকট আসবে বিশুদ্ধ অন্তঃকরণ নিয়ে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3022,26,90,'মুত্তাকীদের নিকটবর্তী করা হবে জান্নাত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3023,26,91,'আর পথভ্রষ্টদের জন্য উম্মোচিত করা হবে জাহান্নাম। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3024,26,92,'তাদেরকে বলা হবেঃ তারা কোথায়, তোমরা যাদের ইবাদাত করতে – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3025,26,93,'আল্লাহর পরিবর্তে? তারা কি তোমাদের সাহায্য করতে পারে অথবা তারা কি আত্মরক্ষা করতে সক্ষম? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3026,26,94,'অতঃপর তাদেরকে ও পথভ্রষ্টদেরকে জাহান্নামে নিক্ষেপ করা হবে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3027,26,95,'এবং ইবলীস বাহিনীর সবাইকেও। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3028,26,96,'তারা সেখানে বিতর্কে লিপ্ত হয়ে বলবে – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3029,26,97,'আল্লাহর শপথ! আমরাতো স্পষ্ট বিভ্রান্তিতেই ছিলাম। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3030,26,98,'যখন আমরা তোমাদেরকে জগতসমূহের রবের সমকক্ষ মনে করতাম। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3031,26,99,'আমাদেরকে দুস্কৃতকারীরাই বিভ্রান্ত করেছিল। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3032,26,100,'পরিণামে আমাদের কোন সুপারিশকারী নেই। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3033,26,101,'কোন সহৃদয় বন্ধুও নেই। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3034,26,102,'হায়! যদি আমাদের একবার প্রত্যাবর্তনের সুযোগ ঘটত তাহলে আমরা মু’মিনদের অন্তর্ভুক্ত হতাম!');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3035,26,103,'এতে অবশ্যই নিদর্শন রয়েছে, কিন্তু তাদের অধিকাংশ মু’মিন নয়। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3036,26,104,'তোমার রাব্ব, তিনিতো পরাক্রমশালী, পরম দয়ালু। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3037,26,105,'নূহের সম্প্রদায় রাসূলদের প্রতি মিথ্যা আরোপ করেছিল। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3038,26,106,'যখন তাদের ভাই নূহ তাদেরকে বললঃ তোমরা কি সাবধান হবেনা? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3039,26,107,'আমিতো তোমাদের জন্য এক বিশ্বস্ত রাসূল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3040,26,108,'অতএব আল্লাহকে ভয় কর এবং আমার আনুগত্য কর। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3041,26,109,'আমি তোমাদের নিকট এ জন্য কোন প্রতিদান চাইনা; আমার পুরস্কারতো জগতসমূহের রবের নিকটই রয়েছে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3042,26,110,'সুতরাং আল্লাহকে ভয় কর এবং আমার আনুগত্য কর। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3043,26,111,'তারা বললঃ আমরা কি তোমার প্রতি বিশ্বাস স্থাপন করব, অথচ ইতর লোকেরা তোমার অনুসরণ করছে? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3044,26,112,'নূহ বললঃ তারা কি কাজ করছে তা জানা আমার কি দরকার? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3045,26,113,'তাদের হিসাব গ্রহণতো আমার রবেরই কাজ; যদি তোমরা বুঝতে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3046,26,114,'মু’মিনদেরকে তাড়িয়ে দেয়া আমার কাজ নয়,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3047,26,115,'আমিতো শুধু একজন স্পষ্ট সতর্ককারী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3048,26,116,'তারা বললঃ হে নূহ! তুমি যদি নিবৃত্ত না হও তাহলে তুমি অবশ্যই প্রস্তরাঘাতে নিহতদের অন্তর্ভুক্ত হবে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3049,26,117,'নূহ বললঃ হে আমার রাব্ব! আমার সম্প্রদায়তো আমাকে অস্বীকার করছে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3050,26,118,'সুতরাং আমার ও তাদের মধ্যে স্পষ্ট মীমাংসা করে দিন এবং আমাকে এবং আমার সাথে যে সব মু’মিন রয়েছে তাদেরকে রক্ষা করুন। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3051,26,119,'অতঃপর আমি তাকে ও তার সঙ্গে যারা ছিল তাদেরকে রক্ষা করলাম বোঝাই করা নৌ-যানে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3052,26,120,'অতঃপর অবশিষ্ট সবাইকে নিমজ্জিত করলাম। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3053,26,121,'এতে অবশ্যই রয়েছে নিদর্শন, কিন্তু তাদের অধিকাংশই বিশ্বাসী নয়। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3054,26,122,'এবং তোমার রাব্ব, তিনিতো পরাক্রমশালী, দয়ালু। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3055,26,123,'‘আদ সম্প্রদায় রাসূলদেরকে মিথ্যা প্রতিপন্ন  করেছিল। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3056,26,124,'যখন তাদের ভাই হুদ তাদেরকে বললঃ তোমরা কি সাবধান হবেনা?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3057,26,125,'আমি তোমাদের জন্য এক বিশ্বস্ত রাসূল। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3058,26,126,'অতএব আল্লাহকে ভয় কর এবং আমার আনুগত্য কর। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3059,26,127,'আমি তোমাদের নিকট এর জন্য কোন প্রতিদান চাইনা, আমার পুরস্কারতো জগতসমূহের রবের নিকট রয়েছে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3060,26,128,'তোমরা কি প্রতিটি উঁচু স্থানে নিরর্থক স্মৃতিসৌধ/ভাস্কর্য নির্মাণ করছ? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3061,26,129,'আর তোমরা প্রাসাদ নির্মাণ করছ এই মনে করে যে, তোমরা চিরস্থায়ী হবে?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3062,26,130,'এবং যখন তোমরা আঘাত হান তখন আঘাত হেনে থাক কঠোরভাবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3063,26,131,'তোমরা আল্লাহকে ভয় কর এবং আমার আনুগত্য কর। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3064,26,132,'ভয় কর তাঁকে, যিনি তোমাদেরকে দিয়েছেন সেই সমূদয় জ্ঞান যা তোমরা জান।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3065,26,133,'তোমাদের দিয়েছেন পশু-সম্পদ ও সন্তান-সন্ততি। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3066,26,134,'উদ্যান ও প্রস্রবন। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3067,26,135,'আমি তোমাদের জন্য আশংকা করি মহা দিনের শাস্তির। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3068,26,136,'তারা বললঃ তুমি উপদেশ দাও অথবা না’ই দাও, উভয়ই আমাদের জন্য সমান। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3069,26,137,'এটাতো পূর্ববর্তীদেরই স্বভাব। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3070,26,138,'আমরা শাস্তি প্রাপ্তদের অন্তর্ভুক্ত নই।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3071,26,139,'অতঃপর তারা তাকে প্রত্যাখ্যান করল এবং আমি তাদের ধ্বংস করলাম। এতে অবশ্যই রয়েছে নিদর্শন; কিন্তু তাদের অধিকাংশই মু’মিন নয়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3072,26,140,'এবং তোমার রাব্ব পরাক্রমশালী, পরম দয়ালু। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3073,26,141,'ছামূদ সম্প্রদায় রাসূলদের মিথ্যা প্রতিপন্ন করেছিল। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3074,26,142,'যখন তাদের ভাই সালিহ তাদেরকে বললঃ তোমরা কি সাবধান হবেনা? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3075,26,143,'আমিতো তোমাদের জন্য এক বিশ্বস্ত রাসূল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3076,26,144,'অতএব আল্লাহকে ভয় কর এবং আমার আনুগত্য কর। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3077,26,145,'আমি তোমাদের নিকট এ জন্য কোন প্রতিদান চাইনা, আমার পুরস্কারতো জগতসমূহের রবের নিকটই রয়েছে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3078,26,146,'তোমাদেরকে কি এ জগতে ভোগ বিলাসের মধ্যে নিরাপদে ছেড়ে দেয়া হবে – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3079,26,147,'উদ্যানে, প্রস্রবণে – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3080,26,148,'ও শস্যক্ষেতে এবং সুকোমল গুচ্ছ বিশিষ্ট খেজুর বাগানে? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3081,26,149,'তোমরাতো নৈপুণ্যের সাথে পাহাড় কেটে গৃহ নির্মাণ করেছ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3082,26,150,'তোমরা আল্লাহকে ভয় কর এবং আমার আনুগত্য কর। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3083,26,151,'এবং সীমা লংঘনকারীদের আদেশ মান্য করনা – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3084,26,152,'যারা পৃথিবীতে অশান্তি সৃষ্টি করে এবং শান্তি স্থাপন করেনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3085,26,153,'তারা বললঃ তুমিতো যাদুগ্রস্তদের অন্যতম। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3086,26,154,'তুমিতো আমাদের মত একজন মানুষ, অতএব তুমি যদি সত্যবাদী হও তাহলে একটি নিদর্শন উপস্থিত কর। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3087,26,155,'সালিহ বললঃ এই যে উষ্ট্রী, এর জন্য রয়েছে পানি পানের এবং তোমাদের জন্য রয়েছে পানি পানের পালা নির্ধারিত এক এক দিনে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3088,26,156,'এবং তোমরা ওর কোন অনিষ্ট সাধন করনা; তাহলে মহা দিনের শাস্তি তোমাদের উপর আপতিত হবে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3089,26,157,'কিন্তু তারা ওকে বধ করল, পরিণামে তারা অনুতপ্ত হল। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3090,26,158,'অতঃপর শাস্তি তাদেরকে গ্রাস করল; এতে অবশ্যই নিদর্শন রয়েছে। কিন্তু তাদের অধিকাংশই মু’মিন নয়। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3091,26,159,'তোমার রাব্ব, তিনি পরাক্রমশালী, পরম দয়ালু। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3092,26,160,'লূতের সম্প্রদায় রাসূলদেরকে অস্বীকার করেছিল। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3093,26,161,'যখন তাদের ভাই লূত তাদেরকে বললঃ তোমরা কি সাবধান হবেনা? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3094,26,162,'আমিতো তোমাদের জন্য এক বিশ্বস্ত রাসূল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3095,26,163,'সুতরাং তোমরা আল্লাহকে ভয় কর এবং আমার আনুগত্য কর। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3096,26,164,'আমি এ জন্য তোমাদের কাছে কোন প্রতিদান চাইনা, আমার পুরস্কারতো জগতসমূহের রবের নিকটই রয়েছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3097,26,165,'সৃষ্টির মধ্যে তোমরা কি শুধু পুরুষের সাথেই উপগত হবে? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3098,26,166,'আর তোমাদের রাব্ব তোমাদের জন্য যে স্ত্রীলোক সৃষ্টি করেছেন তাদেরকে তোমরা বর্জন করে থাক, বরং তোমরা সীমালংঘনকারী সম্প্রদায়। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3099,26,167,'তারা বললঃ হে লূত! তুমি যদি নিবৃত্ত না হও তাহলে অবশ্যই তুমি নির্বাসিত হবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3100,26,168,'লূত বলল, আমি তোমাদের এই কাজকে ঘৃণা করি। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3101,26,169,'হে আমার রাব্ব! আমাকে ও আমার পরিবারবর্গকে, তারা যা করে তা হতে রক্ষা কর। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3102,26,170,'অতঃপর আমি তাকে এবং তার পরিবার পরিজনের সবাইকে রক্ষা করলাম – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3103,26,171,'এক বৃদ্ধা ব্যতীত, সে ছিল পশ্চাতে অবস্থানকারীদের অন্তর্ভুক্ত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3104,26,172,'অতঃপর অন্যদেরকে ধ্বংস করলাম। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3105,26,173,'তাদের উপর শাস্তিমূলক বৃষ্টি বর্ষণ করেছিলাম, এবং ভীতি প্রদর্শনের জন্য এই বৃষ্টি ছিল কত নিকৃষ্ট! ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3106,26,174,'এতে অবশ্যই নিদর্শন রয়েছে, কিন্তু তাদের অধিকাংশই মু’মিন নয়। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3107,26,175,'তোমার রাব্ব, তিনিতো পরাক্রমশালী, পরম দয়ালু। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3108,26,176,'আইকাবাসীরা রাসূলদেরকে অস্বীকার করেছিল – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3109,26,177,'যখন শু‘আইব তাদেরকে বলেছিলঃ তোমরা কি সাবধান হবেনা? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3110,26,178,'আমিতো তোমাদের জন্য একজন বিশ্বস্ত রাসূল। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3111,26,179,'সুতরাং তোমরা আল্লাহকে ভয় কর এবং আমার আনুগত্য কর। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3112,26,180,'আমি তোমাদের নিকট এ জন্য কোন প্রতিদান চাইনা; আমার পুরস্কারতো জগতসমূহের রবের নিকটই রয়েছে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3113,26,181,'তোমরা মাপে পূর্ণ মাত্রায় দিবে; যারা মাপে কমতি করে তাদের অন্তর্ভুক্ত হয়োনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3114,26,182,'এবং তোমরা ওজন করবে সঠিক দাঁড়িপাল্লায়। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3115,26,183,'লোকদেরকে তাদের প্রাপ্য বস্তু কম দিবেনা এবং পৃথিবীতে বিপর্যয় সৃষ্টি করে ফিরনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3116,26,184,'এবং তোমরা ভয় কর তাঁকে, যিনি তোমাদেরকে এবং তোমাদের পূর্বে যারা গত হয়েছে তাদেরকে সৃষ্টি করেছেন। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3117,26,185,'তারা বললঃ তুমিতো যাদুগ্রস্তদের অন্তর্ভুক্ত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3118,26,186,'তুমি আমাদেরই মত একজন মানুষ বলে আমরা মনে করি, তুমি মিথ্যাবাদীদের অন্তর্ভুক্ত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3119,26,187,'তুমি যদি সত্যবাদী হও তাহলে আকাশের একটি খন্ড আমাদের উপর ফেলে দাও। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3120,26,188,'সে বললঃ আমার রাব্ব ভাল জানেন, যা তোমরা কর। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3121,26,189,'অতঃপর তারা তাকে প্রত্যাখ্যান করল, পরে তাদেরকে মেঘাচ্ছন্ন দিনের শাস্তি গ্রাস করল; এটাতো ছিল এক ভীষণ দিনের শাস্তি। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3122,26,190,'এতে অবশ্যই রয়েছে নিদর্শন, কিন্তু তাদের অধিকাংশই মু’মিন নয়। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3123,26,191,'তোমার রাব্ব! তিনিতো পরাক্রমশালী, পরম দয়ালু। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3124,26,192,'নিশ্চয়ই ইহা (আল কুরআন) জগতসমূহের রাব্ব হতে অবতারিত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3125,26,193,'জিবরাঈল ইহা নিয়ে অবতরণ করেছে –');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3126,26,194,'তোমার হৃদয়ে, যাতে তুমি সতর্ককারী হতে পার। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3127,26,195,'অবতীর্ণ করা হয়েছে সুস্পষ্ট আরাবী ভাষায়। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3128,26,196,'পূর্ববর্তী কিতাবসমূহে অবশ্যই এর উল্লেখ রয়েছে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3129,26,197,'বানী ইসরাঈলের পন্ডিতরা এটা অবগত আছে, এটা কি তাদের জন্য নিদর্শন নয়? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3130,26,198,'আমি যদি ইহা কোন আজমীর (ভিন্ন ভাষী) প্রতি অবতীর্ণ করতাম – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3131,26,199,'এবং ওটা সে তাদের নিকট পাঠ করত, তাহলে তারা তাতে ঈমান আনতনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3132,26,200,'এভাবেই আমি পাপীদের অন্তরে অবিশ্বাস সঞ্চার করেছি। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3133,26,201,'তারা এতে ঈমান আনবেনা যতক্ষণ না তারা মর্মন্তদ শাস্তি প্রত্যক্ষ করে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3134,26,202,'অতঃপর এটা তাদের নিকট এসে পড়বে আকস্মিকভাবে, তারা কিছুই বুঝতে পারবেনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3135,26,203,'তখন তারা বলবেঃ আমাদেরকে কি অবকাশ দেয়া হবে? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3136,26,204,'তারা কি তাহলে আমার শাস্তি ত্বরান্বিত করতে চায়? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3137,26,205,'তুমি চিন্তা করে দেখ, যদি আমি তাদেরকে দীর্ঘকাল ভোগ-বিলাস করতে দিই,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3138,26,206,'অতঃপর তাদেরকে যে বিষয়ে সতর্ক করা হয়েছিল তা তাদের নিকট এসে পড়ে – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3139,26,207,'তখন তাদের ভোগ-বিলাসের উপকরণ তাদের কোন কাজে আসবে কি? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3140,26,208,'আমি এমন কোন জনপদ ধ্বংস করিনি যার জন্য সতর্ককারী ছিলনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3141,26,209,'এটা উপদেশ স্বরূপ, আর আমি অত্যাচারী নই। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3142,26,210,'শাইতানরা ইহাসহ অবতীর্ণ হয়নি। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3143,26,211,'তারা এ কাজের যোগ্য নয় এবং তারা এর সামর্থ্যও রাখেনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3144,26,212,'তাদেরকে শ্রবণের সুযোগ হতে দূরে রাখা হয়েছে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3145,26,213,'অতএব তুমি অন্য কোন মা‘বূদকে আল্লাহর সাথে ডেকোনা, তাহলে তুমি শাস্তি প্রাপ্তদের অন্তর্ভুক্ত হবে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3146,26,214,'তোমার নিকটতম আত্মীয়বর্গকে সতর্ক করে দাও। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3147,26,215,'এবং যারা তোমার অনুসরণ করে, সেই সব মু’মিনের প্রতি বিনয়ী হও। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3148,26,216,'তারা যদি তোমার অবাধ্যতা করে তাহলে তুমি বলঃ তোমরা যা কর তার জন্য আমি দায়ী নই। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3149,26,217,'তুমি নির্ভর কর পরাক্রমশালী, পরম দয়ালু আল্লাহর উপর। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3150,26,218,'যিনি তোমাকে দেখেন যখন তুমি দন্ডায়মান হও (সালাতের জন্য)।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3151,26,219,'এবং দেখেন সাজদাহকারীদের সাথে তোমার উঠা বসা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3152,26,220,'তিনিতো সর্বশ্রোতা, সর্বজ্ঞ। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3153,26,221,'তোমাদেরকে কি জানাব, কার নিকট শাইতানরা অবতীর্ণ হয়?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3154,26,222,'তারাতো অবতীর্ণ হয় প্রত্যেক চরম মিথ্যাবাদী ও পাপীর নিকট।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3155,26,223,'তারা কান পেতে থাকে এবং তাদের অধিকাংশই মিথ্যাবাদী। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3156,26,224,'এবং কবিদের অনুসরণ করে তারা, যারা বিভ্রান্ত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3157,26,225,'তুমি কি দেখনা, তারা বিভ্রান্ত হয়ে প্রত্যেক উপত্যকায় ঘুরে বেড়ায়?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3158,26,226,'এবং যা তারা করেনা, তা বলে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3159,26,227,'কিন্তু তারা ব্যতীত, যারা ঈমান আনে ও সৎকার্য করে এবং আল্লাহকে বার বার স্মরণ করে ও অত্যাচারিত হবার পর প্রতিশোধ গ্রহণ করে। অত্যাচারীরা শীঘ্রই জানবে তাদের গন্তব্যস্থল কোথায়?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3160,27,1,'তা সীন; এগুলি আয়াত আল-কুরআনের এবং সুস্পষ্ট কিতাবের। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3161,27,2,'পথ নির্দেশ ও সুসংবাদ মু’মিনদের জন্য। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3162,27,3,'যারা সালাত কায়েম করে ও যাকাত প্রদান করে এবং যারা আখিরাতে নিশ্চিত বিশ্বাসী। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3163,27,4,'যারা আখিরাতে বিশ্বাস করেনা তাদের দৃষ্টিতে তাদের কাজকে আমি শোভন করেছি, ফলে তারা বিভ্রান্তিতে ঘুরে বেড়ায়। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3164,27,5,'এদেরই জন্য রয়েছে কঠিন শাস্তি এবং এরাই আখিরাতে সর্বাধিক ক্ষতিগ্রস্ত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3165,27,6,'নিশ্চয়ই তোমাকে আল কুরআন দেয়া হয়েছে প্রজ্ঞাময়, সর্বজ্ঞের নিকট হতে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3166,27,7,'স্মরণ কর সেই সময়ের কথা, যখন মূসা তার পরিবারবর্গকে বলেছিলঃ আমি আগুন দেখেছি, সত্ত্বর আমি সেখান হতে তোমাদের জন্য কোন খবর আনব অথবা তোমাদের জন্য আনব জ্বলন্ত অঙ্গার, যাতে তোমরা আগুন পোহাতে পার। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3167,27,8,'অতঃপর সে যখন ওর নিকট এলো তখন ঘোষিত হলঃ ধন্য সেই ব্যক্তি যে আছে এই আগুনের মধ্যে এবং যারা আছে ওর চতুস্পার্শ্বে। জগতসমূহের রাব্ব আল্লাহ পবিত্র ও মহিমান্বিত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3168,27,9,'হে মূসা! আমিতো আল্লাহ পরাক্রমশালী, প্রজ্ঞাময়। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3169,27,10,'তুমি তোমার লাঠি নিক্ষেপ কর, অতঃপর যখন সে ওটাকে সর্পের ন্যায় ছুটাছুটি করতে দেখল তখন সে পিছনের দিকে ছুটতে লাগল এবং ফিরেও তাকালনা। বলা হলঃ হে মূসা! ভীত হয়োনা, নিশ্চয়ই আমি এমন, আমার সান্নিধ্যে রাসূলগণ ভয় পায়না। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3170,27,11,'তবে যারা যুল্‌ম করার পর মন্দ কাজের পরিবর্তে সৎ কাজ করে তাদের প্রতি আমি ক্ষমাশীল, পরম দয়ালু। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3171,27,12,'তোমার হাত তোমার বক্ষপার্শ্বে বস্ত্রের মধ্যে প্রবেশ করাও। এটা বের হয়ে আসবে শুভ্র নির্দোষ হয়ে; এটা ফির‘আউন ও তার সম্প্রদায়ের নিকট আনীত নয়টি নিদর্শনের অন্তর্গত; তারাতো সত্যত্যাগী সম্প্রদায়। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3172,27,13,'অতঃপর যখন তাদের নিকট আমার স্পষ্ট আয়াত এলো তখন তারা বললঃ এটাতো সুস্পষ্ট যাদু। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3173,27,14,'তারা অন্যায় ও উদ্ধতভাবে নিদর্শনগুলি প্রত্যাখ্যান করল, যদিও তাদের অন্তর ঐগুলিকে সত্য বলে গ্রহণ করেছিল। দেখ, বিপর্যয় সৃষ্টিকারীদের পরিণাম কি হয়েছিল! ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3174,27,15,'আমি অবশ্যই দাউদকে ও সুলাইমানকে জ্ঞান দান করেছিলাম এবং তারা বলেছিলঃ প্রশংসা আল্লাহর যিনি আমাদেরকে তাঁর বহু মু’মিন বান্দাদের উপর শ্রেষ্ঠত্ব দান করেছেন। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3175,27,16,'সুলাইমান হয়েছিল দাউদের উত্তরাধিকারী এবং সে বলেছিলঃ হে লোক সকল! আমাকে পক্ষীকুলের ভাষা শিক্ষা দেয়া হয়েছে এবং আমাকে সব কিছু হতে প্রদান করা হয়েছে; এটা অবশ্যই সুস্পষ্ট অনুগ্রহ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3176,27,17,'সুলাইমানের সামনে সমবেত করা হল তার বাহিনীকে, জিন, মানুষ ও পক্ষীকুলকে এবং তাদেরকে বিন্যস্ত করা হল বিভিন্ন বুহ্যে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3177,27,18,'যখন তারা পিপীলিকা অধ্যুষিত উপত্যকায় পৌঁছল তখন এক পিপীলিকা বললঃ হে পিপীলিকা বাহিনী! তোমরা তোমাদের গৃহে প্রবেশ কর, যেন সুলাইমান এবং তার বাহিনী তাদের অজ্ঞাতসারে তোমাদেরকে পদতলে পিষে না ফেলে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3178,27,19,'সুলাইমান ওর উক্তিতে মৃদু হাস্য করল এবং বললঃ হে আমার রাব্ব! আপনি আমাকে সামর্থ্য দিন যাতে আমি আপনার প্রতি কৃতজ্ঞতা প্রকাশ করতে পারি, আমার প্রতি ও আমার মাতা-পিতার প্রতি আপনি যে অনুগ্রহ করেছেন তজ্জন্য এবং যাতে আমি সৎ কাজ করতে পারি, যা আপনি পছন্দ করেন এবং আপনার অনুগ্রহে আমাকে আপনার সৎকর্মপরায়ণ বান্দাদের শ্রেণীভুক্ত করুন। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3179,27,20,'সুলাইমান পক্ষীকুলের সন্ধান নিল এবং বললঃ ব্যাপার কি, হুদহুদকে দেখছিনা যে! সে অনুপস্থিত না কি? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3180,27,21,'সে উপযুক্ত কারণ দর্শাতে না পারলে আমি অবশ্যই তাকে কঠিন শাস্তি দিব অথবা যবাহ করব। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3181,27,22,'অনতি বিলম্বে হুদহুদ এসে পড়ল এবং বললঃ আপনি যা অবগত নন আমি তা অবগত হয়েছি এবং ‘সাবা’ হতে সুনিশ্চিত সংবাদ নিয়ে এসেছি। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3182,27,23,'আমি এক নারীকে দেখলাম যে তাদের উপর রাজত্ব করছে; তাকে সব কিছু দেয়া হয়েছে এবং তার আছে এক বিরাট সিংহাসন। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3183,27,24,'আমি তাকে ও তার সম্প্রদায়কে দেখলাম, তারা আল্লাহর পরিবর্তে সূর্যকে সাজদাহ করছে; শাইতান তাদের কার্যাবলী তাদের নিকট শোভন করেছে এবং তাদেরকে সৎ পথ হতে নিবৃত্ত করেছে; ফলে তারা সৎ পথ প্রাপ্ত হয়না –');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3184,27,25,'তারা নিবৃত্ত রয়েছে আল্লাহকে সাজদাহ করা হতে, যিনি আকাশমন্ডলী ও পৃথিবীর লুকায়িত বস্তুকে প্রকাশ করেন, এবং যিনি জানেন যা তোমরা গোপন কর এবং যা তোমরা প্রকাশ কর। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3185,27,26,'আল্লাহ, তিনি ছাড়া কোন মা‘বূদ নেই। তিনি মহাআরশের অধিপতি। [সাজদাহ]');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3186,27,27,'সুলাইমান বললঃ আমি দেখব, তুমি সত্য বলেছ, না কি তুমি মিথ্যাবাদী?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3187,27,28,'তুমি যাও আমার এই পত্র নিয়ে এবং এটা তাদের নিকট অর্পন কর; অতঃপর তাদের নিকট হতে দূরে সরে থেক এবং লক্ষ্য কর তাদের প্রতিক্রিয়া কি? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3188,27,29,'সেই নারী বললঃ হে পরিষদবর্গ! আমাকে এক সম্মানিত পত্র দেয়া হয়েছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3189,27,30,'ইহা সুলাইমানের নিকট হতে এবং ইহা দয়াময়, পরম দয়ালু আল্লাহর নামে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3190,27,31,'অহমিকা বশে আমাকে অমান্য করনা, এবং আনুগত্য স্বীকার করে আমার নিকট উপস্থিত হও।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3191,27,32,'সেই নারী বললঃ হে পরিষদবর্গ! আমার এই সমস্যায় তোমাদের অভিমত দাও; আমি যা সিদ্ধান্ত নিই তাতো তোমাদের উপস্থিতিতেই নিই। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3192,27,33,'তারা বললঃ আমরাতো শক্তিশালী ও কঠোর যোদ্ধা; তবে সিদ্ধান্ত গ্রহণের ক্ষমতা আপনারই। কি আদেশ করবেন তা আপনি ভেবে দেখুন। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3193,27,34,'সে বললঃ রাজা-বাদশাহরা যখন কোন জনপদে প্রবেশ করে তখন ওকে বিপর্যস্ত করে দেয় এবং সেখানকার মর্যাদাবান ব্যক্তিদেরকে অপদস্থ করে; এরাও এ রূপই করবে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3194,27,35,'আমি তাদের নিকট উপঢৌকন পাঠাচ্ছি, দেখি দূতেরা কি বার্তা নিয়ে ফিরে আসে!');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3195,27,36,'অতঃপর যখন দূত সুলাইমানের নিকট এলো তখন সুলাইমান বললঃ তোমরা আমাকে ধন-সম্পদ দিয়ে কি সাহায্য করবে? আল্লাহ আমাকে যা দিয়েছেন তা তোমাদেরকে যা দিয়েছেন তা হতে উৎকৃষ্ট। অথচ তোমরা তোমাদের উপঢৌকন নিয়ে আনন্দ বোধ করছ। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3196,27,37,'তাদের নিকট ফিরে যাও, আমি অবশ্যই তাদের বিরুদ্ধে নিয়ে আসব এক সৈন্যবাহিনী যার মুকাবিলা করার শক্তি তাদের নেই। আমি অবশ্যই তাদেরকে সেখান হতে বহিস্কার করব লাঞ্ছিতভাবে এবং তারা হবে অপমানিত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3197,27,38,'সুলাইমান আরও বললঃ হে আমার পরিষদবর্গ! তারা আমার নিকট এসে আত্মসমর্পন করার পূর্বে তোমাদের মধ্যে কে তার সিংহাসন আমার নিকট নিয়ে আসবে? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3198,27,39,'এক শক্তিশালী জিন বললঃ আপনি আপনার স্থান হতে উঠার পূর্বে আমি ওটা আপনার নিকট এনে দিব এবং এ ব্যাপারে আমি অবশ্যই ক্ষমতাবান, বিশ্বস্ত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3199,27,40,'কিতাবের জ্ঞান যার ছিল সে বললঃ আপনি চোখের পলক ফেলার পূর্বেই আমি ওটা আপনাকে এনে দিব। সুলাইমান যখন ওটা সামনে রক্ষিত অবস্থায় দেখল তখন সে বললঃ এটা আমার রবের অনুগ্রহ যাতে তিনি আমাকে পরীক্ষা করতে পারেন যে, আমি কৃতজ্ঞ না অকৃতজ্ঞ; যে কৃতজ্ঞতা প্রকাশ করে সে তা করে তার নিজের কল্যাণের জন্য এবং যে অকৃতজ্ঞ হয় সে জেনে রাখুক যে, আমার রাব্ব অভাবমুক্ত, মহানুভব। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3200,27,41,'সুলাইমান বললঃ তার সিংহাসনের আকৃতি বদলে দাও; দেখি সে সঠিক দিশা পায়, না কি সে বিভ্রান্তের অন্তর্ভুক্ত হয়। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3201,27,42,'ঐ নারী যখন এলো, তখন তাকে জিজ্ঞেস করা হলঃ তোমার সিংহাসন কি এইরূপই? সে বললঃ এটাতো যেন ওটাই; আমাদেরকে ইতোপূর্বেই প্রকৃত জ্ঞান দান করা হয়েছে এবং আমরা আত্মসমর্পনও করেছি। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3202,27,43,'আল্লাহর পরিবর্তে সে যার পূজা করত তা’ই তাকে সত্য হতে নিবৃত করেছিল, সে ছিল কাফির সম্প্রদায়ের অন্তর্ভুক্ত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3203,27,44,'তাকে বলা হলঃ এই প্রাসাদে প্রবেশ কর। যখন সে ওটা দেখল তখন সে ওটাকে এক গভীর জলাশয় মনে করল এবং সে তার উভয় পায়ের গোছা অনাবৃত করল। সুলাইমান বললঃ স্বচ্ছ স্ফটিক মন্ডিত প্রাসাদ। সেই নারী বললঃ হে আমার রাব্ব! আমিতো নিজের প্রতি যুল্‌ম করেছিলাম, আমি সুলাইমানের সাথে জগতসমূহের রাব্ব আল্লাহর নিকট আত্মসমর্পন করছি। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3204,27,45,'আমি ছামূদ সম্প্রদায়ের নিকট তাদের ভাই সালিহকে পাঠিয়েছিলাম এই মর্মে যে, তোমরা আল্লাহর ইবাদাত কর। কিন্তু তারা দ্বিধা বিভক্ত হয়ে বিতর্কে লিপ্ত হল। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3205,27,46,'সে বললঃ হে আমার সম্প্রদায়! তোমরা কেন কল্যাণের পূর্বে অকল্যাণ ত্বরান্বিত করতে চাচ্ছ? কেন তোমরা আল্লাহর নিকট ক্ষমা প্রার্থনা করছনা, যাতে তোমরা অনুগ্রহ ভাজন হতে পার? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3206,27,47,'তারা বললঃ তোমাকে ও তোমার সঙ্গে যারা আছে তাদেরকে আমরা অমঙ্গলের কারণ মনে করি। সালিহ বললঃ তোমাদের শুভাশুভ আল্লাহর এখতিয়ারে, বস্তুতঃ তোমরা এমন এক সম্প্রদায় যাদেরকে পরীক্ষা করা হচ্ছে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3207,27,48,'আর সেই শহরে ছিল এমন নয় ব্যক্তি যারা দেশে বিপর্যয় সৃষ্টি করত এবং সৎ কাজ করতনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3208,27,49,'তারা বললঃ তোমরা আল্লাহর নামে শপথ গ্রহণ কর; আমরা রাতে তাকে ও তার পরিবার পরিজনকে অবশ্যই আক্রমণ করব, অতঃপর তার অভিভাবককে নিশ্চিত বলবঃ তার পরিবার পরিজনের হত্যা আমরা প্রত্যক্ষ করিনি; আমরা অবশ্যই সত্যবাদী। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3209,27,50,'তারা এক চক্রান্ত করেছিল এবং আমিও এক কৌশল অবলম্বন করলাম, কিন্তু তারা বুঝতে পারেনি। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3210,27,51,'অতএব দেখ, তাদের চক্রান্তের পরিণাম কি হয়েছে। আমি অবশ্যই তাদেরকে ও তাদের সম্প্রদায়ের সকলকে ধ্বংস করেছি। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3211,27,52,'এইতো তাদের ঘরবাড়ী সীমা লংঘন হেতু যা জনশূন্য অবস্থায় পড়ে আছে; এতে জ্ঞানী সম্প্রদায়ের জন্য অবশ্যই নিদর্শন রয়েছে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3212,27,53,'এবং যারা মু’মিন ও মুত্তাকী ছিল তাদেরকে আমি উদ্ধার করেছি। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3213,27,54,'স্মরণ কর লূতের কথা, সে তার সম্প্রদায়কে বলেছিলঃ তোমরা জেনে শুনে কেন অশ্লীল কাজ করছ? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3214,27,55,'তোমরা কি কাম-তৃপ্তির জন্য নারীকে ছেড়ে পুরুষে উপগত হবে? তোমরাতো এক অজ্ঞ সম্প্রদায়। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3215,27,56,'উত্তরে তার সম্প্রদায় শুধু বললঃ লূত পরিবারকে তোমাদের জনপদ হতে বহিস্কার কর, এরাতো এমন লোক যারা অতি পবিত্র সাজতে চায়। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3216,27,57,'অতঃপর তাকে ও তার পরিজনবর্গকে আমি উদ্ধার করলাম, তার স্ত্রী ব্যতীত, তাকে করেছিলাম ধ্বংসপ্রাপ্তদের অন্তর্ভুক্ত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3217,27,58,'তাদের উপর ভয়ঙ্কর বৃষ্টি বর্ষণ করেছিলাম; যাদেরকে ভীতি প্রদর্শন করা হয়েছিল তাদের জন্য এই বর্ষণ ছিল কত মারাত্মক। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3218,27,59,'বলঃ প্রশংসা আল্লাহই এবং শান্তি তাঁর মনোনীত বান্দাদের প্রতি। শ্রেষ্ঠ কি আল্লাহ, নাকি তারা যাদেরকে শরীক করা হয়? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3219,27,60,'বরং তিনি সৃষ্টি করেছেন আকাশমন্ডলী ও পৃথিবী এবং আকাশ হতে তোমাদের জন্য বর্ষণ করেন বৃষ্টি; অতঃপর আমি ওটা দ্বারা মনোরম উদ্যান সৃষ্টি করি। ওর বৃক্ষাদি উদ্গত করার ক্ষমতা তোমাদের নেই। আল্লাহর সাথে অন্য কোন মা‘বূদ আছে কি? তবুও তারা এমন এক সম্প্রদায় যারা সত্য হতে বিচ্যুত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3220,27,61,'বলত, কে পৃথিবীকে করেছেন বাসোপযোগী এবং ওর মাঝে মাঝে প্রবাহিত করেছেন নদ-নদী এবং তাকে স্থির রাখার জন্য স্থাপন করেছেন সুদৃঢ় পর্বত ও দুই সমুদ্রের মধ্যে সৃষ্টি করেছেন অন্তরায়; আল্লাহর সাথে অন্য কোন মা‘বূদ আছে কি? তবুও তাদের অনেকেই জানেনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3221,27,62,'কে আর্তের আহবানে সাড়া দেন, যখন সে তাঁকে ডাকে এবং বিপদ-আপদ দূর করেন এবং তোমাদেরকে পৃথিবীতে প্রতিনিধি করেন? আল্লাহর সাথে অন্য কোন মা‘বূদ আছে কি? তোমরা উপদেশ অতি সামান্যই গ্রহণ করে থাকো। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3222,27,63,'কে তোমাদেরকে স্থলের ও পানির অন্ধকারে পথ প্রদর্শন করেন এবং কে স্বীয় অনুগ্রহের প্রাক্কালে সুসংবাদবাহী বায়ু প্রেরণ করেন? আল্লাহর সাথে অন্য কোন মা‘বূদ আছে কি? তারা যাকে শরীক করে আল্লাহ তা হতে বহু উর্ধ্বে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3223,27,64,'বলত, কে আদিতে সৃষ্টি করেন, অতঃপর ওর পুনরাবৃত্তি করবেন, এবং কে তোমাদেরকে আকাশ ও পৃথিবী হতে জীবনোপকরণ দান করেন? আল্লাহর সাথে অন্য কোন মা‘বূদ আছে কি? বলঃ তোমরা যদি সত্যবাদী হও তাহলে তোমাদের প্রমাণ পেশ কর। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3224,27,65,'বলঃ আল্লাহ ব্যতীত আকাশমন্ডলী ও পৃথিবীতে কেহই অদৃশ্য বিষয়ের জ্ঞান রাখেনা এবং তারা জানেনা তারা কখন পুনরুত্থিত হবে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3225,27,66,'বরং আখিরাত সম্পর্কে তাদের জ্ঞান নিঃশেষ হয়েছে; তারাতো এ বিষয়ে সন্দেহের মধ্যে রয়েছে, বরং এ বিষয়ে তারা অন্ধ। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3226,27,67,'কাফিরেরা বলেঃ আমরা ও আমাদের পিতৃ-পুরুষরা মাটিতে পরিণত হয়ে গেলেও কি আমাদের পুনরুত্থিত করা হবে? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3227,27,68,'এ বিষয়েতো আমাদেরকে এবং পূর্ব-পুরুষদেরকে ভীতি প্রদর্শন করা হয়েছিল। এটাতো পূর্ববর্তী উপকথা ব্যতীত আর কিছুই নয়। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3228,27,69,'বলঃ পৃথিবীতে পরিভ্রমণ কর এবং দেখ অপরাধীদের পরিণাম কিরূপ হয়েছে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3229,27,70,'তাদের সম্পর্কে তুমি দুঃখ করনা এবং তাদের ষড়যন্ত্রে মনঃক্ষুন্ন হয়োনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3230,27,71,'তারা বলেঃ তোমরা যদি সত্যবাদী হও তাহলে বল, কখন এই প্রতিশ্রুতি পূর্ণ হবে? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3231,27,72,'বলঃ তোমরা যে বিষয়ে ত্বরান্বিত করতে চাচ্ছ সম্ভবতঃ তার কিছু তোমাদের নিকটবর্তী হয়েছে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3232,27,73,'নিশ্চয়ই তোমার রাব্ব মানুষের প্রতি অনুগ্রহশীল; কিন্তু তাদের অধিকাংশই অকৃতজ্ঞ। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3233,27,74,'তাদের অন্তর যা গোপন করে এবং তারা যা প্রকাশ করে তা তোমার রাব্ব অবশ্যই জানেন। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3234,27,75,'আকাশে ও পৃথিবীতে এমন কোন গোপন রহস্য নেই যা সুস্পষ্ট কিতাবে নেই। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3235,27,76,'বানী ইসরাঈল যে সমস্ত বিষয়ে মতভেদ করে, এই কুরআন তার অধিকাংশ তাদের নিকট বিবৃত করে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3236,27,77,'আর নিশ্চয়ই এটি মু’মিনদের জন্য হিদায়াত ও রাহমাত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3237,27,78,'তোমার রাব্ব নিজ সিদ্ধান্ত অনুযায়ী তাদের মধ্যে ফাইসালা করে দিবেন। তিনি পরাক্রমশালী, সর্বজ্ঞ। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3238,27,79,'অতএব আল্লাহর উপর নির্ভর কর; তুমিতো স্পষ্ট সত্যে প্রতিষ্ঠিত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3239,27,80,'মৃতকে তুমি কথা শোনাতে পারবেনা, বধিরকেও পারবেনা আহবান শোনাতে, যখন তারা পিছন ফিরে চলে যায়। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3240,27,81,'তুমি অন্ধদেরকে তাদের পথভ্রষ্টতা হতে পথে আনতে পারবে না। তুমি শোনাতে পারবে শুধু তাদেরকে যারা বিশ্বাস করে। আর তারাই আত্মসমর্পনকারী (মুসলিম)। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3241,27,82,'যখন ঘোষিত শাস্তি তাদের উপর এসে যাবে তখন আমি মাটির গহবর হতে বের করব এক জীব, যা তাদের সাথে কথা বলবে; এ জন্য যে, মানুষ আমার নিদর্শনে অবিশ্বাসী। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3242,27,83,'স্মরণ কর সেই দিনের কথা যেদিন আমি সমবেত করব প্রত্যেক সম্প্রদায় হতে এক একটি দলকে, যারা আমার নির্দেশাবলী প্রত্যাখ্যান করত এবং তাদেরকে সারিবদ্ধ করা হবে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3243,27,84,'যখন তারা সমবেত হবে তখন (আল্লাহ তাদেরকে) বলবেনঃ তোমরা আমার নিদর্শন প্রত্যাখ্যান করেছিলে, অথচ ওটা তোমরা জ্ঞানায়ত্ত করতে পারনি? না তোমরা অন্য কিছু করছিলে?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3244,27,85,'সীমা লংঘন হেতু তাদের উপর ঘোষিত শাস্তি এসে পড়বে; ফলে তারা কিছুই বলতে পারবেনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3245,27,86,'তারা কি অনুধাবন করেনা যে, আমি রাত সৃষ্টি করেছি তাদের বিশ্রামের জন্য এবং দিনকে করেছি আলোকপ্রদ? এতে মু’মিন সম্প্রদায়ের জন্য অবশ্যই নিদর্শন রয়েছে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3246,27,87,'আর যেদিন শিঙ্গায় ফুৎকার দেয়া হবে সেদিন আল্লাহ যাদেরকে চান তারা ব্যতীত আকাশমন্ডলী ও পৃথিবীর সবাই ভীত-বিহবল হয়ে পড়বে এবং সবাই তাঁর নিকট আসবে বিনীত অবস্থায়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3247,27,88,'তুমি পর্বতমালা দেখে অচল মনে করছ, অথচ সেদিন ওগুলি হবে মেঘপুঞ্জের ন্যায় চলমান, এটা আল্লাহরই সৃষ্টি-নৈপুণ্য, যিনি সব কিছুকে করেছেন সুষম। তোমরা যা কর সেই সম্বন্ধে তিনি সম্যক অবগত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3248,27,89,'যে কেহ সৎ কাজ নিয়ে আসবে, সে উৎকৃষ্টতর প্রতিফল পাবে এবং সেদিন তারা শংকা হতে নিরাপদ থাকবে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3249,27,90,'আর যে কেহ অসৎ কাজ নিয়ে আসবে, তাকে অধোমুখে নিক্ষেপ করা হবে আগুনে (এবং তাদেরকে বলা হবে) তোমরা যা করতে তারই প্রতিফল তোমাদেরকে দেয়া হচ্ছে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3250,27,91,'আমিতো আদিষ্ট হয়েছি এই নগরীর রবের ইবাদাত করতে, যিনি একে করেছেন সম্মানিত। সব কিছু তাঁরই। আমি আরও আদিষ্ট হয়েছি যেন আমি আত্মসমর্পনকারীদের অন্তর্ভুক্ত হই। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3251,27,92,'আমি আরও আদিষ্ট হয়েছি কুরআন আবৃত্তি করতে। অতঃপর যে ব্যক্তি সৎ পথ অনুসরণ করে, সে তা অনুসরণ করে নিজেরই কল্যাণের জন্য এবং কেহ ভ্রান্ত পথ অবলম্বন করলে তুমি বলঃ আমিতো শুধু সর্তককারীদের মধ্যে একজন। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3252,27,93,'আর বলঃ প্রশংসা আল্লাহরই, তিনি তোমাদেরকে সত্ত্বর দেখাবেন তাঁর নিদর্শন, তখন তোমরা তা বুঝতে পারবে। তোমরা যা কর সে সম্বন্ধে তোমাদের রাব্ব গাফিল নন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3253,28,1,'তা-সীন-মীম। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3254,28,2,'এই আয়াতগুলি সুস্পষ্ট কিতাবের। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3255,28,3,'আমি তোমার নিকট মূসা ও ফির‘আউনের কিছু বৃত্তান্ত যথাযথভাবে বিবৃত করছি, মু’মিন সম্প্রদায়ের উদ্দেশে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3256,28,4,'নিশ্চয়ই ফির‘আউন তার দেশে পরাক্রমশালী হয়েছিল এবং সেখানকার অধিবাসীবৃন্দকে বিভিন্ন শ্রেণীতে বিভক্ত করে তাদের একটি শ্রেণীকে সে হীনবল করেছিল, তাদের পুত্রদেরকে সে হত্যা করত এবং নারীদেরকে সে জীবিত রাখত। সেতো ছিল বিপর্যয় সৃষ্টিকারী। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3257,28,5,'আমি ইচ্ছা করলাম, সে দেশে যাদেরকে হীনবল করা হয়েছিল তাদের প্রতি অনুগ্রহ করতে; তাদেরকে নেতৃত্ব দান করতে ও দেশের অধিকারী করতে, ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3258,28,6,'আর তাদেরকে দেশের ক্ষমতায় প্রতিষ্ঠিত করতে; এবং ফির‘আউন, হামান ও তাদের বাহিনীকে তা দেখিয়ে দিতে যা তাদের নিকট থেকে তারা আশংকা করত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3259,28,7,'আমি মূসার মায়ের অন্তরে ইংগিতে নির্দেশ করলামঃ শিশুটিকে তুমি স্তন্য দান করতে থাক; যখন তুমি তার সম্পর্কে কোন আশংকা করবে তখন তাকে দরিয়ায় নিক্ষেপ কর এবং ভয় করনা, দুঃখ করনা; আমি তাকে তোমার নিকট ফিরিয়ে দিব এবং তাকে রাসূলদের একজন করব। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3260,28,8,'অতঃপর ফির‘আউনের লোকজন তাকে কুড়িয়ে নিল। এর পরিণামতো এই ছিল যে, সে তাদের শত্রু ও দুঃখের কারণ হবে। ফির‘আউন, হামান ও তাদের বাহিনী ছিল অপরাধী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3261,28,9,'ফির‘আউনের স্ত্রী বললঃ এই শিশু আমার ও তোমার নয়ন প্রীতিকর। তাকে হত্যা করনা, সে আমাদের উপকারে আসতে পারে, আমরা তাকে সন্তান হিসাবেও গ্রহণ করতে পারি। প্রকৃত পক্ষে তারা এর পরিণাম বুঝতে পারেনি। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3262,28,10,'মূসা-জননীর হৃদয় অস্থির হয়ে পড়েছিল; যাতে সে আস্থাশীল হয় তজ্জন্য আমি তার হৃদয়কে দৃঢ় করে না দিলে সে তার পরিচয়তো প্রকাশ করেই দিত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3263,28,11,'সে মূসার বোনকে বললঃ এর পিছনে পিছনে যাও, সে তাদের অজ্ঞাতসারে দূর হতে তাকে দেখছিল। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3264,28,12,'পূর্ব হতেই আমি ধাত্রীস্তন্য পানে তাকে বিরত রেখেছিলাম। মূসার বোন বললঃ তোমাদেরকে কি আমি এমন এক পরিবারের সন্ধান দিব যারা তোমাদের হয়ে একে লালন পালন করবে এবং এর মঙ্গলকামী হবে? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3265,28,13,'অতঃপর আমি তাকে ফিরিয়ে দিলাম তার জননীর নিকট যাতে তার চক্ষু জুড়ায়, সে দুঃখ না করে এবং বুঝতে পারে যে, আল্লাহর প্রতিশ্রুতি সত্য; কিন্তু অধিকাংশ মানুষই এটা জানেনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3266,28,14,'যখন মূসা পূর্ণ যৌবনে উপনীত ও পরিণত বয়স্ক হল তখন আমি তাকে হিকমাত ও জ্ঞান দান করলাম। এভাবে আমি সৎকর্মপরায়ণদেরকে পুরস্কার প্রদান করে থাকি। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3267,28,15,'সে নগরীতে প্রবেশ করল, যখন এর অধিবাসীরা ছিল অসতর্ক। সেখানে সে দু’টি লোককে সংঘর্ষে লিপ্ত দেখল - একজন তার নিজ দলের এবং অপর জন তার শত্রু দলের। মূসার দলের লোকটি তার শত্রুর বিরুদ্ধে তার সাহায্য প্রার্থনা করল। তখন মূসা তাকে ঘুষি মারল, এতেই তার মৃত্যু হল। মূসা বললঃ এটা শাইতানের কান্ড, সেতো প্রকাশ্য শত্রু ও পথভ্রষ্টকারী। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3268,28,16,'সে বললঃ হে আমার রাব্ব! আমিতো আমার নিজের প্রতি যুলম করেছি; সুতরাং আমাকে ক্ষমা করুন! অতঃপর তিনি তাকে ক্ষমা করলেন। তিনিতো ক্ষমাশীল, পরম দয়ালু। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3269,28,17,'সে আরও বললঃ হে আমার রাব্ব! আপনি যেহেতু আমার উপর অনুগ্রহ করেছেন, আমি কখনও অপরাধীদের সাহায্যকারী হবনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3270,28,18,'অতঃপর ভীত সতর্ক অবস্থায় সেই নগরীতে তার প্রভাত হল। হঠাৎ সে শুনতে পেলো, পূর্বদিন যে ব্যক্তি তার সাহায্য চেয়েছিল সে তার সাহায্যের জন্য চীৎকার করছে। মূসা তাকে বললঃ তুমিতো সুস্পষ্টই একজন বিভ্রান্ত ব্যক্তি। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3271,28,19,'অতঃপর মূসা যখন উভয়ের শত্রুকে ধরতে উদ্যত হল তখন সেই ব্যক্তি বলে উঠলঃ হে মূসা! গতকল্য তুমি যেমন এক ব্যক্তিকে হত্যা করেছ সেভাবে আমাকেও কি হত্যা করতে চাচ্ছ? তুমিতো পৃথিবীতে স্বেচ্ছাচারী হতে চাচ্ছ, শান্তি স্থাপনকারী হতে চাওনা? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3272,28,20,'নগরীর দূর প্রান্ত হতে এক ব্যক্তি ছুটে এলো এবং বললঃ হে মূসা! পরিষদবর্গ তোমাকে হত্যা করার পরামর্শ করেছে। সুতরাং তুমি বাইরে চলে যাও, আমিতো তোমার মঙ্গলকামী। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3273,28,21,'ভীত সতর্ক অবস্থায় সে সেখান হতে বের হয়ে পড়ল এবং বললঃ হে আমার রাব্ব! আপনি যালিম সম্প্রদায় হতে আমাকে রক্ষা করুন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3274,28,22,'যখন মূসা মাদইয়ান অভিমুখে যাত্রা শুরু করল তখন বললঃ আশা করি, আমার রাব্ব আমাকে সরল পথ প্রদর্শন করবেন। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3275,28,23,'যখন সে মাদইয়ানের কূপের নিকট পৌঁছল তখন দেখল যে, একদল লোক তাদের পশুগুলিকে পানি পান করাচ্ছে এবং তাদের পশ্চাতে দু’জন নারী তাদের পশুগুলিকে আগলাচ্ছে। মূসা বললঃ তোমাদের কি ব্যাপার? তারা বললঃ আমরা আমাদের পশুগুলিকে পানি পান করাতে পারিনা, যতক্ষণ রাখালরা তাদের পশুগুলিকে নিয়ে সরে না যায়, আমাদের পিতা অতি বৃদ্ধ। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3276,28,24,'মূসা তখন তাদের পশুগুলিকে পানি পান করালো। অতঃপর সে ছায়ার নীচে আশ্রয় গ্রহণ করল ও বললঃ হে আমার রাব্ব! আপনি আমার প্রতি যে অনুগ্রহ করবেন আমি তার কাঙ্গাল। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3277,28,25,'তখন নারীদ্বয়ের একজন লজ্জা জড়িত চরণে তার নিকট এলো এবং বললঃ আমার পিতা আপনাকে আমন্ত্রণ করেছেন, আমাদের পশুগুলিকে পানি পান করানোর পারিশ্রমিক আপনাকে দেয়ার জন্য। অতঃপর মূসা তার নিকট এসে সমস্ত বৃত্তান্ত বর্ণনা করলে সে বললঃ ভয় করনা, তুমি যালিম সম্প্রদায়ের কবল হতে বেঁচে গেছ। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3278,28,26,'তাদের একজন বললঃ হে পিতা! আপনি একে মজুর নিযুক্ত করুন; কারণ আপনার মজুর হিসাবে উত্তম হবে সেই ব্যক্তি যে শক্তিশালী, বিশ্বস্ত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3279,28,27,'সে মূসাকে বললঃ আমি আমার এই কন্যাদ্বয়ের একজনকে তোমার সাথে বিয়ে দিতে চাই এই শর্তে যে, তুমি আট বছর আমার কাজ করবে, যদি তুমি দশ বছর পূর্ণ কর সেটা তোমার ইচ্ছা। আমি তোমাকে কষ্ট দিতে চাইনা। আল্লাহ ইচ্ছা করলে তুমি আমাকে সদাচারী পাবে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3280,28,28,'মূসা বললঃ আপনার ও আমার মধ্যে এই চুক্তিই রইল। এ দু’টি মেয়াদের কোন একটি আমি পূর্ণ করলে আমার বিরুদ্ধে কোন অভিযোগ থাকবেনা। আমরা যে বিষয়ে কথা বলছি আল্লাহ তার স্বাক্ষী। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3281,28,29,'মূসা যখন মেয়াদ পূর্ণ করার পর স্বপরিবারে যাত্রা শুরু করল তখন সে তূর পর্বতের দিকে আগুন দেখতে পেল। সে তার পরিজনবর্গকে বললঃ তোমরা অপেক্ষা কর, আমি আগুন দেখেছি, সম্ভবতঃ আমি সেথা হতে তোমাদের জন্য খবর আনতে পারি, অথবা এক খন্ড জ্বলন্ত কাষ্ঠ আনতে পারি যাতে তোমরা আগুন পোহাতে পার।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3282,28,30,'যখন মূসা আগুনের নিকট পৌঁছল তখন উপত্যকার দক্ষিণ পাশে পবিত্র ভূমিস্থিত এক বৃক্ষ হতে তাকে আহবান করে বলা হলঃ হে মূসা! আমিই আল্লাহ, জগতসমূহের রাব্ব। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3283,28,31,'আরও বলা হলঃ তুমি তোমার লাঠি নিক্ষেপ কর। অতঃপর যখন সে ওটাকে সর্পের ন্যায় ছুটাছুটি করতে দেখল তখন পিছনের দিকে ছুটতে লাগল এবং ফিরে তাকালোনা। তাকে বলা হলঃ হে মূসা! সামনে এসো, ভয় করনা, তুমিতো নিরাপদ। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3284,28,32,'তোমার হাত তোমার বগলে রাখ, ওটা বের হয়ে আসবে শুভ্র সমুজ্জ্বল নির্দোষ হয়ে। ভয় দূর করার জন্য তোমার হস্তদ্বয় তোমার উপর চেপে ধর। এ দু’টি তোমার রাব্ব প্রদত্ত প্রমাণ, ফির‘আউন ও তার পরিষদবর্গের জন্য। তারাতো সত্যত্যাগী সম্প্রদায়। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3285,28,33,'মূসা বললঃ হে আমার রাব্ব! আমিতো তাদের একজনকে হত্যা করেছি। ফলে আমি আশংকা করছি যে, তারা আমাকে হত্যা করবে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3286,28,34,'আমার ভাই হারূন আমা অপেক্ষা বাগ্মী; অতএব তাকে আমার সাহায্যকারী রূপে প্রেরণ করুন, সে আমাকে সমর্থন করবে। আমি আশংকা করি যে, তারা আমাকে মিথ্যাবাদী বলবে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3287,28,35,'(আল্লাহ) বললেনঃ আমি তোমার ভাইয়ের দ্বারা তোমার বাহু শক্তিশালী করব এবং তোমাদের উভয়কে প্রাধান্য দান করব। তারা তোমাদের কাছে পৌঁছতে পারবেনা। তোমরা এবং তোমাদের অনুসারীরা আমার নিদর্শন বলে তাদের উপর প্রবল হবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3288,28,36,'মূসা যখন তাদের নিকট আমার সুস্পষ্ট নিদর্শনগুলি নিয়ে এলো তখন তারা বললঃ এটাতো অলীক ইন্দ্রজাল মাত্র! আমাদের পূর্ব-পুরুষদের কাছে আমরা কখনও এরূপ কথা শুনিনি। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3289,28,37,'মূসা বললঃ আমার রাব্ব সম্যক অবগত, কে তাঁর নিকট হতে পথ নির্দেশ নিয়ে এসেছে, এবং আখিরাতে কার পরিণাম শুভ হবে। নিশ্চয়ই যালিমরা সফলকাম হবেনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3290,28,38,'ফির‘আউন বললঃ হে পরিষদবর্গ! আমি ছাড়া তোমাদের অন্য উপাস্য আছে বলে আমি জানিনা। হে হামান! তুমি আমার জন্য ইট পোড়াও এবং একটি সুউচ্চ প্রাসাদ তৈরী কর; হয়তো আমি তাতে উঠে মূসার মা‘বূদকে দেখতে পাব। তবে আমি অবশ্য মনে করি যে, সে মিথ্যাবাদী। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3291,28,39,'ফির‘আউন ও তার বাহিনী অন্যায়ভাবে পৃথিবীতে অহংকার করেছিল যে, তারা আমার নিকট প্রত্যাবর্তিত হবেনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3292,28,40,'অতএব আমি তাকে ও তার বাহিনীকে পাকড়াও করলাম এবং তাদেরকে সমুদ্রে নিক্ষেপ করলাম। দেখ, যালিমদের পরিণাম কি হয়ে থাকে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3293,28,41,'তাদেরকে আমি নেতা করেছিলাম। তারা লোকদেরকে জাহান্নামের দিকে আহবান করত। কিয়ামাত দিবসে তাদেরকে সাহায্য করা হবেনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3294,28,42,'এই পৃথিবীতে আমি তাদের পশ্চাতে লাগিয়ে দিয়েছি অভিসম্পাত এবং কিয়ামাত দিবসেও তারা দুর্দশাগ্রস্ত লোকদের অন্তর্ভুক্ত থাকবে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3295,28,43,'আমিতো পূর্ববর্তী বহু মানব গোষ্ঠিকে বিনাশ করার পর মূসাকে দিয়েছিলাম কিতাব, মানব জাতির জন্য জ্ঞান-বর্তিকা, পথ নির্দেশ ও দয়া স্বরূপ, যাতে তারা উপদেশ গ্রহণ করে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3296,28,44,'মূসাকে যখন আমি বিধান দিয়েছিলাম তখন তুমি পশ্চিম প্রান্তে উপস্থিত ছিলেনা এবং তুমি প্রত্যক্ষদর্শীও ছিলেনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3297,28,45,'বস্তুতঃ আমি অনেক মানব গোষ্ঠির আবির্ভাব ঘটিয়েছিলাম, অতঃপর তাদের বহু যুগ অতিবাহিত হয়েছে। তুমিতো মাদইয়ানবাসীদের মধ্যে বিদ্যমান ছিলেনা, তাদের নিকট আমার আয়াত আবৃত্তি করার জন্য। আমিই ছিলাম রাসূল প্রেরণকারী। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3298,28,46,'মূসাকে যখন আমি আহবান করেছিলাম তখন তুমি তূর পর্বত-পার্শ্বে উপস্থিত ছিলেনা। বস্তুতঃ এটা তোমার রবের নিকট হতে দয়া স্বরূপ যাতে তুমি এমন এক সম্প্রদায়কে সতর্ক করতে পার যাদের নিকট তোমার পূর্বে কোন সতর্ককারী আসেনি, যেন তারা উপদেশ গ্রহণ করে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3299,28,47,'রাসূল না পাঠালে তাদের কৃতকর্মের জন্য তাদের কোন বিপদ হলে তারা বলতঃ হে আমাদের রাব্ব! আপনি আমাদের নিকট কোন রাসূল প্রেরণ করলেন না কেন? তাহলে আমরা আপনার নিদর্শন মেনে চলতাম এবং আমরা হতাম মু’মিন। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3300,28,48,'অতঃপর যখন আমার নিকট হতে তাদের নিকট সত্য এসে গেল, তারা বলতে লাগলঃ মূসাকে যেরূপ দেয়া হয়েছিল অনুরূপ তাকে দেয়া হলনা কেন? কিন্তু পূর্বে মূসাকে যা দেয়া হয়েছিল তা কি তারা অস্বীকার করেনি? তারা বলেছিলঃ উভয়ই যাদু, একে অপরকে সমর্থন করে; এবং তারা বলেছিলঃ আমরা প্রত্যেককে প্রত্যাখ্যান করি। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3301,28,49,'বলঃ তোমরা সত্যবাদী হলে আল্লাহর নিকট হতে এক কিতাব আনয়ন কর, যে পথ নির্দেশ এতদুভয় হতে উৎকৃষ্টতর হবে, আমি সেই কিতাব অনুসরণ করব। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3302,28,50,'অতঃপর তারা যদি তোমার আহবানে সাড়া না দেয় তাহলে জানবে যে, তারা শুধু নিজেদের খেয়াল-খুশীর অনুসরণ করে। আল্লাহর পথ-নির্দেশ অগ্রাহ্য করে যে ব্যক্তি নিজ খেয়াল-খুশীর অনুসরণ করে সে অপেক্ষা অধিক বিভ্রান্ত আর কে? আল্লাহ যালিম সম্প্রদায়কে পথ প্রদর্শন করেননা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3303,28,51,'আমিতো তাদের নিকট উপর্যুপরি বাণী পৌঁছে দিয়েছি যাতে তারা উপদেশ গ্রহণ করে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3304,28,52,'এর পূর্বে আমি যাদেরকে কিতাব দিয়েছিলাম তারা এতে বিশ্বাস করে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3305,28,53,'যখন তাদের নিকট এটি আবৃত্তি করা হয় তখন তারা বলেঃ আমরা এতে ঈমান এনেছি, এটি আমাদের রাব্ব হতে আগত সত্য। আমরাতো পূর্বেও আত্মসমর্পনকারী ছিলাম। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3306,28,54,'তাদেরকে দু’বার পারিশ্রমিক প্রদান করা হবে; কারণ তারা ধৈর্যশীল এবং তারা ভাল দ্বারা মন্দের মুকাবিলা করে এবং আমি তাদেরকে যা দিয়েছি তা হতে তারা ব্যয় করে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3307,28,55,'তারা যখন অসার বাক্য শ্রবণ করে তখন তা উপেক্ষা করে চলে এবং বলেঃ আমাদের কাজের ফল আমাদের জন্য এবং তোমাদের কাজের ফল তোমাদের জন্য; তোমাদের প্রতি সালাম। আমরা অজ্ঞদের সঙ্গ চাইনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3308,28,56,'তুমি যাকে ভালবাস, ইচ্ছা করলেই তাকে সৎ পথে আনতে পারবেনা। তবে আল্লাহ যাকে ইচ্ছা সৎ পথে আনেন এবং তিনিই ভাল জানেন কারা সৎ পথ অনুসরণকারী। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3309,28,57,'তারা বলেঃ আমরা যদি তোমার সাথে সৎ পথ অনুসরণ করি তাহলে আমাদেরকে দেশ হতে উৎখাত করা হবে। (আল্লাহ বলেন) আমি কি তাদের জন্য এক নিরাপদ ‘‘হারাম’’ প্রতিষ্ঠিত করিনি, যেখানে সর্ব প্রকার ফলমূল আমদানী হয় আমার দেয়া রিয্ক স্বরূপ? কিন্তু তাদের অধিকাংশই এটা জানেনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3310,28,58,'কত জনপদকে আমি ধ্বংস করেছি যার বাসিন্দারা নিজেদের ভোগ সম্পদের দম্ভ করত। এগুলিইতো তাদের ঘরবাড়ী, তাদের পরে এগুলিতে লোকজন সামান্যই বসবাস করেছে; আর আমিতো চূড়ান্ত মালিকানার অধিকারী। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3311,28,59,'তোমার রাব্ব জনপদসমূহকে ধ্বংস করেন না, ওর কেন্দ্রে তাঁর আয়াত আবৃত্তি করার জন্য রাসূল প্রেরণ না করা পর্যন্ত এবং আমি জনপদসমূহকে তখনই ধ্বংস করি যখন ওর বাসিন্দারা যুল্‌ম করে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3312,28,60,'তোমাদের যা কিছু দেয়া হয়েছে তাতো পার্থিব জীবনের ভোগ শোভা এবং যা আল্লাহর নিকট রয়েছে তা উত্তম এবং স্থায়ী। তোমরা কি অনুধাবন করবেনা? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3313,28,61,'যাকে আমি উত্তম পুরস্কারের প্রতিশ্রুতি দিয়েছি সে যা পাবে, সে কি ঐ ব্যক্তির সমান যাকে আমি পার্থিব জীবনের ভোগ সম্ভার দিয়েছি, অতঃপর যাকে কিয়ামাত দিবসে অপরাধী রূপে হাযির করা হবে? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3314,28,62,'আর সেদিন তিনি তাদেরকে আহবান করে বলবেনঃ তোমরা যাদেরকে আমার শরীক গণ্য করতে তারা কোথায়? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3315,28,63,'যাদের জন্য শাস্তি অবধারিত হয়েছে তারা বলবেঃ হে আমাদের রাব্ব! এদেরকেও আমরা বিভ্রান্ত করেছিলাম, যেমন আমরা বিভ্রান্ত হয়েছিলাম; আপনার সমীপে আমরা দায়িত্ব হতে অব্যাহতি চাচ্ছি। এরা আমাদের ইবাদাত করতনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3316,28,64,'তাদেরকে বলা হবেঃ তোমাদের দেবতাগুলিকে আহবান কর। তখন তারা তাদেরকে ডাকবে। কিন্তু তারা তাদের ডাকে সাড়া দিবেনা। তারা শাস্তি প্রত্যক্ষ করবে; হায়! তারা যদি সৎ পথ অনুসরণ করত!');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3317,28,65,'আর সেদিন (আল্লাহ) তাদেরকে ডেকে বলবেনঃ তোমরা রাসূলদেরকে কি জবাব দিয়েছিলে? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3318,28,66,'সেদিন সকল তথ্য তাদের নিকট হতে বিলুপ্ত হবে এবং তারা একে অপরকে জিজ্ঞাসাবাদ করতে পারবেনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3319,28,67,'তবে যে ব্যক্তি তাওবাহ করেছিল এবং ঈমান এনেছিল ও সৎ কাজ করেছিল সেতো সাফল্য অর্জনকারীদের অন্তর্ভুক্ত হবে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3320,28,68,'তোমার রাব্ব যা ইচ্ছা সৃষ্টি করেন এবং যাকে ইচ্ছা মনোনীত করেন, এতে তাদের কোন ক্ষমতা নেই, আল্লাহ পবিত্র মহান এবং তারা যাকে শরীক করে তা হতে তিনি উর্ধ্বে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3321,28,69,'আর তোমার রাব্ব জানেন তাদের অন্তর যা গোপন করে এবং তারা যা ব্যক্ত করে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3322,28,70,'তিনিই আল্লাহ, তিনি ব্যতীত কোন মা‘বূদ নেই; দুনিয়া ও আখিরাতে প্রশংসা তাঁরই, বিধান তাঁরই আয়ত্বাধীন; তোমরা তাঁরই দিকে প্রত্যাবর্তিত হবে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3323,28,71,'বলঃ তোমরা কি ভেবে দেখেছ, আল্লাহ যদি রাতকে কিয়ামাতের দিন পর্যন্ত স্থায়ী করেন তাহলে আল্লাহ ব্যতীত এমন কোন ইলাহ আছে কি যে তোমাদেরকে আলোক দান করতে পারে? তোমরা কি কর্ণপাত করবেনা? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3324,28,72,'বলঃ ভেবে দেখ তো, আল্লাহ যদি দিনকে কিয়ামাতের দিন পর্যন্ত স্থায়ী করেন তাহলে আল্লাহ ব্যতীত এমন ইলাহ কে আছে যে তোমাদেরকে রাত দান করতে পারে, যাতে তোমরা বিশ্রাম করবে? তোমরা কি তবুও ভেবে দেখবেনা? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3325,28,73,'তিনিই তাঁর রাহমাতের দ্বারা তোমাদের জন্য সৃষ্টি করেছেন দিন ও রাত, যাতে তোমরা বিশ্রাম গ্রহণ কর এবং তাঁর অনুগ্রহ তালাশ কর, এবং যাতে তোমরা কৃতজ্ঞতা প্রকাশ কর। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3326,28,74,'সেদিন তিনি তাদেরকে আহবান করে বলবেনঃ তোমরা যাদেরকে শরীক গণ্য করতে তারা কোথায়? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3327,28,75,'প্রত্যেক সম্প্রদায় হতে আমি একজন সাক্ষী আলাদা করব এবং বলবঃ তোমাদের প্রমাণ উপস্থিত কর। তখন তারা জানতে পারবে, মা‘বূদ হবার অধিকার আল্লাহরই এবং তারা যা উদ্ভাবন করত তা তাদের নিকট হতে অন্তর্হিত হবে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3328,28,76,'কারূন ছিল মূসার সম্প্রদায়ভুক্ত, বস্তুতঃ সে তাদের প্রতি ঔদ্ধত্য প্রকাশ করেছিল। আমি তাকে দান করেছিলাম এমন ধনভান্ডার যার চাবিগুলি বহন করা একদল বলবান লোকের পক্ষেও কষ্টসাধ্য ছিল। স্মরণ কর, তার সম্প্রদায় তাকে বলেছিলঃ দম্ভ করনা, নিশ্চয়ই আল্লাহ দাম্ভিকদের পছন্দ করেননা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3329,28,77,'আল্লাহ তোমাকে যা দান করেছেন তদ্বারা আখিরাতের আবাস অনুসন্ধান কর। আর দুনিয়া হতে তোমার অংশ ভুলে যেওনা; এবং পরোপকার কর যেমন আল্লাহ তোমার প্রতি অনুগ্রহ করেছেন, আর পৃথিবীতে বিপর্যয় সৃষ্টি করতে চেওনা। নিশ্চয়ই আল্লাহ বিপর্যয় সৃষ্টিকারীকে ভালবাসেন না ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3330,28,78,'সে বললঃ এই সম্পদ আমি আমার জ্ঞান বলে প্রাপ্ত হয়েছি। সে কি জানতোনা যে, আল্লাহ তার পূর্বে ধ্বংস করেছেন বহু মানব গোষ্ঠিকে, যারা তার চেয়ে শক্তিতে ছিল প্রবল এবং ছিল অধিক প্রাচুর্যশালী? কিন্তু অপরাধীদেরকে তাদের অপরাধ সম্পর্কে তৎক্ষণাৎ প্রশ্ন করা হয়না। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3331,28,79,'কারূন তার সম্প্রদায়ের সামনে উপস্থিত হয়েছিল জাকজমক সহকারে। যারা পার্থিব জীবন কামনা করত তারা বললঃ আহা! কারূনকে যেরূপ দেয়া হয়েছে আমাদেরকে যদি তা দেয়া হত! প্রকৃতই সে মহা ভাগ্যবান! ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3332,28,80,'আর যাদেরকে জ্ঞান দেয়া হয়েছিল তারা বললঃ ধিক্ তোমাদেরকে! যারা ঈমান আনে ও সৎ কাজ করে তাদের জন্য আল্লাহর পুরস্কার শ্রেষ্ঠ এবং ধৈর্যশীল ব্যতীত এটা কেহ পাবেনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3333,28,81,'অতঃপর আমি কারূনকে ও তার প্রাসাদকে ভূগর্ভে প্রোথিত করলাম। তার স্বপক্ষে এমন কোন দল ছিলনা যে আল্লাহর শাস্তি হতে তাকে সাহায্য করতে পারত এবং সে নিজেও আত্মরক্ষায় সক্ষম ছিলনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3334,28,82,'পূর্বদিন যারা তার অবস্থা কামনা করেছিল তারা বলতে লাগলঃ দেখলেতো আল্লাহ তাঁর বান্দাদের মধ্যে যার জন্য ইচ্ছা তার রিয্ক বর্ধিত করেন এবং যার জন্য ইচ্ছা হ্রাস করেন। যদি আল্লাহ আমাদের প্রতি সদয় না হতেন তাহলে আমাদেরকেও তিনি ভূগর্ভে প্রোথিত করতেন। দেখলে তো! কাফিরেরা সফলকাম হয়না। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3335,28,83,'এটা আখিরাতের সেই আবাস যা আমি নির্ধারিত করি তাদের জন্য যারা এই পৃথিবীতে ঔদ্ধতা প্রকাশ করতে ও বিপর্যয় সৃষ্টি করতে চায়না। শুভ পরিণাম মুত্তাকীদের জন্য। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3336,28,84,'কেহ যদি সৎ কাজ করে তাহলে সে তার কাজ অপেক্ষা উত্তম ফল পাবে, আর যে মন্দ কাজ করে সেতো শাস্তি পাবে শুধু তার কাজ অনুপাতে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3337,28,85,'যিনি তোমার জন্য কুরআনকে করেছেন বিধান তিনি তোমাকে অবশ্যই ফিরিয়ে আনবেন প্রত্যাবর্তন স্থানে। বলঃ আমার রাব্ব ভাল জানেন কে সৎ পথের নির্দেশ এনেছে এবং কে স্পষ্ট বিভ্রান্তিতে আছে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3338,28,86,'তুমি আশা করনি যে, তোমার প্রতি কিতাব অবতীর্ণ হবে। এটাতো শুধু তোমার রবের অনুগ্রহ। সুতরাং তুমি কখনও কাফিরদের সাহায্যকারী হয়োনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3339,28,87,'তোমার প্রতি আল্লাহর আয়াত অবতীর্ণ হওয়ার পর কেহ যেন তোমাকে কিছুতেই সেগুলি হতে বিরত না রাখে। তুমি তোমার রবের দিকে আহবান করতে থাক এবং কিছুতেই মুশরিকদের অন্তর্ভুক্ত হয়োনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3340,28,88,'তুমি আল্লাহর সাথে অন্য ইলাহকে ডেকনা, তিনি ছাড়া অন্য কোন ইলাহ নেই। আল্লাহর সত্তা ব্যতীত সব কিছু ধ্বংসশীল। বিধান তাঁরই এবং তাঁরই নিকট তোমরা প্রত্যাবর্তিত হবে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3341,29,1,'আলিফ লাম মীম, ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3342,29,2,'মানুষ কি মনে করেছে যে, ‘আমরা ঈমান এনেছি’ এ কথা বললেই তাদেরকে অব্যাহতি দেয়া হবে এবং তাদেরকে পরীক্ষা করা হবেনা? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3343,29,3,'আমিতো তাদের পূর্ববর্তীদেরকেও পরীক্ষা করেছিলাম; আল্লাহ অবশ্যই প্রকাশ করে দিবেন কারা সত্যবাদী ও কারা মিথ্যাবাদী। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3344,29,4,'যারা মন্দ কাজ করে তারা কি মনে করে যে, তারা আমার আয়ত্তের বাইরে চলে যাবে? তাদের সিদ্ধান্ত কত মন্দ! ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3345,29,5,'যে আল্লাহর সাথে সাক্ষাৎ কামনা করে সে জেনে রাখুক যে, আল্লাহর নির্ধারিত কাল আসবেই। তিনি সর্বশ্রোতা, সর্বজ্ঞ। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3346,29,6,'যে কেহ (কঠোর) সাধনা করে, সে তা করে নিজেরই জন্য; আল্লাহতো বিশ্বজগত হতে অনপেক্ষ। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3347,29,7,'আর যারা ঈমান আনে ও সৎ কাজ করে, নিশ্চয়ই আমি তাদের মন্দ কর্মগুলি মিটিয়ে দিব এবং তাদের কাজের উত্তম ফল দান করব। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3348,29,8,'আমি মানুষকে নির্দেশ দিয়েছি তার মাতা-পিতার প্রতি সদ্ব্যবহার করতে; কিন্তু তারা যদি তোমার উপর বল প্রয়োগ করে, আমার সাথে এমন কিছু শরীক করতে যে সম্পর্কে তোমার কোন জ্ঞান নেই তাহলে তুমি তাদেরকে মান্য করনা। আমারই নিকট তোমাদের প্রত্যাবর্তন। অতঃপর আমি তোমাদেরকে জানিয়ে দিব যা তোমরা করতে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3349,29,9,'যারা ঈমান আনে ও সৎ কাজ করে, আমি অবশ্যই তাদেরকে সৎকর্মপরায়ণদের অন্তর্ভুক্ত করব। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3350,29,10,'মানুষের মধ্যে কতক লোক বলেঃ ‘‘আমরা আল্লাহতে বিশ্বাস করি।’’ কিন্তু আল্লাহর পথে যখন তারা কষ্টে পতিত হয় তখন তারা মানুষের পীড়নকে আল্লাহর শাস্তির মত গণ্য করে এবং তোমার রবের নিকট হতে কোন সাহায্য এলে তারা বলতে থাকে, ‘আমরাতো তোমাদের সাথেই ছিলাম’। বিশ্ববাসীর অন্তরে যা আছে আল্লাহ কি তা সম্যক অবগত নন? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3351,29,11,'আল্লাহ অবশ্যই প্রকাশ করে দিবেন কারা ঈমান এনেছে এবং অবশ্যই প্রকাশ করে দিবেন কারা মুনাফিক। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3352,29,12,'কাফিরেরা মু’মিনদেরকে বলেঃ ‘আমাদের পথ অনুসরণ কর, আমরা তোমাদের পাপভার বহন করব।’ কিন্তু তারাতো তাদের পাপভারের কিছুই বহন করবেনা। তারা অবশ্যই মিথ্যাবাদী। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3353,29,13,'এবং তারা নিজেদের বোঝা বহন করবে এবং নিজেদের বোঝার সাথে আরও বোঝা; এবং তারা যে মিথ্যা উদ্ভাবন করে সেই সম্পর্কে কিয়ামাত দিবসে অবশ্যই তাদেরকে প্রশ্ন করা হবে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3354,29,14,'আমিতো নূহকে তার সম্প্রদায়ের নিকট প্রেরণ করেছিলাম এবং সে তাদের মধ্যে পঞ্চাশ কম হাজার বছর অবস্থান করেছিল। অতঃপর প্লাবন তাদেরকে গ্রাস করে। কারণ তারা ছিল সীমালংঘনকারী। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3355,29,15,'অতঃপর আমি তাকে এবং যারা তরণীতে আরোহণ করেছিল তাদেরকে রক্ষা করলাম এবং বিশ্ব জগতের জন্য একে করলাম একটি নিদর্শন। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3356,29,16,'স্মরণ কর ইবরাহীমের কথা, সে তার সম্প্রদায়কে বলেছিলঃ তোমরা আল্লাহর ইবাদাত কর এবং তাঁকে ভয় কর, তোমাদের জন্য এটাই শ্রেয় যদি তোমরা জানতে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3357,29,17,'তোমরাতো আল্লাহ ব্যতীত শুধু মূর্তি পূজা করছ এবং মিথ্যা উদ্ভাবন করছ। তোমরা আল্লাহ ব্যতীত যাদের পূজা কর তারা তোমাদের জীবনোপকরনের মালিক নয়। সুতরাং তোমরা জীবনোপকরণ কামনা কর আল্লাহর নিকট এবং তাঁরই ইবাদাত কর ও তাঁর কৃতজ্ঞতা প্রকাশ কর। তোমরা তাঁরই নিকট প্রত্যাবর্তিত হবে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3358,29,18,'তোমরা যদি আমাকে মিথ্যাবাদী বল তাহলে জেনে রেখ যে, তোমাদের পূর্ববর্তীরাও নাবীদেরকে মিথ্যাবাদী বলেছিল। সুস্পষ্টভাবে প্রচার করা ছাড়া রাসূলের আর কোন দায়িত্ব নেই। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3359,29,19,'তারা কি লক্ষ্য করেনা যে, কিভাবে আল্লাহ সৃষ্টিকে অস্তিত্ব দান করেন? অতঃপর পুনরায় সৃষ্টি করবেন। এটাতো আল্লাহর জন্য সহজ। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3360,29,20,'বলঃ পৃথিবীতে পরিভ্রমণ কর এবং অনুধাবন কর কিভাবে তিনি সৃষ্টি শুরু করেছেন? অতঃপর আল্লাহ পুনর্বার সৃষ্টি করবেন পরবর্তী সৃষ্টি। আল্লাহতো সর্ব বিষয়ে সর্বশক্তিমান। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3361,29,21,'তিনি যাকে ইচ্ছা শাস্তি দেন, যার প্রতি ইচ্ছা অনুগ্রহ করেন। তোমরা তাঁরই নিকট প্রত্যাবর্তিত হবে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3362,29,22,'আর তোমরা (তাঁকে) অক্ষমকারী নও যমীনে এবং না আসমানে। আর আল্লাহ ছাড়া তোমাদের কোন অভিভাবক ও সাহায্যকারী নেই।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3363,29,23,'যারা আল্লাহর নিদর্শন ও তাঁর সাক্ষাৎকার অস্বীকার করে তারাই আমার অনুগ্রহ হতে নিরাশ হয়। তাদের জন্য রয়েছে যন্ত্রণাদায়ক শাস্তি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3364,29,24,'সুতরাং ইবরাহীমের সম্প্রদায়ের লোকেরা এ ছাড়া কোন জবাব দিলনা, তারা বললঃ তাকে হত্যা কর অথবা অগ্নিদগ্ধ কর। অতঃপর আল্লাহ তাকে আগুন থেকে রক্ষা করলেন। নিশ্চয়ই এতে নিদর্শন রয়েছে মু’মিন সম্প্রদায়ের জন্য। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3365,29,25,'ইবরাহীম বললঃ তোমরা আল্লাহর পরিবর্তে মূর্তিগুলিকে উপাস্যরূপে গ্রহণ করেছ পার্থিব জীবনে তোমাদের পারস্পরিক বন্ধুত্বের খাতিরে; কিন্তু কিয়ামাত দিবসে তোমরা একে অপরকে অস্বীকার করবে এবং পরস্পরকে অভিসম্পাত দিবে। তোমাদের আবাস হবে জাহান্নাম এবং তোমাদের কোন সাহায্যকারী থাকবে না। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3366,29,26,'লূত তার প্রতি বিশ্বাস স্থাপন করল। (ইবরাহীম) বললঃ আমি আমার রবের উদ্দেশ্যে দেশ ত্যাগ করছি। তিনিতো পরাক্রমশালী, প্রজ্ঞাময়। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3367,29,27,'আমি ইবরাহীমকে দান করলাম ইসহাক ও ইয়াকূব এবং তার বংশধরদের জন্য স্থির করলাম নবুওয়াত ও কিতাব এবং আমি তাকে পুরষ্কৃত করেছিলাম দুনিয়ায় এবং আখিরাতেও। নিশ্চয়ই সে হবে সৎকর্মপরায়ণদের অন্যতম। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3368,29,28,'স্মরণ কর লূতের কথা, সে তার সম্প্রদায়কে বলেছিলঃ তোমরা এমন অশ্লীল কাজ করছো যা তোমাদের পূর্বে বিশ্বে কেহ করেনি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3369,29,29,'তোমরা পুরুষের উপর উপগত হচ্ছ এবং তোমরা রাহাজানি করে থাক এবং তোমরা নিজেদের মজলিশে প্রকাশ্য ঘৃণ্য কাজ করে থাক। উত্তরে তার সম্প্রদায় শুধু এই বললঃ আমাদের উপর আল্লাহর শাস্তি আনয়ন কর, যদি তুমি সত্যবাদী হও। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3370,29,30,'সে বললঃ হে আমার রাব্ব! বিপর্যয় সৃষ্টিকারী সম্প্রদায়ের বিরুদ্ধে আমাকে সাহায্য করুন। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3371,29,31,'যখন আমার প্রেরিত মালাইকা/ফেরেশতারা সুসংবাদসহ ইবরাহীমের নিকট এলো, তারা বলেছিলঃ আমরা এই জনপদবাসীকে ধ্বংস করব, এর অধিবাসীতো সীমা লংঘনকারী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3372,29,32,'ইবরাহীম বললঃ এই জনপদে লূত রয়েছে। তারা বললঃ সেখানে কারা আছে তা আমরা ভাল জানি; আমরাতো লূতকে ও তাঁর পরিজনবর্গকে রক্ষা করবই, তাঁর স্ত্রীকে ব্যতীত; সে পশ্চাতে অবস্থানকারীদের অন্তর্ভুক্ত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3373,29,33,'এবং যখন আমার প্রেরিত মালাইকা/ফেরেশতারা লূতের নিকট এলো তখন তাদের জন্য সে বিষণ্ণ হয়ে পড়ল এবং নিজেকে তাদের রক্ষায় অসমর্থ মনে করল। তারা বললঃ ভয় করনা, দুঃখও করনা; আমরা তোমাকে ও তোমার পরিবারবর্গকে রক্ষা করব, তোমার স্ত্রী ব্যতীত; সে পশ্চাতে অবস্থানকারীদের অন্তর্ভুক্ত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3374,29,34,'আমরা এই জনপদবাসীর উপর আকাশ হতে শাস্তি নাযিল করব, কারণ তারা ছিল পাপাচারী। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3375,29,35,'আমি বোধশক্তি সম্পন্ন সম্প্রদায়ের জন্য এতে একটি স্পষ্ট নিদর্শন রেখেছি। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3376,29,36,'আমি মাদইয়ানবাসীদের প্রতি তাদের ভাই শুআ’ইবকে পাঠিয়েছিলাম। সে বলেছিলঃ হে আমার সম্প্রদায়! তোমরা আল্লাহর ইবাদাত কর, শেষ দিনকে ভয় কর এবং পৃথিবীতে বিপর্যয় ঘটিওনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3377,29,37,'কিন্তু তারা তার প্রতি মিথ্যা আরোপ করল; অতঃপর তারা ভূমিকম্প দ্বারা আক্রান্ত হল; ফলে তারা নিজ গৃহে নতজানু অবস্থায় শেষ হয়ে গেল। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3378,29,38,'এবং আমি ‘আদ ও ছামূদকে ধ্বংস করেছিলাম; তাদের বাড়ীঘরই তোমাদের জন্য এর সুস্পষ্ট প্রমাণ। শাইতান তাদের কাজকে তাদের দৃষ্টিতে সুশোভিত করেছিল এবং তাদেরকে সৎপথ অবলম্বনে বাধা দিয়েছিল, যদিও তারা ছিল বিচক্ষণ। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3379,29,39,'স্মরণ কর কারূন, ফির‘আউন ও হামানকে; মূসা তাদের নিকট সুস্পষ্ট নিদর্শন নিয়ে এসেছিল; তখন তারা দেশে দম্ভ করত; কিন্তু তারা আমার শাস্তি এড়াতে পারেনি। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3380,29,40,'তাদের প্রত্যেককেই তাদের অপরাধের জন্য শাস্তি দিয়েছিলাম; তাদের কারও প্রতি প্রেরণ করেছি প্রস্তরসহ প্রচন্ড ঝটিকা, তাদের কেহকে আঘাত করেছিল মহানাদ, কেহকে আমি প্রোথিত করেছিলাম ভূ-গর্ভে এবং কেহকে করেছিলাম নিমজ্জিত। আল্লাহ তাদের প্রতি কোন যুল্‌ম করেননি; তারা নিজেরাই নিজেদের প্রতি যুল্‌ম করেছিল। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3381,29,41,'আল্লাহর পরিবর্তে যারা অপরকে অভিভাবক রূপে গ্রহণ করে তাদের দৃষ্টান্ত মাকড়সা, যে নিজের জন্য ঘর তৈরী করে; এবং ঘরের মধ্যে মাকড়সার ঘরইতো দুর্বলতম, যদি তারা জানত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3382,29,42,'তারা আল্লাহর পরিবর্তে যা কিছুকে আহবান করে আল্লাহ তা জানেন এবং তিনি পরাক্রমশালী, প্রজ্ঞাময়। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3383,29,43,'মানুষের জন্য এ সব দৃষ্টান্ত বর্ণনা করে থাকি, কিন্তু শুধু জ্ঞানী ব্যক্তিরাই এটা বুঝে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3384,29,44,'আল্লাহ যথাযথভাবে আকাশমন্ডলী ও পৃথিবী সৃষ্টি করেছেন, এতে অবশ্যই নিদর্শন রয়েছে মু’মিন সম্প্রদায়ের জন্য। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3385,29,45,'তুমি তোমার প্রতি প্রত্যাদিষ্ট কিতাব আবৃত্তি কর এবং সালাত প্রতিষ্ঠিত কর। নিশ্চয়ই সালাত বিরত রাখে অশ্লীল ও মন্দ কাজ হতে। আল্লাহর স্মরণই সর্বশ্রেষ্ঠ। তোমরা যা কর, আল্লাহ তা জানেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3386,29,46,'তোমরা উত্তম পন্থা ব্যতীত কিতাবীদের সাথে বিতর্ক করবেনা, তবে তাদের সাথে করতে পার যারা তাদের মধ্যে সীমালংঘনকারী এবং বলঃ আমাদের প্রতি ও তোমাদের প্রতি যা অবতীর্ণ হয়েছে তাতে আমরা বিশ্বাস করি এবং আমাদের মা‘বূদ ও তোমাদের মা‘বূদতো একই এবং আমরা তাঁরই প্রতি আত্মসমর্পণকারী। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3387,29,47,'এভাবেই আমি তোমার প্রতি কুরআন অবতীর্ণ করেছি এবং যাদেরকে আমি কিতাব দিয়েছিলাম তারা এতে বিশ্বাস করে এবং এদেরও (মাক্কাবাসী) কেহ কেহ এতে বিশ্বাস করে। শুধু কাফিরেরাই আমার নিদর্শনাবলী অস্বীকার করে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3388,29,48,'তুমিতো এর পূর্বে কোন কিতাব পাঠ করনি এবং স্বহস্তে কোন দিন কিতাব লিখনি যে, মিথ্যাচারীরা সন্দেহ পোষণ করবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3389,29,49,'বস্তুতঃ যাদেরকে জ্ঞান দেয়া হয়েছে তাদের অন্তরে এটা স্পষ্ট নিদর্শন। শুধু যালিমরাই আমার নিদর্শন অস্বীকার করে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3390,29,50,'তারা বলেঃ রবের নিকট হতে তার প্রতি নিদর্শন প্রেরিত হয়না কেন? বলঃ নিদর্শন আল্লাহর ইচ্ছাধীন, আমিতো একজন প্রকাশ্য সতর্ককারী মাত্র। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3391,29,51,'এটা কি তাদের জন্য যথেষ্ট নয় যে, আমি তোমার নিকট কুরআন অবতীর্ণ করেছি যা তাদের নিকট পাঠ করা হয়? এতে অবশ্যই মু’মিন সম্প্রদায়ের জন্য অনুগ্রহ ও উপদেশ রয়েছে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3392,29,52,'বলঃ আমার ও তোমাদের মধ্যে সাক্ষী হিসাবে আল্লাহই যথেষ্ট। আকাশমন্ডলী ও পৃথিবীতে যা কিছু রয়েছে তা তিনি অবগত। যারা বাতিলকে বিশ্বাস করে ও আল্লাহকে অস্বীকার করে তারাইতো ক্ষতিগ্রস্ত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3393,29,53,'তারা তোমাকে শাস্তি ত্বরান্বিত করতে বলে; যদি নির্ধারিত সময় না থাকত তাহলে শাস্তি তাদের উপর এসে যেত। নিশ্চয়ই তাদের উপর শাস্তি আসবে আকস্মিকভাবে, তাদের অজ্ঞাতসারে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3394,29,54,'তারা তোমাকে শাস্তি ত্বরান্বিত করতে বলে; জাহান্নামতো কাফিরদেরকে পরিবেষ্টন করবেই। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3395,29,55,'সেদিন শাস্তি তাদেরকে আচ্ছন্ন করবে উর্ধ্ব ও অধঃদেশ হতে এবং তিনি বলবেনঃ তোমরা যা করতে তার স্বাদ আস্বাদন কর। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3396,29,56,'হে আমার মু’মিন বান্দারা! আমার পৃথিবী প্রশস্ত; সুতরাং তোমরা আমারই ইবাদাত কর। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3397,29,57,'জীব মাত্রই মৃত্যুর স্বাদ গ্রহণকারী; অতঃপর তোমরা আমারই নিকট প্রত্যাবর্তিত হবে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3398,29,58,'যারা ঈমান আনে ও সৎ কাজ করে আমি অবশ্যই তাদের বসবাসের জন্য সুউচ্চ প্রাসাদ দান করব জান্নাতে, যার পাদদেশে নদী প্রবাহিত, সেখানে তারা স্থায়ী হবে, কত উত্তম প্রতিদান সৎ কর্মশীলদের – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3399,29,59,'যারা ধৈর্য অবলম্বন করে এবং তাদের রবের উপর নির্ভর করে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3400,29,60,'এমন কত জীব জন্তু রয়েছে যারা নিজেদের খাদ্য মওজুদ রাখেনা; আল্লাহই রিয্ক দান করেন তাদেরকে ও তোমাদেরকে এবং তিনি সর্বশ্রোতা, সর্বজ্ঞ। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3401,29,61,'যদি তুমি তাদেরকে জিজ্ঞেস করঃ কে আকাশমন্ডলী ও পৃথিবী সৃষ্টি করেছেন এবং চাঁদ-সূর্যকে নিয়ন্ত্রণ করছেন? তারা অবশ্যই বলবেঃ আল্লাহ! তাহলে তারা কোথায় ফিরে যাচ্ছে? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3402,29,62,'আল্লাহ তাঁর বান্দাদের মধ্যে যাকে ইচ্ছা তার রিয্ক বর্ধিত করেন এবং যার জন্য ইচ্ছা তা সীমিত করেন। আল্লাহ সর্ব বিষয়ে সম্যক অবহিত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3403,29,63,'যদি তুমি তাদেরকে জিজ্ঞেস করঃ ভূমি মৃত হওয়ার পর আকাশ হতে বারি বর্ষণ করে কে ওকে সঞ্জীবিত করে? তারা অবশ্যই বলবেঃ আল্লাহ! বলঃ প্রশংসা আল্লাহরই। কিন্তু তাদের অধিকাংশই এটা অনুভব করেনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3404,29,64,'এই পার্থিব জীবনতো ক্রীড়া-কৌতুক ব্যতীত কিছুই নয়। পারলৌকিক জীবনই প্রকৃত জীবন, যদি তারা জানতো। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3405,29,65,'তারা যখন নৌযানে আরোহণ করে তখন তারা বিশুদ্ধ চিত্তে একনিষ্ঠভাবে আল্লাহকে ডাকে; অতঃপর তিনি যখন স্থলে ভিড়িয়ে তাদেরকে উদ্ধার করেন তখন তারা শিরকে লিপ্ত হয়। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3406,29,66,'তাদের প্রতি আমার দান তারা অস্বীকার করে এবং ভোগ বিলাসে মত্ত থাকে; অচিরেই তারা জানতে পারবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3407,29,67,'তারা কি দেখেনা যে, আমি হারামকে নিরাপদ স্থান করেছি, অথচ এর চতুস্পার্শ্বে যে সব মানুষ আছে তাদের উপর হামলা করা হয়; তাহলে কি তারা অসত্যেই বিশ্বাস করবে এবং আল্লাহর অনুগ্রহ অস্বীকার করবে? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3408,29,68,'যে ব্যক্তি আল্লাহ সম্বন্ধে মিথ্যা রচনা করে অথবা তাঁর নিকট হতে আগত সত্যকে অস্বীকার করে তার অপেক্ষা অধিক যালিম আর কে? জাহান্নামই কি কাফিরদের আবাসস্থল নয়? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3409,29,69,'যারা আমার উদ্দেশ্যে সংগ্রাম করে, আমি তাদেরকে অবশ্যই আমার পথে পরিচালিত করব। আল্লাহ অবশ্যই সৎকর্মপরায়ণদের সাথে আছেন। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3410,30,1,'আলিফ লাম মীম। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3411,30,2,'রোমকরা পরাজিত হয়েছে – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3412,30,3,'নিকটবর্তী অঞ্চলে; কিন্তু তারা তাদের এই পরাজয়ের পর শীঘ্রই বিজয়ী হবে – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3413,30,4,'কয়েক বছরের মধ্যেই। পূর্বের ও পরের সিদ্ধান্ত আল্লাহরই। আর সেদিন মু’মিনরা হর্ষোৎফুল্ল হবে – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3414,30,5,'আল্লাহর সাহায্যে, তিনি যাকে ইচ্ছা সাহায্য করেন এবং তিনি পরাক্রমশালী, পরম দয়ালু। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3415,30,6,'এটা আল্লাহরই প্রতিশ্রুতি; আল্লাহ তাঁর প্রতিশ্রুতি ভঙ্গ করেননা, কিন্তু অধিকাংশ লোক জানেনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3416,30,7,'তারা পার্থিব জীবনের বাহ্যিক দিক সম্বন্ধে অবগত, আর আখিরাত সম্বন্ধে তারা গাফিল। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3417,30,8,'তারা কি নিজেদের অন্তরে ভেবে দেখেনা যে, আল্লাহ আকাশমন্ডলী, পৃথিবী ও এতদুভয়ের অন্তবর্তী সব কিছু সৃষ্টি করেছেন যথাযথভাবেই এক নির্দিষ্ট কালের জন্য? কিন্তু মানুষের মধ্যে অনেকেই তাদের রবের সাক্ষাতে অবিশ্বাসী। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3418,30,9,'তারা কি পৃথিবীতে ভ্রমণ করেনা এবং দেখেনা যে, তাদের পূর্ববর্তীদের পরিণাম কিরূপ হয়েছে? শক্তিতে তারা ছিল তাদের অপেক্ষা প্রবল; তারা জমি চাষ করত, তারা (পূর্ববর্তীরা) ওটা আবাদ করত তাদের অপেক্ষা অধিক। তাদের নিকট এসেছিল তাদের রাসূলগণ সুস্পষ্ট নিদর্শনসহ; বস্তুতঃ তাদের প্রতি যুলম করা আল্লাহর কাজ ছিলনা, বরং তারা নিজেরাই নিজেদের প্রতি যুলম করেছিল। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3419,30,10,'অতঃপর যারা মন্দ কাজ করেছিল তাদের পরিণাম হয়েছে মন্দ; কারণ তারা আল্লাহর আয়াতসমূহ প্রত্যাখ্যান করত এবং তা নিয়ে ঠাট্টা-বিদ্রুপ করত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3420,30,11,'আল্লাহ আদিতে সৃষ্টি করেন, অতঃপর তিনি এর পুনরাবৃত্তি করবেন, তখন তোমরা তাঁরই নিকট প্রত্যাবর্তিত হবে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3421,30,12,'যেদিন কিয়ামাত হবে সেদিন অপরাধীরা হতাশ হয়ে পড়বে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3422,30,13,'তাদের দেব-দেবীগুলো তাদের জন্য সুপারিশ করবেনা এবং তারাই তাদের দেব-দেবীগুলোকে অস্বীকার করবে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3423,30,14,'যেদিন কিয়ামাত সংঘটিত হবে সেদিন মানুষ বিভক্ত হয়ে পড়বে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3424,30,15,'অতএব যারা ঈমান এনেছে ও সৎ কাজ করেছে তারা জান্নাতে আনন্দে থাকবে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3425,30,16,'আর যারা কুফরী করেছে এবং আমার নিদর্শনাবলী ও পরকালের সাক্ষাৎকার অস্বীকার করেছে তারাই শাস্তি ভোগ করতে থাকবে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3426,30,17,'সুতরাং তোমরা আল্লাহর পবিত্রতা ও মহিমা ঘোষণা কর সন্ধ্যায় ও প্রভাতে – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3427,30,18,'আর অপরাহ্নে ও যুহরের সময়; এবং আকাশমন্ডলী ও পৃথিবীর সকল প্রশংসা তাঁরই। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3428,30,19,'তিনিই মৃত হতে জীবন্তের এবং জীবন্ত হতে মৃতের আবির্ভাব ঘটান এবং ভূমির মৃত্যুর পর ওকে পুনরুজ্জীবিত করেন। এভাবেই তোমরা উত্থিত হবে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3429,30,20,'তাঁর নিদর্শনাবলীর মধ্যে রয়েছে যে, তিনি তোমাদেরকে মাটি হতে সৃষ্টি করেছেন। এখন তোমরা মানুষ, সর্বত্র ছড়িয়ে পড়েছ। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3430,30,21,'এবং তাঁর নিদর্শনাবলীর মধ্যে রয়েছে যে, তিনি তোমাদের জন্য তোমাদের মধ্য হতে সৃষ্টি করেছেন তোমাদের সঙ্গিনীদেরকে যাতে তোমরা তাদের সাথে শান্তিতে বাস করতে পার এবং তিনি তোমাদের মধ্যে পারস্পরিক ভালবাসা ও দয়া সৃষ্টি করেছেন। চিন্তাশীল সম্প্রদায়ের জন্য এতে অবশ্যই বহু নিদর্শন রয়েছে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3431,30,22,'এবং তাঁর নিদর্শনাবলীর মধ্যে রয়েছে আকাশমন্ডলী ও পৃথিবীর সৃষ্টি এবং তোমাদের ভাষা ও বর্ণের বৈচিত্র্য। এতে জ্ঞানীদের জন্য অবশ্যই নিদর্শন রয়েছে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3432,30,23,'এবং তাঁর নিদর্শনাবলীর মধ্যে রয়েছে রাত ও দিনে তোমাদের নিদ্রা এবং তাঁর অনুগ্রহ অন্বেষণ। এতে অবশ্যই নিদর্শন রয়েছে শ্রবণকারী সম্প্রদায়ের জন্য। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3433,30,24,'এবং তাঁর নিদর্শনাবলীর মধ্যে রয়েছে যে, তিনি তোমাদেরকে প্রদর্শন করেন বিদ্যুৎ, ভয় ও ভরসা সঞ্চারক রূপে এবং তিনি আকাশ হতে বারি বর্ষণ করেন এবং তদ্বারা ভূমিকে ওর মৃত্যুর পর পুনরুজ্জীবিত করেন, এতে অবশ্যই নিদর্শন রয়েছে বোধশক্তি সম্পন্ন সম্প্রদায়ের জন্য। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3434,30,25,'তাঁর নিদর্শনাবলীর মধ্যে রয়েছে যে, তাঁরই আদেশে আকাশ ও পৃথিবীর স্থিতি; অতঃপর তিনি (আল্লাহ) যখন তোমাদেরকে মাটি হতে উঠার জন্য একবার আহবান করবেন তখন তোমরা উঠে আসবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3435,30,26,'আকাশমন্ডলী ও পৃথিবীতে যা কিছু আছে তা তাঁরই। সবাই তাঁর আজ্ঞাবহ। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3436,30,27,'তিনিই প্রথমবার সৃষ্টি করেছেন, অতঃপর তিনি এটাকে সৃষ্টি করবেন পুনর্বার; এটা তাঁর জন্য অতি সহজ। আকাশমন্ডলী ও পৃথিবীতে সর্বোচ্চ মর্যাদা তাঁরই, এবং তিনি পরাক্রমশালী, প্রজ্ঞাময়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3437,30,28,'(আল্লাহ) তোমাদের জন্য তোমাদের নিজেদের মধ্যে একটি দৃষ্টান্ত পেশ করছেনঃ তোমাদেরকে আমি যে রিয্ক দিয়েছি তোমাদের অধিকারভুক্ত দাস-দাসীদের কেহ কি তাতে তোমাদের সমান অংশীদার? তোমরা কি তাদেরকে সেরূপ ভয় কর যেরূপ তোমরা পরস্পরকে ভয় কর? এভাবেই আমি বোধশক্তি সম্পন্ন সম্প্রদায়ের জন্য নির্দেশনাবলী বিবৃত করি। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3438,30,29,'বস্তুতঃ সীমালংঘনকারীরা অজ্ঞতা বশতঃ তাদের খেয়াল খুশীর অনুসরণ করে, সুতরাং আল্লাহ যাকে পথভ্রষ্ট করেন, কে তাকে সৎ পথে পরিচালিত করবে? তাদের কোন সাহায্যকারী নেই। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3439,30,30,'তুমি একনিষ্ঠ হয়ে নিজেকে দীনের উপর প্রতিষ্ঠিত কর। আল্লাহর প্রকৃতির অনুসরণ কর, যে প্রকৃতি অনুযায়ী তিনি মানুষ সৃষ্টি করেছেন; আল্লাহর সৃষ্টির কোন পরিবর্তন নেই। এটাই সরল দীন। কিন্তু অধিকাংশ মানুষ জানেনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3440,30,31,'বিশুদ্ধ চিত্তে তাঁর অভিমুখী হয়ে তাঁকে ভয় কর, সালাত কায়েম কর এবং মুশরিকদের অন্তর্ভুক্ত হয়োনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3441,30,32,'যারা নিজেদের দীনে মতভেদ সৃষ্টি করেছে এবং বিভিন্ন দলে বিভক্ত হয়েছে, প্রত্যেক দলই নিজ নিজ মতবাদ নিয়ে উৎফুল্ল। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3442,30,33,'মানুষকে যখন দুঃখ-দৈন্য স্পর্শ করে তখন তারা বিশুদ্ধ চিত্তে তাদের রাববকে ডাকে, অতঃপর তিনি যখন তাদেরকে স্বীয় অনুগ্রহ আস্বাদন করান তখন তাদের এক দল তাদের রবের সাথে শরীক করে – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3443,30,34,'তাদেরকে আমি যা দিয়েছি তা অস্বীকার করার জন্য। সুতরাং ভোগ করে নাও, শীঘ্রই তোমরা জানতে পারবে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3444,30,35,'আমি কি তাদের নিকট এমন কোন দলীল অবতীর্ণ করেছি যা তাদেরকে আমার শরীক করতে বলে? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3445,30,36,'আমি যখন মানুষকে অনুগ্রহের আস্বাদ দিই তখন তারা উৎফুল্ল হয় এবং তাদের কৃতকর্মের ফলে দুর্দশাগ্রস্ত হলেই তারা হতাশ হয়ে পড়ে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3446,30,37,'তারা কি লক্ষ্য করেনা যে, আল্লাহ যার জন্য ইচ্ছা তার রিয্ক প্রশস্ত করেন অথবা তা সীমিত করেন? এতে অবশ্যই নিদর্শন রয়েছে মু’মিন সম্প্রদায়ের জন্য। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3447,30,38,'অতএব আত্মীয়কে দিয়ে দাও তার প্রাপ্য এবং অভাবগ্রস্ত ও মুসাফিরকেও। যারা আল্লাহর সন্তুষ্টি কামনা করে তাদের জন্য এটা শ্রেয় এবং তারাই সফলকাম। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3448,30,39,'মানুষের ধন সম্পদ বৃদ্ধি পাবে, এ আশায় সুদে যা কিছু তোমরা দিয়ে থাক, আল্লাহর দৃষ্টিতে তা বৃদ্ধি পায়না। বরং আল্লাহর সন্তুষ্টি লাভের আশায় তোমরা যা দান কর তার পরিবর্তে তোমরা বহুগুণ প্রাপ্ত হবে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3449,30,40,'আল্লাহই তোমাদেরকে সৃষ্টি করেছেন, অতঃপর তোমাদেরকে রিয্ক দিয়েছেন; তিনি তোমাদের মৃত্যু ঘটাবেন এবং পরে তোমাদেরকে জীবিত করবেন। তোমাদের দেব-দেবীগুলোর এমন কেহ আছে কি, যে এ সবের কোন একটিও করতে পারে? তারা যাদেরকে শরীক করে, আল্লাহ তা হতে পবিত্র, মহান। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3450,30,41,'মানুষের কৃতকর্মের কারণে সমুদ্রে ও স্থলে বিপর্যয় ছড়িয়ে পড়ে, যার ফলে তাদেরকে কোন কোন কর্মের শাস্তি তিনি আস্বাদন করান, যাতে তারা ফিরে আসে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3451,30,42,'বলঃ তোমরা পৃথিবীতে পরিভ্রমণ কর এবং দেখ, তোমাদের পূর্ববর্তীদের পরিণাম কি হয়েছে! তাদের অধিকাংশই ছিল মুশরিক। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3452,30,43,'তুমি সরল দীনে নিজেকে প্রতিষ্ঠিত কর, আল্লাহর নির্দেশে অনিবার্য দিন আসার পূর্বে, সেদিন মানুষ বিভক্ত হয়ে পড়বে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3453,30,44,'যে কুফরী করে, কুফরীর শাস্তি তারই; যারা সৎ কাজ করে তারা নিজেদেরই জন্য রচনা করে শান্তির আবাস। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3454,30,45,'কারণ যারা ঈমান আনে ও সৎ কাজ করে, তিনি (আল্লাহ) তাদেরকে নিজ অনুগ্রহে পুরস্কৃত করেন। তিনি কাফিরদেরকে পছন্দ করেননা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3455,30,46,'তাঁর নিদর্শনাবলীর একটি এই যে, তিনি বায়ু প্রেরণ করেন সুসংবাদ দেয়ার জন্য ও তোমাদেরকে তাঁর অনুগ্রহ আস্বাদন করানোর জন্য, এবং যেন তাঁর বিধানে নৌযানগুলি বিচরণ করে, যাতে তোমরা তাঁর অনুগ্রহ সন্ধান করতে পার এবং তাঁর প্রতি কৃতজ্ঞ হও। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3456,30,47,'আমি তোমার পূর্বে রাসূলদেরকে প্রেরণ করেছিলাম তাদের নিজ নিজ সম্প্রদায়ের নিকট। তারা তাদের নিকট সুস্পষ্ট নিদর্শন নিয়ে এসেছিল; অতঃপর আমি অপরাধীদেরকে শাস্তি দিয়েছিলাম। মু’মিনদেরকে সাহায্য করা আমার দায়িত্ব। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3457,30,48,'আল্লাহ! তিনি বায়ু প্রেরণ করেন; ফলে এটা মেঘমালাকে সঞ্চারিত করে, অতঃপর তিনি একে যেমন ইচ্ছা আকাশে ছড়িয়ে দেন, পরে একে খন্ড বিখন্ড করেন এবং তুমি দেখতে পাও ওটা হতে নির্গত হয় বারিধারা; অতঃপর যখন তিনি তাঁর বান্দাদের মধ্যে যাদেরকে ইচ্ছা তাদের নিকট ওটা পৌঁছে দেন তখন তারা হয় হর্ষোৎফুল্ল – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3458,30,49,'যদিও তারা তাদের প্রতি বৃষ্টি বর্ষণের পূর্বে নিরাশ ছিল। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3459,30,50,'আল্লাহর অনুগ্রহের ফল সম্বন্ধে চিন্তা কর - কিভাবে তিনি ভূমির মৃত্যুর পর ওকে পুনরুজ্জীবিত করেন! এভাবেই আল্লাহ মৃতকে জীবিত করেন, কারণ তিনি সর্ব বিষয়ে সর্ব শক্তিমান। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3460,30,51,'এবং আমি যদি এমন বায়ু প্রেরণ করি যার ফলে তারা দেখে যে, শষ্য পীত বর্ণ ধারণ করেছে তখনতো তারা অকৃতজ্ঞ হয়ে পড়ে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3461,30,52,'তুমিতো মৃতকে শোনাতে পারবেনা, বধিরকেও পারবেনা আহবান শোনাতে, যখন তারা পৃষ্ঠ প্রদর্শন করে সরে পড়ে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3462,30,53,'আর তুমি অন্ধকেও ফিরিয়ে আনতে পারবে না তাদের পথভ্রষ্টতা হতে। যারা আমার নিদর্শনাবলীতে বিশ্বাস করে শুধু তাদেরকেই তুমি শোনাতে পারবে, কারণ তারা আত্মসমর্পনকারী। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3463,30,54,'তিনিই আল্লাহ! যিনি তোমাদেরকে সৃষ্টি করেন দুর্বল অবস্থায়; দুর্বলতার পর তিনি দেন শক্তি, শক্তির পর আবার দেন দুর্বলতা ও বার্ধক্য। তিনি যা ইচ্ছা সৃষ্টি করেন এবং তিনিই সর্বজ্ঞ, সর্বশক্তিমান। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3464,30,55,'যেদিন কিয়ামাত হবে সেদিন অপরাধীরা শপথ করে বলবে যে, তারা এক ঘন্টার বেশি (দুনিয়ায়) অবস্থান করেনি। এভাবেই তারা সত্যভ্রষ্ট হত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3465,30,56,'কিন্তু যাদেরকে জ্ঞান ও ঈমান দেয়া হয়েছে তারা বলবেঃ তোমরাতো আল্লাহর বিধানে পুনরুত্থান দিবস পর্যন্ত অবস্থান করেছ। এটাইতো পুনরুত্থান দিবস, কিন্তু তোমরা জানতেনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3466,30,57,'সেদিন সীমা লংঘনকারীর ওযর আপত্তি তাদের কাজে আসবেনা এবং তাদেরকে (আল্লাহর) সন্তুষ্টি লাভের সুযোগও দেয়া হবেনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3467,30,58,'আমিতো মানুষের জন্য এই কুরআনে সর্ব প্রকার দৃষ্টান্ত দিয়েছি। তুমি যদি তাদের নিকট কোন নিদর্শন হাজির কর তাহলে কাফিরেরা অবশ্যই বলবেঃ তোমরাতো মিথ্যাশ্রয়ী। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3468,30,59,'যাদের জ্ঞান নেই আল্লাহ তাদের হৃদয় এভাবে মোহর করে দেন। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3469,30,60,'অতএব তুমি ধৈর্য ধারণ কর, নিশ্চয়ই আল্লাহর প্রতিশ্রুতি সত্য। যারা দৃঢ় বিশ্বাসী নয় তারা যেন তোমাকে বিচলিত করতে না পারে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3470,31,1,'আলিফ লাম মীম ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3471,31,2,'এগুলি জ্ঞানগর্ভ কিতাবের আয়াত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3472,31,3,'পথ নির্দেশ ও দয়া স্বরূপ, সৎকর্মপরায়ণদের জন্য। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3473,31,4,'যারা সালাত কায়েম করে, যাকাত দেয়, তারাই আখিরাতে নিশ্চিত বিশ্বাসী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3474,31,5,'তারাই তাদের রবের নির্দেশিত পথে রয়েছে এবং তারাই সফলকাম। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3475,31,6,'মানুষের মধ্যে কেহ কেহ অজ্ঞতা বশতঃ আল্লাহর পথ হতে বিচ্যুত করার জন্য অসার বাক্য ক্রয় করেএবং আল্লাহর প্রদর্শিত পথ নিয়ে ঠাট্টা-বিদ্রুপ করে, তাদেরই জন্য রয়েছে অবমাননাকর শাস্তি। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3476,31,7,'যখন তার নিকট আমার আয়াত আবৃত্তি করা হয় তখন সে দম্ভভরে মুখ ফিরিয়ে নেয়, যেন সে এটা শুনতে পায়নি, যেন তার কর্ণ দু’টি বধির; অতএব তাদেরকে যন্ত্রণাদায়ক শাস্তির সুসংবাদ দাও। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3477,31,8,'যারা ঈমান আনে ও সৎ কাজ করে তাদের জন্য রয়েছে সুখ কানন, ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3478,31,9,'সেখানে তারা স্থায়ী হবে। আল্লাহর প্রতিশ্রুতি সত্য। তিনি পরাক্রমশালী, প্রজ্ঞাময়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3479,31,10,'তিনি আকাশমন্ডলী নির্মাণ করেছেন স্তম্ভ ব্যতীত, তোমরা এটা দেখছ। তিনিই পৃথিবীতে স্থাপন করেছেন পবর্তমালা যাতে এটা তোমাদেরকে নিয়ে ঢলে না পড়ে এবং এতে ছড়িয়ে দিয়েছেন সর্ব প্রকার জীব-জন্তু এবং আমিই আকাশ হতে বারি বর্ষণ করে এতে উদ্ভব করি সর্বপ্রকার কল্যাণকর উদ্ভিদ। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3480,31,11,'এটা আল্লাহর সৃষ্টি! তিনি ছাড়া অন্যরা কি সৃষ্টি করেছে আমাকে দেখাও। সীমালংঘনকারীরাতো স্পষ্ট বিভ্রান্তিতে রয়েছে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3481,31,12,'আর আমি তো লুকমানকে হিকমাত  দিয়েছিলাম (এবং বলেছিলাম) যে, ‘আল্লাহর শুকরিয়া আদায় কর। আর যে শুকরিয়া আদায় করে সে তো নিজের জন্যই শুকরিয়া আদায় করে এবং যে অকৃতজ্ঞ হয় (তার জেনে রাখা উচিত) আল্লাহ অমুখাপেক্ষী, প্রশংসিত’।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3482,31,13,'স্মরণ কর, যখন লুকমান উপদেশাচ্ছলে তার পুত্রকে বলেছিলঃ হে বৎস! আল্লাহর সাথে কোন শরীক করনা। নিশ্চয়ই শিরক হচ্ছে চরম যুলম।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3483,31,14,'আমিতো মানুষকে তার মাতাপিতার প্রতি সদাচরণের নির্দেশ দিয়েছি। মা সন্তানকে কষ্টের পর কষ্ট বরণ করে গর্ভে ধারণ করে এবং তার দুধ ছাড়ানো হয় দুই বছরে। সুতরাং আমার প্রতি ও তোমার মাতাপিতার প্রতি কৃতজ্ঞ হও। প্রত্যাবর্তনতো আমারই নিকট। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3484,31,15,'তোমার মাতা-পিতা যদি তোমাকে পীড়াপীড়ি করে আমার সাথে শরীক করতে যে বিষয়ে তোমার কোন জ্ঞান নেই তাহলে তুমি তাদের কথা মানবেনা। তবে পৃথিবীতে তাদের সাথে বসবাস করবে সদ্ভাবে এবং যে বিশুদ্ধ চিত্তে আমার অভিমুখি হয়েছে তার পথ অবলম্বন কর, অতঃপর তোমাদের প্রত্যাবর্তন আমারই নিকট এবং তোমরা যা করতে সে বিষয়ে আমি তোমাদেরকে অবহিত করব। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3485,31,16,'হে বৎস! কোন কিছু যদি সরিষার দানা পরিমানও হয় এবং তা যদি থাকে শিলাগর্ভে অথবা আকাশে কিংবা মাটির নীচে, আল্লাহ ওটাও হাযির করবেন। আল্লাহ সূক্ষ্মদর্শী, সর্ববিষয়ে খবর রাখেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3486,31,17,'হে বৎস! সালাত কায়েম করবে, ভাল কাজের আদেশ করবে ও মন্দ কাজ হতে নিষেধ করবে এবং আপদে-বিপদে ধৈর্য ধারণ করবে, এটাইতো দৃঢ় সংকল্পের কাজ। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3487,31,18,'অহংকার বশে তুমি মানুষকে অবজ্ঞা করনা এবং পৃথিবীতে উদ্ধতভাবে বিচরণ করনা। কারণ আল্লাহ কোন উদ্ধত, অহংকারীকে পছন্দ করেননা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3488,31,19,'পদচারণায় মধ্য পন্থা অবলম্বন করবে এবং তোমার কন্ঠস্বর করবে নীচু; স্বরের মধ্যে গাধার স্বরই সর্বাপেক্ষা অপ্রীতিকর। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3489,31,20,'তোমরা কি দেখনা যে, আকাশমন্ডলী ও পৃথিবীতে যা কিছু আছে সবই আল্লাহ তোমাদের কল্যাণে নিয়োজিত করেছেন এবং তোমাদের প্রতি তাঁর প্রকাশ্য ও অপ্রকাশ্য অনুগ্রহ সম্পূর্ণ করেছেন? মানুষের মধ্যে কেহ কেহ অজ্ঞতা বশতঃ আল্লাহ সম্বন্ধে বিতন্ডা করে, তাদের না আছে পথ নির্দেশক আর না আছে কোন দীপ্তিমান কিতাব।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3490,31,21,'তাদেরকে যখন বলা হয় - আল্লাহ যা অবতীর্ণ করেছেন তা অনুসরণ কর তখন তারা বলেঃ বরং আমরা আমাদের পিতৃপুরুষদেরকে যাতে পেয়েছি তারই অনুসরণ করব। শাইতান যদি তাদেরকে জ্বলন্ত অগ্নির শাস্তির দিকে আহবান করে তবুও কি? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3491,31,22,'যদি কেহ সৎ কর্মপরায়ণ হয়ে আল্লাহর নিকট আত্মসমর্পন করে তাহলে সেতো দৃঢ়ভাবে ধারণ করে এক মাযবূত হাতল, যাবতীয় কার্যের পরিণাম আল্লাহর দিকে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3492,31,23,'কেহ কুফরী করলে তার কুফরী যেন তোমাকে ক্লিষ্ট না করে। আমারই নিকট তাদের প্রত্যাবর্তন। অতঃপর আমি তাদেরকে অবহিত করব তারা যা করত। অন্তরে যা রয়েছে সে সম্বন্ধে আল্লাহ সবিশেষ অবহিত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3493,31,24,'আমি তাদেরকে জীবনোপকরণ ভোগ করতে দিব স্বল্পকালের জন্য। অতঃপর তাদেরকে কঠিন শাস্তি ভোগ করতে বাধ্য করব। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3494,31,25,'তুমি যদি তাদেরকে জিজ্ঞেস করঃ আকাশমন্ডলী ও পৃথিবী কে সৃষ্টি করেছেন? তাহলে তারা নিশ্চয়ই বলবেঃ আল্লাহ! বলঃ প্রশংসা আল্লাহর। কিন্তু তাদের অধিকাংশই জানেনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3495,31,26,'আকাশমন্ডলী ও পৃথিবীতে যা কিছু আছে তা আল্লাহরই। আল্লাহ অভাবমুক্ত, প্রশংসা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3496,31,27,'পৃথিবীর সমস্ত বৃক্ষ যদি কলম হয় এবং এই যে সমুদ্র, এর সাথে যদি আরও সাতটি সমুদ্র যুক্ত হয়ে কালি হয় তবুও আল্লাহর বাণী নিঃশেষ হবেনা। আল্লাহ পরাক্রমশালী, প্রজ্ঞাময়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3497,31,28,'তোমাদের সকলের সৃষ্টি ও পুনরুত্থান একটি মাত্র প্রাণীর সৃষ্টি ও পুনরুত্থানের অনুরূপ। আল্লাহ সর্বশ্রোতা, সম্যক দ্রষ্টা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3498,31,29,'তুমি কি দেখনা যে, আল্লাহ রাতকে দিনে এবং দিনকে রাতে প্রবেশ করান? তিনি চাঁদ-সূর্যকে করেছেন নিয়মাধীন, প্রত্যেকটি বিচরণ করে নির্দিষ্ট কাল পর্যন্ত; তোমরা যা কর আল্লাহ সে সম্পর্কে অবহিত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3499,31,30,'এর কারণ এই যে, আল্লাহ সত্য এবং তারা তাঁর পরিবর্তে যাকে ডাকে তা মিথ্যা। আল্লাহতো সমুচ্চ, মহান। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3500,31,31,'তুমি কি লক্ষ্য করনা যে, আল্লাহর অনুগ্রহে নৌযানগুলি সমুদ্রে বিচরণ করে, যদ্দবারা তিনি তোমাদেরকে তাঁর নিদর্শনাবলীর কিছু প্রদর্শন করেন? এতে অবশ্যই নিদর্শন রয়েছে প্রত্যেক ধৈর্যশীল, কৃতজ্ঞ ব্যক্তির জন্য। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3501,31,32,'যখন তরঙ্গ তাদেরকে আচ্ছন্ন করে মেঘচ্ছায়ার মত তখন তারা আল্লাহকে ডাকে বিশুদ্ধ চিত্তে। কিন্তু যখন তিনি তাদেরকে উদ্ধার করে স্থলে পৌঁছান তখন তাদের কেহ কেহ সরল পথে থাকে। শুধু বিশ্বাসঘাতক, অকৃতজ্ঞ ব্যক্তিই তাঁর নির্দেশাবলী অস্বীকার করে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3502,31,33,'হে লোকসকল! তোমরা তোমাদের রাববকে ভয় কর এবং ভয় কর সেই দিনের যখন পিতা সন্তানের কোন উপকারে আসবেনা এবং সন্তানও কোন উপকারে আসবেনা তার পিতার। নিশ্চয়ই আল্লাহর প্রতিশ্রুতি সত্য, সুতরাং পার্থিব জীবন যেন তোমাদেরকে কিছুতেই প্রতারিত না করে এবং সেই প্রবঞ্চক যেন তোমাদেরকে কিছুতেই আল্লাহ সম্পর্কে প্রবঞ্চিত না করে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3503,31,34,'কিয়ামাতের জ্ঞান শুধু আল্লাহর নিকট রয়েছে, তিনিই বৃষ্টি বর্ষণ করেন এবং তিনিই জানেন যা জরায়ুতে রয়েছে। কেহ জানেনা আগামীকাল সে কী অর্জন করবে এবং কেহ জানেনা কোন্ স্থানে তার মৃত্যু ঘটবে। আল্লাহ সর্বজ্ঞ, সর্ব বিষয়ে অবহিত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3504,32,1,'আলিফ লাম মীম। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3505,32,2,'এই কিতাব জগতসমূহের রবের নিকট হতে অবতীর্ণ, এতে কোন সন্দেহ নেই। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3506,32,3,'তাহলে কি তারা বলেঃ এটি সে নিজে রচনা করেছে? না, এটি তোমার রাবব হতে আগত সত্য, যাতে তুমি এমন এক সম্প্রদায়কে সতর্ক করতে পার যাদের নিকট তোমার পূর্বে কোন সতর্ককারী আসেনি। হয়তো তারা সৎ পথে চলবে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3507,32,4,'আল্লাহ, তিনি আকাশমন্ডলী, পৃথিবী ও এতদুভয়ের অন্তবর্তী সব কিছু সৃষ্টি করেছেন ছয় দিনে। অতঃপর তিনি আরশে সমাসীন হন। তিনি ছাড়া তোমাদের কোন অভিভাবক নেই এবং সাহায্যকারীও নেই, তবুও কি তোমরা উপদেশ গ্রহণ করবেনা? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3508,32,5,'তিনি আকাশ হতে পৃথিবী পর্যন্ত সমুদয় বিষয় পরিচালনা করেন, অতঃপর একদিন সব কিছুই তাঁর সমীপে সমুত্থিত হবে, যে দিনের পরিমাপ হবে তোমাদের হিসাবে হাজার বছরের সমান।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3509,32,6,'তিনিই দৃশ্য ও অদৃশ্যের পরিজ্ঞাতা, পরাক্রমশালী, পরম দয়ালু – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3510,32,7,'যিনি তাঁর প্রত্যেক সৃষ্টিকে সৃষ্টি করেছেন উত্তম রূপে এবং কাদা মাটি হতে মানব সৃষ্টির সূচনা করেছেন। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3511,32,8,'অতঃপর তার বংশ উৎপন্ন করেছেন তুচ্ছ তরল পদার্থের নির্যাস হতে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3512,32,9,'পরে তিনি ওকে করেছেন সুষম এবং ওতে ফুঁকে দিয়েছেন রুহ্ তাঁর নিকট হতে এবং তোমাদেরকে দিয়েছেন কর্ণ, চক্ষু ও অন্তঃকরণ; তোমরা অতি সামান্যই কৃতজ্ঞতা প্রকাশ করে থাক। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3513,32,10,'তারা বলেঃ আমরা মাটিতে পর্যবসিত হলেও কি আমাদেরকে আবার নতুন করে সৃষ্টি করা হবে? বস্তুতঃ তারা তাদের রবের সাক্ষাৎকার অস্বীকার করে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3514,32,11,'বলঃ তোমাদের জন্য নিযুক্ত মৃত্যুর মালাক/ফেরেশতা তোমাদের প্রাণ হরণ করবে। অবশেষে তোমরা তোমাদের রবের নিকট প্রত্যাবর্তীত হবে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3515,32,12,'এবং হায়! তুমি যদি দেখতে! যখন অপরাধীরা তাদের রবের সামনে অধোবদন হয়ে বলবেঃ হে আমাদের রাবব! আমরা প্রত্যক্ষ করলাম ও শ্রবণ করলাম; এখন আপনি আমাদেরকে পুনরায় প্রেরণ করুন, আমরা সৎ কাজ করব, আমরাতো দৃঢ় বিশ্বাসী। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3516,32,13,'আমি ইচ্ছা করলে প্রত্যেক ব্যক্তিকে সৎ পথে পরিচালিত করতে পারতাম; কিন্তু আমার এ কথা অবশ্যই সত্যঃ আমি নিশ্চয়ই জিন ও মানুষ উভয় দ্বারা জাহান্নাম পূর্ণ করব। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3517,32,14,'এখন শাস্তি আস্বাদন কর, কারণ আজকের এই সাক্ষাৎকারের কথা তোমরা বিস্মৃত হয়েছিলে। আমিও তোমাদেরকে বিস্মৃত হলাম, তোমরা যা করতে তজ্জন্য তোমরা স্থায়ী শাস্তি ভোগ করতে থাক। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3518,32,15,'শুধু তারাই আমার নিদর্শনাবলী বিশ্বাস করে যারা ওর দ্বারা উপদিষ্ট হলে সাজদাহয় লুটিয়ে পড়ে এবং তাদের রবের সপ্রশংস পবিত্রতা ও মহিমা ঘোষণা করে এবং অহংকার করেনা। [সাজদাহ]');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3519,32,16,'তারা শয্যা ত্যাগ করে তাদের রাববকে ডাকে আশায় ও আশংকায়, এবং তাদেরকে যে রিয্ক দান করেছি তা হতে তারা ব্যয় করে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3520,32,17,'কেহই জানেনা, তাদের জন্য নয়ন প্রীতিকর কি কি প্রতিদান লুকায়িত রয়েছে তাদের কৃতকর্মের পুরস্কার স্বরূপ। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3521,32,18,'তাহলে কি যে ব্যক্তি মু’মিন হয়েছে সে পাপাচারীর ন্যায়? তারা সমান নয়। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3522,32,19,'যারা ঈমান আনে এবং সৎ কাজ করে তাদের কৃতকর্মের ফল স্বরূপ তাদের আপ্যায়নের জন্য জান্নাত হবে তাদের বাসস্থল। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3523,32,20,'আর যারা পাপাচার করেছে তাদের বাসস্থান হবে জাহান্নাম; যখনই তারা জাহান্নাম হতে বের হতে চাবে তখনই তাদের ফিরিয়ে দেয়া হবে তাতে এবং তাদেরকে বলা হবেঃ যে আগুনের শাস্তিকে তোমরা মিথ্যা বলতে তা আস্বাদন কর। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3524,32,21,'বড় শাস্তির পূর্বে তাদেরকে আমি অবশ্যই লঘু শাস্তি আস্বাদন করাব, যাতে তারা ফিরে আসে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3525,32,22,'যে ব্যক্তি তার রবের নির্দেশনাবলী দ্বারা উপদিষ্ট হয়ে তা হতে মুখ ফিরিয়ে নেয় তার অপেক্ষা অধিক যালিম আর কে? আমি অবশ্যই অপরাধীদেরকে শাস্তি দিয়ে থাকি। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3526,32,23,'আমিতো মূসাকে কিতাব দিয়েছিলাম; অতএব তুমি তার সাক্ষাত সম্বন্ধে সন্দেহ করনা, আমি তাকে বানী ইসরাঈলের জন্য পথ নির্দেশক করেছিলাম। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3527,32,24,'আর আমি তাদের মধ্য হতে নেতা মনোনীত করেছিলাম যারা আমার নির্দেশ অনুসারে পথ প্রদর্শন করত। যখন তারা ধৈর্য ধারণ করত তখন তারা ছিল আমার নিদর্শনাবলীতে দৃঢ় বিশ্বাসী। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3528,32,25,'তারা নিজেদের মধ্যে যে বিষয়ে মতবিরোধ করছে তোমার রাববই কিয়ামাত দিবসে ওর ফাইসালা করে দিবেন। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3529,32,26,'এটাও কি তাদেরকে পথ প্রদর্শন করলনা যে, আমি তাদের পূর্বে ধ্বংস করেছি কত মানব গোষ্ঠি, যাদের বাসভূমিতে এরা বিচরণ করছে? এতে অবশ্যই নিদর্শন রয়েছে; তবুও কি তারা শুনবেনা? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3530,32,27,'তারা কি লক্ষ্য করেনা যে, আমি উষর ভূমির পানি প্রবাহিত করে ওর সাহায্যে উদগত করি শস্য, যা হতে আহার্য গ্রহণ করে তাদের গৃহপালিত চতুস্পদ জন্তুগুলি এবং তারা নিজেরাও? তাদের কি দৃষ্টিশক্তি নেই? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3531,32,28,'তারা জিজ্ঞেস করেঃ তোমরা যদি সত্যবাদী হও তাহলে বল, কখন হবে এই ফাইসালা? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3532,32,29,'বলঃ ফাইসালার দিন কাফিরদের ঈমান আনা তাদের কোন কাজে আসবেনা এবং তাদেরকে অবকাশ দেয়া হবেনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3533,32,30,'অতএব তুমি তাদেরকে উপেক্ষা কর এবং অপেক্ষা কর; তারাও অপেক্ষা করছে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3534,33,1,'হে নাবী! আল্লাহকে ভয় কর এবং কাফিরদের ও মুনাফিকদের আনুগত্য করনা। আল্লাহ সর্বজ্ঞ, প্রজ্ঞাময়। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3535,33,2,'তোমার রবের নিকট হতে তোমার প্রতি যা অহী হয় উহার অনুসরণ কর; তোমরা যা কর আল্লাহ সেই বিষয়ে সম্যক অবহিত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3536,33,3,'আর তুমি নির্ভর কর আল্লাহর উপর এবং কর্মবিধায়ক হিসাবে আল্লাহই যথেষ্ট। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3537,33,4,'আল্লাহ কোন মানুষের অভ্যন্তরে দু’টি হৃদয় সৃষ্টি করেননি; তোমাদের স্ত্রীরা, যাদের সাথে তোমরা যিহার করে থাক, তাদেরকে আল্লাহ তোমাদের জননী করেননি এবং তোমাদের পোষ্যপুত্রকে তিনি (আল্লাহ) তোমাদের প্রকৃত পুত্র করেননি; ঐগুলি তোমাদের মুখের কথা। আল্লাহ সত্য কথাই বলেন এবং তিনিই সরল পথ নির্দেশ করেন। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3538,33,5,'তোমরা তাদেরকে ডাক তাদের পিতৃ পরিচয়ে; আল্লাহর দৃষ্টিতে এটা অধিক ন্যায় সঙ্গত, যদি তোমরা তাদের পিতৃ-পরিচয় না জান তাহলে তারা তোমাদের ধর্মীয় ভাই অথবা বন্ধু; এ ব্যাপারে তোমরা কোন ভুল করলে তোমাদের কোন অপরাধ নেই, কিন্তু তোমাদের অন্তরে সংকল্প থাকলে অপরাধ হবে। আর আল্লাহ ক্ষমাশীল, পরম দয়ালু। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3539,33,6,'নাবী মু’মিনদের নিকট তাদের নিজেদের অপেক্ষা ঘনিষ্টতর এবং তার স্ত্রীরা তাদের মা। আল্লাহর বিধান অনুসারে মু’মিন মুহাজির অপেক্ষা যারা আত্মীয় তারা পরস্পরের নিকটতম। তবে তোমরা যদি তোমাদের বন্ধু-বান্ধবদের প্রতি দাক্ষিণ্য প্রদর্শন করতে চাও তাহলে করতে পার। এটা কিতাবে লিপিবদ্ধ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3540,33,7,'স্মরণ কর, যখন আমি নাবীদের নিকট হতে অঙ্গীকার গ্রহণ করেছিলাম এবং তোমার নিকট হতেও এবং নূহ, ইবরাহীম, মূসা, মারইয়াম তনয় ঈসার নিকট হতে, তাদের নিকট হতে গ্রহণ করেছিলাম দৃঢ় অঙ্গীকার – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3541,33,8,'সত্যবাদীদেরকে তাদের সত্যবাদিতা সম্বন্ধে জিজ্ঞেস করার জন্য। তিনি কাফিরদের জন্য প্রস্তুত রেখেছেন মর্মন্তদ শাস্তি! ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3542,33,9,'হে মু’মিনগণ! তোমরা তোমাদের প্রতি আল্লাহর অনুগ্রহের কথা স্মরণ কর, যখন শত্রুবাহিনী তোমাদের বিরুদ্ধে সমাগত হয়েছিল এবং আমি তাদের বিরুদ্ধে প্রেরণ করেছিলাম ঝঞ্ঝাবায়ু এবং এক বাহিনী যা তোমরা দেখনি। তোমরা যা কর আল্লাহ তার সম্যক দ্রষ্টা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3543,33,10,'যখন তারা তোমাদের বিরুদ্ধে সমাগত হয়েছিল উচ্চ অঞ্চল ও নিম্ন অঞ্চল হতে, তোমাদের চক্ষু বিস্ফোরিত হয়েছিল; তোমাদের প্রাণ হয়ে পড়েছিল কন্ঠাগত এবং তোমরা আল্লাহ সম্বন্ধে নানাবিধ ধারণা পোষণ করছিলে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3544,33,11,'তখন মু’মিনরা পরীক্ষিত হয়েছিল এবং তারা ভীষণভাবে প্রকম্পিত হয়েছিল। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3545,33,12,'এবং মুনাফিকরাও যাদের অন্তরে ছিল ব্যাধি, তারা বলছিলঃ আল্লাহ এবং তাঁর রাসূল আমাদেরকে যে প্রতিশ্রুতি দিয়েছিলেন তা প্রতারণা ছাড়া কিছুই নয়। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3546,33,13,'আর তাদের এক দল বলেছিলঃ হে ইয়াসরিববাসী! তোমাদের কোন স্থান নেই, তোমরা ফিরে চল। এবং তাদের মধ্যে একদল নাবীর নিকট অব্যাহতি প্রার্থনা করে বলেছিলঃ আমাদের বাড়ী ঘর অরক্ষিত, অথচ ওগুলি অরক্ষিত ছিলনা, আসলে পলায়ন করাই ছিল তাদের উদ্দেশ্য।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3547,33,14,'যদি শত্রুরা নগরীর বিভিন্ন দিক হতে প্রবেশ করে তাদেরকে বিদ্রোহের জন্য প্ররোচিত করত তাহলে অবশ্যই তারা তাই করে বসত; তারা এতে কালবিলম্ব করতনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3548,33,15,'তারা পূর্বেই আল্লাহর সাথে অঙ্গীকার করেছিল যে, তারা পৃষ্ঠ প্রদর্শন করবেনা। আল্লাহর সাথে কৃত অঙ্গীকার সম্বন্ধে অবশ্যই জিজ্ঞাসাবাদ করা হবে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3549,33,16,'বলঃ তোমাদের কোন লাভ হবেনা যদি তোমরা মৃত্যু অথবা হত্যার ভয়ে পলায়ন কর এবং সেই ক্ষেত্রে তোমাদেরকে সামান্যই ভোগ করতে দেয়া হবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3550,33,17,'বলঃ কে তোমাদেরকে আল্লাহ হতে রক্ষা করবে যদি তিনি তোমাদের অমঙ্গল ইচ্ছা করেন এবং তিনি যদি তোমাদেরকে অনুগ্রহ করতে ইচ্ছা করেন তাহলে কে তোমাদের ক্ষতি করবে? তারা আল্লাহ ছাড়া নিজেদের কোন অভিভাবক ও সাহায্যকারী পাবেনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3551,33,18,'আল্লাহ অবশ্যই জানেন তোমাদের মধ্যে কারা তোমাদেরকে যুদ্ধে অংশ গ্রহণে বাধা দেয় এবং তাদের ভ্রাতৃবর্গকে বলেঃ আমাদের সঙ্গে এসো। তারা কমই যুদ্ধে অংশ নেয়। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3552,33,19,'তারা তোমাদের ব্যাপারে ঈর্ষাবোধ করে। যখন বিপদ আসে তখন তুমি দেখবে যে, মৃত্যুভয়ে মূর্ছাতুর ব্যক্তির ন্যায় চক্ষু উল্টিয়ে তারা তোমার দিকে তাকিয়ে আছে। কিন্তু যখন বিপদ চলে যায় তখন তারা সম্পদের লোভে তোমাদের সাথে বাক চাতুরীতায় অবতীর্ণ হয়। তারা ঈমান আনেনি, এ জন্য আল্লাহ তাদের কার্যাবলী নিষ্ফল করে দিয়েছেন এবং আল্লাহর পক্ষে এটা সহজ। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3553,33,20,'তারা মনে করে যে, সম্মিলিত বাহিনী চলে যায়নি। যদি সম্মিলিত বাহিনী আবার এসে পড়ে তখন তারা কামনা করবে যে, ভাল হত যদি তারা যাযাবর মরুবাসীদের সাথে থেকে তোমাদের সংবাদ নিত। তারা তোমাদের সাথে অবস্থান করলেও তারা যুদ্ধ অল্পই করত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3554,33,21,'তোমাদের মধ্যে যারা আল্লাহ ও আখিরাতের প্রতি বিশ্বাস রাখে এবং আল্লাহকে অধিক স্মরণ করে তাদের জন্য রাসূলের অনুসরণের মধ্যে রয়েছে উত্তম আদর্শ। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3555,33,22,'মু’মিনরা যখন সম্মিলিত বাহিনীকে দেখল তখন তারা বলে উঠলঃ এটাতো তা’ই, আল্লাহ ও তাঁর রাসূল যার প্রতিশ্রুতি আমাদেরকে দিয়েছেন এবং আল্লাহ ও তাঁর রাসূল সত্যই বলেছিলেন। আর এতে তাদের ঈমান ও আনুগত্যই বৃদ্ধি পেল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3556,33,23,'মু’মিনদের মধ্যে কতক আল্লাহর সাথে তাদের কৃত অঙ্গীকার পূর্ণ করেছে; তাদের কেহ কেহ শাহাদাত বরণ করেছে এবং কেহ কেহ প্রতীক্ষায় রয়েছে। তারা তাদের অঙ্গীকারে কোন পরিবর্তন করেনি। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3557,33,24,'কারণ আল্লাহ সত্যবাদীদেরকে পুরস্কৃত করেন সত্যবাদীতার জন্য এবং তাঁর ইচ্ছা হলে মুনাফিকদেরকে শাস্তি দেন অথবা তাদেরকে ক্ষমা করেন। আল্লাহ ক্ষমাশীল, পরম দয়ালু। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3558,33,25,'আল্লাহ কাফিরদেরকে ক্রুদ্ধাবস্থায় বিফল মনোরথ হয়ে ফিরে যেতে বাধ্য করলেন। কোন কল্যাণ তারা লাভ করেনি। যুদ্ধে মু’মিনদের জন্য আল্লাহই যথেষ্ট। আল্লাহ সর্বশক্তিমান, পরাক্রমশালী। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3559,33,26,'কিতাবীদের মধ্যে যারা তাদেরকে সাহায্য করেছিল তাদেরকে তিনি তাদের দুর্গ হতে অবতরণে বাধ্য করলেন এবং তাদের অন্তরে ভীতি সঞ্চার করলেন; ফলে তোমরা তাদের কতককে হত্যা করেছ এবং কতককে করছ বন্দী। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3560,33,27,'তিনি তোমাদেরকে অধিকারী করলেন তাদের ভূমি, ঘরবাড়ী ও ধন সম্পদের এবং এমন ভূমি যা তোমরা এখনও পদানত করনি। আল্লাহ সর্ববিষয়ে সর্বশক্তিমান।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3561,33,28,'হে নাবী! তুমি তোমার স্ত্রীদেরকে বলঃ তোমরা যদি পার্থিব জীবন এবং ওর ভুষণ কামনা কর তাহলে এসো, আমি তোমাদের ভোগ-সামগ্রীর ব্যবস্থা করে দিই এবং সৌজন্যের সাথে তোমাদেরকে বিদায় দিই। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3562,33,29,'আর যদি তোমরা আল্লাহ, তাঁর রাসূল ও আখিরাত কামনা কর তাহলে তোমাদের মধ্যে যারা সৎ কর্মশীল আল্লাহ তাদের জন্য মহা প্রতিদান প্রস্তুত রেখেছেন। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3563,33,30,'হে নাবী-পত্নীরা! যে কাজ স্পষ্টত অশ্লীল, তোমাদের মধ্যে কেহ তা করলে, তাকে দ্বিগুণ শাস্তি দেয়া হবে এবং এটা আল্লাহর জন্য সহজ। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3564,33,31,'তোমাদের যে কেহ আল্লাহ এবং তাঁর রাসূলের প্রতি অনুগত হবে ও সৎ কার্য করবে তাকে আমি পুরস্কার দিব দু’বার এবং তার জন্য রেখেছি সম্মানজনক রিয্ক। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3565,33,32,'হে নাবীর পত্নীরা! তোমরা অন্য নারীদের মত নও, যদি তোমরা আল্লাহকে ভয় কর তাহলে পর-পুরুষের সাথে কোমল কন্ঠে এমনভাবে কথা বলনা যাতে অন্তরে যার ব্যাধি রয়েছে সে প্রলুব্ধ হয় এবং তোমরা ন্যায় সঙ্গত কথা বলবে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3566,33,33,'এবং তোমরা স্বগৃহে অবস্থান করবে; প্রাচীন জাহেলী যুগের মত নিজেদেরকে প্রদর্শন করে বেড়াবেনা। তোমরা সালাত কায়েম করবে ও যাকাত প্রদান করবে এবং আল্লাহ ও তাঁর রাসূলের অনুগত থাকবে; হে নাবীর পরিবার! আল্লাহ শুধু চান তোমাদের হতে অপবিত্রতা দূর করতে এবং তোমাদেরকে সম্পূর্ণ রূপে পবিত্র করতে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3567,33,34,'আল্লাহর আয়াত ও জ্ঞানের কথা, যা তোমাদের গৃহে পঠিত হয়, তা তোমরা স্মরণ রাখবে; আল্লাহ অতি সূক্ষ্মদর্শী, সর্ব বিষয়ে অবহিত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3568,33,35,'অবশ্যই আত্ম-সমর্পনকারী (মুসলিম) পুরুষ ও আত্ম-সমর্পনকারী (মুসলিম) নারী, মু’মিন পুরুষ ও মু’মিনা নারী, অনুগত পুরুষ ও অনুগতা নারী, সত্যবাদী পুরুষ ও সত্যবাদিনী নারী, ধৈর্যশীল পুরুষ ও ধৈর্যশীলা নারী, বিনীত পুরুষ ও বিনীতা নারী, দানশীল পুরুষ ও দানশীলা নারী, সিয়াম পালনকারী পুরুষ ও সিয়াম পালনকারী নারী, যৌনাঙ্গ হিফাযাতকারী পুরুষ ও যৌনাঙ্গ হিফাযাতকারী নারী, আল্লাহকে অধিক স্মরণকারী পুরুষ ও অধিক স্মরণকারী নারী - এদের জন্য আল্লাহ প্রস্তুত রেখেছেন ক্ষমা ও মহা প্রতিদান।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3569,33,36,'আল্লাহ ও তাঁর রাসূল কোন বিষয়ে নির্দেশ দিলে কোন মু’মিন পুরুষ কিংবা মু’মিনা নারীর সে বিষয়ে কোন সিদ্ধান্তের অধিকার থাকবেনা। কেহ আল্লাহ এবং তাঁর রাসূলকে অমান্য করলে সেতো স্পষ্টই পথভ্রষ্ট।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3570,33,37,'স্মরণ কর, আল্লাহ যাকে অনুগ্রহ করেছেন এবং তুমিও যার প্রতি অনুগ্রহ করছ, তুমি তাকে বলেছিলেঃ তুমি তোমার স্ত্রীর সাথে সম্পর্ক বজায় রাখ এবং আল্লাহকে ভয় কর। তুমি তোমার অন্তরে যা গোপন রেখেছ আল্লাহ তা প্রকাশ করে দিচ্ছেন; তুমি লোকদেরকে ভয় করছিলে, অথচ আল্লাহকে ভয় করাই তোমার পক্ষে অধিকতর সঙ্গত। অতঃপর যায়িদ যখন তার (যাইনাবের) সাথে বিয়ের সর্ম্পক ছিন্ন করল তখন আমি তাকে তোমার সাথে পরিণয় সূত্রে আবদ্ধ করলাম, যাতে মু’মিনদের পোষ্য পুত্ররা নিজ স্ত্রীর সাথে বিবাহ সূত্র ছিন্ন করলে সেই সব রমনীকে বিয়ে করায় মু’মিনদের জন্য কোন বিঘ্ন না হয়। আল্লাহর আদেশ কার্যকরী হয়েই থাকে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3571,33,38,'আল্লাহ নাবীর জন্য যা বিধি সম্মত করেছেন তা করতে তার জন্য কোন বাধা নেই। পূর্বে যে সব নাবী অতীত হয়েছে, তাদের ক্ষেত্রেও এটাই ছিল আল্লাহর বিধান। আল্লাহর বিধান সুনির্ধারিত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3572,33,39,'তারা আল্লাহর বাণী প্রচার করত এবং তাঁকে ভয় করত, আর আল্লাহকে ছাড়া অন্য কেহকেও ভয় করতনা। হিসাব গ্রহণে আল্লাহই যথেষ্ট। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3573,33,40,'মুহাম্মাদ (সাঃ) তোমাদের মধ্যেকার কোন পুরুষের পিতা নয়, কিন্তু (সে) আল্লাহর রসূল এবং শেষ নবী। আল্লাহ সর্ববিষয়ে সর্বজ্ঞাতা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3574,33,41,'হে মু’মিনগণ! তোমরা আল্লাহকে অধিক স্মরণ কর। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3575,33,42,'এবং সকাল সন্ধ্যায় আল্লাহর পবিত্রতা ও মহিমা ঘোষণা কর। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3576,33,43,'তিনি তোমাদের প্রতি অনুগ্রহ করেন এবং তাঁর মালাইকা/ফেরেশতারাও তোমাদের জন্য অনুগ্রহের প্রার্থনা করে অন্ধকার হতে তোমাদেরকে আলোয় নিয়ে আসার জন্য, এবং তিনি মু’মিনদের প্রতি পরম দয়ালু। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3577,33,44,'যেদিন তারা আল্লাহর সাথে সাক্ষাৎ করবে সেদিন তাদের প্রতি অভিবাদন হবে ‘সালাম’। তিনি তাদের জন্য প্রস্তুত রেখেছেন উত্তম প্রতিদান।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3578,33,45,'হে নাবী! আমিতো তোমাকে পাঠিয়েছি সাক্ষী হিসাবে এবং সুসংবাদদাতা ও সতর্ককারী রূপে – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3579,33,46,'আল্লাহর অনুমতিক্রমে তাঁর দিকে আহবানকারী রূপে এবং উজ্জ্বল প্রদীপ রূপে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3580,33,47,'তুমি মু’মিনদেরকে সুসংবাদ দাও যে, তাদের জন্য আল্লাহর নিকট রয়েছে মহা অনুগ্রহ। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3581,33,48,'আর তুমি কাফির ও মুনাফিকদের কথা শোননা; তাদের নির্যাতন উপেক্ষা কর এবং নির্ভর কর আল্লাহর উপর; কর্মবিধায়ক রূপে আল্লাহই যথেষ্ট। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3582,33,49,'হে মু’মিনগণ! তোমরা মু’মিনা নারীদেরকে বিয়ে করার পর তাদেরকে স্পর্শ করার পূর্বে তালাক দিলে তোমাদের জন্য তাদের পালনীয় কোন ইদ্দাত নেই যা তোমরা গননা করবে। তোমরা তাদেরকে কিছু সামগ্রী দিবে এবং সৌজন্যের সাথে তাদেরকে বিদায় করবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3583,33,50,'হে নাবী! আমি তোমার জন্য বৈধ করেছি তোমার স্ত্রীদেরকে, যাদের মোহর তুমি প্রদান করেছ এবং বৈধ করেছি ‘ফায়’ হিসাবে আল্লাহ তোমাকে যা দান করেছেন তন্মধ্য হতে যারা তোমার মালিকানাধীন হয়েছে তাদেরকে এবং বিয়ের জন্য বৈধ করেছি তোমার চাচার কন্যা ও ফুফুর কন্যাকে, মামার কন্যা ও খালার কন্যাকে, যারা তোমার সাথে দেশ ত্যাগ করেছে এবং কোন মু’মিনা নারী নাবীর নিকট নিজেকে নিবেদন করলে, এবং নাবী তাকে বিয়ে করতে চাইলে সেও বৈধ। এটা বিশেষ করে তোমারই জন্য, অন্য মু’মিনদের জন্য নয়; যাতে তোমার কোন অসুবিধা না হয়। মু’মিনদের স্ত্রী এবং তাদের মালিকানাধীন দাসীদের সম্বন্ধে যা আমি নির্ধারিত করেছি তা আমি জানি। আল্লাহ ক্ষমাশীল, পরম দয়ালু। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3584,33,51,'তুমি তাদের মধ্যে যাকে ইচ্ছা তোমার নিকট হতে দূরে রাখতে পার এবং যাকে ইচ্ছা তোমার নিকট স্থান দিতে পার এবং তুমি যাকে দূরে রেখেছ তাকে কামনা করলে তোমার কোন অপরাধ নেই। এই বিধান এ জন্য যে, এতে তোমাদের তুষ্টি সহজতর হবে এবং তারা দুঃখ পাবেনা, আর তাদেরকে তুমি যা দিবে তাতে তাদের প্রত্যেকেই প্রীত থাকবে। তোমাদের অন্তরে যা আছে আল্লাহ তা জানেন। আল্লাহ সর্বজ্ঞ, সহনশীল। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3585,33,52,'এরপর, তোমার জন্য কোন নারী বৈধ নয় এবং তোমার স্ত্রীদের পরির্বতে অন্য স্ত্রী গ্রহণও বৈধ নয় যদিও তাদের সৌন্দর্য তোমাকে মুগ্ধ করে, তবে তোমার অধিকারভুক্ত দাসীদের ব্যাপারে এই বিধান প্রযোজ্য নয়। আল্লাহ সব কিছুর উপর তীক্ষ্ণ দৃষ্টি রাখেন। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3586,33,53,'হে মু’মিনগণ! তোমাদেরকে অনুমতি দেয়া না হলে তোমরা খাদ্য প্রস্তুত হওয়ার আগেই আহারের জন্য নাবী-গৃহে প্রবেশ করনা। তবে তোমাদেরকে আহবান করলে তোমরা প্রবেশ কর এবং আহার শেষে তোমরা চলে যেও; তোমরা কথাবার্তায় মশগুল হয়ে পড়না। কারণ তোমাদের এই আচরণ নাবীকে পীড়া দেয়, সে তোমাদেরকে উঠিয়ে দিতে সংকোচ বোধ করে। কিন্তু আল্লাহ সত্য বলতে সংকোচ বোধ করেননা। তোমরা তার স্ত্রীদের নিকট হতে কিছু চাইলে পর্দার অন্তরাল হতে চাইবে। এই বিধান তোমাদের ও তাদের হৃদয়ের জন্য অধিকতর পবিত্র। তোমাদের কারও পক্ষে আল্লাহর রাসূলকে কষ্ট দেয়া অথবা তার মৃত্যুর পর তার স্ত্রীদেরকে বিয়ে করা সংগত নয়। আল্লাহর দৃষ্টিতে এটা ঘোরতর অপরাধ। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3587,33,54,'তোমরা কোন বিষয় প্রকাশই কর অথবা গোপনই রাখ আল্লাহ সর্ব বিষয়ে সর্বজ্ঞ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3588,33,55,'নাবী-পত্নীদের জন্য তাদের পিতৃগণ, পুত্রগণ, ভ্রাতৃগণ, ভ্রাতুস্পুত্রগণ, ভগ্নীপুত্রগণ, সেবিকাগণ এবং তাদের অধিকারভুক্ত দাস-দাসীদের সম্মুখে উপস্থিত হওয়ায় কোন অপরাধ নেই। হে নাবীর পত্নিগণ! আল্লাহকে ভয় কর, আল্লাহ সবকিছু অবলোকন করেন। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3589,33,56,'আল্লাহ নাবীর প্রতি অনুগ্রহ করেন এবং তাঁর মালাইকারাও নাবীর জন্য অনুগ্রহ প্রার্থনা করে। হে মু’মিনগণ! তোমরাও নাবীর জন্য অনুগ্রহ প্রার্থনা কর এবং তাকে যথাযথভাবে সালাম জানাও। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3590,33,57,'যারা আল্লাহ ও রাসূলকে পীড়া দেয়, আল্লাহ তাদেরকে দুনিয়া ও আখিরাতে অভিশপ্ত করেন এবং তিনি তাদের জন্য রেখেছেন লাঞ্ছনাদায়ক শাস্তি। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3591,33,58,'মু’মিন পুরুষ ও মু’মিনা নারী কোন অপরাধ না করলেও যারা তাদেরকে পীড়া দেয় তারা অপবাদ ও স্পষ্ট পাপের বোঝা বহন করে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3592,33,59,'হে নাবী! তুমি তোমার স্ত্রীদেরকে, কন্যাদেরকে ও মু’মিনা নারীদেরকে বলঃ তারা যেন তাদের চাদরের কিয়দংশ নিজেদের উপর টেনে দেয়। এতে তাদের চেনা সহজতর হবে, ফলে তাদেরকে উত্যক্ত করা হবেনা। আল্লাহ ক্ষমাশীল, পরম দয়ালু। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3593,33,60,'মুনাফিকরা এবং যাদের অন্তরে ব্যাধি আছে এবং যারা নগরে গুজব রটনা করে, তারা বিরত না হলে আমি নিশ্চয়ই তাদের বিরুদ্ধে তোমাকে প্রবল করব; এরপর এই নগরীতে তোমার প্রতিবেশী রূপে তারা স্বল্প সময়ই থাকবে – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3594,33,61,'অভিশপ্ত হয়ে, তাদেরকে যেখানেই পাওয়া যাবে সেখানেই ধরা হবে এবং নির্দয়ভাবে হত্যা করা হবে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3595,33,62,'পূর্বে যারা অতীত হয়ে গেছে তাদের ব্যাপারে এটাই ছিল আল্লাহর বিধান। তুমি কখনও আল্লাহর বিধানে কোন পরিবর্তন পাবেনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3596,33,63,'লোকেরা তোমাকে কিয়ামাত সম্পর্কে জিজ্ঞেস করছে। বলঃ এর জ্ঞান শুধু আল্লাহরই আছে। তুমি এটা কি করে জানবে যে, সম্ভবতঃ কিয়ামাত শীঘ্রই হয়ে যেতে পারে? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3597,33,64,'আল্লাহ কাফিরদেরকে অভিশপ্ত করেছেন এবং তাদের জন্য প্রস্তুত রেখেছেন জ্বলন্ত আগুন। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3598,33,65,'সেখানে তারা স্থায়ী হবে এবং তারা কোন অভিভাবক ও সাহায্যকারী পাবেনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3599,33,66,'যেদিন তাদের মুখ-মন্ডল আগুনে উলট পালট করা হবে সেদিন তারা বলবেঃ হায়! আমরা যদি আল্লাহকে মানতাম ও রাসূলকে মানতাম! ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3600,33,67,'তারা আরও বলবেঃ হে আমাদের রাবব! আমরা আমাদের নেতা ও বড় লোকদের আনুগত্য করেছিলাম এবং তারা আমাদেরকে পথভ্রষ্ট করেছিল। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3601,33,68,'হে আমাদের রাব্ব! তাদেরকে দ্বিগুণ শাস্তি প্রদান করুন এবং তাদেরকে দিন মহা অভিসম্পাত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3602,33,69,'হে মু’মিনগণ! মূসাকে যারা ক্লেশ দিয়েছে তোমরা তাদের মত হয়োনা; তারা যা রটনা করেছিল আল্লাহ তা হতে তাকে নির্দোষ প্রমাণিত করেন; এবং আল্লাহর নিকট সে মর্যাদাবান। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3603,33,70,'হে মু’মিনগণ! আল্লাহকে ভয় কর এবং সঠিক কথা বল। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3604,33,71,'তাহলে তিনি তোমাদের কাজকে ক্রটিমুক্ত করবেন এবং তোমাদের পাপ ক্ষমা করবেন। যারা আল্লাহ ও তাঁর রাসূলের আনুগত্য করে তারা অবশ্যই মহাসাফল্য অর্জন করবে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3605,33,72,'আমিতো আসমান, যমীন ও পবর্তমালার প্রতি এই আমানত অর্পণ করেছিলাম, তারা এটা বহন করতে অস্বীকার করল এবং ওতে শংকিত হল, কিন্তু মানুষ ওটা বহন করল; সেতো অতিশয় যালিম, অতিশয় অজ্ঞ। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3606,33,73,'পরিণামে আল্লাহ মুনাফিক পুরুষ ও মুনাফিক নারী এবং মুশরিক পুরুষ ও মুশরিক নারীকে শাস্তি দিবেন এবং আল্লাহ মু’মিন পুরুষ ও মু’মিনা নারীকে ক্ষমা করবেন। আল্লাহ ক্ষমাশীল, পরম দয়ালু। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3607,34,1,'প্রশংসা আল্লাহর যিনি আকাশমন্ডলী ও পৃথিবীতে যা কিছু আছে সব কিছুরই মালিক এবং আখিরাতেও প্রশংসা তাঁরই। তিনি প্রজ্ঞাময়, সর্ব বিষয়ে অবহিত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3608,34,2,'তিনি জানেন যা ভূমিতে প্রবেশ করে, তা হতে যা নির্গত হয় এবং যা আকাশ হতে বর্ষিত হয় ও যা কিছু আকাশে উত্থিত হয়। তিনিই পরম দয়ালু, ক্ষমাশীল। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3609,34,3,'কাফিরেরা বলেঃ আমাদের উপর কিয়ামাত আসবেনা। বলঃ আসবেই, শপথ আমার রবের! নিশ্চয়ই তোমাদের নিকট ওটা আসবে। তিনি অদৃশ্য সম্বন্ধে সম্যক পরিজ্ঞাত; আকাশমন্ডলী ও পৃথিবীতে তাঁর অগোচর নয় অণু পরিমাণ কিছু কিংবা তদপেক্ষা ক্ষুদ্র অথবা বৃহৎ কিছু; বরং ওর প্রত্যেকটি লিপিবদ্ধ আছে সুস্পষ্ট কিতাবে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3610,34,4,'এটা এ জন্য যে, যারা মু’মিন ও সৎ কর্মপরায়ণ তিনি তাদেরকে পুরস্কৃত করবেন। তাদেরই জন্য আছে ক্ষমা ও সম্মানজনক রিয্ক। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3611,34,5,'যারা আমার আয়াতসমূহকে ব্যর্থ করার চেষ্টা করে তাদের জন্য রয়েছে ভয়ংকর মর্মন্তুদ শাস্তি। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3612,34,6,'যাদেরকে জ্ঞান দেয়া হয়েছে তারা জানে যে, তোমার রবের নিকট হতে তোমার প্রতি যা অবতীর্ণ হয়েছে তা সত্য। ইহা মানুষকে পরাক্রমশালী ও প্রশংসা আল্লাহর পথ নির্দেশ করে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3613,34,7,'কাফিরেরা বলেঃ আমরা কি তোমাদেরকে এমন ব্যক্তির সন্ধান দিব যে তোমাদেরকে বলে তোমাদের দেহ সম্পূর্ণ ছিন্নভিন্ন হয়ে পড়লেও তোমরা সৃষ্টি রূপে উত্থিত হবে? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3614,34,8,'সে কি আল্লাহ সম্বন্ধে মিথ্যা উদ্ভাবন করে অথবা সে কি উন্মাদ? বস্তুতঃ যারা আখিরাতে বিশ্বাস করেনা তারা শাস্তি ও ঘোর বিভ্রান্তিতে রয়েছে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3615,34,9,'তারা কি তাদের সম্মুখে ও পশ্চাতে, আসমান ও যমীনে যা আছে তার প্রতি লক্ষ্য করেনা? আমি ইচ্ছা করলে তাদেরকেসহ ভূমি ধ্বসিয়ে দিব অথবা তাদের উপর আকাশ মন্ডলের পতন ঘটাব; আল্লাহর অভিমুখী প্রতিটি বান্দার জন্য এতে অবশ্যই নিদর্শন রয়েছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3616,34,10,'আমি নিশ্চয়ই দাঊদের প্রতি অনুগ্রহ করেছিলাম এবং আদেশ করেছিলাম, হে পবর্তমালা! তোমরা দাঊদের সাথে আমার পবিত্রতা ঘোষণা কর এবং বিহংগকূলকেও। তার জন্য নমনীয় করেছিলাম লৌহ – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3617,34,11,'(এই আদেশ করে) ‘তুমি পূর্ণ মাপের বর্ম তৈরী কর, কড়াসমূহ যথাযথভাবে সংযুক্ত কর এবং তোমরা সৎ কাজ কর’, তোমরা যা কিছু কর আমি ওর সম্যক দ্রষ্টা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3618,34,12,'সুলাইমানের অধীন করেছিলাম বায়ুকে যা প্রভাতে এক মাসের পথ অতিক্রম করত এবং সন্ধ্যায় এক মাসের পথ অতিক্রম করত। আমি তার জন্য গলিত তাম্রের এক প্রস্রবন প্রবাহিত করেছিলাম। এবং ছিল জিন সম্প্রদায় যারা তাদের রবের অনুমতিক্রমে নিজেদের কতক তার সামনে কাজ করত। তাদের মধ্যে যে আমার নির্দেশ অমান্য করে তাকে আমি জ্বলন্ত অগ্নির শাস্তি আস্বাদন করাব। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3619,34,13,'তারা সুলাইমানের ইচ্ছানুযায়ী প্রাসাদ, ভাস্কর্য, হাউয সদৃশ বৃহদাকার পাত্র এবং সুদৃঢ় ভাবে চুল্লির উপর স্থাপিত বৃহদাকার ডেগ নির্মাণ করত। (আমি বলেছিলাম) হে দাউদ পরিবার! কৃতজ্ঞতার সাথে তোমরা কাজ করতে থাক। আমার বান্দাদের মধ্যে অল্পই কৃতজ্ঞ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3620,34,14,'যখন আমি সুলাইমানের মৃত্যু ঘটালাম তখন জিনদেরকে তার মৃত্যু বিষয় জানালো শুধু মাটির পোকা যা সুলাইমানের লাঠি খাচ্ছিল। যখন সুলাইমান পড়ে গেল তখন জিনেরা বুঝতে পারল যে, তারা যদি অদৃশ্য বিষয় অবগত থাকত তাহলে তারা লাঞ্ছনাদায়ক শাস্তিতে আবদ্ধ থাকতনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3621,34,15,'সাবাবাসীদের জন্য তাদের বাসভূমিতে ছিল এক নিদর্শন! দু’টি উদ্যান, একটি ডান দিকে, অপরটি বাম দিকে। তাদেরকে বলা হয়েছিলঃ তোমরা তোমাদের রাবব প্রদত্ত রিয্ক ভোগ কর এবং তাঁর প্রতি কৃতজ্ঞতা প্রকাশ কর। উত্তম এই স্থান এবং ক্ষমাশীল তোমাদের রাবব! ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3622,34,16,'অতঃপর তারা আদেশ অমান্য করল। ফলে আমি তাদের উপর প্রবাহিত করলাম বাঁধভাঙ্গা বন্যা এবং তাদের উদ্যান দু’টি পরিবর্তন করে দিলাম এমন দু’টি উদ্যানে যাতে উৎপন্ন হয় বিস্বাদ ফল-মূল, ঝাউ গাছ এবং কিছু কুল গাছ। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3623,34,17,'আমি তাদেরকে এই শাস্তি দিয়েছিলাম তাদের কুফরীর কারণে। আমি কৃতঘ্ন ব্যতীত আর কেহকেও এমন শাস্তি দিইনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3624,34,18,'তাদের এবং যে সব জনপদের প্রতি আমি অনুগ্রহ করেছিলাম সেইগুলির অন্তর্বর্তী স্থানে দৃশ্যমান বহু জনপদ স্থাপন করেছিলাম এবং ঐ সব জনপদে ভ্রমণের যথাযথ ব্যবস্থা করেছিলাম এবং তাদেরকে বলেছিলামঃ তোমরা এই সব জনপদে নিরাপদে ভ্রমণ কর দিনে ও রাতে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3625,34,19,'কিন্তু তারা বললঃ হে আমাদের রাবব! আমাদের সফরের মানযিলগুলির ব্যবধান বর্ধিত করুন! এভাবে তারা নিজেদের প্রতি যুলম করেছিল। ফলে আমি তাদেরকে কাহিনীর বিষয়বস্তুতে পরিণত করলাম এবং তাদেরকে ছিন্ন-ভিন্ন করে দিলাম। এতে প্রত্যেক ধৈর্যশীল, কৃতজ্ঞ ব্যক্তির জন্য নিদর্শন রয়েছে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3626,34,20,'তাদের সম্বন্ধে ইবলীস তার ধারণা সত্য প্রমাণ করল, ফলে তাদের মধ্যে একটি মু’মিন দল ব্যতীত সবাই তার অনুসরণ করল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3627,34,21,'তাদের উপর শাইতানের কোন আধিপত্য ছিলনা। কারা আখিরাতে বিশ্বাসী এবং কারা ওতে সন্দিহান তা প্রকাশ করে দেয়াই ছিল আমার উদ্দেশ্য। তোমার রাবব সর্ব বিষয়ের তত্ত্বাবধায়ক। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3628,34,22,'তুমি বলঃ তোমরা আহবান কর তাদেরকে যাদেরকে তোমরা আল্লাহর পরিবর্তে মা‘বূদ মনে করতে। তারা আকাশমন্ডলী ও পৃথিবীতে অণু পরিমান কোনো কিছুর মালিক নয় এবং এতদুভয়ে তাদের কোন অংশ নেই এবং না তাঁরা সাহায্যকারী। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3629,34,23,'যাকে অনুমতি দেয়া হয় সে ছাড়া আল্লাহর নিকট কারও সুপারিশ ফলপ্রসু হবেনা। অতঃপর যখন তাদের অন্তর হতে ভয় বিদুরিত হবে তখন তারা পরস্পরের মধ্যে জিজ্ঞাসাবাদ করবেঃ তোমাদের রাবব কি বললেন? তদুত্তরে তারা বলবেঃ যা সত্য তিনি তাই বলেছেন। তিনি সমুচ্চ, মহান। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3630,34,24,'বলঃ আকাশমন্ডলী ও পৃথিবী হতে কে তোমাকে রিয্ক প্রদান করে? বলঃ আল্লাহ! এবং নিশ্চয়ই আমরা অথবা তোমরা সৎ পথে স্থিত অথবা স্পষ্ট বিভ্রান্তিতে পতিত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3631,34,25,'বলঃ আমাদের অপরাধের জন্য তোমাদেরকে জবাবদিহি করতে হবেনা এবং তোমরা যা কর সে সম্পর্কে আমাদেরকেও জবাবদিহি করতে হবেনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3632,34,26,'বলঃ আমাদের রাবব আমাদেরকে একত্রিত করবেন, অতঃপর তিনি আমাদের মধ্যে ন্যায়ের সাথে ফাইসালা করে দিবেন, তিনিই শ্রেষ্ঠ ফাইসালাকারী, সর্বজ্ঞ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3633,34,27,'বলঃ তোমরা আমাকে দেখাও তাদেরকে যাদেরকে শরীক রূপে তাঁর সাথে জুড়ে দিয়েছ। না, কখনও নয়। বস্তুতঃ আল্লাহ পরাক্রমশালী, প্রজ্ঞাময়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3634,34,28,'আমিতো তোমাকে সমগ্র মানব জাতির প্রতি সুসংবাদ দাতা ও সতর্ককারী রূপে প্রেরণ করেছি। কিন্তু অধিকাংশ মানুষ জানেনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3635,34,29,'তারা জিজ্ঞেস করেঃ তোমরা যদি সত্যবাদী হও তাহলে বল, এই প্রতিশ্রুতি কখন বাস্তবায়িত হবে?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3636,34,30,'বলঃ তোমাদের জন্য আছে এক নির্ধারিত দিন যা মুহুর্তকাল বিলম্বিত করতে পারবেনা, ত্বরান্বিতও করতে পারবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3637,34,31,'কাফিরেরা বলেঃ আমরা এই কুরআনে কখনও বিশ্বাস স্থাপন করবনা, এর পূর্ববর্তী কিতাবসমূহেও না। হায়! তুমি যদি দেখতে! যালিমদেরকে যখন তাদের রবের সামনে দন্ডায়মান করা হবে তখন তারা পরস্পর বাদ প্রতিবাদ করতে থাকবে। যাদেরকে দুর্বল মনে করা হত তারা ক্ষমতাদর্পীদেরকে বলবেঃ তোমরা না থাকলে আমরা অবশ্যই মু’মিন হতাম। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3638,34,32,'যারা ক্ষমতাদর্পী ছিল তারা যাদেরকে দুর্বল মনে করা হত তাদেরকে বলবেঃ তোমাদের নিকট সৎ পথের দিশা আসার পর আমরা কি তোমাদেরকে ওটা হতে নিবৃত্ত করেছিলাম? বস্তুতঃ তোমরাইতো ছিলে অপরাধী। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3639,34,33,'যাদেরকে দুর্বল মনে করা হত তারা ক্ষমতাদর্পীদেরকে বলবেঃ প্রকৃত পক্ষে তোমরাইতো দিন-রাত চক্রান্তে লিপ্ত ছিলে, আমাদেরকে নির্দেশ দিয়েছিলে যেন আমরা আল্লাহকে অমান্য করি এবং তাঁর শরীক স্থাপন করি। যখন তারা শাস্তি প্রত্যক্ষ করবে তখন তারা অনুতাপ গোপন রাখবে এবং আমি কাফিরদের গলদেশে শৃংখল পরিয়ে দিব। তারা যা করত তারই প্রতিফল তাদেরকে দেয়া হবে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3640,34,34,'যখনই আমি কোন জনপদে সতর্ককারী প্রেরণ করেছি তখনই ওর বিত্তশালী অধিবাসীরা বলেছেঃ তোমরা যা সহ প্রেরিত হয়েছ আমরা তা প্রত্যাখ্যান করি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3641,34,35,'তারা আরও বলতঃ আমরা ধনে-জনে সমৃদ্ধশালী; সুতরাং আমাদেরকে কিছুতেই শাস্তি দেয়া হবেনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3642,34,36,'বলঃ আমার রাবব যার প্রতি ইচ্ছা রিয্ক বর্ধিত করেন, অথবা এটা সীমিত করেন; কিন্তু অধিকাংশ লোকই এটা জানেনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3643,34,37,'তোমাদের ধন সম্পদ ও সন্তান-সন্ততি এমন কিছু নয় যা তোমাদেরকে আমার নিকটবর্তী করে দিবে; যারা ঈমান আনে ও সৎ কাজ করে তারাই তাদের কর্মের জন্য পাবে বহুগুণ পুরস্কার; আর তারা প্রাসাদে নিরাপদে থাকবে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3644,34,38,'যারা আমার আয়াতকে ব্যর্থ করার চেষ্টা করবে তারা শাস্তি ভোগ করতে থাকবে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3645,34,39,'বলঃ আমার রাবব তাঁর বান্দাদের মধ্যে যার প্রতি ইচ্ছা তার রিয্ক বর্ধিত করেন অথবা ওটা সীমিত করেন। তোমরা যা কিছু ব্যয় করবে তিনি তার প্রতিদান দিবেন। তিনি শ্রেষ্ঠ রিয্কদাতা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3646,34,40,'যেদিন তাদের একত্র করবেন এবং মালাইকা/ফেরেশতাদেরকে জিজ্ঞেস করবেনঃ এরা কি তোমাদেরই পূজা করত? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3647,34,41,'মালাইকা বলবেঃ আপনি পবিত্র, মহান! আমাদের সাহায্যকারী আপনিই, তারা নয়, তারাতো পূজা করত জিনদের এবং তাদের অধিকাংশই ছিল তাদের প্রতি বিশ্বাসী। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3648,34,42,'আজ তোমাদের একে অন্যের উপকার কিংবা অপকার করার ক্ষমতা নেই। যারা যুল্‌ম করেছিল তাদেরকে বলবঃ তোমরা যে আগুনের শাস্তি অস্বীকার করতে তা আস্বাদন কর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3649,34,43,'তাদের নিকট যখন আমার সুস্পষ্ট আয়াতসমূহ আবৃত্তি করা হয় তখন তারা বলেঃ তোমাদের পূর্ব-পুরুষ যার ইবাদাত করত এই ব্যক্তিইতো তার ইবাদাতে তোমাদেরকে বাঁধা দিতে চায়। তারা আরও বলেঃ এটাতো মিথ্যা উদ্ভাবন ব্যতীত কিছু নয়। এবং কাফিরদের নিকট যখন সত্য আসে তখন তারা বলেঃ এটাতো এক সুস্পষ্ট যাদু! ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3650,34,44,'আমি তাদেরকে পূর্বে কোন কিতাব দিইনি যা তারা অধ্যয়ন করত এবং তোমার পূর্বে তাদের নিকট কোন সতর্ককারীও প্রেরণ করিনি। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3651,34,45,'তাদের পূর্ববর্তীরাও মিথ্যা আরোপ করেছিল। তাদেরকে আমি যা দিয়েছিলাম এরা তার এক দশমাংশও পায়নি, তবুও তারা আমার রাসূলদেরকে মিথ্যাবাদী বলেছিল। ফলে কত ভয়ংকর হয়েছিল আমার শাস্তি! ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3652,34,46,'বলঃ আমি তোমাদেরকে একটি বিষয়ে উপদেশ দিচ্ছি, তোমরা আল্লাহর উদ্দেশে দুই জন অথবা এক জন করে দাঁড়াও, অতঃপর তোমরা চিন্তা করে দেখ - তোমাদের সংগী আদৌ উন্মাদ নয়। সেতো আসন্ন কঠিন শাস্তি সম্পর্কে তোমাদের সতর্ককারী মাত্র।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3653,34,47,'বলঃ আমি তোমাদের নিকট কোন পারিশ্রমিক চেয়ে থাকলে তা তোমাদেরই, আমার পুরস্কারতো আছে আল্লাহর নিকট এবং তিনি সর্ব বিষয়ে দ্রষ্টা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3654,34,48,'বলঃ আমার রাবব সত্য নিক্ষেপ করেন; তিনি অদৃশ্যের পরিজ্ঞাতা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3655,34,49,'বলঃ সত্য এসেছে এবং অসত্য না পারে নতুন কিছু সৃষ্টি করতে এবং না পারে পুনরাবৃত্তি করতে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3656,34,50,'বলঃ আমি বিভ্রান্ত হলে বিভ্রান্তির পরিণাম আমারই এবং যদি আমি সৎ পথে থাকি তাহলে তা এ জন্য যে, আমার প্রতি আমার রাবব অহী প্রেরণ করেন। তিনি সর্বশ্রোতা, সন্নিকট। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3657,34,51,'তুমি যদি দেখতে যখন তারা ভীত বিহবল হয়ে পড়বে! তারা অব্যাহতি পাবেনা এবং তারা নিকটস্থ স্থান হতে ধৃত হবে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3658,34,52,'আর তারা বলবেঃ আমরা তাঁকে বিশ্বাস করলাম। কিন্তু এত দূরবর্তী স্থান হতে তারা নাগাল পাবে কিরূপে?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3659,34,53,'তারাতো পূর্বে তাকে প্রত্যাখ্যান করেছিল; তারা দূরবর্তী স্থান হতে অদৃশ্য বিষয়ে বাক্য ছুড়ে মারত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3660,34,54,'তাদের ও তাদের প্রবৃত্তির মাঝে অন্তরাল করা হয়েছে, যেমন পূর্বে করা হয়েছিল তাদের সমপন্থীদের ক্ষেত্রে। তারা ছিল বিভ্রান্তিকর সন্দেহের মধ্যে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3661,35,1,'প্রশংসা আকাশমন্ডলী ও পৃথিবীর সৃষ্টিকর্তা আল্লাহরই যিনি বাণীবাহক করেন মালাইকা/ফেরেশতাদেরকে যারা দুই দুই, তিন তিন অথবা চার চার পাখা বিশিষ্ট। তিনি তাঁর সৃষ্টিতে যা ইচ্ছা সৃষ্টি করেন। আল্লাহ সর্ব বিষয়ে সর্ব শক্তিমান। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3662,35,2,'আল্লাহ মানুষের প্রতি কোন অনুগ্রহ অবারিত করলে কেহ ওটা নিবারন করতে পারেনা এবং তিনি কিছু নিরুদ্ধ করতে চাইলে অতঃপর কেহ ওর উম্মুক্তকারী নেই। তিনি পরাক্রমশালী, প্রজ্ঞাময়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3663,35,3,'হে মানুষ! তোমাদের প্রতি আল্লাহর অনুগ্রহ স্মরণ কর। আল্লাহ ছাড়া কি কোন স্রষ্টা আছে যে তোমাদেরকে আকাশমন্ডলী ও পৃথিবী হতে রিয্ক দান করে? তিনি ছাড়া কোন মা‘বূদ নেই। সুতরাং কোথায় তোমরা বিপথে চালিত হচ্ছ? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3664,35,4,'তারা যদি তোমার প্রতি মিথ্যা আরোপ করে তাহলে তোমার পূর্বেও রাসূলদের প্রতি মিথ্যা আরোপ করা হয়েছিল। আল্লাহর নিকটই সব কিছু প্রমাণিত হবে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3665,35,5,'হে মানুষ! আল্লাহর প্রতিশ্রুতি সত্য; সুতরাং পার্থিব জীবন যেন তোমাদেরকে কিছুতেই প্রতারিত না করে এবং সেই প্রবঞ্চক যেন কিছুতেই আল্লাহ সম্পর্কে প্রবঞ্চিত না করে তোমাদেরকে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3666,35,6,'শাইতান তোমাদের শত্রু; সুতরাং তাকে শত্রু হিসাবে গ্রহণ কর। সেতো তার দলবলকে আহবান করে শুধু এ জন্য যে, তারা যেন উত্তপ্ত জাহান্নামের সাথী হয়। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3667,35,7,'যারা কুফরী করে তাদের জন্য আছে কঠিন শাস্তি, কিন্তু যারা ঈমান আনে ও সৎ কাজ করে তাদের জন্য আছে ক্ষমা ও মহা পুরস্কার।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3668,35,8,'কেহকেও যদি তার মন্দ কাজ শোভন করে দেখানো হয় এবং সে ওটাকে উত্তম মনে করে সেই ব্যক্তি কি তার সমান যে সৎ কাজ করে? আল্লাহ যাকে ইচ্ছা বিভ্রান্ত করেন এবং যাকে ইচ্ছা সৎ পথে পরিচালিত করেন। অতএব তুমি তাদের জন্য আক্ষেপ করে তোমার প্রাণকে ধ্বংস করনা। তারা যা করে আল্লাহ তা জানেন। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3669,35,9,'আল্লাহই বায়ু প্রেরণ করে তা দ্বারা মেঘমালা সঞ্চালিত করেন। অতঃপর আমি তা নির্জীব ভূ-খন্ডের দিকে পরিচালিত করি, অতঃপর আমি ওটা দ্বারা ধরিত্রীকে ওর মৃত্যুর পর সঞ্জীবিত করি। পুনরুত্থান এ রূপেই হবে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3670,35,10,'কেহ ক্ষমতা চাইলে সে জেনে রাখুক যে, সমস্ত ক্ষমতাতো আল্লাহরই। তাঁরই দিকে পবিত্র বাণীসমূহ আরোহণ করে এবং সৎ কাজ ওকে উন্নীত করে, আর যারা মন্দ কর্মের ফন্দি আটে তাদের জন্য আছে কঠিন শাস্তি। তাদের ফন্দি ব্যর্থ হবেই। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3671,35,11,'আল্লাহ তোমাদেরকে সৃষ্টি করেছেন মাটি হতে; অতঃপর শুক্র বিন্দু হতে, অতঃপর তোমাদেরকে করেছেন যুগল! আল্লাহর অজ্ঞাতসারে কোন নারী গর্ভ ধারণ করেনা এবং প্রসবও করেনা; কোন দীর্ঘায়ু ব্যক্তির আয়ু বৃদ্ধি করা হয়না অথবা তার আয়ু হ্রাস করা হয়না। কিন্তু তাতো রয়েছে কিতাবে। এটা আল্লাহর জন্য সহজ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3672,35,12,'দু’টি দরিয়া একরূপ নয়; একটির পানি সুমিষ্ট ও সুপেয়, অপরটির পানি লোনা, খর। প্রত্যেকটি হতে তোমরা তাজা গোশত আহার কর এবং অলংকার যা তোমরা পরিধান কর, এবং রত্নাবলী আহরণ কর এবং তোমরা দেখ যে, ওর বুক চিরে নৌযান চলাচল করে যাতে তোমরা তাঁর অনুগ্রহ অনুসন্ধান করতে পার এবং যাতে তোমরা কৃতজ্ঞ হও।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3673,35,13,'তিনি রাতকে দিনে প্রবেশ করান এবং দিনকে প্রবেশ করান রাতে। তিনি সূর্য ও চাঁদকে করেছেন নিয়ন্ত্রিত; প্রত্যেকে পরিভ্রমণ করে এক নির্দিষ্ট কাল পর্যন্ত। তিনিই আল্লাহ! তোমাদের রাবব! সার্বভৌমত্ব তাঁরই। আর তোমরা আল্লাহর পরিবর্তে যাদেরকে ডাক তারাতো খেজুর বীচির আবরণেরও অধিকারী নয়। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3674,35,14,'তোমরা তাদেরকে আহবান করলে তারা তোমাদের আহবান শুনবেনা এবং শুনলেও তোমাদের আহবানে সাড়া দিবেনা। তোমরা তাদেরকে যে শরীক করছ তা তারা কিয়ামাত দিবসে অস্বীকার করবে। তাঁর মত কেহই তোমাকে অবহিত করতে পারেনা, তিনি সর্বজ্ঞ। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3675,35,15,'হে লোক সকল! তোমরাতো আল্লাহর মুখাপেক্ষী, কিন্তু আল্লাহ অভাবমুক্ত, প্রশংসা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3676,35,16,'তিনি ইচ্ছা করলে তোমাদেরকে অপসারণ করতে পারেন এবং এক নূতন সৃষ্টি আনয়ন করতে পারেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3677,35,17,'এটা আল্লাহর পক্ষে কঠিন নয়। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3678,35,18,'কোন বহনকারী অন্যের বোঝা বহন করবেনা, কোন ভারাক্রান্ত ব্যক্তি যদি কেহকেও এটা বহন করতে আহবান করে তাহলে তার কিছুই বহন করা হবেনা, নিকট আত্মীয় হলেও। তুমি শুধু সতর্ক করতে পার তাদেরকে যারা তাদের রাববকে না দেখে ভয় করে এবং সালাত কায়েম করে। যে কেহ নিজেকে পরিশোধন করে সেতো পরিশোধন করে নিজেরই কল্যাণের জন্য। প্রত্যাবর্তনতো আল্লাহরই নিকট।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3679,35,19,'সমান নয় অন্ধ ও চক্ষুম্মান, ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3680,35,20,'অন্ধকার ও আলো, ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3681,35,21,'ছায়া ও রোদ, ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3682,35,22,'আর সমান নয় জীবিত ও মৃত। আল্লাহ যাকে ইচ্ছা শ্রবণ করান। তুমি শোনাতে সমর্থ হবেনা যারা কাবরে রয়েছে তাদেরকে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3683,35,23,'তুমি একজন সতর্ককারী মাত্র। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3684,35,24,'আমি তোমাকে সত্যসহ প্রেরণ করেছি সুসংবাদ দাতা ও সতর্ককারী রূপে; এমন কোন সম্প্রদায় নেই যার নিকট সতর্ককারী প্রেরিত হয়নি। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3685,35,25,'তারা যদি তোমার প্রতি মিথ্যা আরোপ করে তাহলে তাদের পূর্ববর্তীরাও মিথ্যা আরোপ করেছিল; তাদের নিকট এসেছিল তাদের রাসূলগণ সুস্পষ্ট নিদর্শন, গ্রন্থাদি ও দীপ্তিমান কিতাবসহ। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3686,35,26,'অতঃপর আমি কাফিরদেরকে শাস্তি দিয়েছিলাম। কি ভয়ংকর আমার শাস্তি! ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3687,35,27,'তুমি কি দেখনা যে, আল্লাহ আকাশ হতে বৃষ্টি বর্ষন করেন এবং উহা দ্বারা আমি বিচিত্র বর্ণের ফলমূল উদ্গত করি? পাহাড়ের মধ্যে আছে বিচিত্র বর্ণের ফল - শুভ্র, লাল ও নিকষ কালো।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3688,35,28,'এভাবে রং বেরংয়ের মানুষ, জানোয়ার ও চতুস্পদ জন্তু রয়েছে। আল্লাহর বান্দাদের মধ্যে যারা জ্ঞানী তারাই তাঁকে ভয় করে; আল্লাহ পরাক্রমশালী, ক্ষমাশীল। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3689,35,29,'যারা আল্লাহর কিতাব পাঠ করে, সালাত কায়েম করে, আমি তাদেরকে যে রিয্ক দিয়েছি তা গোপনে ও প্রকাশ্যে ব্যয় করে, তারাই আশা করতে পারে তাদের এমন ব্যবসায়ের যার ক্ষয় নেই –');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3690,35,30,'এ জন্য যে, আল্লাহ তাদের কর্মের পূর্ণ প্রতিফল দিবেন এবং নিজ অনুগ্রহে তাদেরকে আরো বেশি দিবেন। তিনিতো ক্ষমাশীল, গুণগ্রাহী। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3691,35,31,'আমি তোমার প্রতি যে কিতাব অবতীর্ণ করেছি তা সত্য। এটা পূর্ববর্তী কিতাবের সমর্থক। আল্লাহ তাঁর বান্দাদের সব কিছু জানেন ও দেখেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3692,35,32,'অতঃপর আমি কিতাবের অধিকারী করলাম আমার বান্দাদের মধ্যে তাদেরকে যাদেরকে আমি মনোনীত করেছি; তবে তাদের কেহ নিজের প্রতি অত্যাচারী, কেহ মধ্য পন্থী এবং কেহ আল্লাহর ইচ্ছায় কল্যাণের কাজে অগ্রগামী। এটাই মহা অনুগ্রহ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3693,35,33,'তারা প্রবেশ করবে স্থায়ী জান্নাতে, সেখানে তাদেরকে স্বর্ণ নির্মিত কংকন ও মুক্তা দ্বারা অলংকৃত করা হবে এবং সেখানে তাদের পোশাক পরিচ্ছদ হবে রেশমের।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3694,35,34,'এবং তারা বলবেঃ প্রশংসা আল্লাহর যিনি আমাদের দুঃখ দুর্দশা দূরীভূত করেছেন! আমাদের রাববতো ক্ষমাশীল, গুণগ্রাহী। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3695,35,35,'যিনি নিজ অনুগ্রহে আমাদেরকে স্থায়ী আবাস দিয়েছেন, যেখানে ক্লেশ আমাদেরকে স্পর্শ করে না। এবং ক্লান্তিও স্পর্শ করে না। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3696,35,36,'কিন্তু যারা কুফরী করে তাদের জন্য আছে জাহান্নামের আগুন। তাদের মৃত্যুর আদেশ দেয়া হবেনা যে, তারা মরবে এবং তাদের জন্য জাহান্নামের শাস্তিও লাঘব করা হবেনা। এভাবে আমি প্রত্যেক অকৃতজ্ঞকে শাস্তি দিয়ে থাকি। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3697,35,37,'সেখানে তারা আর্তনাদ করে বলবেঃ হে আমাদের রাবব! আমাদেরকে নিস্কৃতি দিন, আমরা সৎ কাজ করব, পূর্বে যা করতাম তা করবনা। আল্লাহ বলবেনঃ আমি কি তোমাদেরকে এত দীর্ঘ জীবন দান করিনি যে, তখন কেহ সতর্ক হতে চাইলে সতর্ক হতে পারতে? তোমাদের নিকটতো সতর্ককারীরাও এসেছিল। সুতরাং শাস্তি আস্বাদন কর; যালিমদের কোন সাহায্যকারী নেই।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3698,35,38,'আল্লাহ আকাশমন্ডলী ও পৃথিবীর অদৃশ্য বিষয় অবগত আছেন। অন্তরে যা রয়েছে সে সম্বন্ধে তিনি সবিশেষ অবহিত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3699,35,39,'তিনিই তোমাদেরকে পৃথিবীতে প্রতিনিধি করেছেন। সুতরাং কেহ কুফরী করলে তার কুফরীর জন্য সে নিজেই দায়ী হবে। কাফিরদের কুফরী শুধু তাদের রবের ক্রোধই বৃদ্ধি করে এবং কাফিরদের কুফরী তাদের ক্ষতিই বৃদ্ধি করে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3700,35,40,'বলঃ তোমরা আল্লাহর পরিবর্তে যাদেরকে ডাক সেসব দেবদেবীর কথা ভেবে দেখেছ কি? তারা পৃথিবীতে কিছু সৃষ্টি করে থাকলে আমাকে দেখাও; অথবা আকাশমন্ডলী সৃষ্টিতে তাদের কোন অংশ আছে কি? না কি আমি তাদেরকে এমন কোন কিতাব দিয়েছি যার প্রমাণের উপর এরা নির্ভর করে? বস্তুতঃ যালিমরা একে অপরকে মিথ্যা প্রতিশ্রুতি দিয়ে থাকে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3701,35,41,'আল্লাহ আকাশমন্ডলী ও পৃথিবীকে সংরক্ষণ করেন যাতে ওরা স্থানচ্যূত না হয়, ওরা স্থানচ্যূত হলে তিনি ব্যতীত কে ওদেরকে সংরক্ষণ করবে? তিনি অতি সহনশীল, ক্ষমাপরায়ণ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3702,35,42,'তারা দৃঢ়তার সাথে আল্লাহর শপথ করে বলত যে, তাদের নিকট কোন সতর্ককারী এলে তারা অন্য সব সম্প্রদায় অপেক্ষা সৎ পথের অধিকতর অনুসারী হবে। কিন্তু তাদের নিকট যখন সতর্ককারী এলো তখন তারা শুধু তাদের বিমুখতাই বৃদ্ধি করল – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3703,35,43,'পৃথিবীতে ঔদ্ধত্য প্রকাশ এবং কূট ষড়যন্ত্রের কারণে। কূট ষড়যন্ত্র ওর উদ্যোক্তাদেরকেই পরিবেষ্টন করে। তাহলে কি তারা প্রতীক্ষা করছে পূর্ববর্তীদের প্রতি প্রযুক্ত বিধানের? কিন্তু তুমি আল্লাহর বিধানের কখনও কোন পরিবর্তন পাবেনা এবং আল্লাহর বিধানের কোন ব্যতিক্রমও দেখবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3704,35,44,'তারা কি পৃথিবীতে ভ্রমণ করেনি? তাহলে তাদের পূর্ববর্তীদের পরিণাম কি হয়েছিল তা দেখতে পেত। তারাতো এদের অপেক্ষা অধিকতর শক্তিশালী ছিল। আল্লাহ এমন নন যে, আকাশমন্ডলী এবং পৃথিবীর কোন কিছুই তাঁকে অক্ষম করতে পারে; তিনি সর্বজ্ঞ, সর্বশক্তিমান।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3705,35,45,'আল্লাহ মানুষকে তাদের কৃতকর্মের জন্য শাস্তি দিলে ভূপৃষ্ঠের কোন জীব জন্তুকেই রেহাই দিতেনন।, কিন্তু তিনি এক নির্দিষ্টকাল পর্যন্ত তাদেরকে অবকাশ দিয়ে থাকেন। অতঃপর তাদের নির্দিষ্ট কাল এসে গেলে আল্লাহ হবেন তাঁর বান্দাদের সম্যক দ্রষ্টা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3706,36,1,'ইয়া সীন। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3707,36,2,'শপথ জ্ঞানগর্ভ কুরআনের। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3708,36,3,'তুমি অবশ্যই রাসূলদের অন্তর্ভুক্ত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3709,36,4,'তুমি সরল পথে প্রতিষ্ঠিত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3710,36,5,'কুরআন অবতীর্ণ পরাক্রমশালী, পরম দয়ালু আল্লাহর নিকট হতে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3711,36,6,'যাতে তুমি সতর্ক করতে পার এমন এক জাতিকে যাদের পিতৃ-পুরুষদেরকে সতর্ক করা হয়নি, যার ফলে তারা গাফিল। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3712,36,7,'তাদের অধিকাংশের জন্য সেই বাণী অবধারিত হয়েছে; সুতরাং তারা ঈমান আনবেনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3713,36,8,'আমি তাদের গলদেশে চিবুক পর্যন্ত বেড়ি পরিয়েছি, ফলে তারা উর্ধ্বমুখী হয়ে গেছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3714,36,9,'আমি তাদের সম্মুখে প্রাচীর ও পশ্চাতে প্রাচীর স্থাপন করেছি এবং তাদেরকে আবৃত করেছি, ফলে তারা দেখতে পায়না। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3715,36,10,'তুমি তাদেরকে সতর্ক কর কিংবা না কর তাদের জন্য উভয়ই সমান; তারা ঈমান আনবেনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3716,36,11,'তুমি শুধু তাদেরকেই সতর্ক করতে পার যারা উপদেশ মেনে চলে এবং না দেখে দয়াময় রাহমানকে ভয় করে। অতএব তুমি তাদেরকে ক্ষমা ও মহা পুরস্কারের সংবাদ দাও। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3717,36,12,'আমিই মৃতকে করি জীবিত এবং লিখে রাখি যা তারা অগ্রে প্রেরণ করে এবং যা তারা পশ্চাতে রেখে যায়, আমি প্রত্যেক বিষয়কে স্পষ্ট কিতাবে সংরক্ষিত রেখেছি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3718,36,13,'তাদের নিকট উপস্থিত কর এক জনপদের অধিবাসীদের দৃষ্টান্ত; তাদের নিকট এসেছিল রাসূলগণ। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3719,36,14,'যখন আমি তাদের নিকট পাঠিয়েছিলাম দু’জন রাসূল, কিন্তু তারা তাদেরকে মিথ্যাবাদী বলল; তখন আমি তাদেরকে শক্তিশালী করেছিলাম তৃতীয় একজন দ্বারা এবং তারা বলেছিলঃ আমরাতো তোমাদের নিকট প্রেরিত হয়েছি। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3720,36,15,'তারা বললঃ তোমরাতো আমাদের মত মানুষ, দয়াময় কিছুই অবতীর্ণ করেননি, তোমরা শুধু মিথ্যাই বলছ। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3721,36,16,'তারা বললঃ আমাদের রাব্ব জানেন যে, আমরা অবশ্যই তোমাদের নিকট প্রেরিত হয়েছি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3722,36,17,'স্পষ্টভাবে প্রচার করাই আমাদের দায়িত্ব। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3723,36,18,'তারা বললঃ আমরা তোমাদেরকে অমঙ্গলের কারণ মনে করি। যদি তোমরা বিরত না হও তাহলে তোমাদেরকে অবশ্যই প্রস্তরাঘাতে হত্যা করব এবং আমাদের পক্ষ হতে তোমাদের উপর যন্ত্রণাদায়ক শাস্তি অবশ্যই আপতিত হবে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3724,36,19,'তারা বললঃ তোমাদের অমঙ্গল তোমাদেরই সাথে, এটা কি এ জন্য যে, আমরা তোমাদেরকে উপদেশ দিচ্ছি? বস্তুতঃ তোমরা এক সীমালংঘনকারী সম্প্রদায়। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3725,36,20,'নগরীর প্রান্ত হতে এক ব্যক্তি ছুটে এলো, সে বললঃ হে আমার সম্প্রদায়! রাসূলদের অনুসরণ কর। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3726,36,21,'অনুসরণ কর তাদের যারা তোমাদের নিকট কোন প্রতিদান চায়না এবং তারা সৎ পথপ্রাপ্ত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3727,36,22,'আমার কি যুক্তি আছে যে, যিনি আমাকে সৃষ্টি করেছেন এবং যাঁর নিকট তোমরা প্রত্যাবর্তিত হবে আমি তাঁর ইবাদাত করবনা? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3728,36,23,'আমি কি তাঁর পরিবর্তে অন্য মা‘বূদ গ্রহণ করব? দয়াময় (আল্লাহ) আমাকে ক্ষতিগ্রস্ত করতে চাইলে তাদের সুপারিশ আমার কোন কাজে আসবেনা এবং তারা আমাকে উদ্ধারও করতে পারবেনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3729,36,24,'এরূপ করলে আমি অবশ্যই স্পষ্ট বিভ্রান্তিতে পতিত হব। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3730,36,25,'আমিতো তোমাদের রবের উপর ঈমান এনেছি, অতএব তোমরা আমার কথা শোন। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3731,36,26,'তাকে বলা হলঃ জান্নাতে প্রবেশ কর। সে বলে উঠলঃ হায়! আমার সম্প্রদায় যদি জানতে পারত – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3732,36,27,'কি কারণে আমার রাব্ব আমাকে ক্ষমা করেছেন এবং সম্মানিত করেছেন। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3733,36,28,'আমি তার মৃত্যুর পর তার সম্প্রদায়ের বিরুদ্ধে আকাশ হতে কোন বাহিনী প্রেরণ করিনি এবং প্রেরণের প্রয়োজনও ছিলনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3734,36,29,'ওটা ছিল শুধুমাত্র এক মহানাদ। ফলে তারা নিথর নিস্তব্ধ হয়ে গেল। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3735,36,30,'পরিতাপ বান্দাদের জন্য! তাদের নিকট যখনই কোন রাসূল এসেছে তখনই তারা তাকে ঠাট্টা বিদ্রুপ করেছে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3736,36,31,'তারা কি লক্ষ্য করেনা যে, তাদের পূর্বে কত মানবগোষ্ঠীকে আমি ধ্বংস করেছি যারা তাদের মধ্যে ফিরে আসবেনা? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3737,36,32,'এবং অবশ্যই তাদের সকলকে একত্রে আমার নিকট উপস্থিত করা হবে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3738,36,33,'তাদের জন্য একটি নিদর্শন মৃত ধরিত্রী, যাকে আমি সঞ্জীবিত করি এবং যা হতে উৎপন্ন করি শস্য, যা তারা আহার করে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3739,36,34,'তাতে আমি সৃষ্টি করি খেজুর ও আঙ্গুরের উদ্যান এবং উৎসারিত করি প্রস্রবণ – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3740,36,35,'যাতে তারা আহার করতে পারে এর ফলমূল হতে, অথচ তাদের হাত ওটা সৃষ্টি করেনি। তবুও কি তারা কৃতজ্ঞতা প্রকাশ করবেনা? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3741,36,36,'পবিত্র মহান তিনি, যিনি উদ্ভিদ, মানুষ এবং তারা যাদেরকে জানেনা তাদের প্রত্যেককে সৃষ্টি করেছেন জোড়ায় জোড়ায়। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3742,36,37,'তাদের এক নিদর্শন রাত, ওটা হতে আমি দিবালোক অপসারিত করি, তখন সকলেই অন্ধকারাচ্ছন্ন হয়ে পড়ে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3743,36,38,'এবং সূর্য ভ্রমণ করে ওর নির্দিষ্ট গন্তব্যের দিকে। এটা পরাক্রমশালী, সর্বজ্ঞের নিয়ন্ত্রণ। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3744,36,39,'এবং চন্দ্রের জন্য আমি নির্দিষ্ট করেছি বিভিন্ন মানযিল, অবশেষে ওটা শুষ্ক বক্র পুরাতন খেজুর শাখার আকার ধারণ করে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3745,36,40,'সূর্যের পক্ষে সম্ভব নয় চাঁদের নাগাল পাওয়া এরং রাতের পক্ষে সম্ভব নয় দিনকে অতিক্রম করা; এবং প্রত্যেকে নিজ নিজ কক্ষপথে সাতার কাটে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3746,36,41,'তাদের এক নিদর্শন এই যে, আমি তাদের বংশধরদের বোঝাই নৌযানে আরোহণ করিয়েছিলাম। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3747,36,42,'এবং তাদের জন্য অনুরূপ যানবাহন সৃষ্টি করেছি যাতে তারা আরোহণ করে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3748,36,43,'আমি ইচ্ছা করলে তাদেরকে নিমজ্জিত করতে পারি; সেই অবস্থায় তারা কোন সাহায্যকারী পাবেনা এবং তারা পরিত্রাণও পাবেনা – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3749,36,44,'আমার অনুগ্রহ না হলে এবং কিছুকালের জন্য জীবনোপকরণ ভোগ করতে না দিলে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3750,36,45,'যখন তাদেরকে বলা হয়ঃ যা তোমাদের সম্মুখে ও পশ্চাতে রয়েছে সে সম্বন্ধে সাবধান হও যাতে তোমরা অনুগ্রহভাজন হতে পার। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3751,36,46,'আর যখনই তাদের রবের নিদর্শনাবলীর কোন নিদর্শন তাদের নিকট আসে তখন তারা তা হতে মুখ ফিরিয়ে নেয়। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3752,36,47,'যখন তাদেরকে বলা হয়, আল্লাহ তোমাদেরকে যে জীবনোপকরণ দিয়েছেন তা হতে ব্যয় কর তখন কাফিরেরা মু’মিনদেরকে বলেঃ যাকে ইচ্ছা করলে আল্লাহ খাওয়াতে পারতেন আমরা কেন তাকে খাওয়াব? তোমরাতো স্পষ্ট বিভ্রান্তিতে রয়েছ। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3753,36,48,'তারা বলেঃ তোমরা যদি সত্যবাদী হও তাহলে বলঃ এই প্রতিশ্রুতি কখন পূর্ণ হবে?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3754,36,49,'এরাতো অপেক্ষায় আছে এক মহানাদের যা এদেরকে আঘাত করবে এদের বাক বিতন্ডা কালে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3755,36,50,'তখন তারা অসীয়াত করতে সমর্থ হবেনা এবং নিজেদের পরিবার পরিজনের নিকট ফিরেও আসতে পারবেনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3756,36,51,'যখন শিঙ্গায় ফুৎকার দেয়া হবে তখনই তারা কাবর হতে ছুটে আসবে তাদের রবের দিকে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3757,36,52,'তারা বলবেঃ হায়! দুর্ভোগ আমাদের! কে আমাদেরকে আমাদের নিদ্রাস্থল হতে উঠালো? দয়াময় (আল্লাহ) তো এরই প্রতিশ্রুতি দিয়েছিলেন এবং রাসূলগণ সত্যই বলেছিলেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3758,36,53,'এটা হবে শুধুমাত্র এক মহানাদ; তখনই তাদের সকলকে উপস্থিত করা হবে আমার সম্মুখে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3759,36,54,'আজ কারও প্রতি কোন যুলম করা হবেনা এবং তোমরা যা করতে শুধু তারই প্রতিফল দেয়া হবে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3760,36,55,'এ দিন জান্নাতবাসীরা আনন্দে মগ্ন থাকবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3761,36,56,'তারা এবং তাদের সঙ্গিনীরা সুশীতল ছায়ায় সুসজ্জিত আসনে হেলান দিয়ে বসবে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3762,36,57,'সেখানে থাকবে তাদের জন্য ফল-মূল এবং থাকবে যা তারা ফরমায়েশ করবে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3763,36,58,'পরম দয়ালু রবের পক্ষ হতে তাদেরকে বলা হবে ‘সালাম’। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3764,36,59,'আর হে অপরাধীরা! তোমরা আজ পৃথক হয়ে যাও। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3765,36,60,'হে বানী আদম! আমি কি তোমাদেরকে নির্দেশ দেইনি যে, তোমরা শাইতানের দাসত্ব করনা, কারণ সে তোমাদের প্রকাশ্য শত্রু? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3766,36,61,'আর আমার ইবাদাত কর, এটাই সরল পথ? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3767,36,62,'শাইতানতো তোমাদের বহু দলকে বিভ্রান্ত করেছিল, তবুও কি তোমরা বুঝনি? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3768,36,63,'এটা সেই জাহান্নাম যার প্রতিশ্রুতি তোমাদেরকে দেয়া হয়েছিল। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3769,36,64,'আজ তোমরা এতে প্রবেশ কর; কারণ তোমরা একে অবিশ্বাস করেছিলে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3770,36,65,'আমি আজ এদের মুখে মোহর লাগিয়ে দিব। এদের হাত কথা বলবে আমার সাথে এবং এদের পা সাক্ষ্য দিবে এদের কৃতকর্মের। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3771,36,66,'আমি ইচ্ছা করলে এদের চক্ষুগুলিকে লোপ করে দিতে পারতাম, তখন এরা পথ চলতে চাইলে কি করে দেখতে পেত? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3772,36,67,'এবং আমি ইচ্ছা করলে এদেরকে স্ব স্ব স্থানে বিকৃত করে দিতে পারতাম, ফলে এরা চলতে পারতো না এবং ফিরেও আসতে পারতো না। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3773,36,68,'আমি যাকে দীর্ঘ জীবন দান করি তার স্বাভাবিক গঠনে অবনতি ঘটাই। তবুও কি তারা বুঝেনা? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3774,36,69,'আমি তাকে কাব্য রচনা করতে শিখাইনি এবং এটা তার পক্ষে শোভনীয় নয়। ইহাতো শুধু উপদেশ এবং সুস্পষ্ট কুরআন। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3775,36,70,'যাতে সে সতর্ক করতে পারে জীবিতদেরকে এবং যাতে কাফিরদের বিরুদ্ধে শাস্তির কথা সত্য হতে পারে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3776,36,71,'তারা কি লক্ষ্য করেনা যে, নিজ হতে সৃষ্ট বস্তুর মধ্যে তাদের জন্য আমি সৃষ্টি করেছি গৃহ পালিত পশু এবং তারাই ওগুলির অধিকারী। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3777,36,72,'এবং আমি ওগুলিকে তাদের বশীভূত করেছি। ওগুলির কতক তাদের বাহন এবং কতক তারা আহার করে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3778,36,73,'তাদের জন্য ওগুলিতে রয়েছে বহু উপকারিতা, আর আছে পানীয় বস্তু। তবুও কি তারা কৃতজ্ঞ হবেনা? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3779,36,74,'তারাতো আল্লাহর পরিবর্তে অন্য মা‘বূদ গ্রহণ করেছে এ আশায় যে, তারা সাহায্য প্রাপ্ত হবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3780,36,75,'কিন্তু এসব ইলাহ তাদের সাহায্য করতে সক্ষম নয়, তাদেরকে তাদের বাহিনীরূপে উপস্থিত করা হবে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3781,36,76,'অতএব তাদের কথা তোমাদের যেন দুঃখ না দেয়। আমিতো জানি যা তারা গোপন করে এবং যা তারা ব্যক্ত করে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3782,36,77,'মানুষ কি দেখেনা যে, আমি তাকে সৃষ্টি করেছি শুক্র বিন্দু হতে? অথচ পরে সে হয়ে পড়ে প্রকাশ্য বিতন্ডাকারী। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3783,36,78,'আর সে আমার সম্বন্ধে উপমা রচনা করে, অথচ সে নিজের সৃষ্টির কথা ভুলে যায়। বলেঃ অস্থিতে কে প্রাণ সঞ্চার করবে যখন ওটা পচে গলে যাবে? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3784,36,79,'বলঃ ওর মধ্যে প্রাণ সঞ্চার করবেন তিনিই যিনি ওটা প্রথমবার সৃষ্টি করেছেন এবং তিনি প্রত্যেকটি সৃষ্টি সম্বন্ধে সম্যক অবগত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3785,36,80,'তিনি তোমাদের জন্য সবুজ বৃক্ষ হতে আগুন উৎপাদন করেন এবং তোমরা ওটা দ্বারা প্রজ্জ্বলিত কর। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3786,36,81,'যিনি আকাশমন্ডলী ও পৃথিবী সৃষ্টি করেছেন তিনি কি তাদের অনুরূপ সৃষ্টি করতে সক্ষম নন? হ্যাঁ, নিশ্চয়ই তিনি মহাস্রষ্টা, সর্বজ্ঞ। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3787,36,82,'তাঁর ব্যাপারতো শুধু এই যে, যখন তিনি কোন কিছুর ইচ্ছা করেন তখন বলেন ‘হও’, ফলে তা হয়ে যায়। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3788,36,83,'অতএব পবিত্র ও মহান তিনি যাঁর হাতে রয়েছে প্রত্যেক বিষয়ের সার্বভৌম ক্ষমতা এবং তাঁর নিকট তোমরা প্রত্যাবর্তিত হবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3789,37,1,'শপথ তাদের যারা সারিবদ্ধভাবে দন্ডায়মান। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3790,37,2,'এবং যারা কঠোর পরিচালক। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3791,37,3,'এবং যারা জিকর আবৃত্তিতে মশগুল। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3792,37,4,'নিশ্চয়ই তোমাদের মা‘বূদ এক। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3793,37,5,'যিনি আকাশমন্ডলী ও পৃথিবী এবং এতদুভয়ের অন্তবর্তী সব কিছুর রাব্ব, এবং রাব্ব সকল উদয়স্থলের। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3794,37,6,'আমি নিকটবর্তী আকাশকে নক্ষত্ররাজির সুষমা দ্বারা সুশোভিত করেছি। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3795,37,7,'এবং রক্ষা করেছি প্রত্যেক বিদ্রোহী শাইতান হতে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3796,37,8,'ফলে তারা উর্ধ্ব জগতের কিছু শ্রবণ করতে পারেনা এবং তাদের প্রতি উল্কা নিক্ষিপ্ত হয় সকল দিক হতে – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3797,37,9,'বিতাড়নের জন্য এবং তাদের জন্য রয়েছে অবিরাম শাস্তি। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3798,37,10,'তবে কেহ হঠাৎ কিছু শুনে ফেললে জ্বলন্ত উল্কাপিন্ড তার পশ্চাদ্ধাবন করে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3799,37,11,'তাদেরকে জিজ্ঞেস করঃ তাদেরকে সৃষ্টি করা কঠিনতর নাকি আমি অন্য যা কিছু সৃষ্টি করেছি তা সৃষ্টি কঠিনতর? তাদেরকে আমি সৃষ্টি করেছি আঠাল মাটি হতে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3800,37,12,'তুমিতো বিস্ময় বোধ করছ, আর তারা করছে বিদ্রুপ। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3801,37,13,'এবং যখন তাদেরকে উপদেশ দেয়া হয় তখন তারা তা গ্রহণ করেনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3802,37,14,'তারা কোন নিদর্শন দেখলে উপহাস করে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3803,37,15,'এবং বলেঃ এটাতো এক সুস্পষ্ট যাদু ব্যতীত আর কিছুই নয়। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3804,37,16,'আমরা যখন মরে যাব এবং মাটি ও অস্থিতে পরিণত হব তখন কি আমাদেরকে পুনরুত্থিত করা হবে? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3805,37,17,'এবং আমাদের পূর্ব-পুরুষদেরকেও?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3806,37,18,'বলঃ হ্যাঁ এবং তোমরা হবে লাঞ্ছিত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3807,37,19,'ওটা একটি মাত্র প্রচন্ড শব্দ। আর তখনই তারা প্রত্যক্ষ করবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3808,37,20,'এবং তারা বলবেঃ হায়! দুর্ভোগ আমাদের! এটাইতো কর্মফল দিন। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3809,37,21,'এটাই ফাইসালার দিন যা তোমরা অস্বীকার করতে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3810,37,22,'(মালাইকাকে বলা হবে) একত্রিত কর যালিম ও তাদের সহচরদেরকে এবং তাদেরকে যাদের ইবাদাত করত, ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3811,37,23,'আল্লাহর পরিবর্তে এবং তাদেরকে ধাবিত কর জাহান্নামের পথে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3812,37,24,'অতঃপর তাদেরকে থামাও, কারণ তাদেরকে প্রশ্ন করা হবে; ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3813,37,25,'তোমাদের কি হল যে, তোমরা একে অপরকে সাহায্য করছনা? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3814,37,26,'বস্তুতঃ সেদিন তারা আত্মসমর্পন করবে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3815,37,27,'এবং তারা একে অপরের সামনাসামনি হয়ে জিজ্ঞাসাবাদ করবে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3816,37,28,'তারা বলবেঃ তোমরাতো তোমাদের শক্তি নিয়ে আমাদের নিকট আসতে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3817,37,29,'তারা বলবেঃ তোমরা তো বিশ্বাসীই ছিলে না। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3818,37,30,'এবং তোমাদের উপর আমাদের কোন কর্তৃত্ব ছিলনা; বস্তুতঃ তোমরাই ছিলে সীমালংঘনকারী সম্প্রদায়। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3819,37,31,'আমাদের বিরুদ্ধে আমাদের রবের কথা সত্য হয়েছে; আমাদেরকে অবশ্যই শাস্তি আস্বাদন করতে হবে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3820,37,32,'আমরা তোমাদেরকে বিভ্রান্ত করেছিলাম, কারণ আমরা নিজেরাও ছিলাম বিভ্রান্ত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3821,37,33,'তারা সবাই সেদিন শাস্তিতে শরীক হবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3822,37,34,'অপরাধীদের প্রতি আমি এরূপই করে থাকি। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3823,37,35,'যখন তাদেরকে বলা হত যে, আল্লাহ ছাড়া কোন মা‘বূদ নেই তখন তারা অহংকার করত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3824,37,36,'এবং বলতঃ আমরা কি এক পাগল কবির কথায় আমাদের মা‘বূদদেরকে বর্জন করব? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3825,37,37,'বরং সেতো সত্য নিয়ে এসেছে এবং সে সমস্ত রাসূলকে সত্য বলে স্বীকার করেছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3826,37,38,'তোমরা অবশ্যই মর্মন্তদ শাস্তির স্বাদ গ্রহণ করবে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3827,37,39,'এবং তোমরা যা করতে তারই প্রতিফল পাবে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3828,37,40,'তবে তারা নয়, যারা আল্লাহর একনিষ্ঠ বান্দা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3829,37,41,'তাদের জন্য রয়েছে নির্ধারিত রিয্ক –');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3830,37,42,'ফল-মূল এবং তারা হবে সম্মানিত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3831,37,43,'সুখ কাননে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3832,37,44,'তারা মুখোমুখি আসনে আসীন হবে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3833,37,45,'তাদেরকে ঘুরে ঘুরে পরিবেশন করা হবে বিশুদ্ধ সূরাপূর্ণ পাত্র – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3834,37,46,'শুভ্র উজ্জ্বল, যা হবে পানকারীদের জন্য সুস্বাদু। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3835,37,47,'তাতে ক্ষতিকর কিছুই থাকবেনা এবং তারা তাতে মাতালও হবেনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3836,37,48,'আর তাদের সঙ্গে থাকবে আনত নয়না আয়তলোচনা হুরবৃন্দ। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3837,37,49,'তারা যেন সুরক্ষিত ডিম্ব। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3838,37,50,'তারা একে অপরের সাথে পরস্পর জিজ্ঞাসাবাদ করবে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3839,37,51,'তাদের কেহ বলবেঃ আমার ছিল এক সঙ্গী। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3840,37,52,'সে বলতঃ তুমি কি বিশ্বাস কর যে – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3841,37,53,'আমরা যখন মরে যাব এবং আমরা মৃত্তিকা ও অস্থিতে পরিণত হব তখনও আমাদেরকে প্রতিফল দেয়া হবে?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3842,37,54,'(আল্লাহ) বলবেনঃ তোমরা কি তাকে দেখতে চাও? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3843,37,55,'অতঃপর সে ঝুকে দেখবে এবং তাকে দেখতে পাবে জাহান্নামের মধ্যস্থলে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3844,37,56,'সে বলবেঃ আল্লাহর শপথ! তুমিতো আমাকে প্রায় ধ্বংসই করেছিলে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3845,37,57,'আমার রবের অনুগ্রহ না থাকলে আমিও আটক ব্যক্তিদের মধ্যে শামিল হতাম। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3846,37,58,'আমাদের আর মৃত্যু হবেনা – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3847,37,59,'প্রথম মৃত্যু ছাড়া এবং আমাদেরকে শাস্তিও দেয়া হবেনা! ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3848,37,60,'এটাতো মহা সাফল্য। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3849,37,61,'এরূপ সাফল্যের জন্য সাধকদের উচিৎ সাধনা করা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3850,37,62,'আপ্যায়নের জন্য কি এটাই শ্রেষ্ঠ, না কি যাক্কুম বৃক্ষ? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3851,37,63,'যালিমদের জন্য আমি ওটা সৃষ্টি করেছি পরীক্ষা স্বরূপ। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3852,37,64,'এই বৃক্ষ উদ্গত হয় জাহান্নামের তলদেশ হতে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3853,37,65,'ওটার মোচা যেন শাইতানের মাথা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3854,37,66,'ওটা হতে তারা আহার করবে এবং উদর পূর্ণ করবে ওটা দ্বারা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3855,37,67,'তদুপরি তাদের জন্য থাকবে ফুটন্ত পানির মিশ্রণ। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3856,37,68,'আর তাদের গন্তব্য হবে অবশ্যই প্রজ্জ্বলিত আগুনের দিকে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3857,37,69,'তারা তাদের পিতৃপুরুষদেরকে পেয়েছিল বিপথগামী। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3858,37,70,'আর তারা তাদের পদাংক অনুসরণে ধাবিত হয়েছিল। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3859,37,71,'তাদের আগেও পূর্ববর্তীদের অধিকাংশ বিপথগামী হয়েছিল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3860,37,72,'এবং আমি তাদের মধ্যে সতর্ককারী প্রেরণ করেছিলাম। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3861,37,73,'সুতরাং লক্ষ্য কর, যাদেরকে সতর্ক করা হয়েছিল তাদের পরিণাম কি হয়েছিল! ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3862,37,74,'তবে আল্লাহর একনিষ্ঠ বান্দাদের কথা স্বতন্ত্র। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3863,37,75,'নূহ আমাকে আহবান করেছিল, আর আমি কত উত্তম সাড়া দানকারী। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3864,37,76,'তাকে ও তার পরিবারবর্গকে আমি উদ্ধার করেছিলাম মহা সংকট হতে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3865,37,77,'তার বংশধরদেরকেই আমি বিদ্যমান রেখেছি বংশ পরম্পরায়। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3866,37,78,'আমি এটা পরবর্তীদের স্মরণে রেখেছি। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3867,37,79,'সমগ্র বিশ্বের মধ্যে নূহের প্রতি শান্তি বর্ষিত হোক। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3868,37,80,'এভাবেই আমি সৎ কর্মশীলদেরকে পুরস্কৃত করে থাকি। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3869,37,81,'সে ছিল আমার মু’মিন বান্দাদের অন্যতম। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3870,37,82,'অবশিষ্ট সকলকে আমি নিমজ্জিত করেছিলাম।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3871,37,83,'ইবরাহীম তার অনুগামীদের অন্তর্ভুক্ত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3872,37,84,'স্মরণ কর, সে তার রবের নিকট উপস্থিত হয়েছিল বিশুদ্ধ চিত্তে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3873,37,85,'যখন সে তার পিতা ও তার সম্প্রদায়কে জিজ্ঞেস করেছিলঃ তোমরা কিসের পূজা করছ?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3874,37,86,'তোমরা কি আল্লাহর পরিবর্তে অলীক মা‘বূদগুলিকে চাও? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3875,37,87,'জগতসমূহের রাব্ব সম্বন্ধে তোমাদের ধারণা কি? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3876,37,88,'অতঃপর সে একবার তারকারাজির দিকে একবার তাকালো। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3877,37,89,'এবং বললঃ আমি অসুস্থ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3878,37,90,'অতঃপর তারা তাকে পশ্চাতে রেখে চলে গেল। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3879,37,91,'পরে সে সন্তর্পণে তাদের দেবতাগুলির নিকট গেল এবং বললঃ তোমরা খাদ্য গ্রহণ করছ না কেন? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3880,37,92,'তোমাদের কি হয়েছে যে, তোমরা কথা বলনা? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3881,37,93,'অতঃপর সে তাদের উপর সবলে ডান হাত দিয়ে আঘাত হানলো। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3882,37,94,'তখন ঐ লোকগুলি তার দিকে ছুটে এল। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3883,37,95,'সে বললঃ তোমরা নিজেরা যাদেরকে খোদাই করে নির্মাণ কর, তোমরা কি তাদেরই পূজা কর?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3884,37,96,'প্রকৃত পক্ষে আল্লাহই সৃষ্টি করেছেন তোমাদেরকে এবং তোমরা যা তৈরী কর তা’ও। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3885,37,97,'তারা বললঃ এর জন্য এক ইমারাত তৈরী কর, অতঃপর একে জ্বলন্ত আগুনে নিক্ষেপ কর। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3886,37,98,'তারা তার বিরুদ্ধে চক্রান্তের সংকল্প করেছিল; কিন্তু আমি তাদেরকে অতিশয় হেয় করেছিলাম। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3887,37,99,'এবং সে বললঃ আমি আমার রবের দিকে চললাম, তিনি অবশ্যই আমাকে সৎ পথে পরিচালিত করবেন। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3888,37,100,'হে আমার রাব্ব! আমাকে এক সৎকর্মপরায়ণ সন্তান দান করুন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3889,37,101,'অতঃপর আমি তাকে এক স্থির বুদ্ধিসম্পন্ন পুত্রের সুসংবাদ দিলাম।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3890,37,102,'অতঃপর সে যখন তার পিতার সাথে কাজ করার মত বয়সে উপনীত হল তখন ইবরাহীম বললঃ বৎস! আমি স্বপ্নে দেখি যে, তোমাকে আমি যবাহ করছি, এখন তোমার অভিমত কি, বল। সে বললঃ হে আমার পিতা! আপনি যা আদিষ্ট হয়েছেন তাই করুন। আল্লাহ ইচ্ছা করলে আপনি আমাকে ধৈর্যশীল পাবেন। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3891,37,103,'যখন তারা উভয়ে আনুগত্য প্রকাশ করল এবং ইবরাহীম তার পুত্রকে কাত করে শায়িত করল –');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3892,37,104,'তখন আমি তাকে আহবান করে বললামঃ হে ইবরাহীম – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3893,37,105,'তুমিতো স্বপ্নাদেশ সত্যিই পালন করলে। এভাবেই আমি সৎকর্মশীলদেরকে পুরস্কৃত করে থাকি। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3894,37,106,'নিশ্চয়ই এটা ছিল এক স্পষ্ট পরীক্ষা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3895,37,107,'আমি তাকে মুক্ত করলাম এক মহান কুরবানীর বিনিময়ে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3896,37,108,'আমি এটা পরবর্তীদের স্মরণে রেখেছি। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3897,37,109,'ইবরাহীমের উপর শান্তি বর্ষিত হোক। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3898,37,110,'এভাবে আমি সৎ কর্মশীলদেরকে পুরস্কৃত করে থাকি। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3899,37,111,'সে ছিল আমার মু’মিন বান্দাদের অন্যতম। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3900,37,112,'আমি তাকে সুসংবাদ দিয়েছিলাম ইসহাকের, সে ছিল এক নাবী, সৎকর্মশীলদের অন্যতম। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3901,37,113,'আমি তাকে বারাকাত দান করেছিলাম এবং ইসহাককেও, তাদের বংশধরদের মধ্যে কতক সৎকর্মপরায়ণ এবং কতক নিজেদের প্রতি স্পষ্ট অত্যাচারী। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3902,37,114,'আমি অনুগ্রহ করেছিলাম মূসা ও হারূনের উপর। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3903,37,115,'এবং তাদেরকে ও তাদের সম্প্রদায়কে আমি উদ্ধার করেছিলাম মহা সংকট হতে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3904,37,116,'আমি সাহায্য করেছিলাম তাদেরকে, ফলে তারা হয়েছিল বিজয়ী। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3905,37,117,'আমি উভয়কে দিয়েছিলাম সুস্পষ্ট কিতাব।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3906,37,118,'এবং তাদেরকে আমি পরিচালিত করেছিলাম সরল পথে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3907,37,119,'আমি তাদের উভয়কে পরবর্তীতে স্মরণে রেখেছি। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3908,37,120,'মূসা ও হারূনের উপর শান্তি বর্ষিত হোক। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3909,37,121,'এভাবে আমি সৎকর্মশীলদেরকে পুরস্কৃত করে থাকি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3910,37,122,'তারা উভয়েই ছিল আমার মু’মিন বান্দাদের অন্তর্ভুক্ত ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3911,37,123,'ইলিয়াসও ছিল রাসূলদের একজন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3912,37,124,'স্মরণ কর, সে তার সম্প্রদায়কে বলেছিলঃ তোমরা কি সতর্ক হবেনা? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3913,37,125,'তোমরা কি বা’লকে (দেবমূর্তি) ডাকবে এবং পরিত্যাগ করবে শ্রেষ্ঠ স্রষ্টা – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3914,37,126,'আল্লাহকে, যিনি রাব্ব তোমাদের এবং রাব্ব তোমাদের পূর্ব-পুরুষদের? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3915,37,127,'কিন্তু তারা তাকে মিথ্যাবাদী বলেছিল, অতএব তাদেরকে অবশ্যই শাস্তির জন্য উপস্থিত করা হবে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3916,37,128,'তবে আল্লাহর একনিষ্ঠ বান্দাদের কথা স্বতন্ত্র। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3917,37,129,'আমি এটা পরবর্তীদের স্মরণে রেখেছি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3918,37,130,'ইলিয়াসের উপর শান্তি বর্ষিত হোক। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3919,37,131,'এভাবে আমি সৎকর্মশীলদেরকে পুরস্কৃত করে থাকি। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3920,37,132,'সে ছিল আমার মু’মিন বান্দাদের অন্যতম। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3921,37,133,'লূত ছিল রাসূলদের একজন। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3922,37,134,'আমি তাকে ও তার পরিবারের সবাইকে উদ্ধার করেছিলাম। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3923,37,135,'এক বৃদ্ধা ব্যতীত, যে ছিল পশ্চাতে অবস্থানকারীদের অন্তর্ভুক্ত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3924,37,136,'অতঃপর অবশিষ্টদেরকে আমি সম্পূর্ণরূপে ধ্বংস করেছিলাম।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3925,37,137,'তোমরা তাদের ধ্বংসাবশেষগুলি অতিক্রম করে থাক সকালে –');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3926,37,138,'এবং সন্ধ্যায়। তবুও কি তোমরা অনুধাবন করবেনা? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3927,37,139,'ইউনুসও ছিল রাসূলদের একজন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3928,37,140,'স্মরণ কর, যখন সে পালিয়ে বোঝাই নৌযানে পৌঁছল। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3929,37,141,'অতঃপর সে লটারীতে যোগদান করল এবং পরাভূত হল। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3930,37,142,'পরে এক বৃহদাকার মৎস্য তাকে গিলে ফেলল; তখন সে নিজেকে ধিক্কার দিতে লাগল। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3931,37,143,'সে যদি আল্লাহর মহিমা ঘোষণা না করত – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3932,37,144,'তাহলে তাকে পুনরুত্থান দিন পর্যন্ত থাকতে হত ওর উদরে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3933,37,145,'অতঃপর তাকে আমি নিক্ষেপ করলাম এক তৃণহীন প্রান্তরে এবং সে ছিল রুগ্ন। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3934,37,146,'পরে আমি তার উপর এক লাউ গাছ উদগত করলাম। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3935,37,147,'তাকে আমি লক্ষ বা ততোধিক লোকের প্রতি প্রেরণ করেছিলাম। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3936,37,148,'এবং তারা ঈমান এনেছিল; ফলে আমি তাদেরকে কিছু কালের জন্য জীবনোপভোগ করতে দিলাম।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3937,37,149,'এখন তাদেরকে জিজ্ঞেস করঃ তোমার রবের জন্য কি রয়েছে কন্যা সন্তান এবং তাদের জন্য পুত্র সন্তান? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3938,37,150,'অথবা আমি কি মালাইকাকে নারী রূপে সৃষ্টি করেছি, আর তারা তা প্রত্যক্ষ করেছিল? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3939,37,151,'দেখ তারা মনগড়া কথা বলে যে – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3940,37,152,'আল্লাহ সন্তান জন্ম দিয়েছেন। তারা নিশ্চয়ই মিথ্যাবাদী। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3941,37,153,'তিনি কি পুত্র সন্তানের পরিবর্তে কন্যা সন্তান পছন্দ করতেন? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3942,37,154,'তোমাদের কি হয়েছে, তোমরা কি রূপ বিচার কর? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3943,37,155,'তাহলে কি তোমরা উপদেশ গ্রহণ করবেনা? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3944,37,156,'তোমাদের কি সুস্পষ্ট দলীল প্রমাণ আছে?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3945,37,157,'তোমরা সত্যবাদী হলে তোমাদের কিতাব উপস্থিত কর। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3946,37,158,'আল্লাহ ও জিন জাতির মধ্যে তারা আত্মীয়তার সম্পর্ক স্থির করেছে; অথচ জিনেরা জানে যে, তাদেরকেও উপস্থিত করা হবে শাস্তির জন্য।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3947,37,159,'তারা যা বলে তা হতে আল্লাহ পবিত্র, মহান – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3948,37,160,'আল্লাহর একনিষ্ঠ বান্দারা ব্যতীত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3949,37,161,'তোমরা এবং তোমরা যাদের ইবাদাত কর – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3950,37,162,'তোমরা কেহই কেহকেও আল্লাহ সম্বন্ধে বিভ্রান্ত করতে পারবেনা – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3951,37,163,'শুধু প্রজ্জ্বলিত আগুনে প্রবেশকারীকে ব্যতীত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3952,37,164,'‘‘আমাদের প্রত্যেকের জন্যই নির্ধারিত স্থান রয়েছে, ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3953,37,165,'আমরাতো সারিবদ্ধভাবে দন্ডায়মান, ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3954,37,166,'এবং আমরা অবশ্যই তাঁর পবিত্রতা ঘোষণাকারী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3955,37,167,'তারাইতো বলে এসেছে – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3956,37,168,'‘‘পূর্ববর্তীদের কিতাবের মত যদি আমাদের কোন কিতাব থাকত – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3957,37,169,'তাহলে অবশ্যই আমরা আল্লাহর একনিষ্ঠ বান্দা হতাম’’। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3958,37,170,'কিন্তু তারা কুরআন প্রত্যাখ্যান করল এবং শীঘ্রই তারা জানতে পারবে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3959,37,171,'আমার প্রেরিত বান্দাদের সম্পর্কে আমার এই বাক্য পূর্বেই স্থির হয়েছে যে, ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3960,37,172,'অবশ্যই তারা জয়ী হবে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3961,37,173,'এবং আমার বাহিনীই হবে বিজয়ী। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3962,37,174,'অতএব কিছু কালের জন্য তুমি তাদেরকে উপেক্ষা কর। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3963,37,175,'তুমি তাদেরকে পর্যবেক্ষণ কর, শীঘ্রই তারা প্রত্যক্ষ করবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3964,37,176,'তারা কি তাহলে আমার শাস্তি ত্বরান্বিত করতে চায়?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3965,37,177,'তাদের আঙ্গিনায় যখন শাস্তি নেমে আসবে তখন সতর্কীকৃতদের প্রভাত হবে কত মন্দ! ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3966,37,178,'অতএব কিছুকালের জন্য তুমি তাদেরকে উপেক্ষা কর। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3967,37,179,'তুমি তাদেরকে পর্যবেক্ষণ কর, শীঘ্রই তারা পরিণাম প্রত্যক্ষ করবে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3968,37,180,'তারা যা আরোপ করে তা হতে পবিত্র ও মহান তোমার রাব্ব, যিনি সকল ক্ষমতার অধিকারী। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3969,37,181,'শান্তি বর্ষিত হোক রাসূলগণের প্রতি। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3970,37,182,'প্রশংসা জগতসমূহের রাব্ব আল্লাহরই প্রাপ্য।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3971,38,1,'সা‘দ, শপথ উপদেশপূর্ণ কুরআনের! ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3972,38,2,'কিন্তু কাফিরেরা ঔদ্ধত্য ও বিরোধিতায় ডুবে আছে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3973,38,3,'এদের পূর্বে আমি কত জনগোষ্ঠী ধ্বংস করেছি, তখন তারা আর্ত চিৎকার করেছিল। কিন্তু তখন পরিত্রাণের কোনই উপায় ছিলনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3974,38,4,'তারা বিস্ময় বোধ করছে যে, তাদের নিকট তাদেরই মধ্য হতে একজন সতর্ককারী এসেছে এবং কাফিরেরা বলেঃ এতো এক যাদুকর, মিথ্যাবাদী, ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3975,38,5,'সে কি অনেক মা‘বূদের পরিবর্তে এক মা‘বূদ বানিয়ে নিয়েছে? এতো এক অত্যাশ্চর্য ব্যাপার! ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3976,38,6,'তাদের প্রধানরা সরে পড়ে এই বলেঃ তোমরা চলে যাও এবং তোমাদের দেবতাগুলির পূজায় তোমরা অটল থাক। নিশ্চয়ই এ ব্যাপারটি উদ্দেশ্যমূলক। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3977,38,7,'আমরাতো অন্য ধর্মাদর্শে এরূপ কথা শুনিনি; এটা এক মনগড়া উক্তি মাত্র। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3978,38,8,'আমাদের মধ্য হতে কি তারই উপর কুরআন অবতীর্ণ হল? প্রকৃত পক্ষে তারা আমার কুরআনে সন্দিহান, তারা এখনও আমার শাস্তি আস্বাদন করেনি। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3979,38,9,'তাদের নিকট কি রয়েছে অনুগ্রহের ভান্ডার তোমার রবের, যিনি পরাক্রমশালী, মহান দাতা? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3980,38,10,'তাদের কি সার্বভৌমত্ব আছে আকাশমন্ডলী ও পৃথিবী এবং এতদুভয়ের অন্তবর্তী সব কিছুর উপর? থাকলে তারা সিড়ি বেয়ে আরোহণ করুক। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3981,38,11,'বহু দলের এই বাহিনীও সে ক্ষেত্রে অবশ্যই পরাজিত হবে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3982,38,12,'তাদের পূর্বেও রাসূলদেরকে মিথ্যাবাদী বলেছিল নূহের সম্প্রদায়, ‘আদ, বহু শিবিরের অধিপতি ফির‘আউন – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3983,38,13,'আর সামূদ, লূত সম্প্রদায় ও আইকা’র অধিবাসী। তারা ছিল এক একটি বিশাল বাহিনী। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3984,38,14,'তাদের প্রত্যেকেই রাসূলদেরকে মিথ্যাবাদী বলেছে। ফলে তাদের ক্ষেত্রে আমার শাস্তি হয়েছে যথার্থ। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3985,38,15,'তারাতো অপেক্ষা করছে একটি মাত্র প্রচন্ড নিনাদের যাতে কোন বিরাম থাকবেনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3986,38,16,'তারা বলেঃ হে আমাদের রাব্ব! বিচার দিনের পূর্বেই আমাদের প্রাপ্য আমাদেরকে শীঘ্র দিয়ে দাও। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3987,38,17,'তারা যা বলে তাতে তুমি ধৈর্য ধারণ কর। আর স্মরণ কর, আমার শক্তিশালী বান্দা দাঊদের কথা; সে ছিল অতিশয় আল্লাহর অভিমুখী। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3988,38,18,'আমি নিয়োজিত করেছিলাম পর্বতমালাকে, ওরা সকাল সন্ধ্যায় তার সাথে আমার পবিত্রতা ও মহিমা ঘোষণা করত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3989,38,19,'এবং সমবেত বিহংকুলকেও, সবাই ছিল তার অভিমুখী। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3990,38,20,'আমি তার রাজ্যকে সুদৃঢ় করেছিলাম এবং তাকে দিয়েছিলাম প্রজ্ঞা ও ফাইসালাকারী বাগ্মিতা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3991,38,21,'তোমার নিকট বিবাদকারী লোকের বৃত্তান্ত পৌঁছেছে কি, যখন তারা প্রাচীর ডিঙ্গিয়ে এল ইবাদাতখানায় – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3992,38,22,'এবং দাউদের নিকট পৌঁছল, তখন তাদের কারণে সে ভীত হয়ে পড়ল। তারা বললঃ ভীত হবেননা, আমরা দুই বিবাদমান পক্ষ - আমরা একে অপরের উপর যুল্‌ম করেছি; অতএব আমাদের মধ্যে ন্যায় বিচার করুন, অবিচার করবেননা এবং আমাদেরকে সঠিক পথ নির্দেশ করুন। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3993,38,23,'এ আমার ভাই - এর আছে নিরানব্বইটি দুম্বা এবং আমার আছে মাত্র একটি দুম্বা; তবুও সে বলে আমার জিম্মায় এটি দিয়ে দাও, এবং কথায় সে আমার প্রতি কঠোরতা প্রদর্শন করেছে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3994,38,24,'দাউদ বললঃ তোমার দুম্বাটিকে তার দুম্বাগুলির সাথে যুক্ত করার দাবী করে সে তোমার প্রতি যুল্‌ম করেছে। শরীকদের অনেকে একে অন্যের উপর অবিচার করে থাকে, করেনা শুধু মু’মিন ও সৎ কর্মশীল ব্যক্তিরা এবং তারা সংখ্যায় স্বল্প। দাউদ বুঝতে পারল যে, আমি তাকে পরীক্ষা করলাম। অতঃপর সে তার রবের নিকট ক্ষমা প্রার্থনা করল এবং নত হয়ে লুটিয়ে পড়ল ও তাঁর অভিমুখী হল। [সাজদাহ] ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3995,38,25,'অতঃপর আমি তার ক্রটি ক্ষমা করলাম। আমার নিকট তার জন্য রয়েছে উচ্চ মর্যাদা ও শুভ পরিণাম। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3996,38,26,'হে দাউদ! আমি তোমাকে পৃথিবীতে প্রতিনিধি করেছি, অতএব তুমি লোকদের মধ্যে সুবিচার কর এবং খেয়াল খুশীর অনুসরণ করনা, কেননা এটা তোমাকে আল্লাহর পথ হতে বিচ্যুত করবে। যারা আল্লাহর পথ পরিত্যাগ করে তাদের জন্য রয়েছে কঠিন শাস্তি, কারণ তারা বিচার দিনকে বিস্মৃত হয়ে আছে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3997,38,27,'আমি আকাশ ও পৃথিবী এবং এতদুভয়ের মধ্যস্থিত কোন কিছুই অনর্থক সৃষ্টি করিনি, যদিও কাফিরদের ধারণা তাই। সুতরাং কাফিরদের জন্য রয়েছে জাহান্নামের দুর্ভোগ। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3998,38,28,'যারা ঈমান আনে ও সৎ কাজ করে এবং যারা পৃথিবীতে বিপর্যয় সৃষ্টি করে বেড়ায় আমি কি তাদেরকে সমগন্য করব? আমি কি মুত্তাকীদেরকে অপরাধীদের সমান গন্য করব? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (3999,38,29,'এক কল্যাণময় কিতাব ইহা, আমি তোমার উপর অবতীর্ণ করেছি, যাতে মানুষ এর আয়াতসমূহ অনুধাবন করে এবং বোধশক্তিসম্পন্ন ব্যক্তিরা উপদেশ গ্রহণ করে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4000,38,30,'আমি দাঊদকে দান করলাম সুলাইমান। সে ছিল উত্তম বান্দা এবং সে ছিল অতিশয় আল্লাহ অভিমুখী। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4001,38,31,'যখন অপরাহ্নে তার সামনে ধাবমান উৎকৃষ্ট অশ্বরাজিকে উপস্থিত করা হল – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4002,38,32,'তখন সে বললঃ আমিতো আমার রবের স্মরণ হতে বিমুখ হয়ে ঐশ্বর্য প্রীতিতে মগ্ন হয়ে পড়েছি, এদিকে সূর্য অস্তমিত হয়ে গেছে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4003,38,33,'ওগুলোকে পুনরায় আমার সামনে নিয়ে এসো। অতঃপর সে ওগুলোর পা ও গলদেশ ছেদন করতে লাগল। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4004,38,34,'আমি সুলাইমানকে পরীক্ষা করলাম এবং তার আসনের উপর রাখলাম একটি দেহ; অতঃপর সুলাইমান আমার অভিমুখী হল। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4005,38,35,'সে বললঃ হে আমার রাব্ব! আমাকে ক্ষমা করুন এবং এমন এক রাজ্য দান করুন যার অধিকারী আমি ছাড়া আর কেহ না হয়। আপনিতো পরম দাতা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4006,38,36,'তখন আমি তার অধীন করে দিলাম বায়ুকে যা তার আদেশে, সে যেখানে ইচ্ছা করত সেখানে মৃদুমন্দ গতিতে প্রবাহিত হত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4007,38,37,'এবং শাইতানদেরকে, যারা সবাই ছিল প্রাসাদ নির্মাণকারী ও ডুবুরী। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4008,38,38,'এবং শৃংখলে আবদ্ধ করলাম আরও অনেককে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4009,38,39,'এসব আমার অনুগ্রহ, এটা তুমি অন্যকে দিতে অথবা নিজে রাখতে পার। এ জন্য তোমাকে হিসাব দিতে হবেনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4010,38,40,'এবং আমার নিকট তার জন্য রয়েছে উচ্চ মর্যাদা ও শুভ পরিণাম। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4011,38,41,'স্মরণ কর আমার বান্দা আইয়ুবকে! যখন সে তার রাব্বকে আহবান করে বলেছিলঃ শাইতানতো আমাকে যন্ত্রণা ও কষ্টে ফেলেছে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4012,38,42,'আমি তাকে বললামঃ তুমি তোমার পা দ্বারা ভূমিতে আঘাত কর, এইতো গোসলের সুশীতল পানি ও পান করার পানীয়। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4013,38,43,'আমি তাকে দিলাম তার পরিজনবর্গ ও তাদের মত আরও, আমার অনুগ্রহস্বরূপ ও বোধশক্তিসম্পন্ন লোকদের জন্য উপদেশস্বরূপ। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4014,38,44,'আমি তাকে আদেশ করলামঃ এক মুষ্টি তৃণ তুলে নাও এবং তা দ্বারা আঘাত কর ও শপথ ভঙ্গ করনা। আমি তাকে পেলাম ধৈর্যশীল। কত উত্তম বান্দা সে! সে ছিল আমার অভিমুখী। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4015,38,45,'স্মরণ কর, আমার বান্দা ইবরাহীম, ইসহাক ও ইয়াকূবের কথা, তারা ছিল শক্তিশালী ও সূক্ষ্মদর্শী। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4016,38,46,'আমি তাদেরকে অধিকারী করেছিলাম এক বিশেষ গুণের, ওটা ছিল পরকালের স্মরণ। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4017,38,47,'অবশ্যই তারা ছিল আমার মনোনীত ও উত্তম বান্দাদের অন্তর্ভুক্ত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4018,38,48,'স্মরণ কর ইসমাঈল, আল ইয়াসাআ’ ও যুলকিফলের কথা, তারা প্রত্যেকেই ছিল সজ্জন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4019,38,49,'এটা এক স্মরণীয় বর্ণনা এবং মুত্তাকীদের জন্য রয়েছে উত্তম আবাস – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4020,38,50,'চিরস্থায়ী জান্নাত, তাদের জন্য উম্মুক্ত রয়েছে যার দ্বার।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4021,38,51,'সেখানে তারা আসীন হবে হেলান দিয়ে, সেখানে তারা বহুবিধ ফলমূল ও পানীয়ের জন্য আদেশ দিবে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4022,38,52,'আর তাদের পাশে থাকবে আনত নয়না সমবয়স্কা তরুণীরা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4023,38,53,'এটাই হিসাব দিনে তোমাদেরকে দেয়া প্রতিশ্রুতি। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4024,38,54,'এটাই আমার দেয়া রিয্ক যা নিঃশেষ হবেনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4025,38,55,'এটা এরূপই! আর সীমালংঘনকারীদের জন্য রয়েছে নিকৃষ্টতম ঠিকানা – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4026,38,56,'জাহান্নাম, সেখানে তারা প্রবেশ করবে, কত নিকৃষ্ট বিশ্রামস্থল। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4027,38,57,'এটা সীমালংঘনকারীদের জন্য। সুতরাং তারা আস্বাদন করুক ফুটন্ত পানি ও পুঁজ। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4028,38,58,'আরও আছে এ রূপ বিভিন্ন ধরণের শাস্তি। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4029,38,59,'এইতো এক বাহিনী, তোমাদের সাথে প্রবেশ করেছে। তাদের জন্য নেই অভিনন্দন, তারাতো জাহান্নামে জ্বলবে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4030,38,60,'অনুসারীরা বলবেঃ বরং তোমরাও, তোমাদের জন্যও তো অভিনন্দন নেই। তোমরাইতো পূর্বে ওটা আমাদের জন্য ব্যবস্থা করেছ, কত নিকৃষ্ট এই আবাসস্থল! ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4031,38,61,'তারা বলবেঃ হে আমাদের রাব্ব! যে এটা আমাদের সম্মুখীন করেছে জাহান্নামে তার শাস্তি আপনি দ্বিগুণ বর্ধিত করুন! ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4032,38,62,'তারা আরও বলবেঃ আমাদের কি হল যে, আমরা যে সব লোককে মন্দ বলে গণ্য করতাম তাদেরকে দেখতে পাচ্ছিনা?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4033,38,63,'তাহলে কি আমরা তাদেরকে অহেতুক ঠাট্টা বিদ্রুপের পাত্র মনে করতাম, নাকি তাদের ব্যাপারে আমাদের দৃষ্টি বিভ্রম ঘটেছে? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4034,38,64,'এটা নিশ্চিত সত্য জাহান্নামীদের এই বাদ প্রতিবাদ। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4035,38,65,'বলঃ আমিতো একজন সতর্ককারী মাত্র এবং আল্লাহ ছাড়া কোন মা‘বূদ নেই যিনি এক, পরাক্রমশালী – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4036,38,66,'যিনি আকাশমন্ডলী, পৃথিবী এবং এগুলির মধ্যস্থিত সবকিছুর রাব্ব, যিনি পরাক্রমশালী, মহা ক্ষমাশীল। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4037,38,67,'বলঃ এটা এক মহা সংবাদ – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4038,38,68,'যা হতে তোমরা মুখ ফিরিয়ে নিচ্ছ। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4039,38,69,'উর্ধ্বলোকে তাদের বাদানুবাদ সম্পর্কে আমার কোন জ্ঞান ছিলনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4040,38,70,'আমার নিকটতো এই অহী এসেছে যে, আমি একজন স্পষ্ট সতর্ককারী। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4041,38,71,'স্মরণ কর, তোমার রাব্ব মালাইকা/ফেরেশতাদেরকে বলেছিলেনঃ আমি মানুষ সৃষ্টি করছি কর্দম হতে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4042,38,72,'যখন আমি ওকে সুষম করব এবং ওতে আমার সৃষ্টি রূহ সঞ্চার করব তখন তোমরা ওর প্রতি সাজদাহবনত হও। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4043,38,73,'তখন মালাইকা/ফেরেশতারা সবাই সাজদাহবনত হল – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4044,38,74,'শুধু ইবলীস ব্যতীত; সে অহংকার করল এবং কাফিরদের অন্তর্ভুক্ত হল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4045,38,75,'তিনি বললেনঃ হে ইবলীস! আমি যাকে নিজ হাতে সৃষ্টি করেছি তার প্রতি সাজদাহবনত হতে তোকে কিসে বাধা দিল? তুই কি ঔদ্ধত্য প্রকাশ করলি, নাকি তুই উচ্চ মর্যাদাসম্পন্ন? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4046,38,76,'সে বললঃ আমি তার চেয়ে শ্রেষ্ঠ। আপনি আমাকে আগুন হতে সৃষ্টি করেছেন এবং তাকে সৃষ্টি করেছেন মাটি হতে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4047,38,77,'তিনি বললেনঃ তুই এখান হতে বের হয়ে যা, নিশ্চয়ই তুই বিতাড়িত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4048,38,78,'এবং তোর উপর আমার লা’নত স্থায়ী হবে, কর্মফল দিন পর্যন্ত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4049,38,79,'সে বললঃ হে আমার রাব্ব! আপনি আমাকে অবকাশ দিন পুনরুত্থান দিন পর্যন্ত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4050,38,80,'তিনি বললেনঃ তুই অবকাশ প্রাপ্তদের অন্তর্ভুক্ত হলি – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4051,38,81,'অবধারিত সময় উপস্থিত হওয়ার দিন পর্যন্ত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4052,38,82,'সে বললঃ আপনার ক্ষমতার শপথ! আমি তাদের সবাইকে পথভ্রষ্ট করব। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4053,38,83,'তবে তাদের মধ্যে আপনার একনিষ্ঠ বান্দাদেরকে নয়। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4054,38,84,'তিনি বললেনঃ তবে এটাই সত্য, আর আমি সত্যই বলি –');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4055,38,85,'তোর দ্বারা ও তোর অনুসারীদের দ্বারা আমি জাহান্নাম পূর্ণ করবই। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4056,38,86,'বলঃ আমি এর জন্য তোমাদের নিকট কোন প্রতিদান চাইনা এবং যারা মিথ্যা দাবী করে আমি তাদের অন্তর্ভুক্ত নই। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4057,38,87,'ইহাতো বিশ্বজগতের জন্য উপদেশ মাত্র। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4058,38,88,'এর সংবাদ তোমরা অবশ্যই জানবে, কিছুকাল পরে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4059,39,1,'এই কিতাব অবতীর্ণ পরাক্রমশালী, প্রজ্ঞাময় আল্লাহর নিকট হতে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4060,39,2,'আমি তোমার নিকট এই কিতাব সত্যসহ অবতীর্ণ করেছি; সুতরাং আল্লাহর ইবাদাত কর তাঁর আনুগত্যে বিশুদ্ধ চিত্ত হয়ে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4061,39,3,'জেনে রেখ, অবিমিশ্র আনুগত্য আল্লাহরই প্রাপ্য। যারা আল্লাহর পরিবর্তে অন্যকে অভিভাবক রূপে গ্রহণ করে তারা বলেঃ আমরা তো এদের পূজা এ জন্যই করি যে, এরা আমাদেরকে আল্লাহর সান্নিধ্য এনে দিবে। তারা যে বিষয়ে নিজেদের মধ্যে মতভেদ করছে আল্লাহ তার ফাইসালা করে দিবেন। যে মিথ্যাবাদী ও কাফির, আল্লাহ তাকে সৎ পথে পরিচালিত করেন না। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4062,39,4,'আল্লাহ সন্তান গ্রহণ করতে ইচ্ছা করলে তিনি তাঁর সৃষ্টির মধ্যে যাকে ইচ্ছা মনোনীত করতে পারতেন। পবিত্র ও মহান তিনি। তিনিই আল্লাহ এক, প্রবল পরাক্রমশালী। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4063,39,5,'তিনি যথাযথভাবে আকাশমন্ডলী ও পৃথিবী সৃষ্টি করেছেন। তিনি রাত দ্বারা দিনকে আচ্ছাদিত করেন এবং রাতকে আচ্ছাদিত করেন দিন দ্বারা। সূর্য ও চন্দ্রকে তিনি করেছেন নিয়মাধীন। প্রত্যেকেই পরিক্রমন করে এক নির্দিষ্ট কাল পর্যন্ত। জেনে রেখ, তিনি পরাক্রমশালী, ক্ষমাশীল। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4064,39,6,'তিনি তোমাদেরকে সৃষ্টি করেছেন একই ব্যক্তি হতে। অতঃপর তিনি তা হতে তার সঙ্গিনী সৃষ্টি করেছেন। তিনি তোমাদের দিয়েছেন আট প্রকার গৃহপালিত পশু। তিনি তোমাদের মাতৃগর্ভের ত্রিবিধ অন্ধকারে পর্যায়ক্রমে সৃষ্টি করেছেন। তিনিই আল্লাহ! তোমাদের রাব্ব। সার্বভৌমত্ব তাঁরই, তিনি ছাড়া কোন মা‘বূদ নেই। অতএব তোমরা মুখ ফিরিয়ে কোথায় চলেছ? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4065,39,7,'তোমরা অকৃতজ্ঞ হলে আল্লাহ তোমাদের মুখাপেক্ষী নন, তিনি তাঁর বান্দাদের অকৃতজ্ঞতা পছন্দ করেননা। যদি তোমরা কৃতজ্ঞ হও তাহলে তিনি তোমাদের জন্য এটাই পছন্দ করেন। একের বোঝা অন্যে বহন করবেনা। অতঃপর তোমাদের রবের নিকট তোমাদের প্রত্যাবর্তন এবং তোমরা যা করতে তিনি তোমাদেরকে তা অবগত করাবেন। অন্তরে যা আছে তা তিনি সম্যক অবগত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4066,39,8,'মানুষকে যখন দুঃখ দৈন্য স্পর্শ করে তখন সে অনুতপ্ত হয়ে একনিষ্ঠভাবে তার রাব্বকে ডাকে। কিন্তু পরে যখন তিনি তার প্রতি অনুগ্রহ করেন তখন সে বিস্মৃত হয়ে যায় পূর্বে যাকে সে ডেকেছিল তাঁকে এবং সে আল্লাহর সমকক্ষ দাঁড় করায়, অপরকে তাঁর পথ হতে বিভ্রান্ত করার জন্য। বলঃ কুফরীর জীবন অবস্থায় তুমি কিছু কাল উপভোগ করে নাও। বস্তুতঃ তুমিতো জাহান্নামেরই অধিবাসী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4067,39,9,'যে ব্যক্তি রাতের বিভিন্ন সময়ে সাজদাহবনত হয়ে ও দাঁড়িয়ে আনুগত্য প্রকাশ করে, আখিরাতকে ভয় করে এবং তার রবের অনুগ্রহ প্রত্যাশা করে সে কি তার সমান যে তা করেনা? বলঃ যারা জানে এবং যারা জানেনা তারা কি সমান? বোধশক্তি সম্পন্ন লোকেরাই শুধু উপদেশ গ্রহণ করে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4068,39,10,'বলঃ (আমার এই কথা) হে আমার মু’মিন বান্দারা! তোমরা তোমাদের রাব্বকে ভয় কর। যারা এই দুনিয়ায় কল্যাণকর কাজ করে তাদের জন্য আছে কল্যাণ। প্রশস্ত আল্লাহর পৃথিবী, ধৈর্যশীলদেরকে অপরিমিত পুরষ্কার দেয়া হবে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4069,39,11,'বলঃ আমি আদিষ্ট হয়েছি আল্লাহর আনুগত্যে একনিষ্ঠ হয়ে তাঁর ইবাদাত করতে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4070,39,12,'আর আদিষ্ট হয়েছি, আমি যেন আত্মসমর্পনকারীদের অগ্রণী হই। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4071,39,13,'বলঃ আমি যদি আমার রবের অবাধ্য হই তাহলে আমি ভয় করি মহা দিনের শাস্তির। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4072,39,14,'বলঃ আমি ইবাদাত করি আল্লাহরই, তাঁর প্রতি আমার আনুগত্যকে একনিষ্ঠ রেখে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4073,39,15,'অতএব তোমরা আল্লাহর পরিবর্তে যার ইচ্ছা তার ইবাদাত কর। বলঃ কিয়ামাত দিবসে ক্ষতিগ্রস্ত তারাই যারা নিজেদের ও নিজেদের পরিবারবর্গের ক্ষতিসাধন করে। জেনে রেখ, এটাই সুস্পষ্ট ক্ষতি। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4074,39,16,'তাদের জন্য থাকবে তাদের উর্ধ্বদিকে আগুনের আচ্ছাদন এবং নিম্নদিকেও আচ্ছাদন। এ দ্বারা আল্লাহ তাঁর বান্দাদেরকে সতর্ক করেন। হে আমার বান্দারা! তোমরা আমাকে ভয় কর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4075,39,17,'যারা তাগুতের পূজা হতে দূরে থাকে এবং আল্লাহর অভিমুখী হয় তাদের জন্য আছে সুসংবাদ। অতএব সুসংবাদ দাও আমার বান্দাদেরকে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4076,39,18,'যারা মনোযোগ সহকারে কথা শুনে এবং ওর মধ্যে যা উত্তম তা গ্রহণ করে তাদেরকে আল্লাহ সৎ পথে পরিচালিত করেন এবং তারাই বোধশক্তি সম্পন্ন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4077,39,19,'যার উপর দন্ডাদেশ অবধারিত হয়েছে, তুমি কি রক্ষা করতে পারবে সেই ব্যক্তিকে যে জাহান্নামে আছে? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4078,39,20,'তবে যারা তাদের রাব্বকে ভয় করে তাদের জন্য আছে বহু প্রাসাদ, যার উপর নির্মিত আরও প্রাসাদ, যার পাদদেশে নদী প্রবাহিত; আল্লাহ প্রতিশ্রুতি দিয়েছেন, আল্লাহ প্রতিশ্রুতি ভঙ্গ করেননা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4079,39,21,'তুমি কি দেখনা যে, আল্লাহ আকাশ হতে বারি বর্ষণ করেন; অতঃপর ভূমিতে নির্ঝর রূপে প্রবাহিত করেন এবং তদ্বারা বিবিধ বর্ণের ফসল উৎপন্ন করেন, অতঃপর ওটা শুকিয়ে যায় এবং তোমরা ওটা পীত বর্ণ দেখতে পাও, অবশেষে তিনি ওটা খড়কুটায় পরিণত করেন? এতে অবশ্যই উপদেশ রয়েছে বোধশক্তিসম্পন্নদের জন্য। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4080,39,22,'আল্লাহ ইসলামের জন্য যার বক্ষ উন্মুক্ত করে দিয়েছেন এবং যে তার রবের আলোকে আছে সে কি তার সমান (যে এরূপ নয়); দুর্ভোগ সেই কঠোর হৃদয় ব্যক্তিদের জন্য যারা আল্লাহর স্মরণে পরান্মুখ! তারা স্পষ্ট বিভ্রান্তিতে রয়েছে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4081,39,23,'আল্লাহ অবতীর্ণ করেছেন উত্তম বাণী সম্বলিত কিতাব যা সুসামঞ্জস এবং যা পুনঃ পুনঃ আবৃত্তি করা হয়। এতে যারা তাদের রাব্বকে ভয় করে তাদের গাত্র রোমাঞ্চিত হয়, অতঃপর তাদের দেহ-মন প্রশান্ত হয়ে আল্লাহর স্মরণে ঝুঁকে পড়ে; এটাই আল্লাহর পথনির্দেশ, তিনি যাকে ইচ্ছা এটা দ্বারা পথ প্রদর্শন করেন। আল্লাহ যাকে বিভ্রান্ত করেন তার কোন পথ প্রদর্শক নেই। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4082,39,24,'যে ব্যক্তি কিয়ামাত দিবসে তার মুখমন্ডল দ্বারা কঠিন শাস্তি ঠেকাতে চাবে সে কি তার মত যে নিরাপদ? যালিমদের বলা হবে, তোমরা যা অর্জন করতে তার শাস্তি আস্বাদন কর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4083,39,25,'তাদের পূর্ববর্তীরাও মিথ্যা আরোপ করেছিল, ফলে শাস্তি তাদেরকে গ্রাস করল তাদের অজ্ঞাতসারে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4084,39,26,'ফলে আল্লাহ তাদেরকে পার্থিব জীবনে লাঞ্ছনা ভোগ করালেন এবং আখিরাতের শাস্তিতো কঠিনতর, যদি তারা জানতো! ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4085,39,27,'আমি এই কুরআনে মানুষের জন্য সর্ব প্রকার দৃষ্টান্ত উপস্থিত করেছি যাতে তারা উপদেশ গ্রহণ করে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4086,39,28,'আরাবী ভাষার এই কুরআন বক্রতামুক্ত, যাতে মানুষ সাবধানতা অবলম্বন করে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4087,39,29,'আল্লাহ একটি দৃষ্টান্ত পেশ করছেনঃ এক ব্যক্তির মালিক অনেক যারা পরস্পর বিরুদ্ধ ভাবাপন্ন এবং এক ব্যক্তির মালিক শুধু একজন; এই দুইয়ের অবস্থা কি সমান? প্রশংসা আল্লাহরই প্রাপ্য; কিন্তু তাদের অধিকাংশই এটা জানেনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4088,39,30,'তুমিতো মরণশীল এবং তারাও মরণশীল। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4089,39,31,'অতঃপর কিয়ামাত দিবসে তোমরা পরস্পর তোমাদের রবের সামনে বাকবিতন্ডা করবে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4090,39,32,'যে ব্যক্তি আল্লাহ সম্বন্ধে মিথ্যা বলে এবং সত্য আসার পর তা প্রত্যাখ্যান করে সে অপেক্ষা যালিম আর কে? কাফিরদের আবাসস্থল কি জাহান্নাম নয়? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4091,39,33,'যারা সত্য এনেছে এবং যারা সত্যকে সত্য বলে মেনেছে তারাইতো মুত্তাকী। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4092,39,34,'তাদের বাঞ্ছিত সব কিছুই আছে তাদের রবের নিকট। এটাই সৎকর্মশীলদের পুরস্কার। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4093,39,35,'কারণ তারা যে সব মন্দ কাজ করেছিল আল্লাহ তা ক্ষমা করে দিবেন এবং তাদেরকে তাদের সৎ কাজের জন্য পুরস্কৃত করবেন। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4094,39,36,'আল্লাহ কি তাঁর বান্দার জন্য যথেষ্ট নন? অথচ তারা তোমাদেরকে আল্লাহর পরিবর্তে অপরের ভয় দেখায়। আল্লাহ যাকে বিভ্রান্ত করেন তার জন্য কোন পথ প্রদর্শক নেই। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4095,39,37,'এবং যাকে আল্লাহ হিদায়াত দান করেন তার জন্য কোন পথভ্রষ্টকারী নেই, আল্লাহ কি পরাক্রমশালী দন্ডবিধায়ক নন? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4096,39,38,'তুমি যদি তাদেরকে জিজ্ঞেস করঃ আকাশমন্ডলী ও পৃথিবী কে সৃষ্টি করেছেন? তারা অবশ্যই বলবেঃ আল্লাহ! বলঃ তোমরা কি ভেবে দেখেছ যে, আল্লাহ আমার অনিষ্ট চাইলে তোমরা আল্লাহর পরিবর্তে যাদেরকে ডাক তারা কি সেই অনিষ্টতা দূর করতে পারবে? অথবা তিনি আমার প্রতি অনুগ্রহ করতে চাইলে তারা কি সেই অনুগ্রহ রোধ করতে পারবে? বলঃ আমার জন্য আল্লাহই যথেষ্ট। নির্ভরকারীরা আল্লাহর উপর নির্ভর করে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4097,39,39,'বলঃ হে আমার সম্প্রদায়! তোমরা স্ব স্ব অবস্থায় কাজ করতে থাক, আমিও আমার কাজ করছি। শীঘ্রই জানতে পারবে – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4098,39,40,'কার উপর আসবে লাঞ্ছনাদায়ক শাস্তি এবং কার উপর আপতিত হবে স্থায়ী শাস্তি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4099,39,41,'আমি তোমার প্রতি সত্যসহ কিতাব অবতীর্ণ করেছি মানুষের কল্যাণের জন্য। অতঃপর যে সৎ পথ অবলম্বন করে সে তা করে নিজেরই কল্যাণের জন্য এবং যে বিপথগামী হয় সেতো বিপথগামী হয় নিজেরই ধ্বংসের জন্য এবং তুমি তাদের তত্ত্বাবধায়ক নও। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4100,39,42,'আল্লাহই প্রাণ হরণ করেন জীবসমূহের, তাদের মৃত্যুর সময় এবং যাদের মৃত্যু আসেনি তাদের প্রাণও নিদ্রার সময়। অতঃপর যার জন্য মৃত্যুর সিদ্ধান্ত করেন তার প্রাণ তিনি রেখে দেন এবং অপরগুলি ফিরিয়ে দেন, এক নির্দিষ্ট সময়ের জন্য। এতে অবশ্যই নিদর্শন রয়েছে চিন্তাশীল সম্প্রদায়ের জন্য।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4101,39,43,'তাহলে কি তারা আল্লাহ ছাড়া অপরকে সুপারিশ সাব্যস্ত করেছে? বলঃ তাদের কোন ক্ষমতা না থাকলেও এবং তারা না বুঝলেও? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4102,39,44,'বলঃ সুপারিশ ইখতিয়ারে, আকাশমন্ডলী ও পৃথিবীর সার্বভৌমত্ব আল্লাহরই। অতঃপর তাঁরই নিকট তোমরা প্রত্যানীত হবে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4103,39,45,'একক আল্লাহর কথা বলা হলে, যারা আখিরাতে বিশ্বাস করেনা তাদের অন্তর বিতৃষ্ণায় সংকুচিত হয় এবং আল্লাহর পরিবর্তে তাদের দেবতাগুলির উল্লেখ করা হলে তারা আনন্দে উল্লসিত হয়। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4104,39,46,'বলঃ হে আল্লাহ! আকাশমন্ডলী ও পৃথিবীর স্রষ্টা! দৃশ্য ও অদৃশ্যের পরিজ্ঞাতা! আপনার বান্দারা যে বিষয়ে মতবিরোধ করে, আপনি তাদের মধ্যে ওর ফাইসালা করে দিবেন। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4105,39,47,'যারা যুল্‌ম করেছে, যদি তাদের দুনিয়ায় যা আছে তা সম্পূর্ণ এবং এর সম পরিমাণ সম্পদও থাকে তাহলে কিয়ামাত দিবসে কঠিন শাস্তি হতে মুক্তিপণ স্বরূপ সকল বিষয় সম্পত্তি তারা দিয়ে দিবে এবং তাদের জন্য আল্লাহর নিকট হতে এমন কিছু প্রকাশিত হবে যা তারা কল্পনাও করেনি। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4106,39,48,'তাদের কৃতকর্মের মন্দ ফল তাদের নিকট প্রকাশ হয়ে পড়বে এবং তারা যা নিয়ে ঠাট্টা বিদ্রুপ করত তা তাদেরকে পরিবেষ্টন করবে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4107,39,49,'মানুষকে দুঃখ দৈন্য স্পর্শ করলে সে আমাকে আহবান করে। অতঃপর যখন আমি তার প্রতি অনুগ্রহ করি তখন সে বলেঃ আমিতো এটা লাভ করেছি আমার জ্ঞানের মাধ্যমে। বস্তুতঃ এটা এক পরীক্ষা, কিন্তু তাদের অধিকাংশই বুঝেনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4108,39,50,'তাদের পূর্ববর্তীরাও এটাই বলত, কিন্তু তাদের কৃতকর্ম তাদের কোন কাজে আসেনি। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4109,39,51,'তাদের কর্মের মন্দ ফল তাদের উপর আপতিত হয়েছে, তাদের মধ্যে যারা যুল্‌ম করে তাদের উপরও তাদের কর্মের মন্দ ফল আপতিত হবে এবং তারা ব্যর্থও করতে পারবেনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4110,39,52,'তারা কি জানেনা, আল্লাহ যার জন্য ইচ্ছা তার রিয্ক বৃদ্ধি করেন অথবা হ্রাস করেন। এতে অবশ্যই নিদর্শন রয়েছে মু’মিন সম্প্রদায়ের জন্য।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4111,39,53,'বলঃ (আমার এ কথা) হে আমার বান্দারা! তোমরা যারা নিজেদের প্রতি অবিচার করেছ - আল্লাহর অনুগ্রহ হতে নিরাশ হয়োনা; আল্লাহ সমুদয় পাপ ক্ষমা করে দিবেন। তিনিতো ক্ষমাশীল, পরম দয়ালু। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4112,39,54,'তোমরা তোমাদের রবের অভিমুখী হও এবং তাঁর নিকট আত্মসমর্পণ কর তোমাদের নিকট শাস্তি আসার পূর্বে, অতঃপর তোমাদেরকে সাহায্য করা হবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4113,39,55,'অনুসরণ কর তোমাদের প্রতি তোমাদের রবের নিকট হতে উত্তম যা অবতীর্ণ করা হয়েছে তার, তোমাদের উপর অতর্কিতভাবে তোমাদের অজ্ঞাতে শাস্তি আসার পূর্বে – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4114,39,56,'যাতে কেহকেও বলতে না হয়ঃ হায়! আল্লাহর প্রতি আমার কর্তব্যে আমি যে শৈথিল্য করেছি তার জন্য আফসোস! আমিতো ঠাট্টাকারীদের অন্তর্ভুক্ত ছিলাম।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4115,39,57,'অথবা কেহ যেন না বলেঃ আল্লাহ আমাকে পথ প্রদর্শন করলে আমিতো অবশ্যই মুত্তাকীদের অন্তর্ভুক্ত হতাম। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4116,39,58,'অথবা শাস্তি প্রত্যক্ষ করলে যেন কেহকে বলতে না হয়ঃ আহা! যদি একবার পৃথিবীতে আমার প্রত্যাবর্তন ঘটত তাহলে আমি সৎকর্মশীল হতাম।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4117,39,59,'প্রকৃত ব্যাপারতো এই যে, আমার নিদর্শন তোমার নিকট এসেছিল, কিন্তু তুমি এগুলিকে মিথ্যা বলেছিলে এবং অহংকার করেছিলে; আর তুমিতো ছিলে কাফিরদের একজন। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4118,39,60,'যারা আল্লাহর প্রতি মিথ্যা আরোপ করে, তুমি কিয়ামাত দিবসে তাদের মুখমন্ডল কালো দেখবে। ঔদ্ধত্যদের আবাসস্থল কি জাহান্নাম নয়?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4119,39,61,'আল্লাহ মুত্তাকীদের উদ্ধার করবেন তাদের সাফল্যসহ; তাদেরকে অমঙ্গল স্পর্শ করবেনা এবং তারা দুঃখও পাবেনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4120,39,62,'আল্লাহ সব কিছুর স্রষ্টা এবং তিনি সব কিছুর কর্মবিধায়ক। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4121,39,63,'আকাশমন্ডলী ও পৃথিবীর চাবি তাঁরই নিকট। যারা আল্লাহর আয়াতকে অস্বীকার করে তারাই ক্ষতিগ্রস্ত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4122,39,64,'বলঃ হে অজ্ঞ ব্যক্তিরা! তোমরা কি আমাকে আল্লাহ ব্যতীত অন্যের ইবাদাত করতে বলছ? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4123,39,65,'তোমার প্রতি, তোমার পূর্ববর্তীদের প্রতি অবশ্যই অহী হয়েছে; তুমি আল্লাহর শরীক স্থির করলে তোমার কাজ নিস্ফল হবে এবং তুমি হবে ক্ষতিগ্রস্ত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4124,39,66,'অতএব তুমি আল্লাহরই ইবাদাত কর এবং কৃতজ্ঞ হও। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4125,39,67,'তারা আল্লাহর যথোচিত সম্মান করেনা। কিয়ামাত দিবসে সমস্ত পৃথিবী থাকবে তাঁর হাতের মুষ্টিতে এবং আকাশমন্ডলী ভাঁজ করা থাকবে তাঁর ডান হাতে। পবিত্র ও মহান তিনি, তারা যাকে শরীক করে তিনি তার উর্ধ্বে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4126,39,68,'এবং শিঙ্গায় ফুৎকার দেয়া হবে, ফলে যাদেরকে আল্লাহ ইচ্ছা করবেন তারা ব্যতীত আকাশমন্ডলী ও পৃথিবীর সবাই মুর্ছিত হয়ে পড়বে। অতঃপর আবার শিঙ্গায় ফুৎকার দেয়া হবে, তৎক্ষণাৎ তারা দন্ডায়মান হয়ে তাকাতে থাকবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4127,39,69,'যমীন ওর রবের জ্যোতিতে উদ্ভাসিত হবে, ‘আমলনামা পেশ করা হবে এবং নাবীদেরকে ও সাক্ষীদেরকে হাযির করা হবে এবং সকলের মধ্যে ন্যায় বিচার করা হবে ও তাদের প্রতি যুলম করা হবেনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4128,39,70,'প্রত্যেকের কৃতকর্মের পূর্ণ প্রতিফল দেয়া হবে। তারা যা করে সেই সম্পর্কে আল্লাহ সবিশেষ অবহিত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4129,39,71,'কাফিরদেরকে জাহান্নামের দিকে দলে দলে হাঁকিয়ে নিয়ে যাওয়া হবে। যখন তারা সেখানে উপস্থিত হবে তখন ওর প্রবেশ দ্বারগুলি খুলে দেয়া হবে এবং জাহান্নামের রক্ষীরা তাদেরকে বলবেঃ তোমাদের নিকট কি তোমাদের মধ্য হতে রাসূল আসেনি যারা তোমাদের নিকট তোমাদের রবের আয়াত আবৃত্তি করত এবং এই দিনের সাক্ষাৎ সম্বন্ধে সতর্ক করত? তারা বলবেঃ অবশ্যই এসেছিল। বস্তুতঃ কাফিরদের প্রতি শাস্তির কথা বাস্তবায়িত হয়েছে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4130,39,72,'তাদেরকে বলা হবেঃ জাহান্নামের দ্বারসমূহে প্রবেশ কর, তাতে স্থায়ীভাবে অবস্থিতির জন্য। কত নিকৃষ্ট উদ্ধতদের আবাসস্থল!');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4131,39,73,'যারা তাদের রাব্বকে ভয় করত তাদেরকে দলে দলে জান্নাতের দিকে নিয়ে যাওয়া হবে। যখন তারা সেখানে উপস্থিত হবে তখন ওর দ্বারসমূহ খুলে দেয়া হবে এবং জান্নাতের রক্ষীরা তাদেরকে বলবেঃ তোমাদের প্রতি সালাম, তোমরা সুখী হও এবং জান্নাতে প্রবেশ কর স্থায়ীভাবে অবস্থানের জন্য। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4132,39,74,'তারা প্রবেশ করে বলবেঃ প্রশংসা আল্লাহর যিনি আমাদের প্রতি তাঁর প্রতিশ্রুতি পূর্ণ করেছেন এবং আমাদের অধিকারী করেছেন এই ভূমির; আমরা জান্নাতে যেখানে ইচ্ছা বসবাস করব। সদাচারীদের পুরস্কার কত উত্তম!');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4133,39,75,'এবং তুমি মালাইকা/ফেরেশতাদেরকে দেখতে পাবে যে, তারা আরশের চতুস্পার্শ্বে ঘিরে তাদের রবের সপ্রশংস পবিত্রতা ও মহিমা ঘোষণা করছে। আর তাদের বিচার করা হবে ন্যায়ের সাথে। বলা হবেঃ প্রশংসা জগতসমূহের রাব্ব আল্লাহর প্রাপ্য। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4134,40,1,'হা- মীম। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4135,40,2,'এই কিতাব অবতীর্ণ হয়েছে পরাক্রমশালী সর্বজ্ঞ আল্লাহর নিকট হতে – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4136,40,3,'যিনি পাপ ক্ষমা করেন এবং তাওবাহ কবূল করেন, যিনি শাস্তি দানে কঠোর, শক্তিশালী। তিনি ব্যতীত কোন মা‘বূদ নেই। প্রত্যাবর্তন তাঁরই নিকট। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4137,40,4,'শুধু কাফিরেরাই আল্লাহর নিদর্শন সম্বন্ধে বিতর্ক করে; সুতরাং দেশে দেশে তাদের অবাধ বিচরণ যেন তোমাদের বিভ্রান্ত না করে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4138,40,5,'তাদের পূর্বে নূহের সম্প্রদায় এবং তাদের পরে অন্যান্য দলও মিথ্যা আরোপ করেছিল। প্রত্যেক সম্প্রদায় নিজ নিজ রাসূলকে আবদ্ধ করার জন্য অভিসন্ধি করেছিল এবং তারা অসার তর্কে লিপ্ত হয়েছিল, সত্যকে ব্যর্থ করে দেয়ার জন্য। ফলে আমি তাদেরকে পাকড়াও করলাম এবং কত কঠোর ছিল আমার শাস্তি!');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4139,40,6,'এভাবে কাফিরদের ক্ষেত্রে সত্য হল তোমার রবের বাণী - এরা জাহান্নামী। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4140,40,7,'যারা আরশ ধারণ করে আছে এবং যারা এর চতুস্পার্শ্ব ঘিরে আছে তারা তাদের রবের পবিত্রতা ও মহিমা ঘোষণা করে প্রশংসার সাথে এবং তাতে বিশ্বাস স্থাপন করে এবং মু’মিনদের জন্য ক্ষমা প্রার্থনা করে বলেঃ হে আমাদের রাব্ব! আপনার দয়া ও জ্ঞান সর্বব্যাপী, অতএব যারা তাওবাহ করে ও আপনার পথ অবলম্বন করে, আপনি তাদেরকে ক্ষমা করুন এবং জাহান্নামের শাস্তি হতে রক্ষা করুন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4141,40,8,'হে আমাদের রাব্ব! আপনি তাদেরকে দাখিল করুন স্থায়ী জান্নাতে, যার প্রতিশ্রুতি আপনি তাদেরকে দিয়েছেন এবং তাদের মাতা-পিতা, পতি-পত্নী ও সন্তান-সন্ততির মধ্যে যারা সৎ কাজ করেছে তাদেরকেও। আপনিতো পরাক্রমশালী, প্রজ্ঞাময়। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4142,40,9,'এবং আপনি তাদেরকে পাপ হতে রক্ষা করুন, সেই দিন আপনি যাকে শাস্তি হতে রক্ষা করবেন তাকেতো অনুগ্রহই করবেন, এটাইতো মহাসাফল্য।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4143,40,10,'কাফিরদেরকে উচ্চ কন্ঠে বলা হবেঃ তোমাদের নিজেদের প্রতি তোমাদের ক্ষোভ অপেক্ষা আল্লাহর অপ্রসন্নতা ছিল অধিক, যখন তোমাদের ঈমানের প্রতি আহবান করা হয়েছিল, আর তোমরা তা অস্বীকার করেছিলে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4144,40,11,'তারা বলবেঃ হে আমাদের রাব্ব! আপনি আমাদেরকে প্রাণহীন অবস্থায় দু’বার রেখেছেন এবং দু’বার আমাদেরকে প্রাণ দিয়েছেন। আমরা আমাদের অপরাধ স্বীকার করছি; এখন নিস্ক্রমণের কোন পথ মিলবে কি?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4145,40,12,'তোমাদের এই পার্থিব শাস্তি এ জন্য যে, যখন এক আল্লাহকে ডাকা হত তখন তোমরা তাঁকে অস্বীকার করতে এবং আল্লাহর শরীক স্থির করা হলে তোমরা তা বিশ্বাস করতে। বস্তুতঃ মহান আল্লাহরই সমস্ত কর্তৃত্ব। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4146,40,13,'তিনিই তোমাদেরকে তাঁর নিদর্শনাবলী দেখান এবং আকাশ হতে প্রেরণ করেন তোমাদের জন্য রিযক। আল্লাহর অভিমুখী ব্যক্তিই উপদেশ গ্রহণ করে থাকে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4147,40,14,'সুতরাং আল্লাহকে ডাক তাঁর আনুগত্যে একনিষ্ঠ হয়ে, যদিও কাফিরেরা এটা অপছন্দ করে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4148,40,15,'তিনি সুউচ্চ মর্যাদার অধিকারী, আরশের অধিপতি, তিনি তাঁর বান্দাদের মধ্যে যার প্রতি ইচ্ছা অহী প্রেরণ করেন স্বীয় আদেশসহ যাতে সে সতর্ক করতে পারে কিয়ামাতের দিন সম্পর্কে –');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4149,40,16,'যেদিন মানুষ বের হয়ে পড়বে। সেদিন আল্লাহর নিকট তাদের কিছুই গোপন থাকবেনা। ঐ দিন কর্তৃত্ব কার? এক, পরাক্রমশালী আল্লাহরই। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4150,40,17,'এদিন প্রত্যেককে তার কৃতকর্মের ফল দেয়া হবে; কারও প্রতি যুলম করা হবে না। নিশ্চয়ই আল্লাহ হিসাব গ্রহণে তৎপর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4151,40,18,'তাদেরকে সতর্ক করে দাও আসন্ন দিন সম্পর্কে, যখন দুঃখ কষ্টে তাদের প্রাণ কন্ঠাগত হবে। যালিমদের জন্য কোন অন্তরঙ্গ বন্ধু নেই, যার সুপারিশ গ্রাহ্য হবে এমন কোন সুপারিশকারীও নেই। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4152,40,19,'চক্ষুর অপব্যবহার ও অন্তরে যা গোপন আছে সেই সম্বন্ধে তিনি অবহিত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4153,40,20,'আল্লাহ বিচার করেন সঠিকভাবে; আল্লাহর পরিবর্তে তারা যাদেরকে ডাকে তারা বিচার করতে অক্ষম। আল্লাহ সর্বশ্রোতা, সর্বদ্রষ্টা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4154,40,21,'তারা কি পৃথিবীতে ভ্রমণ করেনা? তাহলে দেখতে পেত তাদের পূর্ববর্তীদের পরিণাম কি হয়েছিল? পৃথিবীতে তারা ছিল এদের অপেক্ষা শক্তিতে এবং কীর্তিতে প্রবলতর। অতঃপর আল্লাহ তাদেরকে শাস্তি দিয়েছিলেন তাদের অপরাধের জন্য এবং আল্লাহর শাস্তি হতে তাদেরকে রক্ষা করার কেহ ছিলনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4155,40,22,'এটা এ জন্য যে, তাদের নিকট তাদের রাসূলগণ নিদর্শনসহ এলে তারা তাদেরকে প্রত্যাখ্যান করেছিল। ফলে আল্লাহ তাদেরকে শাস্তি দিলেন। তিনিতো শক্তিশালী, শাস্তি দানে কঠোর। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4156,40,23,'আমি আমার নিদর্শন ও স্পষ্ট প্রমাণসহ মূসাকে প্রেরণ করেছিলাম – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4157,40,24,'ফির‘আউন, হামান ও কারূণের নিকট; কিন্তু তারা বলেছিলঃ এতো এক যাদুকর, চরম মিথ্যাবাদী। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4158,40,25,'অতঃপর যখন মূসা আমার নিকট হতে সত্য নিয়ে তাদের নিকট উপস্থিত হল তখন তারা বললঃ মূসার উপর যারা ঈমান এনেছে তাদের পুত্র সন্তানদেরকে হত্যা কর এবং নারীদেরকে জীবিত রাখ। কিন্তু কাফিরদের ষড়যন্ত্র ব্যর্থ হবেই। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4159,40,26,'ফির‘আউন বললঃ আমাকে ছেড়ে দাও, আমি মূসাকে হত্যা করি এবং সে তার রবের শরণাপন্ন হোক। আমি আশংকা করি যে, সে তোমাদের দীনের পরিবর্তন ঘটাবে অথবা সে পৃথিবীতে বিপর্যয় সৃষ্টি করবে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4160,40,27,'মূসা বললঃ যারা বিচার দিনে বিশ্বাস করেনা, সেই সব উদ্ধত ব্যক্তি হতে আমি আমার ও তোমাদের রবের শরণাপন্ন হচ্ছি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4161,40,28,'ফির‘আউন বংশের এক ব্যক্তি যে মু’মিন ছিল এবং নিজ ঈমান গোপন রাখত, বললঃ তোমরা কি এক ব্যক্তিকে এ জন্য হত্যা করবে যে, সে বলেঃ আমার রাব্ব আল্লাহ, অথচ সে তোমাদের রবের নিকট হতে সুস্পষ্ট প্রমাণসহ তোমাদের নিকট এসেছে? সে মিথ্যাবাদী হলে তার মিথ্যাবাদিতার জন্য সে দায়ী হবে, আর যদি সে সত্যবাদী হয় তাহলে সে তোমাদেরকে যে শাস্তির কথা বলে তার কিছুতো তোমাদের উপর আপতিত হবেই। নিশ্চয়ই সীমা লংঘনকারী ও মিথ্যাবাদীকে আল্লাহ সৎ পথে পরিচালিত করেননা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4162,40,29,'হে আমার সম্প্রদায়! আজ কর্তৃত্ব তোমাদের, দেশে তোমরাই প্রবল; কিন্তু আমাদের উপর আল্লাহর শাস্তি এসে পড়লে কে আমাদেরকে সাহায্য করবে? ফির‘আউন বললঃ আমি যা বুঝি, আমি তোমাদের তাই বলছি। আমি তোমাদেরকে শুধু সৎ পথই দেখিয়ে থাকি। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4163,40,30,'মু’মিন ব্যক্তিটি বললঃ হে আমার সম্প্রদায়! আমি তোমাদের জন্য পূর্ববর্তী সম্প্রদায়সমূহের শাস্তির দিনের অনুরূপ দুর্দিনের আশংকা করি – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4164,40,31,'যেমন ঘটেছিল নূহের কাওম, আদ, সামূদ এবং তাদের পরবর্তীদের ক্ষেত্রে। আল্লাহতো বান্দাদের প্রতি কোন যুলম করতে চান না। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4165,40,32,'হে আমার সম্প্রদায়! আমি তোমাদের জন্য আশংকা করি কিয়ামাত দিবসের – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4166,40,33,'যেদিন তোমরা পশ্চাৎ ফিরে পলায়ন করতে চাবে, আল্লাহর শাস্তি হতে তোমাদেরকে রক্ষা করার কেহ থাকবেনা। আল্লাহ যাকে পথভ্রষ্ট করেন তার জন্য কোন পথ প্রদর্শক নেই। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4167,40,34,'পূর্বেও তোমাদের নিকট ইউসুফ এসেছিল স্পষ্ট নিদর্শনসহ; কিন্তু সে যা নিয়ে এসেছিল তোমরা তাতে বার বার সন্দেহ পোষণ করতে। পরিশেষে যখন তার মৃত্যু হল তখন তোমরা বলেছিলেঃ অতঃপর আল্লাহ আর কেহকেও রাসূল করে প্রেরণ করবেন না। এভাবে আল্লাহ বিভ্রান্ত করেন সীমালংঘনকারী ও সংশয়বাদীদেরকে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4168,40,35,'যারা নিজেদের নিকট কোন দলীল প্রমাণ না থাকলেও আল্লাহর নিদর্শন সম্পর্কে বিতন্ডায় লিপ্ত হয় তাদের এ কাজ আল্লাহ এবং মু’মিনদের দৃষ্টিতে অতিশয় ঘৃণাহ। এভাবে আল্লাহ প্রত্যেক উদ্ধত ও স্বৈরাচারী ব্যক্তির হৃদয়কে মোহর করে দেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4169,40,36,'ফির‘আউন বললঃ হে হামান! আমার জন্য তুমি নির্মাণ কর এক সুউচ্চ প্রাসাদ যাতে আমি পাই অবলম্বন –');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4170,40,37,'আসমানে আরোহনের অবলম্বন, যেন আমি দেখতে পাই মূসার মা‘বূদকে; তবে আমিতো তাকে মিথ্যাবাদী মনে করি। এভাবেই ফির‘আউনের নিকট শোভনীয় করা হয়েছিল তার মন্দ কাজকে এবং তাকে নিবৃত্ত করা হয়েছিল সরল পথ হতে এবং ফির‘আউনের ষড়যন্ত্র ব্যর্থ হয়েছিল সম্পূর্ণ রূপে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4171,40,38,'মু’মিন ব্যক্তিটি বললঃ হে আমার সম্প্রদায়! তোমরা আমার অনুসরণ কর, আমি তোমাদেরকে সঠিক পথে পরিচালিত করব। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4172,40,39,'হে আমার সম্প্রদায়! এ পার্থিব জীবনতো অস্থায়ী উপভোগের বস্তু এবং আখিরাতই হচ্ছে চিরস্থায়ী আবাস।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4173,40,40,'কেহ মন্দ কাজ করলে সে শুধু তার কাজের অনুরূপ শাস্তি পাবে এবং পুরুষ কিংবা নারীর মধ্যে যারা মু’মিন হয়ে সৎ কাজ করে তারা দাখিল হবে জান্নাতে, সেখানে তাদেরকে দেয়া হবে অপরিমিত জীবনোপকরণ। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4174,40,41,'হে আমার সম্প্রদায়! কি আশ্চর্য, আমি তোমাদেরকে আহবান করছি মুক্তির দিকে, আর তোমরা আমাকে আহবান করছ জাহান্নামের দিকে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4175,40,42,'তোমরা আমাকে বলছ আল্লাহকে অস্বীকার করতে এবং তাঁর সমকক্ষ দাঁড় করাতে, যার সম্পর্কে আমার কোন জ্ঞান নেই; পক্ষান্তরে আমি তোমাদেরকে আহবান করছি পরাক্রমশালী, ক্ষমাশীল আল্লাহর দিকে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4176,40,43,'নিশ্চয়ই তোমরা আমাকে আহবান করছো এমন এক জনের দিকে যে দুনিয়া ও আখিরাতে কোথাও আহবানযোগ্য নয়। বস্তুতঃ আমাদের প্রত্যাবর্তনতো আল্লাহর নিকট এবং সীমালংঘনকারীরাই জাহান্নামের অধিবাসী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4177,40,44,'আমি তোমাদেরকে যা বলছি, তোমরা তা অচিরেই স্মরণ করবে এবং আমি আমার ব্যাপার আল্লাহয় অপর্ণ করছি; আল্লাহ তাঁর বান্দাদের প্রতি সবিশেষ দৃষ্টি রাখেন। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4178,40,45,'অতঃপর আল্লাহ তাকে তাদের ষড়যন্ত্রের অনিষ্টতা হতে রক্ষা করলেন এবং কঠিন শাস্তি পরিবেষ্টন করল ফির‘আউন সম্প্রদায়কে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4179,40,46,'সকাল-সন্ধ্যায় তাদেরকে উপস্থিত করা হয় আগুনের সম্মুখে এবং যেদিন কিয়ামাত সংঘটিত হবে সেদিন বলা হবেঃ ফির‘আউন সম্প্রদায়কে নিক্ষেপ কর কঠিনতম শাস্তিতে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4180,40,47,'যখন তারা জাহান্নামে পরস্পর বিতর্কে লিপ্ত হবে তখন দুর্বলেরা দাম্ভিকদেরকে বলবেঃ আমরাতো তোমাদের অনুসারী ছিলাম, এখন কি তোমরা আমাদের থেকে জাহান্নামের আগুনের কিয়দংশ নিবারণ করবে?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4181,40,48,'দাম্ভিকেরা বলবেঃ আমরা সবাইতো জাহান্নামে আছি, নিশ্চয়ই আল্লাহ বান্দাদের বিচার করে ফেলেছেন। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4182,40,49,'জাহান্নামীরা ওর প্রহরীদেরকে বলবেঃ তোমাদের রবের নিকট প্রার্থনা কর, তিনি যেন আমাদের হতে লাঘব করেন শাস্তি, এক দিনের জন্য।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4183,40,50,'তারা বলবেঃ তোমাদের নিকট কি স্পষ্ট নিদর্শনসহ তোমাদের রাসূলগণ আসেননি? জাহান্নামীরা বলবেঃ অবশ্যই এসেছিল। প্রহরীরা বলবেঃ তাহলে তোমরাই প্রার্থনা কর, আর কাফিরদের প্রার্থনা ব্যর্থই হয়। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4184,40,51,'নিশ্চয়ই আমি আমার রাসূলদেরকে ও মু’মিনদেরকে সাহায্য করব পার্থিব জীবনে এবং যেদিন সাক্ষীগণ দন্ডায়মান হবে,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4185,40,52,'যেদিন যালিমদের কোনো ওযর আপত্তি কোন কাজে আসবেনা, তাদের জন্য রয়েছে লা’নত এবং নিকৃষ্ট আবাস। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4186,40,53,'আমি অবশ্যই মূসাকে দান করেছিলাম পথনির্দেশ এবং বানী ইসরাঈলকে উত্তরাধিকারী করেছিলাম সেই কিতাবের –');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4187,40,54,'পথনির্দেশ ও উপদেশ স্বরূপ; বোধশক্তি সম্পন্ন লোকদের জন্য। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4188,40,55,'অতএব তুমি ধৈর্য ধারণ কর; নিশ্চয়ই আল্লাহর প্রতিশ্রুতি সত্য, তুমি তোমার ত্রুটির জন্য ক্ষমা প্রার্থনা কর এবং সকাল-সন্ধ্যায় তোমার রবের পবিত্রতা ও মহিমা ঘোষণা কর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4189,40,56,'যারা নিজেদের নিকট কোন দলীল না থাকলেও আল্লাহর নিদর্শন সম্পর্কে বিতর্কে লিপ্ত হয় তাদের অন্তরে আছে শুধু অহংকার, যা সফল হবার নয়। অতএব আল্লাহর শরণাপন্ন হও, তিনিতো সর্বশ্রোতা, সর্বদ্রষ্টা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4190,40,57,'মানব সৃষ্টি অপেক্ষা আকাশমন্ডলী ও পৃথিবীর সৃষ্টিতো কঠিনতর, কিন্তু অধিকাংশ মানুষ এটা জানেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4191,40,58,'সমান নয় অন্ধ ও চক্ষুম্মান এবং যারা ঈমান আনে ও সৎ কাজ করে, আর যারা দুস্কৃতিপরায়ণ। তোমরা অল্পই উপদেশ গ্রহণ করে থাক। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4192,40,59,'কিয়ামাত অবশ্যম্ভাবী, এতে কোন সন্দেহ নেই, কিন্তু অধিকাংশ লোক বিশ্বাস করেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4193,40,60,'তোমাদের রাব্ব বললেনঃ তোমরা আমাকে ডাক, আমি তোমাদের ডাকে সাড়া দিব। কিন্তু যারা অহংকারে আমার ইবাদাতে বিমুখ তারা অবশ্যই জাহান্নামে প্রবেশ করবে লাঞ্ছিত হয়ে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4194,40,61,'আল্লাহই তোমাদের বিশ্রামের জন্য সৃষ্টি করেছেন রাত এবং আলোকজ্জ্বল করেছেন দিনকে। আল্লাহতো মানুষের প্রতি অনুগ্রহশীল, কিন্তু অধিকাংশ মানুষ কৃতজ্ঞতা প্রকাশ করেনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4195,40,62,'তিনিই আল্লাহ তোমাদের রাব্ব, সব কিছুর স্রষ্টা; তিনি ব্যতীত কোন মা‘বূদ নেই; সুতরাং তোমরা কিভাবে বিপথগামী হচ্ছ? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4196,40,63,'এভাবেই বিপথগামী হয় তারা যারা আল্লাহর নিদর্শনাবলিকে অস্বীকার করে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4197,40,64,'আল্লাহই তোমাদের জন্য পৃথিবীকে করেছেন বাসোপযোগী এবং আকাশকে করেছেন ছাদ এবং তোমাদের আকৃতি করেছেন উৎকৃষ্ট এবং তোমাদেরকে দান করেছেন উৎকৃষ্ট রিয্ক। এইতো আল্লাহ, তোমাদের রাব্ব। কত মহান জগতসমূহের রাব্ব আল্লাহ! ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4198,40,65,'তিনি চিরঞ্জীব, তিনি ব্যতীত কোন মা‘বূদ নেই। সুতরাং তোমরা তাঁকেই ডাক, তাঁর আনুগত্যে একনিষ্ঠ হয়ে। প্রশংসা জগতসমূহের রাব্ব আল্লাহর প্রাপ্য। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4199,40,66,'বলঃ আমার রবের নিকট হতে আমার নিকট সুস্পষ্ট নিদর্শন আসার পূর্বে তোমরা আল্লাহ ব্যতীত যাদেরকে আহবান কর তাদের ইবাদাত করতে আমাকে নিষেধ করা হয়েছে এবং আমি আদিষ্ট হয়েছি জগতসমূহের রবের নিকট আত্মসমর্পণ করতে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4200,40,67,'তিনিই তোমাদের সৃষ্টি করেছেন মাটি হতে, পরে শুক্র বিন্দু হতে, তারপর তাদেরকে বের করেন শিশু রূপে, অতঃপর তোমরা উপনীত হও যৌবনে, তারপর হও বৃদ্ধ। তোমাদের মধ্যে কারও এর পূর্বেই মৃত্যু ঘটে এবং এটা এ জন্য যে, তোমরা নির্ধারিত কাল প্রাপ্ত হও এবং যাতে তোমরা অনুধাবন করতে পার – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4201,40,68,'তিনিই জীবন দান করেন ও মৃত্যু ঘটান এবং যখন তিনি কিছু করা স্থির করেন তখন তিনি বলেনঃ হও, এবং তা হয়ে যায়। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4202,40,69,'তুমি কি লক্ষ্য করনা তাদের প্রতি যারা আল্লাহর নিদর্শন সম্পর্কে বিতর্ক করে? কিভাবে তাদেরকে বিপথগামী করা হচ্ছে?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4203,40,70,'যারা অস্বীকার করে কিতাব ও যা সহ আমি রাসূলদেরকে প্রেরণ করেছিলাম তা, শীঘ্রই তারা জানতে পারবে – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4204,40,71,'যখন তাদের গলদেশে বেড়ি ও শৃংখল পড়িয়ে টেনে নিয়ে যাওয়া হবে –');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4205,40,72,'ফুটন্ত পানিতে। অতঃপর তাদেরকে দগ্ধ করা হবে আগুনে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4206,40,73,'এরপর তাদেরকে বলা হবেঃ কোথায় তারা যাদেরকে তোমরা তাঁর শরীক করতে –');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4207,40,74,'আল্লাহ ব্যতীত? তারা বলবেঃ তারাতো আমাদের নিকট হতে অদৃশ্য হয়েছে, বস্তুতঃ পূর্বে আমরা এমন কিছুকেই আহবান করিনি। এভাবে আল্লাহ কাফিরদের বিভ্রান্ত করেন। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4208,40,75,'এটা এ কারণে যে, তোমরা পৃথিবীতে অযথা উল্লাস করতে এবং এ কারণে যে, তোমরা দম্ভ করতে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4209,40,76,'তোমরা জাহান্নামের বিভিন্ন দরজা দিয়ে প্রবেশ কর তাতে স্থায়ীভাবে অবস্থিতির জন্য, আর কতই না নিকৃষ্ট উদ্ধতদের আবাসস্থল!');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4210,40,77,'সুতরাং তুমি ধৈর্য ধারণ কর। আল্লাহর প্রতিশ্রুতি সত্য। আমি তাদেরকে যে প্রতিশ্রুতি প্রদান করি তার কিছু যদি দেখিয়েই দিই অথবা তোমার মৃত্যু ঘটাই - তাদের প্রত্যাবর্তনতো আমারই নিকট।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4211,40,78,'আমিতো তোমার পূর্বে অনেক রাসূল প্রেরণ করেছিলাম; তাদের কারও কারও কথা তোমার নিকট বিবৃত করেছি এবং কারও কারও কথা তোমার নিকট বিবৃত করিনি। আল্লাহর অনুমতি ছাড়া কোন নিদর্শন উপস্থিত করা কোন রাসূলের কাজ নয়। আল্লাহর আদেশ এলে, ন্যায় সংগতভাবে ফাইসালা হয়ে যাবে। তখন মিথ্যাশ্রয়ীরা ক্ষতিগ্রস্ত হবে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4212,40,79,'আল্লাহই তোমাদের জন্য চতুস্পদ জন্তু সৃষ্টি করেছেন, কতক আরোহণ করার জন্য এবং কতক তোমরা আহার করে থাক।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4213,40,80,'এতে তোমাদের জন্য রয়েছে প্রচুর উপকার; তোমরা যা প্রয়োজন বোধ কর এটা দ্বারা তা পূর্ণ করে থাক এবং এদের উপর ও নৌযানের উপর তোমাদেরকে বহন করা হয়। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4214,40,81,'তিনি তোমাদেরকে তাঁর নিদর্শনাবলী দেখিয়ে থাকেন। সুতরাং তোমরা আল্লাহর কোন্ কোন্ নি‘আমাত অস্বীকার করবে?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4215,40,82,'তারা কি পৃথিবীতে ভ্রমণ করেনি ও দেখেনি তাদের পূর্ববর্তীদের কি পরিণাম হয়েছিল? পৃথিবীতে তারা ছিল এদের অপেক্ষা সংখ্যায় অধিক এবং শক্তিতে ও কীর্তিতে অধিক প্রবল। তারা যা করত তা তাদের কোন কাজে আসেনি। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4216,40,83,'তাদের নিকট যখন স্পষ্ট নিদর্শনসহ তাদের রাসূল আসত তখন তারা নিজেদের জ্ঞানের দম্ভ করত। তারা যা নিয়ে ঠাট্টা বিদ্রুপ করত তাই তাদেরকে বেষ্টন করল। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4217,40,84,'অতঃপর যখন তারা আমার শাস্তি প্রত্যক্ষ করল তখন বললঃ আমরা এক আল্লাহয়ই ঈমান আনলাম এবং আমরা তাঁর সাথে যাদেরকে শরীক করতাম তাদেরকে প্রত্যাখ্যান করলাম। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4218,40,85,'তারা যখন আমার শাস্তি প্রত্যক্ষ করল তখন তাদের ঈমান তাদের কোন উপকারে এলোনা। আল্লাহর এই বিধান পূর্ব হতেই তার বান্দাদের মধ্যে চলে আসছে এবং সেই ক্ষেত্রে কাফিরেরা ক্ষতিগ্রস্ত হয়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4219,41,1,'হা মীম। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4220,41,2,'ইহা দয়াময়, পরম দয়ালুর নিকট হতে অবতীর্ণ। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4221,41,3,'এটা এক কিতাব, বিশদভাবে বিবৃত হয়েছে এর আয়াতসমূহ আরাবী ভাষায়, কুরআনরূপে জ্ঞানী সম্প্রদায়ের জন্য – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4222,41,4,'সুসংবাদ দাতা ও সতর্ককারীরূপে, কিন্তু তাদের অধিকাংশই বিমুখ হয়েছে। সুতরাং তারা শুনবে না। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4223,41,5,'তারা বলেঃ তুমি যার প্রতি আমাদেরকে আহবান করছ সেই বিষয়ে আমাদের অন্তর আবরণে আচ্ছাদিত, কর্ণে আছে বধিরতা এবং তোমার ও আমাদের মধ্যে আছে অন্তরাল; সুতরাং তুমি তোমার কাজ কর এবং আমরা আমাদের কাজ করি। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4224,41,6,'বলঃ আমিতো তোমাদের মতই একজন মানুষ, আমার প্রতি অহী হয় যে, তোমাদের মা‘বূদ একমাত্র মা‘বূদ। অতএব তাঁরই পথ দৃঢ়ভাবে অবলম্বন কর এবং তাঁরই নিকট ক্ষমা প্রার্থনা কর। দুর্ভোগ অংশীবাদীদের জন্য –');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4225,41,7,'যারা যাকাত প্রদান করেনা এবং তারা আখিরাতেও অবিশ্বাসী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4226,41,8,'যারা ঈমান আনে ও সৎ কাজ করে তাদের জন্য রয়েছে নিরবচ্ছিন্ন পুরস্কার। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4227,41,9,'বলঃ তোমরা কি তাকে অস্বীকার করবেই যিনি পৃথিবী সৃষ্টি করেছেন দুই দিনে এবং তোমরা তাঁর সমকক্ষ দাঁড় করাতে চাও? তিনিতো জগতসমূহের রাব্ব। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4228,41,10,'তিনি স্থাপন করেছেন অটল পর্বতমালা ভূপৃষ্ঠে এবং তাতে রেখেছেন কল্যাণ এবং চার দিনে ব্যবস্থা করেছেন খাদ্যের - সমভাবে, যাঞ্চাকারীদের জন্য।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4229,41,11,'অতঃপর তিনি আকাশের দিকে মনোনিবেশ করেন যা ছিল ধূম্রপুঞ্জ বিশেষ। অতঃপর তিনি ওটাকে এবং পৃথিবীকে বললেনঃ তোমরা উভয়ে এসো স্বেচ্ছায় অথবা অনিচ্ছায়। তারা বললঃ আমরা এলাম অনুগত হয়ে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4230,41,12,'অতঃপর তিনি আকাশমন্ডলীকে দুই দিনে সপ্তাকাশে পরিণত করলেন এবং প্রত্যেক আকাশে উহার বিধান ব্যক্ত করলেন এবং আমি নিকটবর্তী আকাশকে সুশোভিত করলাম প্রদীপমালা দ্বারা এবং করলাম সুরক্ষিত। এটা পরাক্রমশালী সর্বজ্ঞ আল্লাহর ব্যবস্থাপনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4231,41,13,'তবুও তারা যদি মুখ ফিরিয়ে নেয় তাহলে বলঃ আমিতো তোমাদেরকে সতর্ক করছি এক ধ্বংসকর শাস্তির; আদ ও সামূদ জাতির অনুরূপ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4232,41,14,'যখন তাদের নিকট রাসূলগণ এসেছিল তাদের সম্মুখ ও পশ্চাৎ হতে এবং বলেছিলঃ তোমরা আল্লাহ ব্যতীত কারও ইবাদাত করনা তখন তারা বলেছিলঃ আমাদের রবের এই রূপ ইচ্ছা হলে তিনি অবশ্যই মালাক/ফেরেশতা প্রেরণ করতেন। অতএব তোমরা যাসহ প্রেরিত হয়েছ আমরা তা প্রত্যাখ্যান করছি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4233,41,15,'আর ‘আদ সম্প্রদায়ের ব্যাপার এই যে, তারা পৃথিবীতে অযথা দম্ভ করত এবং বলতঃ আমাদের অপেক্ষা শক্তিশালী কে আছে? তারা কি তাহলে লক্ষ্য করেনি, যে আল্লাহ তাদেরকে সৃষ্টি করেছেন তিনি তাদের অপেক্ষা শক্তিশালী? অথচ তারা আমার নিদর্শনাবলীকে অস্বীকার করত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4234,41,16,'অতঃপর আমি তাদেরকে পার্থিব জীবনে লাঞ্ছনাদায়ক শাস্তি আস্বাদন করার জন্য তাদের বিরূদ্ধে প্রেরণ করেছিলাম ঝঞ্ঝা-বায়ু, অমঙ্গলজনক দিনে। আখিরাতের শাস্তিতো অধিকতর লাঞ্ছনাদায়ক এবং তাদের সাহায্য করা হবেনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4235,41,17,'আর সামূদ সম্প্রদায়ের ব্যাপার এই যে, আমি তাদেরকে পথ নির্দেশ করেছিলাম, কিন্তু তারা সৎ পথের পরিবর্তে ভ্রান্ত পথ অবলম্বন করেছিল। অতঃপর তাদেরকে লাঞ্ছনাদায়ক শাস্তি আঘাত হানলো তাদের কৃতকর্মের পরিণাম স্বরূপ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4236,41,18,'আমি উদ্ধার করলাম তাদেরকে যারা ঈমান এনেছিল এবং যারা তাকওয়া অবলম্বন করত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4237,41,19,'যেদিন আল্লাহর শত্রুদেরকে জাহান্নাম অভিমুখে সমবেত করা হবে সেদিন তাদেরকে বিন্যস্ত করা হবে বিভিন্ন দলে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4238,41,20,'পরিশেষে যখন তারা জাহান্নামের সন্নিকটে পৌঁছবে তখন তাদের কান, চোখ ও ত্বক তাদের কৃতকর্ম সম্পর্কে সাক্ষ্য দিবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4239,41,21,'জাহান্নামীরা তাদের ত্বককে জিজ্ঞেস করবেঃ তোমরা আমাদের বিরুদ্ধে সাক্ষ্য দিচ্ছ কেন? উত্তরে তারা বলবেঃ আল্লাহ! যিনি সব কিছুকে বাকশক্তি দিয়েছেন তিনি আমাদেরকেও বাকশক্তি দিয়েছেন। তিনি তোমাদেরকে সৃষ্টি করেছেন প্রথমবার এবং তাঁরই নিকট তোমরা প্রত্যাবর্তিত হবে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4240,41,22,'তোমরা কিছু গোপন করতে না এই বিশ্বাসে যে, তোমাদের কর্ণ, চক্ষু এবং ত্বক তোমাদের বিরুদ্ধে সাক্ষ্য দিবেনা; উপরন্ত তোমরা মনে করতে যে, তোমরা যা করতে তার অনেক কিছুই আল্লাহ জানেননা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4241,41,23,'তোমাদের রাব্ব সম্বন্ধে তোমাদের এই ধারণাই তোমাদের ধ্বংস এনেছে। ফলে তোমরা হয়েছ ক্ষতিগ্রস্ত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4242,41,24,'এখন তারা ধৈর্য ধারণ করলেও জাহান্নামই হবে তাদের আবাস স্থল এবং তারা অনুগ্রহ চাইলেও অনুগ্রহ প্রাপ্ত হবেনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4243,41,25,'আমি তাদের জন্য নির্ধারণ করে দিয়েছিলাম সহচর যারা তাদের সম্মুখ ও পশ্চাতে যা আছে তা তাদের দৃষ্টিতে শোভন করে দেখিয়েছিল এবং তাদের ব্যাপারেও তাদের পূর্ববর্তী জিন ও মানবদের ন্যায় শাস্তির কথা বাস্তব হয়েছে। তারাতো ছিল ক্ষতিগ্রস্ত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4244,41,26,'কাফিরেরা বলেঃ তোমরা এই কুরআন শ্রবণ করনা এবং তা আবৃত্তি কালে শোরগোল সৃষ্টি কর যাতে তোমরা জয়ী হতে পার। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4245,41,27,'আমি অবশ্যই কাফিরদের কঠিন শাস্তি আস্বাদন করাব এবং নিশ্চয়ই আমি তাদেরকে নিকৃষ্ট কার্যকলাপের প্রতিফল দিব – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4246,41,28,'জাহান্নাম; এটাই আল্লাহর শত্রুদের পরিণাম; সেখানে তাদের জন্য রয়েছে স্থায়ী আবাস, আমার নিদর্শনাবলী অস্বীকৃতির প্রতিফল স্বরূপ। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4247,41,29,'কাফিরেরা বলবেঃ হে আমাদের রাব্ব! যে সব জিন ও মানুষ আমাদেরকে পথভ্রষ্ট করেছিল তাদের উভয়কে দেখিয়ে দিন, আমরা উভয়কে পদদলিত করব, যাতে তারা লাঞ্ছিত হয়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4248,41,30,'যারা বলেঃ আমাদের রাব্ব আল্লাহ! অতঃপর অবিচল থাকে, তাদের নিকট অবতীর্ণ হয় মালাইকা (ফেরেশতা) এবং বলেঃ তোমরা ভীত হয়োনা, চিন্তিত হয়োনা এবং তোমাদেরকে যে জান্নাতের প্রতিশ্রুতি দেয়া হয়েছিল তার জন্য আনন্দিত হও। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4249,41,31,'আমরাই তোমাদের বন্ধু - দুনিয়ার জীবনে ও আখিরাতে। সেখানে তোমাদের জন্য রয়েছে যা কিছু তোমাদের মন চায় এবং সেখানে তোমাদের জন্য রয়েছে যা তোমরা ফরমায়েস কর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4250,41,32,'এটা হবে ক্ষমাশীল, পরম দয়ালু আল্লাহর পক্ষ হতে আপ্যায়ন। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4251,41,33,'ঐ ব্যক্তি অপেক্ষা কথায় কে উত্তম যে আল্লাহর প্রতি মানুষকে আহবান করে, সৎ কাজ করে এবং বলেঃ আমিতো আত্মসমর্পনকারীদের অন্তর্ভুক্ত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4252,41,34,'ভাল কাজ এবং মন্দ কাজ সমান হতে পারেনা। মন্দ প্রতিহত কর উৎকৃষ্ট দ্বারা; ফলে তোমার সাথে যার শত্রুতা আছে সে হয়ে যাবে অন্তরঙ্গ বন্ধুর মত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4253,41,35,'এই গুণের অধিকারী করা হয় শুধু তাদেরকেই যারা ধৈর্যশীল, এই গুণের অধিকারী করা হয় শুধু তাদেরকেই যারা মহা ভাগ্যবান।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4254,41,36,'যদি শাইতানের কু-মন্ত্রণা তোমাকে প্ররোচিত করে তাহলে আল্লাহকে স্মরণ করবে; তিনি সর্বশ্রোতা, সর্বজ্ঞ। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4255,41,37,'তাঁর নিদর্শনাবলীর মধ্যে রয়েছে রাত ও দিন, সূর্য ও চাঁদ। তোমরা সূর্যকে সাজদাহ করনা, চাঁদকেও নয়; সাজদাহ কর আল্লাহকে, যিনি এগুলি সৃষ্টি করেছেন, যদি তোমরা প্রকৃতই তাঁর ইবাদাত কর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4256,41,38,'তারা অহংকার করলেও যারা তোমার রবের সান্নিধ্যে রয়েছে তারাতো দিন ও রাতে তাঁর পবিত্রতা ও মহিমা ঘোষণা করে এবং তারা ক্লান্তিও বোধ করেনা। [সাজদাহ]');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4257,41,39,'আর তাঁর একটি নিদর্শন এই যে, তুমি ভূমিকে দেখতে পাও শুষ্ক উষর, অতঃপর আমি তাতে বৃষ্টি বর্ষণ করলে তা আন্দোলিত ও স্ফীত হয়; যিনি জীব দেন তিনিই মৃতের জীবন দানকারী। তিনিতো সর্ব বিষয়ে সর্ব শক্তিমান। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4258,41,40,'যারা আমার আয়াতসমূহকে বিকৃত করে তারা আমার অগোচর নয়। শ্রেষ্ঠ কে? যে ব্যক্তি জাহান্নামে নিক্ষিপ্ত হবে সে, নাকি যে কিয়ামাত দিবসে নিরাপদে থাকবে সে? তোমাদের যা ইচ্ছা তা কর; তোমরা যা কর তিনি তার দ্রষ্টা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4259,41,41,'যারা তাদের নিকট কুরআন আসার পর তা প্রত্যাখ্যান করে তাদের মধ্যে চিন্তা ভাবনার অভাব রয়েছে। ইহা অবশ্যই এক মহিমাময় গ্রন্থ। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4260,41,42,'কোন মিথ্যা এতে অনুপ্রবেশ করবেনা। সম্মুখ হতেও নয়, পশ্চাৎ হতেও নয়; এটা প্রজ্ঞাময় প্রশংসা আল্লাহর নিকট হতে অবতীর্ণ। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4261,41,43,'তোমার সম্বন্ধেতো তাই বলা হয় যা বলা হত তোমার পূর্ববর্তী রাসূলদের সম্পর্কে। তোমার রাব্ব অবশ্যই ক্ষমাশীল এবং কঠিন শাস্তিদাতা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4262,41,44,'আমি যদি আরাবী ভাষায় কুরআন অবতীর্ণ না করতাম তাহলে তারা অবশ্যই বলত, এর আয়াতগুলি বিশদভাবে বিবৃত হয়নি কেন? কি আশ্চর্য যে, এর ভাষা আজমী, অথচ রাসূল আরাবীয়। বলঃ মু’মিনদের জন্য ইহা (কুরআন) পথ নির্দেশ ও ব্যাধির প্রতিকার। কিন্তু যারা অবিশ্বাসী তাদের কর্ণে রয়েছে বধিরতা এবং কুরআন হবে তাদের জন্য অন্ধত্ব। তারা এমন যে, যেন তাদেরকে আহবান করা হয় বহু দূর হতে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4263,41,45,'আমিতো মূসাকে কিতাব দিয়েছিলাম, অতঃপর এতে মতভেদ ঘটেছিল। তোমার রবের পক্ষ হতে পূর্ব সিদ্ধান্ত না থাকলে তাদের মীমাংসা হয়ে যেত। তারা অবশ্যই এ সম্বন্ধে বিভ্রান্তকর সন্দেহে রয়েছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4264,41,46,'যে সৎ কাজ করে সে নিজের কল্যাণের জন্যই তা করে এবং কেহ মন্দ কাজ করলে ওর প্রতিফল সে’ই ভোগ করবে। তোমার রাব্ব বান্দাদের প্রতি কোন যুলম করেন না। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4265,41,47,'কিয়ামাতের জ্ঞান শুধু আল্লাহর কাছেই ন্যস্ত, তাঁর অজ্ঞাতসারে কোন ফল আবরণ হতে বের হয়না, কোন নারী গর্ভধারণ করেনা এবং সন্তানও প্রসব করেনা। যেদিন আল্লাহ তাদেরকে ডেকে বলবেনঃ আমার শরীকরা কোথায়? তখন তারা বলবেঃ আমরা আপনার নিকট নিবেদন করছি যে, এ ব্যাপারে আমরা কিছুই জানিনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4266,41,48,'পূর্বে তারা যাদেরকে আহবান করত তারা অদৃশ্য হয়ে যাবে এবং অংশীবাদীরা উপলদ্ধি করবে যে, তাদের নিস্কৃতির কোন উপায় নেই। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4267,41,49,'মানুষ ধন-সম্পদ প্রার্থনায় কোন ক্লান্তি বোধ করেনা, কিন্তু যখন তাকে দুঃখ দৈন্য স্পর্শ করে তখন সে অত্যন্ত নিরাশ ও হতাশ হয়ে পড়ে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4268,41,50,'দুঃখ দৈন্য স্পর্শ করার পর যদি তাকে আমি অনুগ্রহের আস্বাদ দিই তখন সে বলেই থাকেঃ এটা আমার প্রাপ্য এবং আমি মনে করিনা যে, কিয়ামাত সংঘটিত হবে; আর আমি যদি আমার রবের নিকট প্রত্যাবর্তিত হইও তাহলে তাঁর নিকটতো আমার জন্য কল্যাণই থাকবে। আমি কাফিরদেরকে তাদের কৃতকর্ম সম্বন্ধে অবশ্যই অবহিত করব এবং তাদেরকে আস্বাদন করাব কঠোর শাস্তি। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4269,41,51,'যখন আমি মানুষের প্রতি অনুগ্রহ করি তখন সে মুখ ফিরিয়ে নেয় ও দূরে সরে যায় এবং তাকে অনিষ্টতা স্পর্শ করলে সে তখন দীর্ঘ প্রার্থনায় রত হয়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4270,41,52,'বলঃ তোমরা ভেবে দেখেছ কি, যদি এই কুরআন আল্লাহর নিকট থেকে অবতীর্ণ হয়ে থাকে এবং তোমরা ইহা প্রত্যাখ্যান কর, তাহলে যে ব্যক্তি ঘোর বিরুদ্ধাচরণে লিপ্ত সে অপেক্ষা অধিক বিভ্রান্ত আর কে? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4271,41,53,'আমি শীঘ্র তাদের জন্য আমার নিদর্শনাবলী ব্যক্ত করব বিশ্বজগতে এবং তাদের নিজেদের মধ্যে। ফলে তাদের নিকট সুস্পষ্ট হয়ে উঠবে যে ওটাই সত্য। এটা কি যথেষ্ট নয় যে, তোমার রাব্ব সর্ব বিষয়ে অবহিত?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4272,41,54,'জেনে রেখ, এরা এদের রবের সাথে সাক্ষাৎকারে সন্ধিহান। জেনে রেখ, সব কিছুকে আল্লাহ পরিবেষ্টন করে রেখেছেন। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4273,42,1,'হা, মীম। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4274,42,2,'আইন, সীন, কাফ। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4275,42,3,'পরাক্রমশালী, প্রজ্ঞাময় আল্লাহ এভাবেই তোমার পূর্ববর্তীদের মতই তোমার প্রতি প্রত্যাদেশ করেন। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4276,42,4,'আকাশমন্ডলী ও পৃথিবীতে যা কিছু আছে তা তাঁরই। তিনি সমুন্নত, মহান। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4277,42,5,'আকাশমন্ডলী উর্ধ্বদেশ হতে ভেঙ্গে পড়ার উপক্রম হয় এবং মালাইকা/ফেরেশতারা তাদের রবের সপ্রশংস পবিত্রতা ও মহিমা ঘোষণা করে এবং মর্তবাসীদের জন্য ক্ষমা প্রার্থনা করে। জেনে রেখ, আল্লাহতো ক্ষমাশীল, পরম দয়ালু। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4278,42,6,'যারা আল্লাহর পরিবর্তে অপরদেরকে অভিভাবক রূপে গ্রহণ করে, আল্লাহ তাদের প্রতি কঠোর দৃষ্টি রাখেন। তুমি তাদের কর্মবিধায়ক নও।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4279,42,7,'এভাবে আমি তোমার প্রতি কুরআন অবতীর্ণ করেছি আরাবী ভাষায় যাতে তুমি সতর্ক করতে পার মাক্কা এবং ওর চতুর্দিকের জনগণকে এবং সতর্ক করতে পার কিয়ামাতের দিন সম্পর্কে, যাতে কোন সন্দেহ নেই। সেদিন একদল জান্নাতে প্রবেশ করবে এবং একদল জাহান্নামে প্রবেশ করবে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4280,42,8,'আল্লাহ ইচ্ছা করলে মানুষকে একই উম্মাত করতে পারতেন। বস্তুতঃ তিনি যাকে ইচ্ছা তাকে স্বীয় অনুগ্রহের অধিকারী করেন। যালিমদের কোন অভিভাবক নেই, কোন সাহায্যকারীও নেই। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4281,42,9,'তারা কি আল্লাহর পরিবর্তে অপরকে অভিভাবক রূপে গ্রহণ করেছে? কিন্তু আল্লাহ! অভিভাবকতো তিনিই এবং তিনি মৃতকে জীবিত করেন। তিনি সর্ব বিষয়ে সর্বশক্তিমান। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4282,42,10,'তোমরা যে বিষয়েই মতভেদ কর না কেন, ওর মীমাংসাতো আল্লাহরই নিকট। বলঃ তিনিই আল্লাহ! আমার রাব্ব। আমি নির্ভর করি তাঁর উপর এবং আমি তাঁরই অভিমুখী!');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4283,42,11,'তিনি আকাশমন্ডলী ও পৃথিবীর সৃষ্টিকর্তা, তিনি তোমাদের মধ্য হতে তোমাদের জোড়া সৃষ্টি করেছেন এবং আন‘আমের জোড়া; এভাবে তিনি তোমাদের বংশ বিস্তার করেন; কোন কিছুই তাঁর সদৃশ নয়, তিনি সর্বশ্রোতা, সর্বদ্রষ্টা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4284,42,12,'আকাশমন্ডলী ও পৃথিবীর চাবি তাঁরই নিকট। তিনি যার প্রতি ইচ্ছা তার রিয্ক বর্ধিত করেন অথবা সংকুচিত করেন। তিনি সর্ব বিষয়ে সবিশেষ অবহিত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4285,42,13,'তিনি তোমাদের জন্য বিধিবদ্ধ করেছেন দীন যার নির্দেশ দিয়েছিলেন তিনি নূহকে। আর যা আমি অহী করেছিলাম তোমাদের এবং যার নির্দেশ দিয়েছিলাম ইবরাহীম, মূসা ও ঈসাকে এই বলে যে, তোমরা দীনকে প্রতিষ্ঠিত কর এবং ওতে মতভেদ করনা। তুমি মুশরিকদেরকে যার প্রতি আহবান করছ তা তাদের নিকট দুর্বহ মনে হয়। আল্লাহ যাকে ইচ্ছা দীনের প্রতি আকৃষ্ট করেন এবং যে তার অভিমুখী হয় তাকে দীনের দিকে পরিচালিত করেন। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4286,42,14,'তাদের নিকট জ্ঞান আসার পর শুধুমাত্র পারস্পরিক বিদ্বেষ বশতঃ তারা নিজেদের মধ্যে মতভেদ ঘটায়। এক নির্ধারিত কাল পর্যন্ত অবকাশ সম্পর্কে তোমার রবের পূর্ব সিদ্ধান্ত না থাকলে তাদের বিষয়ে ফাইসালা হয়ে যেত। তাদের পর যারা কিতাবের উত্তরাধিকারী হয়েছে তারা কুরআন সম্পর্কে বিভ্রান্তিকর সন্দেহে রয়েছে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4287,42,15,'সুতরাং তুমি ওর দিকে আহবান কর এবং ওতেই দৃঢ় প্রতিষ্ঠিত থাক যেভাবে তুমি আদিষ্ট হয়েছ এবং তাদের খেয়াল খুশীর অনুসরণ করনা। বলঃ আল্লাহ যে কিতাব অবতীর্ণ করেছেন আমি তাতে বিশ্বাস করি এবং আমি আদিষ্ট হয়েছি তোমাদের মধ্যে ন্যায় বিচার করতে। আল্লাহই আমাদের রাব্ব এবং তোমাদেরও রাব্ব। আমাদের কাজ আমাদের এবং তোমাদের কাজ তোমাদের। আমাদের এবং তোমাদের মধ্যে বিবাদ নেই। আল্লাহই আমাদেরকে একত্রিত করবেন এবং প্রত্যাবর্তন তাঁরই নিকট।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4288,42,16,'আল্লাহকে স্বীকার করার পর যারা আল্লাহ সম্পর্কে বিতর্ক করে তাদের যুক্তি-তর্ক তাদের রবের দৃষ্টিতে অসার এবং তারা তাঁর ক্রোধের পাত্র এবং তাদের জন্য রয়েছে কঠিন শাস্তি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4289,42,17,'আল্লাহই অবতীর্ণ করেছেন সত্যসহ কিতাব এবং তুলাদন্ড। তুমি কি জান, সম্ভবতঃ কিয়ামাত আসন্ন?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4290,42,18,'যারা এটা বিশ্বাস করেনা তারাই এটা ত্বরান্বিত করতে চায়। আর যারা বিশ্বাসী তারা ওকে ভয় করে এবং জানে যে, ওটা সত্য; জেনে রেখ, কিয়ামাত সম্পর্কে যারা বাক-বিতন্ডা করে তারা ঘোর বিভ্রান্তিতে রয়েছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4291,42,19,'আল্লাহ তাঁর বান্দাদের প্রতি অতি দয়ালু। তিনি যাকে ইচ্ছা রিয্ক দান করেন। তিনি প্রবল পরাক্রমশালী। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4292,42,20,'যে কেহ আখিরাতের প্রতিদান কামনা করে তার জন্য আমি তার ফসল বর্ধিত করে দিই এবং যে দুনিয়ার প্রতিদান কামনা করে আমি তাকে ওরই কিছু দিই। কিন্তু আখিরাতে তার জন্য কিছুই থাকবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4293,42,21,'তাদের কি এমন কতকগুলি দেবতা আছে যারা তাদের জন্য বিধান দিয়েছে এমন দীনের যার অনুমতি আল্লাহ দেননি? ফাইসালার ঘোষণা না থাকলে, তাদের বিষয়ে সিদ্ধান্ত হয়েই যেত। নিশ্চয়ই যালিমদের জন্য রয়েছে বেদনাদায়ক শাস্তি। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4294,42,22,'তুমি যালিমদেরকে ভীত সন্ত্রস্ত দেখবে তাদের কৃতকর্মের জন্য; আর এটাই আপতিত হবে তাদের উপর। যারা ঈমান আনে ও সৎ কাজ করে তারা থাকবে জান্নাতের মনোরম স্থানে। তারা যা কিছু চাবে তাদের রবের নিকট তা’ই পাবে। এটাইতো মহা অনুগ্রহ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4295,42,23,'এই সুসংবাদই আল্লাহ দেন তাঁর বান্দাদেরকে যারা ঈমান আনে ও সৎ কাজ করে। বলঃ আমি এর বিনিময়ে তোমাদের নিকট হতে আত্মীয়ের সৌহার্দ্য ব্যতীত অন্য কোন প্রতিদান চাইনা। যে উত্তম কাজ করে আমি তার জন্য এতে কল্যাণ বর্ধিত করি। আল্লাহ ক্ষমাশীল, গুণগ্রাহী। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4296,42,24,'তারা কি বলে যে, সে আল্লাহ সম্পর্কে মিথ্যা উদ্ভাবন করেছে? যদি তা’ই হত তাহলে আল্লাহ ইচ্ছা করলে তোমার হৃদয় মোহর করে দিতেন। আল্লাহ মিথ্যাকে মুছে দেন এবং নিজ বাণী দ্বারা সত্যকে প্রতিষ্ঠিত করেন। অন্তরে যা আছে সেই বিষয়ে তিনি সবিশেষ অবহিত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4297,42,25,'তিনিই তাঁর বান্দাদের তাওবাহ কবূল করেন এবং পাপ মোচন করেন এবং তোমরা যা কর তিনি তা জানেন। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4298,42,26,'তিনি মু’মিন ও সৎকর্মশীলদের আহবানে সাড়া দেন এবং তাদের প্রতি তার অনুগ্রহ বর্ধিত করেন; কাফিরদের জন্য রয়েছে কঠিন শাস্তি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4299,42,27,'আল্লাহ তাঁর বান্দাদেরকে জীবনোপকরণের প্রাচুর্য দিলে তারা পৃথিবীতে অবশ্যই বিপর্যয় সৃষ্টি করত; কিন্তু তিনি তাঁর ইচ্ছা মত সঠিক পরিমানেই দিয়ে থাকেন। তিনি তাঁর বান্দাদেরকে সম্যক জানেন ও দেখেন। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4300,42,28,'তারা যখন হতাশাগ্রস্ত হয়ে পড়ে তখনই তিনি বৃষ্টি বর্ষণ করেন এবং তাঁর করুণা বিস্তার করেন। তিনিই অভিভাবক, প্রশংসা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4301,42,29,'তাঁর অন্যতম নিদর্শন আকাশমন্ডলী ও পৃথিবীর সৃষ্টি এবং এতদুভয়ের মধ্যে তিনি যে সব জীবজন্তু ছড়িয়ে দিয়েছেন সেগুলি। তিনি যখন ইচ্ছা তখনই ওদেরকে সমবেত করতে সক্ষম। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4302,42,30,'তোমাদের যে বিপদ-আপদ ঘটে তাতো তোমাদেরই কৃতকর্মের ফল এবং তোমাদের অনেক অপরাধ তিনি ক্ষমা করে দেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4303,42,31,'তোমরা পৃথিবীতে আল্লাহর অভিপ্রায়কে ব্যর্থ করতে পারবেনা এবং আল্লাহ ব্যতীত তোমাদের কোন অভিভাবক নেই, সাহায্যকারীও নেই।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4304,42,32,'তাঁর অন্যতম নিদর্শন পর্বত সদৃশ সমুদ্রে চলমান নৌযানসমূহ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4305,42,33,'তিনি ইচ্ছা করলে বায়ুকে স্তদ্ধ করে দিতে পারেন; ফলে নৌযানসমূহ নিশ্চল হয়ে পড়বে সমুদ্র পৃষ্ঠে। নিশ্চয়ই এতে নিদর্শন রয়েছে ধৈর্যশীল ও কৃতজ্ঞ ব্যক্তির জন্য।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4306,42,34,'অথবা তিনি তাদের কৃতকর্মের জন্য সেইগুলিকে বিধ্বস্ত করে দিতে পারেন এবং অনেককে তিনি ক্ষমাও করেন। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4307,42,35,'আর আমার নিদর্শন সম্পর্কে যারা বির্তক করে তারা যেন জানতে পারে যে, তাদের কোন নিস্কৃতি নেই। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4308,42,36,'বস্তুতঃ তোমাদেরকে যা কিছু দেয়া হয়েছে তা পার্থিব জীবনের ভোগ। কিন্তু আল্লাহর নিকট যা আছে তা উত্তম ও স্থায়ী - তাদের জন্য, যারা ঈমান আনে ও তাদের রবের উপর নির্ভর করে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4309,42,37,'যারা গুরুতর পাপ ও অশ্লীল কাজ হতে বেঁচে থাকে এবং ক্রোধাবিষ্ট হয়েও ক্ষমা করে দেয় – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4310,42,38,'যারা তাদের রবের আহবানে সাড়া দেয়, সালাত কায়েম করে, নিজেদের মধ্যে পরামর্শের মাধ্যমে নিজেদের কাজ সম্পাদন করে এবং তাদেরকে আমি যে রিয্ক দিয়েছি তা হতে ব্যয় করে – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4311,42,39,'এবং যারা অত্যাচারিত হলে প্রতিশোধ গ্রহণ করে – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4312,42,40,'মন্দের প্রতিফল অনুরূপ মন্দ দ্বারা এবং যে ক্ষমা করে ও আপোষ-নিস্পত্তি করে তার পুরস্কার আল্লাহর নিকট রয়েছে। আল্লাহ যালিমদের পছন্দ করেন না।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4313,42,41,'তবে অত্যাচারিত হওয়ার পর যারা প্রতিবিধান করে তাদের বিরুদ্ধে কোন ব্যবস্থা গ্রহণ করা হবেনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4314,42,42,'শুধু তাদের বিরুদ্ধে ব্যবস্থা গ্রহণ করা হবে যারা মানুষের উপর অত্যাচার করে এবং পৃথিবীতে অন্যায়ভাবে বিদ্রোহাচরণ করে বেড়ায়, তাদের জন্য রয়েছে বেদনাদায়ক শাস্তি। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4315,42,43,'অবশ্য যে ধৈর্য ধারণ করে এবং ক্ষমা করে দেয় তাতো হবে দৃঢ় সম্পর্কেরই কাজ। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4316,42,44,'আল্লাহ যাকে পথভ্রষ্ট করেন তার জন্য কোন অভিভাবক নেই। যালিমরা যখন শাস্তি প্রত্যক্ষ করবে তখন তুমি তাদেরকে বলতে শুনবেঃ প্রত্যাবর্তনের কোন উপায় আছে কি? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4317,42,45,'তুমি তাদেরকে দেখতে পাবে যে, তাদেরকে (জাহান্নামের সামনে) উপস্থিত করা হচ্ছে, লাঞ্ছিত ও অপমানিত অবস্থায়। মু’মিনরা কিয়ামাত দিবসে বলবেঃ ক্ষতিগ্রস্ত তারাই যারা নিজেদের ও নিজেদের পরিজনবর্গের ক্ষতি সাধন করেছে। জেনে রেখ, যালিমরা ভোগ করবে স্থায়ী শাস্তি। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4318,42,46,'আল্লাহ ব্যতীত তাদেরকে সাহায্য করার জন্য তাদের কোন অভিভাবক থাকবেনা এবং আল্লাহ যাকে পথভ্রষ্ট করেন তার কোন গতি নেই।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4319,42,47,'তোমরা তোমাদের রবের আহবানে সাড়া দাও সেই দিন আসার পূর্বে যা আল্লাহর বিধানে অপ্রতিরোদ্ধ, ‘যেদিন তোমাদের কোন আশ্রয়স্থল থাকবেনা, আর না (তোমাদের পাপ) অস্বীকার করার সুযোগ থাকবে।’ ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4320,42,48,'তারা যদি মুখ ফিরিয়ে নেয় তাহলে তোমাকেতো আমি তাদের রক্ষক করে পাঠাইনি। তোমার কাজতো শুধু প্রচার করে যাওয়া। আমি মানুষকে যখন অনুগ্রহ আস্বাদন করাই তখন সে উৎফুল্ল হয় এবং যখন তাদের কৃতকর্মের জন্য তাদের বিপদ আপদ ঘটে তখন মানুষ হয়ে যায় অকৃতজ্ঞ। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4321,42,49,'আকাশমন্ডলী ও পৃথিবীর সার্বভৌমত্ব আল্লাহরই। তিনি যা ইচ্ছা তা’ই সৃষ্টি করেন। তিনি যাকে ইচ্ছা কন্যা সন্তান এবং যাকে ইচ্ছা পুত্র সন্তান দান করেন। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4322,42,50,'অথবা দান করেন পুত্র ও কন্যা উভয়ই এবং যাকে ইচ্ছা তাকে করে দেন বন্ধ্যা। তিনি সর্বজ্ঞ, সর্বশক্তিমান।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4323,42,51,'মানুষের এমন মর্যাদা নেই যে, আল্লাহ তার সাথে কথা বলবেন অহীর মাধ্যম ছাড়া, অথবা পর্দার অন্তরাল ব্যতিত, অথবা এমন দূত প্রেরণ ছাড়া যে দূত তাঁর অনুমতিক্রমে তিনি যা চান তা ব্যক্ত করে। তিনি সমুন্নত, প্রজ্ঞাময়। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4324,42,52,'এভাবে আমি তোমার প্রতি প্রত্যাদেশ করেছি রূহ তথা আমার নির্দেশ; তুমিতো জানতেনা কিতাব কি ও ঈমান কি! পক্ষান্তরে আমি একে করেছি আলো যা দ্বারা আমি আমার বান্দাদের মধ্যে যাকে ইচ্ছা পথ-নির্দেশ করি; তুমিতো প্রদর্শন কর শুধু সরল পথ – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4325,42,53,'সেই আল্লাহর পথ যিনি আকাশমন্ডলী ও পৃথিবীতে যা কিছু আছে তার মালিক। জেনে রেখ, সকল বিষয়ের পরিণাম আল্লাহরই দিকে প্রত্যাবর্তন করে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4326,43,1,'হা, মীম। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4327,43,2,'শপথ সুস্পষ্ট কিতাবের! ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4328,43,3,'আমি এটা অবতীর্ণ করেছি আরাবী ভাষায় কুরআন রূপে, যাতে তোমরা বুঝতে পার। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4329,43,4,'এটা রয়েছে আমার নিকট উম্মুল কিতাবে; এটা মহান, জ্ঞানগর্ভ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4330,43,5,'আমি কি তোমাদের হতে এই উপদেশ বাণী সম্পূর্ণরূপে প্রত্যাহার করে নিব এই কারণে যে, তোমরা সীমালংঘনকারী সম্প্রদায়? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4331,43,6,'পূর্ববর্তীদের নিকট আমি বহু নাবী প্রেরণ করেছিলাম।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4332,43,7,'এবং যখনই তাদের নিকট কোন নাবী এসেছে, তারা তাকে ঠাট্টা বিদ্রুপ করেছে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4333,43,8,'তাদের মধ্যে যারা এদের অপেক্ষা শক্তিতে প্রবল ছিল তাদেরকে আমি ধ্বংস করেছিলাম; আর এভাবে চলে আসছে পূর্ববর্তীদের অনুরূপ দৃষ্টান্ত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4334,43,9,'তুমি যদি তাদেরকে জিজ্ঞেস করঃ কে আকাশমন্ডলী ও পৃথিবী সৃষ্টি করেছেন? তারা অবশ্যই বলবেঃ এগুলিতো সৃষ্টি করেছেন পরাক্রমশালী, সর্বজ্ঞ আল্লাহ – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4335,43,10,'যিনি তোমাদের জন্য পৃথিবীকে করেছেন শয্যা এবং ওতে করেছেন তোমাদের চলার পথ, যাতে তোমরা সঠিক পথ পেতে পার; ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4336,43,11,'এবং যিনি আকাশ হতে বারি বর্ষণ করেন পরিমিতভাবে। এবং আমি তদ্বারা সঞ্জীবিত করি নির্জীব ভূখন্ডকে। এভাবেই তোমাদেরকে পুনরুত্থিত করা হবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4337,43,12,'এবং যিনি যুগলসমূহের প্রত্যেককে সৃষ্টি করেন এবং যিনি তোমাদের জন্য সৃষ্টি করেন এমন নৌযান ও  চতুস্পদ জন্তু যাতে তোমরা আরোহণ কর –');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4338,43,13,'যাতে তোমরা ওদের পৃষ্ঠে স্থির হয়ে বসতে পার, তারপর তোমাদের রবের অনুগ্রহ স্মরণ কর যখন তোমরা ওর উপর স্থির হয়ে বস এবং বলঃ পবিত্র ও মহান তিনি যিনি এদেরকে আমাদের বশীভূত করে দিয়েছেন যদিও আমরা সমর্থ ছিলামনা এদেরকে বশীভূত করতে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4339,43,14,'আমরা আমাদের রবের নিকট অবশ্যই প্রত্যাবর্তন করব।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4340,43,15,'তারা তাঁর বান্দাদের মধ্য হতে তাঁর অংশ সাব্যস্ত করেছে। মানুষতো স্পষ্টই অকৃতজ্ঞ। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4341,43,16,'তিনি কি তাঁর সৃষ্টি হতে নিজের জন্য কন্যা সন্তান গ্রহণ করেছেন এবং তোমাদেরকে বিশিষ্ট করেছেন পুত্র সন্তান দ্বারা?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4342,43,17,'দয়াময় আল্লাহর প্রতি তারা যা আরোপ করে তাদের কেহকে সেই সন্তানের সংবাদ দেয়া হলে তার মুখমন্ডল কালো হয়ে যায় এবং সে দুঃসহ মর্ম যাতনায় ক্লিষ্ট হয়। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4343,43,18,'তারা কি আল্লাহর প্রতি আরোপ করে এমন সন্তান, যে অলংকারে আবৃত হয়ে লালিত পালিত হয় এবং তর্ক-বিতর্ক কালে স্পষ্ট বক্তব্যে অসমর্থ?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4344,43,19,'তারা দয়াময় আল্লাহর বান্দা মালাইকাকে নারী গন্য করেছে। এদের সৃষ্টি কি তারা প্রত্যক্ষ করেছিল? তাদের উক্তি লিপিবদ্ধ করা হবে এবং তাদেরকে জিজ্ঞেস করা হবে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4345,43,20,'তারা বলেঃ দয়াময় আল্লাহ ইচ্ছা করলে আমরা এদের পূজা করতামনা। এ বিষয়ে তাদের কোন জ্ঞান নেই; তারাতো শুধু মিথ্যাই বলছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4346,43,21,'আমি কি তাদেরকে কুরআনের পূর্বে কোন কিতাব দান করেছি, যা তারা দৃঢ়ভাবে ধারণ করে আছে? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4347,43,22,'বরং তারা বলেঃ আমরাতো আমাদের পূর্ব পুরুষদেরকে পেয়েছি এক মতাদর্শের অনুসারী এবং আমরা তাদেরই পদাঙ্ক অনুসরণ করছি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4348,43,23,'এভাবে তোমার পূর্বে কোন জনপদে যখনই কোন সতর্ককারী প্রেরণ করেছি তখন ওর সমৃদ্ধশালী ব্যক্তিরা বলতঃ আমরাতো আমাদের পূর্ব-পুরুষদেরকে পেয়েছি এক মতাদর্শের অনুসারী এবং আমরা তাদেরই পদাঙ্ক অনুসরণ করছি। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4349,43,24,'সেই সতর্ককারী বলতঃ তোমরা তোমাদের পূর্ব-পুরুষদেরকে যে পথে পেয়েছ, আমি যদি তোমাদের জন্য তদপেক্ষা উৎকৃষ্ট পথ-নির্দেশ আনয়ন করি তাহলেও কি তোমরা তাদের পদাঙ্ক অনুসরণ করবে? তারা বলতঃ তোমরা যা সহ প্রেরিত হয়েছ আমরা তা প্রত্যাখ্যান করি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4350,43,25,'অতঃপর আমি তাদেরকে তাদের কর্মের প্রতিফল দিলাম; দেখ, মিথ্যাচারীদের পরিণাম কি হয়েছে! ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4351,43,26,'স্মরণ কর, ইবরাহীম তার পিতা এবং সম্প্রদায়কে বলেছিলঃ তোমরা যাদের পূজা কর তাদের সাথে আমার কোন সম্পর্ক নেই। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4352,43,27,'সম্পর্ক আছে শুধু তাঁরই সাথে যিনি আমাকে সৃষ্টি করেছেন এবং তিনিই আমাকে সৎ পথে পরিচালিত করবেন। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4353,43,28,'এই ঘোষণাকে সে স্থায়ী বাণী রূপে রেখে গেছে তার পরবর্তীদের জন্য যাতে তারা প্রত্যাবর্তন করে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4354,43,29,'বরং আমিই তাদেরকে এবং তাদের পূর্ব-পুরুষদেরকে সুযোগ দিয়েছিলাম ভোগের; অবশেষে তাদের নিকট এলো সত্য ও স্পষ্ট প্রচারক রাসূল। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4355,43,30,'যখন তাদের নিকট সত্য এলো তখন তারা বললঃ এটাতো যাদু এবং আমরা এটা প্রত্যাখ্যান করি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4356,43,31,'এবং তারা বলেঃ এই কুরআন কেন অবতীর্ণ করা হলনা দুই জনপদের কোন প্রতিপত্তিশালী ব্যক্তির উপর? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4357,43,32,'তারা কি তোমার রবের করুণা বন্টন করে? আমিই তাদের মধ্যে জীবিকা বন্টন করি তাদের পার্থিব জীবনে এবং একজনকে অপরের উপর মর্যাদায় উন্নীত করি যাতে একে অপরের দ্বারা কাজ করিয়ে নিতে পারে; এবং তারা যা জমা করে তা হতে তোমার রবের অনুগ্রহ উৎকৃষ্টতর। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4358,43,33,'সত্য প্রত্যাখ্যানে মানুষ এক মতাবলম্বী হয়ে পড়বে, এই আশংকা না থাকলে দয়াময় আল্লাহকে যারা অস্বীকার করে তাদেরকে আমি দিতাম তাদের গৃহের জন্য রৌপ্য নির্মিত ছাদ ও সিড়ি যাতে তারা আরোহণ করে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4359,43,34,'এবং তাদের গৃহের জন্য দিতাম রৌপ্য নির্মিত দরজা, বিশ্রামের জন্য পালঙ্ক যাতে তারা হেলান দিয়ে বসত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4360,43,35,'এবং স্বর্ণের নির্মিতও। আর এই সবইতো শুধু পার্থিব জীবনের ভোগ সম্ভার। মুত্তাকীদের জন্য তোমার রবের নিকট রয়েছে আখিরাতের কল্যাণ। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4361,43,36,'যে ব্যক্তি দয়াময় আল্লাহর স্মরণে বিমুখ হয় আমি তার জন্য নিয়োজিত করি এক শাইতান, অতঃপর সে হয় তার সহচর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4362,43,37,'শাইতানরাই মানুষকে সৎ পথ হতে বিরত রাখে, অথচ মানুষ মনে করে যে, তারা সৎ পথে পরিচালিত হচ্ছে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4363,43,38,'অবশেষে যখন সে আমার নিকট উপস্থিত হবে তখন সে শাইতানকে বলবেঃ হায়! আমার ও তোমার মধ্যে যদি পূর্ব ও পশ্চিমের ব্যবধান থাকত! কত নিকৃষ্ট সহচর সে! ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4364,43,39,'যেহেতু তোমরা সীমালংঘন করেছিলে, তাই আজ তোমাদের এই অনুতাপ তোমাদের কোন কাজে আসবে না, তোমরা তো সবাই শাস্তিতে শরীক। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4365,43,40,'তুমি কি শোনাতে পারবে বধিরকে? অথবা যে অন্ধ এবং যে ব্যক্তি স্পষ্ট বিভ্রান্তিতে আছে তাকে কি পারবে সৎ পথে পরিচালিত করতে? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4366,43,41,'আমি যদি তোমার মৃত্যু ঘটাই, তবুও আমি তাদেরকে শাস্তি দিব। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4367,43,42,'অথবা আমি তাদেরকে যে শাস্তির ভীতি প্রদর্শন করেছি যদি আমি তোমাকে তা প্রত্যক্ষ করাই তাহলে তাদের উপর আমার পূর্ণ ক্ষমতা রয়েছে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4368,43,43,'সুতরাং তোমার প্রতি যা অহী করা হয়েছে তা দৃঢ়ভাবে অবলম্বন কর। তুমি সরল পথেই রয়েছ। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4369,43,44,'নিশ্চয়ই ইহা (কুরআন) তোমার এবং তোমার সম্প্রদায়ের জন্য উপদেশ, তোমাদেরকে অবশ্যই এ বিষয়ে প্রশ্ন করা হবে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4370,43,45,'তোমার পূর্বে আমি যে সব রাসূল প্রেরণ করেছিলাম তাদেরকে তুমি জিজ্ঞেস কর, আমি কি দয়াময় আল্লাহ ব্যতীত কোন উপাস্য স্থির করেছিলাম যার ইবাদাত করা যায়? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4371,43,46,'মূসাকে আমি আমার নিদর্শনসহ ফির‘আউন ও তার পরিষদবর্গের নিকট পাঠিয়েছিলাম; সে বলেছিলঃ আমি জগতসমূহের রবের পক্ষ হতে প্রেরিত রাসূল। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4372,43,47,'সে তাদের নিকট আমার নিদর্শনসহ আসা মাত্র তারা তা নিয়ে হাসি-ঠাট্টা করতে লাগল। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4373,43,48,'আমি তাদেরকে এমন কোন নিদর্শন দেখাইনি যা ওর অনুরূপ নিদর্শন অপেক্ষা শ্রেষ্ঠ নয়। আমি তাদেরকে শাস্তি দিলাম যাতে তারা প্রত্যাবর্তন করে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4374,43,49,'তারা বলেছিলঃ হে যাদুকর! তোমার রবের নিকট তুমি আমাদের জন্য তা প্রার্থনা কর যা তিনি তোমার সাথে অংগীকার করেছেন; তাহলে আমরা অবশ্যই সৎ পথ অবলম্বন করব। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4375,43,50,'অতঃপর যখন আমি তাদের উপর হতে শাস্তি বিদূরিত করলাম তখনই তারা অংগীকার ভংগ করতে লাগল। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4376,43,51,'ফির‘আউন তার সম্প্রদায়ের মধ্যে এই বলে ঘোষণা করলঃ হে আমার সম্প্রদায়! মিসর রাজ্য কি আমার নয়? এই নদীগুলি আমার পাদদেশে প্রবাহিত, তোমরা কি দেখনা? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4377,43,52,'আমি কি শ্রেষ্ঠ নই এই ব্যক্তি হতে যে হীন এবং স্পষ্ট কথা বলতে অক্ষম? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4378,43,53,'মূসাকে কেন দেয়া হল না স্বর্ণ বলয়, অথবা তার সাথে কেন এলো না মালাইকা/ফেরেশতা দলবদ্ধভাবে? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4379,43,54,'এভাবে সে তার সম্প্রদায়কে হতবুদ্ধি করে দিল। ফলে তারা তার কথা মেনে নিল। তারাতো ছিল এক সত্যত্যাগী সম্প্রদায়। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4380,43,55,'যখন তারা আমাকে ক্রোধান্বিত করল তখন আমি তাদেরকে শাস্তি দিলাম এবং নিমজ্জিত করলাম তাদের সবাইকে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4381,43,56,'অতঃপর পরবর্তীদের জন্য আমি তাদেরকে করে রাখলাম অতীত ইতিহাস ও দৃষ্টান্ত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4382,43,57,'যখন মারইয়াম তনয়ের দৃষ্টান্ত উপস্থিত করা হয় তখন তোমার সম্প্রদায় শোরগোল শুরু করে দেয়। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4383,43,58,'এবং বলেঃ আমাদের দেবতাগুলি শ্রেষ্ঠ, না ঈসা? তারা শুধু বাক-বিতন্ডার উদ্দেশেই তোমাকে এ কথা বলে। বস্তুতঃ তারাতো শুধু বাক-বিতন্ডাকারী সম্প্রদায়। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4384,43,59,'সেতো ছিল আমারই এক বান্দা, যাকে আমি অনুগ্রহ করেছিলাম এবং করেছিলাম বানী ইসরাঈলের জন্য দৃষ্টান্ত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4385,43,60,'আমি ইচ্ছা করলে তোমাদের মধ্য হতে মালাইকা/ফেরেশতা সৃষ্টি করতে পারতাম, যারা পৃথিবীতে উত্তরাধিকারী হত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4386,43,61,'ঈসাতো কিয়ামাতের নিদর্শন; সুতরাং তোমরা কিয়ামাতে সন্দেহ পোষণ করনা এবং আমাকে অনুসরণ কর। এটাই সরল পথ। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4387,43,62,'শাইতান যেন তোমাদেরকে কিছুতেই নিবৃত্ত না করে, সেতো তোমাদের প্রকাশ্য শত্রু।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4388,43,63,'ঈসা যখন স্পষ্ট নিদর্শনসহ এলো তখন সে বললঃ আমিতো তোমাদের নিকট এসেছি প্রজ্ঞাসহ এবং তোমরা যে বিষয়ে মতভেদ করছ তা স্পষ্ট করে দেয়ার জন্য। সুতরাং তোমরা আল্লাহকে ভয় কর এবং আমার অনুসরণ কর। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4389,43,64,'আল্লাহই আমার রাব্ব এবং তোমাদের রাব্ব। অতএব তাঁর ইবাদাত কর; এটাই সরল পথ। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4390,43,65,'অতঃপর তাদের কতিপয় দল মতানৈক্য সৃষ্টি করল; সুতরাং যালিমদের জন্য দুর্ভোগ, যন্ত্রণাদায়ক দিনের শাস্তির। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4391,43,66,'তারাতো তাদের অজ্ঞাতসারে আকস্মিকভাবে কিয়ামাত আসারই অপেক্ষা করছে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4392,43,67,'বন্ধুরা সেই দিন হয়ে পড়বে একে অপরের শত্রু, তবে মু’মিনরা ব্যতীত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4393,43,68,'হে আমার বান্দাগণ! আজ তোমাদের কোন ভয় নেই এবং দুঃখিতও হবেনা তোমরা – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4394,43,69,'যারা আমার আয়াতে বিশ্বাস করেছিল এবং আত্মসমর্পণ করেছিল। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4395,43,70,'তোমরা এবং তোমাদের সহধর্মিনীগণ সানন্দে জান্নাতে প্রবেশ কর। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4396,43,71,'স্বর্ণের থালা ও পানপাত্র নিয়ে তাদেরকে প্রদক্ষিণ করা হবে; সেখানে রয়েছে সবকিছু, অন্তর যা চায় এবং নয়ন যাতে তৃপ্ত হয়। সেখানে তোমরা স্থায়ী হবে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4397,43,72,'এটাই জান্নাত, তোমাদেরকে যার অধিকারী করা হয়েছে, তোমাদের কর্মের ফল স্বরূপ। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4398,43,73,'সেখানে তোমাদের জন্য রয়েছে প্রচুর ফলমূল, তোমরা আহার করবে তা হতে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4399,43,74,'নিশ্চয়ই অপরাধীরা জাহান্নামের শাস্তিতে থাকবে স্থায়ী। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4400,43,75,'তাদের শাস্তি লাঘব করা হবেনা এবং তারা হতাশ হয়ে পড়বে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4401,43,76,'আমি তাদের প্রতি যুলম করিনি, বরং তারা নিজেরাই ছিল যালিম। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4402,43,77,'তারা চিৎকার করে বলবেঃ হে মালিক (জাহান্নামের অধিকর্তা) তোমার রাব্ব আমাদেরকে নিঃশেষ করে দিন। সে বলবেঃ তোমরা এভাবেই থাকবে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4403,43,78,'আল্লাহ বলবেনঃ আমি তোমাদের নিকট সত্য পৌঁছিয়ে ছিলাম, কিন্তু তোমাদের অধিকাংশই ছিলে সত্য বিমুখ। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4404,43,79,'তারা কি কোন ব্যাপারে চূড়ান্ত সিদ্ধান্ত গ্রহণ করেছে? আমিই সিদ্ধান্ত গ্রহণকারী। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4405,43,80,'তারা কি মনে করে যে, আমি তাদের গোপন বিষয় ও মন্ত্রনার খবর রাখিনা? অবশ্যই রাখি। আমার মালাইকা/ফেরেশতাতো তাদের নিকট থেকে সব কিছু লিপিবদ্ধ করে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4406,43,81,'বলঃ দয়াময় ‘রাহমানের’ কোন সন্তান থাকলে আমি হতাম তার উপাসকগণের অগ্রণী। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4407,43,82,'তারা যা আরোপ করে তা হতে পবিত্র আকাশমন্ডলী ও পৃথিবীর মহান অধিপতি এবং আরশের অধিকারী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4408,43,83,'অতএব তাদেরকে যে দিনের কথা বলা হয়েছে তার সম্মুখীন হবার পূর্ব পর্যন্ত বাক-বিতন্ডা ও ক্রীড়া-কৌতুক করতে দাও। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4409,43,84,'তিনিই মা‘বূদ নভোমন্ডলের, তিনিই মা‘বূদ ভূতলের এবং তিনি প্রজ্ঞাময়, সর্বজ্ঞ। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4410,43,85,'কত মহান তিনি, আকাশমন্ডলী ও পৃথিবী এবং এইগুলির মধ্যবর্তী সব কিছুর সার্বভৌম অধিপতি! কিয়ামাতের জ্ঞান শুধু তাঁরই আছে এবং তাঁরই নিকট তোমরা প্রত্যাবর্তিত হবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4411,43,86,'আল্লাহর পরিবর্তে তারা যাদেরকে ডাকে, সুপারিশের ক্ষমতা তাদের নেই, তবে যারা সত্য উপলব্ধি করে ওর সাক্ষ্য দেয় তারা ব্যতীত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4412,43,87,'তুমি যদি তাদেরকে জিজ্ঞেস কর, কে তাদেরকে সৃষ্টি করেছে তাহলে তারা অবশ্যই বলবেঃ আল্লাহ! তবুও তারা কোথায় ফিরে যাচ্ছে? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4413,43,88,'আমি অবগত আছি রাসূলের এই উক্তি - হে আমার রাব্ব! এই সম্প্রদায়তো ঈমান আনবেনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4414,43,89,'সুতরাং তুমি তাদেরকে উপেক্ষা কর এবং বলঃ সালাম! তারা শীঘ্রই জানতে পারবে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4415,44,1,'হা মীম। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4416,44,2,'শপথ সুস্পষ্ট কিতাবের।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4417,44,3,'আমি ইহা অবতীর্ণ করেছি এক মুবারাক রাতে, আমিতো সতর্ককারী। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4418,44,4,'এ রাতে প্রত্যেক গুরুত্বপূর্ণ বিষয় স্থিরীকৃত হয় – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4419,44,5,'আমার আদেশক্রমে; আমি রাসূল প্রেরণ করে থাকি – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4420,44,6,'তোমার রবের অনুগ্রহ স্বরূপ, তিনি সর্বশ্রোতা, সর্বজ্ঞ –');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4421,44,7,'যিনি আকাশমন্ডলী, পৃথিবী এবং ওগুলির মধ্যস্থিত সব কিছুর রাব্ব - যদি তোমরা নিশ্চিত বিশ্বাসী হও। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4422,44,8,'তিনি ব্যতীত কোন মা‘বূদ নেই, তিনি জীবন দান করেন এবং তিনিই মৃত্যু ঘটান; তিনিই তোমাদের এবং তোমাদের পূর্ব-পুরুষদেরও রাব্ব। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4423,44,9,'বস্তুতঃ তারা সন্দেহের বশবর্তী হয়ে হাসি ঠাট্টা করছে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4424,44,10,'অতএব তুমি অপেক্ষা কর সেই দিনের যেদিন স্পষ্ট ধুম্রাচ্ছন্ন হবে আকাশ। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4425,44,11,'এবং তা আবৃত করে ফেলবে মানব জাতিকে। এটা হবে যন্ত্রণাদায়ক শাস্তি। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4426,44,12,'তখন তারা বলবেঃ হে আমাদের রাব্ব! আমাদেরকে এই শাস্তি হতে মুক্তি দিন, আমরা ঈমান আনব।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4427,44,13,'তারা কি করে উপদেশ গ্রহণ করবে? তাদের নিকটতো এসেছে স্পষ্ট ব্যাখ্যাকারী এক রাসূল; ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4428,44,14,'অতঃপর তারা তাকে অমান্য করে বলেঃ সেতো শিখানো বুলি বলছে, সেতো এক পাগল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4429,44,15,'আমি তোমাদের শাস্তি কিছু কালের জন্য রহিত করছি, তোমরাতো তোমাদের পূর্বাবস্থায় ফিরে যাবে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4430,44,16,'যেদিন আমি তোমাদেরকে প্রবলভাবে পাকড়াও করব সেদিন আমি তোমাদেরকে শাস্তি দিবই।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4431,44,17,'এদের পূর্বে আমি ফির‘আউন সম্প্রদায়কে পরীক্ষা করেছিলাম এবং তাদের নিকটও এসেছিল এক মহান রাসূল। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4432,44,18,'সে বললঃ আল্লাহর বান্দাদেরকে আমার নিকট প্রত্যর্পণ কর। আমি তোমাদের জন্য এক বিশ্বস্ত রাসূল। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4433,44,19,'এবং তোমরা আল্লাহর বিরুদ্ধে উদ্ধত হয়োনা, আমি তোমাদের নিকট উপস্থিত করছি স্পষ্ট প্রমাণ। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4434,44,20,'তোমরা যাতে আমাকে প্রস্তরাঘাতে হত্যা করতে না পার তজ্জন্য আমি আমার রাব্ব ও তোমাদের রবের আশ্রয় প্রার্থনা করছি। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4435,44,21,'যদি তোমরা আমার কথায় বিশ্বাস স্থাপন না কর তাহলে তোমরা আমা হতে দূরে থাক। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4436,44,22,'অতঃপর মূসা তার রবের নিকট নিবেদন করলঃ এরাতো এক অপরাধী সম্প্রদায়। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4437,44,23,'আমি বলেছিলামঃ তুমি আমার বান্দাদেরকে নিয়ে রাতে বের হয়ে পড়, তোমাদের পশ্চাদ্ধাবন করা হবে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4438,44,24,'সমুদ্রকে স্থির থাকতে দাও, তারা এমন এক বাহিনী যারা নিমজ্জিত হবে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4439,44,25,'তারা পশ্চাতে রেখে গেল কত উদ্যান ও প্রস্রবণ, ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4440,44,26,'কত শস্য ক্ষেত ও সুরম্য প্রাসাদ, ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4441,44,27,'কত বিলাস উপকরণ, যা তাদেরকে আনন্দ দিত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4442,44,28,'এরূপই ঘটেছিল এবং আমি এই সমুদয়ের উত্তরাধিকারী করেছিলাম ভিন্ন সম্প্রদায়কে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4443,44,29,'আকাশ এবং পৃথিবীর কেহই তাদের জন্য অশ্রুপাত করেনি এবং তাদেরকে অবকাশও দেয়া হয়নি। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4444,44,30,'আমি উদ্ধার করেছিলাম বানী ইসরাঈলকে লাঞ্ছনাদায়ক শাস্তি হতে – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4445,44,31,'ফির‘আউনের; সে তো পরাক্রান্ত সীমালংঘনকারীদের মধ্যে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4446,44,32,'আমি জেনে শুনেই তাদেরকে বিশ্বে শ্রেষ্ঠত্ব দিয়েছিলাম।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4447,44,33,'এবং তাদেরকে দিয়েছিলাম নিদর্শনাবলী, যাতে ছিল সুস্পষ্ট পরীক্ষা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4448,44,34,'তারা বলেই থাকে – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4449,44,35,'আমাদের প্রথম মৃত্যু ব্যতীত আর কিছুই নেই এবং আমরা আর পুনরুত্থিত হবনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4450,44,36,'অতএব তোমরা যদি সত্যবাদী হও তাহলে আমাদের পূর্ব-পুরুষদেরকে উপস্থিত কর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4451,44,37,'শ্রেষ্ঠ কি তারা না তুব্বা সম্প্রদায় ও তাদের পূর্ববর্তীরা? আমি তাদেরকে ধ্বংস করেছিলাম, অবশ্যই তারা ছিল অপরাধী। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4452,44,38,'আমি আকাশমন্ডলী ও পৃথিবী এবং এতদুভয়ের মধ্যস্থিত কোন কিছুই ক্রীড়াচ্ছলে সৃষ্টি করিনি। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4453,44,39,'আমি এ দু’টি অযথা সৃষ্টি করিনি, কিন্তু তাদের অধিকাংশই এটা জানেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4454,44,40,'নিশ্চয়ই সকলের জন্য নির্ধারিত রয়েছে তাদের বিচার দিবস –');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4455,44,41,'যেদিন এক বন্ধু অপর বন্ধুর কোন কাজে আসবেনা এবং তারা সাহায্যও পাবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4456,44,42,'তবে আল্লাহ যার প্রতি দয়া করেন তার কথা স্বতন্ত্র। তিনি পরাক্রমশালী, পরম দয়ালু। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4457,44,43,'নিশ্চয়ই যাক্কুম বৃক্ষ হবে –');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4458,44,44,'পাপীর খাদ্য। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4459,44,45,'গলিত তাম্রের মত; ওটা তার উদরে ফুটতে থাকবে –');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4460,44,46,'ফুটন্ত পানির মত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4461,44,47,'(বলা হবে) তাকে ধর এবং টেনে নিয়ে যাও জাহান্নামের মধ্যস্থলে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4462,44,48,'অতঃপর তার মাথার উপর ফুটন্ত পানি ঢেলে দিয়ে শাস্তি দাও। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4463,44,49,'এবং বলা হবেঃ আস্বাদন কর, তুমিতো ছিলে সম্মানিত, অভিজাত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4464,44,50,'এটাতো ওটাই, যে বিষয়ে তোমরা সন্দেহ করতে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4465,44,51,'মুত্তাকীরা থাকবে নিরাপদ স্থানে – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4466,44,52,'উদ্যান ও ঝর্ণার মাঝে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4467,44,53,'তারা পরিধান করবে মিহি ও পুরু রেশমী বস্ত্র এবং তারা মুখোমুখী হয়ে বসবে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4468,44,54,'এরূপই ঘটবে; তাদেরকে সঙ্গিনী দিব আয়তলোচনা হুর। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4469,44,55,'সেখানে তারা প্রশান্তচিত্তে বিবিধ ফলমূল আনতে বলবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4470,44,56,'প্রথম মৃত্যুর পর তারা সেখানে আর মৃত্যু আস্বাদন করবেনা। তাদেরকে জাহান্নামের শাস্তি হতে রক্ষা করবেন – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4471,44,57,'তোমার রাব্ব নিজ অনুগ্রহে। এটাইতো মহা সাফল্য।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4472,44,58,'আমি তোমার ভাষায় কুরআনকে সহজ করে দিয়েছি যাতে তারা উপদেশ গ্রহণ করে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4473,44,59,'সুতরাং তুমি প্রতীক্ষা কর, তারাওতো প্রতীক্ষমান।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4474,45,1,'হা মীম। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4475,45,2,'এই কিতাব পরাক্রমশালী, প্রজ্ঞাময় আল্লাহর নিকট হতে অবতীর্ণ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4476,45,3,'আকাশমন্ডলী ও পৃথিবীতে নিদর্শন রয়েছে মু’মিনদের জন্য।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4477,45,4,'তোমাদের সৃষ্টিতে এবং জীবজন্তুর বিস্তারে নিদর্শন রয়েছে নিশ্চিত বিশ্বাসীদের জন্য। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4478,45,5,'নিদর্শন রয়েছে চিন্তাশীল সম্প্রদায়ের জন্য, রাত ও দিনের পরিবর্তনে, আল্লাহ আকাশ হতে যে বারি বর্ষণ দ্বারা ধরিত্রীকে ওর মৃত্যুর পর পুনরুজ্জীবিত করেন তাতে এবং বায়ুর পরিবর্তনে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4479,45,6,'এগুলি আল্লাহর আয়াত যা আমি তোমার নিকট আবৃত্তি করছি যথাযথভাবে; সুতরাং আল্লাহ এবং তাঁর আয়াতের পরিবর্তে তারা আর কোন্ বাণীতে বিশ্বাস করবে?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4480,45,7,'দুর্ভোগ প্রত্যেক মিথ্যাবাদী পাপীর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4481,45,8,'\r\nযে আল্লাহর আয়াতের আবৃত্তি শোনে, অথচ ঔদ্ধত্যের সাথে অটল থাকে যেন সে তা শোনেনি, তাকে সংবাদ দাও যন্ত্রণাদায়ক শাস্তির।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4482,45,9,'যখন আমার কোন আয়াত সে অবগত হয় তখন সে তা নিয়ে পরিহাস করে। তাদের জন্য রয়েছে লাঞ্ছনাদায়ক শাস্তি। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4483,45,10,'তাদের পশ্চাতে রয়েছে জাহান্নাম; তাদের কৃতকর্ম তাদের কোন কাজে আসবেনা, তারা আল্লাহর পরিবর্তে যাদেরকে অভিভাবক স্থির করেছে তারাও নয়। তাদের জন্য রয়েছে মহাশাস্তি। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4484,45,11,'কুরআন সৎ পথের দিশারী; যারা তাদের রবের নিদর্শনাবলী প্রত্যাখ্যান করে তাদের জন্য রয়েছে অতিশয় যন্ত্রণাদায়ক শাস্তি। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4485,45,12,'আল্লাহই সমুদ্রকে তোমাদের কল্যাণে নিয়োজিত করেছেন, যাতে তাঁর আদেশে তাতে নৌযানসমূহ চলাচল করতে পারে এবং যাতে তোমরা তাঁর অনুগ্রহ অনুসন্ধান করতে পার ও তাঁর প্রতি কৃতজ্ঞ হও।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4486,45,13,'তিনি তোমাদের কল্যাণে নিয়োজিত করেছেন আকাশমন্ডলী ও পৃথিবীর সব কিছু নিজ অনুগ্রহে। চিন্তাশীল সম্প্রদায়ের জন্য এতে রয়েছে নিদর্শন। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4487,45,14,'মু’মিনদেরকে বলঃ তারা যেন ক্ষমা করে তাদেরকে যারা আল্লাহর দিনগুলির প্রত্যাশা করেনা, এটা এ জন্য যে আল্লাহ প্রত্যেক সম্প্রদায়কে তার কৃতকর্মের জন্য প্রতিদান দিবেন। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4488,45,15,'যে সৎ কাজ করে সে তার কল্যাণের জন্যই তা করে এবং কেহ মন্দ কাজ করলে ওর প্রতিফল সে’ই ভোগ করবে, অতঃপর তোমরা তোমাদের রবের নিকট প্রত্যাবর্তিত হবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4489,45,16,'আমিতো বানী ইসরাঈলকে কিতাব, কর্তৃত্ব ও নবুওয়াত দান করেছিলাম এবং তাদেরকে উত্তম জীবনোপকরণ দিয়েছিলাম এবং দিয়েছিলাম শ্রেষ্ঠত্ব বিশ্বজগতের উপর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4490,45,17,'তাদেরকে সুস্পষ্ট প্রমাণ দান করেছিলাম দীন সম্পর্কে। তাদের নিকট জ্ঞান আসার পর তারা শুধু পরস্পর বিদ্বেষবশতঃ বিরোধিতা করেছিল, তারা যে বিষয়ে মতবিরোধ করত, তোমার রাব্ব কিয়ামাত দিবসে তাদের মধ্যে সেই বিষয়ের ফায়সালা করে দিবেন। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4491,45,18,'এরপর আমি তোমাকে প্রতিষ্ঠিত করেছি দীনের বিশেষ বিধানের উপর। সুতরাং তুমি ওর অনুসরণ কর, অজ্ঞদের খেয়াল খুশীর অনুসরণ করনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4492,45,19,'আল্লাহর মুকাবিলায় তারা তোমার কোন উপকার করতে পারবেনা; যালিমরা একে অপরের বন্ধু; আর আল্লাহ মুত্তাকীদের বন্ধু। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4493,45,20,'এই কুরআন মানব জাতির জন্য সুস্পষ্ট দলীল এবং নিশ্চিত বিশ্বাসী সম্প্রদায়ের জন্য পথনির্দেশ ও রাহমাত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4494,45,21,'দুস্কৃতিকারীরা কি মনে করে যে, আমি জীবন ও মৃত্যুর দিক দিয়ে তাদেরকে তাদের সমান গণ্য করব, যারা ঈমান আনে ও সৎ কাজ করে? তাদের সিদ্ধান্ত কতই মন্দ! ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4495,45,22,'আল্লাহ আকাশমন্ডলী ও পৃথিবী সৃষ্টি করেছেন যথাযথভাবে এবং যাতে প্রত্যেক ব্যক্তি তার কাজ অনুযায়ী ফল পেতে পারে, আর তাদের প্রতি যুলম করা হবে না। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4496,45,23,'তুমি কি লক্ষ্য করছ তাকে, যে তার খেয়াল খুশীকে নিজের মা‘বূদ বানিয়ে নিয়েছে? আল্লাহ জেনে শুনেই তাকে বিভ্রান্ত করেছেন এবং তার কর্ণ ও হৃদয় মোহর করে দিয়েছেন এবং তার চক্ষুর উপর রেখেছেন আবরণ। অতএব, কে তাকে পথ নির্দেশ করবে? তবুও কি তোমরা উপদেশ গ্রহণ করবেনা?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4497,45,24,'তারা বলেঃ একমাত্র পার্থিব জীবনই আমাদের জীবন, আমরা মরি ও বাঁচি, আর সময়ই (কাল) আমাদেরকে ধ্বংস করে। বস্তুতঃ এ ব্যাপারে তাদের কোন জ্ঞান নেই, তারাতো শুধু মনগড়া কথা বলে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4498,45,25,'তাদের নিকট যখন আমার সুস্পষ্ট আয়াত আবৃত্তি করা হয় তখন তাদের কোন যুক্তি থাকেনা শুধু এই উক্তি ছাড়া যে, তোমরা সত্যবাদী হলে আমাদের পূর্ব-পুরুষদেরকে উপস্থিত কর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4499,45,26,'বলঃ আল্লাহই তোমাদেরকে জীবন দান করেন ও তোমাদের মৃত্যু ঘটান। অতঃপর তিনি তোমাদেরকে কিয়ামাত দিবসে একত্রিত করবেন যাতে কোন সন্দেহ নেই। কিন্তু অধিকাংশ মানুষ তা জানেনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4500,45,27,'আকাশমন্ডলী ও পৃথিবীর সার্বভৌমত্ব আল্লাহরই; যেদিন কিয়ামাত সংঘটিত হবে সেদিন মিথ্যাশ্রয়ীরা হবে ক্ষতিগ্রস্ত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4501,45,28,'এবং প্রত্যেক সম্প্রদায়কে দেখবে ভয়ে নতজানু; প্রত্যেক সম্প্রদায়কে তার আমলনামার প্রতি আহবান করা হবে, আজ তোমাদেরকে তারই প্রতিফল দেয়া হবে যা তোমরা করতে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4502,45,29,'এই আমার লিপি, এটা তোমাদের বিরুদ্ধে সত্য সাক্ষ্য দিবে। তোমরা যা করতে তা আমি লিপিবদ্ধ করেছিলাম। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4503,45,30,'যারা ঈমান আনে ও সৎ কাজ করে, তাদের রাব্ব তাদেরকে দাখিল করবেন স্বীয় রাহমাতে। এটাই মহা সাফল্য।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4504,45,31,'পক্ষান্তরে যারা কুফরী করে তাদেরকে বলা হবেঃ তোমাদের নিকট কি আমার আয়াত পাঠ করা হয়নি? কিন্তু তোমরা ঔদ্ধত্য প্রকাশ করেছিলে এবং তোমরা ছিলে এক অপরাধী সম্প্রদায়। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4505,45,32,'যখন বলা হয়ঃ আল্লাহর প্রতিশ্রুতি সত্য এবং কিয়ামাত - এতে কোন সন্দেহ নেই, তখন তোমরা বলে থাকঃ আমরা জানিনা কিয়ামাত কি; আমরা মনে করি এটা একটি ধারনা মাত্র এবং আমরা এ বিষয়ে নিশ্চিত নই। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4506,45,33,'তাদের মন্দ কাজগুলি তাদের নিকট প্রকাশ হয়ে পড়বে এবং যা নিয়ে তারা ঠাট্টা বিদ্রুপ করত তা তাদেরকে পরিবেষ্টন করবে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4507,45,34,'আর বলা হবেঃ আজ আমি তোমাদেরকে বিস্মৃত হব যেমন তোমরা এই দিনের সাক্ষাৎকারকে বিস্মৃত হয়েছিলে। তোমাদের আশ্রয়স্থল হবে জাহান্নাম এবং তোমাদের কোন সাহায্যকারী থাকবেনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4508,45,35,'এটা এ জন্য যে, তোমরা আল্লাহর নিদর্শনাবলীকে বিদ্রুপ করেছিলে এবং পার্থিব জীবন তোমাদেরকে প্রতারিত করেছিল। সুতরাং সেদিন তাদেরকে জাহান্নাম হতে বের করা হবেনা এবং আল্লাহর সন্তুষ্টি লাভের চেষ্টার সুযোগ দেয়া হবেনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4509,45,36,'প্রশংসা আল্লাহরই যিনি আকাশমন্ডলীর রাব্ব, পৃথিবীর রাব্ব, জগতসমূহের রাব্ব। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4510,45,37,'আকাশমন্ডলী ও পৃথিবীতে গৌরব গরিমা তাঁরই এবং তিনি পরাক্রমশালী, প্রজ্ঞাময়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4511,46,1,'হা মীম।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4512,46,2,'এই কিতাব পরাক্রমশালী, প্রজ্ঞাময় আল্লাহর নিকট হতে অবতীর্ণ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4513,46,3,'আকাশমন্ডলী ও পৃথিবী এবং এতদুভয়ের মধ্যস্থিত সব কিছুই আমি যথাযথভাবে নির্দিষ্ট কালের জন্য সৃষ্টি করেছি; কিন্তু কাফিরদেরকে যে বিষয়ে সতর্ক করা হয়েছে তা হতে মুখ ফিরিয়ে নেয়। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4514,46,4,'বলঃ তোমরা আল্লাহর পরিবর্তে যাদেরকে ডাক তাদের কথা ভেবে দেখেছ কি? তারা পৃথিবীতে কি সৃষ্টি করেছে আমাকে দেখাও অথবা আকাশমন্ডলীতে তাদের কোন অংশীদারীত্ব আছে কি? পূর্ববর্তী কোন কিতাব অথবা পরম্পরাগত কোন জ্ঞান থাকলে তা তোমরা আমার নিকট উপস্থিত কর - যদি তোমরা সত্যবাদী হও। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4515,46,5,'সেই ব্যক্তি অপেক্ষা বিভ্রান্ত কে যে আল্লাহর পরিবর্তে এমন কিছুকে ডাকে যা কিয়ামাতের দিন পর্যন্তও তার ডাকে সাড়া দিবেনা? এবং ওগুলি তাদের প্রার্থনা সম্বন্ধে অবহিত নয়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4516,46,6,'যখন কিয়ামাত দিবসে মানুষকে একত্রিত করা হবে তখন ঐগুলো হবে তাদের শত্রু, ঐগুলো তাদের ইবাদাত অস্বীকার করবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4517,46,7,'যখন তাদের নিকট আমার সুস্পষ্ট আয়াত আবৃত্তি করা হয় এবং তাদের নিকট সত্য উপস্থিত হয় তখন কাফিরেরা বলেঃ এটাতো সুস্পষ্ট যাদু। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4518,46,8,'তারা কি তাহলে বলে যে, সে এটা উদ্ভাবন করেছে? বলঃ যদি আমি উদ্ভাবন করে থাকি তাহলে তোমরা আল্লাহর শাস্তি হতে আমাকে কিছুতেই রক্ষা করতে পারবেনা। তোমরা যে বিষয়ে আলোচনায় লিপ্ত আছ সেই সম্বন্ধে আল্লাহ সবিশেষ অবহিত। আমার ও তোমাদের মধ্যে সাক্ষী হিসাবে তিনিই যথেষ্ট এবং তিনি ক্ষমাশীল, পরম দয়ালু।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4519,46,9,'বলঃ আমি এমন কোন প্রথম ও নতুন নাবী নই। আমি জানিনা, আমার ও তোমাদের ব্যাপারে কি করা হবে; আমি আমার প্রতি যা অহী করা হয় শুধু তারই অনুসরণ করি। আমি এক স্পষ্ট সতর্ককারী মাত্র।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4520,46,10,'বলঃ তোমরা ভেবে দেখেছ কি যে, এই কুরআন আল্লাহর নিকট হতে অবতীর্ণ হয়ে থাকে, আর তোমরা এতে অবিশ্বাস কর, উপরন্ত বানী ইসরাঈলের একজন এর অনুরূপ সম্পর্কে সাক্ষ্য দিয়ে এতে বিশ্বাস স্থাপন করল অথচ তোমরা কর ঔদ্ধত্য প্রকাশ, তাহলে তোমাদের পরিণাম কি হবে? আল্লাহ যালিমদেরকে সৎ পথে চালিত করেননা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4521,46,11,'মু’মিনদের সম্পর্কে কাফিরেরা বলেঃ এটা ভাল হলে তারা এর দিকে আমাদের চেয়ে অগ্রগামী হতনা। তারা এর দ্বারা পরিচালিত নয় বলে, বলেঃ এটাতো এক পুরাতন মিথ্যা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4522,46,12,'এর পূর্বে ছিল মূসার কিতাব - আদর্শ ও অনুগ্রহ স্বরূপ এই কিতাব - এর সমর্থক, আরাবী ভাষায়, যেন এটা যালিমদেরকে সতর্ক করে এবং যারা সৎ কাজ করে তাদেরকে সুসংবাদ দেয়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4523,46,13,'যারা বলেঃ আমাদের রাব্বতো আল্লাহ এবং এই বিশ্বাসে অবিচল থাকে, তাদের কোন ভয় নেই এবং তারা দুঃখিতও হবেনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4524,46,14,'এরাই জান্নাতের অধিবাসী, সেখানে এরা স্থায়ী হবে, এটাই তাদের কর্মফল। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4525,46,15,'আমি মানুষকে তার মাতা-পিতার প্রতি সদয় ব্যবহারের নির্দেশ দিয়েছি। তার জননী তাকে গর্ভে ধারণ করে কষ্টের সাথে এবং প্রসব করে কষ্টের সাথে, তার গর্ভধারণ ও দুধ ছাড়ানোর সময়কাল ত্রিশ মাস, ক্রমে সে পূর্ণ শক্তি প্রাপ্ত হয় এবং চল্লিশ বছরে উপনীত হওয়ার পর বলেঃ হে আমার রাব্ব! আপনি আমাকে সামর্থ্য দিন যাতে আমি আপনার কৃতজ্ঞতা প্রকাশ করতে পারি, আমার প্রতি ও আমার মাতা-পিতার প্রতি আপনি যে অনুগ্রহ করেছেন তার জন্য এবং যাতে আমি সৎ কাজ করতে পারি যা আপনি পছন্দ করেন; আমার জন্য আমার সন্তান সন্ততিদেরকে সৎকর্মপরায়ণ করুন, আমি আপনারই অভিমুখী হলাম এবং আত্মসমর্পণ করলাম।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4526,46,16,'আমি এদের  সৎকাজগুলি গ্রহণ করে থাকি এবং মন্দ কাজগুলি ক্ষমা করি, তারা জান্নাতবাসীদের অন্তর্ভুক্ত। এদেরকে যে প্রতিশ্রুতি দেয়া হয়েছে তা সত্য প্রমাণিত হবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4527,46,17,'আর এমন লোক আছে, যে তার মাতা-পিতাকে বলেঃ আফসোস তোমাদের জন্য। তোমরা কি আমাকে এ ভয় দেখাতে চাও যে, আমি পুনরুত্থিত হব যদিও আমার পূর্বে বহু পুরুষ গত হয়েছে! তখন তার মাতা-পিতা আল্লাহর নিকট ফরিয়াদ করে বলেঃ দুর্ভোগ তোমার জন্য! বিশ্বাস স্থাপন কর, আল্লাহর প্রতিশ্রুতি অবশ্যই সত্য। কিন্তু সে বলেঃ এটাতো অতীত কালের উপকথা ব্যতীত কিছুই নয়। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4528,46,18,'এদের পূর্বে যে জিন ও মানুষ সম্প্রদায় গত হয়েছে তাদের মত এদের প্রতিও আল্লাহর উক্তি সত্য হয়েছে। এরাই ক্ষতিগ্রস্ত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4529,46,19,'প্রত্যেকের মর্যাদা তার কাজ অনুযায়ী; এটা এ জন্য যে, আল্লাহ প্রত্যেকের কর্মের পূর্ণ প্রতিফল দিবেন এবং তার প্রতি অবিচার করা হবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4530,46,20,'যেদিন কাফিরদেরকে জাহান্নামের সন্নিকটে উপস্থিত করা হবে সেদিন তাদেরকে বলা হবেঃ তোমরাতো পার্থিব জীবনে সুখ-সম্ভোগ ভোগ করে নিঃশেষ করেছ, সুতরাং আজ তোমাদেরকে দেয়া হবে অবমাননাকর শাস্তি; কারণ তোমরা পৃথিবীতে অন্যায়ভাবে ঔদ্ধত্য প্রকাশ করেছিলে এবং তোমরা ছিলে সত্যদ্রোহী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4531,46,21,'স্মরণ কর, ‘আদ সম্প্রদায়ের ভাইয়ের কথা, যার পূর্বে এবং পরেও সতর্ককারী এসেছিল, সে তার আহকাফবাসী সম্প্রদায়কে বালুকাময় উচ্চ উপত্যকায় সতর্ক করেছিল এই বলেঃ আল্লাহ ব্যতীত কারও ইবাদাত করনা। আমি তোমাদের জন্য মহাদিনের শাস্তির আশংকা করছি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4532,46,22,'তারা বলেছিলঃ তুমি আমাদেরকে আমাদের দেব দেবীগুলির পূজা হতে নিবৃত্ত করতে এসেছ? তুমি সত্যবাদী হলে আমাদেরকে যার ভয় দেখাচ্ছ তা আনয়ণ কর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4533,46,23,'সে বললঃ এর জ্ঞানতো শুধু আল্লাহর নিকট রয়েছে; আমি যা সহ প্রেরিত হয়েছি শুধু তা’ই তোমাদের নিকট প্রচার করি, কিন্তু আমি দেখছি, তোমরা এক মূঢ় সম্প্রদায়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4534,46,24,'অতঃপর যখন তারা তাদের উপত্যকার দিকে মেঘ আসতে দেখল তখন তারা বলতে লাগলঃ ওটাতো মেঘ, আমাদেরকে বৃষ্টি দান করবে। হুদ বললঃ এটাইতো ওটা যা তোমরা ত্বরান্বিত করতে চেয়েছ, এতে রয়েছে এক ঝড়-মর্মন্তদ শাস্তি বহনকারী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4535,46,25,'আল্লাহর নির্দেশে এটা সব কিছুকে ধ্বংস করে দিবে। অতঃপর তাদের পরিণাম এই হল যে, তাদের বসতিগুলি ছাড়া আর কিছুই রইলনা। এভাবে আমি অপরাধী সম্প্রদায়কে প্রতিফল দিয়ে থাকি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4536,46,26,'আমি তাদেরকে যে প্রতিষ্ঠা দিয়েছিলাম, তোমাদেরকে তা দেইনি; আমি তাদেরকে দিয়েছিলাম কান, চোখ ও হৃদয়; কিন্তু ঐ কান, চোখ ও হৃদয় তাদের কোন কাজে আসেনি; কেননা তারা আল্লাহর আয়াতসমূহকে অস্বীকার করেছিল। যা নিয়ে তারা ঠাট্টা বিদ্রুপ করত তা’ই তাদেরকে পরিবেষ্টন করল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4537,46,27,'আমিতো ধ্বংস করেছিলাম তোমাদের চতুস্পার্শ্ববর্তী জনপদসমূহ; আমি তাদেরকে বিভিন্নভাবে আমার নিদর্শনাবলী বিবৃত করেছিলাম, যাতে তারা ফিরে আসে সৎ পথে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4538,46,28,'তারা আল্লাহর সান্নিধ্য লাভের জন্য আল্লাহর পরিবর্তে যাদেরকে মা‘বূদ রূপে গ্রহণ করেছিল তারা তাদেরকে সাহায্য করলনা কেন? বস্তুতঃ তাদের মা‘বূদগুলি তাদের নিকট হতে অন্তর্হিত হয়ে পড়ল। তাদের মিথ্যা ও অলীক উদ্ভাবনের পরিণাম এরূপই। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4539,46,29,'স্মরণ কর, আমি তোমার প্রতি আকৃষ্ট করেছিলাম একদল জিনকে, যারা কুরআন পাঠ শুনছিল, যখন তারা তার নিকট উপস্থিত হল; তারা একে অপরকে বলতে লাগলঃ চুপ করে শ্রবণ কর। যখন কুরআন পাঠ সমাপ্ত হল তখন তারা তাদের সম্প্রদায়ের নিকট ফিরে গেল সতর্ককারীরূপে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4540,46,30,'তারা বলেছিলঃ হে আমাদের সম্প্রদায়! আমরা এক কিতাব পাঠ শ্রবণ করেছি যা অবতীর্ণ হয়েছে মূসার পরে, এটা তার পূর্ববর্তী কিতাবকে সমর্থন করে এবং সত্য ও সরল পথের দিকে পরিচালিত করে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4541,46,31,'হে আমাদের সম্প্রদায়! আল্লাহর দিকে আহবানকারীর প্রতি সাড়া দাও এবং তার প্রতি বিশ্বাস স্থাপন কর, আল্লাহ তোমাদের পাপ ক্ষমা করবেন এবং যন্ত্রণাদায়ক শাস্তি হতে তোমাদেরকে রক্ষা করবেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4542,46,32,'যদি কেহ আল্লাহর দিকে আহবানকারীর ডাকে সাড়া না দেয় তাহলে সে পৃথিবীতে আল্লাহর অভিপ্রায় ব্যর্থ করতে পারবেনা এবং আল্লাহ ছাড়া তাদের কোন সাহায্যকারী থাকবেনা। তারাতো সুস্পষ্ট বিভ্রান্তিতে রয়েছে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4543,46,33,'তারা কি দেখেনা যে, আল্লাহ আকাশমন্ডলী ও পৃথিবী সৃষ্টি করেছেন এবং এ সবের সৃষ্টিতে কোন ক্লান্তি বোধ করেননি? তিনি মৃতকে জীবন দান করতেও সক্ষম। নিশ্চয়ই তিনি সর্ব বিষয়ে সর্বশক্তিমান।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4544,46,34,'যেদিন কাফিরদেরকে উপস্থিত করা হবে জাহান্নামের নিকট, সেদিন তাদেরকে বলা হবেঃ এটা কি সত্য নয়? তারা বলবেঃ আমাদের রবের শপথ! এটা সত্য। তখন তাদেরকে বলা হবেঃ শাস্তি আস্বাদান কর। কারণ তোমরা ছিলে সত্য প্রত্যাখ্যানকারী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4545,46,35,'অতএব তুমি ধৈর্য ধারণ কর যেমন করেছিল দৃঢ় প্রতিজ্ঞ রাসূলগণ এবং তাদের জন্য (শাস্তির) প্রার্থনায় তড়িঘড়ি করনা। তাদেরকে যে বিষয়ে সতর্ক করা হয়েছে তা যেদিন তারা প্রত্যক্ষ করবে সেদিন তাদের মনে হবে, তারা যেন দিনের এক দন্ডের বেশি পৃথিবীতে অবস্থান করেনি। এটা এক স্পষ্ট ঘোষণা, আল্লাহ হতে বিমুখ সম্প্রদায় ব্যতীত কেহকেও ধ্বংস করা হবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4546,47,1,'যারা ঈমান আনে, সৎ কাজ করে এবং মুহাম্মাদের প্রতি যা অবতীর্ণ হয়েছে তাতে বিশ্বাস করে, আর উহাই (কুরআন) তাদের রাব্ব হতে সত্য; তিনি তাদের মন্দ কাজগুলি ক্ষমা করবেন এবং তাদের অবস্থা ভাল করবেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4547,47,2,'যারা ঈমান আনে, সৎ কাজ করে এবং মুহাম্মাদের প্রতি যা অবতীর্ণ হয়েছে তাতে বিশ্বাস করে, আর উহাই (কুরআন) তাদের রাব্ব হতে সত্য; তিনি তাদের মন্দ কাজগুলি ক্ষমা করবেন এবং তাদের অবস্থা ভাল করবেন। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4548,47,3,'এটা এ জন্য যে, যারা কুফরী করে তারা মিথ্যার অনুসরণ করে এবং যারা ঈমান আনে তারা তাদের রাব্ব হতে প্রেরিত সত্যেরই অনুসরণ করে। এভাবে আল্লাহ মানুষের জন্য তাদের দৃষ্টান্ত স্থাপন করেন। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4549,47,4,'অতএব যখন তোমরা কাফিরদের সাথে যুদ্ধে মুকাবিলা কর তখন তাদের গর্দানে আঘাত কর, পরিশেষে যখন তোমরা তাদেরকে সম্পূর্ণ রূপে পরাভূত করবে তখন তাদেরকে কষে বাঁধবে; অতঃপর হয় অনুকম্পা, না হয় মুক্তিপণ। তোমরা জিহাদ চালাবে যতক্ষণ না ওরা অস্ত্র নামিয়ে ফেলে। এটাই বিধান। এটা এ জন্য যে, আল্লাহ ইচ্ছা করলে তাদেরকে শাস্তি দিতে পারতেন, কিন্তু তিনি চান তোমাদেরকে অপরদের দ্বারা পরীক্ষা করতে। যারা আল্লাহর পথে নিহত হয় তিনি কখনও তাদের আমল বিনষ্ট হতে দেননা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4550,47,5,'তিনি তাদেরকে সৎ পথে পরিচালিত করবেন এবং তাদের অবস্থা ভাল করে দিবেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4551,47,6,'তিনি তাদেরকে দাখিল করবেন জান্নাতে যার কথা তিনি তাদেরকে জানিয়েছিলেন। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4552,47,7,'হে মু’মিনগণ! যদি তোমরা আল্লাহকে সাহায্য কর তাহলে আল্লাহ তোমাদেরকে সাহায্য করবেন এবং তোমাদের অবস্থান দৃঢ় করবেন। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4553,47,8,'যারা কুফরী করেছে তাদের জন্য রয়েছে দুর্ভোগ এবং তিনি তাদের কাজ ব্যর্থ করে দিবেন। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4554,47,9,'এটা এ জন্য যে, আল্লাহ যা অবতীর্ণ করেছেন তারা তা অপছন্দ করে। সুতরাং আল্লাহ তাদের কাজ নিস্ফল করে দিবেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4555,47,10,'তারা কি পৃথিবীতে ভ্রমণ করেনি এবং দেখেনি তাদের পূর্ববর্তীদের পরিণাম কি হয়েছিল? আল্লাহ তাদেরকে ধ্বংস করেছেন এবং কাফিরদের জন্য রয়েছে অনুরূপ পরিণাম। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4556,47,11,'এটা এ জন্য যে, আল্লাহ মু’মিনদের অভিভাবক এবং কাফিরদের কোন অভিভাবক নেই।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4557,47,12,'যারা ঈমান আনে ও সৎ কাজ করে আল্লাহ তাদেরকে দাখিল করবেন জান্নাতে, যার নিম্নদেশে নদী প্রবাহিত; কিন্তু যারা কুফরী করে তারা ভোগ বিলাসে লিপ্ত থাকে এবং জন্তু-জানোয়ারের মত উদর পূর্তি করে; তাদের বাসস্থান হবে জাহান্নাম।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4558,47,13,'তারা যে জনপদ হতে তোমাকে বিতাড়িত করেছে তা অপেক্ষা অতি শক্তিশালী কত জনপদ ছিল; আমি তাদেরকে ধ্বংস করেছি এবং তাদেরকে সাহায্য করার কেহ ছিলনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4559,47,14,'যে ব্যক্তি তার রাব্ব হতে প্রেরিত সুস্পষ্ট প্রমাণের উপর প্রতিষ্ঠিত সে কি তার ন্যায় যার নিকট নিজের মন্দ কাজগুলি শোভন প্রতীয়মান হয় এবং যারা নিজ খেয়াল খুশীর অনুসরণ করে? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4560,47,15,'মুত্তাকীদেরকে যে জান্নাতের প্রতিশ্রুতি দেয়া হয়েছে তার দৃষ্টান্তঃ ওতে আছে নির্মল পানির নহর; আছে দুধের নহর যার স্বাদ অপরিবর্তনীয়, আছে পানকারীদের জন্য সুস্বাদু সুরার নহর এবং পরিশোধিত মধুর নহর। সেখানে তাদের জন্য থাকবে বিবিধ ফলসমূহ ও তাদের রবের ক্ষমা। মুত্তাকীরা কি তাদের ন্যায় যারা জাহান্নামে স্থায়ী হবে এবং যাদেরকে পান করতে দেয়া হবে ফুটন্ত পানি যা তাদের নাড়িভুড়ি ছিন্ন বিচ্ছিন্ন করে দিবে? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4561,47,16,'তাদের মধ্যে কতক তোমার কথা শ্রবণ করে, অতঃপর তোমার নিকট হতে বের হয়ে যারা জ্ঞানবান তাদেরকে বলেঃ এই মাত্র সে কি বলল? এদের অন্তরের উপর আল্লাহ মোহর মেরে দিয়েছেন এবং তারা নিজেদের খেয়াল খুশীরই অনুসরণ করে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4562,47,17,'যারা সৎ পথ অবলম্বন করে আল্লাহ তাদের সৎ পথে চলার শক্তি বৃদ্ধি করেন এবং তাদেরকে মুত্তাকী হবার শক্তি দান করেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4563,47,18,'তারা কি শুধু এ জন্য অপেক্ষা করছে যে, কিয়ামাত তাদের নিকট এসে পড়ূক আকস্মিক ভাবে? কিয়ামাতের লক্ষণসমূহতো এসেই পড়েছে! কিয়ামাত এসে পড়লে তারা উপদেশ গ্রহণ করবে কেমন করে? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4564,47,19,'সুতরাং জেনে রেখ, আল্লাহ ছাড়া কোন মা‘বূদ নেই; ক্ষমা প্রার্থনা কর তোমার এবং মু’মিন নর নারীদের ত্রুটির জন্য। আল্লাহ তোমাদের গতিবিধি এবং অবস্থান সম্বন্ধে সম্যক অবগত আছেন। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4565,47,20,'মু’মিনরা বলেঃ একটি সূরা অবতীর্ণ হয় না কেন? অতঃপর যদি সুস্পষ্ট মর্ম বিশিষ্ট কোন সূরা অবতীর্ণ হয় এবং তাতে জিহাদের কোন নির্দেশ থাকে তাহলে তুমি দেখবে যাদের অন্তরে ব্যাধি আছে তারা মৃত্যুভয়ে বিহবল মানুষের মত তোমার দিকে তাকাচ্ছে। শোচনীয় পরিণাম ওদের। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4566,47,21,'আনুগত্য ও ন্যায় সঙ্গত বাক্য ওদের জন্য উত্তম ছিল; সুতরাং জিহাদের সিদ্ধান্ত হলে যদি তারা আল্লাহর প্রতি প্রদত্ত অঙ্গীকার পূরণ করত তাহলে তাদের জন্য এটা মঙ্গলজনক হত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4567,47,22,'ক্ষমতায় অধিষ্ঠিত হলে সম্ভবতঃ তোমরা পৃথিবীতে বিপর্যয় সৃষ্টি করবে এবং আত্মীয়তার বন্ধন ছিন্ন করবে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4568,47,23,'আল্লাহ এদেরকেই করেন অভিশপ্ত, আর করেন বধির ও দৃষ্টিশক্তিহীন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4569,47,24,'তাহলে কি তারা কুরআন সম্বন্ধে অভিনিবেশ সহকারে চিন্তা করেনা? তাদের অন্তর তালাবদ্ধ – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4570,47,25,'যারা নিজেদের নিকট সৎ পথ ব্যক্ত হওয়ার পর তা পরিত্যাগ করে, শাইতান তাদের কাজকে শোভন করে দেখায় এবং তাদের মিথ্যা আশ্বাস দেয়। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4571,47,26,'এটা এ জন্য যে, আল্লাহ যা অবতীর্ণ করেছেন তা যারা অপছন্দ করে তাদেরকে তারা বলেঃ আমরা কোন্ কোন্ বিষয়ে তোমাদের আনুগত্য করব? আল্লাহ তাদের গোপন অভিসন্ধি অবগত আছেন। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4572,47,27,'মালাইকা/ফেরেশতারা যখন তাদের মুখমন্ডলে ও পৃষ্ঠদেশে আঘাত করতে করতে প্রাণ হরণ করবে তখন তাদের দশা কেমন হবে! ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4573,47,28,'এটা এ জন্য যে, যাতে আল্লাহর অসন্তোষ জন্মায় তারা তার অনুসরণ করে এবং তাঁর সন্তুষ্টি লাভের প্রয়াসকে অপ্রিয় গণ্য করে; তিনি তাদের কাজ নিষ্ফল করে দেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4574,47,29,'যাদের অন্তরে ব্যাধি আছে তারা কি মনে করে যে, আল্লাহ তাদের বিদ্বেষ ভাব প্রকাশ করে দিবেননা? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4575,47,30,'আমি ইচ্ছা করলে তোমাকে তাদের পরিচয় দিতাম। ফলে তুমি তাদের লক্ষণ দেখে তাদেরকে চিনতে পারতে, তুমি অবশ্যই কথার ভঙ্গিতে তাদেরকে চিনতে পারবে। আল্লাহ তোমাদের কাজ সম্পর্কে অবগত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4576,47,31,'আমি অবশ্যই তোমাদেরকে পরীক্ষা করব, যতক্ষণ না আমি জেনে নেই তোমাদের মধ্যে কে জিহাদকারী ও ধৈর্যশীল এবং আমি তোমাদের কার্যাবলী পরীক্ষা করি। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4577,47,32,'যারা কুফরী করে এবং মানুষকে আল্লাহর পথ হতে নিবৃত্ত করে এবং নিজেদের নিকট পথের দিশা ব্যক্ত হওয়ার পর রাসূলের বিরোধিতা করে, তারা আল্লাহর কোন ক্ষতি করতে পারবেনা। তিনি তাদের কাজ ব্যর্থ করবেন। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4578,47,33,'হে মু’মিনগণ! তোমরা আল্লাহর আনুগত্য কর, রাসূলের আনুগত্য কর এবং তোমাদের কর্মফল বিনষ্ট করনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4579,47,34,'যারা কুফরী করে এবং আল্লাহর পথ হতে মানুষকে নিবৃত্ত করে, অতঃপর কাফির অবস্থায় মৃত্যু বরণ করে, আল্লাহ তাদেরকে কিছুতেই ক্ষমা করবেন না।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4580,47,35,'সুতরাং তোমরা হীনবল হয়ো না এবং সন্ধির প্রস্তাব করনা, তোমরাই প্রবল; আল্লাহ তোমাদের সঙ্গে আছেন, তিনি তোমাদের কর্মফল কখনও ক্ষুন্ন করবেন না। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4581,47,36,'পার্থিব জীবনতো শুধু ক্রীড়া-কৌতুক। যদি তোমরা ঈমান আন ও তাকওয়া অবলম্বন কর তাহলে আল্লাহ তোমাদের পুরস্কার দিবেন এবং তিনি তোমাদের ধন সম্পদ চাননা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4582,47,37,'তোমাদের নিকট হতে তিনি তা চাইলে এবং তজ্জন্য তোমাদের উপর চাপ দিলে তোমরাতো কার্পন্য করবে, এবং তিনি তোমাদের মনের সংকীর্ণতা প্রকাশ করে দিবেন। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4583,47,38,'দেখ, তোমরাইতো তারা যাদের আল্লাহর পথে ব্যয় করতে বলা হচ্ছে, অথচ তোমাদের অনেকে কৃপণতা করছ; যারা কার্পণ্য করে তারাতো কার্পণ্য করে নিজেদেরই প্রতি। আল্লাহ অভাবমুক্ত এবং তোমরা অভাবগ্রস্ত, যদি তোমরা পৃষ্ঠ প্রদর্শন কর তাহলে তিনি অন্য জাতিকে তোমাদের স্থলবর্তী করবেন; তারা তোমাদের মত হবেনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4584,48,1,'নিশ্চয়ই আমি তোমাকে দিয়েছি সুস্পষ্ট বিজয়। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4585,48,2,'যেন আল্লাহ তোমার অতীত ও ভবিষ্যৎ ত্রুটিসমূহ মার্জনা করেন এবং তোমার প্রতি তাঁর অনুগ্রহ পূর্ণ করেন ও তোমাকে সরল পথে পরিচালিত করেন। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4586,48,3,'এবং তোমাকে আল্লাহ বলিষ্ঠ সাহায্য দান করেন। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4587,48,4,'তিনিই মু’মিনদের অন্তরে প্রশান্তি দান করেন যেন তারা তাদের ঈমানের সাথে ঈমান বৃদ্ধি করে নেয়; আকাশমন্ডলী ও পৃথিবীর বাহিনীসমূহ আল্লাহরই এবং আল্লাহই সর্বজ্ঞ, প্রজ্ঞাময়। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4588,48,5,'এটা এ জন্য যে, তিনি মু’মিন পুরুষ ও মু’মিন মহিলাদেরকে দাখিল করবেন জান্নাতে যার নিম্নদেশে নদী প্রবাহিত; সেখানে তারা স্থায়ী হবে এবং তিনি তাদের পাপ মোচন করবেন; এটাই আল্লাহর দৃষ্টিতে মহা সাফল্য।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4589,48,6,'এবং মুনাফিক পুরুষ ও মুনাফিক মহিলা, মুশরিক পুরুষ ও মুশরিক মহিলা, যারা আল্লাহ সম্বন্ধে মন্দ ধারণা পোষণ করে তাদেরকে শাস্তি দিবেন। অমঙ্গল চক্র তাদের জন্য, আল্লাহ তাদের প্রতি রুষ্ট হয়েছেন এবং তাদেরকে অভিশপ্ত করেছেন এবং তাদের জন্য জাহান্নাম প্রস্তুত রেখেছেন; ওটা কত নিকৃষ্ট আবাস! ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4590,48,7,'আল্লাহরই আকাশমন্ডলী ও পৃথিবীর বাহিনীসমূহ এবং আল্লাহই পরাক্রমশালী, প্রজ্ঞাময়। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4591,48,8,'আমি তোমাকে প্রেরণ করেছি সাক্ষীরূপে, সুসংবাদদাতা ও সতর্ককারীরূপে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4592,48,9,'যাতে তোমরা আল্লাহ ও তার রাসূলের প্রতি ঈমান আন এবং রাসূলকে সাহায্য কর ও সম্মান কর; সকাল সন্ধ্যায় আল্লাহর পবিত্রতা ও মহিমা ঘোষণা কর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4593,48,10,'যারা তোমার বাইআত গ্রহণ করে তারাতো আল্লাহরই বাইআত গ্রহণ করে। আল্লাহর হাত তাদের হাতের উপর। সুতরাং যে ওটা ভঙ্গ করে, ওটা ভঙ্গ করার পরিনাম তারই এবং যে আল্লাহর সাথে অঙ্গীকার পূর্ণ করে তিনি তাকে মহা পুরস্কার দেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4594,48,11,'যে সব আরাব মরুবাসী গৃহে রয়ে গেছে তারা তোমাকে বলবেঃ আমাদের ধন সম্পদ ও পরিবার পরিজন আমাদেরকে ব্যস্ত রেখেছে। অতএব আমাদের জন্য ক্ষমা প্রার্থনা করুন। তারা মুখে যা বলে তা তাদের অন্তরে নেই। তাদেরকে বলঃ আল্লাহ তোমাদের কারও কোন ক্ষতি কিংবা মঙ্গল সাধনের ইচ্ছা করলে কে তাকে নিবৃত্ত করতে পারে? বস্তুতঃ তোমরা যা কর সে বিষয়ে আল্লাহ সম্যক অবহিত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4595,48,12,'না, তোমরা ধারণা করেছিলে যে, রাসূল ও মু’মিনগণ তাদের পরিবার পরিজনের নিকট কখনই ফিরে আসতে পারবেনা এবং এই ধারণা তোমাদের অন্তরে প্রীতিকর মনে হয়েছিল; তোমরা মন্দ ধারণা করেছিলে, তোমরাতো ধ্বংসম্মুখ এক সম্প্রদায়। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4596,48,13,'যারা আল্লাহ ও তাঁর রাসূলের প্রতি ঈমান আনেনা, আমি সেই সব কাফিরের জন্য জ্বলন্ত অগ্নি প্রস্তুত করে রেখেছি। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4597,48,14,'আকাশমন্ডলী ও পৃথিবীর সার্বভৌমত্ব আল্লাহর; তিনি যাকে ইচ্ছা ক্ষমা করেন এবং যাকে ইচ্ছা শাস্তি দেন। তিনি ক্ষমাশীল ও পরম দয়ালু। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4598,48,15,'তোমরা যখন যুদ্ধলদ্ধ সম্পদ সংগ্রহের জন্য যাবে তখন যারা গৃহে রয়ে গিয়েছিল তারা বলবেঃ আমাদেরকে তোমাদের সাথে যেতে দাও। তারা আল্লাহর প্রতিশ্রুতি পরিবর্তন করতে চায়। বলঃ তোমরা কিছুতেই আমাদের সঙ্গী হতে পারবেনা। আল্লাহ পূর্বেই এরূপ ঘোষণা করেছেন। তারা বলবেঃ তোমরাতো আমাদের প্রতি বিদ্বেষ পোষণ করেছ। বস্তুতঃ তাদের বোধশক্তি সামান্য। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4599,48,16,'যে সব আরাব মরুবাসী গৃহে রয়ে গিয়েছিল তাদেরকে বলঃ তোমরা আহুত হবে এক প্রবল পরাক্রান্ত জাতির সাথে যুদ্ধ করতে; তোমরা তাদের সাথে যুদ্ধ করবে যতক্ষণ না তারা আত্মসমর্পণ করে। তোমরা এই নির্দেশ পালন করলে আল্লাহ তোমাদেরকে উত্তম পুরস্কার দান করবেন। আর তোমরা যদি পূর্বানুরূপ পৃষ্ঠ প্রদর্শন কর তাহলে তিনি তোমাদেরকে মর্মন্তদ শাস্তি দিবেন। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4600,48,17,'অন্ধের জন্য, খঞ্জের জন্য, রুগ্নের জন্য কোনো অপরাধ নেই। এবং যে কেহ আল্লাহ ও তাঁর রাসূলের আনুগত্য করবে আল্লাহ তাকে দাখিল করবেন জান্নাতে, যার নিম্নদেশে নদী প্রবাহিত; কিন্তু যে ব্যক্তি পৃষ্ঠ প্রদর্শন করবে তিনি তাকে বেদনাদায়ক শাস্তি দিবেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4601,48,18,'মু’মিনরা যখন বৃক্ষতলে তোমার নিকট বাইআত গ্রহণ করল তখন আল্লাহ তাদের প্রতি সন্তুষ্ট হলেন, তাদের অন্তরে যা ছিল তা তিনি অবগত ছিলেন; তাদেরকে তিনি দান করলেন প্রশান্তি এবং তাদেরকে পুরস্কার দিলেন আসন্ন বিজয়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4602,48,19,'এবং বিপুল পরিমাণ যুদ্ধ লদ্ধ সম্পদ যা তারা হস্তগত করবে; আল্লাহ পরাক্রমশালী, প্রজ্ঞাময়। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4603,48,20,'আল্লাহ তোমাদেরকে প্রতিশ্রুতি দিয়েছেন যুদ্ধে লভ্য বিপুল সম্পদের যার অধিকারী হবে তোমরা। তিনি ইহা তোমাদের জন্য ত্বরান্বিত করেছিলেন এবং তিনি তোমাদের হতে মানুষের হস্ত নিবারিত করেছেন যেন তোমরা কৃতজ্ঞ হও এবং এটা মু’মিনদের জন্য এক নিদর্শন এবং আল্লাহ তোমাদেরকে পরিচালিত করেন সরল পথে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4604,48,21,'আরও বহু সম্পদ রয়েছে যা এখনো তোমাদের অধিকারে আসেনি, ওটাতো আল্লাহর নিকট রক্ষিত আছে। আল্লাহ সর্ব বিষয়ে সর্বশক্তিমান। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4605,48,22,'কাফিরেরা তোমাদের মুকাবিলা করলে পরিণামে তারা অবশ্যই পৃষ্ঠ প্রদর্শন করত, তখন তারা কোন অভিভাবক ও সাহায্যকারী পেতনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4606,48,23,'ইহাই আল্লাহর বিধান, প্রাচীনকাল হতে চলে আসছে; তুমি আল্লাহর এই বিধানে কোন পরিবর্তন পাবেনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4607,48,24,'তিনি মাক্কা উপত্যকায় তাদের হাত তোমাদের হতে এবং তোমাদের হাত তাদের হতে নিবারিত করেছেন তাদের উপর তোমাদেরকে বিজয়ী করার পর। তোমরা যা কিছু কর আল্লাহ তা দেখেন। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4608,48,25,'তারাইতো কুফরী করেছিল এবং নিবৃত্ত করেছিল তোমাদেরকে মাসজিদুল হারাম হতে এবং বাধা দিয়েছিল কুরবানীর জন্য আবদ্ধ পশুগুলিকে যথাস্থানে পৌঁছতে। তোমাদের যুদ্ধের আদেশ দেয়া হত, যদি না থাকত এমন কতকগুলি মু’মিন নর ও নারী যাদেরকে তোমরা জাননা, তাদেরকে তোমরা পদদলিত করতে অজ্ঞাতসারে। ফলে তাদের কারণে তোমরা ক্ষতিগ্রস্ত হতে। যুদ্ধের নির্দেশ দেয়া হয়নি এ জন্য যে, তিনি যাকে ইচ্ছা নিজ অনুগ্রহ দান করবেন, যদি তারা পৃথক হত, আমি তাদের মধ্যস্থিত কাফিরদেরকে মর্মন্তদ শাস্তি দিতাম। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4609,48,26,'যখন কাফিরেরা তাদের অন্তরে পোষণ করত গোত্রীয় অহমিকা-অজ্ঞতা যুগের অহমিকা, তখন আল্লাহ তাঁর রাসূল ও মু’মিনদেরকে স্বীয় প্রশান্তি দান করলেন; আর তাদেরকে তাকওয়ার বাক্যে সুদৃঢ় করলেন এবং তারাই ছিল এর অধিকতর যোগ্য ও উপযুক্ত। আল্লাহ সমস্ত বিষয়ে সম্যক জ্ঞান রাখেন। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4610,48,27,'নিশ্চয়ই আল্লাহ তাঁর রাসূলের স্বপ্ন বাস্তবায়িত করেছেন, আল্লাহর ইচ্ছায় তোমরা অবশ্যই মাসজিদুল হারামে প্রবেশ করবে নিরাপদে - কেহ কেহ মাথা মুন্ডন করবে, কেহ কেহ কেশ কর্তন করবে; তোমাদের কোন ভয় থাকবেনা। আল্লাহ জানেন, তোমরা যা জাননা। এটা ছাড়াও তিনি তোমাদের দিয়েছেন এক সদ্য বিজয়। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4611,48,28,'তিনি তাঁর রাসূলকে পথ নির্দেশ ও সত্য দীনসহ প্রেরণ করেছেন, অপর সমস্ত দীনের উপর একে জয়যুক্ত করার জন্য। সাক্ষী হিসাবে আল্লাহই যথেষ্ট। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4612,48,29,'মুহাম্মাদ আল্লাহর রাসূল; তার সহচরগণ কাফিরদের প্রতি কঠোর এবং নিজেদের মধ্যে পরস্পরের প্রতি সহানুভূতিশীল; আল্লাহর অনুগ্রহ ও সন্তুষ্টি কামনায় তুমি তাদেরকে রুকু ও সাজদাহয় অবনত দেখবে। তাদের মুখে সাজদাহর চিহ্ন থাকবে, তাওরাতে তার বর্ণনা এইরূপই এবং ইঞ্জীলেও। তাদের দৃষ্টান্ত একটি চারাগাছ, যা হতে নিগর্ত হয় কিশলয়, অতঃপর ওটা শক্ত পুষ্ট হয় এবং পরে কান্ডের উপর দাঁড়ায় দৃঢ়ভাবে যা কৃষকের জন্য আনন্দদায়ক। এভাবে আল্লাহ মু’মিনদের সমৃদ্ধি দ্বারা কাফিরদের অন্তরজ্বালা সৃষ্টি করেন। যারা ঈমান আনে ও সৎ কাজ করে আল্লাহ তাদেরকে প্রতিশ্রুতি দিয়েছেন ক্ষমা ও পুরস্কারের।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4613,49,1,'হে মু’মিনগণ! আল্লাহ ও তাঁর রাসূলের সামনে তোমরা কোন বিষয়ে অগ্রনী হয়োনা এবং আল্লাহকে ভয় কর, নিশ্চয়ই আল্লাহ সর্বশ্রোতা, সর্বজ্ঞ। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4614,49,2,'হে মু’মিনগণ! তোমরা নাবীর কন্ঠস্বরের উপর নিজেদের কন্ঠস্বর উচু করনা এবং নিজেদের মধ্যে যেভাবে উচ্চৈঃস্বরে কথা বল তার সাথে সেই রূপ উচ্চৈঃস্বরে কথা বলনা; কারণ এতে তোমাদের কাজ নিস্ফল হয়ে যাবে তোমাদের অজ্ঞাতসারে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4615,49,3,'যারা আল্লাহর রাসূলের সামনে নিজেদের কন্ঠস্বর নীচু করে, আল্লাহ তাদের অন্তরকে তাকওয়ার জন্য পরিশোধিত করেছেন। তাদের জন্য রয়েছে ক্ষমা ও মহা পুরস্কার।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4616,49,4,'যারা ঘরের পিছন হতে তোমাকে উচ্চৈঃস্বরে ডাকে তাদের অধিকাংশই নির্বোধ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4617,49,5,'তুমি বের হয়ে তাদের নিকট আসা পর্যন্ত যদি তারা ধৈর্য ধারণ করত তাহলে তা’ই তাদের জন্য উত্তম হত। আল্লাহ ক্ষমাশীল, পরম দয়ালু। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4618,49,6,'হে মু’মিনগণ! যদি কোন পাপাচারী তোমাদের নিকট কোন বার্তা আনয়ণ করে, তোমরা তা পরীক্ষা করে দেখবে যাতে অজ্ঞতা বশতঃ তোমরা কোন সম্প্রদায়কে ক্ষতিগ্রস্ত না কর এবং পরে তোমাদের কৃতকর্মের জন্য অনুতপ্ত না হও।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4619,49,7,'তোমরা জেনে রেখ যে, তোমাদের মধ্যে আল্লাহর রাসূল রয়েছেন; তিনি বহু বিষয়ে তোমাদের কথা মেনে নিলে তোমরাই কষ্ট পাবে। কিন্তু আল্লাহ তোমাদের নিকট ঈমানকে প্রিয় করেছেন এবং উহা তোমাদের হৃদয়গ্রাহী করেছেন; কুফরী, পাপাচার ও অবাধ্যতাকে করেছেন তোমাদের নিকট অপ্রিয়। ওরাই সৎ পথ অবলম্বনকারী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4620,49,8,'এটা আল্লাহর দান ও অনুগ্রহ; আল্লাহ সর্বজ্ঞ, প্রজ্ঞাময়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4621,49,9,'মু’মিনদের দুই দল দ্বন্দ্বে লিপ্ত হলে তোমরা তাদের মধ্যে মীমাংসা করে দিবে; অতঃপর তাদের একদল অপর দলকে আক্রমণ করলে আক্রমণকারী দলের বিরুদ্ধে যুদ্ধ করবে যতক্ষণ না তারা আল্লাহর নির্দেশের দিকে ফিরে আসে। যদি তারা ফিরে আসে তাহলে তাদের মধ্যে ন্যায়ের সাথে ফাইসালা করবে এবং সুবিচার করবে। নিশ্চয়ই আল্লাহ সুবিচারকারীদেরকে ভালবাসেন। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4622,49,10,'মু’মিনরা পরস্পর ভাই ভাই। সুতরাং তোমরা দুই ভাইয়ের মধ্যে মীমাংসা করে দাও এবং আল্লাহকে ভয় কর যাতে তোমরা অনুগ্রহ প্রাপ্ত হও। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4623,49,11,'হে মু’মিনগণ! কোন পুরুষ যেন অপর কোন পুরুষকে উপহাস না করে; কেননা যাকে উপহাস করা হয় সে উপহাসকারী অপেক্ষা উত্তম হতে পারে এবং কোন মহিলা অপর কোন মহিলাকেও যেন উপহাস না করে; কেননা যাকে উপহাস করা হয় সে উপহাসকারিনী অপেক্ষা উত্তম হতে পারে। তোমরা একে অপরের প্রতি দোষারোপ করনা এবং তোমরা একে অপরকে মন্দ নামে ডেকনা; ঈমান আনার পর মন্দ নামে ডাকা গর্হিত কাজ। যারা এ ধরণের আচরণ হতে নিবৃত্ত না হয় তারাই যালিম। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4624,49,12,'হে মু’মিনগণ! তোমরা বহুবিধ অনুমান হতে দূরে থাক; কারণ অনুমান কোন কোন ক্ষেত্রে পাপ এবং তোমরা একে অপরের গোপনীয় বিষয় অনুসন্ধান করনা এবং একে অপরের পশ্চাতে নিন্দা করনা। তোমাদের মধ্যে কি কেহ তার মৃত ভাইয়ের মাংস ভক্ষণ করতে চায়? বস্তুতঃ তোমরাতো এটাকে ঘৃণাই মনে কর। তোমরা আল্লাহকে ভয় কর। আল্লাহ তাওবাহ গ্রহণকারী, পরম দয়ালু। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4625,49,13,'হে মানুষ! আমি তোমাদেরকে সৃষ্টি করেছি এক পুরুষ ও এক নারী হতে, পরে তোমাদেরকে বিভক্ত করেছি বিভিন্ন জাতি ও গোত্রে, যাতে তোমরা একে অপরের সাথে পরিচিত হতে পার। তোমাদের মধ্যে ঐ ব্যক্তিই আল্লাহর নিকট অধিক মর্যাদা সম্পন্ন যে অধিক মুত্তাকী। আল্লাহ সব কিছু জানেন, সব কিছুর খবর রাখেন। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4626,49,14,'আরাব মরুবাসীরা বলেঃ আমরা ঈমান আনলাম। তুমি বলঃ তোমরা ঈমান আননি, বরং তোমরা বলঃ আমরা আত্মসমর্পন করেছি; কারণ ঈমান এখনো তোমাদের অন্তরে প্রবেশ করেনি। যদি তোমরা আল্লাহ ও তাঁর রাসূলের আনুগত্য কর তাহলে তোমাদের কর্মফল সামান্য পরিমানও কম করা হবেনা। আল্লাহ ক্ষমাশীল, পরম দয়ালু। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4627,49,15,'তারাই মু’মিন যারা আল্লাহ ও তাঁর রাসূলের প্রতি ঈমান আনার পর সন্দেহ পোষণ করেনা এবং জীবন ও সম্পদ দ্বারা আল্লাহর পথে সংগ্রাম করে, তারাই সত্যনিষ্ঠ। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4628,49,16,'বলঃ তোমরা কি তোমাদের দীন সম্পর্কে আল্লাহকে অবহিত করছ? অথচ আল্লাহ জানেন যা কিছু আছে আকাশমন্ডলী ও পৃথিবীতে। আল্লাহ সর্ব বিষয়ে সম্যক অবহিত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4629,49,17,'তারা মনে করে আত্মসমর্পণ করে তোমাকে ধন্য করেছে। বলঃ তোমাদের আত্মসমর্পণ আমাকে ধন্য করেছে মনে করনা। বরং আল্লাহই ঈমানের দিকে পরিচালিত করে তোমাদের ধন্য করেছেন, যদি তোমরা সত্যবাদী হও। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4630,49,18,'আল্লাহ আকাশমন্ডলী ও পৃথিবীর অদৃশ্য বিষয় সম্পর্কে অবগত আছেন। তোমরা যা কর আল্লাহ তা দেখেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4631,50,1,'কাফ, শপথ কুরআনের (তুমি অবশ্যই সতর্ককারী)।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4632,50,2,'কিন্তু কাফিরেরা তাদের মধ্য হতে একজন সতর্ককারী আবির্ভূত হতে দেখে বিস্ময় বোধ করে এবং বলেঃ এটাতো এক আশ্চর্য ব্যাপার। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4633,50,3,'আমাদের মৃত্যু হলে এবং আমরা মৃত্তিকায় পরিণত হলে আমরা কি পুনরুজ্জীবিত হব? এ প্রত্যাবর্তন সুদূর পরাহত! ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4634,50,4,'আমিতো জানি, মৃত্তিকা ক্ষয় করে তাদের কতটুকু এবং আমার নিকট আছে রক্ষিত ফলক। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4635,50,5,'বস্তুতঃ তাদের নিকট সত্য আসার পর তা প্রত্যাখ্যান করেছে। ফলে তারা সংশয়ে দোদুল্যমান। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4636,50,6,'তারা কি তাদের উর্ধ্বস্থিত আকাশের দিকে তাকিয়ে দেখেনা যে, আমি কিভাবে ওটা নির্মাণ করেছি এবং ওকে সুশোভিত করেছি এবং ওতে কোন ফাটলও নেই? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4637,50,7,'আমি বিস্তৃত করেছি ভূমিকে ও তাতে স্থাপন করেছি পর্বতমালা এবং ওতে উদ্গত করেছি নয়ন প্রীতিকর সর্বপ্রকার উদ্ভিদ – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4638,50,8,'আল্লাহর অনুরাগী প্রত্যেক ব্যক্তির জন্য, জ্ঞান ও উপদেশ স্বরূপ। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4639,50,9,'আকাশ হতে আমি বর্ষণ করি কল্যাণকর বৃষ্টি এবং তদ্বারা আমি সৃষ্টি করি উদ্যান ও উদগত করি শস্য। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4640,50,10,'ও সমুন্নত খেজুর বৃক্ষ, যাতে আছে গুচ্ছ খেজুর – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4641,50,11,'আমার বান্দাদের জীবিকা স্বরূপ; আর আমি সঞ্জীবিত করি মৃত ভূমিকে; এভাবে পুনরুত্থান ঘটবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4642,50,12,'তাদের পূর্বেও সত্য প্রত্যাখ্যান করেছিল নূহের সম্প্রদায়, রা''স ও সামূদ সম্প্রদায় –');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4643,50,13,'আদ, ফির‘আউন ও লূত সম্প্রদায়। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4644,50,14,'এবং আইকাহর অধিবাসী ও তুব্বা সম্প্রদায়; তারা সবাই রাসূলদেরকে মিথ্যাবাদী বলেছিল। ফলে তাদের উপর আমার শাস্তি আপতিত হয়েছে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4645,50,15,'আমি কি প্রথমবার সৃষ্টি করেই ক্লান্ত হয়ে পড়েছি যে, পুনঃসৃষ্টি বিষয়ে তারা সন্দেহ পোষণ করছে?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4646,50,16,'আমিই মানুষকে সৃষ্টি করেছি এবং তার প্রকৃতি তাকে যে কুমন্ত্রনা দেয় তা আমি জানি। আমি তার গ্রীবাস্থিত ধমণী অপেক্ষাও নিকটতর। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4647,50,17,'স্মরণ রেখ, দুই মালাক/ফেরেশতা তার ডানে ও বামে বসে তার কাজ লিপিবদ্ধ করে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4648,50,18,'মানুষ যে কথাই উচ্চারণ করে তা গ্রহণ করার জন্য তার কাছে সদা প্রস্তুত প্রহরী রয়েছে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4649,50,19,'মৃত্যুযন্ত্রণা অবশ্যই আসবে। এটা হতেই তোমরা অব্যাহিত চেয়ে আসছ। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4650,50,20,'আর শিঙ্গায় ফুৎকার দেয়া হবে, ওটাই সেই ভয় প্রদর্শনের দিন। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4651,50,21,'সেদিন প্রত্যেক ব্যক্তি উপস্থিত হবে। তার সাথে থাকবে চালক ও তার কর্মের সাক্ষী। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4652,50,22,'তুমি এই দিন সম্বন্ধে উদাসীন ছিলে, এখন তোমার সম্মুখ হতে পর্দা উম্মোচন করেছি। অদ্য তোমার দৃষ্টি প্রখর। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4653,50,23,'তার সঙ্গী মালাক/ফেরেশতা বলবেঃ এইতো আমার নিকট ‘আমলনামা প্রস্তুত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4654,50,24,'আদেশ করা হবেঃ তোমরা উভয়েই নিক্ষেপ কর জাহান্নামে, প্রত্যেক উদ্ধত কাফিরকে – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4655,50,25,'কল্যাণকর কাজে প্রবল বাধা দানকারী, সীমালংঘনকারী ও সন্দেহ পোষণকারীকে –');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4656,50,26,'যে ব্যক্তি আল্লাহর সাথে অন্য মা‘বূদ গ্রহণ করত তাকে কঠিন শাস্তিতে নিক্ষেপ কর। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4657,50,27,'তার সহচর শাইতান বলবেঃ হে আমাদের রাব্ব! আমি তাকে অবাধ্য হতে প্ররোচিত করিনি। বস্তুতঃ সে নিজেই ছিল ঘোর বিভ্রান্ত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4658,50,28,'আল্লাহ বলবেনঃ আমার সামনে বাক-বিতন্ডা করনা; তোমাদেরকে আমিতো পূবেই সতর্ক করেছি। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4659,50,29,'আমার কথার রদ বদল হয়না এবং আমি আমার বান্দাদের প্রতি কোন অবিচার করিনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4660,50,30,'সেই দিন আমি জাহান্নামকে জিজ্ঞেস করবঃ তুমি কি পূর্ণ হয়ে গেছ? জাহান্নাম বলবেঃ আরও আছে কি? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4661,50,31,'আর জান্নাতকে মুত্তাকীদের নিকটস্থ করা হবে - কোন দূরত্ব থাকবেনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4662,50,32,'এরই প্রতিশ্রুতি তোমাদেরকে দেয়া হয়েছিল - প্রত্যেক আল্লাহর অনুরাগী, হিফাযাতকারীর জন্য। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4663,50,33,'যারা না দেখেই দয়াময় আল্লাহকে ভয় করে এবং বিনীত চিত্তে উপস্থিত হয় – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4664,50,34,'তাদেরকে বলা হবেঃ শান্তির সাথে তোমরা তাতে প্রবেশ কর; এটা অনন্ত জীবনের দিন। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4665,50,35,'সেখানে তারা যা কামনা করবে তা’ই পাবে এবং আমার নিকট রয়েছে তারও অধিক।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4666,50,36,'আমি তাদের পূর্বে আরও কত মানব গোষ্ঠীকে ধ্বংস করেছি যারা ছিল তাদের অপেক্ষা শক্তিতে প্রবল, তারা দেশ-বিদেশে ভ্রমণ করে ফিরত। পরে তাদের অন্য কোন আশ্রয়স্থল রইলনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4667,50,37,'এতে উপদেশ রয়েছে তার জন্য যার আছে অন্তঃকরণ, অথবা যে শ্রবণ করে নিবিষ্ট চিত্তে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4668,50,38,'আমি আকাশমন্ডলী ও পৃথিবী এবং এগুলির মধ্যস্থিত সব কিছু সৃষ্টি করেছি ছয় দিনে। আমাকে কোন ক্লান্তি স্পর্শ করেনি। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4669,50,39,'অতএব তারা যা বলে তাতে তুমি ধৈর্য ধারণ কর এবং তোমার রবের প্রশংসা, পবিত্রতা ও মহিমা ঘোষণা কর সূর্যোদয় ও সূর্যাস্তের পূর্বে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4670,50,40,'তাঁর পবিত্রতা ও মহিমা ঘোষণা কর রাতের একাংশে এবং সালাতের পরেও।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4671,50,41,'শোন, যেদিন এক ঘোষণাকারী নিকটবর্তী স্থান হতে আহবান করবে – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4672,50,42,'যেদিন মানুষ অবশ্যই শ্রবণ করবে মহানাদ, সেই দিনই পুনরুত্থান দিন। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4673,50,43,'আমিই জীবন দান করি, মৃত্যু ঘটাই এবং সকলের প্রত্যাবর্তন আমারই দিকে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4674,50,44,'যেদিন পৃথিবী বিদীর্ণ হবে এবং মানুষ বের হয়ে আসবে ব্যস্ত ত্রস্ত হয়ে, এই সমবেত সমাবেশ করণ আমার জন্য সহজ। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4675,50,45,'তারা যা বলে তা আমি জানি, তুমি তাদের উপর জবরদস্তিকারী নও। সুতরাং যে আমার শাস্তিকে ভয় করে তাকে উপদেশ দান কর কুরআনের সাহায্যে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4676,51,1,'শপথ ধূলি ঝঞ্ঝার, ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4677,51,2,'শপথ বোঝা বহনকারী মেঘপুঞ্জের, ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4678,51,3,'অতঃপর স্বচ্ছন্দ গতিময় নৌযানের, ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4679,51,4,'আর শপথ কর্মবন্টনকারী মালাইকা/ফেরেশতার। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4680,51,5,'তোমাদেরকে প্রদত্ত প্রতিশ্রুতি অবশ্যই সত্য।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4681,51,6,'কর্মফল দিন অবশ্যম্ভাবী। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4682,51,7,'শপথ বহু পথ বিশিষ্ট আকাশের! ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4683,51,8,'তোমরাতো পরস্পর বিরোধী কথায় লিপ্ত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4684,51,9,'যে ব্যক্তি সত্যভ্রষ্ট সে’ই তা পরিত্যাগ করে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4685,51,10,'অভিশপ্ত হোক মিথ্যাচারীরা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4686,51,11,'যারা অজ্ঞ ও উদাসীন – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4687,51,12,'তারা জিজ্ঞেস করেঃ কর্মফল দিন কবে হবে? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4688,51,13,'(বলঃ) সেই দিন, যখন তাদেরকে শাস্তি দেয়া হবে অগ্নিতে, ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4689,51,14,'এবং (বলা হবে) তোমরা তোমাদের শাস্তি আস্বাদন কর, তোমরা এই শাস্তিই ত্বরান্বিত করতে চেয়েছিলে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4690,51,15,'সেদিন মুত্তাকীরা থাকবে প্রস্রবণ বিশিষ্ট জান্নাতে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4691,51,16,'উপভোগ করবে তা যা তাদের রাব্ব তাদেরকে প্রদান করবেন; কারণ পার্থিব জীবনে তারা ছিল সৎকর্মপরায়ণ। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4692,51,17,'তারা রাতের সামান্য অংশই অতিবাহিত করত নিদ্রায়,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4693,51,18,'রাতের শেষ প্রহরে তারা ক্ষমা প্রার্থনা করত, ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4694,51,19,'এবং তাদের ধন সম্পদে রয়েছে অভাবগ্রস্ত ও বঞ্চিতের হক। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4695,51,20,'নিশ্চিত বিশ্বাসীদের নিদর্শন রয়েছে ধরিত্রীতে – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4696,51,21,'এবং তোমাদের মধ্যেও। তোমরা কি অনুধাবন করবেনা? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4697,51,22,'আকাশে রয়েছে তোমাদের রিযকের উৎস ও প্রতিশ্রুত সবকিছু।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4698,51,23,'আকাশ ও পৃথিবীর রবের শপথ! অবশ্যই তোমাদের বাক স্ফুর্তির মতই এ সব সত্য। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4699,51,24,'তোমার নিকট ইবরাহীমের সম্মানিত মেহমানদের বৃত্তান্ত এসেছে কি? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4700,51,25,'যখন তারা তার নিকট উপস্থিত হয়ে বললঃ সালাম। উত্তরে সে বললঃ সালাম। এরাতো অপরিচিত লোক! ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4701,51,26,'অতঃপর সে গৃহাভ্যন্তরে গেল এবং একটি মাংশল ভাজা গো-বৎস নিয়ে এল। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4702,51,27,'তাদের সামনে রাখল এবং বললঃ তোমরা খাচ্ছনা কেন? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4703,51,28,'এতে তাদের সম্পর্কে তার মনে ভীতির সঞ্চার হল। তারা বললঃ ভীত হয়োনা। অতঃপর তারা তাকে এক জ্ঞানী পুত্র সন্তানের সুসংবাদ দিল। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4704,51,29,'তখন তার স্ত্রী চিৎকার করতে করতে সামনে এসে গাল চাপড়িয়ে বললঃ এই বৃদ্ধা বন্ধ্যার সন্তান হবে?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4705,51,30,'তারা বললঃ তোমার রাব্ব এরূপই বলেছেন; তিনি প্রজ্ঞাময়, সর্বজ্ঞ। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4706,51,31,'সে (ইবরাহীম) বললঃ হে প্রেরিত মালাইকা/ফেরেশতাগণ! আপনাদের বিশেষ কাজ কি? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4707,51,32,'তারা বললঃ আমাদেরকে এক অপরাধী সম্প্রদায়ের নিকট প্রেরণ করা হয়েছে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4708,51,33,'তাদের উপর নিক্ষেপ করার জন্য মাটির শক্ত ঢেলা, ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4709,51,34,'যা সীমালংঘনকারীদের জন্য চিহ্নিত তোমার রবের নিকট হতে ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4710,51,35,'সেখানে যেসব মু’মিন ছিল আমি তাদেরকে উদ্ধার করেছিলাম –');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4711,51,36,'এবং সেখানে একটি পরিবার ব্যতীত কোন আত্মসমর্পনকারী (মুসলিম) আমি পাইনি –');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4712,51,37,'যারা যন্ত্রণাদায়ক শাস্তিকে ভয় করে আমি তাদের জন্য ওতে একটি নিদর্শন রেখেছি, ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4713,51,38,'এবং নিদর্শন রেখেছি মূসার বৃত্তান্তে, যখন আমি তাকে প্রমাণসহ ফির‘আউনের নিকট প্রেরণ করেছিলাম। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4714,51,39,'তখন সে ক্ষমতা দম্ভে মুখ ফিরিয়ে নিল এবং বললঃ এই ব্যক্তি হয় এক যাদুকর, না হয় উম্মাদ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4715,51,40,'সুতরাং আমি তাকে ও তার দলবলকে শাস্তি দিলাম এবং তাদেরকে সমুদ্রে নিক্ষেপ করলাম; সেতো ছিল তিরস্কারযোগ্য। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4716,51,41,'এবং নিদর্শন রয়েছে আ‘দের ঘটনায় যখন আমি তাদের বিরুদ্ধে প্রেরণ করেছিলাম অকল্যাণকর বায়ু। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4717,51,42,'এটা যা কিছুর উপর দিয়ে বয়ে গিয়েছিল তাকেই চূর্ন বিচূর্ণ করে দিয়েছিল। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4718,51,43,'আরও নিদর্শন রয়েছে সামূদের বৃত্তান্তে, যখন তাদেরকে বলা হলঃ ভোগ করে নাও স্বল্পকাল,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4719,51,44,'কিন্তু তারা তাদের রবের আদেশ অমান্য করল; ফলে তাদের প্রতি বজ্রাঘাত হল এবং তারা তা দেখতে ছিল। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4720,51,45,'তারা উঠে দাঁড়াতে পারলনা এবং তা প্রতিরোধ করতেও পারলনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4721,51,46,'আমি ধ্বংস করেছিলাম তাদের পূর্বে নূহের সম্প্রদায়কে, তারা ছিল সত্যত্যাগী সম্প্রদায়। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4722,51,47,'আমি আকাশ নির্মাণ করেছি আমার ক্ষমতা বলে এবং আমি অবশ্যই মহাসম্প্রসারণকারী, ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4723,51,48,'এবং আমি ভূমিকে বিছিয়ে দিয়েছি; আমি কত সুন্দরভাবে বিছিয়েছি এটা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4724,51,49,'আমি প্রত্যেক বস্তু সৃষ্টি করেছি জোড়ায় জোড়ায়, যাতে তোমরা উপদেশ গ্রহণ কর। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4725,51,50,'আল্লাহর দিকে ধাবিত হও; আমি তোমাদের প্রতি আল্লাহ কর্তৃক প্রেরিত সতর্ককারী। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4726,51,51,'তোমরা আল্লাহর সাথে কোন মা‘বূদ স্থির করনা; আমি তোমাদের প্রতি আল্লাহ কর্তৃক প্রেরিত স্পষ্ট সতর্ককারী ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4727,51,52,'এভাবে তাদের পূর্ববর্তীদের নিকট যখনই কোন রাসূল এসেছে, তারা বলেছেঃ তুমিতো এক যাদুকর, না হয় উম্মাদ!');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4728,51,53,'তারা কি একে অপরকে এই মন্ত্রণাই দিয়ে এসেছে? বস্তুতঃ তারা এক সীমালংঘনকারী সম্প্রদায়। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4729,51,54,'অতএব তুমি তাদেরকে উপেক্ষা কর, এতে তুমি অপরাধী হবেনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4730,51,55,'তুমি উপদেশ দিতে থাক, কারণ উপদেশ মু’মিনদের উপকারে আসবে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4731,51,56,'আমি সৃষ্টি করেছি জিন ও মানুষকে এ জন্য যে, তারা আমারই ইবাদাত করবে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4732,51,57,'আমি তাদের নিকট হতে জীবিকা চাইনা এবং এও চাইনা যে, তারা আমার আহার যোগাবে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4733,51,58,'আল্লাহই রিয্ক দান করেন এবং তিনি প্রবল, পরাক্রান্ত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4734,51,59,'যালিমদের প্রাপ্য ওটাই যা অতীতে তাদের সম মতাবলম্বীরা ভোগ করেছে। সুতরাং তারা এর জন্য আমার নিকট যেন ত্বরা না করে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4735,51,60,'কাফিরদের জন্য দুর্ভোগ তাদের ঐ দিনের যে দিনের বিষয়ে তাদেরকে সতর্ক করা হয়েছে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4736,52,1,'শপথ তুর পর্বতের, ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4737,52,2,'শপথ কিতাবের, যা লিখিত আছে – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4738,52,3,'উন্মুক্ত পত্রে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4739,52,4,'শপথ বায়তুল মা‘মুরের, ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4740,52,5,'শপথ সমুন্নত আকাশের, ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4741,52,6,'এবং শপথ উদ্বেলিত সমুদ্রের।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4742,52,7,'তোমার রবের শাস্তি অবশ্যম্ভাবী, ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4743,52,8,'এর নিবারণ করার কেহ নেই। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4744,52,9,'যেদিন আকাশ আন্দোলিত হবে প্রবলভাবে – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4745,52,10,'এবং পর্বত চলবে দ্রুত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4746,52,11,'দুর্ভোগ সেইদিন মিথ্যাশ্রয়ীদের – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4747,52,12,'যারা ক্রীড়াচ্ছলে অসার কার্যকলাপে লিপ্ত থাকে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4748,52,13,'যেদিন তাদেরকে হাকিয়ে নিয়ে যাওয়া হবে জাহান্নামের আগুনের দিকে,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4749,52,14,'(বলা হবে) এটাই সেই আগুন যাকে তোমরা মিথ্যা মনে করতে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4750,52,15,'এটা কি যাদু? না কি তোমরা দেখছনা? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4751,52,16,'তোমরা এতে প্রবেশ কর, অতঃপর তোমরা ধৈর্য ধারণ কর অথবা না করা উভয়ই তোমাদের জন্য সমান। তোমরা যা করতে তোমাদেরকে তারই প্রতিফল দেয়া হচ্ছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4752,52,17,'মুত্তাকীরা থাকবে জান্নাতে ও ভোগ করবে বিলাস।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4753,52,18,'তাদের রাব্ব তাদেরকে যা দিবেন তারা তা উপভোগ করবে এবং তিনি তাদেরকে রক্ষা করবেন জাহান্নামের শাস্তি হতে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4754,52,19,'তোমরা যা করতে তার প্রতিফল স্বরূপ তোমরা তৃপ্তির সাথে পানাহার করতে থাক। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4755,52,20,'তারা বসবে শ্রেণীবদ্ধভাবে সজ্জিত আসনে হেলান দিয়ে; আমি তাদের মিলন ঘটাব আয়তলোচনা হুরের সঙ্গে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4756,52,21,'এবং যারা ঈমান আনে আর তাদের সন্তান-সন্ততি ঈমানে তাদের অনুগামী হয়, তাদের সাথে মিলিত করাব তাদের সন্তান-সন্ততিকে এবং তাদের কর্মফল আমি কিছুমাত্র হ্রাস করবনা, প্রত্যেক ব্যক্তি নিজ কৃতকর্মের জন্য দায়ী। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4757,52,22,'আমি তাদেরকে দিব ফল-মূল এবং গোশত যা তারা পছন্দ করে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4758,52,23,'সেখানে তারা একে অপরের নিকট হতে গ্রহণ করবে পান পাত্র, যা হতে পান করলে কেহ অসার কথা বলবেনা এবং অসৎ কাজেও লিপ্ত হবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4759,52,24,'সুরক্ষিত মুক্তা সদৃশ কিশোরেরা সেখানে তাদের জন্য নিয়োজিত থাকবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4760,52,25,'তারা একে অপরের দিকে মুখোমুখি জিজ্ঞসাবাদ করবে – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4761,52,26,'এবং বলবেঃ পূর্বে আমরা পরিবার-পরিজনের মধ্যে শংকিত অবস্থায় ছিলাম –');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4762,52,27,'অতঃপর আমাদের প্রতি আল্লাহ অনুগ্রহ করেছেন এবং আমাদেরকে অগ্নির শাস্তি হতে রক্ষা করেছেন,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4763,52,28,'আমরা পূর্বেও আল্লাহকে আহবান করতাম, তিনিতো কৃপাময়, পরম দয়ালু।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4764,52,29,'অতএব তুমি উপদেশ দান করতে থাক, তোমার রবের অনুগ্রহে তুমি গণক নও, উম্মাদও নও।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4765,52,30,'তারা কি বলতে চায় যে, সে একজন কবি, আমরা তার জন্য কালের বিপর্যয়ের প্রতীক্ষা করছি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4766,52,31,'বলঃ তোমরা প্রতীক্ষা কর, আমিও তোমাদের সাথে প্রতীক্ষা করছি। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4767,52,32,'তাহলে কি তাদের বুদ্ধি তাদেরকে এই বিষয়ে প্ররোচিত করে, না তারা এক সীমালংঘনকারী সম্প্রদায়?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4768,52,33,'তারা কি বলেঃ এই কুরআন তার নিজের রচনা? বরং তারা অবিশ্বাসী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4769,52,34,'তারা যদি সত্যবাদী হয় তাহলে এই সদৃশ কোন রচনা উপস্থিত করুক। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4770,52,35,'তারা কি স্রষ্টা ব্যতীত সৃষ্টি হয়েছে, না তারা নিজেরাই স্রষ্টা? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4771,52,36,'না কি তারা আকাশ ও পৃথিবী সৃষ্টি করেছে? বরং তারাতো অবিশ্বাসী ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4772,52,37,'তোমার রবের ভান্ডার কি তাদের নিকট রয়েছে, না তারা এ সমুদয়ের নিয়ন্তা?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4773,52,38,'না কি তাদের কোন সিড়ি আছে যাতে আরোহণ করে তারা শ্রবণ করে? থাকলে তাদের সেই শ্রোতা সুস্পষ্ট প্রমাণ উপস্থিত করুক।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4774,52,39,'তাহলে কি কন্যা সন্তান তাঁর জন্য এবং পুত্র সন্তান তোমাদের জন্য?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4775,52,40,'তাহলে কি তুমি তাদের নিকট পারিশ্রমিক চাচ্ছ যে, তারা একে একটি দুর্বহ বোঝা মনে করবে? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4776,52,41,'না কি অদৃশ্য বিষয়ে তাদের কোন জ্ঞান আছে যে, তারা এই বিষয়ে কিছু লিখে? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4777,52,42,'অথবা তারা কি কোন ষড়যন্ত্র করতে চায়? পরিণামে কাফিরেরাই হবে ষড়যন্ত্রের শিকার।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4778,52,43,'না কি আল্লাহ ব্যতীত তাদের অন্য কোন মা‘বূদ আছে? তারা যাকে শরীক স্থির করে আল্লাহ তা হতে পবিত্র।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4779,52,44,'তারা আকাশের কোন খন্ড ভেঙ্গে পড়তে দেখলেও বলবেঃ এটাতো এক পূঞ্জীভুত মেঘ। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4780,52,45,'তাদেরকে উপেক্ষা করে চল সেই দিন পর্যন্ত, যে দিন তারা বজ্রাঘাতের সম্মুখীন হবে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4781,52,46,'যেদিন তাদের ষড়যন্ত্র কোন কাজে আসবেনা এবং তাদেরকে সাহায্যও করা হবেনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4782,52,47,'এ ছাড়াও আরও শাস্তি রয়েছে যালিমদের জন্য। কিন্তু তাদের অধিকাংশই তা জানেনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4783,52,48,'ধৈর্য ধারণ কর তোমার রবের নির্দেশের অপেক্ষায়; তুমি আমার চোখের সামনেই রয়েছ। তুমি তোমার রবের প্রশংসা, পবিত্রতা ও মহিমা ঘোষণা কর যখন তুমি শয্যা ত্যাগ কর –');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4784,52,49,'এবং তাঁর পবিত্রতা ঘোষণা কর রাতে ও তারকার অস্ত গমনের পর। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4785,53,1,'শপথ নক্ষত্রের, যখন ওটা হয় অস্তমিত,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4786,53,2,'তোমাদের সঙ্গী বিভ্রান্ত নয়, বিপথগামীও নয়,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4787,53,3,'এবং সে মনগড়া কথাও বলেনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4788,53,4,'এটাতো অহী, যা তার প্রতি প্রত্যাদেশ হয়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4789,53,5,'তাকে শিক্ষা দান করে শক্তিশালী –');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4790,53,6,'প্রজ্ঞা সম্পন্ন; সে নিজ আকৃতিতে স্থির হয়েছিল, ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4791,53,7,'তখন সে ঊর্ধ্ব দিগন্তে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4792,53,8,' অতঃপর সে তার নিকটবর্তী হল, অতি নিকটবর্তী। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4793,53,9,'ফলে তাদের মধ্যে দুই ধনুকের ব্যবধান রইল, অথবা তারও কম। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4794,53,10,'তখন আল্লাহ তাঁর বান্দার প্রতি যা অহী করার তা অহী করলেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4795,53,11,'যা সে দেখেছে তার অন্তকরণ তা অস্বীকার করেনি। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4796,53,12,'সে যা দেখেছে তোমরা কি সে বিষয়ে তার সংগে বিতর্ক করবে? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4797,53,13,'নিশ্চয়ই সে তাকে আরেকবার দেখেছিল। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4798,53,14,'সিদরাতুল মুনতাহার নিকট, ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4799,53,15,'যার নিকট অবস্থিত বাসোদ্যান।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4800,53,16,'যখন বৃক্ষটি, যদ্বারা আচ্ছাদিত হবার তদ্বারা ছিল আচ্ছাদিত, ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4801,53,17,'তার দৃষ্টি বিভ্রম হয়নি, দৃষ্টি লক্ষ্যচ্যূতও হয়নি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4802,53,18,'সেতো তার প্রতিপালকের মহান নিদর্শনাবলী দেখেছিল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4803,53,19,'তোমরা কি ভেবে দেখেছ লাত ও উযযা সম্বন্ধে? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4804,53,20,'এবং তৃতীয় আরেকটি ‘মানাত’ সম্বন্ধে? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4805,53,21,'তাহলে কি পুত্র-সন্তান তোমাদের জন্য এবং কন্যা-সন্তান আল্লাহর জন্য? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4806,53,22,'এ ধরণের বন্টনতো অসঙ্গত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4807,53,23,'এগুলির কতক নামমাত্র যা তোমাদের পূর্ব-পুরুষরা ও তোমরা রেখেছ, যার সমর্থনে আল্লাহ কোন দলীল প্রেরণ করেননি। তারাতো অনুমান এবং নিজেদের প্রবৃত্তিরই অনুসরণ করে, অথচ তাদের নিকট তাদের রবের পথনির্দেশ এসেছে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4808,53,24,'মানুষ যা চায় তাই কি সে পায়?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4809,53,25,'বস্তুতঃ ইহকাল ও পরকাল আল্লাহরই।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4810,53,26,'আকাশে কত মালাইকা/ফেরেশতা রয়েছে, তাদের কোন সুপারিশ ফলপ্রসু হবেনা যতক্ষণ আল্লাহ যাকে ইচ্ছা এবং যার প্রতি সন্তষ্ট তাকে অনুমতি না দেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4811,53,27,'যারা আখিরাতে বিশ্বাস করেনা তারাই নারীবাচক নাম দিয়ে থাকে মালাইকাদেরকে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4812,53,28,'অথচ এ বিষয়ে তাদের কোন জ্ঞান নেই, তারা শুধু অনুমানের অনুসরণ করে; সত্যের মুকাবিলায় অনুমানের কোন মূল্য নেই। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4813,53,29,'অতএব যে আমার স্মরণে বিমুখ তাকে উপেক্ষা করে চল; সেতো শুধু পার্থিব জীবন কামনা করে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4814,53,30,'তাদের জ্ঞানের দৌড় এই পর্যন্ত। তোমার রাব্বই ভাল জানেন কে তাঁর পথ হতে বিচ্যুত; তিনিই ভাল জানেন কে সৎপথ প্রাপ্ত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4815,53,31,'আকাশমন্ডলী ও পৃথিবীতে যা কিছু আছে তা আল্লাহরই। যারা মন্দ কাজ করে তাদেরকে তিনি দেন মন্দ ফল এবং যারা সৎ কাজ করে তাদেরকে দেন উত্তম পুরস্কার। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4816,53,32,'যারা বিরত থাকে গুরুতর পাপ ও অশ্লীল কার্য হতে, ছোট- খাট অপরাধ করলেও তোমার রবের ক্ষমা অপরিসীম; তিনি (আল্লাহ) তোমাদের সম্পর্কে সম্যক অবগত, যখন তিনি তোমাদেরকে সৃষ্টি করেছিলেন মাটি হতে এবং যখন তোমরা মাতৃগর্ভে ভ্রুণরূপে অবস্থান কর। অতএব তোমরা আত্মপ্রশংসা করনা, তিনিই সম্যক জানেন মুত্তাকী কে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4817,53,33,'তুমি কি দেখেছ সেই ব্যক্তিকে যে মুখ ফিরিয়ে নেয়; ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4818,53,34,'এবং দান করে সামান্যই, পরে বন্ধ করে দেয়? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4819,53,35,'তার কি অদৃশ্যের জ্ঞান আছে যে সে জানবে?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4820,53,36,'তাকে কি অবগত করা হয়নি যা আছে মূসার কিতাবে,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4821,53,37,'এবং ইবরাহীমের কিতাবে যে পালন করেছিল তার দায়িত্ব?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4822,53,38,'ওটা এই যে, কোন বহনকারী অপরের বোঝা বহন করবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4823,53,39,'আর এই যে, মানুষ তা’ই পায় যা সে করে,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4824,53,40,'আর এই যে, তার কাজ অচিরেই দেখানো হবে, ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4825,53,41,'অতঃপর তাকে দেয়া হবে পূর্ণ প্রতিদান।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4826,53,42,'আর এই যে, সব কিছুর সমাপ্তিতো তোমার রবের নিকট। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4827,53,43,'আর এই যে, তিনিই হাসান, তিনিই কাঁদান। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4828,53,44,'এবং এই যে, তিনিই মারেন, তিনিই বাঁচান, ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4829,53,45,'আর এই যে, তিনিই সৃষ্টি করেন যুগল পুরুষ ও নারী – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4830,53,46,'শুক্র বিন্দু হতে যখন তা স্খলিত হয়;');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4831,53,47,'আর এই যে, পুনরুত্থান ঘটানোর দায়িত্ব তাঁরই।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4832,53,48,'আর এই যে, তিনিই অভাবমুক্ত করেন ও সম্পদ দান করেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4833,53,49,'আর এই যে, তিনি ‘শি’রা’ নক্ষত্রের মালিক। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4834,53,50,'এবং এই যে, তিনিই প্রথম ‘আদ সম্প্রদায়কে ধ্বংস করেছিলেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4835,53,51,'এবং সামূদ সম্প্রদায়কেও, কেহকেও তিনি বাকী রাখেননি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4836,53,52,'আর এদের পূর্বে নূহের সম্প্রদায়কেও; তারা ছিল অতিশয় যালিম ও অবাধ্য।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4837,53,53,'উৎপাটিত আবাস ভূমিকে উল্টিয়ে নিক্ষেপ করেছিলেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4838,53,54,'ওকে আচ্ছন্ন করল কি সর্বগ্রাসী শাস্তি! ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4839,53,55,'তুমি তোমার রবের কোন্ অনুগ্রহ সম্পর্কে সন্দেহ পোষণ করবে?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4840,53,56,'অতীতের সতর্ককারীদের ন্যায় এই নাবীও এক সতর্ককারী; ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4841,53,57,'কিয়ামাত আসন্ন,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4842,53,58,'আল্লাহ ছাড়া কেহই এটা ব্যক্ত করতে সক্ষম নয়। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4843,53,59,'তোমরা কি এই কথায় বিস্ময় বোধ করছ! ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4844,53,60,'এবং হাসি- ঠাট্টা করছ! ক্রন্দন করছনা?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4845,53,61,'তোমরাতো উদাসীন,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4846,53,62,'অতএব আল্লাহকে সাজদাহ কর এবং তাঁর ইবাদাত কর। [সাজদাহ]');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4847,54,1,'কিয়ামাত আসন্ন, চাঁদ বিদীর্ণ হয়েছে,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4848,54,2,'তারা কোনো নিদর্শন দেখলে মুখ ফিরিয়ে নেয় এবং বলেঃ এটাতো চিরাচরিত যাদু।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4849,54,3,'তারা সত্য প্রত্যাখ্যান করে এবং নিজ খেয়াল-খুশীর অনুসরণ করে, আর প্রত্যেক ব্যাপারই যথাসময়ে লক্ষ্যে পৌঁছবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4850,54,4,'তাদের নিকট এসেছে সুসংবাদ, যাতে আছে সাবধান বাণী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4851,54,5,'এটা পরিপূর্ণ জ্ঞান, তবে এই সতর্ক বাণী তাদের কোন উপকারে আসেনি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4852,54,6,'অতএব তুমি তাদেরকে উপেক্ষা কর। যেদিন আহবানকারী আহবান করবে এক ভয়াবহ পরিণামের দিকে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4853,54,7,'অপমানে অবনমিত নেত্রে সেই দিন তারা কাবর হতে বের হবে বিক্ষিপ্ত পঙ্গপালের ন্যায়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4854,54,8,'তারা আহবানকারীর দিকে ছুটে আসবে ভীত-বিহবল হয়ে। কাফিরেরা বলবেঃ কঠিন এই দিন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4855,54,9,'এদের পূর্বে নূহের সম্প্রদায়ও মিথ্যা আরোপ করেছিল আমার বান্দার প্রতি এবং বলেছিলঃ এতো এক পাগল। আর তাকে ভীতি প্রদর্শন করা হয়েছিল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4856,54,10,'তখন সে তার রাব্বকে আহবান করে বলেছিলঃ আমিতো অসহায়; অতএব তুমি আমার প্রতিবিধান কর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4857,54,11,'ফলে আমি উন্মুক্ত করে দিলাম আকাশের দ্বার, প্রবল বারি বর্ষণে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4858,54,12,'এবং মাটি হতে উৎসারিত করলাম প্রস্রবণ। অতঃপর সকল পানি মিলিত হল এক পরিকল্পনা অনুসারে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4859,54,13,'তখন নূহকে আরোহণ করালাম কাষ্ঠ ও কীলক নির্মিত এক নৌযানে,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4860,54,14,'যা চলত আমার প্রত্যক্ষ তত্ত্বাবধানে। এটা পুরস্কার তার জন্য যে প্রত্যাখ্যাত হয়েছিল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4861,54,15,'আমি এটাকে রেখে দিয়েছি এক নিদর্শনরূপে; অতএব উপদেশ গ্রহণকারী কেহ আছে কি?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4862,54,16,'কি কঠোর ছিল আমার শাস্তি ও সতর্কবাণী!');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4863,54,17,'কুরআন আমি সহজ করে দিয়েছি উপদেশ গ্রহণের জন্য, সুতরাং উপদেশ গ্রহণকারী কেহ আছে কি? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4864,54,18,'আ‘দ সম্প্রদায় সত্য প্রত্যাখ্যান করেছিল, ফলে কি কঠোর হয়েছিল আমার শাস্তি ও সতর্ক বাণী!');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4865,54,19,'তাদের উপর আমি প্রেরণ করেছিলাম ঝঞ্ঝাবায়ু নিরবিচ্ছিন্ন দুর্ভোগের দিনে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4866,54,20,'মানুষকে ওটা উৎখাত করেছিল উন্মুলিত খেজুর কান্ডের ন্যায়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4867,54,21,'কি কঠোর ছিল আমার শাস্তি ও সতর্ক বাণী!');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4868,54,22,'কুরআন আমি সহজ করে দিয়েছি উপদেশ গ্রহণের জন্য; অতএব উপদেশ গ্রহণ করার কেহ আছে কি? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4869,54,23,'সামূদ সম্প্রদায় সতর্ককারীদেরকে মিথ্যাবাদী বলেছিল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4870,54,24,'তারা বলেছিলঃ আমরা কি আমাদেরই সম্প্রদায়ের এক ব্যক্তির অনুসরণ করব? তাহলে তো আমরা বিপথগামী এবং উন্মাদরূপে গন্য হব।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4871,54,25,'আমাদের মধ্যে কি তারই প্রতি প্রত্যাদেশ হয়েছে? না, সেতো একজন মিথ্যাবাদী, দাম্ভিক।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4872,54,26,'আগামীকাল তারা জানবে, কে মিথ্যাবাদী, দাম্ভিক।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4873,54,27,'আমি তাদের পরীক্ষার জন্য পাঠিয়েছি এক উষ্ট্রী; অতএব তুমি তাদের আচরণ লক্ষ্য কর এবং ধৈর্যশীল হও,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4874,54,28,'আর তুমি তাদেরকে জানিয়ে দাও যে, তাদের মধ্যে পানি বন্টন নির্ধারিত এবং পানির অংশের জন্য প্রত্যেকে হাযির হবে পালাক্রমে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4875,54,29,'অতঃপর তারা তাদের এক সঙ্গীকে আহবান করল, সে ওকে ধরে হত্যা করল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4876,54,30,'কি কঠোর ছিল আমার শাস্তি ও সতর্ক বাণী!');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4877,54,31,'আমি তাদেরকে আঘাত হেনেছিলাম এক মহানাদ দ্বারা; ফলে তারা হয়ে গেল খোয়াড় প্রস্তুতকারী বিখন্ডিত শুস্ক শাখা-প্রশাখার ন্যায়। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4878,54,32,'আমি কুরআন সহজ করে দিয়েছি উপদেশ গ্রহণের জন্য; অতএব উপদেশ গ্রহণ করার কেহ আছে কি?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4879,54,33,'লূত সম্প্রদায় প্রত্যাখ্যান করেছিল সতর্ককারীদেরকে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4880,54,34,'আমি তাদের উপর প্রেরণ করেছিলাম প্রস্তর বহনকারী প্রচন্ড ঝটিকা, কিন্তু লূত পরিবারের উপর নয়; তাদেরকে আমি উদ্ধার করেছিলাম রাতের শেষাংশে, ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4881,54,35,'আমার বিশেষ অনুগ্রহ স্বরূপ; যারা কৃতজ্ঞ আমি এভাবেই তাদেরকে পুরস্কৃত করে থাকি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4882,54,36,'লূত তাদেরকে সতর্ক করেছিল আমার কঠিন শাস্তি সম্পর্কে। কিন্তু তারা সতর্ক বাণী সম্বন্ধে বিতন্ডা শুরু করল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4883,54,37,'তারা লূতের নিকট হতে তার মেহমানদেরকে দাবী করল, তখন আমি তাদের দৃষ্টিশক্তি লোপ করে দিলাম এবং বললামঃ আস্বাদন কর আমার শাস্তি এবং সতর্কবাণীর পরিণাম। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4884,54,38,'প্রত্যুষে বিরামহীন শাস্তি তাদেরকে আঘাত করল। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4885,54,39,'(আমি বললাম) আস্বাদন কর আমার শাস্তি এবং সতর্কবাণীর পরিণাম।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4886,54,40,'আমি কুরআন সহজ করে দিয়েছি উপদেশ গ্রহণের জন্য; অতএব উপদেশ গ্রহণকারী কেহ আছে কি? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4887,54,41,'ফির‘আউন সম্প্রদায়ের নিকটও এসেছিল সতর্ককারী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4888,54,42,'কিন্তু তারা আমার সকল নিদর্শন প্রত্যাখ্যান করল, অতঃপর পরাক্রমশালী ও সর্বশক্তিমান রূপে আমি তাদেরকে সুকঠিন শাস্তি দিলাম।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4889,54,43,'তোমাদের মধ্যকার কাফিরেরা কি তাদের অপেক্ষা শ্রেষ্ঠ? না কি তোমাদের অব্যাহতির কোন সনদ রয়েছে পূর্ববর্তী কিতাবে? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4890,54,44,'এরা কি বলে, আমরা এক সংঘবদ্ধ অপরাজেয় দল? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4891,54,45,'এই দলতো শীঘ্রই পরাজিত হবে এবং পৃষ্ঠ প্রদর্শন করবে, ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4892,54,46,'অধিকন্ত কিয়ামাত তাদের শাস্তির নির্ধারিত কাল এবং কিয়ামাত হবে কঠিনতর ও তিক্ততর। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4893,54,47,'নিশ্চয়ই অপরাধীরা বিভ্রান্ত ও বিকারগ্রস্ত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4894,54,48,'যেদিন তাদেরকে উপুড় করে টেনে নিয়ে যাওয়া হবে জাহান্নামের দিকে সেই দিন বলা হবেঃ জাহান্নামের যন্ত্রণা আস্বাদন কর। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4895,54,49,'আমি সব কিছু সৃষ্টি করেছি নির্ধারিত পরিমাপে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4896,54,50,'আমার আদেশতো একটি কথায় নিস্পন্ন, চোখের পলকের মত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4897,54,51,'আমি ধ্বংস করেছি তোমাদের মত দলগুলিকে; অতএব উহা হতে উপদেশ গ্রহণকারী কেহ আছে কি?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4898,54,52,'তাদের সমস্ত কার্যকলাপ আছে ‘আমলনামায়, ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4899,54,53,'আছে ক্ষুদ্র ও বৃহৎ সব কিছুই লিপিবদ্ধ;');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4900,54,54,'মুত্তাকীরা থাকবে স্রোতস্বিনী বিধৌত জান্নাতে –');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4901,54,55,'যোগ্য আসনে, সার্বভৌম ক্ষমতার অধিকারী আল্লাহর সান্নিধ্যে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4902,55,1,'দয়াময় আল্লাহ! ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4903,55,2,'তিনিই শিক্ষা দিয়েছেন কুরআন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4904,55,3,'তিনিই সৃষ্টি করেছেন মানুষ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4905,55,4,'তিনিই তাকে শিখিয়েছেন ভাব প্রকাশ করতে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4906,55,5,'সূর্য ও চাঁদ আবর্তন করে নির্ধারিত কক্ষপথে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4907,55,6,'‘তারকা ও বৃক্ষ উভয়ে (আল্লাহকে) সাজদাহ করে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4908,55,7,'তিনি আকাশকে করেছেন সমুন্নত এবং স্থাপন করেছেন মানদন্ড – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4909,55,8,'যাতে তোমরা ভারসাম্য লংঘন না কর। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4910,55,9,'ওযনের ন্যায্য মান প্রতিষ্ঠিত কর এবং মিযানে কম করনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4911,55,10,'তিনি পৃথিবীকে স্থাপন করেছেন সৃষ্ট জীবের জন্য। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4912,55,11,'এতে রয়েছে ফল-মূল এবং খেজুর বৃক্ষ, যার ফল আবরণযুক্ত –');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4913,55,12,'এবং খোসাবিশিষ্ট দানা ও সুগন্ধ গুল্ম।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4914,55,13,'সুতরাং তোমরা উভয়ে তোমাদের প্রতিপালকের কোন্ অনুগ্রহ অস্বীকার করবে?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4915,55,14,'মানুষকে তিনি সৃষ্টি করেছেন পোড়া মাটির মত শুস্ক মৃত্তিকা হতে –');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4916,55,15,'আর জিনকে সৃষ্টি করেছেন নির্ধূম অগ্নিশিখা হতে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4917,55,16,'সুতরাং তোমরা উভয়ে তোমাদের প্রতিপালকের কোন্ অনুগ্রহ অস্বীকার করবে?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4918,55,17,'তিনিই দুই উদয়াচল ও দুই অস্তাচলের নিয়ন্তা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4919,55,18,'সুতরাং তোমরা উভয়ে তোমাদের রবের কোন্ অনুগ্রহ অস্বীকার করবে? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4920,55,19,'তিনি প্রবাহিত করেন দুই দরিয়া, যারা পরস্পর মিলিত হয়, ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4921,55,20,'কিন্তু ওদের মধ্যে রয়েছে এক অন্তরাল যা ওরা অতিক্রম করতে পারেনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4922,55,21,'সুতরাং তোমরা উভয়ে তোমাদের রবের কোন্ অনুগ্রহ অস্বীকার করবে? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4923,55,22,'উভয় দরিয়া হতে উৎপন্ন হয় মুক্তা ও প্রবাল। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4924,55,23,'সুতরাং তোমরা উভয়ে তোমাদের রবের কোন্ অনুগ্রহ অস্বীকার করবে?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4925,55,24,'সমুদ্রে বিচরণশীল পর্বত সদৃশ নৌযানসমূহ তাঁরই নিয়ন্ত্রণাধীন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4926,55,25,'সুতরাং তোমরা উভয়ে তোমাদের রবের কোন্ অনুগ্রহ অস্বীকার করবে? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4927,55,26,'ভূপৃষ্ঠে যা কিছু আছে সব কিছু নশ্বর, ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4928,55,27,'অবিনশ্বর শুধু তোমার রবের মুখমন্ডল যিনি মহিমাময়, মহানুভব।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4929,55,28,'সুতরাং তোমরা উভয়ে তোমাদের রবের কোন্ অনুগ্রহ অস্বীকার করবে? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4930,55,29,'আকাশমন্ডলী ও পৃথিবীতে যা আছে সবাই তাঁর নিকট প্রার্থী, প্রতিনিয়ত তিনি অতি গুরুত্বপূর্ণ কাজে রত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4931,55,30,'সুতরাং তোমরা উভয়ে তোমাদের রবের কোন্ অনুগ্রহ অস্বীকার করবে?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4932,55,31,'হে মানুষ ও জিন! আমি শীঘ্রই তোমাদের প্রতি মনোনিবেশ করব। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4933,55,32,'সুতরাং তোমরা উভয়ে তোমাদের রবের কোন্ অনুগ্রহ অস্বীকার করবে? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4934,55,33,'হে জিন ও মানুষ সম্প্রদায়! আকাশমন্ডলী ও পৃথিবীর সীমা তোমরা যদি অতিক্রম করতে পার, অতিক্রম কর; কিন্তু তোমরা তা পারবেনা আল্লাহর অনুমতি ব্যতীত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4935,55,34,'সুতরাং তোমরা উভয়ে তোমাদের রবের কোন্ অনুগ্রহ অস্বীকার করবে?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4936,55,35,'তোমাদের প্রতি প্রেরিত হবে অগ্নিশিখা ও ধুম্রপুঞ্জ, তখন তোমরা তা প্রতিরোধ করতে পারবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4937,55,36,'সুতরাং তোমরা উভয়ে তোমাদের রবের কোন্ অনুগ্রহ অস্বীকার করবে? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4938,55,37,'যেদিন আকাশ বিদীর্ণ হবে সেইদিন ওটা রক্ত-রঙ্গে রঞ্জিত চর্মের রূপ ধারণ করবে; ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4939,55,38,'সুতরাং তোমরা উভয়ে তোমাদের রবের কোন্ অনুগ্রহ অস্বীকার করবে? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4940,55,39,'সেদিন মানুষকে তার অপরাধ সম্বন্ধে জিজ্ঞেস করা হবেনা, আর না জিনকে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4941,55,40,'সুতরাং তোমরা উভয়ে তোমাদের রবের কোন্ অনুগ্রহ অস্বীকার করবে?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4942,55,41,'অপরাধীদের পরিচয় পাওয়া যাবে তাদের চেহারা হতে; তাদেরকে পাকড়াও করা হবে পা ও মাথার ঝুঁটি ধরে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4943,55,42,'সুতরাং তোমরা উভয়ে তোমাদের রবের কোন্ অনুগ্রহ অস্বীকার করবে? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4944,55,43,'এটাই সেই জাহান্নাম, যা অপরাধীরা অবিশ্বাস করত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4945,55,44,'তারা জাহান্নামের আগুন ও ফুটন্ত পানির মধ্যে ছুটাছুটি করবে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4946,55,45,'সুতরাং তোমরা উভয়ে তোমাদের রবের কোন্ অনুগ্রহ অস্বীকার করবে? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4947,55,46,'আর যে ব্যক্তি আল্লাহর সামনে উপস্থিত হওয়ার ভয় রাখে, তার জন্য রয়েছে দু’টি উদ্যান। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4948,55,47,'সুতরাং তোমরা উভয়ে তোমাদের রবের কোন্ অনুগ্রহ অস্বীকার করবে? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4949,55,48,'উভয়ই বহু শাখা-পল্লব বিশিষ্ট বৃক্ষে পূর্ণ। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4950,55,49,'সুতরাং তোমরা উভয়ে তোমাদের রবের কোন্ অনুগ্রহ অস্বীকার করবে? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4951,55,50,'উভয় উদ্যানে রয়েছে প্রবাহমান দুই প্রস্রবণ;');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4952,55,51,'সুতরাং তোমরা উভয়ে তোমাদের রবের কোন্ অনুগ্রহ অস্বীকার করবে? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4953,55,52,'উভয় উদ্যানে রয়েছে প্রত্যক ফল, জোড়ায় জোড়ায়। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4954,55,53,'সুতরাং তোমরা উভয়ে তোমাদের রবের কোন্ অনুগ্রহ অস্বীকার করবে? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4955,55,54,'সেখানে তারা হেলান দিয়ে বসবে পুরু রেশমের আস্তর বিশিষ্ট ফরাশে, দুই উদ্যানের ফল হবে তাদের নিকটবর্তী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4956,55,55,'সুতরাং তোমরা উভয়ে তোমাদের রবের কোন্ অনুগ্রহ অস্বীকার করবে? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4957,55,56,'সেই সবের মাঝে রয়েছে বহু আনতনয়না, যাদেরকে পূর্বে কোন মানুষ অথবা জিন স্পর্শ করেনি। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4958,55,57,'সুতরাং তোমরা উভয়ে তোমাদের রবের কোন্ অনুগ্রহ অস্বীকার করবে?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4959,55,58,'তারা যেন প্রবাল ও পদ্মরাগ; ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4960,55,59,'সুতরাং তোমরা উভয়ে তোমাদের রবের কোন্ অনুগ্রহ অস্বীকার করবে? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4961,55,60,'উত্তম কাজের জন্য উত্তম পুরস্কার ব্যতীত আর কি হতে পারে?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4962,55,61,'সুতরাং তোমরা উভয়ে তোমাদের রবের কোন্ অনুগ্রহ অস্বীকার করবে? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4963,55,62,'এই উদ্যানদ্বয় ব্যতীত আরও দু’টি উদ্যান রয়েছে –');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4964,55,63,'সুতরাং তোমরা উভয়ে তোমাদের রবের কোন্ অনুগ্রহ অস্বীকার করবে?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4965,55,64,'ঘন সবুজ এ উদ্যানটি দু’টি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4966,55,65,'সুতরাং তোমরা উভয়ে তোমাদের রবের কোন্ অনুগ্রহ অস্বীকার করবে? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4967,55,66,'উভয় উদ্যানে রয়েছে উচ্ছলিত দুই প্রস্রবন। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4968,55,67,'সুতরাং তোমরা উভয়ে তোমাদের রবের কোন্ অনুগ্রহ অস্বীকার করবে? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4969,55,68,'সেখানে রয়েছে ফলমূল, খেজুর ও আনার। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4970,55,69,'সুতরাং তোমরা উভয়ে তোমাদের রবের কোন্ অনুগ্রহ অস্বীকার করবে? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4971,55,70,'সেই সকলের মাঝে রয়েছে সুশীলা, সুন্দরীগণ। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4972,55,71,'সুতরাং তোমরা উভয়ে তোমাদের রবের কোন্ অনুগ্রহ অস্বীকার করবে?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4973,55,72,'তারা তাবুতে সুরক্ষিত হুর। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4974,55,73,'সুতরাং তোমরা উভয়ে তোমাদের রবের কোন্ অনুগ্রহ অস্বীকার করবে?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4975,55,74,'তাদেরকে ইতোপূর্বে কোন মানুষ অথবা জিন স্পর্শ করেনি। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4976,55,75,'সুতরাং তোমরা উভয়ে তোমাদের রবের কোন্ অনুগ্রহ অস্বীকার করবে?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4977,55,76,'তারা হেলান দিয়ে বসবে সবুজ তাকিয়ায় ও সুন্দর গালিচার উপর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4978,55,77,'সুতরাং তোমরা উভয়ে তোমাদের রবের কোন্ অনুগ্রহ অস্বীকার করবে?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4979,55,78,'কত মহান তোমার রবের নাম যিনি মহিমাময় ও মহানুভব! ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4980,56,1,'যখন কিয়ামাত সংঘটিত হবে – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4981,56,2,'তখন সংঘটন অস্বীকার করার কেহ থাকবেনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4982,56,3,'এটা কেহকে করবে নীচ, কেহকে করবে সমুন্নত;');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4983,56,4,'যখন প্রবল কম্পনে প্রকম্পিত হবে পৃথিবী –');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4984,56,5,'এবং পবর্তমালা চূর্ণ-বিচূর্ণ হয়ে পড়বে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4985,56,6,'ফলে ওটা পর্যবসিত হবে উৎক্ষিপ্ত ধূলিকণায় – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4986,56,7,'এবং তোমরা বিভক্ত হয়ে পড়বে তিন শ্রেণীতে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4987,56,8,'ডান দিকের দল! কত ভাগ্যবান ডান দিকের দল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4988,56,9,'এবং বাম দিকের দল! কত হতভাগা বাম দিকের দল!');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4989,56,10,'আর অগ্রবর্তীগণইতো অগ্রবর্তী। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4990,56,11,'তারাই নৈকট্য প্রাপ্ত –');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4991,56,12,'সুখ উদ্যানের। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4992,56,13,'বহু সংখ্যক হবে পূর্ববর্তীদের মধ্য হতে; ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4993,56,14,'এবং অল্প সংখ্যক হবে পরবর্তীদের মধ্য হতে,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4994,56,15,'স্বর্ণ খচিত আসনে – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4995,56,16,'তারা হেলান দিয়ে বসবে, পরস্পর মুখোমুখী হয়ে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4996,56,17,'তাদের সেবায় ঘোরাফিরা করবে চির কিশোরেরা –');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4997,56,18,'পান-পাত্র, কুজা ও প্রস্রবন নিঃসৃত সুরাপূর্ণ পেয়ালা নিয়ে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4998,56,19,'সেই সুরা পানে তাদের শিরঃপীড়া হবেনা, তারা জ্ঞানহারাও হবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (4999,56,20,'এবং তাদের পছন্দ মত ফলমূল –');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5000,56,21,'আর তাদের ঈস্পিত পাখীর গোশত দিয়ে; ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5001,56,22,'আর তাদের জন্য থাকবে আয়তলোচনা হুর – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5002,56,23,'সুরক্ষিত মুক্তা সদৃশ – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5003,56,24,'তাদের কর্মের পুরস্কার স্বরূপ। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5004,56,25,'সেখানে তারা শুনবেনা কোন অসার অথবা পাপ বাক্য, ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5005,56,26,'‘সালাম’ আর ‘সালাম’ বাণী ব্যতীত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5006,56,27,'আর ডান দিকের দল! কত ভাগ্যবান ডান দিকের দল! ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5007,56,28,'তারা থাকবে এক উদ্যানে, সেখানে আছে কন্টকহীন কুল বৃক্ষ, ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5008,56,29,'কাঁদি ভরা কদলী বৃক্ষ, ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5009,56,30,'সম্প্রসারিত ছায়া, ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5010,56,31,'সদ্য প্রবাহমান পানি, ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5011,56,32,'ও প্রচুর ফলমূল –');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5012,56,33,'যা শেষ হবেনা এবং যা নিষিদ্ধও হবেনা,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5013,56,34,'আর সমুচ্চ শয্যাসমূহ। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5014,56,35,'তাদের জন্য আমি করেছি বিশেষ সৃষ্টি। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5015,56,36,'তাদেরকে করেছি কুমারী, ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5016,56,37,'সোহাগিনী ও সমবয়স্কা – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5017,56,38,'(এ সবই) ডান দিকের লোকদের জন্য। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5018,56,39,'তাদের অনেকে হবে পূর্ববর্তীদের মধ্য হতে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5019,56,40,'এবং অনেকে হবে পরবর্তীদের মধ্য হতে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5020,56,41,'আর বাম দিকের দল, কত হতভাগা বাম দিকের দল!');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5021,56,42,'তারা থাকবে অত্যুষ্ণ বায়ু ও উত্তপ্ত পানিতে,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5022,56,43,'কৃষ্ণ বর্ণ ধুম্রের ছায়ায়, ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5023,56,44,'যা শীতলও নয়, আরামদায়কও নয়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5024,56,45,'ইতোপূর্বে তারাতো মগ্ন ছিল ভোগ বিলাসে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5025,56,46,'এবং তারা অবিরাম লিপ্ত ছিল ঘোরতর পাপ কর্মে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5026,56,47,'তারা বলতঃ মরে অস্থি ও মৃত্তিকায় পরিণত হলেও কি পুনরুত্থিত হব আমরা?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5027,56,48,'এবং আমাদের পূর্বপুরুষরাও? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5028,56,49,'বলঃ অবশ্যই পূর্ববতী ও পরবর্তীদের –');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5029,56,50,'সকলকে একত্রিত করা হবে এক নির্ধারিত দিনের নির্ধারিত সময়ে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5030,56,51,'অতঃপর হে বিভ্রান্ত মিথ্যা আরোপকারীরা!');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5031,56,52,'তোমরা অবশ্যই আহার করবে যাক্কুম বৃক্ষ হতে,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5032,56,53,'এবং ওটা দ্বারা তোমরা উদর পূর্ণ করবে, ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5033,56,54,'তারপর তোমরা পান করবে অত্যুষ্ণ পানি –');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5034,56,55,'পান করবে তৃষ্ণার্ত উষ্ট্রের ন্যায়। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5035,56,56,'কিয়ামাত দিবসে ওটাই হবে তাদের আপ্যায়ন। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5036,56,57,'আমিই তোমাদেরকে সৃষ্টি করেছি, তাহলে কেন তোমরা বিশ্বাস করছনা? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5037,56,58,'তোমরা কি ভেবে দেখেছ তোমাদের বীর্যপাত সম্বন্ধে?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5038,56,59,'ওটা কি তোমরা সৃষ্টি কর, না আমি সৃষ্টি করি? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5039,56,60,'আমি তোমাদের জন্য মৃত্যু নির্ধারিত করেছি এবং আমি অক্ষম নই – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5040,56,61,'তোমাদের স্থলে তোমাদের সদৃশ আনয়ন করতে এবং তোমাদেরকে এমন এক আকৃতি দান করতে যা তোমরা জাননা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5041,56,62,'তোমরাতো অবগত হয়েছ প্রথম সৃষ্টি সম্বন্ধে, তাহলে তোমরা অনুধাবন করনা কেন? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5042,56,63,'তোমরা যে বীজ বপন কর সে সম্পর্কে চিন্তা করেছ কি? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5043,56,64,'তোমরা কি ওকে অংকুরিত কর, না আমি অংকুরিত করি?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5044,56,65,'আমি ইচ্ছা করলে একে খড়-কুটায় পরিণত করতে পারি, তখন হতবুদ্ধি হয়ে পড়বে তোমরা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5045,56,66,'বলবেঃ আমাদেরতো সর্বনাশ হয়েছে!');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5046,56,67,'আমরা হৃত সর্বস্ব হয়ে পড়েছি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5047,56,68,'তোমরা যে পানি পান কর সেই সম্পর্কে তোমরা ভেবে দেখেছ কি? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5048,56,69,'তোমরাই কি ওটা মেঘ হতে নামিয়ে আন, না কি আমি ওটা বর্ষণ করি?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5049,56,70,'আমি ইচ্ছা করলে ওটা লবণাক্ত করে দিতে পারি। তবুও কি তোমরা কৃতজ্ঞতা প্রকাশ করবেনা? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5050,56,71,'তোমরা যে অগ্নি প্রজ্জ্বলিত কর তা লক্ষ্য করে দেখেছ কি? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5051,56,72,'তোমরাই কি ওর বৃক্ষ সৃষ্টি কর, না আমি সৃষ্টি করি? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5052,56,73,'আমি একে করেছি নিদর্শন এবং মরুচারীদের প্রয়োজনীয় বস্তু। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5053,56,74,'সুতরাং তুমি তোমার মহান রবের নামের পবিত্রতা ও মহিমা ঘোষণা কর। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5054,56,75,'আমি শপথ করছি নক্ষত্র রাজির অস্তাচলের! ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5055,56,76,'অবশ্যই এটা এক মহা শপথ, যদি তোমরা জানতে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5056,56,77,'নিশ্চয়ই এটা সম্মানিত কুরআন –');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5057,56,78,'যা আছে সুরক্ষিত কিতাবে,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5058,56,79,'যারা পুতঃ পবিত্র তারা ব্যতীত অন্য কেহ তা স্পর্শ করেনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5059,56,80,'ইহা জগতসমূহের রবের নিকট হতে অবতীর্ণ। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5060,56,81,'তবুও কি তোমরা এই বাণীকে তুচ্ছ গণ্য করবে? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5061,56,82,'এবং তোমরা মিথ্যারোপকেই তোমাদের উপজীব্য করে নিয়েছ! ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5062,56,83,'পরন্তু কেন নয় - প্রাণ যখন কন্ঠাগত হয়, ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5063,56,84,'এবং তখন তোমরা তাকিয়ে থাক, ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5064,56,85,'আর আমি তোমাদের অপেক্ষা তার নিকটতর, কিন্তু তোমরা দেখতে পাওনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5065,56,86,'তোমরা যদি কর্তৃত্বাধীন না হও – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5066,56,87,'তাহলে তোমরা ওটা ফিরাওনা কেন? যদি তোমরা সত্যবাদী হও! ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5067,56,88,'যদি সে নৈকট্য প্রাপ্তদের একজন হয় –');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5068,56,89,'তার জন্য রয়েছে আরাম, উত্তম জীবনোপকরণ ও সুখময় উদ্যান;');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5069,56,90,'আর যদি সে ডান দিকের একজন হয় – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5070,56,91,'তাকে বলা হবেঃ হে দক্ষিণ পার্শ্ববর্তী! তোমার প্রতি শান্তি। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5071,56,92,'কিন্তু সে যদি সত্য অস্বীকারকারী ও বিভ্রান্তদের অন্যতম হয় –');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5072,56,93,'তাহলে রয়েছে আপ্যায়ন, অত্যুষ্ণ পানির দ্বারা – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5073,56,94,'এবং দহন, জাহান্নামের। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5074,56,95,'এটাতো ধ্রুব সত্য।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5075,56,96,'অতএব তুমি তোমার মহান রবের নামের পবিত্রতা ও মহিমা ঘোষণা কর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5076,57,1,'আকাশমন্ডলী ও পৃথিবীতে যা কিছু আছে সবই আল্লাহর পবিত্রতা ও মহিমা ঘোষণা করে। তিনি পরাক্রমশালী, প্রজ্ঞাময়। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5077,57,2,'আকাশমন্ডলী ও পৃথিবীর সার্বভৌমত্ব তাঁরই। তিনি জীবন দান করেন ও মৃত্যু ঘটান; তিনি সর্ব বিষয়ে সর্বশক্তিমান। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5078,57,3,'তিনিই আদি, তিনিই অন্ত, তিনিই ব্যক্ত, তিনিই গুপ্ত এবং তিনি সর্ব বিষয়ে সম্যক অবহিত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5079,57,4,'তিনিই ছয় দিনে আকাশমন্ডলী ও পৃথিবী সৃষ্টি করেছেন, অতঃপর আরশে সমাসীন হয়েছেন। তিনি জানেন যা কিছু ভূমিতে প্রবেশ করে এবং যা কিছু তা হতে বের হয় এবং আকাশ হতে যা কিছু নামে ও আকাশে যা কিছু উত্থিত হয়। তোমরা যেখানেই থাক না কেন তিনি তোমাদের সঙ্গে আছেন, তোমরা যা কিছু কর আল্লাহ তা দেখেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5080,57,5,'আকাশমন্ডলী ও পৃথিবীর সার্বভৌমত্ব তাঁরই, এবং আল্লাহরই দিকে সব বিষয় প্রত্যাবর্তিত হবে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5081,57,6,'তিনিই রাতকে প্রবেশ করান দিনে এবং দিনকে প্রবেশ করান রাতে, এবং তিনি অর্ন্তযামী। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5082,57,7,'আল্লাহ ও তাঁর রাসূলের প্রতি ঈমান আন এবং আল্লাহ তোমাদেরকে যা কিছুর উত্তরাধিকারী করেছেন তা হতে ব্যয় কর। তোমাদের মধ্যে যারা ঈমান আনে ও ব্যয় করে তাদের জন্য আছে মহা পুরস্কার। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5083,57,8,'তোমাদের কি হল যে, তোমরা আল্লাহতে ঈমান আনছনা? অথচ রাসূল তোমাদেরকে তোমাদের রবের প্রতি আহবান করছে এবং আল্লাহ তোমাদের নিকট হতে অংগীকার গ্রহণ করেছেন, অবশ্য তোমরা যদি তাতে বিশ্বাসী হও। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5084,57,9,'তিনিই তাঁর বান্দার প্রতি সুস্পষ্ট আয়াত অবতীর্ণ করেন, তোমাদেরকে অন্ধকার হতে আলোতে নিয়ে আসার জন্য; আল্লাহতো তোমাদের প্রতি করুণাময়, পরম দয়ালু।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5085,57,10,'তোমরা আল্লাহর পথে কেন ব্যয় করবেনা? আকাশমন্ডলী ও পৃথিবীর মালিকানাতো আল্লাহরই। তোমাদের মধ্যে যারা মাক্কা বিজয়ের পূর্বে ব্যয় করেছে এবং সংগ্রাম করেছে তারা এবং পরবর্তীরা সমান নয়; তারা মর্যাদায় শ্রেষ্ঠ তাদের অপেক্ষা যারা পরবর্তীকালে ব্যয় করেছে ও সংগ্রাম করেছে। তবে আল্লাহ উভয়ের কল্যাণের প্রতিশ্রুতি দিয়েছেন। তোমরা যা কর আল্লাহ তা সবিশেষ অবহিত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5086,57,11,'কে আছে যে আল্লাহকে দিবে উত্তম ঋণ? তাহলে তিনি বহু গুণে একে বৃদ্ধি করবেন এবং তার জন্য রয়েছে মহা পুরস্কার। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5087,57,12,'সেদিন তুমি দেখবে মু’মিন নর-নারীদেরকে তাদের সম্মুখ ভাগে ও ডান পাশে তাদের জ্যোতি বিচ্ছুরিত হবে। (বলা হবে) আজ তোমাদের জন্য সুসংবাদ জান্নাতের, যার পাদদেশে নদী প্রবাহিত হবে, সেখানে তোমরা স্থায়ী হবে, এটাই মহা সাফল্য। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5088,57,13,'সেদিন মুনাফিক নর ও নারী মু’মিনদের বলবেঃ তোমরা আমাদের জন্য একটু থাম, যাতে আমরা তোমাদের জ্যোতির কিছু গ্রহণ করতে পারি। বলা হবেঃ তোমরা তোমাদের পিছনে ফিরে যাও এবং আলোর সন্ধান কর। অতঃপর উভয়ের মাঝে স্থাপিত হবে একটি প্রাচীর যাতে একটি দরযা থাকবে, ওর অভ্যন্তরে থাকবে রাহমাত এবং বহির্ভাগে থাকবে শাস্তি। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5089,57,14,'মুনাফিকরা মু’মিনদেরকে ডেকে জিজ্ঞেস করবেঃ আমরা কি তোমাদের সঙ্গে ছিলামনা? তারা বলবেঃ হ্যাঁ, কিন্তু তোমরা নিজেরাই নিজেদেরকে বিপদগ্রস্ত করেছ; তোমরা প্রতীক্ষা করেছিলে, সন্দেহ পোষণ করেছিলে এবং অলীক আকাংখা তোমাদেরকে মোহাচ্ছন্ন করে রেখেছিল আল্লাহর হুকুম আসা পর্যন্ত, আর মহা প্রতারক তোমাদেরকে প্রতারিত করেছিল আল্লাহ সম্পর্কে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5090,57,15,'আজ তোমাদের নিকট হতে কোন মুক্তিপণ গ্রহণ করা হবেনা এবং যারা কুফরী করেছিল তাদের নিকট হতেও নয়। জাহান্নামই তোমাদের আবাসস্থল, এটাই তোমাদের মাওলা (যোগ্য স্থান), কত নিকৃষ্ট এই পরিণাম! ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5091,57,16,'যারা ঈমান আনে তাদের হৃদয় ভক্তি-বিগলিত হওয়ার সময় কি আসেনি আল্লাহর স্মরণে এবং যে সত্য অবতীর্ণ হয়েছে তাতে? এবং পূর্বে যাদেরকে কিতাব দেয়া হয়েছিল তাদের মত যেন তারা না হয়, বহুকাল অতিক্রান্ত হয়ে গেলে যাদের অন্তঃকরণ কঠিন হয়ে পড়েছিল। তাদের অধিকাংশই সত্যত্যাগী। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5092,57,17,'জেনে রেখ, আল্লাহই ধরিত্রীকে ওর মৃত্যুর পর পুনর্জীবিত করেন। আমি নিদর্শনগুলি তোমাদের জন্য বিশদভাবে ব্যক্ত করেছি যাতে তোমরা বুঝতে পার। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5093,57,18,'দানশীল পুরুষ ও দানশীলা নারী এবং যারা আল্লাহকে উত্তম ঋণ দান করে তাদেরকে দেয়া হবে বহুগুণ বেশি এবং তাদের জন্য রয়েছে মহা পুরস্কার। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5094,57,19,'যারা আল্লাহ ও তাঁর রাসূলের উপর ঈমান আনে, তারাই তাদের রবের নিকট সত্যনিষ্ঠ ও শহীদ। তাদের জন্য রয়েছে তাদের প্রাপ্য পুরস্কার ও জ্যোতি এবং যারা কুফরী করেছে ও আমার নিদর্শন অস্বীকার করেছে তারাই জাহান্নামের অধিবাসী। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5095,57,20,'তোমরা জেনে রেখ যে, পার্থিব জীবনতো ক্রীড়া কৌতুক, জাঁকজমক, পারস্পরিক অহংকার প্রকাশ, ধন-সম্পদ ও সন্তান-সন্ততিতে প্রাচুর্য লাভের প্রতিযোগিতা ব্যতীত আর কিছুই নয়; ওর উপমা বৃষ্টি, যদ্বারা উৎপন্ন শস্য সম্ভার কৃষকদেরকে চমৎকৃত করে, অতঃপর ওটা শুকিয়ে যায়, ফলে তুমি ওটা পীতবর্ণ দেখতে পাও; অবশেষে ওটা খড়কুটায় পরিণত হয়। পরকালে (অবিশ্বাসীদের জন্য) রয়েছে কঠিন শাস্তি এবং (সৎপথ অনুসারীদের জন্য রয়েছে) আল্লাহর ক্ষমা ও সন্তুষ্টি। পার্থিব জীবন ছলনাময় ভোগ ব্যতীত কিছুই নয়। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5096,57,21,'তোমরা অগ্রণী হও তোমাদের রবের ক্ষমা ও সেই জান্নাত লাভের প্রয়াসে যা প্রশস্ততায় আকাশ ও পৃথিবীর মত, যা প্রস্তুত করা হয়েছে আল্লাহ ও তাঁর রাসূলগণে বিশ্বাসীদের জন্য। এটা আল্লাহর অনুগ্রহ যাকে ইচ্ছা তিনি এটা দান করেন; আল্লাহ মহা অনুগ্রহশীল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5097,57,22,'পৃথিবীতে অথবা ব্যক্তিগতভাবে তোমাদের উপর যে বিপর্যয় আসে আমি তা সংঘটিত করার পূর্বেই তা লিপিবদ্ধ থাকে, আল্লাহর পক্ষে এটা খুবই সহজ। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5098,57,23,'এটা এ জন্য যে, তোমরা যা হারিয়েছ তাতে যেন তোমরা বিমর্ষ না হও, এবং যা তিনি তোমাদেরকে দিয়েছেন তার জন্য হর্ষোৎফুল্ল না হও। আল্লাহ পছন্দ করেননা ঔদ্ধত ও অহংকারীদেরকে – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5099,57,24,'যারা কার্পন্য করে এবং মানুষকে কার্পণ্যের নির্দেশ দেয়; যে মুখ ফিরিয়ে নেয় সে জেনে রাখুক যে, আল্লাহতো অভাবমুক্ত, প্রশংসা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5100,57,25,'নিশ্চয়ই আমি আমার রাসূলদেরকে প্রেরণ করেছি স্পষ্ট প্রমাণসহ এবং তাদের সঙ্গে দিয়েছি কিতাব ও ন্যায় নীতি, যাতে মানুষ সুবিচার প্রতিষ্ঠা করে। আমি লৌহও দিয়েছি যাতে রয়েছে প্রচন্ড শক্তি এবং রয়েছে মানুষের জন্য বহুবিধ কল্যাণ; এটা এ জন্য যে, আল্লাহ প্রকাশ করে দিবেন কে প্রত্যক্ষ না করেও তাঁকে ও তাঁর রাসূলদেরকে সাহায্য করে। আল্লাহ সর্বশক্তিমান, পরাক্রমশালী। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5101,57,26,'আমি নূহ এবং ইবরাহীমকে এবং তাদের বংশধরদের জন্য স্থির করেছিলাম নবুওয়াত ও কিতাব, কিন্তু তাদের অল্পই সৎ পথ অবলম্বন করেছিল এবং অধিকাংশই ছিল সত্যত্যাগী। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5102,57,27,'অতঃপর আমি তাদের অনুগামী করেছিলাম আমার রাসূলগণকে এবং অনুগামী করেছিলাম মারইয়াম-পুত্র ঈসাকে, আর তাকে দিয়েছিলাম ঈঞ্জীল এবং তার অনুসারীদের অন্তরে দিয়েছিলাম করুণা ও দয়া; কিন্তু দরবেশী জীবনতো তারা নিজেরাই আল্লাহর সন্তুষ্টি লাভের জন্য প্রবর্তন করেছিল; আমি তাদেরকে এর বিধান দিইনি; অথচ এটাও তারা যথাযথভাবে পালন করেনি। তাদের মধ্যে যারা ঈমান এনেছিল তাদেরকে আমি করেছিলাম পুরস্কৃত এবং তাদের অধিকাংশই সত্যত্যাগী। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5103,57,28,'হে মু’মিনগণ! আল্লাহকে ভয় কর এবং তাঁর রাসূলের প্রতি বিশ্বাস স্থাপন কর। তিনি তাঁর অনুগ্রহে তোমাদেরকে দিবেন দ্বিগুণ পুরস্কার এবং তিনি তোমাদেরকে দিবেন আলো, যার সাহায্যে তোমরা চলবে এবং তিনি তোমাদেরকে ক্ষমা করবেন। আল্লাহ ক্ষমাশীল, পরম দয়ালু। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5104,57,29,'এটা এ জন্য যে, কিতাবীরা যেন জানতে পারে যে, আল্লাহর সামান্যতম অনুগ্রহের উপরও তাদের কোন অধিকার নেই; অনুগ্রহ আল্লাহরই এখতিয়ারে, যাকে ইচ্ছা তাকে তিনি তা দান করেন। আল্লাহ মহা অনুগ্রহশীল। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5105,58,1,'(হে রাসূল) আল্লাহ শুনেছেন সেই নারীর কথা যে তার স্বামীর বিষয়ে তোমার সাথে বাদানুবাদ করেছে এবং আল্লাহর নিকট ফরিয়াদ করেছে। আল্লাহ তোমাদের কথোপকথন শোনেন; আল্লাহ সর্বশ্রোতা, সর্বদ্রষ্টা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5106,58,2,'তোমাদের মধ্যে যারা নিজেদের স্ত্রীদের সাথে যিহার করে তারা জেনে রাখুক যে, তাদের স্ত্রীরা তাদের মা নয়; যারা তাদেরকে জন্মদান করে শুধু তারাই তাদের মা; তারাতো অসঙ্গত ও ভিত্তিহীন কথাই বলে; নিশ্চয়ই আল্লাহ পাপ মোচনকারী, ক্ষমাশীল। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5107,58,3,'যারা নিজেদের স্ত্রীদের সাথে ‘‘যিহার’’ করে এবং পরে তাদের উক্তি প্রত্যাহার করে, তাহলে একে অপরকে স্পর্শ করার পূর্বে একটি দাস মুক্ত করতে হবে - এই নির্দেশ তোমাদেরকে দেয়া হল। তোমরা যা কর আল্লাহ তা সম্যক অবগত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5108,58,4,'কিন্তু যার এ সামর্থ্য নেই, একে অপরকে স্পর্শ করার পূর্বে তাকে একাদিক্রমে দুই মাস সিয়াম পালন করতে হবে। যে তাতেও অসমর্থ হবে সে ষাটজন অভাবগ্রস্তকে খাওয়াবে; এটা এ জন্য যে, তোমরা যেন আল্লাহ ও তাঁর রাসূলের উপর বিশ্বাস স্থাপন কর। এগুলি আল্লাহর নির্ধারিত বিধান, কাফিরদের জন্য রয়েছে বেদনাদায়ক শাস্তি। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5109,58,5,'যারা আল্লাহ ও তাঁর রাসূলের বিরুদ্ধাচরণ করে, তাদেরকে অপদস্থ করা হবে যেমন অপদস্থ করা হয়েছে তাদের পূর্ববর্তীদেরকে; আমি সুস্পষ্ট আয়াত অবতীর্ণ করেছি; কাফিরদের জন্য রয়েছে লাঞ্ছনাদায়ক শাস্তি। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5110,58,6,'সেদিন, যেদিন আল্লাহ তাদের সকলকে একত্রে পুনরুত্থিত করবেন এবং তাদেরকে জানিয়ে দিবেন যা তারা করত; আল্লাহ উহার হিসাব রেখেছেন, যদিও তারা তা বিস্মৃত হয়েছে। আল্লাহ সর্ব বিষয়ে সম্যক দ্রষ্টা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5111,58,7,'তুমি কি অনুধাবন করনা, আকাশমন্ডলী ও পৃথিবীতে যা কিছু আছে আল্লাহ তা জানেন? তিন ব্যক্তির মধ্যে এমন কোন গোপন পরামর্শ হয়না যাতে চতুর্থ হিসাবে তিনি উপস্থিত থাকেননা; এবং পাঁচ ব্যক্তির মধ্যে এমন কোন গোপন পরামর্শ হয়না যাতে ষষ্ঠ হিসাবে তিনি উপস্থিত থাকেননা; তারা এতদপেক্ষা কম হোক বা বেশি হোক, তারা যেখানেই থাকুকনা কেন তিনি তাদের সাথে আছেন। তারা যা করে, তিনি তাদেরকে কিয়ামাত দিবসে তা জানিয়ে দিবেন। আল্লাহ সর্ব বিষয়ে সম্যক অবগত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5112,58,8,'তুমি কি তাদেরকে লক্ষ্য করনা, যাদেরকে গোপন পরামর্শ করতে নিষেধ করা হয়েছিল; অতঃপর তারা যা নিষিদ্ধ তারই পুনরাবৃত্তি করে এবং পাপাচরণ, সীমা লংঘন ও রাসূলের বিরুদ্ধাচরণের জন্য কানাকানি করে। তারা যখন তোমার নিকট আসে তখন তারা তোমাকে এমন কথা দ্বারা অভিবাদন করে যদ্বারা আল্লাহ তোমাকে অভিবাদন করেননি। তারা মনে মনে বলেঃ আমরা যা বলি উহার জন্য আল্লাহ আমাদেরকে শাস্তি দেননা কেন? জাহান্নামই তাদের উপযুক্ত শাস্তি, সেখানে তারা প্রবেশ করবে, কত নিকৃষ্ট সেই আবাস!');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5113,58,9,'হে মু’মিনগণ! তোমরা যখন গোপন পরামর্শ কর, সেই পরামর্শ যেন পাপাচরণ, সীমা লংঘন ও রাসূলের বিরুদ্ধাচরণ সম্পর্কে না হয়। কল্যাণকর কাজ ও তাকওয়া অবলম্বনের পরামর্শ কর এবং ভয় কর আল্লাহকে যাঁর নিকট তোমরা সমবেত হবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5114,58,10,'শাইতানের প্ররোচনায় হয় এই গোপন পরামর্শ, মু’মিনদেরকে দুঃখ দেয়ার জন্য; কিন্তু আল্লাহর ইচ্ছা ব্যতীত শাইতান তাদের সামান্যতম ক্ষতি সাধনেও সক্ষম নয়। মু’মিনদের কর্তব্য হল আল্লাহর উপর নির্ভর করা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5115,58,11,'হে মু’মিনগণ! যখন তোমাদের বলা হয় মজলিসের স্থান প্রশস্ত করে দাও, তখন তোমরা স্থান করে দিবে। আল্লাহ তোমাদের জন্য স্থান প্রশস্ত করে দিবেন এবং যখন বলা হয় উঠে যাও, তখন উঠে যাবে। তোমাদের মধ্যে যারা ঈমান এনেছে এবং যাদেরকে জ্ঞান দান করা হয়েছে আল্লাহ তাদেরকে মর্যাদায় উন্নত করবেন; আল্লাহ লিপিবদ্ধ করে রেখেছেন, তোমরা যা কর সেই সম্পর্কে আল্লাহ সবিশেষ অবহিত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5116,58,12,'হে মু’মিনগণ! তোমরা রাসূলের সাথে চুপি চুপি কথা বলতে চাইলে উহার পূর্বে সাদাকাহ প্রদান করবে, এটাই তোমাদের জন্য শ্রেয় ও পরিশোধক। যদি তাতে অক্ষম হও তাহলে এ জন্য তোমাদেরকে অপরাধী গণ্য করা হবেনা। কেননা আল্লাহ ক্ষমাশীল, পরম দয়ালু।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5117,58,13,'তোমরা কি চুপে চুপে কথা বলার পূর্বে সাদাকাহ প্রদান কষ্টকর মনে কর? যখন তোমরা সাদাকাহ দিতে পারলেনা, আর আল্লাহ তোমাদেরকে ক্ষমা করে দিলেন, তখন তোমরা সালাত প্রতিষ্ঠিত কর, যাকাত প্রদান কর এবং আল্লাহ ও তাঁর রাসূলের আনুগত্য কর। তোমরা যা কর আল্লাহ তা সম্যক অবগত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5118,58,14,'তুমি কি তাদের প্রতি লক্ষ্য করনি, যারা আল্লাহ যে সম্প্রদায়ের প্রতি রুষ্ট, তাদের সাথে বন্ধুত্ব করে? তারা তোমাদের (মুসলিমদের) দলভুক্ত নয় এবং তাদেরও (ইয়াহুদদের) দলভুক্ত নয় এবং তারা জেনে মিথ্যা শপথ করে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5119,58,15,'আল্লাহ তাদের জন্য প্রস্তুত রেখেছেন কঠিন শাস্তি। কত মন্দ তারা যা করে!');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5120,58,16,'তারা তাদের শপথগুলিকে ঢাল হিসাবে ব্যবহার করে, এভাবে তারা আল্লাহর পথ হতে মানুষকে নিবৃত্ত করে; তাদের জন্য রয়েছে লাঞ্ছনাদায়ক শাস্তি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5121,58,17,'আল্লাহর শাস্তির মুকাবিলায় তাদের ধন সম্পদ ও সন্তান-সন্ততি তাদের কোন কাজে আসবেনা; তারাই জাহান্নামের অধিবাসী, সেখানে তারা স্থায়ী হবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5122,58,18,'যেদিন আল্লাহ পুনরুত্থিত করবেন তাদের সকলকে, তখন তারা তাঁর (আল্লাহর) নিকট সেরূপ শপথ করবে যেরূপ শপথ তোমাদের নিকট করে এবং তারা মনে করে যে, এতে তারা উপকৃত হবে। সাবধান! তারাইতো মিথ্যাবাদী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5123,58,19,'শাইতান তাদের উপর প্রভুত্ব বিস্তার করেছে। ফলে তাদেরকে ভুলিয়ে দিয়েছে আল্লাহর স্মরণ। তারা শাইতানেরই দল। সাবধান! শাইতানের দল অবশ্যই ক্ষতিগ্রস্ত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5124,58,20,'যারা আল্লাহ ও তাঁর রাসূলের বিরুদ্ধাচরণ করে তারা হবে চরম লাঞ্ছিতদের অন্তর্ভুক্ত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5125,58,21,'আল্লাহ সিদ্ধান্ত গ্রহণ করেছেন, আমি এবং আমার রাসূল অবশ্যই বিজয়ী হব। আল্লাহ শক্তিমান, পরাক্রমশালী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5126,58,22,'আল্লাহ ও আখিরাতে বিশ্বাসী এমন কোন সম্প্রদায় তুমি পাবেনা যারা ভালবাসে আল্লাহ ও তাঁর রাসূলের বিরুদ্ধাচারীদেরকে, হোক না এই বিরুদ্ধাচারীরা তাদের পিতা, পুত্র, ভাই অথবা তাদের জ্ঞাতি গোষ্ঠি। তাদের অন্তরে (আল্লাহ) ঈমান লিখে দিয়েছেন এবং তাদেরকে শক্তিশালী করেছেন তাঁর অদৃশ্য শক্তি দ্বারা; তিনি তাদেরকে দাখিল করবেন জান্নাতে যার পাদদেশে নদী প্রবাহিত, সেখানে তারা স্থায়ী হবে; আল্লাহ তাদের প্রতি সন্তুষ্ট এবং তারাও তাঁর প্রতি সন্তুষ্ট, তারাই আল্লাহর দল। জেনে রেখ, আল্লাহর দলই সফলকাম হবে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5127,59,1,'আকাশমন্ডলী ও পৃথিবীতে যা কিছু আছে সবই তাঁর পবিত্রতা ও মহিমা ঘোষণা করে, তিনি পরাক্রমশালী, প্রজ্ঞাময়। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5128,59,2,'তিনিই কিতাবীদের মধ্যে যারা কাফির তাদেরকে প্রথম সমাবেশেই তাদের আবাসভূমি হতে বিতাড়িত করেছিলেন। তোমরা কল্পনাও করনি যে, তারা নির্বাসিত হবে এবং তারা মনে করেছিল যে, তাদের দুর্ভেদ্য দুর্গগুলি তাদেরকে রক্ষা করবে আল্লাহ হতে; কিন্তু আল্লাহর শাস্তি এমন এক দিক হতে এলো যা ছিল তাদের ধারনাতীত এবং তাদের অন্তরে তা ত্রাসের সঞ্চার করল। তারা ধ্বংস করে ফেলল তাদের বাড়ীঘর নিজেদের হাতে এবং মু’মিনদের হাতেও। অতএব হে চক্ষুম্মান ব্যক্তিবর্গ! তোমরা উপদেশ গ্রহণ কর!');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5129,59,3,'আল্লাহ তাদের নির্বাসনের সিদ্ধান্ত গ্রহণ না করলে তাদেরকে পৃথিবীতে অন্য শাস্তি দিতেন; পরকালে তাদের জন্য রয়েছে জাহান্নামের শাস্তি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5130,59,4,'উহা এ জন্য যে, তারা আল্লাহ ও তাঁর রাসূলের বিরুদ্ধাচরণ করেছিল, এবং কেহ আল্লাহর বিরুদ্ধাচরণ করলেতো আল্লাহ শাস্তি দানে কঠোর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5131,59,5,'তোমরা যে খেজুর বৃক্ষগুলি কর্তন করেছ এবং যেগুলি কান্ডের উপর স্থির রেখে দিয়েছ, তাতো আল্লাহরই অনুমতিক্রমে; এটা এ জন্য যে, আল্লাহ পাপাচারীদেরকে লাঞ্ছিত করবেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5132,59,6,'আল্লাহ তাদের (ইয়াহুদীদের) নিকট হতে যে ‘ফাই’ তাঁর রাসূলকে দিয়েছেন, উহার জন্য তোমরা অশ্ব কিংবা উষ্ট্রে আরোহণ করে যুদ্ধ করনি; আল্লাহতো যার উপর ইচ্ছা তাঁর রাসূলদের কর্তৃত্ব দান করেন; আল্লাহ সর্ব বিষয়ে সর্বশক্তিমান।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5133,59,7,'আল্লাহ এই জনপদবাসীদের নিকট হতে তাঁর রাসূলকে যা কিছু দিয়েছেন তা আল্লাহর, তাঁর রাসূলের, রাসূলের স্বজনগণের এবং ইয়াতীমদের, অভাবগ্রস্ত ও পথচারীদের যাতে তোমাদের মধ্যে যারা বিত্তবান শুধু তাদের মধ্যেই ঐশ্বর্য আবর্তন না করে। অতএব রাসূল তোমাদেরকে যা দেয় তা তোমরা গ্রহণ কর এবং যা হতে তোমাদেরকে নিষেধ করে তা হতে বিরত থাক। তোমরা আল্লাহকে ভয় কর, আল্লাহ শাস্তি দানে কঠোর। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5134,59,8,'এই সম্পদ অভাবগ্রস্ত মুহাজিরদের জন্য যারা নিজেদের ঘরবাড়ী ও সম্পত্তি হতে উৎখাত হয়েছে। তারা আল্লাহর অনুগ্রহ ও সন্তুষ্টি কামনা করে এবং আল্লাহ ও তাঁর রাসূলের সাহায্য করে। তারাইতো সত্যাশ্রয়ী। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5135,59,9,'মুহাজিরদের আগমনের পূর্বে যারা এই নগরীতে বসবাস করেছে ও ঈমান এনেছে তারা মুহাজিরদেরকে ভালবাসে এবং মুহাজিরদেরকে যা দেয়া হয়েছে তার জন্য তারা অন্তরে বিদ্বেষ পোষণ করেনা, আর তারা তাদেরকে নিজেদের উপর প্রাধান্য দেয় নিজেরা অভাবগ্রস্ত হলেও; যারা কার্পণ্য হতে নিজেদেরকে মুক্ত করেছে তারাই সফলকাম। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5136,59,10,'যারা তাদের পরে এসেছে তারা বলেঃ হে আমাদের রাব্ব! আমাদেরকে এবং ঈমানে অগ্রগামী আমাদের ভাইদেরকে ক্ষমা করুন এবং মু’মিনদের বিরুদ্ধে আমাদের অন্তরে হিংসা-বিদ্বেষ রাখবেননা। হে আমাদের রাব্ব! আপনিতো দয়ার্দ্র, পরম দয়ালু। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5137,59,11,'তুমি কি মুনাফিকদেরকে দেখনি? তারা কিতাবীদের মধ্যে যারা কুফরী করেছে তাদের ঐ সব সঙ্গীকে বলেঃ তোমরা যদি বহিস্কৃত হও তাহলে আমরা অবশ্যই তোমাদের সাথে দেশত্যাগী হব এবং আমরা তোমাদের ব্যাপারে কখনও কারও কথা মানবো না এবং যদি তোমরা আক্রান্ত হও তাহলে আমরা অবশ্যই তোমাদেরকে সাহায্য করব। কিন্তু আল্লাহ সাক্ষ্য দিচ্ছেন যে, তারা অবশ্যই মিথ্যাবাদী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5138,59,12,'বস্তুতঃ তারা বহিস্কৃত হলে মুনাফিকরা তাদের সাথে দেশ ত্যাগ করবেনা এবং তারা আক্রান্ত হলে তারা তাদেরকে সাহায্য করবেনা এবং তারা সাহায্য করতে এলেও অবশ্যই পৃষ্ঠ প্রদর্শন করবে, অতঃপর তারা কোন সাহায্যই পাবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5139,59,13,'প্রকৃত পক্ষে, তাদের অন্তরে আল্লাহ অপেক্ষা তোমরাই অধিকতর ভয়ংকর। এটা এ জন্য যে, তারা এক নির্বোধ সম্প্রদায়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5140,59,14,'তারা সবাই সমবেতভাবেও তোমাদের বিরুদ্ধে যুদ্ধ করতে সমর্থ হবেনা, কিন্তু শুধু সুরক্ষিত জনপদের অভ্যন্তরে অথবা দুর্গ প্রাচীরের অন্তরালে থেকে, পরস্পরের মধ্যে তাদের যুদ্ধ প্রচন্ড। তুমি মনে কর তারা ঐক্যবদ্ধ, কিন্তু তাদের মনের মিল নেই; এটা এ জন্য যে, তারা এক নির্বোধ সম্প্রদায়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5141,59,15,'তাদের তুলনা তাদের পূর্বে যারা নিজেদের কৃতকর্মের শাস্তি আস্বাদন করেছে, এবং তাদের জন্যও রয়েছে যন্ত্রণাদায়ক শাস্তি। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5142,59,16,'তাদের তুলনা হচ্ছে শাইতান, যখন সে মানুষকে বলে, কুফরী কর। অতঃপর যখন সে কুফরী করে তখন শাইতান বলেঃ তোমার সাথে আমার কোন সম্পর্ক নেই, আমি জগতসমূহের রাব্ব আল্লাহকে ভয় করি। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5143,59,17,'ফলে উভয়ের পরিণাম হবে জাহান্নাম। সেখানে তারা স্থায়ী হবে এবং এটাই যালিমদের কর্মফল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5144,59,18,'হে মু’মিনগণ! আল্লাহকে ভয় কর, প্রত্যেকেই ভেবে দেখুক যে, আগামী কালের জন্য সে কি অগ্রিম পাঠিয়েছে। আর আল্লাহকে ভয় কর; তোমরা যা কর আল্লাহ সে সম্পর্কে অবহিত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5145,59,19,'আর তাদের মত হয়োনা যারা আল্লাহকে বিস্মৃত হয়েছে, ফলে আল্লাহ তাদেরকে আত্মবিস্মৃত করেছেন। তারাইতো পাপাচারী ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5146,59,20,'জাহান্নামের অধিবাসী এবং জান্নাতের অধিবাসী সমান নয়। জান্নাতবাসীরাই সফলকাম। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5147,59,21,'যদি আমি এই কুরআন পর্বতের উপর অবতীর্ণ করতাম তাহলে তুমি দেখতে যে, ওটা আল্লাহর ভয়ে বিনীত ও বিদীর্ণ হয়ে গেছে। আমি এ সব দৃষ্টান্ত বর্ণনা করি মানুষের জন্য যাতে তারা চিন্তা করে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5148,59,22,'তিনিই আল্লাহ, তিনি ছাড়া কোন মা‘বূদ নেই, তিনি অদৃশ্য এবং দৃশ্যের পরিজ্ঞাতা; তিনি দয়াময়, পরম দয়ালু। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5149,59,23,'তিনিই আল্লাহ, তিনি ব্যতীত কোন মা‘বূদ নেই। তিনিই অধিপতি, তিনিই পবিত্র, তিনিই শান্তি, তিনিই নিরাপত্তা বিধায়ক, তিনিই রক্ষক, তিনিই পরাক্রমশালী, তিনিই প্রবল, তিনিই অতীব মহিমান্বিত; যারা তাঁর শরীক স্থির করে আল্লাহ তা হতে পবিত্র ও মহান। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5150,59,24,'তিনিই আল্লাহ, সৃজনকর্তা, উদ্ভাবক, রূপদাতা, সকল উত্তম নাম তাঁরই। আকাশ ও পৃথিবীতে যা কিছু আছে সমস্তই তাঁর পবিত্রতা ও মহিমা ঘোষণা করে। তিনি পরাক্রমশালী, প্রজ্ঞাময়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5151,60,1,'হে মুমিনগণ! আমার শত্রু ও তোমাদের শত্রুকে বন্ধু রূপে গ্রহণ করনা; তোমরা কি তাদের সাথে বন্ধুত্ব করছ? অথচ তারা তোমাদের নিকট যে সত্য এসেছে তা প্রত্যাখ্যান করেছে; রাসূলকে এবং তোমাদেরকে বহিস্কার করেছে এ কারণে যে, তোমরা তোমাদের রাব্ব আল্লাহর উপর ঈমান এনেছ। যদি তোমরা আমার সন্তুষ্টি লাভের জন্য আমার পথে জিহাদের উদ্দেশে বহির্গত হয়ে থাক তাহলে কেন তোমরা তাদের সাথে গোপনে বন্ধুত্ব করছ? তোমরা যা গোপন কর এবং তোমরা যা প্রকাশ কর তা আমি সম্যক অবগত। তোমাদের যে কেহ এটা করে সেতো বিচ্যুত হয় সরল পথ হতে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5152,60,2,'তোমাদেরকে কাবু করতে পারলে তারা হবে তোমাদের শত্রু এবং হাত ও জিহবা দ্বারা তোমাদের অনিষ্ট সাধন করবে এবং কামনা করবে যেন তোমরা কুফরী কর। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5153,60,3,'তোমাদের অত্মীয়-স্বজন ও সন্তান-সন্ততি কিয়ামাত দিবসে কোন কাজে আসবেনা। আল্লাহ তোমাদের মধ্যে ফাইসালা করে দিবেন; তোমরা যা কর তিনি তা দেখেন। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5154,60,4,'তোমাদের জন্য ইবরাহীম ও তার অনুসারীদের মধ্যে রয়েছে উত্তম আদর্শ। তারা তাদের সম্প্রদায়কে বলেছিলঃ তোমাদের সঙ্গে এবং তোমরা আল্লাহর পরিবর্তে যার ইবাদাত কর তার সঙ্গে আমাদের কোন সম্পর্ক নেই। আমরা তোমাদেরকে মানিনা। তোমাদের ও আমাদের মধ্যে শুরু হল শত্রুতা ও বিদ্বেষ চিরকালের জন্য, যদি না তোমরা এক আল্লাহয় ঈমান আন। তবে ব্যতিক্রম তার পিতার প্রতি ইবরাহীমের উক্তিঃ আমি নিশ্চয়ই তোমার জন্য ক্ষমা প্রার্থনা করব, যদিও তোমার ব্যাপারে আমি আল্লাহর নিকট কোন অধিকার রাখিনা। (ইবরাহীম ও তার অনুসারীগণ বলেছিল) হে আমাদের রাববঃ আমরাতো আপনারই উপর নির্ভর করেছি, আপনারই অভিমুখী হয়েছি এবং প্রত্যাবর্তনতো আপনারই নিকট।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5155,60,5,'হে আমাদের রাব্ব আপনি আমাদেরকে কাফিরদের পীড়নের পাত্র করবেননা, হে আমাদের রাব্ব! আপনি আমাদেরকে ক্ষমা করুন! আপনিতো পরাক্রমশালী, প্রজ্ঞাময়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5156,60,6,'তোমরা যারা আল্লাহ ও আখিরাতের প্রত্যাশা কর নিশ্চয়ই তাদের জন্য তাদের মধ্যে রয়েছে উত্তম আদর্শ। কেহ মুখ ফিরিয়ে নিলে সে জেনে রাখুক যে, আল্লাহতো অভাবমুক্ত, প্রশংসা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5157,60,7,'যাদের সাথে তোমাদের শত্রুতা রয়েছে সম্ভবতঃ আল্লাহ তাদের ও তোমাদের মধ্যে বন্ধুত্ব সৃষ্টি করে দিবেন; আল্লাহ সর্বশক্তিমান, পরম দয়ালু।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5158,60,8,'দীনের ব্যাপারে যারা তোমাদের বিরুদ্ধে যুদ্ধ করেনি এবং তোমাদের স্বদেশ হতে বহিস্কৃত করেনি তাদের প্রতি মহানুভবতা প্রদর্শন ও ন্যায়বিচার করতে আল্লাহ তোমাদেরকে নিষেধ করেন না। আল্লাহতো ন্যায়পরায়ণদেরকে ভালবাসেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5159,60,9,'আল্লাহ শুধু তাদের সাথে বন্ধুত্ব করতে নিষেধ করেন যারা দীনের ব্যাপারে তোমাদের সাথে যুদ্ধ করেছে, তোমাদেরকে স্বদেশ হতে বহিস্কৃত করেছে এবং তোমাদের বহিঃস্করণে সাহায্য করেছে। তাদের সাথে যারা বন্ধুত্ব করে তারাতো অত্যাচারী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5160,60,10,'হে মু’মিনগণ! তোমাদের নিকট মু’মিনা নারীরা দেশত্যাগী হয়ে এলে তোমরা তাদেরকে পরীক্ষা কর, আল্লাহ তাদের ঈমান সম্বন্ধে সম্যক অবগত আছেন। যদি তোমরা জানতে পার যে, তারা মু’মিনা তাহলে তাদেরকে কাফিরদের নিকট ফেরত পাঠিয়ে দিওনা। মু’মিনা নারীরা কাফিরদের জন্য বৈধ নয় এবং কাফিরেরা মু’মিনা নারীদের জন্য বৈধ নয়। কাফিরেরা যা ব্যয় করেছে তা তাদেরকে ফিরিয়ে দিবে। অতঃপর তোমরা তাদেরকে বিয়ে করলে কোন অপরাধ হবেনা, যদি তোমরা তাদেরকে তাদের মোহর দিয়ে দাও। তোমরা কাফির নারীদের সাথে দাম্পত্য সম্পর্ক বজায় রেখনা। তোমরা যা ব্যয় করেছ তা ফেরত চাবে এবং কাফিরেরা ফেরৎ চাবে যা তারা ব্যয় করেছে। এটাই আল্লাহর বিধান; তিনি তোমাদের মধ্যে ফাইসালা করে থাকেন। আল্লাহ সর্বজ্ঞ, প্রজ্ঞাময়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5161,60,11,'তোমাদের স্ত্রীদের মধ্যে যদি কেহ হাতছাড়া হয়ে কাফিরদের নিকট চলে যায় এবং তোমাদের যদি সুযোগ আসে তখন যাদের স্ত্রীরা হাতছাড়া হয়ে গেছে তাদের জন্য তারা (কাফিরেরা) যা ব্যয় করেছে তার সম পরিমাণ অর্থ প্রদান করবে। ভয় কর আল্লাহকে যাকে তোমরা বিশ্বাস কর। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5162,60,12,'হে নাবী! মু’মিনা নারীরা যখন তোমার নিকট এসে বাইআ’ত করে এই মর্মে যে, তারা আল্লাহর সাথে কোন শরীক স্থির করবেনা, চুরি করবেনা, ব্যভিচার করবেনা, নিজেদের সন্তানদেরকে হত্যা করবেনা, তারা সজ্ঞানে কোন অপবাদ রচনা করে রটাবেনা এবং সত্য কাজে তোমাকে অমান্য করবেনা তখন তাদের বাইআ’ত গ্রহণ কর এবং তাদের জন্য আল্লাহর নিকট ক্ষমা প্রার্থনা কর। আল্লাহতো ক্ষমাশীল, পরম দয়ালু। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5163,60,13,'হে মু’মিনগণ! আল্লাহ যে সম্প্রদায়ের প্রতি রুষ্ট তোমরা তাদের সাথে বন্ধুত্ব করনা, তারাতো আখিরাত সম্পর্কে হতাশ হয়ে পড়েছে যেমন হতাশ হয়েছে কাফিরেরা সমাধিস্থদের বিষয়ে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5164,61,1,'আকাশমন্ডলী ও পৃথিবীতে যা কিছু আছে সমস্তই আল্লাহর পবিত্রতা ও মহিমা ঘোষণা করে। তিনি পরাক্রমশালী, প্রজ্ঞাময়। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5165,61,2,'হে মু’মিনগণ! তোমরা যা করনা তা তোমরা কেন বল? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5166,61,3,'তোমরা যা করনা তোমাদের তা বলা আল্লাহর দৃষ্টিতে অতিশয় অসন্তোষজনক।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5167,61,4,'যারা আল্লাহর পথে সংগ্রাম করে সারিবদ্ধভাবে সুদৃঢ় প্রাচীরের মত, আল্লাহ তাদেরকে ভালবাসেন। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5168,61,5,'স্মরণ কর মূসা তার সম্প্রদায়কে বলেছিলঃ হে আমার সম্প্রদায়! তোমরা আমাকে কেন কষ্ট দিচ্ছ যখন তোমরা জান যে, আমি তোমাদের নিকট আল্লাহর রাসূল? অতঃপর তারা যখন বক্র পথ অবলম্বন করল তখন আল্লাহ তাদের হৃদয়কে বক্র করে দিলেন। আল্লাহ পাপাচারী সম্প্রদায়কে হিদায়াত করেন না। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5169,61,6,'স্মরণ কর, মারইয়াম তনয় ঈসা বললঃ হে বানী ইসরাঈল! আমি তোমাদের নিকট আল্লাহর রাসূল এবং আমার পূর্ব হতে তোমাদের নিকট যে তাওরাত রয়েছে আমি উহার সমর্থক এবং আমার পরে আহমাদ নামে যে রাসূল আসবেন আমি তাঁর সুসংবাদদাতা। পরে সে যখন স্পষ্ট নিদর্শনসহ তাদের নিকট এলো তখন তারা বলতে লাগলঃ এটাতো এক স্পষ্ট যাদু। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5170,61,7,'যে ব্যক্তি ইসলামের দিকে আহুত হয়েও আল্লাহ সম্বন্ধে মিথ্যা রচনা করে তার অপেক্ষা অধিক যালিম আর কে হতে পারে? আল্লাহ যালিম সম্প্রদায়কে সৎ পথে পরিচালিত করেননা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5171,61,8,'তারা আল্লাহর নূর ফুৎকারে নিভিয়ে দিতে চায়, কিন্তু আল্লাহ তাঁর নূর পূর্ণরূপে উদ্ভাসিত করবেন, যদিও কাফিরেরা তা অপছন্দ করে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5172,61,9,'তিনিই তাঁর রাসূলকে প্রেরণ করেছেন হিদায়াত এবং সত্য দীনসহ সকল দীনের উপর ওকে শ্রেষ্ঠত্ব দানের জন্য, যদিও মুশরিকরা তা অপছন্দ করে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5173,61,10,'হে মু’মিনগণ! আমি কি তোমাদের এমন এক বাণিজ্যের সন্ধান দিব যা তোমাদের রক্ষা করবে যন্ত্রণাদায়ক শাস্তি হতে?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5174,61,11,'তা এই যে, তোমরা আল্লাহ ও তাঁর রাসূলের উপর বিশ্বাস স্থাপন করবে এবং তোমাদের ধন সম্পদ ও জীবন দ্বারা আল্লাহর পথে জিহাদ করবে। এটাই তোমাদের জন্য শ্রেয় যদি তোমরা জানতে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5175,61,12,'আল্লাহ তোমাদের পাপ ক্ষমা করে দিবেন এবং তোমাদের দাখিল করবেন জান্নাতে, যার পাদদেশে নদী প্রবাহিত এবং স্থায়ী জান্নাতের উত্তম বাসগৃহে। এটাই মহা সাফল্য। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5176,61,13,'আর তিনি দান করবেন তোমাদের বাঞ্ছিত আরও একটি অনুগ্রহ; আল্লাহর সাহায্য এবং আসন্ন বিজয়; মু’মিনদেরকে এর সুসংবাদ দাও।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5177,61,14,'হে মু’মিনগণ! আল্লাহর দীনের সাহায্যকারী হও, যেমন মারইয়াম তনয় ঈসা তার শিষ্যদেরকে বলেছিলঃ আল্লাহর পথে কে আমার সাহায্যকারী হবে? হাওয়ারীগণ বলেছিলঃ আমরাইতো আল্লাহর পথে সাহায্যকারী। অতঃপর বানী ইসরাঈলের একদল ঈমান আনল এবং একদল কুফরী করল। পরে আমি মু’মিনদেরকে শক্তিশালী করলাম তাদের শত্রুদের মুকাবিলায়; ফলে তারা বিজয়ী হল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5178,62,1,'আকাশমন্ডলী ও পৃথিবীতে যা কিছু আছে সবাই পবিত্রতা ও মহিমা ঘোষণা করে আল্লাহর, যিনি অধিপতি, পবিত্র, পরাক্রমশালী, প্রজ্ঞাময়। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5179,62,2,'তিনিই উম্মীদের মধ্যে তাদের একজনকে পাঠিয়েছেন রাসূল রূপে যে তাদের নিকট আবৃত্তি করে তাঁর আয়াত, তাদেরকে পবিত্র করে এবং শিক্ষা দেয় কিতাব ও হিকমাত; ইতোপূর্বেতো তারা ছিল ঘোর বিভ্রান্তিতে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5180,62,3,'আর তাদের অন্যান্যের জন্যও, যারা এখনও তাদের সাথে মিলিত হয়নি। আল্লাহ পরাক্রমশালী, প্রজ্ঞাময়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5181,62,4,'এটা আল্লাহরই অনুগ্রহ, যাকে ইচ্ছা তিনি এটা দান করেন। আল্লাহতো মহা অনুগ্রহশীল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5182,62,5,'যাদেরকে তাওরাতের দায়িত্বভার অর্পণ করা হয়েছিল, অতঃপর তা তারা বহন করেনি, তাদের দৃষ্টান্ত পুস্তক বহনকারী গর্দভ। কত নিকৃষ্ট সেই সম্প্রদায়ের দৃষ্টান্ত যারা আল্লাহর আয়াতকে মিথ্যা প্রতিপন্ন করে। আল্লাহ ফাসিক/পাপাচারী সম্প্রদায়কে পথপ্রদর্শন করেন না। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5183,62,6,'বলঃ হে ইয়াহুদীরা! যদি তোমরা মনে কর যে, তোমরাই আল্লাহর বন্ধু, অন্য কোন মানবগোষ্ঠী নয়; তাহলে তোমরা মৃত্যু কামনা কর, যদি তোমরা সত্যবাদী হও। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5184,62,7,'কিন্তু তারা তাদের হস্ত যা অগ্রে প্রেরণ করেছে তার কারণে কখনও মৃত্যু কামনা করবেনা। আল্লাহ যালিমদের সম্পর্কে সম্যক অবগত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5185,62,8,'বলঃ তোমরা যে মৃত্যু হতে পলায়ন কর সেই মৃত্যুর সাথে তোমাদের সাক্ষাৎ হবেই। অতঃপর তোমরা উপস্থিত হবে অদৃশ্য ও দৃশ্যের পরিজ্ঞাতা আল্লাহর নিকট এবং তোমাদেরকে জানিয়ে দেয়া হবে যা তোমরা করতে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5186,62,9,'হে মু’মিনগণ! জুমু’আর দিন যখন সালাতের জন্য আহবান করা হয় তখন তোমরা আল্লাহর স্মরণে ধাবিত হও এবং ক্রয়-বিক্রয় ত্যাগ কর; এটাই তোমাদের জন্য শ্রেয়, যদি তোমরা উপলদ্ধি কর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5187,62,10,'সালাত সমাপ্ত হলে তোমরা পৃথিবীতে ছড়িয়ে পড়বে এবং আল্লাহর অনুগ্রহ সন্ধান করবে ও আল্লাহকে অধিক স্মরণ করবে যাতে তোমরা সফলকাম হও।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5188,62,11,'যখন তারা কোন ব্যবসা বা খেল-তামাশা দেখে তখন তারা তোমাকে দাঁড়ানো অবস্থায় রেখে ওর দিকে ছুটে যায়। বলঃ আল্লাহর নিকট যা আছে তা ক্রীড়া কৌতুক ও ব্যবসা অপেক্ষা উৎকৃষ্ট। আল্লাহ সর্বশ্রেষ্ঠ রিয্ক দাতা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5189,63,1,'মুনাফিকরা যখন তোমার নিকট আসে তখন তারা বলেঃ আমরা সাক্ষ্য দিচ্ছি যে, নিশ্চয়ই আপনি আল্লাহর রাসূল। আর আল্লাহ জানেন যে, তুমি নিশ্চয়ই তাঁর রাসূল এবং আল্লাহ সাক্ষ্য দিচ্ছেন যে, মুনাফিকরা অবশ্যই মিথ্যাবাদী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5190,63,2,'তারা তাদের শপথগুলিকে ঢাল রূপে ব্যবহার করে, আর তারা আল্লাহর পথ হতে মানুষকে নিবৃত্ত করে। তারা যা করছে তা কত মন্দ!');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5191,63,3,'এটা এ জন্য যে, তারা ঈমান আনার পর কুফরী করেছে; ফলে তাদের হৃদয় মোহর করে দেয়া হয়েছে, পরিণামে তারা বোধশক্তি হারিয়ে ফেলেছে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5192,63,4,'তুমি যখন তাদের দিকে তাকাও তখন তাদের দেহাকৃতি তোমার নিকট প্রীতিকর মনে হয় এবং তারা যখন কথা বলে তখন তুমি সাগ্রহে তাদের কথা শ্রবণ কর, যদিও তারা দেয়ালে ঠেকানো কাঠের স্তম্ভ সদৃশ। তারা যে কোন শোরগোলকে মনে করে তাদেরই বিরুদ্ধে। তারাই শত্রু, অতএব তাদের সম্পর্কে সতর্ক হও, আল্লাহ তাদেরকে ধ্বংস করুন! বিভ্রান্ত হয়ে তারা কোথায় চলছে?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5193,63,5,'যখন তাদেরকে বলা হয়ঃ তোমরা এসো, আল্লাহর রাসূল তোমাদের জন্য ক্ষমা প্রার্থনা করবেন, তখন তারা মাথা ঘুরিয়ে নেয় এবং তুমি তাদেরকে দেখতে পাও যে, তারা দম্ভভরে ফিরে যায়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5194,63,6,'তুমি তাদের জন্য ক্ষমা প্রার্থনা কর অথবা না কর, উভয়ই তাদের জন্য সমান। আল্লাহ তাদেরকে কখনও ক্ষমা করবেননা। আল্লাহ পাপাচারী সম্প্রদায়কে সৎ পথে পরিচালিত করেননা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5195,63,7,'তারাই বলেঃ আল্লাহর রাসূলের সহচরদের জন্য ব্যয় করনা যতক্ষণ না তারা সরে পড়ে। আকাশমন্ডলী ও পৃথিবীর ধন ভান্ডারতো আল্লাহরই। কিন্তু মুনাফিকরা তা বুঝেনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5196,63,8,'তারা বলেঃ আমরা মাদীনায় প্রত্যাবর্তন করলে সেখান হতে প্রবল দুর্বলকে বহিস্কৃত করবেই। কিন্তু সম্মানতো আল্লাহরই, আর তাঁর রাসূল ও মু’মিনদের। কিন্তু মুনাফিকরা এটা জানেনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5197,63,9,'হে মু’মিনগণ! তোমাদের ঐশ্বর্য ও সন্তান-সন্ততি যেন তোমাদেরকে আল্লাহর স্মরণে উদাসীন না করে। যারা উদাসীন হবে তারাইতো ক্ষতিগ্রস্ত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5198,63,10,'আমি তোমাদেরকে যে রিয্ক দিয়েছি তোমরা তা হতে ব্যয় করবে তোমাদের কারও মৃত্যু আসার পূর্বে; অন্যথায় সে বলবেঃ হে আমার রাব্ব! আমাকে আরও কিছু কালের জন্য অবকাশ দিলে আমি সাদাকাহ করতাম এবং সৎকর্মশীলদের অন্তর্ভুক্ত হতাম। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5199,63,11,'কিন্তু নির্ধারিত কাল যখন উপস্থিত হবে, আল্লাহ তখন কেহকেও অবকাশ দিবেননা। তোমরা যা কর আল্লাহ সে সম্বন্ধে সবিশেষ অবহিত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5200,64,1,'আকাশমন্ডলী ও পৃথিবীতে যা কিছু আছে সবাই তাঁর পবিত্রতা ও মহিমা ঘোষণা করে, সার্বভৌমত্ব তাঁরই এবং প্রশংসা তাঁরই; তিনি সর্ব বিষয়ে সর্বশক্তিমান।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5201,64,2,'তিনিই তোমাদেরকে সৃষ্টি করেছেন, অতঃপর তোমাদের মধ্যে কেহ হয় কাফির এবং কেহ মু’মিন। তোমরা যা কর আল্লাহ সম্যক দ্রষ্টা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5202,64,3,'তিনি সৃষ্টি করেছেন আকাশমন্ডলী ও পৃথিবী যথাযথভাবে এবং তোমাদেরকে আকৃতি দান করেছেন। তোমাদের আকৃতি করেছেন সুশোভন এবং প্রত্যাবর্তনতো তাঁরই নিকট।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5203,64,4,'আকাশমন্ডলী ও পৃথিবীতে যা কিছু আছে সবই তিনি জানেন, তোমরা যা গোপন কর ও তোমরা যা প্রকাশ কর এবং তিনি অন্তর্যামী। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5204,64,5,'তোমাদের নিকট কি পৌঁছেনি পূর্ববর্তী কাফিরদের বৃত্তান্ত? তারা তাদের কর্মের মন্দফল আস্বাদন করেছিল এবং তাদের জন্য রয়েছে যন্ত্রণাদায়ক শাস্তি। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5205,64,6,'তা এ জন্য যে, তাদের নিকট যখন তাদের রাসূলগণ স্পষ্ট নিদর্শনসহ আসতো তখন তারা বলতঃ মানুষই কি আমাদের পথের সন্ধান দিবে? অতঃপর তারা কুফরী করল ও মুখ ফিরিয়ে নিল; কিন্তু এতে আল্লাহর কিছু আসে যায়না। আল্লাহ অভাবমুক্ত, প্রশংসা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5206,64,7,'কাফিরেরা দাবী করে যে, তারা কখনও পুনরুত্থিত হবেনা। বলঃ নিশ্চয়ই হবে, আমার রবের শপথ! তোমরা অবশ্যই পুনরুত্থিত হবে। অতঃপর তোমরা যা করতে তোমাদের সেই সম্বন্ধে অবশ্যই অবহিত করা হবে। এটা আল্লাহর পক্ষে সহজ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5207,64,8,'অতএব তোমরা আল্লাহ, তাঁর রাসূল ও যে জ্যোতি আমি অবতীর্ণ করেছি তাতে বিশ্বাস স্থাপন কর। তোমাদের কৃতকর্ম সম্পর্কে আল্লাহ সবিশেষ অবহিত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5208,64,9,'স্মরণ কর, যেদিন তিনি তোমাদেরকে সমবেত করবেন সমাবেশ দিনে, সেদিন হবে লাভ লোকসানের দিন। যে ব্যক্তি আল্লাহয় বিশ্বাস করে ও সৎ কাজ করে তিনি তার পাপ মোচন করবেন এবং তাকে দাখিল করবেন জান্নাতে, যার পাদদেশে নদী প্রবাহিত, সেখানে তারা হবে চিরস্থায়ী। এটাই মহা সাফল্য।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5209,64,10,'কিন্তু যারা কুফরী করে এবং আমার নিদর্শনসমূহকে অস্বীকার করে তারাই জাহান্নামের অধিবাসী, সেখানে তারা স্থায়ী হবে, কত মন্দ ঐ প্রত্যাবর্তনস্থল!');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5210,64,11,'আল্লাহর অনুমতি ব্যতিরেকে কোন বিপদই আপতিত হয়না এবং যে আল্লাহকে বিশ্বাস করে তিনি তার অন্তরকে সুপথে পরিচালিত করেন। আল্লাহ সর্ব বিষয়ে সম্যক অবগত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5211,64,12,'আল্লাহর আনুগত্য কর এবং তাঁর রাসূলের আনুগত্য কর, যদি তোমরা মুখ ফিরিয়ে নাও তাহলে আমার রাসূলের দায়িত্ব শুধু স্পষ্টভাবে প্রচার করা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5212,64,13,'আল্লাহ ব্যতীত কোন মা‘বূদ নেই; সুতরাং মু’মিনরা যেন আল্লাহ উপরই নির্ভর করে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5213,64,14,'হে মু’মিনগণ! তোমাদের স্ত্রী ও সন্তান-সন্ততিদের মধ্যে কেহ কেহ তোমাদের শত্রু, অতএব তাদের সম্পর্কে তোমরা সতর্ক থেক। তোমরা যদি তাদেরকে মার্জনা কর, তাদের দোষ ক্রটি উপেক্ষা কর এবং তাদেরকে ক্ষমা কর তাহলে জেনে রেখ যে, আল্লাহ ক্ষমাশীল, পরম দয়ালু।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5214,64,15,'তোমাদের সম্পদ ও সন্তান সন্ততি তোমাদের জন্য পরীক্ষা। আল্লাহরই নিকট রয়েছে মহা পুরস্কার।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5215,64,16,'তোমরা আল্লাহকে যথাযথ ভয় কর, শোন, আনুগত্য কর ও ব্যয় কর তোমাদের নিজেদেরই কল্যাণে; যারা অন্তরের কার্পণ্য হতে মুক্ত তারাই সফলকাম।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5216,64,17,'যদি তোমরা আল্লাহকে উত্তম ঋণ দান কর তাহলে তিনি তোমাদের জন্য ওটা দ্বিগুণ বৃদ্ধি করবেন এবং তিনি তোমাদেরকে ক্ষমা করবেন। আল্লাহ গুণগ্রাহী ও সহনশীল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5217,64,18,'তিনি দৃশ্য ও অদৃশ্যের পরিজ্ঞাতা, পরাক্রমশালী, প্রজ্ঞাময়। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5218,65,1,'হে নাবী! তোমরা যদি তোমাদের স্ত্রীদেরকে তালাক দিতে ইচ্ছা কর তাহলে তাদেরকে তালাক দিও ইদ্দাতের প্রতি লক্ষ্য রেখে, ইদ্দাতের হিসাব রেখ এবং তোমাদের রাব্ব আল্লাহকে ভয় কর; তোমরা তাদেরকে তাদের বাসগৃহ হতে বহিস্কার করনা এবং তারাও যেন বের না হয়, যদি না তারা লিপ্ত হয় স্পষ্ট অশ্লীলতায়; এগুলি আল্লাহর বিধান। যে আল্লাহর বিধান লংঘন করে সে নিজেরই উপর অত্যাচার করে। তুমি জাননা, হয়তো আল্লাহ এরপর কোন উপায় করে দিবেন। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5219,65,2,'তাদের ইদ্দাত পূরণের কাল আসন্ন হলে তোমরা হয় যথাবিধি তাদেরকে রেখে দিবে, না হয় তাদেরকে যথাবিধি পরিত্যাগ করবে এবং তোমাদের মধ্য হতে দু’জন ন্যায় পরায়ণ লোককে সাক্ষী রাখবে; তোমরা আল্লাহর জন্য সঠিক সাক্ষ্য দিও। ওটা দ্বারা তোমাদের মধ্যে যে আল্লাহ ও আখিরাতে বিশ্বাস করে তাকে উপদেশ দেয়া হচ্ছে। যে আল্লাহকে ভয় করে আল্লাহ তার নিস্কৃতির ব্যবস্থা করে দিবেন –');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5220,65,3,'আর তাকে তার ধারণাতীত উৎস থেকে দান করবেন রিয্ক; যে ব্যক্তি আল্লাহর উপর নির্ভর করে তার জন্য আল্লাহই যথেষ্ট, আল্লাহ তাঁর ইচ্ছা পূরণ করবেনই, আল্লাহ সব কিছুর জন্য স্থির করেছেন নির্দিষ্ট মাত্রা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5221,65,4,'তোমাদের যে সব স্ত্রীর ঋতুমতী হওয়ার আশা নেই তাদের ইদ্দাত সম্পর্কে তোমরা সন্দেহ করলে তাদের ইদ্দাতকাল হবে তিন মাস এবং যাদের এখনও রজশ্বালা হয়নি তাদেরও। এবং গর্ভবতী নারীদের ইদ্দাতকাল সন্তান প্রসব পর্যন্ত। আল্লাহকে যে ভয় করে আল্লাহ তার সমস্যার সমাধান সহজ করে দিবেন। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5222,65,5,'এটা আল্লাহর বিধান যা তিনি তোমাদের প্রতি অবতীর্ণ করেছেন; আল্লাহকে যে ভয় করে তিনি তার পাপ মোচন করবেন এবং তাকে দিবেন মহা পুরস্কার। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5223,65,6,'তোমরা তোমাদের সামর্থ্য অনুযায়ী যে স্থানে বাস কর তাদেরকে সেই স্থানে বাস করতে দিও; তাদেরকে উত্যক্ত করনা সংকটে ফেলার জন্য, তারা গর্ভবতী হয়ে থাকলে সন্তান প্রসব পর্যন্ত তাদের জন্য ব্যয় করবে, যদি তারা তোমাদের সন্তানদেরকে স্তন্য দান করে তাহলে তাদেরকে পারিশ্রমিক দিবে এবং সন্তানের কল্যাণ সম্পর্কে তোমরা সংগতভাবে নিজেদের মধ্যে পরামর্শ করবে; তোমরা যদি নিজ নিজ দাবীতে অনমনীয় হও তাহলে অন্য নারী তার পক্ষে স্তন্য দান করবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5224,65,7,'বিত্তবান নিজ সামর্থ অনুযায়ী ব্যয় করবে এবং যার জীবনোপকরণ সীমিত সে, আল্লাহ যা তাকে দান করেছেন তা হতে ব্যয় করবে। আল্লাহ যাকে যে সামর্থ্য দিয়েছেন তদপেক্ষা গুরুতর বোঝা তিনি তার উপর চাপিয়ে দেননা। আল্লাহ কষ্টের পর দিবেন স্বস্তি। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5225,65,8,'কত জনপদ তাদের রাব্ব ও তাঁর রাসূলদের নির্দেশের বিরুদ্ধাচরণ করেছিল দম্ভভরে। ফলে আমি তাদের নিকট হতে কঠোর হিসাব নিয়েছিলাম এবং তাদেরকে দিয়েছিলাম কঠিন শাস্তি। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5226,65,9,'অতঃপর তারা তাদের কৃতকর্মের শাস্তি আস্বাদন করল; ক্ষতিই ছিল তাদের কর্মের পরিণাম।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5227,65,10,'আল্লাহ তাদের জন্য কঠিন শাস্তি প্রস্তুত করে রেখেছেন। অতএব তোমরা আল্লাহকে ভয় কর। হে বোধসম্পন্ন ব্যক্তিগণ, যারা ঈমান এনেছ! নিশ্চয়ই আল্লাহ তোমাদের প্রতি অবতীর্ণ করেছেন উপদেশ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5228,65,11,'প্রেরণ করেছেন এমন এক রাসূল যে তোমাদের নিকট আল্লাহর সুস্পষ্ট আয়াত আবৃত্তি করে, যারা বিশ্বাসী ও সৎকর্মপরায়ণ তাদেরকে অন্ধকার থেকে আলোতে আনার জন্য। যে কেহ আল্লাহকে বিশ্বাস করে ও সৎ কাজ করে তিনি তাকে দাখিল করবেন জান্নাতে, যার পাদদেশে নদী প্রবাহিত, সেখানে তারা চিরস্থায়ী হবে; আল্লাহ তাকে উত্তম জীবনোপকরণ দিবেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5229,65,12,'আল্লাহই সৃষ্টি করেছেন সপ্ত আকাশ এবং পৃথিবীও সেই পরিমাণ। ওগুলির মধ্যে নেমে আসে তাঁর নির্দেশ; ফলে তোমরা বুঝতে পার যে, আল্লাহ সর্ব বিষয়ে সর্বশক্তিমান এবং জ্ঞানে আল্লাহ সব কিছুকে পরিবেষ্টন করে রয়েছেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5230,66,1,'হে নাবী! আল্লাহ তোমার জন্য যা বৈধ করেছেন তুমি তা নিষিদ্ধ করছ কেন? তুমি তোমার স্ত্রীদের সন্তুষ্টি চাচ্ছ? আল্লাহ ক্ষমাশীল, পরম দয়ালু। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5231,66,2,'আল্লাহ তোমাদের শপথ হতে মুক্তি লাভের ব্যবস্থা করেছেন, আল্লাহ তোমাদের সহায়; তিনি সর্বজ্ঞ, প্রজ্ঞাময়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5232,66,3,'যখন নাবী তার স্ত্রীদের একজনকে গোপনে কিছু বলেছিল, অতঃপর যখন সে তা অন্যকে বলে দিয়েছিল এবং আল্লাহ নাবীকে তা জানিয়ে দিয়েছিলেন তখন নাবী এই বিষয়ে কিছু ব্যক্ত করল এবং কিছু অব্যক্ত রাখল। যখন নাবী তা তার সেই স্ত্রীকে জানালো তখন সে বললঃ কে আপনাকে এটা অবহিত করল? নাবী বললঃ আমাকে অবহিত করেছেন তিনি যিনি সর্বজ্ঞ, সম্যক অবহিত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5233,66,4,'যদি তোমরা উভয়ে অনুতপ্ত হয়ে আল্লাহর দিকে প্রত্যাবর্তন কর যেহেতু তোমাদের হৃদয় ঝুকে পড়েছে (তাহলে আল্লাহ তোমাদের ক্ষমা করবেন)। কিন্তু তোমরা যদি নাবীর বিরুদ্ধে একে অপরের পোষকতা কর তাহলে জেনে রেখ যে, আল্লাহই তার বন্ধু এবং জিবরাঈল ও সৎ আমলকারী মু’মিনগণও; উপরন্ত অন্যান্য মালাইকা/ফেরেশতারাও তার সাহায্যকারী। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5234,66,5,'যদি নাবী তোমাদের সকলকে পরিত্যাগ করে তাহলে তার রাব্ব সম্ভবতঃ তাকে দিবেন তোমাদের অপেক্ষা উৎকৃষ্টতর স্ত্রী যারা হবে আত্মসমর্পনকারিণী, বিশ্বাসিনী, আনুগত্যকারিনী, তাওবাহকারিনী, ইবাদাতকারিনী, সিয়াম পালনকারিণী, অকুমারী এবং কুমারী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5235,66,6,'হে বিশ্বাস স্থাপনকারীগণ! তোমরা নিজেদেরকে এবং তোমাদের পরিবার পরিজনকে রক্ষা কর অগুন হতে, যার ইন্ধন হবে মানুষ ও পাথর, যাতে নিয়োজিত আছে নির্মম হৃদয় কঠোর স্বভাবের মালাইকা (ফেরেশতা), যারা অমান্য করেনা আল্লাহ যা তাদেরকে আদেশ করেন তা এবং তারা যা করতে আদিষ্ট হয় তা’ই করে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5236,66,7,'হে কাফিরেরা! আজ তোমরা দোষ স্খলনের চেষ্টা করনা। তোমরা যা করতে তোমাদেরকে তারই প্রতিফল দেয়া হবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5237,66,8,'হে বিশ্বাস স্থাপনকারীগণ! তোমরা আল্লাহর নিকট তাওবাহ কর, বিশুদ্ধ তাওবাহ; সম্ভবতঃ তোমাদের রাব্ব তোমাদের মন্দ কাজগুলি মোচন করে দিবেন এবং তোমাদেরকে দাখিল করবেন জান্নাতে, যার পাদদেশে নদী প্রবাহিত। সেই দিন নাবী এবং তাঁর বিশ্বাসী বান্দাদেরকে আল্লাহ অপদস্ত করবেননা। তাদের জ্যোতি তাদের সম্মুখে এবং দক্ষিণ পার্শ্বে ধাবিত হবে। তারা বলবেঃ হে আমাদের রাব্ব! আমাদের জ্যোতিকে পূর্ণতা দান করুন এবং আমাদেরকে ক্ষমা করুন, আপনি সর্ব বিষয়ে ক্ষমতাবান।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5238,66,9,'হে নাবী! কাফির ও মুনাফিকের বিরুদ্ধে জিহাদ কর এবং তাদের প্রতি কঠোর হও। তাদের আশ্রয়স্থল জাহান্নাম, ওটা কত নিকৃষ্ট প্রত্যাবর্তনস্থল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5239,66,10,'আল্লাহ কাফিরদের জন্য নূহ ও লূতের স্ত্রীর দৃষ্টান্ত উপস্থিত করছেন; তারা ছিল আমার বান্দাদের মধ্যে দুই সংকল্প পরায়ণ বান্দার অধীন। কিন্তু তারা তাদের প্রতি বিশ্বাসঘাতকতা করেছিল, ফলে নূহ ও লূত তাদেরকে আল্লাহর শাস্তি হতে রক্ষা করতে পারলনা এবং তাদেরকে বলা হলঃ জাহান্নামে প্রবেশকারীদের সাথে তোমরাও তাতে প্রবেশ কর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5240,66,11,'আল্লাহ বিশ্বাসীদের জন্য উপস্থিত করেছেন ফির‘আউন পত্নীর দৃষ্টান্ত, যে প্রার্থনা করেছিলঃ হে আমার রাব্ব! আপনার সন্নিধানে জান্নাতে আমার জন্য একটি গৃহ নির্মাণ করুন এবং আমাকে উদ্ধার করুন ফির‘আউন ও তার দুস্কৃতি হতে এবং আমাকে উদ্ধার করুন যালিম সম্প্রদায় হতে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5241,66,12,'আরও দৃষ্টান্ত দিচ্ছেন ইমরান তনয়া মারইয়ামের, যে তার সতীত্ব রক্ষা করেছিল, ফলে আমি তার মধ্যে রূহ ফুঁকে দিয়েছিলাম এবং সে তার রবের বাণী ও তাঁর কিতাবসমূহ সত্য বলে গ্রহণ করেছিল; সে ছিল অনুগতদের একজন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5242,67,1,'মহা মহিমান্বিত তিনি, সর্বময় কর্তৃত্ব যাঁর করায়ত্ব; তিনি সর্ব বিষয়ে সর্বশক্তিমান –');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5243,67,2,'যিনি সৃষ্টি করেছেন মৃত্যু ও জীবন, তোমাদের পরীক্ষা করার জন্য - কে তোমাদের মধ্যে কর্মে উত্তম? তিনি পরাক্রমশালী, ক্ষমাশীল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5244,67,3,'তিনি সৃষ্টি করেছেন স্তরে স্তরে সপ্তাকাশ। দয়াময় আল্লাহর সৃষ্টিতে তুমি কোন খুঁত দেখতে পাবেনা; আবার দেখ, কোন ত্রুটি দেখতে পাও কি?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5245,67,4,'অতঃপর তুমি বারবার দৃষ্টি ফিরাও, সেই দৃষ্টি ব্যর্থ ও ক্লান্ত হয়ে তোমার দিকে ফিরে আসবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5246,67,5,'আমি নিকটবর্তী আকাশকে সুশোভিত করেছি প্রদীপমালা দ্বারা এবং ওগুলিকে করেছি শাইতানের প্রতি নিক্ষেপের উপকরণ এবং তাদের জন্য প্রস্তুত রেখেছি জ্বলন্ত আগুনের শাস্তি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5247,67,6,'যারা তাদের রাব্বকে অস্বীকার করে তাদের জন্য রয়েছে জাহান্নামের শাস্তি, ওটা কত মন্দ প্রত্যাবর্তন স্থল!');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5248,67,7,'যখন তন্মধ্যে নিক্ষিপ্ত হবে তখন তারা উহার উৎক্ষিপ্ত গর্জন শুনতে পাবে, আর ওটা হবে উদ্বেলিত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5249,67,8,'রোষে জাহান্নাম যেন ফেটে পড়বে, যখনই তাতে কোন দলকে নিক্ষেপ করা হবে, তাদেরকে রক্ষীরা জিজ্ঞেস করবেঃ তোমাদের নিকট কি কোন সতর্ককারী আসেনি?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5250,67,9,'তারা বলবেঃ অবশ্যই আমাদের নিকট সতর্ককারী এসেছিল, আমরা তাকে মিথ্যাবাদী গণ্য করেছিলাম এবং বলেছিলামঃ আল্লাহ কিছুই অবতীর্ণ করেননি, তোমরাতো মহা বিভ্রান্তিতে রয়েছ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5251,67,10,'এবং তারা আরও বলবেঃ যদি আমরা শুনতাম অথবা বিবেক-বুদ্ধি প্রয়োগ করতাম তাহলে আমরা জাহান্নামবাসী হতামনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5252,67,11,'তারা তাদের অপরাধ স্বীকার করবে। অভিশাপ জাহান্নামীদের জন্য! ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5253,67,12,'নিশ্চয়ই যারা তাদের রাব্বকে না দেখেই ভয় করে তাদের জন্য রয়েছে ক্ষমা ও মহাপুরস্কার।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5254,67,13,'তোমরা তোমাদের কথা গোপনেই বল অথবা প্রকাশ্যে বল, তিনিতো অন্তর্যামী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5255,67,14,'তোমরা তোমাদের কথা গোপনেই বল অথবা প্রকাশ্যে বল, তিনি তো অন্তর্যামী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5256,67,15,'তিনিইতো তোমাদের জন্য ভূমিকে সুগম করে দিয়েছেন; অতএব তোমরা দিক-দিগন্তে বিচরণ কর এবং তাঁর প্রদত্ত জীবনোপকরণ হতে আহার্য গ্রহণ কর; পুনরুত্থানতো তাঁরই নিকট। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5257,67,16,'তোমরা কি নিশ্চিত আছ যে, আকাশে যিনি রয়েছেন তিনি তোমাদেরকেসহ ভূমিকে ধ্বসিয়ে দিবেননা, আর ওটা আকস্মিকভাবে থর থর করে কাঁপতে থাকবে?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5258,67,17,'অথবা তোমরা নিশ্চিত আছ যে, আকাশে যিনি রয়েছেন তিনি তোমাদের উপর কংকরবর্ষী ঝঞ্ঝা প্রেরণ করবেননা? তখন তোমরা জানতে পারবে কি রূপ ছিল আমার সতর্ক বাণী! ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5259,67,18,'এবং এদের পূর্ববর্তীরাও মিথ্যা আরোপ করেছিল; ফলে কি রূপ হয়েছিল আমার শাস্তি! ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5260,67,19,'তারা কি লক্ষ্য করেনা তাদের উর্ধ্বদেশে বিহঙ্গকূলের প্রতি, যারা পক্ষ বিস্তার করে ও সংকুচিত করে? দয়াময় আল্লাহই তাদেরকে স্থির রাখেন। তিনি সর্ব বিষয়ে সম্যক দ্রষ্টা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5261,67,20,'দয়াময় আল্লাহ ব্যতীত তোমাদের এমন কোন সৈন্যবাহিনী আছে কি যারা তোমাদের সাহায্য করবে? কাফিরেরা তো বিভ্রান্তিতে রয়েছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5262,67,21,'এমন কে আছে, যে তোমাদের জীবনোপকরণ দান করবে, তিনি যদি জীবনোপকরণ বন্ধ করে দেন? বস্তুতঃ তারা অবাধ্যতা ও সত্য বিমুখতায় অবিচল রয়েছে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5263,67,22,'যে ব্যক্তি মুখে ভর দিয়ে ঝুকে চলে সে কি ঠিক পথে চলে, না কি সেই ব্যক্তি যে সরল পথে চলে? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5264,67,23,'বলঃ তিনিই তোমাদেরকে সৃষ্টি করেছেন এবং তোমাদেরকে দিয়েছেন শ্রবণশক্তি, দৃষ্টিশক্তি ও অন্তঃকরণ। তোমরা অল্পই কৃতজ্ঞতা প্রকাশ করে থাক। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5265,67,24,'বলঃ তিনিই পৃথিবীতে তোমাদেরকে ছড়িয়ে দিয়েছেন এবং তাঁরই নিকট তোমাদেরকে সমবেত করা হবে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5266,67,25,'তারা বলেঃ তোমরা যদি সত্যবাদী হও তাহলে বল, এই প্রতিশ্রুতি কখন বাস্তবায়িত হবে?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5267,67,26,'বলঃ এর জ্ঞান শুধু আল্লাহরই নিকট আছে, আমিতো স্পষ্ট সতর্ককারী মাত্র।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5268,67,27,'যখন ওটা আসন্ন দেখবে তখন কাফিরদের মুখমন্ডল ম্লান হয়ে যাবে এবং তাদেরকে বলা হবেঃ এটাইতো তোমরা চাচ্ছিলে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5269,67,28,'বলঃ তোমরা ভেবে দেখেছ কি - যদি আল্লাহ আমাকে ও আমার সঙ্গীদেরকে ধ্বংস করেন অথবা আমাদের প্রতি দয়া প্রদর্শন করেন তাতে কাফিরদের কি? তাদেরকে কে রক্ষা করবে বেদনাদায়ক শাস্তি হতে?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5270,67,29,'বলঃ তিনি দয়াময়, আমরা তাঁকে বিশ্বাস করি ও তাঁরই উপর নির্ভর করি, শীঘ্রই তোমরা জানতে পারবে কে স্পষ্ট বিভ্রান্তিতে রয়েছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5271,67,30,'বলঃ তোমরা ভেবে দেখেছ কি, কোনো এক ভোরে যদি পানি ভূ-গর্ভে তোমাদের নাগালের বাইরে চলে যায় তাহলে কে তোমাদেরকে এনে দিবে প্রবাহমান পানি?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5272,68,1,'নুন, শপথ কলমের এবং ওরা যা লিপিবদ্ধ করে তার। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5273,68,2,'তোমার রবের অনুগ্রহে তুমি উম্মাদ নও। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5274,68,3,'তোমার জন্য অবশ্যই রয়েছে নিরবচ্ছিন্ন পুরস্কার। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5275,68,4,'তুমি অবশ্যই মহান চরিত্রের অধিকারী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5276,68,5,'শীঘ্রই তুমি দেখবে এবং তারাও দেখবে – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5277,68,6,'তোমাদের মধ্যে কে বিকারগ্রস্ত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5278,68,7,'তোমার রাব্বতো সম্যক অবগত আছেন যে, কে তাঁর পথ হতে বিচ্যুত হয়েছে এবং তিনি জানেন তাদেরকে যারা সৎ পথপ্রাপ্ত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5279,68,8,'সুতরাং তুমি মিথ্যাচারীদের অনুসরণ করনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5280,68,9,'তারা চায় যে, তুমি নমনীয় হও, তাহলে তারাও নমনীয় হবে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5281,68,10,'এবং অনুসরণ করনা তার যে কথায় কথায় শপথ করে, যে লাঞ্ছিত – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5282,68,11,'পশ্চাতে নিন্দাকারী, যে একের কথা অপরের নিকট লাগিয়ে বেড়ায় – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5283,68,12,'যে কল্যাণের কাজে বাঁধা দান করে, সে সীমালংঘনকারী, পাপিষ্ঠ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5284,68,13,'রূঢ় স্বভাব এবং তদুপরি কুখ্যাত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5285,68,14,'সে ধন-সম্পদ ও সন্তান-সন্ততিতে সমৃদ্ধশালী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5286,68,15,'তার নিকট আমার আয়াত আবৃত্তি করা হলে সে বলেঃ এটাতো সেকালের উপকথা মাত্র।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5287,68,16,'আমি তার নাসিকা দাগিয়ে দিব। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5288,68,17,'আমি তাদেরকে পরীক্ষা করেছি, যেভাবে পরীক্ষা করেছিলাম উদ্যান অধিপতিদেরকে, যখন তারা শপথ করেছিল যে, তারা প্রত্যুষে আহরণ করবে বাগানের ফল, ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5289,68,18,'এবং তারা ইনশাআল্লাহ বলেনি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5290,68,19,'অতঃপর তোমার রবের নিকট হতে এক বিপর্যয় হানা দিল সেই উদ্যানে, যখন তারা ছিল নিদ্রিত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5291,68,20,'ফলে ওটা দগ্ধ হয়ে কৃষ্ণবর্ণ ধারণ করল। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5292,68,21,'প্রত্যুষে তারা একে অপরকে ডেকে বলল – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5293,68,22,'তোমরা যদি ফল আহরণ করতে চাও তাহলে তরিৎ বাগানে চল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5294,68,23,'অতঃপর তারা চললো নিম্ন স্বরে কথা বলতে বলতে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5295,68,24,'অদ্য যেন তোমাদের নিকট কোন অভাবগ্রস্ত ব্যক্তি প্রবেশ করতে না পারে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5296,68,25,'অতঃপর তারা নিবৃত্ত করতে সক্ষম - এই বিশ্বাস নিয়ে প্রভাতকালে বাগানে যাত্রা করল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5297,68,26,'অতঃপর তারা যখন বাগানের অবস্থা প্রত্যক্ষ করল, তারা বললঃ আমরাতো দিশা হারিয়ে ফেলেছি! ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5298,68,27,'না, আমরাতো বঞ্চিত! ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5299,68,28,'তাদের শ্রেষ্ঠ ব্যক্তি বললঃ আমি কি তোমাদেরকে বলিনি? এখন তোমরা আল্লাহর পবিত্রতা ও মহিমা ঘোষণা করছনা কেন? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5300,68,29,'তখন তারা বললঃ আমরা আমাদের রবের পবিত্রতা ও মহিমা ঘোষণা করছি, আমরাতো ছিলাম সীমালংঘনকারী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5301,68,30,'অতঃপর তারা একে অপরের প্রতি দোষারোপ করতে লাগল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5302,68,31,'তারা বললঃ হায়! দুর্ভোগ আমাদের! আমরাতো ছিলাম সীমালংঘনকারী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5303,68,32,'আমরা আশা রাখি, আমাদের রাব্ব এর পরিবর্তে আমাদেরকে দিবেন উৎকৃষ্টতর উদ্যান; আমরা আমাদের রবের অভিমুখী হলাম। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5304,68,33,'শাস্তি এরূপই হয়ে থাকে এবং আখিরাতের শাস্তি কঠিনতর, যদি তারা জানত!');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5305,68,34,'মুত্তাকীদের জন্য অবশ্যই রয়েছে ভোগ বিলাসপূর্ণ জান্নাত, তাদের রবের নিকট।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5306,68,35,'আমি কি আত্মসমর্পনকারীদেরকে অপরাধীদের সদৃশ গন্য করব? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5307,68,36,'তোমাদের কি হয়েছে? তোমাদের এ কেমন সিদ্ধান্ত? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5308,68,37,'তোমাদের নিকট কি কোন কিতাব আছে যা তোমরা অধ্যয়ন কর – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5309,68,38,'যে, তোমাদের জন্য ওতে রয়েছে যা তোমরা পছন্দ কর? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5310,68,39,'আমি কি তোমাদের সাথে কিয়ামাত পর্যন্ত বলবৎ এমন কোন প্রতিজ্ঞায় আবদ্ধ আছি যে, তোমরা নিজেদের জন্য যা স্থির করবে তা পাবে? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5311,68,40,'তুমি তাদেরকে জিজ্ঞেস কর, তাদের মধ্যে এই দাবীর যিম্মাদার কে?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5312,68,41,'তাদের কি কোন শরীক আছে? থাকলে তারা তাদের শরীকদের উপস্থিত করুক, যদি তারা সত্যবাদী হয়। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5313,68,42,'স্মরণ কর, গোছা পর্যন্ত পা খোলার দিনের কথা, সেদিন তাদেরকে আহবান করা হবে সাজদাহ করার জন্য, কিন্তু তারা তা করতে সক্ষম হবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5314,68,43,'তাদের দৃষ্টি অবনত, হীনতা তাদেরকে আচ্ছন্ন করবে, অথচ যখন তারা নিরাপদ ছিল তখনতো তাদেরকে আহবান করা হয়েছিল সাজদাহ করতে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5315,68,44,'যারা আমাকে এবং এই বাণীকে প্রত্যাখ্যান করে তাদেরকে ছেড়ে দাও আমার হাতে, আমি তাদেরকে এমনভাবে ক্রমে ক্রমে ধরব যে, তারা জানতে পারবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5316,68,45,'আর আমি তাদেরকে সময় দিয়ে থাকি, আমার কৌশল অত্যন্ত বলিষ্ঠ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5317,68,46,'তুমি কি তাদের নিকট পারিশ্রমিক চাচ্ছ যে, তারা একে একটি দুর্বহ দন্ড মনে করে?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5318,68,47,'তাদের কি অদৃশ্যের জ্ঞান আছে যে, তারা তা লিখে রাখে? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5319,68,48,'অতএব তুমি ধৈর্য ধারণ কর তোমার রবের নির্দেশের অপেক্ষায়, তুমি মৎস্য সহচরের ন্যায় অধৈর্য হয়োনা, সে বিষাদ আচ্ছন্ন অবস্থায় কাতর প্রার্থনা করছিল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5320,68,49,'তার রবের অনুগ্রহ তার নিকট না পৌঁছলে সে লাঞ্ছিত হয়ে নিক্ষিপ্ত হত উন্মুক্ত প্রান্তরে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5321,68,50,'পুনরায় তার রাব্ব তাকে মনোনীত করলেন এবং তাকে সৎ কর্মপরায়ণদের অন্তর্ভুক্ত করলেন। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5322,68,51,'কাফিরেরা যখন কুরআন শ্রবণ করে তখন তারা যেন উহাদের তীক্ষ্ণ দৃষ্টি দ্বারা তোমাকে আছড়ে ফেলে দিবে এবং বলবে ‘‘এতো এক পাগল’’। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5323,68,52,'কুরআনতো বিশ্ব জগতের জন্য উপদেশ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5324,69,1,'সেই অবশ্যম্ভাবী ঘটনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5325,69,2,'কি সেই অবশ্যম্ভাবী ঘটনা?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5326,69,3,'কিসে তোমাকে জানাবে সেই অবশ্যম্ভাবী ঘটনা কি?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5327,69,4,'‘‘আদ ও সামূদ’’ সম্প্রদায় অস্বীকার করেছিল মহাপ্রলয়। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5328,69,5,'আর সামূদ সম্প্রদায় - তাদেরকে ধ্বংস করা হয়েছিল এক প্রলয়ংকর বিপর্যয় দ্বারা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5329,69,6,'আর ‘আদ’ সম্প্রদায় - তাদেরকে ধ্বংস করা হয়েছিল এক প্রচন্ড ঝঞ্ঝাবায়ু দ্বারা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5330,69,7,'যা তিনি তাদের উপর প্রবাহিত করেছিলেন সাত রাত ও আট দিন বিরামহীনভাবে; তুমি যদি তখন উক্ত সম্প্রদায়কে দেখতে তাহলে দেখতে পেতে যে, তারা সেখানে লুটিয়ে পড়ে আছে বিক্ষিপ্ত অসার খেজুর কান্ডের ন্যায়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5331,69,8,'তুমি তাদের কোনো অস্তিত্ব দেখতে পাও কি? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5332,69,9,'ফির‘আউন, তার পূর্ববর্তীরা এবং লূত সম্প্রদায় পাপাচারে লিপ্ত ছিল। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5333,69,10,'তারা তাদের রবের রাসূলকে অমান্য করেছিল, ফলে তিনি তাদেরকে শাস্তি দেন, কঠোর সেই শাস্তি!');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5334,69,11,'যখন প্লাবন হয়েছিল তখন আমি তোমাদেরকে (মানব জাতিকে) আরোহণ করিয়েছিলাম নৌযানে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5335,69,12,'আমি উহা করেছিলাম তোমাদের শিক্ষার জন্য এবং এ জন্য যে, শ্রুতিধর কর্ণ ইহা সংরক্ষণ করে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5336,69,13,'যখন শিঙ্গায় ফুৎকার দেয়া হবে, একটি মাত্র ফুৎকার,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5337,69,14,'পর্বতমালা সমেত পৃথিবী উৎক্ষিপ্ত হবে এবং একই ধাক্কায় তারা চূর্ণ বিচূর্ণ হয়ে যাবে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5338,69,15,'সেদিন সংঘটিত হবে মহা প্রলয়। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5339,69,16,'এবং আকাশ বিদীর্ণ হয়ে বিক্ষিপ্ত হয়ে পড়বে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5340,69,17,'মালাইকা/ফেরেশতারা আকাশের প্রান্তদেশে থাকবে এবং সেদিন আটজন মালাইকা/ফেরেশতা তাদের রবের ‘আরশকে ধারণ করবে তাদের উর্ধ্বে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5341,69,18,'সেই দিন উপস্থিত করা হবে তোমাদেরকে এবং তোমাদের কিছুই গোপন থাকবেনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5342,69,19,'তখন যাকে তার ‘আমলনামা ডান হাতে দেয়া হবে সে বলবেঃ নাও, আমার ‘আমলনামা পাঠ করে দেখ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5343,69,20,'আমি জানতাম যে, আমাকে হিসাবের সম্মুখীন হতে হবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5344,69,21,'সুতরাং সে যাপন করবে সন্তোষজনক জীবন –');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5345,69,22,'সুমহান জান্নাতে – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5346,69,23,'যার ফলরাশি অবনমিত থাকবে নাগালের মধ্যে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5347,69,24,'তাদেরকে বলা হবেঃ পানাহার কর তৃপ্তির সাথে, তোমরা অতীত দিনে যা করেছিলে তার বিনিময়ে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5348,69,25,'কিন্তু যার ‘আমলনামা তার বাম হস্তে দেয়া হবে সে বলবেঃ ‘হায়! আমাকে যদি দেয়াই না হত আমার ‘আমলনামা!');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5349,69,26,'এবং আমি যদি না জানতাম আমার হিসাব!');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5350,69,27,'হায়! আমার মৃত্যুই যদি আমার শেষ হত!');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5351,69,28,'আমার ধনসম্পদ আমার কোন কাজেই এলোনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5352,69,29,'আমার ক্ষমতাও অপসৃত হয়েছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5353,69,30,'মালাইকা/ফেরেশতাদেরকে বলা হবেঃ ধর ওকে, গলদেশে বেড়ি পরিয়ে দাও। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5354,69,31,'অতঃপর নিক্ষেপ কর জাহান্নামে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5355,69,32,'পুনরায় তাকে শৃংখলিত কর সত্তর হাত দীর্ঘ এক শৃংখলে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5356,69,33,'সে মহান আল্লাহয় বিশ্বাসী ছিলনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5357,69,34,'এবং অভাবগ্রস্তকে খাদ্যদানে উৎসাহিত করতনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5358,69,35,'অতএব সেদিন সেখানে তার কোন সুহৃদ থাকবেনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5359,69,36,'এবং কোন খাদ্য থাকবেনা, ক্ষতনিঃসৃত স্রাব ব্যতীত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5360,69,37,'যা অপরাধী ব্যতীত কেহ খাবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5361,69,38,'আমি কসম করছি উহার যা তোমরা দেখতে পাও। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5362,69,39,'এবং যা তোমরা দেখতে পাওনা –');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5363,69,40,'নিশ্চয়ই এই কুরআন এক সম্মানিত রাসূলের বাহিত বার্তা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5364,69,41,'ইহা কোন কবির রচনা নয়; তোমরা অল্পই বিশ্বাস কর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5365,69,42,'ইহা কোন গণকের কথাও নয়, তোমরা অল্পই অনুধাবন কর। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5366,69,43,'ইহা জগতসমূহের রবের নিকট হতে অবতীর্ণ। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5367,69,44,'সে যদি আমার নামে কিছু রচনা করে চালাতে চেষ্টা করত – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5368,69,45,'আমি অবশ্যই তার ডান হাত ধরে ফেলতাম। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5369,69,46,'এবং কেটে দিতাম তার জীবন-ধমনী। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5370,69,47,'অতঃপর তোমাদের মধ্যে এমন কেহ নেই যে, তাকে রক্ষা করতে পারবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5371,69,48,'এই কুরআন মুত্তাকীদের জন্য অবশ্যই এক উপদেশ। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5372,69,49,'আমি অবশ্যই জানি যে, তোমাদের মধ্যে মিথ্যা আরোপকারী রয়েছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5373,69,50,'এবং এই কুরআন নিশ্চয়ই কাফিরদের অনুশোচনার কারণ হবে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5374,69,51,'অবশ্যই এটা নিশ্চিত সত্য।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5375,69,52,'অতএব তুমি মহান রবের নামের পবিত্রতা ও মহিমা ঘোষণা কর। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5376,70,1,'এক ব্যক্তি চাইল সংঘটিত হোক শাস্তি যা অবধারিত – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5377,70,2,'কাফিরদের জন্য, ইহা প্রতিরোধ করার কেহ নেই। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5378,70,3,'ইহা আসবে আল্লাহর নিকট হতে যিনি সমুচ্চ মর্যাদার অধিকারী। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5379,70,4,'মালাইকা/ফেরেশতা এবং রূহ আল্লাহর দিকে উর্ধ্বগামী হয় এমন একদিনে, যা পার্থিব পঞ্চাশ হাজার বৎসরের সমান।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5380,70,5,'সুতরাং তুমি ধৈর্য ধারণ কর, পরম ধৈর্য।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5381,70,6,'তারা ঐ দিনকে মনে করে সুদূর। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5382,70,7,'কিন্তু আমি দেখছি ইহা আসন্ন। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5383,70,8,'সেদিন আকাশ হবে গলিত ধাতুর মত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5384,70,9,'এবং পর্বতসমূহ হবে রঙ্গীন পশমের মত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5385,70,10,'এবং সুহৃদ সুহৃদের খোঁজ খবর নিবেনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5386,70,11,'তাদেরকে করা হবে একে অপরের দৃষ্টিগোচর। অপরাধী সেদিনের শাস্তির বদলে দিতে চাবে তার সন্তান-সন্ততিকে,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5387,70,12,'তার স্ত্রী ও ভাইকে,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5388,70,13,'তার জ্ঞাতি গোষ্ঠিকে যারা তাকে আশ্রয় দিত – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5389,70,14,'এবং পৃথিবীর সকলকে, যাতে এই মুক্তিপণ তাকে মুক্তি দেয়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5390,70,15,'না, কখনই নয়, ইহাতো লেলিহান অগ্নি –');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5391,70,16,'যা গাত্র হতে চামড়া খসিয়ে দিবে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5392,70,17,'জাহান্নাম সেই ব্যক্তিকে ডাকবে, যে সত্যের প্রতি পৃষ্ঠ প্রদর্শন করেছিল ও মুখ ফিরিয়ে নিয়েছিল – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5393,70,18,'যে সম্পদ পুঞ্জীভুত এবং সংরক্ষিত করে রাখছিল। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5394,70,19,'মানুষতো সৃষ্টি হয়েছে অতিশয় অস্থির চিত্ত রূপে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5395,70,20,'যখন বিপদ তাকে স্পর্শ করে সে হয় হা হুতাশকারী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5396,70,21,'আর যখন কল্যাণ তাকে স্পর্শ করে তখন সে হয় অতি কৃপণ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5397,70,22,'তবে সালাত আদায়কারী ব্যতীত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5398,70,23,'যারা তাদের সালাতে সদা নিষ্ঠাবান।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5399,70,24,'আর যাদের সম্পদের নির্ধারিত হক রয়েছে – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5400,70,25,'প্রার্থী ও বঞ্চিতের।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5401,70,26,'এবং যারা কর্মফল দিনকে সত্য বলে জানে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5402,70,27,'আর যারা তাদের রবের শাস্তি সম্পর্কে ভীত সন্ত্রস্ত,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5403,70,28,'নিশ্চয়ই তাদের রবের শাস্তি হতে নিঃশঙ্ক থাকা যায়না –');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5404,70,29,'এবং যারা নিজেদের যৌনাঙ্গকে সংযত রাখে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5405,70,30,'তাদের স্ত্রী অথবা অধিকারভুক্ত দাসীদের ক্ষেত্র ব্যতীত, এতে তারা নিন্দনীয় হবেনা – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5406,70,31,'তবে কেহ এদেরকে ছাড়া অন্যকে কামনা করলে তারা হবে সীমালংঘনকারী। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5407,70,32,'এবং যারা আমানাত ও প্রতিশ্রুতি রক্ষা করে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5408,70,33,'আর যারা তাদের সাক্ষ্য দানে অটল, ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5409,70,34,'এবং নিজেদের সালাতে যত্নবান – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5410,70,35,'তারাই সম্মানিত হবে জান্নাতে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5411,70,36,'কাফিরদের হল কি যে, ওরা তোমার দিকে ছুটে আসছে – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5412,70,37,'ডান ও বাম দিক হতে দলে দলে?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5413,70,38,'তাদের প্রত্যেকে কি এই প্রত্যাশা করে যে, তাকে দাখিল করা হবে প্রাচুর্যময় জান্নাতে?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5414,70,39,'না তা হবেনা, আমি তাদেরকে যা হতে সৃষ্টি করেছি তা তারা জানে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5415,70,40,'আমি শপথ করছি উদয়াচল ও অস্তাচলের অধিপতির! নিশ্চয়ই আমি সক্ষম – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5416,70,41,'তাদের অপেক্ষা উৎকৃষ্টতর মানবগোষ্ঠী তাদের স্থলবর্তী করতে; এবং আমি অক্ষম নই।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5417,70,42,'অতএব তাদেরকে বাক-বিতন্ডা ও ক্রীড়া কৌতুকে মত্ত থাকতে দাও, যে দিন সম্পর্কে তাদেরকে সতর্ক করা হয়েছিল তার সম্মুখীন হওয়ার পূর্ব পর্যন্ত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5418,70,43,'সেদিন তারা কাবর হতে বের হবে দ্রুত বেগে। মনে হবে তারা কোন একটি লক্ষ্যস্থলের দিকে ধাবিত হচ্ছে –');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5419,70,44,'অবনত নেত্রে; হীনতা তাদেরকে আচ্ছন্ন করবে; এটাই সেদিন, যে বিষয়ে সতর্ক করা হয়েছিল তাদেরকে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5420,71,1,'নূহকে আমি প্রেরণ করেছিলাম তার সম্প্রদায়ের প্রতি এই নির্দেশসহঃ তুমি তোমার সম্প্রদায়কে সতর্ক কর তাদের প্রতি শাস্তি আসার পূর্বে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5421,71,2,'সে বলেছিলঃ হে আমার সম্প্রদায়! আমিতো তোমাদের জন্য স্পষ্ট সতর্ককারী – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5422,71,3,'এ বিষয়ে যে, তোমরা আল্লাহর ইবাদাত কর, তাঁকে ভয় কর এবং আমার আনুগত্য কর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5423,71,4,'তিনি তোমাদের পাপ ক্ষমা করবেন এবং তিনি তোমাদেরকে অবকাশ দিবেন এক নির্দিষ্ট কাল পর্যন্ত; নিশ্চয়ই আল্লাহ কর্তৃক নির্ধারিত কাল উপস্থিত হলে উহা বিলম্বিত হয়না; যদি তোমরা এটা জানতে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5424,71,5,'সে বলেছিলঃ হে আমার রাব্ব! আমিতো আমার সম্প্রদায়কে দিন-রাত আহবান করছি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5425,71,6,'কিন্তু আমার আহবান তাদের পলায়ন প্রবণতাই বৃদ্ধি করছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5426,71,7,'আমি যখন তাদের আহবান করি যাতে তুমি তাদেরকে ক্ষমা কর, তারা কানে অংগুলি দেয়, নিজেদেরকে বস্ত্রাবৃত করে ও জিদ করতে থাকে এবং অতিশয় ঔদ্ধত্য প্রকাশ করে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5427,71,8,'অতঃপর আমি তাদের আহবান করেছি প্রকাশ্যে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5428,71,9,'পরে আমি সোচ্চার প্রচার করেছি ও উপদেশ দিয়েছি গোপনে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5429,71,10,'বলেছিঃ তোমাদের রবের কাছে ক্ষমা প্রার্থনা কর, তিনিতো মহা ক্ষমাশীল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5430,71,11,'তিনি তোমাদের জন্য প্রচুর বৃষ্টিপাত ঘটাবেন। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5431,71,12,'তিনি তোমাদের সমৃদ্ধ করবেন ধন-সম্পদ ও সন্তান-সন্ততিতে এবং তোমাদের জন্য স্থাপন করবেন উদ্যান ও প্রবাহিত করবেন নদী-নালা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5432,71,13,'তোমাদের কি হয়েছে যে, তোমরা আল্লাহর শ্রেষ্ঠত্ব স্বীকার করতে চাচ্ছনা? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5433,71,14,'অথচ তিনিই তোমাদেরকে সৃষ্টি করেছেন পর্যায়ক্রমে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5434,71,15,'তোমরা কি লক্ষ্য করনা আল্লাহ কিভাবে সৃষ্টি করেছেন সপ্ত আকাশ স্তরে স্তরে? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5435,71,16,'এবং সেখানে চাঁদকে স্থাপন করেছেন আলোক রূপে ও সূর্যকে স্থাপন করেছেন প্রদীপ রূপে;');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5436,71,17,'তিনি তোমাদেরকে সৃষ্টি করেছেন মৃত্তিকা হতে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5437,71,18,'অতঃপর তাতে তিনি তোমাদের প্রত্যাবৃত্ত করবেন ও পরে পুনরুত্থিত করবেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5438,71,19,'এবং আল্লাহ তোমাদের জন্য ভূমিকে করেছেন বিস্তৃত – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5439,71,20,'যাতে তোমরা চলাফিরা করতে পার প্রশস্ত পথে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5440,71,21,'নূহ বলেছিলঃ হে আমার রাব্ব! আমার সম্প্রদায়তো আমাকে অমান্য করছে এবং অনুসরণ করছে এমন লোকের যার ধন সম্পদ ও সন্তান-সন্ততি তার ক্ষতি ব্যতীত আর কিছুই বৃদ্ধি করেনি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5441,71,22,'তারা ভয়ানক ষড়যন্ত্র করেছিল। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5442,71,23,'এবং বলেছিলঃ তোমরা কখনও পরিত্যাগ করনা তোমাদের দেব-দেবীকে; পরিত্যাগ করনা ওয়াদ, সুওয়া, আগুছ, আউক ও নাসরকে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5443,71,24,'তারা অনেককে বিভ্রান্ত করছে; সুতরাং যালিমদের বিভ্রান্তি ব্যতীত আর কিছুই বৃদ্ধি করেনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5444,71,25,'তাদের অপরাধের জন্য তাদেরকে নিমজ্জিত করা হয়েছিল এবং পরে তাদেরকে দাখিল করা হয়েছিল আগুনে, অতঃপর তারা কেহকেও আল্লাহর মুকাবিলায় পায়নি সাহায্যকারী। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5445,71,26,'নূহ আরও বলেছিলঃ হে আমার রাব্ব! পৃথিবীতে কাফিরদের মধ্য হতে কোন গৃহবাসীকে অব্যাহতি দিওনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5446,71,27,'তুমি তাদেরকে অব্যাহতি দিলে তারা তোমার বান্দাদেরকে বিভ্রান্ত করবে এবং জন্ম দিতে থাকবে কেবল দুস্কৃতিকারী ও কাফির।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5447,71,28,'হে আমার রাব্ব! তুমি ক্ষমা কর আমাকে, আমার মাতা-পিতাকে এবং যারা মু’মিন হয়ে আমার গৃহে প্রবেশ করে তাদেরকে এবং মু’মিন পুরুষ ও মু’মিনা নারীদেরকে; আর যালিমদের শুধু ধ্বংসই বৃদ্ধি কর। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5448,72,1,'বলঃ আমার প্রতি অহী প্রেরিত হয়েছে যে, জিনদের একটি দল মনোযোগ সহকারে শ্রবণ করেছে এবং বলেছে, আমরাতো এক বিস্ময়কর কুরআন শ্রবণ করেছি –');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5449,72,2,'যা সঠিক পথ নির্দেশ করে; ফলে আমরা এতে বিশ্বাস স্থাপন করেছি। আমরা কখনও আমাদের রবের সাথে কোন শরীক স্থির করবনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5450,72,3,'এবং নিশ্চয়ই সমুচ্চ আমাদের রবের মর্যাদা; তিনি গ্রহণ করেননি কোন পত্নী এবং না কোন সন্তান। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5451,72,4,'এবং আমাদের মধ্যকার নির্বোধরা আল্লাহ সম্বন্ধে অতি অবাস্তব উক্তি করত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5452,72,5,'অথচ আমরা মনে করতাম মানুষ এবং জিন, আল্লাহ সম্বন্ধে কখনও মিথ্যা আরোপ করবেনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5453,72,6,'আর যে কতিপয় মানুষ কতক জিনের স্মরণ নিত, ফলে তারা জিনদের আত্মম্ভরিতা বাড়িয়ে দিত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5454,72,7,'আর জিনরা বলেছিলঃ তোমাদের মত মানুষও মনে করে যে, মৃত্যুর পর আল্লাহ কেহকেও পুনরুত্থিত করবেননা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5455,72,8,'এবং আমরা চেয়েছিলাম আকাশের তথ্য সংগ্রহ করতে। কিন্তু আমরা দেখতে পেলাম কঠোর প্রহরী ও উল্কাপিন্ড দ্বারা আকাশ পরিপূর্ণ। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5456,72,9,'আর পূর্বে আমরা আকাশের বিভিন্ন ঘাঁটিতে সংবাদ শোনার জন্য বসতাম। কিন্তু এখন কেহ সংবাদ শুনতে চাইলে সে তার উপর নিক্ষেপের জন্য প্রস্তুত জ্বলন্ত উল্কাপিন্ডের সম্মুখীন হয়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5457,72,10,'আমরা জানিনা, জগতবাসীর অমংগলই অভিপ্রেত, না কি তাদের রাব্ব তাদের মংগল করার ইচ্ছা রাখেন। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5458,72,11,'এবং আমাদের কতক সৎ কর্মপরায়ণ এবং কতক এর ব্যতিক্রম, আমরা ছিলাম বিভিন্ন পথের অনুসারী; ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5459,72,12,'এখন আমরা বুঝেছি যে, আমরা পৃথিবীতে আল্লাহকে পরাভূত করতে পারবনা এবং পলায়ন করেও তাঁকে ব্যর্থ করতে পারবনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5460,72,13,'আমরা যখন পথ নির্দেশক বাণী শুনলাম তখন তাতে বিশ্বাস স্থাপন করলাম। যে ব্যক্তি তার রবের প্রতি ঈমান আনে তার কোন ক্ষতি কিংবা জোর জবরদস্তির আশংকা থাকবেনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5461,72,14,'আমাদের কতক আত্মসমপর্ণকারী এবং কতক সীমা লংঘনকারী; যারা আত্মসমর্পণ করে তারা সুচিন্তিতভাবে সত্য পথ বেছে নেয়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5462,72,15,'অপর পক্ষে সীমা লংঘনকারীতো জাহান্নামেরই ইন্ধন। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5463,72,16,'তারা যদি সত্য পথে প্রতিষ্ঠিত থাকত, তাদেরকে আমি প্রচুর বারি বর্ষণের মাধ্যমে সমৃদ্ধ করতাম – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5464,72,17,'যদ্বারা আমি তাদেরকে পরীক্ষা করতাম। যে ব্যক্তি তার রবের স্মরণ হতে বিমুখ হয় তিনি তাকে দুঃসহ শাস্তিতে প্রবেশ করাবেন। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5465,72,18,'এবং এই যে মাসজিদসমূহ, আল্লাহরই জন্য। সুতরাং আল্লাহর সাথে তোমরা অন্য কেহকেও ডেকনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5466,72,19,'আর এই যে, যখন আল্লাহর বান্দা তাঁকে ডাকার জন্য দন্ডায়মান হল তখন তারা তার নিকট ভিড় জমালো।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5467,72,20,'বলঃ আমি আমার রাব্বকে ডাকি এবং তাঁর সাথে কেহকে শরীক করিনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5468,72,21,'বলঃ আমি তোমাদের মঙ্গল-অমঙ্গলের মালিক নই। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5469,72,22,'বলঃ আল্লাহর শাস্তি হতে কেহ আমাকে রক্ষা করতে পারবেনা এবং আল্লাহ ব্যতীত আমি কোন আশ্রয় পাবনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5470,72,23,'কেবল আল্লাহর বাণী পৌঁছানো এবং তা প্রচার করাই আমার কাজ। যারা আল্লাহ ও তাঁর রাসূলকে অমান্য করে তাদের জন্য রয়েছে জাহান্নামের আগুন, সেখানে তারা চিরস্থায়ী হবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5471,72,24,'যখন তারা প্রতিশ্রুতি প্রত্যক্ষ করবে তখন তারা বুঝতে পারবে, কে সাহায্যকারীর দিক দিয়ে দুর্বল এবং কে সংখ্যায় স্বল্প। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5472,72,25,'বলঃ আমি জানিনা তোমাদেরকে যে শাস্তির প্রতিশ্রুতি দেয়া হয়েছে তা কি আসন্ন, না কি আমার রাব্ব এর জন্য কোন দীর্ঘ মেয়াদ স্থির করবেন? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5473,72,26,'তিনি অদৃশ্যের পরিজ্ঞাতা, তিনি তাঁর অদৃশ্যের জ্ঞান কারও নিকট প্রকাশ করেননা – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5474,72,27,'তাঁর মনোনীত রাসূল ব্যতীত। সেক্ষেত্রে আল্লাহ রাসূলের অগ্রে এবং পশ্চাতে প্রহরী নিয়োজিত করেন,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5475,72,28,'রাসূলগণ তাদের রবের বাণী পৌঁছে দিয়েছেন কি না জানার জন্য। রাসূলগণের নিকট যা আছে তা তাঁর জ্ঞান গোচর এবং তিনি সমস্ত কিছুর বিস্তারিত হিসাব রাখেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5476,73,1,'হে বস্ত্রাবৃত! ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5477,73,2,'রাত জাগরণ কর কিছু অংশ ব্যতীত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5478,73,3,'অর্ধ রাত কিংবা তদপেক্ষা কিছু কম। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5479,73,4,'অথবা তদপেক্ষা বেশী। আর কুরআন আবৃত্তি কর ধীরে ধীরে স্পষ্ট ও সুন্দরভাবে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5480,73,5,'আমি তোমার প্রতি অবতীর্ণ করেছি গুরুত্বপূর্ণ বাণী। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5481,73,6,'নিশ্চয়ই রাতে জাগরণ ইবাদাতের জন্য গভীর মনোনিবেশ, হৃদয়ঙ্গম এবং স্পষ্ট উচ্চারণে অনুকূল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5482,73,7,'দিনে তোমার জন্য রয়েছে দীর্ঘ কর্মব্যস্ততা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5483,73,8,'সুতরাং তুমি তোমার রবের নাম স্মরণ কর এবং একনিষ্ঠভাবে তাতে মগ্ন হও। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5484,73,9,'তিনি পূর্ব ও পশ্চিমের রাব্ব, তিনি ব্যতীত কোন ইলাহ নেই; অতএব তাঁকেই কর্ম-বিধায়ক রূপে গ্রহণ কর। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5485,73,10,'লোকে যা বলে, তাতে তুমি ধৈর্য ধারণ কর এবং সৌজন্য সহকারে উহাদেরকে পরিহার করে চল। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5486,73,11,'ছেড়ে দাও আমাকে এবং বিলাস সামগ্রীর অধিকারী সত্য প্রত্যাখ্যানকারীদেরকে; আর কিছু কালের জন্য তাদেরকে অবকাশ দাও। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5487,73,12,'আমার নিকট আছে শৃংখল প্রজ্জ্বলিত আগুন। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5488,73,13,'আর আছে এমন খাদ্য যা গলায় আটকে যায় এবং মর্মন্তদ শাস্তি। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5489,73,14,'সেই দিনে পৃথিবী ও পর্বতমালা প্রকম্পিত হবে এবং পর্বতসমূহ বহমান বালুকারাশিতে পরিণত হবে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5490,73,15,'আমি তোমাদের নিকট পাঠিয়েছি রাসূল তোমাদের জন্য স্বাক্ষী স্বরূপ, যেমন রাসূল পাঠিয়েছিলাম ফিরআউনের নিকট।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5491,73,16,'কিন্তু ফির‘আউন সেই রাসূলকে অমান্য করেছিল, ফলে আমি তাকে কঠিন শাস্তি দিয়েছিলাম। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5492,73,17,'অতএব যদি তোমরা কুফরী কর, কি করে আত্মরক্ষা করবে সেদিন যেদিন শিশুদেরকে পরিণত করবে বৃদ্ধে?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5493,73,18,'যেদিন আকাশ হবে বিদীর্ণ; তাঁর প্রতিশ্রুতি অবশ্যই বাস্তবায়িত হবে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5494,73,19,'ইহা এক উপদেশ, অতএব যার অভিরুচি সে তার রবের পথ অবলম্বন করুক!');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5495,73,20,'তোমার রাব্বতো জানেন যে, তুমি জাগরণ কর কখনও রাতের প্রায় দুই তৃতীয়াংশ, অর্ধাংশ ও এক-তৃতীয়াংশ এবং জাগে তোমার সংগে যারা আছে তাদের একটি দলও এবং আল্লাহই নির্ধারণ করেন দিন ও রাতের পরিমাণ। তিনি জানেন যে, তোমরা এর সঠিক হিসাব রাখতে পারনা, অতএব আল্লাহ তোমাদের প্রতি ক্ষমা পরবশ হয়েছেন। অতএব কুরআনের যতটুকু আবৃত্তি করা তোমাদের জন্য সহজ ততটুকু আবৃত্তি কর; আল্লাহ জানেন যে, তোমাদের মধ্যে কেহ কেহ অসুস্থ হয়ে পড়বে, কেহ কেহ আল্লাহর অনুগ্রহ সন্ধানে দেশ ভ্রমণ করবে এবং কেহ কেহ আল্লাহর পথে সংগ্রামে লিপ্ত হবে। অতএব কুরআন হতে যতটুকু সহজসাধ্য আবৃত্তি কর। সালাত কায়েম কর, যাকাত প্রদান কর এবং আল্লাহকে দাও উত্তম ঋণ। তোমরা তোমাদের আত্মার মঙ্গলের জন্য ভাল যা কিছু অগ্রিম প্রেরণ করবে তোমরা তা পাবে আল্লাহর নিকট। উহা উৎকৃষ্টতর এবং পুরস্কার হিসাবে মহত্তর। আর তোমরা ক্ষমা প্রার্থনা কর আল্লাহর নিকট। নিশ্চয়ই আল্লাহ ক্ষমাশীল, পরম দয়ালু।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5496,74,1,'হে বস্ত্রাচ্ছাদিত!');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5497,74,2,'উঠ, সতর্ক বাণী প্রচার কর। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5498,74,3,'এবং তোমার রবের শ্রেষ্ঠত্ব ঘোষণা কর। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5499,74,4,'তোমার পরিচ্ছদ পরিস্কার রাখ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5500,74,5,'অপবিত্রতা হতে দূরে থাক। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5501,74,6,'অধিক পাওয়ার প্রত্যাশায় দান করনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5502,74,7,'এবং তোমার রবের উদ্দেশ্যে ধৈর্য ধর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5503,74,8,'যেদিন শিঙ্গায় ফুৎকার দেয়া হবে – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5504,74,9,'সেদিন হবে এক সংকটের দিন –');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5505,74,10,'যা কাফিরদের জন্য সহজ নয়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5506,74,11,'আমাকে ছেড়ে দাও এবং তাকে, যাকে আমি সৃষ্টি করেছি অসাধারণ করে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5507,74,12,'আমি তাকে দিয়েছি বিপুল ধন সম্পদ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5508,74,13,'এবং নিত্য সঙ্গী পুত্রগণ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5509,74,14,'এবং তাকে দিয়েছি স্বচ্ছন্দময় জীবনের প্রচুর উপকরণ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5510,74,15,'এর পরও সে কামনা করে যে, আমি তাকে আরও অধিক দিই।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5511,74,16,'না, তা হবেনা, সেতো আমার নিদর্শনসমূহের ঔদ্ধত বিরুদ্ধাচারী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5512,74,17,'আমি অচিরেই তাকে ক্রমবর্ধমান শাস্তি দ্বারা আচ্ছন্ন করব। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5513,74,18,'সে চিন্তা করল এবং সিদ্ধান্ত গ্রহণ করল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5514,74,19,'অভিশপ্ত হোক সে! কেমন করে সে এ সিদ্ধান্ত গ্রহণ করল! ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5515,74,20,'আরও অভিশপ্ত হোক সে! কেমন করে সে এই সিদ্ধান্তে উপনীত হল! ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5516,74,21,'সে আবার চেয়ে দেখল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5517,74,22,'অতঃপর সে ভ্রু কুঞ্চিত করল ও মুখ বিকৃত করল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5518,74,23,'অতঃপর সে পৃষ্ঠ প্রদর্শন করল এবং অহংকার করল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5519,74,24,'এবং ঘোষণা করল, এতো লোক পরম্পরায় প্রাপ্ত যাদু ভিন্ন আর কিছু নয়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5520,74,25,'এটাতো মানুষেরই কথা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5521,74,26,'আমি তাকে নিক্ষেপ করব সাকার-এ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5522,74,27,'তুমি কি জান সাকার কি? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5523,74,28,'উহা তাদের জীবিতাবস্থায় রাখবেনা এবং মৃত অবস্থায়ও ছেড়ে দিবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5524,74,29,'উহাতো গাত্রচর্ম দগ্ধ করবে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5525,74,30,'উহার তত্ত্বাবধানে রয়েছে উনিশ জন প্রহরী। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5526,74,31,'আমি তাদেরকে করেছি জাহান্নামের প্রহরী। কাফিরদের পরীক্ষা স্বরূপ। আমি তাদের এই সংখ্যা উল্লেখ করেছি যাতে কিতাবীদের দৃঢ় প্রত্যয় জন্মে, বিশ্বাসীদের বিশ্বাস বর্ধিত হয় এবং বিশ্বাসীরা ও কিতাবীরা সন্দেহ পোষণ না করে। এর ফলে যাদের অন্তরে ব্যাধি আছে তারা ও কাফিরেরা বলবেঃ আল্লাহ এই অভিনব উক্তি দ্বারা কি বুঝাতে চেয়েছেন? এভাবে আল্লাহ যাকে ইচ্ছা পথভ্রষ্ট করেন এবং যাকে ইচ্ছা পথ নির্দেশ করেন। তোমার রবের বাহিনী সম্পর্কে একমাত্র তিনিই জানেন। এটাতো মানুষের জন্য সাবধান বাণী। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5527,74,32,'কক্ষনো না, চন্দ্রের শপথ!');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5528,74,33,'শপথ রাতের, যখন তার অবসান ঘটে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5529,74,34,'শপথ প্রভাতকালের, যখন উহা হয় আলোকোজ্জ্বল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5530,74,35,'নিশ্চয়ই জাহান্নাম ভয়াবহ বিপদসমূহের অন্যতম।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5531,74,36,'মানুষের জন্য সতর্ককারী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5532,74,37,'তোমাদের মধ্যে যে অগ্রসর হতে চায় কিংবা যে পিছিয়ে পড়ে, তার জন্য। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5533,74,38,'প্রত্যেক ব্যক্তি নিজ কৃতকর্মের দায়ে আবদ্ধ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5534,74,39,'তবে ডান পার্শ্বস্থ ব্যক্তিগণের নয়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5535,74,40,'তারা থাকবে উদ্যানে এবং তারা পরস্পরে জিজ্ঞাসাবাদ করবে –');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5536,74,41,'অপরাধীদের সম্পর্কে, ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5537,74,42,'তোমাদেরকে কিসে জাহান্নামে নিক্ষেপ করেছে? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5538,74,43,'তারা বলবেঃ আমরা সালাত আদায় করতামনা – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5539,74,44,'আমরা অভাবগ্রস্তকে আহার্য দান করতামনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5540,74,45,'এবং আমরা আলোচনাকারীদের সাথে আলোচনায় নিমগ্ন থাকতাম –');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5541,74,46,'আমরা কর্মফল দিন অস্বীকার করতাম – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5542,74,47,'আমাদের নিকট মৃত্যুর আগমন পর্যন্ত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5543,74,48,'ফলে সুপারিশকারীদের সুপারিশ তাদের কোন কাজে আসবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5544,74,49,'তাদের কি হয়েছে যে, তারা মুখ ফিরিয়ে নেয় উপদেশ হতে?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5545,74,50,'তারা যেন ভীত- সন্ত্রস্থ গর্দভ – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5546,74,51,'যা সিংহের সম্মুখ হতে পলায়নপর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5547,74,52,'বস্তুতঃ তাদের প্রত্যেকেই কামনা করে যে, তাকে একটি উম্মুক্ত গ্রন্থ দেয়া হোক।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5548,74,53,'না ইহা হবার নয়, বরং তারাতো আখিরাতের ভয় পোষণ করেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5549,74,54,'কখনও না, এটাতো উপদেশ মাত্র।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5550,74,55,'অতএব যার ইচ্ছা সে ইহা হতে উপদেশ গ্রহণ করুক।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5551,74,56,'আল্লাহর ইচ্ছা ছাড়া কেহ উপদেশ গ্রহণ করবেনা, একমাত্র তিনিই ভয়ের যোগ্য এবং তিনিই ক্ষমা করার অধিকারী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5552,75,1,'আমি শপথ করছি কিয়ামাত দিবসের।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5553,75,2,'আরও শপথ করছি তিরস্কারকারী আত্মার।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5554,75,3,'মানুষ কি মনে করে যে, আমি তার অস্থিসমূহ একত্র করতে পারবনা?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5555,75,4,'বস্তুতঃ আমি তার অঙ্গুলীর অগ্রভাগ পর্যন্ত পুনঃ বিন্যস্ত করতে সক্ষম।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5556,75,5,'তবুও মানুষ তার সম্মুখে যা আছে তা অস্বীকার করতে চায়;');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5557,75,6,'সে প্রশ্ন করেঃ কখন কিয়ামাত দিবস আসবে?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5558,75,7,'যখন চক্ষু স্থির হয়ে যাবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5559,75,8,'এবং চক্ষু হয়ে পড়বে জ্যোতিহীন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5560,75,9,'যখন সূর্য ও চাঁদকে একত্র করা হবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5561,75,10,'সেদিন মানুষ বলবেঃ আজ পালানোর স্থান কোথায়?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5562,75,11,'না, কোন আশ্রয়স্থল নেই।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5563,75,12,'সেদিন ঠাঁই হবে তোমার রবের নিকট। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5564,75,13,'সেদিন মানুষকে অবহিত করা হবে সে কি অগ্রে পাঠিয়েছে এবং কি পশ্চাতে রেখে গেছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5565,75,14,'বস্তুতঃ মানুষ নিজের সম্বন্ধে সম্যক অবগত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5566,75,15,'যদিও সে নানা অজুহাতের অবতারণা করে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5567,75,16,'তাড়াতাড়ি অহী আয়ত্ত্ব করার জন্য তুমি তোমার জিহবা দ্রুততার সাথে সঞ্চালন করনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5568,75,17,'ইহা সংরক্ষণ ও পাঠ করানোর দায়িত্ব আমারই।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5569,75,18,'সুতরাং যখন আমি উহা পাঠ করাই তুমি সেই পাঠের অনুসরণ কর। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5570,75,19,'অতঃপর এর বিশদ ব্যাখ্যার দায়িত্ব আমারই।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5571,75,20,'না, তোমরা প্রকৃত পক্ষে পার্থিব জীবনকে ভালবাস।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5572,75,21,'এবং আখিরাতকে উপেক্ষা কর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5573,75,22,'সেদিন কোন কোন মুখমন্ডল উজ্জ্বল হবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5574,75,23,'তারা তাদের রবের দিকে তাকিয়ে থাকবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5575,75,24,'কোন কোন মুখমন্ডল হয়ে পড়বে বিবর্ণ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5576,75,25,'এবং এই আশংকায় যে, এক ধ্বংসকারী বিপর্যয় আসন্ন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5577,75,26,'যখন প্রাণ কন্ঠাগত হবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5578,75,27,'এবং বলা হবেঃ কে তাকে রক্ষা করবে? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5579,75,28,'তখন তার প্রত্যয় হবে যে, উহা বিদায়ক্ষণ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5580,75,29,'এবং পায়ের সংগে পা জড়িয়ে যাবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5581,75,30,'সেদিন তোমার রবের নিকট সমস্ত কিছু প্রত্যানীত হবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5582,75,31,'সে বিশ্বাস করেনি এবং সালাত আদায় করেনি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5583,75,32,'বরং সে প্রত্যাখ্যান করেছিল ও মুখ ফিরিয়ে নিয়েছিল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5584,75,33,'অতঃপর সে তার পরিবার পরিজনের নিকট ফিরে গিয়েছিল দম্ভভরে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5585,75,34,'তোমার দুর্ভোগের উপর দুর্ভোগ!');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5586,75,35,'আবার তোমার দুর্ভোগের উপর দুর্ভোগ!');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5587,75,36,'মানুষ কি মনে করে যে, তাকে নিরর্থক ছেড়ে দেয়া হবে?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5588,75,37,'সে কি স্খলিত শুক্রবিন্দু ছিলনা? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5589,75,38,'অতঃপর সে রক্তপিন্ডে পরিণত হয়। তারপর আল্লাহ তাকে আকৃতি দান করেন ও সুঠাম করেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5590,75,39,'অতঃপর তিনি তা হতে সৃষ্টি করেন যুগল নর ও নারী। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5591,75,40,'তবুও কি সেই স্রষ্টা মৃতকে পুনর্জীবিত করতে সক্ষম নন?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5592,76,1,'কাল-প্রবাহে মানুষের উপর এক সময় অতিবাহিত হয়েছে যখন সে উল্লেখযোগ্য কিছু ছিলনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5593,76,2,'আমি তো মানুষকে সৃষ্টি করেছি মিলিত শুক্রবিন্দু হতে, তাকে পরীক্ষা করার জন্য; এ জন্য আমি তাকে করেছি শ্রবণ ও দৃষ্টিশক্তিসম্পন্ন। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5594,76,3,'আমি তাকে পথের নির্দেশ দিয়েছি; হয় সে কৃতজ্ঞ হবে, না হয় সে অকৃতজ্ঞ হবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5595,76,4,'আমি অকৃতজ্ঞদের জন্য প্রস্তুত রেখেছি শৃংখল, বেড়ি ও লেলিহান অগ্নি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5596,76,5,'সৎ কর্মশীলরা পান করবে মিশ্রিত পানীয় ‘কাফুর’ –');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5597,76,6,'এমন একটি প্রস্রবণের যা হতে আল্লাহর বান্দাগণ পান করবে, তারা এই প্রস্রবণকে যেখানে ইচ্ছা প্রবাহিত করতে পারবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5598,76,7,'তারা কর্তব্য পালন করে এবং সেদিনের ভয় করে, যেদিন বিপত্তি হবে ব্যাপক।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5599,76,8,'তাদের চাহিদা থাকা সত্ত্বেও আল্লাহর সন্তুষ্টির জন্য মিসকীন, ইয়াতীম ও বন্দীকে আহার্য দান করে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5600,76,9,'এবং বলেঃ কেবল আল্লাহর সন্তুষ্টি লাভের উদ্দেশ্যে আমরা তোমাদেরকে আহার্য দান করি, আমরা তোমাদের নিকট হতে প্রতিদান চাইনা, কৃতজ্ঞতাও নয়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5601,76,10,'আমরা আশংকা করি আমাদের রবের নিকট হতে এক ভীতিপ্রদ ভয়ংকর দিনের।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5602,76,11,'পরিণামে আল্লাহ তাদেরকে রক্ষা করবেন সেই দিনের অনিষ্টতা হতে এবং তাদেরকে দিবেন উৎফুল্লতা ও আনন্দ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5603,76,12,'আর তাদের ধৈর্যশীলতার পুরস্কার স্বরূপ তাদেরকে দেয়া হবে জান্নাত ও রেশমী বস্ত্র। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5604,76,13,'সেখানে তারা সমাসীন হবে সুসজ্জিত আসনে, তারা সেখানে অতিশয় গরম ও অতিশয় শীত অনুভব করবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5605,76,14,'উহার বৃক্ষছায়া তাদের উপর ঝুকে থাকবে এবং এর ফলমূল সম্পূর্ণ রূপে তাদের নাগালের মধ্যে থাকবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5606,76,15,'তাদেরকে পরিবেশন করা হয়ে রৌপ্য পাত্রে এবং স্ফটিকের মত স্বচ্ছ পান পাত্রে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5607,76,16,'রূপালী স্ফটিক পাত্রে পরিবেশনকারীরা যথাযথ পরিমাণে উহা পূর্ণ করবে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5608,76,17,'সেখানে তাদের পান করতে দেয়া হবে যানযাবীল মিশ্রিত পানীয় – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5609,76,18,'জান্নাতের এমন এক প্রসবণের যার নাম সালসাবীল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5610,76,19,'তাদেরকে পরিবেশন করবে চির কিশোরগণ, তাদেরকে দেখে মনে হবে তারা যেন বিক্ষিপ্ত মুক্তা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5611,76,20,'তুমি যখন সেখানে দেখবে, দেখতে পাবে ভোগ বিলাসের উপকরণ এবং বিশাল রাজ্য।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5612,76,21,'তাদের আবরণ হবে সূক্ষ্ণ সবুজ রেশম ও স্থুল রেশম; তারা অলংকৃত হবে রৌপ্য নির্মিত কংকনে, আর তাদের রাব্ব তাদেরকে পান করাবেন বিশুদ্ধ পানীয়। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5613,76,22,'অবশ্যই এটাই তোমাদের পুরস্কার এবং তোমাদের কর্মপ্রচেষ্টা স্বীকৃতি প্রাপ্ত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5614,76,23,'আমি তোমার প্রতি কুরআন অবতীর্ণ করেছি পর্যায়ক্রমে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5615,76,24,'সুতরাং ধৈর্যের সাথে তোমার রবের নির্দেশের প্রতীক্ষা কর এবং তাদের মধ্য কোনো পাপীষ্ঠ অথবা কাফিরের আনুগত্য করনা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5616,76,25,'এবং তোমার রবের নাম স্মরণ কর সকাল সন্ধ্যায়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5617,76,26,'রাতের কিয়দংশ তাঁর প্রতি সাজদায় নত হও এবং রাতের দীর্ঘ সময় তাঁর পবিত্রতা ও মহিমা ঘোষণা কর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5618,76,27,'তারা ভালবাসে পার্থিব জীবনকে এবং পরবর্তী কঠিন দিনকে তারা উপেক্ষা করে চলে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5619,76,28,'আমি তাদের সৃষ্টি করেছি এবং তাদের গঠন সুদৃঢ় করেছি। আমি যখন ইচ্ছা করব তখন তাদের পরিবর্তে তাদের অনুরূপ এক জাতিকে প্রতিষ্ঠিত করব। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5620,76,29,'ইহা এক উপদেশ, অতএব যার ইচ্ছা সে তার রবের পথ অবলম্বন করুক।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5621,76,30,'তোমরা ইচ্ছা করবেনা যদি না আল্লাহ ইচ্ছা করেন। আল্লাহ সর্বজ্ঞ, প্রজ্ঞাময়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5622,76,31,'তিনি যাকে ইচ্ছা তাঁর অনুগ্রহের অন্তর্ভুক্ত করেন, কিন্তু যালিমদের জন্যতো তিনি প্রস্তুত রেখেছেন মর্মন্তদ শাস্তি। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5623,77,1,'শপথ কল্যাণ স্বরূপ প্রেরিত বায়ুর; ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5624,77,2,'আর প্রলয়ঙ্করী ঝটিকার; ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5625,77,3,'শপথ সঞ্চালনকারী বায়ুর;');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5626,77,4,'আর মেঘপুঞ্জ বিচ্ছিন্নকারী বায়ুর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5627,77,5,'এবং তার, যে মানুষের অন্তরে পৌঁছে দেয় উপদেশ – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5628,77,6,'অনুশোচনা স্বরূপ অথবা সতর্কতা স্বরূপ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5629,77,7,'নিশ্চয়ই তোমাদেরকে যে প্রতিশ্রুতি দেয়া হয়েছে তা অবশ্যম্ভাবী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5630,77,8,'যখন নক্ষত্ররাজির আলো নির্বাপিত হবে,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5631,77,9,'যখন আকাশ বিদীর্ণ হবে,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5632,77,10,'এবং যখন পর্বতমালা উম্মুলিত ও বিক্ষিপ্ত হবে,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5633,77,11,'এবং রাসূলগণের নিরূপিত সময় উপস্থিত হবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5634,77,12,'এই সমূদয় স্থগিত রাখা হয়েছে কোন দিনের জন্য? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5635,77,13,'বিচার দিনের জন্য।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5636,77,14,'বিচার দিন সম্বন্ধে তুমি জান কি?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5637,77,15,'সেদিন দুর্ভোগ মিথ্যা আরোপকারীদের জন্য।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5638,77,16,'আমি কি পূর্ববর্তীদেরকে ধ্বংস করিনি?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5639,77,17,'অতঃপর আমি পরবর্তীদেরকে তাদের অনুগামী করব।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5640,77,18,'অপরাধীদের প্রতি আমি এরূপই করে থাকি। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5641,77,19,'সেদিন দুর্ভোগ মিথ্যা আরোপকারীদের জন্য।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5642,77,20,'আমি কি তোমাদেরকে তুচ্ছ পানি হতে সৃষ্টি করিনি?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5643,77,21,'অতঃপর আমি তা স্থাপন করেছি নিরাপদ আধারে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5644,77,22,'এক নির্দিষ্ট কাল পর্যন্ত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5645,77,23,'আমি এটাকে গঠন করেছি পরিমিতভাবে, আমি কত নিপুণ স্রষ্টা। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5646,77,24,'সেদিন দুর্ভোগ মিথ্যা আরোপকারীদের জন্য।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5647,77,25,'আমি কি ভূমিকে সৃষ্টি করিনি ধারণকারী রূপে – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5648,77,26,'জীবিত ও মৃতের জন্য?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5649,77,27,'আমি তাতে স্থাপন করেছি সুদৃঢ় উচ্চ পর্বতমালা এবং তোমাদেরকে দিয়েছি সুপেয় পানি। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5650,77,28,'সেদিন দুর্ভোগ মিথ্যা আরোপকারীদের জন্য।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5651,77,29,'তোমরা যাকে অস্বীকার করতে, চল তারই দিকে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5652,77,30,'চল তিন কুন্ডল বিশিষ্ট ছায়ার দিকে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5653,77,31,'যে ছায়া শীতল নয় এবং যা রক্ষা করেনা অগ্নি শিখা হতে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5654,77,32,'ইহা উৎক্ষেপ করবে অট্টালিকা তুল্য বৃহৎ স্ফুলিংগ। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5655,77,33,'উহা পীতবর্ণ উষ্ট্রশ্রেণী সদৃশ। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5656,77,34,'সেদিন দুর্ভোগ মিথ্যা আরোপকারীদের জন্য।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5657,77,35,'ইহা এমন একদিন যেদিন কারও বাকস্ফুর্তি হবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5658,77,36,'এবং তাদেরকে ওযর পেশ করার অনুমতি দেয়া হবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5659,77,37,'সেদিন দুর্ভোগ মিথ্যা আরোপকারীদের জন্য।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5660,77,38,'ইহাই ফাইসালার দিন, আমি একত্রিত করব তোমাদেরকে এবং পূর্ববর্তীদেরকে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5661,77,39,'তোমাদের কোন অপকৌশল থাকলে তা প্রয়োগ কর আমার বিরুদ্ধে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5662,77,40,'সেদিন দুর্ভোগ মিথ্যা আরোপকারীদের জন্য।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5663,77,41,'মুত্তাকীরা থাকবে ছায়ায় ও প্রস্রবণ বহুল স্থানে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5664,77,42,'তাদের বাঞ্ছিত ফলমূলের প্রাচুর্যের মধ্যে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5665,77,43,'তোমাদের কর্মের পুরস্কার স্বরূপ তোমরা তৃপ্তির সাথে পানাহার কর। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5666,77,44,'এভাবে আমি সৎ কর্মপরায়ণদেরকে পুরস্কৃত করে থাকি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5667,77,45,'সেদিন দুর্ভোগ মিথ্যা আরোপকারীদের জন্য। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5668,77,46,'তোমরা পানাহার কর এবং ভোগ করে লও অল্প কিছুদিন, তোমরাতো অপরাধী। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5669,77,47,'সেদিন দুর্ভোগ মিথ্যা আরোপকারীদের জন্য।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5670,77,48,'যখন তাদেরকে বলা হয়ঃ ‘আল্লাহর প্রতি নত হও’, তারা নত হয়না।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5671,77,49,'সেদিন দুর্ভোগ অস্বীকারকারীদের জন্য।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5672,77,50,'সুতরাং তারা কোন্ কথায় এরপর বিশ্বাস স্থাপন করবে?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5673,78,1,'তারা পরস্পর কোন্ বিষয়ে জিজ্ঞাসাবাদ করছে?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5674,78,2,'সেই মহান সংবাদ সম্বন্ধে –');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5675,78,3,'যে বিষয়ে তারা মতবিরোধ করে থাকে!');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5676,78,4,'কখনই না, তাদের ধারণা অবাস্তব, তারা শীঘ্রই জানতে পারবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5677,78,5,'আবার বলি, কখনই না, তারা অচিরেই অবগত হবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5678,78,6,'আমি কি পৃথিবীকে শয্যা (রূপে) নির্মাণ করিনি?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5679,78,7,'এবং পর্বতসমূহকে কীলক রূপে নির্মাণ করিনি? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5680,78,8,'আমি সৃষ্টি করেছি তোমাদেরকে জোড়ায় জোড়ায়। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5681,78,9,'তোমাদের জন্য নিদ্রাকে করে দিয়েছি বিশ্রাম, ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5682,78,10,'করেছি রজনীকে আবরণ, ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5683,78,11,'এবং করেছি দিনকে জীবিকা আহরণের জন্য (উপযোগী)।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5684,78,12,'আর নির্মাণ করেছি তোমাদের উর্ধ্বদেশে সুদৃঢ় সপ্ত আকাশ,');
