INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5689,78,17,'নিশ্চয়ই নির্ধারিত আছে মীমাংসা দিন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5690,78,18,'সেদিন শিঙ্গায় ফুৎকার দেয়া হবে এবং তোমরা দলে দলে সমাগত হবে,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5691,78,19,'আকাশকে উন্মুক্ত করা হবে, ফলে ওটা হয়ে যাবে বহু দ্বারবিশিষ্ট।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5692,78,20,'এবং সঞ্চালিত করা হবে পর্বতসমূহকে, ফলে সেগুলি হয়ে যাবে মরীচিকা বৎ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5693,78,21,'নিশ্চয়ই জাহান্নাম ওঁৎ পেতে রয়েছে। ');
