INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5699,78,27,'তারা কখনও হিসাবের আশংকা করতনা,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5700,78,28,'এবং তারা দৃঢ়তার সাথে আমার নিদর্শনাবলী অস্বীকার করেছিল। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5701,78,29,'সব কিছুই আমি সংরক্ষণ করেছি লিখিতভাবে।');
