INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5708,78,36,'এটাই তোমার রবের অনুগ্রহের পূর্ণ প্রতিদান।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5709,78,37,'যিনি রাব্ব আকাশমন্ডলী, পৃথিবী ও ওগুলির অন্তঃবর্তী সব কিছুর, যিনি দয়াময়; তাঁর নিকট আবেদন-নিবেদনের শক্তি তাদের থাকবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5710,78,38,'সেদিন রুহ্ ও মালাইকা/ফেরেশতাগণ সারিবদ্ধভাবে দাঁড়াবে; দয়াময় যাকে অনুমতি দিবেন সে ছাড়া অন্যেরা কথা বলবেনা এবং সে সঙ্গত কথা বলবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5711,78,39,'এই দিন সুনিশ্চিত। অতএব যার অভিরুচি সে তার রবের শরণাপন্ন হোক।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5712,78,40,'আমি তোমাদেরকে আসন্ন শাস্তি সম্পর্কে সতর্ক করলাম; সেদিন মানুষ তার কৃতকর্ম প্রত্যক্ষ করবে এবং কাফির বলতে থাকবেঃ হায়রে হতভাগা আমি! যদি আমি মাটি হয়ে যেতাম!');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5713,79,1,'শপথ তাদের যারা নির্মমভাবে উৎপাটন করে,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5714,79,2,'এবং যারা মৃদুভাবে বন্ধন মুক্ত করে দেয়, ');
