INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5752,79,40,'পক্ষান্তরে যে স্বীয় রবের সামনে উপস্থিত হওয়ার ভয় রাখে এবং কুপ্রবৃত্তি হতে নিজেকে বিরত রাখে,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5753,79,41,'জান্নাতই হবে তার অবস্থিতি স্থান। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5754,79,42,'তারা তোমাকে জিজ্ঞেস করে কিয়ামাত সম্পর্কে যে, ওটা কখন ঘটবে?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5755,79,43,'এর আলোচনার সাথে তোমার কি সম্পর্ক?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5756,79,44,'এর উত্তম জ্ঞান আছে তোমার রবেরই নিকট।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5757,79,45,'যে ওর ভয় রাখে তুমি শুধু তারই সতর্ককারী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5758,79,46,'যেদিন তারা এটা প্রত্যক্ষ করবে সেদিন তাদের মনে হবে, যেন তারা পৃথিবীতে এক সন্ধ্যা অথবা এক প্রভাতের অধিক অবস্থান করেনি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5759,80,1,'সে ভ্রু কুঞ্চিত করল এবং মুখ ফিরিয়ে নিলো,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5760,80,2,'যেহেতু তার নিকট এক অন্ধ আগমন করেছিল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5761,80,3,'তুমি কেমন করে জানবে সে হয়তো পরিশুদ্ধ হত,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5762,80,4,'অথবা উপদেশ গ্রহণ করত, ফলে উপদেশ তার উপকারে আসতো।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5763,80,5,'পক্ষান্তরে যে পরওয়া করেনা –');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5764,80,6,'তুমি তার প্রতি মনোযোগ দিয়েছো।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5765,80,7,'অথচ সে নিজে পরিশুদ্ধ না হলে তোমার কোন দায়িত্ব নেই। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5766,80,8,'অন্যপক্ষ যে তোমার নিকট ছুটে এলো – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5767,80,9,'তার সেই সশংক চিত্ত – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5768,80,10,'তুমি তাকে অবজ্ঞা করলে! ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5769,80,11,'না, এই আচরণ অনুচিত, এটাতো উপদেশ বাণী;');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5770,80,12,'যে ইচ্ছা করবে সে ইহা স্মরণ রাখবে, ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5771,80,13,'ইহা আছে মর্যাদাময় পত্রসমূহে (লিখিত) – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5772,80,14,'(এবং) উন্নত পুতঃ –');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5773,80,15,'লেখকদের হাতে (সুরক্ষিত)।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5774,80,16,'(ঐ লেখকগণ) মহৎ ও সৎ। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5775,80,17,'মানুষ ধ্বংস হোক! সে কত অকৃতজ্ঞ!');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5776,80,18,'তিনি তাকে কোন বস্তু হতে সৃষ্টি করেছেন?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5777,80,19,'শুক্র বিন্দু হতে তিনি তাকে সৃষ্টি করেন, পরে তার পরিমিত বিকাশ সাধন করেন,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5778,80,20,'অতঃপর তার জন্য পথ সহজ করে দেন;');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5779,80,21,'অতঃপর তার মৃত্যু ঘটান এবং তাকে কাবরস্থ করেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5780,80,22,'এরপর যখন ইচ্ছা তিনি তাকে পুনরুজ্জীবিত করবেন। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5781,80,23,'তিনি তাকে যে আদেশ করেছেন, সেতো তা পালন করেনি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5782,80,24,'মানুষ তার খাদ্যের প্রতি লক্ষ্য করুক।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5783,80,25,'আমিই প্রচুর বারি বর্ষণ করি,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5784,80,26,'অতঃপর আমি ভূমিকে প্রকৃষ্ট রূপে বিদীর্ণ করি; ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5785,80,27,'এবং ওতে আমি উৎপন্ন করি শস্য;');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5786,80,28,'দ্রাক্ষা, শাক-সবজি,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5787,80,29,'যাইতূন, খেজুর,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5788,80,30,'বহু বৃক্ষবিশিষ্ট উদ্যান, ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5789,80,31,'ফল এবং গবাদির খাদ্য,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5790,80,32,'এটা তোমাদের ও তোমাদের পশুগুলির ভোগের জন্য।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5791,80,33,'যখন ঐ ধ্বংস ধ্বনি এসে পড়বে;');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5792,80,34,'সেদিন মানুষ পলায়ন করবে তার ভাই হতে, ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5793,80,35,'এবং তার মাতা, তার পিতা,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5794,80,36,'তার স্ত্রী ও তার সন্তান হতে, ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5795,80,37,'সেদিন তাদের প্রত্যেকের হবে এমন গুরুতর অবস্থা যা তাকে সম্পূর্ণরূপে ব্যস্ত রাখবে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5796,80,38,'সেদিন বহু মুখমন্ডল হবে দীপ্তিমান; ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5797,80,39,'সহাস্য ও প্রফুল্ল। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5798,80,40,'এবং অনেক মুখমন্ডল হবে সেদিন ধূলি ধূসরিত,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5799,80,41,'সেগুলিকে আচ্ছন্ন করবে কালিমা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5800,80,42,'তারাই কাফির ও পাপাচারী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5801,81,1,'সূর্য যখন নিস্প্রভ হবে, ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5802,81,2,'যখন নক্ষত্ররাজি খসে পড়বে, ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5803,81,3,'পর্বতসমূহকে যখন চলমান করা হবে,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5804,81,4,'যখন পূর্ণ-গর্ভা উষ্ট্রী উপেক্ষিত হবে,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5805,81,5,'যখন বন্য পশুগুলি একত্রীকৃত হবে;');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5806,81,6,'এবং সমুদ্রগুলিকে যখন উদ্বেলিত করা হবে;');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5807,81,7,'দেহে যখন আত্মা পুনঃসংযোজিত হবে,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5808,81,8,'যখন জীবন্ত প্রোথিতা কন্যাকে জিজ্ঞেস করা হবে –');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5809,81,9,'কি অপরাধে তাকে হত্যা করা হয়েছিল?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5810,81,10,'যখন ‘আমলনামা উন্মোচিত হবে,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5811,81,11,'যখন আকাশের আবরণ অপসারিত হবে,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5812,81,12,'জাহান্নামের আগুন যখন উদ্দীপিত করা হবে,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5813,81,13,'এবং জান্নাত যখন নিকটর্বতী করা হবে,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5814,81,14,'তখন প্রত্যেক ব্যক্তিই জানবে সে কি নিয়ে এসেছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5815,81,15,'কিন্তু না, আমি প্রত্যাবর্তনকারী তারকাপুঞ্জের শপথ করছি!');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5816,81,16,'যা গতিশীল ও স্থিতিবান;');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5817,81,17,'শপথ রাতের যখন ওর আবির্ভাব হয়,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5818,81,18,'আর উষার যখন ওর আবির্ভাব হয়,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5819,81,19,'নিশ্চয়ই এই কুরআন সম্মানিত বার্তাবহের আনীত বাণী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5820,81,20,'যে সামর্থশালী, আরশের মালিকের নিকট মর্যাদাসম্পন্ন,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5821,81,21,'যাকে সেখানে মান্য করা হয় এবং যে বিশ্বাসভাজন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5822,81,22,'এবং তোমাদের সহচর উন্মাদ নয়,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5823,81,23,'সেতো তাকে স্পষ্ট দিগন্তে অবলোকন করেছে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5824,81,24,'সে অদৃশ্য বিষয় সম্পর্কে বর্ণনা করতে কার্পন্য করেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5825,81,25,'এবং ইহা অভিশপ্ত শাইতানের বাক্য নয়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5826,81,26,'সুতরাং তোমরা কোথায় চলেছ?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5827,81,27,'এটাতো শুধু বিশ্বজগতের জন্য উপদেশ –');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5828,81,28,'তোমাদের মধ্যে যে সরল পথে চলতে চায় তার জন্য।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5829,81,29,'তোমরা ইচ্ছা করবেনা, যদি জগতসমূহের রাব্ব আল্লাহ ইচ্ছা না করেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5830,82,1,'আকাশ যখন বিদীর্ণ হবে,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5831,82,2,'যখন নক্ষত্রমন্ডলী বিক্ষিপ্তভাবে ঝরে পড়বে,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5832,82,3,'যখন সমুদ্র উদ্বেলিত হবে,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5833,82,4,'এবং যখন কাবরসমূহ সমুত্থিত হবে;');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5834,82,5,'যখন প্রত্যেকে যা পূর্বে প্রেরণ করেছে এবং পশ্চাতে পরিত্যাগ করেছে তা পরিজ্ঞাত হবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5835,82,6,'হে মানুষ! কিসে তোমাকে তোমার মহান রাব্ব (আল্লাহ) হতে প্রতারিত করল?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5836,82,7,'যিনি তোমাকে সৃষ্টি করেছেন, অতঃপর তোমাকে সুঠাম করেছেন এবং অতঃপর সুবিন্যস্ত করেছেন,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5837,82,8,'যে আকৃতিতে চেয়েছেন, তিনি তোমাকে গঠন করেছেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5838,82,9,'না, কখনই না, তোমরাতো শেষ বিচারকে অস্বীকার করে থাকো;');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5839,82,10,'অবশ্যই রয়েছে তোমাদের উপর সংরক্ষকগণ;');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5840,82,11,'সম্মানিত লেখকবর্গ;');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5841,82,12,'তারা অবগত হয় যা তোমরা কর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5842,82,13,'পুণ্যবানগণতো থাকবে পরম সুখ সম্পদে;');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5843,82,14,'এবং দুস্কর্মকারীরা থাকবে জাহান্নামে;');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5844,82,15,'তারা কর্মফল দিনে তাতে প্রবিষ্ট হবে;');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5845,82,16,'তারা ওটা হতে অন্তর্হিত হতে পারবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5846,82,17,'কর্মফল দিন কি তা কি তুমি জান?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5847,82,18,'আবার বলিঃ কর্মফল দিন কি তা কি তুমি অবগত আছ?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5848,82,19,'সেদিন একের অপরের জন্য কিছু করার সামর্থ্য থাকবেনা; এবং সেদিন সমস্ত কর্তৃত্ব হবে একমাত্র আল্লাহর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5849,83,1,'মন্দ পরিণাম তাদের জন্য যারা মাপে কম দেয়,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5850,83,2,'যারা লোকের নিকট হতে মেপে নেয়ার সময় পূর্ণ মাত্রায় গ্রহণ করে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5851,83,3,'এবং যখন তাদের জন্য মেপে অথবা ওজন করে দেয় তখন কম দেয়। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5852,83,4,'তারা কি চিন্তা করেনা যে, তারা পুনরুত্থিত হবে,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5853,83,5,'সেই মহান দিনে;');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5854,83,6,'যে দিন দাঁড়াবে সমস্ত মানুষ জগতসমূহের রবের সম্মুখে!');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5855,83,7,'না, না, কখনই না; পাপাচারীদের ‘আমলনামা নিশ্চয়ই সিজ্জীনে থাকে;');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5856,83,8,'সিজ্জীন কি তা কি তুমি জান?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5857,83,9,'ওটা হচ্ছে লিখিত পুস্তক।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5858,83,10,'সেদিন মন্দ পরিণাম হবে মিথ্যাচারীদের –');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5859,83,11,'যারা কর্মফল দিনকে অস্বীকার করে,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5860,83,12,'আর সীমা লংঘনকারী মহাপাপী ব্যতীত কেহই ওকে মিথ্যা বলতে পারেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5861,83,13,'তার নিকট আমার আয়াতসমূহ আবৃত্তি করা হলে সে বলেঃ এটাতো পূরাকালীন কাহিনী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5862,83,14,'না, এটা সত্য নয়, বরং তাদের কৃতকর্মের ফলেই তাদের মনের উপর মরিচা জমে গেছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5863,83,15,'না, অবশ্যই সেদিন তারা তাদের রবের সাক্ষাত হতে অন্তরীণ থাকবে;');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5864,83,16,'অনন্তর নিশ্চয়ই তারা জাহান্নামে প্রবেশ করবে;');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5865,83,17,'অতঃপর বলা হবেঃ এটাই তা যা তোমরা অস্বীকার করতে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5866,83,18,'অবশ্যই পুণ্যবানদের ‘আমলনামা ইল্লিয়্যীনে থাকবে,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5867,83,19,'ইল্লিয়্যীন কি তা কি তুমি জান?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5868,83,20,'(তা হচ্ছে) লিখিত পুস্তক।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5869,83,21,'আল্লাহর সান্নিধ্য প্রাপ্তরা ওটা প্রত্যক্ষ করবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5870,83,22,'সৎ আমলকারীতো থাকবে পরম স্বাচ্ছন্দ্যে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5871,83,23,'তারা সুসজ্জিত আসনে বসে অবলোকন করবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5872,83,24,'তুমি তাদের মুখমন্ডলে স্বাচ্ছন্দ্যের দীপ্তি দেখতে পাবে,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5873,83,25,'তাদেরকে মোহরযুক্ত বিশুদ্ধ মদিরা হতে পান করানো হবে,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5874,83,26,'ওর মোহর হচ্ছে কস্তুরীর। আর থাকে যদি কারও কোন আকাংখা বা কামনা, তাহলে তারা এরই কামনা করুক।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5875,83,27,'ওর মিশ্রণ হবে তাসনীমের।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5876,83,28,'এটি একটি প্রস্রবণ, যা হতে নৈকট্য প্রাপ্ত ব্যক্তিরা পান করে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5877,83,29,'অপরাধীরা মু’মিনদেরকে উপহাস করত,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5878,83,30,'এবং তারা যখন মু’মিনদের নিকট দিয়ে যেত তখন,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5879,83,31,'এবং যখন তাদের আপনজনের নিকট ফিরে আসতো তখন তারা ফিরতো উৎফুল্ল হয়ে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5880,83,32,'এবং যখন তাদেরকে দেখত তখন বলতঃ এরাইতো পথভ্রষ্ট।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5881,83,33,'তাদেরকে তো এদের সংরক্ষকরূপে পাঠানো হয়নি!');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5882,83,34,'আজ তাই মু’মিনগণ উপহাস করছে কাফিরদেরকে –');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5883,83,35,'সুসজ্জিত আসন হতে তাদেরকে অবলোকন করে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5884,83,36,'কাফিরেরা তাদের কৃত কর্মের ফল পেলো তো?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5885,84,1,'যখন আকাশ বিদীর্ণ হবে,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5886,84,2,'এবং ওটা স্বীয় রবের আদেশ পালন করবে, আর ওকে তদুপযোগী করা হবে,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5887,84,3,'এবং পৃথিবীকে যখন সম্প্রসারিত করা হবে,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5888,84,4,'এবং পৃথিবী তার অভ্যন্তরে যা আছে তা বাইরে নিক্ষেপ করবে ও শূন্য গর্ভ হয়ে যাবে,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5889,84,5,'এবং তার রবের আদেশ পালন করবে, আর ওকে তদুপযোগী করা হবে (তখন তোমরা পুনরুত্থিত হবেই)।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5890,84,6,'হে মানবসকল! তোমরা কঠোর পরিশ্রম করতে থাক, তোমাদের আমাল অনুযায়ী তোমাদের রবের সাক্ষাত লাভ করবে, আর এটাতো অবশ্যম্ভাবি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5891,84,7,'অতঃপর যাকে ডান হাতে তার কর্মলিপি প্রদত্ত হবে –');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5892,84,8,'তার হিসাব-নিকাশতো সহজভাবে গৃহীত হবে,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5893,84,9,'এবং সে তার স্বজনদের নিকট প্রফুল্ল চিত্তে ফিরে যাবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5894,84,10,'এবং যাকে তার কর্মলিপি তার পৃষ্ঠের পশ্চাদ্ভাগে দেয়া হবে –');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5895,84,11,'ফলে অচিরেই সে মৃত্যুকে আহবান করবে,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5896,84,12,'এবং জ্বলন্ত আগুনে সে প্রবেশ করবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5897,84,13,'সে তার স্বজনদের মধ্যেতো সহর্ষে ছিল,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5898,84,14,'যেহেতু সে ভাবতো যে, সে কখনই প্রত্যাবর্তিত হবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5899,84,15,'হ্যাঁ,(অবশ্যই প্রত্যাবর্তিত হবে) নিশ্চয়ই তার রাব্ব তার উপর সবিশেষ দৃষ্টি রাখেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5900,84,16,'আমি শপথ করি অস্তরাগের –');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5901,84,17,'এবং রাতের, আর ওটা যা কিছুর সমাবেশ ঘটায় তার,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5902,84,18,'এবং শপথ চন্দ্রের যখন ওটা পরিপূর্ণ হয়,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5903,84,19,'নিশ্চয়ই তোমরা এক স্তর হতে অন্য স্তরে আরোহণ করবে,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5904,84,20,'সুতরাং তাদের কি হল যে, তারা বিশ্বাস স্থাপন করেনা?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5905,84,21,'এবং তাদের নিকট কুরআন পঠিত হলে তারা সাজদাহ করেনা? [সাজদাহ]');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5906,84,22,'পরন্তু কাফিরেরাই অসত্যারোপ করে,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5907,84,23,'অথচ তারা (মনে মনে) যা পোষণ করে আল্লাহ তা সবিশেষ পরিজ্ঞাত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5908,84,24,'সুতরাং তাদেরকে যন্ত্রণাদায়ক শাস্তির সুসংবাদ প্রদান কর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5909,84,25,'যারা ঈমান আনে ও সৎ কাজ করে তাদের জন্য রয়েছে অবিচ্ছিন্ন পুরস্কার।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5910,85,1,'শপথ রাশিচক্র সমন্বিত আকাশের,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5911,85,2,'এবং প্রতিশ্রুত দিনের,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5912,85,3,'শপথ দ্রষ্টা ও দৃষ্টের।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5913,85,4,'ধ্বংস হয়েছিল কুন্ডের অধিপতিরা –');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5914,85,5,'ইন্ধনপূর্ণ যে কুন্ডে ছিল অগ্নি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5915,85,6,'যখন তারা এর পাশে উপবিষ্ট ছিল;');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5916,85,7,'এবং তারা মু’মিনদের সাথে যা করেছিল তা প্রত্যক্ষ করছিল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5917,85,8,'তারা তাদেরকে নির্যাতন করেছিল শুধু এ কারণে যে, তারা সেই মহিমাময় পরাক্রান্ত প্রশংসাভাজন আল্লাহর প্রতি ঈমান এনেছিল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5918,85,9,'আকাশমন্ডলী ও পৃথিবীর সার্বভৌমত্ব যাঁর, আর আল্লাহ সর্ব বিষয়ে দ্রষ্টা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5919,85,10,'যারা বিশ্বাসী নর নারীকে বিপদাপন্ন করেছে এবং পরে তাওবাহ করেনি, তাদের জন্য আছে জাহান্নামের শাস্তি ও দহন যন্ত্রণা (নির্ধারিত) রয়েছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5920,85,11,'যারা ঈমান আনে ও সৎ কাজ করে তাদের জন্যই আছে জান্নাত, যার নিম্নে স্রোতস্বিনী প্রবাহিত; এটাই সুমহান, সফলকাম।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5921,85,12,'তোমার রবের শাস্তি বড়ই কঠিন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5922,85,13,'তিনিই অস্তিত্ব দান করেন ও পুনরাবর্তন ঘটান,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5923,85,14,'এবং তিনি ক্ষমাশীল, প্রেমময়,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5924,85,15,'আরশের অধিপতি মহিমময়,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5925,85,16,'তিনি যা ইচ্ছা করেন তা’ই করে থাকেন,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5926,85,17,'তোমার নিকট কি পৌঁছেছে সৈন্য বাহিনীর বৃত্তান্ত –');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5927,85,18,'ফির‘আউন ও ছামূদের?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5928,85,19,'তবু কাফিরেরা মিথ্যা আরোপ করায় রত,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5929,85,20,'এবং আল্লাহ তাদের পরিবেষ্টন করে রয়েছেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5930,85,21,'এটা কুরআন,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5931,85,22,'সংরক্ষিত ফলকে লিপিবদ্ধ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5932,86,1,'শপথ আকাশের এবং রাতে যা আবির্ভূত হয় তার;');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5933,86,2,'তুমি কী জান রাতে যা আবির্ভূত হয় তা কি?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5934,86,3,'ওটা দীপ্তিমান নক্ষত্র!');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5935,86,4,'প্রত্যেক জীবের উপরই সংরক্ষক রয়েছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5936,86,5,'সুতরাং মানুষের চিন্তা করা উচিত যে, তাকে কিসের দ্বারা সৃষ্টি করা হয়েছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5937,86,6,'তাকে সৃষ্টি করা হয়েছে সবেগে স্খলিত পানি হতে,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5938,86,7,'এটা নির্গত হয় পৃষ্ঠদেশ ও পঞ্জরাস্থির মধ্য হতে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5939,86,8,'নিশ্চয়ই তিনি তার পুনরাবর্তনে ক্ষমতাবান।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5940,86,9,'যেদিন গোপন বিষয়সমূহ পরীক্ষা করা হবে – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5941,86,10,'সেদিন তার কোন ক্ষমতা থাকবেনা এবং সাহায্যকারীও না।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5942,86,11,'শপথ আসমানের যা ধারণ করে বৃষ্টি,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5943,86,12,'এবং শপথ যমীনের যা বিদীর্ণ হয়,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5944,86,13,'নিশ্চয়ই ইহা (আল কুরআন) মীমাংসাকারী বাণী,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5945,86,14,'এবং এটা নিরর্থক নয়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5946,86,15,'তারা ভীষণ ষড়যন্ত্র করে,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5947,86,16,'আর আমিও ভীষণ কৌশল করি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5948,86,17,'অতএব কাফিরদেরকে অবকাশ দাও, তাদেরকে অবকাশ দাও কিছু কালের জন্য।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5949,87,1,'তুমি তোমার সুমহান রবের নামের পবিত্রতা ও মহিমা ঘোষণা কর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5950,87,2,'যিনি সৃষ্টি করেছেন, অতঃপর যথাযথভাবে সমন্বিত করেছেন,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5951,87,3,'এবং যিনি নিয়ন্ত্রণ করেছেন, তারপর পথ দেখিয়েছেন,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5952,87,4,'এবং যিনি তৃণাদী উৎপন্ন করেছেন,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5953,87,5,'পরে ওকে বিশুস্ক বিমলিন করেছেন,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5954,87,6,'অচিরেই আমি তোমাকে পাঠ করাব, ফলে তুমি বিস্মৃত হবেনা – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5955,87,7,'আল্লাহ যা ইচ্ছা করবেন তদ্ব্যতীত, নিশ্চয়ই তিনি প্রকাশ্য ও গুপ্ত বিষয় জ্ঞাত আছেন,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5956,87,8,' আমি তোমার জন্য কল্যাণের পথকে সহজ করে দিব।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5957,87,9,'অতএব উপদেশ যদি ফলপ্রসু হয় তাহলে উপদেশ প্রদান কর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5958,87,10,'যারা ভয় করে তারা উপদেশ গ্রহণ করবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5959,87,11,'আর ওটা উপেক্ষা করবে সে, যে নিতান্ত হতভাগা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5960,87,12,'সে ভয়াবহ অগ্নিকুন্ডে প্রবেশ করবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5961,87,13,'অতঃপর সে সেখানে মরবেও না, বাঁচবেও না।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5962,87,14,'নিশ্চয়ই সে সাফল্য লাভ করবে যে পবিত্রতা অর্জন করে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5963,87,15,'এবং স্বীয় রবের নাম স্মরণ করে ও সালাত আদায় করে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5964,87,16,'কিন্তু তোমরা পার্থিব জীবনকে পছন্দ করে থাক,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5965,87,17,'অথচ আখিরাতের জীবনই উত্তম ও অবিনশ্বর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5966,87,18,'নিশ্চয়ই এটা পূর্ববর্তী গ্রন্থসমূহে (বিদ্যমান) আছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5967,87,19,'(বিশেষতঃ) ইবরাহীম ও মূসার গ্রন্থসমূহে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5968,88,1,'তোমার কাছে কি সমাচ্ছন্নকারী সংবাদ পৌঁছেছে?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5969,88,2,'সেদিন বহু মুখমন্ডল অবনত হবে;');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5970,88,3,'কর্মক্লান্ত পরিশ্রান্তভাবে;');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5971,88,4,'তারা প্রবেশ করবে জ্বলন্ত আগুনে;');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5972,88,5,'তাদেরকে উত্তপ্ত প্রস্রবণ হতে পান করানো হবে,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5973,88,6,'তাদের জন্য বিষাক্ত কন্টক ব্যতীত খাদ্য নেই – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5974,88,7,'যা তাদেরকে পুষ্ট করবেনা এবং তাদের ক্ষুধাও নিবৃত্ত করবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5975,88,8,'বহু মুখমন্ডল হবে সেদিন আনন্দোজ্জ্বল –');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5976,88,9,'নিজেদের কর্মসাফল্যে পরিতৃপ্ত,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5977,88,10,'সমুন্নত কাননে অবস্থিতি হবে –');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5978,88,11,'সেখানে তারা অবান্তর বাক্য শুনবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5979,88,12,'সেখানে আছে প্রবহমান ঝর্ণাধারা –');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5980,88,13,'তন্মধ্যে রয়েছে সমুচ্চ আসনসমূহ;');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5981,88,14,'এবং সুরক্ষিত পান পাত্রসমূহ,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5982,88,15,'ও সারি সারি তাকিয়াসমূহ,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5983,88,16,'এবং সম্প্রসারিত গালিচাসমূহ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5984,88,17,'তাহলে কি তারা উষ্ট্র পালের দিকে লক্ষ্য করেনা যে, কিভাবে ওকে সৃষ্টি করা হয়েছে?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5985,88,18,'এবং আকাশের দিকে যে, কিভাবে ওটাকে সমুচ্চ করা হয়েছে?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5986,88,19,'এবং পর্বতমালার দিকে যে, কিভাবে ওটা দৃঢ়ভাবে স্থাপন করা হয়েছে?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5987,88,20,'এবং ভূতলের দিকে যে, কিভাবে ওটাকে সমতল করা হয়েছে?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5988,88,21,'অতএব তুমি উপদেশ দিতে থাক, তুমিতো একজন উপদেশ দাতা মাত্র।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5989,88,22,'তুমি তাদের কর্মনিয়ন্ত্রক নও।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5990,88,23,'তবে কেহ মুখ ফিরিয়ে নিলে ও কুফরী করলে –');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5991,88,24,'আল্লাহ তাকে কঠোর দন্ডে দন্ডিত করবেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5992,88,25,'নিশ্চয়ই তাদের প্রত্যাবর্তন আমারই নিকট।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5993,88,26,'অতঃপর আমারই নিকট তাদের হিসাব-নিকাশ (গ্রহণের ভার)।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5994,89,1,'শপথ ঊষার,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5995,89,2,'শপথ দশ রাতের,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5996,89,3,'শপথ জোড় ও বেজোড়ের,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5997,89,4,'এবং শপথ রাতের, যখন ওটা গত হতে থাকে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5998,89,5,'জ্ঞানবান ব্যক্তি জন্য কি এর মধ্যে কসম করার উপাদান নেই?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (5999,89,6,'তুমি কি দেখনি তোমার রাব্ব কি করেছিলেন ‘আদ বংশের –');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6000,89,7,'ইরাম গোত্রের প্রতি, যারা অধিকারী ছিল সুউচ্চ প্রাসাদের?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6001,89,8,'যার সমতুল্য অন্য কোন নগর নির্মিত হয়নি?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6002,89,9,'এবং ছামূদের প্রতি, যারা উপত্যকার পাথর কেটে গৃহ নির্মাণ করেছিল?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6003,89,10,'এবং বহু সৈন্য শিবিরের অধিপতি ফির‘আউনের প্রতি – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6004,89,11,'যারা নগরসমূহে উদ্ধতাচরণ করেছিল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6005,89,12,'অতঃপর সেখানে তারা বহু বিপ্লব (সংঘটিত) করেছিল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6006,89,13,'সুতরাং তোমার রাব্ব তাদের উপর শাস্তির কশাঘাত হানলেন। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6007,89,14,'নিশ্চয়ই তোমার রাব্ব সবই দেখেন ও সময়ের প্রতীক্ষায় থাকেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6008,89,15,'মানুষতো এমনই যে, তার রাব্ব যখন তাকে পরীক্ষা করেন, পরে তাকে সম্মানিত করেন এবং সুখ-সম্পদ দান করেন, তখন সে বলেঃ আমার রাব্ব আমাকে সম্মানিত করেছেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6009,89,16,'এবং আবার যখন তাকে পরীক্ষা করেন, অতঃপর তার রিয্ক সংকুচিত করেন, তখন সে বলেঃ আমার রাব্ব আমাকে হীন করেছেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6010,89,17,'না, কখনই নয়। বস্তুতঃ তোমরা ইয়াতীমদেরকে সম্মান করনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6011,89,18,'এবং তোমরা অভাবগ্রস্তকে খাদ্য দানে পরস্পরকে উৎসাহিত করনা,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6012,89,19,'এবং তোমরা উত্তরাধিকারীদের প্রাপ্য সম্পদ সম্পূর্ণ রূপে আহার করে থাকো,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6013,89,20,'এবং তোমরা ধন-সম্পদকে অত্যধিক ভালবেসে থাক,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6014,89,21,'এটা সংগত নয়। পৃথিবীকে যখন চূর্ণ বিচূর্ণ করা হবে,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6015,89,22,'এবং যখন তোমার রাব্ব আগমন করবেন, আর সারিবদ্ধভাবে মালাইকা/ফেরেশতাগণও সমুপস্থিত হবে –');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6016,89,23,'সেদিন জাহান্নামকে আনয়ন করা হবে এবং সেদিন মানুষ উপলদ্ধি করবে, কিন্তু এই উপলদ্ধি তার কি করে কাজে আসবে?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6017,89,24,'সে বলবেঃ হায়! আমার এ জীবনের জন্য আমি যদি কিছু অগ্রিম পাঠাতাম!');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6018,89,25,'সেই দিন তাঁর শাস্তির মত শাস্তি কেহ দিতে পারবেনা,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6019,89,26,'এবং তাঁর বন্ধনের মত বন্ধন কেহ করতে পারবেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6020,89,27,'বলা হবেঃ হে প্রশান্ত চিত্ত!');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6021,89,28,'তুমি তোমার রবের নিকট ফিরে এসো সন্তুষ্ট ও সন্তোষ ভাজন হয়ে,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6022,89,29,'অতঃপর তুমি আমার বান্দাদের অন্তর্ভুক্ত হও,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6023,89,30,'এবং আমার জান্নাতে প্রবেশ কর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6024,90,1,'শপথ করছি এই নগরের,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6025,90,2,'আর তুমি এই নগরের বৈধ অধিকারী হবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6026,90,3,'শপথ জন্মদাতার এবং যা সে জন্ম দিয়েছে তার।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6027,90,4,'অবশ্যই আমি মানুষকে সৃষ্টি করেছি ক্লেশের মধ্যে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6028,90,5,'সে কি মনে করে যে, কখনও তার উপর কেহ ক্ষমতাবান হবেনা? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6029,90,6,'সে বলেঃ আমি রাশি রাশি অর্থ উড়িয়ে দিয়েছি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6030,90,7,'সে কি ধারণা করে যে, তাকে কেহই দেখছেনা?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6031,90,8,'আমি কি তার জন্য সৃষ্টি করিনি চক্ষু যুগল?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6032,90,9,'তার জিহবা ও ওষ্ঠদ্বয়?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6033,90,10,'এবং আমি তাদেরকে দু’টি পথ দেখিয়েছি ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6034,90,11,'কিন্তু সে গিরিসংকটে প্রবেশ করলনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6035,90,12,'তুমি কি জান, গিরিসংকট কি?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6036,90,13,'এটা হচ্ছে দাসকে মুক্তি প্রদান।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6037,90,14,'অথবা দুর্ভিক্ষের সময় আহার্য দান –');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6038,90,15,'পিতৃহীন আত্মীয়কে,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6039,90,16,'অথবা ধূলায় লুন্ঠিত দরিদ্রকে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6040,90,17,'অতঃপর অন্তর্ভুক্ত হওয়া মু’মিনদের এবং তাদের যারা পরস্পরকে উপদেশ দেয় ধৈর্য ধারনের ও দয়া দাক্ষিণ্যের।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6041,90,18,'তারাই সৌভাগ্যশালী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6042,90,19,'যারা আমার নিদর্শন প্রত্যাখ্যান করেছে তারাই হতভাগ্য।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6043,90,20,'তাদের উপরই অবরুদ্ধ রয়েছে প্রচন্ড আগুন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6044,91,1,'শপথ সূর্যের যখন সে আচ্ছন্ন করে,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6045,91,2,'শপথ চন্দ্রের যখন ওটা সূর্যের পর আবির্ভুত হয়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6046,91,3,'শপথ দিনের, যখন ওটা ওকে প্রকাশ করে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6047,91,4,'শপথ রাতের, যখন ওটা ওকে আচ্ছাদিত করে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6048,91,5,'শপথ আকাশের এবং যিনি ওটা নির্মাণ করেছেন তাঁর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6049,91,6,'শপথ পৃথিবীর এবং যিনি ওকে বিস্তৃত করেছেন তাঁর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6050,91,7,'শপথ মানুষের এবং তাঁর, যিনি তাকে সুঠাম করেছেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6051,91,8,'অতঃপর তাকে তার অসৎ কাজ ও তার সৎ কার্জের জ্ঞান দান করেছেন,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6052,91,9,'সে’ই সফলকাম হবে যে নিজেকে পবিত্র করবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6053,91,10,'এবং সে’ই ব্যর্থ মনোরথ হবে যে নিজেকে কলুষাচ্ছন্ন করবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6054,91,11,'ছামূদ সম্প্রদায় অবাধ্যতা বশতঃ সত্যকে মিথ্যা বলে প্রত্যাখ্যান করল,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6055,91,12,'সুতরাং তাদের মধ্যে যে সর্বাধিক হতভাগ্য সে যখন তৎপর হয়ে উঠল –');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6056,91,13,'তখন আল্লাহর রাসূল তাদেরকে বললঃ আল্লাহর উষ্ট্রী ও, ওকে পানি পান করানোর বিষয়ে সাবধান হও,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6057,91,14,'কিন্তু তারা তাকে মিথ্যাবাদী বলল, অতঃপর ঐ উষ্ট্রিকে কেটে ফেলল। সুতরাং তাদের পাপের জন্য তাদের রাব্ব তাদেরকে সমূলে ধ্বংস করলেন, অতঃপর তাদেরকে ভূমিসাৎ করে ফেললেন,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6058,91,15,'আর তিনি ওর পরিণামকে ভয় করলেন না।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6059,92,1,'শপথ রাতের যখন ওটা আচ্ছন্ন করে,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6060,92,2,'শপথ দিনের যখন ওটা উদ্ভাসিত করে,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6061,92,3,'এবং শপথ নর ও নারীর যা তিনি সৃষ্টি করেছেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6062,92,4,'অবশ্যই তোমাদের কর্মপ্রচেষ্টা বিভিন্ন মুখী,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6063,92,5,'সুতরাং কেহ দান করলে, সংযত হলে –');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6064,92,6,'এবং সদ্বিষয়কে সত্যজ্ঞান করলে –');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6065,92,7,'অচিরেই আমি তার জন্য সুগম করে দিব সহজ পথ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6066,92,8,'পক্ষান্তরে কেহ কার্পণ্য করলে ও নিজেকে স্বয়ংসম্পূর্ণ মনে করলে –');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6067,92,9,'আর যা উত্তম তা অস্বীকার করলে –');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6068,92,10,'অচিরেই তার জন্য আমি সুগম করে দিব কঠোর পরিণামের পথ –');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6069,92,11,'এবং তার সম্পদ তার কোন কাজে আসবেনা যখন সে ধ্বংস হবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6070,92,12,'আমার কাজতো শুধু পথ নির্দেশ করা,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6071,92,13,'আমিই মালিক পরলোকের ও ইহলোকের।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6072,92,14,'আমি তোমাদেরকে জাহান্নামের লেলিহান আগুন হতে সতর্ক করে দিয়েছি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6073,92,15,'তাতে প্রবেশ করবে সে’ই যে নিতান্ত হতভাগা –');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6074,92,16,'যে অসত্যারোপ করে ও মুখ ফিরিয়ে নেয়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6075,92,17,'আর ওটা হতে রক্ষা পাবে সেই পরম মুত্তাকী –');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6076,92,18,'যে স্বীয় সম্পদ দান করে আত্মশুদ্ধির উদ্দেশে,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6077,92,19,'এবং তার প্রতি কারও অনুগ্রহের প্রতিদান হিসাবে নয়,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6078,92,20,'বরং শুধু তার মহান রবের সন্তোষ লাভের প্রত্যাশায়,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6079,92,21,'সেতো অচিরেই সন্তোষ লাভ করবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6080,93,1,'শপথ পূর্বাহ্নের।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6081,93,2,'শপথ রাতের যখন ওটা সমাচ্ছন্ন করে ফেলে;');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6082,93,3,'তোমার রাব্ব তোমাকে পরিত্যাগ করেননি এবং তোমার প্রতি বিরূপও হননি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6083,93,4,'তোমার জন্য পরবর্তী সময়তো পূর্ববর্তী সময় অপেক্ষা শ্রেয় বা কল্যাণকর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6084,93,5,'অচিরেই তোমার রাব্ব তোমাকে এরূপ দান করবেন যাতে তুমি সন্তুষ্ট হবে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6085,93,6,'তিনি কি তোমাকে পিতৃহীন অবস্থায় পাননি, অতঃপর তোমাকে আশ্রয় দান করেননি? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6086,93,7,'তিনি তোমাকে পেলেন পথ সম্পর্কে অনবহিত, অতঃপর তিনি পথের নির্দেশ দিলেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6087,93,8,'তিনি তোমাকে পেলেন নিঃস্ব অবস্থায়, অতঃপর অভাবমুক্ত করলেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6088,93,9,'অতএব তুমি পিতৃহীনের প্রতি কঠোর হয়োনা,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6089,93,10,'আর সওয়ালকারীকে ভৎর্সনা করনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6090,93,11,'তুমি তোমার রবের অনুগ্রহের কথা ব্যক্ত করতে থাকো।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6091,94,1,'আমি কি তোমার বক্ষ তোমার কল্যাণে উন্মুক্ত করে দিইনি? ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6092,94,2,'আমি তোমা হতে অপসারণ করেছি তোমার সেই ভার – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6093,94,3,'যা তোমার পৃষ্ঠকে অবনমিত করেছিল;');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6094,94,4,'এবং আমি তোমার খ্যাতিকে উচ্চ মর্যাদা দান করেছি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6095,94,5,'কষ্টের সাথেই তো স্বস্তি আছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6096,94,6,'অবশ্যই কষ্টের সাথে স্বস্তি আছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6097,94,7,'অতএব যখনই অবসর পাও সাধনা কর,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6098,94,8,'এবং তোমার রবের প্রতিই মনোনিবেশ কর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6099,95,1,'শপথ ‘তীন’ ও যাইতূন’ এর –');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6100,95,2,'শপথ ‘সিনাই’ পর্বতের – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6101,95,3,'এবং শপথ এই নিরাপদ বা শান্তিময় নগরীর –');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6102,95,4,'আমিতো সৃষ্টি করেছি মানুষকে সুন্দরতম গঠনে,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6103,95,5,'অতঃপর আমি তাকে হীনতাগ্রস্তদের হীনতমে পরিণত করি – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6104,95,6,'কিন্তু তাদের নয় যারা মু’মিন ও সৎ কর্মপরায়ণ; তাদের জন্যতো আছে নিরবিচ্ছিন্ন পুরস্কার।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6105,95,7,'সুতরাং এরপর কিসে তোমাকে কর্মফল সম্বন্ধে অবিশ্বাসী করে?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6106,95,8,'আল্লাহ কি বিচারকদের মধ্যে শ্রেষ্ঠ নন?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6107,96,1,'তুমি পাঠ কর তোমার রবের নামে, যিনি সৃষ্টি করেছেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6108,96,2,'সৃষ্টি করেছেন মানুষকে রক্তপিন্ড হতে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6109,96,3,'পাঠ করঃ আর তোমার রাব্ব মহা মহিমান্বিত,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6110,96,4,'যিনি কলমের সাহায্যে শিক্ষা দিয়েছেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6111,96,5,'তিনি শিক্ষা দিয়েছেন মানুষকে যা সে জানতনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6112,96,6,'বস্তুতঃ মানুষতো সীমালংঘন করেই থাকে,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6113,96,7,'কারণ সে নিজেকে অভাবমুক্ত বা অমুখাপেক্ষী মনে করে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6114,96,8,'তোমার রবের নিকট প্রত্যাবর্তন সুনিশ্চিত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6115,96,9,'তুমি কি তাকে দেখেছো যে বাধা দেয় –');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6116,96,10,'এক বান্দাকে যখন সে সালাত আদায় করে?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6117,96,11,'তুমি লক্ষ্য করেছ কি যদি সে সৎ পথে থাকে?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6118,96,12,'অথবা তাকওয়ার নির্দেশ দেয়?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6119,96,13,'তুমি লক্ষ্য করেছ কি যদি সে মিথ্যা আরোপ করে ও মুখ ফিরিয়ে নেয়?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6120,96,14,'তাহলে সে কি অবগত নয় যে, আল্লাহ দেখছেন?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6121,96,15,'সাবধান! সে যদি নিবৃত্ত না হয় তাহলে আমি তাকে অবশ্যই হেচড়িয়ে নিয়ে যাব মাথার সম্মুখ ভাগের কেশগুচ্ছ ধরে –');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6122,96,16,'মিথ্যাবাদী, পাপিষ্ঠের কেশগুচ্ছ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6123,96,17,'অতএব সে তার পার্শ্বচরদের আহবান করুক।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6124,96,18,'আমিও আহবান করব জাহান্নামের প্রহরীদেরকে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6125,96,19,'সাবধান! তুমি তার অনুসরণ করনা। সাজদাহ কর ও আমার নিকটবর্তী হও। [সাজদাহ]');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6126,97,1,'নিশ্চয়ই আমি ইহা অবতীর্ণ করেছি মহিমান্বিত রাতে;');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6127,97,2,'আর মহিমান্বিত রাত সম্বন্ধে তুমি জান কী?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6128,97,3,'মহিমান্বিত রাত হাজার মাস অপেক্ষা উত্তম।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6129,97,4,'ঐ রাতে (মালাইকা) ফিরিশতাগণ ও রুহ অবতীর্ণ হয় প্রত্যেক কাজে তাদের রবের অনুমতিক্রমে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6130,97,5,'শান্তিই শান্তি! সেই রাত-ফাজরের আবির্ভাব পর্যন্ত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6131,98,1,'কিতাবীদের মধ্যে যারা কুফরী করেছিল এবং মুশরিকরা আপন মতে অবিচল ছিল তাদের নিকট সুস্পষ্ট প্রমান না আসা পর্যন্ত। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6132,98,2,'আল্লাহর নিকট হতে এক রাসূল, যে আবৃত্তি করে পবিত্র গ্রন্থ,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6133,98,3,'যাতে সু-প্রতিষ্ঠিত ব্যবস্থাসমূহ রয়েছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6134,98,4,'যাদেরকে কিতাব দেয়া হয়েছিল তারাতো বিভক্ত হল তাদের নিকট সু-স্পষ্ট প্রমাণ আসার পর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6135,98,5,'তারাতো আদিষ্ট হয়েছিল আল্লাহর আনুগত্যে বিশুদ্ধ চিত্ত হয়ে একনিষ্ঠভাবে তাঁর ইবাদাত করতে এবং সালাত কায়েম করতে ও যাকাত প্রদান করতে; এটাই সু-প্রতিষ্ঠিত ধর্ম।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6136,98,6,'কিতাবীদের মধ্যে যারা কুফরী করে তারা এবং মুশরিকরা জাহান্নামের আগুনে স্থায়ীভাবে অবস্থান করবে; তারাই সৃষ্টির অধম।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6137,98,7,'যারা ঈমান আনে ও সৎ কাজ করে তারাই সৃষ্টির শ্রেষ্ঠ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6138,98,8,'তাদের রবের নিকট আছে তাদের পুরস্কার স্থায়ী জান্নাত, যার নিম্নদেশে নদী প্রবাহিত, সেখানে তারা চিরস্থায়ী হবে; আল্লাহ তাদের উপর প্রসন্ন এবং তারাও তাঁর উপর সন্তুষ্ট; ইহা এ জন্য যে, তারা তাদের রাব্বকে ভয় করে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6139,99,1,'পৃথিবী যখন কম্পনে প্রবলভাবে প্রকম্পিত হবে,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6140,99,2,'এবং পৃথিবী যখন তার ভারসমূহ বের করে দিবে,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6141,99,3,'এবং মানুষ বলবেঃ এর কি হল?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6142,99,4,'সেদিন পৃথিবী তার বৃত্তান্ত বর্ণনা করবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6143,99,5,'তোমার রাব্ব তাকে আদেশ করবেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6144,99,6,'সেদিন মানুষ দলে দলে বের হবে, কারণ তাদেরকে তাদের কৃতকর্ম দেখানো হবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6145,99,7,'কেহ অণু পরিমাণ সৎ কাজ করলে তাও দেখতে পাবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6146,99,8,'এবং কেহ অণু পরিমান অসৎ কাজ করলে তাও দেখতে পাবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6147,100,1,'শপথ উর্ধ্বশ্বাসে ধাবমান অশ্বরাজীর,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6148,100,2,'যারা ক্ষুরাঘাতে অগ্নি স্ফুলিংগ বিচ্ছুরিত করে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6149,100,3,'যারা অভিযান করে প্রভাতকালে,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6150,100,4,'এবং যারা ঐ সময়ে ধূলি উৎক্ষিপ্ত করে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6151,100,5,'অতঃপর শত্রুদলের অভ্যন্তরে ঢুকে পড়ে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6152,100,6,'মানুষ অবশ্যই তার রবের প্রতি অকৃতজ্ঞ,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6153,100,7,'এবং নিশ্চয়ই সে নিজেই এ বিষয়ের সাক্ষী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6154,100,8,'এবং অবশ্যই সে ধন সম্পদের আসক্তিতে অত্যন্ত কঠিন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6155,100,9,'তবে কি সে সেই সম্পর্কে অবহিত নয় যে, কবরে যা আছে তা কখন উত্থিত হবে?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6156,100,10,'এবং অন্তরে যা আছে তা প্রকাশ করা হবে?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6157,100,11,'সেদিন তাদের কি ঘটবে, তাদের রাব্ব অবশ্যই তা সবিশেষ অবহিত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6158,101,1,'মহা প্রলয়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6159,101,2,'মহা প্রলয় কী?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6160,101,3,'মহা প্রলয় সম্বন্ধে তুমি কী জান?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6161,101,4,'সেদিন মানুষ হবে বিক্ষিপ্ত পতংগের মত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6162,101,5,'এবং পর্বতসমূহ হবে ধূনিত রঙ্গীন পশমের মত।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6163,101,6,'তখন যার পাল্লা ভারী হবে – ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6164,101,7,'সেতো লাভ করবে প্রীতিপদ জীবন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6165,101,8,'এবং যার পাল্লা হালকা হবে –');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6166,101,9,'তার স্থান হবে হা’বিয়াহ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6167,101,10,'ওটা কি, তা কি তুমি জান?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6168,101,11,'ওটা অতি উত্তপ্ত অগ্নি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6169,102,1,'প্রাচুর্যের প্রতিযোগিতা তোমাদেরকে মোহাচ্ছন্ন করে রাখে,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6170,102,2,'যতক্ষণ না তোমরা কাবরসমূহে উপস্থিত হচ্ছ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6171,102,3,'এটা সংগত নয়, তোমরা শীঘ্রই এটা জানতে পারবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6172,102,4,'আবার বলি, এটা সংগত নয়, তোমরা শীঘ্রই এটা জানতে পারবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6173,102,5,'সাবধান! তোমাদের নিশ্চিত জ্ঞান থাকলে অবশ্যই তোমরা মোহাচ্ছন্ন হতেনা।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6174,102,6,'তোমরাতো জাহান্নাম দেখবেই।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6175,102,7,'আবার বলি, তোমরাতো ওটা দেখবেই চাক্ষুষ প্রত্যয়ে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6176,102,8,'এরপর অবশ্যই সেদিন তোমরা সুখ সম্পদ সম্বন্ধে জিজ্ঞাসিত হবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6177,103,1,'মহাকালের শপথ!');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6178,103,2,'মানুষ অবশ্যই ক্ষতির মধ্যে রয়েছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6179,103,3,'কিন্তু তারা নয়, যারা ঈমান আনে ও সৎ কাজ করে এবং পরস্পরকে সত্যের উপদেশ দেয় ও ধৈর্য ধারণে পরস্পরকে উদ্ধুদ্ধ করে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6180,104,1,'দুর্ভোগ প্রত্যেকের, যে পশ্চাতে ও সম্মুখে লোকের নিন্দা করে,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6181,104,2,'যে অর্থ জমায় ও তা গুণে গুণে রাখে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6182,104,3,'সে ধারণা করে যে, তার অর্থ তাকে অমর করে রাখবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6183,104,4,'কখনও না, সে অবশ্যই নিক্ষিপ্ত হবে হুতামায়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6184,104,5,'হুতামা কী, তা কি তুমি জান?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6185,104,6,'ওটা আল্লাহর প্রজ্জ্বলিত হুতাশন –');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6186,104,7,'যা হৃদয়কে গ্রাস করবে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6187,104,8,'নিশ্চয়ই ওটা তাদেরকে পরিবেষ্টন করে রাখবে,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6188,104,9,'দীর্ঘায়িত স্তম্ভসমূহে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6189,105,1,'তুমি কি দেখনি যে, তোমার রাব্ব হস্তি অধিপতিদের কিরূপ (পরিণতি) করেছিলেন?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6190,105,2,'তিনি কি তাদের চক্রান্ত ব্যর্থ করে দেননি?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6191,105,3,'তাদের বিরুদ্ধে তিনি ঝাঁকে ঝাঁকে পক্ষীকূল প্রেরণ করেছিলেন –');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6192,105,4,'যারা তাদের উপর প্রস্তর কংকর নিক্ষেপ করেছিল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6193,105,5,'অতঃপর তিনি তাদেরকে ভক্ষিত তৃণ সদৃশ করে দেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6194,106,1,'যেহেতু কুরাইশের আসক্তি আছে,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6195,106,2,'আসক্তি আছে তাদের শীত ও গ্রীষ্ম সফরের,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6196,106,3,'অতএব তারা ইবাদাত করুক এই গৃহের রবের,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6197,106,4,'যিনি তাদেরকে ক্ষুধায় আহার্য দান করেছেন এবং ভয় হতে তাদেরকে নিরাপদ করেছেন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6198,107,1,'তুমি কি দেখেছ তাকে, যে কর্মফলকে মিথ্যা বলে থাকে?');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6199,107,2,'সেতো সে, যে পিতৃহীনকে রূঢ়ভাবে তাড়িয়ে দেয়,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6200,107,3,'এবং সে অভাবগ্রস্তকে খাদ্য দানে উৎসাহ প্রদান করেনা,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6201,107,4,'সুতরাং পরিতাপ সেই সালাত আদায়কারীদের জন্য –');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6202,107,5,'যারা তাদের সালাতে অমনোযোগী,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6203,107,6,'যারা লোক দেখানোর জন্য ওটা করে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6204,107,7,'এবং গৃহস্থালীর প্রয়োজনীয় ছোট খাট সাহায্য দানে বিরত থাকে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6205,108,1,'আমি অবশ্যই তোমাকে কাওছার দান করেছি,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6206,108,2,'সুতরাং তোমার রবের উদ্দেশে সালাত আদায় কর এবং কুরবানী কর।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6207,108,3,'নিশ্চয়ই তোমার প্রতি বিদ্বেষ পোষণকারীইতো নিবংর্শ।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6208,109,1,'বলঃ হে কাফিরেরা!');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6209,109,2,'আমি তার ইবাদাত করিনা যার ইবাদাত তোমরা কর,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6210,109,3,'এবং তোমরাও তাঁর ইবাদাতকারী নও যাঁর ইবাদাত আমি করি,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6211,109,4,'এবং আমি ইবাদাতকারী নই তার যার ইবাদাত তোমরা করে আসছ,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6212,109,5,'এবং তোমরা তাঁর ইবাদাতকারী নও যাঁর ইবাদাত আমি করি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6213,109,6,'তোমাদের জন্য তোমাদের কর্মফল এবং আমার জন্য আমার কর্মফল।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6214,110,1,'যখন আসবে আল্লাহর সাহায্য ও বিজয়,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6215,110,2,'এবং তুমি মানুষকে দলে দলে আল্লাহর দীনে প্রবেশ করতে দেখবে,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6216,110,3,'তখন তুমি তোমার রবের কৃতজ্ঞতা মূলক পবিত্রতা ও মহিমা ঘোষণা কর এবং তাঁর সমীপে ক্ষমা প্রার্থনা কর; তিনিতো সর্বাপেক্ষা অধিক অনুতাপ গ্রহণকারী।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6217,111,1,'ধ্বংস হোক আবু লাহাবের হস্তদ্বয় এবং ধ্বংস হোক সে নিজেও।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6218,111,2,'ওর ধন সম্পদ ও ওর উপার্জন ওর কোন উপকারে আসেনি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6219,111,3,'অচিরেই সে শিখা বিশিষ্ট জাহান্নামের আগুনে প্রবেশ করবে,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6220,111,4,'এবং তার স্ত্রীও-যে ইন্ধন বহন করে। ');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6221,111,5,'তার গলদেশে খেজুর বাকলের রজ্জু রয়েছে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6222,112,1,'বলঃ তিনিই আল্লাহ, একক/অদ্বিতীয়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6223,112,2,'আল্লাহ কারও মুখাপেক্ষী নন।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6224,112,3,'তাঁর কোন সন্তান নেই এবং তিনিও কারও সন্তান নন,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6225,112,4,'এবং তাঁর সমতুল্য কেহই নেই।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6226,113,1,'বলঃ আমি আশ্রয় চাচ্ছি ঊষার স্রষ্টার।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6227,113,2,'তিনি যা সৃষ্টি করেছেন উহার অনিষ্টতা হতে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6228,113,3,'অনিষ্টতা হতে রাতের, যখন ওটা অন্ধকারাচ্ছন্ন হয়।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6229,113,4,'এবং ঐ সব নারীর অনিষ্টতা হতে যারা গ্রন্থিতে ফুৎকার দেয়,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6230,113,5,'এবং অনিষ্টতা হতে হিংসুকের, যখন সে হিংসা করে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6231,114,1,'বলঃ আমি আশ্রয় চাচ্ছি মানুষের রবের,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6232,114,2,'যিনি মানবমন্ডলীর বাদশাহ বা অধিপতি।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6233,114,3,'যিনি মানবমন্ডলীর উপাস্য।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6234,114,4,'আত্মগোপনকারী কু-মন্ত্রণাদাতার অনিষ্টতা হতে।');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6235,114,5,'যে কু-মন্ত্রণা দেয় মানুষের অন্তরে,');
INSERT INTO `bn_mujibur` (index,sura,aya,text) VALUES (6236,114,6,'জিনের মধ্য হতে অথবা মানুষের মধ্য হতে।');
