INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1,1,1,'(আরম্ভ করছি) পরম করুণাময় অসীম দয়াময় আল্লাহর নামে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2,1,2,'যাবতীয় প্রশংসা জগৎসমূহের প্রতিপালক আল্লাহরই জন্য। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3,1,3,'যিনি পরম করুণাময় অতি দয়ালূ।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4,1,4,'যিনি প্রতিফল দিবসের মালিক। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5,1,5,'আমরা কেবল তোমারই ‘ইবাদাত করি এবং কেবলমাত্র তোমারই সাহায্য প্রার্থনা করি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6,1,6,'আমাদেরকে সরল সঠিক পথ প্রদর্শন কর ও তার প্রতি অটুট থাকার তাওফীক দান কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (7,1,7,'তাদের পথ, যাদের প্রতি তুমি অনুগ্রহ করেছ। তাদের পথ, যারা গযবপ্রাপ্ত (ইয়াহূদী) ও পথভ্রষ্ট (খ্রিস্টান) নয়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (8,2,1,'আলিফ, লাম, মীম।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (9,2,2,'এটা ঐ (মহান) কিতাব যাতে কোন সন্দেহ নেই, মুত্তাকীদের জন্য পথ নির্দেশ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (10,2,3,'যারা গায়বের প্রতি ঈমান আনে, নামায কায়িম করে এবং আমি যে জীবনোপকরণ তাদেরকে দিয়েছি তাথেকে তারা ব্যয় করে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (11,2,4,'আর তোমার প্রতি যা নাযিল হয়েছে ও তোমার পূর্বে যা নাযিল হয়েছে তাতে তারা বিশ্বাস স্থাপন করে এবং পরকালের প্রতিও তারা নিশ্চিত বিশ্বাসী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (12,2,5,'তারাই তাদের প্রতিপালকের হিদায়াতের উপর প্রতিষ্ঠিত আছে, আর তারাই সফলকাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (13,2,6,'নিশ্চয় যারা কুফরী করেছে তাদেরকে তুমি ভয় দেখাও আর না দেখাও উভয়টাই তাদের জন্য সমান, তারা ঈমান আনবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (14,2,7,'আল্লাহ তাদের অন্তর ও কানের উপর মোহর করে দিয়েছেন, আর তাদের চোখে আছে আবরণ আর তাদের জন্য আছে মহা শাস্তি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (15,2,8,'মানুষের মধ্যে এমন লোক আছে যারা বলে, ‘‘আমরা আল্লাহর প্রতি এবং আখেরাতের দিনের প্রতি ঈমান এনেছি’’ কিন্তু প্রকৃতপক্ষে তারা মু’মিন নয়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (16,2,9,'তারা আল্লাহ ও মু’মিনদেরকে প্রতারিত করে, আসলে তারা নিজেদেরকে ছাড়া অন্য কাউকে প্রতারিত করে না, কিন্তু এটা তারা উপলব্ধি করতে পারে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (17,2,10,'তাদের অন্তরে আছে ব্যাধি, অতঃপর আল্লাহ তাদের ব্যাধি বাড়িয়ে দিয়েছেন আর তাদের জন্য রয়েছে যন্ত্রণাদায়ক শাস্তি, কারণ তারা মিথ্যেবাদী।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (18,2,11,'তাদেরকে যখন বলা হয়, ‘পৃথিবীতে ফাসাদ সৃষ্টি করো না’; তারা বলে, ‘আমরা তো সংশোধনকারী’। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (19,2,12,'মূলতঃ তারাই অশান্তি সৃষ্টিকারী, কিন্তু তারা তা বুঝতে পারে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (20,2,13,'যখন তাদেরকে বলা হয়, যে সব লোক ঈমান এনেছে তাদের মতো তোমরাও ঈমান আন, তারা বলে, ‘নির্বোধেরা যেমন ঈমান এনেছে, আমরাও কি তেমনি ঈমান আনব’? আসলে তারাই নির্বোধ, কিন্তু তারা তা’ বুঝতে পারে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (21,2,14,'যখন তারা মু’মিনদের সংস্পর্শে আসে তখন বলে, ‘আমরা ঈমান এনেছি’; আর যখন তারা নিভৃতে তাদের শয়ত্বানদের (সর্দারদের) সঙ্গে মিলিত হয় তখন বলে, ‘আমরা তোমাদের সাথেই আছি, আমরা শুধু তাদের সঙ্গে ঠাট্টা-তামাশা করি মাত্র’। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (22,2,15,'আল্লাহ তাদের সঙ্গে (জবাবে) উপহাস করেন এবং তাদের অবাধ্যতায় বিভ্রান্তের মত তাদেরকে ঘুরে মরার অবকাশ দেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (23,2,16,'তারাই হিদায়াতের বিনিময়ে গোমরাহী ক্রয় করেছে, কিন্তু তাতে তাদের ব্যবসা লাভজনক হয়নি, আর তারা সৎপথপ্রাপ্তও নয়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (24,2,17,'তাদের উদাহরণ, যেমন এক ব্যক্তি আগুন জ্বালালো, তা যখন তার চারদিক আলোকিত করল আল্লাহ তখন তাদের জ্যোতি অপসারণ করে দিলেন এবং তাদেরকে এমন ঘন অন্ধকারে ফেলে দিলেন যে, তারা কিছুই দেখতে পায় না।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (25,2,18,'তারা বধির, মূক, অন্ধ; কাজেই তারা (হিদায়াতের দিকে) ফিরে আসবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (26,2,19,'অথবা যেমন আকাশের বর্ষণমুখর মেঘ, যাতে আছে গাঢ় অন্ধকার, বজ্রধ্বনি ও বিদ্যুৎ চমক, বজ্রধ্বনিতে মৃত্যু ভয়ে তারা তাদের কানে আঙ্গুল ঢুকিয়ে দেয়। আল্লাহ কাফেরদেরকে পরিবেষ্টন করে আছেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (27,2,20,'বিদ্যুৎচমক তাদের দৃষ্টিশক্তি প্রায় কেড়ে নেয়, যখনই বিদ্যুৎচমক তাদের সামনে প্রকাশিত হয়, তখনই তারা পথ চলতে থাকে এবং যখন তাদের উপর অন্ধকার ছেয়ে যায়, তখন তারা থমকে দাঁড়ায়, আল্লাহ ইচ্ছে করলে তাদের শ্রবণ ও দৃষ্টি শক্তি হরণ করতে পারতেন, আল্লাহ সকল বিষয়ে সর্বশক্তিমান। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (28,2,21,'হে মানুষ! ‘তোমরা তোমাদের সেই প্রতিপালকের ইবাদাত কর যিনি তোমাদেরকে ও তোমাদের পূর্ববর্তীদেরকে সৃষ্টি করেছেন, যাতে তোমরা মুত্তাকী (পরহেযহার) হতে পার’। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (29,2,22,'যিনি তোমাদের জন্য পৃথিবীকে বিছানা ও আকাশকে ছাদ করেছেন এবং আকাশ হতে পানি বর্ষণ ক’রে তদ্বারা তোমাদের জীবিকার জন্য ফলমূল উৎপাদন করেন, কাজেই জেনে বুঝে কাউকেও আল্লাহর সমকক্ষ দাঁড় করো না।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (30,2,23,'আমি আমার বান্দাহর প্রতি যা নাযিল করেছি তাতে তোমাদের কোন সন্দেহ থাকলে তোমরা তার মত কোন সূরাহ এনে দাও আর তোমরা যদি সত্যবাদী হও, তবে আল্লাহ ছাড়া তোমাদের সকল সাহায্যকারীকে আহবান কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (31,2,24,'যদি তোমরা না পার এবং কক্ষনো পারবেও না, তাহলে সেই আগুনকে ভয় কর, যার ইন্ধন হবে মানুষ এবং পাথর, যা প্রস্তুত রয়েছে কাফেরদের জন্য। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (32,2,25,'যারা ঈমান আনে ও সৎকর্ম করে তাদেরকে সুসংবাদ দাও যে, তাদের জন্য আছে জান্নাত, যার নিম্নদেশে নদীসমূহ প্রবাহিত। তাদেরকে যখনই ফলমূল খেতে দেয়া হবে, তখনই তারা বলবে, আমাদেরকে পূর্বে জীবিকা হিসেবে যা দেয়া হতো, এতো তারই মতো। একই রকম ফল তাদেরকে দেয়া হবে এবং সেখানে রয়েছে তাদের জন্য পবিত্র সঙ্গিণী, তারা সেখানে চিরস্থায়ী হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (33,2,26,'নিশ্চয় আল্লাহতো মশা অথবা তার চেয়েও ক্ষুদ্র কোন বস্তুর উদাহরণ দিতে লজ্জাবোধ করেন না; অতএব যারা ঈমানদার তারা জানে যে, এ সত্য তাদের প্রতিপালকের নিকট থেকে এসেছে, কিন্তু যারা অবিশ্বাসী তারা বলে যে, আল্লাহ কী উদ্দেশ্যে এ উদাহরণ পেশ করেছেন? (আসল ব্যাপার হল) তিনি এর দ্বারা অনেককেই বিভ্রান্ত করেন, আবার অনেককেই সৎপথে পরিচালিত করেন। বস্তুতঃ তিনি ফাসিকদের ছাড়া আর কাউকেও বিভ্রান্ত করেন না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (34,2,27,'যারা আল্লাহর সঙ্গে সুদৃঢ় অঙ্গীকার করার পর তা ভঙ্গ করে এবং যে সম্পর্ক অক্ষুণ্ণ রাখার নির্দেশ আল্লাহ করেছেন, তা ছিন্ন করে এবং পৃথিবীতে অশান্তি সৃষ্টি করে, তারাই ক্ষতিগ্রস্ত।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (35,2,28,'তোমরা আল্লাহকে কীভাবে অস্বীকার করছ? অথচ তোমরা ছিলে মৃত, অতঃপর তিনি তোমাদেরকে জীবন্ত করেছেন, আবার তোমাদের মৃত্যু ঘটাবেন, তারপর আবার জীবিত করবেন, তারপর তাঁর দিকেই তোমরা ফিরে যাবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (36,2,29,'পৃথিবীর সবকিছু তিনি তোমাদের জন্য সৃষ্টি করেছেন, তারপর তিনি আকাশের দিকে মনোসংযোগ করেন এবং তা সপ্তাকাশে বিন্যস্ত করেন, তিনি সকল বিষয়ে বিশেষভাবে অবহিত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (37,2,30,'স্মরণ কর, তোমার প্রতিপালক যখন ফেরেশতাদেরকে বললেন, ‘আমি যমীনে প্রতিনিধি সৃষ্টি করছি’; তারা বলল, ‘আপনি কি সেখানে এমন কাউকেও পয়দা করবেন যে অশান্তি সৃষ্টি করবে ও রক্তপাত ঘটাবে? আমরাই তো আপনার প্রশংসামূলক স্তুতিগান ও পবিত্রতা ঘোষণা করি’। তিনি বললেন, ‘আমি যা জানি, তোমরা তা জান না’। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (38,2,31,'এবং তিনি আদাম (আ.)-কে সকল বস্তুর নাম শিক্ষা দিলেন, তারপর সেগুলো ফেরেশতাদের সামনে উপস্থাপন করলেন এবং বললেন, ‘এ বস্তুগুলোর নাম আমাকে বলে দাও, যদি তোমরা সত্যবাদী হও’। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (39,2,32,'তারা বলল, ‘আপনি পবিত্র মহান, আপনি আমাদেরকে যা শিক্ষা দিয়েছেন, তাছাড়া আমাদের কোন জ্ঞানই নেই, নিশ্চয়ই আপনি সর্বজ্ঞ ও প্রজ্ঞাময়’। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (40,2,33,'তিনি নির্দেশ করলেন, ‘হে আদাম! এ জিনিসগুলোর নাম তাদেরকে জানিয়ে দাও’। যখন সে এ সকল নাম তাদেরকে বলে দিল, তখন তিনি বললেন, ‘আমি কি তোমাদেরকে বলিনি যে, নভোমন্ডল ও ভূমন্ডলের অদৃশ্য বস্তু সম্পর্কে আমি নিশ্চিতভাবে অবহিত এবং তোমরা যা প্রকাশ কর ও গোপন কর, আমি তাও অবগত’? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (41,2,34,'যখন আমি ফেরেশতাদেরকে বললাম, আদামকে সাজদাহ কর, তখন ইবলীস ছাড়া সকলেই সাজদাহ করল, সে অমান্য করল ও অহঙ্কার করল, কাজেই সে কাফিরদের অন্তর্ভুক্ত হয়ে গেল।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (42,2,35,'আমি বললাম, ‘হে আদাম! তুমি ও তোমার স্ত্রী জান্নাতে বসবাস কর এবং যেখানে যা ইচ্ছে খাও, কিন্তু এই গাছের নিকটে যেয়ো না, গেলে তোমরা সীমালঙ্ঘনকারীদের মধ্যে শামিল হবে’। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (43,2,36,'কিন্তু শয়ত্বান তাত্থেকে তাদের পদস্খলন ঘটাল এবং তারা দু’জন যেখানে ছিল, তাদেরকে সেখান থেকে বের করে দিল; আমি বললাম, ‘নেমে যাও, তোমরা পরস্পর পরস্পরের শত্রু, দুনিয়াতে কিছু কালের জন্য তোমাদের বসবাস ও জীবিকা আছে’। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (44,2,37,'তারপর আদাম (আ.) তার প্রতিপালকের নিকট হতে কিছু বাণী প্রাপ্ত হল, অতঃপর আল্লাহ তার প্রতি ক্ষমা প্রদর্শন করলেন, তিনি অত্যন্ত ক্ষমাশীল, পরম দয়ালু। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (45,2,38,'আমি বললাম, ‘তোমরা সকলেই এখান হতে নেমে যাও, পরে যখন আমার নিকট হতে তোমাদের কাছে সৎপথের নির্দেশ আসবে তখন যারা আমার সৎপথের অনুসরণ করবে তাদের কোন ভয় নেই এবং তারা দুঃখিতও হবে না’। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (46,2,39,'আর যারা কুফরী করবে ও আমার নিদর্শনগুলোকে অস্বীকার করবে, তারাই জাহান্নামী; সেখানে তারা চিরকাল থাকবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (47,2,40,'হে বানী ইসরাঈল! আমার সেই অনুগ্রহকে স্মরণ কর, যদ্বারা আমি তোমাদেরকে অনুগৃহীত করেছি এবং আমার সঙ্গে তোমাদের অঙ্গীকার পূর্ণ কর, আমিও তোমাদের সঙ্গে আমার অঙ্গীকার পূর্ণ করব এবং তোমরা কেবল আমাকেই ভয় কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (48,2,41,'আর তোমরা ঈমান আন সেই কিতাবের প্রতি, যা তোমাদের নিকট আছে তার প্রত্যয়নকারী এবং তোমরাই তার প্রথম প্রত্যাখ্যানকারী হয়ো না এবং আমার আয়াতের বিনিময়ে সামান্য মূল্য গ্রহণ করো না, তোমরা কেবল আমাকেই ভয় কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (49,2,42,'তোমরা সত্যকে মিথ্যের সাথে মিশ্রিত করো না এবং জেনে শুনে সত্য গোপন করো না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (50,2,43,'তোমরা নামায কায়িম কর, যাকাত দাও এবং রুকূ‘কারীদের সঙ্গে রুকূ‘ কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (51,2,44,'তোমরা কি মানুষকে সৎকর্মের নির্দেশ দিবে এবং নিজেদের কথা ভুলে যাবে, অথচ তোমরা কিতাব পাঠ কর, তবে কি তোমরা বুঝ না? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (52,2,45,'তোমরা ধৈর্য্য ও সলাতের মাধ্যমে সাহায্য প্রার্থনা কর, আর তা আল্লাহভীরু ব্যক্তিবর্গ ছাড়া অন্য সকলের কাছে নিশ্চিতভাবে কঠিন।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (53,2,46,'যারা নিশ্চিত বিশ্বাস করে যে, তাদের প্রতিপালকের সঙ্গে তাদের সাক্ষাৎ ঘটবেই এবং তাঁরই দিকে তারা ফিরে যাবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (54,2,47,'হে বানী ইসরাঈল! আমার সেই অনুগ্রহকে স্মরণ কর যদ্বারা আমি তোমাদেরকে অনুগৃহীত করেছিলাম এবং পৃথিবীতে সকলের উপরে তোমাদেরকে শ্রেষ্ঠত্ব দিয়েছিলাম।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (55,2,48,'তোমরা সেদিনকে ভয় কর যেদিন কেউ কারো উপকারে আসবে না এবং কারও সুপারিশ গৃহীত হবে না এবং কারও নিকট থেকে ক্ষতিপূরণ গ্রহণ করা হবে না আর তারা কোন রকম সাহায্যও পাবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (56,2,49,'স্মরণ কর, আমি যখন তোমাদেরকে ফেরাউন গোষ্ঠী হতে মুক্তি দিয়েছিলাম, যারা তোমাদের পুত্র সন্তানকে হত্যা ক’রে আর তোমাদের নারীদেরকে জীবিত রেখে তোমাদেরকে মর্মান্তিক যাতনা দিত আর এতে তোমাদের প্রভুর পক্ষ হতে ছিল মহাপরীক্ষা। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (57,2,50,'স্মরণ কর, যখন তোমাদের জন্য সাগরকে বিভক্ত করেছিলাম এবং তোমাদেরকে উদ্ধার করেছিলাম এবং ফেরাউন গোষ্ঠীকে নিমজ্জিত করেছিলাম আর তোমরা তা চেয়ে চেয়ে দেখছিলে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (58,2,51,'যখন মূসার উপর চল্লিশ রজনীর ওয়া‘দা করেছিলাম (কিতাব প্রদানের জন্য), তার (প্রস্থানের) পর তোমরা তখন বাছুরকে উপাস্যরূপে গ্রহণ করেছিলে, বস্তুতঃ তোমরা তো ছিলে যালিম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (59,2,52,'এরপরও আমি তোমাদেরকে ক্ষমা করেছি যাতে তোমরা কৃতজ্ঞতা প্রকাশ কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (60,2,53,'স্মরণ কর, যখন আমি মূসাকে কিতাব ও ফুরকান দিয়েছিলাম, যাতে তোমরা সৎপথ অবলম্বন কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (61,2,54,'স্মরণ কর, মূসা যখন আপন কওমের লোককে বলল, ‘হে আমার কওম! বাছুরকে উপাস্যরূপে গ্রহণ ক’রে তোমরা নিজেদের প্রতি কঠিন অত্যাচার করেছ, কাজেই তোমরা তোমাদের সৃষ্টিকর্তার নিকট তাওবাহ কর এবং তোমরা নিজেদেরকে (নিরপরাধীরা অপরাধীদেরকে) হত্যা কর, তোমাদের স্রষ্টার নিকট এটাই শ্রেয়, অতঃপর তিনি তোমাদের প্রতি ক্ষমা প্রদর্শন করলেন, তিনি অত্যন্ত ক্ষমাশীল, পরম দয়ালু।’');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (62,2,55,'স্মরণ কর, যখন তোমরা বলেছিলে, ‘হে মূসা! আমরা আল্লাহ্কে সরাসরি না দেখা পর্যন্ত তোমাকে কক্ষনো বিশ্বাস করব না’। তখন বজ্র তোমাদেরকে পাকড়াও করেছিল আর তোমরা নিজেরাই তা প্রত্যক্ষ করছিলে।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (63,2,56,'অতঃপর তোমাদের মৃত্যুর পর আমি তোমাদেরকে আবার জীবিত করলাম, যাতে তোমরা কৃতজ্ঞতা প্রকাশ কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (64,2,57,'আমি মেঘ দ্বারা তোমাদের উপর ছায়া বিস্তার করলাম, তোমাদের কাছে মান্না ও সালওয়া প্রেরণ করলাম, (আর বললাম)তোমাদেরকে যা দান করেছি তাথেকে বৈধ বস্তুগুলো খাও, আর মূলত তারা আমার প্রতি কোন যুলম করেনি, বরং তারা নিজেদের প্রতিই যুলম করেছিল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (65,2,58,'স্মরণ কর, যখন আমি বললাম, এ জনপদে প্রবেশ কর, সেখানে যা ইচ্ছে স্বচ্ছন্দে আহার কর, দ্বার দিয়ে নতশিরে প্রবেশ কর এবং বল; ‘ক্ষমা চাই’। আমি তোমাদের অপরাধ ক্ষমা করব এবং সৎকর্মশীলদের প্রতি আমার দান বৃদ্ধি করব।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (66,2,59,'কিন্তু যারা অত্যাচার করেছিল তারা তাদেরকে যা বলা হয়েছিল তার পরিবর্তে অন্য কথা বলল, কাজেই যালিমদের প্রতি আমি আকাশ হতে শাস্তি প্রেরণ করলাম, কারণ তারা সত্য ত্যাগ করেছিল।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (67,2,60,'স্মরণ কর, যখন মূসা (আ.) তার কওমের জন্য পানি প্রার্থনা করল, আমি বললাম, ‘তোমার লাঠি দ্বারা পাথরে আঘাত কর’। ফলে তাথেকে বারটি ঝর্ণা প্রবাহিত হল, প্রত্যেক গোত্র নিজ নিজ পানের জায়গা চিনে নিল,(বললাম) ‘আল্লাহ প্রদত্ত রিয্ক হতে তোমরা পানাহার কর এবং দুষ্কৃতিকারীর মত পৃথিবীতে ফাসাদ সৃষ্টি করো না’। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (68,2,61,'স্মরণ কর, যখন তোমরা বলেছিলে, ‘হে মূসা! আমরা একই রকম খাদ্যে কক্ষনো ধৈর্য ধারণ করব না, কাজেই তুমি তোমার প্রতিপালকের কাছে আমাদের জন্য দু‘আ কর, তিনি যেন ভূমি হতে উৎপাদিত দ্রব্য শাক-সব্জি, কাঁকড়, গম, মসুর ও পেঁয়াজ আমাদের জন্য উৎপাদন করেন’। মূসা বললঃ ‘তোমরা কি উৎকৃষ্ট বস্তুকে নিকৃষ্ট বস্তুর সঙ্গে বদল করতে চাও, তবে কোন নগরে প্রবেশ কর; তোমরা যা চাও তা সেখানে আছে’ এবং তাদেরকে লাঞ্ছনা ও দারিদ্রের কশাঘাত করা হল ও তারা আল্লাহর কোপে পতিত হল। তা এজন্য যে, তারা আল্লাহর নিদর্শনকে অস্বীকার করত এবং নাবীগণকে অন্যায়ভাবে হত্যা করত। অবাধ্যতা ও সীমালঙ্ঘন করার জন্যই তাদের এ পরিণতি হয়েছিল।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (69,2,62,'নিশ্চয় যারা ঈমান এনেছে, যারা ইয়াহূদী হয়েছে এবং খৃষ্টান ও সাবিঈন- যারাই আল্লাহ ও শেষ দিবসে বিশ্বাস করে ও সৎকাজ করে, তাদের জন্য পুরস্কার তাদের প্রতিপালকের নিকট আছে, তাদের কোন ভয় নেই এবং তারা দুঃখিত হবে না।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (70,2,63,'নিশ্চয় যারা ঈমান এনেছে, যারা ইয়াহূদী হয়েছে এবং খৃষ্টান ও সাবিঈন- যারাই আল্লাহ ও শেষ দিবসে বিশ্বাস করে ও সৎকাজ করে, তাদের জন্য পুরস্কার তাদের প্রতিপালকের নিকট আছে, তাদের কোন ভয় নেই এবং তারা দুঃখিত হবে না।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (71,2,64,'এরপরেও তোমরা মুখ ফিরিয়ে নিলে; আল্লাহর অনুগ্রহ ও অনুকম্পা তোমাদের প্রতি না থাকলে তোমরা অবশ্যই ক্ষতিগ্রস্তদের পর্যায়ভুক্ত হয়ে যেতে।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (72,2,65,'তোমাদের মধ্যে যারা শনিবার সম্পর্কে সীমালঙ্ঘন করেছিল, তাদেরকে তোমরা অবশ্যই জান, আমি তাদেরকে বলেছিলাম, ‘তোমরা ঘৃণিত বানরে পরিণত হও’।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (73,2,66,'আমি তা তাদের সমসাময়িক ও পরবর্তীগণের শিক্ষা গ্রহণের জন্য দৃষ্টান্ত এবং মুত্তাকীদের জন্য উপদেশ স্বরূপ করেছি।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (74,2,67,'স্মরণ কর, যখন মূসা (আ.) স্বীয় সম্প্রদায়কে বলেছিল, ‘আল্লাহ তোমাদেরকে একটি গরু যবহ করার আদেশ দিচ্ছেন’; তারা বলেছিল, ‘তুমি কি আমাদের সঙ্গে ঠাট্টা করছো’? মূসা বলল, আল্লাহর আশ্রয় নিচ্ছি, যাতে আমি অজ্ঞদের অন্তর্ভুক্ত না হই। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (75,2,68,'তারা বলল, ‘আমাদের জন্য তোমার প্রতিপালককে স্পষ্টভাবে জানিয়ে দিতে বল তা কীরূপ’? মূসা বলল, ‘আল্লাহ বলছেন, তা এমন এক গরু যা বৃদ্ধও নয় এবং অল্প বয়স্কও নয়- মধ্য বয়সী। সুতরাং যা (করতে) আদিষ্ট হয়েছ, তা পালন কর’।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (76,2,69,'তারা বলল, ‘আমাদের জন্য তোমার প্রতিপালককে স্পষ্টভাবে জানিয়ে দিতে বল, ওর রং কী’? মূসা বলল, ‘আল্লাহ বলছেন, তা হলুদ বর্ণের গরু, তার রং উজ্জ্বল গাঢ়, যা দর্শকদেরকে আনন্দ দেয়’।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (77,2,70,'তারা বলল, ‘আমাদের জন্য তোমার প্রতিপালককে স্পষ্টভাবে জানিয়ে দিতে বল গরুটি কেমন? কারণ সব গরু আমাদের কাছে সমান, আর আল্লাহ ইচ্ছা করলে নিশ্চয় আমরা পথের দিশা পাব।’');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (78,2,71,'মূসা বলল, ‘তিনি বলছেন, তা এমন এক গরু যা জমি চাষে ও ক্ষেতে পানি সেচের জন্য ব্যবহৃত হয়নি বরং সুস্থ ও নিখুঁত’। তারা বলল, ‘এখন তুমি সত্য প্রকাশ করেছ’। তারা তাকে যবহ করল যদিও তাদের জন্য সেটা প্রায় অসম্ভব ছিল।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (79,2,72,'স্মরণ কর, তোমরা যখন এক ব্যক্তিকে হত্যা করেছিলে এবং একে অন্যের প্রতি দোষারোপ করছিলে, তোমরা যা গোপন করছিলে আল্লাহ তা প্রকাশ করে দিলেন।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (80,2,73,'আমি বললাম, ‘তার (অর্থাৎ যবহকৃত গরুর) কোন অংশ দ্বারা একে আঘাত কর’। এভাবে আল্লাহ মৃতকে জীবন দান করেন, আর তোমাদেরকে তাঁর নিদর্শন দেখিয়ে থাকেন যাতে তোমরা জ্ঞানলাভ করতে পার।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (81,2,74,'এরপরও তোমাদের হৃদয় কঠিন হয়ে গেল, তা পাথর কিংবা তদপেক্ষা কঠিন। কতক পাথরও এমন আছে যে তা হতে ঝর্ণাধারা প্রবাহিত হয় এবং কতক এরূপ যে, ফেটে যাওয়ার পর তা হতে পানি নির্গত হয়। আবার কতক এমন যা আল্লাহর ভয়ে ধ্বসে পড়ে এবং তোমরা যা কর আল্লাহ সে সম্বন্ধে বেখেয়াল নন।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (82,2,75,'তোমরা কি এই আশা কর যে, তারা তোমাদের কথায় ঈমান আনবে? অথচ তাদের একদল আল্লাহর বাণী শ্রবণ করত ও বুঝার পর জেনে শুনে তা বিকৃত করত।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (83,2,76,'যখন তারা মু’মিনদের সংস্পর্শে আসে তখন বলে, ‘আমরা বিশ্বাস স্থাপন করেছি’। আবার যখন তারা নিভৃতে একে অন্যের সঙ্গে মিলিত হয় তখন বলে, ‘আল্লাহ তোমাদের কাছে যা (তাওরাতে) ব্যক্ত করেছেন [মুহাম্মাদ (সা.) সম্পর্কে] তোমরা কি তা তাদেরকে বলে দাও যাতে এর দ্বারা তারা তোমাদের প্রতিপালকের সম্মুখে তোমাদের বিরুদ্ধে যুক্তি পেশ করবে? তোমরা কি বুঝ না’? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (84,2,77,'তাদের কি জানা নেই যে, যা তারা গোপন রাখে অথবা প্রকাশ করে অবশ্যই আল্লাহ তা জানেন?');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (85,2,78,'তাদের মাঝে এমন কিছু নিরক্ষর লোক আছে, যাদের মিথ্যা আকাঙ্ক্ষা ছাড়া কিতাবের কোন জ্ঞানই নেই, তারা কেবল অলীক ধারণা পোষণ করে।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (86,2,79,'সুতরাং অভিসম্পাত তাদের জন্য যারা নিজ হাতে কিতাব রচনা করে এবং নিকৃষ্ট মূল্য লাভের জন্য বলে এটা আল্লাহর নিকট হতে, তাদের হাত যা রচনা করেছে তার জন্য তাদের শাস্তি অবধারিত এবং তারা যা উপার্জন করে তার জন্যও শাস্তি রয়েছে।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (87,2,80,'তারা বলে, ‘গুটি কয়েক দিন ছাড়া আগুন কক্ষনো আমাদেরকে স্পর্শ করবে না’। বল, ‘তোমরা কি আল্লাহর নিকট হতে প্রতিশ্রুতি নিয়েছো যে প্রতিশ্রুতি আল্লাহ ভঙ্গ করবেন না? কিংবা আল্লাহ সম্পর্কে এমন কিছু বলছ, যা তোমরা জান না’।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (88,2,81,'তবে হাঁ, যারা পাপ কাজ করে এবং যাদের পাপরাশি তাদেরকে ঘিরে ফেলে তারাই জাহান্নামী, সেখানে তারা চিরকাল থাকবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (89,2,82,'আর যারা ঈমান আনে ও নেক ‘আমাল করে তারাই জান্নাতবাসী, তারা সেখানে চিরস্থায়ী হবে।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (90,2,83,'আর স্মরণ কর, যখন বানী ইসরাঈলের শপথ নিয়েছিলাম যে, তোমরা আল্লাহ ছাড়া অন্য কারো ‘ইবাদাত করবে না, মাতা-পিতা, আত্মীয়-স্বজন, অনাথ ও দরিদ্রদের প্রতি সদয় ব্যবহার করবে এবং মানুষের সাথে সদালাপ করবে, নামায কায়িম করবে এবং যাকাত দিবে। কিন্তু অল্প সংখ্যক লোক ছাড়া তোমরা অগ্রাহ্যকারী হয়ে মুখ ফিরিয়ে নিলে।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (91,2,84,'আর যখন তোমাদের শপথ নিয়েছিলাম যে, তোমরা একে অন্যের রক্তপাত করবে না এবং স্বজনদেরকে স্বদেশ হতে বহিষ্কার করবে না, তারপর তোমরা তা স্বীকার করেছিলে আর এ বিষয়ে তোমরাই সাক্ষী।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (92,2,85,'পরন্ত তোমরাই সেই লোক যারা পরস্পর নিজেদেরকে হত্যা করছ এবং তোমাদের একদলকে স্বদেশ থেকে বহিষ্কার করছ, তোমরা নিজেরা তাদের বিরুদ্ধে অন্যায় ও সীমালঙ্ঘন করে পরস্পরের সহযোগিতা করছ এবং তারা যখন বন্দীরূপে তোমাদের নিকট হাজির হয়, তখন তোমরা মুক্তিপণ আদায় কর, মূলতঃ তাদের বহিষ্করণই তোমাদের জন্য ছিল নিষিদ্ধ; তাহলে কি তোমরা কিতাবের কিছু অংশকে বিশ্বাস কর এবং কিছু অংশকে প্রত্যাখ্যান কর? অতএব তোমাদের যারা এমন করে তাদের পার্থিব জগতে লাঞ্ছনা ও অবমাননা ছাড়া আর কী প্রতিদান হতে পারে? এবং ক্বিয়ামাতের দিন তারা কঠিন শাস্তির মধ্যে নিক্ষিপ্ত হবে, আর তারা যা করে সে সম্পর্কে আল্লাহ গাফেল নন।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (93,2,86,'তারাই পরকালের বিনিময়ে পার্থিব জীবন ক্রয় করে। কাজেই তাদের শাস্তি কম করা হবে না এবং তারা সাহায্যপ্রাপ্তও হবে না।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (94,2,87,'এবং নিশ্চয়ই আমি মূসাকে কিতাব দিয়েছি এবং তার পরে ক্রমান্বয়ে রসূলদেরকে প্রেরণ করেছি, মারইয়াম পুত্র ঈসাকে সুস্পষ্ট প্রমাণ দিয়েছি এবং ‘পবিত্র আত্মা’যোগে (জিবরাঈলের মাধ্যমে) তাকে শক্তিশালী করেছি, অতঃপর যখনই কোন রসূল এমন কিছু এনেছে যা তোমাদের মনঃপুত নয়, তখনই তোমরা অহংকার করেছ এবং কিছু সংখ্যককে অস্বীকার করেছ এবং কিছু সংখ্যককে হত্যা করেছ।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (95,2,88,'তারা দাবী করেছিল যে, ‘আমাদের হৃদয় আচ্ছাদিত’, বরং কুফরী করার কারণে আল্লাহ তাদেরকে অভিসম্পাত করেছেন, অতএব তাদের অল্প সংখ্যকই ঈমান আনে।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (96,2,89,'তাদের কাছে যা আছে, তার সমর্থক কিতাব যখন আল্লাহর নিকট থেকে আসল, যদিও পূর্বে কাফিরদের বিরুদ্ধে তারা এর সাহায্যে বিজয় প্রার্থনা করত, আর সেটাকে তারা চিনতেও পারল, তবুও যখন তা তাদের নিকট আসল, তখন তারা তা অবিশ্বাস করল; সুতরাং অবিশ্বাসকারীদের প্রতি আল্লাহর অভিসম্পাত।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (97,2,90,'তা কতই না নিকৃষ্ট যার বিনিময়ে তারা নিজেদের আত্মাকে বিক্রি করেছে, তা এই যে, আল্লাহ যা অবতীর্ণ করেছেন, জিদের বশে তারা তা প্রত্যাখ্যান করত শুধু এজন্য যে, আল্লাহ তাঁর বান্দাহদের মধ্য হতে যাকে ইচ্ছে অনুগ্রহ করেন, অতএব তারা ক্রোধের উপর ক্রোধের পাত্র হল এবং কাফিরদের জন্যই লাঞ্ছনাদায়ক শাস্তি রয়েছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (98,2,91,'আর যখন তাদেরকে বলা হয়, ‘আল্লাহ যা অবতীর্ণ করেছেন তাতে ঈমান আন’; তারা বলে, ‘আমাদের প্রতি যা অবতীর্ণ হয়েছে তাতে আমরা বিশ্বাস করি’। অথচ পরবর্তী কিতাবকে (কুরআনকে) তারা প্রত্যাখ্যান করে, যদিও তা সত্য এবং যা তাদের নিকট আছে তার সমর্থক। বল, ‘যদি তোমরা বিশ্বাসী ছিলে তবে কেন তোমরা পূর্বে নাবীদেরকে হত্যা করেছিলে’?');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (99,2,92,'এবং নিশ্চয়ই মূসা তোমাদের নিকট স্পষ্ট প্রমাণসহ এসেছে, তারপরেও তোমরা যালিম সেজে গো-বৎসকে উপাস্যরূপে গ্রহণ করেছিলে।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (100,2,93,'স্মরণ কর, যখন তোমাদের শপথ নিয়েছিলাম এবং তূর পর্বতকে তোমাদের ঊর্ধ্বে তুলেছিলাম এবং বলেছিলাম, ‘যা দিলাম তা দৃঢ়রূপে ধারণ কর এবং শ্রবণ কর’। তারা বলেছিল, আমরা শুনলাম ও অমান্য করলাম। কুফুরীর কারণে তাদের অন্তরে গো-বৎস-প্রীতি শিকড় গেড়ে বসেছিল। বল, ‘যদি তোমরা বিশ্বাসী হও, তবে তোমাদের বিশ্বাস যার নির্দেশ দেয়, তা কতই না নিকৃষ্ট’!');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (101,2,94,'বল, ‘যদি আল্লাহর নিকট পরকালের বাসস্থান অন্যলোক ছাড়া কেবলমাত্র তোমাদের জন্যই হয়, তাহলে তোমরা মৃত্যু কামনা কর, যদি সত্যবাদী হয়ে থাক’। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (102,2,95,'কিন্তু তাদের কৃতকর্মের জন্য তারা কক্ষনো তা কামনা করবে না এবং আল্লাহ যালিমদের সম্পর্কে খুবই অবহিত।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (103,2,96,'অবশ্যই তুমি তাদেরকে বেঁচে থাকার ব্যাপারে সকল মানুষ এমনকি মুশরিক অপেক্ষাও অধিক লোভী দেখতে পাবে, তাদের প্রত্যেকেই আকাঙ্ক্ষা করে যদি হাজার বছর আয়ু দেয়া হত, কিন্তু দীর্ঘায়ু তাদেরকে শাস্তি হতে রেহাই দিতে পারবে না, তারা যা করে, আল্লাহ তার দ্রষ্টা।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (104,2,97,'বল, ‘যে ব্যক্তি জিবরাঈলের শত্রু হয়েছে, (সে রাগে মরে যাক) কেননা সে তো আল্লাহর হুকুমে তোমার অন্তরে কুরআন পৌঁছিয়ে দিয়েছে, যা এর পূর্ববর্তী কিতাবের সমর্থক এবং যাতে ঈমানদারদের জন্য পথনির্দেশ ও সুসংবাদ রয়েছে’।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (105,2,98,'যে ব্যক্তি আল্লাহর, তাঁর ফেরেশতাদের ও তাঁর রসূলগণের এবং জিবরাঈলের ও মীকাইলের শত্রু সাজবে, নিশ্চয়ই আল্লাহও (এসব) কাফিরদের শত্রু।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (106,2,99,'এবং আমি তোমার নিকট সুস্পষ্ট আয়াত নাযিল করেছি, ফাসিকরা ছাড়া অন্য কেউ তা অস্বীকার করে না।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (107,2,100,'এটা কি নয় যে তারা যখনই কোন অঙ্গীকার করে, তখনই তাদের কোন না কোন দল সেই অঙ্গীকারকে বর্জন করে? বরং তাদের অধিকাংশই ঈমান রাখে না।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (108,2,101,'এবং যখন তাদের কাছে আল্লাহর পক্ষ হতে রসূল আসল যে এদের নিকট যে কিতাব রয়েছে, সেই কিতাবের সমর্থক, তখন যাদেরকে কিতাব দেয়া হয়েছিল তাদের একদল আল্লাহর কিতাবকে পিঠের পিছনে ফেলে দিল, যেন তারা কিছুই জানে না।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (109,2,102,'এবং সুলায়মানের রাজত্বকালে শয়ত্বানরা যা পাঠ করত, তারা তা অনুসরণ করত, মূলতঃ সুলায়মান কুফরী করেনি বরং শয়ত্বানরাই কুফুরী করেছিল, তারা মানুষকে যাদু শিক্ষা দিত এবং যা বাবিলের দু’জন ফেরেশতা হারূত ও মারূতের উপর পৌঁছানো হয়েছিল এবং ফেরেশতাদ্বয় কাউকেও (তা) শিখাতো না যে পর্যন্ত না বলত, আমরা পরীক্ষা স্বরূপ, কাজেই তুমি কুফরী কর না,এতদসত্ত্বেও তারা উভয়ের নিকট হতে এমন জিনিস শিক্ষা করতো, যদ্বারা তারা স্বামী-স্ত্রীর মধ্যে বিচ্ছেদ সৃষ্টি করতো, মূলতঃ তারা তাদের এ কাজ দ্বারা আল্লাহর বিনা হুকুমে কারও ক্ষতি করতে পারত না, বস্তুতঃ এরা এমন বিদ্যা শিখত, যদ্দ্বারা তাদের ক্ষতি সাধিত হত আর এদের কোন উপকার হত না এবং অবশ্যই তারা জানত যে, যে ব্যক্তি ঐ কাজ অবলম্বন করবে পরকালে তার কোনই অংশ থাকবে না, আর যার পরিবর্তে তারা স্বীয় আত্মাগুলোকে বিক্রয় করেছে, তা কতই না জঘন্য, যদি তারা জানত!');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (110,2,103,'আর যদি তারা ঈমান আনত এবং মুত্তাকী হত তবে আল্লাহর নিকট শ্রেষ্ঠতর সুফল ছিল, যদি তারা জানত! ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (111,2,104,'হে বিশ্বাসীগণ! তোমরা ‘রা‘এনা’ বলে সম্বোধন করো না, (যার অর্থ আমাদের রাখাল) বরং তোমরা বলবে ‘‘উনযুরনা’’ (অর্থাৎ আমাদের প্রতি নেকদৃষ্টি দিবেন!) এবং শুনে নাও, বস্তুতঃ অবিশ্বাসীদের জন্যই রয়েছে কষ্টদায়ক শাস্তি।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (112,2,105,'গ্রন্থধারীদের মধ্যে যারা অবিশ্বাসী তারা ও মুশরিকরা এটা চায় না যে, তোমাদের প্রতিপালকের পক্ষ হতে তোমাদের প্রতি কোন কল্যাণ অবতীর্ণ হোক অথচ আল্লাহ যাকে ইচ্ছে স্বীয় দয়ায় নির্দিষ্ট করে নেন এবং আল্লাহ মহা অনুগ্রহশীল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (113,2,106,'আমি কোন আয়াত রহিত করলে কিংবা ভুলিয়ে দিলে, তাত্থেকে উত্তম কিংবা তারই মত আয়াত নিয়ে আসি, তুমি কি জান না যে, আল্লাহ প্রত্যেক বস্তুর উপর ক্ষমতাবান। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (114,2,107,'তুমি কি জান না যে, আকাশমন্ডলী ও ভূমন্ডলের রাজত্ব সেই আল্লাহরই এবং আল্লাহ ছাড়া তোমাদের কোনও অভিভাবক নেই এবং সাহায্যকারীও নেই। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (115,2,108,'তোমরা কি তোমাদের রসূলকে তেমন প্রশ্ন করতে চাও যেমন মূসাকে প্রশ্ন করা হয়েছিল? যে ব্যক্তি ঈমানের পরিবর্তে কুফরী করে, সে ব্যক্তি অবশ্যই সরল পথ হতে বিচ্যুত হয়।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (116,2,109,'গ্রন্থধারীগণের অনেকেই তাদের কাছে সত্য স্পষ্ট হয়ে যাওয়ার পরও তাদের অন্তরে পোষিত হিংসার দাহনে ইচ্ছে পোষণ করে যে, যদি তোমাদেরকে তোমাদের ঈমান আনার পর কুফরীতে ফিরিয়ে নিতে পারত; সুতরাং তোমরা ক্ষমা কর ও মার্জনা কর যে পর্যন্ত না আল্লাহ স্বীয় হুকুম প্রকাশ করেন, নিশ্চয়ই আল্লাহ সকল বস্তুর উপর ক্ষমতাবান।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (117,2,110,'এবং তোমরা নামায কায়িম কর এবং যাকাত দাও এবং যা কিছু সৎ কার্যাবলী তোমরা স্বীয় আত্মার জন্যে আগে পাঠাবে, তোমরা তা আল্লাহর নিকট পাবে, তোমরা যা কিছু করছো নিশ্চয়ই আল্লাহ  তা’ দেখছেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (118,2,111,'তারা বলে,  ইয়াহূদী এবং  নাসারাগণ ছাড়া কেউ জান্নাতে প্রবেশ করতে পারবে না, ওটা তাদের আকাঙ্ক্ষা মাত্র। বল, ‘যদি তোমরা সত্যবাদী হও, তবে নিজেদের দলীল পেশ কর’।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (119,2,112,'বরং যে ব্যক্তি আল্লাহর কাছে আত্মসমর্পণ করে আর সৎকর্মশীল হয়, তার জন্য তার প্রতিপালকের নিকট পুণ্যফল রয়েছে, তাদের কোন ভয় নেই, তাদের কোন দুঃখ নেই।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (120,2,113,'আর ইয়াহূদীরা বলে যে, নাসারাদের মাযহাবের কোন ভিত্তি নেই; নাসারারা বলে যে, ইয়াহূদীদের মাযহাবের কোন ভিত্তি নেই, অথচ তারা কিতাব পাঠ করে, এভাবে যারা কিছু জানে না তারাও ওদের মতই বলে, যার সম্বন্ধে তারা মতবিরোধ করছে, আল্লাহ ক্বিয়ামাতের দিন তাদের মধ্যে সেই বিষয়ের সমাধান করবেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (121,2,114,'তার চেয়ে বড় যালেম কে, যে ব্যক্তি আল্লাহর মাসজিদগুলোতে আল্লাহর নাম নিতে বাধা দেয় এবং ওগুলোর ধ্বংস সাধনের চেষ্টা করে? অথচ ভয়ে ভীত না হয়ে তাদের জন্য মাসজিদে প্রবেশ সঙ্গত ছিল না, এদের জন্য দুনিয়াতে আছে লাঞ্ছনা এবং পরকালে তাদের জন্য রয়েছে কঠিন শাস্তি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (122,2,115,'পূর্ব পশ্চিম আল্লাহরই, সুতরাং তোমরা যে দিকেই মুখ কর না কেন, সেদিকেই আছে আল্লাহর চেহারা, আল্লাহ সুবিস্তৃত, সর্বজ্ঞ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (123,2,116,'তারা বলে যে, আল্লাহ সন্তান গ্রহণ করেছেন, তিনি অতি পবিত্র, বরং যা কিছু আকাশসমূহে এবং ভূ-মন্ডলে আছে সমস্তই তাঁর, সকলই তাঁর অনুগত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (124,2,117,'আল্লাহ আকাশমন্ডলী ও পৃথিবীর সৃজনকারী, যখন কোন কাজ করতে মনস্থ করেন, তখন তার জন্য শুধু বলেন, হয়ে যাও, তক্ষুনি তা হয়ে যায়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (125,2,118,'যারা কিছু জানে না তারা বলে, কেন আল্লাহ আমাদের সঙ্গে কথা বলেন না? কিংবা আমাদের নিকট কোন নির্দেশ কেন আসে না? এভাবে আগের লোকেরাও তাদের মতই বলত, এদের অন্তরগুলো একই রকম, আমি দৃঢ় বিশ্বাসীদের জন্য নিদর্শনাবলী পরিষ্কারভাবে বিবৃত করেছি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (126,2,119,'আমি তোমাকে সত্যদ্বীনসহ সুসংবাদদাতা এবং ভয় প্রদর্শনকারী হিসেবে প্রেরণ করেছি, জাহান্নামীদের সম্বন্ধে তোমাকে কোন প্রশ্ন করা হবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (127,2,120,'ইয়াহূদী ও নাসারারা তোমার প্রতি রাজী হবে না যে পর্যন্ত না তুমি তাদের ধর্মের আদর্শ গ্রহণ কর। বল, ‘আল্লাহর দেখানো পথই প্রকৃত সুপথ এবং তুমি যদি জ্ঞান আসার পরেও এদের ইচ্ছে অনুযায়ী চল, তাহলে তোমার জন্য আল্লাহর ক্রোধ হতে রক্ষা করার মত কোন অভিভাবক ও সাহায্যকারী থাকবে না’। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (128,2,121,'আমি যাদেরকে কিতাব দিয়েছি, তারা যথাযথভাবে কিতাব তিলাওয়াত করে, তারাই এতে বিশ্বাস পোষণ করে আর যারা এর প্রতি অবিশ্বাস করে, তারাই ক্ষতিগ্রস্ত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (129,2,122,'হে বানী ইসরাঈল! আমার সেই অনুগ্রহ স্মরণ কর, যা আমি তোমাদের প্রতি করেছি এবং আমি তোমাদেরকে বিশ্বজগতের সকলের উপর শ্রেষ্ঠত্ব দিয়েছি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (130,2,123,'আর তোমরা সেই দিনের ভয় কর, যে দিন কেউ কারো কোন কাজে আসবে না এবং কারো পক্ষ হতে কোন ক্ষতিপূরণ গ্রহণ করা হবে না এবং কারও সুপারিশ ফল দিবে না এবং তারা সাহায্যপ্রাপ্তও হবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (131,2,124,'এবং স্মরণ কর যখন ইবরাহীমকে তার প্রতিপালক কতিপয় বিষয়ে পরীক্ষা করলেন, অতঃপর সে  সেগুলো  পূর্ণ করল, তখন  আল্লাহ  বললেন, ‘আমি তোমাকে মানবজাতির নেতা করছি’। ইব্রাহীম আরয করল, ‘আর আমার বংশধর হতেও’? নির্দেশ হল, আমার অঙ্গীকারের মধ্যে যালিমরা শামিল নয়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (132,2,125,'এবং স্মরণ কর যখন আমি কা‘বাগৃহকে মানুষের জন্য মিলনকেন্দ্র এবং নিরাপদস্থল করলাম এবং বললাম, ‘মাকামে ইবরাহীমকে সলাতের স্থান হিসেবে গ্রহণ কর’ এবং ইবরাহীম ও ইসমাঈলকে বলেছিলাম, ‘আমার গৃহকে তাওয়াফকারী, ই‘তিকাফকারী এবং রুকূ ও সাজদাহকারীদের জন্য পবিত্র রাখবে’। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (133,2,126,'স্মরণ কর যখন ইবরাহীম প্রার্থনা করেছিল, ‘হে আমার প্রতিপালক! তুমি এ শহরকে নিরাপদস্থল কর এবং এর অধিবাসীদের মধ্যে যারা আল্লাহ ও পরকালের উপর ঈমান আনে, তাদেরকে ফলমূল হতে জীবিকা প্রদান কর’। নির্দেশ হল, ‘যে কেউ কুফরী করবে তাকেও আমি কিছু দিনের জন্য উপকার লাভ করতে দেব এবং তারপর তাকে জাহান্নামের আগুনে দাখিল করব, আর কতই না নিকৃষ্ট তাদের ফেরার জায়গা’!');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (134,2,127,'আর (স্মরণ কর) যখন ইব্রাহীম ও ইসমাঈল কাবাগৃহের ভিত্তি তুলছিল, তখন প্রার্থনা করল, ‘হে আমাদের প্রতিপালক!  আমাদের পক্ষ থেকে কবুল কর, নিশ্চয়ই তুমি সর্বশ্রোতা ও সর্বজ্ঞাতা’। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (135,2,128,'হে আমাদের প্রতিপালক! ‘আমাদেরকে তোমার অনুগত কর, আমাদের খান্দানে একদল সৃষ্টি কর, যারা তোমার আজ্ঞাবহ হয় আর আমাদেরকে ইবাদাতের নিয়ম-কানুন শিক্ষা দাও এবং আমাদের অপরাধ ক্ষমা কর, নিশ্চয়ই তুমি অত্যন্ত ক্ষমাশীল, পরম দয়ালু’। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (136,2,129,'‘হে আমাদের প্রতিপালক! এদের কাছে একজন রসূল এদের মধ্য হতে প্রেরণ কর, যে এদেরকে তোমার আয়াতগুলো পড়ে শুনাবে এবং এদেরকে কিতাব ও জ্ঞান-বিজ্ঞান শিক্ষা দেবে এবং এদেরকে বিশুদ্ধ করবে, নিশ্চয় তুমি ক্ষমতাশালী, প্রজ্ঞাময়।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (137,2,130,'সেই নির্বোধ ছাড়া অন্য এমন কে আছে যে মিল্লাতে ইব্রাহীম হতে ফিরে যাবে এবং নিশ্চয় আমি তাকে পছন্দ  করেছি  এবং আখেরাতেও সে নেককারদের অন্তর্গত হবে।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (138,2,131,'তার প্রতিপালক যখন তাকে বলেছিলেন, ‘তুমি আত্মসমর্পণ কর’, উত্তরে  সে বলল,  ‘আমি  সারা জগতের প্রতিপালকের কাছে আত্মসমর্পণ করলাম’। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (139,2,132,'আর এ বিষয়ে ইবরাহীম ও ইয়া‘কূব স্বীয় পুত্রগণকে অন্তিম উপদেশ দান করে গেছে- ‘হে পুত্রগণ! আল্লাহ এ দ্বীনকে তোমাদের জন্য পছন্দ করেছেন; কাজেই তোমরা মুসলিম না হয়ে মৃত্যুবরণ করো না’।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (140,2,133,'তোমরা কি সে সময় উপস্থিত ছিলে, যখন ইয়াকুবের মৃত্যু এসে পৌঁছেছিল? তখন সে তার পুত্রদেরকে জিজ্ঞেস করেছিল, ‘আমার পরে তোমরা কার উপাসনা করবে’? পুত্রগণ উত্তর দিয়েছিল, ‘আমরা আপনার এবং আপনার পূর্বপুরুষ ইবরাহীম, ইসমাঈল ও ইসহাকের উপাস্যের উপাসনা করব, যিনি অদ্বিতীয় উপাস্য এবং আমরা তাঁরই প্রতি আত্মসমর্পিত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (141,2,134,'এ লোকেরা গত হয়ে গেছে, তাদের জন্য তাদের কৃতকর্ম এবং তোমাদের জন্য তোমাদের কৃতকর্ম এবং তাদের কৃতকর্ম সম্বন্ধে তোমাদেরকে জিজ্ঞেস করা হবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (142,2,135,'ওরা বলে, ‘তোমরা ইয়াহূদী বা নাসারা হয়ে যাও তাহলে সঠিক পথ পাবে’। বল, ‘বরং একনিষ্ঠ হয়ে ইবরাহীমের ধর্মাদর্শ অনুসরণ কর আর তিনি মুশরিকদের অন্তর্গত ছিলেন না’। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (143,2,136,'তোমরা বল, ‘আমরা আল্লাহর উপর ঈমান এনেছি এবং যা আমাদের প্রতি নাযিল করা হয়েছে এবং যা ইবরাহীম ও ইসমা‘ঈল এবং ইসহাক ও ইয়াকূব ও বংশধরদের প্রতি অবতীর্ণ করা হয়েছে এবং যা মূসা ও ‘ঈসাকে দেয়া হয়েছে আর যা অন্যান্য নাবীগণকে তাদের প্রতিপালকের পক্ষ হতে দেয়া হয়েছে (এ সবের প্রতিও ঈমান এনেছি), আমরা এদের মধ্য হতে কোন একজনের ব্যাপারেও কোন পার্থক্য করি না এবং আমরা তাঁরই কাছে আত্মসমর্পিত’।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (144,2,137,'সুতরাং এরা যদি তেমন ঈমান আনে, যেমন তোমরা ঈমান এনেছো, তাহলে তারা সঠিক পথ পাবে আর যদি অস্বীকার করে, তবে তারা ভেদাভেদে লিপ্ত, সে অবস্থায় তোমার জন্য তাদের (অনিষ্ট হতে বাঁচার জন্য) আল্লাহ্ই যথেষ্ট এবং তিনিই সর্বশ্রোতা ও সর্বজ্ঞাতা। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (145,2,138,'(আমাদের দ্বীন) আল্লাহর রঙে রঞ্জিত এবং আল্লাহর রঙ অপেক্ষা আর কার রঙ উত্তম হবে? এবং আমরা তাঁরই ‘ইবাদাতকারী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (146,2,139,'বল, ‘তোমরা কি আল্লাহর সম্বন্ধে আমাদের সাথে ঝগড়া করছ? অথচ তিনি আমাদেরও প্রতিপালক এবং তোমাদেরও প্রতিপালক এবং আমাদের জন্য আমাদের কৃতকর্ম এবং তোমাদের জন্য তোমাদের কৃতকর্ম এবং আমরা তাঁর প্রতি একনিষ্ঠ।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (147,2,140,'তোমরা কি বলছ, ইবরাহীম ও ইসমাঈল এবং ইসহাক ও ইয়াকূব এবং তার বংশধর সকলেই ইয়াহূদী কিংবা নাসারা ছিল’? বল,‘ তোমরাই বেশী জান নাকি আল্লাহ’? ঐ ব্যক্তি হতে বড় যালিম আর কে হবে, যে আল্লাহর পক্ষ হতে আগত সাক্ষ্যকে গোপন করে? তোমরা যা কিছু করছ, আল্লাহ সে বিষয়ে গাফিল নন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (148,2,141,'এ সব লোক যারা ছিল, তারা গত হয়ে গেছে, তাদের জন্য তাদের কামাই আর তোমাদের জন্য তোমাদের কামাই আর তারা যা করত সে সম্পর্কে তোমাদেরকে জিজ্ঞেস করা হবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (149,2,142,'শীঘ্রই এ নির্বোধেরা বলবে, কিসে তাদেরকে ফিরিয়ে দিল তাদের সেই ক্বিবলা হতে যা তারা অনুসরণ করে আসছিল। বল, পূর্ব এবং পশ্চিম আল্লাহরই, তিনি যাকে ইচ্ছে সরল পথ প্রদর্শন করেন।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (150,2,143,'আর এভাবে আমি তোমাদেরকে মধ্যপন্থী উম্মাত করেছি, যাতে তোমরা লোকেদের উপর সাক্ষী হও এবং নাবী তোমাদের উপর সাক্ষী হয়। আর তুমি এ যাবৎ যে ক্বিবলার উপর ছিলে, তাকে এ উদ্দেশে ক্বিবলা করেছিলাম, যাতে আমি জানতে পারি কে রসূলের অনুসরণ করে আর কে পায়ের ভরে উল্টো দিকে ফিরে যায়। আল্লাহ যাদেরকে হিদায়াত দিয়েছেন তারা বাদে অন্যের নিকট এটা বড়ই কষ্টকর ছিল। আর আল্লাহ তোমাদের ঈমান বিনষ্ট করবেন না, নিশ্চয়ই আল্লাহ মানুষের প্রতি করুণাশীল, অতি দয়ালু।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (151,2,144,'নিশ্চয়ই আমি তোমার আকাশের দিকে মুখ ফিরিয়ে দেখাকে লক্ষ্য করেছি, যে ক্বিবলা তুমি পছন্দ কর, আমি তোমাকে সেদিকে ফিরে যেতে আদেশ করছি। তুমি মাসজিদুল হারামের দিকে মুখ ফিরাও এবং তোমরা যেখানেই থাক, ওরই দিকে মুখ ফিরাও; বস্তুতঃ যাদেরকে কিতাব দেয়া হয়েছে তাদের জানা আছে যে, ক্বিবলার পরিবর্তন তাদের প্রতিপালকের পক্ষ হতে প্রকৃতই সত্য এবং তারা যা করে আল্লাহ সে সম্পর্কে মোটেই গাফিল নন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (152,2,145,'আর যদি তুমি কিতাবধারীদের সামনে সমুদয় দলীল হাজির কর, তবুও তারা তোমার ক্বিবলার অনুসরণ করবে না আর তুমিও তাদের ক্বিবলার অনুসরণকারী নও, আর তারা একে অপরের ক্বিবলার অনুসরণকারী নয়। যদি তুমি তোমার নিকট জ্ঞান আসার পরেও তাদের মনগড়া মতবাদসমূহের অনুসরণ কর, সে অবস্থায় তুমিও অবাধ্য দলেরই অন্তর্ভুক্ত হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (153,2,146,'আমি যাদেরকে কিতাব দিয়েছি, তারা তাকে সে রকমই চিনে, যেমন চিনে নিজের পুত্রদেরকে, আর ওদের কতকলোক জেনে শুনে সত্যকে গোপন করে থাকে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (154,2,147,'প্রকৃত সত্য তোমার প্রতিপালকের নিকট হতেই (এসেছে), কাজেই তোমরা সন্দেহকারীদের অন্তর্ভুক্ত হয়ো না।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (155,2,148,'প্রত্যেকের জন্যই  একটা  নির্দিষ্ট লক্ষ্য  আছে, সেদিকেই সে মুখ করে। কাজেই তোমরা সৎ কাজের দিকে ধাবমান হও। যেখানেই তোমরা অবস্থান কর, আল্লাহ তোমাদের সকলকে একত্রিত করবেন। নিশ্চয়ই আল্লাহ সকল বস্তুর উপর ক্ষমতাবান। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (156,2,149,'আর তুমি যেখান থেকেই বের হও, নিজের মুখ মাসজিদে হারামের দিকে ফেরাও, নিশ্চয়ই তা তোমার প্রতিপালকের নিকট হতে পাঠানো সত্য, বস্তুতঃ তোমরা যা করছ আল্লাহ সে সম্পর্কে মোটেই গাফিল নন।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (157,2,150,'তুমি যেখান থেকেই বের হও, নিজের মুখ মাসজিদে হারামের দিকে ফিরাও, তোমরা যেখানেই থাক না কেন, নিজেদের মুখগুলো ওর দিকে করিও, যাতে তাদের মধ্যেকার যালিম লোক ছাড়া অন্যান্য লোকেদের তোমাদের বিরুদ্ধে কোন কথা বলার না থাকে, কাজেই তাদেরকে ভয় করো না, আমাকেই ভয় কর, যাতে আমি তোমাদের প্রতি আমার নি‘মাত পূর্ণ করতে পারি, যাতে তোমরা সত্য পথে পরিচালিত হতে পার। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (158,2,151,'যেমন (তোমরা আমার একটি অনুগ্রহ লাভ করেছ যে) আমি তোমাদেরই মধ্য হতে তোমাদের কাছে একজন রসূল পাঠিয়েছি, যে আমার আয়াতগুলো তোমাদেরকে পড়ে শুনায়, তোমাদেরকে শুদ্ধ করে, তোমাদেরকে কিতাব ও জ্ঞান-বিজ্ঞান (সুন্নাত) শিক্ষা দেয় এবং তোমাদেরকে এমন সব বিষয় শিক্ষা দেয় যা তোমরা জানতে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (159,2,152,'কাজেই তোমরা আমাকে স্মরণ কর, আমিও তোমাদেরকে স্মরণ করব এবং আমার শোকর করতে থাক, না-শোকরী করো না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (160,2,153,'হে মু’মিনগণ! ধৈর্য ও সলাতের মাধ্যমে সাহায্য প্রার্থনা কর, নিশ্চয় আল্লাহ ধৈর্যশীলদের সঙ্গে আছেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (161,2,154,'আর আল্লাহর পথে নিহতদেরকে মৃত বলো না, বরং তারা জীবিত,  কিন্তু  তোমরা  বুঝ  না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (162,2,155,'তোমাদেরকে ভয় ও ক্ষুধা এবং ধন-সম্পদ, জীবন ও ফল-ফসলের ক্ষয়-ক্ষতি (এসবের) কোনকিছুর দ্বারা নিশ্চয়ই পরীক্ষা করব, ধৈর্যশীলদেরকে সুসংবাদ প্রদান কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (163,2,156,'নিশ্চয়ই যারা বিপদকালে বলে থাকে, ‘আমরা আল্লাহরই আর আমরা তাঁরই দিকে প্রত্যাবর্তনকারী’। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (164,2,157,'এদের প্রতি রয়েছে তাদের প্রতিপালকের পক্ষ হতে অনুগ্রহ ও করুণা আর এরাই হিদায়াতপ্রাপ্ত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (165,2,158,'নিশ্চয়ই ‘সাফা’ এবং ‘মারওয়া’ আল্লাহর নিদর্শনগুলোর অন্যতম। কাজেই যে ব্যক্তি কাবাগৃহের হাজ্জ অথবা ‘উমরাহ করবে, এ দু’টোর সাঈ করাতে তাদের কোনই গুনাহ নেই এবং যে ব্যক্তি স্বেচ্ছায় কোন সৎ কাজ করবে তাহলে নিশ্চয় আল্লাহ (তার ব্যাপারে) গুণগ্রাহী এবং সর্বজ্ঞ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (166,2,159,'নিশ্চয়ই যারা আমার অবতীর্ণ কোন দলীল এবং হিদায়াতকে লোকেদের জন্য আমি কিতাবের মধ্যে বর্ণনা করার পরেও গোপন করে, আল্লাহ তাদেরকে অভিসম্পাত করেন আর অভিসম্পাতকারীরাও তাদের প্রতি অভিসম্পাত করে থাকে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (167,2,160,'কিন্তু যারা তাওবাহ করে এবং সংশোধন করে নেয় এবং (সত্যকে) সুস্পষ্টভাবে বর্ণনা করে, তাদের তাওবাহ আমি কবুল করি, বস্তুতঃ আমি অত্যধিক তাওবাহ কবুলকারী, পরম দয়ালু। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (168,2,161,'নিশ্চয় যারা কাফির এবং কাফের অবস্থাতেই মারা যায়, এমন লোকেদের প্রতি আল্লাহর, ফেরেশতাদের এবং সকল মানুষের অভিসম্পাত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (169,2,162,'তাতে তারা চিরকাল থাকবে, তাদের উপর ‘আযাব হালকা করা হবে না আর তাদেরকে বিরামও দেয়া হবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (170,2,163,'তোমাদের উপাস্য হচ্ছেন এক আল্লাহ, তিনি ছাড়া সত্যিকারের কোন উপাস্য নেই। তিনি পরম করুনাময়, অতি দয়ালু। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (171,2,164,'নিশ্চয়ই আকাশসমূহ ও পৃথিবীর সৃষ্টির মধ্যে, রাত ও দিনের বিবর্তনের মধ্যে, লোকের উপকারী দ্রব্যাদিসহ সমুদ্রে চলাচলকারী জলযানের মধ্যে এবং আকাশ হতে আল্লাহর বর্ষিত সেই পানির মধ্যে যদ্বারা তিনি পৃথিবীকে- মরে যাওয়ার পর আবার জীবিত করেন এবং তাতে সকল প্রকার জীব-জন্তুর বিস্তারণে এবং বাতাসের গতি পরিবর্তনের মধ্যে এবং আকাশ ও ভূমন্ডলের মধ্যস্থলে নিয়ন্ত্রিত মেঘপুঞ্জের মধ্যে বিবেকসম্পন্ন লোকেদের জন্য নিদর্শনাবলী রয়েছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (172,2,165,'আর কোন কোন লোক এমনও আছে, যে আল্লাহ ছাড়া অন্যান্যকে আল্লাহর সমকক্ষরূপে গ্রহণ করে, আল্লাহকে ভালবাসার মত তাদেরকে ভালবাসে। কিন্তু যারা মু’মিন আল্লাহর সঙ্গে তাদের ভালবাসা প্রগাঢ় এবং কী উত্তমই হত যদি এ যালিমরা শাস্তি দেখার পর যেমন বুঝবে তা যদি এখনই বুঝত যে, সমস্ত শক্তি আল্লাহরই জন্য এবং আল্লাহ শাস্তি দানে অত্যন্ত কঠোর।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (173,2,166,'স্মরণ কর, যাদেরকে অনুসরণ করা হত তারা অনুসরণকারীদের সঙ্গে তাদের কোন সম্পর্কের কথা অস্বীকার করবে, তারা শাস্তি দেখবে আর তাদের মধ্যেকার যাবতীয় সম্পর্ক সম্বন্ধ ছিন্ন হয়ে যাবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (174,2,167,'অনুসরণকারীরা বলবে, যদি কোনও প্রকারে আমাদের ফিরে যাবার সুযোগ ঘটত, তাহলে আমরাও তাদের সঙ্গে সম্পর্ক ছিন্ন করতাম যেমনভাবে তারা সম্পর্ক ছিন্ন করল। এভাবে আল্লাহ তাদেরকে তাদের কাজগুলো দেখাবেন তাদের জন্য আক্ষেপরূপে এবং জাহান্নাম হতে তারা বের হতে পারবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (175,2,168,'ওহে মনুষ্যজাতি! ভূমন্ডলে বিদ্যমান বস্তুগুলো হতে হালাল উত্তম জিনিসগুলো খাও এবং শায়ত্বনের পদাঙ্ক অনুসরণ করে চলো না, বস্তুতঃ সে তোমাদের প্রকাশ্য শত্রু। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (176,2,169,'সে তোমাদেরকে শুধু অসৎ এবং অশ্লীল কাজের নির্দেশ দেয়, আর তোমাদেরকে নির্দেশ দেয় আল্লাহর সম্বন্ধে এমন কথা বলার যা তোমরা জান না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (177,2,170,'যখন তাদেরকে বলা হয়, তোমরা ঐ জিনিসের অনুসরণ কর যা আল্লাহ নাযিল করেছেন, তখন তারা বলে, বরং আমরা তারই উপর চলব, যার উপর আমরা আমাদের বাপ-দাদাদের পেয়েছি, যদিও তাদের বাপ-দাদারা কিছুই বুঝত না এবং সঠিক পথে চলত না তবুও। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (178,2,171,'এ কাফিরদের তুলনা সেই ব্যক্তির মত যে এমন কিছুকে ডাকে যা হাঁক-ডাক ছাড়া আর কিছুই শোনে না, বধির, মূক ও অন্ধ; কাজেই তারা বুঝবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (179,2,172,'হে মু’মিনগণ! আমার দেয়া পবিত্র বস্তুগুলো খেতে থাক এবং আল্লাহর উদ্দেশে শোকর করতে থাক, যদি তোমরা তাঁরই ‘ইবাদত করে থাক। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (180,2,173,'নিশ্চয় আল্লাহ তোমাদের প্রতি হারাম করেছেন মৃত-জীব, রক্ত এবং শূকরের মাংস এবং সেই জন্তু যার প্রতি আল্লাহ ছাড়া অন্যের নাম নেয়া হয়েছে, তবে যে ব্যক্তি নিরুপায় হয়ে পড়ে কিন্তু সে নাফরমান ও সীমালঙ্ঘনকারী নয়, তার উপর কোন গুনাহ নেই, নিশ্চয় আল্লাহ ক্ষমাশীল, দয়ালু। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (181,2,174,'কিতাব হতে আল্লাহ যা নাযিল করেছেন, যারা এটা গোপন করে এবং এর বিনিময়ে স্বল্প মূল্য গ্রহণ করে, এরা নিজেদের পেটে একমাত্র আগুন ভক্ষণ করে, ওদের সাথে আল্লাহ ক্বিয়ামাতের দিন কথা বলবেন না এবং ওদেরকে পবিত্রও করবেন না; এবং ওদের জন্য আছে যন্ত্রণাদায়ক শাস্তি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (182,2,175,'এরা এমন লোক, যারা হিদায়াতের বিনিময়ে গোমরাহী এবং ক্ষমার বিনিময়ে শাস্তি ক্রয় করেছে, তারা আগুন সহ্য করতে কতই না ধৈর্যশীল! ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (183,2,176,'(তাদের প্রতি শাস্তির হুকুম দেয়া হয়েছে) এজন্য যে, আল্লাহ্ই কিতাবকে সত্যরূপে নাযিল করেছেন আর যারা কিতাব সম্বন্ধে মতভেদ করেছে তারা চরম মতভেদে পড়ে আছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (184,2,177,'তোমরা নিজেদের মুখ পূর্ব দিকে কর কিংবা পশ্চিম দিকে এতে কোন কল্যাণ নেই বরং কল্যাণ আছে এতে যে, কোন ব্যক্তি ঈমান আনবে আল্লাহ, শেষ দিবস, ফেরেশতাগণ, কিতাবসমূহ ও নাবীগণের প্রতি এবং আল্লাহর ভালবাসার্থে ধন-সম্পদ আত্মীয়-স্বজন, ইয়াতীম-মিসকীন, মুসাফির ও যাচ্ঞাকারীদের এবং দাসত্বজীবন হতে নিস্কৃতি দিতে দান করবে এবং নামায কায়িম করবে ও যাকাত দিতে থাকবে, ওয়া‘দা করার পর স্বীয় ওয়া‘দা পূর্ণ করবে এবং অভাবে, দুঃখ-ক্লেশে ও সংকটে ধৈর্য ধারণ করবে, এ লোকেরাই সত্যপরায়ণ আর এ লোকেরাই মুত্তাকী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (185,2,178,'হে মু’মিনগণ! তোমাদের প্রতি নিহতদের কিসাস সম্পর্কে আদেশ দেয়া যাচ্ছে, স্বাধীন ব্যক্তির বদলে স্বাধীন, গোলামের বদলে গোলাম এবং স্ত্রীলোকের বদলে স্ত্রীলোক, অতঃপর যাকে তার ভাইয়ের পক্ষ হতে কিছু অংশ মাফ ক’রে দেয়া হয়, সে অবস্থায় যথাযথ বিধির অনুসরণ করা ও সততার সঙ্গে তার দেয় আদায় করা বিধেয়, এটা তোমাদের প্রভুর পক্ষ হতে ভার লাঘব ও অনুগ্রহ, এরপর যে কেউ বাড়াবাড়ি করবে, তার জন্য যন্ত্রণাদায়ক শাস্তি রয়েছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (186,2,179,'কিসাসের মধ্যে তোমাদের জন্য জীবন নিহিত রয়েছে, যাতে হে জ্ঞানী সমাজ! (হত্যানুষ্ঠান হতে) তোমরা নিবৃত্ত থাক। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (187,2,180,'তোমাদেরকে নির্দেশ দেয়া যাচ্ছে যে, যখন তোমাদের কারও সামনে মৃত্যু উপস্থিত হয় এবং সেই ব্যক্তি কিছু সম্পত্তি ছেড়ে যায়, তবে সে ব্যক্তি যেন সঙ্গতভাবে ওয়াসীয়াত করে যায় পিতা-মাতা ও নিকট সম্পর্কীয়দের জন্য, মুত্তাকীদের জন্য এটা একটা কর্তব্য। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (188,2,181,'অতঃপর যে ব্যক্তি তা শুনে নেয়ার পর ওয়াসীয়াতের পরিবর্তন ঘটাবে, তবে তার গুনাহ সেই লোকদেরই প্রতি যারা তার পরিবর্তন ঘটাবে, নিশ্চয় আল্লাহ সর্বশ্রোতা ও সর্বজ্ঞ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (189,2,182,'যে ব্যক্তি ওয়াসীয়াতকারীর পক্ষ হতে পক্ষপাতিত্বের ভয় করে কিংবা অন্যায়ের আশঙ্কা করে, অতঃপর সে যদি তাদের মধ্যে মিটমাট করে দেয়, তবে তার কোনই গুনাহ নেই, নিশ্চয় আল্লাহ বড়ই ক্ষমাশীল, পরম দয়ালু। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (190,2,183,'হে ঈমানদারগণ! তোমাদের প্রতি রোযা ফরয করা হয়েছে, যেমন তোমাদের আগের লোকেদের প্রতি ফরয করা হয়েছিল, যাতে তোমরা মুত্তাকী হতে পার। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (191,2,184,'(রোযা) নির্দিষ্ট কয়েকটি দিনের জন্য, অতঃপর তোমাদের মধ্যে যে পীড়িত কিংবা মুসাফির সে অন্য সময় এ সংখ্যা পূরণ করে নেবে এবং শক্তিহীনদের উপর কর্তব্য হচ্ছে ফিদইয়া প্রদান করা, এটা একজন মিসকীনকে অন্নদান করা এবং যে ব্যক্তি নিজের খুশীতে সৎ কাজ করতে ইচ্ছুক, তার পক্ষে তা আরও উত্তম আর সে অবস্থায় রোযা পালন করাই তোমাদের পক্ষে উত্তম, যদি তোমরা বুঝ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (192,2,185,'রমাযান মাস- যার মধ্যে কুরআন নাযিল করা হয়েছে লোকেদের পথ প্রদর্শক এবং হিদায়াতের সুস্পষ্ট বর্ণনারূপে এবং সত্য-মিথ্যার পার্থক্যকারীরূপে, কাজেই তোমাদের মধ্যে যে ব্যক্তি এ মাস পাবে, সে যেন এ মাসে রোযা পালন করে আর যে পীড়িত কিংবা সফরে আছে, সে অন্য সময় এ সংখ্যা পূরণ করবে, আল্লাহ তোমাদের জন্য যা সহজ তা চান, যা কষ্টদায়ক তা চান না যেন তোমরা মেয়াদ পূর্ণ করতে পার, আর তোমাদেরকে সৎপথে পরিচালিত করার কারণে তোমরা আল্লাহর মাহাত্ম্য ঘোষণা কর, আর যাতে তোমরা কৃতজ্ঞতা প্রকাশ করতে পার। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (193,2,186,'যখন আমার বান্দাগণ আমার সম্পর্কে তোমার নিকট জিজ্ঞেস করে, আমি তো (তাদের) নিকটেই, আহবানকারী যখন আমাকে আহবান করে আমি তার আহবানে সাড়া দেই; সুতরাং তাদের উচিত আমার নির্দেশ মান্য করা এবং আমার প্রতি ঈমান আনা, যাতে তারা সরলপথ প্রাপ্ত হয়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (194,2,187,'তোমাদের জন্য রমাযানের রাতে তোমাদের বিবিগণের নিকট গমন করা জায়িয করা হয়েছে, তারা তোমাদের আচ্ছাদন আর তোমরা তাদের আচ্ছাদন। আল্লাহ জানতেন যে, তোমরা নিজেদের সঙ্গে প্রতারণা করছিলে। সুতরাং তিনি তোমাদেরকে ক্ষমা করলেন এবং তোমাদের অব্যাহতি দিলেন। অতএব, এখন থেকে তোমরা তাদের সঙ্গে সহবাস করতে পার এবং আল্লাহ তোমাদের জন্য যা কিছু বিধিবদ্ধ করেছেন তা লাভ কর এবং তোমরা আহার ও পান করতে থাক যে পর্যন্ত তোমাদের জন্য কালো রেখা হতে ঊষাকালের সাদা রেখা প্রকাশ না পায়। তৎপর রাতের আগমন পর্যন্ত রোযা পূর্ণ কর, আর মাসজিদে ই’তিকাফ অবস্থায় তাদের সাথে সহবাস করো না। এসব আল্লাহর আইন, কাজেই এগুলোর নিকটবর্তী হয়ো না। আল্লাহ মানবজাতির জন্য নিজের আয়াতসমূহ বিস্তারিতভাবে বর্ণনা করেন, যাতে তারা মুত্তাকী হতে পারে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (195,2,188,'আর তোমরা অন্যায়ভাবে পরস্পরের মাল গ্রাস করো না এবং জানা সত্ত্বেও অসৎ উপায়ে লোকের মাল গ্রাস করার উদ্দেশে তা বিচারকের নিকট নিয়ে যেও না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (196,2,189,'লোকেরা তোমাকে নতুন চাঁদ সম্পর্কে প্রশ্ন করছে। বল, তা মানুষের ও হাজ্জের জন্য সময় নির্ধারক। তোমরা যে গৃহের পেছন দিক দিয়ে প্রবেশ কর, তাতে কোন পুণ্য নেই, বরং পুণ্য আছে কেউ তাকওয়া অবলম্বন করলে, কাজেই তোমরা (সদর) দরজাগুলো দিয়ে গৃহে প্রবেশ কর এবং আল্লাহ্কে ভয় করতে থাক, যাতে তোমরা সফলকাম হতে পার। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (197,2,190,'তোমরা আল্লাহর পথে সেই লোকেদের বিরুদ্ধে যুদ্ধ কর, যারা তোমাদের বিরুদ্ধে যুদ্ধ করবে, কিন্তু সীমা অতিক্রম করো না। আল্লাহ নিশ্চয়ই সীমা অতিক্রমকারীকে ভালবাসেন না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (198,2,191,'তাদেরকে যেখানেই পাও হত্যা কর এবং তাদেরকে বের করে দাও যেখান থেকে তারা তোমাদেরকে বের করে দিয়েছে। বস্তুতঃ ফিতনা হত্যার চেয়েও গুরুতর। তোমরা মাসজিদে হারামের নিকট তাদের সাথে যুদ্ধ করো না, যে পর্যন্ত তারা তোমাদের সাথে সেখানে যুদ্ধ না করে, কিন্তু যদি তারা তোমাদের সাথে যুদ্ধ করে, তবে তোমরাও তাদের হত্যা কর, এটাই কাফিরদের প্রতিদান। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (199,2,192,'অতঃপর যদি তারা বিরত হয়, তবে আল্লাহ ক্ষমাশীল, বড়ই দয়ালু। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (200,2,193,'ফিতনা দূরীভূত না হওয়া পর্যন্ত এবং দীন আল্লাহর জন্য নির্ধারিত না হওয়া পর্যন্ত তাদের বিরুদ্ধে যুদ্ধ কর, অতঃপর যদি তারা বিরত হয় তবে যালিমদের উপরে ছাড়া কোনও প্রকারের কঠোরতা অবলম্বন জায়িয হবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (201,2,194,'সম্মানিত মাস হচ্ছে সম্মানিত মাসের বিনিময়ে এবং পবিত্রতা রক্ষার দায়িত্ব সবার জন্য সমান, কাজেই যে কেউ তোমাদের প্রতি কঠোর আচরণ করে, তবে তোমরাও তাদের প্রতি কঠোর আচরণ কর যেমনি কঠোরতা সে তোমাদের প্রতি করেছে এবং আল্লাহ্কে ভয় কর এবং জেনে রেখ, আল্লাহ মুত্তাকীদের সঙ্গে আছেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (202,2,195,'তোমরা আল্লাহর পথে ব্যয় কর এবং স্বহস্তে নিজেদেরকে ধ্বংসে নিক্ষেপ করো না এবং কল্যাণকর কাজ করে যাও, নিশ্চয়ই আল্লাহ কল্যাণকারীদেরকে ভালবাসেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (203,2,196,'আল্লাহর সন্তুষ্টির উদ্দেশ্যে হাজ্জ ও ‘উমরাহকে পূর্ণ কর, কিন্তু যদি তোমরা বাধাগ্রস্ত হও, তবে যা সম্ভব কুরবানী দিবে এবং কুরবানী যথাস্থানে না পৌঁছা পর্যন্ত নিজেদের মস্তক মুন্ডন করো না, তবে তোমাদের মধ্যে যে পীড়িত কিংবা মাথায় যন্ত্রণাগ্রস্ত, সে রোযা কিংবা সদাক্বাহ বা কুরবানী দ্বারা ফিদইয়া দিবে এবং যখন তোমরা নিরাপদ থাক, তখন যে কেউ ‘উমরাহকে হাজ্জের সঙ্গে মিলিয়ে উপকার লাভ করতে ইচ্ছুক, সে যেমন সম্ভব কুরবানী দিবে এবং যার পক্ষে সম্ভব না হয়, সে ব্যক্তি হাজ্জের দিনগুলোর মধ্যে তিনদিন এবং গৃহে ফেরার পর সাতদিন, এই মোট দশদিন রোযা পালন করবে। এটা সেই লোকের জন্য, যার পরিবারবর্গ মাসজিদে হারামের বাসিন্দা নয়। আল্লাহকে ভয় কর আর জেনে রেখ, নিশ্চয়ই আল্লাহ শাস্তিদানে কঠোর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (204,2,197,'হাজ্জ হয় কয়েকটি নির্দিষ্ট মাসে, অতঃপর এ মাসগুলোতে যে কেউ হাজ্জ করার মনস্থ করবে, তার জন্য হাজ্জের মধ্যে স্ত্রী সম্ভোগ, অন্যায় আচরণ ও ঝগড়া-বিবাদ বৈধ নয় এবং তোমরা যে কোন সৎ কাজই কর, আল্লাহ তা জানেন এবং তোমরা পাথেয়ের ব্যবস্থা করবে আর তাক্বওয়াই শ্রেষ্ঠ পাথেয়। হে জ্ঞানী সমাজ! আমাকেই ভয় করতে থাক।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (205,2,198,'তোমাদের প্রতি কোন গুনাহ নেই যদি তোমরা তোমাদের প্রতিপালকের অনুগ্রহ খোঁজ কর এবং যখন তোমরা আরাফাত হতে ফিরবে তখন মাশ‘আরুল হারামের নিকট আল্লাহকে স্মরণ করবে এবং তাঁকে স্মরণ করবে যেরূপ তিনি তোমাদেরকে শিক্ষা দিয়েছেন, বস্তুতঃ তোমরা এর আগে ছিলে পথভ্রষ্টদের অন্তর্গত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (206,2,199,'তারপর তোমরা ফিরে আসবে যেখান থেকে লোকেরা ফিরে আসে এবং আল্লাহর নিকট ক্ষমা প্রার্থী হও, নিশ্চয় আল্লাহ ক্ষমাশীল, অতি দয়ালু। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (207,2,200,'অতঃপর মহান হাজ্জের করণীয় কার্যাবলী সমাপ্ত করবে, তখন আল্লাহর স্মরণে মশগুল হও, যেমন তোমরা নিজেদের বাপ-দাদাদের স্মরণে মশগুল থাক, বরং তার চেয়েও বেশি স্মরণ কর। লোকেদের কেউ কেউ বলে থাকে- হে আমাদের  প্রতিপালক! আমাদেরকে এ দুনিয়াতেই প্রদান কর, বস্তুতঃ সে আখেরাতে কিছুই পাবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (208,2,201,'লোকেদের মধ্যে কিছু লোক এমন আছে, যারা বলে থাকে, হে আমাদের প্রতিপালক! আমাদেরকে দুনিয়াতেও কল্যাণ দাও এবং আখেরাতেও কল্যাণ দাও এবং আমাদেরকে জাহান্নামের আযাব হতে রক্ষা কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (209,2,202,'এরাই সেই লোক, যাদের কৃতকার্যে তাদের প্রাপ্য অংশ রয়েছে এবং আল্লাহ সত্বর হিসাবগ্রহণকারী।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (210,2,203,'তোমরা নির্দিষ্ট দিনগুলোতে আল্লাহকে স্মরণ করবে; অতঃপর যে ব্যক্তি তাড়াতাড়ি ক’রে দু’দিনে চলে যায় তার প্রতি কোন গুনাহ নেই এবং যে ব্যক্তি অধিক সময় পর্যন্ত বিলম্ব করবে, তার প্রতিও গুনাহ নেই, এটা তার জন্য যে ব্যক্তি তাকওয়া অবলম্বন করবে এবং আল্লাহ্কে ভয় করতে থাকবে এবং জেনে রেখ, তোমরা সকলেই তাঁরই দিকে একত্রিত হবে।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (211,2,204,'মানুষের মধ্যে এমন আছে, পার্থিব জীবন সম্পর্কিত যার কথাবার্তা তোমাকে চমৎকৃত করে, আর সে ব্যক্তি তার অন্তরে যা আছে সে সম্পর্কে আল্লাহকে সাক্ষী রাখে অথচ সে ব্যক্তি খুবই ঝগড়াটে।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (212,2,205,'যখন তোমার কাছ থেকে সে ব্যক্তি ফিরে যায়, তখন দেশের মধ্যে অনিষ্ট ঘটাতে এবং শস্যাদি ও পশুসমূহকে ধবংস করতে চেষ্টা করে কিন্তু আল্লাহ ফাসাদ পছন্দ করেন না।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (213,2,206,'যখন তাকে বলা হয়, আল্লাহকে ভয় কর, তখন অহঙ্কার তাকে গুনাহর দিকে আকর্ষণ করে, জাহান্নামই তার জন্য যথেষ্ট আর তা কতই না জঘন্য আবাসস্থল!');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (214,2,207,'মানুষের মধ্যে এমন আছে যে, আল্লাহর সন্তুষ্টি অর্জনে নিজের প্রাণ দিয়ে থাকে, বস্তুতঃ আল্লাহ তাঁর বান্দাদের প্রতি অত্যধিক দয়ালু।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (215,2,208,'হে মু’মিনগণ! ইসলামের মধ্যে পূর্ণভাবে প্রবেশ কর এবং শায়ত্বনের পদাঙ্ক অনুসরণ করে চলো না, নিশ্চয়ই সে তোমাদের প্রকাশ্য শত্রু। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (216,2,209,'তোমাদের নিকট সুস্পষ্ট হুকুম পৌঁছার পরেও যদি তোমাদের পদস্খলন ঘটে তবে জেনে রেখ, নিশ্চয়ই আল্লাহ মহাপরাক্রান্ত, প্রজ্ঞাময়।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (217,2,210,'এরা কি এজন্য অপেক্ষা করছে যে, মেঘমালার ছত্র লাগিয়ে ফেরেশতাদের সঙ্গে নিয়ে আল্লাহ তাদের কাছে আগমন করবেন, অতঃপর সবকিছুর মীমাংসা হয়ে যাবে? বস্তুতঃ সকল কার্য আল্লাহর নিকটেই ফিরে যায় (চূড়ান্ত সিদ্ধান্তের জন্য)।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (218,2,211,'বানী ইসরাঈলকে জিজ্ঞেস কর, আমি তাদেরকে কত সুস্পষ্ট নিদর্শন দিয়েছি। কোন ব্যক্তি আল্লাহর নিয়ামাত তার নিকট পৌঁছার পর পরিবর্তন করলে, নিশ্চয়ই আল্লাহ শাস্তি দানে কঠোর।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (219,2,212,'কাফিরদের নিকট পার্থিব জীবন মোহনীয় করা হয়েছে এবং তারা মুমিনদেরকে বিদ্রূপ করে থাকে, বস্তুতঃ ক্বিয়ামাতের দিন মুত্তাকীগণ তাদের চেয়ে উন্নত অবস্থায় থাকবে, আল্লাহ যাকে ইচ্ছে অপরিমিত রিযক দিয়ে থাকেন।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (220,2,213,'মানুষ একই দলভুক্ত ছিল। তারপর আল্লাহ তাদের নিকট নাবীগণকে প্রেরণ করেন সুসংবাদদাতা ও সতর্ককারীরূপে এবং তাদের মাধ্যমে কিতাব নাযিল করেন সত্যভাবে, মানুষদের মধ্যে মীমাংসা করার জন্য যে বিষয়ে তারা মতপার্থক্য করেছিল। এতদসত্ত্বেও যাদেরকে কিতাব দেয়া হয়েছিল, তাদের নিকট সুস্পষ্ট প্রমাণাদি আসার পর পারস্পরিক জিদের কারণেই তারা মতভেদ সৃষ্টি করল। অতঃপর আল্লাহ বিশ্বাসীদেরকে সেই সত্য পথ নিজের করুণায় দেখিয়ে দিলেন, যে সম্বন্ধে তারা মতভেদ করছিল, আল্লাহ যাকে ইচ্ছে সোজা পথ প্রদর্শন করেন।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (221,2,214,'তোমরা কি এমন ধারণা পোষণ কর যে, তোমরা জান্নাতে প্রবেশ লাভ করবে, অথচ এখনও পর্যন্ত তোমাদের আগের লোকেদের মত অবস্থা তোমাদের সামনে আসেনি? তাদেরকে অভাবের তীব্র তাড়না এবং মুসীবত স্পর্শ করেছিল এবং তারা এতদূর বিকম্পিত হয়েছিল যে, নাবী ও তার সঙ্গের মু’মিনগণ চিৎকার করে বলেছিল- আল্লাহর সাহায্য কখন আসবে? জেনে রেখ, নিশ্চয়ই আল্লাহর সাহায্য নিকটবর্তী।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (222,2,215,'তোমাকে লোকে জিজ্ঞেস করছে, তারা কী ব্যয় করবে? বলে দাও, সৎকাজে যা-ই ব্যয় কর, তা তোমাদের মাতা-পিতা ও নিকটাত্মীয়, ইয়াতীম ও অভাবগ্রস্ত মুসাফিরদের প্রাপ্য। তোমরা যা কিছু সৎ কাজ কর, নিশ্চয়ই আল্লাহ সে বিষয়ে বিশেষভাবে অবহিত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (223,2,216,'তোমাদের প্রতি যুদ্ধ লিপিবদ্ধ করে দেয়া হয়েছে, অথচ তা তোমাদের কাছে অপ্রিয় কিন্তু তোমরা কোন কিছু অপছন্দ কর সম্ভবতঃ তোমাদের জন্য তা কল্যাণকর এবং সম্ভবতঃ কোন কিছু তোমাদের কাছে প্রিয় অথচ তা তোমাদের জন্য অকল্যাণকর। বস্তুতঃ আল্লাহ্ই জানেন, তোমরা জান না।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (224,2,217,'পবিত্র মাসে লড়াই করা সম্বন্ধে তোমাকে তারা জিজ্ঞেস করছে। বল, এতে যুদ্ধ করা ভয়ঙ্কর গুনাহ। পক্ষান্তরে আল্লাহর পথ হতে বাধা দান, আল্লাহর সঙ্গে কুফুরী, কা‘বা গৃহে যেতে বাধা দেয়া এবং তাত্থেকে তার বাসিন্দাদেরকে বের করে দেয়া আল্লাহর নিকট তার চেয়ে অধিক অন্যায়। ফিতনা হত্যা হতেও গুরুতর অন্যায়। যদি তাদের সাধ্যে কুলায় তারা তোমাদের বিরুদ্ধে যুদ্ধ করতেই থাকবে, যে পর্যন্ত না তারা তোমাদেরকে তোমাদের দ্বীন হতে ফিরিয়ে না দেয় এবং তোমাদের যে কেউ নিজের দ্বীন হতে ফিরে যায়, অতঃপর সেই ব্যক্তি কাফির অবস্থায় মারা যায়, তবে এমন লোকের কর্ম দুনিয়াতে এবং আখেরাতে ব্যর্থ হয়ে যাবে। আর এরা অগ্নিবাসী, চিরকালই তাতে থাকবে।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (225,2,218,'নিশ্চয়ই যারা ঈমান এনেছে এবং হিজরত করেছে ও আল্লাহর পথে জিহাদ করেছে, এরাই আল্লাহর রহমত আশা করে, আল্লাহ ক্ষমাশীল, পরম দয়ালু।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (226,2,219,'তোমাকে লোকে মদ ও জুয়া সম্পর্কে জিজ্ঞেস করছে। বল, ‘ঐ দু’টোতে আছে ভয়ঙ্কর গুনাহ এবং মানুষের জন্য উপকারও কিন্তু এ দু’টোর পাপ এ দু’টোর উপকার অপেক্ষা অধিক’। তোমাকে জিজ্ঞেস করছে, কী তারা ব্যয় করবে? বল, ‘যা উদ্বৃত্ত’। এভাবে আল্লাহ তোমাদের প্রতি আদেশাবলী বিস্তারিতভাবে বর্ণনা করছেন, যাতে তোমরা চিন্তা কর ।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (227,2,220,'দুনিয়া এবং আখিরাত সম্বন্ধে। আরও তারা তোমাকে ইয়াতীমদের সম্বন্ধে জিজ্ঞেস করছে; বল, ‘তাদের উপকার করা উত্তম’ এবং যদি তাদের সঙ্গে তোমরা একত্রে থাক, তবে তারা তো তোমাদেরই ভাই। বস্তুতঃ আল্লাহ জানেন কে অনিষ্টকারী আর কে কল্যাণকামী এবং আল্লাহ যদি ইচ্ছে করতেন, নিশ্চয়ই এ বিষয়ে তোমাদেরকে কঠোরতার মধ্যে নিক্ষেপ করতেন, নিঃসন্দেহে আল্লাহ প্রবল পরাক্রান্ত, প্রজ্ঞাময়।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (228,2,221,'মুশরিকা নারীরা ঈমান না আনা পর্যন্ত তোমরা তাদেরকে বিবাহ করো না। মূলতঃ মু’মিন ক্রীতদাসী মুশরিকা নারী হতে উত্তম ওদেরকে তোমাদের যতই ভাল লাগুক না কেন, ঈমান না আনা পর্যন্ত মুশরিক পুরুষদের সাথে বিবাহ দিও না, বস্তুতঃ মুশরিককে তোমাদের যতই ভাল লাগুক না কেন, মু’মিন গোলাম তার চেয়ে উত্তম। ওরা অগ্নির দিকে আহবান করে আর আল্লাহ নিজের অনুগ্রহে জান্নাত ও ক্ষমার দিকে আহবান করেন। তিনি মানুষদের জন্য নিজের হুকুমগুলো বিস্তারিতভাবে বর্ণনা করছেন যাতে তারা শিক্ষা গ্রহণ করে।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (229,2,222,'লোকেরা তোমাকে ঋতু সম্বন্ধে জিজ্ঞেস করছে। বল, ‘তা অশুচি’। কাজেই ঋতুকালে স্ত্রী-সহবাস হতে বিরত থাক এবং যে পর্যন্ত পবিত্র না হয়, তাদের নিকটবর্তী হয়ো না। তারপর যখন পবিত্র হবে, তখন তাদের সঙ্গে সহবাস কর, যেভাবে আল্লাহ অনুমতি দিয়েছেন। নিশ্চয়ই আল্লাহ তওবাহ্কারীদেরকে ভালবাসেন আর পবিত্রতা অবলম্বীদেরকেও ভালবাসেন।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (230,2,223,'তোমাদের স্ত্রীগণ তোমাদের শস্যক্ষেত্র। সুতরাং তোমরা তোমাদের শস্যক্ষেত্রে যে প্রকারে ইচ্ছে গমন কর এবং নিজেদের জন্য ভবিষ্যতের বন্দোবস্ত কর এবং আল্লাহকে ভয় কর এবং জেনে রেখ যে, তোমাদেরকে তাঁর কাছে হাজির হতে হবে। আর বিশ্বাসীদেরকে সুসংবাদ শুনিয়ে দাও।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (231,2,224,'তোমরা সৎকাজ, তাকওয়া অবলম্বন এবং মানুষের মধ্যে শান্তি স্থাপন হতে বিরত থাকবে- আল্লাহর নামে এমন শপথ করে তাকে অজুহাত করে নিও না। বস্তুতঃ আল্লাহ সর্বশ্রোতা ও সর্বজ্ঞাতা। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (232,2,225,'তোমরা সৎকাজ, তাকওয়া অবলম্বন এবং মানুষের মধ্যে শান্তি স্থাপন হতে বিরত থাকবে- আল্লাহর নামে এমন শপথ করে তাকে ওজুহাত করে নিও না। বস্তুতঃ আল্লাহ সর্বশ্রোতা ও সর্বজ্ঞাতা।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (233,2,226,'যারা নিজেদের স্ত্রীদের নিকট না যাওয়ার জন্য শপথ গ্রহণ করে, তাদের জন্য চার মাসের অবকাশ আছে। যদি তারা (উক্ত সময়ের মধ্যে) ফিরে আসে, তবে নিশ্চয়ই আল্লাহ ক্ষমাশীল, পরম দয়ালু। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (234,2,227,'এবং তারা যদি তালাক দেয়ার দৃঢ়প্রত্যয় গ্রহণ করে, তবে আল্লাহ সর্বশ্রোতা ও সর্বজ্ঞ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (235,2,228,'তালাকপ্রাপ্তা নারীরা তিন মাসিক পর্যন্ত প্রতীক্ষা করবে এবং তাদের পক্ষে জায়িয নয় সে বস্তু গোপন করা যা তাদের পেটে আল্লাহ সৃজন করেছেন, যদি তারা আল্লাহ ও পরকালের প্রতি বিশ্বাস রাখে। তাদের স্বামীরা তাদেরকে উক্ত সময়ের মধ্যে পুনঃ গ্রহণে অধিক হকদার, যদি তারা আপোস-নিস্পত্তি করতে চায় এবং পুরুষদের উপর নারীদেরও হাক্ব আছে, যেমন নিয়ম অনুযায়ী পুরুষদের নারীদের উপরও হাক্ব আছে, অবশ্য নারীদের উপর পুরুষদের বিশেষ মর্যাদা আছে এবং আল্লাহ মহাপরাক্রান্ত, প্রজ্ঞাশীল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (236,2,229,'তালাক দুই দফা, অতঃপর হয় ভালভাবে পুনঃ গ্রহণ কিংবা সদ্ব্যবহার সহকারে বিদায় দান এবং তোমাদের পক্ষে তাদেরকে দেয়া মালের কিছুই ফিরিয়ে নেয়া জায়িয হবে না, কিন্তু যদি তারা উভয়ে আশঙ্কা করে যে তারা আল্লাহর নির্ধারিত সীমারেখা রক্ষা করে চলতে পারবে না (তাহলে অন্য ব্যবস্থা)।  অতঃপর  যদি  তোমরা  (উভয় পক্ষের শালিসগণ) আশঙ্কা কর যে উভয়পক্ষ আল্লাহর আইনসমূহ ঠিক রাখতে পারবে না, তাহলে উভয়ের প্রতি কোন গুনাহ নেই যদি কোন কিছুর বিনিময়ে স্ত্রী নিজেকে মুক্ত করতে চায়। এগুলো আল্লাহর আইন, কাজেই তোমরা এগুলোকে লঙ্ঘন করো না, আর যারা আল্লাহর আইনসমূহ লঙ্ঘন করবে, তারাই যালিম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (237,2,230,'অতঃপর যদি সে তাকে (চূড়ান্ত) তালাক দেয়, তবে এরপর সেই পুরুষের পক্ষে সেই স্ত্রী (বিবাহ) হালাল হবে না, যে পর্যন্ত না সে অন্য কাউকে স্বামী হিসেবে গ্রহণ করে। অতঃপর যদি সে তাকে তালাক দেয়, তবে উভয়ের পুনরায় মিলিত হওয়াতে গুনাহ নেই, যদি উভয়ের আস্থা জন্মে যে উভয়ে আল্লাহর আইনসমূহ ঠিক রাখতে পারবে। এসব আল্লাহর (আইন) সীমাসমূহ, এগুলোকে সেই লোকদের জন্য তিনি বর্ণনা করেন যারা জ্ঞানী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (238,2,231,'যখন তোমরা স্ত্রীদেরকে তালাক দাও এবং তাদের ইদ্দত পূর্ণ হয়ে আসে তখন হয় তাদেরকে ভালভাবে গ্রহণ করে রেখে দাও, নইলে ভালভাবে বিদায় দাও, আর বাড়াবাড়ি করে তাদের ক্ষতি করার উদ্দেশে আটকে রেখো না। যে এমন করবে, সে নিজেরই উপর যুলম করে। তোমরা আল্লাহর আহকামকে হাসি-ঠাট্টারূপে গ্রহণ করো না এবং তোমাদের প্রতি আল্লাহর অনুগ্রহ, তাঁর কিতাব ও জ্ঞানগর্ভ বাক্যাবলী (সুন্নাত) যা তিনি তোমাদের প্রতি নাযিল করেছেন, যদ্দবারা তিনি তোমাদেরকে উপদেশ দেন, তা স্মরণ কর আর আল্লাহকে ভয় কর আর জেনে রেখ, নিশ্চয়ই আল্লাহ সর্ববিষয়ে সর্বজ্ঞ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (239,2,232,'যখন তোমরা স্ত্রীদেরকে তালাক দাও, তারপর তাদের ইদ্দৎ পূর্ণ হয়ে যায়, সে অবস্থায় তারা স্বামীদের সাথে বিবাহ বন্ধনে আবদ্ধ হতে চাইলে তাদেরকে বাধা দিও না যখন তারা বৈধভাবে উভয়ে আপোষে সম্মত হয়। তোমাদের মধ্যে যে ব্যক্তি আল্লাহ ও শেষ দিনে বিশ্বাসী তাকে এ উপদেশ দেয়া হচ্ছে। এটা তোমাদের পক্ষে অতি বিশুদ্ধতা ও পবিত্রতার বিষয় এবং আল্লাহ্ই বিশেষরূপে জানেন, তোমরা জান না।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (240,2,233,'যে ব্যক্তি দুধপান কাল পূর্ণ করাতে ইচ্ছুক তার জন্য মায়েরা নিজেদের সন্তানদেরকে পূর্ণ দু’ বৎসরকাল স্তন্য দান করবে। জনকের উপর দায়িত্ব হল ভালভাবে তাদের অন্নবস্ত্রের ব্যবস্থা করা। কাউকেও সাধ্যের অতিরিক্ত হুকুম দেয়া হয় না, যেন মাকে তার সন্তানের জন্য এবং সন্তানের জন্মদাতাকে সন্তানের জন্য কষ্ট দেয়া না হয় এবং ওয়ারিশের প্রতিও একই রকম নির্দেশ, তৎপর যদি উভয়ের সম্মতি ও যুক্তিক্রমে দুধ ছাড়াতে ইচ্ছে করে, তবে তাদের প্রতি কোন গুনাহ নেই এবং যদি তোমরা স্বীয় সন্তানদেরকে কোন ধাত্রী দ্বারা দুধ পান করাতে ইচ্ছে কর, তবে তোমাদের প্রতি কোন গুনাহ নেই, যদি তোমরা যা দিতে চাচ্ছিলে তা যথারীতি আদায় করে দাও। আল্লাহকে ভয় কর এবং জেনে রেখ, তোমরা যা কিছুই কর, নিশ্চয়ই আল্লাহ তার সম্যক দ্রষ্টা। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (241,2,234,'তোমাদের মধ্য হতে যারা স্ত্রীদেরকে রেখে মারা যাবে সে অবস্থায় স্ত্রীরা নিজেদেরকে চার মাস দশ দিন বিরত রাখবে। তারপর যখন তাদের ইদ্দৎকাল পূর্ণ হবে, তখন তোমাদের নিজেদের সম্বন্ধে বৈধভাবে যা কিছু করবে তাতে তোমাদের কোন গুনাহ নেই। বস্তুতঃ তোমরা যা কিছু করছ, আল্লাহ সে বিষয়ে পরিজ্ঞাত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (242,2,235,'তোমাদের প্রতি গুনাহ নেই যদি তোমরা কথার ইশারায় নারীদেরকে বিবাহের প্রস্তাব পাঠাও, কিংবা নিজেদের মনে গোপন রাখ। আল্লাহ অবগত আছেন যে, ঐ স্ত্রীলোকদের সাথে তোমাদের বিবাহ করার খেয়াল সত্বরই জাগবে, কিন্তু তাদের সাথে গোপন অঙ্গীকার করো না, কিন্তু বৈধভাবে কথাবার্তা বলতে পার এবং তোমরা বিবাহ সম্পাদনের সংকল্প করো না যে পর্যন্ত ইদ্দৎ পূর্ণ না হয় এবং জেনে রেখ, আল্লাহ নিশ্চয়ই তোমাদের মনোভাব জ্ঞাত আছেন, সুতরাং তাঁকে ভয় কর এবং জেনে রেখ, নিশ্চয়ই আল্লাহ ক্ষমাশীল, পরম সহিষ্ণু। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (243,2,236,'তোমাদের প্রতি কোন গুনাহ নেই, যদি তোমরা স্ত্রীদেরকে স্পর্শ না ক’রে, কিংবা তাদের মহর ধার্য না করে তালাক দাও এবং তোমরা স্ত্রীদের জন্য খরচের সংস্থান করবে, অবস্থাপন্ন ব্যক্তি তার সাধ্যমত এবং অবস্থাহীন ব্যক্তি তার সাধ্যমত বিধি অনুযায়ী খরচপত্রের ব্যবস্থা করবে, পুণ্যবানদের উপর এটা দায়িত্ব। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (244,2,237,'যদি তোমরা তাদেরকে স্পর্শ করার পূর্বে তালাক দাও, অথচ তাদের মহর ধার্য করা হয়, সে অবস্থায় ধার্যকৃত মহরের অর্ধেক, কিন্তু যদি স্ত্রীরা দাবী মাফ করে দেয় কিংবা যার হাতে বিয়ের বন্ধন আছে সে মাফ করে দেয়, বস্তুতঃ ক্ষমা করাই তাকওয়ার অধিক নিকটবর্তী এবং তোমরা পারস্পরিক সহায়তা হতে বিমুখ হয়ো না, যা কিছু তোমরা করছ আল্লাহ নিশ্চয়ই তার সম্যক দ্রষ্টা। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (245,2,238,'তোমরা সলাতের প্রতি যত্নবান হও, বিশেষ করে মধ্যবর্তী সলাতের প্রতি এবং আল্লাহর সামনে বিনীতভাবে দন্ডায়মান হও। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (246,2,239,'যদি তোমরা ভয় কর, তবে পদচারী কিংবা আরোহী অবস্থায়ই নামায আদায় করবে। যখন নিরুদ্বেগ হবে, তখন আল্লাহকে স্মরণ কর যেভাবে আল্লাহ তোমাদেরকে শিক্ষা দিয়েছেন, যা তোমরা জানতে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (247,2,240,'তোমাদের মধ্যে যারা বিবিদেরকে রেখে মারা যাবে, তারা বিবিদের জন্য অসিয়ত করবে যেন এক বৎসরকাল সুযোগ-সুবিধা পায় এবং গৃহ হতে বের ক’রে দেয়া না হয়, তবে যদি তারা নিজেরাই বের হয়ে যায়, তবে তোমাদের প্রতি গুনাহ নেই তারা নিজেদের ব্যাপারে বৈধভাবে কিছু করলে; আল্লাহ মহাশক্তিধর, সুবিজ্ঞ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (248,2,241,'তালাকপ্রাপ্তা নারীদের সঙ্গতভাবে ভরণ-পোষণ করা মুত্তাকীদের কর্তব্য। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (249,2,242,'তোমাদের জন্য আল্লাহ নিজের আহকাম এমনভাবে ব্যাখ্যা করে বর্ণনা করছেন, যাতে তোমরা বুঝতে পার। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (250,2,243,'তুমি কি সেই লোকদের প্রতি লক্ষ্য করনি, যারা মৃত্যুকে এড়াবার জন্য নিজেদের ঘর থেকে হাজারে হাজারে বের হয়ে গিয়েছিল, তখন আল্লাহ তাদেরকে বললেন, ‘তোমাদের মৃত্যু হোক’। তৎপর তাদেরকে জীবিত করে উঠালেন। নিশ্চয়ই আল্লাহ লোকেদের প্রতি দয়াশীল কিন্তু অধিকাংশ লোক শোকর করে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (251,2,244,'তোমরা আল্লাহর পথে যুদ্ধ কর এবং জেনে রেখ, আল্লাহ নিশ্চয়ই সর্বশ্রোতা, সর্বজ্ঞ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (252,2,245,'এমন ব্যক্তি কে আছে যে আল্লাহকে উত্তম কর্জ প্রদান করবে? তাহলে তার সেই কর্জকে তার জন্য আল্লাহ বহু গুণ বর্ধিত করে দেবেন এবং আল্লাহই সীমিত ও প্রসারিত ক’রে থাকেন এবং তাঁর দিকেই তোমরা ফিরে যাবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (253,2,246,'তুমি কি মূসার পরবর্তী বানী ইসরাঈলের প্রধানদের প্রতি লক্ষ্য করনি? তারা তাদের নাবীকে বলেছিল, ‘আমাদের জন্য একজন বাদশাহ ঠিক করুন, যাতে আমরা আল্লাহর পথে জিহাদ করি’। নির্দেশ হল, ‘এমন সম্ভাবনা আছে কি যে, যদি তোমাদের প্রতি জিহাদের হুকুম দেয়া হয় তবে তোমরা জিহাদ করবে না’? তারা বলল, ‘আমরা কী ওজরে আল্লাহর পথে জিহাদ করব না, যখন আমরা আমাদের গৃহ ও সন্তানাদি হতে বহিস্কৃত হয়েছি’। অতঃপর যখন তাদের প্রতি জিহাদের হুকুম হল, তখন তাদের অল্প সংখ্যক লোক ছাড়া সকলেই ফিরে দাঁড়াল এবং আল্লাহ যালিমদেরকে খুব ভালভাবেই জানেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (254,2,247,'তাদেরকে তাদের নাবী বলল, আল্লাহ যথার্থই তালুতকে তোমাদের বাদশাহ ঠিক করেছেন। তারা বলল, ‘আমাদের উপর কী প্রকারে তার রাজক্ষমতা মিলতে পারে যখন তার চেয়ে আমরাই রাজশক্তির অধিক যোগ্যপাত্র আর তাকে আর্থিক স্বচ্ছলতাও প্রদান করা হয়নি’! নাবী বলল, আল্লাহ তাকেই তোমাদের উপর পছন্দ করেছেন এবং তাকে জ্ঞানে ও দেহে সমৃদ্ধ করেছেন, আল্লাহ যাকে ইচ্ছে নিজের রাজ্য দান করেন, বস্তুতঃ আল্লাহ পর্যাপ্ত দাতা ও প্রজ্ঞাময়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (255,2,248,'তাদের নাবী তাদেরকে বলল, তালুতের বাদশাহ হওয়ার নিদর্শন এই যে, তোমাদের নিকট সিন্দুক আসবে, যার মধ্যে তোমাদের প্রতিপালকের শান্তি বাণী রয়েছে এবং সেই অবশিষ্ট জিনিস, যা মূসা ও হারূন সম্প্রদায় রেখে গেছে, ওটা ফেরেশতাগণ উঠিয়ে আনবে, এতে তোমাদের জন্য নিদর্শন আছে যদি তোমরা মু’মিন হও। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (256,2,249,'অতঃপর যখন তালুত সৈন্যসহ রওয়ানা হল, বলল, ‘আল্লাহ একটা নদী দ্বারা তোমাদের পরীক্ষা করবেন, মূলতঃ যে কেউ ওটার পানি পান করবে সে ব্যক্তি আমার দলভুক্ত নয় আর যে তা খাবে না, সে নিশ্চয়ই আমার দলভুক্ত হবে, কিন্তু যে এক অঞ্জলি পানি নিবে সেও (আমার দলভুক্ত)’। অতঃপর অতি অল্প সংখ্যক ছাড়া তারা সকলেই তাত্থেকে পান করল। এরপর তালুত এবং তার সাথী মু’মিনগণ নদী পার হয়ে বলল, ‘আজ জ্বালুত ও তার সৈন্যদের সাথে যুদ্ধ করার শক্তি আমাদের নেই। কিন্তু যাদের এ ধারণা ছিল যে, তাদেরকে আল্লাহর সামনে হাজির হতে হবে তারা বলল, ‘আল্লাহর হুকুমে বহু ক্ষেত্রে ক্ষুদ্র দল বৃহৎ দলের উপর জয়যুক্ত হয়েছে’। আল্লাহ ধৈর্যশীলদের সঙ্গে আছেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (257,2,250,'যখন তারা জ্বালুত ও তার সৈন্যদের সম্মুখীন হল, তখন বলল, ‘হে আমাদের প্রতিপালক! আমাদেরকে ধৈর্যধারণের শক্তি দান কর এবং আমাদের পদগুলো দৃঢ় রেখ এবং কাফির দলের উপর আমাদেরকে জয়যুক্ত কর’। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (258,2,251,'অতঃপর তারা আল্লাহর হুকুমে তাদেরকে (শত্রুদেরকে) পরাজিত করল এবং দাঊদ জ্বালুতকে কতল করল এবং আল্লাহ দাঊদকে রাজ্য ও হেকমত দান করলেন এবং তাকে শিক্ষা দিলেন যা ইচ্ছে। যদি আল্লাহ মানবজাতির একদলকে অন্যদল দ্বারা প্রতিহত না করতেন, তাহলে পৃথিবী বিপর্যস্ত হয়ে যেত, কিন্তু আল্লাহ সর্বজগতের প্রতি কৃপালু। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (259,2,252,'এসব আল্লাহরই আয়াত, যা আমি সঠিকভাবে তোমাকে পড়িয়ে শুনাচ্ছি এবং নিশ্চয়ই তুমি রসূলদের অন্তর্ভুক্ত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (260,2,253,'এ রসূলগণ এরূপ যে, তাদের মধ্যে কাউকে অন্য কারও উপর শ্রেষ্ঠত্ব দিয়েছি। তাদের মধ্যে কেউ এমন আছে যে, তাদের সাথে আল্লাহ কথা বলেছেন এবং তাদের কাউকে পদমর্যাদায় উচ্চ করেছেন। আমি মারইয়ামের পুত্র ঈসাকে সুস্পষ্ট দলীলসমূহ প্রদান করেছি, রূহুল কুদুস (জিবরাঈল) দ্বারা সাহায্য করেছি এবং আল্লাহ যদি ইচ্ছে করতেন, তাহলে তাদের পরবর্তীরা তাদের নিকট সুস্পষ্ট দলীল পৌঁছার পর পরস্পর যুদ্ধ বিগ্রহ করত না, কিন্তু তারা পরস্পর মতভেদ সৃষ্টি করল, তাদের কেউ কেউ ঈমান আনল এবং কেউ কেউ কুফরী করল, আল্লাহ যদি ইচ্ছে করতেন, তাহলে তারা যুদ্ধবিগ্রহ করত না, কিন্তু আল্লাহ যা ইচ্ছে করেন, তা-ই করে থাকেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (261,2,254,'হে ঈমানদারগণ! আমার দেয়া জীবিকা থেকে খরচ কর সেদিন আসার পূর্বে যেদিন কোন বিক্রয়, বন্ধুত্ব এবং সুপারিশ কাজে আসবে না। বস্তুতঃ কাফিরগণই অত্যাচারী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (262,2,255,'আল্লাহ, তিনি ছাড়া সত্যিকারের কোন উপাস্য নেই, তিনি চিরঞ্জীব, সর্বদা রক্ষণাবেক্ষণকারী। তাঁকে তন্দ্রা ও নিদ্রা স্পর্শ করে না। আকাশমন্ডলে ও ভূমন্ডলে যা কিছু আছে, তাঁরই। কে সেই ব্যক্তি যে তাঁর অনুমতি ছাড়া তাঁর নিকট সুপারিশ করে? তিনি লোকদের সমুদয় প্রকাশ্য ও অপ্রকাশ্য অবস্থা জানেন। পক্ষান্তরে মানুষ তাঁর জ্ঞানের কোনকিছুই আয়ত্ত করতে সক্ষম নয়, তিনি যে পরিমাণ ইচ্ছে করেন সেটুকু ছাড়া। তাঁর কুরসী আকাশ ও পৃথিবী পরিবেষ্টন করে আছে এবং এ দু’য়ের রক্ষণাবেক্ষণ তাঁকে ক্লান্ত করে না, তিনি উচ্চ মর্যাদাশীল, মহান। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (263,2,256,'দীনের মধ্যে জবরদস্তির অবকাশ নেই, নিশ্চয় হিদায়াত গোমরাহী হতে সুস্পষ্ট হয়ে গেছে। কাজেই যে ব্যক্তি মিথ্যে মা’বুদদেরকে (তাগুতকে) অমান্য করল এবং আল্লাহর প্রতি ঈমান আনল, নিশ্চয়ই সে দৃঢ়তর রজ্জু ধারণ করল যা ছিন্ন হওয়ার নয়। আল্লাহ সর্বশ্রোতা এবং সর্বজ্ঞাতা। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (264,2,257,'আল্লাহ মু’মিনদের অভিভাবক, তাদেরকে অন্ধকার থেকে আলোর দিকে বের করে আনেন এবং কাফিরদের অভিভাবক হচ্ছে তাগুত, সে তাদেরকে আলো থেকে অন্ধকারের দিকে নিয়ে যায়। এরাই আগুনের বাসিন্দা, এরা চিরকাল সেখানে থাকবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (265,2,258,'তুমি কি সেই ব্যক্তির ঘটনা সম্পর্কে চিন্তা কর নি, যে ইবরাহীমের সঙ্গে তার প্রতিপালক সম্বন্ধে তর্ক করেছিল, যেহেতু আল্লাহ তাকে রাজত্ব দান করেছিলেন। ইবরাহীম তাকে যখন বলল, ‘আমার প্রতিপালক তিনিই, যিনি জীবিত করেন এবং মৃত্যু ঘটান’। সে বলল, ‘আমিও জীবিত করি এবং মৃত্যু ঘটাই’। ইবরাহীম বলল, ‘আল্লাহ সূর্যকে পূর্ব দিক থেকে উদিত করেন, তুমি তাকে পশ্চিম দিক থেকে উদিত কর’। তখন সেই কাফিরটি হতভম্ব হয়ে গেল। বস্তুতঃ আল্লাহ যালিমদেরকে সুপথ দেখান না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (266,2,259,'কিংবা এমন ব্যক্তির ঘটনা সম্পর্কে (তুমি কি চিন্তা করনি) যে এক নগর দিয়ে এমন অবস্থায় যাচ্ছিল যে তা উজাড় অবস্থায় ছিল। সে বলল, ‘আল্লাহ এ নগরীকে এর মৃত্যুর পরে কীভাবে জীবিত করবেন’? তখন আল্লাহ তাকে একশ’ বছর মৃত রাখলেন। তারপর তাকে জীবিত করে তুললেন ও জিজ্ঞেস করলেন, ‘তুমি এ অবস্থায় কতকাল ছিলে’? সে বলল, ‘একদিন ছিলাম কিংবা একদিন হতেও কম’। আল্লাহ বললেন, ‘বরং তুমি একশ’ বছর ছিলে, এক্ষণে তুমি তোমার খাদ্যের ও পানীয়ের দিকে লক্ষ্য কর, এটা পচে যায়নি। আর গাধাটার দিকে তাকিয়ে দেখ, আর এতে উদ্দেশ্য এই যে, আমি তোমাকে মানুষের জন্য উদাহরণ করব। আবার তুমি হাড়গুলোর দিকে লক্ষ্য কর, আমি কীভাবে ওগুলো জোড়া লাগিয়ে দেই, তারপর গোশত দ্বারা ঢেকে দেই। এরপর যখন তার কাছে স্পষ্ট হয়ে গেল, তখন সে বলল, ‘এখন আমি পূর্ণ বিশ্বাস করছি যে, আল্লাহ্ই সর্ববিষয়ে সর্বশক্তিমান’। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (267,2,260,'যখন ইবরাহীম বলেছিল, ‘হে আমার প্রতিপালক! তুমি মৃতকে কীরূপে জীবিত করবে আমাকে দেখাও’। আল্লাহ বললেন, ‘তুমি কি বিশ্বাস কর না’? সে আরয করল, ‘নিশ্চয়ই, তবে যাতে আমার অন্তঃকরণ স্বস্তি লাভ করে (এজন্য তা দেখতে চাই)’। আল্লাহ বললেন, তাহলে চারটি পাখী নাও এবং তাদেরকে বশীভূত কর। তারপর ওদের এক এক টুকরো প্রত্যেক পাহাড়ের উপর রেখে দাও, অতঃপর সেগুলোকে ডাক দাও, তোমার নিকট দৌড়ে আসবে। জেনে রেখ যে, নিশ্চয় আল্লাহ মহাপরাক্রমশালী, প্রজ্ঞাময়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (268,2,261,'যারা আল্লাহর পথে নিজেদের মাল ব্যয় করে, তাদের (দানের) তুলনা সেই বীজের মত, যাত্থেকে সাতটি শীষ জন্মিল, প্রত্যেক শীষে একশত করে দানা এবং আল্লাহ যাকে ইচ্ছে করেন, বর্ধিত হারে দিয়ে থাকেন। বস্তুতঃ আল্লাহ প্রাচুর্যের অধিকারী, জ্ঞানময়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (269,2,262,'যারা আল্লাহর পথে নিজেদের ধন ব্যয় ক’রে নিজেদের দানের কথা মনে করিয়ে দেয় না আর (দান গ্রহীতাকে) কষ্ট দেয় না, তাদের প্রতিদান তাদের প্রতিপালকের নিকট নির্ধারিত আছে, তাদের কোন ভয় নেই, মর্মপীড়াও নেই। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (270,2,263,'যে দানের পর কষ্ট দেয়া হয় তার চেয়ে ভাল কথা ও ক্ষমা উত্তম; বস্তুতঃ আল্লাহ অভাবমুক্ত ও পরম সহিষ্ণু। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (271,2,264,'হে ঈমানদারগণ! দানের কথা মনে করিয়ে দিয়ে ও কষ্ট দিয়ে তোমরা নিজেদের দান-খয়রাতকে সে ব্যক্তির ন্যায় ব্যর্থ করে দিও না যে নিজের ধন লোক দেখানোর জন্য ব্যয় করে থাকে, অথচ সে আল্লাহ ও পরকালে বিশ্বাসী নয়। তার তুলনা সেই মসৃণ পাথরের মত, যাতে সামান্য কিছু মাটি আছে, অতঃপর প্রবল বৃষ্টিপাত তাকে পরিষ্কার করে ফেলে। তারা স্বীয় কৃত কার্যের ফল কিছুই পাবে না; আল্লাহ কাফিরদেরকে পথপ্রদর্শন করেন না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (272,2,265,'যারা আল্লাহর সন্তুষ্টি সাধন ও নিজেদের মনে (ঈমানের) দৃঢ়তা সৃষ্টির উদ্দেশে নিজেদের ধন ব্যয় করে থাকে তাদের তুলনা সেই বাগানের  ন্যায়  যা  উচ্চভূমিতে অবস্থিত, তাতে মুষলধারে বৃষ্টিপাতের ফলে দ্বিগুণ ফল ধরে, যদি তাতে বৃষ্টিপাত নাও হয়, তবে শিশির বিন্দুই যথেষ্ট, তোমরা যা কিছুই কর, আল্লাহ তার সম্যক দ্রষ্টা। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (273,2,266,'তোমাদের কেউ কি পছন্দ করে যে, তার এমন একটা খেজুর ও আঙ্গুরের বাগান হোক, যার নীচ দিয়ে ঝর্ণাধারা প্রবাহিত, তার জন্য তাতে সব রকম ফল আছে, আর তার বার্ধক্যও সমুপস্থিত, তার কতকগুলো সন্তান-সন্ততি আছে যারা কাজকর্মের লায়েক নয়, এ অবস্থায় বাগানের উপর অগ্নি হাওয়া বয়ে গেল, যার ফলে সেটি জ্বলে গেল? আল্লাহ তোমাদের জন্য নিদর্শনসমূহ এভাবে বর্ণনা করছেন, যাতে তোমরা চিন্তা করে দেখ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (274,2,267,'হে মু’মিনগণ! তোমাদের উপার্জিত উত্তম সম্পদ থেকে এবং তোমাদের জন্য ভূমি থেকে যা উৎপন্ন করেছি তাত্থেকে ব্যয় কর এবং নিকৃষ্ট বস্তু ব্যয় করার নিয়ত করো না, বস্তুতঃ তোমরা তা গ্রহণ কর না, যদি না তোমাদের চক্ষু বন্ধ করে থাক। আর জেনে রেখ, আল্লাহ অমুখাপেক্ষী, প্রশংসিত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (275,2,268,'শয়ত্বান তোমাদেরকে গরীব হয়ে যাওয়ার ভয় দেখায় এবং লজ্জাকর বিষয়ের নির্দেশ দেয় এবং আল্লাহ নিজ পক্ষ হতে তোমাদের সাথে ক্ষমার ও অনুগ্রহের ওয়াদা করছেন এবং আল্লাহ প্রাচুর্যের অধিকারী, মহাজ্ঞানী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (276,2,269,'যাকে ইচ্ছে তিনি হিকমাত দান করেন এবং যে ব্যক্তি এ জ্ঞানপ্রাপ্ত হয়, নিঃসন্দেহে সে মহাসম্পদ প্রাপ্ত হয় এবং উপদেশ তারাই গ্রহণ করে, যারা জ্ঞানী।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (277,2,270,'তোমরা যে ব্যয়ই কর কিংবা যে কোন মানৎ কর, আল্লাহ নিশ্চয়ই তা জানেন কিন্তু যালিমদের জন্য কোন সাহায্যকারী নেই। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (278,2,271,'যদি তোমরা প্রকাশ্যে দান কর তবে তাও উত্তম, আর যদি তোমরা তা গোপনে কর এবং তা অভাবগ্রস্তদেরকে দান কর, তবে তা তোমাদের জন্য আরো উত্তম, অধিকন্তু তিনি তোমাদের কিছু গুনাহ মোচন করে দেবেন, বস্তুতঃ যা কিছু তোমরা করছ, আল্লাহ তার খবর রাখেন।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (279,2,272,'তাদেরকে ঠিক পথে নিয়ে আসা তোমার দায়িত্ব নয়, বরং আল্লাহ যাকে ইচ্ছে ঠিক পথে পরিচালিত করেন, বস্তুতঃ তোমরা যা কিছু ব্যয় কর, তা তোমাদের নিজেদের জন্যই এবং তোমরা তো শুধু আল্লাহর সন্তুষ্টির জন্যই ব্যয় করে থাক এবং যা কিছু তোমরা মাল হতে ব্যয় করবে, তোমাদেরকে তার ফল পুরোপুরি দেয়া হবে এবং তোমাদের প্রতি অন্যায় করা  হবে না।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (280,2,273,'ওটা সেই  অভাবগ্রস্তদের  প্রাপ্য যারা আল্লাহর পথে আবদ্ধ আছে, দেশময় ঘুরে বেড়াতে পারে না, ভিক্ষাবৃত্তি অবলম্বী না হওয়ার কারণে অজ্ঞ লোকেরা তাদেরকে অভাবমুক্ত মনে করে, তাদের চেহারা দেখে তুমি তাদেরকে চিনতে পারবে। তারা লোকেদের কাছে নাছোড় হয়ে ভিক্ষা করে না এবং তোমাদের মাল হতে যা কিছু ব্যয় করবে, নিশ্চয়ই আল্লাহ সে সম্পর্কে সুপরিজ্ঞাত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (281,2,274,'যারা নিজেদের মাল রাতে ও দিনে, প্রকাশ্যে অপ্রকাশ্যে ব্যয় করে থাকে, তাদের জন্য সেই দানের সওয়াব তাদের প্রতিপালকের নিকট রয়েছে এবং তাদের কোন ভয় নেই, তারা চিন্তিতও হবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (282,2,275,'যারা সূদ খায়, তারা সেই লোকের মত দাঁড়াবে যাকে শয়ত্বান স্পর্শ দ্বারা বেহুশ করে দেয়, এ শাস্তি এজন্য যে, তারা বলে, ‘ক্রয়-বিক্রয় সূদের মতই’, অথচ কারবারকে আল্লাহ হালাল করেছেন এবং তিনি সূদকে হারাম করেছেন। সুতরাং যার নিকট তার প্রতিপালকের পক্ষ হতে উপদেশবাণী পৌঁছল এবং সে বিরত হল, পূর্বে যা (সূদের আদান-প্রদান) হয়ে গেছে, তা তারই, তার বিষয় আল্লাহর জিম্মায় এবং যারা আবার আরম্ভ করবে তারাই অগ্নির বাসিন্দা, তারা তাতে চিরকাল থাকবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (283,2,276,'আল্লাহ সূদকে বিলুপ্ত করেন এবং খয়রাতকে বৃদ্ধি করেন, আল্লাহ অকৃতজ্ঞ পাপীদেরকে ভালবাসেন না।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (284,2,277,'যারা ঈমান আনে, সৎকাজ করে, নামায কায়িম করে এবং যাকাত আদায় করে, তাদের জন্য তাদের প্রতিপালকের নিকট সওয়াব নির্ধারিত আছে। তাদের কোন ভয় নেই, তারা চিন্তিতও না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (285,2,278,'হে মু’মিনগণ! তোমরা আল্লাহকে ভয় কর এবং বাকী সূদ ছেড়ে দাও, যদি তোমরা ঈমানদার হও। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (286,2,279,'অতঃপর যদি না ছাড় তবে আল্লাহ ও তাঁর রসূলের নিকট হতে যুদ্ধের ঘোষণা শুনে লও। কিন্তু যদি তোমরা তাওবাহ কর, তবে তোমরা তোমাদের মূলধন পাবে, এতে তোমাদের দ্বারা অত্যাচার হবে না, আর তোমরাও অত্যাচারিত হবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (287,2,280,'যদি সে (ঋণ গ্রহণকারী) দরিদ্র হয়, তবে স্বচ্ছল অবস্থা আসা পর্যন্ত অবকাশ দিবে আর মাফ করে দেয়া তোমাদের পক্ষে অতি উত্তম, যদি তোমরা জানতে! ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (288,2,281,'তোমরা সেদিনের ভয় কর, যেদিন তোমাদেরকে আল্লাহর দিকে ফিরিয়ে নেয়া হবে। অতঃপর প্রত্যেক লোককে তার কৃতকর্মের বিনিময় দেয়া হবে এবং তারা কিছুমাত্র অত্যাচারিত হবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (289,2,282,'হে বিশ্বাসীগণ! যখন তোমরা নির্দিষ্ট সময়ের জন্য ধারে কারবার করবে, তখন তা লিখে রাখবে, তোমাদের মধ্যে যেন কোন একজন লেখক ন্যায্যভাবে লিখে দেয়, লেখক যেন লিখতে অস্বীকার না করে, যেরূপ আল্লাহ তাকে শিক্ষা দিয়েছেন, কাজেই সে যেন লিখে এবং কর্জ-গ্রহীতা যেন লেখার বিষয়বস্তু বলে দেয় এবং তার প্রতিপালক আল্লাহর ভয় রাখে এবং প্রাপ্য হতে যেন কোনও প্রকারের কাটছাঁট না করে। যদি কর্জ-গ্রহীতা স্বল্প-বুদ্ধি অথবা দুর্বল কিংবা লেখার বিষয়বস্তু বলতে অক্ষম হয়, তবে তার অভিভাবক যেন লেখার বিষয়বস্তু ন্যায্যভাবে বলে দেয় এবং তোমাদের আপন পুরুষ লোকের মধ্য হতে দু’জন সাক্ষী রাখ, যদি দু’জন পুরুষ না পাওয়া যায়, তাহলে একজন পুরুষ ও দু’জন স্ত্রী লোক, যাদের সাক্ষ্য সম্পর্কে তোমরা রাজী আছ, এটা এজন্য যে, যদি একজন ভুলে যায় তবে অন্যজন স্মরণ করিয়ে দেবে এবং যখন সাক্ষীগণকে ডাকা হবে, তখন যেন (সাক্ষ্য দিতে) অস্বীকার না করে। ছোট হোক বা বড় হোক তোমরা নির্দিষ্ট মেয়াদসহ লিখে রাখাকে তাচ্ছিল্যভরে উপেক্ষা করো না, এ লিখে রাখা আল্লাহর নিকট ইনসাফ বজায় রাখার জন্য দৃঢ়তর, সঠিক প্রমাণের জন্য সহজতর এবং তোমরা যাতে কোনও সন্দেহে পতিত না হও এর নিকটবর্তী। কিন্তু যদি কোন সওদা তোমরা পরস্পর নগদ নগদ সম্পাদন কর, তবে না লিখলেও তোমাদের কোন দোষ নেই। আর তোমরা যখন পরস্পর কেনাবেচা কর তখন সাক্ষী রেখ। কোনও লেখক ও সাক্ষীকে যেন কষ্ট দেয়া না হয় এবং যদি এরূপ কর, তাহলে তোমাদের গুনাহ হবে। কাজেই আল্লাহকে ভয় কর এবং আল্লাহ তোমাদেরকে শিক্ষা দিচ্ছেন এবং আল্লাহ সর্ববিষয়ে সুপরিজ্ঞাত।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (290,2,283,'যদি তোমরা সফরে থাক এবং কোন লেখক না পাও, তাহলে বন্ধক রাখার জিনিসগুলো অন্যের দখলে রাখতে হবে, যদি তোমরা পরস্পর পরস্পরকে বিশ্বাস কর তাহলে যাকে বিশ্বাস করা হয়েছে সে যেন আমানত ফিরিয়ে দেয় এবং তার প্রতিপালক আল্লাহকে ভয় করে। তোমরা সাক্ষ্য গোপন করো না, যে ব্যক্তি তা গোপন করে, তার অন্তর পাপী। তোমরা যা কর আল্লাহ সে সম্পর্কে বিশেষভাবে অবহিত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (291,2,284,'যা কিছু আকাশসমূহে ও ভূমন্ডলে আছে, সবকিছু আল্লাহরই। তোমাদের অন্তরে যা আছে, তা তোমরা প্রকাশ কর কিংবা গোপন কর আল্লাহ তার হিসাব তোমাদের নিকট হতে গ্রহণ করবেন। সুতরাং যাকে ইচ্ছে  তিনি  ক্ষমা করবেন এবং  যাকে ইচ্ছে তিনি শাস্তি দিবেন। আল্লাহ সর্ববিষয়ে ক্ষমতাবান। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (292,2,285,'রসূল (সাঃ) তার প্রতিপালকের পক্ষ হতে যা তার প্রতি অবতীর্ণ হয়েছে তাতে ঈমান এনেছে এবং মু’মিনগণও। তারা সবাই আল্লাহর উপর, তাঁর ফেরেশতাদের উপর, তাঁর কিতাবসমূহের উপর এবং রসূলগণের উপর বিশ্বাস স্থাপন করেছে, (তারা বলে), ‘আমরা রসূলগণের মধ্যে কারও ব্যাপারে তারতম্য করি না’ এবং তারা এ কথাও বলে যে, ‘আমরা শুনেছি এবং মেনে নিয়েছি। হে আমাদের প্রতিপালক! আমাদেরকে ক্ষমা কর আর প্রত্যাবর্তন তোমারই দিকে’। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (293,2,286,'আল্লাহ কোন ব্যক্তির উপর তার সাধ্যের অতিরিক্ত কিছু আরোপ করেন না, সে ভাল যা করেছে সে তার সওয়াব পাবে এবং স্বীয় মন্দ কৃতকর্মের জন্য সে নিজেই নিগ্রহ ভোগ করবে। হে আমাদের প্রতিপালক! আমরা যদি ভুলে যাই কিংবা ভুল করি, তাহলে আমাদেরকে পাকড়াও করো না, হে আমাদের প্রতিপালক! আমাদের আগের লোকেদের উপর যেমন গুরু-দায়িত্ব অর্পণ করেছিলে, আমাদের উপর তেমন দায়িত্ব অর্পণ করো না; হে আমাদের প্রতিপালক! যে ভার বহনের ক্ষমতা আমাদের নেই, এমন ভার আমাদের উপর চাপিয়ে দিও না, (ভুল-ত্রুটি উপেক্ষা করে) আমাদেরকে রেহাই দাও, আমাদেরকে ক্ষমা কর এবং আমাদের প্রতি দয়া কর; তুমিই আমাদের প্রতিপালক, কাজেই আমাদেরকে কাফিরদের উপর জয়যুক্ত কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (294,3,1,'আলিফ-লাম-মীম।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (295,3,2,'আল্লাহ, তিনি ছাড়া সত্যিকারের কোন ইলাহ নেই, তিনি চিরঞ্জীব সকলের রক্ষণাবেক্ষণকারী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (296,3,3,'তিনি সত্য সহকারে তোমার উপর কিতাব অবতীর্ণ করেছেন, যা পূর্বতন কিতাবের সমর্থক এবং তিনি তাওরাত ও ইঞ্জীল অবতীর্ণ করেছেন ।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (297,3,4,'ইতোপূর্বে মানবজাতির পথ প্রদর্শনের জন্য; আর তিনি সেই মানদন্ড নাযিল করেছেন যা হাক্ব ও বাতিলের পার্থক্য দেখিয়ে দেয়; নিশ্চয়ই যারা আল্লাহর আয়াতের সাথে কুফুরী করে, তাদের জন্য কঠিন শাস্তি রয়েছে। আল্লাহ মহাপরাক্রমশালী, দন্ডদাতা। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (298,3,5,'নিশ্চয়ই ভূমন্ডলের ও নভোমন্ডলের কোন জিনিসই আল্লাহর নিকট গোপন থাকে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (299,3,6,'তিনিই তোমাদেরকে মায়ের পেটে যেভাবে ইচ্ছে আকৃতি দেন, তিনি ছাড়া সত্যিকারের কোন ইলাহ নেই, তিনি মহাশক্তিমান ও প্রজ্ঞাশীল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (300,3,7,'তিনিই তোমার উপর এমন কিতাব নাযিল করেছেন, যার কতিপয় আয়াত মৌলিক-সুস্পষ্ট অর্থবোধক, এগুলো হল কিতাবের মূল আর অন্যগুলো পুরোপুরি স্পষ্ট নয়; কিন্তু যাদের অন্তরে বক্রতা আছে, তারা গোলযোগ সৃষ্টির উদ্দেশে এবং ভুল ব্যাখ্যার উদ্দেশে উক্ত আয়াতগুলোর অনুসরণ করে যেগুলো পুরোপুরি স্পষ্ট নয়। মূলত: এর মর্ম আল্লাহ ছাড়া কেউই জানে না। যারা জ্ঞানে সুগভীর তারা বলে যে, আমরা তার উপর ঈমান এনেছি, এ সবকিছুই আমাদের প্রতিপালকের নিকট হতে এসেছে, মূলতঃ জ্ঞানবান ব্যক্তিরা ছাড়া কেউই নসীহত গ্রহণ করে না।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (301,3,8,'হে আমাদের প্রতিপালক! সৎ পথ প্রদর্শনের পরে তুমি আমাদের অন্তরগুলোকে বক্র করে দিও না, আমাদেরকে তোমার নিকট হতে রহমত প্রদান কর, মূলতঃ তুমিই মহান দাতা। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (302,3,9,'হে আমাদের প্রতিপালক! নিশ্চয়ই তুমি সমুদয় মানুষকে একদিন সমবেত করবে, যাতে কোনও সন্দেহ নাই। নিশ্চয়ই আল্লাহ অঙ্গীকারের খেলাফ করেন না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (303,3,10,'নিশ্চয়ই যারা কুফরী করে তাদের ধন-সম্পদ ও সন্তান-সন্ততি আল্লাহর সামনে কোন কাজে লাগবে না এবং তারাই আগুনের ইন্ধন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (304,3,11,'তাদের স্বভাব ফেরাওনী দল এবং তাদের আগের লোকেদের মত যারা আমার আয়াতগুলোকে মিথ্যা প্রতিপন্ন করেছে; সুতরাং আল্লাহ তাদের গুনাহের দরুন তাদেরকে পাকড়াও করলেন, আল্লাহ শাস্তি দানে অত্যন্ত কঠোর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (305,3,12,'যারা কুফরী করে তাদেরকে বলে দাও, ‘তোমরা অচিরেই পরাজিত হবে আর তোমাদেরকে জাহান্নামের দিকে হাঁকানো হবে, ওটা কতই না নিকৃষ্ট আবাসস্থান’! ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (306,3,13,'তোমাদের জন্য অবশ্যই নিদর্শন আছে সেই দু’দল সৈন্যের মধ্যে যারা পরস্পর প্রতিদ্বন্দীরূপে দাঁড়িয়েছিল (বদর প্রান্তরে)। একদল আল্লাহর পথে যুদ্ধ করেছিল এবং অপরদল ছিল কাফির, কাফিররা মুসলিমদেরকে প্রকাশ্য চোখে দ্বিগুণ দেখছিল। আল্লাহ যাকে ইচ্ছে স্বীয় সাহায্যের দ্বারা শক্তিশালী করে থাকেন, নিশ্চয়ই এতে দৃষ্টিমানদের জন্য শিক্ষা রয়েছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (307,3,14,'মানুষের কাছে সুশোভিত করা হয়েছে নারী, সন্তান, স্ত্তপীকৃত স্বর্ণ ও রৌপ্যভান্ডার, চিহ্নযুক্ত অশ্বরাজি, গৃহপালিত পশু এবং শস্যক্ষেত্র, এসব পার্থিব জীবনের সম্পদ, আর আল্লাহ -তাঁরই নিকট রয়েছে উত্তম আশ্রয়স্থল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (308,3,15,'বল, আমি কি তোমাদেরকে এ সব হতেও অতি উত্তম কোন কিছুর সংবাদ দেব? যারা মুত্তাকী তাদের জন্য তাদের প্রতিপালকের নিকট এমন বাগান রয়েছে, যার নিম্নে নদী প্রবাহিত, তারা তাতে চিরকাল থাকবে আর রয়েছে পবিত্র সঙ্গী এবং আল্লাহর সন্তুষ্টি, বস্তুতঃ আল্লাহ বান্দাগণের সম্পর্কে সম্যক দ্রষ্টা। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (309,3,16,'যারা প্রার্থনা করে, ‘হে আমাদের প্রতিপালক! আমরা ঈমান এনেছি, অতএব আমাদের গুনাহসমূহ ক্ষমা কর এবং আমাদেরকে জাহান্নামের শাস্তি হতে রক্ষা কর’। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (310,3,17,'তারা ধৈর্যশীল, সত্যবাদী, (আল্লাহর প্রতি) আজ্ঞাবহ, (আল্লাহর পথে) ব্যয়কারী এবং শেষ রাতে ক্ষমাপ্রার্থনাকারী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (311,3,18,'আল্লাহ সাক্ষ্য দেন যে, তিনি ছাড়া সত্যিকার কোন ইলাহ নেই এবং ফেরেশতাগণ ও ন্যায়নীতিতে প্রতিষ্ঠিত জ্ঞানীগণও (সাক্ষ্য দিচ্ছে যে,) তিনি ছাড়া সত্যিকার কোন ইলাহ নেই, তিনি মহাপরাক্রান্ত, মহাজ্ঞানী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (312,3,19,'নিশ্চয় আল্লাহর নিকট একমাত্র দ্বীন হল ইসলাম। বস্তুতঃ যাদেরকে কিতাব দেয়া হয়েছিল তারা জ্ঞান লাভের পর একে অন্যের উপর প্রাধান্য লাভের জন্য মতভেদ সৃষ্টি করেছে এবং যে ব্যক্তি আল্লাহর নিদর্শনসমূহ্কে অস্বীকার করবে, (সে জেনে নিক) নিশ্চয়ই আল্লাহ হিসাব গ্রহণে অতিশয় তৎপর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (313,3,20,'অতঃপর যদি (আহলে কিতাব) তোমার সাথে তর্ক করে তবে বলে দাও, ‘আমি আল্লাহর নিকট আত্মসমর্পণ করেছি আর আমার অনুসারীগণও আত্মসমর্পণ করেছে এবং আহলে কিতাব ও উম্মীগণকে বল, ‘তোমরা কি আত্মসমর্পণ করেছ’? অতঃপর যদি তারা আত্মসমর্পণ করে তবে নিশ্চয়ই তারা পথ পাবে আর তারা যদি মুখ ফিরিয়ে নেয়, তবে তোমার দায়িত্ব শুধু প্রচার করা। আল্লাহ বান্দাদের সম্পর্কে সম্যক দ্রষ্টা। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (314,3,21,'যারা আল্লাহর আয়াতসমূহকে অমান্য করে, নাবীদেরকে অন্যায়ভাবে হত্যা করে এবং মানুষদের মধ্যে যারা ন্যায়-নীতি শিক্ষা দেয় তাদেরকে হত্যা করে, তাদেরকে যন্ত্রণাদায়ক শাস্তির সংবাদ দাও। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (315,3,22,'এরাই তারা যাদের সমুদয় ‘আমাল দুনিয়া ও আখেরাতে নিষ্ফল হবে এবং তাদের কোন সাহায্যকারী নেই। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (316,3,23,'তুমি কি তাদের প্রতি দৃষ্টি নিক্ষেপ করনি যাদেরকে কিতাবের অংশ দেয়া হয়েছিল? তাদেরকে আল্লাহর কিতাবের দিকে আহবান করা হয়েছিল, যাতে এটা তাদের মধ্যে মীমাংসা করে দেয়, এরপর তাদের একদল বিমুখতা অবলম্বন করে ফিরে যায়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (317,3,24,'এটা এজন্য যে, তারা বলে, দিন কতক ছাড়া জাহান্নামের আগুন কক্ষনো আমাদেরকে স্পর্শ করবে না এবং তাদের কল্পিত ধারণাসমূহ দ্বীনের ব্যাপারে তাদেরকে ধোঁকায় ফেলে রেখেছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (318,3,25,'অনন্তর তাদের কী দশা ঘটবে, যখন আমি তাদেরকে সেই দিনে একত্রিত করব, যে দিনটি সংঘটিত হওয়ার ব্যাপারে কোনই সন্দেহ নেই এবং প্রত্যেককে তার অর্জিত প্রতিফল পূর্ণভাবে দেয়া হবে আর তাদের প্রতি কোন যুলম করা হবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (319,3,26,'বল, ‘হে আল্লাহ! তুমি সমুদয় রাজ্যের মালিক, যাকে ইচ্ছে রাজ্য দান কর আর যার থেকে ইচ্ছে রাজ্য কেড়ে নাও এবং যাকে ইচ্ছে সম্মানিত কর আর যাকে ইচ্ছে অপদস্থ কর, তোমারই হাতে সব রকম কল্যাণ, নিশ্চয়ই তুমি সকল বস্তুর উপর ক্ষমতাবান’। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (320,3,27,'তুমিই রাতকে দিনের ভিতর আর দিনকে রাতের ভিতর ঢুকিয়ে দাও, তুমিই জীবিতকে মৃত হতে বের কর এবং মৃতকে জীবিত হতে বের কর আর যাকে ইচ্ছে বেহিসাব রিয্ক্ব দান কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (321,3,28,'মু’মিনগণ যেন মু’মিনগণ ছাড়া কাফিরদের সঙ্গে বন্ধুত্ব না করে, মূলতঃ যে এমন করবে আল্লাহর সাথে তার কোন কিছুরই সম্পর্ক নেই, তবে ব্যতিক্রম হল যদি তোমরা তাদের যুলম হতে আত্মরক্ষার জন্য সতর্কতা অবলম্বন কর। আর আল্লাহ তাঁর নিজের সম্বন্ধে তোমাদেরকে সাবধান করছেন এবং আল্লাহরই দিকে প্রত্যাবর্তন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (322,3,29,'বল, ‘তোমরা তোমাদের অন্তরের বিষয়কে গোপন কর অথবা প্রকাশ কর, আল্লাহ তা জানেন, আর তিনি জানেন যা কিছু আকাশসমূহে এবং ভূভাগে আছে; আল্লাহ সকল বস্তুর উপর ক্ষমতাবান’। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (323,3,30,'যে দিন প্রত্যেক আত্মা যা কিছু নেক ‘আমাল করেছে এবং যা কিছু বদ ‘আমাল করেছে তা বিদ্যমান পাবে; সেই আত্মা কামনা করবে যদি তার এবং ওর (অর্থাৎ তার মন্দ কর্মফলের) মধ্যে দুস্তর ব্যবধান হত। আল্লাহ তাঁর নিজের সম্পর্কে তোমাদেরকে সাবধান করছেন, বস্তুতঃ আল্লাহ বান্দাগণের প্রতি খুবই করুণাশীল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (324,3,31,'বলে দাও, ‘যদি তোমরা আল্লাহকে ভালবাস, তবে আমার অনুসরণ কর, আল্লাহ তোমাদেরকে ভালবাসবেন এবং তোমাদের গুনাহসকল ক্ষমা করবেন, বস্তুতঃ আল্লাহ অতি ক্ষমাশীল, পরম দয়ালু’।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (325,3,32,'বল, ‘তোমরা আল্লাহর ও রসূলের আজ্ঞাবহ হও’। অতঃপর যদি তারা না মানে, তবে (জেনে রেখ) আল্লাহ কাফিরদেরকে ভালবাসেন না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (326,3,33,'নিশ্চয়ই আল্লাহ আদাম ও নূহকে এবং ইবরাহীমের ও ‘ইমরানের গোত্রকে বিশ্বজগতের উপর মনোনীত করেছেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (327,3,34,'এরা একে অন্যের বংশধর এবং আল্লাহ সর্বশ্রোতা ও সর্বজ্ঞ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (328,3,35,'(স্মরণ কর) যখন ‘ইমরানের স্ত্রী আরয করেছিল, হে আমার প্রতিপালক! আমার উদরে যা আছে, তাকে আমি একান্ত তোমার উদ্দেশে উৎসর্গ করলাম, কাজেই আমার পক্ষ হতে তা গ্রহণ কর, নিশ্চয়ই তুমি সর্বশ্রোতা ও সর্বজ্ঞ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (329,3,36,'অতঃপর যখন সে তাকে প্রসব করল, বলে উঠল, হে আমার প্রতিপালক! আমি কন্যা প্রসব করেছি এবং আল্লাহ ভাল করেই জানেন যা সে প্রসব করেছে; বস্তুতঃ পুত্র কন্যার মত নয় এবং আমি তার নাম রাখলাম মারইয়াম এবং আমি তাকে ও তার বংশধরকে বিতাড়িত শয়ত্বান হতে তোমার আশ্রয়ে ছেড়ে দিলাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (330,3,37,'তখন তার প্রতিপালক তাকে সন্তুষ্টি সহকারে গ্রহণ করলেন এবং তাকে উত্তমরূপে লালন পালন করলেন এবং যাকারিয়াকে তার তত্ত্বাবধায়ক করলেন। যখনই যাকারিয়া মারইয়ামের কক্ষে প্রবেশ করত, তার কাছে খাদ্য সামগ্রী দেখতে পেত; জিজ্ঞেস করত- হে মারইয়াম! ‘এসব কোত্থেকে তোমার কাছে আসে’? মারইয়াম বলত, ‘ওসব আল্লাহর নিকট হতে (আসে)’। নিশ্চয়ই আল্লাহ যাকে ইচ্ছে বেহিসাব রিযক দান করেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (331,3,38,'ওখানেই যাকারিয়া নিজ প্রতিপালকের কাছে প্রার্থনা করে বলল, ‘হে আমার প্রতিপালক! আমাকে তোমার পক্ষ হতে একটি সুসন্তান দান কর। নিশ্চয় তুমি প্রার্থনা শ্রবণকারী’। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (332,3,39,'যখন যাকারিয়া ‘ইবাদাত কক্ষে সলাতে দন্ডায়মান তখন ফেরেশতারা তাকে সম্বোধন করে বলল : আল্লাহ তোমাকে ইয়াহ্ইয়া’র সুসংবাদ দিচ্ছেন, সে হবে আল্লাহর পক্ষ হতে আগত কালেমার সত্যতার সাক্ষ্যদাতা, নেতা, গুনাহ হতে বিরত ও নেক বান্দাগণের মধ্য হতে একজন নাবী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (333,3,40,'যাকারিয়া বলল, হে আমার প্রতিপালক! আমার সন্তান হবে কীভাবে, আমি তো বার্ধক্যে পৌঁছেছি এবং আমার স্ত্রীও বন্ধ্যা। তিনি বললেন, ‘এভাবেই, আল্লাহ যা ইচ্ছে করেন তা-ই সম্পন্ন করে থাকেন’। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (334,3,41,'সে বলল, ‘হে আমার প্রতিপালক! আমার জন্য কোন নিদর্শন দাও’। তিনি বললেন, ‘তোমার জন্য নিদর্শন এই যে, তিনদিন তুমি ইশারা ছাড়া লোকের সঙ্গে কথা বলতে পারবে না এবং তোমার প্রতিপালককে অধিক স্মরণ করবে এবং সকালে ও সন্ধ্যায় তাঁর পবিত্রতা ও মহিমা ঘোষণা করবে’।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (335,3,42,'স্মরণ কর, যখন ফেরেশতারা বলেছিল, ‘হে মারইয়াম! আল্লাহ তোমাকে বেছে নিয়েছেন এবং তোমাকে পবিত্র করেছেন, আর তামাম দুনিয়ার নারীদের উপর তোমাকে মনোনীত করেছেন’। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (336,3,43,'হে মারইয়াম! ‘তুমি তোমার প্রতিপালকের অনুগত হও, সাজদাহ কর এবং রুকূ‘কারীদের সঙ্গে রুকূ‘ কর’।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (337,3,44,'এসব অদৃশ্যের সংবাদ, আমি তোমার কাছে তা ওয়াহী দ্বারা পৌঁছে দিচ্ছি। বস্তুতঃ তুমি তাদের নিকট উপস্থিত ছিলে না যখন তাদের কোন্ ব্যক্তি মারইয়ামকে লালন-পালন করবে সেই ভাগ্য নির্ণয়ের জন্য কলম নিক্ষেপ করছিল এবং তুমি তাদের নিকট উপস্থিত ছিলে না, যখন তারা (মারইয়ামের অভিভাবক কে হবে তা নিয়ে) বাদানুবাদ করছিল।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (338,3,45,'(স্মরণ কর) যখন ফেরেশতারা বলল, ‘হে মারইয়াম! নিশ্চয় আল্লাহ তোমাকে তাঁর একটি কথার সুসংবাদ দিচ্ছেন। তার নাম মারইয়ামের পুত্র ঈসা-মসীহ, সে দুনিয়া ও আখেরাতে সম্মানিত ও সান্নিধ্য প্রাপ্তদের অন্তর্ভুক্ত হবে।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (339,3,46,'‘সে লোকেদের সাথে দোলনা থাকা অবস্থায় এবং বয়োঃপ্রাপ্ত অবস্থায় কথা বলবে এবং নেক বান্দাদের অন্তর্গত হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (340,3,47,'মারইয়াম বলল, ‘হে আমার প্রতিপালক! কীভাবে আমার পুত্র হবে, অথচ আমাকে কোন মানব স্পর্শ করেনি’। তিনি বললেন, ‘এভাবেই’ আল্লাহ সৃজন করেন যা তিনি ইচ্ছে করেন, তিনি যখন কিছু স্থির করেন তখন বলেন, ‘‘হয়ে যাও’’ সুতরাং তা হয়ে যায়।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (341,3,48,'আর তিনি তাকে কিতাব, হিকমাত, তাওরাত ও ইঞ্জিল শিক্ষা দেবেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (342,3,49,'‘আর তিনি তাকে বানী ইসরাঈলের নিকট রসূল হিসেবে প্রেরণ করবেন’। সে বলবে, ‘নিশ্চয় আমি তোমাদের প্রতিপালকের পক্ষ হতে নিদর্শনসহ তোমাদের নিকট এসেছি, আমি তোমাদের জন্য মাটি দ্বারা পাখীর মত একটা কায়া গঠন করব, অতঃপর তাতে ফুঁৎকার দেব, ফলে আল্লাহর হুকুমে তা পাখি হয়ে যাবে এবং আল্লাহর হুকুমে আমি জন্মান্ধ ও কুষ্ঠরোগীকে আরোগ্য করব ও আল্লাহর হুকুমে মৃতকে জীবিত করব এবং আমি তোমাদেরকে বলে দেব তোমাদের গৃহে তোমরা যা আহার কর এবং সঞ্চয় করে রাখ; নিশ্চয়ই এ কাজে তোমাদের জন্য নিদর্শন রয়েছে, যদি তোমরা মু’মিন হও।’');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (343,3,50,'‘(আর আমি এসেছি) আমার সামনে তাওরাতের নিদর্শন যা রয়েছে তার সমর্থকরূপে যেন তোমাদের জন্য কোন কোন জিনিস হালাল করে দেই যা তোমাদের প্রতি হারাম ছিল এবং আমি তোমাদের প্রতিপালকের পক্ষ হতে নিদর্শনসহ তোমাদের নিকট এসেছি, কাজেই তোমরা আল্লাহকে ভয় কর এবং আমাকে অনুসরণ কর’। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (344,3,51,'‘নিশ্চয় আল্লাহ আমার প্রতিপালক এবং তোমাদেরও প্রতিপালক, অতএব তাঁর ‘ইবাদাত কর, এটাই সরল পথ’। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (345,3,52,'অতঃপর ‘ঈসা যখন তাদের অবিশ্বাস অনুভব করল, তখন বলল, ‘কেউ আছে কি যে আল্লাহর পথে আমার সহায়ক হবে’। হাওয়ারীগণ বলল, ‘আমরাই আল্লাহর সাহায্যকারী, আমরা আল্লাহর প্রতি ঈমান এনেছি এবং সাক্ষী থাকুন যে, আমরা আত্মসমর্পণকারী’।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (346,3,53,'‘হে আমাদের প্রতিপালক! তুমি যা অবতীর্ণ করেছ আমরা তার উপর ঈমান এনেছি, রসূলের আনুগত্য স্বীকার করেছি, সুতরাং আমাদেরকে সাক্ষ্যদানকারীদের মধ্যে লিপিবদ্ধ কর।’');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (347,3,54,'এবং তারা কৌশল করেছিল এবং আল্লাহ্ও (জবাবে) কৌশল করেছিলেন, আল্লাহ কৌশলীদের শ্রেষ্ঠ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (348,3,55,'(স্মরণ কর) যখন আল্লাহ ‘ঈসাকে বলেছিলেন, ‘হে ‘ঈসা! আমি তোমার সাথে কৃত ওয়াদা পূর্ণ করব (তোমাকে মৃত্যু ছাড়াই দুনিয়া থেকে কবজ করব) এবং তোমাকে আমার কাছে উঠিয়ে নেব এবং তোমাকে কাফিরদের হতে মুক্ত করব আর তোমার অনুসরণকারীদেরকে ক্বিয়ামাত পর্যন্ত অবিশ্বাসীদের উপর বিজয়ী রাখব; অতঃপর আমার কাছে তোমাদের প্রত্যাবর্তন, তখন আমি তোমাদের মধ্যে  ফায়সালা  করে  দেব যে  বিষয়ে  তোমরা  মতভেদ করছ।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (349,3,56,'অতঃপর যারা অবিশ্বাসী, তাদেরকে দুনিয়া ও আখেরাতে কঠোর শাস্তি দেব এবং কেউই তাদের সাহায্যকারী নেই। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (350,3,57,'পক্ষান্তরে যারা ঈমান এনেছে এবং সৎ কাজ করেছে, তাদেরকে তিনি পুরোপুরি সাওয়াব দান করবেন, বস্তুতঃ আল্লাহ যালিমদেরকে ভালবাসেন না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (351,3,58,'এসব আমি তোমাকে পড়ে শুনাচ্ছি আয়াতসমষ্টি ও জ্ঞানগর্ভ বাণী হতে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (352,3,59,'আল্লাহর নিকট ঈসার অবস্থা আদামের অবস্থার মত, মাটি দ্বারা তাকে গঠন করে তাকে হুকুম করলেন, হয়ে যাও, ফলে সে হয়ে গেল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (353,3,60,'এ বাস্তব ঘটনা তোমার প্রতিপালকের পক্ষ হতেই, সুতরাং তুমি সংশয়কারীদের অন্তর্ভুক্ত হয়ো না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (354,3,61,'তোমার নিকট জ্ঞান আসার পর যে ব্যক্তি তোমার সাথে (ঈসার সম্বন্ধে) বিতর্ক করবে তাকে বল, ‘আসো, আমাদের পুত্রদেরকে এবং তোমাদের পুত্রদেরকে আর আমাদের নারীদেরকে এবং তোমাদের নারীদেরকে এবং আমাদের নিজেদেরকে এবং তোমাদের নিজেদেরকে আহবান করি, অতঃপর আমরা মুবাহলা করি আর মিথ্যুকদের প্রতি আল্লাহর অভিসম্পাত বর্ষণ করি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (355,3,62,'নিশ্চয়ই এটা প্রকৃত ঘটনা। আল্লাহ ছাড়া অন্য সত্য ইলাহ নেই। আর নিশ্চয়ই আল্লাহ মহাপরাক্রান্ত, প্রজ্ঞাময়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (356,3,63,'তা’ সত্ত্বেও যদি তারা মুখ ফিরিয়ে নেয়, তবে নিশ্চয়ই আল্লাহ কলহ সৃষ্টিকারীদের বিষয়ে বিশেষভাবে অবহিত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (357,3,64,'বল, ‘হে আহলে কিতাব! এমন এক কথার দিকে আসো, যা আমাদের ও তোমাদের মধ্যে একই, তা এই যে, আমরা আল্লাহ ভিন্ন অন্য কারো ‘ইবাদাত করব না এবং কোন কিছুকে তাঁর শরীক করব না এবং আল্লাহকে বাদ দিয়ে আমাদের মধ্যে কেউ কাউকে রব হিসেবে গ্রহণ করব না। তারপরও যদি তারা মুখ ফিরিয়ে নেয় তবে বলে দাও, তোমরা এ বিষয়ে সাক্ষী থাক যে, আমরা আত্মসমর্পণকারী।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (358,3,65,'হে আহলে কিতাব! তোমরা কেন ইবরাহীম সম্পর্কে তর্ক করছ? তাওরাত এবং ইঞ্জিল তো তারপরেই অবতীর্ণ হয়েছে, তোমরা কি তাও বুঝ না? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (359,3,66,'বস্তুতঃ তোমরাই এমন লোক যে, যে সম্পর্কে তোমাদের কিছু জ্ঞান আছে, সে বিষয়ে তো বিতর্ক করেছ, তোমরা এমন বিষয়ে কেন বিতর্ক করছ যে বিষয়ে তোমাদের কোনই জ্ঞান নেই? বস্তুতঃ আল্লাহ্ই জ্ঞাত আছেন, তোমরা জ্ঞাত নও। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (360,3,67,'ইবরাহীম না ইয়াহূদী ছিল, না নাসারা, বরং একনিষ্ঠ আত্মসমর্পণকারী এবং সে মুশরিক দলের অন্তর্ভুক্ত ছিল না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (361,3,68,'নিশ্চয় ইবরাহীমের সঙ্গে ঘনিষ্ঠতায় সেই লোকেরাই অধিক হকদার যারা তার অনুসরণ করেছে এবং এই নাবী, আর যারা ঈমান এনেছে, বস্তুতঃ আল্লাহ মু’মিনদের অভিভাবক। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (362,3,69,'কিতাবধারীদের একদল চায় যাতে তোমাদেরকে পথভ্রষ্ট করতে পারে, অথচ তারা নিজেদেরকে ছাড়া অন্য কাউকেও পথভ্রষ্ট করে না, কিন্তু তারা উপলব্ধি করতে পারে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (363,3,70,'হে আহলে কিতাব! কেন তোমরা আল্লাহর আয়াতগুলোকে অস্বীকার করছ, অথচ তোমরা নিজেরাই তার সাক্ষী? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (364,3,71,'হে আহলে কিতাব! কেন তোমরা সত্যকে মিথ্যার সঙ্গে মিশ্রিত করছ আর জেনে বুঝে সত্যকে গোপন করছ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (365,3,72,'আহলে কিতাবের একটা দল বলল, যারা ঈমান এনেছে তাদের প্রতি যা অবতীর্ণ হয়েছে তার উপর দিনের শুরুতে তোমরা ঈমান আন এবং দিনের শেষে অস্বীকার (কুফরী) কর, হয়ত তারা (ইসলাম ত্যাগ করে) ফিরে আসবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (366,3,73,'‘এবং তোমাদের দ্বীনের অনুসারী ছাড়া আর কাউকেও বিশ্বাস করো না। তাদেরকে বলে দাও, আল্লাহর (নির্দেশিত) পথই একমাত্র পথ; (এবং এটা আল্লাহর নীতি যে) একদিন তোমাদেরকে যা দেয়া হয়েছিল তা-ই অন্য কাউকে দেয়া হবে অথবা অন্য লোকেরা তোমাদের প্রতিপালকের সামনে তোমাদের বিরুদ্ধে পেশ করার জন্য মযবুত প্রমাণ পেয়ে যাবে। বল, ‘কল্যাণ আল্লাহরই হাতে, তিনি যাকে ইচ্ছে তা দান করেন এবং আল্লাহ প্রাচুর্যশালী ও সর্বজ্ঞ’।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (367,3,74,'তিনি যাকে ইচ্ছে নিজের দয়ার জন্য খাস করে বেছে নেন এবং আল্লাহ মহা কল্যাণের অধিকারী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (368,3,75,'আহলে কিতাবের মধ্যে কেউ কেউ এমন আছে যে, যদি তাদের নিকট স্বর্ণের স্তুপ গচ্ছিত রাখ, তবে তোমাকে তা ফেরত দেবে, পক্ষান্তরে তাদের কেউ কেউ এমন যে, একটি দিনারও যদি তাদের নিকট গচ্ছিত রাখ, তার পেছনে লেগে না থাকলে সে তোমাকে তা ফেরত দেবে না, এটা এজন্য যে, তারা বলে, ‘নিরক্ষরদের প্রতি আমাদের কোন দায়-দায়িত্ব নেই’, বস্তুতঃ তারা জেনে শুনে আল্লাহর সম্পর্কে মিথ্যে বলে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (369,3,76,'তবে হ্যাঁ, যে ব্যক্তি নিজের ওয়া‘দা পূর্ণ করবে এবং আল্লাহকে ভয় করে চলবে, নিশ্চয় আল্লাহ (এই) মুত্তাকীদেরকে ভালবাসেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (370,3,77,'নিশ্চয় যারা আল্লাহর সাথে কৃত অঙ্গীকার এবং নিজেদের শপথকে তুচ্ছ মূল্যে বিক্রয় করে, এরা আখেরাতের কোন অংশই পাবে না এবং আল্লাহ ক্বিয়ামাতের দিন তাদের সঙ্গে কথা বলবেন না, তাদের প্রতি দৃষ্টিপাত করবেন না এবং তাদেরকে পবিত্র করবেন না, বস্তুতঃ তাদের জন্য আছে যন্ত্রণাদায়ক শাস্তি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (371,3,78,'এদের মধ্যে একদল আছে যারা কিতাবকে জিহবা দ্বারা বিকৃত করে যাতে তোমরা তাকে কিতাবের অংশ বলে মনে কর, মূলতঃ তা কিতাবের অংশ নয় এবং তারা বলে, ‘এটা আল্লাহর নিকট হতে অবতীর্ণ, বস্তুতঃ তা আল্লাহর নিকট হতে অবতীর্ণ নয়, তারা জেনে শুনে আল্লাহর প্রতি মিথ্যারোপ করে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (372,3,79,'কোন মানব সন্তানের পক্ষে এটা সম্ভব নয় যে, আল্লাহ তাকে কিতাব, জ্ঞান ও নুবুওয়াত দান করেন, অতঃপর সে লোকেদেরকে বলে, ‘তোমরা আল্লাহকে ছেড়ে আমার বান্দা হয়ে যাও, বরং (সে বলবে), ‘তোমরা আল্লাহ্ওয়ালা হও; যেহেতু তোমরা কিতাব শিক্ষা দান কর এবং নিজেরাও পাঠ কর’। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (373,3,80,'সে ব্যক্তি তোমাদেরকে বলবে না যে, তোমরা ফেরেশতাদেরকে এবং নাবীদেরকে মা’বূদরূপে গ্রহণ কর, তোমরা মুসলিম হওয়ার পরও কি সে তোমাদেরকে কুফরীর নির্দেশ দিতে পারে? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (374,3,81,'(স্মরণ কর) যখন আল্লাহ নাবীদের নিকট হতে এ অঙ্গীকার নিয়েছিলেন যে, আমি তোমাদেরকে কিতাব এবং জ্ঞান যা কিছু প্রদান করেছি, অতঃপর তোমাদের কাছে যা আছে তার সমর্থক কোন রসূল যখন তোমাদের নিকট আসবে, তখন অবশ্য তোমরা তার প্রতি ঈমান আনবে এবং তাকে সাহায্য করবে। তিনি জিজ্ঞেস করলেন, ‘তোমরা অঙ্গীকার করলে তো? এবং যে বিষয়ে আমি তোমাদের নিকট হতে অঙ্গীকার নিলাম, তোমরা তা মানলে তো?’ তারা বলল, ‘আমরা অঙ্গীকার করলাম’। আল্লাহ বললেন, ‘তোমরা সাক্ষী থাক এবং আমিও তোমাদের সাথে সাক্ষী থাকলাম’।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (375,3,82,'অতঃপর যারা মুখ ফিরিয়ে নিবে তারাই হল ফাসেক। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (376,3,83,'এরা কি আল্লাহর দ্বীন ছাড়া অন্য দ্বীনের সন্ধান করছে? অথচ আসমান ও যমীনে যা আছে সবই ইচ্ছেয় ও অনিচ্ছেয় তাঁরই কাছে আত্মসমর্পণ করেছে এবং তাঁরই দিকে সকলের প্রত্যাবর্তন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (377,3,84,'বল, ‘আমরা আল্লাহর উপর এবং আমাদের প্রতি ও ইবরাহীম, ইসমাঈল, ইসহাক, ইয়াকুব এবং তাঁর বংশধরের প্রতি যা কিছু অবতীর্ণ হয়েছে এবং মূসা, ঈসা ও অন্যান্য নাবীগণকে তাঁদের প্রতিপালকের পক্ষ হতে যা কিছু দেয়া হয়েছে তার উপর ঈমান এনেছি; আমরা তাঁদের মধ্যে কোন পার্থক্য করি না এবং আমরা তাঁরই কাছে আত্মসমর্পিত।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (378,3,85,'আর যে ব্যক্তি ইসলাম ব্যতীত অন্য কোন দ্বীন গ্রহণ করতে চাইবে কক্ষনো তার সেই দ্বীন কবূল করা হবে না এবং আখেরাতে সে ব্যক্তি ক্ষতিগ্রস্তদের অন্তর্ভুক্ত হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (379,3,86,'আল্লাহ কীরূপে সেই সম্প্রদায়কে সুপথ দেখাবেন যারা ঈমান আনার পর, এ রসূলকে সত্য বলে স্বীকার করার পর এবং তাদের নিকট সুস্পষ্ট দলীল আসার পর কুফরী করে? বস্তুতঃ আল্লাহ যালিম কওমকে পথ দেখান না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (380,3,87,'এরাই তারা যাদের প্রতিফল এই যে, তাদের প্রতি আল্লাহর, ফেরেশতাদের এবং সমুদয় মানবের অভিসম্পাত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (381,3,88,'তারা ওতেই চিরকাল থাকবে, তাদের শাস্তি লঘু করা হবে না এবং তাদেরকে বিরামও দেয়া হবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (382,3,89,'কিন্তু এরপর যারা তাওবাহ করে এবং নিজেদেরকে সংশোধন করে তারা ব্যতীত। নিশ্চয়ই আল্লাহ ক্ষমাশীল, অতি দয়ালু। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (383,3,90,'নিশ্চয়ই যারা ঈমান আনার পর কুফরী করল, অতঃপর তাদের কুফরী বেড়েই চলল, তাদের তাওবাহ কক্ষনো কবুল করা হবে না এবং এ লোকেরাই পথভ্রষ্ট। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (384,3,91,'নিশ্চয়ই যারা কুফরী করে এবং সেই কাফির অবস্থায়ই মারা যায়, তাদের কেউ পৃথিবী-ভরা স্বর্ণও বিনিময় স্বরূপ প্রদান করতে চাইলে তা তার কাছ থেকে কক্ষণো গ্রহণ করা হবে না। এরাই তারা যাদের জন্য যন্ত্রণাদায়ক শাস্তি রয়েছে এবং তাদের কোন সাহায্যকারী নেই। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (385,3,92,'তোমরা তোমাদের প্রিয়বস্তু খরচ না করা পর্যন্ত কক্ষনো পুণ্য লাভ করবে না, যা কিছু তোমরা খরচ কর-নিশ্চয়ই আল্লাহ সে বিষয়ে খুব ভালভাবেই অবগত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (386,3,93,'তাওরাত নাযিলের পূর্বে ইয়াকুব নিজের উপর যা হারাম করেছিল, তাছাড়া সকল খাদ্য বানী ইসরাঈলের জন্য হালাল ছিল। বল, ‘তোমরা তাওরাত নিয়ে এসো, এবং তা পাঠ কর, যদি তোমরা সত্যবাদী হও’। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (387,3,94,'এরপরও যারা আল্লাহ সম্পর্কে মিথ্যারোপ করবে, তারা যালিম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (388,3,95,'বল, ‘আল্লাহ সত্য বলেছেন, সুতরাং তোমরা একনিষ্ঠভাবে ইবরাহীমের অনুসরণ কর, সে মুশরিকদের অন্তর্ভুক্ত নয়’। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (389,3,96,'নিঃসন্দেহে প্রথম ঘর যা মানুষের জন্য স্থাপন করা হয়েছিল, তাতো মক্কা্য়, যা বরকতমন্ডিত এবং সারা জাহানের জন্য পথপ্রদর্শক। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (390,3,97,'তাতে সুস্পষ্ট নিদর্শনাবলী রয়েছে (যেমন) মাক্বামে ইবরাহীম(ইবরাহীমের দাঁড়ানোর জায়গা)। যে কেউ তাতে প্রবেশ করবে নিরাপদ হবে। আল্লাহর জন্য উক্ত ঘরের হাজ্জ করা লোকেদের উপর আবশ্যক যার সে পর্যন্ত পৌঁছার সামর্থ্য আছে এবং যে ব্যক্তি অস্বীকার করবে, (সে জেনে রাখুক) নিঃসন্দেহে আল্লাহ বিশ্ব জাহানের মুখাপেক্ষী নন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (391,3,98,'বল, ‘হে কিতাবধারীগণ! তোমরা আল্লাহর নিদর্শনসমূহকে কেন অমান্য করছ? বস্তুতঃ তোমরা যা করছ, আল্লাহ তার সাক্ষী’। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (392,3,99,'বল, হে কিতাবধারীগণ! যে ব্যক্তি ঈমান এনেছে তাকে কেন আল্লাহর পথে বাধা দিচ্ছ, ওকে বক্র করার পথ খুঁজছ, অথচ তোমরাই (এ পথের সত্যতার) সাক্ষী? এমতাবস্থায় আল্লাহ তোমাদের কার্যাবলী সম্বন্ধে বে-খবর নন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (393,3,100,'হে মু’মিনগণ! তোমরা যদি কিতাবীদের মধ্য হতে কোন দলের কথা মেনে নাও, তবে তারা তোমাদের ঈমান আনার পর আবার তোমাদেরকে কাফির বানিয়ে ছাড়বে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (394,3,101,'আর তোমরা কেমন করে কুফরী করতে পার যখন তোমাদেরকে আল্লাহর আয়াতসমূহ পড়ে শুনানো হচ্ছে এবং তোমাদের মাঝে তাঁর রসূলও মওজুদ আছেন? যে ব্যক্তি আল্লাহকে সুদৃঢ়ভাবে গ্রহণ করে, নিশ্চয়ই সে সরল পথের দিকে পরিচালিত হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (395,3,102,'হে মু’মিনগণ! আল্লাহকে ভয় কর যেমনভাবে তাঁকে ভয় করা উচিত। তোমরা মুসলিম না হয়ে কক্ষনো মরো না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (396,3,103,'আল্লাহর রজ্জুকে সমবেতভাবে দৃঢ়ভাবে ধারণ কর, পরস্পর বিচ্ছিন্ন হয়ো না এবং তোমাদের প্রতি আল্লাহর নি‘মাত স্মরণ কর, যখন তোমরা ছিলে পরস্পর শত্রু, তিনি তোমাদের অন্তরে প্রীতির সঞ্চার করলেন, ফলে তোমরা আল্লাহর অনুগ্রহে পরস্পর ভাই ভাই হয়ে গেলে। তোমরা অগ্নি-গহ্বরের প্রান্তে ছিলে, অতঃপর আল্লাহ তোমাদেরকে তাত্থেকে রক্ষা করলেন। এভাবে আল্লাহ নিজের নিদর্শনাবলী তোমাদের কাছে স্পষ্টভাবে বর্ণনা করেন যাতে তোমরা সঠিক পথ প্রাপ্ত হও। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (397,3,104,'তোমাদের মধ্যে এমন একটা দল হোক, যারা কল্যাণের দিকে আহবান করে, সৎ কাজের আদেশ করে এবং অসৎ কাজ হতে নিষেধ করে আর এরাই সফলকাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (398,3,105,'তোমরা সেই লোকদের মত হয়ে যেয়ো না যারা তাদের নিকট সুস্পষ্ট নিদর্শন পৌঁছার পরে বিভক্ত হয়েছে ও মতভেদ করেছে এবং এ শ্রেণীর লোকেদের জন্য আছে মহা শাস্তি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (399,3,106,'সে দিন কতক মুখ উজ্জ্বল হবে আর কতক মুখ কালো হবে, যাদের মুখ কালো হবে, (তাদেরকে বলা হবে), তোমরা কি ঈমান আনার পরও কুফরী করেছিলে? কাজেই নিজেদের কুফরীর জন্য শাস্তি ভোগ করতে থাক। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (400,3,107,'যাদের মুখ উজ্জ্বল হবে, তারা আল্লাহর রহমতের মধ্যে থাকবে, তারা তাতে চিরকাল থাকবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (401,3,108,'এসব আল্লাহর আয়াত, যা ঠিকভাবে আমি তোমাকে পড়ে শুনাচ্ছি এবং আল্লাহ বিশ্বজগতের প্রতি যুলম করতে চান না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (402,3,109,'যা কিছু আসমানে আছে আর যমীনে আছে সব আল্লাহরই এবং যাবতীয় বিষয়াদি আল্লাহর দিকেই ফিরে যাবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (403,3,110,'তোমরাই সর্বোত্তম উম্মাত, মানবজাতির (সর্বাত্মক কল্যাণের) জন্য তোমাদের আবির্ভুত করা হয়েছে, তোমরা সৎকাজের আদেশ দাও এবং অসৎ কাজ হতে নিষেধ কর ও আল্লাহর প্রতি ঈমান রক্ষা করে চল। যদি আহলে কিতাব ঈমান আনত, তাহলে নিশ্চয়ই তাদের জন্য ভাল হত, তাদের মধ্যে কেউ কেউ মু’মিন এবং তাদের অধিকাংশই ফাসেক। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (404,3,111,'সামান্য কষ্ট দেয়া ব্যতীত তারা তোমাদের কোন ক্ষতি করতে পারবে না, আর যদি তোমাদের সাথে যুদ্ধে লিপ্ত হয়, তবে তোমাদেরকে পৃষ্ঠ প্রদর্শন করবে, অতঃপর তারা সাহায্যপ্রাপ্ত হবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (405,3,112,'আল্লাহর অনুকম্পা ও মানুষের আশ্রয় ছাড়া যেখানেই তারা অবস্থান করুক, সেখানেই তারা হয়েছে লাঞ্ছিত, তারা আল্লাহর গযবে পরিবেষ্টিত এবং তাদের উপর পতিত হয়েছে দারিদ্রের কশাঘাত। এটা এজন্য যে, তারা আল্লাহর নিদর্শনসমূহ প্রত্যাখ্যান করত এবং নাবীগণকে অন্যায়ভাবে হত্যা করত, এটা এজন্য যে, তারা অবাধ্যতা এবং সীমালঙ্ঘন করত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (406,3,113,'তারা সবাই সমান নয়। আহলে কিতাবদের কত লোক এমনও আছে যারা দ্বীনের উপর প্রতিষ্ঠিত আছে, তারা রাত্রিকালে আল্লাহর আয়াতসমূহ পাঠ করে থাকে এবং তারা সাজদাহ করে থাকে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (407,3,114,'তারা আল্লাহর ও পরকালের প্রতি বিশ্বাস পোষণ করে, সৎকাজের আদেশ দেয় এবং অসৎকাজ হতে নিষেধ করে এবং কল্যাণকর কাজে তৎপর থাকে। বস্তুতঃ তারা পুণ্যবানদের মধ্যে গণ্য। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (408,3,115,'তারা যা কিছু সৎকাজ করুক কোন কিছুই প্রত্যাখ্যান করা হবে না এবং আল্লাহ মুত্তাকীদের বিষয়ে বিশেষরূপে পরিজ্ঞাত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (409,3,116,'যারা কুফরী করে, আল্লাহর নিকটে তাদের ধন-সম্পদ ও সন্তান-সন্ততি কক্ষনো কোন কাজে আসবে না এবং তারা হচ্ছে অগ্নির অধিবাসী, তারা তাতে চিরকাল থাকবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (410,3,117,'এ পার্থিব জীবনে তারা যা ব্যয় করে তার দৃষ্টান্ত হিমশীতল  বাতাসের  মত যা নিজেদের প্রতি  যুলমকারী  সম্প্রদায়ের শস্যক্ষেত্রকে আঘাত করে এবং তাকে নষ্ট করে। মূলতঃ তাদের প্রতি আল্লাহ কোন প্রকার যুলম করেননি, বরং তারা নিজেরাই নিজেদের প্রতি যুলম করে থাকে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (411,3,118,'হে মু’মিনগণ! তোমরা তোমাদের নিজেদের লোক ছেড়ে অন্য কাউকেও বন্ধুরূপে গ্রহণ করো না, কারণ তারা তোমাদেরকে নষ্ট করতে ত্রুটি করবে না, তারা কেবল তোমাদের দুর্ভোগ কামনা করে, বস্তুতঃ তাদের মুখেও শত্রুতা প্রকাশিত হয়ে পড়ে এবং তাদের অন্তর যা লুকিয়ে রাখে তা আরও ভয়ঙ্কর, আমি তোমাদের কাছে তাদের লক্ষণগুলো স্পষ্ট করে দিলাম, যদি তোমরা অনুধাবন কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (412,3,119,'অবশ্য তোমরাই সেই লোক যে, তোমরা তাদেরকে মহববত কর, কিন্তু তারা তোমাদের সাথে মহববত রাখে না, উপরন্তু তোমরা সকল কিতাবের প্রতিও বিশ্বাস রাখ এবং যখন তারা তোমাদের সঙ্গে সাক্ষাৎ করে তখন বলে যে, ‘আমরাও ঈমান এনেছি’ এবং যখন তারা একান্তে মিলিত হয় তখন তোমাদের প্রতি গোস্বায় নিজেদের আঙ্গুলের মাথা কামড়াতে থাকে। তাদেরকে বল, ‘তোমরা নিজেদের গোস্বায় মরতে থাক’। বস্তুতঃ অন্তরে যা আছে আল্লাহ তা খুব ভালভাবেই জানেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (413,3,120,'যদি তোমাদের কল্যাণ হয়, তা তাদেরকে দুঃখ দেয়, আর যদি তোমাদের অকল্যাণ হয়, তাতে তারা আনন্দিত হয়, যদি তোমরা ধৈর্যশীল হও এবং তাকওয়া অবলম্বন কর তবে তাদের চক্রান্ত তোমাদের কিছুই ক্ষতি করতে পারবে না, নিশ্চয় তারা যা কিছু করছে, আল্লাহ তা পরিবেষ্টন করে আছেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (414,3,121,'(স্মরণ কর) যখন তুমি সকাল বেলায় তোমার পরিজন হতে বের হয়ে মু’মিনদেরকে যুদ্ধের জন্য জায়গায় জায়গায় মোতায়েন করছিলে, আল্লাহ সবকিছুই শুনেন, সবকিছুই জানেন।। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (415,3,122,'যখন তোমাদের মধ্যকার দু’দল ভীরুতা প্রকাশ করতে মনস্থ করেছিল, কিন্তু আল্লাহ উভয়ের বন্ধু ছিলেন, মু’মিনদের উচিত আল্লাহর উপর ভরসা করা। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (416,3,123,'এবং আল্লাহ তোমাদের হীন অবস্থায় বাদ্র যুদ্ধক্ষেত্রে তোমাদের সাহায্য করেছেন, সুতরাং আল্লাহকে ভয় করে চল, যেন তোমরা শোকরগুজার হতে পার। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (417,3,124,'(স্মরণ কর) যখন তুমি মু’মিনদেরকে বলছিলে, ‘তোমাদের জন্য কি এটা যথেষ্ট নয় যে, তোমাদের প্রতিপালক তিন সহস্র ফেরেশতা অবতরণপূর্বক তোমাদের সাহায্য করবেন?’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (418,3,125,'হ্যাঁ, যদি তোমরা ধৈর্যধারণ কর এবং তাকওয়া অবলম্বন কর এবং তারা (অর্থাৎ শত্রুরা) মুহূর্তের মধ্যে এখানে তোমাদের উপর এসে পড়ে, তাহলে তোমাদের প্রতিপালক বিশেষভাবে চিহ্নিত পাঁচ সহস্র ফেরেশতা দ্বারা তোমাদেরকে সাহায্য করবেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (419,3,126,'এটা আল্লাহ কেবলমাত্র তোমাদের সুসংবাদের জন্য এবং তা দ্বারা তোমাদের চিত্ত-প্রশান্তির জন্য করেছেন, মূলতঃ সাহায্য তো শুধু আল্লাহরই নিকট হতে, যিনি পরাক্রান্ত, প্রজ্ঞাময়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (420,3,127,'যাতে তিনি কাফিরদের দলবিশেষকে ধ্বংস করে দেন কিংবা লাঞ্ছিত করেন, ফলে তারা নিরাশ হয়ে প্রত্যাবর্তন করে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (421,3,128,'আল্লাহ তাদের প্রতি ক্ষমাশীল হবেন অথবা তাদেরকে শাস্তি প্রদান করবেন- এ ব্যাপারে তোমার কিছু করার নেই। কেননা তারা হচ্ছে যালিম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (422,3,129,'আকাশসমূহে ও যমীনে যা কিছুই আছে সেসব আল্লাহরই, তিনি যাকে ইচ্ছে ক্ষমা করেন এবং যাকে ইচ্ছে শাস্তি প্রদান করেন। আল্লাহ ক্ষমাশীল, পরম দয়ালু। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (423,3,130,'হে মু’মিনগণ! তোমরা সুদ খেও না ক্রমবর্ধিতভাবে, আল্লাহকে ভয় কর, যাতে তোমরা সফলকাম হতে পার। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (424,3,131,'ভয় কর সেই আগুনকে, যা কাফিরদের জন্য প্রস্তুত করা হয়েছে।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (425,3,132,'আল্লাহর ও রসূলের আনুগত্য কর, যাতে তোমরা কৃপাপ্রাপ্ত হতে পার। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (426,3,133,'তোমরা দ্রুত অগ্রসর হও তোমাদের প্রতিপালকের ক্ষমার দিকে ও সেই জান্নাতের দিকে যার বিস্তৃতি হচ্ছে আসমানসমূহ ও যমীনের সমান, যা মুত্তাকীদের জন্য তৈরী করা হয়েছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (427,3,134,'যারা সচ্ছল ও অসচ্ছল অবস্থায় (আল্লাহর পথে) ব্যয় করে এবং যারা ক্রোধ সংবরণকারী এবং মানুষের প্রতি ক্ষমাশীল, আল্লাহ সৎকর্মশীলদের ভালবাসেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (428,3,135,'যারা কোন পাপ কাজ করে ফেললে কিংবা নিজেদের প্রতি যুলম করলে আল্লাহকে স্মরণ করে এবং নিজেদের পাপের জন্য ক্ষমা প্রার্থনা করে এবং আল্লাহ ব্যতীত গুনাহসমূহের ক্ষমাকারী কেই বা আছে এবং তারা জেনে শুনে নিজেদের (পাপ) কাজের পুনরাবৃত্তি করে না।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (429,3,136,'এরাই তারা যাদের জন্য রয়েছে তাদের প্রতিপালকের পক্ষ হতে ক্ষমা এবং এমন এক জান্নাত যার নিম্নে ঝর্ণাধারা প্রবাহিত, তারা তার স্থায়ী অধিবাসী এবং সৎকর্মশীলদের পুরস্কার কতই না উত্তম! ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (430,3,137,'তোমাদের পূর্বেও অনেক সম্প্রদায় গত হয়েছে, তোমরা দুনিয়া পর্যটন কর, তারপর দেখ, যারা মিথ্যা ব’লে অমান্য করেছিল তাদের পরিণাম কী দাঁড়িয়েছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (431,3,138,'এটা হচ্ছে মানুষের জন্য সুস্পষ্ট বর্ণনা এবং মুত্তাকীদের জন্য হিদায়াত ও নাসীহাত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (432,3,139,'তোমরা হীনবল ও দুঃখিত হয়ো না, বস্তুতঃ তোমরাই জয়ী থাকবে যদি তোমরা মু’মিন হও। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (433,3,140,'যদি তোমাদেরকে আঘাত স্পর্শ করে, অনুরূপ আঘাত তো অপর পক্ষকেও স্পর্শ করেছিল। (জয়-পরাজয়ের) এ দিনগুলোকে আমি মানুষের মধ্যে আবর্তিত করে থাকি যাতে আল্লাহ মু’মিনদেরকে চিনে নিতে পারেন এবং তোমাদের মধ্যে কাউকে কাউকে শহীদ হিসেবে গ্রহণ করতে পারেন, বস্তুতঃ আল্লাহ যালিমদেরকে ভালবাসেন না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (434,3,141,'এবং (এ জন্যেও) যেন আল্লাহ মু’মিনদেরকে সংশোধন করেন ও কাফিরদের নিশ্চিহ্ন করেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (435,3,142,'তোমরা কি ভেবেছ যে, তোমরা জান্নাতে প্রবেশ করবে, অথচ আল্লাহ এখন পর্যন্তও পরখ করেননি তোমাদের মধ্যে কে জিহাদ করেছে আর কারা ধৈর্যশীল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (436,3,143,'(শাহাদাতের) মৃত্যুর সাক্ষাৎ লাভের পূর্বে তোমরা তা কামনা করতে, এখন তো তোমরা তা দিব্যদৃষ্টিতে দেখলে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (437,3,144,'মুহাম্মাদ হচ্ছে একজন রসূল মাত্র, তাঁর পূর্বে আরও অনেক রসূল গত হয়েছে; কাজেই যদি সে মারা যায় কিংবা নিহত হয়, তবে কি তোমরা উল্টাদিকে ঘুরে দাঁড়াবে? এবং যে ব্যক্তি উল্টাদিকে ফিরে দাঁড়ায় সে আল্লাহর কোনই ক্ষতি করতে পারবে না এবং আল্লাহ কৃতজ্ঞদেরকে অতিশীঘ্র বিনিময় প্রদান করবেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (438,3,145,'কোন জীবই আল্লাহর অনুমতি ছাড়া মরতে পারে না, তার মেয়াদ নির্ধারিত। যে ব্যক্তি পার্থিব ফল চায়, আমি তাত্থেকে তাকে দেই, আর যে ব্যক্তি আখেরাতের ফল চায়, আমি তাকে তাত্থেকে দেই এবং কৃতজ্ঞদেরকে আমি শীঘ্রই বিনিময় প্রদান করব। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (439,3,146,'কত নাবী যুদ্ধ করেছে, তাদের সাথে ছিল বহু লোক, তখন তারা আল্লাহর পথে তাদের উপর সংঘটিত বিপদের জন্য হীনবল হয়নি, দুর্বল হয়নি, দুর্বল, অপারগ হয়নি, বস্তুতঃ আল্লাহ ধৈর্যশীলদেরকে ভালবাসেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (440,3,147,'তাদের মুখ হতে কেবল এ কথাই বেরিয়েছিল- ‘হে আমাদের প্রতিপালক! আমাদের অপরাধগুলো এবং আমাদের কাজ-কর্মে বাড়াবাড়িগুলোকে তুমি ক্ষমা করে দাও, আমাদেরকে দৃঢ়পদ রেখ এবং কাফিরদলের উপর আমাদেরকে সাহায্য কর।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (441,3,148,'সুতরাং আল্লাহ তাদেরকে পার্থিব সুফল প্রদান করলেন আর পরকালীন উৎকৃষ্ট সুফল। আল্লাহ সৎকর্মশীলদেরকে ভালবাসেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (442,3,149,'হে বিশ্বাসীগণ! যদি তোমরা কাফিরদের আনুগত্য কর তাহলে তারা তোমাদেরকে পশ্চাদ্দিকে ফিরিয়ে নিয়ে যাবে, তখন তোমরা ক্ষতিগ্রস্ত হয়ে পড়বে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (443,3,150,'বরং আল্লাহ্ই তোমাদের অভিভাবক এবং তিনিই সর্বশ্রেষ্ঠ সাহায্যকারী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (444,3,151,'অতিসত্বরই আমি কাফিরদের অন্তরে ভয় সঞ্চার করব, কারণ তারা আল্লাহর শরীক গ্রহণ করেছে যার স্বপক্ষে তিনি কোনও সনদ অবতীর্ণ করেননি, তাদের নিবাস হবে জাহান্নাম এবং যালিমদের নিবাস কতই না জঘন্য! ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (445,3,152,'(উহূদের রণক্ষেত্রে) আল্লাহ তোমাদেরকে স্বীয় ওয়া‘দা সঠিকরূপে দেখালেন, যখন তোমরা আল্লাহর নির্দেশে কাফিরদেরকে নিপাত করছিলে, অতঃপর যখন তোমরা নিজেরাই (পার্থিব লাভের বশে) দুর্বল হয়ে গেলে এবং (নেতার) হুকুম সম্বন্ধে মতভেদ করলে এবং তোমাদেরকে তোমাদের আকাঙ্ক্ষিত বস্তু দেখানোর পর তোমরা অবাধ্য হলে, তোমাদের কেউ কেউ দুনিয়ার প্রত্যাশী হলে এবং কেউ কেউ পরকাল চাইলে, অতঃপর তিনি তোমাদেরকে শত্রুদের হতে ফিরিয়ে দিলেন তোমাদেরকে পরীক্ষা করার জন্য, আল্লাহ অবশ্য তোমাদেরকে ক্ষমা করলেন, বস্তুতঃ আল্লাহ মু’মিনদের প্রতি অনুগ্রহশীল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (446,3,153,'(স্মরণ কর) যখন তোমরা উঁচু জমির দিকে উঠছিলে এবং কারও দিকে ফিরে তাকানোর মত হুঁশটুকু তোমাদের ছিল না এবং রসূল তোমাদের পশ্চাতে থেকে তোমাদেরকে ডাকছিল। ফলে আল্লাহ তোমাদেরকে কষ্টের উপর কষ্ট প্রদান করলেন, যাতে তোমরা যা হারিয়েছ অথবা তোমাদের উপর যে বিপদ এসেছে (তার কারণ উপলব্ধি করার পর ভবিষ্যতে) তার জন্য দুঃখিত না হও, বস্তুতঃ তোমরা যা-ই কর আল্লাহ সে ব্যাপারে বিশেষভাবে অবহিত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (447,3,154,'অতঃপর কষ্টের পর আল্লাহ তোমাদের প্রতি শান্তি-তন্দ্রা প্রেরণ করলেন, যা তোমাদের একদলকে আচ্ছন্ন করল এবং অন্যদল মূর্খের মতো আল্লাহর প্রতি কুধারণা পোষণ করতঃ নিজেরাই নিজেদের জীবনকে উদ্বেগাকুল করে বলল, কাজ-কর্মের ব্যাপারে (সিদ্ধান্ত গ্রহণের) আমাদের কিছুমাত্র অধিকার আছে কি? বল, ‘সমস্তই আল্লাহর নিরঙ্কুশ অধিকারভুক্ত’। তারা এমন সব কথা অন্তরে পোষণ করে- যা তোমার কাছে প্রকাশ করে না। তারা বলে, ‘যদি মতামত প্রদানের অধিকার আমাদের কিছুমাত্রও থাকত, তাহলে আমরা এ স্থলে নিহত হতাম না’। বলে দাও, ‘যদি তোমরা তোমাদের ঘরেও থাকতে, তথাপি যাদের ভাগ্যে মৃত্যু লেখা ছিল, তারা তাদের এ মৃত্যুশয্যার পানে বের হয়ে পড়ত’। এবং এজন্যও যে আল্লাহ তোমাদের অন্তরের ভেতরের বিষয়গুলো পরীক্ষা করেন এবং তোমাদের অন্তরস্থ বিষয়গুলোকে পরিষ্কার করেন, বস্তুতঃ আল্লাহ সকলের অন্তরের কথা সম্পর্কে বিশেষভাবে অবহিত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (448,3,155,'দু’দল পরস্পর সম্মুখীন হওয়ার দিন তোমাদের মধ্যে যারা পলায়নপর হয়েছিল, তোমাদের কোন কোন অতীত কার্যকলাপের জন্য শয়ত্বান তাদের পদস্খলন ঘটিয়েছিল এবং নিঃসন্দেহে আল্লাহ তাদেরকে ক্ষমা করেছেন; নিশ্চয়ই আল্লাহ ক্ষমাপরায়ণ, অতি সহনশীল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (449,3,156,'হে মু’মিনগণ! তোমরা তাদের মত হয়ো না, যারা কুফরী করে এবং তাদের ভাই-বন্ধুগণ যখন বিদেশে সফর করে কিংবা কোথাও যুদ্ধে লিপ্ত হয় তাদের সম্বন্ধে বলে, ‘তারা আমাদের কাছে থাকলে মরত না, নিহতও হত না’। ফলে আল্লাহ এটিকে তাদের মনের অনুতাপে পরিণত করে দেন, বস্তুতঃ আল্লাহ্ই জীবিত করেন ও মৃত্যুদান করেন আর তোমরা যা কিছু কর, আল্লাহ তার সম্যক দ্রষ্টা। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (450,3,157,'যদি তোমরা আল্লাহর পথে নিহত হও কিংবা মৃত্যুবরণ কর, তবে আল্লাহর দয়া ও ক্ষমা অতি উত্তম তারা যা সঞ্চয় করে তার চেয়ে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (451,3,158,'তোমরা মারা গেলে বা নিহত হলে অবশ্যই আল্লাহর নিকট তোমাদেরকে একত্রিত করা হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (452,3,159,'সুতরাং আল্লাহর পরম অনুগ্রহ যে তুমি তাদের উপর দয়ার্দ্র রয়েছ, এবং যদি তুমি রূঢ় মেজাজ ও কঠিন হৃদয় হতে তবে অবশ্যই তারা তোমার নিকট হতে সরে যেত। সুতরাং তাদের দোষ ক্ষমা কর এবং আল্লাহর কাছে তাদের গুনাহের জন্য ক্ষমা চাও এবং কাজ-কর্মে তাদের সঙ্গে পরামর্শ কর, অতঃপর যখন (কোন ব্যাপারে) সংকল্পবদ্ধ হও, তখন আল্লাহরই প্রতি ভরসা কর; নিশ্চয় আল্লাহ ভরসাকারীদেরকে পছন্দ করেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (453,3,160,'যদি আল্লাহ তোমাদের সাহায্য করেন, তবে তোমাদের উপর কেউই বিজয়ী হতে পারবে না এবং যদি তিনি তোমাদের সাহায্য না করেন, সে অবস্থায় এমন কে আছে যে, তোমাদেরকে সাহায্য করবে? মু’মিনদের আল্লাহর প্রতি নির্ভর করাই উচিত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (454,3,161,'কোন নাবী খিয়ানাত করতে পারে না, যে ব্যক্তি খিয়ানাত করবে, সে খিয়ানাতকৃত বস্তুসহ ক্বিয়ামাতের দিন উপস্থিত হবে, অতঃপর প্রত্যেককে যা সে অর্জন করেছে তা পুরোপুরি দেয়া হবে, কারও প্রতি কোন প্রকার যুলম করা হবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (455,3,162,'যে ব্যক্তি আল্লাহর সন্তুষ্টি অনুসরণ করে, সে কি আল্লাহর আক্রোশে পতিত লোকের ন্যায় হতে পারে? তার নিবাস হল জাহান্নাম, আর তা কতই না নিকৃষ্ট প্রত্যাবর্তনস্থল! ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (456,3,163,'আল্লাহর নিকট তাদের বিভিন্ন মর্যাদা রয়েছে, বস্তুতঃ তারা যা কিছুই করছে, আল্লাহ তার সম্যক দ্রষ্টা। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (457,3,164,'নিশ্চয় আল্লাহ মু’মিনদের প্রতি অত্যন্ত অনুকম্পা প্রদর্শন করেছেন, যখন তাদের নিকট তাদের নিজস্ব একজনকে রসূল করে পাঠিয়েছেন, সে তাদেরকে আল্লাহর আয়াত পড়ে শুনাচ্ছে, তাদেরকে পরিশোধন করছে, তাদেরকে কিতাব ও হিকমাত (সুন্নাহ) শিক্ষা দিচ্ছে, যদিও তারা পূর্বে সুস্পষ্ট গোমরাহীতে ছিল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (458,3,165,'কী ব্যাপার! তোমাদের উপর যখন বিপদ এসেছে অথচ তোমরা তো (বদর যুদ্ধে তোমাদের শত্রুদের) এটা অপেক্ষা দ্বিগুণ বিপদ ঘটিয়েছিলে, এখন তোমরা বলছ, ‘এটা কোথেকে আসল’? (তাদেরকে) বল, ‘ওটা তোমাদের নিজেদের নিকট থেকেই এসেছে’, নিশ্চয় আল্লাহ সকল বস্তুর উপর ক্ষমতাবান। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (459,3,166,'দু’দল পরস্পরের সম্মুখীন হওয়ার দিন যে বিপদ পৌঁছেছিল, তা আল্লাহর হুকুমে ঘটেছিল, এর উদ্দেশ্য ছিল প্রকৃত মু’মিনদেরকে জেনে নেয়া। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (460,3,167,'আর মুনাফিকদেরকেও জেনে নেয়া। তাদেরকে বলা হয়েছিল; এসো, ‘আল্লাহর পথে যুদ্ধ কর, কিংবা (কমপক্ষে) নিজেদের প্রতিরক্ষার ব্যবস্থা কর’। তখন তারা বলল, ‘যদি আমরা জানতাম যুদ্ধ হবে, তাহলে অবশ্যই তোমাদের অনুসরণ করতাম’। তারা ঐ দিন ঈমানের চেয়ে কুফরীরই নিকটতম ছিল, তারা মুখে এমন কথা বলে যা তাদের অন্তরে নেই, যা কিছু তারা গোপন করে আল্লাহ তা বিশেষরূপে জ্ঞাত আছেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (461,3,168,'যারা বসে থেকে নিজেদের ভাইদের সম্বন্ধে বলতে লাগল, আমাদের কথামত চললে তারা নিহত হত না। বল, তোমরা সত্যবাদী হলে তোমাদের নিজেদের উপর থেকে মরণকে হটিয়ে দাও। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (462,3,169,'যারা আল্লাহর পথে নিহত হয়েছে, তাদেরকে মৃত ভেব না, বরং তারা জীবিত, তাদের প্রতিপালকের সান্নিধ্যে থেকে তারা রিযকপ্রাপ্ত হচ্ছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (463,3,170,'আল্লাহ নিজ অনুগ্রহে তাদেরকে যা দিয়েছেন তা লাভ করে তারা আনন্দিত আর যে সব ঈমানদার লোক তাদের পেছনে (পৃথিবীতে) রয়ে গেছে, এখনও তাদের সাথে এসে মিলিত হয়নি, তাদের কোন ভয় ও চিন্তে নেই জেনে তারা আনন্দিত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (464,3,171,'ল্লাহর নি‘মাত এবং অনুগ্রহের কারণে তারা আনন্দ প্রকাশ করে আর এটা জেনে যে, আল্লাহ মু’মিনদের সাওয়াব বিনষ্ট করেন না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (465,3,172,'আঘাতপ্রাপ্ত হওয়ার পরও যারা আল্লাহ এবং রসূলের ডাকে সাড়া দিয়েছে তাদের মধ্যে যারা সৎকাজ করে ও তাকওয়া অবলম্বন করে তাদের জন্য রয়েছে মহা প্রতিদান। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (466,3,173,'যাদেরকে লোকে খবর দিয়েছিল যে, একটা বড় বাহিনী তোমাদের বিরুদ্ধে জড় হচ্ছে, কাজেই তাদেরকে ভয় কর। তখন তা তাদের ঈমান বৃদ্ধি করে দিল এবং তারা বলল, ‘আমাদের জন্যে আল্লাহ্ই যথেষ্ট এবং তিনি কতই না উত্তম কর্মবিধায়ক!’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (467,3,174,'অতঃপর তারা আল্লাহর নি‘মাত ও অনুগ্রহসহ ফিরে আসল, কোনও প্রকার অনিষ্ট তাদেরকে স্পর্শ করেনি, তারা আল্লাহর সন্তুষ্টির অনুসরণ করেছিল এবং আল্লাহ মহাকল্যাণময়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (468,3,175,'এ লোকেরা হচ্ছে শয়ত্বান; তোমাদেরকে তার বন্ধুদের ভয় দেখায়, তোমরা তাদেরকে ভয় করো না, আমাকেই ভয় কর, যদি তোমরা মু’মিন হও। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (469,3,176,'যারা কুফরীর দিকে দ্রুত ধাবমান তারা যেন তোমাকে মর্মপীড়া না দেয়। তারা কক্ষনো আল্লাহর কিছুই ক্ষতি করতে পারবে না। পরকালে কোন অংশই আল্লাহ তাদেরকে দিতে ইচ্ছে করেন না, তাদের জন্য আছে মহাশাস্তি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (470,3,177,'যারা ঈমানের বিনিময়ে কুফরী ক্রয় করেছে, তারা কক্ষনো আল্লাহর কিছুমাত্র অনিষ্ট করতে পারবে না, তাদের জন্য আছে যন্ত্রণাদায়ক শাস্তি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (471,3,178,'কাফিরগণ যেন কিছুতেই এ ধারণা পোষণ না করে যে, তাদেরকে আমি যে অবকাশ দিয়েছি তা তাদের জন্য মঙ্গলজনক; আমি তাদেরকে শুধু এজন্য অবকাশ দেই যে, যেন তাদের পাপের পরিমাণ বেড়ে যায়, তাদের জন্য আছে লাঞ্ছনাদায়ক শাস্তি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (472,3,179,'অসৎকে সৎ থেকে পৃথক না করা পর্যন্ত তোমরা যে অবস্থায় আছ, আল্লাহ মু’মিনদেরকে সে অবস্থায় ছেড়ে দিতে পারেন না এবং আল্লাহ তোমাদেরকে গায়িবের বিধান জ্ঞাত করেন না, তবে আল্লাহ তাঁর রাসূলগণের মধ্যে যাকে ইচ্ছে বেছে নেন, কাজেই তোমরা আল্লাহ এবং তাঁর রসূলগণের প্রতি ঈমান আন। যদি তোমরা ঈমান আন আর তাকওয়া অবলম্বন কর, তাহলে তোমাদের জন্য আছে মহাপুরস্কার। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (473,3,180,'আল্লাহ নিজ অনুগ্রহে যা তাদেরকে দিয়েছেন তাতে যারা কৃপণতা করে, তারা যেন মনে না করে যে, তা তাদের জন্য কল্যাণকর, বরং তা তাদের জন্য (খুবই) অকল্যাণকর, তারা যাতে কৃপণতা করেছে, সত্বর ক্বিয়ামাতের দিন তারই বেড়ি তাদের গলায় পরিয়ে দেয়া হবে। আসমান ও যমীনের স্বত্বাধিকার কেবল আল্লাহরই। তোমরা যা কিছুই করছ আল্লাহ সে সম্পর্কে পূর্ণরূপে অবহিত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (474,3,181,'আল্লাহ অবশ্যই তাদের উক্তি শ্রবণ করেছেন যারা বলে, ‘আল্লাহ দরিদ্র এবং আমরা ধনী’, তারা যা বলে তা আমি অবশ্যই লিপিবদ্ধ করে রাখব এবং (তাদের) অন্যায়ভাবে নাবীগণকে হত্যা করার বিষয়টিও (লিপিবদ্ধ করে রাখব) এবং আমি বলব- ‘জাহান্নামের দহন যন্ত্রণা ভোগ কর’। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (475,3,182,'এটা তোমাদের আগেই পাঠানো কাজের বিনিময়, কারণ আল্লাহ স্বীয় বান্দাগণের প্রতি কোন প্রকার যুলম করেন না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (476,3,183,'যারা ব’লে থাকে যে, ‘আল্লাহ আমাদের কাছে অঙ্গীকার নিয়েছেন, যেন আমরা আগুনে গ্রাস করে এমন কোন কুরবানী আমাদের সামনে না দেখানো পর্যন্ত কোন রসূলের প্রতি ঈমান না আনি’। বল, ‘আমার পূর্বে বহু রসূল বহু প্রমাণসহ তোমাদের নিকট এসেছিল এবং তোমাদের কথিত সেই মু’জিযা নিয়েও (এসেছিল)। যদি তোমরা সত্যবাদী হও, তাহলে কেন তোমরা তাদেরকে হত্যা করেছিলে?’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (477,3,184,'তারপরও যদি কাফিরগণ তোমাকে অস্বীকার করে, তবে তোমার পূর্বেও রসূলগণকে অস্বীকার করা হয়েছিল যারা স্পষ্ট নিদর্শন, অনেক সহীফা এবং দীপ্তিমান কিতাব নিয়ে এসেছিল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (478,3,185,'প্রতিটি জীবন মৃত্যুর আস্বাদ গ্রহণ করবে এবং ক্বিয়ামাতের দিন তোমাদেরকে পূর্ণমাত্রায় বিনিময় দেয়া হবে। যে ব্যক্তিকে জাহান্নামের আগুন হতে রক্ষা করা হল এবং জান্নাতে দাখিল করা হল, অবশ্যই সে ব্যক্তি সফলকাম হল, কেননা পার্থিব জীবন ছলনার বস্তু ছাড়া আর কিছুই নয়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (479,3,186,'অবশ্যই তোমরা তোমাদের ধনের ও জনের ব্যাপারে পরীক্ষিত হবে এবং তোমরা নিশ্চয়ই তোমাদের আগের কিতাবধারীদের ও মুশরিকদের নিকট হতে দুঃখজনক অনেক কথা শুনবে এবং তোমরা যদি ধৈর্যধারণ কর আর তাকওয়া অবলম্বন কর, তবে অবশ্যই তা হবে দৃঢ় সংকল্পের কাজ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (480,3,187,'(স্মরণ কর) আল্লাহ আহলে কিতাবদের নিকট থেকে অঙ্গীকার নিয়েছিলেন- তোমরা অবশ্যই তা (অর্থাৎ কিতাব) মানুষের কাছে স্পষ্টভাবে বর্ণনা করবে আর তা গোপন করবে না, কিন্তু তারা তা অগ্রাহ্য করল এবং সামান্য মূল্যে বিক্রি করল, তারা যা ক্রয় করল সে বস্তু কতই না মন্দ! ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (481,3,188,'তোমরা এ সব লোককে আযাব থেকে সুরক্ষিত মনে করো না যারা নিজেদের কৃতকর্মের জন্য আনন্দিত এবং এমন কাজের জন্য প্রশংসিত হতে চায় মূলতঃ যা তারা করেনি, তাদের জন্য আছে যন্ত্রণাদায়ক শাস্তি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (482,3,189,'বস্তুতঃ আসমান ও যমীনে আছে আল্লাহরই রাজত্ব এবং আল্লাহ সকল জিনিসের উপর ক্ষমতাবান। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (483,3,190,'নিশ্চয়ই আসমানসমূহ ও যমীনের সৃষ্টিতে এবং রাত্র ও দিনের আবর্তনে জ্ঞানবানদের জন্য বহু নিদর্শন আছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (484,3,191,'যারা আল্লাহকে দন্ডায়মান, উপবিষ্ট এবং শায়িত অবস্থায় স্মরণ করে থাকে এবং আসমান ও যমীনের সৃষ্টির ব্যাপারে চিন্তা করে (ও বলে) : ‘হে আমাদের প্রতিপালক! তুমি এসব অনর্থক সৃষ্টি করনি, তোমার পবিত্রতা বর্ণনা করছি, সুতরাং আমাদেরকে অগ্নির শাস্তি হতে রক্ষা কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (485,3,192,'হে আমাদের প্রতিপালক! তুমি যাকে আগুনে নিক্ষেপ করবে, তাকে অবশ্যই তুমি অপমান করবে আর যালিমদের কোন সাহায্যকারী নেই। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (486,3,193,'হে আমাদের প্রতিপালক! আমরা একজন ঘোষণাকারীকে ঈমানের ঘোষণা করতে শুনেছি যে, ‘তোমরা তোমাদের প্রতিপালকের প্রতি ঈমান আনো’। সে অনুযায়ী আমরা ঈমান এনেছি। সুতরাং হে আমাদের প্রতিপালক! আমাদের গুনাহ্গুলো ক্ষমা কর এবং আমাদের থেকে আমাদের মন্দ কাজগুলো বিদূরিত কর আর নেক বান্দাদের সঙ্গে শামিল করে আমাদের মৃত্যু ঘটাও। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (487,3,194,'‘হে আমাদের প্রতিপালক! তুমি স্বীয় রসূলদের মারফত আমাদেরকে যেসব বস্তুর ওয়াদা শুনিয়েছ, তা আমাদেরকে দান কর এবং ক্বিয়ামাতের দিন আমাদেরকে লাঞ্ছিত করো না, নিশ্চয়ই তুমি ওয়া‘দা খেলাফ কর না।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (488,3,195,'তখন তাদের প্রতিপালক তাদের ডাকে সাড়া দিয়ে বললেন, তোমাদের মধ্যে পুরুষ হোক কিংবা নারীই হোক কোন কর্মীরই কর্মফল আমি নষ্ট করি না, তোমরা একে অপরের অংশ। সুতরাং যারা হিজরাত করবে এবং স্বীয় গৃহ হতে বিতাড়িত হবে ও আমার পথে নির্যাতিত হবে, যুদ্ধ করবে ও নিহত হবে, নিশ্চয় আমি তাদের গুনাহসমূহ দূর করে দেব এবং নিশ্চয়ই তাদেরকে এমন জান্নাতে দাখিল করবো, যার নীচ দিয়ে বয়ে যাচ্ছে নদ-নদী, আল্লাহর নিকট হতে পুরস্কার হিসেবে, বস্তুতঃ আল্লাহর নিকটেই উত্তম বিনিময়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (489,3,196,'দেশে দেশে কাফিরদের সদম্ভ পদচারণা তোমাকে যেন বিভ্রান্ত না করে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (490,3,197,'সামান্য ভোগ, তারপর জাহান্নাম তাদের আবাস, আর তা কতই না নিকৃষ্ট বিশ্রামস্থল! ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (491,3,198,'কিন্তু যারা তাদের প্রতিপালককে ভয় করে, তাদের জন্য আছে জান্নাত যার পাদদেশে ঝর্ণাধারা প্রবাহিত, তারা তাতে চিরকাল থাকবে, এ হল আল্লাহর নিকট হতে আতিথ্য আর আল্লাহর নিকট যা আছে, তা সৎকর্মপরায়ণদের জন্য অতি উত্তম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (492,3,199,'আহলে কিতাবদের মধ্যেকার কিছু লোক নিঃসন্দেহে এমনও আছে, যারা আল্লাহর উপর এবং তোমাদের উপর অবতীর্ণ কিতাবের এবং তাদের প্রতি অবতীর্ণ কিতাবের উপর ঈমান রাখে, তারা আল্লাহর প্রতি বিনয়াবনত, তারা আল্লাহর আয়াতকে তুচ্ছমূল্যে বিক্রয় করে না, এরাই তারা যাদের জন্য তাদের কর্মফল নির্ধারিত রয়েছে তাদের প্রতিপালকের নিকট, আল্লাহ হিসেব গ্রহণে ত্বরিতগতি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (493,3,200,'হে মু’মিনগণ! ধৈর্য অবলম্বন কর, দৃঢ়তা প্রদর্শন কর, নিজেদের প্রতিরক্ষাকল্পে পারস্পরিক বন্ধন মজবুত কর এবং আল্লাহকে ভয় কর, যাতে তোমরা সফলকাম হতে পার। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (494,4,1,'হে মনুষ্য সমাজ! তোমরা তোমাদের প্রতিপালককে ভয় কর, যিনি তোমাদেরকে একটি মাত্র ব্যক্তি হতে পয়দা করেছেন এবং তা হতে তার জোড়া সৃষ্টি করেছেন, অতঃপর সেই দু’জন হতে বহু নর-নারী ছড়িয়ে দিয়েছেন এবং তোমরা আল্লাহকে ভয় কর, যাঁর নামে তোমরা পরস্পর পরস্পরের নিকট (হাক্ব) চেয়ে থাক এবং সতর্ক থাক জ্ঞাতি-বন্ধন সম্পর্কে, নিশ্চয়ই আল্লাহ তোমাদের উপর তীক্ষ্ণ দৃষ্টি রাখেন।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (495,4,2,'এবং ইয়াতীমদেরকে তাদের ধন-সম্পদ প্রদান কর এবং ভালোর সাথে মন্দের বদল করো না এবং তাদের মাল নিজেদের মালের সঙ্গে মিশিয়ে গ্রাস করো না, নিশ্চয় এটা মহাপাপ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (496,4,3,'যদি তোমরা আশঙ্কা কর যে, (নারী) ইয়াতীমদের প্রতি সুবিচার করতে পারবে না, তবে নারীদের মধ্য হতে নিজেদের পছন্দমত দুই-দুই, তিন-তিন ও চার-চার জনকে বিবাহ কর, কিন্তু যদি তোমরা আশঙ্কা কর যে, তোমরা সুবিচার করতে পারবে না, তাহলে একজনকে কিংবা তোমাদের অধীনস্থ দাসীকে; এটাই হবে অবিচার না করার কাছাকাছি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (497,4,4,'নারীদেরকে তাদের মোহর স্বতঃস্ফূর্ত হয়ে দেবে, অতঃপর তারা যদি সন্তোষের সঙ্গে তাথেকে তোমাদের জন্য কিছু ছেড়ে দেয়, তবে তা তৃপ্তির সঙ্গে ভোগ কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (498,4,5,'এবং তোমরা অল্প-বুদ্ধিসম্পন্নদেরকে নিজেদের মাল প্রদান করো না, যা আল্লাহ তোমাদের জীবনে প্রতিষ্ঠিত থাকার উপকরণ করেছেন এবং সে মাল হতে তাদের অন্ন-বস্ত্রের ব্যবস্থা করবে এবং তাদের সঙ্গে দয়ার্দ্র ন্যায়ানুগ কথা বলবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (499,4,6,'ইয়াতীমদেরকে পরখ কর যে পর্যন্ত না তারা বিবাহযোগ্য হয়, যদি তাদের মধ্যে বিচারবোধ লক্ষ্য কর, তবে তাদেরকে তাদের মাল ফিরিয়ে দেবে। তাদের বয়ঃপ্রাপ্তির ভয়ে অপব্যয় করে এবং তাড়াতাড়ি করে তাদের মাল খেয়ে ফেলো না। আর যে অভাবমুক্ত, সে যেন নিবৃত্ত থাকে এবং যে অভাবগ্রস্ত সে ন্যায়সঙ্গতভাবে ভোগ করবে এবং যখন তাদের মাল  তাদেরকে সমর্পণ করবে, তাদের সামনে সাক্ষী রাখবে; হিসাব গ্রহণে আল্লাহই যথেষ্ট। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (500,4,7,'মাতা-পিতা এবং আত্মীয়দের রেখে যাওয়া সম্পত্তিতে পুরুষদের অংশ রয়েছে; আর মাতা-পিতা এবং আত্মীয়দের রেখে যাওয়া সম্পত্তিতে নারীদেরও অংশ আছে, তা অল্পই হোক আর বেশিই হোক, এক নির্ধারিত অংশ।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (501,4,8,'(সম্পত্তি) বণ্টনকালে স্বজন, ইয়াতীম এবং মিসকীন উপস্থিত থাকলে তাদেরকেও তাত্থেকে কিছু দিয়ে দেবে, তাদের সঙ্গে দয়ার্দ্র ন্যায়ানুগ কথা বলবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (502,4,9,'তারা যেন ভয় করে যে, অসহায় সন্তান পেছনে ছেড়ে গেলে তারাও তাদের জন্য চিন্তিত হত, সুতরাং তারা যেন আল্লাহকে ভয় করে এবং সঙ্গত কথা বলে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (503,4,10,'যারা ইয়াতীমদের মাল অন্যায়ভাবে গ্রাস করে, তারা তো নিজেদের পেটে কেবল অগ্নিই ভক্ষণ করে, তারা শীঘ্রই জ্বলন্ত আগুনে জ্বলবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (504,4,11,'আল্লাহ তোমাদেরকে তোমাদের সন্তান-সন্ততির (অংশ) সম্পর্কে নির্দেশ দিচ্ছেন, পুরুষ দুই নারীর অংশের সমান পাবে, তবে সন্তান-সন্ততি যদি শুধু দু’জন নারীর অধিক হয় তাহলে তাঁরা রেখে যাওয়া সম্পত্তির তিন ভাগের দু’ ভাগ পাবে, আর কেবল একটি কন্যা থাকলে সে অর্ধেক পাবে এবং তার পিতা-মাতা উভয়ের প্রত্যেকে রেখে যাওয়া সম্পত্তির ছয় ভাগের এক ভাগ পাবে যদি তার সন্তান থাকে, আর যদি তার সন্তান না থাকে এবং তার ওয়ারিশ মাতা-পিতাই হয়, সে অবস্থায় তার মাতার জন্য এক তৃতীয়াংশ, কিন্তু তার ভাই-বোন থাকলে, তার মাতা পাবে ছয় ভাগের এক ভাগ, (ঐসব বণ্টন হবে) তার কৃত ওয়াসীয়াত অথবা ঋণ পরিশোধের পর। তোমরা জান না তোমাদের পিতা এবং সন্তানদের মধ্যে কে তোমাদের পক্ষে উপকারের দিক দিয়ে অধিকতর নিকটবর্তী। (এ বণ্টন) আল্লাহর পক্ষ হতে নির্ধারিত করে দেয়া হয়েছে, নিশ্চয় আল্লাহ মহাজ্ঞানী, প্রজ্ঞাশীল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (505,4,12,'তোমাদের স্ত্রীদের রেখে যাওয়া সম্পত্তির অর্ধেক তোমাদের জন্য- যদি তাদের কোন সন্তান না থাকে আর যদি সন্তান থাকে, তবে তোমাদের জন্য তাদের রেখে যাওয়া সম্পত্তির এক চতুর্থাংশ, তাদের কৃত ওয়াসীয়াত কিংবা ঋণ পরিশোধের পর এবং তারা তোমাদের রেখে যাওয়া সম্পত্তির সিকি অংশ পাবে যদি তোমাদের কোন সন্তান না থাকে, আর তোমাদের সন্তান থাকলে তাদের জন্য তোমাদের সম্পত্তির আট ভাগের একভাগ- তোমাদের কৃত ওয়াসীয়ত কিংবা ঋণ পরিশোধের পর। যদি পিতা-মাতাহীন ও সন্তানহীন কোন পুরুষ বা নারীর শুধু বৈপিত্রেয় একটি ভাই বা একটি ভগ্নি থাকে, তবে প্রত্যেকের জন্য ছ’ ভাগের এক ভাগ। যদি তারা তার চেয়ে অধিক হয়, তবে সকলেই তৃতীয়াংশে শরীক হবে কৃত ওয়াসীয়াত কিংবা ঋণ পরিশোধের পরে, যদি কারো জন্য ক্ষতিকর না হয়, এ হল আল্লাহর বিধান, আল্লাহ সর্বজ্ঞ, সহনশীল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (506,4,13,'এসব আল্লাহর নির্ধারিত সীমা এবং যে ব্যক্তি আল্লাহ এবং তাঁর রসূলের অনুসরণ করবে, আল্লাহ তাকে জান্নাতে দাখিল করবেন, যার পাদদেশে ঝর্ণাধারা প্রবাহিত, তারা তাতে চিরবাসী হবে এবং এটা বিরাট সাফল্য। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (507,4,14,'আর যে ব্যক্তি আল্লাহ এবং তাঁর রসূলের নাফরমানী করবে এবং তাঁর নির্ধারিত সীমালঙ্ঘন করবে, আল্লাহ তাকে জাহান্নামে দাখিল করবেন, সে তাতে চিরবাসী হবে এবং সে অবমাননাকর শাস্তি ভোগ করবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (508,4,15,'তোমাদের যে সব নারী ব্যভিচার করবে, তোমরা তাদের বিরুদ্ধে তোমাদের চারজনের সাক্ষ্য গ্রহণ কর। যদি তারা সাক্ষ্য দেয়, তবে তোমরা তাদেরকে সে সময় পর্যন্ত গৃহে আবদ্ধ করে রাখবে যে পর্যন্ত না তাদের মৃত্যু হয় কিংবা আল্লাহ তাদের জন্য কোন পৃথক পথ বের করেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (509,4,16,'তোমাদের মধ্যেকার যে দু’জন তাতে লিপ্ত হবে, তোমরা সে দু’জনকে শাস্তি দেবে, অতঃপর যদি তারা তাওবাহ করে এবং নিজেদেরকে সংশোধন করে তবে তাদের ব্যাপারে নিবৃত্ত হও, নিশ্চয় আল্লাহ অতিশয় তাওবাহ কবূলকারী, পরম দয়ালু। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (510,4,17,'নিশ্চয়ই যারা অজ্ঞতাবশতঃ মন্দ কাজ ক’রে বসে, তৎপর সত্বর তাওবাহ করে, এরাই তারা যাদের তাওবাহ আল্লাহ কবূল করেন। আল্লাহ মহাজ্ঞানী, মহাবিজ্ঞানী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (511,4,18,'এমন লোকেদের তাওবাহ নিস্ফল যারা গুনাহ করতেই থাকে, অতঃপর মৃত্যুর মুখোমুখী হলে বলে, আমি এখন তাওবাহ করছি এবং (তাওবাহ) তাদের জন্যও নয় যাদের মৃত্যু হয় কাফির অবস্থায়। এরাই তারা যাদের জন্য ভয়াবহ শাস্তির ব্যবস্থা করে রেখেছি।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (512,4,19,'হে ঈমানদারগণ! জোরপূর্বক নারীদের ওয়ারিশ হওয়া তোমাদের জন্য বৈধ নয় আর তাদেরকে দেয়া মাল হতে কিছু উসূল করে নেয়ার উদ্দেশে তাদের সঙ্গে রূঢ় আচরণ করবে না, যদি না তারা সুস্পষ্ট ব্যভিচার করে। তাদের সাথে দয়া ও সততার সঙ্গে জীবন যাপন কর, যদি তাদেরকে না-পছন্দ কর, তবে হতে পারে যে তোমরা যাকে না-পছন্দ করছ, বস্তুতঃ তারই মধ্যে আল্লাহ বহু কল্যাণ দিয়ে রেখেছেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (513,4,20,'যদি তোমরা এক স্ত্রী বদলিয়ে তদস্থলে অন্য স্ত্রী গ্রহণের ইচ্ছে কর এবং তাদের একজনকে অগাধ সম্পদও দিয়ে থাক, তবুও তাত্থেকে কিছুই ফিরিয়ে নিও না। তোমরা কি (স্ত্রীর নামে) মিথ্যে দুর্নাম রটিয়ে সুস্পষ্ট গুনাহ করে তা ফেরত নেবে? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (514,4,21,'কেমন করেই বা তোমরা তা গ্রহণ করবে যখন তোমরা একে অন্যের সাথে সঙ্গত হয়েছ এবং তারা তোমাদের নিকট হতে (বিবাহ বন্ধনের) সুস্পষ্ট প্রতিশ্রুতি নিয়েছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (515,4,22,'যাদেরকে তোমাদের পিতৃপুরুষ বিয়ে করেছে, সেসব নারীকে বিয়ে করো না, পূর্বে যা হয়ে গেছে হয়ে গেছে, নিশ্চয়ই তা অশ্লীল, অতি ঘৃণ্য ও নিকৃষ্ট পন্থা। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (516,4,23,'তোমাদের প্রতি হারাম করা হয়েছে তোমাদের মা এবং মেয়ে, বোন, ফুফু, খালা, ভাইঝি, ভাগিনী, দুধ মা, দুধ বোন, শ্বাশুড়ী, তোমাদের স্ত্রীদের মধ্যে যার সাথে সঙ্গত হয়েছ তার পূর্ব স্বামীর ঔরসজাত মেয়ে যারা তোমাদের তত্ত্বাবধানে আছে, কিন্তু যদি তাদের সাথে তোমরা সহবাস না করে থাক, তবে (তাদের বদলে তাদের মেয়েদেরকে বিয়ে করলে) তোমাদের প্রতি গুনাহ নেই এবং (তোমাদের প্রতি হারাম করা হয়েছে) তোমাদের ঔরসজাত পুত্রের স্ত্রী এবং এক সঙ্গে দু’ বোনকে (বিবাহ বন্ধনে) রাখা, পূর্বে যা হয়ে গেছে, হয়ে গেছে, নিশ্চয়ই আল্লাহ পরম ক্ষমাশীল, দয়ালু। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (517,4,24,'নারীদের মধ্যে বিবাহবন্ধনে আবদ্ধ নারীগণও তোমাদের জন্য নিষিদ্ধ, কিন্তু তোমাদের অধিকারভুক্ত দাসীদের বাদে, আল্লাহ এসব ব্যবস্থা তোমাদের উপর ফরয করে দিয়েছেন। তোমাদের জন্য নিষিদ্ধ নারীদের ছাড়া অন্যান্য সকল নারীদেরকে মোহরের অর্থের বদলে বিবাহ বন্ধনে আবদ্ধ করতে চাওয়া তোমাদের জন্য বৈধ করা হয়েছে, অবৈধ যৌন সম্পর্কের জন্য নয়। অতঃপর তাদের মধ্যে যাদের তোমরা সম্ভোগ করেছ, তাদেরকে তাদের ধার্যকৃত মোহর প্রদান কর। তোমাদের প্রতি কোনও গুনাহ নেই মোহর ধার্যের পরও তোমরা উভয়ের সম্মতির ভিত্তিতে মোহরের পরিমাণে হেরফের করলে, নিশ্চয় আল্লাহ সবিশেষ পরিজ্ঞাত ও পরম কুশলী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (518,4,25,'তোমাদের যে ব্যক্তির স্বাধীনা মু’মিন নারী বিবাহের ক্ষমতা না থাকে, সে যেন তোমাদের অধীনস্থ মু’মিনা দাসী বিবাহ করে এবং আল্লাহ বিশেষরূপে তোমাদের ঈমানকে জানেন। তোমাদের একজন অন্যজন থেকে উদ্ভূত, কাজেই তাদেরকে বিয়ে কর তাদের মালিকের অনুমতি নিয়ে, ন্যায়সঙ্গতভাবে তাদের মহর তাদেরকে দিয়ে দাও, তারা হবে সচ্চরিত্রা, ব্যভিচারিণী নয়, উপপতি গ্রহণকারিণীও নয়। বিবাহের দূর্গে সুরক্ষিত হওয়ার পর তারা যদি ব্যভিচার করে, তবে তাদের শাস্তি আজাদ নারীদের অর্ধেক; এ ব্যবস্থা তার জন্য তোমাদের যে ব্যক্তি (অবিবাহিত থাকার কারণে) ব্যভিচারের ভয় করে। ধৈর্য ধারণ করা তোমাদের পক্ষে উত্তম এবং আল্লাহ পরম ক্ষমাশীল, পরম দয়ালু। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (519,4,26,'আল্লাহ ইচ্ছে করেন তোমাদের নিকট সবিস্তারে বর্ণনা করতে তোমাদের আগেকার লোকেদের নিয়মনীতি তোমাদের দেখানোর গ্রহণের জন্য, আর তোমাদেরকে ক্ষমা করার জন্য; বস্তুতঃ আল্লাহ সুপরিজ্ঞাত, পরম কুশলী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (520,4,27,'আল্লাহ তোমাদেরকে ক্ষমা করতে চান, পক্ষান্তরে কুপ্রবৃত্তির অনুসারীরা চায় যে, তোমরা (আল্লাহর নিকট হতে) দূরে বহু দূরে সরে যাও। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (521,4,28,'আল্লাহ তোমাদের ভার লঘু করতে চান, কারণ মানুষকে দুর্বলরূপে সৃষ্টি করা হয়েছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (522,4,29,'হে ঈমানদারগণ! তোমরা অন্যায়ভাবে একে অন্যের সম্পদ গ্রাস করো না, তবে পারস্পরিক সম্মতিতে ব্যবসায় বৈধ এবং নিজেদের ধ্বংস ডেকে এনো না কিংবা তোমরা পরস্পরকে হত্যা করো না, নিশ্চয় আল্লাহ তোমাদের প্রতি কৃপাময়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (523,4,30,'যে ব্যক্তি সীমালঙ্ঘন ক’রে অন্যায়ভাবে এটা করবে, তাকে আমি অতিসত্বর অগ্নিতে দগ্ধ করব, এটা আল্লাহর পক্ষে সহজ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (524,4,31,'যদি তোমরা নিষিদ্ধ কাজের বড় বড় গুলো হতে বিরত থাক, তাহলে আমি তোমাদের ছোট ছোট পাপগুলো ক্ষমা ক’রে দেব এবং তোমাদেরকে এক মহামর্যাদার স্থানে প্রবেশ করাব। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (525,4,32,'তোমরা তা কামনা করো না যা দ্বারা আল্লাহ তোমাদের কাউকে কারো উপর মর্যাদা প্রদান করেছেন। পুরুষেরা তাদের কৃতকার্যের অংশ পাবে, নারীরাও তাদের কৃতকর্মের অংশ পাবে এবং তোমরা আল্লাহর নিকট তাঁর অনুগ্রহ কামনা কর, নিশ্চয়ই আল্লাহ সর্ববিষয়ে সর্বজ্ঞ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (526,4,33,'মাতা-পিতা ও স্বজনদের রেখে যাওয়া সম্পদে আমি প্রত্যেক উত্তরাধিকারের অংশ স্থির করেছি এবং যাদের সঙ্গে তোমাদের অঙ্গীকার রয়েছে, কাজেই তাদেরকে তাদের অংশ প্রদান কর, নিশ্চয়ই আল্লাহ সর্ববিষয়ে প্রত্যক্ষদর্শী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (527,4,34,'পুরুষগণ নারীদের উপর কর্তৃত্বশীল এ কারণে যে, আল্লাহ তাদের এককে অন্যের উপর মর্যাদা প্রদান করেছেন, আর এজন্য যে, পুরুষেরা স্বীয় ধন-সম্পদ হতে ব্যয় করে। ফলে পুণ্যবান স্ত্রীরা (আল্লাহ ও স্বামীর প্রতি) অনুগতা থাকে এবং পুরুষের অনুপস্থিতিতে তারা তা (অর্থাৎ তাদের সতীত্ব ও স্বামীর সম্পদ) সংরক্ষণ করে যা আল্লাহ সংরক্ষণ করতে আদেশ দিয়েছেন। যদি তাদের মধ্যে অবাধ্যতার সম্ভাবনা দেখতে পাও, তাদেরকে সদুপদেশ দাও এবং তাদের সাথে শয্যা বন্ধ কর এবং তাদেরকে (সঙ্গতভাবে) প্রহার কর, অতঃপর যদি তারা তোমাদের অনুগত হয়, তাহলে তাদের উপর নির্যাতনের বাহানা খোঁজ করো না, নিশ্চয় আল্লাহ সর্বোচ্চ, সর্বশ্রেষ্ঠ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (528,4,35,'যদি তোমরা তাদের মধ্যে অনৈক্যের আশংকা কর, তবে স্বামীর আত্মীয়-স্বজন হতে একজন এবং স্ত্রীর আত্মীয়-স্বজন হতে একজন সালিস নিযুক্ত কর। যদি উভয়ে মীমাংসা করিয়ে দেয়ার ইচ্ছে করে, তবে আল্লাহ উভয়ের মধ্যে নিষ্পত্তির ব্যবস্থা করবেন, নিশ্চয় আল্লাহ সব কিছু জানেন, সকল কিছুর খবর রাখেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (529,4,36,'তোমরা আল্লাহর ‘ইবাদাত কর, কিছুকেই তাঁর শরীক করো না এবং মাতা-পিতা, আত্মীয়-স্বজন, ইয়াতীম অভাবগ্রস্ত, নিকট প্রতিবেশী, দূর প্রতিবেশী, সাথী, মুসাফির এবং তোমাদের আয়ত্তাধীন দাস-দাসীদের সঙ্গে সদ্ব্যবহার কর, নিশ্চয়ই আল্লাহ ঐ লোককে ভালবাসেন না, যে অহংকারী, দাম্ভিক। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (530,4,37,'যারা কৃপণতা করে অধিকন্তু লোকেদেরকেও কার্পণ্য করার আদেশ দেয় এবং আল্লাহ নিজ অনুগ্রহে তাদেরকে যা দিয়েছেন তা গোপন করে, (ঐসব) কাফিরদের জন্য আমি লাঞ্ছনাদায়ক শাস্তি প্রস্তুত করে রেখেছি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (531,4,38,'(আর সেসব লোককেও আল্লাহ পছন্দ করেন না) যারা মানুষকে দেখানোর জন্য নিজেদের ধন-সম্পদ খরচ করে এবং আল্লাহ ও আখিরাত দিবসের উপর বিশ্বাস রাখে না। শয়ত্বান কারো সঙ্গী হলে সে সঙ্গী কতই না জঘন্য! ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (532,4,39,'তাদের কী ক্ষতি হত যদি তারা আল্লাহ ও আখিরাত দিবসের উপর ঈমান আনত, আর আল্লাহ তাদেরকে যে রিযক দিয়েছেন তাত্থেকে ব্যয় করত, বস্তুতঃ আল্লাহ তাদের বিষয় খুব জ্ঞাত আছেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (533,4,40,'আল্লাহ অণু পরিমাণও যুলম করেন না, আর কোন পুণ্য কাজ হলে তাকে তিনি দ্বিগুণ করেন এবং নিজের নিকট হতেও বিরাট পুরস্কার দান করেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (534,4,41,'সুতরাং তখন কী অবস্থা দাঁড়াবে, যখন আমি প্রত্যেক উম্মাতের মধ্য হতে এক একজনকে সাক্ষী উপস্থিত করব এবং তোমাকেও হাজির করব তাদের উপর সাক্ষ্য দানের জন্য। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (535,4,42,'যারা অস্বীকার করেছে এবং রসূল-এর নাফরমানী করেছে, তারা সে দিন কামনা করবে, হায়! তারা যদি মাটির সাথে মিশে যেত এবং তারা আল্লাহ হতে কোন কথাই লুকিয়ে রাখতে পারবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (536,4,43,'হে ঈমানদারগণ! তোমরা নেশাগ্রস্ত অবস্থায় সলাতের নিকটবর্তী হয়ো না যতক্ষণ না তোমরা যা বল, তা বুঝতে পার এবং অপবিত্র অবস্থায়ও (সলাতের কাছে যেও না) গোসল না করা পর্যন্ত (মসজিদে) পথ অতিক্রম করা ব্যতীত; এবং যদি তোমরা পীড়িত হও কিংবা সফরে থাক; অথবা তোমাদের কেউ শৌচস্থান হতে আসে অথবা তোমরা স্ত্রী সঙ্গম করে থাক, অতঃপর পানি না পাও, তবে পবিত্র মাটি দিয়ে তায়াম্মুম কর, আর তা দিয়ে তোমাদের মুখমন্ডল ও হস্তদ্বয় মাসহ কর; আল্লাহ নিশ্চয়ই পাপ মোচনকারী, ক্ষমাশীল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (537,4,44,'তুমি কি সেই লোকেদের প্রতি লক্ষ্য করনি, যাদেরকে কিতাবের অংশ দেয়া হয়েছিল? তারা নিজেরা পথভ্রষ্টতার সওদা করে আর তারা চায় তোমরাও পথভ্রষ্ট হয়ে যাও। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (538,4,45,'আল্লাহ তোমাদের শত্রুদেরকে ভালভাবে জানেন, অভিভাবক হিসেবে আল্লাহ্ই যথেষ্ট এবং সাহায্যকারী হিসেবে আল্লাহ্ই যথেষ্ট। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (539,4,46,'ইয়াহূদীদের কতক লোক কথাকে প্রকৃত স্থান থেকে সরিয়ে বিকৃত করে এবং বলে, ‘আমরা শুনলাম ও অমান্য করলাম’ এবং শুনেও না শোনার মত আর নিজেদের জিহবা কুঞ্চিত ক’রে এবং দ্বীনের প্রতি দোষারোপ ক’রে বলে, ‘রাইনা’ (আমাদের রাখাল)। কিন্তু তারা যদি বলত ‘আমরা শুনলাম ও মেনে নিলাম, শোন এবং আমাদের প্রতি লক্ষ্য কর, তবে তা তাদের জন্য উত্তম এবং সঙ্গত হত, কিন্তু তাদের কুফরীর কারণে আল্লাহ তাদেরকে লা‘নত করেছেন, তারা স্বল্পসংখ্যক ব্যতীত ঈমান আনবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (540,4,47,'ওহে যাদেরকে কিতাব দেয়া হয়েছে, আমি যা নাযিল করেছি, তার উপর তোমরা ঈমান আন, যা তোমাদের কাছে যা আছে তার সমর্থক আমি তোমাদের মুখগুলোকে বিকৃত করে সেগুলোকে পিছনের দিকে ফিরিয়ে দেয়ার পূর্বে (ঈমান আন), কিংবা শনিবারওয়ালাদেরকে যেমন অভিসম্পাত করেছিলাম, এদেরকেও তেমনি অভিসম্পাত করার পূর্বে। বস্তুতঃ আল্লাহর হুকুম কার্যকরী হয়েই থাকে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (541,4,48,'নিশ্চয় আল্লাহ তাঁর সাথে শরীক করা ক্ষমা করবেন না। এটা ছাড়া অন্য সব যাকে ইচ্ছে মাফ করবেন এবং যে আল্লাহর সাথে শরীক করল, সে এক মহা অপবাদ আরোপ করল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (542,4,49,'তুমি কি তাদেরকে দেখনি, যারা নিজেদেরকে পবিত্র ব’লে দাবী করে? বরং আল্লাহ্ই যাকে ইচ্ছে পবিত্র করেন। (কেউ প্রকৃতই পবিত্রতা অর্জন করলে তা অগ্রাহ্য করতঃ) তাদের প্রতি সামান্য পরিমাণও অন্যায় করা হবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (543,4,50,'দেখ, এরা আল্লাহর সম্বন্ধে কেমন মিথ্যে রচনা করে, সুস্পষ্ট গুনাহের জন্য এটাই যথেষ্ট। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (544,4,51,'যারা কিতাবের জ্ঞানের একাংশ প্রদত্ত হয়েছে, সেই লোকদের প্রতি তুমি কি লক্ষ্য করনি, তারা অমূলক যাদু, প্রতিমা ও তাগূতের প্রতি বিশ্বাস করে এবং কাফিরদের সম্বন্ধে বলে যে তারা মু’মিনগণের তুলনায় অধিক সঠিক পথে রয়েছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (545,4,52,'এরা সেই লোক, যাদেরকে আল্লাহ অভিশাপ দিয়েছেন এবং যাকে আল্লাহ অভিশাপ দেন, তুমি কক্ষনো তার সাহায্যকারী পাবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (546,4,53,'তাদের কি শাসন ক্ষমতায় কোন অংশ আছে? তা থাকলে তারা লোকেদেরকে তিল পরিমাণও দিত না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (547,4,54,'কিংবা আল্লাহ নিজ অনুগ্রহে লোকেদেরকে যেসব নি‘মাত দান করেছেন, সেজন্য কি এরা তাদের হিংসা করে, আমি ইবরাহীমের বংশধরদেরকেও তো কিতাব ও হিকমাত দিয়েছিলাম, তাদেরকে সুবিশাল রাজ্যও প্রদান করেছিলাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (548,4,55,'অতঃপর তাদের মধ্যে কেউ কেউ তার উপর ঈমান আনল এবং তাদের মধ্যে কেউ কেউ তার থেকে মুখ ফিরিয়ে রাখল, (দগ্ধ করার জন্য) প্রজ্জ্বলিত জাহান্নামই যথেষ্ট। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (549,4,56,'যারা আমার আয়াতসমূহকে প্রত্যাখ্যান করে নিশ্চয়ই আমি তাদেরকে আগুনে দগ্ধ করব, যখন তাদের গায়ের চামড়া দগ্ধ হবে, আমি সেই চামড়াকে নতুন চামড়া দ্বারা বদলে দেব যেন তারা (শাস্তির পর) শাস্তি ভোগ করে। আল্লাহ নিশ্চয়ই পরাক্রমশালী ও বিজ্ঞানময়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (550,4,57,'যারা ঈমান এনেছে এবং সৎকাজ করেছে নিশ্চয়ই আমি তাদেরকে জান্নাতে দাখিল করব যার নিম্নে ঝর্ণাধারা প্রবাহিত, সেখানে তারা চিরবাসী হবে, তাতে তাদের জন্য থাকবে পবিত্র স্ত্রী এবং আমি তাদেরকে চির স্নিগ্ধ ঘন ছায়ায় দাখিল করব। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (551,4,58,'নিশ্চয়ই আল্লাহ তোমাদেরকে নির্দেশ দিচ্ছেন, হকদারদের হক তাদের কাছে পৌঁছে দিতে। তোমরা যখন মানুষের মাঝে বিচার করবে তখন ন্যায়পরায়ণতার সঙ্গে বিচার করবে। আল্লাহ তোমাদেরকে কত উত্তম উপদেশই না দিচ্ছেন; নিশ্চয়ই আল্লাহ সবকিছু শোনেন, সবকিছু দেখেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (552,4,59,'হে ঈমানদারগণ! তোমরা আল্লাহর অনুগত হও এবং রসূলের অনুগত হও এবং তোমাদের মধ্যকার কর্তৃস্থানীয় ব্যক্তিগণের; যদি কোন বিষয়ে তোমাদের মধ্যে মতভেদ ঘটে, তাহলে সেই বিষয়কে আল্লাহ এবং রসূলের (নির্দেশের) দিকে ফিরিয়ে দাও যদি তোমরা আল্লাহ এবং আখিরাত দিবসের প্রতি ঈমান এনে থাক; এটাই উত্তম এবং সুন্দরতম মর্মকথা। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (553,4,60,'তুমি কি সেই লোকেদের প্রতি লক্ষ্য করনি, যারা তোমাদের প্রতি অবতীর্ণ কিতাবের এবং তোমার আগে অবতীর্ণ কিতাবের উপর ঈমান এনেছে বলে দাবী করে, কিন্তু তাগূতের কাছে বিচারপ্রার্থী হতে চায়, অথচ তাকে প্রত্যাখ্যান করার জন্য তাদেরকে নির্দেশ দেয়া হয়েছে, শয়ত্বান তাদেরকে পথভ্রষ্ট করে বহুদূরে নিয়ে যেতে চায়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (554,4,61,'যখন তাদেরকে বলা হয়- তোমরা আল্লাহর অবতীর্ণ কিতাবের এবং রসূলের দিকে এসো, তখন তুমি ঐ মুনাফিকদেরকে দেখবে, তারা তোমা হতে ঘৃণা ভরে মুখ ফিরিয়ে নিচ্ছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (555,4,62,'যখন তাদের কৃতকার্যের জন্য তাদের উপর বিপদ আপতিত হবে, তখন কী অবস্থা হবে? তখন তারা আল্লাহর নামে শপথ করতে করতে তোমার কাছে এসে বলবে, ‘আমরা সদ্ভাব ও সম্প্রীতি ছাড়া অন্য কিছু চাইনি।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (556,4,63,'তারা সেই লোক, যাদের অন্তরস্থিত বিষয়ে আল্লাহ পরিজ্ঞাত, কাজেই তুমি তাদেরকে উপেক্ষা কর, তাদেরকে সদুপদেশ দান কর, আর তাদেরকে এমন কথা বল যা তাদের অন্তর স্পর্শ করে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (557,4,64,'আমি রসূল এ উদ্দেশেই প্রেরণ করেছি, যেন আল্লাহর নির্দেশে তাঁর আনুগত্য করা হয়। যখন তারা নিজেদের উপর যুলম করেছিল, তখন যদি তোমার নিকট চলে আসত আর আল্লাহর কাছে ক্ষমাপ্রার্থী হত এবং রসূলও তাদের পক্ষে ক্ষমা চাইত, তাহলে তারা আল্লাহকে নিরতিশয় তাওবাহ কবূলকারী ও পরম দয়ালুরূপে পেত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (558,4,65,'কিন্তু না, তোমার প্রতিপালকের শপথ! তারা মু’মিন হবে না, যে পর্যন্ত না তারা তাদের বিবাদ-বিসম্বাদের মীমাংসার ভার তোমার উপর ন্যস্ত না করে, অতঃপর তোমার ফয়সালার ব্যাপারে তাদের মনে কিছু মাত্র কুণ্ঠাবোধ না থাকে, আর তারা তার সামনে নিজেদেরকে পূর্ণরূপে সমর্পণ করে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (559,4,66,'যদি আমি তাদের প্রতি ফরয করে দিতাম যে তোমরা নিজেদেরকে হত্যা কর, কিংবা নিজেদের দেশ থেকে বেরিয়ে যাও, তবে স্বল্পসংখ্যক লোক ছাড়া কেউ তা পালন করত না, যা করতে তাদেরকে উপদেশ দেয়া হয় তা যদি তারা পালন করত, তবে তা তাদের পক্ষে কল্যাণকর হত এবং দৃঢ় চিত্ততার কারণ হত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (560,4,67,'সে অবস্থায় অবশ্যই আমি তাদেরকে আমার নিকট হতে মহাপুরস্কার দান করতাম।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (561,4,68,'এবং তাদেরকে অবশ্যই সঠিক পথ প্রদর্শন করতাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (562,4,69,'যারা আল্লাহ ও রসূলের আনুগত্য করে, তারা নাবী, সিদ্দীক, শহীদ এবং নেককার লোকদের সঙ্গী হবে, যাদের প্রতি আল্লাহ নি‘মাত দান করেছেন, তারা কতই না উত্তম সঙ্গী! ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (563,4,70,'এটা আল্লাহর পক্ষ হতে অনুগ্রহ, সর্বজ্ঞ হিসেবে আল্লাহ্ই যথেষ্ট। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (564,4,71,'হে ঈমানদারগণ! তোমরা সতর্কতা অবলম্বন কর এবং দলে দলে ভাগ হয়ে কিংবা মিলিতভাবে অগ্রসর হও। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (565,4,72,'এবং তোমাদের কেউ কেউ নিশ্চয়ই পশ্চাতে সরে থাকবে, অতঃপর তোমাদের উপর কোন বিপদ আসলে সে বলবে, ‘আল্লাহ আমার প্রতি অনুগ্রহ করেছেন যে, আমি তাদের সঙ্গে উপস্থিত ছিলাম না।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (566,4,73,'আর যদি তোমাদের প্রতি আল্লাহর অনুগ্রহ হয়, তবে যেন তোমাদের ও তাদের মধ্যে কোন প্রকারের সম্পর্ক ছিল না, এমনিভাবে অবশ্যই বলে উঠবে, ‘হায় পরিতাপ! আমিও যদি তাদের সঙ্গে থাকতাম তাহলে মহা সাফল্য লাভ করতাম।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (567,4,74,'সুতরাং যারা আখেরাতের বিনিময়ে পার্থিব জীবন বিক্রয় করে তারা আল্লাহর পথে জিহাদ করুক এবং যে ব্যক্তি আল্লাহর পথে জিহাদ করবে, অতঃপর সে নিহত হোক অথবা বিজয়ী হোক, অচিরেই আমি তাকে মহা প্রতিফল দান করব। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (568,4,75,'তোমাদের কী হয়েছে যে, তোমরা আল্লাহর পথে এবং অসহায় নারী-পুরুষ আর শিশুদের (রক্ষার) জন্য লড়াই করবে না, যারা দু‘আ করছে- ‘হে আমাদের প্রতিপালক! আমাদেরকে এ যালিম অধ্যূষিত জনপথ হতে মুক্তি দাও, তোমার পক্ষ হতে কাউকেও আমাদের বন্ধু বানিয়ে দাও এবং তোমার পক্ষ হতে কাউকেও আমাদের সাহায্যকারী করে দাও।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (569,4,76,'ঈমানদারগণ আল্লাহর পথে যুদ্ধ করে আর যারা কাফির তারা তাগুতের পথে যুদ্ধ করে। কাজেই তোমরা শায়ত্বনের বন্ধুদের বিরুদ্ধে যুদ্ধ কর, শায়ত্বনের ফন্দি অবশ্যই দুর্বল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (570,4,77,'তুমি কি তাদেরকে দেখনি, যাদেরকে বলা হয়েছিল, তোমরা তোমাদের হাত সংযত রাখ, নামায কায়িম কর এবং যাকাত দাও? অতঃপর যখন তাদের প্রতি জিহাদ ফরয করা হল, তখন তাদের একদল মানুষকে এমন ভয় করতে লাগল যেমন আল্লাহকে ভয় করা উচিত, বরং তার চেয়েও বেশী এবং বলতে লাগল, ‘হে আমাদের প্রতিপালক! কেন আমাদের প্রতি জিহাদ ফরয করলে, আমাদেরকে আরও কিছু অবসর দিলে না কেন?’ বল, ‘পার্থিব ভোগ সামান্য, যে তাকওয়া অবলম্বন করে তার জন্য আখিরাতই উত্তম, তোমাদের প্রতি বিন্দুমাত্র অন্যায় করা হবে না।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (571,4,78,'তোমরা যেখানেই থাক না কেন, মৃত্যু তোমাদেরকে পেয়ে বসবেই, যদিও তোমরা সুউচ্চ সুদৃঢ় দূর্গ মধ্যে অবস্থান কর। যদি তাদের কোন কল্যাণ ঘটে, তখন তারা বলে, এটা আল্লাহর তরফ হতে। পক্ষান্তরে যদি তাদের কোন অকল্যাণ ঘটে তখন বলে, ‘এটা তো তোমার তরফ হতে।’ বল, ‘সবকিছুই আল্লাহর তরফ হতে।’ এ সম্প্রদায়ের হল কী যে, তারা কোন কথাই বুঝে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (572,4,79,'তোমার কোন কল্যাণ হলে তা হয় আল্লাহর তরফ হতে এবং তোমার যে কোন অকল্যাণ হলে তা হয় তোমার নিজের কারণে এবং আমি তোমাকে মানুষের জন্য রসূলরূপে প্রেরণ করেছি, (এ কথার) সাক্ষী হিসেবে আল্লাহ্ই যথেষ্ট। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (573,4,80,'যে রসূলের আনুগত্য করল, সে তো আল্লাহরই আনুগত্য করল, কেউ মুখ ফিরিয়ে নিলে (জোরপূর্বক তাকে সৎপথে আনার জন্য) আমি তোমাকে তাদের প্রতি পাহারাদার করে পাঠাইনি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (574,4,81,'তারা মুখেই আনুগত্য করে, কিন্তু যখন তোমার নিকট থেকে চলে যায়, তখন তাদের মধ্যে একটা দল রাত্রিবেলা একত্রিত হয়ে তোমার কথার বিরুদ্ধে সলা পরামর্শ করে, আল্লাহ তাদের রাত্রিকালীন মন্ত্রণাগুলো লিখে রাখছেন, কাজেই তাদের প্রতি ভ্রুক্ষেপ করো না,  আল্লাহর উপর ভরসা কর এবং আল্লাহ্ই কর্মবিধায়ক  হিসেবে  যথেষ্ট।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (575,4,82,'তারা  কি কুরআনের মর্ম বিষয়ে চিন্তে-ভাবনা করে না? যদি তা আল্লাহ ছাড়া অন্য কারো নিকট হতে আসত, তবে তাতে তারা অবশ্যই বহু অসঙ্গতি পেত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (576,4,83,'যখন তাদের নিকট নিরাপত্তার কিংবা ভয়ের কোন সংবাদ আসে তখন তারা তা রটিয়ে দেয়। যদি তারা তা রসূলের কিংবা তাদের মধ্যে যারা ক্ষমতার অধিকারী তাদের গোচরে আনত, তবে তাদের মধ্য হতে তথ্যানুসন্ধানীগণ প্রকৃত তথ্য জেনে নিত। যদি তোমাদের প্রতি আল্লাহর দয়া ও করুণা না থাকত তবে তোমাদের অল্প সংখ্যক ছাড়া সকলেই শায়ত্বনের অনুসরণ করত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (577,4,84,'কাজেই আল্লাহর পথে যুদ্ধ কর, তোমাকে শুধু তোমার নিজের জন্য দায়ী করা হবে, মু’মিনদের উদ্বুদ্ধ কর, হতে পারে যে আল্লাহ কাফিরদের শক্তি সংযত করবেন এবং আল্লাহ শক্তিতে অতি প্রবল, শাস্তিদানে অতি কঠোর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (578,4,85,'যে ব্যক্তি ভাল কাজের জন্য সুপারিশ করবে, তার জন্য তাতে (সাওয়াবের) অংশ আছে এবং যে মন্দ কাজের জন্য সুপারিশ করবে, তার জন্য তাতে অংশ আছে, আল্লাহ সকল বিষয়ে খোঁজ রাখেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (579,4,86,'যখন তোমাদেরকে সসম্মানে সালাম প্রদান করা হয়, তখন তোমরা তার চেয়ে উত্তমরূপে জওয়াবী সালাম দাও কিংবা (কমপক্ষে) অনুরূপভাবে দাও, নিশ্চয়ই আল্লাহ (ক্ষুদ্র-বৃহৎ) সকল বিষয়ে হিসাব গ্রহণকারী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (580,4,87,'আল্লাহ, তিনি ছাড়া সত্যিকারের কোন ইলাহ নেই, তিনি ক্বিয়ামাত দিবসে সকলকে একত্র করবেনই, এতে কোনই সন্দেহ নেই, আল্লাহ অপেক্ষা আর কার কথা অধিক সত্য হতে পারে? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (581,4,88,'তোমাদের কী হল যে, মুনাফিকদের ব্যাপারে (কী নীতি অবলম্বন করা হবে সে বিষয়ে) তোমরা দু’ দলে বিভক্ত হয়ে গেলে? বস্তুতঃ আল্লাহ তাদের   এ   কার্যকলাপের  কারণে   তাদেরকে  উল্টা  মুখে  ফিরিয়ে দিয়েছেন (ইসলাম থেকে আবার কুফরের দিকে)। আল্লাহ যাকে পথভ্রষ্ট করেছেন, তোমরা কি তাকে সুপথ দেখাতে চাও? বস্তুতঃ আল্লাহ যাকে পথভ্রষ্ট করেন, তার জন্য কক্ষনো পথ খুঁজে পাবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (582,4,89,'তারা আকাঙ্ক্ষা করে যে, তারা নিজেরা যেমন কুফরী করেছে, তোমরাও তেমনি কুফরী কর, যাতে তোমরা তাদের সমান হয়ে যাও। কাজেই তাদের মধ্য হতে কাউকেও বন্ধুরূপে গ্রহণ করো না, যে পর্যন্ত তারা আল্লাহর পথে হিজরত না করে। যদি তারা মুখ ফিরিয়ে নেয়, তবে তাদেরকে গ্রেফতার কর এবং যেখানেই তাদেরকে পাও, হত্যা কর। তাদের মধ্য হতে কোন বন্ধু ও সাহায্যকারী গ্রহণ করো না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (583,4,90,'কিন্তু (সে সব মুনাফিক এ কথার মধ্যে শামিল নয়) যারা তোমাদের সাথে চুক্তিবদ্ধ কোন জাতির সঙ্গে গিয়ে মিলিত হয়। তেমনি (তারাও এর মধ্যে শামিল নয়) যারা তোমার কাছে আসে আর তারা ঝগড়া-বিবাদে উৎসাহী নয়, তারা না তোমাদের বিরুদ্ধে লড়াই করতে চায়, না নিজের জাতির বিরুদ্ধে। আল্লাহ যদি ইচ্ছে করতেন, তবে তাদেরকে তোমাদের উপর প্রবল করে দিতেন, সে অবস্থায় তারা নিশ্চয় তোমাদের বিরুদ্ধে যুদ্ধ করত। কাজেই তারা যদি তোমাদের হতে সরে থাকে, তোমাদের বিরুদ্ধে যুদ্ধ না করে ও তোমাদের নিকট শান্তি প্রস্তাব করে, তবে আল্লাহ তোমাদের জন্য তাদের বিরুদ্ধে কোনও ব্যবস্থা অবলম্বনের পথ রাখেননি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (584,4,91,'অচিরেই তোমরা কতক লোককে এমনও পাবে, যারা তোমাদের কাছ থেকেও নিরাপদ থাকতে চায়, তাদের নিজ সম্প্রদায় থেকেও নিরাপদ থাকতে চায়, যখন তাদেরকে ফিতনার দিকে মনোনিবেশ করানো হয় তখন তাতেই জড়িয়ে পড়ে। কাজেই যদি তারা তোমাদের শত্রুতা হতে সরে না যায় এবং তোমাদের নিকট শান্তি প্রস্তাব না করে এবং তাদের হস্ত সংবরণ না করে, তবে তাদেরকে গ্রেফতার কর আর যেখানেই পাও হত্যা কর, এরাই হচ্ছে সেই সব লোক তোমাদেরকে যাদের বিরুদ্ধাচরণের স্পষ্ট অধিকার দিয়েছি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (585,4,92,'কোন মু’মিনকে হত্যা করা কোন মু’মিনের কাজ নয় তবে ভুলবশত হতে পারে, কেউ কোন মু’মিনকে ভুলক্রমে হত্যা করলে, একজন মু’মিন দাস মুক্ত করা বা তার পরিবারবর্গকে রক্তপণ দেয়া কর্তব্য, যদি না তারা ক্ষমা করে দেয়। যদি সে তোমাদের শত্রুপক্ষের লোক হয় এবং মু’মিন হয় তবে একজন মু’মিন গোলাম আযাদ করা কর্তব্য, আর যদি সে এমন গোত্রের লোক হয় যাদের মধ্যে ও তোমাদের মধ্যে চুক্তি রয়েছে তবে তার পরিবারকে রক্তপণ দেয়া এবং একজন মু’মিন গোলাম আযাদ করা কর্তব্য এবং যে ব্যক্তি সঙ্গতিহীন সে একাদিক্রমে দু’ মাস রোযা পালন করবে। এটাই হল আল্লাহর নিকট তাওবাহ করার ব্যবস্থা, আল্লাহ মহাজ্ঞানী, সুবিজ্ঞ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (586,4,93,'যে ব্যক্তি কোন মু’মিনকে ইচ্ছাপূর্বক হত্যা করে, তার শাস্তি জাহান্নাম। যাতে স্থায়ীভাবে থাকবে, তার উপর আল্লাহর ক্রোধ ও অভিসম্পাত। আল্লাহ তার জন্য মহাশাস্তি নির্দিষ্ট করে রেখেছেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (587,4,94,'হে মু’মিনগণ! যখন তোমরা আল্লাহর পথে যাত্রা করবে তখন কে বন্ধু আর কে শত্রু তা পরীক্ষা করে নেবে, কেউ তোমাদেরকে সালাম করলে তাকে বলো না, ‘তুমি মু’মিন নও’, তোমরা ইহজগতের সম্পদের আকাঙ্ক্ষা কর, বস্তুতঃ আল্লাহর নিকট তোমাদের জন্য প্রচুর গনীমত আছে। তোমরাও এর পূর্বে এ রকমই ছিলে (অর্থাৎ তোমারাও তাদের মতই তোমাদের ঈমানকে তোমাদের কওম থেকে গোপন করতে), তৎপর আল্লাহ তোমাদের প্রতি কৃপা করেছেন, কাজেই অগ্রে বিশেষভাবে পরীক্ষা করে নিবে; তোমরা যা কিছু কর, সে বিষয়ে আল্লাহ সবিশেষ অবহিত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (588,4,95,'অক্ষম নয় এমন বসে-থাকা মু’মিনরা আর জান-মাল দ্বারা আল্লাহর পথে জিহাদকারীগণ সমান নয়; নিজেদের ধন-প্রাণ দ্বারা জিহাদকারীদেরকে বসে-থাকা লোকেদের উপর আল্লাহ মর্যাদা দিয়েছেন। আল্লাহ সকলের জন্যই কল্যাণের ওয়াদা করেছেন এবং মুজাহিদদেরকে বসে-থাকা লোকেদের তুলনায় আল্লাহ মহাপুরস্কার দিয়ে মর্যাদা দান করেছেন।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (589,4,96,'ওটা আল্লাহর নিকট হতে পদমর্যাদা, ক্ষমা ও দয়া; আল্লাহ বড়ই ক্ষমাশীল, পরম দয়াবান। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (590,4,97,'যারা নিজেদের আত্মার উপর যুলম করেছিল এমন লোকেদের প্রাণ হরণের সময় ফেরেশতারা তাদেরকে জিজ্ঞেস করে- ‘তোমরা কোন্ কাজে নিমজ্জিত ছিলে’? তারা বলে, ‘দুনিয়ায় আমরা দুর্বল ক্ষমতাহীন ছিলাম’, ফেরেশতারা বলে, ‘আল্লাহর যমীন কি প্রশস্ত ছিল না যাতে তোমরা হিজরাত করতে’? সুতরাং তাদের আবাসস্থল হবে জাহান্নাম এবং তা কতই না নিকৃষ্ট প্রত্যাবর্তন স্থান! ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (591,4,98,'কিন্তু যে সকল সহায়হীন পুরুষ, নারী ও বালক যারা উপায় বের করতে পারে না আর তারা পথও পায় না, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (592,4,99,'আশা আছে যে, আল্লাহ তাদেরকে ক্ষমা করবেন এবং আল্লাহ গুনাহ মোচনকারী, বড়ই ক্ষমাশীল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (593,4,100,'যে ব্যক্তি আল্লাহর পথে হিজরাত করবে, সে পৃথিবীতে বহু আশ্রয়স্থল এবং প্রাচুর্য প্রাপ্ত হবে; এবং যে ব্যক্তি নিজের গৃহ হতে আল্লাহ ও তাঁর রসূলের দিকে হিজরাত করে বের হয়, অতঃপর মৃত্যু তাকে পেয়ে বসে তার সাওয়াব আল্লাহর জিম্মায় সাব্যস্ত হয়ে গেছে এবং আল্লাহ অতি ক্ষমাশীল, পরম দয়ালু। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (594,4,101,'যখন তোমরা দেশে-বিদেশে সফর কর, তখন নামায কসর করাতে তোমাদের কিছুমাত্র দোষ নেই, যদি তোমরা ভয় কর যে, কাফিরগণ তোমাদেরকে বিপদগ্রস্ত করবে। নিঃসন্দেহে কাফিরগণ তো তোমাদের প্রকাশ্য শত্রু। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (595,4,102,'এবং যখন তুমি মু’মিনদের মাঝে অবস্থান করবে আর তাদের সঙ্গে নামায কায়িম করবে তখন তাদের একটি দল যেন তোমার সঙ্গে দাঁড়ায় এবং সশস্ত্র থাকে, তাদের সাজদাহ করা হলে তারা যেন তোমাদের পশ্চাতে অবস্থান করে এবং অপর যে দলটি নামায আদায় করেনি তারা যেন তোমার সঙ্গে নামায আদায় করে এবং সতর্ক ও সশস্ত্র থাকে; কাফিরগণ কামনা করে যে, তোমরা যেন তোমাদের অস্ত্রশস্ত্র ও সরঞ্জামের ব্যাপারে অসতর্ক হও, যাতে তারা একজোটে তোমাদের উপর ঝাঁপিয়ে পড়তে পারে। যদি তোমাদের বৃষ্টির কারণে কষ্ট হয়, কিংবা তোমরা পীড়িত হও, তবে অস্ত্র রেখে দিলে তোমাদের কোন গুনাহ নেই, কিন্তু সতর্কতা অবলম্বন করবে, আল্লাহ কাফিরদের জন্য অবমাননাকর শাস্তি প্রস্তুত করে রেখেছেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (596,4,103,'যখন তোমরা নামায আদায় করে নেবে, তখন দাঁড়িয়ে, বসে, শুয়ে আল্লাহকে স্মরণ করবে, অতঃপর যখন তোমরা নিরাপদ হবে তখন (যথানিয়মে) নামায কায়িম করবে। নির্দিষ্ট সময়ে নামায কায়িম করা মু’মিনদের জন্য অবশ্য কর্তব্য। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (597,4,104,'এ (শত্রু) কওমের পশ্চাদ্ধাবণে দুর্বলতা দেখাবে না, কেননা যদি তোমরা কষ্ট পাও, তবে তোমাদের মত তারাও তো কষ্ট পায়, আর তোমরা আল্লাহ হতে এমন কিছু আশা কর, যা তারা আশা করে না। আল্লাহ মহাজ্ঞানী, বিজ্ঞানময়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (598,4,105,'অবশ্যই আমি সত্য সহকারে তোমার প্রতি কিতাব নাযিল করেছি, যেন তুমি যা আল্লাহ তোমাকে জানিয়েছেন, সে অনুসারে মানুষের মধ্যে বিচার ফায়সালা কর এবং খিয়ানতকারীদের পক্ষে তর্ক করো না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (599,4,106,'আর আল্লাহর নিকট ক্ষমা চাও, নিশ্চয় আল্লাহ অতিশয় ক্ষমাশীল, পরম দয়ালু। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (600,4,107,'নিজেদের (বিবেক ও জ্ঞানের) প্রতি খিয়ানতকারীদের পক্ষে তুমি বাদানুবাদ করো না, কারণ আল্লাহ অতি খিয়ানতকারী চরম পাপীকে পছন্দ করেন না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (601,4,108,'তারা মানুষ হতে গোপন করে থাকে, কিন্তু তারা আল্লাহ হতে গোপন করতে পারে না; কেননা যে সময়ে তারা রাত্রে এমন বিষয়ে পরামর্শ করে যা আল্লাহ পছন্দ করেন না, তখনও তিনি তাদের সঙ্গেই থাকেন; আল্লাহ তাদের সমুদয় কার্যকলাপকে বেষ্টন করে আছেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (602,4,109,'দেখ, ওরা সেই লোক যাদের পক্ষে পার্থিব জীবনে তোমরা বিতর্ক করছ কিন্তু ক্বিয়ামাত দিবসে তাদের পক্ষ হতে আল্লাহর সম্মুখে কে ঝগড়া করবে? কিংবা কে তাদের উকীল হবে? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (603,4,110,'যে ব্যক্তি অসৎকাজ করে কিংবা নিজের আত্মার প্রতি যুলম করে, অতঃপর আল্লাহ হতে ক্ষমা ভিক্ষে করে, সে আল্লাহকে অতি ক্ষমাশীল, পরম দয়ালু পাবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (604,4,111,'এবং যে ব্যক্তি কোন পাপকাজ করে, সে নিজের বিরুদ্ধেই তা করে, বস্তুতঃ আল্লাহ মহাবিজ্ঞানী, প্রজ্ঞাময়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (605,4,112,'যে ব্যক্তি কোন ত্রুটি কিংবা পাপ ক’রে তা কোন নির্দোষ ব্যক্তির উপর চাপিয়ে দেয়, সে তো জ্বলন্ত অপবাদ এবং সুস্পষ্ট গুনাহ নিজের উপর চাপিয়ে নেয়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (606,4,113,'যদি তোমার প্রতি আল্লাহর করুণা এবং দয়া না হত, তবে তাদের একদল তো তোমাকে পথভ্রষ্ট করতেই চেয়েছিল; বস্তুতঃ তারা নিজেদেরকে ছাড়া অন্য কাউকে পথভ্রষ্ট করে না আর তারা তোমার কিছুই অনিষ্ট করতে পারবে না, কারণ আল্লাহ তোমার প্রতি কিতাব ও হিকমাত নাযিল করেছেন এবং তুমি যা জানতে না তা তোমাকে শিক্ষা দিয়েছেন এবং তোমার উপর রয়েছে আল্লাহর অপরিসীম অনুগ্রহ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (607,4,114,'তাদের অধিকাংশ গোপন পরামর্শের মধ্যে কোন কল্যাণ নেই, কিন্তু কল্যাণ আছে যে ব্যক্তি দান-খয়রাত অথবা কোন সৎকাজের কিংবা লোকেদের মধ্যে মিলমিশের নির্দেশ দেয়। যে কেউ আল্লাহর সন্তুষ্টি সাধন উদ্দেশে এমন কাজ করবে, আমি তাকে মহা পুরস্কার দান করব। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (608,4,115,'যে ব্যক্তি সত্য পথ প্রকাশিত হওয়ার পরও রসূলের বিরোধিতা করে এবং মু’মিনদের পথ বাদ দিয়ে ভিন্ন পথ অনুসরণ করে, আমি তাকে সে পথেই ফিরাব যে পথে সে ফিরে যায়, আর তাকে জাহান্নামে দগ্ধ করব, কত মন্দই না সে আবাস! ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (609,4,116,'নিশ্চয়ই আল্লাহ তাঁর সঙ্গে শরীক করাকে ক্ষমা করেন না, এছাড়া অন্য সব যাকে ইচ্ছে মাফ করেন এবং যে ব্যক্তি আল্লাহর সঙ্গে শরীক করে, সে চরমভাবে গোমরাহীতে পতিত হল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (610,4,117,'তারা আল্লাহকে ছেড়ে শুধু কতকগুলো দেবীরই পূজা করে, তারা কেবল আল্লাহদ্রোহী শায়ত্বনের পূজা করে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (611,4,118,'আল্লাহ তাকে লা‘নাত করেছেন কারণ সে বলেছিল, ‘আমি তোমার বান্দাদের থেকে নির্দিষ্ট অংশকে আমার অনুসারী হিসেবে গ্রহণ করব।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (612,4,119,'তাদেরকে অবশ্য অবশ্যই পথভ্রষ্ট করব, তাদেরকে অবশ্য অবশ্যই বহু প্রলোভন দেব এবং তাদেরকে অবশ্য অবশ্যই নির্দেশ দেব, ফলে তারা জন্তু-জানোয়ারের কান ছেদন করবে, আমি তাদেরকে অবশ্য অবশ্যই নির্দেশ দেব, ফলে তারা অবশ্য অবশ্যই আল্লাহর সৃষ্টি বিকৃত করবে। আল্লাহকে বাদ দিয়ে যে কেউ শয়ত্বানকে অভিভাবক হিসেবে গ্রহণ করে, সে সুস্পষ্টত ক্ষতিগ্রস্ত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (613,4,120,'সে তাদেরকে আশ্বাস দেয়, মিথ্যা প্রলোভন দেয়, বস্তুতঃ শয়ত্বান তাদেরকে যে আশ্বাস দেয় তা ছলনা ছাড়া আর কিছুই নয়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (614,4,121,'এরাই তারা যাদের আবাসস্থল জাহান্নাম এবং তারা তাত্থেকে নিস্কৃতি পাওয়ার কোন পথ পাবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (615,4,122,'আর যারা ঈমান এনেছে ও নেক কাজ করেছে অবিলম্বে আমি তাদেরকে জান্নাতে দাখিল করব; যার পাদদেশে ঝর্ণাধারা প্রবাহিত, তারা তাতে চিরকাল চিরস্থায়ী হয়ে থাকবে, আল্লাহর ওয়া‘দা সত্য, কথায় আল্লাহ অপেক্ষা কে বেশি সত্যবাদী? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (616,4,123,'তোমাদের বাসনা আর আহলে কিতাবদের বাসনা কোন কাজে আসবে না। যে ব্যক্তি কোন কুকর্ম করবে, সে তার বিনিময় প্রাপ্ত হবে, সে আল্লাহ ছাড়া তার জন্য কোন অভিভাবক পাবে না, কোন সাহায্যকারীও না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (617,4,124,'পুরুষ ও নারীর মধ্যে যে সৎকাজ করবে আর সে ঈমানদারও বটে, তাহলে তারা জান্নাতে প্রবেশ করবে, তাদের প্রতি তিল পরিমাণও অন্যায় করা হবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (618,4,125,'সে ব্যক্তি অপেক্ষা দ্বীনে কে বেশি উত্তম যে ব্যক্তি আল্লাহর কাছে আত্মসমর্পণ করে, অধিকন্তু সে সৎকর্মশীল এবং একনিষ্ঠভাবে ইবরাহীমের দ্বীন অনুসরণ করে। আল্লাহ ইবরাহীমকে একান্ত বন্ধুরূপে গ্রহণ করেছিলেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (619,4,126,'আসমান ও যমীনে যা কিছু আছে সব আল্লাহরই এবং আল্লাহ সবকিছুকেই পরিবেষ্টন করে আছেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (620,4,127,'লোকেরা তোমার কাছে নারীদের সম্বন্ধে বিধান জানতে চাচ্ছে। বলে দাও, ‘আল্লাহ তাদের সম্বন্ধে তোমাদেরকে বিধান জানিয়ে দিচ্ছেন সেসব নারী সম্পর্কে যাদের প্রাপ্য তোমরা প্রদান কর না অথচ তাদেরকে বিয়ে করতে চাও এবং অসহায় শিশুদের সম্পর্কে এবং ইয়াতীমদের প্রতি তোমাদের ন্যায়বিচার করা সম্পর্কে যা কিতাবে তোমাদেরকে শুনানো হয় তাও জানিয়ে দেন।’ যা কিছু সৎ কাজ তোমরা কর, তদ্বিষয়ে আল্লাহ ভালভাবেই জ্ঞাত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (621,4,128,'এবং যদি কোন নারী স্বীয় স্বামী হতে রূঢ়তা কিংবা উপেক্ষার আশঙ্কা করে, তবে তারা পরস্পর আপোষ করলে তাদের কোন গুনাহ নেই, বস্তুতঃ আপোষ করাই উত্তম। আর নফস্ কৃপণতার দিকে সহজেই ঝুঁকে পড়ে, আর যদি তোমরা সৎকর্ম কর এবং তাকওয়া অবলম্বন কর তবে তোমরা যা কর আল্লাহ তার খবর রাখেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (622,4,129,'তোমরা কক্ষনো স্ত্রীদের মধ্যে সমতা রক্ষা করতে পারবে না যদিও প্রবল ইচ্ছে কর, তোমরা একজনের দিকে সম্পূর্ণরূপে ঝুঁকে পড়ো না এবং অন্যকে ঝুলিয়ে রেখ না। যদি তোমরা নিজেদেরকে সংশোধন কর এবং তাকওয়া অবলম্বন কর, তবে আল্লাহ অতি ক্ষমাশীল, পরম দয়ালু। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (623,4,130,'তারা যদি উভয়ে পৃথক হয়ে যায় তবে আল্লাহ আপন প্রাচুর্য দিয়ে প্রত্যেককে অভাবমুক্ত করে দেবেন, আল্লাহ প্রাচুর্যময়, মহাকুশলী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (624,4,131,'যা কিছু আকাশসমূহে ও ভূমন্ডলে আছে সমস্ত আল্লাহরই এবং অবশ্যই আমি তোমাদের আগে যাদেরকে কিতাব দেয়া হয়েছিল তাদেরকে আর তোমাদেরকেও আদেশ দিয়েছি যে, আল্লাহকে ভয় কর আর যদি অমান্য কর তবে আকাশসমূহে যা আছে ও ভূমন্ডলে যা আছে তা আল্লাহরই। আল্লাহ অভাবমুক্ত, অতিশয় প্রশংসিত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (625,4,132,'আসমানে যা আছে আর যমীনে যা আছে সব আল্লাহরই, কার্যনির্বাহক হিসেবে আল্লাহ্ই যথেষ্ট। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (626,4,133,'তিনি ইচ্ছে করলে হে মানুষ! তোমাদেরকে বিলুপ্ত ক’রে তিনি অন্য সম্প্রদায়কে আনতে পারেন, আল্লাহ তা করতে পুরোপুরি সক্ষম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (627,4,134,'যে ব্যক্তি পার্থিব পুরস্কার কামনা করে সে জেনে রাখুক যে আল্লাহর নিকট ইহলৌকিক ও পারলৌকিক পুরস্কার আছে। আল্লাহ সর্বশ্রোতা, সর্বদ্রষ্টা। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (628,4,135,'হে ঈমানদারগণ! ন্যায়ের প্রতি সুপ্রতিষ্ঠ ও আল্লাহর জন্য সাক্ষ্যদাতা হও যদিও তা তোমাদের নিজেদের কিংবা মাতা-পিতা এবং আত্মীয়গণের বিরুদ্ধে হয়, কেউ ধনী হোক বা দরিদ্র হোক, আল্লাহ উভয়েরই ঘনিষ্ঠতর। অতএব প্রবৃত্তির অনুসরণ করো না যাতে তোমরা ন্যায়বিচার করতে পার এবং যদি তোমরা বক্রভাবে কথা বল কিংবা সত্যকে এড়িয়ে যাও তবে নিশ্চয় তোমরা যা করছ, আল্লাহ সে বিষয়ে সম্পূর্ণ অবগত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (629,4,136,'হে ঈমানদারগণ! তোমরা আল্লাহ, তাঁর রসূলের, তাঁর রসূলের নিকট তিনি অবতীর্ণ করেছেন সেই কিতাবের এবং পূর্বে নাযিলকৃত কিতাবের উপর ঈমান আন। যে ব্যক্তি আল্লাহকে ও তাঁর ফেরেশতাদেরকে, তাঁর কিতাবসমূহকে, তাঁর রসূলগণকে এবং শেষ দিবসকে অস্বীকার করে সে সীমাহীন পথভ্রষ্টতায় পতিত হয়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (630,4,137,'যারা ঈমান আনল, অতঃপর কুফরী করল আবার ঈমান আনল আবার কুফরী করল, অতঃপর কুফরীতে অগ্রসর হতে থাকল, আল্লাহ তাদেরকে ক্ষমা করবেন না এবং পথপ্রদর্শন করবেন না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (631,4,138,'মুনাফিকদেরকে সুসংবাদ শুনিয়ে দাও যে, তাদের জন্য যন্ত্রণাদায়ক শাস্তি রয়েছে।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (632,4,139,'যারা মু’মিনদেরকে ছেড়ে কাফিরদেরকে বন্ধুরূপে গ্রহণ করে তারা কি তাদের নিকট ইযযত চায়? ইযযতের সবকিছুই আল্লাহর অধিকারে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (633,4,140,'কিতাবে তোমাদের নিকট তিনি নাযিল করেছেন যে, যখন তোমরা শুনবে আল্লাহর আয়াতের প্রতি কুফরী হচ্ছে এবং তার প্রতি ঠাট্টা করা হচ্ছে, তখন তাদের নিকট বসো না যে পর্যন্ত তারা অন্য আলোচনায় লিপ্ত না হয়, নচেৎ তোমরাও তাদের মত হয়ে যাবে, নিশ্চয় আল্লাহ মুনাফিক ও কাফিরদের সকলকেই জাহান্নামে একত্রিত করবেন।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (634,4,141,'তারা (অর্থাৎ মুনাফিকরা) তোমাদের ব্যাপারে ওঁৎ পেতে থাকে, তারা আল্লাহর তরফ হতে তোমাদের জয়লাভ হলে বলে আমরা কি তোমাদের সঙ্গে ছিলাম না? আর যদি কাফিরদের কিছু বিজয় ঘটে তখন তারা বলে, আমরা কি তোমাদের উপর বিজয়ী ছিলাম না এবং আমরা কি তোমাদেরকে মু’মিনদের হতে রক্ষা করিনি? এমতাবস্থায় আল্লাহ ক্বিয়ামাত দিবসে তোমাদের মধ্যে মীমাংসা করে দেবেন এবং আল্লাহ কক্ষনো মু’মিনদের বিরুদ্ধে কাফিরদেরকে (জয়লাভের) পথ করে দেবেন না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (635,4,142,'নিশ্চয় মুনাফিকগণ আল্লাহর সঙ্গে ধোঁকাবাজি করে, তিনি তাদেরকে ধোঁকায় ফেলে শাস্তি দেন এবং তারা যখন সলাতের জন্য দাঁড়ায়, তখন শৈথিল্যভরে দাঁড়ায়, লোক দেখানোর জন্য, তারা আল্লাহকে সামান্যই স্মরণ করে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (636,4,143,'তারা মাঝখানে দোদুল্যমান, না এদের দিকে, না ওদের দিকে; বস্তুতঃ আল্লাহ যাকে পথভ্রষ্ট করেন তুমি তার জন্য কক্ষনো কোন পথ পাবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (637,4,144,'হে ঈমানদারগণ! তোমরা মু’মিনদের পরিবর্তে কাফিরদেরকে তোমাদের বন্ধুরূপে গ্রহণ করো না, তোমরা কি তোমাদের নিজেদের বিরুদ্ধে আল্লাহর কাছে সুস্পষ্ট প্রমাণ পেশ করতে চাও? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (638,4,145,'মুনাফিকরা থাকবে জাহান্নামের সর্বনিম্ন স্তরে, তুমি তাদের জন্য কক্ষনো কোন সাহায্যকারী পাবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (639,4,146,'অবশ্য তারা এদের মধ্যে শামিল নয় যারা তাওবাহ করবে, নিজেদেরকে সংশোধন করবে, আল্লাহকে মজবুতভাবে ধারণ করবে, আর নিজেদের দ্বীনকে আল্লাহর জন্য একনিষ্ঠ করবে। তখন তারা মু’মিনদের সঙ্গী হিসেবে গণ্য হবে আর আল্লাহ অচিরেই মু’মিনদেরকে মহা প্রতিফল দান করবেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (640,4,147,'তোমরা যদি শোকরগুজারি কর আর ঈমান আন তাহলে তোমাদেরকে শাস্তি দিয়ে আল্লাহ কী করবেন? আল্লাহ (সৎকাজের বড়ই) পুরস্কারদাতা, সর্ববিষয়ে জ্ঞাত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (641,4,148,'খারাপ কথার প্রচার প্রপাগান্ডা আল্লাহ পছন্দ করেন না, তবে যার প্রতি অন্যায় করা হয়েছে (তার কথা আলাদা), আল্লাহ সর্বশ্রোতা, সর্বজ্ঞ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (642,4,149,'তোমরা যদি ভাল কাজ প্রকাশ্যে কর কিংবা তা গোপনে কর কিংবা মন্দকে ক্ষমা কর তবে আল্লাহও দোষত্রুটি মোচনকারী, ক্ষমতার অধিকারী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (643,4,150,'যারা আল্লাহ ও তাঁর রসূলদেরকে অস্বীকার করে আর আল্লাহ ও রসূলদের মাঝে পার্থক্য সৃষ্টি করতে চায় আর বলে (রসূলদের) কতককে আমরা মানি আর কতককে মানি না, আর তারা তার (কুফর ও ঈমানের) মাঝ দিয়ে একটা রাস্তা বের করতে চায় ।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (644,4,151,'তারাই হল প্রকৃত কাফির আর কাফিরদের জন্য আমি অবমাননাকর শাস্তি প্রস্তুত করে রেখেছি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (645,4,152,'আর যারা আল্লাহ ও তাঁর রসূলদের প্রতি বিশ্বাস স্থাপন করে আর তাদের কারো মধ্যে কোন পার্থক্য করে না, তিনি অবশ্যই তাদেরকে তাদের পুরস্কার দান করবেন, আল্লাহ বড়ই ক্ষমাশীল, বড়ই দয়ালু। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (646,4,153,'কিতাবধারীগণ তোমাকে আসমান থেকে তাদের সামনে কিতাব নিয়ে আসতে বলে। তারা তো মূসার কাছে এর চেয়েও বড় দাবী পেশ করেছিল। তারা বলেছিল- আমাদেরকে প্রকাশ্যে আল্লাহকে দেখাও। তখন তাদের অন্যায় ও বাড়াবাড়ির কারণে বিদ্যুৎ তাদের উপর আঘাত হেনেছিল। অতঃপর তাদের কাছে স্পষ্ট প্রমাণ আসার পরেও তারা গো-বৎসকে (উপাস্য) গ্রহণ করেছিল, তাও আমি ক্ষমা করে দিয়েছিলাম, আর মূসাকে সুস্পষ্ট কর্তৃত্ব দান করেছিলাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (647,4,154,'তাদের নিকট হতে অঙ্গীকার গ্রহণের জন্য আমি তূর পাহাড়কে তাদের ঊর্ধ্বে তুলে ধরেছিলাম, আর তাদেরকে বলেছিলাম- অবনত মস্তকে (নগর) দ্বারে প্রবেশ কর আর তাদেরকে বলেছিলাম শনিবারের আইন ভঙ্গ করো না, আর তাদের নিকট থেকে নিয়েছিলাম পাকা প্রতিশ্রুতি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (648,4,155,'(তাদের প্রতি আল্লাহর অসন্তোষ নেমে এসেছে) তাদের ওয়া‘দা ভঙ্গের কারণে, আর আল্লাহর নিদর্শনসমূহকে অস্বীকার করার কারণে, অন্যায়ভাবে নাবীগণকে তাদের হত্যা করার কারণে, আর ‘আমাদের হৃদয়গুলো আচ্ছাদিত’ তাদের এ কথা বলার কারণে- বরং তাদের অস্বীকৃতির কারণে আল্লাহ তাদের হৃদয়গুলোতে মোহর মেরে দিয়েছেন। যে কারণে তাদের অল্পসংখ্যক ছাড়া ঈমান আনে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (649,4,156,'(তাদের প্রতি আল্লাহর অসন্তোষ নেমে এসেছে) তাদের কুফরীর জন্য আর মারইয়ামের প্রতি তাদের গুরুতর অপবাদপূর্ণ কথা উচ্চারণের জন্য। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (650,4,157,'আর ‘আমরা আল্লাহর রসূল মাসীহ ঈসা ইবনু মারইয়ামকে হত্যা করেছি’ তাদের এ উক্তির জন্য। কিন্তু তারা না তাকে হত্যা করেছে, না তাকে ক্রুশবিদ্ধ করেছে, কেবলমাত্র তাদের জন্য (এক লোককে) তার সদৃশ করা হয়েছিল, আর যারা এ বিষয়ে মতভেদ করেছিল তারাও এ সম্পর্কে সন্দেহে পতিত হয়েছিল। শুধু অমূলক ধারণার অনুসরণ ছাড়া এ ব্যাপারে তাদের কোন জ্ঞানই ছিল না। এটা নিশ্চিত সত্য যে, তারা তাকে হত্যা করেনি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (651,4,158,'বরং আল্লাহ তাকে নিজের কাছে উঠিয়ে নিয়েছেন, আর আল্লাহ হলেন মহাপরাক্রমশালী, মহাবিজ্ঞানী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (652,4,159,'কিতাবওয়ালাদের মধ্যে এমন কেউ নেই যে, তার মৃত্যুর পূর্বে তার প্রতি অবশ্যই ঈমান আনবে না, আর ক্বিয়ামাতের দিন সে তাদের বিরুদ্ধে সাক্ষ্য দেবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (653,4,160,'আমি ইয়াহূদীদের জন্য পবিত্র বস্তুসমূহ যা তাদের জন্য হালাল ছিল, তা হারাম করে দিয়েছি তাদের বাড়াবাড়ির কারণে আর বহু লোককে আল্লাহর পথে তাদের বাধা দেয়ার কারণে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (654,4,161,'এবং তাদের সুদ গ্রহণের কারণে যদিও তাত্থেকে তাদেরকে নিষেধ করা হয়েছিল এবং তাদের অন্যায়ভাবে লোকেদের ধন-সম্পদ গ্রাস করার কারণে এবং আমি তাদের মাঝে যারা অবিশ্বাসী তাদের জন্যে যন্ত্রণাদায়ক শাস্তি প্রস্তুত করে রেখেছি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (655,4,162,'কিন্তু তাদের মধ্যে যারা জ্ঞানে পরিপক্ক আর মু’মিনগণ তোমার প্রতি যা অবতীর্ণ হয়েছে আর তোমার পূর্বে যা অবতীর্ণ হয়েছে তাতে বিশ্বাস স্থাপন করে আর তারা নামায ক্বায়িমকারী ও যাকাত আদায়কারী এবং আল্লাহ্তে ও শেষ দিনের প্রতি বিশ্বাসী; এদেরকেই আমি শীঘ্র মহাপুরস্কার দান করব। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (656,4,163,'আমি তোমার কাছে ওয়াহী পাঠিয়েছি যেমন নূহ ও তার আগের নাবীগণের নিকট ওয়াহী পাঠিয়েছিলাম, আর ইবরাহীম, ইসমাঈল, ইসহাক, ইয়াকুব ও তার বংশধর আর ঈসা, আইয়ূব, ইউনুস, হারূন ও সুলায়মানের নিকটও ওয়াহী পাঠিয়েছিলাম আর আমি দাঊদকে যাবূর প্রদান করেছিলাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (657,4,164,'আমি সেই রসূলদের প্রতিও ওয়াহী পাঠিয়েছি যাদের সম্পর্কে আমি তোমাকে আগেই বলেছি, আর অনেক রসূল যাদের কথা আমি তোমাকে বলিনি। আর আল্লাহ মূসার সাথে কথা বলেছেন সরাসরি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (658,4,165,'রসূলগণ ছিলেন সুসংবাদদাতা ও সতর্ককারী যাতে রসূলদের আগমনের পর আল্লাহর বিরুদ্ধে মানুষের কোন অযুহাতের সুযোগ না থাকে। আল্লাহ হলেন মহাপরাক্রমশালী, বিজ্ঞানময়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (659,4,166,'কিন্তু আল্লাহ সাক্ষ্য দিচ্ছেন যে, যা তিনি নাযিল করেছেন তা তাঁর জ্ঞানের ভিত্তিতেই নাযিল করেছেন। ফেরেশতারাও সে সাক্ষ্য দিচ্ছে আর সাক্ষী হিসেবে আল্লাহ্ই যথেষ্ট। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (660,4,167,'যারা কুফরী করে আর লোকেদেরকে আল্লাহর পথ হতে ফিরিয়ে রাখে তারা চরম পথভ্রষ্টতায় পথভ্রষ্ট হয়ে গেছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (661,4,168,'যারা কুফরী করেছে আর বাড়াবাড়ি করেছে, আল্লাহ তাদেরকে ক্ষমা করবেন না, তাদেরকে কোন পথও দেখাবেন না ।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (662,4,169,'জাহান্নামের পথ ছাড়া, যাতে তারা চিরকালের জন্য স্থায়ী হবে, আর এটা আল্লাহর জন্য সহজ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (663,4,170,'হে মানুষ! রসূল তোমাদের কাছে তোমাদের প্রতিপালকের নিকট হতে সত্য বিধান নিয়ে এসেছে, কাজেই তোমরা ঈমান আন, এটা তোমাদের জন্য কল্যাণকর হবে, আর যদি কুফরী কর (তাহলে জেনে রেখ) আকাশসমূহে আর যমীনে যা কিছু আছে সবই আল্লাহর আর আল্লাহ হলেন সর্বজ্ঞ, মহা কুশলী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (664,4,171,'ওহে কিতাবধারীগণ! তোমরা তোমাদের দ্বীনের ব্যাপারে বাড়াবাড়ি করো না, আর আল্লাহ সম্বন্ধে সত্য ছাড়া কিছু বলো না, ঈসা মাসীহ তো আল্লাহর রসূল আর তাঁর বানী যা তিনি মারইয়ামের নিকট প্রেরণ করেছিলেন, আর তাঁর পক্ষ হতে নির্দেশ, কাজেই তোমরা আল্লাহর প্রতি ও তাঁর রসূলগণের প্রতি ঈমান আনো, আর বলো না ‘তিন’ (জন ইলাহ আছে), নিবৃত্ত হও, তা হবে তোমাদের জন্য কল্যাণকর, আল্লাহ তো একক ইলাহ, তিনি পবিত্র এত্থেকে যে, তাঁর সন্তান হবে। আসমানসমূহে আর যমীনে যা আছে সব কিছু তাঁরই, আর কর্মবিধায়ক হিসেবে আল্লাহই যথেষ্ট। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (665,4,172,'মাসীহ আল্লাহর বান্দা হওয়াকে তুচ্ছ জ্ঞান করে না, আর নৈকট্যের অধিকারী ফেরেশতারাও না। যে তাঁর ‘ইবাদাতকে হেয় জ্ঞান করে আর অহঙ্কার করে, তিনি তাদের সকলকে শীঘ্রই তাঁর কাছে একত্রিত করবেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (666,4,173,'কিন্তু যারা ঈমান আনে ও সৎ কাজ করে আল্লাহ তাদের পুরস্কার পুরোপুরি  দান করবেন, আর  নিজ অনুগ্রহে আরো বেশি দেবেন। আর যারা তুচ্ছ জ্ঞান করবে ও অহঙ্কার করবে তাদেরকে কষ্টদায়ক শাস্তি দান করবেন। আল্লাহ ব্যতীত তাদের জন্য তারা না পাবে কোন অভিভাবক, না (পাবে) কোন সাহায্যকারী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (667,4,174,'হে মানবমন্ডলী! তোমাদের প্রতিপালকের নিকট হতে তোমাদের কাছে উজ্জ্বল প্রমাণ এসে পৌঁছেছে, আর আমি তোমাদের নিকট সুস্পষ্ট জ্যোতি অবতীর্ণ করেছি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (668,4,175,'অতঃপর যারা আল্লাহর প্রতি ঈমান আনে আর তাঁকে দৃঢ়ভাবে অাঁকড়ে ধরে থাকে, অচিরেই তিনি তাদেরকে তাঁর রহমত ও অনুগ্রহের মধ্যে দাখিল করবেন এবং তাঁর দিকে সরল-সুদৃঢ় পথে পরিচালিত করবেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (669,4,176,'লোকেরা তোমার কাছে ফাতাওয়া জিজ্ঞেস করছে; বল, আল্লাহ তোমাদেরকে পিতা-মাতাহীন নিঃসন্তান ব্যক্তি সম্পর্কে ফাতাওয়া দিচ্ছেন, কোন ব্যক্তি মারা গেলে তার যদি সন্তান না থাকে আর তার একটি বোন থাকে, তবে রেখে যাওয়া সম্পত্তির অর্ধেক সে পাবে, আর সে (মৃত নারী) যদি সন্তানহীনা হয় তবে তার ভাই তার উত্তরাধিকারী হবে, আর দু’ বোন থাকলে তারা তার রেখে যাওয়া সম্পত্তির দু’-তৃতীয়াংশ পাবে, আর যদি ভাই ও বোন দু’ই থাকে, তবে পুরুষ পাবে দু’জন নারীর সমান। আল্লাহ সুস্পষ্টভাবে বর্ণনা করেছেন যাতে তোমরা বিভ্রান্তিতে পতিত না হও। আল্লাহ যাবতীয় ব্যাপারে পূর্ণরূপে অবহিত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (670,5,1,'ওহে মু’মিনগণ! তোমরা অঙ্গীকারসমূহ পূর্ণ কর। তোমাদের জন্য গৃহপালিত চতুস্পদ জন্তু হালাল করা হল- সেগুলো ছাড়া যেগুলোর বিবরণ তোমাদেরকে দেয়া হচ্ছে, আর ইহরাম অবস্থায় শিকার করা অবৈধ। আল্লাহ যা চান হুকুম দেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (671,5,2,'ওহে মু’মিনগণ! আল্লাহর নিদর্শনাবলীর, হারাম মাসের, কা‘বায় প্রেরিত কুরবানীর পশুর এবং গলদেশে মাল্য পরিহিত পশুর অসম্মান করো না, যারা নিজ প্রতিপালকের অনুগ্রহ ও সন্তুষ্টি লাভের উদ্দেশে পবিত্র গৃহের আশ্রয়ে চলেছে তাদেরও (অবমাননা) করো না। তোমরা যখন ইহরামমুক্ত হবে তখন শিকার করতে পার। তোমাদেরকে যারা মসজিদে হারাম থেকে বাধা প্রদান করেছিল, তাদের প্রতি শত্রুতা তোমাদেরকে যেন অবশ্যই সীমালঙ্ঘনে প্ররোচিত না করে। সৎকাজ ও তাক্বওয়ার ব্যাপারে তোমরা পরস্পরকে সহযোগিতা কর, পাপ ও সীমালঙ্ঘনের কাজে পরস্পরকে সহযোগিতা করো না। আল্লাহকে ভয় কর, আল্লাহ শাস্তি দানে অত্যন্ত কঠোর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (672,5,3,'তোমাদের জন্য হারাম করা হয়েছে মৃতজন্তু, (প্রবাহিত) রক্ত, শূকরের মাংস, আল্লাহ ছাড়া অন্যের নামে যবহকৃত পশু, আর শ্বাসরুদ্ধ হয়ে মৃত জন্তু, আঘাতে মৃত জন্তু, উপর থেকে পতনের ফলে মৃত, সংঘর্ষে মৃত আর হিংস্র জন্তুতে খাওয়া পশু- তবে জীবিত পেয়ে যা তোমরা যবহ করতে পেরেছ তা বাদে, আর যা কোন আস্তানায় (বা বেদীতে) যবহ করা হয়েছে, আর জুয়ার তীর দ্বারা ভাগ্য নির্ণয় করা (এগুলো তোমাদের জন্য হারাম করা হয়েছে)। এসবগুলো পাপ কাজ। আজ কাফিরগণ তোমাদের দ্বীনের বিরোধিতা করার ব্যাপারে পুরোপুরি নিরাশ হয়ে গেছে, কাজেই তাদেরকে ভয় করো না, কেবল আমাকেই ভয় কর। আজ আমি তোমাদের জন্য তোমাদের দ্বীনকে পূর্ণাঙ্গ করে দিলাম, তোমাদের প্রতি আমার নিআমাত পূর্ণ করে দিলাম এবং ইসলামকে তোমাদের দ্বীন হিসেবে কবূল করে নিলাম। তবে কেউ পাপ করার প্রবণতা ব্যতীত ক্ষুধার জ্বালায় (নিষিদ্ধ বস্তু খেতে) বাধ্য হলে আল্লাহ বড়ই ক্ষমাশীল, পরম দয়ালু। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (673,5,4,'লোকেরা তোমাকে জিজ্ঞেস করছে তাদের জন্য কী কী হালাল করা হয়েছে। বল, যাবতীয় ভাল ও পবিত্র বস্তু তোমাদের জন্য হালাল করা হয়েছে, আর শিকারী পশু-পক্ষী- যাদেরকে তোমরা শিক্ষা দিয়েছ যেভাবে আল্লাহ তোমাদেরকে শিক্ষা দিয়েছেন- সুতরাং তারা যা তোমাদের জন্য ধরে রাখে তা তোমরা ভক্ষণ করবে আর তাতে আল্লাহর নাম উচ্চারণ করবে, আর আল্লাহকে ভয় করবে, আল্লাহ হিসাব গ্রহণে ত্বরিৎগতি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (674,5,5,'আজ তোমাদের জন্য যাবতীয় ভাল ও পবিত্র বস্তু হালাল করা হল আর যাদেরকে কিতাব দেয়া হয়েছে তাদের খাদ্য তোমাদের জন্য হালাল, আর তোমাদের খাদ্য তাদের জন্য হালাল, সচ্চরিত্রা মু’মিন নারী এবং তোমাদের পূর্বে যাদেরকে কিতাব দেয়া হয়েছে তাদের সচ্চরিত্রা নারী তোমাদের জন্য হালাল করা হল যখন তোমরা তাদেরকে মোহরানা প্রদান কর, বিবাহের দূর্গে স্থান দানের উদ্দেশ্যে, ব্যভিচারী হিসেবে নয় এবং গোপন সঙ্গী গ্রহণকারী হিসেবে নয়। কেউ ঈমান অমান্য করলে, তার কার্যাদি নিষ্ফল হবে। আর সে আখেরাতে ক্ষতিগ্রস্তদের অন্তর্ভুক্ত হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (675,5,6,'হে মু’মিনগণ! তোমরা যখন সলাতের জন্য উঠবে, তখন তোমাদের মুখমন্ডল এবং কনুই পর্যন্ত হস্তদ্বয় ধৌত করবে। আর তোমাদের মাথা মাসেহ করবে এবং পা গোড়ালি পর্যন্ত ধৌত করবে। তোমরা যদি অপবিত্র অবস্থায় থাক তবে বিধিমত পবিত্রতা অর্জন করবে। আর যদি পীড়িত হও বা সফরে থাক অথবা তোমাদের কেউ যদি মলত্যাগ করে আসে অথবা যদি তোমরা স্ত্রীদের সাথে সহবাস কর আর পানি না পাও তাহলে পবিত্র মাটি দিয়ে তায়াম্মুম করবে তা দিয়ে তোমাদের মুখমন্ডল ও হাত মাসেহ করবে। আল্লাহ তোমাদের উপর সংকীর্ণতা চাপিয়ে দিতে চান না, তিনি তোমাদেরকে পবিত্র করতে চান আর তোমাদের প্রতি  তাঁর নি‘আমাত পূর্ণ  করতে চান, যাতে তোমরা কৃতজ্ঞতা প্রকাশ কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (676,5,7,'তোমাদের প্রতি আল্লাহর নিআমতের কথা স্মরণ কর আর তাঁর অঙ্গীকারের কথা যা তিনি তোমাদের নিকট থেকে গ্রহণ করেছিলেন যখন তোমরা বলেছিলে- আমরা শুনলাম ও মেনে নিলাম। আল্লাহকে ভয় কর, অন্তরে যা আছে সে সম্পর্কে আল্লাহ খুব ভালভাবেই অবগত আছেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (677,5,8,'হে মু’মিনগণ! তোমরা ন্যায়ের সাক্ষ্যদাতা হিসেবে আল্লাহর পথে দৃঢ়ভাবে দন্ডায়মান থাক, কোন সম্প্রদায়ের প্রতি শত্রুতা তোমাদেরকে যেন এতটা উত্তেজিত না করে যে তোমরা ইনসাফ করা ত্যাগ করবে, সুবিচার কর, এটা তাক্বওয়ার নিকটবর্তী, তোমরা যা কর সে সম্পর্কে আল্লাহ সম্পূর্ণ ওয়াকিফহাল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (678,5,9,'যারা ঈমান আনে ও সৎকাজ করে তাদের প্রতি আল্লাহ ওয়া‘দা করেছেন যে, তাদের জন্য রয়েছে ক্ষমা ও মহা পুরস্কার। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (679,5,10,'যারা কুফরী করে, আর আমার নিদর্শনাবলীকে অস্বীকার করে তারা হল জাহান্নামের অধিবাসী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (680,5,11,'হে মু’মিনগণ! তোমরা আল্লাহর নি‘আমাতের কথা স্মরণ কর যখন একটি সম্প্রদায় তোমাদের বিরুদ্ধে তাদের হস্ত উত্তোলন করতে চেয়েছিল, তখন তিনি তোমাদের থেকে তাদের হাত নিবৃত্ত করেছিলেন। আল্লাহকে ভয় কর, আর মুমিনগণ যেন আল্লাহরই উপর ভরসা করে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (681,5,12,'আল্লাহ বানী ইসরাঈলের নিকট থেকে প্রতিশ্রুতি গ্রহণ করেছিলেন, আর তাদের মধ্যে বারজন প্রধান নিযুক্ত করেছিলেন। আর আল্লাহ বলেছিলেন, আমি তোমাদের সঙ্গে আছি, যদি তোমরা নামায কায়িম কর, যাকাত আদায় কর এবং আমার রসূলগণের প্রতি বিশ্বাস স্থাপন কর আর তাদেরকে সাহায্য-সহযোগিতা কর আর আল্লাহকে ঋণ দান কর উত্তম ঋণ, তাহলে আমি তোমাদের পাপগুলো অবশ্য অবশ্যই দূর করে দেব, আর অবশ্য অবশ্যই তোমাদেরকে জান্নাতে দাখিল করাব যার নিম্নে ঝর্ণাধারা প্রবাহিত। এরপরও তোমাদের মধ্যে যারা কুফুরী করবে তারা সত্য সঠিক পথ হারিয়ে ফেলবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (682,5,13,'তাদের অঙ্গীকার ভঙ্গের কারণে আমি তাদেরকে লা‘নাত করেছি আর তাদের হৃদয়কে আরো শক্ত করে দিয়েছি, তারা শব্দগুলোকে স্বস্থান থেকে বিচ্যুত করেছিল এবং তাদেরকে দেয়া উপদেশের বড় অংশ তারা ভুলে গিয়েছিল। তুমি অল্প সংখ্যক ছাড়া তাদেরকে সর্বদা বিশ্বাসঘাতকতা করতে দেখতে পাবে। কাজেই তাদেরকে ক্ষমা কর, মার্জনা কর, আল্লাহ সৎকর্মশীলদের ভালবাসেন।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (683,5,14,'আর যারা বলে ‘‘আমরা খ্রীস্টান’’ আমি তাদের হতেও অঙ্গীকার গ্রহণ করেছিলাম। কিন্তু তারা তাদের প্রতি উপদেশের একটা বড় অংশ ভুলে গিয়েছিল। কাজেই আমি কিয়ামাত পর্যন্ত তাদের পরস্পরের মধ্যে শত্রুতা ও বিদ্বেষ জিইয়ে দিয়েছি। তারা যা করছিল অচিরেই আল্লাহ তা তাদেরকে জানিয়ে দিবেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (684,5,15,'হে কিতাবধারীগণ! তোমাদের কাছে আমার রসূল এসে গেছে, সে তোমাদেরকে অনেক বিষয় বর্ণনা করে কিতাব থেকে যা তোমরা গোপন করতে আর অনেক বিষয় উপেক্ষা করে। তোমাদের নিকট আল্লাহর পক্ষ হতে জ্যোতি ও স্পষ্ট কিতাব এসেছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (685,5,16,'আল্লাহ তদ্বারা তাদেরকে শান্তি ও নিরাপত্তার পথে পরিচালিত করেন যারা তাঁর সন্তুষ্টি অনুসন্ধান করে এবং নিজ অনুমতিক্রমে তিনি তাদেরকে অন্ধকার হতে বের করে আলোর দিকে নিয়ে আসেন আর তাদেরকে সরল সঠিক পথে পরিচালিত করেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (686,5,17,'তারা কুফরী করেছে যারা বলে মাসীহ্ ইবনে মারইয়ামই আল্লাহ। বল, মাসীহ ইবনে মারইয়াম, আর তার মা এবং পৃথিবীতে যারা আছে সকলকে ধ্বংস করতে চাইলে আল্লাহর বিরুদ্ধে কারো এতটুকু ক্ষমতা আছে কি? আসমানসমূহে আর পৃথিবীতে ও এদের মধ্যে যা কিছু আছে সব আল্লাহরই মালিকানাধীন। তিনি যা ইচ্ছে সৃষ্টি করেন। আল্লাহ্ সকল বিষয়ে সর্বশক্তিমান। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (687,5,18,'ইয়াহূদী ও নাসারারা বলে, আমরা আল্লাহর পুত্র ও তাঁর প্রিয়পাত্র। বল, তাহলে তোমাদের পাপের জন্য আল্লাহ তোমাদেরকে শাস্তি দেন কেন? বরং তিনি যাদের সৃষ্টি করেছেন তোমরা তাদের অন্তর্গত মানুষ, তিনি যাকে ইচ্ছে ক্ষমা করেন, যাকে ইচ্ছে শাস্তি দেন, আকাশসমূহ ও পৃথিবী আর এদের মধ্যে যা আছে সবকিছুর সার্বভৌমত্ব আল্লাহরই জন্য, আর প্রত্যাবর্তন তাঁরই পানে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (688,5,19,'ওহে আহলে কিতাব! রসূল প্রেরণে বিরতির পর তোমাদের কাছে আমার রসূল এসে স্পষ্টভাবে তোমাদের নিকট বর্ণনা করে দিচ্ছে যাতে তোমরা বলতে না পার যে, আমাদের কাছে কোন সুসংবাদদাতা ও সাবধানকারী আগমন করেনি। এখন তাই সুসংবাদদাতা ও সাবধানকারী এসে গেছে। আর আল্লাহ হচ্ছেন সকল বিষয়ে সর্বশক্তিমান।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (689,5,20,'স্মরণ কর, যখন মূসা তার সম্প্রদায়কে বলেছিল, হে আমার সম্প্রদায়! তোমরা তোমাদের প্রতি আল্লাহর নি‘য়ামাত স্মরণ কর যে, তিনি তোমাদের মধ্যে নাবী করেছেন এবং তোমাদেরকে বাদশাহ করেছেন আর তোমাদেরকে তিনি এমন কিছু দিয়েছেন যা বিশ্বভুবনে অন্য কাউকে দেননি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (690,5,21,'হে আমার সম্প্রদায়! তোমাদের জন্য যে পবিত্র ভূমি আল্লাহ নির্দিষ্ট করেছেন তাতে প্রবেশ কর আর পশ্চাতে ঘুরে দাঁড়িও না, তা করলে ধ্বংসে পতিত হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (691,5,22,'তারা বলল, হে মূসা! ওখানে অত্যন্ত শক্তিধর এক সম্প্রদায় আছে, তারা ওখান থেকে বের না হওয়া পর্যন্ত আমরা ওখানে প্রবেশ করব না, তারা যদি বের হয় তাহলেই আমরা প্রবেশ করব। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (692,5,23,'যারা (আল্লাহকে) ভয় করছিল তাদের মধ্যে দু’জন লোক যাদেরকে আল্লাহ অনুগ্রহ করেছিলেন বলল, তাদের দরজায় হানা দাও, ঢুকলেই তোমরা জয়ী হয়ে যাবে। তোমরা মু’মিন হলে আল্লাহর উপর ভরসা কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (693,5,24,'তারা বলল, হে মূসা! তারা ওখানে যদ্দিন থাকবে তদ্দিন আমরা ওখানে কক্ষনো প্রবেশ করব না, কাজেই তুমি আর তোমার প্রতিপালক যাও আর যুদ্ধ কর, আমরা এখানেই বসে রইলাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (694,5,25,'সে বলল, ‘‘হে আমার প্রতিপালক! আমার আর আমার ভাইয়ের উপর ছাড়া কারো উপর আমার ক্ষমতা নেই, কাজেই এই বিদ্রোহী সম্প্রদায় থেকে আমাদেরকে পৃথক করে দিন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (695,5,26,'আল্লাহ বললেন, তবে তা চল্লিশ বছর পর্যন্ত তাদের জন্য নিষিদ্ধ হল, উদভ্রান্তের মত তারা যমীনে ঘুরে বেড়াবে, কাজেই এই অবাধ্য সম্প্রদায়ের জন্য হা-হুতাশ করো না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (696,5,27,'আদামের দু’পুত্রের খবর তাদেরকে সঠিকভাবে জানিয়ে দাও। উভয়ে যখন একটি করে কুরবানী হাজির করেছিল তখন তাদের একজনের নিকট হতে কবূল করা হল। অন্যজনের নিকট হতে কবূল করা হল না। সে বলল, আমি তোমাকে অবশ্য অবশ্যই হত্যা করব। অন্যজন বলল, ‘আল্লাহ কেবল মুত্তাকীদের কুরবানী কবূল করেন,');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (697,5,28,'আমাকে হত্যা করার জন্য তুমি আমার দিকে হাত বাড়ালেও আমি তোমাকে হত্যা করার জন্য তোমার দিকে হাত বাড়াব না, আমি বিশ্ব জগতের প্রতিপালক আল্লাহকে ভয় করি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (698,5,29,'আমি চাই তুমি আমার ও তোমার পাপের বোঝা বহন কর আর অগ্নিবাসী হয়ে যাও, অন্যায়কারীদের এটাই প্রতিদান।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (699,5,30,'অতঃপর তার আত্মা তাকে ভ্রাতৃহত্যার কাজে প্ররোচিত করল। ফলতঃ সে তাকে হত্যা করল এবং ক্ষতিগ্রস্তদের অন্তর্ভুক্ত হয়ে গেল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (700,5,31,'তারপর আল্লাহ একটি কাক পাঠালেন, সে মাটি খনন করতে লাগল, সে তার ভাইয়ের লাশ কীভাবে গোপন করবে তা দেখানোর জন্য। সে বলল, ধিক আমাকে! আমি এই কাকটির মতও হতে পারলাম না যাতে আমার ভাইয়ের লাশ গোপন করতে পারি? তাই সে অনুতপ্ত হল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (701,5,32,'এ কারণেই আমি বানী ইসরাঈলের জন্য বিধান দিয়েছিলাম যে, যে ব্যক্তি মানুষ হত্যা কিংবা যমীনে সন্ত্রাস সৃষ্টির কারণ ব্যতীত কাউকে হত্যা করবে সে যেন তামাম মানুষকেই হত্যা করল। আর যে মানুষের প্রাণ বাঁচালো, সে যেন তামাম মানুষকে বাঁচালো। তাদের কাছে আমার রসূলগণ সুস্পষ্ট প্রমাণ নিয়ে এসেছিল, এরপরও তাদের অধিকাংশই পৃথিবীতে বাড়াবাড়িই করেছিল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (702,5,33,'যারা আল্লাহ ও তাঁর রসূলের বিরুদ্ধে যুদ্ধ করে আর যমীনে বিশৃক্মখলা ছড়িয়ে বেড়ায় তাদের শাস্তি হল এই যে, তাদেরকে হত্যা করা হবে অথবা ক্রুশবিদ্ধ করা হবে অথবা তাদের হাত পা বিপরীত দিক থেকে কেটে ফেলা হবে, অথবা তাদেরকে দেশ থেকে নির্বাসিত করা হবে। এ হল তাদের জন্য দুনিয়াতে লাঞ্ছনা, আর তাদের জন্য আখেরাতে রয়েছে মহাশাস্তি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (703,5,34,'(তবে এ শাস্তি) তাদের জন্য নয় যারা তোমাদের আয়ত্বে আসার পূর্বে তাওবা করবে। জেনে রেখ, আল্লাহ বড়ই ক্ষমাশীল, পরম দয়ালু। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (704,5,35,'হে ঈমানদারগণ! আল্লাহকে ভয় কর, তাঁর নৈকট্য অনুসন্ধান কর এবং তাঁর পথে জিহাদ কর যাতে তোমরা সফলকাম হতে পার। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (705,5,36,'যারা কুফরী করেছে দুনিয়ায় যা কিছু আছে সব যদি তাদের হয় এবং আরো সমপরিমাণও হয় ক্বিয়ামাত দিবসের শাস্তি থেকে পরিত্রাণের মুক্তিপণ হিসেবে, তবুও তা তাদের থেকে গ্রহণ করা হবে না; তাদের জন্য আছে যন্ত্রণাদায়ক শাস্তি।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (706,5,37,'তারা আগুন থেকে বেরিয়ে আসতে চাইবে, কিন্তু তারা তাত্থেকে বের হতে পারবে না, তাদের জন্য আছে স্থায়ী শাস্তি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (707,5,38,'আর চোর ও চোরনী তাদের হাত কেটে দাও, তাদের কৃতকর্মের ফল স্বরূপ, আল্লাহর পক্ষ থেকে দৃষ্টান্তমূলক শাস্তি। আল্লাহ হলেন মহাপরাক্রান্ত, মহাবিজ্ঞানী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (708,5,39,'কিন্তু অন্যায় করার পর কেউ তাওবা করলে এবং (নিজেকে) সংশোধন করলে, আল্লাহ তার প্রতি ক্ষমাদৃষ্টি করেন, আল্লাহ হলেন বড়ই ক্ষমাশীল, পরম দয়ালু। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (709,5,40,'তুমি কি জান না আল্লাহ, আকাশসমূহ ও যমীনের সার্বভৌমত্ব তাঁরই, যাকে ইচ্ছে তিনি শাস্তি দেন, যাকে ইচ্ছে ক্ষমা করেন, আল্লাহ সর্ব বিষয়ে সর্বশক্তিমান। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (710,5,41,'হে রসূল! কুফরীর ব্যাপারে তাদের প্রতিযোগিতা যেন তোমাকে দুঃখ না দেয়, যারা মুখে বলে ঈমান এনেছি কিন্তু তাদের অন্তর ঈমান আনেনি। আর যারা ইয়াহূদী, তারা মিথ্যা কথা শুনতে বিশেষ পারদর্শী, তারা তোমার কথাগুলো অন্য সম্প্রদায়ের স্বার্থে কান পেতে শোনে যারা তোমার নিকট (কখনো) আসেনি, এরা আল্লাহর কিতাবের শব্দগুলোকে প্রকৃত অর্থ হতে বিকৃত করে। তারা বলে, তোমরা এ রকম নির্দেশপ্রাপ্ত হলে মানবে, আর তা না হলে বর্জন করবে। বস্তুত আল্লাহই যাকে ফিতনায় ফেলতে চান, তার জন্য আল্লাহর কাছে তোমার কিছুই করার নেই। ওরা হল সেই লোক, যাদের অন্তরাত্মাকে আল্লাহ পবিত্র করতে চান না। তাদের জন্য দুনিয়াতে আছে লাঞ্ছনা, আর তাদের জন্য আখেরাতে আছে মহা শাস্তি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (711,5,42,'তারা বেশিবেশি মিথ্যে শুনতে আগ্রহী, হারাম ভক্ষণকারী, তারা যদি তোমার কাছে আসে তাহলে (ইচ্ছে হলে) তাদের বিবাদ নিস্পত্তি কর নতুবা অস্বীকার কর। অস্বীকার করলে তারা তোমার কোনই ক্ষতি করতে পারবে না। যদি বিচার ফায়সালা কর তাহলে ইনসাফের সাথে তাদের বিচার ফায়সালা কর, আল্লাহ্ ন্যায়পরায়ণদেরকে ভালবাসেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (712,5,43,'এরা তোমাকে কীভাবে বিচারক মানতে পারে যখন তাদের মাঝেই তাওরাত বিদ্যমান আছে, তার ভিতর আল্লাহর বিধান আছে, এর পরেও তারা মুখ ফিরিয়ে নেয়, বস্তুত তারা মু’মিনই নয়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (713,5,44,'আমি তাওরাত অবতীর্ণ করেছিলাম, তাতে ছিল সঠিক পথের দিশা ও আলো। অনুগত নাবীগণ এর দ্বারা ইয়াহূদীদেরকে ফায়সালা দিত। দরবেশ ও আলিমরাও (তাই করত) কারণ তাদেরকে আল্লাহর কিতাবের রক্ষক করা হয়েছিল আর তারা ছিল এর সাক্ষী। কাজেই মানুষকে ভয় করো না, আমাকেই ভয় কর, আর আমার আয়াতকে নগণ্য মূল্যে বিক্রয় করো না। আল্লাহ যা নাযিল করেছেন, সে অনুযায়ী যারা বিচার ফায়সালা করে না তারাই কাফির। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (714,5,45,'আমি তাদের জন্য তাতে বিধান দিয়েছিলাম যে, জানের বদলে জান, চোখের বদলে চোখ, নাকের বদলে নাক, কানের বদলে কান, আর দাঁতের বদলে দাঁত। আর জখমের বদলে অনুরূপ জখম। কেউ ক্ষমা করে দিলে তাতে তারই পাপ মোচন হবে। আল্লাহ যা নাযিল করেছেন সে অনুযায়ী যারা বিচার ফায়সালা করে না তারাই যালিম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (715,5,46,'তাদের পশ্চাতে মারইয়াম পুত্র ‘ঈসাকে পাঠিয়েছিলাম তাদের সামনে তাওরাত কিতাবের যা কিছু ছিল তার সত্যতা প্রতিপাদনকারী হিসেবে। তাকে ইঞ্জিল দিয়েছিলাম, যাতে ছিল সত্য পথের দিশা ও আলো, এবং ইহা পূর্ববর্তী কিতাব তাওরাতকে সত্যায়নকারী এবং মুত্তাকীদের জন্য সঠিক পথের দিশা ও নাসীহাত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (716,5,47,'ইঞ্জিলের অনুসারীগণ যেন আল্লাহ তাতে যে বিধান দিয়েছেন তদনুযায়ী বিচার ফয়সালা করে। আল্লাহ যা নাযিল করেছেন তদনুযায়ী যারা বিচার ফায়সালা করে না তারাই ফাসিক। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (717,5,48,'আর আমি সত্য বিধানসহ তোমার প্রতি কিতাব নাযিল করেছি যা পূর্ববর্তী কিতাবসমূহের সত্যায়নকারী ও সংরক্ষক। কাজেই মানুষদের মধ্যে বিচার ফায়সালা কর আল্লাহ যা নাযিল করেছেন তদনুসারে, আর তোমার কাছে যে সত্যবিধান এসেছে তা ছেড়ে দিয়ে তাদের খেয়াল খুশির অনুসরণ করো না। আমি তোমাদের প্রত্যেকের জন্য একটি শরীয়াত ও একটি কর্মপথ নির্ধারণ করেছি। আল্লাহ ইচ্ছে করলে তোমাদেরকে এক উম্মাত করতেন। কিন্তু তিনি তোমাদেরকে যা দিয়েছেন সেই ব্যাপারে তিনি তোমাদের পরীক্ষা করতে চান। কাজেই তোমরা সৎকর্মে অগ্রগামী হও, তোমাদের সকলের প্রত্যাবর্তন আল্লাহর দিকেই। অতঃপর তোমরা যে বিষয়ে মতভেদ করছিলে, সে সম্বন্ধে তিনি তোমাদেরকে অবহিত করবেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (718,5,49,'আর তুমি তাদের মধ্যে বিচার ফয়সালা কর আল্লাহ যা নাযিল করেছেন তদনুযায়ী, তাদের খেয়াল খুশির অনুসরণ করবে না। আর তাদের থেকে সতর্ক থাক তারা যেন আল্লাহ তোমার প্রতি যা নাযিল করেছেন তার কোন কিছু থেকে তোমাকে ফেতনায় না ফেলতে পারে। যদি তারা মুখ ফিরিয়ে নেয়, তবে জেনে রেখ, আল্লাহ তাদের কোন কোন পাপের কারণে তাদেরকে শাস্তি দিতে চান, মানুষদের অধিকাংশই প্রকৃতপক্ষে সত্য ত্যাগী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (719,5,50,'তারা কি জাহিলী যুগের আইন বিধান চায়? দৃঢ় বিশ্বাসী সম্প্রদায়ের জন্য আইন-বিধান প্রদানে আল্লাহ হতে কে বেশী শ্রেষ্ঠ? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (720,5,51,'হে ঈমানদারগণ! তোমরা ইয়াহূদ ও নাসারাদেরকে বন্ধু হিসেবে গ্রহণ করো না, তারা একে অপরের বন্ধু। তোমাদের মধ্যে কেউ তাদেরকে বন্ধুরূপে গ্রহণ করলে সে তাদেরই অন্তর্ভুক্ত হবে। আল্লাহ যালিমদেরকে সৎপথে পরিচালিত করেন না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (721,5,52,'যাদের অন্তরে ব্যাধি আছে তুমি তাদেরকে দেখবে তারা তাদের (অর্থাৎ ইয়াহূদী, নাসারা মুশরিকদের) দৌড়ে গিয়ে বলবে, আমাদের ভয় হয় আমরা বিপদের চক্করে পড়ে না যাই। হয়তো আল্লাহ বিজয় দান করবেন কিংবা নিজের পক্ষ হতে এমন কিছু দিবেন যাতে তারা তাদের অন্তরে যা লুকিয়ে রেখেছিল তার কারণে লজ্জিত হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (722,5,53,'মু’মিনগণ বলবে, এরা কি তারাই যারা আল্লাহর নামে শক্ত কসম খেয়ে বলত যে, তারা অবশ্যই তোমাদের সঙ্গে আছে। তাদের কৃতকর্ম নিস্ফল হয়ে গেছে, যার ফলে তারা ক্ষতিগ্রস্ত হয়েছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (723,5,54,'হে ঈমানদারগণ! তোমাদের মধ্য হতে কেউ তার দ্বীন হতে ফিরে গেলে সত্বর আল্লাহ এমন এক সম্প্রদায়কে নিয়ে আসবেন যাদেরকে তিনি ভালবাসেন আর তারাও তাঁকে ভালবাসবে, তারা মু’মিনদের প্রতি কোমল আর কাফিরদের প্রতি কঠোর হবে, তারা আল্লাহর পথে যুদ্ধ করবে, কোন নিন্দুকের নিন্দাকে তারা ভয় করবে না, এটা আল্লাহর অনুগ্রহ- যাকে ইচ্ছে তিনি দান করেন এবং আল্লাহ প্রাচুর্যের অধিকারী, সর্বজ্ঞ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (724,5,55,'তোমাদের বন্ধু কেবল আল্লাহ, তাঁর রসূল ও মু’মিনগণ যারা নামায কায়িম করে, যাকাত আদায় করে এবং আল্লাহর কাছে অবনত হয়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (725,5,56,'যে কেউ আল্লাহ ও তাঁর রসূল এবং ঈমানদারগণকে বন্ধুরূপে গ্রহণ করবে (সে দেখতে পাবে যে) আল্লাহর দলই বিজয়ী হবে।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (726,5,57,'হে ঈমানদারগণ! তোমাদের পূর্বে যাদেরকে কিতাব দেয়া হয়েছিল তাদের মধ্যে যারা তোমাদের দ্বীনকে হাসি-তামাসা ও খেলার বস্তু হিসেবে গ্রহণ করে তাদেরকে এবং কাফিরদেরকে বন্ধুরূপে গ্রহণ করো না আর আল্লাহকে ভয় কর যদি তোমরা মু’মিন হও। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (727,5,58,'তোমরা যখন সলাতের জন্য আহবান জানাও তখন তারা সেটিকে তামাশা ও খেলার বস্তু হিসেবে গ্রহণ করে। এটা এজন্য যে, তারা হল নির্বোধ সম্প্রদায়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (728,5,59,'বল, ‘ওহে কিতাবধারী সম্প্রদায়! তোমরা এ ছাড়া অন্য কারণে আমাদের প্রতি রাগান্বিত নও যে, আমরা আল্লাহর প্রতি এবং আমাদের প্রতি আর আমাদের পূর্বে যা নাযিল হয়েছিল তার প্রতি ঈমান এনেছি, তোমাদের অধিকাংশই তো হচ্ছে ফাসিক।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (729,5,60,'বল, আমি তোমাদেরকে কি এর চেয়ে খারাপ কিছুর সংবাদ দেব যা আল্লাহর নিকট প্রতিদান হিসেবে আছে? (আর তা হল) যাকে আল্লাহ লা‘নাত করেছেন, যার উপর তিনি ক্রোধান্বিত হয়েছেন, যাদের কতককে তিনি বানর ও শুকরে পরিণত করেছেন আর যারা তাগুতের ‘ইবাদাত করেছে তারাই সবচেয়ে নিকৃষ্ট মানের লোক এবং সরল সত্য পথ হতে সবচেয়ে বিচ্যুত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (730,5,61,'যখন তারা তোমাদের কাছে আসে তখন বলে ‘‘আমরা ঈমান এনেছি’’। বাস্তবে তারা কুফরী নিয়েই প্রবেশ করে, কুফরী নিয়েই বেরিয়ে যায়, তারা যা লুকিয়ে রাখে আল্লাহ সে সম্পর্কে পূর্ণরূপে অবগত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (731,5,62,'তাদের অনেককেই তুমি পাপ, শত্রুতা আর হারাম ভক্ষণের প্রতিযোগিতায় ব্যস্ত দেখতে পাবে। তারা যা করে তা কতই না নিকৃষ্ট! ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (732,5,63,'দরবেশ ও পুরোহিতগণ তাদেরকে পাপের কথা বলা হতে এবং হারাম ভক্ষণ থেকে নিষেধ করে না কেন? তারা যা করে তা কতই না নিকৃষ্ট! ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (733,5,64,'ইয়াহূদীরা বলে, আল্লাহর হাত আবদ্ধ, তাদের হাতই আবদ্ধ, তাদের (প্রলাপ) উক্তির কারণে তারা হয়েছে অভিশপ্ত, বরং আল্লাহর উভয় হাত প্রসারিত, যেভাবে ইচ্ছে করেন দান করেন, তোমার প্রতিপালকের নিকট হতে তোমার নিকট যা অবতীর্ণ হয়েছে তা তাদের অনেকের সীমালঙ্ঘন ও কুফরী অবশ্য অবশ্যই বাড়িয়ে দিবে, আর ক্বিয়ামাত অবধি আমি তাদের পরস্পরের মধ্যে শত্রুতা ও বিদ্বেষ সৃষ্টি করে দিয়েছি। যখনই তারা যুদ্ধের আগুন প্রজ্জ্বলিত করে, আল্লাহ তা নিভিয়ে দেন, আর তারা দুনিয়ায় ফাসাদ ছড়িয়ে বেড়ায়, আল্লাহ ফাসাদ সৃষ্টিকারীদের পছন্দ করেন না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (734,5,65,'গ্রন্থধারীরা যদি ঈমান আনতো আর তাকওয়া অবলম্বন করত তাহলে আমি তাদের পাপ অবশ্যই মোচন করে দিতাম আর তাদেরকে নিআমতে ভরা জান্নাতে অবশ্যই দাখিল করতাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (735,5,66,'তারা যদি তাওরাত ইঞ্জিল আর তাদের প্রতিপালকের নিকট থেকে যা তাদের প্রতি অবতীর্ণ হয়েছে তার নিয়ম-বিধান প্রতিষ্ঠিত করত, তাহলে তাদের উপর থেকে আর তাদের পায়ের নীচ থেকে আহার্য পেত। তাদের মধ্যে একটি সত্যপন্থী দল আছে, কিন্তু তাদের অধিকাংশই যা করে তা খারাপ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (736,5,67,'হে রসূল! তোমার প্রতিপালকের নিকট থেকে যা তোমার প্রতি অবতীর্ণ হয়েছে তা প্রচার কর, যদি না কর তাহলে তুমি তাঁর বার্তা পৌঁছানোর দায়িত্ব পালন করলে না। মানুষের অনিষ্ট হতে আল্লাহ্ই তোমাকে রক্ষা করবেন, আল্লাহ কাফির সম্প্রদায়কে কক্ষনো সৎপথ প্রদর্শন করবেন না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (737,5,68,'বল, হে গ্রন্থধারীগণ! তোমরা তাওরাত, ইঞ্জিল আর তোমাদের রবের নিকট হতে যা তোমাদের প্রতি নাযিল হয়েছে তার বিধি-বিধান প্রতিষ্ঠিত না করা পর্যন্ত তোমরা কোন ভিত্তির উপরই দন্ডায়মান নও। তোমার রবের নিকট হতে যা তোমার প্রতি অবতীর্ণ হয়েছে তা তাদের অনেকের সীমালঙ্ঘন আর কুফরী অবশ্য অবশ্যই বৃদ্ধি করবে। কাজেই কাফির সম্প্রদায়ের জন্য তুমি আফসোস করো না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (738,5,69,'যারা ঈমান এনেছে আর যারা ইয়াহূদী হয়েছে আর সাবিয়ী সম্প্রদায় আর নাসারা, যে কেউ আল্লাহ আর আখিরাত দিবসের প্রতি ঈমান আনবে আর সৎকাজ করবে, তাদের জন্য কোন ভয় নেই, চিন্তা-ভাবনা নেই। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (739,5,70,'আমি বানী ইসরাঈলের নিকট হতে অঙ্গীকার নিয়েছিলাম আর তাদের কাছে রসূলগণকে পাঠিয়েছিলাম। যখনই কোন রসূল তাদের কাছে এমন কিছু নিয়ে এসেছে যা তাদের মনঃপুত নয়, তখন কতককে তারা অমান্য করেছে আর কতককে হত্যা করেছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (740,5,71,'তারা ভেবেছিল তাদের কোন বিপর্যয় হবে না এজন্য তারা অন্ধ ও বধির হয়ে গিয়েছিল। অতঃপর আল্লাহ তাদের প্রতি ক্ষমা দৃষ্টি করলেন। তারপর তাদের অনেকেই পুনরায় অন্ধ ও বধির হয়ে গেল। তারা যা করে আল্লাহ তার সম্যক দ্রষ্টা। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (741,5,72,'তারা অবশ্যই কুফরী করেছে যারা বলে, মারইয়াম পুত্র মাসীহই হচ্ছেন আল্লাহ। মাসীহ তো বলেছিল, হে বানী ইসরাঈল! তোমরা আল্লাহর ‘ইবাদাত কর যিনি আমার প্রতিপালক এবং তোমাদেরও প্রতিপালক। যে ব্যক্তি আল্লাহর সঙ্গে অংশীস্থাপন করে তার জন্য আল্লাহ অবশ্যই জান্নাত হারাম করে দিয়েছেন আর তার আবাস হল জাহান্নাম। যালিমদের জন্য কোন সাহায্যকারী নেই। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (742,5,73,'তারা অবশ্যই কুফরী করেছে যারা বলে আল্লাহ তিন জনের মধ্যে একজন, কারণ এক ইলাহ ছাড়া আর কোন সত্যিকার ইলাহ নেই। তারা যা বলছে তা থেকে তারা যদি নিবৃত্ত না হয়, তাহলে তাদের মধ্যে যারা কুফরী করেছে তাদেরকে যন্ত্রণাদায়ক আযাব গ্রাস করবেই। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (743,5,74,'তারা কি আল্লাহর নিকট তাওবা করবে না, তাঁর কাছে ক্ষমা চাইবে না, আল্লাহ তো হলেন বড়ই ক্ষমাশীল, পরম দয়ালু।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (744,5,75,'মারইয়াম পুত্র ‘ঈসা রসূল ছাড়া কিছুই ছিল না। তার পূর্বে আরো রসূল অতীত হয়ে গেছে, তার মা ছিল সত্যপন্থী মহিলা, তারা উভয়েই খাবার খেত; লক্ষ্য কর তাদের কাছে (সত্যের) নিদর্শনসমূহ কেমন সুস্পষ্টভাবে তুলে ধরছি আর এটাও লক্ষ্য কর যে, কীভাবে তারা (সত্য হতে) বিপরীত দিকে চলে যাচ্ছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (745,5,76,'বল, তোমরা আল্লাহকে বাদ দিয়ে কি এমন কিছুর ‘ইবাদাত করছ যাদের না আছে কোন ক্ষতি করার ক্ষমতা, আর না আছে উপকার করার। আর আল্লাহ তিনি হলেন সর্বশ্রোতা, সর্বজ্ঞ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (746,5,77,'বল, হে কিতাবধারীগণ! তোমরা তোমাদের দ্বীন সম্বন্ধে অন্যায়ভাবে বাড়াবাড়ি করো না, আর সেই সম্প্রদায়ের খেয়াল খুশির অনুসরণ করো না যারা ইতোপূর্বে পথভ্রষ্ট হয়ে গেছে, অনেককে পথভ্রষ্ট করেছে আর সোজা পথ থেকে বিচ্যুত হয়ে গেছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (747,5,78,'বানী ইসরাঈলের মধ্যে যারা কুফরী করেছিল তাদেরকে দাঊদ ও মারইয়াম পুত্র ঈসার মুখে (উচ্চারিত কথার দ্বারা) অভিশাপ দেয়া হয়েছে। এটা এই কারণে যে তারা অমান্য করেছিল আর তারা ছিল সীমালঙ্ঘনকারী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (748,5,79,'তারা যে সব অসৎকর্ম করত তাত্থেকে একে অন্যকে নিষেধ করত না। তারা যা করত তা কতই না নিকৃষ্ট! ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (749,5,80,'তাদের অনেককে তুমি এমন লোকেদের সঙ্গে বন্ধুত্ব করতে দেখবে যারা কুফরী করে। তাদের আত্মা তাদের জন্য আগে যা পাঠিয়েছে তা কতই না নিকৃষ্ট যার জন্য আল্লাহর গোস্বা তাদের উপর পতিত হয়েছে আর তারা চিরস্থায়ী ‘আযাবে নিমজ্জিত হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (750,5,81,'তারা যদি আল্লাহর, নাবীর ও তার প্রতি যা নাযিল হয়েছে তার উপর ঈমান আনতো তবে তাদেরকে (অর্থাৎ কাফিরদেরকে) বন্ধুরূপে গ্রহণ করত না, কিন্তু তাদের অধিকাংশই ফাসিক। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (751,5,82,'যারা ঈমান এনেছে তাদের প্রতি মানুষের মধ্যে ইয়াহূদ ও মুশরিকদেরকে তুমি অবশ্যই সবচেয়ে বেশি শত্রুতাপরায়ণ দেখতে পাবে, আর যারা বলে ‘‘আমরা নাসারা’’ তাদেরকে তুমি যারা ঈমান এনেছে তাদের জন্য বন্ধুত্বে নিকটতর দেখতে পাবে, কেননা তাদের মধ্যে ‘ইবাদাতকারী ‘আলিম ও সংসার বিরাগী আছে আর তারা অহংকারও করে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (752,5,83,'রসূলের প্রতি যা অবতীর্ণ হয় তারা যখন তা শুনে, তুমি দেখবে, সত্যকে চিনতে পারার কারণে তখন তাদের চক্ষু অশ্রুসিক্ত হয়ে উঠে। তারা বলে, হে আমাদের প্রতিপালক! আমরা ঈমান এনেছি, কাজেই তুমি আমাদেরকে সাক্ষীদাতাদের তালিকাভূক্ত কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (753,5,84,'আমাদের কী হয়েছে যে, আমরা আল্লাহ্তে এবং যে সত্যবিধান আমাদের নিকট এসেছে তাতে ঈমান আনব না, আর আমরা প্রত্যাশা করি যে, আমাদের প্রতিপালক আমাদেরকে সৎকর্মশীলদের অন্তর্ভুক্ত করবেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (754,5,85,'তাদের এ কথার কারণে আল্লাহ তাদেরকে জান্নাত দান করবেন, যার তলদেশে ঝর্ণাধারা প্রবাহিত, তাতে তারা চিরকাল থাকবে, আর এটাই হল সৎকর্মশীলদের পুরস্কার। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (755,5,86,'আর যারা আমার আয়াতগুলোকে প্রত্যাখ্যান করবে ও মিথ্যা জানবে, তারা হবে জাহান্নামের অধিবাসী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (756,5,87,'ওহে ঈমানদারগণ! পবিত্র বস্তুরাজি যা আল্লাহ তোমাদের জন্য হালাল করে দিয়েছেন সেগুলোকে হারাম করে নিও না আর সীমালঙ্ঘন করো না, অবশ্যই আল্লাহ সীমালঙ্ঘনকারীদের ভালবাসেন না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (757,5,88,'যে সমস্ত হালাল ও পবিত্র জীবিকা আল্লাহ তোমাদেরকে দিয়েছেন সেগুলো ভক্ষণ কর, যে আল্লাহর প্রতি তোমরা ঈমান এনেছ তাঁকে ভয় কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (758,5,89,'তোমাদের অর্থহীন শপথের জন্য আল্লাহ তোমাদেরকে পাকড়াও করবেন না, কিন্তু বুঝে সুঝে যে সব শপথ তোমরা কর তার জন্য তোমাদেরকে পাকড়াও করবেন। (এ পাকড়াও থেকে অব্যাহতির) কাফফারা হল দশ জন মিসকিনকে মধ্যম মানের খাদ্যদান যা তোমরা তোমাদের স্ত্রী পরিবারকে খাইয়ে থাক, অথবা তাদেরকে বস্ত্রদান অথবা একজন ক্রীতদাস মুক্তকরণ। আর এগুলো করার যার সামর্থ্য নেই তার জন্য তিন দিন রোযা পালন। এগুলো হল তোমাদের শপথের কাফফারা যখন তোমরা শপথ কর। তোমরা তোমাদের শপথ রক্ষা করবে। আল্লাহ তাঁর আয়াতসমূহ তোমাদের জন্য বিষদভাবে বর্ণনা করেন যাতে তোমরা শোকর আদায় কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (759,5,90,'হে বিশ্বাসীগণ! মদ, জুয়া আর মূর্তী ও ভাগ্য নির্ধারক তীর ঘৃণিত শয়তানী কাজ, তোমরা তা বর্জন কর, যাতে তোমরা সাফল্যমন্ডিত হতে পার। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (760,5,91,'মদ আর জুয়ার মাধ্যমে শয়তান তো চায় তোমাদের মাঝে শত্রুতা আর বিদ্বেষ সৃষ্টি করতে, আল্লাহর স্মরণ আর নামায থেকে তোমাদেরকে বাধা দিতে। কাজেই তোমরা কি এসব থেকে বিরত থাকবে? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (761,5,92,'তোমরা আল্লাহকে মেনে চল আর তাঁর রসূলকে মেনে চল আর (মন্দ থেকে) সতর্ক থাক আর যদি তোমরা মুখ ফিরিয়ে নাও, তাহলে জেনে রেখ আমার রসূলের দায়িত্ব হল সুস্পষ্টভাবে (আমার বাণী) পৌঁছে দেয়া। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (762,5,93,'যারা ঈমান আনে আর সৎকর্ম করে তারা পূর্বে যা খেয়েছে তার জন্য তাদের উপর কোন পাপ নেই যদি তারা (হারাম থেকে) বিরত থাকে, আর ঈমান আনে ও সৎকাজ করে, অতঃপর সাবধানতা অবলম্বন করে আর ঈমানের উপর থাকে, অতঃপর আল্লাহকে ভয় করে এবং সৎকাজ করে। আল্লাহ সৎকর্মশীলদের ভালবাসেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (763,5,94,'ওহে ঈমানদারগণ! আল্লাহ তোমাদেরকে কঠিন পরীক্ষার সম্মুখীন করবেন (মুহরিম অবস্থায়) শিকারের ব্যাপারে যা তোমাদের হাত আর বর্শার নাগালের ভিতর এসে যায়, এটা জেনে নেয়ার জন্য যে অদৃশ্যে থেকেও কে আল্লাহকে ভয় করে। (সুস্পষ্টভাবে জানানো হয়েছে) এরপরও যারা সীমালঙ্ঘন করে তাদের জন্য রয়েছে যন্ত্রণাদায়ক শাস্তি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (764,5,95,'ওহে বিশ্বাসীগণ! ইহরাম অবস্থায় তোমরা শিকার হত্যা করো না। জেনে বুঝে তোমাদের কেউ তা হত্যা করলে তার বিনিময় হচ্ছে অনুরূপ গৃহপালিত জন্তু। যে ব্যাপারে তোমাদের মধ্যের ন্যায়পরায়ণ দু’জন লোক ফায়সালা করে দেবে, তা কা‘বাতে কুরবানীর জন্য পাঠাতে হবে। কিংবা তার কাফফারা হল কয়েকজন মিসকিনকে খাদ্য দান অথবা তদনুরূপ রোযা পালন, যেন সে স্বীয় কৃতকর্মের ফল ভোগ করে, পূর্বে যা হয়ে গেছে আল্লাহ তা ক্ষমা করেছেন; কেউ (পাপকাজ) পুনরায় করলে আল্লাহ তার নিকট হতে প্রতিশোধ গ্রহণ করবেন। আল্লাহ মহা পরাক্রমশালী, প্রতিশোধ গ্রহণে পূর্ণ সক্ষম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (765,5,96,'সমুদ্রের শিকার ও তা ভক্ষণ তোমাদের জন্য হালাল করা হয়েছে, তোমাদের আর সফরকারীদের ভোগের জন্য। ইহরাম অবস্থায় থাকা পর্যন্ত স্থলের শিকার তোমাদের জন্য হারাম করা হয়েছে। আল্লাহকে ভয় কর যাঁর কাছে তোমাদেরকে একত্রিত করা হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (766,5,97,'পবিত্র ক্বা‘বাগৃহকে আল্লাহ তোমাদের জন্য (জীবনে) প্রতিষ্ঠার উপকরণ করে দিয়েছেন, আর হারাম মাস, কুরবানীর উদ্দেশে ক্বা‘বায় প্রেরিত পশু এবং তাকে চিহ্নিত করার গলার মালাকেও। এটা এজন্য যাতে তোমরা জানতে পার যে, যা কিছু আসমানসমূহে আছে আর যা কিছু যমীনে আছে, আল্লাহ তা জানেন, আর আল্লাহ সকল বিষয়ে সবচেয়ে বেশি অবহিত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (767,5,98,'জেনে রেখ যে, শাস্তি দানে আল্লাহ অত্যন্ত কঠোর আর আল্লাহ হলেন বড়ই ক্ষমাশীল, পরম দয়ালু। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (768,5,99,'(আল্লাহর বাণী) পৌঁছে দেয়া ছাড়া রসূলের উপর দায়িত্ব নেই। তোমরা যা প্রকাশ কর আর গোপন কর, আল্লাহ তা জানেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (769,5,100,'বল, অপবিত্র আর পবিত্র সমান নয়, যদিও অপবিত্র বস্তুর প্রাচুর্য তোমাকে আকৃষ্ট করে। কাজেই হে জ্ঞানী সম্প্রদায়! তোমরা আল্লাহকে ভয় কর যাতে তোমরা সফলকাম হতে পার। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (770,5,101,'হে ঈমানদারগণ! তোমরা এমন বিষয়ে প্রশ্ন করো না, যা তোমাদের কাছে প্রকাশ করা হলে তা তোমাদেরকে কষ্ট দিবে। যে কালে কুরআন নাযিল হচ্ছে সে সময় যদি ওসব বিষয়ে প্রশ্ন কর তবে তা তোমাদের জন্য প্রকাশ করে দেয়া হবে। (এ পর্যন্ত যা করেছ) আল্লাহ তা ক্ষমা করেছেন, আর আল্লাহ হলেন বড়ই ক্ষমাশীল, পরম সহিষ্ণু। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (771,5,102,'তোমাদের পূর্বে এক সম্প্রদায় (অর্থাৎ ইয়াহূদীরা) তা জিজ্ঞেস করেছিল অত:পর (তা অমান্য করে) তারা কাফিরে পরিণত হয়েছিল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (772,5,103,'আল্লাহ না নির্দিষ্ট করেছেন বাহীরাহ্, না সাইবাহ্, না ওয়াসীলাহ্, না হাম বরং যারা কুফরী করেছে তারাই আল্লাহর নামে মিথ্যা আরোপ করে, তাঁদের অধিকাংশই নির্বোধ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (773,5,104,'তাদেরকে যখন বলা হয় আল্লাহ যা অবতীর্ণ করেছেন তার দিকে ও রসূলের দিকে এসো (তখন) তারা বলে, আমরা আমাদের পিতৃপুরুষকে যা করতে দেখেছি (যে পথ ও পন্থা অবলম্বন করতে দেখেছি) আমাদের জন্য তাই যথেষ্ট। যদিও তাদের পিতৃপুরুষরা কিছুই জানত না এবং সঠিক পথপ্রাপ্ত ছিল না (তবুও কি তারা তাদের পথেই চলবে)? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (774,5,105,'হে মু’মিনগণ! তোমাদের দায়িত্ব তোমাদের উপর। যখন তোমরা সত্যপথ প্রাপ্ত হয়েছ তখন যারা পথভ্রষ্ট হয়ে গেছে তারা তোমাদের কোন ক্ষতি করতে পারবে না। তোমাদের সকলের প্রত্যাবর্তন আল্লাহর দিকে, অতঃপর তোমরা যা করছিলে সে সম্পর্কে তোমাদেরকে জানিয়ে দেয়া হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (775,5,106,'হে বিশ্বাসীগণ! তোমাদের কারো মৃত্যু উপস্থিত হলে যখন ওসিয়াত করবে তখন তোমাদের মধ্য হতে দু’জন ন্যায়পরায়ণ লোককে সাক্ষী করবে, আর সফররত অবস্থায় মৃত্যুর মুসিবত উপস্থিত হলে তোমাদের ছাড়া অন্যদের থেকে দু’জন সাক্ষী রাখবে। (সাক্ষীদের সত্যতা সম্পর্কে) তোমাদের সন্দেহ হলে সলাতের পর তাদেরকে রেখে দেবে আর তারা আল্লাহর নামে শপথ করবে যে, আমরা কোন কিছুর বিনিময়ে সাক্ষ্য বিক্রয় করব না, যদিও সে আমাদের আত্মীয় হয়, আর আল্লাহর ওয়াস্তে কৃত সাক্ষ্য গোপন করব না, করলে পাপীদের মধ্যে গণ্য হয়ে যাব। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (776,5,107,'যদি জানা যায় যে, তারা পাপে জড়িয়ে পড়েছে, তবে তাদের স্থলে অন্য দু’জন সেই লোকেদের মধ্য হতে দাঁড়াবে, যাদের স্বত্ব পূর্বের দু’জন সাক্ষী নষ্ট করতে চেয়েছিল। তারা আল্লাহর নামে শপথ করে বলবে যে, আমাদের সাক্ষ্য অবশ্যই তাদের সাক্ষ্য অপেক্ষা অধিক সত্য (আর আমরা আমাদের সাক্ষ্যদানের ব্যাপারে) সীমালঙ্ঘন করিনি, করলে আমরা অবশ্য যালিমদের মধ্যে গণ্য হয়ে যাব। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (777,5,108,'এ পন্থায় বেশি সম্ভাবনা আছে যে, লোকে ঠিকভাবে সাক্ষ্যদান করবে, কিংবা অন্ততঃপক্ষে তারা এ ভয় অবশ্যই করবে যে, তাদের কসম করার পর অপর কোন কসম দ্বারা তাদের প্রতিবাদ করা না হয়। আল্লাহকে ভয় কর আর শোন; আল্লাহ সত্য পরিত্যাগকারী সম্প্রদায়কে সঠিক পথে পরিচালিত করেন না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (778,5,109,'আল্লাহ যে দিন রসূলগণকে একত্রিত করবেন; অতঃপর বলবেন, তোমাদেরকে কী জবাব দেয়া হয়েছিল। তারা বলবে, আমরা কিছুই জানি না, তুমিই সকল গোপন তত্ত্ব জান।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (779,5,110,'যখন আল্লাহ বলেন, ‘‘হে ‘ঈসা বিন মারইয়াম! তুমি তোমার প্রতি আর তোমার মায়ের প্রতি আমার নি‘মাতের কথা স্মরণ কর। আমি তোমাকে রূহুল কুদুর (জিবারাঈল) দিয়ে শক্তিশালী করেছি, তুমি দোলনায় থাকা অবস্থায় আর পূর্ণ বয়সেও মানুষের সাথে কথা বলেছ। স্মরণ কর আমি তোমাকে কিতাব, হিকমাত, তাওরাত ও ইঞ্জিল শিক্ষা দিয়েছিলাম। তুমি আমার অনুমতিক্রমে মাটি দ্বারা পাখীর মত আকৃতি গঠন করতে আর তাতে ফুঁক দিতে তখন তা আমার হুকুমে পাখী হয়ে যেত, জন্মান্ধ আর কুষ্ঠ ব্যাধিগ্রস্ত ব্যক্তিকে তুমি আমার হুকুমে আরোগ্য করতে, স্মরণ কর আমার হুকুমে তুমি মৃতকে জীবিত করতে, স্মরণ কর যখন আমি তোমার থেকে বানী ইসরাঈলকে নিবৃত্ত রেখেছিলাম যখন তুমি তাদের কাছে সুস্পষ্ট নিদর্শনাদি নিয়ে আসলে, তখন তাদের মধ্যে যারা কুফরী করেছিল তারা বলল- "এটা তো স্পষ্ট যাদু ছাড়া আর কিছুই নয়।" ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (780,5,111,'স্মরণ কর যখন আমি হাওয়ারীদেরকে হুকুম করেছিলাম যে, আমার প্রতি আর আমার রসূলের প্রতি ঈমান আন; তারা বলেছিল, আমরা ঈমান আনলাম আর তুমি সাক্ষী থেক যে, আমরা মুসলিম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (781,5,112,'(স্মরণ কর) যখন হাওয়ারীরা বলেছিল, হে ‘ঈসা ইবনু মারইয়াম! আপনার প্রতিপালক কি আমাদের নিকট আসমান থেকে খাঞ্চা  ভর্তি খাদ্য  পাঠাতে পারবেন?  সে বলেছিল, তোমরা যদি মু’মিন হয়ে থাক তাহলে আল্লাহকে ভয় কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (782,5,113,'তারা বলেছিল, আমরা চাই যে আমরা তাত্থেকে কিছু খাব, তাতে আমাদের অন্তর পরিতৃপ্তি লাভ করবে, আর আমরা জানতে পারব যে, আপনি আমাদেরকে সত্য বলেছেন, আর সে ব্যাপারে আমরা সাক্ষী হয়ে থাকব। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (783,5,114,'‘ঈসা ইবনু মারইয়াম বলেছিল, হে আল্লাহ! আমাদের প্রতিপালক আমাদের নিকট আসমান থেকে খাদ্য ভর্তি খাঞ্চা প্রেরণ কর যা আমাদের প্রথম থেকে শেষ সকল ব্যক্তির জন্য আনন্দের ব্যাপার হবে আর হবে তোমার থেকে একটা নিদর্শন। আর আমাদেরকে জীবিকা দান কর; তুমিই সর্বোত্তম রিযকদাতা। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (784,5,115,'আল্লাহ বললেন, আমি তোমাদের কাছে তা নাযিল করব, অতঃপর তোমাদের কেউ কুফরী করলে আমি তাকে এমনভাবে শাস্তি প্রদান করব, যে শাস্তি আমি বিশ্বজগতের কাউকে দেইনি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (785,5,116,'স্মরণ কর, যখন আল্লাহ ঈসা ইবনু মারইয়ামকে বললেন, তুমি কি লোকেদেরকে বলেছিলে, আল্লাহকে ছেড়ে আমাকে আর আমার মাতাকে ইলাহ বানিয়ে নাও।’ (উত্তরে) সে বলেছিল, ‘পবিত্র মহান তুমি, এমন কথা বলা আমার শোভা পায় না যে কথা বলার কোন অধিকার আমার নেই, আমি যদি তা বলতাম, সেটা তো তুমি জানতেই; আমার অন্তরে কী আছে তা তুমি জান কিন্তু তোমার অন্তরে কী আছে তা আমি জানি না, তুমি অবশ্যই যাবতীয় গোপনীয় তত্ত্ব সম্পর্কে পূর্ণরূপে ওয়াকেফহাল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (786,5,117,'তুমি আমাকে যে ব্যাপারে নির্দেশ করেছ তা ছাড়া আমি তাদেরকে অন্য কিছুই বলিনি, (তা এই) যে, তোমরা আল্লাহর ‘ইবাদাত কর যিনি আমার ও তোমাদের প্রতিপালক, আর তাদের কাজ কর্মের ব্যাপারে সাক্ষী ছিলাম যদ্দিন আমি তাদের মাঝে ছিলাম, অতঃপর যখন তুমি  আমাকে উঠিয়ে নিলে, তখন তুমিই ছিলে তাদের কার্যকলাপের তত্ত্বাবধায়ক, আর তুমি হলে প্রত্যেক ব্যাপারে সাক্ষী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (787,5,118,'তুমি যদি তাদেরকে শাস্তি দাও তবে তারা তো তোমারই বান্দাহ আর যদি তাদেরকে ক্ষমা কর তুমি তো মহাপরাক্রান্ত মহাপ্রজ্ঞার অধিকারী।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (788,5,119,'আল্লাহ বলবেন, আজকের দিনে সত্যপন্থীদের সত্যপন্থা উপকার দিবে, তাদের জন্য রয়েছে জান্নাত যার তলদেশে ঝর্ণাধারা প্রবাহিত, তাতে তারা স্থায়ী হয়ে চিরকাল থাকবে, আল্লাহ তাদের প্রতি সন্তুষ্ট হয়েছেন আর তারাও তাঁর প্রতি সন্তুষ্ট। এটাই হল মহা সাফল্য। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (789,5,120,'আসমানসমূহের আর যমীনের আর এদের মাঝে যা কিছু আছে তার সার্বভৌমত্ব আল্লাহরই, আর তিনি সকল বিষয়ে সর্বশক্তিমান। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (790,6,1,'সমস্ত প্রশংসা আল্লাহর জন্য যিনি আসমানসমূহ ও যমীন সৃষ্টি করেছেন আর সৃষ্টি করেছেন অন্ধকার ও আলো, এতদসত্ত্বেও যারা কুফরী করেছে তারা (অন্যকে) তাদের প্রতিপালকের সমকক্ষ দাঁড় করিয়েছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (791,6,2,'যিনি মাটি থেকে তোমাদেরকে সৃষ্টি করেছেন অতঃপর (তোমাদের জীবনের জন্য) একটি নির্দিষ্ট মেয়াদ নির্ধারিত করেছেন, এছাড়া আরেকটি নির্ধারিত মেয়াদ আছে (যে সম্পর্কিত জ্ঞান আছে) তাঁর কাছে, কিন্তু তোমরা সন্দেহই করে চলেছ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (792,6,3,'আসমানসমূহ আর যমীনে তিনিই আল্লাহ, তোমাদের গোপন বিষয়াদি আর তোমাদের প্রকাশ্য বিষয়াদি সম্পর্কে তিনি জানেন, আর তিনি জানেন যা তোমরা উপার্জন কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (793,6,4,'তাদের রব্বের নিদর্শনাবলী হতে এমন কোন নিদর্শন তাদের কাছে আসে না যা থেকে তারা মুখ ফিরিয়ে নেয় না।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (794,6,5,'(এখন) যে সত্য তাদের কাছে এসেছে তারা তা অস্বীকার করেছে। শীঘ্রই তাদের কাছে সে খবর আসবে যে সম্পর্কে তারা ঠাট্টা-বিদ্রূপ করত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (795,6,6,'তারা কি লক্ষ্য করে না তাদের পূর্বে আমি কত জনগোষ্ঠীকে ধ্বংস করে দিয়েছি, তাদেরকে দুনিয়ায় (এমনভাবে) প্রতিষ্ঠিত করেছিলাম যে শক্তি-প্রতিষ্ঠা তোমাদেরকে দেয়া হয়নি, তাদের জন্য প্রচুর বৃষ্টিপাত ঘটিয়েছিলাম, তৈরি করেছিলাম নদী যা তাদের নিম্নদেশ দিয়ে প্রবাহিত হত, অতঃপর তাদের পাপের কারণে তাদেরকে ধ্বংস করে দিয়েছি আর তাদের পরে নতুন জনগোষ্ঠীর উত্থান ঘটালাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (796,6,7,'আমি যদি তোমার উপর কাগজে লেখা কিতাব নাযিল করতাম আর তা তারা তাদের হাতে স্পর্শ করত, তাহলে অবিশ্বাসীরা অবশ্যই বলত এটা স্পষ্ট  যাদু  ছাড়া  আর  কিছু  না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (797,6,8,'আর তারা বলে, আমাদের কাছে ফেরেশতা পাঠানো হয় না কেন? আমি যদি ফেরেশতা পাঠাতাম তাহলে (যাবতীয় ব্যাপারে) চূড়ান্ত ফায়সালাই তো হয়ে যেত, অতঃপর তাদেরকে আর অবকাশ দেয়া হত না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (798,6,9,'আর আমি যদি তাকে ফেরেশতা করতাম তবে তাকে মানুষের আকৃতি বিশিষ্টই করতাম, আর তাদেরকে অবশ্যই গোলকধাঁধায় ফেলে দিতাম যেমন ধাঁধাঁয় তারা এখন পড়েছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (799,6,10,'তোমার পূর্বেও রসূলদেরকে ঠাট্টা-বিদ্রূপ করা হয়েছে, অতঃপর যা নিয়ে তারা ঠাট্টা-বিদ্রূপ করত তাই তাদেরকে পরিবেষ্টন করে ফেলল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (800,6,11,'বল, দুনিয়ায় পরিভ্রমণ কর, অতঃপর দেখ সত্য প্রত্যাখ্যানকারীদের পরিণাম কী দাঁড়িয়েছিল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (801,6,12,'বল, আসমানে আর যমীনে যা আছে তা কার? বল, আল্লাহরই। দয়া করা তিনি তাঁর জন্য কর্তব্য স্থির করে নিয়েছেন, তিনি কিয়ামাত দিবসে তোমাদের সবাইকে একত্রিত করবেন, এতে কোন সন্দেহ নেই। যারা নিজেরাই নিজেদের ক্ষতি করেছে তারা ঈমান আনবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (802,6,13,'রাতে (অন্ধকারে) আর দিনে (আলোয়) যা বাস করে তা তাঁরই, তিনি হলেন সর্বশ্রোতা, সর্বজ্ঞ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (803,6,14,'বল, আমি কি আসমান যমীনের সৃষ্টিকর্তা আল্লাহকে বাদ দিয়ে অন্যকে অভিভাবক বানিয়ে নেব, অথচ তিনিই খাওয়ান, তাঁকে খাওয়ানো হয় না, বল আমাকে আদেশ করা হয়েছে আমি যেন আত্মসমর্পণকারীদের প্রথম হই, আর তুমি কিছুতেই মুশরিকদের মধ্যে শামিল হবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (804,6,15,'বল, যদি আমি আমার রবের অবাধ্য হই, তবে আমি বড় (ভয়াবহ) দিনের শাস্তির ভয় করি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (805,6,16,'সে দিন যাকে (শাস্তি থেকে) রক্ষা করা হবে তাকে তো বড় অনুগ্রহ করা হবে। আর এটাই হবে সুস্পষ্ট সাফল্য। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (806,6,17,'আল্লাহ তোমার কোন ক্ষতি করতে চাইলে তিনি ছাড়া কেউ তা সরাতে পারবে না। আর তিনি যদি তোমার কল্যাণ করতে চান, তবে তো সব কিছুই করার তাঁর ক্ষমতা রয়েছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (807,6,18,'তিনি তাঁর বান্দাদের উপর একচ্ছত্র নিয়ন্ত্রণকারী, তিনি হলেন প্রজ্ঞাময়, সর্ববিষয়ে ওয়াকিফহাল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (808,6,19,'বল, সাক্ষ্যে সবচেয়ে বড় বিষয় কোনটি? বল, আল্লাহ আমার ও তোমাদের মধ্যে সাক্ষী। আর এ কুরআন আমার প্রতি নাযিল করা হয়েছে যাতে আমি তার সাহায্যে তোমাদেরকে আর যাদের কাছে তা পৌঁছবে তাদেরকে সতর্ক করি। তোমরা কি এমন সাক্ষ্য দিতে পার যে, আল্লাহর সঙ্গে অন্য ইলাহও আছে? বল, আমি এমন সাক্ষ্য দেই না, বল তিনি তো এক ইলাহ আর তোমরা যে তাঁর অংশীদার স্থাপন কর, তা থেকে আমি সম্পূর্ণ মুক্ত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (809,6,20,'আমি যাদেরকে কিতাব দিয়েছি তারা তাকে (অর্থাৎ নাবীকে) তেমনি চিনে যেমন চিনে তাদের নিজেদের সন্তানদেরকে, যারা নিজেদের আত্মার ধ্বংস সাধন করেছে, তারা ঈমান আনতে পারবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (810,6,21,'তার থেকে বড় যালিম আর কে আছে যে আল্লাহ সম্পর্কে মিথ্যে রচনা করে অথবা তাঁর নিদর্শনসমূহকে প্রত্যাখ্যান করে? যালিমরা কক্ষনো সফলকাম হবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (811,6,22,'স্মরণ করে সে দিনকে, যে দিন আমি তাদের সবাইকে একত্রিত করব আর যারা শিরক করেছিল তাদেরকে বলব- যাদেরকে তোমরা আমার শরীক মনে করতে তারা কোথায়?');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (812,6,23,'তখন তারা এ কথা বলা ছাড়া আর কোন ফিতনা সৃষ্টি করতে পারবে না যে, আমাদের প্রতিপালক আল্লাহর কসম! আমরা মুশরিক ছিলাম না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (813,6,24,'লক্ষ্য কর তারা নিজেদের সম্পর্কে কেমন মিথ্যে কথা বলবে, আর তারা মিছেমিছি যা উদ্ভাবন করেছিল তা নিস্ফল হয়ে যাবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (814,6,25,'তাদের মধ্যে কতক লোক আছে যারা তোমার দিকে কান পাতে। তাদের অন্তরের উপর আমি পর্দা ঢেলে দিয়েছি যাতে তারা উপলব্ধি করতে না পারে, তাদের কানে আছে বধিরতা, সমস্ত নিদর্শন দেখলেও তারা তাতে ঈমান আনবে না, এমনকি যখন তারা তোমার কাছে আসে তোমার সাথে বিতর্ক করে। কাফিরগণ বলে এটা তো পূর্বেকার লোকেদের গল্প-কাহিনী ছাড়া আর কিছুই নয়।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (815,6,26,'তারা তা (শোনা) থেকে অন্যদের বিরত করে, আর নিজেরাও তাত্থেকে দূরে সরে থাকে, তারা নিজেরাই নিজেদের ধ্বংস সাধন করে কিন্তু সে বোধ তাদের নেই। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (816,6,27,'যদি তুমি দেখতে যখন তাদেরকে জাহান্নামের কিনারায় দাঁড় করানো হবে তখন তারা বলবে হায়! আমাদেরকে যদি আবার (পৃথিবীতে) পাঠানো হত, তাহলে আমরা আমাদের প্রতিপালকের নিদর্শনাবলীকে মিথ্যে মনে করতাম না, আর আমরা মু’মিনদের অন্তর্ভুক্ত হতাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (817,6,28,'বরং(আসল ব্যাপার হল) আগে (দুনিয়াতে) তারা (মিথ্যের আবরণে) যা গোপন করে রাখত এখন তা তাদের কাছে প্রকাশ করা হয়েছে আর তাদেরকে (পৃথিবীতে) ফিরিয়ে দেয়া হলে তারা আবার তা-ই করবে যা করতে তাদেরকে নিষেধ করা হয়েছে, নিশ্চয় তারা হল মিথ্যুক।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (818,6,29,'তারা বলে, আমাদের দুনিয়ার জীবন ছাড়া আর কোন জীবন নেই, আমাদেরকে আবার (জীবিত করে) উঠানো হবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (819,6,30,'তুমি যদি দেখতে যখন তাদেরকে তাদের প্রতিপালকের সামনে দাঁড় করানো হবে, তখন তিনি বলবেন, (তোমরা এখন যে পরিস্থিতির সম্মুখীন হয়েছ) তা কি সত্য নয়? তারা বলবে, আমাদের রবের কসম তা সত্য। তিনি বলবেন, তোমরা কুফরী করেছিলে তার জন্য এখন শাস্তি ভোগ কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (820,6,31,'যারা আল্লাহর সাক্ষাতকে মিথ্যে জেনেছিল তারা ক্ষতিগ্রস্ত হয়ে গেছে। এমনকি যখন কিয়ামাত হঠাৎ তাদের কাছে হাজির হবে তখন তারা বলবে, হায় আক্ষেপ! আমরা এ ব্যাপারে অবহেলা করেছিলাম। তারা তাদের পিঠে তাদের পাপের বোঝা বহন করবে। দেখ, তারা যা বহন করবে তা কতই না নিকৃষ্ট! ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (821,6,32,'দুনিয়ার জীবন খেল-তামাশা ছাড়া আর কিছুই না। যারা তাকওয়া অবলম্বন করে তাদের জন্য পরকালের জীবনই অতি কল্যাণময়, তবুও কি তোমাদের বোধদয় হবে না? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (822,6,33,'তারা যা বলে তা তোমাকে কষ্ট দেয় এটা আমি অবশ্যই ভালভাবে অবগত, কেননা তারা তো তোমাকে মিথ্যে মনে করে না, প্রকৃতপক্ষে যালিমরা আল্লাহর আয়াতকেই প্রত্যাখ্যান করে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (823,6,34,'তোমার পূর্বেও রসূলগণকে মিথ্যে মনে করা হয়েছে কিন্তু তাদেরকে মিথ্যে মনে করা এবং কষ্ট দেয়া সত্ত্বেও তারা ধৈর্যধারণ করেছে, যতক্ষণ না তাদের কাছে আমার সাহায্য এসেছে। আল্লাহর ওয়াদার পরিবর্তন হয় না, নাবীগণের কিছু সংবাদ তো তোমার নিকট পৌঁছেছেই। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (824,6,35,'তাদের উপেক্ষা যদি তোমার কাছে কঠিন বলে মনে হয় তাহলে পারলে ভূগর্ভে সুড়ঙ্গের কিংবা আকাশে আরোহণের জন্য সিঁড়ির সন্ধান কর অত:পর তাদের কাছে (নতুন) নিদর্শন হাজির কর। আল্লাহ ইচ্ছে করলে তাদের সকলকে সৎপথে একত্র করতেন। কাজেই তুমি মূর্খদের মত হয়ো না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (825,6,36,'যারা শোনে শুধু তারাই ডাকে সাড়া দেয়। আর মৃতকে আল্লাহ আবার জীবিত করবেন; অতঃপর তাঁর দিকেই তাদেরকে ফিরিয়ে নেয়া হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (826,6,37,'তারা বলে, তার কাছে তার রবের নিকট হতে কোন নিদর্শন নাযিল হয় না কেন? বল, অবশ্যই আল্লাহ নিদর্শন অবতীর্ণ করতে সক্ষম। কিন্তু তাদের অধিকাংশ লোকই অবগত নয়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (827,6,38,'ভূপৃষ্টে বিচরণশীল এমন কোন জীব নেই, আর দু’ডানা সহযোগে উড্ডয়নশীল এমন কোন পাখি নেই যারা তোমাদের মত একটি উম্মাত নয়। (লাওহে মাহ্ফুয অথবা আল-কুরআন) কিতাবে আমি কোন কিছুই বাদ দেইনি। অতঃপর তাদের প্রতিপালকের কাছে তাদেরকে একত্রিত করা হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (828,6,39,'যারা আমার আয়াতকে মিথ্যে জানে তারা বধির ও মুক। তারা আছে গভীর অন্ধকারে, আল্লাহ যাকে চান পথভ্রষ্ট করে দেন, আর যাকে চান সঠিক পথে স্থাপন করেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (829,6,40,'বল, তোমরা কি ভেবে দেখেছ, তোমাদের উপর যদি আল্লাহর শাস্তি এসে পড়ে কিংবা তোমাদের উপর ক্বিয়ামাত এসে যায় তাহলে কি তোমরা আল্লাহ ছাড়া অন্য কাউকে ডাকবে? (বল না) তোমরা যদি সত্যবাদী হও। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (830,6,41,'বরং (এমন অবস্থায়) তোমরা একমাত্র তাঁকেই ডেকে থাক, অতঃপর ইচ্ছে করলে তিনি তা দূর করে দেন যার জন্য তোমরা তাঁকে ডাক আর তোমরা যাদেরকে তাঁর অংশীদার বানাও তাদের কথা ভুলে যাও। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (831,6,42,'তোমার পূর্বে আমি অনেক জাতির কাছে রসূল পাঠিয়েছি, কিন্তু তাদের অবাধ্যতার কারণে) অভাব অনটন আর দুঃখ-ক্লেশ দিয়ে তাদেরকে পাকড়াও করেছিলাম যাতে তারা বিনীত হয়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (832,6,43,'আমার শাস্তি যখন তাদের উপর পড়ল তখন তারা বিনয় নম্রতা অবলম্বন করল না কেন? বরং তাদের অন্তর আরো শক্ত হয়ে গেল, আর তারা যা করছিল শয়তান সেগুলোকে তাদের জন্য (খুব ভাল কাজ হিসেবে) সুশোভিত করে দিয়েছিল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (833,6,44,'তাদেরকে যে নাসীহাত করা হয়েছিল তারা যখন তা ভুলে গেল, তখন আমি তাদের জন্য যাবতীয় নি‘আমাতের দরজা খুলে দিলাম; পরিশেষে, তাদেরকে যা দেয়া হল তাতে তারা যখন আনন্দে মেতে উঠল, হঠাৎ করে তাদেরকে ধরে বসলাম। তখন (যাবতীয় কল্যাণ থেকে) তারা নিরাশ হয়ে গেল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (834,6,45,'অতঃপর যারা যুলম করেছিল তাদের শিকড় কেটে দেয়া হল, আর সমস্ত প্রশংসা বিশ্বজগতের প্রতিপালকের জন্য। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (835,6,46,'অতঃপর যারা যুলম করেছিল তাদের শিকড় কেটে দেয়া হল, আর সমস্ত প্রশংসা বিশ্বজগতের প্রতিপালকের জন্য। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (836,6,47,'বল, তোমরা কি চিন্তা করে দেখেছ, যদি তোমাদের কাছে আকস্মিক বা প্রকাশ্যে আল্লাহর শাস্তি নেমে আসে তাহলে যালিম সম্প্রদায় ছাড়া আর কে ধ্বংস হবে? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (837,6,48,'আমি তো রসূলদেরকে কেবল সুসংবাদদাতা ও সতর্ককারী করে পাঠিয়েছি, অতঃপর (রসূলের আনুগত্য করে) যারা ঈমান আনে ও নিজেকে সংশোধন করে তাদের নেই কোন ভয়, নেই তাদের কোন দুঃখ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (838,6,49,'আর যারা আমার আয়াতসমূহকে মিথ্যে মনে করে, শাস্তি তাদেরকে স্পর্শ করবে, কেননা তারা নাফরমানীতে লিপ্ত ছিল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (839,6,50,'বল, আমি তোমাদেরকে বলি না যে, আমার কাছে আল্লাহর ধন-ভান্ডার আছে, আর আমি অদৃশ্যের খবরও জানি না। আর আমি তোমাদেরকে এ কথাও বলি না যে, আমি ফেরেশতা, আমার প্রতি যা অবতীর্ণ করা হয় তাছাড়া (অন্য কিছুর) আমি অনুসরণ করি না। বল, অন্ধ আর চোখওয়ালা কি সমান, তোমরা কি চিন্তা করে দেখ না? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (840,6,51,'তুমি তা দিয়ে (অর্থাৎ কুরআন দিয়ে) তাদেরকে সতর্ক কর যারা ভয় করে যে, তাদেরকে তাদের প্রতিপালকের দিকে একত্রিত করা হবে, যিনি ছাড়া তাদের জন্য কোন অভিভাবক নেই এবং সুপারিশকারী নেই- যাতে তারা সংযত হয়ে চলে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (841,6,52,'যারা তাদের প্রতিপালকের সন্তুষ্টি লাভের উদ্দেশ্যে সকাল-সন্ধ্যায় তাঁকে ডাকে তাদেরকে (অর্থাৎ তোমার নিকট সমাগত গরীব মু’মিনদেরকে) তুমি দূরে সরিয়ে দিও না। তাদের কোন ‘আমালের জন্য তোমাকে কোন জবাবদিহি করতে হবে না, আর তোমার কোন ‘আমালের জন্যও তাদেরকে কোন জবাবদিহি করতে হবে না, কাজেই তুমি যদি তাদেরকে অর্থাৎ (গরীব মু’মিনদেরকে) দূরে সরিয়ে দাও তবে তুমি যালিমদের মধ্যে গণ্য হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (842,6,53,'আর এভাবেই আমি তাদের একদলকে অন্যদলের মাধ্যমে পরীক্ষা করেছি যাতে তারা বলে, এরা কি সেই লোক আমাদের মধ্যে যাদেরকে আল্লাহ অনুগ্রহ করেছেন, আল্লাহ কি তাঁর কৃতজ্ঞ বান্দাহদের সম্পর্কে অধিক অবগত নন? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (843,6,54,'আমার আয়াতে বিশ্বাসী লোকেরা যখন তোমার কাছে আসে তখন তাদেরকে বল, ‘‘তোমাদের প্রতি সালাম’’। তোমাদের প্রতিপালক দয়া-রহমাতের নীতি নিজের প্রতি অবধারিত করে নিয়েছেন আর তা হচ্ছে যদি তোমাদের কোন ব্যক্তি না জেনে অন্যায় পাপ করে, অতঃপর তাওবাহ করে ও নিজেকে সংশোধন করে, তাহলে আল্লাহ বড়ই ক্ষমাশীল, বড়ই দয়ালু। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (844,6,55,'এভাবেই আমি আমার আয়াতগুলোকে সুস্পষ্টভাবে বর্ণনা করি যাতে অপরাধীদের পথ পরিষ্কাররূপে প্রকাশ পেয়ে যায়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (845,6,56,'বল, তোমরা আল্লাহকে বাদ দিয়ে যাদেরকে ডাকো, তাদের ইবাদত করতে আমাকে নিষেধ করা হয়েছে। বল, আমি তোমাদের খোশ-খেয়ালের অনুসরণ করি না, তা করলে আমি পথভ্রষ্ট হয়ে যাব, সে অবস্থায় আমি আর হিদায়াতপ্রাপ্তদের অন্তর্ভুক্ত থাকতে পারব না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (846,6,57,'বল, আমি আমার প্রতিপালকের নিকট থেকে পাওয়া এক উজ্জ্বল প্রমাণের উপর প্রতিষ্ঠিত; অতচ তোমরা তা মিথ্যে মনে করেছ। যা তোমরা খুব তাড়াতাড়ি পেতে চাও (অর্থাৎ আল্লাহর আযাব) তা আমার আয়ত্তে নেই। হুকুম বা কর্তৃত্বের মালিকানা আল্লাহ ছাড়া কারো কাছে নেই। তিনিই সত্যকথা বর্ণনা করেন, আর তিনিই সর্বোত্তম ফায়সালাকারী।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (847,6,58,'বল, তোমরা যা তাড়াতাড়ি চাচ্ছ তা যদি আমার কাছে থাকত তাহলে আমার আর তোমাদের মধ্যে যে ব্যাপার তার ফায়সালা হয়ে যেত, অন্যায়কারীদের সম্পর্কে আল্লাহ খুবভাবেই অবহিত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (848,6,59,'সমস্ত গায়বের চাবিকাঠি তাঁর কাছে, তিনি ছাড়া আর কেউ তা জানে না, জলে-স্থলে যা আছে তা তিনি জানেন, এমন একটা পাতাও পড়ে না যা তিনি জানেন না। যমীনের গহীন অন্ধকারে কোন শস্য দানা নেই, নেই কোন ভেজা ও শুকনো জিনিস যা সুস্পষ্ট কিতাবে (লিখিত) নেই। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (849,6,60,'তিনিই রাত্রিকালে তোমাদের আত্মাকে নিয়ে নেন, আর দিনের বেলা যা তোমরা কর তা তিনি জানেন। অতঃপর দিনের বেলা তিনি তোমাদের জাগিয়ে দেন, যাতে জীবনের নির্দিষ্টকাল পূর্ণ হয়। অতঃপর তাঁর পানেই তোমাদের প্রত্যাবর্তন, অতঃপর তিনি তোমাদের নিকট বর্ণনা করে দেবেন যা তোমরা করছিলে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (850,6,61,'তিনি তাঁর বান্দাহ্দের উপর পূর্ণ কর্তৃত্বশীল, আর তিনি তোমাদের উপর রক্ষক নিযুক্ত করেন। অতঃপর তোমাদের কারো মৃত্যুর সময় উপস্থিত হলে আমার প্রেরিতগণ (ফেরেশতারা) তার মৃত্যু ঘটায়। নিজেদের কর্তব্য পালনে তারা বিন্দুমাত্র ত্রুটি করে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (851,6,62,'অতঃপর তাদেরকে তাদের প্রকৃত প্রতিপালকের নিকট ফিরিয়ে নেয়া হবে। সাবধান! কর্তৃত্ব তাঁরই, আর তিনি হিসাব গ্রহণে সর্বাপেক্ষা ত্বরিতগতি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (852,6,63,'বল, বিনীতভাবে আর সংগোপনে যখন তাঁকে ডাক তখন জল-স্থলের অন্ধকার হতে কে তোমাদেরকে রক্ষা করে। (বিপদে পড়লে বলতে থাক) এত্থেকে তুমি যদি আমাদেরকে রক্ষা কর তাহলে আমরা অবশ্যই কৃতজ্ঞদের অন্তর্ভুক্ত হব।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (853,6,64,'বল, আল্লাহই তোমাদেরকে এথেকে আর সমস্ত বিপদ থেকে রক্ষা করেন, অতঃপর তোমরা তাঁর অংশী স্থির কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (854,6,65,'বল, তিনি তোমাদের উপর থেকে অথবা পদতল থেকে ‘আযাব পাঠাতে (সক্ষম) অথবা তোমাদেরকে দলে দলে বিভক্ত করার মাধ্যমে একদলকে অন্যদলের সংঘাত সংঘর্ষ ও হিংসা হানাহানির আস্বাদ গ্রহণ করাতে সক্ষম। লক্ষ্য কর, আমি নিদর্শনাবলী কেমন বিস্তারিত বর্ণনা করছি যাতে তারা উপলব্ধি করতে পারে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (855,6,66,'তোমার কওম তা (অর্থাৎ ‘আযাবকে) মিথ্যে মনে করছে কিন্তু তা প্রকৃত সত্য। বল, আমি তোমাদের কর্মবিধায়ক নই। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (856,6,67,'প্রত্যেক (ভবিষ্যৎ) বাণীর (সত্যরূপে প্রকাশিত হওয়ার) জন্য একটা সময় নির্ধারিত করা আছে আর তা তোমরা শীঘ্রই জানতে পারবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (857,6,68,'যখন তুমি দেখ আমার আয়াত নিয়ে তারা উপহাসপূর্ণ আলোচনা করছে তখন তাদের থেকে সরে পড় যে পর্যন্ত তারা অন্য বিষয়ের আলোচনায় প্রবৃত্ত না হয়। আর যখন শয়ত্বান তোমাকে (আল্লাহর এই নাসীহাত) ভুলিয়ে দেয় তখন স্মরণ হয়ে গেলেই যালিম সম্প্রদায়ের সঙ্গে আর বসবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (858,6,69,'তাদের কোন কাজের হিসেব দেয়ার দায়-দায়িত্ব মুত্তাকীদের উপর নেই। কিন্তু উপদেশ দেয়া কর্তব্য যাতে ওরাও তাকওয়া অবলম্বন করে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (859,6,70,'যারা তাদের দ্বীনকে খেলা তামাশা বানিয়ে নিয়েছে আর পার্থিব জীবন যাদেরকে প্রতারিত করেছে তুমি তাদেরকে বর্জন কর। আর তা (অর্থাৎ কুরআন) দিয়ে তাদেরকে উপদেশ দাও যাতে কেউ স্বীয় কৃতকর্মের কারণে ধ্বংস না হয়, আল্লাহ ছাড়া তার কোন অভিভাবক নেই এবং কোন সুপারিশকারী নেই, (মুক্তির) বিনিময়ে সব কিছু দিতে চাইলেও তা তার থেকে গ্রহণ করা হবে না, ওরাই তারা যারা তাদের কৃতকর্মের জন্য ধ্বংস হবে, তাদের জন্য আছে ফুটন্ত গরম পানীয় আর মহা শাস্তি, যেহেতু তারা কুফরীতে লিপ্ত ছিল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (860,6,71,'বল, আল্লাহকে বাদ দিয়ে আমরা কি এমন কিছুকে ডাকব যা আমাদের উপকারও করে না, অপকারও করে না? আল্লাহ আমাদেরকে হিদায়াত দানের পর আমরা কি পিছনে ফিরে যাব তার মত শয়ত্বান যাকে বিভ্রান্ত করে দিয়েছে আর দুনিয়ায় সে ঘুরে মরছে। অথচ তার সঙ্গী সাথীরা তাকে সঠিক পথের দিকে ডাক দিয়ে বলছে, এদিকে এসো। বল, আল্লাহর হিদায়াতই হচ্ছে সত্যিকারের হিদায়াত, বিশ্বজগতের প্রতিপালকের কাছে আত্মসমর্পণ করার জন্যই আমাদেরকে আদেশ দেয়া হয়েছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (861,6,72,'আরও (আদিষ্ট হয়েছি) নামায কায়িম করতে আর তাঁকে ভয় করতে আর তিনি হলেন (সেই সত্ত্বা) যাঁর কাছে তোমাদেরকে সমবেত করা হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (862,6,73,'তিনি আসমান আর যমীনকে সত্যিকারভাবে সৃষ্টি করেছেন (খেলা-তামাশার জন্য নয়)। আর যখনই তিনি বলবেন, (কিয়ামাত) ‘হও’, তখনই তা হয়ে যাবে, তাঁর কথাই প্রকৃত সত্য। যেদিন সিঙ্গায় ফুৎকার দেয়া হবে সেদিন কর্তৃত্ব থাকবে তাঁরই হাতে। অদৃশ্য ও দৃশ্য সম্বন্ধে ওয়াকেফহাল, তিনি হিকমাতওয়ালা, সবকিছুর ব্যাপারে তিনি সবিশেষ জ্ঞাত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (863,6,74,'আর (স্মরণ কর) যখন ইবরাহীম তার পিতা আজরকে বলেছিল, আপনি কি মূর্তিগুলোকে ‘ইলাহ’রূপে গ্রহণ করেছেন, আমি তো আপনাকে আর আপনার জাতিকে স্পষ্টভাবে পথভ্রষ্টতায় নিমজ্জিত দেখছি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (864,6,75,'এভাবে আমি ইবরাহীমকে আকাশ ও পৃথিবী রাজ্যের ব্যবস্থাপনা দেখিয়েছি যাতে সে নিশ্চিত বিশ্বাসীদের অন্তর্ভুক্ত হতে পারে।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (865,6,76,'রাতের আঁধার যখন তাকে আচ্ছন্ন করল তখন সে নক্ষত্র দেখতে পেল, (তখন) বলল, এটাই হচ্ছে আমার প্রতিপালক। কিন্তু যখন তা অস্তমিত হল, সে বলল, যা অস্তমিত হয়ে যায় তার প্রতি আমার কোন অনুরাগ নেই। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (866,6,77,'অতঃপর সে যখন চন্দ্রকে উজ্জ্বল হয়ে উঠতে দেখল তখন বলল, এটা হচ্ছে আমার প্রতিপালক। কিন্তু যখন তা অস্তমিত হল তখন সে বলল, আমার প্রতিপালক যদি আমাকে সঠিক পথের দিশা না দেন তাহলে আমি অবশ্যই পথভ্রষ্ট সম্প্রদায়ের অন্তর্ভুক্ত হয়ে যাব। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (867,6,78,'অতঃপর যখন সে সূর্যকে অতি উজ্জ্বল হয়ে উদিত হতে দেখল তখন বলল, এটাই হচ্ছে আমার প্রতিপালক, এটাই হচ্ছে সব থেকে বড়। অতঃপর যখন তা অস্তমিত হল তখন সে বলল, হে আমার জাতির লোকেরা! তোমরা যেগুলোকে (আল্লাহর) অংশীদার স্থির কর সেগুলোর সাথে আমার কোন সম্পর্ক নেই। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (868,6,79,'আমি একনিষ্ঠ হয়ে তাঁর দিকে আমার মুখ ফিরিয়ে নিচ্ছি যিনি আকাশমন্ডলী আর পৃথিবীকে সৃষ্টি করেছেন। আর আমি মুশরিকদের অন্তর্ভুক্ত নই। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (869,6,80,'তার জাতি তার সাথে বাদানুবাদ করল। সে বলল, তোমরা আল্লাহর ব্যাপারে আমার সাথে বাদানুবাদ করছ অথচ তিনি আমাকে সৎপথ দেখিয়েছেন। তোমরা যাদেরকে তার অংশীদার স্থির কর আমি তাদেরকে ভয় করি না। অবশ্য আল্লাহ যদি কিছু ইচ্ছে করেন (তবে সে কথা আলাদা)। প্রতিটি বস্তু সম্পর্কে আমার প্রতিপালকের জ্ঞান পরিব্যাপ্ত, তোমরা কি তা বুঝবে না? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (870,6,81,'তোমরা যাদেরকে (আল্লাহর) অংশীদার স্থির করেছ আমি তাদেরকে কীভাবে ভয় করতে পারি, যেখানে তোমরা আল্লাহর অংশীদার বানিয়ে নিতে ভয় কর না যে সম্পর্কে আল্লাহ কোন প্রমাণই নাযিল করেননি। নিরাপত্তা লাভের ব্যাপারে (এ) দু’টি দলের মধ্যে কোনটি বেশি হকদার? বল, যদি তোমাদের জানা থাকে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (871,6,82,'যারা ঈমান এনেছে আর যুলম (অর্থাৎ শিরক) দ্বারা তাদের ঈমানকে কলুষিত করেনি, নিরাপত্তা লাভ তারাই করবে আর তারাই হল সঠিক পথপ্রাপ্ত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (872,6,83,'এ হল আমার যুক্তি-প্রমাণ যা আমি ইবরাহীমকে দিয়েছিলাম তার কাওমের বিপক্ষে, আমি যাকে ইচ্ছে মর্যাদায় উন্নীত করি। তোমাদের প্রতিপালক নিশ্চয়ই হিকমাতওয়ালা,সর্বজ্ঞ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (873,6,84,'আমি তাকে  দান  করেছিলাম ইসহাক আর ইয়াকূব; তাদের প্রত্যেককে সৎ পথ দেখিয়েছিলাম, আর এর পূর্বে নূহকে সৎ পথ দেখিয়েছিলাম আর তার বংশধর থেকে দাঊদ, সুলাইমান, আইঊব, ইউসুফ, মূসা ও হারূনকে (সৎ পথ দেখিয়েছিলাম), সৎ কর্মশীলদের আমি এভাবেই পুরস্কৃত করে থাকি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (874,6,85,'আর যাকারিয়া, ইয়াহইয়া, ‘ঈসা ও ইলিয়াস- এরা সবাই সৎকর্মশীলদের অন্তর্ভুক্ত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (875,6,86,'আর ইসমাঈল, আল ইয়াসা‘আ, ইউনুস ও লূত- এদের প্রত্যেককে আমি বিশ্বজগতে জাতিসমূহের উপর শ্রেষ্ঠত্ব দান করেছিলাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (876,6,87,'তাদের পিতৃপুরুষ, বংশধর ও ভ্রাতৃবর্গ থেকে তাদেরকে বেছে নিয়েছি আর সত্য-সঠিক পথে পরিচালিত করেছি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (877,6,88,'এ হচ্ছে আল্লাহর হিদায়াত, তিনি তাঁর বান্দাহদেরকে থেকে যাকে ইচ্ছে হিদায়াত করেন, তারা যদি শিরক করত তবে তাদের সব কৃতকর্ম বিনষ্ট হয়ে যেত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (878,6,89,'এরা তারাই যাদেরকে আমি কিতাব, হিকমাত ও নবুওত দান করেছিলাম, এখন যদি তারা (অর্থাৎ বিধর্মীরা) এগুলোকে মেনে নিতে অস্বীকার করে তাহলে আমি এগুলোর ভার এমন সম্প্রদায়ের কাছে সোপর্দ করেছি যারা (অর্থাৎ মু’মিনরা) এগুলোর অস্বীকারকারী হবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (879,6,90,'ওরা হল তারা যাদেরকে আল্লাহ হিদায়াত দান করেছিলেন, তুমি তাদের পথ অনুসরণ কর; বল, এর জন্য (অর্থাৎ বাণী পৌঁছে দেয়ার জন্য) আমি তোমাদের কাছে কোন  পারিশ্রমিক  চাই না। এটা সারা দুনিয়ার মানুষের জন্য উপদেশ বাণী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (880,6,91,'তারা আল্লাহকে যথাযথ মর্যাদা দিতে পারেনি যখন তারা এ কথা বলেছে যে, আল্লাহ কোন মানুষের কাছে কোন কিছুই অবতীর্ণ করেননি। বল, তাহলে ঐ কিতাব কে অবতীর্ণ করেছিলেন যা নিয়ে এসেছিলেন মূসা, যা ছিল মানুষের জন্য আলোকবর্তিকা ও সঠিক পথের দিকদিশারী, কাগজের পৃষ্ঠায় যা তোমরা প্রকাশ কর আর বেশির ভাগই গোপন কর, যার সাহায্যে তোমাদেরকে জানিয়ে দেয়া হয়েছে যা তোমরাও জানতে না, তোমাদের বাপ-দাদারাও জানত না? বল, (মূসার প্রতি ঐ কিতাব) আল্লাহ্ই (নাযিল করেছিলেন), অতঃপর তাদেরকে তাদের নিরর্থক আলোচনায় মত্ত হয়ে থাকতে দাও। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (881,6,92,'আর [এখন যা মুহাম্মাদ (সা.)-এর প্রতি নাযিল করা হয়েছে] এ কিতাব বরকতে ভরপুর, তাদের কাছে যে কিতাব আছে তার সত্যতা প্রতিপাদনকারী, আর তা মক্কা ও তার চতুষ্পার্শবস্থ এলাকার লোকেদেরকে সতর্ক করার জন্য প্রেরিত। যারা আখেরাতে বিশ্বাস করে তারা এতে বিশ্বাস করে, আর তারা তাদের সলাতের হিফাযাত করে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (882,6,93,'তার থেকে বড় যালিম আর কে যে আল্লাহ সম্পর্কে মিথ্যে কথা রচনা করে অথবা বলে, আমার প্রতি ওয়াহী নাযিল হয়; যদিও তার কাছে কিছুই অবতীর্ণ হয় না। আর যে বলে : আল্লাহ যা নাযিল করেন আমি শীঘ্রই তার অনুরূপ নাযিল করব। হায়! যদি তুমি ঐ যালিমদেরকে দেখতে পেতে যখন তারা মৃত্যু যন্ত্রণায় ছটফট করতে থাকবে, আর ফেরেশতারা হাত বাড়িয়ে দিয়ে বলবে, তোমাদের জানগুলোকে বের করে দাও, আজ তোমাদেরকে অবমাননাকর আযাব দেয়া হবে যেহেতু তোমরা আল্লাহ সম্পর্কে এমন কথা বলতে যা প্রকৃত সত্য নয় আর তাঁর নিদর্শনগুলোর ব্যাপারে ঔদ্ধত্য প্রদর্শন করতে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (883,6,94,'(ক্বিয়ামাতের দিন আল্লাহ বলবেন) তোমরা আমার নিকট তেমনই নিঃসঙ্গ অবস্থায় হাজির হয়েছ যেমনভাবে আমি তোমাদেরকে প্রথমবার সৃষ্টি করেছিলাম, তোমাদেরকে যা (নি‘মাতরাজি) দান করেছিলাম তা তোমরা তোমাদের পেছনে ফেলে রেখে এসেছ, আর তোমাদের সাথে সেই সুপারিশকারীগণকেও দেখছি না যাদের সম্পর্কে তোমরা ধারণা করতে যে তোমাদের কার্য উদ্ধারের ব্যাপারে তাদের অংশ আছে। তোমাদের মধ্যেকার সম্পর্ক একেবারেই ছিন্ন হয়ে গেছে, আর তোমরা যে সব ধারণা করতে সে সব অকার্যকর প্রমাণিত হয়েছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (884,6,95,'আল্লাহই হচ্ছেন শস্য দানা ও বীজ বিদীর্ণকারী, তিনি মৃত থেকে জীবন্তকে বের করেন এবং জীবন্ত থেকে মৃতকে। এই হচ্ছেন আল্লাহ; সৎপথ থেকে তোমরা কোথায় চলে যাচ্ছ? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (885,6,96,'তিনি ঊষার উন্মেষ ঘটান, তিনি রাত সৃষ্টি করেছেন শান্তি ও আরামের জন্য, সূর্য ও চন্দ্র বানিয়েছেন গণনার জন্য। এসব মহাপরাক্রমশালী সর্বজ্ঞাতা কর্তৃক নির্ধারিত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (886,6,97,'তিনি তোমাদের জন্য নক্ষত্ররাজি সৃষ্টি করেছেন যাতে তোমরা সেগুলোর সাহায্যে জলে স্থলে অন্ধকারে পথের দিশা লাভ করতে পার। আমি আমার নিদর্শনগুলোকে জ্ঞানীদের জন্য বিশদভাবে বর্ণনা করে দিয়েছি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (887,6,98,'তিনি তোমাদেরকে একটি প্রাণ হতে সৃষ্টি করেছেন, তারপর প্রত্যেকের জন্য একটা অবস্থান স্থল আছে আর একটি আছে তাকে গচ্ছিত রাখার জায়গা। জ্ঞান-বুদ্ধি সম্পন্ন লোকেদের জন্য আমি আমার আয়াতগুলোকে বিশদভাবে বর্ণনা করে দিয়েছি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (888,6,99,'তিনিই আকাশ থেকে বৃষ্টি বর্ষণ করেন অতঃপর তা দ্বারা আমি সকল প্রকার উদ্ভিদ উদগত করি, অতঃপর তা থেকে সবুজ পাতা উদ্গত করি, অতঃপর তা থেকে ঘন সন্নিবিষ্ট শস্যদানা উৎপন্ন করি, খেজুর গাছের মোচা থেকে ঝুলন্ত কাঁদি নির্গত করি, আঙ্গুরের বাগান সৃষ্টি করি, আর সৃষ্টি করি জায়তুন ও ডালিম, সেগুলো একই রকম এবং বিভিন্ন রকমও। লক্ষ্য কর তার ফলের প্রতি যখন গাছে ফল আসে আর ফল পাকে। এসবের ভিতরে মু’মিন সম্প্রদায়ের জন্য অবশ্যই নিদর্শন আছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (889,6,100,'তারা জ্বীনকে আল্লাহর অংশীদার স্থির করে অথচ তাদেরকে তিনিই সৃষ্টি করেছেন, তারা না জেনে না বুঝে আল্লাহর জন্য পুত্র-কন্যা স্থির করে, তাদের এসব কথা হতে তিনি পবিত্র ও মহান। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (890,6,101,'তিনি আকাশমন্ডলী ও যমীনের উদ্ভাবক, কীভাবে তাঁর সন্তান হতে পারে যেহেতু তাঁর কোন সঙ্গীণীই নেই, সব কিছু তো তিনিই সৃষ্টি করেছেন, আর প্রতিটি জিনিস সম্পর্কে তিনি পূর্ণ জ্ঞান রাখেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (891,6,102,'এই হলেন তোমাদের প্রতিপালক, তিনি ছাড়া সত্যিকারের কোন ইলাহ নেই, সব কিছুর স্রষ্টা; কাজেই তোমরা তাঁরই ‘ইবাদাত কর, তিনি সমস্ত বিষয়ের র্কমবিধায়ক। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (892,6,103,'দৃষ্টি তার নাগাল পায় না বরং তিনিই সকল দৃষ্টি নাগালে রাখেন, তিনি অতিশয় সূক্ষ্ণদর্শী, সব বিষয়ে ওয়াকিফহাল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (893,6,104,'তোমাদের কাছে তোমাদের প্রতিপালকের নিকট থেকে (অন্তরের) আলো এসে পৌঁছেছে, যে লোক (এই আলো দিয়ে) দেখবে তাতে তার নিজেরই কল্যাণ হবে, আর যে অন্ধ থাকবে, তার অকল্যাণ তার ঘাড়েই পড়বে। (বাণী পৌঁছে দেয়ার দায়িত্ব দিয়ে আমাকে পাঠানো হয়েছে) আমি তোমাদেরকে পাহারা দেয়ার জন্য দায়িত্বপাপ্ত হইনি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (894,6,105,'এভাবেই আমি নিদর্শনগুলোকে বার বার নানাভাবে বর্ননা করি। যার ফলে তারা (অর্থাৎ অবিশ্বাসীরা) বলে, তুমি (এসব কথা অন্যের কাছ থেকে) শিখে নিয়েছ, বস্তুত আমি জ্ঞানী লোকদের জন্য তা সুস্পষ্টভাবে বিবৃত করি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (895,6,106,'তুমি তোমার প্রতিপালকের নিকট থেকে যে ওয়াহী পাও তার অনুসরণ কর, তিনি ছাড়া সত্যিকারের কোন ইলাহ নেই, তুমি মুশরিকদের দিক থেকে মুখ ফিরিয়ে নাও। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (896,6,107,'আল্লাহ ইচ্ছে করলে তারা আল্লাহর অংশী স্থির করত না, (আসল শিক্ষা তাদেরকে জানিয়ে দেয়া হয়েছে, এখন তারা মানুক বা না মানুক) আমি তোমাকে তাদের উপর পাহারাদার পাঠায়নি (জোর করে আল্লাহর ‘ইবাদাত করিয়ে নেয়ার জন্য), আর তুমি তাদের পক্ষ থেকে কর্মসম্পাদনকারীও নও। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (897,6,108,'(ওহে মুমিনগণ!) আল্লাহকে বাদ দিয়ে যাদেরকে তারা ডাকে তোমরা তাদেরকে গালি দিও না, কেননা তারা তাদের অজ্ঞতাপ্রসূত শত্রুতার বশবর্তী হয়ে আল্লাহকে গালি দেবে। আর এভাবেই আমি প্রত্যেক জাতির জন্য তাদের কার্যকলাপকে তাদের দৃষ্টিতে চাকচিক্যময় করে দিয়েছি, অতঃপর তাদের প্রত্যাবর্তন (ঘটবে) তাদের প্রতিপালকের নিকট, তখন তিনি তাদেরকে জানিয়ে দিবেন যা কিছু তারা করতো। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (898,6,109,'তারা আল্লাহর নামে কঠিন শপথ করে বলে- তাদের নিকট যদি কোন নিদর্শন আসত তবে তারা অবশ্যই তাঁর প্রতি ঈমান আনত। বল, নিদর্শন (আনার ব্যাপারটি) হল আল্লাহর ইখতিয়ারভুক্ত। নিদর্শন আসলেও তারা যে ঈমান আনবে না, এ কথা কীভাবে তোমাদেরকে বুঝানো যাবে? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (899,6,110,'আর আমিও এদের অন্তর ও দৃষ্টিসমূহকে (অন্য দিকে) ফিরিয়ে দেব, আর তাদেরকে তাদের অবাধ্যতার ঘূর্ণিপাকে অন্ধের মত ঘুরে মরার সুযোগ দেব। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (900,6,111,'আমি যদি তাদের কাছে ফেরেশতা অবতীর্ণ করতাম আর মৃতরা তাদের সাথে কথা বলত আর আমি তাদের সামনে যাবতীয় বস্তু হাজির করে দিতাম তবুও আল্লাহর ইচ্ছে ব্যতীত তারা ঈমান আনত না, মূলতঃ তাদের অধিকাংশই অজ্ঞতাপূর্ণ কাজ করে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (901,6,112,'এভাবে আমি প্রত্যেক নাবীর জন্য মানুষ আর জ্বীন শয়তানদের মধ্য হতে শত্রু বানিয়ে দিয়েছি, প্রতারণা করার উদ্দেশে তারা একে অপরের কাছে চিত্তাকর্ষক কথাবার্তা বলে। তোমার প্রতিপালক ইচ্ছে করলে তারা তা করত না, কাজেই তাদেরকে আর তাদের মিথ্যে চর্চাকে উপেক্ষা করে চল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (902,6,113,'তার দিকে (অর্থাৎ চিত্তাকর্ষক প্রতারণার দিকে) সে সব লোকের অন্তর আকৃষ্ট হতে দাও যারা আখেরাতের প্রতি ঈমান আনে না, আর তাতেই তাদেরকে সন্তুষ্ট থাকতে দাও আর যে পাপকাজ তারা করতে চায় তা তাদেরকে করতে দাও। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (903,6,114,'বল, আমি কি আল্লাহ ছাড়া অন্যকে বিচারক মানব, যখন তিনি সেই (সত্তা) যিনি তোমাদের নিকট কিতাব নাযিল করেছেন, যা বিশদভাবে বিবৃত। আমি যাদেরকে (পূর্বে) কিতাব দিয়েছিলাম তারা জানে যে, তা সত্যতা সহকারে তোমার প্রতিপালকের নিকট হতে অবতীর্ণ হয়েছে। কাজেই কিছুতেই তুমি সন্দেহ পোষণকারীদের মধ্যে শামিল হয়ো না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (904,6,115,'সত্যতা ও ইনসাফের দিক দিয়ে তোমার প্রতিপালকের বাণী পরিপূর্ণ। তাঁর বাণী পরিবর্তন করার কেউ নেই। আর তিনি হলেন সর্বশ্রোতা ও সর্বজ্ঞ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (905,6,116,'তুমি যদি পৃথিবীর অধিকাংশ লোকের অনুসরণ কর তাহলে তারা তোমাকে আল্লাহর পথ হতে বিচ্যুত করে ফেলবে, তারা তো কেবল আন্দাজ-অনুমানের অনুসরণ করে চলে, তারা মিথ্যাচার ছাড়া কিছু করে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (906,6,117,'তোমার রব্ব খুব ভাল করেই জানেন কে তাঁর পথ থেকে ভ্রষ্ট হয়ে গেছে আর তিনি হিদায়াতপ্রাপ্তদের সম্পর্কেও সর্বাধিক অবহিত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (907,6,118,'কাজেই যে পশু যবেহ করার সময় আল্লাহর নাম নেয়া হয়েছে তা তোমরা খাও যদি তাঁর নিদর্শনাবলীতে তোমরা বিশ্বাসী হয়ে থাক। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (908,6,119,'তোমাদের কী হয়েছে যে, যাতে আল্লাহর নাম নেয়া হয়েছে তা তোমরা খাবে না? তোমাদের জন্য যা হারাম করা হয়েছে তা তোমাদের জন্য বিশদভাবে বাতলে দেয়া হয়েছে, তবে যদি তোমরা নিরুপায় হও (তবে ততটুকু নিষিদ্ধ বস্তু খেতে পার যাতে প্রাণে বাঁচতে পার), কিন্তু অনেক লোকই অজ্ঞানতাবশতঃ তাদের খেয়াল খুশী দ্বারা অবশ্যই (অন্যদেরকে) পথভ্রষ্ট করে, তোমার প্রতিপালক সীমালঙ্ঘনকারীদের সম্পর্কে সবচেয়ে বেশি অবগত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (909,6,120,'তোমরা প্রকাশ্য ও গোপন পাপ বর্জন কর, যারা পাপ অর্জন করে তারা তাদের অর্জনের যথোচিত প্রতিফল পাবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (910,6,121,'যাতে (যবহ করার সময়) আল্লাহর নাম নেয়া হয়নি তা তোমরা মোটেই খাবে না, তা হচ্ছে পাপাচার, শায়ত্বনেরা তাদের বন্ধুদেরকে তোমাদের সঙ্গে তর্ক-ঝগড়া করার জন্য প্ররোচিত করে; যদি তোমরা তাদের কথা মান্য করে চল তাহলে তোমরা অবশ্যই মুশরিক হয়ে যাবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (911,6,122,'যে ব্যক্তি মৃত ছিল, তাকে আমি জীবিত করলাম, তার জন্য আলোর ব্যবস্থা করলাম যার সাহায্যে সে মানুষের মাঝে চলাফেরা করে, সে কি তার মত যে অন্ধকারে নিমজ্জিত, যাত্থেকে সে কক্ষনো বেরিয়ে আসতে পারবে না। এটা এজন্য যে, কাফিররা যা করছে তা তাদের জন্য চাকচিক্যময় করে দেয়া হয়েছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (912,6,123,'এভাবে আমি প্রত্যেক জনপদে সেখানকার অপরাধী প্রধানদেরকে চক্রান্তজাল বিস্তার করার সুযোগ দিয়েছি, কিন্তু এ চক্রান্তের ফাঁদে তারা নিজেরাই পতিত হয়, কিন্তু সেটা তারা উপলব্ধিই করতে পারে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (913,6,124,'যখন তাদের কাছে কোন নিদর্শন আসে তখন তারা বলে, ‘আমরা কক্ষনো বিশ্বাস করব না যতক্ষণ না আমাদেরকে তা দেয়া হয় যা আল্লাহর রসূলগণকে দেয়া হয়েছিল। (নির্বোধেরা এ আবদার করলেও) আল্লাহ খুব ভালভাবেই জানেন তাঁর রিসালাতের দায়িত্ব কোথায় দিতে হবে, অপরাধীরা শীঘ্রই তাদের চক্রান্তের প্রতিফল হিসেবে আল্লাহর পক্ষ হতে লাঞ্ছনা ও কঠিন শাস্তির সম্মুখীন হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (914,6,125,'আল্লাহ যাকে সৎপথ দেখাতে চান, তার অন্তরকে ইসলামের জন্য খুলে দেন, আর যাকে পথভ্রষ্ট করতে চান তার অন্তরকে সংকীর্ণ সংকুচিত করে দেন, (তার জন্য ইসলাম মান্য করা এমনি কঠিন) যেন সে আকাশে আরোহণ করছে। যারা ঈমান আনে না তাদের উপর আল্লাহ এভাবে লাঞ্ছনা চাপিয়ে দেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (915,6,126,'অথচ (ইসলামের) এ পথই তোমার প্রতিপালকের সরল-সঠিক পথ, যারা নাসীহাত গ্রহণ করে আমি তাদের জন্য নিদর্শনাবলী বিশদভাবে বিবৃত করে দিয়েছি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (916,6,127,'তাদের জন্য তাদের প্রতিপালকের কাছে আছে শান্তিধাম, তিনিই তাদের পৃষ্ঠপোষক এজন্য যে তারা (সঠিক) ‘আমাল করেছিল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (917,6,128,'যেদিন তাদের সবাইকে একত্রিত করা হবে, (সেদিন আল্লাহ বলবেন) হে জ্বিন সমাজ! তোমরা মানব সমাজের উপর খুব বাড়াবাড়ি করেছ। মানব সমাজের মধ্য থেকে তাদের বন্ধুরা বলবে, হে আমাদের প্রতিপালক! আমরা পরস্পরে পরস্পরের নিকট হতে লাভবান হয়েছি আর আমরা আমাদের নির্ধারিত সময়ে পৌঁছে গেছি যা তুমি আমাদের জন্য নির্ধারিত করেছিলে। আল্লাহ বলবেন, জাহান্নামই হল তোমাদের বাসস্থান, তার মধ্যেই হবে তোমাদের চিরবাস, তবে আল্লাহ যদি অন্যরূপ ইচ্ছে করেন (তবে তাই হবে)। তোমার প্রতিপালক কুশলী এবং সর্বজ্ঞ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (918,6,129,'এভাবেই আমি (পরকালে) যালিমদেরকে পরস্পরের সঙ্গী বানিয়ে দেব সেই উপার্জনের বিনিময়ে যা তারা (দুনিয়াতে পরস্পরে এক সঙ্গে মিলে) করছিল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (919,6,130,'(আল্লাহ জিজ্ঞেস করবেন) হে জ্বিন ও মানব সমাজ! তোমাদের কাছে তোমাদের মধ্য হতে কি রসূলগণ আসেননি যারা তোমাদের কাছে আমার আয়াত বর্ণনা করত আর এ দিনের সাথে যে সাক্ষাৎ ঘটবে সে ব্যাপারে তোমাদেরকে ভয় প্রদর্শন করত? তারা বলবে, আমরা আমাদের বিরুদ্ধে সাক্ষ্য দিচ্ছি; মূলতঃ এ দুনিয়ার জীবন তাদেরকে প্রতারিত করেছে, তারা নিজেদের বিরুদ্ধে সাক্ষ্য দেবে যে তারা কাফির ছিল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (920,6,131,'এটা এজন্য যে আল্লাহ কোন জনপদকে ধ্বংস করেন না অন্যায়ভাবে এবং (সত্যপথ কোনটি আর ভুলপথ কোনটি সে সম্পর্কে) যখন তারা থাকে অনবহিত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (921,6,132,'প্রত্যেককে তার ‘আমাল অনুযায়ী মর্যাদা দেয়া হবে আর তারা যা করে সে ব্যাপারে তোমার প্রতিপালক বে-খবর নন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (922,6,133,'তোমার প্রতিপালক অমুখাপেক্ষী, অত্যন্ত দয়াশীল। তিনি ইচ্ছে করলে তোমাদেরকে অপসারিত করবেন এবং যাকে ইচ্ছে তোমাদের স্থলাভিষিক্ত করবেন যেমন তিনি তোমাদেরকে অন্য সম্প্রদায়ের বংশ হতে সৃষ্টি করেছেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (923,6,134,'যা ঘটবে বলে তোমাদেরকে ওয়াদা দেয়া হয়েছে তা ঘটবেই, তা ব্যর্থ করে দেয়ার ক্ষমতা তোমাদের নেই। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (924,6,135,'বল, ‘হে আমার সম্প্রদায়! তোমরা তোমাদের (ধর্মীয়) অবস্থানে থেকে যা করছ করে যাও, আমিও আমার কাজ করছি, তোমরা শীঘ্রই জানতে পারবে কল্যাণময় পরিণতি কার হবে। যালিমগণ কখনও সফলকাম হয় না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (925,6,136,'আল্লাহ যে শস্য ও গবাদি পশু সৃষ্টি করেছেন তাত্থেকে তারা আল্লাহর জন্য একটা অংশ নির্দিষ্ট করে আর তারা তাদের ধারণামত বলে এ অংশ আল্লাহর জন্য, আর এ অংশ আমাদের দেবদেবীদের জন্য। যে অংশ তাদের দেবদেবীদের জন্য তা আল্লাহর নিকট পৌঁছে না, কিন্তু যে অংশ আল্লাহর তা তাদের দেবদেবীদের নিকট পৌঁছে। কতই না নিকৃষ্ট এই লোকদের ফায়সালা! ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (926,6,137,'আর এভাবে তাদের দেবদেবীরা বহু মুশরিকদের চোখে নিজেদের সন্তান হত্যাকে আকর্ষণীয় করে দিয়েছে তাদেরকে ধ্বংস করার জন্য এবং তাদের দ্বীনকে সন্দেহপূর্ণ করার জন্য। আল্লাহ যদি ইচ্ছে করতেন তবে তারা তা করতে পারত না, কাজেই তাদেরকে ছেড়ে দাও, তারা তাদের মিথ্যে নিয়ে মগ্ন থাকুক। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (927,6,138,'তারা তাদের ধারণা অনুসারে বলে, এই এই গবাদি পশু ও ফসল সুরক্ষিত। আমরা যার জন্য ইচ্ছে করব সে ছাড়া কেউ এগুলো খেতে পারবে না। এ সব তাদের কল্পিত। কতক গবাদি পশুর পিঠে চড়া নিষিদ্ধ করা হয়েছে, কতক গবাদি পশু যবহ করার সময় তারা আল্লাহর নাম নেয় না, (এসব বাধা-নিষেধ) আল্লাহর প্রতি মিথ্যে রচনা স্বরূপ করে থাকে। এসব মিথ্যে রচনার প্রতিফল তিনি শীঘ্রই তাদেরকে প্রদান করবেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (928,6,139,'তারা আরো বলে, এসব গবাদি পশুর গর্ভে যা আছে তা আমাদের পুরুষদের জন্য নির্দিষ্ট, আর আমাদের স্ত্রীলোকদের জন্য নিষিদ্ধ, কিন্তু তা (অর্থাৎ গর্ভস্থিত বাচ্চা) যদি মৃত হয় তবে সকলের তাতে অংশ আছে। তাদের এই মিথ্যে রচনার প্রতিফল অচিরেই তিনি তাদেরকে দেবেন, তিনি বড়ই হিকমাতওয়ালা, সর্বজ্ঞ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (929,6,140,'যারা মূর্খের মত না জেনে তাদের সন্তানদের হত্যা করেছে আর আল্লাহর নামে মিথ্যে কথা বানিয়ে নেয়ার মাধ্যমে আল্লাহর দেয়া জীবিকাকে হারাম করে নিয়েছে, তারা নিশ্চিতরূপে ক্ষতিগ্রস্ত হয়েছে। তারা পথভ্রষ্ট হয়ে গেছে আর তারা কস্মিনকালেও হিদায়াতপ্রাপ্ত ছিল না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (930,6,141,'আর তিনি (আল্লাহ) যিনি লতাগুল্ম বিশিষ্ট আর লতা-বিশিষ্ট নয় এমন উদ্যানরাজি, খেজুর গাছ ও বিভিন্ন স্বাদের খাদ্যশস্য, একই ধরনের ও আলাদা ধরনের যায়তুন ও ডালিম সৃষ্টি করেছেন। যখন ফল ধরে তখন ফল খাও, আর ফসল তোলার দিন (নির্ধারিত ওশর ও অনির্ধারিত দানের মাধ্যমে) হক আদায় কর, অপচয় করো না, নিশ্চয় তিনি অপচয়কারীদের পছন্দ করেন না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (931,6,142,'গবাদি পশুর মধ্যে কতক আছে ভারবাহী আর কতক আছে গোশত ও আচ্ছাদনের সামগ্রী দানকারী, আল্লাহ তোমাদেরকে যে রিয্ক্ব দিয়েছেন তাত্থেকে ভক্ষণ কর আর শায়ত্বনের পদাঙ্ক অনুসরণ করো না, সে তোমাদের খোলাখুলি দুশমন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (932,6,143,'(নর-মাদী চার) জোড়ায় আট প্রকার, মেষের দু’টি, ছাগলের দু’টি। বল, তিনি কি নর দু’টি হারাম করেছেন, না মাদী দু’টি কিংবা মাদী দু’টির গর্ভে যা আছে তা? এ সম্পর্কিত জ্ঞানের ভিত্তিতে জবাব দাও যদি তোমরা সত্যবাদী হয়ে থাক। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (933,6,144,'আর উটের দু’টি, আর গরুর দু’টি। বল, এদের নর দু’টি কি তিনি হারাম করেছেন, না মাদী দু’টি অথবা মাদী দু’টির গর্ভে যা আছে তা? তোমরা কি তখন উপস্থিত ছিলে যখন আল্লাহ এ রকম নির্দেশ দিয়েছিলেন? যে ব্যক্তি মানুষদেরকে পথভ্রষ্ট করার উদ্দেশ্যে কোন রকম ‘ইলম ছাড়াই আল্লাহর সম্বন্ধে মিথ্যে রচনা করে তার থেকে বড় যালিম আর কে হতে পারে? বস্তুতঃ আল্লাহ যালিম সম্প্রদায়কে হিদায়াত দান করেন না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (934,6,145,'বল, আমার প্রতি যে ওয়াহী করা হয়েছে তাতে মানুষ যা আহার করে তার কিছুই নিষিদ্ধ পাই না মৃত, প্রবহমান রক্ত ও শূকরের মাংস ছাড়া। কারণ তা অপবিত্র অথবা আল্লাহ ছাড়া অন্যের নামে যবহ করা ফাসিকী কাজ। তবে যে ব্যক্তি নিরুপায় হয়ে পড়ে কিন্তু সে নাফরমান ও সীমালঙ্ঘনকারী নয়, তাহলে তোমার প্রতিপালক বড়ই ক্ষমাশীল, পরম দয়ালু। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (935,6,146,'যারা ইয়াহূদী হয়েছে তাদের জন্য আমি যাবতীয় তীক্ষ্ণধার নখযুক্ত পশু হারাম করেছিলাম আর তাদের জন্য গরু-ছাগলের চর্বিও হারাম করেছিলাম, এগুলোর পিঠের কিংবা নাড়িভুড়ির বা হাড়ের সাথে লেগে থাকা চর্বি ছাড়া। তাদের অবাধ্যতার শাস্তি এভাবে তাদেরকে দিয়েছিলাম, আমি অবশ্যই সত্য কথা বলছি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (936,6,147,'অতঃপর যদি তারা তোমাকে মিথ্যে মনে করে তবে তাদেরকে বল, তোমাদের প্রতিপালক প্রশস্ত দয়ার মালিক, (কিন্তু তাওবা না করে অপরাধীই থেকে গেলে) তবে অপরাধী সম্প্রদায়ের উপর হতে তাঁর শাস্তি রদ হয় না।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (937,6,148,'যারা শিরক করেছে তারা বলবে, আল্লাহ ইচ্ছে করলে আমরা শিরক করতাম না, আর আমাদের পিতৃপুরুষরাও করত না, আর কোন কিছুই (আমাদের উপর) হারাম করে নিতাম না, এভাবে তাদের আগের লোকেরাও সত্যকে মিথ্যে গণ্য করেছিল, অবশেষে তারা আমার শাস্তি আস্বাদন করেছিল। বল, তোমাদের কাছে কি প্রকৃত জ্ঞান আছে, থাকলে তা আমাদের কাছে পেশ কর, তোমরা তো কেবল ধারণা-অনুমানের অনুসরণ করে চলেছ, তোমরা তো মিথ্যাচারই করে যাচ্ছ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (938,6,149,'চূড়ান্ত সত্য-নির্ভর প্রমাণ তো আল্লাহর কাছে আছে, আল্লাহ যদি চাইতেন তাহলে তিনি অবশ্যই তোমাদের সকলকে সত্যপথে পরিচালিত করতেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (939,6,150,'বল, আল্লাহ যে এটা হারাম করেছেন সে ব্যাপারে যারা সাক্ষ্য দেবে তাদেরকে হাজির কর। তারা সাক্ষ্য দিলেও তুমি তাদের সাথে সাক্ষ্য দিও না। যারা আমার আয়াতগুলোকে অমান্য করে, আর যারা আখেরাতে বিশ্বাস করে না আর তাদের প্রতিপালকের সমকক্ষ দাঁড় করায় তুমি কক্ষনো তাদের খেয়ালখুশির অনুসরণ করো না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (940,6,151,'বল, ‘এসো, তোমাদের প্রতিপালক তোমাদের জন্য যা নিষিদ্ধ করেছেন তা পড়ে শোনাই, তা এই যে, তাঁর সাথে কোন কিছুকে শরীক করো না, পিতা-মাতার সঙ্গে সদ্ব্যবহার কর, দরিদ্রতার ভয়ে তোমাদের সন্তানদের হত্যা করো না, আমিই তোমাদেরকে আর তাদেরকে জীবিকা দিয়ে থাকি, প্রকাশ্য বা গোপন কোন অশ্লীলতার কাছেও যেয়ো না, আল্লাহ যে প্রাণ হরণ করা হারাম করেছেন তা ন্যায় সঙ্গত কারণ ছাড়া হত্যা করো না। এ সম্পর্কে তিনি তোমাদেরকে নির্দেশ দিচ্ছেন যাতে তোমরা চিন্তা- ভাবনা করে কাজ কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (941,6,152,'(ইয়াতীমরা) বয়ঃপ্রাপ্ত না হওয়া পর্যন্ত কল্যাণ সাধনের উদ্দেশ্য ছাড়া ইয়াতীমদের সম্পত্তির নিকটবর্তী হয়ো না। পরিমাপ ও ওজন ন্যায়সঙ্গতভাবে পূর্ণ কর, আমি কোন ব্যক্তির উপর সাধ্যের অতিরিক্ত দায়িত্ব চাপিয়ে দেই না, যখন কথা বলবে তখন ইনসাফপূর্ণ কথা বলবে- নিকটাত্মীয়দের সম্পর্কে হলেও, আর আল্লাহর সঙ্গে কৃত ওয়াদা পূর্ণ কর। এসব ব্যাপারে তিনি নির্দেশ দিচ্ছেন যাতে তোমরা উপদেশ গ্রহণ কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (942,6,153,'আর এটাই আমার সঠিক সরল পথ, কাজেই তোমরা তার অনুসরণ কর, আর নানান পথের অনুসরণ করো না, করলে তা তোমাদেরকে তাঁর পথ থেকে বিচ্ছিন্ন করে ফেলবে। এভাবে তিনি তোমাদেরকে নির্দেশ দিচ্ছেন যাতে তোমরা তাঁকে ভয় করে যাবতীয় পাপ থেকে বেঁচে চলতে পার। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (943,6,154,'অতঃপর (তোমরা অবগত হও যে) আমি মূসাকে কিতাব দিয়েছিলাম যার মাধ্যমে আমি আমার নি‘য়ামাত পূর্ণ করে দিয়েছিলাম তাদের জন্য যারা উত্তম কাজ করে, তাতে ছিল যাবতীয় বিষয়ের বিশদ বিবরণ, (তা ছিল) সঠিক পথের দিশারী ও দয়া স্বরূপ যাতে তারা তাদের প্রতিপালকের সঙ্গে সাক্ষাতের ব্যাপারে বিশ্বাস স্থাপন করে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (944,6,155,'আর এ কিতাব যা আমি অবতীর্ণ করলাম তা বরকতময়, কাজেই তা মান্য কর, আর আল্লাহকে ভয় করে চল, যাতে তোমাদের উপর দয়া বর্ষিত হয়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (945,6,156,'যাতে তারা না বলতে পারে যে কিতাব তো শুধু আমাদের পূর্বের দু’দল (ইয়াহূদী ও খ্রীস্টান) এর উপর অবতীর্ণ হয়েছিল আর আমরা জানতাম না ওরা কী পড়ত আর পড়াত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (946,6,157,'অথবা তোমরা না বলতে পার যে আমাদের উপর যদি কিতাব অবতীর্ণ হত তাহলে আমরা তাদের চেয়ে বেশি হিদায়াতপ্রাপ্ত হতাম। তাই এখন তোমাদের কাছে তোমাদের প্রতিপালকের পক্ষ থেকে সুস্পষ্ট প্রমাণ, হিদায়াত ও রহমত এসেছে। অতঃপর যে ব্যক্তি আল্লাহর (এ সব) আয়াতসমূহকে মিথ্যে মনে ক’রে তাত্থেকে মুখ ফিরিয়ে নেবে তার চেয়ে বড় যালিম আর কে আছে? আমার আয়াতসমূহ থেকে যারা মুখ ফিরিয়ে নেয়, তাদেরকে অচিরেই আমি তাদের মুখ ফিরিয়ে নেয়ার কারণে নিকৃষ্টতম শাস্তি প্রদান করব। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (947,6,158,'তারা কি এই অপেক্ষায় আছে যে, তাদের কাছে ফেরেশতারা আসবে অথবা তোমার প্রতিপালক (স্বয়ং) আসবেন কিংবা তোমার রবেবর কিছু নিদর্শন আসবে (তখন তারা ঈমান আনবে)? যে দিন তোমার রবেবর কতক নিদর্শন এসে যাবে সে দিন ঐ ব্যক্তির ঈমান কোন সুফল দিবে না যে পূর্বে ঈমান আনেনি বা ঈমানের মাধ্যমে কল্যাণ অর্জন করেনি। বল, তোমরা অপেক্ষা কর (তাহলে দেখতে পাবে তোমাদের কুফরীর পরিণাম কী দাঁড়ায়), আমরাও অপেক্ষায় থাকলাম (আমাদের পুরস্কার প্রাপ্তি ও তোমাদের পরিণতি দেখার জন্য)। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (948,6,159,'যারা নিজেদের (পূর্ণ পরিণত) দ্বীনকে খন্ডে খন্ডে বিভক্ত করে নিয়েছে আর (আপন আপন অংশ নিয়ে) দলে দলে ভাগ হয়ে গেছে তাদের কোন কাজের সাথে তোমার কোন সম্পর্ক নেই। তাদের ব্যাপারটি পুরোপুরি আল্লাহর ইখতিয়ারভুক্ত। (সময় হলেই) তিনি তাদেরকে জানিয়ে  দেবেন  তাদের কৃতকর্ম সম্পর্কে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (949,6,160,'যে ব্যক্তি সৎকর্ম করবে তার জন্য আছে দশ গুণ পুরস্কার, আর যে ব্যক্তি অসৎকাজ করবে তাকে শুধু কৃতকর্মের তুল্য প্রতিফল দেয়া হবে, তাদের উপর অত্যাচার করা হবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (950,6,161,'বল, আমাকে আমার রব্ব সরল সঠিক পথে পরিচালিত করেছেন (যা) সুপ্রতিষ্ঠিত দ্বীন, একনিষ্ঠ ইবরাহীমের পথ। সে মুশরিকদের অন্তর্ভুক্ত ছিল না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (951,6,162,'বল, আমার নামায, আমার যাবতীয় ‘ইবাদাত, আমার জীবন, আমার মরণ (সব কিছুই) বিশ্বজগতের প্রতিপালক আল্লাহর জন্যই (নিবেদিত)। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (952,6,163,'তাঁর কোন শরীক নেই, আমাকে এরই নির্দেশ দেয়া হয়েছে আর আমিই সর্বপ্রথম আত্মসমর্পণকারী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (953,6,164,'আমি কি আল্লাহকে ছেড়ে অন্য প্রতিপালক তালাশ করব? (অথচ প্রকৃতপক্ষে) তিনিই সব কিছুর প্রতিপালক। প্রত্যেক ব্যক্তি যা অর্জন করে তার জন্য সে নিজেই দায়ী হবে। কোন ভারবহনকারীই অন্যের গুনাহের ভার বহন করবে না। অবশেষে তোমাদের প্রত্যাবর্তন স্থল তোমাদের প্রতিপালকের নিকটেই, তখন তিনি তোমাদেরকে জানিয়ে দেবেন যে সকল বিষয়ে তোমরা মতভেদে লিপ্ত ছিলে (সে সব বিষয়ে প্রকৃত সত্য কোনটি)। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (954,6,165,'তিনিই তোমাদেরকে পৃথিবীতে পরস্পরের স্থলাভিষিক্ত বানিয়েছেন, মর্যাদায় তোমাদের কতককে কতকের উপরে স্থান দিয়েছেন, আমি তোমাদেরকে যা দিয়েছি ওগুলোর মাধ্যমে তোমাদেরকে পরীক্ষা করার জন্য, তোমার রবব তো শাস্তি দানে দ্রুত (ব্যবস্থা গ্রহণ করেন) আর তিনি অবশ্যই বড়ই ক্ষমাশীল, পরম দয়ালু। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (955,7,1,'আলিফ, লাম, মীম, সাদ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (956,7,2,'এটি একটি কিতাব যা তোমার উপর নাযিল করা হয়েছে, এ ব্যাপারে তোমার অন্তরে যেন কোন প্রকার কুণ্ঠাবোধ না হয়, (এটা নাযিল করা হয়েছে অমান্যকারীদেরকে) এর দ্বারা ভয় প্রদর্শনের জন্য এবং মু’মিনদেরকে উপদেশ প্রদানের জন্য। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (957,7,3,'তোমাদের প্রতিপালকের নিকট হতে তোমাদের প্রতি যা অবতীর্ণ হয়েছে তোমরা তা মান্য করে চল, তাঁকে ছাড়া (অন্যদের) অভিভাবক মান্য করো না, তোমরা খুব সামান্য উপদেশই গ্রহণ কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (958,7,4,'আমি কত জনপদকে ধ্বংস করে দিয়েছি। আমার শাস্তি তাদের নিকট এসেছিল হঠাৎ রাত্রিবেলা কিংবা দুপুর বেলা তারা যখন বিশ্রাম নিচ্ছিল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (959,7,5,'আমার শাস্তি যখন তাদের উপর এসেছিল তখন এ কথা বলা ছাড়া তারা আর কোন ধ্বনি উচ্চারণ করতে পারেনি যে, ‘‘অবশ্যই আমরা যালিম ছিলাম’’। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (960,7,6,'অতঃপর যাদের নিকট রসূল পাঠানো হয়েছিল আমি অবশ্যই তাদেরকে জিজ্ঞেস করব আর রসূলগণকেও (আল্লাহর বাণী পৌঁছে দেয়া সম্পর্কে) অবশ্যই জিজ্ঞেস করব। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (961,7,7,'অতঃপর পরিপূর্ণ জ্ঞানের ভিত্তিতে তাদের নিকট তাদের সমস্ত কাহিনী অবশ্যই জানিয়ে দেব, কেননা আমি তো মোটেই অনুপস্থিত ছিলাম না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (962,7,8,'সেদিনের ওজন হবে ঠিক ঠিক। ফলে যাদের পাল্লা ভারী হবে তারা সফলকাম হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (963,7,9,'যাদের পাল্লা হালকা হবে তারা হল যারা নিজেদেরকে ক্ষতিগ্রস্ত করেছে, কারণ তারা আমার নিদর্শনসমূহকে প্রত্যাখ্যান করেছিল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (964,7,10,'আমি তোমাদেরকে যমীনে প্রতিষ্ঠিত করেছি; আর সেখানে তোমাদের জন্য জীবিকার ব্যবস্থা করেছি তোমরা খুব সামান্যই কৃতজ্ঞতা প্রকাশ কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (965,7,11,'আমি তো তোমাদেরকে সৃষ্টি করেছি, অতঃপর তোমাদের আকৃতি দিয়েছি, অতঃপর ফেরেশতাদের নির্দেশ দিলাম আদামকে সাজদাহ করার জন্য। তখন ইবলিস ছাড়া সবাই সিজদা করল। সে সিজদাকারীদের অন্তর্ভুক্ত হল না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (966,7,12,'(আল্লাহ) বললেন, ‘আমি নির্দেশ দেয়ার পরেও কিসে তোকে সাজদাহ থেকে নিবৃত্ত রাখল?’ সে বলল, ‘আমি তার চেয়ে উত্তম, আমাকে সৃষ্টি করেছ আগুন থেকে আর তাকে সৃষ্টি করেছ কাদা থেকে।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (967,7,13,'তিনি বললেন, ‘নেমে যা এখান থেকে, এর ভিতরে থেকে অহঙ্কার করবে তা হতে পারে না, অতএব বেরিয়ে যা, অধমদের মাঝে তোর স্থান।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (968,7,14,'সে বলল, ‘তাহলে যেদিন সবাই (দুনিয়া ছেড়ে) উঠবে সেদিন পর্যন্ত আমাকে সময় দাও।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (969,7,15,'তিনি বললেন, নিশ্চয়ই তুই নিকৃষ্টদের অন্তর্ভুক্ত ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (970,7,16,'সে বলল, যেহেতু তার কারণেই (পথ থেকে) আমাকে ছুঁড়ে ফেলে দিয়েছ, কাজেই আমি অবশ্যই তোমার সরল পথে মানুষদের জন্য ওঁৎ পেতে থাকব। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (971,7,17,'তারপর আমি তাদের সামনে দিয়ে, তাদের পেছন দিয়ে, তাদের ডান দিয়ে, তাদের বাম দিয়ে, তাদের কাছে অবশ্যই আসব, তুমি তাদের অধিকাংশকেই শোকর আদায়কারী পাবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (972,7,18,'তিনি বললেন, ধিকৃত আর বিতাড়িত হয়ে এখান থেকে বেরিয়ে যা, তাদের মধ্যে যারা তোকে মান্য করবে তোমাদের সবাইকে দিয়ে আমি অবশ্যই জাহান্নাম ভর্তি করব। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (973,7,19,'‘আর, হে আদাম! তুমি ও তোমার স্ত্রী জান্নাতে বাস করতে থাক, দু’জনে যা পছন্দ হয় খাও আর এই গাছের কাছে যেও না, তাহলে যালিমদের দলে শামিল হয়ে যাবে।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (974,7,20,'অতঃপর শয়ত্বান তাদেরকে কুমন্ত্রণা দিল তাদের লজ্জাস্থান প্রকাশ করার জন্য যা তাদের পরস্পরের নিকট গোপন রাখা হয়েছিল; আর বলল, ‘তোমাদেরকে তোমাদের রবব এ গাছের নিকটবর্তী হতে যে নিষেধ করেছেন তার কারণ এছাড়া আর কিছুই নয় যে (নিকটবর্তী হলে) তোমরা দু’জন ফেরেশতা হয়ে যাবে কিংবা (জান্নাতে) স্থায়ী হয়ে যাবে।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (975,7,21,'সে শপথ করে তাদের বলল, ‘আমি তোমাদের সত্যিকারের হিতাকাঙ্ক্ষী।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (976,7,22,'এভাবে সে ধোঁকা দিয়ে তাদের অধঃপতন ঘটিয়ে দিল। যখন তারা গাছের ফলের স্বাদ নিল, তখন তাদের গোপনীয় স্থান পরস্পরের নিকট প্রকাশিত হয়ে গেল, তারা জান্নাতের পাতা দিয়ে নিজেদেরকে ঢাকতে লাগল। তখন তাদের প্রতিপালক তাদেরকে ডেকে বললেন, ‘আমি কি তোমাদেরকে এ গাছের কাছে যেতে নিষেধ করিনি আর বলিনি- শয়ত্বান হচ্ছে তোমাদের উভয়ের খোলাখুলি দুশমন?’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (977,7,23,'তারা বলল, ‘হে আমাদের প্রতিপালক! আমরা নিজেদের প্রতি অন্যায় করে ফেলেছি, যদি তুমি আমাদেরকে ক্ষমা না কর আর দয়া না কর তাহলে আমরা অবশ্য অবশ্যই ক্ষতিগ্রস্তদের অন্তর্ভুক্ত হয়ে যাব।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (978,7,24,'তিনি বললেন, ‘তোমরা নেমে যাও, তোমরা একে অন্যের শত্রু, পৃথিবীতে তোমাদের অবস্থান ও জীবিকা থাকবে একটা নির্দিষ্ট সময়ের জন্য।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (979,7,25,'বললেন, ‘ওখানে তোমরা জীবন যাপন করবে, ওখানেই তোমাদের মৃত্যু হবে, আর তাথেকেই তোমাদেরকে বের করা হবে।’');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (980,7,26,'হে আদাম সন্তান! আমি তোমাদেরকে পোষাক-পরিচ্ছদ দিয়েছি তোমাদের লজ্জাস্থান আবৃত করার জন্য এবং শোভা বর্ধনের জন্য। আর তাকওয়ার পোশাক হচ্ছে সর্বোত্তম পোশাক। ওটা আল্লাহর নিদর্শনসমূহের মধ্যে একটি যাতে তারা উপদেশ গ্রহণ করে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (981,7,27,'হে আদাম সন্তান! শয়ত্বান যেন তোমাদেরকে কিছুতেই ফিতনায় ফেলতে না পারে যেমনভাবে তোমাদের পিতা-মাতাকে (আদম ও হাওয়াকে) জান্নাত থেকে বের করেছিল। সে তাদের পরস্পরকে লজ্জাস্থান দেখানোর জন্য তাদের দেহ হতে পোষাক খুলিয়ে ফেলেছিল। সে আর তার সাথীরা তোমাদেরকে এমনভাবে দেখতে পায় যে তোমরা তাদেরকে দেখতে পাও না। যারা ঈমান আনে না তাদের জন্য আমি শয়ত্বানকে অভিভাবক বানিয়ে দিয়েছি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (982,7,28,'তারা যখন কোন অশ্লীল কাজ করে তখন তারা বলে- ‘আমরা আমাদের পিতৃপুরুষদেরকে এ কাজই করতে দেখেছি, আর আল্লাহ আমাদেরকে এসব কাজ করার আদেশ দিয়েছেন।’ বল, ‘আল্লাহ অশ্লীলতার নির্দেশ দেন না, আল্লাহর সম্বন্ধে তোমরা কি এমন কথা বলছ যা তোমরা জান না?’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (983,7,29,'বল, ‘আমার প্রতিপালক ন্যায়পরায়ণতার নির্দেশ দিয়েছেন’, আর প্রত্যেক সলাতে তোমাদের লক্ষ্য ও মনোযোগকে (তাঁর প্রতি) নিবদ্ধ কর, তাঁর আনুগত্যে বিশুদ্ধ-চিত্ত হয়ে তাঁকে ডাক। যেভাবে তোমাদেরকে প্রথমে সৃষ্টি করা হয়েছে (তোমাদের মৃত্যুর পর আবার জীবিত হয়ে) সেভাবেই তোমরা ফিরে আসবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (984,7,30,'একদলকে তিনি সঠিক পথ দেখিয়েছেন, আর অন্য দলের প্রতি গোমরাহী নির্ধারিত হয়েছে, তারা আল্লাহকে বাদ দিয়ে শয়ত্বানদেরকে তাদের অভিভাবক করে নিয়েছে আর মনে করছে যে তারা সঠিক পথে আছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (985,7,31,'হে আদাম সন্তান! প্রত্যেক সলাতের সময় তোমরা সাজসজ্জা গ্রহণ কর, আর খাও, পান কর কিন্তু অপচয় করো না, অবশ্যই তিনি অপচয়কারীদেরকে পছন্দ করেন না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (986,7,32,'বল, ‘যে সব সৌন্দর্য-শোভামন্ডিত বস্তু ও পবিত্র জীবিকা তিনি তাঁর বান্দাদের জন্য সৃষ্টি করেছেন কে তা হারাম করল’? বল, ‘সে সব হচ্ছে ঈমানদারদের জন্য দুনিয়ার জীবনে বিশেষতঃ ক্বিয়ামাতের দিনে। এভাবে আমি জ্ঞানী সম্প্রদায়ের জন্য নিদর্শনসমূহ বিশদভাবে বিবৃত করি।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (987,7,33,'বল, ‘আমার প্রতিপালক অবশ্যই প্রকাশ্য ও গোপন অশ্লীলতা, পাপ, অন্যায়, বিরোধিতা, আল্লাহর অংশীদার স্থির করা যে ব্যাপারে তিনি কোন প্রমাণ নাযিল করেননি, আর  আল্লাহ  সম্পর্কে তোমাদের  অজ্ঞতাপ্রসূত  কথাবার্তা নিষিদ্ধ করে দিয়েছেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (988,7,34,'প্রতিটি জাতির জন্য সময় নির্ধারিত আছে। তাদের নির্ধারিত সময় যখন এসে যাবে তখন এক মুহূর্তকাল পশ্চাৎ-অগ্র হবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (989,7,35,'হে আদাম সন্তান! তোমাদের কাছে তোমাদেরই মধ্যে থেকে যখন রসূলগণ আসে যারা তোমাদের কাছে আমার আয়াতগুলোকে বিশদভাবে ব্যাখ্যা করে, তখন যারা তাকওয়া অবলম্বন করে আর নিজেদেরকে সংশোধন করে নেয়, তাদের কোন ভয় নেই, তারা চিন্তিত হবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (990,7,36,'আর যারা আমার আয়াতগুলোকে অস্বীকার করবে আর সেগুলোর ব্যাপারে ঔদ্ধত্য দেখাবে, তারাই হল জাহান্নামের বাসিন্দা, তাতে তারা চিরকাল থাকবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (991,7,37,'তাত্থেকে বড় যালিম আর কে আছে যে আল্লাহ সম্পর্কে মিথ্যে রচনা করে অথবা তাঁর আয়াতগুলোকে অস্বীকার করে? কিতাবে লিখিত (দুনিয়াতে তাদের জন্য) নির্দিষ্ট অংশ তাদের কাছে পৌঁছবে, যে পর্যন্ত না আমার প্রেরিত ফেরেশতা তাদের জান কব্য করার জন্য তাদের কাছে আসবে। তারা (অর্থাৎ ফেরেশতারা) জিজ্ঞেস করবে, ‘আল্লাহ ছাড়া যাদেরকে তোমরা আহবান করতে তারা কোথায়’? তারা বলবে, ‘তারা আমাদের থেকে উধাও হয়ে গেছে’ আর তারা নিজেদের বিরুদ্ধে সাক্ষ্য দিবে যে তারা কাফির ছিল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (992,7,38,'আল্লাহ বলবেন, ‘তোমাদের আগে জ্বিন ও মানুষের মধ্যে যারা জাহান্নামে প্রবেশ করেছে, তাদের মাঝে প্রবেশ কর।’ যখনই একটি দল প্রবেশ করবে, তখন তারা অন্যদলকে অভিসম্পাত করবে। অবশেষে সবাই যখন তার ভিতর একত্রিত হবে, তখন প্রত্যেকটি পরবর্তী দল আগের দল সম্পর্কে বলবে, ‘হে আমাদের প্রতিপালক! ওরাই আমাদেরকে পথভ্রষ্ট করেছে, কাজেই ওদেরকে আগুনে দ্বিগুণ শাস্তি দাও।’ আল্লাহ বলবেন, ‘প্রত্যেকের শাস্তি দ্বিগুণ করা হয়েছে (নিজেরা পথভ্রষ্ট হওয়ার জন্য এবং অন্যদের পথভ্রষ্ট করার জন্য) কিন্তু তোমরা জান না।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (993,7,39,'তাদের পূর্ববর্তী দল (আগের জেনারেশন) পরবর্তী দলকে (পরের জেনারেশনকে) বলবে, ‘আমাদের চেয়ে তোমাদের বেশি কোন মর্যাদা নেই, কাজেই তোমাদের কৃতকর্মের জন্য শাস্তিও স্বাদ গ্রহণ কর।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (994,7,40,'যারা আমার আয়াতগুলোকে অস্বীকার করে আর এ ব্যাপারে ঔদ্ধত্য প্রকাশ করে তাদের জন্য আকাশের দরজাগুলো উন্মুক্ত হবে না আর তারা জান্নাতে প্রবেশ করবে না-যতক্ষণ না সূঁচের ছিদ্রে উট প্রবেশ করে। এভাবেই আমি অপরাধীদেরকে প্রতিফল দিয়ে থাকি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (995,7,41,'তাদের জন্য হবে জাহান্নামের বিছানা, আর উপরে ভাঁজের পর ভাঁজ করা অগ্নির আচ্ছাদন। আর এভাবেই আমি যালিমদেরকে প্রতিফল দিয়ে থাকি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (996,7,42,'আর যারা ঈমান আনে আর সৎকাজ করে- আমি কারো উপর সাধ্যের অতিরিক্ত দায়িত্ব চাপিয়ে দেইনা- তারা হবে জান্নাতের অধিবাসী, সেখানে তারা হবে স্থায়ী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (997,7,43,'তাদের অন্তর থেকে হিংসা-বিদ্বেষ দূর করে দেব, তাদের পাদদেশে নির্ঝরিণী প্রবাহিত হবে, আর তারা বলবে, যাবতীয় প্রশংসা আল্লাহর যিনি আমাদেরকে এ পথ দেখিয়েছেন, আমরা কিছুতেই পথ পেতাম না যদি না আল্লাহ আমাদেরকে পথ দেখাতেন। আমাদের প্রতিপালকের রসূলগণ প্রকৃত সত্য নিয়েই এসেছিলেন। তাদেরকে আহবান করে জানানো হবে- ‘তোমরা (দুনিয়াতে) যে ‘আমাল করতে তার ফলে তোমরা এ জান্নাতের উত্তরাধিকারী হয়েছ।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (998,7,44,'জান্নাতবাসীরা জাহান্নামবাসীদেরকে সম্বোধন করে বলবে যে, ‘আমাদেরকে আমাদের প্রতিপালক যে ওয়া‘দা দিয়েছিলেন তা আমরা ঠিক ঠিক পেয়েছি। আর তোমরাও কি তোমাদের প্রতিপালকের ওয়া‘দা ঠিক মত পেয়েছ?’ তারা বলবে, ‘হাঁ’। তখন একজন ঘোষণাকারী তাদের মাঝে ঘোষণা করবে যে, যালিমদের উপর আল্লাহর অভিশাপ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (999,7,45,'যারা আল্লাহর পথে বাধা সৃষ্টি করে আর তাকে বাঁকা করতে চায়, আর তারা পরকাল অস্বীকারকারী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1000,7,46,'উভয় দলের মাঝে আছে পর্দা আর আ‘রাফে (জান্নাত ও জাহান্নামের মধ্যবর্তী অংশ) কিছু লোক থাকবে যারা প্রত্যেক লোককে তার চিহ্ন দ্বারা চিনতে পারবে (যে সে জান্নাতের বাসিন্দা না জাহান্নামের)। জান্নাতবাসীদেরকে ডেকে তারা বলবে, ‘তোমাদের প্রতি সালাম’। তারা (আ‘রাফবাসীরা) তখনও জান্নাতে প্রবেশ করেনি কিন্তু তারা আশা করছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1001,7,47,'যখন তাদের দৃষ্টি অগ্নিবাসীদের দিকে ঘুরিয়ে দেয়া হবে তখন তারা বলবে, ‘হে আমাদের প্রতিপালক! আমাদেরকে যালিমদের সঙ্গী করো না।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1002,7,48,'আ‘রাফবাসীরা যাদেরকে চিহ্ন দেখে চিনতে পারবে তাদেরকে ডেকে বলবে, ‘তোমাদের দলবল আর গর্ব-অহঙ্কার কোন কাজে আসল না।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1003,7,49,'এরাই (অর্থাৎ জান্নাতবাসীরা) কি ঐ সব লোক যাদের ব্যাপারে তোমরা শপথ করে বলতে যে আল্লাহ তাদের প্রতি কোন দয়া দেখাবেন না। (আজ এদেরকেই বলা হল) তোমরা জান্নাতে প্রবেশ কর, তোমাদের কোন ভয় নেই, তোমরা দুঃখিতও হবে না।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1004,7,50,'জাহান্নামীরা জান্নাতীদের ডেকে বলবে, ‘আমাদেরকে কিছু পানি ঢেলে দাও কিংবা আল্লাহ তোমাদেরকে যে রিযক্ দিয়েছেন তাত্থেকে কিছু দাও।’ তারা বলবে, ‘আল্লাহ এ দু’টো কাফিরদের জন্য নিষিদ্ধ করে দিয়েছেন।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1005,7,51,'‘যারা তাদের দ্বীনকে খেলা-তামাশা বানিয়ে নিয়েছিল আর দুনিয়ার জীবন যাদেরকে প্রতারিত করেছিল।’ কাজেই আজকের দিনে তাদেরকে আমি ভুলে যাব যেভাবে তারা এ দিনের সাক্ষাৎকে ভুলে গিয়েছিল এবং আমার আয়াতসমূহকে অস্বীকার করে চলেছিল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1006,7,52,'আমি অবশ্যই তাদের কাছে এক কিতাব এনে দিয়েছিলাম যা ছিল পূর্ণ জ্ঞানের ভিত্তিতে বিশদভাবে বিবৃত যা ছিল বিশ্বাসী সম্প্রদায়ের জন্য সঠিক পথের দিশারী ও রহমত স্বরূপ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1007,7,53,'তারা কি তার পরিণামের অপেক্ষা করছে (কাফিরদেরকে যে পরিণামের ব্যাপারে এ কিতাব খবর দিয়েছে) যখন তার (খবর দেয়া) পরিণাম এসে যাবে তখন পূর্বে যারা এর কথা ভুলে গিয়েছিল তারা বলবে, আমাদের প্রতিপালকের রসূলগণ তো প্রকৃত সত্য নিয়েই এসেছিল, এখন কোন সুপারিশকারী আছে কি যারা আমাদের জন্য সুপারিশ করবে অথবা আমাদেরকে (পৃথিবীতে) ফিরে যেতে দেয়া হবে কি যাতে আমরা যে ‘আমাল করছিলাম তত্থেকে ভিন্নতর ‘আমাল করি? তারা নিজেদেরকে ক্ষতিগ্রস্ত করে ফেলেছে, আর তারা যে মিথ্যে রচনা করত তাও তাদের কাছ থেকে উধাও হয়ে গেছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1008,7,54,'তোমাদের প্রতিপালক আল্লাহ যিনি ছয় দিনে আকাশমন্ডলী ও পৃথিবী সৃষ্টি করেছেন, অতঃপর আরশে সমুন্নত হয়েছেন। দিনকে তিনি রাতের পর্দা দিয়ে ঢেকে দেন, তারা একে অন্যকে দ্রুতগতিতে অনুসরণ করে এবং সূর্য, চন্দ্র, তারকারাজি তাঁরই আজ্ঞাবহ। জেনে রেখ, সৃষ্টি তাঁর, হুকুমও (চলবে) তাঁর, বরকতময় আল্লাহ বিশ্বজগতের প্রতিপালক। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1009,7,55,'তোমরা তোমাদের প্রতিপালককে বিনয়ের সঙ্গে এবং গোপনে আহবান কর, তিনি সীমালঙ্ঘনকারীদেরকে পছন্দ করেন না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1010,7,56,'শান্তি-শৃঙ্খলা স্থাপিত হওয়ার পর পৃথিবীতে বিপর্যয় সৃষ্টি করো না, আর তাঁকে ভয়-ভীতি ও আশা-ভরসা নিয়ে ডাকতে থাক, আল্লাহর দয়া তো (সব সময়) তাদের নিকটে আছে যারা সৎ কাজ করে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1011,7,57,'তিনি তাঁর রহমতের পূর্বে সুসংবাদের ঘোষক হিসেবে বায়ু প্রেরণ করেন, অতঃপর যখন তা মেঘের ভারী বোঝা বহন করে, তখন আমি তাকে মৃত ভূখন্ডের দিকে তাড়িয়ে নিয়ে যাই, যাত্থেকে আমি পানি বর্ষণ করি, অতঃপর তাত্থেকে আমি সর্বপ্রকার ফল উৎপন্ন করি। এভাবেই আমি মৃতকে জীবিত করি যাতে তোমরা শিক্ষা গ্রহণ কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1012,7,58,'উৎকৃষ্ট ভূমি তার প্রতিপালকের নির্দেশে বৃক্ষলতা উৎপাদন করে, আর নিকৃষ্ট যমীন থেকে কঠিন পরিশ্রম না করলে কিছুই উৎপন্ন হয় না। এভাবেই আমি আয়াতগুলো বারবার বিবৃত করি কৃতজ্ঞ সম্প্রদায়ের জন্য। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1013,7,59,'আমি নূহকে তার সম্প্রদায়ের নিকট পাঠিয়েছিলাম। সে বলেছিল, ‘হে আমার সম্প্রদায়! তোমরা আল্লাহর ‘ইবাদাত কর, তিনি ছাড়া তোমাদের কোন ইলাহ নাই।’ (তোমরা আল্লাহর নির্দেশ অমান্য করলে) মহাদিনে আমি তোমাদের জন্য শাস্তির আশঙ্কা করি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1014,7,60,'তার সম্প্রদায়ের প্রধানগণ বলেছিল, ‘আমরা নিশ্চিতভাবে তোমাকে প্রকাশ্য গোমরাহীতে নিমজ্জিত দেখছি।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1015,7,61,'সে বলল, ‘হে আমার সম্প্রদায়! আমাতে কোন বিভ্রান্তি নেই, বরং আমি বিশ্বজগতের প্রতিপালকের পক্ষ হতে প্রেরিত রসূল।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1016,7,62,'আমি তোমাদের কাছে আমার প্রতিপালকের বাণী পৌঁছে দিচ্ছি আর তোমাদেরকে নাসীহাত করছি, আর আমি আল্লাহর নিকট হতে (এমন সব বিষয়) জানি যা তোমরা জান না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1017,7,63,'‘তোমরা কি এ বিষয়ে বিস্ময় প্রকাশ করছ যে তোমাদের মধ্যেকার একজন লোকের নিকট তোমাদের জন্য তোমাদের প্রতিপালকের নিকট হতে উপদেশ বাণী এসেছে তোমাদেরকে সতর্ক করার জন্য যাতে তোমরা তাক্বওয়া অবলম্বন করে আল্লাহর রহমতপ্রাপ্ত হতে পার।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1018,7,64,'কিন্তু তারা তাকে (মিথ্যেবাদী মনে করে) অস্বীকার করল। অতঃপর আমি তাকে আর তার সঙ্গে নৌকায় যারা ছিল তাদেরকে বাঁচিয়ে দিলাম আর আমার আয়াতগুলোকে যারা অস্বীকার করেছিল তাদেরকে ডুবিয়ে মারলাম। তারা ছিল এক অন্ধ সম্প্রদায়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1019,7,65,'আর ‘আদ জাতির কাছে (পাঠিয়েছিলাম) তাদের ভাই হূদকে। সে বলেছিল, ‘হে আমার সম্প্রদায়! তোমরা আল্লাহর ‘ইবাদাত কর, তিনি ছাড়া তোমাদের অন্য কোন ইলাহ নেই, তোমরা কি তাক্বওয়া অবলম্বন করবে না?’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1020,7,66,'তার জাতির প্রধানগণ- যারা ইমান আনতে অস্বীকার করেছিল, বলল, ‘তুমি নিশ্চিতই নির্বুদ্ধিতায় ডুবে আছ, আর আমরা মনে করি তুমি মিথ্যেবাদী।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1021,7,67,'সে বলল, ‘হে আমার সম্প্রদায়! আমার মাঝে কোন নির্বুদ্ধিতা নেই, বরং আমি বিশ্বজগতের প্রতিপালকের প্রেরিত রসূল।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1022,7,68,'‘আমি তোমাদের কাছে আমার প্রতিপালকের বাণী পৌঁছে দিচ্ছি, আর আমি তোমাদের বিশ্বস্ত কল্যাণকামী।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1023,7,69,'তোমরা কি আশ্চর্য হচ্ছ যে, তোমাদেরই মধ্যে একজন লোকের উপর তোমাদের প্রতিপালকের নিকট হতে উপদেশ এসেছে তোমাদেরকে সাবধান করার উদ্দেশে। আরো স্মরণ কর তিনি তোমাদেরকে নূহের কওমের পর তাদের স্থলাভিষিক্ত করেছেন আর দৈহিক গঠনে অধিকতর বলিষ্ঠ করেছেন, কাজেই আল্লাহর অনুগ্রহের কথা স্মরণ কর যাতে তোমরা সাফল্যমন্ডিত হতে পার।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1024,7,70,'তারা বলল, ‘তুমি কি আমাদের কাছে এজন্য এসেছ যাতে আমরা এক আল্লাহর ‘ইবাদাত করি আর আমাদের পিতৃপুরুষগণ যার ‘ইবাদাত করত তা ত্যাগ করি? কাজেই তুমি যদি সত্যবাদী হও তাহলে আমাদেরকে যে জিনিসের ওয়াদা করছ (ভয় দেখাচ্ছ) তা নিয়ে এসো।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1025,7,71,'সে বলল, ‘তোমাদের উপর তোমাদের প্রতিপালকের অসন্তোষ ও ক্রোধ নেমে এসেছে। তাহলে তোমরা কি আমার সঙ্গে এমন কতকগুলো নাম সম্পর্কে বিতর্ক করছ যেগুলোর নামকরণ তোমরা আর তোমাদের বাপ-দাদারাই করেছে যে সম্পর্কে আল্লাহ কোন প্রমাণ নাযিল করেননি? তাহলে তোমরা অপেক্ষা কর, আমিও তোমাদের সঙ্গে অপেক্ষমান থাকলাম।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1026,7,72,'আমার করুণায় আমি তাকে ও তার সঙ্গী-সাথীদেরকে রক্ষা করলাম আর আমার নিদর্শনাবলীকে যারা অস্বীকার করেছিল এবং ঈমান আনেনি তাদের মূল উৎপাটন করলাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1027,7,73,'আমি সামুদ জাতির কাছে তাদের ভাই সালিহকে পাঠিয়েছিলাম। সে বলেছিল, হে আমার সম্প্রদায়! তোমরা আল্লাহর ‘ইবাদাত কর, তিনি ছাড়া তোমাদের কোন ইলাহ নেই। তোমাদের নিকট তোমাদের প্রতিপালকের পক্ষ হতে সুস্পষ্ট প্রমাণ এসেছে। এটি হল আল্লাহর উটনী, তোমাদের জন্য নিদর্শন, তাকে আল্লাহর যমীনে চরে খেতে দাও, মন্দ উদ্দেশে একে স্পর্শ করবে না, করলে পীড়াদায়ক শাস্তি তোমাদেরকে গ্রাস করবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1028,7,74,'স্মরণ কর, ‘আদ জাতির পরে তিনি তোমাদেরকে তাদের স্থলাভিষিক্ত করেছেন আর তোমাদেরকে যমীনে বসতি দান করেছেন, তোমরা তার সমতলে প্রাসাদ নির্মাণ করছ আর পাহাড় কেটে ঘর তৈরি করছ, কাজেই আল্লাহর অনুগ্রহের কথা স্মরণ কর, পৃথিবীতে ফাসাদ সৃষ্টি কর না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1029,7,75,'তার জাতির গর্বিত প্রধানগণ ঐসব লোকদেরকে বলেছিল যাদেরকে দুর্বল করে রাখা হয়েছিল, যারা তাদের মধ্যে ঈমান এনেছিল- ‘‘তোমরা কি জান যে সালিহ তার প্রতিপালক কর্তৃক প্রেরিত? তারা বলেছিল, ‘তিনি যে বাণী নিয়ে প্রেরিত হয়েছেন তাতে আমরা বিশ্বাসী’। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1030,7,76,'যারা অহঙ্কার করেছিল তারা বলেছিল, ‘তোমরা যাতে বিশ্বাস করেছ আমরা তা অস্বীকার করছি।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1031,7,77,'অতঃপর তারা উষ্ট্রীটিকে মেরে ফেলল এবং তাদের প্রতিপালকের স্পষ্ট বিরোধিতা করে চলল আর বলল, ‘হে সালিহ! তুমি যদি রসূল হয়েই থাক তাহলে তা নিয়ে এসো আমাদেরকে যার ওয়াদা করছ (ভয় দেখাচ্ছ)।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1032,7,78,'অতঃপর ভূমিকম্প তাদেরকে হঠাৎ পাকড়াও করল আর তারা তাদের ঘরগুলোতে মুখ থুবড়ে পড়ে রইল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1033,7,79,'আর সালিহ এ কথা বলে তাদেরকে পরিত্যাগ করল যে, হে আমার সম্প্রদায়! ‘আমি তোমাদের কাছে আমার প্রতিপালকের বাণী পৌঁছে দিয়েছি, আর তোমাদেরকে সদুপদেশ দিয়েছি কিন্তু সদুপদেশ দানকারীদেরকে তোমরা পছন্দ কর না।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1034,7,80,'আর আমি লূতকে পাঠিয়েছিলাম। যখন সে তার জাতিকে বলেছিল, ‘তোমরা এমন নির্লজ্জতার কাজ করছ যা বিশ্বজগতে তোমাদের পূর্বে কোন একজনও করেনি।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1035,7,81,'তোমরা যৌন তাড়নায় স্ত্রীদের বাদ দিয়ে পুরুষদের নিকট গমন করছ, তোমরা হচ্ছ এক সীমালঙ্ঘনকারী সম্প্রদায়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1036,7,82,'তার জাতির এ ছাড়া আর কোন জবাব ছিল না যে, ‘তোমাদের জনপদ থেকে এদেরকে বের করে দাও, এরা এমন লোক যারা খুব পবিত্র হতে চায়।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1037,7,83,'অতঃপর আমি তাকে আর তার পরিবার-পরিজনকে রক্ষা করলাম কিন্তু তার স্ত্রী ছাড়া, কেননা সে ছিল পেছনে অবস্থানকারীদের অন্তর্ভুক্ত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1038,7,84,'তাদের উপর এক পাথরের বৃষ্টি বর্ষিয়ে দিলাম। তারপর দেখ, অপরাধীদের পরিণতি কী হয়েছিল! ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1039,7,85,'আমি মাদইয়ানবাসীদের কাছে তাদের ভাই শু‘আয়বকে পাঠিয়েছিলাম। (সে বলেছিল) ‘হে আমার সম্প্রদায়! তোমরা আল্লাহর ‘ইবাদাত কর, তিনি ছাড়া তোমাদের কোন ইলাহ নেই, তোমাদের প্রতিপালকের নিকট থেকে তোমাদের কাছে তো স্পষ্ট নিদর্শন এসে গেছে, কাজেই মাপ ও ওজন সঠিকভাবে কর, লোকেদেরকে তাদের প্রাপ্য বিষয়ে ক্ষতিগ্রস্ত করো না, পৃথিবীর সংশোধনের পর তাতে বিপর্যয় সৃষ্টি কর না, এটাই তোমাদের জন্য কল্যাণকর যদি তোমরা সত্যিই মু’মিন হয়ে থাক।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1040,7,86,'‘যারা ঈমান এনেছে তাদেরকে ভয় দেখানোর জন্য আর আল্লাহর পথ থেকে বাধা দেয়ার জন্য প্রতিটি পথে পাহারা বসিও না, আর তাতে (অর্থাৎ আল্লাহর সহজ-সরল পথে) বক্রতা অনুসন্ধান কর না।’ স্মরণ কর, ‘তোমরা সংখ্যায় অল্প ছিলে, অতঃপর আল্লাহ তোমাদেরকে সংখ্যাধিক্য করে দিয়েছেন। লক্ষ্য কর, বিপর্যয় সৃষ্টিকারীদের পরিণতি কী হয়েছিল।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1041,7,87,'‘আমি যা নিয়ে প্রেরিত হয়েছি তাতে যদি তোমাদের একদল ঈমান আনে আর একদল ঈমান না আনে, তাহলে ধৈর্য ধারণ কর যে পর্যন্ত না আল্লাহ আমাদের আর তোমাদের মাঝে মীমাংসা করে দেন, তিনি হলেন সর্বোত্তম মীমাংসাকারী।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1042,7,88,'তার জাতির উদ্ধত সর্দারগণ বলল, ‘ওহে শু‘আয়ব! আমরা তোমাকে আর তোমার সঙ্গে যারা ঈমান এনেছে তাদেরকে আমাদের জনপদ থেকে বের করে দেবই, অথবা তুমি আমাদের ধর্মবিশ্বাসে অবশ্যই ফিরে আসবে।’ সে বলল, ‘আমরা যদি তাতে রাজী না হই তবুও?’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1043,7,89,'আল্লাহ যখন আমাদেরকে তোমাদের ধর্মবিশ্বাস থেকে রক্ষা করেছেন, তখন যদি আমরা তাতে ফিরে যাই, তাহলে তো আমরা আল্লাহর প্রতি মিথ্যারোপ করে ফেলব। আমরা তাতে ফিরে যেতে পারি না আমাদের প্রতিপালক আল্লাহর ইচ্ছে ব্যতীত। প্রতিটি বিষয় সম্পর্কে আমাদের প্রতিপালকের জ্ঞান পরিব্যাপ্ত, আমরা আল্লাহরই প্রতি নির্ভর করি। হে আমাদের প্রতিপালক! ‘তুমি আমাদের আর আমাদের জাতির মধ্যে সঠিকভাবে ফায়সালা করে দাও আর তুমি হলে সর্বোত্তম মীমাংসাকারী।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1044,7,90,'তার জাতির যারা কুফরী করেছিল সেই প্রধানগণ বলল, ‘তোমরা যদি শু‘আয়বের কথা মেনে নাও তাহলে তোমরা ক্ষতিগ্রস্তদের অন্তর্ভুক্ত হয়ে যাবে।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1045,7,91,'অতঃপর ভূমিকম্প তাদেরকে হঠাৎ আঘাত হানল আর তারা তাদের ঘরবাড়িতে উপুড় হয়ে পড়ে রইল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1046,7,92,'যেন শু‘আয়বকে অস্বীকারকারীরা সেখানে কোনদিন বসবাস করেনি। যারা শু‘আয়বকে মিথ্যা প্রতিপন্ন করেছিল তারা ক্ষতিগ্রস্ত হয়ে গেল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1047,7,93,'সে তাদেরকে ত্যাগ করল আর বলল, ‘হে আমার জাতির লোকেরা! আমি তোমাদের কাছে আমার প্রতিপালকের প্রেরিত বাণী পৌঁছে দিয়েছি, আর তোমাদেরকে সদুপদেশ দিয়েছি, কাজেই আমি কাফির জাতির জন্য কী করে আক্ষেপ করতে পারি।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1048,7,94,'আমি কোন জনপদে এমন কোন নাবীই পাঠাইনি যেখানকার অধিবাসীদেরকে অভাব-অনটন আর দুঃখ-কষ্ট কশাঘাত করেনি যাতে তারা নম্রতা ও কাতরতা প্রকাশ করে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1049,7,95,'অতঃপর আমি দূরাবস্থাকে সচ্ছলতা দিয়ে বদলে দিলাম আর শেষ পর্যন্ত তারা প্রাচুর্যে বেড়ে গেল আর বলল, ‘আমাদের পিতৃপুরুষের উপরও এ রকম সুখ-দুঃখের দিন আসত।’ অতঃপর আমি তাদেরকে হঠাৎ পাকড়াও করলাম অথচ তারা টের করতেও পারল না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1050,7,96,'জনপদগুলোর লোকেরা যদি ঈমান আনত আর তাক্বওয়া অবলম্বন করত তাহলে আমি তাদের জন্য আসমান আর যমীনের কল্যাণ উন্মুক্ত করে দিতাম কিন্তু তারা (সত্যকে) প্রত্যাখ্যান করল। কাজেই তাদের কৃতকর্মের কারণে তাদেরকে পাকড়াও করলাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1051,7,97,'জনপদের লোকেরা কি নিজেদেরকে নিরাপদ মনে করছে যে আমার শাস্তি তাদের উপর পতিত হবে না রাতের বেলা যখন তারা ঘুমিয়ে থাকবে? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1052,7,98,'অথবা জনপদের লোকেরা কি নিজেদেরকে নিরাপদ মনে করছে যে আমার শাস্তি তাদের উপর প্রকাশ্য দিবালোকে আসবে না যখন তারা খেলাধূলায় মত্ত থাকবে? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1053,7,99,'তারা কি আল্লাহর কৌশল থেকে নির্ভয় হয়ে গেছে? নিশ্চিত ক্ষতিগ্রস্ত সম্প্রদায় ছাড়া আল্লাহর কৌশল হতে কেউ নির্ভয় হতে পারে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1054,7,100,'যারা কোন দেশের উত্তরাধিকার লাভ করেছে সেখানকার (আগের) অধিবাসীদের পর, তারা কি এ শিক্ষা লাভ করে না যে, আমি তাদেরকে তাদের পাপের কারণে পাকড়াও করতে পারি আর তদের অন্তরে সীল লাগিয়ে দিতে পারি যাতে তারা কিছুই শুনবে না? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1055,7,101,'এসব জনপদের কিছু বিবরণ তোমাকে জানালাম। তাদের কাছে তো তাদের রসূলগণ স্পষ্ট প্রমাণ নিয়ে এসেছিল, কিন্তু যেহেতু তারা আগেভাগেই প্রত্যাখ্যান করে দিয়েছিল এজন্য আর ঈমান আনতে প্রস্তুত ছিল না। এভাবে আল্লাহ কাফিরদের অন্তরে সীল লাগিয়ে দেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1056,7,102,'তাদের অধিকাংশকেই আমি প্রতিশ্রুতি পালনকারী পাইনি, বরং অধিকাংশকে ফাসিকই পেয়েছি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1057,7,103,'তাদের পরে আমি মূসাকে আমার নিদর্শনাবলী সহকারে ফির‘আওন ও তার প্রধানদের কাছে পাঠিয়েছিলাম। কিন্তু সে সবের প্রতি তারা অন্যায় আচরণ প্রদর্শন করে। লক্ষ্য কর, তারপর বিপর্যয় সৃষ্টিকারীদের পরিণতি কেমন হয়েছিল! ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1058,7,104,'মূসা বলেছিল, হে ফির‘আওন! আমি বিশ্বজগতের প্রতিপালকের পক্ষ থেকে প্রেরিত রসূল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1059,7,105,'আমার জন্য অবশ্য করণীয় এই যে, আমি প্রকৃত সত্য ছাড়া আল্লাহর সম্বন্ধে কিছু বলি না, তোমাদের প্রতিপালকের নিকট হতে স্পষ্ট প্রমাণ সহকারে তোমাদের কাছে এসেছি। কাজেই বানী ইসরাঈলকে আমার সঙ্গে পাঠিয়ে দাও। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1060,7,106,'ফিরআউন বলল, নিদর্শন নিয়েই তুমি যদি এসে থাক, তাহলে সত্যবাদী হলে তুমি তা পেশ কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1061,7,107,'সে তার লাঠি নিক্ষেপ করল আর তখন তা একটা প্রকাশ্য অজগর হয়ে গেল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1062,7,108,'আর সে তার হাত (বগল থেকে) টেনে বের করল, তখন তা দর্শকদের দৃষ্টিতে সাদা উজ্জ্বল হয়ে দেখা দিল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1063,7,109,'ফির‘আওন গোষ্ঠীর প্রধানরা বলল, নিশ্চয় সে বিজ্ঞ যাদুকর।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1064,7,110,'‘সে চায় তোমাদেরকে বের করে দিতে তোমাদের দেশ থেকে। এখন তোমাদের পরামর্শ কী?’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1065,7,111,'তারা বলল, ‘তাকে ও তার ভাইকে অবকাশ দাও, আর নগরে নগরে সংগ্রাহক পাঠিয়ে দাও’। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1066,7,112,'তারা তোমার কাছে সব বড় বড় যাদুকরকে নিয়ে আসবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1067,7,113,'যাদুকররা ফিরআউনের নিকট এসে বলল, আমরা যদি বিজয়ী হই, তবে আমাদের জন্য পুরস্কার আছে তো? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1068,7,114,'সে বলল, হাঁ, তোমরা অবশ্যই (আমার) নৈকট্যলাভকারীদের মধ্যে শামিল হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1069,7,115,'তারা বলল, ‘হে মূসা! তুমিই কি (প্রথমে যাদু) ছুঁড়বে, না আমরাই ছুঁড়ব?’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1070,7,116,'সে বলল, ‘তোমরাই ছুঁড়’। যখন তারা বান ছুঁড়ল তখন লোকজনের চোখ যাদুগ্রস্ত হয়ে গেল, তারা ভীত সন্ত্রস্ত হয়ে পড়ল। তারা বড়ই সাংঘাতিক এক যাদু দেখাল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1071,7,117,'আমি মূসার কাছে ওয়াহী করলাম, ‘তোমার লাঠি ছুঁড়ে দাও’। তখন তা তাদের অলীক বস্তুগুলোকে গ্রাস করতে শুরু করল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1072,7,118,'প্রকৃত সত্য প্রকাশ হয়ে গেল, তারা যা সাজিয়েছিল তা নিস্ফল হয়ে গেল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1073,7,119,'তারা সেখানে পরাজিত হল আর লাঞ্ছিত অবস্থায় ফিরে গেল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1074,7,120,'আর যাদুকররা সাজদায় লুটিয়ে পড়ল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1075,7,121,'তারা বলল, ‘আমরা বিশ্বজগতের প্রতিপালকের প্রতি ঈমান আনলাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1076,7,122,'মূসা আর হারূনের প্রতিপালকের প্রতি।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1077,7,123,'ফির‘আওন বলল, ‘আমি হুকুম দেয়ার আগেই তোমরা তার প্রতি ঈমান আনল? অবশ্যই এটা তোমাদের ষড়যন্ত্র যেটা নগরে বসে তোমরা পাকিয়েছ, এর বাসিন্দাদের বহিস্কৃত করার উদ্দেশে। এর পরিণাম শীঘ্রই টের পাবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1078,7,124,'তোমাদের হাত পা গুলোকে বিপরীত দিক থেকে অবশ্যই আমি কেটে দেব, তারপর তোমাদের সব্বাইকে শূলে চড়াব। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1079,7,125,'তারা বলল, আমরা অবশ্যই আমাদের প্রতিপালকের দিকে প্রত্যাবর্তন করব। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1080,7,126,'তুমি আমাদের উপর প্রতিশোধ নিচ্ছ শুধু এ কারণে যে, আমাদের কাছে যখন আমাদের প্রতিপালকের নিদর্শন এসেছে তখন আমরা তাতে বিশ্বাস স্থাপন করেছি। হে আমাদের প্রতিপালক! আমাদেরকে ধৈর্য ও দৃঢ়তা অবলম্বনের গুণে অভিষিক্ত কর আর মুসলমান হিসেবে আমাদের মৃত্যু দান কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1081,7,127,'ফির‘আওন গোষ্ঠীর সরদারগণ বলল, ‘আপনি কি মূসা আর তার জাতির লোকেদেরকে যমীনে বিপর্যয় সৃষ্টি করার জন্য ছেড়ে দেবেন আর দেবেন আপনাকে আর আপনার মা‘বূদদেরকে বর্জন করতে?’ সে বলল, ‘আমি তাদের পুত্র সন্তানদেরকে হত্যা করব আর তাদের নারীদেরকে জীবিত রাখব, আমরা তাদের উপর অপ্রতিরোধ্য।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1082,7,128,'মূসা তার সম্প্রদায়কে বলল, ‘তোমরা আল্লাহর সাহায্য কামনা কর আর ধৈর্য অবলম্বন কর, যমীনের মালিক হলেন আল্লাহ, তিনি তাঁর বান্দাহদের মধ্য হতে যাকে ইচ্ছে করবেন তার উত্তরাধিকারী বানাবেন, কল্যাণময় পরিণাম হচ্ছে মুত্তাকীদের জন্য।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1083,7,129,'তারা বলল, ‘আমাদের নিকট তোমার আসার পূর্বেও আমাদেরকে জ্বালাতন করা হয়েছে আর তোমার আসার পরেও।’ সে বলল, ‘সত্বর তোমাদের প্রতিপালক তোমাদের দুশমনকে ধ্বংস করবেন আর তোমাদেরকে দেশে প্রতিনিধিত্ব দান করবেন, অতঃপর তিনি দেখবেন তোমরা কেমন ‘আমাল কর।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1084,7,130,'আমি ফির‘আওনী গোষ্ঠীকে দুর্ভিক্ষ আর ফল-ফসলের ক্ষয়-ক্ষতি দিয়ে পাকড়াও করেছিলাম যাতে তারা উপদেশ গ্রহণ করে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1085,7,131,'তারা সুদিনের মুখ দেখলে বলত, ‘এটাই তো আমাদের প্রাপ্য।’ আর যখন তারা দুর্দিনে পতিত হত তখন মূসা আর তার সঙ্গী-সাথীদেরকে অলক্ষুণে বলে দোষারোপ করত। প্রকৃতপক্ষে তাদের মন্দভাগ্য তো আল্লাহর হাতে নিবদ্ধ, কিন্তু তাদের অধিকাংশই তা জানে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1086,7,132,'তারা বলল, ‘আমাদেরকে যাদুগ্রস্ত করার জন্য তুমি যে কোন নিদর্শনই নিয়ে আস না কেন, আমরা কিছুতেই তোমাতে বিশ্বাস করব না।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1087,7,133,'অতঃপর আমি তাদের উপর প্লাবন, পঙ্গপাল, উকুন, ব্যাঙ ও রক্তের বিপদ পাঠিয়েছিলাম সুস্পষ্ট নিদর্শন হিসেবে, কিন্তু তারা ঔদ্ধত্য প্রকাশ করল। তারা ছিল এক অপরাধী জাতি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1088,7,134,'যখন তাদের উপর কোন বালা-মুসিবত আসত তখন তারা বলত, ‘হে মূসা! তোমার প্রতিপালকের নিকট আমাদের জন্য প্রার্থনা জানাও যে মতে তিনি তোমার সাথে অঙ্গীকার করেছেন, যদি আমাদের থেকে বিপদ দূর করে দাও তাহলে আমরা অবশ্যই তোমাতে ঈমান আনব আর বানী ইসরাঈলকে অবশ্যই তোমার সাথে পাঠিয়ে দেব।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1089,7,135,'অতঃপর যখন তাদের উপর থেকে বিপদ সরিয়ে দিতাম একটা নির্দিষ্ট সময় পর্যন্ত যা তাদেরকে পূর্ণ করতে হত, তখন তারা অঙ্গীকার ভঙ্গ করত।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1090,7,136,'তখন আমি তাদের উপর প্রতিশোধ নিলাম আর তাদেরকে সমুদ্রে ডুবিয়ে মারলাম কেননা তারা আমার নিদর্শনসমূহকে অস্বীকার করেছিল আর এ ব্যাপারে তারা ছিল চিন্তা-ভাবনাহীন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1091,7,137,'আর আমি দুর্বল করে রাখা লোকেদেরকে সেই যমীনের পূর্বের আর পশ্চিমের উত্তরাধিকারী বানিয়ে দিলাম যাতে আমি কল্যাণ নিহিত রেখেছি। এভাবে বানী ইসরাঈলের ব্যাপারে তাদের ধৈর্য ধারণের কারণে তোমার প্রতিপালকের কল্যাণময় অঙ্গীকার পূর্ণ হল আর ফির‘আওন ও তার লোকজনের গৌরবময় কাজ ও সুন্দর প্রাসাদগুলোকে ধ্বংস করে দিলাম।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1092,7,138,'বানী ইসরাঈলকে আমি সমুদ্র পার করিয়ে দিলাম, অতঃপর তারা এমন এক জাতির নিকট এলো যারা ছিল প্রতিমা পূজারী। মূসার লোকজন বলল, হে মূসা! আমাদের জন্যও ‘কোন দেবতা বানিয়ে দাও যেমন তাদের দেবতা আছে। মূসা বলল, তোমরা হলে এমন এক সম্প্রদায় যারা মূর্খদের মতো আচরণ করে।’');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1093,7,139,'এসব লোক যাতে মত্ত আছে তা ধ্বংস হয়ে যাবে আর তারা যে সব কাজ করছে তা সব বাতিল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1094,7,140,'সে বলল, ‘আমি কি আল্লাহ ছাড়া তোমাদের জন্য অন্য ইলাহ খুঁজব অথচ তিনি তোমাদেরকে বিশ্বজগতের উপর প্রাধান্য দিয়েছেন?’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1095,7,141,'স্মরণ কর, আমি তোমাদেরকে ফির‘আওনী গোষ্ঠী থেকে রক্ষা করেছি যারা তোমাদেরকে কঠিন আযাবে ডুবিয়ে রেখেছিল, যারা তোমাদের ছেলে সন্তানগুলোকে হত্যা করছিল আর তোমাদের নারীদেরকে জীবিত রাখছিল, এতে তোমাদের জন্য ছিল তোমাদের রবেবর পক্ষ হতে এক কঠিন পরীক্ষা। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1096,7,142,'আমি মূসার জন্য (সিনাই পর্বতের উপর) ত্রিশ রাত্রি নির্ধারণ করলাম। অতঃপর আরো দশ (বাড়িয়ে) দিয়ে (সেই সময়) পূর্ণ করলাম। এভাবে তার প্রতিপালকের নির্ধারিত চল্লিশ রাত্রি পূর্ণ হল। মূসা তার ভাই হারূনকে বলল, ‘আমার অনুপস্থিতিতে আমার সম্প্রদায়ের জন্য তুমি আমার প্রতিনিধিত্ব কর, সংশোধন কর, বিপর্যয় সৃষ্টিকারীদের অনুসরণ করো না।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1097,7,143,'মূসা যখন আমার নির্ধারিত সময়ে আসল, আর তার রবব তার সঙ্গে কথা বললেন, তখন সে বলল, ‘হে আমার প্রতিপালক! আমাকে দেখা দাও, আমি তোমাকে দেখব’। তিনি বললেন, ‘তুমি আমাকে কক্ষনো দেখতে পাবে না, বরং তুমি পাহাড়ের দিকে তাকাও, যদি তা নিজ স্থানে স্থির থাকতে পারে তাহলে তুমি আমাকে দেখতে পাবে।’ অতঃপর তার প্রতিপালক যখন পাহাড়ে নিজ জ্যোতি বিচ্ছুরিত করলেন, তখন তা পাহাড়কে চূর্ণ-বিচূর্ণ করে দিল আর মূসা চৈতন্য হারিয়ে পড়ে গেল। যখন চেতনা ফিরে পেল, তখন সে বলল, ‘পবিত্র তোমার সত্ত্বা, আমি অনুশোচনা ভরে তোমার পানেই ফিরে এলাম, আর আমি প্রথম ঈমান আনছি।’');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1098,7,144,'তিনি বললেন, ‘হে মূসা! আমি আমার রিসালাত (যা তোমাকে দিয়েছি) ও আমার বাক্য (যা তোমার সঙ্গে বলেছিলাম তার) দ্বারা সকল লোকের মধ্য থেকে তোমাকে নির্বাচিত করেছি। কাজেই যা তোমাকে দিয়েছি তা গ্রহণ কর আর শোকর আদায়কারীদের অন্তর্ভুক্ত হও। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1099,7,145,'আমি তার জন্য ফলকে লিখে দিলাম সকল বিষয় সংক্রান্ত নাসীহাত আর সকল বিষয়ে স্পষ্ট ব্যাখ্যা (আর তাকে বললাম) এগুলো শক্ত হাতে ধর, তোমার লোকজনকে আদেশ দাও এগুলো উত্তম পন্থায় মান্য করে চলতে। শীঘ্রই আমি তোমাদেরকে (আল্লাহর নির্দেশ অমান্য করে ধ্বংসস্ত্তপে পরিণত) ফাসিকদের বাসস্থান দেখাব। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1100,7,146,'যারা অন্যায়ভাবে পৃথিবীতে অহঙ্কার করে বেড়ায় আমি শীঘ্রই তাদের দৃষ্টিকে আমার নিদর্শন হতে ফিরিয়ে দেব। তারা আমার নিদর্শন দেখলেও তাতে বিশ্বাস করবে না। তারা সত্যপথ দেখলেও তাকে পথ হিসেবে গ্রহণ করবে না। তারা বক্র পথ দেখলে তাকে পথ হিসেবে গ্রহণ করবে। তার কারণ হল তারা আমার নিদর্শনগুলোকে মিথ্যে বলে উড়িয়ে দিয়েছে আর এগুলোর ব্যাপারে তারা ছিল একেবারে চিন্তা ভাবনাহীন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1101,7,147,'যারা আমার নিদর্শনগুলোকে আর আখেরাতের সাক্ষাৎকে মিথ্যে জেনে অস্বীকার করে তাদের ‘আমালগুলো নিস্ফল। তারা যা করত সে অনুযায়ী প্রতিফল ছাড়া তারা কী আর আশা করতে পারে? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1102,7,148,'মূসার অনুপস্থিতিতে তার জাতির লোকেরা তাদের অলঙ্কারের সাহায্যে গো-বৎসের একটা অবয়ব তৈরি করল যা গরুর ন্যায় ‘হাম্বা’ আওয়াজ করত। তারা কি দেখল না যে তা তাদের সঙ্গে কথা বলে না, আর তাদেরকে পথও দেখায় না। কিন্তু তা সত্ত্বেও তারা তাকে মা‘বূদ হিসেবে গ্রহণ করল। তারা ছিল যালিম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1103,7,149,'অতঃপর যখন তারা অনুতপ্ত হল আর বুঝতে পারল যে তারা পথহারা হয়ে গেছে, তারা বলল, ‘আমাদের প্রতিপালক যদি আমাদের উপর দয়া না করেন আর আমাদেরকে ক্ষমা না করেন তাহলে আমরা অবশ্যই ক্ষতিগ্রস্তদের অন্তর্ভুক্ত হয়ে যাব।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1104,7,150,'রপর মূসা যখন ক্ষোভ আর ক্রোধ নিয়ে তার লোকজনের কাছে ফিরে এল তখন বলল, ‘আমার অনুপস্থিতিতে তোমরা কত নিকৃষ্টভাবেই না আমার প্রতিনিধির দায়িত্ব পালন করেছ! তোমাদের প্রতিপালকের নির্দেশ লাভের আগেই তোমরা তাড়াহুড়ো করে বসলে?’ অতঃপর সে ফলকগুলো ছুঁড়ে ফেলে দিল, আর নিজ ভাইয়ের মাথার চুল ধরে নিজের কাছে টেনে নিয়ে আসল। হারূন বলল, ‘হে আমার মায়ের পেটের ভাই! লোকেরা আমাকে দুর্বল করে দিয়েছিল আর আমাকে হত্যা করতে উদ্যত হয়েছিল, কাজেই আমার ব্যাপারে দুশমনদেরকে আনন্দিত হওয়ার সুযোগ দিও না আর আমাকে যালিম সম্প্রদায়ের মধ্যে গণ্য করো না।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1105,7,151,'মূসা বলল, ‘হে আমার প্রতিপালক! আমাকে আর আমার ভাইকে ক্ষমা কর, আর আমাদেরকে তোমার রহমতের মধ্যে দাখিল কর, তুমিই সর্বাধিক বড় দয়াবান।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1106,7,152,'যারা গো-বৎসকে (মা‘বূদ হিসেবে) গ্রহণ করেছে, পার্থিব জীবনে তাদের উপর তাদের প্রতিপালকের পক্ষ থেকে রাগ ও লাঞ্ছনা পতিত হবে। মিথ্যা রচনাকারীদেরকে আমরা এভাবেই শাস্তি দিয়ে থাকি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1107,7,153,'আর যারা অসৎ কাজ করে, অতঃপর তাওবাহ করে আর ঈমান আনে তাহলে এ সবের পর তোমার প্রতিপালক অবশ্যই বড়ই ক্ষমাশীল, পরম দয়ালু। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1108,7,154,'মূসার ক্রোধ যখন ঠান্ডা হল তখন সে ফলকগুলো উঠিয়ে নিল, সেগুলোর লেখায় ছিল ঐসব লোকেদের জন্য পথ নির্দেশ আর রহমত যারা তাদের প্রতিপালককে ভয় করে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1109,7,155,'মূসা তার জাতির সত্তর জন লোককে বাছাই করল আমার নির্ধারিত সময়ে উপস্থিত হওয়ার জন্য। যখন ভূমিকম্প তাদের উপর আঘাত হানল তখন সে বলল, ‘হে আমার প্রতিপালক! তুমি ইচ্ছে করলে তো এদেরকে আর আমাকেও আগেই ধ্বংস করে দিতে পারতে! আমাদের মধ্যেকার নির্বোধেরা যা করেছে তার জন্য কি তুমি আমাদেরকে ধ্বংস করে দেবে? ওটা তো কেবল তোমার পরীক্ষা, যাকে চাও তদ্দবারা পথভ্রষ্ট কর, আর যাকে চাও সত্য পথে পরিচালিত কর, তুমি আমাদের প্রতি দয়া কর, তুমিই তো সবচেয়ে বেশী ক্ষমাশীল।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1110,7,156,'‘আমাদের জন্য এ দুনিয়ার কল্যাণ লিখে দাও আর পরকালেও। আমরা তোমার দিকেই প্রত্যাবর্তন করছি।’ আল্লাহ বললেন, ‘শাস্তি তো আমি যাকে ইচ্ছে দেই, আর আমার রহমত সব বিষয়ে পরিব্যাপ্ত আর তা আমি তাদের জন্য লিখে দিব যারা তাক্বওয়া অবলম্বন করবে, যাকাত দিবে আর যারা আমার নিদর্শনাবলীতে বিশ্বাসী হবে।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1111,7,157,'যারা প্রেরিত উম্মী নাবীকে অনুসরণ করবে যা তাদের কাছে রক্ষিত তাওরাত ও ইনজীলে তারা লিখিত পাবে। সে তাদেরকে সৎকাজের নির্দেশ দেয়, অসৎ কাজ করতে নিষেধ করে, পবিত্র বস্তুসমূহ তাদের জন্য হালাল করে, অপবিত্র বস্তুগুলো তাদের জন্য নিষিদ্ধ করে, তাদের থেকে গুরুভার সরিয়ে দেয় আর সেই শৃক্মখল (হালাল-হারামের বানোয়াট বিধি-নিষেধ) যাতে ছিল তারা বন্দী। কাজেই যারা তার প্রতি ঈমান আনে, তাকে সম্মান প্রদর্শন করে, তাকে সাহায্য-সহযোগিতা করে আর তার উপর অবতীর্ণ আলোর অনুসরণ করে, তারাই হচ্ছে সফলকাম।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1112,7,158,'বল, হে মানুষ! আমি তোমাদের সকলের জন্য আল্লাহর রসূল, (সেই আল্লাহর) যিনি আকাশসমূহ আর পৃথিবীর রাজত্বের মালিক, তিনি ছাড়া সত্যিকারের কোন ইলাহ নেই, তিনিই জীবিত করেন আর মৃত্যু আনেন। কাজেই তোমরা ঈমান আন আল্লাহর প্রতি ও তাঁর প্রেরিত সেই উম্মী বার্তাবাহকের প্রতি যে নিজে আল্লাহর প্রতি ও তাঁর যাবতীয় বাণীর প্রতি বিশ্বাস করে, তোমরা তাঁর অনুসরণ কর যাতে তোমরা সঠিক পথ পেতে পার। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1113,7,159,'মূসার সম্প্রদায়ের মধ্যে এক দল লোক আছে যারা সত্য বিধান অনুযায়ী (অন্যকে) পথ দেখায় আর সত্য বিধান অনুযায়ী ইনসাফ করে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1114,7,160,'আমি তাদেরকে বারটি গোত্রে বা জাতিতে বিভক্ত করেছিলাম। মূসার জাতির লোকেরা যখন তার কাছে পানি চাইল তখন আমি মূসার প্রতি ওয়াহী অবতীর্ণ করলাম যে, তোমার লাঠি দিয়ে পাথরে আঘাত কর। এর ফলে তাত্থেকে বারটি ঝর্ণা উৎসারিত হল। প্রত্যেক গোত্র তাদের পানি পানের স্থান চিনে নিল, তাদেরকে মেঘের ছায়ায় আশ্রয় দিলাম, তাদের উপর আমি মান্না ও সালওয়া অবতীর্ণ করলাম আর বললাম, ‘তোমাদেরকে আমি যে জীবিকা দিয়েছি তাত্থেকে পবিত্র বস্তুগুলো আহার কর।’ (কিন্তু তারা আমার নির্দেশ অমান্য করে) আমার প্রতি কোন যুলম করেনি, প্রকৃতপক্ষে তারা নিজেদের উপরই যুলম করছিল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1115,7,161,'স্মরণ কর, তাদেরকে যখন বলা হয়েছিল- ‘‘এই জনপদে বাস কর, যেখানে ইচ্ছে আহার কর আর বল ‘(আমাদেরকে) ক্ষমা কর’, অবনত মস্তকে দ্বারে প্রবেশ কর, (তাহলে) তোমাদের ত্রুটিগুলো ক্ষমা করে দেব, আর নেককার লোকদেরকে অতিরিক্ত আরো দেব।’’');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1116,7,162,'কিন্তু তাদের মধ্যেকার যালিম লোকেরা তাদেরকে বলা কথাকে বদলে ফেললো। কাজেই তাদের উপর আকাশ থেকে আযাব পাঠালাম, সীমালঙ্ঘনে তাদের লিপ্ত থাকার কারণে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1117,7,163,'তাদেরকে জিজ্ঞেস কর ঐ জনবসতি সম্পর্কে যা সমুদ্রের উপকূলে বিদ্যমান ছিল। তারা শনিবারের সীমালঙ্ঘন করেছিল। শনিবার পালনের দিন মাছগুলো প্রকাশ্যতঃ তাদের নিকটে আসত। আর যেদিন শনিবারের অনুষ্ঠান থাকত না সেদিন সেগুলো আসত না। এটা হত এজন্য যে, তারা অবাধ্যতায় লিপ্ত থাকার কারণে তাদেরকে পরীক্ষায় ফেলে দিয়েছিলাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1118,7,164,'স্মরণ কর, যখন তাদের একদল বলেছিল- ‘তোমরা এমন লোকদেরকে কেন নাসীহাত করছ যাদেরকে আল্লাহ ধ্বংস করবেন অথবা কঠিন শাস্তি দিবেন’। নাসীহাতকারীগণ বলেছিল, ‘তোমাদের প্রতিপালকের নিকট (দায়িত্ব পালন না করার) অভিযোগ থেকে মুক্ত হওয়ার জন্য আর তারা যাতে তাক্বওয়া অবলম্বন করে।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1119,7,165,'যে উপদেশ তাদেরকে দেয়া হচ্ছিল তারা যখন তা ভুলে গেল তখন যারা মন্দ কাজ থেকে (অন্যদেরকে) নিষেধ করত তাদেরকে রক্ষা করলাম। আর যালিমদেরকে কঠিন আযাবে পাকড়াও করলাম যেহেতু তারা অবাধ্যতায় লিপ্ত ছিল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1120,7,166,'যখন তারা চরম ধৃষ্টতা দেখিয়ে ঐ কাজগুলো করতে থাকল যা করতে তাদেরকে নিষেধ করা হয়েছিল, তখন তাদের উদ্দেশে বললাম, ‘ঘৃণিত অপমানিত, বানরে রূপান্তরিত হয়ে যাও’। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1121,7,167,'স্মরণ কর, যখন তোমার প্রতিপালক ঘোষণা করলেন যে, আমি অবশ্যই ক্বিয়ামাত পর্যন্ত বানী ইসরাঈলের উপর এমন লোকদেরকে পাঠাব যারা তাদেরকে কঠিন শাস্তি দিতে থাকবে, তোমার প্রতিপালক তো শাস্তিদানে খুবই তৎপর, আর তিনি (মন্দ পরিত্যাগকারীদের জন্য) অবশ্যই বড়ই ক্ষমাশীল, পরম দয়ালু। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1122,7,168,'পৃথিবীতে আমি তাদেরকে নানা দলে বিভক্ত করে দিয়েছিলাম, তাদের মধ্যে কিছু দল ছিল সৎ, কতক দল অন্য রকম এবং সুখ আর দুখ দিয়ে তাদেরকে পরীক্ষা করেছিলাম যাতে তারা (আল্লাহর নির্দেশের পথে) ফিরে আসে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1123,7,169,'তাদের পরে (পাপিষ্ঠ) বংশধরগণ তাদের স্থলাভিষিক্ত হয়ে কিতাবের উত্তরাধিকারী হয় যারা দুনিয়ার নিকৃষ্ট স্বার্থ গ্রহণ করে আর বলে, ‘(আমরা যা কিছুই করি না কেন) আমাদেরকে ক্ষমা করা হবে’। আর দুনিয়ার স্বার্থ তাদের সামনে আসলে আবার তা গ্রহণ করে নেয়। (তাওরাত) কিতাবে কি তাদের নিকট থেকে এ অঙ্গীকার নেয়া হয়নি যে তারা আল্লাহ সম্বন্ধে প্রকৃত সত্য ছাড়া বলবে না? তারা তো ঐ কিতাবে যা আছে তা পাঠ করেও থাকে। যারা তাক্বওয়া অবলম্বন করে তাদের জন্য পরকালের আবাসই উত্তম, তোমরা কি বুঝবে না? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1124,7,170,'যারা কিতাবকে শক্তভাবে আঁকড়ে ধরে, নামায প্রতিষ্ঠা করে, আমি (এসব) সৎকর্মশীলদের কর্মফল কখনো বিনষ্ট করি না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1125,7,171,'স্মরণ কর, আমি যখন পাহাড়কে বানী ইসরাঈলদের উপর তুলে ধরলাম তা যেন একটা সামিয়ানা। তারা ভাবল, ওটা তাদের উপর বুঝি পতিত হবে। (এমত অবস্থায় তাদেরকে বললাম) ‘তোমাদেরকে যা দিলাম তা শক্তভাবে ধারণ কর আর তাতে যা আছে তা মনে রেখ, যাতে তোমরা তাক্বওয়া লাভ করতে পার’। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1126,7,172,'স্মরণ কর, যখন তোমার প্রতিপালক আদম সন্তানদের পৃষ্ঠ হতে তাদের বংশধরদের বের করলেন আর তাদেরকেই সাক্ষী বানিয়ে জিজ্ঞেস করলেন, ‘আমি কি তোমাদের প্রতিপালক নই?’ তারা বলল, ‘হ্যাঁ; এ ব্যাপারে আমরা সাক্ষ্য দিচ্ছি।’ (এটা এজন্য করা হয়েছিল) যাতে তোমরা ক্বিয়ামাতের দিন না বল যে, ‘এ সম্পর্কে আমরা একেবারেই বে-খবর ছিলাম’। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1127,7,173,'অথবা তোমরা এ কথা না বল যে, ‘পূর্বে আমাদের পিতৃ-পুরুষরাই শিরক করেছে আর তাদের পরে আমরা তাদেরই সন্তানাদি (যা করতে দেখেছি তাই করছি) তাহলে ভ্রান্ত পথের অনুসারীরা যা করেছে তার জন্য কি আপনি আমাদেরকে ধ্বংস করবেন?’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1128,7,174,'এভাবে আমি নিদর্শনসমূহ বিশদভাবে বিবৃত করছি, আর হয়ত তারা (আমার পথে) ফিরে আসবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1129,7,175,'তাদেরকে ঐ লোকের সংবাদ পড়ে শোনাও যাকে আমি আমার নিদর্শনসমূহ প্রদান করেছিলাম। কিন্তু সে সেগুলোকে এড়িয়ে যায়। অতঃপর শয়ত্বান তাকে অনুসরণ করে, ফলে সে পথভ্রষ্টদের দলে শামিল হয়ে যায়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1130,7,176,'আমি ইচ্ছে করলে আমার নিদর্শনের মাধ্যমে তাকে অবশ্যই উচ্চতর মর্যাদা দিতাম। কিন্তু সে দুনিয়ার প্রতিই ঝুঁকে পড়ল আর তার প্রবৃত্তির অনুসরণ করল। তাই তার দৃষ্টান্ত হল কুকুরের দৃষ্টান্তের মত। যদি তুমি তার উপর বোঝা চাপাও তাহলে জিভ বের করে হাঁপাতে থাকে এবং তাকে ছেড়ে দিলেও জিভ বের করে হাঁপাতে থাকে। এটাই হল ঐ সম্প্রদায়ের উদাহরণ যারা আমার আয়াতসমূহকে মিথ্যে মনে করে অমান্য করে। তুমি এ কাহিনী শুনিয়ে দাও যাতে তারা চিন্তা-ভাবনা করে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1131,7,177,'যে সম্প্রদায় আমার আয়াতগুলোকে মিথ্যে মনে করে প্রত্যাখ্যান করে আর নিজেদের উপর যুলম করতে থাকে তাদের উদাহরণ কতই না মন্দ! ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1132,7,178,'আল্লাহ যাকে হিদায়াত করেন সেই হিদায়াত লাভ করে, আর যাকে পথভ্রষ্ট করেন তারাই হয় ক্ষতিগ্রস্ত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1133,7,179,'আমি বহু সংখ্যক জ্বীন আর মানুষকে জাহান্নামের জন্য সৃষ্টি করেছি, তাদের হৃদয় আছে কিন্তু তা দিয়ে উপলব্ধি করে না, তাদের চোখ আছে কিন্তু তা দিয়ে দেখে না, তাদের কান আছে কিন্তু তা দিয়ে শোনে না, তারা জন্তু-জানোয়ারের মত, বরং তার চেয়েও পথভ্রষ্ট, তারা একেবারে বে-খবর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1134,7,180,'সুন্দর যত নাম সবই আল্লাহর জন্য। কাজেই তাঁকে ডাক ঐ সব নামের মাধ্যমে। যারা তার নামের মধ্যে বিকৃতি ঘটায় তাদেরকে পরিত্যাগ কর। তারা যা করছে তার ফল তারা শীঘ্র পাবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1135,7,181,'আমি যাদেরকে সৃষ্টি করেছি তাদের মধ্যে একদল আছে যারা সঠিকভাবে পথ নির্দেশ দেয়, আর তার মাধ্যমেই সুবিচার করে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1136,7,182,'রা আমার আয়াতগুলোকে প্রত্যাখ্যান করে আমি তাদেরকে ধাপে ধাপে এমনভাবে ধ্বংসের দিকে নিয়ে যাই যে, তারা টেরও করতে পারে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1137,7,183,'আমি তাদেরকে অবকাশ ও সুযোগ দেই, আমার কুশলী ব্যবস্থাপনা অত্যন্ত মযবুত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1138,7,184,'তারা কি চিন্তা-ভাবনা করে না সে তাদের সঙ্গী (আমার রসূল) তো উম্মাদ নয়, সে তো প্রকাশ্য এক সতর্ককারী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1139,7,185,'তারা কি আসমান-যমীনের রাজত্বে আর আল্লাহ যে সব বস্তু সৃষ্টি করেছেন তাতে কিছুই দেখে না? তারা কি চিন্তা করে না যে হয়ত তাদের জীবনের মেয়াদ নিকটেই এসে গেছে? এরপর তারা কোন বাণীর উপর ঈমান আনবে? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1140,7,186,'আল্লাহ যাকে পথভ্রষ্ট করেন তার জন্য কোন হিদায়াত নেই। তিনি তাদেরকে তাদের বিদ্রোহী ভূমিকায় বিভ্রান্ত হয়ে ঘুরপাক খেতে ছেড়ে দেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1141,7,187,'তারা তোমাকে ক্বিয়ামাত সম্পর্কে জিজ্ঞেস করে কখন তা সংঘটিত হবে। বল, ‘এ বিষয়ে জ্ঞান রয়েছে আমার প্রতিপালকের নিকট। তিনি ছাড়া কেউ প্রকাশ করতে পারে না কখন তা ঘটবে। আসমান ও যমীনে তা হবে বড় এক কঠিন দিন। আকস্মিকভাবে তা তোমাদের উপর এসে পড়বে।’ লোকেরা তোমাকে এ সম্পর্কে এমনভাবে জিজ্ঞেস করছে যেন তুমি আগ্রহ সহকারে এটার খোঁজে ব্যস্ত আছ। বল, ‘এ বিষয়ের জ্ঞান তো শুধু আল্লাহরই নিকট আছে। কিন্তু (এ সত্যটা) অধিকাংশ লোকই জানে না।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1142,7,188,'বল, আল্লাহ যা ইচ্ছে করেন তা ছাড়া আমার নিজের ভাল বা মন্দ করার কোন ক্ষমতা আমার নেই। আমি যদি অদৃশ্যের খবর জানতাম তাহলে নিজের জন্য অনেক বেশি ফায়দা হাসিল করে নিতাম, আর কোন প্রকার অকল্যাণই আমাকে স্পর্শ করত না। যারা ঈমান আনবে আমি সেই সম্প্রদায়ের প্রতি সতর্ককারী ও সুসংবাদদাতা ছাড়া অন্য কিছু নই। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1143,7,189,'তিনিই তোমাদেরকে এক ব্যক্তি হতে সৃষ্টি করেছেন আর তাত্থেকে তার জোড়া সৃষ্টি করেছেন যাতে সে তার কাছে শান্তি পায়। যখন সে স্ত্রীর সাথে সঙ্গত হয় তখন সে লঘু গর্ভধারণ করে আর তা নিয়ে চলাফেরা করে। গর্ভ যখন ভারী হয়ে যায় তখন উভয়ে তাদের প্রতিপালক আল্লাহকে ডেকে বলে, ‘যদি তুমি আমাদেরকে (গঠন ও স্বভাবে) ভাল সন্তান দান কর তাহলে আমরা কৃতজ্ঞ থাকব।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1144,7,190,'যখন তিনি তাদেরকে সর্বাঙ্গ-সুন্দর সন্তান দান করেন, তখন তারা তাদেরকে যা দেয়া হয় তাতে অন্যকে আল্লাহর শরীক গণ্য করে। তারা যাদেরকে শরীক গণ্য করে আল্লাহ তাদের থেকে অনেক ঊর্ধ্বে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1145,7,191,'তারা কি এমন কিছুকে শরীক করে যারা কিছুই সৃষ্টি করে না? বরং তাদেরকেই সৃষ্টি করা হয়েছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1146,7,192,'তারা না পারে তাদেরকে (অর্থাৎ তাদের ‘ইবাদাতকারীদেরকে) সাহায্য করতে, না পারে নিজেদেরকে সাহায্য করতে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1147,7,193,'তোমরা তাদেরকে যদি সত্যপথে চলার জন্য ডাক, তারা তোমাদের অনুসরণ করবে না। তাদেরকে ডাক কিংবা চুপচাপ থাক, তোমাদের জন্য উভয়ই সমান। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1148,7,194,'আল্লাহ ছাড়া যাদেরকে তোমরা ডাক তারা তোমাদের মতই বান্দাহ্। (ঠিক আছে) তাদেরকে ডাকতে থাক, তোমরা যদি সত্যবাদী হয়ে থাক তাহলে তারা তোমাদের ডাকে সাড়া দিক। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1149,7,195,'তাদের কি পা আছে যা দিয়ে তারা চলাফেরা করে? তাদের কি হাত আছে যা দিয়ে তারা ধরে? তাদের কি চোখ আছে যা দিয়ে তারা দেখে? তাদের কি কান আছে যা দিয়ে তারা শোনে? বল, তোমরা যাদেরকে আল্লাহর শরীক করছ তাদেরকে আহবান কর, আর আমার বিরুদ্ধে ষড়যন্ত্র কর এবং আমাকে অবকাশ দিও না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1150,7,196,'আল্লাহই হলেন আমার অভিভাবক যিনি কিতাব অবতীর্ণ করেছেন, আর তিনিই সৎকর্মশীলদের অভিভাবকত্ব করে থাকেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1151,7,197,'কে (অর্থাৎ আল্লাহকে) ছাড়া যাদেরকে তোমরা ডাক, তারা তোমাদেরকে সাহায্য করার কোন ক্ষমতা রাখে না, পারে না নিজেদেরকেও সাহায্য করতে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1152,7,198,'তাদেরকে যদি সঠিক পথের দিকে ডাক, তারা শোনে না, তুমি দেখ যে তারা তোমার দিকে তাকিয়ে আছে কিন্তু আসলে তারা কিছুই দেখতে পায় না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1153,7,199,'ক্ষমাশীলতার নীতি অবলম্বন কর, সত্য-সঠিক কাজের আদেশ দাও আর জাহিলদেরকে এড়িয়ে চল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1154,7,200,'শয়ত্বান যদি উস্কানি দিয়ে তোমাকে প্ররোচিত করতে চায় তাহলে আল্লাহর আশ্রয় প্রার্থনা কর, তিনি হলেন সর্বশ্রোতা, সর্বজ্ঞ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1155,7,201,'যারা তাক্বওয়া অবলম্বন করে শায়ত্বনের স্পর্শে তাদের মনে কুমন্ত্রণা জাগলে তারা আল্লাহকে স্মরণ করে, তখন তাদের ঈমান-চক্ষু খুলে যায়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1156,7,202,'কিন্তু তাদের (অর্থাৎ শায়ত্বনের) সঙ্গী-সাথীরা (কাফির ও মুনাফিক্বরা) তাদেরকে ভ্রান্ত পথে নিয়ে যায় আর এ ব্যাপারে চেষ্টার কোন ত্রুটি করে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1157,7,203,'যখন তুমি তাদের কাছে কোন নিদর্শন হাজির কর না, তখন তারা বলে, তুমি নিজেই একটা নিদর্শন বেছে নাও না কেন? বল, ‘আমার প্রতিপালকের পক্ষ হতে আমার প্রতি যা ওয়াহী করা হয় আমি তো তারই অনুসরণ করি। এটা তোমাদের রবেবর পক্ষ হতে মু’মিন সম্প্রদায়ের জন্য উজ্জ্বল আলো, হিদায়াত ও রহমত।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1158,7,204,'যখন কুরআন পাঠ করা হয় তখন তোমরা তা মনোযোগের সঙ্গে শ্রবণ কর আর নীরবতা বজায় রাখ যাতে তোমাদের প্রতি রহম করা হয়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1159,7,205,'তোমার প্রতিপালককে মনে মনে বিনয়ের সঙ্গে ভয়-ভীতি সহকারে অনুচ্চস্বরে সকাল-সন্ধ্যায় স্মরণ কর আর উদাসীনদের দলভুক্ত হয়ো না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1160,7,206,'যারা তোমার প্রতিপালকের নিকট আছে তারা তাঁর ‘ইবাদাত করার ব্যাপারে ঔদ্ধত্য প্রকাশ করে না, তারা তাঁর মহিমা ঘোষণা করে আর তাঁর জন্য সাজদাহয় অবনত হয়। [সাজদাহ] ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1161,8,1,'তারা তোমাকে যুদ্ধে প্রাপ্ত সম্পদ সম্পর্কে জিজ্ঞেস করছে। বল, ‘যুদ্ধে প্রাপ্ত সম্পদ হচ্ছে আল্লাহ ও তাঁর রসূলের; কাজেই তোমরা আল্লাহকে ভয় কর আর নিজেদের সম্পর্ককে সুষ্ঠু সুন্দর ভিত্তির উপর প্রতিষ্ঠিত কর। তোমরা যদি মু’মিন হয়ে থাক তবে তোমরা আল্লাহ ও তাঁর রসূলের আনুগত্য কর।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1162,8,2,'মু’মিন তো তারাই আল্লাহর কথা আলোচিত হলেই যাদের অন্তর কেঁপে উঠে, আর তাদের কাছে যখন তাঁর আয়াত পঠিত হয়, তখন তা তাদের ঈমান বৃদ্ধি করে আর তারা তাদের প্রতিপালকের উপর নির্ভর করে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1163,8,3,'তারা নামায ক্বায়িম করে, আর আমি তাদেরকে যে জীবিকা দিয়েছি তাত্থেকে ব্যয় করে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1164,8,4,'এসব লোকেরাই হল প্রকৃত মু’মিন। এদের জন্য এদের প্রতিপালকের নিকট আছে নানান মর্যাদা, ক্ষমা আর সম্মানজনক জীবিকা। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1165,8,5,'(তারা যেমন প্রকৃত মু’মিন) ঠিক তেমনি প্রকৃতভাবেই তোমার প্রতিপালক তোমাকে তোমার ঘর হতে বের করে এনেছিলেন যদিও মু’মিনদের একদল তা পছন্দ করেনি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1166,8,6,'সত্য স্পষ্ট হওয়ার পরও তারা তোমার সঙ্গে বাদানুবাদে লিপ্ত হয়েছিল, (তাদের অবস্থা দেখে মনে হচ্ছিল যে,) তারা যেন চেয়ে চেয়ে দেখছিল যে, তাদেরকে মৃত্যুর দিকে তাড়িয়ে নেয়া হচ্ছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1167,8,7,'স্মরণ কর, যখন আল্লাহ তোমাদেরকে প্রতিশ্রুতি দিয়েছিলেন যে, দু’টি দলের মধ্যে একটি তোমরা পাবে, আর তোমরা চেয়েছিলে যেন নিরস্ত্র দলটি তোমরা লাভ কর আর আল্লাহ চেয়েছিলেন তাঁর বাণী দ্বারা সত্যকে সত্যরূপে প্রতিষ্ঠিত করতে আর কাফিরদের জড় কেটে দিতে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1168,8,8,'যাতে তিনি সত্যকে সত্য হিসেবে প্রতিষ্ঠিত করেন আর মিথ্যেকে মিথ্যে প্রমাণিত করেন, যদিও তা পাপীদের কাছে পছন্দনীয় নয়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1169,8,9,'স্মরণ কর, যখন তোমরা তোমাদের প্রতিপালকের নিকট সাহায্য প্রার্থনা করছিলে তখন তিনি তোমাদেরকে জবাব দিলেন, ‘আমি তোমাদেরকে এক হাজার ফেরেশতা দিয়ে সাহায্য করব যারা পর পর আসবে।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1170,8,10,'আর আল্লাহ যে এটা করেছিলেন তার উদ্দেশ্য তোমাদেরকে সুসংবাদ দান ছাড়া অন্য কিছু নয় আর যাতে এর মাধ্যমে তোমাদের অন্তর প্রশান্তি লাভ করে। কেননা, সাহায্য তো একমাত্র আল্লাহর নিকট থেকেই আসে। আল্লাহ তো মহাপরাক্রমশালী, মহাবিজ্ঞানী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1171,8,11,'স্মরণ কর, যখন আল্লাহ তাঁর নিকট হতে প্রশান্তি ধারা হিসেবে তোমাদেরকে তন্দ্রায় আচ্ছন্ন করেছিলেন, আকাশ হতে তোমাদের উপর বৃষ্টিধারা বর্ষণ করেছিলেন তোমাদেরকে তা দিয়ে পবিত্র করার জন্য। তোমাদের থেকে শয়ত্বানী পংকিলতা দূর করার জন্য, তোমাদের দিলকে মজবুত করার জন্য আর তা দিয়ে তোমাদের পায়ের ভিত শক্ত করার জন্য। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1172,8,12,'স্মরণ কর যখন তোমার প্রতিপালক ফেরেশতাদের প্রতি ওয়াহী পাঠিয়েছিলেন, ‘আমি তোমাদের সঙ্গেই আছি; অতএব মু’মিনদেরকে তোমরা দৃঢ়পদ রেখ’। অচিরেই আমি কাফিরদের দিলে ভীতি সঞ্চার করব, কাজেই তাদের স্কন্ধে আঘাত হান, আঘাত হান প্রত্যেকটি আঙ্গুলের গিঁটে গিঁটে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1173,8,13,'এর কারণ হল, তারা আল্লাহ ও রসূলের বিরোধিতা করে আর যারা আল্লাহ ও তাঁর রসূলের বিরোধিতা করবে (তাদের জেনে রাখা দরকার) আল্লাহ শাস্তিদানে বড়ই কঠোর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1174,8,14,'এটাই তোমাদের শাস্তি, অতএব তার স্বাদ গ্রহণ কর, কাফিরদের জন্য আছে আগুনের (জাহান্নামের) শাস্তি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1175,8,15,'হে মু’মিনগণ! তোমরা যখন যোদ্ধা-বাহিনীরূপে কাফিরদের সম্মুখীন হও, তখন তাদেরকে পৃষ্ঠপ্রদর্শন করবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1176,8,16,'এমন দিনে যুদ্ধে কৌশল অবলম্বন বা নিজ দলের সঙ্গে মিলিত হওয়ার উদ্দেশ্য ছাড়া কেউ তাদেরকে পৃষ্ঠপ্রদর্শন করলে সে তো আল্লাহর গজবে পরিবেষ্টিত হয়ে পড়ল, জাহান্নামই তার ঠিকানা আর তা কতই না নিকৃষ্ট প্রত্যাবর্তনস্থল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1177,8,17,'(আসল ব্যাপার হল) তোমরা তাদেরকে হত্যা করনি, বরং আল্লাহই তাদেরকে হত্যা করেছেন, তুমি যখন নিক্ষেপ করছিলে তাতো তুমি নিক্ষেপ করনি, বরং আল্লাহই নিক্ষেপ করেছিলেন যাতে তিনি মু’মিনদেরকে এক সুন্দরতম পরীক্ষায় সফলতার সঙ্গে উত্তীর্ণ করতে পারেন। আল্লাহ হলেন সর্বশ্রোতা, সর্বজ্ঞ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1178,8,18,'আমার এ ব্যবস্থা তোমাদের জন্য, (আর কাফিরদের সম্পর্কে কথা এই যে) আল্লাহ কাফিরদের ষড়যন্ত্রকে অকেজো করে দেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1179,8,19,'(ওহে কাফিরগণ!) তোমরা মীমাংসা চাচ্ছিলে, মীমাংসা তো তোমাদের কাছে এসে গেছে; আর যদি তোমরা (অন্যায় থেকে) বিরত হও, তবে তা তোমাদের জন্যই কল্যাণকর, তোমরা যদি আবার (অন্যায়) কর, আমিও আবার শাস্তি দিব, তোমাদের দল-বাহিনী সংখ্যায় অধিক হলেও তোমাদের কোন উপকারে আসবে না এবং আল্লাহ তো মু’মিনদের সঙ্গে আছেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1180,8,20,'ওহে বিশ্বাসীগণ! আল্লাহ ও তাঁর রসূলের আনুগত্য কর এবং আদেশ শোনার পর তা অমান্য কর না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1181,8,21,'তোমরা তাদের মত হয়ো না যারা বলেছিল, ‘আমরা শুনলাম’; প্রকৃতপক্ষে তারা শোনেনি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1182,8,22,'আল্লাহর নিকট সবচেয়ে নিকৃষ্ট জীব হচ্ছে যারা (হক্ব কথা শুনার ব্যাপারে) বধির এবং (হক্ব কথা বলার ব্যাপারে) বোবা, যারা কিছুই বোঝে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1183,8,23,'আল্লাহ যদি দেখতেন যে, তাদের মধ্যে কোন ভাল গুণ নিহিত আছে তবে তিনি তাদেরকে শুনবার তাওফীক দিতেন। আর (গুণ না থাকা অবস্থায়) তিনি যদি তাদেরকে শুনতে দিতেন তাহলে তারা উপেক্ষা করে মুখ ফিরিয়ে নিত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1184,8,24,'ওহে বিশ্বাসীগণ! তোমরা আল্লাহ ও তাঁর রসূলের ডাকে সাড়া দাও যখন তোমাদেরকে ডাকা হয় (এমন বিষয়ের দিকে) যা তোমাদের মাঝে জীবন সঞ্চার করে, আর জেনে রেখ যে আল্লাহ মানুষ ও তার অন্তরের মাঝে প্রতিবন্ধক হয়ে যান আর তোমাদেরকে তাঁর কাছেই একত্রিত করা হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1185,8,25,'সতর্ক থাক সেই শাস্তি হতে যা বিশেষভাবে তোমাদের যালিম লোকেদেরকেই আক্রমন করবে না। আর জেনে রেখ যে আল্লাহ শাস্তিদানে খুবই কঠোর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1186,8,26,'স্মরণ কর সে সময়ের কথা যখন  তোমরা ছিলে সংখ্যায়  অল্প, দুনিয়াতে তোমাদেরকে দুর্বল হিসেবে গণ্য করা হত। তোমরা আশঙ্কা করতে যে, মানুষেরা তোমাদের কখন না হঠাৎ ধরে নিয়ে যায়। এমন অবস্থায় তিনি তোমাদেরকে আশ্রয় দিলেন, তাঁর সাহায্য দিয়ে তোমাদেরকে শক্তিশালী করলেন, উত্তম জীবিকা দান করলেন যাতে  তোমরা (তাঁর নির্দেশ পালনের মাধ্যমে) কৃতজ্ঞতা প্রকাশ কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1187,8,27,'হে মু’মিনগণ! তোমরা জেনে বুঝে আল্লাহ ও তাঁর রসূলের সঙ্গে বিশ্বাসঘাতকতা করো না, আর যে বিষয়ে তোমরা আমানাত প্রাপ্ত হয়েছ তাতেও বিশ্বাস ভঙ্গ করো না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1188,8,28,'জেনে রেখ, তোমাদের ধন-সম্পদ আর সন্তান- সন্ততি হচ্ছে পরীক্ষার সামগ্রী মাত্র। (এ পরীক্ষায় যারা উত্তীর্ণ হবে তাদের জন্য) আল্লাহর নিকট রয়েছে মহাপুরস্কার। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1189,8,29,'ওহে ঈমানদারগণ! তোমরা যদি আল্লাহকে ভয় কর তাহলে তিনি তোমাদেরকে ভাল ও মন্দের মধ্যে পার্থক্য করার শক্তি প্রদান করবেন, তোমাদের দোষ-ত্রুটি দূর করে দিবেন, তোমাদেরকে ক্ষমা করবেন, আল্লাহ বড়ই অনুগ্রহশীল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1190,8,30,'স্মরণ কর, সেই সময়ের কথা যখন কাফিরগণ তোমাকে বন্দী করার কিংবা হত্যা করার কিংবা দেশ থেকে বের করে দেয়ার জন্য ষড়যন্ত্র করে। তারা চক্রান্ত করে আর আল্লাহও কৌশল করেন। আল্লাহই হচ্ছেন সর্বশ্রেষ্ঠ কৌশলী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1191,8,31,'তাদের কাছে যখন আমার আয়াত পাঠ করা হয় তখন তারা বলে, ‘শুনলাম তো, ইচ্ছে করলে এ রকম কথা আমরাও বলতে পারি, এগুলো তো আগে কালের কেচ্ছা কাহিনী ছাড়া আর কিছুই না।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1192,8,32,'স্মরণ কর, যখন তারা বলেছিল, ‘হে আল্লাহ! এটা যদি তোমার নিকট থেকে (প্রেরিত) সত্য (দ্বীন) হয় তাহলে আমাদের উপর আসমান থেকে পাথর বর্ষণ কর, কিংবা আমাদের  উপর কোন যন্ত্রণাদায়ক শাস্তি নিয়ে এসো।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1193,8,33,'তুমি তাদের  মাঝে  থাকা অবস্থায় আল্লাহ তাদেরকে শাস্তি দিবেন না এবং যখন তারা ক্ষমা প্রার্থনা করতে থাকবে এরূপ অবস্থায়ও আল্লাহ তাদেরকে শাস্তি দিবেন না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1194,8,34,'আল্লাহ যে তাদেরকে শাস্তি দিবেন না এ ব্যাপারে ওজর পেশ করার জন্য তাদের কাছে কী আছে যখন তারা (মানুষদেরকে) মাসজিদুল হারাম-এর পথে বাধা দিচ্ছে? তারা তো ওর (প্রকৃত) মুতাওয়াল্লী নয়, মুত্তাকীরা ছাড়া কেউ তার মুতাওয়াল্লী হতে পারে না, কিন্তু তাদের অধিকাংশ লোক এ সম্পর্কে অবগত নয়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1195,8,35,'আল্লাহর ঘরের নিকট তাদের নামায হাত তালি মারা আর শিশ দেয়া ছাড়া আর কিছুই না, (এসব অপরাধে লিপ্ত ব্যক্তিদেরকে বলা হবে) ‘‘আযাব ভোগ কর যেহেতু তোমরা কুফরীতে লিপ্ত ছিলে’’। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1196,8,36,'যে সব লোক সত্যকে মেনে নিতে অস্বীকার করেছে তারা আল্লাহর পথ হতে (লোকেদেরকে) বাধা দেয়ার জন্য তাদের ধন-সম্পদ ব্যয় করে থাকে, তারা তা ব্যয় করতেই থাকবে, অতঃপর এটাই তাদের দুঃখ ও অনুশোচনার কারণ হবে। পরে তারা পরাজিতও হবে। যারা কুফরী করে তাদেরকে (অবশেষে) জাহান্নামের পানে একত্রিত করা হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1197,8,37,'যাতে আল্লাহ পবিত্র থেকে অপবিত্রকে আলাদা করে দেন, অতঃপর অপবিত্রদের এককে অন্যের উপর রাখবেন, সকলকে স্তুপীকৃত করবেন; অতঃপর এই সমষ্টিকে জাহান্নামে নিক্ষেপ করবেন। এরাই হল সর্বস্বান্ত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1198,8,38,'যারা কুফরী করে তাদেরকে বল, ‘তারা যদি নিবৃত্ত হয় তাহলে তারা পূর্বে যা করেছে তা ক্ষমা করা হবে, আর যদি (কুফরীর) পুনরাবৃত্তি করে, তাহলে আগের লোকেদের (প্রতি অনুসৃত) নীতির দৃষ্টান্ত তো অতীতের পাতাতেই আছে।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1199,8,39,'তাদের বিরুদ্ধে যুদ্ধ চালিয়ে যাও যে পর্যন্ত না ফিতনা (কুফর ও শিরক) খতম হয়ে যায় আর দ্বীন পুরোপুরিভাবে আল্লাহর জন্য হয়ে যায়। অতঃপর তারা যদি বিরত হয় তাহলে তারা (ন্যায় বা অন্যায়) যা করে আল্লাহ তার সম্যক দ্রষ্টা। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1200,8,40,'আর তারা যদি মুখ ফিরিয়ে নেয় তাহলে জেনে রেখ যে, আল্লাহই তোমাদের অভিভাবক, কতই না উত্তম অভিভাবক! কতই না উত্তম সাহায্যকারী! ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1201,8,41,'তোমরা জেনে রেখ যে, যুদ্ধে যা তোমরা লাভ কর তার এক-পঞ্চমাংশ হচ্ছে আল্লাহ, তাঁর রসূল, রসূলের আত্মীয়স্বজন, ইয়াতীম, মিসকীন ও মুসাফিরদের জন্য যদি তোমরা আল্লাহর উপর আর চূড়ান্ত ফায়সালার দিন অর্থাৎ দু’পক্ষের (মুসলমান ও কাফের বাহিনীর) মিলিত হওয়ার দিন আমি যা আমার বান্দাহর উপর অবতীর্ণ করেছিলাম তার উপর বিশ্বাস করে থাক। আর আল্লাহ হলেন সকল বিষয়ের উপর ক্ষমতাবান। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1202,8,42,'স্মরণ কর, যখন তোমরা উপত্যকার নিকট প্রান্তে ছিলে আর তারা ছিল দূরের প্রান্তে আর উটের আরোহী কাফেলা (আবূ সুফইয়ানের নেতৃত্বে কুরাইশের সম্পদ সহ কাফেলা) ছিল তোমাদের অপেক্ষা নিম্নভূমিতে। যদি তোমরা যুদ্ধ করার ব্যাপারে পরস্পরের মধ্যে পূর্বেই সিদ্ধান্ত গ্রহণ করতে, তবুও যুদ্ধ করার এ সিদ্ধান্ত তোমরা অবশ্যই রক্ষা করতে পারতে না (তোমাদের দুর্বল অবস্থার কারণে), যাতে আল্লাহ সে কাজ সম্পন্ন করতে পারেন যা ছিল পূর্বেই স্থিরীকৃত, যেন যাকে ধ্বংস হতে হবে সে যেন সুস্পষ্ট দলীলের ভিত্তিতে ধ্বংস হয় আর যাকে জীবিত থাকতে হবে সেও যেন সুস্পষ্ট দলীলের ভিত্তিতে জীবিত থাকে। আল্লাহ নিশ্চয়ই সর্বশ্রোতা, সর্বজ্ঞ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1203,8,43,'স্মরণ কর, আল্লাহ তোমাকে স্বপ্নের মাধ্যমে তাদের সংখ্যাকে অল্প করে দেখিয়েছিলেন, যদি তিনি তাদের সংখ্যাকে তোমার নিকট বেশি করে দেখাতেন তাহলে তোমরা অবশ্যই সাহস হারিয়ে ফেলতে আর যুদ্ধের বিষয় নিয়ে অবশ্যইঝগড়া শুরু করে দিতে। কিন্তু আল্লাহ্ই তোমাদেরকে রক্ষা করেছিলেন, অন্তরে যা আছে সে সম্পর্কে তিনি খুবই ভালভাবে অবহিত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1204,8,44,'স্মরণ কর, যখন তোমরা পরস্পরের সম্মুখীন হয়েছিলে তখন তিনি তোমাদের চোখে তাদেরকে স্বল্প সংখ্যক দেখিয়েছিলেন এবং তাদের চোখে তোমাদেরকে স্বল্প সংখ্যক দেখিয়েছিলেন সেই ব্যাপারটি সম্পন্ন করার জন্য যা ছিল পূর্ব স্থিরীকৃত। যাবতীয় বিষয় (অবশেষে) আল্লাহরই নিকট ফিরে আসে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1205,8,45,'হে ঈমানদারগণ! যখন তোমরা কোন বাহিনীর সম্মুখীন হবে তখন অবিচল থাকবে আর আল্লাহকে বেশি বেশি স্মরণ করবে, যাতে তোমরা সাফল্য লাভ করতে পার। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1206,8,46,'আল্লাহ ও তাঁর রসূলের আনুগত্য কর, পরস্পরে ঝগড়া বিবাদ করো না, তা করলে তোমরা সাহস হারিয়ে ফেলবে, তোমাদের শক্তি-ক্ষমতা বিলুপ্ত হবে। আর ধৈর্য ধারণ করবে, আল্লাহ ধৈর্যশীলদের সঙ্গে আছেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1207,8,47,'তোমরা তাদের মত হয়ো না যারা গর্ব অহঙ্কারসহ লোক দেখানোর জন্য এবং আল্লাহর পথে বাধা দেয়ার জন্য নিজেদের ঘর হতে বের হয়েছিল, তারা যা কিছুই করুক না কেন, আল্লাহ তাদেরকে ঘিরে রেখেছেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1208,8,48,'স্মরণ কর, যখন শয়ত্বান তাদের কার্যকলাপকে তাদের দৃষ্টিতে খুবই চাকচিক্যময় করে দেখিয়েছিল আর তাদেরকে বলেছিল, ‘আজ তোমাদেরকে পরাজিত করতে পারে মানুষের মাঝে এমন কেউই নাই, আমি তোমাদের পাশেই আছি।’ অতঃপর দল দু’টি যখন পরস্পরের দৃষ্টির গোচরে আসলো তখন সে পিছনে সরে পড়ল আর বলল, ‘তোমাদের সাথে আমার কোনই সম্পর্ক নেই, আমি তো দেখি (কাফিরদের বিরুদ্ধে যুদ্ধ করার জন্য আল্লাহর নাযিলকৃত ফেরেশতা) যা তোমরা দেখতে পাও না, আমি অবশ্যই আল্লাহকে ভয় করি কেননা আল্লাহ শাস্তিদানে অত্যন্ত কঠোর।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1209,8,49,'স্মরণ কর, মুনাফিক্বরা আর যাদের অন্তরে রোগ আছে তারা বলে, ‘এই লোকগুলোকে তাদের দ্বীন ধোঁকায় ফেলে দিয়েছে’। (কিন্তু আসল ব্যাপার হল) কেউ যদি আল্লাহর উপর ভরসা করে তাহলে আল্লাহ তো প্রবল পরাক্রান্ত, মহাবিজ্ঞানী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1210,8,50,'তুমি যদি দেখতে যখন ফেরেশতারা কাফিরদের প্রাণবায়ু নির্গত করছে তখন তাদের মুখে আর পিঠে প্রহার করছে আর বলছে অগ্নিতে দগ্ধ হওয়ার যন্ত্রণা ভোগ কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1211,8,51,'এটা হল তাই যা তোমাদের হস্তগুলো (অর্জন করে) আগে পাঠিয়েছে কেননা আল্লাহ তো তাঁর বান্দাহদের প্রতি অত্যাচারী নন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1212,8,52,'ফির‘আওনের লোকজন ও তাদের আগের লোকেদের মতই ওরা আল্লাহর নিদর্শনাবলীকে প্রত্যাখ্যান করেছে, কাজেই তাদের পাপের কারণে আল্লাহ তাদেরকে পাকড়াও করেছেন। নিশ্চয়ই আল্লাহ শক্তিমান, শাস্তিদানে অত্যন্ত কঠোর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1213,8,53,'এটা এজন্য যে, আল্লাহ কোন সম্প্রদায়ের নিকট দেয়া তাঁর অবদানকে পরিবর্তন করেন না যতক্ষণ না তারা নিজেরাই (তাদের কর্মনীতির মাধ্যমে) তা পরিবর্তন করে। নিশ্চয়ই আল্লাহ সর্বশ্রোতা, সর্বজ্ঞ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1214,8,54,'ফিরআউনের লোকজন ও তাদের আগের লোকেদের মতই তারা তাদের প্রতিপালকের নিদর্শনগুলোকে মিথ্যা জেনেছিল। কাজেই তাদের পাপের কারণে আমি তাদেরকে ধ্বংস করে দিয়েছিলাম আর ফিরআউনের লোকজনকে ডুবিয়ে মেরেছিলাম। এরা সবাই ছিল যালিম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1215,8,55,'যারা কুফরী করে আল্লাহর নিকট তারাই নিকৃষ্টতম জীব, অতঃপর আর তারা ঈমান আনবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1216,8,56,'তাদের মধ্যে (বিশেষভাবে নিকৃষ্ট তারা) তুমি যাদের সঙ্গে চুক্তিতে আবদ্ধ হও, অতঃপর তারা সে চুক্তি প্রত্যেকবার ভঙ্গ করে আর তারা (আল্লাহকে) ভয় করে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1217,8,57,'যুদ্ধে তোমরা যদি তাদেরকে বাগে পেয়ে যাও তাহলে ওদের পেছনে যারা ওদের সাথী-সঙ্গী আছে তাদের থেকে ওদেরকে এমনভাবে ছিন্ন-বিচ্ছিন্ন করে ফেলবে যাতে মনে রাখার মত তারা একটা শিক্ষা পেয়ে যায়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1218,8,58,'আর যদি তুমি কোন সম্প্রদায়ের চুক্তি ভঙ্গের আশঙ্কা কর তাহলে (তাদের চুক্তিকে) তাদের প্রতি নিক্ষেপ কর যাতে সমান সমান অবস্থা বিরাজিত হয়। আল্লাহ নিশ্চয়ই (ওয়াদা-চুক্তি-প্রতিশ্রুতি) ভঙ্গকারীদের পছন্দ করেন না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1219,8,59,'যারা কুফরী করে তারা যেন এটা ধারণা না করে যে তারা প্রাধান্য লাভ করে নিয়েছে, তারা মু’মিনদেরকে কক্ষনো পরাজিত করতে পারবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1220,8,60,'আর তাদেরকে মুকাবালা করার জন্য যথাসাধ্য শক্তি ও অশ্ব-বাহিনী সদা প্রস্তুত রাখবে যদ্বারা তোমরা ভয় দেখাতে থাকবে আল্লাহর শত্রু আর তোমাদের শত্রুকে, আর তাদের ছাড়াও অন্যান্যদেরকেও যাদেরকে তোমরা জান না কিন্তু আল্লাহ তাদেরকে জানেন। তোমরা আল্লাহর পথে যা খরচ কর তার পুরোপুরি প্রতিদান তোমাদেরকে দেয়া হবে, আর তোমাদের সাথে কক্ষনো যুলম করা হবে না।  ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1221,8,61,'তারা যদি সন্ধির দিকে ঝুঁকে, তুমিও তার দিকে ঝুঁকে পড়, আর আল্লাহর উপর নির্ভর কর, নিশ্চয়ই তিনি সর্বশ্রোতা, সর্বজ্ঞ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1222,8,62,'আর তারা যদি তোমাকে ধোঁকা দেয়ার নিয়্যাত করে, সেক্ষেত্রে আল্লাহই তোমার জন্য যথেষ্ট। তিনি তো তাঁর সাহায্য ও মু’মিনদের দ্বারা তোমাকে শক্তিশালী করেছেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1223,8,63,'তিনি তাদের হৃদয়গুলোকে প্রীতির বন্ধনে জুড়ে দিয়েছেন। দুনিয়ায় যা কিছু আছে তার সবটুকু খরচ করলেও তুমি তাদের অন্তরগুলোকে প্রীতির ডোরে বাঁধতে পারতে না, কিন্তু আল্লাহ তাদের মধ্যে বন্ধন সৃষ্টি করে দিয়েছেন, তিনি তো প্রবল পরাক্রান্ত, মহাবিজ্ঞানী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1224,8,64,'হে নাবী! আল্লাহই তোমার আর তোমার অনুসারী ঈমানদারদের জন্য যথেষ্ট। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1225,8,65,'হে নাবী! যুদ্ধের ব্যাপারে মু’মিনদেরকে উদ্বুদ্ধ কর। তোমাদের মধ্যে বিশজন ধৈর্যশীল থাকলে তারা দু’শ জনের উপর জয়ী হবে এবং তোমাদের মধ্যে (ঐরূপ) একশ’ জন থাকলে তারা একহাজার কাফিরের উপর বিজয়ী হবে। কেননা তারা হচ্ছে এমন লোক যারা (ন্যায়-অন্যায় সম্পর্কে) কোন বোধ রাখে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1226,8,66,'(তবে) এখন আল্লাহ তোমাদের দায়িত্বভার কমিয়ে দিয়েছেন। আল্লাহ তো জানেন যে তোমাদের ভিতর দুর্বলতা রয়ে গেছে, কাজেই তোমাদের মাঝে যদি একশ’ জন ধৈর্যশীল হয় তবে তারা দু’শ জনের উপর বিজয়ী হবে। আর যদি তোমাদের মাঝে এক হাজার (ঐ রকম) লোক পাওয়া যায় তাহলে তারা আল্লাহর হুকুমে দু’হাজার লোকের উপর জয়ী হবে। আর আল্লাহ ধৈর্যশীলদের সাথে (আছেন)। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1227,8,67,'কোন নাবীর জন্য এটা সঠিক কাজ নয় যে, দেশে (আল্লাহর দুশমনদেরকে) পুরোমাত্রায় পরাভূত না করা পর্যন্ত তার (হাতে) যুদ্ধ-বন্দী থাকবে। তোমরা দুনিয়ার স্বার্থ চাও আর আল্লাহ চান আখিরাত (এর সাফল্য), আল্লাহ প্রবল পরাক্রান্ত, মহাবিজ্ঞানী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1228,8,68,'আল্লাহর লেখন যদি পূর্বেই লেখা না হত তাহলে তোমরা যা (মুক্তিপণ হিসেবে) গ্রহণ করেছ তজ্জন্য তোমাদের উপর মহাশাস্তি পতিত হত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1229,8,69,'এক্ষণে, যুদ্ধে গানীমাত হিসেবে যা তোমরা লাভ করেছ তা ভোগ কর, তা বৈধ ও পবিত্র। আল্লাহকে ভয় করে চলো, নিশ্চয়ই আল্লাহ অতি ক্ষমাশীল, অতি দয়ালু। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1230,8,70,'হে নাবী! তোমাদের হাতে যে সব যুদ্ধবন্দী আছে তাদেরকে বল, ‘আল্লাহ যদি তোমাদের অন্তরে ভাল কিছু দেখেন তাহলে তোমাদের কাছ থেকে (মুক্তিপণ) যা নেয়া হয়েছে তাত্থেকে উত্তম কিছু তোমাদেরকে তিনি দান করবেন আর তোমাদেরকে ক্ষমা করে দেবেন। আল্লাহ অতি ক্ষমাশীল, অতি দয়ালু।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1231,8,71,'আর যদি তারা তোমার সাথে খিয়ানাত করার ইচ্ছে করে (তবে সেটা অসম্ভব কিছু নয়, কারণ এর থেকেও গুরুতর যে) তারা পূর্বে আল্লাহর সাথে খিয়ানাত করেছে, কাজেই আল্লাহ তাদেরকে তোমার অধীন করে দিয়েছেন। আল্লাহ সর্ববিষয়ে বিশেষভাবে অবগত, সর্বশ্রেষ্ঠ প্রজ্ঞাবান। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1232,8,72,'যারা ঈমান এনেছে, হিজরাত করেছে, নিজেদের মাল দিয়ে জান দিয়ে আল্লাহর রাস্তায় জিহাদ করেছে আর যারা তাদেরকে আশ্রয় দিয়েছে, সাহায্য করেছে, এরা পরস্পর পরস্পরের বন্ধু। আর যারা ঈমান এনেছে কিন্তু হিজরাত করেনি তারা হিজরাত না করা পর্যন্ত তাদের পৃষ্ঠপোষকতা করার কোন দায়-দায়িত্ব তোমার উপর নেই, তবে তারা যদি দ্বীনের ব্যাপারে তোমাদের সাহায্য চায় তাহলে তাদেরকে সাহায্য করা তোমাদের কর্তব্য, তবে তাদের বিরুদ্ধে নয় যাদের সঙ্গে তোমাদের মৈত্রী চুক্তি রয়েছে। তোমরা যা কর আল্লাহ তা দেখেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1233,8,73,'আর যারা কুফরী করে তারা একে অপরের বন্ধু। যদি তোমরা তা না কর (অর্থাৎ তোমরা পরস্পর পরস্পরের সাহায্যে এগিয়ে না আস) তাহলে দুনিয়াতে ফিতনা ও মহাবিপর্যয় দেখা দিবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1234,8,74,'যারা ঈমান এনেছে, হিজরাত করেছে, আল্লাহর রাস্তায় জিহাদ করেছে আর যারা তাদেরকে আশ্রয় দিয়েছে, সাহায্য-সহযোগিতা করেছে- তারাই প্রকৃত ঈমানদার। তাদের জন্য আছে ক্ষমা আর সম্মানজনক জীবিকা। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1235,8,75,'যারা পরে ঈমান এনেছে, হিজরাত করেছে আর তোমাদের সাথে মিলিত হয়ে জিহাদ করেছে, এসব লোক তোমাদেরই মধ্যে গণ্য। কিন্তু আল্লাহর বিধানে রক্ত সম্পর্কীয়গণ পরস্পর পরস্পরের নিকট অগ্রগণ্য। আল্লাহ সকল বিষয়ে সবচেয়ে বেশী অবগত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1236,9,1,'মুশরিকদের মধ্যেকার যাদের সঙ্গে তোমরা সন্ধিচুক্তি করেছিলে তাদের সাথে আল্লাহ ও তাঁর রসূলের পক্ষ হতে সম্পর্কচ্ছেদের ঘোষণা দেয়া হল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1237,9,2,'অতঃপর (হে কাফিরগণ!) চার মাস তোমরা যমীনে (ইচ্ছে মত) চলাফেরা করে নাও; আর জেনে রেখ যে, তোমরা আল্লাহকে নত করতে পারবে না, আল্লাহ্ই সত্য প্রত্যাখ্যানকারীদেরকে লাঞ্ছিত করবেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1238,9,3,'আল্লাহ ও তাঁর রসূলের পক্ষ হতে বড় হাজ্জের দিনে মানুষদের কাছে ঘোষণা দেয়া হল যে আল্লাহ মুশরিকদের সাথে সম্পর্কহীন এবং তাঁর রসূলও। কাজেই এখন যদি তোমরা তাওবাহ কর, তাতে তোমাদেরই ভাল হবে, আর যদি তোমরা মুখ ফিরিয়ে নাও তাহলে জেনে রেখ যে, তোমরা আল্লাহকে হীন-দুর্বল করতে পারবে না, আর যারা কুফরী করে চলেছে তাদেরকে ভয়াবহ শাস্তির সুসংবাদ শুনিয়ে দাও।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1239,9,4,'কিন্তু মুশরিকদের মধ্যে যারা তোমাদের সঙ্গে চুক্তি রক্ষার ব্যাপারে বিন্দুমাত্র ত্রুটি করেনি, আর তোমাদের বিরুদ্ধে কাউকে সাহায্যও করেনি, তাদের সাথে নির্দিষ্ট সময় পর্যন্ত চুক্তি পূর্ণ কর। অবশ্যই আল্লাহ মুত্তাকীদের ভালবাসেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1240,9,5,'তারপর (এই) নিষিদ্ধ মাস অতিক্রান্ত হয়ে গেলে মুশরিকদেরকে যেখানে পাও হত্যা কর, তাদেরকে পাকড়াও কর, তাদেরকে ঘেরাও কর, তাদের অপেক্ষায় প্রত্যেক ঘাঁটিতে ওৎ পেতে বসে থাক। কিন্তু তারা যদি তাওবাহ করে, নামায প্রতিষ্ঠা করে, যাকাত আদায় করে, তাহলে তাদের পথ ছেড়ে দাও, নিশ্চয়ই আল্লাহ বড়ই ক্ষমাশীল, বড়ই দয়ালু। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1241,9,6,'মুশরিকদের কেউ যদি তোমার কাছে আশ্রয় প্রার্থনা করে তবে তাকে আশ্রয় দাও যাতে সে আল্লাহর বাণী শোনার সুযোগ পায়; তারপর তাকে তার নিরাপদ জায়গায় পৌঁছে দাও। এটা এজন্য করতে হবে যে, এরা এমন এক সম্প্রদায় যারা (ভাল-মন্দ, সত্য-মিথ্যা সম্পর্কে) অজ্ঞ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1242,9,7,'আল্লাহ ও তাঁর রসূলের সঙ্গে মুশরিকদের চুক্তি কী করে কার্যকর থাকতে পারে? অবশ্য ঐসব লোক ছাড়া যাদের সঙ্গে তোমরা মাসজিদুল হারামের নিকট চুক্তিবদ্ধ হয়েছিলে; তারা যদ্দিন তোমাদের সঙ্গে চুক্তি ঠিক রাখে, তোমরাও তাদের সঙ্গে কৃত চুক্তিতে দৃঢ় থাক। নিশ্চয়ই আল্লাহ মুত্তাকীদের ভালবাসেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1243,9,8,'কীভাবে (চুক্তি থাকতে পারে) যদি তারা তোমাদেরকে পরাজিত করতে পারে তাহলে তারা তোমাদের সঙ্গে না আত্মীয়তার মর্যাদা দেয়, আর না ওয়াদা-অঙ্গীকারের; তারা তাদের মুখের কথায় তোমাদেরকে সন্তুষ্ট রাখতে চায় কিন্তু তাদের অন্তর তা অস্বীকার করে, তাদের অধিকাংশই সত্যত্যাগী অপরাধী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1244,9,9,'আল্লাহর আয়াতকে তারা (দুনিয়াবী স্বার্থে) অতি তুচ্ছ মূল্যে বিক্রি করে দিয়েছে, আল্লাহর পথে (মানুষদের চলার ক্ষেত্রে) প্রতিবন্ধকতা সৃষ্টি করেছে। তারা যা করে কতই না জঘন্য সে কাজ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1245,9,10,'কোন ঈমানদার ব্যক্তির ব্যাপারে তারা না কোন আত্মীয়তার মর্যাদা দেয়, আর না কোন ওয়াদা-অঙ্গীকারের। এরা হল সেই লোক যারা সীমালঙ্ঘনকারী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1246,9,11,'এখন যদি তারা তাওবাহ করে, নামায প্রতিষ্ঠা করে, যাকাত আদায় করে তাহলে তারা তোমাদের দ্বীনী ভাই। জ্ঞান-বুদ্ধিসম্পন্ন লোকেদের জন্য আমি স্পষ্ট করে নিদর্শন বলে দিলাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1247,9,12,'তারা যদি চুক্তি করার পর তাদের শপথ ভঙ্গ করে আর তোমাদের দীনের বিরুদ্ধে কটুক্তি করে, তাহলে কাফিরদের নেতৃবৃন্দের বিরুদ্ধে লড়াই কর, শপথ বলে কোন জিনিস তাদের কাছে নেই, (কাজেই শক্তি প্রয়োগ কর) যাতে তারা (শয়ত্বানী কার্যকলাপ থেকে) নিবৃত্ত হয়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1248,9,13,'তোমরা সেই সম্প্রদায়ের বিরুদ্ধে লড়াই কেন করবে না যারা তাদের প্রতিশ্রুতি ভঙ্গ করেছে, যারা রসূলকে দেশ থেকে বের করে দেয়ার ষড়যন্ত্র করেছিল? প্রথমে তারাই তোমাদেরকে আক্রমণ করেছিল। তোমরা কি তাদেরকে ভয় কর? তোমরা যাকে ভয় করবে তার সবচেয়ে বেশি হকদার হলেন আল্লাহ যদি তোমরা মু’মিন হয়ে থাক। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1249,9,14,'তাদের বিরুদ্ধে লড়াই কর, তোমাদের হাত দিয়েই আল্লাহ তাদেরকে শাস্তি দিবেন, তাদেরকে অপমানিত করবেন, তাদের বিরুদ্ধে তোমাদেরকে সাহায্য করবেন আর মু’মিনদের প্রাণ ঠান্ডা করবেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1250,9,15,'তিনি তাদের মনের জ্বালা নিভিয়ে দিবেন, আল্লাহ যাকে চাইবেন তাওবাহ করার তাওফীক দিবেন, আর আল্লাহ হলেন সর্বজ্ঞ, প্রজ্ঞাময়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1251,9,16,'তোমরা কি মনে কর যে, তোমাদেরকে এমনিই ছেড়ে দেয়া হবে যে পর্যন্ত আল্লাহ জেনে না নেবেন তোমাদের মধ্যে কারা তাঁর পথে জিহাদ করেছে, আর আল্লাহ, তাঁর রসূল ও মু’মিনদের ছাড়া অন্য কাউকে বন্ধু ও অভিভাবক হিসেবে গ্রহণ করেন নি? তোমরা যা কর সে সম্পর্কে আল্লাহ বিশেষভাবে অবহিত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1252,9,17,'মুশরিকদের এটা কাজ নয় যে, তারা আল্লাহর মাসজিদের রক্ষণাবেক্ষণকারী সেবক হবে যখন তারা নিজেরাই নিজেদের কুফরীর সাক্ষ্য দেয়, তাদের সমস্ত কাজ বরবাদ হয়ে গেছে, জাহান্নামেই তারা হবে চিরস্থায়ী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1253,9,18,'আল্লাহর মাসজিদের আবাদ তো তারাই করবে যারা আল্লাহ ও শেষ দিবসের প্রতি ঈমান আনে, নামায প্রতিষ্ঠা করে, যাকাত আদায় করে আর আল্লাহ ছাড়া অন্য কাউকে ভয় করে না। আশা করা যায়, তারাই হবে সঠিক পথপ্রাপ্তদের অন্তর্ভুক্ত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1254,9,19,'হাজীদেরকে পানি পান করানো আর মাসজিদে হারামের আবাদ করাকে কি তোমরা তাদের কাজের সমান মনে কর যারা আল্লাহ ও শেষ দিনে বিশ্বাস করে আর আল্লাহর পথে জিহাদ করে? আল্লাহর দৃষ্টিতে এরা সমান নয়। (যারা ভ্রান্তপথে আল্লাহও সন্তুষ্টি খুঁজে এমন) যালিম সম্প্রদায়কে আল্লাহ সৎপথে পরিচালিত করেন না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1255,9,20,'যারা ঈমান আনে, হিজরাত করে, আর নিজেদের জান-মাল দিয়ে আল্লাহর পথে জিহাদ করে, আল্লাহর নিকট তাদের বিরাট মর্যাদা রয়েছে, এরাই হল সফলকাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1256,9,21,'তাদের প্রতিপালক তাদেরকে সুসংবাদ দিচ্ছেন তাঁর দয়া ও সন্তুষ্টির, আর জান্নাতের যেখানে তাদের জন্য আছে স্থায়ী সুখ-সামগ্রী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1257,9,22,'যেখানে তারা চিরদিন থাকবে। আল্লাহর কাছেই তো রয়েছে মহাপুরস্কার। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1258,9,23,'হে বিশ্বাসীগণ! তোমরা তোমাদের পিতা আর ভাইদেরকে বন্ধুরূপে গ্রহণ করো না যদি তারা ঈমানের চেয়ে কুফরীকেই বেশি ভালবাসে। তোমাদের মধ্যে যারা তাদেরকে বন্ধুরূপে গ্রহণ করে, তারাই যালিম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1259,9,24,'বল, ‘যদি তোমাদের পিতারা, আর তোমাদের সন্তানেরা, আর তোমাদের ভাইয়েরা, আর তোমাদের স্ত্রীরা, আর তোমাদের গোষ্ঠীর লোকেরা আর ধন-সম্পদ যা তোমরা অর্জন করেছ, আর ব্যবসা তোমরা যার মন্দার ভয় কর, আর বাসস্থান যা তোমরা ভালবাস (এসব) যদি তোমাদের নিকট প্রিয়তর হয় আল্লাহ, তাঁর রসূল ও তাঁর পথে জিহাদ করা হতে, তাহলে অপেক্ষা কর যতক্ষণ না আল্লাহ তাঁর চূড়ান্ত ফয়সালা তোমাদের কাছে নিয়ে আসেন।’ আর আল্লাহ অবাধ্য আচরণকারীদেরকে সঠিক পথ প্রদর্শন করেন না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1260,9,25,'বস্তুতঃ আল্লাহ তোমাদেরকে বহু যুদ্ধ ক্ষেত্রে সাহায্য করেছেন আর হুনায়নের যুদ্ধের দিন, তোমাদের সংখ্যার আধিক্য তোমাদেরকে গর্বে মাতোয়ারা করে দিয়েছিল, কিন্তু তা তোমাদের কোন কাজে আসেনি, যমীন সুপ্রশস্ত হওয়া সত্বেও তা তোমাদের নিকট সংকীর্ণই হয়ে গিয়েছিল, আর তোমরা পিছন ফিরে পালিয়ে গিয়েছিলে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1261,9,26,'তারপর আল্লাহ তাঁর রসূলের উপর, আর মু’মিনদের উপর তাঁর প্রশান্তির অমিয়ধারা বর্ষণ করলেন, আর পাঠালেন এমন এক সেনাবাহিনী যা তোমরা দেখতে পাওনি, আর তিনি কাফিরদেরকে শাস্তি প্রদান করলেন। এভাবেই আল্লাহ কাফিরদেরকে প্রতিফল দিয়ে থাকেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1262,9,27,'এরপরও আল্লাহ যার জন্য ইচ্ছে করবেন তাকে ক্ষমার দৃষ্টিতে দেখবেন, আল্লাহ বড়ই ক্ষমাশীল, বড়ই দয়ালু। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1263,9,28,'ওহে বিশ্বাসীগণ! মুশরিকরা হল অপবিত্র, কাজেই এ বছরের পর তারা যেন মাসজিদে হারামের নিকট না আসে। তোমরা যদি দরিদ্রতার ভয় কর, তবে আল্লাহ ইচ্ছে করলে অচিরেই তাঁর অনুগ্রহের মাধ্যমে তোমাদেরকে অভাব-মুক্ত করে দেবেন। নিশ্চয়ই আল্লাহ সর্বজ্ঞ, মহাবিজ্ঞানী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1264,9,29,'যাদেরকে কিতাব দেয়া হয়েছে তাদের মধ্যে যারা আল্লাহর প্রতি ঈমান আনে না, আর শেষ দিনের প্রতিও না, আর আল্লাহ ও তাঁর রসূল যা হারাম করেছেন তাকে হারাম গণ্য করে না, আর সত্য দ্বীনকে নিজেদের দ্বীন হিসেবে গ্রহণ করে না তাদের বিরুদ্ধে যুদ্ধ কর যে পর্যন্ত না তারা বশ্যতা সহকারে স্বেচ্ছায় ট্যাক্স দেয়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1265,9,30,'ইয়াহূদীরা বলে, ‘উযায়র আল্লাহর পুত্র। আর নাসারারা বলে, ‘মাসীহ আল্লাহর পুত্র। এসব তাদের মুখের কথা। এতে তারা তাদের পূর্বেকার কাফিরদের কথারই অনুকরণ করে। আল্লাহ তাদেরকে ধ্বংস করুন! কেমনভাবে তারা সত্য পথ থেকে দূরে ছিটকে পড়েছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1266,9,31,'আল্লাহকে বাদ দিয়ে তারা তাদের ‘আলিম আর দরবেশদেরকে রব বানিয়ে নিয়েছে; আর মারইয়াম-পুত্র মাসীহকেও। অথচ তাদেরকে এক ইলাহ ব্যতীত (অন্যের) ‘ইবাদাত করার আদেশ দেয়া হয়নি। তিনি ব্যতীত সত্যিকারের কোন ইলাহ নেই, পবিত্রতা আর মহিমা তাঁরই, (বহু ঊর্ধ্বে তিনি) তারা যাদেরকে (তাঁর) অংশীদার গণ্য করে তাত্থেকে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1267,9,32,'তারা তাদের মুখের ফুঁৎকারে আল্লাহর আলোকে নিভিয়ে দিতে চায়, কিন্তু আল্লাহ তা হতে দিবেন না, তিনি তাঁর আলোকে পূর্ণ না করে ছাড়বেন না, যদিও কাফিরগণ তা অপছন্দ করে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1268,9,33,'তিনি তাঁর রসূলকে হিদায়াত আর সঠিক দ্বীনসহ পাঠিয়েছেন যাবতীয় দ্বীনের উপর একে বিজয়ী করার জন্য যদিও মুশরিকগণ অপছন্দ করে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1269,9,34,'হে বিশ্বাসীগণ! অবশ্যই ‘আলিম ও দরবেশদের অনেকেই ভুয়ো কর্মকান্ডের মাধ্যমে মানুষদের সম্পদ গ্রাস করে থাকে আর আল্লাহর পথে বাধা সৃষ্টি করে। যারা স্বর্ণ ও রৌপ্য জমা করে আর আল্লাহর পথে তা ব্যয় করে না তাদেরকে ভয়াবহ শাস্তির সুসংবাদ দাও। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1270,9,35,'যেদিন জাহান্নামের আগুনে তা উত্তপ্ত করা হবে আর তা দিয়ে তাদের কপালে, পার্শ্বদেশে ও পিঠে দাগ দেয়া হবে, (আর তাদেরকে বলা হবে) ‘এটা হল, যা তোমরা নিজেদের জন্য স্তুপীকৃত করেছিলে, কাজেই যা জমা করছিলে তার স্বাদ গ্রহণ কর।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1271,9,36,'আসমান-যমীন সৃষ্টির দিন থেকেই আল্লাহর কিতাবে (লৌহ মাহফুজে) মাসগুলোর সংখ্যা হল বার। তার মধ্যে চারটি নিষিদ্ধ মাস। এটা হল সুপ্রতিষ্ঠিত দ্বীন। কাজেই ঐ সময়ের মধ্যে নিজেদের উপর যুলম করো না। মুশরিকদের বিরুদ্ধে সর্বাত্মকভাবে যুদ্ধ কর, যেমন তারা তোমাদের বিরুদ্ধে সর্বাত্মকভাবে যুদ্ধ করে। জেনে রেখ, আল্লাহ অবশ্যই মুত্তাকীদের সঙ্গে আছেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1272,9,37,'নিষিদ্ধ মাসকে পিছিয়ে দেয়া কুফরীর উপর আরেক কুফরী কাজ যা দ্বারা কাফিরদেরকে পথভ্রষ্ট করা হয়। এক বছর তারা একটি মাসকে হালাল করে, আরেক বছর ঐ মাসটিকে হারাম করে যাতে আল্লাহর হারাম করা মাসগুলোর সংখ্যা পূর্ণ করা যায়। এভাবে তারা আল্লাহর হারাম করা মাসগুলোকে হারাম করে নেয়। তাদের খারাপ কাজগুলো তাদের কাছে আনন্দদায়ক। আল্লাহ কাফির সম্প্রদায়কে সঠিক পথ দেখান না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1273,9,38,'হে ঈমানদারগণ! তোমাদের হয়েছে কী যে, যখন তোমাদেরকে আল্লাহর পথে বের হওয়ার নির্দেশ দেয়া হয় তখন তোমরা আরো জোরে মাটি কামড়ে ধর। তোমরা কি আখেরাতের স্থলে দুনিয়ার জীবনকেই বেশি পছন্দ কর? আখেরাতের তুলনায় পার্থিব জীবনের ভোগ সামগ্রী তো অতি সামান্য। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1274,9,39,'তোমরা যদি যুদ্ধাভিযানে বের না হও, তাহলে তোমাদেরকে ভয়াবহ শাস্তি দেয়া হবে, আর তোমাদের স্থলে অন্য সম্প্রদায়কে আনা হবে (অথচ) তোমরা তাঁর কোনই ক্ষতি করতে পারবে না। আল্লাহ সকল বিষয়ে সর্বশক্তিমান। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1275,9,40,'যদি তোমরা তাকে [অর্থাৎ রসূল (সা.)-কে] সাহায্য না কর (তাতে কোনই পরোয়া নেই) কারণ আল্লাহ তো তাকে সেই সময় সাহায্য করেছেন যখন কাফিররা তাকে বের করে দিয়েছিল, সে ছিল দু’জনের দ্বিতীয়জন যখন তারা দু’জন গুহার মধ্যে ছিল, যখন সে তার সঙ্গীকে বলছিল, ‘চিন্তা করো না, আল্লাহ আমাদের সঙ্গে আছেন’। তখন আল্লাহ তার প্রতি তাঁর প্রশান্তি বর্ষণ করলেন আর তাকে এমন সেনাবাহিনী দিয়ে শক্তিশালী করলেন তোমরা যা দেখতে পাওনি, আর তিনি কাফিরদের মুখের বুলিকে গভীর নীচে ফেলে দিলেন। আর আল্লাহর বাণীই রয়েছে সর্বোচ্চ। আল্লাহ হলেন প্রবল পরাক্রান্ত, মহাবিজ্ঞানী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1276,9,41,'যুদ্ধাভিযানে বেরিয়ে পড়, অবস্থা হালকাই হোক আর ভারীই হোক (অস্ত্র কম থাকুক আর বেশি থাকুক) আর আল্লাহর রাস্তায় তোমাদের মাল দিয়ে আর তোমাদের জান দিয়ে জিহাদ কর, এটাই তোমাদের জন্য সর্বোত্তম, তোমরা যদি জানতে! ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1277,9,42,'দুনিয়াবী কোন স্বার্থ থাকলে আর যাত্রা সহজ হলে তারা অবশ্যই তোমার সাথে যেত। কিন্তু পথ তাদের কাছে দীর্ঘ ও ভারী মনে হয়েছে। অচিরেই তারা আল্লাহর নামে হলফ করে বলবে, ‘আমরা যদি পারতাম তাহলে অবশ্যই তোমাদের সঙ্গে বের হতাম।’ আসলে তারা নিজেরাই নিজেদেরকে ধ্বংস করছে, আর আল্লাহ জানেন যে, তারা অবশ্যই মিথ্যেবাদী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1278,9,43,'আল্লাহ তোমাকে মাফ করুন। কারা সত্য বলেছে তা স্পষ্ট না হতেই আর মিথ্যাবাদীদেরকে তুমি না চিনেই কেন তুমি তাদেরকে অব্যাহতি দিয়ে দিলে? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1279,9,44,'যারা আল্লাহয় ও শেষ দিনে বিশ্বাস করে তারা তাদের মাল দিয়ে আর জান দিয়ে জিহাদ করা থেকে অব্যাহতি পাওয়ার জন্য তোমার কাছে অনুমতি প্রার্থনা করে না। মুত্তাকীদের সম্পর্কে আল্লাহ খুবই অবগত আছেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1280,9,45,'তোমার কাছে অব্যাহতি প্রার্থনা তারাই করে যারা আল্লাহ্ ও শেষ দিবসে বিশ্বাস করে না, যাদের অন্তর সন্দেহপূর্ণ, কাজেই তারা তাদের সন্দেহের মধ্যে ঘুরপাক খাচ্ছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1281,9,46,'(যুদ্ধাভিযানে) বের হওয়ার তাদের যদি ইচ্ছেই থাকত তবে তারা সেজন্য অবশ্যই প্রস্ত্ততি নিত। কিন্তু তাদের অভিযানে গমনই আল্লাহর পছন্দ নয়, কাজেই তিনি তাদেরকে পশ্চাতে ফেলে রাখেন আর তাদেরকে বলা হয়, ‘যারা (নিস্ক্রিয় হয়ে) বসে থাকে তাদের সাথে বসে থাক’। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1282,9,47,'তারা যদি তোমাদের সঙ্গে বের হত তাহলে বিশৃঙ্খলা ছাড়া আর কিছুই বাড়াত না আর তোমাদের মাঝে ফিতনা সৃষ্টির উদ্দেশে তোমাদের মাঝে ছুটাছুটি করত, আর তোমাদের মাঝে তাদের কথা শুনার লোক আছে। আল্লাহ যালিমদের সম্পর্কে খুব ভালভাবেই অবহিত আছেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1283,9,48,'আগেও তারা ফিতনা সৃষ্টি করতে চেয়েছে আর তোমার অনেক কাজ নষ্ট করেছে যতক্ষণ না প্রকৃত সত্য এসে হাজির হল আর আল্লাহর বিধান প্রকাশিত হয়ে গেল যদিও এতে তারা ছিল নাখোশ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1284,9,49,'তাদের মাঝে এমন লোক আছে যারা বলে, ‘আমাকে অব্যাহতি দিন, আমাকে পরীক্ষায় ফেলবেন না।’ জেনে রেখ, তারা তো ফিতনাতে পড়েই আছে। বস্তুতঃ জাহান্নাম কাফিরদেরকে চারদিক থেকে ঘিরেই রেখেছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1285,9,50,'তোমার মঙ্গল হলে তা তাদেরকে মনোকষ্ট দেয়, আর তোমার উপর বিপদ আসলে তারা খুশির সঙ্গে এ কথা বলতে বলতে সরে পড়ে যে, ‘আমরা আগেই সাবধানতা অবলম্বন করেছিলাম।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1286,9,51,'বলে দাও, ‘আল্লাহ আমাদের জন্য যা নির্দিষ্ট করে দিয়েছেন তাছাড়া অন্য কিছুই আমাদের ঘটবে না, তিনিই আমাদের রক্ষক, আর আল্লাহর উপরই মু’মিনদের ভরসা করা দরকার।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1287,9,52,'বল, ‘তোমরা আমাদের জন্য যে জিনিসের অপেক্ষা করছ তা দু’টো ভালোর একটি ছাড়া আর কিছুই না (শাহাদাত কিংবা বিজয়) আর আমরা অপেক্ষা করছি এজন্য যে, আল্লাহ নিজেই তোমাদেরকে শাস্তি দেন অথবা আমাদের হাত দিয়ে দেয়ান। কাজেই অপেক্ষায় থাক, আমরা তোমাদের সাথে অপেক্ষায় থাকলাম।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1288,9,53,'বল, ‘স্বেচ্ছায় দান কর আর অনিচ্ছায়, তোমাদের থেকে কক্ষনো তা গ্রহণ করা হবে না; তোমরা হলে এক ফাসিক সম্প্রদায়।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1289,9,54,'তাদের কাছ থেকে অর্থ সাহায্য গ্রহণ নিষিদ্ধ করার কারণ এ ছাড়া আর কিছু নয় যে তারা আল্লাহ ও তাঁর রসূলকে অস্বীকার করে, সলাতে আসলে আসে শৈথিল্যভরে আর দান করলেও করে অনিচ্ছা নিয়ে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1290,9,55,'কাজেই তাদের ধন-সম্পত্তি আর সন্তান-সন্ততি যেন তোমার চোখ ধাঁধিয়ে না দেয়, ওসব দিয়েই আল্লাহ দুনিয়াতে ওদেরকে শাস্তি দিতে চান আর কাফির অবস্থাতেই যেন তাদের জান বাহির হয়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1291,9,56,'তারা আল্লাহর নামে কসম করে বলে যে, তারা তোমাদেরই মধ্যের লোক, তারা কক্ষনো তোমাদের মধ্যের লোক নয়, প্রকৃতপক্ষে তারা ভীত-সন্ত্রস্ত লোক। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1292,9,57,'তারা পালিয়ে গিয়ে আশ্রয় নেয়ার জায়গা পেলে কিংবা গিরিগুহা বা ঢুকে থাকার মত জায়গা পেলে সেখানেই তারা ক্ষিপ্রগতিতে ছুটে যেত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1293,9,58,'তাদের মধ্যে এমন লোক আছে যারা সদাক্বাহ (বণ্টনের) ব্যাপারে তোমার প্রতি দোষারোপ করে, তাত্থেকে দেয়া হলে খুশি হয়, আর তাত্থেকে না দেয়া হলে সাথে সাথে ক্ষুব্ধ হয়ে পড়ে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1294,9,59,'আল্লাহ ও তাঁর রসূল তাদেরকে যা দিয়েছেন তারা যদি তাতে সন্তুষ্ট থাকত আর বলত, ‘আমাদের জন্য আল্লাহই যথেষ্ট, অচিরেই আল্লাহ অনুগ্রহ করে আমাদেরকে দিবেন আর তাঁর রসূলও, আমরা আশা ভরসা নিয়ে আল্লাহর দিকেই চেয়ে থাকি।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1295,9,60,'সদাক্বাহ হল ফকীর, মিসকীন ও তৎসংশ্লিষ্ট কর্মচারী ও যাদের মন জয় করা উদ্দেশ্য তাদের জন্য, দাসমুক্তি ও ঋণগ্রস্তদের জন্য, আল্লাহর পথে (ব্যয়ের জন্য) আর মুসাফিরের জন্য। এটা আল্লাহ কর্তৃক নির্ধারিত ফরয। আর আল্লাহ হলেন সর্বজ্ঞ, মহাবিজ্ঞানী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1296,9,61,'তাদের মাঝে এমন লোকও আছে যারা নাবীকে কষ্ট দেয় আর বলে তিনি কান কথা শুনেন। বল, ‘তোমাদের যাতে ভালো আছে সে তাই শোনে’। সে আল্লাহয় বিশ্বাস রাখে, আর মু’মিনদেরকেও বিশ্বাস করে, আর তোমাদের মধ্যে যারা ঈমান এনেছে তাদের জন্য রহমত। অপরপক্ষে আল্লাহর রসূলকে যারা কষ্ট দেয় তাদের জন্য রয়েছে ভয়াবহ ‘আযাব। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1297,9,62,'তোমাদেরকে খুশি করার জন্য তারা তোমাদের সামনে আল্লাহর নামে কসম করে। তারা যদি মু’মিন হয়ে থাকে তবে কাউকে খুশি করতে চাইলে আল্লাহ ও তাঁর রসূলই এর সবচেয়ে বেশি হকদার। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1298,9,63,'তারা কি জানে না যে, যে ব্যক্তি আল্লাহ ও তাঁর রসূলের বিরোধিতা করে তার জন্য আছে জাহান্নামের আগুন যেখানে সে হবে চিরস্থায়ী? আর এটা খুবই লাঞ্ছনার ব্যাপার। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1299,9,64,'মুনাফিকরা ভয় পায় তাদের মনের কথা প্রকাশ করে তাদের ব্যাপারে কোন সূরাহ নাযিল হয়ে যায় নাকি। বল, ‘ঠাট্টা করতে থাক, তোমরা যে ব্যাপারে ভয় পাও, আল্লাহ তা প্রকাশ করে দিবেন’। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1300,9,65,'তাদেরকে জিজ্ঞেস করলে তারা জোর দিয়েই বলবে, ‘আমরা হাস্য রস আর খেল-তামাশা করছিলাম।’ বল, ‘আল্লাহ, তাঁর আয়াত ও তাঁর রসূলকে নিয়ে তোমরা বিদ্রূপ করছিলে?’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1301,9,66,'ওযর পেশের চেষ্টা করো না, ঈমান আনার পর তোমরা কুফরী করেছ। তোমাদের মধ্যেকার কোন দলকে ক্ষমা করলেও অন্যদেরকে শাস্তি দেব, কারণ তারা অপরাধী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1302,9,67,'মুনাফিক পুরুষ আর মুনাফিক নারী সব এক রকম, তারা অন্যায় কাজের নির্দেশ দেয় আর সৎ কাজ করতে নিষেধ করে, (আল্লাহর পথে ব্যয় করার ব্যাপারে) হাত গুটিয়ে রাখে, তারা আল্লাহকে ভুলে গেছে, তাই তিনিও তাদেরকে ভুলে গেছেন। মুনাফিকরাই তো ফাসিক। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1303,9,68,'আল্লাহ মুনাফিক পুরুষ, মুনাফিক নারী ও কাফিরদের জন্য জাহান্নামের আগুনের ওয়া‘দা দিয়েছেন, তাতে তারা চিরদিন থাকবে, তা-ই তাদের জন্য যথেষ্ট। তাদের উপর আছে আল্লাহর অভিশাপ, আর আছে তাদের জন্য স্থায়ী ‘আযাব। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1304,9,69,'(তোমাদের কাজ-কারবার) তোমাদের আগের লোকেদের মতই, যারা ছিল তোমাদের চেয়ে অধিক শক্তির অধিকারী, আর ধন-মাল আর সন্তান-সন্ততিতেও তোমাদের চেয়ে অধিক সমৃদ্ধিশালী, তাদের প্রাপ্য অংশ তারা ভোগ করে গেছে, এখন তোমরাও তোমাদের প্রাপ্য অংশ ভোগ কর যেমন তোমাদের আগের লোকেরা তাদের প্রাপ্য অংশ ভোগ করেছে, আর তোমরা অনর্থক কথাবার্তায় লিপ্ত আছ যেমন তারা অনর্থক কথাবার্তায় লিপ্ত ছিল, এরাই হল তারা দুনিয়া ও আখেরাতে যাদের কাজ-কর্ম নিস্ফল হয়ে গেছে, আর তারাই ক্ষতিগ্রস্ত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1305,9,70,'তাদের কাছে কি তাদের আগের লোকদের সংবাদ আসেনি, নূহের জাতি, ‘আদ, সামূদ, ইব্রাহীমের সম্প্রদায় এবং মাদ্য়্যানের অধিবাসীবৃন্দ আর উল্টে দেয়া নগরসমূহের? তাদের কাছে তাদের রসূলগণ স্পষ্ট নিদর্শন নিয়ে এসেছিল, আল্লাহ তাদের উপর যুলম করেননি, আসলে (তারাই তাদের অন্যায় কার্যকলাপের মাধ্যমে) নিজেদের আত্মার উপর যুলম করেছিল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1306,9,71,'মু’মিন পুরুষ আর মু’মিন নারী পরস্পর পরস্পরের বন্ধু, তারা সৎকাজের নির্দেশ দেয়, অন্যায় কাজ থেকে নিষেধ করে, নামায ক্বায়িম করে, যাকাত দেয়, আল্লাহ ও তাঁর রসূলের আনুগত্য করে। তাদের প্রতিই আল্লাহ করুণা প্রদর্শন করবেন। আল্লাহ তো প্রবল পরাক্রান্ত, মহা প্রজ্ঞাবান। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1307,9,72,'মু’মিন পুরুষ আর মু’মিন নারীর জন্য আল্লাহ অঙ্গীকার করেছেন জান্নাতের যার নিম্নদেশ দিয়ে ঝর্ণাধারা প্রবাহিত, তাতে তারা চিরদিন থাকবে, আর জান্নাতে চিরস্থায়ী উত্তম বাসগৃহের; আর সবচেয়ে বড় (যা তারা লাভ করবে তা) হল আল্লাহর সন্তুষ্টি। এটাই হল বিরাট সাফল্য। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1308,9,73,'হে নাবী! কাফির ও মুনাফিকদের বিরুদ্ধে যুদ্ধ কর, তাদের প্রতি কঠোরতা অবলম্বন কর, তাদের বাসস্থান হল জাহান্নাম, আর তা কতই না নিকৃষ্ট আশ্রয়স্থল! ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1309,9,74,'তারা আল্লাহর নামে শপথ করে বলে যে তারা (অন্যায়) কিছু বলেনি, কিন্তু তারা তো কুফরী কথা বলেছে আর ইসলাম গ্রহণ করার পরও কুফরী করেছে। তারা ষড়যন্ত্র করেছিল কিন্তু তাতে সফল হয়নি, তাদের এ প্রতিশোধ স্পৃহার কারণ এছাড়া আর কিছু ছিল না যে আল্লাহ করুণাবশতঃ ও তাঁর রসূল তাদেরকে সম্পদশালী করে দিয়েছেন। এখন যদি তারা অনুশোচনাভরে এ পথ থেকে ফিরে আসে তবে তা তাদের জন্যই কল্যাণকর। আর যদি তারা মুখ ফিরিয়ে নেয় তবে আল্লাহ তাদেরকে দুনিয়া ও আখেরাতে ভয়াবহ শাস্তি দিবেন। পৃথিবীতে রক্ষক আর সাহায্যকারী হিসেবে কাউকে তারা পাবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1310,9,75,'তাদের মধ্যেকার কিছুলোক আল্লাহর সঙ্গে ওয়া‘দা করেছিল, ‘যদি তিনি আমাদেরকে তাঁর অনুগ্রহ হতে দান করেন তবে আমরা অবশ্যই দান করব আর অবশ্যই সৎ লোকদের মধ্যে শামিল থাকব।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1311,9,76,'অতঃপর আল্লাহ যখন তাদেরকে স্বীয় করুণার দানে ধন্য করলেন, তখন তারা দান করার ব্যাপারে কার্পণ্য করল আর বে-পরোয়াভাবে মুখ ফিরিয়ে নিল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1312,9,77,'পরিণামে আল্লাহর সঙ্গে কৃত তাদের ওয়াদা ভঙ্গের কারণে এবং মিথ্যাচারে লিপ্ত থাকার কারণে তাদের অন্তরে মুনাফিকী বদ্ধমূল করে দিলেন; ঐ দিন পর্যন্ত যেদিন তারা তাঁর সাথে সাক্ষাৎ করবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1313,9,78,'তারা কি জানে না যে, আল্লাহ তাদের গোপন কথাবার্তা আর গোপন পরামর্শ সম্পর্কে অবহিত আছেন আর আল্লাহ তো যাবতীয় অদৃশ্য সম্পর্কে সবচেয়ে বেশি অবগত আছেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1314,9,79,'মু’মিনদের মধ্যে যারা মুক্ত হস্তে দান করে, তাদেরকে যারা দোষারোপ করে আর সীমাহীন কষ্টে দানকারীদেরকে যারা বিদ্রূপ করে আল্লাহ তাদেরকে জবাবে বিদ্রূপ করেন আর তাদের জন্য রয়েছে ভয়াবহ শাস্তি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1315,9,80,'তুমি তাদের জন্য ক্ষমা প্রার্থনা কর বা না কর (উভয়ই সমান), তুমি তাদের জন্য সত্তর বার ক্ষমা প্রার্থনা করলেও আল্লাহ কক্ষনো তাদেরকে ক্ষমা করবেন না। এটা এজন্য যে, তারা আল্লাহ ও তাঁর রসূলের সাথে কুফুরী করেছে। আর আল্লাহ ফাসিক লোকদেরকে সঠিক পথ প্রদর্শন করেন না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1316,9,81,'(তাবুক অভিযানে) যারা পিছনে থেকে গিয়েছিল তারা রসূলের বিরোধিতায় বসে থাকাতেই আনন্দ প্রকাশ করেছিল আর তাদের ধন-সম্পদ ও জান দিয়ে আল্লাহর পথে জিহাদ করতে তারা অপছন্দ করেছিল। তারা বলেছিল, ‘গরমের মধ্যে অভিযানে বেরিও না’। বল, ‘জাহান্নামের আগুনই তাপে প্রচন্ডতম’। তারা যদি বুঝত! ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1317,9,82,'তারা যেন কম হাসে এবং বেশী কাঁদে, তারা যে (পাপ) কামাই করছে তার ফলস্বরূপ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1318,9,83,'আল্লাহ যদি তোমাকে তাদের কোন দলের কাছে ফিরিয়ে নিয়ে আসেন আর যদি তারা (তোমার সঙ্গে) অভিযানে বের হবার জন্য অনুমতি প্রার্থনা করে তখন বলবে, ‘আমার সাথে কক্ষনো বের হতে পারবে না আর কক্ষনো আমার সঙ্গে গিয়ে শত্রুর বিরুদ্ধে লড়তে পারবে না, তোমরা প্রথমবারেই নিস্ক্রিয় হয়ে বসে থাকাকেই বেশি পছন্দ করে নিয়েছো, কাজেই (এখন) পিছ-পড়াদের সাথেই বসে থাক’। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1319,9,84,'তাদের কেউ মারা গেলে তুমি কক্ষনো তাদের জন্য (জানাযার) নামায পড়বে না, আর তাদের কবরের পাশে দন্ডায়মান হবে না। তারা আল্লাহ ও তাঁর রসূলের সঙ্গে কুফুরী করেছে আর বিদ্রোহী পাপাচারী অবস্থায় তাদের মৃত্যু হয়েছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1320,9,85,'তাদের মালধন আর সন্তান-সন্ততি তোমার যেন চোখ ধাঁধিয়ে না দেয়, দুনিয়াতে আল্লাহ সে সব দিয়েই তাদেরকে শাস্তি দেয়ার ইচ্ছে করেন আর কাফির অবস্থায় যেন তাদের প্রাণবায়ু নির্গত হয়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1321,9,86,'যখন কোন সূরাহ অবতীর্ণ করা হয় যে, ‘আল্লাহর প্রতি বিশ্বাস স্থাপন কর আর তাঁর রসূলের সঙ্গে থেকে জিহাদ কর’- তখন শক্তি-সামর্থ্য সম্পন্ন লোকেরা তোমার নিকট অব্যাহতি প্রার্থনা করে আর বলে, ‘আমাদেরকে রেহাই দিন, যারা (ঘরে) বসে থাকে আমরা তাদের সঙ্গেই থাকব।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1322,9,87,'তারা পিছনে (ঘরে বসে) থাকা স্ত্রীলোকদের সাথে থাকাকেই পছন্দ করে, তাদের হৃদয়কে সীল করে দেয়া হয়েছে, কাজেই তারা কিছুই বুঝতে পারে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1323,9,88,'কিন্তু রসূল আর তার সাথে যারা ঈমান এনেছে তারা তাদের মাল দিয়ে এবং জান দিয়ে জিহাদ করে। যাবতীয় কল্যাণ তো তাদেরই জন্য। সফলকাম তো তারাই। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1324,9,89,'আল্লাহ তাদের জন্য জান্নাত প্রস্তুত করে রেখেছেন যার নিম্নদেশে ঝর্ণাধারা প্রবাহিত যাতে তারা চিরকাল থাকবে। এটাই হল বিরাট সফলতা। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1325,9,90,'বেদুইনদের মধ্যেও ওজর-আপত্তি পেশকারীরা এসে অব্যাহতির আবেদন জানালো। যারা (নিজেদের ঈমান থাকার ব্যাপারে) আল্লাহ ও তাঁর রসূলের নিকট মিথ্যা বলেছিল তারাও পিছনে রয়ে গেল। তাদের (অর্থাৎ বেদুইনদের) মধ্যে যারা কুফুরী করেছে শীঘ্রই এক ভয়ঙ্কর ‘আযাব তাদেরকে পাকড়াও করবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1326,9,91,'দুর্বলের উপর, পীড়িতের উপর আর ব্যয় করার মত কোন সম্বল যাদের নেই তাদের উপর কোন অভিযোগ নেই, যদি তারা আল্লাহ ও তাঁর রসূলের প্রতি বিশ্বস্ত হয়ে থাকে। সৎ কর্মপরায়ণদের বিরুদ্ধে অভিযোগ উত্থাপন করার কোন সুযোগ নেই, আর আল্লাহ বড়ই ক্ষমাশীল, পরম দয়ালু। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1327,9,92,'তাদের বিরুদ্ধেও কোন অভিযোগ নেই যারা তোমার কাছে যখন বাহন চাওয়ার জন্য এসেছিল তখন তুমি বলেছিলে, ‘আমি তো তোমাদের জন্য কোন বাহন পাচ্ছি না’। তখন তারা ফিরে গেল, আর সে সময় তাদের চোখ থেকে অশ্রু ঝরে পড়ছিল- এ দুঃখে যে, ব্যয় বহন করার মত কোন কিছু তাদের ছিল না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1328,9,93,'অভিযোগ তো তাদের বিরুদ্ধে যারা সম্পদশালী হওয়া সত্ত্বেও (যুদ্ধে যাওয়া হতে) তোমার কাছে অব্যাহতি প্রার্থনা করেছিল, যারা ঘরে বসা থাকা (স্ত্রী লোকদের) সঙ্গে থাকতেই পছন্দ করেছিল, আল্লাহ তাদের হৃদয়কে সীল করে দিয়েছেন আর এজন্য (কিসে নিজেদের কল্যাণ আছে আর কিসে অকল্যাণ) তা তারা জানে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1329,9,94,'তোমরা তাদের কাছে (যুদ্ধ শেষে) ফিরে আসলে তারা তোমাদের কাছে ওজর পেশ করবে। বল- ওজর পেশ করো না, তোমাদেরকে আমরা কক্ষনো বিশ্বাস করব না। আল্লাহ তোমাদের খবর আমাদের কাছে জানিয়ে দিয়েছেন। আল্লাহ ও তাঁর রসূল অবশ্যই তোমাদের কাজকর্মের প্রতি দৃষ্টি রাখবেন। অতঃপর দৃশ্য ও অদৃশ্য সম্পর্কে যিনি জ্ঞাত তাঁর কাছে তোমাদেরকে ফিরিয়ে নেয়া হবে, অতঃপর তোমরা যা করতে সে সম্পর্কে তিনি তোমাদের জানিয়ে দিবেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1330,9,95,'তোমরা তাদের কাছে ফিরে আসলে তারা তোমাদের নিকট আল্লাহর নামে শপথ করবে যাতে তোমরা তাদেরকে উপেক্ষা কর। কাজেই তোমরা তাদেরকে উপেক্ষা কর, তারা অপবিত্র, তাদের বাসস্থান জাহান্নাম, তারা যা করেছে এটাই তার ন্যায্য প্রাপ্য। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1331,9,96,'তারা তোমাদের কাছে শপথ করবে যাতে তোমরা তাদের উপর খুশি হয়ে যাও, কিন্তু তোমরা তাদের উপর খুশি হলেও, আল্লাহ অবাধ্য সম্প্রদায়ের প্রতি সন্তুষ্ট হবেন না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1332,9,97,'বেদুঈন আরবরা কুফুরী আর মুনাফিকীতে সবচেয়ে কঠোর, আর আল্লাহ তাঁর রসূলের প্রতি যা অবতীর্ণ করেছেন তার সীমারেখার ব্যাপারে অজ্ঞ থাকার তারা অধিক উপযুক্ত, আর আল্লাহ সর্বজ্ঞ, মহা প্রজ্ঞাবান। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1333,9,98,'কতক বেদুঈন যা তারা আল্লাহর পথে ব্যয় করে তাকে জরিমানা বলে গণ্য করে আর তোমাদের দুঃখ মুসিবতের জন্য অপেক্ষা করতে থাকে, মন্দের চক্র তাদেরকেই ঘিরে ধরুক। আর আল্লাহ তো সব কিছুই শুনেন, সব কিছু জানেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1334,9,99,'কতক বেদুঈন আল্লাহতে ও শেষ দিবসে বিশ্বাস করে আর তারা যা আল্লাহর পথে ব্যয় করে তাকে তারা আল্লাহর নৈকট্য ও রসূলের দু‘আ লাভের মাধ্যম মনে করে, সত্যিই তা তাদের (আল্লাহর) নৈকট্য লাভের মাধ্যম, অচিরেই আল্লাহ তাদেরকে তাঁর রহমাতের মধ্যে প্রবিষ্ট করবেন, অবশ্যই আল্লাহ অতি ক্ষমাশীল, অতি দয়ালু। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1335,9,100,'মুহাজির ও আনসারদের মধ্যে যারা প্রথম সারির অগ্রণী আর যারা তাদেরকে যাবতীয় সৎকর্মে অনুসরণ করেছে, আল্লাহ তাদের প্রতি সন্তুষ্ট আর তারাও তাঁর প্রতি সন্তুষ্ট, তাদের জন্য তিনি প্রস্তুত করে রেখেছেন জান্নাত যার তলদেশে ঝর্ণাধারা প্রবাহিত, সেখানে তারা চিরকাল থাকবে। এটাই হল মহান সফলতা। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1336,9,101,'তোমাদের চতুষ্পার্শ্বে কতক বেদুঈন হল মুনাফিক, আর মাদীনাবাসীদের কেউ কেউ মুনাফিকীতে অনঢ়, তুমি তাদেরকে চেন না, আমি তাদেরকে চিনি, আমি তাদেরকে দ্বিগুণ শাস্তি দেব, (ক্ষুধা বা নিহত হওয়া এবং কবরের শাস্তি) অতঃপর তাদেরকে মহা শাস্তির পানে ফিরিয়ে আনা হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1337,9,102,'আর অন্য কতক লোক তাদের অপরাধ স্বীকার করেছে, তারা একটি সৎ কাজের সাথে আরেকটি মন্দ কাজকে মিশ্রিত করেছে, আশা করা যায় আল্লাহ তাদের তাওবা কবুল করবেন, অবশ্যই আল্লাহ অতি ক্ষমাশীল, অতীব দয়ালু। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1338,9,103,'তাদের সম্পদ থেকে সদাকাহ গ্রহণ করবে যাতে তা দিয়ে তাদেরকে পবিত্র ও পরিশুদ্ধ করতে পার। তুমি তাদের জন্য দু‘আ করবে, বস্তুতঃ তোমার দু‘আ তাদের জন্য স্বস্তিদায়ক, আর আল্লাহ সবকিছু শোনেন সব কিছু জানেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1339,9,104,'তারা কি জানে না যে, আল্লাহ তাঁর বান্দাদের (অনুশোচনাপূর্ণ) ক্ষমাপ্রার্থনা কবুল করে থাকেন আর সদাকাহ গ্রহণ করেন। আর আল্লাহই তো তাওবাহ কবূলকারী, অতি দয়ালু। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1340,9,105,'বল, তোমরা আমল করতে থাক। অচিরেই আল্লাহ তোমাদের আমালের প্রতি লক্ষ্য রাখবেন এবং তাঁর রসূল ও মু’মিনগণও (লক্ষ্য রাখবে), আর অচিরেই তোমাদেরকে দৃশ্য ও অদৃশ্যের পরিজ্ঞাতার দিকে ফিরিয়ে আনা হবে, আর তিনি তখন তোমরা যে আমল করছিলে তা তোমাদের জানিয়ে দিবেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1341,9,106,'আর কতক আল্লাহর ফায়সালার অপেক্ষায় থাকল, তিনি তাদেরকে শাস্তি দিবেন অথবা তাদের তাওবাহ কবূল করবেন; আল্লাহ সর্বজ্ঞ, বড়ই প্রজ্ঞাময়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1342,9,107,'আর যারা মাসজিদ তৈরী করেছে ক্ষতিসাধন, কুফুরী আর মু’মিনদের মধ্যে বিভেদ সৃষ্টির উদ্দেশে, আর যে ব্যক্তি ইতোপূর্বে আল্লাহ ও তাঁর রসূলের বিরুদ্ধে যুদ্ধ করেছে তার ঘাঁটি হিসেবে ব্যবহারের নিমিত্তে, তারা অবশ্য অবশ্যই শপথ করবে যে, আমাদের উদ্দেশ্য সৎ ব্যতীত নয়। আল্লাহ সাক্ষ্য দিচ্ছেন যে তারা অবশ্যই মিথ্যাবাদী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1343,9,108,'তুমি ওর ভিতরে কক্ষনো দাঁড়াবে না। প্রথম দিন থেকেই যে মাসজিদের ভিত্তি তাকওয়ার উপর প্রতিষ্ঠিত, তোমার দাঁড়ানোর জন্য সেটাই অধিক উপযুক্ত, সেখানে এমন সব লোক আছে যারা পবিত্রতা লাভ করতে ভালবাসে, আর আল্লাহ পবিত্রতা লাভকারীদের ভালবাসেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1344,9,109,'কে উত্তম যে তার ভিত্তি আল্লাহভীরুতা ও আল্লাহর সন্তুষ্টির উপর স্থাপন করে সে, না ঐ ব্যক্তি যে তার ভিত্তি স্থাপন করে পতনোম্মুখ একটি ধসের কিনারায় যা তাকে নিয়ে জাহান্নামের আগুনে ধসে পড়বে? আল্লাহ যালিমদের সঠিক পথে পরিচালিত করেন না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1345,9,110,'তাদের তৈরি ঘরটি তাদের অন্তরে সদা-সর্বদা সন্দেহের উদ্রেক করে যাবে যে পর্যন্ত না তাদের হৃদয়গুলো ছিন্ন ভিন্ন হয়ে যায়। আল্লাহ সর্বজ্ঞ, মহা প্রজ্ঞাময়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1346,9,111,'নিশ্চয় আল্লাহ মু’মিনদের কাছ থেকে তাদের জান আর মাল কিনে নিয়েছেন কারণ তাদের জন্য (বিনিময়ে) আছে জান্নাত। তারা আল্লাহর পথে যুদ্ধ করে। অতঃপর (দুশমনদের) হত্যা করে এবং (নিজেরা) নিহত হয়। এ ওয়া‘দা তাঁর উপর অবশ্যই পালনীয় যা আছে তাওরাত, ইঞ্জিল ও কুরআনে। আল্লাহর চেয়ে আর কে বেশী নিজ ওয়া‘দা পালনকারী? কাজেই তোমরা যে ক্রয় বিক্রয় সম্পন্ন করেছ তার জন্য আনন্দিত হও, আর এটাই হল মহান সফলতা। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1347,9,112,'তারা অনুশোচনাভরে (আল্লাহর দিকে) প্রত্যাবর্তনকারী, ‘ইবাদাতকারী, আল্লাহর প্রশংসাকারী, রোযা পালনকারী, রুকু‘কারী, সাজদাহকারী, সৎকাজের আদেশ দানকারী, অন্যায় কাজ হতে নিষেধকারী, আল্লাহর নির্ধারিত সীমা সংরক্ষণকারী, কাজেই (এসব) মু’মিনদেরকে সুসংবাদ দাও। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1348,9,113,'নাবী ও মু’মিনদের জন্য শোভনীয় নয় মুশরিকদের জন্য ক্ষমা প্রার্থনা করা, তারা আত্মীয়-স্বজন হলেও, যখন এটা তাদের কাছে সুস্পষ্ট যে, তারা জাহান্নামের অধিবাসী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1349,9,114,'ইবরাহীমের পিতার জন্য ক্ষমা প্রার্থনার ব্যাপারটি কেবলমাত্র তার প্রতিশ্রুতি রক্ষার্থে যা সে তার পিতাকে দিয়েছিল। কিন্তু যখন এটা তার কাছে স্পষ্ট হয়ে গেল যে, সে আল্লাহর শত্রু, তখন সে তার থেকে সম্পর্ক ছিন্ন করল; ইবরাহীম ছিল অতি কোমল হৃদয়, সহিষ্ণু। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1350,9,115,'আল্লাহ কোন সম্প্রদায়কে হিদায়াত দানের পর তাদেরকে গুমরাহ করেন না যে পর্যন্ত না তিনি তাদের কাছে স্পষ্ট করে দেন কোন্ বিষয়ে তাদেরকে তাকওয়া অবলম্বন করতে হবে। আল্লাহ হলেন সর্ব বিষয়ে সর্বাধিক অবহিত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1351,9,116,'আকাশমন্ডলী ও পৃথিবীর রাজত্ব আল্লাহরই, তিনিই জীবন দান করেন  আর  তিনিই  মৃত্যু ঘটান। আল্লাহ ছাড়া তোমাদের নেই কোন অভিভাবক, নেই কোন সাহায্যকারী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1352,9,117,'আল্লাহ অনুগ্রহ করেছেন নাবীর প্রতি, এবং মুহাজির ও আনসারদের প্রতি যারা সংকটকালে তাকে অনুসরণ করেছিল। এমনকি তাদের মধ্যে কিছু লোকের অন্তর বেঁকে যাওয়ার উপক্রম হওয়ার পরেও আল্লাহ তাদেরকে ক্ষমা করে দিয়েছিলেন। তিনি তাদের প্রতি বড়ই স্নেহশীল, বড়ই দয়ালু। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1353,9,118,'আর (তিনি অনুগ্রহ করলেন) ঐ তিনজনের প্রতিও যারা (তাবুকের যুদ্ধে অংশগ্রহণ করা থেকে) পিছনে থেকে গিয়েছিল [কা‘ব ইবনে মালিক, মুরারা ইবনে রাবী‘আ ও হিলাল ইবনে উমাইয়্যা (রাযি।)] তাঁরা অনুশোচনার আগুনে এমনি দগ্ধীভূত হয়েছিলেন যে] শেষ পর্যন্ত পৃথিবী তার পূর্ণ বিস্তৃতি নিয়েও তাদের প্রতি সংকুচিত হয়ে গিয়েছিল আর তাদের জীবন দুর্বিষহ হয়ে উঠল আর তারা বুঝতে পারল যে, আল্লাহ ছাড়া তাদের কোন আশ্রয়স্থল নেই তাঁর পথে ফিরে যাওয়া ব্যতীত। অতঃপর তিনি তাদের প্রতি অনুগ্রহ করলেন যাতে তারা অনুশোচনায় তাঁর দিকে ফিরে আসে। আল্লাহ  অতিশয় তাওবাহ কবূলকারী, বড়ই দয়ালু। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1354,9,119,'ওহে  বিশ্বাসীগণ!  তোমরা আল্লাহকে ভয় কর এবং সত্যপন্থীদের অর্ন্তভুক্ত হও। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1355,9,120,'মাদীনাবাসী ও তার চতুষ্পার্শ্বস্থ বেদুঈনদের জন্য উচিত নয় আল্লাহর রসূলের (সঙ্গ বাদ দিয়ে) পেছনে থেকে যাওয়া আর নিজেদের জীবনকে তাঁর জীবনের চেয়ে প্রিয় জ্ঞান করা। কেননা এমন কক্ষনো হবে না যে, তারা আল্লাহর পথে তৃষ্ণা, দৈহিক ক্লেশ ও ক্ষুধা ভোগ করবে, আর কাফিরদের ক্রোধ উদ্রেককারী কোন পদক্ষেপ গ্রহণ করবে আর শত্রুদের নিকট থেকে কিছু লাভ করবে আর তার বিনিময়ে তাদের জন্য কোন নেক ‘আমাল লেখা হবে না (অবশ্যই লেখা হবে)। নিশ্চয় আল্লাহ সৎকর্মশীলদের প্রতিফল বিনষ্ট করেন না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1356,9,121,'আর এটাও হবে না যে, তারা কম বা বেশী মাল (আল্লাহর পথে) খরচ করবে আর (জিহাদে) তারা কোন উপত্যকা অতিক্রম করবে অথচ তা তাদের নামে লেখা হবে না (অবশ্যই লেখা হবে) যাতে আল্লাহ  তাদেরকে তাদের কাজের উৎকৃষ্ট প্রতিদান দিতে পারেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1357,9,122,'মু’মিনদের সকলের একসঙ্গে অভিযানে বের হওয়া ঠিক নয়। তাদের প্রত্যেক দল থেকে একটি অংশ কেন বের হয় না যাতে তারা দ্বীন সম্পর্কে জ্ঞানের অনুশীলন করতে পারে এবং ফিরে আসার পর তাদের সম্প্রদায়কে সতর্ক করতে পারে যাতে তারা (অসদাচরণ) থেকে বিরত হয়? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1358,9,123,'হে মু’মিনগণ! যে সব কাফির তোমাদের নিকটবর্তী তাদের বিরুদ্ধে যুদ্ধ কর, যাতে তারা তোমাদের মধ্যে দৃঢ়তা দেখতে পায়, আর জেনে রেখ যে, আল্লাহ মুত্তাকীদের সঙ্গে আছেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1359,9,124,'যখনই কোন সূরাহ নাযিল হয় তখন তাদের কতক লোক (বিদ্রূপ করে) বলে- ‘‘এতে তোমাদের কার ঈমান বৃদ্ধি হল?’’(মুনাফিকরা জেনে রাখুক) যারাই ঈমান এনেছে তাদের ঈমান বৃদ্ধি হয় আর তারা এতে আনন্দিত হয়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1360,9,125,'আর যাদের অন্তরে ব্যাধি আছে তাদের নাপাকীর উপর আরো নাপাকী বাড়িয়ে দেয় (প্রতিটি নতুন সূরা), আর তাদের মৃত্যু হয় কাফির অবস্থায়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1361,9,126,'তারা কি দেখে না যে, প্রতি বছরই তাদেরকে একবার বা দু’বার পরীক্ষায় ফেলা হয়; তারপরেও তারা তাওবাও করে না, আর শিক্ষাও গ্রহণ করে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1362,9,127,'যখনই কোন সূরাহ নাযিল হয় তখনই তারা পরস্পরে চোখ চাওয়া-চাওয়ি করে আর (ইঙ্গিতে জিজ্ঞেস করে) ‘তোমাদেরকে কেউ দেখছে না তো?’’ অতঃপর তারা চুপিসারে সরে পড়ে। আল্লাহ তাদের অন্তরকে (সত্য পথ থেকে) ফিরিয়ে দিয়েছেন, কেননা তারা এমনই এক সম্প্রদায় যারা বুঝে না।  ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1363,9,128,'তোমাদের মধ্য থেকেই তোমাদের নিকট একজন রসূল এসেছেন, তোমাদেরকে যা কিছু কষ্ট দেয় তা তার নিকট  খুবই  কষ্টদায়ক। সে তোমাদের কল্যাণকামী, মু’মিনদের প্রতি করুণাসিক্ত, বড়ই দয়ালু। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1364,9,129,'এ সত্ত্বেও যদি তারা মুখ ফিরিয়ে নেয় তাহলে বলে দাও- আল্লাহ্ই আমার জন্য যথেষ্ট, তিনি ছাড়া সত্যিকারের কোন ইলাহ নেই, তাঁর উপরই আমি ভরসা করি, তিনি হলেন মহান আরশের মালিক। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1365,10,1,'আলিফ-লাম-র, এগুলো মহা বিজ্ঞানময় গ্রন্থের আয়াতসমূহ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1366,10,2,'মানুষের কাছে কি এটা আশ্চর্যের বিষয় যে, আমি তাদেরই মধ্যেকার একজন লোকের কাছে ওয়াহী পাঠিয়েছি যে, লোকদের সতর্ক করে দাও, আর যারা ঈমান আনে তাদেরকে সুসংবাদ দাও যে, তাদের জন্য তাদের প্রতিপালকের কাছে আছে মহা মর্যাদা, (কিন্তু) কাফিররা বলে, ‘এ ব্যক্তি তো প্রকাশ্য যাদুকর’। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1367,10,3,'নিশ্চয়ই তোমাদের প্রতিপালক হলেন আল্লাহ যিনি আকাশমন্ডলী আর পৃথিবীকে ছয় দিনে সৃষ্টি করেছেন। অতঃপর তিনি আরশে সমুন্নত হয়েছেন। তিনি যাবতীয় বিষয়াদি পরিচালনা করেন। তাঁর অনুমতি প্রাপ্তি ছাড়া সুপারিশ করার কেউ নেই। ইনিই হলেন আল্লাহ, তোমাদের প্রতিপালক। কাজেই তোমরা তাঁরই ‘ইবাদাত কর, তোমরা কি উপদেশ গ্রহণ করবে না? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1368,10,4,'তাঁর কাছেই তোমাদের সকলের প্রত্যাবর্তন। আল্লাহর ওয়া‘দা নিশ্চিত সত্য। তিনি সৃষ্টির সূচনা করেন, পরে তিনিই আবার সৃষ্টি করবেন যাতে তিনি- যারা ঈমান এনেছে ও সৎকর্ম করেছে- তাদেরকে পূর্ণ ইনসাফের সাথে প্রতিদান দিতে পারেন। আর যারা কুফুরী করেছে তাদের জন্য আছে অতি উত্তপ্তপানীয় ও বেদনা দায়ক শাস্তি, যেহেতু তারা সত্য প্রত্যাখ্যান করত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1369,10,5,'তিনি সূর্যকে করেছেন তেজোদীপ্ত, আর চন্দ্রকে করেছেন আলোকময় আর তার (হ্রাস বৃদ্ধির) মানযিলসমূহ সঠিকভাবে নির্ধারণ করেছেন যাতে তোমরা বৎসর গুণে (সময়ের) হিসাব রাখতে পার। আল্লাহ এটা অনর্থক সৃষ্টি করেননি, তিনি নিদর্শনগুলোকে বিশদভাবে বর্ণনা করেন জ্ঞানী সম্প্রদায়ের জন্য। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1370,10,6,'নিশ্চয়ই রাত ও দিনের আবর্তনে, আর আকাশমন্ডলী ও পৃথিবীর মাঝে আল্লাহ যা সৃষ্টি করেছেন তাতে মুত্তাকী সম্প্রদায়ের জন্য অবশ্যই নিদর্শন রয়েছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1371,10,7,'যারা আমার সঙ্গে সাক্ষাৎ লাভের আশা রাখে না, এবং দুনিয়ার জীবন নিয়েই সন্তুষ্ট থাকে আর তাতেই নিশ্চিন্ত হয় এবং যারা আমার নিদর্শনগুলো হতে একেবারে উদাসীন, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1372,10,8,'তাদের আবাস হল জাহান্নাম তাদের কৃতকর্মের কারণে।  ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1373,10,9,'যারা ঈমান আনে আর সৎ ‘আমাল করে, তাদের প্রতিপালক তাদের ঈমানের বদৌলতে তাদেরকে সৎপথে পরিচালিত করবেন। নি‘মাতরাজি দ্বারা পরিপূর্ণ জান্নাতে, তাদের পাদদেশে ঝর্ণাধারা প্রবাহিত হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1374,10,10,'তার ভিতরে তাদের দু’আ হবে, ‘‘পবিত্র তুমি হে আল্লাহ’’। আর সেখানে তাদের অভিবাদন হবে ‘‘শান্তি’’, আর তাদের দু‘আর সর্বশেষ কথা হবে ‘‘সমস্ত প্রশংসা জগৎসমূহের প্রতিপালক আল্লাহর জন্য’’।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1375,10,11,'মানুষের অপকর্মের শাস্তি হিসেবে আল্লাহ যদি মানুষের অকল্যাণ করার ব্যাপারে দ্রুততা অবলম্বন করতেন যতটা দ্রুততার সঙ্গে তারা (দুনিয়ার) কল্যাণ পেতে চায়, তবে তাদের কাজ করার অবকাশ কবেই না খতম করে দেয়া হত, (কিন্তু আল্লাহ তা করেন না)। কাজেই যারা আমার সাক্ষাতের আশা রাখে না, তাদেরকে আমি তাদের অবাধ্যতায় দিশেহারা হয়ে ঘুরে বেড়ানোর অবকাশ দেই। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1376,10,12,'মানুষকে যখন দুঃখ ক্লেশ স্পর্শ করে, তখন তারা শুয়ে, বসে ও দাঁড়িয়ে আমাকে ডাকতে থাকে। অতঃপর যখন আমি তার দুঃখ ক্লেশ দূর করে দেই, তখন সে এমনভাবে চলে যায়, মনে হয় যেন তাকে দুঃখ-ক্লেশ স্পর্শ করার কারণে সে আমাকে কখনই ডাকেনি। এভাবেই যারা সীমালঙ্ঘন করে তাদের জন্য তাদের কাজকর্মগুলোকে চাকচিক্যময় বানিয়ে দেয়া হয়েছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1377,10,13,'তোমাদের পূর্বেকার বহু জনগোষ্ঠীকে আমি ধ্বংস করে দিয়েছি যখন তারা বাড়াবাড়িতে লিপ্ত হয়েছিল, তাদের কাছে রসূলগণ সুস্পষ্ট নিদর্শনসমূহ নিয়ে এসেছিল, কিন্তু তারা আদৌ ঈমান আনেনি। এভাবেই আমি অপরাধীদেরকে (পাপের) প্রতিদান দিয়ে থাকি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1378,10,14,'অতঃপর তাদের পর আমি তোমাদেরকে পৃথিবীতে তাদের স্থলাভিষিক্ত করেছি এটা দেখার জন্য যে, তোমরা কী রকম ‘আমাল কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1379,10,15,'যখন আমার সুস্পষ্ট আয়াতগুলো তাদের কাছে পঠিত হয়, তখন যারা আমার সাক্ষাতের আশা রাখে না তারা বলে, ‘এটা বাদে অন্য আরেকটা কুরআন আন কিংবা ওটাকে বদলাও’। বল, ‘‘আমার নিজের ইচ্ছেমত ওটা বদলানো আমার কাজ নয়, আমার কাছে যা ওয়াহী করা হয় আমি কেবল সেটারই অনুসরণ করে থাকি। আমি আমার প্রতিপালকের অবাধ্যতা করলে এক অতি বড় বিভীষিকার দিনে আমি শাস্তির ভয় করি’’। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1380,10,16,'বল, ‘‘আল্লাহর ইচ্ছে হলে আমি তোমাদের কাছে তা তিলাওয়াত করতাম না, আর আল্লাহও তোমাদেরকে তার খবর দিতেন না। আমি তো এর পূর্বে একটা দীর্ঘ সময় তোমাদের মাঝে অতিবাহিত করেছি, তা সত্ত্বেও তোমরা কি বুঝবে না? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1381,10,17,'তার চেয়ে বড় যালিম আর কে হতে পারে যে মিথ্যা রচনা ক’রে আল্লাহর নামে চালিয়ে দেয় অথবা আল্লাহর আয়াতসমূহকে মিথ্যা বলে ঘোষণা করে; নিশ্চিতই অপরাধীরা সাফল্য লাভ করতে পারে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1382,10,18,'আর তারা আল্লাহকে ছেড়ে ‘ইবাদাত করে এমন কিছুর যা না পারে তাদের কোন ক্ষতি করতে, আর না পারে কোন উপকার করতে। আর তারা বলে, ‘‘ওগুলো আমাদের জন্য আল্লাহর কাছে সুপারিশকারী’’। বল, ‘‘তোমরা কি আল্লাহকে এমন কিছুর সংবাদ দিতে চাও, যা তিনি অবগত নন, না আকাশমন্ডলীতে আর না যমীনে? মহান পবিত্র তিনি, তোমরা যা কিছুকে তাঁর শরীক গণ্য কর তাত্থেকে তিনি বহু ঊর্ধ্বে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1383,10,19,'মানুষ ছিল এক উম্মতভুক্ত। পরে তারা মতভেদ সৃষ্টি করল। তোমার প্রতিপালক পূর্বেই যদি সিদ্ধান্ত গ্রহণ না করতেন, তাহলে যে বিষয়ে তারা মতভেদ করছে তার মীমাংসা অবশ্যই করে দেয়া হত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1384,10,20,'তারা বলে, ‘‘তাঁর প্রতিপালকের পক্ষ থেকে তাঁর কাছে কোন নিদর্শন অবতীর্ণ হয় না কেন?’’ এদের জবাবে বলে দাও, ‘‘অদৃশ্য জগতের একচ্ছত্র মালিক হলেন আল্লাহ, কাজেই তোমরা অপেক্ষা কর (এবং ভবিষ্যতে কী হয় দেখ), আমিও তোমাদের সাথে অপেক্ষমান থাকলাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1385,10,21,'দুঃখ কষ্ট মানুষকে স্পর্শ করার পর আমি যখন তাদেরকে অনুগ্রহ আস্বাদন করতে দেই, তখন তারা আমার নিদর্শনগুলোর ব্যাপারে কুট কৌশলের আশ্রয় নেয়। বল, ‘কৌশল গ্রহণে আল্লাহ হলেন ক্ষিপ্রগতিসম্পন্ন, তোমরা যে সব কূটচাল গ্রহণ কর আমার ফেরেশতাগণ তা লিপিবদ্ধ করে রাখে।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1386,10,22,'তিনি তোমাদেরকে জলে ও স্থলে ভ্রমণ করান। এমনকি যখন তোমরা নৌকায় আরোহণ করে অনুকূল হাওয়ার তালে আমোদ আহলাদে সফর করতে থাক, তখন ঝড়ো হাওয়া আঘাত হানে আর চারদিক থেকে তরঙ্গ ধেয়ে আসে, আর তারা মনে করে যে, তারা তরঙ্গমালায় পরিবেষ্টিত হয়ে পড়েছে। তখন তারা বিশুদ্ধ আনুগত্যে আল্লাহকে ডেকে বলে, ‘তুমি যদি এত্থেকে আমাদেরকে পরিত্রাণ দাও তাহলে অবশ্য অবশ্যই আমরা শুকরগুজার বান্দাদের অন্তর্ভুক্ত হয়ে যাব।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1387,10,23,'অতঃপর যেমনই তিনি তাদেরকে বাঁচিয়ে দেন, তখন তারা অন্যায়ভাবে যমীনে বিদ্রোহী আচরণ শুরু করে দেয়। ওহে মানুষ! তোমাদের এ বিদ্রোহ তো (প্রকৃতপক্ষে) তোমাদের নিজেদেরই বিপক্ষে, অস্থায়ী দুনিয়ার আনন্দ সামগ্রী মাত্র। অতঃপর আমার কাছেই তোমাদের প্রত্যাবর্তন হবে, তখন আমি তোমাদেরকে জানিয়ে দেব তোমরা যা কিছু করছিলে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1388,10,24,'দুনিয়ার জীবনের দৃষ্টান্ত হচ্ছে যেমন আকাশ থেকে আমি পানি বর্ষণ করি যার সংস্পর্শে ঘন সন্নিবিষ্ট ভূমি জাত উদ্ভিদ উৎপন্ন হয়। যাত্থেকে ভক্ষণ করে মানুষ আর জীবজন্তু। অবশেষে যমীন যখন সোনালীরূপ ধারণ করে আর শোভামন্ডিত হয়, আর তার মালিকগণ ভাবতে থাকে যে, ওগুলো তাদের হাতের মুঠোয় তখন রাত্রিকালে কিংবা দিনের বেলা আমার নির্দেশ এসে পড়ে আর আমি ওগুলো এমনভাবে ধ্বংস করে দেই মনে হয় যেন গতকাল সেখানে কোন কিছুই ছিল না। এভাবে আমি আমার নিদর্শনগুলোকে বিশদভাবে বর্ণনা করি ঐ সম্প্রদায়ের জন্য যারা চিন্তাভাবনা করে বুঝতে চেষ্টা করে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1389,10,25,'আল্লাহ তোমাদেরকে শান্তির কেন্দ্রভূমির দিকে আহবান জানান আর যাকে তিনি চান সঠিক পথে পরিচালিত করেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1390,10,26,'যারা কল্যাণকর কাজ করে তাদের জন্য রয়েছে কল্যাণ এবং আরো অতিরিক্ত (পুরস্কার), কলংক ও লাঞ্ছনা তাদের মুখমন্ডলকে মলিন করবে না, তারাই হল জান্নাতের অধিবাসী, সেখানে তারা থাকবে চিরকাল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1391,10,27,'পক্ষান্তরে যারা মন্দ কাজ করে, তারা তাদের মন্দ কাজের শাস্তি পাবে কাজের অনুপাতে এবং অপমান তাদেরকে আচ্ছাদিত করবে, আল্লাহর (শাস্তি) হতে কেউই তাদেরকে রক্ষা করতে পারবে না- যেন তাদের মুখমন্ডলকে আচ্ছাদিত করে দেয়া হয়েছে গাঢ় অন্ধকার রাত্রির টুকরো দিয়ে; তারা জাহান্নামের অধিবাসী, তারা তার মধ্যে চিরকাল থাকবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1392,10,28,'সেদিন আমি তাদের সবাইকে একত্রিত করে যারা শিরক করেছিল তাদেরকে বলব, ‘‘তোমরা এবং তোমরা যাদেরকে শরীক করেছিলে তারা নিজ নিজ জায়গায় থাক।’’ আমি তাদেরকে পরস্পর থেকে পৃথক করে দেব আর তারা যাদেরকে শরীক করেছিল তারা বলবে, ‘তোমরা তো আমাদের ‘ইবাদাত করতে না।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1393,10,29,'এখন আল্লাহই আমাদের আর তোমাদের মাঝে সাক্ষী হিসেবে যথেষ্ট, আমরা তোমাদের ‘ইবাদাতের ব্যাপারে সম্পূর্ণ বেখবর ছিলাম।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1394,10,30,'সেখানে প্রতিটি আত্মা তার পূর্বকৃত কাজ (এর ফলাফল) দেখতে পাবে। তাদেরকে তাদের প্রকৃত অভিভাবক আল্লাহর নিকট ফিরিয়ে আনা হবে, আর তাদের রচিত সকল মিথ্যা তাদের থেকে বিলীন হয়ে যাবে।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1395,10,31,'তাদের জিজ্ঞেস কর, ‘আকাশ আর যমীন হতে কে তাদের জীবিকার ব্যবস্থা করে? কিংবা শ্রবণশক্তি ও দর্শনশক্তি কার মালিকানাধীন? আর মৃত থেকে জীবিতকে কে বের করেন আর কে মৃতকে জীবিত থেকে বের করেন? যাবতীয় বিষয়ের শাসন ও নিয়ন্ত্রণ কার অধীনস্থ?’ তারা বলে উঠবে, ‘‘আল্লাহ’’। তাহলে তাদেরকে বল, ‘তবুও তোমরা তাক্বওয়াহ অবলম্বন করবে না?’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1396,10,32,'তিনিই আল্লাহ, তোমাদের প্রকৃত প্রতিপালক। প্রকৃত সত্যের পর গুমারাহী ছাড়া আর কী থাকতে পারে? তোমাদেরকে কোনদিকে ঘুরানো হচ্ছে? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1397,10,33,'এভাবে সত্য প্রত্যাখ্যানকারীদের ব্যাপারে তোমার প্রতিপালকের কথা সত্য সাব্যস্ত হয়েছে যে, তারা ঈমান আনবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1398,10,34,'বল ‘‘তোমরা যাদেরকে শরীক কর তাদের মধ্যে এমন কেউ আছে কি যে সৃষ্টির সূচনা করে এবং তার পুনরাবর্তনও ঘটাতে পারে?’’ বল ‘‘আল্লাহই সৃষ্টির সূচনা করেন এবং তার পুনরাবর্তন ঘটান।’’ তাহলে কীভাবে তোমরা বিচ্যুত হয়ে যাচ্ছ (সত্য পথ থেকে)? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1399,10,35,'বল ‘‘তোমরা যাদেরকে শরীক কর তাদের কেউ কি সত্যের পথ দেখাতে পারে?’’ বল, ‘আল্লাহই সত্যের পথ দেখান।’ তবে যিনি সত্য পথে পরিচালিত করেন আনুগত্য লাভের তিনিই বেশি হাকদার, না কি সেই যাকে পথ না দেখালে পথ পায় না? তোমাদের হয়েছে কী? তোমরা কীভাবে সিদ্ধান্ত নিচ্ছ? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1400,10,36,'তাদের অধিকাংশই কেবল ধারণার অনুসরণ করে, সত্যের মুকাবালায় ধারণা কোন কাজে আসে না। তোমরা যা কর সে সম্পর্কে আল্লাহ সর্বাধিক অবগত।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1401,10,37,'এ কুরআন আল্লাহ ছাড়া অন্য কারো রচিত নয়। উপরন্তু তা পূর্বে যা নাযিল হয়েছিল তার সমর্থক আর বিস্তারিত ব্যাখ্যাকৃত কিতাব, এতে কোন সন্দেহ নেই, জগৎসমূহের প্রতিপালকের নিকট হতে (নাযিলকৃত)। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1402,10,38,'তারা কি এ কথা বলে যে, সে [অর্থাৎ মুহাম্মাদ (সা.)] এটা রচনা করেছে? বল, তাহলে তোমরাও এর মত একটা সূরাহ (রচনা করে) নিয়ে এসো আর আল্লাহকে বাদ দিয়ে যাকে পার তাকে ডেকে নাও যদি তোমরা সত্যবাদী হয়ে থাক [যে মুহাম্মাদ (সাঃ)-ই তা রচনা করেছেন]। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1403,10,39,'বরং যে বিষয় তাদের জ্ঞানের সীমার মধ্যে আসে না, আর যার পরিণাম ফল এখনও উপস্থিত হয়নি তা তারা অস্বীকার করে। এভাবে তাদের পূর্ববর্তী লোকেরাও মিথ্যা মনে ক’রে অমান্য করেছিল। এখন দেখ, এই যালিমদের পরিণতি কী হয়েছে! ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1404,10,40,'এদের কিছু লোক এতে বিশ্বাস করে আর কতক এতে বিশ্বাস করে না। তোমার প্রতিপালক এই ফাসাদ সৃষ্টিকারী লোকদের সম্পর্কে সবচেয়ে বেশি অবহিত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1405,10,41,'যদি তারা তোমাকে মিথ্যা জেনে অমান্য ক’রে তাহলে বল, ‘আমার কাজের জন্য আমি দায়ী, আর তোমাদের কাজের জন্য তোমরা দায়ী, আমি যা করি তার দায়-দায়িত্ব থেকে তোমরা মুক্ত, আর তোমরা যা কর তার দায়-দায়িত্ব থেকে আমি মুক্ত।’');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1406,10,42,'এদের মধ্যে কেউ কেউ তোমার কথা শুনার ভান করে। তাহলে তুমি কি বধিরকে শুনাবে, তারা না বুঝলেও? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1407,10,43,'তাদের মধ্যে কেউ কেউ তোমার দিকে তাকায়, তুমি কি অন্ধকে পথ দেখাবে, তারা না দেখলেও? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1408,10,44,'অবশ্যই আল্লাহ মানুষদের প্রতি কোন যুলম করেন না, কিন্তু মানুষ নিজেদের প্রতি যুলম ক’রে থাকে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1409,10,45,'যে দিন তাদেরকে একত্রিত করা হবে (সেদিন তারা মনে করবে যে) দিনের এক মুহূর্তের বেশি তারা (দুনিয়াতে) অবস্থান করেনি। তারা পরস্পরকে চিনতে পারবে। যারা আল্লাহর সাক্ষাৎকে অস্বীকার করে তারা ক্ষতিগ্রস্ত হয়েছে আর তারা কখনই সঠিক পথপ্রাপ্ত ছিল না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1410,10,46,'আমি তাদেরকে যে পরিণতির ভয় দেখিয়েছি তার কিছু অংশ আমি যদি তোমাকে দেখিয়ে দেই, কিংবা (তার পূর্বেই) তোমাকে যদি উঠিয়ে দেই, (অবস্থা যেটাই হোক না কেন) তাদের প্রত্যাবর্তন তো আমার কাছেই, (সর্বোপরি) তারা যা কিছু করছে আল্লাহ হচ্ছেন তার সাক্ষী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1411,10,47,'প্রত্যেক জাতির জন্য (পাঠানো হয়েছে) একজন রসূল। তাদের রসূল যখন এসেছে তখন ন্যায়পরায়ণতার সঙ্গে তাদের মাঝে ফায়সালা করা হয়েছে। তাদের প্রতি কোন যুলম করা হয়নি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1412,10,48,'তারা বলে, ‘‘তোমরা সত্যবাদী হলে (বল তো) এই প্রতিশ্রুতি কখন সত্য হয়ে দেখা দিবে?’’');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1413,10,49,'বল, ‘আল্লাহর ইচ্ছে ব্যতীত আমার নিজেরও কোন ক্ষতি বা লাভ করার ক্ষমতা নেই।’ প্রত্যেক সম্প্রদায়ের জন্য একটা নির্দিষ্ট সময় নির্ধারিত আছে। তাদের সেই নির্দিষ্ট সময় চলে আসলে তারা এক মুহূর্তও আগ-পাছ করতে পারবে না।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1414,10,50,'বল, ‘তোমরা কি চিন্তা করে দেখেছ যদি তাঁর ‘আযাব হঠাৎ করে রাতে বা দিনে তোমাদের উপর এসে পড়ে (তাহলে তো তোমাদের করার কিছুই নেই) অপরাধীরা তার কোন্টা তাড়াহুড়ো করে আনতে চায়? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1415,10,51,'তাহলে ওটা বাস্তবে ঘটে যাওয়ার পরই কি তোমরা তাতে বিশ্বাস করতে চাও? (ঘটে যাওয়ার পর বলা হবে) ‘এখন, (বিশ্বাস করলে) এটার জন্য তোমরা তাড়াহুড়ো করছিলে!’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1416,10,52,'অবশেষে যালিমদেরকে বলা হবে- ‘স্থায়ী শাস্তির স্বাদ গ্রহণ কর, তোমরা যা কিছু উপার্জন করেছিলে তার প্রতিফল ছাড়া তোমাদের আর কী দেয়া যেতে পারে! ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1417,10,53,'তারা তোমার কাছে জানতে চায় (তুমি যা বলছ) সেগুলো কি প্রকৃতই সঠিক? বল-‘হাঁ, আমার রবের কসম! তা একবারেই সত্য। তোমরা তা বানচাল করতে পারবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1418,10,54,'যুলম করেছে এমন প্রত্যেকেই দুনিয়াতে যা কিছু আছে যদি তা সব তার হত তবে সে বিনিময়ে তা প্রদান করে ‘আযাব হতে বাঁচতে চাইত। তারা যখন ‘আযাব প্রত্যক্ষ করবে তখন মনের দুঃখ-তাপ গোপন করবে। ন্যায়পরায়ণতার সঙ্গে তাদের ব্যাপারে ফায়সালা করা হবে। তাদের প্রতি কোন প্রকার যুলম করা হবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1419,10,55,'জেনে রেখ, আসমানসমূহ আর যমীনে যা কিছু আছে সবই আল্লাহর। জেনে রেখ, আল্লাহর ওয়া‘দা সত্য, কিন্তু অধিকাংশ মানুষ (এ সকল বিষয়) জানে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1420,10,56,'তিনি জীবন দান করেন, তিনিই মৃত্যু দেন, আর তাঁর দিকেই তোমরা ফিরে যাবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1421,10,57,'হে মানুষ! তোমাদের প্রতিপালকের নিকট থেকে তোমাদের কাছে এসেছে নাসীহাত আর তোমাদের অন্তরে যা আছে তার নিরাময়, আর মু’মিনদের জন্য সঠিক পথের দিশা ও রহমাত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1422,10,58,'বল, আল্লাহর অনুগ্রহ ও দয়ার বদৌলতে (তা এসেছে), এজন্য তারা আনন্দিত হোক। তারা যা স্তুপীকৃত করছে তার চেয়ে তা (অর্থাৎ হিদায়াত ও রহমাতপূর্ণ কুরআন) উত্তম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1423,10,59,'বল-তোমরা কি ভেবে দেখেছ আল্লাহ যে রিযক তোমাদের জন্য পাঠিয়েছেন, তোমরা তার কতকগুলোকে হারাম আর কতককে হালাল করে নিয়েছ। বল, আল্লাহ কি তোমাদেরকে এর অনুমতি দিয়েছেন? না তোমরা আল্লাহর উপর মিথ্যা অপবাদ দিচ্ছ? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1424,10,60,'যারা আল্লাহর প্রতি মিথ্যোরোপ করে, কিয়ামাতের দিন (আল্লাহ তাদের সঙ্গে যে ব্যবহার করবেন সে) সম্পর্কে তাদের কী ধারণা? আল্লাহ তো মানুষদের উপর বড়ই অনুগ্রহশীল, কিন্তু তাদের অধিকাংশই আল্লাহর শোকর করে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1425,10,61,'তুমি যে অবস্থাতেই থাক না কেন, আর তুমি কুরআন থেকে যা কিছুই তিলাওয়াত কর না কেন, আর যে ‘আমালই তোমরা কর না কেন, আমি তোমাদের উপর রয়েছি প্রত্যক্ষদর্শী, যখন তোমরা তাতে পূর্ণরূপে মনোনিবেশ কর। এমন অণু পরিমাণ বা তাত্থেকে ছোট বা তাত্থেকে বড় বস্তু না আছে পৃথিবীতে, আর না আছে আসমানে যা তোমার প্রতিপালকের দৃষ্টির আড়ালে আছে। তা (লেখা) আছে এক সুস্পষ্ট কিতাবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1426,10,62,'জেনে রেখ! আল্লাহর বন্ধুদের কোন ভয় নেই আর তারা দুঃখিতও হবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1427,10,63,'যারা ঈমান আনে আর তাকওয়া অবলম্বন করে ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1428,10,64,'তাদের জন্য সুসংবাদ দুনিয়ার জীবনে আর আখেরাতেও। আল্লাহর কথার কোন হেরফের হয় না, এটাই হল বিরাট সাফল্য। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1429,10,65,'ওদের কথা যেন তোমাকে দুঃখ না দেয়, যাবতীয় সম্মান আল্লাহরই জন্য, তিনি সব কিছুই শোনেন, সব কিছু জানেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1430,10,66,'জেনে রেখ! যা কিছু আসমানসমূহে আছে আর যারা যমীনে আছে সবাই আল্লাহর। (এ অবস্থায়) যারা আল্লাহকে বাদ দিয়ে (তাদের মনগড়া) শরীকদের ডাকে তারা কিসের অনুসরণ করে? তারা ধারণা-অনুমান ছাড়া অন্য কিছুরই অনুসরণ করে না, আর তারা শুধু মিথ্যাই বলে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1431,10,67,'তিনিই তোমাদের জন্য রাত বানিয়েছেন যেন তোমরা তাতে শান্তি লাভ করতে পার, আর দিন সৃষ্টি করেছেন (সব কিছু) দেখার জন্য। অবশ্যই এতে নিদর্শন আছে ঐ সম্প্রদায়ের জন্য যারা (মনোযোগ দিয়ে) শোনে।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1432,10,68,'ওরা বলে-‘‘আল্লাহ পুত্র গ্রহণ করেছেন’’। মহান পবিত্র তিনি, তিনি কারো মুখাপেক্ষী নন, আসমানসমূহে ও যমীনে যা আছে সবই তাঁর মালিকানাধীন, (আল্লাহ পুত্র গ্রহণ করেছেন) এ ব্যাপারে তোমাদের কাছে কোন প্রমাণ নেই, তাহলে তোমরা কি আল্লাহ সম্পর্কে এমন কথা বলছ যে বিষয়ে তোমাদের কোনই জ্ঞান নেই? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1433,10,69,'বল ‘‘যারা আল্লাহ সম্পর্কে মিথ্যে রচনা করে, তারা কক্ষনো কল্যাণ পাবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1434,10,70,'দুনিয়াতে আছে তাদের জন্য সামান্য ভোগ্যবস্তু, অতঃপর আমার কাছেই হবে তাদের প্রত্যাবর্তন, তখন তাদের কুফুরীর কারণে তাদেরকে আমি কঠিন ‘আযাব আস্বাদন করাব। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1435,10,71,'তাদেরকে নূহের কাহিনী পড়ে শোনাও। যখন সে তার সম্প্রদায়কে বলেছিল, ‘হে আমার সম্প্রদায়! আমার অবস্থিতি আর আল্লাহর আয়াতসমূহ দ্বারা তোমাদের প্রতি আমার উপদেশ দান যদি তোমাদের নিকট অসহ্য মনে হয় (তাতে আমার কোন পরোয়া নেই) কারণ আমি ভরসা করি আল্লাহর উপর। তোমরা তোমাদের শরীকদেরকে নিয়ে সম্মিলিতভাবে সিদ্ধান্ত গ্রহণ কর, পরে তোমাদের সিদ্ধান্তের ব্যাপারে তোমাদের মাঝে যেন অস্পষ্টতা না থাকে, অতঃপর আমার উপর তা কার্যকর কর আর আমাকে কোন অবকাশই দিও না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1436,10,72,'আর যদি তোমরা (আমার আহবান থেকে) মুখ ফিরিয়ে নাও (তাতে আমার কোন ক্ষতি হবে না), আমি তো তোমাদের কাছে কোন পারিশ্রমিক চাচ্ছি না, আমার পারিশ্রমিক আছে কেবল আল্লাহরই নিকট, আমাকে আত্মসমর্পণকারীদের মধ্যে শামিল হওয়ারই আদেশ দেয়া হয়েছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1437,10,73,'কিন্তু তারা তাকে মিথ্যে বলে অমান্য করল। তখন আমি তাকে আর তার সঙ্গে যারা নৌকায় ছিল তাদেরকে রক্ষা করলাম আর তাদেরকে (পৃথিবীতে) উত্তরাধিকারী বানালাম, আর যারা আমার আয়াতসমূহকে মিথ্যে ব’লে অমান্য করেছিল তাদেরকে ডুবিয়ে মারলাম। এখন দেখ যাদেরকে সতর্ক করা হয়েছিল (তারা সতর্ক না হওয়ায়) তাদের কী পরিণাম ঘটেছিল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1438,10,74,'নূহের পর আমি রসূলদেরকে তাদের সম্প্রদায়ের কাছে পাঠিয়েছিলাম, তারা তাদের কাছে সুস্পষ্ট নিদর্শন নিয়ে এসেছিল। কিন্তু পূর্বে তারা মিথ্যে জেনে প্রত্যাখ্যান করায় পরে আর ঈমান আনতে প্রস্তুত হয়নি। সীমালঙ্ঘনকারীদের হৃদয়ে এভাবেই আমি মোহর লাগিয়ে দেই। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1439,10,75,'তাদের পর আমি মূসা ও হারূনকে আমার নিদর্শন সহকারে ফির‘আওন ও তার প্রধানদের নিকট পাঠিয়েছিলাম। কিন্তু তারা অহঙ্কার করে, তারা ছিল এক অপরাধী সম্প্রদায়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1440,10,76,'অতঃপর আমার নিকট থেকে যখন তাদের কাছে প্রকৃত সত্য এসে পড়ল, তখন তারা বলল, ‘‘এটা তো অবশ্যই সুস্পষ্ট যাদু’’। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1441,10,77,'মূসা বলল, ‘‘প্রকৃত সত্য সম্পর্কে তোমরা (এ রকম) কথা বলছ যখন তা তোমাদের কাছে এসে গেছে। এটা কি যাদু? যাদুকররা মুক্তি পাবে না।’’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1442,10,78,'তারা বলল, ‘‘তোমরা কি আমাদেরকে ঐ পথ থেকে সরিয়ে দেয়ার জন্য এসেছ আমরা আমাদের পিতৃপুরুষদেরকে যে পথের উপর পেয়েছি আর এজন্য যে যমীনে তোমাদের দু’জনের প্রাধান্য প্রতিষ্ঠিত হয়? আমরা তোমাদের কথা মোটেই মেনে নেব না।’’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1443,10,79,'ফির‘আওন বলল, ‘‘সকল পারদর্শী যাদুকরদের আমার কাছে নিয়ে এসো’’। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1444,10,80,'যাদুকররা যখন এসে গেল, তখন মূসা তাদেরকে বলল, ‘‘নিক্ষেপ কর তোমরা যা নিক্ষেপ করবে’’। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1445,10,81,'তারা যখন নিক্ষেপ করল, তখন মূসা বলল, ‘‘তোমরা যা নিয়ে এসেছ তাতো যাদু, আল্লাহ এখনই তা ব্যর্থ করে দেবেন, আল্লাহ বিশৃঙ্খলাকারীদের কাজকে সংশোধন করেন না’’। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1446,10,82,'আল্লাহ তাঁর বাণীর সাহায্যে প্রকৃত সত্যকে প্রতিষ্ঠিত করবেনই, অপরাধীদের কাছে তা যতই অপ্রীতিকর হোক না কেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1447,10,83,'মূসার উপর তার জাতির মধ্য হতে গুটিকয়েক লোক ব্যতীত কেউ ঈমান আনেনি ফির‘আওন ও তার প্রধানদের নির্যাতনের ভয়ে। বাস্তবিকই ফির‘আওন দুনিয়াতে খুবই উদ্ধত ছিল, আর সে ছিল অবশ্যই সীমালঙ্ঘনকারীদের অন্তর্ভুক্ত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1448,10,84,'মূসা বলেছিল, ‘‘হে আমার জাতির লোকেরা! তোমরা যদি আল্লাহর প্রতি বিশ্বাস স্থাপন করে থাক তাহলে তোমরা তাঁরই উপর ভরসা কর যদি তোমরা আত্মসমর্পণকারী হও’’। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1449,10,85,'তখন তারা বলল, ‘‘আমরা আল্লাহর উপরই ভরসা করি, হে আমাদের প্রতিপালক! আমাদেরকে যালিম জাতির অত্যাচারের পাত্র করো না, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1450,10,86,'আর তোমার অনুগ্রহে আমাদেরকে কাফির সম্প্রদায় থেকে রক্ষা কর।’’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1451,10,87,'আমি মূসা আর তার ভাইয়ের প্রতি ওয়াহী করলাম যে, ‘‘মিসরে তোমাদের সম্প্রদায়ের জন্য ঘর তৈরি কর আর তোমাদের ঘরগুলোকে ‘ইবাদাত গৃহ কর, আর নামায প্রতিষ্ঠা কর এবং মু’মিনদেরকে সুসংবাদ দাও’’। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1452,10,88,'মূসা বলল, ‘‘হে আমার প্রতিপালক! তুমি ফির‘আওন আর তার প্রধানদেরকে এ পার্থিব জগতে চাকচিক্য আর ধন সম্পদ দান করেছ আর এর দ্বারা হে আমাদের রবব! তারা মানুষকে তোমার পথ থেকে বিচ্যুত করছে, হে আমার প্রতিপালক! তাদের সম্পদ ধ্বংস করে দাও, আর তাদের হৃদয়কে কঠিন করে দাও, যাতে তারা ভয়াবহ ‘আযাব দেখার পূর্ব পর্যন্ত ঈমান আনতে সক্ষম না হয় (যেহেতু তারা বার বার আল্লাহর নিদর্শন দেখেও সত্য দ্বীনের শত্রুতায় অটল হয়ে আছে)। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1453,10,89,'আল্লাহ তা‘আলা জবাব দিলেন, ‘‘তোমাদের দু’জনের দু‘আ কবূল করা হল, কাজেই তোমরা মজবুত হয়ে থাক, আর তোমরা কক্ষনো তাদের পথ অনুসরণ করো না যারা কিছুই জানে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1454,10,90,'আমি বানী ইসরাঈলকে সমুদ্র পার করিয়ে নিলাম আর ফির‘আওন ও তার সৈন্য সামন্ত ঔদ্ধত্য ও সীমালঙ্ঘন ক’রে তাদের পেছনে ছুটল, অতঃপর যখন সে ডুবতে শুরু করল তখন সে বলল, ‘আমি ঈমান আনছি যে, তিনি ছাড়া সত্যিকারের কোন ইলাহ নেই যাঁর প্রতি বানী ইসরাঈল ঈমান এনেছে, আর আমি আত্মসমর্পণকারীদের অন্তর্ভুক্ত।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1455,10,91,'‘‘এখন (ঈমান আনছ), আগে তো অমান্য করেছ আর ফাসাদকারীদের অন্তর্ভুক্ত থেকেছ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1456,10,92,'আজ আমি তোমার দেহকে রক্ষা করব যাতে তুমি তোমার পরবর্তীদের জন্য নিদর্শন হতে পার।’ অধিকাংশ মানুষই আমার নিদর্শনাবলী সম্পর্কে নিশ্চিতই উদাসীন।’’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1457,10,93,'আমি বানী ইসরাঈলকে মর্যাদাপূর্ণ আবাসস্থলে প্রতিষ্ঠিত করেছিলাম আর তাদেরকে উত্তম রিযক দিয়েছিলাম। অতঃপর তাদের কাছে (আল্লাহর প্রেরিত) সঠিক জ্ঞান আসার পূর্ব পর্যন্ত তারা মতভেদ করেনি। তারা যে বিষয়ে মতভেদ করেছিল কিয়ামাত দিবসে তোমার প্রতিপালক অবশ্যই তা মীমাংসা করে দিবেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1458,10,94,'আমি তোমার প্রতি যা অবতীর্ণ করেছি তাতে যদি তুমি সন্দেহ পোষণ কর তাহলে তোমার পূর্বে থেকে যারা কিতাব পাঠ করে আসছে তাদেরকে জিজ্ঞেস কর। তোমার কাছে তোমার প্রতিপালকের নিকট থেকে প্রকৃত সত্য এসেছে। কাজেই তুমি কক্ষনো সন্দেহ পোষণকারীদের মধ্যে শামিল হয়ো না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1459,10,95,'আর তুমি কক্ষনো তাদের মধ্যে শামিল হয়ো না যারা আল্লাহর আয়াতসমূহকে মিথ্যে জেনে অমান্য করে, তাহলে তুমি ক্ষতিগ্রস্তদের অন্তর্ভুক্ত হয়ে যাবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1460,10,96,'তাদের সম্পর্কে তোমার প্রতিপালকের বাক্য সত্য প্রমাণিত হয়েছে যে, তারা ঈমান আনবে না, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1461,10,97,'এমনকি তাদের কাছে প্রত্যেকটি নিদর্শন আসলেও- যে পর্যন্ত না তারা ভয়াবহ শাস্তি প্রত্যক্ষ করবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1462,10,98,'এমন কোন জনপদের দৃষ্টান্ত আছে কি যে তারা (শাস্তি দেখার পর) ঈমান আনল আর তাদের ঈমান উপকারে আসল- একমাত্র ইউনুসের সম্প্রদায় ছাড়া? তারা যখন ঈমান আনল, তখন আমি দুনিয়ার জীবনে তাদের থেকে হীনতাব্যঞ্জক ‘আযাব সরিয়ে দিলাম, আর একটা নির্দিষ্ট মেয়াদ পর্যন্ত তাদেরকে জীবন উপভোগ করার সুযোগ দিলাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1463,10,99,'তোমার প্রতিপালক ইচ্ছে করলে দুনিয়ার সমস্ত লোক অবশ্যই ঈমান আনত, তাহলে কি তুমি ঈমান আনার জন্য মানুষদের উপর জবরদস্তি করবে? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1464,10,100,'আল্লাহর অনুমতি ব্যতিরেকে কেউ ঈমান আনতে পারবে না, আর যারা বিবেক বুদ্ধি খাটায় না, আল্লাহ তাদের উপর গুমরাহী চাপিয়ে দেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1465,10,101,'বল ‘‘আসমানসমূহ আর যমীনে যা কিছু আছে তার দিকে চেয়ে দেখ, যারা ঈমান আনে না তাদের জন্য নিদর্শনাবলী আর ভয়-ভীতি প্রদর্শন কোন কাজে আসে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1466,10,102,'তবে তারা কি তাদের পূর্বে যে সব দিনগুলো ঘটে গেছে সে রকম ঘটা ছাড়া অন্য কিছুর অপেক্ষা করছে? বল, ‘‘তাহলে অপেক্ষা কর, আমিও তোমাদের সাথে অপেক্ষমান থাকলাম।’’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1467,10,103,'অবশেষে আমি আমার রসূলদেরকে আর মু’মিনদেরকে রক্ষা করি, এভাবে মু’মিনদেরকে রক্ষা করা আমার কর্তব্য। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1468,10,104,'বল, ‘‘হে মানুষ! আমার দ্বীন সম্পর্কে যদি তোমাদের কোন সন্দেহ থাকে তাহলে জেনে রেখ, তোমরা আল্লাহকে বাদ দিয়ে যার ‘ইবাদাত কর, আমি তাদের ‘ইবাদাত করি না, বরং আমি আল্লাহর ‘ইবাদাত করি যিনি তোমাদের মৃত্যু ঘটান, আর আমি আদিষ্ট হয়েছি মু’মিনদের অন্তর্ভুক্ত হওয়ার জন্য। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1469,10,105,'আর আমাকে এও আদেশ দেয়া হয়েছে যে, তুমি দ্বীনের দিকে তোমার মুখ প্রতিষ্ঠিত কর একনিষ্ঠভাবে, আর তুমি কক্ষনো মুশরিকদের মধ্যে শামিল হয়ো না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1470,10,106,'আর আল্লাহকে বাদ দিয়ে আহবান করো না এমন কিছুকে যা না পারে তোমার কোন উপকার করতে, আর না পারে ক্ষতি করতে; যদি তুমি তা কর তাহলে তুমি যালিমদের মধ্যে শামিল হয়ে যাবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1471,10,107,'আল্লাহ যদি তোমাকে কষ্ট দিতে চান তাহলে তিনি ছাড়া তা দূর করার কেউ নেই, আর আল্লাহ যদি তোমার কল্যাণ করতে চান, তাহলে তাঁর অনুগ্রহ রদ করার কেউ নেই। তিনি তাঁর বান্দাহদের মধ্যে যাকে চান অনুগ্রহ দিয়ে ধন্য করেন। তিনি বড়ই ক্ষমাশীল, বড়ই দয়ালু। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1472,10,108,'বল, ‘‘হে মানুষ! তোমাদের প্রতিপালকের নিকট হতে তোমাদের কাছে প্রকৃত সত্য এসে পৌঁছেছে। অতঃপর যে সঠিক পথ অবলম্বন করবে, সে নিজের কল্যাণের জন্যই সঠিক পথ ধরবে। আর যারা পথভ্রষ্ট হবে তারা পথভ্রষ্ট হবে নিজেদেরই ক্ষতি করার জন্য, আমি তোমাদের হয়ে কাজ উদ্ধার করে দেয়ার কেউ নেই। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1473,10,109,'তোমার নিকট যে ওয়াহী অবতীর্ণ করা হয়েছে তুমি তার অনুসরণ কর আর তুমি ধৈর্য্য অবলম্বন কর যে পর্যন্ত না আল্লাহ ফায়সালা প্রদান করেন। বস্তুতঃ তিনিই হলেন সর্বোত্তম ফায়সালাকারী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1474,11,1,'আলিফ, লাম, রা; এটা এমন একটা গ্রন্থ, এর আয়াতগুলো সুদৃঢ়, অতঃপর সবিস্তারে ব্যাখ্যাকৃত মহাজ্ঞানী, সর্বজ্ঞের নিকট হতে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1475,11,2,'(এটা শিক্ষা দেয়) যে, তোমরা আল্লাহ ছাড়া কারো ‘ইবাদাত করবে না, আমি অবশ্যই তাঁর পক্ষ হতে তোমাদের জন্য ভয় প্রদর্শনকারী ও সুসংবাদদাতা। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1476,11,3,'(এটা শিক্ষা দেয়) যে, তোমরা তোমাদের প্রতিপালকের নিকট ক্ষমা চাও, আর অনুশোচনাভরে তাঁর দিকেই ফিরে এসো, তাহলে তিনি একটা নির্দিষ্ট কাল পর্যন্ত তোমাদেরকে উত্তম জীবন সামগ্রী ভোগ করতে দিবেন, আর অনুগ্রহ লাভের যোগ্য প্রত্যেক ব্যক্তিকে তিনি তাঁর অনুগ্রহ দানে ধন্য করবেন। আর যদি তোমরা মুখ ফিরিয়ে নাও তাহলে আমি তোমাদের উপর বড় এক কঠিন দিনের ‘আযাবের আশঙ্কা করছি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1477,11,4,'তোমাদের প্রত্যাবর্তন আল্লাহর কাছেই, আর তিনি সব কিছুই করতে সক্ষম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1478,11,5,'লক্ষ্য কর, এরা নিজেদের বুক ঘুরিয়ে নেয় যাতে তারা তাঁর (অর্থাৎ আল্লাহর) থেকে লুকিয়ে থাকতে পারে। সাবধান! এরা যখন কাপড় দিয়ে নিজেরা নিজেদেরকে ঢেকে নেয়, তখন তারা যা গোপন করে আর প্রকাশ করে তিনি তা জানেন। তাদের মনের গভীরে যা আছে সে বিষয়ে তিনি সবচেয়ে বেশি অবহিত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1479,11,6,'যমীনে বিচরণশীল এমন কোন জীব নেই যার জীবিকার দায়িত্ব আল্লাহর উপর নেই, তিনি জানেন তাদের থাকার জায়গা কোথায় আর কোথায় তাদেরকে (মৃত্যুর পর) রাখা হয়, সব কিছুই আছে সুস্পষ্ট লিপিকায়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1480,11,7,'আর তিনিই আসমানসমূহ আর যমীনকে ছ’দিনে সৃষ্টি করেছেন। ইতোপূর্বে তাঁর আরশ ছিল পানির উপর। (সৃষ্টি করেছেন) তোমাদেরকে পরীক্ষা করার উদ্দেশে যে, তোমাদের মধ্যে ‘আমালের ক্ষেত্রে কারা শ্রেষ্ঠ। তুমি যদি বল, ‘‘মৃত্যুর পর তোমাদেরকে অবশ্য অবশ্যই আবার উঠানো হবে, তাহলে কাফিররা অবশ্যই বলবে যে, এতো সুস্পষ্ট যাদু। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1481,11,8,'আমি যদি নির্দিষ্ট সময়ের জন্য তাদের থেকে শাস্তি বিলম্বিত করি, তাহলে তারা অবশ্য অবশ্যই বলবে যে, ‘কিসে ওটাকে আটকে রাখল? সাবধান! এমন দিন তাদের কাছে আসবে যা তাদের থেকে কেউ ফিরিয়ে দিতে পারবে না, আর যা নিয়ে তারা ঠাট্টা বিদ্রূপ করছিল তাই তাদেরকে ঘিরে ফেলবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1482,11,9,'আমি যদি মানুষকে আমার পক্ষ থেকে রহমত আস্বাদন করাই অতঃপর তা তার থেকে ছিনিয়ে নেই, তখন সে অবশ্যই হতাশ ও অকৃতজ্ঞ হয়ে পড়ে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1483,11,10,'আর যদি তার উপরে আসা দুঃখ কষ্টের পর তাকে নি‘মাতের স্বাদ আস্বাদন করাই, তখন সে অবশ্য অবশ্যই বলবে, ‘আমার দূরবস্থা কেটে গেছে’। তখন সে আনন্দে উৎফুল্ল হয়, হয়ে পড়ে অহঙ্কারী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1484,11,11,'কিন্তু যারা ধৈর্যশীল ও নেক ‘আমালকারী তারা ওরকম নয়। আর এরাই হল যাদের জন্য আছে ক্ষমা ও বিরাট প্রতিদান। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1485,11,12,'তুমি কি তোমার প্রতি যা ওয়াহী করা হয়েছে তার কিছু অংশ পরিত্যাগ করতে চাও আর তোমার মন সংকুচিত করতে চাও তাদের এ কথা বলার কারণে যে ‘‘তার কাছে ধনভান্ডার অবতীর্ণ হয় না কেন, কিংবা তার কাছে ফেরেশতা আসে না কেন?’’ তুমি তো কেবল ভয় প্রদর্শনকারী, যাবতীয় কাজ পরিচালনার দায়িত্ব আল্লাহর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1486,11,13,'তারা কি বলে ‘‘সে [অর্থাৎ মুহাম্মাদ (সা.)] ওটা রচনা করেছে? বল, ‘‘তাহলে তোমরা এর মত দশটি সূরাহ রচনা করে আন, আর (এ কাজে সাহায্য করার জন্য) আল্লাহকে বাদ দিয়ে যাকে ডাকতে পার ডেকে নাও যদি তোমরা সত্যবাদী হয়েই থাক । ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1487,11,14,'তারা যদি তোমাদের ডাকে সাড়া না দেয় তাহলে জেনে নাও যে, আল্লাহর জ্ঞান অনুসারেই তা অবতীর্ণ হয়েছে। আরো জেনে রাখ যে, তিনি ছাড়া সত্যিকারের কোন ইলাহ নেই। তাহলে এখন কি তোমরা আত্মসমর্পণ করবে? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1488,11,15,'যারা এ দুনিয়ার জীবন আর তার শোভা সৌন্দর্য কামনা করে, তাদেরকে এখানে তাদের কর্মের পুরোপুরি ফল আমি দিয়ে দেই, আর তাতে তাদের প্রতি কোন কমতি করা হয় না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1489,11,16,'কিন্তু আখেরাতে তাদের জন্য আগুন ছাড়া কিছুই নাই, এখানে যা কিছু তারা করেছে তা নিষ্ফল হয়ে গেছে, আর তাদের যাবতীয় কাজকর্ম ব্যর্থ হয়ে গেছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1490,11,17,'তাহলে যে লোক তার প্রতিপালকের পক্ষ থেকে স্পষ্ট প্রমাণের উপর আছে এবং তাঁর পক্ষ হতে এক সাক্ষী ওটা পড়ে শোনাচ্ছে (সে কি অবিশ্বাসীদের সমান হতে পারে?) আর তার পূর্বে পথ প্রদর্শক ও রহমত স্বরূপ এসেছিল মূসার কিতাব। ওরাই তাতে (অর্থাৎ কুরআনে) বিশ্বাসী। যারাই এটাকে অস্বীকার করবে, জাহান্নামই হল তাদের প্রতিশ্রুত স্থান। কাজেই এ সম্পর্কে তুমি কোন প্রকার সন্দেহে নিপতিত হয়ো না। এটা তোমার প্রতিপালকের পক্ষ থেকে আগত প্রকৃত সত্য, কিন্তু অধিকাংশ মানুষ বিশ্বাস করে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1491,11,18,'যারা আল্লাহ সম্পর্কে মিথ্যা রচনা করে তাদের থেকে বড় যালিম আর কে হতে পারে? তাদেরকে তাদের প্রতিপালকের সামনে উপস্থিত করা হবে আর সাক্ষীরা সাক্ষ্য দিবে যে, এই লোকরাই তাদের রব্বের বিরুদ্ধে মিথ্যা বলেছিল। শুনে রেখ! আল্লাহর অভিশাপ সেই যালিমদের উপর ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1492,11,19,'যারা আল্লাহর পথ হতে লোকেদেরকে ফিরিয়ে রাখে আর তাকে বক্র করতে চায়, আর তারা আখিরাতকে অস্বীকার করে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1493,11,20,'দুনিয়াতে তারা আল্লাহকে অক্ষম করে দিতে পারত না, আর আল্লাহ ছাড়া তাদের কোন সাহায্যকারীও ছিল না, তাদের শাস্তি দ্বিগুণ করা হবে। তারা না শুনতে পারত, আর না দেখতে পারত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1494,11,21,'এরা সেই লোক যারা নিজেদেরকে নিজেরাই ক্ষতিগ্রস্ত করেছে, আর তারা যা কিছু রচনা করেছিল তা তাদের কাছ থেকে উধাও হয়ে গেছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1495,11,22,'এতে কোন সন্দেহ নেই যে আখেরাতে এরাই সবচেয়ে ক্ষতিগ্রস্ত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1496,11,23,'নিশ্চয় যারা ঈমান আনে, সৎকর্ম করে আর তাদের রবেবর কাছে বিনীত, তারাই জান্নাতের অধিবাসী, তারা সেখানে স্থায়ী হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1497,11,24,'দু’শ্রেণীর লোকের দৃষ্টান্ত হল যেমন একজন হল অন্ধ ও বধির, অন্যজন চক্ষুষ্মান ও শ্রবণশীল, এ দু’জন কি তুলনায় সমান হতে পারে? এরপরও কি তোমরা শিক্ষা গ্রহণ করবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1498,11,25,'আমি নূহকে তার কাওমের কাছে পাঠিয়েছিলাম। (সে বলেছিল) আমি তোমাদের জন্য একজন স্পষ্ট সতর্ককারী, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1499,11,26,'যে, তোমরা আল্লাহ ছাড়া কারো ‘ইবাদাত করো না, অন্যথায় আমি আশঙ্কা করছি যে, তোমাদের উপর একদিন বেদনাদায়ক ‘আযাব আসবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1500,11,27,'জওয়াবে তার জাতির প্রধানগণ- যারা অবিশ্বাসী ছিল তারা বলল, ‘আমরা তোমাকে আমাদের মত মানুষ ছাড়া অন্য কিছু দেখছি না, আর প্রকাশ্যতঃ আমাদের হীন অধম লোকগুলো ছাড়া তোমার পথ অবলম্বন করতে দেখছি না, আমাদের উপর তোমার কোন প্রাধান্যও দেখছি না, বরং আমরা তোমাদেরক মিথ্যুক বলেই মনে করি।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1501,11,28,'সে বলল, ‘‘হে আমার সম্প্রদায়! তোমরা চিন্তা-ভাবনা করে দেখ, আমি যদি আমার প্রতিপালকের নিকট হতে প্রাপ্ত সুস্পষ্ট প্রমাণের উপর প্রতিষ্ঠিত থাকি আর তিনি তাঁর পক্ষ থেকে আমাকে অনুগ্রহ প্রদান করে থাকেন যা তোমাদের দৃষ্টি থেকে গোপনে রাখা হয়েছে, এমতাবস্থায় তা গ্রহণ করার ব্যাপারে আমি কি তোমাদেরকে বাধ্য করতে পারি যখন তোমরা তা অপছন্দ কর? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1502,11,29,'হে আমার জাতির লোকেরা! আমি এ কাজে তোমাদের কাছে কোন ধন-সম্পদ চাই না, আমার পারিশ্রমিক আছে কেবল আল্লাহর কাছে। আর মু’মিনদের তাড়িয়ে দেয়া আমার জন্য শোভনীয় নয়, তারা তাদের প্রতিপালকের সাক্ষাৎ অবশ্যই লাভ করবে, কিন্তু আমি দেখছি তোমরা এমন এক জাতি যারা মূর্খের আচরণ করছ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1503,11,30,'হে আমার জাতির লোকেরা! আমি যদি এই লোকদেরকে তাড়িয়ে দেই তাহলে আমাকে আল্লাহর পাকড়াও থেকে কে বাঁচাবে? তবুও কি তোমরা উপদেশ নিবে না? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1504,11,31,'আমি তো তোমাদেরকে এ কথা বলছি না যে, আমার কাছে আল্লাহর ধন-ভান্ডারসমূহ আছে। আর আমি অদৃশ্যের খবরও জানি না। আর আমি এ কথাও বলি না যে, আমি ফেরেশতা। আমি এ কথাও বলি না যে, তোমাদের চোখ যে সব লোককে অবজ্ঞা করে, আল্লাহ কক্ষনো তাদের কল্যাণ করবেন না। তাদের অন্তরে কী আছে আল্লাহ্ই তা বেশী জানেন। (এ রকম কথা বললে) আমি তো যালিমদের শামিল হয়ে যাবো। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1505,11,32,'তারা বলল, ‘হে নূহ! তুমি আমাদের সাথে ঝগড়া করেছ, খুব বেশি বেশি ঝগড়া করেছ, এখন যার ভয় আমাদেরকে দেখাচ্ছ তা আমাদের কাছে নিয়ে এসো, যদি তুমি সত্যবাদী হও।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1506,11,33,'নূহ বলল, ‘‘আল্লাহই তা তোমাদের কাছে নিয়ে আসবেন যদি তিনি চান, তোমরা ব্যর্থ করতে পারবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1507,11,34,'আমি তোমাদের কোন কল্যাণ করতে চাইলেও আমার কল্যাণ কামনা তোমাদের কোন উপকারে আসবে না যদি আল্লাহ তোমাদেরকে পথহারা করতে চান। তিনিই তোমাদের রবব, আর তাঁর কাছেই তোমরা ফিরে যাবে।’’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1508,11,35,'তারা কি বলে যে, এ লোকই এ সব রচনা করেছে? বল, আমি যদি তা রচনা করে থাকি, তাহলে আমার অপরাধের দায়িত্ব আমার উপর। তোমরা যে অপরাধ করেছ তাত্থেকে আমি মুক্ত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1509,11,36,'নূহের কাছে ওয়াহী পাঠানো হয়েছিল যে, যারা ঈমান এনেছে তারা ছাড়া তোমার সম্প্রদায়ের আর কোন লোক কক্ষনো ঈমান আনবে না, কাজেই তারা যা করছে তার জন্য তুমি হা-হুতাশ করো না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1510,11,37,'অতএব আমার পর্যবেক্ষণের অধীনে আর আমার ওয়াহী অনুসারে তুমি নৌকা তৈরি কর, আর যারা বাড়াবাড়ি করেছে তাদের ব্যাপারে আমার কাছে কোন আবেদন করো না, তারা অবশ্যই ডুববে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1511,11,38,'নূহ নৌকা তৈরি করছিল, আর যখনই তার জাতির প্রধান ব্যক্তিরা তার নিকট দিয়ে যাচ্ছিল, তারা তাকে ঠাট্টা করছিল। সে বলল, ‘তোমরা যদি আমাদেরকে ঠাট্টা কর, তাহলে আমরাও তোমাদেরকে (ভবিষ্যতে) ঠাট্টা করব যেমনভাবে তোমরা (এখন) ঠাট্টা করছ, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1512,11,39,'তোমরা (শীঘ্রই) জানতে পারবে কার উপর লাঞ্ছনাকর ‘আযাব আসে আর কার উপর আসে স্থায়ী ‘আযাব। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1513,11,40,'শেষে যখন আমার নির্দেশ এসে গেল, আর তন্দুর (পানিতে) উথলে উঠল, আমি বললাম, ‘প্রত্যেক শ্রেণীর যুগলের দু’টি তাতে তুলে নাও আর তোমার পরিবার পরিজনকে, তাদের ছাড়া যাদের ব্যাপারে আগেই ঘোষণা দেয়া হয়েছে। আর যারা ঈমান এনেছে তাদেরকেও (তুলে নাও)। তার সঙ্গে ঈমান এনেছিল খুব অল্প কয়েকজনই। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1514,11,41,'নূহ বলল, ‘এতে আরোহণ কর, আল্লাহর নামে এর গতি ও এর স্থিতি, আমার প্রতিপালক অবশ্যই বড়ই ক্ষমাশীল, বড়ই দয়ালু।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1515,11,42,'পর্বত সদৃশ তরঙ্গমালার মধ্য দিয়ে তা তাদেরকে নিয়ে বয়ে চলল। তখন নূহ তার পুত্রকে- যে তাদের থেকে আলাদা হয়ে গিয়েছিল- ডাক দিয়ে বলল, ‘হে আমার পুত্র! আমাদের সঙ্গে আরোহণ কর, কাফিরদের সঙ্গে থেক না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1516,11,43,'সে (অর্থাৎ নূহের পুত্র) বলল, ‘আমি এক্ষুণি পাহাড়ে আশ্রয় নেব যা আমাকে পানি থেকে রক্ষা করবে।’ নূহ বলল, ‘আজ আল্লাহর হুকুম থেকে কোন কিছুই রক্ষা করতে পারবে না, অবশ্য আল্লাহ যার প্রতি দয়া করবেন সে রক্ষা পাবে।’ অতঃপর ঢেউ তাদের দু’জনার মাঝে আড়াল করল আর সে ডুবে যাওয়া লোকেদের মধ্যে শামিল হয়ে গেল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1517,11,44,'অতঃপর বলা হল, ‘হে যমীন! তোমার পানি গিলে ফেল, আর হে আকাশ, থাম।’ অতঃপর পানি যমীনে বসে গেল, কার্য সমাপ্ত হল, নৌকা জুদী পর্বতে এসে ভিড়ল, আর বলা হল- ‘যালিম লোকেরা ধ্বংস হোক!’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1518,11,45,'নূহ তার প্রতিপালককে আহবান জানাল। সে বলল, ‘হে আমার প্রতিপালক! আমার পুত্র তো আমার পরিবারভুক্ত, আর তোমার ও‘য়াদা সত্য আর তুমি বিচারকদের সর্বশ্রেষ্ঠ বিচারক।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1519,11,46,'তিনি বললেন, ‘ওহে নূহ! সে তো তোমার পরিবারের লোক নয়, তার আচার আচরণ অসৎ, কাজেই যে বিষয়ে তোমার কোন জ্ঞান নেই সে বিষয়ে আমার কাছে আবেদন করো না, আমি তোমাকে উপদেশ দিচ্ছি, তুমি যেন মূর্খদের মধ্যে শামিল না হও। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1520,11,47,'সে বলল, ‘হে আমার প্রতিপালক! যে বিষয়ে আমার কোন জ্ঞান নেই সে বিষয়ে প্রশ্ন করা হতে আমি তোমার কাছে আশ্রয় চাচ্ছি। যদি তুমি আমাকে ক্ষমা না কর আর আমার প্রতি দয়া না কর তাহলে আমি ক্ষতিগ্রস্তদের অন্তর্ভুক্ত হয়ে যাব।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1521,11,48,'বলা হল, ‘হে নূহ! তুমি নেমে পড়, আমার পক্ষ হতে শান্তি ও বরকত তোমার প্রতি আর তোমার সঙ্গীদের মধ্যে অনেক দলের প্রতি, আর এ ছাড়া অন্য লোকেদের আমি জীবন উপভোগ করতে দেব, (কিন্তু) পরে আমার নিকট হতে মর্মান্তিক ‘আযাব তাদেরকে স্পর্শ করবে।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1522,11,49,'এ সব হল অদৃশ্যের খবর যা তোমাকে ওয়াহী দ্বারা জানিয়ে দিচ্ছি, যা এর পূর্বে না তুমি জানতে, না তোমার জাতির লোকেরা জানত। কাজেই ধৈর্য ধর, শুভ পরিণতি মুত্তাকীদের জন্যই নির্দিষ্ট। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1523,11,50,'আর ‘আদ জাতির নিকট তাদের ভাই হূদকে পাঠিয়েছিলাম। সে বলেছিল, ‘হে আমার সম্প্রদায়! তোমরা আল্লাহর ‘ইবাদাত কর, তিনি ছাড়া তোমাদের আর কোন ইলাহ নেই। তোমরা তো শুধু মিথ্যে বানিয়ে নিয়েছ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1524,11,51,'হে আমার সম্প্রদায়! এর বিনিময়ে আমি তোমাদের কাছে কোন পারিশ্রমিক চাই না। আমার পারিশ্রমিক তো তাঁর জিম্মায় যিনি আমাকে সৃষ্টি করেছেন, তবুও কি তোমরা জ্ঞান-বুদ্ধি খাটাবে না? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1525,11,52,'হে আমার সম্প্রদায়! তোমরা তোমাদের রবেবর কাছে ক্ষমা প্রার্থনা কর, অতঃপর অনুশোচনাভরে তাঁর দিকেই ফিরে যাও, তিনি তোমাদের জন্য প্রচুর বৃষ্টি বর্ষণ করবেন, তোমাদের শক্তিকে আরো শক্তি দিয়ে বাড়িয়ে দিবেন, আর অপরাধী হয়ে মুখ ফিরিয়ে নিও না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1526,11,53,'তারা বলল, ‘হে হূদ! তুমি আমাদের কাছে কোন সুস্পষ্ট প্রমাণ নিয়ে আসনি, আর তোমার কথায় আমরা আমাদের উপাস্যগুলোকে ত্যাগ করতে পারি না, আমরা তোমাতে বিশ্বাসী নই। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1527,11,54,'আমরা এ কথাই বলি যে, তোমার উপর আমাদের কোন উপাস্যের অশুভ ছায়া পড়েছে।’ সে বলল, ‘আমি আল্লাহকে সাক্ষী রাখছি আর তোমরাও সাক্ষী হও যে, তোমরা আল্লাহ ছাড়া যাকে তাঁর শরীক কর তার সাথে আমি পুরোপুরি সম্পর্কহীন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1528,11,55,'তাঁকে (আল্লাহ) ব্যতীত তোমরা সবাই মিলে আমার বিরুদ্ধে ষড়যন্ত্র কর আর আমাকে একটুও অবকাশ দিও না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1529,11,56,'আমি নির্ভর করি আল্লাহর উপর যিনি আমার আর তোমাদের রব, এমন কোন জীব নেই যার কতৃত্ব তাঁর হাতে নয়, নিশ্চয়ই আমার রব সরল পথের উপর প্রতিষ্ঠিত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1530,11,57,'এরপরও যদি তোমরা মুখ ফিরিয়ে লও (তবে জেনে রেখ), আমাকে যা দিয়ে তোমাদের কাছে পাঠানো হয়েছে আমি তো তোমাদের কাছে তা পৌঁছে দিয়েছি, এখন আমার প্রতিপালক তোমাদের স্থলে অন্য সম্প্রদায়কে নিয়ে আসবেন আর তোমরা তাঁর কোনই ক্ষতি করতে পারবে না। আমার প্রতিপালক সব কিছুর রক্ষণাবেক্ষণকারী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1531,11,58,'আমার নির্দেশ যখন এসে গেল, তখন আমি হূদকে আর তার সঙ্গে যারা ঈমান এনেছিল তাদেরকে আমার দয়ায় রক্ষা করলাম, আর তাদেরকে বাঁচিয়ে নিলাম এক কঠিন ‘আযাব হতে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1532,11,59,'এই হল ‘আদ, তারা তাদের প্রতিপালকের আয়াতসমূহকে অস্বীকার করেছিল, আর তাদের রসূলদেরকে অমান্য করেছিল, প্রত্যেক প্রবল পরাক্রান্ত, সত্য-দ্বীনের দুশমনের নির্দেশের তারা অনুসরণ করেছিল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1533,11,60,'এ দুনিয়াতে তাদের পিছনে লা‘নত রয়েছে, আর কিয়ামাতের দিনেও। জেনে রেখ, ‘আদ জাতি তাদের প্রতিপালককে অস্বীকার করেছিল। জেনে রেখ! ধ্বংস করা হয়েছিল ‘আদকে যারা ছিল হূদের সম্প্রদায়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1534,11,61,'আমি সামূদ জাতির কাছে তাদের ভাই সালিহকে পাঠিয়েছিলাম। সে বলল, ‘হে আমার সম্প্রদায়! তোমরা আল্লাহর ‘ইবাদাত কর, তিনি ছাড়া তোমাদের আর কোন ইলাহ নেই, তিনিই তোমাদেরকে মাটি থেকে পয়দা করেছেন, আর তাতেই তোমাদেরকে প্রতিষ্ঠিত করেছেন, কাজেই তাঁর কাছে তোমরা ক্ষমা প্রার্থনা কর, আর তাঁর পানেই ফিরে এসো, আমার প্রতিপালক তো অতি নিকটে, আর তিনি আহবানে সাড়াদানকারী।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1535,11,62,'তারা বলল, ‘‘হে সালিহ! এর পূর্বে তুমি তো আমাদের মাঝে ছিলে আশা-আকাঙ্ক্ষার পাত্র, তুমি কি আমাদেরকে সেই মা‘বূদদের ‘ইবাদাত করতে নিষেধ করছ আমাদের পিতৃ পুরুষরা যার ‘ইবাদাত করত? তুমি আমাদেরকে যে দিকে ডাকছ সে সম্পর্কে আমরা বিভ্রান্তিকর সংশয়ে পড়ে আছি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1536,11,63,'সালিহ বলল, ‘‘হে আমার সম্প্রদায়! তোমরা  কি চিন্তা  করে  দেখেছ,  যদি আমি আমার প্রতপালকের সুস্পষ্ট প্রমাণের উপর থাকি আর তাঁর পক্ষ থেকে আমাকে অনুগ্রহ করা হয়, এমতাবস্থায় আমি যদি তার অবাধ্য হই তাহলে আল্লাহর (‘আযাব) থেকে আমাকে কে রক্ষা করবে? তোমরা তো কেবল আমার ক্ষতিই বাড়িয়ে দিতে চাও। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1537,11,64,'হে আমার জাতির লোকেরা! এটা আল্লাহর উষ্ট্রী, তোমাদের জন্য একটা নিদর্শন। একে আল্লাহর যমীনে চলে ফিরে খেয়ে বেড়াতে দাও, একে কোন প্রকার কষ্ট দিও না, নচেৎ শীঘ্রই তোমাদেরকে ‘আযাব পাকড়াও করবে।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1538,11,65,'কিন্তু তারা উষ্ট্রীটির পাগুলো কেটে ফেলল। তখন সে তাদেরকে বলল, ‘তোমরা তোমাদের ঘরে তিনটি দিন জীবন উপভোগ করে নাও, এটা এমন এক ও‘য়াদা যা মিথ্যে হতে পারে না।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1539,11,66,'অতঃপর আমার হুকুম যখন আসল তখন আমি সালিহ আর তার সঙ্গে যারা ঈমান এনেছিল তাদেরকে আমার দয়ায় বাঁচিয়ে নিলাম আর সে দিনের লাঞ্ছনা হতে রক্ষা করলাম। তোমার প্রতিপালক তিনিই তো শক্তিশালী, প্রতাপশালী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1540,11,67,'যারা যুলম করেছিল এক প্রচন্ড শব্দ তাদেরকে আঘাত হানল, আর তারা নিজ নিজ গৃহে নতজানু হয়ে পড়ে রইল- ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1541,11,68,'যেন তারা সেখানে কোন দিনই বাস করেনি। জেনে রেখ, সামুদজাতি তাদের প্রতিপালককে অমান্য করেছিল। জেনে রেখ, সামূদকে (রহমাত থেকে) দূরে নিক্ষেপ করা হয়েছিল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1542,11,69,'আমার প্রেরিত ফেরেশতাগণ ইবরাহীমের কাছে সুসংবাদ নিয়ে এসেছিল। তারা এসে বলল ‘‘তোমার প্রতি সালাম! সেও বলল, ‘তোমাদের প্রতিও সালাম!’ অনতিবিলম্বে সে ভুনা করা বাছুর নিয়ে আসলো। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1543,11,70,'যখন সে দেখল তাদের হাত তার (অর্থাৎ খাবারের) দিকে পৌঁছতেছে না, সে তাদের সম্পর্কে সন্দিগ্ধ হল আর তাদের ব্যাপারে ভীতি অনুভব করল। তারা বলল, ‘ভয় পেয়ো না, আমাদেরকে পাঠানো হয়েছে লূতের সম্প্রদায়ের প্রতি।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1544,11,71,'(ইবরাহীমের) স্ত্রী দাঁড়িয়েছিল, সে হেসে ফেলল। তখন আমি তাকে ইসহাকের আর ইসহাকের পর ইয়া‘কূবের সুসংবাদ দিলাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1545,11,72,'সে বলল, ‘হায় আমার কপাল! সন্তান হবে আমার, আমি তো অতি বুড়ি আর আমার এই স্বামীও বৃদ্ধ, এতো এক আশ্চর্য ব্যাপার।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1546,11,73,'তারা বলল, ‘আল্লাহর কাজে তুমি আশ্চর্য হচ্ছ, ওহে (ইবরাহীমের) পরিবারবর্গ! তোমাদের উপর রয়েছে আল্লাহর দয়া ও বরকতসমূহ, তিনি বড়ই প্রশংসিত, বড়ই মহান।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1547,11,74,'পরে যখন ইবরাহীমের আতঙ্ক দূর হল, আর তার কাছে সুসংবাদ আসল, তখন সে লূত জাতির ব্যাপারে আমার সাথে ঝগড়া করল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1548,11,75,'অবশ্যই ইবরাহীম ছিল বড়ই সহিষ্ণু, কোমল হৃদয় আর আল্লাহমুখী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1549,11,76,'‘হে ইবরাহীম! এথেকে তুমি নিবৃত্ত হও, তোমার প্রতিপালকের নির্দেশ এসে গেছে, তাদের প্রতি শাস্তি আসবেই যা রদ হবার নয়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1550,11,77,'আমার প্রেরিত বার্তাবাহকগণ যখন লূতের কাছে আসলো, তাদের আগমনে সে ঘাবড়ে গেল। (তাদেরকে রক্ষায়) নিজেকে অসমর্থ মনে করল, আর বলল, ‘আজ বড়ই বিপদের দিন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1551,11,78,'তার কওমের লোকেরা হুড়মুড় করে তার কাছে ছুটে আসলো, আগে থেকেই তারা এ রকম অসৎ কাজে অভ্যস্ত ছিল। সে বলল, ‘হে আমার সম্প্রদায়! এই আমার (নিজের বা জাতির) কন্যারা আছে, তারা তোমাদের জন্য অধিক পবিত্র (যদি তোমরা বিয়ে কর), কাজেই তোমরা আল্লাহকে ভয় কর, আমার মেহমানদের ব্যাপারে আমাকে লজ্জিত করো না, তোমাদের মধ্যে কি ভাল মানুষ একটিও নেই?’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1552,11,79,'তারা বলল, ‘তোমার তো জানাই আছে যে, তোমার (নিজের বা জাতির) কন্যাদের আমাদের কোন দরকার নেই, আমরা কী চাই তাতো তুমি অবশ্যই জান।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1553,11,80,'সে বলল, ‘তোমাদেরকে দমন করার ক্ষমতা আমার যদি থাকত! অথবা কোন মজবুত আশ্রয়ে যদি আশ্রয় নিতে পারতাম!’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1554,11,81,'আগুন্তুকরা বলল, ‘হে লূত! আমরা তোমার প্রতিপালক প্রেরিত বার্তাবাহক, তারা তোমার কাছে কক্ষনো পৌঁছতে পারবে না, কাজেই কিছুটা রাত বাকী থাকতে তুমি তোমার পরিবার-পরিজন নিয়ে বেরিয়ে পড়, তোমাদের কেউ যেন পিছনের দিকে না তাকায়। কিন্তু তোমার স্ত্রী (তোমাদের সঙ্গী হতে পারবে না) তারও তাই ঘটবে, অন্যদের যা ঘটবে। সকাল হল তাদের (শাস্তি আসার) নির্ধারিত সময়, সকাল কি নিকটবর্তী নয়?’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1555,11,82,'তারপর আমার নির্দেশ যখন এসে গেল, তখন আমি সেই জনপদকে উপর নীচ করে উল্টে দিলাম, আর তাদের উপর স্তরে স্তরে পাকানো মাটির প্রস্তর বর্ষণ করলাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1556,11,83,'যে প্রস্তর খন্ডের প্রতিটিই তোমার প্রতিপালকের নিকট চিহ্নিত ছিল। যালিমদের জন্য এ শাস্তি বেশী দূরের ব্যাপার নয়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1557,11,84,'আর মাদইয়ানবাসীদের কাছে আমি তাদের ভাই শু‘আয়বকে পাঠিয়েছিলাম। সে বলেছিল, ‘হে আমার সম্প্রদায়! তোমরা আল্লাহর ‘ইবাদাত কর, তিনি ছাড়া তোমাদের কোন সত্য ইলাহ নেই, আর মাপে ও ওজনে কম দিও না, আমি তোমাদেরকে ভাল অবস্থাতেই দেখছি। কিন্তু আমি তোমাদের জন্য শাস্তির আশঙ্কা করছি সে দিনের যেদিন তোমাদেরকে চতুর্দিক থেকে ঘিরে ধরবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1558,11,85,'হে আমার সম্প্রদায়! মাপ ও ওজন ইনসাফের সঙ্গে পূর্ণ করো, লোকদেরকে তাদের প্রাপ্য কম দিও না, আর যমীনে ফাসাদ সৃষ্টি করে বেড়িও না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1559,11,86,'আল্লাহর অনুমোদিত উদ্বৃত্ত (অর্থাৎ লাভ) তোমাদের জন্য উত্তম, যদি তোমরা মু’মিন হও, আমি তোমাদের পর্যবেক্ষক নই।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1560,11,87,'তারা বলল, ‘হে শু‘আয়ব! তোমার ইবাদত কি তোমাকে এই হুকুম দেয় যে, আমাদের পিতৃপুরুষ যার ‘ইবাদাত করত আমরা তা পরিত্যাগ করি বা আমাদের ধন-সম্পদের ব্যাপারে আমাদের ইচ্ছে (মাফিক ব্যয় করা) বর্জন করি, তুমি তো দেখছি বড়ই ধৈর্যশীল, ভাল মানুষ।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1561,11,88,'সে বলল, ‘হে আমার জাতির লোকেরা! তোমরা কি ভেবে দেখেছ যদি আমি আমার প্রতিপালকের স্পষ্ট প্রমাণের উপর প্রতিষ্ঠিত হয়ে থাকি আর তিনি আমাকে তাঁর পক্ষ থেকে উত্তম রিযক দিয়ে থাকেন (তাহলে আমি কীভাবে তোমাদের অন্যায় কাজের সঙ্গী হতে পারি?), আমি তোমাদেরকে যে কাজ করতে নিষেধ করি সেটা তোমাদের প্রতি বিরুদ্ধাচরণ করার ইচ্ছায় নয়, আমি তো সাধ্যমত সংশোধন করতে চাই, আমার কাজের সাফল্য তো আল্লাহরই পক্ষ হতে, আমি তাঁর উপরই নির্ভর করি, আর তাঁর দিকেই মুখ করি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1562,11,89,'হে আমার সম্প্রদায়! আমার সঙ্গে বিরোধ তোমাদেরকে যেন কিছুতেই এমন কাজে উদ্বুদ্ধ না করে যাতে তোমাদের উপর এমন বিপদ আসে যেমন বিপদ এসেছিল নূহের জাতির কিংবা হূদের জাতির কিংবা সালিহ্র জাতির উপর। আর লূতের জাতির অবস্থান তো তোমাদের থেকে মোটেই দূরে নয়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1563,11,90,'তোমরা তোমাদের প্রতিপালকের নিকট ক্ষমা প্রার্থনা কর, অতঃপর তাঁরই নিকট তাওবাহ কর। আমার প্রতিপালক তো পরম দয়ালু, বড়ই ভালবাসা পোষণকারী।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1564,11,91,'তারা বলল, ‘হে শু‘আয়ব! তুমি যা বল তার অনেক কথাই আমরা বুঝি না, আমরা আমাদের মধ্যে তোমাকে অবশ্যই দুর্বল দেখছি, তোমার গোত্র না থাকলে আমরা তোমাকে অবশ্যই পাথর নিক্ষেপ ক’রে মেরে ফেলতাম, আমাদের উপর তোমার কোন ক্ষমতাই নেই। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1565,11,92,'সে বলল, ‘হে আমার সম্প্রদায়! আমার স্বজনরা কি তোমাদের কাছে আল্লাহর চেয়েও প্রবল! তোমরা তো তাঁকে সম্পূর্ণতঃ পেছনে ফেলে রেখেছ, তোমরা যা করছ আমার প্রতিপালক তা সব কিছুই অবগত।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1566,11,93,'হে আমার সম্প্রদায়! ‘তোমরা নিজ নিজ অবস্থানে থেকে কাজ করতে থাক, আমিও আমার  কাজ করতে  থাকি, তোমরা  অচিরেই জানতে পারবে কার উপর আসবে লাঞ্ছনাদায়ক শাস্তি আর কে মিথ্যেবাদী। কাজেই তোমরা অপেক্ষায় থাক, আমিও তোমাদের সাথে অপেক্ষায় থাকলাম।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1567,11,94,'আমার হুকুম যখন আসলো, তখন আমি আমার দয়ায় শু‘আয়ব আর তার সঙ্গে যারা ঈমান এনেছিল তাদেরকে বাঁচিয়ে দিলাম। আর যারা যুলম করেছিল তাদেরকে এক প্রচন্ড শব্দ আঘাত হানল যার ফলে তারা নিজেদের গৃহে নতজানু হয়ে পড়ে রইল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1568,11,95,'(এমনভাবে) যেন তারা সেখানে কোনদিনই বসবাস করেনি। জেনে রেখ, মাদইয়ানবাসীদেরকে দূরে নিক্ষেপ করা হল, যেমনভাবে দূরে নিক্ষেপ করা হয়েছিল সামূদজাতিকে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1569,11,96,'আমি মূসাকে পাঠিয়েছিলাম আমার নিদর্শন আর স্পষ্ট প্রমাণ সহকারে ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1570,11,97,'ফির‘আওন আর তার প্রধানদের কাছে, কিন্তু তারা ফির‘আওনের হুকুমই মেনে নিল, আর ফির‘আওনের হুকুম সত্য নির্ভর ছিল না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1571,11,98,'ক্বিয়ামাতের দিন সে তার সম্প্রদায়ের পুরোভাগে থাকবে আর তাদেরকে জাহান্নামে নিয়ে যেতে নেতৃত্ব দেবে, কতই না নিকৃষ্ট এ অবতরণ স্থল যাতে তারা উপনীত হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1572,11,99,'এ দুনিয়াতেও অভিশাপ তাদের পেছনে ছুটছে আর ক্বিয়ামাতের দিনেও। কত নিকৃষ্টই না সে পুরস্কার যা তাদেরকে দেয়া হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1573,11,100,'এ হল জনপদসমূহের কিছু খবরাদি যা আমি তোমার নিকট বর্ণনা করলাম, তাদের কতক এখনও দাঁড়িয়ে আছে আর কতক কর্তিত ফসলের দশা প্রাপ্ত হয়েছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1574,11,101,'আমি তাদের উপর যুলম করিনি বরং তারাই নিজেদের উপর যুলম করেছিল, কিন্তু তোমার প্রতিপালকের হুকুম যখন এসে গেল, তখন আল্লাহকে বাদ দিয়ে তারা যেসব ইলাহকে ডাকত ওগুলো তাদের কোনই কাজে আসল না,  তারা  ধ্বংস ছাড়া কিছুই বৃদ্ধি করতে পারল না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1575,11,102,'তোমার প্রতিপালকের পাকড়াও এ রকমই হয়ে থাকে যখন তিনি পাকড়াও করেন কোন জনপদকে যখন তারা যুল্মে লিপ্ত থাকে। অবশ্যই তাঁর পাকড়াও ভয়াবহ, বড়ই কঠিন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1576,11,103,'এতে অবশ্যই নিদর্শন আছে তার জন্য যে আখেরাতের শাস্তিকে ভয় করে। এটা এমন দিন, যে দিনের জন্য সব মানুষকে একত্রিত করা হবে, এটা হাযির হওয়ার দিন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1577,11,104,'আমি একটা নির্দিষ্ট সময়ের জন্য তাকে বিলম্বিত করি মাত্র। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1578,11,105,'সে দিন যখন আসবে তখন তাঁর অনুমতি ছাড়া কেউ মুখ খুলতে পারবে না, তাদের কেউ হবে হতভাগা, আর কেউ হবে সৌভাগ্যবান। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1579,11,106,'যারা হতভাগা হবে তারা জাহান্নামে যাবে, সেখানে তাদের জন্য আছে হা-হুতাশ আর আর্ত চীৎকার। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1580,11,107,'সেখানে তারা স্থায়ী হবে চিরকালের জন্য যে পর্যন্ত আকাশসমূহ ও পৃথিবী বিদ্যমান থাকবে যদি না তোমার প্রতিপালক অন্য কিছু ইচ্ছে করেন। তোমার প্রতিপালক অবশ্যই করতে সক্ষম যা তিনি করতে চান। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1581,11,108,'আর যারা সৌভাগ্যবান হবে, তারা জান্নাতে স্থায়ী হবে যে পর্যন্ত আকাশসমূহ ও পৃথিবী বিদ্যমান থাকবে, যদি না তোমার প্রতিপালক অন্য রকম ইচ্ছে করেন। এ হল এক অব্যাহত পুরস্কার। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1582,11,109,'কাজেই তারা যেগুলোর ‘ইবাদাত করে সেগুলোর ব্যাপারে সন্দেহে পতিত হয়ো না। তারা যেগুলোর ‘ইবাদাত করে সেগুলো তা ছাড়া আর কিছুই নয় যেগুলোর ‘ইবাদাত পূর্বে তাদের পিতৃপুরুষরা করত, আমি অবশ্যই তাদের প্রাপ্য অংশ তাদেরকে পূর্ণ মাত্রাতেই দেব, কোনই কমতি করা হবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1583,11,110,'ইতোপূর্বে আমি মূসাকেও কিতাব দিয়েছিলাম, কিন্তু তাতেও মতবিরোধ করা হয়েছিল। তোমার প্রতিপালকের পক্ষ থেকে একটি কথা যদি আগেই বলে দেয়া না হত, তাহলে তাদের মাঝে অবশ্যই ফায়সালাই ক’রে দেয়া হত, এ ব্যাপারে তারা অবশ্য সন্দেহপূর্ণ সংশয়ে পড়ে আছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1584,11,111,'এতে সন্দেহ নেই যে, তোমার প্রতিপালক প্রত্যেককেই তাদের ‘আমালের প্রতিফল অবশ্য অবশ্যই পুরোপুরি দান করবেন, তারা যা করে সে বিষয়ে তিনি পূর্ণ ওয়াকিফহাল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1585,11,112,'কাজেই তুমি ও তোমার সাথে যারা (আল্লাহর দিকে) তাওবা করেছে সুদৃঢ় হয়ে থাক আল্লাহ যেভাবে তোমাকে আদেশ দিয়েছেন, আর সীমালঙ্ঘন করো না। তোমরা যা কিছু কর তিনি তা ভালভাবেই দেখেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1586,11,113,'তোমরা যালিমদের প্রতি ঝুঁকে পড়ো না, তাহলে আগুন তোমাদেরকে স্পর্শ করবে, আর তখন আল্লাহ ছাড়া কেউ তোমাদের অভিভাবক থাকবে না, অত:পর তোমাদেরকে সাহায্যও করা হবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1587,11,114,'তুমি নামায প্রতিষ্ঠা কর দিনের দু’ প্রান্ত সময়ে আর কিছুটা রাত অতিবাহিত হওয়ার পর, পূণ্যরাজি অবশ্যই পাপরাশিকে দূর করে দেয়, এটা তাদের জন্য উপদেশ যারা উপদেশ গ্রহণ করে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1588,11,115,'তুমি ধৈর্য ধর, কারণ আল্লাহ সৎকর্মশীল লোকদের কর্মফল কখনও বিনষ্ট করেন না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1589,11,116,'তাহলে তোমাদের পূর্ব যুগের লোকদের মধ্যে এমন সৎকর্মশীল কেন হয়নি যারা যমীনে বিপর্যয় সৃষ্টি করা হতে বাধা দিত? এমন লোক কমই ছিল আর তাদেরকে আমি বাঁচিয়ে নিয়েছিলাম। যালিমরা তো তাদেরকে দেয়া সামগ্রীর সুখ স্বাচ্ছন্দ্যেই লিপ্ত থাকতো আর তারা ছিল অপরাধী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1590,11,117,'তোমার প্রতিপালক এমন নন যে, তিনি অন্যায়ভাবে কোন জনপদ ধ্বংস করবেন এমতাবস্থায় যে, তার অধিবাসীরা সদাচারী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1591,11,118,'তোমার প্রতিপালক চাইলে মানুষকে অবশ্যই এক জাতি করতে পারতেন, কিন্তু তারা মতভেদ করতেই থাকবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1592,11,119,'তবে তোমার প্রতিপালক যাদের প্রতি দয়া করেন তারা (মতবিরোধ করবে) না। এই উদ্দেশ্যেই তিনি তাদেরকে সৃষ্টি করেছেন, আর তোমার প্রতিপালকের এ বাণী পূর্ণ হবেই যে, আমি জাহান্নামকে জ্বিন আর মানুষ দিয়ে অবশ্য অবশ্যই ভরে দেব। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1593,11,120,'রসূলদের যে সব সংবাদসমূহ আমি তোমার কাছে বর্ণনা করলাম, এর দ্বারা আমি তোমার দিলকে মযবুত করছি, এতে তুমি প্রকৃত সত্যের জ্ঞান লাভ করবে আর মু’মিনদের জন্য এটা উপদেশ ও স্মারক। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1594,11,121,'যারা ঈমান আনে না তাদেরকে বল, ‘তোমরা নিজেদের মত ও পথে থেকে কাজ করে যাও, আমরা (আমাদের) কাজ করছি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1595,11,122,'আর তোমরা অপেক্ষা কর, আমরাও অপেক্ষায় থাকলাম।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1596,11,123,'আসমানসমূহ ও যমীনের অদৃশ্যের জ্ঞান কেবল আল্লাহরই রয়েছে। সকল বিষয়ই (চূড়ান্ত সিদ্ধান্তের জন্য) তাঁর কাছে ফিরে যায়, কাজেই তুমি তাঁরই ‘ইবাদাত কর, আর তাঁর উপরই নির্ভর কর, তোমরা যা কিছু করছ, সে সম্পর্কে তোমার প্রতিপালক মোটেই বে-খবর নন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1597,12,1,'আলিফ, লাম-রা, এগুলো সুস্পষ্ট কিতাবের আয়াতসমূহ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1598,12,2,'আমি তা অবতীর্ণ করেছি, আরবী ভাষার কুরআন, যাতে তোমরা ভালভাবে বুঝতে পার। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1599,12,3,'আমি তোমার কাছে সর্বোত্তম কাহিনী বর্ণনা করছি, এ কুরআন তোমার কাছে ওয়াহী যোগে পাঠিয়ে, যদিও এর পূর্বে তুমি না-জানা লোকদের মধ্যেই শামিল ছিলে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1600,12,4,'স্মরণ কর, ইউসুফ যখন তার পিতাকে বলেছিল, ‘হে আব্বাজান! আমি (স্বপ্নে) দেখেছি এগারটি তারকা আর সূর্য ও চন্দ্র; দেখলাম তারা আমাকে সাজদাহ করছে।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1601,12,5,'তার পিতা বললেন, ‘হে আমার পুত্র! তোমার স্বপ্নের কথা তোমার ভাইদের কাছে বর্ণনা করো না। যদি কর তাহলে তারা তোমার বিরুদ্ধে চক্রান্ত করবে। শাইত্বান তো মানুষের প্রকাশ্য দুশমন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1602,12,6,'(স্বপ্নে যেমন দেখেছ) এভাবে তোমার প্রতিপালক তোমাকে মনোনীত করবেন, তোমাকে স্বপ্নের ব্যাখ্যা শিক্ষা দিবেন এবং তিনি তাঁর অনুগ্রহ তোমার প্রতি আর ইয়া‘কূব পরিবারের প্রতি পূর্ণ করবেন যেভাবে তিনি তা পূর্বে তোমার পিতৃ-পুরুষ ইবরাহীম ও ইসহাকের প্রতি পূর্ণ করেছিলেন, নিশ্চয়ই তোমার রব্ব সর্বজ্ঞ, বড়ই প্রজ্ঞাবান।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1603,12,7,'ইউসুফ আর তার ভাইদের ঘটনায় সত্য সন্ধানীদের জন্য অবশ্যই নিদর্শনাবলী আছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1604,12,8,'স্মরণ কর, যখন তারা (বৈমাত্রেয় ভাইগণ) বলাবলি করছিল, ‘নিশ্চয়ই ইউসুফ আর তার (সহোদর) ভাই আমাদের পিতার কাছে আমাদের চেয়ে বেশি প্রিয়, অথচ আমরা পুরো একটা দল, আমাদের পিতা স্পষ্ট ভুলের মধ্যে আছেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1605,12,9,'তোমরা ইউসুফকে হত্যা করে ফেল কিংবা তাকে কোন ভূমিতে ফেলে আস, তাহলে তোমাদের পিতার দৃষ্টি তোমাদের প্রতিই নিবদ্ধ হবে, তার পর তোমরা (তাওবাহ করে) ভাল লোক হয়ে যাবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1606,12,10,'তাদের একজন বলল, তোমরা ইউসুফকে হত্যা করো না, তোমাদেরকে যদি কিছু করতেই হয় বরং তাকে কোন অন্ধ কূপে ফেলে দাও, কোন যাত্রীদল তাকে উঠিয়ে নিয়ে যাবে।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1607,12,11,'তারা বলল, ‘হে আমাদের আব্বাজান! কী ব্যাপার, আপনি ইউসুফের ব্যাপারে আমাদেরকে বিশ্বাস করেন না কেন, অথচ আমরা অবশ্যই তার কল্যাণকামী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1608,12,12,'তাকে আগামীকাল আমাদের সঙ্গে পাঠিয়ে দিন, সে আমোদ করবে আর খেলবে, আমরা তার পুরোপুরি দেখাশুনা করব।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1609,12,13,'পিতা বলল, ‘তোমরা যে তাকে নিয়ে যাবে সেটা আমাকে অবশ্যই কষ্ট দেবে আর আমি ভয় করছি যে, নেকড়ে বাঘ তাকে খেয়ে ফেলবে, আর তখন তার সম্পর্কে তোমরা বে-খেয়াল হয়ে থাকবে।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1610,12,14,'তারা বলল, ‘আমরা একটা দল থাকতে তাকে যদি নেকড়ে বাঘে খেয়ে ফেলে, তাহলে তো আমরা অপদার্থই বনে যাবো।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1611,12,15,'অতঃপর যখন তারা তাকে নিয়ে গেল, আর তাকে অন্ধ কূপে ফেলার ব্যাপারে মতৈক্যে পৌঁছল, এমতাবস্থায় আমি ইউসুফকে ওয়াহী করলাম- এক সময় আসবে যখন তুমি তাদেরকে তাদের এ কর্মের কথা অবশ্য অবশ্যই ব্যক্ত করবে।’ অথচ তারা (অর্থাৎ ইউসুফের ভাইয়েরা) মোটেই টের পাবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1612,12,16,'রাতের প্রথম প্রহরে তারা তাদের পিতার কাছে কাঁদতে কাঁদতে আসল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1613,12,17,'তারা বলল, ‘হে আমাদের আব্বাজান! আমরা দৌড়ের প্রতিযোগিতা করছিলাম, আর ইউসুফকে আমরা আমাদের জিনিসপত্রের কাছে রেখে গিয়েছিলাম, তখন তাকে নেকড়ে বাঘে খেয়ে ফেলল, কিন্তু আপনি তো আমাদের কথা বিশ্বাস করবেন না, আমরা সত্যবাদী হলেও।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1614,12,18,'তারা তার জামায় মিছেমিছি রক্ত মাখিয়ে নিয়ে এসেছিল। পিতা বলল, ‘না, বরং তোমাদের প্রবৃত্তি তোমাদেরকে একটা কাহিনী বানাতে উদ্বুদ্ধ করেছে। ঠিক আছে, আমি পুরোপুরি ধৈর্য ধারণ করব, তোমরা যা বানিয়েছ সে ব্যাপারে আল্লাহই আমার আশ্রয়স্থল।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1615,12,19,'সেখানে একটা কাফেলা আসলো। তারা তাদের পানি সংগ্রহকারীকে পাঠালো। সে তার পানির বালতি নামিয়ে দিল। সে বলল, ‘কী খুশির খবর! এ যে দেখছি এক বালক!’ তারা তাকে পণ্য দ্রব্য জ্ঞানে লুকিয়ে দিল, আর তারা যা করছিল সে সম্পর্কে আল্লাহ খুবই অবহিত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1616,12,20,'তারা তাকে স্বল্প মূল্যে- মাত্র কয়টি দিরহামের বিনিময়ে বিক্রি করে দিল, তারা ছিল তাকে তুচ্ছ জ্ঞানকারী! ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1617,12,21,'মিসরের যে লোক তাকে ক্রয় করেছিল, সে তার স্ত্রীকে বলল, ‘তার থাকার সুব্যবস্থা কর, সম্ভবতঃ সে আমাদের উপকারে আসবে কিংবা তাকে আমরা পুত্র হিসেবেও গ্রহণ করে নিতে পারি।’ এভাবে আমি ইউসুফকে সে দেশে প্রতিষ্ঠিত করলাম তাকে স্বপ্ন ব্যাখ্যার কিছু জ্ঞান শিক্ষা দেয়ার জন্য। আল্লাহ তাঁর কাজের ব্যাপারে পূর্ণ কর্তৃত্বশীল। কিন্তু অধিকাংশ লোকই (তা) জানে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1618,12,22,'যখন সে তার পরিপূর্ণ যৌবনে পৌঁছল, তখন তাকে বিচার-বুদ্ধি ও জ্ঞান দান করলাম, আমি সৎকর্মশীলদেরকে এভাবেই প্রতিদান দিয়ে থাকি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1619,12,23,'যে মহিলার ঘরে সে ছিল, সে (মহিলাটি) তার থেকে অসৎ কর্ম কামনা করল। সে দরজাগুলো বন্ধ করে দিল আর বলল, ‘এসো’। সে (ইউসুফ) বলল, ‘আমি আল্লাহর আশ্রয় নিচ্ছি। তিনি আমার রবব, তিনি আমার থাকার ব্যবস্থা কত উত্তম করেছেন, যালিমরা কক্ষনো সাফল্য লাভ করতে পারে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1620,12,24,'সেই মহিলা তার প্রতি আসক্ত হয়েছিল আর সে (ইউসুফ)ও তার প্রতি আসক্ত হয়েই যেত যদি সে তার প্রতিপালকের নিদর্শন না দেখত। আমি তা দেখিয়েছিলাম তাকে অসৎ কর্ম ও নির্লজ্জতা থেকে সরিয়ে রাখার উদ্দেশ্যে, সে ছিল বিশুদ্ধ-হৃদয় বান্দাদের অন্তর্ভুক্ত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1621,12,25,'তারা উভয়ে দরজার দিকে দৌড় দিল আর স্ত্রীলোকটি পিছন হতে তার জামা ছিঁড়ে ফেলল। এ সময় স্ত্রীলোকটির স্বামীকে তারা দু’জনে দরজার কাছে পেল। মহিলাটি বলল, ‘যে তোমার পরিবারের সাথে অপকর্ম করতে চায় তাকে জেলে পাঠানো অথবা ভয়াবহ শাস্তি ছাড়া উপযুক্ত দন্ড কী আর দেয়া যেতে পারে?’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1622,12,26,'সে (ইউসুফ) বলল, ‘সে-ই আমা হতে অসৎ কর্ম কামনা করেছে’। তখন মহিলাটির পরিবারের এক সাক্ষী সাক্ষ্য দিল- ‘যদি তার জামা সম্মুখ দিক থেকে ছেঁড়া হয়ে থাকে, তবে মহিলাটি সত্য বলেছে আর সে মিথ্যাবাদীদের অন্তর্ভুক্ত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1623,12,27,'আর যদি তার জামা পেছন হতে ছেঁড়া হয়ে থাকে, তবে মহিলাটি মিথ্যে বলেছে আর সে সত্যবাদীদের অন্তুর্ভক্ত।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1624,12,28,'স্বামী যখন ইউসুফের জামাটি পেছন হতে ছেঁড়া দেখতে পেল, তখন সে বলল, ‘এ সব হল তোমাদের নারীদের ছলনা, তোমাদের কূট কৌশল বড়ই কঠিন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1625,12,29,'ওহে ইউসুফ! তুমি ব্যাপারটা উপেক্ষা কর, আর ওহে নারী! তুমি তোমার অপরাধের জন্য ক্ষমা চাও, প্রকৃতপক্ষে তুমিই অপরাধী।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1626,12,30,'নগরীর কতক মহিলা বলল, ‘আযীযের স্ত্রী তার যুবক ক্রীতদাসের প্রতি আকৃষ্ট হয়ে পড়েছে, ভালবাসা তাকে উন্মাদ করে ফেলেছে, আমরা নিশ্চিতই তাকে প্রকাশ্য ভ্রান্তিতে নিপতিত দেখছি।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1627,12,31,'মহিলাটি যখন তাদের চক্রান্তের কথা জানতে পারল, তখন তাদেরকে ডেকে পাঠাল আর তাদের জন্য হেলান দিয়ে বসার ব্যবস্থা করল, আর তাদের প্রত্যেককে একটা করে ছুরি দিল। অতঃপর ইউসুফকে বলল, ‘ওদের সামনে বেরিয়ে এসো।’ যখন তারা তাকে দেখল, বিস্ময়ে অভিভূত হয়ে গেল আর নিজেদের হাত কেটে ফেলল, আর বলল, ‘আল্লাহ আমাদের রক্ষা করুন, এ তো মানুষ নয়, এতো এক সম্মানিত ফেরেশতা।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1628,12,32,'মহিলাটি বলল, ‘এ হল সেই যার ব্যাপারে তোমরা আমাকে ভৎর্সনা করছ, আমিই তো তাকে ভুলাতে চেষ্টা করেছি, কিন্তু সে নিজেকে নিষ্পাপ রেখেছে, আমি তাকে যে আদেশ করি তা যদি সে না করে, তাহলে তাকে অবশ্যই কয়েদ করা হবে, আর সে হীন লোকদের অন্তর্ভুক্ত হবে।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1629,12,33,'সে (ইউসুফ) বলল, ‘হে আমার রবব! তারা আমাকে যে দিকে ডাকছে তার চেয়ে জেলখানা আমার কাছে অধিক প্রিয়। তুমি যদি আমা হতে তাদের অপকৌশল সরিয়ে না দাও তা হলে আমি তাদের প্রতি আকৃষ্ট হয়ে পড়ব, আর অজ্ঞদের দলে শামিল হয়ে যাব।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1630,12,34,'তখন তার প্রতিপালক তার ডাকে সাড়া দিলেন আর তার থেকে তাদের কূট কৌশল অপসারিত করলেন, তিনি সব কিছু শুনেন, সব কিছু জানেন।  ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1631,12,35,'নিদর্শনবলী দেখার পর তাদের মনে হল যে, কিছু দিনের জন্য তাকে অবশ্য অবশ্যই কারারুদ্ধ করতে হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1632,12,36,'তার সঙ্গে দু’যুবকও কারাগারে প্রবেশ করেছিল। তাদের একজন বলল, ‘আমি স্বপ্নে দেখলাম যে আমি মদ তৈরি করছি।’ অন্যজন বলল, ‘আমি স্বপ্নে দেখলাম, আমি মাথায় রুটি বহন করছি আর পাখী তাত্থেকে খাচ্ছে। আমাদেরকে এর ব্যাখ্যা বলে দাও, আমরা দেখছি তুমি একজন সৎকর্মশীল লোক।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1633,12,37,'সে (ইউসুফ) বলল, ‘তোমাদেরকে যে খাবার দেয়া হয় তা আসার আগেই আমি তোমাদেরকে তার ব্যাখ্যা জানিয়ে দেব। আমার প্রতিপালক আমাকে যে জ্ঞান দান করেছেন এটা সেই জ্ঞানেরই অংশ। যে সম্প্রদায় আল্লাহতে বিশ্বাস করে না আর আখেরাতে অবিশ্বাসী, আমি তাদের নিয়ম নীতি পরিত্যাগ করেছি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1634,12,38,'আমি আমার পূর্বপুরুষ ইবরাহীম, ইসহাক ও ইয়া‘কূবের আদর্শের অনুসরণ করি। আল্লাহর সাথে কোন কিছুকে শরীক করা আমাদের কাজ নয়। এটা আমাদের প্রতি ও মানুষের প্রতি আল্লাহর অনুগ্রহ, কিন্তু অধিকাংশ লোকই শোকর করে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1635,12,39,'হে আমার জেলের সঙ্গীদ্বয়! ভিন্ন ভিন্ন প্রতিপালক ভালো, না মহাপরাক্রমশালী এক আল্লাহ? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1636,12,40,'তাঁকে বাদ দিয়ে তোমরা যার ‘ইবাদাত করছ তা কতকগুলো নাম ছাড়া আর কিছুই নয়, যে নামগুলো তোমরা আর তোমাদের পিতৃ পুরুষরা রেখেছ। এ ব্যাপারে আল্লাহ কোন প্রমাণ নাযিল করেননি। আল্লাহ ছাড়া কোন বিধান দাতা নেই। তিনি আদেশ করেছেন যে, তোমরা তাঁকে ছাড়া আর কারো ‘ইবাদাত করবে না, এটাই সঠিক দ্বীন, কিন্তু অধিকাংশ মানুষই তা জানে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1637,12,41,'হে আমার জেলের সঙ্গীদ্বয়! তোমাদের দু’জনের একজন তার প্রভুকে মদ পান করাবে আর অন্যজনকে শূলে দেয়া হবে, আর পাখী তার মস্তক ঠুকরে খাবে। তোমরা দু’জন যে সম্পর্কে জানতে চেয়েছ তার ফায়সালা হয়ে গেছে।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1638,12,42,'তাদের দু’জনের মধ্যে যে জন মুক্তি পাবে ব’লে সে (ইউসুফ) মনে করল তাকে বলল, ‘তোমার প্রভুর কাছে আমার সম্পর্কে বলিও।’ কিন্তু শয়তান তাকে তার প্রভুর কাছে ইউসুফের কথা উল্লেখ করতে ভুলিয়ে দিল। ফলে ইউসুফ বেশ কয়েক বছর কারাগারে আটক থেকে গেল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1639,12,43,'রাজা বললেন, ‘আমি স্বপ্নে দেখলাম সাতটি হৃষ্টপুষ্ট গাভী, সাতটি জীর্ণশীর্ণ গাভী তাদেরকে খাচ্ছে। (আর দেখলাম) সাতটি সবুজ সতেজ শীষ আর অন্য সাতটি শুকনো। ওহে সভাষদগণ! আমার কাছে তোমরা আমার স্বপ্নের ব্যাখ্যা কর যদি তোমরা ব্যাখ্যা করতে পার।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1640,12,44,'তারা বলল, ‘এতো অস্পষ্ট (অর্থহীন) স্বপ্নের কথা, আর আমরা এ ধরনের স্বপ্ন ব্যাখ্যায় অভিজ্ঞ নই।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1641,12,45,'দু’জনের মধ্যে যে জন জেল থেকে মুক্তি পেয়েছিল আর দীর্ঘকাল পর যার স্মরণ হল সে বলল, ‘আমি তোমাদেরকে তার ব্যাখ্যা বলে দেব, তবে তোমরা আমাকে (জেলখানায় ইউসুফের কাছে) পাঠাও। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1642,12,46,'সে বলল, ‘হে সত্যবাদী ইউসুফ! সাতটি হৃষ্টপুষ্ট গাভী, যাদেরকে খাচ্ছে জীর্ণশীর্ণ সাতটি গাভী আর সাতটি সবুজ সতেজ শীষ আর অন্যগুলো শুকনো। (আমাদেরকে এর ব্যাখ্যা জানিয়ে দাও) যাতে আমি তাদের কাছে ফিরে যেতে পারি আর তারা জেনে নিতে পারে।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1643,12,47,'সে (ইউসুফ) বলল, ‘সাত বছর তোমরা এক নাগাড়ে চাষ করবে, অতঃপর যখন ফসল কাটবে তখন তোমরা যে সামান্য পরিমাণ খাবে তা বাদে শিষ সমেত সংরক্ষণ করবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1644,12,48,'এরপর আসবে সাতটি কঠিন বছর। এ সময়ের জন্য পূর্বে যা তোমরা সঞ্চয় করেছিলে তা লোকে খাবে, কেবল সেই অল্পটুকু বাদে যা তোমরা সঞ্চয় করবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1645,12,49,'এর পর আসবে একটা বছর যখন মানুষের জন্য প্রচুর বৃষ্টিপাত হবে আর মানুষ প্রচুর ভোগবিলাস করবে।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1646,12,50,'রাজা বলল, ‘তোমরা তাকে (ইউসুফকে) আমার কাছে নিয়ে এসো।’ দূত যখন তার কাছে আসলো তখন ইউসুফ বলল, ‘তোমার প্রভুর কাছে ফিরে যাও এবং তাকে জিজ্ঞেস কর, সেই মহিলাদের ব্যাপারটি কী যারা তাদের হাত কেটে ফেলেছিল? আমার প্রতিপালক অবশ্যই তাদের কৌশল সম্পর্কে অবগত।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1647,12,51,'রাজা মহিলাদের জিজ্ঞেস করল- ‘তোমরা যখন ইউসুফকে ভুলাতে চেষ্টা করেছিলে তখন তোমাদের কী হয়েছিল?’ তারা বলল, ‘আল্লাহ আমাদেরকে রক্ষা করুন! আমরা তার মাঝে কোন দোষ দেখতে পাইনি।’ আযীযের স্ত্রী বলল, ‘এখন সত্য প্রকাশিত হয়ে পড়েছে, আমিই তাকে ভুলাতে চেষ্টা করেছিলাম, নিশ্চয়ই সে ছিল সত্যবাদী।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1648,12,52,'(ইউসুফ বলল) ‘আমার উদ্দেশ্য এই যে, সে (অর্থাৎ আযীয) যেন জানতে পারে যে, তার অনুপস্থিতিতে আমি তার প্রতি বিশ্বাসঘাতকতা করিনি আর আল্লাহ বিশ্বাসঘাতকদের কৌশলকে অবশ্যই সফল হতে দেন না।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1649,12,53,'(সে বলল), ‘আমি নিজেকে দোষমুক্ত মনে করি না, নফস্ তো মন্দ কাজে প্ররোচিত করতেই থাকে, আমার প্রতিপালক যার প্রতি দয়া করেন সে ছাড়া। আমার প্রতিপালক বড়ই ক্ষমাশীল, বড়ই দয়ালু।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1650,12,54,'রাজা বললেন, ‘তাকে আমার কাছে নিয়ে এসো, আমি তাকে আমার জন্য বিশেষভাবে নির্দিষ্ট করে নেব।’ অতঃপর সে (ইউসুফ) যখন তার সাথে কথা বলল, তখন রাজা বলল, ‘আজ তুমি আমাদের কাছে খুবই মর্যাদাশীল ও বিশ্বস্ত হিসেবে পরিগণিত।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1651,12,55,'সে (ইউসুফ) বলল- ‘আমাকে দেশের ধন-ভান্ডারের দায়িত্ব দিন, আমি উত্তম রক্ষক ও যথেষ্ট জ্ঞানের অধিকারী।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1652,12,56,'এভাবে আমি ইউসুফকে সে দেশে প্রতিষ্ঠিত করলাম। দেশের যেখানে ইচ্ছে সে নিজের স্থান করে নিতে পারত, আমি যাকে চাই আমার রহমাত দিয়ে ধন্য করি, আমি সৎকর্মশীলদের কর্মফল কক্ষনো বিনষ্ট করি না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1653,12,57,'আর আখেরাতের কর্মফল তাদের জন্য অবশ্যই উত্তম যারা ঈমান আনে আর তাকওয়া অবলম্বন করে কাজ করতে থাকে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1654,12,58,'ইউসুফের ভাইয়েরা আসল এবং তার কাছে হাযির হল। সে তাদেরকে চিনতে পারল, কিন্তু তারা তাকে চিনতে পারল না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1655,12,59,'সে যখন তাদের দ্রব্য সামগ্রীর ব্যবস্থা করে দিল তখন সে বলল, ‘তোমরা তোমাদের সৎ ভাইকে আমার কাছে নিয়ে আসবে, তোমরা কি দেখছ না, আমি কীভাবে পাত্র ভরে দেই, আর আমি উত্তম অতিথি সেবক। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1656,12,60,'তোমরা যদি তাকে না নিয়ে আসো তাহলে আমার কাছে তোমাদের জন্য কোন শস্য বরাদ্দ হবে না, আর আমার কাছেও আসতে পারবে না।’  ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1657,12,61,'তারা বলল- ‘এ ব্যাপারে আমরা তার পিতাকে রাযী করাতে চেষ্টা করব আর আমরা তা করবই।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1658,12,62,'সে (ইউসুফ) তার খাদেমদেরকে বলল, ‘তারা যে পণ্যমূল্য দিয়েছে তা তাদের মাল পত্রের মধ্যে গোপনে রেখে দাও, যাতে তারা তাদের পরিবারবর্গের কাছে ফিরে গিয়ে তা জানতে পারে, তাহলে তারা আবার আসবে।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1659,12,63,'তারপর যখন তারা তাদের পিতার কাছে ফিরে গেল তখন বলল, ‘হে আমাদের পিতা! আমাদের জন্য শষ্যের বরাদ্দ নিষিদ্ধ করা হয়েছে, কাজেই আমাদের ভাইকে আমাদের সঙ্গে পাঠিয়ে দিন, যাতে আমরা শস্যের বরাদ্দ পেতে পারি, আমরা অবশ্যই তার হিফাযাত করব।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1660,12,64,'(পিতা) বলল, ‘আমি কি তার ব্যাপারে তোমাদেরকে তেমনি বিশ্বাস করব ইতোপূর্বে যেমন তোমাদেরকে তার ভাইয়ের ব্যাপারে বিশ্বাস করেছিলাম? আল্লাহই উত্তম সংরক্ষক আর তিনিই সর্বশ্রেষ্ঠ দয়ালু।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1661,12,65,'তারা যখন তাদের মাল-পত্র খুলল তখন দেখতে পেল যে, তাদেরকে তাদের পণ্যমূল্য ফিরিয়ে দেয়া হয়েছে। তারা বলল, ‘হে আমাদের আব্বাজান! আমরা আর কী চাই। এই দেখুন, আমাদের পণ্যমূল্য আমাদেরকে ফিরিয়ে দেয়া হয়েছে, আমরা আমাদের পরিবারের জন্য (আরো) খাদ্য আনব, আমাদের ভাইয়ের হিফাযাতও করব, আরো এক উট-বোঝাই মাল বেশি আনব, এ পরিমাণ সহজেই পাওয়া যাবে।’');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1662,12,66,'(পিতা) বলল- ‘আমি তাকে তোমাদের সাথে কিছুতেই পাঠাবো না যতক্ষণ না তোমরা আল্লাহর নামে শপথ কর যে, তোমরা তাকে আমার কাছে নিয়ে আসবেই যদি না তোমাদেরকে একযোগে ঘিরে ফেলা হয়।’ অতঃপর তারা যখন তার কাছে প্রতিজ্ঞা করল তখন সে বলল, ‘আমরা যে কথা বলছি আল্লাহই তার সাক্ষী ও অভিভাবক।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1663,12,67,'পিতা বলল, ‘হে আমার সন্তানেরা! তোমরা এক দ্বার দিয়ে (মিসরে) প্রবেশ কর না, বরং ভিন্ন ভিন্ন দরজা দিয়ে প্রবেশ করবে (মানুষের সন্দেহ কিংবা কুদৃষ্টি এড়ানোর জন্য)। আমি আল্লাহর হুকুমের বিরুদ্ধে তোমাদের কোনই উপকার করতে পারব না। আল্লাহ ছাড়া হুকুম দাতা কেউ নেই, আমি তাঁর উপরই নির্ভর করি, যারা নির্ভর করতে চায়, তারা তাঁর উপর নির্ভর করুক।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1664,12,68,'তাদের পিতা যেভাবে আদেশ করেছিল যখন তারা সেভাবে প্রবেশ করল, তখন আল্লাহর হুকুমের বিরুদ্ধে তা তাদের কোন কাজে আসল না। তবে ইয়া‘কুব তার মনের একটা অভিপ্রায় পূর্ণ করেছিল মাত্র। আমার দেয়া শিক্ষার বদৌলতে অবশ্যই সে ছিল জ্ঞানবান, কিন্তু অধিকাংশ মানুষ প্রকৃত ব্যাপার সম্পর্কে অবগত নয়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1665,12,69,'যখন তারা ইউসুফের কাছে হাজির হল, তখন সে তার সহোদর ভাইকে নিজের কাছে রাখল। আর বলল, ‘আমিই তোমার ভাই, কাজেই ওরা যা করত তার জন্য দুঃখ করো না।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1666,12,70,'অতঃপর ইউসুফ যখন তাদের রসদপত্র প্রস্তুত করে দিল, তখন সে তার সহোদর ভাইয়ের রসদপত্রের ভিতর পান পাত্রটি রেখে দিল। তখন এক ঘোষক ঘোষণা দিল, ‘হে কাফেলার লোক! তোমরা নিশ্চয়ই চোর।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1667,12,71,'তারা তাদের দিকে ফিরে বলল, ‘তোমাদের কী হারিয়েছে?’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1668,12,72,'তারা বলল- ‘আমরা রাজার পান পাত্র হারিয়েছি, (তাদের প্রধান ব্যক্তি বলল) যে তা এনে দিতে পারবে তার জন্য আছে এক উট-বোঝাই মাল। আর আমিই এ ব্যাপারে প্রতিজ্ঞাবদ্ধ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1669,12,73,'ইউসুফের ভাইয়েরা বলল, ‘আল্লাহর শপথ! তোমরা তো জান আমরা এ দেশে অশান্তি সৃষ্টি করতে আসিনি, আর আমরা চোরও নই।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1670,12,74,'রাজকর্মচারীরা বলল, ‘তোমরা মিথ্যেবাদী হলে যে চুরি করেছে তার শাস্তি কী হবে?’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1671,12,75,'তারা বলল, ‘তার শাস্তি হল যার মালের ভিতর ওটা পাওয়া যাবে তাকেই ধরে রাখা হবে। সীমালঙ্ঘনকারীদেরকে আমরা এভাবেই শাস্ত দিয়ে থাকি।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1672,12,76,'অতঃপর ইউসুফ তার নিজ ভাইয়ের মালপত্র তল্লাশির আগে অন্যদের মাল তল্লাশি শুরু করল। অতঃপর সেটি তার নিজ ভাইয়ের মালপত্র থেকে বের করল। এভাবে আমি ইউসুফের জন্য পরিকল্পনা করেছিলাম। রাজার আইন অনুযায়ী সে তার সহোদর ভাইকে আটক করতে পারত না-আল্লাহর ইচ্ছে ব্যতীত। আমি যার জন্য ইচ্ছে করি মর্যাদা উচ্চ করি, প্রত্যেক জ্ঞানীর উপরে আছেন একজন সর্বজ্ঞ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1673,12,77,'ইউসুফের ভাইয়েরা বলল, ‘সে যদি চুরি করে থাকে (তবে তা অসম্ভব নয়, কেননা) এর পূর্বে তার সহোদর ভাইও চুরি করেছিল। তখন ইউসুফ এ ব্যাপারটি তার মনেই গোপন রাখল, তা তাদের কাছে প্রকাশ করল না। সে (মনে মনে) বলল- তোমাদের অবস্থান তো আরো নিকৃষ্টতম, তোমরা যা বলছ সে সম্পর্কে আল্লাহ খুব ভালভাবেই অবগত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1674,12,78,'তারা বলল, ‘হে ‘আযীয! এর পিতা আছেন যিনি খুবই বৃদ্ধ, কাজেই তার স্থলে আমাদের একজনকে রাখুন, আমরা তো আপনাকে বড়ই সদাচারী লোকদের অন্তর্ভুক্ত দেখছি।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1675,12,79,'সে বলল, ‘যার কাছে আমাদের মাল পেয়েছি তাকে ছাড়া অন্যকে ধরার কাজ থেকে আল্লাহর পানাহ চাচ্ছি, তা করলে আমরা যালিম হিসেবে পরিগণিত হব।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1676,12,80,'যখন তারা ইউসুফের নিকট থেকে নিরাশ হয়ে গেল তখন তারা নির্জনে গিয়ে পরামর্শ করল। তাদের মধ্যে বয়সে সবার বড় ভাইটি বলল, ‘তোমরা কি জান না যে, তোমাদের পিতা তোমাদের থেকে আল্লাহর নামে শপথ গ্রহণ করেছেন, আর এর পূর্বেও তোমরা ইউসুফের ব্যাপারে (তোমাদের কর্তব্য পালনে) ব্যর্থ হয়েছ, কাজেই আমি কিছুতেই এখান থেকে নড়বো না যতক্ষণ না আমার পিতা আমাকে অনুমতি দিবেন কিংবা আল্লাহ আমার ব্যাপারে কোন নির্দেশ দিবেন, কেননা তিনি হলেন সর্বোত্তম ফায়সালাকারী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1677,12,81,'তোমরা তোমাদের পিতার কাছে ফিরে যাও, গিয়ে বল, ‘হে আমাদের পিতা! আপনার ছেলে চুরি করেছে। আমরা যেটুকু জানি তারই চাক্ষুষ বিবরণ দিচ্ছি, চোখের আড়ালের ঘটনা তত্ত্বাবধান করার ক্ষমতা আমাদের নেই। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1678,12,82,'আমরা যে জনপদে ছিলাম তার বাসিন্দাদের জিজ্ঞেস করুন আর যে কাফেলার সঙ্গে আমরা এসেছি তাদেরকেও, আমরা অবশ্যই সত্যবাদী।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1679,12,83,'ই‘য়াকুব বলল, ‘না, বরং তোমরা নিজেরাই একটা কাহিনী সাজিয়ে নিয়ে এসেছ, কাজেই ধৈর্য ধারণই আমার জন্য শ্রেয়, সম্ভবতঃ আল্লাহ তাদেরকে একত্রে আমার কাছে এনে দেবেন। তিনি হলেন সর্বজ্ঞ, বড়ই প্রজ্ঞাময়।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1680,12,84,'তিনি তাদের থেকে মুখ ফিরিয়ে নিলেন আর বললেন, ‘ইউসুফের জন্য বড়ই পরিতাপ।’ শোকে দুঃখে তার দু’চোখ সাদা হয়ে গিয়েছিল, আর সে অস্ফুট মনস্তাপে ভুগছিল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1681,12,85,'তারা বলল, ‘আল্লাহর শপথ! আপনি ইউসুফের স্মরণ ত্যাগ করবেন না যতক্ষণ না আপনি মুমূর্ষু হবেন কিংবা আপনি মৃত্যুবরণ করেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1682,12,86,'সে বলল, ‘আমি আমার দুঃখ বেদনা আল্লাহর কাছেই নিবেদন করছি, আর আমি আল্লাহর নিকট হতে যা জানি, তোমরা তা জান না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1683,12,87,'হে আমার ছেলেরা! তোমরা যাও, গিয়ে ইউসুফ আর তার ভাইয়ের খোঁজ খবর লও, আল্লাহর রহমাত থেকে নিরাশ হয়ো না, কেননা কাফির সম্প্রদায় ছাড়া আল্লাহর রহমাত হতে কেউ নিরাশ হয় না।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1684,12,88,'যখন তারা ইউসুফের দরবারে উপস্থিত হল, তারা বলল, ‘হে আযীয! আমাদেরকে আর আমাদের পরিবারবর্গকে বিপদে ঘিরে ধরেছে, আর আমরা স্বল্প পুঁজি নিয়ে এসেছি, আমাদেরকে পূর্ণ ওজনের শষ্য দিন আর আমাদেরকে দান খায়রাত করুন। আল্লাহ দানশীলদেরকে পুরস্কৃত করেন।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1685,12,89,'সে বলল, ‘তোমরা কি জান তোমরা ইউসুফ আর তার ভাইয়ের সঙ্গে কেমন ব্যবহার করেছিলে, যখন তোমরা অজ্ঞ-মূর্খ ছিলে?’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1686,12,90,'তারা বলল, ‘তাহলে তুমিই কি ইউসুফ?’ সে বলল, ‘আমিই ইউসুফ আর এটা হল আমার ভাই। আল্লাহ আমাদের প্রতি অনুগ্রহ করেছেন। যে ব্যক্তি তাকওয়া অবলম্বন করে আর ধৈর্যধারণ করে এমন সৎকর্মশীলদের কর্মফল আল্লাহ কক্ষনো বিনষ্ট করেন না।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1687,12,91,'তারা বলল, ‘আল্লাহর কসম! আল্লাহ তোমাকে আমাদের উপর মর্যাদা দিয়েছেন, আমরাই ছিলাম অপরাধী।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1688,12,92,'সে বলল, ‘আজ তোমাদের বিরুদ্ধে আমার কোনই অভিযোগ নেই, আল্লাহ তোমাদেরকে মাফ করুন! তিনি হলেন সর্বশ্রেষ্ঠ দয়ালু।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1689,12,93,'তোমরা আমার এ জামাটি নিয়ে যাও আর তা আমার পিতার মুখমন্ডলে রাখ, তিনি দৃষ্টিশক্তিসম্পন্ন হয়ে উঠবেন, আর তোমাদের পরিবারের সবাইকে আমার কাছে নিয়ে এসো।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1690,12,94,'যাত্রীদল যখন (মিসর থেকে) বেরিয়ে পড়ল এমন সময়ে তাদের পিতা (বাড়ীর লোকজনকে) বলল, ‘তোমরা আমাকে বয়োবৃদ্ধ দিশেহারা মনে না করলে (তোমরা জেনে রাখ) আমি অবশ্যই ইউসুফের ঘ্রাণ পাচ্ছি।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1691,12,95,'বাড়ীতে উপস্থিত ব্যক্তিরা বলল, ‘আল্লাহর কসম! তুমি তো সেই তোমার পুরনো বিভ্রান্তিতেই আছ দেখছি।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1692,12,96,'সুসংবাদদাতা যখন এসে হাযির হল, তখন সে জামাটি ই‘য়াকুবের মুখমন্ডলের উপর রাখল, তাতে সে দৃষ্টিশক্তি ফিরে পেল। সে বলল, ‘আমি কি তোমাদের বলিনি যে, আমি আল্লাহর নিকট হতে যা জানি তা তোমরা জান না।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1693,12,97,'তারা বলল, ‘হে আমাদের পিতা! আমাদের গুনাহসমূহ মাফির জন্য প্রার্থনা করুন। আমরাই ছিলাম অপরাধী।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1694,12,98,'সে বলল, ‘শীঘ্রই আমি আমার রবেবর কাছে তোমাদের ক্ষমার জন্য প্রার্থনা জানাব, তিনি তো বড়ই ক্ষমাশীল, বড়ই দয়ালু।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1695,12,99,'তারা যখন ইউসুফের কাছে উপস্থিত হল, সে তার পিতা-মাতাকে নিজের কাছে স্থান দিল এবং বলল, ‘আল্লাহর ইচ্ছেয় পূর্ণ নিরাপত্তায় মিসরে প্রবেশ করুন।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1696,12,100,'সে তার পিতা-মাতাকে সিংহাসনে উঠিয়ে নিল আর সকলে তার সম্মানে সাজদাহয় ঝুঁকে পড়ল। ইউসুফ বলল, ‘হে পিতা! এ-ই হচ্ছে আমার সে আগের দেখা স্বপ্নের ব্যাখ্যা। আমার রব্ব একে সত্যে পরিণত করেছেন, তিনি আমার প্রতি অনুগ্রহ করেছেন যে, তিনি আমাকে কয়েদখানা থেকে বের করে এনেছেন। আর শাইত্বান আমার আর আমার ভাইদের মধ্যে বিভেদ সৃষ্টি করার পরও তিনি আপনাদেরকে মরু অঞ্চল থেকে এখানে (মিসরে) এনে দিয়েছেন। আমার রব্ব যা করতে ইচ্ছে করেন তা সূক্ষ্ণ উপায়ে বাস্তবায়িত করে থাকেন, তিনি বড়ই বিজ্ঞ, বড়ই প্রজ্ঞাময়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1697,12,101,'‘হে আমার প্রতিপালক! তুমি আমাকে রাজত্ব দান করেছ, আর আমাকে স্বপ্নের ব্যাখ্যা শিখিয়েছ। আসমান যমীনের সৃষ্টিকর্তা! তুমিই দুনিয়ায় আর আখেরাতে আমার অভিভাবক, তুমি মুসলিম অবস্থায় আমার মৃত্যু দান করো এবং আমাকে সৎকর্মশীলদের অন্তর্ভুক্ত করো।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1698,12,102,'অদৃশ্য জগতের খবর থেকে এটা তোমাকে ওয়াহী করে জানালাম। ষড়যন্ত্র করার সময় যখন তারা তাদের কাজে জোটবদ্ধ হয়েছিল তখন তুমি তো তাদের কাছে ছিলে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1699,12,103,'তুমি যত প্রবল আগ্রহ ভরেই চাও না কেন, মানুষদের অধিকাংশই ঈমান আনবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1700,12,104,'তুমি তার জন্য তাদের কাছে কোন পারিশ্রমিক চাচ্ছ না, এটা তো বিশ্বজগতের সকলের জন্য উপদেশ মাত্র। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1701,12,105,'আসমানে আর যমীনে বহু নিদর্শন আছে যেগুলোর উপর দিয়ে তারা (প্রতিনিয়ত) অতিক্রম করে কিন্তু এসব কিছু থেকে তারা অবজ্ঞা করছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1702,12,106,'অধিকাংশ মানুষ আল্লাহতে বিশ্বাস করে, কিন্তু সাথে সাথে শিরকও করে।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1703,12,107,'তারা কি নিশ্চিন্ত যে, আল্লাহর সর্বগ্রাসী ‘আযাব তাদের উপর এসে পড়বে না? কিংবা হঠাৎ ক্বিয়ামাত তাদের উপর এসে পড়বে না যা তারা টেরও পাবে না? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1704,12,108,'বল, ‘এটাই আমার পথ, আল্লাহর পথে আহবান জানাচ্ছি, আমি ও আমার অনুসারীরা, স্পষ্ট জ্ঞানের মাধ্যমে। আল্লাহ মহান, পবিত্র; আমি কক্ষনো মুশরিকদের মধ্যে শামিল হব না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1705,12,109,'তোমার পূর্বে জনপদবাসীদের মধ্যে যাদের কাছে ওয়াহী করতাম তারা পুরুষ মানুষ ব্যতীত ছিল না। তারা কি পৃথিবীতে ভ্রমণ করতঃ দেখে না যে, তাদের পূর্ববর্তী লোকেদের পরিণাম কী হয়েছিল? যারা তাকওয়া অবলম্বন করে, নিশ্চিতই পরলোকের ঘর তাদের আরো উত্তম, তবুও কি তোমরা বুঝবে না? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1706,12,110,'(হে নাবী! তোমার পূর্বেও এমন ঘটেছে যে,) শেষ পর্যন্ত রসূলগণ নিরাশ হয়েছে, আর লোকেরা মনে করেছে যে, তাদেরকে মিথ্যে কথা বলা হয়েছে, তখন তাদের (রাসূলদের) কাছে আমার সাহায্য এসে পৌঁছেছে, এভাবেই আমি যাকে ইচ্ছে রক্ষা করি। অপরাধী সম্প্রদায় থেকে আমার শাস্তি কক্ষনো ফিরিয়ে নেয়া হয় না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1707,12,111,'এদের কাহিনীসমূহে বোধশক্তিসম্পন্ন মানুষদের জন্য শিক্ষণীয় বিষয় আছে। এ কুরআন কোন মিথ্যে রচনা নয়, বরং তাদের পূর্বে আগত কিতাবের প্রত্যয়নকারী আর যাবতীয় বিষয়ের বিস্তারিত বিররণে সমৃদ্ধ, আর মু’মিন সম্প্রদায়ের জন্য পথের দিশারী ও রহমাত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1708,13,1,'আলিফ-লাম-মীম-র, এগুলো কিতাবের আয়াতসমূহ, আর তোমার প্রতিপালকের নিকট থেকে তোমার প্রতি যা অবতীর্ণ হয়েছে তা প্রকৃত সত্য, কিন্তু অধিকাংশ মানুষই ঈমান আনে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1709,13,2,'আল্লাহই স্তম্ভ ছাড়াই আকাশমন্ডলীকে ঊর্ধ্বে তুলে রেখেছেন, যা তোমরা দেখছ, অতঃপর তিনি আরশে সমুন্নত হয়েছেন তিনিই সূর্য ও চন্দ্রকে নিয়মের বন্ধনে বশীভূত রেখেছেন, প্রত্যেকেই নির্দিষ্ট সময়ের জন্য গতিশীল আছে। যাবতীয় বিষয় তিনিই নিয়ন্ত্রণ করেন, তিনি নিদর্শনসমূহ বিশদভাবে বর্ণনা করেন যাতে তোমরা তোমাদের প্রতিপালকের সঙ্গে সাক্ষাতের ব্যাপারে দৃঢ় বিশ্বাসী হতে পার। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1710,13,3,'তিনিই যমীনকে বিছিয়ে দিয়েছেন আর তাতে পর্বত ও নদীনালা সংস্থাপিত করেছেন, আর তাতে সকল প্রকারের ফল জোড়ায় জোড়ায় সৃষ্টি করেছেন। তিনি দিবসের উপর রাতের আবরণ টেনে দেন। চিন্তাশীল সম্প্রদায়ের জন্য এতে অবশ্যই নিদর্শনাবলী রয়েছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1711,13,4,'যমীনে আছে বিভিন্ন ভূখন্ড যা পরস্পর সংলগ্ন, আছে আঙ্গুরের বাগান, শস্য ক্ষেত, খেজুর গাছ- একই মূল হতে উদ্গত আর একই মূল থেকে উদগত নয়- যদিও একই পানিতে সিক্ত। খাওয়ার স্বাদে এদের কতককে কতকের উপর শ্রেষ্ঠত্ব দিয়েছি। জ্ঞানী সম্প্রদায়ের জন্য এতে অবশ্যই নিদর্শনাবলী রয়েছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1712,13,5,'তুমি যদি বিস্ময়বোধ কর তবে বিস্ময়কর হল তাদের কথাঃ ‘আমরা যখন মাটিতে পরিণত হব তখন কি আমাদেরকে নতুনভাবে আবার সৃষ্টি করা হবে?’ তারা হল সেই লোক যারা তাদের প্রতিপালককে অস্বীকার করে, এদের গলায় আছে লোহার শিকল, আর এরা জাহান্নামের অধিবাসী, তাতে তারা চিরকাল থাকবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1713,13,6,'কল্যাণের আগে আগে অকল্যাণ নিয়ে আসার জন্য তোমার নিকট তারা তাড়াহুড়া করছে, এদের পূর্বে এর বহু দৃষ্টান্ত অতীত হয়েছে। মানুষ সীমালঙ্ঘন করলেও তোমার প্রতিপালক তাদের প্রতি অবশ্যই ক্ষমাশীল আর তোমার প্রতিপালক অবশ্যই শাস্তিদানেও কঠোর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1714,13,7,'যারা কুফুরী করেছে তারা বলে, ‘তার কাছে তার প্রতিপালকের পক্ষ হতে কোন নিদর্শন অবতীর্ণ হয় না কেন?’ তুমি তো শুধু সতর্ককারী, আর প্রত্যেক সম্প্রদায়ের জন্য রয়েছে একজন সঠিক পথ প্রদর্শনকারী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1715,13,8,'আল্লাহ জানেন প্রতিটি নারী যা গর্ভে বহন করে, প্রত্যেক গর্ভে যা কমে বা বাড়ে তাও, প্রতিটি জিনিস তাঁর কাছে আছে পরিমাণ মত।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1716,13,9,'গোপন ও প্রকাশ্য সম্পর্কে তিনি জ্ঞাত, তিনি মহান, সর্বোচ্চ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1717,13,10,'তোমাদের কেউ কথা গোপন করে বা প্রকাশ করে, কেউ রাতে লুকিয়ে থাকে বা দিনে প্রকাশ্যে চলাফেরা করে, সবাই তাঁর কাছে সমান। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1718,13,11,'মানুষের সামনে ও পেছনে পাহারাদার নিযুক্ত আছে যারা আল্লাহর হুকুম মোতাবেক তাকে রক্ষণাবেক্ষণ করে। আল্লাহ কোন সম্প্রদায়ের অবস্থা পরিবর্তন করেন না যতক্ষণ না তারা নিজেরাই তাদের অবস্থা পরিবর্তন করে। আল্লাহ কোন সম্প্রদায়ের অকল্যাণ করতে চাইলে তা রদ করার কেউ নেই, আর তিনি ছাড়া তাদের কোন অভিভাবক নেই। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1719,13,12,'তিনিই তোমাদেরকে দেখান বিদ্যুৎ ভীতিকর ও আশা সঞ্চারক। তিনিই উত্তোলিত করেন মেঘ, (প্রবৃদ্ধির) বৃষ্টিতে ভারাক্রান্ত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1720,13,13,'বজ্রনাদ তাঁরই ভয়ে তাঁর প্রশংসা বর্ণনা করে আর ফেরেশতারাও। তিনি গর্জনকারী বজ্র প্রেরণ করেন আর তা দিয়ে যাকে ইচ্ছে আঘাত করেন, আর তারা আল্লাহ সম্পর্কে বিতন্ডায় লিপ্ত হয়। অথচ তিনি বড়ই শক্তিশালী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1721,13,14,'সত্যিকার আহবান-প্রার্থনা তাঁরই প্রাপ্য, যারা তাঁকে ছাড়া অন্যকে ডাকে, তারা তাদেরকে কোনই জবাব দেয় না। তারা হল ঐ লোকের মত, যে তার মুখে পানি পৌঁছবে ভেবে পানির দিকে হাত প্রসারিত করে দেয়, অথচ সে পানি তার মুখে কক্ষনো পৌঁছবে না। কাফিরদের আহবান নিষ্ফল ব্যতীত নয়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1722,13,15,'আসমানে আর যমীনে যা কিছু আছে ইচ্ছায় বা অনিচ্ছায় সকাল-সন্ধ্যায় আল্লাহর প্রতি সাজদাহয় অবনত হয় আর তাদের ছায়াগুলোও। [সাজদাহ]');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1723,13,16,'বল, আকাশ ও যমীনের প্রতিপালক কে? বল, আল্লাহ। বল, তোমরা কি তাঁকে বাদ দিয়ে এমন অভিভাবক গ্রহণ করেছ যাদের নিজেদের কোন লাভ-লোকসান করার ক্ষমতা নেই। বল, অন্ধ ও চক্ষুষ্মান কি সমান? কিংবা অন্ধকার আর আলো কি সমান? কিংবা তারা কি আল্লাহর অংশীদার বানিয়েছে তাদেরকে যারা তাঁর সৃষ্টির মত সৃষ্টি করেছে, যে কারণে সৃষ্টি তাদের কাছে সমান মনে হয়েছে? বল, আল্লাহ সবকিছুর সৃষ্টিকর্তা, তিনি এক, মহা প্রতাপশালী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1724,13,17,'তিনি আকাশ থেকে বৃষ্টি বর্ষণ করেন যার ফলে উপত্যকাগুলো পরিমিতভাবে প্লাবিত হয়, প্লাবণে উপরিভাগে ফেনা জমে উঠে, আর এ রকম ফেনা জমে উঠে যখন অলঙ্কার ও তৈজসপত্র বানানোর জন্য সেগুলো আগুনে গলানো হয়। এভাবে আল্লাহ প্রকৃত সত্য ও মিথ্যার দৃষ্টান্ত বর্ণনা করে থাকেন। ফেনা খড়কুটোর মত উড়ে যায়, আর যা মানুষের জন্য উপকারী তা যমীনে স্থিতিশীল হয়। এভাবে আল্লাহ দৃষ্টান্ত দিয়ে (মানুষকে বুঝিয়ে) থাকেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1725,13,18,'যারা তাদের প্রতিপালকের ডাকে সাড়া দেয় তাদের জন্য আছে (যাবতীয়) কল্যাণ। যারা তাঁর ডাকে সাড়া দেয় না, তারা যদি দুনিয়াতে যা কিছু আছে যে সবের মালিক হয় এবং আরো অত পরিমাণও তাদের হয়, নিজেদের মুক্তির বিনিময়ে তা সবই তারা দিতে চাইবে, তাদের হিসাব হবে বড়ই কঠিন, তাদের আবাসস্থল জাহান্নাম, আর তা কতই না নিকৃষ্ট ঠিকানা! ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1726,13,19,'যে ব্যক্তি জানে যে, তোমার প্রতিপালকের নিকট থেকে তোমার প্রতি যা অবতীর্ণ হয়েছে তা সত্য, সে কি ঐ ব্যক্তির সমান যে অন্ধ? বুদ্ধিমান লোকেরাই উপদেশ গ্রহণ করে থাকে ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1727,13,20,'যারা আল্লাহকে দেয়া তাদের ও‘য়াদা রক্ষা করে আর প্রতিশ্রুতি ভঙ্গ করে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1728,13,21,'তারা সে সব সম্পর্ক সম্বন্ধ বহাল রাখে যা বহাল রাখার জন্য আল্লাহ নির্দেশ দিয়েছেন। তারা তাদের প্রতিপালককে ভয় করে এবং ভয়াবহ হিসাব নিকাশকে ভয় পায়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1729,13,22,'তারা তাদের প্রতিপালকের চেহারা (সন্তুষ্টি) কামনায় ধৈর্য ধারণ করে, নামায প্রতিষ্ঠা করে, আর আমি তাদেরকে যে জীবিকা দিয়েছি তাত্থেকে গোপনে ও প্রকাশ্যে ব্যয় করে। তারা অন্যায়কে ন্যায় দ্বারা প্রতিরোধ করে। পরকালের ঘর প্রাপ্তি এদের জন্যই নির্দিষ্ট। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1730,13,23,'তা হল স্থায়ী জান্নাত, তাতে তারা প্রবেশ করবে। আর তাদের পিতৃ পুরুষ, স্বামী-স্ত্রী ও সন্তানাদির মধ্যে যারা সৎকর্ম করেছে তারাও। আর ফেরেশতারা সকল দরজা দিয়ে তাদের কাছে হাযির হয়ে সংবর্ধনা জানাবে (এই বলে যে)- ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1731,13,24,'‘‘তোমাদের প্রতি শান্তি বর্ষিত হোক, কারণ তোমরা ধৈর্যধারণ করেছিলে। কতই না উত্তম পরকালের এই ঘর!’’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1732,13,25,'আর যারা আল্লাহর সাথে শক্ত ও‘য়াদা করার পর তা ভঙ্গ করে, আল্লাহ যা বহাল রাখার নির্দেশ দিয়েছেন তা ছিন্ন করে, আর যমীনে অশান্তি সৃষ্টি করে, তাদের প্রতি অভিশাপ, তাদের জন্য আছে অতিশয় মন্দ আবাসস্থল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1733,13,26,'আল্লাহ রিযক সম্প্রসারিত করেন যার জন্য ইচ্ছে করেন, আর যার জন্য চান সীমিত পরিমাণে দেন। তারা পার্থিব জীবনে আনন্দে মেতে আছে অথচ আখেরাতের তুলনায় দুনিয়ার জীবন অতি নগণ্য বস্তু। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1734,13,27,'সত্য প্রত্যাখ্যানকারীরা বলে, ‘তার কাছে তার প্রতিপালকের নিকট হতে কোন নিদর্শন অবতীর্ণ হয় না কেন?’ বল, ‘আল্লাহ যাকে ইচ্ছে গুমরাহ করেন, আর যে তাঁর অভিমুখী তাকে সত্য পথে পরিচালিত করেন।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1735,13,28,'তারাই ঈমান আনে এবং আল্লাহর স্মরণে তাদের অন্তর প্রশান্তি লাভ করে। জেনে রেখ, আল্লাহর স্মরণের মাধ্যমেই দিলের সত্যিকারের প্রশান্তি লাভ করা যায়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1736,13,29,'যারা ঈমান আনে ও সৎকাজ করে, সৌভাগ্য তাদেরই, উত্তম পরিণাম তাদের জন্যই। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1737,13,30,'(পূর্বে যেমন পাঠিয়েছিলাম) এভাবেই আমি তোমাকে এমন এক সম্প্রদায়ের কাছে পাঠিয়েছি যার পূর্বে অনেক সম্প্রদায় গত হয়ে গেছে, যাতে তুমি তাদের কাছে তেলাওয়াত কর যা আমি তোমার প্রতি ওয়াহী করি, তবুও তারা দয়াময় আল্লাহকে অস্বীকার করে। বল, ‘তিনি আমার প্রতিপালক, তিনি ছাড়া সত্যিকারের কোন ইলাহ নেই, আমি তাঁর উপরই নির্ভর করি আর তিনিই প্রত্যাবর্তনস্থল।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1738,13,31,'কুরআন দিয়ে পর্বতকে যদি গতিশীল করা যেত, কিংবা যমীনকে দীর্ণ করা যেত কিংবা তা দিয়ে মৃত মানুষকে কথা বলানো যেত (তবুও তারা তাতে বিশ্বাস করত না)। (আলৌকিক কিছু করা মানুষের সাধ্যাতীত) বরং সমস্ত কিছুই আল্লাহর ক্ষমতাভুক্ত। যারা ঈমান এনেছে তারা কি জানে না যে, আল্লাহ ইচ্ছে করলে সকল মানুষকে সৎ পথে পরিচালিত করতে পারতেন। যারা কুফুরী করে তাদের কার্যকলাপের কারণে তাদের উপর কোন না কোন বিপদ আসতেই থাকে কিংবা তাদের ঘরের আশেপাশেই নাযিল হতে থাকে, যতক্ষণ না আল্লাহর ও‘য়াদা পূর্ণ হয়। নিশ্চয়ই আল্লাহ ওয়াদার ব্যতিক্রম করেন না।  ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1739,13,32,'তোমার আগেও বহু রসূলকে ঠাট্টা-বিদ্রূপ করা হয়েছিল, তখন আমি কাফিরদেরকে (ইচ্ছেমত কাজ চালিয়ে যাওয়ার) অবকাশ দিয়েছিলাম, অবশেষে তাদেরকে পাকড়াও করেছিলাম। কেমন (ভয়াবহ) ছিল আমার শাস্তি! ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1740,13,33,'প্রত্যেক প্রাণীর উপার্জনের প্রতি যিনি দৃষ্টি রাখেন (তিনি কি তাদের অক্ষম ইলাহদের মত?) অথচ তারা আল্লাহর শরীক নির্দিষ্ট করে রেখেছে। বল, ‘তাদের নাম বল (যাদেরকে তোমরা আল্লাহর অংশীদার মনে কর), তোমরা কি পৃথিবীর এমন কোন সংবাদ তাঁকে দিতে চাও যা তিনি জানেন না। নাকি এগুলো কেবল কথার প্রদর্শনী? প্রকৃত ব্যাপার হল, কাফিরদের নিকট তাদের কলা কৌশলকে আকর্ষণীয় করে দেয়া হয়েছে আর তাদেরকে সৎ পথ থেকে বিরত রাখা হয়েছে। আল্লাহ যাকে গুমরাহ করেন তাঁকে পথ দেখানোর কেউ নেই।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1741,13,34,'দুনিয়ার জীবনে তাদের জন্য আছে শাস্তি, আর আখেরাতের শাস্তি অবশ্যই আরো বেশি কঠিন। আল্লাহর শাস্তি থেকে বাঁচানোর তাদের কেউ নেই। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1742,13,35,'মুত্তাকীদেরকে যে জান্নাতের ওয়া‘দা দেয়া হয়েছে তার দৃষ্টান্ত এই যে, তার তলদেশ দিয়ে ঝর্ণাধারা প্রবাহিত, তারা ফলফলাদি চিরস্থায়ী আর তার ছায়াও। যারা তাক্ওয়া অবলম্বন করে তাদের পরিণাম হবে এই। আর কাফিরদের পরিণতি হবে জাহান্নামের আগুন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1743,13,36,'আমি যাদেরকে কিতাব দিয়েছিলাম তারা তোমার প্রতি যা অবতীর্ণ হয়েছে তাতে উৎফুল্ল, কিন্তু কতক দল তার কোন কোন কথা মানে না। বল, ‘আমি আল্লাহর ‘ইবাদাত করার জন্য এবং তাঁর সাথে কোন কিছু শারীক না করার জন্য নির্দেশপ্রাপ্ত হয়েছি, আমি তাঁর দিকেই আহবান জানাই, আর আমার প্রত্যাবর্তন তাঁর দিকেই। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1744,13,37,'এভাবে আমি একে বিধানরূপে নাযিল করেছি আরবী ভাষায়। তোমার কাছে জ্ঞান আসার পরেও তুমি যদি তাদের খাহেশের অনুসরণ কর, তবে আল্লাহর মোকাবালায় তোমার কোন অভিভাবক থাকবে না, থাকবে না কোন রক্ষাকারী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1745,13,38,'আমি তোমার পূর্বেও রসূলগণকে পাঠিয়েছিলাম, আর তাদেরকে দিয়েছিলাম স্ত্রী ও সন্তানাদি, আল্লাহর হুকুম ব্যতীত নির্দশন হাযির করার শক্তি কোন রসূলের নেই। যাবতীয় বিষয়ের নির্দিষ্ট সময় লিপিবদ্ধ আছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1746,13,39,'আল্লাহ যা ইচ্ছে করেন নিশ্চিহ্ন করে দেন আর যা ইচ্ছে প্রতিষ্ঠিত রাখেন, উম্মুল কিতাব তাঁর নিকটই রক্ষিত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1747,13,40,'আমি তাদেরকে যে শাস্তি দেয়ার ওয়া‘দা করেছি তার কিছু যদি তোমাকে দেখাই কিংবা (দেখানোর পূর্বেই) তোমার মৃত্যু ঘটাই, (উভয় অবস্থাতেই) তোমার দায়িত্ব হল প্রচার করে দেয়া, আর হিসেব নেয়ার কাজ হল আমার। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1748,13,41,'তারা কি দেখে না আমি তাদের জন্য যমীনকে চার দিক থেকে সংকীর্ণ করে আনছি? আল্লাহ হুকুম দেন, তাঁর হুকুম পেছনে ঠেলে দেবে এমন কেউ নেই। হিসেব গ্রহণের ব্যাপারে তিনি খুবই দ্রুতগতি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1749,13,42,'ওদের আগে যারা ছিল তারাও চক্রান্ত করেছিল, যাবতীয় চক্রান্ত আল্লাহর আয়ত্তাধীন। তিনি জানেন প্রত্যেক ব্যক্তি কী কামাই করছে। আর কাফিরগণ অচিরেই জানতে পারবে ভাল পরিণাম কাদের জন্য। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1750,13,43,'কাফিররা বলে, ‘তুমি আল্লাহর প্রেরিত নও।’ বল, ‘আমার ও তোমাদের মাঝে সাক্ষী হিসেবে আল্লাহই যথেষ্ট এবং যাদের কিতাবের জ্ঞান আছে তারাও।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1751,14,1,'আলিফ-লাম-র, একটা কিতাব যা তোমার প্রতি অবতীর্ণ করেছি যাতে তুমি মানুষকে তাদের প্রতিপালকের নির্দেশে অন্ধকার থেকে নিয়ে আসতে পার আলোর দিকে- মহাপরাক্রমশালী প্রশংসিতের পথে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1752,14,2,'আল্লাহ- আসমানসমূহে যা কিছু আছে আর পৃথিবীতে যা কিছু আছে, সবই তাঁর মালিকানাধীন। কিন্তু কাফিরদের জন্য আছে কঠিন শাস্তির দুর্ভোগ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1753,14,3,'যারা আখেরাতের তুলনায় দুনিয়ার জিন্দিগীকে শ্রেয় জ্ঞান করে, যারা আল্লাহর পথ থেকে (লোকদেরকে) বিরত রাখে আর তাতে বক্রতা আনার আকাঙ্ক্ষা পোষণ করে। এরা গোমরাহীতে বহু দূরে চলে গেছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1754,14,4,'আমি কোন রসূলকেই তার জাতির ভাষা ছাড়া পাঠাইনি যাতে তাদের কাছে স্পষ্টভাবে (আমার নির্দেশগুলো) বর্ণনা করতে পারে। অতঃপর আল্লাহ যাকে ইচ্ছে পথহারা করেছেন, আর যাকে ইচ্ছে সঠিক পথ দেখিয়েছেন, তিনি বড়ই পরাক্রান্ত, বিজ্ঞানময়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1755,14,5,'আর অবশ্যই আমি মূসাকে আমার নিদর্শনসমূহ দিয়ে পাঠিয়েছিলাম আর বলেছিলাম, তোমার জাতিকে অন্ধকার থেকে আলোতে বের করে আন, আর তাদেরকে আল্লাহর হুকুমে ঘটিত অতীতের ঘটনাবলী দিয়ে উপদেশ দাও। এতে প্রত্যেক পরম সহিষ্ণু ও পরম কৃতজ্ঞ ব্যক্তির জন্য অবশ্যই নিদর্শনসমূহ রয়েছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1756,14,6,'স্মরণ কর, যখন মূসা তার সম্প্রদায়কে বলেছিল, ‘তোমরা তোমাদের প্রতি আল্লাহর নি‘মাতের কথা স্মরণ কর যখন তিনি তোমাদেরকে ফির‘আওনী গোষ্ঠী থেকে রক্ষা করেছিলেন যারা তোমাদেরকে জঘন্য রকমের শাস্তিতে পিষ্ট করছিল। তোমাদের পুত্রদেরকে তারা হত্যা করত আর তোমাদের নারীদেরকে জীবিত রাখত। এটা ছিল তোমাদের জন্য আল্লাহর পক্ষ হতে এক কঠিন পরীক্ষা। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1757,14,7,'স্মরণ কর, যখন তোমাদের প্রতিপালক ঘোষণা করেন, যদি তোমরা কৃতজ্ঞতা প্রকাশ কর তাহলে আমি অবশ্যই তোমাদের জন্য (আমার নি‘য়ামাত) বৃদ্ধি করে দেব, আর যদি তোমরা অকৃতজ্ঞ হও (তবে জেনে রেখ, অকৃতজ্ঞদের জন্য) আমার শাস্তি অবশ্যই কঠিন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1758,14,8,'মূসা বলেছিল, তোমরা আর দুনিয়ার সকল লোক যদি অকৃতজ্ঞ হও (তাতে কিছুই যায় আসে না) কারণ আল্লাহ অমুখাপেক্ষী, প্রশংসিত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1759,14,9,'তোমাদের পূর্বেকার লোকেদের খবর কি তোমাদের কাছে পৌঁছেনি? নূহ, ‘আদ আর সামূদ সম্প্রদায়ের, আর তাদের পরবর্তীদের; তাদের সম্পর্কে আল্লাহ ছাড়া কেউ জানে না। রসূলগণ তাদের কাছে স্পষ্ট নিদর্শনাসমূহ নিয়ে এসেছিল, তখন তারা নিজেদের মুখে হাত চেপে ধরল আর বলল, ‘যে জিনিস দিয়ে তোমাদেরকে পাঠানো হয়েছে তা আমরা অস্বীকার করি আর যে বিষয়ের প্রতি তোমরা আমাদেরকে আহবান জানাচ্ছ সে সম্পর্কে আমরা বিভ্রান্তিকর সন্দেহের মধ্যে রয়েছি।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1760,14,10,'তাদের রসূলগণ বলেছিল, ‘আল্লাহ সম্পর্কে সন্দেহ? যিনি আসমানসমূহ ও যমীনের সৃষ্টিকর্তা, তিনি তোমাদেরকে ডাকছেন তোমাদের অপরাধ মার্জনা করার জন্য আর একটা নির্দিষ্ট সময় পর্যন্ত তোমাদেরকে অবকাশ দেয়ার জন্য।’ তারা বলল, ‘তুমি আমাদেরই মত মানুষ বৈ তো নও, আমাদের পূর্বপুরুষরা যার ‘ইবাদাত করত তাত্থেকে আমাদেরকে তুমি বাধা দিতে চাও, তাহলে তুমি (তোমার দাবীর স্বপক্ষে) আমাদের কাছে সুস্পষ্ট প্রমাণ উপস্থিত কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1761,14,11,'তাদের রসূলগণ তাদেরকে বলেছিল, ‘যদিও আমরা তোমাদের মতই মানুষ ব্যতীত নই, কিন্তু আল্লাহ তাঁর বান্দাহদের মধ্যে যার উপর ইচ্ছে অনুগ্রহ করেন। আল্লাহর হুকুম ছাড়া তোমাদের কাছে কোন প্রমাণ উপস্থিত করা আমাদের কাজ নয়। মু’মিনদের উচিত আল্লাহরই উপর ভরসা করা। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1762,14,12,'আমরা আল্লাহর উপর ভরসা করব না কেন, তিনিই তো আমাদেরকে পথ দেখিয়েছেন, তোমরা আমাদেরকে যে ক্লেশই দাওনা কেন, আমরা তাতে অবশ্য অবশ্যই ধৈর্য ধারণ করব, আর ভরসাকারীদের আল্লাহরই উপর ভরসা করা উচিত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1763,14,13,'কাফিরগণ তাদের রসূলদের বলেছিল, ‘আমরা তোমাদেরকে আমাদের দেশ থেকে অবশ্য অবশ্যই বের করে দেব, অন্যথায় তোমাদেরকে অবশ্য অবশ্যই আমাদের ধর্মমতে ফিরে আসতে হবে।’ এমতাবস্থায় রসূলদের প্রতি তাদের প্রতিপালক এ মর্মে ওয়াহী করলেন যে, ‘আমি যালিমদেরকে অবশ্য অবশ্যই ধ্বংস করব। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1764,14,14,'আর তাদের পরে তোমাদেরকে অবশ্য অবশ্যই যমীনে পুনর্বাসিত করব। এ (শুভ) সংবাদ তাদের জন্য যারা আমার সামনে এসে দাঁড়ানোর ব্যাপারে ভয় রাখে আর আমার শাস্তির ভয় দেখানোতে শংকিত হয়।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1765,14,15,'তারা (অর্থাৎ কাফিররা) চূড়ান্ত বিজয়ের ফায়সালা কামনা করেছিল, কিন্তু (আল্লাহ ও তাঁর রসূলদের বিরোধিতা করার কারণে) প্রত্যেক উদ্ধত সীমালঙ্ঘনকারী ব্যর্থ হয়ে গেল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1766,14,16,'এদের জন্য পরবর্তীতে আছে জাহান্নাম, আর এদেরকে পান করানো হবে গলিত পুঁজ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1767,14,17,'সে তা খুব কষ্ট করে গিলতে চেষ্টা করবে, আর খুব কমই গিলতে পারবে। মৃত্যু যন্ত্রণা তার কাছে চতুর্দিক থেকে আসবে কিন্তু সে মরবে না, এরপর তার জন্য থাকবে এক কঠিন ‘আযাব। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1768,14,18,'যারা তাদের প্রতিপালককে অস্বীকার করে তাদের ‘আমালের দৃষ্টান্ত হল সেই ছাইয়ের মত যা ঝড়ের দিনে বাতাস প্রচন্ড বেগে উড়িয়ে নিয়ে যায়। নিজেদের উপার্জনের কিছুই তারা কাজে লাগাতে পারে না। এটাই ঘোর গুমরাহী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1769,14,19,'তুমি কি দেখ না যে, আল্লাহ যথাযথ নিয়ম বিধানসহ আসমানসমূহ ও যমীন সৃষ্টি করেছেন, তিনি চাইলে তোমাদেরকে সরিয়ে দিবেন আর এক নতুন সৃষ্টি নিয়ে আসবেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1770,14,20,'এটা আল্লাহর জন্য কঠিন কিছু নয়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1771,14,21,'তারা সকলে আল্লাহর নিকট উপস্থিত হবে। তখন যারা অহঙ্কার করেছিল তাদেরকে দুর্বলরা বলবে, আমরা তো তোমাদেরই অনুসারী ছিলাম, কাজেই এখন আল্লাহর শাস্তির কোন কিছু আমাদের থেকে তোমরা দূর করতে পার কি?’ তারা বলবে, ‘আল্লাহ আমাদেরকে সত্যপথে পরিচালিত করলে আমরাও অবশ্যই তোমাদেরকে সত্য পথ দেখাতাম। এখন আমরা ধৈর্যহারা হই কিংবা ধৈর্যধারণ করি দু’টোই আমাদের জন্য সমান, আমাদের কোন নিষ্কৃতি নেই।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1772,14,22,'বিচার-ফায়সালা সম্পন্ন হলে শয়ত্বান বলবে, ‘আল্লাহ তোমাদের জন্য যে ওয়া‘দা করেছিলেন তা ছিল সত্য ওয়া‘দা। আর আমিও তোমাদেরকে ওয়া‘দা দিয়েছিলাম, কিন্তু আমি তার খেলাপ করেছি, তোমাদের উপর আমার কোনই প্রভাব ছিল না, আমি কেবল তোমাদেরকে আহবান জানিয়েছিলাম আর তোমরা আমার আহবানে সাড়া দিয়েছিলে। কাজেই তোমরা আমাকে তিরস্কার করো না, বরং নিজেদেরকেই তিরস্কার কর, এখানে না আমি তোমাদের ফরিয়াদ শুনতে পারি, না তোমরা আমার ফরিয়াদ শুনতে পার। ইতোপূর্বে তোমরা যে আমাকে (আল্লাহর) শরীক করেছিলে আমি তা অস্বীকার করছি। যালিমদের জন্য আছে ভয়াবহ শাস্তি।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1773,14,23,'যারা ঈমান আনে আর সৎ কাজ করে তাদেরকে জান্নাতে দাখিল করা হবে যার তলদেশে ঝর্ণাধারা প্রবাহিত। সেখানে তারা তাদের প্রতিপালকের অনুমতিক্রমে চিরকাল থাকবে। সেখানে তাদেরকে শান্তির বার্তা দিয়ে সংবর্ধনা জ্ঞাপন করা হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1774,14,24,'তুমি কি দেখ না কীভাবে আল্লাহ দৃষ্টান্ত উপস্থাপন করেন? উৎকৃষ্ট বাক্যের তুলনা উৎকৃষ্ট গাছের ন্যায় যার মূল সুদৃঢ়ভাবে স্থাপিত আর শাখা-প্রশাখা আকাশপানে বিস্তৃত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1775,14,25,'তার প্রতিপালকের হুকুমে তা সব সময় ফল দান করে। মানুষদের জন্য আল্লাহ দৃষ্টান্ত বর্ণনা করেন যাতে তারা শিক্ষা গ্রহণ করে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1776,14,26,'মন্দ বাক্য মন্দ বৃক্ষের সঙ্গে তুলনীয়, ভূপৃষ্ঠের উপরিভাগেই যাকে মূল থেকে উপড়ে ফেলা হয়েছে, যার কোন স্থায়িত্ব নেই। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1777,14,27,'যারা ঈমান এনেছে তাদেরকে আল্লাহ সুপ্রতিষ্ঠিত বাণীর অবলম্বনে দুনিয়ার জীবনে ও আখেরাতে প্রতিষ্ঠিত রাখবেন আর যালিমদেরকে আল্লাহ ধ্বংস করে দেবেন। তিনি যা ইচ্ছে করেন তাই করেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1778,14,28,'তুমি কি তাদের ব্যাপারে চিন্তা কর না যারা আল্লাহর অনুগ্রহের বিনিময়ে অকৃতজ্ঞতার নীতি অবলম্বন করে আর তাদের জাতিকে ধ্বংসের ঘরে নামিয়ে আনে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1779,14,29,'(তা হল) জাহান্নাম, তাতে তারা প্রবেশ করবে, বসবাসের এ জায়গা কতই না নিকৃষ্ট! ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1780,14,30,'আর তারা (অন্যকে) আল্লাহর সমকক্ষ স্থির করে তাঁর পথ থেকে বিপথগামী করার উদ্দেশে। বল, ‘ভোগ করে নাও, শেষ পর্যন্ত জাহান্নামেই তোমাদেরকে ফিরে যেতে হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1781,14,31,'আমার বান্দাদের মধ্যে যারা ঈমান এনেছে তাদেরকে বল নামায প্রতিষ্ঠা করতে আর যে জীবিকা আমি তাদেরকে দিয়েছি তাত্থেকে গোপনে ও প্রকাশ্যে ব্যয় করতে- সেদিন আসার পূর্বে যেদিন না চলবে কোন কেনা-বেচা আর না কোন বন্ধুত্ব। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1782,14,32,'তিনিই আল্লাহ যিনি আসমানসমূহ ও যমীন সৃষ্টি করেছেন, তিনি আকাশ হতে পানি বর্ষণ করেন যা দিয়ে নানা প্রকার ফলফলাদি জন্মে তোমাদের জীবিকার জন্য। তিনি নৌযানগুলোকে তোমাদের নিয়ন্ত্রণাধীন করে দিয়েছেন, যাতে সেগুলো তাঁর নির্দেশে সমুদ্রে চলাচল করে আর তিনি নদীগুলোকে তোমাদের অধীন করে দিয়েছেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1783,14,33,'তিনি সূর্য ও চন্দ্রকে তোমাদের কাজে লাগিয়ে রেখেছেন, তারা অনুগত হয়ে নিজ পথে চলছে। আর তিনি রাত ও দিনকে তোমাদের কাজে লাগিয়ে রেখেছেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1784,14,34,'তিনি তোমাদেরকে সে সব কিছুই দিয়েছেন যা তোমরা চেয়েছ (তোমরা তোমাদের প্রয়োজনীয় সব কিছুই পেয়েছ) আর তোমরা আল্লাহর অনুগ্রহ গণনা করতে চাইলে কক্ষনো তার সংখ্যা নির্ধারণ করতে পারবে না। মানুষ অবশ্যই বড়ই যালিম, বড়ই অকৃতজ্ঞ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1785,14,35,'স্মরণ কর, ইবরাহীম যখন বলেছিল, ‘হে আমার রব্ব! তুমি এ নগরীকে নিরাপদ কর আর আমাকে আর আমার সন্তানদেরকে প্রতিমা পূজা থেকে রক্ষে কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1786,14,36,'হে আমার প্রতিপালক! এ (প্রতিমা)-গুলো বহু সংখ্যক মানুষকে পথভ্রষ্ট করেছে। কাজেই (প্রতিমাগুলোকে বাদ দিয়ে) যারা আমাকে অনুসরণ করবে তারা আমার দলভুক্ত। আর যে আমার অবাধ্য হবে সেক্ষেত্রে তুমি তো বড়ই ক্ষমাশীল, বড়ই দয়ালু। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1787,14,37,'হে আমাদের প্রতিপালক! আমি আমার সন্তানদের একাংশকে শস্যক্ষেতহীন উপত্যকায় তোমার সম্মানিত ঘরের নিকট পুনর্বাসিত করলাম। হে আমার প্রতিপালক! তারা যাতে নামায কায়িম করে। কাজেই তুমি মানুষের অন্তরকে তাদের প্রতি অনুরাগী করে দাও আর ফল-ফলাদি দিয়ে তাদের জীবিকার ব্যবস্থা কর যাতে তারা শুকরিয়া আদায় করে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1788,14,38,'হে আমাদের প্রতিপালক! তুমি তো জান যা আমরা গোপন করি আর যা প্রকাশ করি, আসমান ও যমীনের কোন বস্তুই আল্লাহ হতে গোপন নেই। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1789,14,39,'যাবতীয় প্রশংসা আল্লাহর যিনি আমার বার্ধক্য অবস্থায় আমাকে ইসমাঈল ও ইসহাককে দান করেছেন, আমার প্রতিপালক অবশ্যই আহবান শ্রবণকারী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1790,14,40,'হে আমার প্রতিপালক! আমাকে নামায প্রতিষ্ঠাকারী বানাও আর আমার সন্তানদেরকেও, হে আমাদের প্রতিপালক! তুমি আমার প্রার্থনা কবূল কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1791,14,41,'হে আমাদের প্রতিপালক! হিসাব গ্রহণের দিন আমাকে, আমার পিতা-মাতাকে আর মু’মিনদেরকে ক্ষমা করে দিও, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1792,14,42,'যালিমরা যা করছে সে ব্যাপারে তোমরা আল্লাহকে কক্ষনো উদাসীন মনে কর না। তিনি তাদেরকে সেদিন পর্যন্ত ঢিল দিচ্ছেন যেদিন ভয়ে আতঙ্কে চক্ষু স্থির হয়ে যাবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1793,14,43,'আতঙ্কিত হয়ে মাথা তুলে পালাতে থাকবে, দৃষ্টি তাদের নিজেদের পানে ফিরে আসবে না, আর তাদের দিল উড়ে যাবে।  ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1794,14,44,'কাজেই মানুষকে সতর্ক কর সেদিনের ব্যাপারে যেদিন তাদের উপর ‘আযাব আসবে। যারা যুলম করেছিল তারা তখন বলবে, ‘হে আমাদের প্রতিপালক! আমাদেরকে অল্পদিনের জন্য সময় দাও, আমরা তোমার আহবানে সাড়া দিব আর রসূলদের কথা মেনে চলব।’ (তখন তাদেরকে বলা হবে) তোমরা কি পূর্বে শপথ করে বলনি যে, তোমাদের কক্ষনো পতন ঘটবে না? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1795,14,45,'অথচ তোমরা সেই লোকগুলোর বাসভূমিতে বসবাস করছিলে যারা নিজেদের প্রতি যুলম করেছিল আর তোমাদেরকে স্পষ্ট করে দেখিয়ে দেয়া হয়েছিল আমি তাদের সঙ্গে কেমন ব্যবহার করেছিলাম। আর আমি বহু উদাহরণ টেনে তোমাদেরকে বুঝিয়েও দিয়েছিলাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1796,14,46,'তারা যে চক্রান্ত করেছিল তা ছিল সত্যিই ভয়ানক, কিন্তু তাদের চক্রান্ত আল্লাহর দৃষ্টির ভিতরেই ছিল, যদিও তাদের চক্রান্তগুলো এমন ছিল না যে, তাতে পর্বতও টলে যেত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1797,14,47,'(অবস্থা যতই প্রতিকূল হোক না কেন) তুমি কক্ষনো মনে কর না যে, আল্লাহ তাঁর রসূলগণকে দেয়া ওয়া‘দা খেলাপ করবেন, আল্লাহ মহা প্রতাপশালী, প্রবল প্রতিশোধ গ্রহণকারী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1798,14,48,'যেদিন এ পৃথিবী বদলে গিয়ে অন্য এক পৃথিবীতে রূপান্তরিত হবে আর আসমানসমূহও (বদলে যাবে), আর মানুষ সমুস্থাপিত হবে এক ও অপ্রতিরোধ্য আল্লাহর সম্মুখে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1799,14,49,'সেদিন তুমি অপরাধীদেরকে দেখবে শৃঙ্খলে তাদের হাত পা শক্ত করে বাঁধা। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1800,14,50,'তাদের পোশাক হবে আলকাতরার আর আগুন তাদের মুখমন্ডল আচ্ছন্ন করবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1801,14,51,'(এটা করা হবে এজন্য) যাতে আল্লাহ প্রত্যেক ব্যক্তিকে তার কৃতকর্মের প্রতিফল দিতে পারেন। আল্লাহ তো হিসাব গ্রহণে খুবই দ্রুতগতি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1802,14,52,'এটা মানুষদের জন্য একটা বার্তা যার দ্বারা তাদেরকে সতর্ক করা হচ্ছে আর যাতে তারা জানতে পারে যে, তিনি এক ইলাহ আর যাতে বুদ্ধিমান মানুষেরা উপদেশ লাভ করে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1803,15,1,'আলিফ-লাম-র, এগুলো কিতাবের এবং সুস্পষ্ট কুরআনে আয়াতসমূহ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1804,15,2,'এমন একটা সময় আসবে যখন কাফিরগণ আক্ষেপ করে বলবে, ‘হায়, আমরা যদি মুসলিম হয়ে যেতাম!’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1805,15,3,'ছেড়ে দাও ওদেরকে, ওরা খেতে থাক আর ভোগ করতে থাক, আর (মিথ্যে) আশা ওদেরকে উদাসীনতায় ডুবিয়ে রাখুক, শীঘ্রই ওরা (ওদের ‘আমালের পরিণতি) জানতে পারবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1806,15,4,'আমি যে জনপদকেই ধ্বংস করেছি তাদের জন্য ছিল লিখিত একটা নির্দিষ্ট সময়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1807,15,5,'কোন জাতিই তাদের নির্দিষ্ট কালকে অগ্র-পশ্চাৎ করতে পারে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1808,15,6,'তারা বলে, ‘ওহে ঐ ব্যক্তি যার প্রতি কুরআন অবতীর্ণ হয়েছে! তুমি তো অবশ্যই পাগল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1809,15,7,'তুমি সত্যবাদী হলে আমাদের নিকট ফেরেশতাদের হাজির করছ না কেন?’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1810,15,8,'যথাযথ কারণ ছাড়া আমি ফেরেশতা পাঠাই না, পাঠালে কাফিরদেরকে আর কোন অবকাশ দেয়া হবে না।  ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1811,15,9,'নিশ্চয় আমিই কুরআন নাযিল করেছি আর অবশ্যই আমি তার সংরক্ষক। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1812,15,10,'তোমার পূর্ববর্তী জাতিগুলোর কাছেও আমি রসূল পাঠিয়েছিলাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1813,15,11,'তাদের কাছে এমন কোন রসূল আসেনি যাকে তারা ঠাট্টা-বিদ্রূপ করেনি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1814,15,12,'এভাবে আমি এ রকম আচরণ পাপীদের অন্তরে বদ্ধমূল করে দেই। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1815,15,13,'তারা এর প্রতি ঈমান আনবে না, পূর্ববর্তী লোকেদেরও এ নিয়ম-নীতি চলে এসেছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1816,15,14,'যদি তাদের জন্য আকাশের দরজা খুলে দেয়া হত, আর তারা তাতে উঠতে থাকত, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1817,15,15,'তারা অবশ্যই বলত, ‘আমাদের চোখকে বাঁধিয়ে দেয়া হয়েছে, বরং আমাদের উপর যাদু করা হয়েছে।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1818,15,16,'আমি আকাশে গ্রহ-নক্ষত্র সৃষ্টি করেছি আর দর্শকদের জন্য তা সুসজ্জিত করে দিয়েছি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1819,15,17,'আর প্রত্যেক অভিশপ্ত শয়ত্বান থেকে সেগুলোকে সুরক্ষিত করে দিয়েছি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1820,15,18,'কিন্তু কেউ চুরি করে (খবর) শুনতে চাইলে উজ্জ্বল অগ্নিশিখা তার পশ্চাদ্ধাবণ করে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1821,15,19,'আর পৃথিবী, আমি সেটাকে বিছিয়ে দিয়েছি আর তাতে পর্বতরাজি সংস্থাপিত করেছি আর তাতে সকল বস্তু উদগত করেছি যথাযথ পরিমাণে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1822,15,20,'আর তাতে তোমাদের জীবন ধারণের ব্যবস্থা করেছি আর তাদেরও যাদের রিযকদাতা তোমরা নও। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1823,15,21,'এমন কোন জিনিসই নেই যার ভান্ডার আমার কাছে নেই, কিন্তু আমি সেগুলো আমার জ্ঞান মোতাবেক নির্দিষ্ট পরিমাণে সরবরাহ করে থাকি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1824,15,22,'আমি বৃষ্টি-সঞ্চারী বাতাস প্রেরণ করি, অতঃপর আসমান থেকে পানি বর্ষণ করি আর তা তোমাদের পান করাই, তোমরা তার স্টোর কীপার নও। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1825,15,23,'আমিই জীবন দেই আর মৃত্যু ঘটাই আর আমিই চূড়ান্ত উত্তরাধিকারী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1826,15,24,'তোমাদের মধ্যেকার যারা পূর্বে গত হয়ে গেছে আমি তাদেরকে জানি আর পরে যারা আসবে তাদেরকেও জানি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1827,15,25,'অবশ্যই তোমার প্রতিপালক তিনি সববাইকে একত্রিত করবেন, তিনি মহাবিজ্ঞানী, সর্বজ্ঞ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1828,15,26,'আমি কাল শুষ্ক ঠনঠনে মাটির গাড়া থেকে মানুষকে সৃষ্টি করেছি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1829,15,27,'এর পূর্বে আমি জ্বীনকে আগুনের লেলিহান আগুন থেকে সৃষ্টি করেছি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1830,15,28,'স্মরণ কর যখন তোমার প্রতিপালক ফেরেশতাদেরকে বলেছিলেন, ‘আমি কাল শুষ্ক ঠনঠনে মাটির কাদা থেকে মানুষ সৃষ্টি করছি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1831,15,29,'আমি যখন তাকে পূর্ণ মাত্রায় বানিয়ে দেব আর তাতে আমার পক্ষ হতে রূহ ফুঁকে দেব, তখন তোমরা তার প্রতি সাজদায় পড়ে যেও। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1832,15,30,'তখন ফেরেশতারা সবাই সাজদাহ করল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1833,15,31,'ইবলীস বাদে, সে সাজদাহ্কারীদের দলভুক্ত হতে অস্বীকৃতি জানাল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1834,15,32,'আল্লাহ বলবেন, ‘হে ইবলীস! তোমার কী হল যে তুমি সাজদাহকারীদের দলভুক্ত হলে না?’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1835,15,33,'ইবলীস বলল, ‘আমার কাজ নয় মানুষকে সাজদাহ্ করা যাকে তুমি পচা কর্দমের ঠনঠনে গাড়া থেকে সৃষ্টি করেছ।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1836,15,34,'তিনি বললেন, ‘বেরিয়ে যাও এখান থেকে, কারণ তুমি হলে অভিশপ্ত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1837,15,35,'বিচার দিবস পর্যন্ত তোমার উপর থাকল লা‘নত।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1838,15,36,'সে বলল, ‘হে আমার প্রতিপালক! পুনরুত্থান দিবস পর্যন্ত আমাকে সময় দিন।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1839,15,37,'তিনি বললেন, ‘তোমাকে সময় দেয়া হল ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1840,15,38,'সেদিন পর্যন্ত যার নির্দিষ্ট ক্ষণ আমার জানা আছে।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1841,15,39,'সে বলল, ‘হে আমার প্রতিপালক! যেহেতু আপনি আমাকে ভ্রান্তপথে ঠেলে দিলেন, কাজেই আমিও পৃথিবীতে মানুষের কাছে পাপকাজকে অবশ্য অবশ্যই সুশোভিত করে দেখাব আর তাদের সবাইকে অবশ্য অবশ্যই বিভ্রান্ত করব। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1842,15,40,'কিন্তু তাদের মধ্যে আপনার বাছাই করা বান্দাহদের ছাড়া।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1843,15,41,'তিনি বললেন- (আমার বাছাই করা বান্দারা যে পথে চলছে) এটাই আমার কাছে পৌঁছার সরল সোজা পথ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1844,15,42,'আমার প্রকৃত বান্দাহ্দের উপর তোমার কোন আধিপত্য চলবে না, তোমাকে যারা অনুসরণ করে সেই বিভ্রান্তরা ছাড়া। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1845,15,43,'আর তাদের সবার জন্য অবশ্যই ওয়া‘দাকৃত স্থান হচ্ছে জাহান্নাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1846,15,44,'তার সাতটা দরজা আছে। প্রত্যেক দরজার জন্য তাদের মধ্যে শ্রেণী নির্দিষ্ট আছে।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1847,15,45,'অবশ্যই মুত্তাকীরা থাকবে জান্নাতে আর নির্ঝরিণীগুলোর মধ্যে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1848,15,46,'তাদেরকে বলা হবে, ‘পূর্ণ শান্তি ও নিরাপত্তার সাথে তোমরা এতে প্রবেশ কর।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1849,15,47,'তাদের অন্তর থেকে আমি বিদ্বেষ দূরীভূত করব, তারা ভ্রাতৃবন্ধনে আবদ্ধ হয়ে আসনে মুখোমুখী সমাসীন হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1850,15,48,'কোন ক্লান্তি তাদেরকে স্পর্শ করবে না, আর সেখান থেকে তারা কখনও বহিষ্কৃতও হবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1851,15,49,'আমার বান্দাহদেরকে সংবাদ দাও যে, আমি বড়ই ক্ষমাশীল, বড়ই দয়ালু। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1852,15,50,'আর আমার শাস্তি- তা বড়ই ভয়াবহ শাস্তি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1853,15,51,'তাদেরকে ইবরাহীমের মেহমানের কাহিনী জানিয়ে দাও। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1854,15,52,'তারা যখন তার কাছে উপস্থিত হল তখন তারা বলল, ‘তোমার প্রতি সালাম।’ তখন সে বলল, ‘তোমাদের দেখে আমরা শংকিত।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1855,15,53,'তারা বলল, ‘শংকা করো না, আমরা তোমাকে এক জ্ঞানী পুত্রের সুখবর দিচ্ছি।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1856,15,54,'সে বলল, ‘বার্ধক্য যখন আমাকে স্পর্শ করেছে তখন তোমরা আমাকে সুখবর দিচ্ছ। আচ্ছা, তোমাদের সুখবরটা কী?’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1857,15,55,'তারা বলল, ‘তোমাকে আমরা প্রকৃতই সুসংবাদ দিচ্ছি। কাজেই তুমি নিরাশদের অন্তর্ভুক্ত হয়ো না।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1858,15,56,'সে বলল, ‘পথভ্রষ্টরা ছাড়া আর কে তার প্রতিপালকের রহমাত থেকে নিরাশ হয়?’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1859,15,57,'সে বলল, ‘হে আল্লাহর প্রেরিতরা! তোমরা কোন্ কাজে আগমন করেছ?’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1860,15,58,'তারা বলল, ‘আমরা এক অপরাধী জাতির বিরুদ্ধে প্রেরিত হয়েছি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1861,15,59,'তবে লূতের পরিবার বাদে, তাদের সবাইকে আমরা অবশ্যই রক্ষা করব। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1862,15,60,'তবে তার স্ত্রীকে নয়, আমরা (আল্লাহর নির্দেশক্রমে) তার জন্য নির্ধারিত করে দিয়েছি যে, সে পেছনে থেকে যাওয়া লোকেদের মধ্যে শামিল থাকবে।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1863,15,61,'আল্লাহর প্রেরিতরা যখন লূত পরিবারের নিকট আসল, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1864,15,62,'সে বলল, ‘আপনাদেরকে তো অপরিচিত লোক মনে হচ্ছে।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1865,15,63,'তারা বলল, ‘আমরা তা-ই নিয়ে এসেছি যে ব্যাপারে এ লোকেরা সন্দেহে পতিত ছিল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1866,15,64,'তোমার কাছে আমরা সত্য নিয়েই এসেছি, আর আমরা অবশ্যই সত্যবাদী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1867,15,65,'কাজেই কিছুটা রাত থাকতে তুমি তোমার পরিবারবর্গকে নিয়ে বেরিয়ে পড় আর তুমি তাদের পেছনে পেছনে চলতে থাক। তোমাদের কেউ যেন পেছনে ফিরে না তাকায় বরং যেখানে যেতে বলা হচ্ছে চলে যাও।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1868,15,66,'আমি লূতকে এ সিদ্ধান্ত জানিয়ে দিলাম যে, সকাল হতে না হতেই সমূলে ধ্বংস করা হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1869,15,67,'শহরের লোকেরা আনন্দ সহকারে (লূতের ঘরে) উপস্থিত হল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1870,15,68,'লূত বলল, ‘এরা আমার মেহমান, কাজেই তোমরা আমাকে লাঞ্ছিত করো না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1871,15,69,'তোমরা আল্লাহকে ভয় কর, আমাকে লজ্জিত করো না।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1872,15,70,'তারা বলল, দুনিয়াব্যাপী বিষয় নিয়ে কথা বলতে আমরা কি তোমাকে নিষেধ করিনি?’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1873,15,71,'(লূত (আ.)) বলল, ‘তোমরা যদি কিছু করতেই চাও তাহলে এই আমার (জাতির) কন্যারা আছে।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1874,15,72,'তোমার জীবনের শপথ হে নাবী! তারা উন্মত্ত নেশায় আত্মহারা হয়ে পড়েছিল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1875,15,73,'সূর্যোদয়ের সময়ে এক প্রচন্ড ধ্বনি তাদের উপর আঘাত হানল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1876,15,74,'আর আমি সে জনপদকে উল্টে (উপর-নীচ) করে দিলাম আর তাদের উপর পাকানো মাটির প্রস্তর বর্ষণ করলাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1877,15,75,'এতে অবশ্যই অন্তর্দৃষ্টিসম্পন্ন লোকেদের জন্য অনেক নিদর্শন রয়েছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1878,15,76,'এটি মানুষের চলাচল পথের পাশেই বিদ্যমান। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1879,15,77,'মু’মিনদের জন্য এতে বড়ই নিদর্শন রয়েছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1880,15,78,'আর আয়কাহবাসীরাও অবশ্যই যালিম ছিল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1881,15,79,'কাজেই তাদের উপর প্রতিশোধ নিয়েছিলাম, এ দু’টো জনপদই প্রকাশ্য পথের উপর অবস্থিত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1882,15,80,'হিজর-এর লোকেরাও রসূলদেরকে অমান্য করেছিল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1883,15,81,'আমি তাদেরকে আমার নিদর্শনাবলী দিয়েছিলাম কিন্তু তাত্থেকে তারা মুখ ফিরিয়েই রেখেছিল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1884,15,82,'তারা পাহাড় খোদাই করতঃ ঘর তৈরি করে নিজেদেরকে নিরাপদ ভাবত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1885,15,83,'অতঃপর এক সকালে প্রচন্ড ধ্বনি তাদের উপর আঘাত হানল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1886,15,84,'তাদের উপার্জন তাদের কোন কাজে আসল না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1887,15,85,'আমি আসমানসমূহ, যমীন আর এ দু’য়ের মাঝে যা কিছু আছে প্রকৃত উদ্দেশ্য ছাড়া সৃষ্টি করিনি। ক্বিয়ামাত অবশ্যই আসবে, কাজেই উত্তম পন্থায় (তাদেরকে) এড়িয়ে যাও। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1888,15,86,'নিশ্চয় তোমার প্রতিপালক তিনি সর্বস্রষ্টা, সর্বজ্ঞ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1889,15,87,'আমি তোমাকে দিয়েছি পুনঃ পুনঃ আবৃত্ত সপ্ত আয়াত আর মহা কুরআন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1890,15,88,'তুমি দুনিয়ার দ্রব্য সামগ্রীর প্রতি চোখ তুলে তাকিও না যা আমি তাদের বিভিন্ন লোকেদের দিয়েছি। (তারা ভুল চিন্তা ও ভুল কর্মের মাধ্যমে নিজেদের ভয়াবহ পরিণাম ডেকে আনছে, এমতাবস্থায়) তাদের জন্য তুমি দুঃখ করো না, আর মু’মিনদের জন্য তোমার (অনুকম্পার) ডানা মেলে দাও। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1891,15,89,'আর বলে দাও, ‘আমি তো স্পষ্ট ভাষায় সতর্ককারী মাত্র।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1892,15,90,'যে ধরনের সতর্কীকরণ পাঠানো হয়েছিল (আল্লাহর কিতাবকে) বিভক্তকারী (ইয়াহূদী ও খ্রীস্টান)দের উপর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1893,15,91,'যারা কুরআনকে (নিজেদের খেয়াল খুশিমত) ভাগ ভাগ করে ফেলেছে (যেটা ইচ্ছে মানছে, যেটা ইচ্ছে অমান্য করছে)। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1894,15,92,'অতএব শপথ তোমার রব্বের! তাদের সববাইকে অবশ্য অবশ্যই আমি জিজ্ঞেস করব ।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1895,15,93,'তারা যা করছে সে সম্পর্কে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1896,15,94,'কাজেই তোমাকে যে বিষয়ের হুকুম দেয়া হয়েছে তা জোরে শোরে প্রকাশ্যে প্রচার কর, আর মুশরিকদের থেকে মুখ ফিরিয়ে নাও। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1897,15,95,'(সেই) ঠাট্টা-বিদ্রূপকারীদের বিরুদ্ধে তোমার জন্য আমিই যথেষ্ট ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1898,15,96,'যারা আল্লাহর সাথে অন্যকেও ইলাহ বানিয়ে নিয়েছে, (কাজেই শিরকের পরিণতি কী) শীঘ্রই তারা জানতে পারবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1899,15,97,'আমি জানি, তারা যে সব কথা-বার্তা বলে তাতে তোমার মন সংকুচিত হয়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1900,15,98,'কাজেই প্রশংসা সহকারে তুমি তোমার প্রতিপালকের পবিত্রতা ঘোষণা কর, আর সাজদাহকারীদের দলভুক্ত হও। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1901,15,99,'আর তোমার রব্বের ‘ইবাদাত করতে থাক তোমার সুনিশ্চিত ক্ষণের (অর্থাৎ মৃত্যুর) আগমন পর্যন্ত।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1902,16,1,'আল্লাহর নির্দেশ এসে গেছে, অতএব এর জন্য তাড়াহুড়ো করো না। তিনি মহান পবিত্র, তারা যাকে শরীক সাব্যস্ত করে তাত্থেকে তিনি বহু ঊর্ধ্বে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1903,16,2,'তিনি তাঁর এ রূহকে (ওহীক) যে বান্দাহর উপর চান স্বীয় নির্দেশক্রমে ফেরেশতাদের মাধ্যমে অবতীর্ণ করেন (এই মর্মে যে) তোমরা সতর্ক কর যে, আমি ছাড়া সত্যিকারের কোন ইলাহ নেই, কাজেই আমাকে ভয় কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1904,16,3,'তিনি (বিশেষ উদ্দেশ্য সাধনের নিমিত্তে) প্রকৃতভাবেই আসমানসমূহ যমীন সৃষ্টি করেছেন, তারা যাকে আল্লাহর অংশীদার গণ্য করে তাত্থেকে তিনি বহু ঊর্ধ্বে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1905,16,4,'তিনি শুক্র-কীট থেকে মানুষ সৃষ্টি করেছেন অথচ সে প্রকাশ্য ঝগড়াটে সেজে বসল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1906,16,5,'তিনি চতুস্পদ জন্তু সৃষ্টি করেছেন এবং তাতে তোমাদের জন্য আছে (শীত) নিবারক আর বহু উপকারিতাও, আর সেগুলো থেকে তোমরা আহার কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1907,16,6,'তোমরা গর্বভরে সৌন্দর্য অনুভব কর যখন তোমরা সন্ধ্যাবেলা সেগুলোকে বাড়ীর পানে হাঁকিয়ে আন আর সকাল বেলা বিচরণের জন্য পাঠাও। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1908,16,7,'আর এগুলো তোমাদের ভার বোঝা বহন ক’রে এমন স্থান পর্যন্ত নিয়ে যায়, প্রাণান্তকর ক্লেশ ছাড়া যেখানে তোমরা পৌঁছতে পারতে না, তোমাদের প্রতিপালক অবশ্যই বড়ই দয়ার্দ্র, বড়ই দয়ালু। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1909,16,8,'তিনি ঘোড়া, খচ্চর ও গর্দভ সৃষ্টি করেছেন যাতে তোমরা ওগুলোতে আরোহণ করতে পার আর শোভা-সৌন্দর্যের জন্যও; তিনি পয়দা করেন অনেক কিছু যা তোমাদের জানা নেই। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1910,16,9,'আল্লাহরই দায়িত্বে রয়েছে সরল পথপ্রদর্শন। পথগুলোর মধ্যে বাঁকা পথও আছে। তিনি যদি ইচ্ছে করতেন তাহলে অবশ্যই তোমাদের সকলকেই সঠিক পথ প্রদর্শন করতেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1911,16,10,'তিনি আকাশ থেকে পানি বর্ষণ করেন যাতে আছে তোমাদের জন্য পানীয় আর তাতে জন্মে বৃক্ষ লতা যা তোমাদের পশুগুলোকে খাওয়াও। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1912,16,11,'তিনি তা দিয়ে তোমাদের জন্য জন্মান শস্য, যায়তূন, খেজুর, আঙ্গুর এবং সর্বপ্রকার ফল। এতে চিন্তাশীল মানুষদের জন্য নিদর্শন রয়েছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1913,16,12,'তিনিই রাত ও দিনকে তোমাদের উপকারে নিয়োজিত করেছেন। আর সুরুজ ও চাঁদকেও; এবং তারকারাজিও তাঁরই নির্দেশে নিয়ন্ত্রিত; বিবেকসম্পন্ন লোকেদের জন্য এতে অবশ্যই বহু নিদর্শন রয়েছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1914,16,13,'আর তিনি তোমাদের জন্য যমীনে বিভিন্ন রং-এর বস্তুরাজি সৃষ্টি করেছেন। এতে ঐ সমস্ত লোকেদের জন্য নিশ্চিতভাবে নিদর্শন আছে যারা উপদেশ গ্রহণ করতে চায়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1915,16,14,'তিনিই সমুদ্রকে কল্যাণে নিয়োজিত রেখেছেন যাতে তোমরা তাত্থেকে তাজা গোশত খেতে পার, আর তাত্থেকে তোমরা রত্নরাজি সংগ্রহ করতে পার যা তোমরা অলংকার হিসেবে পরিধান কর। আর নৌযানগুলোকে তোমরা দেখতে পাও ঢেউয়ের বুক চিরে তাতে চলাচল করে, যাতে তোমরা তাঁর অনুগ্রহ তালাশ করতে পার আর শোকর আদায় করতে পার। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1916,16,15,'তিনি যমীনে সুদৃঢ় পর্বত সংস্থাপিত করেছেন যাতে যমীন তোমাদেরকে নিয়ে আন্দোলিত না হয়, আর সংস্থাপিত করেছেন নদী নির্ঝরিণী আর পথ যাতে তোমরা পথের সন্ধান পেতে পার। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1917,16,16,'আর দিক-দিশা প্রদানকারী চিহ্নসমূহ; আর তারকারাজির সাহায্যেও তারা পথনির্দেশ লাভ করে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1918,16,17,'যিনি সৃষ্টি করেন তিনি কি তার মত যে সৃষ্টি করে না? তবে কি তোমরা শিক্ষা গ্রহণ করবে না? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1919,16,18,'তোমরা আল্লাহর নি‘মাতসমূহকে গণনা করলে তার সংখ্যা নির্ণয় করতে পারবে না; আল্লাহ অবশ্যই বড়ই ক্ষমাশীল, বড়ই দয়ালু। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1920,16,19,'আল্লাহ জানেন তোমরা যা গোপন কর আর যা তোমরা প্রকাশ কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1921,16,20,'তারা আল্লাহ ছাড়া অন্য যাদেরকে ডাকে তারা কিছুই সৃষ্টি করে না, তারা (নিজেরাই) সৃষ্ট। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1922,16,21,'তারা প্রাণহীন, জীবিত নয়, তাদের কোনই চেতনা নেই কবে তাদেরকে (পুনর্জীবিত করে) উঠানো হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1923,16,22,'তোমাদের ইলাহ হলেন এক ইলাহ। কাজেই যারা আখেরাতে বিশ্বাস করে না, তাদের অন্তর সত্য-অস্বীকারকারী আর তারা অহংকারী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1924,16,23,'এতে কোন সন্দেহ নেই যে, আল্লাহ জানেন যা তারা গোপন করে আর যা প্রকাশ করে, তিনি অহঙ্কারীদেরকে ভালবাসেন না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1925,16,24,'তাদেরকে যখন বলা হয়, ‘তোমাদের প্রতিপালক কী নাযিল করেছেন’ তখন তারা বলে- ‘পূর্ববর্তীদের কল্প-কাহিনী’। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1926,16,25,'যার ফলে ক্বিয়ামাত দিবসে তারা বহন করবে নিজেদের পাপের বোঝা পূর্ণ মাত্রায়, আর (আংশিক) তাদেরও পাপের বোঝা যাদেরকে তারা গুমরাহ করেছে নিজেদের অজ্ঞতার কারণে। হায়, তারা যা বহন করবে তা কতই না নিকৃষ্ট! ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1927,16,26,'তাদের পূর্বে যারা ছিল তারাও চক্রান্ত করেছিল। ফলে আল্লাহ তাদের ইমারাতকে মূল থেকে উৎপাটিত করেছিলেন আর উপর থেকে ছাদ তাদের উপর ভেঙ্গে পড়ল, আর তাদের প্রতি শাস্তি পতিত হল এমন দিক হতে যা তারা এতটুকু টের পায়নি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1928,16,27,'অতঃপর ক্বিয়ামাত দিনে তিনি তাদেরকে অপমানিত করবেন আর বলবেন, ‘আমার অংশীদাররা কোথায় যাদের সম্পর্কে তোমরা (ঈমানদারদের সঙ্গে) বাক-বিতন্ডা করতে?’ যাদেরকে (দুনিয়ায়) জ্ঞান দেয়া হয়েছিল তারা বলবে, ‘আজ অপমান আর দুর্ভাগ্য তো কাফিরদের জন্য ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1929,16,28,'ফেরেশতারা যাদের মৃত্যু ঘটায় নিজেদের প্রতি যুলম করা অবস্থায়।’ অতঃপর তারা আত্মসমর্পণ ক’রে বলবে, ‘আমরা তো কোন খারাপ কাজ করতাম না।’ (ফেরেশতারা জবাব দিবে) ‘বরং, তোমরা যা করছিলে আল্লাহ সে বিষয়ে খুব ভালভাবেই অবগত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1930,16,29,'কাজেই জাহান্নামের দরজায় প্রবেশ কর, সেখানে তোমাদেরকে চিরকাল থাকতে হবে, দাম্ভিকদের অবাসস্থল কতই না মন্দ!’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1931,16,30,'মুত্তাকীদের যখন বলা হয়, ‘তোমাদের প্রতিপালক কী অবতীর্ণ করেছেন?’ তারা বলে, ‘যা কিছু উৎকৃষ্ট (তা-ই অবতীর্ণ করেছেন)।’ যারা সৎকর্ম করে তাদের জন্য এ দুনিয়াতে আছে কল্যাণ, আর তাদের পরকালের ঘর তো নিশ্চিতই কল্যাণকর। মুত্তাকীদের আবাসস্থল কতই না উত্তম! ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1932,16,31,'(তা হল) স্থায়ী জান্নাত যাতে তারা প্রবেশ করবে, তার নিম্নদেশ দিয়ে ঝর্ণাধারা প্রবাহিত, তারা যা ইচ্ছে করবে সেখানে তাদের জন্য তা-ই আছে- আল্লাহ মুত্তাকীদেরকে এভাবেই পুরস্কৃত করেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1933,16,32,'ফেরেশতা যাদের মৃত্যু ঘটায় পবিত্র অবস্থায় এই ব’লে যে, ‘তোমাদের উপর শান্তি বর্ষিত হোক, তোমরা যে ‘আমাল করতে তার ফল হিসেবে জান্নাতে প্রবেশ কর।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1934,16,33,'তারা কি এই অপেক্ষায় আছে যে, ফেরেশতারা তাদের কাছে আসবে কিংবা তোমার প্রতিপালকের ফায়সালা এসে পড়বে? তাদের পূর্ববর্তীরাও এ রকমই করত। আল্লাহ তাদের প্রতি কোন যুলম করেননি বরং তারা নিজেরাই নিজেদের প্রতি যুলম করত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1935,16,34,'কাজেই তাদের ‘আমালের মন্দ পরিণতি তাদের উপর আপতিত হল আর যে বিষয়কে তারা ঠাট্টা-বিদ্রূপ করত তা-ই তাদেরকে ঘিরে ফেলল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1936,16,35,'মুশরিকরা বলে, ‘আল্লাহ ইচ্ছে করলে তাঁকে বাদ দিয়ে অন্য কোন কিছুর ‘ইবাদাত আমরাও করতাম না, আর আমাদের পিতৃপুরুষরাও না, আর তাঁর হুকুম ছাড়া কোন কিছুকে হারাম গণ্যও করতাম না। তাদের পূর্ববর্তীরাও এ রকমই করত। (তারা রসূলদের কথা অমান্য করলে রসূলদের তো আর কিছুই করার নেই, কারণ) স্পষ্টভাবে পৌঁছে দেয়া ছাড়া রসূলদের উপর কি কোন দায়িত্ব আছে? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1937,16,36,'প্রত্যেক জাতির কাছে আমি রসূল পাঠিয়েছি (এ সংবাদ দিয়ে) যে, আল্লাহর ‘ইবাদাত কর আর তাগুতকে বর্জন কর। অতঃপর আল্লাহ তাদের মধ্যে কতককে সৎপথ দেখিয়েছেন, আর কতকের উপর অবধারিত হয়েছে গুমরাহী, অতএব যমীনে ভ্রমণ করে দেখ, সত্য প্রত্যাখ্যানকারীদের পরিণতি কী ঘটেছিল! ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1938,16,37,'তুমি যদি তাদের হিদায়াতের জন্য লালায়িত হও তবে (জেনে রেখে যে) আল্লাহ তাকে সৎপথ দেখান না যাকে তিনি বিপথগামী হতে ছেড়ে দেন আর তাদের কোন সাহায্যকারী নেই। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1939,16,38,'তারা আল্লাহর নামে শক্ত কসম খেয়ে বলে, ‘যার মুত্যৃ ঘটে আল্লাহ তাকে পুনরায় জীবিত করবেন না।’ অবশ্যই করবেন, এটা তো একটা প্রতিশ্রুতি যা পূরণ করা তাঁর দায়িত্ব, কিন্তু অধিকাংশ মানুষই তা জানে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1940,16,39,'(তাদেরকে পুনর্জীবিত করা হবে) যারা এ ব্যাপারে মতভেদ করেছিল তাদের কাছে স্পষ্ট করে দেয়ার জন্য, আর কাফিরগণ যাতে জানতে পারে যে, তারা ছিল মিথ্যেবাদী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1941,16,40,'কোন বিষয়ে আমি ইচ্ছে করলে বলি, ‘হয়ে যাও’, ফলে তা হয়ে যায়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1942,16,41,'যারা অত্যাচারিত হওয়ার পরও আল্লাহর পথে হিজরাত করেছে, আমি তাদেরকে অবশ্য অবশ্যই এ দুনিয়াতে উত্তম আবাস দান করব, আর আখেরাতের পুরস্কার তো অবশ্যই সবচেয়ে বড়। হায়, তারা যদি জানত! ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1943,16,42,'(আখেরাতের ঐ পুরস্কার তাদের জন্য) যারা ধৈর্যধারণ করে আর তাদের প্রতিপালকের উপর নির্ভর করে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1944,16,43,'আমি তোমার পূর্বে পুরুষ মানুষ ব্যতীত পাঠাইনি যাদের কাছে আমি ওয়াহী করতাম। তোমরা যদি না জান তাহলে তোমরা আল্লাহর কিতাব সম্পর্কে যারা অবগত তাদেরকে জিজ্ঞেস কর ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1945,16,44,'(অতীতের রসূলদেরকে পাঠিয়েছিলাম) স্পষ্ট প্রমাণাদি আর কিতাব দিয়ে; আর এখন তোমার প্রতি কুরআন অবতীর্ণ করছি মানুষকে স্পষ্টভাবে বুঝিয়ে দেয়ার জন্য যা তাদের প্রতি অবতীর্ণ করা হয়েছে আর যাতে তারা চিন্তা-ভাবনা করে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1946,16,45,'যারা (ইসলামের বিরুদ্ধে) কূট-কৌশল করছে তারা কি নির্ভয় হয়ে গেছে যে, আল্লাহ তাদেরকে যমীনে ধ্বসিয়ে দিবেন না, অথবা তাদের কাছে শাস্তি এসে পড়বে না এমন দিক থেকে যা তারা এতটুকুও টের পাবে না ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1947,16,46,'কিংবা তাদের চলাফেরার ভিতরেই তিনি তাদেরকে পাকড়াও করবেন না, অতঃপর তারা তো তা ব্যর্থ করে দিতে পারবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1948,16,47,'অথবা তিনি তাদেরকে পাকড়াও করবেন না যখন তারা আসন্ন মুসীবাতের চিন্তায় ভীত-সন্ত্রস্ত হয়ে থাকবে, (আসল কথা হল আল্লাহ মানুষকে খুবই অবকাশ দিয়ে থাকেন) কেননা তোমাদের প্রতিপালক অবশ্যই অতি দয়ার্দ্র, বড়ই দয়ালু। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1949,16,48,'তারা কি আল্লাহর সৃষ্টি করা জিনিসের দিকে লক্ষ্য করে না, যার ছায়া আল্লাহর প্রতি সাজদার অবস্থায় ডানে-বামে পতিত হয়, আর তারা বিনয় প্রকাশ করে? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1950,16,49,'আকাশসমূহ ও পৃথিবীতে যত জীব-জন্তু ফেরেশতারা, সমস্তই আল্লাহকে সাজদাহ করে; তারা অহঙ্কার করে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1951,16,50,'তারা তাদের উপরে আল্লাহকে ভয় করে আর তারা তা-ই করে যা তাদেরকে আদেশ দেয়া হয়। [সাজদাহ] ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1952,16,51,'আল্লাহ বললেন, ‘তোমরা দু’ ইলাহ গ্রহণ করো না, তিনি তো এক ইলাহ; কাজেই আমাকে- কেবল আমাকেই ভয় কর।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1953,16,52,'আকাশসমূহ আর যমীনে যা কিছু আছে তা তাঁরই, আর দ্বীন সদা-সর্বদা একান্তভাবে তাঁরই জন্য। তাহলে তোমরা কি আল্লাহ ছাড়া অন্য কাউকে ভয় করবে? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1954,16,53,'যে নি‘মাতই তোমরা পেয়েছ তাতো আল্লাহর নিকট হতেই। আর যখন দুঃখ-কষ্ট তোমাদেরকে স্পর্শ করে, তখন তাঁর কাছেই তোমরা আকুল আবেদন জানাতে থাক। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1955,16,54,'অতঃপর যখন তিনি তোমাদের থেকে দুঃখ-কষ্ট দূর করে দেন, তখন তোমাদের একদল তাদের প্রতিপালকের সঙ্গে অন্যকে শরীক করে বসে ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1956,16,55,'আমি তাদেরকে যা দিয়েছি তাদের না-শোকরি করা জন্য। অতএব তোমরা ভোগ করে নাও, শীঘ্রই তোমরা জানতে পারবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1957,16,56,'আর আমি তাদেরকে যে রিযক দিয়েছি তার একাংশ তারা ঐ সবের জন্য নির্ধারিত করে যাদের সম্পর্কে তারা কিছুই জানে না। আল্লাহর শপথ! তোমাদের এই মিথ্যে উদ্ভাবন সম্পর্কে তোমাদেরকে অবশ্য অবশ্যই জিজ্ঞেস করা হবে ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1958,16,57,'আর তারা কন্যা সন্তানগুলোকে আল্লাহর জন্য নির্ধারিত করে, তিনি (তাত্থেকে) পবিত্র মহান, আর তারা নিজেদের জন্য (নির্ধারিত করে) যা তাদের আকাঙ্ক্ষা হয়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1959,16,58,'তাদের কাউকে যখন কন্যা সন্তানের সুসংবাদ দেয়া হয়, তখন তার মুখ কালো হয়ে যায় আর সে অন্তর্জ্বালায় পুড়তে থাকে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1960,16,59,'লজ্জায় সে মানুষ থেকে মুখ লুকায় খারাপ সংবাদ পাওয়ার কারণে। সে চিন্তা করে যে অপমান মাথায় করে তাকে রেখে দেবে, না তাকে মাটিতে পুঁতে ফেলবে। হায়, তারা যা সিদ্ধান্ত করে তা কতই না জঘন্য! ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1961,16,60,'যারা আখেরাতে বিশ্বাস করে না, খারাপ উপমা তাদের জন্য। মহোত্তম উপমা সব আল্লাহর জন্য, তিনি হলেন প্রতাপান্বিত, প্রজ্ঞাময়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1962,16,61,'আল্লাহ যদি মানুষকে তাদের সীমালঙ্ঘনের জন্য পাকড়াও করতেন, তাহলে যমীনের উপর কোন প্রাণীকেই তিনি রেহাই দিতেন না। কিন্তু তিনি একটা নির্দিষ্ট কাল পর্যন্ত তাদেরকে সময় দেন। তাদের সময় এসে গেলে এক মুহূর্তও অগ্র-পশ্চাৎ করা হয় না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1963,16,62,'তারা আল্লাহর জন্য তা-ই নির্ধারণ করে যা (নিজেদের জন্য) অপছন্দ করে, আর তাদের জিহবা মিথ্যা বলে যে, কল্যাণ তাদেরই জন্য। কোন সন্দেহ নেই যে, তাদের জন্য আছে আগুন আর সর্বপ্রথম তাদেরকেই সেখানে জলদি পৌঁছে দেয়া হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1964,16,63,'আল্লাহর কসম! তোমার পূর্বে আমি বহু জাতির কাছে রসূল পাঠিয়েছিলাম, কিন্তু শয়ত্বান তাদের কাছে তাদের কার্যকলাপকে শোভনীয় করে দিয়েছিল, আর আজ সে-ই তাদের অভিভাবক, তাদের জন্য রয়েছে ভয়াবহ শাস্তি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1965,16,64,'আমি তোমার প্রতি কিতাব এজন্য নাযিল করেছি যাতে তুমি সে সকল বিষয় স্পষ্ট করে দিতে পার যে বিষয়ে তারা মতভেদ করেছিল, আর (এ কিতাব) বিশ্বাসী সম্প্রদায়ের জন্য পথপ্রদর্শক ও রহমাত স্বরূপ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1966,16,65,'আল্লাহ আকাশ থেকে পানি বর্ষণ করেন, অতঃপর তার দ্বারা যমীনকে তার মৃত্যুর পর পুনরায় জীবিত করেন। এতে ঐ সম্প্রদায়ের জন্য বহু নিদর্শন আছে যারা লক্ষ্য করে শুনে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1967,16,66,'তোমাদের জন্য গবাদি পশুতেও অবশ্যই শিক্ষা নিহিত আছে। তোমাদেরকে পান করাই ওদের পেটের গোবর আর রক্তের মাঝ থেকে বিশুদ্ধ দুগ্ধ যা পানকারীদের জন্য খুবই উপাদেয়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1968,16,67,'আর খেজুর ও আঙ্গুর ফল থেকে তোমরা মদ ও উত্তম খাদ্য প্রস্তুত কর, জ্ঞানী সম্প্রদায়ের জন্য এতে অবশ্যই বহু নিদর্শন রয়েছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1969,16,68,'তোমার প্রতিপালক মৌমাছির প্রতি এলহাম করেছেন যে, পাহাড়ে, বৃক্ষে আর উঁচু চালে বাসা তৈরি কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1970,16,69,'অতঃপর প্রত্যেক ফল থেকে আহার কর, অতঃপর তোমার প্রতিপালকের (শিখানো) সহজ পদ্ধতি অনুসরণ কর। এর পেট থেকে রং-বেরং এর পানীয় বের হয়। এতে মানুষের জন্য আছে আরোগ্য। চিন্তাশীল মানুষের জন্য এতে অবশ্যই নিদর্শন আছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1971,16,70,'আল্লাহ্ই তোমাদেরকে পয়দা করেছেন, অতঃপর তিনি তোমাদের মৃত্যু ঘটাবেন। তোমাদের কাউকে অকর্মণ্য বয়সে ফিরিয়ে দেয়া হয়, যাতে জ্ঞান লাভ করার পরেও আর কোন কিছুর জ্ঞান থাকে না। আল্লাহ সর্বশ্রেষ্ঠ জ্ঞানী, সর্বাপেক্ষা শক্তিমান। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1972,16,71,'রিযকের ব্যাপারে আল্লাহ তোমাদের মধ্যে কাউকে কারো উপর প্রাধান্য দিয়েছেন। যাদেরকে প্রাধান্য দেয়া হয়েছে তারা তাদের রিযক থেকে তাদের অধীনস্থ চাকর- গোলামদেরকে এমনভাবে ফিরিয়ে দেয় না, যাতে এক্ষেত্রে তারা সমান হয়ে যায়। (অথচ তারা নানান কিছুকে আল্লাহর অংশীদার বানিয়ে ওগুলোকে আল্লাহর সমান করে ফেলছে) তাহলে কি তারা আল্লাহর অনুগ্রহ অস্বীকার করে? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1973,16,72,'আল্লাহ তোমাদের স্বজাতির মধ্য হতেই তোমাদের জোড়া সৃষ্টি করেছেন আর তোমাদের জন্য তোমাদের জোড়া থেকে পুত্র-পৌত্রাদি বানিয়েছেন আর তোমাদেরকে উৎকৃষ্ট রিযক দিয়েছেন। তবুও কি তারা ভিত্তিহীন বাতিল জিনিসের উপর ঈমান পোষণ করবে আর আল্লাহর অনুগ্রহকে তারা অস্বীকার করবে? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1974,16,73,'আর আল্লাহকে বাদ দিয়ে ‘ইবাদাত করবে অন্যের যারা আকাশ ও যমীন থেকে তাদের জন্য রিযক দেয়ার মালিক নয়, আর তারা এ কাজে সক্ষমও নয়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1975,16,74,'কাজেই কারো সাথে আল্লাহর তুলনা দিও না। আল্লাহ জানেন, আর তোমরা জান না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1976,16,75,'আল্লাহ দৃষ্টান্ত দিচ্ছেনঃ অন্যের মালিকানাভুক্ত এক দাস যে কোন কিছু করারই ক্ষমতা রাখে না। আর এক লোক যাকে আমি আমার পক্ষ হতে উত্তম জীবিকা দান করেছি আর তাত্থেকে সে গোপনে প্রকাশ্যে দান করে, (এ) দু’জন কি সমান? সমস্ত প্রশংসা আল্লাহরই জন্য, কিন্তু তাদের অধিকাংশই জানে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1977,16,76,'আল্লাহ আরো দৃষ্টান্ত দিচ্ছেন দু’ব্যক্তির তাদের একজন হল বোবা, কোন কিছুই করতে সক্ষম নয়। তার মনিবের উপর সে একটা বোঝা, তাকে যেখানেই পাঠানো হোক না কেন, কোন কল্যাণই সে নিয়ে আসবে না। সে কি ঐ ব্যক্তির সমান যে ইনসাফের নির্দেশ দেয় আর সরল সুদৃঢ় পথে প্রতিষ্ঠিত? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1978,16,77,'আকাশসমূহ ও যমীনের অদৃশ্যের জ্ঞান কেবল আল্লাহরই আছে। ক্বিয়ামাতের ব্যাপার তো চোখের পলকের মত বরং তাত্থেকেও দ্রুত। আল্লাহ সব কিছু করতেই সক্ষম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1979,16,78,'আল্লাহ তোমাদেরকে তোমাদের মায়ের পেট থেকে বের করেন, তোমরা কিছুই জানতে না। তিনি তোমাদেরকে শোনার শক্তি, দেখার শক্তি আর অন্তর দান করেছেন যাতে তোমরা শোকর আদায় করতে পার। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1980,16,79,'আকাশের শূন্যলোকে নিয়ন্ত্রিত পাখীগুলোর প্রতি কি তারা লক্ষ্য করে না? আল্লাহ ছাড়া কেউ তাদেরকে স্থির রাখে না, এতে মু’মিন সম্প্রদায়ের জন্য অবশ্যই বহু নিদর্শন রয়েছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1981,16,80,'আল্লাহ তোমাদের গৃহগুলোকে তোমাদের আরাম শান্তির জায়গা বানিয়ে দিয়েছেন। তিনি পশুর চামড়া থেকে তোমাদের জন্য এমন গৃহের ব্যবস্থা করেছেন যা তোমরা হালকাবোধ কর তোমাদের ভ্রমণকালে কিংবা অবস্থানকালে। তিনি ওগুলোর পশম, লোম আর চুল থেকে তোমাদের পরিধেয় ও ব্যবহার্য সামগ্রীর ব্যবস্থা করেছেন যা তোমরা কিছুকাল পর্যন্ত কাজে লাগাও। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1982,16,81,'আল্লাহ যা সৃষ্টি করেছেন তাত্থেকে তোমাদের জন্য ছায়ার ব্যবস্থা করেছেন, পাহাড়-পর্বতে তোমাদের আত্মগোপনের জায়গা বানিয়েছেন। তিনি তোমাদের জন্য পোশাক দিয়েছেন যা তোমাদেরকে তাপ থেকে রক্ষা করে। আর দিয়েছেন এমন পোশাক যা তোমাদেরকে সংঘাতের সময় রক্ষা করে। এভাবে তিনি তোমাদের জন্য তাঁর নি‘মাতসমূহ পূর্ণ করেন যাতে তোমরা তাঁর প্রতি আত্মসমর্পণ কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1983,16,82,'এরপরও যদি তারা মুখ ফিরিয়ে নেয় তবে (জোরপূর্বক তাদেরকে সঠিক পথে আনা তোমার দায়িত্ব নয়) তোমার দায়িত্ব কেবল স্পষ্টভাবে বাণী পৌঁছে দেয়া। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1984,16,83,'তারা আল্লাহর নি‘মাতকে চিনতে পারে, কিন্তু তা সত্ত্বেও সেগুলো অগ্রাহ্য করে, তাদের অধিকাংশই অকৃতজ্ঞ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1985,16,84,'(সেদিন কী অবস্থা হবে) যেদিন আমি প্রত্যেক সম্প্রদায় থেকে একজন সাক্ষী দাঁড় করাব আর কাফিরদেরকে (কোন অযুহাত পেশ করার) অনুমতি দেয়া হবে না, আর ক্ষমা প্রার্থনা করারও সুযোগ দেয়া হবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1986,16,85,'সীমালঙ্ঘনকারীরা যখন ‘আযাব প্রত্যক্ষ করবে তাদের থেকে তখন তা কমানো হবে না, আর তাদেরকে সময়-সুযোগও দেয়া হবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1987,16,86,'মুশরিকরা যাদেরকে আল্লাহর শারীক বানিয়েছিল তাদেরকে যখন দেখবে তখন তারা বলবে, ‘হে আমাদের রব্ব! এরাই হল আমাদের শারীক মা‘বূদ তোমাকে বাদ দিয়ে যাদেরকে আমরা ডাকতাম।’ তখন এ কথাকে তাদের দিকেই ছুঁড়ে ফেলে দিয়ে তাদের সেই মা‘বূদরা বলবে, ‘তোমরা অবশ্যই মিথ্যেবাদী।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1988,16,87,'সেদিন তারা আল্লাহর নিকট খোলাখুলি আত্মসমর্পণ করবে, আর তারা যে সব মিথ্যে উদ্ভাবন করেছিল তা তাদের নিকট হতে হারিয়ে যাবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1989,16,88,'যারা আল্লাহকে অস্বীকার করে আর আল্লাহর পথে বাধা সৃষ্টি করে, আমি তাদের শাস্তির উপর শাস্তি বৃদ্ধি করব, কারণ তারা ফাসাদ সৃষ্টি করত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1990,16,89,'সেদিন আমি প্রত্যেক উম্মাত থেকে তাদের নিজেদেরই মধ্য হতে একজন সাক্ষী দাঁড় করাব, আর (তোমার) এই লোকদের ব্যাপারে সাক্ষ্যদাতা হিসেবে (হে মুহাম্মাদ!) আমি তোমাকে আনব। আমি তোমার প্রতি এ কিতাব নাযিল করেছি যা প্রত্যেকটি বিষয়ের সুস্পষ্ট ব্যাখ্যা, সত্য পথের নির্দেশ, রহমাত আর আত্মসমর্পণকারীদের জন্য সুসংবাদ স্বরূপ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1991,16,90,'আল্লাহ ন্যায়-বিচার, সদাচরণ ও আত্মীয়দেরকে দেয়ার হুকুম দিচ্ছেন, আর তিনি নিষেধ করছেন অশ্লীলতা, অপকর্ম আর বিদ্রোহ থেকে। তিনি তোমাদেরকে উপদেশ দিয়েছেন যাতে তোমরা শিক্ষা গ্রহণ কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1992,16,91,'তোমরা পরস্পর অঙ্গীকারে আবদ্ধ হলে আল্লাহর অঙ্গীকার পূর্ণ করবে, নিজেদের অঙ্গীকার পাকা-পোখত করার পর তা ভঙ্গ করো না, যেহেতু তোমরা আল্লাহকে নিজেদের উপর সাক্ষী বানিয়ে নিয়েছ, তোমরা যা কর আল্লাহ সে সম্পর্কে ওয়াকেফহাল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1993,16,92,'তোমরা এমন নারীর মত হয়ো না যে তার সূতাগুলোকে শক্ত করে পাকানোর পর নিজেই তার পাক খুলে টুকরো টুকরো করে দেয়। তোমরা তোমাদের শপথগুলোকে পারস্পরিক ব্যাপারে ধোঁকা-প্রতারণার হাতিয়ার হিসেবে ব্যবহার কর যেন একদল আরেক দল অপেক্ষা বেশি ফায়দা লাভ করতে পারে। আল্লাহ কেবল এর দ্বারা তোমাদেরকে পরীক্ষা করেন। আল্লাহ ক্বিয়ামাতের দিন অবশ্য অবশ্যই স্পষ্ট করে দিবেন যে বিষয়ে তোমরা মতভেদ করতে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1994,16,93,'আল্লাহ ইচ্ছে করলে তোমাদেরকে অবশ্যই এক উম্মাত করে দিতেন, কিন্তু তিনি যাকে ইচ্ছে গুমরাহ্ করেন, আর যাকে ইচ্ছে সঠিক পথপ্রদর্শন করেন। তোমরা যা কর সে সম্পর্কে অবশ্য অবশ্যই তোমাদেরকে জিজ্ঞাসাবাদ করা হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1995,16,94,'তোমরা তোমাদের নিজেদের শপথগুলোকে পরস্পর ধোঁকা-প্রবঞ্চনার উপায় হিসেবে গ্রহণ করো না, তা করলে দৃঢ়ভাবে প্রতিষ্ঠিত হওয়ার পর পা আবার পিছলে যাবে, আর আল্লাহর পথে বাধা দেয়ার কারণে তোমরা মন্দ পরিণতি ভোগ করবে, আর তোমাদের জন্য থাকবে কঠিন শাস্তি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1996,16,95,'তোমরা আল্লাহর সঙ্গে কৃত ওয়া‘দা নগণ্য মূল্যে বিক্রয় করো না। আল্লাহর নিকট যা আছে তোমাদের জন্য তা-ই উত্তম- তোমরা যদি জানতে! ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1997,16,96,'তোমাদের কাছে যা আছে তা শেষ হয়ে যাবে, আর আল্লাহর কাছে যা আছে তা টিকে থাকবে। আমরা অবশ্য অবশ্যই ধৈর্যশীলদেরকে তারা যা করে তার চেয়ে উত্তম প্রতিদান দেব। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1998,16,97,'পুরুষ আর নারীদের মধ্যে যে কেউ সৎকাজ করবে আর সে মু’মিনও, তাকে আমি অবশ্য অবশ্যই উত্তম জীবন দান করব আর তাদেরকে অবশ্য অবশ্যই তারা যা করে তার চেয়ে উত্তম প্রতিফল দান করব। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (1999,16,98,'তুমি যখনি কুরআন পাঠ করবে তখন অভিশপ্ত শয়ত্বান থেকে আল্লাহর নিকট আশ্রয় চাইবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2000,16,99,'যারা ঈমান এনেছে তাদের উপর তার কোন প্রভাব খাটে না, আর তারা তাদের প্রতিপালকের উপর নির্ভর করে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2001,16,100,'তার প্রভাব কেবল তাদের উপরই খাটে যারা তাকে অভিভাবক হিসেবে গ্রহণ করে আর যারা তাকে আল্লাহর শরীক করে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2002,16,101,'আমি যখন এক আয়াতের বদলে অন্য আয়াত নাযিল করি- আর আল্লাহ ভালভাবেই জানেন, যা তিনি নাযিল করেন- তখন এই লোকেরা বলে, ‘তুমি তো মিথ্যা রচনাকারী।’ প্রকৃত ব্যাপার এই যে, এ সম্পর্কে তাদের অধিকাংশেরই কোন জ্ঞান নেই। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2003,16,102,'বল, ‘এ কুরআন তোমার প্রতিপালকের পক্ষ থেকে রুহুল কুদূস (জিবরীল) ঠিক ঠিকভাবে নাযিল করেছেন ঈমানদারদেরকে দৃঢ়ভাবে প্রতিষ্ঠিত রাখার জন্য এবং মুসলিমদের হিদায়াত ও সুসংবাদ দানের জন্য।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2004,16,103,'আমি জানি, তারা বলে, ‘এক মানুষ তাকে [অর্থাৎ মুহাম্মাদ (সা.)-কে] শিখিয়ে দেয়।’ অথচ দুষ্টবুদ্ধি প্রণোদিত হয়ে তারা যে লোকটির কথা বলছে তার ভাষা তো অনারব, অপরপক্ষে কুরআনের ভাষা হল স্পষ্ট আরবী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2005,16,104,'যারা আল্লাহর নিদর্শনাবলীতে বিশ্বাস করে না, আল্লাহ তাদেরকে সঠিক পথে পরিচালিত করেন না, আর তাদের জন্য আছে ভয়ানক শাস্তি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2006,16,105,'যারা আল্লাহর নিদর্শনসমূহে বিশ্বাস করে না, তারাই মিথ্যে রচনা করে আর তারাই মিথ্যাবাদী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2007,16,106,'কোন ব্যক্তি তার ঈমান গ্রহণের পর আল্লাহকে অবিশ্বাস করলে এবং কুফরীর জন্য তার হৃদয় খুলে দিলে তার উপর আল্লাহর গযব পতিত হবে আর তার জন্য আছে মহা শাস্তি, তবে তার জন্য নয় যাকে (কুফরীর জন্য) বাধ্য করা হয় অথচ তার দিল ঈমানের উপর অবিচল থাকে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2008,16,107,'এর কারণ এই যে, তারা আখিরাত অপেক্ষা দুনিয়ার জীবনকে বেশি ভালবাসে, আর আল্লাহ ঈমান প্রত্যাখ্যানকারীদেরকে সঠিক পথ দেখান না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2009,16,108,'এরা ঐ সব লোক আল্লাহ যাদের অন্তর, কান আর চোখে মোহর মেরে দিয়েছেন আর তারা বে-খেয়াল, উদাসীন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2010,16,109,'এতে কোন সন্দেহই নেই যে, আখেরাতে ক্ষতিগ্রস্ত এরাই হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2011,16,110,'অবশ্যই তোমার প্রতিপালক তাদের জন্য যারা নির্যাতিত হওয়ার পর হিজরাত করে, অতঃপর জিহাদ করে, অতঃপর ধৈর্যধারণ করে, এ সবের পর তোমার প্রতিপালক অবশ্যই বড়ই ক্ষমাশীল, বড়ই দয়ালু। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2012,16,111,'সেদিন প্রত্যেক লোক নিজের পক্ষে যুক্তি পেশ করবে আর প্রত্যেক ব্যক্তিকে তার কৃতকর্মের প্রতিফল পূর্ণমাত্রায় দেয়া হবে, তাদের প্রতি কোন যুলম করা হবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2013,16,112,'আল্লাহ এক জনবসতির দৃষ্টান্ত পেশ করছেন যা ছিল নিরাপদ, চিন্তা-ভাবনাহীন। সবখান থেকে সেখানে আসত জীবন ধারণের পর্যাপ্ত উপকরণ। অতঃপর সে জনপদ আল্লাহর নি‘মাতরাজির কুফুরী করল, অতঃপর আল্লাহ তাদের কৃতকর্মের কারণে ক্ষুধা ও ভয়-ভীতির মুসীবাত তাদেরকে আস্বাদন করালেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2014,16,113,'তাদের কাছে তাদের মধ্য হতেই রসূল এসেছিল কিন্তু তারা তাকে মিথ্যা মনে ক’রে প্রত্যাখ্যান করল, তখন শাস্তি তাদেরকে পাকড়াও করল যখন তারা ছিল সীমালঙ্ঘনে লিপ্ত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2015,16,114,'কাজেই আল্লাহ তোমাদেরকে যে সকল বৈধ পবিত্র রিযক দিয়েছেন তা তোমরা খাও আর আল্লাহর অনুগ্রহের শুকরিয়া আদায় কর যদি তোমরা প্রকৃতই তাঁর বন্দেগী করতে ইচ্ছুক হও। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2016,16,115,'আল্লাহ তোমাদের জন্য হারাম করেছেন মৃত জীব, রক্ত, শূকরের মাংস আর যা যবেহ করার সময় আল্লাহ ছাড়া অন্যের নাম নেয়া হয়েছে। কিন্তু কেউ ইচ্ছাকৃতভাবে অবাধ্য না হয়ে ও সীমালঙ্ঘন না ক’রে নিতান্ত নিরুপায় (হয়ে এসব খেতে বাধ্য) হলে আল্লাহ তো বড়ই ক্ষমাশীল, বড়ই দয়ালু। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2017,16,116,'তোমাদের জিহবা থেকে মিথ্যে কথা বেরোয় বলেই তোমরা আল্লাহর প্রতি মিথ্যারোপ করার জন্য এমন কথা বলো না যে, এটা হালাল, আর এটা হারাম। যারা আল্লাহ সম্পর্কে মিথ্যে উদ্ভাবন করে, তারা কক্ষনো কল্যাণ লাভ করতে পারে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2018,16,117,'(এসব মিথ্যাচারে লাভ হয়) সামান্য ভোগের বস্তু, অতঃপর তাদের জন্য আছে ভয়াবহ শাস্তি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2019,16,118,'আর যারা ইয়াহূদী হয়েছিল আমি তাদের প্রতি হারাম করেছিলাম যা আমি তোমার কাছে পূর্বেই উল্লেখ করেছি। আমি তাদের উপর কোন যুলম করিনি, কিন্তু তারা নিজেরাই নিজেদের প্রতি যুলম করত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2020,16,119,'তোমার প্রতিপালক তাদের জন্য যারা অজ্ঞতার কারণে খারাপ কাজ করে, অতঃপর তাওবাহ করে ও নিজেদের ‘আমাল সংশোধন করে, তোমার প্রতিপালক তাদের জন্য অবশ্যই বড়ই ক্ষমাশীল, বড়ই দয়ালু। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2021,16,120,'ইবরাহীম ছিল আল্লাহর প্রতি বিনয়াবনত একনিষ্ঠ এক উম্মাত, আর সে মুশরিকদের অন্তর্ভুক্ত ছিল না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2022,16,121,'সে ছিল আল্লাহর নি‘মাতরাজির জন্য শোকরগুযার। আল্লাহ তাকে বেছে নিয়েছিলেন আর তাকে সরল সঠিক পথ দেখিয়েছিলেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2023,16,122,'আমি তাকে দুনিয়াতে কল্যাণ দান করেছিলাম, আর আখেরাতেও সে অবশ্যই সৎকর্মশীলদের অন্তর্ভুক্ত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2024,16,123,'অতঃপর তোমার প্রতি ওয়াহী করছি যে, তুমি একনিষ্ঠ ইবরাহীমের মতাদর্শ অনুসরণ কর; আর সে তো মুশরিকদের দলভুক্ত ছিল না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2025,16,124,'‘শনিবার পালন’ তো তাদের উপর চাপিয়ে দেয়া হয়েছিল যারা এর নিয়ম পালনে মতভেদ করেছিল। যে বিষয়ে তারা মতভেদ করত তোমার প্রতিপালক ক্বিয়ামাতের দিন সে বিষয়ে অবশ্যই তাদের মধ্যে ফায়সালা করে দেবেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2026,16,125,'জ্ঞান-বুদ্ধি আর উত্তম উপদেশের মাধ্যমে তুমি (মানুষকে) তোমার প্রতিপালকের পথে আহবান জানাও আর লোকেদের সাথে বিতর্ক কর এমন পন্থায় যা অতি উত্তম। তোমার প্রতিপালক ভালভাবেই জানেন কে তাঁর পথ ছেড়ে গুমরাহ হয়ে গেছে। আর কে সঠিক পথে আছে তাও তিনি বেশি জানেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2027,16,126,'যদি তোমরা প্রতিশোধ গ্রহণ করতে চাও তবে ততটুকু প্রতিশোধ গ্রহণ কর যতটুকু অন্যায় তোমাদের উপর করা হয়েছে। আর যদি তোমরা ধৈর্য ধারণ কর তবে ধৈর্যধারণকারীদের জন্য অবশ্যই তা উত্তম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2028,16,127,'তুমি ধৈর্য ধারণ কর, তোমার ধৈর্য তো কেবল আল্লাহ ব্যতীত নয়, ওদের কার্যকলাপে তুমি দুঃখিত হয়ো না, আর ওদের ষড়যন্ত্র করার কারণে অন্তরে কুণ্ঠাবোধ করো না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2029,16,128,'যারা তাক্বওয়া অবলম্বন করে আর সৎকর্মশীল, আল্লাহ তো তাদেরই সঙ্গে আছেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2030,17,1,'পবিত্র ও মহীয়ান তিনি যিনি তাঁর বান্দাহকে রাতের বেলা ভ্রমণ করিয়েছেন মাসজিদুল হারাম থেকে মাসজিদুল আকসা পর্যন্ত, যার চারপাশকে আমি কল্যাণময় করেছি। তাকে আমার নিদর্শনাবলী দেখানোর জন্য, তিনি সর্বশ্রোতা, সর্বদ্রষ্টা। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2031,17,2,'আর আমি মূসাকে কিতাব দিয়েছিলাম আর সেটাকে করেছিলাম ইসরাঈল বংশীয়দের জন্য সত্যপথের নির্দেশক। (তাতে নির্দেশ দিয়েছিলাম) যে, আমাকে ছাড়া অন্যকে কর্ম নিয়ন্তা গ্রহণ করো না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2032,17,3,'(তোমরা তো) তাদের সন্তান! যাদেরকে আমি নূহের সঙ্গে নৌকায় বহন করেয়েছিলাম, সে ছিল এক শুকরগুজার বান্দা। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2033,17,4,'আমি কিতাবের মাধ্যমে বানী ইসরাঈলকে জানিয়ে দিয়েছিলাম যে, তোমরা অবশ্য অবশ্যই পৃথিবীর বুকে দু’ দু’বার বিপর্যয় সৃষ্টি করবে আর অবশ্য অবশ্যই অত্যধিক গর্বে ফুলে উঠবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2034,17,5,'অতঃপর যখন দু’টির মধ্যে প্রথমটির সময় এসে উপস্থিত হল, তখন আমি তোমাদের বিরুদ্ধে পাঠিয়ে দিলাম আমার বান্দাদেরকে যারা ছিল যুদ্ধে অতি শক্তিশালী, তারা (তোমাদের) ঘরের কোণায় কোণায় ঢুকে পড়ল, আর সতর্কবাণী পূর্ণ হল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2035,17,6,'অতঃপর আমি তোমাদেরকে তাদের উপর বিজয় দান করলাম আর তোমাদেরকে ধন-সম্পদ আর সন্তানাদি দিয়ে সাহায্য করলাম, তোমাদেরকে জনবলে বহুগুণ বাড়িয়ে দিলাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2036,17,7,'তোমরা ভাল কাজ করলে নিজেদের কল্যাণের জন্যই তা করবে, আর যদি তোমরা মন্দ কাজ কর, তাও করবে নিজেদেরই জন্য। অতঃপর যখন দ্বিতীয় প্রতিশ্রুতি পূরণের সময় আসলো, (তখন আমি তোমাদের শত্রুদেরকে শক্তি দিলাম) যেন তারা তোমাদের চেহারা বিকৃত করে দেয়, আর মাসজিদে (আকসায়) ঢুকে পড়ে যেভাবে তারা সেখানে প্রথমবার ঢুকে পড়েছিল, আর তাদের সম্মুখে যা পড়ে তাকে সম্পূর্ণরূপে ধ্বংস করে দেয়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2037,17,8,'(এরপরও) হয়তো তোমাদের প্রতিপালক তোমাদের প্রতি দয়া করবেন, কিন্তু যদি তোমরা (তোমাদের পূর্বকৃত পাপের) পুনরাবৃত্তি কর, তবে আমিও (পূর্বে দেয়া শাস্তির) পুনরাবৃত্তি করব। ঈমান প্রত্যাখ্যানকারীদের জন্য আমি জাহান্নামকে কারাগার বানিয়ে রেখেছি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2038,17,9,'নিশ্চয়ই এ কুরআন সেই পথ দেখায় যা সোজা ও সুপ্রতিষ্ঠিত, আর যারা সৎ কাজ করে সেই মু’মিনদেরকে সুসংবাদ দেয় যে, তাদের জন্য রয়েছে মহা পুরস্কার। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2039,17,10,'আর (তা সংবাদ দেয় যে) যারা আখেরাতে ঈমান আনে না, তাদের জন্য আমি ভয়ঙ্কর ‘আযাব প্রস্তুত করে রেখেছি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2040,17,11,'মানুষ (তার নির্বুদ্ধিতার কারণে কল্যাণকর ভেবে) অকল্যাণ প্রার্থনা করে যেমনভাবে কল্যাণ প্রার্থনা করা উচিত। মানুষ বড়ই তাড়াহুড়াকারী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2041,17,12,'আমি রাত আর দিনকে দু’টো নিদর্শন বানিয়েছি। আমি রাতের নিদর্শনটিকে জ্যোতিহীন করেছি, আর দিনের নিদর্শনটিকে করেছি আলোয় উজ্জ্বল যাতে তোমরা তোমাদের প্রতিপালকের অনুগ্রহ অনুসন্ধান করতে পার আর যাতে বছরের সংখ্যা আর হিসাব জানতে পার; আমি সকল বিষয় বিস্তারিতভাবে ব্যাখ্যা করে দিয়েছি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2042,17,13,'আমি প্রত্যেক লোকের ভাগ্য তার কাঁধেই ঝুলিয়ে রেখেছি (অর্থাৎ তার ভাগ্যের ভাল-মন্দের কারণ তার নিজের মধ্যেই নিহিত আছে) আর ক্বিয়ামতের দিন তার জন্য আমি এক কিতাব বের করব যাকে সে উন্মুক্ত অবস্থায় পাবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2043,17,14,'(তাকে বলা হবে) ‘পাঠ কর তোমার কিতাব, আজ তোমার হিসাব নেয়ার ব্যাপারে তুমিই যথেষ্ট।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2044,17,15,'যে সঠিক পথে চলবে সে তার নিজের কল্যাণের জন্যই সঠিক পথে চলবে, আর যে গুমরাহ হবে তার গুমরাহীর পরিণাম তার নিজের উপরেই পড়বে। কোন বোঝা বহনকারী অন্যের বোঝা বহন করবে না। আমি ‘আযাব দেই না যতক্ষণ একজন রসূল না পাঠাই। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2045,17,16,'আমি যখন কোন জনবসতিকে ধ্বংস করতে চাই তখন তাদের সচ্ছল ব্যক্তিদেরকে আদেশ করি (আমার আদেশ মেনে চলার জন্য)। কিন্তু তারা অবাধ্যতা করতে থাকে। তখন সে জনবসতির প্রতি আমার ‘আযাবের ফায়সালা সাব্যস্ত হয়ে যায়। তখন আমি তা সম্পূর্ণরূপে বিধ্বস্ত করে দেই। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2046,17,17,'নূহের পর বহু বংশধারাকে আমি ধ্বংস করে দিয়েছি, বান্দাদের পাপকাজের খবর রাখা আর লক্ষ্য রাখার জন্য তোমার প্রতিপালকই যথেষ্ট। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2047,17,18,'যে কেউ নগদ নগদ পেতে চায় তাকে আমি এখানেই জলদি করে দিয়ে দেই যাকে যা দিতে ইচ্ছে করি, অবশেষে তার জন্য জাহান্নাম নির্ধারণ করি। তাতে সে জ্বলবে ধিকৃত ও রহমাত বঞ্চিত অবস্থায়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2048,17,19,'আর যে ব্যক্তি আখিরাত কামনা করে আর তার জন্য চেষ্টা করে যতখানি চেষ্টা করা দরকার আর সে মু’মিনও, এরাই হল তারা যাদের চেষ্টা সাধনা সাদরে গৃহীত হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2049,17,20,'তোমার প্রতিপালকের দান থেকে আমি এদেরকে আর ওদেরকে সকলকেই সাহায্য করে থাকি, তোমার প্রতিপালকের দান তো বন্ধ হওয়ার নয়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2050,17,21,'লক্ষ্য কর, আমি তাদের কতককে অন্যদের উপর কীভাবে শ্রেষ্ঠত্ব দিয়েছি, আর আখিরাত তো নিশ্চয়ই মর্যাদায় সর্বোচ্চ ও গুণে সর্বোত্তম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2051,17,22,'আল্লাহর সাথে অন্য কোন ইলাহ সাব্যস্ত করো না, করলে তিরস্কৃত হতভাগ্য হয়ে পড়ে থাকবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2052,17,23,'তোমার প্রতিপালক হুকুম জারি করেছেন যে, তিনি ছাড়া অন্য কারো ‘ইবাদাত করো না, আর পিতা-মাতার সঙ্গে সদ্ব্যবহার করো। তাদের একজন বা তাদের উভয়ে যদি তোমার জীবদ্দশায় বার্ধক্যে উপনীত হয়, তবে তাদেরকে বিরক্তি বা অবজ্ঞাসূচক কথা বলো না, আর তাদেরকে ভৎর্সনা করো না। তাদের সাথে সম্মানজনক কথা বল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2053,17,24,'তাদের জন্য সদয়ভাবে নম্রতার বাহু প্রসারিত করে দাও আর বল, ‘হে আমার প্রতিপালক! তাদের প্রতি দয়া কর যেমনভাবে তারা আমাকে শৈশবে লালন পালন করেছেন।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2054,17,25,'তোমাদের প্রতিপালক খুব ভাল করেই জানেন তোমাদের অন্তরে কী আছে। তোমরা যদি সৎকর্মশীল হও, তবে যারা বার বার তাঁর দিকে ফিরে আসে তিনি তো তাদের প্রতি পরম ক্ষমাশীল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2055,17,26,'আর আত্মীয়-স্বজনকে তাদের প্রাপ্য অধিকার দাও এবং মিসকীন ও মুসাফিরদেরকেও, আর অপব্যয়ে অপচয় করো না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2056,17,27,'অপচয়কারীরা শয়তানের ভাই আর শয়তান তো তার প্রতিপালকের প্রতি না-শোকর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2057,17,28,'তুমি যদি তাদেরকে (অর্থাৎ অভাবী আত্মীয়, মিসকীন ও মুসাফিরদেরকে) পাশ কাটাতে চাও এজন্য যে, তুমি এখনও নিজের জন্য তোমার প্রতিপালকের অনুগ্রহ লাভের সন্ধানে ব্যাপৃত যা তুমি প্রত্যাশা কর, এমতাবস্থায় তাদের সঙ্গে নম্রভাবে কথা বল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2058,17,29,'তোমার হাতকে তোমার গলার সাথে বেঁধে দিও না, আর তা একেবারে প্রসারিত করেও দিওনা, তা করলে তুমি তিরস্কৃত ও নিঃস্ব হয়ে বসে পড়বে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2059,17,30,'তোমার প্রতিপালক যার জন্য ইচ্ছে রিযক্ প্রশস্ত করেন, যার জন্য ইচ্ছে সীমিত করেন, তিনি তাঁর বান্দাদের সম্পর্কে ওয়াকিফহাল, প্রত্যক্ষদর্শী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2060,17,31,'দরিদ্রতার ভয়ে তোমরা তোমাদের সন্তানদেরকে হত্যা করো না। আমিই তাদেরকে রিযক দেই আর তোমাদেরকেও, তাদের হত্যা মহাপাপ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2061,17,32,'আর যিনা-ব্যভিচারের কাছেও যেও না, তা হচ্ছে অশ্লীল কাজ আর অতি জঘন্য পথ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2062,17,33,'যথাযথ কারণ ছাড়া আল্লাহ যাকে হত্যা করা নিষিদ্ধ করেছেন তাকে হত্যা করো না। কাউকে অন্যায়ভাবে হত্যা করা হলে আমি তার উত্তরাধিকারীকে অধিকার দিয়েছি (কিসাস দাবী করার বা ক্ষমা করে দেয়ার) কাজেই সে যেন হত্যার ব্যাপারে সীমালঙ্ঘন না করে, কারণ তাকে তো সাহায্য করা হয়েছে (আইন-বিধান দিয়ে)। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2063,17,34,'ইয়াতীম বয়োপ্রাপ্ত না হওয়া পর্যন্ত তার সম্পদের কাছেও যেয়ো না সৎ উদ্দেশ্য ব্যতীত। আর ওয়া‘দা পূর্ণ কর, ওয়া‘দা সম্পর্কে অবশ্যই জিজ্ঞাসাবাদ করা হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2064,17,35,'মাপ দেয়ার সময় মাপ পূর্ণমাত্রায় করবে, আর ওজন করবে ত্রুটিহীন দাঁড়িপাল্লায়। এটাই উত্তম নীতি আর পরিণামেও তা উৎকৃষ্ট। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2065,17,36,'আর সে বিষয়ের পেছনে ছুটো না, যে বিষয়ে তোমার কোন জ্ঞান নেই। কান, চোখ আর অন্তর- এগুলোর সকল বিষয়ে অবশ্যই জিজ্ঞাসাবাদ করা হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2066,17,37,'যমীনে গর্বভরে চলাফেরা করো না, তুমি কক্ষনো যমীনকে বিদীর্ণ করতে পারবে না, আর উচ্চতায় পর্বতের ন্যায় হতেও পারবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2067,17,38,'এগুলোর মধ্যে যে সমস্ত বিষয় মন্দ, তোমার প্রতিপালকের নিকট তা ঘৃণিত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2068,17,39,'এসব সেই হিকমাতের অন্তর্ভুক্ত যা তোমার প্রতিপালক তোমার প্রতি ওয়াহী করেছেন। আল্লাহর সঙ্গে অপর কোন ইলাহ স্থির করো না, করলে তুমি নিন্দিত ও যাবতীয় কল্যাণ বঞ্চিত হয়ে জাহান্নামে নিক্ষিপ্ত হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2069,17,40,'তাহলে কি (হে কাফিরগণ!) তোমাদের জন্য তোমাদের প্রতিপালক সন্তান নির্বাচিত করেছেন, আর নিজের জন্য ফেরেশতাদের মধ্য হতে কন্যা গ্রহণ করেছেন? বাস্তবিকই তোমরা বড় ভয়ানক কথা বলছো। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2070,17,41,'আমি এ কুরআনে নানাভাবে (বিষয়াবলী) ব্যাখ্যা করেছি যাতে তারা উপদেশ গ্রহণ করে, কিন্তু তা তাদের (সত্য হতে) পলায়নের মনোবৃত্তিই বৃদ্ধি করেছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2071,17,42,'বল- তাঁর সঙ্গে যদি আরো ইলাহ থাকত যেমন তারা বলে, তাহলে তারা অবশ্যই আরশের মালিকের নিকট পৌঁছার জন্য পথের সন্ধান করত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2072,17,43,'তিনি পবিত্র ও অতি উচ্চ, তারা যা বলে তাত্থেকে অনেক অনেক ঊর্ধ্বে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2073,17,44,'সাত আসমান, যমীন আর এগুলোর মাঝে যা আছে সব কিছুই তাঁর মহিমা ঘোষণা করে। এমন কোন জিনিসই নেই যা তাঁর প্রশংসাসহ পবিত্রতা ও মহিমা ঘোষণা করে না। কিন্তু তোমরা বুঝতে পার না কীভাবে তারা তাঁর মহিমা ঘোষণা করে। তিনি পরম সহিষ্ণু, বড়ই ক্ষমাপরায়ণ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2074,17,45,'তুমি যখন কুরআন পাঠ কর তখন আমি তোমার আর যারা আখেরাতে বিশ্বাস করে না তাদের মাঝে একটা অদৃশ্য পর্দা স্থাপন ক’রে দিয়েছি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2075,17,46,'আর আমি তাদের অন্তরের উপর এক আবরণ দিয়ে দিয়েছি যাতে তারা কুরআন বুঝতে না পারে, আর তাদের কানে সৃষ্টি করেছি বধিরতা। আর যখন তুমি কুরআনে তোমার প্রতিপালকের একত্বের উল্লেখ কর, তখন তারা (সত্য থেকে) পালিয়ে পিছনে মুখ ঘুরিয়ে নেয়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2076,17,47,'আমি ভাল করেই জানি তারা কান লাগিয়ে কী শুনে যখন তারা তোমার কথা কান লাগিয়ে শুনে। আর যখন তারা গোপনে পরস্পর আলোচনায় বসে তখন যালিমরা বলে, ‘তোমরা তো কেবল এক যাদুগ্রস্ত লোকের অনুসরণ করে চলেছ।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2077,17,48,'লক্ষ্য কর, তারা তোমার সম্পর্কে কেমন সব উদাহরণ দিচ্ছে! যার ফলে তারা পথহারা হয়ে গেছে আর তারা কক্ষনো পথ পাবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2078,17,49,'তারা বলে, ‘কী! আমরা হাড্ডি আর ধূলা-মাটিতে পরিণত হওয়ার পর কি এক নতুন সৃষ্টিরূপে উত্থিত হব?’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2079,17,50,'বল, ‘তোমরা যদি পাথর কিংবা লোহাও হয়ে যাও, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2080,17,51,'অথবা এমন কিছু যা তোমাদের ধারণায় (জীবিত হওয়া) খুবই কঠিন (তবুও তোমাদেরকে উঠানো হবে)।’ তারা বলবে, ‘কে আছে এমন যে আমাদেরকে পুনরায় জীবনে ফিরিয়ে আনবে?’ বল, ‘তিনিই যিনি তোমাদেরকে প্রথমবার সৃষ্টি করেছেন।’ তখন তারা (ঠাট্টার ছলে) তোমার সামনে মাথা নাড়বে আর বলবে, ‘সেটা কখন ঘটবে?’ বল, ‘হতে পারে সেটা শীঘ্রই ঘটবে।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2081,17,52,'যে দিন তিনি তোমাদেরকে ডাকবেন আর তোমরা তাঁর প্রশংসা করতে করতে তাঁর ডাকে সাড়া দিবে আর তোমরা ধারণা করবে যে, তোমরা খুব অল্প সময়ই অবস্থান করেছিলে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2082,17,53,'আমার বান্দাদেরকে বলতে বল এমন কথা যা খুবই উত্তম। শয়তান মানুষের মাঝে ঝগড়া-বিভেদ-বিশৃঙ্খলা সৃষ্টি করে, শয়তান হল মানুষের প্রকাশ্য দুশমন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2083,17,54,'তোমাদের প্রতিপালক তোমাদেরকে খুব ভাল ক’রেই জানেন। তিনি ইচ্ছে করলে তোমাদের প্রতি অনুগ্রহ করবেন, আর ইচ্ছে করলে তোমাদেরকে শাস্তি দেবেন; আমি তোমাকে (হে নাবী!) তাদের কাজকর্মের জন্য দায়িত্বশীল করে পাঠাইনি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2084,17,55,'আসমান আর যমীনে যারা আছে তোমার প্রতিপালক তাদেরকে ভাল ক’রেই জানেন। আমি নাবীগণের কতককে অন্যদের উপর মর্যাদা দান করেছি আর দাঊদকে দিয়েছি যাবূর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2085,17,56,'বল, ‘তোমরা আল্লাহ ছাড়া যাদেরকে ইলাহ মনে কর তাদেরকে ডাক, (ডাকলেও দেখতে পাবে) তারা তোমাদের দুঃখ-বেদনা দূর করতে বা বদলাতে সক্ষম নয়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2086,17,57,'তারা যাদেরকে ডাকে তারা নিজেরাই তো তাদের প্রতিপালকের নিকট পৌঁছার পথ অনুসন্ধান করে যে, কে তাঁর অধিক নিকটবর্তী হতে পারবে, আর তাঁর দয়া প্রত্যাশা করে এবং তাঁর শাস্তিকে ভয় করে। তোমার প্রতিপালকের শাস্তি তো ভয় করার মতই। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2087,17,58,'এমন কোন জনবসতি নেই যাকে আমি ক্বিয়ামাত দিনের পূর্বে ধ্বংস করব না কিংবা তাকে কঠিন শাস্তি দিব না, এটা (আল্লাহর) কিতাবে লিপিবদ্ধ আছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2088,17,59,'আমি নিদর্শন প্রেরণ করা হতে এজন্য বিরত থাকি যে, পূর্বের লোকেরা তা মিথ্যা মনে ক’রে প্রত্যাখ্যান করেছিল। আমি সামূদ জাতির নিকট উষ্ট্রী পাঠিয়েছিলাম এক প্রত্যক্ষ নিদর্শন হিসেবে কিন্তু তারা তার প্রতি যুলম করল; ভয় প্রদর্শনের উদ্দেশ্যেই কেবল আমি নিদর্শন পাঠিয়ে থাকি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2089,17,60,'স্মরণ কর, আমি তোমাকে বলেছিলাম যে, তোমার রবব মানুষদেরকে ঘিরে রেখেছেন। আমি তোমাকে (মি’রাজের মাধ্যমে) যে দৃশ্য দেখিয়েছি তা এবং কুরআনে উল্লেখিত অভিশপ্ত (জাক্কুম) গাছটিও মানুষদেরকে পরীক্ষা করার জন্য (যে কারা তা বিশ্বাস ক’রে নেককার হয় আর কারা তা অবিশ্বাস ক’রে পাপী হয়)। আমি তাদেরকে ভয় দেখাই ও সাবধান করি, কিন্তু তাতে তাদের চরম অবাধ্যতাই বৃদ্ধি পায়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2090,17,61,'স্মরণ কর, যখন আমি ফেরেশতাগণকে বলেছিলাম, ‘আদামকে সাজদাহ কর তখন ইবলিশ ছাড়া সবাই তাকে সাজদাহ করল। সে বলেছিল, ‘আমি কি তাকে সাজদাহ করব যাকে তুমি মাটি থেকে পয়দা করেছ?’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2091,17,62,'সে বলল, ‘আপনি কি ব্যাপারটা খেয়াল করেছেন যে, আপনি এ ব্যক্তিকে আমার উপর সম্মান দিচ্ছেন! আপনি যদি আমাকে ক্বিয়ামাতের দিন পর্যন্ত সময় দেন, তাহলে আমি অল্প কিছু বাদে তার বংশধরদেরকে অবশ্য অবশ্যই আমার কর্তৃত্বাধীনে এনে ফেলব।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2092,17,63,'আল্লাহ বললেন, ‘যাও, তাদের মধ্যে যারা তোমাকে মেনে চলবে, জাহান্নামই হবে তোমাদের সকলের প্রতিফল, পূর্ণ প্রতিফল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2093,17,64,'তাদের মধ্যে তুমি যাকে পার উস্কে দাও তোমার কথা দিয়ে, তোমার অশ্বারোহী আর পদাতিক বাহিনী দিয়ে তুমি আক্রমণ চালাও, আর তাদের ধন-সম্পদ ও সন্তানাদিতে ভাগ বসিয়ে দাও (যথেচ্ছভাবে সম্পদ উপার্জন ও ব্যয় করার পরামর্শ দিয়ে আর সন্তান কামনা ও প্রতিপালনে আল্লাহর বিধান লঙ্ঘনের উপদেশ দিয়ে) আর তাদেরকে প্রতিশ্রুতি দাও।’ শয়তান তাদেরকে যে প্রতিশ্রুতি দেয় তাতো ছলনা ছাড়া আর কিছুই নয়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2094,17,65,'‘আমার বান্দাহদের ব্যাপার হল, তাদের উপর তোমার কোন আধিপত্য চলবে না।’ কর্ম সম্পাদনে তোমার প্রতিপালকই যথেষ্ট। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2095,17,66,'তোমাদের (প্রকৃত) প্রতিপালক তো তিনিই, যিনি সমুদ্রে তোমাদের জন্য সুস্থিরভাবে নৌযান পরিচালনা করেন, যাতে তোমরা তাঁর অনুগ্রহ সন্ধান করতে পার, তিনি তোমাদের প্রতি বড়ই দয়ালু। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2096,17,67,'সমুদ্রে যখন বিপদ তোমাদেরকে পেয়ে বসে, তখন তাঁকে ছাড়া অন্য যাদেরকে তোমরা (উপাস্য ভেবে) আহবান কর তারা (তখন তোমাদের মন থেকে) হারিয়ে যায়। অতঃপর তিনি যখন তোমাদেরকে স্থলে এনে বাঁচিয়ে দেন, তখন তোমরা মুখ ফিরিয়ে নাও। মানুষ হল বড়ই অকৃতজ্ঞ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2097,17,68,'তোমরা কি নির্ভয় হয়ে গেছ যে তিনি তোমাদেরকে স্থলভাগেই যমীনের মধ্যে ধ্বসিয়ে দিবেন না, কিংবা তোমাদের উপর শিলা বর্ষণকারী ঝড়ো হাওয়া পাঠাবেন না? এমতাবস্থায় তোমাদের রক্ষাকারী কাউকে তোমরা পাবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2098,17,69,'তোমরা কি ভয়হীন হয়ে গেছ যে, তিনি তোমাদেরকে আরেকবার সমুদ্রে নিয়ে যাবেন না আর তোমাদের উপর প্রচন্ড ঝড়ো হাওয়া পাঠাবেন না আর তোমাদের অকৃতজ্ঞতার কারণে তোমাদেরকে ডুবিয়ে দেবেন না? তখন তোমরা আমার বিরুদ্ধে কোন সাহায্যকারী পাবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2099,17,70,'আমি আদাম সন্তানকে সম্মানিত করেছি, তাদের জন্য জলে স্থলে যানবাহনের ব্যবস্থা করেছি, তাদেরকে পবিত্র রিযক দিয়েছি আর আমি তাদেরকে আমার অধিকাংশ সৃষ্টির উপর মর্যাদায় শ্রেষ্ঠত্ব দান করেছি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2100,17,71,'স্মরণ কর, যেদিন আমি সকল সম্প্রদায়কে তাদের নেতাসহ ডাকব, অতঃপর যাদেরকে তাদের ‘আমলনামা ডান হাতে দেয়া হবে, তারা তাদের ‘আমালনামা পাঠ করবে (আনন্দচিত্তে) আর তাদের প্রতি এতটুকু যুলম করা হবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2101,17,72,'যে ব্যক্তি এখানে (সত্য পথ দেখার ব্যাপারে) অন্ধ, সে আখেরাতেও হবে অন্ধ, আর সঠিক পথ থেকে অধিক বিচ্যুত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2102,17,73,'আমি তোমার প্রতি যে ওয়াহী করেছি তাত্থেকে তোমাকে পদস্খলিত করার জন্য তারা চেষ্টার কোন ত্রুটি করেনি যাতে তুমি আমার সম্বন্ধে তার (অর্থাৎ নাযিলকৃত ওয়াহীর) বিপরীতে মিথ্যা রচনা কর, তাহলে তারা তোমাকে অবশ্যই বন্ধু বানিয়ে নিত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2103,17,74,'আমি তোমাকে দৃঢ় প্রতিষ্ঠিত না রাখলে তুমি তাদের দিকে কিছু না কিছু ঝুঁকেই পড়তে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2104,17,75,'তুমি তা করলে আমি তোমাকে এ দুনিয়ায় দ্বিগুণ আর পরকালেও দ্বিগুণ ‘আযাবের স্বাদ আস্বাদন করাতাম। সে অবস্থায় তুমি তোমার জন্য আমার বিরুদ্ধে কোন সাহায্যকারী পেতে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2105,17,76,'তারা তোমাকে যমীন থেকে উৎখাত করতে চেয়েছিল যাতে তারা তোমাকে তাত্থেকে বের করে দিতে পারে, সেক্ষেত্রে তারা এখানে তোমার পরে খুব অল্পকালই টিকে থাকত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2106,17,77,'তোমার পূর্বে আমি আমার যে সব রসূল পাঠিয়েছিলাম তাদের ক্ষেত্রে এটাই ছিল নিয়ম আর তুমি আমার নিয়মের কোন পরিবর্তন দেখতে পাবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2107,17,78,'সূর্য পশ্চিমে ঢলে পড়ার সময় হতে রাত্রির গাঢ় অন্ধকার পর্যন্ত নামায প্রতিষ্ঠা কর, আর ফাজরের সলাতে কুরআন পাঠ (করার নীতি অবলম্বন কর), নিশ্চয়ই ফাজরের সলাতের কুরআন পাঠ (ফেরেশতাগণের) সরাসরি সাক্ষ্য হয়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2108,17,79,'আর রাত্রির কিছু অংশে তাহাজ্জুদ পড়, ওটা তোমার জন্য নফল, শীঘ্রই তোমার প্রতিপালক তোমাকে প্রশংসিত স্থানে উন্নীত করবেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2109,17,80,'বল, ‘হে আমার প্রতিপালক! আমাকে (যেখানেই) প্রবেশ করাও, (সেটা কর) সত্য ও সম্মানের প্রবেশ, আর আমাকে (যেখান হতেই) বের কর, (সেটা কর) সত্য ও সম্মানের বহির্গমন, আর তোমার নিকট হতে আমাকে এক সাহায্যকারী শক্তি দান কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2110,17,81,'বল, ‘সত্য এসে গেছে আর মিথ্যা বিলুপ্ত হয়েছে, মিথ্যা তো বিলুপ্ত হওয়ারই।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2111,17,82,'আমি কুরআন হতে (ক্রমশঃ) অবতীর্ণ করি যা মু’মিনদের জন্য আরোগ্য ও রহমাত, কিন্তু তা যালিমদের ক্ষতিই বৃদ্ধি করে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2112,17,83,'আমি যখন মানুষের প্রতি অনুগ্রহ বর্ষণ করি তখন সে মুখ ফিরিয়ে নেয়, আর অহঙ্কারে দূরে সরে পড়ে; কিন্তু যখন অমঙ্গল তাকে স্পর্শ করে তখন সে নিরাশ হয়ে যায়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2113,17,84,'বল, ‘প্রত্যেকেই স্বীয় রীতি-পন্থা অনুযায়ী কাজ করে। এখন তোমার রববই ভাল জানেন কে চলার পথে অধিকতর সঠিক পথে আছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2114,17,85,'তোমাকে তারা রূহ সম্পর্কে জিজ্ঞেস করে। বল, ‘রূহ হচ্ছে আমার প্রতিপালকের হুকুমের অন্তর্ভুক্ত (একটি হুকুম)। এ সম্পর্কে তোমাকে অতি সামান্য জ্ঞানই দেয়া হয়েছে।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2115,17,86,'ইচ্ছে করলে আমি তোমার প্রতি যা ওয়াহী করেছি তা কেড়ে নিতে পারতাম, সে অবস্থায় তুমি আমার বিরুদ্ধে তোমার জন্য কোন কার্য সম্পাদনকারী পাবে না ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2116,17,87,'তোমার প্রতিপালকের দয়া ছাড়া। তোমার প্রতি তাঁর অনুগ্রহ (সত্যিই) বিরাট। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2117,17,88,'বল, ‘এ কুরআনের মত একখানা কুরআন আনার জন্য যদি সমগ্র মানব আর জ্বীন একত্রিত হয় তবুও তারা তার মত আনতে পারবে না, যদিও তারা পরস্পর পরস্পরকে সাহায্য ও সহযোগিতা করে।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2118,17,89,'আমি এ কুরআনে মানুষের জন্য যাবতীয় দৃষ্টান্ত বিস্তারিতভাবে বর্ণনা করেছি, কিন্তু অধিকাংশ মানুষই ঈমান গ্রহণ করতে অস্বীকার করে কেবল কুফরিই করল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2119,17,90,'তারা বলে, ‘আমরা তোমার প্রতি কক্ষনো ঈমান আনব না যে পর্যন্ত তুমি আমাদের জন্য যমীন থেকে ঝর্ণাধারা প্রবাহিত না করবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2120,17,91,'কিংবা (যতক্ষণ না) তোমার খেজুর ও আঙ্গুরের বাগান হবে যার ফাঁকে ফাঁকে তুমি ঝর্ণা প্রবাহিত করবে অজস্র ধারায়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2121,17,92,'অথবা (যতক্ষণ না) তুমি আকাশকে টুকরো টুকরো করে আমাদের উপর ফেলবে যেমন তুমি বলে থাক (যে তা ঘটবে) কিংবা আল্লাহ আর ফেরেশতাগণকে সরাসরি আমাদের সামনে এনে দেবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2122,17,93,'কিংবা (যতক্ষণ না) তোমার একটা স্বর্ণখচিত গৃহ হবে কিংবা তুমি আসমানে আরোহণ করবে। আর তোমার এ আরোহণকেও আমরা কক্ষনো বিশ্বাস করব না যতক্ষণ না তুমি আমাদের প্রতি এক কিতাব অবতীর্ণ করবে যা আমরা পাঠ করব।’ বল, ‘আমি আমার প্রতিপালকের মহিমা ও পবিত্রতা ঘোষণা করছি, আমি একজন মানুষ রসূল ছাড়া কি অন্য কিছু? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2123,17,94,'মানুষের কাছে যখন পথের নির্দেশ আসে তখন তাদেরকে ঈমান আনতে তাদের এ কথা ছাড়া অন্য কিছুই বিরত রাখে না যে, ‘আল্লাহ কি মানুষকে রসূল বানিয়ে পাঠিয়েছেন?’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2124,17,95,'বল, ‘দুনিয়াতে যদি ফেরেশতাগণের বসবাস হত যারা নিশ্চিন্তে নিরাপদে চলাফেরা করত, তাহলে অবশ্যই আমি তাদের কাছে ফেরেশতা রসূল পাঠাতাম।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2125,17,96,'বল, ‘আমার ও তোমাদের মাঝে আল্লাহর সাক্ষ্যই যথেষ্ট, তিনি তাঁর বান্দাদের সম্পর্কে পুরোপুরি ওয়াকিফহাল, আর তিনি সর্বদ্রষ্টা।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2126,17,97,'আল্লাহ যাকে সঠিক পথে পরিচালিত করেন সে পথপ্রাপ্ত আর যাদেরকে তিনি পথভ্রষ্ট করেন, তুমি কক্ষনো তাদের জন্য তাঁকে ছাড়া অন্য কাউকে অভিভাবক পাবে না। ক্বিয়ামাতের দিন আমি তাদেরকে একত্রিত করব তাদের মুখের ভরে অন্ধ, বোবা ও বধির অবস্থায়। তাদের আবাস হচ্ছে জাহান্নাম। যখনই তার আগুন নিস্তেজ হয়ে আসবে, আমি তাদের জন্য অগ্নির দহন শক্তি বৃদ্ধি করে দেব। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2127,17,98,'এটাই তাদের প্রতিফল, কেননা তারা আমার নিদর্শনসমূহ প্রত্যাখ্যান করেছিল আর বলেছিল, ‘যখন আমরা হাড্ডি ও চূর্ণ ধূলায় পরিণত হব তখনও কি আমাদেরকে নতুন এক সৃষ্টির আকারে আবার উঠানো হবে?’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2128,17,99,'তারা কি লক্ষ্য করে না যে আল্লাহ- যিনি আসমানসমূহ ও যমীন সৃষ্টি করেছেন- তিনি তাদের মত মানুষ (পুনরায়) সৃষ্টি করতে সক্ষম। তিনি তাদের জন্য একটা সুনির্দিষ্ট সময় স্থির করেছেন, যাতে কোনই সন্দেহ নেই। কিন্তু যালিমরা অমান্য করে কেবল কুফরিই করল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2129,17,100,'বল, ‘তোমরা যদি আমার প্রতিপালকের রাহমাতের ভান্ডারের মালিক হয়ে যেতে, তবুও খরচ হয়ে যাবার ভয়ে তোমরা তা অবশ্যই ধরে রাখতে।’ বাস্তবিকই মানুষ বড়ই সংকীর্ণ-চিত্ত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2130,17,101,'আমি মূসাকে নয়টি স্পষ্ট নিদর্শন দিয়েছিলাম। বানী ইসরাঈলকে জিজ্ঞেস করে দেখ, যখন সে তাদের (অর্থাৎ ফির‘আওন ও তার প্রধানদের) নিকট আসল তখন ফির‘আওন তাকে বলল, ‘ওহে মূসা! আমি তোমাকে অবশ্যই যাদুগ্রস্ত মনে করি।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2131,17,102,'মূসা বলল, ‘তুমি তো জান যে, এসব চোখ-খুলে-দেয়া নিদর্শন আসমানসমূহ ও যমীনের প্রতিপালক ছাড়া অন্য কেউ অবতীর্ণ করেনি, হে ফির‘আওন! আমি তো তোমাকে মনে করি এক ধ্বংসপ্রাপ্ত লোক।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2132,17,103,'অতঃপর ফিরআউন তাদেরকে দুনিয়া থেকে সরিয়ে দিতে চাইল। তখন আমি তাকে আর তার সঙ্গী-সাথীদের সব্বাইকে ডুবিয়ে মারলাম।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2133,17,104,'এরপর আমি বানী ইসরাঈলকে বললাম, ‘তোমরা যমীনের উপর বসবাস কর, অতঃপর যখন প্রতিশ্রুত ক্বিয়ামাত আসবে তখন আমি তোমাদেরকে সংমিশ্রিত দলবলে হাজির করব।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2134,17,105,'এ কুরআনকে আমি সত্যতা সহকারে নাযিল করেছি আর সত্যতা সহকারেই তা নাযিল হয়েছে। আমি তোমাকে শুধু সুসংবাদদাতা ও সতর্ককারী হিসেবে পাঠিয়েছি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2135,17,106,'আমি এ কুরআনকে ভাগে ভাগে বিভক্ত করেছি যাতে তুমি থেমে থেমে মানুষকে তা পাঠ করে শুনাতে পার, কাজেই আমি তা ক্রমশঃ নাযিল করেছি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2136,17,107,'বল, ‘তোমরা কুরআনে বিশ্বাস কর কিংবা বিশ্বাস না কর, ইতোপূর্বে যাদেরকে জ্ঞান দেয়া হয়েছে তাদেরকে যখন কুরআন পাঠ করে শুনানো হয়, তখন তারা অধোমুখে সাজদাহয় লুটিয়ে পড়ে।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2137,17,108,'আর তারা বলে, ‘আমাদের রব্ব মহান, পবিত্র; আমাদের রব্বের ও‘য়াদা অবশ্যই পূর্ণ হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2138,17,109,'তারা কাঁদতে কাঁদতে অধোমুখে সাজদাহয় লুটিয়ে পড়ে আর তা তাদের বিনয় ও নম্রতা বাড়িয়ে দেয়।[সাজদাহ] ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2139,17,110,'বল, ‘তোমরা আল্লাহ নামে ডাকো বা রহমান নামে ডাকো, যে নামেই তাঁকে ডাকো না কেন (সবই ভাল) কেননা সকল সুন্দর নামই তো তাঁর।’ তোমার সলাতে স্বর উচ্চ করো না, আর তা খুব নীচুও করো না, এ দু’য়ের মধ্যবর্তী পথ অবলম্বন কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2140,17,111,'বল, ‘সকল প্রশংসাই আল্লাহর যিনি সন্তান গ্রহণ করেন না, যাঁর শাসন-কর্তৃত্বে কোন অংশীদার নেই, দুর্দশাগ্রস্ত হওয়া থেকে বাঁচার জন্য যাঁর কোন অভিভাবকের প্রয়োজন হয় না। অতএব পূর্ণ শ্রেষ্ঠত্বে তাঁর শ্রেষ্ঠত্ব ঘোষণা কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2141,18,1,'সকল প্রশংসা আল্লাহর জন্য যিনি তাঁর বান্দাদের প্রতি কিতাব নাযিল করেছেন, আর তাতে কোন বক্রতার অবকাশ রাখেননি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2142,18,2,'(তিনি সেটাকে করেছেন) সত্য, স্পষ্ট ও অকাট্য তাঁর কঠিন শাস্তি সম্পর্কে সতর্ক করার জন্য। আর যারা সৎকাজ করে সেই মু’মিনদেরকে সুসংবাদ দেয়ার জন্য যে, তাদের জন্য আছে উত্তম প্রতিফল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2143,18,3,'তাতে তারা চিরকাল থাকবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2144,18,4,'আর তাদেরকে সতর্ক করার জন্য যারা বলে, ‘আল্লাহ পুত্র গ্রহণ করেছেন।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2145,18,5,'এ সম্পর্কে তাদের কোন জ্ঞান নেই, আর তাদের পিতৃ-পুরুষদেরও ছিল না। তাদের মুখ থেকে বের হয় বড়ই সাংঘাতিক কথা। তারা যা বলে তা মিথ্যে ছাড়া কিছুই নয়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2146,18,6,'তারা এ বাণীতে (কুরআনে) বিশ্বাস না করার কারণে মনে হচ্ছে (হে নাবী!) তুমি তার দুঃখে তোমার নিজের জান বিনাশ করে দেবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2147,18,7,'যমীনের উপর যা কিছু আছে আমি সেগুলোকে তার শোভা-সৌন্দর্য করেছি যাতে আমি মানুষকে পরীক্ষা করতে পারি যে, ‘আমালের ক্ষেত্রে কারা উত্তম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2148,18,8,'আমি অবশ্যই তার উপর যা আছে তা বৃক্ষলতাহীন শুকনো ধূলা মাটিতে পরিণত করব। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2149,18,9,'তুমি কি মনে কর যে, গুহা ও রকীমের অধিবাসীরা ছিল আমার নিদর্শনগুলোর মধ্যে বিস্ময়কর? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2150,18,10,'যুবকরা যখন গুহায় আশ্রয় গ্রহণ করল তখন তারা বলল, ‘হে আমাদের প্রতিপালক! তুমি তোমার নিকট হতে আমাদেরকে রহমত দান কর আর আমাদের ব্যাপারটি সুষ্ঠুভাবে সম্পাদন কর।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2151,18,11,'অতঃপর আমি তাদেরকে গুহায় ঘুমন্ত অবস্থায় কয়েক বছর রেখে দিলাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2152,18,12,'পরে তাদেরকে জাগ্রত করলাম এটা জানার জন্য যে, (গুহাবাসীরা আর যাদের যুগে তারা জাগ্রত হয়েছিল সে যুগের লোকেরা এ) দু’টি দলের মধ্যে কোনটি তাদের অবস্থানকালের সঠিক হিসাব করতে পারে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2153,18,13,'আমি তাদের সঠিক বৃত্তান্ত তোমার কাছে বর্ণনা করছি। তারা ছিল কয়েকজন যুবক, তারা তাদের প্রতিপালকের উপর ঈমান এনেছিল আর আমি তাদের হিদায়াত (ঈমানী চেতনাকে) বৃদ্ধি করে দিয়েছিলাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2154,18,14,'আর তাদের দিল মযবুত করে দিয়েছিলাম যখন তারা (মুশরিক রাজা ও তার অনুসারীদের বিরুদ্ধে) দাঁড়িয়ে গেল তখন বলল, ‘আমাদের প্রতিপালক তো তিনিই যিনি আসমানসমূহ যমীনের প্রতিপালক। আমরা কক্ষনো তাঁকে ত্যাগ করে অন্য কোন ইলাহকে ডাকব না। যদি আমরা ডাকি তাহলে মহা অপরাধের কথাই বলা হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2155,18,15,'আমাদের জাতির এই লোকেরা তাঁর পরিবর্তে বহু ইলাহ গ্রহণ করেছে, তারা তাদের ইলাহদের ব্যাপারে সুস্পষ্ট দলীল প্রমাণ পেশ করে না কেন? তার থেকে বড় যালিম আর কে হতে পারে যে আল্লাহ সম্পর্কে মিথ্যা রচনা করে? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2156,18,16,'এখন যেহেতু তোমরা তাদের থেকে আর তারা আল্লাহ ছাড়া যেগুলোর ‘ইবাদাত করে তাদের থেকে সম্পর্ক ছিন্ন করে নিয়েছ, তখন চল, গুহায় গিয়ে আশ্রয় গ্রহণ কর। তোমাদের জন্য তোমাদের প্রতিপালক তাঁর কল্যাণ বিস্তার করবেন আর তোমাদের কাজ-কর্মকে ফলদায়ক করে দেবেন।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2157,18,17,'তুমি দেখতে পেতে সূর্য উদয়ের সময় তাদের গুহা হতে ডান দিকে হেলে যেত, আর যখন তা অস্তমিত হত তখন তা তাদের থেকে বাম দিকে নেমে যেত, আর তারা ছিল গুহার অভ্যন্তরে বিশাল চত্বরে। এ হচ্ছে আল্লাহর নিদর্শনসমূহের অন্যতম। আল্লাহ যাকে সৎপথ দেখান সে সঠিকপথপ্রাপ্ত আর যাকে তিনি পথহারা করেন, তার জন্য তুমি কক্ষনো সৎপথের দিশা দানকারী অভিভাবক পাবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2158,18,18,'তুমি মনে করবে যে তারা সজাগ, অথচ তারা ছিল ঘুমন্ত, আমি তাদের ডানে বামে পার্শ্ব পরিবর্তন করাতাম। আর তাদের কুকুরটি ছিল গুহাদ্বারের সম্মুখে তার সামনের পা দু’টি প্রসারিত করে। তুমি যদি তাদেরকে তাকিয়ে দেখতে তাহলে অবশ্যই পেছন ফিরে পালিয়ে যেতে, আর অবশ্যই তাদের ভয়ে আতঙ্কিত হয়ে পড়তে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2159,18,19,'আর তাদের এই অবস্থায় আমি তাদেরকে জাগ্রত করলাম যাতে তারা পরস্পরকে জিজ্ঞাসাবাদ করে। তাদের একজন বলল, ‘তোমরা ক’দ্দিন থাকলে?’ তারা বলল, ‘আমরা একদিন কিংবা একদিনের কিছু অংশ হয়তো রয়েছি।’ (শেষে) তারা (সবাই) বলল, ‘তোমাদের প্রতিপালকই ভাল জানেন তোমরা কতকাল রয়েছ। এখন তোমাদের একজনকে তোমাদের এই মুদ্রা নিয়ে শহরে পাঠিয়ে দাও। সে যেন দেখে উত্তম খাবার কোনটি আর তাথেকে তোমাদের জন্য কিছু খাবার নিয়ে আসে। সে যেন বুদ্ধিমত্তার সঙ্গে কাজ করে, আর তোমাদের ব্যাপারে কেউ যেন কোনক্রমেই টের না পায়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2160,18,20,'যদি তারা তোমাদের কথা জেনে ফেলে তাহলে তারা তোমাদেরকে পাথর মেরে হত্যা করবে কিংবা তোমাদেরকে তাদের ধর্মে ফিরিয়ে নিবে, সে অবস্থায় তোমরা কক্ষনো কল্যাণ লাভ করতে পারবে না।’');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2161,18,21,'আমি এভাবে তাদের ব্যাপারটা লোকেদেরকে ওয়াকিফহাল করে দিলাম যাতে তারা জানতে পারে যে, আল্লাহর ওয়া‘দা সত্য, আর ক্বিয়ামাতের দিন সম্পর্কে কোন সন্দেহ নেই। যখন তারা (অর্থাৎ নগরবাসীরা) নিজেদের কর্তব্য সম্পর্কে নিজেদের মধ্যে বাদানুবাদ করছিল, (কতক) বলল, ‘তাদের উপর সৌধ নির্মাণ কর।’ তাদের প্রতিপালক তাদের সম্পর্কে ভাল জানেন। তাদের কর্তব্যকর্ম সম্পর্কে যাদের মতামত প্রাধান্য লাভ করল তারা বলল, ‘আমরা তাদের উপর অবশ্য অবশ্যই মাসজিদ নির্মাণ করব।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2162,18,22,'কতক লোক বলবে, ‘তারা ছিল তিনজন, তাদের চতুর্থটি ছিল তাদের কুকুর।’ আর কতক লোক বলবে, ‘তারা ছিল পাঁচ জন, তাদের ষষ্ঠটি ছিল তাদের কুকুর’, (এ কথা তারা বলবে) অজানা বিষয়ে সন্দেহপূর্ণ অনুমানের ভিত্তিতে। আবার তাদের কতক লোক বলবে, ‘তারা ছিল সাতজন, আর তাদের অষ্টমটি ছিল তাদের কুকুর।’ বল, ‘তাদের সংখ্যা সম্পর্কে আমার প্রতিপালকই বেশি জানেন।’ অল্প কয়জন ছাড়া তাদের সংখ্যা সম্পর্কে কেউ জানে না। কাজেই সাধারণ কথাবার্তা ছাড়া তাদের ব্যাপার নিয়ে বিতর্ক করো না, আর তাদের সম্পর্কে কারো কাছে কিছু জিজ্ঞেসও করো না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2163,18,23,'কোন বিষয় সম্পর্কে কক্ষনো বল না যে, ‘ওটা আমি আগামীকাল করব।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2164,18,24,'‘আল্লাহ ইচ্ছে করলে’ বলা ছাড়া। যদি ভুলে যাও (তবে মনে পড়ার সঙ্গে সঙ্গে) তোমার প্রতিপালককে স্মরণ কর আর বল, ‘আশা করি আমার প্রতিপালক আমাকে এর চেয়েও সত্যের নিকটবর্তী পথে পরিচালিত করবেন। (কেননা এক ব্যক্তি যেভাবেই সঠিক পথে চলুক না কেন, তার চেয়েও উত্তমভাবে পথ চলা যেতে পারে)। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2165,18,25,'আর (কারো মতে) তারা তাদের গুহায় ছিল তিনশ’ বছর আর কিছু লোক নয় বছর বাড়িয়ে নিয়েছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2166,18,26,'বল, ‘আল্লাহই ভাল জানেন তারা কতকাল (গুহায়) ছিল।’ আসমানসমূহ যমীনের অদৃশ্যের জ্ঞান তাঁরই আছে, কত স্পষ্টই না তিনি দেখেন, কত স্পষ্টই না তিনি শোনেন। তিনি ছাড়া তাদের কোন অভিভাবক নেই, তিনি তাঁর কর্তৃত্বে কাউকে অংশীদার করেন না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2167,18,27,'আর তুমি তোমার কাছে ওয়াহীকৃত তোমার প্রতিপালকের কিতাব থেকে পাঠ করে শুনাও, তাঁর কথা পরিবর্তন করে দেবে এমন কেউ নেই, আর তাঁকে ছাড়া তুমি কক্ষনো অন্য কাউকে আশ্রয়স্থল হিসেবে পাবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2168,18,28,'তুমি দৃঢ় চিত্ত হয়ে তাদের সাথে অবস্থান কর যারা সকাল-সন্ধ্যা তাদের প্রতিপালককে আহবান করে তাঁর সন্তুষ্টি লাভের সন্ধানে। পার্থিব জীবনের শোভা ও চাকচিক্য কামনায় তুমি তাদের থেকে তোমার দৃষ্টি ফিরিয়ে নিও না। তুমি তার আনুগত্য কর না যার অন্তরকে আমি আমার স্মরণ হতে উদাসীন করে দিয়েছি, যে নিজের প্রবৃত্তির আনুগত্য করে আর যার কার্যকলাপ হচ্ছে সীমালঙ্ঘনমূলক। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2169,18,29,'আর বলে দাও, ‘সত্য এসেছে তোমাদের রব্বের নিকট হতে, কাজেই যার ইচ্ছে ঈমান আনুক আর যার ইচ্ছে সত্যকে অস্বীকার করুক।’ আমি (অস্বীকারকারী) যালিমদের জন্য আগুন প্রস্তুত করে রেখেছি যার লেলিহান শিখা তাদেরকে ঘিরে ফেলেছে। তারা পানীয় চাইলে তাদেরকে গলিত শিশার ন্যায় পানি দেয়া হবে যা তাদের মুখমন্ডল দগ্ধ করবে, কতই না নিকৃষ্ট পানীয়! আর কতই না নিকৃষ্ট আশ্রয়স্থল! ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2170,18,30,'যারা ঈমান আনে আর সৎ কাজ করে- যে উত্তমভাবে কাজ করে আমি তার কর্মফল বিনষ্ট করি না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2171,18,31,'তাদের জন্য আছে স্থায়ী জান্নাত যার নিম্নদেশে ঝর্ণাধারা প্রবাহিত। সেখানে তাদেরকে অলংকৃত করা হবে স্বর্ণ কংকণে। সূক্ষ্ম ও গাঢ় রেশমের সবুজ পোশাক তারা পরিধান করবে। তারা গদি লাগানো উচ্চাসনে হেলান দিয়ে বসবে। কতই না উত্তম পুরস্কার! কতই না উত্তম আশ্রয়স্থল! ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2172,18,32,'তুমি তাদের কাছে দু’ব্যক্তির দৃষ্টান্ত বর্ণনা কর যাদের একজনকে আমি দিয়েছিলাম দু’টি আঙ্গুরের বাগান, আর ওগুলোকে খেজুর গাছ দিয়ে ঘিরে দিয়েছিলাম আর ও দু’টির মাঝে দিয়েছিলাম শষ্যক্ষেত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2173,18,33,'দু’টো বাগানই ফল দিত, এতে এতটুকু ত্রুটি করত না। এ দু’য়ের মাঝে আমি ঝর্ণাধারা প্রবাহিত করেছিলাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2174,18,34,'লোকটির উৎপাদন ছিল প্রচুর। একদিন কথাবার্তা বলার সময় সে তার প্রতিবেশীকে বলল, ‘আমি সম্পদে তোমা হতে শ্রেষ্ঠ, আর জনবলে তোমা হতে শক্তিশালী।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2175,18,35,'নিজের প্রতি যুলম করে সে তার বাগানে প্রবেশ করল। সে বলল, ‘আমি ধারণা করি না যে, এটা কোনদিন ধ্বংস হয়ে যাবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2176,18,36,'আমি মনে করি না যে কিয়ামাত হবে। আর যদি আমাকে আমার প্রতিপালকের কাছে ফিরিয়ে নেয়া হয়ই, তাহলে অবশ্য অবশ্যই আমি পরিবর্তে আরো উৎকৃষ্ট স্থান পাব। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2177,18,37,'কথার প্রসঙ্গ টেনে তার সাথী বলল, ‘তুমি কি তাঁকে অস্বীকার করছ যিনি তোমাকে মাটি থেকে সৃষ্টি করেছেন, অতঃপর শুক্র-কীট হতে, অতঃপর তোমাকে পূর্ণাঙ্গ দেহসম্পন্ন মানুষ বানিয়ে দিয়েছেন? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2178,18,38,'(আর আমার ব্যাপারে কথা হল) সেই আল্লাহই আমার প্রতিপালক, আমি কাউকে আমার প্রতিপালকের শরীক করব না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2179,18,39,'তুমি যখন তোমার বাগানে প্রবেশ করলে তখন কেন বললে না, ‘আল্লাহ যা ইচ্ছে করেছেন (তা-ই হয়েছে), আল্লাহ ছাড়া কারো কোন শক্তি নেই। যদিও তুমি আমাকে ধনে-জনে তোমার চেয়ে কম দেখ, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2180,18,40,'সম্ভবতঃ আমার প্রতিপালক আমাকে তোমার বাগান অপেক্ষাও উত্তম কিছু দান করবেন আর তোমার বাগানের উপর আসমান হতে কোন বিপদ পাঠিয়ে দিবেন, ফলে তা শূন্য ময়দানে পরিণত হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2181,18,41,'কিংবা তার পানি ভূ-গর্ভে চলে যাবে, ফলে তুমি কক্ষনো তার খোঁজ পাবে না।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2182,18,42,'ধ্বংস তার ফল-ফসলকে ঘিরে ফেলল আর তাতে সে যা খরচ করেছিল তার জন্য হাত মলতে লাগল। তা ছিন্ন ভিন্ন অবস্থায় ভূমিসাৎ হয়ে গিয়েছিল। সে বলল, ‘হায়, আমি যদি আমার রব্ব-এর সাথে কাউকে শরীক না করতাম!’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2183,18,43,'আর আল্লাহ ছাড়া তাকে সাহায্য করার কোন দলবলও ছিল না, আর সে নিজেও এর মোকাবিলা করতে পারল না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2184,18,44,'এ ব্যাপারে যাবতীয় কর্তৃত্ব ক্ষমতা সেই সত্যিকার আল্লাহর জন্যই নির্দিষ্ট। পুরস্কার দানে তিনিই উৎকৃষ্ট, আর সফল পরিণাম দানে তিনিই শ্রেষ্ঠ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2185,18,45,'তাদের কাছে দুনিয়ার এ জীবনের দৃষ্টান্ত পেশ কর : তা হল পানির মত যা তিনি আকাশ হতে বর্ষণ করেন, যা দিয়ে যমীনে গাছ-গাছড়া ঘন হয়ে উদ্গত হয়, অতঃপর তা শুকনো খড়কুটায় পরিণত হয় যাকে বাতাস উড়িয়ে নিয়ে যায়। আল্লাহ হলেন সকল বিষয়ে শক্তিমান। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2186,18,46,'ধন-সম্পদ আর সন্তানাদি পার্থিব জীবনের শোভা-সৌন্দর্য, আর তোমার প্রতিপালকের নিকট পুরস্কার লাভের জন্য স্থায়ী সৎকাজ হল উৎকৃষ্ট আর আকাঙ্ক্ষা পোষণের ভিত্তি হিসেবেও উত্তম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2187,18,47,'(সেদিনের কথা চিন্তা কর) যেদিন আমি পর্বতমালাকে চালিত করব, আর পৃথিবীকে দেখতে পাবে উন্মুক্ত প্রান্তর আর তাদের সববাইকে আমি একত্রিত করব, কাউকেও বাদ দেব না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2188,18,48,'তাদেরকে তোমার প্রতিপালকের সামনে সারিবদ্ধভাবে হাজির করা হবে (আর তাদেরকে বলা হবে), ‘তোমরা আমার কাছে এসেছ তেমনিভাবে যেভাবে আমি তোমাদেরকে প্রথমবার সৃষ্টি করেছিলাম। কিন্তু তোমরা তো ধারণা করেছিলে যে, আমার কাছে তোমাদের সাক্ষাতের নির্দিষ্টকাল আমি কক্ষনো উপস্থিত করব না।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2189,18,49,'আর ‘আমালনামা হাজির করা হবে, আর তাতে যা (লেখে রাখা আছে) তার কারণে তুমি অপরাধী লোকদেরকে দেখতে পাবে ভীত আতঙ্কিত। আর তারা বলবে, ‘হায় কপাল! এটা কেমন কিতাব যে ছোট বড় কোন কাজই ছেড়ে দেয়নি বরং সব কিছুর হিসাব রেখেছে।’ তারা যা করেছে তা সামনে উপস্থিত পাবে, আর তোমার প্রতিপালক কারো প্রতি যুলম করবেন না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2190,18,50,'স্মরণ কর, যখন আমি ফেরেশতাদেরকে বলেছিলাম, ‘আদামকে সাজদাহ কর।’ তখন ইবলিশ ছাড়া তারা সবাই সাজদাহ করল। সে ছিল জ্বীনদের অন্তর্ভুক্ত। সে তার প্রতিপালকের নির্দেশ লঙ্ঘন করল। এতদসত্ত্বেও তোমরা কি আমাকে বাদ দিয়ে তাকে আর তার বংশধরকে অভিভাবক বানিয়ে নিচ্ছ? অথচ তারা তোমাদের দুশমন। যালিমদের এই বিনিময় বড়ই নিকৃষ্ট! ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2191,18,51,'আসমান যমীনের সৃষ্টিকালে আমি তাদেরকে সাক্ষী রাখিনি, আর তাদের নিজেদের সৃষ্টি (প্রত্যক্ষ করার জন্য)ও না, পথভ্রষ্টকারীদেরকে সাহায্যকারী গ্রহণ করা আমার কাজ নয়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2192,18,52,'সেদিন তিনি বলবেন, ‘তোমরা যাদেরকে আমার শরীক মনে করতে তাদেরকে ডাক।’ তখন তারা তাদেরকে ডাকবে, কিন্তু তারা তাদের ডাকে সাড়া দেবে না। আর আমি উভয় দলের মাঝে রেখে দেব এক ধ্বংস-গহ্বর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2193,18,53,'অপরাধীরা আগুন দেখতে পাবে আর মনে করবে যে, তাদেরকে তাতে পতিত হতে হবে, কিন্তু সেখান থেকে ফিরে যাবার তারা কোনই উপায় পাবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2194,18,54,'আমি এ কুরআনে মানুষের (জ্ঞান লাভের) জন্য যাবতীয় দৃষ্টান্ত বিশদভাবে বিবৃত করেছি কিন্তু মানুষ অধিকাংশ বিষয়েই বিতর্ককারী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2195,18,55,'তাদের কাছে যখন পথের নির্দেশ আসে তখন ঈমান আনতে আর তাদের প্রতিপালকের কাছে ক্ষমা চাইতে মানুষকে এ ছাড়া আর অন্য কিছুই বাধা দেয় না যে, (তারা অপেক্ষায় থাকে যে) অতীতের জাতিগুলোর সঙ্গে যা করা হয়েছে, কখন তাদের সঙ্গেও তাই করা হবে অথবা কখন ‘আযাবকে তারা সরাসরি সামনে দেখতে পাবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2196,18,56,'আমি রসূলদেরকে পাঠিয়ে থাকি একমাত্র সুসংবাদদাতা আর সতর্ককারী হিসেবে। কিন্তু কাফিরগণ মিথ্যা যুক্তি পেশ করে বিতর্ক করছে তা দিয়ে সত্যকে দুর্বল করে দেয়ার উদ্দেশে, আর তারা আমার নিদর্শন ও ভয় দেখানোকে হাসি-তামাশার বিষয় বানিয়ে নিয়েছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2197,18,57,'তার থেকে বড় যালিম আর কে আছে যাকে তার প্রতিপালকের আয়াতগুলো স্মরণ করিয়ে দেয়া হলে সে তাত্থেকে মুখ ফিরিয়ে নেয় আর সে পূর্বে কৃত তার কর্মের (খারাপ পরিণতির) কথা ভুলে যায়। আমি তাদের অন্তরের উপর আবরণ দিয়ে দিয়েছি যাতে তারা তা (অর্থাৎ কুরআন) বুঝতে না পারে, আর তাদের কানে এঁটে দিয়েছি বধিরতা। তুমি তাদেরকে সৎপথে ডাকলেও তারা কক্ষনো সৎপথ গ্রহণ করবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2198,18,58,'তোমার প্রতিপালক ক্ষমাশীল, দয়ার আধার। তাদের কৃতকর্মের জন্য তিনি যদি তাদেরকে পাকড়াও করতেন, তাহলে তাদের উপর অবশ্যই শীঘ্রই ‘আযার পাঠাতেন, কিন্তু তাদের সঙ্গে কৃত ওয়া‘দার জন্য আছে নির্দিষ্ট সময়, যাকে পাশ কাটিয়ে তারা কক্ষনো আশ্রয়ের কোন জায়গা পাবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2199,18,59,'ঐ সব জনদপকে আমি ধ্বংস করে দিয়েছিলাম যখন তারা বাড়াবাড়ি করেছিল, আর তাদের ধ্বংস সাধনের জন্য একটা প্রতিশ্রুত সময় স্থির করেছিলাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2200,18,60,'স্মরণ কর, যখন মূসা তার সঙ্গীকে বলেছিল, ‘দুই সমুদ্রের মিলনস্থলে পৌঁছা না পর্যন্ত আমি চলতেই থাকবো যদিও বছরের পর বছর চলতেও হয়।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2201,18,61,'অতঃপর যখন তারা দু’জনে দুই সমুদ্রের মিলন স্থলে পৌঁছল, তারা তাদের মাছের কথা ভুলে গেল আর সেটি সমুদ্রে তার পথ করে নিল সুড়ঙ্গের মত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2202,18,62,'যখন তারা আরো এগিয়ে গেল, সে তার সঙ্গীকে বলল, ‘আমাদের সকালের খাবার আন, আমরা আমাদের এই সফরে বড়ই ক্লান্ত হয়ে পড়েছি।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2203,18,63,'সঙ্গীটি বলল, ‘আপনি কি লক্ষ্য করেছেন, আমরা যখন শিলাখন্ডে (বসে) ছিলাম তখন আমি মাছের কথা ভুলে গিয়েছিলাম। সেটার কথা আপনাকে বলতে শয়তানই আমাকে ভুলিয়ে দিয়েছিল আর মাছটি বিস্ময়করভাবে সমুদ্রে তার রাস্তা করে চলে গিয়েছিল।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2204,18,64,'মূসা বলল, ‘এটাই তো সে জায়গা যেটা আমরা খুঁজছি।’ কাজেই তারা তাদের পায়ের চিহ্ন ধরে ফিরে গেল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2205,18,65,'তখন তারা আমার বান্দাদের এক বান্দাকে পেল, যার প্রতি আমার পক্ষ থেকে অনুগ্রহ দান করেছিলাম আর আমার পক্ষ থেকে তাকে বিশেষ জ্ঞান দান করেছিলাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2206,18,66,'মূসা তাকে বলল, ‘আমি কি এ শর্তে আপনার অনুসরণ করব যে, আপনি আমাকে সেই (বিশেষ) জ্ঞান থেকে শিক্ষা দেবেন যে জ্ঞান আপনাকে শেখানো হয়েছে?’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2207,18,67,'সে বলল, ‘আপনি কিছুতেই আমার সাথে ধৈর্য ধারণ করতে পারবেন না।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2208,18,68,'আপনি কীভাবে সে বিষয়ে ধৈর্য ধারণ করবেন যা আপনার জ্ঞানের আয়ত্বের বাইরে?’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2209,18,69,'মূসা বলল, ‘আল্লাহ চাইলে আমাকে ধৈর্যশীলই পাবেন, আমি আপনার কোন নির্দেশই লঙ্ঘন করব না।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2210,18,70,'সে বলল, ‘আপনি যেহেতু আমার অনুসরণ করতেই চান, তাহলে আপনি আমাকে কোন ব্যাপারেই প্রশ্ন করবেন না যতক্ষণ না আমি নিজেই সে সম্পর্কে আপনাকে বলি।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2211,18,71,'অতঃপর তারা দু’জনে চলতে লাগল যতক্ষণ না তারা নৌকায় উঠল, অতঃপর লোকটি নৌকায় ছিদ্র করে দিল। মূসা বলল, ‘আপনি কি তার আরোহীদেরকে ডুবিয়ে দেয়ার জন্য তাতে ছিদ্র করলেন? আপনি তো এক অদ্ভুত কাজ করলেন।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2212,18,72,'সে বলল, ‘আমি কি আপনাকে বলিনি যে, আপনি কিছুতেই আমার সঙ্গে ধৈর্য ধরতে পারবেন না?’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2213,18,73,'মূসা বলল, ‘আমার ভুলের জন্য আমাকে পাকড়াও করবেন না, আর আমার ব্যাপারে আপনি অধিক কড়াকড়ি করবেন না।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2214,18,74,'তারপর তারা চলতে লাগল। চলতে চলতে এক বালককে তারা দেখতে পেল। তখন সে তাকে হত্যা করে ফেলল। মূসা বলল, ‘আপনি কি এক নিরাপরাধ জীবনকে কোন প্রকার হত্যার অপরাধ ছাড়াই হত্যা করে দিলেন? আপনি তো গুরুতর এক অন্যায় কাজ করে ফেললেন!’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2215,18,75,'সে বলল, ‘আমি কি আপনাকে বলিনি যে, আপনি আমার সঙ্গে কক্ষনো ধৈর্য ধরতে পারবেন না?’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2216,18,76,'মূসা বলল, ‘এরপর আমি যদি কোন বিষয়ে আপনাকে জিজ্ঞেস করি, তাহলে আপনি আর আমাকে সঙ্গে রাখবেন না, ওযর অন্যায় আমার পক্ষ থেকেই ঘটেছে।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2217,18,77,'তারপর তারা উভয়ে চলতে লাগল, চলতে চলতে তারা এক জনপদে পৌঁছে সেখানকার লোকেদের কাছে খাবার চাইল। কিন্তু তারা তাদের দু’জনকে আতিথ্য প্রদান করতে অস্বীকার করল। তারা দু’জন সেখানে একটা দেয়াল দেখতে পেল যা পড়ে যাবার উপক্রম হয়েছিল। তখন সে ব্যক্তি সেটিকে সুপ্রতিষ্ঠিত করে দিল। মূসা বলল, ‘আপনি ইচ্ছে করলে এর জন্য পারিশ্রমিক নিতে পারতেন।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2218,18,78,'লোকটি বলল, ‘এখানেই আপনার সাথে আমার সম্পর্কের বিচ্ছেদ ঘটল। এখন আমি আপনাকে ব্যাখ্যা জানিয়ে দেব যে বিষয়ে আপনি ধৈর্য ধরতে পারেননি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2219,18,79,'নৌকাটির ব্যাপার হল- তা ছিল কয়েকজন গরীব লোকের, তারা সমুদ্রে জীবিকার জন্য শ্রম করত। আমি সেটাকে খুঁতযুক্ত করতে চাইলাম, কারণ তাদের পশ্চাতে ছিল এক রাজা, সে জোরপূর্বক সব নৌকা কেড়ে নিত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2220,18,80,'আর বালকটির ব্যাপার হল- তার মাতা-পিতা ছিল মু’মিন। আমরা শঙ্কা করলাম যে, সে তার বিদ্রোহমূলক আচরণ ও (আল্লাহর প্রতি) কুফুরী দ্বারা তাদেরকে কষ্ট দেবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2221,18,81,'এ কারণে আমরা চাইলাম যে, তাদের প্রতিপালক তাদেরকে তার পরিবর্তে অধিক পবিত্র ও দয়া-মায়ায় অধিক ঘনিষ্ঠ (সন্তান) দান করবেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2222,18,82,'আর ঐ দেয়ালটির বিষয় হল- তা ছিল ঐ শহরের দু’জন ইয়াতীম বালকের। তার নীচে ছিল তাদের জন্য রক্ষিত ধন, তাদের পিতা ছিল এক সৎ ব্যক্তি। তাই তোমার প্রতিপালক চাইলেন তারা দু’জন যৌবনে উপনীত হোক আর তাদের গচ্ছিত ধন বের করে নিক- যা হল তোমার প্রতিপালকের রহমত বিশেষ। এ সব আমি নিজের পক্ষ থেকে করিনি। এ হল সে বিষয়ের ব্যাখ্যা যে সম্পর্কে আপনি ধৈর্য ধারণ করতে পারেননি।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2223,18,83,'তোমাকে তারা যুলকারনাইন সম্পর্কে জিজ্ঞেস করছে। বল, ‘আমি তার বিষয় তোমাদের নিকট কিছু বর্ণনা করব।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2224,18,84,'আমি তাকে পৃথিবীতে আধিপত্য দান করেছিলাম আর তাকে সব রকমের উপায় উপাদান দিয়েছিলাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2225,18,85,'একবার সে এক রাস্তা ধরল (অর্থাৎ একদিকে একটা অভিযান চালাল)। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2226,18,86,'চলতে চলতে যখন সে সূর্যাস্তের স্থানে পৌঁছল, তখন সে সূর্যকে অস্বচ্ছ জলাশয়ে ডুবতে দেখল আর সেখানে একটি জাতির লোকেদের সাক্ষাৎ পেল। আমি বললাম, ‘হে যুলকারনাইন! তুমি তাদেরকে শাস্তি দিতে পার কিংবা তাদের সঙ্গে (সদয়) ব্যবহারও করতে পার।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2227,18,87,'সে বলল, ‘যে ব্যক্তি যুলম করবে আমি তাকে অচিরেই শাস্তি দেব, অতঃপর তাকে তার প্রতিপালকের কাছে ফিরিয়ে আনা হবে, তখন তিনি তাকে কঠিন ‘আযাব দেবেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2228,18,88,'আর যে ব্যক্তি ঈমান আনবে আর সৎকাজ করবে তার জন্য আছে উত্তম পুরস্কার আর আমি তাকে সহজ কাজের নির্দেশ দেব। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2229,18,89,'তারপর সে আরেক পথ ধরল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2230,18,90,'চলতে চলতে সে সূর্যোদয়ের স্থানে পৌঁছল। সে সূর্যকে এমন এক জাতির উপর উদয় হতে দেখতে পেল, আমি যাদের জন্য সূর্য থেকে বাঁচার কোন আড়ালের ব্যবস্থা করিনি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2231,18,91,'এই হল তাদের অবস্থা। তার সামনে যা ছিল আমি সে সম্পর্কে ছিলাম সম্পূর্ণ অবহিত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2232,18,92,'এরপর সে আরেক পথ ধরল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2233,18,93,'চলতে চলতে সে দু’ পাহাড়ের মাঝে এসে পৌঁছল। সেখানে সে এক সম্প্রদায়কে দেখতে পেল। যারা কথাবার্তা কমই বুঝতে পারে।  ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2234,18,94,'তারা বলল, ‘হে যুলক্বারনায়ন! ইয়াজূজ মা’জূজ পৃথিবীতে অশান্তি সৃষ্টি করছে, অতএব আমরা কি আপনাকে কর দেব যে, আপনি আমাদের ও তাদের মাঝে একটা বাঁধ নির্মাণ করে দেবেন?’  ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2235,18,95,'সে বলল, ‘আমাকে আমার প্রতিপালক যা দিয়েছেন তা-ই যথেষ্ট, কাজেই তোমরা আমাকে শক্তি-শ্রম দিয়ে সাহায্য কর, আমি তোমাদের ও তাদের মাঝে এক সুদৃঢ় প্রাচীর গড়ে দেব। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2236,18,96,'আমার কাছে লোহার পাত এনে দাও।’ শেষ পর্যন্ত যখন সে দু’পাহাড়ের মাঝের ফাঁকা জায়গা পুরোপুরি ভরাট করে দিল সে বলল, ‘তোমরা হাপরে দম দিতে থাক।’ শেষ পর্যন্ত যখন তা আগুনের মত লাল হয়ে গেল তখন সে বলল, ‘আনো, আমি এর উপর গলিত তামা ঢেলে দেব।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2237,18,97,'এরপর তারা (অর্থাৎ ইয়াজূজ-মা‘জুজ) তা অতিক্রম করতে পারবে না, আর তা ভেদ করতেও পারবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2238,18,98,'সে বলল, ‘এ আমার প্রতিপালকের করুণা, যখন আমার প্রতিপালকের ওয়া‘দার নির্দিষ্ট সময় আসবে, তখন তিনি তাকে ধূলিসাৎ করে দেবেন আর আমার প্রতিপালকের ওয়া‘দা সত্য। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2239,18,99,'আমি তাদেরকে সেদিন এমন অবস্থায় ছেড়ে দেব যে, তারা একদল আরেক দলের উপর তরঙ্গমালার মত পড়বে। আর শিঙ্গায় ফুঁক দেয়া হবে। অতঃপর আমরা সব মানুষকে একসঙ্গে একত্রিত করব। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2240,18,100,'আমি সেদিন জাহান্নামকে কাফিরদের জন্য সরাসরি হাযির করব। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2241,18,101,'আমার স্মরণ থেকে যাদের চক্ষু ছিল আবরণে ঢাকা আর তারা শুনতেও সক্ষম ছিল না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2242,18,102,'যারা কুফুরী নীতি গ্রহণ করেছে তারা কি মনে করে যে, তারা আমার পরিবর্তে আমার বান্দাহদেরকে অভিভাবকরূপে গ্রহণ করবে? আমি কাফিরদের মেহমানদারির জন্য জাহান্নাম প্রস্তুত করে রেখেছি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2243,18,103,'বল, ‘আমি তোমাদেরকে কি সংবাদ দেব নিজেদের ‘আমালের ক্ষেত্রে কারা সবচেয়ে বেশি ক্ষতিগ্রস্ত?’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2244,18,104,'তারা হল সে সব লোক দুনিয়ার জীবনে যাদের চেষ্টা সাধনা ব্যর্থ হয়ে গেছে আর তারা নিজেরা মনে করছে যে, তারা সঠিক কাজই করছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2245,18,105,'তারা হল সে সব লোক যারা তাদের প্রতিপালকের নিদর্শন ও তাঁর সাথে সাক্ষাৎকে অমান্য করে। যার ফলে তাদের যাবতীয় ‘আমাল নিস্ফল হয়ে গেছে। কিয়ামাতের দিন আমি তাদের (কাজের) জন্য কোন ওজন কায়িম করব না (অর্থাৎ তাদের এ সব ‘আমাল ওজনযোগ্য হিসেবে গণ্য করা হবে না)। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2246,18,106,'এটাই তাদের প্রতিফল-জাহান্নাম, কারণ তারা কুফুরী করেছে আর আমার নিদর্শন ও রসূলদেরকে হাসি-তামাশার বিষয় বানিয়েছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2247,18,107,'যারা ঈমান আনে আর সৎকাজ করে তাদের আপ্যায়নের জন্য আছে ফিরদাউসের বাগান। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2248,18,108,'সেখানে তারা স্থায়ী হয়ে থাকবে, সেখান থেকে বের হয়ে আর অন্যত্র যেতে চাইবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2249,18,109,'বল, ‘সমুদ্রগুলো যদি আমার প্রতিপালকের কথা লেখার জন্য কালি হয়ে যায়, তবে আমার প্রতিপালকের কথা লেখা শেষ হওয়ার আগেই সমুদ্র অবশ্যই নিঃশেষ হয়ে যাবে, আমি যদি এর সাহায্যের জন্য আরো অনুরূপ পরিমাণ সমুদ্র নিয়ে আসি তবুও।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2250,18,110,'বল, ‘আমি তোমাদেরই মত একজন মানুষ, আমার নিকট ওয়াহী করা হয় যে, তোমাদের ইলাহ কেবল এক ইলাহ। কাজেই যে ব্যক্তি তার প্রতিপালকের সঙ্গে সাক্ষাতের আশা করে, সে যেন সৎ ‘আমাল করে আর তার প্রতিপালকের ‘ইবাদাতে কাউকে শরীক না করে।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2251,19,1,'কাফ্-হা-ইয়্যা-‘আইন-সাদ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2252,19,2,'এটা তোমার প্রতিপালকের অনুগ্রহের বিবরণ তাঁর বান্দাহ যাকারিয়্যার প্রতি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2253,19,3,'যখন সে তার প্রতিপালককে আহবান করেছিল- এক গোপন আহবানে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2254,19,4,'সে বলেছিল, ‘হে আমার প্রতিপালক! আমার হাড়গুলো দুর্বল হয়ে গেছে, আর বার্ধক্যে আমার মস্তক সাদা হয়ে গেছে, হে আমার প্রতিপালক! তোমাকে ডেকে আমি কখনো বিফল হইনি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2255,19,5,'আমার পরে আমার স্বগোত্রীয়রা (কী করবে) সে সম্পর্কে আমি আশঙ্কাবোধ করছি, আর আমার স্ত্রী হল বন্ধ্যা, কাজেই তুমি তোমার তরফ থেকে আমাকে একজন উত্তরাধিকারী দান কর ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2256,19,6,'যে আমার উত্তরাধিকারী হবে আর উত্তরাধিকারী হবে ইয়া‘কুব পরিবারের; আর হে আমার প্রতিপালক! তাকে করুন আপনার সন্তুষ্টির পাত্র। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2257,19,7,'তিনি বললেন, ‘হে যাকারিয়া! আমি তোমাকে একটি পুত্রের শুভ সংবাদ দিচ্ছি যার নাম হবে ইয়াহইয়া, পূর্বে এ নামে আমি কাউকে আখ্যায়িত করিনি।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2258,19,8,'সে বলল, ‘হে আমার পালনকর্তা! আমার পুত্র হবে কেমন করে, আমার স্ত্রী তো বন্ধ্যা, আর আমি বার্ধক্যের শেষ স্তরে পৌঁছে গেছি।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2259,19,9,'তিনি বললেন, ‘এভাবেই হবে, তোমার প্রতিপালক বলেছেন, ‘এটা আমার পক্ষে সহজ। ইতোপূর্বে আমিই তোমাকে সৃষ্টি করেছি যখন তুমি কিছুই ছিলে না।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2260,19,10,'সে বলল, ‘হে আমার পালনকর্তা! আমার জন্য একটা চিহ্ন স্থির করে দিন।’ তিনি বললেন, ‘তোমার চিহ্ন এই যে, তুমি তিন রাত মানুষের সঙ্গে কথা বলবে না যদিও তুমি কথা বলতে সক্ষম।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2261,19,11,'অতঃপর সে তার কুঠরি থেকে বের হয়ে তার সম্প্রদায়ের কাছে গেল এবং ইশারায় তাদেরকে সকাল-সন্ধ্যায় আল্লাহর প্রশংসা-পবিত্রতা বর্ণনা করতে বলল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2262,19,12,'(তার পুত্রের কাছে নির্দেশ আসল) ‘হে ইয়াহ্ইয়া! এ কিতাব (তাওরাত) সুদৃঢ়ভাবে ধারণ কর।’ আমি তাকে বাল্য কালেই জ্ঞান দান করেছিলাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2263,19,13,'আর (দিয়েছিলাম) আমার পক্ষ থেকে দয়া-মায়া ও পবিত্রতা। সে ছিল আল্লাহভীরু ।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2264,19,14,'আর পিতা-মাতার প্রতি সদয়, সে দাম্ভিক, অবাধ্য ছিল না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2265,19,15,'তার উপর শান্তি যেদিন সে জন্মেছে, যেদিন তার মৃত্যু হবে আর যেদিন সে জীবন্ত হয়ে উত্থিত হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2266,19,16,'এ কিতাবে (উল্লেখিত) মারইয়ামের কাহিনী বর্ণনা কর- যখন সে তার পরিবারবর্গ হতে আলাদা হয়ে পূর্ব দিকে এক জায়গায় আশ্রয় নিয়েছিল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2267,19,17,'সে তাদের থেকে (আড়াল করার জন্য) পর্দা টানিয়ে দিল। তখন আমি তার কাছে আমার রূহ্কে (অর্থাৎ জিবরীলকে) পাঠিয়ে দিলাম। তখন সে (অর্থাৎ জিবরীল) তার সামনে পূর্ণ মানুষের আকৃতি ধারণ করল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2268,19,18,'মারইয়াম বলল, ‘আমি তোমা হতে দয়াময় আল্লাহর আশ্রয় প্রার্থনা করছি যদি তুমি আল্লাহকে ভয় কর’ (তবে আমার নিকট এসো না)।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2269,19,19,'সে বলল, ‘আমি তোমার প্রতিপালক কর্তৃক প্রেরিত হয়েছি তোমাকে একটি পুত-পবিত্র পুত্র দানের উদ্দেশ্যে।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2270,19,20,'সে বলল, ‘কেমন করে আমার পুত্র হতে পারে, যখন কোন মানুষ আমাকে স্পর্শ করেনি, আর আমি অসতীও নই।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2271,19,21,'সে বলল, ‘এভাবেই হবে, তোমার প্রতিপালক বলেছেন- ‘ওটা আমার জন্য সহজ, আমি তাকে মানুষের জন্য নিদর্শন বানাতে চাই আর আমার পক্ষ থেকে এক রহমত, এ ব্যাপারে সিদ্ধান্ত হয়ে গেছে।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2272,19,22,'অতঃপর ছেলে তার গর্ভে আসল। তখন সে তা নিয়ে দূরবর্তী জায়গায় চলে গেল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2273,19,23,'সন্তান প্রসবের বেদনা তাকে এক খেজুর বৃক্ষতলের দিকে তাড়িত করল। সে বলে উঠল, ‘হায়! এর আগেই যদি আমি মরে যেতাম আর (মানুষের) স্মৃতি থেকে পুরোপুরি মুছে যেতাম!’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2274,19,24,'নিম্নদিক থেকে তাকে ডাক দেয়া হল, ‘তুমি দুঃখ করো না, তোমার প্রতিপালক তোমার পাদদেশ দিয়ে এক নির্ঝরিণী প্রবাহিত করে দিয়েছেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2275,19,25,'খেজুর গাছের কান্ড ধরে তুমি তোমার দিকে নাড়া দাও, তা তোমার উপর তাজা পরিপক্ক খেজুর পতিত করবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2276,19,26,'অতঃপর খাও, পান কর আর (তোমার) চোখ জুড়াও। যদি তুমি কোন লোককে দেখতে পাও তাহলে বলবে- আমি রহমান আল্লাহর জন্য সাওম পালনের মানৎ করেছি, কাজেই আমি কোন মানুষের সঙ্গে  আজ কিছুতেই কথা বলব না।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2277,19,27,'অতঃপর সে তার সন্তানকে বয়ে নিয়ে তার সম্প্রদায়ের কাছে আসল। তারা বলল, ‘হে মারইয়াম! তুমি তো এক অদ্ভুত জিনিস নিয়ে এসেছ! ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2278,19,28,'ওহে হারূনের বোন! তোমার পিতা তো খারাপ লোক ছিল না, আর তোমার মাও ছিল না কোন অসতী নারী।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2279,19,29,'তখন মারইয়াম তার ছেলের দিকে ইশারা করল। তারা বলল, ‘আমরা কোলের বাচ্চার সঙ্গে কীভাবে কথা বলব?’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2280,19,30,'শিশুটি বলে উঠল, ‘আমি আল্লাহর বান্দাহ, তিনি আমাকে কিতাব দিয়েছেন, আর আমাকে নবী করেছেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2281,19,31,'আমি যেখানেই থাকি না কেন তিনি আমাকে বরকতময় করেছেন আর আমাকে নামায ও যাকাতের হুকুম দিয়েছেন- যতদিন আমি জীবিত থাকি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2282,19,32,'আর (তিনি আমাকে করেছেন) আমার মাতার প্রতি সদয়, তিনি আমাকে দাম্ভিক হতভাগ্য করেননি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2283,19,33,'আমার উপর আছে শান্তি যেদিন আমি জন্মেছি, যেদিন আমার মৃত্যু হবে আর আমি যেদিন জীবিত হয়ে উত্থিত হব।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2284,19,34,'এই হচ্ছে মারইয়াম-পুত্র ঈসা, (এটাই) সত্য কথা যে বিষয়ে লোকেরা সন্দেহ পোষণ করে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2285,19,35,'সন্তান গ্রহণ করা আল্লাহর কাজ নয়, তিনি পবিত্র, মহান; যখন তিনি কিছু করার সিদ্ধান্ত করেন তখন তার জন্য শুধু বলেন, ‘হয়ে যাও’, আর তা হয়ে যায়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2286,19,36,'আল্লাহ্ই আমার ও তোমাদের প্রতিপালক, কাজেই তোমরা তাঁরই ‘ইবাদাত কর, এটাই সরল সুদৃঢ় পথ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2287,19,37,'অতঃপর দলগুলো তাদের মধ্যে মতভেদ করল। কাজেই সেই ভয়াবহ দিনের উপস্থিতকালে কাফিরদের জন্য ধ্বংস অনিবার্য। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2288,19,38,'যেদিন তারা আমার কাছে আসবে সেদিন কতই না স্পষ্টভাবে তারা শুনতে পাবে আর দেখতে পাবে। কিন্তু যালিমরা আজ স্পষ্ট গুমরাহীর মধ্যে পড়ে আছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2289,19,39,'বিপদের দিন সম্পর্কে তাদেরকে সতর্ক করে দাও যখন ব্যাপার চূড়ান্ত করা হবে আর (দেখ) এ সম্পর্কে তারা উদাসীন আর তারা ঈমান আনে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2290,19,40,'যমীন আর যমীনের উপর যারা আছে তাদের মালিকানা আমারই থাকবে, আর তারা আমার কাছেই ফিরে আসবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2291,19,41,'এ কিতাবে উল্লেখিত ইবরাহীমের কথা স্মরণ কর, সে ছিল একজন সত্যনিষ্ঠ মানুষ, একজন নবী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2292,19,42,'যখন সে তার পিতাকে বলেছিল, ‘হে আমার পিতা! আপনি কেন এমন জিনিসের ‘ইবাদাত করেন যা শুনে না, দেখে না, আর আপনার কোন কাজেই আসে না? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2293,19,43,'হে আমার পিতা! আমার কাছে এমন জ্ঞান এসেছে যা আপনার কাছে আসেনি, কাজেই আমার অনুসরণ করুন, আমি আপনাকে সরল সঠিক পথ দেখাব। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2294,19,44,'হে আমার পিতা! আপনি শয়তানের ‘ইবাদাত করবেন না, শয়তান হচ্ছে দয়াময়ের বিরুদ্ধে বিদ্রোহী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2295,19,45,'হে আমার পিতা! আমার ভয় হয় যে, দয়াময়ের ‘আযাব আপনাকে ধরে বসবে, তখন আপনি শয়তানের বন্ধু হয়ে যাবেন।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2296,19,46,'পিতা বলল, ‘হে ইবরাহীম! তুমি কি আমার দেবদেবীগুলো থেকে মুখ ফিরিয়ে নিচ্ছ? যদি তুমি বিরত না হও তবে আমি অবশ্যই তোমাকে পাথরের আঘাতে মেরে ফেলব। তুমি চিরতরে আমার কাছ থেকে দূর হয়ে যাও।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2297,19,47,'ইবরাহীম বলল, ‘আপনার প্রতি সালাম, আমি আমার প্রতিপালকের নিকট আপনার জন্য ক্ষমা প্রার্থনা করব, তিনি আমার প্রতি বড়ই মেহেরবান। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2298,19,48,'আমি পরিত্যাগ করছি আপনাদেরকে আর আপনারা আল্লাহকে বাদ দিয়ে যাদেরকে ডাকেন তাদেরকে। আমি আমার প্রতিপালককে ডাকি, আশা করি আমি আমার প্রতিপালককে ডেকে বঞ্চিত হব না।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2299,19,49,'অতঃপর সে যখন তাদেরকে আর তারা আল্লাহকে বাদ দিয়ে যাদের ‘ইবাদাত করত তাদেরকে পরিত্যাগ করল, তখন আমি তাকে দান করলাম ইসহাক্ব আর ইয়া‘কূব আর তাদের প্রত্যেককে নবী করলাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2300,19,50,'আমি তাদেরকে দান করলাম আমার অনুগ্রহ আর সত্যিকার নাম-যশের সুউচ্চ সুখ্যাতি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2301,19,51,'এ কিতাবে উল্লেখিত মূসার কথা স্মরণ কর, সে ছিল বিশেষভাবে মনোনীত, আর একজন রসূল ও নবী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2302,19,52,'আমি তাকে ডাক দিয়েছিলাম তূর পাহাড়ের ডান পার্শ্ব থেকে আর গোপনে কথাবার্তা বলার জন্য তাকে নিকটবর্তী করেছিলাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2303,19,53,'আর আমি স্বীয় অনুগ্রহে তাকে দান করেছিলাম তার ভাই হারূনকে, সেও ছিল একজন নবী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2304,19,54,'এ কিতাবে উল্লেখিত ইসমাঈলের কথা স্মরণ কর, সে ছিল ওয়া‘দা রক্ষায় (দৃঢ়) সত্যবাদী, আর ছিল একজন রসূল ও নবী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2305,19,55,'সে তার পরিবারবর্গকে নামায ও যাকাতের হুকুম দিত আর সে ছিল তার প্রতিপালকের নিকট সন্তুষ্টির পাত্র। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2306,19,56,'এ কিতাবে উল্লেখিত ইদরীসের কথা স্মরণ কর, সে ছিল সত্যনিষ্ঠ, একজন নবী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2307,19,57,'আমি তাকে উচ্চ মর্যাদায় উন্নীত করেছিলাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2308,19,58,'এরাই হল তারা আদাম বংশের নবীগণের মধ্য হতে, আর নূহের সঙ্গে যাদেরকে (নৌকায়) আরোহণ করিয়েছিলাম তাদের মধ্য হতে, আর ইবরাহীম ও ইসরাঈলের বংশধরদের মধ্য হতে যাদেরকে আমি অনুগ্রহ করেছিলাম, এরা তাদেরই মধ্য হতে, যাদেরকে আমি পথনির্দেশ দিয়েছিলাম আর বেছে নিয়েছিলাম, এদের নিকট দয়াময়ের আয়াত আবৃত্তি করা হলে তারা সাজদায় অবনত হয়ে কান্নাভরে লুটিয়ে পড়ত। [সাজদাহ] ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2309,19,59,'অতঃপর তাদের পর এল অপদার্থ পরবর্তীরা, তারা নামায হারালো, আর লালসার বশবর্তী হল। তারা অচিরেই ধ্বংসের সম্মুখীন হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2310,19,60,'তারা বাদে যারা তাওবাহ করবে, ঈমান আনবে আর সৎ কাজ করবে। ফলে এরাই জান্নাতে প্রবেশ করবে, এদের প্রতি এতটুকু যুলম করা হবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2311,19,61,'স্থায়ী জান্নাত, দয়াময় তাঁর বান্দাহ্কে যে অদৃশ্য বিষয়ের প্রতিশ্রুতি দিয়েছেন, তাঁর প্রতিশ্রুতি অবশ্যই পূর্ণ হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2312,19,62,'সেখানে তারা শান্তির সম্ভাষণ ছাড়া কোন অপবাক্য শুনবে না। আর সকাল-সন্ধ্যা সেখানে তাদের জন্য থাকবে জীবন ধারণের উপকরণ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2313,19,63,'এই হল সেই জান্নাত আমি যার উত্তরাধিকারী করব আমার বান্দাহদের মধ্যে মুত্তাকীদেরকে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2314,19,64,'(ফেরেশতাগণ বলে) ‘আমরা আপনার প্রতিপালকের হুকুম ছাড়া অবতরণ করি না, যা আমাদের সামনে আছে, আর যা আমাদের পেছনে আছে আর এ দু’য়ের মাঝে যা আছে তা তাঁরই, আপনার প্রতিপালক কক্ষনো ভুলে যান না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2315,19,65,'তিনি আকাশ, যমীন আর এ দু’য়ের মাঝে যা আছে তার প্রতিপালক, কাজেই তুমি তাঁর ‘ইবাদাত কর, আর তাঁর ‘ইবাদাতে নিয়মিত ও দৃঢ় থাক। তুমি কি তাঁর নামের গুণসম্পন্ন অন্য আর কেউ আছে বলে জান? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2316,19,66,'মানুষ বলে, ‘কী! আমি যখন মরে যাব, আমাকে তখন কি জীবিত করে উঠানো হবে?’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2317,19,67,'মানুষ কি স্মরণ করে না যে, আমি পূর্বে তাকে সৃষ্টি করেছি আর সে তখন কিছুই ছিল না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2318,19,68,'কাজেই তোমার রব্বের কসম! আমি তাদেরকে অবশ্য অবশ্যই একত্রিত করব আর শয়তানদেরকেও। অতঃপর তাদেরকে জাহান্নামের চতুষ্পার্শ্বে নতজানু অবস্থায় অবশ্য অবশ্যই হাজির করব। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2319,19,69,'অতঃপর প্রত্যেক দল হতে দয়াময়ের প্রতি সবচেয়ে অবাধ্যকে অবশ্য অবশ্যই টেনে বের করব। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2320,19,70,'আর আমি অবশ্য অবশ্যই খুব ভাল করে জানি তাদের মধ্যে কারা জাহান্নামে দগ্ধ হওয়ার সর্বাধিক উপযুক্ত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2321,19,71,'তোমাদের মধ্যে এমন কেউ নেই যাকে জাহান্নাম অতিক্রম করতে হবে না, এটা তোমার প্রতিপালকের অনিবার্য ফয়সালা। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2322,19,72,'অতঃপর মুত্তাকীদেরকে আমি রক্ষা করব আর যালিমদেরকে তার মধ্যে নতজানু অবস্থায় রেখে দেব। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2323,19,73,'আমার স্পষ্ট আয়াত যখন তাদের নিকট আবৃত্তি করা হয় তখন কাফিররা মু’মিনদেরকে বলে, ‘দু’টি দলের মধ্যে মর্যাদায় কোনটি শ্রেষ্ঠ আর মজলিস হিসেবে অধিক উত্তম?’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2324,19,74,'আমি তাদের পূর্বে বহু মানব বংশকে ধ্বংস করেছি যারা উপায় উপকরণে আর বাইরের চাকচিক্যে তাদের অপেক্ষা শ্রেষ্ঠ ছিল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2325,19,75,'বল, যারা গুমরাহীতে পড়ে আছে, দয়াময় তাদের জন্য (রশি) ঢিল দিয়ে দেন, যে পর্যন্ত না তারা দেখতে পাবে যার ওয়া‘দা তাদেরকে দেয়া হচ্ছে- তা শাস্তিই হোক কিংবা ক্বিয়ামতই হোক।’ তখন তারা জানতে পারবে মর্যাদায় কে নিকৃষ্ট আর জনবলে দুর্বল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2326,19,76,'যারা সৎপথ সন্ধান করে আল্লাহ তাদেরকে অধিক হিদায়াত দান করেন। আর স্থায়ী সৎকর্মসমূহ তোমার পালনকর্তার নিকট সাওয়াবের দিক দিয়ে শ্রেষ্ঠ আর প্রতিদান হিসেবেও শ্রেষ্ঠ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2327,19,77,'তুমি কি লক্ষ্য করেছ সে ব্যক্তিকে যে আমার নিদর্শনসমূহকে প্রত্যাখ্যান করেছে আর সে বলে, ‘আমাকে অবশ্য অবশ্যই সম্পদ আর সন্তানাদি দেয়া হবে।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2328,19,78,'সে কি অদৃশ্য বিষয় জেনে ফেলেছে, নাকি দয়াময়ের নিকট থেকে প্রতিশ্রুতি লাভ করেছে? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2329,19,79,'কক্ষনো না, তারা যা বলে আমি তা লিখে রাখব আর আমি তার শাস্তি বাড়াতেই থাকব, বাড়াতেই থাকব। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2330,19,80,'সে যে বিষয়ের কথা বলে (তার সম্পদ-সরঞ্জাম ও জনবলের গর্ব করে) তা থাকবে আমার অধিকারে আর সে আমার কাছে আসবে নিঃসঙ্গ, একাকী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2331,19,81,'তারা আল্লাহকে বাদ দিয়ে অন্যান্য ইলাহ গ্রহণ করেছে যাতে তারা (অর্থাৎ ঐ কল্পিত মা‘বুদগুলো) তাদের জন্য পৃষ্ঠপোষক হয়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2332,19,82,'কক্ষনো না, তারা তাদের ‘ইবাদাত অস্বীকার করবে আর তাদের বিরোধী হয়ে যাবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2333,19,83,'তুমি কি লক্ষ্য কর না যে, আমি কাফিরদের জন্য শয়ত্বানকে ছেড়ে রেখেছি তাদেরকে মন্দ কর্ম করতে প্ররোচিত করার জন্য। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2334,19,84,'কাজেই তাদের ব্যাপারে তাড়াহুড়ো করো না, আমি গুণে রাখছি তাদের জন্য নির্দিষ্ট (দিবসের) সংখ্যা। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2335,19,85,'যেদিন মুত্তাকীদেরকে দয়াময়ের নিকট একত্রিত করব সম্মানিত অতিথি হিসেবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2336,19,86,'আর আমি অপরাধীদেরকে জাহান্নামের দিকে তাড়িয়ে নিয়ে যাব (যেভাবে পিপাসার্ত গরু বাছুরকে পানির দিকে তাড়িয়ে নেয়া হয়)। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2337,19,87,'দয়াময়ের নিকট যে প্রতিশ্রুতি লাভ করেছে সে ছাড়া অন্য কারো সুপারিশ করার অধিকার থাকবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2338,19,88,'তারা বলে, ‘দয়াময় সন্তান গ্রহণ করেছেন।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2339,19,89,'(এমন কথা ব’লে) তোমরা তো এক ভয়ানক বিষয়ের অবতারণা করেছ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2340,19,90,'যাতে আকাশ বিদীর্ণ হওয়ার, পৃথিবী খন্ড খন্ড হওয়ার আর পর্বতমালা চূর্ণ বিচূর্ণ হয়ে পতিত হওয়ার, উপক্রম হয়েছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2341,19,91,'কারণ তারা দয়াময়ের প্রতি সন্তান আরোপ করে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2342,19,92,'অথচ দয়াময়ের মহান মর্যাদার জন্য এটা শোভনীয় নয় যে, তিনি সন্তান গ্রহণ করবেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2343,19,93,'আকাশ আর যমীনে এমন কেউ নেই যে, দয়াময়ের নিকট বান্দাহ হয়ে হাযির হবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2344,19,94,'তিনি তাদেরকে পরিবেষ্টন করে রেখেছেন আর তাদেরকে বিশেষভাবে গুণে গুণে রেখেছেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2345,19,95,'কিয়ামাতের দিন তাদের সবাই তাঁর কাছে আসবে একাকী অবস্থায়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2346,19,96,'যারা ঈমান আনে ও সৎ কাজ করে, দয়াময় তাদের প্রতি ভালবাসা দান করবেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2347,19,97,'আমি তোমার ভাষায় কুরআনকে সহজ করেছি যাতে তুমি তার সাহায্যে মুত্তাকীদেরকে সুসংবাদ দিতে পার আর ঝগড়াটে লোকেদেরকে সতর্ক করতে পার। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2348,19,98,'তাদের পূর্বে আমি কত মানব বংশকে ধ্বংস করে দিয়েছি, তুমি তাদের একজনকেও কি (এখন) দেখতে পাও অথবা তাদের ক্ষীণতম আওয়াজও কি শুনতে পাও? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2349,20,1,'ত্ব-হা-। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2350,20,2,'তোমাকে ক্লেশ দেয়ার জন্য আমি তোমার প্রতি কুরআন নাযিল করিনি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2351,20,3,'বরং তা (নাযিল করেছি) কেবল সতর্কবাণী হিসেবে যে (আল্লাহকে) ভয় করে তার জন্য। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2352,20,4,'যিনি পৃথিবী ও সুউচ্চ আকাশ সৃষ্টি করেছেন তাঁর নিকট হতে তা নাযিল হয়েছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2353,20,5,'‘আরশে দয়াময় সমুন্নত আছেন।  ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2354,20,6,'যা আকাশসমূহে আছে, যা যমীনে আছে, যা এ দু’য়ের মাঝে আছে আর যা ভূগর্ভে আছে সব তাঁরই। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2355,20,7,'যদি তুমি উচ্চকণ্ঠে কথা বল (তাহলে জেনে রেখ) তিনি গুপ্ত ও তদপেক্ষাও গুপ্ত বিষয় জানেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2356,20,8,'আল্লাহ, তিনি ব্যতীত সত্যিকারের কোন ইলাহ নেই, সুন্দর নামসমূহ তাঁরই। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2357,20,9,'মূসার কাহিনী তোমার কাছে পৌঁছেছে কি? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2358,20,10,'যখন সে আগুন দেখল (মাদ্ইয়ান থেকে মিসর যাবার পথে), তখন সে তার পরিবারবর্গকে বলল, ‘তোমরা এখানে অবস্থান কর, আমি আগুন দেখেছি, সম্ভবতঃ আমি তাত্থেকে তোমাদের জন্য কিছু জ্বলন্ত আগুন আনতে পারব কিংবা আগুনের নিকট পথের সন্ধান পাব। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2359,20,11,'তারপর যখন যে আগুনের কাছে আসল, তাকে ডাক দেয়া হল, ‘হে মূসা! ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2360,20,12,'বাস্তবিকই আমি তোমার প্রতিপালক, কাজেই তোমার জুতা খুলে ফেল, তুমি পবিত্র তুওয়া উপত্যকায় আছ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2361,20,13,'আমি তোমাকে বেছে নিয়েছি, কাজেই তুমি মনোযোগ দিয়ে শুনো যা তোমার প্রতি ওয়াহী করা হচ্ছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2362,20,14,'প্রকৃতই আমি আল্লাহ, আমি ছাড়া সত্যিকারের কোন ইলাহ নেই, কাজেই আমার ‘ইবাদাত কর, আর আমাকে স্মরণ করার উদ্দেশ্যে নামায কায়িম কর।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2363,20,15,'কিয়ামাত আসবেই, আমি তা গোপন রাখতে চাই, যাতে প্রত্যেক ব্যক্তি স্বীয় চেষ্টা-সাধনা অনুপাতে ফল লাভ করতে পারে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2364,20,16,'কাজেই ক্বিয়ামতে যে বিশ্বাস করে না এবং স্বীয় প্রবৃত্তির অনুসরণ করে, সে যেন তোমাকে তাত্থেকে কোনক্রমেই বিচ্যুত করতে না পারে, তাহলে তুমি ধ্বংস হয়ে যাবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2365,20,17,'‘হে মূসা! তোমার ডান হাতে ওটা কী?’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2366,20,18,'সে বলল, ‘এটা আমার লাঠি, আমি ওতে ভর দেই, এর সাহায্যে আমি আমার মেষপালের জন্য গাছের পাতা ঝেড়ে দেই আর এতে আমার আরো অনেক কাজ হয়।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2367,20,19,'আল্লাহ বললেন, ‘হে মূসা! ওটা নিক্ষেপ কর।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2368,20,20,'তখন সে তা নিক্ষেপ করল, অমনি তা সাপ হয়ে ছুটোছুটি করতে লাগল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2369,20,21,'আল্লাহ বললেন, ‘ওটাকে ধর, ভয় পেও না, আমি সেটাকে এক্ষুনি তার আগের রূপে ফিরিয়ে দেব। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2370,20,22,'আর তোমার হাত বগলে দিয়ে টান, তা ক্ষতিকর নয় এমন শুভ্রোজ্জ্বল হয়ে বের হয়ে আসবে- অন্য আরেকটি নিদর্শন হিসেবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2371,20,23,'যাতে আমি তোমাকে আমার বড় বড় নিদর্শনগুলোর কিছু দেখাতে পারি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2372,20,24,'ফেরাউনের কাছে যাও, বাস্তবিকই সে সীমালঙ্ঘন করেছে।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2373,20,25,'মূসা বলল, ‘হে আমার প্রতিপালক! আমার জন্য আমার বক্ষকে প্রশস্ত করে দাও ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2374,20,26,'আর আমার জন্য আমার কাজকে সহজ করে দাও। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2375,20,27,'আমার জিহ্বার জড়তা দূর করে দাও।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2376,20,28,'যাতে তারা আমার কথা বুঝতে পারে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2377,20,29,'আর আমার পরিবার হতে আমার জন্য একজন সাহায্যকারী বানিয়ে দাও। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2378,20,30,'আমার ভাই হারূনকে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2379,20,31,'তার দ্বারা আমার শক্তি বৃদ্ধি কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2380,20,32,'আমার কাজে তাকে অংশীদার কর।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2381,20,33,'যাতে আমরা বেশি বেশি করে তোমার পবিত্রতা ও মহিমা ঘোষণা করতে পারি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2382,20,34,'আর তোমাকে অধিক স্মরণ করতে পারি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2383,20,35,'তুমি তো আমাদের অবস্থা সবই দেখছ।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2384,20,36,'তিনি (আল্লাহ) বললেন, ‘হে মূসা! তোমার প্রার্থনা গৃহীত হল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2385,20,37,'আমি তো তোমার উপর আরো একবার অনুগ্রহ করেছিলাম।  ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2386,20,38,'যখন আমি তোমার মায়ের প্রতি ইঙ্গিত করেছিলাম যা ওয়াহীযোগে জানানো হয়েছিল- ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2387,20,39,'যে তুমি মূসাকে সিন্দুকের মধ্যে রাখ। তারপর তা দরিয়ায় ভাসিয়ে দাও। অতঃপর দরিয়া তাকে পাড়ে ঠেলে দেবে। তাকে আমার শত্রু ও তার শত্রু উঠিয়ে নেবে। আমি আমার নিকট হতে তোমার প্রতি ভালবাসা ঢেলে দিয়েছিলাম, যাতে তুমি আমার দৃষ্টির সম্মুখে প্রতিপালিত হও।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2388,20,40,'যখন তোমার বোন গিয়ে বলল, ‘তোমাদেরকে কি বলে দেব কে এই শিশুর তত্ত্বাবধান ও প্রতিপালনের ভার নেবে?’ এভাবে আমি তোমাকে তোমার মায়ের কাছে ফিরিয়ে আনলাম যাতে তার চোখ জুড়ায় আর সে দুঃখ না পায়। আর তুমি এক লোককে হত্যা করেছিলে, অতঃপর আমি তোমাকে মনোবেদনা থেকে মুক্তি দিয়েছি। আমি তোমাকে অনেক পরীক্ষা করেছি। অতঃপর তুমি কয়েক বছর মাদ্ইয়ানবাসীদের মধ্যে অবস্থান করলে, এরপর হে মূসা! তুমি নির্ধারিত সময়ে এসে হাযির হয়েছ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2389,20,41,'আমি তোমাকে আমার নিজের জন্য তৈরি করেছি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2390,20,42,'তুমি আর তোমার ভাই আমার নিদর্শন নিয়ে যাও আর তোমরা আমাকে স্মরণ করার বিষয়ে শৈথিল্য করো না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2391,20,43,'তোমরা দু’জন ফেরাউনের নিকট যাও, বস্তুতঃ সে সীমালঙ্ঘন করেছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2392,20,44,'তার সঙ্গে তোমরা নম্রভাবে কথা বলবে, হয়তো সে উপদেশ গ্রহণ করবে কিংবা (আল্লাহর) ভয় করবে।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2393,20,45,'তারা বলল, ‘হে আমাদের প্রতিপালক! আমাদের ভয় হচ্ছে সে আমাদের প্রতি হঠাৎ উত্তেজিত হয়ে উঠবে কিংবা আমাদের প্রতি আচরণে বাড়াবাড়ি করবে।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2394,20,46,'তিনি বললেন, ‘তোমরা ভয় করো না, আমি তোমাদের সাথেই আছি, আমি (সব কিছু) শুনি আর দেখি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2395,20,47,'কাজেই তোমরা দু’জন তার কাছে এসো আর বল- আমরা তোমার প্রতিপালকের রসূল, কাজেই বানী ইসরাঈলকে আমাদের সঙ্গে পাঠিয়ে দাও, তাদের প্রতি উৎপীড়ন করো না। তোমার প্রতিপালকের নিদর্শনসহই আমরা তোমার কাছে এসেছি, আর শান্তি বর্ষিত হোক তাদের প্রতি যারা সৎ পথ অনুসরণ করে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2396,20,48,'আমাদের কাছে ওয়াহী করা হয়েছে যে শাস্তি (অপেক্ষা করছে) তার জন্য যে (সত্য) প্রত্যাখ্যান করে আর মুখ ফিরিয়ে নেয়।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2397,20,49,'ফেরাউন বলল, ‘হে মূসা! কে তোমাদের প্রতিপালক?’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2398,20,50,'মূসা বলল, ‘আমাদের প্রতিপালক তিনি যিনি সকল (সৃষ্ট) বস্তুকে আকৃতি দান করেছেন, অতঃপর পথ প্রদর্শন করেছেন।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2399,20,51,'ফেরাউন বলল, ‘তাহলে আগের যুগের লোকেদের অবস্থা কী?’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2400,20,52,'মূসা বলল, ‘তার জ্ঞান আমার প্রতিপালকের কাছে (রক্ষিত) কিতাবে আছে, আমার প্রতিপালক ভুল করেন না, ভুলেও যান না।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2401,20,53,'যিনি তোমাদের জন্য যমীনকে করেছেন বিছানা, আর তাতে তোমাদের জন্য ক’রে দিয়েছেন চলার পথ। আর আকাশ থেকে তিনি পানি বর্ষণ করেন আর তা দিয়ে আমি বিভিন্ন লতা-যুগল উদগত করি যার প্রত্যেকটি অন্যটি থেকে আলাদা। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2402,20,54,'তোমরা খাও আর তোমাদের গবাদি পশু চরাও, এতে বিবেকবানদের জন্য বহু নিদর্শন আছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2403,20,55,'মাটি থেকে আমি তোমাদেরকে সৃষ্টি করেছি, তাতেই আমি তোমাদেরকে ফিরিয়ে নেব, আর তাত্থেকে তোমাদেরকে আবার বের করব। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2404,20,56,'আমি তাকে (অর্থাৎ ফেরাউনকে) আমার যাবতীয় নিদর্শন দেখিয়েছিলাম কিন্তু সে মিথ্যে মনে ক’রে প্রত্যাখ্যান করেছে ও অমান্য করেছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2405,20,57,'সে বলল, ‘হে মূসা! তুমি কি তোমার যাদুর বলে আমাদেরকে আমাদের দেশ থেকে বের করে দেয়ার জন্য এসেছ? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2406,20,58,'তাহলে আমরাও অবশ্য অবশ্যই তোমার কাছে অনুরূপ যাদু হাযির করব, কাজেই একটা মধ্যবর্তী স্থানে আমাদের ও তোমাদের মিলিত হওয়ার জন্য একটা নির্দিষ্ট সময় নির্ধারণ কর যার খেলাফ আমরাও করব না, আর তুমিও করবে না।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2407,20,59,'মূসা বলল, ‘তোমাদের (সঙ্গে) ওয়া‘দার নির্ধারিত সময় হল উৎসবের দিন আর দুপুরের আগেই মানুষ যেন পৌঁছে যায়।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2408,20,60,'তখন ফেরাউন উঠে গেল, অতঃপর তার কলা-কৌশল একত্র করল, তারপর ফিরে এল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2409,20,61,'মূসা তাদেরকে বলল, ‘হায় তোমাদের দুর্ভাগ্য! তোমরা আল্লাহর প্রতি মিথ্যে আরোপ করো না, করলে তিনি তোমাদেরকে ভয়ানক শাস্তি দ্বারা ধ্বংস করে দেবেন। যে মিথ্যে আরোপ করে সে-ই ব্যর্থ হয়।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2410,20,62,'তখন তারা তাদের কাজ-কর্ম নিয়ে নিজেদের মধ্যে বাদানুবাদ করল আর তারা গোপনে পরামর্শ করল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2411,20,63,'তারা বলল, ‘এ দু’জন নিশ্চিতই যাদুকর, তারা তাদের যাদুর বলে তোমাদেরকে তোমাদের দেশ থেকে বের করে দিতে চায় আর তোমাদের উৎকৃষ্ট জীবন পদ্ধতি মুছে ফেলতে চায়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2412,20,64,'কাজেই তোমরা তোমাদের কলা-কৌশল একত্রিত কর, অতঃপর সারিবদ্ধ হয়ে হাজির হয়ে যাও। আজ যে বিজয় লাভ করবে, সে-ই (বরাবর) সফল হবে।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2413,20,65,'তারা বলল, ‘হে মূসা! তুমিই (প্রথমে) নিক্ষেপ করবে, না আমরাই প্রথমে নিক্ষেপ করব?’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2414,20,66,'মূসা বলল, ‘বরং তোমরাই নিক্ষেপ কর।’ তখন তাদের যাদুর কারণে মূসার মনে হল যে, তাদের রশি আর লাঠিগুলো ছুটোছুটি করছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2415,20,67,'তখন মূসা তার মনে ভীতি অনুভব করল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2416,20,68,'আমি বললাম, ‘ভয় করো না, তুমিই বিজয়ী হবে।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2417,20,69,'তোমার ডান হাতে যা আছে তা নিক্ষেপ কর, তারা যা করেছে এটা তা সব গিলে ফেলবে, তারা যা করেছে তাতো কেবল যাদুকরের কলা-কৌশল। যাদুকর যে রূপ ধরেই আসুক না কেন, সফল হবে না।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2418,20,70,'(মূসার স্পষ্ট নিদর্শন যখন দেখল) তখন যাদুকরেরা (আল্লাহর প্রতি) সাজদায় লুটিয়ে পড়ল। তারা বলল, ‘আমরা হারূন ও মূসার প্রতিপালকের উপর ঈমান আনলাম।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2419,20,71,'ফেরাউন বলল, ‘আমি তোমাদেরকে অনুমতি দেয়ার আগেই তোমরা তার প্রতি বিশ্বাস স্থাপন করলে? নিশ্চয়ই সে তোমাদের প্রধান যে তোমাদেরকে যাদু শিখিয়েছে। কাজেই আমি অবশ্য অবশ্যই তোমাদের হাত আর পা বিপরীত দিক থেকে কেটে ফেলব আর খেজুর গাছের শাখায় তোমাদেরকে অবশ্য অবশ্যই শূলে চড়াব আর তখন তোমরা অবশ্য অবশ্যই জানতে পারবে আমাদের মধ্যে কার ‘আযাব বেশি শক্ত আর বেশি স্থায়ী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2420,20,72,'তারা বলল, ‘আমাদের নিকট যে স্পষ্ট নিদর্শন এসেছে এবং যিনি আমাদেরকে সৃষ্টি করেছেন তার উপর আমরা তোমাকে কক্ষনো প্রাধান্য দেব না। কাজেই তুমি যা করতে চাও তাই কর। কেননা তুমি কেবল এ পার্থিব জীবনেই কর্তৃত্ব খাটাতে পার। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2421,20,73,'আমরা আমাদের প্রতিপালকের উপর ঈমান এনেছি যাতে তিনি আমাদের অপরাধ ক্ষমা করেন আর যে যাদু করতে তুমি আমাদেরকে বাধ্য করেছ তাও (ক্ষমা করেন), আল্লাহই সর্বশ্রেষ্ঠ ও স্থায়ী।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2422,20,74,'যে কেউ তার প্রতিপালকের নিকট অপরাধী অবস্থায় হাযির হবে তার জন্যে আছে জাহান্নাম, সেখানে সে না মরবে, আর না বাঁচবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2423,20,75,'যে কেউ সৎ ‘আমাল ক’রে মু’মিন অবস্থায় তাঁর নিকট হাযির হবে, তাদের জন্য আছে সুউচ্চ মর্যাদা। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2424,20,76,'স্থায়ী জান্নাত, যার পাদদেশে বয়ে চলছে নির্ঝরিণী, সেখানে তারা চিরকাল থাকবে। যারা নিজেদেরকে পবিত্র করে তাদের এটাই পুরস্কার। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2425,20,77,'আমি মূসাকে ওয়াহী করলাম যে, আমার বান্দাহদেরকে নিয়ে রাতের বেলা যাত্রা কর আর তাদের জন্য সমুদ্রের ভিতর একটা শুকনো পথ বানিয়ে নাও। আর পেছন থেকে (ফেরাউন) ধরে ফেলবে এ ভয় করো না, আর (অন্য কোন) আশঙ্কাও করো না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2426,20,78,'অতঃপর ফেরাউন তার সৈন্যসামন্ত নিয়ে তাদের পিছু নিল, অতঃপর সমুদ্র তাদের উপর চড়াও হল আর তাদেরকে ডুবিয়ে দিল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2427,20,79,'ফেরাউন তার জাতিকে বিপথগামী করেছিল এবং তাদেরকে সঠিক পথ দেখায়নি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2428,20,80,'হে বানী ইসরাঈল! আমি তোমাদেরকে তোমাদের শত্রুর কবল থেকে উদ্ধার করেছিলাম আর আমি তোমাদেরকে তূর পাহাড়ের ডানপাশে প্রতিশ্রুতি দিয়েছিলাম (তাওরাত দানের জন্য) আর তোমাদের কাছে পাঠিয়েছিলাম মান্না ও সালওয়া। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2429,20,81,'তোমাদেরকে যে রিযক দিয়েছি তাত্থেকে উত্তমগুলো আহার কর, আর এতে বাড়াবাড়ি করো না, করলে তোমাদের উপর আমার ‘আযাব সাব্যস্ত হয়ে যাবে। আর আমার ‘আযাব যার উপর সাব্যস্ত হয় সে তো ধ্বংসই হয়ে যায়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2430,20,82,'আর যে তাওবাহ করে, ঈমান আনে ও সৎকাজ করে আর সৎপথে অটল থাকে, আমি তার জন্য অবশ্যই অতি ক্ষমাশীল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2431,20,83,'‘হে মূসা! তোমার সম্প্রদায়কে পেছনে ফেলে (তূর পাহাড়ে আসতে) তুমি জলদি করলে কেন?’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2432,20,84,'মূসা বলল, ‘এই তো তারা আমার পদচিহ্ন ধরে আসছে, আমি আপনার কাছে জলদি এলাম, হে আমার প্রতিপালক! যাতে আপনি সন্তুষ্ট হন।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2433,20,85,'তিনি বললেন, ‘তোমার অনুপস্থিতিতে আমি তোমার সম্প্রদায়কে পরীক্ষা করেছি আর সামিরী তাদেরকে গুমরাহ্ করেছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2434,20,86,'তখন মূসা রাগে-দুঃখে তার জাতির কাছে ফিরে গেল। সে বলল, ‘হে আমার জাতি! তোমাদের প্রতিপালক কি তোমাদের সঙ্গে ও‘য়াদা করেননি, এক উত্তম ওয়া‘দা। ওয়া‘দা (পূরণের সময় আসতে) কি তোমাদের নিকট সুদীর্ঘ মনে হয়েছে, নাকি তোমরা চেয়েছ যে, তোমাদের প্রতি তোমাদের প্রতিপালকের শাস্তি নেমে আসুক, যে কারণে তোমরা আমার কাছে দেয়া তোমাদের ওয়া‘দা ভঙ্গ করলে?’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2435,20,87,'তারা বলল, ‘আমাদের সাধ্য থাকা পর্যন্ত আমরা তোমার প্রতি দেয়া আমাদের ওয়া‘দা ভঙ্গ করিনি, কিন্তু আমাদের উপর লোকেদের অলঙ্কারের বোঝা চাপিয়ে দেয়া হয়েছিল, আর আমরা তা নিক্ষেপ করেছিলাম (আগুনে), এমনিভাবে সামিরীও নিক্ষেপ করেছিল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2436,20,88,'তখন সে (আগুন থেকে) গো-বৎসের প্রতিকৃতি বের করল, মনে হত সেটা যেন হাম্বা রব করছে। অতঃপর তারা বলল, ‘এটাই তোমাদের ইলাহ আর মূসারও ইলাহ, কিন্তু মূসা ভুলে গেছে।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2437,20,89,'তারা কি ভেবে দেখে না যে, তা তাদের কথার জবাব দেয় না, আর তা তাদের কোন ক্ষতি বা উপকার করার সামর্থ্যও রাখে না? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2438,20,90,'হারূন তাদেরকে আগেই বলেছিল, ‘হে আমার জাতির লোকেরা! এর (অর্থাৎ গো-বৎসের) দ্বারা তোমাদেরকে পরীক্ষায় ফেলা হয়েছে, তোমাদের প্রতিপালক হলেন দয়াময় (আল্লাহ), কাজেই তোমরা আমার অনুসরণ কর আর আমার কথা মান্য কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2439,20,91,'তারা বলল, ‘আমাদের কাছে মূসা ফিরে না আসা পর্যন্ত আমরা সদাসর্বদা এর সাথেই সংযুক্ত হয়ে থাকব। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2440,20,92,'সে (মূসা) বলল, ‘হে হারূন! তুমি যখন দেখলে যে, তারা গুমরাহ্ হয়ে গেছে তখন তোমাকে কে নিষেধ করল ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2441,20,93,'আমাকে অনুসরণ করতে? তাহলে তুমি কি আমার আদেশ অমান্য করলে? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2442,20,94,'হারূন বলল, ‘হে আমার মায়ের পুত্র! আমার দাড়ি ধরে টেন না, আর আমার (মাথার) চুল ধরেও টেন না, আমি ভয় করেছিলাম তুমি বলবে যে, বানী ইসরাঈলের মাঝে তুমি বিভেদ সৃষ্টি করেছ আর তুমি আমার কথা পালন করনি।’');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2443,20,95,'মূসা বলল, ‘এখন তোমার ব্যাপারটা কী, হে সামিরী?’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2444,20,96,'সে বলল, ‘আমি দেখেছি যা ওরা দেখেনি, অতঃপর আমি প্রেরিত ব্যক্তির (অর্থাৎ জিবরীলের) পদচিহ্ন থেকে এক মুঠ মাটি নিলাম, অতঃপর আমি তা নিক্ষেপ করলাম (বাছুরের প্রতিকৃতিতে)। আমার মন আমাকে এ মন্ত্রণাই দিল।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2445,20,97,'মূসা বলল, ‘তুই দূর হ! এ জীবনে তোর জন্য এ শাস্তিই থাকল যে, তুই বলবি- আমাকে স্পর্শ করো না, আর তোর জন্য একটা নির্দিষ্ট ওয়া‘দা আছে যার খেলাফ হবে না। আর তোর ইলাহর পানে চেয়ে দেখ যাকে তুই ঘিরে থাকতি, আমি তাকে অবশ্য অবশ্যই জ্বলন্ত আগুনে জ্বালিয়ে দেব, আর তাকে ছড়িয়ে ছিটিয়ে অবশ্য অবশ্যই সাগরে নিক্ষেপ করব।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2446,20,98,'তোমাদের ইলাহ একমাত্র আল্লাহ, যিনি ছাড়া সত্যিকারের কোন ইলাহ নেই। যাবতীয় বিষয়ে তাঁর জ্ঞান পরিব্যাপ্ত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2447,20,99,'এভাবে পূর্বে যা ঘটে গেছে তার কিছু সংবাদ আমি তোমার কাছে বর্ণনা করছি, আর আমি আমার নিকট থেকে তোমাকে দান করেছি উপদেশ (বা কুরআন)। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2448,20,100,'যে তাত্থেকে মুখ ফিরিয়ে নেবে, কিয়ামাতের দিন সে (পাপের) বোঝা বহন করবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2449,20,101,'তারা এ অবস্থাতেই স্থায়ীভাবে থাকবে, কিয়ামাতের দিন এ বোঝা তাদের জন্য কতই না মন্দ হবে! ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2450,20,102,'যেদিন সিঙ্গায় ফুঁ দেয়া হবে আর আমি অপরাধীদেরকে একত্রিত করব (ভয়ে ভীত-সন্ত্রস্ত) দৃষ্টিহীন অবস্থায়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2451,20,103,'তারা চুপিসারে নিজেদের মধ্যে বলাবলি করবে যে, (দুনিয়াতে) দশ দিনের বেশি তোমরা অবস্থান করনি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2452,20,104,'আমি ভালভাবেই জানি তারা যা বলে। তাদের মধ্যে যে উত্তম পথের অনুসারী সে বলবে, ‘তোমরা একদিনের বেশি অবস্থান করনি।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2453,20,105,'তারা তোমাকে পর্বতগুলো সম্পর্কে প্রশ্ন করে। বল, আমার প্রতিপালক সেগুলো সমূলে উৎপাটিত করবেন এবং ধূলির ন্যায় বিক্ষিপ্ত করবেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2454,20,106,'অতঃপর তিনি তাকে (অর্থাৎ ভূমিকে) মসৃণ সমতলভূমি করে ছাড়বেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2455,20,107,'তাতে তুমি দেখবে না কোন বক্রতা ও উচ্চতা। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2456,20,108,'সেদিন তারা (সোজাসুজি) আহবানকারীর অনুসরণ করবে যার কথা এদিক ওদিক হবে না। দয়াময়ের সম্মুখে সেদিন যাবতীয় আওয়াজ স্তব্ধ হয়ে যাবে (এমনভাবে) যে মৃদু গুঞ্জন ছাড়া তুমি কিছুই শুনবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2457,20,109,'সেদিন কারো সুপারিশ কোন কাজে আসবে না, দয়াময় যাকে অনুমতি দেবেন আর যার কথায় সন্তুষ্ট হবেন তার (সুপারিশ) ব্যতীত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2458,20,110,'তাদের সম্মুখে ও পশ্চাতে যা আছে তা তিনি জানেন, তারা জ্ঞান দিয়ে তাঁকে আয়ত্ত করতে পারে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2459,20,111,'চিরঞ্জীব চিরস্থায়ীর সম্মুখে সকলেই হবে অধোমুখী, আর সে ব্যর্থ হবে যে যুলমের (পাপের) ভার বহন করবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2460,20,112,'যে সৎ কাজ করবে মু’মিন হয়ে, তার অবিচার বা ক্ষতির কোন আশংকা নেই। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2461,20,113,'এভাবেই আমি কুরআনকে আরবী ভাষায় নাযিল করেছি আর তাতে সতর্কবাণী বিস্তারিতভাবে বিবৃত করেছি যাতে তারা আল্লাহকে ভয় করে অথবা তা হয় তাদের জন্য উপদেশ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2462,20,114,'আল্লাহ সর্বোচ্চ, প্রকৃত অধিপতি, তোমার প্রতি (আল্লাহর) ওয়াহী সম্পূর্ণ হওয়ার পূর্বে তুমি কুরআন বক্ষে ধারণের ব্যাপারে তাড়াহুড়ো করো না। আর বল, ‘হে আমার প্রতিপালক! জ্ঞানে আমায় সমৃদ্ধি দান করুন।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2463,20,115,'ইতোপূর্বে আমি আদামের নিকট থেকে অঙ্গীকার গ্রহণ করেছিলাম, কিন্তু সে ভুলে গিয়েছিল, আমি তাকে দৃঢ়-সংকল্প পাইনি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2464,20,116,'স্মরণ কর, যখন ফেরেশতাগণকে বলেছিলাম, ‘তোমরা আদামকে সেজদা কর,’ তখন ইবলিস ছাড়া সবাই সেজদা করল, সে অমান্য করল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2465,20,117,'তখন আমি বললাম, ‘হে আদাম! এ হচ্ছে তোমার আর তোমার স্ত্রীর দুশমন। কাজেই সে যেন কিছুতেই তোমাদেরকে জান্নাত থেকে বের করে না দেয়, তাহলে তোমরা দুর্দশায় পতিত হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2466,20,118,'তোমার জন্য (এত অধিক পরিমাণ) দেয়া হল যে, তুমি সেখানে (অর্থাৎ জান্নাতে) ক্ষুধার্তও হবে না, নগ্নও হবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2467,20,119,'সেখানে তুমি তৃষ্ণার্তও হবে না, রোদেও পুড়বে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2468,20,120,'কিন্তু শয়ত্বান তাকে কুমন্ত্রণা দিল। সে বলল, ‘হে আদাম! আমি কি তোমাকে জানিয়ে দেব চিরস্থায়ী জীবনদায়ী গাছের কথা আর এমন রাজ্যের কথা যা কোনদিন ক্ষয় হবে না?’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2469,20,121,'অতঃপর তারা (স্বামী-স্ত্রী) দু’জনে তা (অর্থাৎ সেই গাছ) থেকে খেল তখন তাদের সামনে তাদের লজ্জাস্থান খুলে গেল আর তারা জান্নাতের গাছের পাতা দিয়ে নিজেদেরকে ঢাকতে লাগল। আদাম তার প্রতিপালকের অবাধ্যতা করল, ফলে সে পথভ্রান্ত হয়ে গেল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2470,20,122,'এরপর তার পালনকর্তা তাকে বাছাই করলেন, তার তাওবাহ কবূল করলেন এবং তাকে সঠিক পথে পরিচালিত করলেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2471,20,123,'তিনি বললেন, ‘তোমরা দু’জনে (আদাম ও ইবলীস) একই সঙ্গে নীচে নেমে যাও, তোমরা একে অপরের শত্রু। অতঃপর আমার নিকট থেকে তোমাদের কাছে সঠিক পথের নির্দেশ আসবে, তখন যে আমার পথ নির্দেশ অনুসরণ করবে সে পথভ্রষ্ট হবে না এবং কষ্টে পতিত হবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2472,20,124,'আর যে আমার স্মরণ থেকে মুখ ফিরিয়ে নেবে, তার জীবিকা হবে সংকীর্ণ আর তাকে কিয়ামাতের দিন উত্থিত করব অন্ধ অবস্থায়।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2473,20,125,'সে বলবে, ‘হে আমার প্রতিপালক! কেন আমাকে অন্ধ ক’রে উঠালে? আমি তো চক্ষুষ্মান ছিলাম।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2474,20,126,'আল্লাহ বলবেন, ‘এভাবেই তো আমার নিদর্শনসমূহ যখন তোমার কাছে এসেছিল তখন তুমি তা ভুলে গিয়েছিলে। আজকের দিনে সেভাবেই তোমাকে ভুলে যাওয়া হচ্ছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2475,20,127,'আমি এভাবেই প্রতিফল দেই তাদেরকে যারা সীমালঙ্ঘন করে এবং তার প্রতিপালকের নিদর্শনে বিশ্বাস করে না। আর আখিরাতের ‘আযাব অবশ্যই সবচেয়ে বেশী কঠিন ও সবচেয়ে বেশী স্থায়ী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2476,20,128,'এটাও কি তাদেরকে সঠিক পথ প্রদর্শন করল না যে, আমি তাদের আগে কত মানব বংশকে ধ্বংস করে দিয়েছি যাদের বাসস্থানের ভিতর দিয়ে এরা চলাচল করে। বুদ্ধিমানদের জন্য অবশ্যই এতে নিদর্শন রয়েছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2477,20,129,'তোমার প্রতিপালকের নিকট থেকে পূর্ব সিদ্ধান্ত না থাকলে (তাদের শাস্তি) অবশ্যই এসে পড়ত, কিন্তু এর জন্য নির্দিষ্ট আছে একটি সময়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2478,20,130,'কাজেই তারা যা বলছে তাতে তুমি ধৈর্য ধারণ কর এবং তোমার প্রতিপালকের প্রশংসাগীতি (নিয়মিত) উচ্চারণ কর সূর্যোদয়ের পূর্বে ও তা অস্তমিত হওয়ার পূর্বে এবং তাঁর পবিত্রতা ও মহিমা ঘোষণা কর রাত্রিকালে ও দিনের প্রান্তগুলোয় যাতে তুমি সন্তুষ্ট হতে পার। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2479,20,131,'তুমি কক্ষনো চোখ খুলে তাকিও না ঐ সব বস্তুর প্রতি যা আমি তাদের বিভিন্ন দলকে পার্থিব জীবনে উপভোগের জন্য সৌন্দর্য স্বরূপ দিয়েছি, এসব দিয়ে তাদেরকে পরীক্ষা করার জন্য। তোমার প্রতিপালকের দেয়া রিযকই হল সবচেয়ে উত্তম ও সবচেয়ে বেশী স্থায়ী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2480,20,132,'আর তোমার পবিরার-পরিজনকে নামাযের নির্দেশ দাও আর তাতে অবিচল থাক। তোমার কাছে আমি রিযক চাই না, আমিই তোমাকে রিযক দিয়ে থাকি, উত্তম পরিণাম মুত্তাকীদের জন্য নির্দিষ্ট। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2481,20,133,'তারা বলে, ‘সে তার প্রতিপালকের নিকট থেকে আমাদের কাছে কোন নিদর্শন নিয়ে আসে না কেন? তাদের কাছে কি আসেনি স্পষ্ট প্রমাণ যা ছিল পূর্ববর্তী (ওয়াহীকৃত) কিতাবগুলোতে।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2482,20,134,'এর (অর্থাৎ কোন নিদর্শন আসার) আগেই আমি যদি তাদেরকে ‘আযাব দিয়ে ধ্বংস করে দিতাম তাহলে তারা বলত, ‘হে আমাদের প্রতিপালক! তুমি আমাদের কাছে কেন একজন রসূল পাঠালে না? তাহলে আমরা অবশ্যই তোমার নিদর্শন মেনে চলতাম আমরা অপমানিত ও হেয় হবার আগেই। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2483,20,135,'বল, (ইসলামের অনুসারীদের পরিণতি দেখার জন্য চারপাশের) সবাই অপেক্ষা করছে, কাজেই তোমরাও অপেক্ষা কর, তাহলেই তোমরা জানতে পারবে যে, কারা সরল পথের পথিক আর কারা সঠিক পথপ্রাপ্ত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2484,21,1,'মানুষের হিসাব গ্রহণের কাল ক্রমশঃ ঘনিয়ে আসছে কিন্তু তারা গাফলতিতে মুখ ফিরিয়ে রেখেছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2485,21,2,'তাদের কাছে তাদের প্রতিপালকের পক্ষ হতে যখনই কোন নতুন উপদেশ আসে, তখন তারা তা হাসি-তামাশার বস্তু মনে করেই শোনে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2486,21,3,'তাদের অন্তর থাকে খেলায় মগ্ন। যালিমরা গোপনে পরামর্শ করে- এটা তোমাদেরই মত মানুষ ছাড়া কি অন্য কিছু? তোমরা কি দেখে-শুনে যাদুর কবলে পড়বে? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2487,21,4,'বল, ‘আমার প্রতিপালক আসমান ও যমীনে (উচ্চারিত প্রতিটি) কথাই জানেন, আর তিনি সব কিছু শোনেন, সব কিছু জানেন।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2488,21,5,'তারা এও বলে, ‘এসব অলীক স্বপ্ন, না হয় সে মিথ্যে উদ্ভাবন করেছে, না হয় সে একজন কবি। কাজেই সে আমাদের কাছে এমন নিদর্শন নিয়ে আসুক যেমন পূর্ববর্তী (নবী)-গণের কাছে পাঠানো হয়েছিল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2489,21,6,'তাদের পূর্বে আমি যে সমস্ত জনপদ ধ্বংস করেছি তাদের একটিও ঈমান আনেনি, তাহলে এরা কি ঈমান আনবে? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2490,21,7,'তোমার পূর্বে যে সব রসূল পাঠিয়েছিলাম যাদের প্রতি আমি ওয়াহী করতাম তারা মানুষই ছিল, তোমরা যদি না জান তবে (অবতীর্ণ) কিতাবের জ্ঞান যাদের আছে তাদেরকে জিজ্ঞেস কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2491,21,8,'তাদেরকে এমন দেহবিশিষ্ট করিনি যে তারা খাদ্য খেত না আর তারা ছিল না চিরস্থায়ী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2492,21,9,'অতঃপর আমি তাদেরকে দেয়া আমার ওয়া‘দা সত্যে পরিণত করলাম। ফলতঃ আমি তাদেরকে এবং আরো যাদেরকে চাইলাম রক্ষা করলাম আর সীমালঙ্ঘনকারীদেরকে ধ্বংস করে দিলাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2493,21,10,'আমি তোমাদের প্রতি নাযিল করেছি এক কিতাব যাতে তোমাদের জন্য আছে উপদেশ, তোমরা কি তবুও বুঝবে না? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2494,21,11,'কত জনপদ ছিল যেগুলোকে আমি পুরোপুরি ধ্বংস করে দিয়েছি যার অধিবাসীরা ছিল যালিম। তাদের পরে আমি অন্য জাতি সৃষ্টি করেছি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2495,21,12,'তারা যখন আমার শাস্তি (’র আগমন) অনুভব করল, তখন তারা তাত্থেকে পালিয়ে যেতে (চেষ্টা) করল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2496,21,13,'(ফেরেশতারা তাদেরকে ঠাট্টা করে বলেছিল) পালিয়ে যেয়ো না, ফিরে এসো তোমরা যে ভোগ-বিলাসে মত্ত ছিলে তার দিকে আর তোমাদের আবাসগুলোতে, যাতে তোমাদেরকে জিজ্ঞাসাবাদ করা যায় (‘আযাবের রূপটা কেমন দেখলে?)। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2497,21,14,'তারা বলল, ‘হায় আমাদের দুর্ভাগ্য! আমরা সত্যিই অন্যায়কারী ছিলাম।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2498,21,15,'তাদের এ আর্তনাদ বন্ধ হয়নি যতক্ষণ না আমি তাদেরকে করেছিলাম কাটা শস্য ও নিভানো আগুনের মত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2499,21,16,'আসমান, যমীন আর এ দু’য়ের মধ্যে যা কিছু আছে তা আমি খেলতে খেলতে বানাইনি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2500,21,17,'আমি যদি খেলাধূলার বস্তু বানাতে চাইতাম তাহলে আমার কাছে যা আছে তা নিয়েই তা করতাম, যদি আমাকে করতে হত! ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2501,21,18,'বরং আমি সত্যকে মিথ্যের উপর নিক্ষেপ করি, অতঃপর তা মিথ্যের মস্তক চূর্ণ-বিচূর্ণ করে দেয়, তৎক্ষণাৎ মিথ্যে নিশ্চিহ্ন হয়ে যায়। তোমরা (আল্লাহ সম্পর্কে অযথা বহু মিথ্যে কথা বানিয়ে নিয়ে) যা বলছ এ কারণে তোমাদের জন্য দুর্ভোগ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2502,21,19,'আসমান ও যমীনে যারা আছে তারা তাঁরই মালিকানাধীন, আর যারা তাঁর সন্নিকটে আছে তারা গর্বভরে তাঁর ‘ইবাদাত থেকে বিমুখ হয় না, আর তারা (কক্ষনো তাঁর ‘ইবাদাত করার ব্যাপারে) ক্লান্তিবোধ করে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2503,21,20,'তারা রাত-দিন তাঁর পবিত্রতা ঘোষণা করতে থাকে, তারা কক্ষনো শিথিলতা করে না বা আগ্রহ হারায় না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2504,21,21,'তারা (অর্থাৎ মুশরিকরা) মাটি থেকে (তৈরী) যে সব দেবতা গ্রহণ করেছে তারা কি (মৃতদেরকে) জীবিত করতে সক্ষম? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2505,21,22,'আসমান ও যমীনে যদি আল্লাহ ছাড়া আরো অনেক ইলাহ থাকত তবে (আসমান ও যমীন) উভয়ই ধ্বংস হয়ে যেত। কাজেই আরশের অধিপতি আল্লাহ মহান ও পবিত্র সে সব থেকে যা তারা তাঁর প্রতি আরোপ করে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2506,21,23,'তিনি যা করেন সে ব্যাপারে তিনি জিজ্ঞাসিত হবেন না, বরং তারা জিজ্ঞাসিত হবে (তাদের কাজের ব্যাপারে)। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2507,21,24,'নাকি তারা তাঁকে বাদ দিয়ে অনেক ইলাহ গ্রহণ করেছে? বল, ‘তোমরা তোমাদের প্রমাণ এনে হাযির কর। (আল্লাহ ছাড়া কোন ইলাহ নেই) এটাই আমার সাথে যারা আছে তাদের কথা আর আমার পূর্বে যারা ছিল তাদেরও কথা, কিন্তু তাদের (অর্থাৎ সত্য প্রত্যাখ্যানকারীদের) অধিকাংশই প্রকৃত সত্য জানে না, যার জন্য তারা মুখ ফিরিয়ে নেয়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2508,21,25,'আমি তোমার পূর্বে এমন কোন রসূলই পাঠাইনি যার প্রতি আমি ওয়াহী করিনি যে, আমি ছাড়া সত্যিকারের কোন ইলাহ নেই। কাজেই তোমরা আমারই ‘ইবাদাত কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2509,21,26,'তারা বলে, ‘দয়াময় সন্তান গ্রহণ করেছেন’, তিনি এসব থেকে মহা পবিত্র। তারা হল তাঁর বান্দাহ যাদেরকে সম্মানে উন্নীত করা হয়েছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2510,21,27,'তিনি কথা বলার আগেই তারা (অর্থাৎ সম্মানিত বান্দারা) কথা বলে না, তারা তাঁর নির্দেশেই কাজ করে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2511,21,28,'তাদের সামনে আর পেছনে যা আছে তা তিনি জানেন। তিনি যাদের প্রতি খুবই সন্তুষ্ট তাদের ব্যাপারে ছাড়া তারা কোন সুপারিশ করে না। তারা তাঁর ভয় ও সম্মানে ভীত-সন্ত্রস্ত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2512,21,29,'তাদের মধ্যে যে বলবে যে, ‘তিনি ব্যতীত আমিই ইলাহ’, তাহলে আমি তাকে তার প্রতিফল দেব জাহান্নাম। যালিমদেরকে আমি এভাবেই পুরস্কার দিয়ে থাকি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2513,21,30,'অবিশ্বাসীরা কি দেখে না যে, আকাশ আর যমীন এক সঙ্গে সংযুক্ত ছিল, অতঃপর আমি উভয়কে আলাদা করে দিলাম, আর প্রাণসম্পন্ন সব কিছু পানি থেকে সৃষ্টি করলাম। তবুও কি তারা ঈমান আনবে না? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2514,21,31,'আর পৃথিবীতে আমি স্থাপন করেছি সুদৃঢ় পর্বত যাতে পৃথিবী তাদেরকে নিয়ে নড়াচড়া না করে। আর তাতে সৃষ্টি করেছি প্রশস্ত পথ যাতে তারা পথ পেতে পারে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2515,21,32,'আর আমি আকাশকে করেছি সুরক্ষিত ছাদ, কিন্তু এ সবের নিদর্শন থেকে তারা মুখ ফিরিয়ে নেয়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2516,21,33,'তিনিই সৃষ্টি করেছেন রাত ও দিন, সূর্য আর চন্দ্র, প্রত্যেকেই তার চক্রাকার পথে সাঁতার কাটছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2517,21,34,'তোমার পূর্বেও আমি কোন মানুষকে চিরস্থায়ী করিনি। তুমি যদি মারা যাও, তাহলে তারা কি চিরস্থায়ী হবে? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2518,21,35,'প্রত্যেক আত্মাকে মৃত্যু আস্বাদন করত হবে। আমি তোমাদেরকে ভাল ও মন্দ (উভয়টি দিয়ে এবং উভয় অবস্থায় ফেলে এর) দ্বারা পরীক্ষা করি। আমার কাছেই তোমাদেরকে ফিরিয়ে আনা হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2519,21,36,'কাফিররা যখন তোমাকে দেখে, তখন তারা তোমাকে একমাত্র উপহাসের পাত্র হিসেবেই গণ্য করে। (আর তারা বলে) ‘এই কি সেই লোক যে তোমাদের দেবতাগুলোর ব্যাপারে কথা বলে? অথচ এই লোকগুলোই ‘রহমান’ (শব্দটির) উল্লেখকে অগ্রাহ্য করে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2520,21,37,'মানুষকে তাড়াহুড়াকারী করে সৃষ্টি করা হয়েছে। শীঘ্রই আমি তোমাদেরকে আমার নিদর্শনগুলো দেখাব (যে সব অলৌকিক ব্যাপার বদর ও অন্যান্য যুদ্ধে কাফিরদেরকে দেখানো হয়েছিল), কাজেই তোমরা আমাকে জলদি করতে বল না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2521,21,38,'আর তারা বলে ‘তোমরা যদি সত্যবাদী হও তাহলে (বল) প্রতিশ্রুতি কখন বাস্তবে পরিণত হবে?’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2522,21,39,'অবিশ্বাসীরা যদি (সে সময়ের কথা) জানত যখন তারা তাদের মুখ হতে অগ্নি প্রতিরোধ করতে পারবে না, আর তাদের পিঠ থেকেও না, আর তারা সাহায্যপ্রাপ্তও হবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2523,21,40,'বরং তা তাদের উপর হঠাৎ এসে যাবে আর তা তাদেরকে হতবুদ্ধি করে দেবে। অতঃপর তারা তা রোধ করতে পারবে না, আর তাদেরকে অবকাশও দেয়া হবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2524,21,41,'তোমার পূর্বেও রসূলদেরকে ঠাট্টা করা হয়েছে, অতঃপর যা দিয়ে তারা ঠাট্টা করত তা উল্টো ঠাট্টা-বিদ্রুপই তাদেরকে পরিবেষ্টন করে নিয়েছিল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2525,21,42,'বল, ‘কে তোমাদেরকে রাতে আর দিনে রহমান (এর গযব) থেকে নিরাপদ রাখতে পারে? তবুও তারা তাদের প্রতিপালকের স্মরণ থেকে মুখ ফিরিয়ে নেয়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2526,21,43,'তবে কি তাদের এমন দেবদেবী আছে যা তাদেরকে রক্ষা করবে আমার (প্রতিরক্ষা) ছাড়াই? তারা তো নিজেদেরকেই সাহায্য করতে পারে না, আর তারা আমার বিরুদ্ধে কোন প্রতিরক্ষাও পাবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2527,21,44,'বরং আমিই তাদেরকে আর তাদের পিতৃ-পুরুষদেরকে পার্থিব ভোগ্যবস্তু দিয়েছিলাম আর তাদেরকে আয়ুও দেয়া হয়েছিল দীর্ঘ; তারা কি দেখছে না যে, আমি তাদের দেশকে চারপাশের (তাদের নিয়ন্ত্রিত) সীমান্ত হতে সংকুচিত করে আনছি? এরপরও কি তারা বিজয়ী হবে?’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2528,21,45,'বল, আমি তোমাদেরকে একমাত্র (আল্লাহর) ওয়াহী দ্বারাই সতর্ক করি, কিন্তু বধিররা ডাক শুনবে না যখন তাদেরকে সতর্ক করা হয়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2529,21,46,'তোমার প্রতিপালকের গযবের একটা নিঃশ্বাস যদি তাদের উপর পতিত হয় তবে তারা অবশ্য অবশ্যই বলে উঠবে, ‘হায় আমাদের দুর্ভাগ্য! আমরাই তো ছিলাম অপরাধী।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2530,21,47,'আর কিয়ামাত দিবসে আমি সুবিচারের মানদন্ড স্থাপন করব, অতঃপর কারো প্রতি এতটুকুও অন্যায় করা হবে না। (কর্ম) সরিষার দানা পরিমাণ হলেও তা আমি হাযির করব, হিসাব গ্রহণে আমিই যথেষ্ট। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2531,21,48,'আমি মূসা ও হারূনকে (সত্য-মিথ্যার) মানদন্ড, আলো ও বাণী দিয়েছিলাম মুত্তাকীদের জন্য। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2532,21,49,'যারা না দেখেই তাদের প্রতিপালককে ভয় করে আর তারা কিয়ামাত সম্পর্কে ভীত-শংকিত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2533,21,50,'এ হচ্ছে কল্যাণময় উপদেশ বাণী, আমি তা নাযিল করেছি: তবুও কি তা তোমরা প্রত্যাখ্যান করবে? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2534,21,51,'আমি ইতোপূর্বে ইবরাহীমকে সঠিক পথে চলার জ্ঞান দান করেছিলাম আর তার সম্পর্কে আমি খুব ভালভাবে জানতাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2535,21,52,'যখন সে তার পিতাকে ও তার জাতিকে বলল, ‘এ মূর্তিগুলো কী যাদের সঙ্গে তোমরা নিজেদেরকে (ভক্তির বাঁধনে) বেঁধে রেখেছ?’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2536,21,53,'তারা বলল, ‘আমরা আমাদের পিতৃপুরুষদেরকে এদের পূজো করতে দেখেছি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2537,21,54,'সে বলল, ‘তোমরা রয়েছ স্পষ্ট গুমরাহীতে, তোমরা আর তোমাদের পিতৃপুরুষরাও।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2538,21,55,'তারা বলল, ‘তুমি কি আমাদের কাছে প্রকৃত সত্য এনেছ, না তুমি আমাদের সঙ্গে তামাশা করছ?’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2539,21,56,'সে বলল, ‘বরং, তোমাদের প্রতিপালক হলেন আকাশ ও পৃথিবীর প্রতিপালক যিনি ওগুলোকে সৃষ্টি করেছেন, আর এ বিষয়ে আমি একজন সাক্ষ্যদাতা। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2540,21,57,'কসম আল্লাহর! তোমরা পেছন ফিরে চলে গেলেই আমি তোমাদের মূর্তিগুলোর বিরুদ্ধে অবশ্য অবশ্যই একটা কৌশল গ্রহণ করব। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2541,21,58,'তারপর সে মূর্তিগুলোকে টুকরো টুকরো করে দিল, ওগুলোর বড়টি ছাড়া, যাতে পূজারীরা ওটার প্রতিই মনোযোগী হয়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2542,21,59,'তারা বলল, ‘আমাদের মূর্তিগুলোর সাথে এমনটা কে করল? সে অবশ্যই যালিম।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2543,21,60,'কেউ কেউ বলল, ‘এক যুবককে এদের সম্পর্কে বলতে শুনেছি, তাকে ইবরাহীম বলা হয়।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2544,21,61,'তারা বলল, ‘তাকে নিয়ে এসো লোকজনের সামনে যাতে তারা সাক্ষী হতে পারে।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2545,21,62,'তারা বলল, ‘ওহে ইবরাহীম! আমাদের উপাস্যদের সাথে তুমিই কি এমনটি করেছ?’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2546,21,63,'সে বলল, ‘না, তাদের এই বড়টাই এ সব করেছে, তারা যদি কথা বলতে পারে তবে তাদেরকে জিজ্ঞেস কর।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2547,21,64,'তখন তারা বিষয়টি নিয়ে মনে মনে চিন্তা করল এবং পরস্পর বলাবলি করল- তোমরা নিজেরাই তো অন্যায়কারী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2548,21,65,'তাদের মাথা হেঁট হয়ে গেল। (তখন তারা বলল) ‘তুমি তো জানই ওরা কথা বলতে পারে না।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2549,21,66,'সে বলল, ‘তাহলে তোমরা কি আল্লাহর পরিবর্তে এমন কিছুর ‘ইবাদাত কর যা না পারে তোমাদের কোন উপকার করতে, আর না পারে তোমাদের ক্ষতি করতে? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2550,21,67,'ধিক তোমাদের প্রতি আর আল্লাহর পরিবর্তে তোমরা যাদের ‘ইবাদাত কর তাদেরও প্রতি। এরপরও কি তোমরা বুঝবে না।  ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2551,21,68,'তারা বলল, ‘তাকে পুড়িয়ে মার আর তোমাদের উপাস্যদের সাহায্য কর- যদি তোমরা কিছু করতেই চাও। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2552,21,69,'আমি বললাম, ‘হে অগ্নি! তুমি ইবরাহীমের জন্য শীতল ও শান্তিময় হয়ে যাও।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2553,21,70,'তারা তার বিরুদ্ধে চক্রান্ত করেছিল কিন্তু আমি তাদেরকেই সবচেয়ে ক্ষতিগ্রস্ত করে ছাড়লাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2554,21,71,'আর আমি তাকে ও (তার ভ্রাতুষ্পুত্র) লূতকে উদ্ধার করে তাদেরকে এমন দেশে (অর্থাৎ ফিলিস্তিনে) নিয়ে গেলাম যা আমি বিশ্ববাসীর জন্য কল্যাণময় করেছি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2555,21,72,'আমি ইবরাহীমকে দান করেছিলাম ইসহাক, আর অতিরিক্ত হিসেবে (পৌত্র) ইয়া‘কুব, (তাদের) প্রত্যেককেই আমি করেছিলাম সৎকর্মশীল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2556,21,73,'আর তাদেরকে বানিয়েছিলাম নেতা, তারা আমার নির্দেশে মানুষকে সঠিক পথ দেখাত। তাদের প্রতি ওয়াহী করেছিলাম সৎ কাজ করার জন্য, নিয়মিত নামায প্রতিষ্ঠা করার ও যাকাত প্রদানের জন্য, তারা আমারই ‘ইবাদাত করত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2557,21,74,'আর আমি লূতকেও দিয়েছিলাম বিচারশক্তি ও জ্ঞান। আমি তাকে উদ্ধার করেছিলাম সেই জনবসতি থেকে যা অশ্লীল কাজে লিপ্ত ছিল, তারা ছিল এক খারাপ পাপাচারী জাতি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2558,21,75,'আমি তাকে আমার রাহমাতের অন্তর্ভুক্ত করেছিলাম, সে ছিল সৎ কর্মশীলদের একজন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2559,21,76,'স্মরণ কর নূহের কথা, ইতোপূর্বে যখন সে (আমাকে) ডেকেছিল, তখন আমি তার ডাকে সাড়া দিয়েছিলাম আর তাকে ও তার পরিবারবর্গকে মহা বিপদ থেকে রক্ষা করেছিলাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2560,21,77,'আর আমি তাকে সেই লোকেদের বিরুদ্ধে সাহায্য করেছিলাম যারা আমার আয়াতকে মিথ্যে বলে প্রত্যাখ্যান করেছিল, তারা ছিল এক খারাপ সম্প্রদায়, কাজেই তাদের সব্বাইকে (বানে) ডুবিয়ে দিয়েছিলাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2561,21,78,'স্মরণ কর দাঊদ ও সুলায়মানের কথা যখন তারা কৃষিক্ষেত সম্পর্কে বিচার করছিল যখন তাতে রাতের বেলা কোন ব্যক্তির মেষ ঢুকে পড়েছিল, আর আমি তাদের বিচারকার্য প্রত্যক্ষ করছিলাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2562,21,79,'আমি সুলায়মানকে এ বিষয়ের (সঠিক) বুঝ দিয়েছিলাম আর (তাদের) প্রত্যেককে আমি দিয়েছিলাম বিচারশক্তি ও জ্ঞান। আমি পর্বত ও পাখীদেরকে দাঊদের অধীন ক’রে দিয়েছিলাম, তারা দাঊদের সাথে আমার মাহাত্ম্য ও পবিত্রতা ঘোষণা করত। (এসব) আমিই করতাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2563,21,80,'আমিই তাকে বর্ম নির্মাণ শিক্ষা দিয়েছিলাম তোমাদের উপকারে তোমাদের পারস্পরিক যুদ্ধে তোমাদেরকে রক্ষা করার জন্য, কাজেই তোমরা কি কৃতজ্ঞ হবে? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2564,21,81,'(আমার ক্ষমতা বলেই) আমি উদ্দাম বায়ুকে (স্বাভাবিক গতি সম্পন্ন) করেছিলাম সুলায়মানের জন্য। তার নির্দেশ মত তা প্রবাহিত হত সেই দেশের দিকে যাতে আমি কল্যাণ রেখেছি। আমি প্রত্যেক বিষয় সম্পর্কেই অবগত ছিলাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2565,21,82,'শয়ত্বানদের কতক তার জন্য ডুবুরির কাজ করত, এছাড়া অন্য কাজও করত, আমিই তাদেরকে রক্ষা করতাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2566,21,83,'স্মরণ কর আইয়ূবের কথা, যখন সে তার প্রতিপালককে ডেকেছিল : (এই ব’লে যে) আমি দুঃখ কষ্টে নিপতিত হয়েছি, তুমি তো দয়ালুদের সর্বশ্রেষ্ঠ দয়ালু। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2567,21,84,'তখন আমি তার ডাকে সাড়া দিয়েছিলাম আর তার দুঃখ ক্লেশ দূর করে দিয়েছিলাম। আর তার পরিবারবর্গকে তার কাছে ফিরিয়ে দিয়েছিলাম, আর তাদের সাথে তাদের মত আরো দিয়েছিলাম আমার পক্ষ হতে রহমত স্বরূপ আর আমার যারা ‘ইবাদাত করে তাদের জন্য স্মারক হিসেবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2568,21,85,'স্মরণ কর ইসমা‘ঈল, ইদরীস ও জুল-কিফল এর কথা, তাদের প্রত্যেকেই ছিল ধৈর্যশীল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2569,21,86,'আমি তাদেরকে আমার রাহমাতের অন্তর্ভুক্ত করেছিলাম, কারণ তারা ছিল সৎকর্মশীল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2570,21,87,'স্মরণ কর যুন্-নুন এর কথা- যখন সে গোস্বাভরে চলে গিয়েছিল, আর ভেবেছিল যে তার উপর আমার কোন ক্ষমতা খাটবে না। অতঃপর সে (সমুদ্রের) গভীর অন্ধকার থেকে ডেকেছিল যে, তুমি ছাড়া সত্যিকারের কোন ইলাহ নেই, তোমারই পবিত্রতা, মহিমা ঘোষণা করছি; বাড়াবাড়ি আমিই করেছি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2571,21,88,'আমি তখন তার ডাকে সাড়া দিয়েছিলাম, আর দুঃশ্চিন্তা থেকে তাকে মুক্ত করেছিলাম। মু’মিনদেরকে আমি এভাবেই উদ্ধার করি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2572,21,89,'আর স্মরণ কর যাকারিয়ার কথা, যখন সে তার প্রতিপালককে ডেকেছিল : ‘হে আমার প্রতিপালক! আমাকে সন্তানহীন করে রেখ না, যদিও তুমি উত্তরাধিকারীদের মধ্যে সর্বশ্রেষ্ঠ।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2573,21,90,'তখন আমি তার ডাকে সাড়া দিয়েছিলাম আর তাকে দান করেছিলাম ইয়াহইয়া, আমি তার নিমিত্তে তার স্ত্রীর বন্ধ্যাত্ব দূর করে দিয়েছিলাম। এরা সৎ কাজে ছিল ক্ষিপ্রগতি, তারা আমাকে ডাকতো আশা নিয়ে ও ভীত হয়ে, আর তারা ছিল আমার প্রতি বিনয়ী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2574,21,91,'স্মরণ কর সেই নারীর (অর্থাৎ মারইয়ামের) কথা যে তার সতীত্বকে সংরক্ষণ করেছিল। অতঃপর আমি তার ভিতর আমার রূহ ফুঁকে দিয়েছিলাম আর তাকে ও তার পুত্রকে বিশ্বজগতের জন্য নিদর্শন করেছিলাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2575,21,92,'তোমাদের এ সব জাতিগুলো একই জাতি, আর আমি তোমাদের প্রতিপালক, কাজেই আমারই ‘ইবাদাত কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2576,21,93,'কিন্তু তারা (পরবর্তীতে) নিজেদের কাজ-কর্মে পরস্পর পার্থক্য সৃষ্টি করেছে, তা সত্ত্বেও তারা সবাই আমার কাছে ফিরে আসবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2577,21,94,'কাজেই কেউ যদি মু’মিন হয়ে সৎ কাজ করে, তবে তার প্রচেষ্টা অস্বীকার করা হবে না, আমি তা তার জন্য লিখে রাখি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2578,21,95,'আমি যে সব জনবসতি ধ্বংস করেছি তাদের জন্য এটা নিষিদ্ধ করা হয়েছে যে তারা আর ফিরে আসবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2579,21,96,'এমনকি (তখনও তারা ফিরে আসবে না) যখন ইয়া’জূজ ও মা’জূজের জন্য (প্রাচীর) খুলে দেয়া হবে আর তারা প্রতিটি পাহাড় কেটে ছুটে আসবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2580,21,97,'সত্য ওয়া‘দার (পূর্ণতার) সময় ঘনিয়ে আসবে, তখন আতঙ্কে কাফিরদের চক্ষু স্থির হয়ে যাবে। (তখন তারা বলবে) হায়! আমরা তো এ ব্যাপারে উদাসীন ছিলাম, না, আমরা ছিলাম অন্যায়কারী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2581,21,98,'তোমরা (কাফিররা) আর আল্লাহকে বাদ দিয়ে তোমরা যাদের ‘ইবাদাত কর সেগুলো জাহান্নামের জ্বালানী। তাতে তোমরা প্রবেশ করবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2582,21,99,'তারা যদি ইলাহ হত, তাহলে তারা তাতে প্রবেশ করত না, তাতে তারা সবাই স্থায়ী হয়ে থাকবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2583,21,100,'সেখানে তারা ফুঁপিয়ে ফুঁপিয়ে কাঁদবে, সেখানে কিছুই শুনবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2584,21,101,'পূর্ব থেকেই আমার পক্ষ হতে যাদের জন্য কল্যাণ নির্ধারিত আছে তাদেরকে তাত্থেকে (অর্থাৎ জাহান্নাম থেকে) বহু দূরে রাখা হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2585,21,102,'তারা জাহান্নামের ক্ষীণতম শব্দও শুনবে না, আর তারা তাদের মনের বাসনা অনুযায়ী চিরকাল বসবাস করবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2586,21,103,'মহা ত্রাস তাদেরকে চিন্তাযুক্ত করবে না, আর ফেরেশতাগণ তাদেরকে অভ্যর্থনা জানাবে (এই কথা বলে যে), ‘এটাই তোমাদের দিন যার ওয়া‘দা তোমাদেরকে দেয়া হয়েছিল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2587,21,104,'সেদিন আমি আকাশকে গুটিয়ে নেব যেমনভাবে (লিখিত) কাগজ-দলীল গুটিয়ে রাখা হয়। যেভাবে আমি প্রথমবার সৃষ্টি করেছিলাম, সেভাবে আবার সৃষ্টি করব। ওয়া‘দা আমি করেছি, তা আমি পূর্ণ করবই। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2588,21,105,'(এর আগে মূসাকে) বাণী দেয়ার পর আমি যুবূরে লিখে দিয়েছিলাম যে, আমার সৎকর্মপরায়ণ বান্দাহ্গণই পৃথিবীর উত্তরাধিকার লাভ করবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2589,21,106,'নিশ্চয়ই এতে (অর্থাৎ এই কুরআনে) উপদেশবাণী রয়েছে ‘ইবাদাতকারী লোকেদের জন্য। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2590,21,107,'আমি তোমাকে বিশ্ববাসীর জন্য পাঠিয়েছি কেবল রহমত হিসেবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2591,21,108,'বল- আমার প্রতি এ ওয়াহীই করা হয়েছে যে, তোমাদের ইলাহ একমাত্র ইলাহ, কাজেই তোমরা কি তাঁর নির্দেশের প্রতি মাথা নত করবে? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2592,21,109,'তবে তারা মুখ ফিরিয়ে নিলে বল : আমি তোমাদের কাছে যথাযথভাবে বাণী পৌঁছে দিয়েছি। আর আমি জানি না তোমাদেরকে যার ওয়া‘দা দেয়া হয়েছে তা নিকটবর্তী, নাকি দূরবর্তী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2593,21,110,'তিনি জানেন যে কথা প্রকাশ করা হয় আর তিনি জানেন যা তোমরা (তোমাদের অন্তরে) লুকিয়ে রাখ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2594,21,111,'আমি জানি না সম্ভবতঃ বিলম্বের মধ্যে তোমাদের জন্য রয়েছে পরীক্ষা আর কিছু কালের জন্য সুখভোগের সুযোগ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2595,21,112,'রসূল বলেছিল- হে আমার প্রতিপালক! তুমি ন্যায্য মীমাংসা করে দাও, আর আমাদের প্রতিপালক তো দয়ার আধার। তোমরা (তাঁর বিরুদ্ধে) যে সব কথা বলছ সে বিষয়ে তিনিই একমাত্র আশ্রয়স্থল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2596,22,1,'হে মানুষ! তোমরা তোমাদের প্রতিপালককে ভয় কর, কিয়ামাতের কম্পন এক ভয়ানক জিনিস। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2597,22,2,'সেদিন তুমি দেখবে প্রতিটি দুগ্ধদায়িনী ভুলে যাবে তার দুগ্ধপোষ্য শিশুকে, আর প্রত্যেক গর্ভবতী গর্ভপাত করে ফেলবে, আর মানুষকে দেখবে মাতাল, যদিও তারা প্রকৃতপক্ষে মাতাল নয়, কিন্তু আল্লাহর শাস্তি বড়ই কঠিন (যার কারণে তাদের ঐ অবস্থা ঘটবে)। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2598,22,3,'কতক মানুষ জ্ঞান ছাড়াই আল্লাহ সম্বন্ধে বাদানুবাদ করে, আর প্রত্যেক অবাধ্য শয়ত্বানের অনুসরণ করে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2599,22,4,'যার (অর্থাৎ শয়ত্বানের) সম্পর্কে বিধান করা হয়েছে যে, যে কেউ তার সঙ্গে বন্ধুত্ব গড়বে, সে তাকে বিপথগামী করবে, আর তাকে প্রজ্জ্বলিত অগ্নি শাস্তির দিকে পরিচালিত করবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2600,22,5,'হে মানুষ! পুনরুত্থানের ব্যাপারে যদি তোমরা সন্দিহান হও, তাহলে (চিন্তা করে দেখ) আমি তোমাদেরকে সৃষ্টি করেছি মাটি থেকে, অতঃপর শুক্র হতে, অতঃপর জমাট রক্ত থেকে, অতঃপর মাংসপিন্ড হতে পূর্ণ আকৃতিবিশিষ্ট বা অপূর্ণ আকৃতিবিশিষ্ট অবস্থায় (আমার শক্তি-ক্ষমতা) তোমাদের সামনে স্পষ্ট করে তুলে ধরার জন্য। আর আমি যাকে ইচ্ছে করি তাকে একটা নির্দিষ্ট কাল পর্যন্ত মাতৃগর্ভে রাখি, অতঃপর তোমাদেরকে বের করে আনি শিশুরূপে, অতঃপর (লালন পালন) করি যাতে তোমরা তোমাদের পূর্ণ শক্তির বয়সে পৌঁছতে পার। তোমাদের কারো কারো মৃত্যু ঘটাই, আর কতককে ফিরিয়ে দেয়া হয় নিস্ক্রিয় বার্ধক্যে যাতে (অনেক) জ্ঞান লাভের পরেও তাদের আর কোন জ্ঞান থাকে না। অতঃপর (আরো) তোমরা ভূমিকে দেখ শুষ্ক, মৃত; অতঃপর আমি যখন তাতে পানি বর্ষণ করি তখন তাতে প্রাণ চাঞ্চল্য সৃষ্টি হয়, তা আন্দোলিত ও স্ফীত হয়, আর তা উদগত করে সকল প্রকার নয়নজুড়ানো উদ্ভিদ (জোড়ায় জোড়ায়)। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2601,22,6,'এ রকম হয় এজন্য যে, আল্লাহ হলেন সত্য সঠিক, আর তিনিই মৃতকে জীবিত করেন, আর তিনি সকল বিষয়ে ক্ষমতাবান। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2602,22,7,'আর কিয়ামাত অবশ্যই আসবে, এ ব্যাপারে কোন সন্দেহ নেই এবং যারা কবরে আছে আল্লাহ তাদেরকে অবশ্যই পুনরুত্থিত করবেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2603,22,8,'তবুও মানুষের মধ্যে এমন আছে যারা জ্ঞান, পথের দিশা ও কোন আলোকপ্রদানকারী কিতাব ছাড়াই আল্লাহ সম্পর্কে বিতর্ক করে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2604,22,9,'(বিতর্ক করে অবজ্ঞাভরে) ঘাড় বাঁকিয়ে (লোকেদেরকে) আল্লাহর পথ থেকে বিচ্যুত করার উদ্দেশে। তার জন্য আছে লাঞ্ছনা এ দুনিয়াতে, আর কিয়ামাতের দিন তাকে আস্বাদন করাব (অগ্নির) দহন যন্ত্রণা। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2605,22,10,'(বলা হবে) তোমার হাত দু’খানা আগেই যা পাঠিয়েছিল এটা তারই ফল, কারণ আল্লাহ তো তাঁর বান্দাহদের প্রতি যালিম নন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2606,22,11,'মানুষের মধ্যে এমন কতক আছে যারা শেষ সীমায় অবস্থান করে আল্লাহর ‘ইবাদাত করে। অতঃপর তার কল্যাণ হলে, তা নিয়ে সে তৃপ্ত থাকে, আর কোন পরীক্ষার সম্মুখীন হলে সে তার পূর্বের অবস্থায় ফিরে যায়। ক্ষতিগ্রস্ত হয় সে দুনিয়াতেও আর আখিরাতেও- এটাই হল স্পষ্ট ক্ষতি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2607,22,12,'আল্লাহকে বাদ দিয়ে সে এমন কিছুকে ডাকে যা না পারে তার কোন ক্ষতি করতে আর না পারে কোন উপকার করতে, এটাই হল চরম আকারের গুমরাহী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2608,22,13,'সে এমন কিছুকে ডাকে যার লাভের চেয়ে ক্ষতিই নিকটবর্তী, কত মন্দই না এই অভিভাবক, আর কত মন্দই না এই সঙ্গী! ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2609,22,14,'যারা ঈমান আনে ও সৎ কাজ করে আল্লাহ তাদেরকে দাখিল করবেন জান্নাতে যার নিম্নদেশ দিয়ে ঝর্ণাধারা প্রবাহিত, আল্লাহ যা করতে চান, তাই করেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2610,22,15,'যে কেউ ধারণা করে যে আল্লাহ তাকে (অর্থাৎ তাঁর রসূলকে) কক্ষনো দুনিয়া ও আখিরাতে সাহায্য করবেন না, তাহলে সে আকাশ পর্যন্ত একটা দড়ি ঝুলিয়ে নিক, অতঃপর তা কেটে দিক, অতঃপর সে দেখুক তার কলা-কৌশল তার রাগের কারণ দূর করে কিনা। (রসূলের দুশমন নিজের ঝুলানো দড়িটাই কাটুক, কেননা সে তো রসূলের প্রতি আল্লাহর সাহায্যের দড়িটা কক্ষনো কাটতে পারবে না।) ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2611,22,16,'এভাবেই আমি স্পষ্ট নিদর্শনরূপে কুরআন অবতীর্ণ করেছি আর আল্লাহ যাকে চান সঠিক পথে পরিচালিত করেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2612,22,17,'যারা ঈমান এনেছে আর যারা ইয়াহূদী হয়েছে, আর যারা সাবিয়ী, নাসারা, অগ্নিপূজক ও মুশরিক, আল্লাহ কিয়ামাতের দিন এদের মধ্যে ফয়সালা করে দেবেন (যে কারা সঠিক পথে আছে), কারণ আল্লাহ সব কিছুর প্রত্যক্ষদর্শী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2613,22,18,'তুমি কি দেখ না যে আল্লাহকে সেজদা করে যারা আকাশে আছে, আর যারা পৃথিবীতে আছে আর সূর্য, চন্দ্র, তারকারাজি, পর্বতসমূহ, বৃক্ষরাজি, জীবজন্তু এবং মানুষের মধ্যে অনেকে? আর অনেকের প্রতি শাস্তি সাব্যস্ত হয়ে গেছে। আল্লাহ যাকে লাঞ্ছিত করতে চান, তাকে সম্মানিত করার কেউ নেই। আল্লাহ যা ইচ্ছে করেন তাই করেন।[সাজদাহ] ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2614,22,19,'এরা বিবাদের দু’টি পক্ষ, (মু’মিনরা একটি পক্ষ, আর সমস্ত কাফিররা আরেকটি পক্ষ) এরা এদের প্রতিপালক সম্বন্ধে বাদানুবাদ করে, অতঃপর যারা (তাদের প্রতিপালককে) অস্বীকার করে, তাদের জন্য তৈরি করা হয়েছে আগুনের পোশাক, তাদের মাথার উপর ঢেলে দেয়া হবে ফুটন্ত পানি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2615,22,20,'যা দিয়ে তাদের পেটে যা আছে তা ও তাদের চামড়া গলিয়ে দেয়া হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2616,22,21,'উপরন্তু তাদের (শাস্তির) জন্য থাকবে লোহার মুগুর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2617,22,22,'যখনই তারা যন্ত্রণার চোটে তাত্থেকে বেরিয়ে আসতে চাইবে (তখনই) তাদেরকে তার ভিতরে ফিরিয়ে দেয়া হবে, (আর বলা হবে, আগুনে) পুড়ার শাস্তি আস্বাদন কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2618,22,23,'যারা ঈমান আনে আর সৎ কাজ করে, আল্লাহ তাদেরকে দাখিল করবেন জান্নাতে যার তলদেশে ঝর্ণাধারা প্রবাহিত। সেখানে তাদেরকে অলংকৃত করা হবে সোনার কাঁকন আর মুক্তা দিয়ে আর সেখানে তাদের পোশাক হবে রেশমের। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2619,22,24,'তাদেরকে (দুনিয়ার জীবনে) পথ দেখানো হয়েছিল পবিত্র বাক্যের (অর্থাৎ কালিমা তাইয়্যেবা বা আল-কুরআনের) দিকে আর তারা পরিচালিত হয়েছিল তাঁর পথে যিনি সকল প্রশংসার দাবীদার। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2620,22,25,'যারা কুফুরী করে আর আল্লাহর পথে (মানুষের চলার ক্ষেত্রে) বাধা সৃষ্টি করে আর মাসজিদে হারামে যেতেও- যাকে আমি করেছি স্থানীয় বাসিন্দা ও অন্যদেশবাসী সকলের জন্য সমান। যে তাতে অন্যায়ভাবে কোন ধর্মদ্রোহী কাজ করার ইচ্ছে করে তাকে আমি আস্বাদন করাব ভয়াবহ শাস্তি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2621,22,26,'স্মরণ কর যখন আমি ইবরাহীমকে (পবিত্র) গৃহের স্থান চিহ্নিত করে দিয়েছিলাম, (তখন বলেছিলাম) আমার সাথে কোন কিছুকে অংশীদার গণ্য করবে না, আর আমার গৃহকে পবিত্র রাখবে তাওয়াফকারী, নামাযে কিয়ামকারী, রূকু‘কারী ও সেজদাকারীদের জন্য। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2622,22,27,'আর মানুষের মাঝে হাজ্জের ঘোষণা দাও, তারা তোমার কাছে আসবে পায়ে হেঁটে, আর সব (পথক্লান্ত) শীর্ণ উটের পিঠে, বহু দূরের গভীর পর্বত সংকুল পথ বেয়ে ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2623,22,28,'যাতে তারা তাদের জন্য (এখানে রাখা দুনিয়া ও আখিরাতের) কল্যাণগুলো প্রত্যক্ষ করতে পারে আর তিনি তাদেরকে চতুষ্পদ জন্তু হতে যে রিযক দান করেছেন, নির্দিষ্ট দিনগুলোতে তার উপর আল্লাহর নাম উচ্চারণ করতে পারে। কাজেই তোমরা (নিজেরা) তাত্থেকে খাও আর দুঃস্থ অভাবীদের খাওয়াও। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2624,22,29,'অতঃপর তারা যেন তাদের দৈহিক অপরিচ্ছন্নতা দূর করে, তাদের মানৎ পূর্ণ করে আর প্রাচীন গৃহের তাওয়াফ করে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2625,22,30,'এটাই (হাজ্জ), যে কেউ আল্লাহর নির্ধারিত অনুষ্ঠানগুলোর সম্মান করবে, সেটা তার প্রতিপালকের নিকট তার জন্য উত্তম। চতুষ্পদ জন্তুগুলো তোমাদের জন্য হালাল করা হয়েছে সেগুলো ছাড়া যেগুলোর ব্যাপারে তোমাদেরকে পড়ে শুনানো হয়েছে। কাজেই তোমরা মূর্তিদের অপবিত্রতা বর্জন কর আর মিথ্যে কথা পরিহার কর ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2626,22,31,'আল্লাহর প্রতি একনিষ্ঠ হয়ে তাঁর সাথে শরীক না করে। যে কেউ আল্লাহর সাথে শরীক করে সে যেন আকাশ থেকে পড়ে গেল, আর পাখী তাকে ছোঁ মেরে নিয়ে গেল কিংবা বাতাস তাকে উড়িয়ে নিয়ে দূরবর্তী স্থানে ছুঁড়ে ফেলে দিল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2627,22,32,'এই (তার অবস্থা), আর যে কেউ আল্লাহর নিদর্শনগুলোকে সম্মান করবে সে তো তার অন্তরস্থিত আল্লাহ-ভীতি থেকেই তা করবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2628,22,33,'এতে (অর্থাৎ কুরবানীর পশুতে) তোমাদের জন্য নানান উপকার রয়েছে এক নির্দিষ্ট কাল পর্যন্ত (কুরবানীর জায়গায় পৌঁছা পর্যন্ত তোমরা এই জন্তুগুলোর দ্বারা উপকৃত হতে পার), সর্বশেষে এগুলোর কুরবানীর স্থান হচ্ছে প্রাচীন ঘরের নিকট। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2629,22,34,'আমি প্রতিটি সম্প্রদায়ের জন্য (কুরবানীর) নিয়ম করে দিয়েছি। তাদেরকে চতুষ্পদ জন্তু হতে যে রিযক্ দেয়া হয়েছে সেগুলোর উপর তারা যেন আল্লাহর নাম উচ্চারণ করে, (এই বিভিন্ন নিয়ম-পদ্ধতির মূল লক্ষ্য কিন্তু এক- আল্লাহর নির্দেশ পালন), কারণ তোমাদের উপাস্য একমাত্র উপাস্য, কাজেই তাঁর কাছেই আত্মসমর্পণ কর আর সুসংবাদ দাও সেই বিনীতদেরকে- ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2630,22,35,'‘আল্লাহ’ নামের উল্লেখ হলেই যাদের অন্তরাত্মা কেঁপে উঠে, যারা তাদের বিপদাপদে ধৈর্যধারণ করে, নামায কায়িম করে, আর তাদেরকে আমি যে রিযক্ দিয়েছি তাত্থেকে তারা ব্যয় করে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2631,22,36,'আর (কুরবানীর) উটগুলোকে আমি করেছি আল্লাহর নিদর্শনসমূহের অন্যতম। তাতে তোমাদের জন্য কল্যাণ আছে, কাজেই সারিবদ্ধভাবে দাঁড়ানো অবস্থায় ওগুলোর উপর তোমরা আল্লাহর নাম উচ্চারণ কর। যখন তা পার্শ্বভরে পড়ে যায়, তখন তাথেকে খাও আর যারা (ভিক্ষে না ক’রে) পরিতৃপ্ত থাকে তাদেরকে আর যারা কাকুতি মিনতি ক’রে যাচ্ঞা করে তাদেরকেও খাওয়াও। এভাবে আমি ওগুলোকে তোমাদের অধীন করে দিয়েছি যাতে তোমরা কৃতজ্ঞতা প্রকাশ কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2632,22,37,'আল্লাহর কাছে ওগুলোর না গোশত পৌঁছে, আর না রক্ত পৌঁছে বরং তাঁর কাছে পৌঁছে তোমাদের তাকওয়া। এভাবে তিনি ওগুলোকে তোমাদের অধীন করে দিয়েছেন যাতে তোমরা আল্লাহর শ্রেষ্ঠত্ব ঘোষণা করতে পার এজন্য যে, তিনি তোমাদেরকে সঠিক পথ দেখিয়েছেন, কাজেই সৎকর্মশীলদেরকে তুমি সুসংবাদ দাও। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2633,22,38,'আল্লাহ মু’মিনদেরকে রক্ষা করেন (যাবতীয় মন্দ হতে)। আল্লাহ কোন খিয়ানাতকারী, অকৃতজ্ঞকে পছন্দ করেন না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2634,22,39,'যাদের বিরুদ্ধে যুদ্ধ করা হয় তাদেরকে যুদ্ধের অনুমতি দেয়া হল, কেননা তাদের প্রতি অত্যাচার করা হয়েছে। আল্লাহ তাদেরকে সাহায্য করতে অবশ্যই সক্ষম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2635,22,40,'তাদেরকে অন্যায়ভাবে গৃহ থেকে বহিষ্কার করা হয়েছে শুধু তাদের এ কথা বলার কারণে যে, ‘আল্লাহ আমাদের প্রতিপালক।’ আল্লাহ যদি মানুষদের এক দলের দ্বারা অন্য দলকে প্রতিহত না করতেন, তাহলে বিধ্বস্ত হয়ে যেত খ্রীষ্টান সংসারত্যাগীদের উপাসনালয়, গির্জা ও ইয়াহূদীদের উপাসনার স্থান আর মাসজিদসমূহ যেখানে আল্লাহর নাম অধিকহারে স্মরণ করা হয়। আল্লাহ অবশ্যই তাকে সাহায্য করেন যে তাঁকে সাহায্য করে, আল্লাহ শক্তিমান, পরাক্রান্ত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2636,22,41,'(এরা হল) যাদেরকে আমি যমীনে প্রতিষ্ঠিত করলে তারা নামায প্রতিষ্ঠা করে, যাকাত প্রদান করে, সৎ কাজের আদেশ দেয় ও মন্দ কাজে নিষেধ করে, সকল কাজের শেষ পরিণাম (ও সিদ্ধান্ত) আল্লাহর হাতে নিবদ্ধ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2637,22,42,'লোকেরা যদি তোমাকে অস্বীকার করে তাহলে (জেনে রেখ এটা কোন নতুন ব্যাপার নয়) তাদের পূর্বে নূহ, ‘আদ ও সামূদ সম্প্রদায়ও (তাদের রসূলদের) অস্বীকার করেছিল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2638,22,43,'আর ইবরাহীমের সম্প্রদায় ও লূতের সম্প্রদায়ও (অস্বীকার করেছিল)। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2639,22,44,'আর মাদইয়ানবাসীরাও [অস্বীকার করেছিল যারা ছিল শু‘আয়ব (আঃ)-এর সম্প্রদায়], আর মূসাকেও অস্বীকার করা হয়েছিল। আমি অস্বীকারকারীদেরকে সময়- সুযোগ দিয়েছিলাম, অতঃপর তাদেরকে পাকড়াও করেছিলাম। কত ভীষণ ছিল আমাকে অস্বীকার করার পরিণতি! ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2640,22,45,'আমি কত জনবসতিকে ধ্বংস করেছি যেগুলোর অধিবাসীরা ছিল যালিম, সেগুলো ছাদের ভরে পতিত হয়ে বিধ্বস্ত হয়েছিল, বিরাণ হয়েছিল কত কূপ আর সুউচ্চ সুদৃঢ় প্রাসাদরাজি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2641,22,46,'তারা কি যমীনে ভ্রমণ করে না? তাহলে তারা হৃদয় দিয়ে বুঝতে পারত, আর তাদের কান শুনতে পারত। প্রকৃতপক্ষে চোখ অন্ধ নয়, বরং বুকের ভিতর যে হৃদয় আছে তা-ই অন্ধ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2642,22,47,'তারা তোমাকে তাড়াতাড়ি শাস্তি নিয়ে আসতে বলে (কিন্তু শাস্তি তো আসবে আল্লাহর ও‘য়াদা অনুযায়ী নির্দিষ্ট সময়ে), কেননা আল্লাহ কক্ষনো তাঁর ওয়া‘দা খেলাফ করেন না, তোমার প্রতিপালকের একদিন হল তোমাদের গণনায় এক হাজার বছরের সমান। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2643,22,48,'আমি কত জনপদকে সময়-সুযোগ দিয়েছি যখন তারা ছিল অন্যায় কাজে লিপ্ত। অতঃপর সেগুলোকে পাকড়াও করেছিলাম, (পালিয়ে কেউ তো কোথাও যেতে পারবে না) কেননা (সকলের) প্রত্যাবর্তন আমারই কাছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2644,22,49,'বল, ‘হে মানুষ! আমি (প্রেরিত হয়েছি) তোমাদের জন্য এক সুস্পষ্ট সতর্ককারীরূপে।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2645,22,50,'কাজেই যারা ঈমান আনবে আর সৎ কাজ করবে তাদের জন্য কেবল আছে ক্ষমা আর সম্মানজনক জীবিকা। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2646,22,51,'আর যারা আমার নিদর্শনের বিরুদ্ধে চেষ্টা চালায়, সেগুলোর উদ্দেশ্যকে ব্যর্থ করার জন্য, তারাই হল জাহান্নামের বাসিন্দা। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2647,22,52,'আমি তোমার পূর্বে যে সব রসূল কিংবা নবী পাঠিয়েছি তাদের কেউ যখনই কোন আকাঙ্ক্ষা করেছে তখনই শয়ত্বান তার আকাঙ্ক্ষায় (প্রতিবন্ধকতা, সন্দেহ-সংশয়) নিক্ষেপ করেছে, কিন্তু শয়ত্বান যা নিক্ষেপ করে আল্লাহ তা মুছে দেন, অতঃপর আল্লাহ তাঁর নিদর্শনসমূহকে সুপ্রতিষ্ঠিত করেন। কারণ আল্লাহ সর্বজ্ঞ, সর্বশ্রেষ্ঠ হিকমতওয়ালা। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2648,22,53,'(তিনি এটা হতে দেন এজন্য) যাতে তিনি শয়ত্বান যা মিশিয়ে দিয়েছে তা দ্বারা পরীক্ষা করতে পারেন তাদেরকে যাদের অন্তরে (মুনাফিকীর) ব্যাধি আছে, যারা শক্ত হৃদয়ের। অন্যায়কারীরা চরম মতভেদে লিপ্ত আছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2649,22,54,'(আর অপরদিকে) যাতে, যাদেরকে জ্ঞান দেয়া হয়েছে তারা জানতে পারে যে, এটা তোমার প্রতিপালকের পক্ষ থেকে (প্রেরিত) সত্য, অতঃপর তারা যেন তাতে বিশ্বাসী হয় আর তাদের অন্তর নম্রতাভরে তার প্রতি খুলে দেয়া হয়, কারণ আল্লাহ ঈমানদারদেরকে অবশ্যই সঠিক পথে পরিচালিত করেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2650,22,55,'অবিশ্বাসীরা তাতে (অর্থাৎ ওয়াহীতে) সন্দেহ পোষণ করা থেকে বিরত হবে না যতক্ষণ না কিয়ামাত আসবে হঠাৎ ক’রে অথবা তাদের উপর শাস্তি এসে যাবে এক বন্ধ্যা দিনে (যা কাফিরদেরকে কোন সুফল দিবে না)। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2651,22,56,'ক্ষমতা-আধিপত্য সেদিন হবে আল্লাহরই, তিনি তাদের মাঝে বিচার-ফয়সালা করবেন। অতঃপর যারা ঈমান আনে ও সৎ কাজ করে তারা থাকবে নি‘মাতরাজিতে পরিপূর্ণ জান্নাতে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2652,22,57,'আর যারা কুফুরী করে আর আমার আয়াতসমূহকে মিথ্যে জেনে প্রত্যাখ্যান করে তাদের জন্য আছে অপমানজনক শাস্তি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2653,22,58,'আর যারা আল্লাহর পথে হিজরাত করেছে, অতঃপর নিহত হয়েছে, কিংবা মারা গেছে, আল্লাহ তাদেরকে অবশ্য অবশ্যই উৎকৃষ্ট রিযক্ দান করবেন, আর আল্লাহ- তিনি তো সর্বোত্তম রিযকদাতা। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2654,22,59,'তিনি তাদেরকে অবশ্য অবশ্যই দাখিল করবেন এমন জায়গায় যা পেয়ে তারা খুবই সন্তুষ্ট হবে, আর আল্লাহ নিশ্চিতই অবশ্যই সর্বজ্ঞ, পরম সহিষ্ণু। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2655,22,60,'এতো হল তাদের অবস্থা, আর যে ব্যক্তি আঘাতপ্রাপ্ত হলে সমপরিমাণ প্রতিশোধ গ্রহণ করে, অতঃপর আবার সে নিপীড়িত হয়, আল্লাহ তাকে অবশ্য অবশ্যই সাহায্য করবেন, আল্লাহ অবশ্যই মাফকারী ক্ষমাশীল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2656,22,61,'এটা এজন্য যে, আল্লাহ রাতকে ঢুকিয়ে দেন দিনে, আর দিনকে ঢুকিয়ে দেন রাতে (দুঃখ বেদনার অন্ধকার দূর করে আনন্দের আলো এনে দেন আর আনন্দিত জনকে দুঃখের অাঁধারে ডুবিয়ে দেন)। আর আল্লাহ তো সব কিছু শোনেন, সব কিছু দেখেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2657,22,62,'এজন্য যে, আল্লাহ- তিনিই সত্য, আর তাঁকে বাদ দিয়ে তারা অন্য যাকে ডাকে তা অলীক, অসত্য, আর আল্লাহ, তিনি তো সর্বোচ্চ, সুমহান। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2658,22,63,'তুমি কি লক্ষ্য কর না যে, আল্লাহ আকাশ থেকে পানি বর্ষণ করেন, যার ফলে পৃথিবী সবুজে আচ্ছাদিত হয়ে যায়, নিশ্চয় আল্লাহ সূক্ষ্ণদর্শী, সর্ববিষয়ে ওয়াকিফহাল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2659,22,64,'আকাশ ও পৃথিবীতে যা কিছু আছে সবই তাঁর, আর আল্লাহ, তিনি যাবতীয় অভাব থেকে মুক্ত, যাবতীয় প্রশংসার অধিকারী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2660,22,65,'তুমি কি লক্ষ্য কর না যে, পৃথিবীতে যা কিছু আছে সব তিনি তোমাদের কল্যাণ-কাজে লাগিয়ে রেখেছেন। আর নৌযানগুলো সমুদ্রে চলাচল করে তাঁর হুকুমেই? তিনিই আকাশকে স্থির রাখেন যাতে তা পৃথিবীতে পতিত না হয় তাঁর অনুমতি ছাড়া। আল্লাহ মানুষের প্রতি নিশ্চিতই বড়ই করুণাশীল, বড়ই দয়াবান। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2661,22,66,'তিনিই তোমাদেরকে জীবন দিয়েছেন, অতঃপর তিনিই তোমাদের মৃত্যু ঘটাবেন, অতঃপর আবার তোমাদেরকে জীবন দিবেন। মানুষ সত্যিই বড়ই অকৃতজ্ঞ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2662,22,67,'প্রত্যেক সম্প্রদায়ের জন্য আমি (‘ইবাদাতের) নিয়ম-কানুন নির্ধারণ করে দিয়েছি যা তারা অনুসরণ করে। কাজেই তারা যেন এ বিষয়ে তোমার সঙ্গে তর্ক বিতর্ক না করে। তুমি (তাদেরকে) তোমার প্রতিপালকের দিকে ডাক, তুমি অবশ্যই সরল সঠিক পথে আছ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2663,22,68,'তারা যদি তোমার সঙ্গে বিতর্ক করে তাহলে বল- তোমরা যা কর আল্লাহ তা ভাল করেই জানেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2664,22,69,'আল্লাহ কিয়ামাতের দিন সে বিষয়ে তোমাদের মধ্যে মীমাংসা করে দিবেন যে বিষয়ে তোমরা মতভেদ করছ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2665,22,70,'তুমি কি জান না যে, আল্লাহ জানেন যা আছে আকাশে আর পৃথিবীতে, এ সবই লিপিতে (রেকর্ডে) আছে। এটা আল্লাহর পক্ষে সহজ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2666,22,71,'আর তারা আল্লাহকে বাদ দিয়ে এমন কিছুকে ডাকে যার সমর্থনে তিনি কোন দলীল পাঠাননি আর যে সম্পর্কে তাদের কোন জ্ঞান নেই। (এসব) যালিমদের কোন সাহায্যকারী নেই। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2667,22,72,'আর তাদের সম্মুখে যখন আমার সুস্পষ্ট আয়াত তিলাওয়াত করা হয় তখন তুমি কাফিরদের চেহারায় একটা অনীহার ভাব দেখতে পাবে। তাদের কাছে যারা আমার আয়াত তিলাওয়াত করে তাদেরকে তারা আক্রমণ করতে উদ্যত হয়। বল, তাহলে আমি কি তোমাদেরকে এর চেয়েও খারাপ কিছুর সংবাদ দেব? তা হল আগুন।  আল্লাহ  কাফিরদেরকে এর ওয়া‘দা দিয়েছেন। আর তা কতই না নিকৃষ্ট প্রত্যাবর্তনস্থল! ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2668,22,73,'হে মানুষ! একটা দৃষ্টান্ত পেশ করা হচ্ছে, সেটা মনোযোগ দিয়ে শোন। আল্লাহর পরিবর্তে তোমরা যাদেরকে ডাক তারা কক্ষনো একটা মাছিও সৃষ্টি করতে পারে না, এজন্য তারা সবাই একত্রিত হলেও। আর মাছি যদি তাদের কাছ থেকে কিছু ছিনিয়ে নিয়ে যায়, তারা তার থেকে তা উদ্ধারও করতে পারে না, প্রার্থনাকারী আর যার কাছে প্রার্থনা করা হয় উভয়েই দুর্বল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2669,22,74,'তারা আল্লাহর যথাযোগ্য মর্যাদা দেয় না, আল্লাহ নিশ্চিতই ক্ষমতাশালী, মহা পরাক্রান্ত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2670,22,75,'আল্লাহ ফেরেশতাগণের মধ্য হতে বাণীবাহক মনোনীত করেন আর মানুষদের মধ্য হতেও, আল্লাহ সব কিছু শোনেন, সব কিছু দেখেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2671,22,76,'তিনি জানেন তাদের সামনে যা আছে আর তাদের পেছনে যা আছে, আর সমস্ত ব্যাপার (চূড়ান্ত ফয়সালার জন্য) আল্লাহর কাছে ফিরে যায়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2672,22,77,'হে মু’মিনগণ! তোমরা রুকূ‘ কর, সেজদা কর আর তোমাদের প্রতিপালকের ‘ইবাদাত কর ও সৎকাজ কর যাতে তোমরা সাফল্য লাভ করতে পার। [সাজদাহ] ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2673,22,78,'আর আল্লাহর পথে জিহাদ কর যেভাবে জিহাদ করা উচিত। তিনি তোমাদেরকে বেছে নিয়েছেন। দ্বীনের ভিতর তিনি তোমাদের উপর কোন কঠোরতা চাপিয়ে দেননি। এটাই তোমাদের পিতা ইবরাহীমের দ্বীন, আল্লাহ তোমাদের নাম রেখেছেন ‘মুসলিম’ পূর্বেও, আর এ কিতাবেও (ঐ নামই দেয়া হয়েছে) যাতে রসূল তোমাদের জন্য সাক্ষী হয় আর তোমরা সাক্ষী হও মানব জাতির জন্য। কাজেই তোমরা নামায প্রতিষ্ঠা কর, যাকাত দাও আর আল্লাহকে আঁকড়ে ধর। তিনিই তোমাদের অভিভাবক। কতই না উত্তম অভিভাবক আর কতই না উত্তম সাহায্যকারী! ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2674,23,1,'মু’মিনরা সফলকাম হয়ে গেছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2675,23,2,'যারা নিজেদের নামাযে বিনয় নম্রতা অবলম্বন করে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2676,23,3,'যারা অসার কথাবার্তা এড়িয়ে চলে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2677,23,4,'যারা যাকাত দানে সক্রিয়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2678,23,5,'যারা নিজেদের যৌনাঙ্গকে সংরক্ষণ করে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2679,23,6,'নিজেদের স্ত্রী ও মালিকানাভুক্ত দাসী ব্যতীত, কারণ এ ক্ষেত্রে তারা নিন্দা থেকে মুক্ত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2680,23,7,'এদের অতিরিক্ত যারা কামনা করে তারাই সীমালঙ্ঘনকারী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2681,23,8,'আর যারা নিজেদের আমানাত ও ওয়াদা পূর্ণ করে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2682,23,9,'আর যারা নিজেদের নামাযের ব্যাপারে যত্নবান। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2683,23,10,'তারাই হল উত্তরাধিকারী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2684,23,11,'তারা ফিরদাউসের উত্তরাধিকার লাভ করবে, যাতে তারা চিরস্থায়ী হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2685,23,12,'আমি মানুষকে মাটির সারাংশ থেকে সৃষ্টি করেছি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2686,23,13,'অতঃপর আমি তাকে শুক্রবিন্দু রূপে এক সংরক্ষিত আধারে স্থাপন করেছি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2687,23,14,'পরে আমি শুক্রবিন্দুকে পরিণত করি জমাট বাঁধা রক্তে, অতঃপর মাংসপিন্ডকে পরিণত করি হাড্ডিতে, অতঃপর হাড্ডিকে আবৃত করি মাংস দিয়ে, অতঃপর তাকে এক নতুন সৃষ্টিতে উন্নীত করি। কাজেই সর্বোত্তম স্রষ্টা আল্লাহ কতই না মহান! ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2688,23,15,'এরপর তোমরা অবশ্যই মরবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2689,23,16,'তারপর কিয়ামাতের দিন তোমাদেরকে পুনরুত্থিত করা হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2690,23,17,'আমি তোমাদের উপরে সপ্ত স্তর সৃষ্টি করেছি, আমি (আমার) সৃষ্টির ব্যাপারে অমনোযোগী নই। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2691,23,18,'আমি আকাশ থেকে পরিমিত বৃষ্টি বর্ষণ করি, অতঃপর আমি তা যমীনে সংরক্ষণ করি আর আমি পানিকে সরিয়ে দিতে অবশ্যই সক্ষম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2692,23,19,'অতঃপর আমি তা দিয়ে তোমাদের জন্য খেজুর ও আঙ্গুরের বাগান সৃষ্টি করি, ওতে তোমাদের জন্য আছে পর্যাপ্ত ফল, যাথেকে তোমরা খাও (আর সুখভোগ কর)। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2693,23,20,'আর (আমি সৃষ্টি করি) এক বৃক্ষ যা সিনাই পর্বতে জন্মে, যাথেকে তেল উৎপন্ন হয়, আর ভক্ষণকারীদের জন্য রসনা তৃপ্তিকর।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2694,23,21,'আর গবাদি পশুর ভিতরে তোমাদের জন্য আছে অবশ্যই শিক্ষণীয় দৃষ্টান্ত। তাদের পেটে যা আছে তাত্থেকে আমি তোমাদেরকে পান করাই (দুধ) আর ওতে তোমাদের জন্য আছে বহুবিধ উপকার। তোমরা তাথেকে খাও (গোশত)। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2695,23,22,'আর ওতে আর নৌযানে তোমরা আরোহণ কর।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2696,23,23,'আমি নূহকে তাঁর সম্প্রদায়ের কাছে পাঠিয়েছিলাম। সে বলেছিল : ‘হে আমার জাতি! তোমরা আল্লাহর ‘ইবাদাত কর, তিনি ছাড়া তোমাদের কোন ইলাহ নেই, তোমরা কি (তাঁকে) ভয় করবে না?’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2697,23,24,'তার সম্প্রদায়ের প্রধানগণ যারা কুফুরী করেছিল- বলেছিল : ‘এতো তোমাদের মত মানুষ ছাড়া কিছুই না, সে তোমাদের উপর প্রাধান্য লাভ করতে চায়, আল্লাহ (কাউকে নবীরূপে পাঠানোর) ইচ্ছে করলে তো তিনি ফেরেশতা পাঠাতেন, আমরা আমাদের পূর্ব পুরুষদের সময়ে এ সব কথা তো শুনিনি।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2698,23,25,'এতো এমন লোক যাকে পাগলামিতে পেয়েছে, কাজেই তার ব্যাপারে তোমরা কিছু কাল অপেক্ষা কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2699,23,26,'নূহ বলল : ‘হে আমার প্রতিপালক! তুমি আমাকে সাহায্য কর, কারণ তারা আমাকে মিথ্যেবাদী বলছে।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2700,23,27,'তখন আমি তার কাছে ওয়াহী পাঠালাম- আমার দৃষ্টির সম্মুখে আমার নির্দেশ অনুযায়ী নৌযান তৈরি কর, অতঃপর যখন আমার নির্দেশ আসবে আর উনুন (পানিতে) উথলে উঠবে, তখন নৌকায় তুলে নাও প্রত্যেক জীবের এক এক জোড়া আর তোমার পরিবারবর্গকে, তাদের মধ্যে যাদের বিপক্ষে পূর্বে সিদ্ধান্ত হয়ে গেছে তাদেরকে বাদ দিয়ে। আর অন্যায়কারীদের পক্ষে আমার নিকট আবেদন করো না, তারা (বানে) ডুববেই। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2701,23,28,'যখন তুমি নৌযানে উঠে যাবে, তুমি আর তোমার সঙ্গীরা- তখন বলবে : ‘সকল প্রশংসা আল্লাহরই যিনি আমাদেরকে যালিম সম্প্রদায় থেকে উদ্ধার করেছেন।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2702,23,29,'আর বলো : হে আমার প্রতিপালক! আমাকে কল্যাণকরভাবে নামিয়ে দাও, নামানোতে তুমিই সর্বোত্তম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2703,23,30,'এতে অবশ্যই নিদর্শন রয়েছে (মানুষের বুঝার জন্য), আমি (মানুষকে) পরীক্ষা করি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2704,23,31,'অতঃপর তাদের পর আরেক মানব বংশ (‘আদ সম্প্রদায়কে) সৃষ্টি করেছিলাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2705,23,32,'আর তাদের মাঝে তাদেরই একজনকে রসূল করে পাঠিয়েছিলাম এই ব’লে যে, তোমরা আল্লাহর ‘ইবাদাত কর, তিনি ছাড়া তোমাদের কোন ইলাহ নেই, তবুও কি তোমরা (তাঁকে) ভয় করবে না? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2706,23,33,'কিন্তু তার সম্প্রদায়ের প্রধানগণ- যারা কুফুরী করেছিল, আর আখিরাতের সাক্ষাৎকে মিথ্যা বলে প্রত্যাখ্যান করেছিল, যাদেরকে আমি পার্থিব জীবনে সুখ স্বাচ্ছন্দ্য দিয়েছিলাম- বলেছিল : ‘সে তো তোমাদের মত মানুষ ছাড়া কিছু নয়। তোমরা যা খাও, সেও তাই খায়, তোমরা যা পান কর, সেও তাই পান করে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2707,23,34,'তোমরা যদি তোমাদের মতই একজন মানুষের আনুগত্য কর, তাহলে তোমরা অবশ্যই ক্ষতিগ্রস্ত হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2708,23,35,'সে কি তোমাদের সঙ্গে এই ওয়া‘দা করে যে, যখন তোমরা মরে যাবে আর মাটি ও হাড্ডিতে পরিণত হবে তখন তোমাদেরকে আবার জীবিত করা হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2709,23,36,'দূরে ওটা, বহু দূরে যার ওয়া‘দা তোমাদেরকে দেয়া হচ্ছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2710,23,37,'আমাদের এ দুনিয়ার জীবন ছাড়া কিছুই নেই, এখানেই আমরা মরি বাঁচি, আমাদেরকে কক্ষনো আবার উঠানো হবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2711,23,38,'সে তো এমন এক ব্যক্তি যে আল্লাহ সম্পর্কে মিথ্যে বানিয়ে নিয়েছে। আমরা তাকে বিশ্বাস করি না।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2712,23,39,'(নবী) বলল : ‘হে আমার প্রতিপালক! তারা আমাকে মিথ্যেবাদী ব’লে দোষারোপ করছে- এ ব্যাপারে তুমি আমাকে সাহায্য কর।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2713,23,40,'আল্লাহ বললেন, ‘অল্প সময়ের মধ্যেই তারা অনুতপ্ত হবে।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2714,23,41,'অতঃপর সত্যি সত্যিই এক ভয়ঙ্কর শব্দ তাদেরকে আঘাত করল আর তাদেরকে ভাগাড়ে পরিণত করলাম, কাজেই ধ্বংস হোক পাপী সম্প্রদায়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2715,23,42,'অতঃপর তাদের পরে আমি বহু জাতি সৃষ্টি করেছিলাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2716,23,43,'কোন জাতিই তাদের নির্দিষ্ট কালকে অগ্র পশ্চাৎ করতে পারে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2717,23,44,'এরপর একাদিক্রমে আমি আমার রসূলদেরকে পাঠিয়েছি। যখনই কোন জাতির কাছে তাদের রসূল এসেছে, তারা তাকে মিথ্যে বলে প্রত্যাখ্যান করেছে, অতঃপর শাস্তি প্রাপ্তির ব্যাপারে তারা একে অন্যের অনুসরণ করেছে, অতঃপর তাদেরকে কাহিনী বানিয়ে দিলাম। কাজেই যে জাতি ঈমান আনে না তারা ধ্বংস হোক! ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2718,23,45,'অতঃপর আমি মূসা ও তার ভাই হারূনকে পাঠিয়েছিলাম নিদর্শন আর সুস্পষ্ট প্রমাণ সহকারে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2719,23,46,'ফেরাউন ও তার প্রধানগণের কাছে। কিন্তু তারা অহংকার করল, তারা ছিল এক উদ্ধত জাতি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2720,23,47,'তারা বলল, ‘আমরা কি আমাদেরই মত দু’জন লোকের প্রতি বিশ্বাস স্থাপন করব, অথচ তাদের সম্প্রদায় আমাদের দাস?’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2721,23,48,'তারা তাদের দু’জনকে মিথ্যে বলে প্রত্যাখ্যান করল, ফলে তারা ধ্বংসপ্রাপ্তদের অন্তর্ভুক্ত হয়ে গেল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2722,23,49,'আমি মূসাকে কিতাব দিয়েছিলাম যাতে তারা সঠিক পথ পেতে পারে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2723,23,50,'আমি মারইয়াম-পুত্র আর তার মাকে নিদর্শন করেছিলাম আর তাদেরকে স্বস্তি নিরাপত্তা ও ঝর্ণা বিশিষ্ট উচ্চ স্থানে আশ্রয় দিয়েছিলাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2724,23,51,'হে রসূলগণ! পবিত্র বস্তু আহার কর, আর সৎ কাজ কর, তোমরা যা কর সে সম্পর্কে আমি পূর্ণরূপে অবগত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2725,23,52,'তোমাদের এসব উম্মাত তো একই উম্মাত, আর আমিই তোমাদের প্রতিপালক, কাজেই আমাকেই ভয় কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2726,23,53,'কিন্তু মানুষ তাদের কর্তব্য কর্মকে বহুধা বিভক্ত করে ফেলেছে, আর প্রত্যেক দলই তাদের কাছে যা আছে তাই নিয়ে আনন্দিত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2727,23,54,'কাজেই তাদেরকে কিছুকাল তাদের অজ্ঞানতাপ্রসূত বিভ্রান্তিতে থাকতে দাও। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2728,23,55,'তারা কি ভেবে নিয়েছে, আমি যে তাদেরকে ধনৈশ্বর্য ও সন্তানাদির প্রাচুর্য দিয়ে সাহায্য করেছি ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2729,23,56,'এর দ্বারা কি তাদের কল্যাণ ত্বরান্বিত করছি? না, তারা বুঝে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2730,23,57,'নিশ্চয় যারা তাদের প্রতিপালকের ভয়ে ভীত-সন্ত্রস্ত হয়ে থাকে, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2731,23,58,'যারা তাদের প্রতিপালকের নিদর্শনাবলীতে বিশ্বাস স্থাপন করে, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2732,23,59,'যারা তাদের প্রতিপালকের সঙ্গে শরীক করে না, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2733,23,60,'যারা তাদের দানের বস্তু দান করে আর তাদের অন্তর ভীত শংকিত থাকে এ জন্যে যে, তাদেরকে তাদের প্রতিপালকের কাছে ফিরে যেতে হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2734,23,61,'এরাই কল্যাণকাজে দ্রুতগতি, আর তাতে তারা অগ্রগামী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2735,23,62,'আমি কাউকে তার সাধ্যাতীত কষ্ট দেই না, আর আমার কাছে এমন এক কিতাব আছে যা সত্য বলে, আর তাদের প্রতি মোটেই যুলম করা হবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2736,23,63,'বরং তাদের অন্তর এ বিষয়ে অজ্ঞানতায় আচ্ছন্ন হয়ে আছে, এছাড়া তাদের আরো (মন্দ) কাজ আছে যা তারা করতে থাকবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2737,23,64,'অবশেষে আমি যখন তাদের (অর্থাৎ কাফিরদের) মধ্যে বিত্ত সম্পদশালীদেরকে শাস্তি দ্বারা পাকড়াও করব, তখন তারা চিৎকার জুড়ে দেবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2738,23,65,'(বলা হবে) ‘আজ চিৎকার করো না, আমার কাছ থেকে তোমরা সাহায্য পাবে না।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2739,23,66,'আমার আয়াত তোমাদের কাছে পড়ে শোনানো হত, কিন্তু তোমরা গোড়ালির ভরে পিছনে ঘুরে দাঁড়াতে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2740,23,67,'অহংকারবশতঃ (কুরআন) সম্পর্কে অর্থহীন কথা বলতে যেমন কেউ রাতে গল্প বলে।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2741,23,68,'তাহলে তারা কি (আল্লাহর) এ বাণী সম্পর্কে চিন্তা-ভাবনা করে না? কিংবা তাদের কাছে এমন কিছু (নতুন বস্তু) এসেছে যা তাদের পূর্বপুরুষদের কাছে আসেনি? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2742,23,69,'কিংবা তারা কি তাদের রসূলকে চিনতে পারে না এজন্য তারা তাকে অস্বীকার করছে ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2743,23,70,'অথবা তারা কি বলে যে, সে উন্মাদ? না, প্রকৃতপক্ষে সে তাদের কাছে সত্য নিয়ে এসেছে, কিন্তু তাদের অধিকাংশই সত্যকে অপছন্দ করে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2744,23,71,'সত্য যদি তাদের ইচ্ছে-আকাঙ্ক্ষার অনুসারী হত তাহলে আকাশ পৃথিবী আর এ দু’য়ের মাঝে যা আছে সব লন্ডভন্ড হয়ে যেত। (তাদের কামনা-বাসনার) বিপরীতে আমি তাদেরকে দিয়েছি তাদের জন্য উপদেশবাণী কিন্তু তারা উপদেশবাণী থেকে মুখ ফিরিয়ে রাখে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2745,23,72,'অথবা তুমি কি তাদের কাছ থেকে কোন প্রতিদান চাও? তোমার প্রতিপালকের প্রতিদানই সর্বোত্তম, আর তিনিই সর্বশ্রেষ্ঠ রিযকদাতা। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2746,23,73,'তুমি তো নিশ্চিতই তাদেরকে সরল সুদৃঢ় পথের দিকে ডাকছ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2747,23,74,'যারা আখিরাতে বিশ্বাস করে না তারা সরল পথ থেকে বিচ্যুত হয়ে গেছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2748,23,75,'আমি তাদের প্রতি দয়া করলেও আর তাদের দুঃখ দুর্দশা দূর করলেও তারা তাদের অবাধ্যতায় ঘুরপাক খেতে থাকবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2749,23,76,'আমি তাদেরকে শাস্তি দ্বারা পাকড়াও করেছিলাম, কিন্তু তারা তাদের প্রতিপালকের নিকট নত হল না, আর তারা কাকুতি মিনতিও করল না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2750,23,77,'অবশেষে আমি তাদের জন্য কঠিন শাস্তির দরজা খুলে দেব, তখন তারা তাতে হতাশ হয়ে পড়বে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2751,23,78,'তিনিই তোমাদের জন্য সৃষ্টি করেছেন কান, চোখ ও অন্তর; তোমরা কৃতজ্ঞতা অল্পই করে থাক। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2752,23,79,'তিনিই তোমাদেরকে পৃথিবীতে ছড়িয়ে দিয়েছেন আর তাঁর কাছেই তোমাদেরকে একত্রিত করা হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2753,23,80,'তিনিই জীবন দেন ও মৃত্যু ঘটান আর দিন-রাতের পরিবর্তন তাঁর দ্বারাই হয়, তবুও কি তোমরা বুঝবে না? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2754,23,81,'বরং তারা তাই বলেছিল যা বলেছিল আগের লোকেরা। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2755,23,82,'তারা বলে- ‘আমরা যখন ম’রে মাটি ও হাড়-হাড্ডি হয়ে যাব (তারপরেও কি) আমাদেরকে আসলেই আবার উঠানো হবে? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2756,23,83,'এ বিষয়ে আমাদেরকে ওয়া‘দা দেয়া হয়েছে আর অতীতে আমাদের পূর্ব পুরুষদেরকেও। এসব তো পুরনো কালের কিসসা কাহিনী ছাড়া কিছুই না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2757,23,84,'বল : এ পৃথিবী আর তার ভিতরে যা আছে তা কার? (বল) যদি তোমরা জান! ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2758,23,85,'তারা বলবে- আল্লাহর। বল : তবুও কি তোমরা শিক্ষা গ্রহণ করবে না? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2759,23,86,'বলঃ সাত আসমান আর মহান আরশের মালিক কে? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2760,23,87,'তারা বলবেঃ (এগুলোর মালিকানা) আল্লাহর। বলঃ তবুও কি তোমরা (আল্লাহকে) ভয় করবে না? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2761,23,88,'বলঃ সব কিছুর একচ্ছত্র কর্তৃত্ব কার হাতে? তিনি (সকলকে) আশ্রয় দেন, তাঁর উপর কোন আশ্রয় দাতা নেই, (বল) তোমরা যদি জান। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2762,23,89,'তারা বলবে (সকল কিছুর কর্তৃত্ব) আল্লাহর। তাহলে কেমন করে তোমরা যাদুগ্রস্ত হয়ে পড়ছ? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2763,23,90,'কিছুই না, আমি তাদের নিকট সত্য পাঠিয়েছি কিন্তু তারা বাস্তবিকই মিথ্যেবাদী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2764,23,91,'আল্লাহ কোন সন্তান গ্রহণ করেননি, আর তাঁর সাথে অন্য কোন ইলাহ নেই, (থাকলে) প্রত্যেক ইলাহ আপন সৃষ্টি নিয়ে অবশ্যই চলে যেত, আর অবশ্যই একে অপরের উপর চড়াও হত, তারা তাঁর প্রতি যা আরোপ করে তাত্থেকে তিনি কত মহান ও পবিত্র! ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2765,23,92,'তিনি দৃশ্য ও অদৃশ্যের জ্ঞানের অধিকারী, তারা যা তাঁর শরীক বানায়, তাত্থেকে তিনি বহু ঊর্ধ্বে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2766,23,93,'বল, ‘হে আমার প্রতিপালক! তুমি যদি আমাকে দেখাও (আমার জীবদ্দশায়) যার প্রতিশ্রুতি তাদেরকে দেয়া হয়েছে, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2767,23,94,'তাহলে হে আমার প্রতিপালক! তুমি আমাকে যালিম সম্প্রদায়ের অন্তর্ভুক্ত করো না’। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2768,23,95,'আমি তাদেরকে (শাস্তি প্রদানের) যে ওয়াদা করেছি তা আমি তোমাকে দেখাতে অবশ্যই সক্ষম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2769,23,96,'মন্দের মুকাবিলা কর যা উত্তম তাই দিয়ে, তারা যা বলে সে সম্পর্কে আমি সবিশেষ অবগত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2770,23,97,'আর বল : ‘হে আমার প্রতিপালক! আমি শয়ত্বানের কুমন্ত্রণা হতে তোমার নিকট আশ্রয় প্রার্থনা করছি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2771,23,98,'আর আমি তোমার নিকট আশ্রয় প্রার্থনা করছি, হে আমার প্রতিপালক! যাতে তারা আমার কাছে আসতে না পারে।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2772,23,99,'এমনকি যখন তাদের কারো কাছে মৃত্যু এসে হাজির হয় তখন সে বলে : ‘হে আমার প্রতিপালক! আমাকে আবার (দুনিয়াতে) পাঠিয়ে দাও। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2773,23,100,'যাতে আমি সৎ কাজ করতে পারি যা আমি করিনি। কক্ষনো না, এটা তো তার একটা কথার কথা মাত্র। তাদের সামনে পর্দা থাকবে পুনরুত্থানের দিন পর্যন্ত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2774,23,101,'অতঃপর যখন শিঙ্গায় ফুঁ দেয়া হবে সেদিন তাদের পরস্পরের মাঝে আত্মীয় বন্ধন থাকবে না। একে অপরের কাছে জিজ্ঞেসও করবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2775,23,102,'যাদের (সৎ কাজের) পাল্লা ভারী হবে তারাই হবে সফলকাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2776,23,103,'যাদের পাল্লা হালকা হবে তারাই ওরা যারা নিজেদের ক্ষতিসাধন করেছে, জাহান্নামে তারা চিরস্থায়ী হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2777,23,104,'আগুন তাদের মুখমন্ডল দগ্ধ করবে আর তারা বীভৎস চেহারা নিয়ে তার ভিতরে দাঁত কটমট করতে থাকবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2778,23,105,'(তাদেরকে বলা হবে) ‘তোমাদের নিকট কি আমার আয়াতগুলো আবৃত্তি করা হত না? তোমরা সেগুলোকে মিথ্যে জেনে প্রত্যাখ্যান করেছিলে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2779,23,106,'তারা বলবে- ‘হে আমাদের প্রতিপালক! দুর্ভাগ্য আমাদেরকে পরাস্ত করেছিল, আর আমরা ছিলাম এক পথভ্রষ্ট জাতি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2780,23,107,'হে আমাদের প্রতিপালক! আমাদেরকে এত্থেকে বের করে নাও, আমরা যদি আবার কুফুরী করি তাহলে আমরা তো যালিম হিসেবে পরিগণিত হব। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2781,23,108,'আল্লাহ বলবেন- ‘তোমরা ধিকৃত অবস্থায় এখানেই পড়ে থাক, আমার সঙ্গে কোন কথা বল না।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2782,23,109,'আমার বান্দাহদের একদল বলত- ‘হে আমাদের প্রতিপালক! আমরা ঈমান এনেছি, তুমি আমাদেরকে ক্ষমা কর, আমাদের প্রতি দয়া কর, তুমি তো সর্বশ্রেষ্ঠ দয়ালু।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2783,23,110,'কিন্তু তাদেরকে নিয়ে তোমরা হাসি তামাশা করতে এমনকি তা তোমাদেরকে আমার কথা ভুলিয়ে দিয়েছিল, তোমরা তাদেরকে নিয়ে হাসি-ঠাট্টাই করতে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2784,23,111,'আজ আমি তাদেরকে পুরস্কৃত করলাম তাদের ধৈর্য ধারণের কারণে, আজ তারাই তো সফলকাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2785,23,112,'আল্লাহ বলবেন : ‘পৃথিবীতে কয় বছর তোমরা অবস্থান করেছিলে?’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2786,23,113,'তারা বলবে : ‘আমরা একদিন বা এক দিনের কিছু অংশ অবস্থান করেছি। অতএব তুমি গণনাকারীদের জিজ্ঞেস কর।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2787,23,114,'তিনি বলবেন : ‘তোমরা অল্প সময়ই অবস্থান করেছিলে, তোমরা যদি জানতে! ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2788,23,115,'তোমরা কি ভেবেছিলে যে, আমি তোমাদেরকে তামাশার বস্তু হিসেবে সৃষ্টি করেছি আর তোমাদেরকে আমার কাছে ফিরিয়ে আনা হবে না? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2789,23,116,'সুউচ্চ মহান আল্লাহ যিনি প্রকৃত মালিক, তিনি ছাড়া সত্যিকারের কোন ইলাহ নেই, সম্মানিত আরশের অধিপতি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2790,23,117,'যে ব্যক্তি আল্লাহর সঙ্গে অন্য ইলাহকেও ডাকে, এ ব্যাপারে তার কাছে কোন দলীল প্রমাণ নেই, একমাত্র তার প্রতিপালকের কাছেই তার হিসাব হবে, কাফিরগণ অবশ্যই সফলকাম হবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2791,23,118,'কাজেই বল : ‘হে আমার প্রতিপালক! ক্ষমা কর ও রহম কর, তুমি রহমকারীদের মধ্যে সর্বশ্রেষ্ঠ।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2792,24,1,'একটি সূরাহ যা আমি নাযিল করেছি আর তা ফরয করে দিয়েছি, আর তার ভেতরে আমি সুস্পষ্ট আয়াত নাযিল করেছি, যাতে তোমরা উপদেশ গ্রহণ কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2793,24,2,'ব্যভিচারিণী ও ব্যভিচারী তাদের প্রত্যেককে একশত করে বেত্রাঘাত কর। আল্লাহর আইন কার্যকর করার ব্যাপারে তাদের প্রতি দয়ামায়া তোমাদেরকে যেন প্রভাবিত না করে, যদি তোমরা আল্লাহ ও আখিরাত দিনের প্রতি বিশ্বাস স্থাপন করে থাক। একদল মু’মিন যেন তাদের শাস্তি প্রত্যক্ষ করে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2794,24,3,'ব্যভিচারী বিয়ে করে না ব্যভিচারিণী বা মুশরিকা নারী ছাড়া। আর ব্যভিচারিণী- তাকে বিয়ে করে না ব্যভিচারী বা মুশরিক পুরুষ ছাড়া, মু’মিনদের জন্য এটা নিষিদ্ধ করা হয়েছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2795,24,4,'যারা সতী সাধ্বী নারীর উপর অপবাদ দেয়, অতঃপর চারজন সাক্ষী উপস্থিত না করে, তাদেরকে আশিটি বেত্রাঘাত কর, আর তাদের সাক্ষ্য কক্ষনো গ্রহণ কর না, এরাই না-ফরমান। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2796,24,5,'অবশ্য এরপর যদি তারা তাওবাহ করে ও সংশোধিত হয়, কেননা আল্লাহ বড়ই ক্ষমাশীল, অতি দয়ালু। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2797,24,6,'আর যারা নিজেদের স্ত্রীদের উপর অপবাদ দেয়, কিন্তু নিজেদের ছাড়া তাদের অন্য কোন সাক্ষী না থাকে, এ রকম প্রত্যেক লোকের সাক্ষ্য এভাবে হবে যে, সে চারবার আল্লাহর নামে শপথ করে বলবে যে, সে অবশ্যই সত্যবাদী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2798,24,7,'আর পঞ্চমবারে বলবে যে, সে যদি মিথ্যেবাদী হয় তবে তার উপর আল্লাহর লা’নত পতিত হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2799,24,8,'আর স্ত্রীর শাস্তি রহিত হবে যদি সে আল্লাহর নামে চারবার শপথ ক’রে বলে যে, সে (তার স্বামী) অবশ্যই মিথ্যেবাদী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2800,24,9,'এবং পঞ্চমবারে বলে যে, তার স্বামী সত্যবাদী হলে তার নিজের উপর আল্লাহর গযব পতিত হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2801,24,10,'তোমাদের প্রতি আল্লাহর অনুগ্রহ ও দয়া না থাকলে (তোমরা ধ্বংস হয়ে যেতে), আল্লাহ তাওবাহ গ্রহণকারী, বড়ই হিকমতওয়ালা। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2802,24,11,'যারা এ অপবাদ উত্থাপন করেছে তারা তোমাদেরই একটি দল, এটাকে তোমাদের জন্য ক্ষতিকর মনে কর না, বরং তা তোমাদের জন্য কল্যাণকর। তাদের প্রত্যেকের জন্য আছে প্রতিফল যতটুকু পাপ সে করেছে। আর এ ব্যাপারে যে নেতৃত্ব দিয়েছে তার জন্য আছে মহা শাস্তি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2803,24,12,'তোমরা যখন এটা শুনতে পেলে তখন কেন মু’মিন পুরুষ ও মু’মিন স্ত্রীরা তাদের নিজেদের লোক সম্পর্কে ভাল ধারণা করল না আর বলল না, ‘এটা তো খোলাখুলি অপবাদ।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2804,24,13,'তারা চারজন সাক্ষী হাযির করল না কেন? যেহেতু তারা সাক্ষী হাযির করেনি সেহেতু আল্লাহর নিকট তারাই মিথ্যেবাদী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2805,24,14,'দুনিয়া ও আখিরাতে তোমাদের উপর যদি আল্লাহর অনুগ্রহ ও দয়া না থাকত, তবে তোমরা যাতে তড়িঘড়ি লিপ্ত হয়ে পড়েছিলে তার জন্য মহা শাস্তি তোমাদেরকে পাকড়াও করত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2806,24,15,'যখন এটা তোমরা মুখে মুখে ছড়াচ্ছিলে আর তোমাদের মুখ দিয়ে এমন কথা বলছিলে যে বিষয়ে তোমাদের কোন জ্ঞান ছিল না, আর তোমরা এটাকে নগণ্য ব্যাপার মনে করেছিলে, কিন্তু আল্লাহর নিকট তা ছিল গুরুতর ব্যাপার। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2807,24,16,'তোমরা যখন এটা শুনলে তখন তোমরা কেন বললে না যে, এ ব্যাপারে আমাদের কথা বলা ঠিক নয়। আল্লাহ পবিত্র ও মহান, এটা তো এক গুরুতর অপবাদ! ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2808,24,17,'আল্লাহ তোমাদেরকে উপদেশ দিচ্ছেন তোমরা আর কখনো এর (অর্থাৎ এ আচরণের) পুনরাবৃত্তি করো না যদি তোমরা মু’মিন হয়ে থাক। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2809,24,18,'আল্লাহ তোমাদের জন্য স্পষ্টভাবে আয়াত বর্ণনা করছেন, কারণ তিনি হলেন সর্ববিষয়ে জ্ঞানের অধিকারী, বড়ই হিকমতওয়ালা। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2810,24,19,'যারা পছন্দ করে যে, মু’মিনদের মধ্যে অশ্লীলতার বিস্তৃতি ঘটুক তাদের জন্য আছে ভয়াবহ শাস্তি দুনিয়া ও আখিরাতে। আল্লাহ জানেন আর তোমরা জান না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2811,24,20,'তোমাদের প্রতি আল্লাহর অনুগ্রহ ও দয়া না থাকলে (তোমরা ধ্বংস হয়ে যেতে), আল্লাহ দয়ার্দ্র, বড়ই দয়াবান। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2812,24,21,'হে ঈমানদারগণ! তোমরা শয়ত্বানের পদাংক অনুসরণ করো না। কেউ শয়ত্বানের পদাংক অনুসরণ করলে সে তাকে নির্লজ্জতা ও অপকর্মের আদেশ দেবে, তোমাদের প্রতি আল্লাহর অনুগ্রহ ও দয়া না থাকলে তোমাদের একজনও কক্ষনো পবিত্রতা লাভ করতে পারত না। অবশ্য যাকে ইচ্ছে আল্লাহ পবিত্র করে থাকেন, আল্লাহ সব কিছু শোনেন, সর্ববিষয়ে অবগত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2813,24,22,'তোমাদের মধ্যে যারা মর্যাদা ও প্রাচুর্যের অধিকারী তারা যেন শপথ না করে যে, তারা আত্মীয়-স্বজন, মিসকীন এবং আল্লাহর পথে হিজরাতকারীদেরকে সাহায্য করবে না। তারা যেন তাদেরকে ক্ষমা করে ও তাদের ত্রুটি-বিচ্যুতি উপেক্ষা করে। তোমরা কি পছন্দ কর না যে, আল্লাহ তোমাদেরকে ক্ষমা করে দিন? আল্লাহ বড়ই ক্ষমাশীল, বড়ই দয়ালু। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2814,24,23,'যারা সতী-সাধ্বী, সহজ-সরল ঈমানদার নারীর প্রতি অপবাদ আরোপ করে, তারা দুনিয়া ও আখিরাতে অভিশপ্ত আর তাদের জন্য আছে গুরুতর শাস্তি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2815,24,24,'যেদিন তাদের বিরুদ্ধে সাক্ষ্য দেবে তাদের জিহবা, তাদের হাত, তাদের পা- তাদের কৃতকর্মের ব্যাপারে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2816,24,25,'আল্লাহ সেদিন তাদেরকে তাদের ন্যায্য পাওনা পুরোপুরিই দেবেন আর তারা জানতে পারবে যে, আল্লাহই সত্য স্পষ্ট ব্যক্তকারী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2817,24,26,'চরিত্রহীনা নারী চরিত্রহীন পুরুষদের জন্য, আর চরিত্রহীন পুরুষ চরিত্রহীনা নারীদের জন্য, চরিত্রবতী নারী চরিত্রবান পুরুষের জন্য, আর চরিত্রবান পুরুষ চরিত্রবতী নারীর জন্য। লোকেরা যা বলে তাত্থেকে তারা পবিত্র। তাদের জন্য আছে ক্ষমা ও সম্মানজনক জীবিকা। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2818,24,27,'হে ঈমানদারগণ! তোমরা নিজেদের গৃহ ছাড়া অন্যের গৃহে প্রবেশ করো না, অনুমতি প্রার্থনা এবং গৃহবাসীদেরকে সালাম দেয়া ব্যতীত। এটাই তোমাদের জন্য কল্যাণকর যাতে তোমরা উপদেশ লাভ কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2819,24,28,'সেখানে যদি তোমরা কাউকে না পাও, তাহলে তাতে প্রবেশ করবে না, যতক্ষণ না তোমাদেরকে অনুমতি দেয়া হয়। আর যদি তোমাদেরকে বলা হয়, ‘ফিরে যাও, তাহলে ফিরে যাবে, এটাই তোমাদের জন্য বেশি পবিত্র’। তোমরা যা কর সে সম্পর্কে আল্লাহ সবচেয়ে বেশি অবগত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2820,24,29,'সে ঘরে কেউ বাস করে না, তোমাদের মালমাত্তা থাকে, সেখানে প্রবেশ করলে তোমাদের কোন পাপ হবে না, আল্লাহ জানেন তোমরা যা প্রকাশ কর আর যা তোমরা গোপন কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2821,24,30,'মু’মিনদের বল তাদের দৃষ্টি অবনমিত করতে আর তাদের লজ্জাস্থান সংরক্ষণ করতে, এটাই তাদের জন্য বেশি পবিত্র, তারা যা কিছু করে সে সম্পর্কে আল্লাহ খুব ভালভাবেই অবগত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2822,24,31,'আর ঈমানদার নারীদেরকে বলে দাও তাদের দৃষ্টি অবনমিত করতে আর তাদের লজ্জাস্থান সংরক্ষণ করতে, আর তাদের শোভা সৌন্দর্য প্রকাশ না করতে যা এমনিতেই প্রকাশিত হয় তা ব্যতীত। তাদের ঘাড় ও বুক যেন মাথার কাপড় দিয়ে ঢেকে দেয়। তারা যেন তাদের স্বামী, পিতা, শ্বশুর, পুত্র, স্বামীর পুত্র, ভাই, ভাই-এর ছেলে, বোনের ছেলে, নিজেদের মহিলাগণ, স্বীয় মালিকানাধীন দাসী, পুরুষদের মধ্যে যৌন কামনামুক্ত পুরুষ আর নারীদের গোপন অঙ্গ সম্পর্কে অজ্ঞ বালক ছাড়া অন্যের কাছে নিজেদের শোভা সৌন্দর্য প্রকাশ না করে। আর তারা যেন নিজেদের গোপন শোভা সৌন্দর্য প্রকাশ করার জন্য সজোরে পদচারণা না করে। হে মু’মিনগণ! তোমরা আল্লাহর নিকট তাওবাহ কর, যাতে তোমরা সফলকাম হতে পার। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2823,24,32,'তোমাদের মধ্যে যারা বিবাহহীন তাদের বিবাহ সম্পন্ন কর আর তোমাদের সৎ দাস-দাসীদেরও। তারা যদি নিঃস্ব হয় তাহলে আল্লাহ তাদেরকে নিজ অনুগ্রহে অভাবমুক্ত করে দেবেন, আল্লাহ প্রচুর দানকারী, সর্ববিষয়ে জ্ঞাত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2824,24,33,'যাদের বিয়ের সম্বল নেই তারা যেন সংযম অবলম্বন করে যতক্ষণ না আল্লাহ নিজ অনুগ্রহে তাদেরকে অভাবমুক্ত করেন। আর তোমাদের দাসদাসীদের মধ্যে যারা মুক্তির জন্য লিখিত চুক্তি পেতে চায়, তাদের সঙ্গে চুক্তি কর যদি তাতে কোন কল্যাণ আছে ব’লে তোমরা জান। আল্লাহ তোমাদেরকে যে মাল দিয়েছেন তাত্থেকে তাদেরকে দান কর। তোমাদের দাসীরা নিজেদের পবিত্রতা রক্ষা করতে চাইলে তোমরা পার্থিব জীবনের সম্পদের লালসায় তাদেরকে ব্যভিচারে বাধ্য করো না। যদি কেউ তাদের উপর জবরদস্তি করে তবে তাদের উপর জবরদস্তির পর আল্লাহ (তাদের প্রতি) ক্ষমাশীল, পরম দয়ালু। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2825,24,34,' তোমাদের নিকট অবতীর্ণ করেছি সুস্পষ্ট আয়াত আর তোমাদের পূর্বে যারা অতীত হয়ে গেছে তাদের দৃষ্টান্ত ও মুত্তাকীদের জন্য দিয়েছি উপদেশ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2826,24,35,'আল্লাহ আসমান ও যমীনের আলো, তাঁর আলোর দৃষ্টান্ত হল যেন একটি তাক- যার ভিতরে আছে একটি প্রদীপ, প্রদীপটি হচ্ছে কাঁচের ভিতরে, কাঁচটি যেন একটি উজ্জ্বল নক্ষত্র, যা প্রজ্জ্বলিত করা হয় বরকতময় যায়তুন গাছের তেল দ্বারা যা পূর্বদেশীয়ও নয়, আর পশ্চিমদেশীয়ও নয়। আগুন তাকে স্পর্শ না করলেও তার তেল যেন উজ্জ্বলের বেশ নিকটবর্তী, আলোর উপরে আলো। আল্লাহ যাকে ইচ্ছে করেন স্বীয় আলোর দিকে পথ দেখান। আল্লাহ মানুষের জন্য দৃষ্টান্ত পেশ করেন, আল্লাহ সর্ববিষয়ে অধিক জ্ঞাত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2827,24,36,'(এ রকম আলো জ্বালানো হয়) সে সব গৃহে (অর্থাৎ মাসজিদে ও উপাসনালয়ে) যেগুলোকে সমুন্নত রাখতে আর তাতে তাঁর নাম স্মরণ করতে আল্লাহ নির্দেশ দিয়েছেন, ওগুলোতে তাঁর মাহাত্ম্য ঘোষণা করা হয় সকাল ও সন্ধ্যায় (বার বার) ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2828,24,37,'ঐ সব লোকের দ্বারা ব্যবসায় ও ক্রয়-বিক্রয় যাদেরকে তাঁর স্মরণ হতে বিচ্যুত করতে পারে না, আর নামায প্রতিষ্ঠা ও যাকাত প্রদান থেকেও না। তাদের ভয় করে (কেবল) সেদিনের যেদিন অন্তর ও দৃষ্টিসমূহ উল্টে যাবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2829,24,38,'(তারা এভাবে আল্লাহর ইবাদাত করে) যাতে আল্লাহ তাদেরকে পুরস্কৃত করেন তাদের উত্তম কার্যাবলী অনুসারে আর নিজ অনুগ্রহে আরো অধিক দেন, কারণ আল্লাহ যার জন্য ইচ্ছে করেন অপরিমিত রিযক্ দান করেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2830,24,39,'আর যারা কুফুরী করে তাদের কাজকর্ম হল বালুকাময় মরুভূমির মরীচিকার মত। পিপাসার্ত ব্যক্তি সেটাকে পানি মনে করে অবশেষে সে যখন তার নিকটে আসে, সে দেখে ওটা কিছুই না, সে সেখানে পায় আল্লাহকে, অতঃপর আল্লাহ তার হিসাব চুকিয়ে দেন। আল্লাহ দ্রুত হিসাব গ্রহণ করে থাকেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2831,24,40,'অথবা (কাফিরদের অবস্থা) বিশাল গভীর সমুদ্রে গভীর অন্ধকারের ন্যায়, যাকে আচ্ছন্ন করে রেখেছে ঢেউয়ের উপরে ঢেউ, তার উপরে মেঘ, একের পর এক অন্ধকারের স্তর, কেউ হাত বের করলে সে তা একেবারেই দেখতে পায় না। আল্লাহ যাকে আলো দান করেন না, তার জন্য কোন আলো নেই। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2832,24,41,'তুমি কি দেখ না, তিনি হলেন আল্লাহ, আসমান ও যমীনে যারা আছে সকলেই যাঁর প্রশংসা গীতি উচ্চারণ করে আর (উড়ন্ত) পাখীরাও তাদের ডানা বিস্তার ক’রে? তাদের প্রত্যেকেই তাদের ‘ইবাদাত ও প্রশংসাগীতির পদ্ধতি জানে, তারা যা করে আল্লাহ সে সম্পর্কে খুবই অবগত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2833,24,42,'আসমান ও যমীনের রাজত্বের মালিকানা আল্লাহর জন্যই, আর ফিরে আসতে হবে আল্লাহর কাছেই। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2834,24,43,'তুমি কি দেখ না আল্লাহ মেঘমালাকে চালিত করেন, অতঃপর সেগুলোকে একত্রে জুড়ে দেন, অতঃপর সেগুলোকে স্তুপীকৃত করেন, অতঃপর তুমি তার মধ্য থেকে পানির ধারা বের হতে দেখতে পাও, অতঃপর তিনি আকাশে স্থিত মেঘমালার পাহাড় থেকে শিলা বর্ষণ করেন, অতঃপর তিনি যাকে ইচ্ছে তা দিয়ে আঘাত করেন আর যার কাছ থেকে ইচ্ছে তা সরিয়ে নেন। তার বিদ্যুতের চমক দৃষ্টিশক্তি প্রায় কেড়ে নেয়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2835,24,44,'আল্লাহ রাত দিনের আবর্তন ঘটান, অন্তর্দৃষ্টি সম্পন্ন লোকেদের জন্য এতে শিক্ষণীয় বিষয় রয়েছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2836,24,45,'আল্লাহ পানি হতে সমস্ত জীবন সৃষ্টি করেছেন। তাদের কতক পেটের ভরে চলে, কতক দু’পায়ের উপর চলে, আর কতক চার পায়ের উপর চলে। আল্লাহ যা চান তাই সৃষ্টি করেন। আল্লাহ সর্ববিষয়ের উপর সর্বোচ্চ ক্ষমতার অধিকারী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2837,24,46,'আমি সুস্পষ্ট নিদর্শন অবতীর্ণ করেছি, আল্লাহ যাকে চান সরল সঠিক পথ প্রদর্শন করেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2838,24,47,'তারা বলে- আমরা আল্লাহর প্রতি ইমান আনলাম আর রসূলের প্রতিও আর আমরা মেনে নিলাম। কিন্তু এরপরও তাদের মধ্যেকার একদল মুখ ফিরিয়ে নেয়, তারা (প্রকৃতপক্ষে) মু’মিন নয়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2839,24,48,'তাদেরকে যখন তাদের মাঝে ফয়সালা করার উদ্দেশে আল্লাহ ও তাঁর রসূলের পানে আহবান করা হয়, তখন তাদের একদল মুখ ফিরিয়ে নেয়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2840,24,49,'কিন্তু ‘হাক’ (অর্থাৎ প্রাপ্য) যদি তাদের পক্ষে থাকে তাহলে পূর্ণ বিনয়ের সঙ্গে তারা রসূলের দিকে ছুটে আসে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2841,24,50,'তাদের অন্তরে কি রোগ আছে, না তারা সন্দেহ পোষণ করে? না তারা এই ভয়ের মধ্যে আছে যে আল্লাহ ও তাঁর রসূল তাদের প্রতি অন্যায় করবেন? তা নয়, আসলে তারা নিজেরাই অন্যায়কারী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2842,24,51,'মু’মিনদেরকে যখন তাদের মাঝে ফয়সালা করার জন্য আল্লাহ ও তাঁর রসূলের দিকে ডাকা হয়, তখন মু’মিনদের জওয়াব তো এই হয় যে, তারা বলে, আমরা শুনলাম ও মেনে নিলাম, আর তারাই সফলকাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2843,24,52,'যারা আল্লাহ ও তাঁর রসূলের আনুগত্য করে, আল্লাহকে ভয় করে ও তাঁর অবাধ্যতা পরিহার করে চলে তারাই কৃতকার্য। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2844,24,53,'(হে নবী!) তারা শক্ত করে কসম খেয়ে বলে যে, তুমি তাদেরকে আদেশ করলে তারা অবশ্য অবশ্যই বের হবে, বল : তোমরা কসম খেয়ো না, স্বাভাবিক আনুগত্যই কাম্য, বস্তুতঃ তোমরা যা কর, আল্লাহ সে সম্পর্কে সম্পূর্ণ অবহিত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2845,24,54,'বল : আল্লাহর আনুগত্য ও রসূলের আনুগত্য কর, অতঃপর যদি তোমরা মুখ ফিরিয়ে লও তাহলে তার (অর্থাৎ রসূলের) উপর অর্পিত দায়িত্বের জন্য সে দায়ী, আর তোমাদের উপর অর্পিত দায়িত্বের জন্য তোমরাই দায়ী, তোমরা যদি তার আনুগত্য কর তবে সঠিক পথ পাবে, রসূলের দায়িত্ব হচ্ছে স্পষ্টভাবে (বাণী) পৌঁছে দেয়া। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2846,24,55,'তোমাদের মধ্যে যারা ঈমান আনে আর সৎকাজ করে আল্লাহ তাদেরকে প্রতিশ্রুতি দিয়েছেন যে তিনি তাদেরকে অবশ্যই পৃথিবীতে খিলাফাত দান করবেন যেমন তাদের পূর্ববর্তীদেরকে তিনি খিলাফাত দান করেছিলেন এবং তিনি তাদের দ্বীনকে অবশ্যই কর্তৃত্বে প্রতিষ্ঠিত করবেন যা তিনি তাদের জন্য পছন্দ করেছেন এবং তিনি তাদের ভয়-ভীতিপূর্ণ অবস্থাকে পরিবর্তিত করে তাদেরকে অবশ্যই নিরাপত্তা দান করবেন। তারা আমার ‘ইবাদাত করবে, কোন কিছুকে আমার শরীক করবে না। এরপরও যারা সত্য প্রত্যাখ্যান করবে তারাই বিদ্রোহী, অন্যায়কারী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2847,24,56,'তোমরা (নিয়মিত) নামায প্রতিষ্ঠা কর, যাকাত প্রদান কর ও রসূলের আনুগত্য কর যাতে তোমরা অনুগ্রহ প্রাপ্ত হও। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2848,24,57,'তুমি কাফিরদেরকে এমন মনে কর না যে, তারা পৃথিবীতে আল্লাহর ইচ্ছেকে পরাভূত করার ক্ষমতা রাখে, তাদের বাসস্থান হল আগুন; কতই না নিকৃষ্ট আশ্রয়স্থল! ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2849,24,58,'হে ঈমানদারগণ! তোমাদের মালিকানাধীন দাসদাসীগণ আর তোমাদের যারা বয়:প্রাপ্ত হয়নি তারা যেন (তোমাদের কাছে আসতে) তোমাদের অনুমতি গ্রহণ করে তিন সময়ে- ফাজর নামাযের পূর্বে, আর যখন দুপুরে রোদের প্রচন্ডতায় তোমরা তোমাদের পোশাক খুলে রাখ আর ‘ইশার নামাযের পর। এ তিনটি তোমাদের পোশাকহীন হওয়ার সময়। এ সময়গুলো ছাড়া অন্য সময়ে (প্রবেশ করলে) তোমাদের উপর আর তাদের উপর কোন দোষ নেই। তোমাদের এককে অন্যের কাছে ঘুরাফিরা করতেই হয়। এভাবে আল্লাহ তোমাদের জন্য নির্দেশ খুবই স্পষ্টভাবে বর্ণনা করেন, আল্লাহ সর্বজ্ঞ, বড়ই হিকমতওয়ালা। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2850,24,59,'তোমাদের শিশুরা যখন বয়োঃপ্রাপ্ত হবে তখন তারা যেন তোমাদের নিকট আসতে অনুমতি নেয়, যেমন তাদের বয়োজ্যেষ্ঠরা অনুমতি নেয়। এভাবে আল্লাহ তাঁর নির্দেশ খুবই স্পষ্টভাবে বর্ণনা করেন, কারণ আল্লাহ সর্বজ্ঞ, বড়ই হিকমতওয়ালা। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2851,24,60,'বয়স্কা নারীরা যারা বিয়ের আশা রাখে না, তাদের প্রতি কোন দোষ বর্তাবে না যদি তারা তাদের সৌন্দর্য প্রদর্শন না ক’রে তাদের (উপরি) পোশাক খুলে রাখে, তবে এথেকে যদি তারা বিরত থাকে তবে সেটাই তাদের জন্য উত্তম। আল্লাহ সব কিছু শোনেন, সব কিছু সম্পর্কে পূর্ণ অবগত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2852,24,61,'অন্ধের জন্য দোষ নেই, খোঁড়ার জন্য দোষ নেই, পীড়িতের জন্য দোষ নেই আর তোমাদের জন্যও দোষ নেই খাদ্য গ্রহণ করতে তোমাদের গৃহে, কিংবা তোমাদের পিতৃগণের গৃহে, কিংবা তোমাদের মাতৃগণের গৃহে, কিংবা তোমাদের ভ্রাতাদের গৃহে, কিংবা তোমাদের বোনেদের গৃহে, কিংবা তোমাদের চাচাদের গৃহে, কিংবা তোমাদের ফুফুদের গৃহে, কিংবা তোমাদের মামাদের গৃহে, কিংবা তোমাদের খালাদের গৃহে, কিংবা ঐ সমস্ত গৃহে, যেগুলোর চাবি রয়েছে তোমাদের হাতে কিংবা তোমাদের বিশ্বস্ত বন্ধুদের গৃহে। তোমরা একত্রে আহার কর কিংবা আলাদা আলাদা তাতে তোমাদের উপর কোন দোষ নেই। যখন তোমরা গৃহে, প্রবেশ করবে তখন তোমরা স্বজনদেরকে সালাম জানাবে যা আল্লাহর দৃষ্টিতে বরকতময় পবিত্র সম্ভাষণ। এভাবে আল্লাহ তোমাদের জন্য নির্দেশসমূহ বিশদভাবে বর্ণনা করেন যাতে তোমরা বুঝতে পার। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2853,24,62,'ঈমানদার তো তারাই যারা ঈমান আনে আল্লাহর উপর ও তাঁর রসূলের উপর আর তারা যখন রসূলের সঙ্গে সমষ্টিগত কাজে মিলিত হয়, তখন তার অনুমতি না নিয়ে চলে যায় না। যারা তোমার কাছে অনুমতি প্রার্থনা করে তারাই আল্লাহ ও তাঁর রসূলের প্রতি বিশ্বাসী। কাজেই তাদের কেউ তাদের কোন কাজে যাওয়ার জন্য তোমার কাছে অনুমতি চাইলে তুমি তাদের যাকে ইচ্ছে অনুমতি দিবে আর তাদের জন্য আল্লাহর নিকট ক্ষমা প্রার্থনা করবে। আল্লাহ বড়ই ক্ষমাশীল, বড়ই দয়ালু। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2854,24,63,'রসূলের ডাককে তোমরা তোমাদের একের প্রতি অন্যের ডাকের মত গণ্য করো না। আল্লাহ তাদেরকে জানেন যারা তোমাদের মধ্যে চুপিসারে সরে পড়ে। কাজেই যারা তার আদেশের বিরুদ্ধাচরণ করে তারা সতর্ক হোক যে, তাদের উপর পরীক্ষা নেমে আসবে কিংবা তাদের উপর নেমে আসবে ভয়াবহ শাস্তি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2855,24,64,'নিশ্চিত জেনে রেখ, আসমান ও যমীনে যা কিছু আছে সবই আল্লাহর। তোমরা যে অবস্থায় আছ তা তিনি জানেন। অতঃপর যেদিন তারা তাঁর কাছে ফিরে যাবে সেদিন তিনি তাদেরকে জানিয়ে দেবেন তারা যা করত, আল্লাহ সকল বিষয়ে সবচেয়ে বেশি অবগত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2856,25,1,'মহা কল্যাণময় তিনি যিনি তাঁর বান্দাহর উপর সত্য-মিথ্যার পার্থক্যকারী (কিতাব) নাযিল করেছেন যাতে সে বিশ্বজগতের জন্য সতর্ককারী হতে পারে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2857,25,2,'যিনি যমীন ও আসমানের রাজত্বের মালিক, তিনি কোন সন্তান গ্রহণ করেননি, রাজত্বে তাঁর কোন অংশীদার নেই, তিনি সমস্ত কিছু সৃষ্টি করেছেন, আর সেগুলোকে যথাযথ করেছেন পরিমিত অনুপাতে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2858,25,3,'আর তারা তাঁকে বাদ দিয়ে ইলাহরূপে গ্রহণ করেছে অন্য কিছুকে যারা কিছুই সৃষ্টি করে না, বরং তারা নিজেরাই সৃষ্টি হয়েছে। তারা ক্ষমতা রাখে না নিজেদের ক্ষতি বা উপকার করার আর ক্ষমতা রাখে না মৃত্যু, জীবন ও পুনরুত্থানের উপর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2859,25,4,'কাফিররা বলে- ‘এটা মিথ্যে ছাড়া আর কিছুই নয়, সে তা (অর্থাৎ কুরআন) উদ্ভাবণ করেছে এবং ভিন্ন জাতির লোক এ ব্যাপারে তাকে সাহায্য করেছে।’ আসলে তারা অন্যায় ও মিথ্যের আশ্রয় নিয়েছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2860,25,5,'তারা বলে- ‘এগুলো পূর্ব যুগের কাহিনী যা সে [অর্থাৎ মুহাম্মাদ (সা.)] লিখিয়ে নিয়েছে আর এগুলোই তার কাছে সকাল-সন্ধ্যা শোনানো হয়।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2861,25,6,'বল : ‘তা তিনিই নাযিল করেছেন যিনি আসমান-যমীনের যাবতীয় গোপন বিষয় অবগত আছেন। তিনি বড়ই ক্ষমাশীল, অতি দয়ালু। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2862,25,7,'তারা বলে- ‘এ কেমন রসূল যে খাবার খায়, আর হাট-বাজারে চলাফেরা করে? তার কাছে ফেরেশতা অবতীর্ণ হয় না কেন যে তার সঙ্গে থাকত সতর্ককারী হয়ে? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2863,25,8,'কিংবা তাকে ধন-ভান্ডার দেয়া হয় না কেন, অথবা তার জন্য একটা বাগান হয় না কেন যাত্থেকে সে আহার করত?’ যালিমরা বলে- ‘তোমরা তো এক যাদুগ্রস্ত লোকেরই অনুসরণ করছ।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2864,25,9,'দেখ, তারা তোমার ব্যাপারে কেমন সব উপমা পেশ করছে! যার ফলে তারা পথভ্রষ্ট হয়ে গেছে, অতএব তারা কোন ক্রমেই পথ পাবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2865,25,10,'মহা কল্যাণময় তিনি যিনি ইচ্ছে করলে তোমাকে ওগুলোর চেয়েও উৎকৃষ্ট জিনিস দিতে পারেন- বাগ-বাগিচা, যার নিচ দিয়ে বয়ে চলেছে নির্ঝরিণী, দিতে পারেন তিনি তোমাকে প্রাসাদরাজি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2866,25,11,'আসলে তারা কিয়ামাতকে অস্বীকার করে, আর যারা কিয়ামাতকে অস্বীকার করে, তাদের জন্য আমি প্রস্তুত করে রেখেছি জ্বলন্ত আগুন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2867,25,12,'আগুন যখন তাদেরকে বহু দূরবর্তী স্থান থেকে দেখবে, তখন তারা শুনতে পারে তার ক্রুদ্ধ গর্জন ও হুঙ্কার। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2868,25,13,'যখন তাদেরকে এক সঙ্গে বেঁধে জাহান্নামের কোন সংকীর্ণ স্থানে নিক্ষেপ করা হবে, তখন সেখানে তারা মৃত্যুকে ডাকবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2869,25,14,'(বলা হবে) ‘তোমরা আজ এক মৃত্যুকে ডেক না, অনেক মৃত্যুকে ডাক।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2870,25,15,'তাদেরকে জিজ্ঞেস কর- এটাই উত্তম না চিরস্থায়ী জান্নাত, মুত্তাকীদেরকে যার ওয়াদা দেয়া হয়েছে? তাদের জন্য এটা হবে প্রতিদান ও শেষ আবাসস্থল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2871,25,16,'সেখানে তাদের জন্য তা-ই থাকবে যা তারা ইচ্ছে করবে। সেখানে তারা চিরকাল থাকবে। এটা একটা ওয়াদা যা পূরণ করা তোমার প্রতিপালকের দায়িত্ব। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2872,25,17,'যেদিন তিনি তাদেরকে আর আল্লাহকে বাদ দিয়ে তারা যেগুলোর ‘ইবাদাত করত সেগুলোকে একত্র করবেন, সেদিন তিনি জিজ্ঞেস করবেন- ‘তোমরাই কি আমার এ সব বান্দাহদেরকে পথভ্রষ্ট করেছিলে? না তারা নিজেরাই পথ থেকে বিচ্যুত হয়েছিল?’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2873,25,18,'তারা বলবে : ‘পবিত্র ও মহান তুমি। আমাদের জন্য শোভনীয় ছিল না যে তোমাকে বাদ দিয়ে অন্যান্য ইলাহ গ্রহণ করব, বরং তুমি ওদেরকে আর ওদের পিতৃপুরুষদেরকে দিয়েছিলে পার্থিব ভোগ সম্ভার, পরিণামে তারা ভুলে গিয়েছিল (তোমার প্রেরিত) বাণী, যার ফলে তারা পরিণত হল এক ধ্বংসপ্রাপ্ত জাতিতে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2874,25,19,'(আল্লাহ মুশরিকদেরকে বলবেন) ‘তোমরা যা বলতে সে ব্যাপারে তারা তোমাদেরকে মিথ্যে প্রমাণিত করেছে। কাজেই তোমরা না পারবে (তোমাদের শাস্তি) প্রতিরোধ করতে আর না পাবে সাহায্য। তোমাদের মধ্যে যে অন্যায়কারী আমি তাকে গুরুতর শাস্তি আস্বাদন করাব। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2875,25,20,'আমি তোমার পূর্বে যে সব রসূল পাঠিয়েছিলাম তারা সবাই খাদ্য গ্রহণ করত, আর হাট-বাজারে চলাফেরা করত। আমি তোমাদের এককে অপরের জন্য করেছি পরীক্ষাস্বরূপ (কাউকে করেছি ধনী, কাউকে গরীব, কাউকে সবল, কাউকে দুর্বল, কাউকে রুগ্ন, কাউকে সুস্থ), দেখি, তোমরা (নিজ নিজ অবস্থার উপর) ধৈর্য ধারণ কর কিনা। তোমার প্রতিপালক সব কিছু দেখেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2876,25,21,'যারা আমার সাক্ষাৎ আশা করে না তারা বলে, আমাদের কাছে ফেরেশতা নাযিল করা হয় না কেন? অথবা আমরা আমাদের প্রতিপালককে দেখতে পাই না কেন? তারা নিজেদের অন্তরে অহংকার পোষণ করে আর তারা মেতে উঠেছে গুরুতর অবাধ্যতায়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2877,25,22,'যেদিন তারা ফেরেশতাদেরকে দেখতে পাবে, অপরাধীদের জন্য সেদিন কোন সুখবর থাকবে না, আর (ফেরেশতাগণ বলবে তোমাদের সুখ-শান্তির পথে আছে) দুর্লঙ্ঘ বাধা। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2878,25,23,'তারা (দুনিয়ায়) যে ‘আমাল করেছিল আমি সেদিকে অগ্রসর হব, অতঃপর তাকে বানিয়ে দেব ছড়ানো ছিটানো ধূলিকণা (সদৃশ)। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2879,25,24,'সেদিন জান্নাতবাসীরা স্থায়ী বাসস্থান হিসেবে উত্তম আর বিশ্রামস্থল হিসেবে উৎকৃষ্ট অবস্থায় থাকবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2880,25,25,'সেদিন মেঘমালা সহ আকাশ বিদীর্ণ হবে আর ফেরেশতাদেরকে ধীরে ধীরে নীচে নামিয়ে দেয়া হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2881,25,26,'সেদিন সত্যিকারের কর্তৃত্ব হবে দয়াময় (আল্লাহ)’র এবং কাফিরদের জন্য দিনটি হবে কঠিন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2882,25,27,'অপরাধী সেদিন স্বীয় হস্তদ্বয় দংশন করতে করতে বলবে, ‘হায় আফসোস! আমি যদি রসূলের সাথে পথ অবলম্বন করতাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2883,25,28,'হায় আমার দুর্ভাগ্য! আমি যদি অমুককে বন্ধুরূপে গ্রহণ না করতাম! ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2884,25,29,'সে তো আমাকে উপদেশ বাণী থেকে বিভ্রান্ত করেছিল আমার কাছে তা আসার পর, শয়ত্বান মানুষের প্রতি বিশ্বাসঘাতক। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2885,25,30,'রসূল বলবে- ‘হে আমার প্রতিপালক! আমার জাতির লোকেরা এ কুরআনকে পরিত্যক্ত গণ্য করেছিল।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2886,25,31,'এভাবেই আমি প্রত্যেক নবীর জন্য অপরাধীদের মধ্য হতে শত্রু বানিয়ে দিয়েছি, পথ প্রদর্শক ও সাহায্যকারী হিসেবে তোমার প্রতিপালকই যথেষ্ট। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2887,25,32,'কাফিররা বলে- তার কাছে পুরো কুরআন এক সাথে অবতীর্ণ করা হল না কেন? আমি এভাবেই অবতীর্ণ করেছি। তোমার হৃদয়কে তা দ্বারা সুদৃঢ় করার জন্য আমি তোমার কাছে তা ধীরে ধীরে পরিকল্পিত স্তরে ক্রমশঃ আবৃত্তি করিয়েছি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2888,25,33,'তোমার কাছে তারা এমন কোন সমস্যাই নিয়ে আসে না যার সঠিক সমাধান ও সুন্দর ব্যাখ্যা আমি তোমাকে দান করিনি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2889,25,34,'যাদেরকে মুখের ভরে জাহান্নামের পানে একত্রিত করা হবে, তারা মর্যাদার ক্ষেত্রে সবচেয়ে নিকৃষ্ট আর পথের দিক থেকে সবচেয়ে গুমরাহ্। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2890,25,35,'আমি মূসাকে কিতাব দিয়েছিলাম আর তার সাথে তার ভাই হারূনকে আমি সাহায্যকারী বানিয়েছিলাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2891,25,36,'অতঃপর তাদেরকে বলেছিলাম, ‘তোমরা সেই জাতির নিকট যাও যারা আমার নিদর্শনকে প্রত্যাখ্যান করেছে।’ অতঃপর আমি তাদেরকে পূর্ণ বিধ্বস্তিতে বিধ্বস্ত করে দিয়েছিলাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2892,25,37,'আর নূহের জাতি যখন রসূলদেরকে মিথ্যারোপ করল, আমি তাদেরকে ডুবিয়ে দিলাম, আর মানুষের জন্য তাদেরকে নিদর্শন বানিয়ে দিলাম। আমি যালিমদের জন্য ভয়াবহ শাস্তি প্রস্তুত করে রেখেছি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2893,25,38,'সে রকমই আমি ধ্বংস করেছি ‘আদ, সামূদ, কূপবাসী আর তাদের মধ্যবর্তী বহু বহু বংশধরকে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2894,25,39,'আমি তাদের প্রত্যেকের জন্য দৃষ্টান্ত বর্ণনা করেছিলাম (যাতে তারা সঠিক পথের সন্ধান পেতে পারে) আর তাদের প্রত্যেককেই সম্পূর্ণরূপে ধ্বংস করে দিয়েছিলাম (তাদের পাপের কারণে)। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2895,25,40,'তারা (অর্থাৎ কাফিররা) তো সে জনপদ দিয়েই অতিক্রম করে যার উপর বর্ষিত হয়েছিল অকল্যাণের বৃষ্টি, তারা কি তা দেখে না? আসলে তারা পুনরুত্থানের কথা চিন্তা করে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2896,25,41,'তারা যখন তোমাকে দেখে, তারা তোমাকে ঠাট্টা-বিদ্রূপের পাত্র ছাড়া অন্য কিছু গণ্য করে না, আর বলে : এটা কি সেই লোক আল্লাহ যাকে রসূল করে পাঠিয়েছেন? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2897,25,42,'সে তো আমাদেরকে আমাদের ইলাহদের থেকে অবশ্যই সরিয়ে দিত যদি আমরা তাদের প্রতি দৃঢ়চিত্ত না থাকতাম। যখন তারা শাস্তি দেখবে তখন জানবে যে পথের ক্ষেত্রে কারা অধিক ভ্রষ্ট ছিল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2898,25,43,'তুমি কি তাকে দেখ না যে তার খেয়াল খুশিকে ইলাহরুপে গ্রহণ করেছে? এর পরেও কি তুমি তার কাজের জিম্মাদার হতে চাও? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2899,25,44,'তুমি কি এটা মনে কর যে, তাদের অধিকাংশ লোক শুনে বা বুঝে? তারা পশু বৈ তো নয়, বরং তারা সঠিক পথ থেকে আরো বেশি ভ্রষ্ট। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2900,25,45,'তুমি কি তোমার প্রতিপালককে দেখ না কীভাবে তিনি ছায়াকে দীর্ঘ করেন (সূর্য উদয়ের সময়ে, অতঃপর তা ক্রমেই ছোট হতে হতে দুপুর বেলা ছায়া ক্ষুদ্র আকৃতি ধারণ করে, দুপুরের পর আবার ছায়া দীর্ঘ হতে থাকে), তিনি চাইলে ছায়াকে অবশ্যই স্থির রাখতে পারতেন। সূর্যকেই আমি করেছি তার (অর্থাৎ ছায়ার) নির্ণায়ক (সূর্যের অবস্থানের কারণেই ছায়া ছোট ও দীর্ঘ হয়)। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2901,25,46,'অতঃপর আমি তাকে নিজের দিকে গুটিয়ে নেই, ধীরে ধীরে ক্রমাগতভাবে (কারো মৃত্যু হলে, কিছু বিনাশ হলে, কিছু গুটানো হলে- সব কিছুই আল্লাহর দিকে চলে যায়)। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2902,25,47,'তিনিই তোমাদের জন্য রাতকে করেছেন আবরণ, নিদ্রাকে করেছেন আরামপ্রদ আর দিনকে করেছেন (নিদ্রারূপী সাময়িক মৃত্যুর পর) আবার জীবন্ত হয়ে উঠার সময়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2903,25,48,'তিনিই তার (বৃষ্টিরূপী) অনুগ্রহের পূর্বে সুসংবাদ হিসেবে বায়ু পাঠিয়ে দেন আর আমি আকাশ থেকে বিশুদ্ধ পানি বর্ষণ করি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2904,25,49,'যা দিয়ে আমি মৃত যমীনকে জীবিত করে তুলি এবং তৃষ্ণা নিবারণ করি আমার সৃষ্টির অন্তর্গত অনেক জীবজন্তুর ও মানুষের। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2905,25,50,'আমি পানিকে (সকলের প্রয়োজন মেটানোর জন্য) তাদের মাঝে বণ্টন করি যাতে তারা (আল্লাহর অনুগ্রহের কথা) স্মরণ করে, কিন্তু মানুষদের অধিকাংশই ঈমান গ্রহণ করতে অস্বীকার ক’রে কেবল কুফরিই করল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2906,25,51,'আমি ইচ্ছে করলে প্রত্যেক জনবসতিতে সতর্ককারী পাঠাতাম। (কিন্তু সারা বিশ্বের জন্য একজন নবী পাঠিয়ে সকলকে একই উম্মাত হওয়ার অনুগ্রহ লাভে ধন্য করেছি)। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2907,25,52,'কাজেই তুমি কাফিরদের আনুগত্য করো না: আর কুরআনের সাহায্যে তাদের বিরুদ্ধে সংগ্রাম কর- কঠোর সংগ্রাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2908,25,53,'তিনিই সমুদ্রকে দু’ ধারায় প্রবাহিত করেছেন- একটি সুপেয় সুস্বাদু আরেকটি লবণাক্ত কটু, উভয়ের মাঝে টেনে দিয়েছেন এক আবরণ- এক অনতিক্রম্য বিভক্তি-প্রাচীর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2909,25,54,'তিনিই পানি থেকে সৃষ্টি করেছেন মানুষ, অতঃপর মানুষকে করেছেন বংশ সম্পর্কীয় ও বিবাহ সম্পর্কীয়, তোমার প্রতিপালক সব কিছু করতে সক্ষম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2910,25,55,'তারা আল্লাহকে বাদ দিয়ে এমন কিছুর ‘ইবাদাত করে যা না পারে তাদের কোন উপকার করতে, আর না পারে কোন ক্ষতি করতে, আর কাফির হচ্ছে তার প্রতিপালকের বিরুদ্ধে সাহায্যকারী।  ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2911,25,56,'আমি তোমাকে পাঠিয়েছি কেবল সুসংবাদদাতা ও ভয় প্রদর্শনকারী হিসেবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2912,25,57,'বল- এজন্য আমি তোমাদের কাছে এছাড়া কোন প্রতিদান চাই না যে, যার ইচ্ছে সে তার প্রতিপালকের দিকে পথ অবলম্বন করুক। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2913,25,58,'আর তুমি নির্ভর কর সেই চিরঞ্জীবের উপর যিনি মরবেন না। আর তাঁর প্রশংসা ও পবিত্রতা ঘোষণা কর। তিনি তাঁর বান্দাহদের গুনাহর খবর রাখার ব্যাপারে যথেষ্ট। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2914,25,59,'তিনি আসমান, যমীন আর এ দু’য়ের ভিতরে যা আছে তা ছ’দিনে (ছ’টি সময় স্তরে) সৃষ্টি করেছেন, অতঃপর আরশে সমুন্নত হয়েছেন। তিনিই রাহমান, কাজেই তাঁর সম্পর্কে তাকে জিজ্ঞেস কর যে এ সম্পর্কিত জ্ঞান রাখে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2915,25,60,'তাদেরকে যখন বলা হয় ‘রহমান’-এর উদ্দেশে সাজদায় অবনত হও, তারা বলে- ‘রহমান আবার কী? আমাদেরকে তুমি যাকেই সেজদা করতে বলবে আমরা তাকেই সেজদা করব নাকি?’ এতে তাদের অবাধ্যতাই বেড়ে যায়। [সাজদাহ]');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2916,25,61,'কতই না কল্যাণময় তিনি যিনি আসমানে নক্ষত্ররাজির সমাবেশ ঘটিয়েছেন আর তাতে স্থাপন করেছেন প্রদীপ আর আলো বিকিরণকারী চন্দ্র। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2917,25,62,'আর তিনিই রাত আর দিনকে করেছেন পরস্পরের অনুগামী তাদের জন্য যারা উপদেশ গ্রহণ করতে চায়, অথবা কৃতজ্ঞতা প্রকাশ করতে চায়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2918,25,63,'আর রহমানের বান্দা তারাই যারা যমীনে নম্রভাবে চলাফেরা করে আর অজ্ঞ লোকেরা তাদেরকে সম্বোধন করলে তারা বলে- ‘শান্তি’, (আমরা বিতর্কে লিপ্ত হতে চায় না)। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2919,25,64,'আর তারা রাত কাটায় তাদের প্রতিপালকের উদ্দেশে সাজদায় অবনত ও দন্ডায়মান অবস্থায়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2920,25,65,'আর তারা বলে : ‘হে আমাদের প্রতিপালক! তুমি আমাদের থেকে জাহান্নামের শাস্তি দূর কর, তার শাস্তি তো ভয়াবহ বিপদ।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2921,25,66,'তা আবাসস্থল আর অবস্থান নেয়ার জায়গা হিসেবে কতই না নিকৃষ্ট! ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2922,25,67,'আর যখন তারা ব্যয় করে তখন অপব্যয় করে না, আর কৃপণতাও করে না; এ দু’য়ের মধ্যবর্তী পন্থা গ্রহণ করে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2923,25,68,'তারা আল্লাহর সাথে অন্য কোন ইলাহকে ডাকে না। আর যথার্থতা ব্যতীত কোন প্রাণ হত্যা করে না যা আল্লাহ নিষিদ্ধ করেছেন আর তারা ব্যভিচার করে না। আর যে এগুলো করে সে শাস্তির সাক্ষাৎ লাভ করবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2924,25,69,'ক্বিয়ামতের দিন তার শাস্তি দ্বিগুণ করা হবে আর সে সেখানে লাঞ্ছিত হয়ে চিরবাস করবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2925,25,70,'তবে তারা নয় যারা তাওবাহ করবে, ঈমান আনবে, আর সৎ কাজ করবে। আল্লাহ এদের পাপগুলোকে পুণ্যে পরিবর্তিত করে দেবেন; আল্লাহ বড়ই ক্ষমাশীল, বড়ই দয়ালু। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2926,25,71,'আর যে ব্যক্তি তাওবাহ করে আর সৎকাজ করে, সে আল্লাহর দিকে প্রত্যাবর্তন করে- পূর্ণ প্রত্যাবর্তন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2927,25,72,'আর যারা মিথ্যে সাক্ষ্য দেয় না, আর বেহুদা কর্মকান্ডের পাশ দিয়ে অতিক্রম করলে সসম্মানে পাশ কাটিয়ে চলে যায়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2928,25,73,'আর তাদেরকে তাদের প্রতিপালকের আয়াত স্মরণ করিয়ে দেয়া হলে যারা তার প্রতি বধির ও অন্ধের ন্যায় আচরণ করে না (শুনেও শুনে না, দেখেও দেখে না- এমন করে না)। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2929,25,74,'আর যারা প্রার্থনা করে : হে আমাদের প্রতিপালক! আমাদেরকে এমন স্ত্রী ও সন্তানাদি দান কর যারা আমাদের চোখ জুড়িয়ে দেয় আর আমাদেরকে মুত্তাকীদের নেতা বানিয়ে দাও। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2930,25,75,'এদেরকেই তাদের ধৈর্যধারণের কারণে জান্নাতের সুউচ্চ স্থান দান ক’রে পুরস্কৃত করা হবে। সেখানে তাদেরকে অভ্যর্থনা জানানো হবে সংবর্ধনা ও সালাম জানিয়ে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2931,25,76,'সেখানে তারা চিরকাল থাকবে, আবাসস্থল ও অবস্থানস্থল হিসেবে তা কতই না উৎকৃষ্ট! ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2932,25,77,'(কাফিরদেরকে) বল : ‘তোমাদের ব্যাপারে আমার প্রতিপালকের কী প্রয়োজন পড়েছে তোমরা যদি তাঁকে না ডাকো? তোমরা তো তাঁকে অস্বীকার করেছ, কাজেই শীঘ্রই তোমাদের উপর এসে পড়বে অপ্রতিরোধ্য (শাস্তি)। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2933,26,1,'ত্ব-সীন-মীম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2934,26,2,'এগুলো সুস্পষ্ট (বা সুস্পষ্টকারী) কিতাবের আয়াত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2935,26,3,'তুমি হয়ত এ দুঃখে তোমার প্রাণনাশ করবে যে, তারা মু’মিন হচ্ছে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2936,26,4,'আমি ইচ্ছে করলে তাদের কাছে আসমান থেকে এমন নিদর্শন পাঠাতাম যে তার সামনে তাদের মাথা নত হয়ে যেত (অর্থাৎ তারা ঈমান আনতে বাধ্য হত)। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2937,26,5,'তাদের কাছে যখনই দয়াময় আল্লাহর পক্ষ হতে নতুন কোন নসীহত আসে তখনই তারা তাত্থেকে মুখ ফিরিয়ে নেয়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2938,26,6,'তারা (আল্লাহর বাণীকে) অস্বীকারই করেছে, শীঘ্রই তাদের কাছে তার সত্য উদঘাটিত হবে যা নিয়ে তারা ঠাট্টা-বিদ্রূপ করত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2939,26,7,'তারা কি যমীনের দিকে চেয়ে দেখে না, আমি তাতে সব ধরনের উৎকৃষ্ট উদ্ভিদ পয়দা করেছি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2940,26,8,'অবশ্যই এতে নিদর্শন আছে (আল্লাহ সম্পর্কে চিন্তা ক’রে ঈমান আনার জন্য), কিন্তু তাদের অধিকাংশই ঈমান আনে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2941,26,9,'নিশ্চয় তোমার প্রতিপালক মহা পরাক্রমশালী, অতি দয়ালু। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2942,26,10,'স্মরণ কর, যখন তোমার প্রতিপালক মূসাকে ডাক দিয়ে বললেন, ‘তুমি যালিম সম্প্রদায়ের কাছে যাও, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2943,26,11,'ফেরাউনের সম্প্রদায়ের কাছে। তারা কি ভয় করে না? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2944,26,12,'সে বলেছিল, ‘হে আমার প্রতিপালক! আমার ভয় হচ্ছে তারা আমাকে মিথ্যে মনে ক’রে প্রত্যাখ্যান করবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2945,26,13,'আর আমার অন্তর সংকুচিত হয়ে যাচ্ছে, আমার জিহ্বা সাবলীলভাবে কথা বলতে পারে না। কাজেই আপনি হারূনের প্রতি রিসালাত দিন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2946,26,14,'তদুপরি আমার বিরুদ্ধে অপরাধের অভিযোগও তাদের আছে, কাজেই আমার ভয় হচ্ছে তারা আমাকে হত্যা করবে।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2947,26,15,'আল্লাহ বললেন, ‘কক্ষনো না, তোমরা দু’জনে আমার (দেয়া) নিদর্শন নিয়ে যাও, আমি তোমাদের সঙ্গে থেকে সব কিছু শুনতে থাকব। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2948,26,16,'কাজেই তোমরা দু’জনে ফেরাউনের কাছে যাও আর গিয়ে বল যে, আমরা বিশ্বজগতের প্রতিপালকের প্রেরিত রসূল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2949,26,17,'বানী ইসরাঈলকে আমাদের সঙ্গে পাঠিয়ে দাও।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2950,26,18,'ফেরাউন বলল, ‘আমরা কি তোমাকে শিশুকালে আমাদের মধ্যে লালন পালন করিনি? আর তুমি কি তোমার জীবনের কতকগুলো বছর আমাদের মাঝে কাটাওনি? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2951,26,19,'তুমি তোমার কর্ম যা করার করেছ (আমাদের একজন লোককে হত্যা ক’রে), তুমি বড় অকৃতজ্ঞ।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2952,26,20,'মূসা বলল : ‘আমি তো তা করেছিলাম সে সময় যখন আমি ছিলাম (সঠিক পথ সম্পর্কে) অজ্ঞ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2953,26,21,'অতঃপর তোমাদের ভয়ে ভীত হয়ে আমি তোমাদের থেকে পালিয়ে গেলাম। অতঃপর আমার রবব আমাকে প্রজ্ঞা দান করলেন, আর আমাকে করলেন রসূলদের একজন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2954,26,22,'তুমি আমার প্রতি তোমার যে অনুগ্রহের খোঁটা দিচ্ছ তাতো এই যে, তুমি বানী ইসরাঈলকে দাসে পরিণত করেছ।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2955,26,23,'ফেরাউন বলল : ‘বিশ্বজগতের প্রতিপালক আবার কী?’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2956,26,24,'মূসা বলল : ‘(যিনি) আসমান ও যমীন ও এ দু’য়ের মাঝে যা কিছু আছে সব কিছুর প্রতিপালক- যদি তোমরা নিঃসন্দেহে বিশ্বাসী হও। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2957,26,25,'ফেরাউন তার চারপাশের লোকেদেরকে বলল- ‘তোমরা শুনছ তো?’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2958,26,26,'মূসা বলল : ‘(তিনি) তোমাদের প্রতিপালক ও শুরু থেকে পূর্ববর্তী তোমাদের বাপ-দাদাদেরও প্রতিপালক।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2959,26,27,'(ফিরআউন) বলল: ‘তোমাদের রসূল যে তোমাদের নিকট প্রেরিত হয়েছে সে নিশ্চিতই পাগল।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2960,26,28,'(মূসা) বললঃ ‘(তিনিই) পূর্ব ও পশ্চিমের প্রতিপালক, আর এ উভয় দিকের মাঝে যা আছে তারও- যদি তোমাদের বুদ্ধিসুদ্ধি থাকে।’');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2961,26,29,'(ফেরাউন) বলল : ‘যদি তুমি আমাকে বাদ দিয়ে (অন্য কিছুকে) ইলাহ হিসেবে গ্রহণ কর, তাহলে আমি তোমাকে অবশ্য অবশ্যই কারারুদ্ধ করব।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2962,26,30,'(মূসা) বলল : ‘আমি যদি তোমার কাছে কোন স্পষ্ট জিনিস নিয়ে আমি তবুও? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2963,26,31,'(ফেরাউন) বলল : ‘তাহলে তা আনো, যদি তুমি সত্যবাদী হও।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2964,26,32,'তখন মূসা তার লাঠি ছুঁড়ে দিল আর সহসাই তা স্পষ্ট এক অজগর হয়ে গেল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2965,26,33,'আর মূসা (বগলের নীচ দিয়ে) নিজের হাত টেনে বের করল, আর তা দর্শকদের সামনে ঝকমক করতে লাগল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2966,26,34,'ফেরাউন তার চারপাশের প্রধানদের বলল : ‘সে অবশ্যই এক দক্ষ যাদুকর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2967,26,35,'যাদুর বলে সে তোমাদেরকে তোমাদের দেশ থেকে বের করে দিতে চায়। এখন বল তোমাদের নির্দেশ কী?’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2968,26,36,'তারা বলল : ‘তাকে ও তার ভাইকে (কিছু সময়) অপেক্ষায় ফেলে রাখুন আর জড়ো করার জন্য ঘোষকদেরকে নগরে নগরে পাঠিয়ে দিন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2969,26,37,'তারা আপনার কাছে প্রত্যেকটি অভিজ্ঞ যাদুকরকে নিয়ে আসবে।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2970,26,38,'কাজেই যাদুকরদেরকে একত্রিত করা হল একটি নির্দিষ্ট দিন ক্ষণের জন্য যা ছিল সুবিদিত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2971,26,39,'আর জনগণকে বলা হল- ‘তোমরা কি সম্মিলিত হবে? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2972,26,40,'যাতে আমরা যাদুকরদের (এবং তাদের প্রধান পৃষ্ঠপোষক ফেরাউনের) দীন অনুসরণ করতে পারি যদি তারা বিজয়ী হয়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2973,26,41,'যাদুকররা যখন আসলো, তারা ফেরাউনকে বলল : ‘আমরা জয়ী হলে আমাদেরকে পুরস্কার দেয়া হবে তো?’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2974,26,42,'ফেরাউন বলল- ‘হাঁ, তখন তোমরা অবশ্যই আমার নৈকট্যলাভকারীদের অন্তর্ভুক্ত হবে।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2975,26,43,'মূসা তাদেরকে বলল- ‘নিক্ষেপ কর যা তোমরা নিক্ষেপ করবে।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2976,26,44,'তখন তারা তাদের রশিগুলো ও লাঠিগুলো নিক্ষেপ করল আর তারা বলল- ‘ফেরাউনের ইযযতের শপথ! আমরা অবশ্যই জয়ী হব।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2977,26,45,'অতঃপর মূসা তার লাঠি নিক্ষেপ করল। হঠাৎ তা তাদের অলীক কীর্তিগুলোকে গিলতে লাগল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2978,26,46,'তখন যাদুকররা সিজদায় লুটিয়ে পড়ল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2979,26,47,'তারা বলল- ‘আমরা বিশ্বাস স্থাপন করলাম রাব্বুল ‘আলামীনের প্রতি, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2980,26,48,'যিনি মূসা ও হারুনের প্রতিপালক।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2981,26,49,'ফেরাউন বলল- ‘আমি তোমাদেরকে অনুমতি দেয়ার আগেই তোমরা তাতে বিশ্বাস আনলে? নিশ্চয়ই সে তোমাদের ওস্তাদ যে তোমাদেরকে যাদু শিখিয়েছে। শীঘ্রই তোমরা (এর পরিণাম) জানতে পারবে। আমি অবশ্য অবশ্যই তোমাদের হাত-পাগুলোকে বিপরীত দিক থেকে কেটে ফেলব আর তোমাদের সব্বাইকে অবশ্য অবশ্যই ‘শূলে চড়াব। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2982,26,50,'তারা বলল- কোনই ক্ষতি নেই, আমরা আমাদের প্রতিপালকের পানে প্রত্যাবর্তন করব। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2983,26,51,'আমাদের একমাত্র আশা এই যে, আমাদের প্রতিপালক আমাদের ত্রুটি-বিচ্যুতি ক্ষমা করবেন, কারণ আমরা বিশ্বাস স্থাপনকারীদের মধ্যে প্রথম।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2984,26,52,'আমি মূসাকে ওহীযোগে নির্দেশ দিলাম আমার বান্দাদেরকে নিয়ে রাত্রিকালে বের হয়ে যাও, নিশ্চয়ই তোমাদের পশ্চাদ্ধাবন করা হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2985,26,53,'অতঃপর ফেরাউন শহরে নগরে সংগ্রাহক পাঠিয়ে দিল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2986,26,54,'(এই ব’লে যে) এরা (বানী ইসরাঈলরা) ক্ষুদ্র একটি দল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2987,26,55,'তারা আমাদেরকে অবশ্যই ক্রোধান্বিত করেছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2988,26,56,'আর আমরা অবশ্যই সদা সতর্ক একটি দল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2989,26,57,'এভাবে আমি ফেরাউন গোষ্ঠীকে (তাদের নিজেদেরই) উদ্যানরাজি আর ঝর্ণাসমূহ থেকে বহিস্কার করলাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2990,26,58,'আর ধনভন্ডারসমূহ ও সম্মানজনক অবস্থান থেকে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2991,26,59,'এভাবেই ঘটেছিল, আমি বানী ইসরাঈলকে এসব কিছুর উত্তরাধিকারী করে দিয়েছিলাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2992,26,60,'কাজেই তারা (অর্থাৎ ফেরাউন গোষ্ঠী) সূর্যোদয়কালে তাদের পশ্চাদ্ধাবন করল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2993,26,61,'যখন দু‘দল পরস্পরকে দেখল তখন মূসার সঙ্গীরা বলল- ‘আমরা তো ধরা পড়েই গেলাম।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2994,26,62,'মূসা বলল- ‘কক্ষনো না, আমার রব আমার সঙ্গে আছেন, শীঘ্রই তিনি আমাকে পথ নির্দেশ করবেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2995,26,63,'তখন আমি মূসার প্রতি ওয়াহী করলাম- ‘তোমার লাঠি দিয়ে সমুদ্রে আঘাত কর।’ ফলে তা বিভক্ত হয়ে প্রত্যেক ভাগ সুবিশাল পর্বতের ন্যায় হয়ে গেল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2996,26,64,'আমি সেখানে অপর দলটিকে পৌছে দিলাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2997,26,65,'আর মূসা ও তার সঙ্গী সবাইকে উদ্ধার করলাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2998,26,66,'অতঃপর অপর দলটিকে ডুবিয়ে মারলাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (2999,26,67,'এতে অবশ্যই নিদর্শন আছে, কিন্তু তাদের অধিকাংশই বিশ্বাস করে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3000,26,68,'তোমার পালনকর্তা অবশ্যই পরাক্রমশালী, বড়ই দয়ালু। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3001,26,69,'ওদেরকে ইবরাহীমের বৃত্তান্ত শুনিয়ে দাও। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3002,26,70,'যখন সে তার পিতা ও তার সম্প্রদায়কে বলেছিল- ‘তোমরা কিসের ইবাদত কর?’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3003,26,71,'তারা বলেছিল- ‘আমরা মূর্তির পূজা করি, আর আমরা সদা সর্বদা তাদেরকে আঁকড়ে থাকি।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3004,26,72,'ইবরাহীম বলল- ‘তোমরা যখন (তাদেরকে) ডাক তখন কি তারা তোমাদের কথা শোনে? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3005,26,73,'কিংবা তোমাদের উপকার করে অথবা অপকার?’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3006,26,74,'তারা বলল- ‘না তবে আমরা আমাদের পিতৃদেরকে এরকম করতে দেখেছি।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3007,26,75,'সে বলল-তোমরা কি ভেবে দেখেছ তোমরা কিসের পূজা করে যাচ্ছ? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3008,26,76,'তোমরা আর তোমাদের আগের পিতৃপুরুষরা? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3009,26,77,'তারা সবাই আমার শত্রু, বিশ্বজগতের পালনকর্তা ছাড়া। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3010,26,78,'তিনি আমাকে সৃষ্টি করেছেন অতঃপর তিনিই আমাকে পথ দেখান। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3011,26,79,'আর তিনিই আমাকে খাওয়ান ও পান করান। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3012,26,80,'আর আমি যখন পীড়িত হই তখন তিনিই আমাকে আরোগ্য করেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3013,26,81,'যিনি আমার মৃত্যু ঘটাবেন, পুনরায় আমাকে জীবিত করবেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3014,26,82,'আর যিনি, আমি আশা করি- কিয়ামাতের দিন আমার দোষ-ত্রুটি ক্ষমা করে দেবেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3015,26,83,'হে আমার পালনকর্তা! আমাকে প্রজ্ঞা দান কর এবং আমাকে সৎকর্মশীলদের অর্ন্তভুক্ত কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3016,26,84,'এবং আমাকে পরবর্তীদের মধ্যে সত্যভাষী কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3017,26,85,'এবং আমাকে নি‘য়ামাতপূর্ণ জান্নাতের উত্তরাধিকারীদের অন্তর্ভুক্ত কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3018,26,86,'আর তুমি আমার পিতাকে ক্ষমা কর, তিনি তো গুমরাহদের অর্ন্তভুক্ত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3019,26,87,'এবং পুনরুত্থান দিবসে আমাকে অপমানিত করো না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3020,26,88,'যেদিন ধন-সম্পদ ও সন্তান-সন্তুতি কোন কাজে আসবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3021,26,89,'কেবল (সাফল্য লাভ করবে) সে ব্যক্তি যে বিশুদ্ধ অন্তর নিয়ে আল্লাহর নিকট আসবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3022,26,90,'আর জান্নাতকে মুত্তাকীদের নিকটবর্তী করা হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3023,26,91,'এবং পথভ্রষ্টদের সম্মুখে জাহান্নামকে উন্মোচিত করা হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3024,26,92,'আর তাদেরকে বলা হবে, তোমরা যার ‘ইবাদাত করতে তারা কোথায় ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3025,26,93,'আল্লাহকে বাদ দিয়ে? তারা কি তোমাদের সাহায্য করতে পারে কিংবা তাদের নিজেদেরকে সাহায্য করতে পারে? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3026,26,94,'অতঃপর তাদেরকে ও পথভ্রষ্টদেরকে জাহান্নামে মুখের ভরে নিক্ষেপ করা হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3027,26,95,'আর ইবলীসের দলবল সবাইকে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3028,26,96,'সেখানে তারা বিতর্কে লিপ্ত হয়ে বলবে, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3029,26,97,'‘আল্লাহর কসম! আমরা অবশ্য স্পষ্ট গুমরাহীতে ছিলাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3030,26,98,'যখন আমরা তোমাদেরকে সর্বজগতের পালনকর্তার সমকক্ষ স্থির করতাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3031,26,99,'অপরাধীরাই আমাদেরকে গোমরাহ্ করেছিল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3032,26,100,'কাজেই আমাদের কোন সুপারিশকারী নেই। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3033,26,101,'একজন অন্তরঙ্গ বন্ধুও নেই। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3034,26,102,'আমাদের যদি একটিবার পৃথিবীতে ফিরে যাওয়ার সুযোগ হত, তাহলে আমরা মু’মিনদের অর্ন্তভুক্ত হয়ে যেতাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3035,26,103,'এতে অবশ্যই নিদর্শন আছে, কিন্তু তাদের অধিকাংশই ঈমান আনে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3036,26,104,'তোমার প্রতিপালক, তিনি অবশ্যই মহা পরাক্রমশালী, পরম দয়ালু। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3037,26,105,'নূহের কওম রসুলগণকে মিথ্যে ব’লে প্রত্যাখান করেছিল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3038,26,106,'যখন তাদের ভ্রাতা নূহ তাদেরকে বলেছিল- ‘তোমরা কি ভয় করবে না (আল্লাহকে)? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3039,26,107,'আমি তোমাদের জন্য (প্রেরিত) বিশ্বস্ত রাসুল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3040,26,108,'কাজেই তোমরা আল্লাহকে ভয় কর ও আমার অনুসরণ কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3041,26,109,'আমি তার জন্য তোমাদের কাছে কোন প্রতিদান চাই না। আমার প্রতিদান একমাত্র বিশ্বজগতের প্রতিপালকের কাছেই আছে।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3042,26,110,'কাজেই তোমরা আল্লাহকে ভয় কর এবং আমার অনুসরণ কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3043,26,111,'তারা বলল- ‘আমরা কি তোমার প্রতি বিশ্বাস করব যখন তোমার অনুসরণ করছে একেবারে নিম্নশ্রেণীর লোকেরা।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3044,26,112,'নূহ বলল- ‘তারা কী করত সেটা আমার জানা নেই। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3045,26,113,'তাদের হিসাব নেয়া তো আমার প্রতিপালকের কাজ, যদি তোমরা বুঝতে! ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3046,26,114,'মু’মিনদেরকে তাড়িয়ে দেয়া আমার কাজ নয়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3047,26,115,'আমি তো শুধু একজন সুস্পষ্ট সতর্ককারী।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3048,26,116,'তারা বলল- ‘হে নূহ! তুমি যদি বিরত না হও, তাহলে তুমি নিশ্চিতই প্রস্তরাঘাতে নিহত হবে।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3049,26,117,'নূহ বলল- ‘হে আমার প্রতিপালক! আমার সম্প্রদায় আমাকে প্রত্যাখান করছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3050,26,118,'কাজেই তুমি আমার ও তাদের মধ্যে ফয়সালা ক’রে দাও, আর আমাকে ও আমার সঙ্গী মু’মিনদেরকে ক্ষমা কর।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3051,26,119,'অতঃপর আমি তাকে ও তার সঙ্গে যারা ছিল তাদেরকে বোঝাই নৌযানে রক্ষা করলাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3052,26,120,'তারপর অবশিষ্ট সবাইকে ডুবিয়ে দিলাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3053,26,121,'অবশ্যই এতে নিদর্শন আছে, কিন্তু তাদের অধিকাংশই বিশ্বাসী নয়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3054,26,122,'তোমার প্রতিপালক, অবশ্যই তিনি প্রবল পরাক্রান্ত, পরম দয়ালু। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3055,26,123,'‘আদ সম্প্রদায় রসূলগণকে মিথ্যে সাব্যস্ত করেছিল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3056,26,124,'যখন তাদের ভাই হূদ তাদেরকে বলল- ‘তোমরা কি (আল্লাহকে) ভয় করবে না? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3057,26,125,'আমি তোমাদের জন্য (প্রেরিত) এক বিশ্বস্ত রসুল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3058,26,126,'কাজেই তোমরা আল্লাহকে ভয় কর এবং আমাকে মান্য কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3059,26,127,'আর এ জন্য আমি তোমাদের কাছে কোন প্রতিদান চাই না, আমার প্রতিদান আছে কেবল বিশ্বজগতের প্রতিপালকের নিকট। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3060,26,128,'তোমরা কি প্রতিটি উচ্চস্থানে অনর্থক স্মৃতিস্তম্ভ নির্মাণ করছ? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3061,26,129,'আর বড় বড় প্রাসাদ নির্মাণ করছ. যেন তোমরা চিরদিন থাকবে? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3062,26,130,'আর যখন তোমরা (দুর্বল শ্রেণীর লোকদের উপর) আঘাত হান, তখন আঘাত হান নিষ্ঠুর মালিকের মত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3063,26,131,'কাজেই তোমরা আল্লাহকে ভয় কর এবং আমাকে মান্য কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3064,26,132,'ভয় কর তাঁকে যিনি তোমাদেরকে যাবতীয় বস্তু দান করেছেন যা তোমাদের জানা আছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3065,26,133,'যিনি তোমাদেরকে দান করেছেন গবাদি পশু ও সন্তান-সন্তুতি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3066,26,134,'আর উদ্যানরাজি ও ঝর্ণাসমূহ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3067,26,135,'আমি তোমাদের জন্য মহা দিবসের শাস্তির ভয় করছি।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3068,26,136,'তারা বলল- ‘তুমি নসীহত কর আর না কর, আমাদের জন্য দু’ই সমান। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3069,26,137,'এসব (কথাবার্তা বলা) পূর্ববর্তী লোকেদের অভ্যাস ছাড়া আর অন্য কিছুই না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3070,26,138,'আমাদেরকে শাস্তি দেয়া হবে না।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3071,26,139,'অতঃপর তারা তাকে মিথ্যে ব’লে প্রত্যাখ্যান করল। তখন আমি তাদেরকে ধ্বংস করে দিলাম। অবশ্যই এতে নিদর্শন রয়েছে, কিন্তু তাদের অধিকাংশই বিশ্বাস করে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3072,26,140,'এবং তোমার প্রতিপালক, তিনি মহা প্রতাপশালী, বড়ই দয়ালু। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3073,26,141,'সামূদ জাতি রসূলগণকে প্রত্যাখ্যান করেছিল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3074,26,142,'যখন তাদের ভাই সালিহ তাদেরকে বলেছিল- তোমরা কি (আল্লাহকে) ভয় করবে না? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3075,26,143,'আমি তোমাদের জন্য (প্রেরিত) বিশ্বস্ত রসূল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3076,26,144,'কাজেই তোমরা আল্লাহকে ভয় কর ও আমাকে মান্য কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3077,26,145,'আর এজন্য আমি তোমাদের কাছে কোন প্রতিদান চাই না, আমার প্রতিদান তো আছে একমাত্র বিশ্বজগতের প্রতিপালকের নিকট। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3078,26,146,'তোমাদেরকে কি এখানে যে সব (ভোগ বিলাস) আছে তাতেই নিরাপদে রেখে দেয়া হবে? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3079,26,147,'উদ্যানরাজি আর ঝার্ণাসমূহে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3080,26,148,'আর শষ্যক্ষেতে ও ফুলে আচ্ছাদিত (ফলে ভারাক্রান্ত) খেজুর বাগানে? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3081,26,149,'এবং তোমরা দক্ষতার সাথে পাহাড় কেটে গৃহ নির্মাণ করছ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3082,26,150,'কাজেই তোমরা আল্লাহকে ভয় কর ও আমাকে মান্য কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3083,26,151,'এবং সীমালঙ্ঘনকারীদের নির্দেশ মান্য কর না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3084,26,152,'যারা পৃথিবীতে বিশৃঙ্খলা সৃষ্টি করে, সংস্কার করে না।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3085,26,153,'তারা বলল- ‘তুমি তো কেবল যাদুগ্রস্তদের একজন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3086,26,154,'তুমি আমাদের মত মানুষ ছাড়া আর কিছুই না। কাজেই তুমি সত্যবাদী হলে একটা নিদর্শন হাজির কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3087,26,155,'সালিহ বলল- ‘এই একটি উটনি, এর জন্য আছে পানি পানের পালা আর তোমাদের জন্য আছে পানি পানের পালা নির্ধারিত দিনে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3088,26,156,'অনিষ্ট সাধনের নিমিত্তে তাকে স্পর্শ কর না, তাহলে তোমাদেরকে মহা দিবসের আযাব পাকড়াও করবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3089,26,157,'কিন্তু তারা তাকে বধ করল, ফলে তারা অনুতপ্ত হল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3090,26,158,'অতঃপর আযাব তাদেরকে পাকড়াও করল। এতে অবশ্যই নিদর্শন রয়েছে, কিন্তু তাদের অধিকাংশই বিশ্বাস করে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3091,26,159,'আর তোমার প্রতিপালক তিনি তো মহা পরাক্রমশালী, বড়ই দয়ালু। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3092,26,160,'লূতের সম্প্রদায় রসুলদেরকে মিথ্যে বলে প্রত্যাখ্যান করেছিল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3093,26,161,'যখন তাদের ভাই লূত তাদেরকে বলেছিল- ‘তোমরা কি (আল্লাহকে) ভয় করবে না? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3094,26,162,'আমি তো তোমাদের জন্য (প্রেরিত) একজন বিশ্বস্ত রসূল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3095,26,163,'কাজেই তোমরা আল্লাহকে ভয় কর ও আমাকে মান্য কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3096,26,164,'আমি এজন্য তোমাদের কাছে কোন প্রতিদান চাই না, আমার প্রতিদান একমাত্র জগতসমূহের প্রতিপালকের নিকট রয়েছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3097,26,165,'জগতের সকল প্রাণীর মধ্যে তোমরাই কি পুরুষদের সঙ্গে উপগত হও, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3098,26,166,'এবং তোমাদের প্রতিপালক তোমাদের জন্য যে স্ত্রীগণকে সৃষ্টি করেছেন তাদেরকে ত্যাগ কর? রবং তোমরা এক সীমালঙ্ঘনকারী সম্প্রদায়।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3099,26,167,'তারা বলল- ‘হে লূত! তুমি যদি বিরত না হও তবে তুমি অবশ্য অবশ্যই বহিস্কৃত হবে।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3100,26,168,'লূত বলল- ‘আমি তোমাদের এ কাজকে ঘৃণা করি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3101,26,169,'হে আমার প্রতিপালক! তারা যা করে তা থেকে তুমি আমাকে ও আমার পরিবারবর্গকে রক্ষা কর।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3102,26,170,'অতঃপর আমি তাকে ও তার পরিবারবর্গের সকলকে রক্ষা করলাম ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3103,26,171,'এক বৃদ্ধা ছাড়া। সে ছিল পেছনে অবস্থানকারীদের অন্তর্ভুক্ত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3104,26,172,'অতঃপর অন্যদের সকলকে পুরোপুরি ধ্বংস করে দিলাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3105,26,173,'তাদের উপর বর্ষণ করলাম (শাস্তির) বৃষ্টি, ভয় প্রদর্শিতদের জন্য এ বৃষ্টি ছিল কতই না মন্দ! ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3106,26,174,'নিশ্চয়ই এতে নিদর্শন রয়েছে, কিন্তু অধিকাংশই বিশ্বাস করে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3107,26,175,'তোমার প্রতিপালক, তিনি মহা প্রতাপশালী, বড়ই দয়ালু। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3108,26,176,'বনের অধিবাসীরা রসূলদেরকে মিথ্যে বলে প্রত্যাখ্যান করেছিল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3109,26,177,'যখন শু‘আয়ব তাদেরকে বলেছিল- ‘তোমরা কি (আল্লাহকে) ভয় করবে না? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3110,26,178,'আমি তোমাদের জন্য (প্রেরিত) বিশ্বস্ত রাসুল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3111,26,179,'কাজেই তোমরা আল্লাহকে ভয় কর এবং আমাকে মান্য কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3112,26,180,'এ জন্য আমি তোমাদের কাছে কোন প্রতিদান চাই না, আমার প্রতিদান তো রয়েছে একমাত্র জগতসমূহের প্রতিপালকের নিকট। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3113,26,181,'মাপে পূর্ণ মাত্রায় দাও আর যারা মাপে কম দেয় তাদের অন্তর্ভুক্ত হয়ো না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3114,26,182,'সঠিক দাঁড়িপাল্লায় ওজন করবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3115,26,183,'মানুষকে তাদের প্রাপ্যবস্তু কম দিবে না। আর পৃথিবীতে বিশৃঙ্খলা সৃষ্টি করো না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3116,26,184,'এবং ভয় কর তাঁকে যিনি তোমাদেরকে এবং তোমাদের পূর্ববর্তী বংশাবলীকে সৃষ্টি করেছেন।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3117,26,185,'তারা বলল- ‘তুমি তো কেবল যাদুগ্রস্তদের একজন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3118,26,186,'তুমি আমাদের মতই মানুষ বৈ নও, আমরা মনে করি তুমি অবশ্য মিথ্যাবাদীদের অন্তর্ভুক্ত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3119,26,187,'তুমি সত্যবাদী হলে আকাশের এক টুকরো আমাদের উপর ফেলে দাও।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3120,26,188,'শু‘আয়ব বলল- ‘তোমরা যা কর, আমার প্রতিপালক সে সম্পর্কে বেশি অবগত।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3121,26,189,'কিন্তু তারা তাকে প্রত্যাখ্যান করল। ফলে তাদেরকে এক মেঘাচ্ছন্ন দিবসের শাস্তি পাকড়াও করল। তা ছিল এক মহা দিবসের ‘আযাব। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3122,26,190,'এতে অবশ্যই নিদর্শন রয়েছে। কিন্তু তাদের অধিকাংশই বিশ্বাস করে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3123,26,191,'আর তোমার প্রতিপালক, তিনি অবশ্যই মহা প্রতাপশালী, বড়ই দয়ালু। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3124,26,192,'অবশ্যই এ কুরআন জগতসমূহের প্রতিপালকের নিকট হতে অবতীর্ণ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3125,26,193,'বিশ্বস্ত আত্মা (জিবরাঈল) একে নিয়ে অবতরণ করেছে ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3126,26,194,'তোমার অন্তরে যাতে তুমি সতর্ককারীদের অন্তর্ভুক্ত হও।  ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3127,26,195,'সুস্পষ্ট আরবী ভাষায়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3128,26,196,'পূর্ববর্তী কিতাবসমূহেও নিশ্চয় এর উল্লেখ আছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3129,26,197,'এটা কি তাদের জন্য নিদর্শন নয় যে, বানী ইসরাঈলের পন্ডিতগণ তা জানত (যে তা সত্য)। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3130,26,198,'আমি যদি তা কোন অনারবের প্রতি অবতীর্ণ করতাম, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3131,26,199,'অতঃপর সে তা তাদের নিকট পাঠ করত, তাহলে তারা তাতে বিশ্বাস আনত না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3132,26,200,'এভাবে আমি অপরাধীদের অন্তরে অবিশ্বাস সঞ্চার করেছি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3133,26,201,'তারা এর প্রতি ঈমান আনবে না যতক্ষণ না তারা ভয়াবহ শাস্তি প্রত্যক্ষ করে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3134,26,202,'কাজেই তা তাদের কাছে হঠাৎ এসে পড়বে, তারা কিছুই বুঝতে পারবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3135,26,203,'তারা তখন বলবে- ‘আমাদেরকে কি অবকাশ দেয়া হবে?’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3136,26,204,'তারা কি আমার শাস্তি দ্রুত কামনা করে? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3137,26,205,'তুমি কি ভেবে দেখেছ আমি যদি তাদেরকে কতক বছর ভোগ বিলাস করতে দেই, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3138,26,206,'অতঃপর তাদেরকে যে বিষয়ের ও‘য়াদা দেয়া হত তা তাদের কাছে এসে পড়ে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3139,26,207,'তখন তাদের বিলাসের সামগ্রী তাদের কোন উপকারে আসবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3140,26,208,'আমি এমন কোন জনপদ ধ্বংস করিনি যার জন্য কোন ভয় প্রদর্শনকারী ছিল না ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3141,26,209,'স্মরণ করানোর জন্য। আমি কখনো অন্যায়কারী নই। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3142,26,210,'শয়ত্বানরা তা (অর্থাৎ কুরআন) নিয়ে অবতরণ করেনি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3143,26,211,'তারা এ কাজের যোগ্য নয় আর তারা এর সামর্থ্যও রাখে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3144,26,212,'তাদেরকে এটা শোনা থেকে অবশ্যই দূরে রাখা হয়েছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3145,26,213,'কাজেই তুমি অন্য কোন ইলাহ্কে আল্লাহর সঙ্গে ডেক না। ডাকলে তুমি শাস্তিপ্রাপ্তদের অন্তর্ভুক্ত হয়ে যাবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3146,26,214,'আর তুমি সতর্ক কর তোমার নিকটাত্মীয় স্বজনদের ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3147,26,215,'যে সকল বিশ্বাসীরা তোমার আনুগত্য করে তাদের জন্য তুমি তোমার অনুকম্পার বাহু প্রসারিত কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3148,26,216,'তারা যদি তোমার অবাধ্যতা করে তাহলে তুমি বলে দাও- তোমরা যা কর তার সঙ্গে আমি সম্পর্কহীন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3149,26,217,'আর তুমি প্রবল পরাক্রান্ত পরম দয়ালুর উপর নির্ভর কর; ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3150,26,218,'যিনি তোমাকে দেখেন যখন তুমি (নামাযের জন্য) দন্ডায়মান হও। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3151,26,219,'আর (তিনি দেখেন) সাজদাকারীদের সঙ্গে তোমার চলাফিরা। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3152,26,220,'তিনি সব কিছু শোনেন, সব কিছু জানেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3153,26,221,'আমি কি তোমাদেরকে জানাব কাদের নিকট শয়ত্বানরা অবতীর্ণ হয়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3154,26,222,'তারা অবতীর্ণ হয় প্রত্যেকটি চরম মিথ্যুক ও পাপীর নিকট। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3155,26,223,'ওরা কান পেতে থাকে আর তাদের অধিকাংশই মিথ্যাবাদী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3156,26,224,'বিভ্রান্তরাই কবিদের অনুসরণ করে, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3157,26,225,'তুমি কি দেখ না যে, তারা প্রতি ময়দানেই উদভ্রান্ত হয়ে ফিরে? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3158,26,226,'আর তারা যা বলে তা তারা নিজেরা করে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3159,26,227,'কিন্তু ওরা ব্যতীত যারা ঈমান আনে ও সৎকাজ করে আর আল্লাহকে খুব বেশি স্মরণ করে আর নির্যাতিত হওয়ার পর নিজেদের প্রতিরক্ষার ব্যবস্থা করে। যালিমরা শীঘ্রই জানতে পারবে কোন্ (মহা সংকটময়) জায়গায় তারা ফিরে যাচ্ছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3160,27,1,'তা-সীন, এগুলো কুরআনের আয়াত ও সুস্পষ্ট কিতাবের; ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3161,27,2,'মু’মিনদের জন্য পথের দিশা ও সুসংবাদ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3162,27,3,'যারা নামায কায়েম করে, যাকাত আদায় করে আর তারা আখিরাতে বিশ্বাসী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3163,27,4,'যারা আখিরাতে বিশ্বাস করে না, তাদের (চোখে) তাদের কর্মকান্ডকে আমি সুশোভিত করেছি, কাজেই তারা উদভ্রান্ত হয়ে ঘুরে বেড়ায়, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3164,27,5,'এরাই হল যাদের জন্য রয়েছে কঠিন শাস্তি আর এরাই আখিরাতে সবচেয়ে ক্ষতিগ্রস্ত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3165,27,6,'নিশ্চয় তোমাকে কুরআন দেয়া হয়েছে মহাবিজ্ঞ সর্বজ্ঞের নিকট হতে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3166,27,7,'স্মরণ কর যখন মূসা তার পরিবারবর্গকে বলেছিল- ‘আমি আগুন দেখেছি, কাজেই আমি শীঘ্রই সেখান থেকে তোমাদের জন্য খবর নিয়ে আসব কিংবা তোমাদের কাছে জ্বলন্ত আগুন নিয়ে আসব যাতে তোমরা আগুন পোহাতে পার।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3167,27,8,'অতঃপর সে যখন আগুনের কাছে আসল তখন আওয়াজ হল- ‘ধন্য, যারা আছে এই আলোর মধ্যে আর তার আশেপাশে, বিশ্বজাহানের প্রতিপালক পবিত্র, মহিমান্বিত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3168,27,9,'হে মূসা! আমি মহা পরাক্রান্ত প্রজ্ঞাময় আল্লাহ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3169,27,10,'তুমি তোমার লাঠি নিক্ষেপ কর; অতঃপর যখন সে ওটাকে ছুটাছুটি করতে দেখল যেন ওটা একটা সাপ, তখন সে পেছনের দিকে ছুটতে লাগল এবং ফিরেও দেখল না। (তখন বলা হল) হে মূসা! তুমি ভয় করো না, নিশ্চয়ই আমার কাছে রসূলগণ ভয় পায় না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3170,27,11,'তবে যে অত্যাচার করে অতঃপর মন্দ কাজের পরিবর্তে সৎ কাজ করে, তাহলে নিশ্চয় আমি ক্ষমাশীল, বড়ই দয়ালু। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3171,27,12,'আর তুমি তোমার হাত বগলে ঢুকাও, তা শুভ্র হয়ে বের হয়ে আসবে দোষমুক্ত অবস্থায়, তা হল ফেরাউন ও তার সম্প্রদায়ের নিকট নিয়ে আসা নয়টি নিদর্শনের অন্তর্ভুক্ত। নিশ্চয় তারা ছিল পাপাচারী সম্প্রদায়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3172,27,13,'অতঃপর যখন তাদের কাছে আমার দৃশ্যমান নিদর্শন আসল, তারা বলল- ‘এটা স্পষ্ট যাদু। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3173,27,14,'তারা অন্যায় ঔদ্ধত্যভরে নিদর্শনগুলোকে প্রত্যাখ্যান করল যদিও তাদের অন্তর এগুলোকে সত্য বলে বিশ্বাস করেছিল। অতএব দেখ, ফাসাদ সৃষ্টিকারীদের পরিণতি কেমন হয়েছিল! ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3174,27,15,'আমি দাঊদ ও সুলাইমানকে জ্ঞান দান করেছিলাম। তারা উভয়ে বলেছিল, ‘সকল প্রশংসা আল্লাহর যিনি তাঁর বহু মু’মিন বান্দাদের উপর আমাদেরকে মর্যাদা দান করেছেন।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3175,27,16,'সুলাইমান দাঊদের উত্তরাধিকারী হয়েছিল। সে বলেছিল- ‘হে মানুষেরা! আমাকে পক্ষীকুলের ভাষা শিক্ষা দেয়া হয়েছে আর আমাদেরকে সব কিছু দেয়া হয়েছে, এটা (আল্লাহর পক্ষ হতে) অবশ্যই সুস্পষ্ট অনুগ্রহ।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3176,27,17,'সুলাইমানের সামনে তার সেনাবাহিনীকে সমবেত করা হল, জ্বিন, মানুষ ও পক্ষীকুলকে; অতঃপর তাদেরকে বিভিন্ন বুহ্যে বিন্যস্ত করা হল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3177,27,18,'যখন তারা পিপীলিকার উপত্যকায় আসল তখন একটি পিপীলিকা বলল- ‘ওহে পিঁপড়ার দল! তোমাদের বাসস্থানে ঢুকে পড়, যাতে সুলাইমান ও তার সৈন্যবাহিনী তাদের অগোচরে তোমাদেরকে পদপিষ্ট ক’রে না ফেলে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3178,27,19,'সুলাইমান তার কথায় খুশিতে মুচকি হাসল আর বলল- ‘হে আমার প্রতিপালক! তুমি আমার প্রতি ও আমার পিতামাতার প্রতি যে অনুগ্রহ দান করেছ তার জন্য কৃতজ্ঞতা প্রকাশের আমাকে শক্তি দান কর আর যাতে এমন সৎকাজ করতে পারি যাতে তুমি সন্তুষ্ট হও আর তোমার দয়ায় আমাকে তোমার সৎকর্মশীল বান্দাদের অন্তর্ভুক্ত কর।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3179,27,20,'অতঃপর সুলাইমান পাখীদের খোঁজ খবর নিল। সে বলল, কী ব্যাপার, হুদহুদকে তো দেখছি না, নাকি সে অনুপস্থিত? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3180,27,21,'আমি তাকে অবশ্য অবশ্যই শাস্তি দেব কঠিন শাস্তি কিংবা তাকে অবশ্য অবশ্যই হত্যা করব অথবা সে অবশ্য অবশ্যই আমাকে তার (অনুপস্থিতির) উপযুক্ত কারণ দর্শাবে।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3181,27,22,'অতঃপর হুদহুদ অবিলম্বে এসে বলল- ‘আমি যা অবগত হয়েছি আপনি তা অবগত নন, আমি সাবা থেকে নিশ্চিত খবর নিয়ে আপনার কাছে এসেছি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3182,27,23,'আমি দেখলাম এক নারী তাদের উপর রাজত্ব করছে আর তাকে সব কিছুই দেয়া হয়েছে আর তার আছে এক বিরাট সিংহাসন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3183,27,24,'এবং আমি তাকে আর তার সম্প্রদায়কে দেখলাম আল্লাহর পরিবর্তে সূর্যকে সেজদা করতে। শয়ত্বান তাদের কাজকে তাদের জন্য শোভন করে দিয়েছে এবং তাদেরকে সৎপথ থেকে বাধা দিয়ে রেখেছে কাজেই তারা সৎপথ পায় না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3184,27,25,'(শয়ত্বান বাধা দিয়ে রেখেছে) যাতে তারা আল্লাহকে সেজদা না করে যিনি আকাশমন্ডলী ও পৃথিবীর লুক্কায়িত বস্তুকে প্রকাশ করেন, যিনি জানেন তোমরা যা গোপন কর আর তোমরা যা প্রকাশ কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3185,27,26,'আল্লাহ, তিনি ছাড়া সত্যিকারের কোন ইলাহ্ নেই, (তিনি) মহান ‘আরশের অধিপতি।’[সাজদাহ]');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3186,27,27,'সুলাইমান বলল- ‘এখন আমি দেখব, তুমি সত্য বলেছ, না তুমি মিথ্যেবাদী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3187,27,28,'আমার এই পত্র নিয়ে যাও আর এটা তাদের কাছে অর্পণ কর। অতঃপর তাদের কাছ থেকে সরে পড় তারপর দেখ, তারা কী জবাব দেয়।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3188,27,29,'সেই নারী বলল- ‘ওহে সভাসদগণ! এই যে আমাকে এক সম্মানযোগ্য পত্র দেয়া হয়েছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3189,27,30,'এটা সুলাইমানের পক্ষ হতে আর তা এই : অসীম দাতা, অতীব দয়ালু আল্লাহর নামে শুরু, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3190,27,31,'আমার প্রতি উদ্ধত হয়ো না, অনুগত হয়ে আমার কাছে হাজির হও। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3191,27,32,'সে বলল, ওহে সভাসদরা! তোমরা আমার কর্তব্য সম্পর্কে আমাকে সিদ্ধান্ত দাও। তোমাদের উপস্থিতি ব্যতীত আমি কোন ব্যাপারে সিদ্ধান্ত গ্রহণ করিনি।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3192,27,33,'তারা বলল- ‘আমরা শক্তির অধিকারী ও কঠোর যোদ্ধা, কিন্তু সিন্ধান্ত গ্রহণের অধিকারী আপনিই, কাজেই চিন্তা করে দেখুন, আপনি কী আদেশ করবেন।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3193,27,34,'সে বলল- ‘রাজারা যখন কোন জনপদে ঢুকে তখন বিপর্যয় ডেকে আনে এবং তথাকার সম্মানিত ব্যক্তিদেরকে অপমানিত করে ছাড়ে আর এরাও তাই করবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3194,27,35,'আমি তাঁর কাছে উপঢৌকন পাঠাচ্ছি, তারপর দেখি, দূতেরা কী (জবাব) নিয়ে আসে।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3195,27,36,'অতঃপর দূতরা যখন সুলাইমানের কাছে আসল, সুলাইমান বলল- ‘তোমরা কি আমাকে সম্পদ দিয়ে সাহায্য করছ, কিন্তু আল্লাহ আমাকে যা দিয়েছেন তা তোমাদেরকে যা দিয়েছেন তার চেয়ে উত্তম, বরং তোমরাই তোমাদের উপঢৌকন নিয়ে আনন্দ কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3196,27,37,'তাদের কাছে ফিরে যাও, আমি অবশ্য অবশ্যই তাদের বিরুদ্ধে এক সেনাবাহিনী নিয়ে আসব যার মুকাবালা করার শক্তি তাদের নেই, আমি অবশ্য অবশ্যই তাদেরকে অপমানিত করে সেখানে থেকে বের করে দেব আর তারা হবে অপদস্থ।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3197,27,38,'সুলাইমান বলল- ‘হে সভাসদবর্গ! তারা আমার কাছে আত্মসমর্পণ করে আসার আগে তোমাদের মধ্যে কে তার সিংহাসন আমার নিকট নিয়ে আসবে?’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3198,27,39,'এক শক্তিধর জ্বিন বলল- ‘আপনি আপনার জায়গা থেকে উঠবার আগে আমি তা আপনার কাছে এনে দেব, এ কাজে আমি অবশ্যই ক্ষমতার অধিকারী ও আস্থাভাজন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3199,27,40,'যার কাছে কিতাবের (তাওরাতের) জ্ঞান ছিল সে বলল- ‘আপনার দৃষ্টি আপনার দিকে ফিরে আসার পূর্বেই আমি তা আপনার কাছে এনে দেব।’ সুলাইমান যখন তা তার সামনে রক্ষিত দেখতে পেল তখন সে বলল- ‘এটা আমার প্রতিপালকের অনুগ্রহ, আমাকে পরীক্ষা করার জন্য- আমি কৃতজ্ঞতা প্রকাশ করি, না অকৃতজ্ঞ হই। যে কৃতজ্ঞতা প্রকাশ করে সে নিজের কল্যাণেই কৃতজ্ঞতা প্রকাশ করে। আর যে অকৃতজ্ঞ হয় (সে জেনে রাখুক), নিশ্চয়ই আমার প্রতিপালক অভাবমুক্ত, মর্যাদায় সর্বশ্রেষ্ঠ।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3200,27,41,'সুলাইমান বলল- ‘তার সিংহাসনের আকৃতি বদলে দাও, অতঃপর আমরা দেখি, সে (তার নিজের পৌঁছার পূর্বেই আলৌকিকভাবে তার সিংহাসন সুলায়মানের দরবারে রক্ষিত দেখে সত্য) পথের দিশা পায়, না যারা পথের দিশা পায় না সে তাদের অন্তর্ভুক্ত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3201,27,42,'যখন সে নারী আসল তখন তাকে বলা হল- ‘এটা কি তোমার সিংহাসন?’ সে বলল, ‘এটা যেন সেটাই, আমাদেরকে এর আগেই (আপনার সম্পর্কে) জ্ঞান দান করা হয়েছে আর আমরা আত্মসমর্পণ করেছি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3202,27,43,'আল্লাহর পরিবর্তে সে যার পূজা করত তাই তাকে সত্য পথে চলা থেকে বাধা দিয়ে রেখেছিল, সে নারী ছিল কাফির সম্প্রদায়ের অন্তর্ভুক্ত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3203,27,44,'তাকে বলা হল- ‘প্রাসাদে প্রবেশ কর।’ যখন সে তা দেখল, সে ওটাকে পানির হ্রদ মনে করল এবং সে তার পায়ের গোছা খুলে ফেলল। সুলাইমান বলল- ‘এটা তো স্বচ্ছ কাঁচমন্ডিত প্রাসাদ। সে নারী বলল- হে আমার প্রতিপালক! আমি অবশ্যই নিজের প্রতি যুলম করেছি আর আমি সুলাইমানের সঙ্গে বিশ্বজগতের প্রতিপালক আল্লাহর নিকট আত্মসমর্পণ করছি।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3204,27,45,'আমি সামূদ সম্প্রদায়ের নিকট তাদের ভাই সালিহকে পাঠিয়েছিলাম (এই আদেশ দিয়ে যে) তোমরা আল্লাহর ‘ইবাদাত কর। অতঃপর তারা দু’ভাগ হয়ে গিয়ে বিতর্কে জড়িয়ে পড়ল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3205,27,46,'সালিহ্ বলল- ‘হে আমার সম্প্রদায়! তোমরা কল্যাণের পূর্বে দ্রুত অকল্যাণ কামনা করছ কেন? তোমরা কেন আল্লাহর নিকট ক্ষমা প্রার্থনা কর না যাতে তোমরা রহমতপ্রাপ্ত হও?’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3206,27,47,'তারা বলল- আমরা তোমাকে আর তোমার সঙ্গে যারা আছে তাদেরকে অকল্যাণের কারণ ব’লে মনে করি।’ সে বলল- ‘তোমাদের অকল্যাণ আল্লাহর নিকট, বরং তোমরা এমন এক সম্প্রদায় যাদেরকে পরীক্ষা করা হচ্ছে।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3207,27,48,'আর সেই শহরে ছিল নয় ব্যক্তি যারা দেশে ফাসাদ সৃষ্টি করত আর তারা সংশোধন করত না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3208,27,49,'তারা বলল- ‘তোমরা আল্লাহর নামে শপথ কর যে, তোমরা রাত্রিযোগে তাকে আর তার পরিবারবর্গকে অবশ্য অবশ্যই আক্রমণ করবে, অতঃপর আমরা তার অভিভাবককে অবশ্য অবশ্যই বলব, আমরা তার পরিবারবর্গের হত্যাকান্ড প্রত্যক্ষ করিনি। আমরা অবশ্যই সত্যবাদী।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3209,27,50,'তারা এক চক্রান্ত করেছিল আর আমিও এক কৌশল অবলম্বন করেছিলাম, কিন্তু তারা বুঝতে পারেনি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3210,27,51,'অতঃপর দেখ তাদের চক্রান্তের পরিণতি কেমন হয়েছিল? আমিই তাদেরকে ও তাদের সম্প্রদায়কে, সকলকে ধ্বংস করে দিয়েছিলাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3211,27,52,'এই তো তাদের ঘরদোর সম্পূর্ণ উজাড়, কারণ তারা বাড়াবাড়ি করেছিল। এতে জ্ঞানী সম্প্রদায়ের জন্য অবশ্যই নিদর্শন আছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3212,27,53,'আর যারা ঈমান এনেছিল ও (আল্লাহকে) ভয় করত তাদেরকে রক্ষা করেছিলাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3213,27,54,'স্মরণ কর লূতের কথা, সে তার সম্প্রদায়কে বলেছিল- তোমরা দেখে-শুনে কেন অশ্লীল কাজ করছ, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3214,27,55,'তোমরা কি কাম আসক্তি মিটানোর জন্য নারীদের বাদ দিয়ে পুরুষদের নিকট গমন কর? তোমরা এমন এক জাতি যারা মূর্খের আচরণ করছ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3215,27,56,'তখন তার সম্প্রদায়ের এ কথা বলা ছাড়া আর কোন জওয়াব ছিল না যে, তোমাদের জনপদ থেকে লূতের পরিবারবর্গকে বের করে দাও, এরা এমন লোক যারা পবিত্র সাজতে চায়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3216,27,57,'অতঃপর আমি তাকে ও তার পরিবারবর্গকে রক্ষা করলাম, তার স্ত্রী ব্যতীত। আমি তার ভাগ্য ধ্বংসপ্রাপ্তদের মধ্যে নির্ধারণ করেছিলাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3217,27,58,'আর আমি তাদের উপর বর্ষিয়ে ছিলাম এক ভয়ংকর বৃষ্টি। ভীতি প্রদর্শিতদের উপর এ বৃষ্টি ছিল কতই না মন্দ! ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3218,27,59,'বল, যাবতীয় প্রশংসা আল্লাহরই জন্য এবং শান্তি তাঁর মনোনীত বান্দাগণের প্রতি। আল্লাহ শ্রেষ্ঠ, না তা যার শরীক করে তারা? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3219,27,60,'নাকি তিনিই (শ্রেষ্ঠ) যিনি সৃষ্টি করেছেন আসমানসমূহ ও পৃথিবী এবং তোমাদের জন্য আকাশ থেকে বৃষ্টি বর্ষণ করেন, অতঃপর তা দ্বারা আমি মনোরম উদ্যানরাজি উদগত করি, তার বৃক্ষাদি উদগত করার ক্ষমতা তোমাদের নেই। আল্লাহর সাথে অন্য কোন ইলাহ আছে কি? বরং তারা হচ্ছে এক ন্যায়-বিচ্যুত সম্প্রদায়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3220,27,61,'নাকি তিনিই (শ্রেষ্ঠ) যিনি এই পৃথিবীকে বাসযোগ্য করেছেন আর তার ফাঁকে ফাঁকে নদীনালা প্রবাহিত করেছেন, তাতে সুদৃঢ় পর্বত সংস্থাপিত করেছেন এবং দু’ দরিয়ার মাঝে পার্থক্যকারী আড়াল সৃষ্টি করেছেন; আল্লাহর সাথে অন্য কোন ইলাহ আছে কি? বরং তাদের অধিকাংশই জানে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3221,27,62,'নাকি তিনিই (শ্রেষ্ঠ) যিনি আর্তের আহবানে সাড়া দেন যখন সে তাঁকে ডাকে এবং দুঃখ-কষ্ট দূর করেন আর তোমাদেরকে পৃথিবীর উত্তরাধিকারী করেন? আল্লাহর সঙ্গে অন্য কোন ইলাহ্ আছে কি? অতি সামান্য উপদেশই তোমরা গ্রহণ কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3222,27,63,'নাকি তিনিই (শ্রেষ্ঠ) যিনি জল স্থলের গভীর অন্ধকারে পথ দেখান এবং যিনি তাঁর (বৃষ্টিরূপী) অনুগ্রহের পূর্বক্ষণে শুভবার্তাবাহী বাতাস প্রেরণ করেন? আল্লাহর সঙ্গে অন্য কোন ইলাহ্ আছে কি? তারা যাকে (আল্লাহর) শরীক করে, আল্লাহ তা থেকে অনেক ঊর্ধ্বে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3223,27,64,'নাকি তিনিই (শ্রেষ্ঠ) যিনি সৃষ্টির সূচনা করেন, অতঃপর তার পুনরাবৃত্তি করেন এবং যিনি তোমাদেরকে আসমান ও যমীন থেকে রিযক দান করেন? আল্লাহর সাথে অন্য কোন ইলাহ্ আছে কি? বল, তোমরা সত্যবাদী হলে তোমাদের প্রমাণপঞ্জি পেশ কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3224,27,65,'বল, আকাশ ও পৃথিবীতে যারা আছে তারা কেউই অদৃশ্য বিষয়ের জ্ঞান রাখে না আল্লাহ ছাড়া, আর তারা জানে না কখন তাদেরকে জীবিত ক’রে উঠানো হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3225,27,66,'বরং আখিরাত সম্পর্কিত তাদের জ্ঞানের সীমা শেষ, বরং এ ব্যাপারে তারা সন্দেহে আচ্ছন্ন বরং এ বিষয়ে তারা অন্ধ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3226,27,67,'অবিশ্বাসীরা বলে- আমরা আর আমাদের পিতৃপুরুষরা যখন মাটি হয়ে যাব তারপরও কি আমাদেরকে নিশ্চিতই বের করে উঠানো হবে? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3227,27,68,'এ ওয়া‘দা আমাদেরকে দেয়া হয়েছে, আমাদেরকে এবং পূর্বে আমাদের পিতৃপুরুষদেরকেও; এ সব পূর্বকালের কাহিনী ছাড়া কিছুই নয়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3228,27,69,'বল, পৃথিবীতে ভ্রমণ কর, অতঃপর দেখ অপরাধীদের পরিণাম কেমন হয়েছিল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3229,27,70,'তাদের জন্য দুঃখ কর না, আর তাদের চক্রান্তের কারণে মনে কষ্ট নিও না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3230,27,71,'তারা বলে- ‘তোমরা যদি সত্যবাদী হয়ে থাক তাহলে, এ ‘ওয়াদা কখন বাস্তবায়িত হবে? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3231,27,72,'বল, তোমরা যা পাওয়ার জন্য তাড়াহুড়া করছ সম্ভবতঃ তার কিছু তোমাদের পিঠের পেছনে এসে গেছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3232,27,73,'তোমার প্রতিপালক নিশ্চয়ই মানুষের প্রতি অনুগ্রহশীল, কিন্তু তাদের অধিকাংশই কৃতজ্ঞতা প্রকাশ করে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3233,27,74,'তোমার প্রতিপালক অবশ্যই জানেন তাদের অন্তর যা গোপন করে আর যা প্রকাশ করে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3234,27,75,'আকাশে আর যমীনে এমন কোন অদৃশ্য বিষয় নেই যা সুস্পষ্ট কিতাবে নেই। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3235,27,76,'নিশ্চয় এ কুরআন সেগুলোর অধিকাংশ বিবৃত করে যে বিষয়ে বানী ইসরাঈল মতভেদ করেছিল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3236,27,77,'আর তা নিশ্চিতই মু’মিনদের জন্য সঠিক পথের দিশারী ও রহমত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3237,27,78,'তোমার প্রতিপালক তাঁর বিধান অনুযায়ী তাদের মধ্যে ফয়সালা করে দেবেন আর তিনি প্রবল পরাক্রান্ত, সর্বজ্ঞানের অধিকারী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3238,27,79,'কাজেই তুমি আল্লাহর উপর নির্ভর কর, তুমি তো সুস্পষ্ট সত্যের উপর আছ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3239,27,80,'তুমি মৃতদেরকে শুনাতে পারবে না, আর বধিরকেও আহবান শুনাতে পারবে না (বিশেষতঃ) যখন তারা পিঠ ফিরিয়ে চলে যায়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3240,27,81,'আর তুমি অন্ধকেও তাদের গুমরাহী থেকে ফিরিয়ে আনার জন্য সৎপথ দেখাতে পারবে না। তুমি কেবল তাদেরকেই শুনাতে পারবে যারা আমার নিদর্শনাবলীতে বিশ্বাস করে, কাজেই তারা আত্মসমর্পণ করে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3241,27,82,'তাদেরকে দেয়া প্রতিশ্রুতি যখন বাস্তবে পরিণত হবে, আমি তখন (কিয়ামাত আগমণের নির্দশন হিসেবে) ভূমি থেকে তাদের জন্য একটি জন্তু বের করব যা তাদের সঙ্গে এ কথা বলবে যে, মানুষ আমার নিদর্শনসমূহে বিশ্বাস করত না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3242,27,83,'যেদিন আমি প্রত্যেকটি সম্প্রদায় হতে একটি দলকে সমবেত করব যারা আমার নিদর্শনাবলীকে অস্বীকার করেছিল, অতঃপর তাদেরকে সারিবদ্ধ করা হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3243,27,84,'যখন তারা এসে যাবে তখন আল্লাহ বলবেন- তোমরা কি আমার নিদর্শনকে মিথ্যে বলে প্রত্যাখ্যান করেছিলে যদিও তা তোমরা জ্ঞানায়ত্ত করতে পারনি; নাকি তোমরা অন্য কিছু করছিলে? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3244,27,85,'তাদেরকে দেয়া প্রতিশ্রুতি বাস্তবে ঘটে যাবে তাদের অন্যায় কার্যকলাপের কারণে আর তারা (তাদের পক্ষে) কোন কথা বলতে পারবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3245,27,86,'তারা কি লক্ষ্য করে না যে, আমি রাত বানিয়েছি যাতে তারা তাতে আরাম করতে পারে আর দিনকে করেছি আলো দানকারী? বিশ্বাসী সম্প্রদায়ের জন্য অবশ্যই এতে নিদর্শন আছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3246,27,87,'আর যে দিন শিঙ্গায় ফুঁ দেয়া হবে সেদিন যারা আকাশে আছে আর যারা যমীনে আছে তারা ভীত-সন্ত্রস্ত হয়ে পড়বে, তবে আল্লাহ যাদের জন্য ইচ্ছে করবেন তারা বাদে। সবাই তাঁর কাছে আসবে বিনয়ে অবনত হয়ে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3247,27,88,'তুমি পর্বতগুলোকে দেখ আর মনে কর তা অচল, কিন্তু সেগুলো চলমান হবে যেমন মেঘমালা চলে। এটা আল্লাহর সৃষ্টি নৈপুণ্য, যিনি সব কিছুকে করেছেন যথাযথ। তোমরা যা কিছু কর সে সম্পর্কে তিনি সম্পূর্ণরূপে অবগত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3248,27,89,'যারা সৎকাজ নিয়ে উপস্থিত হবে, তাদের জন্য আছে উৎকৃষ্টতর প্রতিদান, সেদিন তারা ভীতি শঙ্কা থেকে নিরাপদ থাকবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3249,27,90,'যারা অসৎকর্ম নিয়ে হাজির হবে, তাদেরকে মুখের ভরে আগুনে নিক্ষেপ করা হবে, (আর তাদেরকে বলা হবে) তোমরা যা করতে কেবল তারই প্রতিদান তোমাদেরকে দেয়া হচ্ছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3250,27,91,'আমি নির্দেশিত হয়েছি এই (মক্কা) নগরীর প্রতিপালকের ‘ইবাদাত করার জন্য যিনি তাকে (অর্থাৎ এই নগরীকে) সম্মানিত করেছেন। সকল বস্তু তাঁরই, আর আমি আদিষ্ট হয়েছি আমি যেন (আল্লাহর নিকট) আত্মসমর্পণকারীদের অন্তর্ভুক্ত হই। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3251,27,92,'আর আমি যেন কুরআন তিলাওয়াত করি। অতঃপর যে সঠিক পথে চলবে, সে নিজের কল্যাণেই সঠিক পথে চলবে। আর কেউ গুমরাহ্ হলে তুমি বল, আমি তো সতর্ককারীদের একজন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3252,27,93,'আর বল, যাবতীয় প্রশংসা আল্লাহরই। তিনি শীঘ্রই তোমাদেরকে তাঁর নিদর্শনগুলো দেখাবেন আর তা তোমরা চিনতে পারবে। তোমরা যা কর সে সম্পর্কে তোমার পালনকর্তা অমনোযোগী নন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3253,28,1,'তা-সীন-মীম, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3254,28,2,'এগুলো সুস্পষ্ট কিতাবের আয়াত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3255,28,3,'আমি মূসা ও ফিরআউনের কাহিনী হতে কিছু তোমার কাছে সত্যিকারভাবে বিবৃত করছি বিশ্বাসী সম্প্রদায়ের উদ্দেশে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3256,28,4,'বস্তুতঃ ফেরাউন দেশে উদ্ধত হয়ে গিয়েছিল আর সেখানকার অধিবাসীদেরকে বিভিন্ন শ্রেণীতে বিভক্ত করে তাদের একটি শ্রেণীকে দুর্বল করে রেখেছিল, তাদের পুত্রদেরকে সে হত্যা করত আর তাদের নারীদেরকে জীবিত রাখত; সে ছিল ফাসাদ সৃষ্টিকারী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3257,28,5,'দেশে যাদেরকে দুর্বল করে রাখা হয়েছিল আমি তাদের প্রতি অনুগ্রহ করার ইচ্ছে করলাম, আর তাদেরকে নেতা ও উত্তরাধিকারী করার (ইচ্ছে করলাম)। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3258,28,6,'আর (ইচ্ছে করলাম) তাদেরকে দেশে প্রতিষ্ঠিত করতে, আর ফেরাউন, হামান ও তাদের সৈন্য বাহিনীকে দেখিয়ে দিতে যা তারা তাদের (অর্থাৎ মূসার সম্প্রদায়ের) থেকে আশঙ্কা করত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3259,28,7,'আমি মূসার মায়ের প্রতি ওয়াহী করলাম যে, তাকে স্তন্য পান করাতে থাক। যখন তুমি তার সম্পর্কে আশঙ্কা করবে, তখন তুমি তাকে দরিয়ায় নিক্ষেপ করবে, আর তুমি ভয় করবে না, দুঃখও করবে না, আমি তাকে অবশ্যই তোমার কাছে ফিরিয়ে দেব আর তাকে রসূলদের একজন করব। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3260,28,8,'অতঃপর ফেরাউনের লোকজন তাকে উঠিয়ে নিল যাতে সে তাদের জন্য শত্রু হতে ও দুঃখের কারণ হতে পারে। ফেরাউন, হামান ও তাদের বাহিনীর লোকেরা তো ছিল অপরাধী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3261,28,9,'ফেরাঊনের স্ত্রী বলল- ‘এ শিশু আমার ও তোমার চক্ষু শীতলকারী, তাকে হত্যা কর না, সে আমাদের উপকারে লাগতে পারে অথবা তাকে আমরা পুত্র হিসেবেও গ্রহণ করতে পারি আর তারা কিছুই বুঝতে পারল না (তাদের এ কাজের পরিণাম কী)। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3262,28,10,'মূসার মায়ের অন্তর বিচলিত হয়ে উঠল। সে তো তার পরিচয় প্রকাশ করেই ফেলত যদি না আমি তার চিত্তকে দৃঢ় করতাম যাতে সে আস্থাশীল হয়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3263,28,11,'মূসার মা মূসার বোনকে বলল- ‘তার পিছনে পিছনে যাও।’ সে দূর থেকে তাকে দেখছিল কিন্তু তারা টের পায়নি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3264,28,12,'আগে থেকে আমি তাকে ধাত্রী-স্তন্য পান থেকে বিরত রেখেছিলাম। মূসার বোন বলল- ‘আমি কি তোমাদেরকে এমন একটা পরিবারের খোঁজ দেব যারা তাকে তোমাদের পক্ষে লালন পালন করবে আর তারা হবে তার হিতাকাঙ্ক্ষী।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3265,28,13,'এভাবে আমি তাকে তার মায়ের কাছে ফিরিয়ে আনলাম যাতে তার চোখ জুড়ায়, সে দুঃখ না করে আর জানতে পারে যে, আল্লাহর ও‘য়াদা সত্য; কিন্তু অধিকাংশ মানুষ তা জানে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3266,28,14,'মূসা যখন যৌবনে পদার্পণ করল আর পূর্ণ পরিণত হল, তখন আমি তাকে হিকমত ও জ্ঞান দান করলাম; আমি সৎকর্মশীলদেরকে এভাবেই পুরস্কৃত করে থাকি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3267,28,15,'সে শহরে প্রবেশ করল যখন সেখানের লোকেরা অসতর্ক অবস্থায় ছিল। তখন সে দু’জন লোককে সংঘর্ষরত অবস্থায় পেল। একজন তার দলের, অপরজন তার শত্রুদলের। তখন তার দলের লোকটি তার শত্রুদলের লোকটির বিরুদ্ধে তার কাছে সাহায্যের আবেদন জানালো। তখন মূসা তাকে ঘুসি মারল এবং হত্যা করে ফেলল। মূসা বলল- ‘এটা শয়ত্বানের কাজ। সে নিশ্চয় প্রকাশ্য শত্রু, গুমরাহকারী।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3268,28,16,'সে বলল- ‘হে আমার প্রতিপালক! আমি নিজের আত্মার উপর যুলম করেছি, অতএব আমাকে ক্ষমা কর।’ অতঃপর আল্লাহ তাকে ক্ষমা করলেন, অবশ্যই তিনি ক্ষমাশীল, অতি দয়ালু। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3269,28,17,'মূসা বলল, ‘হে আমার প্রতিপালক! তুমি যেহেতু আমার উপর অনুগ্রহ করেছ, কাজেই আমি কক্ষনো পাপীদের সাহায্যকারী হব না।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3270,28,18,'শহরে তার সকাল হল ভীত-সন্ত্রস্ত অবস্থায়। হঠাৎ সে শুনল যে লোকটি গতকাল তার কাছে সাহায্য চেয়েছিল (আবার) সে সাহায্যের জন্য চীৎকার করছে। মূসা তাকে বলল- ‘তুমি প্রকাশ্যই একজন বেওকুফ লোক। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3271,28,19,'অতঃপর মূসা যখন উভয়ের শত্রুকে ধরতে উদ্যত হল, তখন সে বলল- ‘ওহে মূসা! তুমি কি আমাকে হত্যা করতে চাও যেভাবে গতকাল একটা লোককে হত্যা করেছ, তুমি তো পৃথিবীতে স্বেচ্ছাচারী হতে চাচ্ছ, সংশোধনকারীদের মধ্যে গণ্য হতে চাচ্ছ না।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3272,28,20,'নগরের প্রান্ত হতে এক লোক ছুটে আসল। সে বলল- ‘হে মূসা! পারিষদগণ তোমাকে হত্যার পরামর্শ করছে, কাজেই তুমি বাইরে চলে যাও, আমি তোমার হিতাকাঙ্ক্ষী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3273,28,21,'তখন মূসা ভীত-সন্ত্রস্ত অবস্থায় সেখান থেকে বেরিয়ে পড়ল সতর্কতার সঙ্গে। সে বলল, ‘হে আমার প্রতিপালক! তুমি আমাকে যালিম গোষ্ঠী হতে রক্ষা কর।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3274,28,22,'যখন সে মাদইয়ান অভিমুখী হল, সে বলল- ‘আশা করি আমার পালনকর্তা আমাকে সরল সোজা পথ দেখাবেন।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3275,28,23,'যখন সে মাদইয়ানের কূপের কাছে পৌঁছল, সে একদল লোককে দেখল (তাদের জন্তুগুলোকে) পানি পান করাতে, তাদের ছাড়া আরো দু’জন স্ত্রীলোককে দেখল (নিজেদের পশুগুলোকে) আগলে রাখতে। মূসা জিজ্ঞেস করল- ‘তোমাদের দু’জনের ব্যাপার কী?’ তারা বলল- ‘আমরা আমাদের জন্তুগুলোকে পান করাতে পারি না যতক্ষণ না রাখালেরা (তাদের পশুগুলোকে) সরিয়ে না নেয় (পানি পান করানোর পর), আর আমাদের পিতা খুবই বয়োবৃদ্ধ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3276,28,24,'তখন মূসা তাদের জন্য পশুগুলোকে পানি পান করিয়ে দিল, অতঃপর পেছন ফিরে ছায়ায় গিয়ে বসল, অতঃপর বলল- ‘হে আমার পালনকর্তা! তুমি আমার প্রতি যে অনুগ্রহই করবে আমি তো তারই ভিখারী।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3277,28,25,'তখন নারীদ্বয়ের একজন তার কাছে সলজ্জ পদে আসল। সে বলল- ‘আমার পিতা তোমাকে ডাকছে তুমি আমাদের জন্য (জন্তুগুলোকে) পানি পান করিয়েছ তোমাকে তার প্রতিদান দেয়ার জন্য। যখন মূসা তার কাছে গেল আর তার কাছে সকল ঘটনা বিবৃত করল, সে বলল- তুমি ভয় করো না, ‘তুমি যালিম গোষ্ঠীর থেকে রেহাই পেয়ে গেছ।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3278,28,26,'স্ত্রীলোক দু’টির একজন বলল- ‘হে পিতা! পারিশ্রমিকের বিনিময়ে তাকে নিযুক্ত করুন, আপনি যাদেরকে মজুর নিযুক্ত করবেন তাদের মধ্যে উত্তম যে শক্তিশালী, বিশ্বস্ত।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3279,28,27,'সে বলল- ‘আমার এই কন্যাদ্বয়ের একজনের সঙ্গে তোমার বিয়ে দেয়ার আমি ইচ্ছে করেছি এই শর্তে যে, তুমি আট বছর আমার কাজ করে দেবে, আর যদি দশ বছর পূর্ণ কর সেটা তোমার ইচ্ছেধীন, আমি তোমাকে কষ্টে ফেলতে চাই না, আল্লাহ ইচ্ছে করলে তুমি আমাকে সৎকর্মপরায়ণ পাবে।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3280,28,28,'মূসা বলল- আমার ও আপনার মধ্যে এই চুক্তি রইল, আমি দু’টি মেয়াদের যেটিই পূর্ণ করি না কেন, আমার বিরুদ্ধে কোন অভিযোগ করা হবে না, আমরা যে কথা বলছি, আল্লাহ তার সাক্ষী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3281,28,29,'অতঃপর মূসা যখন মেয়াদ পূর্ণ ক’রে তার পরিবার নিয়ে যাত্রা করল, তখন সে তূর পর্বতের দিকে আগুন দেখতে পেল। সে তার পরিবারবর্গকে বলল- ‘তোমরা অপেক্ষা কর, আমি আগুন দেখেছি, আমি তোমাদের জন্য সেখান থেকে খবর আনতে পারি কিংবা জ্বলন্ত কাষ্ঠখন্ড আনতে পারি যাতে তোমরা আগুন পোহাতে পার।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3282,28,30,'মূসা যখন আগুনের কাছে পৌঁছল তখন পবিত্র ভূমিতে অবস্থিত উপত্যকার ডান দিকে বৃক্ষ থেকে তাকে আহবান দিয়ে বলা হল- ‘হে মূসা! আমিই আল্লাহ, জগতসমূহের পালনকর্তা।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3283,28,31,'আর (বলা হল) ‘তোমার লাঠি নিক্ষেপ কর।’ অতঃপর যখন সে সেটাকে দেখল ছুটাছুটি করতে যেন ওটা একটা সাপ, তখন পেছনের দিকে দৌড় দিল, ফিরেও তাকাল না। (তখন তাকে বলা হল) ‘ওহে মূসা! সামনে এসো, ভয় করো না, তুমি নিরাপদ।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3284,28,32,'তোমার হাত তোমার বগলে রাখ, তা দোষমুক্ত জ্যোতির্ময় হয়ে বেরিয়ে আসবে, ভয় থেকে রক্ষার্থে তোমার হাত তোমার উপর চেপে ধর। এ দু’টি হল ফেরাউন ও তার পারিষদবর্গের জন্য তোমার প্রতিপালকের পক্ষ থেকে প্রমাণ, নিশ্চয় তারা পাপাচারী সম্প্রদায়।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3285,28,33,'মূসা বলল- ‘হে আমার প্রতিপালক! আমি তাদের একজনকে হত্যা করেছি, কাজেই আমি আশঙ্কা করছি তারা আমাকে হত্যা করবে।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3286,28,34,'আর আমার ভাই হারূন আমার চেয়ে প্রাঞ্জলভাষী, কাজেই তাকে তুমি সাহায্যকারীরূপে আমার সঙ্গে প্রেরণ কর, সে আমাকে সমর্থন জানাবে। আমি আশঙ্কা করছি তারা আমাকে মিথ্যে বলে প্রত্যাখ্যান করবে।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3287,28,35,'আল্লাহ বললেন- ‘আমি তোমার ভ্রাতার মাধ্যমে তোমার হাতকে শক্তিশালী করব এবং তোমাদেরকে প্রমাণপঞ্জি দান করব, যার ফলে তারা তোমাদের কাছে পৌঁছতেই পারবে না। আমার নিদর্শন বলে তোমরা এবং তোমাদের অনুসারীরাই বিজয়ী থাকবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3288,28,36,'মূসা যখন তাদের কাছে সুস্পষ্ট নিদর্শন নিয়ে আসল, তারা বলল- এতো অলীক যাদু মাত্র, আমাদের পূর্বপুরুষদের যামানায় এ সবের কথা তো শুনিনি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3289,28,37,'মূসা বলল- ‘আমার প্রতিপালক বেশ ভাল ক’রেই জানেন কে তাঁর নিকট থেকে পথ নির্দেশ নিয়ে এসেছে এবং কার পরিণাম আখিরাতে শুভ হবে। যালিমরা কক্ষনো সাফল্য লাভ করবে না।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3290,28,38,'ফিরআউন বলল- ‘হে পারিষদবর্গ! আমি ছাড়া তোমাদের কোন ইলাহ আছে বলে আমি জানি না। কাজেই ওহে হামান! তুমি আমার জন্য ইট পোড়াও, অতঃপর আমার জন্য একটি প্রাসাদ নির্মাণ কর যাতে আমি মূসার ইলাহকে দেখতে পারি, আমার নিশ্চিত ধারণা যে, সে একজন মিথ্যেবাদী।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3291,28,39,'ফেরাউন ও তার বাহিনী অকারণে পৃথিবীতে অহংকার করেছিল আর তারা ভেবেছিল যে, তাদেরকে আমার কাছে ফিরিয়ে আনা হবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3292,28,40,'কাজেই আমি তাকে ও তার বাহিনীকে পাকড়াও করলাম, অতঃপর তাদেরকে সমুদ্রে নিক্ষেপ করলাম। এখন দেখ, যালিমদের পরিণতি কী হয়েছিল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3293,28,41,'আমি তাদেরকে নেতা করেছিলাম। তারা জাহান্নামের দিকে আহবান করত, ক্বিয়ামতের দিন তারা সাহায্যপ্রাপ্ত হবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3294,28,42,'এ পৃথিবীতে তাদের পেছনে আমি লাগিয়ে দিয়েছি অভিসম্পাত আর ক্বিয়ামতের দিন তারা হবে দুর্দশাগ্রস্ত ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3295,28,43,'আমি পূর্ববর্তী অনেক মানব গোষ্ঠীকে ধ্বংস করার পর মূসাকে কিতাব দিয়েছিলাম- মানুষের জন্য আলোকবর্তিকা, সত্যপথের নির্দেশ ও রহমতস্বরূপ যাতে তারা উপদেশ গ্রহণ করে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3296,28,44,'মূসাকে যখন আমি নির্দেশনামা দিয়েছিলাম তখন তুমি (তূওয়া উপত্যকার) পশ্চিম প্রান্তে ছিলে না, আর ছিলে না তুমি প্রত্যক্ষদর্শী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3297,28,45,'কিন্তু আমি অনেক মানবগোষ্ঠী সৃষ্টি করেছিলাম, অতঃপর তাদের অনেক যুগ গত হয়ে গেছে। তুমি মাদইয়ানবাসীদের মধ্যে বিদ্যমান ছিলে না তাদের কাছে আমার আয়াত আবৃত্তি করার জন্য, কিন্তু (তাদের মাঝে) রসূল প্রেরণকারী আমিই ছিলাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3298,28,46,'আমি যখন (মূসাকে) ডাক দিয়েছিলাম তখন তুমি তূর পর্বতের পাশে ছিলে না। কিন্তু (তোমাকে পাঠানো হয়েছে) তোমার প্রতিপালকের রহমতস্বরূপ যাতে তুমি এমন একটি সম্প্রদায়কে সতর্ক করতে পার যাদের কাছে তোমার পূর্বে সতর্ককারী পাঠানো হয়নি, যাতে তারা উপদেশ গ্রহণ করে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3299,28,47,'রসূল না পাঠালে তাদের কৃতকর্মের কারণে কোন বিপদ হলে তারা বলত- ‘হে আমাদের প্রতিপালক! আমাদের কাছে রসূল পাঠালে না কেন, পাঠালে তোমার আয়াতসমূহের অনুসরণ করতাম আর আমরা মু’মিন হয়ে যেতাম।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3300,28,48,'অতঃপর আমার নিকট থেকে তাদের কাছে যখন সত্য আসল তখন তারা বলল- ‘মূসাকে যা দেয়া হয়েছিল তাকে কেন সেরূপ দেয়া হল না? ইতোপূর্বে মূসাকে যা দেয়া হয়েছিল তারা কি তা অস্বীকার করেনি?’ তারা বলেছিল- ‘দু’টোই যাদু, একটা আরেকটার সহায়তাকারী। আর তারা বলেছিল আমরা (তাওরাত ও কুরআন) সবই প্রত্যাখ্যান করি।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3301,28,49,'বল, তোমরা যদি সত্যবাদী হও তবে আল্লাহর নিকট হতে এমন কিতাব নিয়ে এসো যা সত্যপথ নির্দেশ করার ব্যাপারে এ দু’ (কিতাব) হতে অধিক উৎকৃষ্ট, আমি সে কিতাবের অনুসরণ করব। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3302,28,50,'অতঃপর তারা যদি তোমার কথায় সাড়া না দেয় তাহলে জেনে রেখ, তারা শুধু তাদের প্রবৃত্তির অনুসরণ করে। আল্লাহর পথ নির্দেশ ছাড়াই যে নিজের প্রবৃত্তির অনুসরণ করে, তার চেয়ে অধিক পথভ্রষ্ট আর কে আছে? আল্লাহ যালিম সম্প্রদায়কে সঠিক পথে পরিচালিত করেন না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3303,28,51,'আমি তাদের কাছে ক্রমাগত বাণী পৌঁছে দিয়েছি যাতে তারা উপদেশ গ্রহণ করে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3304,28,52,'এর পূর্বে আমি যাদেরকে কিতাব দিয়েছিলাম তারা (অর্থাৎ তাদের কতক লোক) তাতে বিশ্বাস করে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3305,28,53,'তাদের নিকট যখন তা আবৃত্তি করা হয় তখন তারা বলে- আমরা এতে বিশ্বাস করি, এ সত্য আমাদের প্রতিপালকের নিকট থেকে (আগত), এর পূর্বেই আমরা আত্মসমর্পণকারী ছিলাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3306,28,54,'তাদেরকে তাদের পারিশ্রমিক দু’বার দেয়া হবে যেহেতু তারা ধৈর্য ধারণ করেছে এবং তারা ভাল দিয়ে মন্দের প্রতিহত করে আর আমি তাদেরকে যে রিযক দিয়েছি তাত্থেকে তারা ব্যয় করে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3307,28,55,'তারা যখন নিরর্থক কথাবার্তা শুনে তখন তাত্থেকে ফিরে থাকে আর বলে- আমাদের কাজের ফল আমরা পাব, তোমাদের কাজের ফল তোমরা পাবে, তোমাদের প্রতি সালাম, অজ্ঞদের সাথে আমাদের কোন প্রয়োজন নেই। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3308,28,56,'তুমি যাকে ভালবাস তাকে সৎপথ দেখাতে পারবে না, বরং আল্লাহ্ই যাকে চান সৎ পথে পরিচালিত করেন, সৎপথপ্রাপ্তদের তিনি ভাল করেই জানেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3309,28,57,'তারা বলে- ‘আমরা যদি তোমার সাথে সৎপথের অনুসরণ করি তাহলে আমরা আমাদের দেশ থেকে উৎখাত হব।’ আমি কি তাদের জন্য এক নিরাপদ ‘হারাম’ প্রতিষ্ঠিত করিনি যেখানে সর্বপ্রকার ফলমূলের নজরানা আসে আমার পক্ষ থেকে রিযক স্বরূপ? কিন্তু তাদের অধিকাংশই তা জানে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3310,28,58,'আমি কত জনপদকে ধ্বংস করেছি যারা তাদের (ভোগবিলাসপূর্ণ) জীবনে চরম উৎকর্ষ সাধন করেছিল। এই তো তাদের বাসস্থান, তাদের পর এগুলোতে খুব অল্প লোকই বসবাস করেছে, অবশেষে আমিই মালিক রয়েছি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3311,28,59,'তোমার প্রতিপালক কোন জনপদ ধ্বংস করেন না, যতক্ষণ না তিনি তার কেন্দ্রে রসূল প্রেরণ না করেন যে তাদের কাছে আমার আয়াতসমূহ আবৃত্তি করে; আমি কোন জনপদকে ধ্বংস করি না যতক্ষণ না তার বাসিন্দারা অত্যাচারী হয়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3312,28,60,'তোমাদেরকে যে সব বস্তু দেয়া হয়েছে তা পার্থিব জীবনের ভোগ্যবস্তু ও তার শোভা মাত্র। আর আল্লাহর নিকট যা কিছু আছে তা সর্বশ্রেষ্ঠ ও চিরস্থায়ী, তবুও কি তোমরা বুঝবে না? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3313,28,61,'আমি যাকে ও‘য়াদা দিয়েছি, কল্যাণের ও‘য়াদা আর সেটা সে পাবেও, সে কি ঐ ব্যক্তির সমান যাকে আমি পার্থিব জীবনের ভোগ-সম্ভার দিয়েছি, অতঃপর ক্বিয়ামতের দিন তাকে হাজির করা হবে (অপরাধীরূপে)? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3314,28,62,'সেদিন তাদেরকে ডাক দিয়ে তিনি বলবেন- ‘যাদেরকে তোমরা আমার শরীক গণ্য করতে তারা কোথায়?’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3315,28,63,'যাদের বিরুদ্ধে অভিযোগ প্রমাণিত হবে তারা বলবে- ‘হে আমাদের পালনকর্তা! ওদেরকে আমরা বিভ্রান্ত করেছিলাম, ওদেরকে বিভ্রান্ত করেছিলাম, যেমন আমরা নিজেরা বিভ্রান্ত হয়েছিলাম, আমরা তোমার কাছে দায়মুক্ত হচ্ছি (যে আমরা জোর ক’রে তাদেরকে বিভ্রান্ত করিনি)। এরা তো আমাদের ‘ইবাদাত করত না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3316,28,64,'তাদেরকে বলা হবে, ‘তোমাদের দেবতাগুলোকে ডাক, তখন তারা তাদেরকে ডাকবে’। কিন্তু তারা তাদের ডাকে সাড়া দিবে না। তারা শাস্তি দেখতে পাবে (তাদের সামনে)। তারা যদি সৎপথপ্রাপ্ত হত! ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3317,28,65,'আর সেদিন আল্লাহ তাদেরকে ডাক দিবেন এবং বলবেন- ‘তোমরা রসূলদেরকে কী জওয়াব দিয়েছিলে?’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3318,28,66,'সে দিন তাদের কথা বন্ধ হয়ে যাবে, অতঃপর পরস্পরকে জিজ্ঞাসাবাদও করতে পারবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3319,28,67,'কিন্তু যে ব্যক্তি (তার জীবনে) তাওবাহ করেছিল আর ঈমান এনেছিল আর সৎকাজ করেছিল, আশা করা যায় সে সাফল্যমন্ডিতদের অন্তর্ভুক্ত হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3320,28,68,'তোমার প্রতিপালক যা ইচ্ছে সৃষ্টি করেন আর যাকে ইচ্ছে মনোনীত করেন। এতে তাদের কোন এখতিয়ার নেই, আল্লাহ পবিত্র, মহান। তারা যাকে শরীক করে তাত্থেকে তিনি বহু ঊর্ধ্বে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3321,28,69,'তোমার প্রতিপালক জানেন তাদের অন্তর যা গোপন করে আর যা প্রকাশ করে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3322,28,70,'আর তিনিই আল্লাহ, তিনি ছাড়া সত্যিকারের কোন ইলাহ্ নেই, সমস্ত প্রশংসা তাঁরই- প্রথমেও আর শেষেও, বিধান তাঁরই, আর তোমাদেরকে তাঁর দিকেই ফিরিয়ে নেয়া হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3323,28,71,'তোমরা কি ভেবে দেখেছ আল্লাহ যদি তোমাদের উপর রাতকে ক্বিয়ামতের দিন পর্যন্ত স্থায়ী করতেন তাহলে আল্লাহ ছাড়া কোন ইলাহ্ আছে কি যে তোমাদেরকে আলো এনে দিত? তবুও কি তোমরা কর্ণপাত করবে না? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3324,28,72,'তোমরা কি ভেবে দেখেছ আল্লাহ যদি তোমাদের উপর দিনকে ক্বিয়ামতের দিন পর্যন্ত স্থায়ী করতেন তাহলে আল্লাহ ছাড়া কোন ইলাহ্ আছে কি যে তোমাদের জন্য রাত্রি এনে দিত যাতে তোমরা আরাম করতে? তোমরা কি চিন্তা করে দেখবে না? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3325,28,73,'তিনিই স্বীয় রহমতে তোমাদের জন্য রাত ও দিন করেছেন যাতে তোমরা তাতে আরাম করতে পার আর তাঁর অনুগ্রহ সন্ধান করতে পার এবং যাতে তোমরা কৃতজ্ঞতা প্রকাশ কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3326,28,74,'সেদিন তিনি তাদেরকে ডাক দিবেন এবং বলবেন- ‘তোমরা যাদেরকে আমার শরীক মনে করতে তারা কোথায়?’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3327,28,75,'আমি প্রত্যেক সম্প্রদায় থেকে একজন সাক্ষী বের করে আনব, অতঃপর বলব- ‘তোমাদের (নির্দোষিতার পক্ষে) প্রমাণ হাজির কর। তখন তারা জানতে পারবে যে, ইলাহ হওয়ার অধিকার আল্লাহরই আর তারা যা উদ্ভাবন করত তা তাদের কাছ থেকে উধাও হয়ে যাবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3328,28,76,'কারূন ছিল মূসার সম্প্রদায়ভুক্ত, কিন্তু সে তাদের প্রতি উদ্ধত আচরণ করেছিল। তাকে আমি এমন ধনভান্ডার দিয়েছিলাম যে, তার চাবিগুলো বহন করা মাত্র একদল বলবান লোকের পক্ষে কষ্টকর ছিল। স্মরণ কর যখন তার সম্প্রদায় তাকে বলেছিল (ধনের) গর্ব করো না, আল্লাহ গর্বিতদেরকে ভালবাসেন না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3329,28,77,'আল্লাহ তোমাকে যা দিয়েছেন তা দিয়ে তুমি আখিরাতে (স্থায়ী সুখভোগের) আবাস অনুসন্ধান কর, আর দুনিয়ায় তোমার অংশের কথা ভুলে যেও না, (মানুষের) কল্যাণ সাধন কর, যেমন আল্লাহ তোমার কল্যাণ করেছেন, দেশে বিপর্যয় সৃষ্টির কামনা কর না, নিশ্চয় আল্লাহ বিপর্যয় সৃষ্টিকারীদের ভালবাসেন না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3330,28,78,'সে বলল- ‘এ সম্পদ তো আমাকে দেয়া হয়েছে যেহেতু আমার কাছে জ্ঞান আছে।’ সে কি জানে না যে, আল্লাহ তার পূর্বে অনেক মানব গোষ্ঠীকে ধ্বংস করে দিয়েছেন যারা শক্তিতে তার চেয়ে ছিল প্রবল আর জনসংখ্যায় ছিল অধিক? অপরাধীদেরকে (তাদের অপরাধ সম্পর্কে মুখে) কিছুই জিজ্ঞেস করা হবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3331,28,79,'কারূন শান-শওকাতের সাথে তার সম্প্রদায়ের সামনে হাজির হল। যারা পার্থিব জীবন কামনা করে তারা বলে উঠল- ‘হায়! কারূনকে যা দেয়া হয়েছে আমাদের জন্যও যদি তা হত! সত্যই সে মহা ভাগ্যবান ব্যক্তি।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3332,28,80,'যাদেরকে জ্ঞান দেয়া হয়েছিল তারা বলল- ‘ধিক তোমাদের প্রতি, আল্লাহর পুরস্কারই শ্রেষ্ঠতর তাদের জন্য যারা ঈমান আনে ও সৎ কাজ করে, আর সত্যপথে অবিচল ধৈর্যশীল ছাড়া অন্য কেউ তা প্রাপ্ত হয় না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3333,28,81,'অতঃপর আমি ভূগর্ভে প্রোথিত করলাম কারূনকে ও তার প্রাসাদকে। আল্লাহর ব্যতীত তাকে সাহায্য করার কোন দল ছিল না, আর সে নিজেও নিজেকে রক্ষা করতে পারল না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3334,28,82,'গতকাল যারা তার মর্যাদার ন্যায় কামনা করেছিল তারা সকলে বলতে লাগল, ‘হায়, আল্লাহ তাঁর বান্দাদের যার জন্য ইচ্ছে রিযক বর্ধিত করেন আর যার জন্য ইচ্ছে হ্রাস করেন। আল্লাহ যদি আমাদের প্রতি অনুগ্রহ না করতেন তবে তিনি আমাদেরকেও ভূগর্ভে প্রোথিত করে দিতেন। হায়! অবিশ্বাসীরা সাফল্যমন্ডিত হয় না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3335,28,83,'সেই আখিরাতের ঘর আমি তাদের জন্য করেছি যারা পৃথিবীর বুকে ঔদ্ধত্য প্রকাশ ও বিপর্যয় সৃষ্টি করতে চায় না। শুভ পরিণাম আল্লাহভীরুদের জন্য। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3336,28,84,'যে কেউ সৎকর্ম নিয়ে হাজির হবে তার জন্য আছে আরো উত্তম প্রতিদান। আর যে মন্দ কাজ নিয়ে হাজির হবে তাহলে যারা মন্দকর্ম করে তাদেরকে তাদের কাজ অনুপাতেই শাস্তি দেয়া হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3337,28,85,'যিনি তোমার প্রতি কুরআন  বিধিবদ্ধ  করেছেন  তিনি অবশ্যই  তোমাকে  মূলভূমিতে (মাক্কায়) ফিরিয়ে আনবেন। বল, আমার প্রতিপালক ভাল করেই জানেন কে সৎপথের নির্দেশ নিয়ে এসেছে আর কে আছে সুস্পষ্ট গুমরাহীতে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3338,28,86,'তুমি তো প্রত্যাশা করনি যে, তোমার প্রতি কিতাব অবতীর্ণ করা হবে। এটা তোমার প্রতিপালকের রহমত বিশেষ। কাজেই তুমি কক্ষনো কাফিরদের সমর্থনকারী হয়ো না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3339,28,87,'তোমার প্রতি আল্লাহর আয়াত নাযিল হওয়ার পর তারা যেন তোমাকে তাত্থেকে কক্ষনো বিমুখ না করতে পারে। তুমি তোমার প্রতিপালকের দিকে আহবান জানাও, আর কিছুতেই মুশরিকদের অন্তর্ভুক্ত হয়ো না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3340,28,88,'আল্লাহর সঙ্গে অন্য কোন ইলাহকে ডেকো না, তিনি ছাড়া সত্যিকারের কোন ইলাহ নেই, তাঁর (সত্তা) ছাড়া সকল কিছুই ধ্বংসশীল। বিধান তাঁরই, আর তাঁর কাছেই তোমাদেরকে ফিরিয়ে আনা হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3341,29,1,'আলিফ-লাম-মীম ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3342,29,2,'লোকেরা কি মনে করে যে ‘আমরা ঈমান এনেছি’ বললেই তাদেরকে অব্যাহতি দিয়ে দেয়া হবে, আর তাদেরকে পরীক্ষা করা হবে না? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3343,29,3,'তাদের পূর্বে যারা ছিল আমি তাদেরকে পরীক্ষা করেছিলাম; অতঃপর আল্লাহ অবশ্য অবশ্যই জেনে নেবেন কারা সত্যবাদী আর কারা মিথ্যেবাদী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3344,29,4,'যারা মন্দ কাজ করে তারা কি ভেবে নিয়েছে যে, তারা আমার আগে বেড়ে যাবে? তাদের ফয়সালা বড়ই খারাপ! ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3345,29,5,'যে ব্যক্তি আল্লাহর সাক্ষাতের আকাঙ্ক্ষা করে (সে জেনে রাখুক যে) আল্লাহর নির্ধারিত কাল অবশ্যই আসবে, তিনি সব কিছু শোনেন, সব কিছু জানেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3346,29,6,'যে লোক (আল্লাহর পথে) সর্বাত্মক প্রচেষ্টা চালায়, সে তার নিজের কল্যাণেই প্রচেষ্টা চালায়, আল্লাহ সৃষ্টিজগত থেকে অবশ্যই বে-পরওয়া। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3347,29,7,'আর যারা ঈমান আনে আর সৎ কাজ করে, আমি অবশ্য অবশ্যই তাদের মন্দ কাজগুলোকে মুছে দেব, আর তাদেরকে অবশ্য অবশ্যই প্রতিদান দেব তাদের উৎকৃষ্ট কাজগুলোর অনুপাতে যা তারা করত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3348,29,8,'পিতা-মাতার প্রতি সদ্ব্যবহার করার জন্য আমি মানুষের প্রতি ফরমান জারি করেছি। তারা যদি তোমার উপর বলপ্রয়োগ করে আমার সঙ্গে শরীক করার জন্য এমন কিছুকে যে সম্পর্কে তোমার কোন জ্ঞান নেই, তাহলে তুমি তাদেরকে মান্য কর না। আমার কাছেই তোমাদের প্রত্যাবর্তন, অতঃপর আমি তোমাদেরকে জানিয়ে দেব যা তোমরা করছিলে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3349,29,9,'যারা ঈমান আনে ও সৎকাজ করে আমি অবশ্য অবশ্যই তাদেরকে সৎকর্মশীলদের অন্তর্ভুক্ত করব। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3350,29,10,'মানুষের মধ্যে কতক আছে যারা বলে ‘আমরা আল্লাহর প্রতি ঈমান এনেছি।’ অতঃপর তাদেরকে যখন আল্লাহর পথে কষ্ট দেয়া হয়, তখন তারা মানুষের উৎপীড়নকে আল্লাহর ‘আযাবের মত মনে করে। আর যদি (তোমার কাছে) তোমার প্রতিপালকের পক্ষ থেকে সাহায্য আসে তখন তারা অবশ্য অবশ্যই বলে যে, ‘আমরা তো (সব সময়) তোমাদের সাথেই ছিলাম। সকল সৃষ্টির অন্তরে কী আছে সে সম্পর্কে আল্লাহ কি সর্বাধিক অবগত নন? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3351,29,11,'আল্লাহ অবশ্য অবশ্যই জেনে নেবেন কারা ঈমান এনেছে আর অবশ্য অবশ্যই জেনে নেবেন কারা মুনাফিক। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3352,29,12,'কাফিররা মু’মিনদেরকে বলে, ‘আমাদের পথ অনুসরণ কর, আমরা তোমাদের পাপের বোঝা বহন করব, মূলতঃ তারা তাদের পাপের কিছুই বহন করবে না, অবশ্যই তারা মিথ্যেবাদী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3353,29,13,'তারা অবশ্য অবশ্যই তাদের নিজেদের পাপের বোঝা বহন করবে, নিজেদের বোঝার সাথে আরো বোঝা, আর তারা যে সব মিথ্যে উদ্ভাবন করত সে সম্পর্কে ক্বিয়ামত দিবসে তারা অবশ্য অবশ্যই জিজ্ঞাসিত হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3354,29,14,'আমি নূহকে তার সম্প্রদায়ের কাছে পাঠিয়েছিলাম, অতঃপর সে পঞ্চাশ বছর কম হাজার বছর তাদের মাঝে অবস্থান করেছিল। অতঃপর মহাপ্লাবন তাদেরকে গ্রাস করল কারণ তারা ছিল সীমালঙ্ঘনকারী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3355,29,15,'অতঃপর আমি তাকে ও নৌকারোহীদেরকে রক্ষে করলাম আর এটাকে করলাম বিশ্বজগতের জন্য নিদর্শন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3356,29,16,'স্মরণ কর যখন ইবরাহীম তার সম্প্রদায়কে বলেছিল- ‘তোমরা আল্লাহর ‘ইবাদাত কর আর তাঁকে ভয় কর। এটাই তোমাদের জন্য কল্যাণকর, তোমরা যদি জানতে! ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3357,29,17,'তোমরা আল্লাহকে বাদ দিয়ে প্রতিমার পূজা করছ আর তোমরা মিথ্যে উদ্ভাবন করছ। আল্লাহকে বাদ দিয়ে তোমরা যে সবগুলোর পূজা করছ তারা তোমাদেরকে রিযক দানের কোন ক্ষমতা রাখে না, কাজেই তোমরা আল্লাহর নিকট রিযক তালাশ কর, আর তাঁরই ‘ইবাদাত কর, আর তাঁরই প্রতি কৃতজ্ঞতা প্রকাশ কর, তোমাদেরকে তাঁরই দিকে ফিরিয়ে নেয়া হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3358,29,18,'তোমরা যদি (রাসূলকে) মিথ্যে বলে অস্বীকার কর তবে তোমাদের পূর্বের বংশাবলীও অস্বীকার করেছিল, সুস্পষ্টভাবে প্রচার করা ব্যতীত রসূলের উপর কোন দায়িত্ব নেই। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3359,29,19,'তারা কি লক্ষ্য করে না যে, আল্লাহ কীভাবে সৃষ্টির সূচনা করেন অতঃপর তার পুনরাবর্তন ঘটান, নিশ্চয় এটা আল্লাহর জন্য সহজ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3360,29,20,'বল- ‘তোমরা পৃথিবীতে ভ্রমণ কর, অতঃপর লক্ষ্য কর কীভাবে আল্লাহ সৃষ্টির সূচনা করেছেন, অতঃপর আল্লাহ সৃষ্টি করবেন পরবর্তী সৃষ্টি, আল্লাহ সকল বিষয়ের উপর ক্ষমতাবান। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3361,29,21,'যাকে ইচ্ছে তিনি শাস্তি দেন আর যার প্রতি ইচ্ছে তিনি রহমত বর্ষণ করেন আর তাঁর কাছেই তোমাদেরকে ফিরিয়ে নেয়া হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3362,29,22,'তোমরা (আল্লাহকে) না পৃথিবীতে ব্যর্থ করতে পারবে আর না আকাশে, আল্লাহ ছাড়া তোমাদের জন্য নেই কোন অভিভাবক, নেই কোন সাহায্যকারী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3363,29,23,'যারা আল্লাহর নিদর্শনাবলীকে আর তাঁর সাক্ষাৎকে অস্বীকার করে, তারা আমার রহমত থেকে নিরাশ হবে আর তাদের জন্য আছে ভয়াবহ শাস্তি।  ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3364,29,24,'অতঃপর ইবরাহীমের সম্প্রদায়ের এ কথা বলা ছাড়া কোন জওয়াব ছিল না যে, তাকে হত্যা কর অথবা তাকে অগ্নিদগ্ধ কর।’ অতঃপর আল্লাহ তাকে অগ্নি থেকে রক্ষা করলেন। এতে বিশ্বাসী সম্প্রদায়ের জন্য অবশ্যই নিদর্শন রয়েছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3365,29,25,'ইবরাহীম বলল- তোমরা আল্লাহকে বাদ দিয়ে প্রতিমাগুলোকে (উপাস্যরূপে) গ্রহণ করেছ পার্থিব জীবনে তোমাদের পারস্পরিক ভালবাসা রক্ষার উদ্দেশ্যে। অতঃপর ক্বিয়ামতের দিন তোমাদের একে অপরকে অস্বীকার করবে আর একে অপরকে অভিশাপ দিবে, তোমাদের ঠিকানা হবে জাহান্নাম আর তোমাদের থাকবে না কোন সাহায্যকারী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3366,29,26,'অতঃপর লূত তার (অর্থাৎ ইবরাহীমের) প্রতি ঈমান এনেছিল। ইবরাহীম বলল- আমি আমার পালনকর্তার উদ্দেশ্যে দেশ ত্যাগ করছি, তিনি মহাপরাক্রান্ত, বড়ই হিকমতওয়ালা। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3367,29,27,'আমি তাকে দান করেছিলাম ইসহাক্ব ও ইয়া‘কুব, তার বংশধরদের জন্য স্থির করেছিলাম নবূওয়াত ও কিতাব, আর তাকে প্রতিদান দিয়েছিলাম দুনিয়াতে, এবং আখিরাতেও অবশ্যই সে সৎকর্মশীলদের অন্তর্ভুক্ত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3368,29,28,'স্মরণ কর লূতের কথা, যখন সে তার সম্প্রদায়কে বলেছিল- অবশ্যই তোমরা এমন এক অশ্লীল কাজ করছ যা তোমাদের পূর্বে বিশ্বজগতে কেউ করেনি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3369,29,29,'তোমরা (কাম-তাড়িত হয়ে) পুরুষদের কাছে যাও, রাহাজানি কর এবং নিজেদের মজলিসে ঘৃণ্য কর্ম কর। তার সম্প্রদায়ের এ কথা বলা ছাড়া কোন জওয়াব ছিল না যে, তুমি সত্যবাদী হলে আমাদের উপর আল্লাহর ‘আযাব নিয়ে এসো। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3370,29,30,'সে বলল- হে আমার প্রতিপালক! ফাসাদ সৃষ্টিকারী সম্প্রদায়ের বিরুদ্ধে আমাকে সাহায্য কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3371,29,31,'যখন আমার দূতগণ (অর্থাৎ ফেরেশতারা) ইব্রাহীমের কাছে সুসংবাদ নিয়ে আসল, তারা বলল- আমরা এ জনপদের বাসিন্দাদের ধ্বংস করব, এর অধিবাসীরা তো যালিম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3372,29,32,'ইবরাহীম বলল- ওখানে তো লূত আছে। তারা বলল- ওখানে কারা আছে আমরা তা ভাল করেই জানি, আমরা তাকে আর তার পরিবারবর্গকে অবশ্য অবশ্যই রক্ষা করব তার স্ত্রীকে ছাড়া, সে ধ্বংসপ্রাপ্তদের অন্তর্ভুক্ত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3373,29,33,'আমার দূতরা যখন লূতের কাছে আসল তখন সে তাদের জন্য বিষণ্ণ হয়ে পড়ল এবং (তাদের রক্ষার ব্যাপারে) নিজেকে অসহায় মনে করল। তখন তারা বলল- তোমরা ভয় কর না, দুঃখ কর না, আমরা তোমাকে আর তোমার পরিবারবর্গকে রক্ষা করব তোমার স্ত্রীকে ছাড়া, সে ধ্বংসপ্রাপ্তদের অন্তর্ভুক্ত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3374,29,34,'আমরা এ জনপদের বাসিন্দাদের উপর আসমানী শাস্তি নাযিল করব, কারণ তারা ছিল পাপাচারে লিপ্ত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3375,29,35,'এতে আমি জ্ঞানী সম্প্রদায়ের জন্য এক সুস্পষ্ট নিদর্শন রেখে দিয়েছি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3376,29,36,'মাদইয়ানের বাসিন্দাদের কাছে আমি তাদের ভাই শু‘আয়বকে পাঠিয়েছিলাম। সে বলল- হে আমার সম্প্রদায়! তোমরা আল্লাহর ‘ইবাদাত কর, শেষ দিবসকে ভয় কর, পৃথিবীতে ফাসাদ সৃষ্টি কর না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3377,29,37,'কিন্তু তারা তাকে মিথ্যে ব’লে অস্বীকার করল, অতঃপর মহা কম্পন তাদেরকে পাকড়াও করল আর তারা নিজেদের গৃহে উপুড় হয়ে শেষ হয়ে গেল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3378,29,38,'(স্মরণ কর) ‘আদ ও সামূদ (জাতির) কথা, তাদের বাড়ীঘর হতেই তাদের (করুণ পরিণতি) সম্পর্কে সুস্পষ্টরূপে তোমাদের জানা হয়ে গেছে। তাদের কাজগুলোকে শয়ত্বান তাদের দৃষ্টিতে মনোমুগ্ধকর করেছিল। যার ফলে সৎপথে চলতে তাদেরকে বাধা দিয়েছিল, যদিও তারা ছিল তীক্ষ্ণ দৃষ্টির অধিকারী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3379,29,39,'(স্মরণ কর) কারূন, ফেরাউন ও হামানের কথা। মূসা তাদের কাছে সুস্পষ্ট নিদর্শন নিয়ে এসেছিল, অতঃপর তারা পৃথিবীতে অহংকার করল, কিন্তু তারা (আমাকে পেছনে ফেলে) আগে বেড়ে যেতে পারেনি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3380,29,40,'ওদের প্রত্যেককেই আমি তার পাপের কারণে পাকড়াও করেছিলাম। তাদের কারো প্রতি আমি পাঠিয়েছিলাম পাথরসহ ঝটিকা, কারো প্রতি আঘাত হেনেছিল বজ্রের প্রচন্ড আওয়াজ, কাউকে আমি প্রোথিত করেছি ভূগর্ভে আর কাউকে দিয়েছিলাম ডুবিয়ে। তাদের প্রতি আল্লাহ কোন যুলম করেননি, তারা নিজেরাই নিজেদের প্রতি যুলম করেছিল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3381,29,41,'যারা আল্লাহকে বাদ দিয়ে অন্যদেরকে অভিভাবকরূপে গ্রহণ করেছে তাদের দৃষ্টান্ত হল মাকড়সার মত। সে ঘর বানায়, আর ঘরের মধ্যে মাকড়সার ঘরই সবচেয়ে দুর্বল; যদি তারা জানত! ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3382,29,42,'তারা আল্লাহর পরিবর্তে যা কিছুকে ডাকে আল্লাহ তা জানেন, তিনি মহাপরাক্রান্ত, মহাপ্রজ্ঞাময়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3383,29,43,'এ সব দৃষ্টান্ত আমি মানুষদের জন্য বর্ণনা করছি, কেবল জ্ঞানীরাই তা বুঝে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3384,29,44,'আসমানসমূহ আর পৃথিবীকে আল্লাহ উদ্দেশ্য সাধনের উপযোগী ক’রে সৃষ্টি করেছেন, এতে বিশ্বাসীদের জন্য অবশ্যই নিদর্শন আছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3385,29,45,'তোমার প্রতি যা ওয়াহী করা হয়েছে কিতাব থেকে তা পাঠ কর আর নামায প্রতিষ্ঠা কর; নামায অশ্লীল ও মন্দ কাজ হতে বিরত রাখে। নিশ্চয়ই আল্লাহর স্মরণই সর্বশ্রেষ্ঠ (বিষয়)। তোমরা যা কর আল্লাহ তা জানেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3386,29,46,'উত্তম পন্থা ছাড়া কিতাবধারীদের সাথে তর্ক- বিতর্ক কর না; তবে তাদের মধ্যে যারা বাড়াবাড়ি করে তারা বাদে। আর বল, আমরা ঈমান এনেছি আমাদের প্রতি যা নাযিল হয়েছে আর তোমাদের প্রতি যা নাযিল হয়েছে তার উপর; আমাদের ইলাহ ও তোমাদের ইলাহ একই, আর তাঁর কাছেই আমরা আত্মসমর্পণ করেছি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3387,29,47,'(পূর্বে যেভাবে নাযিল করেছিলাম) এভাবেই আমি তোমার প্রতি কিতাব (কুরআন) নাযিল করেছি। কাজেই আমি যাদেরকে কিতাব দিয়েছিলাম তারা তাতে বিশ্বাস করে, আর তাদেরও (অর্থাৎ মক্কার মুশরিকদেরও) কতক তাতে বিশ্বাস স্থাপন করে, অবিশ্বাসীরা ছাড়া আমার নিদর্শনাবলীকে কেউ অস্বীকার করে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3388,29,48,'তুমি তো এর পূর্বে কোন কিতাব পাঠ করনি, আর তুমি নিজ হাতে কোন কিতাব লেখনি, এমন হলে মিথ্যাবাদীরা সন্দেহ পোষণ করত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3389,29,49,'বরং যাদেরকে জ্ঞান দেয়া হয়েছে তাদের অন্তরে তা (কুরআন) এক সুস্পষ্ট নিদর্শন। অন্যায়কারীরা ছাড়া আমার নিদর্শনাবলীকে কেউ অস্বীকার করে না ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3390,29,50,'তারা বলে- তার কাছে তার প্রতিপালকের নিকট হতে কোন নিদর্শন অবতীর্ণ হয় না কেন? বল, নিদর্শন তো আছে আল্লাহর কাছে, আমি কেবল একজন সুস্পষ্ট সতর্ককারী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3391,29,51,'এটা কি তাদের জন্য যথেষ্ট (নিদর্শন) নয় যে, আমি তোমার প্রতি কিতাব নাযিল করেছি যা তাদের সম্মুখে পাঠ করা হয়, বিশ্বাসী সম্প্রদায়ের জন্য অবশ্যই এতে অনুগ্রহ ও উপদেশ রয়েছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3392,29,52,'বল, আমার আর তোমাদের মাঝে সাক্ষী হিসেবে আল্লাহ্ই যথেষ্ট, আসমানসমূহ আর যমীনে যা আছে তা তিনি জানেন। আর যারা মিথ্যায় বিশ্বাস করে এবং আল্লাহকে অস্বীকার করে তারাই হল ক্ষতিগ্রস্ত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3393,29,53,'তারা তোমাকে তাড়াতাড়ি শাস্তি আনতে বলে। (‘আযাবের) সময়কাল যদি না নির্ধারিত থাকত তবে তাদের উপর শাস্তি অবশ্যই এসে পড়ত। তা তাদের উপর অবশ্য অবশ্যই আসবে আকস্মিকভাবে, তারা (আগেভাগে) টেরও পাবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3394,29,54,'তারা তোমাকে তাড়াতাড়ি শাস্তি আনতে বলে। জাহান্নাম কাফিরদেরকে ঠিকই ঘেরাও করে ফেলবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3395,29,55,'সেদিন ‘আযাব তাদেরকে ঢেকে নেবে তাদের উপর থেকে আর তাদের পায়ের নীচে হতে। আল্লাহ বলবেন- তোমরা যা করতে তার স্বাদ ভোগ কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3396,29,56,'হে আমার বান্দারা! যারা ঈমান এনেছ, আমার যমীন প্রশস্ত, কাজেই তোমরা একমাত্র আমারই ‘ইবাদাত কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3397,29,57,'প্রতিটি প্রাণ মৃত্যুর স্বাদ গ্রহণ করবে, অতঃপর আমার কাছেই তোমাদেরকে ফিরিয়ে আনা হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3398,29,58,'যারা ঈমান আনে আর সৎ কাজ করে, আমি তাদেরকে অবশ্য অবশ্যই জান্নাতে সুউচ্চ প্রাসাদে বাসস্থান দেব যার তলদেশে নদীসমূহ প্রবাহিত, তার ভেতরে তারা চিরকাল থাকবে। সৎকর্মশীলদের প্রতিদান কতই না উত্তম! ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3399,29,59,'যারা ধৈর্যধারণ করে আর তাদের প্রতিপালকের উপর ভরসা রাখে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3400,29,60,'এমন অনেক জীবজন্তু আছে যারা নিজেদের খাদ্য মজুদ রাখে না, আল্লাহই তাদেরকে রিযক দান করেন আর তোমাদেরকেও। তিনি সব কিছু শোনেন, সব কিছু জানেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3401,29,61,'তুমি যদি তাদেরকে জিজ্ঞেস কর- কে আসমানসমূহ ও যমীন সৃষ্টি করেছেন, আর সূর্য ও চন্দ্রকে নিয়ন্ত্রণ করেছেন? তারা অবশ্য অবশ্যই বলবে- আল্লাহ। তাহলে তারা কোথায় ঘুরে বেড়াচ্ছে? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3402,29,62,'আল্লাহ্ই তাঁর বান্দাদের মধ্যে যার জন্য ইচ্ছে রিযক প্রশস্ত করেন আর যার জন্য ইচ্ছে সীমাবদ্ধ করেন। আল্লাহ সর্ব বিষয়ে সর্বাধিক অবগত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3403,29,63,'যদি তুমি তাদেরকে জিজ্ঞেস কর- আকাশ হতে কে পানি বর্ষণ ক’রে যমীনকে তার মৃত্যুর পর আবার সঞ্জীবিত করেন? তারা অবশ্য অবশ্যই বলবে- আল্লাহ। বল, সমস্ত প্রশংসা আল্লাহর জন্য। কিন্তু তাদের অধিকাংশই বুঝে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3404,29,64,'পার্থিব এ জীবন ক্রীড়া-কৌতুক ছাড়া কিছু নয়, আখিরাতের জীবনই প্রকৃত জীবন- তারা যদি জানত! ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3405,29,65,'তারা যখন নৌযানে আরোহণ করে তখন বিশুদ্ধ অন্তঃকরণে একনিষ্ঠ হয়ে তারা আল্লাহকে ডাকে। অতঃপর তিনি যখন তাদেরকে নিরাপদে স্থলে পৌঁছে দেন, তখন তারা (অন্যকে আল্লাহর) শরীক ক’রে বসে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3406,29,66,'যার ফলে তাদের প্রতি আমার দানকে তারা অস্বীকার করে আর ভোগ বিলাসে ডুবে থাকে। শীঘ্রই তারা জানতে পারবে! ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3407,29,67,'তারা কি দেখে না যে, আমি ‘হারাম’ (মক্কা)-কে করেছি নিরাপদ স্থান অথচ তাদের চতুষ্পার্শ্ব থেকে মানুষকে ছিনিয়ে নেয়া হয়। তবে তারা কি মিথ্যাতেই বিশ্বাস করবে আর আল্লাহর অনুগ্রহকে অস্বীকার করবে? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3408,29,68,'তার চেয়ে বড় যালিম আর কে আছে যে আল্লাহর সম্বন্ধে মিথ্যে রচনা করে আর প্রকৃত সত্যকে অস্বীকার করে যখন তা তাঁর নিকট থেকে আসে? কাফিরদের আবাস স্থল কি জাহান্নামের ভিতরে নয়? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3409,29,69,'আর যারা আমার পথে সর্বাত্মক প্রচেষ্টা চালায় তাদেরকে আমি অবশ্য অবশ্যই আমার পথে পরিচালিত করব। অবশ্যই আল্লাহ সৎকর্মপরায়ণদের সঙ্গে আছেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3410,30,1,'আলিম-লাম-মীম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3411,30,2,'রোমানরা পরাজিত হয়েছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3412,30,3,'নিকটস্থ ভূমিতে, কিন্তু তারা তাদের পরাজয়ের পর শীঘ্রই জয়লাভ করবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3413,30,4,'কয়েক (তিন থেকে নয়) বছরের মধ্যেই; (কোন্ কাজ হবে) আগে ও (কোন্ কাজ হবে) পরে সে ফয়সালা আল্লাহরই। সেদিন মু’মিনরা আনন্দ করবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3414,30,5,'(সে বিজয় অর্জিত হবে) আল্লাহর সাহায্যে। যাকে ইচ্ছে তিনি সাহায্য করেন, তিনি মহাপরাক্রমশালী, বড়ই দয়ালু। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3415,30,6,'এটা আল্লাহর ও‘য়াদা, আল্লাহ তাঁর ও‘য়াদার ব্যতিক্রম করেন না, কিন্তু অধিকাংশ লোকই জানে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3416,30,7,'তারা পার্থিব জীবনের বাহ্যিক দিক জানে, আর তারা পরকালের খবর রাখে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3417,30,8,'তারা কি তাদের মনে ভেবে দেখে না যে, আল্লাহ আকাশমন্ডলী, পৃথিবী ও এ দু’এর মধ্যবর্তী সবকিছু সৃষ্টি করেছেন যথাযথভাবে ও নির্দিষ্ট কালের জন্য, কিন্তু অনেক মানুষ তাদের প্রতিপালকের সাক্ষাতে নিশ্চিতই অবিশ্বাসী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3418,30,9,'তারা কি পৃথিবীতে ভ্রমণ করে না? তাহলে তারা দেখত যে, তাদের পূর্ববর্তীদের পরিণাম কী হয়েছিল। তারা শক্তিতে ছিল এদের চেয়ে অধিক প্রবল। তারা যমীন চাষ করত আর তা আবাদ করত এদের আবাদ করার চেয়ে বেশি। তাদের কাছে তাদের রসূলগণ স্পষ্ট প্রমাণ নিয়ে এসেছিল। অতঃপর আল্লাহ তাদের উপর যুলম করেননি, তারা নিজেরাই নিজেদের উপর যুলম করেছিল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3419,30,10,'অতঃপর যারা মন্দ কাজ করত, তাদের পরিণাম হয়েছিল মন্দ; কারণ তারা আল্লাহর নিদর্শনাবলীকে অস্বীকার করেছিল আর সেগুলো নিয়ে ঠাট্টা-বিদ্রূপ করত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3420,30,11,'আল্লাহ সৃষ্টির সূচনা করেন, অতঃপর তিনি তার পুনরাবৃত্তি করবেন, অতঃপর তাঁর দিকেই তোমাদেরকে ফিরিয়ে আনা হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3421,30,12,'যেদিন ক্বিয়ামত সংঘটিত হবে সেদিন অপরাধীরা হতাশ হয়ে পড়বে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3422,30,13,'তারা যাদেরকে শরীক গণ্য করত তাদের মধ্যে কেউ তাদের জন্য সুপারিশকারী সাজবে না এবং তারা তাদের শরীকদেরকে অস্বীকার করবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3423,30,14,'যেদিন ক্বিয়ামত সংঘটিত হবে সেদিন মানুষরা পৃথক হয়ে যাবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3424,30,15,'অতঃপর যারা ঈমান এনেছে ও সৎ কাজ করেছে তাদেরকে জান্নাত দিয়ে পরিতুষ্ট করা হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3425,30,16,'আর যারা কুফুরী করেছে এবং আমার নিদর্শনাবলী ও আখিরাতের সাক্ষাৎকে অস্বীকার করেছে, তাদেরকে ‘আযাবের মধ্যে উপস্থিত করা হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3426,30,17,'অতএব তোমরা আল্লাহর পবিত্রতা ও মহিমা ঘোষণা কর যখন তোমরা সন্ধ্যায় উপনীত হও আর সকালে, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3427,30,18,'এবং অপরাহ্নে ও যুহরের সময়ে; আর আসমানসমূহে ও যমীনে প্রশংসা তো একমাত্র তাঁরই। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3428,30,19,'তিনিই জীবন্তকে বের করেন মৃত থেকে আর মৃতকে বের করেন জীবন্ত থেকে। যমীনকে তিনিই পুনরায় জীবিত করেন তার মৃত্যুর পর, এভাবেই তোমাদেরকে বের করা হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3429,30,20,'তাঁর নিদর্শনের মধ্যে হল এই যে, তিনি তোমাদেরকে মাটি থেকে সৃষ্টি করেছেন, অতঃপর তোমরা এখন মানুষ, সবখানে ছড়িয়ে রয়েছ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3430,30,21,'তাঁর নিদর্শনের মধ্যে হল এই যে, তিনি তোমাদের জন্য তোমাদের মধ্য হতেই তোমাদের সঙ্গিণী সৃষ্টি করেছেন যাতে তোমরা তার কাছে শান্তি লাভ করতে পার আর তিনি তোমাদের মধ্যে পারস্পরিক ভালবাসা ও দয়া সৃষ্টি করেছেন। এর মাঝে অবশ্যই বহু নিদর্শন আছে সেই সম্প্রদায়ের জন্য যারা চিন্তা করে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3431,30,22,'তাঁর নিদর্শনের মধ্যে হল, আসমানসমূহ ও যমীনের সৃষ্টি এবং তোমাদের ভাষা ও বর্ণের বিভিন্নতা। জ্ঞানীদের জন্য অবশ্যই এতে আছে বহু নিদর্শন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3432,30,23,'তাঁর নিদর্শনের মধ্যে হল রাতে ও দিনে তোমাদের ঘুম এবং তোমাদের (দ্বারা) তাঁর অনুগ্রহ তালাশ করা। মনোযোগী লোকেদের জন্য অবশ্যই এতে আছে অনেক নিদর্শন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3433,30,24,'তাঁর নিদর্শনের মধ্যে হল এই যে, তিনি তোমাদেরকে দেখান বিদ্যুৎ ভীতি ও ভরসা সঞ্চারীরূপে, আর তিনি আকাশ হতে পানি বর্ষণ করেন যা দিয়ে যমীনকে তার মৃত্যুর পর আবার জীবিত করেন, জ্ঞানবুদ্ধিসম্পন্ন মানুষদের জন্য অবশ্যই এতে বহু নিদর্শন আছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3434,30,25,'তাঁর নিদর্শনের মধ্যে হল এই যে, আকাশ ও পৃথিবী তাঁর হুকুমেই দাঁড়িয়ে আছে। অতঃপর তিনি যখন তোমাদেরকে মাটি থেকে উঠার জন্য ডাক দেবেন একটি ডাক, তখন তোমরা উঠে আসবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3435,30,26,'আকাশমন্ডলী ও পৃথিবীতে যা আছে সব তাঁরই, সকলই তাঁর প্রতি অনুগত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3436,30,27,'তিনিই সৃষ্টির সূচনা করেন, অতঃপর তার পুনরাবৃত্তি করবেন আর তা তার জন্য খুবই সহজ। আকাশ ও পৃথিবীতে সর্বোচ্চ দৃষ্টান্ত তাঁর জন্যই, তিনিই মহাপরাক্রমশালী, বড়ই হিকমতওয়ালা। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3437,30,28,'আল্লাহ তোমাদের জন্য তোমাদের মধ্য হতেই একটা দৃষ্টান্ত পেশ করছেনঃ আমি তোমাদেরকে যে রিযক দিয়েছি তাতে তোমাদের অধিকারভুক্ত দাসদাসীরা কি অংশীদার যার ফলে তাতে তোমরা সমান? তোমরা কি তাদেরকে তেমনভাবে ভয় কর যেমন ভয় কর তোমাদের নিজেদের পরস্পরকে? এভাবে বোধশক্তিসম্পন্ন মানুষদের জন্য আমি নিদর্শনাবলী বিস্তারিতভাবে ব্যাখ্যা করি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3438,30,29,'বরং সীমালঙ্ঘনকারীরা কোন জ্ঞান ছাড়াই তাদের খেয়াল-খুশির অনুসরণ করে; কাজেই আল্লাহই যাকে পথভ্রষ্ট করেন তাকে সৎপথ দেখাবে কে? তাদের কোন সাহায্যকারী নেই। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3439,30,30,'কাজেই দ্বীনের প্রতি তোমার মুখমন্ডল নিবদ্ধ কর একনিষ্ঠভাবে। এটাই আল্লাহর প্রকৃতি, যে প্রকৃতি তিনি মানুষকে দিয়েছেন, আল্লাহর সৃষ্টি কার্যে কোন পরিবর্তন নেই, এটাই সুপ্রতিষ্ঠিত দ্বীন, কিন্তু অধিকাংশ মানুষ জানে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3440,30,31,'তাঁর অভিমুখী হও, আর তাঁকে ভয় কর, নামায প্রতিষ্ঠা কর, আর মুশরিকদের অন্তর্ভুক্ত হয়ো না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3441,30,32,'যারা নিজেদের দ্বীনকে বিভক্ত করে ফেলেছে এবং বিভিন্ন দলে ভাগ হয়ে গেছে। প্রত্যেক দল নিজেদের কাছে যা আছে তাই নিয়ে উল্লসিত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3442,30,33,'মানুষকে যখন দুঃখ-বিপদ স্পর্শ করে তখন তারা তাদের প্রতিপালককে ডাকে তাঁর অভিমুখী হয়ে। অতঃপর তিনি যখন তাদেরকে নিজ অনুগ্রহ আস্বাদন করান তখন তাদের একদল তাদের প্রতিপালকের অংশীদার সাব্যস্ত করে বসে ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3443,30,34,'আমি তাদের প্রতি যে অনুগ্রহ করেছি তা অস্বীকার করার জন্য। তাহলে ভোগ করে নাও, শীঘ্রই তোমরা জানতে পারবে! ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3444,30,35,'আমি কি তাদের কাছে এমন কোন দলীল অবতীর্ণ করেছি যা তাদেরকে সেগুলোর কথা বলে তারা যেগুলোর শরীক করে? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3445,30,36,'আমি যখন মানুষকে অনুগ্রহ আস্বাদন করাই তখন তারা তাতে উল্লসিত হয়, তারপর তাদের হাত যা আগে পাঠিয়েছে তার ফলে দুর্দশা যখন তাদেরকে পেয়ে বসে, তখন তারা হতাশ হয়ে পড়ে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3446,30,37,'তারা কি লক্ষ্য করে না যে, আল্লাহ যার জন্যে ইচ্ছে করেন রিযক প্রশস্ত করেন আর (যার জন্য ইচ্ছে) সীমাবদ্ধ করেন? বিশ্বাসী লোকেদের জন্য অবশ্যই এতে বহু নিদর্শন আছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3447,30,38,'কাজেই আত্মীয়দেরকে তাদের ন্যায্য প্রাপ্য দিয়ে দাও আর অভাবগ্রস্ত এবং মুসাফিরদেরকেও। যারা আল্লাহর চেহারা (দর্শন) কামনা করে, এটা তাদের জন্য উত্তম, আর তারাই সফলকাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3448,30,39,'মানুষের ধন বৃদ্ধির উদ্দেশ্যে তোমরা যে সুদ দিয়ে থাক, আল্লাহর দৃষ্টিতে তা ধন বৃদ্ধি করে না। কিন্তু তোমরা আল্লাহর চেহারা (সন্তুষ্টি) লাভের উদ্দেশ্যে যে যাকাত দাও (তা বৃদ্ধি পায়), তারাই দ্বিগুণ প্রতিদান লাভ করে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3449,30,40,'আল্লাহ্ই তোমাদেরকে সৃষ্টি করেছেন, অতঃপর তোমাদেরকে রিযক দিয়েছেন, অতঃপর তিনি তোমাদের মৃত্যু ঘটাবেন, অতঃপর তোমাদেরকে জীবিত করবেন। তোমরা যাদেরকে (আল্লাহর) অংশীদার মান্য কর তাদের মধ্যে কেউ আছে কি এ সবের কোন কিছু করতে পারে? তারা যাদেরকে অংশীদার গণ্য করে আল্লাহ তাদের থেকে পবিত্র, বহু ঊর্ধ্বে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3450,30,41,'মানুষের কৃতকর্মের কারণে জলে স্থলে বিপর্যয় দেখা দিয়েছে যাতে তিনি তাদেরকে তাদের কোন কোন কাজের শাস্তি আস্বাদন করান, যাতে তারা (অসৎ পথ হতে) ফিরে আসে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3451,30,42,'বল, পৃথিবীতে পরিভ্রমণ কর, অতঃপর দেখ আগে যারা ছিল তাদের পরিণাম কী হয়েছে। তাদের অধিকাংশই ছিল মুশরিক। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3452,30,43,'কাজেই তুমি নিজেকে সত্য দ্বীনে প্রতিষ্ঠিত রাখ, আল্লাহর পক্ষ থেকে এমন দিন (ক্বিয়ামত দিবস) আসার পূর্বে যা কক্ষনো প্রত্যাহার করা হবে না। সেদিন মানুষ বিভক্ত হয়ে যাবে (দু’ভাগে)। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3453,30,44,'যে কুফুরী করে সেই তার কুফুরীর শাস্তি ভোগ করবে, আর যারা সৎকর্ম করে তারা নিজেদেরই সুখ সরঞ্জাম সংগ্রহ করছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3454,30,45,'যাতে তিনি স্বীয় অনুগ্রহভান্ডার থেকে তাদেরকে পুরস্কৃত করতে পারেন যারা ঈমান আনে ও সৎ কাজ করে। তিনি কাফিরদেরকে ভালবাসেন না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3455,30,46,'তাঁর নিদর্শনের মধ্যে হল এই যে, তিনি বায়ু প্রেরণ করেন সুসংবাদ দানের জন্য ও তোমাদেরকে তাঁর অনুগ্রহ আস্বাদন করানোর জন্য। তাঁর নির্দেশে নৌযান চলে যাতে তোমরা তাঁর অনুগ্রহ অনুসন্ধান করতে পার আর তাঁর প্রতি কৃতজ্ঞতা প্রকাশ করতে পার। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3456,30,47,'তোমার পূর্বে আমি রসূলগণকে পাঠিয়েছিলাম তাদের নিজ নিজ জাতির নিকট। তারা তাদের কাছে সুস্পষ্ট নিদর্শন নিয়ে এসেছিল। অতঃপর যারা অন্যায় করেছিল আমি তাদেরকে শাস্তি দিয়েছিলাম। মু’মিনদের সাহায্য করা আমার দায়িত্ব। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3457,30,48,'আল্লাহ যিনি বায়ু প্রেরণ করেন, অতঃপর তা মেঘমালার সঞ্চার করে, অতঃপর তিনি তা আকাশে ছড়িয়ে দেন যেভাবে ইচ্ছে করেন, অতঃপর তাকে খন্ড বিখন্ড করে দেন, তারপর তুমি দেখতে পাও তার মাঝ থেকে বৃষ্টি-ফোঁটা নির্গত হচ্ছে, অতঃপর তিনি তাঁর বান্দাহদের মধ্যে যাদের নিকট তিনি ইচ্ছে করেন তাদের কাছে যখন তা পৌঁছে দেন তখন তারা হয় আনন্দিত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3458,30,49,'যদিও ইতোপূর্বে তাদের প্রতি বৃষ্টি বর্ষণের পূর্বে তারা ছিল চরমভাবে হতাশ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3459,30,50,'অতএব আল্লাহর রহমাতের ফল দেখে নাও, কীভাবে তিনি ভূমিকে তার মৃত্যুর পর জীবিত করেন। এভাবেই নিশ্চয় তিনি মৃতকে জীবিত করবেন, কেননা সব কিছুর উপর তিনি সর্বশক্তিমান। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3460,30,51,'আমি যদি এমন বায়ু প্রেরণ করি যার ফলে তারা দেখে শস্য হলদে হয়ে গেছে, তখন তারা অবশ্যই অকৃতজ্ঞ হয়ে যায়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3461,30,52,'তুমি তো মৃতকে শুনাতে পারবে না, বধিরকেও শুনাতে পারবে না আহবান, যখন তারা পেছন ফিরে চলে যায়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3462,30,53,'তুমি অন্ধদেরকেও তাদের পথভ্রষ্টতা থেকে ফিরিয়ে পথে আনতে পারবে না। যারা আমার নিদর্শনাবলীতে বিশ্বাস করে তুমি কেবল তাদেরকেই শুনাতে পারবে, কারণ তারা (আল্লাহর প্রতি) আত্মসমর্পণকারী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3463,30,54,'তিনি আল্লাহ যিনি তোমাদেরকে (অসহায়) দুর্বল অবস্থায় সৃষ্টি করেছেন, দুর্বলতার পর দিয়েছেন শক্তি, শক্তির পর আবার দিয়েছেন দুর্বলতা ও বার্ধক্য। তিনি যা ইচ্ছে করেন সৃষ্টি করেন। তিনি সর্বশ্রেষ্ঠ জ্ঞানী, সবচেয়ে শক্তিধর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3464,30,55,'যেদিন ক্বিয়ামত সংঘটিত হবে সেদিন অন্যায়কারীরা কসম করে বলবে যে, তারা মূহূর্তকালের বেশি অবস্থান করেনি। এভাবেই তারা সত্য পথ থেকে বিচ্যুত হত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3465,30,56,'আর যাদেরকে ঈমান ও জ্ঞান দেয়া হয়েছিল তারা বলবে- তোমরা আল্লাহর কিতাব মতে পুনরুত্থান দিবস পর্যন্ত অবস্থান করেছ। এটাই হল পুনরুত্থান দিবস কিন্তু তোমরা জানতে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3466,30,57,'অন্যায়কারীদের ওযর আপত্তি সেদিন কোন কাজে আসবে না এবং তাদেরকে সংশোধনেরও সুযোগ দেয়া হবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3467,30,58,'আমি মানুষদের জন্য এ কুরআনে যাবতীয় দৃষ্টান্ত বর্ণনা করেছি। তুমি যদি তাদের কাছে কোন নিদর্শন নিয়ে আস তাহলে কাফিররা অবশ্য অবশ্যই বলবে- তোমরা মিথ্যে বলা ছাড়া আর কিছুই করছ না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3468,30,59,'যাদের জ্ঞান নেই এভাবেই আল্লাহ তাদের হৃদয় মোহরাঙ্কিত করে দেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3469,30,60,'কাজেই তুমি ধৈর্য ধর, আল্লাহর ও‘য়াদা সত্য। যারা দৃঢ় বিশ্বাস রাখে না, তারা যেন তোমাকে উত্তেজিত না করতে পারে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3470,31,1,'আলিফ-লাম-মীম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3471,31,2,'এগুলো হিকমাতে ভরপুর কিতাবের আয়াত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3472,31,3,'সৎকর্মশীলদের জন্য পথ নির্দেশ ও রহমত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3473,31,4,'যারা নামায প্রতিষ্ঠা করে, যাকাত দেয় আর তারা আখেরাতের প্রতি দৃঢ় বিশ্বাস রাখে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3474,31,5,'তারাই তাদের পালনকর্তার সঠিক পথে আছে আর তারাই সফলকাম। (দুনিয়া ও আখেরাতে) ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3475,31,6,'কতক মানুষ আল্লাহর পথ থেকে বিচ্যুত করার উদ্দেশে অজ্ঞতাবশতঃ অবান্তর কথাবার্তা (গান-বাজনা) ক্রয় করে আর আল্লাহর পথকে ঠাট্টা-বিদ্রূপ করে। ওদের জন্যই আছে অবমাননাকর শাস্তি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3476,31,7,'যখন তার কাছে আমার আয়াত আবৃত্তি করা হয়, তখন সে অহংকারবশতঃ এমনভাবে মুখ ফিরিয়ে নেয় যেন সে তা শুনতেই পায়নি, যেন তার দুই কানে বধিরতা আছে, কজেই তাকে ভয়াবহ শাস্তির সুসংবাদ দাও। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3477,31,8,'যারা ঈমান আনে আর সৎকাজ করে তাদের জন্য আছে নি‘য়ামাতে ভরা জান্নাত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3478,31,9,'তাতে তারা চিরকাল থাকবে। আল্লাহর ও‘য়াদা সত্য আর তিনি মহাপরাক্রমশালী, মহাপ্রজ্ঞাময়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3479,31,10,'তিনি আকাশমন্ডলী নির্মাণ করেছেন স্তম্ভ ছাড়া যা তোমরা দেখছ। তিনি পৃথিবীতে স্থাপন করেছেন দৃঢ়ভাবে দন্ডায়মান পর্বতমালা যাতে পৃথিবী তোমাদেরকে নিয়ে নড়াচড়া না করে আর তাতে ছড়িয়ে দিয়েছেন সকল প্রকার জীবজন্তু, আর আমিই আকাশ থেকে পানি বর্ষণ করি, অতঃপর তাতে উদ্গত করি যাবতীয় কল্যাণকর উদ্ভিদ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3480,31,11,'এগুলো আল্লাহর সৃষ্টি। কাজেই আমাকে দেখাও তিনি ছাড়া অন্যেরা কী সৃষ্টি করেছে। বরং যালিমরা সুস্পষ্ট গুমরাহীতে ডুবে আছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3481,31,12,'আমি লুকমানকে প্রজ্ঞা দান করেছিলাম। (তাকে বলেছিলাম) যে, তুমি আল্লাহর কৃতজ্ঞতা প্রকাশ কর। যে কৃতজ্ঞতা প্রকাশ করে, সে তা নিজের কল্যাণেই করে। আর কেউ অকৃতজ্ঞ হলে (সে জেনে রাখুক যে) আল্লাহ অমুখাপেক্ষী, প্রশংসিত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3482,31,13,'স্মরণ কর, যখন লুকমান তার ছেলেকে নসীহত ক’রে বলেছিল- হে বৎস! আল্লাহর সাথে কোন কিছুকে শিরক কর না, শিরক হচ্ছে অবশ্যই বিরাট যুলম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3483,31,14,'আমি মানুষকে তার পিতা-মাতার প্রতি সদ্ব্যবহারের নির্দেশ দিয়েছি। তার মা তাকে কষ্টের পর কষ্ট সহ্য করে গর্ভে ধারণ করে। তার দুধ ছাড়ানো হয় দু’বছরে, (নির্দেশ দিচ্ছি) যে, আমার প্রতি ও তোমার পিতামাতার প্রতি কৃতজ্ঞ হও। (তোমাদের সকলের) প্রত্যাবর্তন তো আমারই কাছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3484,31,15,'তোমার পিতামাতা যদি তোমাকে পীড়াপীড়ি করে আমার অংশীদার স্থির করার জন্য যার জ্ঞান তোমার নেই, তবে তুমি তাদের কথা মানবে না। কিন্তু পৃথিবীতে তাদের সাথে সদ্ভাবে বসবাস করবে। যে আমার অভিমুখী হয় তার পথ অনুসরণ করবে। অতঃপর আমারই নিকট তোমাদের প্রত্যাবর্তন। তখন আমি তোমাদেরকে জানিয়ে দেব তোমরা যা করছিলে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3485,31,16,'হে বৎস! কোন বস্তু যদি সরিষার দানা পরিমাণও হয় আর তা থাকে পাথরের ভিতরে অথবা আকাশে অথবা যমীনের নীচে, আল্লাহ তাকে এনে হাজির করবেন। আল্লাহ সূক্ষ্ণদর্শী, সব কিছুর খবর রাখেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3486,31,17,'হে বৎস! তুমি নামায কায়িম কর, সৎ কাজের নির্দেশ দাও আর মন্দ কাজ হতে নিষেধ কর এবং বিপদাপদে ধৈর্যধারণ কর। নিশ্চয় এটা দৃঢ় সংকল্পের কাজ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3487,31,18,'অহংকারের বশবর্তী হয়ে তুমি মানুষকে অবজ্ঞা কর না, আর পৃথিবীতে গর্বভরে চলাফেরা কর না, নিশ্চয়ই আল্লাহ কোন দাম্ভিক অহংকারীকে পছন্দ করেন না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3488,31,19,'চলাফেরায় সংযত ভাব অবলম্বন কর এবং কণ্ঠস্বর নীচু কর। স্বরের মধ্যে নিশ্চয়ই গাধার স্বর সর্বাপেক্ষা শ্রুতিকটু। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3489,31,20,'তোমরা কি লক্ষ্য কর না যে, যা কিছু আসমানসমূহে আর যমীনে আছে, আল্লাহ সমস্তই তোমাদের কল্যাণে নিয়োজিত করেছেন এবং তোমাদের প্রতি তাঁর প্রকাশ্য ও অপ্রকাশ্য নি‘য়ামাতসমূহ পরিপূর্ণ করে দিয়েছেন? কতক মানুষ জ্ঞান ছাড়াই আল্লাহ সম্বন্ধে বাক-বিতন্ডা করে, তাদের না আছে সঠিক পথের দিশা, আর না আছে কোন আলোপ্রদ কিতাব। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3490,31,21,'তাদেরকে যখন বলা হয়- আল্লাহ যা নাযিল করেছেন তা অনুসরণ কর, তখন তারা বলে- বরং আমরা তারই অনুসরণ করব আমাদের পিতৃ-পুরুষদেরকে যে পথ অনুসরণ করতে দেখেছি। শয়ত্বান যদি তাদেরকে জ্বলন্ত আগুনের শাস্তির দিকে ডাকে, তবুও কি (তারা তারই অনুসরণ করবে)? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3491,31,22,'যে কেউ আল্লাহর প্রতি আত্মসমর্পণ করে আর সে সৎকর্মশীল, সে দৃঢ়ভাবে ধারণ করে এক মজবুত হাতল। যাবতীয় কর্ম পরিণাম ফলের জন্য আল্লাহর দিকে ফিরে যায়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3492,31,23,'কেউ কুফরী করলে তার কুফরী তোমাকে যেন মনোকষ্ট না দেয়, তাদের প্রত্যাবর্তন আমার কাছেই; অতঃপর আমি তাদেরকে জানিয়ে দেব তারা কী করত। (মানুষের) অন্তরসমূহে কী আছে সে সম্পর্কে আল্লাহই সবচেয়ে বেশি জানেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3493,31,24,'অল্প সময়ের জন্য তাদেরকে ভোগ করতে দেব, অবশেষে তাদেরকে গুরুতর শাস্তিতে (প্রবেশ করতে) বাধ্য করব। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3494,31,25,'যদি তুমি তাদেরকে জিজ্ঞেস কর- আকাশমন্ডলী ও যমীন কে সৃষ্টি করেছে, তারা অবশ্য অবশ্যই বলবে- আল্লাহ। বল, যাবতীয় প্রশংসা আল্লাহর, কিন্তু তাদের অধিকাংশই জানে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3495,31,26,'আকাশমন্ডলী আর যমীনে যা আছে সব আল্লাহরই, নিশ্চয়ই আল্লাহ, তিনি সকল অভাব-মুক্ত, সকল প্রশংসার অধিকারী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3496,31,27,'দুনিয়ার সব গাছ যদি কলম হয় আর সমুদ্র (কালি হয়) আর তার সাথে আরো সাত সমুদ্র যুক্ত হয়, তবুও আল্লাহর (প্রশংসার) কথা (লেখা) শেষ হবে না। আল্লাহ মহাপরাক্রমশালী, মহাপ্রজ্ঞার অধিকারী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3497,31,28,'তোমাদের সকলের সৃষ্টি ও পুনরুত্থান একজন মানুষের (সৃষ্টি ও পুনরুত্থানের) মতই। আল্লাহ সর্বশ্রোতা, সর্বদ্রষ্টা। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3498,31,29,'তুমি কি দেখ না যে, আল্লাহ রাত্রিকে দিনে এবং দিনকে রাত্রিতে প্রবিষ্ট করেন এবং সূর্য ও চন্দ্রকে নিয়ন্ত্রণ করেন, প্রত্যেকেই বিচরণ করছে নির্দিষ্টকৃত সময় অনুযায়ী, তোমরা যা কিছু কর আল্লাহ সে সম্পর্কে পূর্ণরূপে অবহিত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3499,31,30,'এসব প্রমাণ করে যে, আল্লাহ্ই সত্য এবং তাঁর পরিবর্তে তারা যাকে ডাকে তা মিথ্যে। আল্লাহ, তিনি তো হলেন সর্বোচ্চ, সুমহান। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3500,31,31,'তুমি কি লক্ষ্য কর না যে, নৌযানগুলো আল্লাহর অনুগ্রহে সমুদ্রে চলাচল করে যাতে তিনি তোমাদেরকে তাঁর নিদর্শনের কিছু দেখাতে পারেন। এতে অবশ্যই প্রত্যেক ধৈর্যশীল কৃতজ্ঞ ব্যক্তির জন্য নিদর্শন রয়েছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3501,31,32,'ঢেউ যখন তাদেরকে (মেঘের) ছায়ার মত ঢেকে নেয়, তখন তারা আল্লাহকে ডাকতে থাকে তাঁর প্রতি একনিষ্ঠ আনুগত্যে। অতঃপর আমি যখন তাদেরকে উদ্ধার ক’রে স্থলে এনে দেই, তখন তাদের কতক ন্যায়পূর্ণ আচরণ করে। কেবল মিথ্যাচারী অকৃতজ্ঞ ব্যক্তিই আমার নিদর্শনাবলী অস্বীকার করে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3502,31,33,'হে মানুষ! তোমাদের প্রতিপালককে ভয় কর আর ভয় কর সে দিনের, যেদিন পিতা তার সন্তানের কোন উপকার করতে পারবে না। সন্তানও পিতার কোনই উপকার করতে পারবে না। আল্লাহর ও‘য়াদা সত্য, কাজেই পার্থিব জীবন যেন কিছুতেই তোমাদেরকে ধোঁকায় ফেলতে না পারে আর প্রধান প্রতারক (শয়ত্বান) যেন কিছুতেই তোমাদেরকে আল্লাহ্ সম্পর্কে প্রতারিত না করে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3503,31,34,'কিয়ামতের জ্ঞান কেবল আল্লাহর নিকটই আছে, তিনিই বৃষ্টি বর্ষণ করেন, জরায়ুতে কী আছে তা তিনিই জানেন। কেউ জানে না আগামীকাল সে কী অর্জন করবে, কেউ জানে না কোন্ জায়গায় সে মরবে। আল্লাহ সর্বজ্ঞ, সর্বাধিক অবহিত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3504,32,1,'আলিফ-লাম-মীম, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3505,32,2,'এ কিতাব বিশ্বজগতের পালনকর্তার নিকট হতে অবতীর্ণ, এতে কোন সন্দেহ নেই। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3506,32,3,'তবে তারা কি বলে যে, সে নিজেই তা রচনা করেছে (এবং আল্লাহর নিকট থেকে আগত কিতাব ব’লে মিথ্যে দাবী করছে, না তা নয়), বরং তা তোমাদের প্রতিপালকের নিকট থেকে (আগত) সত্য যাতে তুমি এমন এক সম্প্রদায়কে সতর্ক করতে পার যাদের কাছে তোমার পূর্বে কোন সকর্তকারী আসেনি, সম্ভবতঃ তারা সঠিকপথ প্রাপ্ত হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3507,32,4,'আল্লাহ যিনি আকাশমন্ডলী ও পৃথিবী এবং এ দু’এর মাঝে যা কিছু আছে ছয় দিনে সৃষ্টি করেছেন- অতঃপর তিনি ‘আরশে সমুন্নত হন। তিনি ব্যতীত তোমাদের জন্য কোন অভিভাবক নেই, সুপারিশকারীও নেই। তবুও কি তোমরা উপদেশ গ্রহণ করবে না? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3508,32,5,'তিনি আকাশ হতে পৃথিবী পর্যন্ত কার্য পরিচালনা করেন, অতঃপর সকল বিষয়াদি তাঁরই কাছে একদিন উত্থিত হবে যার পরিমাপ তোমাদের গণনা অনুযায়ী হাজার বছর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3509,32,6,'এমনই তিনি, অদৃশ্য ও দৃশ্যমান সম্পর্কে জ্ঞাত, মহাপরাক্রমশালী, পরম দয়ালু। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3510,32,7,'যিনি সব কিছুকে উত্তমরূপে সৃষ্টি করেছেন, আর মানুষ সৃষ্টির সূচনা করেছেন মাটি থেকে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3511,32,8,'অতঃপর তিনি তার বংশধর সৃষ্টি করেন তুচ্ছ তরল পদার্থের নির্যাস থেকে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3512,32,9,'অতঃপর তিনি তাকে সামঞ্জস্যপূর্ণ করেছেন আর তার ভিতরে স্বীয় রূহ হতে ফুঁক দিয়েছেন, আর তোমাদেরকে দিয়েছেন শ্রবণেন্দ্রীয়, দর্শনেন্দ্রিয় ও অন্তঃকরণ; কৃতজ্ঞতা তোমরা সামান্যই প্রকাশ কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3513,32,10,'তারা বলে, কী! আমরা মাটিতে মিশিয়ে গেলেও কি আমাদেরকে আবার নতুন ক’রে সৃষ্টি করা হবে? বরং তারা তাদের প্রতিপালকের সাক্ষাৎকে অস্বীকার করে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3514,32,11,'বল, তোমাদের প্রাণ হরণের দায়িত্বে নিয়োজিত ফেরেশতা তোমাদের প্রাণ হরণ করবে, অতঃপর তোমাদের প্রতিপালকের নিকট তোমাদেরকে ফিরিয়ে আনা হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3515,32,12,'তুমি যদি দেখতে যখন অপরাধীরা তাদের প্রতিপালকের সামনে মাথা নীচু করে দাঁড়িয়ে থাকবে (আর বলবে), হে আমাদের প্রতিপালক! আমরা দেখলাম ও শুনলাম; কাজেই আমাদেরকে আবার পাঠিয়ে দিন, আমরা ভাল কাজ করব, আমরা (এখন) দৃঢ় বিশ্বাসী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3516,32,13,'আমি যদি ইচ্ছে করতাম তাহলে প্রত্যেক ব্যক্তিকে সৎ পথে পরিচালিত করতাম। কিন্তু আমার (এ) কথা অবশ্যই সত্য প্রতিপন্ন হবেঃ আমি নিশ্চয়ই জাহান্নামকে জ্বিন ও মানুষ মিলিয়ে পূর্ণ করব। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3517,32,14,'কাজেই (শাস্তির) স্বাদ গ্রহণ কর, কেননা এ দিনের সাক্ষাৎকে তোমরা ভুলে গিয়েছিলে, আমিও তোমাদেরকে ভুলে গেছি। তোমরা চিরস্থায়ী শাস্তি আস্বাদন করতে থাক, তোমরা যা করছিলে তার কারণে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3518,32,15,'আমার নিদর্শনাবলীতে কেবল তারাই বিশ্বাস করে যাদেরকে এর দ্বারা উপদেশ দেয়া হলে সিজদায় লুটিয়ে পড়ে আর তাদের প্রতিপালকের প্রশংসা সহকারে তাঁর পবিত্রতা ও মহিমা ঘোষণা করে আর তারা অহংকার করে না।[সাজদাহ] ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3519,32,16,'তারা তাদের (দেহের) পার্শ্বগুলো বিছানা থেকে আলাদা ক’রে (জাহান্নামের) ভীতি ও (জান্নাতের) আশা নিয়ে তাদের প্রতিপালককে ডাকে, আর আমি তাদেরকে যে রিযক দিয়েছি তাত্থেকে (আল্লাহর পথে) ব্যয় করে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3520,32,17,'কোন ব্যক্তিই (এখন) জানে না চোখ জুড়ানো কী (জিনিস) তাদের জন্য লুকিয়ে রাখা হয়েছে তাদের কাজের পুরস্কার হিসেবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3521,32,18,'তবে কি, মু’মিন ব্যক্তি পাপাচারীর ন্যায় (হতে পারে)? তারা সমান নয়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3522,32,19,'যারা ঈমান আনে আর সৎ কাজ করে তাদের বাসস্থান হবে জান্নাত, তারা যে কাজ করত তার আপ্যায়ন স্বরূপ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3523,32,20,'আর যারা পাপাচার করে তাদের বাসস্থান হবে জাহান্নাম। যখনই তারা তাত্থেকে বেরিয়ে আসতে চাইবে, তাদেরকে তাতেই ফিরিয়ে দেয়া হবে, আর তাদেরকে বলা হবে- তোমরা অগ্নির শাস্তি আস্বাদন কর যা তোমরা মিথ্যে ব’লে অস্বীকার করতে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3524,32,21,'গুরুতর শাস্তির আগে আমি তাদেরকে অবশ্য অবশ্যই লঘু শাস্তি আস্বাদন করাবো যাতে তারা (অনুশোচনা নিয়ে) ফিরে আসে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3525,32,22,'তার চেয়ে বড় যালিম আর কে আছে যাকে তার প্রতিপালকের আয়াতসমূহ দিয়ে উপদেশ দান করা হলে সে তাত্থেকে মুখ ফিরিয়ে নেয়? আমি অপরাধীদেরকে শাস্তি দেব। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3526,32,23,'আমি মূসাকে কিতাব দিয়েছিলাম, কাজেই তুমি তার (অর্থাৎ আল কুরআনের) প্রাপ্তিতে সন্দেহে পতিত হয়ো না। আমি ওটাকে বানী ইসরাঈলের জন্য পথপ্রদর্শক করেছিলাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3527,32,24,'আর আমি তাদের মধ্য হতে নেতা মনোনীত করেছিলাম যারা আমার নির্দেশ মুতাবেক সৎপথপ্রদর্শন করত যতদিন তারা ধৈর্য অবলম্বন করেছিল আর আমার আয়াতসমূহের উপর দৃঢ় বিশ্বাসী ছিল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3528,32,25,'তোমার প্রতিপালক, তিনি ক্বিয়ামতের দিন তাদের মধ্যে ফয়সালা করবেন তারা যে বিষয়ে মতভেদ করত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3529,32,26,'এটাও কি তাদেরকে সত্য পথ দেখায় না যে, আমি তাদের পূর্বে কত মানব বংশ ধ্বংস করেছি যাদের বাসভূমির উপর দিয়ে তারা (এখন) চলাফেরা করে? এতে অবশ্যই (আল্লাহর) নিদর্শন আছে, তবুও কি তারা শুনবে না? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3530,32,27,'তারা কি লক্ষ্য করে না যে, আমি উষর ভূমিতে পানি প্রবাহিত ক’রে তা দিয়ে শষ্য উদগত করি যাথেকে তাদের গবাদি পশু ও তারা নিজেরা খাদ্য গ্রহণ করে, তবুও কি তারা লক্ষ্য করবে না? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3531,32,28,'আর তারা বলেঃ তোমরা যদি সত্যবাদী হও তাহলে বল, এ ফয়সালা কখন হবে? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3532,32,29,'বল, ফয়সালার দিনে (সব কিছু দেখার পর) কাফিরদের ঈমান আনয়ন তাদের কোন উপকার দিবে না, আর তাদেরকে কোন সময়ও দেয়া হবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3533,32,30,'কাজেই তুমি তাদেরকে এড়িয়ে চল আর (আল্লাহর ফয়সালার জন্য) অপেক্ষা কর, তারাও (সেই ফয়সালার জন্য আছে) অপেক্ষমান। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3534,33,1,'হে নবী (সা)! আল্লাহকে ভয় কর, আর কাফির ও মুনাফিকদের আনুগত্য কর না, নিশ্চয় আল্লাহ সর্বজ্ঞাতা, মহাপ্রজ্ঞাময়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3535,33,2,'তোমার প্রতি তোমার প্রতিপালকের নিকট হতে যা ওয়াহী করা হয় তুমি তার অনুসরণ কর। তোমরা যা কর সে সম্পর্কে আল্লাহ পূর্ণরূপে অবহিত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3536,33,3,'আর তুমি নির্ভর কর আল্লাহর উপর, কর্ম সম্পাদনে আল্লাহই যথেষ্ট। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3537,33,4,'আল্লাহ কোন মানুষের বুকে দু’টি অন্তর সৃষ্টি করেননি। তোমাদের স্ত্রীদের মধ্যে যাদের সঙ্গে তোমরা জিহার কর, তিনি তাদেরকে তোমাদের জননী করেননি, আর তিনি তোমাদের পোষ্য পুত্রদেরকে তোমাদের পুত্র করেননি, এগুলো তোমাদের মুখের বুলি, আল্লাহই বলেন সত্য কথা, আর তিনিই দেখান (সঠিক) পথ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3538,33,5,'তাদেরকে তাদের পিতৃ-পরিচয়ে ডাক, আল্লাহর কাছে এটাই অধিক ইনসাফপূর্ণ। আর যদি তোমরা তাদের পিতৃ-পরিচয় না জান, তাহলে তারা তোমাদের দ্বীনী ভাই এবং তোমাদের বন্ধু। এ ব্যাপারে তোমাদের ত্রুটি-বিচ্যুতি হলে তোমাদের কোন গুনাহ্ নেই, কিন্তু (ধর্তব্য হল) তোমাদের অন্তরের সংকল্প। আল্লাহ অতি ক্ষমাশীল, পরম দয়ালু। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3539,33,6,'নবী (সাঃ) মু’মিনদের নিকট তাদের নিজেদের চেয়ে ঘনিষ্ঠ, আর তার স্ত্রীগণ তাদের মাতা। আল্লাহর বিধানে মু’মিন ও মুহাজিরদের (দ্বীনী সম্পর্ক) অপেক্ষা আত্মীয়-স্বজনগণ পরস্পর পরস্পরের নিকট ঘনিষ্ঠতর। তবে তোমরা তোমাদের বন্ধু বান্ধবদের প্রতি দয়া-দাক্ষিণ্য প্রদর্শন করতে চাইলে, করতে পার। (আল্লাহর) কিতাবে এটাই লিখিত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3540,33,7,'স্মরণ কর, আমি যখন নবীদের কাছ থেকে অঙ্গীকার নিয়েছিলাম আর তোমার কাছ থেকেও; আর নূহ্, ইব্রাহীম, মূসা আর মারইয়াম পুত্র ‘ঈসা থেকেও। আমি তাদের কাছ থেকে নিয়েছিলাম দৃঢ় অঙ্গীকার ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3541,33,8,'সত্যবাদীদেরকে (অর্থাৎ নবীদেরকে) তাদের সত্যবাদিতা (অর্থাৎ আল্লাহর বাণী পৌঁছে দেয়ার কাজ) সম্পর্কে জিজ্ঞেস করার জন্য। তিনি কাফিরদের জন্য প্রস্তুত করে রেখেছেন ভয়াবহ শাস্তি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3542,33,9,'হে মু’মিনগণ! তোমরা তোমাদের প্রতি আল্লাহর অনুগ্রহের কথা স্মরণ কর যখন সৈন্যবাহিনী তোমাদের বিরুদ্ধে নিকটবর্তী হয়েছিল, অতঃপর আমি তাদের বিরুদ্ধে পাঠিয়েছিলাম ঝড়ো হাওয়া এবং এক (ফেরেশতারূপী) সৈন্যবাহিনী যা তোমরা দেখনি। তোমরা যা কর আল্লাহ তা প্রত্যক্ষকারী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3543,33,10,'তারা যখন তোমাদের কাছে এসেছিল তোমাদের উপর থেকে আর তোমাদের নীচের দিক থেকে, তখন তোমাদের চক্ষু হয়েছিল বিস্ফোরিত আর প্রাণ হয়েছিল কণ্ঠাগত; আর তোমরা আল্লাহ সম্পর্কে নানা রকম (খারাপ) ধারণা পোষণ করতে শুরু করেছিলে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3544,33,11,'সে সময় মু’মিনগণকে পরীক্ষা করা হয়েছিল আর তাদেরকে ভীষণ কম্পনে প্রকম্পিত করা হয়েছিল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3545,33,12,'আর স্মরণ কর, যখন মুনাফিকরা এবং যাদের অন্তরে রোগ আছে তারা বলছিল- আল্লাহ ও তাঁর রসূল আমাদেরকে যে ওয়া‘দা দিয়েছেন তা ধোঁকা ছাড়া আর কিছুই নয়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3546,33,13,'স্মরণ কর, যখন তাদের একদল বলেছিল- হে ইয়াসরিববাসী! তোমরা (শত্রুর আক্রমণের বিরুদ্ধে) দাঁড়াতে পারবে না, কাজেই তোমরা ফিরে যাও। আর তাদের একদল এই বলে নবী (সা)র কাছে অব্যাহতি চাচ্ছিল যে, আমাদের বাড়ীঘর অরক্ষিত অথচ ওগুলো অরক্ষিত ছিল না, আসলে পালিয়ে যাওয়াই তাদের ছিল একমাত্র উদ্দেশ্য। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3547,33,14,'যদি শত্রুপক্ষ (মদীনা নগরীর) চারদিক থেকে তাদের উপর আক্রমণ করতো, অতঃপর তাদেরকে কুফুরীর আহবান করা হত, তবে তারা তাই করে বসত। তাতে তারা মোটেও বিলম্ব করত না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3548,33,15,'অথচ তারা ইতোপূর্বে আল্লাহর সাথে অঙ্গীকার করেছিল যে, তারা পৃষ্ঠপ্রদর্শন করবে না। আল্লাহর সঙ্গে কৃত ওয়া‘দা সম্পর্কে অবশ্যই জিজ্ঞেস করা হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3549,33,16,'বল, পলায়নে তোমাদের কোনই লাভ হবে না, যদি তোমরা মৃত্যু অথবা হত্যা থেকে পলায়ন কর তাহলে তোমাদেরকে সামান্যই ভোগ করতে দেয়া হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3550,33,17,'বল, তোমাদেরকে আল্লাহ (’র শাস্তি) হতে কে রক্ষে করবে তিনি যদি তোমাদের অকল্যাণ করতে চান অথবা তোমাদেরকে অনুগ্রহ করতে চান? তারা আল্লাহকে ছাড়া তাদের জন্য না পাবে কোন অভিভাবক, আর না কোন সাহায্যকারী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3551,33,18,'আল্লাহ তোমাদের মধ্যে তাদেরকে নিশ্চিতই জানেন কারা (যুদ্ধে অংশগ্রহণে) বাধা সৃষ্টিকারী আর কারা নিজেদের ভাইদেরকে বলে- আমাদের কাছে এসো। যুদ্ধ তারা সামান্যই করে  ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3552,33,19,'তোমাদের প্রতি কৃপণতার বশবর্তী হয়ে। যখন বিপদ আসে তখন তুমি তাদেরকে দেখবে মৃত্যু ভয়ে অচেতন ব্যক্তির ন্যায় চোখ উল্টিয়ে তারা তোমার দিকে তাকাচ্ছে। অতঃপর বিপদ যখন কেটে যায় তখন ধনের লালসায় তারা তোমাদেরকে তীক্ষ্ম বাক্য-বাণে বিদ্ধ করে। এরা ঈমান আনেনি। এজন্য আল্লাহ তাদের কার্যাবলী নিস্ফল করে দিয়েছেন, আর তা আল্লাহর জন্য সহজ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3553,33,20,'তারা মনে করে সম্মিলিত বাহিনী চলে যায়নি। সম্মিলিত বাহিনী যদি আবার এসে যায়, তাহলে তারা কামনা করবে যে, যদি মরুচারীদের মধ্যে থেকে তারা তোমাদের সংবাদ  নিতে পারত! তারা তোমাদের মধ্যে অবস্থান করলেও তারা যুদ্ধ সামান্যই করত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3554,33,21,'তোমাদের জন্য আল্লাহর রসূলের মধ্যে উত্তম আদর্শ রয়েছে যারা আল্লাহ ও শেষ দিনের আশা রাখে আর আল্লাহকে অধিক স্মরণ করে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3555,33,22,'মু’মিনরা যখন সম্মিলিত বাহিনীকে দেখল তখন তারা বলে উঠল- আল্লাহ ও তাঁর রসূল এরই ওয়া‘দা আমাদেরকে দিয়েছিলেন এবং আল্লাহ ও তাঁর রসূল সত্য বলেছেন। এতে তাদের ঈমান ও আনুগত্যের আগ্রহই বৃদ্ধি পেল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3556,33,23,'মু’মিনদের মধ্যে কতক লোক আল্লাহর সঙ্গে কৃত তাদের অঙ্গীকার সত্যে পরিণত করেছে। তাদের কতক উদ্দেশ্য বাস্তবায়নে (শাহাদাত বরণ) করেছে আর তাদের কতক অপেক্ষায় আছে। তারা (তাদের সংকল্প) কখনো তিল পরিমাণ পরিবর্তন করেনি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3557,33,24,'যাতে আল্লাহ (ওয়া‘দার প্রতি) সত্যবাদীদেরকে তাদের সত্যবাদিতার পুরস্কার দেন আর মুনাফিকদেরকে ইচ্ছে করলে শাস্তি দেন অথবা তাদেরকে ক্ষমা করেন। আল্লাহ পরম ক্ষমাশীল, পরম দয়ালু।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3558,33,25,'আর আল্লাহ কাফিরদেরকে তাদের রাগের অবস্থাতেই ফিরিয়ে দিলেন, তারা কোন কল্যাণ লাভ করতে পারেনি। যুদ্ধে মু’মিনদের জন্য আল্লাহই যথেষ্ট। আল্লাহ সর্বশক্তিমান, মহাপরাক্রমশালী।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3559,33,26,'আর গ্রন্থধারীদের মধ্য হতে যারা তাদেরকে (অর্থাৎ সম্মিলিত কাফির বাহিনীকে) সাহায্য করেছিল তাদেরকে তিনি তাদের দূর্গ হতে নামিয়ে দিলেন এবং তাদের অন্তরে ভীতি নিক্ষেপ করলেন। তাদের কতককে তোমরা হত্যা করলে, আর কতককে করলে বন্দী।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3560,33,27,'আর তিনি তোমাদেরকে উত্তরাধিকারী করে দিলেন তাদের ভূমির, তাদের ঘরবাড়ির আর তাদের ধন-সম্পদের; আর এমন এক ভূখন্ডের যেখানে তোমরা (এখনও) অভিযানই পরিচালনা করনি। আল্লাহ সর্ববিষয়ে ক্ষমতাবান।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3561,33,28,'হে নবী (সা.)! তুমি তোমার স্ত্রীদের বলে দাও- তোমরা যদি পার্থিব জীবন আর তার শোভাসৌন্দর্য কামনা কর, তাহলে এসো, তোমাদেরকে ভোগসামগ্রী দিয়ে দেই এবং উত্তম পন্থায় তোমাদেরকে বিদায় দেই। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3562,33,29,'আর তোমরা যদি আল্লাহ, তাঁর রসূল ও পরকালের গৃহ কামনা কর, তবে তোমাদের মধ্যে যারা সৎকর্মশীল তাদের জন্য আল্লাহ মহা পুরস্কার প্রস্তুত করে রেখেছেন।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3563,33,30,'হে নবী পত্নীগণ! তোমাদের মধ্যে কেউ প্রকাশ্যে অশ্লীল কাজ করলে তাকে দ্বিগুণ শাস্তি দেয়া হবে। আল্লাহর জন্য এটা সহজ।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3564,33,31,'তোমাদের মধ্যে যে কেউ আল্লাহ ও তাঁর রসূলের অনুগত হবে আর সৎ কাজ করবে, আমি তাকে দু’বার পুরস্কার দেব আর তার জন্য আমি সম্মানজনক রিযক প্রস্তুত রেখেছি।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3565,33,32,'হে নবী পত্নীগণ! তোমরা অন্য নারীদের মত নও। তোমরা যদি আল্লাহকে ভয় কর, তাহলে পর পুরুষের সঙ্গে আকর্ষণীয় ভঙ্গিতে কথা বলো না, যাতে যার অন্তরে ব্যাধি আছে সে প্রলুদ্ধ হয়। তোমরা সঙ্গত কথা বলবে।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3566,33,33,'আর তোমরা নিজেদের গৃহে অবস্থান কর, প্রাচীন অজ্ঞতার যুগের মত চোখ ঝলসানো প্রদর্শনী করে বেড়িও না। আর তোমরা নামায প্রতিষ্ঠা কর, যাকাত প্রদান কর এবং আল্লাহ ও তাঁর রসূলের আনুগত্য কর। হে নবীর পরিবার! আল্লাহ কেবল চান তোমাদের থেকে অপবিত্রতা দূর করতে এবং তোমাদেরকে পবিত্র ও নিষ্কলংক করতে।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3567,33,34,'তোমাদের গৃহে আল্লাহর আয়াত ও হিকমত থেকে যা পাঠ করা হয় তার চর্চা কর। আল্লাহ অতি সূক্ষ্ণদর্শী, সর্ববিষয়ে খবর রাখেন।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3568,33,35,'মুসলিম পুরুষ ও মুসলিম নারী, মু’মিন পুরুষ ও মু’মিন নারী, অনুগত পুরুষ ও অনুগত নারী, সত্যনিষ্ঠ পুরুষ ও সত্যনিষ্ঠ নারী, ধৈর্যশীল পুরুষ ও ধৈর্যশীল নারী, বিনয়ী পুরুষ ও বিনয়ী নারী, দানশীল পুরুষ ও দানশীল নারী, রোযাপালনকারী পুরুষ ও রোযাপালনারী নারী, যৌনাঙ্গের সুরক্ষাকারী পুরুষ ও যৌনাঙ্গের সুরক্ষাকারী নারী, আল্লাহকে অধিক স্মরণকারী পুরুষ ও আল্লাহকে অধিক স্মরণকারী নারী- আল্লাহ তাদের জন্য প্রস্তুত রেখেছেন ক্ষমা ও মহাপ্রতিদান।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3569,33,36,'আল্লাহ ও তাঁর রসূল কোন নির্দেশ দিলে কোন মু’মিন পুরুষ ও মু’মিন নারী উক্ত নির্দেশের ভিন্নতা করার কোন অধিকার রাখে না। যে আল্লাহ ও তাঁর রসূলকে অমান্য করে সে স্পষ্টতই সত্য পথ হতে দুরে সরে পড়ল।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3570,33,37,'স্মরণ কর, আল্লাহ যাকে অনুগ্রহ করেছেন আর তুমিও যাকে অনুগ্রহ করেছ তাকে তুমি যখন বলছিলে- তুমি তোমার স্ত্রীকে (বিবাহবন্ধনে) রেখে দাও এবং আল্লাহকে ভয় কর। তুমি তোমার অন্তরে লুকিয়ে রাখছিলে যা আল্লাহ প্রকাশ করতে চান, তুমি লোকদেরকে ভয় করছিলে, অথচ আল্লাহই সবচেয়ে বেশি এ অধিকার রাখেন যে, তুমি তাঁকে ভয় করবে। অতঃপর যায়্দ যখন তার (যায়নাবের) সঙ্গে সম্পর্ক ছিন্ন করল তখন আমি তাকে তোমার সঙ্গে বিবাহবন্ধনে আবদ্ধ করে দিলাম যাতে মু’মিনদের পোষ্যপুত্ররা তাদের স্ত্রীদের সঙ্গে সম্পর্ক ছিন্ন করলে সেসব নারীকে বিবাহ করার ব্যাপারে মু’মিনদের কোন বিঘ্ন না হয়। আল্লাহর আদেশ কার্যকরী হবেই।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3571,33,38,'আল্লাহ নবীর জন্য যা বিধিবদ্ধ করেছেন তার জন্য তা করতে কোন বাধা নেই। পূর্বে যারা অতীত হয়ে গেছে তাদের ক্ষেত্রেও এটাই ছিল আল্লাহর বিধান। আল্লাহর বিধান সুনির্ধারণে নির্ধারিত।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3572,33,39,'তারা (নবীগণ) আল্লাহর বাণী প্রচার করত আর তাঁকে ভয় করত। আল্লাহ ছাড়া কাউকে তারা ভয় করত না। (মানুষদের) হিসাব গ্রহণে আল্লাহই যথেষ্ট।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3573,33,40,'তারা (নবীগণ) আল্লাহর বাণী প্রচার করত আর তাঁকে ভয় করত। আল্লাহ ছাড়া কাউকে তারা ভয় করত না। (মানুষদের) হিসাব গ্রহণে আল্লাহই যথেষ্ট। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3574,33,41,'হে ঈমানদারগণ! তোমরা আল্লাহকে বেশি বেশি স্মরণ কর।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3575,33,42,'আর সকাল-সন্ধ্যায় তাঁর পবিত্রতা ও মহিমা ঘোষণা কর।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3576,33,43,'তিনি তোমাদের প্রতি তাঁর অনুগ্রহ বর্ষণ করেন, আর তাঁর ফেরেশতারাও (তোমাদের জন্য) তাঁর অনুগ্রহ প্রার্থনা করে তোমাদেরকে অন্ধকার থেকে বের করে আলোতে আনার জন্যে। মু’মিনদের প্রতি তিনি পরম দয়ালু।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3577,33,44,'যেদিন তারা আল্লাহর সাথে সাক্ষাৎ করবে সেদিন তাদের অভিবাদন হবে ‘সালাম’। তিনি তাদের জন্য প্রস্তুত রেখেছেন সম্মানজনক প্রতিদান।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3578,33,45,'হে নবী (সা.)! আমি তোমাকে পাঠিয়েছি (যুগে যুগে প্রেরিত নবী রসূলগণ যে তাঁদের উম্মাতের কাছে আল্লাহর বাণী পৌঁছে দিয়েছেন- এ কথার) সাক্ষীস্বরূপ এবং সুসংবাদদাতা ও সতর্ককারীরূপে,');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3579,33,46,'আর আল্লাহর অনুমতিক্রমে তাঁর পথে আহবানকারী ও আলোকপ্রদ প্রদীপরূপে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3580,33,47,'তুমি মু’মিনদেরকে সুসংবাদ দাও যে, তাদের জন্য আল্লাহর নিকট আছে বিশাল অনুগ্রহ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3581,33,48,'তুমি কাফির ও মুনাফিকদের কথা মান্য করো না। তাদের যন্ত্রণাকে মনে স্থান দিও না, আর নির্ভর কর আল্লাহর উপর, কার্যনির্বাহীরূপে আল্লাহ্ই যথেষ্ট। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3582,33,49,'হে মু’মিনগণ! তোমরা যখন কোন মু’মিন নারীকে বিবাহ কর, অতঃপর তাদেরকে স্পর্শ করার পূর্বেই তাদেরকে তালাক দাও, তখন তাদের জন্য তোমাদেরকে কোন ইদ্দত পালন করতে হবে না যা তোমরা (অন্যক্ষেত্রের তালাকে) গণনা করে থাক। কাজেই কিছু সামগ্রী তাদেরকে দাও আর তাদেরকে বিদায় দাও উত্তম বিদায়ে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3583,33,50,'হে নবী (সা.)! আমি তোমার জন্য বৈধ করেছি তোমার স্ত্রীগণকে যাদের মোহরানা তুমি প্রদান করেছ; আর বৈধ করেছি আল্লাহ ফায় (বিনা যুদ্ধে লব্ধ) হিসেবে তোমাকে যা দান করেছেন তার মধ্য হতে যারা তোমার মালিকানাধীন হয়েছে তাদেরকে, আর তোমার চাচার কন্যা ও ফুফুর কন্যাকে, তোমার মামার কন্যা ও তোমার খালার কন্যাকে যারা তোমার সঙ্গে হিজরাত করেছে। আর কোন মু’মিন নারী যদি নবীর নিকট নিজেকে নিবেদন করে আর নবী যদি তাকে বিয়ে করতে চায় সেও বৈধ, এটা মু’মিনদের বাদ দিয়ে বিশেষভাবে তোমার জন্য যাতে তোমার কোন অসুবিধে না হয়। মু’মিনগণের জন্য তাদের স্ত্রী ও দাসীদের ব্যাপারে যা নির্ধারিত করেছি আমার তা জানা আছে। আল্লাহ অতি ক্ষমাশীল, পরম দয়ালু। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3584,33,51,'তুমি তাদের যাকে ইচ্ছে সরিয়ে রাখতে পার, আর যাকে ইচ্ছে তোমার কাছে আশ্রয় দিতে পার। আর তুমি যাকে আলাদা ক’রে রেখেছ তাকে কামনা করলে তোমার কোন অপরাধ নেই। এতে অধিক সম্ভাবনা আছে যে, তাদের চক্ষু শীতল থাকবে, তারা দুঃখ পাবে না, আর তুমি তাদের সকলকে যা দাও তাতে তারা সন্তুষ্ট থাকবে। তোমাদের অন্তরে যা আছে আল্লাহ তা জানেন। আল্লাহ সর্বজ্ঞ, সহনশীল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3585,33,52,'অতঃপর আর কোন নারী তোমার জন্য বৈধ নয়। আর তাদের পরিবর্তে অন্য স্ত্রী গ্রহণ করাও হালাল নয় যদিও তাদের সৌন্দর্য তোমাকে চমৎকৃত করে। তবে তোমার অধিকারভুক্ত দাসীদের ব্যাপারে এই নিয়ম প্রযোজ্য নয়। আল্লাহ সব বিষয়ের উপর দৃষ্টি রাখেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3586,33,53,'তোমরা যারা ঈমান এনেছ শোন! নবীগৃহে প্রবেশ কর না যতক্ষণ না তোমাদেরকে অনুমতি দেয়া হয় খাদ্য গ্রহণের জন্য, (আগেভাগেই এসে পড় না) খাদ্য প্রস্তুতির জন্য অপেক্ষা করে যেন বসে থাকতে না হয়। তবে তোমাদেরকে ডাকা হলে তোমরা প্রবেশ কর। অতঃপর তোমাদের খাওয়া হলে তোমরা চলে যাও। কথাবার্তায় মশগুল হয়ে যেয়ো না। তোমাদের এ কাজ নবীকে কষ্ট দেয়। সে তোমাদেরকে (উঠে যাওয়ার জন্য বলতে) লজ্জাবোধ করে, আল্লাহ সত্য কথা বলতে লজ্জাবোধ করেন না। তোমরা যখন তার স্ত্রীগণের নিকট কোন কিছু চাও, তখন পর্দার আড়াল হতে তাদের কাছে চাও। এটাই তোমাদের ও তাদের অন্তরের জন্য পবিত্রতর। তোমাদের জন্য আল্লাহর রসূলকে কষ্ট দেয়া সঙ্গত নয়। আর তার মৃত্যুর পর তার স্ত্রীগণকে বিয়ে করাও তোমাদের জন্য কক্ষনো বৈধ নয়। আল্লাহর দৃষ্টিতে এটা মহা অপরাধ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3587,33,54,'তোমরা কোন বিষয় প্রকাশ কর অথবা তা গোপন কর- আল্লাহ তো সব বিষয়ে সর্বজ্ঞ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3588,33,55,'কোন অপরাধ নেই (যদি নবীর স্ত্রীগণ সামনে যায়) তাদের পিতৃদের, তাদের পুত্রদের, তাদের ভ্রাতৃদের, তাদের ভ্রাতুষ্পুত্রদের, তাদের ভগ্নীপুত্রদের, তাদের নারীদের ও তাদের অধিকারভুক্ত দাস-দাসীদের। আর (হে নবীর স্ত্রীগণ!) তোমরা আল্লাহকে ভয় কর, আল্লাহ সকল জিনিসেরই প্রত্যক্ষদর্শী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3589,33,56,'আল্লাহ নবীর প্রতি অনুগ্রহ করেন, তাঁর ফেরেশতাগণ নবীর জন্য অনুগ্রহ প্রার্থনা করে। হে মু’মিনগণ! তোমরাও নবীর জন্য অনুগ্রহ প্রার্থনা কর এবং যথাযথ শ্রদ্ধাভরে সালাম জানাও। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3590,33,57,'যারা আল্লাহ ও তাঁর রসূলকে কষ্ট দেয়, আল্লাহ তাদেরকে দুনিয়া ও আখিরাতে অভিশপ্ত করেছেন আর তাদের জন্য প্রস্তুত রেখেছেন অপমানজনক শাস্তি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3591,33,58,'আর যারা মু’মিন পুরুষ ও মু’মিন নারীদেরকে কষ্ট দেয় তাদের কোন অপরাধ ছাড়াই, তারা অপবাদের ও সুস্পষ্ট পাপের বোঝা বহন করে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3592,33,59,'হে নবী! তুমি তোমার স্ত্রীদেরকে, তোমার কন্যাদেরকে আর মু’মিনদের নারীদেরকে বলে দাও- তারা যেন তাদের চাদরের কিছু অংশ নিজেদের উপর টেনে দেয় (যখন তারা বাড়ীর বাইরে যায়), এতে তাদেরকে চেনা সহজতর হবে এবং তাদেরকে উত্যক্ত করা হবে না। আল্লাহ অতি ক্ষমাশীল, পরম দয়ালু। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3593,33,60,'মুনাফিকরা আর যাদের অন্তরে রোগ আছে তারা আর শহরে গুজব রটনাকারীরা যদি বিরত না হয়, তবে আমি অবশ্য অবশ্যই তাদের বিরুদ্ধে তোমাকে তেজোদীপ্ত করব, অতঃপর তারা তোমার প্রতিবেশী হিসেবে এ শহরে থাকতে পারবে না। তবে অল্পদিনের জন্য থাকবে ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3594,33,61,'অভিশপ্ত অবস্থায়; তাদেরকে যেখানেই পাওয়া যাবে ধরা হবে এবং হত্যা করার মতই হত্যা করা হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3595,33,62,'যারা অতীত হয়ে গেছে তাদের ক্ষেত্রে এটাই ছিল আল্লাহর বিধান, তুমি আল্লাহর বিধানে কক্ষনো হেরফের পাবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3596,33,63,'লোকে তোমাকে ক্বিয়ামত সম্পর্কে প্রশ্ন করে। বল, তার জ্ঞান কেবল আল্লাহর নিকটই আছে। কিসে তোমাকে জানাবে- সম্ভবতঃ ক্বিয়ামত নিকটেই। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3597,33,64,'আল্লাহ কাফিরদেরকে অভিশাপ দিয়েছেন আর তাদের জন্য জ্বলন্ত অগ্নি প্রস্তুত রেখেছেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3598,33,65,'তাতে তারা চিরকাল থাকবে, তারা না পাবে কোন অভিভাবক, আর না পাবে কোন সাহায্যকারী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3599,33,66,'আগুনে যেদিন তাদের মুখ উপুড় করে দেয়া হবে সেদিন তারা বলবে- হায়! আমরা যদি আল্লাহকে মানতাম ও রসূলকে মানতাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3600,33,67,'আর তারা বলবে- হে আমাদের পালনকর্তা! আমরা আমাদের নেতাদেরকে ও আমাদের প্রধানদেরকে মান্য করতাম। তারাই আমাদেরকে গুমরাহ করেছিল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3601,33,68,'হে আমাদের পালনকর্তা! তাদেরকে দ্বিগুণ শাস্তি দাও আর তাদেরকে মহা অভিশাপে অভিশাপ দাও। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3602,33,69,'হে মু’মিনগণ! তোমরা তাদের মত হয়ো না যারা মূসাকে কষ্ট দিয়েছিল। অতঃপর তারা যা বলেছিল আল্লাহ তাত্থেকে তাকে নির্দোষ প্রমাণিত করেন। সে ছিল আল্লাহর নিকট সম্মানিত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3603,33,70,'হে মু’মিনগণ! তোমরা আল্লাহকে ভয় কর এবং সরল সঠিক কথা বল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3604,33,71,'আল্লাহ তোমাদের জন্য তোমাদের আমলগুলোকে ত্রুটিমুক্ত করবেন আর তোমাদের পাপগুলোকে ক্ষমা করে দিবেন। যে ব্যক্তি আল্লাহ ও তাঁর রসূলের আনুগত্য করে সে সাফল্য লাভ করে- মহাসাফল্য। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3605,33,72,'আমি আসমান, যমীন ও পর্বতের প্রতি (ইসলামের বোঝা বহন করার) আমানাত পেশ করেছিলাম। কিন্তু তারা তা বহন করতে অস্বীকৃতি জানাল, তারা তাতে আশংকিত হল, কিন্তু মানুষ সে দায়িত্ব নিল। সে বড়ই অন্যায়কারী, বড়ই অজ্ঞ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3606,33,73,'পরিণামে আল্লাহ মুনাফিক পুরুষ ও মুনাফিক নারী এবং মুশরিক পুরুষ ও মুশরিক নারীকে শাস্তি দিবেন এবং মু’মিন পুরুষ ও মু’মিন নারীকে ক্ষমা প্রদর্শন করবেন। আল্লাহ অতি ক্ষমাশীল, পরম দয়ালু। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3607,34,1,'যাবতীয় প্রশংসা আল্লাহর যিনি আকাশ ও পৃথিবীতে যা আছে সব কিছুর মালিক। আখিরাতেও প্রশংসা তাঁরই; তিনি মহা প্রজ্ঞাশীল, সকল বিষয়ে অবহিত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3608,34,2,'ভূমিতে যা প্রবেশ করে আর তাত্থেকে যা বের হয়, আর আকাশ হতে যা অবতীর্ণ হয় আর তাতে যা উত্থিত হয় তা তিনি জানেন। তিনি পরম দয়ালু, পরম ক্ষমাশীল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3609,34,3,'কাফিরগণ বলে- ক্বিয়ামত আমাদের নিকট আসবে না। বল, না, আমার প্রতিপালকের শপথ! তোমাদের নিকট তা অবশ্য অবশ্যই আসবে। তিনি যাবতীয় অদৃশ্যের জ্ঞানী। তাঁর থেকে লুক্কায়িত নেই আকাশ ও পৃথিবীতে ক্ষুদ্রাতিক্ষুদ্র অনুকণা, না তার থেকে ছোট আর না তার থেকে বড় (কোনটাই নেই লুক্কায়িত)। সবই আছে (লাওহে মাহফুয নামক) এক সুস্পষ্ট কিতাবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3610,34,4,'যাতে তিনি প্রতিদান দিতে পারেন তাদেরকে যারা ঈমান আনে ও সৎকাজ করে, এদেরই জন্য আছে ক্ষমা ও সম্মানজনক রিযক। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3611,34,5,'যারা আমার আয়াতকে ব্যর্থ করার চেষ্টা চালায় তাদেরই জন্য রয়েছে যন্ত্রণাদায়ক ভয়াবহ শাস্তি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3612,34,6,'যাদেরকে জ্ঞান দেয়া হয়েছে তারা তোমার কাছে তোমার প্রতিপালকের পক্ষ থেকে যা অবতীর্ণ হয়েছে তাকে সত্য বলে জানে এবং (তারা আরো জানে যে) তা মহাপরাক্রমশালী ও প্রশংসিত (আল্লাহ)’র পথে পরিচালিত করে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3613,34,7,'কাফিরগণ বলে- তোমাদেরকে কি আমরা এমন একজন লোকের সন্ধান দেব যে তোমাদেরকে খবর দেয় যে, তোমরা ছিন্ন ভিন্ন হয়ে গেলেও তোমাদেরকে নতুনভাবে সৃষ্টি করা হবে? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3614,34,8,'সে আল্লাহ সম্পর্কে মিথ্যে বলে, না হয় সে পাগল। বস্তুতঃ যারা আখিরাতে বিশ্বাস করে না তারাই শাস্তি এবং সুদূর গুমরাহীতে পড়ে আছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3615,34,9,'তারা কি আকাশ ও পৃথিবীতে তাদের সামনে ও পেছনে লক্ষ্য করে না? আমি ইচ্ছে করলে তাদেরকেসহ ভূমি ধ্বসিয়ে দেব অথবা আকাশের এক টুকরো তাদের উপর ফেলে দেব। প্রতিটি আল্লাহ অভিমুখী বান্দাহর জন্য এতে নিদর্শন রয়েছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3616,34,10,'আমি আমার পক্ষ থেকে দাঊদের প্রতি অনুগ্রহ করেছিলাম। (আমি আদেশ করেছিলাম) হে পর্বতমালা! তোমরা দাঊদের সঙ্গে আমার পবিত্রতা ঘোষণা কর আর পাখীদেরকেও (এ আদেশ করেছিলাম)। আমি লোহাকে তার জন্য নরম করেছিলাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3617,34,11,'যাতে তুমি পূর্ণ মাপের বর্ম তৈরি করতে পার, কড়াসমূহ সঠিকভাবে সংযুক্ত কর আর তোমরা সৎকর্ম কর। তোমরা যা কর আমি তার প্রত্যক্ষদর্শী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3618,34,12,'আর সুলাইমানের জন্য (আমি তার অধীন করে দিয়েছিলাম) বাতাসকে। তার সকালের অতিক্রমণ ছিল এক মাসের পথ আর সন্ধ্যার অতিক্রমণ ছিল এক মাসের পথ। আমি তার জন্য গলিত তামার ঝরণা প্রবাহিত করেছিলাম। কতক জ্বিন তার সম্মুখে কাজ করত তার পালনকর্তার অনুমতিক্রমে। তাদের যে কেউ আমার নির্দেশ অমান্য করে, তাকে আমি জ্বলন্ত আগুনের শাস্তি আস্বাদন করাব। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3619,34,13,'তারা সুলাইমানের ইচ্ছে অনুযায়ী তার জন্য প্রাসাদ, ভাস্কর্য, হাউযের ন্যায় বৃহদাকার পাত্র এবং চুল্লির উপর স্থাপিত বিশালাকায় ডেগ নির্মাণ করত। (আমি বলেছিলাম) হে দাঊদের সন্তানগণ! তোমরা কৃতজ্ঞচিত্তে কাজ করে যাও। আমার বান্দাদের অল্পই কৃতজ্ঞ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3620,34,14,'অতঃপর আমি যখন সুলাইমানের মৃত্যু ঘটালাম, তখন ঘুণে পোকাই জ্বিনদেরকে তার মৃত্যু সম্পর্কে অবহিত করল, তারা (ধীরে ধীরে) সুলাইমানের লাঠি খেয়ে যাচ্ছিল। যখন সে পড়ে গেল তখন জ্বিনেরা বুঝতে পারল যে, তারা (নিজেরা) যদি অদৃশ্য বিষয় সম্পর্কে অবগত থাকত তাহলে তাদেরকে লাঞ্ছনাদায়ক শাস্তির মধ্যে পড়ে থাকতে হত না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3621,34,15,'সাবার অধিবাসীদের জন্য তাদের বাসভূমিতে একটা নিদর্শন ছিল- দু’টো বাগান; একটা ডানে, একটা বামে। (তাদেরকে বলেছিলাম) তোমাদের প্রতিপালক প্রদত্ত রিযক ভোগ কর আর তাঁর প্রতি কৃতজ্ঞতা প্রকাশ কর। সুখ-শান্তির শহর আর ক্ষমাশীল পালনকর্তা। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3622,34,16,'কিন্তু তারা (আল্লাহ হতে) মুখ ফিরিয়ে নিল। কাজেই আমি তাদের বিরুদ্ধে পাঠালাম বাঁধ-ভাঙ্গা বন্যা, আর আমি তাদের বাগান দু’টিকে পরিবর্তিত করে দিলাম এমন দু’টি বাগানে যাতে জন্মিত বিস্বাদ ফল, ঝাউগাছ আর কিছু কুল গাছ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3623,34,17,'অকৃতজ্ঞতাভরে তাদের সত্য প্রত্যাখ্যান করার জন্য আমি তাদেরকে এ শাস্তি দিয়েছিলাম। আমি অকৃতজ্ঞদের ছাড়া এমন শাস্তি কাউকে দেই না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3624,34,18,'তাদের এবং যে সব জনপদের প্রতি আমি অনুগ্রহ  বর্ষণ  করেছিলাম  সেগুলোর  মাঝে  অনেক দৃশ্যমান জনপদ স্থাপন করে দিয়েছিলাম এবং ওগুলোর মাঝে সমান সমান দূরত্বে সফর মনযিল করে দিয়েছিলাম। (আর তাদেরকে বলেছিলাম) তোমরা এ সব জনপদে রাতে আর দিনে নিরাপদে ভ্রমণ কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3625,34,19,'কিন্তু তারা বলল- হে আমাদের পালনকর্তা! আমাদের সফর-মঞ্জিলগুলোর মাঝে ব্যবধান বাড়িয়ে দাও। তারা নিজেদের প্রতি যুলম করেছিল। কাজেই আমি তাদেরকে কাহিনী বানিয়ে ছাড়লাম (যে কাহিনী শোনানো হয়) আর তাদেরকে ছিন্ন ভিন্ন করে দিলাম। এতে প্রত্যেক ধৈর্যশীল কৃতজ্ঞ ব্যক্তির জন্য নিদর্শন রয়েছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3626,34,20,'তাদের ব্যাপারে ইবলীস তার ধারণা সত্য প্রমাণিত করল (যে ধারণা ইবলীস আল্লাহর নিকট ব্যক্ত করেছিল যে, অল্প সংখ্যক ব্যতীত সে মানুষদেরকে নিজের বশীভূত করে ছাড়বে)। ফলে মু’মিনদের একটি দল ছাড়া তারা সবাই তার অনুসরণ করল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3627,34,21,'তাদের উপর শয়ত্বানের কোন ক্ষমতা ছিল না, তবে কে আখিরাতে বিশ্বাস করে আর কে তাতে সন্দেহ করে, তা প্রকাশ করাই ছিল আমার উদ্দেশ্য। তোমার প্রতিপালক সকল বিষয়ে হিফাযাতকারী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3628,34,22,'বল, আল্লাহর পরিবর্তে তোমরা যাদেরকে (ইলাহ) মনে করতে তাদেরকে ডাক। তারা আসমান ও যমীনে অণু পরিমাণও কোন কিছুর মালিক নয়। এ দু’য়ে তাদের এতটুকু অংশ নেই, আর তাদের কেউ আল্লাহর সাহায্যকারীও নয়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3629,34,23,'তাঁর কাছে সুপারিশ কোন কাজে আসবে না, তবে তাদের ব্যতীত যাদেরকে তিনি অনুমতি দেবেন। অতঃপর তাদের (অর্থাৎ আল্লাহর নৈকট্যলাভকারী ফেরেশতার কিংবা অন্যের জন্য সুপারিশ করার অনুমতিপ্রাপ্তদের) অন্তর থেকে যখন ভয় দূর হবে তখন তারা পরস্পর জিজ্ঞেস করবে- তোমাদের পালনকর্তা কী নির্দেশ দিলেন? তারা বলবে- যা সত্য ও ন্যায় (তার নির্দেশই তিনি দিয়েছেন), তিনি সর্বোচ্চ, সর্বশ্রেষ্ঠ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3630,34,24,'বল- আসমান ও যমীন হতে কে তোমাদেরকে রিযক দান করেন? বল- আল্লাহ। হয় আমরা, না হয় তোমরা অবশ্যই সৎপথে পরিচালিত অথবা স্পষ্ট গুমরাহিতে পতিত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3631,34,25,'বল- আমাদের অপরাধের জন্য তোমরা জিজ্ঞাসিত হবে না, আর তোমরা যা কর তার জন্য আমাদেরকে জবাবদিহি করতে হবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3632,34,26,'বল- আমাদের পালনকর্তা আমাদেরকে একত্রিত করবেন অবশেষে তিনি আমাদের মাঝে সত্য ও ন্যায়সঙ্গতভাবে ফয়সালা করবেন। তিনি সর্বশ্রেষ্ঠ ফয়সালাকারী, সর্বজ্ঞাতা। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3633,34,27,'বল- তোমরা আমাকে (তাদেরকে) দেখাও যাদেরকে তোমরা তাঁর অংশীদার জুড়ে দিয়েছ। কক্ষনো (দেখাতে পারবে) না, বরং তিনি আল্লাহ, মহাপরাক্রমশালী, মহাপ্রজ্ঞাময়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3634,34,28,'আমি তোমাকে সমগ্র মানবমন্ডলীর জন্য সুসংবাদদাতা ও সতর্ককারীরূপে প্রেরণ করেছি, কিন্তু অধিকাংশ মানুষ জানে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3635,34,29,'তারা বলে- তুমি যদি সত্যবাদী হও তাহলে বল, (তোমার শুনানো) ও‘য়াদা কখন বাস্তবায়িত হবে? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3636,34,30,'বল- তোমাদের জন্য আছে এক নির্ধারিত দিন যা তোমরা এক মুহূর্তকালের জন্য বিলম্বিত করতে পারবে না, আর ত্বরান্বিত করতেও পারবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3637,34,31,'কাফিরগণ বলে- আমরা এ কুরআনে কক্ষনো বিশ্বাস করব না, আর তার আগের কিতাবগুলোতেও না। তুমি যদি দেখতে! যখন যালিমদেরকে তাদের প্রতিপালকের সম্মুখে দাঁড় করানো হবে, তখন তারা পরস্পর বাদানুবাদ করবে। যাদেরকে দুর্বল ক’রে রাখা হয়েছিল তারা দাম্ভিকদেরকে বলবে- তোমরা না থাকলে আমরা অবশ্যই মু’মিন হতাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3638,34,32,'যাদেরকে দুর্বল ক’রে রাখা হয়েছিল দাম্ভিকরা তাদেরকে বলবে- তোমাদের কাছে সত্য পথের দিশা আসার পর আমরা কি তোমাদেরকে তাত্থেকে বাধা দিয়েছিলাম? বরং তোমরা নিজেরাই ছিলে অপরাধী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3639,34,33,'যাদেরকে দুর্বল ক’রে রাখা হয়েছিল তারা দাম্ভিকদের বলবে- তোমরাই তো বরং দিন-রাত চক্রান্ত করতে। তোমরা আমাদেরকে নির্দেশ দিতে যাতে আমরা আল্লাহকে অস্বীকার করি আর তাঁর সমকক্ষ স্থির করি। যখন তারা শাস্তি দেখবে তখন মনের অনুতাপ মনেই লুকিয়ে রাখবে, আর আমি কাফিরদের গলায় শৃঙ্খল পরিয়ে দেব। তারা যা (দুনিয়াতে) করত তার প্রতিফলই তাদেরকে দেয়া হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3640,34,34,'যখনই কোন জনপদে আমি সতর্ককারী পাঠিয়েছি, সেখানকার বিত্তবানরা বলেছে তোমাদেরকে যা দিয়ে পাঠানো হয়েছে আমরা তা অস্বীকার করছি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3641,34,35,'তারা বলত- ধন-মাল আর বাল-বাচ্চায় আমরা বেশি, আমরা শাস্তিপ্রাপ্ত হতে পারি না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3642,34,36,'বল- আমার প্রতিপালক যার জন্য চান রিযক প্রশস্ত করেন বা সীমিত করেন, কিন্তু (এর তাৎপর্য) অধিকাংশ লোকই জানে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3643,34,37,'ওটা না তোমাদের মাল-ধন, আর না তোমাদের সন্তান-সন্ততি যা তোমাদেরকে আমার নিকটবর্তী করবে। তবে যে কেউ ঈমান আনে আর সৎকাজ করে তাদেরই জন্য আছে বহুগুণ প্রতিদান তাদের কাজের জন্য। তারা সুউচ্চ প্রাসাদে নিরাপদে থাকবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3644,34,38,'যারা আমার আয়াতসমূহকে ব্যর্থ করার জন্য চেষ্টা চালায়, তাদেরকে ‘আযাবে উপস্থিত করা  হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3645,34,39,'বল- আমার প্রতিপালকই তাঁর বান্দাহদের মধ্যে যার জন্য ইচ্ছে রিযক প্রশস্ত করেন, আর যার জন্য ইচ্ছে সীমিত করেন। তোমরা যা কিছু (সৎ কাজে) ব্যয় কর, তিনি তার বিনিময় দেবেন। তিনিই সর্বশ্রেষ্ঠ রিযকদাতা। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3646,34,40,'যেদিন তিনি তাদের সববাইকে একত্রিত করবেন, অতঃপর ফেরেশতাদেরকে বলবে-ওরা কি একমাত্র তোমাদেরই পূজা করত? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3647,34,41,'ফেরেশতারা বলবে- পবিত্র মহান তুমি, তুমিই আমাদের অভিভাবক, তারা নয়। বরং তারা জ্বিনদের পূজা করত; ওদের অধিকাংশই ওদের প্রতি বিশ্বাসী ছিল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3648,34,42,'আজ তোমাদের একে অন্যের কোন লাভ বা ক্ষতি করতে পারবে না। যারা যুলম করেছিল তাদেরকে আমি বলব- তোমরা জাহান্নামের শাস্তি আস্বাদন কর যা তোমরা মিথ্যে বলে অস্বীকার করতে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3649,34,43,'তাদের সম্মুখে যখন আমার সুস্পষ্ট আয়াতসমূহ তিলাওয়াত করা হয় তখন তারা বলে- এটা তো এমন একজন মানুষ, যে তোমাদেরকে তোমাদের পূর্বপুরুষ যার ‘ইবাদাত করত তাত্থেকে বাধা দিতে চায়। তারা আরো বলে যে, এটা মনগড়া মিথ্যে ছাড়া আর কিছুই নয়। সত্য যখন কাফিরদের নিকট আসে তখন তারা বলে- এটা তো সুস্পষ্ট যাদু। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3650,34,44,'আমি তাদেরকে (অর্থাৎ মক্কার কুরায়শদের) কোন কিতাব দেইনি যা তারা পাঠ করত আর আমি তোমার পূর্বে তাদের কাছে কোন সতর্ককারীও পাঠাইনি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3651,34,45,'তাদের পূর্ববর্তীরাও (সত্যকে) মিথ্যে বলে অস্বীকার করেছিল। আমি তাদেরকে যা দিয়েছিলাম, এরা তার এক দশমাংশও পায়নি। তবুও ওরা যখন আমার রসূলগণকে অস্বীকার করেছিল তখন কত ভয়ংকর হয়েছিল আমার শাস্তি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3652,34,46,'বল- আমি তোমাদেরকে একটি বিষয়ে নসীহত করছিঃ তোমরা আল্লাহর উদ্দেশে দু’ দু’জন বা এক একজন করে দাঁড়াও, অতঃপর চিন্তা করে দেখ, তোমাদের সঙ্গী উন্মাদ নয়। সে তো সামনের কঠিন শাস্তি সম্পর্কে একজন সতর্ককারী মাত্র। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3653,34,47,'বল- আমি তোমাদের নিকট হতে কোন পারিশ্রমিক চাই না, বরং তা তোমাদেরই জন্য। আমার পুরস্কার আছে কেবল আল্লাহর কাছে। তিনি সকল বিষয়ে প্রত্যক্ষদর্শী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3654,34,48,'বল- আমার প্রতিপালক সত্য ছুঁড়ে দেন (অসত্যকে আঘাত করার জন্যে)। (যাবতীয়) অদৃশ্য সম্পর্কে তিনি পূর্ণরূপে অবগত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3655,34,49,'বল- সত্য এসে গেছে, আর মিথ্যের নতুন করে আবির্ভাবও ঘটবে না, আর তার পুনরাবৃত্তিও হবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3656,34,50,'বল- আমি যদি গুমরাহ হই, তবে নিজের ক্ষতি করতেই গুমরাহ হব। আর আমি যদি সৎ পথে চলি, তবে আমার প্রতিপালক যে আমার প্রতি ওয়াহী করেন তার বদৌলতেই। তিনি সর্বশ্রোতা, (সদা) সন্নিকটবর্তী। (আহবানকারীর ডাকে সাড়া দেন।) ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3657,34,51,'তুমি যদি দেখতে! যখন তারা ভয়ে কম্পমান হয়ে পড়বে, কিন্তু তারা কোন অব্যাহতি পাবে না। এক্কেবারে কাছের জায়গা থেকেই তাদেরকে ধরে ফেলা হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3658,34,52,'আর তারা বলবে- (এখন) আমরা ঈমান আনলাম। কিন্তু (ঈমান যেখানে আনতে হত সে স্থান থেকে তারা তো বহু দূরে এসে পড়েছে) এত দূরের জায়গা থেকে তারা ঈমানের নাগাল পাবে কীভাবে? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3659,34,53,'তারা তো আগেই তা প্রত্যাখ্যান করেছিল। তারা অদৃশ্য বিষয় সম্পর্কে বহু দূর থেকে (আন্দাজ অনুমানে) কথা ছুঁড়ে দিত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3660,34,54,'তাদের এবং তাদের কামনা-বাসনার মাঝে রেখে দেয়া হয়েছে এক প্রাচীর। তাদের মতের ও পথের লোকেদের ক্ষেত্রে পূর্বেও এমনটিই করা হয়েছিল। তারা ছিল সংশয়পূর্ণ সন্দেহে পতিত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3661,35,1,'যাবতীয় প্রশংসা আকাশমন্ডলী ও পৃথিবীর সৃষ্টিকর্তা আল্লাহর জন্য। তিনি দূত মনোনীত করেন ফেরেশতামন্ডলীকে, যারা দুই দুই বা তিন তিন বা চার চার ডানা বিশিষ্ট। তিনি সৃষ্টিতে যা ইচ্ছে বৃদ্ধি করেন। আল্লাহ সকল বিষয়ের উপর সর্বশক্তিমান। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3662,35,2,'আল্লাহ মানুষের জন্য তাঁর অনুগ্রহ থেকে যা খুলে দেন তা নিবারণ করার কেউ নেই। আর তিনি যা বারিত করেন অতঃপর কেউ তা প্রেরণ করতে পারে না। তিনি মহাপরাক্রমশালী, মহাপ্রজ্ঞাময়।  ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3663,35,3,'হে মানুষ! তোমরা তোমাদের প্রতি আল্লাহর অনুগ্রহের কথা স্মরণ কর। তিনি ছাড়া কোন সৃষ্টিকর্তা আছে কি যে তোমাদেরকে আকাশ ও যমীন থেকে রিযক দান করে? তিনি ছাড়া সত্যিকারের কোন ইলাহ নেই, তাহলে কীভাবে তোমরা বিপথগামী হচ্ছ? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3664,35,4,'তারা যদি তোমাকে মিথ্যে ব’লে অস্বীকার করে, তাহলে (তাদের এ আচরণ তো কোন নতুন বিষয় নয়) তোমার পূর্বেও রসূলদেরকে মিথ্যে ব’লে অস্বীকার করা হয়েছে। (যে যা-ই কিছু করুক না কেন) সমস্ত কার্যকলাপ (হিসাব নিকাশের জন্য) আল্লাহর কাছে ফিরে যাবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3665,35,5,'হে মানুষ! আল্লাহর ওয়া‘দা সত্য, কাজেই পার্থিব জীবন কিছুতেই যেন তোমাদেরকে প্রতারিত না করে; আর সেই প্রধান প্রতারক (শয়ত্বান) যেন কিছুতেই তোমাদেরকে আল্লাহ সম্পর্কে প্রতারিত না করে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3666,35,6,'শয়ত্বান তোমাদের শত্রু, কাজেই তাকে শত্রু হিসেবে গ্রহণ কর। সে কেবল তার দলবলকে ডাকে, যাতে তারা জ্বলন্ত অগ্নির সঙ্গী হয়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3667,35,7,'যারা কুফুরী করে তাদের জন্য রয়েছে কঠিন শাস্তি। আর যারা ঈমান আনে আর সৎকাজ করে তাদের জন্য রয়েছে ক্ষমা ও মহা প্রতিফল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3668,35,8,'যাকে তার মন্দ কর্ম শোভনীয় ক’রে দেখানো হয়, অতঃপর সে সেটাকে উত্তম মনে করে (সে কি তার সমান, যে সৎ পথে পরিচালিত?) আল্লাহ যাকে ইচ্ছে বিপথগামী করেন, আর যাকে ইচ্ছে সঠিক পথে পরিচালিত করেন। কাজেই তাদের জন্য আক্ষেপ ক’রে, তুমি তোমার জীবনকে ধ্বংস হতে দিও না। তারা যা করে আল্লাহ তা খুব ভালভাবেই জানেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3669,35,9,'আল্লাহ, যিনি বায়ু প্রেরণ করেন, অতঃপর তা মেঘ সঞ্চার করে, অতঃপর আমি তা নির্জীব ভূখন্ডের দিকে পরিচালিত করি। অতঃপর আমি তা দিয়ে মৃত্তিকাকে তার মৃত্যুর পর আবার সঞ্জীবিত করি। এভাবেই (ঘটবে) পুনরুত্থান। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3670,35,10,'কেউ সম্মান-সুখ্যাতি চাইলে (আল্লাহকে উপেক্ষা করে তা লাভ করা যাবে না), সে জেনে নিক যাবতীয় সম্মান-সুখ্যাতির অধিকারী হলেন আল্লাহ। তাঁরই দিকে উত্থিত হয় পবিত্র কথাগুলো আর সৎকাজ সেগুলোকে উচ্চে তুলে ধরে। যারা মন্দ কাজের চক্রান্ত করে তাদের জন্য আছে কঠিন শাস্তি। তাদের চক্রান্ত নিষ্ফল হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3671,35,11,'আল্লাহ তোমাদেরকে মাটি থেকে সৃষ্টি করেছেন, অতঃপর শুক্র-বিন্দু হতে, অতঃপর তিনি তোমাদেরকে করেছেন (স্বামী-স্ত্রীর) জোড়া। তাঁর অবগতি ব্যতীত কোন নারী গর্ভ ধারণ করে না বা (তার বোঝা) হালকা করে না। কোন দীর্ঘায়ুর আয়ু দীর্ঘ করা হয় না, আর তার আয়ু কমানো হয় না কিতাবের লিখন ছাড়া। এটা (অর্থাৎ এ সবের হিসাব রাখা ও তত্ত্বাবধান করা) আল্লাহর জন্য সহজ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3672,35,12,'দু’টি দরিয়াও এক রকম নয়। একটি সুমিষ্ট, সুস্বাদু, সুপেয়; অন্যটি লবণাক্ত, বিস্বাদ। তথাপি তোমরা সকল (প্রকার পানি) থেকে তাজা গোশত আহার কর আর বের কর অলংকার- পরিধান করার জন্যে। তোমরা দেখতে পাও নৌযানগুলো ঢেউয়ের বুক চিরে চলাচল করে যাতে তোমরা তাঁর অনুগ্রহ খোঁজ করতে পার, আর যাতে তোমরা কৃতজ্ঞতা প্রকাশ কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3673,35,13,'তিনি রাতকে দিনে ঢুকিয়ে দেন, আর দিনকে রাতে ঢুকিয়ে দেন। তিনি সূর্য ও চন্দ্রকে তাঁর নিয়ন্ত্রণের অধীনে (কাজে নিয়োজিত) রেখেছেন। প্রত্যেকেই নির্ধারিত সময় অনুসারে গতিশীল আছে। এই হলেন আল্লাহ, তোমাদের প্রতিপালক। রাজত্ব তাঁরই। তোমরা তাঁর পরিবর্তে যাদেরকে ডাক তারা তো খেজুরের অাঁটি সংলগ্ন (অত্যন্ত পাতলা ও দুর্বল) আবরণেরও মালিক নয়।  ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3674,35,14,'তোমরা তাদেরকে ডাকলে তারা তোমাদের ডাক শুনবে না আর যদি শুনেও, তবুও তোমাদের (ডাকে) সাড়া দিতে পারবে না। আর তোমরা যে তাদেরকে (আল্লাহর) অংশীদার গণ্য করতে, ক্বিয়ামতের দিন তা তারা অস্বীকার করবে। কেউই তোমাদেরকে সর্বজ্ঞ আল্লাহর মত খবর জানাতে পারবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3675,35,15,'হে মানুষ! তোমরাই আল্লাহর মুখাপেক্ষী, আর আল্লাহ তিনি তো অভাবহীন, প্রশংসিত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3676,35,16,'তিনি ইচ্ছে করলে তোমাদেরকে বিলুপ্ত করতে পারেন, আর এক নতুন সৃষ্টি আনতে পারেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3677,35,17,'এটা আল্লাহর পক্ষে কঠিন নয়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3678,35,18,'কোন বহনকারী অন্যের (পাপের) বোঝা বইবে না। কেউ যদি তার গুরুভার বয়ে দেয়ার জন্য অন্যকে ডাকে তবে তার কিছুই বয়ে দেয়া হবে না- নিকটাত্মীয় হলেও। তুমি কেবল তাদেরকেই সতর্ক করতে পার, যারা না দেখেই তাদের প্রতিপালককে ভয় করে আর নামায প্রতিষ্ঠা করে। যে কেউ নিজেকে পরিশুদ্ধ করে সে তো পরিশুদ্ধ করে নিজের কল্যাণেই। আল্লাহর দিকেই (সকলের) প্রত্যাবর্তন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3679,35,19,'অন্ধ আর চোখওয়ালা সমান নয়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3680,35,20,'আর অন্ধকার ও আলোও (সমান নয়)। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3681,35,21,'আর ছায়া ও রোদও (সমান নয়)। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3682,35,22,'আর জীবিত ও মৃতও সমান নয়। আল্লাহ যাকে ইচ্ছে করেন শোনান; যারা ক্ববরে আছে তুমি তাদেরকে শোনাতে পার না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3683,35,23,'তুমি তো কেবল একজন সতর্ককারী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3684,35,24,'আমি তোমাকে সত্যসহ পাঠিয়েছি সুসংবাদদাতা ও সতর্ককারীরূপে। এমন কোন সম্প্রদায় নেই যাতে সতর্ককারী আসেনি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3685,35,25,'তারা যদি তোমাকে মিথ্যে ব’লে অস্বীকার করে, (তাহলে জেনে রেখ, এটা কোন নতুন ব্যাপার নয়, কারণ) তাদের পূর্ববর্তীরাও (নবী-রসূলদেরকে) মিথ্যে ব’লে অস্বীকার করেছিল। তাদের কাছে তাদের রসূলগণ সুস্পষ্ট নিদর্শন, লিখিত দলীল ও আলোকপ্রদ কিতাব নিয়ে এসেছিল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3686,35,26,'যারা কুফরী করেছিল, অতঃপর আমি তাদেরকে পাকড়াও করেছিলাম। কী (ভয়ংকর) ছিল আমার শাস্তি! ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3687,35,27,'তুমি কি দেখ না যে, আল্লাহ আকাশ হতে পানি বর্ষণ করেন, অতঃপর আমি তা দিয়ে রং বেরংয়ের ফলমূল উদগত করি। পাহাড়ের মধ্যে আছে বিভিন্ন বর্ণের গিরিপথ- সাদা, লাল আর নিকষ কালো। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3688,35,28,'তেমনিভাবে মানুষ, জীব-জন্তু আর গৃহপালিত পশুদের মধ্যেও রয়েছে তাদের বিভিন্ন রং। আল্লাহর বান্দাহদের মধ্যে তারাই তাঁকে ভয় করে যারা জ্ঞানী। আল্লাহ মহা ক্ষমতাশালী, পরম দয়ালু।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3689,35,29,'যারা আল্লাহর কিতাব তিলাওয়াত করে, নামায প্রতিষ্ঠা করে আর আল্লাহ তাদেরকে যে রিযক দিয়েছেন তাথেকে গোপনে ও প্রকাশ্যে ব্যয় করে, তারা এমন এক ব্যবসায়ের আশা করে যাতে কক্ষনো লোকসান হবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3690,35,30,'কারণ, তিনি তাদেরকে তাদের প্রতিফল পূর্ণমাত্রায় দান করবেন এবং নিজ অনুগ্রহে আরো বেশি দিবেন। তিনি অতি ক্ষমাশীল, (ভাল কাজের) বড়ই মর্যাদাদানকারী।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3691,35,31,'আমি যে কিতাবটি হতে তোমার প্রতি ওয়াহী করেছি তা সত্য, পূর্ববর্তী কিতাবের সত্যায়নকারী। আল্লাহ তাঁর বান্দাহদের সম্পর্কে অবশ্যই সব খবর রাখেন এবং সব দেখেন।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3692,35,32,'অতঃপর সেই কিতাবটির উত্তরাধিকারী করেছি আমি আমার বান্দাহদের মধ্যে হতে যাদেরকে বেছে নিয়েছি। অতঃপর তাদের কতক নিজেদের প্রতি অত্যাচার করেছে, আর কতক মধ্যপন্থী। তাদের কতক আল্লাহর নির্দেশে সৎ কাজে অগ্রণী। এটাই (মানুষের প্রতি আল্লাহর) খুবই বড় দয়া।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3693,35,33,'স্থায়ী জান্নাতে তারা প্রবেশ করবে। যেখানে তাদেরকে স্বর্ণ ও মুক্তার কঙ্কণে অলঙ্কৃত করা হবে। যেখানে তাদের পোশাক হবে রেশমের।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3694,35,34,'আর তারা বলবে- যাবতীয় প্রশংসা আল্লাহর যিনি আমাদের দুঃখ কষ্ট দূর করে দিয়েছেন। আমাদের প্রতিপালক অবশ্যই পরম ক্ষমাশীল, (ভাল কাজের) বড়ই মর্যাদাদানকারী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3695,35,35,'যিনি স্বীয় অনুগ্রহে আমাদেরকে স্থায়ী আবাস দান করেছেন। সেখানে ক্লেশ আমাদেরকে স্পর্শ করে না, ক্লান্তিও আমাদেরকে স্পর্শ করে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3696,35,36,'আর যারা কুফুরী করে, তাদের জন্য আছে জাহান্নামের আগুন। তাদের জন্য কোন সময় নির্ধারণ করা হবে না যে, তারা (নির্ধারিত সময় আসলে) মরে যাবে, আর তাদের থেকে শাস্তিও কমানো হবে না। প্রত্যেক অকৃতজ্ঞকে আমি এভাবেই প্রতিফল দিয়ে থাকি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3697,35,37,'সেখানে তারা চিৎকার করে বলবে- হে আমাদের পালনকর্তা! বের করুন আমাদেরকে, আমরা সৎকাজ করব, আমরা যে কাজ করতাম তা করব না। আমি কি তোমাদেরকে এতটা বয়স দেইনি যে, তখন কেউ নসীহত গ্রহণ করতে চাইলে নসীহত গ্রহণ করতে পারতে? আর তোমাদের কাছে সতর্ককারীও এসেছিল। কাজেই শাস্তি ভোগ কর, যালিমদের কোন সাহায্যকারী নেই। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3698,35,38,'আল্লাহ আসমান যমীনের অদৃশ্য বিষয় সম্পর্কে অবগত। অন্তরে যা আছে সে সম্পর্কে তিনি বিশেষভাবে জ্ঞাত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3699,35,39,'তিনি তোমাদেরকে পৃথিবীতে (নিজের) প্রতিনিধি করেছেন। অতএব যে কুফুরী করবে তার কুফুরীর জন্য সে নিজেই দায়ী হবে। কাফিরদের কুফর তাদের প্রতিপালকের ঘৃণাই বৃদ্ধি করে। কাফিরদের কুফর তাদের ক্ষতিই বৃদ্ধি করে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3700,35,40,'বল- তোমরা কি তোমাদের শরীকদেরকে দেখেছ আল্লাহর পরিবর্তে তোমরা যাদেরকে ডেকে থাক? তারা পৃথিবীতে কী সৃষ্টি করেছে তা আমাকে দেখাও; কিংবা আকাশমন্ডলীতে তাদের কোন শরীকানা আছে কি? কিংবা আমি কি তাদেরকে কোন কিতাব দিয়েছি যাত্থেকে কোন সুস্পষ্ট প্রমাণের উপর তারা আছে? বরং সীমালঙ্ঘনকারীরা একে অপরকে প্রতারণামূলক ওয়া‘দা দিয়ে থাকে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3701,35,41,'আল্লাহই আসমান ও যমীনকে স্থির রাখেন যাতে ও দু’টো টলে না যায়। ও দু’টো যদি টলে যায় তাহলে তিনি ছাড়া কে ও দু’টোকে স্থির রাখবে? তিনি পরম সহিষ্ণু, পরম ক্ষমাশীল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3702,35,42,'আর তারা (অর্থাৎ মক্কার মুশরিকরা) শক্তভাবে কসম খেয়ে বলত যে, তাদের কাছে সতর্ককারী আসলে তারা অন্য যে কোন সম্প্রদায়ের চেয়ে অবশ্য অবশ্যই সৎ পথের অধিক অনুসারী হয়ে যাবে। কিন্তু তাদের কাছে যখন সতর্ককারী আসল, তা বাড়িয়েই তুলল তাদের ঘৃণা, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3703,35,43,'যমীনে উদ্ধত আচরণ আর কু-চক্রান্ত। কু-চক্রান্ত তাকেই ঘিরে ধরবে যে তা করবে। তাহলে তারা কি তাদের পূর্ববর্তীদের উপর (আল্লাহর পক্ষ হতে) যে বিধান প্রয়োগ করা হয়েছে তারই অপেক্ষা করছে? তুমি আল্লাহর বিধানে কক্ষনো কোন পরিবর্তন পাবে না। তুমি আল্লাহর বিধানে কক্ষনো কোন ব্যতিক্রম পাবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3704,35,44,'তারা কি পৃথিবীতে ভ্রমণ করে না? তাহলে দেখত, তাদের পূর্ববর্তীদের পরিণাম কী হয়েছিল। তারা তো শক্তিতে ছিল এদের চেয়েও শক্তিশালী। আসমান ও যমীনের কোন কিছুই আল্লাহকে অপারগ করতে পারে না। তিনি সর্বজ্ঞাতা, সকল শক্তির অধিকারী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3705,35,45,'আল্লাহ মানুষকে তার কৃতকর্মের জন্য পাকড়াও করতে চাইলে ভূপৃষ্ঠের একটি প্রাণীকেও রেহাই দিতেন না। কিন্তু তিনি তাদের জন্য একটা নির্ধারিত কাল পর্যন্ত সময় বিলম্বিত করেন। অতঃপর তাদের সে নির্ধারিত সময় যখন এসে যায়, (তখন আল্লাহর ফয়সালা কার্যকরী হতে এক মুহূর্তও বিলম্ব ঘটে না), কারণ আল্লাহ (প্রতিটি মুহূর্তে) তাঁর বান্দাহদের পর্যবেক্ষণকারী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3706,36,1,'ইয়াসীন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3707,36,2,'শপথ হিকমতপূর্ণ কুরআনের। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3708,36,3,'তুমি অবশ্যই রসূলগণের অন্তর্ভুক্ত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3709,36,4,'তুমি সরল সঠিক পথে প্রতিষ্ঠিত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3710,36,5,'(এ কুরআন) মহাপরাক্রমশালী পরম করুণাময় (আল্লাহ) হতে অবতীর্ণ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3711,36,6,'যাতে তুমি সতর্ক করতে পার এমন এক সম্প্রদায়কে যাদের পিৃতপুরুষদেরকে সতর্ক করা হয়নি, কাজেই তারা (আল্লাহর নিদর্শন সম্পর্কে) উদাসীন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3712,36,7,'(জেনে বুঝে আল্লাহর অবাধ্য হওয়ার কারণে) তাদের অধিকাংশের উপর (তাদের অন্তঃকরণে সীল লাগিয়ে দেয়ার) বাণী অবধারিত হয়ে গেছে, কাজেই তারা ঈমান আনবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3713,36,8,'আমি তাদের গলদেশে (তাদের জিদ ও অহমিকার) বেড়ি পরিয়ে দিয়েছি আর তা থুতনি পর্যন্ত (গিয়ে ঠেকেছে), কাজেই তারা মাথা খাড়া করে রেখেছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3714,36,9,'তাদের সামনে আমি একটা (বাধার) প্রাচীর দাঁড় করিয়ে দিয়েছি, আর পেছনে একটা প্রাচীর, উপরন্তু তাদেরকে ঢেকে দিয়েছি; কাজেই তারা দেখতে পায় না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3715,36,10,'তুমি তাদেরকে সতর্ক কর আর না কর, তাদের কাছে দু’টোই সমান, তারা ঈমান আনবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3716,36,11,'তুমি তো সতর্ক কেবল তাকেই করতে পার যে লোক উপদেশ মেনে চলে আর দয়াময় (আল্লাহ)-কে না দেখেও ভয় করে। অতঃপর এদেরকে তুমি ক্ষমা ও সম্মানজনক পুরস্কারের সুসংবাদ দাও। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3717,36,12,'আমিই মৃতকে জীবিত করি, আর লিখে রাখি যা তারা আগে পাঠিয়ে দেয় আর যা পেছনে ছেড়ে যায়। সব কিছুই আমি স্পষ্ট কিতাবে সংরক্ষিত করে রেখেছি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3718,36,13,'তাদের কাছে দৃষ্টান্ত স্বরূপ এক জনপদবাসীদের কথা শুনিয়ে দাও যখন তাদের কাছে এসেছিল রসূলগণ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3719,36,14,'যখন তাদের কাছে দু’জন রসূল পাঠিয়েছিলাম তখন তারা সে দু’জনকে মিথ্যে ব’লে প্রত্যাখ্যান করল। অতঃপর আমি তৃতীয় আরেকজন দ্বারা তাদের শক্তি বৃদ্ধি করলাম। তারা বলল- আমরা তোমাদের প্রতি প্রেরিত হয়েছি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3720,36,15,'জনপদবাসীরা বলল- তোমরা তো আমাদেরই মত মানুষ বতীত অন্য কিছু নও। আর দয়াময় (আল্লাহ) কোন কিছুই নাযিল করেননি। তোমরা শুধু মিথ্যেই বলছ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3721,36,16,'রসূলগণ বলল- আমাদের পালনকর্তা জানেন আমরা অবশ্যই তোমাদের প্রতি প্রেরিত হয়েছি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3722,36,17,'স্পষ্টভাবে (আল্লাহর বাণী) পৌঁছে দেয়াই আমাদের একমাত্র দায়িত্ব। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3723,36,18,'জনপদের লোকেরা বলল- আমরা তোমাদেরকেই অমঙ্গলের কারণ মনে করি। তোমরা যদি (প্রচারকার্য থেকে) নিবৃত্ত না হও, তাহলে আমরা অবশ্য অবশ্যই তোমাদেরকে পাথর মেরে হত্যা করব এবং আমাদের পক্ষ থেকে বড়ই মর্মান্তিক শাস্তি তোমাদের উপর অবশ্য অবশ্যই নেমে আসবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3724,36,19,'রসূলগণ বলল- তোমাদের অমঙ্গলের কারণ তোমাদের সাথেই আছে (আর তা হল তোমাদের অপকর্ম)। তোমাদেরকে নসীহত করা হলেই কি (সেটাকে তোমরা তোমাদের অমঙ্গলের কারণ মনে কর)? আসলে তোমরা হচ্ছ এক সীমালঙ্ঘনকারী জাতি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3725,36,20,'নগর প্রান্ত থেকে এক লোক ছুটে আসলো। সে বলল- হে আমার জাতির লোকেরা! তোমরা রসূলদের মান্য কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3726,36,21,'তোমরা মান্য কর এদেরকে- যারা তোমাদের কাছে কোন প্রতিদান চায় না। উপরন্তু তারা সঠিক পথে পরিচালিত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3727,36,22,'কেন আমি তাঁর ‘ইবাদাত করব না যিনি আমাকে সৃষ্টি করেছেন, আর তাঁর কাছেই তোমাদেরকে ফিরে যেতে হবে? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3728,36,23,'আমি কি তাঁর পরিবর্তে (অন্য) সব ইলাহ্ গ্রহণ করব? করুণাময় আল্লাহ যদি আমার কোন ক্ষতি করতে চান তবে আমার জন্য তাদের সুপারিশ কোনই কাজে আসবে না, আর তারা আমাকে উদ্ধার করতেও পারবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3729,36,24,'তা যদি করি, তাহলে আমি স্পষ্ট পথভ্রষ্টতেই পতিত হব। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3730,36,25,'আমি তো তোমাদের পালনকর্তার উপর ঈমান এনেছি, কাজেই তোমরা আমার কথা শুন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3731,36,26,'(লোকেরা তাকে হত্যা করে ফেললে আল্লাহর পক্ষ থেকে) তাকে বলা হল- জান্নাতে প্রবেশ কর। (তখন) সে বলল- হায়! আমার জাতির লোকেরা যদি জানত, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3732,36,27,'আমার পালনকর্তা কোন্ জিনিসের বদৌলতে আমাকে ক্ষমা করেছেন আর আমাকে সম্মানিতদের অন্তর্ভুক্ত করেছেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3733,36,28,'আমি তার মৃত্যুর পর তার জাতির বিরুদ্ধে আসমান থেকে কোন সৈন্যবাহিনী পাঠাইনি, আর তা পাঠানোর আমার কোন দরকারও ছিল না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3734,36,29,'ওটা ছিল মাত্র একটা প্রচন্ড শব্দ, ফলে তারা সহসাই নিস্তব্ধ হয়ে গেল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3735,36,30,'বান্দাহদের জন্য পরিতাপ! তাদের কাছে এমন কোন রসূলই আসেনি যাকে তারা ঠাট্টা-বিদ্রূপ করেনি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3736,36,31,'তারা কি দেখে না যে, তাদের পূর্বে আমি কত মানবগোষ্ঠীকে ধ্বংস করে দিয়েছি? তারা তাদের কাছে ফিরে আসবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3737,36,32,'তাদের সব্বাইকে একত্রে আমার কাছে হাজির করা হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3738,36,33,'মৃত যমীন তাদের জন্য একটা নিদর্শন। তাকে আমি জীবিত করি আর তা থেকে আমি উৎপন্ন করি শস্য যা থেকে তারা খায়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3739,36,34,'আর আমি তাতে খেজুর ও আঙ্গুরের বাগান তৈরি করি, আর তাতে প্রবাহিত করি ঝর্ণাধারা। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3740,36,35,'যাতে তারা তার ফল খেতে পারে- যা তারা তাদের হাত দিয়ে বানায়নি। তাহলে কেন তারা কৃতজ্ঞতা প্রকাশ করে না? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3741,36,36,'পূত পবিত্র সেই সত্তা যিনি জোড়া সৃষ্টি করেছেন প্রত্যেকটির যা উৎপন্ন করে যমীন, আর তাদের নিজেদের ভিতরেও আর সে সবেও যা তারা জানে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3742,36,37,'তাদের জন্য একটি নিদর্শন হচ্ছে রাত, তাত্থেকে আমি দিনকে সরিয়ে নেই, ফলে তখনই তারা অন্ধকারে ডুবে যায়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3743,36,38,'আর সূর্য তার জন্যে নির্দিষ্ট ক’রে দেয়া জায়গায় গতিশীল, এটা মহা পরাক্রমশালী সর্বজ্ঞের সুনিরূপিত নির্ধারণ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3744,36,39,'আর চাঁদ-তার জন্য আমি নির্দিষ্ট করেছি বিভিন্ন মান্যিল (যা সে অতিক্রম করে), এমনকি শেষ পর্যন্ত সেটি খেজুরের কাঁদির পুরানো শুকনো দন্ডের মত হয়ে ফিরে আসে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3745,36,40,'সূর্যের পক্ষে সম্ভব নয় চাঁদকে ধরে ফেলা, আর রাতের পক্ষে সম্ভব নয় দিনকে ছাড়িয়ে আগে বেড়ে যাওয়া, প্রত্যেকেই নিজ নিজ কক্ষ পথে সাঁতার কাটছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3746,36,41,'তাদের জন্য (আমার কুদরাতের) আরো একটি নিদর্শন এই যে, আমি তাদের বংশধরদেরকে (মহা প্লাবণের সময়) ভরা নৌকায় আরোহণ করিয়েছি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3747,36,42,'আর তাদের জন্য ঐ ধরনের আরো যানবাহন তৈরি করেছি যাতে তারা আরোহণ করে থাকে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3748,36,43,'আমি ইচ্ছে করলে তাদেরকে ডুবিয়ে দিতে পারি, তখন (তাদের ফরিয়াদ শুনার জন্য) কোন সাহায্যকারী থাকবে না, আর তারা পরিত্রাণও পাবে না ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3749,36,44,'আমার রহমত না হলে, আর কিছু কালের জন্য তাদেরকে জীবন উপভোগ করতে না দিলে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3750,36,45,'তাদেরকে যখন বলা হয় ‘‘তোমাদের সামনে যে পরিণাম আসছে তাত্থেকে আর তোমাদের পেছনের (অতীত জাতিগুলোর উপর ঘটে গেছে সে রকম) ‘আযাব থেকে নিজেদেরকে রক্ষা কর যাতে তোমাদের উপর রহম করা হয় (তখন তারা মুখ ফিরিয়ে নেয়)।’’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3751,36,46,'তাদের কাছে তাদের প্রতিপালকের নিদর্শন থেকে যখনই কোন নিদর্শন আসে তখনই তারা তাত্থেকে মুখ ফিরিয়ে নেয়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3752,36,47,'তাদেরকে যখন বলা হয় ‘আল্লাহ তোমাদেরকে যে রিযক দিয়েছেন তাত্থেকে তোমরা (আল্লাহর পথে) ব্যয় কর; তখন কাফিররা মু’মিনদেরকে বলে, ‘‘আমরা কি এমন লোককে খাওয়াবো আল্লাহ ইচ্ছে করলে যাকে খাওয়াতে পারতেন? তোমরা তো স্পষ্ট পথভ্রষ্টতে পড়ে আছ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3753,36,48,'আর তারা বলে, ‘‘তোমরা যদি সত্যবাদী হও তাহলে বল, (ক্বিয়ামতের) এ ও‘য়াদা কখন পূর্ণ হবে?’’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3754,36,49,'তারা যে জন্য অপেক্ষা করছে সেটাতো একটা প্রচন্ড শব্দ যা তাদেরকে পাকড়াও করবে যখন তারা নিজেদের মধ্যে বাক-বিতন্ডায় লিপ্ত থাকবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3755,36,50,'(ক্বিয়ামত এমনই হঠাৎ আক্রমণ করবে যে) তারা না পারবে ওসীয়াত করতে আর না পারবে তাদের পরিবার পরিজনের কাছে ফিরে যেতে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3756,36,51,'আর যখন শিঙ্গায় ফুঁক দেয়া হবে, তখনই তারা ক্ববর থেকে তাদের পালনকর্তার দিকে ছুটে আসবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3757,36,52,'তারা বলবে, ‘হায় আমাদের দুর্ভোগ! আমাদেরকে আমাদের ঘুমের জায়গা থেকে কে উঠালো? (তাদেরকে জবাব দেয়া হবে) ‘‘এটা হল তাই- দয়াময় আল্লাহ যার ও‘য়াদা দিয়েছিলেন, আর রসূলগণও সত্য কথাই বলেছিলেন।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3758,36,53,'মাত্র একটা প্রচন্ড শব্দ হবে, তক্ষুণি তাদের সব্বাইকে আমার সামনে হাজির করা হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3759,36,54,'আজ কারো প্রতি কোন যুলম করা হবে না, তোমরা যে ‘আমাল করছিলে তোমাদেরকে কেবল তারই প্রতিফল দেয়া হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3760,36,55,'সে দিন জান্নাতীরা আনন্দে মশগুল হয়ে থাকবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3761,36,56,'তারা আর তাদের স্ত্রীরা সুশীতল ছায়ায়, উঁচু উঁচু আসনে হেলান দিয়ে বসবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3762,36,57,'তাদের জন্য সেখানে থাকবে ফলমূল আর তাদের জন্য থাকবে তারা যা কিছু পেতে চাইবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3763,36,58,'দয়াময় প্রতিপালকের পক্ষ থেকে তাদেরকে ‘সালাম’ বলে সম্ভাষণ করা হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3764,36,59,'(সে দিন বলা হবে) ‘হে অপরাধীরা! আজ তোমরা আলাদা হয়ে যাও।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3765,36,60,'‘হে আদাম সন্তান! আমি কি তোমাদেরকে নির্দেশ দেইনি যে, তোমরা শয়ত্বানের ‘ইবাদাত করো না, কারণ সে তোমাদের প্রকাশ্য দুশমন? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3766,36,61,'আর আমারই ‘ইবাদাত কর, এটাই সরল সঠিক পথ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3767,36,62,'(কিন্তু তোমাদেরকে সতর্ক করে দেয়া সত্ত্বেও) শয়ত্বান তোমাদের বহু দলকে বিভ্রান্ত করে দিয়েছে, তবুও কি তোমরা বুঝ না? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3768,36,63,'এটা সেই জাহান্নাম যে বিষয়ে তোমাদেরকে ভয় দেখানো হয়েছিল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3769,36,64,'আজ তাতে প্রবেশ কর, কেননা তোমরা এটাকে অবিশ্বাস করেছিলে।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3770,36,65,'আজ আমি তাদের মুখে সীল মোহর লাগিয়ে দেব, তাদের হাত আমার সঙ্গে কথা বলবে, আর তারা যা করত সে সম্পর্কে তাদের পাগুলো সাক্ষ্য দেবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3771,36,66,'আমি ইচ্ছে করলে তাদের দৃষ্টিশক্তি বিলুপ্ত করে দিতাম। তখন তারা পথের দিকে দৌঁড়ে দেখতে চাইলে কীভাবে তারা দেখতে পেত? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3772,36,67,'আমি ইচ্ছে করলে তাদের নিজ নিজ জায়গাতেই তাদের আকৃতি পরিবর্তন করে দিতাম, তখন তারা না সামনের দিকে চলতে পারত, আর না পারত পেছনে ফিরে যেতে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3773,36,68,'আমি যাকে দীর্ঘ জীবন দেই, তাকে সৃষ্টির ক্ষেত্রে পূর্বের অবস্থায় ফিরিয়ে আনি। তবুও কি তারা বুঝে না? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3774,36,69,'[কাফিররা রসূল (সা.)-কে উদ্দেশ্য করে বলে, লোকটা একটা কবি। কিন্তু) আমি রসূলকে কবিতা শিখাইনি, আর তা তার জন্য শোভনীয়ও নয়। তাতো এক উপদেশ ও স্পষ্ট কুরআন ব্যতীত অন্য কিছু নয়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3775,36,70,'যাতে সে (আত্মিকভাবে) জীবিতকে সতর্ক করতে পারে আর কাফিরদের বিরুদ্ধে অকাট্য দলীল হতে পারে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3776,36,71,'তারা কি দেখে না যে আমার হাতে তৈরি জিনিসগুলোর মধ্যে আমি তাদের জন্য সৃষ্টি করেছি গৃহপালিত পশু আর এখন তারা এগুলোর মালিক! ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3777,36,72,'আমি এগুলোকে তাদের বশীভূত করে দিয়েছি, ফলে এগুলোর কতক তাদের বাহন, আর এদের কতকগুলো তারা খায়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3778,36,73,'তাদের জন্য এগুলোতে আছে বহু উপকার আর পানীয় দ্রব্য। তবুও তারা কেন শুকরিয়া আদায় করে না? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3779,36,74,'তারা আল্লাহর পরিবর্তে অনেক ইলাহ গ্রহণ করেছে এই আশায় যে, তারা (ঐ সব ইলাহ দ্বারা) সাহায্যপ্রাপ্ত হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3780,36,75,'ঐ সব ইলাহ তাদেরকে সাহায্য করতে সক্ষম নয়, বরং (উল্টো) এ লোকেরাই (ঐ সব ইলাহকে সাহায্য করার জন্য) সদা প্রস্তুত সেনাবাহিনীর মত হাযির হয়ে আছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3781,36,76,'কাজেই তাদের কথাবার্তা তোমাকে যেন দুঃখ না দেয়; আমি জানি তারা যা গোপন করে, আর যা প্রকাশ করে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3782,36,77,'মানুষ কি দেখে না যে আমি তাকে সৃষ্টি করেছি শুক্রবিন্দু হতে? অতঃপর সে হয়ে গেল সুস্পষ্ট ঝগড়াটে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3783,36,78,'সে (আমার সৃষ্টির সাথে) আমার তুলনা করে, অথচ সে তার নিজের সৃষ্টির ব্যপারটি ভুলে যায় (যে তাকে আমিই সৃষ্টি করেছি)। সে বলে, ‘হাড়গুলোকে কে আবার জীবন্ত করবে যখন তা পচে গলে যাবে?’’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3784,36,79,'বল, ‘‘তাকে তিনিই জীবন্ত করবেন যিনি ওগুলোকে প্রথমবার সৃষ্টি করেছেন, আর তিনি প্রতিটি সৃষ্টি সম্পর্কে সবচেয়ে বেশি অবগত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3785,36,80,'যিনি তোমাদের জন্য সবুজ গাছ হতে আগুন তৈরি করেছেন, অতঃপর তোমরা তাত্থেকে আগুন জ্বালাও। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3786,36,81,'যিনি আসমান যমীন সৃষ্টি করেছেন তিনি কি সেই লোকদের অনুরূপ (আবার) সৃষ্টি করতে সক্ষম নন? হাঁ, অবশ্যই। তিনি মহা স্রষ্টা, সর্বজ্ঞ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3787,36,82,'তাঁর কাজকর্ম কেবল এ রকম যে, যখন তিনি কোন কিছুর ইচ্ছে করেন তখন তাকে হুকুম করেন যে হয়ে যাও, আর অমনি তা হয়ে যায়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3788,36,83,'কাজেই পবিত্র ও মহান তিনি যাঁর হাতে সব কিছুর সর্বময় কর্তৃত্ব, আর তাঁর কাছেই তোমাদের (সকলকে) ফিরিয়ে আনা হবে। 	');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3789,37,1,'শপথ তাদের যারা সারিবদ্ধভাবে দাঁড়ানো, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3790,37,2,'অতঃপর যারা ধমক দিয়ে তিরস্কার করে তাদের শপথ, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3791,37,3,'আর যারা (আল্লাহর) যিকর আবৃত্তিতে লিপ্ত, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3792,37,4,'তোমাদের প্রকৃত ইলাহ অবশ্য একজন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3793,37,5,'যিনি আসমান, যমীন আর এ দু’য়ের মাঝে যা আছে এবং সকল উদয় স্থলের মালিক। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3794,37,6,'আমি নিকটবর্তী আসমানকে তারকারাজির সৌন্দর্য দ্বারা সুশোভিত করেছি, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3795,37,7,'আর (এটা করেছি) প্রত্যেক বিদ্রোহী শয়ত্বান থেকে সুরক্ষার ব্যবস্থা হিসেবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3796,37,8,'যার ফলে তারা উচ্চতর জগতের কিছু শুনতে পারে না, চতুর্দিক থেকে তাদের প্রতি নিক্ষেপ করা হয় (উল্কাপিন্ড) ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3797,37,9,'(তাদেরকে) তাড়ানোর জন্য। তাদের জন্য আছে বিরামহীন শাস্তি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3798,37,10,'তবে কেউ ছোঁ মেরে কিছু শুনে ফেললে জ্বলন্ত উল্কাপিন্ড তার পিছু নেয়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3799,37,11,'তাদেরকে জিজ্ঞেস কর- সৃষ্টির ক্ষেত্রে কি তারাই বেশি প্রবল, না আমি অন্য যা কিছু সৃষ্টি করেছি তা (বেশি প্রবল)? আমি তো তাদেরকে সৃষ্টি করেছি (অতি নগণ্য) মাটি থেকে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3800,37,12,'(আল্লাহর শক্তি-ক্ষমতা-মহিমা দেখে) তুমি কর বিস্ময়বোধ, আর তারা করে বিদ্রূপ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3801,37,13,'তাদেরকে উপদেশ দেয়া হলে তারা উপদেশ নেয় না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3802,37,14,'তারা আল্লাহর কোন নিদর্শন দেখলে ঠাট্টা করে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3803,37,15,'আর তারা বলে- ‘এটা স্পষ্ট যাদু ছাড়া আর কিছুই না।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3804,37,16,'আমরা যখন মরব এবং মাটি ও হাড়ে পরিণত হব, তখনো কি আমাদেরকে আবার জীবিত করে উঠানো হবে? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3805,37,17,'এবং আমাদের পূর্বপুরুষদেরকেও (উঠানো হবে)?’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3806,37,18,'তাদেরকে বল, ‘হাঁ, এবং তোমরা হবে লাঞ্ছিত।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3807,37,19,'ওটা (হবে) মাত্র একটা প্রচন্ড শব্দ, আর তখনই তারা স্বচক্ষে (সব কিছু) দেখতে পাবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3808,37,20,'তারা আরো বলবে- ‘‘হায় আমাদের দুর্ভাগ্য! এটাই তো কর্মফলের দিন।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3809,37,21,'এটাই ফয়সালার দিন যাকে তোমরা মিথ্যে বলে অস্বীকার করতে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3810,37,22,'(হুকুম দেয়া হবে) ‘একত্র কর যালিমদেরকে আর তাদের সঙ্গীদেরকে এবং তাদেরকেও, যাদের তারা ‘ইবাদাত করত ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3811,37,23,'আল্লাহর (‘ইবাদাতের) পরিবর্তে, আর তাদেরকে জাহান্নামের পথ দেখাও। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3812,37,24,'অতঃপর ওদেরকে থামাও ওদেরকে তো প্রশ্ন করা হবে- ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3813,37,25,'‘তোমাদের হয়েছে কী, তোমরা পরস্পরকে সাহায্য করছ না কেন?’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3814,37,26,'বরং আজ তারা (বিচারের সামনে) আত্মসমপর্ণ করবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3815,37,27,'তারা একে অপরের দিকে মুখ করে পরস্পর জিজ্ঞাসাবাদ করবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3816,37,28,'তারা (তাদের ক্ষমতাশালীদেরকে) বলবে, ‘‘তোমরা তো তোমাদের ক্ষমতা নিয়ে আমাদের কাছে আসতে।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3817,37,29,'তারা (অর্থাৎ ক্ষমতার অধিকারীরা) উত্তর দিবে- ‘‘তোমরা তো (বিচার দিবসের প্রতি) বিশ্বাসীই ছিলে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3818,37,30,'আর তোমাদের উপর আমাদের কোন কর্তৃত্বও ছিল না, বরং তোমরা ছিলে সীমালঙ্ঘনকারী জাতি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3819,37,31,'আমাদের বিপক্ষে আমাদের পালনকর্তার উক্তিই সত্য হয়েছে, আমাদেরকে অবশ্যই শাস্তির স্বাদ নিতে হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3820,37,32,'আসলে আমরাই তোমাদেরকে গোমরাহ করেছিলাম, কারণ আমরা নিজেরাও গোমরাহ ছিলাম।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3821,37,33,'সেদিন (দুর্বল আর সবল) সবাই ‘আযাবে শরীক হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3822,37,34,'অপরাধীদের প্রতি আমি এ রকমই (আচরণ) করে থাকি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3823,37,35,'তাদেরকে যখন ‘আল্লাহ ছাড়া সত্যিকারের কোন ইলাহ নেই’ বলা হত, তখন তারা অহংকার করত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3824,37,36,'আর তারা বলত, ‘‘আমরা কি এক পাগলা কবির কথা মেনে আমাদের ইলাহগুলোকে ত্যাগ করব? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3825,37,37,'বরং সে [অর্থাৎ মুহাম্মাদ (সা.)] সত্য নিয়ে এসেছে এবং (পূর্বে আগমনকারী) রসূলদেরকে সত্যায়িত করেছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3826,37,38,'(এখন তাদেরকে বলা হবে) ‘তোমরা অবশ্যই মর্মান্তিক শাস্তির স্বাদ ভোগ করবে, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3827,37,39,'তোমাদেরকে কেবল তারই প্রতিফল দেয়া হবে যা তোমরা করতে।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3828,37,40,'কিন্তু আল্লাহর একনিষ্ঠ বান্দারা নয় (তারা এ সব ‘আযাব থেকে রক্ষা পাবে)। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3829,37,41,'তাদের জন্য আছে নির্ধারিত রিযক- ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3830,37,42,'ফলমূল; আর তারা হবে সম্মানিত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3831,37,43,'(তারা থাকবে) নি‘য়ামাতের ভরা জান্নাতে ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3832,37,44,'উচ্চাসনে মুখোমুখী হয়ে ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3833,37,45,'তাদের কাছে চক্রাকারে পরিবেশন করা হবে স্বচ্ছ প্রবাহিত ঝর্ণার সুরাপূর্ণ পাত্র। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3834,37,46,'নির্মল পানীয়, পানকারীদের জন্য সুপেয়, সুস্বাদু। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3835,37,47,'নেই তাতে দেহের জন্য ক্ষতিকর কোন কিছু, আর তারা তাতে মাতালও হবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3836,37,48,'তাদের কাছে থাকবে সংযত নয়না, সতী সাধ্বী, ডাগর ডাগর সুন্দর চক্ষু বিশিষ্টা সুন্দরীরা (হুরগণ)। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3837,37,49,'তারা যেন সযত্নে ঢেকে রাখা ডিম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3838,37,50,'অতঃপর তারা পরস্পরের মুখোমুখী হয়ে একে ‘অপরের খবর জিজ্ঞেস করবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3839,37,51,'তাদের একজন বলবে- ‘‘(দুনিয়ায়) আমার ছিল একজন সাথী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3840,37,52,'সে বলত- ‘‘তুমি কি বিশ্বাস কর যে, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3841,37,53,'আমরা যখন মরে যাব আর মাটি ও হাড্ডিতে পরিণত হব তখনো সত্যিই কি আমাদেরকে পুরস্কার ও শাস্তি দেয়া হবে? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3842,37,54,'আল্লাহ বলবেন- ‘ তোমরা কি তাকে উঁকি দিয়ে দেখতে চাও?’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3843,37,55,'তারপর সে উঁকি দিয়ে দেখবে এবং তাকে জাহান্নামের মাঝখানে দেখতে পাবে।  ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3844,37,56,'সে বলবে, ‘আল্লাহর কসম! তুমি তো আমাকে প্রায় ধ্বংসই করে দিয়েছিলে, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3845,37,57,'আমার প্রতিপালকের অনুগ্রহ না হলে আমিও তো (জাহান্নামের ভিতর) হাজির করা লোকেদের মধ্যে শামিল থাকতাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3846,37,58,'এখন আমাদের আর মৃত্যু হবে না ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3847,37,59,'আমাদের প্রথম মৃত্যুর পর, আর আমাদেরকে শাস্তিও দেয়া হবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3848,37,60,'এটাই তো মহাসাফল্য। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3849,37,61,'এ রকম সাফল্যের জন্যই ‘আমলকারীদের ‘আমল করা উচিত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3850,37,62,'আপ্যায়ন হিসেবে এটা উত্তম, না, (জাহান্নামের) জাক্কুম গাছ? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3851,37,63,'এ গাছটাকে আমি যালিমদের পরীক্ষা করার জন্য (একটা উপকরণ) বানিয়েছি (কেননা, যালিমরা বলে যে, জাহান্নামের ভিতর আবার গাছ হয় কী করে?) ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3852,37,64,'এটা এমন একটা গাছ যা জাহান্নামের তলদেশ থেকে বের হয়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3853,37,65,'এর চূড়াগুলো যেন শয়ত্বানের মাথা (অর্থাৎ দেখতে খুবই খারাপ।) ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3854,37,66,'জাহান্নামের অধিবাসীরা তাত্থেকে খাবে আর তা দিয়ে পেট পূর্ণ করবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3855,37,67,'এর উপর তাদেরকে দেয়া হবে ফুটন্ত পানির (পূঁজ সম্বলিত) মিশ্রণ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3856,37,68,'অতঃপর তাদের প্রত্যাবর্তন হবে জ্বলন্ত আগুনের দিকে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3857,37,69,'তারা তাদের পিতৃপুরুষদেরকে বিপথগামী পেয়েছিল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3858,37,70,'অতঃপর তাদেরই পদাংক অনুসরণ করে ছুটে চলেছিল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3859,37,71,'এদের আগের লোকেদের অধিকাংশই পথভ্রষ্ট হয়ে গিয়েছিল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3860,37,72,'আমি তাদের মাঝে সতর্ককারী (রসূল) পাঠিয়েছিলাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3861,37,73,'এখন দেখ, এই সতর্ক করে দেয়া লোকেদের পরিণাম কী হয়েছিল! ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3862,37,74,'কিন্তু আল্লাহর একনিষ্ট বান্দাদের কথা ভিন্ন (এ সব খারাপ পরিণতি থেকে সম্পূর্ণ মুক্ত ছিল।) ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3863,37,75,'(ইতোপূর্বে) নূহ আমাকে ডেকেছিল, অতঃপর (দেখ) আমি কতই না উত্তম সাড়াদাতা ছিলাম! ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3864,37,76,'তাকে আর তার পরিবারবর্গকে আমি মহা বিপদ থেকে উদ্ধার করেছিলাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3865,37,77,'আর তার বংশধরদেরকেই আমি বংশানুক্রমে বিদ্যমান রাখলাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3866,37,78,'আর আমি তাকে পরবর্তীদের মাঝে স্মরণীয় করে রাখলাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3867,37,79,'বিশ্বজগতে নূহের প্রতি সালাম/শান্তি বর্ষিত হোক। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3868,37,80,'সৎকর্মশীলদেরকে আমি এভাবেই প্রতিদান দিয়ে থাকি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3869,37,81,'সে ছিল আমার মু’মিন বান্দাহদের একজন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3870,37,82,'অতঃপর অন্যদের আমি ডুবিয়ে দিয়েছিলাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3871,37,83,'অবশ্যই ইবরাহীম ছিল তারই দলের লোক। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3872,37,84,'সে যখন তার প্রতিপালকের কাছে বিশুদ্ধ অন্তর নিয়ে হাজির হল, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3873,37,85,'সে  তখন  তার  পিতাকে  ও  তার  জাতিকে  বলল, ‘তোমরা কিসের ‘ইবাদাত কর? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3874,37,86,'তোমরা কি আল্লাহকে বাদ দিয়ে মিথ্যে মা’বুদ পেতে চাও? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3875,37,87,'বিশ্ব জগতের প্রতিপালক সম্পর্কে তোমরা কী ধারণা পোষণ কর? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3876,37,88,'অতঃপর তারকারাজির দিকে সে একবার তাকাল (অর্থাৎ চিন্তে ভাবনা করল) ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3877,37,89,'তারপর বলল, ‘‘আমি অসুস্থ।’’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3878,37,90,'অতঃপর তারা তাকে পেছনে রেখে চলে গেল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3879,37,91,'তারপর সে চুপে চুপে তাদের উপাস্যদের কাছে ঢুকে পড়ল আর বলল, (আপনাদের সম্মুখে রাখা এত উপাদেয় খাবার) আপনারা খাচ্ছেন না কেন? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3880,37,92,'কী হয়েছে আপনাদের, কথা বলছেন না কেন? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3881,37,93,'অতঃপর সে তাদের উপর ঝাঁপিয়ে পড়ে সজোরে আঘাত করল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3882,37,94,'তখন লোকেরা (ফিরে এসে) তার দিকে ছুটে আসল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3883,37,95,'সে বলল, ‘‘তোমরা (পাথর) খোদাই করে সেগুলো নিজেরা বানাও, সেগুলোরই আবার ‘ইবাদাত কর? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3884,37,96,'আল্লাহই সৃষ্টি করেছেন তোমাদেরকে আর তোমরা যা তৈরি কর সেগুলোকেও।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3885,37,97,'তারা বলল, ‘তার জন্য একটা অগ্নিকুন্ড তৈরি কর, অতঃপর তাকে আগুনে নিক্ষেপ কর।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3886,37,98,'তারা তার বিরুদ্ধে একটা ষড়যন্ত্র করতে চেয়েছিল। কিন্তু আমি তাদেরকে এক্কেবারে হীন করে ছাড়লাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3887,37,99,'সে বলল, ‘আমি আমার প্রতিপালকের দিকে চললাম, তিনি আমাকে অবশ্যই সঠিক পথ দেখাবেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3888,37,100,'হে আমার প্রতিপালক! তুমি আমাকে এক সৎকর্মশীল পুত্র সন্তান দান কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3889,37,101,'অতঃপর আমি তাকে এক অতি ধৈর্যশীল পুত্রের সুসংবাদ দিলাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3890,37,102,'অতঃপর সে যখন তার পিতার সাথে চলাফিরা করার বয়সে পৌঁছল, তখন ইবরাহীম (আঃ) বলল, ‘বৎস! আমি স্বপে দেখেছি যে, আমি তোমাকে যবেহ করছি, এখন বল,  তোমার অভিমত কী? সে বলল, ‘হে পিতা! আপনাকে যা আদেশ করা হয়েছে আপনি তাই করুন, আল্লাহ চাইলে আপনি আমাকে ধৈর্যশীলই পাবেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3891,37,103,'দু’জনেই যখন আনুগত্যে মাথা নুইয়ে দিল। আর ইবরাহীম তাকে উপুড় ক’রে শুইয়ে দিল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3892,37,104,'তখন আমি তাকে ডাক দিলাম, ‘হে ইবরাহীম! ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3893,37,105,'স্বপ্নে দেয়া আদেশ তুমি সত্যে পরিণত করেই ছাড়লে। এভাবেই আমি সৎকর্মশীলদেরকে প্রতিদান দিয়ে থাকি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3894,37,106,'অবশ্যই এটা ছিল এক সুস্পষ্ট পরীক্ষা। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3895,37,107,'আমি এক মহান কুরবানীর বিনিময়ে পুত্রটিকে ছাড়িয়ে নিলাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3896,37,108,'আর আমি তাকে পরবর্তীদের মাঝে স্মরণীয় করে রাখলাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3897,37,109,'ইবরাহীমের উপর শান্তি বর্ষিত হোক! ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3898,37,110,'সৎকর্মশীলদেরকে আমি এভাবেই প্রতিদান দিয়ে থাকি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3899,37,111,'সে ছিল আমার মু’মিন বান্দাহদের অন্তর্ভুক্ত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3900,37,112,'আর তাকে সুসংবাদ দিয়েছিলাম ইসহাকের- যে ছিল সৎকর্মশীল বান্দাহদের অন্তর্ভুক্ত একজন নবী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3901,37,113,'আর আমি বরকত দিলাম তাকে আর ইসহাককে; (তাদের দু’জনের) বংশধরদের কতক সৎকর্মশীল, আর কতক নিজেদের প্রতি সুস্পষ্ট যুলুমকারী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3902,37,114,'আমি মূসা ও হারূনের প্রতি অনুগ্রহ করেছিলাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3903,37,115,'আর তাদের দু’জনকে এবং তাদের জাতিকে মহা বিপদ থেকে রক্ষা করেছিলাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3904,37,116,'আর আমি তাদেরকে সাহায্য করেছিলাম, যার ফলে তারাই বিজয়ী হয়েছিল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3905,37,117,'আমি উভয়কে দিয়েছিলাম সুস্পষ্ট কিতাব। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3906,37,118,'আর তাদের উভয়কে সরল সঠিক পথে পরিচালিত করেছিলাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3907,37,119,'আমি তাদের উভয়কে পরবর্তীদের জন্য স্মরণীয় করে রাখলাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3908,37,120,'মূসা ও হারূনের প্রতি শান্তি বর্ষিত হোক। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3909,37,121,'এভাবেই আমি সৎকর্মশীলদেরকে প্রতিদান দিয়ে থাকি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3910,37,122,'তারা দু’জন ছিল আমার মু’মিন বান্দাহদের অন্তর্ভুক্ত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3911,37,123,'ইলিয়াসও ছিল অবশ্যই রসূলদের একজন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3912,37,124,'স্মরণ কর, যখন সে তার জাতিকে বলেছিল, ‘তোমরা কি (আল্লাহকে) ভয় করবে না? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3913,37,125,'‘তোমরা কি বা‘য়ালকে ডাক, আর পরিত্যাগ কর সর্বোত্তম সৃষ্টিকারী ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3914,37,126,'আল্লাহকে, যিনি তোমাদের প্রতিপালক আর তোমাদের পূর্ববর্তী পিতৃপুরুষদেরও প্রতিপালক। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3915,37,127,'কিন্তু তারা তাকে মিথ্যে বলে প্রত্যাখ্যান করল, কাজেই তাদেরকে অবশ্যই (শাস্তির জন্য) হাজির করা হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3916,37,128,'কিন্তু আল্লাহর একনিষ্ঠ বান্দাহদেরকে নয়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3917,37,129,'আমি তাকে পরবর্তীদের মাঝে স্মরণীয় করে রাখলাম।  ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3918,37,130,'ইলিয়াসের প্রতি শান্তি বর্ষিত হোক। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3919,37,131,'এভাবেই আমি সৎকর্মশীলদেরকে প্রতিদান দিয়ে থাকি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3920,37,132,'সে ছিল আমার মু’মিন বান্দাহদের অন্তুর্ভুক্ত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3921,37,133,'লূতও ছিল অবশ্যই রসূলদের একজন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3922,37,134,'স্মরণ কর যখন আমি তাকে আর তার পরিবারের সকলকে উদ্ধার করেছিলাম ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3923,37,135,'এক বৃদ্ধা ছাড়া- সে ছিল পিছ-পড়াদের একজন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3924,37,136,'অতঃপর অন্য সব্বাইকে আমি পুরোপুরি ধ্বংস করে ছিয়েছিলাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3925,37,137,'তোমরা তো তাদের ধ্বংসপ্রাপ্ত বসতিগুলোর উপর দিয়ে অবশ্যই চলাচল কর সকালে ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3926,37,138,'ও সন্ধ্যায়, তোমরা কি বুঝবে না? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3927,37,139,'ইউনুসও ছিল রসূলদের একজন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3928,37,140,'স্মরণ কর, যখন সে পালিয়ে বোঝাই নৌকায় পৌঁছেছিল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3929,37,141,'অতঃপর (দোষী খুঁজার জন্য যে লটারী করা হল সেই) লটারীতে সে অংশ নিল আর তাতে হেরে গেল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3930,37,142,'পরে একটা বড় মাছে তাকে গিলে ফেলল, সে কাজ করেছিল ধিক্কারযোগ্য। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3931,37,143,'সে যদি (অনুতপ্ত হয়ে) আল্লাহর তাসবীহকারী না হত, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3932,37,144,'তাহলে নিশ্চিতই তাকে পুনরুত্থানের দিন পর্যন্ত মাছের পেটে থাকতে হত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3933,37,145,'অতঃপর আমি তাকে তৃণলতাহীন প্রান্তরে নিক্ষেপ করলাম, আর সে ছিল রুগ্ন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3934,37,146,'অতঃপর আমি তার উপর লাউ-কুমড়া জাতীয় লতা-পাতাযুক্ত একটা গাছ বের করে দিলাম।  ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3935,37,147,'অতঃপর তাকে এক লাখ বা তার চেয়ে বেশি লোকের কাছে পাঠালাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3936,37,148,'তারা ঈমান আনল, কাজেই আমি তাদেরকে কিছুকাল পর্যন্ত জীবন উপভোগ করতে দিলাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3937,37,149,'এখন তুমি তাদেরকে (অর্থাৎ মক্কার কাফিরদেরকে) জিজ্ঞেস কর ‘কন্যাগুলোই কি তোমাদের প্রতিপালকের জন্য, আর তাদের নিজেদের জন্য পুত্রগণ? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3938,37,150,'নাকি আমি ফেরেশতাদেরকে মেয়ে হিসেবে সৃষ্টি করেছিলাম আর তারা (অর্থাৎ মক্কার কাফিররা সেখানে) হাজির ছিল? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3939,37,151,'দেখ, তারা অবশ্যই তাদের মন-গড়া কথা বলে যে, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3940,37,152,'আল্লাহ সন্তানের জন্ম দিয়েছেন। তারা অবশ্যই মিথ্যেবাদী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3941,37,153,'তিনি কি পুত্রদের চেয়ে কন্যাদেরকেই বেশি পছন্দ করেছেন? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3942,37,154,'তোমাদের কী হয়েছে, তোমরা কেমন ফয়সালা করছ? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3943,37,155,'তাহলে কি তোমরা উপদেশ গ্রহণ করবে না? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3944,37,156,'(তোমরা যা বলছ তার স্বপক্ষে) তোমাদের কি সুস্পষ্ট দলীল-প্রমাণ আছে? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3945,37,157,'তোমরা সত্যবাদী হলে নিয়ে এসো তোমাদের কিতাব। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3946,37,158,'তারা আল্লাহ ও জ্বিন জাতির মাঝে একটা বংশ সম্পর্ক সাব্যস্ত করেছে, অথচ জ্বিনেরা ভালভাবে জানে যে, তাদেরকেও শাস্তির জন্য অবশ্যই হাজির করা হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3947,37,159,'তারা যা বলে আল্লাহ সে সব (দোষ-ত্রুটি) থেকে পবিত্র। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3948,37,160,'কিন্তু আল্লাহর একনিষ্ঠ বান্দারা এসব কথা বলে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3949,37,161,'তোমরা আর তোমরা যাদের ‘ইবাদাত কর তারা ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3950,37,162,'আল্লাহর সঙ্গে সম্পর্ক স্থাপনের ব্যাপারে তোমরা কাউকেও ফিতনায় ফেলতে পারবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3951,37,163,'পারবে কেবল তাকে, যে জ্বলন্ত আগুনে প্রবেশকারী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3952,37,164,'আমাদের (ফেরেশতাদের) প্রত্যেকের জন্য একটা নির্দিষ্ট স্থান আছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3953,37,165,'আমরা সারিবদ্ধভাবে দন্ডায়মান (খেদমত দেয়ার জন্য)। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3954,37,166,'আমরা অবশ্যই তাঁর পবিত্রতা ও মহিমা ঘোষণাকারী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3955,37,167,'এ লোকেরা তো বলত  ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3956,37,168,'আগের লোকেদের মত আমাদের কাছে যদি কোন কিতাব থাকত ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3957,37,169,'তাহলে আমরা অবশ্যই আল্লাহর একনিষ্ঠ বান্দাহ হতাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3958,37,170,'কিন্তু (এখন কুরআন আসার পর) তারা সেটা অমান্য ও অস্বীকার করল। তারা শীঘ্রই জানতে পারবে (এর পরিণাম)। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3959,37,171,'আমার প্রেরিত বান্দাহ্দের সম্পর্কে আমার এ কথা আগেই বলা আছে যে, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3960,37,172,'তাদেরকে অবশ্যই সাহায্য করা হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3961,37,173,'আর আমার সৈন্যরাই বিজয়ী হবে ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3962,37,174,'কাজেই কিছু সময়ের জন্য তুমি তাদেরকে উপেক্ষা কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3963,37,175,'আর তাদেরকে দেখতে থাক, তারা শীঘ্রই দেখতে পাবে (ঈমান ও কুফুরীর পরিণাম)। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3964,37,176,'তারা কি আমার শাস্তি তরান্বিত করতে চায়? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3965,37,177,'শাস্তি যখন তাদের উঠানে নেমে আসবে, তখন কতই না মন্দ হবে ঐ লোকেদের সকালটি যাদেরকে সতর্ক করা হয়েছিল! ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3966,37,178,'কাজেই কিছু সময়ের জন্য তুমি তাদেরকে উপেক্ষা কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3967,37,179,'আর দেখতে থাক, শীঘ্রই তারা দেখতে পাবে (ঈমান ও কুফুরীর পরিণাম)। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3968,37,180,'সকল সম্মান ও ক্ষমতার রব্ব, তোমার প্রতিপালক পবিত্র ও মহান সে সকল কথাবার্তা হতে যা তারা আরোপ করে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3969,37,181,'শান্তি বর্ষিত হোক রসূলদের প্রতি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3970,37,182,'আর যাবতীয় প্রশংসা বিশ্বজগতের প্রতিপালক আল্লাহর জন্যই। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3971,38,1,'স্ব-দ, উপদেশপূর্ণ কুরআনের শপথ- (এটা সত্য)। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3972,38,2,'কিন্তু কাফিররা আত্মম্ভরিতা আর বিরোধিতায় নিমজ্জিত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3973,38,3,'তাদের পূর্বে আমি কত মানবগোষ্ঠীকে ধ্বংস করে দিয়েছি, অবশেষে তারা (ক্ষমা লাভের জন্য) আর্তচিৎকার করেছিল, কিন্তু তখন পরিত্রাণ লাভের আর কোন অবকাশই ছিল না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3974,38,4,'আর তারা (এ ব্যাপারে) বিস্ময়বোধ করল যে, তাদের কাছে তাদেরই মধ্য হতে একজন সতর্ককারী এসেছে। কাফিরগণ বলল- ’এটা একটা যাদুকর, মিথ্যুক। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3975,38,5,'সে কি সব ইলাহকে এক ইলাহ বানিয়ে ফেলেছে? এটা বড়ই আশ্চর্য ব্যাপার তো!’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3976,38,6,'তাদের প্রধানরা প্রস্থান করে এই ব’লে যে, ‘তোমরা চলে যাও আর অবিচলিত চিত্তে তোমাদের ইলাহ্দের পূজায় লেগে থাক। অবশ্যই এ ব্যাপারটির পিছনে অন্য উদ্দেশ্য আছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3977,38,7,'এমন কথা তো আমাদের নিকট অতীতের মিল্লাতগুলো থেকে শুনিনি। এটা শুধু একটা মন-গড়া কথা। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3978,38,8,'আমাদের মধ্যে তার কাছেই কি বাণী পাঠানো হয়েছে?’ আসলে তারা আমার বাণীতে সন্দিহান, (তার কারণ) তারা এখনও আমার শাস্তির স্বাদ পায়নি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3979,38,9,'(রহমতের ভান্ডার আছে আমার কাছে, তাত্থেকে যাকে যতখানি ইচ্ছে আমি দেই) তাদের কাছে কি তোমার প্রতিপালকের রহমতের ভান্ডার আছে যিনি মহা প্রতাপশালী, অসীম দাতা? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3980,38,10,'কিংবা আকাশ ও পৃথিবী- এ দু’ এর মাঝে যা আছে তার সর্বময় ক্ষমতা কি তাদের আছে? তাহলে তারা বিশ্ব পরিচালনার উচ্চস্থানে উঠে পড়ুক। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3981,38,11,'(আরবের কাফিরদের) সম্মিলিত বাহিনীর এই দলটি এখানেই (অর্থাৎ এই মক্কা নগরীতেই একদিন) পরাজিত হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3982,38,12,'তাদের পূর্বে নূহের জাতি, ‘আদ ও বহু সেনা শিবিরের অধিপতি ফেরাউনও রসূলদেরকে মিথ্যে বলে অস্বীকার করেছিল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3983,38,13,'আর সামূদ, লূতের জাতি ও আইকাবাসী- এরা ছিল বিরাট বিরাট দল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3984,38,14,'এদের কেউই এমন নয় যারা রসূলদেরকে অস্বীকার করেনি। ফলে (তাদের উপর) আমার শাস্তি হয়েছিল অবধারিত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3985,38,15,'(আজ) এই লোকেরা তো প্রচন্ড একটা বিস্ফোরণের অপেক্ষায় আছে, (তা যখন ঘটবে) তাতে কোন বিরাম থাকবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3986,38,16,'এরা বলে, হে আমাদের প্রতিপালক! হিসাবের দিনের আগেই আমাদের প্রাপ্য (শাস্তি) আমাদেরকে তাড়াতাড়ি দিয়ে দিন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3987,38,17,'এরা যা বলে তাতে তুমি ধৈর্য ধারণ কর, আর আমার বান্দাহ দাঊদের কথা স্মরণ কর, সে ছিল শক্তি-সামর্থ্যের অধিকারী আর বড়ই আল্লাহর দিকে প্রত্যাবর্তনকারী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3988,38,18,'আমি পর্বতমালাকে কাজে নিয়োজিত করেছিলাম, তারা তার সঙ্গে সকাল-সন্ধ্যা আমার পবিত্রতা ও মহিমা ঘোষণা করত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3989,38,19,'আর পাখীরা সমবেত হত, সকলেই তার সঙ্গে আল্লাহ অভিমুখী হত (তাসবীহ করার মাধ্যমে)। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3990,38,20,'আমি তার রাজত্বকে সুদৃঢ় করেছিলাম, আর তাকে দিয়েছিলাম জ্ঞান-বুদ্ধি-বিচক্ষণতা আর বিচারকার্য ও কথাবার্তায় উত্তম সিদ্ধান্ত দানের যোগ্যতা। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3991,38,21,'বিবদমান লোকেদের কথা তোমার কাছে পৌঁছেছে কি? যখন তারা প্রাচীর ডিঙ্গিয়ে ‘ইবাদাতখানায় আসলো, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3992,38,22,'যখন তারা দাঊদের কাছে প্রবেশ করল, তাদেরকে দেখে সে ভয় পেয়ে গেল। তারা বলল- ভয় করবেন না, আমরা বিবাদের দু’পক্ষ, আমাদের একে অপরের উপর যুলম করেছে, কাজেই আমাদের মাঝে ন্যায্য বিচার করে দিন, অবিচার করবেন না, আর আমাদেরকে সোজা পথের নির্দেশ দিন।  ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3993,38,23,'এ হচ্ছে আমার ভাই, এর আছে নিরানব্বইটা দুম্বী, আর আমার আছে মাত্র একটা দুম্বী; তবুও সে বলে- এটি আমার তত্ত্বাবধানে দিয়ে দাও, আর সে যুক্তি-তর্কে আমাকে পরাস্ত করেছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3994,38,24,'দাঊদ বলল- তোমার (মাত্র) একটি দুম্বীকে তার দুম্বীর পালে যুক্ত করার দাবী করে (সে) তোমার প্রতি যুলম করেছে। শরীকদের অধিকাংশই সত্যিই পরস্পরের প্রতি বাড়াবাড়ি করে, কিন্তু যারা ঈমান আনে আর সৎ ‘আমাল করে তারা ব্যতীত, এদের সংখ্যা খুবই কম। দাঊদ বুঝতে পারল আমি তাকে পরীক্ষা করেছি। তখন সে তার প্রতিপালকের কাছে ক্ষমা প্রার্থনা করল, সাজদায় লুটিয়ে পড়ল ও তাঁর পানে ফিরে আসল।(সাজদাহ) ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3995,38,25,'তখন আমি তার সে অপরাধ ক্ষমা করে দিলাম। তার জন্য আমার কাছে অবশ্যই আছে নৈকট্য আর উত্তম প্রত্যাবর্তনস্থল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3996,38,26,'হে দাঊদ! আমি তোমাকে পৃথিবীতে (আমার) প্রতিনিধি করেছি, কাজেই তুমি মানুষের মধ্যে ন্যায়পরায়ণতার সঙ্গে শাসন-বিচার পরিচালনা কর, এবং প্রবৃত্তির অনুসরণ করো না। কেননা, তা  তোমাকে  আল্লাহর পথ হতে বিচ্যুত করে ফেলবে। যারা আল্লাহর পথ থেকে বিচ্যুত হয়, তাদের জন্য আছে কঠিন ‘আযাব, কারণ তারা হিসাব-নিকাশের দিনকে ভুলে গেছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3997,38,27,'আমি আকাশ, পৃথিবী ও এ দু’ এর মাঝে যা আছে তা অনর্থক সৃষ্টি করিনি। এ রকম ধারণা তো কাফিররা করে, কাজেই কাফিরদের জন্য আছে আগুনের দুর্ভোগ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3998,38,28,'যারা ঈমান আনে আর সৎ কাজ করে তাদেরকে কি আমি ওদের মত করব যারা দুনিয়াতে বিপর্যয় সৃষ্টি করে? আমি মুত্তাক্বীদের কি অপরাধীদের মত গণ্য করব? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (3999,38,29,'এটি একটি কল্যাণময় কিতাব তোমার কাছে অবতীর্ণ করেছি যাতে তারা এর আয়াতগুলোর প্রতি চিন্তা-ভাবনা করে, আর জ্ঞান-বুদ্ধিসম্পন্ন লোকেরা উপদেশ গ্রহণ করে থাকে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4000,38,30,'আমি দাঊদের জন্য দান করেছিলাম সুলাইমান। কতই না উত্তম বান্দাহ! বার বার (অনুশোচনাভরে) আল্লাহর দিকে প্রত্যাবর্তনকারী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4001,38,31,'যখন তার সামনে সন্ধ্যাকালে উৎকৃষ্ট জাতের দ্রুতগামী অশ্ব উপস্থিত করা হল, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4002,38,32,'তখন সে বলল- আমি আমার প্রতিপালকের স্মরণ হতে ধন-সম্পদকে বেশি ভালবেসে ফেলেছি, এমনকি সূর্য (রাতের) পর্দায় লুকিয়ে গেছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4003,38,33,'(সে তার সন্ধ্যাকালীন ‘ইবাদাত সম্পন্ন করে (বলল) ওগুলোকে আমার কাছে আবার এনে হাজির কর। তখন সে তাদের পায়ে ও গলায় হাত বুলাতে লাগল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4004,38,34,'আমি সুলাইমানকে পরীক্ষা করলাম (তার রাজত্ব কেড়ে নিয়ে) আর তার সিংহাসনের উপর রাখলাম একটি দেহ (শয়তানকে, কাজেই সুলাইমান কিছু সময়ের জন্য তার রাজত্ব হারাল) অতঃপর সে (আনুগত্য নিয়ে অনুশোচনা করে আল্লাহর পানে) প্রত্যাবর্তন করল (আর আল্লাহর অনুগ্রহে ফিরে পেল তার রাজত্ব ও সিংহাসন)। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4005,38,35,'সে বলল- হে আমার প্রতিপালক! আমাকে ক্ষমা কর, আর আমাকে এমন  রাজ্য  দান কর যা আমার  পরে আর কারো জন্য শোভনীয় হবে না। তুমি হলে পরম দাতা। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4006,38,36,'অতঃপর বাতাসকে তার অধীন করে দিলাম, তার আদেশে তা মৃদুমন্দ গতিতে প্রবাহিত হত, যেখানে সে ইচ্ছে করত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4007,38,37,'আর শয়ত্বানদেরকেও (তার বশীভূত করে দিলাম), সব ছিল নির্মাতা ও ডুবুরী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4008,38,38,'আর অন্যদেরকেও যারা ছিল শৃঙ্খলে আবদ্ধ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4009,38,39,'(আমি তাকে বললাম) এ সব আমারই দান। এত্থেকে তুমি যাকে ইচ্ছে দাও, কিংবা (না দিয়ে) নিজের কাছে রেখে দাও, তোমাকে কোন হিসেব দিতে হবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4010,38,40,'তার জন্য আমার কাছে অবশ্যই আছে নৈকট্য আর উত্তম প্রত্যাবর্তনস্থল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4011,38,41,'স্মরণ কর আমার বান্দা আইয়ূবের কথা, যখন সে তার প্রতিপালককে ডেকে বলেছিল- শয়ত্বান আমাকে কষ্ট আর ‘আযাবে ফেলেছে (অর্থাৎ আমার ধৈর্যচ্যুতি ঘটিয়ে আমাকে আল্লাহর অকৃতজ্ঞ বান্দাহ বানানোর জন্য কুমন্ত্রণা দিয়ে চলেছে)। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4012,38,42,'(আমি তাকে নির্দেশ দিলাম) তুমি তোমার পা দিয়ে যমীনে আঘাত কর, এই তো ঠান্ডা পানি, গোসলের জন্য আর পানের জন্য। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4013,38,43,'আমি তাকে দান করলাম তার পরিবার-পরিজন আর তাদের সাথে তাদের মত আরো, আমার রহমত স্বরূপ আর জ্ঞান-বুদ্ধিসম্পন্ন লোকেদের জন্য উপদেশ স্বরূপ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4014,38,44,'(আমি তাকে বললাম) কিছু ঘাস লও আর তা দিয়ে আঘাত কর, (আর তোমার স্ত্রীকে একশত বেত্রাঘাত করার শপথ) ভঙ্গ করো না। আমি তাকে পেয়েছিলাম পূর্ণ ধৈর্যশীল, কতই না উত্তম বান্দাহ, প্রকৃতই (আল্লাহ) অভিমুখী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4015,38,45,'স্মরণ কর আমার বান্দাহ ইবরাহীম, ইসহাক্ব ও ইয়া‘কূব-এর কথা- তারা ছিল শক্তি ও সূক্ষ্ণদর্শিতার অধিকারী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4016,38,46,'বস্তুত আমি তাদেরকে বৈশিষ্ট্যমন্ডিত করেছিলাম এক বিশেষ বৈশিষ্ট্যে- তা হল পরলোকের স্মরণ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4017,38,47,'আমার দৃষ্টিতে তারা ছিল আমার বাছাইকৃত উত্তম বান্দাহদের অন্তর্ভুক্ত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4018,38,48,'স্মরণ কর ইসমাঈল, ইয়াসা‘আ ও যুলকিফলের কথা- এরা সবাই ছিল উত্তমদের মধ্যে গণ্য। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4019,38,49,'এ হচ্ছে স্মৃতিচারণ, মুত্তাক্বীদের জন্য অবশ্যই আছে উত্তম প্রত্যাবর্তন স্থল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4020,38,50,'চিরস্থায়ী জান্নাত, তাদের জন্য উন্মুক্ত দ্বার। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4021,38,51,'সেখানে তারা হেলান দিয়ে বসবে, চাইবে প্রচুর ফলমূল আর পানীয়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4022,38,52,'আর তাদের পাশে থাকবে সতীসাধ্বী সংযতনয়না সমবয়স্কা রমণীগণ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4023,38,53,'এসব হল যা তোমাদেরকে হিসাবের দিনে দেয়ার ওয়া‘দা দেয়া হচ্ছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4024,38,54,'এ হল আমার দেয়া রিযক- যা কক্ষনো ফুরাবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4025,38,55,'সত্য বটে, এ সব (মুত্তাক্বীদের জন্য); আর আল্লাহদ্রোহীদের জন্য অবশ্যই আছে নিকৃষ্ট প্রত্যাবর্তনস্থল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4026,38,56,'জাহান্নাম, সেখানে তারা জ্বলবে, কতই না নিকৃষ্ট বিশ্রামস্থল! ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4027,38,57,'সত্য বটে, এসব (আল্লাহদ্রোহীদের জন্য), কাজেই সেখানে তারা পান করুক ফুটন্ত পানি ও রক্ত পুঁজ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4028,38,58,'এ ধরনের আরো অন্যান্য (শাস্তি) যা তাদের জন্য যথোপযুক্ত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4029,38,59,'(নিজেদের একদল অনুসারীকে জাহান্নামের দিকে আসতে দেখে জাহান্নামীরা বলাবলি করবে) এই তো এক বাহিনী তোমাদের সঙ্গে এসে প্রবেশ করছে। তাদের জন্য নেই কোন সংবর্ধনা, তারা আগুনে জ্বলবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4030,38,60,'অনুসারীরা বলবে- না, বরং তোমরাই (জ্বলে মর), তোমাদের জন্যও নেই কোন অভিনন্দন। আমাদের জন্য এ ব্যবস্থা আগে তোমরাই করে দিয়েছ। কতই না নিকৃষ্ট এই আবাসস্থল! ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4031,38,61,'তারা বলবে- হে আমাদের প্রতিপালক! আমাদের জন্য যে এ ব্যবস্থা এনে দিয়েছে তাকে জাহান্নামে দ্বিগুণ শাস্তি দাও। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4032,38,62,'তারা বলবে- ব্যাপার কী! আমরা যে লোকগুলোকে (দুনিয়ায়) খুব খারাপ বলে গণ্য করতাম তাদেরকে তো দেখছি না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4033,38,63,'আমরা কি তাদের সঙ্গে অযথাই ঠাট্টা-বিদ্রুপ করতাম, না তাদের ব্যাপারে আমাদের দৃষ্টিভ্রম ঘটেছে? (অর্থাৎ তারা হয়ত জাহান্নামেই আছে কিন্তু আমাদের চোখ তাদেরকে দেখতে পাচ্ছে না) ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4034,38,64,'এটা নিশ্চিত সত্য, জাহান্নামের বাসিন্দাদের এই বাগবিতন্ডা। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4035,38,65,'বল- আমি তো কেবল একজন সতর্ককারী, সার্বভৌম অপ্রতিরোধ্য এক ও একক আল্লাহ ছাড়া সত্য কোন ইলাহ নেই। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4036,38,66,'যিনি আকাশ ও পৃথিবী এবং এ দু’এর মাঝে যা আছে সব কিছুর প্রতিপালক- যিনি মহা পরাক্রমশালী, বড়ই ক্ষমাশীল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4037,38,67,'বল, এটা এক ভয়ানক সংবাদ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4038,38,68,'যাত্থেকে তোমরা মুখ ফিরিয়ে নিচ্ছ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4039,38,69,'(বল) আমি ঊর্ধ্ব জগতের কোন জ্ঞান রাখি না যখন তারা (অর্থাৎ ফেরেশতারা) বাদানুবাদ করছিল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4040,38,70,'আমার কাছে ওয়াহী করা হয়েছে যে, আমি কেবল একজন স্পষ্ট সতর্ককারী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4041,38,71,'স্মরণ কর, যখন তোমার প্রতিপালক ফেরেশতাদেরকে বললেন- আমি কাদা থেকে মানুষ সৃষ্টি করতে যাচ্ছি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4042,38,72,'আমি যখন তাকে সঠিকভাবে বানিয়ে ফেলব আর তার ভিতরে আমার রূহ ফুঁকে দেব, তখন তোমরা তার সামনে সাজদাহয় পড়ে যাবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4043,38,73,'তখন ফেরেশতারা সবাই সেজদা করল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4044,38,74,'ইবলীস ছাড়া। সে অহঙ্কার করল আর কাফিরদের অন্তর্ভুক্ত হয়ে গেল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4045,38,75,'আল্লাহ বললেন- হে ইবলীস! আমি যাকে নিজ হাতে সৃষ্টি করলাম তাকে সেজদা করতে কিসে তোমাকে নিষেধ করল? তুমি কি দম্ভ দেখালে, না তুমি খুব উচ্চ মানের অধিকারী হয়েছ? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4046,38,76,'সে বলল- আমি তার চেয়ে উত্তম, আপনি আমাকে আগুন থেকে সৃষ্টি করেছেন আর তাকে সৃষ্টি করেছেন মাটি থেকে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4047,38,77,'তিনি বললেন- তাহলে তুমি এখান থেকে বেরিয়ে যাও, তুমি হলে লাঞ্ছিত, বিতাড়িত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4048,38,78,'বিচার দিবস পর্যন্ত তোমার উপর থাকল আমার অভিশাপ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4049,38,79,'সে বলল- হে আমার প্রতিপালক! তাহলে আমাকে পুনরুত্থানের দিন পর্যন্ত সময় দিন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4050,38,80,'তিনি বললেন- তোমাকে সময় দেয়া হল, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4051,38,81,'সেদিন পর্যন্ত যার (আগমন) কাল সুনির্ধারিত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4052,38,82,'সে বলল- আপনার ক্ষমতার কসম! আমি ওদের সব্বাইকে অবশ্যই পথভ্রষ্ট করব। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4053,38,83,'তাদের মধ্যে আপনার একনিষ্ঠ বান্দাদের বাদে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4054,38,84,'তিনি বললেন- এটাই সত্য, আমি সত্যই বলি যে, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4055,38,85,'আমি তোমাকে আর তাদের (অর্থাৎ মানুষদের) মধ্যে যারা তোমাকে অনুসরণ করবে তাদের সব্বাইকে দিয়ে অবশ্যই জাহান্নাম পূর্ণ করব। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4056,38,86,'বল- আমি এর (অর্থাৎ সত্য-সঠিক পথের দিকে ডাকার) জন্য তোমাদের কাছে পারিশ্রমিক চাই না, আর আমি কোন ধোঁকাবাজ নই। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4057,38,87,'এটা তো বিশ্বজগতের জন্য কেবল উপদেশ বাণী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4058,38,88,'কিছুকাল পরেই এর সংবাদ তোমরা অবশ্য অবশ্যই জানতে পারবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4059,39,1,'এ কিতাব নাযিল হয়েছে মহাপরাক্রমশালী মহাবিজ্ঞানী আল্লাহর নিকট হতে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4060,39,2,'আমি তোমার প্রতি এ কিতাব অবতীর্ণ করেছি সত্যতা সহকারে, (এতে নেই কোন প্রকার মিথ্যে) কাজেই আল্লাহর ‘ইবাদাত কর দ্বীনকে (অর্থাৎ আনুগত্য, হুকুম পালন, দাসত্ব ও গোলামীকে) একমাত্র তাঁরই জন্য নির্দিষ্ট করে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4061,39,3,'জেনে রেখ, খালেস দ্বীন কেবল আল্লাহরই জন্য। যারা তাঁকে বাদ দিয়ে অন্যদেরকে অভিভাবক বানিয়ে নিয়েছে তারা বলে- আমরা তাদের ‘ইবাদাত একমাত্র এ উদ্দেশেই করি যে, তারা আমাদেরকে আল্লাহর নৈকট্যে পৌঁছে দেবে। (সত্য পথ থেকে সরে গিয়ে মিথ্যে পথ ও মতের জন্ম দিয়ে) তারা যে মতভেদ করছে, আল্লাহ তার চূড়ান্ত ফয়সালা করে দেবেন। যে মিথ্যেবাদী ও কাফির আল্লাহ তাকে সঠিক পথ দেখান না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4062,39,4,'আল্লাহ সন্তান গ্রহণ করার ইচ্ছে করলে তিনি তার সৃষ্টিকুল থেকে নিজ পছন্দ মত বেছে নিতেন। এসব থেকে তিনি পবিত্র। অপ্রতিরোধ্য ক্ষমতার অধিকারী তিনি এক ও একক আল্লাহ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4063,39,5,'তিনি আকাশ ও পৃথিবী সৃষ্টি করেছেন যথাযথভাবে। রাত দিনকে ঢেকে নেয়, আর দিন ঢেকে নেয় রাতকে। তিনিই নিয়ন্ত্রণ করেন সুরুজ আর চাঁদকে, প্রত্যেকেই চলছে নির্দিষ্ট সময় অনুসারে। জেনে রেখ, তিনি মহাপরাক্রমশালী, পরম ক্ষমাশীল (মহাশক্তিধর হওয়া সত্ত্বেও বার বার ক্ষমা করেন)। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4064,39,6,'তিনি তোমাদেরকে একই ব্যক্তি থেকে সৃষ্টি করেছেন। অতঃপর তার থেকে তিনি তার জুড়ি সৃষ্টি করেছেন। তিনি তোমাদের জন্য বানিয়েছেন আট গৃহপালিত পশু (চার) জোড়ায় জোড়ায়। তিনি তোমাদেরকে সৃষ্টি করেছেন তোমাদের মায়েদের গর্ভে, এক এক পর্যায়ে এক এক আকৃতি দিয়ে, তিন তিনটি অন্ধকার আবরণের মধ্যে। এই হল তোমাদের প্রতিপালক, সর্বময় কর্তৃত্ব তাঁরই, তিনি ছাড়া সত্যিকারের কোন ইলাহ নেই, কাজেই (ভুয়ো ক্ষমতার অধিকারী, দাম্ভিক ও স্বার্থান্বেষী মহল কর্তৃক) তোমাদেরকে কোন্ দিকে ফিরিয়ে নেয়া হচ্ছে? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4065,39,7,'তোমরা যদি কুফুরী কর তবে (জেনে রেখ), আল্লাহ তোমাদের মুখাপেক্ষী নন। তিনি তাঁর বান্দাহদের জন্য কুফুরী আচরণ পছন্দ করেন না, তোমরা যদি কৃতজ্ঞ হও, তবে তোমাদের জন্য তা তিনি পছন্দ করেন। একের (পাপের) বোঝা অন্যে বহন করবে না। শেষমেষ তোমাদেরকে তোমাদের পালনকর্তার কাছেই ফিরে যেতে হবে, তখন তিনি তোমাদেরকে জানিয়ে দেবেন তোমরা যা করছিলে। তিনি তো অন্তরের খবর পর্যন্ত জানেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4066,39,8,'দুঃখ-মুসিবত যখন মানুষকে স্পর্শ করে তখন সে তার প্রতিপালককে ডাকতে থাকে তাঁর প্রতি বড়ই একনিষ্ঠ হয়ে। অতঃপর তিনি যখন নিজ পক্ষ থেকে অনুগ্রহ দিয়ে তাকে ধন্য করেন, তখন পূর্বে সে যেজন্য তাঁকে ডেকেছিল তা ভুলে যায় এবং অন্যদেরকে আল্লাহর সমকক্ষ দাঁড় করায় তাঁর পথ থেকে পথভ্রষ্ট করার জন্য। বলে দাও, কুফুরীর জীবন কিছুকাল ভোগ করে নাও, (অতঃপর) তুমি তো হবে জাহান্নামের অধিবাসী। (এ ব্যক্তি ভাল, না ঐ ব্যক্তি) ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4067,39,9,'যে রাত্রির বিভিন্ন প্রহরে সেজদা ও দন্ডায়মান অবস্থায় বিনয় ও শ্রদ্ধা-ভক্তি প্রকাশ করে, আখিরাতকে ভয় করে, আর তার প্রতিপালকের অনুগ্রহ প্রত্যাশা করে? বল- যারা জানে আর যারা জানে না, তারা কি সমান? বিবেক-বুদ্ধিসম্পন্ন লোকেরাই কেবল উপদেশ গ্রহণ করে থাকে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4068,39,10,'বল, হে ঈমানদারগণ! তোমরা তোমাদের প্রতিপালককে ভয় কর। এ দুনিয়ায় যারা ভাল কাজ করবে, তাদের জন্য আছে কল্যাণ। আল্লাহর যমীন প্রশস্ত (এক এলাকায় ‘ইবাদাত-বন্দেগী করা কঠিন হলে অন্যত্র চলে যাও)। আমি ধৈর্যশীলদেরকে তাদের পুরস্কার অপরিমিতভাবে দিয়ে থাকি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4069,39,11,'বল- আমাকে আদেশ দেয়া হয়েছে আল্লাহর ‘ইবাদাত করতে তাঁর আনুগত্যে একনিষ্ঠ হয়ে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4070,39,12,'আমাকে আদেশ দেয়া হয়েছে আমি যেন সর্বাগ্রে মুসলিম হই। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4071,39,13,'বল- আমি যদি আমার প্রতিপালকের অবাধ্য হই, তবে আমি ভয়ঙ্কর দিনের শাস্তির ভয় করি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4072,39,14,'বল- আমি ‘ইবাদাত করি আল্লাহর বিশুদ্ধভাবে তাঁর প্রতি আমার আনুগত্যের মাধ্যমে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4073,39,15,'অতএব, তাঁকে বাদ দিয়ে তোমরা যার ইচ্ছে ‘ইবাদাত কর (এতে আল্লাহর কোন ক্ষতি হবে না, ক্ষতি তোমাদেরই হবে)। বল- যারা নিজেদেরকে আর নিজেদের পরিবার-পরিজনকে ক্বিয়ামতের দিনে ক্ষতিগ্রস্ত করে, তারাই তো ক্ষতিগ্রস্ত। জেনে রেখ, এটাই হল স্পষ্ট ক্ষতি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4074,39,16,'তাদের উপরেও থাকবে আগুনের স্তর, আর নীচেও থাকবে (আগুনের) স্তর। এ রকম পরিণতির ব্যাপারে আল্লাহ তাঁর বান্দাহদেরকে সাবধান করছেন। কাজেই হে আমার বান্দাহরা! আমাকে ভয় কর।  ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4075,39,17,'যারা তাগূতের দাসত্ব থেকে দূরে থাকে, আর আল্লাহর অভিমুখী হয়, সুসংবাদ তাদেরই জন্য। কাজেই সুসংবাদ দাও আমার বান্দাদেরকে ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4076,39,18,'যারা মনোযোগ দিয়ে কথা শুনে আর এর উত্তমগুলো মেনে চলে। ওরাই হল তারা আল্লাহ যাদেরকে সৎপথে পরিচালিত করেন আর ওরাই হল জ্ঞান-বুদ্ধিসম্পন্ন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4077,39,19,'শাস্তির ফয়সালা যার উপর অবধারিত হয়ে গেছে, যে আছে জাহান্নামের আগুনে তুমি কি তাকে রক্ষা করতে পার? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4078,39,20,'কিন্তু যারা তাদের প্রতিপালককে ভয় করে, তাদের জন্য রয়েছে প্রাসাদের পর প্রাসাদ, যেগুলোর উপর নির্মাণ করা হয়েছে প্রাসাদ আর প্রাসাদ, যার নিচ দিয়ে ঝর্ণাধারা প্রবাহিত। এটা আল্লাহর ও‘য়াদা, আল্লাহ ওয়াদা ভঙ্গ করেন না।  ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4079,39,21,'তুমি কি দেখ না আল্লাহ আকাশ হতে বৃষ্টি বর্ষণ করেন আর তা ঝর্ণা ধারায় যমীনে প্রবাহিত করেন, অতঃপর তা দিয়ে বিচিত্র রঙের ফসল উৎপন্ন করেন, অতঃপর তা শুকিয়ে যায়, তখন তোমরা তা হলুদ বর্ণ দেখ, শেষ পর্যন্ত তিনি ওগুলোকে খড়-ভুষিতে পরিণত করেন। এতে জ্ঞান-বুদ্ধিসম্পন্ন লোকেদের জন্য অবশ্যই নসীহত আছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4080,39,22,'ইসলামের জন্য আল্লাহ যার বক্ষ উন্মোচিত করে দিয়েছেন, যার ফলে সে তার প্রতিপালকের দেয়া আলোর উপর রয়েছে (সে কি তার সমান যে কঠোর হৃদয়ের)? ধ্বংস তাদের জন্য যাদের অন্তর আল্লাহ স্মরণের ব্যাপারে আরো শক্ত হয়ে গেছে। তারা আছে স্পষ্ট বিভ্রান্তিতে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4081,39,23,'আল্লাহ সর্বোত্তম বাণী অবতীর্ণ করেছেন- এমন কিতাব যা সামঞ্জস্যপূর্ণ, যার বিষয়াবলী পুনরাবৃত্তি করা হয়েছে। যারা তাদের প্রতিপালককে ভয় করে তাদের গা এতে শিউরে উঠে। তখন তাদের দেহ-মন আল্লাহর স্মরণের প্রতি বিনম্র হয়ে যায়। এ হল আল্লাহর হিদায়াত, যাকে ইচ্ছে তদ্দবারা হিদায়াত করেন। আর আল্লাহ যাকে পথহারা করেন, তার কোন পথপ্রদর্শক নেই। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4082,39,24,'ক্বিয়ামতের দিন যে ব্যক্তি তার (হাত পা বাঁধা থাকার কারণে) মুখমন্ডলের সাহায্যে ভয়ানক ‘আযাবের আঘাত ঠেকাতে চাইবে (সে কি তার মত যে এসব থেকে নিরাপদ)? যালিমদেরকে বলা হবে- তোমরা যা অর্জন করেছ তার স্বাদ গ্রহণ কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4083,39,25,'তাদের পূর্ববর্তীরাও (নুবুওয়াতকে) অস্বীকার করেছিল। অতঃপর তাদের কাছে এমন দিক থেকে ‘আযাব এসেছিল যা তারা একটু টেরও পায়নি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4084,39,26,'কাজেই আল্লাহ তাদেরকে দুনিয়ার জিন্দেগিতেই লাঞ্ছনার স্বাদ ভোগ করালেন। আর অবশ্যই আখিরাতের শাস্তি সবচেয়ে কঠিন। তারা যদি জানত! ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4085,39,27,'আমি এ কুরআনে মানুষের জন্য সব রকমের দৃষ্টান্ত ও উপমা উপস্থিত করেছি যাতে তারা উপদেশ গ্রহণ করে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4086,39,28,'আরবী ভাষায় (অবতীর্ণ) কুরআন, এতে নেই কোন বক্রতা (পেচানো কথা), যাতে তারা (অন্যায় অপকর্ম হতে) বেঁচে চলতে পারে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4087,39,29,'আল্লাহ একটা দৃষ্টান্ত দিচ্ছেনঃ এক ব্যক্তি যার মুনিব অনেক- যারা পরস্পরের বিরোধী। আরেক ব্যক্তি যার সম্পূর্ণ মালিকানা একজনের (উপর ন্যস্ত), তুলনায় এ দু’জন কি সমান? যাবতীয় প্রশংসা আল্লাহরই (যে তিনি আমাদেরকে নানান দেবদেবীর কবল থেকে রক্ষা ক’রে একমাত্র তাঁরই সঙ্গে সরাসরি যোগাযোগ স্থাপন করার জন্য আমাদেরকে নির্দেশ দিয়েছেন), কিন্তু মানুষদের অধিকাংশ (এ আসল সত্যটা) জানে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4088,39,30,'তুমিও মরবে আর তারাও মরবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4089,39,31,'অতঃপর ক্বিয়ামত দিবসে তোমরা তোমাদের প্রতিপালকের সম্মুখে বাদানুবাদ করবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4090,39,32,'যে ব্যক্তি আল্লাহর প্রতি মিথ্যারোপ করে আর সত্য সমাগত হওয়ার পর তা অস্বীকার করে তার চেয়ে বড় যালিম আর কে আছে? (এমন) কাফিরদের আবাসস্থল কি জাহান্নামে নয়? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4091,39,33,'যারা সত্য নিয়ে আগমণ করেছে এবং সত্যকে সত্য হিসেবে মেনে নিয়েছে, তারাই তো মুত্তাকী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4092,39,34,'তাদের জন্য তাদের প্রতিপালকের নিকট আছে যা তারা ইচ্ছে করবে। তা-ই হল সৎকর্মশীলদের প্রতিদান। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4093,39,35,'যাতে তারা যে সব মন্দ কাজ করেছে আল্লাহ্ তা মুছে দিতে পারেন, আর তারা যে সব সৎ কাজ করেছে তজ্জন্য তাদেরকে পুরস্কৃত করেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4094,39,36,'আল্লাহ কি তাঁর বান্দাহর জন্য যথেষ্ট নন? অথচ তোমাকে তারা আল্লাহর পরিবর্তে অন্যদের ভয় দেখায়। আল্লাহ যাকে পথহারা করেন তার জন্য কেউ পথ দেখাবার নেই।  ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4095,39,37,'আর আল্লাহ যাকে পথ দেখান, তাকে পথভ্রষ্ট করার কেউ নেই। আল্লাহ কি মহাশক্তিধর প্রতিশোধ গ্রহণকারী নন? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4096,39,38,'তুমি যদি তাদেরকে জিজ্ঞেস কর- আকাশ ও পৃথিবী সৃষ্টি করেছে কে? তারা অবশ্য অবশ্যই বলবে, আল্লাহ। তোমরা কি চিন্তা করে দেখেছ যে, আল্লাহ আমার ক্ষতি করতে চাইলে আল্লাহর পরিবর্তে তোমরা যাদেরকে ডাক, তারা কি সে ক্ষতি দূর করতে পারবে? অথবা তিনি আমার প্রতি অনুগ্রহ করতে চাইলে, তারা কি তাঁর অনুগ্রহ ঠেকাতে পারবে? বল, আমার জন্য আল্লাহ্ই যথেষ্ট, নির্ভরকারীরা তাঁর উপরই নির্ভর করে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4097,39,39,'বল, হে আমার সম্প্রদায়! তোমরা তোমাদের পথ ও মত অনুযায়ী কাজ করে যাও, আমিও কাজ করে যাচ্ছি, অচিরেই তোমরা জানতে পারবে ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4098,39,40,'কার উপর আসে অপমানজনক শাস্তি, আর কার উপর পতিত হয় স্থায়ী ‘আযাব।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4099,39,41,'আমি তোমার প্রতি কিতাব অবতীর্ণ করেছি মানুষের (শিক্ষা গ্রহণের) জন্য সত্য (দ্বীন) সহকারে। অতঃপর যে সঠিক পথে চলবে, নিজের কল্যাণের জন্যই চলবে। আর যে বিভ্রান্ত হবে, বিভ্রান্ত হবে কেবল নিজের ক্ষতি করার জন্য; তুমি তাদের (কাজের) জন্য যিম্মাদার নও। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4100,39,42,'আল্লাহ প্রাণ গ্রহণ করেন সেগুলোর মৃত্যুর সময়, আর যারা মরেনি তাদের নিদ্রাকালে। অতঃপর যার মৃত্যুর সিদ্ধান্ত হয়ে গেছে তার (প্রাণ) রেখে দেন, আর অন্যগুলো একটা নির্দিষ্ট সময়ের জন্য ফিরিয়ে দেন। যারা চিন্তা গবেষণা করে তাদের জন্য এতে বহু নিদর্শন আছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4101,39,43,'তারা কি আল্লাহকে ছাড়া (অন্যদেরকে নিজেদের মুক্তির জন্য) সুপারিশকারী বানিয়ে নিয়েছে? বল- তারা কোন কিছুর মালিক না হওয়া সত্ত্বেও, আর তারা না বুঝলেও? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4102,39,44,'বল- শাফা‘আত সম্পূর্ণ আল্লাহর ইখতিয়ারভুক্ত। আকাশ ও পৃথিবীর রাজত্ব তাঁরই, অতঃপর তাঁর কাছেই তোমাদেরকে ফিরিয়ে আনা হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4103,39,45,'এক আল্লাহর উল্লেখ করা হলেই যারা ক্বিয়ামতে বিশ্বাস করে না, তাদের অন্তর বিতৃষ্ণায় ভরে যায়। আর আল্লাহ ছাড়া অন্যান্য উপাস্যের উল্লেখ করা হলেই তারা আনন্দে উৎফুল্ল হয়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4104,39,46,'বল- হে আল্লাহ! আকাশ ও পৃথিবীর স্রষ্টা, দৃশ্য ও অদৃশ্যের জ্ঞানের অধিকারী, তুমি তোমার বান্দাহদের মাঝে মীমাংসা করে দেবে যে বিষয়ে তারা মতভেদ করছে. ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4105,39,47,'যারা অন্যায়কারী দুনিয়াতে যা কিছু আছে সমস্ত কিছু যদি তাদেরই হয়, আর তার সাথে আরো অত পরিমাণ হয়, তারা ক্বিয়ামতের কঠিন ‘আযাব থেকে বাঁচার জন্য মুক্তিপণ স্বরূপ দিতে চাইবে। সেখানে আল্লাহর নিকট থেকে তারা এমন কিছুর সম্মুখীন হবে যা তারা কক্ষনো অনুমানও করেনি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4106,39,48,'তাদের কৃতকর্মের মন্দ রূপ সেদিন প্রকাশ হয়ে পড়বে, আর তারা যা নিয়ে ঠাট্টা-বিদ্রূপ করত তাই তাদেরকে ঘিরে ফেলবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4107,39,49,'মানুষকে বিপদাপদ স্পর্শ করলে আমাকে ডাকে। অতঃপর আমি যখন তাকে আমার পক্ষ থেকে নি‘মাত দিয়ে ধন্য করি তখন সে বলে- আমার জ্ঞান গরিমার বদৌলতেই আমাকে তা দেয়া হয়েছে। না, তা নয়। এটা একটা পরীক্ষা (অনুগ্রহ লাভ করে কে আল্লাহর কৃতজ্ঞ হয় আর কে নিজের বড়াই প্রকাশ করে তা দেখার জন্য)। কিন্তু (এর গুঢ়তত্ত্ব) তাদের অধিকাংশই বুঝে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4108,39,50,'তাদের আগে যারা ছিল তারাও এ কথাই বলত। কিন্তু তারা যা করত তা তাদের কোনই কাজে আসেনি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4109,39,51,'তাদের কর্মের খারাপ পরিণাম তাদের উপর পতিত হয়েছিল। আর এদের মধ্যেও যারা যুলম করেছে তাদের কর্মের মন্দ পরিণাম এদেরই উপর পতিত হবে। এরা তা ব্যর্থ করতে পারবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4110,39,52,'এরা কি জানে না যে, আল্লাহ যার জন্য ইচ্ছে রিযক প্রশস্ত করেন আর (যার জন্য ইচ্ছে) সংকুচিত করেন? মু’মিন লোকদের জন্য অবশ্যই এতে বহু নিদর্শন আছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4111,39,53,'বল- হে আমার বান্দাগণ! যারা নিজেদের উপর বাড়াবাড়ি করেছ, তোমরা আল্লাহর রহমত থেকে নিরাশ হয়ো না। আল্লাহ সমস্ত গুনাহ ক্ষমা করে দেবেন। তিনি অতি ক্ষমাশীল, অতি দয়ালু। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4112,39,54,'তোমরা তোমাদের প্রতিপালকের অভিমুখী হও আর তাঁর অনুগত হও তোমাদের কাছে ‘আযাব আসার পূর্বে। (‘আযাব এসে গেলে) তোমাদেরকে সাহায্য করা হবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4113,39,55,'তোমাদের প্রতিপালকের নিকট হতে তোমাদের প্রতি যা অবতীর্ণ করা হয়েছে তাতে যাকে উত্তম আখ্যায়িত করা হয়েছে তোমরা সেগুলোর অনুসরণ কর তোমাদের কাছে অকস্মাৎ ‘আযাব এসে যাওয়ার পূর্বে যে বিষয়ে তোমরা টেরও পাবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4114,39,56,'যাতে কাউকে বলতে না হয়- হায় আফসোস! আমি আল্লাহর প্রতি (আমার কর্তব্যে) অবহেলা করেছিলাম, আর আমি তো ঠাট্টা বিদ্রূপকারীদের অন্তর্ভুক্ত ছিলাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4115,39,57,'অথবা এ কথা যেন বলতে না হয় যে, আল্লাহ যদি আমাকে সঠিক পথ দেখাতেন, তাহলে আমি অবশ্যই মুত্তাক্বীদের অন্তর্ভুক্ত হতাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4116,39,58,'অথবা শাস্তি দেখার পর কাউকে যেন বলতে না হয়, আমাকে যদি একবার (পৃথিবীতে) ফিরে যাবার সুযোগ দেয়া হত, তাহলে আমি সৎকর্মশীলদের অন্তর্ভুক্ত হতাম।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4117,39,59,'(তাকে উত্তর দেয়া হবে) না, বরং তোমার কাছে আমার নিদর্শন এসেছিল, তখন তুমি সেগুলোকে মিথ্যে বলে অস্বীকার করেছিলে, অহংকার করেছিলে আর কাফিরদের অন্তর্ভুক্ত ছিলে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4118,39,60,'যারা আল্লাহর প্রতি মিথ্যারোপ করে ক্বিয়ামতের দিনে তুমি তাদের মুখগুলো কালো দেখতে পাবে. অহংকারীদের আবাসস্থল কি জাহান্নামে নয়? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4119,39,61,'আর আল্লাহ মুত্তাক্বীদেরকে রক্ষা করবেন তাদের সফলতার কারণে। কোন খারাবী তাদেরকে স্পর্শ করবে না, আর তারা দুঃখিতও হবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4120,39,62,'আল্লাহ সব কিছুর স্রষ্টা আর তিনি সব কিছুর অভিভাবক এবং কর্ম সম্পাদনকারী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4121,39,63,'আসমান আর যমীনের কুঞ্জি তাঁরই হাতে, আর যারা আল্লাহর নিদর্শনগুলোকে অস্বীকার করে, তারাই তো ক্ষতিগ্রস্ত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4122,39,64,'বল, ওহে অজ্ঞরা! তোমরা কি আমাকে আল্লাহ ছাড়া অন্যের ‘ইবাদাত করার আদেশ করছ? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4123,39,65,'কিন্তু তোমার কাছে আর তোমাদের পূর্ববর্তীদের কাছে ওয়াহী করা হয়েছে যে, তুমি যদি (আল্লাহর) শরীক স্থির কর, তাহলে তোমার কর্ম অবশ্য অবশ্যই নিস্ফল হয়ে যাবে, আর তুমি অবশ্য অবশ্যই ক্ষতিগ্রস্তদের অন্তর্ভুক্ত হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4124,39,66,'না, বরং আল্লাহর ‘ইবাদাত কর, আর শুকরগুজারদের অন্তর্ভুক্ত হও। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4125,39,67,'তারা আল্লাহর যথাযোগ্য মর্যাদা দেয় না। ক্বিয়ামতের দিন সমগ্র পৃথিবী তাঁর হাতের মুষ্ঠিতে থাকবে, আর আকাশমন্ডলী থাকবে ভাঁজ করা অবস্থায় তাঁর ডান হাতে। মাহাত্ম্য তাঁরই, তারা যাদেরকে তাঁর শরীক করে তিনি তাদের বহু ঊর্ধ্বে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4126,39,68,'আর যখন শিঙ্গায় ফুঁৎকার দেয়া হবে তখন মুর্ছিত হয়ে পড়বে যারা আছে আকাশে আর যারা আছে যমীনে, তবে আল্লাহর ইচ্ছেয় এত্থেকে যে রেহাই পাবে তার কথা ভিন্ন। অতঃপর শিঙ্গায় আবার ফুঁ দেয়া হবে, তখন তারা উঠে দাঁড়িয়ে তাকাতে থাকবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4127,39,69,'পৃথিবী তার প্রতিপালকের জ্যোতিতে ঝলমল করে উঠবে, আর ‘আমালনামা সামনে আনা হবে। নবীগণ ও সাক্ষীগণকে উপস্থিত করা হবে। সকলের মাঝে ন্যায়পরায়ণতার সাথে বিচার করা হবে, তাদের প্রতি যুলম করা হবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4128,39,70,'প্রত্যেকের কাজের পূর্ণ প্রতিফল দেয়া হবে। লোকেরা যা করে তা তিনি খুব ভালভাবেই জানেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4129,39,71,'কাফিরদেরকে দলে দলে জাহান্নামের দিকে হাঁকিয়ে নিয়ে যাওয়া হবে। শেষে যখন তারা সেখানে পোঁছবে, তখন তার দরজাগুলো খুলে দেয়া হবে। তখন জাহান্নামের রক্ষীরা তাদেরকে বলবে- তোমাদের কাছে তোমাদেরই ভিতর থেকে কি রসূলগণ আসেননি যারা তোমাদের কাছে তোমাদের প্রতিপালকের আয়াত পড়ে শোনাতেন আর তোমাদেরকে যে এ দিনের সাক্ষাৎ করতে হবে এ সম্পর্কে তোমাদেরকে সতর্ক করতেন? তারা বলবে- হাঁ, এসেছিল। কিন্তু (এ স্বীকারোক্তি সত্ত্বেও) কাফিরদের প্রতি শাস্তির ফয়সালা অবধারিত হয়ে গেছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4130,39,72,'তাদেরকে বলা হবে- জাহান্নামের দরজা দিয়ে প্রবেশ কর, তোমাদেরকে চিরকাল এখানে থাকতে হবে। অহংকারীদের আবাসস্থল কতই না নিকৃষ্ট! ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4131,39,73,'যারা তাদের প্রতিপালককে ভয় করত তাদেরকে দলে দলে জান্নাতের দিকে নিয়ে যাওয়া হবে। যখন তারা সেখানে এসে পৌঁছবে, জান্নাতের দরজাগুলো (পূর্ব থেকেই) উন্মুক্ত (দেখতে পাবে)। জান্নাতের দ্বার রক্ষীরা বলবে- তোমাদের উপর শান্তি (বর্ষিত হোক), চমৎকার কাজ করেছ তোমরা, কাজেই চিরকালের জন্য এতে প্রবেশ কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4132,39,74,'(জান্নাতে প্রবেশ করে) তারা বলবে- সমস্ত প্রশংসা আল্লাহর যিনি তাঁর ও‘য়াদাকে সত্যিকারভাবে পূর্ণ করেছেন, আর আমাদেরকে (জান্নাতের) যমীনের অধিকারী বানিয়ে দিয়েছেন। আমরা জান্নাতের যেথায় ইচ্ছে বসবাসের জায়গা ক’রে নিতে পারি। সৎকর্মশীলদের প্রতিফল কতই না উত্তম! ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4133,39,75,'তুমি ফেরেশতাদেরকে ‘আরশের চারপাশ ঘিরে তাদের প্রতিপালকের মাহাত্ম্য ঘোষণা ও প্রশংসা করতে দেখতে পাবে। মানুষের মাঝে ন্যায়নিষ্ঠার সঙ্গে বিচার-ফয়সালা করা হবে। আর ঘোষণা দেয়া হবে যে, যাবতীয় প্রশংসা বিশ্বজগতের প্রতিপালকের জন্য। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4134,40,1,'হা-মীম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4135,40,2,'এ কিতাব নাযিল হয়েছে মহা প্রতাপের অধিকারী সর্বজ্ঞ আল্লাহর নিকট হতে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4136,40,3,'যিনি পাপ ক্ষমাকারী, তাওবাহ কবূলকারী, কঠোর শাস্তিদাতা, বড়ই অনুগ্রহশীল, তিনি ছাড়া সত্যিকারের কোন ইলাহ নেই, প্রত্যাবর্তন তাঁরই কাছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4137,40,4,'কাফিররা ছাড়া অন্য কেউ আল্লাহর আয়াত নিয়ে ঝগড়া করে না। কাজেই দেশে দেশে তাদের অবাধ বিচরণ তোমাকে যেন ধোঁকায় না ফেলে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4138,40,5,'এদের পূর্বে নূহের জাতি আর তাদের পরে বহু দল-গোষ্ঠী (রসূলদেরকে) অস্বীকার করেছিল। প্রত্যেক জাতি তাদের রসূলের বিরুদ্ধে চক্রান্ত করেছিল তাকে পাকড়াও করার জন্য, আর অসার অস্ত্রের সাহায্যে বিরোধিতায় লিপ্ত হয়েছিল তা দিয়ে সত্যকে খন্ডন করার জন্য। ফলে আমি তাদেরকে পাকড়াও করেছিলাম। অতঃপর দেখ, কেমন কঠোর ছিল আমার শাস্তি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4139,40,6,'এভাবে কাফিরদের ব্যাপারে তোমার প্রতিপালকের বাণী সত্য প্রমাণিত হল যে, তারা জাহান্নামের অধিবাসী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4140,40,7,'যারা ‘আরশ বহন করে আছে, আর যারা আছে তার চারপাশে, তারা তাঁর প্রশংসার সাথে তাঁর মাহাত্ম্য ঘোষণা করে আর তাঁর প্রতি ঈমান পোষণ করে আর মু’মিনদের জন্য ক্ষমা প্রার্থনা ক’রে বলে- হে আমাদের প্রতিপালক! তুমি তোমার রহমত ও জ্ঞান দিয়ে সব কিছুকে বেষ্টন করে রেখেছ, কাজেই যারা তাওবাহ করে ও তোমার পথ অনুসরণ করে তাদেরকে ক্ষমা কর, আর জাহান্নামের ‘আযাব থেকে তাদেরকে রক্ষা কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4141,40,8,'হে আমাদের প্রতিপালক! তুমি তাদেরকে আর তাদের পিতৃপুরুষ, স্বামী-স্ত্রী ও সন্তানাদির মধ্যে যারা সৎকাজ করেছে তাদেরকেও চিরস্থায়ী জান্নাতে প্রবেশ করান যার ওয়া‘দা তুমি তাদেরকে দিয়েছ; তুমি মহা পরাক্রমশালী, মহা বিজ্ঞ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4142,40,9,'সমস্ত খারাবী থেকে তাদেরকে রক্ষা কর। সেদিন তুমি যাকে সমস্ত খারাবী থেকে রক্ষা করলে, তার উপর তো দয়াই করলে। ওটাই হল বিরাট সাফল্য। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4143,40,10,'যারা কুফুরী করেছিল তাদেরকে ডাক দিয়ে বলা হবে- তোমাদের নিজেদের প্রতি তোমাদের ক্ষোভের চেয়ে (তোমাদের প্রতি) আল্লাহর ক্ষোভ অধিক, (কেননা) তোমাদেরকে যখন ঈমানের দিকে আহবান করা হয়েছিল, তখন তোমরা অস্বীকার করেছিলে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4144,40,11,'তারা বলবে, হে আমাদের প্রতিপালক! তুমি দু’বার আমাদের মৃত্যু দিয়েছ (জন্মের আগের মৃত অবস্থা আর জীবন শেষের মৃত্যু) আর আমাদেরকে দু’বার জীবন দিয়েছ। আমরা আমাদের পাপ স্বীকার করছি, (এখান থেকে) বের হওয়ার কোন পথ আছে কি?  ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4145,40,12,'(তখন তাদেরকে উত্তর দেয়া হবে) তোমাদের এ শাস্তির কারণ এই যে, যখন এক আল্লাহকে ডাকা হত, তখন তোমরা তা মেনে নিতে অস্বীকার করতে। আর যখন অন্যদেরকে তাঁর অংশীদার গণ্য করা হত, তখন তোমরা তাতে বিশ্বাস স্থাপন করতে। হুকুম দেয়ার মালিক আল্লাহ- যিনি সর্বোচ্চ, সর্বশ্রেষ্ঠ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4146,40,13,'তিনি তাঁর নিদর্শনাবলী তোমাদেরকে দেখান আর আকাশ থেকে তোমাদের জন্য রিযক অবতীর্ণ করেন. আল্লাহ-অভিমুখীরা ছাড়া কেউ উপদেশ গ্রহণ করে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4147,40,14,'কাজেই আল্লাহকে ডাক আনুগত্যকে একমাত্র তাঁরই জন্য নিদিষ্ট করো, যদিও কাফিরগণ তা অপছন্দ করে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4148,40,15,'তিনি সর্বোচ্চ মর্যাদার অধিকারী, ‘আরশের অধিপতি। তিনি তাঁর নির্দেশে তাঁর বান্দাদের যার প্রতি ইচ্ছে ওয়াহী প্রেরণ করেন যাতে সে সাক্ষাতের দিন সম্পর্কে সতর্ক করে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4149,40,16,'মানুষ যেদিন (ক্ববর থেকে) বের হয়ে আসবে, আল্লাহর কাছে তাদের কোন কিছুই গোপন থাকবে না। (সেদিন ঘোষণা দেয়া হবে) আজ একচ্ছত্র কর্তৃত্ব কার? (উত্তর আসবে) এক ও একক মহাপরাক্রমশালী আল্লাহর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4150,40,17,'প্রত্যেক ব্যক্তি যে কর্ম করেছে আজ তার প্রতিফল দেয়া হবে। আজ নেই কোন যুলম। আল্লাহ অতি দ্রুত হিসাব গ্রহণকারী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4151,40,18,'তাদেরকে সতর্ক কর সেই ঘনিয়ে আসা দিন সম্পর্কে যখন ওষ্ঠাগত প্রাণ নিয়ে তারা দুঃখ-কষ্ট সংবরণ করবে। যালিমদের জন্য কোন অন্তরঙ্গ বন্ধু থাকবে না, এমন কোন সুপারিশকারীও থাকবে না যার কথা গ্রহণ করা হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4152,40,19,'আল্লাহ চক্ষুর অন্যায় কর্ম সম্পর্কেও অবগত, আর অন্তর যা গোপন করে সে সম্পর্কেও। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4153,40,20,'আল্লাহ হক্ব বিচার করেন। তারা আল্লাহর পরিবর্তে যাদেরকে ডাকে তারা মোটেই বিচার করতে পারে না। আল্লাহ সব কিছু শোনেন, সব কিছু দেখেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4154,40,21,'তারা কি পৃথিবীতে ভ্রমণ করে না, তাহলে দেখতে পেত তাদের পূর্বে যারা ছিল তাদের পরিণাম কী হয়েছিল। তারা শক্তিতে আর স্মৃতি চিহ্নে ছিল এদের চেয়ে প্রবল। অতঃপর তাদের পাপের কারণে আল্লাহ তাদেরকে পাকড়াও করেছিলেন, আল্লাহর শাস্তি থেকে তাদেরকে বাঁচানোর কেউ ছিল না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4155,40,22,'এটা এজন্য যে, রসূলগণ তাদের কাছে স্পষ্ট (নিদর্শন) নিয়ে এসেছিল। কিন্তু তারা তাদেরকে প্রত্যাখ্যান করেছিল। তখন আল্লাহ তাদেরকে পাকড়াও করেছিলেন। তিনি প্রবল শক্তিধর, শাস্তিদানে কঠোর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4156,40,23,'আমি মূসাকে আমার নিদর্শন ও সুস্পষ্ট ফরমান দিয়ে পাঠিয়েছিলাম ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4157,40,24,'ফেরাউন, হামান ও কারূনের কাছে। কিন্তু তারা বলল (এ লোকটা) এক যাদুকর, ঘোর মিথ্যুক। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4158,40,25,'তারপর যখন মূসা তাদের কাছে আমার সত্যসহ আসল তখন তারা বলল- এর সঙ্গে যারা ঈমান এনেছে তাদের পুত্র সন্তানদের হত্যা কর, আর তাদের নারীদেরকে জীবিত রাখ। কাফিরদের ষড়যন্ত্র অকার্যকর ছাড়া আর কিছুই না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4159,40,26,'ফিরআউন বলল- ছেড়ে দাও আমাকে, আমি মূসাকে হত্যা করব, ডাকুক সে তার রব্বকে। আমি আশঙ্কা করছি, সে তোমাদের জীবন পদ্ধতিকে বদলে দেবে কিংবা দেশে বিপর্যয় বিশৃঙ্খলা সৃষ্টি করবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4160,40,27,'মূসা বলল- আমি আমার ও তোমাদের প্রতিপালকের আশ্রয় গ্রহণ করছি সকল দাম্ভিক অহংকারীদের হতে, যারা বিচার দিবসে বিশ্বাস করে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4161,40,28,'ফেরাউনের দলের এক মু’মিন ব্যক্তি- যে তার ঈমানকে গোপন রেখেছিল- বলল, তোমরা একজন লোককে শুধু কি এজন্য মেরে ফেলবে যে, সে বলে, আল্লাহ আমার প্রতিপালক। অথচ সে তোমাদের কাছে তোমাদের প্রতিপালকের পক্ষ হতে সুস্পষ্ট প্রমাণ নিয়ে এসেছে। সে যদি মিথ্যাবাদী হয় তাহলে তার মিথ্যা বলার পরিণাম সে নিজেই ভুগবে। আর সে যদি সত্যবাদী হয়, তাহলে সে তোমাদেরকে যে শাস্তির ভয় দেখাচ্ছে তার কিছু না কিছু তোমাদের উপর পড়বেই। যে সীমালঙ্ঘন করে আর মিথ্যে বলে আল্লাহ তাকে সঠিক পথ দেখান না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4162,40,29,'হে আমার জাতির লোকেরা! আজ তোমাদেরই কর্তত্ব চলছে, দেশে আজ তোমরাই বিজয়ী শক্তি। কিন্তু আল্লাহর শাস্তি যদি এসেই পড়ে, তাহলে তাত্থেকে আমাদেরকে কে রক্ষে করবে? ফেরাউন বলল- আমি তোমাদেরকে শুধু তা-ই বলছি, আমি নিজে যা বুঝেছি; আমি তোমাদেরকে সত্যপথই দেখাচ্ছি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4163,40,30,'যে লোকটি ঈমান এনেছিল সে বলল- হে আমার জাতির লোকেরা! আগের জাতিগুলোর উপর যেমন (শাস্তির) দিন এসেছিল, আমি তোমাদের উপরও সে রকম (বিপর্যয়ের) আশঙ্কা করছি ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4164,40,31,'যা এসেছিল নূহ, ‘আদ, সামূদ জাতি আর তাদের পরবর্তীদের উপর। আল্লাহ তো বান্দাহদের উপর যুলম করতে চান না (বরং তাদের নিজেদের অন্যায় অপকর্মের কারণেই আল্লাহ তাদেরকে শাস্তি দেন)। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4165,40,32,'হে আমার জাতি! আমি আশঙ্কা করছি তোমাদের আর্ত চিৎকার আর কান্নাকাটি করার একটি দিনের। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4166,40,33,'যেদিন তোমরা পিছন ফিরে পালিয়ে যাবে। কিন্তু আল্লাহ (’র কব্জা) থেকে তোমাদের রক্ষাকারী কেউ হবে না, আর আল্লাহ যাকে বিপথগামী করেন, তার জন্য কোন পথপ্রদর্শনকারী নেই। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4167,40,34,'ইতোপূর্বে ইউসুফ তোমাদের কাছে সুস্পষ্ট নিদর্শন নিয়ে এসেছিল। কিন্তু সে তোমাদের কাছে যা নিয়ে এসেছিল, সে ব্যাপারে তোমরা সন্দেহ পোষণ করেই চললে। শেষ পর্যন্ত যখন তার মৃত্যু হল তখন তোমরা বললে- ওর পরে আল্লাহ আর কক্ষনো কোন রসূল পাঠাবেন না। সীমালঙ্ঘনকারী ও সন্দেহবাদীদেরকে আল্লাহ এভাবেই পথভ্রষ্ট করেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4168,40,35,'যারা নিজেদের কাছে কোন যুক্তি-প্রমাণ না আসলেও আল্লাহর নিদর্শনাবলীর ব্যাপারে বাক-বিতন্ডা করে। আল্লাহর দৃষ্টিতে আর মু’মিনদের দৃষ্টিতে (এ আচরণ) খুবই ঘৃণিত। আল্লাহ এভাবে প্রত্যেক দাম্ভিক স্বৈরাচারীর অন্তরের উপর মোহর মেরে দেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4169,40,36,'ফেরাউন বলল- হে হামান! তুমি আমার জন্য এক সুউচ্চ ইমারত তৈরি কর যাতে আমি উপায় পেয়ে যাই ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4170,40,37,'আকাশে উঠার উপায়, যাতে আমি মূসার ইলাহকে দেখতে পাই; আমি মূসাকে অবশ্যই মিথ্যাবাদী মনে করি। এভাবে ফেরাউনের জন্য তার মন্দ কাজকে সুশোভিত করা হয়েছিল আর সঠিক পথ থেকে তাকে বিরত রাখা হয়েছিল। ফেরাউনের অপকৌশল কেবল তার ধ্বংসই ডেকে এনেছিল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4171,40,38,'যে লোকটি ঈমান এনেছিল সে আরো বলল- হে আমার জাতির লোকেরা! তোমরা আমার অনুসরণ কর, আমি তোমাদেরকে সঠিক পথ দেখাচ্ছি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4172,40,39,'হে আমার সম্প্রদায়! পার্থিব এ জীবন (অস্থায়ী) ভোগ্য বস্তু মাত্র, আর আখিরাতই হল চিরকালীন আবাসস্থল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4173,40,40,'যে খারাপ কাজ করবে তাকে কাজের অনুপাতেই প্রতিফল দেয়া হবে। পুরুষ হোক আর নারী হোক যে ব্যক্তিই সৎ কাজ করবে (উপরন্তু) সে মু’মিনও, তাহলে তারাই জান্নাতে প্রবেশ করবে, তার মধ্যে তারা বে-হিসাব রিযক প্রাপ্ত হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4174,40,41,'হে আমার সম্প্রদায়! বড়ই আশ্চর্য! আমি তোমাদেরকে ডাকছি মুক্তির দিকে, আর তোমরা ডাকছো আগুনের দিকে।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4175,40,42,'তোমরা আমাকে ডাকছ যেন আমি আল্লাহর সাথে কুফুরী করি এবং তাঁর অংশী স্থাপন করি যে সম্পর্কে আমার কোন জ্ঞান নেই। অপরপক্ষে আমি তোমাদেরকে ডাকছি মহাপরাক্রান্ত ক্ষমাশীল (আল্লাহর) দিকে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4176,40,43,'কোন সন্দেহ নেই তোমরা আমাকে ডাকছ শুধু তার দিকে যে আহবান পাওয়ার যোগ্য নয়- না দুনিয়াতে, না আখিরাতে। আমাদের প্রত্যাবর্তন তো হল আল্লাহর দিকে আর সীমালঙ্ঘনকারীরা হল জাহান্নামের বাসিন্দা। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4177,40,44,'শীঘ্রই তোমরা স্মরণ করবে আমি তোমাদেরকে যা বলছি। আমি আমার নিজের ব্যাপারটা আল্লাহর উপর সোপর্দ করছি (আমার বাঁচা-মরার জন্য আমি মোটেও ভাবি না)। আল্লাহ তাঁর বান্দাহদের উপর (সদাসর্বদা) দৃষ্টি রাখেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4178,40,45,'অতঃপর আল্লাহ তাকে তাদের ষড়যন্ত্রের খারাবী থেকে রক্ষা করলেন, আর কঠিন শাস্তি ফেরাউনের লোকজনকে চতুর্দিক থেকে ঘিরে ফেলল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4179,40,46,'(ক্ববরে) তাদেরকে সকাল-সন্ধ্যায় জাহান্নামের সামনে উপস্থিত করা হয় আর যেদিন ক্বিয়ামত সংঘটিত হবে সেদিন (বলা হবে) ফেরাউনের জাতি গোষ্ঠীকে কঠিন ‘আযাবে প্রবিষ্ট কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4180,40,47,'জাহান্নামে তারা যখন বাক-বিতন্ডা করবে, তখন দুর্বলেরা দাপটওয়ালাদের বলবে- আমরা তো তোমাদেরই অনুসারী ছিলাম, কাজেই তোমরা কি (এখন) আমাদের আগুনের শাস্তির কিছুটা শেয়ার নেবে? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4181,40,48,'দাপটওয়ালারা বলবে- আগুনে আমরা সবাই তো আছি, আল্লাহ বান্দাহদের বিচার তো করেই ফেলেছেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4182,40,49,'আগুনের বাসিন্দারা জাহান্নামের রক্ষীদের বলবে- তোমাদের প্রতিপালকের নিকট দু‘আ কর, তিনি যেন আমাদের থেকে একদিনের শাস্তি কমিয়ে দেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4183,40,50,'রক্ষীগণ বলবে- রসূলগণ কি স্পষ্ট প্রমাণ নিয়ে তোমাদের কাছে আসেনি? তারা (উত্তরে) বলবে, হাঁ (এসেছিল)। জাহান্নামের রক্ষীরা বলবে- তাহলে তোমরাই দু‘আ কর, কাফিরদের দু‘আ নিষ্ফলই হয়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4184,40,51,'আমি আমার রসূলদেরকে আর মু’মিনদেরকে অবশ্যই সাহায্য করব দুনিয়ার জীবনে আর (ক্বিয়ামতে) যে দিন সাক্ষীরা দাঁড়াবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4185,40,52,'যেদিন যালিমদের ওজর-আপত্তি কোন উপকারে আসবে না। তাদের জন্য আছে লা‘নত, তাদের জন্য আছে নিকৃষ্ট বাসস্থান। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4186,40,53,'ইতোপূর্বে আমি মূসাকে দিয়েছিলাম- পথ নির্দেশ আর বানী ইসরাঈলকে করেছিলাম (মূসার নিকট প্রদত্ত) কিতাবের উত্তরাধিকারী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4187,40,54,'(সে কিতাব ছিল) জ্ঞানবুদ্ধিসম্পন্ন লোকেদের জন্য সঠিক পথের দিশারী ও উপদেশ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4188,40,55,'কাজেই তুমি ধৈর্য ধারণ কর, (তুমি দেখতে পাবে) আল্লাহর ও‘য়াদা সত্য। তুমি তোমার ভুল-ভ্রান্তির জন্য ক্ষমা প্রার্থনা কর আর সকাল-সন্ধ্যা তোমার প্রতিপালকের প্রশংসা সহকারে পবিত্রতা বর্ণনা কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4189,40,56,'কোন সুস্পষ্ট প্রমাণ না পেয়েই যারা আল্লাহর আয়াত সম্পর্কে তর্ক করে, তাদের বুকের ভিতর আছে কেবল অহংকার, কিন্তু তারা তা (অর্থাৎ প্রকৃত বড়ত্ব ও শ্রেষ্ঠত্ব আল্লাহর দয়া ছাড়া) কক্ষনো অর্জন করতে পারে না। কাজেই আল্লাহর কাছে আশ্রয় চাও, (কারণ) তিনি সব কিছু শোনেন, সব কিছু দেখেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4190,40,57,'অবশ্যই আসমান ও যমীনের সৃষ্টি মানুষ সৃষ্টির চেয়ে বড় (ব্যাপার)। কিন্তু অধিকাংশ মানুষ (অজ্ঞতা ও চিন্তা না করার কারণে) তা জানে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4191,40,58,'অন্ধ আর চক্ষুষ্মান সমান নয়, (সমান নয়) যারা ঈমান আনে ও সৎ কাজ করে আর যারা অন্যায়কারী। উপদেশ থেকে শিক্ষা তোমরা সামান্যই গ্রহণ কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4192,40,59,'ক্বিয়ামত অবশ্যই আসবে, এতে কোন সন্দেহ নেই, কিন্তু অধিকাংশ লোক (তা) বিশ্বাস করে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4193,40,60,'তোমার প্রতিপালক বলেন- তোমরা আমাকে ডাকো, আমি (তোমাদের ডাকে) সাড়া দেব। যারা অহংকারবশতঃ আমার ‘ইবাদাত করে না, নিশ্চিতই তারা লাঞ্ছিত অবস্থায় জাহান্নামে প্রবেশ করবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4194,40,61,'আল্লাহ যিনি তোমাদের জন্য রাত্রি বানিয়েছেন যাতে তোমরা তাতে প্রশান্তি লাভ করতে পার, আর দিনকে করেছেন আলোকময়। আল্লাহ মানুষদের প্রতি বড়ই অনুগ্রহশীল, কিন্তু অধিকাংশ মানুষই (আল্লাহর অনুগ্রহ লাভের পরও) কৃতজ্ঞতা স্বীকার করে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4195,40,62,'এ হলেন আল্লাহ, তোমাদের প্রতিপালক, সব কিছুর সৃষ্টিকর্তা। তিনি ছাড়া সত্যিকারের কোন ইলাহ নেই; এমতাবস্থায় তোমাদেরকে সত্য থেকে কীভাবে বিভ্রান্ত করা হচ্ছে? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4196,40,63,'বিভ্রান্ত এভাবেই করা হয় তাদেরকে যারা আল্লাহর আয়াতকে অস্বীকার করে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4197,40,64,'আল্লাহ যিনি তোমাদের জন্য যমীনকে করেছেন মেঝে, আর আকাশকে করেছেন ছাদ। তিনি তোমাদেরকে আকৃতি দিয়েছেন, অতঃপর তোমাদের আকৃতিকে সুন্দর করেছেন। তিনি পবিত্র বস্তু থেকে তোমাদেরকে রিযক দান করেন। এ হলেন আল্লাহ তোমাদের প্রতিপালক। কাজেই মহিমা গৌরব আল্লাহর যিনি বিশ্ব জগতের প্রতিপালক। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4198,40,65,'চিরঞ্জীব তিনি, তিনি ছাড়া সত্যিকারের কোন ইলাহ নেই। কাজেই তাঁকে ডাক আনুগত্যকে একমাত্র তাঁরই জন্য বিশুদ্ধ করে। যাবতীয় প্রশংসা বিশ্বজগতের প্রতিপালক আল্লাহরই জন্য। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4199,40,66,'বল, আমার প্রতিপালকের কাছ থেকে আমার কাছে সুস্পষ্ট প্রমাণ এসেছে, এমতাবস্থায় আমাকে তাদের ‘ইবাদাত করতে নিষেধ করা হয়েছে আল্লাহকে বাদ দিয়ে যাদেরকে তোমরা আহবান কর। আর আমাকে আদেশ দেয়া হয়েছে বিশ্বজগতের প্রতিপালকের নিকট আত্মসমর্পণ করার জন্য। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4200,40,67,'তিনিই তোমাদেরকে সৃষ্টি করেছেন মাটি থেকে, অতঃপর শুক্রবিন্দু থেকে, অতঃপর জমাট বাঁধা রক্ত থেকে, অতঃপর তোমাদেরকে বের করে এনেছেন শিশুরূপে, অতঃপর তিনি তোমাদের বৃদ্ধি দান করেন যাতে তোমরা তোমাদের পূর্ণ শক্তির বয়সে পৌঁছতে পার, অতঃপর আরো বৃদ্ধি দেন যাতে তোমরা বৃদ্ধ হও। তোমাদের মধ্যে কারো কারো আগেই মৃত্যু ঘটান যাতে তোমরা তোমাদের জন্য নির্দিষ্ট সময়ে পৌঁছে যাও আর যাতে তোমরা (আল্লাহর সৃষ্টি কুশলতা) অনুধাবন কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4201,40,68,'তিনিই জীবন দেন ও মৃত্যু ঘটান। যখন কোন কিছু করার সিদ্ধান্ত করেন, তার জন্য তিনি বলেন- হও, তখন তা হয়ে যায়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4202,40,69,'তুমি কি তাদের প্রতি লক্ষ্য কর না যারা আল্লাহর নির্দশনগুলো সম্পর্কে বাক-বিতন্ডা করে? (সত্য থেকে) তাদেরকে কীভাবে ফিরিয়ে নেয়া হচ্ছে? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4203,40,70,'যারা কিতাবকে আর আমি আমার রসূলদেরকে যা দিয়ে পাঠিয়েছি তাকে অস্বীকার করে, তারা শীঘ্রই জানতে পারবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4204,40,71,'যখন তাদের গলায় থাকবে বেড়ি আর শিকল; তাদের টেনে নিয়ে যাওয়া হবে ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4205,40,72,'ফুটন্ত পানিতে, অতঃপর তাদেরকে আগুনে দগ্ধ করা হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4206,40,73,'অতঃপর তাদেরকে বলা হবে- তারা কোথায় তোমরা যাদেরকে (আল্লাহর) শরীক গণ্য করতে ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4207,40,74,'আল্লাহকে বাদ দিয়ে? (উত্তরে) তারা বলবে- তারা আমাদের কাছ থেকে উধাও হয়ে গেছে। না, আমরা আগে (যার অস্তিত্ব আছে এমন) কোন কিছুকেই ডাকিনি। এভাবে আল্লাহ কাফিরদেরকে পথভ্রষ্ট হওয়ার সুযোগ দেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4208,40,75,'এর কারণ এই যে, তোমরা অসত্য নিয়ে উল্লাস করতে, আর এজন্য যে, তোমরা গর্ব অহংকার করতে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4209,40,76,'তোমরা জাহান্নামের দরজাগুলো দিয়ে প্রবেশ কর, চিরকাল তার ভিতরে থাকার জন্য। দাম্ভিকদের বাসস্থান কতই না নিকৃষ্ট! ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4210,40,77,'অতএব তুমি ধৈর্য ধারণ কর, (কেননা) আল্লাহর ওয়া‘দা সত্য। অতঃপর আমি তাদেরকে (শাস্তির) যে ওয়া‘দা দিয়েছি তার কিছু যদি তোমাকে (এ পৃথিবীতেই) দেখিয়ে দেই অথবা (তাদেরকে শাস্তি দেয়ার পূর্বেই) তোমার মৃত্যু ঘটাই, (উভয় অবস্থাতেই) তাদেরকে আমার কাছেই ফিরে আসতে হবে। (আমার শাস্তি থেকে তারা রেহাই পাবে না)। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4211,40,78,'আমি তোমার পূর্বে অনেক রসূল পাঠিয়েছিলাম। তাদের মধ্যে কারো কারো কাহিনী আমি তোমার কাছে বর্ণনা করেছি, তাদের মধ্যে কারো কারো কথা আমি তোমার কাছে বর্ণনা করিনি। আল্লাহর অনুমতি ছাড়া কোন নিদর্শন নিয়ে আসা কোন রসূলের পক্ষে সম্ভব নয়। আল্লাহর নির্দেশ যখন জারী হয়ে যায়, তখন ন্যায়সঙ্গতভাবে (বিষয়টির) ফয়সালা করা হয়। আর যারা মিথ্যার উপর দাঁড়িয়েছিল তারা তখনকার তখনই ক্ষতিগ্রস্ত হয়ে পড়ে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4212,40,79,'আল্লাহ- যিনি তোমাদের জন্য গবাদি পশু সৃষ্টি করেছেন, যাতে তোমরা তাদের কতকগুলোর উপর আরোহণ করতে পার, আর কতকগুলো থেকে আহার করতে পার। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4213,40,80,'ওগুলোতে তোমাদের জন্য আছে বহু উপকার। তোমরা তোমাদের অন্তরে যে প্রয়োজনবোধ কর, যাতে ওগুলোর দ্বারা তা মিটাতে পার। ওগুলোর উপর আর নৌযানে তোমাদেরকে বহন করা হয়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4214,40,81,'তিনি তোমাদেরকে তাঁর নিদর্শন দেখান। কাজেই আল্লাহর কোন্ নিদর্শনকে তোমরা অস্বীকার কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4215,40,82,'তারা কি পৃথিবীতে ভ্রমণ করে না? করলে তারা দেখত, তাদের পূর্ববর্তীদের পরিণতি কী হয়েছিল। দুনিয়ায় এদের চেয়ে তারা সংখ্যায় অধিক ছিল, আর শক্তি সামর্থ্য ও কীর্তি চিহ্নে বেশি প্রবল ছিল। কিন্তু তারা যা অর্জন করেছিল তা তাদের কোন উপকারে আসেনি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4216,40,83,'তাদের কাছে যখন তাদের রসূলগণ স্পষ্ট নিদর্শন নিয়ে আসল, তখন তারা তাদের নিজেদের কাছে যে জ্ঞান ও বিদ্যা ছিল তাতেই উৎফুল্ল হয়ে উঠল। অতঃপর যা নিয়ে তারা ঠাট্টা বিদ্রুপ করত তা-ই তাদেরকে ঘিরে ফেলল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4217,40,84,'আমার শাস্তি তারা যখন দেখল তখন তারা বলল- আমরা এক আল্লাহর প্রতি ঈমান আনলাম আর যাদেরকে আমরা (আল্লাহর) শরীক গণ্য করতাম তাদেরকে প্রত্যাখ্যান করলাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4218,40,85,'তারা যখন আমার শাস্তি দেখল তখন তাদের ঈমান (গ্রহণ) তাদের কোন উপকারে আসল না। আল্লাহর (এ) বিধান তাঁর বান্দাহদের উপর (বহু) পূর্ব হতেই (কার্যকর হয়ে) চলে আসছে। আর এ ক্ষেত্রে কাফিররাই ক্ষতিগ্রস্ত হয়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4219,41,1,'হা-মীম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4220,41,2,'পরম দয়াময়, পরম দয়ালুর নিকট হতে অবতীর্ণ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4221,41,3,'এক কিতাব, যার আয়াতগুলো বিস্তারিতভাবে ব্যাখ্যাকৃত, আরবী ভাষার কুরআন, জ্ঞানসম্পন্ন মানুষদের জন্য। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4222,41,4,'সুসংবাদবাহী ও সাবধানকারী। কিন্তু ওদের অধিকাংশই (এ কুরআন থেকে) মুখ ফিরিয়ে নিয়েছে, কাজেই ওরা শুনবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4223,41,5,'তারা বলে, তুমি আমাদেরকে যার দিকে ডাক দিচ্ছ তাত্থেকে আমাদের দিল আবরণে ঢাকা আছে, আর আমাদের কানে আছে বধিরতা, আমাদের আর তোমাদের মাঝে আছে এক পর্দা (অর্থাৎ তোমার দীন প্রচারের কারণে আমাদের ও তোমাদের মাঝে বিচ্ছেদ ঘটে গেছে), কাজেই তুমি তোমার কাজ কর, আমরা আমাদের কাজ করি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4224,41,6,'বল, আমি তোমাদের মতই একজন মানুষ। (পার্থক্য শুধু এই যে) আমার কাছে ওয়াহী করা হয় যে, তোমাদের ইলাহ কেবল এক ইলাহ; কাজেই তোমরা তাঁরই সরল সঠিক পথে চল, তাঁর কাছে ক্ষমা প্রার্থনা কর। ধ্বংস তাদের জন্য যারা আল্লাহর সাথে অন্যদেরকে শরীক গণ্য করে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4225,41,7,'যারা যাকাত দেয় না, আর তারা আখিরাত অমান্য করে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4226,41,8,'যারা ঈমান আনে আর নেক কাজ করে, তাদের জন্য আছে এমন পুরস্কার যা কোন দিনও বন্ধ হবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4227,41,9,'বল- তোমরা কি তাঁকে অস্বীকারই করছ যিনি যমীনকে সৃষ্টি করেছেন দু’দিনে আর তাঁর সমকক্ষ বানাচ্ছ? তিনিই তো বিশ্বজগতের রব্ব। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4228,41,10,'(যমীন সৃষ্টির পর) তার বুকে তিনি সৃদৃঢ় পর্বতমালা স্থাপন করেছেন, যমীনকে বরকতমন্ডিত করেছেন আর তাতে প্রার্থীদের প্রয়োজন মুতাবেক নির্দিষ্ট পরিমাণ খাদ্য সঞ্চিত করেছেন চার দিনে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4229,41,11,'তারপর নজর দিয়েছেন আকাশের দিকে যখন তা ছিল ধোঁয়া (’র মত)। তখন তিনি আকাশ আর পৃথিবীকে বললেন- আমার অনুগত হও, ইচ্ছায় অথবা অনিচ্ছায়। উভয়ে বলল- আমরা স্বেচ্ছায় অনুগত হলাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4230,41,12,'অতঃপর তিনি আকাশমন্ডলীকে সাত আকাশে বিন্যস্ত করলেন দু’দিনে আর প্রত্যেক আকাশকে তার বিধি-ব্যবস্থা ওয়াহীর মাধ্যমে প্রদান করলেন। আমি আলোকমালার সাহায্যে দুনিয়ার আকাশের শোভাবর্ধন করলাম আর সুরক্ষার (ও ব্যবস্থা করলাম)। এ হল মহা পরাক্রমশালী সর্বজ্ঞ আল্লাহর সুনির্ধারিত (ব্যবস্থাপনা)। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4231,41,13,'এরপরও তারা যদি মুখ ফিরিয়ে নেয় তাহলে বল- আমি তোমাদেরকে অকস্মাৎ শাস্তির ভয় দেখাচ্ছি- ‘আদ ও সামূদের (উপর নেমে আসা) অকস্মাৎ-শাস্তির মত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4232,41,14,'তাদের কাছে যখন তাদের অগ্রে ও পশ্চাতে (একের পর এক) রসূলগণ এসেছিল (আর বলেছিল)- তোমরা আল্লাহ ছাড়া কারো ‘ইবাদাত করো না। (জবাবে) তারা বলেছিল- আমাদের প্রতিপালক ইচ্ছে করলে তো অবশ্যই ফেরেশতা নাযিল করতেন। কাজেই তোমাদেরকে যা দিয়ে পাঠানো হয়েছে আমরা তা অমান্য করছি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4233,41,15,'আর ‘আদ-এর অবস্থা ছিল এই যে, দুনিয়াতে তারা না-হক অহংকার করেছিল, আর বলেছিল- আমাদের চেয়ে বেশি শক্তিশালী আর কে আছে? তারা কি চিন্তা করে দেখেনি যে, আল্লাহ- যিনি তাদেরকে সৃষ্টি করেছেন- শক্তিতে তাদের চেয়ে প্রবল? কিন্তু তারা আমার আয়াতগুলো অস্বীকার করতেই থাকল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4234,41,16,'অতঃপর আমি অশুভ দিনগুলোতে তাদের বিরুদ্ধে ঝাঞ্ঝা বায়ু পাঠালাম যাতে তারা দুনিয়ার জীবনে অপমানজনক শাস্তি আস্বাদন করে। আখিরাতের শাস্তি অবশ্যই অধিক লাঞ্ছনাদায়ক আর তারা সাহায্যপ্রাপ্তও হবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4235,41,17,'আর সামূদের অবস্থা এই যে, আমি তাদেরকে সঠিক পথে চলার নির্দেশ দিয়েছিলাম। কিন্তু তারা সঠিক পথে চলার পরিবর্তে অন্ধ হয়ে থাকাকেই পছন্দ করে ছিল। তখন তাদের কৃতকর্মের কারণে অপমানজনক শাস্তির বজ্রাঘাত তাদেরকে পাকড়াও করল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4236,41,18,'আর আমি তাদেরকে বাঁচিয়ে দিলাম যারা ঈমান এনেছিল আর (আল্লাহকে) ভয় করে চলত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4237,41,19,'যে দিন আল্লাহর দুশমনদেরকে জাহান্নামের দিকে সমবেত করা হবে, তাদেরকে বিভিন্ন দলে বিন্যস্ত করা হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4238,41,20,'শেষ পর্যন্ত যখন তারা জাহান্নামের নিকটে পৌঁছবে, তখন তারা যা করত সে সম্পর্কে তাদের কান, তাদের চোখ আর তাদের চামড়া তাদের বিরুদ্ধে সাক্ষ্য দেবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4239,41,21,'তারা তাদের চামড়াকে বলবে- আমাদের বিরুদ্ধে কেন সাক্ষ্য দিচ্ছ? তারা উত্তর দিবে- আল্লাহ আমাদেরকে কথা বলার শক্তি দিয়েছেন, যিনি সব কিছুকেই (আজ) কথা বলার শক্তি দিয়েছেন। তিনিই প্রথমবার তোমাদেরকে সৃষ্টি করেছেন, আর তাঁর কাছেই তোমাদেরকে ফিরিয়ে নেয়া হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4240,41,22,'(দুনিয়ায় নিজেদের শরীরের অংশগুলোকে তোমরা) এই ভেবে গোপন করতে না যে, না তোমাদের কান, না তোমাদের চোখ আর না তোমাদের চামড়া তোমাদের বিরুদ্ধে সাক্ষ্য দেবে। বরং তোমরা মনে করতে যে, তোমরা যা কর তার অধিকাংশই আল্লাহ জানেন না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4241,41,23,'তোমাদের এই (ভুল) ধারণাই- যা তোমরা তোমাদের প্রতিপালক সম্পর্কে পোষণ করতে- তোমাদেরকে ধ্বংস করেছে, ফলে তোমরা ক্ষতিগ্রস্তদের দলে শামিল হয়ে গেছ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4242,41,24,'এখন যদি তারা ধৈর্য ধারণ করে তবুও জাহান্নামই হবে তাদের আবাস, আর যদি তারা ক্ষমা প্রার্থনা করে তবুও তারা ক্ষমাপ্রাপ্ত হবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4243,41,25,'আমি তাদেরকে প্রাণের বন্ধু জুটিয়ে দিয়েছিলাম, যারা তাদেরকে তাদের সামনের আর পিছনের প্রত্যেকটি জিনিসকে চাকচিক্যময় করে দেখাচ্ছিল। শেষ পর্যন্ত তাদের উপরও তেমনি ‘আযাবের ফয়সালা কার্যকর হল, যা তাদের পূর্ববর্তী জ্বিন ও মানব দলের উপর কার্যকর হয়েছিল। বাস্তবিকই তারা ক্ষতিগ্রস্ত হয়ে গেল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4244,41,26,'কাফিররা বলে- এ কুরআন শুনো না, আর তা পড়ার কালে শোরগোল কর যাতে তোমরা বিজয়ী হতে পার। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4245,41,27,'ফলতঃ আমি কাফিরদেরকে অবশ্য অবশ্যই কঠিন শাস্তি আস্বাদন করাব, আর আমি অবশ্য অবশ্যই তাদেরকে তাদের নিকৃষ্টতম কাজের অনুসারে প্রতিফল দিব। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4246,41,28,'আল্লাহর দুশমনদের জন্য প্রতিফল হল এ জাহান্নাম। তাতে আছে তাদের জন্য চিরস্থায়ী আবাস। (এ হল তাদের) প্রতিফল, কারণ তারা আমার আয়াতগুলোকে অস্বীকার করত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4247,41,29,'কাফিররা বলবে- হে আমাদের প্রতিপালক! জ্বিন ও মানুষদের মধ্যেকার যারা আমাদেরকে পথভ্রষ্ট করেছিল তাদের উভয়কে দেখিয়ে দিন, তাদের উভয়কে পায়ের তলায় পিষ্ট করব, যাতে তারা নিকৃষ্ট অধমদের মধ্যে গণ্য হয়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4248,41,30,'যারা বলে- আমাদের প্রতিপালক আল্লাহ, অতঃপর (সে কথার উপর) সুদৃঢ় থাকে, ফেরেশতারা তাদের নিকট অবতীর্ণ হয় আর বলে, তোমরা ভয় করো না, চিন্তা করো না, আর জান্নাতের সুসংবাদ গ্রহণ কর যার ওয়া‘দা তোমাদেরকে দেয়া হয়েছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4249,41,31,'পার্থিব জীবনে আর আখিরাতে আমরাই তোমাদের সঙ্গী-সাথী। আর সেখানে (অর্থাৎ জান্নাতে) তোমাদের জন্য তোমাদের মন যা চায় তা-ই আছে; তোমরা যে জিনিসের আকাঙ্ক্ষা কর, তোমাদের জন্য সেখানে তা-ই আছে ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4250,41,32,'পরম ক্ষমাশীল, পরম দয়ালু (আল্লাহ)’র পক্ষ থেকে আপ্যায়ন হিসেবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4251,41,33,'কথায় ঐ ব্যক্তি থেকে কে বেশি উত্তম যে (মানুষকে) আল্লাহর দিকে আহবান করে, আর সৎ কাজ করে এবং বলে, ‘আমি (আল্লাহর প্রতি) অনুগতদের অন্তর্ভুক্ত’। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4252,41,34,'ভাল আর মন্দ সমান নয়। উৎকৃষ্ট দিয়ে মন্দকে দূর কর। তখন দেখবে, তোমার আর যার মধ্যে শত্রুতা আছে সে যেন অন্তরঙ্গ বন্ধু। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4253,41,35,'এ গুণ কেবল তারাই লাভ করে যারা ধৈর্যশীল, এ গুণ কেবল তারাই লাভ করে যারা মহা ভাগ্যবান। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4254,41,36,'শয়ত্বানের পক্ষ থেকে যদি তুমি কুমন্ত্রণা অনুভব কর, তাহলে আল্লাহর আশ্রয় প্রার্থনা কর। তিনি সর্বশ্রোতা সর্বজ্ঞ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4255,41,37,'তাঁর নিদর্শনগুলোর মধ্যে হল রাত, দিন, সূর্য আর চন্দ্র। সূর্যকে সেজদা করো না, চন্দ্রকেও না। সেজদা কর আল্লাহকে যিনি ওগুলোকে সৃষ্টি করেছেন যদি সত্যিকারভাবে একমাত্র তাঁরই তোমরা ইবাদাত করতে চাও। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4256,41,38,'অতঃপর তারা যদি অহংকার করে তবে (তারা জেনে নিক যে), তোমার প্রতিপালকের নিকটে যারা রয়েছে তারা দিন-রাত তাঁর মাহাত্ম্য বর্ণনায় লিপ্ত আছে, আর তারা কখনও ক্লান্তিবোধ করে না।[সাজদাহ] ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4257,41,39,'তাঁর নিদর্শনগুলোর মধ্যে হল এই যে, তুমি যমীনকে দেখ শুষ্ক অনুর্বর পড়ে আছে। অতঃপর আমি যখন তার উপর বৃষ্টি বর্ষণ করি তখন তা সতেজ হয় ও বেড়ে যায়। যিনি এ মৃত যমীনকে জীবিত করেন, তিনি অবশ্যই মৃতদেরকে জীবিত করবেন। তিনি সকল বিষয়ের উপর ক্ষমতাবান। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4258,41,40,'যারা আমার আয়াতসমূহের অর্থকে ভিন্নপথে পরিচালিত করে, তারা আমার থেকে লুক্কায়িত নয়। যাকে জাহান্নামে নিক্ষেপ করা হবে সেই উত্তম না ঐ ব্যক্তি যে ক্বিয়ামতের দিন সম্পূর্ণ নিরাপদ হয়ে হাজির হবে? তোমাদের যা ইচ্ছে হয় করতে থাক। তোমরা যা কর তা তিনি (খুব ভালভাবেই) দেখেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4259,41,41,'যারা তাদের কাছে উপদেশ বাণী আসার পর তা প্রত্যাখ্যান করে (তারা আমার থেকে লুক্কায়িত নয়)। এটা হল অবশ্যই এক মহা শক্তিশালী গ্রন্থ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4260,41,42,'মিথ্যা এর কাছে না এর সামনে দিয়ে আসতে পারে, না এর পিছন দিয়ে। এটা অবতীর্ণ হয়েছে মহাজ্ঞানী, সকল প্রশংসার যোগ্য (আল্লাহ)’র পক্ষ হতে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4261,41,43,'তোমাকে তা-ই বলা হচ্ছে যা বলা হত তোমার পূর্ববর্তী রসূলদেরকে। তোমার প্রতিপালক অবশ্যই ক্ষমার অধিকারী, আর যন্ত্রণাদায়ক শাস্তিদাতা। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4262,41,44,'আমি যদি একে অনারব ভাষায় (অবতীর্ণ) কুরআন করতাম তাহলে তারা অবশ্যই বলত- এর আয়াতগুলো সুস্পষ্টভাবে বর্ণনা করা হল না কেন? আশ্চর্য ব্যাপার! কিতাব হল অনারব দেশীয় আর শ্রোতারা হল আরবীভাষী। বল- যারা ঈমান আনে তাদের জন্য এ কুরআন সঠিক পথের দিশারী ও আরোগ্য (লাভের উপায়)। যারা ঈমান আনে না তাদের কানে আছে বধিরতা, আর এ কুরআন তাদের জন্য অন্ধত্ব। (যেন) বহু দূর থেকে তাদেরকে ডাকা হচ্ছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4263,41,45,'(ইতোপূর্বে) মূসাকে আমি কিতাব দিয়েছিলাম, কিন্তু তাতে মতভেদ করা হয়েছিল। তোমার প্রতিপালক যদি পূর্বেই একটি কথা ঘোষণা করে না দিতেন, তাহলে তাদের মধ্যে (মতভেদের ব্যাপারে) ফয়সালা করেই দিতেন। তারা এ ব্যাপারে এক অস্থিরতাপূর্ণ সন্দেহে লিপ্ত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4264,41,46,'যে সৎকাজ করবে নিজের কল্যাণেই করবে. যে অসৎ কাজ করবে তার পরিণতি তাকেই ভোগ করতে হবে। তোমার প্রতিপালক বান্দাদের প্রতি যালিম নন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4265,41,47,'ক্বিয়ামত কখন সংঘটিত হবে সে সম্পর্কিত জ্ঞান একমাত্র তাঁর কাছেই আছে। কোন ফলই তার আবরণ থেকে বেরিয়ে আসে না, এবং কোন নারী গর্ভধারণ করে না আর সন্তান প্রসব করে না তাঁর অজ্ঞাতে। যে দিন তিনি তাদেরকে ডেকে বলবেন- আমার শরীকরা কোথায় (তোমরা যেগুলো বানিয়ে নিয়েছিলে)? তারা বলবে- আপনাকে নিশ্চয়তা দিচ্ছি যে, (আজ) আমাদের কেউ (আপনার শরীক থাকার ব্যাপারে) সাক্ষ্য দাতা হবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4266,41,48,'পূর্বে তারা যাদেরকে ডাকত তারা তাদের থেকে উধাও হয়ে যাবে, আর তারা বুঝতে পারবে যে, তাদের পালানোর কোন জায়গা নেই। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4267,41,49,'মানুষ নিজের কল্যাণ কামনায় দু‘আ প্রার্থনা করতে ক্লান্ত হয় না, আর মন্দ যখন তাকে স্পর্শ করে, তখন সে নৈরাশ্যে ডুবে যায়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4268,41,50,'দুঃখ-বিপদ মানুষকে স্পর্শ করার পর আমি যখন তাকে আমার অনুগ্রহ আস্বাদন করাই তখন সে অবশ্য অবশ্যই বলে- এটা আমার ন্যায্য পাওনা। ক্বিয়ামত যে সংঘটিত হবে আমি মোটেই তা মনে করি না। আর যদি আমাকে আমার প্রতিপালকের নিকট ফিরিয়ে নেয়া হয়ই, তবে তাঁর কাছে আমার জন্য অবশ্যই কল্যাণ আছে। অতঃপর আমি কাফিরদেরকে অবশ্য অবশ্যই জানিয়ে দেব তারা (দুনিয়াতে) কী কাজ করেছিল। আর অবশ্য অবশ্যই তাদেরকে আমি কঠিন শাস্তি আস্বাদন করাব। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4269,41,51,'আমি যখন মানুষকে নি‘মাত দেই তখন সে মুখ ফিরিয়ে নেয়, আর পাশ কেটে (আমার থেকে দূরে) সরে যায়। আর যখন মন্দ তাকে স্পর্শ করে, তখন সে লম্বা-চওড়া দু‘আ করতে থাকে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4270,41,52,'বল- তোমরা কি ভেবে দেখেছ এ (কুরআন) যদি আল্লাহর পক্ষ থেকেই এসে থাকে, আর তোমরা সেটাকে অস্বীকার করতেই থাক, তাহলে সে ব্যক্তি ছাড়া অধিক পথভ্রষ্ট আর কে আছে, যে বিরোধিতায় বহু দূরে চলে গেছে? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4271,41,53,'আমি তাদেরকে আমার নিদর্শনাবলী দেখাব দূর দিগন্তে (অর্থাৎ দূর পর্যন্ত ইসলামের আলো বিচ্ছুরিত হবে) আর তাদের নিজেদের মধ্যেও (অর্থাৎ কাফিররা নতজানু হয়ে ইসলাম কবূল করবে) যখন তাদের কাছে স্পষ্ট হয়ে যাবে যে, এ কুরআন সত্য। এটা কি যথেষ্ট নয় যে, তোমার প্রতিপালক সব কিছুরই সাক্ষী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4272,41,54,'শুনে রাখ, এরা তাদের প্রতিপালকের সঙ্গে সাক্ষাতের ব্যাপারে সন্দেহে পড়ে আছে। শুনে রাখ, তিনি সব কিছুকেই বেষ্টন করে আছেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4273,42,1,'হা-মীম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4274,42,2,'‘আইন-সীন-ক্বাফ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4275,42,3,'এভাবেই মহাপরাক্রমশালী মহাজ্ঞানী আল্লাহ তোমার প্রতি এবং তোমার পূর্বে যারা ছিল তাদের প্রতি ওয়াহী নাযিল করেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4276,42,4,'আকাশ ও যমীনে যা কিছু আছে সবই তাঁর, তিনি সর্বোচ্চ, মহান। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4277,42,5,'আকাশ উপর থেকে ফেটে পড়ার উপক্রম হয় (সুমহান আল্লাহর প্রতি শ্রদ্ধাপূর্ণ ভয়ভীতিতে) আর ফেরেশতারা তাদের প্রতিপালকের প্রশংসা ও মহিমা ঘোষণা করে এবং যারা পৃথিবীতে আছে তাদের জন্য (আল্লাহর নিকট) ক্ষমা প্রার্থনা করে। জেনে রেখ, আল্লাহ, তিনি বড়ই ক্ষমাশীল, অতি দয়ালু। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4278,42,6,'যারা আল্লাহর পরিবর্তে অন্যদেরকে অভিভাবকরূপে গ্রহণ করে, আল্লাহ তাদের প্রতি নযর রাখছেন, তাদের (কাজের) দায়-দায়িত্ব তোমার উপর নেই। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4279,42,7,'এভাবে আমি তোমার প্রতি কুরআন আরবী ভাষায় নাযিল করেছি যাতে তুমি উম্মুল কুরা (মক্কা শহর) ও তার চার পাশে যারা আছে তাদেরকে সতর্ক করতে পার, আর সতর্ক কর একত্রিত হওয়ার দিন সম্পর্কে যে বিষয়ে কোন সন্দেহ নেই। (একত্রিত হওয়ার পর) এক দল যাবে জান্নাতে, আরেক দল যাবে জাহান্নামে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4280,42,8,'আল্লাহ ইচ্ছে করলে তাদেরকে একই উম্মত করতেন, কিন্তু তিনি যাকে ইচ্ছে তাঁর রহমাতের মধ্যে দাখিল করেন, আর যালিমদের জন্য নেই কোন অভিভাবক, নেই কোন সাহায্যকারী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4281,42,9,'কী! তারা কি আল্লাহর পরিবর্তে অন্যদেরকে অভিভাবক গ্রহণ করে নিয়েছে? আল্লাহই তো একমাত্র অভিভাবক, তিনিই মৃতকে জীবিত করেন আর তিনি সব কিছুর উপর ক্ষমতাবান। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4282,42,10,'তোমরা যে সব বিষয়ে মতভেদ কর তার মীমাংসা আল্লাহর উপর সোপর্দ. সেই আল্লাহই আমার প্রতিপালক, আমি তাঁর উপরই নির্ভর করি, আর তাঁরই অভিমুখী হই। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4283,42,11,'আকাশসমূহ ও যমীনের স্রষ্টা। তিনি তোমাদের জন্য তোমাদের মধ্য হতে যুগল সৃষ্টি করেছেন, চতুস্পদ জন্তুদের মধ্যেও সৃষ্টি করেছেন জোড়া, এভাবেই তিনি তোমাদের বংশধারা বিস্তৃত করেন, কোন কিছুই তাঁর সদৃশ নয়, তিনি সব শোনেন, সব দেখেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4284,42,12,'আসমান ও যমীনের চাবিকাঠি তাঁরই হাতে নিবদ্ধ। যার জন্য ইচ্ছে তিনি রিযক প্রশস্ত করেন ও (যার জন্য ইচ্ছে) সীমিত করেন। সব বিষয়েই তিনি সবচেয়ে জ্ঞানী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4285,42,13,'তিনি তোমাদের জন্য দ্বীনের সেই বিধি-ব্যবস্থাই দিয়েছেন যার হুকুম তিনি দিয়েছিলেন নূহকে। আর সেই (বিধি ব্যবস্থাই) তোমাকে ওয়াহীর মাধ্যমে দিলাম যার হুকুম দিয়েছিলাম ইবরাহীম, মূসা ও ‘ঈসাকে- তা এই যে, তোমরা দ্বীন প্রতিষ্ঠিত কর, আর তাতে বিভক্তি সৃষ্টি করো না, ব্যাপারটি মুশরিকদের জন্য কঠিন হয়ে দাঁড়িয়েছে যার দিকে তুমি তাদেরকে আহবান জানাচ্ছ। আল্লাহ যাকে ইচ্ছে করেন তাঁর পথে বেছে নেন, আর তিনি তাঁর পথে পরিচালিত করেন তাকে, যে তাঁর অভিমুখী হয়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4286,42,14,'মানুষের কাছে ইলম আসার পর (বিভিন্ন অংশে) বিভক্ত হয়ে গেল নিজেদের মধ্যে বাড়াবাড়ি করার কারণে। পূর্বেই যদি তোমার প্রতিপালকের পক্ষ হতে একটা নির্দিষ্ট মেয়াদ পর্যন্ত ফয়সালা মূলতবী রাখার কথা ঘোষিত না হত, তাহলে তাদের মধ্যে (পূর্বেই) ফয়সালা করে দেয়া হত। আগেকার লোকেদের পরে যারা (তাওরাত ও ইঞ্জিল এ দু’) কিতাব উত্তরাধিকার সূত্রে পাপ্ত হয়েছে, তারা অস্বস্তিকর সন্দেহে পতিত হয়েছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4287,42,15,'অবস্থার এই প্রেক্ষাপটে (হে নবী!) তাদেরকে আহবান কর (দ্বীনের প্রতি), আর তোমাকে যে হুকুম দেয়া হয়েছে তুমি তার প্রতি সুদৃঢ় থাক, আর তাদের খেয়াল খুশির অনুসরণ করো না। আর বল, আল্লাহ যে কিতাবই অবতীর্ণ করেছেন আমি তার প্রতি ঈমান এনেছি। তোমাদের মাঝে ইনসাফ করার জন্য আমাকে নির্দেশ দেয়া হয়েছে। আল্লাহ আমাদেরও প্রতিপালক, তোমাদেরও প্রতিপালক। আমাদের কাজের প্রতিফল আমরা ভোগ করব। আর তোমাদের কাজের প্রতিফল তোমরা ভোগ করবে। আমাদের আর তোমাদের মাঝে কোন ঝগড়া নেই। আল্লাহ আমাদের সবাইকে (একদিন) একত্রিত করবেন, আর তাঁর কাছেই (সকলকে) ফিরে যেতে হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4288,42,16,'আল্লাহর আহবানে সাড়া দেয়ার পর সে সম্পর্কে যারা বিতর্কে লিপ্ত হয়, তাদের দলীল প্রমাণ তাদের রব্ব-এর কাছে বাতিল। তাদের প্রতি (আল্লাহর) গযব আর তাদের জন্য আছে কঠিন শাস্তি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4289,42,17,'তিনিই আল্লাহ যিনি সত্য ও ইনসাফের মানদন্ড সহকারে কিতাব অবতীর্ণ করেছেন। তুমি কি জান, সম্ভবতঃ চূড়ান্ত ফয়সালার সময় নিকটবর্তী হয়ে গেছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4290,42,18,'যে সব লোক তাতে বিশ্বাস করে না, তারাই তার (অর্থাৎ ক্বিয়ামতের) আগমনকে তরান্বিত করতে চায়। কিন্তু যারা বিশ্বাসী তারা তাকে ভয় করে আর তারা জানে যে, তা সত্য। জেনে রেখ, যারা ক্বিয়ামত সম্পর্কে বিতর্ক করে, তারা স্পষ্টতই সত্য পথ হতে বহু দূরে চলে গেছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4291,42,19,'আল্লাহ তাঁর বান্দাহদের প্রতি মেহেরবান, তিনি যাকে যা ইচ্ছে রিযক দেন। তিনি প্রবল, মহাপরাক্রমশালী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4292,42,20,'যে লোক পরকালের ক্ষেত করতে চায়, আমি তার জন্য তার ক্ষেতে বৃদ্ধি দান করি। আর যে লোক দুনিয়ার ক্ষেত চায়, আমি তাকে তাত্থেকে দেই, কিন্তু পরকালে তার অংশে (বা ভাগ্যে) কিছুই নেই। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4293,42,21,'কী! তাদের কি এমন শরীক আছে যারা তাদের জন্য দ্বীনের বিধি-বিধান দিয়েছে যার অনুমতি আল্লাহ দেননি? ফয়সালা সম্পর্কিত (পূর্ব) ঘোষণা দেয়া না হলে তাদের মধ্যে (তৎক্ষণাৎ) ফয়সালা করে দেয়া হত। যালিমদের জন্য আছে যন্ত্রণাদায়ক শাস্তি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4294,42,22,'যালিমদেরকে তুমি তাদের কৃতকর্মের কারণে ভীত-সন্ত্রস্ত দেখতে পাবে। তাদের কর্মের শাস্তি অবশ্যই তাদের উপর পতিত হবে। আর যারা ঈমান এনেছে আর সৎকর্ম করেছে তারা জান্নাতের বাগিচায় অবস্থান করবে। তারা যা ইচ্ছে করবে, তাদের জন্য তাদের প্রতিপালকের নিকট তা-ই আছে। ওটাই অতি বড় অনুগ্রহ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4295,42,23,'এটা হল তাই আল্লাহ যার সুসংবাদ দিয়েছেন তাঁর সে সব বান্দাহদের জন্য যারা ঈমান আনে আর সৎ কাজ করে। বল, এ কাজের জন্য আত্মীয়তার ভালবাসা ছাড়া তোমাদের কাছে কিছুই চাই না। যে কেউ উত্তম কাজ করে, আমি তার জন্য তাতে পুণ্য বাড়িয়ে দেই। আল্লাহ বড়ই ক্ষমাশীল, ভাল কাজের বড়ই মর্যাদাদানকারী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4296,42,24,'তারা কি বলে যে, এ লোক আল্লাহর নামে মিথ্যা রচনা করেছে? আল্লাহ চাইলে তোমার হৃদয়ে মোহর মেরে দিতেন। বস্তুতঃ তিনি মিথ্যাকে মিটিয়ে দেন এবং নিজ বাক্য দ্বারা সত্যকে প্রতিষ্ঠিত করেন। তিনি (সকলের) অন্তরে নিহিত বিষয় সম্পর্কে খুবই অবগত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4297,42,25,'তিনি তাঁর বান্দাহদের তাওবাহ ক্ববূল করেন, পাপ ক্ষমা করেন আর তিনি জানেন তোমরা যা কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4298,42,26,'যারা ঈমান আনে আর সৎকার্ম করে তিনি তাদের আহবান শুনেন, আর তিনি তাদের প্রতি নিজ অনুগ্রহ বাড়িয়ে দেন আর কাফিরদের জন্য আছে কঠিন শাস্তি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4299,42,27,'আল্লাহ যদি তাঁর সকল বান্দাহদের জন্য রিযক পর্যাপ্ত করে দিতেন, তাহলে তারা অবশ্যই যমীনে বিদ্রোহ সৃষ্টি করত; কিন্তু তিনি একটা নির্দিষ্ট পরিমাণে যতটুকু ইচ্ছে নাযিল করেন। তিনি তাঁর বান্দাহ্দের সম্পর্কে পূর্ণ ওয়াকিফহাল, তিনি তাদের প্রতি সর্বদা দৃষ্টি রাখেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4300,42,28,'মানুষ নিরাশ হয়ে যাওয়ার পর তিনিই বৃষ্টি বর্ষণ করেন, আর স্বীয় রহমত ছড়িয়ে দেন, তিনি-ই সকল গুণে প্রশংসিত প্রকৃত অভিভাবক। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4301,42,29,'তাঁর নিদর্শনসমূহের অন্তর্গত হল আসমান ও যমীনের সৃষ্টি, আর এ দু’য়ের ভিতর যে প্রাণীকুল ছড়িয়ে দিয়েছেন তিনি। আর যখন ইচ্ছে তিনি তাদেরকে একত্রিত করতে সক্ষম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4302,42,30,'তোমাদের উপর যে বিপদই উপনীত হয় তা তোমাদের হাতের উপার্জনের কারণেই, তিনি অনেক অপরাধই ক্ষমা করে দেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4303,42,31,'তোমরা যমীনে (আল্লাহর কর্ম ও পরিকল্পনাকে) ভন্ডুল (অক্ষম) করতে পারবে না। আল্লাহ ছাড়া তোমাদের না আছে কোন অভিভাবক, আর না আছে সাহায্যকারী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4304,42,32,'তাঁর নিদর্শনসমূহের মধ্যে হল সমুদ্রে নির্বিঘ্নে চলমান জাহাজ- পাহাড়ের মত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4305,42,33,'তিনি যদি ইচ্ছে করেন বাতাসকে থামিয়ে দিতে পারেন, তখন জাহাজগুলো সমুদ্র পৃষ্ঠে গতিহীন হয়ে পড়বে। এতে প্রত্যেক অতি ধৈর্যশীল শুকর আদায়কারীর জন্য অবশ্যই নিদর্শন রয়েছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4306,42,34,'কিংবা তিনি ওগুলোকে ধ্বংস করে দিতে পারেন তাদের উপার্জিত পাপের কারণে, আর প্রকৃতপক্ষে তিনি তো (তাদের) অনেক দোষ-ত্রুটিই ক্ষমা করে দেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4307,42,35,'(আর ওগুলোকে ধ্বংস করা হয়) এজন্যও যে, আমার আয়াত নিয়ে যারা বিতর্ক করে তারা যেন জেনে নেয় যে, তাদের (আল্লাহর কাছ থেকে পালিয়ে অন্যত্র আশ্রয় নেয়ার জন্য) কোন আশ্রয়স্থল নেই। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4308,42,36,'(এখানে) তোমাদেরকে যা কিছু দেয়া হয়েছে তা অস্থায়ী দুনিয়ার জীবনের (সামান্য) ভোগ্যবস্তু মাত্র। আল্লাহর নিকট যা আছে তা উৎকৃষ্ট এবং স্থায়ী (আর তা হল) তাদের জন্য যারা ঈমান আনে এবং তাদের প্রতিপালকের উপর নির্ভর করে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4309,42,37,'যারা বড় বড় পাপ এবং অশ্লীল  কার্যকলাপ হতে বেঁচে চলে এবং রাগান্বিত হয়েও ক্ষমা করে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4310,42,38,'যারা তাদের প্রতিপালকের (নির্দেশ পালনের মাধ্যমে তাঁর) প্রতি সাড়া দেয়, নিয়মিত নামায প্রতিষ্ঠা করে, পারস্পরিক পরামর্শের ভিত্তিতে নিজেদের কার্যাদি পরিচালনা করে। আর আমি তাদেরকে যে রিযক দিয়েছি তাত্থেকে ব্যয় করে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4311,42,39,'আর তাদের উপর অতিরিক্ত বাড়াবাড়ি করা হলে নিজেদের প্রতিরক্ষার ব্যবস্থা করে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4312,42,40,'অন্যায়ের প্রতিবিধান হল অনুরূপ অন্যায়। অতঃপর যে ক্ষমা করে এবং সমঝোতা করে, তার প্রতিদান দেয়া আল্লাহর যিম্মায়। সীমাঙ্ঘনকারীদেরকে আল্লাহ পছন্দ করেন না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4313,42,41,'কেউ অত্যাচারিত হয়ে নিজের প্রতিরক্ষার ব্যবস্থা নিলে, তার বিরুদ্ধে কোন অভিযোগ নেই। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4314,42,42,'অভিযোগ তো তাদের বিরুদ্ধে যারা মানুষের প্রতি অত্যাচার করে, আর অন্যায়ভাবে যমীনে বিদ্রোহ সৃষ্টি করে; তাদের জন্য আছে যন্ত্রণাদায়ক শাস্তি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4315,42,43,'আর যে ব্যক্তি (অত্যাচারিত হওয়ার পরও) ধৈর্য ধারণ করে, আর (অত্যাচারীকে) ক্ষমা করে দেয়, তাহলে তা অবশ্যই দৃঢ় চিত্ততার কাজ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4316,42,44,'আল্লাহই যাকে পথভ্রষ্ট করেন, তার জন্য তিনি ছাড়া কোন অভিভাবক নেই। তুমি দেখবে, যখন যালিমরা ‘আযাব প্রত্যক্ষ করবে তখন বলবে- ফিরে যাওয়ার কোন পথ আছে কি? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4317,42,45,'তুমি (আরো) দেখবে, তাদেরকে জাহান্নামের সম্মুখে উপস্থিত করা হবে, তারা থাকবে অপমানে অবনত, তারা লুকিয়ে তাকাবে। আর যারা ঈমান এনেছে তারা বলবে, সত্যিকার ক্ষতিগ্রস্ত তো তারাই যারা ক্বিয়ামতের দিনে নিজেদেরকে এবং নিজেদের পরিবার-পরিজনকে ক্ষতিগ্রস্ত করেছে। সাবধান! যালিমরা থাকবে স্থায়ী ‘আযাবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4318,42,46,'তাদের কোন অভিভাবক থাকবে না আল্লাহ্ ব্যতীত যারা তাদেরকে সাহায্য করতে পারে। আল্লাহ যাকে পথভ্রষ্ট করেন তার (শাস্তি থেকে বাঁচার) কোন রাস্তা নেই। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4319,42,47,'তোমরা তোমাদের প্রতিপালকের কথায় সাড়া দাও সেই দিন আসার পূর্বে আল্লাহ (’র হুকুমে যা সংঘটিত হওয়া) থেকে ফিরিয়ে রাখার কেউ নেই। সেদিন তোমাদের জন্য আশ্রয়স্থল থাকবে না, থাকবে না তোমাদের জন্য কোন প্রতিরোধকারী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4320,42,48,'এতদসত্ত্বেও তারা যদি মুখ ফিরিয়েই নেয় (তাহলে ফিরিয়ে নিক, কারণ) আমি তোমাকে তাদের হিফাযাতকারী বানিয়ে পাঠাইনি। কথা পৌঁছে দেয়াই তোমার দায়িত্ব। আমি যখন মানুষকে আমার রহমত আস্বাদন করাই, তখন সে উৎফুল্ল হয়। আর যখন তাদের কৃতকর্মের কারণে তাদের কোন অনিষ্ট হয়, তখন মানুষ অকৃতজ্ঞ হয়ে যায়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4321,42,49,'আসমান ও যমীনের রাজত্ব আল্লাহরই, যা চান তিনি সৃষ্টি করেন। যাকে চান কন্যা-সন্তান দেন, যাকে চান পুত্র সন্তান দেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4322,42,50,'অথবা তাদেরকে দেন পুত্র ও কন্যা উভয়ই। আর যাকে ইচ্ছে বন্ধ্যা করেন। তিনি সর্ব বিষয়ে সর্বাধিক অবহিত ও ক্ষমতাবান। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4323,42,51,'কোন মানুষের এ মর্যাদা নেই যে, আল্লাহ তার সাথে (সরাসরি) কথা বলবেন ওয়াহীর মাধ্যম বা পর্দার আড়াল বা কোন দূত প্রেরণ ছাড়া। অতঃপর আল্লাহর অনুমতিক্রমে সে (মনোনীত মানুষের কাছে) ওয়াহী করে যা তিনি (আল্লাহ) চান। তিনি সুমহান ও মহাবিজ্ঞানী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4324,42,52,'এভাবে (উপরোক্ত ৩টি উপায়েই) আমার নির্দেশের মূল শিক্ষাকে তোমার কাছে আমি ওয়াহী যোগে প্রেরণ করেছি। তুমি জানতে না কিতাব কী, ঈমান কী, কিন্তু আমি একে (অর্থাৎ ওয়াহী যোগে প্রেরিত কুরআনকে) করেছি আলো, যার সাহায্যে আমার বান্দাহদের মধ্য হতে যাকে ইচ্ছে আমি সঠিক পথে পরিচালিত করি। তুমি নিশ্চিতই (মানুষদেরকে) সঠিক পথের দিকে নির্দেশ করছ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4325,42,53,'যা কিছু আকাশে আছে আর যমীনে আছে এসবের মালিক যিনি সেই আল্লাহর পথে। শুনে রাখ! আল্লাহর কাছেই সব বিষয় ফিরে যায়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4326,43,1,'হা-মীম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4327,43,2,'শপথ সুস্পষ্ট কিতাবের। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4328,43,3,'আমি ওটাকে করেছি আরবী ভাষার কুরআন যাতে তোমরা বুঝতে পারে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4329,43,4,'আমার কাছে তা উম্মুল কিতাবে (লাওহে মাহফুজে) সংরক্ষিত আছে, আর তা হল অতি উচ্চ মর্যাদাসম্পন্ন জ্ঞান-বিজ্ঞানে পূর্ণ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4330,43,5,'তোমরা এক সীমালঙ্ঘনকারী জাতি- এ কারণে কি আমি তোমাদের কাছ থেকে কুরআন প্রত্যাহার করে নেব? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4331,43,6,'পূর্বেকার জাতিগুলোর মধ্যে আমি বহু রসূল পাঠিয়েছিলাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4332,43,7,'তাদের কাছে এমন কোন নবী আসেনি যাকে তারা ঠাট্টা-বিদ্রূপ করেনি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4333,43,8,'আমি তাদেরকে ধ্বংস করেছি- যারা ছিল এদের চেয়ে অনেক বেশি শক্তিশালী। পূর্বের জাতিগুলোর উদাহরণ অতীত হয়ে গেছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4334,43,9,'তুমি যদি তাদেরকে জিজ্ঞেস কর- আকাশ ও যমীন কে সৃষ্টি করেছে? তারা অবশ্য অবশ্যই বলবে- ওগুলো প্রবল পরাক্রমশালী মহাজ্ঞানী আল্লাহ সৃষ্টি করেছেন, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4335,43,10,'যিনি তোমাদের জন্য যমীনকে করেছেন বিস্তৃত, আর তাতে তোমাদের জন্য বানিয়েছেন চলার পথ- যাতে তোমরা সঠিক পথ পেতে পার। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4336,43,11,'যিনি আকাশ থেকে পরিমিত পানি বর্ষণ করেন যা দিয়ে তিনি মৃত ভূ-ভাগকে সঞ্জীবিত করেন। এভাবেই তোমাদেরকে বের করা হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4337,43,12,'তিনি সব কিছুকে জোড়া জোড়া সৃষ্টি করেছেন, আর তোমাদের জন্য নৌযান ও গবাদি পশু সৃষ্টি করেছেন যাতে তোমরা আরোহণ কর, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4338,43,13,'যাতে তোমরা যখন তাদের পিঠে স্থির হয়ে বস, তখন যেন তোমাদের প্রতিপালকের অনুগ্রহের কথা স্মরণ কর আর বল- মহান ও পবিত্র তিনি যিনি, এগুলোকে আমাদের (ব্যবহারের জন্য) বশীভূত করে দিয়েছেন, আমরা এগুলোকে বশীভূত করতে সক্ষম ছিলাম না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4339,43,14,'আর আমাদেরকে অবশ্যই আমাদের প্রতিপালকের দিকে ফিরে যেতে হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4340,43,15,'আর তারা আল্লাহর বান্দাদের মধ্য হতে কতককে আল্লাহর অংশ মনে করে নিয়েছে (যেমন ‘ঈসা (আঃ)-কে তারা আল্লাহর পুত্র মনে করে)। মানুষ অবশ্যই স্পষ্টতঃ অকৃতজ্ঞ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4341,43,16,'কি! তিনি তাঁরই সৃষ্টি হতে কন্যা সন্তান গ্রহণ করেছেন আর তোমাদের জন্য মনোনীত করেছেন পুত্র সন্তান? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4342,43,17,'তাদের কাউকে যখন সংবাদ দেয়া হয় সেই সন্তানের যা তারা দয়াময় আল্লাহর প্রতি আরোপ করে, তখন তার মুখ মন্ডলে কালিমা ছেয়ে যায়, আর মন দুঃখ বেদনায় ভরে যায়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4343,43,18,'তারা কি আল্লাহর জন্য নির্ধারিত করে এমন সন্তান যে অলংকারে লালিত পালিত হয়, আর বিতর্ককালে বক্তব্যকে স্পষ্ট করতে অসমর্থ? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4344,43,19,'তারা দয়াময়ের বান্দাহ্ ফেরেশতাদেরকে নারী গণ্য করে। তারা কি ফেরেশতাদেরকে সৃষ্টি সরাসরি দেখেছে? তাদের সাক্ষ্য লিখে রাখা হবে এবং তারা জিজ্ঞাসিত হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4345,43,20,'তারা আরো বলে- দয়াময় আল্লাহ ইচ্ছে করলে আমরা ওদের পূঁজা করতাম না। এ বিষয়ে তাদের কোন জ্ঞানই নেই। তারা শুধু আন্দাজ অনুমানে কথা বলে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4346,43,21,'আমি কি তাদেরকে এর (অর্থাৎ কুরআনের) আগে কোন কিতাব দিয়েছি অতঃপর তারা তা অাঁকড়ে ধরে আছে? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4347,43,22,'বরং তারা বলে- আমরা আমাদের পূর্ব পুরুষদেরকে এক ধর্মমত পালনরত পেয়েছি আর আমরা তাদের পদাঙ্ক অনুসরণ করে পথপ্রাপ্ত হয়েছি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4348,43,23,'এভাবে তোমার পূর্বে যখনই আমি কোন জনপদে সতর্ককারী (নবী-রসূল) পাঠিয়েছি, তখনই তাদের সম্পদশালী লোকেরা বলেছে- আমরা আমাদের পূর্বপুরুষদেরকে এক ধর্মমত পালনরত পেয়েছি আর আমরা তাদেরই পদাঙ্ক অনুসরণ করছি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4349,43,24,'তখন সেই সতর্ককারী বলত- তোমরা তোমাদের পিতৃপুরুষকে যে ধর্মমতের উপর পেয়েছ, আমি যদি তোমাদের কাছে তার চেয়ে উৎকৃষ্ট ধর্মমত নিয়ে আসি (তবুও কি তোমরা তাদেরই অনুসরণ করবে)? তারা বলতঃ তোমাদেরকে যা দিয়ে পাঠানো হয়েছে আমরা তা প্রত্যাখ্যান করি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4350,43,25,'অতঃপর আমি তাদের উপর প্রতিশোধ গ্রহণ করলাম, এখন দেখ, মিথ্যুকদের পরিণতি কী হয়েছিল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4351,43,26,'স্মরণ কর, ইবরাহীম (আ.) যখন তার পিতাকে ও তার জাতিকে বলেছিল- তোমরা যেগুলোর পূজা কর, সেগুলো থেকে আমি সম্পর্কহীন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4352,43,27,'আমার সম্পর্ক আছে শুধু তাঁর সাথে যিনি আমাকে সৃষ্টি করেছেন, তিনিই আমাকে সঠিক পথ দেখাবেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4353,43,28,'এ কথাটিকে সে স্থায়ী বাণীরূপে তার পরবর্তীদের মধ্যে রেখে গেছে, যাতে তারা (আল্লাহর পথে) ফিরে আসে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4354,43,29,'হ্যাঁ, আমিই তাদেরকে আর তাদের পূর্বপুরুষকে ভোগের সামগ্রী দিয়েছিলাম। অবশেষে তাদের কাছে আসলো সত্য এবং সবকিছু স্পষ্টকারী রসূল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4355,43,30,'সত্য যখন তাদের কাছে আসল তখন তারা বলল- এটা যাদু, আমরা এটা মানি না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4356,43,31,'তারা বলল- এ কুরআন (মক্কা ও তায়েফ এ) দু’ জনপদের কোন গণ্যমান্য ব্যক্তির উপর কেন অবতীর্ণ হল না? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4357,43,32,'তোমার প্রতিপালকের রহমত কি তারা বণ্টন করে (যে তাদের মর্জিমত কুরআন নাযিল করতে হবে)? তাদের মাঝে তাদের জীবিকা আমিই বণ্টন করেছি পার্থিব জীবনে এবং মর্যাদায় এককে অন্যের উপর উন্নত করি যাতে একে অপরের সহায়তায় গ্রহণ করতে পারে। তারা যা সঞ্চয় করে, তোমার প্রতিপালকের রহমত তাত্থেকে উত্তম ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4358,43,33,'(সত্যকে অস্বীকার ক’রে) সব এক জাতিতে পরিণত হবে এ আশঙ্কা না থাকলে যারা দয়াময় আল্লাহকে অস্বীকার করে তাদেরকে অবশ্যই দিতাম তাদের গৃহের জন্য রৌপ্য নির্মিত ছাদ আর সিঁড়ি যাতে তারা আরোহণ করত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4359,43,34,'আর তাদের গৃহের জন্য দিতাম দরজা ও পালঙ্ক যাতে তারা হেলান দিয়ে বসত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4360,43,35,'আর স্বর্ণ নির্মিতও দিতাম। এগুলো সবই তো পার্থিব জীবনের ভোগ্য সামগ্রী মাত্র। আর আখিরাত তোমার প্রতিপালকের কাছে তাদেরই জন্য যারা (আল্লাহকে) ভয় করে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4361,43,36,'যে ব্যক্তি দয়াময় আল্লাহর স্মরণ থেকে নিজেকে ফিরিয়ে নেয়, আমি তার জন্য শয়ত্বানকে নিয়োজিত করি, অতঃপর সে হয় তার ঘনিষ্ঠ সহচর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4362,43,37,'তারাই মানুষকে সৎপথে চলতে অবশ্যই বাধা দেয়, আর মানুষ মনে করে যে, তারা সঠিক পথে রয়েছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4363,43,38,'অবশেষে সে যখন আমার কাছে আসবে, তখন শয়ত্বানকে বলবে, হায়! আমার ও তোমার মাঝে যদি পূর্ব ও পশ্চিমের ব্যবধান থাকত! কতই না নিকৃষ্ট সহচর সে! ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4364,43,39,'তোমাদের হা-হুতাশ আজ তোমাদের কোন কাজে আসবে না যেহেতু তোমরা সীমালঙ্ঘন করেছিলে। তোমরা হবে শাস্তির অংশীদার। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4365,43,40,'তুমি কি বধিরকে শুনাতে পারবে অথবা যে অন্ধ আর যে আছে সুস্পষ্ট পথভ্রষ্টের মধ্যে তাকে সৎপথ দেখাতে পারবে? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4366,43,41,'আমি যদি তোমাকে (দুনিয়া থেকে) নিয়েও যাই, তবু আমি তাদের কাছ থেকে প্রতিশোধ গ্রহণ করব। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4367,43,42,'অথবা আমি তাদেরকে যে ‘আযাবের ও‘য়াদা দিয়েছি তা যদি তোমাকে দেখিয়ে দেই (আমার পক্ষে সবই সম্ভব), কারণ তাদের উপর আমার পূর্ণ ক্ষমতা আছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4368,43,43,'কাজেই তোমার প্রতি যা ওয়াহী করা হয় তুমি তা দৃঢ়ভাবে আঁকড়ে ধর, কেননা তুমি তো আছ সরল সঠিক পথে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4369,43,44,'কুরআন হল অবশ্যই তোমার জন্য আর তোমার জাতির জন্য উপদেশ বাণী। আর এ সম্পর্কে অচিরেই তোমাদেরকে জওয়াবদিহি করতে হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4370,43,45,'আমি তোমার পূর্বে যে সব রসূল পাঠিয়েছিলাম তাদেরকে জিজ্ঞেস কর (অর্থাৎ তাদের কিতাব দেখ ও তাদের সত্যিকার অনুসারীদের নিকট যাচাই কর) আমি কি দয়াময় আল্লাহ ছাড়া কোন ইলাহ স্থির করেছিলাম যাদের ‘ইবাদাত করতে হবে? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4371,43,46,'আমি মূসাকে আমার নিদর্শনসহ ফেরাউন ও তার প্রধানদের নিকট পাঠিয়েছিলাম। মূসা (আ.) বলেছিল- আমি বিশ্বজগতের প্রতিপালকের রসূল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4372,43,47,'যখন সে আমার আয়াতসমূহ নিয়ে তাদের কাছে আসলো, তখন তারা তা নিয়ে হাসি-ঠাট্টা করতে লাগল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4373,43,48,'আমি তাদেরকে যে নিদর্শনই দেখাতাম তা হত পূর্ববর্তী নিদর্শন থেকে বড়। আমি তাদেরকে শাস্তি দ্বারা পাকড়াও করলাম যাতে তারা (আমার পথে) ফিরে আসে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4374,43,49,'(শাস্তিতে আক্রান্ত হয়ে) তারা বলেছিল- ওহে যাদুকর! তুমি আমাদের জন্য তোমার প্রতিপালকের কাছে তা চাও যার ও‘য়াদা তিনি তোমার কাছে করেছেন। আমরা অবশ্যই সৎপথে চলব। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4375,43,50,'কিন্তু যখনই আমি তাদের থেকে শাস্তি সরিয়ে দিলাম, তখনই তারা অঙ্গীকার ভঙ্গ করে বসল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4376,43,51,'ফেরাউন তার সম্প্রদায়ের মাঝে ঘোষণা দিল। বলল- হে আমার সম্প্রদায়! মিসরের রাজত্ব কি আমার নয়? আর এ সব নদনদী আমার নীচ দিয়ে বয়ে যাচ্ছে, তোমরা কি তা দেখ না? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4377,43,52,'আমি কি এ লোক থেকে শ্রেষ্ঠ নই যে নীচ, যে নিজের কথাটিও স্পষ্ট করে বলতে অক্ষম! ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4378,43,53,'(সে যদি আল্লাহর রসূলই হয়ে থাকে) তাহলে তাঁকে কেন স্বর্ণ কঙ্কন দেয়া হল না? অথবা সারিবদ্ধভাবে ফেরেশতারা কেন তার সাথে আসল না? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4379,43,54,'এভাবে সে তার জাতির লোকেদের বোকা বানিয়ে দিল। ফলে তারা তার কথা মেনে নিল। তারা ছিল এক পাপাচারী সম্প্রদায়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4380,43,55,'তারা যখন আমাকে রাগান্বিত করল, তখন আমি তাদের উপর প্রতিশোধ নিলাম, অতঃপর সব্বাইকে ডুবিয়ে মারলাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4381,43,56,'এভাবে আমি তাদেরকে করলাম অতীতের মানুষ আর পরবর্তীদের জন্য দৃষ্টান্ত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4382,43,57,'যখন মারইয়াম-পুত্র ‘ঈসার দৃষ্টান্ত তুলে ধরা হয়, তখন তোমার জাতির লোকেরা হট্টগোল শুরু করে দেয়, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4383,43,58,'এবং বলে আমাদের উপাস্যরা উত্তম না ‘ঈসা? (কারণ খৃস্টানরা ‘ঈসাকে উপাস্য বানিয়েছে আর আমরা মক্কাবাসীরা উপাস্য বানিয়েছি অন্যদেরকে)। তারা শুধু বিতর্ক সৃষ্টির উদ্দেশেই তোমার সামনে এ দৃষ্টান্ত পেশ করে। আসলে তারা হল এক ঝগড়াটে জাতি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4384,43,59,'মারইয়াম-পুত্র তো শুধু একজন বান্দাহ যার প্রতি আমি অনুগ্রহ করেছিলাম আর বানী ইসরাঈলের জন্য আমি তাকে করেছিলাম (আমার কুদরাতের বিশেষ এক) নমুনা। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4385,43,60,'আমি ইচ্ছে করলে তোমাদের মধ্য হতে ফেরেশতা পয়দা করতে পারি যারা পর পর উত্তরাধিকারী হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4386,43,61,'‘ঈসা (’র দুনিয়াতে পুনরায় আগমন) ক্বিয়ামতের একটি নিশ্চিত নিদর্শন। অতএব তোমরা ক্বিয়ামতে সন্দেহ পোষণ করো না আর আমার অনুসরণ কর, এটাই সরল সঠিক পথ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4387,43,62,'শয়ত্বান তোমাদেরকে (সৎ পথে চলতে) কিছুতেই যেন বাধাগ্রস্ত করতে না পারে, সে তোমাদের খোলাখুলি দুশমন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4388,43,63,'‘ঈসা যখন স্পষ্ট নিদর্শন নিয়ে এসেছিল তখন সে বলেছিল- আমি তোমাদের কাছে হিকমত নিয়ে এসেছি আর এসেছি কতকগুলো বিষয় স্পষ্ট করার জন্য যাতে তোমরা মতভেদ করছ। কাজেই তোমরা আল্লাহকে ভয় কর, আর আমার কথা মান্য কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4389,43,64,'আল্লাহ তিনি তো আমারও রবব আর তোমাদেরও রবব, কাজেই তোমরা তাঁরই ‘ইবাদাত কর, এটাই সরল সঠিক পথ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4390,43,65,'অতঃপর তাদের বিভিন্ন দল নিজেদের মধ্যে মতানৈক্য করল। কাজেই যালিমদের জন্য যন্ত্রণাদায়ক দিনের ‘আযাবের দুর্ভোগ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4391,43,66,'তারা কি তাদের উপর ক্বিয়ামত অকস্মাৎ এসে পড়ার অপেক্ষা করছে যা তারা টেরও পাবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4392,43,67,'বন্ধুরা সেদিন হয়ে যাবে একজন আরেকজনের দুশমন, তবে মুত্তাকীরা ছাড়া। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4393,43,68,'(মুত্তাকীদেরকে বলা হবে) হে আমার বান্দাগণ! আজ তোমাদের কোন ভয় নেই আর তোমরা দুঃখিতও হবে না- ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4394,43,69,'তোমরা যারা আমার আয়াতসমূহে বিশ্বাস করেছিলে এবং অনুগত ছিলে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4395,43,70,'সানন্দে জান্নাতে প্রবেশ কর তোমরা আর তোমাদের স্ত্রীরা। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4396,43,71,'তাদের কাছে চক্রাকারে পরিবেশন করা হবে স্বর্ণের থালা ও পান পাত্র। সেখানে তা-ই আছে মন যা চাইবে, আর চোখ যাতে তৃপ্ত হবে। তোমরা তাতে চিরকাল থাকবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4397,43,72,'এই হল জান্নাত তোমাদেরকে যার উত্তরাধিকারী করা হয়েছে, কারণ তোমরা (সৎ) ‘আমাল করেছিলে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4398,43,73,'তোমাদের জন্য সেখানে আছে প্রচুর ফল যাত্থেকে তোমরা খাবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4399,43,74,'(আর অন্যদিকে) অপরাধীরা জাহান্নামের ‘আযাবে চিরকাল থাকবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4400,43,75,'তাদের শাস্তি কমানো হবে না, আর তাতে তারা হতাশ হয়ে পড়বে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4401,43,76,'আমি তাদের উপর যুলম করিনি, বরং তারা নিজেরাই যালিম ছিল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4402,43,77,'তারা চীৎকার ক’রে বলবে- হে ‘মালেক (জাহান্নামের দাড়োয়ান)! তোমার প্রতিপালক যেন আমাদের দফারফা ক’রে দেন। সে জওয়াব দিবে- ‘তোমরা (এ অবস্থাতেই পড়ে) থাকবে’। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4403,43,78,'আমি তো তোমাদের কাছে সত্য নিয়ে গিয়েছিলাম, কিন্তু তোমাদের অধিকাংশই ছিলে সত্যকে অপছন্দকারী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4404,43,79,'তারা কি (নিজেদের মধ্যে সলা-পরামর্শ করে কোন) চূড়ান্ত সিদ্ধান্ত নিয়েছে? চূড়ান্ত সিদ্ধান্ত তো আমিই নিয়ে থাকি (যাবতীয় ব্যাপারে আল্লাহ যা চান তাই শেষে ঘটে)। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4405,43,80,'তারা কি মনে করে যে, আমি তাদের গোপন কথা, গোপন পরামর্শ শুনি না? নিশ্চয়ই শুনি, আর আমার ফেরেশতারাহ তাদের কাছে থেকে লিখে নেয়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4406,43,81,'বল- দয়াময়ের কোন সন্তান থাকলে আমিই তার সর্বপ্রথম ‘ইবাদাতকারী হতাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4407,43,82,'তারা যা (তাঁর প্রতি) আরোপ করে তা হতে আকাশ ও পৃথিবীর প্রতিপালক ‘আরশের পালনকর্তা মহান, পবিত্র। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4408,43,83,'কাজেই তাদেরকে বাক-চতুরতা ও ক্রীড়া-কৌতুক করতে দাও সে দিনের সাক্ষাৎ পর্যন্ত যে দিনের ও‘য়াদা তাদেরকে দেয়া হয়েছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4409,43,84,'আকাশমন্ডলে তিনিই ইলাহ, যমীনে তিনিই ইলাহ, তিনি মহাবিজ্ঞ, সর্বজ্ঞ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4410,43,85,'অতি মহান ও পবিত্র তিনি, আকাশ, পৃথিবী ও এ দু’য়ের মাঝে যা আছে তার একচ্ছত্র কর্তৃত্ব যাঁর হাতে, ক্বিয়ামতের জ্ঞান তাঁর কাছেই আছে (যে তা কখন ঘটবে), আর তোমাদেরকে তাঁর কাছেই ফিরে যেতে হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4411,43,86,'আল্লাহর পরিবর্তে যাদেরকে তারা ডাকে তারা সুপারিশের অধিকারী নয়, তবে যে জ্ঞানের ভিত্তিতে সত্যের সাক্ষ্য দেয় সে ছাড়া। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4412,43,87,'তুমি যদি তাদেরকে জিজ্ঞেস কর- তাদেরকে কে সৃষ্টি করেছে, তাহলে তারা অবশ্য অবশ্যই বলবে, আল্লাহ। অত:পর তারা কোথায় ফিরে যাচ্ছে? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4413,43,88,'রসূলের এ উক্তির শপথঃ ‘হে আমার প্রতিপালক! এ জাতির লোকেরা ঈমান আনবে না।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4414,43,89,'কাজেই তুমি তাদের থেকে মুখ ফিরিয়ে নাও আর বল- (তোমাদের প্রতি) সালাম। শীঘ্রই তারা জানতে পারবে (তাদের আচরণের পরিণতি কী)। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4415,44,1,'হা-মীম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4416,44,2,'সুস্পষ্ট কিতাবের কসম! ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4417,44,3,'আমি একে অবতীর্ণ করেছি এক রবকতময় রাতে, (কেননা) আমি (মানুষকে) সতর্ককারী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4418,44,4,'এ রাতে প্রতিটি প্রজ্ঞাপূর্ণ বিষয় স্থির করা হয় ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4419,44,5,'আমার আদেশক্রমে, আমিই প্রেরণকারী, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4420,44,6,'তোমার প্রতিপালকের অনুগ্রহ স্বরূপ তিনি সর্বশ্রোতা, সর্বজ্ঞ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4421,44,7,'আকাশ ও পৃথিবী এবং এ দু’য়ের মাঝে যা কিছু আছে সব কিছুর যিনি প্রতিপালক, (তাঁর মর্যাদা যে কত মহান এ কথা উপলব্ধি করে নাও) যদি তোমরা সত্যিকারই বিশ্বাসী হয়ে থাক। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4422,44,8,'তিনি ছাড়া সত্যিকারের কোন ইলাহ নেই, তিনিই জীবিত করেন ও মৃত্যু ঘটান। তিনিই তোমাদের প্রতিপালক এবং তোমাদের পূর্ববর্তী তোমাদের পিতৃপুরুষদেরও প্রতিপালক। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4423,44,9,'কিন্তু তারা সংশয়ের মাঝে খেলায় মত্ত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4424,44,10,'অতএব তুমি অপেক্ষা কর সে দিনের যেদিন আকাশ ধোঁয়ায় আচ্ছন্ন হবে যা সুস্পষ্ট (ভাবে দেখা যাবে)। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4425,44,11,'(যা) মানুষকে ঢেকে নেবে, তা হবে ভয়াবহ শাস্তি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4426,44,12,'(তখন তারা আরয করবে)- হে আমাদের পালনকর্তা! আমাদের থেকে শাস্তি সরিয়ে দিন, আমরা ঈমান আনলাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4427,44,13,'তারা কীভাবে উপদেশ গ্রহণ করবে? তাদের কাছে তো এসেছে সুস্পষ্ট বর্ণনাকারী এক রসূল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4428,44,14,'এখন তারা তার থেকে মুখ ফিরিয়ে নিচ্ছে আর তারা বলছে (সে হল এক) পাগল- যাকে শিখিয়ে দেয়া হয়েছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4429,44,15,'আমি কিছুকালের জন্য ‘আযাব সরিয়ে নেব, তখন তোমরা আগে যা করছিলে তাই আবার করবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4430,44,16,'যেদিন আমি তোমাদেরকে ভীষণভাবে পাকড়াও করব, সেদিন আমি আবশ্যই প্রতিশোধ নেব। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4431,44,17,'তাদের পূর্বে আমি ফেরাউন জাতিকে পরীক্ষা করেছিলাম। তাদের কাছে এসেছিল এক সম্মানিত রসূল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4432,44,18,'সে বলেছিল- আল্লাহর বান্দাদেরকে আমার কাছে অর্পণ কর। আমি তোমাদের জন্য প্রেরিত বিশ্বস্ত রসূল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4433,44,19,'আর আল্লাহর বিরুদ্ধে ঔদ্ধত্য দেখাইও না, আমি তোমাদের কাছে (আমার রসূল হওয়ার) সুস্পষ্ট প্রমাণ পেশ করছি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4434,44,20,'আমি আমার ও তোমাদের প্রতিপালকের নিকট আশ্রয় গ্রহণ করছি যাতে তোমরা আমাকে পাথরের আঘাতে হত্যা না কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4435,44,21,'তোমরা যদি আমার প্রতি বিশ্বাস না আনো, কমপক্ষে আমার কাছ থেকে দূরে থাক। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4436,44,22,'(কিন্তু তারা ছিল আক্রমণমুখী) তখন সে তার পালনকর্তার নিকট দু‘আ করল- এরা অপরাধী জাতি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4437,44,23,'(তখন আমি বললাম) তুমি আমার বান্দাহদেরকে নিয়ে রাতের বেলায় বের হয়ে পড়, তোমাদের পিছু ধাওয়া করা হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4438,44,24,'(মূসা বানী ইসরাঈলকে নিয়ে সমুদ্র পার হয়ে গিয়ে সমুদ্রকে আবার প্রবহমান করার জন্য স্বীয় লাঠি নিক্ষেপ করলে আল্লাহ বললেন) সমুদ্রকে স্থির থাকতে দাও, তারা (অর্থাৎ ফেরাউনী দল) এমন এক বাহিনী যারা নিমজ্জিত হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4439,44,25,'তারা ছেড়ে গিয়েছিল কত উদ্যান আর ঝর্ণা, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4440,44,26,'শস্য ক্ষেত, আর অভিজাত স্থান, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4441,44,27,'আর বিলাস সামগ্রী- যা নিয়ে তারা আনন্দ করত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4442,44,28,'এমনটাই হয়েছিল, অতঃপর আমি অন্য জাতির লোকদেরকে সে সবের উত্তরাধিকারী করে দিয়েছিলাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4443,44,29,'আসমান আর যমীন তাদের জন্য কাঁদেনি, আর তাদেরকে একটু অবসরও দেয়া হয়নি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4444,44,30,'আমি বানী ইসরাঈলকে রক্ষা করেছিলাম অপমানজনক শাস্তি হতে ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4445,44,31,'ফেরাউনের। সে ছিল সীমালঙ্ঘনকারীদের শীর্ষে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4446,44,32,'আমি জেনে বুঝেই বিশ্বজগতের উপর তাদেরকে (অর্থাৎ বানী ইসরাঈলকে) বেছে নিয়েছিলাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4447,44,33,'এবং তাদেরকে আমি এমন নিদর্শনাবলী দিয়েছিলাম যাতে সুস্পষ্ট পরীক্ষা নিহিত ছিল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4448,44,34,'এই কাফিররা বলে, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4449,44,35,'আমাদের প্রথম মৃত্যুর পর আর কিছু নাই আর আমরা পুনরুত্থিত হব না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4450,44,36,'(মৃত্যুর পর মানুষকে পুনরুত্থিত করা হবে- তোমাদের এ কথায়) তোমরা যদি সত্য হও, তাহলে আমাদের পূর্বপুরুষদেরকে হাজির কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4451,44,37,'এরাই শ্রেষ্ঠ, না তুব্বা সম্প্রদায় আর তাদের আগে যারা ছিল তারা? আমিতো ওদেরকে ধ্বংস করে দিয়েছি। তারা ছিল অপরাধী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4452,44,38,'আমি আকাশ, যমীন আর এদের মাঝে যা আছে সে সব খেল-তামাশার ফলে সৃষ্টি করিনি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4453,44,39,'আমি ওদু’টিকে সত্যিকার উদ্দেশ্যে সৃষ্টি করেছি। কিন্তু তাদের অধিকাংশই (তা) জানে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4454,44,40,'ফয়সালার দিনটি তাদের সবারই নির্ধারিত সময় । ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4455,44,41,'যেদিন বন্ধু বন্ধুর কোন উপকারে আসবে না, আর তাদেরকে সাহায্যও করা হবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4456,44,42,'তবে আল্লাহ যার প্রতি রহমত করবেন তার কথা আলাদা। তিনি মহাপরাক্রান্ত, বড়ই দয়ালু। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4457,44,43,'নিশ্চয়ই যাক্কুম গাছ (হবে) ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4458,44,44,'পাপীর খাদ্য ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4459,44,45,'গলিত তামার মত পেটে ফুটতে থাকবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4460,44,46,'ফুটন্ত পানির মত, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4461,44,47,'(বলা হবে) ওকে ধর, আর ওকে টেনে নিয়ে যাও জাহান্নামের আগুনের মাঝখানে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4462,44,48,'অতঃপর তার মাথার উপর ফুটন্ত পানি ঢেলে ‘আযাব দাও। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4463,44,49,'(বলা হবে) গ্রহণ কর স্বাদ-তুমি তো ছিলে ক্ষমতাশালী, সম্মানী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4464,44,50,'এ হল তাই যাতে তোমরা সন্দেহ করতে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4465,44,51,'নিশ্চয়ই মুত্তাকীরা থাকবে নিরাপদ স্থানে, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4466,44,52,'বাগান আর ঝরণার মাঝে ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4467,44,53,'তারা পরিধান করবে পাতলা ও পুরু রেশমী কাপড়, আর বসবে মুখোমুখী হয়ে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4468,44,54,'এ রকমই হবে, আর তাদের বিয়ে দিয়ে দেব ডাগর ডাগর সুন্দর উজ্জ্বল চোখওয়ালা কুমারীদের (হুরদের) সাথে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4469,44,55,'সেখানে তারা পূর্ণ শান্তি ও নিরাপত্তার সাথে নানান ফলমূল আনতে বলবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4470,44,56,'সেখানে তারা মৃত্যু আস্বাদন করবে না (সেই) প্রথম মৃত্যুর পর, আর তিনি তাদেরকে আগুনের শাস্তি থেকে রক্ষা করবেন ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4471,44,57,'তোমার প্রতিপালকের অনুগ্রহ স্বরূপ, এটাই হল মহা সাফল্য। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4472,44,58,'আমি তোমার ভাষায় কুরআনকে সহজ করে দিয়েছি যাতে তারা উপদেশ গ্রহণ করে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4473,44,59,'কাজেই তুমি অপেক্ষা কর (তাদের পরিণতি দেখার জন্য) তারাও অপেক্ষা করছে (তোমাদের পরিণতি দেখার জন্য)।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4474,45,1,'হা-মীম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4475,45,2,'এ কিতাব অবতীর্ণ হয়েছে মহা পরাক্রমশালী প্রজ্ঞাময় আল্লাহর নিকট হতে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4476,45,3,'আকাশে এবং যমীনে মু’মিনদের (শিক্ষার) জন্য অবশ্যই নিদর্শন আছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4477,45,4,'তোমাদের সৃষ্টিতে, আর প্রাণীকুল ছড়িয়ে দেয়ার মাঝে নিদর্শন আছে নিশ্চিত বিশ্বাসীদের জন্য। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4478,45,5,'রাত ও দিনের আবর্তনে, আর আল্লাহ আকাশ থেকে যে বৃষ্টি বর্ষণ করেন তা দিয়ে যমীনকে তার মৃত্যুর পর আবার জীবিত করেন আর বায়ুর পরিবর্তনে জ্ঞানী সম্প্রদায়ের জন্য নিদর্শন রয়েছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4479,45,6,'এগুলো হল আল্লাহর আয়াত যা সত্যতা সহকারে তোমার কাছে তিলাওয়াত করা হচ্ছে। কাজেই তারা আল্লাহ এবং তাঁর আয়াতের (উপর বিশ্বাস না করলে এর) পর আর কোন্ কথায় বিশ্বাস করবে? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4480,45,7,'ধ্বংস প্রত্যেক মিথ্যাবাদী পাপাচারীর জন্য ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4481,45,8,'যে আল্লাহর আয়াত শোনে যা তার সামনে পাঠ করা হয়, অতঃপর অহমিকার সাথে (কুফুরীর উপর) থাকে যেন সে তা শোনেইনি; কাজেই তাকে ভয়াবহ শাস্তির সংবাদ দাও। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4482,45,9,'আমার আয়াতগুলোর কোন কথা যখন সে অবগত হয় তখন তাকে ঠাট্টা বিদ্রূপের বিষয় বানিয়ে নেয়, তাদের জন্য আছে অপমানজনক শাস্তি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4483,45,10,'তাদের আড়ালে আছে জাহান্নাম, তাদের কৃতকর্ম তাদের কোন কাজে আসবে না। আর আল্লাহর পরিবর্তে তারা যেগুলোকে অভিভাবক হিসেবে গ্রহণ করেছে সেগুলোও (কাজে আসবে) না। তাদের জন্য আছে মহা শাস্তি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4484,45,11,'এ (কুরআন) সঠিক পথের দিশারী। যারা তাদের পালনকর্তার নিদর্শনগুলোকে প্রত্যাখ্যান করে, তাদের জন্য আছে কঠোর যন্ত্রণাদায়ক শাস্তি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4485,45,12,'আল্লাহ- যিনি সমুদ্রকে তোমাদের কল্যাণে নিয়োজিত করেছেন যাতে তাঁর হুকুমে তোমাদের নৌযানসমূহ তাতে চলাচল করতে পারে, আর যাতে তোমরা তাঁর অনুগ্রহ তালাশ কর এবং তোমরা (তাঁর) শোকর আদায় কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4486,45,13,'আর তিনি তোমাদের কল্যাণে নিয়োজিত রেখেছেন যা আছে আকাশে আর যা আছে যমীনে সেগুলোর সব কিছুকে। এতে চিন্তাশীল মানুষদের জন্য অবশ্যই অনেক নিদর্শন আছে।  ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4487,45,14,'মু’মিনদেরকে বল ঐ লোকদেরকে ক্ষমা করতে যারা আল্লাহর ‘আযাবের দিন আসার ব্যাপারে কোন আশংকাবোধ করে না। কেননা আল্লাহই (ভাল বা মন্দ) প্রত্যেক সম্প্রদায়কে তাদের কৃতকর্ম অনুসারে প্রতিফল দিবেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4488,45,15,'যে লোক ভাল কাজ করবে, সে তার নিজের কল্যাণেই তা করবে আর যে মন্দ কাজ করবে তার কুফল সে-ই ভোগ করবে। অতঃপর তোমাদেরকে তোমাদের প্রতিপালকের নিকট ফিরিয়ে নেয়া হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4489,45,16,'আমি বানী ইসরাঈলকে কিতাব, রাজত্ব ও নুবুওয়াত দিয়েছিলাম আর তাদেরকে দিয়েছিলাম উত্তম রিযক, আর তাদেরকে বিশ্ববাসীর উপর শ্রেষ্ঠত্ব দিয়েছিলাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4490,45,17,'আর দ্বীনের ব্যাপারে তাদেরকে দিয়েছিলাম সুস্পষ্ট নিদর্শন। কিন্তু তাদের কাছে (সত্য মিথ্যা সম্পর্কিত নির্ভুল) জ্ঞান আসার পরও শুধু নিজেদের মধ্যে বাড়াবাড়ির কারণে তারা মতভেদ করেছিল। তারা যে বিষয়ে মতভেদ করত, তোমার প্রতিপালক কিয়ামাতের দিন সে বিষয়ে তাদের মধ্যে ফয়সালা করে দেবেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4491,45,18,'অতঃপর (হে নবী সা.!) আমি তোমাকে দ্বীনের (সঠিক) পথের উপর প্রতিষ্ঠিত করেছি, কাজেই তুমি তারই অনুসরণ কর, আর যারা (দ্বীনের বিধি-বিধান) জানেনা তাদের খেয়ালখুশির অনুসরণ করো না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4492,45,19,'আল্লাহ হতে তারা তোমার কোনই উপকার করতে পারবে না। যালিমরা একে অপরের বন্ধু, আর আল্লাহ মুত্তাকীদের বন্ধু। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4493,45,20,'এ (কুরআন) মানুষের জন্য জ্ঞানের আলো, আর নিশ্চিত বিশ্বাসীদের জন্য পথের দিশারী এবং রহমত স্বরূপ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4494,45,21,'যারা অন্যায় কাজ করে তারা কি এ কথা ভেবে নিয়েছে যে, আমি তাদেরকে আর ঈমান গ্রহণকারী সৎকর্মশীলদেরকে সমান গণ্য করব যার ফলে তাদের উভয় দলের জীবন ও মৃত্যু সমান হয়ে যাবে? কতই না মন্দ তাদের ফয়সালা! ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4495,45,22,'আল্লাহ আকাশ ও যমীন সৃষ্টি করেছেন এক সত্যিকার লক্ষ্যে যাতে প্রত্যেক ব্যক্তিকে তার কৃতকর্ম অনুযায়ী প্রতিফল দেয়া যেতে পারে, আর তাদের প্রতি কোন যুলম করা হবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4496,45,23,'তুমি কি লক্ষ্য করেছ তার প্রতি যে তার খেয়াল-খুশিকে ইলাহ বানিয়ে নিয়েছে? আল্লাহ জেনে শুনেই তাকে পথভ্রষ্ট করেছেন আর তার কানে ও দিলে মোহর মেরে দিয়েছেন আর তার চোখের উপর টেনে দিয়েছেন পর্দা। অতঃপর আল্লাহর পর আর কে (আছে যে) তাকে সঠিক পথ দেখাবে? এরপরও কি তোমরা শিক্ষাগ্রহণ করবে না? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4497,45,24,'তারা বলে- জীবন বলতে তো শুধু আমাদের এ দুনিয়ারই জীবন, আমরা মরি আর বেঁচে থাকি (এখানেই)। কালের প্রবাহ ছাড়া অন্য কিছুই আমাদেরকে ধ্বংস করে না। আসলে এ ব্যাপারে তাদের কোন জ্ঞানই নেই। তারা শুধু ধারণা করে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4498,45,25,'তাদের সামনে যখন আমার সুস্পষ্ট আয়াতগুলো পাঠ করা হয়, তখন তাদের কাছে এ কথা বলা ছাড়া আর কোন যুক্তি থাকে না যে, তোমরা যদি সত্যবাদীই হয়ে থাক, তাহলে আমাদের পূর্বপুরুষদেরকে হাজির কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4499,45,26,'বল- আল্লাহই তোমাদেরকে জীবন দান করেন অতঃপর তোমাদের মৃত্যু ঘটান। অতঃপর তিনি তোমাদেরকে ক্বিয়ামতের দিন একত্রিত করবেন যাতে কোন সন্দেহ নেই। কিন্তু অধিকাংশ মানুষ তা জানে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4500,45,27,'আকাশ ও যমীনের রাজত্ব আল্লাহরই, যে দিন ক্বিয়ামত সংঘটিত হবে, সেদিন মিথ্যার অনুসারীরা ধ্বংস হয়ে যাবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4501,45,28,'আর প্রত্যেক সম্প্রদায়কে দেখবে নতজানু হয়ে আছে। প্রত্যেক সম্প্রদায়কে তার ‘আমালনামার পানে আহবান করা হবে। (আর বলা হবে) তোমরা যা করতে আজ তার প্রতিফল দেয়া হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4502,45,29,'আমার এ কিতাব তোমাদের ব্যাপারে সত্য কথাই বলবে, তোমরা যা করতে আমি তাই-ই লিখে রাখতাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4503,45,30,'যারা ঈমান আনে আর সৎকর্ম করে তাদের প্রতিপালক তাদেরকে তাঁর রহমাতের মধ্যে দাখিল করবেন। ওটাই সুস্পষ্ট সাফল্য। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4504,45,31,'আর যারা কুফরী করেছিল তাদেরকে বলা হবে- আমার আয়াতগুলো কি তোমাদের কাছে পাঠ করা হয়নি? তখন তোমরা অহঙ্কার করেছিলে আর তোমরা ছিলে এক অপরাধী জাতি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4505,45,32,'যখন বলা হয়- আল্লাহর প্রতিশ্রুতি সত্য আর ক্বিয়ামতের আগমনে কোনই সন্দেহ নেই, তখন তোমরা বলতে- ‘আমরা জানি না ক্বিয়ামত কী? আমরা মনে করি তা শুধু ধারণা মাত্র, আর (তাতে) আমরা নিশ্চিত বিশ্বাসী নই’। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4506,45,33,'তারা যে সব মন্দ কাজ করত সেগুলো তাদের কাছে প্রকাশ হয়ে পড়বে আর তারা যা নিয়ে ঠাট্টা বিদ্রূপ করত তা তাদেরকে ঘিরে ফেলবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4507,45,34,'আর বলা হবে- ‘আজ আমি তোমাদেরকে ভুলে যাব যেমন করে তোমরা এ দিনের সাক্ষাৎকে ভুলে গিয়েছিলে। তোমাদের বাসস্থান হবে জাহান্নাম আর তোমাদের কোন সাহায্যকারী থাকবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4508,45,35,'এটা এজন্য যে, তোমরা আল্লাহর আয়াতগুলোকে ঠাট্টা বিদ্রূপের বিষয় বানিয়ে নিয়েছিলে, আর দুনিয়ার জীবন তোমাদেরকে ধোঁকায় ফেলে রেখেছিল। কাজেই আজ তাদেরকে জাহান্নাম থেকে বের করা হবে না আর তাওবাহ করার সুযোগ দেয়া হবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4509,45,36,'অতএব প্রশংসা আল্লাহরই জন্য যিনি আসমানের প্রতিপালনকারী, যমীনের প্রতিপালনকারী, বিশ্বজগতের প্রতিপালনকারী । ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4510,45,37,'আকাশ ও যমীনে তাঁরই শ্রেষ্ঠত্ব ও প্রাধান্য, আর তিনি মহাপরাক্রমশালী, প্রজ্ঞাময়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4511,46,1,'হা-মীম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4512,46,2,'কিতাব অবতীর্ণ হয়েছে মহা পরাক্রমশালী প্রজ্ঞাময় আল্লাহর নিকট হতে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4513,46,3,'আমি আকাশ, যমীন ও এ দু’য়ের মাঝে যা আছে তা প্রকৃত উদ্দেশে একটা নির্দিষ্ট সময়ের জন্য সৃষ্টি করেছি। কিন্তু কাফিরগণ, যে বিষয়ে তাদেরকে সতর্ক করা হয় তাত্থেকে মুখ ফিরিয়ে নেয়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4514,46,4,'বল- তোমরা আল্লাহর পরিবর্তে যাদেরকে ডাক, তাদের বিষয়ে চিন্তা করে দেখেছ কি? দেখাও আমাকে তারা যমীনে কী সৃষ্টি করেছে অথবা আকাশমন্ডলে তাদের কোন অংশীদারিত্ব আছে কি? এর আগের কোন কিতাব অথবা পরম্পরাগত কোন জ্ঞান আমার কাছে উপস্থিত কর যদি তোমরা সত্যবাদী হও। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4515,46,5,'তার চেয়ে অধিক গুমরাহ কে, যে আল্লাহর পরিবর্তে এমন কিছুকে ডাকে যা ক্বিয়ামত পর্যন্ত তাকে সাড়া দেবে না, আর তাদের ডাকাডাকি সম্পর্কেও তারা (একদম) বেখবর? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4516,46,6,'ক্বিয়ামতের দিন মানুষকে যখন একত্রিত করা হবে, তখন ঐগুলো (অর্থাৎ উপাস্যরা) হবে মানুষের শত্রু আর মানুষ যে তাদের ‘ইবাদাত করেছিল তা তারা অস্বীকার করবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4517,46,7,'তাদের কাছে যখন আমার সুস্পষ্ট আয়াত আবৃত্তি করা হয় আর সত্য যখন তাদের কাছে উপস্থিত হয়, তখন কাফিররা বলে- এটাতো প্রকাশ্য যাদু। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4518,46,8,'তারা কি বলতে চায় যে, রসূল নিজেই তা রচনা করেছে? বল, তা যদি আমি রচনা করে থাকি তাহলে তোমরা আল্লাহর শাস্তি হতে আমাকে কিছুতেই বাঁচাতে পারবে না। আল্লাহ ভাল করেই জানেন যে বিষয়ে তোমরা মত্ত আছ। আমার আর তোমাদের মধ্যে সাক্ষী হিসেবে তিনিই যথেষ্ট, আর তিনি বড়ই ক্ষমাশীল, বড়ই দয়ালু। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4519,46,9,'বল, আমি রসূলদের মধ্যে নতুন নই, আর আমি এও জানি না যে, আমার সঙ্গে কী ব্যবহার করা হবে আর তোমাদের সঙ্গেইবা কেমন (ব্যবহার করা হবে), আমি কেবল তাই মেনে চলি যা আমার প্রতি ওয়াহী করা হয়। আমি তো স্পষ্ট সতর্ককারী মাত্র। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4520,46,10,'বল- তোমরা কি ভেবে দেখেছ যদি এ (কুরআন) আল্লাহর পক্ষ থেকে এসে থাকে আর তা তোমরা প্রত্যাখ্যান কর অথচ এ ধরনের কালাম সম্পর্কে বানী ইসরাঈলের একজন [‘আবদুল্লাহ বিন সালাম (রাযি.)] সাক্ষ্যও দিয়েছে। সে ঈমান আনল আর তোমরা অহঙ্কার করলে? আল্লাহ যালিম লোকদেরকে সঠিক পথ দেখান না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4521,46,11,'কাফিররা মু’মিনদের সম্পর্কে বলে, তা (অর্থাৎ কুরআন) যদি ভাল হত তাহলে তারা আমাদেরকে পেছনে ফেলে ওটার দিকে এগিয়ে যেতে পারত না (আমরাই কুরআনকে আগে গ্রহণ করে নিতাম) আর যেহেতু তারা এর দ্বারা (অর্থাৎ কাফিররা এ কুরআন দ্বারা) সঠিক পথ পায়নি, সে কারণে তারা অবশ্যই বলবে- এটা এক পুরনো মিথ্যে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4522,46,12,'অথচ ইতোপূর্বে মূসার কিতাব এসেছিল পথপ্রদর্শক ও রহমত স্বরূপ। আর (এখন অবতীর্ণ) এ কিতাব তার সমর্থক, আরবী ভাষায়, যালিমদেরকে সতর্ক করার জন্য আর সৎকর্মশীলদেরকে সুসংবাদ দেয়ার জন্য। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4523,46,13,'যারা বলে, ‘আমাদের প্রতিপালক আল্লাহ’, অতঃপর (তাদের কথার উপর) সুদৃঢ় থাকে, তাহলে তাদের কোন ভয় নেই, আর তারা দুঃখিত হবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4524,46,14,'তারাই জান্নাতের অধিবাসী, তাতে তারা চিরকাল থাকবে, তারা যে কাজ করত তার পুরস্কার স্বরূপ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4525,46,15,'আমি মানুষকে নির্দেশ দিয়েছি তার পিতামাতার প্রতি সদয় আচরণের। তার মা তাকে বহন করেছে কষ্টের সাথে, আর তাকে প্রসব করেছে কষ্টের সাথে। তাকে গর্ভে ধারণ ও দুধপান ছাড়ানোয় সময় লাগে ত্রিশ মাস। অবশেষে সে যখন পূর্ণ শক্তি লাভ করে এবং চল্লিশ বছরে পৌঁছে যায়, তখন সে বলে, ‘হে আমার প্রতিপালক! তুমি আমাকে আর আমার পিতা-মাতাকে যে নি‘মাত দান করেছ তজ্জন্য কৃতজ্ঞতা প্রকাশ করার শক্তি আমাকে দান কর, আর আমাকে এমন সৎকর্ম করার সামর্থ দাও যাতে তুমি সন্তুষ্ট হও, আমার সন্তানদেরকে সৎকর্মপরায়ণ ক’রে আমার প্রতি অনুগ্রহ কর, আমি অনুশোচনাভরে তোমার দিকে ফিরে আসছি, আর আমি অনুগত বান্দাহদের অন্তর্ভুক্ত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4526,46,16,'এই লোকদের হতে আমি তাদের সর্বোত্তম ‘আমালগুলো গ্রহণ করি আর তাদের মন্দ কাজগুলো ক্ষমা করে দেই, তারা জান্নাতবাসীদের অন্তর্ভুক্ত। তাদেরকে যে ও‘য়াদা দেয়া হয়েছে তা সত্য ও‘য়াদা। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4527,46,17,'আর যে নিজ পিতামাতাকে বলে, ‘তোমাদের জন্য আফসোস! তোমরা কি আমাকে ভয় দেখাও যে, (মৃতুর পর) আমাকে উঠানো হবে অথচ আমার পূর্বে বহু মানব গোষ্ঠী অতীত হয়ে গেছে (কই, কেউ তো উঠে আসল না)। বাপ-মা আল্লাহর কাছে প্রার্থনা জানিয়ে (সন্তানকে তিরস্কার করে) বলে- দুর্ভোগ তোমার জন্য, তুমি ঈমান আন, আল্লাহর ও‘য়াদা সত্য।’ তখন সে বলে- ‘এ সব পুরান কালের কাহিনী ছাড়া আর কিছুই না।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4528,46,18,'এরা হল তারাই যাদের প্রতি আল্লাহর ‘আযাবের সিদ্ধান্ত হয়ে গেছে, তাদের মত জ্বিন ও মানুষের মধ্যে হতে যারা তাদের পূর্বে গত হয়ে গেছে। এরা চরমভাবে ক্ষতিগ্রস্ত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4529,46,19,'প্রত্যেকের জন্য মর্যাদা আছে তার কৃতকর্ম অনুসারে, যেন আল্লাহ তাদের কর্মের পুরোপুরি প্রতিফল দেন। তাদের উপর কক্ষনো যুলম করা হবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4530,46,20,'যেদিন কাফিরদেরকে জাহান্নামের সামনে হাজির করা হবে, (তাদেরকে বলা হবে)- ‘তোমরা তোমাদের পার্থিব জীবনেই তোমাদের অংশের নি‘মাতগুলো নিঃশেষ করেছ আর তা ভোগ করেছ। কাজেই আজ তোমাদেরকে অপমানজনক শাস্তি দ্বারা প্রতিফল দেয়া হবে, কেননা তোমরা দুনিয়াতে অন্যায়ভাবে অহঙ্কার করেছিলে আর না-ফরমানী করেছিলে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4531,46,21,'স্মরণ কর ‘আদ জাতির ভ্রাতার (হূদ) কথা। সে তার আহকাফবাসী সম্প্রদায়কে সতর্ক করেছিল। (এ ধরনের) সতর্ককারীরা তার পূর্বেও এসেছিল আর তার পরেও এসেছিল (যারা বলেছিল) যে, ‘তোমরা আল্লাহ ছাড়া কারো ‘ইবাদাত করো না। আমি তোমাদের উপর এক ভয়াবহ দিনের ‘আযাবের আশঙ্কা করছি।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4532,46,22,'লোকেরা বলেছিল- ‘তুমি কি আমাদেরকে আমাদের উপাস্যগুলো হতে সরিয়ে নেয়ার জন্য আমাদের কাছে এসেছ। কাজেই তুমি আমাদেরকে যে শাস্তির ভয় দেখাচ্ছ তা নিয়ে এসো আমাদের কাছে যদি তুমি সত্যবাদী হও।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4533,46,23,'সে বলল (‘আযাব কখন আসবে না আসবে) ‘সে বিষয়ের জ্ঞান তো আল্লাহর নিকট আছে। আমি যা নিয়ে প্রেরিত হয়েছি তাই শুধু তোমাদের কাছে পৌঁছে দিচ্ছি। কিন্তু আমি দেখছি তোমরা এমন এক জাতি যারা মূর্খের আচরণ করছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4534,46,24,'অতঃপর তারা যখন তাদের উপত্যকার দিকে মেঘ আসতে দেখল তখন তারা বলল- ‘এ তো মেঘ, আমাদেরকে বৃষ্টি দিবে।’ না, তা হল সেই জিনিস তোমরা যা তাড়াতাড়ি নিয়ে আসতে চেয়েছিলে। এ হল ঝড়, যাতে আছে ভয়াবহ ‘আযাব। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4535,46,25,'ওটা তার প্রতিপালকের নির্দেশে সবকিছু ধ্বংস করে দেবে। অবস্থা এই দাঁড়াল যে, তাদের (ধ্বংসপ্রাপ্ত) বসতিগুলো ছাড়া আর কিছু দেখা যাচ্ছিল না। অপরাধী জাতিকে আমি এভাবেই প্রতিফল দিয়ে থাকি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4536,46,26,'তাদেরকে আমি যতটা সুদৃঢ়ভাবে প্রতিষ্ঠিত করেছিলাম, তোমাদেরকে (হে কুরায়শ!) তেমন সুদৃঢ়ভাবে প্রতিষ্ঠিত করিনি আর তাদেরকে দিয়েছিলাম কান, চোখ ও হৃদয়। কিন্তু তাদের কান, তাদের চোখ আর তাদের হৃদয় তাদের কোন উপকারে আসেনি যেহেতু তারা আল্লাহর আয়াতসমূহকে অস্বীকার করত। তারা যা নিয়ে ঠাট্টা বিদ্রূপ করত তা-ই তাদেরকে ঘিরে ফেলল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4537,46,27,'আমি ধ্বংস করেছিলাম তোমাদের চারপাশের জনপদ। আমি নানাভাবে নিদর্শন দেখিয়েছিলাম যাতে তারা (সঠিক পথে) ফিরে আসে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4538,46,28,'আল্লাহর নৈকট্য লাভের উদ্দেশে আল্লাহ ব্যতিরেকে যাদেরকে তারা ইলাহরূপে গ্রহণ করেছে তারা তাদেরকে সাহায্য করল না কেন? বরং তারা (অর্থাৎ কল্পিত ইলাহ) তাদের থেকে হারিয়ে গেল। আসলে তা ছিল তাদের মিথ্যাচার আর মনগড়া উদ্ভাবণ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4539,46,29,'স্মরণ কর, যখন জ্বিনদের একটি দলকে তোমার প্রতি ফিরিয়ে দিয়েছিলাম যারা কুরআন শুনছিল। তারা যখন সে স্থানে উপস্থিত হল, তখন তারা পরস্পরে বলল- চুপ করে শুন। পড়া যখন শেষ হল তখন তারা তাদের সম্প্রদায়ের কাছে ফিরে গেল সতর্ককারীরূপে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4540,46,30,'(ফিরে গিয়ে) তারা বলল- হে আমাদের সম্প্রদায়! আমরা একটি কিতাব (এর পাঠ) শুনেছি যা মূসার পরে অবতীর্ণ হয়েছে, তা পূর্বেকার কিতাবগুলোর সত্যতা প্রতিপন্ন করে, সত্যের দিকে আর সঠিক পথের দিকে পরিচালিত করে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4541,46,31,'হে আমাদের সম্প্রদায়! আল্লাহর দিকে আহবানকারীর প্রতি সাড়া দাও এবং তার প্রতি ঈমান আন, আল্লাহ তোমাদের গুনাহ মাফ করে দেবেন আর তোমাদেরকে যন্ত্রণাদায়ক ‘আযাব থেকে রক্ষা করবেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4542,46,32,'আর যে আল্লাহর দিকে আহবানকারীর প্রতি সাড়া দিবে না, দুনিয়াতে সে আল্লাহকে ব্যর্থ করতে পারবে না, আর আল্লাহকে বাদ দিয়ে নেই তার কোন সাহায্যকারী, পৃষ্ঠপোষক। তারা আছে সুস্পষ্ট গুমরাহীতে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4543,46,33,'তারা কি দেখে না যে, আল্লাহ, যিনি আকাশ ও যমীন সৃষ্টি করেছেন আর ওগুলোর সৃষ্টিতে তিনি ক্লান্ত হননি, তিনি মৃতদেরকে জীবন দিতে সক্ষম? নিঃসন্দেহে তিনি সকল বিষয়ের উপর ক্ষমতাবান। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4544,46,34,'কাফিরদেরকে যেদিন জাহান্নামের সামনে হাজির করা হবে (তখন তাদেরকে বলা হবে) এটা কি সত্য নয়? তারা বলবে, ‘কসম আমাদের প্রতিপালকের! এটা সত্য।’ তাদেরকে বলা হবে, ‘তাহলে ‘আযাব আস্বাদন কর, যেহেতু তোমরা সত্যকে অমান্য করেছিলে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4545,46,35,'কাজেই তুমি ধৈর্য ধর যেমনভাবে ধৈর্য ধারণ করেছিল দৃঢ় সংকল্পের অধিকারী রসূলগণ। আর এই লোকেদের ব্যাপারে তাড়াহুড়া করো না। কারণ যে বিষয়ে তাদেরকে সাবধান করা হয়েছে যেদিন তারা তা দেখবে, সেদিন তারা মনে করবে যে, একদিনের কিছু অংশের অধিক তারা দুনিয়াতে অবস্থান করেনি। (তোমার দায়িত্ব) পৌঁছানো, অতঃপর পাপাচারী সম্প্রদায় ছাড়া আর কে ধ্বংস হবে? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4546,47,1,'যারা কুফুরী করে এবং আল্লাহর পথে বাধা সৃষ্টি করে, আল্লাহ তাদের সকল কর্ম ব্যর্থ করে দেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4547,47,2,'আর যারা ঈমান আনে ও সৎকর্ম করে আর মুহাম্মাদের প্রতি যা অবতীর্ণ হয়েছে তাতে বিশ্বাস স্থাপন করে- কারণ তা তাদের প্রতিপালকের প্রেরিত সত্য- তিনি তাদের মন্দ কাজগুলো মুছে দেবেন, আর তাদের অবস্থার উন্নতি ঘটাবেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4548,47,3,'এর কারণ এই যে, যারা কুফুরী করে তারা মিথ্যার অনুসরণ করে, আর যারা ঈমান আনে তারা তাদের প্রতিপালকের প্রেরিত সত্যের অনুসরণ করে। এমনিভাবে আল্লাহ মানুষের জন্য তাদের (মধ্যেকার পাপী এবং পুণ্যবানের) দৃষ্টান্ত বর্ণনা করেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4549,47,4,'অতঃপর যখন তোমরা কাফিরদের সঙ্গে যুদ্ধে অবতীর্ণ হও, তখন তাদের ঘাড়ে আঘাত হানো, অবশেষে যখন তাদেরকে পূর্ণরূপে পরাস্ত কর, তখন তাদেরকে শক্তভাবে বেঁধে ফেল। অতঃপর হয় তাদের প্রতি অনুগ্রহ কর, না হয় তাদের থেকে মুক্তিপণ গ্রহণ কর। তোমরা যুদ্ধ চালিয়ে যাবে, যে পর্যন্ত না শত্রুপক্ষ অস্ত্র সমর্পণ করে। এ নির্দেশই তোমাদেরকে দেয়া হল। আল্লাহ ইচ্ছে করলে (নিজেই) তাদের থেকে প্রতিশোধ নিতে পারতেন। কিন্তু তিনি তোমাদের একজনকে অন্যের দ্বারা পরীক্ষা করতে চান (এজন্য তোমাদেরকে যুদ্ধ করার সুযোগ দেন)। যারা আল্লাহর পথে শহীদ হয় তিনি তাদের কর্মফল কক্ষনো বিনষ্ট করবেন না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4550,47,5,'তিনি তাদেরকে সঠিক পথে পরিচালিত করেন আর তাদের অবস্থা ভাল করে দেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4551,47,6,'অতঃপর তিনি তাদেরকে জান্নাতে প্রবিষ্ট করবেন যা তাদেরকে তিনি জানিয়ে দিয়েছেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4552,47,7,'হে ঈমানদারগণ! তোমরা যদি আল্লাহকে সাহায্য কর, তিনি তোমাদেরকে সাহায্য করবেন আর তোমাদের পাগুলোকে দৃঢ়প্রতিষ্ঠ করবেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4553,47,8,'যারা কুফরী করে তাদের জন্য দুর্ভোগ আর তিনি তাদের কর্মকে বিনষ্ট করে দেবেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4554,47,9,'তা এজন্য যে, আল্লাহ যা অবতীর্ণ করেছেন তারা তা অপছন্দ করে, কাজেই আল্লাহ তাদের কর্ম ব্যর্থ করেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4555,47,10,'তারা কি পৃথিবীতে ভ্রমণ করেনি অতঃপর দেখেনি তাদের আগে যারা ছিল তাদের পরিণাম কী হয়েছে? আল্লাহ তাদেরকে ধ্বংস করে দিয়েছেন, কাফিরদের জন্য আছে অনুরূপ শাস্তি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4556,47,11,'এর কারণ এই যে, যারা ঈমান আনে আল্লাহ তাদের অভিভাবক আর কাফিরদের কোন অভিভাবক নেই। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4557,47,12,'যারা ঈমান আনে আর সৎকাজ করে আল্লাহ তাদেরকে প্রবিষ্ট করবেন জান্নাতে যার তলদেশে ঝর্ণাধারা প্রবাহিত। আর যারা কুফুরী করে তারা ভোগ বিলাসে মত্ত থাকে আর আহার করে যেভাবে আহার করে জন্তু জানোয়াররা। জাহান্নামই তাদের বাসস্থান। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4558,47,13,'তোমার যে জনপদ থেকে তারা তোমাকে বের করে দিয়েছে তার অপেক্ষা শক্তিশালী কত জনপদকে আমি ধ্বংস করে দিয়েছি, অতঃপর কেউ ছিল না তাদের সাহায্যকারী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4559,47,14,'যে ব্যক্তি তার প্রতিপালক থেকে আগত সুস্পষ্ট প্রমাণের উপর প্রতিষ্ঠিত সে কি তার মত যার কাছে তার মন্দ কর্ম সুশোভিত করা হয়েছে আর তারা নিজেদের খেয়ালখুশির অনুসরণ করে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4560,47,15,'মুত্তাক্বীদেরকে যে জান্নাতের ও‘য়াদা দেয়া হয়েছে তার উপমা হলঃ তাতে আছে নির্মল পানির ঝর্ণা, আর আছে দুধের নদী যার স্বাদ অপরিবর্তণীয়, আছে পানকারীদের জন্য সুস্বাদু মদের নদী আর পরিশোধিত মধুর নদী। তাদের জন্য সেখানে আছে সব রকম ফলমূল আর তাদের প্রতিপালকের নিকট হতে ক্ষমা। (এরা কি) তাদের মত যারা চিরকাল থাকবে জাহান্নামে যাদেরকে পান করতে দেয়া হবে গরম পানীয় যা তাদের নাড়িভুঁড়ি ছিন্ন ভিন্ন করে ফেলবে? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4561,47,16,'তাদের মধ্যে কতক লোক তোমার কথা শুনে, অতঃপর যখন তারা তোমার কাছ থেকে বেরিয়ে যায়, তখন যাদেরকে জ্ঞান দেয়া হয়েছে তাদেরকে বলে- এই মাত্র সে কী বলল? এদের অন্তরে আল্লাহ মোহর মেরে দিয়েছেন আর তারা নিজেদের খেয়ালখুশির অনুসরণ করে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4562,47,17,'যারা সঠিক পথে চলে আল্লাহ তাদের সৎপথ প্রাপ্তি বৃদ্ধি করে দেন আর তাদেরকে তাক্বওয়া দান করেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4563,47,18,'তারা কি শুধু এ অপেক্ষায় আছে যে, ক্বিয়ামত তাদের কাছে অকস্মাৎ এসে পড়ুক? ক্বিয়ামতের লক্ষণগুলো তো এসেই গেছে। কাজেই তা এসে পড়লে তারা উপদেশ গ্রহণ করবে কেমন করে? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4564,47,19,'কাজেই জেনে রেখ, আল্লাহ ছাড়া সত্যিকারের কোন ইলাহ নেই, ক্ষমা প্রার্থনা কর তোমার ভুলত্রুটির জন্য আর মু’মিন ও মু’মিনাদের জন্য, আল্লাহ তোমাদের গতিবিধি ও অবস্থান সম্পর্কে অবগত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4565,47,20,'মু’মিনরা বলে- একটি সূরাহ নাযিল হয় না কেন? অতঃপর যখন কোন সুস্পষ্ট অর্থবোধক সূরাহ অবতীর্ণ হয় আর তাতে যুদ্ধের কথা উল্লেখ থাকে, তখন যাদের অন্তরে রোগ আছে তুমি তাদেরকে দেখবে মৃত্যুর ভয়ে জ্ঞানহারা লোকের মত তোমার দিকে তাকাচ্ছে। কাজেই ধ্বংস তাদের জন্য। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4566,47,21,'(আল্লাহর) আনুগত্য করা ও ন্যায়সঙ্গত কথা বলা। অতঃপর যুদ্ধের সিন্ধান্ত হলে তারা যদি আল্লাহর নিকট দেয়া অঙ্গীকার পূর্ণ করত, তবে তা তাদের জন্য কল্যাণকর হত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4567,47,22,'ক্ষমতা পেলে সম্ভবতঃ তোমরা পৃথিবীতে বিপর্যয় সৃষ্টি করবে আর আত্মীয়তার বন্ধন ছিন্ন করবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4568,47,23,'এদের প্রতিই আল্লাহ অভিসম্পাত করেন, অতঃপর তাদেরকে বধির করেন আর তাদের দৃষ্টিশক্তিকে করেন অন্ধ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4569,47,24,'তারা কি কুরআন সম্বন্ধে গভীরভাবে চিন্তা করে না, না তাদের অন্তরে তালা দেয়া আছে? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4570,47,25,'যাদের কাছে সঠিক পথ সুস্পষ্ট হওয়ার পর তারা পিছনে ফিরে যায়, শয়ত্বন তাদের জন্য তাদের কাজকে সুন্দর করে দেখায়, আর তাদেরকে দেয় মিথ্যা আশা। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4571,47,26,'এ কারণেই আল্লাহ যা অবতীর্ণ করেছেন তাকে যারা অপছন্দ করে তাদেরকে তারা বলে দিয়েছে যে, কোন কোন ব্যাপারে আমরা তোমাদেরকে মানবো। আল্লাহ তাদের গোপন কথাবার্তাকে খুব ভাল করেই জানেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4572,47,27,'তখন কেমন দশা হবে যখন ফেরেশতারা তাদের মুখে আর পিঠে মারতে মারতে তাদের জান বের করবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4573,47,28,'এর কারণ এই যে, তারা তারই অনুসরণ করে যা আল্লাহকে অসন্তুষ্ট করে, আর তারা তাঁর সন্তোষকে অপছন্দ করে, ফলে তিনি তাদের সমস্ত ‘আমাল নষ্ট করে দিয়েছেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4574,47,29,'যাদের অন্তরে রোগ আছে তারা কি মনে করে যে, আল্লাহ কক্ষনো তাদের লুকানো বিদ্বেষভাব প্রকাশ করে দিবেন না? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4575,47,30,'আমি যদি ইচ্ছে করতাম তাহলে আমি তোমায় ওদেরকে দেখিয়ে দিতাম। তুমি তাদের মুখ দেখে অবশ্যই চিনতে পারবে আর তাদের কথাবার্তার ধরন দেখে তুমি তাদেরকে অবশ্যই অবশ্যই চিনতে পারবে। আল্লাহ তোমাদের ‘আমাল সম্পর্কে ভালভাবেই জানেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4576,47,31,'আমি তোমাদেরকে অবশ্য অবশ্যই পরীক্ষা করব যতক্ষণ না আমি জেনে নিতে পারি তোমাদের মধ্যে মুজাহিদ আর ধৈর্যশীলদেরকে, আর তোমাদের অবস্থা যাচাই করতে পরি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4577,47,32,'যারা কুফুরী করে আর আল্লাহর পথে চলতে বাধা সৃষ্টি করে, আর তাদের নিকট সত্য পথ স্পষ্ট হয়ে যাওয়ার পরও রসূলের বিরোধিতা করে, তারা আল্লাহর কোনই ক্ষতি করতে পারবে না। আল্লাহ তাদের যাবতীয় ‘আমাল নষ্ট করে দিবেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4578,47,33,'হে ঈমানদারগণ! তোমরা আল্লাহর আনুগত্য কর আর রসূলের আনুগত্য কর আর তোমাদের ‘আমালগুলোকে নষ্ট করে দিও না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4579,47,34,'যারা কুফরী করে আর (মানুষকে) আল্লাহর পথে চলতে বাধা দেয় আর এভাবে কাফির অবস্থাতেই মৃত্যুবরণ করে, আল্লাহ তাদেরকে কক্ষনো ক্ষমা করবেন না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4580,47,35,'কাজেই তোমরা সাহস-হারা হয়ে যেও না আর সন্ধির আবেদন করে বসো না, প্রবল তো তোমরাই। আল্লাহ তোমাদের সঙ্গে আছেন, তিনি তোমাদের ‘আমাল কক্ষনো বিনষ্ট করবেন না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4581,47,36,'দুনিয়ার এ জীবন তো খেলা আর তামাশা মাত্র। তোমরা যদি ঈমান আন, আর আল্লাহকে ভয় করে চল, তিনি তোমাদেরকে তোমাদের প্রতিফল দিবেন (আর এর বিপরীতে) তিনি তোমাদের কাছে তোমাদের মাল ধন চাইবেন না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4582,47,37,'তিনি যদি তোমাদের কাছে তা চাইতেন আর সেজন্য চাপ দিতেন তাহলে তোমরা কৃপণতা করতে আর তাতে তিনি তোমাদের বিদ্বেষভাব প্রকাশ করে দিতেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4583,47,38,'দেখ, তোমরা তো তারাই, তোমাদেরকে আল্লাহর পথে ব্যয় করার জন্য ডাক দেয়া হচ্ছে, তখন তোমাদের কিছু লোক কৃপণতা করছে। যে কৃপণতা করে, সে কৃপণতা করে কেবল নিজের আত্মার সাথে। আল্লাহ তো অভাবহীন আর তোমরাই অভাবী। তোমরা যদি মুখ ফিরিয়ে নাও তাহলে তিনি তোমাদের পরিবর্তে অন্য জাতিকে নিয়ে আসবেন, তখন তারা তোমাদের মত হবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4584,48,1,'আমি তোমাকে দিয়েছি স্পষ্ট বিজয়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4585,48,2,'যাতে আল্লাহ তোমার আগের ও পিছের যাবতীয় ভুলভ্রান্তি ক্ষমা করেন, তোমার উপর তাঁর নি‘মাত পূর্ণ করেন এবং তোমাকে সরল সঠিক পথে পরিচালিত করেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4586,48,3,'আর আল্লাহ তোমাকে প্রবল পরাক্রান্ত সাহায্য দান করেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4587,48,4,'তিনিই মু’মিনদের দিলে প্রশান্তি নাযিল করেন যাতে তারা তাদের ঈমানের সাথে আরো ঈমান বাড়িয়ে নেয়। আসমান ও যমীনের যাবতীয় বাহিনী আল্লাহর কর্তৃত্বের অধীন। আল্লাহ সর্বজ্ঞ ও প্রজ্ঞাময়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4588,48,5,'(তিনি এ কাজ করেন এজন্য) যাতে তিনি মু’মিন পুরুষ ও মু’মিনা নারীকে জান্নাতে প্রবিষ্ট করেন যার তলদেশে ঝর্ণাধারা প্রবাহিত। তাতে তারা চিরকাল থাকবে। তিনি তাদের পাপ মোচন করে দিবেন। আল্লাহর দৃষ্টিতে এটাই বিরাট সাফল্য। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4589,48,6,'আর তিনি মুনাফিক পুরুষ ও মুনাফিকা নারী, মুশরিক পুরুষ ও মুশরিকা নারীকে শাস্তি দিবেন যারা আল্লাহ সম্পর্কে খারাপ ধারণা পোষণ করে। তাদের জন্য আছে অশুভ চক্র। আল্লাহ তাদের উপর রাগান্বিত হয়েছেন আর তাদেরকে লা‘নাত করেছেন। তাদের জন্য প্রস্তুত করে রেখেছেন জাহান্নাম। তা কতই না নিকৃষ্ট আবাসস্থল! ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4590,48,7,'আসমান ও যমীনের যাবতীয় বাহিনী আল্লাহর কর্তৃত্বের অধীন। আল্লাহ মহা পরাক্রমশালী, মহা প্রজ্ঞাময়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4591,48,8,'(হে রাসূল) আমি তোমাকে (সত্যের) সাক্ষ্যদাতা. (বিশ্বাসীদের জন্য) সুসংবাদদাতা ও (অবিশ্বাসীদের জন্য) সতর্ককারী হিসেবে পাঠিয়েছি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4592,48,9,'যেন (ওহে মানুষেরা) তোমরা আল্লাহ ও তাঁর রসূলের প্রতি ঈমান আন, রসূলকে শক্তি যোগাও আর তাকে সম্মান কর, আর সকাল-সন্ধ্যায় আল্লাহর মহিমা ঘোষণা কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4593,48,10,'যারা তোমার কাছে বাই‘আত (অর্থাৎ আনুগত্য করার শপথ) করে আসলে তারা আল্লাহর কাছে বাই‘আত করে। তাদের হাতের উপর আছে আল্লাহর হাত। এক্ষণে যে এ ও‘য়াদা ভঙ্গ করে, এ ও‘য়াদা ভঙ্গের কুফল তার নিজেরই উপর পড়বে। আর যে ও‘য়াদা পূর্ণ করবে- যা সে আল্লাহর সঙ্গে করেছে- তিনি অচিরেই তাকে মহা পুরস্কার দান করবেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4594,48,11,'(যুদ্ধ থেকে) পিছ-পড়া বেদুঈনরা তোমাকে বলবে- আমাদের মালধন আর আমাদের পরিবার-পরিজন আমাদেরকে ব্যস্ত রেখেছিল, কাজেই (হে নবী) আপনি আমাদের জন্য ক্ষমা প্রার্থনা করুন। তারা মুখে এমন কথা বলে যা তাদের অন্তরে নেই। (তাদেরকে) বল, আল্লাহ তোমাদের কোন ক্ষতি বা কোন কল্যাণ করার ইচ্ছে করলে তাঁর বিপক্ষে তোমাদের জন্য কিছু করার ক্ষমতা কার আছে? (কারো কোন ওকালতির দরকার নেই) বরং তোমরা যা কর সে বিষয়ে আল্লাহই খবর রাখেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4595,48,12,'বরং তোমরা ধারণা করেছিলে যে, রসূল ও মু’মিনগণ কক্ষনো তাদের পরিবার পরিজনের কাছে ফিরে আসতে পারবে না, আর এ খেয়ালটা তোমাদের মনে খুবই চমৎকার ক’রে দেয়া হয়েছিল। তোমরা ধারণা করেছিলে বড়ই কু-ধারণা, আসলে তোমরা হলে ধ্বংসের যোগ্য একটা সম্প্রদায়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4596,48,13,'যারা আল্লাহ ও তাঁর রসূলের প্রতি বিশ্বাস স্থাপন করে না, আমি (সে সব) কাফিরদের জন্য প্রস্তুত করে রেখেছি জ্বলন্ত আগুন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4597,48,14,'আসমান যমীনের রাজত্ব আল্লাহর; যাকে চান তিনি ক্ষমা করেন, যাকে ইচ্ছে ‘আযাব দেন। আল্লাহ বড়ই ক্ষমাশীল, অতি দয়ালু। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4598,48,15,'তোমরা যখন গানীমাতের মাল সংগ্রহ করার জন্য যেতে থাকবে তখন পিছনে থেকে যাওয়া লোকগুলো বলবে- ‘আমাদেরকেও তোমাদের সঙ্গে যেতে দাও। তারা আল্লাহর ফরমানকে বদলে দিতে চায়। বল ‘তোমরা কিছুতেই আমাদের সঙ্গে যেতে পারবে না, (খাইবার অভিযানে অংশগ্রহণ এবং সেখানে পাওয়া গানীমাত কেবল তাদের জন্য যারা ইতোপূর্বে হুদাইবিয়ার সফর ও বাই‘আতে রিয্ওয়ানে অংশ নিয়েছে) এমন কথা আল্লাহ পূর্বেই বলে দিয়েছেন। তখন তারা বলবে- ‘তোমরা বরং আমাদের প্রতি হিংসা পোষণ করছ।’ (এটা যে আল্লাহর হুকুম তা তারা বুঝছে না) বরং তারা খুব কমই বুঝে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4599,48,16,'বেদুঈনদের যারা পেছনে রয়ে গিয়েছিল তাদেরকে বল- ‘তোমাদেরকে যুদ্ধ করতে ডাকা হবে খুবই শক্তিশালী এক জাতির বিরুদ্ধে, তোমরা তাদের বিরুদ্ধে যুদ্ধ করবে অথবা তারা আত্মসমর্পণ করবে। তোমরা যদি তখন তা মান্য কর, আল্লাহ তোমাদেরকে উত্তম পুরস্কার দিবেন। আর যদি পিঠ ফিরিয়ে নাও যেমন তোমরা আগে পিঠ ফিরিয়ে নিয়েছিলে, তাহলে আল্লাহ তোমাদেরকে কষ্টদায়ক শাস্তি দিবেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4600,48,17,'অন্ধের উপর কোন দোষ নেই, খোঁড়ার উপর কোন দোষ নেই, রোগীর উপর কোন দোষ নেই। আর যে কেউই আল্লাহ ও তাঁর রসূলের আনুগত্য করবে, আল্লাহ তাকে জান্নাতে প্রবিষ্ট করবেন, যার নীচ দিয়ে ঝর্ণাধারা প্রবাহিত। আর যে ব্যক্তি পিঠ ফিরিয়ে নিবে, তিনি তাকে কষ্টদায়ক শাস্তি দিবেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4601,48,18,'মু’মিনদের প্রতি আল্লাহ সন্তুষ্ট হলেন যখন তারা (হুদাইবিয়ায়) গাছের তলে তোমার কাছে বায়‘আত নিল। আল্লাহ জানতেন তাদের অন্তরে কী আছে, এজন্য তিনি তাদের উপর প্রশান্তি অবতীর্ণ করলেন আর পুরস্কার হিসেবে তাদেরকে দিলেন নিকট আসন্ন বিজয়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4602,48,19,'আর বিপুল পরিমাণ গানীমাত যা তারা লাভ করবে। আল্লাহ মহা পরাক্রান্ত প্রজ্ঞাময়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4603,48,20,'আল্লাহ তোমাদেরকে বিপুল পরিমাণ গানীমাতের ও‘য়াদা দিয়েছেন যা তোমরা লাভ করবে। এটা তিনি তোমাদেরকে আগেই দিলেন আর মানুষদের (অর্থাৎ মাক্কার কুরায়শদের) হাতকে তোমাদের (উপর পতিত হওয়া) থেকে সংযত করে রাখলেন যাতে তা মু’মিনদের জন্য একটা নিদর্শন হয় (যে প্রকৃত মু’মিনদেরকে আল্লাহ অতি সংকটময় মুহূর্তেও রক্ষা করতে পারেন), আর তিনি তোমাদেরকে সরল সঠিক পথে পরিচালিত করেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4604,48,21,'এবং আরো অন্য (সাহায্য, সম্পদ ও বিজয়) যা এখনও তোমাদের অধিকারে আসেনি, আল্লাহ তা স্বীয় আয়ত্তে রেখেছেন, আল্লাহ সকল বিষয়ে ক্ষমতাবান। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4605,48,22,'কাফিরগণ যদি তোমাদের সাথে যুদ্ধ বাঁধাত, তাহলে তারা অবশ্যই পিঠ ফিরিয়ে নিত, সে অবস্থায় তারা কোন পৃষ্ঠপোষক ও সাহায্যকারী পেত না।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4606,48,23,'(এটাই) আল্লাহর বিধান, অতীতেও তাই হয়েছে, তুমি আল্লাহর বিধানে কক্ষনো কোন পরিবর্তন পাবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4607,48,24,'মক্কা উপত্যকায় তিনিই তাদের হাত তোমাদের থেকে আর তোমাদের হাত তাদের থেকে বিরত রেখেছিলেন তোমাদেরকে তাদের উপর বিজয়ী করার পর। তোমরা যা কিছু কর আল্লাহ তা দেখেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4608,48,25,'কুফুরী তো তারাই করেছিল আর তোমাদেরকে মাসজিদুল হারাম থেকে বাধা দিয়েছিল। বাধা দিয়েছিল কুরবানীর পশুগুলোকে কুরবানীর স্থানে পৌঁছতে। মু’মিন পুরুষ আর মু’মিন নারীরা যদি (মাক্কায় কাফিরদের মাঝে) না থাকত যাদের সম্পর্কে তোমরা জান না আর অজ্ঞতাবশতই তোমরা তাদেরকে পর্যুদস্ত করে দিবে যার ফলে তোমাদের উপর কলঙ্ক লেপন হবে-এমন সম্ভাবনা না থাকত, তাহলে তোমাদেরকে যুদ্ধের আদেশ দেয়া হত। যুদ্ধের আদেশ দেয়া হয়নি যাতে আল্লাহ যাকে ইচ্ছে তাঁর রহমাতের মধ্যে শামিল করে নিতে পারেন। (মাক্কায় অনেক মু’মিন আর কাফিররা একত্রিত না থেকে) যদি তারা পৃথক হয়ে থাকত, তাহলে আমি তাদের মধ্যে কাফিরদেরকে ভয়াবহ শাস্তি দিতাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4609,48,26,'কাফিররা যখন তাদের অন্তরে জিদ ও হঠকারিতা জাগিয়ে তুলল- অজ্ঞতার যুগের জিদ ও হঠকারিতা- তখন আল্লাহ তাঁর রসূল ও মু’মিনদের উপর স্বীয় প্রশান্তি অবতীর্ণ করলেন আর তাদের জন্য তাকওয়া অবলম্বনের নির্দেশ-বাণী অপরিহার্য (রূপে পালনীয়) ক’রে দিলেন; আর তারাই ছিল এর সবচেয়ে বেশি হকদার ও যোগ্য অধিকারী। আল্লাহ সকল বিষয়ে সর্বাধিক জ্ঞানের অধিকারী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4610,48,27,'আল্লাহ তাঁর রসূলকে প্রকৃত সত্য স্বপ্নই দেখিয়েছিলেন। আল্লাহর ইচ্ছায় তোমরা অবশ্য অবশ্যই মাসজিদে হারামে প্রবেশ করবে নিরাপদে, মস্তক মুন্ডিত অবস্থায় ও চুল কেটে, ভয়ভীতিহীন হয়ে। আল্লাহ জানেন, যা তোমরা জান না। (সেই স্বপ্ন তো পূর্ণ হবেই) তদুপরি তিনি দিলেন (হুদাইবিয়ার চুক্তি সম্পাদনের মাধ্যমে) নিকটাসন্ন বিজয়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4611,48,28,'তিনিই তাঁর রসূলকে পাঠিয়েছেন হিদায়াত ও সত্য দ্বীন সহকারে সকল দ্বীনের উপর সেটিকে বিজয়ী করার জন্য। (এ ব্যাপারে) সাক্ষী হিসেবে আল্লাহই যথেষ্ট। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4612,48,29,'মুহাম্মাদ আল্লাহর রসুল। আর যে সব লোক তাঁর সঙ্গে আছে তারা কাফিরদের প্রতি অত্যন্ত কঠোর, নিজেদের পরস্পরের প্রতি দয়াশীল। তাদেরকে তুমি দেখবে রুকূ‘ ও সাজদায় অবনত অবস্থায়, তারা আল্লাহর অনুগ্রহ ও সন্তুষ্টি অনুসন্ধানে নিয়োজিত। তাদের চিহ্ন হল, তাদের মুখমন্ডলে সেজদা্র প্রভাব পরিস্ফুট হয়ে আছে। তাদের এমন দৃষ্টান্তের কথা তাওরাতে আছে, তাদের দৃষ্টান্ত ইঞ্জিলেও আছে। (তারা) যেন একটা চারাগাছ তার কচিপাতা বের করে, তারপর তা শক্ত হয়, অতঃপর তা কান্ডের উপর মজবুত হয়ে দাঁড়িয়ে যায়- যা চাষীকে আনন্দ দেয়। (এভাবে আল্লাহ মু’মিনদেরকে দুর্বল অবস্থা থেকে দৃঢ় ভিত্তির উপর দাঁড় ক’রে দেন) যাতে কাফিরদের অন্তর গোস্বায় জ্বলে যায়। তাদের মধ্য থেকে যারা ঈমান আনে আর সৎকর্ম করে, আল্লাহ তাদের জন্য ক্ষমা ও মহা পুরস্কারের প্রতিশ্রুতি দিয়েছেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4613,49,1,'ওহে মু’মিনগণ! তোমরা (কোন বিষয়েই) আল্লাহ ও তাঁর রসূলের আগে বেড়ে যেয়ো না, আল্লাহকে ভয় কর, আল্লাহ সর্বশ্রোতা, সর্বজ্ঞ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4614,49,2,'হে মু’মিনগণ! তোমরা নবীর আওয়াজের উপর তোমাদের আওয়াজ উচ্চ করো না। তোমরা নিজেরা পরস্পরে যেমন উচ্চ আওয়াজে কথা বল, তাঁর সঙ্গে সে রকম উচ্চ আওয়াজে কথা বলো না। তা করলে তোমাদের (যাবতীয়) কাজকর্ম নিস্ফল হয়ে যাবে, আর তোমরা একটু টেরও পাবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4615,49,3,'যারা আল্লাহর রসূলের সামনে তাদের আওয়াজ নীচু করে, আল্লাহ তাদের অন্তরগুলোকে তাকওয়ার জন্য যাচাই-বাছাই করে নিয়েছেন। তাদের জন্য আছে ক্ষমা আর বিরাট পুরস্কার। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4616,49,4,'যারা তোমাকে হুজরার বাইরে থেকে (উচ্চঃস্বরে) ডাকে, তাদের অধিকাংশেরই কোন জ্ঞান নেই। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4617,49,5,'তারা যদি ধৈর্য ধরত যে পর্যন্ত না তুমি তাদের কাছে বেরিয়ে আস, সেটাই তাদের জন্য উত্তম হত। আল্লাহ বড়ই ক্ষমাশীল, অতি দয়ালু। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4618,49,6,'হে মু’মিনগণ! কোন পাপাচারী যদি তোমাদের কাছে কোন খবর নিয়ে আসে, তাহলে তার সত্যতা যাচাই করে লও, তা না হলে তোমরা অজ্ঞতাবশতঃ কোন সম্প্রদায়ের ক্ষতি করে বসবে, অতঃপর তোমরা যা করেছ সেজন্য তোমাদেরকে অনুতপ্ত হতে হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4619,49,7,'তোমরা জেনে রেখ যে, তোমাদের মধ্যে আল্লাহর রসূল বর্তমান আছে। সে যদি অধিকাংশ বিষয়ে তোমাদের কথা মেনে নিত, তাহলে তোমরা অবশ্যই ক্ষতিগ্রস্ত হতে। কিন্তু আল্লাহ তোমাদের কাছে ঈমানকে প্রিয় করে দিয়েছেন। তোমাদের অন্তরে সেটিকে সুশোভিত করেছেন আর তোমাদের কাছে কুফুরী, পাপাচার ও অবাধ্যতাকে ঘৃণিত করেছেন। তারাই সত্য পথপ্রাপ্ত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4620,49,8,'(যা) আল্লাহর করুণা ও অনুগ্রহ। আল্লাহ সর্বজ্ঞ, প্রজ্ঞাময়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4621,49,9,'মু’মিনদের দু’দল লড়াইয়ে জড়িয়ে পড়লে তাদের মধ্যে মীমাংসা করে দাও। অতঃপর একটি দল অপরটির উপর বাড়াবাড়ি করলে যে দলটি বাড়াবাড়ি করে, তার বিরুদ্ধে তোমরা লড়াই কর যতক্ষণ না সে দলটি আল্লাহর নির্দেশের দিকে ফিরে আসে। অতঃপর যদি দলটি ফিরে আসে, তাহলে তাদের মধ্যে ইনসাফের সঙ্গে ফয়সালা কর আর সুবিচার কর; আল্লাহ সুবিচারকারীদেরকে ভালবাসেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4622,49,10,'মু’মিনরা পরস্পর ভাই ভাই, কাজেই তোমাদের ভাইদের মধ্যে শান্তি-সমঝোতা স্থাপন কর, আর আল্লাহকে ভয় কর, যাতে তোমরা দয়া প্রাপ্ত হও। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4623,49,11,'হে মু’মিনগণ! কোন সম্প্রদায় যেন অন্য সম্প্রদায়কে ঠাট্টা-বিদ্রূপ না করে, হতে পারে তারা বিদ্রূপকারীদের চেয়ে উত্তম। আর নারীরা যেন অন্য নারীদেরক ঠাট্টা-বিদ্রূপ না করে, হতে পারে তারা বিদ্রূপকারিণীদের চেয়ে উত্তম। তোমরা একে অন্যের নিন্দা করো না, একে অপরকে মন্দ নামে ডেকো না। ঈমান গ্রহণের পর (ঈমানের আগে কৃত অপরাধকে যা মনে করিয়ে দেয় সেই) মন্দ নাম কতই না মন্দ! (এ সব হতে) যারা তাওবাহ না করে তারাই যালিম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4624,49,12,'হে মু’মিনগণ! তোমরা অধিক ধারণা হতে বিরত থাক। কতক ধারণা পাপের অন্তর্ভুক্ত। তোমরা অন্যের দোষ খোঁজাখুঁজি করো না, একে অন্যের অনুপস্থিতিতে দোষ-ত্রুটি বর্ণনা করো না। তোমাদের কেউ কি তার মৃত ভাইয়ের গোশত খেতে পছন্দ করবে? তোমরা তো সেটাকে ঘৃণাই করে থাক। আল্লাহকে ভয় কর, আল্লাহ খুব বেশি তাওবাহ ক্ববূলকারী, অতি দয়ালু। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4625,49,13,'হে মানুষ! তোমাদেরকে এক পুরুষ ও এক নারী থেকে সৃষ্টি করেছি এবং তোমাদেরকে বিভিন্ন জাতি ও গোষ্ঠীতে বিভক্ত করেছি যাতে তোমরা পরস্পরকে চিনতে পার। তোমাদের মধ্যে আল্লাহর নিকট সেই লোকই অধিক সম্মানীয় যে লোক অধিক মুত্তাক্বী। আল্লাহ সর্বজ্ঞ, সব খবর রাখেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4626,49,14,'বেদুঈনরা বলে, ‘আমরা ঈমান এনেছি’। বল- ‘তোমরা ঈমান আননি, বরং তোমরা বল, ‘আমরা (মৌখিক) আনুগত্য স্বীকার করেছি’, এখন পর্যন্ত তোমাদের অন্তরে ঈমান প্রবেশ করেনি। তোমরা যদি আল্লাহ ও তাঁর রসূলকে মান্য কর তাহলে তোমাদের কৃতকর্মের কিছুই কমতি করা হবে না। আল্লাহ অতি ক্ষমাশীল, পরম দয়ালু। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4627,49,15,'মু’মিন তারাই যারা আল্লাহ ও তাঁর রসূলের উপর ঈমান আনে, অতঃপর কোনরূপ সন্দেহ করে না, আর তাদের মাল দিয়ে ও জান দিয়ে আল্লাহর পথে জিহাদ করে; তারাই সত্যবাদী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4628,49,16,'বল, ‘তোমরা কি তোমাদের দ্বীন সম্পর্কে আল্লাহকে অবগত করতে চাও’? আল্লাহ তো জানেন যা আছে আসমানে আর যা আছে যমীনে। আল্লাহ প্রতিটি বিষয় সম্পর্কে সবিশেষ অবগত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4629,49,17,'এই লোকেরা মনে করে যে, তারা ইসলাম কবূল ক’রে তোমার প্রতি অনুগ্রহ করেছে। তাদেরকে বলে দাও, ‘তোমরা তোমাদের ইসলাম কবূলের দ্বারা আমার প্রতি কোন অনুগ্রহ করনি বরং আল্লাহ্ই তোমাদেরকে ঈমানের পথে পরিচালিত ক’রে তোমাদের প্রতি অনুগ্রহ করেছেন, তোমরা যদি (তোমাদের ঈমান আনার দাবীতে) সত্যবাদী হয়েই থাক। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4630,49,18,'আসমান ও যমীনের গোপন বিষয়ের খবর আল্লাহই জানেন। তোমরা যা কর আল্লাহ তা দেখেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4631,50,1,'ক্বাফ, শপথ মাহাত্ম্যপূর্ণ কুরআনের (যে তুমি আল্লাহর রসূল।) ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4632,50,2,'বরং এ লোকেরা বিস্মিত হচ্ছে যে, তাদের কাছে তাদেরই মধ্য হতে একজন সর্তককারী এসেছে। যার কারণে কাফিররা বলে- ‘এতো বড়ই আশ্চর্যজনক ব্যাপার! ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4633,50,3,'আমরা যখন মরে যাব আর মাটি হয়ে যাব (তখন আমাদেরকে আবার আল্লাহর কাছে ফিরে যেতে হবে)? এ ফিরে যাওয়াটা তো বহু দূরের ব্যাপার। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4634,50,4,'আমি জানি মাটি তাদের কতটুকু ক্ষয় করে আর আমার কাছে আছে এক কিতাব যা (সব কিছুর পূর্ণ বিবরণ) সংরক্ষণ করে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4635,50,5,'তাদের কাছে সত্য আসার পর তারা তা অস্বীকার করেছে, কাজেই এখন তারা সংশয়ের মধ্যে পড়ে আছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4636,50,6,'তারা কি তাদের উপরে অবস্থিত আকাশের দিকে তাকায় না, কীভাবে আমি তাকে বানিয়েছি, তাকে সুশোভিত করেছি আর তাতে নেই কোন ফাটল? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4637,50,7,'আর পৃথিবী- তাকে করেছি বিস্তৃত আর তাতে সংস্থাপিত করেছি পর্বতরাজি আর তাতে উদ্গত করেছি যাবতীয় সুদৃশ্য উদ্ভিদরাজি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4638,50,8,'প্রতিটি (আল্লাহ) অভিমুখী বান্দাহর জন্য চক্ষু উন্মোচনকারী ও উপদেশ হিসেবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4639,50,9,'আমি আকাশ থেকে বর্ষণ করি কল্যাণকর বৃষ্টি আর তা দিয়ে সৃষ্টি করি বাগান আর মাড়াইযোগ্য শস্যদানা, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4640,50,10,'আর উঁচু খেজুর গাছ যাতে আছে খেজুর গুচ্ছ স্তরে স্তরে সাজানো। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4641,50,11,'বান্দাহদের রিযক হিসেবে। আর আমি পানি দিয়ে জীবন্ত করে তুলি মৃত যমীনকে। এভাবেই বের করা হবে (ক্ববর থেকে মানুষদের)। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4642,50,12,'এদের আগে সত্যকে মেনে নিতে অস্বীকার করেছিল নূহের জাতি, রাস্‌স ও সামুদ জাতি, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4643,50,13,'‘আদ, ফেরাউন ও লূত জাতি, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4644,50,14,'আইকাবাসী ও তুব্বার জাতি। তারা সকলেই রসূলদেরকে মিথ্যা বলে অস্বীকার করেছিল, ফলে তাদের উপর আমার শাস্তি অবধারিত হয়েছিল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4645,50,15,'আমরা কি প্রথমবার সৃষ্টি করে ক্লান্ত হয়ে পড়েছি? বস্তুতঃ তারা নতুন সৃষ্টি বিষয়ে সন্দেহে পড়ে আছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4646,50,16,'আমিই মানুষকে সৃষ্টি করেছি, আর তার প্রবৃত্তি তাকে (নিত্য নতুন) কী কুমন্ত্রণা দেয় তাও আমি জানি। আমি তার গলার শিরা থেকেও নিকটবর্তী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4647,50,17,'(তদুপরি) দু’জন লেখক ডানে ও বামে বসে (মানুষের ‘আমাল) লিখছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4648,50,18,'যে কথাই মানুষ উচ্চারণ করে (তা সংরক্ষণের জন্য) তার নিকটে একজন সদা তৎপর প্রহরী আছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4649,50,19,'মৃত্যুর যন্ত্রণা প্রকৃতই আসবে যাত্থেকে অব্যাহতি পাওয়ার জন্য তুমি চেষ্টা করতে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4650,50,20,'অতঃপর সিঙ্গায় ফুঁক দেয়া হবে। সেটাই হল শাস্তির দিন (যে সম্পর্কে মানুষকে সতর্ক করা হয়েছিল)। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4651,50,21,'(সেদিন) প্রত্যেক ব্যক্তি আসবে এমন অবস্থায় যে একজন (ফেরেশতা) তাকে হাঁকিয়ে নিয়ে আসবে আর একজন (ফেরেশতা) থাকবে সাক্ষ্যদাতা হিসেবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4652,50,22,'(বলা হবে) ‘এ দিন সম্পর্কে তুমি ছিলে উদাসীন। তোমার সামনে যে পর্দা ছিল তা আমি সরিয়ে দিয়েছি। (সে কারণে) তোমার দৃষ্টি আজ খুব তীক্ষ্ম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4653,50,23,'তার সঙ্গী (ফেরেশতা) বলবে ‘এই যে আমার কাছে (‘আমালনামা) প্রস্তুত।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4654,50,24,'(নির্দেশ দেয়া হবে) তোমরা উভয়ে প্রত্যেক অবাধ্য কাফিরকে নিক্ষেপ কর জাহান্নামে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4655,50,25,'(যারা ছিল) কল্যাণের প্রতিবন্ধক, সীমালঙ্ঘনকারী ও সন্দিগ্ধ চিত্ত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4656,50,26,'যে আল্লাহর সঙ্গে অন্যকে ইলাহ্ বানিয়ে নিয়েছিল। কাজেই তোমরা উভয়ে তাকে কঠিন ‘আযাবে নিক্ষেপ কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4657,50,27,'তার সঙ্গী বলবে, ‘হে আমাদের প্রতিপালক! আমি তাকে বিদ্রোহী বানাইনি বরং সে নিজেই ছিল সুদূর গুমরাহীর মধ্যে।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4658,50,28,'আল্লাহ বলবেন, ‘আমার সামনে বাদানুবাদ করো না, আমি আগেই তোমাদেরকে সতর্ক করে দিয়েছিলাম। \r\n');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4659,50,29,'আমার কথা কক্ষনো বদলে না, আর আমি আমার বান্দাহদের প্রতি যুলমকারীও নই। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4660,50,30,'সে দিন আমি জাহান্নামকে জিজ্ঞেস করব, ‘তুমি কি পরিপূর্ণ হয়েছ’? সে বলবে, ‘আরো বেশি আছে কি?’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4661,50,31,'মুত্তাক্বীদের জন্য জান্নাতকে নিকটে আনা হবে- তা মোটেই দূরে থাকবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4662,50,32,'(বলা হবে) ‘এ হল তাই যার ও‘য়াদা তোমাদেরকে দেয়া হয়েছিল- প্রত্যেক আল্লাহ অভিমুখী ও (গুনাহ থেকে) খুব বেশি হিফাযাতকারীর জন্য। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4663,50,33,'যে না দেখেই দয়াময় (আল্লাহকে) ভয় করত, আর আল্লাহর নির্দেশ পালনের জন্য বিনয়ে অবনত অন্তর নিয়ে উপস্থিত হত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4664,50,34,'(তাদেরকে বলা হবে) ‘শান্তির সঙ্গে এতে প্রবেশ কর, এটা চিরস্থায়ী জীবনের দিন।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4665,50,35,'সেখানে তাদের জন্য তা-ই আছে যা তারা ইচ্ছে করবে, আর আমার কাছে (তাছাড়াও) আরো বেশি আছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4666,50,36,'তাদের পূর্বে আমি কত জাতিকে ধ্বংস করে দিয়েছি যারা শক্তিতে ছিল তাদের চেয়ে প্রবল, যার ফলে তারা দুনিয়া চষে বেড়াত; তারা পালানোর কোন জায়গা পেয়েছিল কি? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4667,50,37,'এতে অবশ্যই উপদেশ রয়েছে তার জন্য যার আছে (বোধশক্তিসম্পন্ন) অন্তর কিংবা যে খুব মন দিয়ে কথা শুনে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4668,50,38,'আকাশ, যমীন আর এ দু’য়ের মাঝে যা আছে তা আমি ছ’ দিনে সৃষ্টি করেছি; ক্লান্তি আমাকে স্পর্শ করেনি। (আমি সকল মানুষকে বিচারের জন্য হাজির করবই)। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4669,50,39,'কাজেই তারা (মিথ্যা, উপহাসপূর্ণ ও অপমানজনক কথা) যা বলে তাতে তুমি ধৈর্য ধারণ কর আর সূর্যোদয়ের পূর্বে আর সূর্যাস্তের পূর্বে তোমার প্রতিপালকের মহিমা ও প্রশংসা ঘোষণা কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4670,50,40,'আর তাঁর প্রশংসা ঘোষণা কর রাত্রির একাংশে আর নামাযের পরে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4671,50,41,'আর শোন, যেদিন এক ঘোষণাকারী (প্রত্যেক ব্যক্তির) নিকটবর্তী স্থান থেকে ডাক দিবে, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4672,50,42,'যেদিন সমস্ত মানুষ প্রকৃতই শুনতে পাবে এক (ভয়ংকর) ধ্বনি। সেদিনটি হবে (ভূগর্ভ থেকে সকল আত্মার) বের হওয়ার দিন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4673,50,43,'আমিই জীবন দেই, আমিই মুত্যু দেই, আর আমার কাছেই (সব্বাইকে) ফিরে আসতে হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4674,50,44,'যেদিন পৃথিবী দীর্ণ বিদীর্ণ হবে, আর মানুষ ছুটে যাবে (হাশরের পানে)। এই একত্রীকরণ আমার জন্য খুবই সহজ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4675,50,45,'তারা (তোমার বিরুদ্ধে) যা বলে তা আমি ভাল করেই জানি, তুমি তাদের উপর জবরদস্তিকারী নও। কাজেই যে আমার শাস্তির ভয়প্রদর্শনকে ভয় করে, তাকে তুমি কুরআনের সাহায্যে উপদেশ দাও।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4676,51,1,'শপথ সেই বাতাসের যা ধূলাবালি উড়ায়, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4677,51,2,'আর যা উঠিয়ে নেয় আর বহন করে ভারী বোঝা, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4678,51,3,'আর যা ধীর ও শান্ত গতিতে বয়ে চলে ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4679,51,4,'আর যারা কর্ম বণ্টন করে, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4680,51,5,'তোমাদেরকে যার ও‘য়াদা দেয়া হয়েছে তা অবশ্যই সত্য। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4681,51,6,'কর্মফল দিবস অবশ্যই আসবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4682,51,7,'বহু পথ বিশিষ্ট আকাশের শপথ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4683,51,8,'(পরকাল সম্পর্কে) তোমরা অবশ্যই রয়েছ মতভেদের মধ্যে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4684,51,9,'যারা সেই (সত্য) মানতে ভুল করে তারাই গুমরাহ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4685,51,10,'অনুমানকারীরা ধ্বংস হোক, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4686,51,11,'যারা অজ্ঞতা ও উদাসীনতার মধ্যে রয়েছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4687,51,12,'তারা জিজ্ঞেস করে- ‘প্রতিফল দিবস কবে হবে?’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4688,51,13,'(তা হবে সেদিন) যেদিন তাদেরকে আগুনে শাস্তি দেয়া হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4689,51,14,'(তাদেরকে বলা হবে) তোমরা তোমাদের (কৃতকর্মের) শাস্তি ভোগ কর, এটা হচ্ছে তাই যার জন্য তোমরা তাড়াহুড়া করছিলে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4690,51,15,'মুত্তাকীরা থাকবে জান্নাত আর ঝর্ণাধারার মাঝে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4691,51,16,'তাদের প্রতিপালক যা তাদেরকে দিবেন তা তারা ভোগ করবে, কারণ তারা পূর্বে (দুনিয়ার জীবনে) ছিল সৎকর্মশীল, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4692,51,17,'তারা  রাত্রিকালে খুব কমই শয়ন করত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4693,51,18,'আর তারা রাত্রির শেষ প্রহরে ক্ষমা প্রার্থনা করত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4694,51,19,'এবং তাদের ধন-মালে আছে যাঞ্ঝাকারী ও বঞ্চিতদের অধিকার (যা তারা আদায় করত)। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4695,51,20,'নিশ্চিত বিশ্বাসীদের জন্য পৃথিবীতে আছে নিদর্শন, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4696,51,21,'আর (নিদর্শন আছে) তোমাদের মাঝেও, তোমরা কি দেখ না? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4697,51,22,'এবং আকাশে আছে তোমাদের রিযক আর আছে যার ও‘য়াদা তোমাদেরকে দেয়া হয়েছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4698,51,23,'আকাশ ও যমীনের প্রতিপালকের শপথ! এ সব অবশ্যই সত্য, এমনই দৃঢ় সত্য যেমন তোমরা (যে কথাবার্তা) বলে থাক (সেই কথাবার্তা বলার ব্যাপারটা যেমন নিঃসন্দেহে সত্য)। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4699,51,24,'তোমার কাছে ইবরাহীমের সম্মানিত মেহমানদের খবর পৌঁছেছে কি? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4700,51,25,'যখন তারা তার সামনে উপস্থিত হল তখন বলল, ‘সালাম’। সে উত্তর দিল- ‘সালাম’। (ইবরাহীম মনে মনে ভাবল এদেরকে তো দেখছি) অপরিচিত লোক। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4701,51,26,'তখন সে তাড়াতাড়ি তার ঘরের লোকেদের নিকট চলে গেল এবং একটি মোটাতাজা (ভাজা) বাছুর নিয়ে আসল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4702,51,27,'অতঃপর সেটিকে তাদের সামনে রেখে দিল এবং বলল- ‘তোমরা খাচ্ছ না কেন?’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4703,51,28,'(যখন তারা খেল না) তখন সে তাদের ব্যাপারে মনে ভয় পেয়ে গেল। তারা বলল- ‘তুমি ভয় পেও না’, অতঃপর তারা তাকে এক জ্ঞানবান পুত্রের সুসংবাদ দিল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4704,51,29,'তখন তার স্ত্রী চিৎকার করতে করতে এগিয়ে আসল। সে নিজের কপালে আঘাত করে বলল ‘(আমি) এক বৃদ্ধা, বন্ধ্যা’ (আমার কীভাবে সন্তান হবে?) ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4705,51,30,'তারা বলল- ‘‘তোমার প্রতিপালক এ রকমই বলেছেন, তিনি মহা প্রজ্ঞাময়, সর্বজ্ঞ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4706,51,31,'ইবরাহীম বলল- ‘ওহে আল্লাহর দূতগণ (ফেরেশতারা)! তোমাদের কাজ কী (এখন)?’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4707,51,32,'তারা বলল- ‘আমাদেরকে এক অপরাধী জাতির কাছে পাঠানো হয়েছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4708,51,33,'যেন তাদের উপর মাটির পাথর বর্ষণ করি ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4709,51,34,'যা তোমার প্রতিপালকের নিকট চিহ্নিত হয়ে আছে সীমালঙ্ঘনকারীদের জন্য। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4710,51,35,'সেখানে যারা মু’মিন ছিল আমি তাদেরকে বের করে এনেছিলাম, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4711,51,36,'আমি সেখানে মুসলিমদের একটি পরিবার ছাড়া আর পাইনি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4712,51,37,'আর তাদের জন্য সেখানে একটা নিদর্শন রেখে দিলাম যারা অত্যন্ত পীড়াদায়ক ‘আযাবের ভয় করে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4713,51,38,'আর মূসার ঘটনাতেও (নিদর্শন আছে) যখন আমি তাকে স্পষ্ট প্রমাণসহ ফেরাউনের কাছে পাঠিয়েছিলাম, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4714,51,39,'তখন সে তার ক্ষমতার দাপটে মুখ ফিরিয়ে নিল আর বলল- ‘এ লোক একটা যাদুকর না হয় পাগল।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4715,51,40,'শেষে আমি তাকে আর তার সৈন্য সামন্তকে পাকড়াও করলাম আর তাদেরকে সমুদ্রে নিক্ষেপ করলাম। সে ছিল ধিকৃত নিন্দিত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4716,51,41,'আর ‘আদ-এর ঘটনাতেও (নিদর্শন আছে)। আমি তাদের কাছে পাঠিয়েছিলাম অকল্যাণের বাতাস। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4717,51,42,'তা যা কিছুর উপর দিয়ে বয়ে গিয়েছিল তাকে চূর্ণ-বিচূর্ণ ও ধ্বংসস্তুপে পরিণত না করে ছাড়েনি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4718,51,43,'আর সামূদের ঘটনাতেও (নিদর্শন আছে) যখন তাদেরকে বলা হয়েছিল, ‘একটা নির্দিষ্ট সময় পর্যন্ত ভোগ করে লও।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4719,51,44,'কিন্তু তারা ধৃষ্টতার সঙ্গে তাদের প্রতিপালকের আদেশ অমান্য করল। ফলে বজ্রাঘাত তাদেরকে পাকড়াও করল যা তারা চেয়ে চেয়ে দেখছিল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4720,51,45,'তারা (পায়ের ভরে) উঠে দাঁড়াতেও পারল না আর আত্মরক্ষা করতেও পারল না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4721,51,46,'ইতোপূর্বে আমি নূহের জাতিকে ধ্বংস করেছিলাম, তারা ছিল এক পাপাচারী জাতি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4722,51,47,'আমি নিজ হাত দ্বারা আসমান সৃষ্টি করেছি আর আমি অবশ্যই মহা প্রশস্তকারী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4723,51,48,'আর যমীন- তাকে আমিই বিছিয়েছি, আমি কতই না সুন্দর (সমতল) প্রসারণকারী! ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4724,51,49,'আমি প্রত্যেকটি বস্তু সৃষ্টি করেছি জোড়ায় জোড়ায়, যাতে তোমরা শিক্ষা গ্রহণ কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4725,51,50,'অতএব দৌড়াও আল্লাহর দিকে, আমি তোমাদের জন্য তাঁর পক্ষ হতে স্পষ্ট সতর্ককারী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4726,51,51,'তোমরা আল্লাহর সঙ্গে কোন ইলাহ স্থির করো না, আমি তাঁর পক্ষ থেকে তোমাদের জন্য স্পষ্ট সতর্ককারী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4727,51,52,'এভাবে যখনই তাদের আগের লোকেদের মধ্যে কোন রসূল এসেছে, তখনই তারা বলেছে- ‘সে যাদুকর না হয় উন্মাদ।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4728,51,53,'তারা কি বংশ পরম্পরায় এরই অসিয়ত (অর্থাৎ অন্তিম সবক) দিয়ে আসছে, বরং তারা সীমালঙ্ঘনকারী জাতি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4729,51,54,'কাজেই তুমি তাদের দিক থেকে মুখ ফিরিয়ে লও, তার জন্য তুমি তিরস্কৃত হবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4730,51,55,'আর তুমি উপদেশ দিতে থাক, কেননা উপদেশ মু’মিনদের উপকার করবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4731,51,56,'আমি জ্বিন ও মানবকে সৃষ্টি করেছি একমাত্র এ কারণে যে, তারা আমারই ‘ইবাদাত করবে।। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4732,51,57,'আমি তাদের থেকে রিযক চাই না, আর আমি এও চাই না যে, তারা আমাকে খাওয়াবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4733,51,58,'আল্লাহই তো রিযকদাতা, মহা শক্তিধর, প্রবল পরাক্রান্ত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4734,51,59,'কাজেই যারা যুলম করেছে তাদের প্রাপ্য তাই যে প্রাপ্য পূর্বে ছিল তাদের মত লোকেদের; কাজেই (নিজেদের প্রাপ্য পাওয়ার জন্য) তারা যেন তাড়াহুড়া না করে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4735,51,60,'কাফিরদের জন্য ধ্বংস (নেবে আসবে) তাদের সেদিনের যেদিনের ভয় তাদেরকে দেখানো হয়েছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4736,52,1,'শপথ তূর (পর্বত) এর, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4737,52,2,'শপথ কিতাবের যা লিখিত ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4738,52,3,'খোলা পৃষ্ঠায়, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4739,52,4,'শপথ বেশি বেশি আবাদকৃত ঘরের, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4740,52,5,'শপথ সুউচ্চ ছাদের, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4741,52,6,'শপথ তরঙ্গায়িত সমুদ্রের, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4742,52,7,'তোমার প্রতিপালকের ‘আযাব অবশ্যই সংঘটিত হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4743,52,8,'তার প্রতিরোধকারী কেউ নেই। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4744,52,9,'যেদিন আকাশ প্রচন্ডভাবে কাঁপবে, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4745,52,10,'আর পর্বত হবে দ্রুত চলমান, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4746,52,11,'ধ্বংস সেদিন সত্য প্রত্যাখ্যানকারীদের জন্য। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4747,52,12,'যারা নিরর্থক কথার খেলায় মগ্ন আছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4748,52,13,'যেদিন তাদেরকে জাহান্নামের আগুনের দিকে তাড়িয়ে নেয়া হবে ধাক্কাতে ধাক্কাতে, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4749,52,14,'(বলা হবে) এটা হল জাহান্নামের সেই আগুন তোমরা যাকে মিথ্যে জানতে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4750,52,15,'এটা কি যাদু, নাকি তোমরা দেখতে পাচ্ছ না?');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4751,52,16,'এখন এর ভিতর জ্বলতে থাক, অতঃপর ধৈর্য ধর কিংবা ধৈর্য না ধর, তোমাদের জন্য দুই-ই সমান। তোমাদেরকে সেই প্রতিফলই দেয়া হবে যা তোমরা ‘আমাল করতে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4752,52,17,'মুত্তাক্বীরা থাকবে জান্নাতে আর নি‘মাত সম্ভারের মাঝে, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4753,52,18,'তারা ভোগ করবে তাদের প্রতিপালক যা তাদেরকে দিবেন, আর তাদের প্রতিপালক তাদেরকে জাহান্নামের ‘আযাব থেকে রক্ষা করবেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4754,52,19,'(তাদেরকে বলা হবে) খাও আর পান কর খুব মজা করে- তোমরা যে ‘আমাল করতে তার প্রতিফল হিসেবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4755,52,20,'তারা সারিবদ্ধভাবে সাজানো আসনে হেলান দিয়ে বসবে, আর আমি তাদের বিয়ে দিয়ে দেব সুন্দর বড় বড় উজ্জ্বল চক্ষু বিশিষ্টা কুমারীদের সঙ্গে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4756,52,21,'যারা ঈমান আনে আর তাদের সন্তান সন্ততিরা ঈমানের সাথে পিতামাতাকে অনুসরণ করে, আমি তাদের সাথে তাদের সন্তান সন্ততিকে মিলিত করব। তাদের ‘আমালের কোন কিছু থেকেই আমি তাদেরকে বঞ্চিত করব না। প্রত্যেক ব্যক্তিই নিজ কৃতকর্মের জন্য দায়বদ্ধ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4757,52,22,'আমি তাদেরকে দিব ফলমূল আর গোশত যা তারা পছন্দ করবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4758,52,23,'তারা সেখানে পরস্পরের মধ্যে বিনিময় করবে পানপাত্র, থাকবে না সেখানে কোন বেহুদা বকবকানি, থাকবে না কোন পাপের কাজ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4759,52,24,'তাদের চতুর্দিকে ঘুর ঘুর ক’রে তাদের সেবায় নিয়োজিত থাকবে কিশোরেরা (যারা এতই সুন্দর) যেন সযত্নে লুকিয়ে রাখা মণিমুক্তা। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4760,52,25,'তারা পরস্পরের নিকট এগিয়ে গিয়ে একে অপরকে জিজ্ঞেস করবে, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4761,52,26,'তারা বলবে, ‘পূর্বে আমরা আমাদের পরিবারে (দুনিয়াবী নানা কারণে ও আখিরাতের ‘আযাবের আশংকায়) ভয় ভীতির মধ্যে ছিলাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4762,52,27,'অবশেষে আল্লাহ আমাদের প্রতি অনুগ্রহ করেছেন আর আমাদেরকে ঝলসে দেয়া বাতাসের ‘আযাব থেকে রক্ষা করেছেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4763,52,28,'পূর্বে আমরা তাঁর কাছেই দু‘আ করতাম, তিনি অতি অনুগ্রহকারী, পরম দয়াবান। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4764,52,29,'কাজেই তুমি উপদেশ দিতে থাক, তোমার প্রতিপালকের অনুগ্রহে তুমি গণকও নও, আর পাগলও নও। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4765,52,30,'তারা কি বলে সে একজন কবি, যার জন্য আমরা কালচক্রের (বিপদাপদের) অপেক্ষা করছি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4766,52,31,'ওদেরকে বল- তোমরা অপেক্ষা কর, আমিও তোমাদের সঙ্গে অপেক্ষা করছি। (অতঃপর উভয় পক্ষই দেখতে পাবে কার কী পরিণতি হয়)। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4767,52,32,'তাদের বুদ্ধি বিবেক কি তাদেরকে এ নির্দেশ দেয়, নাকি তারা মূলতঃই এক সীমালঙ্ঘনকারী জাতি? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4768,52,33,'তারা কি বলে- ‘সে নিজেই (কুরআন) রচনা করে নিয়েছে? আসলে তারা ঈমানই আনেনি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4769,52,34,'তারা সত্যবাদী হলে এ রকম একটা কালাম তারা নিয়ে আসুক না কেন।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4770,52,35,'তারা কি সৃষ্টি হয়েছে, নাকি তারা নিজেরাই সৃষ্টিকর্তা? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4771,52,36,'নাকি তারা আসমান ও যমীন সৃষ্টি করেছে? আসলে তারা নিশ্চিত বিশ্বাসী নয়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4772,52,37,'নাকি তোমার প্রতিপালকের ধনভান্ডারগুলো তাদের হাতে, না তারা এর নিয়ন্ত্রক? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4773,52,38,'নাকি তাদের কাছে, সিঁড়ি আছে যাতে তারা (আকাশে উঠে যায় আর গোপন কথা) শুনে থাকে? থাকলে তাদের (সেই) শ্রোতা স্পষ্ট প্রমাণ হাজির করুক। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4774,52,39,'নাকি আল্লাহর জন্য কন্যা সন্তান, আর তোমাদের জন্য পুত্র সন্তান? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4775,52,40,'নাকি তুমি তাদের কাছে পারিশ্রমিক চাচ্ছ, যার ফলে তারা ঋণে ভারাক্রান্ত হয়ে পড়ছে? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4776,52,41,'নাকি তাদের কাছে অদৃশ্যের জ্ঞান আছে, আর তারা তা লিখছে? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4777,52,42,'নাকি তারা (তোমার বিরুদ্ধে) চক্রান্ত করতে চায়? তাহলে কিন্তু কাফিররাই চক্রান্তের ফাঁদে আটকা পড়বে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4778,52,43,'নাকি আল্লাহ ছাড়া তাদের জন্য ইলাহ্ আছে? তারা যাকে শরীক গণ্য করে আল্লাহ তা থেকে পবিত্র। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4779,52,44,'আকাশের কোন খন্ড ভেঙ্গে পড়তে দেখলে তারা বলত- ‘এটা তো পুঞ্জীভূত মেঘ। (তাদেরকে আল্লাহর যতই বিস্ময়কর নিদর্শন দেখানো হোক না কেন, তারা ঈমান আনবে না।) ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4780,52,45,'কাজেই তাদেরকে উপেক্ষা কর যতক্ষণ না তারা সাক্ষাৎ করে তাদের সেদিনের যেদিন তারা হবে বজ্রাহত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4781,52,46,'সেদিন তাদের ষড়যন্ত্র তাদের কোন কাজে আসবে না, আর তারা সাহায্যপ্রাপ্তও হবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4782,52,47,'যালিমদের জন্য এছাড়া আরো ‘আযাব রয়েছে, কিন্তু তাদের অধিকাংশই জানে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4783,52,48,'তুমি ধৈর্য ধরে তোমার প্রতিপালকের হুকুমের অপেক্ষায় থাক, কারণ তুমি আমার চোখের সামনেই আছ। আর তুমি তোমার প্রতিপালকের প্রশংসা ঘোষণা কর যখন তুমি উঠ (মাজলিস শেষে, অথবা বিছানা ছেড়ে কিংবা নামাযের জন্য)। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4784,52,49,'আর রাত্রিকালে তাঁর প্রশংসা ও মহিমা ঘোষণা কর আর (রাতের শেষভাগে যখন) তারকারাজি অস্তমিত হয়ে যায়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4785,53,1,'শপথ তারকার যখন তা অস্ত যায়,');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4786,53,2,'তোমাদের (মাঝে ছোট থেকে বড় হয়েছে সেই) সঙ্গী গুমরাহও নয় আর ভুলপথে পরিচালিতও নয়, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4787,53,3,'আর সে মনগড়া কথাও বলে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4788,53,4,'তাতো ওয়াহী যা তার প্রতি প্রত্যাদেশ করা হয়, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4789,53,5,'তাকে শিক্ষা দেয় শক্তিশালী,');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4790,53,6,'প্রজ্ঞার অধিকারী (জিবরাঈল) সে নিজ আকৃতিতে স্থির হয়ে ছিল, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4791,53,7,'আর সে ছিল ঊর্ধ্ব দিগন্তে, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4792,53,8,'অতঃপর সে (নবীর) নিকটবর্তী হল, অতঃপর আসলো আরো নিকটে, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4793,53,9,'ফলে [নবী (সাঃ) ও জিবরাঈলের মাঝে] দুই ধনুকের ব্যবধান রইল অথবা আরো কম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4794,53,10,'তখন (আল্লাহ) তাঁর বান্দাহর প্রতি ওয়াহী করলেন যা ওয়াহী করার ছিল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4795,53,11,'(নবীর) অন্তঃকরণ মিথ্যে মনে করেনি যা সে দেখে ছিল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4796,53,12,'সে যা দেখেছে সে বিষয়ে তোমরা কি তার সঙ্গে বিতর্ক করবে? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4797,53,13,'অবশ্যই সে [অর্থাৎ নবী (সা.)] তাকে [অর্থাৎ জিবরাঈল (আঃ)-কে] আরেকবার দেখেছিল ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4798,53,14,'শেষসীমার বরই গাছের কাছে, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4799,53,15,'যার কাছে অবস্থিত বসবাসের জান্নাত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4800,53,16,'যখন গাছটি যা দিয়ে ঢেকে থাকার তা দিয়ে ঢাকা ছিল, (যার বর্ণনা মানুষের বোধগম্য নয়) ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4801,53,17,'(নবীর) দৃষ্টি ভ্রমও ঘটেনি, সীমা ছাড়িয়েও যায়নি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4802,53,18,'সে তার প্রতিপালকের বড় বড় নিদর্শন দেখেছিল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4803,53,19,'তোমরা কি লাত ও উযযা সম্পর্কে ভেবে দেখেছ? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4804,53,20,'আর তৃতীয় আরেকটি মানাৎ সম্পর্কে? (এ সব অক্ষম, বাকশক্তিহীন, নড়া-চড়ার শক্তিহীন মূর্তিগুলোর পূজা করা কতটা যুক্তিযুক্ত) ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4805,53,21,'কী! তোমাদের জন্য পুত্র সন্তান আর আল্লাহর জন্য কন্যা সন্তান? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4806,53,22,'তাহলে এটাতো খুবই অসঙ্গত ভাগ-বাঁটোয়ারা। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4807,53,23,'এগুলো তো কেবল কতকগুলো নাম যে নাম তোমরা আর তোমাদের পিতৃ পৃরুষরা রেখেছ, এর পক্ষে আল্লাহ কোন প্রমাণ অবতীর্ণ করেননি। তারা তো শুধু অনুমান আর প্রবৃত্তিরই অনুসরণ করে, যদিও তাদের কাছে তাদের প্রতিপালকের পক্ষ থেকে পথ নির্দেশ এসেছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4808,53,24,'মানুষ কি তাই পায় যা সে চায়? (আল্লাহ যাকে যেভাবে ইচ্ছে  সেভাবে দেন ইহকালে আর পরকালে), ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4809,53,25,'কেননা আল্লাহই পরকাল আর ইহকালের মালিক। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4810,53,26,'আকাশে কতই না ফেরেশতা আছে তাদের সুপারিশ কোনই কাজে আসবে না, তবে (কাজে আসবে) যদি তিনি অনুমতি দেন যার জন্য আল্লাহ ইচ্ছে করবেন এবং যার প্রতি তিনি সন্তুষ্ট। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4811,53,27,'যারা আখিরাতে বিশ্বাস করে না তারাই ফেরেশতাদের স্ত্রীবাচক নামে নামকরণ করে থাকে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4812,53,28,'অথচ এ বিষয়ে তাদের কোনই জ্ঞান নেই, তারা কেবল অনুমানেরই অনুসরণ করছে, আর প্রকৃত সত্যের মুকাবালায় অনুমান কোনই কাজে আসে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4813,53,29,'কাজেই যে আমার স্মরণ হতে মুখ ফিরিয়ে নেয় আর পার্থিব জীবন ছাড়া (অন্য কিছুই) কামনা করে না, তুমি তাকে এড়িয়ে চল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4814,53,30,'তাদের জ্ঞানের দৌড় ঐ পর্যন্তই। তোমার প্রতিপালক খুব ভাল করেই জানেন কে তার পথ থেকে গুমরাহ হয়ে গেছে, আর তিনি ভালই জানেন কে সঠিক পথে আছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4815,53,31,'যা আছে আকাশে আর যা আছে যমীনে সব আল্লাহরই- যাতে তিনি যারা মন্দ কাজ করে তাদেরকে তাদের কাজের প্রতিফল দেন আর যারা সৎকর্ম করে তাদেরকে দেন শুভ প্রতিফল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4816,53,32,'যারা বিরত থাকে বড় বড় পাপ আর অশ্লীল কার্যকলাপ থেকে ছোট খাট দোষ-ত্রুটি ছাড়া; বস্তুতঃ তোমার প্রতিপালক ক্ষমা করার ব্যাপারে অতি প্রশস্ত। তিনি তোমাদের সম্পর্কে খুব ভালভাবেই জানেন যখন তিনি তোমাদেরকে মাটি থেকে সৃষ্টি করেছেন আর যখন তোমরা তোমাদের মায়েদের পেটে ভ্রুণ অবস্থায় ছিলে। কাজেই নিজেদেরকে খুব পবিত্র মনে করো না। কে তাক্বওয়া অবলম্বন করে তা তিনি ভালভাবেই জানেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4817,53,33,'তুমি কি তাকে দেখেছ যে (আল্লাহ হতে) মুখ ফিরিয়ে নেয়? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4818,53,34,'এবং সামান্য দান করে অতঃপর (হৃদয়) শক্ত করে ফেলে? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4819,53,35,'তার কি অদৃশ্যের জ্ঞান আছে যার কারণে সে দেখতে পায়? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4820,53,36,'নাকি মূসার কিতাবের তথ্য তার কাছে পৌঁছানো হয়নি, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4821,53,37,'আর ইবরাহীমের (কিতাবের খবর) যে (ইবরাহীম) ছিল পুরোপুরি দায়িত্ব পালনকারী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4822,53,38,'(সে খবর এই) যে, কোন বোঝা বহনকারী বইবে না অপরের বোঝা। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4823,53,39,'আর এই যে, মানুষ যা পাওয়ার জন্য চেষ্টা করে তাছাড়া কিছুই পায় না, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4824,53,40,'আর এই যে, তার চেষ্টা সাধনার ফল শীঘ্রই তাকে দেখানো হবে, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4825,53,41,'অতঃপর তাকে দেয়া হবে পূর্ণ প্রতিফল ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4826,53,42,'আর এই যে, শেষ গন্তব্য হল তোমার প্রতিপালক পর্যন্ত, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4827,53,43,'আর এই যে, তিনিই হাসান, তিনিই কাঁদান। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4828,53,44,'আর এই যে, তিনিই মারেন, তিনিই বাঁচান। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4829,53,45,'আর এই যে, তিনিই সৃষ্টি করেন জোড়া- পুরুষ আর নারী, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4830,53,46,'এক ফোঁটা শুক্র হতে যখন তা নিক্ষিপ্ত হয় ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4831,53,47,'আর এই যে, পুনরায় সৃষ্টির দায়িত্বভার তাঁরই উপর, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4832,53,48,'আর এই যে, তিনিই অভাবমুক্ত করেন আর সম্পদ দেন, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4833,53,49,'আর এই যে, শি‘রা (অর্থাৎ লুব্ধক নক্ষত্র)’র তিনিই প্রতিপালক, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4834,53,50,'আর এই যে, তিনিই প্রাচীন ‘আদ জাতিকে ধ্বংস করেছিলেন, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4835,53,51,'আর সামূদ জাতিকেও, তাদের একজনকেও বাকী রাখেননি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4836,53,52,'আর তার পূর্বে নূহের জাতিকেও, তারা ছিল অত্যধিক যালিম ও সীমালঙ্ঘনকারী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4837,53,53,'তিনি (লূত জাতির) উল্টানো আবাস ভূমিকে উঠিয়ে নিক্ষেপ করেছিলেন, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4838,53,54,'অতঃপর তাকে আচ্ছন্ন করল যা তাকে আচ্ছন্ন করেছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4839,53,55,'অতএব তুমি তোমার প্রতিপালকের কোন নি‘মাতে সন্দেহ পোষণ করবে? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4840,53,56,'অতীতের সতর্ককারীদের মত এ (নবীও) একজন সতর্ককারী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4841,53,57,'আগমনকারী মুহূর্ত (ক্বিয়ামত) নিকটবর্তী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4842,53,58,'আল্লাহ ছাড়া কেউ তা সরাতে পারে না (বা প্রকাশ করতে পারে না)। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4843,53,59,'তোমরা কি এ কথায় বিস্মিত হচ্ছ? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4844,53,60,'আর হাসছ, কাঁদছ না? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4845,53,61,'বৃথা খেল-তামাশায় সময় ক্ষেপন করছ, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4846,53,62,'তাই, আল্লাহর উদ্দেশে সাজদায় পতিত হও আর তাঁর বন্দেগী কর।[সাজদাহ] ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4847,54,1,'ক্বিয়ামত নিকটবর্তী হয়েছে এবং চন্দ্র খন্ডিত হয়েছে, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4848,54,2,'কিন্তু তারা যখন কোন নিদর্শন দেখে তখন মুখ ফিরিয়ে নেয় আর বলে- ‘এটা তো সেই আগের থেকে চলে আসা যাদু।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4849,54,3,'তারা সত্যকে অস্বীকার করে, আর নিজেদের কামনা বাসনার অনুসরণ করে। প্রতিটি বিষয়েরই একটা নির্দিষ্ট সময় আছে (সময় আসলেই বর্তমান অবস্থার পরিবর্তন ঘটবে)। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4850,54,4,'এই লোকেদের কাছে তো (অতীতের জাতিগুলোর) সংবাদ এসেছে যাতে আছে সাবধান বাণী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4851,54,5,'তা (হল) সুদূর প্রসারী জ্ঞান, কিন্তু সেই সতর্কবাণী কোন কাজে আসেনি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4852,54,6,'কাজেই (হে নবী) তুমি তাদের থেকে মুখ ফিরিয়ে নাও (আর অপেক্ষা কর সে দিনের) যেদিন এক আহবানকারী (তাদেরকে) আহবান করবে এক ভয়াবহ বিষয়ের দিকে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4853,54,7,'ভীত-শংকিত চোখে তারা তাদের কবর থেকে বের হয়ে আসবে- যেন তারা বিক্ষিপ্ত পঙ্গপাল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4854,54,8,'ভীত-সন্ত্রস্ত হয়ে তারা আহবানকারীর দিকে ছুটে আসবে। কাফিররা বলবে- ‘‘কঠিন এ দিন’’। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4855,54,9,'তাদের আগে নূহের জাতিও সত্য প্রত্যাখ্যান করেছিল। তারা আমার বান্দাহকে অস্বীকার করেছিল আর বলেছিল- ‘‘একটা পাগল’’; আর তাকে ভয় দেখানো হয়েছিল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4856,54,10,'তখন সে তার প্রতিপালককে ডেকেছিল- ‘‘আমি পরাস্ত হয়েছি, কাজেই তুমি এর প্রতিবিধান কর।’’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4857,54,11,'তখন আমি আকাশের দরজাগুলো খুলে দিয়ে মুষলধারায় বৃষ্টি বর্ষিয়েছিলাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4858,54,12,'আর যমীন থেকে উৎসারিত করেছিলাম ঝর্ণাধারা, অতঃপর (সব) পানি মিলিত হল যে পরিমাণ (পূর্বেই) নির্ধারিত করা হয়েছিল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4859,54,13,'আর আমি নূহকে বহন করলাম কাষ্ঠ আর কীলক যুক্ত (নৌযানে)। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4860,54,14,'যা আমার চোখের সামনে (ও আমার তত্ত্বাবধানে) ভেসে চলল সেই ব্যক্তির পক্ষে প্রতিশোধ হিসেবে যাকে অমান্য ও অস্বীকার করা হয়েছিল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4861,54,15,'এ (ঘটনা) টিকে আমি (চিরকালের জন্য) নিদর্শন হিসেবে রেখে দিলাম, অতএব উপদেশ গ্রহণ করার কেউ আছে কি? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4862,54,16,'কত ভয়ংকর ছিল আমার ‘আযাব ও ভীতি প্রদর্শন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4863,54,17,'আমি কুরআনকে সহজ করে দিয়েছি উপদেশ গ্রহণের জন্য, উপদেশ গ্রহণ করার কেউ আছে কি? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4864,54,18,'‘আদ জাতি সত্য প্রত্যাখ্যান করেছিল, ফলে কত ভয়ংকর ছিল আমার ‘আযাব ও ভীতি প্রদর্শন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4865,54,19,'আমি তাদের উপর পাঠিয়েছিলাম ঝঞ্ঝাবায়ু এক অবিরাম অশুভ দিনে, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4866,54,20,'মানুষকে তা উৎপাটিত করেছিল যেন তারা উৎপাটিত খেজুর গাছের কান্ড। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4867,54,21,'ফলে কত ভয়ংকর ছিল আমার ‘আযাব ও ভীতি প্রদর্শন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4868,54,22,'আমি কুরআনকে সহজ করে দিয়েছি উপদেশ গ্রহণের জন্য, উপদেশ গ্রহণের কেউ আছে কি? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4869,54,23,'সামূদ জাতিও ভয়প্রদর্শনকারীদেরকে অস্বীকার করেছিল, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4870,54,24,'তারা বলেছিল, ‘আমরা কি আমাদেরই মধ্যেকার মাত্র একটা লোকের অনুসরণ করব? তাহলে তো আমরা গুমরাহী আর পাগলামিতে পড়ে যাব।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4871,54,25,'আমাদের (এত মানুষের) মধ্যে শুধু কি তার উপরই বাণী পাঠানো হয়েছে? না, বরং সে বড়ই মিথ্যুক, দাম্ভিক। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4872,54,26,'আগামীকালই তারা জানতে পারবে কে বড়ই মিথ্যুক, দাম্ভিক ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4873,54,27,'আমি একটা উষ্ট্রী পাঠাচ্ছি, তাদেরকে পরীক্ষা করার জন্য, কাজেই (হে সালিহ!) তুমি তাদের আচরণ লক্ষ্য কর আর ধৈর্য ধর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4874,54,28,'আর তাদেরকে জানিয়ে দাও যে, তাদের মধ্যে (ও উষ্ট্রীর মধ্যে) পানি বণ্টিত হবে, প্রত্যেকের পানি পানের পালা আসবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4875,54,29,'শেষে তারা তাদের এক সঙ্গীকে ডাকল আর সে তাকে (অর্থাৎ উষ্ট্রীটিকে) ধরে হত্যা করল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4876,54,30,'ফলে কত ভয়ংকর হয়েছিল আমার ‘আযাব ও ভীতি প্রদর্শন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4877,54,31,'আমি তাদের উপর পাঠিয়েছিলাম একটি মাত্র প্রচন্ড ধ্বনি। ফলে তারা খোঁয়াড়ওয়ালাদের (নির্মিত) ভেঙ্গে চুরে যাওয়া শুকনা ডালপালার মত গুঁড়িয়ে গেল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4878,54,32,'আমি কুরআনকে সহজ করে দিয়েছি উপদেশ গ্রহণের জন্য, উপদেশ গ্রহণের কেউ আছে কি? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4879,54,33,'লূতের জাতি সতর্কবাণী প্রত্যাখ্যান করেছিল, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4880,54,34,'আমি তাদের উপর পাঠিয়েছিলাম প্রস্তরবর্ষী প্রচন্ড বাতাস, (যা তাদেরকে ধ্বংস করে দিয়েছিল) লূতের পরিবারকে বাদ দিয়ে। আমি তাদেরকে রাতের শেষ প্রহরে উদ্ধার করে নিয়েছিলাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4881,54,35,'আমার পক্ষ হতে অনুগ্রহস্বরূপ; এভাবেই আমি তাকে প্রতিফল দেই যে কৃতজ্ঞ হয়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4882,54,36,'লূত আমার কঠোর পাকড়াও সম্পর্কে তাদেরকে সতর্ক করে দিয়েছিল, কিন্তু তারা সতর্কবাণীর বিষয়ে বাক বিতন্ডা করেছিল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4883,54,37,'তারা লূতকে তার মেহমানদের রক্ষণাবেক্ষণ থেকে বিরত রাখতে চেষ্টা করল তখন আমি তাদের চোখগুলোকে অন্ধ করে দিলাম আর বললাম ‘আমার ‘আযাব ও সতর্কবাণীর স্বাদ গ্রহণ কর।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4884,54,38,'অতি সকালে নির্ধারিত শাস্তি তাদেরকে গ্রাস করল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4885,54,39,'তখন আমি বললাম- ‘আমার শাস্তি ও সতর্কবাণীর স্বাদ গ্রহণ কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4886,54,40,'আমি কুরআনকে সহজ করে দিয়েছি উপদেশ গ্রহণের জন্য। উপদেশ গ্রহণের কেউ আছে কি? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4887,54,41,'ফেরাউন গোষ্ঠীর কাছেও (আমার) সতর্কবাণী এসেছিল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4888,54,42,'তারা আমার সকল নিদর্শনকে অস্বীকার করেছিল, তখন আমি তাদেরকে পাকড়াও করেছিলাম মহাপরাক্রমশালী ও ক্ষমতাবানের পাকড়াওয়ে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4889,54,43,'তোমাদের (মক্কাবাসী) কাফিররা কি এ লোকেদের চেয়ে ভাল? নাকি (আসমানী) গ্রন্থাদিতে তোমাদেরকে ক্ষমা করে দেয়ার কথা লেখা আছে? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4890,54,44,'নাকি তারা বলে- ‘আমরা সংঘবদ্ধ দল, নিজেদের প্রতিরক্ষায় সক্ষম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4891,54,45,'এ সংঘবদ্ধ দল শীঘ্রই পরাজিত হবে আর পিছন ফিরে পালাবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4892,54,46,'বরং ক্বিয়ামত হল (তাদের দুষ্কর্মের প্রতিশোধ নেয়ার জন্য) তাদেরকে দেয়া নির্ধারিত সময়, ক্বিয়ামত অতি কঠিন, অতিশয় তিক্ত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4893,54,47,'পাপীরা আছে গুমরাহী আর পাগলামির মধ্যে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4894,54,48,'যেদিন তাদেরকে মুখের ভরে আগুনের মধ্যে হিঁচড়ে টেনে আনা হবে (তখন বলা হবে) ‘জাহান্নামের স্পর্শ আস্বাদন কর।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4895,54,49,'আমি সব কিছু সৃষ্টি করেছি নির্ধারিত পরিমাপে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4896,54,50,'আমার আদেশ তো মাত্র একটি কথা- চোখের পলকের মত। ');
