INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4930,55,29,'আকাশ আর পৃথিবীতে যারা আছে তারা (নিজেদের প্রয়োজন মেটানোর জন্য যা কিছু দরকার) তাঁর কাছেই চায়, প্রতি মুহূর্ত তিনি নতুন নতুন গুরুত্বপূর্ণ কাজে নিয়োজিত (কাউকেও বাঁচাচ্ছেন, কাউকেও মারছেন, কাউকেও সম্মানিত ক’রে ঊর্ধ্বে উঠাচ্ছেন, কাউকেও অপমানিত ক’রে নামিয়ে দিচ্ছেন ইত্যাদি)। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4931,55,30,'অতএব (হে জ্বিন ও মানুষ!) তোমরা তোমাদের প্রতিপালকের কোন্ কোন্ নি‘মাতকে অস্বীকার করবে? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4932,55,31,'(দায়িত্বের) ভারে ভারাক্রান্ত ওহে (জ্বিন ও মানুষ) দু’ (জাতি)! অতি শীঘ্র (কর্মমুক্ত হয়ে তোমাদেরকে জিজ্ঞাসাবাদের জন্য) আমি তোমাদের প্রতি মনোনিবেশ করব, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4933,55,32,'অতএব (হে জ্বিন ও মানুষ!) তোমরা তোমাদের প্রতিপালকের কোন্ কোন্ নি‘মাতকে অস্বীকার করবে? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4934,55,33,'হে জ্বিন ও মানুষ জাতি! তোমরা যদি আকাশ ও পৃথিবীর সীমানা অতিক্রম করতে পার তাহলে অতিক্রম কর, কিন্তু (আল্লাহর) প্রমাণপত্র ছাড়া তোমরা তা অতিক্রম করতে পারবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4935,55,34,'অতএব (হে জ্বিন ও মানুষ!) তোমরা তোমাদের প্রতিপালকের কোন্ কোন্ নি‘মাতকে অস্বীকার করবে? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4936,55,35,'(অতিক্রম করতে চাইলে) তোমাদের দিকে আগুনের শিখা ও ধোঁয়া ছেড়ে দেয়া হবে, তখন তোমরা তা প্রতিরোধ করতে পারবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (4937,55,36,'অতএব (হে জ্বিন ও মানুষ!) তোমরা তোমাদের প্রতিপালকের কোন্ কোন্ নি‘মাতকে অস্বীকার করবে? ');
