INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5036,56,57,'আমিই তো তোমাদেরকে সৃষ্টি করেছি, তাহলে তোমরা সত্যকে বিশ্বাস করবে না কেন? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5037,56,58,'তোমরা কি ভেবে দেখেছ- তোমরা যে বীর্য নিক্ষেপ কর, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5038,56,59,'তা কি তোমরা সৃষ্টি কর, না তার সৃষ্টিকর্তা আমিই। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5039,56,60,'তোমাদের মধ্যে মৃত্যু আমিই নির্ধারণ করি, আর আমি কিছুমাত্র অক্ষম নই ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5040,56,61,'তোমাদের আকার আকৃতি পরিবর্তন করতে আর তোমাদেরকে (নতুনভাবে) এমন এক আকৃতিতে সৃষ্টি করতে যা তোমরা জান না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5041,56,62,'তোমরা তোমাদের প্রথম সৃষ্টি সম্বন্ধে অবশ্যই জান তাহলে (আল্লাহ যে তোমাদেরকে পুনরায় সৃষ্টি করতে সক্ষম এ কথা) তোমরা অনুধাবন কর না কেন? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5042,56,63,'তোমরা কি ভেবে দেখেছ তোমরা যে বীজ বপন কর সে সম্পর্কে? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5043,56,64,'তোমরাই কি তা উৎপন্ন কর, না আমিই উৎপন্নকারী? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5044,56,65,'আমি ইচ্ছে করলে তাকে অবশ্যই খড়কুটা করে দিতে পারি, তখন তোমরা হয়ে যাবে বিস্ময়ে হতবাক। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5045,56,66,'(আর বলবে যে) ‘আমরা তো দায়গ্রস্ত হয়ে পড়লাম, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5046,56,67,'বরং আমরা বঞ্চিত হয়ে গেলাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5047,56,68,'তোমরা কি পানি সম্পর্কে চিন্তা করে দেখেছ যা তোমরা পান কর? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5048,56,69,'তা কি তোমরাই মেঘ থেকে বর্ষণ কর, নাকি তার বষর্ণকারী আমিই? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5049,56,70,'আমি ইচ্ছে করলে তাকে লবণাক্ত করে দিতে পারি, তাহলে কেন তোমরা শোকর আদায় কর না? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5050,56,71,'তোমরা যে আগুন জ্বালাও সে সম্পর্কে কি তোমরা চিন্তা করে দেখেছ? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5051,56,72,'তার (জ্বালানোর) গাছ (অর্থাৎ কাঠ) কি তোমরাই বানিয়েছ, নাকি আমিই বানিয়েছি? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5052,56,73,'আমি তাকে (অর্থাৎ আগুনকে) করেছি স্মারক (যা জাহান্নামের আগুনের কথা স্মরণ করিয়ে দেয়) আর মরুর অধিবাসীদের জন্য দরকারী ও আরামের বস্তু। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5053,56,74,'কাজেই (হে নবী!) তুমি তোমার মহান প্রতিপালকের মহিমা ও গৌরব ঘোষণা কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5054,56,75,'উপরন্তু আমি শপথ করছি তারকারাজির অস্তাচলের। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5055,56,76,'তা অবশ্যই অতি বড় শপথ যদি তোমরা জানতে! ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5056,56,77,'অবশ্যই তা সম্মানিত কুরআন, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5057,56,78,'(যা লিখিত আছে) সুরক্ষিত কিতাবে, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5058,56,79,'পূত-পবিত্র (ফেরেশতা) ছাড়া (শয়ত্বানেরা) তা স্পর্শ করতে পারে না, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5059,56,80,'জগৎ সমূহের প্রতিপালকের নিকট থেকে অবতীর্ণ, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5060,56,81,'তবুও কি তোমরা এ বাণীকে তুচ্ছ মনে করছ? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5061,56,82,'আর তাকে মিথ্যে বলাকেই তোমরা তোমাদের জীবিকা বানিয়ে নিয়েছ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5062,56,83,'তাহলে কেন (তোমরা বাধা দাও না) যখন প্রাণ এসে যায় কণ্ঠনালীতে? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5063,56,84,'আর তোমরা তাকিয়ে তাকিয়ে দেখ, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5064,56,85,'আর আমি তোমাদের চেয়ে তার (অর্থাৎ প্রাণের) নিকটবর্তী, কিন্তু তোমরা দেখতে পাওনা। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5065,56,86,'তোমরা যদি (আমার) কর্তৃত্বের অধীন না হও ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5066,56,87,'তাহলে তোমরা তাকে (অর্থাৎ তোমাদের প্রাণকে মৃত্যুর সময়) ফিরিয়ে নাও না কেন যদি তোমরা (তোমাদের দাবীতে) সত্যবাদী হয়েই থাক? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5067,56,88,'অতএব সে যদি (আল্লাহর) নৈকট্য প্রাপ্তদের একজন হয় ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5068,56,89,'তাহলে (তার জন্য আছে) আরাম-শান্তি, উত্তম রিযক আর নি‘মাতে-ভরা জান্নাত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5069,56,90,'আর যদি সে ডান দিকের একজন হয় ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5070,56,91,'তাহলে হে ডানের বাসিন্দা! তোমার জন্য আছে শান্তি ও নিরাপত্তা। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5071,56,92,'আর সে যদি সত্য অস্বীকারকারী গুমরাহদের অন্তর্গত হয়, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5072,56,93,'তবে তার আপ্যায়ন হবে ফুটন্ত পানি দিয়ে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5073,56,94,'আর (তার জন্য আছে) জাহান্নামের আগুনের দহন, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5074,56,95,'এটা সুনিশ্চিত সত্য। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5075,56,96,'কাজেই তুমি তোমার মহান প্রতিপালকের গৌরব ও মহিমা ঘোষণা কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5076,57,1,'আসমান ও যমীনে যা কিছু আছে সবই আল্লাহর গৌরব ও মহিমা ঘোষণা করে, তিনি প্রবল পরাক্রান্ত, মহা প্রজ্ঞাবান। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5077,57,2,'আসমান ও যমীনের রাজত্ব তাঁরই, তিনিই জীবন দেন, আর তিনিই মৃত্যু দেন, তিনি সব কিছুর উপর ক্ষমতাবান। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5078,57,3,'তিনিই প্রথম, তিনিই শেষ, তিনি প্রকাশিত আবার গুপ্ত, তিনি সকল বিষয় পূর্ণরূপে জ্ঞাত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5079,57,4,'তিনি আসমান ও যমীনকে ছয় দিনে সৃষ্টি করেছেন অতঃপর আরশে সমুন্নত হয়েছেন। তিনি জানেন যা যমীনে প্রবেশ করে, আর যা তাত্থেকে বের হয়, আর যা আকাশ থেকে অবতীর্ণ হয়, আর যা তাতে উঠে যায়, তোমরা যেখানেই থাক তিনি তোমাদের সঙ্গে আছেন, তোমরা যে কাজই কর না কেন, আল্লাহ তা দেখেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5080,57,5,'আসমান ও যমীনের রাজত্ব তাঁরই, যাবতীয় বিষয় তাঁরই দিকে ফিরে যায় (চূড়ান্ত ফয়সালার জন্য)। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5081,57,6,'তিনিই রাতকে প্রবেশ করান দিনের ভিতর, আর দিনকে ঢুকিয়ে দেন রাতের ভিতর, অন্তরের গোপনতত্ত্ব সম্পর্কে তিনি পূর্ণরূপে অবগত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5082,57,7,'তোমরা আল্লাহ ও তাঁর রসূলের প্রতি ঈমান আনো, আর তিনি তোমাদেরকে যার উত্তরাধিকারী করেছেন তাত্থেকে (আল্লাহর পথে) ব্যয় কর । কারণ তোমাদের মধ্যে যারা ঈমান আনে আর ব্যয় করে, তাদের জন্য আছে বিরাট প্রতিফল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5083,57,8,'তোমাদের কী হল যে, তোমরা আল্লাহর প্রতি ঈমান আনবে না যখন রসূল তোমাদেরকে তোমাদের প্রতিপালকের উপর ঈমান আনার জন্য ডাকছে আর তিনি তোমাদের কাছ থেকে প্রতিশ্রুতি গ্রহণ করেছেন, যদি তোমরা বিশ্বাসী হয়েই থাক। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5084,57,9,'তিনিই তাঁর বান্দাহর উপর সুস্পষ্ট আয়াত অবতীর্ণ করেন তোমাদেরকে ঘোর অন্ধকার থেকে আলোতে আনার জন্য, আল্লাহ অবশ্যই তোমাদের প্রতি বড়ই করুণাশীল, অতি দয়ালু। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5085,57,10,'তোমাদের হল কী যে তোমরা আল্লাহর পথে ব্যয় করবে না! আকাশ ও পৃথিবীর উত্তরাধিকার তো আল্লাহরই জন্য (কাজেই তাঁর পথে ব্যয় করলে তোমরা গরীব হয়ে যাবে এ আশঙ্কার কোন কারণ নেই)। তোমাদের মধ্যে যারা (মক্কা) বিজয়ের পূর্বে ব্যয় করেছে আর যুদ্ধ করেছে তারা সমান নয় (তাদের, যারা তা বিজয়ের পরে করেছে); তাদের মর্যাদা অনেক বড় তাদের তুলনায় যারা পরে ব্যয় করেছে ও যুদ্ধ করেছে। উভয়ের জন্যই আল্লাহ কল্যাণের ও‘য়াদা দিয়েছেন। তোমরা যা কিছু কর সে সম্পর্কে আল্লাহ পূর্ণভাবে অবগত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5086,57,11,'এমন কে আছে যে, আল্লাহকে উত্তম ঋণ দিবে? তাহলে তিনি তা তার জন্য কয়েকগুণ বাড়িয়ে দিবেন আর তার জন্য আছে সম্মানজনক প্রতিফল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5087,57,12,'সে দিন তুমি মু’মিন ও মু’মিনাদের দেখবে, তাদের সামনে আর তাদের ডানে তাদের জ্যোতি ছুটতে থাকবে। (তাদেরকে বলা হবে) ‘আজ তোমাদের জন্য জান্নাতের সুসংবাদ যার নীচ দিয়ে ঝর্ণাধারা প্রবাহিত, তাতে (তোমরা) চিরকাল থাকবে। এটাই হল বিরাট সফলতা। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5088,57,13,'সেদিন মুনাফিক পুরুষ ও মুনাফিক নারীরা মু’মিনদেরকে বলবে- ‘তোমরা আমাদের জন্য অপেক্ষা কর, তোমাদের জ্যোতি থেকে আমরা কিছুটা নিয়ে নেই।’ তাদেরকে বলা হবে- ‘তোমরা তোমাদের পেছনে ফিরে যাও, ‘আর আলোর খোঁজ কর।’ তখন তাদের মাঝে একটি আড়াল খাড়া করে দেয়া হবে যার থাকবে একটি দরজা। তার ভিতর ভাগে থাকবে রহমত আর বহির্ভাগের সর্বত্র থাকবে ‘আযাব। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5089,57,14,'তারা মু’মিনদেরকে ডেকে বলবে- ‘আমরা কি তোমাদের সঙ্গে ছিলাম না?’ তারা উত্তর দিবে, ‘হাঁ, কিন্তু তোমরা নিজেরাই নিজেদেরকে বিপদে ফেলে দিয়েছ, তোমরা অপেক্ষা করেছিলে (আমাদের ধ্বংসের জন্য), তোমরা সন্দেহে পতিত ছিলে, আর মিথ্যে আশা আকাঙ্ক্ষা তোমাদেরকে প্রতারিত করেছিল। শেষ পর্যন্ত আল্লাহর হুকুম এসে গেল, আর বড় প্রতারক (শাইত্বন) তোমাদেরকে আল্লাহর ব্যাপারে প্রতারিত করল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5090,57,15,'আজ তোমাদের কাছ থেকে কোন মুক্তিপণ গ্রহণ করা হবে না, আর যারা কুফুরী করেছিল তাদের কাছ থেকেও না। তোমাদের বসবাসের জায়গা জাহান্নাম, সেটাই তোমাদের যথাযোগ্য স্থান। কতই না নিকৃষ্ট সেই আশ্রয়স্থল! ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5091,57,16,'যারা ঈমান এনেছে তাদের জন্য সে সময় কি এখনও আসেনি যে আল্লাহর স্মরণে আর যে প্রকৃত সত্য অবতীর্ণ হয়েছে তাতে তাদের অন্তর বিগলিত হয়ে যাবে? আর তারা যেন সেই লোকদের মত না হয়ে যায় যাদেরকে পূর্বে কিতাব দেয়া হয়েছিল, অতঃপর তাদের উপর অতিবাহিত হয়ে গেল বহু বহু যুগ আর তাদের অন্তর কঠিন হয়ে পড়ল। তাদের অধিকাংশই পাপাচারী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5092,57,17,'জেনে রেখ, আল্লাহই যমীনকে তার মৃত্যুর পর আবার জীবিত করেন। আমি তোমাদের কাছে সুস্পষ্টভাবে নিদর্শন বর্ণনা করেছি যাতে তোমরা বুঝতে পার। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5093,57,18,'দানশীল পুরুষরা আর দানশীলা নারীরা আর যারা আল্লাহকে ঋণ দেয়- উত্তম ঋণ, তাদের জন্য বহুগুণ বাড়িয়ে দেয়া হবে, আর তাদের জন্য আছে মর্যাদাপূর্ণ প্রতিদান। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5094,57,19,'আর যারা আল্লাহ ও তাঁর রসূলের প্রতি ঈমান আনে তারাই তাদের প্রতিপালকের নিকট সিদ্দীক ও শহীদ। তাদের জন্য আছে তাদের প্রতিদান ও তাদের নূর। আর যারা কুফুরী করে আর আমার নিদর্শনকে অস্বীকার করে, তারাই জাহান্নামের বাসিন্দা। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5095,57,20,'তোমরা জেনে রেখ, দুনিয়ার জীবন ক্রীড়া-কৌতুক, শোভা-সৌন্দর্য, পারস্পরিক গর্ব-অহঙ্কার আর ধন-মাল ও সন্তানাদিতে আধিক্যের প্রতিযোগিতা মাত্র। তার উদাহরণ হল বৃষ্টি, আর তা হতে উৎপন্ন শষ্যাদি কৃষকের মনকে আনন্দে ভরে দেয়, তারপর তা পেকে যায়, তখন তুমি তাকে হলুদ বর্ণ দেখতে পাও, পরে তা খড় ভুষি হয়ে যায়। (আর আখেরাতের চিত্র অন্যরকম, পাপাচারীদের জন্য), আখেরাতে আছে কঠিন শাস্তি, (আর নেককারদের জন্য আছে) আল্লাহর ক্ষমা ও সন্তুষ্টি। আর দুনিয়ার জীবনটা তো ধোঁকার বস্তু ছাড়া আর কিছুই না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5096,57,21,'তোমরা এগিয়ে যাও তোমাদের প্রতিপালকের ক্ষমা ও জান্নাত লাভের জন্য, যার প্রশস্ততা আসমান ও যমীনের প্রশস্ততার মত। তা প্রস্তুত রাখা হয়েছে তাদের জন্য যারা আল্লাহ ও তাঁর রসূলদের প্রতি ঈমান এনেছে। এটা আল্লাহর অনুগ্রহ, তিনি তা দেন যাকে ইচ্ছে করেন, আল্লাহ বড়ই অনুগ্রহশীল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5097,57,22,'পৃথিবীতে অথবা তোমাদের নিজেদের উপর এমন কোন মুসীবত আসে না যা আমি সংঘটিত করার পূর্বে কিতাবে লিপিবদ্ধ রাখি না। এটা (করা) আল্লাহর জন্য খুবই সহজ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5098,57,23,'এটা এজন্য যে, তোমাদের যে ক্ষতি হয়েছে তার জন্য তোমরা যেন হতাশাগ্রস্ত না হও, আর তোমাদেরকে যা দান করা হয়েছে তার জন্য তোমরা যেন উৎফুল্ল না হও, কেননা আল্লাহ অহংকারী ও অধিক গর্বকারীকে পছন্দ করেন না- ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5099,57,24,'যারা কৃপণতা করে আর মানুষকে কৃপণতা করার আদেশ দেয় এবং যে ব্যক্তি (আল্লাহর পথ হতে) মুখ ফিরিয়ে নেয় (সে জেনে রাখুক) আল্লাহ অভাব মুক্ত, প্রশংসিত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5100,57,25,'আমি আমার রসূলদেরকে সুস্পষ্ট প্রমাণসহ পাঠিয়েছি আর তাদের সঙ্গে অবতীর্ণ করেছি কিতাব ও (সত্য মিথ্যার) মানদন্ড যাতে মানুষ ইনসাফ ও সুবিচারের উপর প্রতিষ্ঠিত হতে পারে। আমি অবতীর্ণ করেছি লোহা যাতে আছে প্রচন্ড শক্তি আর মানুষের জন্য নানাবিধ উপকার যাতে আল্লাহ পরীক্ষা করতে পারেন আল্লাহকে না দেখেই তাঁকে আর তাঁর রসূলদেরকে কারা (এই লোহার শক্তি দিয়ে ও যাবতীয় উপায়ে) সাহায্য করে। আল্লাহ বড়ই শক্তিমান, মহাপরিক্রমশালী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5101,57,26,'আমি নূহ (আলাইহিস সালাম) আর ইব্রাহীম (আলাইহিস সালাম)-কে পাঠিয়েছিলাম আর তাদের বংশধরদের মধ্যে নুবুওয়াত ও কিতাবের ব্যবস্থা প্রতিষ্ঠা করেছিলাম। তাদের মধ্যে কেউ কেউ সঠিক পথ অবলম্বন করেছিল আর তাদের অধিকাংশই ছিল পাপাচারী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5102,57,27,'অতঃপর তাদের পথ ধরে আমি আমার রসূলদেরকে পাঠিয়েছিলাম, অতঃপর তাদের পেছনে আমি মারইয়াম-পুত্র ‘ঈসা (আলাইহিস সালাম)-কে পাঠিয়েছিলাম আর তাকে দিয়েছিলাম ইঞ্জীল। আর যারা তাকে অনুসরণ করেছিল তাদের অন্তরে দিয়েছিলাম করুণা ও দয়ামায়া। আর বৈরাগ্যবাদ- তা তারা নিজেরাই নতুনভাবে চালু করেছে আল্লাহর সন্তুষ্টি লাভের আশায়। আমি তাদের উপর এ বিধান অপরিহার্য করিনি। আর সেটাও তারা লালন করতে পারেনি যেভাবে তা লালন করা প্রয়োজন ছিল। তাদের মধ্যে যারা ঈমান গ্রহণ করেছিল তাদেরকে আমি তাদের প্রাপ্য পুরস্কার দিয়েছিলাম। কিন্তু তাদের অধিকাংশই ছিল পাপাচারী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5103,57,28,'ওহে মু’মিনগণ! তোমরা আল্লাহকে ভয় কর আর তাঁর রসূলের প্রতি ঈমান আন, তিনি তাঁর অনুগ্রহে তোমাদেরকে দ্বিগুণ পুরস্কার দিবেন আর তিনি তোমাদের জন্য আলোর ব্যবস্থা করবেন যা দিয়ে তোমরা পথ চলবে, আর তিনি তোমাদেরকে ক্ষমা করে দিবেন। আল্লাহ বড়ই ক্ষমাশীল, অতি দয়ালু। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5104,57,29,'(আমি আহলে কিতাব ছাড়া অন্যত্র নুবুওয়াত দিলাম) এ জন্য যে, আহলে কিতাবগণ যেন জেনে নিতে পারে যে, আল্লাহর অনুগ্রহের কোন কিছুকেই তাদের নিয়ন্ত্রণ করার কোন ক্ষমতা নেই, আর (তারা যেন আরো জেনে নিতে পারে যে) অনুগ্রহ একমাত্র আল্লাহর হাতেই, যাকে ইচ্ছে তিনিই তা দেন। আল্লাহ বিশাল অনুগ্রহের অধিকারী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5105,58,1,'আল্লাহ তার কথা শুনেছেন যে নারী (খাওলাহ বিনত সা‘লাবাহ) তার স্বামীর বিষয়ে তোমার সাথে বাদানুবাদ করছে আর আল্লাহর কাছে ফরিয়াদ জানাচ্ছে, আল্লাহ তোমাদের দু’জনের কথা শুনছেন, আল্লাহ সর্বশ্রোতা, সর্বদ্রষ্টা। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5106,58,2,'তোমাদের মধ্যে যারা তাদের স্ত্রীদের সাথে জিহার করে (অর্থাৎ স্ত্রীকে বলে যে, তুমি আমার জন্য আমার মায়ের পিঠের মত) তাদের স্ত্রীরা তাদের মা নয়। তাদের মা তো কেবল তারাই যারা তাদের জন্ম দিয়েছে। তারা অবশ্যই ঘৃণ্য ও মিথ্যে কথা বলে, নিশ্চয়ই আল্লাহ পাপ মোচনকারী, বড়ই ক্ষমাশীল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5107,58,3,'যারা নিজেদের স্ত্রীদের সাথে জিহার করে, অতঃপর তারা যে কথা বলেছে তা প্রত্যাহার করে নেয় তবে পরস্পরকে স্পর্শ করার পূর্বে তাদেরকে একটি দাস মুক্ত করতে হবে, এর দ্বারা তোমাদেরকে উপদেশ দেয়া হচ্ছে। তোমরা যা কর, আল্লাহ তার খবর রাখেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5108,58,4,'কিন্তু যার (দাস সংগ্রহ করার) সামর্থ্য নেই, সে এক নাগাড়ে দু’মাস রোযা রাখবে পরস্পরকে স্পর্শ করার পূর্বে। আর যে তা করতে পারবে না, সে ষাট জন মিসকীনকে খাবার খাওয়াবে। এ নির্দেশ দেয়া হচ্ছে এ জন্য যাতে তোমরা আল্লাহ ও তাঁর রসূলের প্রতি ঈমান আনো। এগুলো আল্লাহর নির্ধারিত সীমা। (যারা এটা অস্বীকার করবে সেই) কাফিরদের জন্য আছে মর্মান্তিক শাস্তি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5109,58,5,'যারা আল্লাহ ও তাঁর রসূলের বিরোধিতা করে তাদেরকে লাঞ্ছিত করা হবে যেমন লাঞ্ছিত করা হয়েছিল তাদের পূর্ববর্তীদেরকে। আমি সুস্পষ্ট আয়াত অবতীর্ণ করেছি আর (অস্বীকারকারী) কাফিরদের জন্য আছে অপমানজনক শাস্তি, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5110,58,6,'সেদিন, যেদিন আল্লাহ তাদের সকলকে আবার জীবিত করে উঠাবেন এবং তাদের কৃতকর্মের সংবাদ তাদেরকে জানিয়ে দিবেন, আল্লাহ তা হিসেব করে রেখেছেন যদিও তারা (নিজেরা) ভুলে গেছে। আল্লাহ প্রতিটি বিষয়ের প্রত্যক্ষদর্শী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5111,58,7,'তুমি কি জান না যে, যা আকাশে আছে আর যা যমীনে আছে আল্লাহ সব জানেন। তিনজনের মধ্যে এমন কোন গোপন পরামর্শ হয় না যাতে চতুর্থজন আল্লাহ হন না, আর পাঁচজনেও হয় না, ষষ্ঠজন তিনি ছাড়া, এর কম সংখ্যকেও হয় না, আর বেশি সংখ্যরেও হয় না, তিনি তাদের সঙ্গে থাকা ব্যতীত, তারা যেখানেই থাকুক না কেন। অতঃপর ক্বিয়ামত দিবসে তিনি জানিয়ে দেবেন যা তারা ‘আমাল করেছিল। আল্লাহ সকল বিষয়ে পূর্ণভাবে অবগত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5112,58,8,'তুমি কি তাদেরকে দেখনি যাদেরকে গোপন পরামর্শ করতে নিষেধ করা হয়েছিল, অতঃপর তারা আবার তাই করল যা করতে তাদেরকে নিষেধ করা হয়েছিল, তারা গোপনে পরামর্শ করল পাপাচার, সীমালঙ্ঘন আর রসূলকে অমান্য করা নিয়ে। তারা যখন তোমার কাছে আসে তখন তারা তোমাকে এমনভাবে সম্ভাষণ করে যেমনভাবে আল্লাহ তোমাকে সম্ভাষণ করেননি। তারা মনে মনে বলে- ‘আমরা যা বলি তার জন্য আল্লাহ আমাদেরকে ‘আযাব দেন না কেন? জাহান্নামই তাদের জন্য যথেষ্ট, তাতে তারা জ্বলবে, কতই না নিকৃষ্ট সেই গন্তব্যস্থল! ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5113,58,9,'হে মু’মিনগণ! তোমরা যখন গোপন পরামর্শ কর, তখন পাপাচার, সীমালঙ্ঘন আর রসূলকে অমান্য করার পরামর্শ কর না। তোমরা সৎকর্ম ও তাকওয়া অবলম্বনের ব্যাপারে পরামর্শ করো। আর তোমরা আল্লাহকে ভয় কর যাঁর কাছে তোমাদেরকে সমবেত করা হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5114,58,10,'গোপন পরামর্শ হল মু’মিনদেরকে দুঃখ দেয়ার জন্য শয়ত্বান প্ররোচিত কাজ। তবে আল্লাহর অনুমতি ছাড়া তা তাদের কোন ক্ষতি করতে পারে না। মু’মিনদের কর্তব্য হল একমাত্র আল্লাহরই উপর ভরসা করা। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5115,58,11,'হে মু’মিনগণ! তোমাদেরকে যখন বলা হয়- ‘বৈঠক প্রশস্ত করে দাও’, তখন তোমরা তা প্রশস্ত করে দিবে, আল্লাহ তোমাদেরকে প্রশস্ততা দান করবেন। আর যখন বলা হয়- ‘তোমরা উঠে যাও’, তখন তোমরা উঠে যাবে। তোমাদের মধ্যে যারা ঈমান এনেছে আর যাদেরকে জ্ঞান দান করা হয়েছে, আল্লাহ তাদেরকে মর্যাদায় উচ্চ করবেন। তোমরা যা কর আল্লাহ তার খবর রাখেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5116,58,12,'হে মু’মিনগণ! তোমরা যখন রসূলের সঙ্গে গোপনে কথা বল, তখন গোপনে কথা বলার আগে সদাক্বাহ দাও, এটাই তোমাদের জন্য উত্তম ও অতি পবিত্র পন্থা। আর যদি সদাক্বাহ জোগাড় করতে না পার, তাহলে আল্লাহ অতি ক্ষমাশীল, অতি দয়ালু। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5117,58,13,'তোমরা কি ভয় পেয়ে গেলে যে, তোমাদেরকে (নবীর সঙ্গে) গোপনে কথাবার্তা বলার আগে সদাক্বাহ দিতে হবে? তোমরা যদি তা না কর, তাহলে আল্লাহ তোমাদেরকে ক্ষমা করে দিলেন, কাজেই তোমরা নামায কায়িম কর, যাকাত দাও এবং আল্লাহ ও তাঁর রসূলের আনুগত্য কর। তোমরা যা কর সে বিষয়ে আল্লাহ পুরোপুরি অবগত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5118,58,14,'তুমি কি তাদেরকে দেখনি যারা সেই সম্প্রদায়ের সঙ্গে বন্ধুত্ব করে যেই সম্প্রদায়ের প্রতি আল্লাহ রাগান্বিত? তারা না তোমাদের লোক, আর না তাদের লোক আর তারা জেনে শুনে মিথ্যে শপথ করে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5119,58,15,'আল্লাহ তাদের জন্য প্রস্তুত করে রেখেছেন কঠিন শাস্তি, তারা যা করে তা কতই না মন্দ! ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5120,58,16,'তারা তাদের শপথগুলোকে ঢাল বানিয়ে নিয়েছে, এর সাহায্যে তারা মানুষকে আল্লাহর পথ থেকে ফিরিয়ে রাখে, এ কারণে তাদের জন্য আছে অপমানজনক শাস্তি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5121,58,17,'তাদের ধন-সম্পদ আর সন্তানাদি আল্লাহর শাস্তি থেকে তাদেরকে বাঁচানোর ব্যাপারে কোন কাজে আসবে না, তারা জাহান্নামের অধিবাসী, সেখানে তারা চিরকাল থাকবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5122,58,18,'আল্লাহ যেদিন তাদের সবাইকে আবার জীবিত করে উঠাবেন, তখন তারা আল্লাহর কাছে ঠিক সে রকম শপথই করবে, যেমন শপথ তারা তোমাদের নিকট করে, আর তারা মনে করে যে, তাদের কিছু (কোন ভিত্তি) আছে। জেনে রেখ, তারা খুবই মিথ্যেবাদী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5123,58,19,'শয়ত্বান তাদের উপর প্রভাব খাটিয়ে বসেছে, আর তাদেরকে আল্লাহর স্মরণ ভুলিয়ে দিয়েছে। তারা শয়ত্বানের দল। জেনে রেখ, শয়ত্বানের দলই ক্ষতিগ্রস্ত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5124,58,20,'যারা আল্লাহর ও তাঁর রসূলের বিরোধিতা করে, তারাই সবচেয়ে বেশী লাঞ্ছিতদের অন্তর্ভুক্ত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5125,58,21,'আল্লাহ লিখে দিয়েছেন যে, অবশ্য অবশ্যই আমি ও আমার রসূলগণ বিজয়ী থাকব। আল্লাহ মহা শক্তিমান ও পরাক্রমশালী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5126,58,22,'আল্লাহ ও পরকালে বিশ্বাসী এমন কোন সম্প্রদায় তুমি পাবে না যারা আল্লাহ ও তাঁর রসূলের বিরোধিতাকারীদেরকে ভালবাসে- হোক না এই বিরোধীরা তাদের পিতা অথবা পুত্র অথবা তাদের ভাই অথবা তাদের জ্ঞাতি গোষ্ঠী। আল্লাহ এদের অন্তরে ঈমান বদ্ধমূল করে দিয়েছেন, আর নিজের পক্ষ থেকে রূহ দিয়ে তাদেরকে শক্তিশালী করেছেন। তাদেরকে তিনি দাখিল করবেন জান্নাতে যার তলদেশ দিয়ে বয়ে চলেছে নদী-নালা, তাতে তারা চিরকাল থাকবে। আল্লাহ তাদের প্রতি সন্তুষ্ট আর তারাও তাঁর প্রতি সন্তুষ্ট। এরাই আল্লাহর দল; জেনে রেখ, আল্লাহর দলই সাফল্যমন্ডিত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5127,59,1,'আসমান ও যমীনে যা কিছু আছে সবই আল্লাহর গৌরব ও মহিমা ঘোষণা করে। আর তিনি (আল্লাহ) পরাক্রমশালী প্রজ্ঞাময়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5128,59,2,'কিতাবধারীদের অন্তর্ভুক্ত কাফিরদেরকে আক্রমণের প্রথম ধাপেই তিনিই তাদের বাড়ী থেকে বের ক’রে দিলেন। তোমরা ধারণাও করনি যে, তারা বের হবে। আর তারা মনে করেছিল যে, তাদের দূর্গগুলো তাদেরকে আল্লাহ (’র কবল) থেকে রক্ষা করবে। কিন্তু আল্লাহ তাদেরকে এমন দিক থেকে পাকড়াও করলেন যা তারা ভাবতেও পারেনি। তিনি তাদের অন্তরে ভীতির সঞ্চার করলেন। তারা তাদের নিজেদের হাত দিয়েই নিজেদের ঘরবাড়ী ধ্বংস করল, আর মু’মিনদের হাতেও (ধ্বংস করাল)। অতএব হে দৃষ্টিসম্পন্ন মানুষেরা! তোমরা শিক্ষা গ্রহণ কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5129,59,3,'আল্লাহ যদি তাদের জন্য নির্বাসন না লিখে দিতেন, তাহলে তিনি তাদেরকে দুনিয়াতেই অবশ্য অবশ্যই (অন্য) শাস্তি দিতেন, পরকালে তো তাদের জন্য জাহান্নামের শাস্তি আছেই। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5130,59,4,'এর কারণ এই যে, তারা আল্লাহ ও তাঁর রসূলের প্রবল বিরোধিতা করেছে; আর যে-ই আল্লাহর বিরোধিতা করবে, আল্লাহ তাকে শাস্তিদানে বড়ই কঠোর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5131,59,5,'তোমরা খেজুরের যে গাছগুলো কেটেছ আর যেগুলোকে তাদের মূলকান্ডের উপর দাঁড়িয়ে থাকতে দিয়েছ, তা আল্লাহর অনুমতিক্রমেই (করেছ)। আর (এ অনুমতি আল্লাহ এজন্য দিয়েছেন) যেন তিনি পাপাচারীদেরকে অপমানিত করেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5132,59,6,'আল্লাহ তাঁর রসূলকে তাদের কাছ থেকে যে ফায় (বিনা যুদ্ধে পাওয়া সম্পদ) দিয়েছেন তার জন্য তোমরা ঘোড়াও দৌড়াওনি, আর উটেও চড়নি, বরং আল্লাহ তাঁর রসূলগণকে যার উপর ইচ্ছে আধিপত্য দান করেন; আল্লাহ সর্ববিষয়ে ক্ষমতাবান। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5133,59,7,'যে ধন-সম্পদ আল্লাহ জনপদবাসীদের কাছ থেকে নিয়ে তাঁর রসূলকে দিলেন তা আল্লাহর জন্য তাঁর রসূলের জন্য আর রসূলের আত্মীয়-স্বজন, ইয়াতীম, মিসকীন ও পথিকদের জন্য যাতে তা তোমাদের মধ্যকার সম্পদশালীদের মধ্যেই আবর্তিত না হয়। রসূল তোমাদেরকে যা দেয় তা গ্রহণ কর, আর তোমাদেরকে যাত্থেকে নিষেধ করে তাত্থেকে বিরত থাক, আল্লাহকে ভয় কর, আল্লাহ কঠিন শাস্তিদাতা। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5134,59,8,'(আর এ সম্পদ) সে সব দরিদ্র মুহাজিরদের জন্য যাদেরকে তাদের বাড়ীঘর ও সম্পত্তি-সম্পদ থেকে উৎখাত করা হয়েছে। যারা আল্লাহর অনুগ্রহ ও সন্তুষ্টি কামনা করে, আর তারা আল্লাহ ও তাঁর রসূলকে সাহায্য করে। এরাই সত্যবাদী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5135,59,9,'(আর এ সম্পদ তাদের জন্যও) যারা মুহাজিরদের আসার আগে থেকেই (মাদীনাহ) নগরীর বাসিন্দা ছিল আর ঈমান গ্রহণ করেছে। তারা তাদেরকে ভালবাসে যারা তাদের কাছে হিজরাত করে এসেছে। মুহাজিরদেরকে যা দেয়া হয়েছে তা পাওয়ার জন্য তারা নিজেদের অন্তরে কোন কামনা রাখে না, আর তাদেরকে (অর্থাৎ মুহাজিরদেরকে) নিজেদের উপর অগ্রাধিকার দেয়- নিজেরা যতই অভাবগ্রস্ত হোক না কেন। বস্তুত: যাদেরকে হৃদয়ের সংকীর্ণতা থেকে রক্ষা করা হয়েছে তারাই সফলকাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5136,59,10,'(এ সম্পদ তাদের জন্যও) যারা অগ্রবর্তীদের পরে (ইসলামের ছায়াতলে) এসেছে। তারা বলে- ‘হে আমাদের প্রতিপালক! আমাদেরকে আর আমাদের ভাইদেরকে ক্ষমা কর যারা ঈমানের ক্ষেত্রে আমাদের অগ্রবর্তী হয়েছে, আর যারা ঈমান এনেছে তাদের ব্যাপারে আমাদের অন্তরে কোন হিংসা বিদ্বেষ রেখো না। হে আমাদের প্রতিপালক! তুমি বড়ই করুণাময়, অতি দয়ালু।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5137,59,11,'তুমি কি তাদেরকে দেখনি যারা মুনাফিকী করেছিল? আহলে কিতাবের মধ্যে যারা কুফুরী করেছিল তাদের সেই ভাইদেরকে তারা (অর্থাৎ মুনাফিকরা) বলেছিল- ‘তোমরা যদি বহিস্কৃত হও, তাহলে অবশ্য অবশ্যই আমরাও তোমাদের সাথে বেরিয়ে  যাব,  আর  তোমাদের  ব্যাপারে  আমরা কক্ষনো কারো কথা মেনে নেব না। আর যদি তোমাদের বিরুদ্ধে যুদ্ধ করা হয়, তাহলে আমরা অবশ্য অবশ্যই তোমাদেরকে সাহায্য করব। আল্লাহ সাক্ষ্য দিচ্ছেন, তারা অবশ্যই মিথ্যেবাদী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5138,59,12,'তারা বহিস্কৃত হলেও এরা তাদের সাথে বেরিয়ে যাবে না। তাদের উপর আক্রমণ করা হলেও এরা তাদেরকে কক্ষনো সাহায্য করবে না। আর এরা সাহায্য করলেও তারা (অর্থাৎ মুনাফিকরা) অবশ্য অবশ্যই পৃষ্ঠপ্রদর্শন করবে, অতঃপর তারা (অর্থাৎ কাফিররা) আর কোন সাহায্যই পাবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5139,59,13,'তাদের অন্তরে আল্লাহর চেয়ে তোমাদের ভয়ই বেশি প্রবল। এর কারণ এই যে, তারা এক বিবেক-বুদ্ধিহীন সম্প্রদায়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5140,59,14,'তারা ঐক্যবদ্ধ হয়ে তোমাদের বিরুদ্ধে যুদ্ধ করতে সমর্থ নয়, সুরক্ষিত জনপদে বা দেয়ালের আড়ালে অবস্থান ছাড়া। তাদের নিজেদের মধ্যেই আছে ভীষণ শত্রুতা। তুমি তাদেরকে ঐক্যবদ্ধ মনে কর কিন্তু তাদের অন্তরগুলো ভিন্ন ভিন্ন। এর কারণ এই যে, তারা এক নির্বোধ সম্প্রদায়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5141,59,15,'এরা তাদের (অর্থাৎ ইয়াহূদী বানু কাইনুকার) মত যারা এদের পূর্বে নিকটবর্তী সময়েই তাদের কৃতকর্মের কুফল আস্বাদন করেছে। তাদের জন্য আছে মর্মান্তিক শাস্তি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5142,59,16,'(তাদের মিত্ররা তাদেরকে প্রতারিত করেছে) শয়ত্বানের মত। যখন মানুষকে সে বলে- ‘কুফুরী কর’। অতঃপর মানুষ যখন কুফুরী করে তখন শয়ত্বান বলে- ‘তোমার সাথে আমার কোন সম্পর্ক নেই, আমি বিশ্বজগতের প্রতিপালক আল্লাহকে ভয় করি।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5143,59,17,'কাজেই তাদের উভয়ের পরিণাম হবে এই যে, তারা চিরকাল জাহান্নামে থাকবে, আর যালিমদের এটাই প্রতিফল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5144,59,18,'হে মু’মিনগণ! তোমরা আল্লাহকে ভয় কর। প্রত্যেকেই চিন্তা করে দেখুক, আগামীকালের জন্য সে কী (পুণ্য কাজ) অগ্রিম পাঠিয়েছে। আর তোমরা আল্লাহকে ভয় কর, তোমরা যা কর আল্লাহ সে সম্পর্কে পুরোপুরি খবর রাখেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5145,59,19,'তোমরা তাদের মত হয়ো না যারা আল্লাহকে ভুলে গেছে, ফলে আল্লাহও তাদেরকে করেছেন আত্মভোলা। এরা পাপাচারী লোক। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5146,59,20,'জাহান্নামের অধিবাসী আর জান্নাতের অধিবাসী সমান হতে পারে না, জান্নাতের অধিবাসীরাই সফল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5147,59,21,'আমি যদি এ কুরআনকে পাহাড়ের উপর অবতীর্ণ করতাম, তাহলে তুমি আল্লাহর ভয়ে তাকে বিনীত ও বিদীর্ণ দেখতে। এ সব উদাহরণ আমি মানুষের জন্য বর্ণনা করি যাতে তারা (নিজেদের ব্যাপারে) চিন্তা-ভাবনা করে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5148,59,22,'তিনিই আল্লাহ, যিনি ছাড়া সত্যিকারের কোন ইলাহ নেই, অদৃশ্য ও দৃশের জ্ঞানের অধিকারী, পরম দয়াময়, পরম দয়ালু। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5149,59,23,'তিনিই আল্লাহ যিনি ছাড়া সত্যিকারের কোন ইলাহ নেই, তিনিই বাদশাহ, অতি পবিত্র, পূর্ণ শান্তিময়, নিরাপত্তা দানকারী, প্রতাপশালী, পর্যবেক্ষক, মহা পরাক্রমশালী, অপ্রতিরোধ্য, প্রকৃত গর্বের অধিকারী। তারা যাকে (তাঁর) শরীক করে তাত্থেকে তিনি পবিত্র, মহান। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5150,59,24,'তিনিই আল্লাহ সৃষ্টিকারী, উদ্ভাবনকারী, আকার আকৃতি প্রদানকারী। সমস্ত উত্তম নামের অধিকারী। আসমান ও যমীনে যা আছে সবই তাঁর গৌরব ও মহিমা ঘোষণা করে। তিনি প্রবল পরাক্রান্ত মহা প্রজ্ঞাবান। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5151,60,1,'হে ঈমানদারগণ! তোমরা আমার ও তোমাদের শত্রুদেরকে বন্ধুরূপে গ্রহণ করো না, তোমরা তাদের কাছে বন্ধুত্বের খবর পাঠাও, অথচ যে সত্য তোমাদের কাছে এসেছে তারা তা প্রত্যাখ্যান করেছে। তারা রসূলকে আর তোমাদেরকে শুধু এ কারণে বের করে দিয়েছে যে, তোমরা তোমাদের প্রতিপালক আল্লাহ্য় বিশ্বাস কর। তোমরা যদি আমার সন্তুষ্টি কামনায় আমার পথে জিহাদে বের হয়ে থাক, তাহলে তোমরা কেন গোপনে তাদের সাথে বন্ধুত্ব করছ? তোমরা যা গোপন কর আর তোমরা যা প্রকাশ কর, তা আমি খুব ভাল করেই জানি। তোমাদের মধ্যে যে তা করে সে সরল পথ থেকে ভ্রষ্ট হয়ে গেছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5152,60,2,'তারা তোমাদেরকে জব্দ করতে পারলেই শত্রুর আচরণ করবে, আর তোমাদের অনিষ্ট করার জন্য তারা তাদের হাত ও মুখের ভাষা সম্প্রসারিত করবে আর তারা চাইবে যে, তোমরাও যেন কুফুরী কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5153,60,3,'ক্বিয়ামতের দিন তোমাদের আত্মীয়-স্বজন ও সন্তানাদি তোমাদের কোনই উপকারে আসবে না। (নিজ নিজ ‘আমালের ভিত্তিতে) আল্লাহ তোমাদের মধ্যে ফয়সালা করে দিবেন; তোমরা যা কর আল্লাহ তা দেখেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5154,60,4,'ইবরাহীম ও তার সঙ্গী-সাথীদের মধ্যে তোমাদের জন্য আছে উত্তম আদর্শ। যখন তারা তাদের সম্প্রদায়কে বলেছিল- ‘তোমাদের সঙ্গে আর আল্লাহকে বাদ দিয়ে তোমরা যাদের ‘ইবাদাত কর তাদের সঙ্গে আমাদের কোন সম্পর্ক নেই। আমরা তোমাদেরকে প্রত্যাখ্যান করছি। আমাদের আর তোমাদের মাঝে চিরকালের জন্য শত্রুতা ও বিদ্বেষ শুরু হয়ে গেছে যতক্ষণ তোমরা এক আল্লাহর প্রতি ঈমান না আনবে। তবে ইবরাহীম যে তার পিতাকে বলেছিল- ‘আমি অবশ্য অবশ্যই তোমার জন্য ক্ষমা প্রার্থনা করব আর আমি তোমার জন্য আল্লাহর কাছ থেকে কোন কিছু করারই অধিকার রাখি না’’- এটা একটা ব্যতিক্রম। (আর ইবরাহীম ও তার সঙ্গী সাথীরা প্রার্থনা করেছিল) ‘হে আমাদের প্রতিপালক! আমরা তোমারই উপর নির্ভর করছি, তোমারই অভিমুখী হচ্ছি, আর (সব কিছুর) প্রত্যাবর্তন তো তোমারই পানে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5155,60,5,'হে আমাদের প্রতিপালক! আমাদেরকে কাফিরদের উৎপীড়নের পাত্র করো না, হে আমাদের প্রতিপালক! তুমি আমাদেরকে ক্ষমা কর, তুমি মহা পরাক্রান্ত, মহা বিজ্ঞানী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5156,60,6,'তোমরা যারা আল্লাহ (’র রহমত) ও শেষ দিবসের (সাফল্যের) প্রত্যাশী তাদের জন্য উত্তম আদর্শ রয়েছে তাদের [অর্থাৎ ইবরাহীম (আঃ) তাঁর অনুসারীদের] মধ্যে। আর কেউ মুখ ফিরিয়ে নিলে (সে জেনে রাখুক) আল্লাহ অমুখাপেক্ষী, প্রশংসিত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5157,60,7,'সম্ভবত আল্লাহ তোমাদের মধ্যে আর তাদের মধ্যেকার যাদেরকে তোমরা শত্রু বানিয়ে নিয়েছ তাদের মধ্যে বন্ধুত্ব সৃষ্টি করে দিবেন (তাদের মুসলিম হয়ে যাওয়ার মাধ্যমে)। আল্লাহ বড়ই শক্তিমান, আল্লাহ অতি ক্ষমাশীল, অতীব দয়ালু। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5158,60,8,'দীনের ব্যাপারে যারা তোমাদের সাথে যুদ্ধ করেনি, আর তোমাদেরকে তোমাদের ঘর-বাড়ী থেকে বের ক’রে দেয়নি তাদের সঙ্গে সদয় ব্যবহার করতে আর ন্যায়নিষ্ঠ আচরণ করতে আল্লাহ নিষেধ করেন নি। আল্লাহ ন্যায়পরায়ণদেরকে ভালবাসেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5159,60,9,'আল্লাহ তোমাদেরকে কেবল তাদের সঙ্গে বন্ধুত্ব করতে নিষেধ করেছেন যারা তোমাদের সঙ্গে দীনের ব্যাপারে যুদ্ধ করেছে, তোমাদেরকে তোমাদের ঘর-বাড়ী থেকে বের করে দিয়েছে আর তোমাদেরকে বের করে দেয়ার ব্যাপারে সাহায্য করেছে। যারা তাদের সঙ্গে বন্ধুত্ব করে তারাই যালিম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5160,60,10,'হে মু’মিনগণ! ঈমানদার নারীরা যখন তোমাদের কাছে হিজরাত করে আসে তখন তাদেরকে পরখ করে দেখ (তারা সত্যিই ঈমান এনেছে কি না)। তাদের ঈমান সম্বন্ধে আল্লাহ খুব ভালভাবেই জানেন। অতঃপর তোমরা যদি জানতে পার যে, তারা মু’মিনা, তাহলে তাদেরকে কাফিরদের কাছে ফেরত পাঠিও না। মু’মিনা নারীরা কাফিরদের জন্য হালাল নয়, আর কাফিররাও মু’মিনা নারীদের জন্য হালাল নয়। কাফির স্বামীরা (মোহর স্বরূপ) যা তাদের জন্য খরচ করেছিল তা কাফিরদেরকে ফেরত দিয়ে দাও। অতঃপর তোমরা তাদেরকে মোহর প্রদান করতঃ বিয়ে করলে তাতে তোমাদের কোন অপরাধ হবে না। তোমরা কাফির নারীদেরকে (বিবাহের) বন্ধনে আটকে রেখ না। তোমরা (তাদের মোহর স্বরূপ) যা ব্যয় করেছ তা ফেরত চেয়ে নাও, আর কাফিররাও ফেরত চেয়ে নিবে যা তারা ব্যয় করেছে (তাদের মু’মিনা স্ত্রীদের মোহর স্বরূপ)। এটা আল্লাহর নির্দেশ। তিনি তোমাদের মাঝে ফয়সালা করে দেন। আল্লাহ সর্ব জ্ঞানের অধিকারী, মহা বিজ্ঞানী।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5161,60,11,'তোমাদের স্ত্রীদের মধ্যে কেউ যদি (কাফির হওয়ার কারণে তোমাদের) হাতছাড়া হয়ে কাফিরদের নিকট থেকে যায় এবং (তাদের কোন স্ত্রীলোক তোমাদের কাছে চলে আসার ফলে) তোমাদের যদি সুযোগ আসে, তখন (তোমাদের মধ্যে) যাদের স্ত্রীগণ হাতছাড়া হয়ে গেছে তাদেরকে তারা (তাদের স্ত্রীদের মোহর স্বরূপ) যা ব্যয় করেছে তার সমপরিমাণ অর্থ প্রদান করবে। তোমরা আল্লাহকে ভয় কর যার প্রতি তোমরা ঈমান এনেছ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5162,60,12,'হে নবী! যখন মু’মিনা নারীরা তোমার কাছে এসে বাই‘আত করে যে, তারা আল্লাহর সঙ্গে কোন কিছুকে শরীক করবে না, চুরি করবে না, যিনা করবে না, নিজেদের সন্তান হত্যা করবে না, জেনে শুনে কোন অপবাদ রচনা ক’রে রটাবে না এবং কোন ভাল কাজে তোমার অবাধ্যতা করবে না- তাহলে তুমি তাদের বাই‘আত (অর্থাৎ তোমার প্রতি আনুগত্যের শপথ) গ্রহণ কর এবং তাদের জন্য আল্লাহর নিকট ক্ষমা প্রার্থনা কর; আল্লাহ অতি ক্ষমাশীল, বড়ই দয়ালু।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5163,60,13,'হে মু’মিনগণ! তোমরা এমন সম্প্রদায়ের সঙ্গে বন্ধুত্ব করো না আল্লাহ যাদের প্রতি রাগান্বিত। তারা পরকাল সম্পর্কে তেমনি নিরাশ যেমন কবরবাসী কাফিররা নিরাশ (কারণ তারা পরকালকে অবিশ্বাস করার কারণে তার জন্য কোন প্রস্তুতি গ্রহণ করেনি।)');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5164,61,1,'আসমানে যা কিছু আছে আর যমীনে যা কিছু আছে (সবই) আল্লাহর প্রশংসা ও মহিমা ঘোষণা করে; তিনি মহাপরাক্রান্ত মহাবিজ্ঞানী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5165,61,2,'হে মু’মিনগণ! তোমরা এমন কথা কেন বল যা তোমরা কর না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5166,61,3,'আল্লাহর দৃষ্টিতে এটা অত্যন্ত নিন্দনীয় ব্যাপার যে, তোমরা বলবে এমন কথা যা তোমরা কর না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5167,61,4,'আল্লাহ তাদেরকে ভালবাসেন যারা তাঁর পথে সারিবদ্ধ হয়ে যুদ্ধ করে- যেন তারা সীসা-গলানো প্রাচীর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5168,61,5,'স্মরণ কর, যখন মূসা তার সম্প্রদায়কে বলেছিল, ‘হে আমার সম্প্রদায়! তোমরা আমাকে কেন কষ্ট দিচ্ছ, তোমরা তো জানই যে, আমি তোমাদের নিকট আল্লাহর রসূল!’ অতঃপর তারা যখন বাঁকা পথ ধরল, আল্লাহ তাদের হৃদয়কে বাঁকা করে দিলেন। আল্লাহ পাপাচারীদেরকে সঠিক পথে পরিচালিত করেন না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5169,61,6,'স্মরণ কর, যখন মারইয়ামের পুত্র ‘ঈসা বলেছিল, ‘হে বানী ইসরাঈল! আমি তোমাদের প্রতি আল্লাহর রসূল, আমার পূর্ববর্তী তাওরাতের আমি সত্যায়নকারী এবং আমি একজন রসূলের সুসংবাদদাতা যিনি আমার পরে আসবেন, যার নাম আহমাদ।’ অতঃপর সে [অর্থাৎ ‘ঈসা (আঃ) যাঁর সম্পর্কে সুসংবাদ দিয়ে ছিলেন সেই নবী] যখন তাদের কাছে সুস্পষ্ট নিদর্শন নিয়ে আসল, তখন তারা বলল, ‘এটা তো স্পষ্ট যাদু।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5170,61,7,'তার চেয়ে অধিক যালিম আর কে যে আল্লাহ সম্বন্ধে মিথ্যে রচনা করে- অথচ তাকে ইসলামের পথ অবলম্বন করার জন্য আহবান জানানো হয়। (এ রকম) যালিম সম্প্রদায়কে আল্লাহ সঠিক পথে চালিত করেন না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5171,61,8,'তারা তাদের মুখের ফুঁৎকারে আল্লাহর নূরকে নিভিয়ে দিতে চায়, কিন্তু আল্লাহ তাঁর নূরকে পরিপূর্ণ করবেনই যদিও কাফিররা (তা) অপছন্দ করে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5172,61,9,'তিনিই তাঁর রসূলকে হিদায়াত ও সত্য দীনসহ পাঠিয়েছেন তাকে সকল দীনের উপর বিজয়ী করার জন্য- যদিও মুশরিকরা (তা) অপছন্দ করে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5173,61,10,'হে মু’মিনগণ! আমি কি তোমাদেরকে এমন এক ব্যবসায়ের সন্ধান দেব যা তোমাদেরকে মর্মান্তিক ‘আযাব থেকে রক্ষা করবে? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5174,61,11,'(তা এই যে) তোমরা আল্লাহ ও তাঁর রসূলের প্রতি ঈমান আনো আর তোমরা তোমাদের মাল ও জান দিয়ে আল্লাহর পথে জিহাদ কর; এটাই তোমাদের জন্য অতি উত্তম, যদি তোমরা জানতে! ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5175,61,12,'(তোমরা যদি আল্লাহর সন্ধান দেয়া ব্যবসা কর তাহলে) তিনি তোমাদের পাপ ক্ষমা করে দেবেন আর তোমাদেরকে দাখিল করবেন জান্নাতে যার তলদেশে ঝর্ণাধারা প্রবাহিত। আর চিরস্থায়ী আবাসস্থল জান্নাতে অতি উত্তম ঘর তোমাদেরকে দান করবেন। এটাই বিরাট সাফল্য। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5176,61,13,'আর অন্য আরেকটিও (তিনি তোমাদেরকে দিবেন) যা তোমরা পছন্দ কর (আর তা হল) আল্লাহর সাহায্য ও আসন্ন বিজয়। (হে নবী!) ঈমানদার লোকেদেরকে তুমি সুসংবাদ দাও। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5177,61,14,'হে মু’মিনগণ! তোমরা আল্লাহর সাহায্যকারী হও, যেমন মারইয়ামের পুত্র ‘ঈসা হাওয়ারীদেরকে বলেছিল- ‘আল্লাহর পথে কে আমার সাহায্যকারী হবে?’ হাওয়ারীরা উত্তরে বলেছিল- ‘আমরাই আল্লাহর সাহায্যকারী (হব)।’ অতঃপর বানী ইসরাঈলের একদল ঈমান আনল, আরেক দল প্রত্যাখ্যান করল। তখন যারা ঈমান আনল তাদেরকে আমি তাদের শত্রুদের উপর শক্তিশালী করলাম। ফলে তারা বিজয়ী হল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5178,62,1,'যা কিছু আসমানে আছে আর যা কিছু যমীনে আছে সব কিছুই প্রশংসা ও মহিমা ঘোষণা করে আল্লাহর যিনি সর্বময় ক্ষমতার অধিকারী, অতি পবিত্র, মহাপরাক্রান্ত, মহাবিজ্ঞানী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5179,62,2,'তিনিই নিরক্ষরদের মাঝে পাঠিয়েছেন তাঁর রসূলকে তাদেরই মধ্য হতে, যে তাদের কাছে আল্লাহর আয়াত পাঠ করে, তাদেরকে পবিত্র করে, আর তাদেরকে কিতাব ও হিকমত শিক্ষা দেয় অথচ ইতোপূর্বে তারা ছিল স্পষ্ট গুমরাহীতে নিমজ্জিত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5180,62,3,'আর (এই রসূলকে পাঠানো হয়েছে) তাদের অন্যান্যদের জন্যও যারা এখনও তাদের সাথে মিলিত হয়নি (কিন্তু তারা ভবিষ্যতে আসবে)। আল্লাহ মহাপরাক্রান্ত, মহাবিজ্ঞানী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5181,62,4,'ওটা আল্লাহর অনুগ্রহ যা তিনি যাকে ইচ্ছে দান করেন; আল্লাহ মহা অনুগ্রহের অধিকারী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5182,62,5,'যাদের উপর তাওরাতের দায়িত্বভার দেয়া হয়েছিল, অতঃপর তা তারা বহন করেনি (অর্থাৎ তারা তাদের উপর ন্যস্ত দায়িত্ব পালন করেনি) তাদের দৃষ্টান্ত হল গাধার মত, যে বহু কিতাবের বোঝা বহন করে (কিন্তু তা বুঝে না)। যে সম্প্রদায় আল্লাহর আয়াতগুলোকে অস্বীকার করে, তাদের দৃষ্টান্ত কতইনা নিকৃষ্ট! যালিম সম্প্রদায়কে আল্লাহ সঠিক পথে পরিচালিত করেন না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5183,62,6,'বল- ‘হে ইয়াহূদী হয়ে যাওয়া লোকেরা! তোমরা যদি মনে কর যে, অন্য সব লোককে বাদ দিয়ে কেবল তোমরাই আল্লাহর বন্ধু, তাহলে তোমরা মৃত্যু কামনা কর, যদি তোমরা সত্যবাদী হয়ে থাকো। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5184,62,7,'কিন্তু তাদের হাত যে সব (কৃতকর্ম) আগে পাঠিয়েছে, সে কারণে তারা কক্ষনো মৃত্যুর কামনা করবে না। আর আল্লাহ যালিমদেরকে খুব ভাল করেই জানেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5185,62,8,'বল- ‘তোমরা যে মৃত্যু থেকে পালাচ্ছ তা অবশ্যই তোমাদের সাথে সাক্ষাৎ করবে। অতঃপর তোমাদেরকে দৃশ্য ও অদৃশ্যের জ্ঞানের অধিকারী (আল্লাহ)’র নিকট ফিরিয়ে নেয়া হবে; অতঃপর তোমাদেরকে জানিয়ে দেয়া হবে যা তোমরা করতে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5186,62,9,'হে মু’মিনগণ! জুমু‘আহর দিনে যখন নামাযের জন্য ডাকা হয়, তখন আল্লাহর স্মরণের দিকে শীঘ্র ধাবিত হও, ক্রয়-বিক্রয় পরিত্যাগ কর, এটাই তোমাদের জন্য অতি উত্তম, যদি তোমরা জানতে! ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5187,62,10,'অতঃপর যখন নামায সমাপ্ত হয়, তখন যমীনে ছড়িয়ে পড়, আর আল্লাহর অনুগ্রহ সন্ধান কর এবং আল্লাহকে বেশি বেশি স্মরণ করতে থাক- যাতে তোমরা সাফল্য লাভ করতে পার। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5188,62,11,'তারা যখন ব্যবসায় অথবা ক্রীড়া কৌতুক দেখে তখন তারা সেদিকে ছুটে যায় আর তোমাকে রেখে যায় দাঁড়ানো অবস্থায়। বল- ‘আল্লাহর কাছে যা আছে তা ক্রীড়া-কৌতুক ও ব্যবসার চেয়ে উত্তম।’ আর আল্লাহ সর্বাপেক্ষা উত্তম রিযকদাতা। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5189,63,1,'মুনাফিকরা যখন তোমার কাছে আসে তখন তারা বলে- ‘আমরা সাক্ষ্য দিচ্ছি যে, আপনি অবশ্যই আল্লাহর রসূল।’ আল্লাহ জানেন, অবশ্যই তুমি তাঁর রসূল আর আল্লাহ সাক্ষ্য দিচ্ছেন যে, মুনাফিকরা অবশ্যই মিথ্যেবাদী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5190,63,2,'তারা তাদের শপথগুলোকে ঢাল হিসেবে ব্যবহার করে আর এ উপায়ে তারা মানুষকে আল্লাহর পথ থেকে নিবৃত্ত করে। তারা যা করে তা কতই না মন্দ! ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5191,63,3,'তার কারণ এই যে, তারা ঈমান আনে, অতঃপর কুফুরী করে। এজন্য তাদের অন্তরে মোহর লাগিয়ে দেয়া হয়েছে। যার ফলে তারা কিছুই বুঝে না ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5192,63,4,'তুমি যখন তাদের দিকে তাকাও তখন তাদের শারীরিক গঠন তোমাকে চমৎকৃত করে। আর যখন তারা কথা বলে তখন তুমি তাদের কথা আগ্রহ ভরে শুন, অথচ তারা দেয়ালে ঠেস দেয়া কাঠের মত (দেখন- সুরত, কিন্ত কার্যক্ষেত্রে কিছুই না)। কোন শোরগোল হলেই তারা সেটাকে নিজেদের বিরুদ্ধে মনে করে (কারণ তাদের অপরাধী মন সব সময়ে শঙ্কিত থাকে- এই বুঝি তাদের কুকীর্তি ফাঁস হয়ে গেল)। এরাই শত্রু, কাজেই তাদের ব্যাপারে সতর্ক থাক। এদের উপর আছে আল্লাহর গযব,  তাদেরকে  কীভাবে  (সত্য পথ  থেকে) ফিরিয়ে নেয়া হচ্ছে! ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5193,63,5,'তাদেরকে যখন বলা হয়, ‘এসো, আল্লাহর রসূল তোমাদের জন্য ক্ষমা প্রার্থনা করবেন, তখন তারা মাথা নেড়ে অস্বীকৃতি জানায়, তখন তুমি দেখতে পাও তারা সদম্ভে তাদের মুখ ফিরিয়ে নেয়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5194,63,6,'তুমি তাদের জন্য ক্ষমা প্রার্থনা কর আর না কর, উভয়ই তাদের জন্য সমান। আল্লাহ তাদেরকে কক্ষনো ক্ষমা করবেন না। আল্লাহ পাপাচারী জাতিকে কক্ষনো সঠিক পথে পরিচালিত করেন না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5195,63,7,'তারা বলে- ‘রসূলের সঙ্গী সাথীদের জন্য অর্থ ব্যয় করো না, শেষে তারা এমনিতেই সরে পড়বে।’ আসমান ও যমীনের ধন ভান্ডার তো আল্লাহরই, কিন্তু মুনাফিকরা তা বুঝে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5196,63,8,'তারা বলে- ‘আমরা যদি মাদীনায় প্রত্যাবর্তন করি, তাহলে সম্মানীরা অবশ্য অবশ্যই হীনদেরকে সেখানে থেকে বহিষ্কার করবে।’ কিন্তু সমস্ত মান মর্যাদা তো আল্লাহর, তাঁর রসূলের এবং মু’মিনদের; কিন্তু মুনাফিকরা তা জানে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5197,63,9,'হে মু’মিনগণ! তোমাদের ধন-সম্পদ আর তোমাদের সন্তানাদি তোমাদেরকে যেন আল্লাহর স্মরণ হতে গাফিল করে না দেয়। যারা এমন করবে তারাই ক্ষতিগ্রস্ত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5198,63,10,'যে রিযক আমি তোমাদেরকে দিয়েছি তাত্থেকে (আল্লাহর পথে) ব্যয় কর তোমাদের কারো মৃত্যু আসার পূর্বে। নচেৎ (মৃত্যু এসে গেলে) সে বলবে, ‘হে আমার প্রতিপালক! তুমি আমাকে আরো কিছুকালের অবকাশ দিলে না কেন? তাহলে আমি সদাক্বাহ করতাম আর সৎকর্মশীলদের মধ্যে শামিল হয়ে যেতাম।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5199,63,11,'আল্লাহ কাউকে কক্ষনো অবকাশ দেন না যখন তার নির্ধারিত সময় এসে যায়। তোমরা যা কর সে সম্পর্কে আল্লাহ পুরোপুরি খবর রাখেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5200,64,1,'যা কিছু আসমানে আছে আর যা কিছু যমীনে আছে সবই আল্লাহর প্রশংসা ও মহিমা ঘোষণা করছে। রাজত্ব তাঁরই, প্রশংসা তাঁরই, আর তিনি সব কিছুর উপর ক্ষমতাবান। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5201,64,2,'তিনিই তোমাদেরকে সৃষ্টি করেছেন, অতঃপর তোমাদের মধ্যে কেউ কাফির, কেউ মু’মিন; তোমরা যা কর আল্লাহ তা দেখেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5202,64,3,'তিনি (বিশেষ উদ্দেশে) সত্যিকারভাবে আসমান ও যমীন সৃষ্টি করেছেন, তিনি তোমাদেরকে আকৃতি দিয়েছেন, অতঃপর তোমাদের আকৃতি সুন্দর করেছেন আর (সববাইকে) ফিরে যেতে হবে তাঁরই দিকে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5203,64,4,'তিনি জানেন যা কিছু আসমান ও যমীনে আছে, আর তিনি জানেন যা তোমরা গোপন কর আর প্রকাশ কর। অন্তরের বিষয়াদি সম্পর্কে তিনি পূর্ণরূপে অবগত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5204,64,5,'ইতোপূর্বে যারা কুফরী করেছিল তাদের খবর কি তোমার কাছে পৌঁছেছে? তারা তাদের মন্দ কর্মের ফল আস্বাদন করেছে, আর তাদের জন্য আছে মর্মান্তিক ‘আযাব। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5205,64,6,'এর কারণ এই যে, তাদের কাছে তাদের রসূলগণ স্পষ্ট নিদর্শনসহ এসেছিল, তখন তারা বলেছিল, ‘(আমাদের মতই) মানুষ কি আমাদেরকে সঠিক পথ দেখাবে?’ কাজেই তারা অস্বীকার করল আর মুখ ফিরিয়ে নিল। তখন আল্লাহও তাদের ব্যাপারে বেপরোয়া হয়ে গেলেন, আল্লাহ অমুখাপেক্ষী, প্রশংসিত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5206,64,7,'কাফিররা ধারণা করে যে, তাদেরকে কক্ষনো আবার জীবিত করে উঠানো হবে না। বল, নিশ্চয়ই (উঠানো) হবে, আমার প্রতিপালকের শপথ! তোমাদেরকে অবশ্য অবশ্যই আবার জীবিত করে উঠানো হবে, অতঃপর তোমাদেরকে অবশ্য অবশ্যই জানিয়ে দেয়া হবে তোমরা (দুনিয়ায়) কী কাজ করেছ। এ কাজ (করা) আল্লাহর জন্য খুবই সহজ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5207,64,8,'কাজেই (এ অবস্থার কথা চিন্তা ক’রে) তোমরা ঈমান আনো আল্লাহর প্রতি ও তাঁর রসূলের প্রতি আর সেই নূর (কুরআন)-এর প্রতি যা আমি অবতীর্ণ করেছি। তোমরা যা কর সে ব্যাপারে আল্লাহ পুরোপুরি অবগত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5208,64,9,'যখন একত্র করার দিন তিনি তোমাদেরকে একত্রিত করবেন, সে দিনটি হবে তোমাদের হার জিতের দিন। যারা আল্লাহর প্রতি ঈমান আনবে আর সৎ কাজ করবে, আল্লাহ তার পাপ মোচন করে দিবেন, আর তাকে জান্নাতে দাখিল করবেন যার নীচ দিয়ে নির্ঝরিণী প্রবাহিত। সেখানে তারা থাকবে চিরকাল সর্বকাল। এটাই মহা সাফল্য। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5209,64,10,'আর যারা কুফুরী করে আর আমার নিদর্শনগুলোকে অস্বীকার করে, তারাই জাহান্নামের অধিবাসী, তাতে তারা চিরকাল থাকবে। কতই না নিকৃষ্ট প্রত্যাবর্তনস্থল! ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5210,64,11,'আল্লাহর অনুমতি ছাড়া কোন বিপদ আসে না। যে ব্যক্তি আল্লাহর প্রতি ঈমান আনে, আল্লাহ তার অন্তরকে সঠিক পথে পরিচালিত করেন। আল্লাহ সকল বিষয়ের সর্বশ্রেষ্ঠ জ্ঞানী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5211,64,12,'তোমরা আল্লাহর আনুগত্য কর ও রসূলের আনুগত্য কর। অতঃপর যদি তোমরা মুখ ফিরিয়ে লও (তাহলে তোমাদেরকে সঠিক পথে ফিরিয়ে আনার জন্য জোর জবরদস্তি করা হবে না) কেননা, আমার রসূলের দায়িত্ব কেবল (আমার বাণী) স্পষ্টভাবে পৌঁছে দেয়া। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5212,64,13,'আল্লাহ তিনি ছাড়া সত্যিকারের কোন ইলাহ নেই। কাজেই মু’মিনরা আল্লাহরই উপর নির্ভর করুক। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5213,64,14,'হে মু’মিনগণ! তোমাদের স্ত্রী আর সন্তানদের মধ্যে কতক তোমাদের শত্রু। কাজেই তোমরা তাদের হতে সতর্ক হও। তোমরা যদি তাদের প্রতি ক্ষমাসুলভ আচরণ কর, তাদের দোষ-ত্রুটি উপেক্ষা কর, আর তাদেরকে ক্ষমা কর, তাহলে (তোমাদের সে কাজ হবে আল্লাহর নিকট পছন্দীয় কারণ) আল্লাহ অতি ক্ষমাশীল, বড়ই দয়ালু। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5214,64,15,'তোমাদের ধন-সম্পদ আর সন্তানাদি পরীক্ষা (’র বস্তু) মাত্র (যারা এ দু’টিকে সঠিকভাবে ব্যবহার ক’রে আল্লাহর পথে অটল থাকবে তারা কৃতকার্য হবে, আর যারা এ দু’টিকে আল্লাহর চেয়ে অধিক ভালবাসবে তারা ব্যর্থ হয়ে যাবে)। আর আল্লাহ এমন যাঁর কাছে আছে মহা পুরস্কার। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5215,64,16,'কাজেই তোমরা আল্লাহকে তোমাদের সাধ্যমত ভয় কর, তোমরা (তাঁর বাণী) শুন, তোমরা (তাঁর) আনুগত্য কর এবং (তাঁর পথে) ব্যয় কর, এটা তোমাদের নিজেদেরই জন্য কল্যাণকর। যারা অন্তরের সংকীর্ণতা থেকে রক্ষা পেল, তারাই সফলকাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5216,64,17,'তোমরা যদি আল্লাহকে উত্তম ঋণ দাও, তবে তিনি তা তোমাদের জন্য দ্বিগুণ করে দেবেন, আর তোমাদেরকে ক্ষমা করবেন, আল্লাহ (কারো কাজের) অতি মর্যাদাদানকারী, সহনশীল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5217,64,18,'তিনি অদৃশ্য ও দৃশ্যের জ্ঞানের অধিকারী, মহাপরাক্রান্ত, মহাবিজ্ঞানী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5218,65,1,'হে নবী! তোমরা যখন স্ত্রীদেরকে তালাক দিতে চাও তখন তাদেরকে তালাক দাও তাদের ‘ইদ্দাতের প্রতি লক্ষ্য রেখে, আর ‘ইদ্দাতের হিসাব সঠিকভাবে গণনা করবে, (তালাক দেয়া ও ‘ইদ্দাত পালন সংক্রান্ত শারী‘আতের বিধি-বিধান পালনে) তোমরা তোমাদের প্রতিপালক আল্লাহকে ভয় কর। তাদেরকে তাদের বাসগৃহ থেকে বের করে দিও না, আর তারা নিজেরাও যেন বের হয়ে না যায়, যদি না তারা স্পষ্ট অশ্লীলতায় লিপ্ত হয়। এগুলো আল্লাহর সীমারেখা। যে কেউ আল্লাহর সীমারেখা লঙ্ঘন করে, সে নিজের উপরই যুলম করে। তোমরা জান না, আল্লাহ হয়তো এরপরও (স্বামী-স্ত্রীর মধ্যে সমঝোতার) কোন উপায় বের করে দিবেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5219,65,2,'অতঃপর যখন তাদের (‘ইদ্দাতের) সময়কাল এসে যায়, তখন তাদেরকে ভালভাবে (স্ত্রী হিসেবে) রেখে দাও, অথবা ভালভাবে তাদেরকে বিচ্ছিন্ন করে দাও। আর তোমাদের মধ্যেকার দু’জন ন্যায়পরায়ণ লোককে সাক্ষী রাখ। তোমরা আল্লাহর জন্য সঠিকভাবে সাক্ষ্য দাও। এর দ্বারা তোমাদেরকে উপদেশ দেয়া হচ্ছে যারা আল্লাহ ও আখিরাত দিবসের প্রতি ঈমান রাখে। যে কেউ আল্লাহকে ভয় করে, আল্লাহ তার জন্য (সমস্যা থেকে উদ্ধার পাওয়ার) কোন না কোন পথ বের করে দিবেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5220,65,3,'আর তাকে রিযক দিবেন (এমন উৎস) থেকে যা সে ধারণাও করতে পারে না। যে কেউ আল্লাহর উপর ভরসা করে, তবে তার জন্য তিনিই যথেষ্ট। আল্লাহ নিজের কাজ সম্পূর্ণ করবেনই। আল্লাহ প্রতিটি জিনিসের জন্য করেছেন একটা সুনির্দিষ্ট মাত্রা। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5221,65,4,'তোমাদের যে সব স্ত্রীগণ মাসিক ঋতু আসার বয়স অতিক্রম করেছে তাদের (‘ইদ্দাতের) ব্যাপারে যদি তোমাদের সন্দেহ সৃষ্টি হয়, সেক্ষেত্রে তাদের ‘ইদ্দাতকাল তিন মাস, আর যারা (অল্প বয়স্কা হওয়ার কারণে) এখনও ঋতুবতী হয়নি (এ নিয়ম) তাদের জন্যও। আর গর্ভবতী স্ত্রীদের ‘ইদ্দাতকাল তাদের সন্তান প্রসব পর্যন্ত। যে আল্লাহকে ভয় করে, আল্লাহ তার কাজ সহজ করে দেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5222,65,5,'এটা আল্লাহর হুকুম যা তিনি তোমাদের উপর অবতীর্ণ করেছেন। যে কেউ আল্লাহকে ভয় করে, আল্লাহ তার পাপ মোচন করে দিবেন, আর তার প্রতিফলকে বিশাল বিস্তৃত করে দিবেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5223,65,6,'(‘ইদ্দাতকালে) নারীদেরকে সেভাবেই বসবাস করতে দাও যেভাবে তোমরা বসবাস কর তোমাদের সামর্থ্য অনুযায়ী, তাদেরকে সংকটে ফেলার জন্য তাদেরকে জ্বালাতন করো না। তারা যদি গর্ভবতী হয়ে থাকে, তবে তারা সন্তান প্রসব না করা পর্যন্ত তাদের ব্যয়ভার বহন কর। অতঃপর তারা যদি তোমাদের সন্তানকে দুধ পান করায়, তবে তাদেরকে তাদের পারিশ্রমিক দাও। (দুধ পান করানোর ব্যাপারে) ন্যায়সঙ্গতভাবে নিজেদের মধ্যে পরামর্শ করে লও। আর (দুধ পান করানোর ব্যাপার নিয়ে) তোমরা যদি একে অপরের প্রতি কড়াকড়ি করতেই থাক, তাহলে (এ অবস্থা থেকে রেহাই পাওয়ার জন্য) অপর কোন স্ত্রীলোক সন্তানকে দুধ পান করাবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5224,65,7,'সচ্ছল ব্যক্তি তার সচ্ছলতা অনুসারে ব্যয় করবে। আর যার রিযক সীমিত করা হয়েছে, সে ব্যয় করবে আল্লাহ তাকে যা দিয়েছেন তাত্থেকে। আল্লাহ যাকে যতটা দিয়েছেন তার অতিরিক্ত বোঝা তার উপর চাপান না। আল্লাহ কষ্টের পর আরাম দিবেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5225,65,8,'কত জনপদ তাদের প্রতিপালকের আর তাঁর রসূলদের হুকুম অমান্য করেছে। ফলে আমরা তাদের থেকে কঠিনভাবে প্রতিশোধ নিয়েছি আর তাদেরকে ‘আযাব দিয়েছি কঠিন ‘আযাব। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5226,65,9,'তারা তাদের কৃতকর্মের খারাপ প্রতিফল আস্বাদন করল, ধ্বংসই হল তাদের কাজের পরিণতি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5227,65,10,'আল্লাহ তাদের জন্য কঠিন শাস্তি প্রস্তুত করে রেখেছেন। অতএব হে জ্ঞানবুদ্ধিসম্পন্ন মানুষেরা! যারা ঈমান এনেছ তোমরা আল্লাহকে ভয় কর। আল্লাহ তোমাদের প্রতি অবতীর্ণ করেছেন উপদেশ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5228,65,11,'(তদুপরি তিনি পাঠিয়েছেন) একজন রসূল যে তোমাদের কাছে আল্লাহর স্পষ্ট আয়াত পাঠ করে, যারা ঈমান আনে আর সৎ কাজ করে তাদেরকে গাঢ় অন্ধকার থেকে আলোতে আনার জন্য। আর যে ব্যক্তি আল্লাহর প্রতি ঈমান আনবে আর সৎ কাজ করবে, তিনি তাকে দাখিল করবেন জান্নাতে যার নীচ দিয়ে বয়ে চলেছে নির্ঝরিণী। তাতে তারা চিরকাল সর্বকাল থাকবে। আল্লাহ তার জন্য অতি উত্তম রিযকের ব্যবস্থা করে রেখেছেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5229,65,12,'আল্লাহই সাত আসমান বানিয়েছেন আর ওগুলোর মত পৃথিবীও, সবগুলোর মাঝে (অর্থাৎ সকল আসমানে আর সকল যমীনে) নেমে আসে আল্লাহর নির্দেশ যাতে তোমরা জানতে পার যে, আল্লাহ সব কিছুর উপর ক্ষমতাবান আর আল্লাহ (স্বীয়) জ্ঞানে সব কিছুকে ঘিরে রেখেছেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5230,66,1,'হে নবী! আল্লাহ যা তোমার জন্য হালাল করেছেন তা তুমি কেন হারাম করছ? (এর দ্বারা) তুমি তোমার স্ত্রীদের সন্তুষ্টি পেতে চাও, (আল্লাহ তোমার এ ত্রুটি ক্ষমা করে দিলেন কেননা) আল্লাহ অতি ক্ষমাশীল, বড়ই দয়ালু। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5231,66,2,'আল্লাহ তোমাদের জন্য নিজেদের কসমের বাধ্যবাধকতা থেকে নিস্কৃতি পাওয়ার ব্যবস্থা করেছেন, আল্লাহ তোমাদের মালিক-মনিব-রক্ষক, আর তিনি সর্বজ্ঞাতা, মহা প্রজ্ঞার অধিকারী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5232,66,3,'স্মরণ কর- যখন নবী তার স্ত্রীদের কোন একজনকে গোপনে একটি কথা বলেছিল। অতঃপর সে স্ত্রী যখন তা (অন্য একজনকে) জানিয়ে দিল, তখন আল্লাহ এ ব্যাপারটি নবীকে জানিয়ে দিলেন। তখন নবী (তার স্ত্রীর কাছে) কিছু কথার উল্লেখ করল আর কিছু কথা ছেড়ে দিল। নবী যখন তা তার স্ত্রীকে জানাল তখন সে বলল, ‘আপনাকে এটা কে জানিয়ে দিল?’’ নবী বলল, ‘‘আমাকে জানিয়ে দিলেন যিনি সর্বজ্ঞাতা, ওয়াকিফহাল।’’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5233,66,4,'তোমরা দু’জন যদি অনুশোচনাভরে আল্লাহর দিকে ফিরে আস (তবে তা তোমাদের জন্য উত্তম), তোমাদের অন্তর (অন্যায়ের দিকে) ঝুঁকে পড়েছে, তোমরা যদি নবীর বিরুদ্ধে একে অপরকে সহযোগিতা কর, তবে (জেনে রেখ) আল্লাহ তার মালিক-মনিব-রক্ষক। আর এ ছাড়াও জিবরীল, নেককার মু’মিনগণ আর ফেরেশতাগণও তার সাহায্যকারী।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5234,66,5,'নবী যদি তোমাদের সবাইকে তালাক দিয়ে দেয় তবে সম্ভবতঃ তার প্রতিপালক তোমাদের পরিবর্তে তাকে দিবেন তোমাদের চেয়ে উত্তম স্ত্রী- যারা হবে আত্মসমপর্ণকারিণী মু’মিনা অনুগতা, তাওবাহকারিণী, ‘ইবাদাতকারিণী, রোযা পালনকারিণী, অকুমারী ও কুমারী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5235,66,6,'হে মু’মিনগণ! তোমরা তোমাদের নিজেদেরকে আর তোমাদের পরিবার-পরিজনকে জাহান্নামের আগুন থেকে রক্ষা কর যার ইন্ধন হবে মানুষ ও পাথর, যাতে মোতায়েন আছে পাষাণ হৃদয় কঠোর স্বভাব ফেরেশতা। আল্লাহ যা আদেশ করেন, তা তারা অমান্য করে না, আর তারা তাই করে, তাদেরকে যা করার জন্য আদেশ দেয়া হয়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5236,66,7,'(সেখানে বলা হবে) ওহে যারা কুফুরী করেছ! আজ ওযর পেশ করো না, তোমরা যা করতে তোমাদেরকে তারই প্রতিফল দেয়া হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5237,66,8,'ওহে যারা ঈমান এনেছ! তোমরা আল্লাহর কাছে তাওবাহ কর- আন্তরিক তাওবাহ। সম্ভবতঃ তোমাদের প্রতিপালক তোমাদের মন্দ কাজগুলোকে তোমাদের থেকে মুছে দিবেন, আর তোমাদেরকে দাখিল করবেন জান্নাতে যার তলদেশ দিয়ে বয়ে চলেছে ঝর্ণাধারা। সে দিন আল্লাহ নবীকে আর তার সঙ্গে যারা ঈমান এনেছিল তাদেরকে লজ্জিত করবেন না। (সেদিনের ভয়াবহ অন্ধকার থেকে মু’মিনদের রক্ষার ব্যবস্থা হিসেবে) তাদের নূর দৌড়াতে থাকবে তাদের সামনে আর তাদের ডান পাশে। তারা বলবে, ‘হে আমাদের প্রতিপালক! আমাদের নূরকে আমাদের জন্য পরিপূর্ণ করে দাও আর আমাদেরকে ক্ষমা কর; তুমি সব কিছুর উপর ক্ষমতাবান।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5238,66,9,'হে নবী! কাফির ও মুনাফিকদের বিরুদ্ধে জিহাদ কর, তাদের প্রতি কঠোর হও। তাদের ঠিকানা জাহান্নাম, কতই না নিকৃষ্ট সেই প্রত্যাবর্তনস্থল! ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5239,66,10,'যারা কুফুরীর নীতি অবলম্বন করে তাদের ব্যাপারে আল্লাহ নূহের স্ত্রী আর লূতের স্ত্রীর দৃষ্টান্ত পেশ করছেন। এরা ছিল আমার দু’নেককার বান্দার অধীনে। কিন্তু তারা দু’জনেই তাদের স্বামীদ্বয়ের প্রতি বিশ্বাসঘাতকতা করেছিল। ফলে নূহ ও লূত তাদেরকে আল্লাহর শাস্তি থেকে রক্ষা করতে পারল না। তাদেরকে বলা হল, ‘‘তোমরা দু’জন জাহান্নামে প্রবেশ কর (অন্যান্য) প্রবেশকারীদের সঙ্গে।’’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5240,66,11,'আর যারা ঈমান আনে তাদের ব্যাপারে আল্লাহ ফেরাউনের স্ত্রীর দৃষ্টান্ত দিচ্ছেন। সে প্রার্থনা করেছিল, ‘‘হে আমার প্রতিপালক! তুমি আমার জন্য তোমার নিকট জান্নাতে একটি ঘর বানিয়ে দাও আর আমাকে তুমি ফেরাউন ও তার (অন্যায়) কার্যকলাপ থেকে রক্ষা কর, উদ্ধার কর আমাকে যালিম সম্প্রদায় থেকে।’’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5241,66,12,'আর (দৃষ্টান্ত দিচ্ছেন) ‘ইমরান-কন্যা মারইয়ামের যে তার লজ্জাস্থান সংরক্ষণ করেছিল, ফলে আমি তার মধ্যে আমার রূহ ফুঁকে দিয়েছিলাম। সে তার প্রতিপালকের বাণী ও তাঁর কিতাবসমূহে (তাওরাত, যবূর ও ইঞ্জীলে) বিশ্বাস স্থাপন করেছিল। সে ছিল অনুগত ও বিনতদের অন্তর্ভুক্ত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5242,67,1,'অতি মহান ও শ্রেষ্ঠ তিনি, সর্বময় কর্তৃত্ব ও রাজত্ব যাঁর হাতে; তিনি সব কিছুর উপর ক্ষমতাবান। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5243,67,2,'যিনি সৃষ্টি করেছেন মরণ ও জীবন যাতে তোমাদেরকে পরীক্ষা করেন- ‘আমালের দিক দিয়ে তোমাদের মধ্যে কোন্ ব্যক্তি সর্বোত্তম? তিনি (একদিকে যেমন) মহা শক্তিধর, (আবার অন্যদিকে) অতি ক্ষমাশীল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5244,67,3,'যিনি সৃষ্টি করেছেন সাত আসমান- একটির উপর আরেকটি। তোমরা মহা দয়াময়ের সৃষ্টিকার্যে কোনরূপ অসামঞ্জস্য দেখতে পাবে না। তোমরা আবার দৃষ্টি ফিরিয়ে দেখ, কোন দোষ-ত্রুটি দেখতে পাও কি? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5245,67,4,'অতঃপর তোমরা বারবার দৃষ্টি ফিরিয়ে দেখ; ক্লান্ত, শ্রান্ত ও ব্যর্থ হয়ে দৃষ্টি তোমার দিকে ফিরে আসবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5246,67,5,'আমি নিকটবর্তী আকাশকে প্রদীপমালা দিয়ে সুসজ্জিত করেছি আর শয়ত্বানকে তাড়িয়ে দেয়ার জন্য, এবং প্রস্তুত করে রেখেছি জ্বলন্ত আগুনের শাস্তি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5247,67,6,'যারা তাদের প্রতিপালককে অস্বীকার করে তাদের জন্য আছে জাহান্নামের শাস্তি; কতই না নিকৃষ্ট সে প্রত্যাবর্তনস্থল! ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5248,67,7,'তাদেরকে যখন তাতে নিক্ষেপ করা হবে তখন তারা জাহান্নামের শ্বাস গ্রহণের (ভয়াবহ) গর্জন শুনতে পাবে আর তা হবে উদ্বেলিত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5249,67,8,'ক্রোধে আক্রোশে জাহান্নাম ফেটে পড়ার উপক্রম হবে। যখনই কোন দলকে তাতে ফেলা হবে তখন তার রক্ষীরা তাদেরকে জিজ্ঞেস করবে, ‘তোমাদের কাছে কি কোন সতর্ককারী আসেনি?’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5250,67,9,'তারা জবাব দিবে, ‘হ্যাঁ, অবশ্যই আমাদের কাছে সতর্ককারী এসেছিল, কিন্তু আমরা তাদেরকে অস্বীকার করেছিলাম আর আমরা বলেছিলাম, ‘আল্লাহ কোন কিছুই অবতীর্ণ করেননি, তোমরা তো ঘোর বিভ্রান্তিতে পড়ে আছ।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5251,67,10,'তারা আরো বলবে, ‘আমরা যদি শুনতাম অথবা বুঝতাম তাহলে আমরা জ্বলন্ত আগুনের বাসিন্দাদের মধ্যে শামিল হতাম না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5252,67,11,'তারা তাদের অপরাধ স্বীকার করবে, অতএব দূর হোক জাহান্নামের অধিবাসীরা! ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5253,67,12,'যারা তাদের প্রতিপালকে না দেখেই ভয় করে তাদের জন্য আছে ক্ষমা আর মহা পুরস্কার। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5254,67,13,'তোমরা তোমাদের কথা চুপেচাপেই বল আর উচ্চৈঃস্বরেই বল, তিনি (মানুষের) অন্তরের গোপন কথা সম্পর্কে পুরোপুরি অবগত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5255,67,14,'যিনি সৃষ্টি করেছেন তিনিই কি জানেন না? তিনি অতি সূক্ষ্মদর্শী, ওয়াকিফহাল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5256,67,15,'তিনি তোমাদের জন্য যমীনকে (তোমাদের ইচ্ছার) অধীন করে দিয়েছেন, কাজেই তোমরা তার বুকের উপর দিয়ে চলাচল কর, আর আল্লাহর দেয়া রিযক হতে আহার কর, পুনরায় জীবিত হয়ে তাঁর কাছেই যেতে হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5257,67,16,'তোমরা কি তোমাদেরকে নিরাপদ মনে করে নিয়েছ যে, যিনি আকাশে আছেন তিনি তোমাদেরকে যমীনে বিধ্বস্ত করে দিবেন না যখন তা হঠাৎ থর থর করে কাঁপতে থাকবে? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5258,67,17,'কিংবা তোমরা কি নিরাপদ হয়ে গেছ যে, যিনি আকাশে আছেন তিনি তোমাদের উপর পাথর বর্ষণকারী ঝড়ো হাওয়া পাঠাবেন না? যাতে তোমরা জানতে পারবে যে, কেমন (ভয়ানক) ছিল আমার সতর্কবাণী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5259,67,18,'তাদের আগের লোকেরাও (আমার সতর্কবাণী) প্রত্যাখ্যান করেছিল, ফলে কেমন (কঠোর) হয়েছিল আমার শাস্তি! ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5260,67,19,'তারা কি তাদের উপর দিকে পাখীগুলোর প্রতি লক্ষ্য করে না যারা ডানা মেলে দেয় আবার গুটিয়ে নেয়? দয়াময় ছাড়া অন্য কেউই তাদেরকে (উপরে) ধরে রাখে না। তিনি সবকিছুর সম্যক দ্রষ্টা। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5261,67,20,'দয়াময় ছাড়া কে তোমাদেরকে সাহায্য করবে তোমাদের সেনাবাহিনী হয়ে? কাফিররা তো কেবল ধোঁকার মধ্যে পড়ে আছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5262,67,21,'অথবা এমন কে আছে যে তোমাদেরকে রিযক দিবে যদি তিনি তাঁর রিযক বন্ধ করে দেন? আসলে তারা অহমিকা ও অনীহায় ডুবে আছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5263,67,22,'যে লোক উপুড় হয়ে মুখের ভরে চলে সেই কি অধিক সৎপথপ্রাপ্ত, না সেই লোক যে সোজা হয়ে সরল সঠিক পথে চলে? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5264,67,23,'বলে দাও, ‘তিনিই তোমাদেরকে সৃষ্টি করেছেন আর তোমাদেরকে দিয়েছেন শোনার ও দেখার শক্তি আর অন্তঃকরণ; তোমরা শোকর আদায় খুব অল্পই করে থাক।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5265,67,24,'বলে দাও, ‘তিনিই তোমাদেরকে যমীনে ছড়িয়ে দিয়েছেন আর তাঁর কাছেই তোমাদেরকে সমবেত করা হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5266,67,25,'তারা বলে, ‘তোমরা যদি সত্যবাদী হয়েই থাক তাহলে বল (ক্বিয়ামত সংঘটিত হওয়ার) ও‘য়াদা কখন (বাস্তবায়িত হবে)? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5267,67,26,'বল, ‘সে জ্ঞান তো কেবল আল্লাহর কাছেই আছে, আমি শুধু একজন স্পষ্ট সতর্ককারী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5268,67,27,'অতঃপর যখন তারা তাকে (অর্থাৎ ক্বিয়ামতকে) নিকটে উপস্থিত দেখতে পাবে, তখন কাফিরদের মুখ মলিন হয়ে যাবে, আর (তাদেরকে) বলা হবে, ‘এই তো (ও‘য়াদা বাস্তবায়িত হয়েছে) যা তোমরা চাচ্ছিলে।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5269,67,28,'বল, ‘‘তোমরা ভেবে দেখেছ কি আল্লাহ যদি আমাকে আর আমার সঙ্গী সাথীদেরকে ধ্বংস করে দেন অথবা আমাদের উপর দয়া করেন (তাতে তোমাদের লাভটা কী?) মর্মান্তিক শাস্তি থেকে কাফিদেরকে বাঁচাবে কে? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5270,67,29,'বল, ‘তিনিই দয়াময়, আমরা তাঁর উপরেই ঈমান রাখি, আর তাঁর উপরেই নির্ভর করি। তোমরা শীঘ্রই জানতে পারবে কে সুস্পষ্ট গুমরাহীতে আছে (আমরা না তোমরা)।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5271,67,30,'বল, ‘‘তোমরা ভেবে দেখেছ কি যদি তোমাদের পানি ভূগর্ভের তলদেশে চলে যায়, তাহলে তোমাদেরকে কে এনে দেবে প্রবহমান পানি?’’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5272,68,1,'নূন, কলমের শপথ আর লেখকেরা যা লেখে তার শপথ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5273,68,2,'তোমার প্রতিপালকের অনুগ্রহে তুমি পাগল নও। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5274,68,3,'তোমার জন্য অবশ্যই আছে অফুরন্ত পুরস্কার, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5275,68,4,'নিশ্চয়ই তুমি মহান চরিত্রের উচ্চমার্গে উন্নীত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5276,68,5,'শীঘ্রই তুমি দেখতে পাবে আর তারাও দেখবে, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5277,68,6,'তোমাদের মধ্যে কে পাগলামিতে আক্রান্ত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5278,68,7,'তোমার প্রতিপালক বেশি জানেন কে তাঁর পথ থেকে গুমরাহ হয়ে গেছে, আর সঠিক পথপ্রাপ্তদেরকেও তিনি ভাল করে জানেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5279,68,8,'কাজেই তুমি মিথ্যাবাদীদের কথা মান্য কর না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5280,68,9,'তারা চায় যে, তুমি যদি নমনীয় হও, তবে তারাও নমনীয় হবে, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5281,68,10,'তুমি তার অনুসরণ কর না, যে বেশি বেশি কসম খায় আর যে (বার বার মিথ্যা কসম খাওয়ার কারণে মানুষের কাছে) লাঞ্ছিত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5282,68,11,'যে পশ্চাতে নিন্দা করে একের কথা অপরের কাছে লাগিয়ে ফিরে, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5283,68,12,'যে ভাল কাজে বাধা দেয়, সীমালঙ্ঘনকারী, পাপিষ্ঠ, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5284,68,13,'কঠোর স্বভাব, তার উপরে আবার কুখ্যাত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5285,68,14,'কারণ সে সম্পদ আর (অনেক) সন্তানাদির অধিকারী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5286,68,15,'তার কাছে যখন আমার আয়াত পাঠ করা হয় তখন সে বলে, ‘‘এতো আগে কালের লোকেদের কিসসা কাহিনী মাত্র। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5287,68,16,'আমি তার শুঁড়ের উপর দাগ দিয়ে দিব (অর্থাৎ তাকে লাঞ্ছিত করব)। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5288,68,17,'আমি এদেরকে (অর্থাৎ মক্কাবাসীদেরকে) পরীক্ষা করেছি যেমন আমি বাগানের মালিকদেরকে পরীক্ষা করেছিলাম। যখন তারা কসম করে বলেছিল যে, তারা সকাল বেলায় অবশ্যই বাগানের ফল সংগ্রহ করে নেবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5289,68,18,'তারা ‘ইনশাআল্লাহ’ বলেনি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5290,68,19,'অতঃপর তোমার প্রতিপালকের পক্ষ থেকে বাগানে এক বিপদ এসে পড়ল যখন তারা ছিল নিদ্রিত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5291,68,20,'যার ফলে তা হয়ে গেল বিবর্ণ কাটা ফসলের মত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5292,68,21,'সকালে একে অপরকে ডেকে বলল, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5293,68,22,'‘তোমরা যদি ফল সংগ্রহ করতে চাও তবে সকাল সকাল ক্ষেতে চল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5294,68,23,'তারা চুপি চুপি কথা বলতে বলতে চলল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5295,68,24,'‘আজ যেন সেখানে তোমাদের কাছে মিসকীনরা অবশ্যই ঢুকতে না পারে।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5296,68,25,'তারা এক (অন্যায়) সিদ্ধান্তে সংকল্পবদ্ধ হয়ে সকাল করল।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5297,68,26,'অতঃপর তারা যখন বাগানটি দেখল তখন তারা বলল, ‘‘আমরা অবশ্যই পথ হারিয়ে ফেলেছি, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5298,68,27,'(অতঃপর ব্যাপারটি বুঝতে পারার পর তারা বলে উঠল) বরং আমরা তো কপাল পোড়া।’’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5299,68,28,'তাদের মধ্যেকার সবচেয়ে ভাল লোকটি বলল, ‘আমি কি তোমাদেরকে বলিনি যে, তোমরা আল্লাহর পবিত্রতা ও মহিমা ঘোষণা করছ না কেন?’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5300,68,29,'তারা বলল, ‘আমরা আমাদের প্রতিপালকের পবিত্রতা ও মহিমা ঘোষণা করছি, আমরা সীমালঙ্ঘনকারীই ছিলাম।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5301,68,30,'অতঃপর তারা একে অপরের দিকে এগিয়ে গিয়ে পরস্পরের প্রতি দোষারোপ করতে লাগল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5302,68,31,'তারা বলল, ‘দুর্ভোগ আমাদের, আমরা ছিলাম সীমালঙ্ঘনকারী, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5303,68,32,'সম্ভবতঃ আমাদের প্রতিপালক এর পরিবর্তে আমাদেরকে উত্তম (বাগান) দিবেন, আমরা আমাদের প্রতিপালকের অভিমুখী হলাম।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5304,68,33,'‘আযাব এ রকমই হয়ে থাকে। আর আখিরাতের ‘আযাব তো সবচেয়ে কঠিন। যদি তারা জানত! ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5305,68,34,'মুত্তাক্বীদের জন্য তাদের প্রতিপালকের নিকট আছে নি‘মাতে পরিপূর্ণ জান্নাত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5306,68,35,'আমি কি আত্মসমর্পণকারীদেরকে অপরাধীদের মত গণ্য করব? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5307,68,36,'তোমাদের কী হয়েছে, তোমরা কেমনভাবে বিচার করে সিদ্ধান্ত দিচ্ছ? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5308,68,37,'তোমাদের কাছে কি (আল্লাহর নাযিলকৃত) কোন কিতাব আছে যা পড়ে তোমরা জানতে পার যে, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5309,68,38,'তোমাদের জন্য সেখানে তাই আছে যা তোমরা পছন্দ কর? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5310,68,39,'অথবা তোমাদের জন্য আমার উপর কি ক্বিয়ামত পর্যন্ত বলবৎ কোন দায়বদ্ধতা আছে যে, তোমরা যা দাবী করবে তাই পাবে? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5311,68,40,'তুমি তাদেরকে জিজ্ঞেস কর (আল্লাহ যে তাদের সঙ্গে প্রতিশ্রুতিতে দায়বদ্ধ) এ ব্যাপারে তাদের মধ্যে কোন্ ব্যক্তি জামিনদার (গ্যারান্টর)? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5312,68,41,'অথবা তাদের কি শরীক উপাস্য আছে, থাকলে তাদের শরীক উপাস্যদেরকে হাজির করুক- তারা যদি সত্যবাদী হয়ে থাকে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5313,68,42,'যেদিন (ক্বিয়ামতে) পায়ের গোছা (হাঁটুর নিম্নভাগ) উন্মোচিত হবে আর তাদেরকে ডাকা হবে সেজদা করার জন্য, কিন্তু তারা সেজদা করতে সক্ষম হবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5314,68,43,'তাদের দৃষ্টি হবে অবনত, অপমান লাঞ্ছনা তাদের উপর চেপে বসবে। (দুনিয়াতে) তারা যখন সুস্থ ও নিরাপদ ছিল তখনও তাদেরকে সেজদা করার জন্য ডাকা হত (কিন্তু তারা সেজদা করত না) ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5315,68,44,'কাজেই ছেড়ে দাও আমাকে আর তাদেরকে যারা এ বাণীকে অস্বীকার করেছে। আমি তাদেরকে ক্রমে ক্রমে আস্তে আস্তে এমনভাবে ধরে টান দিব যে, তারা একটু টেরও পাবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5316,68,45,'আমি তাদেরকে (লম্বা) সময় দেই, আমার কৌশল বড়ই মজবুত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5317,68,46,'তুমি কি তাদের কাছে কোন পারিশ্রমিক চাচ্ছ যে, দেনার দায়ভার বহন করা তাদের জন্য কঠিন হয়ে পড়েছে? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5318,68,47,'নাকি তাদের কাছে গায়বের খবর আছে যা তা তারা লিখে রাখে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5319,68,48,'কাজেই তুমি তোমার প্রতিপালকের হুকুমের জন্য ধৈর্য ধরে অপেক্ষা কর, আর মাছওয়ালা [ইউনুস (আঃ)] এর মত (অধৈর্য) হয়ো না। স্মরণ কর যখন সে (তার প্রতিপালককে ডাক দিয়েছিল চিন্তায় দুঃখে আচ্ছন্ন হয়ে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5320,68,49,'তার প্রতিপালকের অনুগ্রহ যদি তার কাছে না পৌঁছত, তাহলে সে লাঞ্ছিত অবস্থায় ধুঁধুঁ বালুকাময় তীরে নিক্ষিপ্ত হত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5321,68,50,'এভাবে তার প্রতিপালক তাকে বেছে নিলেন আর তাকে সৎকর্মশীলদের অন্তর্ভুক্ত করলেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5322,68,51,'কাফিররা যখন কুরআন শুনে তখন তারা যেন তাদের দৃষ্টি দিয়ে তোমাকে আছড়ে ফেলবে। আর তারা বলে, ‘‘সে তো অবশ্যই পাগল।’’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5323,68,52,'অথচ এ কুরআন বিশ্বজগতের জন্য উপদেশ ছাড়া অন্য কিছুই নয়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5324,69,1,'নিশ্চিত সংঘটিতব্য বিষয়, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5325,69,2,'কী সেই নিশ্চিত সংঘটিতব্য বিষয়? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5326,69,3,'আর তুমি কি জান কী সেই নিশ্চিত সংঘটিতব্য বিষয়? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5327,69,4,'‘আদ ও সামূদ জাতি সেই আকস্মিকভাবে সংঘটিতব্য মহাবিপদকে মিথ্যে বলেছিল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5328,69,5,'অতঃপর সামূদ জাতিকে ধ্বংস করা হয়েছিল এক প্রলয়ংকর বিপর্যয় দিয়ে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5329,69,6,'আর ‘আদকে ধ্বংস করা হয়েছিল এক প্রচন্ড ঝড়ো হাওয়া দিয়ে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5330,69,7,'যা তাদের উপর প্রবাহিত হয়েছিল সাত রাত আট দিন বিরামহীনভাবে, তুমি দেখতে তারা পড়ে আছে ইতস্তত বিক্ষিপ্ত, যেন তারা পুরাতন শুকনো খেজুর গাছের কান্ড। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5331,69,8,'তুমি তাদের কাউকে রক্ষা পেয়ে বেঁচে থাকতে দেখছ কি? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5332,69,9,'ফেরাউন আর তার পূর্ববর্তীরা আর উল্টে দেয়া জনপদবাসীরা গুরুতর পাপে লিপ্ত ছিল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5333,69,10,'তারা তাদের প্রতিপালকের রসূলকে অমান্য করেছিল, তখন তিনি তাদেরকে পাকড়াও করলেন- অত্যন্ত কঠিন পাকড়াও। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5334,69,11,'(নূহের বানের) পানি যখন কূল ছাপিয়ে সীমা ছাড়িয়ে গেল, তখন আমি তোমাদেরকে চলন্ত নৌযানে আরোহণ করালাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5335,69,12,'যেন এ ঘটনাটিকে আমি তোমাদের জন্য শিক্ষাপ্রদ-স্মারক করে রাখি আর সংরক্ষণকারী কান তা সংরক্ষণ করে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5336,69,13,'অতঃপর যখন সিঙ্গায় ফুঁৎকার দেয়া হবে- মাত্র একটি ফুঁৎকার। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5337,69,14,'পৃথিবী আর পর্বতমালা উৎক্ষিপ্ত হবে আর একই আঘাতে তাদেরকে চূর্ণ বিচূর্ণ করা হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5338,69,15,'সেদিন ঘটবে সেই সংঘটিতব্য (মহা) ঘটনা। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5339,69,16,'আকাশ হয়ে যাবে দীর্ণ বিদীর্ণ আর সেদিন তা হবে বাঁধন-হারা-বিক্ষিপ্ত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5340,69,17,'ফেরেশতারা থাকবে আকাশের আশে পাশে। আটজন ফেরেশতা সেদিন তোমার প্রতিপালকের ‘আরশ নিজেদের ঊর্ধ্বে বহন করবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5341,69,18,'সেদিন তোমাদেরকে (বিচারের জন্য) হাজির করা হবে আর তোমাদের কোন কাজই- যা তোমরা গোপন কর- গোপন থাকবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5342,69,19,'তখন যাকে তার ‘আমালনামা তার ডান হাতে দেয়া হবে সে বলবে, ‘এই যে আমার ‘আমালানামা পড়ে দেখ, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5343,69,20,'আমি জানতাম যে, আমাকে আমার হিসাবের সম্মুখীন হতে হবে।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5344,69,21,'অতঃপর সে আনন্দময় জীবন যাপন করবে, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5345,69,22,'উচ্চতম মর্যাদার জান্নাতে, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5346,69,23,'তার ফলসমূহ (ঝুলে থাকবে) নীচে-নাগালের মধ্যে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5347,69,24,'(তাদেরকে বলা হবে) পরিপূর্ণ তৃপ্তির সঙ্গে খাও এবং পান কর বিগত দিনে তোমরা যা (নেক ‘আমাল) করেছিলে তার প্রতিদান স্বরূপ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5348,69,25,'কিন্তু যার ‘আমালনামা বাম হাতে দেয়া হবে সে বলবে, ‘হায়! আমাকে যদি আমার ‘আমালনামা না দেয়া হত, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5349,69,26,'আর আমার হিসাব কী তা যদি আমি না-ই জানতাম, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5350,69,27,'‘হায়! (দুনিয়ার) মৃত্যুই যদি আমার শেষ (অবস্থা) হত! ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5351,69,28,'আমার ধন-সম্পদ আমার কোন কাজে আসল না, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5352,69,29,'আমার (সব) ক্ষমতা আধিপত্য নিঃশেষ হয়ে গেছে, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5353,69,30,'(তখন নির্দেশ আসবে) ধর ওকে, ওর গলায় ফাঁস লাগিয়ে দাও, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5354,69,31,'তারপর ছুড়ে ফেল ওকে জাহান্নামে, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5355,69,32,'তারপর ওকে শিকল দিয়ে বাঁধ- সত্তর হাত দীর্ঘ এক শিকলে,');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5356,69,33,'সে মহান আল্লাহর উপর ঈমান আনেনি, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5357,69,34,'আর না সে মিসকীনকে খাবার খাওয়াতে উৎসাহ দিত, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5358,69,35,'কাজেই আজ এখানে তার কোন বন্ধু নেই, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5359,69,36,'ক্ষত হতে পড়া পুঁজ ছাড়া কোন খাদ্য নেই, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5360,69,37,'যা অপরাধীরা ছাড়া অন্য কেউ খায় না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5361,69,38,'আমি কসম করছি সে সব জিনিসের যা তোমরা দেখতে পাও, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5362,69,39,'আর (সে সব জিনিসেরও) যা তোমরা দেখতে পাও না ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5363,69,40,'যে, অবশ্যই এ কুরআন এক মহা সম্মানিত রসূল [জিবরীল (আঃ)]-এর (বহন করে আনা) বাণী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5364,69,41,'তা কোন কবির কথা নয়, (কবির কথা তো) তোমরা বিশ্বাস করো না, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5365,69,42,'এটা কোন গণকের কথাও নয়, (গণকের কথায় তো) তোমরা নসীহত লাভ করো না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5366,69,43,'এটা বিশ্ব জগতের প্রতিপালকের নিকট থেকে অবতীর্ণ, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5367,69,44,'নবী যদি কোন কথা নিজে রচনা করে আমার নামে চালিয়ে দিত, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5368,69,45,'আমি অবশ্যই তার ডান হাত ধরে তাকে পাকড়াও করতাম, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5369,69,46,'তারপর অবশ্যই কেটে দিতাম তার হৃৎপিন্ডের শিরা, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5370,69,47,'অতঃপর তোমাদের মধ্যে এমন কেউ নেই যে, (আমার গোস্বা থেকে তাকে রক্ষা করার জন্য) বাধা সৃষ্টি করতে পারে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5371,69,48,'মুত্তাক্বীদের জন্য এ কুরআন অবশ্যই এক উপদেশ, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5372,69,49,'আমি অবশ্যই জানি যে, তোমাদের মধ্যে কতক লোক (কুরআনকে) অস্বীকার করবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5373,69,50,'আর এ কুরআন কাফিরদের জন্য অবশ্যই দুঃখ ও হতাশার কারণ হবে (যখন কুরআনে বর্ণিত শাস্তি তাদেরকে ঘিরে ধরবে)। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5374,69,51,'এটা একেবারে নিশ্চিত সত্য। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5375,69,52,'অতএব তুমি তোমার মহান প্রতিপালকের নামের পবিত্রতা ও মহিমা বর্ণনা কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5376,70,1,'এক ব্যক্তি চাইল সে ‘আযাব যা অবশ্যই সংঘটিত হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5377,70,2,'কাফিরদের জন্য তা প্রতিরোধ করার কেউ নেই ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5378,70,3,'(যে শাস্তি আসবে) আল্লাহর নিকট হতে যিনি আসমানে উঠার সিঁড়িগুলোর মালিক, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5379,70,4,'ফেরেশতা এবং রূহ (অর্থাৎ জিবরীল) আল্লাহর দিকে আরোহণ করে এমন এক দিনে, যার পরিমাণ পঞ্চাশ হাজার বছর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5380,70,5,'সুতরাং (হে নবী!) ধৈর্য ধর- সুন্দর সৌজন্যমূলক ধৈর্য। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5381,70,6,'তারা ঐ দিনটিকে সুদূর মনে করছে, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5382,70,7,'কিন্তু আমি তা নিকটে দেখতে পাচ্ছি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5383,70,8,'সেদিন আকাশ হবে গলিত রূপার মত, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5384,70,9,'আর পাহাড়গুলো হবে রঙ্গীণ পশমের মত, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5385,70,10,'বন্ধু বন্ধুর খবর নিবে না, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5386,70,11,'যদিও তাদেরকে রাখা হবে পরস্পরের দৃষ্টির সামনে, অপরাধী সেদিনের ‘আযাব থেকে বাঁচার জন্য বিনিময়ে দিতে চাইবে তার সন্তানাদিকে, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5387,70,12,'তার স্ত্রী ও ভাইকে, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5388,70,13,'আর তার আত্মীয় গোষ্ঠীকে যারা তাকে আশ্রয় দিত, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5389,70,14,'আর দুনিয়ার সব্বাইকে, যাতে তা তাকে রক্ষা করতে পারে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5390,70,15,'না, কক্ষনো নয়, ওটা জ্বলন্ত অগ্নিশিখা, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5391,70,16,'যা চামড়া তুলে দিবে, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5392,70,17,'জাহান্নাম সেই ব্যক্তিকে ডাকবে যে পেছনে ফিরে গিয়েছিল এবং সত্য থেকে মুখ ফিরিয়ে নিয়েছিল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5393,70,18,'সে মালধন জমা করত, অতঃপর তা আগলে রাখত, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5394,70,19,'মানুষকে সৃষ্টি করা হয়েছে খুবই অস্থির-মনা করে, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5395,70,20,'বিপদ তাকে স্পর্শ করলে সে হয় উৎকণ্ঠিত,');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5396,70,21,'কল্যাণ তাকে স্পর্শ করলে সে হয়ে পড়ে অতি কৃপণ, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5397,70,22,'তবে নামায আদায়কারীরা এ রকম নয়, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5398,70,23,'যারা তাদের নামাযে স্থির সংকল্প ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5399,70,24,'যাদের ধন-সম্পদে একটা সুবিদিত অধিকার আছে,');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5400,70,25,'প্রার্থী এবং বঞ্চিতদের, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5401,70,26,'যারা বিচার দিবসকে সত্য মানে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5402,70,27,'যারা তাদের প্রতিপালকের শাস্তি সম্পর্কে ভীত কম্পিত, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5403,70,28,'তাদের প্রতিপালকের শাস্তি এমন যে তাত্থেকে নিজেকে নিরাপদ ভাবা যায় না, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5404,70,29,'যারা নিজেদের লজ্জাস্থান সংরক্ষণ করে ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5405,70,30,'তাদের স্ত্রী অথবা অধিকারভুক্ত দাসী ছাড়া, কেননা তাতে তারা তিরস্কৃত হবে না, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5406,70,31,'তবে এর বাইরে যারা অন্য কাউকে কামনা করবে, তারাই সীমালঙ্ঘনকারী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5407,70,32,'যারা তাদের আমানাত ও ও‘য়াদা রক্ষা করে, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5408,70,33,'যারা তাদের সাক্ষ্যদানে (সত্যতার উপর) সুপ্রতিষ্ঠিত, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5409,70,34,'যারা তাদের নামাযে যত্নবান, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5410,70,35,'তারাই হবে জান্নাতে সম্মানিত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5411,70,36,'কাফিরদের কী হল যে, তারা তোমার দিকে ছুটে আসছে (তোমার কুরআন পাঠ শুনে তোমাকে ঠাট্টা-বিদ্রূপ করার জন্য), ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5412,70,37,'ডান দিক আর বাম দিক থেকে দলে দলে, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5413,70,38,'তাদের প্রত্যেকেই কি এই লোভ করে যে, তাকে নি‘মাত-ভরা জান্নাতে দাখিলা করা হবে? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5414,70,39,'কক্ষনো না, আমি তাদেরকে কী থেকে সৃষ্টি করেছি তা তারা জানে (এমন নগণ্য বস্তু থেকে সৃষ্ট মানুষ কেবল মানুষ হয়ে জন্ম নেয়ার কারণেই জান্নাতে চলে যাবে এ রকম লোভ করা বড়ই অবিবেচনাপ্রসূত ব্যাপার)। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5415,70,40,'আমি শপথ করছি উদয়স্থানসমূহের ও অস্তাচলসমূহের রবেবর-আমি অবশ্যই সক্ষম, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5416,70,41,'তাদের পরিবর্তে তাদের চেয়ে উৎকৃষ্ট মানুষ বানাতে, আমাকে পরাস্ত করবে এমন কেউ নেই। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5417,70,42,'কাজেই তাদেরকে অনর্থক কথাবার্তা ও খেল তামাশায় মত্ত থাকতে দাও যতক্ষণ না তারা তাদের সেদিনের সাক্ষাৎ লাভ করে যে দিনের ও‘য়াদা তাদেরকে দেয়া হয়েছিল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5418,70,43,'যেদিন তারা কবর থেকে বের হবে দ্রুততার সাথে- যেন তারা কোন লক্ষ্যস্থলের দিকে ছুটে চলেছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5419,70,44,'তাদের দৃষ্টি হবে অবনমিত, লাঞ্ছনা তাদেরকে আচ্ছন্ন করবে। এটাই হল সেই দিন যার ও‘য়াদা তাদেরকে দেয়া হচ্ছিল।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5420,71,1,'আমি নূহকে তার জাতির কাছে পাঠিয়েছিলাম (এই নির্দেশ দিয়ে) যে, তুমি তোমার জাতিকে সতর্ক কর তাদের কাছে মর্মান্তিক ‘আযাব আসার পূর্বে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5421,71,2,'সে বলেছিল, ‘‘হে আমার জাতির লোকেরা! আমি তোমাদের জন্য এক সুস্পষ্ট সতর্ককারী, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5422,71,3,'এ বিষয়ে যে, তোমরা আল্লাহর ‘ইবাদাত কর, তাঁকেই ভয় কর, আর আমার কথা মান্য কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5423,71,4,'তাহলে তিনি তোমাদের পাপ ক্ষমা করে দেবেন এবং একটা নির্দিষ্ট সময় পর্যন্ত তোমাদেরকে অবকাশ দেবেন। আল্লাহ কর্তৃক নিদিষ্ট সময় যখন আসবে তখন আর তা বিলম্বিত হবে না। তোমরা যদি জানতে!’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5424,71,5,'সে বলেছিল, ‘হে আমার প্রতিপালক! আমি আমার জাতিকে রাত-দিন ডেকেছি, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5425,71,6,'কিন্তু আমার ডাক কেবল তাদের পলায়নী মনোবৃত্তিকেই বাড়িয়ে দিয়েছে, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5426,71,7,'আমি যখনই তাদেরকে ডাকি যেন তুমি তাদেরকে ক্ষমা করে দাও, তখনই তারা তাদের কানে আঙ্গল ডুকিয়ে দিয়েছে, কাপড়ে মুখ ঢেকে নিয়েছে, জিদ করেছে আর খুব বেশি অহঙ্কার করেছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5427,71,8,'অতঃপর তাদেরকে আমি উচ্চৈঃস্বরে ডেকেছি, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5428,71,9,'এর পর আমি প্রকাশ্যভাবেও তাদের কাছে প্রচার করেছি, আর গোপনে গোপনেও তাদেরকে বুঝিয়েছি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5429,71,10,'আমি বলেছি- ‘তোমরা তোমাদের রব্বের কাছে ক্ষমা চাও, তিনি বড়ই ক্ষমাশীল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5430,71,11,'(তোমরা তা করলে) তিনি অজস্র ধারায় তোমাদের উপর বৃষ্টি বর্ষণ করবেন, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5431,71,12,'তোমাদের ধন সম্পদ ও সন্তানাদি বাড়িয়ে দেবেন, তোমাদের জন্য বাগান সৃষ্টি করবেন এবং তোমাদের জন্য নদীনালা প্রবাহিত করবেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5432,71,13,'‘তোমাদের হল কী যে, তোমরা আল্লাহর শ্রেষ্ঠত্ব মেনে নিতে অস্বীকার করছ? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5433,71,14,'অথচ তিনিই তোমাদেরকে সৃষ্টি করেছেন নানান স্তর অতিক্রম করিয়ে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5434,71,15,'তোমরা কি দেখ না, কীভাবে আল্লাহ সাত আসমান সৃষ্টি করেছেন একের উপরে আরেকটিকে (স্থাপন করে)?');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5435,71,16,'আর তাদের মাঝে চাঁদকে বানিয়েছেন আলো এবং সূর্যকে করেছেন প্রদীপ।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5436,71,17,'আল্লাহ তোমাদেরকে মাটি থেকে উদগত করেন (এবং ক্রমশঃ বাড়িয়ে তোলেন যেমন বাড়িয়ে তোলেন বৃক্ষকে)');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5437,71,18,'অতঃপর এই মাটিতেই তোমাদেরকে ফিরিয়ে আনবেন এবং তোমাদেরকে পুনরুত্থিত করবেন।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5438,71,19,'আল্লাহ তোমাদের জন্য যমীনকে করেছেন সম্প্রসারিত,');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5439,71,20,'যাতে তোমরা তার প্রশস্ত পথ-ঘাট দিয়ে চলাচল করতে পার।’');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5440,71,21,'নূহ বলেছিল, ‘হে আমার প্রতিপালক! তারা আমার কথা প্রত্যাখ্যান করেছে আর আনুগত্য করছে তাদের (অর্থাৎ এমন সব লোকদের) যাদের মালধন আর সন্তানাদি তাদের ক্ষতি ছাড়া অন্য কিছুই বৃদ্ধি করেনি,');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5441,71,22,'আর তারা ষড়যন্ত্র করেছিল সাংঘাতিক ষড়যন্ত্র।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5442,71,23,'আর  তারা বলেছিল, তোমাদের দেবদেবীদের কক্ষনো পরিত্যাগ করো না, আর অবশ্যই পরিত্যাগ করো না ওয়াদ সুআ‘আকে, আর না ‘ইয়াগুস, ইয়া‘ঊক ও নাসরকে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5443,71,24,'তারা গুমরাহ করেছে অনেককে, তুমি যালিমদের গুমরাহী ছাড়া আর কিছুই বৃদ্ধি করো না।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5444,71,25,'পাপের কারণে তাদেরকে ডুবিয়ে দেয়া হয়েছে, পরে তাদেরকে দাখিল করা হয়েছে আগুনে, অত:পর তারা আল্লাহকে ছাড়া কাউকে সাহায্যকারী পায়নি।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5445,71,26,'নূহ বলল, ‘হে আমার রব্ব! ভূপৃষ্ঠে বসবাসকারী কাফিরদের একজনকেও তুমি রেহাই দিও না।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5446,71,27,'তুমি যদি তাদেরকে রেহাই দাও, তাহলে তারা তোমার বান্দাহদেরকে গুমরাহ করে দেবে আর কেবল পাপাচারী কাফির জন্ম দিতে থাকবে।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5447,71,28,'হে আমার রব্ব! তুমি ক্ষমা কর আমাকে, আমার পিতামাতাকে যারা আমার গৃহে মু’মিন হয়ে প্রবেশ করে তাদেরকে আর মু’মিন পুরুষ ও মু’মিন নারীদেরকে; আর যালিমদের জন্য ধ্বংস ছাড়া আর কিছুই বৃদ্ধি করো না।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5448,72,1,'বল, ‘‘আমার কাছে ওয়াহী করা হয়েছে যে, জিন্নদের একটি দল মনোযোগ দিয়ে (কুরআন) শুনেছে অতঃপর তারা বলেছে ‘আমরা এক অতি আশ্চর্যজনক কুরআন শুনেছি ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5449,72,2,'যা সত্য-সঠিক পথ প্রদর্শন করে, যার কারণে আমরা তাতে ঈমান এনেছি, আমরা কক্ষনো কাউকে আমাদের প্রতিপালকের অংশীদার গণ্য করব না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5450,72,3,'আর আমাদের প্রতিপালকের মর্যাদা অতি উচ্চ, তিনি গ্রহণ করেননি কোন স্ত্রী আর কোন সন্তান। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5451,72,4,'আর আমাদের মধ্যেকার নির্বোধেরা তাঁর সম্পর্কে সীমাতিরিক্ত কথাবার্তা বলত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5452,72,5,'আর আমরা ধারণা করতাম যে, মানুষ ও জ্বিন আল্লাহ সম্পর্কে কক্ষনো মিথ্যে কথা বলবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5453,72,6,'আরো এই যে, কতক মানুষ কতক জ্বিনের আশ্রয় নিত, এর দ্বারা তারা জ্বিনদের গর্ব অহঙ্কার বাড়িয়ে দিয়েছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5454,72,7,'আরো এই যে (জ্বিনেরা বলেছিল) তোমরা (জ্বিনেরা) যেমন ধারণা করতে তেমনি মানুষেরা ধারণা করত যে, (মৃত্যুর পর) আল্লাহ কাউকে পুনরুত্থিত করবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5455,72,8,'আর আমরা আকাশের খবর নিতে চেয়েছিলাম কিন্তু আমরা সেটাকে পেলাম কঠোর প্রহরী বেষ্টিত ও জ্বলন্ত উল্কাপিন্ডে পরিপূর্ণ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5456,72,9,'আমরা (আগে) সংবাদ শুনার জন্য আকাশের বিভিন্ন ঘাঁটিতে বসতাম, কিন্তু এখন কেউ সংবাদ শুনতে চাইলে তার উপর নিক্ষেপের জন্য সে জ্বলন্ত অগ্নিকুন্ডকে লুকিয়ে থাকতে দেখে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5457,72,10,'আমরা জানি না (এই পরিবর্তিত অবস্থার মাধ্যমে) পৃথিবীবাসীর অকল্যাণই চাওয়া হচ্ছে, না তাদের প্রতিপালক তাদেরকে সরল সঠিক পথ দেখাতে চান। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5458,72,11,'আর আমাদের কিছু সংখ্যক সৎকর্মশীল, আর কতক এমন নয়, আমরা ছিলাম বিভিন্ন মত ও পথে বিভক্ত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5459,72,12,'আমরা বুঝতে পেরেছি যে, আমরা পৃথিবীতে আল্লাহকে পরাস্ত করতে পারব না, আর পালিয়েও তাঁকে অপারগ করতে পারব না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5460,72,13,'আরো এই যে, আমরা যখন হিদায়াতের বাণী শুনতে পেলাম, তখন তার উপর ঈমান আনলাম। যে ব্যক্তি তার প্রতিপালকের উপর ঈমান আনে তার কোন ক্ষতি বা যুলমের ভয় থাকবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5461,72,14,'আমাদের মধ্যে কিছু সংখ্যক (আল্লাহর প্রতি) আত্মসমর্পণকারী আর কিছু সংখ্যক অন্যায়কারী। যারা আত্মসমর্পণ করে তারা সঠিক পথ বেছে নিয়েছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5462,72,15,'আর যারা অন্যায়কারী তারা জাহান্নামের ইন্ধন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5463,72,16,'আরো (আমার কাছে ওয়াহী করা হয়েছে এই) যে, তারা যদি সত্য-সঠিক পথে প্রতিষ্ঠিত থাকত, তাহলে আমি তাদেরকে প্রচুর পানি পান করাতাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5464,72,17,'যেন আমি তা দিয়ে তাদেরকে পরীক্ষা করতে পারি (যে নি‘মাত পাওয়ার পর তারা শুকর-গুজার হয়, না না-ফরমান হয়)। যে ব্যক্তি তার প্রতিপালকের স্মরণ থেকে মুখ ফিরিয়ে নেয়, তিনি তাকে কঠিন ‘আযাবে প্রবেশ করাবেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5465,72,18,'আরো এই যে, মাসজিদগুলো কেবলমাত্র আল্লাহরই জন্য, কাজেই তোমরা আল্লাহর সঙ্গে অন্য আর কাউকে ডেকো না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5466,72,19,'আরো এই যে, যখন আল্লাহর বান্দা [রসূলুল্লাহ (সা.)] যখন তাঁকে আহবান করার জন্য দাঁড়াল তখন তারা (অর্থাৎ কাফিররা) তার চারপাশে ভিড় জমাল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5467,72,20,'বল: আমি শুধু আমার প্রতিপালককেই ডাকি, আর অন্য কাউকে তাঁর অংশীদার গণ্য করি না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5468,72,21,'বল- ‘আমি তোমাদের কোন ক্ষতি বা কল্যাণ করার ক্ষমতা রাখি না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5469,72,22,'বল- আল্লাহর (শাস্তি) থেকে কেউ আমাকে রক্ষা করতে পারবে না (যদি আমি তাঁর অবাধ্য হই) এবং তাঁর আশ্রয় ছাড়া আমি কোন আশ্রয়ও পাব না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5470,72,23,'আল্লাহর বাণী পৌঁছানো ও তাঁর পায়গাম প্রচার করাই আমার কাজ। যে কেউ আল্লাহ ও তাঁর রসূলকে অমান্য করে, তার জন্য আছে জাহান্নামের আগুন; তাতে তারা চিরকাল থাকবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5471,72,24,'অবশেষে তারা যখন প্রতিশ্রুত শাস্তি দেখতে পাবে, তখন তারা জানতে পারবে যে, সাহায্যকারী হিসেবে কে সবচেয়ে দুর্বল, আর সংখ্যায় কারা সবচেয়ে কম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5472,72,25,'বল- ‘আমি জানি না তোমাদেরকে যার প্রতিশ্রুতি দেয়া হয়েছে তা কি নিকটবর্তী, না তার জন্য আমার প্রতিপালক কোন দীর্ঘ মেয়াদ নির্দিষ্ট করবেন।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5473,72,26,'একমাত্র তিনিই অদৃশ্যের জ্ঞানী, তিনি তাঁর অদৃশ্যের জ্ঞান কারো কাছে প্রকাশ করেন না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5474,72,27,'তাঁর মনোনীত রসূল ব্যতীত। কেননা তিনি তখন তাঁর রসূলের আগে-পিছে পাহারাদার নিযুক্ত করেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5475,72,28,'এটা জানার জন্য যে, রসূলগণ তাদের প্রতিপালকের বাণী সত্যিই পৌঁছে দিয়েছে কিনা। রসূলদের কাছে যা আছে তা তিনি (স্বীয় জ্ঞান দ্বারা) পরিবেষ্টন করে রেখেছেন আর প্রত্যেকটি জিনিসকে গুণে গুণে রেখেছেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5476,73,1,'ওহে চাদরে আবৃত (ব্যক্তি)! ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5477,73,2,'রাতে নামাযে দাঁড়াও তবে (রাতের) কিছু অংশ বাদে, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5478,73,3,'রাতের অর্ধেক (সময় দাঁড়াও) কিংবা তার থেকে কিছুটা কম কর,');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5479,73,4,'অথবা তার চেয়ে বাড়াও, আর ধীরে ধীরে সুস্পষ্টভাবে কুরআন পাঠ কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5480,73,5,'আমি তোমার উপর গুরুভার কালাম নাযিল করব (বিশ্বের বুকে যার প্রচার ও প্রতিষ্ঠা করার দায়িত্বভার অতি বড় কঠিন কাজ)। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5481,73,6,'বাস্তবিকই রাতে বিছানা ছেড়ে উঠা আত্মসংযমের জন্য বেশি কার্যকর এবং (কুরআন) স্পষ্ট উচ্চারণের অনুকূল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5482,73,7,'দিনের বেলায় তোমার জন্য আছে দীর্ঘ কর্মব্যস্ততা। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5483,73,8,'কাজেই তুমি তোমার প্রতিপালকের নাম স্মরণ কর এবং একাগ্রচিত্তে তাঁর প্রতি মগ্ন হও। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5484,73,9,'(তিনি) পূর্ব ও পশ্চিমের সর্বময় কর্তা, তিনি ছাড়া সত্যিকারের কোন ইলাহ নেই, অতএব তাঁকেই তুমি তোমার কার্য সম্পদানকারী বানিয়ে লও। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5485,73,10,'তারা যা বলে সে ব্যাপারে ধৈর্য ধারণ কর আর ভদ্রতার সঙ্গে তাদেরকে পরিহার ক’রে চল।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5486,73,11,'আর ছেড়ে দাও আমাকে আর নানান বিলাস সামগ্রীর মালিক ঐ মিথ্যুকদেরকে এবং তাদেরকে কিছুটা সময় অবকাশ দাও। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5487,73,12,'আমার কাছে আছে শেকল আর দাউ দাউ করে জ্বলা আগুন, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5488,73,13,'আর গলায় আটকে যায় এমন খাবার আর মর্মান্তিক শাস্তি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5489,73,14,'(এসব শাস্তি দেয়া হবে) যেদিন যমীন আর পাহাড়গুলো কেঁপে উঠবে, আর পাহাড়গুলো হবে চলমান বালুকারাশি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5490,73,15,'আমি তোমাদের কাছে  তেমনিভাবে একজন  রসূলকে  তোমাদের  প্রতি সাক্ষ্যদাতা হিসেবে পাঠিয়েছি (যিনি ক্বিয়ামতে সাক্ষ্য দিবেন যে, দ্বীনের দাওয়াত তিনি যথাযথভাবে মানুষের কাছে পৌঁছে দিয়েছেন) যেমনভাবে আমি ফেরাউনের কাছে পাঠিয়েছিলাম একজন রসূলকে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5491,73,16,'তখন ফেরাউন সেই রসূলকে অমান্য করল। ফলে আমি তাকে শক্ত ধরায় ধরলাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5492,73,17,'অতএব তোমরা যদি (এই রসূলকে) অস্বীকার কর, তাহলে তোমরা কীভাবে সেদিন আত্মরক্ষা করবে যেদিনটি (তার ভীষণতা ও ভয়াবহতায়) বালককে ক’রে দেবে বুড়ো। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5493,73,18,'যার কারণে আকাশ ফেটে যাবে, আল্লাহর ওয়া‘দা পূর্ণ হয়ে যাবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5494,73,19,'এটা উপদেশ। কাজেই যার মন চায় সে তার প্রতিপালকের দিকে পথ ধরুক। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5495,73,20,'তোমার প্রতিপালক জানেন যে, তুমি কখনও রাতের দু’তৃতীয়াংশ ‘ইবাদাতের জন্য দাঁড়াও, কখনও অর্ধেক, কখনও রাতের এক তৃতীয়াংশ, তোমার সঙ্গী-সাথীদের একটি দলও (তাই করে)। আল্লাহ্ই রাত আর দিনের পরিমাণ নির্ধারণ করেন। তিনি জানেন, তোমরা তা যথাযথ হিসাব রেখে পালন করতে পারবে না। কাজেই তিনি তোমাদের প্রতি ক্ষমাপরবশ হয়েছেন। কাজেই কুরআনের যতটুকু পড়া তোমার জন্য সহজ হয়, তুমি ততটুকু পড়। তিনি জানেন, তোমাদের মধ্যে কেউ কেউ অসুস্থ হবে, আর কতক আল্লাহর অনুগ্রহ সন্ধানে যমীনে ভ্রমণ করবে, আর কতক আল্লাহর পথে যুদ্ধ করবে। কাজেই তোমাদের জন্য যতটুকু সহজসাধ্য হয় তাই তাত্থেকে পাঠ কর, আর নামায প্রতিষ্ঠা কর, যাকাত দাও আর আল্লাহকে ঋণ দাও উত্তম ঋণ। তোমরা যা কিছু কল্যাণ নিজেদের  জন্য আগে  পাঠাবে,  তা আল্লাহর  নিকট  (সঞ্চিত) পাবে, তাই উত্তম এবং পুরস্কার হিসেবে খুব বড়। তোমরা আল্লাহর নিকট ক্ষমা প্রার্থনা কর, আল্লাহ অতি ক্ষমাশীল, বড়ই দয়ালু। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5496,74,1,'ওহে বস্ত্র আবৃত (ব্যক্তি)!');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5497,74,2,'ওঠ, সতর্ক কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5498,74,3,'আর তোমার প্রতিপালকের শ্রেষ্ঠত্ব ঘোষণা কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5499,74,4,'তোমার পোশাক পরিচ্ছদ পবিত্র রাখ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5500,74,5,'(যাবতীয়) অপবিত্রতা থেকে দূরে থাক। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5501,74,6,'(কারো প্রতি) অনুগ্রহ করো না অধিক পাওয়ার উদ্দেশে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5502,74,7,'তোমার প্রতিপালকের (সন্তুষ্টির) জন্য ধৈর্য ধর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5503,74,8,'যেদিন শিঙ্গায় ফুঁ দেয়া হবে, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5504,74,9,'সেদিনটি হবে বড়ই কঠিন দিন, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5505,74,10,'(যা) কাফিরদের জন্য মোটেই সহজ নয়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5506,74,11,'ছেড়ে দাও আমাকে (তার সঙ্গে বুঝাপড়া করার জন্য) যাকে আমি এককভাবে সৃষ্টি করেছি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5507,74,12,'আর তাকে (ওয়ালীদ বিন মুগীরাহকে) দিয়েছি অঢেল ধন-সম্পদ, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5508,74,13,'আর অনেক ছেলে যারা সব সময় তার কাছেই থাকে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5509,74,14,'এবং তার জীবনকে করেছি সচ্ছল ও সুগম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5510,74,15,'এর পরও সে লোভ করে যে, আমি তাকে আরো দেই। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5511,74,16,'কক্ষনো না, সে ছিল আমার নিদর্শনের বিরুদ্ধাচারী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5512,74,17,'শীঘ্রই আমি তাকে উঠাব শাস্তির পাহাড়ে (অর্থাৎ তাকে দিব বিপদের উপর বিপদ)। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5513,74,18,'সে চিন্তা ভাবনা করল এবং সিদ্ধান্ত নিল, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5514,74,19,'ধ্বংস হোক সে, কীভাবে সে (কুরআনের অলৌকিকতা স্বীকার করার পরও কেবল অহমিকার বশবর্তী হয়ে নবুওয়াতকে অস্বীকার করার) সিদ্ধান্ত নিল! ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5515,74,20,'আবারো ধ্বংস হোক সে, সে সিদ্ধান্ত নিল কীভাবে! ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5516,74,21,'তারপর সে তাকালো। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5517,74,22,'তারপর ভ্রু কুঁচকালো আর মুখ বাঁকালো। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5518,74,23,'তারপর সে পিছনে ফিরল আর অহংকার করল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5519,74,24,'তারপর বলল- ‘এ তো যাদু ছাড়া আর কিছু নয়, এতো পূর্বে থেকেই চলে আসছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5520,74,25,'এটা তো মানুষের কথা মাত্র।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5521,74,26,'শীঘ্রই আমি তাকে জাহান্নামের আগুনে নিক্ষেপ করব। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5522,74,27,'তুমি কি জান জাহান্নামের আগুন কী? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5523,74,28,'তা কাউকে জীবিতও রাখবে না, আর মৃত অবস্থায়ও ছেড়ে দেবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5524,74,29,'চামড়া ঝলসে দেবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5525,74,30,'সেখানে নিয়োজিত আছে ঊনিশ জন ফেরেশতা। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5526,74,31,'আমিই কেবল ফেরেশতাদেরকে জাহান্নামের তত্ত্বাবধায়ক করেছি। আর তাদের (এই) সংখ্যাকে কাফিরদের জন্য একটা পরীক্ষা বানিয়ে দিয়েছি (কেননা তারা এ কথা বিশ্বাসই করতে পারবে না যে মাত্র ঊনিশ জন ফেরেশতা বিশাল জাহান্নামের যাবতীয় দায়িত্ব পালন করবে) আর যেন কিতাবধারীগণ তা দৃঢ়ভাবে বিশ্বাস করে আর ঈমানদারদের ঈমান আরো বৃদ্ধি পায় এবং কিতাবধারীগণ ও ঈমানদারগণ যেন কোন রকম সন্দেহের মধ্যে না থাকে। যাদের অন্তরে রোগ আছে তারা আর কাফিররা যাতে বলে উঠে, ‘‘ এ ধরণের কথা দিয়ে আল্লাহ কী বোঝাতে চেয়েছেন?’’ এভাবে আল্লাহ যাকে চান গুমরাহ করেন আর যাকে চান সঠিক পথে পরিচালিত করেন। তোমার প্রতিপালকের বাহিনী (কারা এবং এর স্যখ্যা কত সে) সম্পর্কে তিনি ছাড়া কেউ জানে না। (জাহান্নামের) এ (বর্ণনা দেয়া হল) কেবল মানুষের নসীহত লাভের জন্য। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5527,74,32,'(এটা) কক্ষনো (ভিত্তিহীন) না, চাঁদের কসম, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5528,74,33,'রাতের কসম যখন তার অবসান হয়, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5529,74,34,'প্রভাতের কসম- যখন তা উজ্জ্বল হয়ে ওঠে, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5530,74,35,'এই জাহান্নাম বড় বড় বিপদগুলোর একটি, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5531,74,36,'মানুষের জন্য সতর্ককারী, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5532,74,37,'তোমাদের মধ্যে যে (কল্যাণের পথে) এগিয়ে যেতে চায় অথবা পেছনে পড়ে থাকতে চায় তার জন্য ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5533,74,38,'প্রত্যেক ব্যক্তি নিজ কৃতকর্মের দায়ে আবদ্ধ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5534,74,39,'কিন্তু ডান পাশের লোকেরা নয়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5535,74,40,'তারা থাকবে জান্নাতে। তারা পরস্পরকে জিজ্ঞেস করবে ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5536,74,41,'অপরাধীদের সম্পর্কে ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5537,74,42,'‘কিসে তোমাদেরকে জাহান্নামে নিয়ে গেছে? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5538,74,43,'তারা বলবে, ‘আমরা নামায আদায়কারী লোকেদের মধ্যে শামিল ছিলাম না,');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5539,74,44,'আর মিসকীনদেরকে খাবার খাওয়াতাম না, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5540,74,45,'আর আমরা (সত্য পথের পথিকদের) সমালোচনা করতাম সমালোচনাকারীদের সঙ্গে (থেকে)। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5541,74,46,'আমরা প্রতিফল দিবসকে অস্বীকার করতাম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5542,74,47,'আমাদের নিকট নিশ্চিত বিশ্বাস (অর্থাৎ মৃত্যু) না আসা পর্যন্ত।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5543,74,48,'তখন সুপারিশকারীদের সুপারিশ তাদের কোন কাজে আসবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5544,74,49,'তাদের হয়েছে কী যে তারা উপদেশ থেকে মুখ ফিরিয়ে নিয়েছে? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5545,74,50,'তারা যেন ভয়ে সন্ত্রস্ত গাধা, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5546,74,51,'সিংহের সামনে থেকে পালাচ্ছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5547,74,52,'বস্তুতঃ তাদের প্রত্যেকেই চায়, তাকে (আল্লাহর পক্ষ থেকে) খোলা চিঠি দেয়া হোক (এই মর্মে যে, তোমরা এই নবীকে মেনে নাও)। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5548,74,53,'না, তা কক্ষনো হতে পারে না, বরং (কথা এই যে) তারা আখিরাতকে ভয় করে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5549,74,54,'না, তা হতে পারে না, এটা (অর্থাৎ কুরআন সকলের জন্য) উপদেশবাণী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5550,74,55,'এক্ষনে যার ইচ্ছে তাত্থেকে শিক্ষা গ্রহণ করুক। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5551,74,56,'আল্লাহর ইচ্ছে ব্যতীত কেউ উপদেশ গ্রহণ করবে না, তিনিই ভয়ের যোগ্য, তিনিই ক্ষমা করার অধিকারী।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5552,75,1,'আমি কসম করছি ক্বিয়ামতের দিনের, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5553,75,2,'আমি আরো কসম করছি সেই মনের যে (অন্যায় কাজ ক’রে বসলে) নিজেকে ধিক্কার দেয় (যে তোমাদেরকে অবশ্যই আবার জীবিত করে উঠানো হবে)। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5554,75,3,'মানুষ কি মনে করে যে, আমি তার হাড়গুলোকে একত্রিত করতে পারব না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5555,75,4,'কেন নয়, আমি তার আঙ্গুলের ডগা পর্যন্ত সঠিকভাবে বানিয়ে দিতে সক্ষম ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5556,75,5,'কিন্তু মানুষ তার আগামী দিনগুলোতেও পাপাচার করতে চায়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5557,75,6,'সে জিজ্ঞেস করে, ‘ক্বিয়ামত দিবস কবে?’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5558,75,7,'যখন চোখ ধাঁধিয়ে যাবে, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5559,75,8,'চাঁদ হয়ে যাবে আলোকহীন ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5560,75,9,'সুরুজ আর চাঁদকে একত্রে জুড়ে দেয়া হবে, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5561,75,10,'সেদিন মানুষ বলবে- ‘আজ পালানোর জায়গা কোথায়?’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5562,75,11,'মোটেই না, আশ্রয়ের কোন জায়গা নেই। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5563,75,12,'সেদিন ঠাঁই হবে (একমাত্র) তোমার প্রতিপালকেরই নিকট। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5564,75,13,'সেদিন মানুষকে জানিয়ে দেয়া হবে সে কী (‘আমাল) আগে পাঠিয়েছে আর কী পেছনে ছেড়ে এসেছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5565,75,14,'আসলে মানুষ নিজেই নিজের সম্পর্কে চাক্ষুসভাবে অবগত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5566,75,15,'যদিও সে নানান অজুহাত পেশ করে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5567,75,16,'(এ সূরাহ অবতীর্ণ হওয়ার সময় আল্লাহর রসূল তা মুখস্থ করার জন্য ব্যস্ত হয়ে পড়লে আল্লাহ অভয় দিয়ে বললেন) তুমি তাড়াতাড়ি ওয়াহী আয়ত্ত করার জন্য তোমার জিভ নাড়াবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5568,75,17,'এর সংরক্ষণ ও পড়ানোর দায়িত্ব আমারই। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5569,75,18,'কাজেই আমি যখন তা পাঠ করি, তখন তুমি সে পাঠের অনুসরণ কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5570,75,19,'অতঃপর তা (ওয়াহীয়ে খফী বা প্রচ্ছন্ন ওয়াহীর মাধ্যমে) বিশদভাবে ব্যাখ্যা করা আমারই দায়িত্ব। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5571,75,20,'(আবার পূর্বের প্রসঙ্গে ফিরে গিয়ে আল্লাহ বলছেন) না, প্রকৃতপক্ষে তোমরা ইহজীবনকেই ভালবাস, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5572,75,21,'আর আখিরাতকে উপেক্ষা কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5573,75,22,'কতক মুখ সেদিন উজ্জ্বল হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5574,75,23,'তারা তাদের প্রতিপালকের দিকে তাকিয়ে থাকবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5575,75,24,'কতক মুখ সেদিন বিবর্ণ হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5576,75,25,'তারা ধারণা করবে যে, তাদের সঙ্গে কোমর-ভাঙ্গা আচরণ করা হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5577,75,26,'(তোমরা যে ভাবছ ক্বিয়ামত হবে না সেটা) কক্ষনো নয়, প্রাণ যখন কণ্ঠে এসে পৌঁছবে, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5578,75,27,'তখন বলা হবে, (তাকে বাঁচানোর জন্য) ঝাড়ফুঁক দেয়ার কেউ আছে কি? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5579,75,28,'সে (অর্থাৎ মুমূর্ষু ব্যক্তি) মনে করবে যে, (দুনিয়া হতে) বিদায়ের ক্ষণ এসে গেছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5580,75,29,'আর জড়িয়ে যাবে এক পায়ের নলা আরেক পায়ের নলার সাথে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5581,75,30,'সেদিন (সব কিছুর) যাত্রা হবে তোমার প্রতিপালকের পানে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5582,75,31,'কিন্তু না, সে বিশ্বাসও করেনি, নামাযও আদায় করেনি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5583,75,32,'বরং সে প্রত্যাখ্যান করেছিল আর মুখ ফিরিয়ে নিয়েছিল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5584,75,33,'অতঃপর সে অতি দম্ভভরে তার পরিবারবর্গের কাছে ফিরে গিয়েছিল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5585,75,34,'দুর্ভোগ তোমার জন্য, দুর্ভোগ, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5586,75,35,'অতঃপর তোমার জন্য দুর্ভোগের উপর দুর্ভোগ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5587,75,36,'মানুষ কি মনে করে নিয়েছে যে তাকে এমনি ছেড়ে দেয়া হবে। (তাকে পুনর্জীবিত করাও হবে না, আর বিচারের জন্য হাজির করাও হবে না)? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5588,75,37,'(তার মৃত্যুর পর আল্লাহ পুনরায় তাকে জীবিত করতে পারবেন না সে এটা কী ভাবে ধারণা করছে?) সে কি (মায়ের গর্ভে) নিক্ষিপ্ত শুক্রবিন্দু ছিল না? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5589,75,38,'তারপর সে হল রক্তপিন্ড, অতঃপর আল্লাহ তাকে সৃষ্টি করলেন ও সুবিন্যস্ত করলেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5590,75,39,'অতঃপর তাথেকে তিনি সৃষ্টি করলেন জুড়ি- পুরুষ ও নারী।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5591,75,40,'এহেন স্রষ্টা কি মৃতকে আবার জীবিত করতে সক্ষম নন? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5592,76,1,'মহাকালের মধ্য হতে মানুষের উপর কি এমন একটা সময় অতিবাহিত হয়নি যখন সে উল্লেখ করার যোগ্য কোন বস্তুই ছিল না? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5593,76,2,'আমি মানুষকে সৃষ্টি করেছি সংমিশ্রিত শুক্রবিন্দু থেকে তাকে পরীক্ষা করার জন্য, এজন্য তাকে করেছি শ্রবণশক্তি ও দৃষ্টিশক্তির অধিকারী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5594,76,3,'আমি তাকে পথ দেখিয়ে দিয়েছি, হয় সে কৃতজ্ঞ হবে, না হয় সে অকৃতজ্ঞ হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5595,76,4,'আমি (অকৃতজ্ঞ) কাফিরদের জন্য প্রস্তুত করে রেখেছি শেকল, বেড়ি আর জ্বলন্ত আগুন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5596,76,5,'(অপরদিকে) নেককার লোকেরা এমন পানপাত্র থেকে পান করবে যাতে কর্পুরের সংমিশ্রণ থাকবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5597,76,6,'আল্লাহর বান্দারা একটি ঝর্ণা থেকে পান করবে। তারা এই ঝর্ণাকে (তাদের) ইচ্ছেমত প্রবাহিত করবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5598,76,7,'যারা মানত পূরণ করে আর সেই দিনকে ভয় করে যার অনিষ্ট হবে সুদূরপ্রসারী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5599,76,8,'আর তারা আল্লাহর প্রতি তাদের ভালবাসার কারণে মিসকীন, ইয়াতীম ও কয়েদীকে খাবার খাওয়ায়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5600,76,9,'তারা বলে- ‘আমরা তোমাদেরকে খাবার খাওয়াচ্ছি কেবল আল্লাহর চেহারা (সন্তুষ্টি) লাভের জন্য, আমরা তোমাদের থেকে কোন প্রতিদান চাই না, চাই না কোন কৃতজ্ঞতা (জ্ঞাপন ও ধন্যবাদ)।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5601,76,10,'আমরা কেবল ভয় করি আমাদের প্রতিপালকের পক্ষ হতে এক ভীতিপ্রদ ভয়ানক দিনের। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5602,76,11,'যার ফলে আল্লাহ তাদেরকে সে দিনের অনিষ্ট হতে রক্ষা করবেন আর তাদেরকে দিবেন সজীবতা ও আনন্দ।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5603,76,12,'আর তাদের ধৈর্য সহিষ্ণুতার বিনিময়ে তাদেরকে দিবেন জান্নাত ও রেশমী পোশাক।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5604,76,13,'সেখানে তারা উচ্চ আসনগুলোতে হেলান দিয়ে বসবে, সেখানে তারা দেখবে না অতি গরম, আর অতি শীত।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5605,76,14,'জান্নাতের বৃক্ষরাজির ছায়া তাদের উপর থাকবে, আর ফলের গুচ্ছ একেবারে তাদের নাগালের মধ্যে রাখা হবে।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5606,76,15,'তাদের সামনে ঘুরে ঘুরে রুপার পাত্র পরিবেশন করা হবে আর সাদা পাথরের পানপাত্র।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5607,76,16,'সেই সাদা পাথরও হবে রুপার তৈরী। তারা এগুলোকে যথাযথ পরিমাণে ভর্তি করবে।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5608,76,17,'তাদেরকে পান করানোর জন্য এমন পাত্র পরিবেশন করা হবে যাতে আদার মিশ্রণ থাকবে।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5609,76,18,'সেখানে আছে একটা ঝর্ণা, যার নাম সালসাবীল।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5610,76,19,'ঘুরে ঘুরে তাদের সেবাদান কার্যে নিয়োজিত থাকবে চিরকিশোরগণ। তুমি যখন তাদেরকে দেখবে, তুমি মনে করবে, তারা যেন ছড়ানো মুক্তা।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5611,76,20,'তুমি যখন দেখবে তখন দেখতে পাবে ভোগ বিলাসের নানান সামগ্রী আর এক বিশাল রাজ্য।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5612,76,21,'তাদের আবরণ হবে চিকন সবুজ রেশম ও মোটা রেশম, আর তাদেরকে অলংকারে সজ্জিত করা হবে রুপার কঙ্কণ দ্বারা, আর তাদের রবব তাদেরকে পান করাবেন পবিত্র পরিচ্ছন্ন পানীয়।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5613,76,22,'‘এটাই তোমাদের প্রতিদান, তোমাদের চেষ্টা-সাধনা সাদরে গৃহীত হয়েছে।’');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5614,76,23,'(হে নবী!) আমি তোমার কাছে কুরআন অবতীর্ণ করেছি ক্রমে ক্রমে (অল্প অল্প করে)। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5615,76,24,'কাজেই তুমি ধৈর্য ধ’রে, তোমার প্রতিপালকের নির্দেশের অপেক্ষা কর আর তাদের মধ্যেকার পাপাচারী অথবা কাফিরের আনুগত্য কর না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5616,76,25,'আর সকাল-সন্ধ্যায় তোমার রব্ব এর নাম স্মরণ কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5617,76,26,'আর রাতের কিছু অংশে তাঁর জন্য সেজদা্য় অবনত হও আর রাতের দীর্ঘ সময় ধরে তাঁর মহিমা বর্ণনা কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5618,76,27,'এ লোকেরা তো পার্থিব জীবনকে ভালবাসে আর তাদের আড়ালে যে (কিয়ামাতের) কঠিন দিন (আসছে) তাকে উপেক্ষা করে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5619,76,28,'আমিই তাদেরকে সৃষ্টি করেছি আর তাদের গঠন মজবুত করেছি। আমি যখন চাইব তখন তাদের স্থলে তাদের মত অন্য লোক আনব। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5620,76,29,'এটা এক উপদেশ, কাজেই যার ইচ্ছে সে (এ উপদেশ মান্য ক’রে) তার প্রতিপালকের পথ ধরুক। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5621,76,30,'তোমরা ইচ্ছে কর না আল্লাহর ইচ্ছে ব্যতীত। (অর্থাৎ আল্লাহ কোন কিছু কার্যকর করতে চাইলে তোমাদের মাঝে ইচ্ছে ও শক্তি সঞ্চার করতঃ তোমাদের মাধ্যমে তা কার্যকর করেন)। আল্লাহ সর্বজ্ঞাতা মহাবিজ্ঞানী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5622,76,31,'তিনি যাকে ইচ্ছে তাঁর রাহমাতে দাখিল করেন। আর যালিমরা- তাদের জন্য তিনি প্রস্তুত করে রেখেছেন বড়ই পীড়াদায়ক শাস্তি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5623,77,1,'পর পর পাঠানো বাতাসের শপথ যা উপকার সাধন করে, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5624,77,2,'অতঃপর তা প্রচন্ড ঝড়ের বেগে বইতে থাকে, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5625,77,3,'শপথ সেই বায়ুর যা (মেঘমালাকে) ছড়িয়ে দেয় দূর দূরান্তে, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5626,77,4,'আর বিচ্ছিন্নকারী বাতাসের শপথ যা (মেঘমালাকে) বিচ্ছিন্ন করে, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5627,77,5,'অতঃপর (মানুষের অন্তরে) পৌঁছে দেয় (আল্লাহর) স্মরণ, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5628,77,6,'(বিশ্বাসী লোকদেরকে) ক্ষমা চাওয়ার সুযোগ দেয়ার জন্য আর (কাফিরদেরকে) সতর্ক করার জন্য। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5629,77,7,'তোমাদেরকে যার ও‘য়াদা দেয়া হয়েছে তা অবশ্যই সংঘটিত হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5630,77,8,'যখন নক্ষত্ররাজির আলো বিলুপ্ত হবে, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5631,77,9,'যখন আকাশ বিদীর্ণ হবে, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5632,77,10,'যখন পবর্তমালা ধুনিত হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5633,77,11,'যখন (হাশরের মাঠে) রসূলগণের একত্রিত হওয়ার সময় এসে পড়বে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5634,77,12,'(এ সব বিষয়) কোন দিনের জন্য স্থগিত রাখা হয়েছে? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5635,77,13,'চূড়ান্ত ফয়সালার দিনের জন্য। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5636,77,14,'সেই চূড়ান্ত ফয়সালার দিনটি কী তা তোমাকে কিসে জানাবে? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5637,77,15,'সে দিন দুর্ভোগ সত্য প্রত্যাখ্যানকারীদের জন্য। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5638,77,16,'আমি কি আগেকার লোকেদেরকে ধ্বংস করে দেইনি? ');
