INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5642,77,20,'আমি কি নগণ্য পানি থেকে তোমাদেরকে সৃষ্টি করিনি? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5643,77,21,'অতঃপর আমি তা রেখেছি এক সুসংরক্ষিত স্থানে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5644,77,22,'একটা নির্ধারিত সময় পর্যন্ত, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5645,77,23,'অতঃপর আমি তাকে গঠন করেছি সুসামঞ্জস্যপূর্ণরূপে, আমি কতই না উত্তম ক্ষমতার অধিকারী! ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5646,77,24,'সে দিন দুর্ভোগ সত্য প্রত্যাখ্যানকারীদের জন্য। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5647,77,25,'আমি কি পৃথিবীকে (সব কিছুকে টেনে গুটিয়ে) ধারণকারীরূপে সৃষ্টি করিনি? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5648,77,26,'জীবিত ও মৃতদেরকে (ভাল আর মন্দকে নেককার আর পাপাচারীকে)। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5649,77,27,'আর আমি তাতে স্থাপন করেছি অনড় সুউচ্চ পবর্তমালা আর তোমাদেরকে পান করিয়েছি সুমিষ্ট সুপেয় পানি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5650,77,28,'সেদিন দুর্ভোগ সত্য প্রত্যাখ্যানকারীদের জন্য। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5651,77,29,'(তাদেরকে বলা হবে) ‘চলো তার দিকে তোমরা যাকে মিথ্যে ব’লে প্রত্যাখ্যান করতে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5652,77,30,'‘চলো সেই (ধোঁয়ার) ছায়ার দিকে যার আছে তিনটি শাখা (অর্থাৎ ডানে, বামে, উপরে সব দিক থেকেই ঘিরে ধরবে), ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5653,77,31,'যা শীতল নয়, আর তা লেলিহান অগ্নিশিখা থেকে বাঁচাতেও পারবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5654,77,32,'সে আগুন প্রাসাদের ন্যায় (বিশাল) স্ফুলিঙ্গ উৎক্ষেপ করবে, ');
