INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5656,77,34,'সেদিন দুর্ভোগ সত্য প্রত্যাখ্যানকারীদের জন্য। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5657,77,35,'এদিন এমন যে, কেউ কথা বলতে পারবে না, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5658,77,36,'তাদেরকে কোন ওজর পেশ করারও সুযোগ দেয়া হবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5659,77,37,'সেদিন দুর্ভোগ সত্য প্রত্যাখ্যানকারীদের জন্য। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5660,77,38,'এটা চূড়ান্ত ফয়সালার দিন, আমি একত্রিত করেছি তোমাদেরকে আর আগের লোকেদেরকে। ');
