INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5665,77,43,'(তাদেরকে বলা হবে) ‘তোমরা তৃপ্তির সঙ্গে খাও আর পান কর, তোমরা যে ‘আমাল করেছিলে তার পুরস্কারস্বরূপ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5666,77,44,'সৎকর্মশীলদেরকে আমি এভাবেই প্রতিফল দিয়ে থাকি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5667,77,45,'সেদিন দুর্ভোগ সত্য প্রত্যাখ্যানকারীদের জন্য। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5668,77,46,'(ওহে সত্য প্রত্যাখ্যানকারীরা!) তোমরা অল্প কিছুকাল খেয়ে নাও আর ভোগ করে নাও, তোমরা তো অপরাধী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5669,77,47,'সেদিন দুর্ভোগ সত্য প্রত্যাখ্যানকারীদের জন্য। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5670,77,48,'তাদেরকে যখন বলা হয় (আল্লাহর সম্মুখে) নত হও, (তাঁর আদেশসমূহ পালনের মাধ্যমে) তখন তারা নত হয় না। ');
