INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5708,78,36,'এটা তোমার প্রতিপালকের পক্ষ থেকে প্রতিফল, যথোচিত দান। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5709,78,37,'যিনি আকাশ, পৃথিবী আর এগুলোর মাঝে যা কিছু আছে সব কিছুর প্রতিপালক, তিনি অতি দয়াময়, তাঁর সম্মুখে কথা বলার সাহস কারো হবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5710,78,38,'সেদিন রূহ (জিবরাঈল) আর ফেরেশতারা সারিবদ্ধ হয়ে দাঁড়াবে, কেউ কোন কথা বলতে পারবে না, সে ব্যতীত যাকে পরম করুণাময় অনুমতি দিবেন, আর সে যথার্থ কথাই বলবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5711,78,39,'এ দিনটি সত্য, সুনিশ্চিত, অতএব যার ইচ্ছে সে তার প্রতিপালকের দিকে আশ্রয় গ্রহণ করুক। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5712,78,40,'আমি তোমাদেরকে নিকটবর্তী শাস্তি সম্পর্কে সতর্ক করছি, যেদিন মানুষ দেখতে পাবে তার হাতগুলো আগেই কী (‘আমাল) পাঠিয়েছে আর কাফির বলবে- ‘হায়! আমি যদি মাটি হতাম (তাহলে আমাকে আজকের এ ‘আযাবের সম্মুখীন হতে হত না)। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5713,79,1,'শপথ সেই ফেরেশতাদের যারা (পাপীদের আত্মা) নির্মমভাবে টেনে বের করে,');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5714,79,2,'আর যারা (নেককারদের আত্মা) খুবই সহজভাবে বের করে, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5715,79,3,'শপথ সেই ফেরেশতাদের যারা দ্রুতগতিতে সাঁতার কাটে, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5716,79,4,'আর (আল্লাহর নির্দেশ পালনের জন্য) ক্ষিপ্র গতিতে এগিয়ে যায়, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5717,79,5,'অতঃপর সব কাজের ব্যবস্থা করে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5718,79,6,'সেদিন ভূকম্পন প্রকম্পিত করবে, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5719,79,7,'তারপর আসবে আরেকটি ভূকম্পন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5720,79,8,'কত হৃদয় সে দিন ভয়ে ভীত হয়ে পড়বে, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5721,79,9,'তাদের দৃষ্টি নত হবে, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5722,79,10,'তারা বলে, ‘আমাদেরকে কি আগের অবস্থায় ফিরিয়ে আনা হবে? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5723,79,11,'আমরা যখন পচা-গলা হাড় হয়ে যাব (তখনও)?’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5724,79,12,'তারা বলে, ‘অবস্থা যদি তাই হয় তাহলে এই ফিরিয়ে আনাটাতো সর্বনাশের ব্যাপার হবে।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5725,79,13,'ওটা তো কেবল একটা বিকট আওয়াজ, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5726,79,14,'সহসাই তারা খোলা ময়দানে আবির্ভূত হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5727,79,15,'মূসার বৃত্তান্ত তোমার কাছে পৌঁছেছে কি? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5728,79,16,'যখন তার প্রতিপালক তাকে পবিত্র তুয়া প্রান্তরে ডাক দিয়ে বলেছিলেন ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5729,79,17,'‘ফেরাউনের কাছে যাও, সে সীমালঙ্ঘন করেছে, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5730,79,18,'তাকে জিজ্ঞেস কর, ‘তুমি কি পবিত্রতা অবলম্বন করতে ইচ্ছুক? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5731,79,19,'আর আমি তোমাকে তোমার প্রতিপালকের দিকে পথ দেখাই যাতে তুমি তাঁকে ভয় কর?’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5732,79,20,'অতঃপর মূসা তাকে বিরাট নিদর্শন দেখাল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5733,79,21,'কিন্তু সে অস্বীকার করল ও অমান্য করল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5734,79,22,'অতঃপর সে (আল্লাহর বিরুদ্ধে) জোর প্রচেষ্টা চালানোর জন্য (সত্যের) উল্টোপথে ফিরে গেল।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5735,79,23,'সে লোকদেরকে একত্রিত করল আর ঘোষণা দিল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5736,79,24,'সে বলল, ‘আমিই তোমাদের সর্বশ্রেষ্ঠ রব’। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5737,79,25,'পরিশেষে আল্লাহ তাকে আখেরাত ও দুনিয়ার ‘আযাবে পাকড়াও করলেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5738,79,26,'যে ভয় করে এমন প্রতিটি লোকের জন্য এতে অবশ্যই শিক্ষা আছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5739,79,27,'তোমাদের সৃষ্টি বেশি কঠিন না আকাশের? তিনি তো সেটা সৃষ্টি করেছেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5740,79,28,'তার ছাদ অনেক উচ্চে তুলেছেন, অতঃপর তাকে ভারসাম্যপূর্ণ করেছেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5741,79,29,'তিনি তার রাতকে আঁধারে ঢেকে দিয়েছেন, আর তার দিবালোক প্রকাশ করেছেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5742,79,30,'অতঃপর তিনি যমীনকে বিস্তীর্ণ করেছেন।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5743,79,31,'তিনি তার ভিতর থেকে বের করেছেন তার পানি ও তার তৃণভূমি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5744,79,32,'পবর্তকে তিনি দৃঢ়ভাবে প্রতিষ্ঠিত করেছেন, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5745,79,33,'এ সমস্ত তোমাদের আর তোমাদের গৃহপালিত পশুগুলোর জীবিকার সামগ্রী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5746,79,34,'অতঃপর যখন মহাসংকট এসে যাবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5747,79,35,'সেদিন মানুষ স্মরণ করবে যা কিছু করার জন্য সে জোর প্রচেষ্টা চালিয়েছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5748,79,36,'এবং জাহান্নামকে দেখানো হবে এমন ব্যক্তিকে যে দেখতে পায়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5749,79,37,'অতঃপর (দুনিয়ায়) যে লোক সীমালঙ্ঘন করেছিল, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5750,79,38,'আর পার্থিব জীবনকে (পরকালের উপর) প্রাধান্য দিয়েছিল ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5751,79,39,'জাহান্নামই হবে তার আবাসস্থল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5752,79,40,'আর যে লোক তার প্রতিপালকের সামনে দাঁড়ানোকে ভয় করেছিল এবং নিজেকে কামনা বাসনা থেকে নিবৃত্ত রেখেছিল, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5753,79,41,'জান্নাতই হবে তার বাসস্থান। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5754,79,42,'এরা তোমাকে জিজ্ঞেস করে ক্বিয়ামত সম্পর্কে- ‘কখন তা ঘটবে?’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5755,79,43,'এর আলোচনার সাথে তোমার কী সম্পর্ক? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5756,79,44,'এ সংক্রান্ত জ্ঞান তোমার প্রতিপালক পর্যন্তই শেষ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5757,79,45,'যারা একে ভয় করে তুমি কেবল তাদের সতর্ককারী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5758,79,46,'যেদিন তারা তা দেখবে সেদিন তাদের মনে হবে, যেন তারা (পৃথিবীতে) এক সন্ধ্যা বা এক সকালের বেশি অবস্থান করেনি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5759,80,1,'(নবী) মুখ ভার করল আর মুখ ঘুরিয়ে নিল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5760,80,2,'(কারণ সে যখন কুরায়শ সরদারদের সাথে আলোচনায় রত ছিল তখন) তার কাছে এক অন্ধ ব্যক্তি আসল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5761,80,3,'(হে নবী!) তুমি কি জান, সে হয়ত পরিশুদ্ধ হত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5762,80,4,'কিংবা উপদেশ গ্রহণ করত, ফলে উপদেশ তার উপকারে লাগত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5763,80,5,'পক্ষান্তরে যে পরোয়া করে না,');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5764,80,6,'তার প্রতি তুমি মনোযোগ দিচ্ছ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5765,80,7,'সে পরিশুদ্ধ না হলে তোমার উপর কোন দোষ নেই। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5766,80,8,'পক্ষান্তরে যে লোক তোমার কাছে ছুটে আসল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5767,80,9,'আর সে ভয়ও করে, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5768,80,10,'তুমি তার প্রতি অমনোযোগী হলে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5769,80,11,'না, এটা মোটেই ঠিক নয়, এটা তো উপদেশ বাণী, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5770,80,12,'কাজেই যার ইচ্ছে তা স্মরণে রাখবে, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5771,80,13,'(এটা লিপিবদ্ধ আছে) মর্যাদাসম্পন্ন কিতাবসমূহে ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5772,80,14,'সমুন্নত, পবিত্র। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5773,80,15,'(এমন) লেখকদের হাতে ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5774,80,16,'(যারা) মহা সম্মানিত পূত-পবিত্র। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5775,80,17,'মানুষ ধ্বংস হোক! কোন্ জিনিস তাকে সত্য প্রত্যাখ্যানে উদ্বুদ্ধ করল? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5776,80,18,'আল্লাহ তাকে কোন বস্তু হতে সৃষ্টি করেছেন? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5777,80,19,'শুক্রবিন্দু হতে। তিনি তাকে সৃষ্টি করেছেন, অতঃপর তাকে পরিমিতভাবে গড়ে তুলেছেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5778,80,20,'অতঃপর তিনি (উপায়-উপকরণ ও প্রয়োজনীয় দ্রব্যসামগ্রী দিয়ে জীবনে চলার জন্য) তার পথ সহজ করে দিয়েছেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5779,80,21,'অতঃপর তার মৃত্যু ঘটান এবং তাকে কবরস্থ করেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5780,80,22,'অতঃপর যখন তিনি চাইবেন তাকে আবার জীবিত করবেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5781,80,23,'না, মোটেই না, আল্লাহ তাকে যে নির্দেশ দিয়ে ছিলেন তা সে এখনও পূর্ণ করেনি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5782,80,24,'মানুষ তার খাদ্যের ব্যপারটাই ভেবে দেখুক না কেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5783,80,25,'আমি প্রচুর পানি ঢালি, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5784,80,26,'তারপর যমীনকে বিদীর্ণ করে দেই, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5785,80,27,'অতঃপর তাতে আমি উৎপন্ন করি-শস্য, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5786,80,28,'আঙ্গুর, তাজা শাক-শব্জী, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5787,80,29,'যয়তূন, খেজুর, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5788,80,30,'আর ঘন বৃক্ষ পরিপূর্ণ বাগবাগিচা, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5789,80,31,'আর নানান জাতের ফল আর ঘাস-লতাপাতা। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5790,80,32,'তোমাদের আর তোমাদের গৃহপালিত পশুগুলোর ভোগের জন্য। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5791,80,33,'অবশেষে যখন কান-ফাটানো শব্দ আসবে; ');
