INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5818,81,18,'আর ঊষার যখন তা নিঃশ্বাস ফেলে অন্ধকারকে বের করে দেয়, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5819,81,19,'এ কুরআন নিশ্চয়ই সম্মানিত রসূলের (অর্থাৎ জিবরাঈলের) আনীত বাণী। ');
