INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5835,82,6,'হে মানুষ! কিসে তোমাকে তোমার মহান প্রতিপালক সম্পর্কে ধোঁকায় ফেলে দিয়েছে? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5836,82,7,'যিনি তোমাকে সৃষ্টি করেছেন, অতঃপর তোমাকে সুঠাম করেছেন, অতঃপর তোমাকে করেছেন ভারসাম্যপূর্ণ। ');
