INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5838,82,9,'না (তোমাদের এই বিভ্রান্তি মোটেই সঠিক নয়), তোমরা তো (আখেরাতের) শাস্তি ও পুরস্কারকে অস্বীকার করে থাক; ');
