INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5848,82,19,'সেদিন কোন মানুষ অপরের জন্য কিছু করার সামর্থ্য রাখবে না, সেদিন সকল কর্তৃত্ব থাকবে একমাত্র আল্লাহরই (ইখতিয়ারে)। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5849,83,1,'দুর্ভোগ ঠকবাজদের জন্য (যারা মাপে বা ওজনে কম দেয়), ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5850,83,2,'যারা লোকের কাছ থেকে মেপে নেয়ার সময় পুরামাত্রায় নেয়, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5851,83,3,'আর যখন তাদেরকে মেপে দেয় বা ওজন ক’রে দেয় তখন কম দেয়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5852,83,4,'তারা কি চিন্তা করে না যে (তাদের মৃত্যুর পর) তাদেরকে আবার উঠানো হবে, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5853,83,5,'এক মহা দিবসে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5854,83,6,'যেদিন মানুষ বিশ্বজগতের প্রতিপালকের সামনে দাঁড়াবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5855,83,7,'(তারা যে সব ধারণা করছে তা) কক্ষনো না, নিশ্চয়ই পাপীদের ‘আমালনামা সিজ্জীনে (সংরক্ষিত) আছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5856,83,8,'তুমি কি জান সিজ্জীন কী');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5857,83,9,'সীলমোহরকৃত কিতাব। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5858,83,10,'সেদিন দুর্ভোগ হবে অস্বীকারকারীদের,');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5859,83,11,'যারা কর্মফল দিবসকে অস্বীকার করে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5860,83,12,'কেবল সীমালঙ্ঘনকারী, পাপাচারী ছাড়া কেউই তা অস্বীকার করে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5861,83,13,'তার সামনে যখন আমার আয়াত পড়ে শোনানো হয়, তখন সে বলে, ‘এ তো প্রাচীন কালের লোকেদের কাহিনী’’। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5862,83,14,'কক্ষনো না, বরং তাদের কৃতকর্মই তাদের অন্তরে জং ধরিয়ে দিয়েছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5863,83,15,'কক্ষনো না, তারা সেদিন তাদের প্রতিপালক থেকে পর্দার আড়ালে থাকবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5864,83,16,'অতঃপর তারা অবশ্যই জাহান্নামে প্রবেশ করবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5865,83,17,'অতঃপর বলা হবে ‘এটাই তা যা তোমরা অস্বীকার করতে।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5866,83,18,'(ভাল-মন্দের বিচার হবে না, শাস্তি-পুরস্কার কিছুই হবে না তা) কক্ষনো না, নিশ্চয়ই সৎলোকদের ‘আমালমানা ‘ইল্লিয়ীনে (সংরক্ষিত) আছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5867,83,19,'তুমি কি জান ইল্লিয়ীন কী? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5868,83,20,'সীলমোহরকৃত কিতাব। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5869,83,21,'আল্লাহর নৈকট্যপ্রাপ্ত (ফেরেশতারা) তার তত্ত্বাবধান করে। ');
