INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5871,83,23,'উচ্চ আসনে বসে তারা (চারদিকের সবকিছু) দেখতে থাকবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5872,83,24,'তুমি তাদের মুখে আরাম আয়েশের উজ্জ্বলতা দেখতে পাবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5873,83,25,'তাদেরকে পান করানো হবে সীল-আঁটা উৎকৃষ্ট পানীয়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5874,83,26,'তার সীল হবে মিশকের, প্রতিযোগীরা এ বিষয়েই প্রতিযোগিতা করুক। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5875,83,27,'তাতে মেশানো থাকবে ‘তাসনীম, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5876,83,28,'ওটা একটা ঝর্ণা, যাত্থেকে (আল্লাহর) নৈকট্যপ্রাপ্তরা পান করবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5877,83,29,'পাপাচারী লোকেরা (দুনিয়ায়) মু’মিনদেরকে ঠাট্টা-বিদ্রুপ করত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5878,83,30,'আর তারা যখন তাদের পাশ দিয়ে অতিক্রম করত তখন পরস্পরে চোখ টিপে ইশারা করত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5879,83,31,'আর তারা যখন তাদের আপন জনদের কাছে ফিরে আসত, তখন (মু’মিনদেরকে ঠাট্টা ক’রে আসার কারণে) ফিরত উৎফুল্ল হয়ে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5880,83,32,'আর তারা যখন মু’মিনদেরকে দেখত তখন বলত, ‘এরা তো অবশ্যই গুমরাহ্।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5881,83,33,'তাদেরকে তো মু’মিনদের হিফাযাতকারী হিসেবে পাঠানো হয়নি (মু’মিনদের কৃতকর্মের হিসাব মু’মিনরাই দিবে)। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5882,83,34,'আজ (জান্নাত হতে) মু’মিনরা কাফিরদের (পরিণতির) উপর হাসছে, ');
