INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5884,83,36,'কাফিররা যা করত তার ‘সওয়াব’ পেল তো? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5885,84,1,'যখন আসমান ফেটে যাবে, ');
