INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5887,84,3,'এবং যমীনকে যখন প্রসারিত করা হবে, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5888,84,4,'আর তা তার ভেতরে যা আছে তা বাইরে নিক্ষেপ করবে ও খালি হয়ে যাবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5889,84,5,'এবং স্বীয় রব-এর নির্দেশ পালন করবে আর তাই তার করণীয়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5890,84,6,'হে মানুষ! তোমাকে তোমার রব পর্যন্ত পৌঁছতে বহু কষ্ট স্বীকার করতে হবে, অতঃপর তুমি তার সাক্ষাৎ লাভ করবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5891,84,7,'অতঃপর  যার ‘আমালনামা  তার  ডান হাতে দেয়া হবে।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5892,84,8,'তার হিসাব সহজভাবেই নেয়া হবে। ');
