INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5898,84,14,'সে ভাবত যে, সে কক্ষনো (আল্লাহর কাছে) ফিরে যাবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5899,84,15,'অবশ্যই ফিরে যাবে, তার প্রতিপালক তার প্রতি দৃষ্টি রাখছেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5900,84,16,'আমি শপথ করি সন্ধ্যাকালীন লালিমার, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5901,84,17,'আর রাতের এবং তা যা কিছুর সমাবেশ ঘটায় তার,');
