INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5903,84,19,'অবশ্যই তোমরা (আধ্যাত্মিক ও জাগতিক সর্বক্ষেত্রে) স্তরে স্তরে উন্নতির সিঁড়ি বেয়ে উর্ধ্বে উঠবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5904,84,20,'অতএব তাদের কী হল যে তারা ঈমান আনে না? ');
