INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5917,85,8,'তারা তাদেরকে নির্যাতন করেছিল একমাত্র এই কারণে যে, তারা মহাপরাক্রান্ত প্রসংসিত আল্লাহর প্রতি ঈমান এনেছিল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5918,85,9,'আসমান ও যমীনের রাজ্বত্ব যাঁর, আর সেই আল্লাহ সব কিছুর প্রত্যক্ষদর্শী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5919,85,10,'যারা মু’মিন পুরুষ ও নারীদের প্রতি যুলম পীড়ন চালায় অতঃপর তাওবাহ করে না, তাদের জন্য আছে জাহান্নামের শাস্তি, আর আছে আগুনে দগ্ধ হওয়ার যন্ত্রণা। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5920,85,11,'যারা ঈমান আনে আর সৎকাজ করে তাদের জন্য আছে জান্নাত, যার পাদদেশ দিয়ে বয়ে চলেছে নির্ঝরিণী, এটা বিরাট সাফল্য। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5921,85,12,'তোমার প্রতিপালকের পাকড়াও অবশ্যই বড় কঠিন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5922,85,13,'তিনিই প্রথমবার সৃষ্টি করেন অতঃপর সৃষ্টির আবর্তন ঘটান। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5923,85,14,'তিনি ক্ষমাশীল, প্রেমময়, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5924,85,15,'‘আরশের অধিপতি, মহা সম্মানিত।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5925,85,16,'যা করতে চান তাই করেন। ');
