INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5928,85,19,'তবুও কাফিররা সত্য প্রত্যাখ্যান করেই চলেছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5929,85,20,'আর আল্লাহ আড়াল থেকে ওদেরকে ঘিরে রেখেছেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5930,85,21,'(কাফিররা অমান্য করলেও এ কুরআনের কোনই ক্ষতি হবে না) বস্তুতঃ এটা সম্মানিত কুরআন, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5931,85,22,'সুরক্ষিত ফলকে লিপিবদ্ধ। ');
