INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5933,86,2,'তুমি কি জান যা রাতে আসে তা কী? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5934,86,3,'উজ্জ্বল নক্ষত্র। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5935,86,4,'প্রত্যেক আত্মার সাথে একজন সংরক্ষক আছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5936,86,5,'অতঃপর মানুষ চিন্তা করে দেখুক কোন জিনিস থেকে তাকে সৃষ্টি করা হয়েছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5937,86,6,'তাকে সৃষ্টি করা হয়েছে সবেগে বের হয়ে আসা পানি থেকে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5938,86,7,'যা বের হয় শিরদাঁড়া ও পাঁজরের মাঝখান থেকে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5939,86,8,'তিনি মানুষকে আবার (জীবনে) ফিরিয়ে আনতে অবশ্যই সক্ষম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5940,86,9,'যেদিন (কাজকর্ম আকীদা বিশ্বাস ও নিয়্যাত সম্পর্কিত) গোপন বিষয়াদি যাচাই পরখ করা হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5941,86,10,'সেদিন মানুষের না থাকবে নিজের কোন সামর্থ্য, আর না থাকবে কোন সাহায্যকারী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5942,86,11,'ঘুরে ঘুরে আসা বৃষ্টিবাহী আকাশের শপথ, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5943,86,12,'এবং গাছপালার চারা গজানোর সময় বক্ষ দীর্ণকারী যমীনের শপথ, (বৃষ্টিপাতের মাধ্যমে বৃক্ষলতার উৎপাদন যেমন অকাট্য সত্য, তেমনি কুরআন যা ঘোষণা করে তাও অকাট্য সত্য) ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5944,86,13,'কুরআন (সত্য-মিথ্যার পার্থক্যকারী) চূড়ান্ত সিদ্ধান্তকারী বাণী, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5945,86,14,'এবং তা কোন হাসি-ঠাট্টামূলক কথা নয়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5946,86,15,'এবং তারা (সত্যের বিরুদ্ধে) ষড়যন্ত্র করছে, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5947,86,16,'আর আমিও (তাদের অন্যায় ধ্বংসাত্মক ষড়যন্ত্র ভন্ডুল করার) কৌশল করছি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5948,86,17,'কাজেই (এই ষড়যন্ত্রকারী) কাফিরদেরকে অবকাশ দাও, তাদেরকে কিছু সময়ের জন্য অবকাশ দাও। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5949,87,1,'তোমার মহান প্রতিপালকের নামের পবিত্রতা ও মহিমা ঘোষণা কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5950,87,2,'যিনি সৃষ্টি করেছেন অতঃপর করেছেন (দেহের প্রতিটি অঙ্গকে) সামঞ্জস্যপূর্ণ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5951,87,3,'যিনি সকল বস্তুকে পরিমাণ মত সৃষ্টি করেছেন, অতঃপর (জীবনে চলার) পথনির্দেশ করেছেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5952,87,4,'যিনি তৃণ ইত্যাদি বের করেছেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5953,87,5,'অতঃপর তাকে কাল আবর্জনায় পরিণত করেছেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5954,87,6,'আমি তোমাকে পড়িয়ে দেব, যার ফলে তুমি ভুলে যাবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5955,87,7,'তবে ওটা বাদে যেটা আল্লাহ (রহিত করার) ইচ্ছে করবেন। তিনি জানেন যা প্রকাশ্য আর যা গোপন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5956,87,8,'আমি তোমার জন্য সহজপথ (অনুসরণ করা) আরো সহজ করে দেব। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5957,87,9,'কাজেই তুমি উপদেশ দাও যদি উপদেশ উপকার দেয়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5958,87,10,'যে ভয় করে সে উপদেশ গ্রহণ করবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5959,87,11,'আর তা উপেক্ষা করবে যে চরম হতভাগা। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5960,87,12,'যে ভয়াবহ আগুনে প্রবেশ করবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5961,87,13,'অতঃপর সেখানে সে না (মরার মত) মরবে, আর না (বাঁচার মত) বাঁচবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5962,87,14,'সাফল্য লাভ করবে সে যে নিজেকে পরিশুদ্ধ করে, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5963,87,15,'আর তার প্রতিপালকের নাম স্মরণ করে ও নামায কায়েম করে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5964,87,16,'কিন্তু তোমরা তো দুনিয়ার জীবনকেই প্রাধান্য দাও, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5965,87,17,'অথচ আখিরাতই অধিক উৎকৃষ্ট ও স্থায়ী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5966,87,18,'আগের কিতাবগুলোতে এ কথা (লিপিবদ্ধ) আছে, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5967,87,19,'ইবরাহীম ও মূসার কিতাবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5968,88,1,'(সব কিছুকে) আচ্ছন্নকারী কিয়ামাতের খবর তোমার কাছে পৌঁছেছে কি? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5969,88,2,'কতক মুখ সেদিন নীচু হবে ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5970,88,3,'হবে কর্মক্লান্ত, শ্রান্ত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5971,88,4,'তারা জ্বলন্ত আগুনে প্রবেশ করবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5972,88,5,'টগবগে ফুটন্ত ঝর্ণা থেকে তাদেরকে পান করানো হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5973,88,6,'কাঁটাযুক্ত শুকনো ঘাস ছাড়া তাদের জন্য আর কোন খাদ্য থাকবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5974,88,7,'যা পুষ্টিসাধন করবে না, আর ক্ষুধাও মিটাবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5975,88,8,'কতক মুখ সেদিন হবে আনন্দে উজ্জ্বল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5976,88,9,'নিজেদের চেষ্টা-সাধনার জন্য সন্তুষ্ট। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5977,88,10,'উচ্চ মর্যাদাপূর্ণ জান্নাতে, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5978,88,11,'সেখানে শুনবে না কোন অনর্থক কথাবার্তা, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5979,88,12,'সেখানে থাকবে প্রবহমান ঝর্ণা, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5980,88,13,'সেখানে থাকবে উন্নত মর্যাদাসম্পন্ন আসন, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5981,88,14,'পানপাত্র থাকবে প্রস্তুত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5982,88,15,'সারি সারি বালিশ, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5983,88,16,'আর থাকবে মখমল- বিছানো। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5984,88,17,'(ক্বিয়ামত হবে একথা যারা অমান্য করে) তারা কি উটের প্রতি লক্ষ্য করে না, (সৃষ্টি কুশলতায় ভরপুর ক’রে) কী ভাবে তা সৃষ্টি করা হয়েছে? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5985,88,18,'এবং আসমানের দিকে, কীভাবে তা ঊর্ধ্বে উঠানো হয়েছে? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5986,88,19,'এবং পর্বতমালার দিকে, কী রকম দৃঢ়ভাবে তাকে প্রতিষ্ঠিত করা হয়েছে? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5987,88,20,'আর যমীনের দিকে, কীভাবে তাকে বিছিয়ে দেয়া হয়েছে? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5988,88,21,'কাজেই তুমি তাদেরকে উপদেশ দাও, তুমি একজন উপদেশদাতা মাত্র। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5989,88,22,'তুমি তাদের ওপর জবরদস্তিকারী নও। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5990,88,23,'তবে কেউ কুফুরি করলে এবং মুখ ফিরিয়ে নিলে ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5991,88,24,'আল্লাহ তাকে মহাশাস্তিতে শাস্তি দেবেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5992,88,25,'তাদেরকে আমার কাছেই ফিরে আসতে হবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5993,88,26,'অতঃপর তাদের হিসাব নেয়া তো আমারই কাজ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5994,89,1,'ঊষার শপথ,');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5995,89,2,'(জিলহাজ্জ মাসের প্রথম) দশ রাতের শপথ, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5996,89,3,'জোড় ও বেজোড়ের শপথ, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5997,89,4,'আর রাতের শপথ যখন তা গত হতে থাকে, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5998,89,5,'অবশ্যই এতে জ্ঞানী ব্যক্তির জন্য শপথ আছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (5999,89,6,'তুমি কি দেখনি তোমার প্রতিপালক ‘আদ জাতির সঙ্গে কী ব্যবহার করেছিলেন? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6000,89,7,'উচ্চ স্তম্ভ নির্মাণকারী ইরাম গোত্রের প্রতি?');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6001,89,8,'যার সমতুল্য অন্য কোন দেশে নির্মিত হয়নি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6002,89,9,'এবং সামূদের প্রতি যারা উপত্যকায় পাথর কেটে গৃহ নির্মাণ করেছিল? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6003,89,10,'এবং (সেনা ছাউনী স্থাপনের কাজে ব্যবহৃত) কীলক-এর অধিপতি ফেরাউনের প্রতি? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6004,89,11,'যারা দেশে সীমালঙ্ঘনমূলক আচরণ করেছিল, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6005,89,12,'আর সেখানে বহু বিপর্যয় সৃষ্টি করেছিল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6006,89,13,'অতঃপর তোমার প্রতিপালক তাদের উপর শাস্তির চাবুক হানলেন ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6007,89,14,'তোমার প্রতিপালক অবশ্যই সতর্ক দৃষ্টি রাখছেন (যেমন ঘাঁটিতে শত্রুর প্রতি দৃষ্টি রাখা হয়)। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6008,89,15,'মানুষ এমন যে, তার প্রতিপালক যখন তাকে পরীক্ষা করেন সম্মান ও অনুগ্রহ দান ক’রে, তখন সে বলে, ‘আমার রব আমাকে সম্মানিত করেছেন।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6009,89,16,'আর যখন তিনি তাকে পরীক্ষা করেন তার রিযক সঙ্কুচিত ক’রে, তখন সে বলে, ‘আমার রব আমাকে লাঞ্ছিত করেছেন।’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6010,89,17,'না (রিযক) কক্ষনো (মান-সম্মানের মানদন্ড) নয়, বরং তোমরা ইয়াতীমের প্রতি সম্মানজনক আচরণ কর না, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6011,89,18,'আর তোমরা ইয়াতীম মিসকিনকে খাদ্য দেয়ার জন্য পরস্পরকে উৎসাহিত কর না, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6012,89,19,'আর তোমরা উত্তরাধিকারীদের সব সম্পদ খেয়ে ফেল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6013,89,20,'আর তোমরা ধনসম্পদকে অতিরিক্ত ভালবাস। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6014,89,21,'এটা মোটেই ঠিক নয়, যখন পৃথিবীকে চূর্ণ বিচূর্ণ করে বালি বানিয়ে দেয়া হবে, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6015,89,22,'আর যখন তোমার প্রতিপালক আসবেন আর ফেরেশতারা আসবে সারিবদ্ধ হয়ে, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6016,89,23,'আর জাহান্নামকে সেদিন (সামনাসামনি) আনা হবে। সেদিন মানুষ উপলব্ধি করবে, কিন্তু তখন এ উপলব্ধি তার কী কাজে আসবে? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6017,89,24,'সে বলবে, ‘হায়! আমার (এখনকার) জীবনের জন্য যদি আমি (সৎকর্ম) আগে পাঠাতাম! ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6018,89,25,'অতঃপর সেদিন তাঁর শাস্তির মত শাস্তি কেউ দিতে পারবে না ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6019,89,26,'এবং তাঁর বাঁধনের মত কেউ বাঁধতে পারবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6020,89,27,'(অপর দিকে নেককার লোককে বলা হবে) হে প্রশান্ত আত্মা! ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6021,89,28,'তোমার রব-এর দিকে ফিরে এসো সন্তুষ্ট হয়ে এবং (তোমার রব-এর) সন্তুষ্টির পাত্র হয়ে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6022,89,29,'অতঃপর আমার (নেক) বান্দাহদের মধ্যে শামিল হও ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6023,89,30,'আর আমার জান্নাতে প্রবেশ কর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6024,90,1,'(কাফিররা বলছে দুনিয়ার জীবনই সবকিছু) না, আমি এই (মক্কা) নগরের শপথ করছি (যে নগরে সকলেই নিরাপদ), ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6025,90,2,'আর তুমি এই নগরের হালালকারী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6026,90,3,' শপথ জন্মদাতা (আদম)-এর আর যা সে জন্ম দিয়েছে (সেই সমস্ত মানুষের), ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6027,90,4,'আমি মানুষকে সৃষ্টি করেছি অত্যন্ত কষ্ট ও শ্রমের মাঝে, (দুনিয়ার প্রত্যেকটি মানুষ কোন না কোন কষ্টের মধ্যে পতিত আছে)। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6028,90,5,'সে কি মনে করে যে তার উপর কেউ ক্ষমতাবান নেই? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6029,90,6,'সে (গর্বের সঙ্গে) বলে যে, আমি প্রচুর ধন-সম্পদ উড়িয়েছি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6030,90,7,'সে কি মনে করে যে তাকে কেউ দেখেনি? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6031,90,8,'আমি কি তাকে দু’টো চোখ দিইনি? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6032,90,9,'আর একটা জিহবা আর দু’টো ঠোঁট? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6033,90,10,'আর আমি তাকে (পাপ ও পুণ্যের) দু’টো পথ দেখিয়েছি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6034,90,11,'(মানুষকে এত গুণবৈশিষ্ট্য ও মেধা দেয়া সত্ত্বেও) সে (ধর্মের) দুর্গম গিরি পথে প্রবেশ করল না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6035,90,12,'তুমি কি জান দুর্গম গিরিপথ কী? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6036,90,13,'(তা হচ্ছে) দাসমুক্তি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6037,90,14,'অথবা দুর্ভিক্ষের দিনে খাদ্য দান ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6038,90,15,'নিকটাত্মীয় ইয়াতীমকে, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6039,90,16,'অথবা দারিদ্র-ক্লিষ্ট মিসকীনকে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6040,90,17,'তদুপরি সে মু’মিনদের মধ্যে শামিল হয় আর পরস্পরকে ধৈর্য ধারণের ও দয়া প্রদর্শনের উপদেশ দেয়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6041,90,18,'তারাই ডানপন্থী (সৌভাগ্যবান লোক)। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6042,90,19,'আর যারা আমার আয়াতকে অস্বীকার করে তারাই বামপন্থী (হতভাগা)। ');
