INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6044,91,1,'শপথ সূর্যের ও তার (উজ্জ্বল) কিরণের,  ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6045,91,2,'শপথ চাঁদের যখন তা সূর্যের পিছনে আসে,  ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6046,91,3,'শপথ দিনের যখন তা সূর্যকে উদ্ভাসিত করে, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6047,91,4,'শপথ রাতের যখন তা সূর্যকে ঢেকে নেয়, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6048,91,5,'শপথ আসমানের আর সেটা যিনি বানিয়েছেন তাঁর, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6049,91,6,'শপথ যমীনের আর সেটা যিনি বিছিয়েছেন তাঁর, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6050,91,7,'শপথ প্রাণের আর তাঁর যিনি তা সামঞ্জস্যপূর্ণ করেছেন, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6051,91,8,'অতঃপর তাকে তার অসৎকর্ম ও সৎকর্মের জ্ঞান দান করেছেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6052,91,9,'সেই সফলকাম হয়েছে যে নিজ আত্মাকে পবিত্র করেছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6053,91,10,'সেই ব্যর্থ হয়েছে যে নিজ আত্মাকে কলূষিত করেছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6054,91,11,'সামূদ জাতি সীমালঙ্ঘন ক’রে (তাদের নবীকে মেনে নিতে) অস্বীকার করেছিল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6055,91,12,'যখন তাদের সবচেয়ে হতভাগা লোকটি মাথা চাড়া দিয়ে উঠল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6056,91,13,'তখন আল্লাহর রাসূল (সালিহ) তাদেরকে বলল, ‘এটা আল্লাহর উটনি, একে পানি পান করতে বাধা দিও না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6057,91,14,'কিন্তু তারা রসূলের কথা অগ্রাহ্য করল এবং উটনির পায়ের রগ কেটে দিল। শেষ পর্যন্ত তাদের পাপের কারণে তাদের প্রতিপালক তাদেরকে ধ্বংস করে মাটিতে মিশিয়ে দিলেন।');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6058,91,15,'আর তিনি (তাঁর এ কাজের) কোন খারাপ পরিণতির ভয় মোটেই পোষণ করেন না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6059,92,1,'শপথ রাতের যখন তা (আলোকে) ঢেকে দেয়, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6060,92,2,'শপথ দিনের যখন তা উদ্ভাসিত হয়ে উঠে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6061,92,3,'আর শপথ তাঁর যিনি সৃষ্টি করেছেন পুরুষ ও নারী, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6062,92,4,'তোমাদের চেষ্টা সাধনা অবশ্যই বিভিন্নমুখী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6063,92,5,'অতএব যে ব্যক্তি (আল্লাহর সন্তুষ্টির জন্য) দান করে ও (আল্লাহকে) ভয় করে, ');
