INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6067,92,9,'আর যা উত্তম তা অমান্য করে, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6068,92,10,'আমি তার জন্য কঠিন পথ (অর্থাৎ অন্যায়, অসত্য, হিংসা ও হানাহানির পথ) সহজ করে দিব। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6069,92,11,'যখন সে ধ্বংস হবে (অর্থাৎ মরবে) তখন তার (সঞ্চিত) ধন-সম্পদ কোনই কাজে আসবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6070,92,12,'সঠিক পথ দেখানো অবশ্যই আমারই কাজ ');
