INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6072,92,14,'কাজেই আমি তোমাদেরকে দাউ দাউ ক’রে জ্বলা আগুন সম্পর্কে সতর্ক করে দিচ্ছি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6073,92,15,'চরম হতভাগা ছাড়া কেউ তাতে প্রবেশ করবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6074,92,16,'যে অস্বীকার করে ও মুখ ফিরিয়ে নেয় ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6075,92,17,'তাত্থেকে দূরে রাখা হবে এমন ব্যক্তিকে যে আল্লাহকে খুব বেশি ভয় করে, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6076,92,18,'যে পবিত্রতা অর্জনের উদ্দেশে নিজের ধন-সম্পদ দান করে, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6077,92,19,'(সে দান করে) তার প্রতি কারো অনুগ্রহের প্রতিদান হিসেবে নয়, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6078,92,20,'একমাত্র তার মহান প্রতিপালকের চেহারা (সন্তোষ) লাভের আশায়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6079,92,21,'সে অবশ্যই অতি শীঘ্র (আল্লাহর নি‘মাত পেয়ে) সন্তুষ্ট হয়ে যাবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6080,93,1,'সকালের উজ্জ্বল আলোর শপথ,');
