INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6082,93,3,'তোমার প্রতিপালক তোমাকে কক্ষনো পরিত্যাগ করেননি, আর তিনি অসন্তুষ্টও নন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6083,93,4,'অবশ্যই পরবর্তী সময় পূর্ববর্তী সময়ের চেয়ে তোমার জন্য হবে অধিক উৎকৃষ্ট। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6084,93,5,'শীঘ্রই তোমার প্রতিপালক তোমাকে (এত নি‘মাত) দিবেন যার ফলে তুমি সন্তুষ্ট হয়ে যাবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6085,93,6,'তিনি কি তোমাকে ইয়াতীম অবস্থায় পান নাই? অতঃপর তিনি আশ্রয় দিয়েছেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6086,93,7,'তিনি তোমাকে পেয়েছিলেন পথের দিশা-হীন, অতঃপর দেখালেন সঠিক পথ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6087,93,8,'তিনি তোমাকে পেলেন নিঃস্ব, অতঃপর করলেন অভাবমুক্ত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6088,93,9,'কাজেই তুমি ইয়াতীমের প্রতি কঠোরতা করবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6089,93,10,'এবং ভিক্ষুককে ধমক দিবে না। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6090,93,11,'আর তুমি তোমার রব-এর নি‘মাতকে (তোমার কথা, কাজকর্ম ও আচরণের মাধ্যমে) প্রকাশ করতে থাক। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6091,94,1,'(হে নবী! ওয়াহীর মাধ্যমে প্রকৃত জ্ঞান ও মানসিক শক্তি দিয়ে) আমি কি তোমার বক্ষদেশকে প্রসারিত করে দেইনি?  ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6092,94,2,'আর আমি তোমার হতে সরিয়ে দিয়েছি (সমাজের অনাচার, অশ্লীলতা ও পঙ্কিলতা দেখে তোমার অন্তরে জেগে উঠা দুঃখ, বেদনা, উদ্বেগ ও অস্থিরতার) ভার, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6093,94,3,'যা তোমার কোমরকে ভেঙ্গে দিচ্ছিল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6094,94,4,'এবং আমি (মু’মিনদের যাবতীয় আবশ্যিক ‘ইবাদাত আযান, ইক্বামাত, নামায, খুৎবাহ ইত্যাদির মাধ্যমে) তোমার স্মৃতিকে উচ্চ মর্যাদায় তুলে ধরেছি। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6095,94,5,'কষ্টের সাথেই স্বস্তি আছে, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6096,94,6,'নিঃসন্দেহে কষ্টের সাথেই স্বস্তি আছে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6097,94,7,'কাজেই তুমি যখনই অবসর পাবে, ‘ইবাদাতের কঠোর শ্রমে লেগে যাবে, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6098,94,8,'এবং তোমার রব-এর প্রতি গভীরভাবে মনোযোগ দিবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6099,95,1,'শপথ তীন ও যায়তূন-এর (যা জন্মে সিরিয়া ও ফিলিস্তিন এলাকায় যে স্থান বহু পুণ্যময় নবী ও রসূলের স্মৃতিতে ধন্য)। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6100,95,2,'শপথ সিনাই পর্বতের (যা নবী মূসার স্মৃতি বিজড়িত), ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6101,95,3,'আর (ইবরাহীম ও ইসমাঈল কর্তৃক নির্মিত কা‘বার) এই নিরাপদ নগরীর শপথ, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6102,95,4,'আমি মানুষকে সৃষ্টি করেছি অতি উত্তম আকার আকৃতি দিয়ে, (এবং জ্ঞান ও যোগ্যতা দিয়ে যার সুন্দরতম নমুনা হল নবী রসূলগণ)। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6103,95,5,'আবার উল্টোদিকে তাকে করেছি হীনদের হীনমত (যেমন আল্লাহ বিদ্রোহী কাফির, অত্যাচারী রাজা-বাদশা-শাসক, খুনী, পুতুল পূজারী ইত্যাদি)। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6104,95,6,'কিন্তু তাদেরকে নয় যারা ঈমান আনে ও সৎ কাজ করে, তাদের জন্য তো আছে অফুরন্ত প্রতিদান। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6105,95,7,'(ভাল কাজের পুরস্কার দেয়া আর অন্যায় কাজের শাস্তি দেয়াই ইনসাফপূর্ণ কথা) কাজেই শেষ বিচারের দিনকে অস্বীকার করতে কিসে তোমাদেরকে উদ্বুদ্ধ করছে? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6106,95,8,'আল্লাহ কি বিচারকদের মধ্যে শ্রেষ্ঠতম (বিচারক) নন? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6107,96,1,'পাঠ কর তোমার প্রতিপালকের নামে যিনি সৃষ্টি করেছেন, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6108,96,2,'সৃষ্টি করেছেন মানুষকে জমাট-বাঁধা রক্তপিন্ড হতে।  ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6109,96,3,'পাঠ কর, আর তোমার রব বড়ই অনুগ্রহশীল। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6110,96,4,'যিনি শিক্ষা দিয়েছেন কলম দিয়ে, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6111,96,5,'শিক্ষা দিয়েছেন মানুষকে যা সে জানত না, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6112,96,6,'না (এমন আচরণ করা) মোটেই ঠিক নয়, মানুষ অবশ্যই সীমালঙ্ঘন করে, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6113,96,7,'কারণ, সে নিজেকে অভাবমুক্ত মনে করে, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6114,96,8,'নিঃসন্দেহে (সকলকে) ফিরে যেতে হবে তোমার প্রতিপালকের দিকে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6115,96,9,'তুমি কি তাকে (অর্থাৎ আবূ জাহলকে) দেখেছ যে নিষেধ করে, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6116,96,10,'এক বান্দাহকে [অর্থাৎ রসূলুল্লাহ (সা.)-কে] যখন সে নামায আদায় করতে থাকে? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6117,96,11,'তুমি কি ভেবে দেখেছ (যাকে নিষেধ করা হচ্ছে) সে যদি সৎ পথে থাকে, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6118,96,12,'আর তাকওয়া অবলম্বনের নির্দেশ দেয় (তাহলে তার এ কাজগুলো কেমন মনে কর?) ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6119,96,13,'তোমার কী ধারণা যদি সে (অর্থাৎ নিষেধকারী ব্যক্তি) সত্যকে অস্বীকার করে আর মুখ ফিরিয়ে নেয় (তাহলে তার এ কাজ কেমন মনে কর?) ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6120,96,14,'সে কি জানে না যে, আল্লাহ দেখেন? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6121,96,15,'না, (সে যা করতে চায়) তা কক্ষনো করতে পারবে না, সে যদি বিরত না হয় তাহলে আমি অবশ্যই তার মাথার সামনের চুলগুচ্ছ ধরে হেঁচড়ে নিয়ে যাব- ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6122,96,16,'মিথ্যাচারী পাপাচারী চুলগুচ্ছ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6123,96,17,'কাজেই সে তার সভাষদদের ডাকুক। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6124,96,18,'আমিও ‘আযাবের ফেরেশতাদেরকে ডাকব, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6125,96,19,'না, তুমি কক্ষনো তার অনুসরণ করো না, তুমি সাজদাহ কর আর (আল্লাহর) নৈকট্য লাভ কর।[সাজদাহ] ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6126,97,1,'আমি কুরআনকে কাদরের রাতে নাযিল  করেছি, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6127,97,2,'তুমি কি জান ক্বাদরের রাত কী? ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6128,97,3,'ক্বাদরের রাত হাজার মাসের চেয়েও অধিক উত্তম, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6129,97,4,'এ রাতে ফেরেশতা আর রূহ তাদের রব-এর অনুমতিক্রমে প্রত্যেক কাজে অবতীর্ণ হয়। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6130,97,5,'(এ রাতে বিরাজ করে) শান্তি আর শান্তি- ফজর উদয় হওয়া পর্যন্ত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6131,98,1,'কিতাবধারীদের মধ্যে যারা কাফির ছিল তারা আর মুশরিকরা (তাদের ভ্রান্ত মত ও পথ হতে) সরে আসত না যতক্ষণ না তাদের কাছে আসত সুস্পষ্ট প্রমাণ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6132,98,2,'(অর্থাৎ) আল্লাহর নিকট হতে একজন রসূল, যে পাঠ করে পবিত্র গ্রন্থ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6133,98,3,'যাতে আছে সঠিক বিধান। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6134,98,4,'যাদেরকে কিতাব দেয়া হয়েছিল তাদের মাঝে বিচ্ছিন্নতা দেখা দিয়েছিল তাদের কাছে সুস্পষ্ট প্রমাণ আসার পর। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6135,98,5,'তাদেরকে এ ছাড়া অন্য কোন হুকুমই দেয়া হয়নি যে, তারা আল্লাহর ‘ইবাদাত করবে খাঁটি মনে একনিষ্ঠভাবে তাঁর আনুগত্যের মাধ্যমে। আর তারা নামায প্রতিষ্ঠা করবে আর যাকাত দিবে। আর এটাই সঠিক সুদৃঢ় দ্বীন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6136,98,6,'কিতাবধারীদের মধ্যে যারা কুফুরী করে তারা আর মুশরিকরা জাহান্নামের আগুনে স্থায়ীভাবে থাকবে। এরাই সৃষ্টির অধম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6137,98,7,'যারা ঈমান আনে আর সৎ কাজ করে তারা সৃষ্টির উত্তম। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6138,98,8,'তাদের প্রতিপালকের কাছে তাদের প্রতিদান আছে স্থায়ী জান্নাত, যার তলদেশ দিয়ে নদ-নদী প্রবাহিত, তাতে তারা চিরকাল স্থায়ীভাবে থাকবে। আল্লাহ তাদের প্রতি সন্তুষ্ট, আর তারাও তাঁর প্রতি সন্তুষ্ট। এ সব কিছু তার জন্য যে তার প্রতিপালককে ভয় করে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6139,99,1,'পৃথিবীকে যখন তার প্রচন্ড কম্পনে কাঁপিয়ে দেয়া হবে, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6140,99,2,'পৃথিবী তার (ভেতরের যাবতীয়) বোঝা বাইরে নিক্ষেপ করবে, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6141,99,3,'এবং মানুষ বলবে ‘এর কী হয়েছে?’ ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6142,99,4,'সে দিন পৃথিবী তার (নিজের উপর সংঘটিত) বৃত্তান্ত বর্ণনা করবে, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6143,99,5,'কারণ তোমার প্রতিপালক তাকে আদেশ করবেন, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6144,99,6,'সেদিন মানুষ বের হবে ভিন্ন ভিন্ন দলে যাতে তাদেরকে তাদের কৃতকর্ম দেখানো যায়, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6145,99,7,'অতএব কেউ অণু পরিমাণও সৎ কাজ করলে সে তা দেখবে, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6146,99,8,'আর কেউ অণু পরিমাণও অসৎ কাজ করলে সেও তা দেখবে। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6147,100,1,'শপথ সেই (ঘোড়া) গুলোর যারা ঊর্ধ্বশ্বাসে দৌড়ায়, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6148,100,2,'অতঃপর (নিজের ক্ষুরের) ঘর্ষণে আগুন ছুটায়, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6149,100,3,'অতঃপর সকালে হঠাৎ আক্রমণ চালায়, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6150,100,4,'আর সে সময় ধূলি উড়ায়, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6151,100,5,'অতঃপর (শত্রু) দলের অভ্যন্তরে ঢুকে পড়ে (এভাবে মানুষ নিজের শক্তি-সামর্থ্য ও আল্লাহর এক অতি বড় নি‘মাত ঘোড়াকে অপরের সম্পদ লুণ্ঠন ও অন্যের প্রতি যুল্মের কাজে ব্যবহার করে), ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6152,100,6,'বস্তুতঃ মানুষ তার রব-এর প্রতি বড়ই অকৃতজ্ঞ। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6153,100,7,'আর সে নিজেই (নিজের কাজ-কর্মের মাধ্যমে) এ বিষয়ের সাক্ষী। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6154,100,8,'আর ধন-সম্পদের প্রতি অবশ্যই সে খুবই আসক্ত। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6155,100,9,'সে কি জানে না, কবরে যা আছে তা যখন উত্থিত হবে, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6156,100,10,'আর অন্তরে যা (কিছু লুকানো) আছে তা প্রকাশ করা হবে, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6157,100,11,'নিঃসন্দেহে তাদের প্রতিপালক সেদিন তাদের সম্পর্কে পুরোপুরি অবহিত থাকবেন। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6158,101,1,'মহা বিপদ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6159,101,2,'কী সেই মহা বিপদ?');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6160,101,3,'মহা বিপদ সম্পর্কে তুমি কী জান? ');
