INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6165,101,8,'আর যার (সৎকর্মের) পাল্লা হালকা হবে, ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6166,101,9,'(জাহান্নামের) অতলস্পর্শী গর্তই হবে তার বাসস্থান। ');
INSERT INTO `bn_taisirul` (index,sura,aya,text) VALUES (6167,101,10,'তুমি কি জান তা কী? ');
